import { Book } from "@/lib/types";

export const _1chronicles: Book = {
  id: "1chronicles",
  name: "1. Chronik",
  shortName: "1.Chr",
  testament: "old",
  introduction: "Die Bücher der Chronik Die Chronik-Bücher tragen in der hebräischen Bibel die Überschrift »Ereignisse der Tage«. Der Name »Chronik« stammt vom Kirchenlehrer Hieronymus, der die Bücher als »Chronik der gesamten göttlichen Geschichte« bezeichnete. Sie behandeln die Geschichte von Adam bis zum Ende des Babylonischen Exils. Der erste Teil (Kap. 1 - 9) besteht überwiegend aus Namenslisten, die zumeist genealogisch geordnet sind, die Gliederung des Volkes in Stämme berücksichtigen, eingehender aber nur die königliche und die priesterlich-levitische Linie aufführen. Gelegentlich sind geschichtliche Notizen eingestreut. Erst in 1 Chr 10 beginnt die Darstellung der Geschichte Davids, die bis zum Ende des ersten Buches reicht. Es folgt die Geschichte Salomos (2 Chr 1 - 9) und die Geschichte Judas bis zum Untergang des Reichs (2 Chr 10 - 36). Die in 1 Chr 10 beginnende Geschichte umfasst den gleichen Zeitraum, der auch in 2 Sam und in den Büchern der Könige behandelt wird; größere Abschnitte sind von dort wörtlich übernommen. Eine umfassende Darstellung der Ereignisse ist nicht angestrebt. Im Mittelpunkt des Interesses stehen das von Gott auserwählte Herrscherhaus David und das auf Gottes Weisung hin gestiftete Heiligtum in Jerusalem. Auf die Geschichte des Nordreichs, das sich von der Dynastie David losgelöst hatte, wird nur selten Bezug genommen und selbst in der Geschichte Davids bleibt unerwähnt, was nicht dem beabsichtigten Ziel dient, nämlich der Verherrlichung des davidischen Königtums und des Kultes in Jerusalem. Ausführlich werden die Leistungen der einzelnen Könige für den Bau und für die Erhaltung des Tempels und ihre Sorge für den Gottesdienst beschrieben. Der Bericht über den Bau des Tempels, der von David vorbereitet und von Salomo ausgeführt wurde, zeigt durch seinen größeren Umfang und seine Stellung in der Mitte des Werkes die Bedeutung des Heiligtums im Glauben Israels an. Der Verfasser ist unbekannt. Aus der Vorliebe, mit der er von den Tempelsängern berichtet, hat man geschlossen, dass er aus ihrem Kreis stammt. In 1 Chr 3,19-24 werden fünf Generationen nach Serubbabel, der seit 537 v. Chr. in Jerusalem tätig war, gezählt. Wenn der Verfasser damit die Vertreter der davidischen Linie bis zu seiner Zeit mitgeteilt hat, ergibt sich die Zeit um 400 v. Chr. als Abfassungszeit der Bücher. Doch ist eine spätere Abfassung nicht auszuschließen. Die Zuverlässigkeit des Chronisten wurde vielfach bestritten. Man warf ihm vor, dass er die geschichtlichen Vorgänge nach seinem rigorosen Vergeltungsglauben einseitig darstelle und Verhältnisse der späteren Zeit in frühere Jahre verlege. Diese Fragen bedürfen in jedem einzelnen Fall einer besonderen Prüfung. Größere Abschnitte der Chronik sind jedenfalls geschichtlich zuverlässigen Berichten der Königsbücher entnommen; an der Geschichtlichkeit zahlreicher Mitteilungen, die der Verfasser anderen Quellen entnimmt, zu zweifeln, besteht kein Grund. Man muss die Arbeitsweise des Verfassers berücksichtigen: Er war von der mosaischen Herkunft der gottesdienstlichen Vorschriften des Pentateuch und ihrer dauernden Geltung überzeugt und hielt es daher für angebracht, mit Urkunden und Ordnungen einer späteren Zeit Vorgänge und Verhältnisse früherer Zeiten zu beschreiben. Daher sind die Bücher der Chronik vor allem für das Verständnis der Zeit des Verfassers bedeutsam. Er selbst will in erster Linie der religiösen Belehrung seiner Zeitgenossen dienen.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Adam, Set, Enosch,", heading: "Geschlechtsregister und Familienlisten: 1,1 - 9,44 | Die Nachkommen Adams bis Abraham: 1,1-27", footnotes: ["(1-27) Gen 5,1-32; 10,2-29; 11,10-27"] },
      { number: 2, text: "Kenan, Mahalalel, Jered," },
      { number: 3, text: "Henoch, Metuschelach, Lamech," },
      { number: 4, text: "Noach, Sem, Ham und Jafet." },
      { number: 5, text: "Die Söhne Jafets waren: Gomer, Magog, Madai, Jawan, Tubal, Meschech und Tiras." },
      { number: 6, text: "Die Söhne Gomers waren: Aschkenas, Rifat und Togarma." },
      { number: 7, text: "Die Söhne Jawans waren: Elischa, Tarschisch, die Kittäer und die Rodaniter." },
      { number: 8, text: "Die Söhne Hams waren: Kusch, Ägypten, Put und Kanaan." },
      { number: 9, text: "Die Söhne des Kusch waren: Seba, Hawila, Sabta, Ragma und Sabtecha; und die Söhne Ragmas waren: Saba und Dedan." },
      { number: 10, text: "Kusch zeugte Nimrod. Dieser wurde der erste Held auf der Erde." },
      { number: 11, text: "Ägypten zeugte die Luditer, die Anamiter, die Lehabiter, die Naftuhiter," },
      { number: 12, text: "die Patrositer und die Kasluhiter, von denen die Philister abstammen, ferner die Kaftoriter." },
      { number: 13, text: "Kanaan zeugte Sidon, seinen Erstgeborenen, und Het," },
      { number: 14, text: "ferner die Jebusiter, die Amoriter, die Girgaschiter," },
      { number: 15, text: "die Hiwiter, die Arkiter, die Siniter," },
      { number: 16, text: "die Arwaditer, die Zemariter und die Hamatiter." },
      { number: 17, text: "Die Söhne Sems waren: Elam, Assur, Arpachschad, Lud, Aram, Uz, Hul, Geter und Meschech." },
      { number: 18, text: "Arpachschad zeugte Schelach, Schelach zeugte Eber." },
      { number: 19, text: "Eber wurden zwei Söhne geboren; der eine hieß Peleg (Teilung), denn zu seiner Zeit wurde das Land geteilt; sein Bruder hieß Joktan." },
      { number: 20, text: "Joktan zeugte Almodad, Schelef, Hazarmawet, Jerach," },
      { number: 21, text: "Hadoram, Usal, Dikla," },
      { number: 22, text: "Obal, Abimaël, Saba," },
      { number: 23, text: "Ofir, Hawila und Jobab. Das alles waren Söhne Joktans." },
      { number: 24, text: "Sem, Arpachschad, Schelach,", footnotes: ["24-27: Der jeweils folgende Name bezeichnet den Sohn des vorausgehenden."] },
      { number: 25, text: "Eber, Peleg, Regu," },
      { number: 26, text: "Serug, Nahor, Terach," },
      { number: 27, text: "Abram, das ist Abraham." },
      { number: 28, text: "Die Söhne Abrahams waren Isaak und Ismael.", heading: "Die Nachkommen Abrahams: 1,28-42", footnotes: ["(28-42) Gen 25,1-4.12-16; 36,4-28", "28-42: Manche Namen dieser und der folgenden Listen sind durch Schreibfehler entstellt und in Gen sowie in G in anderer Form überliefert. In den folgenden Anmerkungen wird darauf nicht im Einzelnen eingegangen."] },
      { number: 29, text: "Das ist die Geschlechterfolge nach ihnen: Der Erstgeborene Ismaels war Nebajot; dann kamen Kedar, Adbeel, Mibsam," },
      { number: 30, text: "Mischma, Duma, Massa, Hadad, Tema," },
      { number: 31, text: "Jetur, Nafisch und Kedma. Das waren die Söhne Ismaels." },
      { number: 32, text: "Die Söhne Keturas, der Nebenfrau Abrahams: Sie gebar Simran, Jokschan, Medan, Midian, Jischbak und Schuach. Die Söhne Jokschans waren Saba und Dedan." },
      { number: 33, text: "Die Söhne Midians waren: Efa, Efer, Henoch, Abida und Eldaa. Sie alle waren Söhne Keturas." },
      { number: 34, text: "Abraham zeugte Isaak. Die Söhne Isaaks waren Esau und Israel." },
      { number: 35, text: "Die Söhne Esaus waren: Elifas, Reguël, Jëusch, Jalam und Korach." },
      { number: 36, text: "Die Söhne des Elifas waren: Teman, Omar, Zefo, Gatam, Kenas, Timna und Amalek." },
      { number: 37, text: "Die Söhne Reguëls waren: Nahat, Serach, Schamma und Misa." },
      { number: 38, text: "Die Söhne Seïrs waren: Lotan, Schobal, Zibon, Ana, Dischon, Ezer und Dischan." },
      { number: 39, text: "Die Söhne Lotans waren Hori und Hemam. Die Schwester Lotans war Timna." },
      { number: 40, text: "Die Söhne Schobals waren: Alwan, Manahat, Ebal, Schefi und Onam. Die Söhne Zibons waren Aja und Ana." },
      { number: 41, text: "Der Sohn Anas war Dischon. Die Söhne Dischons waren: Hemdan, Eschban, Jitran und Keran." },
      { number: 42, text: "Die Söhne Ezers waren Bilhan, Saawan und Akan. Die Söhne Dischons waren Uz und Aran." },
      { number: 43, text: "Die Könige, die in Edom regierten, bevor über die Israeliten ein König regierte, waren folgende: Bela, der Sohn Beors; seine Stadt hieß Dinhaba.", heading: "Die Herrscher in Edom: 1,43-54", footnotes: ["(43-54) Gen 36,31-43"] },
      { number: 44, text: "Als Bela starb, wurde König an seiner Stelle Jobab, der Sohn Serachs, aus Bozra." },
      { number: 45, text: "Als Jobab starb, wurde König an seiner Stelle Huscham aus dem Land der Temaniter." },
      { number: 46, text: "Als Huscham starb, wurde König an seiner Stelle Hadad, der Sohn Bedads, der Midian im Grünland von Moab schlug; seine Stadt hieß Awit." },
      { number: 47, text: "Als Hadad starb, wurde König an seiner Stelle Samla aus Masreka." },
      { number: 48, text: "Als Samla starb, wurde König an seiner Stelle Schaul aus Rehobot am Strom (Eufrat)." },
      { number: 49, text: "Als Schaul starb, wurde König an seiner Stelle Baal-Hanan, der Sohn Achbors." },
      { number: 50, text: "Als Baal-Hanan starb, wurde König an seiner Stelle Hadad; seine Stadt hieß Pagu. Seine Frau hieß Mehetabel; sie war die Tochter Matreds und die Enkelin Me-Sahabs." },
      { number: 51, text: "Als Hadad starb, regierten Häuptlinge in Edom: Häuptling Timna, Häuptling Alwa, Häuptling Jetet," },
      { number: 52, text: "Häuptling Oholibama, Häuptling Ela, Häuptling Pinon," },
      { number: 53, text: "Häuptling Kenas, Häuptling Teman, Häuptling Mibzar," },
      { number: 54, text: "Häuptling Magdiël, Häuptling Iram. Das waren die Häuptlinge Edoms." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Das waren die Söhne Israels: Ruben, Simeon, Levi, Juda, Issachar, Sebulon,", heading: "Die Söhne Jakobs: 2,1-2", footnotes: ["(1-2) Gen 35,23-26"] },
      { number: 2, text: "Dan, Josef, Benjamin, Naftali, Gad und Ascher." },
      { number: 3, text: "Die Söhne Judas waren: Er, Onan und Schela. Diese drei wurden ihm von der Tochter Schuas, der Kanaaniterin, geboren. Aber Er, der Erstgeborene Judas, missfiel dem Herrn und so ließ ihn der Herr sterben.", heading: "Der Stamm Juda: 2,3 - 4,23 | Die Nachkommen Judas: 2,3-9", footnotes: ["(3-9) Gen 38,3-7.27-30", "3-14: Die von Juda zu David führende Linie begegnet auch im Stammbaum Jesu (vgl. Mt 1,3-6 und Lk 3,32f)."] },
      { number: 4, text: "Judas Schwiegertochter Tamar gebar Perez und Serach. Juda hatte insgesamt fünf Söhne." },
      { number: 5, text: "Die Söhne des Perez waren Hezron und Hamul,", footnotes: ["Gen 46,12"] },
      { number: 6, text: "und die Söhne Serachs: Simri, Etan, Heman, Kalkol und Darda, insgesamt fünf." },
      { number: 7, text: "Der Sohn Simris war Karmi und der Sohn Karmis Achan. Dieser brachte Unglück über Israel, da er sich an der geweihten Beute vergriff.", footnotes: ["Jos 7"] },
      { number: 8, text: "Der Sohn Etans war Asarja." },
      { number: 9, text: "Dem Hezron wurden die Söhne Jerachmeel, Ram und Kaleb geboren." },
      { number: 10, text: "Ram zeugte Amminadab, Amminadab zeugte Nachschon, den Anführer der Söhne Judas,", heading: "Die Nachkommen Rams: 2,10-17", footnotes: ["(10-15) Rut 4,19-22"] },
      { number: 11, text: "Nachschon zeugte Salmon, Salmon zeugte Boas," },
      { number: 12, text: "Boas zeugte Obed, Obed zeugte Isai," },
      { number: 13, text: "Isai zeugte Eliab als Erstgeborenen, als zweiten Abinadab, als dritten Schima," },
      { number: 14, text: "als vierten Netanel, als fünften Raddai," },
      { number: 15, text: "als sechsten Ozem, als siebten David." },
      { number: 16, text: "Ihre Schwestern waren Zeruja und Abigajil. Die Söhne der Zeruja waren Abischai, Joab und Asaël, diese drei.", footnotes: ["Die Söhne der Zeruja spielen eine wichtige Rolle in der Geschichte Davids. Joab war Befehlshaber des Heeres Davids."] },
      { number: 17, text: "Abigajil gebar Amasa. Der Vater Amasas war der Ismaeliter Jeter." },
      { number: 18, text: "Kaleb, der Sohn Hezrons, zeugte mit seiner Frau Asuba die Jeriot. Deren Söhne waren Jescher, Schobab und Ardon.", heading: "Die Nachkommen Kalebs: 2,18-24" },
      { number: 19, text: "Als Asuba starb, heiratete Kaleb Efrata, die ihm den Hur gebar." },
      { number: 20, text: "Hur zeugte Uri und Uri zeugte Bezalel.", footnotes: ["Bezalel war der Künstler, der unter Mose das heilige Zelt mit der Bundeslade und den heiligen Geräten angefertigt hat (vgl. Ex 36 - 38)."] },
      { number: 21, text: "Danach ging Hezron zur Tochter Machirs, des Vaters Gileads. Er war sechzig Jahre alt, als er sie nahm, und sie gebar ihm Segub." },
      { number: 22, text: "Segub zeugte Jaïr. Dieser besaß dreiundzwanzig Städte im Land Gilead.", footnotes: ["Ri 10,3"] },
      { number: 23, text: "Doch die Geschuriter und Aramäer nahmen die Zeltdörfer Jaïrs sowie Kenat und seine Tochterstädte weg, insgesamt sechzig Städte. Sie alle hatten den Nachkommen Machirs, des Vaters Gileads, gehört." },
      { number: 24, text: "Nach dem Tod Hezrons kam Kaleb zu Efrata. Eine Frau Hezrons war Abija; sie gebar ihm Aschhur, den Vater Tekoas." },
      { number: 25, text: "Die Söhne Jerachmeels, des Erstgeborenen Hezrons, waren: Ram, der Erstgeborene, ferner Buna, Oren, Ozem und Ahija.", heading: "Die Nachkommen Jerachmeels: 2,25-41" },
      { number: 26, text: "Jerachmeel hatte noch eine andere Frau namens Atara. Sie war die Mutter Onams." },
      { number: 27, text: "Die Söhne Rams, des Erstgeborenen Jerachmeels, waren: Maaz, Jamin und Eker." },
      { number: 28, text: "Die Söhne Onams waren Schammai und Jada und die Söhne Schammais Nadab und Abischur." },
      { number: 29, text: "Die Frau Abischurs hieß Abihajil. Sie gebar ihm Achban und Molid." },
      { number: 30, text: "Die Söhne Nadabs waren Seled und Appajim. Seled starb ohne Nachkommen." },
      { number: 31, text: "Der Sohn Appajims war Jischi, der Sohn Jischis Scheschan und die Tochter Scheschans Achlai.", footnotes: ["die Tochter: sinngemäß korr., vgl. V. 34."] },
      { number: 32, text: "Die Söhne Jadas, des Bruders von Schammai, waren Jeter und Jonatan. Jeter starb ohne Nachkommen." },
      { number: 33, text: "Die Söhne Jonatans waren Pelet und Sasa. Das waren die Nachkommen Jerachmeels." },
      { number: 34, text: "Scheschan hatte keine Söhne, sondern nur Töchter. Scheschan hatte einen ägyptischen Sklaven namens Jarha." },
      { number: 35, text: "Diesem gab Scheschan seine Tochter zur Frau und sie gebar ihm Attai." },
      { number: 36, text: "Attai zeugte Natan, Natan zeugte Sabad," },
      { number: 37, text: "Sabad zeugte Eflal, Eflal zeugte Obed," },
      { number: 38, text: "Obed zeugte Jehu, Jehu zeugte Asarja," },
      { number: 39, text: "Asarja zeugte Helez, Helez zeugte Elasa," },
      { number: 40, text: "Elasa zeugte Sismai, Sismai zeugte Schallum," },
      { number: 41, text: "Schallum zeugte Jekamja und Jekamja zeugte Elischama." },
      { number: 42, text: "Die Söhne Kalebs, des Bruders Jerachmeels, waren: Mescha, sein Erstgeborener - er war der Vater Sifs -, und sein zweiter Sohn Marescha, der Vater Hebrons.", heading: "Die zweite Liste der Nachkommen Kalebs: 2,42-55" },
      { number: 43, text: "Die Söhne Hebrons waren: Korach, Tappuach, Rekem und Schema." },
      { number: 44, text: "Schema zeugte Raham, den Vater Jorkoams, und Rekem zeugte Schammai." },
      { number: 45, text: "Der Sohn Schammais war Maon und Maon war der Vater von Bet-Zur." },
      { number: 46, text: "Efa, die Nebenfrau Kalebs, gebar Haran, Moza und Gases. Haran zeugte Jahdai." },
      { number: 47, text: "Die Söhne Jahdais waren: Regem, Jotam, Geschan, Pelet, Efa und Schaaf." },
      { number: 48, text: "Kalebs Nebenfrau Maacha gebar Scheber und Tirhana." },
      { number: 49, text: "Sie gebar auch Schaaf, den Vater Madmannas, sowie Schewa, den Vater Machbenas und Gibeas. Die Tochter Kalebs war Achsa.", footnotes: ["Jos 15,16"] },
      { number: 50, text: "Das waren die Nachkommen Kalebs. Die Söhne Hurs, des Erstgeborenen der Efrata, waren: Schobal, der Vater von Kirjat-Jearim,", footnotes: ["Zu Hur, dem Sohn der Efrata, vgl. 2,19."] },
      { number: 51, text: "Salmon, der Vater von Betlehem, und Haref, der Vater von Bet-Gader." },
      { number: 52, text: "Schobal, der Vater von Kirjat-Jearim, hatte als Söhne: Reaja und Hazi, den Manahatiter." },
      { number: 53, text: "Die Sippenverbände von Kirjat-Jearim waren die Jeteriter, die Putiter, die Schumatiter und die Mischraïter. Von ihnen zweigten ab die Zoraïter und die Eschtaoliter." },
      { number: 54, text: "Die Söhne Salmons waren: Betlehem, die Netofatiter, Atrot-Bet-Joab, die Hälfte der Manahatiter und der Zoraïter." },
      { number: 55, text: "Die Sippenverbände von Sofer, die Einwohner von Jabez, die Tiratiter, Schimatiter und Suchatiter waren Keniter, die von Hammat, dem Vater von Bet-Rechab, stammten.", footnotes: ["Die Keniter waren ein midianitischer Stamm, der sich z. T. an die Israeliten anschloss."] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Das waren die Söhne Davids, die ihm in Hebron geboren wurden: Sein Erstgeborener war Amnon von Ahinoam aus Jesreel, der zweite Daniel von Abigajil aus Karmel,", heading: "Die Nachkommen Davids: 3,1-24", footnotes: ["(1-4) 2 Sam 3,2-5"] },
      { number: 2, text: "der dritte Abschalom, der Sohn der Maacha, der Tochter des Königs Talmai von Geschur, der vierte Adonija, der Sohn der Haggit," },
      { number: 3, text: "der fünfte Schefatja von Abital, der sechste Jitream von seiner Frau Egla." },
      { number: 4, text: "Sechs wurden ihm in Hebron geboren. Er regierte dort sieben Jahre und sechs Monate. Dreiunddreißig Jahre regierte er in Jerusalem." },
      { number: 5, text: "Und diese wurden ihm in Jerusalem geboren: Schima, Schobab, Natan, Salomo - diese vier waren Söhne der Batseba, der Tochter Ammiëls -,", footnotes: ["(5-8) 2 Sam 5,14-16"] },
      { number: 6, text: "ferner Jibhar, Elischua, Elifelet," },
      { number: 7, text: "Nogah, Nefeg, Jafia," },
      { number: 8, text: "Elischama, Eljada und Elifelet, insgesamt neun." },
      { number: 9, text: "Das ist die Gesamtheit der Söhne Davids, ohne die Söhne der Nebenfrauen. Ihre Schwester war Tamar.", footnotes: ["2 Sam 13,1-22"] },
      { number: 10, text: "Der Sohn Salomos war Rehabeam, dessen Sohn war Abija, dessen Sohn Asa, dessen Sohn Joschafat,", footnotes: ["(10-19) Mt 1,7-12", "10-16: Die Liste nennt aus jeder Generation nur den Inhaber des Königtums. Die Namen sind auch im Stammbaum Jesu enthalten (vgl. Mt 1,6-11)."] },
      { number: 11, text: "dessen Sohn Joram, dessen Sohn Ahasja, dessen Sohn Joasch," },
      { number: 12, text: "dessen Sohn Amazja, dessen Sohn Asarja, dessen Sohn Jotam," },
      { number: 13, text: "dessen Sohn Ahas, dessen Sohn Hiskija, dessen Sohn Manasse," },
      { number: 14, text: "dessen Sohn Amon, dessen Sohn Joschija." },
      { number: 15, text: "Die Söhne Joschijas waren: der Erstgeborene Johanan, der zweite Jojakim, der dritte Zidkija, der vierte Schallum." },
      { number: 16, text: "Jojakims Söhne waren: Jojachin und Zidkija." },
      { number: 17, text: "Die Söhne Jojachins, des Gefangenen, waren: Schealtiël,", footnotes: ["Jojachin wurde 597 v. Chr. als Gefangener nach Babel gebracht (vgl. 2 Kön 24,12)."] },
      { number: 18, text: "Malkiram, Pedaja, Schenazzar, Jekamja, Hoschama und Nedabja." },
      { number: 19, text: "Die Söhne Pedajas waren Serubbabel und Schimi. Die Söhne Serubbabels waren Meschullam und Hananja. Ihre Schwester war Schelomit.", footnotes: ["Serubbabel wird hier als Sohn Pedajas, des dritten Sohns, sonst aber (Esra 3,2; Hag 1,1; Mt 1,12) als Sohn Schealtiëls, des Erstgeborenen Jojachins, bezeichnet."] },
      { number: 20, text: "Meschullams Söhne waren: Haschuba, Ohel, Berechja, Hasadja und Juschab-Hesed, insgesamt fünf." },
      { number: 21, text: "Die Söhne Hananjas waren: Pelatja, Jeschaja, Refaja, Arnan, Obadja und Schechanja.", footnotes: ["Nach dem korrigierten Text sind alle hier genannten Söhne Hananjas untereinander Brüder. Demnach sind fünf Generationen nach Serubbabel genannt; das ist ein wichtiger Hinweis auf die Entstehungszeit des Buchs, vgl. die Einleitung."] },
      { number: 22, text: "Die Söhne Schechanjas waren Schemaja, Hattusch, Jigal, Bariach, Nearja und Schafat, insgesamt sechs." },
      { number: 23, text: "Die Söhne Nearjas waren: Eljoënai, Hiskija und Asrikam, insgesamt drei." },
      { number: 24, text: "Die Söhne Eljoënais waren: Hodawja, Eljaschib, Pelaja, Akkub, Johanan, Delaja und Anani, insgesamt sieben." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Die Söhne Judas waren: Perez, Hezron, Kaleb, Hur und Schobal.", heading: "Einzelne Familien des Stammes Juda:", footnotes: ["Der jeweils folgende Name bezeichnet den Sohn des vorhergehenden."] },
      { number: 2, text: "Reaja, der Sohn Schobals, zeugte Jahat und Jahat zeugte Ahumai und Lahad. Das waren die Sippen der Zoraïter." },
      { number: 3, text: "Und das waren die Söhne Etams: Jesreel, Jischma und Jidbasch. Der Name ihrer Schwester war Hazlelponi." },
      { number: 4, text: "Penuel war der Vater Gedors und Eser der Vater Huschas. Das waren die Nachkommen Hurs, des Erstgeborenen der Efrata, des Vaters von Betlehem." },
      { number: 5, text: "Aschhur, der Vater Tekoas, hatte zwei Frauen, Hela und Naara." },
      { number: 6, text: "Naara gebar ihm Ahusam, Hefer, Temni und Ahaschtari. Das waren die Söhne der Naara." },
      { number: 7, text: "Die Söhne der Hela waren: Zeret, Zohar und Etnan." },
      { number: 8, text: "Koz zeugte Anub, Zobeba, ferner die Sippen Aharhels, des Sohnes Harums." },
      { number: 9, text: "Jabez war angesehener als seine Brüder. Seine Mutter hatte ihn Jabez genannt, denn sie sagte: Ich habe ihn unter Beschwerden geboren.", footnotes: ["Jabez erinnert an das Wort ozeb (= Beschwerde)."] },
      { number: 10, text: "Doch Jabez rief zum Gott Israels und sprach: Möchtest du mich segnen und mein Gebiet erweitern. Möchte deine Hand mit mir sein, dass du mich freimachst von Unheil und ich ohne Beschwerden bleibe. Und Gott erfüllte seine Bitte." },
      { number: 11, text: "Kelub, der Bruder Schuhas, zeugte Mehir, den Vater Eschtons." },
      { number: 12, text: "Eschton zeugte Bet-Rafa, Paseach und Tehinna, den Vater von Ir-Nahasch. Das sind die Männer von Recha." },
      { number: 13, text: "Die Söhne des Kenas waren Otniël und Seraja, die Söhne Otniëls Hatat und Meonotai." },
      { number: 14, text: "Meonotai zeugte Ofra. Seraja zeugte Joab, den Vater von Ge-Haraschim (Tal der Handwerker); denn sie waren Handwerker." },
      { number: 15, text: "Die Söhne Kalebs, des Sohnes Jefunnes, waren: Iru, Ela und Naam. Der Sohn Elas war Kenas." },
      { number: 16, text: "Die Söhne Jehallelels waren: Sif, Sifa, Tirja und Asarel." },
      { number: 17, text: "Die Söhne Esras waren: Jeter, Mered, Efer und Jalon. (Bitja, die ägyptische Frau Mereds,) gebar Mirjam, Schammai und Jischbach, den Vater von Eschtemoa.", footnotes: ["Ergänzt aus V. 18."] },
      { number: 18, text: "Seine judäische Frau gebar: Jered, den Vater von Gedor, Heber, den Vater von Socho, und Jekutiël, den Vater von Sanoach. Das waren die Söhne der Bitja, der Tochter des Pharao, die Mered geheiratet hatte," },
      { number: 19, text: "und die Söhne seiner judäischen Frau, der Schwester Nahams, des Vaters von Keïla, dem Garmiter, und von Eschtemoa, dem Maachatiter." },
      { number: 20, text: "Die Söhne Schimons waren: Amnon, Rinna, Ben-Hanan und Tilon. Die Söhne Jischis waren Sohet und Ben-Sohet." },
      { number: 21, text: "Die Söhne Schelas, des Sohnes Judas, waren: Er, der Vater Lechas, Lada, der Vater Mareschas, sowie die Sippen des Hauses der Byssusbearbeiter von Bet-Aschbea," },
      { number: 22, text: "ferner Jokim und die Männer von Koseba, sowie Joasch und Saraf, die Moab in Besitz genommen hatten, aber wieder nach Betlehem zurückgekehrt waren - die Berichte sind altüberliefert." },
      { number: 23, text: "Sie sind die Töpfer und Einwohner von Netaïm und Gedera und wohnen dort im Dienst des Königs." },
      { number: 24, text: "¥Die Söhne Simeons waren: Jemuël, Jamin, Jarib, Serach und Schaul.", heading: "Der Stamm Simeon: 4,24-43", footnotes: ["Gen 46,10; Num 26,12-14"] },
      { number: 25, text: "Dessen Sohn war Schallum, dessen Sohn Mibsam und dessen Sohn Mischma." },
      { number: 26, text: "Der Sohn Mischmas war Hammuël, dessen Sohn Sakkur und dessen Sohn Schimi." },
      { number: 27, text: "Schimi hatte sechzehn Söhne und sechs Töchter. Doch seine Brüder waren nicht kinderreich und alle ihre Sippen erreichten nicht die Zahl der Söhne Judas." },
      { number: 28, text: "Sie wohnten in Beerscheba, Molada, Hazar-Schual,", footnotes: ["Jos 19,2-8"] },
      { number: 29, text: "Baala, Ezem, Eltolad," },
      { number: 30, text: "Betuël, Horma, Ziklag," },
      { number: 31, text: "Bet-Markabot, Hazar-Susim, Bet-Biri und Schaarajim. Das waren ihre Städte mit den dazugehörenden Gehöften, bis David König wurde," },
      { number: 32, text: "dazu noch Etam, Ajin, Rimmon, Tochen und Aschan, fünf Städte" },
      { number: 33, text: "mit all ihren Gehöften im Umkreis dieser Städte bis nach Baal hin. Das waren ihre Wohnsitze und sie hatten ihre eigenen Stammeslisten." },
      { number: 34, text: "Meschobab, Jamlech, Joscha, der Sohn Amazjas," },
      { number: 35, text: "Joël, Jehu, der Sohn Joschibjas, des Sohnes Serajas, des Sohnes Asiëls," },
      { number: 36, text: "Eljoënai, Jaakoba, Jeschohaja, Asaja, Adiël, Jesimiël, Benaja," },
      { number: 37, text: "Sisa, der Sohn Schifis, des Sohnes Allons, des Sohnes Jedajas, des Sohnes Schimris, des Sohnes Schemajas:" },
      { number: 38, text: "Diese namentlich Genannten waren führende Männer in ihren Sippenverbänden. Ihre Großfamilien hatten sich stark vermehrt." },
      { number: 39, text: "Daher zogen sie, um Weideplätze für ihre Schafe und Ziegen zu suchen, zum Osten des Tals, wo der Weg nach Gedor abzweigt." },
      { number: 40, text: "Sie fanden fette und gute Weideplätze. Das Land war nach allen Seiten hin ausgedehnt, ruhig und friedlich; denn die früheren Bewohner gehörten zu den Hamiten." },
      { number: 41, text: "So kamen diese mit Namen Aufgezeichneten in den Tagen des Königs Hiskija von Juda, zerstörten ihre Zelte und erschlugen die Mëuniter, die sich dort fanden. Sie weihten sie für immer dem Untergang und ließen sich an ihrer Stelle nieder; denn es gab dort Weideplätze für ihre Schafe und Ziegen." },
      { number: 42, text: "Von den Nachkommen Simeons zogen fünfhundert Mann in das Gebirge Seïr. Pelatja, Nearja, Refaja und Usiël, die Söhne Jischis, standen an ihrer Spitze." },
      { number: 43, text: "Sie schlugen die Restbevölkerung, die von den Amalekitern noch vorhanden war, und wohnen dort bis zum heutigen Tag." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Die Söhne Rubens, des Erstgeborenen Israels: Ruben war der Erstgeborene. Da er aber das Bett seines Vaters entweiht hatte, wurde sein Erstgeburtsrecht den Söhnen Josefs, des Sohnes Israels, gegeben. Doch richten sich die Stammeslisten nicht nach dem Erstgeburtsrecht;", heading: "Der Stamm Ruben: 5,1-10", footnotes: ["Gen 35,22; 49,3f", "Der Vers will erklären, warum das Buch den Stamm Juda und nicht die Stämme Ruben oder Josef an erster Stelle bringt: Es geschah, weil ihm das Königtum zufiel."] },
      { number: 2, text: "Juda erlangte nämlich die Herrschaft über seine Brüder und einer aus ihm wurde der Fürst, obwohl das Erstgeburtsrecht bei Josef war." },
      { number: 3, text: "Die Söhne Rubens, des Erstgeborenen Israels, waren: Henoch, Pallu, Hezron und Karmi.", footnotes: ["Gen 46,9"] },
      { number: 4, text: "Der Sohn Joëls war Schemaja, dessen Sohn Gog, dessen Sohn Schimi," },
      { number: 5, text: "dessen Sohn Micha, dessen Sohn Reaja, dessen Sohn Baal," },
      { number: 6, text: "dessen Sohn Beera, den Tiglat-Pileser, der König von Assur, verschleppte. Er war der Anführer der Rubeniter." },
      { number: 7, text: "Seine Brüder wurden, als sie sich nach ihren Geschlechterfolgen in die Stammeslisten eintragen ließen, seinen Familien zugezählt. Der erste war Jëiël, ihm folgte Secharja." },
      { number: 8, text: "Bela war der Sohn des Asas, des Sohnes Schemas, des Sohnes Joëls. Die Rubeniter wohnten in Aroër bis Nebo und Baal-Meon.", footnotes: ["Die Städte liegen im Süden des Ostjordanlands."] },
      { number: 9, text: "Ostwärts wohnten sie bis an den Rand der Steppe, die sich vom Eufrat her erstreckt; denn sie besaßen große Herden im Land Gilead." },
      { number: 10, text: "In den Tagen Sauls führten sie Krieg mit den Hagaritern. Diese fielen in ihre Hand und sie besetzten ihre Zelte auf der ganzen Ostseite von Gilead." },
      { number: 11, text: "Die Nachkommen Gads wohnten ihnen gegenüber im Gebiet des Baschan bis Salcha.", heading: "Der Stamm Gad: 5,11-17" },
      { number: 12, text: "Ihr Oberhaupt war Joël; Schafam war der Zweite, dazu kamen Janai und Schafat; sie wohnten im Baschan." },
      { number: 13, text: "Ihre Brüder mit ihren Großfamilien waren Michael, Meschullam, Scheba, Jorai, Jakan, Sia und Eber, insgesamt sieben." },
      { number: 14, text: "Das waren die Söhne Abihajils, des Sohnes Huris, des Sohnes Jaroachs, des Sohnes Gileads, des Sohnes Michaels, des Sohnes Jeschischais, des Sohnes Jachdos, des Sohnes des Bus: . . .", footnotes: ["Die Namen der Söhne fehlen in H."] },
      { number: 15, text: "Ahi, der Sohn Abdiëls, des Sohnes Gunis, war Haupt ihrer Großfamilien." },
      { number: 16, text: "Sie wohnten in Gilead, im Baschan und seinen Tochterstädten und an allen Weideflächen Scharons bis zu ihren Ausläufern." },
      { number: 17, text: "In den Tagen Jotams, des Königs von Juda, und in den Tagen Jerobeams, des Königs von Israel, wurden alle in die Stammeslisten eingetragen." },
      { number: 18, text: "Die Krieger von Ruben, Gad und dem halben Stamm Manasse, Männer, die Schild und Schwert trugen, den Bogen spannten und im Kampf geübt waren, vierundvierzigtausendsiebenhundertsechzig Mann, die im Heer ausrückten,", heading: "Der Krieg mit Nachbarstämmen: 5,18-22" },
      { number: 19, text: "führten Krieg gegen die Hagariter und gegen Jetur, Nafisch und Nodab." },
      { number: 20, text: "Sie waren siegreich und konnten die Hagariter und deren Verbündete in ihre Gewalt bringen; denn sie schrien im Kampf zu Gott und dieser ließ sich erbitten, da sie auf ihn vertrauten." },
      { number: 21, text: "So führten sie deren Besitz weg: 50000 Kamele, 250000 Schafe und 2000 Esel, dazu 100000 Personen." },
      { number: 22, text: "Viele Feinde wurden erschlagen und fielen, denn es war ein Krieg Gottes. Die Israeliten siedelten sich an ihrer Stelle an und blieben dort bis zur Verschleppung." },
      { number: 23, text: "Die Angehörigen des halben Stammes Manasse wohnten in dem Land, das vom Baschan bis Baal-Hermon, zum Senir und zum Hermongebirge reicht. Sie waren sehr zahlreich.", heading: "Der Stamm Ost-Manasse: 5,23-26" },
      { number: 24, text: "Und das waren die Häupter ihrer Großfamilien: Efer, Jischi, Eliël, Asriël, Jirmeja, Hodawja und Jachdiël. Sie waren tapfere Krieger, Männer von Namen, Häupter ihrer Großfamilien." },
      { number: 25, text: "Doch wurden sie dem Gott ihrer Väter untreu und gaben sich den Göttern der Landesbevölkerung hin, die Gott vor ihnen vertrieben hatte." },
      { number: 26, text: "Darum erweckte der Gott Israels den Geist Puls, des Königs von Assur, das heißt den Geist Tiglat-Pilesers, des Königs von Assur. Dieser führte die Rubeniter, die Gaditer und den halben Stamm Manasse in die Verbannung und brachte sie nach Halach, an den Habor, nach Hara und an den Fluss von Gosan. Dort sind sie bis zum heutigen Tag.", footnotes: ["2 Kön 17,6", "zu Pul: vgl. die Anmerkung zu 2 Kön 15,19."] },
      { number: 27, text: "Die Söhne Levis waren: Gerschon, Kehat und Merari.", heading: "Der Stamm Levi: 5,27-41", footnotes: ["(27-30) Ex 6,16-25"] },
      { number: 28, text: "Die Söhne Kehats waren: Amram, Jizhar, Hebron und Usiël." },
      { number: 29, text: "Die Kinder Amrams waren: Aaron, Mose und Mirjam. Die Söhne Aarons waren: Nadab, Abihu, Eleasar und Itamar." },
      { number: 30, text: "Eleasar zeugte Pinhas, Pinhas zeugte Abischua," },
      { number: 31, text: "Abischua zeugte Bukki, Bukki zeugte Usi," },
      { number: 32, text: "Usi zeugte Serachja, Serachja zeugte Merajot," },
      { number: 33, text: "Merajot zeugte Amarja, Amarja zeugte Ahitub," },
      { number: 34, text: "Ahitub zeugte Zadok, Zadok zeugte Ahimaaz," },
      { number: 35, text: "Ahimaaz zeugte Asarja, Asarja zeugte Johanan," },
      { number: 36, text: "Johanan zeugte Asarja - dieser war es, der im Tempel, den Salomo in Jerusalem erbaute, das Priesteramt verwaltete -,", footnotes: ["Die Zwischenbemerkung ist an falscher Stelle eingereiht. Sie gehört zu dem in V. 35 genannten Asarja (vgl. 1 Kön 4,2)."] },
      { number: 37, text: "Asarja zeugte Amarja, Amarja zeugte Ahitub," },
      { number: 38, text: "Ahitub zeugte Zadok, Zadok zeugte Schallum," },
      { number: 39, text: "Schallum zeugte Hilkija, Hilkija zeugte Asarja," },
      { number: 40, text: "Asarja zeugte Seraja, Seraja zeugte Jozadak." },
      { number: 41, text: "Jozadak zog fort, als der Herr Juda und Jerusalem durch Nebukadnezzar in die Verbannung führte." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Die Söhne Levis waren: Gerschon, Kehat und Merari.", heading: "Die Nachkommen der drei Söhne Levis: 6,1-15" },
      { number: 2, text: "Die Söhne Gerschons hießen Libni und Schimi." },
      { number: 3, text: "Kehats Söhne waren: Amram, Jizhar, Hebron und Usiël." },
      { number: 4, text: "Die Söhne Meraris waren: Machli und Muschi. Das sind die Sippen der Leviten nach ihren Stammvätern:" },
      { number: 5, text: "Der Sohn Gerschons war Libni, dessen Sohn Jahat, dessen Sohn Simma," },
      { number: 6, text: "dessen Sohn Joach, dessen Sohn Iddo, dessen Sohn Serach und dessen Sohn Etni." },
      { number: 7, text: "Der Sohn Kehats war Amminadab, dessen Sohn Korach, dessen Sohn Assir," },
      { number: 8, text: "dessen Sohn Elkana, dessen Sohn Abiasaf, dessen Sohn Assir," },
      { number: 9, text: "dessen Sohn Tahat, dessen Sohn Uriël, dessen Sohn Usija und dessen Sohn Schaul." },
      { number: 10, text: "Die Söhne Elkanas waren Amasai und Ahimot." },
      { number: 11, text: "Der Sohn Elkanas war Elkana, dessen Sohn Zuf, dessen Sohn Tohu,", footnotes: ["1 Sam 1,1"] },
      { number: 12, text: "dessen Sohn Eliab, dessen Sohn Jeroham, dessen Sohn Elkana, dessen Sohn Samuel." },
      { number: 13, text: "Die Söhne Samuels waren: Joël, der Erstgeborene, und der zweite war Abija.", footnotes: ["1 Sam 8,2", "Text korr. nach 1 Sam 8,2."] },
      { number: 14, text: "Der Sohn Meraris war Machli, dessen Sohn Libni, dessen Sohn Schimi, dessen Sohn Usa," },
      { number: 15, text: "dessen Sohn Schima, dessen Sohn Haggija und dessen Sohn Asaja." },
      { number: 16, text: "Diese sind es, die David zur Pflege des Gesangs im Haus des Herrn bestellte, nachdem die Lade eine bleibende Stätte gefunden hatte.", heading: "Abstammung der Sänger: 6,16-32", footnotes: ["David ließ die Lade nach Jerusalem bringen und in einem Zelt aufstellen. Unter Salomo erhielt sie im Tempel ihre bleibende Stätte."] },
      { number: 17, text: "Sie versahen ihren Dienst als Sänger vor der Wohnstätte des Offenbarungszeltes, bis Salomo das Haus des Herrn in Jerusalem baute. Gemäß ihren Satzungen standen sie in ihrem Dienst.", footnotes: ["Nach 1 Chr 21,29 und 2 Chr 1,3 befand sich das heilige Zelt (ohne die Bundeslade) zur Zeit Davids in Gibeon; dort wurden auch die Opfer unter Leitung des Priesters Zadok dargebracht (vgl. 1 Chr 16,39). Der Kult hörte dort auf, als Salomo das Zelt nach Jerusalem bringen ließ."] },
      { number: 18, text: "Diese sind es, die mit ihren Söhnen den Dienst besorgten: Von den Nachkommen der Kehatiter: Heman, der Sänger, der Sohn Joëls, des Sohnes Samuels," },
      { number: 19, text: "des Sohnes Elkanas, des Sohnes Jerohams, des Sohnes Eliëls, des Sohnes Tohus,", footnotes: ["1 Sam 1,1"] },
      { number: 20, text: "des Sohnes Zufs, des Sohnes Elkanas, des Sohnes Mahats, des Sohnes Amasais," },
      { number: 21, text: "des Sohnes Elkanas, des Sohnes Joëls, des Sohnes Asarjas, des Sohnes Zefanjas," },
      { number: 22, text: "des Sohnes Tahats, des Sohnes Assirs, des Sohnes Abiasafs, des Sohnes Korachs," },
      { number: 23, text: "des Sohnes Jizhars, des Sohnes Kehats, des Sohnes Levis, des Sohnes Israels." },
      { number: 24, text: "Sein Bruder war Asaf, der zu seiner Rechten stand: Er war der Sohn Berechjas, des Sohnes Schimas," },
      { number: 25, text: "des Sohnes Michaels, des Sohnes Maasejas, des Sohnes Malkijas," },
      { number: 26, text: "des Sohnes Etnis, des Sohnes Serachs, des Sohnes Adajas," },
      { number: 27, text: "des Sohnes Etans, des Sohnes Simmas, des Sohnes Schimis," },
      { number: 28, text: "des Sohnes Jahats, des Sohnes Gerschoms, des Sohnes Levis." },
      { number: 29, text: "Die Söhne Meraris, ihre Brüder, standen zur Linken: Etan, der Sohn Kischis, des Sohnes Abdis, des Sohnes Malluchs," },
      { number: 30, text: "des Sohnes Haschabjas, des Sohnes Amazjas, des Sohnes Hilkijas," },
      { number: 31, text: "des Sohnes Amzis, des Sohnes Banis, des Sohnes Schemers," },
      { number: 32, text: "des Sohnes Machlis, des Sohnes Muschis, des Sohnes Meraris, des Sohnes Levis." },
      { number: 33, text: "Ihre Brüder, die Leviten, waren für den gesamten Dienst an der Wohnstätte des Hauses Gottes bestimmt.", heading: "Die Aufgaben der Leviten und Priester: 6,33-34" },
      { number: 34, text: "Aaron aber und seine Söhne verbrannten die Opfer auf dem Brandopferaltar und dem Rauchopferaltar. Sie besorgten jeden Dienst am Allerheiligsten und vollzogen die Sühne für Israel, genau wie Mose, der Knecht Gottes, angeordnet hatte." },
      { number: 35, text: "Das sind die Nachkommen Aarons: Sein Sohn war Eleasar, dessen Sohn Pinhas, dessen Sohn Abischua,", heading: "Die Nachkommen Aarons: 6,35-38", footnotes: ["(35-38) 5,29-34"] },
      { number: 36, text: "dessen Sohn Bukki, dessen Sohn Usi, dessen Sohn Serachja," },
      { number: 37, text: "dessen Sohn Merajot, dessen Sohn Amarja, dessen Sohn Ahitub," },
      { number: 38, text: "dessen Sohn Zadok, dessen Sohn Ahimaaz." },
      { number: 39, text: "Das sind ihre Wohnsitze mit ihren Zeltlagern auf ihrem Gebiet: Den Nachkommen Aarons von der Sippe der Kehatiter, auf die das erste Los gefallen war,", heading: "Die Wohnsitze der Leviten: 6,39-66", footnotes: ["(39-66) Jos 21,3-39", "39-66: Einige Ortsnamen fehlen in H und wurden aus Jos 21 ergänzt."] },
      { number: 40, text: "gaben sie Hebron im Land Juda mit seinen Weideflächen ringsum." },
      { number: 41, text: "Das Ackerland der Stadt aber und ihre Gehöfte gaben sie Kaleb, dem Sohn Jefunnes." },
      { number: 42, text: "Sie gaben den Nachkommen Aarons die Asylstadt Hebron, ferner Libna mit seinen Weideflächen, Jattir und Eschtemoa mit seinen Weideflächen,", footnotes: ["Eine Asylstadt ist ein Ort, wohin ein Totschläger fliehen konnte, um vor dem Bluträcher sicher zu sein (vgl. Dtn 19,1-13)."] },
      { number: 43, text: "Holon mit seinen Weideflächen, Debir mit seinen Weideflächen," },
      { number: 44, text: "Aschan mit seinen Weideflächen, (Jutta mit seinen Weideflächen,) Bet-Schemesch mit seinen Weideflächen.", footnotes: ["44f.54: Ergänzt aus Jos 21,16f.23."] },
      { number: 45, text: "Vom Stamm Benjamin gaben sie ihnen: (Gibeon mit seinen Weideflächen,) Geba mit seinen Weideflächen, Alemet mit seinen Weideflächen, Anatot mit seinen Weideflächen. Insgesamt gehörten ihnen dreizehn Städte mit ihren Weideflächen." },
      { number: 46, text: "Den übrigen Sippen von den Nachkommen Kehats gaben sie durch das Los zehn Städte vom halben Stamm Manasse." },
      { number: 47, text: "Den Nachkommen Gerschoms gaben die Israeliten entsprechend ihren Sippen aus dem Stamm Issachar, aus dem Stamm Ascher, aus dem Stamm Naftali und aus dem Stamm Manasse im Baschan dreizehn Städte." },
      { number: 48, text: "Den Nachkommen Meraris gaben sie entsprechend ihren Sippen aus dem Stamm Ruben, aus dem Stamm Gad und aus dem Stamm Sebulon durch das Los zwölf Städte." },
      { number: 49, text: "Die Israeliten gaben den Leviten die Städte samt ihren Weideflächen." },
      { number: 50, text: "Durch das Los gaben sie ihnen aus dem Stamm Juda, aus dem Stamm Simeon und aus dem Stamm Benjamin diese Städte, die sie namentlich bezeichneten." },
      { number: 51, text: "Die Angehörigen der kehatitischen Sippen erhielten durch das Los vom Stamm Efraim folgende Städte:" },
      { number: 52, text: "Man gab ihnen die Asylstadt Sichem mit ihren Weideflächen auf dem Gebirge Efraim, ferner Geser mit seinen Weideflächen," },
      { number: 53, text: "Kibzajim mit seinen Weideflächen, Bet-Horon mit seinen Weideflächen;" },
      { number: 54, text: "(vom Stamm Dan: Elteke mit seinen Weideflächen, Gibbeton mit seinen Weideflächen,) Ajalon mit seinen Weideflächen, Gat-Rimmon mit seinen Weideflächen;" },
      { number: 55, text: "vom halben Stamm Manasse: Taanach mit seinen Weideflächen und Jibleam mit seinen Weideflächen. Diese gaben sie den Sippen der übrigen Kehatiter.", footnotes: ["Die «übrigen» Kehatiter sind jene, die nicht Nachkommen Aarons sind (vgl. 6,39-45)."] },
      { number: 56, text: "Den Nachkommen Gerschoms gaben sie von den Sippen des halben Stamms Manasse: Golan im Baschan mit seinen Weideflächen, Aschtarot mit seinen Weideflächen;" },
      { number: 57, text: "vom Stamm Issachar: Kischjon mit seinen Weideflächen, Daberat mit seinen Weideflächen," },
      { number: 58, text: "Ramot mit seinen Weideflächen, En-Gannim mit seinen Weideflächen;" },
      { number: 59, text: "vom Stamm Ascher: Mischal mit seinen Weideflächen, Abdon mit seinen Weideflächen," },
      { number: 60, text: "Helkat mit seinen Weideflächen, Rehob mit seinen Weideflächen;" },
      { number: 61, text: "vom Stamm Naftali: Kedesch in Galiläa mit seinen Weideflächen, Hammon mit seinen Weideflächen, Kirjatajim mit seinen Weideflächen." },
      { number: 62, text: "Den übrigen Nachkommen Meraris gaben sie vom Stamm Sebulon: (Jokneam mit seinen Weideflächen, Karta mit seinen Weideflächen,) Rimmon mit seinen Weideflächen, Tabor mit seinen Weideflächen;", footnotes: ["Ergänzt nach Jos 21,34."] },
      { number: 63, text: "im Land jenseits des Jordan bei Jericho, östlich des Jordan, vom Stamm Ruben: Bezer in der Steppe mit seinen Weideflächen, Jahaz mit seinen Weideflächen," },
      { number: 64, text: "Kedemot mit seinen Weideflächen, Mefaat mit seinen Weideflächen;" },
      { number: 65, text: "vom Stamm Gad: Ramot in Gilead mit seinen Weideflächen, Mahanajim mit seinen Weideflächen," },
      { number: 66, text: "Heschbon mit seinen Weideflächen und Jaser mit seinen Weideflächen." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Die Söhne Issachars waren: Tola, Puwa, Jaschub und Schimron, zusammen vier.", heading: "Der Stamm Issachar: 7,1-5" },
      { number: 2, text: "Die Söhne Tolas waren: Usi, Refaja, Jeriël, Jachmai, Jibsam und Schemuël. Sie waren Häupter der Großfamilien Tolas, tapfere Krieger, jeder entsprechend seiner Geschlechterfolge. Zur Zeit Davids zählten sie 22600 Mann." },
      { number: 3, text: "Die Söhne Usis waren: Serachja, Michael, Obadja, Joël und Jischija, insgesamt fünf. Sie alle waren Oberhäupter." },
      { number: 4, text: "Sie hatten, geordnet nach ihrer Geschlechterfolge und nach ihren Großfamilien, ein Kriegsheer von 36000 Mann zu stellen; denn sie hatten viele Frauen und Kinder." },
      { number: 5, text: "Ihre Brüder aus allen Sippen Issachars waren tapfere Krieger. Ihre Aufzeichnung ergab insgesamt 87000 Mann." },
      { number: 6, text: "Die Söhne Benjamins waren: Bela, Becher und Jediaël, insgesamt drei.", heading: "Der Stamm Benjamin: 7,6-12", footnotes: ["Gen 46,21"] },
      { number: 7, text: "Die Söhne Belas waren: Ezbon, Usi, Usiël, Jerimot und Ir, insgesamt fünf. Sie waren Häupter ihrer Großfamilien und tapfere Krieger. Ihre Aufzeichnung ergab 22034 Mann." },
      { number: 8, text: "Die Söhne Bechers waren: Semira, Joasch, Eliëser, Eljoënai, Omri, Jeremot, Abija, Anatot und Alemet. Sie alle waren Söhne Bechers." },
      { number: 9, text: "Die Aufzeichnung entsprechend ihrer Geschlechterfolge nach den Häuptern ihrer Großfamilien und nach den tapferen Kriegern ergab 20200 Mann." },
      { number: 10, text: "Der Sohn Jediaëls war Bilhan und die Söhne Bilhans waren: Jëusch, Benjamin, Ehud, Kenaana, Setan, Tarschisch und Ahischahar." },
      { number: 11, text: "Sie alle waren Söhne Jediaëls, Häupter ihrer Großfamilien, tapfere Krieger, 17200 Mann, die im Heer zum Krieg ausrückten." },
      { number: 12, text: "Schuppim und Huppim waren Söhne Irs, Huschim der Sohn Ahers." },
      { number: 13, text: "Die Söhne Naftalis waren: Jachzeel, Guni, Jezer und Schallum, die Nachkommen der Bilha.", heading: "Der Stamm Naftali: 7,13", footnotes: ["Gen 46,24"] },
      { number: 14, text: "Der Sohn Manasses war Asriël. Seine aramäische Nebenfrau gebar Machir, den Vater Gileads.", heading: "Der Stamm Manasse: 7,14-19", footnotes: ["14-19: Der Text ist nur lückenhaft überliefert."] },
      { number: 15, text: "Machir nahm eine Frau für Huppim und Schuppim. Der Name seiner Schwester war Maacha. Der Name des zweiten (Sohnes Hefers) war Zelofhad. Dieser hatte nur Töchter.", footnotes: ["Der Name Hefer ist aus Num 26,33 zu entnehmen."] },
      { number: 16, text: "Maacha, die Frau Machirs, gebar einen Sohn und nannte ihn Peresch; sein Bruder hieß Scheresch. Dessen Söhne waren Ulam und Rekem." },
      { number: 17, text: "Der Sohn Ulams war Bedan. Das waren die Söhne Gileads, des Sohnes Machirs, des Sohnes Manasses." },
      { number: 18, text: "Seine Schwester Molechet gebar Ischhod, Abiëser und Machla." },
      { number: 19, text: "Die Söhne Schemidas waren Achjan, Schechem, Likhi und Aniam." },
      { number: 20, text: "Der Sohn Efraims war Schutelach. Dessen Sohn war Bered, dessen Sohn Tahat, dessen Sohn Elada, dessen Sohn Tahat,", heading: "Der Stamm Efraim: 7,20-29", footnotes: ["Num 26,35-37"] },
      { number: 21, text: "dessen Sohn Sabad und dessen Sohn Schutelach sowie Eser und Elad. Diese erschlug die einheimische Bevölkerung von Gat, weil sie hinabgezogen waren, um deren Vieh wegzunehmen." },
      { number: 22, text: "Darüber trauerte ihr Vater Efraim lange Zeit und seine Brüder kamen, ihn zu trösten." },
      { number: 23, text: "Dann ging er zu seiner Frau; sie wurde schwanger und gebar einen Sohn. Er nannte ihn Beria, da er zur Zeit des Unglücks in seinem Haus geboren wurde.", footnotes: ["Man hörte im Namen Beria einen Anklang zu beraah (= im Unglück)."] },
      { number: 24, text: "Seine Tochter war Scheera. Sie erbaute das untere und das obere Bet-Horon sowie Usen-Scheera." },
      { number: 25, text: "Weitere Söhne (Efraims) waren Refach und Reschef. Dessen Sohn war Telach, dessen Sohn Tahan," },
      { number: 26, text: "dessen Sohn Ladan, dessen Sohn Ammihud, dessen Sohn Elischama," },
      { number: 27, text: "dessen Sohn Nun und dessen Sohn Josua." },
      { number: 28, text: "Ihr Besitz und ihre Wohnplätze waren Bet-El mit seinen Tochterstädten, gegen Osten Naara, gegen Westen Geser mit seinen Tochterstädten, Sichem mit seinen Tochterstädten bis nach Aja und seinen Tochterstädten." },
      { number: 29, text: "In der Hand der Nachkommen Manasses waren Bet-Schean mit seinen Tochterstädten, Taanach mit seinen Tochterstädten, Megiddo mit seinen Tochterstädten und Dor mit seinen Tochterstädten. In ihnen wohnten die Nachkommen Josefs, des Sohnes Israels." },
      { number: 30, text: "Die Söhne Aschers waren: Jimna, Jischwa, Jischwi und Beria. Ihre Schwester war Serach.", heading: "Der Stamm Ascher: 7,30-40", footnotes: ["Gen 46,17"] },
      { number: 31, text: "Die Söhne Berias waren Heber und Malkiël. Dieser war der Vater von Birsajit." },
      { number: 32, text: "Heber zeugte Jaflet, Schemer, Hotam und deren Schwester Schua." },
      { number: 33, text: "Die Söhne Jaflets waren Pasach, Bimhal und Aschwat. Das waren die Söhne Jaflets." },
      { number: 34, text: "Die Söhne Schemers waren: Ahi, Rohga, Hubba und Aram." },
      { number: 35, text: "Die Söhne seines Bruders Hotam waren: Zofach, Jimna, Schelesch und Amal." },
      { number: 36, text: "Die Söhne Zofachs waren: Suach, Harnefer, Schual, Beri, Jimra," },
      { number: 37, text: "Bezer, Hod, Schamma, Schilscha, Jitran und Beera." },
      { number: 38, text: "Die Söhne Jitrans waren: Jefunne, Pispa und Ara." },
      { number: 39, text: "Die Söhne Ullas waren: Arach, Hanniël und Rizja." },
      { number: 40, text: "Alle diese waren Nachkommen Aschers, auserlesene Häupter von Großfamilien, tapfere Krieger, Häupter unter den führenden Männern. Ihre Aufzeichnung für das Kriegsheer ergab 26000 Mann." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Benjamin zeugte Bela als seinen Erstgeborenen, Aschbel als zweiten, Achrach als dritten,", heading: "Das zweite Verzeichnis des Stammes Benjamin: 8,1-28", footnotes: ["Gen 46,21"] },
      { number: 2, text: "Noha als vierten und Rafa als fünften." },
      { number: 3, text: "Bela hatte folgende Söhne: Ard, Gera, Abihud," },
      { number: 4, text: "Abischua, Naaman, Ahoach," },
      { number: 5, text: "Gera, Schefufan und Huram." },
      { number: 6, text: "Und das waren die Söhne Ehuds . . . Sie waren die Familienhäupter der Bewohner von Geba . . . Man führte sie in die Verbannung nach Manahat.", footnotes: ["6f: Der Text ist nur lückenhaft überliefert."] },
      { number: 7, text: "Naaman, Ahija und Gera . . . Dieser führte sie gefangen fort. Er zeugte Usa und Ahihud." },
      { number: 8, text: "Schaharajim zeugte im Grünland von Moab Söhne, nachdem er seine Frauen Huschim und Baara entlassen hatte." },
      { number: 9, text: "Mit seiner Frau Hodesch zeugte er Jobab, Zibja, Mescha, Malkam," },
      { number: 10, text: "Jëuz, Sacheja und Mirma. Das waren seine Söhne, die Häupter ihrer Großfamilien." },
      { number: 11, text: "Mit Huschim zeugte er Abitub und Elpaal." },
      { number: 12, text: "Die Söhne Elpaals waren: Eber, Mischam und Schemed. Dieser baute Ono und Lod mit seinen Tochterstädten." },
      { number: 13, text: "Beria und Schema waren die Häupter der Großfamilien der Einwohner von Ajalon. Sie vertrieben die Einwohner von Gat." },
      { number: 14, text: "Ihre Brüder waren Elpaal, Schaschak und Jeroham." },
      { number: 15, text: "Sebadja, Arad, Eder," },
      { number: 16, text: "Michael, Jischpa und Joha waren Söhne Berias." },
      { number: 17, text: "Sebadja, Meschullam, Hiski, Heber," },
      { number: 18, text: "Jischmerai, Jislia und Jobab waren Söhne Elpaals." },
      { number: 19, text: "Jakim, Sichri, Sabdi," },
      { number: 20, text: "Eliënai, Zilletai, Eliël," },
      { number: 21, text: "Adaja, Beraja und Schimrat waren Söhne Schimis.", footnotes: ["Für Schimi steht in V. 13 Schema."] },
      { number: 22, text: "Jischpan, Eber, Eliël," },
      { number: 23, text: "Abdon, Sichri, Hanan," },
      { number: 24, text: "Hananja, Elam, Antotija," },
      { number: 25, text: "Jifdeja und Penuël waren Söhne Schaschaks." },
      { number: 26, text: "Schamscherai, Scheharja, Atalja," },
      { number: 27, text: "Jaareschja, Elija und Sichri waren Söhne Jerohams." },
      { number: 28, text: "Sie waren Häupter der Großfamilien entsprechend ihrer Geschlechterfolge, ihre Oberhäupter, und wohnten in Jerusalem." },
      { number: 29, text: "In Gibeon wohnte Jëiël, der Vater Gibeons. Seine Frau war Maacha", heading: "Die Familie Sauls: 8,29-40", footnotes: ["(29-38) 9,35-44"] },
      { number: 30, text: "und sein erstgeborener Sohn war Abdon. Ihm folgten Zur, Kisch, Baal, Ner, Nadab," },
      { number: 31, text: "Gedor, Achjo, Secher und Miklot." },
      { number: 32, text: "Miklot zeugte Schima. Auch sie wohnten in Jerusalem bei ihren Brüdern, ihnen gegenüber." },
      { number: 33, text: "Ner zeugte Abner und Kisch zeugte Saul. Saul zeugte Jonatan, Malkischua, Abinadab und Eschbaal.", footnotes: ["1 Sam 31,2"] },
      { number: 34, text: "Der Sohn Jonatans war Merib-Baal. Merib-Baal zeugte Micha.", footnotes: ["2 Sam 9,6"] },
      { number: 35, text: "Die Söhne Michas waren: Piton, Melech, Tachrea und Ahas." },
      { number: 36, text: "Ahas zeugte Joadda und Joadda zeugte Alemet, Asmawet und Simri. Simri zeugte Moza," },
      { number: 37, text: "Moza zeugte Bina; dessen Sohn war Rafa, dessen Sohn Elasa und dessen Sohn Azel." },
      { number: 38, text: "Azel hatte sechs Söhne und das sind ihre Namen: Asrikam, Bochru, Jischmael, Schearja, Obadja und Hanan. Alle diese waren Söhne Azels." },
      { number: 39, text: "Die Söhne seines Bruders Eschek waren: Ulam, sein Erstgeborener; der zweite war Jëusch und der dritte Elifelet." },
      { number: 40, text: "Die Söhne Ulams waren tapfere Krieger, die den Bogen spannten. Sie hatten viele Söhne und Enkel, insgesamt hundertfünfzig. Alle diese gehörten zu den Nachkommen Benjamins." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "So ließ sich ganz Israel in die Stammeslisten eintragen. Sie wurden in das Buch der Könige von Israel und Juda aufgenommen.", footnotes: ["(1-18) Neh 11,3-19"] },
      { number: 2, text: "Juda wurde wegen seiner Treulosigkeit nach Babel weggeführt. Die ersten Ansiedler, die sich wieder auf ihrem Besitz in ihren Städten niederließen, waren Israeliten, Priester, Leviten und Tempeldiener.", heading: "Jerusalemer Familien nach dem Exil: 9,1-18", footnotes: ["«Erste Ansiedler» sind nach dem Hinweis auf die Babylonische Gefangenschaft die ersten Heimkehrer unter Serubbabel und Jeschua (vgl. Esra 2). «Israeliten» sind hier (neben Priestern, Leviten und Tempeldienern) die Laien."] },
      { number: 3, text: "In Jerusalem wohnten von den Angehörigen Judas, Benjamins, Efraims und Manasses folgende:" },
      { number: 4, text: "Utai, der Sohn Ammihuds, des Sohnes Omris, des Sohnes Imris, des Sohnes Banis, ein Nachkomme des Perez, des Sohnes Judas." },
      { number: 5, text: "Von den Nachkommen Schelas: Asaja, der Erstgeborene, und dessen Söhne." },
      { number: 6, text: "Von den Nachkommen Serachs: Jëuël. Die Zahl ihrer Brüder betrug 690 Mann." },
      { number: 7, text: "Von den Nachkommen Benjamins: Sallu, der Sohn Meschullams, des Sohnes Hodawjas, des Sohnes Senuas;" },
      { number: 8, text: "ferner Jibneja, der Sohn Jerohams, und Ela, der Sohn Usis, des Sohnes Michris, sowie Meschullam, der Sohn Schefatjas, des Sohnes Reguëls, des Sohnes Jibnijas;" },
      { number: 9, text: "dazu ihre Brüder nach ihrer Geschlechterfolge, insgesamt 956 Mann. Sie alle waren Oberhäupter ihrer Großfamilien." },
      { number: 10, text: "Von den Priestern: Jedaja, Jojarib, Jachin," },
      { number: 11, text: "Asarja, der Sohn Hilkijas, des Sohnes Meschullams, des Sohnes Zadoks, des Sohnes Merajots, des Sohnes Ahitubs, der Fürst des Hauses Gottes;" },
      { number: 12, text: "ferner Adaja, der Sohn Jerohams, des Sohnes Paschhurs, des Sohnes Malkijas, und Masai, der Sohn Adiëls, des Sohnes Jachseras, des Sohnes Meschullams, des Sohnes Meschillemots, des Sohnes Immers," },
      { number: 13, text: "sowie ihre Brüder, die Häupter ihrer Großfamilien, insgesamt 1760 Mann. Sie waren tüchtige Männer in der Besorgung des Dienstes im Haus Gottes." },
      { number: 14, text: "Von den Leviten: Schemaja, der Sohn Haschubs, des Sohnes Asrikams, des Sohnes Haschabjas, von den Nachkommen Meraris;" },
      { number: 15, text: "ferner Bakbakar, Heresch, Galal, Mattanja, der Sohn Michas, des Sohnes Sichris, des Sohnes Asafs," },
      { number: 16, text: "sowie Abda, der Sohn Schammuas, des Sohnes Galals, des Sohnes Jedutuns, und Berechja, der Sohn Asas, des Sohnes Elkanas, der in den Dörfern der Netofatiter wohnte." },
      { number: 17, text: "Die Torwächter waren: Schallum, Akkub, Talmon und Ahiman. Ihr Bruder Schallum war das Oberhaupt." },
      { number: 18, text: "Er hat bis heute die Wache am Königstor im Osten. Das sind die Torwächter in den Lagern der Leviten." },
      { number: 19, text: "Schallum, der Sohn Kores, des Sohnes Abiasafs, des Sohnes Korachs, und seine Brüder aus seiner Großfamilie, die Korachiter, hatten als Wächter den Dienst an den Schwellen des Zeltes zu besorgen. Ihre Väter waren im Lager des Herrn Wächter am Eingang gewesen.", heading: "Levitische Dienste: 9,19-34" },
      { number: 20, text: "Pinhas, der Sohn Eleasars, war einst ihr Anführer - der Herr sei mit ihm.", footnotes: ["Der Satz «Der Herr sei mit ihm» ist der früheste Beleg für die Sitte, dem Namen einer verehrungswürdigen Person eine Segensformel beizufügen."] },
      { number: 21, text: "Secharja, der Sohn Meschelemjas, war Torwächter am Eingang des Offenbarungszeltes." },
      { number: 22, text: "Die als Torwächter an den Schwellen ausersehen waren, zählten insgesamt 212 Mann. Sie hatten sich in ihren Dörfern in die Stammeslisten eingetragen und waren von David und dem Seher Samuel in ihr Amt eingesetzt worden." },
      { number: 23, text: "Mit ihren Söhnen standen sie nach der Dienstordnung als Wächter an den Toren zum Haus des Herrn, zum Haus des Zeltes." },
      { number: 24, text: "Sie waren nach den vier Himmelsrichtungen aufgestellt, nach Osten, Westen, Norden und Süden." },
      { number: 25, text: "Ihre Brüder wohnten in ihren Dörfern und mussten jeweils sieben Tage, von einem Zeitpunkt zum andern, mit ihnen zum Dienst erscheinen." },
      { number: 26, text: "Ständig im Dienst waren nur die vier obersten Torwächter. Einige Leviten hatten die Aufsicht über die Kammern und die Schätze im Haus Gottes." },
      { number: 27, text: "Sie blieben auch während der Nacht in der Nähe des Hauses Gottes; denn ihnen war die Wache übertragen und sie hatten den Schlüssel, um jeden Morgen zu öffnen." },
      { number: 28, text: "Einige von ihnen überwachten die Geräte für den Dienst. Sie trugen sie abgezählt hinein und brachten sie abgezählt wieder heraus." },
      { number: 29, text: "Andere waren zur Aufsicht über die sonstigen Geräte und alle Geräte des Heiligtums bestellt. Sie hatten das Feinmehl, den Wein und das Öl, den Weihrauch und verschiedenartigen Balsam zu besorgen." },
      { number: 30, text: "Einige von den Söhnen der Priester bereiteten die Salben für den Balsam." },
      { number: 31, text: "Mattitja, einer der Leviten, der Erstgeborene des Korachiters Schallum, war amtlich mit der Zubereitung des Backwerks beauftragt." },
      { number: 32, text: "Ein Teil der Kehatiter, ihrer Amtsbrüder, hatte für die Schaubrote zu sorgen, die sie jeden Sabbat aufzulegen hatten." },
      { number: 33, text: "Das waren die Sänger, Häupter der Großfamilien bei den Leviten, die, von anderen Arbeiten befreit, in den Kammern wohnten, da sie Tag und Nacht im Dienst sein mussten . . .", footnotes: ["Die erwartete Aufzählung der Namen fehlt hier."] },
      { number: 34, text: "Das waren die Häupter der Großfamilien der Leviten nach ihrer Geschlechterfolge, die Häupter. Sie wohnten in Jerusalem." },
      { number: 35, text: "In Gibeon wohnte Jëiël, der Vater Gibeons. Seine Frau war Maacha", heading: "Die Familie Sauls: 9,35-44", footnotes: ["(35-44) 8,29-38"] },
      { number: 36, text: "und sein erstgeborener Sohn war Abdon. Ihm folgten Zur, Kisch, Baal, Ner, Nadab," },
      { number: 37, text: "Gedor, Achjo, Secher und Miklot." },
      { number: 38, text: "Miklot zeugte Schima. Auch sie wohnten in Jerusalem bei ihren Brüdern, ihnen gegenüber." },
      { number: 39, text: "Ner zeugte Abner und Kisch zeugte Saul. Saul zeugte Jonatan, Malkischua, Abinadab und Eschbaal." },
      { number: 40, text: "Der Sohn Jonatans war Merib-Baal. Merib-Baal zeugte Micha." },
      { number: 41, text: "Die Söhne Michas waren: Piton, Melech, Tachrea und Ahas." },
      { number: 42, text: "Ahas zeugte Joadda und Joadda zeugte Alemet, Asmawet und Simri. Simri zeugte Moza," },
      { number: 43, text: "Moza zeugte Bina; dessen Sohn war Refaja, dessen Sohn Elasa und dessen Sohn Azel." },
      { number: 44, text: "Azel hatte sechs Söhne und das sind ihre Namen: Asrikam, Bochru, Jischmael, Schearja, Obadja und Hanan. Das waren die Söhne Azels." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Als die Philister gegen Israel kämpften, flohen die Israeliten vor ihnen. Viele waren gefallen und lagen erschlagen auf dem Gebirge Gilboa.", heading: "Die Geschichte Davids: 10,1 - 29,30 | Das Ende Sauls: 10,1-14", footnotes: ["(1-12) 1 Sam 31,1-13"] },
      { number: 2, text: "Die Philister verfolgten Saul und seine Söhne und erschlugen Sauls Söhne Jonatan, Abinadab und Malkischua." },
      { number: 3, text: "Um Saul selbst entstand ein schwerer Kampf. Die Bogenschützen hatten ihn getroffen und er war schwer verwundet." },
      { number: 4, text: "Da sagte Saul zu seinem Waffenträger: Zieh dein Schwert und durchbohre mich damit! Sonst kommen diese Unbeschnittenen und treiben mit mir ihren Mutwillen. Der Waffenträger wollte es nicht tun, denn er hatte große Angst. Da nahm Saul selbst das Schwert und stürzte sich hinein." },
      { number: 5, text: "Als der Waffenträger sah, dass Saul tot war, stürzte auch er sich in sein Schwert und starb." },
      { number: 6, text: "So kamen Saul und seine drei Söhne ums Leben; sein ganzes Haus starb mit ihm.", footnotes: ["In 8,29-38 werden zwar noch zwölf Generationen nach Saul erwähnt. Da sie aber unbedeutend blieben, kann V. 6 sagen, dass mit Saul sein ganzes Haus, d.h. seine Dynastie, ausstarb."] },
      { number: 7, text: "Als die Israeliten in der Ebene (Jesreel) sahen, dass die Krieger Israels geflohen und dass Saul und seine Söhne tot waren, verließen sie ihre Städte und flohen. Dann kamen die Philister und besetzten die Städte." },
      { number: 8, text: "Als am nächsten Tag die Philister kamen, um die Erschlagenen auszuplündern, fanden sie Saul und seine Söhne, die auf dem Gebirge Gilboa gefallen waren." },
      { number: 9, text: "Sie zogen ihn aus, nahmen seinen Kopf und seine Rüstung und schickten beides im Land der Philister umher, um ihren Götzen und dem Volk die Siegesnachricht zu übermitteln." },
      { number: 10, text: "Die Rüstung Sauls legten sie im Tempel ihrer Götter nieder. Seinen Kopf aber hefteten sie an den Tempel des Dagon.", footnotes: ["Zu Dagon vgl. die Anmerkung zu 1 Sam 5,2."] },
      { number: 11, text: "Als die Einwohner von Jabesch-Gilead hörten, was die Philister mit Saul gemacht hatten,", footnotes: ["1 Sam 11"] },
      { number: 12, text: "brachen alle kriegstüchtigen Männer auf, nahmen die Leiche Sauls und die Leichen seiner Söhne und brachten sie nach Jabesch. Sie begruben ihre Gebeine unter der Terebinthe in Jabesch und fasteten sieben Tage lang." },
      { number: 13, text: "So starb Saul wegen der Treulosigkeit, die er gegen den Herrn begangen hatte. Er hatte das Wort des Herrn nicht befolgt und den Totengeist befragt, um Auskunft zu suchen;", footnotes: ["1 Sam 15,19; 28,8-19"] },
      { number: 14, text: "an den Herrn aber hatte er sich nicht gewandt. Darum ließ er ihn sterben und übergab das Königtum David, dem Sohn Isais." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Ganz Israel versammelte sich bei David in Hebron und sagte: Wir sind doch dein Fleisch und Bein.", heading: "Die Anerkennung Davids als König: 11,1-3", footnotes: ["(1-3) 2 Sam 5,1-3", "dein Fleisch und Bein: vgl. die Anmerkung zu Gen 2,21-23."] },
      { number: 2, text: "Schon früher, als Saul noch König war, hast du Israel in den Kampf und wieder nach Hause geführt. Und der Herr, dein Gott, hat zu dir gesagt: Du sollst der Hirt meines Volkes Israel sein, du sollst der Fürst meines Volkes Israel werden." },
      { number: 3, text: "Alle Ältesten Israels kamen zum König nach Hebron; David schloss mit ihnen in Hebron einen Vertrag vor dem Herrn und sie salbten David zum König von Israel gemäß dem Wort, das der Herr durch Samuel verkündet hatte.", footnotes: ["1 Sam 16"] },
      { number: 4, text: "David zog mit ganz Israel nach Jerusalem, das ist Jebus. Dort saßen noch die Jebusiter, die damals im Land wohnten.", heading: "Die Eroberung Jerusalems: 11,4-9", footnotes: ["(4-9) 2 Sam 5,6-10"] },
      { number: 5, text: "Die Jebusiter aber sagten zu David: Du wirst nicht in die Stadt hereinkommen. Doch David eroberte die Burg Zion; sie wurde die Stadt Davids." },
      { number: 6, text: "Damals sagte David: Wer als erster die Jebusiter schlägt, soll Hauptmann und Anführer werden. Da stieg Joab, der Sohn der Zeruja, als Erster hinauf und wurde Hauptmann." },
      { number: 7, text: "David ließ sich in der Burg nieder und daher nannte man sie Davidstadt." },
      { number: 8, text: "Er baute an der Stadt ringsum vom Millo an und im Umkreis, während Joab die übrige Stadt wiederherstellte." },
      { number: 9, text: "David wurde immer mächtiger und der Herr der Heere war mit ihm." },
      { number: 10, text: "Das sind die Obersten der Helden Davids, die mit ganz Israel in der Sache des Königtums fest zu ihm hielten, um ihn nach dem Wort des Herrn zum König von Israel zu machen.", heading: "Davids Helden: 11,10-19", footnotes: ["(10-19) 2 Sam 23,8-17"] },
      { number: 11, text: "Das ist das Verzeichnis der Helden Davids: Jaschobam, der Sohn Hachmonis, war das Haupt der Drei; er schwang seine Lanze über dreihundert Männer und erschlug sie alle auf einmal.", footnotes: ["Jaschobam ist wohl absichtliche Entstellung des Namens Jischbaal (vgl. 2 Sam 23,8). - drei: H: dreißig. Es gab einen Heldenkreis mit drei und einen größeren mit dreißig Männern."] },
      { number: 12, text: "Nach ihm kommt der Ahoachiter Eleasar, der Sohn Dodos." },
      { number: 13, text: "Er war bei David, als die Philister sich bei Pas-Dammim zum Kampf sammelten. Dort war ein Gerstenfeld. Als das Volk vor den Philistern floh,", footnotes: ["In H sind einige Sätze verloren gegangen, sodass der Name des dritten Helden, Schamma, wegfiel und seine Leistungen auf den zweiten übertragen wurden. Sam 23,9-12 ist der volle Wortlaut noch erhalten. Gerste: in 2 Sam 23,11: Linsen."] },
      { number: 14, text: "stellte er sich mitten in das Feld, behauptete es und schlug die Philister. So verlieh ihm der Herr einen großen Sieg." },
      { number: 15, text: "Als sich einst das Lager der Philister in der Rafaïterebene befand, kamen drei von den dreißig Helden zu David in das Felsennest, in die Höhle Adullam." },
      { number: 16, text: "David hielt sich damals in seiner Bergfestung auf und ein Posten der Philister lag in Betlehem." },
      { number: 17, text: "Da bekam David großen Durst und er sagte: Wer bringt mir Wasser aus der Zisterne am Tor von Betlehem?" },
      { number: 18, text: "Da drangen die drei Helden in das Lager der Philister ein, schöpften Wasser aus der Zisterne am Tor von Betlehem, nahmen es mit und brachten es David. Doch er wollte es nicht trinken, sondern goss es als Trankopfer für den Herrn aus" },
      { number: 19, text: "und sagte: Das sei fern von mir, Gott, dass ich so etwas tue. Soll ich das Blut dieser Männer trinken, die unter Lebensgefahr hingegangen sind? Denn unter Lebensgefahr haben sie es geholt. Darum wollte er es nicht trinken. Eine solche Tat vollbrachten die drei Helden." },
      { number: 20, text: "Joabs Bruder Abischai war der Anführer der Dreißig. Er schwang seinen Speer über dreihundert Männer und erschlug sie; er hatte bei den drei Helden einen großen Namen.", heading: "Weitere Helden Davids: 11,20-47", footnotes: ["(20-47) 2 Sam 23,18-39", "20f: dreißig: in H zu «drei» verschrieben."] },
      { number: 21, text: "Unter den Dreißig war er hoch geehrt und er wurde ihr Anführer, aber an die Drei reichte er nicht heran." },
      { number: 22, text: "Benaja aus Kabzeel, der Sohn Jojadas, war ein tapferer Mann, der große Taten vollbrachte. Er erschlug die beiden Söhne Ariëls aus Moab. Als einmal Schnee gefallen war, kam er zu einer Zisterne und erlegte darin einen Löwen." },
      { number: 23, text: "Auch erschlug er einen riesenhaften Ägypter, der fünf Ellen groß war. Der Ägypter hatte einen Speer gleich einem Weberbaum in der Hand. Benaja aber ging nur mit einem Stock auf ihn los, riss ihm den Speer aus der Hand und tötete ihn mit diesem Speer." },
      { number: 24, text: "Solche Taten vollbrachte Benaja, der Sohn Jojadas. Er hatte bei den drei Helden einen großen Namen." },
      { number: 25, text: "Unter den Dreißig war er hoch geehrt, aber an die Drei reichte er nicht heran. David stellte ihn an die Spitze seiner Leibwache." },
      { number: 26, text: "Tapfere Helden waren Joabs Bruder Asaël, Elhanan aus Betlehem, der Sohn Dodos,", footnotes: ["26-47: Die Namen sind teilweise durch Schreibfehler entstellt. Die Korrekturen stützen sich zumeist auf 2 Sam und G. In 2 Sam schließt die Liste mit Urija; Chr fügt sechzehn weitere Namen hinzu."] },
      { number: 27, text: "Schammot aus Harod, Helez aus Bet-Pelet," },
      { number: 28, text: "Ira aus Tekoa, der Sohn des Ikkesch, Abiëser aus Anatot," },
      { number: 29, text: "Sibbechai aus Huscha, Ilai der Ahoachiter," },
      { number: 30, text: "Mahrai aus Netofa, Heled aus Netofa, der Sohn Baanas," },
      { number: 31, text: "Ittai aus Gibea in Benjamin, der Sohn Ribais, Benaja aus Piraton," },
      { number: 32, text: "Hiddai aus Nahale-Gaasch, Abiël aus Bet-Araba," },
      { number: 33, text: "Asmawet aus Bahurim, Eljachba aus Schaalbim," },
      { number: 34, text: "Jaschen der Guniter, Jonatan aus Harar, der Sohn Schages," },
      { number: 35, text: "Ahiam aus Harar, der Sohn Sachars, Elifal, der Sohn Urs," },
      { number: 36, text: "Hefer aus Mechera, Ahija aus Gilo," },
      { number: 37, text: "Hezro aus Karmel, Naarai, der Sohn Esbais," },
      { number: 38, text: "Joël, der Bruder Natans, Mibhar, der Sohn Hagris," },
      { number: 39, text: "Zelek, der Ammoniter, Nachrai aus Beerot, der Waffenträger Joabs, des Sohnes der Zeruja," },
      { number: 40, text: "Ira aus Jattir, Gareb aus Jattir," },
      { number: 41, text: "Urija, der Hetiter, Sabad, der Sohn Achlais," },
      { number: 42, text: "Adina, der Sohn Schisas, aus dem Stamm Ruben, Haupt der Rubeniter und Anführer von dreißig Mann," },
      { number: 43, text: "Hanan, der Sohn Maachas, Joschafat aus Meten," },
      { number: 44, text: "Usija aus Aschtarot, Schama und Jëiël, die Söhne Hotams, aus Aroër," },
      { number: 45, text: "Jediaël, der Sohn Schimris, und sein Bruder Joha aus Tiz," },
      { number: 46, text: "Eliël aus Mahanajim, Jeribai und Joschawja, die Söhne Elnaams, Jitma, der Moabiter," },
      { number: 47, text: "Eliël, Obed und Jaasiël aus Zoba." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Diese sind es, die zu David nach Ziklag kamen, als er sich noch von Saul, dem Sohn des Kisch, fernhalten musste. Sie gehörten zu den Helden, die ihn im Kampf unterstützten.", heading: "Das dritte Verzeichnis von Anhängern Davids: 12,1-23", footnotes: ["Um sich der Verfolgung durch Saul zu entziehen, war David zum Philisterkönig Achisch übergetreten, der ihm die Stadt Ziklag, nördlich von Beerscheba, als Aufenthalt zuwies (vgl. 1 Sam 27,6)."] },
      { number: 2, text: "Sie waren mit dem Bogen ausgerüstet und konnten mit der rechten und mit der linken Hand Steine schleudern und Pfeile mit dem Bogen abschießen. Von den Stammesbrüdern Sauls, von Benjamin, kamen:" },
      { number: 3, text: "Ahiëser, ihr Anführer, ferner Joasch, der Sohn Schemaas, aus Gibea, Jesiël und Pelet, die Söhne Asmawets, Beracha und Jehu aus Anatot," },
      { number: 4, text: "Jischmaja aus Gibeon, ein Held unter den Dreißig und ihr Anführer," },
      { number: 5, text: "Jirmeja, Jahasiël, Johanan und Josabad aus Gedera," },
      { number: 6, text: "Elusai, Jerimot, Bealja, Schemarja und Schefatja aus Haruf," },
      { number: 7, text: "Elkana, Jischija, Asarel, Joëser und Jaschobam, die Korachiter," },
      { number: 8, text: "Joëla und Sebadja, die Söhne Jerohams, aus Gedor." },
      { number: 9, text: "Auch Gaditer traten zu David über und kamen in die Bergfestung in der Wüste. Sie waren tapfere Helden, kampfgeübte Krieger mit Schild und Lanze, waren wie Löwen anzusehen und flink wie Gazellen auf den Bergen.", footnotes: ["Vor seinem Übertritt zu den Philistern hielt sich David längere Zeit in der Bergfestung Adullam, südwestlich von Betlehem, auf und sammelte Anhänger um sich (vgl. 1 Sam 22,1)."] },
      { number: 10, text: "Eser war der erste, Obadja der zweite, Eliab der dritte," },
      { number: 11, text: "Mischmanna der vierte, Jirmeja der fünfte," },
      { number: 12, text: "Attai der sechste, Eliël der siebte," },
      { number: 13, text: "Johanan der achte, Elsabad der neunte," },
      { number: 14, text: "Jirmeja der zehnte, Machbannai der elfte." },
      { number: 15, text: "Diese waren von den Gaditern gekommen. Sie waren Hauptleute im Heer. Der kleinste von ihnen nahm es mit hundert, der stärkste mit tausend auf." },
      { number: 16, text: "Sie waren es, die im ersten Monat den Jordan überquerten, als er über die Ufer getreten war und die Täler im Osten und Westen abgeschnitten waren." },
      { number: 17, text: "Auch Angehörige Benjamins und Judas kamen zu David in die Bergfestung." },
      { number: 18, text: "David trat zu ihnen heraus, sprach sie an und sagte: Wenn ihr in friedlicher Absicht zu mir kommt, um mir zu helfen, so bin ich zur Gemeinschaft mit euch bereit. Wenn ihr aber kommt, um mich den Feinden zu verraten, obwohl ich kein Unrecht begangen habe, dann soll der Gott unserer Väter es sehen und euch strafen." },
      { number: 19, text: "Da ergriff ein Geist Amasai, das Haupt der Dreißig, sodass er ausrief: Dir, David, gehören wir. Zu dir, Sohn Isais, stehen wir. Heil, Heil sei dir, Heil deinem Helfer; denn dir hilft dein Gott. Da nahm sie David auf und reihte sie ein unter die Anführer seiner Schar." },
      { number: 20, text: "Auch von Manasse gingen einige zu David über. David zog damals im Heer der Philister gegen Saul in den Krieg, brachte ihnen aber keine Hilfe, da ihn die Fürsten der Philister nach einer Beratung wegschickten. Sie sagten nämlich: Er könnte um den Preis unserer Köpfe zu seinem Herrn, zu Saul, übergehen.", footnotes: ["1 Sam 29", "(David) brachte . . . keine Hilfe: Text korr.; H: sie brachten . . . keine Hilfe."] },
      { number: 21, text: "David kehrte daher nach Ziklag zurück. Zu dieser Zeit stießen von Manasse zu ihm: Adnach, Josabad, Jediaël, Michael, Josabad, Elihu und Zilletai, die Häupter der Tausendschaften Manasses." },
      { number: 22, text: "Sie halfen David im Kampf gegen die Räuberscharen (der Amalekiter); denn sie alle waren tapfere Krieger und wurden Oberste im Heer." },
      { number: 23, text: "Tag für Tag kamen Leute zu David, um ihm zu helfen; und so entstand ein großes Heerlager gleich einem Heerlager Gottes." },
      { number: 24, text: "Das sind die Zahlen aller wehrfähigen Männer, die zu David nach Hebron kamen, um ihm nach dem Wort des Herrn das Königtum Sauls zu übertragen:", heading: "Die Gefolgschaft bei der Thronerhebung in Hebron:" },
      { number: 25, text: "Aus Juda kamen 6800 wehrfähige Männer, die Schild und Lanze trugen." },
      { number: 26, text: "Aus Simeon kamen 7100 tapfere Krieger," },
      { number: 27, text: "aus Levi 4600 Mann," },
      { number: 28, text: "dazu Jojada, der Fürst der Nachkommen Aarons, mit 3700 Mann" },
      { number: 29, text: "und Zadok, ein junger, tapferer Krieger, dessen Großfamilie 22 Führer stellte." },
      { number: 30, text: "Aus Benjamin, den Stammesbrüdern Sauls, kamen 3000 Mann. Ihre Mehrheit war bis dahin in der Gefolgschaft des Hauses Saul geblieben." },
      { number: 31, text: "Aus Efraim kamen 20800 in ihren Großfamilien hoch angesehene Krieger," },
      { number: 32, text: "aus dem halben Stamm Manasse 18000 Mann, die namentlich dazu bestimmt waren, zu kommen und David zum König zu machen." },
      { number: 33, text: "Von den Angehörigen Issachars, die die Zeiten verstanden und wussten, was Israel zu tun hatte, kamen 200 Oberste mit allen Stammesbrüdern, die ihrem Befehl unterstanden." },
      { number: 34, text: "Aus Sebulon kamen 50000 wehrfähige Männer, die mit allen Kriegswaffen zu kämpfen verstanden und sich ohne Vorbehalt einordneten." },
      { number: 35, text: "Aus Naftali kamen 1000 Anführer und mit ihnen 37000 Mann mit Schild und Lanze," },
      { number: 36, text: "aus Dan 28600 kampfbereite Männer," },
      { number: 37, text: "aus Ascher 40000 wehrfähige, kampfbereite Männer." },
      { number: 38, text: "Von jenseits des Jordan, aus Ruben, Gad und dem halben Stamm Manasse kamen 120000 Mann mit allen Waffen eines Kriegsheeres." },
      { number: 39, text: "Alle diese waren Krieger, die sich willig einordneten. Sie kamen in ehrlicher Gesinnung nach Hebron, um David zum König von ganz Israel zu machen; aber auch alle übrigen Israeliten waren einig im Wunsch, David zum König zu machen." },
      { number: 40, text: "Sie blieben drei Tage dort bei David; sie aßen und tranken, denn ihre Stammesbrüder hatten für sie gesorgt." },
      { number: 41, text: "Auch jene, die bis nach Issachar, Sebulon und Naftali hin in ihrer Nähe wohnten, brachten auf Eseln, Kamelen, Maultieren und Rindern Lebensmittel zum Unterhalt herbei: Mehl, Feigen- und Traubenkuchen, Wein, Öl und eine große Menge Rinder und Schafe; denn es herrschte Freude in Israel." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "David besprach sich mit den Obersten der Tausend- und Hundertschaften und mit allen führenden Leuten.", heading: "Die Überführung der Bundeslade nach Jerusalem: 13,1-14", footnotes: ["(1-14) 2 Sam 6,12-23"] },
      { number: 2, text: "Dann sagte er zur ganzen Versammlung Israels: Wenn ihr einverstanden seid und wenn es dem Herrn, unserem Gott, gefällt, schicken wir überallhin Boten zu unseren Brüdern, die in allen Gegenden Israels geblieben sind, sowie zu den Priestern und Leviten, die in ihren Städten und auf ihren Weideflächen wohnen. Sie sollen sich bei uns versammeln" },
      { number: 3, text: "und wir wollen die Lade unseres Gottes zu uns heraufholen; denn in den Tagen Sauls haben wir uns nicht um sie gekümmert.", footnotes: ["Die Lade war in der Zeit des Priesters Eli von den Philistern erbeutet worden (vgl. 1 Sam 4,11). Sie befand sich nach ihrer Rückgabe an Israel im Haus Abinadabs in Kirjat-Jearim (vgl. 1 Sam 7,1)."] },
      { number: 4, text: "Die ganze Versammlung stimmte dem Vorhaben zu, denn der Plan gefiel dem ganzen Volk." },
      { number: 5, text: "David versammelte nun ganz Israel vom Schihor in Ägypten bis Lebo-Hamat, um die Lade Gottes von Kirjat-Jearim heraufzuholen.", footnotes: ["Gemeint ist die Ausdehnung des Landes vom östlichsten Nilarm (Schihor) bis Lebo-Hamat an der syrischen Grenze."] },
      { number: 6, text: "Er zog mit ganz Israel nach Baala bei Kirjat-Jearim, das zu Juda gehörte, um von dort die Lade Gottes heraufzuholen, über der der Name des Herrn, der über den Kerubim thront, ausgerufen worden ist." },
      { number: 7, text: "Sie holten die Lade Gottes auf einem neuen Wagen vom Haus Abinadabs ab; Usa und Achjo lenkten den Wagen." },
      { number: 8, text: "David und ganz Israel tanzten und sangen vor Gott mit ganzer Hingabe und spielten auf Zithern, Harfen und Pauken, mit Zimbeln und Trompeten." },
      { number: 9, text: "Als sie zur Tenne Kidons kamen, brachen die Rinder aus und Usa streckte seine Hand aus, um die Lade festzuhalten." },
      { number: 10, text: "Da entbrannte der Zorn des Herrn gegen Usa und er erschlug ihn, weil er seine Hand nach der Lade ausgestreckt hatte, sodass er dort vor Gott starb." },
      { number: 11, text: "David war sehr erregt darüber, dass der Herr den Usa so plötzlich dahingerafft hatte, und man nennt den Ort bis heute darum Perez-Usa (Wegraffung Usas)." },
      { number: 12, text: "An jenem Tag bekam David Angst vor Gott, sodass er sagte: Wie soll ich die Lade Gottes zu mir bringen?" },
      { number: 13, text: "Er ließ sie darum nicht zu sich in die Davidstadt bringen, sondern stellte sie in das Haus Obed-Edoms aus Gat." },
      { number: 14, text: "Drei Monate lang blieb die Lade Gottes im Haus Obed-Edoms und der Herr segnete das Haus Obed-Edoms und alles, was ihm gehörte." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Hiram, der König von Tyrus, schickte eine Gesandtschaft zu David und ließ ihm Zedernholz überbringen; auch Maurer und Zimmerleute schickte er, die ihm einen Palast bauen sollten.", heading: "Das Erstarken des Königtums: 14,1-7", footnotes: ["(1-7) 2 Sam 5,11-16"] },
      { number: 2, text: "So erkannte David, dass ihn der Herr als König von Israel bestätigt hatte; denn wegen seines Volkes Israel war Davids Königtum zu hohem Ansehen gelangt." },
      { number: 3, text: "David nahm sich noch weitere Frauen in Jerusalem und zeugte noch Söhne und Töchter." },
      { number: 4, text: "Das sind die Namen der Söhne, die ihm in Jerusalem geboren wurden: Schima, Schobab, Natan, Salomo,", footnotes: ["(4-7) 3,5-8"] },
      { number: 5, text: "Jibhar, Elischua, Elpelet," },
      { number: 6, text: "Nogah, Nefeg, Jafia," },
      { number: 7, text: "Elischama, Beeljada und Elifelet." },
      { number: 8, text: "Die Philister hörten, dass David zum König von ganz Israel gesalbt worden war. Da zogen alle herauf, um David gefangen zu nehmen. David erfuhr davon und zog ihnen entgegen.", heading: "Krieg mit den Philistern: 14,8-17", footnotes: ["(8-17) 2 Sam 5,17-25"] },
      { number: 9, text: "Als die Philister herankamen und in der Rafaïterebene umherstreiften," },
      { number: 10, text: "befragte David Gott: Soll ich die Philister angreifen? Wirst du sie in meine Hand geben? Der Herr antwortete ihm: Greif sie an, ich will sie in deine Hand geben." },
      { number: 11, text: "Da zog David nach Baal-Perazim. Dort schlug er die Philister und sagte: Gott hat durch meine Hand die Reihen meiner Feinde durchbrochen, wie Wasser (einen Damm) durchbricht. Deshalb nennt man jenen Ort Baal-Perazim (Herr der Durchbrüche)." },
      { number: 12, text: "Die Philister ließen dort ihre Götter zurück und David befahl, sie im Feuer zu verbrennen." },
      { number: 13, text: "Doch die Philister streiften noch einmal im Tal der Rafaïter umher." },
      { number: 14, text: "David befragte Gott wieder und Gott antwortete ihm: Zieh nicht hinter ihnen her, sondern umgeh sie und komm von den Baka-Bäumen her an sie heran!" },
      { number: 15, text: "Wenn du dann in den Wipfeln der Baka-Bäume ein Geräusch wie von Schritten hörst, dann rück zum Kampf aus; denn Gott geht vor dir her, um das Heer der Philister zu schlagen." },
      { number: 16, text: "David tat, was Gott ihm befohlen hatte, und er schlug das Heer der Philister (im ganzen Gebiet) von Gibeon bis Geser." },
      { number: 17, text: "So drang der Ruhm Davids in alle Länder und der Herr legte Furcht vor ihm auf alle Völker." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "David baute sich Häuser in der Davidstadt; er richtete auch eine Stätte für die Lade Gottes her und stellte ein Zelt für sie auf.", heading: "Die Überführung der Lade in die Davidstadt: Die Vorbereitungen:" },
      { number: 2, text: "Damals ordnete er an, dass nur die Leviten die Lade tragen dürfen; denn sie hat der Herr erwählt, seine Lade zu tragen und immerfort bei ihr Dienst zu tun." },
      { number: 3, text: "Hierauf berief David ganz Israel nach Jerusalem, um die Lade des Herrn an den Ort zu bringen, den er für sie hergerichtet hatte." },
      { number: 4, text: "Er ließ die Nachkommen Aarons und die Leviten kommen:" },
      { number: 5, text: "von den Nachkommen Kehats den Vorsteher Uriël und seine Brüder, 120 Mann," },
      { number: 6, text: "von den Nachkommen Meraris den Vorsteher Asaja und seine Brüder, 220 Mann," },
      { number: 7, text: "von den Nachkommen Gerschoms den Vorsteher Joël und seine Brüder, 130 Mann," },
      { number: 8, text: "von den Nachkommen Elizafans den Vorsteher Schemaja und seine Brüder, 200 Mann," },
      { number: 9, text: "von den Nachkommen Hebrons den Vorsteher Eliël und seine Brüder, 80 Mann," },
      { number: 10, text: "von den Nachkommen Usiëls den Vorsteher Amminadab und seine Brüder, 112 Mann." },
      { number: 11, text: "David rief die Priester Zadok und Abjatar sowie die Leviten Uriël, Asaja, Joël, Schemaja, Eliël und Amminadab zu sich" },
      { number: 12, text: "und befahl ihnen: Ihr seid die Familienhäupter der Leviten. Ihr und eure Brüder, heiligt euch und bringt die Lade des Herrn, des Gottes Israels, herauf an den Ort, den ich für sie hergerichtet habe." },
      { number: 13, text: "Weil ihr beim ersten Mal nicht beteiligt wart, hat der Herr, unser Gott, Unglück über uns gebracht; denn wir sind nicht auf ihn bedacht gewesen, wie es sich gehört hätte.", footnotes: ["13-15: Der Chronist sieht im Berühren der Lade durch Laien ein Vergehen gegen Num 4,15."] },
      { number: 14, text: "Da heiligten sich die Priester und die Leviten, um die Lade des Herrn, des Gottes Israels, heraufzubringen." },
      { number: 15, text: "Die Leviten hoben die Lade Gottes mit den Tragstangen auf ihre Schultern, wie es Mose auf Befehl des Herrn angeordnet hatte.", footnotes: ["Num 7,9"] },
      { number: 16, text: "Den Vorstehern der Leviten befahl David, sie sollten ihre Stammesbrüder, die Sänger, mit ihren Instrumenten, mit Harfen, Zithern und Zimbeln, aufstellen, damit sie zum Freudenjubel laut ihr Spiel ertönen ließen." },
      { number: 17, text: "Sie stellten daher die Leviten Heman, den Sohn Joëls, und seinen Stammesbruder Asaf, den Sohn Berechjas, auf, ferner von den Nachkommen Meraris ihren Stammesbruder Etan, den Sohn Kuschajas.", footnotes: ["Von der Abstammung der Sänger berichtet 6,16-32."] },
      { number: 18, text: "Zu ihnen kamen von ihren Brüdern im zweiten Dienstrang: Secharja, Jaasiël, Schemiramot, Jehiël, Unni, Eliab, Benaja, Maaseja, Mattitja, Elifelehu, Mikneja, Obed-Edom und Jëiël, [die Torwächter].", footnotes: ["Secharja, Jaasiël: Text korr.; H: Secharja, Sohn Jaasiëls."] },
      { number: 19, text: "Die Sänger Heman, Asaf und Etan schlugen die bronzenen Zimbeln." },
      { number: 20, text: "Secharja, Jaasiël, Schemiramot, Jehiël, Unni, Eliab, Maaseja und Benaja spielten nach elamitischer Weise auf Harfen", footnotes: ["20f: Die Bedeutung der Ausdrücke «nach elamitischer Weise» und «in der achten Stufe» ist unklar."] },
      { number: 21, text: "und Mattitja, Elifelehu, Mikneja, Obed-Edom, Jëiël und Asasja in der achten Stufe auf Zithern, um den Gesang zu führen." },
      { number: 22, text: "Kenanja, der Vorsteher der Leviten, leitete den Vortrag. Er gab dazu Anweisungen, da er sich darauf verstand." },
      { number: 23, text: "Berechja und Elkana waren Torwächter für die Lade." },
      { number: 24, text: "Die Priester Schebanja, Joschafat, Netanel, Amasai, Secharja, Benaja und Eliëser bliesen die Trompeten vor der Lade Gottes. Obed-Edom und Jehija waren Torwächter für die Lade." },
      { number: 25, text: "David, die Ältesten Israels und die Führer der Tausendschaften, die gegangen waren, um die Bundeslade des Herrn aus dem Haus Obed-Edoms heraufzuholen, waren voller Freude.", heading: "Der Einzug der Lade: 15,25 - 16,3", footnotes: ["(25-16,2) 2 Sam 6,12-19"] },
      { number: 26, text: "Damit Gott den Leviten, die die Bundeslade des Herrn trugen, beistand, opferte man sieben Stiere und sieben Widder." },
      { number: 27, text: "David war mit einem Mantel aus Byssus bekleidet, ebenso alle Leviten, die die Lade trugen, sowie die Sänger und Kenanja, der den Vortrag der Sänger leitete. Dabei trug David das leinene Efod." },
      { number: 28, text: "So brachte ganz Israel die Bundeslade des Herrn hinauf unter großem Jubelgeschrei und unter dem Klang des Widderhorns, unter dem Lärm der Trompeten und Zimbeln, beim Spiel der Harfen und Zithern." },
      { number: 29, text: "Als die Bundeslade des Herrn in die Davidstadt kam, schaute Michal, Sauls Tochter, aus dem Fenster, und als sie sah, wie König David hüpfte und tanzte, verachtete sie ihn in ihrem Herzen." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Man trug die Lade Gottes in das Zelt, das David für sie aufgestellt hatte, setzte sie an ihren Platz in der Mitte des Zeltes und brachte Brand- und Heilsopfer vor Gott dar." },
      { number: 2, text: "Als David mit dem Darbringen der Brand- und Heilsopfer fertig war, segnete er das Volk im Namen des Herrn" },
      { number: 3, text: "und ließ an alle Israeliten, Männer und Frauen, je einen Laib Brot, einen Dattelkuchen und einen Traubenkuchen austeilen." },
      { number: 4, text: "David bestellte für den Dienst vor der Lade des Herrn Leviten, die den Herrn, den Gott Israels, rühmen, loben und preisen sollten.", heading: "Das Loblied Davids: 16,4-36" },
      { number: 5, text: "Er bestellte Asaf als ersten, Secharja als zweiten, ferner Jaasiël, Schemiramot, Jehiël, Mattitja, Eliab, Benaja, Obed-Edom und Jëiël. Sie sollten die Harfen und Zithern spielen, Asaf die Zimbeln schlagen" },
      { number: 6, text: "und die Priester Benaja und Jahasiël ständig die Trompeten blasen vor der Bundeslade Gottes." },
      { number: 7, text: "An jenem Tag ließ David Asaf und seine Amtsbrüder zum ersten Mal diesen Lobpreis zur Ehre des Herrn vortragen:" },
      { number: 8, text: "Dankt dem Herrn! Ruft seinen Namen an! / Macht unter den Völkern seine Taten bekannt!", footnotes: ["(8-22) Ps 105,1-15"] },
      { number: 9, text: "Singt ihm und spielt ihm, / sinnt nach über all seine Wunder!" },
      { number: 10, text: "Rühmt euch seines heiligen Namens! / Alle, die den Herrn suchen, / sollen sich von Herzen freuen." },
      { number: 11, text: "Fragt nach dem Herrn und seiner Macht, / sucht sein Antlitz allezeit!" },
      { number: 12, text: "Denkt an die Wunder, die er getan hat, / an seine Zeichen und die Beschlüsse aus seinem Mund!" },
      { number: 13, text: "Bedenkt es, ihr Nachkommen seines Knechtes Abraham, / ihr Kinder Jakobs, die er erwählt hat!" },
      { number: 14, text: "Er, der Herr, ist unser Gott. / Seine Herrschaft umgreift die Erde." },
      { number: 15, text: "Ewig denkt er an seinen Bund, / an das Wort, das er gegeben hat für tausend Geschlechter," },
      { number: 16, text: "an den Bund, den er mit Abraham geschlossen, / an den Eid, den er Isaak geschworen hat." },
      { number: 17, text: "Er bestimmte ihn als Satzung für Jakob, / als ewigen Bund für Israel." },
      { number: 18, text: "Er sprach: Dir will ich Kanaan geben, / das Land, das dir als Erbe bestimmt ist." },
      { number: 19, text: "Als sie noch gering waren an Zahl, / nur wenige und fremd im Land," },
      { number: 20, text: "und noch zogen von Volk zu Volk, / von einem Reich zum andern," },
      { number: 21, text: "da ließ er sie von niemand bedrücken, / wies ihretwegen Könige zurecht:" },
      { number: 22, text: "Tastet meine Gesalbten nicht an, / tut meinen Propheten nichts zuleide!" },
      { number: 23, text: "Singt dem Herrn, alle Länder der Erde! / Verkündet sein Heil von Tag zu Tag!", footnotes: ["(23-33) Ps 96,1-13"] },
      { number: 24, text: "Erzählt bei den Völkern von seiner Herrlichkeit, / bei allen Nationen von seinen Wundern!" },
      { number: 25, text: "Denn groß ist der Herr und hoch zu preisen, / mehr zu fürchten als alle Götter." },
      { number: 26, text: "Alle Götter der Heiden sind nichtig, / der Herr aber hat den Himmel geschaffen." },
      { number: 27, text: "Hoheit und Pracht sind vor seinem Angesicht, / Macht und Glanz in seinem Heiligtum." },
      { number: 28, text: "Bringt dar dem Herrn, ihr Stämme der Völker, / bringt dar dem Herrn Lob und Ehre!" },
      { number: 29, text: "Bringt dar dem Herrn die Ehre seines Namens, / spendet Opfergaben und tretet vor ihn hin! / In heiligem Schmuck werft euch nieder vor dem Herrn," },
      { number: 30, text: "erbebt vor ihm, alle Länder der Erde! / Den Erdkreis hat er gegründet, sodass er nicht wankt." },
      { number: 31, text: "Der Himmel freue sich, die Erde frohlocke. / Verkündet bei den Völkern: Der Herr ist König." },
      { number: 32, text: "Es brause das Meer und alles, was es erfüllt, / es jauchze die Flur und was auf ihr wächst." },
      { number: 33, text: "Jubeln sollen alle Bäume des Waldes vor dem Herrn, / wenn er kommt, um die Erde zu richten." },
      { number: 34, text: "Danket dem Herrn, denn er ist gütig, / denn seine Huld währt ewig.", footnotes: ["Ps 106,1"] },
      { number: 35, text: "Sagt: Hilf uns, du Gott unsres Heils, / führe uns zusammen, rette uns vor den Völkern! Wir wollen deinen heiligen Namen preisen, / uns rühmen, weil wir dich loben dürfen.", footnotes: ["(35-36) Ps 106,47f"] },
      { number: 36, text: "Gepriesen sei der Herr, der Gott Israels, / vom Anfang bis ans Ende der Zeiten. Und das ganze Volk rief: Amen, und: Lob sei dem Herrn." },
      { number: 37, text: "Auf Befehl Davids blieben Asaf und seine Brüder vor der Bundeslade des Herrn, um dauernd den täglichen Dienst bei ihr zu verrichten.", heading: "Der Gottesdienst in Jerusalem und in Gibeon: 16,37-43", footnotes: ["37-41: Nach Chr wurde der liturgische Dienst fortan sowohl vor der Bundeslade in Jerusalem als auch vor dem Zelt in Gibeon vollzogen. Als Sänger wirkten Heman und Jedutun (= Etan, vgl. 15,17) in Gibeon, Asaf in Jerusalem. Da Zadok für den Opferdienst in Gibeon bestellt war, ist anzunehmen, dass Abjatar in Jerusalem wirkte."] },
      { number: 38, text: "Bei ihnen waren Obed-Edom und seine Brüder, achtundsechzig Mann. Obed-Edom, der Sohn Jedutuns, und Hosa waren Torwächter.", footnotes: ["Chr unterscheidet den Sänger Obed-Edom (15,18.21; 16,5) vom Torhüter gleichen Namens (15,24; 16,38; 26,4) und dem Nichtisraeliten aus Gat (13,13f)."] },
      { number: 39, text: "Dem Priester Zadok aber und seinen Amtsbrüdern, den Priestern, übertrug er den Dienst vor der Wohnstätte des Herrn auf der Kulthöhe in Gibeon." },
      { number: 40, text: "Sie sollten täglich am Morgen und am Abend auf dem Brandopferaltar dem Herrn Opfer darbringen und alles ausführen, was im Gesetz geschrieben steht, auf das der Herr die Israeliten verpflichtet hat." },
      { number: 41, text: "Bei ihnen waren Heman und Jedutun sowie die anderen, die ausgewählt und namentlich dazu bestimmt waren, den Herrn zu loben: «Denn seine Huld währt ewig.»" },
      { number: 42, text: "Heman und Jedutun hatten Trompeten und Zimbeln für die Spieler und Instrumente für die Gotteslieder. Die Söhne Jedutuns waren für das Tor bestellt." },
      { number: 43, text: "Dann ging das ganze Volk wieder nach Hause. Auch David ging heim, um seine Familie zu begrüßen.", footnotes: ["2 Sam 6,19f"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Als nun David in seinem Haus wohnte, sagte er zu dem Propheten Natan: Ich wohne in einem Haus aus Zedernholz, die Bundeslade des Herrn aber steht in einem Zelt.", heading: "Die Verheißung an David: 17,1-15", footnotes: ["(1-15) 2 Sam 7,1-17"] },
      { number: 2, text: "Natan antwortete David: Tu alles, was du im Sinn hast; denn Gott ist mit dir." },
      { number: 3, text: "Aber in jener Nacht erging das Wort Gottes an Natan:" },
      { number: 4, text: "Geh zu meinem Knecht David und sag zu ihm: So spricht der Herr: Nicht du sollst mir das Haus bauen, damit ich darin wohne." },
      { number: 5, text: "Seit dem Tag, als ich Israel aus Ägypten herausgeführt habe, habe ich bis zum heutigen Tag nie in einem Haus gewohnt, sondern bin von Zelt zu Zelt, von Wohnstätte zu Wohnstätte mitgewandert." },
      { number: 6, text: "Habe ich in der Zeit, als ich bei den Israeliten von Ort zu Ort zog, jemals zu einem der Richter Israels, die ich als Hirten über mein Volk eingesetzt hatte, ein Wort gesagt und sie gefragt: Warum habt ihr mir kein Haus aus Zedernholz gebaut?" },
      { number: 7, text: "Sag also jetzt meinem Knecht David: So spricht der Herr der Heere: Ich habe dich von der Weide und von der Herde weggeholt, damit du Fürst meines Volkes Israel wirst," },
      { number: 8, text: "und bin überall mit dir gewesen, wohin du auch gegangen bist. Ich habe alle deine Feinde vor deinen Augen vernichtet und ich will dir einen Namen machen, der dem Namen der Großen auf der Erde gleich ist." },
      { number: 9, text: "Ich will meinem Volk Israel einen Platz zuweisen und will es einpflanzen, damit es an seinem Ort (sicher) wohnen kann und sich nicht mehr ängstigen muss und schlechte Menschen es nicht zugrunde richten wie am Anfang," },
      { number: 10, text: "seit den Tagen, als ich Richter in meinem Volk Israel eingesetzt habe. Ich werfe alle deine Feinde nieder und mache dich groß. Der Herr aber wird dir ein Haus bauen.", footnotes: ["und mache dich groß: Text korr. nach G; H: und verkünde dir (nach 2 Sam 7,11)."] },
      { number: 11, text: "Wenn deine Tage erfüllt sind und du zu deinen Vätern gehst, dann werde ich einen von deinen Nachkommen, einen von deinen Söhnen, als deinen Nachfolger einsetzen und seinem Königtum Bestand verleihen." },
      { number: 12, text: "Er wird für mich ein Haus bauen und ich werde seinem Thron ewigen Bestand verleihen." },
      { number: 13, text: "Ich will für ihn Vater sein und er wird für mich Sohn sein. Meine Huld will ich ihm nicht entziehen, wie ich sie dem entzogen habe, der vor dir König war." },
      { number: 14, text: "In meinem Haus und in meinem Königtum werde ich ihm ewigen Bestand verleihen; sein Thron wird für immer bestehen bleiben." },
      { number: 15, text: "Natan sprach zu David genau so, wie es (ihm) gesagt und offenbart worden war." },
      { number: 16, text: "Da ging König David hin, setzte sich vor dem Herrn nieder und sagte: Wer bin ich, mein Herr und Gott, und was ist mein Haus, dass du mich bis hierher geführt hast?", heading: "Das Gebet Davids: 17,16-27", footnotes: ["(16-27) 2 Sam 7,18-29"] },
      { number: 17, text: "Weil das in deinen Augen noch zu wenig war, o Gott, hast du dem Haus deines Knechtes Zusagen für eine ferne Zukunft gemacht und mich angeschaut . . ., mein Herr und Gott.", footnotes: ["H ist unverständlich."] },
      { number: 18, text: "Was soll David noch weiter zu dir sagen? Du kennst deinen Knecht.", footnotes: ["zu dir sagen: Text korr. nach 2 Sam 7,20."] },
      { number: 19, text: "Herr, um deines Knechtes willen und nach der Absicht deines Herzens hast du alle diese großen Taten getan und alle diese großen Dinge offenbart." },
      { number: 20, text: "Herr, keiner ist dir gleich und außer dir gibt es keinen Gott nach allem, was wir mit unseren Ohren gehört haben." },
      { number: 21, text: "Welches andere Volk auf der Erde ist wie dein Volk Israel? Wo wäre ein Gott hingegangen, um ein Volk für sich freizukaufen und sich den Ruhm großer und erstaunlicher Taten zu verschaffen? Du hast vor den Augen deines Volkes, das du von den Ägyptern freigekauft hast, ganze Völker vertrieben.", footnotes: ["Text korr. nach G."] },
      { number: 22, text: "Du hast Israel für immer zu deinem Volk bestimmt und du, Herr, bist sein Gott geworden." },
      { number: 23, text: "Möge doch jetzt auch das Wort, Herr, das du über deinen Knecht und über sein Haus gesprochen hast, sich für immer als wahr erweisen. Tu, was du gesagt hast." },
      { number: 24, text: "Dann wird dein Name sich als wahr erweisen und groß sein für ewige Zeiten und man wird sagen: Der Herr der Heere ist Israels Gott!, und das Haus deines Knechtes David wird vor deinen Augen Bestand haben.", footnotes: ["Ex 3,13-15"] },
      { number: 25, text: "Denn du, mein Gott, hast deinem Knecht offenbart: Ich will dir ein Haus bauen. Darum fand dein Knecht den Mut, zu dir zu beten." },
      { number: 26, text: "Ja, Herr, du bist der einzige Gott; du hast deinem Knecht ein solches Glück zugesagt." },
      { number: 27, text: "Du hast jetzt gnädig das Haus deines Knechtes gesegnet, damit es ewig vor deinen Augen Bestand hat. Denn du, Herr, hast es gesegnet und es bleibt für immer gesegnet." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Hernach schlug David die Philister. Er unterwarf sie und nahm ihnen Gat und seine Tochterstädte weg.", heading: "Die Siege Davids: 18,1-13", footnotes: ["(1-13) 2 Sam 8,1-14"] },
      { number: 2, text: "Auch die Moabiter schlug er; sie wurden David untertan und tributpflichtig." },
      { number: 3, text: "David schlug auch Hadad-Eser, den König von Zoba, das in Richtung Hamat liegt, als dieser ausgezogen war, um seine Macht am Eufrat zu festigen." },
      { number: 4, text: "David erbeutete von ihm tausend Wagen und nahm siebentausend Wagenkämpfer und zwanzigtausend Fußsoldaten gefangen; er ließ alle Wagenpferde lähmen und behielt nur hundert von ihnen für sich zurück.", footnotes: ["In 2 Sam 8,4 ist nur von siebzehnhundert Wagenkämpfern die Rede."] },
      { number: 5, text: "Als die Aramäer von Damaskus Hadad-Eser, dem König von Zoba, zu Hilfe kamen, erschlug David zweiundzwanzigtausend von ihnen" },
      { number: 6, text: "und besetzte das Aramäerreich von Damaskus. So wurden die Aramäer David untertan und tributpflichtig. Der Herr half David bei allem, was er unternahm." },
      { number: 7, text: "David erbeutete die goldenen Schilde, die die Krieger Hadad-Esers trugen, und brachte sie nach Jerusalem." },
      { number: 8, text: "In Tibhat und Kun, den Städten Hadad-Esers, erbeutete David eine sehr große Menge Bronze, aus der Salomo später das «Eherne Meer», die Säulen und die bronzenen Geräte anfertigen ließ.", footnotes: ["2 Chr 4"] },
      { number: 9, text: "Als Toï, der König von Hamat, hörte, dass David das ganze Heer Hadad-Esers, des Königs von Zoba, geschlagen hatte," },
      { number: 10, text: "schickte er seinen Sohn Hadoram mit Gefäßen aus Gold, Silber und Bronze zu König David. Er ließ ihm den Friedensgruß entbieten und ihn dazu beglückwünschen, dass er Hadad-Eser im Krieg geschlagen hatte. Toë lebte nämlich im Krieg mit Hadad-Eser." },
      { number: 11, text: "König David weihte auch diese Gefäße dem Herrn, zusammen mit dem Silber und Gold, das er von allen Völkern, von Edom und Moab, von den Ammonitern, Philistern und Amalekitern erbeutet hatte." },
      { number: 12, text: "Abischai, der Sohn der Zeruja, schlug von Edom im Salztal achtzehntausend Mann" },
      { number: 13, text: "und David setzte in Edom Vögte ein. So wurde ganz Edom von David unterworfen. Der Herr half David bei allem, was er unternahm." },
      { number: 14, text: "David war König von ganz Israel und sorgte für Recht und Gerechtigkeit in seinem ganzen Volk.", heading: "Die Beamten Davids: 18,14-17", footnotes: ["(14-17) 2 Sam 8,15-18; 20,23-26"] },
      { number: 15, text: "Joab, der Sohn der Zeruja, war Heerführer. Joschafat, der Sohn Ahiluds, war Sprecher des Königs." },
      { number: 16, text: "Zadok, der Sohn Ahitubs, und Abjatar, der Sohn Ahimelechs, waren Priester. Schawscha war Staatsschreiber.", footnotes: ["Text korr. nach 1 Sam 22,20; 23,6 und 30,7."] },
      { number: 17, text: "Benaja, der Sohn Jojadas, war Befehlshaber der Kereter und Peleter. Die Söhne Davids waren die Ersten an der Seite des Königs.", footnotes: ["Nach 2 Sam 8,18 waren die Söhne Davids Priester. Chr vermeidet diese Bezeichnung, da nach dem zu ihrer Zeit geltenden Gesetz nur Angehörige des Stamms Levi Priester sein konnten."] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Als darauf Nahasch, der König der Ammoniter, starb und sein Sohn an seiner Stelle König wurde,", heading: "Der Krieg mit den Ammonitern und Aramäern: 19,1 - 20,3", footnotes: ["(1-20,3) 2 Sam 10,1 - 11,1; 12,30-31"] },
      { number: 2, text: "sagte David: Ich will Hanun, dem Sohn des Nahasch, mein Wohlwollen zeigen, weil sein Vater auch mir sein Wohlwollen gezeigt hat. Und David schickte eine Gesandtschaft zu Hanun und ließ ihm sein Beileid zum Tod seines Vaters aussprechen. Als die Diener Davids in das Land der Ammoniter kamen, um Hanun das Beileid auszusprechen," },
      { number: 3, text: "sagten die Fürsten der Ammoniter zu Hanun: Will David wirklich deinen Vater vor deinen Augen ehren, weil er Leute schickt, die dir sein Beileid aussprechen sollen? Sind seine Diener nicht viel eher zu dir gekommen, um das Land auszukundschaften und zu durchforschen und es dann zu verwüsten?" },
      { number: 4, text: "Darauf ließ Hanun die Diener Davids festnehmen, ihnen den Bart scheren und ihnen die Kleider zur Hälfte abschneiden, bis zum Gesäß herauf. So schickte er sie weg." },
      { number: 5, text: "Sie zogen fort, und als man David meldete, was mit den Männern geschehen war, schickte er ihnen jemand entgegen und ließ ihnen, weil sie so schwer geschändet waren, sagen: Bleibt in Jericho und kehrt erst zurück, wenn euer Bart wieder gewachsen ist." },
      { number: 6, text: "Als die Ammoniter merkten, dass sie sich bei David verhasst gemacht hatten, schickten Hanun und die Ammoniter tausend Talente Silber, um in Mesopotamien, Aram-Maacha und Zoba Wagenkämpfer samt Wagen anzuwerben." },
      { number: 7, text: "Sie warben zweiunddreißigtausend Kriegswagen an und gewannen den König von Maacha mit seinen Kriegsleuten. Diese zogen heran und lagerten vor Medeba. Auch die Ammoniter sammelten sich aus ihren Städten und zogen in den Kampf.", footnotes: ["Medeba: östlich der Nordküste des Toten Meers."] },
      { number: 8, text: "David erfuhr davon und schickte Joab mit dem ganzen Heer, allen kriegstüchtigen Männern, hin." },
      { number: 9, text: "Die Ammoniter rückten aus und stellten sich vor der Stadt zum Kampf auf, während die Könige, die gekommen waren, jeweils für sich draußen auf dem freien Feld standen." },
      { number: 10, text: "Als Joab sah, dass ihm ein Angriff von vorn und von hinten drohte, traf er eine Auswahl aus der Kerntruppe Israels und stellte sie gegen die Aramäer auf." },
      { number: 11, text: "Den Rest des Heeres unterstellte er seinem Bruder Abischai, damit sie sich den Ammonitern gegenüber aufstellten." },
      { number: 12, text: "Joab sagte: Wenn die Aramäer stärker sind als ich, dann komm du mir zu Hilfe! Wenn die Ammoniter stärker sind als du, dann werde ich dir zu Hilfe kommen." },
      { number: 13, text: "Sei tapfer! Wir wollen mutig für unser Volk und für die Städte unseres Gottes kämpfen. Der Herr aber möge tun, was er für recht hält." },
      { number: 14, text: "Dann griff Joab mit seinen Leuten die Aramäer an und diese ergriffen die Flucht vor ihm." },
      { number: 15, text: "Als die Ammoniter sahen, dass die Aramäer flohen, ergriffen sie die Flucht vor seinem Bruder Abischai und zogen sich in die Stadt zurück. Joab kehrte heim nach Jerusalem." },
      { number: 16, text: "Die Aramäer sahen, dass sie von Israel geschlagen waren. Sie schickten Boten und ließen die Aramäer, die diesseits des Stromes wohnten, ausrücken; an ihrer Spitze stand Schobach, der Oberbefehlshaber Hadad-Esers.", footnotes: ["diesseits: vgl. die Anmerkung zu 2 Sam 10,16."] },
      { number: 17, text: "Als man das David meldete, versammelte er ganz Israel, überschritt den Jordan, zog gegen sie und nahm ihnen gegenüber Aufstellung. Während er sich den Aramäern gegenüber zum Kampf aufstellte, eröffneten sie den Kampf." },
      { number: 18, text: "Doch sie mussten vor den Israeliten fliehen. David vernichtete siebentausend aramäische Kriegswagen und tötete vierzigtausend Mann von ihren Fußtruppen. Auch Schobach, den Oberbefehlshaber des Heeres, tötete er.", footnotes: ["In 2 Sam 10,18 ist nur von siebenhundert Wagen die Rede."] },
      { number: 19, text: "Die Vasallen Hadad-Esers sahen nun ein, dass sie von den Israeliten besiegt waren. Sie schlossen Frieden mit David und unterwarfen sich ihm. Von da an hatten die Aramäer kein Verlangen mehr, den Ammonitern zu helfen." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Um die Jahreswende, zu der Zeit, in der die Könige in den Krieg ziehen, führte Joab die Streitmacht in das Feld und verwüstete das Land der Ammoniter. Er rückte bis Rabba vor und belagerte es. David selbst blieb in Jerusalem. Joab eroberte Rabba und zerstörte es." },
      { number: 2, text: "David nahm ihrem König die Krone vom Haupt. Er stellte fest, dass ihr Gewicht ein Talent Gold betrug; an ihr war ein kostbarer Stein. Sie wurde nun Davids Krone. Und er schaffte eine sehr große Beute aus der Stadt fort." },
      { number: 3, text: "Auch ihre Einwohner führte er fort und stellte sie an die Steinsägen, an die eisernen Spitzhacken und an die eisernen Äxte. So machte er es mit allen Städten der Ammoniter. Dann kehrte David mit dem ganzen Heer nach Jerusalem zurück.", footnotes: ["David blieb während der Belagerung Rabbas in Jerusalem. In diese Zeit fällt sein Vergehen mit Batseba, der Frau des Hetiters Urija (vgl. 2 Sam 11,2 - 12,25), über das der Chronist nicht berichtet. und stellte sie: Text korr. nach 2 Sam 12,31."] },
      { number: 4, text: "Danach kam es bei Geser wieder zum Kampf mit den Philistern. Damals erschlug Sibbechai aus Huscha den Sippai, der zu den Rafaïtern gehörte. Die Philister wurden niedergeworfen.", heading: "Einzelne Heldentaten: 20,4-8", footnotes: ["(4-8) 2 Sam 21,18-22", "kam es wieder: Text korr. mit G und 2 Sam 21,18."] },
      { number: 5, text: "Als es wieder einmal zum Kampf gegen die Philister kam, erschlug Elhanan, der Sohn Jaïrs, den Lachmi, den Bruder Goljats aus Gat, dessen Speer einem Weberbaum glich.", footnotes: ["Der Chronist will hier den Widerspruch zwischen 1 Sam 17 und 2 Sam 21,19 ausgleichen."] },
      { number: 6, text: "Dann kam es noch einmal bei Gat zum Kampf. Da trat ein Mann von riesenhafter Größe auf; er hatte je sechs Finger und sechs Zehen, zusammen vierundzwanzig; auch er stammte von Rafa ab." },
      { number: 7, text: "Als er Israel verhöhnte, erschlug ihn Jonatan, der Sohn von Davids Bruder Schima." },
      { number: 8, text: "Diese stammten von Rafa aus Gat ab; sie fielen durch die Hand Davids und seiner Krieger." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Der Satan trat gegen Israel auf und reizte David, Israel zu zählen.", heading: "Davids Volkszählung: 21,1-17", footnotes: ["(1-17) 2 Sam 24,1-17", "Zur Verwerflichkeit der Volkszählung vgl. die Anmerkung zu 2 Sam 24,1. Während nach 2 Sam Gott selbst in David den Entschluss zur Volkszählung weckte, wird dies in Chr dem Satan zugeschrieben, der in Ijob 1 - 2 und in Sach 3,1f als Widersacher und Gegenspieler Gottes erscheint."] },
      { number: 2, text: "David befahl Joab und den Anführern des Volkes: Geht, zählt Israel von Beerscheba bis Dan und bringt mir Bescheid, damit ich weiß, wie viele es sind." },
      { number: 3, text: "Joab aber sagte zum König: Der Herr möge sein Volk vermehren, hundertmal mehr, als es jetzt ist. Sind denn nicht alle, mein Herr und König, Untertanen meines Herrn? Warum hat mein Herr diesen Wunsch? Warum soll Israel in Schuld geraten?" },
      { number: 4, text: "Doch der König beharrte gegenüber Joab auf seinem Befehl. So ging Joab weg und durchzog ganz Israel. Als er nach Jerusalem zurückkam," },
      { number: 5, text: "gab er David das Ergebnis der Volkszählung bekannt. Ganz Israel zählte 1100000 Krieger, die mit dem Schwert kämpfen konnten, und Juda zählte 470000 Mann, die mit dem Schwert kämpfen konnten.", footnotes: ["Nach dem Wortlaut ist die Zahl des Stammes Juda in den 1,1 Millionen enthalten. Die Zahlen in 2 Sam 24,9 ergeben 1,3 Millionen. Erfasst wurden nur die Wehrfähigen. Chr ergänzt, dass auch der Stamm Benjamin, auf dessen Gebiet Jerusalem lag, und der Stamm Levi, der den Kultdienst besorgte, in die Zählung nicht einbezogen waren."] },
      { number: 6, text: "Levi und Benjamin hatte Joab nicht zusammen mit ihnen gezählt, denn der Befehl des Königs war ihm ein Gräuel." },
      { number: 7, text: "Doch das missfiel Gott; darum schlug er Israel." },
      { number: 8, text: "Nun sagte David zu Gott: Ich habe schwer gesündigt, weil ich das getan habe. Doch vergib deinem Knecht seine Schuld, denn ich habe sehr unvernünftig gehandelt." },
      { number: 9, text: "Der Herr aber sprach zu Gad, dem Seher Davids:" },
      { number: 10, text: "Geh und sag zu David: So spricht der Herr: Dreierlei lege ich dir vor. Wähl dir eines davon! Das werde ich dir antun." },
      { number: 11, text: "Gad kam zu David und sagte zu ihm: So spricht der Herr: Wähle dir:" },
      { number: 12, text: "drei Jahre Hungersnot, oder drei Monate, in denen du vor deinen Feinden fliehen musst und das Schwert deiner Gegner dich verfolgt, oder drei Tage, in denen das Schwert des Herrn, die Pest, im Land wütet und der Engel des Herrn über alle Gebiete Israels Verderben bringt. Überleg nun, was ich dem, der mich gesandt hat, als Antwort überbringen soll.", footnotes: ["fliehen musst: Text korr. nach G und 2 Sam 24,13."] },
      { number: 13, text: "Da sagte David zu Gad: Ich habe große Angst. Ich will lieber dem Herrn in die Hände fallen, denn seine Barmherzigkeit ist groß. Den Menschen aber möchte ich nicht in die Hände fallen.", footnotes: ["Davids Antwort schließt nur die zweite Möglichkeit aus."] },
      { number: 14, text: "Da ließ der Herr über Israel eine Pest kommen und es kamen in Israel siebzigtausend Menschen um." },
      { number: 15, text: "Gott sandte einen Engel nach Jerusalem, um es ins Verderben zu stürzen. Doch als er mit der Vernichtung begann, sah es der Herr und das Unheil reute ihn. Er sagte zu dem Engel des Verderbens: Es ist jetzt genug, lass deine Hand sinken! Der Engel des Herrn stand gerade bei der Tenne des Jebusiters Arauna." },
      { number: 16, text: "Als David aufblickte, sah er den Engel des Herrn zwischen Erde und Himmel stehen. Er hielt das gezückte Schwert in der Hand gegen Jerusalem gerichtet. Da fielen David und die Ältesten, die in Trauergewänder gehüllt waren, auf ihr Angesicht nieder" },
      { number: 17, text: "und David rief zu Gott: Habe nicht ich befohlen, das Volk zu zählen? Ich bin es doch, der gesündigt und Böses getan hat. Aber diese, die Herde, was haben denn sie getan? Herr, mein Gott, erheb deine Hand zum Schlag gegen mich und gegen das Haus meines Vaters, nicht aber gegen dein Volk!" },
      { number: 18, text: "Der Engel des Herrn befahl Gad, zu David zu sagen, er solle hinaufgehen und dem Herrn auf der Tenne des Jebusiters Arauna einen Altar errichten.", heading: "Die Bestimmung des Tempelplatzes: 21,18-30", footnotes: ["(18-26) 2 Sam 24,18-25"] },
      { number: 19, text: "David ging hinauf, wie ihm Gad im Namen des Herrn befohlen hatte." },
      { number: 20, text: "Arauna hatte sich umgewandt und den Engel erblickt. Seine vier Söhne, die bei ihm waren, versteckten sich. Er drosch gerade Weizen." },
      { number: 21, text: "Als nun David zu Arauna kam und dieser aufschaute und David sah, kam er aus der Tenne heraus und warf sich vor David mit dem Gesicht zur Erde nieder." },
      { number: 22, text: "Da sagte David zu Arauna: Überlass mir den Platz der Tenne! Ich möchte auf ihm einen Altar für den Herrn errichten. Um den vollen Kaufpreis sollst du ihn mir geben, damit die Plage im Volk aufhört." },
      { number: 23, text: "Arauna antwortete David: Du magst ihn nehmen; mein Herr und König tue, was er für gut findet. Ich gebe dir die Rinder für die Brandopfer, die Dreschschlitten als Brennholz und den Weizen zum Speiseopfer; ich will dir alles geben." },
      { number: 24, text: "Doch König David sagte zu Arauna: Nein, ich will dir den Platz zum vollen Preis abkaufen. Ich will nicht dein Eigentum wegnehmen und dem Herrn unbezahlte Brandopfer darbringen." },
      { number: 25, text: "So gab David dem Arauna für den Platz sechshundert abgewogene Goldschekel.", footnotes: ["Nach 2 Sam 24,24 betrug der Preis für das Grundstück fünfzig Schekel Silber. Chr nennt eine zwölfmal höhere Zahl und spricht von Goldschekeln. Dadurch wird angedeutet, dass der Platz für alle zwölf Stämme erworben wurde und dass es sich um einen sehr kostbaren Bauplatz, nämlich den Tempelplatz, handelt."] },
      { number: 26, text: "Er baute dort einen Altar für den Herrn, brachte Brand- und Heilsopfer dar und rief den Herrn an. Dieser antwortete ihm durch Feuer, das vom Himmel auf den Altar des Brandopfers niederfiel." },
      { number: 27, text: "Dem Engel aber gebot der Herr, das Schwert in die Scheide zu stecken." },
      { number: 28, text: "Damals merkte David, dass ihn der Herr auf der Tenne des Jebusiters Arauna erhörte, und brachte dort Opfer dar." },
      { number: 29, text: "Die Wohnstätte des Herrn aber, die Mose in der Wüste angefertigt hatte, und der Brandopferaltar waren zu jener Zeit auf der Kulthöhe von Gibeon.", footnotes: ["Ex 36,8-38; 2 Chr 1,3-5", "In Gibeon wurden zwar noch die vorgeschriebenen Opfer dargebracht und der kultische Dienst vollzogen (16,39f); doch David vermied es, daran teilzunehmen."] },
      { number: 30, text: "Doch David konnte sich nicht mehr dorthin begeben, um Gott aufzusuchen; denn ihn hatte vor dem Schwert des Engels des Herrn Schrecken erfasst." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Damals sagte David: Hier soll das Haus Gottes, des Herrn, stehen und hier der Altar für die Brandopfer Israels.", heading: "Die Vorbereitungen für den Tempelbau: 22,1-19", footnotes: ["1-29,9: Einzelne Aussagen dieser Kapitel lassen erkennen, dass dem Chronisten das heilige Zelt der Wüstenzeit nach der Beschreibung von Ex und der nachexilische Tempel vorschwebten."] },
      { number: 2, text: "Er ließ daher die Fremden im Land Israel zusammenholen und bestellte sie als Steinmetzen, die Quadern für den Bau des Hauses Gottes behauen sollten.", footnotes: ["In 1 Kön 9,20f wird das von Salomo berichtet. Doch nennt bereits die Beamtenliste Davids in 2 Sam 20,24 einen Aufseher über die Fronarbeiter."] },
      { number: 3, text: "Auch ließ er eine Menge Eisen für die Nägel, für die Torflügel und für die Klammern bereitstellen, dazu Bronze in einer Menge, dass sie nicht mehr gewogen werden konnte," },
      { number: 4, text: "und ungezählte Zedernstämme. Die Bewohner von Sidon und Tyrus lieferten David Zedernholz in Menge." },
      { number: 5, text: "David dachte nämlich: Mein Sohn Salomo ist noch jung und unerfahren; das Haus aber, das dem Herrn gebaut werden soll, muss groß werden und in aller Welt Lob und Bewunderung finden. Ich will daher Vorbereitungen dazu treffen. So stellte David vor seinem Tod vieles bereit." },
      { number: 6, text: "Dann rief er seinen Sohn Salomo und trug ihm auf, dem Herrn, dem Gott Israels, ein Haus zu bauen." },
      { number: 7, text: "Er sagte zu ihm: Ich selbst hatte vor, dem Namen des Herrn, meines Gottes, ein Haus zu bauen.", footnotes: ["2 Sam 7"] },
      { number: 8, text: "Da erging das Wort des Herrn an mich: Du hast viel Blut vergossen und schwere Kriege geführt. Du sollst meinem Namen kein Haus bauen, denn du hast vor meinen Augen viel Blut zur Erde fließen lassen.", footnotes: ["1 Kön 5,17"] },
      { number: 9, text: "Doch wurde dir ein Sohn geboren. Dieser wird ein Mann der Ruhe sein: Ich will ihm Ruhe vor allen seinen Feinden ringsum verschaffen. Salomo ist sein Name und in seinen Tagen werde ich Israel Frieden und Ruhe gewähren.", footnotes: ["Der Name Salomo enthält den Wortstamm schalom (= Frieden)."] },
      { number: 10, text: "Er wird meinem Namen ein Haus bauen; er wird für mich Sohn sein und ich werde für ihn Vater sein. Seinen Königsthron werde ich in Israel festigen für immer.", footnotes: ["17,12"] },
      { number: 11, text: "Möge jetzt der Herr mit dir sein, mein Sohn, damit du Erfolg hast und das Haus des Herrn, deines Gottes, baust, wie er es von dir vorausgesagt hat." },
      { number: 12, text: "Nur gebe dir der Herr Klugheit und Einsicht. Er mache dich zum Gebieter in Israel und helfe dir, das Gesetz des Herrn, deines Gottes, zu erfüllen." },
      { number: 13, text: "Du wirst Erfolg haben, wenn du die Gesetze und Rechtsvorschriften gewissenhaft befolgst, die der Herr dem Mose für Israel aufgetragen hat. Sei mutig und stark! Fürchte dich nicht und verzag nicht!" },
      { number: 14, text: "Sieh her, trotz aller Not konnte ich für das Haus des Herrn hunderttausend Talente Gold und eine Million Talente Silber bereitstellen. Bronze und Eisen sind in solchen Mengen vorhanden, dass sie nicht gewogen werden können. Auch Holz und Steine habe ich herbeigeschafft und du wirst noch mehr dazutun.", footnotes: ["Die Zahlen für die Gold- und Silbermengen sind ungewöhnlich hoch und daher wohl nur Ausdruck für die Hochschätzung des Tempels."] },
      { number: 15, text: "Du hast Handwerker in großer Anzahl, Steinmetzen, Maurer, Zimmerleute und zahllose Künstler, die jede Arbeit" },
      { number: 16, text: "in Gold, Silber, Bronze und Eisen ausführen können. Auf denn, geh ans Werk und der Herr sei mit dir." },
      { number: 17, text: "Darauf befahl David allen führenden Männern Israels, seinem Sohn Salomo zu helfen:" },
      { number: 18, text: "Ist nicht der Herr, euer Gott, mit euch gewesen und hat euch ringsum Ruhe verschafft? Er hat die Bewohner des Landes in meine Gewalt gegeben, sodass nun das Land dem Herrn und seinem Volk unterworfen ist." },
      { number: 19, text: "Richtet daher euer Herz und euren Sinn darauf, den Herrn, euren Gott, zu suchen. Auf denn, baut das Heiligtum Gottes, des Herrn, um dann die Bundeslade des Herrn und die heiligen Geräte Gottes in das Haus zu bringen, das man seinem Namen errichtet." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "David war alt und hochbetagt und bestellte seinen Sohn Salomo zum König von Israel.", heading: "Die Dienstklassen der Leviten: 23,1-23", footnotes: ["1-26,32: Die Einfügung der folgenden Listen beruht auf der Meinung, dass David bereits den Gottesdienst im neuen Tempel geordnet hat. Sie gehören aber der späteren Zeit an (vgl. 23,24-28)."] },
      { number: 2, text: "Er versammelte dazu alle führenden Männer Israels, die Priester und Leviten." },
      { number: 3, text: "Die Leviten waren von dreißig Jahren an aufwärts gezählt worden. Ihre Zahl betrug 38000 Mann.", footnotes: ["Als Beginn des Dienstalters der Leviten werden dreißig Jahre (Num 4,2f.23.30; 1 Chr 23,3), fünfundzwanzig Jahre (Num 8,24) und zwanzig Jahre (1 Chr 23,21.27; 2 Chr 31,17; Esra 3,8) genannt. Zu diesen Änderungen kam es, als in nachexilischer Zeit der liturgische Dienst an Bedeutung gewann und der Aufgabenbereich der Leviten sich vergrößerte."] },
      { number: 4, text: "Von diesen hatten 24000 den Dienst im Haus des Herrn zu leiten, 6000 waren Listenführer und Richter," },
      { number: 5, text: "4000 Torwächter und 4000 sollten den Herrn mit den Instrumenten preisen, die David zum Lobpreis anfertigen ließ.", footnotes: ["die David . . . anfertigen ließ: Text korr. nach G."] },
      { number: 6, text: "David teilte die Leviten in Klassen ein nach den Söhnen Levis: Gerschon, Kehat und Merari." },
      { number: 7, text: "Zu den Nachkommen Gerschons gehörten Ladan und Schimi." },
      { number: 8, text: "Von den Söhnen Ladans war Jehiël der erste; ihm folgten Setam und Joël; insgesamt waren es drei." },
      { number: 9, text: "Die Söhne Jehiëls waren Schelomit, Hasiël und Haran, insgesamt drei. Sie waren die Familienhäupter Ladans.", footnotes: ["In H steht für Jehiël der Name Schimi, dessen Söhne jedoch in V. 10 genannt werden. Man erwartet einen der Söhne Ladans."] },
      { number: 10, text: "Die Söhne Schimis waren Jahat, Sisa, Jëusch und Beria. Das waren die vier Söhne Schimis." },
      { number: 11, text: "Jahat war der erste, Sisa der zweite. Jëusch und Beria hatten nur wenige Söhne; sie bildeten daher nur eine Großfamilie und eine Dienstklasse." },
      { number: 12, text: "Die Söhne Kehats waren: Amram, Jizhar, Hebron und Usiël, insgesamt vier.", footnotes: ["5,28f; 6,3"] },
      { number: 13, text: "Die Söhne Amrams waren Aaron und Mose. Aaron und seine Söhne wurden für immer dazu bestimmt, Hochheiliges zu betreuen, Rauchopfer vor dem Herrn zu verbrennen, ihm zu dienen und allezeit in seinem Namen zu segnen." },
      { number: 14, text: "Die Söhne des Mose aber, des Mannes Gottes, wurden dem Stamm der Leviten zugerechnet." },
      { number: 15, text: "Die Söhne des Mose waren Gerschom und Eliëser.", footnotes: ["Ex 18,3f"] },
      { number: 16, text: "Von den Söhnen Gerschoms war Schubaël der erste." },
      { number: 17, text: "Der Erstgeborene Eliësers war Rehabja. Andere Söhne hatte er nicht. Die Söhne Rehabjas dagegen waren sehr zahlreich." },
      { number: 18, text: "Von den Söhnen Jizhars war Schelomit der erste." },
      { number: 19, text: "Die Söhne Hebrons waren: der erste Jerija, der zweite Amarja, der dritte Jahasiël, der vierte Jekamam." },
      { number: 20, text: "Von den Söhnen Usiëls war Micha der erste und Jischija der zweite." },
      { number: 21, text: "Die Söhne Meraris waren Machli und Muschi. Die Söhne Machlis waren Eleasar und Kisch.", footnotes: ["6,4"] },
      { number: 22, text: "Eleasar hinterließ keine Söhne, als er starb; er hatte nur Töchter und diese heirateten die Söhne des Kisch, ihre Vettern." },
      { number: 23, text: "Die Söhne Muschis waren: Machli, Eder und Jeremot, insgesamt drei." },
      { number: 24, text: "Das waren die Leviten nach ihren Großfamilien mit deren Häuptern, wie sie Kopf für Kopf gezählt und bestellt worden waren. Sie besorgten, von zwanzig Jahren an aufwärts, den Dienst am Haus des Herrn.", heading: "Die Aufgaben der Leviten: 23,24-32" },
      { number: 27, text: "Nach den letzten Anordnungen Davids zählte man die Leviten von zwanzig Jahren an aufwärts.", footnotes: ["Vers sinngemäß umgestellt."] },
      { number: 25, text: "David sagte nämlich: Der Herr, der Gott Israels, hat seinem Volk Israel Ruhe verliehen und für immer in Jerusalem Wohnung genommen." },
      { number: 26, text: "Die Leviten brauchen das Zelt und all seine Geräte für den Dienst nicht mehr zu tragen.", footnotes: ["Num 3,5-9"] },
      { number: 28, text: "Ihre Stellung im Dienst des Hauses des Herrn an der Seite der Söhne Aarons verpflichtet sie vielmehr zur Aufsicht über die Höfe und Kammern, zur Reinigung alles Heiligen, zum Dienst im Haus Gottes," },
      { number: 29, text: "zur Besorgung der Schaubrote, des Feinmehls für die Speiseopfer und die ungesäuerten Brote, der Bratpfannen und des Backwerks sowie zur Überwachung der Hohl- und Längenmaße." },
      { number: 30, text: "Sie müssen auch jeden Morgen und Abend bereitstehen, um den Herrn zu loben und zu preisen." },
      { number: 31, text: "Desgleichen sollen sie bei allen Brandopfern, die an den Sabbaten, Neumonden und Festen dem Herrn dargebracht werden, vollzählig, wie es ihre Pflicht ist, immer vor ihm erscheinen." },
      { number: 32, text: "So sollen sie den Dienst im Offenbarungszelt, den Dienst am Heiligtum und die Bedienung der Söhne Aarons, ihrer Amtsbrüder, bei ihren Verrichtungen im Haus des Herrn übernehmen." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Auch die Nachkommen Aarons waren in Abteilungen gegliedert. Die Söhne Aarons waren: Nadab, Abihu, Eleasar und Itamar.", heading: "Die Dienstklassen der Priester: 24,1-19", footnotes: ["5,29"] },
      { number: 2, text: "Nadab und Abihu starben vor ihrem Vater. Da sie keine Söhne hatten, übernahmen Eleasar und Itamar das Priestertum.", footnotes: ["Lev 10,1f"] },
      { number: 3, text: "David teilte sie gemeinsam mit Zadok, einem Nachkommen Eleasars, und mit Ahimelech, einem Nachkommen Itamars, in ihre Dienstklassen ein." },
      { number: 4, text: "Dabei ergab sich, dass bei den Nachkommen Eleasars mehr führende Männer waren als bei den Nachkommen Itamars. Man teilte daher den Nachkommen Eleasars sechzehn Oberhäupter von Großfamilien zu und den Nachkommen Itamars nur acht für ihre Großfamilien." },
      { number: 5, text: "Doch teilte man diese wie jene durch das Los ein; denn Fürsten des Heiligtums, Fürsten Gottes, waren sowohl unter den Nachkommen Eleasars wie auch unter den Nachkommen Itamars." },
      { number: 6, text: "Der Schreiber Schemaja, der Sohn Netanels, ein Levit, schrieb sie auf im Beisein des Königs, der führenden Männer, des Priesters Zadok, Abjatars, des Sohnes Ahimelechs, sowie der Familienhäupter der Priester und Leviten. Jeweils zwei Großfamilien wurden aus der Linie Eleasars und eine aus der Linie Itamars ausgelost.", footnotes: ["Abjatars, des Sohnes Ahimelechs: Text korr. wie in 18,16, jeweils zwei: sinngemäß korr. - und eine: Text korr. nach G."] },
      { number: 7, text: "Das erste Los fiel auf Jojarib, das zweite auf Jedaja," },
      { number: 8, text: "das dritte auf Harim, das vierte auf Seorim," },
      { number: 9, text: "das fünfte auf Malkija, das sechste auf Mijamin," },
      { number: 10, text: "das siebte auf Koz, das achte auf Abija,", footnotes: ["Zur Priesterklasse Abija gehörte Zacharias, der Vater Johannes' des Täufers (vgl. Lk 1,5)."] },
      { number: 11, text: "das neunte auf Jeschua, das zehnte auf Schechanja," },
      { number: 12, text: "das elfte auf Eljaschib, das zwölfte auf Jakim," },
      { number: 13, text: "das dreizehnte auf Huppa, das vierzehnte auf Jeschebab," },
      { number: 14, text: "das fünfzehnte auf Bilga, das sechzehnte auf Immer," },
      { number: 15, text: "das siebzehnte auf Hesir, das achtzehnte auf Pizzez," },
      { number: 16, text: "das neunzehnte auf Petachja, das zwanzigste auf Jeheskel," },
      { number: 17, text: "das einundzwanzigste auf Jachin, das zweiundzwanzigste auf Gamul," },
      { number: 18, text: "das dreiundzwanzigste auf Delaja, das vierundzwanzigste auf Maasja." },
      { number: 19, text: "Das waren ihre Dienstklassen, nach denen sie in das Haus des Herrn kommen mussten, wie es ihre Pflicht war, die sie von ihrem Vater Aaron übernommen hatten; so hatte es ihm der Herr, der Gott Israels, befohlen." },
      { number: 20, text: "Zu den übrigen Leviten, und zwar zu den Söhnen Amrams, gehörte Schubaël, zu den Söhnen Schubaëls Jechdeja.", heading: "Die Levitenordnung: 24,20-31", footnotes: ["(20-31) 23,17-23"] },
      { number: 21, text: "Von Rehabja, und zwar von den Söhnen Rehabjas, war Jischija der erste." },
      { number: 22, text: "Zu den Nachkommen Jizhars gehörte Schelomit, zu den Söhnen Schelomits Jahat." },
      { number: 23, text: "Von den Söhnen Hebrons war Jerija der erste, Amarja der zweite, Jahasiël der dritte, Jekamam der vierte." },
      { number: 24, text: "Der Sohn Usiëls war Micha. Zu den Söhnen Michas gehörte Schamir." },
      { number: 25, text: "Der Bruder Michas war Jischija. Zu den Söhnen Jischijas gehörte Secharja." },
      { number: 26, text: "Die Söhne Meraris waren Machli und Muschi. Der Sohn Jaasijas war Bani." },
      { number: 27, text: "Meraris Nachkommen über Jaasija waren: Bani, Schoham, Sakkur und Ibri." },
      { number: 28, text: "Zu Machli gehörten Eleasar, der keine Kinder hatte, und Kisch." },
      { number: 29, text: "Der Sohn des Kisch war Jerachmeel." },
      { number: 30, text: "Die Söhne Muschis waren: Machli, Eder und Jerimot. - Das waren die Leviten nach ihren Großfamilien." },
      { number: 31, text: "Auch sie warfen das Los wie ihre Brüder, die Söhne Aarons, im Beisein des Königs David, Zadoks und Ahimelechs sowie der Familienhäupter der Priester und Leviten, und zwar die Familien des Oberhauptes wie die seines jüngsten Bruders." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "David und die Obersten des Heeres sonderten die Söhne Asafs, Hemans und Jedutuns, die auf Zithern, Harfen und Zimbeln spielten, für ihren Dienst aus. Und das ist das Verzeichnis der Männer, die den Dienst versahen:", heading: "Die Dienstklassen der Sänger: 25,1-31" },
      { number: 2, text: "Von den Söhnen Asafs: Sakkur, Josef, Netanja und Asarela. Die Söhne Asafs standen unter der Leitung Asafs, der nach der Anweisung des Königs spielte." },
      { number: 3, text: "Von Jedutun: die Söhne Jedutuns: Gedalja, Zeri, Schimi, Jeschaja, Haschabja und Mattitja, insgesamt sechs. Sie standen unter der Leitung ihres Vaters Jedutun, der zum Lob und Preis des Herrn die Zither spielte." },
      { number: 4, text: "Von Heman: die Söhne Hemans: Bukkija, Mattanja, Usiël, Schubaël, Jerimot, Hananja, Hanani, Eliata, Giddalti, Romamti-Eser, Joschbekascha, Malloti, Hotir, Mahasiot." },
      { number: 5, text: "Sie alle waren Söhne Hemans, des Sehers des Königs, gemäß der Verheißung Gottes, der ihm Erhöhung der Macht versprach. So schenkte Gott dem Heman vierzehn Söhne und drei Töchter." },
      { number: 6, text: "Alle diese spielten unter der Leitung ihrer Väter, nämlich Asafs, Jedutuns und Hemans, beim Gesang im Haus des Herrn auf Zimbeln, Harfen und Zithern. Sie hatten den Dienst im Haus Gottes nach der Weisung des Königs zu besorgen." },
      { number: 7, text: "Sie zählten zusammen mit ihren Amtsbrüdern, die im Gesang zu Ehren des Herrn unterrichtet waren, 288 Mann, lauter geübte Leute." },
      { number: 8, text: "Sie warfen das Los zur Feststellung des Dienstes, und zwar der Geringste wie der Größte, der Meister wie der Schüler." },
      { number: 9, text: "Das erste Los traf auf Josef, das zweite auf Gedalja, seine Brüder und Söhne: 12;", footnotes: ["In H steht vor Josef noch der Name Asaf."] },
      { number: 10, text: "das dritte auf Sakkur, seine Söhne und Brüder: 12;" },
      { number: 11, text: "das vierte auf Zeri, seine Söhne und Brüder: 12;" },
      { number: 12, text: "das fünfte auf Netanja, seine Söhne und Brüder: 12;" },
      { number: 13, text: "das sechste auf Bukkija, seine Söhne und Brüder: 12;" },
      { number: 14, text: "das siebte auf Asarela, seine Söhne und Brüder: 12;" },
      { number: 15, text: "das achte auf Jeschaja, seine Söhne und Brüder: 12;" },
      { number: 16, text: "das neunte auf Mattanja, seine Söhne und Brüder: 12;" },
      { number: 17, text: "das zehnte auf Schimi, seine Söhne und Brüder: 12;" },
      { number: 18, text: "das elfte auf Usiël, seine Söhne und Brüder: 12;" },
      { number: 19, text: "das zwölfte auf Haschabja, seine Söhne und Brüder: 12;" },
      { number: 20, text: "das dreizehnte auf Schubael, seine Söhne und Brüder: 12;" },
      { number: 21, text: "das vierzehnte auf Mattitja, seine Söhne und Brüder: 12;" },
      { number: 22, text: "das fünfzehnte auf Jerimot, seine Söhne und Brüder: 12;" },
      { number: 23, text: "das sechzehnte auf Hananja, seine Söhne und Brüder: 12;" },
      { number: 24, text: "das siebzehnte auf Joschbekascha, seine Söhne und Brüder: 12;" },
      { number: 25, text: "das achtzehnte auf Hanani, seine Söhne und Brüder: 12;" },
      { number: 26, text: "das neunzehnte auf Malloti, seine Söhne und Brüder: 12;" },
      { number: 27, text: "das zwanzigste auf Eliata, seine Söhne und Brüder: 12;" },
      { number: 28, text: "das einundzwanzigste auf Hotir, seine Söhne und Brüder: 12;" },
      { number: 29, text: "das zweiundzwanzigste auf Giddalti, seine Söhne und Brüder: 12;" },
      { number: 30, text: "das dreiundzwanzigste auf Mahasiot, seine Söhne und Brüder: 12;" },
      { number: 31, text: "das vierundzwanzigste auf Romamti-Eser, seine Söhne und Brüder: 12." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Bei den Abteilungen der Torwächter gehörte Meschelemja, der Sohn Kores, ein Nachkomme Abiasafs, zu den Korachitern.", heading: "Die Torwächter: 26,1-19" },
      { number: 2, text: "Von den Söhnen Meschelemjas war Secharja der Erstgeborene, Jediaël der zweite, Sebadja der dritte, Jatniël der vierte," },
      { number: 3, text: "Elam der fünfte, Johanan der sechste, Eljoënai der siebte." },
      { number: 4, text: "Von den Söhnen Obed-Edoms war Schemaja der Erstgeborene, Josabad der zweite, Joach der dritte, Sachar der vierte, Netanel der fünfte," },
      { number: 5, text: "Ammiël der sechste, Issachar der siebte, Pëulletai der achte. Gott hatte ihn nämlich gesegnet." },
      { number: 6, text: "Auch seinem Sohn Schemaja wurden Söhne geboren. Sie wurden Oberhäupter in ihren Großfamilien, denn sie waren tüchtige Männer." },
      { number: 7, text: "Die Söhne Schemajas waren Otni, Refaël und Obed. Die Söhne seines Bruders Elsabad waren Elihu und Semachja, ebenfalls tüchtige Männer.", footnotes: ["Elsabad: Josabad in V. 4 ist derselbe Name, nur mit anderer Gottesbezeichnung gebildet."] },
      { number: 8, text: "Diese alle waren Nachkommen Obed-Edoms. Sie selbst, ihre Söhne und Brüder, waren tüchtige Leute und zu ihrem Dienst befähigt: zweiundsechzig Angehörige Obed-Edoms." },
      { number: 9, text: "Auch Meschelemja hatte Söhne und Brüder, achtzehn tüchtige Männer." },
      { number: 10, text: "Ebenso wurden Hosa, einem Nachkommen Meraris, Söhne geboren. Schimri war ihr Oberhaupt. Sein Vater hatte ihn dazu gemacht, obwohl er nicht der Erstgeborene war." },
      { number: 11, text: "Sein zweiter Sohn war Hilkija, der dritte Tebalja, der vierte Secharja. Insgesamt hatte Hosa dreizehn Söhne und Brüder." },
      { number: 12, text: "Diese Abteilungen der Torwächter besorgten die Wache mit der Gesamtheit ihrer Männer. Wie ihre Brüder taten sie Dienst im Haus des Herrn." },
      { number: 13, text: "Sie warfen für jedes einzelne Tor das Los in ihren Großfamilien, der Geringste wie der Größte." },
      { number: 14, text: "Das Los für das Osttor fiel auf Meschelemja; für seinen Sohn Secharja, einen klugen Berater, warf man das Los und es traf auf das Nordtor." },
      { number: 15, text: "Obed-Edom fiel das Südtor zu und seinen Söhnen das Vorratshaus." },
      { number: 16, text: "Auf Hosa traf das Westtor mit dem Schallechettor an der aufsteigenden Straße. Eine Wache entsprach der andern.", footnotes: ["In H steht vor Hosa der Name Schuppim infolge Verschreibung aus dem vorausgehenden Wort."] },
      { number: 17, text: "Im Osten standen täglich sechs Posten, im Norden täglich vier, im Süden täglich vier, an den Vorratskammern je zwei.", footnotes: ["täglich: sinngemäß ergänzt."] },
      { number: 18, text: "Am Parbar im Westen waren vier für die Straße und zwei für den Parbar bestimmt.", footnotes: ["Zu Parbar vgl. die Anmerkung zu 2 Kön 23,11; dort: Parwar."] },
      { number: 19, text: "Das waren die Abteilungen der Torwächter aus den Nachkommen der Korachiter und den Nachkommen Meraris." },
      { number: 20, text: "Einige von den Leviten, ihren Stammesbrüdern, wachten über die Schätze des Hauses Gottes und über die Schätze aus den Weihegaben.", heading: "Die Aufseher über die Schatzkammern: 26,20-28", footnotes: ["ihren Stammesbrüdern: Text korr. nach G."] },
      { number: 21, text: "Es waren dies die Söhne Ladans, eines Nachkommen Gerschons, nämlich Jehiël", footnotes: ["23,8", "21f: Text aus inhaltlichen Gründen umgestellt."] },
      { number: 22, text: "mit seinen Söhnen sowie Setam und sein Bruder Joël." },
      { number: 21, text: "Sie waren die Familienhäupter Ladans, des Sohnes Gerschons," },
      { number: 22, text: "und wachten über die Schätze im Haus des Herrn." },
      { number: 23, text: "Zu ihnen kamen Nachkommen Amrams, Jizhars, Hebrons und Usiëls.", footnotes: ["23,12f"] },
      { number: 24, text: "Schubaël, der Sohn Gerschoms und Enkel des Mose, war Oberaufseher über die Schätze gewesen.", footnotes: ["23,15-17"] },
      { number: 25, text: "Sein Stammesbruder aus der Linie Eliësers war dessen Sohn Rehabja; dessen Sohn war Jeschaja, dessen Sohn Joram, dessen Sohn Sichri und dessen Sohn Schelomit." },
      { number: 26, text: "Schelomit und seine Brüder hatten die Aufsicht über alle Schätze aus den Weihegaben, die König David, die Häupter der Großfamilien, die Obersten der Tausend- und Hundertschaften und die Obersten des Heeres gespendet hatten." },
      { number: 27, text: "Diese hatten aus der Kriegsbeute Weihegaben gestiftet, um das Haus des Herrn gut auszustatten." },
      { number: 28, text: "Sie überwachten auch alles, was der Seher Samuel, Saul, der Sohn des Kisch, Abner, der Sohn Ners, und Joab, der Sohn der Zeruja, gespendet hatten. Jeder, der eine Weihegabe darbrachte, legte sie in die Hand Schelomits und seiner Brüder." },
      { number: 29, text: "Von den Nachkommen Jizhars waren Kenanja und seine Söhne als Beamte und Richter für die äußeren Angelegenheiten in Israel bestellt.", heading: "Die Verwaltungsdienste: 26,29-32", footnotes: ["Nach Dtn 17,8-13; 19,16f und 21,5 waren die Leviten an der Rechtsprechung beteiligt."] },
      { number: 30, text: "Von den Nachkommen Hebrons hatten Haschabja und seine Brüder, siebzehnhundert tüchtige Männer, die Aufsicht über Israel westlich des Jordan in allen Angelegenheiten des Herrn und im Dienst des Königs." },
      { number: 31, text: "Haupt der Hebroniter war Jerija. Im vierzigsten Jahr der Regierung Davids suchte man anhand der Geschlechterlisten die Großfamilien der Hebroniter ab und fand unter ihnen tüchtige Männer in Jaser in Gilead." },
      { number: 32, text: "König David setzte Jerija und seine Stammesbrüder, zweitausendsiebenhundert tüchtige Männer, Häupter ihrer Familien, über die Rubeniter, Gaditer und den halben Stamm Manasse in allen Angelegenheiten Gottes und des Königs." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Die Israeliten nach ihrer Anzahl, die Familienhäupter, die Obersten der Tausend- und Hundertschaften und ihre Beamten dienten dem König in allen Angelegenheiten der Abteilungen, die Monat für Monat das ganze Jahr hindurch antraten und abzogen. Jede Abteilung war 24000 Mann stark.", heading: "Die Heerführer Davids: 27,1-15" },
      { number: 2, text: "Die erste Abteilung für den ersten Monat befehligte Jaschobam, der Sohn Sabdiëls. Zu seiner Abteilung gehörten 24000 Mann.", footnotes: ["2-15: Die Namen der zwölf Befehlshaber sind in der Liste der Helden Davids enthalten (vgl. 11,11-40)."] },
      { number: 3, text: "Er war ein Nachkomme des Perez und Haupt aller Obersten des Heeres im ersten Monat." },
      { number: 4, text: "Die Abteilung des zweiten Monats befehligte der Ahoachiter Dodai, ein Fürst. Zu seiner Abteilung gehörten 24000 Mann.", footnotes: ["11,12; 2 Sam 23,9", "In H folgen auf den Namen Varianten des Wortes für «Abteilung»."] },
      { number: 5, text: "Der dritte Heerführer, für den dritten Monat, war Benaja, der Sohn des Priesters Jojada, ein Oberhaupt. Zu seiner Abteilung gehörten 24000 Mann.", footnotes: ["11,22-25"] },
      { number: 6, text: "Dieser Benaja war ein Held unter den Dreißig und Anführer der Dreißig sowie seiner eigenen Abteilung. Sein Sohn war Ammisabad." },
      { number: 7, text: "Der vierte Befehlshaber, für den vierten Monat, war Asaël, der Bruder Joabs. Nach ihm war es sein Sohn Sebadja. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 8, text: "Der fünfte, für den fünften Monat, war der Oberst Schamhut, ein Nachkomme Serachs. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 9, text: "Der sechste, für den sechsten Monat, war Ira, der Sohn des Ikkesch, aus Tekoa. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 10, text: "Der siebte, für den siebten Monat, war Helez aus Pelet, ein Efraimiter. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 11, text: "Der achte, für den achten Monat, war der Huschatiter Sibbechai, ein Nachkomme Serachs. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 12, text: "Der neunte, für den neunten Monat, war Abiëser aus Anatot vom Stamm Benjamin. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 13, text: "Der zehnte, für den zehnten Monat, war Mahrai aus Netofa, ein Nachkomme Serachs. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 14, text: "Der elfte, für den elften Monat, war Benaja aus Piraton, ein Efraimiter. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 15, text: "Der zwölfte, für den zwölften Monat, war Heldai aus Netofa vom Geschlecht Otniël. Zu seiner Abteilung gehörten 24000 Mann." },
      { number: 16, text: "Diese standen an der Spitze der Stämme Israels: Fürst bei den Rubenitern war Eliëser, der Sohn Sichris; bei Simeon war es Schefatja, der Sohn Maachas;", heading: "Die Oberhäupter der Stämme: 27,16-24" },
      { number: 17, text: "bei Levi Haschabja, der Sohn Kemuëls; bei Aaron Zadok;" },
      { number: 18, text: "bei Juda Elihu, ein Bruder Davids; bei Issachar Omri, der Sohn Michaels;" },
      { number: 19, text: "bei Sebulon Jischmaja, der Sohn Obadjas; bei Naftali Jerimot, der Sohn Asriëls;" },
      { number: 20, text: "bei den Efraimitern Hoschea, der Sohn Asasjas; bei der einen Hälfte des Stammes Manasse Joël, der Sohn Pedajas;" },
      { number: 21, text: "bei der anderen Hälfte des Stammes Manasse in Gilead war es Jiddo, der Sohn Secharjas; bei Benjamin Jaasiël, der Sohn Abners;" },
      { number: 22, text: "bei Dan Asarel, der Sohn Jerohams. Das waren die Anführer der Stämme Israels." },
      { number: 23, text: "Die Zahl derer, die noch nicht zwanzig Jahre alt waren, hatte David nicht aufnehmen lassen; denn der Herr hatte versprochen, dass er Israel zahlreich werden lasse wie die Sterne des Himmels.", footnotes: ["21,1-6"] },
      { number: 24, text: "Joab, der Sohn der Zeruja, hatte die Zählung begonnen, aber nicht zu Ende geführt, da ihretwegen ein Zorngericht über Israel gekommen war. So wurde ihre Zahl nicht in die Chronik des Königs David aufgenommen.", footnotes: ["21,1-15; 2 Sam 24,1-15"] },
      { number: 25, text: "Asmawet, der Sohn Adiëls, hatte die Aufsicht über die Schätze des Königs, Jonatan, der Sohn Usijas, über die Vorräte auf dem Land, in den Städten, Dörfern und Türmen,", heading: "Die Verwalter des königlichen Besitzes: 27,25-31" },
      { number: 26, text: "Esri, der Sohn Kelubs, über die Feldarbeiter, die den Boden zu bestellen hatten," },
      { number: 27, text: "Schimi aus Rama über die Weinberge, Sabdi aus Schefam über die Weinvorräte in den Weinbergen," },
      { number: 28, text: "Baal-Hanan aus Bet-Gader über die Öl- und Maulbeerfeigenbäume in der Schefela, Joasch über die Ölvorräte," },
      { number: 29, text: "Schitrai, der Scharoniter, über die Rinder, die in der Scharonebene weideten, Schafat, der Sohn Adlais, über die Rinder in den Tälern," },
      { number: 30, text: "der Ismaeliter Obil über die Kamele, Jechdeja aus Meronot über die Esel," },
      { number: 31, text: "der Hagariter Jasis über die Schafe und Ziegen. Sie alle waren Verwalter des Eigentums, das dem König David gehörte." },
      { number: 32, text: "Jonatan, der Onkel Davids, war sein Berater. Er war ein kluger, schriftkundiger Mann. Jehiël, der Sohn Hachmonis, war bei den Söhnen des Königs.", heading: "Die höchsten Beamten: 27,32-34" },
      { number: 33, text: "Ahitofel war Berater des Königs, der Arkiter Huschai war Freund des Königs.", footnotes: ["2 Sam 16,23", "Freund des Königs: eine Amtsbezeichnung (vgl. 1 Kön 4,5)."] },
      { number: 34, text: "Nach Ahitofel war Jojada, der Sohn Benajas (Berater des Königs). Abjatar war Priester und Joab Heerführer des Königs.", footnotes: ["war Priester: sinngemäß ergänzt."] },
    ] },
    { number: 28, verses: [
      { number: 1, text: "David versammelte in Jerusalem alle führenden Männer Israels, die Vorsteher der Stämme und der Abteilungen, die im Dienst des Königs standen, die Obersten der Tausend- und der Hundertschaften, die Verwalter des ganzen Eigentums und des Viehbesitzes des Königs und seiner Söhne, dazu die Hofbeamten, die Helden und alle tapferen Krieger.", heading: "Der Auftrag zum Tempelbau: 28,1-21", footnotes: ["Unter den Helden sind die in 11,11-47 genannten Männer zu verstehen."] },
      { number: 2, text: "Dabei erhob sich König David und sagte: Hört mich an, meine Brüder und mein Volk! Ich selbst hatte vor, für die Bundeslade des Herrn, den Fußschemel unseres Gottes, eine Ruhestätte zu errichten, und traf Vorbereitungen für den Bau.", footnotes: ["2 Sam 7,2-17"] },
      { number: 3, text: "Doch Gott sprach zu mir: Du sollst meinem Namen kein Haus bauen; denn du hast Kriege geführt und Blut vergossen.", footnotes: ["22,8"] },
      { number: 4, text: "Der Herr, der Gott Israels, hat mich aus dem ganzen Haus meines Vaters für immer zum König von Israel auserwählt. Er hat Juda zur Herrschaft bestimmt und im Haus Juda das Haus meines Vaters. Unter den Söhnen meines Vaters hat er an mir Gefallen gefunden, sodass er mich zum König über Israel machte." },
      { number: 5, text: "Unter allen meinen Söhnen aber - der Herr hat mir ja viele Söhne geschenkt - hat er meinen Sohn Salomo erwählt, dass er auf dem Königsthron des Herrn über Israel herrscht.", footnotes: ["Da Gott der eigentliche König Israels und der König sein Stellvertreter ist, ist der Thron des irdischen Königs in Wirklichkeit der Thron Gottes."] },
      { number: 6, text: "Er sprach zu mir: Dein Sohn Salomo soll mein Haus und meine Höfe bauen; denn ihn habe ich mir zum Sohn erwählt und ich werde ihm Vater sein." },
      { number: 7, text: "Ich will seinem Königtum ewigen Bestand verleihen, wenn er mannhaft meine Gebote und Anordnungen befolgt, wie es heute der Fall ist." },
      { number: 8, text: "Und nun ermahne ich euch vor den Augen ganz Israels, der Versammlung des Herrn, und vor den Ohren unseres Gottes: Achtet auf alle Gebote des Herrn, eures Gottes, und befolgt sie, damit ihr im Besitz des prächtigen Landes bleibt und es für immer auf eure Söhne nach euch vererben könnt." },
      { number: 9, text: "Du aber, mein Sohn Salomo, erkenne den Gott deines Vaters; diene ihm mit ungeteiltem Herzen und williger Seele; denn der Herr erforscht alle Herzen und kennt jedes Sinnen der Gedanken. Wenn du ihn suchst, lässt er sich von dir finden. Wenn du ihn aber verlässt, verwirft er dich auf ewig." },
      { number: 10, text: "Sieh nun zu: Der Herr hat dich erwählt, dass du ihm ein Haus als Heiligtum erbaust. Sei mutig und geh ans Werk!" },
      { number: 11, text: "Darauf übergab David seinem Sohn Salomo den Plan der Vorhalle, des Hauptraums mit seinen Schatzkammern, Obergemächern und Innenräumen und des Raums der Deckplatte", footnotes: ["Raum der Deckplatte: Der Chronist nennt so das Allerheiligste, weil für ihn die Deckplatte über der Bundeslade der wichtigste Kultgegenstand ist. An ihr vollzieht nach Lev 16,13f der Hohepriester den Sühneritus für die Gemeinde am Versöhnungstag."] },
      { number: 12, text: "sowie den Plan von allem, was er sich für die Höfe des Tempels und für alle Kammern ringsum vorgenommen hatte. Er gab Anweisungen über die Schätze des Gotteshauses und die Schätze aus den Weihegaben," },
      { number: 13, text: "über die Abteilungen der Priester und Leviten, über den Vollzug des gesamten Dienstes am Haus des Herrn und über alle Geräte, die man zum Dienst im Haus des Herrn benötigte." },
      { number: 14, text: "Dazu übergab er ihm seine Berechnungen über das Gold, mit Angabe des Gewichtes des Goldes für jedes einzelne Dienstgerät, und über alle silbernen Geräte, mit Angabe des Gewichtes eines jeden Dienstgerätes." },
      { number: 15, text: "Er nannte ihm das Gewicht der goldenen Leuchter mit ihren goldenen Lampen, mit Angabe des Gewichtes eines jeden Leuchters und seiner Lampen, sowie der silbernen Leuchter, mit Angabe des Gewichtes eines jeden Leuchters und seiner Lampen, entsprechend der Verwendung eines jeden Leuchters.", footnotes: ["15f: Der Text beschreibt Einrichtungen zur Zeit des Chronisten. 1 Kön 7,48 erwähnt nur einen Schaubrotetisch."] },
      { number: 16, text: "Er bestimmte das Gold mit Angabe des Gewichtes für die einzelnen Schaubrotetische und des Silbers für die silbernen Tische." },
      { number: 17, text: "Ebenso gab er ihm Auskunft über die Gabeln, Schalen und Kannen aus purem Gold, über die goldenen Becher, mit Angabe des Gewichtes eines jeden Bechers, auch über die silbernen Becher, mit Angabe des Gewichtes eines jeden Bechers." },
      { number: 18, text: "Er verordnete geläutertes Gold für den Räucheraltar, mit Angabe des Gewichtes, und Gold für den Aufbau des Thronwagens, die Kerubim, die ihre Flügel ausbreiteten und die Bundeslade des Herrn bedeckten.", footnotes: ["Thronwagen: Bezeichnung der Bundeslade, im Anschluss an Ez 1, wo Gott, wie sonst auf der Bundeslade, auf Kerubim thront und die Kerubim auf einem Wagen mit vier Rädern stehen."] },
      { number: 19, text: "All das legte er dar in einer Schrift aus der Hand des Herrn, die über ihm ruhte, und erörterte alle Arbeiten, die der Plan vorsah.", footnotes: ["über ihm: Text korr. nach G."] },
      { number: 20, text: "Dann sagte David zu seinem Sohn Salomo: Sei mutig und stark! Geh ans Werk! Fürchte dich nicht und verzage nicht! Denn der Herr, mein Gott, wird mit dir sein. Er wird dich nicht erlahmen lassen und dich nicht im Stich lassen, bis alle Arbeiten für den Dienst des Hauses des Herrn zu Ende geführt sind." },
      { number: 21, text: "Sieh, die Abteilungen der Priester und Leviten stehen da, zu jeder Dienstleistung im Haus Gottes bereit. Bei jeder Arbeit und zu jedem Dienst werden dir alle willig nach ihren Fähigkeiten beistehen; ebenso werden dir die führenden Männer und das ganze Volk in jeder Angelegenheit zur Seite stehen." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Darauf wandte sich König David an die ganze Versammlung: Mein Sohn Salomo, den allein Gott erwählt hat, ist noch jung und unerfahren. Das Werk aber ist groß; denn wir bauen nicht für Menschen, sondern für Gott, den Herrn.", heading: "Die Spenden für den Tempelbau: 29,1-9", footnotes: ["22,5"] },
      { number: 2, text: "Nach meinen besten Kräften habe ich nun für das Haus meines Gottes Gold beschafft für die goldenen, Silber für die silbernen, Bronze für die bronzenen, Eisen für die eisernen und Holz für die hölzernen Gegenstände, dazu Schohamsteine mit Einfassungen, Malachit und buntfarbige Steine sowie allerlei Edelsteine und Alabaster in Menge." },
      { number: 3, text: "Aus Liebe zum Haus meines Gottes spende ich aus meinem Besitz, meinem Gold und Silber, über all das hinaus, was ich für das Haus des Heiligtums schon bereitgestellt habe,", footnotes: ["22,14"] },
      { number: 4, text: "noch weitere 3000 Talente Gold vom Ofirgold und 7000 Talente geläutertes Silber. Man soll damit die Wände der Räume verkleiden", footnotes: ["Handelsfahrten nach Ofir sind erst unter Salomo bezeugt (vgl. 1 Kön 9,28)."] },
      { number: 5, text: "und Gold zur Verfügung haben für die goldenen und Silber für die silbernen Gegenstände und für jede Arbeit von Künstlerhand. Wer ist nun bereit, seine Hand ebenso für den Herrn zu füllen?", footnotes: ["Seine Hand für Jahwe füllen: hier nicht Weiheritus, wie Ex 28,41; Lev 8,27; 1 Kön 13,33 in H, sondern: etwas in die Hand nehmen, um es dem Herrn darzubringen."] },
      { number: 6, text: "Da zeigten die Häupter der Großfamilien, die Vorsteher der israelitischen Stämme, die Obersten der Tausend- und Hundertschaften und die obersten Beamten des königlichen Dienstes ihre Freigebigkeit." },
      { number: 7, text: "Sie spendeten für den Bau des Hauses Gottes 5000 Talente Gold, 10000 Golddariken, 10000 Talente Silber, 18000 Talente Bronze, 100000 Talente Eisen.", footnotes: ["Eine Darike ist eine Münze; die Bezeichnung soll auf den Perserkönig Darius I. (522-486 v. Chr.) zurückgehen."] },
      { number: 8, text: "Wer Edelsteine besaß, gab sie zum Schatz des Hauses des Herrn in die Hände Jehiëls, eines Nachkommen Gerschons." },
      { number: 9, text: "Das Volk freute sich über diese Freigebigkeit, denn sie hatten mit ungeteiltem Herzen willig für den Herrn gespendet. Auch König David hatte eine große Freude." },
      { number: 10, text: "Da pries David den Herrn vor der ganzen Versammlung und rief: Gepriesen bist du, Herr, Gott unseres Vaters Israel, von Ewigkeit zu Ewigkeit.", heading: "Das Dankgebet Davids: 29,10-20" },
      { number: 11, text: "Dein, Herr, sind Größe und Kraft, Ruhm und Glanz und Hoheit; dein ist alles im Himmel und auf Erden. Herr, dein ist das Königtum. Du erhebst dich als Haupt über alles." },
      { number: 12, text: "Reichtum und Ehre kommen von dir; du bist der Herrscher über das All. In deiner Hand liegen Kraft und Stärke; von deiner Hand kommt alle Größe und Macht." },
      { number: 13, text: "Darum danken wir dir, unser Gott, und rühmen deinen herrlichen Namen." },
      { number: 14, text: "Doch wer bin ich und was ist mein Volk, dass wir die Kraft besaßen, diese Gaben zu spenden? Von dir kommt ja alles; und was wir dir gegeben haben, stammt aus deiner Hand." },
      { number: 15, text: "Denn wir sind nur Gäste bei dir, Fremdlinge, wie alle unsere Väter. Wie ein Schatten sind unsere Tage auf Erden und ohne Hoffnung.", footnotes: ["Ps 39,13"] },
      { number: 16, text: "Herr, unser Gott, diese ganze Fülle, die wir bereitgestellt haben, um dir, deinem heiligen Namen, ein Haus zu bauen, kam aus deiner Hand; dir gehört alles." },
      { number: 17, text: "Ich weiß, mein Gott, dass du die Herzen prüfst und an Aufrichtigkeit Gefallen hast. Mit aufrichtigem Herzen habe ich dies alles gegeben und ich habe mit Freuden gesehen, wie auch dein Volk, das sich hier eingefunden hat, dir willig spendet.", footnotes: ["Ps 7,10"] },
      { number: 18, text: "Herr, Gott unserer Väter Abraham, Isaak und Israel, erhalte diese Gesinnung für immer im Herzen deines Volkes! Lenke sein Herz auf dich!" },
      { number: 19, text: "Gib auch meinem Sohn Salomo ein ungeteiltes Herz, damit er auf deine Gebote, Anordnungen und Gesetze achtet und alles tut, um den Bau zu errichten, den ich vorbereitet habe." },
      { number: 20, text: "Dann befahl David der ganzen Versammlung: Preist den Herrn, euren Gott! Und die ganze Versammlung lobte den Herrn, den Gott ihrer Väter. Sie verneigten sich und warfen sich nieder vor dem Herrn und dem König." },
      { number: 21, text: "Am folgenden Tag brachten sie dem Herrn Opfer dar. Sie spendeten zu Brandopfern tausend Stiere, tausend Widder, tausend Lämmer sowie die dazugehörenden Trankopfer. Dazu kam eine Menge Schlachtopfer für ganz Israel.", heading: "Salomos Thronbesteigung: 29,21-25" },
      { number: 22, text: "Voller Freude aßen und tranken sie an jenem Tag vor dem Herrn und machten Salomo, den Sohn Davids, zum zweiten Mal zum König. Sie salbten ihn zum Fürsten des Herrn und Zadok salbten sie zum Priester." },
      { number: 23, text: "Salomo setzte sich nun anstelle seines Vaters David als König auf den Thron des Herrn. Er hatte Glück und ganz Israel leistete ihm Gehorsam." },
      { number: 24, text: "Alle hohen Beamten und Helden, auch alle Söhne des Königs David, unterwarfen sich Salomo.", footnotes: ["Vgl. die Anmerkung zu 28,1."] },
      { number: 25, text: "Der Herr ließ Salomo überaus mächtig werden vor den Augen Israels. Er verlieh seinem Königtum einen Glanz, wie ihn kein König in Israel vor ihm besessen hatte." },
      { number: 26, text: "David, der Sohn Isais, war König von ganz Israel.", heading: "Der Abschluss der Geschichte Davids: 29,26-30" },
      { number: 27, text: "Die Zeit, die er in Israel regierte, betrug vierzig Jahre. In Hebron regierte er sieben und in Jerusalem dreiunddreißig Jahre." },
      { number: 28, text: "Er starb in hohem Alter, satt an Tagen, Reichtum und Ehre. Sein Sohn Salomo wurde König an seiner Stelle." },
      { number: 29, text: "Die frühere und die spätere Geschichte des Königs David ist aufgezeichnet in der Geschichte des Sehers Samuel, in der Geschichte des Propheten Natan und in der Geschichte des Sehers Gad.", footnotes: ["Die erwähnten Quellenwerke könnten die biblischen Samuelbücher sein, die über die drei Genannten berichten."] },
      { number: 30, text: "Sie berichten von seiner ganzen Regierung, seiner Machtentfaltung und von den Zeiten, die über ihn, über Israel und alle Reiche der Länder hinweggegangen sind." },
    ] },
  ],
};
