import { Book } from "@/lib/types";

export const _1kings: Book = {
  id: "1kings",
  name: "1. Könige",
  shortName: "1.Kön",
  testament: "old",
  introduction: "Die Bücher der Könige Die beiden ersten Kapitel von 1 Könige bilden den Abschluss der Geschichte Davids und gehören inhaltlich noch zu den Samuelbüchern. Deshalb wurden die Samuel- und Königsbücher vielfach als Einheit verstanden, für die man die gemeinsame Bezeichnung »Bücher der Könige« (in der griechischen Bibel »Bücher der Königsherrschaften«) gebrauchte. Doch sprechen die Verschiedenheit des Stils, der Sprache, der Darstellungsweise und des Urteils über die Könige Israels für die Selbstständigkeit der Königsbücher. Die beiden Bücher enthalten die Geschichte der Könige bis zum Babylonischen Exil. Sie sind in folgende Abschnitte gegliedert: 1. die Geschichte Salomos (1 Kön 1 - 11); 2. die Geschichte der getrennten Reiche (1 Kön 12 - 2 Kön 17); 3. die weitere Geschichte des Südreiches Juda (2 Kön 18 - 25). Umfangreiche Abschnitte über das Wirken der Propheten Elija, Elischa und anderer Gottesmänner sind vor allem in 1 Kön 17 - 19 und 2 Kön 1 - 10 eingeschoben. Die Geschichte der einzelnen Könige ist in einen bestimmten Rahmen eingebaut mit Alters- und Datumsangaben, Urteilen über das religiöse Verhalten, Bemerkungen über Tod, Begräbnis und Nachfolger, mit Hinweisen auf Quellenwerke. Die besonderen Aussagen über die einzelnen Könige sind nach Inhalt und Umfang sehr verschieden und werden der Bedeutung der Herrscher nicht immer gerecht. Als besonders wichtig werden ihre Leistungen für Tempel und Gottesdienst und ihr Verhalten zum Bund Gottes mit Israel gewertet. Als Richtlinien gelten dabei die im Deuteronomium festgelegten Normen der Gottesverehrung. Die Könige des Nordreichs werden daher insgesamt wegen des Kultes in Bet-El getadelt, der im Widerspruch zum Gottesdienst in Jerusalem steht. Erfolge und Unglück werden als Lohn und Strafe für Gehorsam oder Abfall gedeutet. Die Zuverlässigkeit eines Geschichtswerkes beruht auf den Quellen, die bei seiner Abfassung zur Verfügung standen. Die Königsbücher selbst erwähnen eine »Chronik Salomos«, eine »Chronik der Könige von Juda« und eine »Chronik der Könige von Israel« als Werke, in denen der Leser weitere Mitteilungen finden konnte. Viele Anzeichen sprechen dafür, dass noch andere Quellen, z. B. Urkunden, Listen, Prophetengeschichten, Volkserzählungen vorlagen, aus denen einzelne Abschnitte ganz oder in Auszügen übernommen wurden. Oft wurden solche Texte der Absicht des Verfassers entsprechend überarbeitet oder ergänzt; sein persönlicher Beitrag ist vor allem in eingestreuten Ermahnungen oder Betrachtungen zu erkennen (z. B. 2 Kön 17 und 21). Das letzte Ereignis, das in den Königsbüchern erwähnt wird, ist die Begnadigung des Königs Jojachin um 560 v. Chr. Es wurde offensichtlich erwähnt, um mit einem Lichtblick auf die Zukunft schließen zu können. Das Ende des Babylonischen Exils war demnach noch nicht eingetreten, sodass die Zeit zwischen 560 und 538 als Entstehungszeit der Bücher anzunehmen ist. Ihr Verfasser ist unbekannt. Heute werden die beiden Bücher gewöhnlich dem Deuteronomistischen Geschichtswerk zugerechnet (vgl. die Einleitung zum Buch Deuteronomium).",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "König David war alt und hochbetagt; auch wenn man ihn in Decken hüllte, wurde ihm nicht mehr warm.", heading: "Die Geschichte Salomos: 1,1 - 11,43 | Der Kampf um die Nachfolge Davids: 1,1-27" },
      { number: 2, text: "Da sagten seine Diener zu ihm: Man suche für unseren Herrn, den König, ein unberührtes Mädchen, das ihn bedient und pflegt. Wenn es an seiner Seite schläft, wird es unserem Herrn, dem König, warm werden." },
      { number: 3, text: "Man suchte nun im ganzen Land Israel nach einem schönen Mädchen, fand Abischag aus Schunem und brachte sie zum König." },
      { number: 4, text: "Das Mädchen war überaus schön. Sie pflegte den König und diente ihm; doch der König erkannte sie nicht.", footnotes: ["der König erkannte sie nicht: Er hatte mit ihr keine eheliche Gemeinschaft."] },
      { number: 5, text: "Adonija, der Sohn der Haggit, trat anmaßend auf und sagte: Ich werde König sein. Er beschaffte sich Wagen und Besatzung dazu sowie fünfzig Mann, die vor ihm herliefen.", footnotes: ["Ebenso hatte sich Abschalom verhalten, als er nach dem Königtum trachtete (vgl. 2 Sam 15,1). Adonija war jetzt der älteste überlebende Sohn Davids und konnte mit der Thronfolge rechnen."] },
      { number: 6, text: "Sein Vater David hatte ihn nie in seinem Leben getadelt und nie zu ihm gesagt: Warum tust du das? Auch war Adonija ein sehr stattlicher Mann; seine Mutter hatte ihn nach Abschalom geboren." },
      { number: 7, text: "Er verhandelte mit Joab, dem Sohn der Zeruja, und mit dem Priester Abjatar. Beide stellten sich hinter Adonija." },
      { number: 8, text: "Der Priester Zadok aber und Benaja, der Sohn Jojadas, und der Prophet Natan, auch Schimi, Reï und die Helden Davids schlossen sich ihm nicht an." },
      { number: 9, text: "Adonija schlachtete nun am Stein Sohelet bei der Rogel-Quelle Schafe, Rinder und Mastkälber zum Opfermahl. Er lud dazu alle seine Brüder, die Königssöhne, sowie alle Männer von Juda ein, die im Dienst des Königs standen.", footnotes: ["Die Machtübernahme sollte mit einem Opfer verbunden werden. Da Opferfeiern von einzelnen Familien öfter begangen wurden (1 Sam 1,3; 16,2; 20,6; 2 Sam 15,7) und zu ihnen Verwandte und Freunde geladen wurden, konnte Adonija seine Anhänger sammeln, ohne Verdacht zu erregen. Zur Rogel-Quelle vgl. 2 Sam 17,17."] },
      { number: 10, text: "Den Propheten Natan jedoch und Benaja sowie die Helden und seinen Bruder Salomo lud er nicht ein." },
      { number: 11, text: "Da sagte Natan zu Batseba, der Mutter Salomos: Hast du nicht gehört, dass Adonija, der Sohn der Haggit, König geworden ist, ohne dass David, unser Herr, davon weiß?" },
      { number: 12, text: "Komm nun, ich will dir einen Rat geben, wie du dir und deinem Sohn Salomo das Leben retten kannst.", footnotes: ["Zur Sicherung ihrer Herrschaft ließen orientalische Könige oft nach Erlangung des Throns ihre einstigen Gegner beseitigen."] },
      { number: 13, text: "Geh zum König David und sag zu ihm: Mein Herr und König, du hast doch deiner Magd geschworen: Dein Sohn Salomo soll nach mir König sein und er soll auf meinem Thron sitzen. Warum ist nun Adonija König geworden?" },
      { number: 14, text: "Noch während du dort mit dem König redest, will auch ich kommen und deine Worte bestätigen." },
      { number: 15, text: "Batseba ging zum König in das Gemach. Er war sehr gealtert und Abischag aus Schunem bediente ihn." },
      { number: 16, text: "Batseba verneigte sich und warf sich vor dem König nieder, und der König fragte sie: Was willst du?" },
      { number: 17, text: "Sie sagte: Mein Herr, du selbst hast doch deiner Magd beim Herrn, deinem Gott, geschworen: Dein Sohn Salomo soll nach mir König sein und er soll auf meinem Thron sitzen." },
      { number: 18, text: "Nun aber ist Adonija König geworden und du, mein Herr und König, weißt nichts davon." },
      { number: 19, text: "Er hat eine Menge Rinder, Mastkälber und Schafe geschlachtet und alle Söhne des Königs, den Priester Abjatar und den Feldherrn Joab dazu eingeladen. Doch deinen Knecht Salomo hat er nicht eingeladen." },
      { number: 20, text: "Auf dich, mein Herr und König, sind nun die Augen ganz Israels gerichtet. Du sollst ihnen bekannt geben, wer nach meinem Herrn und König auf dem Thron sitzen wird." },
      { number: 21, text: "Sonst müssen ich und mein Sohn Salomo es büßen, wenn mein Herr und König zu seinen Vätern entschlafen ist." },
      { number: 22, text: "Während sie noch mit dem König redete, kam der Prophet Natan." },
      { number: 23, text: "Man meldete dem König: Der Prophet Natan ist da. Er trat vor den König, warf sich vor ihm nieder, mit dem Gesicht zur Erde," },
      { number: 24, text: "und sagte: Mein Herr und König, du hast wohl verfügt: Adonija soll nach mir König sein und er soll auf meinem Thron sitzen." },
      { number: 25, text: "Denn er ist heute hinabgezogen, hat eine Menge Rinder, Mastkälber und Schafe geschlachtet und hat dazu alle Söhne des Königs, die Obersten des Heeres und den Priester Abjatar eingeladen. Sie essen und trinken mit ihm und rufen: Es lebe der König Adonija!" },
      { number: 26, text: "Mich aber, deinen Knecht, sowie den Priester Zadok und Benaja, den Sohn Jojadas, und deinen Knecht Salomo hat er nicht eingeladen." },
      { number: 27, text: "Wenn nun diese Verfügung wirklich von meinem Herrn und König ergangen ist, warum hast du dann deinen Knecht nicht wissen lassen, wer nach meinem Herrn und König auf dem Thron sitzen wird?" },
      { number: 28, text: "Darauf befahl König David: Ruft mir Batseba! Sie kam zum König herein, trat vor den König hin", heading: "Die Bestellung Salomos zum Thronfolger: 1,28-37" },
      { number: 29, text: "und der König schwor ihr: So wahr der Herr lebt, der mein Leben aus jeder Gefahr gerettet hat:" },
      { number: 30, text: "Ich habe dir beim Herrn, dem Gott Israels, geschworen, dass dein Sohn Salomo nach mir König sein und an meiner Stelle auf meinem Thron sitzen soll, und so will ich es heute wahr machen." },
      { number: 31, text: "Da verneigte sich Batseba bis zur Erde, warf sich vor dem König nieder und rief: Ewig lebe mein Herr, der König David!" },
      { number: 32, text: "Hierauf befahl König David: Ruft mir den Priester Zadok, den Propheten Natan und Benaja, den Sohn Jojadas! Sie erschienen vor dem König", footnotes: ["Benaja war der Führer der Leibwache (vgl. 2 Sam 8,18)."] },
      { number: 33, text: "und dieser trug ihnen auf: Nehmt das Gefolge eures Herrn mit euch, setzt meinen Sohn Salomo auf mein eigenes Maultier und führt ihn zum Gihon hinab!", footnotes: ["Die Gihon-Quelle liegt näher am Königspalast als die Rogel-Quelle."] },
      { number: 34, text: "Dort sollen ihn der Priester Zadok und der Prophet Natan zum König von Israel salben und ihr sollt in das Horn stoßen und rufen: Es lebe König Salomo!" },
      { number: 35, text: "Dann zieht mit ihm herauf! Er soll kommen, sich auf meinen Thron setzen und König werden an meiner Stelle; denn ihn habe ich zum Fürsten von Israel und Juda bestimmt." },
      { number: 36, text: "Benaja, der Sohn Jojadas, antwortete dem König: So sei es, so füge es der Herr, der Gott meines Herrn und Königs." },
      { number: 37, text: "Wie der Herr mit meinem Herrn und König war, so möge er auch mit Salomo sein. Er mache seinen Thron noch erhabener als den Thron meines Herrn, des Königs David." },
      { number: 38, text: "Der Priester Zadok, der Prophet Natan und Benaja, der Sohn Jojadas, zogen mit den Keretern und Peletern hinab. Sie setzten Salomo auf das Maultier des Königs David und führten ihn zum Gihon.", heading: "Die Thronbesteigung Salomos: 1,38-53", footnotes: ["Die Kereter und Peleter waren ausländische Söldner, die David als Leibwache dienten."] },
      { number: 39, text: "Der Priester Zadok hatte das Salbhorn aus dem Zelt mitgenommen und salbte Salomo. Hierauf blies man das Widderhorn und alles Volk rief: Es lebe König Salomo!" },
      { number: 40, text: "Nun zog das ganze Volk mit ihm hinauf. Dabei spielten sie auf Flöten und waren voller Freude, sodass bei ihrem Geschrei die Erde zu bersten drohte." },
      { number: 41, text: "Das hörten Adonija und alle Geladenen, die bei ihm waren. Sie hatten eben das Mahl beendet. Als Joab den Schall des Hornes hörte, rief er: Was soll das laute Lärmen in der Stadt?" },
      { number: 42, text: "Während er noch redete, kam Jonatan, der Sohn des Priesters Abjatar. Adonija rief ihm zu: Komm, du bist ein tüchtiger Mann. Du bringst sicher eine gute Nachricht." },
      { number: 43, text: "Doch Jonatan erwiderte Adonija: Im Gegenteil. Unser Herr, der König David, hat Salomo als König eingesetzt." },
      { number: 44, text: "Er hat mit ihm den Priester Zadok ausgeschickt sowie den Propheten Natan und Benaja, den Sohn Jojadas, samt den Keretern und Peletern und diese haben ihn auf das Maultier des Königs gesetzt." },
      { number: 45, text: "Der Priester Zadok und der Prophet Natan haben ihn am Gihon zum König gesalbt. Von dort sind sie voller Freude hinaufgezogen und die Stadt ist in großer Aufregung. Das war der Lärm, den ihr gehört habt." },
      { number: 46, text: "Salomo hat sich bereits auf den königlichen Thron gesetzt." },
      { number: 47, text: "Auch sind die Diener des Königs gekommen, um unseren Herrn, den König David, zu beglückwünschen und zu rufen: Gott lasse Salomos Ruhm noch größer werden als deinen und er mache seinen Thron noch erhabener als deinen Thron. Dabei hat sich der König auf seinem Lager tief verneigt." },
      { number: 48, text: "Auch hat der König gesagt: Gepriesen sei der Herr, der Gott Israels, der mir heute gewährt hat, dass mein Nachkomme auf meinem Thron sitzt und dass meine Augen das noch sehen dürfen." },
      { number: 49, text: "Da erschraken alle Geladenen, die bei Adonija waren, und brachen auf; jeder ging seines Weges." },
      { number: 50, text: "Adonija fürchtete sich vor Salomo. Er stand auf, eilte zum Altar und ergriff dessen Hörner.", footnotes: ["Der Altar als Asylstätte gewährte Adonija Schutz."] },
      { number: 51, text: "Man meldete Salomo: Adonija hat aus Furcht vor König Salomo die Hörner des Altars ergriffen und gesagt: König Salomo schwöre mir zuerst, dass er seinen Knecht nicht mit dem Schwert hinrichten lässt." },
      { number: 52, text: "Salomo versprach: Wenn er sich wie ein rechtschaffener Mann verhält, soll ihm kein Haar gekrümmt werden. Stellt es sich aber heraus, dass er unrecht handelt, muss er sterben." },
      { number: 53, text: "Darauf ließ ihn König Salomo vom Altar wegholen. Als er kam und vor König Salomo niederfiel, befahl ihm dieser: Geh in dein Haus!" },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Als die Zeit herankam, da David sterben sollte, ermahnte er seinen Sohn Salomo:", heading: "Davids letzte Anordnungen und sein Tod: 2,1-12" },
      { number: 2, text: "Ich gehe nun den Weg alles Irdischen. Sei also stark und mannhaft!" },
      { number: 3, text: "Erfüll deine Pflicht gegen den Herrn, deinen Gott: Geh auf seinen Wegen und befolg alle Gebote, Befehle, Satzungen und Anordnungen, die im Gesetz des Mose niedergeschrieben sind. Dann wirst du Erfolg haben bei allem, was du tust, und in allem, was du unternimmst." },
      { number: 4, text: "Und der Herr wird sein Wort wahr machen, das er mir gegeben hat, als er sagte: Wenn deine Söhne auf ihren Weg achten und aufrichtig mit ganzem Herzen und ganzer Seele vor mir leben, wird es dir nie an Nachkommen auf dem Thron Israels fehlen." },
      { number: 5, text: "Du weißt selbst, was Joab, der Sohn der Zeruja, mir angetan hat: was er den beiden Heerführern Israels, Abner, dem Sohn Ners, und Amasa, dem Sohn Jeters, angetan hat. Er hat sie ermordet, hat mit Blut, das im Krieg vergossen wurde, den Frieden belastet und mit unschuldigem Blut den Gürtel an seinen Hüften und die Schuhe an seinen Füßen befleckt.", footnotes: ["2 Sam 3,27; 20,10", "Damals war David nicht in der Lage, Joab zu bestrafen. Nach israelitischer Vorstellung von Vergeltung entsprachen die Anweisungen Davids an seinen Sohn der auf David lastenden Verpflichtung, den Tod der beiden Männer zu rächen."] },
      { number: 6, text: "Lass dich von deiner Weisheit leiten und sorge dafür, dass sein graues Haupt nicht unbehelligt in die Unterwelt kommt." },
      { number: 7, text: "Doch die Söhne Barsillais aus Gilead sollst du freundlich behandeln. Sie sollen zu denen gehören, die von deinem Tisch essen; denn sie sind mir ebenso entgegengekommen, als ich vor deinem Bruder Abschalom fliehen musste.", footnotes: ["2 Sam 17,27-29"] },
      { number: 8, text: "Da ist auch Schimi, der Sohn Geras, vom Stamm Benjamin, aus Bahurim. Er hat einen bösen Fluch gegen mich ausgesprochen, als ich nach Mahanajim floh. Doch ist er mir an den Jordan entgegengekommen und ich habe ihm beim Herrn geschworen, dass ich ihn nicht mit dem Schwert hinrichten werde.", footnotes: ["2 Sam 16,5; 19,19-24", "8f: Die alte Zeit glaubte, dass ein einmal ausgesprochener Fluch mit unwiderstehlicher Kraft fortwirkt. David musste daher fürchten, dass seinem Haus Unglück drohte, solange Schimis Fluch nicht gerächt war. Da er selbst durch den Eid gebunden war, sollte Salomo die Drohung beseitigen."] },
      { number: 9, text: "Jetzt aber lass ihn nicht ungestraft! Du bist ein kluger Mann und weißt, was du mit ihm tun sollst. Schick sein graues Haupt blutig in die Unterwelt!" },
      { number: 10, text: "David entschlief zu seinen Vätern und wurde in der Davidstadt begraben." },
      { number: 11, text: "Die Zeit, in der David über Israel König war, betrug vierzig Jahre. In Hebron regierte er sieben und in Jerusalem dreiunddreißig Jahre." },
      { number: 12, text: "Salomo saß nun auf dem Thron seines Vaters David und seine Herrschaft festigte sich mehr und mehr." },
      { number: 13, text: "Adonija, der Sohn der Haggit, begab sich zu Batseba, der Mutter Salomos. Sie fragte ihn: Kommst du in friedlicher Absicht? Er antwortete: Ja.", heading: "Das Ende der Gegner Salomos: 2,13-46" },
      { number: 14, text: "Dann fuhr er fort: Ich möchte mit dir reden. Sie erwiderte: Rede nur!" },
      { number: 15, text: "Da sagte er: Du weißt, dass mir das Königtum zustand und dass ganz Israel mich als König haben wollte. Doch ist mir die Königswürde entgangen; sie ist meinem Bruder zugefallen, weil sie ihm vom Herrn bestimmt war." },
      { number: 16, text: "Jetzt aber möchte ich eine einzige Bitte an dich richten. Weise mich nicht ab! Sie antwortete: Sprich sie nur aus!" },
      { number: 17, text: "Da begann er: Rede doch mit König Salomo; dich wird er nicht abweisen. Bitte ihn, dass er mir Abischag aus Schunem zur Frau gibt." },
      { number: 18, text: "Batseba erwiderte: Gut, ich werde in deiner Angelegenheit mit dem König reden." },
      { number: 19, text: "Als nun Batseba zu König Salomo kam, um mit ihm wegen Adonija zu sprechen, erhob sich der König, ging ihr entgegen und verneigte sich vor ihr. Dann setzte er sich auf seinen Thron und ließ auch für die Königinmutter einen Thron hinstellen. Sie setzte sich an seine rechte Seite", footnotes: ["19f: Die Mutter des Königs hatte als «Herrin» (15,13) eine Ehrenstellung inne."] },
      { number: 20, text: "und begann: Eine einzige kleine Bitte hätte ich an dich. Weise mich nicht ab! Der König antwortete ihr: Sprich sie nur aus, Mutter! Ich werde dich nicht abweisen." },
      { number: 21, text: "Da bat sie: Man gebe doch Abischag aus Schunem deinem Bruder Adonija zur Frau." },
      { number: 22, text: "Der König Salomo entgegnete seiner Mutter: Warum bittest du für Adonija um Abischag aus Schunem? Fordere doch gleich das Königtum für ihn! Er ist ja mein älterer Bruder und auf seiner Seite stehen der Priester Abjatar und Joab, der Sohn der Zeruja." },
      { number: 23, text: "Und König Salomo schwor beim Herrn: Gott soll mir dies und das antun, wenn dieses Ansinnen Adonija nicht das Leben kostet." },
      { number: 24, text: "So wahr der Herr lebt, der mich eingesetzt und auf den Thron meines Vaters David erhoben hat und der mir, wie er versprochen hat, ein Haus gebaut hat: Noch heute muss Adonija sterben." },
      { number: 25, text: "Darauf schickte König Salomo Benaja, den Sohn Jojadas, hinauf und dieser versetzte Adonija den Todesstoß." },
      { number: 26, text: "Zum Priester Abjatar sagte der König: Geh auf dein Landgut nach Anatot! Zwar hast du den Tod verdient; doch will ich dich heute nicht töten, weil du die Lade Gottes, des Herrn, vor meinem Vater David getragen und alle Demütigungen mit meinem Vater geteilt hast.", footnotes: ["1 Sam 22,20-23"] },
      { number: 27, text: "So setzte Salomo Abjatar als Priester des Herrn ab und erfüllte das Wort, das der Herr über das Haus Eli in Schilo gesprochen hatte.", footnotes: ["1 Sam 2,30-36"] },
      { number: 28, text: "Die Kunde davon erreichte Joab. Er hatte zu Adonija gehalten, sich aber nicht an Abschalom angeschlossen. Er floh in das Zelt des Herrn und ergriff die Hörner des Altars." },
      { number: 29, text: "Man meldete dem König Salomo: Joab ist in das Zelt des Herrn geflohen und steht neben dem Altar. Salomo sandte Benaja, den Sohn Jojadas, mit dem Auftrag: Geh hin und stoß ihn nieder!" },
      { number: 30, text: "Benaja kam in das Zelt des Herrn und rief Joab zu: Der König befiehlt dir herauszukommen. Doch Joab antwortete: Nein, hier will ich sterben. Benaja berichtete dem König: Das hat Joab gesagt, so hat er mir geantwortet." },
      { number: 31, text: "Da gebot ihm der König: Tu, was er gesagt hat! Stoß ihn nieder und begrab ihn! Nimm so von mir und vom Haus meines Vaters das Blut, das Joab ohne Grund vergossen hat." },
      { number: 32, text: "Der Herr lasse sein Blut auf sein Haupt kommen, weil er ohne Wissen meines Vaters zwei Männer, die gerechter und besser waren als er, niedergestoßen und mit dem Schwert getötet hat: Abner, den Sohn Ners, den Heerführer Israels, und Amasa, den Sohn Jeters, den Heerführer Judas." },
      { number: 33, text: "Ihr Blut komme für immer auf das Haupt Joabs und seiner Nachkommen. David aber, seinen Nachkommen, seinem Haus und seinem Thron sei vom Herrn immerfort Heil beschieden." },
      { number: 34, text: "Nun ging Benaja, der Sohn Jojadas, hinauf, stieß Joab nieder und tötete ihn. Er wurde auf seinem Besitz in der Steppe begraben." },
      { number: 35, text: "Der König setzte dann Benaja, den Sohn Jojadas, an seiner Stelle über das Heer und dem Priester Zadok verlieh er die Stelle Abjatars." },
      { number: 36, text: "Hierauf ließ der König Schimi rufen und befahl ihm: Bau dir ein Haus in Jerusalem, bleib hier und geh nicht weg, weder dahin noch dorthin!" },
      { number: 37, text: "Du sollst wissen, dass du sterben musst, sobald du hinausgehst und das Kidrontal überschreitest. Du wirst dann selbst schuld sein an deinem Tod." },
      { number: 38, text: "Schimi antwortete dem König: Gut, dein Knecht wird tun, was mein Herr, der König, bestimmt hat. So blieb Schimi viele Tage in Jerusalem." },
      { number: 39, text: "Nach Verlauf von drei Jahren entflohen zwei Sklaven Schimis zu Achisch, dem Sohn Maachas, dem König von Gat. Man meldete Schimi: Deine Sklaven sind in Gat." },
      { number: 40, text: "Da machte er sich auf, sattelte seinen Esel und begab sich zu Achisch nach Gat, um seine Sklaven zu suchen. Er ging hin und holte sie aus Gat zurück." },
      { number: 41, text: "Es wurde aber Salomo hinterbracht, dass Schimi von Jerusalem nach Gat gegangen und wieder zurückgekehrt sei." },
      { number: 42, text: "Er ließ daher Schimi rufen und hielt ihm vor: Habe ich dich nicht beim Herrn schwören lassen und dich gewarnt: Sobald du weggehst und dich dahin oder dorthin begibst, so weißt du, dass du sterben musst? Und du hast geantwortet: Gut, ich habe es gehört." },
      { number: 43, text: "Warum hast du den Eid beim Herrn und den Befehl, den ich dir gegeben habe, nicht beachtet?" },
      { number: 44, text: "Und weiter sagte der König zu Schimi: Du weißt, wie viel Böses du meinem Vater David angetan hast; jetzt lässt es der Herr auf dich selbst zurückfallen." },
      { number: 45, text: "Der König Salomo aber sei gesegnet und der Thron Davids stehe fest vor dem Herrn in Ewigkeit." },
      { number: 46, text: "Hierauf erteilte der König dem Benaja, dem Sohn Jojadas, Befehl und dieser ging hinaus und versetzte Schimi den Todesstoß. Die Herrschaft war nun fest in der Hand Salomos." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Salomo verschwägerte sich mit dem Pharao, dem König von Ägypten. Er nahm eine Tochter des Pharao zur Frau und brachte sie in die Davidstadt, bis er sein Haus, das Haus des Herrn und die Mauern rings um Jerusalem vollendet hatte.", heading: "Salomos Heirat: 3,1", footnotes: ["Die Heirat bedeutete, dass Salomo von Ägypten als König anerkannt wurde."] },
      { number: 2, text: "Das Volk opferte zu jener Zeit auf den Kulthöhen, weil dem Namen des Herrn noch kein Haus gebaut war.", heading: "Salomos Bitte um Weisheit: 3,2-15", footnotes: ["Kulthöhen in der Nähe vieler Städte galten in alter Zeit als rechtmäßig (vgl. Ex 20,24), wurden aber in Dtn 12 verboten. Der Verfasser der Königsbücher beurteilt Salomo und die späteren Könige nach diesem Verbot."] },
      { number: 3, text: "Salomo aber liebte den Herrn und befolgte die Gebote seines Vaters David; nur brachte er auf den Kulthöhen Schlachtopfer und Rauchopfer dar." },
      { number: 4, text: "So ging der König nach Gibeon, um dort zu opfern; denn hier war die angesehenste Kulthöhe. Tausend Brandopfer legte Salomo auf ihren Altar.", footnotes: ["(4-15) 2 Chr 1,3-13", "In Gibeon, nördlich von Jerusalem, befand sich nach 1 Chr 21,29 und 2 Chr 1,3 das aus der Wüstenzeit stammende heilige Zelt."] },
      { number: 5, text: "In Gibeon erschien der Herr dem Salomo nachts im Traum und forderte ihn auf: Sprich eine Bitte aus, die ich dir gewähren soll." },
      { number: 6, text: "Salomo antwortete: Du hast deinem Knecht David, meinem Vater, große Huld erwiesen; denn er lebte vor dir in Treue, in Gerechtigkeit und mit aufrichtigem Herzen. Du hast ihm diese große Huld bewahrt und ihm einen Sohn geschenkt, der heute auf seinem Thron sitzt." },
      { number: 7, text: "So hast du jetzt, Herr, mein Gott, deinen Knecht anstelle meines Vaters David zum König gemacht. Doch ich bin noch sehr jung und weiß nicht, wie ich mich als König verhalten soll." },
      { number: 8, text: "Dein Knecht steht aber mitten in deinem Volk, das du erwählt hast: einem großen Volk, das man wegen seiner Menge nicht zählen und nicht schätzen kann." },
      { number: 9, text: "Verleih daher deinem Knecht ein hörendes Herz, damit er dein Volk zu regieren und das Gute vom Bösen zu unterscheiden versteht. Wer könnte sonst dieses mächtige Volk regieren?" },
      { number: 10, text: "Es gefiel dem Herrn, dass Salomo diese Bitte aussprach." },
      { number: 11, text: "Daher antwortete ihm Gott: Weil du gerade diese Bitte ausgesprochen hast und nicht um langes Leben, Reichtum oder um den Tod deiner Feinde, sondern um Einsicht gebeten hast, um auf das Recht zu hören," },
      { number: 12, text: "werde ich deine Bitte erfüllen. Sieh, ich gebe dir ein so weises und verständiges Herz, dass keiner vor dir war und keiner nach dir kommen wird, der dir gleicht." },
      { number: 13, text: "Aber auch das, was du nicht erbeten hast, will ich dir geben: Reichtum und Ehre, sodass zu deinen Lebzeiten keiner unter den Königen dir gleicht." },
      { number: 14, text: "Wenn du auf meinen Wegen gehst, meine Gesetze und Gebote befolgst wie dein Vater David, dann schenke ich dir ein langes Leben." },
      { number: 15, text: "Da erwachte Salomo und merkte, dass es ein Traum war. Als er nach Jerusalem kam, trat er vor die Bundeslade des Herrn, brachte Brand- und Heilsopfer dar und gab ein Festmahl für alle seine Diener." },
      { number: 16, text: "Damals kamen zwei Dirnen und traten vor den König.", heading: "Das salomonische Urteil: 3,16-28" },
      { number: 17, text: "Die eine sagte: Bitte, Herr, ich und diese Frau wohnen im gleichen Haus, und ich habe dort in ihrem Beisein geboren." },
      { number: 18, text: "Am dritten Tag nach meiner Niederkunft gebar auch diese Frau. Wir waren beisammen; kein Fremder war bei uns im Haus, nur wir beide waren dort." },
      { number: 19, text: "Nun starb der Sohn dieser Frau während der Nacht; denn sie hatte ihn im Schlaf erdrückt." },
      { number: 20, text: "Sie stand mitten in der Nacht auf, nahm mir mein Kind weg, während deine Magd schlief, und legte es an ihre Seite. Ihr totes Kind aber legte sie an meine Seite." },
      { number: 21, text: "Als ich am Morgen aufstand, um mein Kind zu stillen, war es tot. Als ich es aber am Morgen genau ansah, war es nicht mein Kind, das ich geboren hatte." },
      { number: 22, text: "Da rief die andere Frau: Nein, mein Kind lebt und dein Kind ist tot. Doch die erste entgegnete: Nein, dein Kind ist tot und mein Kind lebt. So stritten sie vor dem König." },
      { number: 23, text: "Da begann der König: Diese sagt: Mein Kind lebt und dein Kind ist tot! und jene sagt: Nein, dein Kind ist tot und mein Kind lebt." },
      { number: 24, text: "Und der König fuhr fort: Holt mir ein Schwert! Man brachte es vor den König." },
      { number: 25, text: "Nun entschied er: Schneidet das lebende Kind entzwei und gebt eine Hälfte der einen und eine Hälfte der anderen!" },
      { number: 26, text: "Doch nun bat die Mutter des lebenden Kindes den König - es regte sich nämlich in ihr die mütterliche Liebe zu ihrem Kind: Bitte, Herr, gebt ihr das lebende Kind und tötet es nicht! Doch die andere rief: Es soll weder mir noch dir gehören. Zerteilt es!" },
      { number: 27, text: "Da befahl der König: Gebt jener das lebende Kind und tötet es nicht; denn sie ist seine Mutter." },
      { number: 28, text: "Ganz Israel hörte von dem Urteil, das der König gefällt hatte, und sie schauten mit Ehrfurcht zu ihm auf; denn sie erkannten, dass die Weisheit Gottes in ihm war, wenn er Recht sprach." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "König Salomo war König von ganz Israel.", heading: "Die Verwaltung des Reiches: 4,1 - 5,8", footnotes: ["1-6: Die Besetzung der Ämter entspricht der späteren Zeit Salomos. Der Priester Zadok ist bereits durch seinen Enkel (1 Chr 5,34f) ersetzt. Die Schaffung der Ämter eines Palastvorstehers, eines Freundes des Königs und eines Vorgesetzten der Statthalter, die in den Listen Davids noch fehlen, zeigt den Fortschritt im Ausbau der Verwaltung."] },
      { number: 2, text: "Dies waren seine obersten Beamten: Asarja, der Enkel Zadoks, war Priester." },
      { number: 3, text: "Elihoref und Ahija, die Söhne Schischas, waren Staatsschreiber, Joschafat, der Sohn Ahiluds, war Sprecher des Königs." },
      { number: 4, text: "Benaja, der Sohn Jojadas, war Heerführer [Zadok und Abjatar waren Priester]." },
      { number: 5, text: "Asarja, der Sohn Natans, war Vorgesetzter der Statthalter. Sabud, der Sohn Natans, war der Freund des Königs,", footnotes: ["«Freund des Königs» ist hier Amtsbezeichnung. In H steht vor dem Titel «Freund» noch das Wort «Priester»."] },
      { number: 6, text: "Ahischar war Palastvorsteher und Adoniram, der Sohn Abdas, war Aufseher über die Fronarbeiten." },
      { number: 7, text: "Salomo hatte zwölf Statthalter für ganz Israel, die den König und sein Haus versorgten. Je einen Monat im Jahr hatte ihn jeder zu versorgen.", footnotes: ["7-19: Die Gaueinteilung erfasste nur das Gebiet, aus dem später das Nordreich Israel entstand."] },
      { number: 8, text: "Das sind ihre Namen: Der Sohn Hurs (war Statthalter) im Gebirge Efraim," },
      { number: 9, text: "der Sohn Dekers in Makaz, Schaalbim, Bet-Schemesch, Elon und Bet-Hanan," },
      { number: 10, text: "der Sohn Heseds in Arubbot. Ihm unterstanden Socho und das ganze Gebiet von Hefer." },
      { number: 11, text: "Dem Sohn Abinadabs unterstand der ganze Höhenrücken von Dor. Er hatte Tafat, eine Tochter Salomos, zur Frau." },
      { number: 12, text: "Baana, der Sohn Ahiluds, verwaltete Taanach, Megiddo und ganz Bet-Schean, das an der Seite von Zaretan unterhalb von Jesreel liegt, (das ist das ganze Gebiet) von Bet-Schean bis Abel-Mehola und bis über Jokneam hinaus." },
      { number: 13, text: "Der Sohn Gebers (war Statthalter) in Ramot-Gilead. Ihm unterstanden die Zeltdörfer Jaïrs, des Sohnes des Manasse, in Gilead und der Kreis Argob im Baschan, sechzig große Städte mit Mauern und bronzenen Torriegeln." },
      { number: 14, text: "Ahinadab, der Sohn Iddos, (war Statthalter) in Mahanajim," },
      { number: 15, text: "Ahimaaz in Naftali. Auch er hatte eine Tochter Salomos, nämlich Basemat, zur Frau." },
      { number: 16, text: "Baana, der Sohn Huschais, (war Statthalter) in Ascher und Bealot," },
      { number: 17, text: "Joschafat, der Sohn Paruachs, in Issachar," },
      { number: 18, text: "Schimi, der Sohn Elas, in Benjamin." },
      { number: 19, text: "Geber, der Sohn Uris, (verwaltete) in Gilead das Land Sihons, des Königs der Amoriter, und Ogs, des Königs des Baschan. Dazu kam ein Vogt im Land Juda." },
      { number: 20, text: "Das Volk von Juda und Israel war zahlreich wie der Sand am Meer. Es hatte zu essen und zu trinken und war glücklich." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Salomo war Herrscher über alle Reiche vom Eufrat bis zum Land der Philister und bis an die Grenze Ägyptens. Sie entrichteten Abgaben und waren Salomo untertan, solange er lebte.", footnotes: ["David hatte im Norden das Reich von Damaskus und im Süden die Edomiter unterworfen und beherrschte dadurch das ganze Gebiet vom Eufrat bis an die Grenzen Ägyptens. Die Küstengebiete der Philister blieben aber selbstständig."] },
      { number: 2, text: "Der tägliche Unterhalt Salomos belief sich auf dreißig Kor Feinmehl, sechzig Kor gewöhnliches Mehl,", footnotes: ["2f: Der große Aufwand diente zur Versorgung aller Personen, die im Dienst des Staates standen."] },
      { number: 3, text: "zehn Mastrinder, zwanzig Weiderinder, hundert Schafe, nicht gerechnet die Hirsche, Gazellen, Rehe und das gemästete Geflügel." },
      { number: 4, text: "Denn er herrschte über das ganze Gebiet diesseits des Stromes, von Tifsach bis Gaza, über alle Könige diesseits des Stromes. Er hatte Frieden ringsum nach allen Seiten.", footnotes: ["Eintragung aus nachexilischer Zeit. Zu «diesseits des Stromes» vgl. die Anmerkung zu 2 Sam 10,16. - Tifsach = Thapsakus am Eufrat."] },
      { number: 5, text: "Juda und Israel lebten in Sicherheit von Dan bis Beerscheba; ein jeder saß unter seinem Weinstock und seinem Feigenbaum, solange Salomo lebte.", footnotes: ["Die Formel «von Dan bis Beerscheba» umschreibt die ganze Nord-Süd-Ausdehnung des von Israeliten bewohnten Gebiets. - Unter seinem Weinstock und seinem Feigenbaum sitzen ist Zeichen des Glücks und der Zufriedenheit (vgl. 2 Kön 18,31; Mi 4,4; Sach 3,10)."] },
      { number: 6, text: "Salomo hatte viertausend Stallplätze für seine Wagenpferde und zwölftausend Mann als Besatzung für die Wagen.", footnotes: ["10,26; 2 Chr 9,25", "Von Salomo wurde der Kriegswagen im israelitischen Heer eingeführt. Seine Besatzung bestand wahrscheinlich aus drei Mann. Wenn jeder Stallplatz zwei Pferde, das Gespann für einen Wagen enthielt, verteilen sich die 12000 Mann auf 4000 Wagen, so 2 Chr 9,25 (H: 40000); der Wirklichkeit näher kommt vielleicht die Zahl 1400 in 1 Kön 10,26."] },
      { number: 7, text: "Jene Statthalter, jeder in seinem Monat, versorgten den König Salomo und alle, die zu seinem Tisch Zutritt hatten. Sie ließen es an nichts fehlen." },
      { number: 8, text: "Die Gerste und das Stroh für die Pferde und Zugtiere brachten sie jeweils an den Ort, für den jeder zuständig war." },
      { number: 9, text: "Gott gab Salomo Weisheit und Einsicht in hohem Maß und Weite des Herzens - wie Sand am Strand des Meeres.", heading: "Die Weisheit Salomos: 5,9-14", footnotes: ["Ps 139,18"] },
      { number: 10, text: "Die Weisheit Salomos war größer als die Weisheit aller Söhne des Ostens und alle Weisheit Ägyptens.", footnotes: ["Söhne des Ostens: die Nomadenstämme der syrisch-arabischen Wüste."] },
      { number: 11, text: "Er war weiser als alle Menschen, weiser als Etan, der Esrachiter, als Heman, Kalkol und Darda, die Söhne Mahols. Sein Name war bekannt bei allen Völkern ringsum.", footnotes: ["1 Chr 2,6; Ps 89,1"] },
      { number: 12, text: "Er verfasste dreitausend Sprichwörter und die Zahl seiner Lieder betrug tausendundfünf." },
      { number: 13, text: "Er redete über die Bäume, von der Zeder auf dem Libanon bis zum Ysop, der an der Mauer wächst. Er redete über das Vieh, die Vögel, das Gewürm und die Fische." },
      { number: 14, text: "Von allen Völkern kamen Leute, um die Weisheit Salomos zu hören, Abgesandte von allen Königen der Erde, die von seiner Weisheit vernommen hatten." },
      { number: 15, text: "Hiram, der König von Tyrus, sandte seine Diener zu Salomo; denn er hatte gehört, dass man ihn anstelle seines Vaters zum König gesalbt habe. Hiram war nämlich zeitlebens ein Freund Davids gewesen.", heading: "Die Vorbereitungen zum Tempelbau: 5,15-32", footnotes: ["(15-32) 2 Chr 2,2-17", "Zum Verhältnis Hirams zu David vgl. 2 Sam 5,11."] },
      { number: 16, text: "Und Salomo ließ Hiram sagen:" },
      { number: 17, text: "Du weißt selbst, dass mein Vater David durch Kriege verhindert war, dem Namen des Herrn, seines Gottes, ein Haus zu bauen, da seine Feinde ihn bedrängten, bis der Herr sie ihm unter die Füße legte." },
      { number: 18, text: "Jetzt aber hat mir der Herr, mein Gott, ringsum Ruhe verschafft. Es gibt keinen Widersacher mehr und keine Gefahr." },
      { number: 19, text: "Darum gedenke ich, dem Namen des Herrn, meines Gottes, ein Haus zu bauen; denn er hat meinem Vater David zugesagt: Dein Sohn, den ich an deiner Stelle auf deinen Thron setzen werde, wird meinem Namen das Haus bauen.", footnotes: ["2 Sam 7,12f"] },
      { number: 20, text: "Befiehl nun, dass man auf dem Libanon Zedern für mich fällt. Meine Knechte sollen mit deinen Knechten arbeiten. Den Lohn für deine Knechte werde ich dir geben, ganz wie du bestimmst. Du weißt ja selbst, dass wir niemand haben, der so gut Holz fällen kann wie die Leute von Sidon." },
      { number: 21, text: "Als Hiram die Botschaft Salomos vernahm, freute er sich sehr und rief aus: Gepriesen sei heute Jahwe, der David einen weisen Sohn als Herrscher über dieses große Volk gegeben hat." },
      { number: 22, text: "Er ließ Salomo sagen: Ich habe die Botschaft vernommen, die du an mich gesandt hast, und werde deinen Wunsch nach Zedern- und Zypressenholz erfüllen." },
      { number: 23, text: "Meine Leute werden es vom Libanon an das Meer schaffen. Ich lasse es dann auf dem Meer an den Ort flößen, den du mir nennen wirst. Dort lasse ich es wieder auseinander nehmen, sodass du es abholen kannst. Du aber erfülle meinen Wunsch und sende Lebensmittel für mein Haus!" },
      { number: 24, text: "Also lieferte Hiram so viel Zedern- und Zypressenholz, wie Salomo wollte," },
      { number: 25, text: "und Salomo gab Hiram zwanzigtausend Kor Weizen zum Unterhalt seines Hofes und zwanzig Kor feinstes Öl. Diese Menge lieferte Salomo Jahr für Jahr an Hiram." },
      { number: 26, text: "Der Herr schenkte Salomo Weisheit, wie er es ihm versprochen hatte. Zwischen Salomo und Hiram herrschte Friede und sie schlossen miteinander ein Bündnis." },
      { number: 27, text: "König Salomo ließ Leute aus ganz Israel zum Frondienst ausheben. Dieser umfasste 30000 Fronpflichtige." },
      { number: 28, text: "Von ihnen schickte er abwechselnd jeden Monat 10000 Mann auf den Libanon. Einen Monat waren sie auf dem Libanon und zwei Monate zu Hause. Adoniram leitete den Frondienst." },
      { number: 29, text: "Ferner hatte Salomo 70000 Lastträger und 80000 Steinhauer im Gebirge,", footnotes: ["Die Lastträger und Steinhauer wurden nach 2 Chr 2,16f aus der nichtisraelitischen Bevölkerung genommen. Den hohen Zahlen, die kaum der Wirklichkeit entsprechen, mag eine nicht mehr bekannte Symbolik zugrunde liegen."] },
      { number: 30, text: "nicht eingerechnet die 3600 Werkführer unter dem Befehl der Statthalter, denen die Leitung der Arbeit oblag. Sie führten die Aufsicht über die Arbeiter." },
      { number: 31, text: "Der König ließ mächtige, kostbare Steine brechen, um mit Quadern das Fundament des Tempels zu legen." },
      { number: 32, text: "Die Bauleute Salomos bearbeiteten mit den Bauleuten Hirams und den Gebalitern das Holz und die Steine und richteten sie her für den Bau des Tempels.", footnotes: ["Gebaliter sind die Bewohner der Stadt Gebal (= Byblus) nördlich von Beirut."] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Im vierhundertachtzigsten Jahr nach dem Auszug der Israeliten aus Ägypten, im vierten Jahr der Regierung Salomos über Israel, im Monat Siw, das ist der zweite Monat, begann er das Haus des Herrn zu bauen.", heading: "Der Tempelbau: 6,1-38", footnotes: ["(1-38) 2 Chr 3,1-14", "1-7,51: Die Kap. 6 und 7 bereiten dem Verständnis erhebliche Schwierigkeiten. Besonders die Angaben über den Tempel sind oft schwer verständlich. H ist mehrfach durch Schreibfehler, Erweiterungen und Kürzungen entstellt. Diese Schwierigkeiten, in der Übersetzung teilweise geglättet, werden in den folgenden Anmerkungen nicht ausdrücklich erwähnt."] },
      { number: 2, text: "Das Haus, das König Salomo für den Herrn baute, war sechzig Ellen lang, zwanzig Ellen breit und dreißig Ellen hoch." },
      { number: 3, text: "Die Vorhalle vor dem Hauptraum des Hauses war zwanzig Ellen breit, entsprechend der Breite des Hauses, und zehn Ellen tief in der Längsrichtung des Hauses." },
      { number: 4, text: "Er machte für das Haus Fenster mit Rahmen und Gittern." },
      { number: 5, text: "An die Wände des Hauses, und zwar an die Wände des Hauptraums und des hinteren Raumes, legte er ringsum einen Anbau mit Kammern." },
      { number: 6, text: "Dieser war im Untergeschoss fünf Ellen, im mittleren sechs und im dritten sieben Ellen breit; die Außenwand des Hauses hatte er abgestuft, um sie nachher nicht beschädigen zu müssen." },
      { number: 7, text: "Beim Bau des Hauses wurden Steine verwendet, die man schon im Steinbruch fertig behauen hatte; Hämmer, Meißel und sonstige eiserne Werkzeuge waren beim Bau des Hauses nicht zu hören." },
      { number: 8, text: "Die Türe zu den unteren Kammern war an der Südseite des Hauses. Über Treppen stieg man zum mittleren und vom mittleren zum dritten Stockwerk hinauf." },
      { number: 9, text: "Als er den Bau des Hauses vollendet hatte, überdeckte er es mit Balken und Brettern aus Zedernholz." },
      { number: 10, text: "Den Anbau führte er um das ganze Haus. Seine Höhe betrug (in jedem Stockwerk) fünf Ellen und Zedernbalken verbanden ihn mit dem Haus." },
      { number: 11, text: "Das Wort des Herrn erging an Salomo:" },
      { number: 12, text: "Dieses Haus, das du baust, - wenn du meinen Geboten gehorchst und auf meine Vorschriften achtest und alle meine Befehle ausführst und befolgst, dann werde ich an dir das Wort wahr machen, das ich zu deinem Vater David gesprochen habe.", footnotes: ["2 Sam 7,8-16"] },
      { number: 13, text: "Und ich werde inmitten der Israeliten wohnen und mein Volk Israel nicht verlassen." },
      { number: 14, text: "So vollendete Salomo den Bau des Hauses." },
      { number: 15, text: "Er täfelte seine Innenwände mit Zedernholz aus; vom Fußboden bis zu den Balken der Decke ließ er eine Holzvertäfelung anbringen. Den Fußboden belegte er mit Zypressenholz." },
      { number: 16, text: "Zwanzig Ellen vor der Rückseite des Hauses errichtete er vom Fußboden bis zum Gebälk eine Wand aus Zedernholz und schuf so die Gotteswohnung, das Allerheiligste." },
      { number: 17, text: "Vierzig Ellen lang war der davorliegende Hauptraum." },
      { number: 18, text: "Im Innern hatte das Haus Zedernverkleidung mit eingeschnitzten Blumengewinden und Blütenranken. Alles war aus Zedernholz, kein Stein war zu sehen." },
      { number: 19, text: "Im Innern des Hauses richtete er die Gotteswohnung ein, um die Bundeslade des Herrn aufstellen zu können." },
      { number: 20, text: "Die Wohnung war zwanzig Ellen lang, zwanzig Ellen breit und zwanzig Ellen hoch; er überzog sie mit bestem Gold. Auch ließ er einen Altar aus Zedernholz herstellen." },
      { number: 21, text: "Das Innere des Hauses ließ Salomo mit bestem Gold auskleiden und vor der Gotteswohnung ließ er goldene Ketten anbringen.", footnotes: ["Ketten: Vielleicht ist ein Wort ausgefallen: einen Vorhang an goldenen Ketten."] },
      { number: 22, text: "So überzog er das ganze Haus vollständig mit Gold; auch den Altar vor der Gotteswohnung überzog er ganz mit Gold." },
      { number: 23, text: "In der Gotteswohnung ließ er zwei Kerubim aus Olivenholz anfertigen. Ihre Höhe betrug zehn Ellen.", footnotes: ["23-28: Die Kerubim hatten wohl menschenähnliches Aussehen. Die Bundeslade wurde zwischen sie gestellt (8,7), sodass sie ihre Flügel über sie breiteten."] },
      { number: 24, text: "Fünf Ellen maß der eine Flügel des Kerubs und fünf Ellen sein anderer Flügel. Von einem Flügelende bis zum anderen waren es zehn Ellen." },
      { number: 25, text: "Auch der zweite Kerub war zehn Ellen hoch. Beide hatten gleiches Maß und gleiche Gestalt." },
      { number: 26, text: "Der eine Kerub war zehn Ellen hoch und ebenso hoch war der andere." },
      { number: 27, text: "Er stellte die Kerubim mitten in den innersten Raum. Ihre Flügel waren so ausgespannt, dass der Flügel des einen Kerubs die eine Wand, der Flügel des zweiten Kerubs die andere Wand, die Flügel in der Mitte des Raumes aber einander berührten." },
      { number: 28, text: "Er ließ die Kerubim mit Gold überziehen." },
      { number: 29, text: "An allen Wänden des Hauses, im inneren wie im äußeren Raum, ließ er ringsum Kerubim, Palmen und Blütenranken einschnitzen." },
      { number: 30, text: "Auch die Fußböden des hinteren und des vorderen Raumes ließ er mit Gold belegen." },
      { number: 31, text: "Für den Eingang zur Gotteswohnung ließ er Türflügel aus Olivenholz anfertigen. Die Giebelbalken und die Seitenpfosten bildeten ein Fünfeck." },
      { number: 32, text: "An den beiden Türflügeln aus Olivenholz ließ er Kerubim, Palmen und Blütenranken einschnitzen und sie mit Gold überziehen, indem er auf die Kerubim und die Palmen Gold auftragen ließ." },
      { number: 33, text: "Ebenso ließ er für den Eingang zum Hauptraum Türpfosten aus Olivenholz anfertigen, die ein Viereck bildeten," },
      { number: 34, text: "dazu zwei Türflügel aus Zypressenholz. Zwei drehbare Teile hatte der eine Türflügel und zwei drehbare Teile der andere." },
      { number: 35, text: "Er ließ auf ihnen Kerubim, Palmen und Blütenranken einschnitzen und auf das Schnitzwerk dünnes Blattgold legen." },
      { number: 36, text: "Er legte den inneren Hof an (und umgab ihn mit einer Mauer) aus drei Lagen Quadern und einer Lage Zedernbalken." },
      { number: 37, text: "Im vierten Jahr, im Monat Siw, war das Fundament für das Haus des Herrn gelegt worden" },
      { number: 38, text: "und im elften Jahr, im Monat Bul, das ist der achte Monat, wurde das Haus mit all seinem Zubehör vollendet, ganz so, wie es geplant war. Sieben Jahre hatte man an ihm gebaut.", footnotes: ["Der Monat Bul entspricht ungefähr unserem Oktober/November."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "An seinem Palast baute Salomo dreizehn Jahre, bis er ihn ganz vollendet hatte.", heading: "Die Palastgebäude: 7,1-12" },
      { number: 2, text: "Er baute das Libanonwaldhaus, hundert Ellen lang, fünfzig Ellen breit und dreißig Ellen hoch, mit drei Reihen von Zedernsäulen und mit Zedernbalken über den Säulen.", footnotes: ["Das Libanonwaldhaus erhielt seinen Namen von den Zedernsäulen, die wie ein Wald in ihm standen. Es übertraf den Tempel an Größe. Zur Zeit Jesajas diente es als Zeughaus (Jes 22,8)."] },
      { number: 3, text: "Eine Decke aus Zedernholz war über den Kammern, die über den Säulen lagen; es waren fünfundvierzig Säulen, fünfzehn in jeder Reihe." },
      { number: 4, text: "In drei Reihen waren Fenster so angebracht, dass dreimal eine Fensterreihe einer anderen gegenüberstand." },
      { number: 5, text: "Alle Türen und Fenster waren viereckig gerahmt. Dreimal stand eine Fensterreihe der anderen gegenüber." },
      { number: 6, text: "Salomo baute die Säulenhalle, fünfzig Ellen lang und dreißig Ellen breit, davor eine Halle mit Säulen und einem Vordach." },
      { number: 7, text: "Er schuf die Thronhalle, das ist die Gerichtshalle, um darin Recht zu sprechen. Sie war vom Fußboden bis zum Gebälk mit Zedernholz ausgetäfelt." },
      { number: 8, text: "Sein eigenes Wohnhaus stand im anderen Hof, von der Halle aus nach innen zu. Es hatte die gleiche Bauart. Auch baute Salomo für die Tochter des Pharao, die er geheiratet hatte, ein Haus, das dieser Halle glich." },
      { number: 9, text: "Alle diese Bauten waren vom Grund bis zu den Gesimsen aus wertvollen Steinen aufgeführt, die in der Größe von Quadern an der Innen- wie Außenseite mit der Säge zugeschnitten waren . . .", footnotes: ["H zum Teil unverständlich."] },
      { number: 10, text: "Die Fundamente bestanden aus wertvollen, mächtigen Steinblöcken, aus Steinen von zehn und Steinen von acht Ellen." },
      { number: 11, text: "Darüber lagen wertvolle Steine in der Größe von Quadern sowie Zedernbalken." },
      { number: 12, text: "Der große Hof war rings von einer Mauer aus drei Lagen Quadern und einer Lage Zedernbalken umgeben; ebenso der innere Hof um das Haus des Herrn und der Hof um die Palasthalle.", footnotes: ["Die gesamten Palastbauten standen in einem Hof, der als der Große Hof bezeichnet wird. Durch die Mauer, die ihn umgab, war die Königsburg als eigener Stadtteil von der Bürgerstadt abgesondert."] },
      { number: 13, text: "König Salomo ließ Hiram aus Tyrus kommen.", heading: "Die Ausstattung des Tempels: 7,13-51", footnotes: ["(13-51) 2 Chr 3,15 - 5,1", "Die Berufung eines ausländischen Künstlers war notwendig, da das Kunstgewerbe in Israel noch wenig entwickelt war."] },
      { number: 14, text: "Dieser war der Sohn einer Witwe aus dem Stamm Naftali. Sein Vater war ein Bronzeschmied aus Tyrus. Er war mit Weisheit, Verstand und Geschick begabt, um jede Bronze-Arbeit auszuführen. Er kam zum König Salomo und führte alle Arbeiten für ihn aus." },
      { number: 15, text: "Er formte die zwei bronzenen Säulen. Achtzehn Ellen betrug die Höhe der einen Säule und ein Band von zwölf Ellen umspannte sie. Ihre Wandstärke betrug vier Finger; innen war sie hohl. Ebenso war die zweite Säule.", footnotes: ["2 Kön 25,17; Jer 52,21"] },
      { number: 16, text: "Dazu machte er zwei Kapitelle, um sie oben auf die Säulen zu setzen; sie waren aus Bronze gegossen. Fünf Ellen betrug die Höhe des einen Kapitells und fünf Ellen die Höhe des anderen." },
      { number: 17, text: "Auch machte er Geflechte [Flechtwerksarbeit, kettenförmige Bänder] für die Kapitelle oben auf den Säulen, und zwar ein Geflecht für das eine Kapitell und ein Geflecht für das andere." },
      { number: 18, text: "Ferner machte er Granatäpfel und legte sie in zwei Reihen ringsum über die Geflechte, sodass sie die Kapitelle oben auf den Säulen bedeckten. Ebenso verfuhr er mit dem zweiten Kapitell." },
      { number: 19, text: "Die Kapitelle oben auf den Säulen hatten die Form einer Lilienblüte . . .", footnotes: ["19f: H unverständlich."] },
      { number: 20, text: ". . ." },
      { number: 21, text: "Er stellte die Säulen an der Vorhalle des Tempels auf. Die eine Säule stellte er auf die rechte Seite und nannte sie Jachin, die andere stellte er auf die linke Seite und nannte sie Boas.", footnotes: ["Die Namen Jachin und Boas werden verschieden gedeutet. Sie bilden vielleicht einen Satz: «Er (Gott) verleihe Festigkeit mit Kraft», und drücken die Hoffnung auf den Bestand des Tempels und des Throns aus."] },
      { number: 22, text: "Oben auf den Säulen waren lilienförmige Gebilde. So wurde die Arbeit an den Säulen zu Ende geführt." },
      { number: 23, text: "Dann machte er das «Meer». Es wurde aus Bronze gegossen und maß zehn Ellen von einem Rand zum andern; es war völlig rund und fünf Ellen hoch. Eine Schnur von dreißig Ellen konnte es rings umspannen.", footnotes: ["Nach 2 Chr 4,6 diente das «Meer» den Priestern zu rituellen Waschungen."] },
      { number: 24, text: "Unterhalb seines Randes waren rundum Rankengebilde. In einer Länge von dreißig Ellen umsäumten sie das Meer ringsum in zwei Reihen. Sie wurden beim Guss mitgegossen." },
      { number: 25, text: "Das Meer stand auf zwölf Rindern. Von ihnen schauten drei nach Norden, drei nach Westen, drei nach Süden und drei nach Osten. Das Meer ruhte oben auf den Rindern. Ihre Hinterteile waren nach innen gekehrt." },
      { number: 26, text: "Die Wand des Meeres war eine Handbreit dick. Sein Rand war wie der Rand eines Bechers geformt, einer Lilienblüte gleich. Es fasste zweitausend Bat." },
      { number: 27, text: "Er machte die zehn fahrbaren Gestelle aus Bronze. Jedes war vier Ellen lang, vier Ellen breit und drei Ellen hoch." },
      { number: 28, text: "Und so waren die Gestelle beschaffen: Sie hatten Querleisten, und zwar Querleisten zwischen den Eckleisten." },
      { number: 29, text: "Auf den Querleisten zwischen den Eckleisten waren Bilder von Löwen, Rindern und Kerubim, und ebenso auf den Eckleisten. Über den Löwen und Rindern sowie unter ihnen waren Kranzgewinde eingehämmert." },
      { number: 30, text: "Jedes Gestell hatte vier bronzene Räder und bronzene Achsen. An den vier Füßen waren Ansätze, die unterhalb des Kessels angegossen waren . . .", footnotes: ["30f.36: H teilweise unverständlich."] },
      { number: 31, text: ". . . (Der Kasten des Gestells) war eineinhalb Ellen hoch. Auch an seiner Öffnung waren Verzierungen. Die Querleisten waren nicht rund, sondern viereckig." },
      { number: 32, text: "Die vier Räder waren unter den Querleisten. Ihre Halter waren am Gestell befestigt. Jedes Rad war eineinhalb Ellen hoch." },
      { number: 33, text: "Sie waren gearbeitet wie Wagenräder. Ihre Halter, Felgen, Speichen und Naben waren alle gegossen." },
      { number: 34, text: "Die vier Ansätze waren an den vier Ecken eines jeden Gestells angebracht und bildeten mit ihm ein Ganzes." },
      { number: 35, text: "Der Aufsatz des Gestells war eine halbe Elle hoch und völlig rund. An ihm waren seine Halter und Leisten angesetzt." },
      { number: 36, text: "Auf die Wandflächen, Halter und Querleisten ließ Salomo Bilder von Kerubim, Löwen und Palmen eingravieren . . ." },
      { number: 37, text: "In dieser Weise fertigte Hiram die zehn Gestelle an. Sie hatten alle gleichen Guss, gleiches Maß und gleiche Gestalt." },
      { number: 38, text: "Dazu machte er zehn bronzene Kessel. Jeder fasste vierzig Bat und hatte eine Weite von vier Ellen. Für jedes der zehn Gestelle war ein Kessel bestimmt." },
      { number: 39, text: "Fünf von den Gestellen brachte er an die Südseite des Hauses und fünf an die Nordseite. Das «Meer» stellte er an die Südseite des Hauses, gegen Südosten." },
      { number: 40, text: "Auch machte Hiram die Töpfe, Schaufeln und Schalen. So führte Hiram alle Arbeiten zu Ende, die er dem König Salomo für das Haus des Herrn anzufertigen hatte:" },
      { number: 41, text: "zwei Säulen, zwei beckenförmige Kapitelle oben auf den Säulen, die zwei Flechtwerke, mit denen man die beiden beckenförmigen Kapitelle oben auf den Säulen bedeckte," },
      { number: 42, text: "die vierhundert Granatäpfel für die beiden Flechtwerke, die in zwei Reihen an jedem Flechtwerk angebracht waren und die beiden beckenförmigen Kapitelle auf den Säulen bedeckten," },
      { number: 43, text: "die zehn fahrbaren Gestelle, die zehn Kessel für die Gestelle," },
      { number: 44, text: "das eine Meer, die zwölf Rinder unter dem Meer," },
      { number: 45, text: "die Töpfe, Schaufeln und Schalen. Alle diese Geräte, die Hiram dem König Salomo für das Haus des Herrn anfertigte, waren aus glatter Bronze." },
      { number: 46, text: "In der Jordanau zwischen Sukkot und Zaretan ließ sie der König in Formen aus festem Lehm gießen.", footnotes: ["Bodenfunde haben gezeigt, dass die hier lebende vorisraelitische Bevölkerung sich auf Metallverarbeitung verstand."] },
      { number: 47, text: "Und Salomo gab allen Geräten ihren Platz. Wegen ihrer überaus großen Menge war das Gewicht der Bronze nicht festzustellen." },
      { number: 48, text: "Salomo ließ alle Geräte, die zum Haus des Herrn gehörten, anfertigen: den goldenen Altar, den goldenen Tisch, auf den man die Schaubrote legte," },
      { number: 49, text: "die fünf Leuchter auf der rechten und die fünf Leuchter auf der linken Seite vor der Gotteswohnung, aus bestem Gold, dazu die goldenen Blüten, Lampen und Dochtscheren," },
      { number: 50, text: "ferner die Becken, Messer, Schalen, Schüsseln und Pfannen aus bestem Gold. Auch die Stirnseiten der Türen des inneren Raumes zum Allerheiligsten und die Stirnseiten der beiden Türflügel, die zum Hauptraum führten, waren mit Gold verkleidet." },
      { number: 51, text: "So wurden alle Arbeiten, die König Salomo für das Haus des Herrn ausführen ließ, vollendet. Dann brachte er die Weihegaben seines Vaters David hinein und legte das Silber, das Gold und die Geräte in die Schatzkammern des Hauses des Herrn." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Damals versammelte Salomo die Ältesten Israels, alle Stammesführer und die Häupter der israelitischen Großfamilien bei sich in Jerusalem, um die Bundeslade des Herrn aus der Stadt Davids, das ist Zion, heraufzuholen.", heading: "Die Übertragung der Bundeslade: 8,1-13", footnotes: ["(1-13) 2 Chr 5,2-11; 6,1f"] },
      { number: 2, text: "Am Fest im Monat Etanim, das ist der siebte Monat, kamen alle Männer Israels bei König Salomo zusammen.", footnotes: ["Der Monat Etanim (Monat «der ständig fließenden Bäche») entspricht ungefähr unserem September/Oktober. Das Fest ist das Laubhüttenfest."] },
      { number: 3, text: "In Gegenwart aller Ältesten Israels nahmen die Priester die Lade" },
      { number: 4, text: "und brachten sie zugleich mit dem Offenbarungszelt und den heiligen Geräten, die im Zelt waren, hinauf. Die Priester und die Leviten übernahmen den Trägerdienst.", footnotes: ["Durch die Unterbringung des Offenbarungszeltes von Gibeon im Tempel und seine Vereinigung mit der Bundeslade übernahm der Tempel die Aufgabe des Wanderheiligtums der Wüstenzeit."] },
      { number: 5, text: "König Salomo aber und die ganze Gemeinde Israels, die bei ihm vor der Lade versammelt war, schlachteten Schafe und Rinder, die man wegen ihrer Menge nicht zählen und nicht berechnen konnte." },
      { number: 6, text: "Darauf stellten die Priester die Bundeslade des Herrn an ihren Platz, in die Gotteswohnung des Hauses, in das Allerheiligste, unter die Flügel der Kerubim." },
      { number: 7, text: "Denn die Kerubim breiteten ihre Flügel über den Ort, wo die Lade stand, und bedeckten sie und ihre Stangen von oben her." },
      { number: 8, text: "Die Stangen waren so lang, dass man ihre Spitzen im Heiligtum vor der Gotteswohnung sehen konnte; draußen aber waren sie nicht zu sehen. Sie blieben dort bis zum heutigen Tag." },
      { number: 9, text: "In der Lade befanden sich nur die zwei steinernen Tafeln, die Mose am Horeb hineingelegt hatte, die Tafeln des Bundes, den der Herr mit den Israeliten beim Auszug aus Ägypten geschlossen hatte." },
      { number: 10, text: "Als dann die Priester aus dem Heiligtum traten, erfüllte die Wolke das Haus des Herrn." },
      { number: 11, text: "Sie konnten wegen der Wolke ihren Dienst nicht verrichten; denn die Herrlichkeit des Herrn erfüllte das Haus des Herrn." },
      { number: 12, text: "Damals sagte Salomo: Der Herr hat die Sonne an den Himmel gesetzt; / er selbst wollte im Dunkel wohnen.", footnotes: ["Der erste Satz ist nur in G überliefert."] },
      { number: 13, text: "Ich habe ein fürstliches Haus für dich gebaut, / eine Wohnstätte für ewige Zeiten." },
      { number: 14, text: "Dann wandte sich der König um und segnete die ganze Versammlung Israels. Alle standen", heading: "Die Ansprache des Königs: 8,14-21", footnotes: ["(14-21) 2 Chr 6,3-11"] },
      { number: 15, text: "und er betete: Gepriesen sei der Herr, der Gott Israels. Seine Hand hat ausgeführt, was sein Mund meinem Vater David verheißen hat, als er sprach:" },
      { number: 16, text: "Seit dem Tag, da ich mein Volk Israel aus Ägypten führte, habe ich aus keinem der Stämme Israels eine Stadt für den Bau eines Hauses erwählt, um meinen Namen dort wohnen zu lassen. David aber habe ich zum Herrscher über mein Volk Israel erwählt." },
      { number: 17, text: "Mein Vater David wollte dem Namen des Herrn, des Gottes Israels, ein Haus bauen.", footnotes: ["(17-19) 2 Sam 7,1-13"] },
      { number: 18, text: "Doch der Herr sprach zu ihm: Wenn du dir vorgenommen hast, meinem Namen ein Haus zu bauen, hast du einen guten Entschluss gefasst." },
      { number: 19, text: "Doch sollst nicht du das Haus bauen; sondern erst dein leiblicher Sohn soll meinem Namen das Haus bauen." },
      { number: 20, text: "Der Herr hat jetzt sein Versprechen, das er gegeben hat, wahr gemacht: Ich bin an die Stelle meines Vaters David getreten und habe den Thron Israels bestiegen, wie es der Herr zugesagt hatte. Ich habe dem Namen des Herrn, des Gottes Israels, das Haus gebaut" },
      { number: 21, text: "und darin einen Raum für die Lade geschaffen. Sie enthält die Tafeln des Bundes, den der Herr mit unseren Vätern geschlossen hat, als er sie aus Ägypten führte." },
      { number: 22, text: "Dann trat Salomo in Gegenwart der ganzen Versammlung Israels vor den Altar des Herrn, breitete seine Hände zum Himmel aus", heading: "Das Weihegebet: 8,22-53", footnotes: ["(22-52) 2 Chr 6,12-40"] },
      { number: 23, text: "und betete: Herr, Gott Israels, im Himmel oben und auf der Erde unten gibt es keinen Gott, der so wie du Bund und Huld seinen Knechten bewahrt, die mit ungeteiltem Herzen vor ihm leben." },
      { number: 24, text: "Du hast das Versprechen gehalten, das du deinem Knecht, meinem Vater David, gegeben hast. Deine Hand hat heute erfüllt, was dein Mund versprochen hat." },
      { number: 25, text: "Und nun, Herr, Gott Israels, halte auch das andere Versprechen, das du deinem Knecht David, meinem Vater, gegeben hast, als du sagtest: Es soll dir nie an einem Nachkommen fehlen, der vor mir auf dem Thron Israels sitzt, wenn nur deine Söhne darauf achten, ihren Weg so vor mir zu gehen, wie du es getan hast." },
      { number: 26, text: "Gott Israels, möge sich jetzt dein Wort, das du deinem Knecht David, meinem Vater, gegeben hast, als wahr erweisen." },
      { number: 27, text: "Wohnt denn Gott wirklich auf der Erde? Siehe, selbst der Himmel und die Himmel der Himmel fassen dich nicht, wie viel weniger dieses Haus, das ich gebaut habe." },
      { number: 28, text: "Wende dich, Herr, mein Gott, dem Beten und Flehen deines Knechtes zu! Höre auf das Rufen und auf das Gebet, das dein Knecht heute vor dir verrichtet." },
      { number: 29, text: "Halte deine Augen offen über diesem Haus bei Nacht und bei Tag, über der Stätte, von der du gesagt hast, dass dein Name hier wohnen soll. Höre auf das Gebet, das dein Knecht an dieser Stätte verrichtet." },
      { number: 30, text: "Achte auf das Flehen deines Knechtes und deines Volkes Israel, wenn sie an dieser Stätte beten. Höre sie im Himmel, dem Ort, wo du wohnst. Höre sie und verzeih!" },
      { number: 31, text: "Wenn sich jemand gegen seinen Nächsten verfehlt und dieser ihm einen Eid abverlangt, den er schwören muss, und er dann kommt und vor deinem Altar in diesem Haus schwört,", footnotes: ["31-51: Der Hauptteil des Gebets fleht nicht um Hilfe in augenblicklicher Not, sondern zählt Fälle auf, die in Zukunft Anlass zu einem Gebet im Tempel geben können. Es lassen sich sieben Bitten feststellen. Die Siebenzahl als heilige Zahl mag beabsichtigt sein."] },
      { number: 32, text: "so höre du es im Himmel und greif ein! Verschaff deinen Knechten Recht; verurteile den Schuldigen und lass sein Tun auf ihn selbst zurückfallen! Den Schuldlosen aber sprich frei und vergilt ihm, wie es seiner Gerechtigkeit entspricht." },
      { number: 33, text: "Wenn dein Volk Israel von einem Feind geschlagen wird, weil es gegen dich gesündigt hat, und dann wieder zu dir umkehrt, deinen Namen preist und in diesem Haus zu dir betet und fleht," },
      { number: 34, text: "so höre du es im Himmel! Vergib deinem Volk Israel seine Sünde; lass sie in das Land zurückkommen, das du ihren Vätern gegeben hast." },
      { number: 35, text: "Wenn der Himmel verschlossen ist und kein Regen fällt, weil sie gegen dich gesündigt haben, und wenn sie dann an diesem Ort beten, deinen Namen preisen und von ihrer Sünde lassen, weil du sie demütigst,", footnotes: ["sie demütigst: Text korr. nach G; H: sie erhörst."] },
      { number: 36, text: "so höre du sie im Himmel! Vergib deinen Knechten und deinem Volk Israel ihre Sünden; denn du führst sie den guten Weg, den sie gehen sollen. Spende Regen deinem Land, das du deinem Volk zum Erbbesitz gegeben hast." },
      { number: 37, text: "Wenn im Land Hungersnot herrscht, wenn Pest ausbricht, wenn Getreidebrand, Rost, Heuschrecken und Ungeziefer auftreten, wenn Feinde sie im eigenen Land bedrängen, wenn irgendeine Plage oder Krankheit sie trifft," },
      { number: 38, text: "(so höre du) jedes Gebet und Flehen eines jeden einzelnen und deines ganzen Volkes Israel; denn sie alle kennen die Not ihres Herzens und erheben ihre Hände zu diesem Haus." },
      { number: 39, text: "Höre sie dann im Himmel, dem Ort, wo du wohnst, und verzeih! Greif ein, und vergilt jedem, wie es sein Tun verdient. Du kennst ja ihre Herzen; denn du allein kennst die Herzen aller Menschen." },
      { number: 40, text: "So werden sie dich fürchten, solange sie in dem Land leben, das du unseren Vätern gegeben hast." },
      { number: 41, text: "Auch Fremde, die nicht zu deinem Volk Israel gehören, werden wegen deines Namens aus fernen Ländern kommen;" },
      { number: 42, text: "denn sie werden von deinem großen Namen, deiner starken Hand und deinem hoch erhobenen Arm hören. Sie werden kommen und in diesem Haus beten." },
      { number: 43, text: "Höre sie dann im Himmel, dem Ort, wo du wohnst, und tu alles, weswegen der Fremde zu dir ruft. Dann werden alle Völker der Erde deinen Namen erkennen. Sie werden dich fürchten, wie dein Volk Israel dich fürchtet, und erfahren, dass dein Name ausgerufen ist über diesem Haus, das ich gebaut habe." },
      { number: 44, text: "Wenn dein Volk auf dem Weg, den du es führst, gegen seine Feinde in den Krieg zieht und wenn es dann zu dir betet, zur Stadt hingewendet, die du erwählt hast, und zu dem Haus hin, das ich deinem Namen gebaut habe," },
      { number: 45, text: "so höre du im Himmel sein Beten und Flehen und verschaff ihm Recht." },
      { number: 46, text: "Wenn sie gegen dich sündigen - es gibt ja niemand, der nicht sündigt - und du ihnen zürnst, sie ihren Bedrängern preisgibst und ihre Feinde sie gefangen fortführen in ein fernes oder nahes Land," },
      { number: 47, text: "so werden sie im Land ihrer Gefangenschaft in sich gehen. Sie werden im Land ihrer Gefangenschaft umkehren, zu dir flehen und rufen: Wir haben gesündigt, Unrecht getan und gefrevelt." },
      { number: 48, text: "Mit ganzem Herzen und ganzer Seele werden sie im Land ihrer Feinde, von denen sie als Gefangene weggeführt wurden, zu dir umkehren und zu dir beten, zum Land hingewendet, das du ihren Vätern gegeben hast, zur Stadt hin, die du erwählt hast, und zum Haus hin, das ich deinem Namen gebaut habe." },
      { number: 49, text: "Höre dann im Himmel, dem Ort, wo du wohnst, ihr Beten und Flehen! Verschaff ihnen Recht" },
      { number: 50, text: "und verzeih deinem Volk, was es gegen dich gesündigt hat; verzeih ihm alle Frevel, die es gegen dich begangen hat. Lass sie bei ihren Unterdrückern Mitleid und Erbarmen finden!" },
      { number: 51, text: "Sie sind ja dein Volk und dein Eigentum, das du aus dem Schmelzofen, aus Ägypten, herausgeführt hast." },
      { number: 52, text: "Halte deine Augen offen für das Flehen deines Knechtes und für das Flehen deines Volkes Israel! Erhöre sie, sooft sie zu dir rufen." },
      { number: 53, text: "Du hast dir Israel unter allen Völkern der Erde als Eigentum ausgewählt, wie du es durch deinen Knecht Mose verkündet hast, als du unsere Väter aus Ägypten geführt hast, Herr und Gott." },
      { number: 54, text: "Als Salomo dieses flehentliche Gebet zum Herrn beendet hatte, erhob er sich auf dem Platz vor dem Altar des Herrn, wo er niedergekniet war und die Hände zum Himmel ausgebreitet hatte.", heading: "Salomos Segen: 8,54-61" },
      { number: 55, text: "Er trat vor die ganze Versammlung Israels, segnete sie und rief mit lauter Stimme:" },
      { number: 56, text: "Gepriesen sei der Herr, der seinem Volk Israel Ruhe geschenkt hat, wie er es versprochen hat. Von all den herrlichen Verheißungen, die er durch seinen Knecht Mose verkündet hat, ist nicht eine hinfällig geworden." },
      { number: 57, text: "Der Herr, unser Gott, sei mit uns, wie er mit unseren Vätern war. Er verlasse uns nicht und verstoße uns nicht." },
      { number: 58, text: "Er lenke unsere Herzen zu sich hin, damit wir auf seinen Wegen gehen und die Gebote, Befehle und Anordnungen befolgen, die er unseren Vätern gegeben hat." },
      { number: 59, text: "Mögen diese Worte, die ich flehend vor dem Herrn, unserem Gott, gesprochen habe, ihm Tag und Nacht gegenwärtig bleiben. Möge er seinem Knecht und seinem Volk Israel Recht verschaffen, wie es jeder Tag verlangt," },
      { number: 60, text: "damit alle Völker der Erde erkennen, dass niemand Gott ist als der Herr allein." },
      { number: 61, text: "Euer Herz aber bleibe ungeteilt beim Herrn, unserem Gott, sodass ihr seinen Gesetzen folgt und auf seine Gebote achtet, wie es heute geschieht." },
      { number: 62, text: "Dann brachten der König und mit ihm ganz Israel vor dem Herrn Opfer dar.", heading: "Der Abschluss der Feier: 8,62-66", footnotes: ["(62-66) 2 Chr 7,4-10"] },
      { number: 63, text: "Zweiundzwanzigtausend Rinder und hundertzwanzigtausend Schafe ließ Salomo als Heilsopfer für den Herrn schlachten. So vollzogen der König und alle Israeliten die Weihe des Hauses des Herrn.", footnotes: ["Zum Heilsopfer vgl. die Anmerkung zu Lev 3,1-17. - Den hohen Zahlen der Opfertiere mag eine uns unbekannte Symbolik zugrunde liegen."] },
      { number: 64, text: "An jenem Tag weihte der König auch die Mitte des Hofes, der vor dem Haus des Herrn war, als er dort das Brandopfer, das Speiseopfer und die Fettstücke der Heilsopfer darbrachte. Der bronzene Altar, der vor dem (Tempel des) Herrn stand, war nämlich zu klein, um das Brandopfer, das Speiseopfer und die Fettstücke der Heilsopfer fassen zu können." },
      { number: 65, text: "Salomo feierte damals mit ganz Israel, das von Lebo-Hamat bis zum Grenzbach Ägyptens zu einer großen Versammlung vor dem Herrn, unserem Gott, erschienen war, das (Laubhütten-)Fest sieben Tage lang [und nochmals sieben Tage, zusammen vierzehn Tage].", footnotes: ["Späterer Zusatz, eingefügt in der Annahme, dass die Tempelweihe und das Laubhüttenfest nicht gleichzeitig, sondern nacheinander in je siebentägiger Feier begangen wurden. - Lebo-Hamat: Name einer Landschaft oder eines Ortes zwischen Libanon und Antilibanon oder weiter im Norden, am Orontes."] },
      { number: 66, text: "Am achten Tag entließ er das Volk. Sie priesen den König und gingen zu ihren Zelten, frohen Mutes und voll Freude über all das Gute, das der Herr an seinem Knecht David und seinem Volk Israel getan hatte." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Nachdem Salomo den Bau des Tempels und des königlichen Palastes vollendet und alle Pläne, die er auszuführen wünschte, verwirklicht hatte,", heading: "Die Verheißung für den Tempel: 9,1-9", footnotes: ["(1-9) 2 Chr 7,11-22"] },
      { number: 2, text: "erschien ihm der Herr zum zweiten Mal, wie er ihm in Gibeon erschienen war.", footnotes: ["(2-3) 3,4f"] },
      { number: 3, text: "Er sprach zu ihm: Ich habe dein flehentliches Gebet, das du an mich gerichtet hast, gehört und dieses Haus, das du gebaut hast, geheiligt. Meinen Namen werde ich für immer hierher legen, meine Augen und mein Herz werden allezeit hier weilen." },
      { number: 4, text: "Wenn du mit ungeteiltem und aufrichtigem Herzen vor mir den Weg gehst, den dein Vater David gegangen ist, und wenn du alles tust, was ich dir befohlen habe, wenn du auf meine Gebote und Rechtsvorschriften achtest," },
      { number: 5, text: "dann werde ich deinen Königsthron auf ewig in Israel bestehen lassen, wie ich es deinem Vater David zugesichert habe, zu dem ich gesagt habe: Es soll dir nie an einem Nachkommen auf dem Thron Israels fehlen.", footnotes: ["2 Sam 7,16"] },
      { number: 6, text: "Doch wenn ihr und eure Söhne euch von mir abwendet und die Gebote und Gesetze, die ich euch gegeben habe, übertretet, wenn ihr euch anschickt, andere Götter zu verehren und anzubeten," },
      { number: 7, text: "dann werde ich Israel in dem Land ausrotten, das ich ihm gegeben habe. Das Haus, das ich meinem Namen geweiht habe, werde ich aus meinem Angesicht wegschaffen und Israel soll zum Gespött und zum Hohn unter allen Völkern werden." },
      { number: 8, text: "Dieses Haus wird zu einem Trümmerhaufen werden und jeder, der vorübergeht, wird sich entsetzen und zischen. Man wird fragen: Warum hat der Herr diesem Land und diesem Haus das angetan?", footnotes: ["wird zu einem Trümmerhaufen werden: sinngemäß korr.; H: wird hoch sein. - Zischen ist ein Zeichen des Spottes."] },
      { number: 9, text: "Und man wird antworten: Weil sie den Herrn, ihren Gott, der ihre Väter aus Ägypten geführt hat, verlassen, sich an andere Götter gehängt, sich vor ihnen niedergeworfen und sie verehrt haben, darum hat der Herr all dieses Unglück über sie gebracht." },
      { number: 10, text: "Zwanzig Jahre hatte Salomo an den beiden Häusern, dem Tempel des Herrn und dem königlichen Palast, gebaut.", heading: "Verschiedene Nachrichten über Salomo: 9,10-28", footnotes: ["(10-28) 2 Chr 8,1-18"] },
      { number: 11, text: "Der König Hiram von Tyrus hatte ihn dabei mit Zedern- und Zypressenholz sowie mit Gold in der gewünschten Menge unterstützt. Damals trat König Salomo zwanzig Städte in der Landschaft Galiläa an Hiram ab." },
      { number: 12, text: "Als aber Hiram aus Tyrus herüberkam, um die Städte anzusehen, die Salomo ihm gegeben hatte, gefielen sie ihm nicht." },
      { number: 13, text: "Er meinte: Was sind das für Städte, die du mir gegeben hast, mein Bruder? - Man nennt sie Land Kabul bis zum heutigen Tag.", footnotes: ["Den Namen konnte man verächtlich verstehen als «wie dürres Holz» (vgl. Jes 44,19). Kabul: östlich von Akko."] },
      { number: 14, text: "Hiram hatte dem König hundertzwanzig Talente Gold gesandt." },
      { number: 15, text: "So verhielt es sich mit dem Frondienst: König Salomo hatte Fronarbeiter ausgehoben zum Bau des Tempels, seines Palastes, des Millo und der Mauern von Jerusalem, Hazor, Megiddo und Geser.", footnotes: ["2 Sam 5,9", "Zu Millo vgl. die Anmerkung zu 2 Sam 5,9."] },
      { number: 16, text: "Der Pharao, der König von Ägypten, war nämlich heraufgezogen, hatte Geser erobert und eingeäschert, die Kanaaniter, die darin wohnten, getötet und die Stadt als Brautgeschenk seiner Tochter, der Frau Salomos, gegeben.", footnotes: ["Vgl. die Anmerkung zu 3,1."] },
      { number: 17, text: "Salomo baute nun Geser wieder auf. Ferner baute er das untere Bet-Horon aus", footnotes: ["17f: Der Ausbau der Städte sicherte das Land gegen Angriffe vom Norden und von Ägypten."] },
      { number: 18, text: "sowie Baala und Tamar in der Steppe im Land (Juda)," },
      { number: 19, text: "dazu alle Vorratsstädte, die ihm gehörten, die Städte für die Wagen und ihre Mannschaft und was er sonst in Jerusalem, auf dem Libanon und im ganzen Bereich seiner Herrschaft zu bauen wünschte." },
      { number: 20, text: "Die Reste der Amoriter, Hetiter, Perisiter, Hiwiter und Jebusiter, die nicht zu den Israeliten gehörten" },
      { number: 21, text: "und von denen noch Nachkommen im Land lebten - die Israeliten hatten sie nicht ausrotten können -, hob Salomo als Fronarbeiter aus und sie blieben es bis zum heutigen Tag." },
      { number: 22, text: "Von den Israeliten aber machte Salomo niemand zum Sklaven; sie waren seine Krieger und Beamten, seine Obersten und Hauptleute, die Befehlshaber über seine Wagen und deren Mannschaft." },
      { number: 23, text: "Die Leitung der Arbeiten Salomos oblag den fünfhundertfünfzig Werkführern unter dem Befehl der Statthalter. Sie hatten die Aufsicht über die Leute, die bei den Arbeiten beschäftigt waren." },
      { number: 24, text: "Als die Tochter des Pharao aus der Davidstadt in ihr Haus hinaufgezogen war, das Salomo für sie gebaut hatte, baute er den Millo aus." },
      { number: 25, text: "Dreimal im Jahr brachte Salomo auf dem Altar, den er für den Herrn erbaut hatte, Brand- und Heilsopfer dar und ließ seine Opfergaben vor dem Herrn in Rauch aufgehen. Und er vollendete das Haus.", footnotes: ["Der König bestritt vielleicht nur den Aufwand für die Opfer, die von den Priestern dargebracht wurden. Die Dreizahl entsprach wohl den drei Hauptfesten (vgl. 2 Chr 8,13). - Opfergaben: Text korr."] },
      { number: 26, text: "König Salomo baute auch eine Flotte in Ezjon-Geber, das bei Elat an der Küste des Schilfmeers in Edom liegt." },
      { number: 27, text: "Hiram schickte seine Leute, geübte Seefahrer, mit den Leuten Salomos zu Schiff aus." },
      { number: 28, text: "Sie fuhren nach Ofir, holten von dort vierhundertzwanzig Talente Gold und brachten es dem König Salomo.", footnotes: ["Ofir wird in Südarabien oder an der gegenüberliegenden Küste Afrikas vermutet."] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Die Königin von Saba hörte vom Ruf Salomos und kam, um ihn mit Rätselfragen auf die Probe zu stellen.", heading: "Die Königin von Saba: 10,1-13", footnotes: ["(1-13) 2 Chr 9,1-12", "Saba: wahrscheinlich eine Landschaft im nördlichen Arabien, die auch in assyrischen Texten genannt wird. Andere denken an das berühmte Handelsvolk der Sabäer in Südarabien. - H hat den Zusatz «im Namen des Herrn»; er ist wahrscheinlich durch Verschreibung aus dem Namen Salomo entstanden."] },
      { number: 2, text: "Sie kam nach Jerusalem mit sehr großem Gefolge, mit Kamelen, die Balsam, eine gewaltige Menge Gold und Edelsteine trugen, trat bei Salomo ein und redete mit ihm über alles, was sie sich vorgenommen hatte." },
      { number: 3, text: "Salomo gab ihr Antwort auf alle Fragen. Es gab nichts, was dem König verborgen war und was er ihr nicht hätte sagen können." },
      { number: 4, text: "Als nun die Königin von Saba die ganze Weisheit Salomos erkannte, als sie den Palast sah, den er gebaut hatte," },
      { number: 5, text: "die Speisen auf seiner Tafel, die Sitzplätze seiner Beamten, das Aufwarten der Diener und ihre Gewänder, seine Getränke und sein Opfer, das er im Haus des Herrn darbrachte, da stockte ihr der Atem." },
      { number: 6, text: "Sie sagte zum König: Was ich in meinem Land über dich und deine Weisheit gehört habe, ist wirklich wahr." },
      { number: 7, text: "Ich wollte es nicht glauben, bis ich nun selbst gekommen bin und es mit eigenen Augen gesehen habe. Und wahrlich, nicht einmal die Hälfte hat man mir berichtet; deine Weisheit und deine Vorzüge übertreffen alles, was ich gehört habe." },
      { number: 8, text: "Glücklich sind deine Männer, glücklich diese deine Diener, die allezeit vor dir stehen und deine Weisheit hören." },
      { number: 9, text: "Gepriesen sei Jahwe, dein Gott, der an dir Gefallen fand und dich auf den Thron Israels setzte. Weil Jahwe Israel ewig liebt, hat er dich zum König bestellt, damit du Recht und Gerechtigkeit übst." },
      { number: 10, text: "Sie gab dem König hundertzwanzig Talente Gold, dazu eine sehr große Menge Balsam und Edelsteine. Niemals mehr kam so viel Balsam in das Land, wie die Königin von Saba dem König Salomo schenkte." },
      { number: 11, text: "Auch die Flotte Hirams, die Gold aus Ofir holte, brachte von dort große Mengen Almuggimholz und Edelsteine.", footnotes: ["Algummimholz oder Almuggimholz ist eine nicht näher bestimmbare wertvolle Holzart."] },
      { number: 12, text: "Der König ließ aus dem Almuggimholz Schnitzarbeiten für das Haus des Herrn und den königlichen Palast sowie Zithern und Harfen für die Sänger anfertigen. Solches Almuggimholz ist nie wieder in das Land gekommen und bis zum heutigen Tag nicht mehr gesehen worden.", footnotes: ["Die genaue Bedeutung des mit «Schnitzarbeiten» übersetzten Wortes ist unbekannt."] },
      { number: 13, text: "König Salomo gewährte der Königin von Saba alles, was sie wünschte und begehrte. Dazu beschenkte er sie reichlich, wie es nur der König Salomo vermochte. Schließlich kehrte sie mit ihrem Gefolge in ihr Land zurück." },
      { number: 14, text: "Das Gewicht des Goldes, das alljährlich bei Salomo einging, betrug sechshundertsechsundsechzig Goldtalente.", heading: "Salomos Reichtum: 10,14-29", footnotes: ["(14-28) 2 Chr 9,13-28", "Das Spiel mit der Zahl sechs lässt eine sinnbildliche Bedeutung vermuten (vgl. Offb 13,18)."] },
      { number: 15, text: "Dabei sind nicht eingerechnet die Abgaben der Kaufleute und die Einnahmen, die von den Händlern, von allen Königen Arabiens und von den Statthaltern des Landes kamen.", footnotes: ["Die Übersetzung setzt geringfügige Korrekturen voraus."] },
      { number: 16, text: "König Salomo ließ zweihundert Schilde aus gehämmertem Gold herstellen; sechshundert Goldschekel verwandte er für jeden Schild.", footnotes: ["Die Schilde dienten als Prunkstücke bei feierlichen Auftritten (vgl. 14,28)."] },
      { number: 17, text: "Dazu machte er dreihundert kleinere Schilde aus gehämmertem Gold; drei Minen Gold verwandte er für jeden. Er brachte sie in das Libanonwaldhaus." },
      { number: 18, text: "Ferner ließ der König einen großen Thron aus Elfenbein anfertigen und mit bestem Gold überziehen." },
      { number: 19, text: "Sechs Stufen führten zum Thron hinauf. An seiner Rückseite war der Kopf eines Jungstiers und zu beiden Seiten des Sitzes befanden sich Armlehnen. Zwei Löwen standen neben den Lehnen", footnotes: ["der Kopf eines Jungstiers: Text korr. nach G; H: ein runder Kopf."] },
      { number: 20, text: "und zwölf zu beiden Seiten der sechs Stufen. Dergleichen ist noch für kein Königreich geschaffen worden." },
      { number: 21, text: "Alle Trinkgefäße des Königs Salomo waren aus Gold; ebenso waren alle Geräte des Libanonwaldhauses aus bestem Gold. Silber galt in den Tagen Salomos als wertlos;" },
      { number: 22, text: "denn der König hatte eine Tarschischflotte auf dem Meer, zusammen mit den Schiffen Hirams. Einmal in drei Jahren kam die Tarschischflotte und brachte Gold, Silber, Elfenbein, Affen und Perlhühner.", footnotes: ["Tarschischflotte: Flotte von Schiffen, die den weiten Weg nach Tarschisch an der Küste Spaniens zurücklegen konnten."] },
      { number: 23, text: "So übertraf König Salomo alle Könige der Erde an Reichtum und Weisheit." },
      { number: 24, text: "Alle Welt begehrte ihn zu sehen und die Weisheit zu hören, die Gott in sein Herz gelegt hatte." },
      { number: 25, text: "Alle brachten ihm Jahr für Jahr ihre Gaben: silberne und goldene Gefäße, Gewänder, Waffen, Balsam, Pferde und Maultiere." },
      { number: 26, text: "Salomo beschaffte sich Wagen und Besatzung dazu. Er hatte vierzehnhundert Wagen und zwölftausend Mann als Besatzung und brachte sie in die Wagenstädte sowie in die Umgebung des Königs nach Jerusalem.", footnotes: ["(26-29) 2 Chr 1,14-17", "vierzehnhundert: vgl. die Anmerkung zu 5,6."] },
      { number: 27, text: "Der König machte das Silber in Jerusalem so häufig wie die Steine und die Zedern so zahlreich wie die Maulbeerfeigenbäume in der Schefela." },
      { number: 28, text: "Man bezog die Pferde für Salomo aus Ägypten und Koë; die Händler des Königs kauften sie in Koë.", footnotes: ["Statt Ägypten (hebräisch Mizrajim) ist vielleicht Muzri (Königreich an der zilizischen Küste, wie Koë) zu lesen."] },
      { number: 29, text: "Ein Wagen, der aus Ägypten kam, kostete sechshundert und ein Pferd hundertfünfzig Silberschekel. Ebenso trieb man Handel mit allen hetitischen und aramäischen Königen." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "König Salomo liebte neben der Tochter des Pharao noch viele andere ausländische Frauen: Moabiterinnen, Ammoniterinnen, Edomiterinnen, Sidonierinnen, Hetiterinnen.", heading: "Salomos Bundesbruch: 11,1-8" },
      { number: 2, text: "Es waren Frauen aus den Völkern, von denen der Herr den Israeliten gesagt hatte: Ihr dürft nicht zu ihnen gehen und sie dürfen nicht zu euch kommen; denn sie würden euer Herz ihren Göttern zuwenden. An diesen hing Salomo mit Liebe.", footnotes: ["Ex 34,16; Dtn 7,3"] },
      { number: 3, text: "Er hatte siebenhundert fürstliche Frauen und dreihundert Nebenfrauen. Sie machten sein Herz abtrünnig.", footnotes: ["Nachahmung der Haremsbräuche orientalischer Herrscher."] },
      { number: 4, text: "Als Salomo älter wurde, verführten ihn seine Frauen zur Verehrung anderer Götter, sodass er dem Herrn, seinem Gott, nicht mehr ungeteilt ergeben war wie sein Vater David." },
      { number: 5, text: "Er verehrte Astarte, die Göttin der Sidonier, und Milkom, den Götzen der Ammoniter." },
      { number: 6, text: "Er tat, was dem Herrn missfiel, und war ihm nicht so vollkommen ergeben wie sein Vater David." },
      { number: 7, text: "Damals baute Salomo auf dem Berg östlich von Jerusalem eine Kulthöhe für Kemosch, den Götzen der Moabiter, und für Milkom, den Götzen der Ammoniter.", footnotes: ["Milkom: Text korr. nach V. 5 und V. 33; H: Moloch."] },
      { number: 8, text: "Dasselbe tat er für alle seine ausländischen Frauen, die ihren Göttern Rauch- und Schlachtopfer darbrachten." },
      { number: 9, text: "Der Herr aber wurde zornig über Salomo, weil sich sein Herz von ihm, dem Gott Israels, abgewandt hatte, der ihm zweimal erschienen war", heading: "Ankündigung des Gerichts: 11,9-13", footnotes: ["3,4-15; 9,1-9"] },
      { number: 10, text: "und ihm verboten hatte, fremden Göttern zu dienen. Doch Salomo hielt sich nicht an das, was der Herr von ihm verlangt hatte." },
      { number: 11, text: "Daher sprach der Herr zu ihm: Weil es so mit dir steht, weil du meinen Bund gebrochen und die Gebote nicht befolgt hast, die ich dir gegeben habe, werde ich dir das Königreich entreißen und es deinem Knecht geben." },
      { number: 12, text: "Nur deines Vaters David wegen werde ich es nicht schon zu deinen Lebzeiten tun; erst deinem Sohn werde ich es entreißen." },
      { number: 13, text: "Doch werde ich ihm das Königtum nicht ganz entreißen; ich lasse deinem Sohn noch einen Stamm wegen meines Knechtes David und wegen Jerusalem, der Stadt, die ich erwählt habe." },
      { number: 14, text: "Der Herr ließ Salomo einen Widersacher erstehen, den Edomiter Hadad aus der königlichen Familie von Edom.", heading: "Salomos Gegner: 11,14-40" },
      { number: 15, text: "Als David die Edomiter geschlagen hatte und sein Heerführer Joab hinaufzog, um die Gefallenen zu begraben, tötete er in Edom alles, was männlich war.", footnotes: ["geschlagen hatte: Text korr. nach G."] },
      { number: 16, text: "Sechs Monate hielt sich Joab mit ganz Israel in Edom auf, bis er alles, was männlich war, ausgerottet hatte." },
      { number: 17, text: "Hadad aber konnte mit einigen Edomitern, die im Dienst seines Vaters standen, entfliehen und nach Ägypten entkommen. Er war noch sehr jung." },
      { number: 18, text: "Sie waren von Midian aufgebrochen und nach Paran gelangt. Dort nahmen sie noch andere Männer mit und kamen nach Ägypten zum Pharao, dem König von Ägypten. Dieser gab Hadad ein Haus, sorgte für seinen Unterhalt und schenkte ihm ein Grundstück." },
      { number: 19, text: "Hadad fand solche Gnade beim Pharao, dass dieser ihm die Schwester seiner Gemahlin, der Königin Tachpenes, zur Frau gab." },
      { number: 20, text: "Die Schwester der Tachpenes gebar ihm den Sohn Genubat und Tachpenes zog ihn im Haus des Pharao auf. So lebte Genubat im Haus des Pharao unter dessen Söhnen." },
      { number: 21, text: "Als nun Hadad in Ägypten hörte, dass David zu seinen Vätern entschlafen und der Heerführer Joab tot war, bat er den Pharao: Lass mich in mein Land zurückkehren!" },
      { number: 22, text: "Der Pharao entgegnete ihm: Was fehlt dir denn bei mir, dass du in dein Land zurückkehren willst? Doch Hadad antwortete: Lass mich ziehen!" },
      { number: 23, text: "Gott ließ Salomo noch einen anderen Widersacher erstehen, Reson, den Sohn Eljadas, der vor seinem Herrn, dem König Hadad-Eser von Zoba, geflohen war.", footnotes: ["23-25: David hatte Hadad-Eser von Zoba besiegt und Damaskus unterworfen (vgl. 2 Sam 8,3-6; 10,16-19). Reson gelang es, sich in Damaskus als König zu behaupten. Das mag zu Beginn der Regierung Salomos geschehen sein."] },
      { number: 24, text: "Als David viele Aramäer niedermachen ließ, sammelte Reson Männer um sich und wurde Anführer einer Freischar. Später zog er nach Damaskus, ließ sich dort nieder und wurde König in Damaskus." },
      { number: 25, text: "Er war ein Widersacher Israels, solange Salomo lebte, und vermehrte das Unglück, das von Hadad ausging; er hasste Israel und herrschte über Aram." },
      { number: 26, text: "Auch Jerobeam, der Sohn Nebats, ein Beamter Salomos, erhob sich gegen den König. Er war ein Efratiter aus Zereda; seine Mutter hieß Zerua und war Witwe.", footnotes: ["Efratiter: Angehöriger des Stammes Efraim. - Zereda: zwischen Jafo und Schilo."] },
      { number: 27, text: "Mit der Erhebung hatte es folgende Bewandtnis: Salomo baute den Millo und schloss die Lücke in der Stadt Davids, seines Vaters.", footnotes: ["27f: Die Erhebung Jerobeams steht in zeitlichem Zusammenhang mit dem Bau des Millo (vgl. die Anmerkung zu 2 Sam 5,9)."] },
      { number: 28, text: "Jerobeam war ein tüchtiger Mann, und als Salomo sah, wie der junge Mann arbeitete, machte er ihn zum Aufseher über alle Fronarbeiten des Hauses Josef." },
      { number: 29, text: "Als in jener Zeit Jerobeam einmal aus Jerusalem herauskam, begegnete ihm auf dem Weg der Prophet Ahija aus Schilo. Dieser war mit einem neuen Mantel bekleidet. Während nun beide allein auf freiem Feld waren," },
      { number: 30, text: "fasste Ahija den neuen Mantel, den er anhatte, zerriss ihn in zwölf Stücke" },
      { number: 31, text: "und sagte zu Jerobeam: Nimm dir zehn Stücke; denn so spricht der Herr, der Gott Israels: Ich nehme Salomo das Königtum weg und gebe dir zehn Stämme.", footnotes: ["31f: Der eine Stamm, der dem Sohn Salomos verbleiben soll, ist Juda. Dass Jerobeam nur zehn statt der erwarteten elf Stämme erhält, erklärt sich aus dem Fehlen Levis, der kein Stammesgebiet besaß. Da auch Simeon nicht mehr als eigener Stamm vorhanden war, sind Efraim und Manasse, die Josefstämme, getrennt mitgezählt."] },
      { number: 32, text: "Nur ein Stamm soll ihm verbleiben wegen meines Knechtes David und wegen Jerusalem, der Stadt, die ich aus allen Stämmen Israels erwählt habe." },
      { number: 33, text: "Denn er hat mich verlassen und Astarte, die Göttin der Sidonier, Kemosch, den Gott der Moabiter, und Milkom, den Gott der Ammoniter, angebetet. Er ist von meinen Wegen abgewichen und hat nicht wie sein Vater David das getan, was mir gefällt; er hat meine Gebote und Satzungen übertreten." },
      { number: 34, text: "Doch werde ich ihm das Königtum nicht ganz wegnehmen. Wegen meines Knechtes David, den ich erwählt habe und der meine Befehle und Gebote befolgt hat, lasse ich ihm die Herrschaft, solange er lebt." },
      { number: 35, text: "Erst seinem Sohn werde ich das Königreich nehmen und dir zehn Stämme geben." },
      { number: 36, text: "Seinem Sohn werde ich einen einzigen Stamm geben, damit meinem Knecht David für immer eine Leuchte vor mir bleibe in Jerusalem, der Stadt, die ich erwählt habe, um auf sie meinen Namen zu legen." },
      { number: 37, text: "Dich aber will ich nehmen, damit du ganz nach deinem Begehren herrschen kannst; du sollst König von Israel sein." },
      { number: 38, text: "Wenn du nun auf alles hörst, was ich dir gebiete, auf meinen Wegen gehst und das tust, was mir gefällt, wenn du meine Gebote und Befehle befolgst wie mein Knecht David, dann werde ich mit dir sein. Ich werde dir ein Haus bauen, das Bestand hat, wie ich es für David gebaut habe, und dir Israel übergeben.", footnotes: ["2 Sam 7,16"] },
      { number: 39, text: "Die Nachkommen Davids werde ich für den Abfall bestrafen, doch nicht für alle Zeiten." },
      { number: 40, text: "Salomo suchte nun Jerobeam zu töten. Doch dieser machte sich auf und floh nach Ägypten zu Schischak, dem König von Ägypten. Dort blieb er bis zum Tod Salomos.", footnotes: ["Schischak hatte 945 v. Chr. die 21. Dynastie, die mit Salomo verschwägert war, gestürzt."] },
      { number: 41, text: "Die übrige Geschichte Salomos, alle seine Taten und die Beweise seiner Weisheit, sind aufgezeichnet in der Chronik Salomos.", heading: "Salomos Tod: 11,41-43", footnotes: ["(41-43) 2 Chr 9,29-31"] },
      { number: 42, text: "Die Zeit, in der Salomo in Jerusalem über ganz Israel König war, betrug vierzig Jahre." },
      { number: 43, text: "Er entschlief zu seinen Vätern und wurde in der Stadt seines Vaters David begraben. Sein Sohn Rehabeam wurde König an seiner Stelle." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Rehabeam begab sich nach Sichem; denn dorthin war ganz Israel gekommen, um ihn zum König zu machen.", heading: "Die Geschichte der getrennten Reiche: Erster Teil: 12,1 - 22,54 | Der Abfall der zehn Nordstämme: 12,1-19", footnotes: ["(1-19) 2 Chr 10,1-19", "Mit «Israel» ist in den Königsbüchern bald das ungeteilte Volk, bald nur die Nordstämme, bald das nach dem Tod Salomos entstandene Nordreich gemeint."] },
      { number: 2, text: "Jerobeam, der Sohn Nebats, hörte davon, während er noch in Ägypten war, wohin er vor dem König Salomo hatte fliehen müssen; er kehrte jetzt aus Ägypten zurück.", footnotes: ["kehrte . . . zurück: Text korr. nach 2 Chr 10,2; H: blieb in Ägypten."] },
      { number: 3, text: "Man sandte zu ihm und ließ ihn rufen. So kamen also Jerobeam und die ganze Versammlung Israels (nach Sichem) und sie sagten zu Rehabeam:" },
      { number: 4, text: "Dein Vater hat uns ein hartes Joch auferlegt. Erleichtere du jetzt den harten Dienst deines Vaters und das schwere Joch, das er uns auferlegt hat. Dann wollen wir dir dienen." },
      { number: 5, text: "Er antwortete ihnen: Geht weg und kommt nach drei Tagen wieder zu mir! Als sich das Volk entfernt hatte," },
      { number: 6, text: "beriet sich König Rehabeam mit den älteren Männern, die zu Lebzeiten seines Vaters Salomo in dessen Dienst gestanden hatten. Er fragte sie: Welchen Rat gebt ihr mir? Was soll ich diesem Volk antworten?" },
      { number: 7, text: "Sie sagten zu ihm: Wenn du dich heute zum Diener dieses Volkes machst, ihnen zu Willen bist, auf sie hörst und freundlich mit ihnen redest, dann werden sie immer deine Diener sein." },
      { number: 8, text: "Doch er verwarf den Rat, den die Älteren ihm gegeben hatten, und beriet sich mit den jungen Leuten, die mit ihm groß geworden waren und jetzt in seinem Dienst standen." },
      { number: 9, text: "Er fragte sie: Welchen Rat gebt ihr mir? Was sollen wir diesem Volk antworten, das zu mir sagt: Erleichtere das Joch, das dein Vater uns auferlegt hat?" },
      { number: 10, text: "Die jungen Leute, die mit ihm groß geworden waren, sagten zu ihm: So sollst du diesem Volk antworten, das zu dir sagt: Dein Vater hat uns ein schweres Joch auferlegt; erleichtere es uns! So sollst du zu ihnen sagen: Mein kleiner Finger ist stärker als die Lenden meines Vaters." },
      { number: 11, text: "Hat mein Vater euch ein schweres Joch aufgebürdet, so werde ich es noch schwerer machen. Mein Vater hat euch mit Peitschen gezüchtigt, ich werde euch mit Skorpionen züchtigen.", footnotes: ["«Skorpione» sind hier Geißeln mit Knoten und Stacheln."] },
      { number: 12, text: "Am dritten Tag kamen Jerobeam und das ganze Volk zu Rehabeam; denn der König hatte ihnen gesagt: Kommt am dritten Tag wieder zu mir!" },
      { number: 13, text: "Der König gab nun dem Volk eine harte Antwort. Er verwarf den Rat, den die Älteren ihm erteilt hatten," },
      { number: 14, text: "und antwortete ihnen nach dem Rat der jungen Leute: Mein Vater hat euer Joch schwer gemacht. Ich werde es noch schwerer machen. Mein Vater hat euch mit Peitschen gezüchtigt, ich werde euch mit Skorpionen züchtigen." },
      { number: 15, text: "Der König hörte also nicht auf das Volk; denn der Herr hatte es so bestimmt, um das Wort wahr zu machen, das er durch Ahija von Schilo zu Jerobeam, dem Sohn Nebats, gesprochen hatte.", footnotes: ["11,29-39"] },
      { number: 16, text: "Als die Israeliten sahen, dass der König nicht auf sie hörte, gaben sie ihm zur Antwort: Welchen Anteil haben wir an David? / Wir haben keinen Erbbesitz beim Sohn Isais. / In deine Zelte, Israel! / Nun kümmere dich um dein Haus, David! So begab sich Israel zu seinen Zelten.", footnotes: ["2 Sam 20,1"] },
      { number: 17, text: "Nur über die Israeliten, die in den Städten Judas wohnten, blieb Rehabeam König.", footnotes: ["Israeliten, die in den Städten Judas wohnten: Angehörige der Nordstämme, die sich in Juda niedergelassen hatten."] },
      { number: 18, text: "Und als er den Fronaufseher Adoniram hinschickte, steinigte ihn ganz Israel zu Tode. Dem König Rehabeam gelang es, den Wagen zu besteigen und nach Jerusalem zu entkommen." },
      { number: 19, text: "So fiel Israel vom Haus David ab und ist abtrünnig bis zum heutigen Tag." },
      { number: 20, text: "Als die Israeliten erfuhren, dass Jerobeam zurückgekehrt war, ließen sie ihn zur Versammlung rufen und machten ihn zum König über ganz Israel. Der Stamm Juda allein hielt noch zum Haus David.", heading: "Jerobeam I. von Israel: 12,20-25" },
      { number: 21, text: "Rehabeam kam nach Jerusalem und versammelte das ganze Haus Juda und den Stamm Benjamin, hundertachtzigtausend auserlesene Krieger, um gegen das Haus Israel zu kämpfen und das Königtum für Rehabeam, den Sohn Salomos, zurückzugewinnen.", footnotes: ["(21-24) 2 Chr 11,1-5"] },
      { number: 22, text: "Doch das Wort Gottes erging an Schemaja, den Mann Gottes:" },
      { number: 23, text: "Sag zu Rehabeam, dem Sohn Salomos, dem König von Juda, und zum ganzen Haus Juda und Benjamin und zum übrigen Volk:" },
      { number: 24, text: "So spricht der Herr: Zieht nicht in den Krieg gegen eure Brüder, die Israeliten! Jeder kehre in sein Haus zurück; denn ich habe es so verfügt. Sie hörten auf das Wort des Herrn und kehrten heim, wie der Herr es befohlen hatte." },
      { number: 25, text: "Jerobeam baute Sichem im Gebirge Efraim aus und ließ sich dort nieder. Von Sichem zog er nach Penuël und baute auch diese Stadt aus.", footnotes: ["Sichem blieb nur kurze Zeit Hauptstadt des Nordreichs. - Penuël lag im Ostjordanland am Mittellauf des Jabbok."] },
      { number: 26, text: "Jerobeam dachte bei sich: Das Königtum könnte wieder an das Haus David fallen.", heading: "Die religiöse Trennung: 12,26-33" },
      { number: 27, text: "Wenn dieses Volk hinaufgeht, um im Haus des Herrn in Jerusalem Opfer darzubringen, wird sich sein Herz wieder seinem Herrn, dem König Rehabeam von Juda, zuwenden. Mich werden sie töten und zu Rehabeam, dem König von Juda, zurückkehren." },
      { number: 28, text: "So ging er mit sich zu Rate, ließ zwei goldene Kälber anfertigen und sagte zum Volk: Ihr seid schon zu viel nach Jerusalem hinaufgezogen. Hier ist dein Gott, Israel, der dich aus Ägypten heraufgeführt hat.", footnotes: ["Ex 32,4", "zum Volk: Text korr. nach G; H: zu ihnen."] },
      { number: 29, text: "Er stellte das eine Kalb in Bet-El auf, das andere brachte er nach Dan." },
      { number: 30, text: "Dies wurde Anlass zur Sünde. Das Volk zog sogar bis nach Dan, vor das eine Kalb.", footnotes: ["vor das eine Kalb: H unklar."] },
      { number: 31, text: "Auch errichtete er Kulthöhen und setzte Priester ein, die aus allen Teilen des Volkes stammten und nicht zu den Söhnen Levis gehörten." },
      { number: 32, text: "Für den fünfzehnten Tag des achten Monats stiftete Jerobeam ein Fest, das dem Fest in Juda entsprach. Er stieg in Bet-El zum Altar hinauf, um den Kälbern zu opfern, die er hatte anfertigen lassen. In Bet-El ließ er auch die Priester, die er für die Kulthöhen bestellt hatte, Dienst tun.", footnotes: ["Das Fest entsprach dem Laubhüttenfest."] },
      { number: 33, text: "Am fünfzehnten Tag des achten Monats stieg er zum Altar hinauf, den er in Bet-El errichtet hatte. Er hatte sich diesen Monat eigens ausgedacht und diesen Tag zu einem Fest für die Israeliten bestimmt. An ihm stieg er zum Altar hinauf, um zu opfern." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Während Jerobeam am Altar stand, um zu opfern, kam ein Gottesmann aus Juda im Auftrag des Herrn nach Bet-El.", heading: "Der ungehorsame Gottesmann: 13,1-34" },
      { number: 2, text: "Er rief im Auftrag des Herrn gegen den Altar: Altar, Altar! So spricht der Herr: Dem Haus David wird ein Sohn geboren mit Namen Joschija. Dieser wird auf dir die Höhenpriester hinschlachten, die auf dir opfern, und die Gebeine von Menschen wird man auf dir verbrennen.", footnotes: ["Über die Erfüllung der Weissagung berichtet 2 Kön 23,15-18. Der Name Joschija wurde erst aufgrund des Geschehenen eingefügt. - Die Totengebeine bewirkten die größte Verunreinigung des Altars."] },
      { number: 3, text: "Er gab an jenem Tag auch ein Zeichen und sprach: Das ist das Zeichen dafür, dass der Herr gesprochen hat: Der Altar wird zerbersten und die Asche auf ihm wird zerstreut werden." },
      { number: 4, text: "Als der König die Worte hörte, die der Gottesmann gegen den Altar in Bet-El ausrief, streckte er am Altar seine Hand aus und befahl: Nehmt ihn fest! Doch die Hand, die er gegen ihn ausgestreckt hatte, erstarrte; er konnte sie nicht mehr zurückziehen." },
      { number: 5, text: "Der Altar aber zerbarst und die Asche auf ihm wurde zerstreut, wie es der Gottesmann im Auftrag des Herrn als Zeichen verkündet hatte." },
      { number: 6, text: "Nun ergriff der König das Wort und sagte zu dem Gottesmann: Besänftige doch den Herrn, deinen Gott, und bete für mich, dass ich meine Hand wieder an mich ziehen kann. Da besänftigte der Gottesmann den Herrn und der König konnte seine Hand wieder an sich ziehen; sie war wie zuvor." },
      { number: 7, text: "Darauf sagte der König zum Gottesmann: Komm in mein Haus und stärke dich! Auch möchte ich dir ein Geschenk geben." },
      { number: 8, text: "Der Gottesmann aber erwiderte dem König: Ich werde nicht mit dir gehen, auch wenn du mir die Hälfte deines Hauses gibst. Ich werde an diesem Ort weder essen noch trinken.", footnotes: ["Der Gottesmann sollte durch sein Verhalten ausdrücken, dass zwischen Gott und dem Nordreich keine Verbindung besteht."] },
      { number: 9, text: "Denn so wurde mir durch das Wort des Herrn befohlen: Du darfst dort weder essen noch trinken; und auf dem Weg, den du gekommen bist, darfst du nicht zurückkehren." },
      { number: 10, text: "Daher schlug er einen anderen Weg ein und kehrte nicht auf dem Weg zurück, auf dem er nach Bet-El gekommen war." },
      { number: 11, text: "In Bet-El aber wohnte ein alter Prophet. Als dessen Söhne heimkamen, erzählten sie ihm alles, was der Gottesmann an diesem Tag in Bet-El getan und was er zum König gesagt hatte.", footnotes: ["Söhne: H hat Einzahl."] },
      { number: 12, text: "Darauf fragte sie der Vater: Auf welchem Weg ist er fortgezogen? Die Söhne hatten gesehen, welchen Weg der Gottesmann, der aus Juda gekommen war, eingeschlagen hatte." },
      { number: 13, text: "Da befahl er ihnen: Sattelt mir den Esel! Sie taten es und er stieg auf," },
      { number: 14, text: "ritt dem Gottesmann nach und traf ihn unter einer Eiche sitzend an. Er fragte ihn: Bist du der Gottesmann, der aus Juda gekommen ist? Jener antwortete: Ich bin es." },
      { number: 15, text: "Da bat er ihn: Komm in mein Haus und iss Brot mit mir!" },
      { number: 16, text: "Doch jener entgegnete: Ich kann nicht mit dir zurückkehren und werde an diesem Ort mit dir weder essen noch trinken." },
      { number: 17, text: "Denn im Auftrag des Herrn ist das Wort an mich ergangen: Du darfst dort weder essen noch trinken und auf dem Weg, den du gekommen bist, darfst du nicht zurückkehren." },
      { number: 18, text: "Der andere aber sagte: Auch ich bin ein Prophet wie du und ein Engel hat mir im Auftrag des Herrn befohlen: Hol ihn zurück! Er soll in dein Haus kommen, um zu essen und zu trinken. So belog er ihn." },
      { number: 19, text: "Nun kehrte er um und aß und trank im Haus des Propheten." },
      { number: 20, text: "Während sie bei Tisch saßen, erging das Wort des Herrn an den Propheten, der ihn zurückgeholt hatte," },
      { number: 21, text: "und er rief es dem Gottesmann aus Juda zu: So spricht der Herr: Weil du gegen den Befehl des Herrn gehandelt und das Verbot übertreten hast, das dir der Herr, dein Gott, auferlegt hat," },
      { number: 22, text: "weil du zurückgekehrt bist und an dem Ort gegessen und getrunken hast, an dem zu essen und zu trinken er dir verboten hatte, darum soll deine Leiche nicht in das Grab deiner Väter kommen." },
      { number: 23, text: "Nachdem der Gottesmann gegessen und getrunken hatte, sattelte der Prophet, der ihn zurückgeholt hatte, für ihn den Esel.", footnotes: ["H wörtlich: sattelte er ihm, dem Propheten, den er zurückgeholt hatte, den Esel."] },
      { number: 24, text: "Er zog fort; doch unterwegs fiel ihn ein Löwe an und tötete ihn. Seine Leiche lag hingestreckt am Weg und der Esel stand neben ihr. Auch der Löwe blieb neben der Leiche stehen.", footnotes: ["Der Auftrag Gottes ist für den Gottesmann ein Schicksal, dem er sich nicht entziehen kann. Weil er sich weigerte, das Fehlen jeglicher Gemeinschaft zwischen Gott und dem Nordreich darzustellen, muss er es in seinem Tod zum Ausdruck bringen, indem er von der Grabesgemeinschaft mit seinen Verwandten ausgeschlossen wird."] },
      { number: 25, text: "Die Leute, die vorübergingen, sahen die Leiche am Weg liegen und den Löwen neben ihr stehen. Sie kamen und erzählten es in der Stadt, in der der alte Prophet wohnte." },
      { number: 26, text: "Als der Prophet, der ihn vom Weg zurückgeholt hatte, davon hörte, sagte er: Das ist der Gottesmann, der gegen den Befehl des Herrn gehandelt hat. Darum hat ihn der Herr dem Löwen preisgegeben und dieser hat ihn zerrissen und getötet, wie es der Herr ihm angekündigt hatte." },
      { number: 27, text: "Dann befahl er seinen Söhnen: Sattelt mir den Esel! Sie taten es" },
      { number: 28, text: "und er machte sich auf und fand die Leiche am Weg hingestreckt; der Esel und der Löwe standen neben ihr. Der Löwe hatte die Leiche nicht gefressen und den Esel nicht zerrissen." },
      { number: 29, text: "Der Prophet hob nun die Leiche auf, legte sie auf den Esel und kam mit ihr in die Stadt zurück, um dem Gottesmann die Totenklage zu halten und ihn zu begraben." },
      { number: 30, text: "Er bestattete ihn in seinem eigenen Grab und man klagte um ihn: Ach, mein Bruder!" },
      { number: 31, text: "Nachdem er ihn begraben hatte, sagte er zu seinen Söhnen: Wenn ich gestorben bin, bringt mich in das Grab, in dem der Gottesmann beigesetzt ist, und legt meine Gebeine neben die seinen!" },
      { number: 32, text: "Denn das Wort, das er im Auftrag des Herrn gegen den Altar in Bet-El und gegen alle Höhentempel in den Städten Samariens gesprochen hat, wird in Erfüllung gehen." },
      { number: 33, text: "Jerobeam kehrte auch nach diesem Ereignis von seinem bösen Weg nicht um. Er bestellte weiterhin aus allen Teilen des Volkes Priester für die Kulthöhen; jeden, der es wünschte, setzte er als Höhenpriester ein." },
      { number: 34, text: "Das aber wurde dem Haus Jerobeam als Sünde angerechnet, sodass es vernichtet und vom Erdboden vertilgt wurde." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "In jener Zeit erkrankte Jerobeams Sohn Abija.", heading: "Das kranke Kind Jerobeams: 14,1-18" },
      { number: 2, text: "Jerobeam befahl daher seiner Frau: Mach dich auf, verkleide dich, damit man nicht erkennt, dass du Jerobeams Frau bist, und geh nach Schilo! Dort wohnt der Prophet Ahija, der mir verkündet hat, dass ich König dieses Volkes sein werde.", footnotes: ["11,29-39"] },
      { number: 3, text: "Nimm zehn Brote, Kuchen und einen Krug Honig mit und geh zu ihm! Er wird dir sagen, was mit dem Kind geschehen wird." },
      { number: 4, text: "Die Frau Jerobeams tat so; sie machte sich auf, ging nach Schilo und kam in das Haus Ahijas. Dieser aber konnte nicht mehr sehen; denn seine Augen waren im Alter starr geworden." },
      { number: 5, text: "Doch der Herr hatte ihm gesagt: Die Frau Jerobeams kommt, um von dir Auskunft über ihren kranken Sohn zu erhalten. Das und das sollst du zu ihr sagen. Wenn sie kommt, wird sie sich verstellen." },
      { number: 6, text: "Sobald sie sich nun der Türe näherte und Ahija das Geräusch ihrer Schritte hörte, rief er: Komm herein, Frau Jerobeams! Warum verstellst du dich? Ich habe dir eine harte Botschaft zu verkünden." },
      { number: 7, text: "Geh heim und sag zu Jerobeam: So spricht der Herr, der Gott Israels: Ich habe dich mitten aus dem Volk emporgehoben und zum Fürsten meines Volkes Israel gemacht." },
      { number: 8, text: "Ich habe das Königtum dem Haus David entrissen und dir gegeben. Du aber bist nicht wie mein Knecht David gewesen, der meine Gebote hielt, mir mit ganzem Herzen folgte und nur das tat, was mir gefällt." },
      { number: 9, text: "Du hast es schlimmer getrieben als alle, die vor dir waren, du bist hingegangen, hast dir andere Götter und Gussbilder gemacht und mich dadurch erzürnt. Mir hast du den Rücken gekehrt." },
      { number: 10, text: "Darum bringe ich Unglück über das Haus Jerobeam und rotte von ihm alles, was männlich ist, bis zum letzten Mann in Israel aus. Ich fege das Haus Jerobeam hinweg, wie man Kot hinwegfegt, bis nichts mehr vorhanden ist.", footnotes: ["16,4; 21,21-24; 2 Kön 9,8"] },
      { number: 11, text: "Wer vom Haus Jerobeam in der Stadt stirbt, den werden die Hunde fressen; und wer auf dem freien Feld stirbt, den werden die Vögel des Himmels fressen. Ja, der Herr hat gesprochen." },
      { number: 12, text: "Du aber mach dich auf und geh in dein Haus! Sobald deine Füße die Stadt betreten, wird der Knabe sterben." },
      { number: 13, text: "Ganz Israel wird ihm die Totenklage halten und ihn begraben; denn er allein wird vom Haus Jerobeam ein Grab erhalten, weil im Haus Jerobeam nur an ihm sich etwas fand, was dem Herrn, dem Gott Israels, gefiel." },
      { number: 14, text: "Der Herr aber wird sich einen König von Israel bestellen, der das Haus Jerobeam ausrottet . . .", footnotes: ["H unverständlich."] },
      { number: 15, text: "Der Herr wird Israel schlagen, dass es schwankt wie das Rohr im Wasser, und er wird Israel aus diesem guten Land, das er den Vätern gegeben hat, ausreißen und es jenseits des Stromes zerstreuen, weil sie sich Kultpfähle gemacht und ihn dadurch erzürnt haben.", footnotes: ["dass es schwankt: sinngemäß ergänzt."] },
      { number: 16, text: "Er wird Israel preisgeben wegen der Sünden, die Jerobeam begangen und zu denen er Israel verführt hat." },
      { number: 17, text: "Da stand die Frau Jerobeams auf und ging. Als sie nach Tirza kam und über die Schwelle des Hauses trat, starb der Knabe.", footnotes: ["Tirza, nördlich von Sichem, blieb bis Omri (885-874 v. Chr.) Hauptstadt des Nordreichs (vgl. die Anmerkung zu 16,24)."] },
      { number: 18, text: "Ganz Israel begrub ihn und hielt ihm die Totenklage, wie der Herr durch seinen Knecht, den Propheten Ahija, vorausgesagt hatte." },
      { number: 19, text: "Die übrige Geschichte Jerobeams, welche Kriege er führte und wie er regierte, ist aufgezeichnet in der Chronik der Könige von Israel.", heading: "Jerobeams Tod: 14,19-20" },
      { number: 20, text: "Die Regierungszeit Jerobeams betrug zweiundzwanzig Jahre. Er entschlief zu seinen Vätern und sein Sohn Nadab wurde König an seiner Stelle." },
      { number: 21, text: "Rehabeam, der Sohn Salomos, war König in Juda. Er war einundvierzig Jahre alt, als er König wurde, und regierte siebzehn Jahre in Jerusalem, der Stadt, die der Herr aus allen Stämmen Israels erwählt hatte, um seinen Namen auf sie zu legen. Seine Mutter hieß Naama und war eine Ammoniterin.", heading: "Rehabeam von Juda: 14,21-31", footnotes: ["(21-31) 2 Chr 12,9-16"] },
      { number: 22, text: "Juda aber tat, was dem Herrn missfiel. Die Sünden, die sie begingen, reizten ihn mehr als alles, was ihre Väter getan hatten." },
      { number: 23, text: "Denn auch sie errichteten Kulthöhen, Steinmale und Kultpfähle auf allen hohen Hügeln und unter jedem üppigen Baum.", footnotes: ["Pfähle aus Holz standen als Sinnbilder der Göttin Aschera oder Astarte neben oder auf den Altären. Steinmale, sog. Mazzeben, wurden zur Erinnerung an Personen (Gen 35,20) oder besondere Erlebnisse (Gen 28,18; 35,14) errichtet und waren in diesem Fall auch für den frommen Israeliten unbedenklich. Dagegen erscheinen sie zusammen mit heiligen Pfählen in heidnischen Kulten als Symbole des Baal. - Üppige Bäume konnten im wasserarmen Palästina leicht als Offenbarungsstätte einer Fruchtbarkeitsgottheit gedeutet werden."] },
      { number: 24, text: "Sogar Hierodulen gab es im Land. Die Israeliten ahmten alle Gräuel der Völker nach, die der Herr vor ihnen vertrieben hatte.", footnotes: ["Die in der Umwelt Israels geübte kultische Unzucht im Dienst heidnischer Götter war nach Dtn 23,18 verboten und wurde von den Propheten bekämpft (vgl. Jer 3,2; Hos 4,14)."] },
      { number: 25, text: "Im fünften Jahr des Königs Rehabeam zog Schischak, der König von Ägypten, gegen Jerusalem." },
      { number: 26, text: "Er raubte die Schätze des Tempels und die Schätze des königlichen Palastes und nahm alles weg, auch alle goldenen Schilde, die Salomo hatte anfertigen lassen." },
      { number: 27, text: "An deren Stelle ließ König Rehabeam bronzene anfertigen und übergab sie den Befehlshabern der Läufer, die den Eingang zum Haus des Königs bewachten.", footnotes: ["Die «Läufer» waren ursprünglich die Begleiter des Königs; später dienten sie als Palastwache."] },
      { number: 28, text: "Sooft nun der König in das Haus des Herrn ging, trugen die Läufer die Schilde und brachten sie dann wieder in ihre Wachstube zurück." },
      { number: 29, text: "Die übrige Geschichte Rehabeams und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 30, text: "Es war aber dauernd Krieg zwischen Rehabeam und Jerobeam." },
      { number: 31, text: "Rehabeam entschlief zu seinen Vätern und wurde bei seinen Vätern in der Davidstadt begraben. Seine Mutter hieß Naama und war eine Ammoniterin. Sein Sohn Abija wurde König an seiner Stelle." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Im achtzehnten Jahr des Königs Jerobeam, des Sohnes Nebats, wurde Abija König von Juda.", heading: "Abija von Juda: 15,1-8", footnotes: ["(1-8) 2 Chr 13,1-2.22-23"] },
      { number: 2, text: "Er regierte drei Jahre in Jerusalem. Seine Mutter hieß Maacha und war eine Enkelin Abschaloms.", footnotes: ["Nach 2 Chr 13,2 war seine Mutter Michaja, die Tochter Uriëls, aus Gibea."] },
      { number: 3, text: "Er verfiel allen Sünden, die sein Vater vor ihm begangen hatte, und sein Herz war nicht ungeteilt beim Herrn, seinem Gott, wie das Herz seines Vaters David." },
      { number: 4, text: "Nur Davids wegen ließ ihm der Herr, sein Gott, eine Leuchte in Jerusalem, indem er seinen Sohn als Nachfolger einsetzte und Jerusalem bestehen ließ." },
      { number: 5, text: "Denn David hatte getan, was dem Herrn gefiel. Er war sein Leben lang von keinem Gebot abgewichen, außer in der Sache Urijas, des Hetiters.", footnotes: ["2 Sam 11,2-25"] },
      { number: 6, text: "Es war aber Krieg zwischen Rehabeam und Jerobeam, solange er lebte.", footnotes: ["Der Vers wiederholt 14,30."] },
      { number: 7, text: "Die übrige Geschichte Abijas und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda. Zwischen Abija und Jerobeam herrschte Krieg." },
      { number: 8, text: "Abija entschlief zu seinen Vätern; man begrub ihn in der Davidstadt. Sein Sohn Asa wurde König an seiner Stelle." },
      { number: 9, text: "Im zwanzigsten Jahr Jerobeams, des Königs von Israel, wurde Asa König von Juda.", heading: "Asa von Juda: 15,9-24", footnotes: ["(9-12) 2 Chr 14,1-4"] },
      { number: 10, text: "Er regierte einundvierzig Jahre in Jerusalem. Seine Großmutter hieß Maacha und war eine Enkelin Abschaloms." },
      { number: 11, text: "Asa tat, was dem Herrn gefiel, wie sein Vater David." },
      { number: 12, text: "Er entfernte die Hierodulen aus dem Land und beseitigte alle Götzenbilder, die seine Väter gemacht hatten." },
      { number: 13, text: "Auch seine Großmutter Maacha enthob er ihrer Stellung als Herrin, weil sie der Aschera ein Schandbild errichtet hatte. Er ließ ihr Schandbild umhauen und im Kidrontal verbrennen.", footnotes: ["(13-15) 2 Chr 15,16-18", "Zur Stellung der Königin-Mutter vgl. die Anmerkung zu 2,19f."] },
      { number: 14, text: "Nur die Kulthöhen verschwanden nicht. Doch das Herz Asas war ungeteilt beim Herrn, solange er lebte." },
      { number: 15, text: "Er brachte auch die Weihegaben seines Vaters und seine eigenen Weihegaben in das Haus des Herrn: Silber, Gold und allerlei Geräte.", footnotes: ["seine eigenen Weihegaben: Text korr. nach G und 2 Chr 15,18."] },
      { number: 16, text: "Zwischen Asa und Bascha, dem König von Israel, herrschte Krieg, solange sie lebten.", footnotes: ["(16-22) 2 Chr 16,1-6"] },
      { number: 17, text: "Bascha, der König von Israel, zog gegen Juda und baute Rama aus, um Asa, den König von Juda, daran zu hindern, in den Krieg zu ziehen.", footnotes: ["Rama: nördlich von Jerusalem. Der Besitz der Stadt gab Bascha die Möglichkeit, den Truppen von Juda den Weg zu ihren nördlichen Grenzorten zu versperren."] },
      { number: 18, text: "Da nahm Asa alles Silber und Gold, das in den Schatzkammern des Tempels noch vorhanden war, dazu alle Schätze des königlichen Palastes, und ließ sie durch seine Abgesandten zu Ben-Hadad bringen, dem Sohn Tabrimmons und Enkel Hesjons, dem König von Aram, der in Damaskus seinen Sitz hatte. Dabei ließ er ihm sagen:" },
      { number: 19, text: "Zwischen mir und dir, zwischen meinem Vater und deinem Vater, soll ein Bündnis sein. Ich schicke dir Silber und Gold als Geschenk. Löse also dein Bündnis mit Bascha, dem König von Israel, damit er von mir abzieht." },
      { number: 20, text: "Ben-Hadad hörte auf König Asa. Er sandte seine Heerführer gegen die Städte Israels und verwüstete Ijon, Dan, Abel-Bet-Maacha, ganz Kinneret sowie das ganze Land Naftali.", footnotes: ["Die Städte liegen westlich des Oberlaufs des Jordan."] },
      { number: 21, text: "Als Bascha dies erfuhr, hörte er auf, Rama auszubauen, und zog sich nach Tirza zurück.", footnotes: ["zog sich zurück: Text korr. nach G und Vg."] },
      { number: 22, text: "König Asa aber bot ganz Juda bis zum letzten Mann auf und ließ die Steine und Balken wegnehmen, mit denen Bascha Rama ausgebaut hatte. Mit ihnen baute König Asa Geba in Benjamin und Mizpa aus." },
      { number: 23, text: "Die ganze übrige Geschichte Asas, alle seine Erfolge und Taten sowie die Städte, die er baute, sind aufgezeichnet in der Chronik der Könige von Juda. In seinem Alter erkrankte er an den Füßen." },
      { number: 24, text: "Asa entschlief zu seinen Vätern und wurde bei seinen Vätern in der Stadt seines Vaters David begraben. Sein Sohn Joschafat wurde König an seiner Stelle." },
      { number: 25, text: "Nadab, der Sohn Jerobeams, wurde König von Israel im zweiten Jahr des Königs Asa von Juda. Er regierte zwei Jahre über Israel", heading: "Nadab von Israel: 15,25-32" },
      { number: 26, text: "und tat, was dem Herrn missfiel. Er folgte den Wegen seines Vaters und hielt an der Sünde fest, zu der dieser Israel verführt hatte." },
      { number: 27, text: "Bascha, der Sohn Ahijas, aus dem Stamm Issachar, zettelte eine Verschwörung gegen ihn an und erschlug ihn bei Gibbeton, das den Philistern gehörte. Nadab belagerte nämlich damals Gibbeton mit ganz Israel." },
      { number: 28, text: "Im dritten Jahr des Königs Asa von Juda tötete ihn Bascha und wurde König an seiner Stelle." },
      { number: 29, text: "Als er aber König geworden war, beseitigte er das ganze Haus Jerobeam. Er ließ keinen am Leben, sondern rottete es völlig aus, wie der Herr durch seinen Knecht Ahija von Schilo vorausgesagt hatte.", footnotes: ["14,10f"] },
      { number: 30, text: "Das geschah wegen der Sünden, die Jerobeam begangen und zu denen er Israel verführt hatte, und wegen des Ärgers, den er dem Herrn, dem Gott Israels, bereitet hatte." },
      { number: 31, text: "Die übrige Geschichte Nadabs und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 32, text: "Zwischen Asa und Bascha, dem König von Israel, herrschte Krieg, solange sie lebten." },
      { number: 33, text: "Im dritten Jahr des Königs Asa von Juda wurde Bascha, der Sohn Ahijas, König über ganz Israel. Er regierte in Tirza vierundzwanzig Jahre", heading: "Bascha von Israel: 15,33 - 16,7" },
      { number: 34, text: "und tat, was dem Herrn missfiel. Er folgte den Wegen Jerobeams und hielt an der Sünde fest, zu der dieser Israel verführt hatte." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Da erging das Wort des Herrn an Jehu, den Sohn Hananis, gegen Bascha:" },
      { number: 2, text: "Ich habe dich aus dem Staub emporgehoben und zum Fürsten meines Volkes Israel gemacht. Du aber bist den Wegen Jerobeams gefolgt und hast mein Volk Israel zur Sünde verleitet, sodass sie mich durch ihre Sünden erzürnten." },
      { number: 3, text: "Darum werde ich Bascha und sein Haus wegfegen und sein Haus dem Haus Jerobeams, des Sohnes Nebats, gleichmachen." },
      { number: 4, text: "Wer vom Haus Bascha in der Stadt stirbt, den werden die Hunde fressen; und wer auf dem freien Feld stirbt, den werden die Vögel des Himmels fressen.", footnotes: ["14,10f; 21,21-24; 2 Kön 9,8"] },
      { number: 5, text: "Die übrige Geschichte Baschas, seine Taten und Erfolge, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 6, text: "Bascha entschlief zu seinen Vätern und wurde in Tirza begraben. Sein Sohn Ela wurde König an seiner Stelle." },
      { number: 7, text: "Das Wort des Herrn war durch den Propheten Jehu, den Sohn Hananis, an Bascha und sein Haus ergangen, weil er tat, was dem Herrn missfiel, weil er ihn durch das Werk seiner Hände erzürnte und es dem Haus Jerobeam gleichtat, aber auch weil er dieses ausgerottet hatte." },
      { number: 8, text: "Im sechsundzwanzigsten Jahr des Königs Asa von Juda wurde Ela, der Sohn Baschas, König von Israel. Er regierte in Tirza zwei Jahre.", heading: "Ela von Israel: 16,8-14" },
      { number: 9, text: "Gegen ihn zettelte sein Knecht Simri, der Befehlshaber der einen Hälfte der Kriegswagen, eine Verschwörung an. Ela war in Tirza und hatte sich im Haus Arzas, des Palastvorstehers in Tirza, völlig betrunken." },
      { number: 10, text: "Simri drang ein und erschlug ihn im siebenundzwanzigsten Jahr des Königs Asa von Juda und wurde König an seiner Stelle." },
      { number: 11, text: "Als er aber König geworden war und den Thron bestiegen hatte, beseitigte er das ganze Haus Bascha. Er ließ nichts am Leben, was männlich war, keinen Verwandten und keinen Freund." },
      { number: 12, text: "So rottete Simri das ganze Haus Bascha aus, wie es der Herr durch den Propheten Jehu über Bascha vorausgesagt hatte." },
      { number: 13, text: "Dies geschah wegen aller Sünden, die Bascha und sein Sohn Ela begangen und zu denen sie Israel verführt hatten. Durch ihre Götzen erzürnten sie den Herrn, den Gott Israels." },
      { number: 14, text: "Die übrige Geschichte Elas und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 15, text: "Im siebenundzwanzigsten Jahr des Königs Asa von Juda war Simri sieben Tage König in Tirza. Das Volk belagerte damals Gibbeton, das den Philistern gehörte.", heading: "Simri von Israel: 16,15-20", footnotes: ["15,27"] },
      { number: 16, text: "Als das Kriegsvolk während der Belagerung hörte, dass Simri eine Verschwörung angezettelt und den König erschlagen hatte, rief ganz Israel Omri, den Befehlshaber des Heeres, noch am gleichen Tag im Lager zum König von Israel aus." },
      { number: 17, text: "Omri zog nun mit ganz Israel von Gibbeton hinauf und schloss Tirza ein." },
      { number: 18, text: "Als Simri sah, dass die Stadt genommen war, zog er sich in den Wohnturm des königlichen Palastes zurück, steckte den Palast über sich in Brand und fand den Tod." },
      { number: 19, text: "Dies traf ihn zur Strafe für die Sünden, die er begangen hatte; denn er hatte getan, was dem Herrn missfiel. Er war den Wegen Jerobeams gefolgt und hatte an der Sünde festgehalten, die dieser begangen und zu der er Israel verführt hatte." },
      { number: 20, text: "Die übrige Geschichte Simris und die Verschwörung, die er angezettelt hat, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 21, text: "Damals spaltete sich das Volk Israel in zwei Parteien. Die eine Hälfte des Volkes stand hinter Tibni, dem Sohn Ginats, und machte ihn zum König; die andere Hälfte schloss sich Omri an.", heading: "Omri von Israel: 16,21-28" },
      { number: 22, text: "Doch die Anhänger Omris waren stärker als die Gefolgschaft Tibnis, des Sohnes Ginats, und als Tibni starb, wurde Omri König." },
      { number: 23, text: "Im einunddreißigsten Jahr des Königs Asa von Juda wurde Omri König von Israel. Er regierte zwölf Jahre, davon sechs in Tirza." },
      { number: 24, text: "Dann kaufte er von Schemer für zwei Talente Silber den Berg Samaria, errichtete Bauten auf ihm und nannte die Stadt, die er baute, Samaria, nach dem Namen Schemers, des Besitzers des Berges.", footnotes: ["Das wichtigste Ereignis in der Geschichte Omris ist die Gründung einer neuen Hauptstadt Samaria. Die Palastanlagen Omris (Umrisse sind zum Teil noch erkennbar) wurden erst unter Ahab zu Ende geführt. Schemer war vielleicht der Name des Berges und des Familienverbands, dem er gehörte. Nach ihm erhielt die Stadt den Namen Schomron, griechisch Samaria."] },
      { number: 25, text: "Omri tat, was dem Herrn missfiel, und trieb es noch schlimmer als alle seine Vorgänger." },
      { number: 26, text: "Er folgte ganz den Wegen Jerobeams, des Sohnes Nebats, und hielt an der Sünde fest, zu der dieser die Israeliten verführt hatte, sodass sie den Herrn, den Gott Israels, durch ihre Götzen erzürnten." },
      { number: 27, text: "Die übrige Geschichte Omris, seine Taten und die Erfolge, die er errang, sind aufgezeichnet in der Chronik der Könige von Israel.", footnotes: ["Von der Bedeutung und dem Ansehen Omris zeugt die Bezeichnung «Haus Omri» für das Nordreich in assyrischen Inschriften."] },
      { number: 28, text: "Omri entschlief zu seinen Vätern und wurde in Samaria begraben. Sein Sohn Ahab wurde König an seiner Stelle." },
      { number: 29, text: "Ahab, der Sohn Omris, wurde König von Israel im achtunddreißigsten Jahr des Königs Asa von Juda. Er regierte in Samaria zweiundzwanzig Jahre über Israel", heading: "Ahab von Israel: 16,29-34" },
      { number: 30, text: "und tat, was dem Herrn missfiel, mehr als alle seine Vorgänger." },
      { number: 31, text: "Es war noch das wenigste, dass er an den Sünden Jerobeams, des Sohnes Nebats, festhielt. Er nahm Isebel, die Tochter Etbaals, des Königs der Sidonier, zur Frau, ging hin, diente dem Baal und betete ihn an." },
      { number: 32, text: "Im Baalstempel, den er in Samaria baute, errichtete er einen Altar für den Baal." },
      { number: 33, text: "Auch stellte er einen Kultpfahl auf und tat noch vieles andere, womit er den Herrn, den Gott Israels, mehr erzürnte als alle Könige Israels vor ihm." },
      { number: 34, text: "In seinen Tagen baute Hiël aus Bet-El Jericho wieder auf. Um den Preis seines Erstgeborenen Abiram legte er die Fundamente und um den Preis seines jüngsten Sohnes Segub setzte er die Tore ein, wie es der Herr durch Josua, den Sohn Nuns, vorausgesagt hatte.", footnotes: ["Jos 6,26", "Der Tod der beiden Söhne mag auf einem Unglücksfall beruhen; man sah aber darin die Erfüllung des Fluchs."] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Der Prophet Elija aus Tischbe in Gilead sprach zu Ahab: So wahr der Herr, der Gott Israels, lebt, in dessen Dienst ich stehe: in diesen Jahren sollen weder Tau noch Regen fallen, es sei denn auf mein Wort hin.", heading: "Elija am Bach Kerit: 17,1-7", footnotes: ["der Prophet Elija aus Tischbe: Text korr. nach G."] },
      { number: 2, text: "Danach erging das Wort des Herrn an Elija:" },
      { number: 3, text: "Geh weg von hier, wende dich nach Osten und verbirg dich am Bach Kerit östlich des Jordan!" },
      { number: 4, text: "Aus dem Bach sollst du trinken und den Raben habe ich befohlen, dass sie dich dort ernähren." },
      { number: 5, text: "Elija ging weg und tat, was der Herr befohlen hatte; er begab sich zum Bach Kerit östlich des Jordan und ließ sich dort nieder." },
      { number: 6, text: "Die Raben brachten ihm Brot und Fleisch am Morgen und ebenso Brot und Fleisch am Abend und er trank aus dem Bach." },
      { number: 7, text: "Nach einiger Zeit aber vertrocknete der Bach; denn es fiel kein Regen im Land." },
      { number: 8, text: "Da erging das Wort des Herrn an Elija:", heading: "Elija in Sarepta: 17,8-24" },
      { number: 9, text: "Mach dich auf und geh nach Sarepta, das zu Sidon gehört, und bleib dort! Ich habe dort einer Witwe befohlen, dich zu versorgen." },
      { number: 10, text: "Er machte sich auf und ging nach Sarepta. Als er an das Stadttor kam, traf er dort eine Witwe, die Holz auflas. Er bat sie: Bring mir in einem Gefäß ein wenig Wasser zum Trinken!" },
      { number: 11, text: "Als sie wegging, um es zu holen, rief er ihr nach: Bring mir auch einen Bissen Brot mit!" },
      { number: 12, text: "Doch sie sagte: So wahr der Herr, dein Gott, lebt: Ich habe nichts mehr vorrätig als eine Hand voll Mehl im Topf und ein wenig Öl im Krug. Ich lese hier ein paar Stücke Holz auf und gehe dann heim, um für mich und meinen Sohn etwas zuzubereiten. Das wollen wir noch essen und dann sterben." },
      { number: 13, text: "Elija entgegnete ihr: Fürchte dich nicht! Geh heim und tu, was du gesagt hast. Nur mache zuerst für mich ein kleines Gebäck und bring es zu mir heraus! Danach kannst du für dich und deinen Sohn etwas zubereiten;" },
      { number: 14, text: "denn so spricht der Herr, der Gott Israels: Der Mehltopf wird nicht leer werden und der Ölkrug nicht versiegen bis zu dem Tag, an dem der Herr wieder Regen auf den Erdboden sendet." },
      { number: 15, text: "Sie ging und tat, was Elija gesagt hatte. So hatte sie mit ihm und ihrem Sohn viele Tage zu essen.", footnotes: ["und ihrem Sohn: Text sinngemäß korr.; H: und ihrem Haus."] },
      { number: 16, text: "Der Mehltopf wurde nicht leer und der Ölkrug versiegte nicht, wie der Herr durch Elija versprochen hatte." },
      { number: 17, text: "Nach einiger Zeit erkrankte der Sohn der Witwe, der das Haus gehörte. Die Krankheit verschlimmerte sich so, dass zuletzt kein Atem mehr in ihm war." },
      { number: 18, text: "Da sagte sie zu Elija: Was habe ich mit dir zu schaffen, Mann Gottes? Du bist nur zu mir gekommen, um an meine Sünde zu erinnern und meinem Sohn den Tod zu bringen.", footnotes: ["Die Frau fürchtet, dass sie durch die Gegenwart Elijas in ihrem Haus das Augenmerk seines Gottes auf sich und ihre Sünden gelenkt hat und dass er sie nun durch den Tod ihres Sohnes bestraft."] },
      { number: 19, text: "Er antwortete ihr: Gib mir deinen Sohn! Und er nahm ihn von ihrem Schoß, trug ihn in das Obergemach hinauf, in dem er wohnte, und legte ihn auf sein Bett." },
      { number: 20, text: "Dann rief er zum Herrn und sagte: Herr, mein Gott, willst du denn auch über die Witwe, in deren Haus ich wohne, Unheil bringen und ihren Sohn sterben lassen?" },
      { number: 21, text: "Hierauf streckte er sich dreimal über den Knaben hin, rief zum Herrn und flehte: Herr, mein Gott, lass doch das Leben in diesen Knaben zurückkehren!", footnotes: ["Das gleiche Verhalten wird von Elischa in 2 Kön 4,34 und von Paulus in Apg 20,10 berichtet."] },
      { number: 22, text: "Der Herr erhörte das Gebet Elijas. Das Leben kehrte in den Knaben zurück und er lebte wieder auf." },
      { number: 23, text: "Elija nahm ihn, brachte ihn vom Obergemach in das Haus hinab und gab ihn seiner Mutter zurück mit den Worten: Sieh, dein Sohn lebt." },
      { number: 24, text: "Da sagte die Frau zu Elija: Jetzt weiß ich, dass du ein Mann Gottes bist und dass das Wort des Herrn wirklich in deinem Mund ist." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Nach langer Zeit - es war im dritten Jahr - erging das Wort des Herrn an Elija: Geh und zeig dich dem Ahab! Ich will Regen auf die Erde senden.", heading: "Das Gottesurteil auf dem Karmel: 18,1-46" },
      { number: 2, text: "Da ging Elija hin, um sich Ahab zu zeigen. Die Hungersnot war groß in Samaria." },
      { number: 3, text: "Daher rief Ahab den Palastvorsteher Obadja. Dieser war sehr gottesfürchtig." },
      { number: 4, text: "Als Isebel die Propheten des Herrn ausrottete, hatte Obadja hundert von ihnen beiseite genommen, sie zu je fünfzig in einer Höhle verborgen und mit Brot und Wasser versorgt." },
      { number: 5, text: "Ahab befahl nun Obadja: Geh an alle Wasserquellen und Bäche im Land! Vielleicht finden wir Gras, damit wir Pferde und Maultiere am Leben erhalten können und nicht einen Teil des Viehs töten müssen." },
      { number: 6, text: "Sie teilten sich das Land, um es zu durchstreifen. Ahab ging in die eine und Obadja in die andere Richtung." },
      { number: 7, text: "Als nun Obadja unterwegs war, kam ihm Elija entgegen. Obadja erkannte ihn, warf sich vor ihm nieder und rief: Bist du es, mein Herr Elija?" },
      { number: 8, text: "Dieser antwortete: Ich bin es. Geh und melde deinem Herrn: Elija ist da." },
      { number: 9, text: "Obadja entgegnete: Was habe ich mir zu Schulden kommen lassen, dass du deinen Knecht an Ahab ausliefern und dem Tod preisgeben willst?" },
      { number: 10, text: "So wahr der Herr, dein Gott, lebt: Es gibt kein Volk und kein Reich, wo mein Herr dich nicht hätte suchen lassen. Und wenn man sagte: Er ist nicht hier, dann ließ er dieses Reich oder Volk schwören, dass man dich nicht gefunden habe.", footnotes: ["Man schrieb dem Prophetenwort eine selbstständig wirkende Macht zu. Ahab glaubt daher, dass Elija die Dürre nicht nur angekündigt, sondern sie auch herbeigeführt hat, und trachtet ihm deshalb nach dem Leben."] },
      { number: 11, text: "Und jetzt befiehlst du: Geh und melde deinem Herrn: Elija ist da." },
      { number: 12, text: "Wenn ich nun von dir weggehe, könnte ja der Geist des Herrn dich an einen Ort tragen, den ich nicht kenne. Käme ich dann zu Ahab, um dich zu melden, und könnte er dich nicht finden, so würde er mich töten. Dabei hat dein Knecht doch von Jugend auf den Herrn gefürchtet." },
      { number: 13, text: "Hat man dir denn nicht berichtet, was ich getan habe, als Isebel die Propheten des Herrn umbrachte? Ich habe doch hundert von ihnen, je fünfzig in einer Höhle, verborgen und mit Brot und Wasser versorgt." },
      { number: 14, text: "Und nun befiehlst du: Geh und melde deinem Herrn: Elija ist da. Ahab würde mich töten." },
      { number: 15, text: "Doch Elija antwortete: So wahr der Herr der Heere lebt, in dessen Dienst ich stehe: Heute noch werde ich ihm vor die Augen treten." },
      { number: 16, text: "Obadja kam zu Ahab und brachte ihm die Nachricht. Ahab ging Elija entgegen." },
      { number: 17, text: "Sobald er ihn sah, rief er aus: Bist du es, Verderber Israels?" },
      { number: 18, text: "Elija entgegnete: Nicht ich habe Israel ins Verderben gestürzt, sondern du und das Haus deines Vaters, weil ihr die Gebote des Herrn übertreten habt und den Baalen nachgelaufen seid." },
      { number: 19, text: "Doch schick jetzt Boten aus und versammle mir ganz Israel auf dem Karmel, auch die vierhundertfünfzig Propheten des Baal und die vierhundert Propheten der Aschera, die vom Tisch Isebels essen." },
      { number: 20, text: "Ahab schickte in ganz Israel umher und ließ die Propheten auf dem Karmel zusammenkommen." },
      { number: 21, text: "Und Elija trat vor das ganze Volk und rief: Wie lange noch schwankt ihr nach zwei Seiten? Wenn Jahwe der wahre Gott ist, dann folgt ihm! Wenn aber Baal es ist, dann folgt diesem! Doch das Volk gab ihm keine Antwort.", footnotes: ["Das Volk fleht sowohl zu Jahwe als auch zu Baal um Regen. Die Rede Elijas schließt aus, dass beide zugleich Gott sein können, und macht dadurch das erwartete Wunder zu einer Entscheidung für Jahwe als den alleinigen Gott."] },
      { number: 22, text: "Da sagte Elija zum Volk: Ich allein bin als Prophet des Herrn übrig geblieben; die Propheten des Baal aber sind vierhundertfünfzig." },
      { number: 23, text: "Man gebe uns zwei Stiere. Sie sollen sich einen auswählen, ihn zerteilen und auf das Holz legen, aber kein Feuer anzünden. Ich werde den andern zubereiten, auf das Holz legen und kein Feuer anzünden." },
      { number: 24, text: "Dann sollt ihr den Namen eures Gottes anrufen und ich werde den Namen des Herrn anrufen. Der Gott, der mit Feuer antwortet, ist der wahre Gott. Da rief das ganze Volk: Der Vorschlag ist gut." },
      { number: 25, text: "Nun sagte Elija zu den Propheten des Baal: Wählt ihr zuerst den einen Stier aus und bereitet ihn zu; denn ihr seid die Mehrheit. Ruft dann den Namen eures Gottes an, entzündet aber kein Feuer!" },
      { number: 26, text: "Sie nahmen den Stier, den er ihnen überließ, und bereiteten ihn zu. Dann riefen sie vom Morgen bis zum Mittag den Namen des Baal an und schrien: Baal, erhöre uns! Doch es kam kein Laut und niemand gab Antwort. Sie tanzten hüpfend um den Altar, den sie gebaut hatten.", footnotes: ["Sie tanzten hüpfend: Das hebräische Wort deutet an, dass bei dem Tanz Bewegungen um den Altar ausgeführt werden, die Not und Behinderung ausdrücken. - den sie gebaut hatten: Text korr. nach den meisten alten Übersetzungen; H: den er erbaut hatte."] },
      { number: 27, text: "Um die Mittagszeit verspottete sie Elija und sagte: Ruft lauter! Er ist doch Gott. Er könnte beschäftigt sein, könnte beiseite gegangen oder verreist sein. Vielleicht schläft er und wacht dann auf.", footnotes: ["Elija knüpft an Vorstellungen an, die den Baalspropheten bekannt waren."] },
      { number: 28, text: "Sie schrien nun mit lauter Stimme. Nach ihrem Brauch ritzten sie sich mit Schwertern und Lanzen wund, bis das Blut an ihnen herabfloss." },
      { number: 29, text: "Als der Mittag vorüber war, verfielen sie in Raserei und das dauerte bis zu der Zeit, da man das Speiseopfer darzubringen pflegt. Doch es kam kein Laut, keine Antwort, keine Erhörung.", footnotes: ["Der Hinweis auf das Speiseopfer ist eine Zeitbestimmung: am späten Nachmittag."] },
      { number: 30, text: "Nun forderte Elija das ganze Volk auf: Tretet her zu mir! Sie kamen und Elija baute den zerstörten Altar Jahwes wieder auf." },
      { number: 31, text: "Er nahm zwölf Steine, nach der Zahl der Stämme der Söhne Jakobs, zu dem der Herr gesagt hatte: Israel soll dein Name sein.", footnotes: ["Gen 32,29; 35,10"] },
      { number: 32, text: "Er fügte die Steine zu einem Altar für den Namen des Herrn, zog rings um den Altar einen Graben und grenzte eine Fläche ab, die zwei Sea Saat hätte aufnehmen können.", footnotes: ["Durch den Graben sollte der Raum um den Altar als heiliger Bezirk abgegrenzt werden."] },
      { number: 33, text: "Sodann schichtete er das Holz auf, zerteilte den Stier und legte ihn auf das Holz." },
      { number: 34, text: "Nun befahl er: Füllt vier Krüge mit Wasser und gießt es über das Brandopfer und das Holz! Hierauf sagte er: Tut es noch einmal! Und sie wiederholten es. Dann sagte er: Tut es zum dritten Mal! Und sie taten es zum dritten Mal.", footnotes: ["Das Übergießen mit Wasser bedeutet vielleicht eine Reinigung des Altars und des Opfers. Zugleich wurde so das Anzünden von Feuer erschwert und das Wunder erschien umso eindrucksvoller."] },
      { number: 35, text: "Das Wasser lief rings um den Altar. Auch den Graben füllte er mit Wasser." },
      { number: 36, text: "Zu der Zeit nun, da man das Speiseopfer darzubringen pflegt, trat der Prophet Elija an den Altar und rief: Herr, Gott Abrahams, Isaaks und Israels, heute soll man erkennen, dass du Gott bist in Israel, dass ich dein Knecht bin und all das in deinem Auftrag tue." },
      { number: 37, text: "Erhöre mich, Herr, erhöre mich! Dieses Volk soll erkennen, dass du, Herr, der wahre Gott bist und dass du sein Herz zur Umkehr wendest." },
      { number: 38, text: "Da kam das Feuer des Herrn herab und verzehrte das Brandopfer, das Holz, die Steine und die Erde. Auch das Wasser im Graben leckte es auf." },
      { number: 39, text: "Das ganze Volk sah es, warf sich auf das Angesicht nieder und rief: Jahwe ist Gott, Jahwe ist Gott!" },
      { number: 40, text: "Elija aber befahl ihnen: Ergreift die Propheten des Baal! Keiner von ihnen soll entkommen. Man ergriff sie und Elija ließ sie zum Bach Kischon hinabführen und dort töten.", footnotes: ["Elija sah in den Baalspropheten die Hauptstützen des heidnischen Kults und nützte die Stimmung des Volkes, um der Gefahr eines neuen Abfalls zu begegnen."] },
      { number: 41, text: "Dann sagte Elija zu Ahab: Geh hinauf, iss und trink; denn ich höre das Rauschen des Regens." },
      { number: 42, text: "Während Ahab wegging, um zu essen und zu trinken, stieg Elija zur Höhe des Karmel empor, kauerte sich auf den Boden nieder und legte seinen Kopf zwischen die Knie." },
      { number: 43, text: "Dann befahl er seinem Diener: Geh hinauf und schau auf das Meer hinaus! Dieser ging hinauf, schaute hinaus und meldete: Es ist nichts zu sehen. Elija befahl: Geh noch einmal hinauf! So geschah es siebenmal." },
      { number: 44, text: "Beim siebten Mal meldete der Diener: Eine Wolke, klein wie eine Menschenhand, steigt aus dem Meer herauf. Darauf sagte Elija: Geh hinauf und sag zu Ahab: Spanne an und fahr hinab, damit der Regen dich nicht aufhält." },
      { number: 45, text: "Es dauerte nicht lange, da verfinsterte sich der Himmel durch Sturm und Wolken und es fiel ein starker Regen. Ahab bestieg den Wagen und fuhr nach Jesreel." },
      { number: 46, text: "Über Elija aber kam die Hand des Herrn. Er gürtete sich und lief vor Ahab her bis dorthin, wo der Weg nach Jesreel abzweigt." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Ahab erzählte Isebel alles, was Elija getan, auch dass er alle Propheten mit dem Schwert getötet habe.", heading: "Elija am Horeb: 19,1-13a" },
      { number: 2, text: "Sie schickte einen Boten zu Elija und ließ ihm sagen: Die Götter sollen mir dies und das antun, wenn ich morgen um diese Zeit dein Leben nicht dem Leben eines jeden von ihnen gleich mache.", footnotes: ["Isebel wagte es nicht, gewaltsam gegen Elija vorzugehen. Ihre Warnung sollte ihn zur Flucht zwingen und seinen Einfluss auf das Volk verhindern."] },
      { number: 3, text: "Elija geriet in Angst, machte sich auf und ging weg, um sein Leben zu retten. Er kam nach Beerscheba in Juda und ließ dort seinen Diener zurück.", footnotes: ["geriet in Angst: Text korr. nach G; H: sah."] },
      { number: 4, text: "Er selbst ging eine Tagereise weit in die Wüste hinein. Dort setzte er sich unter einen Ginsterstrauch und wünschte sich den Tod. Er sagte: Nun ist es genug, Herr. Nimm mein Leben; denn ich bin nicht besser als meine Väter." },
      { number: 5, text: "Dann legte er sich unter den Ginsterstrauch und schlief ein. Doch ein Engel rührte ihn an und sprach: Steh auf und iss!" },
      { number: 6, text: "Als er um sich blickte, sah er neben seinem Kopf Brot, das in glühender Asche gebacken war, und einen Krug mit Wasser. Er aß und trank und legte sich wieder hin." },
      { number: 7, text: "Doch der Engel des Herrn kam zum zweiten Mal, rührte ihn an und sprach: Steh auf und iss! Sonst ist der Weg zu weit für dich." },
      { number: 8, text: "Da stand er auf, aß und trank und wanderte, durch diese Speise gestärkt, vierzig Tage und vierzig Nächte bis zum Gottesberg Horeb.", footnotes: ["Elija begab sich zum Horeb (= Sinai), auf dem das Gesetz verkündet worden war, um das er sich leidenschaftlich bemüht hatte."] },
      { number: 9, text: "Dort ging er in eine Höhle, um darin zu übernachten. Doch das Wort des Herrn erging an ihn: Was willst du hier, Elija?" },
      { number: 10, text: "Er sagte: Mit leidenschaftlichem Eifer bin ich für den Herrn, den Gott der Heere, eingetreten, weil die Israeliten deinen Bund verlassen, deine Altäre zerstört und deine Propheten mit dem Schwert getötet haben. Ich allein bin übrig geblieben und nun trachten sie auch mir nach dem Leben." },
      { number: 11, text: "Der Herr antwortete: Komm heraus und stell dich auf den Berg vor den Herrn! Da zog der Herr vorüber: Ein starker, heftiger Sturm, der die Berge zerriss und die Felsen zerbrach, ging dem Herrn voraus. Doch der Herr war nicht im Sturm. Nach dem Sturm kam ein Erdbeben. Doch der Herr war nicht im Erdbeben.", footnotes: ["11-13a: Bei der Verkündigung der Gebote erschien Gott am Sinai mit Blitz und Donner, Feuer und Erdbeben (Ex 19,16-19) und offenbarte sich so als heiliger und Furcht erregender Gott. Auch vor Elija bot er diese Kräfte auf; doch waren sie nur seine Vorboten."] },
      { number: 12, text: "Nach dem Beben kam ein Feuer. Doch der Herr war nicht im Feuer. Nach dem Feuer kam ein sanftes, leises Säuseln." },
      { number: 13, text: "Als Elija es hörte, hüllte er sein Gesicht in den Mantel, trat hinaus und stellte sich an den Eingang der Höhle.", footnotes: ["13b-18: Die Aufträge sind wohl bei anderer Gelegenheit ergangen; sie sind nicht Fortführung der vorausgehenden Offenbarung. Die ersten beiden Aufträge wurden erst durch Elischa verwirklicht und waren wohl ursprünglich an ihn gerichtet."] },
      { number: 14, text: "Da vernahm er eine Stimme, die ihm zurief: Was willst du hier, Elija? Er antwortete: Mit Leidenschaft bin ich für den Herrn, den Gott der Heere, eingetreten, weil die Israeliten deinen Bund verlassen, deine Altäre zerstört und deine Propheten mit dem Schwert getötet haben. Ich allein bin übrig geblieben und nun trachten sie auch mir nach dem Leben.", heading: "Neue Aufträge: 19,13b-18" },
      { number: 15, text: "Der Herr antwortete ihm: Geh deinen Weg durch die Wüste zurück und begib dich nach Damaskus! Bist du dort angekommen, salbe Hasaël zum König über Aram!", footnotes: ["2 Kön 8,7-15"] },
      { number: 16, text: "Jehu, den Sohn Nimschis, sollst du zum König von Israel salben und Elischa, den Sohn Schafats aus Abel-Mehola, salbe zum Propheten an deiner Stelle.", footnotes: ["2 Kön 9,1-10", "Zur Angleichung an die vorher erwähnte Salbung der Könige wird auch die Berufung Elischas als Salbung bezeichnet."] },
      { number: 17, text: "So wird es geschehen: Wer dem Schwert Hasaëls entrinnt, den wird Jehu töten. Und wer dem Schwert Jehus entrinnt, den wird Elischa töten." },
      { number: 18, text: "Ich werde in Israel siebentausend übrig lassen, alle, deren Knie sich vor dem Baal nicht gebeugt und deren Mund ihn nicht geküsst hat." },
      { number: 19, text: "Als Elija von dort weggegangen war, traf er Elischa, den Sohn Schafats. Er war gerade mit zwölf Gespannen am Pflügen und er selbst pflügte mit dem zwölften. Im Vorbeigehen warf Elija seinen Mantel über ihn.", heading: "Die Berufung Elischas: 19,19-21" },
      { number: 20, text: "Sogleich verließ Elischa die Rinder, eilte Elija nach und bat ihn: Lass mich noch meinem Vater und meiner Mutter den Abschiedskuss geben; dann werde ich dir folgen. Elija antwortete: Geh, aber komm dann zurück! Bedenke, was ich an dir getan habe.", footnotes: ["Der Vorgang erinnert an die Berufung der Jünger Jesu (vgl. Mt 4,19-22). Die volle Ausstattung Elischas zum Propheten erfolgte erst bei der Entrückung Elijas (2 Kön 2)."] },
      { number: 21, text: "Elischa ging von ihm weg, nahm seine zwei Rinder und schlachtete sie. Mit dem Joch der Rinder kochte er das Fleisch und setzte es den Leuten zum Essen vor. Dann stand er auf, folgte Elija und trat in seinen Dienst." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Ben-Hadad, der König von Aram, sammelte sein ganzes Heer. In seinem Gefolge waren zweiunddreißig Könige mit Pferden und Wagen. Er zog herauf, belagerte Samaria und bestürmte es.", heading: "Aramäerkriege: 20,1-43", footnotes: ["1-43: In diesem Kapitel sind Verhältnisse vorausgesetzt, die unter Ahab nicht bestanden; die Vorgänge fallen in die Zeit des Königs Joasch von Israel, vgl. 2 Kön 13,3.7.17-19.24f. Sie werden hier auf Ahab übertragen. Gemeint ist Ben-Hadad III., der Zeitgenosse des Joasch (vgl. 2 Kön 13,24f). Unter Ahab regierte in Damaskus Hadad-Eser."] },
      { number: 2, text: "Dann schickte er Boten in die Stadt zu Ahab, dem König von Israel,", footnotes: ["Der Name Ahab findet sich nur noch in V. 13f."] },
      { number: 3, text: "und ließ ihm sagen: So spricht Ben-Hadad: Dein Silber und dein Gold gehören mir. Auch deine Frauen und deine edlen Söhne gehören mir." },
      { number: 4, text: "Der König von Israel antwortete: Ganz wie du sagst, mein Herr und König, soll es geschehen. Ich gehöre dir mit allem, was ich besitze." },
      { number: 5, text: "Aber die Boten kamen ein zweites Mal und meldeten: So spricht Ben-Hadad: Ich habe dir sagen lassen, dass du mir dein Silber und Gold, deine Frauen und Söhne ausliefern sollst." },
      { number: 6, text: "Doch morgen um diese Zeit werde ich meine Leute zu dir schicken, damit sie dein Haus und auch die Häuser deiner Diener durchsuchen und alles, was wertvoll ist, nehmen und forttragen." },
      { number: 7, text: "Da rief der König von Israel alle Ältesten des Landes zu sich und sagte: Seht und erkennt, dass dieser Mann Böses im Sinn hat. Er hat von mir meine Frauen und Söhne, mein Silber und Gold gefordert, und ich habe ihm nichts verweigert.", footnotes: ["Zur Rettung der Stadt war der König zu den größten persönlichen Opfern bereit. Über das Vermögen der Untertanen wollte er nicht ohne deren Zustimmung verfügen."] },
      { number: 8, text: "Da antworteten ihm alle Ältesten und das ganze Volk: Gehorch ihm nicht und willige nicht ein!" },
      { number: 9, text: "Er gab daher den Abgesandten Ben-Hadads den Bescheid: Sagt zu meinem Herrn, dem König: Alles, was du zuerst von deinem Knecht verlangt hast, will ich tun; doch diese Forderung kann ich nicht erfüllen. Die Boten zogen ab und meldeten es ihrem Herrn." },
      { number: 10, text: "Darauf sandte Ben-Hadad abermals Boten (zu Ahab) und ließ ihm sagen: Die Götter sollen mir dies und das antun, wenn der Staub Samarias ausreicht, um allen Leuten in meinem Gefolge die Hand zu füllen." },
      { number: 11, text: "Doch der König von Israel antwortete: Sagt zu ihm: Wer den Gürtel anlegt, soll sich nicht rühmen wie einer, der ihn bereits ablegt.", footnotes: ["In einer sprichwörtlichen Redensart wird gesagt, dass man sich eines Erfolgs erst rühmen soll, wenn man ihn erlangt hat."] },
      { number: 12, text: "Als Ben-Hadad diese Meldung empfing, hielt er gerade in den Zelten ein Gelage mit den Königen. Er befahl seinen Leuten anzugreifen und diese begannen den Sturm auf die Stadt." },
      { number: 13, text: "Doch ein Prophet kam zu Ahab, dem König von Israel, und sagte: So spricht der Herr: Siehst du diese gewaltige Menge? Ich gebe sie heute in deine Hand und du wirst erkennen, dass ich der Herr bin." },
      { number: 14, text: "Ahab fragte: Durch wen? Er erwiderte: So spricht der Herr: Durch die Truppe der Provinzstatthalter. Als dann der König weiter wissen wollte, wer den Kampf eröffnen solle, sprach er: Du selbst." },
      { number: 15, text: "Daraufhin musterte er die Truppe der Provinzstatthalter; es waren zweihundertzweiunddreißig Mann. Nach ihnen musterte er das ganze Kriegsvolk, die Gesamtheit der Israeliten; es waren siebentausend Mann." },
      { number: 16, text: "Als sie um die Mittagszeit ausrückten, zechte Ben-Hadad mit den zweiunddreißig Königen, seinen Bundesgenossen, in den Zelten." },
      { number: 17, text: "Die Truppe der Provinzstatthalter rückte zuerst aus. Ben-Hadad hatte Beobachter ausgesandt und sie meldeten ihm, dass Leute aus Samaria herauskommen." },
      { number: 18, text: "Er befahl: Wenn sie in friedlicher Absicht kommen, ergreift sie lebendig! Wenn sie zum Kampf ausrücken, ergreift sie ebenfalls lebendig!" },
      { number: 19, text: "Sobald nun die Truppe der Statthalter und das Heer, das ihr folgte, aus der Stadt ausgerückt waren," },
      { number: 20, text: "gelang es jedem einzelnen, seinen Gegner zu überwinden. Die Aramäer mussten fliehen und die Israeliten setzten ihnen nach. Ben-Hadad, der König von Aram, konnte zu Pferd entkommen, ebenso einige Wagenkämpfer." },
      { number: 21, text: "Als dann auch der König von Israel ausrückte, vernichtete er die Pferde und Kriegswagen und bereitete den Aramäern eine schwere Niederlage." },
      { number: 22, text: "Da erschien der Prophet wieder beim König von Israel und sagte zu ihm: Sammle deine Kräfte und überleg dir gut, was du zu tun hast; denn um die Jahreswende wird der König von Aram abermals gegen dich ziehen." },
      { number: 23, text: "Zum König von Aram hatten nämlich seine Ratgeber gesagt: Ihr Gott ist ein Gott der Berge; darum waren sie uns überlegen. Wenn wir aber in der Ebene mit ihnen kämpfen, dann werden wir sie bestimmt besiegen." },
      { number: 24, text: "Tu also folgendes: Entfern die Könige aus ihrer Stellung und ersetze sie durch Statthalter!" },
      { number: 25, text: "Dann sammle ein ebenso großes Heer, wie du es verloren hast, und ebenso viele Pferde und Kriegswagen, wie du zuvor hattest. Wir werden in der Ebene mit ihnen kämpfen und sie gewiss besiegen. Ben-Hadad befolgte ihren Rat." },
      { number: 26, text: "Um die Jahreswende musterte er die Aramäer und rückte zum Krieg gegen Israel bis Afek vor.", footnotes: ["Afek liegt östlich vom See Gennesaret; vielleicht ist ein Ort in der Nähe von Samaria gemeint."] },
      { number: 27, text: "Auch die Israeliten wurden gemustert und mit Lebensmitteln versehen. Sie zogen den Aramäern entgegen und lagerten ihnen gegenüber wie ein paar kleine Ziegenherden; die Aramäer aber füllten die ganze Gegend." },
      { number: 28, text: "Nun trat der Gottesmann zum König von Israel hin und sagte: So spricht der Herr: Weil die Aramäer sagen, dass der Herr ein Gott der Berge und nicht ein Gott der Ebenen sei, gebe ich diese ganze gewaltige Menge in deine Hand; und ihr werdet erkennen, dass ich der Herr bin." },
      { number: 29, text: "Sie lagen dann sieben Tage einander gegenüber. Am siebten Tag kam es zur Schlacht und die Israeliten töteten vom aramäischen Fußvolk hunderttausend Mann an einem Tag." },
      { number: 30, text: "Der Rest floh in die Stadt Afek; doch die Mauer stürzte über den siebenundzwanzigtausend Mann, die übrig geblieben waren, zusammen. Auch Ben-Hadad war in die Stadt geflohen und irrte von einem Gemach in das andere." },
      { number: 31, text: "Da sagten seine Ratgeber zu ihm: Wir haben gehört, dass die Könige des Hauses Israel milde Könige sind. Wir wollen daher Trauergewänder anlegen und mit Stricken um den Hals zum König von Israel hinausgehen. Vielleicht schenkt er dir das Leben." },
      { number: 32, text: "Sie legten also Trauergewänder an und mit Stricken um den Hals kamen sie zum König von Israel. Sie flehten ihn an: Dein Knecht Ben-Hadad bittet dich, ihm das Leben zu schenken. Er antwortete: Lebt er noch? Er ist mein Bruder." },
      { number: 33, text: "Die Männer nahmen das Wort als gutes Zeichen, gingen sogleich darauf ein und sagten: Ben-Hadad ist dein Bruder. Dann befahl der König von Israel: Geht, bringt ihn zu mir! Als Ben-Hadad kam, nahm er ihn zu sich auf seinen Wagen.", footnotes: ["als gutes Zeichen: Text sinngemäß korr."] },
      { number: 34, text: "Da erklärte Ben-Hadad: Die Städte, die mein Vater deinem Vater weggenommen hat, werde ich zurückgeben und in Damaskus magst du dir Handelsniederlassungen errichten, wie mein Vater es in Samaria getan hat. Der König von Israel erwiderte: Auf diese Abmachung hin werde ich dich freilassen. So schloss er mit ihm einen Vertrag und ließ ihn frei.", footnotes: ["Vgl. 2 Kön 13,25. Der König von Israel war zum Frieden bereit, den das Land dringend benötigte. Die Hinrichtung Ben-Hadads hätte seinen Nachfolger nur zur Fortsetzung des Kriegs veranlasst."] },
      { number: 35, text: "Einer von den Prophetenjüngern sprach im Auftrag des Herrn zu seinem Gefährten: Schlag mich! Als dieser sich weigerte, ihn zu schlagen," },
      { number: 36, text: "sagte er zu ihm: Weil du der Stimme des Herrn nicht gehorcht hast, wird dich ein Löwe töten, sobald du von mir weggegangen bist. Der Mann hatte sich kaum von ihm entfernt, als ihn ein Löwe anfiel und tötete.", footnotes: ["13,24"] },
      { number: 37, text: "Hierauf traf der Prophet einen andern und befahl ihm: Schlag mich! Dieser schlug auf ihn ein und verwundete ihn.", footnotes: ["Um beim König mit seiner Erzählung Glauben zu finden, will der Prophet als Verwundeter erscheinen, der vom Schlachtfeld kommt."] },
      { number: 38, text: "Sogleich begab sich der Prophet zum König und stellte sich ihm in den Weg. Durch eine Binde über den Augen hatte er sich unkenntlich gemacht." },
      { number: 39, text: "Als der König vorbeikam, rief er ihn und sagte: Dein Knecht ist in den Kampf gezogen. Da kam jemand herbei, brachte mir einen Gefangenen und befahl: Bewach diesen Mann! Wenn er entkommt, dann musst du es mit deinem Leben büßen, oder du musst ein Talent Silber bezahlen.", footnotes: ["39f: Der König sollte gezwungen werden, sich selbst das Urteil zu sprechen (vgl. 2 Sam 12,1-7)."] },
      { number: 40, text: "Während nun dein Knecht da und dort zu tun hatte, konnte der Gefangene entkommen. Da sagte der König von Israel: Du hast dir dein Urteil selbst gesprochen." },
      { number: 41, text: "Sogleich nahm der Prophet die Binde von den Augen und der König von Israel erkannte ihn als einen von den Propheten." },
      { number: 42, text: "Dieser aber sagte zu ihm: So spricht der Herr: Weil du den Mann, dessen Verderben ich wollte, aus deiner Hand entlassen hast, muss dein Leben für sein Leben, dein Volk für sein Volk einstehen." },
      { number: 43, text: "Missmutig und verdrossen ging der König von Israel nach Hause und kam nach Samaria." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Danach trug sich Folgendes zu. Nabot aus Jesreel hatte einen Weinberg in Jesreel neben dem Palast Ahabs, des Königs von Samarien.", heading: "Nabots Weinberg: 21,1-29", footnotes: ["Jesreel war neben Samaria Residenzstadt."] },
      { number: 2, text: "Ahab verhandelte mit Nabot und schlug ihm vor: Gib mir deinen Weinberg! Er soll mir als Gemüsegarten dienen; denn er liegt nahe bei meinem Haus. Ich will dir dafür einen besseren Weinberg geben. Wenn es dir aber lieber ist, bezahle ich dir den Kaufpreis in Geld." },
      { number: 3, text: "Doch Nabot erwiderte: Der Herr bewahre mich davor, dass ich dir das Erbe meiner Väter überlasse." },
      { number: 4, text: "Darauf kehrte Ahab in sein Haus zurück. Er war missmutig und verdrossen, weil Nabot aus Jesreel zu ihm gesagt hatte: Ich werde dir das Erbe meiner Väter nicht überlassen. Er legte sich auf sein Bett, wandte das Gesicht zur Wand und wollte nicht essen." },
      { number: 5, text: "Seine Frau Isebel kam zu ihm herein und fragte: Warum bist du missmutig und willst nicht essen?" },
      { number: 6, text: "Er erzählte ihr: Ich habe mit Nabot aus Jesreel verhandelt und ihm gesagt: Verkauf mir deinen Weinberg für Geld, oder wenn es dir lieber ist, gebe ich dir einen anderen dafür. Doch er hat geantwortet: Ich werde dir meinen Weinberg nicht geben." },
      { number: 7, text: "Da sagte seine Frau Isebel zu ihm: Du bist doch jetzt König in Israel. Steh auf, iss und sei guter Dinge! Ich werde dir den Weinberg Nabots aus Jesreel verschaffen." },
      { number: 8, text: "Sie schrieb Briefe im Namen Ahabs, versah sie mit seinem Siegel und schickte sie an die Ältesten und Vornehmen, die mit Nabot zusammen in der Stadt wohnten." },
      { number: 9, text: "In den Briefen schrieb sie: Ruft ein Fasten aus und lasst Nabot oben vor allem Volk Platz nehmen!", footnotes: ["Ein allgemeines Fasten konnte bei Unglück und Trauer zur Unterstützung einer Bitte oder zur Aufdeckung einer Schuld angeordnet werden. Der Ehrenplatz, den man Nabot anbot, sollte seine Schuld um so verwerflicher erscheinen lassen."] },
      { number: 10, text: "Setzt ihm aber zwei nichtswürdige Männer gegenüber! Sie sollen gegen ihn als Zeugen auftreten und sagen: Du hast Gott und den König gelästert. Führt ihn dann hinaus und steinigt ihn zu Tode!", footnotes: ["Lev 24,16", "lästern: wörtlich: segnen. Nabot soll beschuldigt werden, Fluchworte über den König ausgesprochen zu haben (vgl. Ex 22,27)."] },
      { number: 11, text: "Die Männer der Stadt, die Ältesten und Vornehmen, die mit ihm zusammen in der Stadt wohnten, taten, was Isebel ihnen geboten hatte, was in den Briefen stand, die sie ihnen gesandt hatte." },
      { number: 12, text: "Sie riefen ein Fasten aus und ließen Nabot oben vor allem Volk Platz nehmen." },
      { number: 13, text: "Es kamen aber auch die beiden nichtswürdigen Männer und setzten sich ihm gegenüber. Sie standen vor dem Volk als Zeugen gegen Nabot auf und sagten: Nabot hat Gott und den König gelästert. Sogleich führte man ihn aus der Stadt hinaus und steinigte ihn zu Tode." },
      { number: 14, text: "Darauf ließen sie Isebel melden: Nabot wurde gesteinigt und ist tot." },
      { number: 15, text: "Sobald sie hörte, dass Nabot gesteinigt wurde und tot war, sagte sie zu Ahab: Auf, nimm den Weinberg Nabots aus Jesreel in Besitz, den er dir für Geld nicht verkaufen wollte; denn Nabot lebt nicht mehr; er ist tot." },
      { number: 16, text: "Als Ahab hörte, dass Nabot tot war, stand er auf und ging zum Weinberg Nabots aus Jesreel hinab, um von ihm Besitz zu ergreifen." },
      { number: 17, text: "Da erging das Wort des Herrn an Elija aus Tischbe:" },
      { number: 18, text: "Mach dich auf und geh Ahab, dem König von Israel, entgegen, der in Samaria seinen Wohnsitz hat. Er ist zum Weinberg Nabots hinabgegangen, um von ihm Besitz zu ergreifen." },
      { number: 19, text: "Sag ihm: So spricht der Herr: Durch einen Mord bist du Erbe geworden? Weiter sag ihm: So spricht der Herr: An der Stelle, wo die Hunde das Blut Nabots geleckt haben, werden Hunde auch dein Blut lecken.", footnotes: ["Die Drohung ging am Sohn Ahabs in Erfüllung (vgl. 2 Kön 9,25f)."] },
      { number: 20, text: "Ahab sagte zu Elija: Hast du mich gefunden, mein Feind? Er erwiderte: Ich habe dich gefunden. Weil du dich hergabst, das zu tun, was dem Herrn missfällt," },
      { number: 21, text: "werde ich Unheil über dich bringen. Ich werde dein Geschlecht hinwegfegen und von Ahabs Geschlecht alles, was männlich ist, bis zum letzten Mann in Israel ausrotten.", footnotes: ["14,10; 16,4; 2 Kön 9,8"] },
      { number: 22, text: "Weil du mich zum Zorn gereizt und Israel zur Sünde verführt hast, werde ich mit deinem Haus verfahren wie mit dem Haus Jerobeams, des Sohnes Nebats, und mit dem Haus Baschas, des Sohnes Ahijas." },
      { number: 23, text: "Und über Isebel verkündet der Herr: Die Hunde werden Isebel an der Mauer von Jesreel auffressen.", footnotes: ["2 Kön 9,36"] },
      { number: 24, text: "Wer von der Familie Ahabs in der Stadt stirbt, den werden die Hunde fressen, und wer auf dem freien Feld stirbt, den werden die Vögel des Himmels fressen." },
      { number: 25, text: "Es gab in der Tat niemand, der sich wie Ahab hergab zu tun, was dem Herrn missfiel, da seine Frau Isebel ihn verführte." },
      { number: 26, text: "Sein Tun war überaus verwerflich; er lief den Götzen nach und folgte den Gebräuchen der Amoriter, die der Herr vor den Israeliten vertrieben hatte." },
      { number: 27, text: "Als Ahab diese Drohungen hörte, zerriss er seine Kleider, trug ein Bußgewand auf dem bloßen Leib, fastete, schlief im Bußgewand und ging bedrückt umher." },
      { number: 28, text: "Da erging das Wort des Herrn an Elija aus Tischbe:" },
      { number: 29, text: "Hast du gesehen, wie Ahab sich vor mir gedemütigt hat? Weil er sich vor mir gedemütigt hat, will ich das Unglück nicht schon in seinen Tagen kommen lassen. Erst in den Tagen seines Sohnes werde ich das Unheil über sein Haus bringen." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Drei Jahre hatte das Land Ruhe und gab es keinen Krieg zwischen Aram und Israel.", heading: "Der Krieg mit den Aramäern, Ahabs Tod: 22,1-40" },
      { number: 2, text: "Als im dritten Jahr Joschafat, der König von Juda, zum König von Israel kam,", footnotes: ["Joram, der Sohn Joschafats, war mit Atalja, der Tochter Ahabs, verheiratet (vgl. 2 Kön 8,18)."] },
      { number: 3, text: "sagte der König von Israel zu seinen Beamten: Ihr wisst doch, dass Ramot-Gilead uns gehört. Wir aber zögern, es dem König von Aram zu entreißen.", footnotes: ["Ramot-Gilead, unter Salomo Sitz eines Statthalters (4,13), war an Damaskus gefallen."] },
      { number: 4, text: "Und er fragte Joschafat: Würdest du mit mir gegen Ramot-Gilead in den Krieg ziehen? Dieser antwortete dem König von Israel: Ich ziehe mit dir, mein Volk mit deinem Volk, meine Pferde mit deinen Pferden.", footnotes: ["2 Kön 3,7; (4-35) 2 Chr 18,1-34"] },
      { number: 5, text: "Joschafat bat aber den König von Israel: Befrag doch zuvor den Herrn!" },
      { number: 6, text: "Da versammelte der König von Israel die Propheten, gegen vierhundert Mann, und fragte sie: Soll ich gegen Ramot-Gilead zu Felde ziehen, oder soll ich es lassen? Sie gaben den Bescheid: Zieh hinauf! Der Herr gibt die Stadt in die Hand des Königs.", footnotes: ["Gemeint sind Propheten, die im Dienst des Königs standen und die nach dem Wunsch des Königs redeten."] },
      { number: 7, text: "Doch Joschafat sagte: Ist hier sonst kein Prophet des Herrn, den wir befragen könnten?" },
      { number: 8, text: "Der König von Israel antwortete Joschafat: Es ist noch einer da, durch den wir den Herrn befragen könnten. Doch ich hasse ihn; denn er weissagt mir nie Gutes, sondern immer nur Schlimmes. Es ist Micha, der Sohn Jimlas. Joschafat erwiderte: Der König sage das nicht." },
      { number: 9, text: "Da rief der König von Israel einen Hofbeamten herbei und befahl ihm, unverzüglich Micha, den Sohn Jimlas, zu holen." },
      { number: 10, text: "Der König von Israel und Joschafat, der König von Juda, saßen in königlichen Gewändern auf ihren Thronen. Sie befanden sich auf der Tenne beim Tor Samarias und alle Propheten weissagten vor ihnen." },
      { number: 11, text: "Zidkija, der Sohn Kenaanas, hatte sich eiserne Hörner gemacht und rief: So spricht der Herr: Mit diesen wirst du die Aramäer niederstoßen, bis du sie vernichtet hast." },
      { number: 12, text: "Alle Propheten weissagten in gleicher Weise und riefen: Zieh nach Ramot-Gilead und sei erfolgreich; der Herr gibt die Stadt in die Hand des Königs." },
      { number: 13, text: "Der Bote aber, der Micha holen sollte, redete ihm zu: Die Worte der Propheten waren ohne Ausnahme günstig für den König. Mögen deine Worte ihren Worten gleichen. Sag daher Gutes an!" },
      { number: 14, text: "Doch Micha erwiderte: So wahr der Herr lebt: Nur was der Herr mir sagt, werde ich sagen." },
      { number: 15, text: "Als er zum König kam, fragte ihn dieser: Micha, sollen wir gegen Ramot-Gilead zu Felde ziehen, oder sollen wir es lassen? Micha antwortete: Zieh hinauf und sei erfolgreich! Der Herr gibt die Stadt in die Hand des Königs." },
      { number: 16, text: "Doch der König entgegnete: Wie oft muss ich dich beschwören, mir im Namen des Herrn nur die Wahrheit zu sagen?", footnotes: ["Ahab erkennt, dass Micha ironisch spricht."] },
      { number: 17, text: "Da sagte Micha: Ich sah ganz Israel über die Berge zerstreut wie Schafe, die keinen Hirten haben. Und der Herr sagte: Sie haben keine Herren mehr. So gehe jeder in Frieden nach Hause." },
      { number: 18, text: "Da wandte sich der König von Israel an Joschafat: Habe ich es dir nicht gesagt? Er weissagt mir nie Gutes, sondern immer nur Schlimmes." },
      { number: 19, text: "Micha aber fuhr fort: Darum - höre das Wort des Herrn: Ich sah den Herrn auf seinem Thron sitzen; das ganze Heer des Himmels stand zu seiner Rechten und seiner Linken.", footnotes: ["Jes 6,1-3", "19-23: Der Entschluss zum Unternehmen gegen Ramot-Gilead, bei dem der König den Tod finden sollte, wird als Rat eines von Gott unterschiedenen Geistes, des Lügengeistes, hingestellt. In ähnlicher Weise wird die Anregung zu der für David verhängnisvollen Volkszählung, die in 2 Sam 24,1 dem zürnenden Gott zugeschrieben wird, in 1 Chr 21,1 dem Satan zugeschrieben."] },
      { number: 20, text: "Und der Herr fragte: Wer will Ahab betören, sodass er nach Ramot-Gilead hinaufzieht und dort fällt? Da hatte der eine diesen, der andere jenen Vorschlag." },
      { number: 21, text: "Zuletzt trat der Geist vor, stellte sich vor den Herrn und sagte: Ich werde ihn betören. Der Herr fragte ihn: Auf welche Weise?" },
      { number: 22, text: "Er gab zur Antwort: Ich werde mich aufmachen und zu einem Lügengeist im Mund all seiner Propheten werden. Da sagte der Herr: Du wirst ihn betören; du vermagst es. Geh und tu es!" },
      { number: 23, text: "So hat der Herr jetzt einen Geist der Lüge in den Mund all deiner Propheten gelegt; denn er hat über dich Unheil beschlossen." },
      { number: 24, text: "Da trat Zidkija, der Sohn Kenaanas, zu Micha, schlug ihn ins Gesicht und rief: Wie, sollte denn der Geist des Herrn von mir gewichen sein, um mit dir zu reden?" },
      { number: 25, text: "Micha erwiderte: Du wirst es an jenem Tag erfahren, an dem du von einem Gemach in das andere eilst, um dich zu verstecken." },
      { number: 26, text: "Der König von Israel aber gab den Befehl: Nehmt Micha fest, führt ihn zum Stadtobersten Amon und zum Prinzen Joasch" },
      { number: 27, text: "und meldet: So spricht der König: Werft diesen Mann ins Gefängnis, und haltet ihn streng bei Brot und Wasser, bis ich wohlbehalten zurückkomme." },
      { number: 28, text: "Doch Micha erwiderte: Wenn du wohlbehalten zurückkommst, dann hat der Herr nicht durch mich geredet. [Und er sagte: Hört, alle ihr Völker!]", footnotes: ["Zusatz aus Mi 1,2, veranlasst durch die Gleichsetzung des hier genannten Micha mit dem Micha des Prophetenbuches."] },
      { number: 29, text: "Darauf zog der König von Israel mit Joschafat, dem König von Juda, gegen Ramot-Gilead." },
      { number: 30, text: "Der König von Israel sagte zu Joschafat: Ich will mich verkleiden und so in den Kampf ziehen. Du aber behalte deine Gewänder an! So ging der König von Israel verkleidet in den Kampf." },
      { number: 31, text: "Der König von Aram hatte aber den zweiunddreißig Obersten seiner Kriegswagen befohlen: Greift niemanden an, er sei hohen oder niederen Ranges, außer den König von Israel!" },
      { number: 32, text: "Als daher die Obersten der Kriegswagen Joschafat erblickten und ihn für den König von Israel hielten, stürmten sie auf ihn ein, sodass er um Hilfe schrie." },
      { number: 33, text: "Doch als sie sahen, dass er nicht der König von Israel war, ließen sie von ihm ab." },
      { number: 34, text: "Ein Mann aber spannte aufs Geratewohl seinen Bogen und traf den König von Israel zwischen Panzer und Leibgurt. Dieser befahl daher seinem Wagenlenker: Wende um und bring mich aus der Schlacht; denn ich bin verwundet." },
      { number: 35, text: "Da aber die Schlacht an jenem Tag heftig wurde, blieb der König im Kampf gegen die Aramäer aufrecht im Wagen stehen. Am Abend starb er. Das Blut der Wunde war in das Innere des Wagens geflossen." },
      { number: 36, text: "Bei Sonnenuntergang ließ man im Lager ausrufen: Jeder kehre in seine Stadt, in sein Land zurück!" },
      { number: 37, text: "So starb der König; man brachte ihn nach Samaria und begrub ihn dort." },
      { number: 38, text: "Als man im Teich von Samaria den Wagen ausspülte, leckten Hunde sein Blut, und Dirnen wuschen sich darin, nach dem Wort, das der Herr gesprochen hatte.", footnotes: ["21,19", "Die Weissagung ist nicht überliefert."] },
      { number: 39, text: "Die übrige Geschichte Ahabs und alle seine Taten, der Bericht über das Elfenbeinhaus, das er gebaut, und die Städte, die er ausgebaut hat, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 40, text: "Ahab entschlief zu seinen Vätern und sein Sohn Ahasja wurde König an seiner Stelle." },
      { number: 41, text: "Im vierten Jahr des Königs Ahab von Israel wurde Joschafat, der Sohn Asas, König von Juda.", heading: "Joschafat von Juda: 22,41-51", footnotes: ["(41-51) 2 Chr 20,31 - 21,1"] },
      { number: 42, text: "Er war fünfunddreißig Jahre alt, als er König wurde, und regierte fünfundzwanzig Jahre in Jerusalem. Seine Mutter hieß Asuba und war eine Tochter Schilhis." },
      { number: 43, text: "Er folgte ganz den Wegen seines Vaters Asa, ohne von ihnen abzuweichen, und tat, was dem Herrn gefiel." },
      { number: 44, text: "Nur die Kulthöhen verschwanden nicht. Das Volk brachte noch Schlacht- und Rauchopfer auf ihnen dar." },
      { number: 45, text: "Joschafat hatte auch Frieden mit dem König von Israel." },
      { number: 46, text: "Die übrige Geschichte Joschafats, die Erfolge, die er errang, und die Kriege, die er führte, sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 47, text: "Er entfernte die letzten Hierodulen, die in den Tagen seines Vaters Asa übrig geblieben waren, aus dem Land.", footnotes: ["15,12"] },
      { number: 48, text: "In Edom gab es damals keinen König; ein Statthalter vertrat den König." },
      { number: 49, text: "Auch baute Joschafat eine Tarschischflotte, die nach Ofir fahren sollte, um Gold zu holen. Doch kam es nicht zur Fahrt, da die Schiffe in Ezjon-Geber zerschellten.", footnotes: ["Schiffe, die nach dem Muster der in 10,22 erwähnten gebaut waren."] },
      { number: 50, text: "Damals sagte Ahasja, der Sohn Ahabs, zu Joschafat: Meine Leute sollen mit deinen Leuten auf den Schiffen fahren. Doch Joschafat lehnte es ab. -" },
      { number: 51, text: "Joschafat entschlief zu seinen Vätern und wurde bei seinen Vätern in der Stadt seines Vaters David begraben. Sein Sohn Joram wurde König an seiner Stelle." },
      { number: 52, text: "Im siebzehnten Jahr des Königs Joschafat von Juda wurde Ahasja, der Sohn Ahabs, in Samaria König von Israel. Er regierte zwei Jahre über Israel", heading: "Ahasja von Israel: 22,52-54" },
      { number: 53, text: "und tat, was dem Herrn missfiel. Er folgte den Wegen seines Vaters und seiner Mutter und den Wegen Jerobeams, des Sohnes Nebats, der Israel zur Sünde verführt hatte." },
      { number: 54, text: "Auch diente er Baal und betete ihn an. So erzürnte er den Herrn, den Gott Israels, ganz so, wie es sein Vater getan hatte." },
    ] },
  ],
};
