import { Book } from "@/lib/types";

export const _1samuel: Book = {
  id: "1samuel",
  name: "1. Samuel",
  shortName: "1.Sam",
  testament: "old",
  introduction: "Die Samuelbücher Die Epoche, die mit dem Namen Samuel verbunden ist, ist eine Zeit des Übergangs. Samuel handelt noch wie einer der Richter, er tritt aber auch wie ein Prophet auf. Unter seiner maßgeblichen Mitwirkung entsteht in Israel das Königtum. Weder zwischen den beiden Büchern noch am Ende von 2 Sam ist ein Einschnitt in der Geschichte Israels zu erkennen. Dennoch hat die Abgrenzung und Zweiteilung des ursprünglich einen Samuelbuches innerhalb des Deuteronomistischen Geschichtswerks (dazu vgl. die Einleitung zu Dtn) Sinn und Grund. 1 Sam schließt mit dem Ende des ersten Königs, des Benjaminiters Saul; in 1 Kön beginnt mit Salomo, dem Nachfolger Davids, die Reihe von Herrschern, die einen bereits zur festen Institution gewordenen Thron besteigen. Der Aufbau der beiden Samuelbücher folgt dem Verlauf der Ereignisse, setzt aber Schwerpunkte, an denen die Darstellung ausführlicher wird. 1 Sam beginnt mit der Kindheitsgeschichte Samuels (Kap. 1 - 3), wohl einer Überlieferung aus Schilo. Daran schließt sich die Lade-Erzählung an (Kap. 4 - 6), zu der sachlich auch 2 Sam 6 gehört. Samuel wirkt als Richter (Kap. 7) und als prophetischer Seher und Gottesmann. Im Auftrag Gottes salbt er Saul zum ersten König Israels (Kap. 8 - 12), dessen Regierungszeit in Kap. 13 - 31 geschildert wird. Nach Sauls Tod wird David, dessen Aufstieg sich seit langem angebahnt hat, König zunächst von Juda (2 Sam 2 - 4), dann von ganz Israel (5,1-5). David erobert Jerusalem (5,6-16), macht es nach einem Sieg über die Philister (5,17-25) zur Hauptstadt und überführt die Bundeslade dorthin (Kap. 6). Gott verheißt ihm durch den Propheten Natan seinen besonderen Beistand und ein ewiges Königtum (Kap. 7). Von den Kriegen Davids ist in den Kapiteln 8, 10 und 11 die Rede. Kap. 9 berichtet von Davids Großmut gegen den Enkel Sauls, Kap. 11 und 12 von seiner Sünde, Kap. 13 - 19 von den Wirren um die Thronnachfolge und von Abschaloms Aufstand und Ende, Kap. 20 von der Erhebung des Benjaminiters Scheba. Hier schließt 1 Kön an. Außerhalb des Erzählungszusammenhangs stehen die Nachträge über die Hungersnot und über die Blutrache der Gibeoniter (21,1-14), über Davids Helden (21,15-22; 23,8-29) - dazu der Psalm Davids (Kap. 22 = Ps 18) und seine letzten Worte (23,1-7) - sowie über die Volkszählung und ihre Bestrafung (Kap. 24). Den Autoren, die die Samuelbücher in der vorliegenden Form gestaltet haben, lagen bereits größere Werke vor, die sie aufnahmen und anscheinend nur wenig veränderten: die bereits genannte LadeErzählung (1 Sam 4 - 6; 2 Sam 6), die Geschichte von Davids Aufstieg (1 Sam 16,13 - 2 Sam 5) und die Geschichte der Thronfolge (2 Sam 10 - 20; 1 Kön 1 und 2). Die Erzählung von der heiligen Lade diente dazu, Jerusalem als die von Israels Gott erwählte Stadt auszuweisen. Im Ziel mit ihr verwandt ist 2 Sam 24, wo auf den Platz des künftigen Tempels durch die Erscheinung des Engels Jahwes hingewiesen wird. Die beiden Darstellungen vom Aufstieg Davids und von seiner Nachfolge sind aus Helden- und Familiengeschichten zusammengefügt. Die Kindheitsgeschichte Samuels (1 Sam 1 - 3) ist mit Überlieferungen des Heiligtums von Schilo eng verbunden. Das Danklied der Hanna (1 Sam 2), das Totenlied auf Saul und Jonatan (2 Sam 1), der Psalm (2 Sam 22) und die letzten Worte Davids (2 Sam 23) sind dem Liedgut Israels entnommen. Die deuteronomistischen Kreise, die bei der Gestaltung der Samuelbücher tätig waren, trugen an wichtigen Punkten ihre eigene theologische Meinung ein, besonders in ihrer Auffassung vom Königtum. Einer königfreundlichen Strömung, die dem geschichtlichen Hergang eher entsprechen dürfte, steht eine kritische Haltung gegenüber. Hier meldet sich warnend (1 Sam 8 und 10) die deuteronomistische Theologie zu Wort, sie bejaht jedoch die davidische Dynastie, vor allem wegen ihrer Verbindung mit dem Tempel (2 Sam 7). So wird in 2 Sam 7 der Grund für die messianische Erwartung gelegt, die bis ins Neue Testament weiterwirken sollte.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Einst lebte ein Mann aus Ramatajim, ein Zufiter vom Gebirge Efraim. Er hieß Elkana und war ein Sohn Jerohams, des Sohnes Elihus, des Sohnes Tohus, des Sohnes Zufs, ein Efraimiter.", heading: "Eli und Samuel: 1,1 - 3,21 | Die Geburt Samuels: 1,1-20", footnotes: ["1 Chr 6,11f.19f", "Rama - H hat die spätere Namensform Ramatajim - ist auch die Wirkungsstätte Samuels. - ein Zufiter: Text korr., vgl. G und 9,5."] },
      { number: 2, text: "Er hatte zwei Frauen. Die eine hieß Hanna, die andere Peninna. Peninna hatte Kinder, Hanna aber hatte keine Kinder." },
      { number: 3, text: "Dieser Mann zog Jahr für Jahr von seiner Stadt nach Schilo hinauf, um den Herrn der Heere anzubeten und ihm zu opfern. Dort waren Hofni und Pinhas, die beiden Söhne Elis, Priester des Herrn.", footnotes: ["Herr der Heere: H: Jahwe Zebaot, von G oft mit Pantokrator (Allherrscher) übersetzt. Mit den Heeren sind die himmlischen Heere, nämlich die Gestirne und die Engel, aber auch die Heere Israels gemeint. - Zu Schilo vgl. die Anmerkung zu Jos 18,1."] },
      { number: 4, text: "An dem Tag, an dem Elkana das Opfer darbrachte, gab er seiner Frau Peninna und all ihren Söhnen und Töchtern ihre Anteile." },
      { number: 5, text: "Hanna aber gab er einen doppelten Anteil; denn er hatte Hanna lieb, obwohl der Herr ihren Schoß verschlossen hatte.", footnotes: ["doppelten: Übersetzung unsicher."] },
      { number: 6, text: "Ihre Rivalin aber kränkte und demütigte sie sehr, weil der Herr ihren Schoß verschlossen hatte." },
      { number: 7, text: "So machte es Elkana Jahr für Jahr. Sooft sie zum Haus des Herrn hinaufzogen, kränkte Peninna sie; und Hanna weinte und aß nichts." },
      { number: 8, text: "Ihr Mann Elkana fragte sie: Hanna, warum weinst du, warum isst du nichts, warum ist dein Herz betrübt? Bin ich dir nicht viel mehr wert als zehn Söhne?" },
      { number: 9, text: "Nachdem man in Schilo gegessen und getrunken hatte, stand Hanna auf und trat vor den Herrn. Der Priester Eli saß an den Türpfosten des Tempels des Herrn auf seinem Stuhl.", footnotes: ["und trat vor den Herrn: ergänzt nach G."] },
      { number: 10, text: "Hanna war verzweifelt, betete zum Herrn und weinte sehr." },
      { number: 11, text: "Sie machte ein Gelübde und sagte: Herr der Heere, wenn du das Elend deiner Magd wirklich ansiehst, wenn du an mich denkst und deine Magd nicht vergisst und deiner Magd einen männlichen Nachkommen schenkst, dann will ich ihn für sein ganzes Leben dem Herrn überlassen; kein Schermesser soll an sein Haupt kommen.", footnotes: ["Samuel wird ähnlich wie ein Nasiräer Gott geweiht (vgl. die Anmerkungen zu Num 6,1-21 und Ri 13,5)."] },
      { number: 12, text: "So betete sie lange vor dem Herrn. Eli beobachtete ihren Mund;" },
      { number: 13, text: "denn Hanna redete nur still vor sich hin, ihre Lippen bewegten sich, doch ihre Stimme war nicht zu hören. Eli hielt sie deshalb für betrunken" },
      { number: 14, text: "und sagte zu ihr: Wie lange willst du dich noch wie eine Betrunkene aufführen? Sieh zu, dass du deinen Weinrausch los wirst!" },
      { number: 15, text: "Hanna gab zur Antwort: Nein, Herr! Ich bin eine unglückliche Frau. Ich habe weder Wein getrunken noch Bier; ich habe nur dem Herrn mein Herz ausgeschüttet." },
      { number: 16, text: "Halte deine Magd nicht für eine nichtsnutzige Frau; denn nur aus großem Kummer und aus Traurigkeit habe ich so lange geredet." },
      { number: 17, text: "Eli erwiderte und sagte: Geh in Frieden! Der Gott Israels wird dir die Bitte erfüllen, die du an ihn gerichtet hast." },
      { number: 18, text: "Sie sagte: Möge deine Magd Gnade finden vor deinen Augen. Dann ging sie weg; sie aß wieder und hatte kein trauriges Gesicht mehr." },
      { number: 19, text: "Am nächsten Morgen standen sie früh auf und beteten den Herrn an. Dann machten sie sich auf den Heimweg und kehrten in ihr Haus nach Rama zurück. Elkana erkannte seine Frau Hanna; der Herr dachte an sie," },
      { number: 20, text: "und Hanna wurde schwanger. Als die Zeit abgelaufen war, gebar sie einen Sohn und nannte ihn Samuel, denn (sie sagte): Ich habe ihn vom Herrn erbeten.", footnotes: ["Der Name wird nur nach dem Klang des Wortes gedeutet."] },
      { number: 21, text: "Als dann Elkana mit seiner ganzen Familie wieder hinaufzog, um dem Herrn das jährliche Opfer und die Gaben, die er gelobt hatte, darzubringen,", heading: "Samuel wird dem Herrn geweiht: 1,21-28" },
      { number: 22, text: "zog Hanna nicht mit, sondern sagte zu ihrem Mann: Ich werde den Knaben erst, wenn er entwöhnt ist, hinaufbringen; dann soll er vor dem Angesicht des Herrn erscheinen und für immer dort bleiben." },
      { number: 23, text: "Ihr Mann Elkana sagte zu ihr: Tu, was dir gefällt. Bleib hier, bis du ihn entwöhnt hast. Wenn nur der Herr sein Wort erfüllt! Die Frau blieb also daheim und stillte ihren Sohn, bis sie ihn entwöhnte." },
      { number: 24, text: "Als sie ihn entwöhnt hatte, nahm sie ihn mit hinauf, dazu einen dreijährigen Stier, ein Efa Mehl und einen Schlauch Wein. So brachte sie ihn zum Haus des Herrn in Schilo; der Knabe aber war damals noch sehr jung.", footnotes: ["einen dreijährigen Stier: Text korr. nach G, vgl. V. 25; H: drei Stiere."] },
      { number: 25, text: "Als sie den Stier geschlachtet hatten, brachten sie den Knaben zu Eli," },
      { number: 26, text: "und Hanna sagte: Bitte, mein Herr, so wahr du lebst, mein Herr, ich bin die Frau, die damals neben dir stand, um zum Herrn zu beten." },
      { number: 27, text: "Ich habe um diesen Knaben gebetet und der Herr hat mir die Bitte erfüllt, die ich an ihn gerichtet habe." },
      { number: 28, text: "Darum lasse ich ihn auch vom Herrn zurückfordern. Er soll für sein ganzes Leben ein vom Herrn Zurückgeforderter sein. Und sie beteten dort den Herrn an." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Hanna betete. Sie sagte: Mein Herz ist voll Freude über den Herrn, / große Kraft gibt mir der Herr. / Weit öffnet sich mein Mund gegen meine Feinde; / denn ich freue mich über deine Hilfe.", heading: "Danklied der Hanna: 2,1-11", footnotes: ["Wörtlich: Mein Horn ist durch Jahwe erhöht. Das Horn ist Symbol der Kraft. Das Lied der Hanna ist Vorbild für das Magnificat Lk 1,46-55."] },
      { number: 2, text: "Niemand ist heilig, nur der Herr; / denn außer dir gibt es keinen (Gott); / keiner ist ein Fels wie unser Gott." },
      { number: 3, text: "Redet nicht immer so vermessen, / kein freches Wort komme aus eurem Mund; / denn der Herr ist ein wissender Gott / und bei ihm werden die Taten geprüft.", footnotes: ["so vermessen: Text korr., vgl. G."] },
      { number: 4, text: "Der Bogen der Helden wird zerbrochen, / die Wankenden aber gürten sich mit Kraft." },
      { number: 5, text: "Die Satten verdingen sich um Brot, / doch die Hungrigen können feiern für immer. / Die Unfruchtbare bekommt sieben Kinder, / doch die Kinderreiche welkt dahin.", footnotes: ["können feiern für immer, wörtlich: hören auf für immer."] },
      { number: 6, text: "Der Herr macht tot und lebendig, / er führt zum Totenreich hinab und führt auch herauf." },
      { number: 7, text: "Der Herr macht arm und macht reich, / er erniedrigt und er erhöht." },
      { number: 8, text: "Den Schwachen hebt er empor aus dem Staub / und erhöht den Armen, der im Schmutz liegt; / er gibt ihm einen Sitz bei den Edlen, / einen Ehrenplatz weist er ihm zu. / Ja, dem Herrn gehören die Pfeiler der Erde; / auf sie hat er den Erdkreis gegründet." },
      { number: 9, text: "Er behütet die Schritte seiner Frommen, / doch die Frevler verstummen in der Finsternis; / denn der Mensch ist nicht stark aus eigener Kraft." },
      { number: 10, text: "Wer gegen den Herrn streitet, wird zerbrechen, / der Höchste lässt es donnern am Himmel. / Der Herr hält Gericht bis an die Grenzen der Erde. / Seinem König gebe er Kraft / und erhöhe die Macht seines Gesalbten.", footnotes: ["der Höchste: Text korr. (vgl. Ps 18,14). - die Macht, wörtlich: das Horn, vgl. die Anmerkung zu V. 1c."] },
      { number: 11, text: "Darauf kehrte Elkana in sein Haus nach Rama zurück, der Knabe aber stand von da an im Dienst des Herrn unter der Aufsicht des Priesters Eli." },
      { number: 12, text: "Die Söhne Elis waren nichtsnutzige Menschen. Sie kümmerten sich nicht um den Herrn", heading: "Die Schuld des Hauses Eli: 2,12-36" },
      { number: 13, text: "und sie pflegten sich gegenüber dem Volk so zu verhalten: Sooft jemand ein Schlachtopfer darbrachte und das Fleisch kochte, kam ein Diener des Priesters mit einer dreizinkigen Gabel in der Hand.", footnotes: ["13-15: Die Söhne Elis begnügten sich nicht mit den ihnen zustehenden Opferanteilen (vgl. Lev 7,33f; Dtn 18,3)."] },
      { number: 14, text: "Er stach in den Kessel oder den Topf, in das Becken oder die Schüssel, und alles, was die Gabel heraufholte, nahm der Priester für sich. So machten sie es bei allen Israeliten, die dorthin, nach Schilo, kamen." },
      { number: 15, text: "Noch bevor man das Fett in Rauch aufgehen ließ, kam der Diener des Priesters und sagte zu dem Mann, der opferte: Gib mir Fleisch zum Braten für den Priester; er nimmt von dir aber kein gekochtes Fleisch an, sondern nur rohes.", footnotes: ["Lev 7,11-17.28-34"] },
      { number: 16, text: "Wenn ihm der Mann erwiderte: Zuerst muss man doch das Fett in Rauch aufgehen lassen, dann nimm dir, was dein Herz begehrt!, sagte ihm der Diener: Nein, gib es sofort her, sonst nehme ich es mit Gewalt." },
      { number: 17, text: "Die Sünde der jungen Männer war sehr schwer in den Augen des Herrn; denn sie behandelten das Opfer des Herrn mit Verachtung." },
      { number: 18, text: "Der junge Samuel aber versah den Dienst vor dem Angesicht des Herrn, bekleidet mit dem leinenen Efod." },
      { number: 19, text: "Seine Mutter machte ihm immer wieder ein kleines Obergewand und brachte es ihm jedes Jahr mit, wenn sie zusammen mit ihrem Mann hinaufzog, um das jährliche Opfer darzubringen." },
      { number: 20, text: "Dann segnete Eli Elkana und seine Frau und sagte: Der Herr gebe dir für den, den er von dir erbeten hat, andere Nachkommenschaft von dieser Frau. Darauf gingen sie wieder in ihren Heimatort zurück." },
      { number: 21, text: "Der Herr aber nahm sich Hannas an; sie wurde schwanger und bekam noch drei Söhne und zwei Töchter. Der Knabe Samuel aber wuchs beim Herrn heran." },
      { number: 22, text: "Eli war sehr alt geworden. Er hörte von allem, was seine Söhne allen Israeliten antaten, auch, dass sie mit den Frauen schliefen, die sich vor dem Eingang des Offenbarungszeltes aufhielten." },
      { number: 23, text: "Er sagte zu ihnen: Warum tut ihr so etwas? Warum muss ich von allen Leuten solch schlimme Dinge über euch hören?" },
      { number: 24, text: "Nein, meine Söhne, was man, wie ich höre, im Volk des Herrn über euch verbreitet, ist nicht gut." },
      { number: 25, text: "Wenn ein Mensch gegen einen Menschen sündigt, kann Gott Schiedsrichter sein. Wenn aber ein Mensch gegen den Herrn sündigt, wer kann dann für ihn eintreten? Aber sie hörten nicht auf die Stimme ihres Vaters; denn der Herr war entschlossen, sie umkommen zu lassen." },
      { number: 26, text: "Der Knabe Samuel aber wuchs heran und gewann immer mehr an Gunst beim Herrn und auch bei den Menschen.", footnotes: ["Lk 2,52"] },
      { number: 27, text: "Da kam ein Gottesmann zu Eli und sagte zu ihm: So spricht der Herr: Habe ich mich dem Haus deines Vaters nicht deutlich offenbart, als deine Vorfahren in Ägypten dem Haus des Pharao gehörten?", footnotes: ["deine Vorfahren, wörtlich: sie."] },
      { number: 28, text: "Ich habe sie aus allen Stämmen Israels für mich als Priester erwählt, damit sie zu meinem Altar hinaufgehen, das Rauchopfer darbringen und vor meinen Augen das Efod tragen. Auch habe ich dem Haus deines Vaters alle Feueropfer der Israeliten überlassen.", footnotes: ["Dtn 18,1"] },
      { number: 29, text: "Warum missachtet ihr also aus Missgunst Schlachtopfer und Speiseopfer, die ich angeordnet habe? Warum ehrst du deine Söhne mehr als mich und warum mästet ihr euch mit dem Besten aller Gaben meines Volkes Israel?", footnotes: ["aus Missgunst: Text korr., vgl. G."] },
      { number: 30, text: "Darum - Spruch des Herrn, des Gottes Israels: Ich hatte fest zugesagt: Dein Haus und das Haus deines Vaters sollen für ewig vor meinem Angesicht ihren Dienst versehen. Nun aber - Spruch des Herrn: Das sei fern von mir; denn nur die, die mich ehren, werde ich ehren, die aber, die mich verachten, geraten in Schande.", footnotes: ["Ex 28,1", "ihren Dienst versehen, wörtlich: ein und aus gehen."] },
      { number: 31, text: "Es werden Tage kommen, da werde ich deinen Arm abhauen und die Macht deines Vaterhauses vernichten; in deinem Haus wird es keinen alten Mann mehr geben.", footnotes: ["1 Kön 2,27"] },
      { number: 32, text: "Du wirst voll Neid auf all das Gute blicken, das der Herr für Israel tun wird. Nie mehr wird es in deinem Haus einen alten Mann geben.", footnotes: ["voll Neid: Text korr., vgl. V. 29."] },
      { number: 33, text: "Nur einen werde ich nicht wegreißen von meinem Altar, wenn ich deine Augen brechen und deine Seele verschmachten lasse; aber der ganze Nachwuchs deines Hauses wird im besten Mannesalter sterben." },
      { number: 34, text: "Und das soll das Zeichen sein, das sich an deinen beiden Söhnen Hofni und Pinhas verwirklichen wird: Beide werden an einem Tag sterben.", footnotes: ["4,11"] },
      { number: 35, text: "Ich aber werde mir einen zuverlässigen Priester einsetzen, der nach meinem Herzen und nach meinem Sinn handeln wird. Ich will ihm ein Haus bauen, das Bestand hat, und er wird allezeit vor den Augen meines Gesalbten seinen Dienst versehen.", footnotes: ["Vgl. die Anmerkung zu V. 30."] },
      { number: 36, text: "Wer dann von deinem Haus noch übrig ist, wird kommen und sich um ein Geldstück oder einen Laib Brot vor ihm niederwerfen und sagen: Nimm mich doch in eine der Priestergruppen auf, damit ich ein Stück Brot zu essen habe." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Der junge Samuel versah den Dienst des Herrn unter der Aufsicht Elis. In jenen Tagen waren Worte des Herrn selten; Visionen waren nicht häufig.", heading: "Die erste Offenbarung an Samuel: 3,1-21" },
      { number: 2, text: "Eines Tages geschah es: Eli schlief auf seinem Platz; seine Augen waren schwach geworden und er konnte nicht mehr sehen.", footnotes: ["4,15"] },
      { number: 3, text: "Die Lampe Gottes war noch nicht erloschen und Samuel schlief im Tempel des Herrn, wo die Lade Gottes stand." },
      { number: 4, text: "Da rief der Herr den Samuel und Samuel antwortete: Hier bin ich." },
      { number: 5, text: "Dann lief er zu Eli und sagte: Hier bin ich, du hast mich gerufen. Eli erwiderte: Ich habe dich nicht gerufen. Geh wieder schlafen! Da ging er und legte sich wieder schlafen." },
      { number: 6, text: "Der Herr rief noch einmal: Samuel! Samuel stand auf und ging zu Eli und sagte: Hier bin ich, du hast mich gerufen. Eli erwiderte: Ich habe dich nicht gerufen, mein Sohn. Geh wieder schlafen!" },
      { number: 7, text: "Samuel kannte den Herrn noch nicht und das Wort des Herrn war ihm noch nicht offenbart worden." },
      { number: 8, text: "Da rief der Herr den Samuel wieder, zum dritten Mal. Er stand auf und ging zu Eli und sagte: Hier bin ich, du hast mich gerufen. Da merkte Eli, dass der Herr den Knaben gerufen hatte." },
      { number: 9, text: "Eli sagte zu Samuel: Geh, leg dich schlafen! Wenn er dich (wieder) ruft, dann antworte: Rede, Herr; denn dein Diener hört. Samuel ging und legte sich an seinem Platz nieder." },
      { number: 10, text: "Da kam der Herr, trat (zu ihm) heran und rief wie die vorigen Male: Samuel, Samuel! Und Samuel antwortete: Rede, denn dein Diener hört." },
      { number: 11, text: "Der Herr sagte zu Samuel: Fürwahr, ich werde in Israel etwas tun, sodass jedem, der davon hört, beide Ohren gellen." },
      { number: 12, text: "An jenem Tag werde ich an Eli vom Anfang bis zum Ende alles verwirklichen, was ich seinem Haus angedroht habe." },
      { number: 13, text: "Ich habe ihm angekündigt, dass ich über sein Haus für immer das Urteil gesprochen habe wegen seiner Schuld; denn er wusste, wie seine Söhne Gott lästern, und gebot ihnen nicht Einhalt.", footnotes: ["Gott lästern: Text korr. nach G; H: sich Fluch zuziehen."] },
      { number: 14, text: "Darum habe ich dem Haus Eli geschworen: Die Schuld des Hauses Eli kann durch Opfer und durch Gaben in Ewigkeit nicht gesühnt werden." },
      { number: 15, text: "Samuel blieb bis zum Morgen liegen, dann öffnete er die Türen zum Haus des Herrn. Er fürchtete sich aber, Eli von der Vision zu berichten." },
      { number: 16, text: "Da rief Eli Samuel und sagte: Samuel, mein Sohn! Er antwortete: Hier bin ich." },
      { number: 17, text: "Eli fragte: Was war es, das er zu dir gesagt hat? Verheimliche mir nichts! Gott möge dir dies und das antun, wenn du mir auch nur eines von all den Worten verheimlichst, die er zu dir gesprochen hat." },
      { number: 18, text: "Da teilte ihm Samuel alle Worte mit und verheimlichte ihm nichts. Darauf sagte Eli: Es ist der Herr. Er tue, was ihm gefällt." },
      { number: 19, text: "Samuel wuchs heran und der Herr war mit ihm und ließ keines von all seinen Worten unerfüllt.", footnotes: ["und ließ . . . unerfüllt, wörtlich: und ließ keines seiner Worte zu Boden fallen."] },
      { number: 20, text: "Ganz Israel von Dan bis Beerscheba erkannte, dass Samuel als Prophet des Herrn beglaubigt war." },
      { number: 21, text: "Auch weiterhin erschien der Herr in Schilo: Der Herr offenbarte sich Samuel in Schilo durch sein Wort." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Das Wort Samuels erging an ganz Israel. Israel zog gegen die Philister in den Krieg. Sie schlugen ihr Lager bei Eben-Eser auf und die Philister hatten ihr Lager in Afek.", heading: "Verlust und Rückkehr der Bundeslade: 4,1 - 7,1 | Die Bundeslade bei den Philistern: 4,1 - 5,12", footnotes: ["29,1", "Afek ist ein bedeutender Ort an der Küstenstraße nördlich von Jafo."] },
      { number: 2, text: "Die Philister rückten in Schlachtordnung gegen Israel vor und der Kampf wogte hin und her. Israel wurde von den Philistern besiegt, die von Israels Heer auf dem Feld etwa viertausend Mann erschlugen." },
      { number: 3, text: "Als das Volk ins Lager zurückkam, sagten die Ältesten Israels: Warum hat der Herr heute die Philister über uns siegen lassen? Wir wollen die Bundeslade des Herrn aus Schilo zu uns holen; er soll in unsere Mitte kommen und uns aus der Gewalt unserer Feinde retten." },
      { number: 4, text: "Das Volk schickte also (Männer) nach Schilo und sie holten von dort die Bundeslade des Herrn der Heere, der über den Kerubim thront. Hofni und Pinhas, die beiden Söhne Elis, begleiteten die Bundeslade Gottes.", footnotes: ["die beiden: Text korr. nach G; H: und dort die beiden."] },
      { number: 5, text: "Als nun die Bundeslade des Herrn ins Lager kam, erhob ganz Israel ein lautes Freudengeschrei, sodass die Erde dröhnte." },
      { number: 6, text: "Die Philister hörten das laute Geschrei und sagten: Was ist das für ein lautes Geschrei im Lager der Hebräer? Als sie erfuhren, dass die Lade des Herrn ins Lager gekommen sei," },
      { number: 7, text: "fürchteten sich die Philister; denn sie sagten: Gott ist zu ihnen ins Lager gekommen. Und sie riefen: Weh uns! Denn so etwas ist früher nie geschehen." },
      { number: 8, text: "Weh uns! Wer rettet uns aus der Hand dieses mächtigen Gottes? Das ist der Gott, der Ägypten mit allerlei Plagen [in der Wüste] geschlagen hat." },
      { number: 9, text: "Seid tapfer, Philister, und seid Männer, damit ihr nicht den Hebräern dienen müsst, wie sie euch gedient haben. Seid Männer und kämpft!" },
      { number: 10, text: "Da traten die Philister zum Kampf an und Israel wurde besiegt, sodass alle zu ihren Zelten flohen. Es war eine sehr schwere Niederlage. Von Israel fielen dreißigtausend Mann Fußvolk." },
      { number: 11, text: "Die Lade Gottes wurde erbeutet und die beiden Söhne Elis, Hofni und Pinhas, fanden den Tod.", footnotes: ["2,34; Ps 78,61"] },
      { number: 12, text: "Ein Benjaminiter lief vom Schlachtfeld weg und kam noch am gleichen Tag nach Schilo, mit zerrissenen Kleidern und Staub auf dem Haupt.", footnotes: ["Zerrissene Kleider und Staub auf dem Haupt sind Zeichen der Trauer und des Schmerzes."] },
      { number: 13, text: "Als er ankam, saß Eli auf seinem Stuhl neben der Straße und hielt Ausschau; denn er hatte Angst um die Lade Gottes. Als der Mann kam und in der Stadt berichtete, schrie die ganze Stadt auf." },
      { number: 14, text: "Eli hörte das laute Geschrei und fragte: Was bedeutet dieser Lärm? Der Mann lief schnell herbei und berichtete Eli." },
      { number: 15, text: "Eli war achtundneunzig Jahre alt; seine Augen waren starr geworden, sodass er nichts mehr sehen konnte.", footnotes: ["3,2"] },
      { number: 16, text: "Der Mann sagte zu Eli: Ich bin der Mann, der vom Schlachtfeld gekommen ist; ich bin heute aus der Schlacht geflohen. Eli fragte: Wie ist es denn zugegangen, mein Sohn?" },
      { number: 17, text: "Der Bote antwortete: Israel ist vor den Philistern geflohen. Das Volk hat eine schwere Niederlage erlitten. Auch deine beiden Söhne Hofni und Pinhas sind tot und die Lade Gottes ist weggeschleppt worden." },
      { number: 18, text: "Als er die Lade Gottes erwähnte, fiel Eli rückwärts von seinem Stuhl neben dem Tor, brach sich das Genick und starb, denn er war ein alter und schwerfälliger Mann. Er war vierzig Jahre lang Richter in Israel gewesen." },
      { number: 19, text: "Seine Schwiegertochter, die Frau des Pinhas, war schwanger und stand vor der Niederkunft. Als sie die Nachricht vernahm, dass die Lade Gottes weggeschleppt und dass ihr Schwiegervater und ihr Mann tot waren, sank sie zu Boden und gebar (ihr Kind); denn die Wehen waren über sie gekommen." },
      { number: 20, text: "Sie lag im Sterben und die Frauen, die um sie herumstanden, sagten: Fürchte dich nicht, du hast einen Sohn geboren. Doch sie achtete nicht darauf und antwortete nichts." },
      { number: 21, text: "Sie nannte den Knaben Ikabod - das will besagen: Fort ist die Herrlichkeit aus Israel - wegen des Verlustes der Lade Gottes und wegen (des Todes) ihres Schwiegervaters und ihres Mannes.", footnotes: ["Der Name Ikabod ist volkstümlich gedeutet."] },
      { number: 22, text: "Sie sagte: Fort ist die Herrlichkeit aus Israel, denn die Lade Gottes ist weggeschleppt worden.", footnotes: ["Ps 78,61"] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Die Philister brachten die Lade Gottes, die sie erbeutet hatten, von Eben-Eser nach Aschdod." },
      { number: 2, text: "Dann nahmen sie die Lade Gottes, brachten sie in den Tempel Dagons und stellten sie neben Dagon auf.", footnotes: ["Dagon war eine bei den Semiten weit verbreitete, auch von den Philistern verehrte Gottheit, vielleicht ein Getreidegott (dagan = Getreide)."] },
      { number: 3, text: "Als die Einwohner von Aschdod aber am nächsten Morgen aufstanden, war Dagon vornüber gefallen und lag vor der Lade des Herrn mit dem Gesicht auf dem Boden. Sie nahmen Dagon und stellten ihn wieder an seinen Platz." },
      { number: 4, text: "Doch als sie am nächsten Morgen in der Frühe wieder aufstanden, da war Dagon wieder vornüber gefallen und lag vor der Lade des Herrn mit dem Gesicht auf dem Boden. Dagons Kopf und seine beiden Hände lagen abgeschlagen auf der Schwelle. Nur der Rumpf war Dagon geblieben." },
      { number: 5, text: "Deshalb treten die Priester Dagons und alle, die in den Tempel Dagons kommen, bis zum heutigen Tag nicht auf die Schwelle des Dagon von Aschdod.", footnotes: ["Die Sitte, nicht auf die Schwelle zu treten, wird hier mit der Niederlage Dagons erklärt. Sie entstand aber wohl aus der Scheu, auf die Schwellendämonen zu treten."] },
      { number: 6, text: "Die Hand des Herrn lastete schwer auf den Einwohnern von Aschdod, und er versetzte sie in Schrecken und schlug Aschdod und sein Gebiet mit der Beulenpest." },
      { number: 7, text: "Als die Einwohner von Aschdod sahen, was geschah, sagten sie: Die Lade des Gottes Israels darf nicht bei uns bleiben; denn seine Hand liegt schwer auf uns und auf unserem Gott Dagon." },
      { number: 8, text: "Sie sandten (Boten) zu allen Fürsten der Philister, riefen sie bei sich zusammen und fragten sie: Was sollen wir mit der Lade des Gottes Israels machen? Die Fürsten erklärten: Die Lade des Gottes Israels soll nach Gat hinüberziehen. Darauf brachte man die Lade des Gottes Israels nach Gat." },
      { number: 9, text: "Doch nachdem man sie nach Gat gebracht hatte, kam die Hand des Herrn über diese Stadt und versetzte sie in gewaltigen Schrecken. Er schlug die Leute der Stadt, vom Kleinsten bis zum Größten, sodass Pestbeulen bei ihnen aufbrachen." },
      { number: 10, text: "Darauf schickte man die Lade Gottes nach Ekron. Als die Lade Gottes nach Ekron kam, schrien die Einwohner von Ekron auf und sagten: Sie bringen die Lade des Gottes Israels zu uns herüber, um uns und unser Volk zu töten." },
      { number: 11, text: "Darauf sandten sie (Boten) zu allen Fürsten der Philister, riefen sie zusammen und sagten: Schafft die Lade des Gottes Israels fort! Sie soll an den Ort zurückkehren, an den sie gehört, und nicht uns und unser Volk töten. Denn ein tödlicher Schrecken lag über der ganzen Stadt; die Hand Gottes lastete schwer auf ihr." },
      { number: 12, text: "Die Leute, die nicht starben, waren mit Pestbeulen geschlagen, und der Hilfeschrei der Stadt stieg zum Himmel empor." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Die Lade des Herrn war sieben Monate lang im Gebiet der Philister.", heading: "Die Rückkehr der Lade: 6,1 - 7,1" },
      { number: 2, text: "Dann riefen die Philister ihre Priester und Wahrsager herbei und fragten sie: Was sollen wir mit der Lade des Herrn machen? Gebt uns Auskunft, auf welche Weise wir sie an den Ort zurückschicken sollen, an den sie gehört." },
      { number: 3, text: "Sie antworteten: Wenn ihr die Lade des Gottes Israels zurückschickt, dann schickt sie nicht ohne Gabe zurück! Ihr müsst vielmehr ein Sühnegeschenk entrichten. Dann werdet ihr Heilung finden und ihr werdet erkennen, warum seine Hand nicht von euch ablässt." },
      { number: 4, text: "Sie fragten weiter: Was für ein Sühnegeschenk sollen wir ihm entrichten? Sie antworteten: Fünf goldene Beulen und fünf goldene Mäuse, entsprechend der Zahl der Philisterfürsten; denn alle hat die gleiche Plage getroffen, auch eure Fürsten.", footnotes: ["Die Weihegeschenke bilden die erkrankten Körperteile und die Plage ab und sind als Bitte an Gott gedacht."] },
      { number: 5, text: "Macht also Abbilder eurer Pestbeulen und der Mäuse, die euer Land verwüsten, und gebt dem Gott Israels die Ehre! Vielleicht lässt er seine Hand leichter werden über euch, eurem Gott und eurem Land." },
      { number: 6, text: "Warum wollt ihr euer Herz verhärten, wie die Ägypter und der Pharao ihr Herz verhärtet haben? Sie mussten doch auch die Israeliten, nachdem sie sie übel behandelt hatten, entlassen und diese konnten fortgehen.", footnotes: ["Ex 8,28; 9,7"] },
      { number: 7, text: "Macht also jetzt einen neuen Wagen; holt zwei säugende Kühe, auf die noch kein Joch gelegt worden ist, und spannt die Kühe vor den Wagen; ihre Kälber aber nehmt ihnen weg und bringt sie nach Hause!" },
      { number: 8, text: "Dann nehmt die Lade des Herrn und stellt sie auf den Wagen und legt daneben in einer Tasche die goldenen Gegenstände, die ihr ihm als Sühnegabe entrichten wollt; dann lasst sie fortziehen!" },
      { number: 9, text: "Gebt aber acht: Wenn die Lade in Richtung Bet-Schemesch, also in ihr Gebiet hinaufzieht, dann war er es, der uns dieses große Unheil zugefügt hat; wenn nicht, dann wissen wir, dass nicht seine Hand uns getroffen hat, sondern dass es ein Zufall gewesen ist.", footnotes: ["Es soll ein Gottesurteil herbeigeführt werden."] },
      { number: 10, text: "Die Leute machten es so. Sie nahmen zwei säugende Kühe und spannten sie an den Wagen; ihre Kälber aber hielten sie zu Hause zurück." },
      { number: 11, text: "Sie stellten die Lade des Herrn auf den Wagen und auch die Tasche mit den goldenen Mäusen und den Abbildern ihrer Geschwüre." },
      { number: 12, text: "Die Kühe aber gingen geradewegs in Richtung Bet-Schemesch; sie folgten genau der Straße, wichen weder nach rechts noch nach links ab und brüllten immerzu. Die Fürsten der Philister folgten ihnen bis zur Grenze von Bet-Schemesch." },
      { number: 13, text: "Die Leute von Bet-Schemesch waren gerade im Tal bei der Weizenernte. Als sie aufblickten und die Lade sahen, freuten sie sich sehr darüber." },
      { number: 14, text: "Als der Wagen bis zum Feld Joschuas von Bet-Schemesch gekommen war, blieb er stehen. Dort lag ein großer Stein. Sie spalteten das Holz des Wagens und brachten die Kühe dem Herrn als Brandopfer dar." },
      { number: 15, text: "[Die Leviten hatten die Lade des Herrn und die Tasche, die neben ihr stand und in der goldene Gegenstände waren, herabgehoben und auf den großen Stein gestellt. Die Männer von Bet-Schemesch brachten an jenem Tag dem Herrn Brandopfer und Schlachtopfer dar.]", footnotes: ["Zusatz im Sinn von Num 4."] },
      { number: 16, text: "Die fünf Fürsten der Philister sahen zu; dann kehrten sie am gleichen Tag nach Ekron zurück." },
      { number: 17, text: "Das sind die goldenen Geschwüre, die die Philister dem Herrn als Sühnegabe entrichtet haben: eines für Aschdod, eines für Gaza, eines für Aschkelon, eines für Gat, eines für Ekron," },
      { number: 18, text: "dazu die goldenen Mäuse entsprechend der Zahl der Philisterstädte unter den fünf Fürsten, von der befestigten Stadt bis zum offenen Dorf. Der große Stein, auf den man die Lade des Herrn gestellt hatte, liegt noch bis zum heutigen Tag auf dem Feld des Joschua aus Bet-Schemesch.", footnotes: ["Der große Stein: Text korr., vgl. G."] },
      { number: 19, text: "Der Herr aber strafte die Leute von Bet-Schemesch, weil sie die Lade des Herrn angeschaut hatten. Er erschlug aus dem Volk siebzig Mann [fünfzigtausend Mann]. Da trauerte das Volk, weil der Herr einen so schweren Schlag gegen das Volk geführt hatte.", footnotes: ["Num 4,20; 2 Sam 6,6f", "Übertreibender Zusatz, der bei einem Teil der Textzeugen fehlt."] },
      { number: 20, text: "Die Männer von Bet-Schemesch sagten: Wer kann vor dem Herrn, diesem heiligen Gott, bestehen? Und zu wem soll er von uns aus hinaufziehen?", footnotes: ["Bet-Schemesch liegt östlich von Aschdod, Kirjat-Jearim westlich von Jerusalem."] },
      { number: 21, text: "Sie schickten Boten zu den Einwohnern von Kirjat-Jearim und ließen sagen: Die Philister haben die Lade des Herrn zurückgebracht. Kommt herab und holt sie zu euch hinauf!", footnotes: ["1 Chr 13,6; Ps 132,6"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Da kamen die Leute von Kirjat-Jearim und holten die Lade des Herrn zu sich hinauf. Sie brachten sie in das Haus Abinadabs auf der Anhöhe. Und seinen Sohn Eleasar weihten sie, dass er die Lade des Herrn bewache." },
      { number: 2, text: "Seit dem Tag, an dem die Lade in Kirjat-Jearim ihre Wohnung nahm, war eine lange Zeit vergangen, zwanzig Jahre. Als sich das ganze Haus Israel klagend an den Herrn wandte,", heading: "Samuel und Saul: 7,2 - 15,35 | Samuel als Richter: 7,2-17" },
      { number: 3, text: "sagte Samuel zum ganzen Haus Israel: Wenn ihr von ganzem Herzen zum Herrn zurückkehren wollt, dann schafft die fremden Götter mitsamt den Astarten aus eurer Mitte fort! Wendet euer Herz wieder dem Herrn zu, und dient ihm allein; dann wird er euch aus der Gewalt der Philister befreien." },
      { number: 4, text: "Da entfernten die Israeliten die Baale und Astarten und dienten nur noch dem Herrn." },
      { number: 5, text: "Darauf sagte Samuel: Versammelt ganz Israel in Mizpa; ich will für euch zum Herrn beten.", footnotes: ["10,17; Ri 20,1"] },
      { number: 6, text: "Da versammelten sie sich in Mizpa, sie schöpften Wasser und gossen es vor dem Herrn aus. Dort fasteten sie an diesem Tag und sagten: Wir haben uns gegen den Herrn versündigt. Samuel sprach den Israeliten Recht in Mizpa.", footnotes: ["Der Wasser-Ritus ist wohl ein Bußakt."] },
      { number: 7, text: "Die Philister erfuhren, dass sich die Israeliten in Mizpa versammelt hatten, und ihre Fürsten zogen gegen Israel heran. Als die Israeliten das hörten, bekamen sie Angst vor den Philistern." },
      { number: 8, text: "Sie sagten zu Samuel: Kehr dich nicht schweigend von uns ab und hör nicht auf, zum Herrn, unserem Gott, zu rufen, damit er uns aus der Hand der Philister befreit." },
      { number: 9, text: "Da nahm Samuel ein junges Lamm und brachte es dem Herrn als Ganzopfer dar. Er rief zum Herrn für Israel und der Herr erhörte ihn." },
      { number: 10, text: "Als Samuel das Brandopfer darbrachte, rückten die Philister schon zum Kampf gegen Israel heran. Da ließ der Herr mit gewaltigem Krachen noch am gleichen Tag einen Donner gegen die Philister erschallen und brachte sie so in Verwirrung, dass sie von den Israeliten geschlagen wurden.", footnotes: ["14,15; Ex 14,24; 23,27; Jos 10,10; Ri 4,15"] },
      { number: 11, text: "Nun zogen die Israeliten von Mizpa aus, verfolgten die Philister bis unterhalb von Bet-Kar und schlugen sie." },
      { number: 12, text: "Samuel nahm einen Stein und stellte ihn zwischen Mizpa und Jeschana auf. Er nannte ihn Eben-Eser (Stein der Hilfe) und sagte: Bis hierher hat uns der Herr geholfen.", footnotes: ["Jeschana: Text korr., vgl. G."] },
      { number: 13, text: "So wurden die Philister gedemütigt und drangen nicht mehr in das Gebiet Israels ein; und die Hand des Herrn lastete auf den Philistern, solange Samuel lebte." },
      { number: 14, text: "Die Städte von Ekron bis Gat, die die Philister Israel weggenommen hatten, kamen an Israel zurück; das ganze Gebiet dieser Städte entriss Israel den Philistern wieder. Aber zwischen Israel und den Amoritern war Friede." },
      { number: 15, text: "Samuel war Richter in Israel, solange er lebte." },
      { number: 16, text: "Jahr für Jahr zog er umher und machte die Runde durch Bet-El, Gilgal und Mizpa und sprach an all diesen Orten Israel Recht." },
      { number: 17, text: "Dann kehrte er jeweils nach Rama zurück, denn dort hatte er sein Haus. Auch dort sprach er Israel Recht und er baute dort einen Altar für den Herrn." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Als Samuel alt geworden war, setzte er seine Söhne als Richter Israels ein.", heading: "Die Forderung nach einem König: 8,1-22" },
      { number: 2, text: "Sein erstgeborener Sohn hieß Joël, sein zweiter Abija. Sie waren in Beerscheba Richter.", footnotes: ["1 Chr 6,13"] },
      { number: 3, text: "Seine Söhne gingen nicht auf seinen Wegen, sondern waren auf ihren Vorteil aus, ließen sich bestechen und beugten das Recht." },
      { number: 4, text: "Deshalb versammelten sich alle Ältesten Israels und gingen zu Samuel nach Rama." },
      { number: 5, text: "Sie sagten zu ihm: Du bist nun alt und deine Söhne gehen nicht auf deinen Wegen. Darum setze jetzt einen König bei uns ein, der uns regieren soll, wie es bei allen Völkern der Fall ist.", footnotes: ["Dtn 17,14-20; Apg 13,20f"] },
      { number: 6, text: "Aber Samuel missfiel es, dass sie sagten: Gib uns einen König, der uns regieren soll. Samuel betete deshalb zum Herrn," },
      { number: 7, text: "und der Herr sagte zu Samuel: Hör auf die Stimme des Volkes in allem, was sie zu dir sagen. Denn nicht dich haben sie verworfen, sondern mich haben sie verworfen: Ich soll nicht mehr ihr König sein.", footnotes: ["10,19; Ex 15,18; Ps 74,12"] },
      { number: 8, text: "Das entspricht ganz ihren Taten, die sie (immer wieder) getan haben, seitdem ich sie aus Ägypten heraufgeführt habe, bis zum heutigen Tag; sie haben mich verlassen und anderen Göttern gedient. So machen sie es nun auch mit dir." },
      { number: 9, text: "Doch hör jetzt auf ihre Stimme, warne sie aber eindringlich und mach ihnen bekannt, welche Rechte der König hat, der über sie herrschen wird." },
      { number: 10, text: "Samuel teilte dem Volk, das einen König von ihm verlangte, alle Worte des Herrn mit." },
      { number: 11, text: "Er sagte: Das werden die Rechte des Königs sein, der über euch herrschen wird: Er wird eure Söhne holen und sie für sich bei seinen Wagen und seinen Pferden verwenden und sie werden vor seinem Wagen herlaufen." },
      { number: 12, text: "Er wird sie zu Obersten über (Abteilungen von) Tausend und zu Führern über (Abteilungen von) Fünfzig machen. Sie müssen sein Ackerland pflügen und seine Ernte einbringen. Sie müssen seine Kriegsgeräte und die Ausrüstung seiner Streitwagen anfertigen." },
      { number: 13, text: "Eure Töchter wird er holen, damit sie ihm Salben zubereiten und kochen und backen." },
      { number: 14, text: "Eure besten Felder, Weinberge und Ölbäume wird er euch wegnehmen und seinen Beamten geben." },
      { number: 15, text: "Von euren Äckern und euren Weinbergen wird er den Zehnten erheben und ihn seinen Höflingen und Beamten geben." },
      { number: 16, text: "Eure Knechte und Mägde, eure besten jungen Leute und eure Esel wird er holen und für sich arbeiten lassen." },
      { number: 17, text: "Von euren Schafherden wird er den Zehnten erheben. Ihr selber werdet seine Sklaven sein." },
      { number: 18, text: "An jenem Tag werdet ihr wegen des Königs, den ihr euch erwählt habt, um Hilfe schreien, aber der Herr wird euch an jenem Tag nicht antworten." },
      { number: 19, text: "Doch das Volk wollte nicht auf Samuel hören, sondern sagte: Nein, ein König soll über uns herrschen." },
      { number: 20, text: "Auch wir wollen wie alle anderen Völker sein. Unser König soll uns Recht sprechen, er soll vor uns herziehen und soll unsere Kriege führen." },
      { number: 21, text: "Samuel hörte alles an, was das Volk sagte, und trug es dem Herrn vor." },
      { number: 22, text: "Und der Herr sagte zu Samuel: Hör auf ihre Stimme und setz ihnen einen König ein! Da sagte Samuel zu den Israeliten: Geht heim, jeder in seine Stadt!" },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Damals lebte in Benjamin ein Mann namens Kisch, ein Sohn Abiëls, des Sohnes Zerors, des Sohnes Bechorats, des Sohnes Afiachs, ein wohlhabender Benjaminiter.", heading: "Die Salbung Sauls zum König: 9,1 - 10,16", footnotes: ["1-11,15: Die verschiedenen, teilweise widersprüchlichen Überlieferungen darüber, wie Saul König wurde, sind hier aneinander gereiht: Der Prophet Samuel benennt und salbt ihn (9,1 - 10,16); er wird durch Losentscheid König (10,17-27); er weist sich selbst durch einen Sieg als Führer des Volkes aus (11,1-15)."] },
      { number: 2, text: "Er hatte einen Sohn namens Saul, der jung und schön war; kein anderer unter den Israeliten war so schön wie er; er überragte alle um Haupteslänge." },
      { number: 3, text: "Eines Tages verliefen sich die Eselinnen von Sauls Vater Kisch. Da sagte Kisch zu seinem Sohn Saul: Nimm einen von den Knechten, mach dich mit ihm auf den Weg und such die Eselinnen!" },
      { number: 4, text: "Sie durchquerten das Gebirge Efraim und durchstreiften das Gebiet von Schalischa, fanden sie aber nicht. Sie zogen durch das Gebiet von Schaalim - ohne Erfolg; dann durchwanderten sie das Land Jemini, fanden sie aber wieder nicht." },
      { number: 5, text: "Als sie in das Gebiet von Zuf gekommen waren, sagte Saul zu seinem Knecht, der ihn begleitete: Komm, wir wollen umkehren, sonst macht sich mein Vater um uns noch mehr Sorgen als um die Eselinnen." },
      { number: 6, text: "Der Knecht erwiderte ihm: In dieser Stadt wohnt doch ein Gottesmann. Er ist sehr angesehen; alles, was er sagt, trifft mit Sicherheit ein. Lasst uns jetzt zu ihm gehen; vielleicht kann er uns sagen, welchen Weg wir hätten gehen sollen.", footnotes: ["»Gottesmann« ist ein Titel für Männer, die von Gott zu einer besonderen Aufgabe erwählt werden, wie Mose (Dtn 33,1) oder die Propheten (1 Kön 13,4-20; 17,18; 2 Kön 1,9f)."] },
      { number: 7, text: "Saul antwortete dem Knecht: Was sollen wir dem Mann mitbringen, wenn wir hingehen? Das Brot in unseren Taschen ist zu Ende. Wir haben nichts, was wir dem Gottesmann als Geschenk bringen könnten. Oder haben wir sonst etwas?" },
      { number: 8, text: "Darauf antwortete ihm der Knecht: Sieh her, ich habe noch einen Viertel-Silberschekel bei mir. Den will ich dem Gottesmann geben, damit er uns Auskunft über den Weg gibt." },
      { number: 9, text: "- Früher sagte man in Israel, wenn man hinging, um Gott zu befragen: Wir wollen zum Seher gehen. Denn wer heute Prophet genannt wird, hieß früher Seher. -" },
      { number: 10, text: "Saul sagte zu seinem Knecht: Dein Vorschlag ist gut. Komm, wir wollen hingehen. Sie gingen also in die Stadt, wo der Gottesmann wohnte." },
      { number: 11, text: "Als sie die Steige zur Stadt hinaufgingen, trafen sie einige Mädchen, die herauskamen, um Wasser zu schöpfen. Sie fragten sie: Ist der Seher hier?" },
      { number: 12, text: "Sie antworteten ihnen: Ja, er ist da, da vorn. Beeilt euch aber, denn er ist gerade in die Stadt gekommen, weil das Volk heute auf der Kulthöhe ein Opferfest feiert." },
      { number: 13, text: "Wenn ihr in die Stadt kommt, werdet ihr ihn gerade noch treffen, bevor er zur Kulthöhe hinaufsteigt, um am Mahl teilzunehmen. Denn das Volk isst nicht, ehe er kommt, weil er das Opfer segnen muss; erst dann essen die Eingeladenen. Geht also hinauf; denn jetzt könnt ihr ihn treffen." },
      { number: 14, text: "Da gingen sie weiter zur Stadt hinauf. Und als sie zur Mitte der Stadt gekommen waren, kam ihnen gerade Samuel entgegen, der zur Kulthöhe hinaufgehen wollte.", footnotes: ["14.18: Über Ort und Art des Zusammentreffens gab es anscheinend verschiedene Überlieferungen."] },
      { number: 15, text: "Der Herr aber hatte Samuel, einen Tag bevor Saul kam, das Ohr für eine Offenbarung geöffnet und gesagt:" },
      { number: 16, text: "Morgen um diese Zeit schicke ich einen Mann aus dem Gebiet Benjamins zu dir. Ihn sollst du zum Fürsten meines Volkes Israel salben. Er wird mein Volk aus der Gewalt der Philister befreien; denn ich habe die Not meines Volkes Israel gesehen und sein Hilfeschrei ist zu mir gedrungen.", footnotes: ["die Not: Text nach G ergänzt."] },
      { number: 17, text: "Als Samuel Saul sah, sagte der Herr zu ihm: Das ist der Mann, von dem ich dir gesagt habe: Der wird über mein Volk herrschen." },
      { number: 18, text: "Saul trat mitten im Tor zu Samuel und fragte: Sag mir doch, wo das Haus des Sehers ist." },
      { number: 19, text: "Samuel antwortete Saul: Ich bin der Seher. Geh vor mir her zur Kulthöhe hinauf! Ihr sollt heute mit mir essen. Morgen früh will ich dich dann weiterziehen lassen. Ich werde dir Auskunft über alles geben, was du auf dem Herzen hast." },
      { number: 20, text: "Über die Eselinnen, die dir vor drei Tagen abhanden gekommen sind, brauchst du dir keine Gedanken zu machen. Sie wurden gefunden. Auf wen aber richtet sich die ganze Sehnsucht Israels? Gilt sie nicht dir und dem ganzen Haus deines Vaters?" },
      { number: 21, text: "Da antwortete Saul: Bin ich nicht ein Benjaminiter, also aus dem kleinsten Stamm Israels? Ist meine Sippe nicht die geringste von allen Sippen des Stammes Benjamin? Warum sagst du so etwas zu mir?", footnotes: ["15,17"] },
      { number: 22, text: "Samuel nahm Saul und seinen Knecht mit, führte sie in das Haus und wies ihnen die Ehrenplätze unter den Eingeladenen an; es waren etwa dreißig Männer (versammelt)." },
      { number: 23, text: "Samuel sagte zum Koch: Bring das Stück her, das ich dir gegeben habe, das, von dem ich dir gesagt habe: Halt es bei dir zurück!" },
      { number: 24, text: "Da trug der Koch die Keule auf und setzte sie Saul vor; er sagte: Das ist das, was übrig blieb. Greif zu und iss! Als ich das Volk eingeladen hatte, ist dieses Stück für dich und für diese Stunde aufgehoben worden. An jenem Tag aß Saul zusammen mit Samuel." },
      { number: 25, text: "Dann stiegen sie von der Kulthöhe in die Stadt hinab und Samuel hatte mit Saul auf dem Flachdach eine Unterredung." },
      { number: 26, text: "Früh am Morgen, als die Dämmerung anbrach, rief Samuel zu Saul aufs Dach hinauf: Steh auf! Ich will dir das Geleit geben. Saul stand auf und beide gingen zusammen hinaus." },
      { number: 27, text: "Als sie zur Grenze des Stadtgebietes gekommen waren, sagte Samuel zu Saul: Sag dem Knecht, er soll vorausgehen; du aber bleib nun hier stehen! Ich will dir ein Gotteswort verkünden. Da ging der Knecht voraus." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Da nahm Samuel den Ölkrug und goss Saul das Öl auf das Haupt, küsste ihn und sagte: Hiermit hat der Herr dich zum Fürsten über sein Erbe gesalbt.", footnotes: ["15,1"] },
      { number: 2, text: "Wenn du jetzt von mir weggehst, wirst du beim Grab der Rahel in Zelzach im Gebiet von Benjamin zwei Männer treffen und sie werden zu dir sagen: Die Eselinnen, nach denen du auf der Suche bist, sind gefunden worden. Doch denkt dein Vater nicht mehr an die Sache mit den Eselinnen, sondern macht sich Sorgen um euch und sagt: Was kann ich nur wegen meines Sohnes unternehmen?", footnotes: ["Gen 35,19f"] },
      { number: 3, text: "Wenn du dann von dort weiterziehst und zur Tabor-Eiche kommst, werden dir dort drei Männer begegnen, die zu Gott nach Bet-El hinaufziehen. Einer trägt ein Böckchen, einer trägt drei Laib Brot und einer trägt einen Schlauch Wein." },
      { number: 4, text: "Sie werden dich grüßen und dir zwei Brote geben; die sollst du von ihnen annehmen." },
      { number: 5, text: "Danach wirst du nach Gibeat-Elohim kommen, wo die Vorposten der Philister stehen. Wenn du dort in die Stadt hineingehst, wirst du eine Schar von Propheten treffen, die von der Kulthöhe herabkommen, und vor ihnen wird Harfe, Pauke, Flöte und Zither gespielt. Sie selbst sind in prophetischer Verzückung." },
      { number: 6, text: "Dann wird der Geist des Herrn über dich kommen und du wirst wie sie in Verzückung geraten und in einen anderen Menschen verwandelt werden.", footnotes: ["Ri 13,25"] },
      { number: 7, text: "Wenn du aber all diese Zeichen erlebst, dann tu, was sich gerade ergibt; denn Gott ist mit dir." },
      { number: 8, text: "Hernach geh mir voraus nach Gilgal hinab! Ich werde dann bereits auf dem Weg zu dir sein, um Brandopfer darzubringen und Heilsopfer zu schlachten. Sieben Tage musst du warten, bis ich zu dir komme. Dann werde ich dir verkünden, was du tun sollst.", footnotes: ["11,14; 13,8"] },
      { number: 9, text: "Als sich Saul nun umwandte, um von Samuel wegzugehen, verwandelte Gott sein Herz. Und noch am gleichen Tag trafen alle diese Zeichen ein." },
      { number: 10, text: "Als sie, Saul und sein Knecht, nach Gibea gelangten, kam ihnen tatsächlich eine Schar von Propheten entgegen. Der Geist Gottes kam über Saul und Saul geriet mitten unter ihnen in prophetische Verzückung.", footnotes: ["19,20-24"] },
      { number: 11, text: "Alle, die ihn von früher kannten, sahen, wie er zusammen mit den Propheten in Verzückung war. Die Leute sagten zueinander: Was ist denn nur mit dem Sohn des Kisch geschehen? Ist auch Saul unter den Propheten?" },
      { number: 12, text: "Einer von dort erwiderte: Wer ist denn schon deren Vater? So ist das Sprichwort entstanden: Ist denn auch Saul unter den Propheten?", footnotes: ["19,24"] },
      { number: 13, text: "Als die Verzückung zu Ende war, ging Saul nach Gibea hinein.", footnotes: ["nach Gibea hinein: Text korr., vgl. G; H: auf die Höhe."] },
      { number: 14, text: "Der Onkel Sauls fragte ihn und seinen Knecht: Wo seid ihr denn gewesen? Er antwortete: Wir waren weg, um die Eselinnen zu suchen. Als wir aber sahen, dass sie nirgends (zu finden) waren, gingen wir zu Samuel." },
      { number: 15, text: "Sauls Onkel sagte: Erzähl mir doch, was Samuel euch gesagt hat." },
      { number: 16, text: "Saul antwortete seinem Onkel: Er hat uns mit Bestimmtheit gesagt, dass die Eselinnen gefunden worden sind. Die Sache mit dem Königtum aber, von der Samuel gesprochen hatte, erzählte er ihm nicht." },
      { number: 17, text: "Samuel rief das Volk zum Herrn nach Mizpa zusammen.", heading: "Die Wahl Sauls zum König: 10,17-27", footnotes: ["7,5"] },
      { number: 18, text: "Er sagte zu den Israeliten: So spricht der Herr, der Gott Israels: Ich habe Israel aus Ägypten heraufgeführt, ich habe euch aus der Gewalt der Ägypter befreit und aus der Gewalt all der Königreiche, die euch bedrängt haben." },
      { number: 19, text: "Ihr aber habt heute euren Gott verworfen, der euer Retter in allen Nöten und Bedrängnissen war, und ihr habt gesagt: Nein, du sollst einen König bei uns einsetzen. Stellt euch jetzt also vor dem Herrn auf, (geordnet) nach euren Stämmen und Tausendschaften.", footnotes: ["8,7", "Nein: Text korr. nach mehreren Textzeugen."] },
      { number: 20, text: "Und Samuel ließ alle Stämme Israels antreten (um das Los zu werfen). Das Los fiel auf den Stamm Benjamin.", footnotes: ["Jos 7,16"] },
      { number: 21, text: "Darauf ließ er den Stamm Benjamin (geordnet) nach seinen Sippen antreten und das Los fiel auf die Sippe Matri. Und schließlich fiel das Los auf Saul, den Sohn des Kisch. Man suchte ihn (überall), fand ihn aber nicht." },
      { number: 22, text: "Sie befragten noch einmal den Herrn: Ist noch jemand hierher gekommen? Der Herr antwortete: Ja, aber er hat sich beim Tross versteckt." },
      { number: 23, text: "Sie liefen hin und holten ihn von dort. Als er mitten unter das Volk trat, überragte er alle anderen um Haupteslänge." },
      { number: 24, text: "Und Samuel sagte zum ganzen Volk: Habt ihr gesehen, wen der Herr erwählt hat? Keiner ist ihm gleich im ganzen Volk. Da jubelte das ganze Volk und sagte: Es lebe der König!" },
      { number: 25, text: "Samuel machte dem Volk das Königsrecht bekannt, schrieb es in ein Buch und legte das Buch vor dem Herrn nieder. Dann entließ Samuel das ganze Volk, jeden in sein Haus.", footnotes: ["Dtn 17,14-20", "Das Königsgesetz bezieht sich nicht auf 8,10-18, sondern eher auf das Königsgesetz von Dtn 17,14-20."] },
      { number: 26, text: "Auch Saul ging in sein Haus nach Gibea. Und mit ihm zog eine Schar von Männern, deren Herz Gott berührt hatte." },
      { number: 27, text: "Niederträchtige Menschen aber sagten: Was kann uns der schon helfen? Sie verachteten ihn und brachten ihm kein Geschenk. Er aber tat, als merkte er es nicht.", footnotes: ["11,12"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Da zog der Ammoniter Nahasch heran und belagerte Jabesch-Gilead. Die Männer von Jabesch sagten zu Nahasch: Schließ einen Vertrag mit uns, dann wollen wir dir dienen.", heading: "Hilfe für Jabesch: 11,1-13" },
      { number: 2, text: "Der Ammoniter Nahasch erwiderte ihnen: Unter einer Bedingung will ich mit euch einen Vertrag schließen: Ich steche euch allen das rechte Auge aus und bringe damit Schande über ganz Israel." },
      { number: 3, text: "Die Ältesten von Jabesch sagten: Lass uns sieben Tage Zeit! Wir wollen Boten durch das ganze Gebiet von Israel schicken. Wenn sich niemand findet, der uns retten kann, werden wir uns dir ergeben." },
      { number: 4, text: "Als die Boten nach Gibea-Saul kamen und die Sache dem Volk vortrugen, brach das ganze Volk in lautes Weinen aus.", footnotes: ["Gibea-Saul liegt nördlich von Jerusalem. Ausgrabungen haben eine Burg aus der Zeit um 1000 v. Chr. freigelegt, die man mit Saul in Verbindung bringt."] },
      { number: 5, text: "Saul kam gerade hinter seinen Rindern vom Feld her und fragte: Was haben denn die Leute, dass sie weinen? Sie erzählten ihm, was die Männer von Jabesch berichtet hatten." },
      { number: 6, text: "Als Saul das hörte, kam der Geist Gottes über ihn und sein Zorn entbrannte heftig.", footnotes: ["Ri 13,25"] },
      { number: 7, text: "Er ergriff ein Gespann Rinder und hieb es in Stücke, schickte die Stücke durch Boten in das ganze Gebiet von Israel und ließ sagen: Wer nicht hinter Saul und Samuel in den Kampf zieht, dessen Rindern soll es ebenso gehen. Da fiel der Schrecken des Herrn auf das ganze Volk und sie rückten aus wie ein Mann." },
      { number: 8, text: "Als Saul sie in Besek musterte, waren es dreihunderttausend Männer aus Israel und dreißigtausend Männer aus Juda." },
      { number: 9, text: "Sie sagten zu den Boten, die gekommen waren: Folgendes sollt ihr den Männern von Jabesch-Gilead sagen: Morgen, wenn die Sonne am heißesten brennt, bekommt ihr Hilfe. Die Boten gingen heim und berichteten alles und die Männer von Jabesch freuten sich." },
      { number: 10, text: "Sie ließen Nahasch sagen: Morgen werden wir zu euch hinauskommen. Dann könnt ihr mit uns machen, was euch gefällt." },
      { number: 11, text: "Am nächsten Morgen stellte Saul das Volk in drei Abteilungen auf. Um die Zeit der Morgenwache drangen sie mitten in das Lager ein und zur Zeit der Mittagshitze hatten sie die Ammoniter geschlagen. Nur wenige blieben übrig; sie wurden zerstreut, sodass nicht einmal zwei von ihnen beieinander blieben." },
      { number: 12, text: "Da sagte das Volk zu Samuel: Wer hat damals gesagt: Soll etwa Saul als König über uns herrschen? Gebt diese Männer heraus, damit wir sie töten.", footnotes: ["10,27"] },
      { number: 13, text: "Saul aber sagte: An diesem Tag soll niemand getötet werden; denn heute hat der Herr Israel Hilfe gebracht.", footnotes: ["14,45"] },
      { number: 14, text: "Samuel sagte zum Volk: Kommt, wir wollen nach Gilgal gehen und dort das Königtum erneuern.", heading: "Samuels Abschied: 11,14 - 12,25", footnotes: ["10,8"] },
      { number: 15, text: "Da ging das ganze Volk nach Gilgal. Sie machten dort in Gilgal Saul vor dem Herrn zum König, schlachteten dem Herrn Heilsopfer und Saul und alle Männer Israels waren voll Freude." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Samuel sagte zu ganz Israel: Ihr wisst, ich habe in allem, was ihr mir gesagt habt, auf euch gehört und einen König bei euch eingesetzt.", footnotes: ["8,6; 11,15"] },
      { number: 2, text: "Seht, hier ist nun der König, der vor euch in den Krieg ziehen wird. Ich selbst bin alt und grau geworden und meine Söhne leben ja mitten unter euch. Von meiner Jugend an bis zum heutigen Tag bin ich vor euch hergegangen." },
      { number: 3, text: "Hier stehe ich; antwortet mir in Gegenwart des Herrn und seines Gesalbten: Wem habe ich je ein Rind weggenommen? Wem habe ich einen Esel weggenommen? Wen habe ich übervorteilt, wen ungerecht behandelt? Von wem habe ich Bestechungsgeld angenommen und habe beide Augen zugedrückt? Ich will es euch zurückerstatten." },
      { number: 4, text: "Sie antworteten: Du hast uns weder übervorteilt noch ungerecht behandelt, noch hast du von jemand etwas angenommen." },
      { number: 5, text: "Er sagte zu ihnen: Der Herr ist Zeuge euch gegenüber und auch sein Gesalbter ist heute Zeuge dafür, dass ihr mir nichts vorwerfen könnt. Sie erwiderten: Sie sind Zeugen.", footnotes: ["dass ihr mir nichts vorwerfen könnt, wörtlich: dass ihr in meiner Hand nichts gefunden habt."] },
      { number: 6, text: "Und Samuel sagte zum Volk: Der Herr ist es gewesen, der Mose und Aaron erschaffen und eure Väter aus Ägypten heraufgeführt hat." },
      { number: 7, text: "Tretet nun zu mir, damit ich mit euch vor den Augen des Herrn ins Gericht gehe und euch alle Wohltaten des Herrn vorhalte, die er euch und euren Vätern erwiesen hat.", footnotes: ["euch vorhalte: ergänzt nach G."] },
      { number: 8, text: "Als Jakob nach Ägypten gekommen war und die Ägypter eure Väter unterdrückten und sie zum Herrn schrien, da sandte der Herr Mose und Aaron. Sie führten eure Väter aus Ägypten heraus und gaben ihnen Wohnsitze an diesem Ort.", footnotes: ["Ex 3,7f", "und die Ägypter eure Väter unterdrückten: ergänzt nach G."] },
      { number: 9, text: "Eure Väter aber vergaßen den Herrn, ihren Gott. Da verkaufte er sie an Sisera, den Feldherrn von Hazor, an die Philister und an den König von Moab und diese führten Krieg gegen sie.", footnotes: ["Ri 4,2; 10,7; 13,1; 3,12"] },
      { number: 10, text: "Da schrien sie zum Herrn und sagten: Wir haben gesündigt; denn wir haben den Herrn verlassen und den Baalen und Astarten gedient. Befrei uns jetzt aus der Gewalt unserer Feinde; wir wollen wieder dir dienen.", footnotes: ["Ri 10,10"] },
      { number: 11, text: "Darauf sandte der Herr Jerubbaal, Barak, Jiftach und Samuel und befreite euch aus der Gewalt eurer Feinde ringsumher, sodass ihr in Sicherheit leben konntet.", footnotes: ["Ri 6,14; 11,29; Hebr 11,32", "Bedan: G und S lesen Barak."] },
      { number: 12, text: "Als ihr aber saht, dass Nahasch, der König der Ammoniter, gegen euch ausrückte, sagtet ihr zu mir: Nein, ein König soll über uns herrschen!, obwohl doch der Herr, euer Gott, euer König ist.", footnotes: ["11,1f; 8,19"] },
      { number: 13, text: "Seht, hier ist euer König, den ihr verlangt und den ihr euch erwählt habt. Ja, der Herr hat euch einen König gegeben." },
      { number: 14, text: "Wenn ihr den Herrn fürchtet und ihm dient, wenn ihr auf seine Stimme hört und euch seinem Befehl nicht widersetzt, wenn sowohl ihr als auch der König, der über euch herrscht, dem Herrn, eurem Gott, folgt (dann geht es euch gut)." },
      { number: 15, text: "Wenn ihr aber nicht auf die Stimme des Herrn hört und euch seinem Befehl widersetzt, dann wird die Hand des Herrn gegen euch (ausgestreckt) sein wie gegen eure Väter." },
      { number: 16, text: "Nun tretet her und seht die große Tat mit an, die der Herr vor euren Augen vollbringen wird." },
      { number: 17, text: "Ist jetzt nicht gerade Weizenernte? Ich werde zum Herrn rufen und er wird Gewitter und Regen schicken. So werdet ihr erkennen und einsehen, wie groß euer Unrecht in den Augen des Herrn ist, das ihr dadurch begangen habt, dass ihr für euch einen König verlangt habt.", footnotes: ["Gewitter und Regen gibt es in der Erntezeit in Palästina äußerst selten."] },
      { number: 18, text: "Samuel rief zum Herrn und der Herr schickte noch am gleichen Tag Gewitter und Regen. Da geriet das ganze Volk in große Furcht vor dem Herrn und vor Samuel" },
      { number: 19, text: "und alle sagten zu Samuel: Bete für deine Knechte zum Herrn, deinem Gott, damit wir nicht umkommen; denn wir haben all unseren Sünden noch die Bosheit hinzugefügt, einen König für uns zu verlangen." },
      { number: 20, text: "Samuel erwiderte dem Volk: Fürchtet euch nicht! Ihr habt all dieses Böse getan; doch weicht (von nun an) nicht mehr von der Nachfolge des Herrn ab und dient dem Herrn mit ganzem Herzen!" },
      { number: 21, text: "Weicht ja nicht ab und folgt nicht den nichtigen (Göttern), die nichts nützen und nicht retten können, weil sie nichtig sind." },
      { number: 22, text: "Um seines großen Namens willen wird der Herr sein Volk nicht verstoßen; denn er hat sich entschlossen, euch zu seinem Volk zu machen." },
      { number: 23, text: "Auch ich weise es weit von mir, mich am Herrn zu versündigen, und höre deshalb nicht auf, für euch zu beten; ich werde euch den guten und geraden Weg weisen." },
      { number: 24, text: "Nur fürchtet den Herrn und dient ihm treu und von ganzem Herzen! Denn ihr seht, welch große Dinge er an euch getan hat." },
      { number: 25, text: "Wenn ihr aber wieder Böses tut, dann werdet sowohl ihr als auch euer König dahingerafft werden." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Saul war . . . Jahre alt, als er König wurde, und er regierte zwei . . . Jahre über Israel.", heading: "Die Herrschaft Sauls: 13,1-22", footnotes: ["Die Zahlen sind nicht überliefert."] },
      { number: 2, text: "Saul wählte sich dreitausend Mann aus Israel aus; zweitausend davon waren bei Saul in Michmas und auf dem Berg von Bet-El und tausend waren bei Jonatan in Gibea-Benjamin. Den Rest des Volkes entließ er, jeden zu seinem Zelt.", footnotes: ["Michmas: nordöstlich von Jerusalem."] },
      { number: 3, text: "Jonatan erschlug den Vogt der Philister, der in Geba saß; die Philister hörten davon. Saul aber ließ im ganzen Land das Widderhorn blasen und ausrufen: Die Hebräer sollen es hören.", footnotes: ["Geba: nordöstlich von Jerusalem."] },
      { number: 4, text: "Und ganz Israel hörte die Kunde: Saul hat den Vogt der Philister erschlagen und dadurch hat sich Israel bei den Philistern verhasst gemacht. Das Volk wurde aufgeboten, Saul nach Gilgal zu folgen." },
      { number: 5, text: "Die Philister versammelten sich zum Kampf gegen Israel; sie hatten dreitausend Wagen und sechstausend Wagenkämpfer und ein Heer so zahlreich wie der Sand am Ufer des Meeres. Sie zogen herauf und schlugen bei Michmas, östlich von Bet-Awen, ihr Lager auf.", footnotes: ["dreitausend: Text korr., vgl. G und S; H: dreißigtausend. - Zu Bet-Awen vgl. Jos 7,2 und Hos 4,15 und die dortigen Anmerkungen."] },
      { number: 6, text: "Als die Israeliten sahen, dass sie in Gefahr gerieten und dass das Volk bedrängt wurde, versteckten sie sich in Höhlen, Schlupflöchern, Felsspalten, Gruben und Zisternen." },
      { number: 7, text: "Viele gingen über den Jordan in das Land Gad und Gilead. Saul war noch in Gilgal und das ganze Volk, das ihm gefolgt war, hatte große Angst.", footnotes: ["Viele: Text korr.; H: Hebräer."] },
      { number: 8, text: "Er wartete in Gilgal sieben Tage auf Samuel, wie vereinbart, aber Samuel kam nicht. Als ihm nun das Volk davonlief,", footnotes: ["10,8"] },
      { number: 9, text: "sagte Saul: Bringt das Brandopfer und die Heilsopfer zu mir her! Und er brachte das Opfer dar." },
      { number: 10, text: "Gerade als er mit der Darbringung des Opfers fertig war, kam Samuel. Saul ging ihm entgegen, um ihn zu begrüßen." },
      { number: 11, text: "Samuel aber fragte: Was hast du getan? Saul antwortete: Weil ich sah, dass mir das Volk davonlief und du nicht zu dem vereinbarten Zeitpunkt gekommen bist und die Philister sich bei Michmas versammelt haben," },
      { number: 12, text: "dachte ich: Jetzt werden die Philister gegen mich nach Gilgal herabziehen, noch ehe ich den Herrn gnädig gestimmt habe. Darum habe ich es gewagt, das Brandopfer darzubringen." },
      { number: 13, text: "Samuel erwiderte Saul: Du hast töricht gehandelt: Hättest du den Befehl befolgt, den dir der Herr, dein Gott, gegeben hat, dann hätte er jetzt deine Herrschaft über Israel für immer gefestigt.", footnotes: ["10,8"] },
      { number: 14, text: "Nun aber wird deine Herrschaft keinen Bestand haben. Der Herr hat sich einen Mann nach seinem Herzen gesucht und ihn zum Fürsten seines Volkes gemacht. Denn du hast nicht befolgt, was der Herr dir befohlen hat.", footnotes: ["16,1; Apg 13,22"] },
      { number: 15, text: "Danach brach Samuel auf und ging von Gilgal nach Gibea-Benjamin hinauf. Saul musterte das Volk, das noch bei ihm war; es waren etwa sechshundert Mann." },
      { number: 16, text: "Saul, sein Sohn Jonatan und die Leute, die noch bei ihnen waren, standen bei Geba-Benjamin, während die Philister ihr Lager bei Michmas hatten." },
      { number: 17, text: "Da zogen aus dem Lager der Philister drei Abteilungen aus, um zu plündern. Eine Abteilung wandte sich in Richtung Ofra im Gebiet von Schual;", footnotes: ["Ofra: nordöstlich von Jerusalem."] },
      { number: 18, text: "die zweite Abteilung zog in Richtung Bet-Horon, und die dritte Abteilung wandte sich in Richtung auf den Hügel, der über das Hyänental weg in die Wüste hinüberschaut.", footnotes: ["den Hügel: Text korr., vgl. G; H: das Gebiet. - Bet-Horon: westlich von Michmas (vgl. die Anmerkung zu V. 2). - Das Hyänental verläuft wohl in Richtung Jericho."] },
      { number: 19, text: "Damals war im ganzen Land kein Schmied zu finden. Denn die Philister hatten sich gesagt: Die Hebräer sollen sich keine Schwerter und Lanzen machen können.", footnotes: ["Ri 5,8"] },
      { number: 20, text: "Alle Israeliten mussten zu den Philistern hinabgehen, wenn jemand sich eine Pflugschar, eine Hacke, eine Axt oder eine Sichel schmieden lassen wollte." },
      { number: 21, text: "Der Preis für das Schärfen der Pflugscharen, der Hacken, des Dreizacks und der Äxte und das Einsetzen des Ochsenstachels betrug ein Pim.", footnotes: ["ein Pim: eine Gewichtseinheit, zwei Drittel eines Schekels. Der Vers ist verderbt und unklar. Die hebräischen Bezeichnungen für die eisernen Geräte werden wie die in V. 20 verschieden gedeutet."] },
      { number: 22, text: "Als es nun zum Krieg kam, fand sich im ganzen Volk, das bei Saul und Jonatan war, weder ein Schwert noch ein Speer. Nur Saul und sein Sohn Jonatan hatten (solche Waffen)." },
      { number: 23, text: "Ein Posten der Philister rückte gegen den Pass von Michmas vor.", heading: "Jonatans Heldentat: 13,23 - 14,15" },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Eines Tages sagte Jonatan, der Sohn Sauls, zu seinem Waffenträger: Komm, wir wollen zu dem Posten der Philister hinübergehen, der da drüben steht. Seinem Vater sagte er aber nichts davon." },
      { number: 2, text: "Saul saß an der Flurgrenze von Gibea unter dem Granatapfelbaum, der bei Migron steht. Das Volk, das er bei sich hatte, zählte etwa sechshundert Mann." },
      { number: 3, text: "Damals trug Ahija, der Sohn Ahitubs, des Bruder Ikabods, des Sohnes des Pinhas, des Sohnes Elis, des Priesters des Herrn in Schilo, das Efod. Auch das Volk wusste nicht, dass Jonatan weggegangen war." },
      { number: 4, text: "An den Übergängen, wo Jonatan zu dem Posten der Philister hinüberzukommen versuchte, war hüben und drüben eine Felszacke; die eine nannte man Bozez, die andere Senne." },
      { number: 5, text: "Die eine Felszacke erhebt sich wie eine Säule auf der Nordseite, gegenüber von Michmas, die andere auf der Südseite, gegenüber von Geba." },
      { number: 6, text: "Jonatan sagte also zu seinem Waffenträger: Komm, wir wollen hinübergehen zu dem Posten dieser Unbeschnittenen. Vielleicht wird der Herr für uns handeln; für den Herrn ist es ja keine Schwierigkeit zu helfen, sei es durch viele oder durch wenige." },
      { number: 7, text: "Sein Waffenträger erwiderte ihm: Tu nur, was du im Sinn hast. Geh, ich bin mit dabei; ganz wie du willst." },
      { number: 8, text: "Jonatan sagte: Hör zu! Wir wollen zu den Männern hinübergehen und uns ihnen offen zeigen." },
      { number: 9, text: "Und wenn sie dann zu uns sagen: Bleibt stehen, bis wir zu euch kommen!, dann bleiben wir stehen, wo wir sind, und gehen nicht zu ihnen hinauf." },
      { number: 10, text: "Wenn sie aber sagen: Kommt zu uns herauf!, dann wollen wir zu ihnen hinaufsteigen; denn der Herr hat sie in unsere Hand gegeben. Das soll für uns das Zeichen sein." },
      { number: 11, text: "Die beiden zeigten sich also dem Posten der Philister. Die Philister sagten: Seht nur, die Hebräer sind aus ihren Löchern hervorgekommen, in denen sie sich versteckt hatten." },
      { number: 12, text: "Die Wachtposten riefen zu Jonatan und seinem Waffenträger hinüber und sagten: Kommt nur herauf zu uns, wir werden euch etwas erzählen. Da sagte Jonatan zu seinem Waffenträger: Geh hinter mir hinauf; denn der Herr hat sie in die Gewalt Israels gegeben." },
      { number: 13, text: "Jonatan kletterte auf Händen und Füßen hinauf und sein Waffenträger folgte ihm. Da ergriffen sie vor Jonatan die Flucht, aber sein Waffenträger, der hinter ihm war, tötete sie.", footnotes: ["Da ergriffen sie die Flucht: Text korr.; H: Da fielen sie um."] },
      { number: 14, text: "Das war der erste Schlag, den Jonatan und sein Waffenträger führten. Sie erschlugen etwa zwanzig Mann, auf einer Fläche, die ungefähr einen halben Morgen groß war." },
      { number: 15, text: "Großer Schrecken entstand im Lager (der Philister) und auf dem Feld und im ganzen Volk; auch der Vorposten und die plündernden Abteilungen erschraken. Dazu bebte die Erde und es entstand ein Gottesschrecken.", footnotes: ["7,10"] },
      { number: 16, text: "Die Späher Sauls in Gibea-Benjamin sahen ein Getümmel, das wogte und hin und her lief.", heading: "Der Sieg über die Philister: 14,16-23" },
      { number: 17, text: "Da sagte Saul zu den Leuten, die bei ihm waren: Prüft einmal und seht nach, wer von unseren Leuten weggegangen ist. Sie sahen nach, und tatsächlich fehlten Jonatan und sein Waffenträger." },
      { number: 18, text: "Saul sagte zu Ahija: Bring die Lade Gottes her! Denn die Lade war an jenem Tag bei den Israeliten (im Lager).", footnotes: ["18f: Saul wollte vermutlich Gott befragen, was zu tun sei."] },
      { number: 19, text: "Während Saul mit dem Priester redete, wurde das Getümmel im Lager der Philister immer größer. Da sagte Saul zu dem Priester: Halt ein!" },
      { number: 20, text: "Saul aber und alle Leute, die bei ihm waren, zogen gemeinsam in den Kampf. Da sahen sie, dass (im Lager der Philister) jeder sein Schwert gegen den anderen gerichtet hatte und dass ein ganz gewaltiges Getümmel entstanden war." },
      { number: 21, text: "Auch die Hebräer, die bis zu diesem Zeitpunkt aufseiten der Philister gestanden und mit ihnen in den Krieg gezogen waren, wandten sich nun (von ihnen) ab, um aufseiten Israels zu sein, das zu Saul und Jonatan hielt." },
      { number: 22, text: "Als die Israeliten, die sich im Gebirge Efraim versteckt hatten, hörten, dass die Philister auf der Flucht waren, setzten auch sie ihnen im Kampf nach" },
      { number: 23, text: "und der Kampf dehnte sich bis Bet-Awen aus. So rettete der Herr an jenem Tag Israel." },
      { number: 24, text: "Die Israeliten aber waren an jenem Tag in Bedrängnis geraten. Darum stellte Saul das Volk unter einen Fluch und sagte: Verflucht sei jeder, der vor dem Abend etwas isst, bevor ich mich an meinen Feinden gerächt habe. Das Volk nahm also bis zum Abend keine Nahrung zu sich.", heading: "Sauls Schwur: 14,24-46" },
      { number: 25, text: "Nun gab es in jener ganzen Gegend viele Bienennester, sodass Honig auf freiem Feld zu finden war.", footnotes: ["Der erste Teil des Verses ist verderbt, die Übersetzung ein Versuch."] },
      { number: 26, text: "Als das Volk zu den Waben kam und sah, dass Honig aus ihnen herausfloss, streckte niemand seine Hand aus, um etwas davon zu nehmen. Denn das Volk fürchtete den Schwur (Sauls)." },
      { number: 27, text: "Jonatan aber hatte nicht gehört, wie sein Vater das Volk beschwor. Er tauchte den Stock, den er in der Hand hielt, mit der Spitze in eine Honigwabe und führte den Honig mit der Hand zum Mund. Da leuchteten seine Augen wieder." },
      { number: 28, text: "Einer aus dem Volk aber sagte: Dein Vater hat das Volk mit einem Eid beschworen und gesagt: Verflucht sei jeder, der heute etwas isst. Das Volk war aber erschöpft." },
      { number: 29, text: "Jonatan erwiderte: Mein Vater stürzt das Land ins Unglück. Seht nur, meine Augen leuchten, weil ich ein bisschen von diesem Honig gegessen habe." },
      { number: 30, text: "Ja, hätte das Volk heute von der Beute, die es gemacht hat, ordentlich gegessen! Dann wäre die Niederlage der Philister noch größer geworden." },
      { number: 31, text: "Die Israeliten schlugen an diesem Tag die Philister (im ganzen Gebiet) zwischen Michmas und Ajalon, das Volk aber war sehr erschöpft." },
      { number: 32, text: "Deshalb stürzte sich das Volk auf die Beute, nahm Schafe, Rinder und Kälber, schlachtete sie, sodass das Blut einfach auf die Erde floss. Und das Volk aß (das Fleisch) samt dem Blut.", footnotes: ["Lev 3,17", "So die vielfach angenommene Deutung von H. Andere Übersetzungsmöglichkeit: Und das Volk aß über dem Blut, d. h. auf der Stelle, auf die das Blut geflossen war; dadurch wurde sie heiliger Bereich."] },
      { number: 33, text: "Da meldete man Saul: Sieh nur, das Volk versündigt sich gegen den Herrn, es isst (das Fleisch) samt dem Blut. Er sagte: Ihr habt ein Unrecht begangen. Wälzt sofort einen großen Stein her!" },
      { number: 34, text: "Und er befahl: Geht unter die Leute und sagt ihnen: Jeder soll sein Rind und sein Lamm zu mir bringen und es hier schlachten und essen. Versündigt euch nicht gegen den Herrn, indem ihr es samt dem Blut esst. Da brachten die Leute noch in der Nacht alles, was sie in der Hand hatten, herbei und schlachteten es dort.", footnotes: ["alles, was: Text korr. nach G. - Der große Stein (V. 32) dient als Altar; das Blut als Sitz des Lebens gehört Gott."] },
      { number: 35, text: "Und Saul erbaute dem Herrn einen Altar. Es war der erste Altar, den er dem Herrn erbaute." },
      { number: 36, text: "Saul sagte: Wir wollen in der Nacht hinter den Philistern herziehen und unter ihnen plündern, bis der Tag anbricht. Keinen von ihnen wollen wir übrig lassen. Die Leute antworteten: Tu nur, was du für richtig hältst. Der Priester aber sagte: Wir wollen (erst) hier vor Gott hintreten." },
      { number: 37, text: "Saul fragte also Gott: Soll ich hinter den Philistern herziehen? Wirst du sie in die Gewalt Israels geben? Gott aber gab an jenem Tag keine Antwort." },
      { number: 38, text: "Darauf sagte Saul: Alle Anführer des Volkes, kommt her und forscht nach, wodurch diese Sünde heute zustande gekommen ist." },
      { number: 39, text: "So wahr der Herr, der Retter Israels, lebt: Selbst wenn es sich um meinen Sohn Jonatan handeln würde - er muss sterben. Doch niemand aus dem Volk antwortete." },
      { number: 40, text: "Saul aber sagte zu allen Israeliten: Ihr sollt auf der einen Seite stehen, ich und mein Sohn Jonatan wollen auf der anderen Seite stehen. Das Volk antwortete Saul: Tu, was du für richtig hältst." },
      { number: 41, text: "Darauf sagte Saul zum Herrn: Gott Israels, gib uns volle Klarheit! Da fiel das Los auf Jonatan und Saul, das Volk aber ging frei aus." },
      { number: 42, text: "Saul sagte: Werft nun das Los zwischen mir und meinem Sohn Jonatan! Das Los fiel auf Jonatan." },
      { number: 43, text: "Da sagte Saul zu Jonatan: Sag mir, was hast du getan? Jonatan bekannte es ihm und sagte: Ich habe mit der Spitze des Stockes, den ich in der Hand hatte, ein wenig Honig genommen und davon versucht. Ich bin bereit zu sterben." },
      { number: 44, text: "Saul erwiderte: Gott möge mir dies und das antun - Jonatan, du musst sterben." },
      { number: 45, text: "Aber das Volk sagte zu Saul: Soll Jonatan sterben, der so viel für die Rettung Israels getan hat? Das darf nicht sein! So wahr der Herr lebt: Ihm soll kein Haar gekrümmt werden. Denn nur mit Gottes Hilfe hat er heute diese Tat vollbracht. So befreite das Volk Jonatan und er brauchte nicht zu sterben.", footnotes: ["11,13"] },
      { number: 46, text: "Saul aber gab die Verfolgung der Philister auf und zog wieder hinauf und die Philister kehrten in ihre Städte zurück." },
      { number: 47, text: "Als Saul die Königswürde über Israel erlangt hatte, führte er ringsum mit all seinen Feinden Krieg: mit Moab und den Ammonitern, mit Edom und den Königen von Zoba und mit den Philistern. Wohin er sich auch wandte, war er siegreich.", heading: "Die Kriege Sauls: 14,47-48", footnotes: ["war er siegreich: Text korr. nach G; H: frevelte er."] },
      { number: 48, text: "Er vollbrachte tapfere Taten, schlug Amalek und befreite Israel aus der Gewalt derer, die es ausraubten." },
      { number: 49, text: "Die Söhne Sauls waren Jonatan, Jischwi und Malkischua. Die ältere seiner beiden Töchter hieß Merab, die jüngere Michal.", heading: "Sauls Familie: 14,49-52" },
      { number: 50, text: "Sauls Frau hieß Ahinoam; sie war eine Tochter des Ahimaaz. Sein Heerführer hieß Abner; er war ein Sohn Ners, des Onkels Sauls;", footnotes: ["2 Sam 2,8"] },
      { number: 51, text: "Kisch, der Vater Sauls, und Ner, der Vater Abners, waren Söhne Abiëls." },
      { number: 52, text: "Der harte Krieg gegen die Philister hörte nicht auf, solange Saul lebte. Jeden starken und kriegstüchtigen Mann, den Saul sah, nahm er in seinen Dienst." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Samuel sagte zu Saul: Der Herr hatte mich gesandt, um dich zum König seines Volkes Israel zu salben. Darum gehorche jetzt den Worten des Herrn!", heading: "Der Krieg gegen die Amalekiter. Sauls Verstoßung: 15,1-35", footnotes: ["10,1"] },
      { number: 2, text: "So spricht der Herr der Heere: Ich habe beobachtet, was Amalek Israel angetan hat: Es hat sich ihm in den Weg gestellt, als Israel aus Ägypten heraufzog.", footnotes: ["Ex 17,8-16; Dtn 25,17-19"] },
      { number: 3, text: "Darum zieh jetzt in den Kampf und schlag Amalek! Weihe alles, was ihm gehört, dem Untergang! Schone es nicht, sondern töte Männer und Frauen, Kinder und Säuglinge, Rinder und Schafe, Kamele und Esel!" },
      { number: 4, text: "Saul bot das Volk auf und musterte es in Telaim. Es waren zweihunderttausend Mann Fußvolk [und zehntausend Mann aus Juda]." },
      { number: 5, text: "Saul rückte bis zur Stadt der Amalekiter vor und legte im Bachtal einen Hinterhalt." },
      { number: 6, text: "Den Kenitern aber ließ er sagen: Auf, zieht fort, verlasst das Gebiet der Amalekiter, damit ich euch nicht zusammen mit ihnen vernichte; denn ihr habt euch gegenüber allen Israeliten freundlich verhalten, als sie aus Ägypten heraufzogen. Da verließen die Keniter das Gebiet der Amalekiter.", footnotes: ["Ri 1,16"] },
      { number: 7, text: "Saul aber schlug die Amalekiter (im ganzen Gebiet) zwischen Hawila und der Gegend von Schur, das Ägypten gegenüberliegt.", footnotes: ["Gen 20,1; 25,18"] },
      { number: 8, text: "Agag, den König von Amalek, brachte er lebend in seine Gewalt; das ganze Volk aber weihte er mit scharfem Schwert dem Untergang." },
      { number: 9, text: "Saul und das Volk schonten Agag, ebenso auch die besten von den Schafen und Rindern, nämlich das Mastvieh und die Lämmer, sowie alles, was sonst noch wertvoll war. Das wollten sie nicht dem Untergang weihen. Nur alles Minderwertige und Wertlose weihten sie dem Untergang.", footnotes: ["das Mastvieh: Text korr. nach der aramäischen Übersetzung; H: die zweiten."] },
      { number: 10, text: "Deshalb erging das Wort des Herrn an Samuel:" },
      { number: 11, text: "Es reut mich, dass ich Saul zum König gemacht habe. Denn er hat sich von mir abgewandt und hat meine Befehle nicht ausgeführt. Das verdross Samuel sehr und er schrie die ganze Nacht zum Herrn." },
      { number: 12, text: "Am nächsten Morgen machte sich Samuel auf den Weg und ging Saul entgegen. Man hatte Samuel mitgeteilt: Saul ist nach Karmel gekommen und hat sich (dort) ein Denkmal errichtet; dann ist er umgekehrt und nach Gilgal hinab weitergezogen.", footnotes: ["Karmel: südlich von Hebron."] },
      { number: 13, text: "Als Samuel nun zu Saul kam, sagte Saul zu ihm: Gesegnet seist du vom Herrn. Ich habe den Befehl des Herrn ausgeführt." },
      { number: 14, text: "Samuel erwiderte: Und was bedeutet dieses Blöken von Schafen, das mir in die Ohren dringt, und das Gebrüll der Rinder, das ich da höre?" },
      { number: 15, text: "Saul antwortete: Man hat sie aus Amalek mitgebracht, weil das Volk die besten von den Schafen und Rindern geschont hat, um sie dem Herrn, deinem Gott, zu opfern. Das übrige haben wir dem Untergang geweiht." },
      { number: 16, text: "Da sagte Samuel zu Saul: Hör auf! Ich will dir verkünden, was der Herr mir heute Nacht gesagt hat. Saul antwortete: Sprich!" },
      { number: 17, text: "Samuel sagte: Bist du nicht, obwohl du dir gering vorkommst, das Haupt der Stämme Israels? Der Herr hat dich zum König von Israel gesalbt.", footnotes: ["9,21"] },
      { number: 18, text: "Dann hat dich der Herr auf den Weg geschickt und gesagt: Geh und weihe die Amalekiter, die Übeltäter, dem Untergang; kämpfe gegen sie, bis du sie vernichtet hast." },
      { number: 19, text: "Warum hast du nicht auf die Stimme des Herrn gehört, sondern hast dich auf die Beute gestürzt und getan, was dem Herrn missfällt?", footnotes: ["28,18; 1 Chr 10,13"] },
      { number: 20, text: "Saul erwiderte Samuel: Ich habe doch auf die Stimme des Herrn gehört; ich bin den Weg gegangen, auf den der Herr mich geschickt hat; ich habe Agag, den König von Amalek, hergebracht und die Amalekiter dem Untergang geweiht." },
      { number: 21, text: "Aber das Volk hat von der Beute einige Schafe und Rinder genommen, das Beste von dem, was dem Untergang geweiht war, um es dem Herrn, / deinem Gott, in Gilgal zu opfern." },
      { number: 22, text: "Samuel aber sagte: Hat der Herr an Brandopfern und Schlachtopfern das gleiche Gefallen / wie am Gehorsam gegenüber der Stimme des Herrn? / Wahrhaftig, Gehorsam ist besser als Opfer, / Hinhören besser als das Fett von Widdern." },
      { number: 23, text: "Denn Trotz ist ebenso eine Sünde wie die Zauberei, / Widerspenstigkeit ist ebenso (schlimm) / wie Frevel und Götzendienst. / Weil du das Wort des Herrn verworfen hast, / verwirft er dich als König.", footnotes: ["16,1"] },
      { number: 24, text: "Da sagte Saul zu Samuel: Ich habe gesündigt; denn ich habe mich über den Befehl des Herrn und deine Anweisungen hinweggesetzt, ich habe mich vor dem Volk gefürchtet und auf seine Stimme gehört." },
      { number: 25, text: "Darum nimm doch die Sünde von mir weg und kehr mit mir zurück, damit ich den Herrn anbete." },
      { number: 26, text: "Samuel erwiderte Saul: Ich kehre nicht mit dir zurück; denn du hast das Wort des Herrn verworfen und nun hat der Herr dich verworfen, sodass du nicht mehr König von Israel sein kannst." },
      { number: 27, text: "Als Samuel sich umwandte, um wegzugehen, griff Saul nach dem Zipfel seines Mantels, doch der riss ab." },
      { number: 28, text: "Da sagte Samuel zu ihm: So entreißt dir heute der Herr die Herrschaft über Israel und gibt sie einem anderen, der besser ist als du.", footnotes: ["28,17"] },
      { number: 29, text: "Er, der ewige Ruhm Israels, kann weder lügen noch bereuen. Er ist doch kein Mensch, sodass er etwas bereuen müsste.", footnotes: ["Num 23,19"] },
      { number: 30, text: "Saul erwiderte: Ich habe gesündigt; erweise mir aber jetzt vor den Ältesten des Volkes und vor Israel die Ehre, mit mir zurückzukehren, damit ich den Herrn, deinen Gott, anbete." },
      { number: 31, text: "Da kehrte Samuel um und folgte Saul und Saul betete den Herrn an." },
      { number: 32, text: "Darauf sagte Samuel: Bringt Agag, den König von Amalek, zu mir! Agag wurde in Fesseln zu ihm gebracht und sagte: Wahrhaftig, die Bitterkeit des Todes ist gewichen." },
      { number: 33, text: "Samuel aber erwiderte: Wie dein Schwert die Frauen um ihre Kinder gebracht, / so sei unter den Frauen deine Mutter kinderlos gemacht. Und Samuel hieb vor den Augen des Herrn in Gilgal Agag in Stücke." },
      { number: 34, text: "Dann ging Samuel nach Rama und Saul zog hinauf in sein Haus nach Gibea-Saul." },
      { number: 35, text: "Samuel sah Saul vor dem Tag seines Todes nicht mehr. Samuel trauerte um Saul, weil es den Herrn reute, dass er Saul zum König über Israel gemacht hatte.", footnotes: ["19,18-24"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Der Herr sagte zu Samuel: Wie lange willst du noch um Saul trauern? Ich habe ihn doch verworfen; er soll nicht mehr als König über Israel herrschen. Fülle dein Horn mit Öl und mach dich auf den Weg! Ich schicke dich zu dem Betlehemiter Isai; denn ich habe mir einen von seinen Söhnen als König ausersehen.", heading: "Saul und David: 16,1 - 31,13 | Davids Salbung zum König: 16,1-13", footnotes: ["13,14; 15,23"] },
      { number: 2, text: "Samuel erwiderte: Wie kann ich da hingehen? Saul wird es erfahren und mich umbringen. Der Herr sagte: Nimm ein junges Rind mit und sag: Ich bin gekommen, um dem Herrn ein Schlachtopfer darzubringen." },
      { number: 3, text: "Lade Isai zum Opfer ein! Ich selbst werde dich dann erkennen lassen, was du tun sollst: Du sollst mir nur den salben, den ich dir nennen werde.", footnotes: ["1 Chr 11,3"] },
      { number: 4, text: "Samuel tat, was der Herr befohlen hatte. Als er nach Betlehem kam, gingen ihm die Ältesten der Stadt zitternd entgegen und fragten: Bedeutet dein Kommen Frieden?" },
      { number: 5, text: "Er antwortete: Frieden. Ich bin gekommen, um dem Herrn ein Schlachtopfer darzubringen. Heiligt euch und kommt mit mir zum Opfer! Dann heiligte er Isai und seine Söhne und lud sie zum Opfer ein." },
      { number: 6, text: "Als sie kamen und er den Eliab sah, dachte er: Gewiss steht nun vor dem Herrn sein Gesalbter." },
      { number: 7, text: "Der Herr aber sagte zu Samuel: Sieh nicht auf sein Aussehen und seine stattliche Gestalt, denn ich habe ihn verworfen; Gott sieht nämlich nicht auf das, worauf der Mensch sieht. Der Mensch sieht, was vor den Augen ist, der Herr aber sieht das Herz.", footnotes: ["Gott sieht: Text ergänzt nach G."] },
      { number: 8, text: "Nun rief Isai den Abinadab und ließ ihn vor Samuel treten. Dieser sagte: Auch ihn hat der Herr nicht erwählt." },
      { number: 9, text: "Isai ließ Schima kommen. Samuel sagte: Auch ihn hat der Herr nicht erwählt." },
      { number: 10, text: "So ließ Isai sieben seiner Söhne vor Samuel treten, aber Samuel sagte zu Isai: Diese hat der Herr nicht erwählt.", footnotes: ["17,12; 1 Chr 2,13-15"] },
      { number: 11, text: "Und er fragte Isai: Sind das alle deine Söhne? Er antwortete: Der jüngste fehlt noch, aber der hütet gerade die Schafe. Samuel sagte zu Isai: Schick jemand hin und lass ihn holen; wir wollen uns nicht zum Mahl hinsetzen, bevor er hergekommen ist." },
      { number: 12, text: "Isai schickte also jemand hin und ließ ihn kommen. David war blond, hatte schöne Augen und eine schöne Gestalt. Da sagte der Herr: Auf, salbe ihn! Denn er ist es.", footnotes: ["17,42"] },
      { number: 13, text: "Samuel nahm das Horn mit dem Öl und salbte David mitten unter seinen Brüdern. Und der Geist des Herrn war über David von diesem Tag an. Samuel aber brach auf und kehrte nach Rama zurück.", footnotes: ["2 Sam 2,4; 5,3; Ps 89,21"] },
      { number: 14, text: "Der Geist des Herrn war von Saul gewichen; jetzt quälte ihn ein böser Geist, der vom Herrn kam.", heading: "David im Dienst Sauls: 16,14-23", footnotes: ["18,10; 19,9", "Die von Gott verliehene Begabung zum Führertum ging Saul verloren; er litt an einer psychischen Störung."] },
      { number: 15, text: "Da sagten die Diener Sauls zu ihm: Du siehst, ein böser Geist Gottes quält dich." },
      { number: 16, text: "Darum möge unser Herr seinen Knechten, die vor ihm stehen, befehlen, einen Mann zu suchen, der die Zither zu spielen versteht. Sobald dich der böse Geist Gottes überfällt, soll er auf der Zither spielen; dann wird es dir wieder gut gehen." },
      { number: 17, text: "Saul sagte zu seinen Dienern: Seht euch für mich nach einem Mann um, der gut spielen kann, und bringt ihn her zu mir!" },
      { number: 18, text: "Einer der jungen Männer antwortete: Ich kenne einen Sohn des Betlehemiters Isai, der Zither zu spielen versteht. Und er ist tapfer und ein guter Krieger, wortgewandt, von schöner Gestalt, und der Herr ist mit ihm." },
      { number: 19, text: "Da schickte Saul Boten zu Isai und ließ ihm sagen: Schick mir deinen Sohn David, der bei den Schafen ist." },
      { number: 20, text: "Isai nahm einen Esel, dazu Brot, einen Schlauch Wein und ein Ziegenböckchen und schickte seinen Sohn David damit zu Saul." },
      { number: 21, text: "So kam David zu Saul und trat in seinen Dienst; Saul gewann ihn sehr lieb, und David wurde sein Waffenträger." },
      { number: 22, text: "Darum schickte Saul zu Isai und ließ ihm sagen: David soll in meinem Dienst bleiben; denn er hat mein Wohlwollen gefunden.", footnotes: ["18,2"] },
      { number: 23, text: "Sooft nun ein Geist Gottes Saul überfiel, nahm David die Zither und spielte darauf. Dann fühlte sich Saul erleichtert, es ging ihm wieder gut und der böse Geist wich von ihm." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Die Philister zogen ihre Truppen zum Kampf zusammen. Sie versammelten sich bei Socho, das zu Juda gehört, und schlugen zwischen Socho und Aseka in Efes-Dammim ihr Lager auf.", heading: "David und Goliat: 17,1-58", footnotes: ["Socho: westlich von Betlehem. - Der Text des Kapitels lässt erkennen, dass verschiedene Überlieferungen über David miteinander verbunden wurden."] },
      { number: 2, text: "Auch Saul und die Männer Israels sammelten sich; sie schlugen ihr Lager im Terebinthental auf und traten zum Kampf gegen die Philister an." },
      { number: 3, text: "Die Philister standen an dem Berg auf der einen Seite, die Israeliten an dem Berg auf der anderen Seite; zwischen ihnen lag das Tal." },
      { number: 4, text: "Da trat aus dem Lager der Philister ein Vorkämpfer namens Goliat aus Gat hervor. Er war sechs Ellen und eine Spanne groß." },
      { number: 5, text: "Auf seinem Kopf hatte er einen Helm aus Bronze und er trug einen Schuppenpanzer aus Bronze, der fünftausend Schekel wog." },
      { number: 6, text: "Er hatte bronzene Schienen an den Beinen und zwischen seinen Schultern hing ein Sichelschwert aus Bronze." },
      { number: 7, text: "Der Schaft seines Speeres war (so dick) wie ein Weberbaum und die eiserne Speerspitze wog sechshundert Schekel. Sein Schildträger ging vor ihm her." },
      { number: 8, text: "Goliat trat vor und rief zu den Reihen der Israeliten hinüber: Warum seid ihr ausgezogen und habt euch zum Kampf aufgestellt? Bin ich nicht ein Philister und seid ihr nicht die Knechte Sauls? Wählt euch doch einen Mann aus! Er soll zu mir herunterkommen." },
      { number: 9, text: "Wenn er mich im Kampf erschlagen kann, wollen wir eure Knechte sein. Wenn ich ihm aber überlegen bin und ihn erschlage, dann sollt ihr unsere Knechte sein und uns dienen." },
      { number: 10, text: "Und der Philister sagte weiter: Heute habe ich die Reihen Israels verhöhnt (und gesagt): Schickt mir doch einen Mann, damit wir gegeneinander kämpfen können." },
      { number: 11, text: "Als Saul und ganz Israel diese Worte des Philisters hörten, erschraken sie und hatten große Angst." },
      { number: 12, text: "David war der Sohn eines Efratiters namens Isai aus Betlehem in Juda, der acht Söhne hatte. Zur Zeit Sauls war Isai bereits alt und betagt.", footnotes: ["16,10"] },
      { number: 13, text: "Die drei ältesten Söhne Isais waren zusammen mit Saul in den Krieg gezogen. Seine drei Söhne, die mit in den Krieg gezogen waren, hießen Eliab - er war der Erstgeborene -, der zweite Abinadab, der dritte Schima." },
      { number: 14, text: "Die drei Ältesten waren Saul gefolgt. David aber war der jüngste." },
      { number: 15, text: "David kehrte öfters vom Hof Sauls nach Betlehem zurück, um die Schafe seines Vaters zu hüten.", footnotes: ["18,2"] },
      { number: 16, text: "Der Philister kam jeden Morgen und Abend und stellte sich kampfbereit hin - vierzig Tage lang." },
      { number: 17, text: "Eines Tages sagte Isai zu seinem Sohn David: Nimm für deine Brüder ein Efa von diesem gerösteten Korn und diese zehn Brote und lauf damit zu ihnen ins Lager." },
      { number: 18, text: "Und diese zehn Käse bring dem Obersten der Tausendschaft! Sieh nach, ob es deinen Brüdern gut geht, und lass dir ein Pfand (als Lebenszeichen) von ihnen geben!" },
      { number: 19, text: "Saul ist mit ihnen und all den anderen Israeliten im Terebinthental und sie kämpfen gegen die Philister." },
      { number: 20, text: "David brach früh am Morgen auf, überließ die Herde einem Wächter, lud die Sachen auf und ging, wie es ihm Isai befohlen hatte. Als er zur Wagenburg kam, rückte das Heer gerade in Schlachtordnung aus und ließ den Kampfruf erschallen." },
      { number: 21, text: "Israel und die Philister stellten sich, Reihe gegen Reihe, zum Kampf auf." },
      { number: 22, text: "David legte das Gepäck ab, überließ es dem Wächter des Trosses und lief zur Schlachtreihe. Er ging zu seinen Brüdern und fragte, wie es ihnen gehe." },
      { number: 23, text: "Während er noch mit ihnen redete, trat gerade aus den Reihen der Philister ihr Vorkämpfer, der Philister namens Goliat aus Gat, hervor; er rief die gewohnten Worte und David hörte es." },
      { number: 24, text: "Als die Israeliten den Mann sahen, hatten sie alle große Angst vor ihm und flohen." },
      { number: 25, text: "Sie sagten: Habt ihr gesehen, wie dieser Mann daherkommt? Er kommt doch nur, um Israel zu verhöhnen. Wer ihn erschlägt, den wird der König sehr reich machen; er wird ihm seine Tochter geben und seine Familie wird er von allen Steuern in Israel befreien." },
      { number: 26, text: "David fragte die Männer, die bei ihm standen: Was wird man für den Mann tun, der diesen Philister erschlägt und die Schande von Israel wegnimmt? Wer ist denn dieser unbeschnittene Philister, dass er die Schlachtreihen des lebendigen Gottes verhöhnen darf?" },
      { number: 27, text: "Die Leute antworteten ihm dasselbe: Das und das wird man dem tun, der ihn erschlägt." },
      { number: 28, text: "Sein ältester Bruder Eliab hörte, wie er mit den Männern redete, und er wurde zornig auf David. Er sagte: Wozu bist du denn hergekommen? Wem hast du denn die paar Schafe in der Wüste überlassen? Ich kenne doch deine Keckheit und die Bosheit in dir. Du bist nur hergekommen, um den Kampf zu sehen." },
      { number: 29, text: "David erwiderte: Was habe ich denn jetzt wieder getan? Ich habe doch nur gefragt." },
      { number: 30, text: "Dann wandte er sich von ihm ab und einem anderen zu und fragte ihn dasselbe. Die Leute antworteten ihm wie beim ersten Mal." },
      { number: 31, text: "Als bekannt wurde, was David gesagt hatte, berichtete man davon auch in Sauls Umgebung und Saul ließ ihn holen." },
      { number: 32, text: "David sagte zu Saul: Niemand soll wegen des Philisters den Mut sinken lassen. Dein Knecht wird hingehen und mit diesem Philister kämpfen." },
      { number: 33, text: "Saul erwiderte ihm: Du kannst nicht zu diesem Philister hingehen, um mit ihm zu kämpfen; du bist zu jung, er aber ist ein Krieger seit seiner Jugend." },
      { number: 34, text: "David sagte zu Saul: Dein Knecht hat für seinen Vater die Schafe gehütet. Wenn ein Löwe oder ein Bär kam und ein Lamm aus der Herde wegschleppte," },
      { number: 35, text: "lief ich hinter ihm her, schlug auf ihn ein und riss das Tier aus seinem Maul. Und wenn er sich dann gegen mich aufrichtete, packte ich ihn an der Mähne und schlug ihn tot." },
      { number: 36, text: "Dein Knecht hat den Löwen und den Bären erschlagen und diesem unbeschnittenen Philister soll es genauso ergehen wie ihnen, weil er die Schlachtreihen des lebendigen Gottes verhöhnt hat." },
      { number: 37, text: "Und David sagte weiter: Der Herr, der mich aus der Gewalt des Löwen und des Bären gerettet hat, wird mich auch aus der Gewalt dieses Philisters retten. Da antwortete Saul David: Geh, der Herr sei mit dir." },
      { number: 38, text: "Und Saul zog David seine Rüstung an; er setzte ihm einen bronzenen Helm auf den Kopf und legte ihm seinen Panzer an," },
      { number: 39, text: "und über der Rüstung hängte er ihm sein Schwert um. David versuchte (in der Rüstung) zu gehen, aber er war es nicht gewohnt. Darum sagte er zu Saul: Ich kann in diesen Sachen nicht gehen, ich bin nicht daran gewöhnt. Und er legte sie wieder ab," },
      { number: 40, text: "nahm seinen Stock in die Hand, suchte sich fünf glatte Steine aus dem Bach und legte sie in die Hirtentasche, die er bei sich hatte (und) die (ihm als) Schleudersteintasche (diente). Die Schleuder in der Hand, ging er auf den Philister zu." },
      { number: 41, text: "Der Philister kam immer näher an David heran; sein Schildträger schritt vor ihm her." },
      { number: 42, text: "Voll Verachtung blickte der Philister David an, als er ihn sah; denn David war noch sehr jung, er war blond und von schöner Gestalt.", footnotes: ["16,12"] },
      { number: 43, text: "Der Philister sagte zu David: Bin ich denn ein Hund, dass du mit einem Stock zu mir kommst? Und er verfluchte David bei seinen Göttern." },
      { number: 44, text: "Er rief David zu: Komm nur her zu mir, ich werde dein Fleisch den Vögeln des Himmels und den wilden Tieren (zum Fraß) geben." },
      { number: 45, text: "David antwortete dem Philister: Du kommst zu mir mit Schwert, Speer und Sichelschwert, ich aber komme zu dir im Namen des Herrn der Heere, des Gottes der Schlachtreihen Israels, den du verhöhnt hast." },
      { number: 46, text: "Heute wird dich der Herr mir ausliefern. Ich werde dich erschlagen und dir den Kopf abhauen. Die Leichen des Heeres der Philister werde ich noch heute den Vögeln des Himmels und den wilden Tieren (zum Fraß) geben. Alle Welt soll erkennen, dass Israel einen Gott hat." },
      { number: 47, text: "Auch alle, die hier versammelt sind, sollen erkennen, dass der Herr nicht durch Schwert und Speer Rettung verschafft; denn es ist ein Krieg des Herrn und er wird euch in unsere Gewalt geben." },
      { number: 48, text: "Als der Philister weiter vorrückte und immer näher an David herankam, lief auch David von der Schlachtreihe (der Israeliten) aus schnell dem Philister entgegen." },
      { number: 49, text: "Er griff in seine Hirtentasche, nahm einen Stein heraus, schleuderte ihn ab und traf den Philister an der Stirn. Der Stein drang in die Stirn ein und der Philister fiel mit dem Gesicht zu Boden." },
      { number: 50, text: "So besiegte David den Philister mit einer Schleuder und einem Stein; er traf den Philister und tötete ihn, ohne ein Schwert in der Hand zu haben." },
      { number: 51, text: "Dann lief David hin und trat neben den Philister. Er ergriff sein Schwert, zog es aus der Scheide, schlug ihm den Kopf ab und tötete ihn. Als die Philister sahen, dass ihr starker Mann tot war, flohen sie." },
      { number: 52, text: "Die Männer von Israel und Juda aber griffen an, erhoben das Kriegsgeschrei und verfolgten die Philister bis nach Gat und bis vor die Tore von Ekron. Von Schaarajim bis nach Gat und Ekron lagen die erschlagenen Philister am Weg.", footnotes: ["nach Gat: Text korr., vgl. G und die zweite Vershälfte; H: bis zu einem Tal."] },
      { number: 53, text: "Nach der Verfolgung kehrten die Israeliten zurück und plünderten das Lager der Philister." },
      { number: 54, text: "David nahm den Kopf des Philisters und brachte ihn nach Jerusalem. Goliats Waffen aber legte er in sein Zelt.", footnotes: ["21,10"] },
      { number: 55, text: "Als Saul David dem Philister entgegengehen sah, sagte er zu Abner, seinem Heerführer: Abner, wessen Sohn ist der junge Mann? Abner antwortete: So wahr du lebst, König, ich weiß es nicht." },
      { number: 56, text: "Der König sagte: Dann erkundige dich, wessen Sohn der Knabe ist." },
      { number: 57, text: "Als David zurückkehrte, nachdem er den Philister erschlagen hatte, nahm ihn Abner mit und führte ihn zu Saul. David hatte den Kopf des Philisters noch in der Hand." },
      { number: 58, text: "Saul fragte ihn: Wessen Sohn bist du, junger Mann? David antwortete: Der Sohn deines Knechtes Isai aus Betlehem." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Nach dem Gespräch Davids mit Saul schloss Jonatan David in sein Herz. Und Jonatan liebte David wie sein eigenes Leben.", heading: "Jonatans Freundschaft mit David: 18,1-9" },
      { number: 2, text: "Saul behielt David von jenem Tag an bei sich und ließ ihn nicht mehr in das Haus seines Vaters zurückkehren.", footnotes: ["16,22; 17,15"] },
      { number: 3, text: "Jonatan schloss mit David einen Bund, weil er ihn wie sein eigenes Leben liebte.", footnotes: ["19,1; 20,8.17; 23,18; 2 Sam 1,26; 21,7"] },
      { number: 4, text: "Er zog den Mantel, den er anhatte, aus und gab ihn David, ebenso seine Rüstung, sein Schwert, seinen Bogen und seinen Gürtel.", footnotes: ["Jonatan gibt symbolisch einen Teil seiner selbst als Zeichen der Verbundenheit."] },
      { number: 5, text: "David zog ins Feld und überall, wohin Saul ihn schickte, hatte er Erfolg, sodass Saul ihn an die Spitze seiner Krieger stellte. David war beim ganzen Volk und bei den Dienern Sauls beliebt." },
      { number: 6, text: "Als sie nach Davids Sieg über den Philister heimkehrten, zogen die Frauen aus allen Städten Israels König Saul singend und tanzend mit Handpauken, Freudenrufen und Zimbeln entgegen.", footnotes: ["(6-7) 21,12; 29,5"] },
      { number: 7, text: "Die Frauen spielten und riefen voll Freude: Saul hat Tausend erschlagen, / David aber Zehntausend." },
      { number: 8, text: "Saul wurde darüber sehr zornig. Das Lied missfiel ihm und er sagte: David geben sie Zehntausend, mir aber geben sie nur Tausend. Jetzt fehlt ihm nur noch die Königswürde." },
      { number: 9, text: "Von diesem Tag an war Saul gegen David voll Argwohn." },
      { number: 10, text: "Am folgenden Tag kam über Saul wieder ein böser Gottesgeist, sodass er in seinem Haus in Raserei geriet. David aber spielte wie jeden Tag. Saul hatte den Speer in der Hand.", heading: "Sauls Feindschaft gegen David: 18,10-30", footnotes: ["16,14"] },
      { number: 11, text: "Saul dachte: Ich will David an die Wand spießen!, und schleuderte den Speer, aber David wich ihm zweimal aus.", footnotes: ["20,33"] },
      { number: 12, text: "Und Saul begann sich vor David zu fürchten, weil der Herr mit David war, Saul aber verlassen hatte." },
      { number: 13, text: "Darum entfernte Saul David aus seiner Umgebung und machte ihn zum Obersten einer Tausendschaft. So zog David an der Spitze der Leute hinaus (in den Krieg) und wieder heim." },
      { number: 14, text: "David hatte Erfolg, wohin ihn auch sein Weg führte, und der Herr war mit ihm." },
      { number: 15, text: "Als Saul sah, dass David sehr erfolgreich war, bekam er noch mehr Angst vor ihm." },
      { number: 16, text: "Ganz Israel und Juda aber liebte David, weil er an ihrer Spitze hinaus (in den Krieg) und wieder heimzog." },
      { number: 17, text: "Saul sagte zu David: Hier ist meine älteste Tochter Merab. Ich will sie dir zur Frau geben, wenn du dich mir als tapfer erweist und die Kriege des Herrn führst. Saul dachte nämlich: Ich will nicht meine Hand gegen ihn erheben; das sollen die Philister tun." },
      { number: 18, text: "David antwortete Saul: Wer bin ich denn und was ist schon meine Sippe und die Verwandtschaft meines Vaters in Israel, dass ich der Schwiegersohn des Königs werden sollte?" },
      { number: 19, text: "Als aber dann die Zeit kam, in der Sauls Tochter Merab David zur Frau gegeben werden sollte, wurde sie Adriël aus Mehola zur Frau gegeben.", footnotes: ["2 Sam 21,8"] },
      { number: 20, text: "Sauls Tochter Michal liebte David; dies teilte man Saul mit. Es war ihm recht;" },
      { number: 21, text: "denn er sagte sich: Ich will sie ihm geben; sie soll ihm zum Verhängnis werden, sodass er den Philistern in die Hände fällt. Saul sagte zu David: Heute in zwei Jahren kannst du mein Schwiegersohn werden.", footnotes: ["in zwei Jahren: Text korr., vgl. V. 26"] },
      { number: 22, text: "Seinen Dienern aber befahl Saul: Redet heimlich mit David und sagt: Du siehst, dass der König Gefallen an dir hat und dass alle seine Diener dich gern haben; du könntest sofort der Schwiegersohn des Königs werden." },
      { number: 23, text: "Sauls Diener redeten also in dieser Weise mit David. David aber erwiderte: Scheint es euch so leicht zu sein, der Schwiegersohn des Königs zu werden? Ich bin doch ein armer und geringer Mann." },
      { number: 24, text: "Die Diener berichteten Saul: Das und das hat David gesagt." },
      { number: 25, text: "Saul antwortete: So sollt ihr David sagen: Der König möchte keine andere Brautgabe als die Vorhäute von hundert Philistern, um an den Feinden des Königs Rache zu nehmen. Saul plante nämlich, David den Philistern in die Hände fallen zu lassen." },
      { number: 26, text: "Seine Diener berichteten David, was Saul gesagt hatte, und es war David recht, dass er so der Schwiegersohn des Königs werden sollte. Die gesetzte Frist war noch nicht um," },
      { number: 27, text: "als David sich auf den Weg machte und mit seinen Leuten zog; er erschlug zweihundert von den Philistern, brachte ihre Vorhäute zum König und legte sie vollzählig vor ihn hin, um sein Schwiegersohn zu werden. Und Saul gab ihm seine Tochter Michal zur Frau.", footnotes: ["2 Sam 3,14"] },
      { number: 28, text: "Als Saul immer deutlicher erkannte, dass der Herr mit David war und dass seine Tochter Michal ihn liebte," },
      { number: 29, text: "fürchtete er sich noch mehr vor David. So wurde Saul für alle Zeit zum Feind Davids." },
      { number: 30, text: "Die Fürsten der Philister zogen (immer wieder) in den Kampf; sooft sie aber ausrückten, hatte David mehr Erfolg als alle anderen Diener Sauls, sodass sein Name immer mehr galt." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Saul redete vor seinem Sohn Jonatan und vor allen seinen Dienern davon, dass er David töten wolle. Sauls Sohn Jonatan aber hatte David sehr gern;", heading: "Jonatans Vermittlung: 19,1-7", footnotes: ["18,3"] },
      { number: 2, text: "deshalb berichtete er David davon und sagte: Mein Vater Saul will dich töten. Nimm dich also morgen früh in Acht, verbirg dich in einem Versteck!" },
      { number: 3, text: "Ich aber will zusammen mit meinem Vater auf das Feld hinausgehen; dort, wo du dich versteckt hältst, werde ich stehen bleiben und mit meinem Vater über dich reden, und wenn ich etwas erfahre, werde ich dir Bescheid geben." },
      { number: 4, text: "Jonatan redete also zugunsten Davids mit seinem Vater und sagte zu ihm: Der König möge sich doch nicht an seinem Knecht David versündigen; denn er hat sich ja auch nicht an dir versündigt und seine Taten sind für dich sehr nützlich gewesen." },
      { number: 5, text: "Er hat sein Leben aufs Spiel gesetzt und den Philister erschlagen. Der Herr hat (durch ihn) ganz Israel viel Hilfe gebracht. Du hast es selbst gesehen und dich darüber gefreut. Warum willst du dich nun versündigen und unschuldiges Blut vergießen, indem du David ohne jeden Grund tötest?", footnotes: ["17,50"] },
      { number: 6, text: "Saul hörte auf Jonatan und schwor: So wahr der Herr lebt: David soll nicht umgebracht werden." },
      { number: 7, text: "Jonatan rief David und berichtete ihm alles. Dann führte Jonatan David zu Saul und David war wieder in Sauls Dienst wie vorher." },
      { number: 8, text: "Der Krieg ging weiter und David zog wieder gegen die Philister in den Kampf. Er brachte ihnen eine schwere Niederlage bei und sie ergriffen die Flucht.", heading: "Davids Flucht: 19,8-24" },
      { number: 9, text: "Doch wieder kam vom Herrn ein böser Geist über Saul, während er in seinem Haus saß und den Speer in der Hand hielt und David auf der Zither spielte.", footnotes: ["16,14"] },
      { number: 10, text: "Da versuchte Saul, David mit dem Speer an die Wand zu spießen; aber er wich Saul aus, sodass der Speer in die Wand fuhr. David floh und brachte sich in Sicherheit. Noch in derselben Nacht" },
      { number: 11, text: "schickte Saul Boten zum Haus Davids, die ihm auflauern und ihn am nächsten Morgen töten sollten. Doch Michal, Davids Frau, warnte ihn und sagte: Wenn du dich nicht noch in dieser Nacht in Sicherheit bringst, wirst du morgen früh umgebracht." },
      { number: 12, text: "Michal ließ David durch das Fenster hinab, sodass er fliehen und sich in Sicherheit bringen konnte." },
      { number: 13, text: "Dann nahm Michal das Götterbild, legte es in Davids Bett, umgab seinen Kopf mit einem Geflecht von Ziegenhaaren und deckte es mit einem Kleidungsstück zu.", footnotes: ["Götterbild: H: Terafim (ein Kultobjekt unbekannter Form)."] },
      { number: 14, text: "Als nun Saul die Boten schickte, die David holen sollten, sagte sie: Er ist krank." },
      { number: 15, text: "Saul schickte die Boten (zurück), um nach David zu sehen, und befahl: Bringt ihn im Bett zu mir her; er soll umgebracht werden." },
      { number: 16, text: "Als die Boten kamen, entdeckten sie im Bett ein Götterbild mit einem Geflecht von Ziegenhaaren um den Kopf." },
      { number: 17, text: "Da sagte Saul zu Michal: Warum hast du mich so betrogen und meinen Feind entkommen lassen, sodass er sich in Sicherheit bringen konnte? Michal antwortete Saul: Er hat zu mir gesagt: Lass mich weggehen, sonst bringe ich dich um." },
      { number: 18, text: "David floh also und brachte sich in Sicherheit. Er kam zu Samuel nach Rama und erzählte ihm alles, was Saul ihm angetan hatte. Dann ging er zusammen mit Samuel ins Prophetenhaus und beide blieben dort.", footnotes: ["18f: Prophetenhaus: in H ein Wort unsicherer Bedeutung; es dürfte sich um eine Prophetenniederlassung handeln (vgl. 2 Kön 6,1f)."] },
      { number: 19, text: "Als man nun Saul berichtete: David ist in Rama, und zwar im Prophetenhaus!," },
      { number: 20, text: "da schickte Saul Boten, um David holen zu lassen. Sobald sie die Schar der Propheten mit Samuel an ihrer Spitze in prophetischer Verzückung sahen, kam der Geist Gottes auch über die Boten Sauls und auch sie gerieten in Verzückung.", footnotes: ["(20-24) 10,10-12"] },
      { number: 21, text: "Als man Saul das meldete, schickte er andere Boten; aber auch sie gerieten in Verzückung. Da schickte er zum dritten Mal Boten; doch auch sie gerieten in Verzückung." },
      { number: 22, text: "Darauf ging er selbst nach Rama. Als er zu der großen Zisterne in Sechu kam, fragte er: Wo sind Samuel und David? Man antwortete ihm: Sie sind gerade im Prophetenhaus in Rama." },
      { number: 23, text: "Als er von dort zum Prophetenhaus in Rama weiterging, kam auch über ihn der Geist Gottes, und er ging in prophetischer Verzückung weiter, bis er zum Prophetenhaus in Rama kam." },
      { number: 24, text: "Er zog sogar seine Kleider aus und blieb auch in Samuels Gegenwart in Verzückung. Den ganzen Tag und die ganze Nacht über lag er nackt da. Deshalb sagt man: Ist denn auch Saul unter den Propheten?", footnotes: ["10,12"] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "David floh aus dem Prophetenhaus in Rama, ging zu Jonatan und hielt ihm vor: Was habe ich denn getan? Was ist meine Schuld? Was habe ich gegen deinen Vater verbrochen, dass er mir nach dem Leben trachtet?", heading: "Jonatans Beistand für David: 20,1-42" },
      { number: 2, text: "Jonatan antwortete ihm: Das ist undenkbar. Du wirst nicht sterben. Du weißt doch: Mein Vater tut nichts Wichtiges oder Unwichtiges, ohne es mir zu offenbaren. Warum sollte mein Vater gerade das vor mir verheimlichen? Nein, das kann nicht sein." },
      { number: 3, text: "David aber beteuerte nochmals: Dein Vater weiß genau, dass ich dein Wohlwollen gefunden habe, und sagt sich: Jonatan soll das nicht wissen, sonst wird er betrübt. Aber, so wahr der Herr lebt und so wahr du selbst lebst: Zwischen mir und dem Tod liegt nur ein Schritt." },
      { number: 4, text: "Jonatan fragte David: Was meinst du? Was könnte ich für dich tun?" },
      { number: 5, text: "David antwortete Jonatan: Du weißt doch, morgen ist Neumond. Da müsste ich unbedingt am Mahl des Königs teilnehmen. Lass mich fortgehen, damit ich mich bis übermorgen Abend auf dem Feld versteckt halte.", footnotes: ["Der Neumond ist wie der Sabbat Fest- und Ruhetag (vgl. 2 Kön 4,23; Hos 2,13; Am 8,5)."] },
      { number: 6, text: "Wenn dein Vater mich vermisst, dann sag: David hat mich dringend gebeten, in seine Heimatstadt Betlehem gehen zu dürfen, weil dort das jährliche Opfer für die ganze Sippe stattfindet." },
      { number: 7, text: "Wenn er dann sagt: Gut!, dann steht es günstig für deinen Knecht. Wenn er aber in heftigen Zorn gerät, dann kannst du daran erkennen, dass das Unheil von ihm beschlossen ist." },
      { number: 8, text: "Zeig deinem Knecht also deine Freundschaft; denn du hast mit deinem Knecht einen Bund vor dem Herrn geschlossen. Wenn ich Schuld auf mich geladen habe, dann töte mich gleich! Warum willst du mich erst zu deinem Vater bringen?", footnotes: ["18,3"] },
      { number: 9, text: "Jonatan antwortete: Das darf nicht mit dir geschehen. Sobald ich sicher weiß, dass mein Vater beschlossen hat, Unheil über dich zu bringen, werde ich es dir auf jeden Fall mitteilen." },
      { number: 10, text: "David aber fragte Jonatan: Wer wird mir die Nachricht bringen, wenn dir dein Vater etwa eine schroffe Antwort gibt?" },
      { number: 11, text: "Jonatan sagte zu David: Komm, wir wollen aufs Feld hinausgehen. Beide gingen aufs Feld hinaus." },
      { number: 12, text: "Und Jonatan sagte zu David: Beim Herrn, dem Gott Israels: Wenn ich morgen oder übermorgen um diese Zeit bei meinem Vater in Erfahrung bringe, dass es gut für David steht, dann werde ich jemand schicken und es dir sagen lassen." },
      { number: 13, text: "Der Herr möge Jonatan dies und das antun: Wenn mein Vater Böses gegen dich im Sinn hat, werde ich es dir sagen und werde dich in Frieden gehen lassen. Der Herr möge mit dir sein, wie er mit meinem Vater gewesen ist." },
      { number: 14, text: "Nicht wahr, wenn ich dann noch am Leben bin, wirst du entsprechend der Huld des Herrn an mir handeln. Wenn ich aber umkomme," },
      { number: 15, text: "dann entzieh meinem Haus niemals deine Gunst, selbst wenn der Herr jeden der Feinde Davids auf dem Erdboden ausrottet." },
      { number: 16, text: "So schloss Jonatan einen Bund mit dem Haus David. - Der Herr halte sich schadlos an Davids Feinden. -", footnotes: ["Der Sinn des zweiten Satzes ist in H unklar, die Übersetzung ein Versuch."] },
      { number: 17, text: "Und Jonatan ließ auch David bei seiner Liebe zu ihm schwören; denn er liebte ihn wie sein eigenes Leben." },
      { number: 18, text: "Dann sagte Jonatan zu ihm: Morgen ist Neumond. Du wirst vermisst werden; denn man wird deinen Platz beobachten." },
      { number: 19, text: "Übermorgen aber geh weit hinab, an den Ort, wo du dich am Tag der Tat versteckt hattest, und setz dich neben den Stein dort.", footnotes: ["Welche Tat gemeint ist, wird aus H nicht deutlich."] },
      { number: 20, text: "Ich werde dann drei Pfeile in seine Nähe schießen, als ob ich ein Ziel treffen wollte." },
      { number: 21, text: "Dann gib Acht: Ich schicke meinen Diener (und sage zu ihm): Geh, such nach den Pfeilen! Wenn ich dem Diener ausdrücklich sage: Pass auf, die Pfeile liegen von dir aus herwärts, hol sie!, dann komm, denn es steht günstig für dich und es liegt nichts vor, so wahr der Herr lebt." },
      { number: 22, text: "Wenn ich aber zu dem jungen Mann sage: Pass auf, die Pfeile liegen von dir aus weiter draußen!, dann geh weg, denn der Herr schickt dich fort." },
      { number: 23, text: "Für diese Vereinbarung, die wir, ich und du, getroffen haben, soll der Herr auf ewig mein und dein Zeuge sein." },
      { number: 24, text: "Daraufhin versteckte sich David auf dem Feld. So kam der Neumond und der König setzte sich zu Tisch, um das Mahl zu halten." },
      { number: 25, text: "Er setzte sich wie jedes Mal auf seinen gewohnten Platz an der Wand; Jonatan saß ihm gegenüber und Abner saß an Sauls Seite. Davids Platz aber blieb leer.", footnotes: ["saß ihm gegenüber: Text korr. nach G; H: stand auf."] },
      { number: 26, text: "Saul sagte an diesem Tag nichts, denn er dachte: Es ist ihm etwas zugestoßen, was ihn unrein sein lässt; sicher ist er nicht rein.", footnotes: ["Für ein Kultmahl muss man im Zustand kultischer Reinheit sein."] },
      { number: 27, text: "Als aber am zweiten Tag, dem Tag nach dem Neumond, der Platz Davids wieder leer blieb, sagte Saul zu seinem Sohn Jonatan: Warum ist der Sohn Isais gestern und heute nicht zum Essen gekommen?" },
      { number: 28, text: "Jonatan antwortete Saul: David hat mich dringend gebeten, nach Betlehem gehen zu dürfen." },
      { number: 29, text: "Er sagte: Lass mich gehen; denn in der Stadt findet ein Opfer unserer Sippe statt. Mein Bruder selbst hat mich aufgefordert (zu kommen). Wenn ich dein Wohlwollen gefunden habe, dann möchte ich jetzt gehen und meine Brüder wieder sehen. Deswegen ist David nicht an den Tisch des Königs gekommen." },
      { number: 30, text: "Da wurde Saul zornig über Jonatan und sagte: Du Sohn eines entarteten und aufsässigen Weibes! Ich weiß sehr gut, dass du dich zu deiner eigenen Schande und zur Schande des Schoßes deiner Mutter für den Sohn Isais entschieden hast.", footnotes: ["23,17"] },
      { number: 31, text: "Doch solange der Sohn Isais auf Erden lebt, wirst weder du noch dein Königtum Bestand haben. Schick also sofort jemand hin und lass ihn holen; denn er ist ein Kind des Todes." },
      { number: 32, text: "Jonatan antwortete seinem Vater Saul: Warum soll er umgebracht werden? Was hat er getan?" },
      { number: 33, text: "Da schleuderte Saul den Speer gegen ihn, um ihn zu töten. Nun wusste Jonatan, dass sein Vater beschlossen hatte, David umzubringen.", footnotes: ["18,11"] },
      { number: 34, text: "Voll Zorn stand er vom Tisch auf und aß an diesem zweiten Neumondtag nichts; denn er war bekümmert wegen David, weil sein Vater ihn beschimpft hatte." },
      { number: 35, text: "Am nächsten Morgen ging Jonatan, wie er mit David verabredet hatte, aufs Feld hinaus und ein junger Diener war bei ihm." },
      { number: 36, text: "Er sagte zu dem Diener: Lauf, such die Pfeile, die ich abschieße. Der Diener lief und er schoss einen Pfeil über ihn hinaus." },
      { number: 37, text: "Als der Diener an die Stelle kam, wohin Jonatan den Pfeil geschossen hatte, rief Jonatan dem Diener nach: Liegt der Pfeil von dir aus nicht noch weiter draußen?" },
      { number: 38, text: "Und er rief dem Diener nach: Beeil dich, schnell, bleib nicht stehen! Der Diener Jonatans hob den Pfeil auf und kam zu seinem Herrn zurück." },
      { number: 39, text: "Der Diener aber ahnte nichts; nur Jonatan und David wussten von der Vereinbarung." },
      { number: 40, text: "Jonatan gab dem Diener, den er bei sich hatte, seine Waffen und sagte zu ihm: Geh, bring sie in die Stadt!" },
      { number: 41, text: "Als der Diener heimgegangen war, verließ David sein Versteck neben dem Stein, warf sich mit dem Gesicht zur Erde nieder und verneigte sich dreimal tief (vor Jonatan). Dann küssten sie einander und beide weinten. David hörte nicht auf zu weinen", footnotes: ["neben dem Stein: Text korr., vgl. V. 19; unklar. - hörte nicht auf zu weinen: H unklar, Übersetzung ein Versuch."] },
      { number: 42, text: "und Jonatan sagte zu ihm: Geh in Frieden! Für das, was wir beide uns im Namen des Herrn geschworen haben, sei der Herr zwischen mir und dir, zwischen meinen und deinen Nachkommen auf ewig Zeuge." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Dann brach David auf und ging weg, Jonatan aber begab sich in die Stadt zurück.", heading: "David bei Ahimelech: 21,1-10" },
      { number: 2, text: "David gelangte nach Nob zu dem Priester Ahimelech. Ahimelech kam David aufgeregt entgegen und fragte ihn: Warum bist du allein und hast niemand bei dir?", footnotes: ["22,9-19", "Nob wird nach Jes 10,32 nördlich von Jerusalem vermutet. Ahimelech ist nach 14,3 ein Urenkel Elis."] },
      { number: 3, text: "David antwortete dem Priester Ahimelech: Der König hat mir einen Auftrag gegeben und zu mir gesagt: Niemand darf etwas von der Angelegenheit erfahren, in der ich dich sende und mit der ich dich beauftrage. Darum habe ich meine Leute an einen bestimmten Ort bestellt." },
      { number: 4, text: "Hast du vielleicht etwas zur Hand für mich? Fünf Brote vielleicht? Dann gib sie mir, oder gib mir, was du sonst finden kannst." },
      { number: 5, text: "Der Priester gab David Antwort und sagte: Gewöhnliches Brot habe ich nicht zur Hand, nur heiliges Brot ist da; aber dann müssen sich die jungen Männer von Frauen ferngehalten haben.", footnotes: ["Lev 24,5-9; Ex 19,15", "5-7: Heiliges Brot darf wie die Schaubrote nur im Zustand kultischer Reinheit genossen werden."] },
      { number: 6, text: "David antwortete dem Priester: Wir haben uns schon gestern und vorgestern von Frauen ferngehalten. Als ich auszog, waren die Waffen der jungen Männer geheiligt; wenn dies auch ein gewöhnlicher Marsch ist, so wird er doch durch die Waffen geheiligt." },
      { number: 7, text: "Da gab ihm der Priester heiliges Brot, denn es gab dort nur die Schaubrote, die man von (dem Tisch) vor dem Angesicht des Herrn entfernt, um an dem Tag, an dem sie weggenommen werden, frisches Brot aufzulegen.", footnotes: ["Mt 12,3f"] },
      { number: 8, text: "Damals hielt sich dort im Heiligtum des Herrn gerade einer von den Knechten Sauls auf, ein Edomiter namens Doëg; er war der Oberste von Sauls Hirten." },
      { number: 9, text: "David fragte Ahimelech: Hast du einen Speer oder ein Schwert zur Hand? Denn ich konnte weder mein Schwert noch andere Waffen mitnehmen, weil der Auftrag des Königs so dringend war." },
      { number: 10, text: "Der Priester antwortete: Das Schwert des Philisters Goliat, den du im Terebinthental erschlagen hast, liegt hier, in einem Mantel eingewickelt, hinter dem Efod. Wenn du es nehmen willst, nimm es! Außer diesem gibt es keines hier. David sagte: Kein anderes kommt ihm gleich; gib es mir!", footnotes: ["17,50f.54", "Efod ist hier ein Kultgegenstand unbekannten Aussehens."] },
      { number: 11, text: "David brach noch am gleichen Tag auf und floh vor Saul. Er kam zu Achisch, dem König von Gat.", heading: "David in Gat, Adullam und Mizpe: 21,11 - 22,5", footnotes: ["27,2; Ps 56,1"] },
      { number: 12, text: "Doch die Diener des Achisch sagten zu ihrem Herrn: Ist das nicht David, der König des Landes? Ist das nicht der, von dem man beim Reigentanz gesungen hat: Saul hat Tausend erschlagen, David aber Zehntausend?", footnotes: ["18,7"] },
      { number: 13, text: "David nahm sich diese Worte zu Herzen und er fürchtete sich sehr vor Achisch, dem König von Gat." },
      { number: 14, text: "Darum verstellte er sich vor ihnen und tat in ihrer Gegenwart so, als sei er wahnsinnig; er kritzelte auf die Flügel des Tores und ließ sich den Speichel in den Bart laufen.", footnotes: ["Ps 34,1"] },
      { number: 15, text: "Achisch sagte zu seinen Dienern: Seht ihr nicht, dass der Mann verrückt ist? Warum bringt ihr ihn zu mir?" },
      { number: 16, text: "Gibt es bei mir nicht schon genug Verrückte, sodass ihr auch noch diesen Mann zu mir herbringt, damit er bei mir verrückt spielt? Soll der etwa auch noch in mein Haus kommen?" },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Darum ging David von dort weg und brachte sich in der Höhle von Adullam in Sicherheit. Als seine Brüder und seine Familie davon hörten, kamen sie zu ihm hinab.", footnotes: ["Adullam: südwestlich von Jerusalem."] },
      { number: 2, text: "Auch schlossen sich ihm viele Männer an, die unter Druck standen, sowie alle möglichen Leute, die Schulden hatten oder verbittert waren, und er wurde ihr Anführer. So waren etwa vierhundert Mann um ihn." },
      { number: 3, text: "Von dort zog David nach Mizpe-Moab und sagte zum König von Moab: Mein Vater und meine Mutter möchten (von zu Hause) wegziehen und bei euch bleiben, bis ich weiß, was Gott mit mir vorhat." },
      { number: 4, text: "So brachte er seine Eltern zum König von Moab und sie blieben bei ihm, solange David sich in den unzugänglichen Bergen aufhielt." },
      { number: 5, text: "Der Prophet Gad aber sagte zu David: Bleib nicht in den Bergen, sondern zieh weg und geh wieder ins Land Juda! Und David zog weg und kam nach Jaar-Heret.", footnotes: ["2 Sam 24,11; 1 Chr 29,29"] },
      { number: 6, text: "Saul hörte, David und die Männer, die er bei sich hatte, seien entdeckt worden. Er saß gerade in Gibea unter der Tamariske auf der Höhe. Er hatte den Speer in der Hand und alle seine Diener standen um ihn herum.", heading: "Sauls Rache an den Priestern von Nob: 22,6-23" },
      { number: 7, text: "Da sagte Saul zu seinen Dienern, die um ihn standen: Hört her, ihr Benjaminiter! Euch allen wird wohl der Sohn Isais Felder und Weinberge geben und euch zu Obersten von Tausendschaften und Hundertschaften machen," },
      { number: 8, text: "weil ihr euch alle gegen mich verschworen habt. Keiner hat mir etwas davon gesagt, als mein Sohn einen Bund mit dem Sohn Isais schloss, und keinem tat es Leid um mich, sodass er mir mitgeteilt hätte, dass mein Sohn meinen Knecht dazu angestiftet hat, mir aufzulauern, wie es jetzt der Fall ist." },
      { number: 9, text: "Da antwortete der Edomiter Doëg, der bei den Dienern Sauls stand: Ich habe den Sohn Isais gesehen, wie er gerade nach Nob zu Ahimelech, dem Sohn Ahitubs, kam.", footnotes: ["21,8; Ps 52,2"] },
      { number: 10, text: "Ahimelech befragte für ihn den Herrn und gab ihm Verpflegung; auch das Schwert des Philisters Goliat gab er ihm." },
      { number: 11, text: "Darauf schickte der König jemand hin und ließ den Priester Ahimelech, den Sohn Ahitubs, rufen sowie alle vom Haus seines Vaters, die Priester von Nob. Als sie alle zum König gekommen waren," },
      { number: 12, text: "sagte Saul: Hör her, Sohn Ahitubs! Er antwortete: Hier bin ich, mein Herr." },
      { number: 13, text: "Saul sagte zu ihm: Warum habt ihr euch gegen mich verschworen, du und der Sohn Isais? Du hast ihm doch Brot und ein Schwert gegeben und für ihn Gott befragt, sodass er sich gegen mich erheben und mir auflauern kann, wie es heute der Fall ist." },
      { number: 14, text: "Ahimelech erwiderte dem König: Wer hat sich denn von allen deinen Dienern so sehr bewährt wie David, der Schwiegersohn des Königs, der Anführer deiner Leibwache, der in deinem Haus hoch geehrt ist?" },
      { number: 15, text: "Habe ich denn erst heute begonnen, für ihn Gott zu befragen? Keineswegs! Der König möge seinem Knecht und dem ganzen Haus meines Vaters nichts unterstellen; denn dein Knecht hat von all dem nichts gewusst, weder Wichtiges noch Unwichtiges." },
      { number: 16, text: "Doch der König sagte: Du bist dem Tod verfallen, Ahimelech, du und das ganze Haus deines Vaters." },
      { number: 17, text: "Und der König sagte den Läufern, die bei ihm standen: Umzingelt die Priester des Herrn und tötet sie! Denn auch sie haben David die Hand gereicht; sie haben gewusst, dass er auf der Flucht war, und mir nichts davon berichtet. Aber die Diener des Königs wollten ihre Hand nicht erheben, um die Priester des Herrn niederzustoßen." },
      { number: 18, text: "Da sagte der König zu Doëg: Komm her, stoß du die Priester nieder! Der Edomiter Doëg ging zu den Priestern hin und stieß sie nieder. Er brachte an jenem Tag fünfundachtzig Männer um, die das leinene Efod trugen." },
      { number: 19, text: "Auch die Priesterstadt Nob schlug er mit scharfem Schwert: Die Männer und Frauen, die Kinder und Säuglinge, die Rinder, Esel und Schafe (erschlug er) mit scharfem Schwert." },
      { number: 20, text: "Nur ein einziger Sohn Ahimelechs, des Sohnes Ahitubs, namens Abjatar konnte sich in Sicherheit bringen und zu David fliehen.", footnotes: ["23,6; Mk 2,26"] },
      { number: 21, text: "Abjatar berichtete David, dass Saul die Priester des Herrn umgebracht hatte." },
      { number: 22, text: "Da sagte David zu Abjatar: Ich wusste schon an jenem Tag, als der Edomiter Doëg dort war, dass er Saul bestimmt alles berichten werde. Ich selbst bin schuld am Tod all der Leute aus dem Haus deines Vaters.", footnotes: ["Ich selbst bin schuld: Text korr. nach G."] },
      { number: 23, text: "Bleib bei mir, fürchte dich nicht; denn der, der mir nach dem Leben trachtet, trachtet auch dir nach dem Leben; bei mir bist du in Sicherheit.", footnotes: ["1 Kön 2,26"] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Man berichtete David: Die Philister kämpfen gerade gegen Keïla und plündern die Scheunen.", heading: "David in Keïla und in der Steppe Sif: 23,1-28", footnotes: ["Keïla: südlich von Adullam (vgl. die Anmerkung zu 22,1)."] },
      { number: 2, text: "Da befragte David den Herrn: Soll ich hinziehen und diese Philister schlagen? Der Herr antwortete David: Geh, schlag die Philister und rette Keïla!" },
      { number: 3, text: "Aber die Männer Davids sagten: Schau, wir müssen schon hier in Juda Angst haben, wie viel mehr erst dann, wenn wir nach Keïla gegen die Truppen der Philister ziehen sollen." },
      { number: 4, text: "David befragte daher noch einmal den Herrn und der Herr gab zur Antwort: Brich auf, zieh nach Keïla hinab; denn ich werde die Philister in deine Hand geben." },
      { number: 5, text: "Darauf zog David mit seinen Männern gegen Keïla und kämpfte gegen die Philister. Er trieb ihre Herden weg und brachte den Philistern eine schwere Niederlage bei. So rettete David die Einwohner von Keïla." },
      { number: 6, text: "Als Abjatar, der Sohn Ahimelechs, zu David nach Keïla floh, nahm er auch das Efod mit.", footnotes: ["22,18-20"] },
      { number: 7, text: "Saul aber wurde berichtet, dass David in Keïla war. Und Saul sagte: Gott hat ihn mir ausgeliefert; er hat sich selbst gefangen, indem er in eine Stadt mit Tor und Riegel gegangen ist." },
      { number: 8, text: "Saul bot das ganze Volk zum Kampf auf, um nach Keïla hinabzuziehen und David und seine Männer zu belagern." },
      { number: 9, text: "Als David merkte, dass Saul Böses gegen ihn im Schild führte, sagte er zu dem Priester Abjatar: Bring das Efod her!" },
      { number: 10, text: "Dann sprach David: Herr, Gott Israels, dein Knecht hat gehört, dass Saul beabsichtigt, nach Keïla zu kommen, um die Stadt meinetwegen zu vernichten." },
      { number: 11, text: "Werden die Bürger von Keïla mich ihm ausliefern? Wird Saul herabkommen, wie dein Knecht gehört hat? Herr, Gott Israels, teile es deinem Knecht mit! Der Herr antwortete: Er wird herabkommen." },
      { number: 12, text: "David fragte weiter: Werden die Bürger von Keïla mich und meine Männer an Saul ausliefern? Der Herr antwortete: Sie werden euch ausliefern." },
      { number: 13, text: "Da brach David mit seinen Männern auf, etwa sechshundert Mann. Sie verließen Keïla und streiften umher, wie es sich gerade ergab. Als Saul berichtet wurde, dass David aus Keïla entkommen war, ließ er von seinem Plan ab und rückte nicht aus." },
      { number: 14, text: "David setzte sich in der Steppe auf Bergfestungen fest; er ließ sich in den Bergen der Steppe Sif nieder. Saul suchte ihn die ganze Zeit, aber Gott gab ihn nicht in seine Hand.", footnotes: ["14f: Sif: südlich von Hebron; Horescha: weiter südlich."] },
      { number: 15, text: "David aber wusste, dass Saul ausgezogen war, weil er ihm nach dem Leben trachtete. Als David in Horescha in der Steppe Sif war," },
      { number: 16, text: "brach Jonatan, der Sohn Sauls, auf und ging zu David nach Horescha; er stärkte Davids Vertrauen auf Gott." },
      { number: 17, text: "Er sagte zu ihm: Fürchte dich nicht; die Hand meines Vaters Saul wird dich nicht erreichen. Du wirst König über Israel sein und ich werde der Zweite nach dir sein. Auch mein Vater Saul weiß das.", footnotes: ["20,30f; 24,21"] },
      { number: 18, text: "Und die beiden schlossen vor dem Herrn (erneut) einen Bund. David blieb in Horescha und Jonatan ging wieder nach Hause.", footnotes: ["18,3"] },
      { number: 19, text: "Einige Sifiter gingen zu Saul nach Gibea hinauf und berichteten ihm: David hat sich bei uns auf den Bergfestungen bei Horescha versteckt, und zwar auf der Anhöhe von Hachila südlich von Jeschimon.", footnotes: ["26,1; Ps 54,2"] },
      { number: 20, text: "Wenn es dir, König, beliebt, herabzukommen, dann komm! Es wird dann unsere Sache sein, ihn dem König auszuliefern." },
      { number: 21, text: "Saul erwiderte: Gesegnet seid ihr vom Herrn, weil ihr Mitleid mit mir habt." },
      { number: 22, text: "Geht, haltet euch bereit, forscht nach und merkt euch den Ort, wo er sich aufhält und wer ihn dort gesehen hat; denn man hat mir gesagt, er sei schlau." },
      { number: 23, text: "Beobachtet und erkundet alle Schlupfwinkel, in denen er sich verstecken könnte. Wenn ihr sichere Angaben habt, kehrt zu mir zurück; dann werde ich mit euch gehen. Wenn er wirklich im Land ist, werde ich ihn unter allen Tausendschaften Judas aufspüren.", footnotes: ["27,1"] },
      { number: 24, text: "Sie brachen auf und zogen vor Saul her nach Sif. David aber und seine Männer waren in der Steppe Maon, in der Araba südlich von Jeschimon." },
      { number: 25, text: "Saul und seine Männer machten sich auf die Suche. Als man das David meldete, zog er weiter hinunter zu den Felsen in der Steppe Maon und blieb dort. Saul hörte davon und folgte ihm in die Steppe Maon." },
      { number: 26, text: "Saul zog auf der einen Seite des Berges dahin, David und seine Männer auf der anderen Seite des Berges. David versuchte, rasch von Saul wegzukommen, während Saul und seine Männer schon dabei waren, ihn und seine Leute zu umzingeln, um sie gefangen zu nehmen." },
      { number: 27, text: "Doch da kam ein Bote zu Saul und sagte: Komm schnell, die Philister sind in das Land eingefallen." },
      { number: 28, text: "Saul ließ von der Verfolgung Davids ab und zog den Philistern entgegen. Deshalb nennt man diesen Ort Sela-Machlekot (Fels der Trennung)." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Von dort zog David hinauf und setzte sich in den schwer zugänglichen Bergen bei En-Gedi fest.", heading: "Davids Achtung vor Sauls Leben: 24,1-23", footnotes: ["En-Gedi: am Westufer des Toten Meeres."] },
      { number: 2, text: "Als Saul von der Verfolgung der Philister zurückkehrte, berichtete man ihm: Gib Acht, David ist in der Steppe von En-Gedi." },
      { number: 3, text: "Da nahm Saul dreitausend Mann, ausgesuchte Leute aus ganz Israel, und zog aus, um David und seine Männer bei den Steinbock-Felsen zu suchen." },
      { number: 4, text: "Auf seinem Weg kam er zu einigen Schafhürden. Dort war eine Höhle. Saul ging hinein, um seine Notdurft zu verrichten. David aber und seine Männer saßen hinten in der Höhle." },
      { number: 5, text: "Da sagten die Männer zu David: Das ist der Tag, von dem der Herr zu dir gesagt hat: Sieh her, ich gebe deinen Feind in deine Gewalt und du kannst mit ihm machen, was dir richtig erscheint. Da stand David auf und schnitt heimlich einen Zipfel von Sauls Mantel ab." },
      { number: 6, text: "Hinterher aber schlug David das Gewissen, weil er einen Zipfel vom Mantel Sauls abgeschnitten hatte.", footnotes: ["6f: Die Tat konnte auch symbolisch als Wegreißen der Herrschaft oder Minderung der Lebenskraft verstanden werden."] },
      { number: 7, text: "Er sagte zu seinen Männern: Der Herr bewahre mich davor, meinem Gebieter, dem Gesalbten des Herrn, so etwas anzutun und Hand an ihn zu legen; denn er ist der Gesalbte des Herrn.", footnotes: ["26,9; 2 Sam 1,14"] },
      { number: 8, text: "Und David fuhr seine Leute mit scharfen Worten an und ließ nicht zu, dass sie sich an Saul vergriffen. Als Saul die Höhle verlassen hatte und seinen Weg fortsetzte," },
      { number: 9, text: "stand auch David auf, verließ die Höhle und rief Saul nach: Mein Herr und König! Als Saul sich umblickte, verneigte sich David bis zur Erde und warf sich (vor ihm) nieder." },
      { number: 10, text: "Dann sagte David zu Saul: Warum hörst du auf die Worte von Leuten, die sagen: Gib acht, David will dein Verderben." },
      { number: 11, text: "Doch heute kannst du mit eigenen Augen sehen, dass der Herr dich heute in der Höhle in meine Gewalt gegeben hat. Man hat mir gesagt, ich solle dich töten; aber ich habe dich geschont. Ich sagte: Ich will nicht die Hand an meinen Herrn legen; denn er ist der Gesalbte des Herrn.", footnotes: ["Ich habe dich geschont: Text korr., vgl. die alten Übersetzungen."] },
      { number: 12, text: "Sieh her, mein Vater! Hier, der Zipfel deines Mantels ist in meiner Hand. Wenn ich einen Zipfel deines Mantels abgeschnitten und dich nicht getötet habe, dann kannst du erkennen und einsehen, dass ich weder Bosheit noch Aufruhr im Sinn habe und dass ich mich nicht gegen dich versündigt habe; du aber stellst mir nach, um mir das Leben zu nehmen." },
      { number: 13, text: "Der Herr soll zwischen mir und dir entscheiden. Der Herr soll mich an dir rächen; aber meine Hand wird dich nicht anrühren," },
      { number: 14, text: "wie das alte Sprichwort sagt: Von den Frevlern geht Frevel aus; aber meine Hand soll dich nicht anrühren.", footnotes: ["David will sagen: Ich bin kein Frevler."] },
      { number: 15, text: "Hinter wem zieht der König von Israel her? Wem jagst du nach? Einem toten Hund, einem einzigen Floh!", footnotes: ["26,20"] },
      { number: 16, text: "Der Herr soll unser Richter sein und zwischen mir und dir entscheiden. Er blicke her, er soll meinen Rechtsstreit führen und mir dir gegenüber Recht verschaffen." },
      { number: 17, text: "Als David das zu Saul gesagt hatte, antwortete Saul: Ist das nicht deine Stimme, mein Sohn David? Und Saul begann laut zu weinen" },
      { number: 18, text: "und sagte zu David: Du bist gerechter als ich; denn du hast mir Gutes erwiesen, während ich böse an dir gehandelt habe." },
      { number: 19, text: "Du hast heute bewiesen, dass du gut an mir gehandelt hast; obwohl der Herr mich in deine Gewalt gegeben hatte, hast du mich nicht getötet." },
      { number: 20, text: "Wenn jemand auf seinen Feind trifft, lässt er ihn dann im Guten seinen Weg weiterziehen? Der Herr möge dir mit Gutem vergelten, was du mir heute getan hast." },
      { number: 21, text: "Jetzt weiß ich, dass du König werden wirst und dass das Königtum in deiner Hand Bestand haben wird." },
      { number: 22, text: "Darum schwöre mir nun beim Herrn, dass du meine Nachkommen nicht ausrotten und meinen Namen nicht aus dem Haus meines Vaters austilgen wirst." },
      { number: 23, text: "Und David schwor es Saul. Saul zog nach Hause, David aber und seine Männer stiegen wieder in die unzugänglichen Berge hinauf." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Samuel starb und ganz Israel versammelte sich und hielt für ihn die Totenklage. Man begrub ihn in seinem Haus in Rama. Danach brach David auf und zog in die Steppe Paran hinab.", heading: "David und Abigajil: 25,1-44", footnotes: ["Paran ist ein Steppengebiet im Norden der Sinaihalbinsel."] },
      { number: 2, text: "Damals lebte in Maon ein Mann, der sein Gut in Karmel hatte. Der Mann war sehr reich; er besaß dreitausend Schafe und tausend Ziegen. Er war eben dabei, in Karmel seine Schafe zu scheren.", footnotes: ["Karmel: vgl. die Anmerkung zu 15,12."] },
      { number: 3, text: "Der Mann hieß Nabal und seine Frau Abigajil. Die Frau war klug und von schöner Gestalt, aber der Mann war roh und bösartig; er war ein Kalebiter." },
      { number: 4, text: "David hörte in der Steppe, dass Nabal dabei war, seine Schafe zu scheren." },
      { number: 5, text: "Er schickte zehn junge Männer hin und sagte zu ihnen: Geht hinauf nach Karmel, und wenn ihr zu Nabal kommt, entbietet ihm in meinem Namen den Friedensgruß" },
      { number: 6, text: "und sagt so zu meinem Bruder: Friede sei mit dir, Friede mit deinem Haus, Friede mit allem, was dir gehört.", footnotes: ["zu meinem Bruder: Text korr., vgl. Vg."] },
      { number: 7, text: "Ich habe soeben gehört, dass du bei der Schafschur bist. Nun sind deine Hirten mit ihren Schafen bei uns gewesen; wir haben ihnen nichts zuleide getan und sie haben nichts vermisst, solange sie in Karmel waren." },
      { number: 8, text: "Frag deine jungen Leute, sie werden es dir bestätigen. Mögen also meine jungen Männer dein Wohlwollen finden; wir sind ja an deinem Festtag gekommen. Darum gib deinen Knechten und deinem Sohn David, was du gerade zur Hand hast.", footnotes: ["Die Schafschur wird mit einem Fest abgeschlossen (vgl. Sam 13,23)."] },
      { number: 9, text: "Davids junge Leute kamen zu Nabal und redeten im Auftrag Davids, wie er es gesagt hatte, mit ihm; danach warteten sie ruhig ab." },
      { number: 10, text: "Nabal aber gab den Knechten Davids zur Antwort: Wer ist denn David, wer ist der Sohn Isais? Heute gibt es viele Knechte, die ihren Herren davongelaufen sind." },
      { number: 11, text: "Soll ich etwa mein Brot und mein Wasser und was ich für meine Schafscherer geschlachtet habe, nehmen und es Männern geben, von denen ich nicht einmal weiß, woher sie sind?" },
      { number: 12, text: "Die Leute Davids kehrten um und kamen (zu David zurück). Als sie angekommen waren, berichteten sie ihm alles, was Nabal gesagt hatte." },
      { number: 13, text: "Da sagte David zu seinen Männern: Jeder hänge sein Schwert um. Alle hängten ihr Schwert um; auch David hängte sein Schwert um. Und sie zogen mit David hinauf, etwa vierhundert Mann, während zweihundert beim Gepäck blieben." },
      { number: 14, text: "Inzwischen hatte einer der jungen Männer Abigajil, der Frau Nabals, berichtet: David hat aus der Steppe Boten geschickt, um unserem Herrn seine Segenswünsche entbieten zu lassen, aber er hat sie schroff abgewiesen." },
      { number: 15, text: "Dabei waren die Männer sehr freundlich zu uns. Wir wurden nicht beschimpft und wir haben nie etwas vermisst in der ganzen Zeit, in der wir mit ihnen umherzogen, als wir auf der Weide waren." },
      { number: 16, text: "Sie haben bei Tag und bei Nacht eine Mauer um uns gebildet, die ganze Zeit hindurch, da wir mit ihnen zusammen waren und unsere Schafe hüteten." },
      { number: 17, text: "Nun aber überleg dir genau, was du tun kannst; denn unserem Herrn und seinem ganzen Haus droht Unheil. Er ist ein so übler Mensch, dass man nicht mit ihm reden kann." },
      { number: 18, text: "Da nahm Abigajil in aller Eile zweihundert Brote und zwei Schläuche Wein, fünf schon zurechtgemachte Schafe, fünf Sea geröstetes Korn, hundert Rosinenkuchen und zweihundert Feigenkuchen, lud alles auf Esel" },
      { number: 19, text: "und sagte zu ihren Knechten: Geht mir schon voraus, ich komme euch gleich nach. Ihrem Mann Nabal aber teilte sie nichts davon mit." },
      { number: 20, text: "Als sie auf ihrem Esel im Schutz eines Berges hinabritt, kamen ihr plötzlich David und seine Männer entgegen, sodass sie mit ihnen zusammentraf." },
      { number: 21, text: "David hatte eben gesagt: Wahrhaftig, ich habe all das, was diesem Mann gehört, ganz umsonst in der Steppe beschützt; von all seinem Besitz wurde nichts vermisst, doch er hat mir Gutes mit Bösem vergolten." },
      { number: 22, text: "Gott möge mir dies und das antun, wenn ich von allem, was ihm gehört, bis zum Morgen auch nur einen Mann übriglasse.", footnotes: ["mir: Text korr., vgl. G; H: den Feinden Davids."] },
      { number: 23, text: "Als Abigajil David sah, stieg sie schnell von ihrem Esel, warf sich vor David nieder und verneigte sich bis zur Erde." },
      { number: 24, text: "Sie fiel ihm zu Füßen und sagte: Mich allein, Herr, trifft die Schuld. Möge deine Magd mit dir reden dürfen; höre, was deine Magd zu sagen hat." },
      { number: 25, text: "Mein Herr achte nicht auf diesen üblen Mann Nabal; denn wie sein Name sagt, so ist er: Nabal (Tor) heißt er und voll Torheit ist er. Ich, deine Magd, habe die jungen Leute, die du, mein Herr, geschickt hast, nicht gesehen." },
      { number: 26, text: "Doch nun, so wahr der Herr lebt und so wahr du selbst lebst, hat dich der Herr davor bewahrt, Blutschuld auf dich zu laden und dir selbst zu helfen. Möge es deinen Feinden und allen, die gegen meinen Herrn Böses planen, ebenso ergehen wie Nabal." },
      { number: 27, text: "Dieses Geschenk aber, das deine Magd meinem Herrn mitgebracht hat, möge jetzt den jungen Leuten gegeben werden, die meinem Herrn folgen." },
      { number: 28, text: "Verzeih deiner Magd ihr Vergehen! Denn der Herr wird meinem Herrn sicher ein Haus errichten, das Bestand hat, weil mein Herr die Kriege des Herrn führt, und man wird dir nichts Böses vorwerfen können, solange du lebst." },
      { number: 29, text: "Wenn sich aber ein Mensch erhebt, um dich zu verfolgen und dir nach dem Leben zu trachten, dann sei das Leben meines Herrn beim Herrn, deinem Gott, eingebunden in den Beutel des Lebens; das Leben deiner Feinde aber möge der Herr mit einer Schleuder fortschleudern.", footnotes: ["Die Wendung «Beutel des Lebens» hängt damit zusammen, dass man kostbare Dinge (Münzen, Gold) in einem versiegelten Beutel aufbewahrt. So sorgfältig soll Davids Leben verwahrt werden."] },
      { number: 30, text: "Wenn dann der Herr meinem Herrn all das Gute erweist, das er dir versprochen hat, und dich zum Fürsten über Israel macht,", footnotes: ["2 Sam 5,2"] },
      { number: 31, text: "dann sollst du nicht darüber stolpern und dein Gewissen soll meinem Herrn nicht vorwerfen können, dass du ohne Grund Blut vergossen hast und dass sich mein Herr selbst geholfen hat. Wenn der Herr aber meinem Herrn Gutes erweist, dann denk an deine Magd!" },
      { number: 32, text: "Da sagte David zu Abigajil: Gepriesen sei der Herr, der Gott Israels, der dich mir heute entgegengeschickt hat." },
      { number: 33, text: "Gepriesen sei deine Klugheit und gepriesen seist du, weil du mich heute daran gehindert hast, Blutschuld auf mich zu laden und mir selbst zu helfen." },
      { number: 34, text: "Aber so wahr der Herr, der Gott Israels, lebt, der mich davon abgehalten hat, dir etwas Böses zu tun: Wärest du mir nicht so schnell entgegengekommen, dann wäre von Nabals Männern am anderen Morgen keiner mehr übrig gewesen." },
      { number: 35, text: "Und David nahm von ihr entgegen, was sie ihm gebracht hatte, und sagte zu ihr: Geh in Frieden hinauf in dein Haus! Denk aber daran: Ich habe auf dich gehört und dich gnädig aufgenommen." },
      { number: 36, text: "Als Abigajil zu Nabal kam, veranstaltete er in seinem Haus gerade ein Trinkgelage wie ein König. Nabal war in fröhlicher Stimmung, aber völlig betrunken. Darum erzählte sie ihm mit keinem Wort, was geschehen war, bis zum anderen Morgen.", footnotes: ["Der Versschluß lautet wörtlich: erzählte sie ihm weder Kleines noch Großes bis zum Morgenlicht."] },
      { number: 37, text: "Als dann am Morgen der Rausch Nabals vorüber war, berichtete ihm seine Frau, was sich zugetragen hatte. Da versagte das Herz in seiner Brust und er war wie versteinert." },
      { number: 38, text: "Nach etwa zehn Tagen schlug der Herr den Nabal, sodass er starb." },
      { number: 39, text: "Als David hörte, dass Nabal tot sei, sagte er: Gepriesen sei der Herr, der meinen Rechtsstreit gegen Nabal wegen der Schmach, die mir angetan wurde, geführt hat und der seinen Knecht von einer bösen Tat zurückgehalten hat; die Bosheit Nabals aber hat der Herr auf ihn selbst zurückfallen lassen. Darauf schickte David (Boten) zu Abigajil (mit dem Angebot), er wolle sie zur Frau nehmen." },
      { number: 40, text: "Die Diener Davids kamen zu Abigajil nach Karmel, redeten mit ihr und sagten: David schickt uns zu dir, weil er dich zur Frau nehmen will." },
      { number: 41, text: "Sie stand auf, verneigte sich bis zur Erde und sagte: Deine Magd steht als Dienerin bereit, um den Dienern meines Herrn die Füße zu waschen." },
      { number: 42, text: "Dann machte sich Abigajil in aller Eile auf, setzte sich auf ihren Esel und ihre fünf Mägde folgten ihr. Sie zog den Boten Davids nach und wurde seine Frau.", footnotes: ["2 Sam 2,2"] },
      { number: 43, text: "Aus Jesreel hatte sich David zuvor schon Ahinoam zur Frau genommen. So hatte er zwei Frauen." },
      { number: 44, text: "Saul aber hatte seine Tochter Michal, die Frau Davids, Palti, dem Sohn des Lajisch aus Gallim, gegeben.", footnotes: ["2 Sam 3,14f"] },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Die Sifiter kamen zu Saul nach Gibea und sagten: David hält sich auf der Anhöhe von Hachila gegenüber von Jeschimon auf.", heading: "Erneute Schonung von Sauls Leben: 26,1-25", footnotes: ["23,19; Ps 54,2"] },
      { number: 2, text: "Saul machte sich mit dreitausend Mann, ausgesuchten Kriegern aus Israel, auf den Weg und zog in die Wüste von Sif hinab, um dort nach David zu suchen." },
      { number: 3, text: "Er schlug sein Lager auf der Anhöhe von Hachila am Weg gegenüber von Jeschimon auf, David aber blieb in der Wüste. Als er sah, dass Saul ihm in die Wüste folgte," },
      { number: 4, text: "schickte er Kundschafter aus und erfuhr, an welchem Ort sich Saul aufhielt." },
      { number: 5, text: "Er brach auf und kam zu dem Ort, wo Saul sein Lager hatte. Und David konnte die Stelle sehen, wo Saul sich mit seinem Heerführer Abner, dem Sohn Ners, zur Ruhe hingelegt hatte: Saul schlief mitten im Lager, während seine Leute rings um ihn herum lagen." },
      { number: 6, text: "Da wandte sich David an den Hetiter Ahimelech und an Abischai, den Sohn der Zeruja, den Bruder Joabs, und sagte: Wer geht mit mir zu Saul ins Lager hinab? Abischai antwortete: Ich gehe mit." },
      { number: 7, text: "So kamen David und Abischai in der Nacht zu den Leuten (Sauls) und fanden Saul mitten im Lager schlafend; sein Speer steckte neben seinem Kopf in der Erde und rings um ihn schliefen Abner und seine Leute." },
      { number: 8, text: "Da sagte Abischai zu David: Heute hat Gott deinen Feind in deine Hand gegeben. Jetzt werde ich ihn mit einem einzigen Speerstoß auf den Boden spießen, einen zweiten brauche ich nicht dafür." },
      { number: 9, text: "David aber erwiderte Abischai: Bring ihn nicht um! Denn wer hat je seine Hand gegen den Gesalbten des Herrn erhoben und ist ungestraft geblieben?" },
      { number: 10, text: "Und er fügte hinzu: So wahr der Herr lebt: Der Herr möge ihn schlagen, ob nun der Tag kommt, an dem er sterben muss, oder ob er in den Krieg zieht und dort umkommt." },
      { number: 11, text: "Mich aber bewahre der Herr davor, dass ich meine Hand gegen den Gesalbten des Herrn erhebe. Nimm jetzt den Speer neben seinem Kopf und den Wasserkrug und lass uns gehen!" },
      { number: 12, text: "David nahm den Speer und den Wasserkrug, die neben Sauls Kopf waren, und sie gingen weg. Niemand sah und niemand bemerkte etwas, und keiner wachte auf; alle schliefen, denn der Herr hatte sie in einen tiefen Schlaf fallen lassen." },
      { number: 13, text: "David ging auf die andere Seite (des Tals) hinüber und stellte sich in größerer Entfernung auf den Gipfel des Berges, sodass ein weiter Zwischenraum zwischen ihnen war." },
      { number: 14, text: "Dann rief er dem Volk und Abner, dem Sohn Ners, zu: Abner, willst du antworten? Abner antwortete und sagte: Wer bist du, warum rufst du nach dem König?" },
      { number: 15, text: "David antwortete Abner: Bist du nicht ein Mann, dem keiner in Israel gleicht? Warum hast du deinen Herrn, den König, nicht bewacht? Es ist nämlich einer aus dem Volk ins Lager eingedrungen, um den König, deinen Herrn, umzubringen." },
      { number: 16, text: "Das war nicht gut, was du da gemacht hast. So wahr der Herr lebt: Ihr habt den Tod verdient, weil ihr euren Herrn, den Gesalbten des Herrn, nicht bewacht habt. Sieh doch nach, wo der Speer des Königs und der Wasserkrug sind, die neben dem Kopf des Königs standen." },
      { number: 17, text: "Saul erkannte die Stimme Davids und sagte: Ist das deine Stimme, mein Sohn David? David antwortete: Es ist meine Stimme, mein Herr und König." },
      { number: 18, text: "Dann fragte er: Warum verfolgt eigentlich mein Herr seinen Knecht? Was habe ich denn getan? Welches Unrecht habe ich begangen?" },
      { number: 19, text: "Möge doch mein Herr, der König, jetzt auf die Worte seines Knechtes hören: Wenn der Herr dich gegen mich aufgereizt hat, möge er ein wohlriechendes Opfer erhalten. Wenn es aber Menschen waren, dann sollen sie verflucht sein vor dem Herrn; denn sie haben mich vertrieben, sodass ich jetzt nicht mehr am Erbbesitz des Herrn teilhaben kann. Sie sagen: Geh fort, diene anderen Göttern!", footnotes: ["Gen 8,21; 4,14.16", "Hier wird die Meinung vertreten, dass ein Gott nur in seinem Land verehrt werden kann."] },
      { number: 20, text: "Doch mein Blut soll nicht fern vom Herrn zur Erde fließen. Der König von Israel ist ausgezogen, um einen einzigen Floh zu suchen, wie man in den Bergen ein Rebhuhn jagt.", footnotes: ["24,15"] },
      { number: 21, text: "Darauf sagte Saul: Ich habe gesündigt. Komm zurück, mein Sohn David! Ja, ich werde dir nichts zuleide tun, weil dir heute mein Leben so kostbar war. Ich sehe ein, ich habe töricht gehandelt und schwere Fehler gemacht.", footnotes: ["2 Kön 1,13f"] },
      { number: 22, text: "David erwiderte: Seht her, hier ist der Speer des Königs. Einer von den jungen Männern soll herüberkommen und ihn holen." },
      { number: 23, text: "Der Herr wird jedem seine Gerechtigkeit und Treue vergelten. Obwohl dich der Herr heute in meine Hand gegeben hatte, wollte ich meine Hand nicht an den Gesalbten des Herrn legen." },
      { number: 24, text: "Doch denk daran: Wie dein Leben heute in meinen Augen wertvoll war, so wird auch mein Leben in den Augen des Herrn wertvoll sein; er wird mich aus aller Bedrängnis erretten." },
      { number: 25, text: "Saul sagte zu David: Gesegnet seist du, mein Sohn David. Du wirst es sicher vollbringen, dir wird es auch bestimmt gelingen. Und David zog weiter, Saul aber kehrte an seinen Ort zurück." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "David überlegte: Eines Tages werde ich doch noch durch Saul umgebracht. Es bleibt nichts anderes übrig, als mich im Land der Philister in Sicherheit zu bringen. Dann wird Saul mich in Ruhe lassen und aufhören, mich weiter im ganzen Gebiet Israels zu suchen, und ich bin seiner Hand entkommen.", heading: "David bei den Philistern: 27,1 - 28,2", footnotes: ["23,23"] },
      { number: 2, text: "David machte sich also auf den Weg und ging mit den sechshundert Männern, die bei ihm waren, zu Achisch hinüber, dem Sohn Maochs, dem König von Gat.", footnotes: ["21,11-16"] },
      { number: 3, text: "Und er blieb mit seinen Leuten bei Achisch in Gat; alle hatten ihre Familien bei sich, David seine beiden Frauen: Ahinoam, die Jesreeliterin, und Abigajil aus Karmel, die (frühere) Frau Nabals." },
      { number: 4, text: "Als man Saul meldete, dass David nach Gat geflohen war, suchte er nicht mehr weiter nach ihm." },
      { number: 5, text: "David sagte zu Achisch: Wenn ich dein Wohlwollen gefunden habe, dann weise mir einen Platz in einer der Städte des flachen Landes zu, wo ich mich niederlassen kann. Warum soll dein Knecht bei dir in der Königsstadt wohnen?" },
      { number: 6, text: "Da gab ihm Achisch noch am gleichen Tag Ziklag. Deshalb gehört Ziklag den Königen von Juda bis zum heutigen Tag.", footnotes: ["30,1"] },
      { number: 7, text: "Die Zeit, die David im Land der Philister verbrachte, betrug ein Jahr und vier Monate." },
      { number: 8, text: "David zog mit seinen Männern aus und sie unternahmen Raubzüge bei den Geschuritern, den Geresitern und den Amalekitern; diese bewohnen von jeher das Gebiet in Richtung Schur und nach Ägypten zu." },
      { number: 9, text: "David verheerte das Land und ließ weder Männer noch Frauen am Leben; Schafe und Rinder, Esel, Kamele und Kleider aber nahm er mit. Wenn er dann zurückkehrte und zu Achisch kam" },
      { number: 10, text: "und Achisch ihn fragte: Wohin habt ihr heute euren Raubzug gemacht?, antwortete David: Ins Südland von Juda, oder: Ins Südland der Jerachmeeliter, oder: Ins Südland der Keniter.", footnotes: ["30,29"] },
      { number: 11, text: "Weder Männer noch Frauen ließ er am Leben und er brachte niemand nach Gat; denn er sagte sich: Niemand soll etwas über uns berichten und sagen können: Das und das hat David gemacht. So hielt er es die ganze Zeit über, solange er sich im Land der Philister aufhielt." },
      { number: 12, text: "Achisch aber schenkte David Vertrauen, denn er sagte sich: Er hat sich bei seinem Volk, bei den Israeliten, so verhasst gemacht, dass er für immer mein Knecht bleiben muss." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "In jenen Tagen sammelten die Philister ihre Truppen, um gegen Israel in den Kampf zu ziehen. Da sagte Achisch zu David: Du weißt sehr wohl, dass du samt deinen Männern mit mir ins Feldlager ziehen musst." },
      { number: 2, text: "David antwortete Achisch: Auf diese Weise wirst du selbst erfahren, was dein Knecht tun wird. Achisch sagte zu David: Gut, ich mache dich für diese ganze Zeit zu meinem Leibwächter." },
      { number: 3, text: "Samuel war gestorben und ganz Israel hatte die Totenklage für ihn gehalten und ihn in seiner Stadt Rama begraben. Saul aber hatte die Totenbeschwörer und die Wahrsager aus dem Land vertrieben.", heading: "Saul bei der Totenbeschwörerin von En-Dor: 28,3-25", footnotes: ["25,1"] },
      { number: 4, text: "Als sich die Philister gesammelt hatten, rückten sie heran und schlugen bei Schunem ihr Lager auf. Saul versammelte ganz Israel und sie schlugen ihr Lager im (Bergland von) Gilboa auf.", footnotes: ["Gilboa: ein Bergzug im Südosten der Jesreelebene."] },
      { number: 5, text: "Als Saul das Lager der Philister sah, bekam er große Angst und sein Herz begann zu zittern." },
      { number: 6, text: "Da befragte Saul den Herrn, aber der Herr gab ihm keine Antwort, weder durch Träume, noch durch die Losorakel, noch durch die Propheten." },
      { number: 7, text: "Daher sagte Saul zu seinen Dienern: Sucht mir eine Frau, die Gewalt über einen Totengeist hat; ich will zu ihr gehen und sie befragen. Seine Diener antworteten ihm: In En-Dor gibt es eine Frau, die über einen Totengeist Gewalt hat." },
      { number: 8, text: "Da machte sich Saul unkenntlich, zog andere Kleider an und ging mit zwei Männern zu der Frau. Sie kamen in der Nacht bei der Frau an und er sagte zu ihr: Wahrsage mir durch den Totengeist! Lass für mich den heraufsteigen, den ich dir nenne.", footnotes: ["1 Chr 10,13"] },
      { number: 9, text: "Die Frau antwortete ihm: Du weißt doch selbst, was Saul getan hat: Er hat die Totenbeschwörer und die Wahrsager im Land ausgerottet. Warum stellst du mir eine Falle, um mich zu töten?" },
      { number: 10, text: "Saul aber schwor ihr beim Herrn und sagte: So wahr der Herr lebt: Es soll dich in dieser Sache keine Schuld treffen." },
      { number: 11, text: "Die Frau sagte: Wen soll ich für dich heraufsteigen lassen? Er antwortete: Lass Samuel für mich heraufsteigen!" },
      { number: 12, text: "Als die Frau Samuel erblickte, schrie sie laut auf und sagte zu Saul: Warum hast du mich getäuscht? Du bist ja Saul!" },
      { number: 13, text: "Der König sagte zu ihr: Hab keine Angst! Was siehst du denn? Die Frau antwortete Saul: Ich sehe einen Geist aus der Erde heraufsteigen." },
      { number: 14, text: "Er fragte sie: Wie sieht er aus? Sie antwortete: Ein alter Mann steigt herauf; er ist in einen Mantel gehüllt. Da erkannte Saul, dass es Samuel war. Er verneigte sich mit dem Gesicht zur Erde und warf sich zu Boden." },
      { number: 15, text: "Und Samuel sagte zu Saul: Warum hast du mich aufgestört und mich heraufsteigen lassen? Saul antwortete: Ich bin in großer Bedrängnis. Die Philister führen Krieg gegen mich und Gott ist von mir gewichen und hat mir keine Antwort mehr gegeben, weder durch die Propheten noch durch die Träume. Darum habe ich dich gerufen, damit du mir sagst, was ich tun soll." },
      { number: 16, text: "Samuel erwiderte: Warum fragst du mich? Der Herr ist doch von dir gewichen und ist dein Feind geworden." },
      { number: 17, text: "Er hat getan, was er durch mich angekündigt hatte: Der Herr hat dir das Königtum aus der Hand gerissen und hat es einem anderen, nämlich David, gegeben.", footnotes: ["15,28"] },
      { number: 18, text: "Weil du nicht auf die Stimme des Herrn gehört und seinen glühenden Zorn an Amalek nicht vollstreckt hast, darum hat dir der Herr heute das getan.", footnotes: ["15,18f; Ex 17,14-16"] },
      { number: 19, text: "Der Herr wird auch Israel zusammen mit dir in die Gewalt der Philister geben und morgen wirst du samt deinen Söhnen bei mir sein; auch das Heerlager Israels wird der Herr in die Gewalt der Philister geben.", footnotes: ["31,6"] },
      { number: 20, text: "Da fiel Saul der Länge nach jäh zu Boden; so sehr war er über die Worte Samuels erschrocken. Es war auch keine Kraft mehr in ihm, weil er den ganzen Tag und die ganze Nacht keinen Bissen gegessen hatte." },
      { number: 21, text: "Die Frau ging zu Saul hin und sah, dass er ganz verstört war; sie sagte zu ihm: Deine Magd hat auf deine Stimme gehört; ich habe mein Leben aufs Spiel gesetzt, als ich auf das hörte, was du zu mir gesagt hast." },
      { number: 22, text: "Jetzt aber höre auch du auf die Stimme deiner Magd! Ich will dir ein Stück Brot zum Essen geben. Dann wirst du wieder zu Kräften kommen und kannst deines Weges gehen." },
      { number: 23, text: "Er aber weigerte sich und sagte: Ich esse nichts. Doch seine Diener und die Frau drängten ihn, bis er auf ihre Stimme hörte. Er erhob sich vom Boden und setzte sich aufs Bett." },
      { number: 24, text: "Die Frau hatte ein Mastkalb im Haus. Sie schlachtete es in aller Eile, nahm Mehl, knetete Teig und backte ungesäuerte Brote." },
      { number: 25, text: "Das alles setzte sie Saul und seinen Knechten vor; sie aßen, standen auf und gingen noch in der gleichen Nacht zurück." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Die Philister versammelten ihr ganzes Heer bei Afek, während die Israeliten ihr Lager an der Quelle bei Jesreel aufgeschlagen hatten.", heading: "Davids Entlassung aus dem Heer der Philister: 29,1-11" },
      { number: 2, text: "Als nun die Fürsten der Philister mit ihren Hundertschaften und Tausendschaften auf dem Marsch waren - David und seine Männer zogen mit Achisch am Schluss -," },
      { number: 3, text: "fragten die Obersten der Philister: Was sollen diese Hebräer hier? Achisch antwortete den Obersten der Philister: Das ist doch David, der Knecht Sauls, des Königs von Israel, der seit Jahr und Tag bei mir ist. Seit dem Tag, da er übergelaufen ist, bis heute fand ich bei ihm nichts Nachteiliges." },
      { number: 4, text: "Die Obersten der Philister aber wurden zornig auf ihn und sagten zu ihm: Schick den Mann zurück! Er soll an den Ort zurückkehren, den du ihm zugewiesen hast, und nicht mit uns in den Kampf ziehen. Dann kann er sich in der Schlacht nicht gegen uns wenden. Womit könnte er sich die Gunst seines Herrn besser erwerben als mit den Köpfen unserer Leute hier?" },
      { number: 5, text: "Ist das nicht der gleiche David, von dem man beim Reigentanz gesungen hat: Saul hat Tausend erschlagen, David aber Zehntausend?", footnotes: ["18,7"] },
      { number: 6, text: "Da rief Achisch David zu sich und sagte zu ihm: So wahr Jahwe lebt, du bist ein aufrechter Mann und es wäre in meinen Augen gut, wenn du mit mir ins Feld ziehen und wieder zurückkommen würdest; denn ich habe an dir nichts Unrechtes entdeckt seit dem Tag, an dem du zu mir gekommen bist, bis heute. Aber den Fürsten bist du nicht genehm." },
      { number: 7, text: "Darum kehr jetzt um und zieh in Frieden; so wirst du nichts tun, was den Fürsten der Philister missfällt." },
      { number: 8, text: "David sagte zu Achisch: Was habe ich denn getan? Was hast du an deinem Knecht auszusetzen gehabt von dem Tag an, an dem ich in deinen Dienst getreten bin, bis heute? Warum darf ich nicht mitkommen und gegen die Feinde meines Herrn, des Königs, kämpfen?" },
      { number: 9, text: "Achisch antwortete David: Gewiss, mir bist du teuer wie ein Engel Gottes; aber die Obersten der Philister haben gesagt: Er soll nicht mit uns in den Kampf ziehen." },
      { number: 10, text: "Darum brich morgen früh auf, samt den Knechten deines Herrn, die mit dir gekommen sind. Brecht morgen früh auf, sobald es hell wird, und geht (nach Hause)!" },
      { number: 11, text: "David machte sich also mit seinen Männern in aller Frühe auf den Weg; er zog noch am Morgen weg und kehrte ins Land der Philister zurück; die Philister aber zogen nach Jesreel hinauf." },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Als David und seine Männer am dritten Tag nach Ziklag kamen, waren die Amalekiter in den Negeb und in Ziklag eingefallen und hatten Ziklag erobert und niedergebrannt.", heading: "Davids Feldzug gegen die Amalekiter: 30,1-31", footnotes: ["27,6"] },
      { number: 2, text: "Die Frauen und was sonst in der Stadt war, Jung und Alt, hatten sie, ohne jemand zu töten, gefangen genommen und bei ihrem Abzug mit sich weggeführt." },
      { number: 3, text: "Als David mit seinen Männern zur Stadt kam, sah er, dass sie niedergebrannt und die Frauen, Söhne und Töchter gefangen genommen worden waren." },
      { number: 4, text: "Da brachen David und die Leute, die bei ihm waren, in lautes Weinen aus und sie weinten, bis sie keine Kraft mehr zum Weinen hatten." },
      { number: 5, text: "Auch die beiden Frauen Davids, die Jesreeliterin Ahinoam und Abigajil, die (frühere) Frau Nabals aus Karmel, waren gefangen genommen worden." },
      { number: 6, text: "David geriet in große Bedrängnis, denn das Volk drohte ihn zu steinigen; alle im Volk waren gegen ihn erbittert wegen (des Verlustes) ihrer Söhne und Töchter. Aber David fühlte, dass der Herr, sein Gott, ihm Kraft gab." },
      { number: 7, text: "Er sagte zu dem Priester Abjatar, dem Sohn Ahimelechs: Bring das Efod zu mir! Abjatar brachte das Efod zu David." },
      { number: 8, text: "Und David befragte den Herrn: Soll ich diese Räuberbande verfolgen? Werde ich sie einholen? Der Herr antwortete: Verfolg sie! Denn du wirst sie mit Sicherheit einholen und deine Leute befreien." },
      { number: 9, text: "David brach also mit den sechshundert Mann, die bei ihm waren, auf und sie kamen bis zum Bach Besor. Die Nachzügler machten dort Rast." },
      { number: 10, text: "David nahm mit vierhundert Mann die Verfolgung auf; die zweihundert Mann machten Rast, weil sie zu erschöpft waren, um den Bach Besor zu überschreiten." },
      { number: 11, text: "Man griff dort im Gelände einen Ägypter auf und brachte ihn zu David. Sie gaben ihm Brot zu essen und Wasser zu trinken," },
      { number: 12, text: "außerdem gepresste Feigen und zwei Rosinenkuchen. Als er gegessen hatte, kehrten seine Lebensgeister zurück; er hatte nämlich schon drei Tage und drei Nächte keinen Bissen Brot mehr gegessen und keinen Schluck Wasser getrunken." },
      { number: 13, text: "David fragte ihn: Zu wem gehörst du und woher bist du? Er sagte: Ich bin ein junger Ägypter, der Sklave eines Amalekiters. Mein Herr hat mich zurückgelassen, als ich heute vor drei Tagen krank wurde." },
      { number: 14, text: "Wir waren in das Südland der Kereter und in das Gebiet von Juda und in das Südland von Kaleb eingefallen und hatten Ziklag niedergebrannt." },
      { number: 15, text: "David sagte zu ihm: Kannst du mich zu dieser Räuberbande hinführen? Er antwortete: Schwöre mir bei Gott, dass du mich nicht tötest und mich nicht an meinen Herrn ausliefern wirst; dann will ich dich zu dieser Räuberbande hinführen." },
      { number: 16, text: "Als er David hinführte, sah man die Amalekiter über die ganze Gegend verstreut; sie aßen und tranken und feierten, weil sie im Land der Philister und im Land Juda so reiche Beute gemacht hatten." },
      { number: 17, text: "David fiel im Morgengrauen über sie her (und der Kampf dauerte) bis zum Abend des folgenden Tages; keiner von ihnen entkam, außer vierhundert jungen Männern, die sich auf ihre Kamele setzen und fliehen konnten." },
      { number: 18, text: "David entriss den Amalekitern alles wieder, was sie erbeutet hatten; auch seine beiden Frauen befreite er." },
      { number: 19, text: "Nichts fehlte, weder Jung noch Alt, weder Söhne noch Töchter, weder von der Beute noch sonst etwas, was die Amalekiter ihnen weggenommen hatten. Alles brachte David zurück." },
      { number: 20, text: "David nahm auch alle Schafe und Rinder mit. Man trieb sie vor David her und sagte: Das ist Davids Beute.", footnotes: ["vor David her: Text korr., vgl. Vg."] },
      { number: 21, text: "David kam zu den zweihundert Mann zurück, die zu erschöpft gewesen waren, um ihm zu folgen, und die man am Bach Besor zurückgelassen hatte. Sie kamen David und seinen Leuten entgegen, und als David mit seinen Leuten herankam, entbot er ihnen den Friedensgruß." },
      { number: 22, text: "Doch einige boshafte und nichtsnutzige Männer, die mit David gezogen waren, sagten: Sie sind nicht mit uns gezogen; darum wollen wir ihnen auch nichts von der Beute geben, die wir zurückgeholt haben, ausgenommen ihre Frauen und ihre Kinder. Diese sollen sie mitnehmen und (nach Hause) gehen." },
      { number: 23, text: "David aber erwiderte: So dürft ihr es mit dem, was der Herr uns gegeben hat, nicht machen, meine Brüder. Er hat uns behütet und die Räuberbande, die uns überfallen hatte, in unsere Hand gegeben." },
      { number: 24, text: "Wer würde in dieser Sache auf euch hören? Nein, der Anteil dessen, der beim Tross geblieben ist, soll genau so groß sein wie der Anteil dessen, der in den Kampf gezogen ist. Man soll die Beute gleichmäßig verteilen.", footnotes: ["Num 31,27"] },
      { number: 25, text: "So geschah es an jenem Tag und auch weiterhin; denn David machte es zu Brauch und Recht in Israel und so ist es bis auf den heutigen Tag geblieben." },
      { number: 26, text: "Als David dann nach Ziklag heimgekehrt war, schickte er einen Teil von der Beute an die Ältesten von Juda, die ihm nahestanden, und ließ ihnen sagen: Hier ist ein Geschenk für euch aus der Beute von den Feinden des Herrn" },
      { number: 27, text: "- an die Ältesten in Bet-El, in Ramat-Negeb, in Jattir," },
      { number: 28, text: "in Aroër, in Sifmot, in Eschtemoa," },
      { number: 29, text: "in Karmel, in den Städten der Jerachmeeliter, in den Städten der Keniter,", footnotes: ["27,10", "Karmel: Text korr. nach G. - Die in VV. 27-31 genannten Städte und Orte liegen wohl alle südlich von Hebron."] },
      { number: 30, text: "in Horma, in Bor-Aschan, in Atach," },
      { number: 31, text: "und in Hebron, ferner an all die Orte, wo David auf seinen Streifzügen mit seinen Männern gewesen war." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Als die Philister gegen Israel kämpften, flohen die Israeliten vor ihnen; viele waren gefallen und lagen erschlagen auf dem Gebirge von Gilboa.", heading: "Sauls Ende: 31,1-13", footnotes: ["(1-13) 1 Chr 10,1-12"] },
      { number: 2, text: "Die Philister verfolgten Saul und seine Söhne und erschlugen Sauls Söhne Jonatan, Abinadab und Malkischua." },
      { number: 3, text: "Um Saul selbst entstand ein schwerer Kampf. Die Bogenschützen hatten ihn getroffen und er war sehr schwer verwundet." },
      { number: 4, text: "Da sagte Saul zu seinem Waffenträger: Zieh dein Schwert und durchbohre mich damit! Sonst kommen diese Unbeschnittenen, durchbohren mich und treiben ihren Mutwillen mit mir. Der Waffenträger wollte es nicht tun; denn er hatte große Angst. Da nahm Saul selbst das Schwert und stürzte sich hinein." },
      { number: 5, text: "Als der Waffenträger sah, dass Saul tot war, stürzte auch er sich in sein Schwert und starb zusammen mit Saul." },
      { number: 6, text: "So kamen Saul, seine drei Söhne, sein Waffenträger und alle seine Männer an jenem Tag gemeinsam ums Leben.", footnotes: ["(5-6) 28,19"] },
      { number: 7, text: "Als die Israeliten auf der anderen Seite der Ebene und jenseits des Jordan sahen, dass die Israeliten geflohen und dass Saul und seine Söhne tot waren, verließen sie ihre Städte und flohen. Dann kamen die Philister und besetzten die Städte." },
      { number: 8, text: "Als am nächsten Tag die Philister kamen, um die Erschlagenen auszuplündern, fanden sie Saul und seine drei Söhne, die auf dem Gebirge von Gilboa gefallen waren." },
      { number: 9, text: "Sie schlugen ihm den Kopf ab, zogen ihm die Rüstung aus und schickten beides im Land der Philister umher, um ihrem Götzentempel und dem Volk die Siegesnachricht zu übermitteln." },
      { number: 10, text: "Die Rüstung Sauls legten sie im Astartetempel nieder; seinen Leichnam aber hefteten sie an die Mauer von Bet-Schean.", footnotes: ["Bet-Schean: südöstlich der Jesreelebene."] },
      { number: 11, text: "Als die Einwohner von Jabesch-Gilead hörten, was die Philister mit Saul gemacht hatten,", footnotes: ["(11f) 11,1-11; 2 Sam 21,12"] },
      { number: 12, text: "brachen alle kriegstüchtigen Männer auf, marschierten die ganze Nacht hindurch und nahmen die Leiche Sauls und die Leichen seiner Söhne von der Mauer von Bet-Schean ab; sie brachten sie nach Jabesch und verbrannten sie dort.", footnotes: ["sie brachten sie: Text korr. nach G; H: sie kamen."] },
      { number: 13, text: "Dann nahmen sie die Gebeine, begruben sie unter der Tamariske von Jabesch und fasteten sieben Tage lang." },
    ] },
  ],
};
