import { Book } from "@/lib/types";

export const _2chronicles: Book = {
  id: "2chronicles",
  name: "2. Chronik",
  shortName: "2.Chr",
  testament: "old",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Salomo, der Sohn Davids, gewann Macht in seinem Königtum, der Herr, sein Gott, war mit ihm und ließ ihn überaus stark werden.", heading: "Die Geschichte Salomos: 1,1 - 9,31 | Salomos Bitte um Weisheit: 1,1-13", footnotes: ["(1-13) 1 Kön 3,4-15"] },
      { number: 2, text: "Er sprach mit ganz Israel, den Obersten der Tausend- und Hundertschaften, den Richtern, mit allen Fürsten aus ganz Israel und mit den Häuptern der Großfamilien." },
      { number: 3, text: "Dann ging er mit der ganzen Versammlung, die bei ihm war, zur Kulthöhe von Gibeon; denn hier war das Offenbarungszelt Gottes, das Mose, der Knecht des Herrn, in der Wüste angefertigt hatte.", footnotes: ["Der Chronist hält das Opfer auf dem alten Brandopferaltar vor dem heiligen Zelt in Gibeon für rechtmäßig (vgl. 1 Chr 16,39f; 21,29); anders 1 Kön 3,2f."] },
      { number: 4, text: "Die Lade Gottes jedoch hatte David aus Kirjat-Jearim an den Ort bringen lassen, den er für sie hergerichtet hatte. Er hatte nämlich in Jerusalem ein Zelt für sie aufgestellt.", footnotes: ["1 Chr 15"] },
      { number: 5, text: "Der bronzene Altar, den Bezalel, der Sohn Uris und Enkel Hurs, gemacht hatte, stand in Gibeon vor der Wohnstätte des Herrn. Ihn suchten Salomo und die ganze Versammlung Israels auf.", footnotes: ["Ex 38,1-7"] },
      { number: 6, text: "Salomo stieg dort auf den bronzenen Altar vor dem Herrn beim Offenbarungszelt und brachte auf ihm tausend Brandopfer dar." },
      { number: 7, text: "In jener Nacht erschien Gott dem Salomo und forderte ihn auf: Sprich eine Bitte aus, die ich dir gewähren soll." },
      { number: 8, text: "Salomo antwortete Gott: Du hast meinem Vater David große Huld erwiesen und mich an seiner Stelle zum König gemacht.", footnotes: ["1 Chr 17"] },
      { number: 9, text: "So möge sich nun, mein Herr und Gott, dein Wort an meinen Vater David als wahr erweisen; denn du hast mich zum König gemacht über ein Volk, das zahlreich ist wie der Staub der Erde.", footnotes: ["Gen 13,16"] },
      { number: 10, text: "Verleih mir daher Weisheit und Einsicht, damit ich weiß, wie ich mich vor diesem Volk verhalten soll. Denn wer könnte sonst dieses mächtige Volk regieren?" },
      { number: 11, text: "Gott antwortete Salomo: Weil dir das am Herzen liegt, weil du nicht um Reichtum, Vermögen, Ehre oder um den Tod deiner Feinde, auch nicht um langes Leben gebeten hast, sondern weil du um Weisheit und Einsicht gebeten hast, um mein Volk zu regieren, zu dessen König ich dich bestellt habe," },
      { number: 12, text: "sollen dir Weisheit und Einsicht zuteil werden. Aber auch Reichtum, Vermögen und Ehre will ich dir geben, wie sie kein König vor dir erlangt hat und auch nach dir keiner haben wird." },
      { number: 13, text: "Darauf ging Salomo von der Kulthöhe in Gibeon, vom Offenbarungszelt, weg nach Jerusalem und herrschte über Israel.", footnotes: ["von der Kulthöhe: Text korr. nach G."] },
      { number: 14, text: "Salomo beschaffte sich Wagen und die Besatzung dazu. Er hatte vierzehnhundert Wagen und zwölftausend Mann als Besatzung und brachte sie in die Wagenstädte sowie in die Umgebung des Königs nach Jerusalem.", heading: "Salomos Reichtum: 1,14-17", footnotes: ["(14-17) 1 Kön 10,26-29"] },
      { number: 15, text: "Der König machte das Silber und das Gold in Jerusalem so häufig wie die Steine und die Zedern so zahlreich wie die Maulbeerfeigenbäume in der Schefela." },
      { number: 16, text: "Man bezog die Pferde für Salomo aus Ägypten und Koë. Die Händler des Königs kauften sie in Koë.", footnotes: ["Vgl. die Anmerkung zu 1 Kön 10,28."] },
      { number: 17, text: "Einen Wagen brachten sie aus Ägypten für sechshundert und ein Pferd für hundertfünfzig Silberschekel. Ebenso trieb man Handel mit allen hetitischen und aramäischen Königen." },
      { number: 18, text: "Salomo beschloss, einen Tempel für den Namen des Herrn und eine königliche Residenz für sich zu bauen.", heading: "Die Vorbereitungen zum Tempelbau: 1,18 - 2,17" },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Er ließ 70000 Lastträger und 80000 Steinhauer im Gebirge auszählen und setzte 3600 Aufseher über sie." },
      { number: 2, text: "Dann sandte er Boten zu Hiram, dem König von Tyrus, und ließ ihm sagen: Du hast meinem Vater David geholfen und ihm Zedern geliefert, damit er sich ein Haus als Wohnung bauen konnte.", footnotes: ["(2-17) 1 Kön 5,15-32"] },
      { number: 3, text: "Ich möchte jetzt dem Namen des Herrn, meines Gottes, ein Haus bauen und es ihm weihen. Man soll wohlriechendes Räucherwerk vor ihm anzünden, ständig die Brote auflegen und jeden Morgen und jeden Abend, an den Sabbaten, Neumondtagen und Festen des Herrn, unseres Gottes, Brandopfer darbringen. So ist es Israel für ewige Zeiten zur Pflicht gemacht worden.", footnotes: ["Das Brot, das aufgelegt wird, sind die Schaubrote (vgl. Lev 24,6)."] },
      { number: 4, text: "Das Haus, das ich bauen will, soll groß werden, denn unser Gott ist größer als alle Götter." },
      { number: 5, text: "Wer aber besitzt die Kraft, ihm ein Haus zu bauen? Der Himmel und die Himmel der Himmel fassen ihn nicht. Und wer bin ich, dass ich ihm ein Haus baue, wenn auch nur, um Rauchopfer vor ihm darzubringen?", footnotes: ["1 Kön 8,27"] },
      { number: 6, text: "Schick mir nun einen fähigen Mann, der Arbeiten in Gold, Silber, Bronze, Eisen, rotem Purpur, Karmesin und blauem Purpur ausführen kann und sich aufs Gravieren versteht. Er soll mit den Künstlern zusammenarbeiten, die bei mir in Juda und Jerusalem sind und die mein Vater David bestellt hat." },
      { number: 7, text: "Liefere mir auch Zedern, Zypressen und Algummimholz vom Libanon! Ich weiß, dass deine Leute die Bäume des Libanon zu fällen verstehen. Meine Leute sollen deinen Leuten dabei helfen.", footnotes: ["Zu Algummim (Almuggim) vgl. die Anmerkung zu 1 Kön 10,11."] },
      { number: 8, text: "Sie sollen eine Menge Holz herrichten; denn das Haus, das ich bauen will, soll groß und wunderbar werden." },
      { number: 9, text: "Den Arbeitern, die das Holz fällen, gebe ich zum Unterhalt für deine Knechte 20000 Kor Weizen, 20000 Kor Gerste, 20000 Bat Wein und 20000 Bat Öl.", footnotes: ["zum Unterhalt: Text korr. nach G und 1 Kön 5,25. 1 Kön spricht nur von 20000 Kor Weizen und 20 Kor (= 200 Bat) Öl."] },
      { number: 10, text: "Hiram, der König von Tyrus, antwortete Salomo in einem Schreiben, das er ihm sandte: Weil der Herr sein Volk liebt, hat er dich ihm als König gegeben." },
      { number: 11, text: "Ferner schrieb Hiram: Gepriesen sei der Herr, der Gott Israels, der Himmel und Erde gemacht hat. Er hat dem König David einen weisen Sohn gegeben, der Einsicht und Verstand besitzt, um ein Haus für den Herrn und eine königliche Residenz für sich selbst zu bauen." },
      { number: 12, text: "Ich schicke dir einen fähigen, klugen Mann, Hiram-Abi,", footnotes: ["1 Kön 7,13f"] },
      { number: 13, text: "den Sohn einer danitischen Frau. Sein Vater stammt aus Tyrus. Er versteht es, Arbeiten in Gold, Silber, Bronze, Eisen, Stein, Holz, rotem und blauem Purpur, Byssus und Karmesin auszuführen, alle Gravierungen zu besorgen und jeden Plan zu entwerfen, der ihm aufgetragen wird. Er wird mit deinen Künstlern und den Künstlern meines Herrn, deines Vaters David, zusammenarbeiten." },
      { number: 14, text: "Den Weizen, die Gerste, das Öl und den Wein, von denen mein Herr gesprochen hat, möge er seinen Knechten liefern." },
      { number: 15, text: "Wir aber werden die Bäume auf dem Libanon fällen, so viele du nötig hast, und sie dir in Flößen auf dem Meer nach Jafo schaffen. Du magst sie dann nach Jerusalem hinaufbringen." },
      { number: 16, text: "Salomo ließ nun alle Fremden zählen, die im Land Israel wohnten, nachdem schon sein Vater David eine Zählung unter ihnen vorgenommen hatte. Es fanden sich 153600 Mann." },
      { number: 17, text: "Von diesen machte er 70000 zu Lastträgern, 80000 zu Steinhauern im Gebirge und 3600 zu Aufsehern, die das Volk zur Arbeit anhalten sollten." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Salomo begann, das Haus des Herrn in Jerusalem auf dem Berg Morija zu bauen, wo der Herr seinem Vater David erschienen war, an der Stätte, die David bestimmt hatte, auf der Tenne des Jebusiters Arauna.", heading: "Der Tempelbau: 3,1-7", footnotes: ["(1-7) 1 Kön 6,1-22.29", "1-4,22: H ist einige Male durch Schreibfehler entstellt; sie werden im folgenden nicht eigens vermerkt. Morija ist die Gegend, in der Abraham nach Gen 22,2 seinen Sohn opfern sollte. Der Ort des Tempels ist also durch die Opfer Abrahams und Davids (2 Sam 24,25; 1 Chr 21,26; 22,1) im Voraus bezeichnet worden."] },
      { number: 2, text: "Er begann den Bau im zweiten Monat, im vierten Jahr seiner Regierung." },
      { number: 3, text: "Dies sind die Grundmaße, die Salomo für den Bau des Hauses Gottes festlegte: Die Länge betrug sechzig Ellen - die Elle nach dem früheren Maß gerechnet - und die Breite zwanzig Ellen." },
      { number: 4, text: "Die Halle vor dem Hauptraum war zwanzig Ellen breit, entsprechend der Breite des Hauses, und zwanzig Ellen hoch. Er überzog sie innen mit purem Gold.", footnotes: ["Nach H hätte die Höhe 120 Ellen betragen."] },
      { number: 5, text: "Den Hauptraum vertäfelte er mit Zypressenholz, überzog dieses mit echtem Gold und brachte Palmen und kettenförmige Bänder darauf an." },
      { number: 6, text: "Auch schmückte er das Haus mit kostbaren Steinen. Das Gold war Parwajimgold.", footnotes: ["Der Ausdruck «Parwajimgold» weist wahrscheinlich auf die Herkunft des Goldes hin."] },
      { number: 7, text: "So überzog er das Haus, die Balken, die Schwellen, seine Wände und Türen mit Gold und ließ in die Wände Kerubim einschnitzen." },
      { number: 8, text: "Er schuf den Raum des Allerheiligsten. Seine Länge betrug zwanzig Ellen, der Breite des Hauses entsprechend, und seine Breite zwanzig Ellen. Er überzog es mit echtem Gold im Gewicht von sechshundert Talenten.", heading: "Das Allerheiligste: 3,8-14", footnotes: ["1 Kön 6,19f"] },
      { number: 9, text: "Für die Nägel verwendete er Gold im Gewicht von fünfzig Schekeln. Auch die Obergemächer ließ er mit Gold verkleiden.", footnotes: ["Die Obergemächer befanden sich im Anbau, der um den Hauptbau und das Allerheiligste lief (vgl. 1 Kön 6,5f)."] },
      { number: 10, text: "Im Raum des Allerheiligsten ließ er zwei Kerubim anfertigen . . . und mit Gold überziehen.", footnotes: ["(10-13) 1 Kön 6,23-28", "H teilweise unverständlich."] },
      { number: 11, text: "Die Flügel der Kerubim hatten zusammen eine Spannweite von zwanzig Ellen. Der Flügel des einen war fünf Ellen lang und berührte die Wand des Hauses Der zweite Flügel war fünf Ellen lang und berührte den Flügel des anderen Kerubs." },
      { number: 12, text: "Der Flügel des zweiten Kerubs war fünf Ellen lang und berührte die Wand des Hauses und auch der andere Flügel war fünf Ellen lang und stieß an den Flügel des ersten Kerubs." },
      { number: 13, text: "Die Flügel dieser Kerubim hatten eine Spannweite von zwanzig Ellen. Sie selbst standen auf ihren Füßen und ihre Gesichter waren dem Innenraum zugewandt." },
      { number: 14, text: "Er verfertigte auch den Vorhang aus blauem und rotem Purpur, aus Karmesin und Byssus und brachte darauf Kerubim an." },
      { number: 15, text: "Für die Vorderseite des Hauses machte er zwei Säulen von achtzehn Ellen Höhe. Das Kapitell auf ihnen maß fünf Ellen.", heading: "Die Ausstattung des Tempels: 3,15 - 5,1", footnotes: ["(15-5,1) 1 Kön 7,15-21", "achtzehn: nach 1 Kön 7,15; H: fünfunddreißig."] },
      { number: 16, text: "Dazu schuf er kettenförmige Bänder und brachte sie oben auf den Säulen an. Auch machte er hundert Granatäpfel und befestigte sie an den kettenförmigen Bändern." },
      { number: 17, text: "Die Säulen stellte er vor dem Tempel auf, die eine auf der rechten, die andere auf der linken Seite. Die rechte nannte er Jachin, die linke Boas.", footnotes: ["Zu den Namen der Säulen vgl. die Anmerkung zu 1 Kön 7,21."] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Er ließ einen bronzenen Altar herstellen, zwanzig Ellen lang, zwanzig Ellen breit und zehn Ellen hoch.", footnotes: ["Der Altar wird im Baubericht von 1 Kön nicht erwähnt; doch wird sein Vorhandensein vorausgesetzt (vgl. 1 Kön 8,22.64)."] },
      { number: 2, text: "Dann machte er das «Meer». Es wurde aus Bronze gegossen, maß zehn Ellen von einem Rand zum andern, war völlig rund und fünf Ellen hoch. Eine Schnur von dreißig Ellen konnte es rings umspannen." },
      { number: 3, text: "Unterhalb seines Rands waren rundum Bilder von Rindern. In einem Band von dreißig Ellen Länge umsäumten sie das Meer ringsum in zwei Reihen. Sie wurden bei seinem Guss mitgegossen.", footnotes: ["H: zehn Ellen."] },
      { number: 4, text: "Das Meer stand auf zwölf Rindern. Von ihnen schauten drei nach Norden, drei nach Westen, drei nach Süden und drei nach Osten. Das Meer ruhte oben auf den Rindern. Ihre Hinterteile waren nach innen gekehrt." },
      { number: 5, text: "Die Wand des Meeres war eine Handbreit dick. Sein Rand war wie der Rand eines Bechers geformt, einer Lilienblüte gleich. Es fasste dreitausend Bat." },
      { number: 6, text: "Auch machte er zehn Kessel, von denen er fünf an die Südseite und fünf an die Nordseite brachte. Sie dienten für die Waschungen; was zum Brandopfer gehörte, sollte man in ihnen abspülen. Das Meer war für die Waschungen der Priester bestimmt." },
      { number: 7, text: "Er machte die zehn goldenen Leuchter der Vorschrift gemäß und stellte sie im Tempel auf, fünf auf der rechten und fünf auf der linken Seite." },
      { number: 8, text: "Ferner machte er zehn Tische und brachte sie in den Tempel, fünf auf die rechte und fünf auf die linke Seite. Desgleichen verfertigte er hundert goldene Schalen." },
      { number: 9, text: "Auch schuf er den Vorhof der Priester und den großen Hof mit seinen Toren. Die Torflügel überzog er mit Bronze.", footnotes: ["Der Hof der Priester wird in 1 Kön 6,36 und 7,12 «innerer Hof» genannt. Die Bezeichnung in Chr deutet an, dass er zu ihrer Zeit nur noch von den Priestern betreten werden durfte."] },
      { number: 10, text: "Das Meer stellte er an die Südseite des Hauses gegen Südosten." },
      { number: 11, text: "Endlich machte Hiram die Töpfe, Schaufeln und Schalen. So führte Hiram die Arbeiten zu Ende, die er dem König Salomo für das Haus Gottes anzufertigen hatte:" },
      { number: 12, text: "zwei Säulen, zwei beckenförmige Kapitelle oben auf den Säulen, die zwei Flechtwerke, mit denen man die beiden beckenförmigen Kapitelle oben auf den Säulen bedeckte," },
      { number: 13, text: "die vierhundert Granatäpfel für die beiden Flechtwerke, die in zwei Reihen an jedem Flechtwerk angebracht waren und die die beiden beckenförmigen Kapitelle auf den Säulen bedeckten," },
      { number: 14, text: "die zehn fahrbaren Gestelle, die zehn Kessel für die Gestelle," },
      { number: 15, text: "das eine Meer, die zwölf Rinder unter ihm," },
      { number: 16, text: "die Töpfe, Schaufeln und Gabeln. Alle diese Geräte machte Hiram-Abi dem König Salomo für das Haus des Herrn aus glatter Bronze." },
      { number: 17, text: "In der Jordanau zwischen Sukkot und Zereda ließ sie der König in Formen aus festem Lehm gießen." },
      { number: 18, text: "Salomo ließ alle diese Geräte in überaus großer Menge anfertigen; das Gewicht der Bronze war darum nicht festzustellen." },
      { number: 19, text: "Salomo ließ alle Geräte, die zum Haus Gottes gehörten, anfertigen: den goldenen Altar, die Tische, auf die man die Schaubrote legte," },
      { number: 20, text: "die Leuchter mit ihren Lampen aus bestem Gold, um sie der Vorschrift gemäß vor der Gotteswohnung anzuzünden," },
      { number: 21, text: "die goldenen Blüten, Lampen und Dochtscheren aus bestem Gold," },
      { number: 22, text: "ferner die Messer, Schalen, Schüsseln und Pfannen aus bestem Gold. An den Türen des Hauses waren die Flügel im inneren Raum gegen das Allerheiligste und die beiden Türflügel, die zum Hauptraum führten, mit Gold verkleidet." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "So wurden alle Arbeiten, die Salomo für das Haus des Herrn ausführen ließ, vollendet. Dann brachte er die Weihegaben seines Vaters David hinein und legte das Silber, das Gold und alle Geräte in die Schatzkammern des Hauses Gottes." },
      { number: 2, text: "Damals versammelte Salomo die Ältesten Israels, alle Stammesführer und die Anführer der israelitischen Großfamilien in Jerusalem, um die Bundeslade des Herrn aus der Stadt Davids, das ist Zion, heraufzuholen.", heading: "Die Übertragung der Bundeslade: 5,2 - 6,2", footnotes: ["(2-6,2) 1 Kön 8,1-13"] },
      { number: 3, text: "Am Fest, das ist im siebten Monat, kamen alle Männer Israels beim König zusammen.", footnotes: ["Das Fest ist das Laubhüttenfest."] },
      { number: 4, text: "In Gegenwart aller Ältesten Israels nahmen die Leviten die Lade" },
      { number: 5, text: "und brachten sie zugleich mit dem Offenbarungszelt und den heiligen Geräten, die im Zelt waren, hinauf. Die Priester und die Leviten übernahmen den Trägerdienst." },
      { number: 6, text: "König Salomo aber und die ganze Gemeinde Israels, die bei ihm vor der Lade versammelt war, schlachteten Schafe und Rinder, die man wegen ihrer Menge nicht zählen und nicht berechnen konnte." },
      { number: 7, text: "Darauf stellten die Priester die Bundeslade des Herrn an ihren Platz, in die Gotteswohnung des Hauses, in das Allerheiligste, unter die Flügel der Kerubim." },
      { number: 8, text: "Denn die Kerubim breiteten ihre Flügel über den Ort, wo die Lade stand, und bedeckten sie und ihre Stangen von oben her." },
      { number: 9, text: "Die Stangen waren so lang, dass man ihre Spitzen an der Lade vor der Gotteswohnung sehen konnte; draußen aber waren sie nicht zu sehen. Sie blieben dort bis zum heutigen Tag.", footnotes: ["bis zum heutigen Tag: Der Chronist übernahm diese Bemerkung unverändert seiner Vorlage. Der Tempel war 586 v. Chr. zerstört worden. Zur Zeit des Chronisten war die Bundeslade nicht mehr vorhanden."] },
      { number: 10, text: "In der Lade befanden sich nur die zwei Tafeln, die Mose am Horeb hineingelegt hatte, die Tafeln des Bundes, den der Herr mit den Israeliten beim Auszug aus Ägypten geschlossen hatte." },
      { number: 11, text: "Darauf traten die Priester aus dem Heiligtum. Alle, die gekommen waren, unabhängig davon, zu welcher Abteilung sie gehörten, hatten sich geheiligt.", footnotes: ["11-13: Fehlt in 1 Kön."] },
      { number: 12, text: "Die levitischen Sänger, Asaf, Heman, Jedutun, ihre Söhne und Brüder, standen alle, in Byssus gekleidet, mit Zimbeln, Harfen und Zithern an der Ostseite des Altars. Bei ihnen waren hundertzwanzig Priester, die auf Trompeten bliesen." },
      { number: 13, text: "Es kam wie aus einem Mund, wenn die Trompeter und Sänger gleichzeitig zum Lob und Preis des Herrn sich vernehmen ließen. Als sie mit ihren Trompeten, Zimbeln und Musikinstrumenten einsetzten und den Herrn priesen, «Denn er ist gütig, denn seine Huld währt ewig», erfüllte eine Wolke den Tempel, das Haus des Herrn.", footnotes: ["Ps 106,1; 107,1; 118,1; 136"] },
      { number: 14, text: "Die Priester konnten wegen der Wolke ihren Dienst nicht verrichten; denn die Herrlichkeit des Herrn erfüllte das Haus Gottes." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Damals sagte Salomo: Der Herr sprach, er wolle im Dunkel wohnen." },
      { number: 2, text: "Ich habe ein fürstliches Haus für dich gebaut, eine Wohnstätte für ewige Zeiten." },
      { number: 3, text: "Dann wandte sich der König um und segnete die ganze Versammlung Israels. Alle standen", heading: "Die Ansprache des Königs: 6,3-11", footnotes: ["(3-11) 1 Kön 8,14-21"] },
      { number: 4, text: "und er betete: Gepriesen sei der Herr, der Gott Israels. Seine Hand hat ausgeführt, was sein Mund meinem Vater David verheißen hat, als er sprach:" },
      { number: 5, text: "Seit dem Tag, da ich mein Volk aus dem Land Ägypten führte, habe ich aus keinem der Stämme Israels eine Stadt für den Bau eines Hauses erwählt, um meinen Namen dort wohnen zu lassen. Auch habe ich keinen Mann zum Fürsten meines Volkes Israel erwählt." },
      { number: 6, text: "Nur Jerusalem habe ich zur Wohnung für meinen Namen und David zum Herrscher über mein Volk Israel erwählt." },
      { number: 7, text: "Mein Vater David wollte dem Namen des Herrn, des Gottes Israels, ein Haus bauen." },
      { number: 8, text: "Doch der Herr sprach zu ihm: Wenn du dir vorgenommen hast, meinem Namen ein Haus zu bauen, hast du einen guten Entschluss gefasst." },
      { number: 9, text: "Doch sollst nicht du das Haus bauen, sondern erst dein leiblicher Sohn soll meinem Namen das Haus bauen." },
      { number: 10, text: "Der Herr hat jetzt sein Versprechen, das er gegeben hat, wahr gemacht: Ich bin an die Stelle meines Vaters David getreten und habe den Thron Israels bestiegen, wie es der Herr zugesagt hat. Ich habe dem Namen des Herrn, des Gottes Israels, das Haus gebaut" },
      { number: 11, text: "und die Lade darin aufgestellt. Sie enthält die Tafeln des Bundes, den der Herr mit den Israeliten geschlossen hat." },
      { number: 12, text: "Dann trat er in Gegenwart der ganzen Versammlung Israels vor den Altar des Herrn und breitete seine Hände aus.", heading: "Das Weihegebet: 6,12-42", footnotes: ["(12-40) 1 Kön 8,22-52"] },
      { number: 13, text: "Salomo hatte nämlich eine bronzene Tribüne anfertigen lassen und sie in der Mitte des Hofes aufgestellt. Sie war fünf Ellen lang, fünf Ellen breit und drei Ellen hoch. Er trat auf sie, ließ sich im Angesicht der ganzen Versammlung Israels auf die Knie nieder, breitete seine Hände zum Himmel aus" },
      { number: 14, text: "und betete: Herr, Gott Israels, im Himmel und auf der Erde gibt es keinen Gott, der so wie du Bund und Huld seinen Knechten bewahrt, die mit ungeteiltem Herzen vor ihm leben." },
      { number: 15, text: "Du hast das Versprechen gehalten, das du deinem Knecht, meinem Vater David, gegeben hast. Deine Hand hat heute erfüllt, was dein Mund versprochen hat." },
      { number: 16, text: "Und nun, Herr, Gott Israels, halte auch das andere Versprechen, das du deinem Knecht David, meinem Vater, gegeben hast, als du sagtest: Es soll dir nie an einem Nachkommen fehlen, der vor mir auf dem Thron Israels sitzt, wenn nur deine Söhne darauf achten, ihren Weg so nach meinem Gesetz zu gehen, wie du es getan hast." },
      { number: 17, text: "Herr, Gott Israels, möge sich jetzt dein Wort, das du deinem Knecht David gegeben hast, als wahr erweisen." },
      { number: 18, text: "Wohnt denn Gott wirklich bei den Menschen auf der Erde? Siehe, selbst der Himmel und die Himmel der Himmel fassen dich nicht, wie viel weniger dieses Haus, das ich gebaut habe." },
      { number: 19, text: "Wende dich, Herr, mein Gott, dem Beten und Flehen deines Knechtes zu! Hör auf das Rufen und auf das Gebet, das dein Knecht vor dir verrichtet." },
      { number: 20, text: "Halte deine Augen offen über diesem Haus bei Tag und bei Nacht, über der Stätte, von der du gesagt hast, dass du deinen Namen hierher legen willst. Höre auf das Gebet, das dein Knecht an dieser Stätte verrichtet." },
      { number: 21, text: "Achte auf das Flehen deines Knechtes und deines Volkes Israel, wenn sie an dieser Stätte beten. Höre sie im Himmel, dem Ort, wo du wohnst. Höre sie und verzeih!" },
      { number: 22, text: "Wenn sich jemand gegen seinen Nächsten verfehlt und dieser ihm einen Eid abverlangt, den er schwören muss, und er dann kommt und vor deinem Altar in diesem Haus schwört," },
      { number: 23, text: "so höre du es im Himmel und greif ein! Verschaff deinen Knechten Recht! Vergilt dem Schuldigen und lass sein Tun auf ihn selbst zurückfallen! Den Schuldlosen aber sprich frei und vergilt ihm, wie es seiner Gerechtigkeit entspricht." },
      { number: 24, text: "Wenn dein Volk Israel von einem Feind geschlagen wird, weil es gegen dich gesündigt hat, und dann wieder umkehrt, deinen Namen preist und in diesem Haus zu dir betet und fleht," },
      { number: 25, text: "so höre du es im Himmel! Vergib deinem Volk Israel seine Sünde; lass sie in das Land zurückkommen, das du ihnen und ihren Vätern gegeben hast." },
      { number: 26, text: "Wenn der Himmel verschlossen ist und kein Regen fällt, weil sie gegen dich gesündigt haben, und wenn sie dann an diesem Ort beten, deinen Namen preisen und von ihrer Sünde lassen, weil du sie demütigst,", footnotes: ["sie demütigst: Text korr. nach G; H: sie erhörst."] },
      { number: 27, text: "so höre du sie im Himmel! Vergib deinen Knechten und deinem Volk Israel ihre Sünden; denn du führst sie den guten Weg, den sie gehen sollen. Spende Regen deinem Land, das du deinem Volk zum Erbbesitz gegeben hast." },
      { number: 28, text: "Wenn im Land Hungersnot herrscht, wenn Pest ausbricht, wenn Getreidebrand, Rost, Heuschrecken und Ungeziefer auftreten, wenn Feinde sie im eigenen Land bedrängen, wenn irgendeine Plage oder Krankheit sie trifft," },
      { number: 29, text: "(so höre du) jedes Gebet und Flehen eines jeden einzelnen und deines ganzen Volkes Israel; denn sie alle kennen ihre Not und ihr Leid und erheben ihre Hände zu diesem Haus." },
      { number: 30, text: "Höre sie dann im Himmel, dem Ort, wo du wohnst, und verzeih! Vergilt jedem, was sein Tun verdient. Du kennst ja ihre Herzen, denn du allein kennst die Herzen der Menschen." },
      { number: 31, text: "So werden sie dich fürchten und auf deinen Wegen gehen, solange sie in dem Land leben, das du unseren Vätern gegeben hast." },
      { number: 32, text: "Auch Fremde, die nicht zu deinem Volk Israel gehören, werden wegen deines großen Namens, deiner starken Hand und deines hoch erhobenen Armes aus fernem Land kommen. Sie werden kommen und in diesem Haus beten." },
      { number: 33, text: "Höre sie dann im Himmel, dem Ort, wo du wohnst, und tu alles, weswegen der Fremde zu dir ruft. Dann werden alle Völker der Erde deinen Namen erkennen. Sie werden dich fürchten, wie dein Volk Israel dich fürchtet, und erfahren, dass dein Name ausgerufen ist über diesem Haus, das ich gebaut habe." },
      { number: 34, text: "Wenn dein Volk gegen seine Feinde in den Krieg zieht, auf dem Weg, den du es sendest, und wenn es dann zu dir betet, zu dieser Stadt hingewendet, die du erwählt hast, und zu dem Haus hin, das ich deinem Namen gebaut habe," },
      { number: 35, text: "so höre du im Himmel sein Beten und Flehen und verschaff ihm Recht!" },
      { number: 36, text: "Wenn sie gegen dich sündigen - es gibt ja niemand, der nicht sündigte - und du ihnen zürnst, sie ihren Bedrängern preisgibst und ihre Feinde sie gefangen fortführen in ein fernes oder nahes Land," },
      { number: 37, text: "so werden sie im Land ihrer Gefangenschaft in sich gehen. Sie werden im Land ihrer Gefangenschaft umkehren, zu dir flehen und rufen: Wir haben gesündigt, Unrecht getan und gefrevelt." },
      { number: 38, text: "Mit ganzem Herzen und ganzer Seele werden sie im Land ihrer Feinde, von denen sie als Gefangene weggeführt wurden, zu dir umkehren und beten, zu dem Land hingewendet, das du ihren Vätern gegeben hast, zu der Stadt hin, die du erwählt hast, und zu dem Haus hin, das ich deinem Namen gebaut habe.", footnotes: ["ihrer Feinde: Text korr. nach G und 1 Kön 8,48; H: ihrer Gefangenschaft."] },
      { number: 39, text: "Höre dann im Himmel, dem Ort, wo du wohnst, ihr Beten und Flehen. Verschaff ihnen Recht und verzeih deinem Volk, was es gegen dich gesündigt hat." },
      { number: 40, text: "Lass jetzt, mein Gott, deine Augen für das Gebet an diesem Ort offen sein und deine Ohren darauf Acht haben!" },
      { number: 41, text: "Herr und Gott, mach dich nun auf zum Ort deiner Ruhe, du und deine machtvolle Lade! Deine Priester, Herr und Gott, sollen sich in Heil kleiden und deine Frommen sich des Glückes freuen.", footnotes: ["Ps 132,8-10"] },
      { number: 42, text: "Herr und Gott, weise deinen Gesalbten nicht ab! Sei eingedenk der Hulderweise an David, deinen Knecht!", footnotes: ["Jes 55,3"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Als Salomo sein Gebet beendet hatte, fiel Feuer vom Himmel und verzehrte das Brandopfer und die Schlachtopfer. Die Herrlichkeit des Herrn erfüllte den Tempel.", heading: "Der Abschluss der Feier: 7,1-10", footnotes: ["Wie vom ersten Opfer Aarons (Lev 9,24) und dem Opfer Davids (1 Chr 21,26) wird nun vom ersten Opfer im Tempel gesagt, dass Gott selbst es entzündete und so den Tempel als rechtmäßige Opferstätte anerkannte."] },
      { number: 2, text: "Die Priester konnten das Haus des Herrn nicht betreten, da die Herrlichkeit des Herrn es erfüllte." },
      { number: 3, text: "Alle Israeliten sahen, wie das Feuer herabfiel und wie die Herrlichkeit des Herrn über dem Tempel erschien. Sie warfen sich mit dem Gesicht zur Erde auf das Steinpflaster nieder, beteten den Herrn an und priesen ihn: «Denn er ist gütig, denn seine Huld währt ewig.»", footnotes: ["5,13"] },
      { number: 4, text: "Dann brachten der König und das ganze Volk vor dem Herrn Opfer dar.", footnotes: ["(4-10) 1 Kön 8,62-66"] },
      { number: 5, text: "Zweiundzwanzigtausend Rinder und hundertzwanzigtausend Schafe ließ König Salomo zum Opfer schlachten. So vollzogen der König und das ganze Volk die Weihe des Hauses Gottes." },
      { number: 6, text: "Die Priester taten ihren Dienst und die Leviten spielten die Instrumente für die Lieder des Herrn. König David hatte diese Geräte anfertigen lassen, um den Herrn zu preisen: «Denn seine Huld währt ewig.» Während die Leviten den Lobpreis Davids vortrugen, bliesen die Priester ihnen gegenüber die Trompeten und ganz Israel nahm daran teil." },
      { number: 7, text: "Salomo weihte auch die Mitte des Hofes, der vor dem Haus des Herrn war, als er dort die Brandopfer und die Fettstücke der Heilsopfer darbrachte. Der bronzene Altar, den Salomo hatte anfertigen lassen, konnte nämlich das Brandopfer, das Speiseopfer und die Fettstücke nicht fassen." },
      { number: 8, text: "Salomo feierte damals mit ganz Israel, das von Lebo-Hamat bis zum Grenzbach Ägyptens zu einer sehr großen Versammlung erschienen war, auch das (Laubhütten-)Fest sieben Tage lang." },
      { number: 9, text: "Am achten Tag hielten sie eine Festversammlung. Die Weihe des Altars hatten sie nämlich sieben Tage lang gefeiert und auch das Fest dauerte sieben Tage." },
      { number: 10, text: "Am dreiundzwanzigsten Tag des siebten Monats entließ der König das Volk zu seinen Zelten. Sie waren voll Freude und frohen Mutes über das Gute, das der Herr an David, an Salomo und an seinem Volk Israel getan hatte." },
      { number: 11, text: "Nachdem Salomo das Haus des Herrn und den königlichen Palast vollendet und alle seine Pläne für das Haus des Herrn und für seinen Palast verwirklicht hatte,", heading: "Die göttliche Verheißung für den Tempel: 7,11-22", footnotes: ["(11-22) 1 Kön 9,1-9"] },
      { number: 12, text: "erschien ihm der Herr in der Nacht und sprach zu ihm: Ich habe dein Gebet gehört und diesen Ort als Opferstätte für mich erwählt." },
      { number: 13, text: "Wenn ich den Himmel verschließe und kein Regen fällt oder wenn ich der Heuschrecke gebiete, das Land kahl zu fressen, wenn ich die Pest in mein Volk sende", footnotes: ["(13-14) 6,26-28"] },
      { number: 14, text: "und mein Volk, über das mein Name ausgerufen ist, sich demütigt und betet, mich sucht und von seinen schlechten Wegen umkehrt, dann höre ich es im Himmel. Ich verzeihe seine Sünde und bringe seinem Land Heilung." },
      { number: 15, text: "Meine Augen sollen jetzt für das Gebet an diesem Ort offen sein und meine Ohren sollen darauf achten." },
      { number: 16, text: "Ich habe jetzt dieses Haus erwählt und geheiligt, damit mein Name ewig hier sei. Meine Augen und mein Herz werden allezeit hier weilen." },
      { number: 17, text: "Wenn du deinen Weg vor mir gehst, wie ihn dein Vater David gegangen ist, und wenn du alles tust, was ich dir befohlen habe, wenn du auf meine Gesetze und Rechtsvorschriften achtest," },
      { number: 18, text: "dann werde ich deinen Königsthron bestehen lassen, wie ich es deinem Vater David zugesichert habe, zu dem ich gesagt habe: Es soll dir nie an einem Herrscher über Israel fehlen." },
      { number: 19, text: "Doch wenn ihr euch von mir abwendet und meine Gesetze und Gebote, die ich euch gegeben habe, übertretet, wenn ihr euch anschickt, andere Götter zu verehren und euch vor ihnen niederzuwerfen," },
      { number: 20, text: "dann werde ich euch aus meinem Land vertreiben, das ich euch gegeben habe. Dieses Haus, das ich meinem Namen geweiht habe, werde ich aus meinem Angesicht wegschaffen und zum Gespött und zum Hohn unter allen Völkern machen." },
      { number: 21, text: "Jeder, der an diesem Haus, das so erhaben war, vorübergeht, wird sich entsetzen und fragen: Warum hat der Herr diesem Land und diesem Haus das angetan?" },
      { number: 22, text: "Und man wird antworten: Weil sie den Herrn, den Gott ihrer Väter, der sie aus Ägypten geführt hat, verlassen und sich an andere Götter gehängt, sich vor ihnen niedergeworfen und sie verehrt haben, darum hat er all dieses Unglück über sie gebracht." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Nach Ablauf von zwanzig Jahren, in denen Salomo das Haus des Herrn und seinen Palast gebaut hatte,", heading: "Verschiedene Nachrichten über Salomo: 8,1-18", footnotes: ["(1-18) 1 Kön 9,10-28"] },
      { number: 2, text: "baute er auch die Städte aus, die Hiram ihm (zurück) gegeben hatte, und siedelte Israeliten darin an.", footnotes: ["Nach 1 Kön 9,11-13 hatte Salomo zwanzig Städte in Galiläa an Hiram abgetreten, die diesem aber nicht gefielen. Der Chronist will sagen, dass Salomo sie wieder zurückerhielt."] },
      { number: 3, text: "Salomo zog nach Hamat-Zoba und eroberte es." },
      { number: 4, text: "Er baute Tadmor in der Steppe aus und alle Vorratsstädte, die er in Hamat anlegte." },
      { number: 5, text: "Er baute auch das obere und das untere Bet-Horon zu Festungen mit Mauern, Toren und Riegeln aus," },
      { number: 6, text: "ebenso Baala und alle Vorratsstädte, die ihm gehörten, die Städte für die Wagen und ihre Mannschaft und was er sonst in Jerusalem, auf dem Libanon und im ganzen Bereich seiner Herrschaft zu bauen wünschte." },
      { number: 7, text: "Die Reste der Hetiter, Amoriter, Perisiter, Hiwiter und Jebusiter, die nicht zu den Israeliten gehörten" },
      { number: 8, text: "und von denen noch Nachkommen im Land lebten - die Israeliten hatten sie nicht ausgerottet - hob Salomo als Fronarbeiter aus und sie blieben es bis zum heutigen Tag." },
      { number: 9, text: "Von den Israeliten aber machte Salomo niemand zum Sklaven für seine Arbeit; sie waren seine Krieger, seine Obersten und Hauptleute, die Befehlshaber über seine Wagen und deren Mannschaft.", footnotes: ["seine Obersten und Hauptleute: Text korr. nach G und 1 Kön 9,22; H: die Obersten seiner Hauptleute."] },
      { number: 10, text: "Zweihundertfünfzig Werkführer, die unter dem Befehl der Statthalter Salomos standen, hatten die Aufsicht über die Arbeiter." },
      { number: 11, text: "Salomo brachte die Tochter des Pharao aus der Davidstadt in das Haus, das er für sie gebaut hatte; denn er sagte: Eine Frau soll nicht im Haus Davids, des Königs von Israel, wohnen; denn die Räume, in die die Lade des Herrn gekommen ist, sind heilig." },
      { number: 12, text: "Damals brachte Salomo auf dem Altar, den er vor der Halle für den Herrn erbaut hatte, Brandopfer dar." },
      { number: 13, text: "Auch an den Sabbaten und Neumondtagen ließ er die Opfer darbringen, die nach dem Befehl des Mose für diese Tage angeordnet waren. Das Gleiche tat er dreimal im Jahr, jeweils an den Festen, am Fest der Ungesäuerten Brote, am Wochenfest und am Laubhüttenfest." },
      { number: 14, text: "Dem Auftrag seines Vaters David folgend, bestellte er die Abteilungen der Priester für ihren Dienst und die Leviten für ihre Aufgabe, Loblieder zu singen und an der Seite der Priester Dienst zu tun, wie es für jeden Tag vorgeschrieben war. Auch bestellte er die Torwächter nach ihren Abteilungen für die einzelnen Tore; denn so lautete der Befehl Davids, des Mannes Gottes." },
      { number: 15, text: "Man wich in keinem Punkt von den Befehlen ab, die der König für die Priester und für die Leviten erlassen hatte." },
      { number: 16, text: "So wurde das ganze Werk Salomos planmäßig ausgeführt, vom Tag der Grundlegung des Hauses des Herrn bis zu seiner Fertigstellung, bis zur Vollendung des Hauses des Herrn." },
      { number: 17, text: "Damals begab sich Salomo nach Ezjon-Geber und Elat an der Küste des Meeres in Edom." },
      { number: 18, text: "Hiram schickte ihm durch seine Knechte Schiffe und geübte Seefahrer. Sie fuhren mit den Leuten Salomos nach Ofir, holten von dort vierhundertfünfzig Talente Gold und brachten es dem König Salomo.", footnotes: ["In Chr sind zwei Tatsachen zusammengefasst: Hiram sandte Schiffe, die im Mittelmeer nach Tarschisch fuhren (9,21), und Seeleute, die von Elat aus mit der israelitischen Schiffsbesatzung nach Ofir fuhren."] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Die Königin von Saba hörte vom Ruf Salomos und kam mit sehr großem Gefolge, mit Kamelen, die Balsam, eine Menge Gold und Edelsteine trugen, nach Jerusalem, um ihn mit Rätselfragen auf die Probe zu stellen. Sie trat bei Salomo ein und redete mit ihm über alles, was sie sich vorgenommen hatte.", heading: "Die Königin von Saba: 9,1-12", footnotes: ["(1-12) 1 Kön 10,1-13", "Zu Saba vgl. die Anmerkung zu 1 Kön 10,1."] },
      { number: 2, text: "Salomo gab ihr Antwort auf alle Fragen. Es gab nichts, was ihm verborgen war und was er ihr nicht hätte sagen können." },
      { number: 3, text: "Als nun die Königin von Saba die Weisheit Salomos erkannte, als sie den Palast sah, den er gebaut hatte," },
      { number: 4, text: "die Speisen auf seiner Tafel, die Sitzplätze seiner Beamten, das Aufwarten seiner Diener und ihre Gewänder, seine Mundschenken und ihre Gewänder, sein Opfer, das er im Haus des Herrn darbrachte, da stockte ihr der Atem.", footnotes: ["sein Opfer: Text korr. nach G und 1 Kön 10,5; H: sein Obergemach."] },
      { number: 5, text: "Sie sagte zum König: Was ich in meinem Land über dich und deine Weisheit gehört habe, ist wirklich wahr." },
      { number: 6, text: "Ich wollte es nicht glauben, bis ich nun selbst gekommen bin und es mit eigenen Augen gesehen habe. Und wahrhaftig, nicht einmal die Hälfte hat man mir berichtet von der Größe deiner Weisheit. Du übertriffst alles, was ich gehört habe." },
      { number: 7, text: "Glücklich sind deine Männer und glücklich diese deine Diener, die allezeit vor dir stehen und deine Weisheit hören." },
      { number: 8, text: "Gepriesen sei Jahwe, dein Gott, der an dir Gefallen gefunden und dich auf seinen Thron gesetzt hat, damit du König bist für Jahwe, deinen Gott. Weil dein Gott Israel liebt und ihm ewigen Bestand verleihen will, hat er dich zum König bestellt, damit du Recht und Gerechtigkeit übst.", footnotes: ["Vgl. die Anmerkung zu 1 Chr 28,5."] },
      { number: 9, text: "Sie gab dem König hundertzwanzig Talente Gold, dazu eine sehr große Menge Balsam und Edelsteine. Balsam, wie ihn die Königin von Saba dem König Salomo schenkte, gab es nicht wieder." },
      { number: 10, text: "Auch die Leute Hirams und die Leute Salomos, die Gold aus Ofir holten, brachten Algummimholz und Edelsteine.", footnotes: ["zu Algummimholz vgl. die Anmerkung zu 1 Kön 10,11."] },
      { number: 11, text: "Der König ließ aus dem Algummimholz Schnitzarbeiten für das Haus des Herrn und den königlichen Palast sowie Zithern und Harfen für die Sänger anfertigen. Dergleichen hatte man vordem im Land Juda nicht gesehen." },
      { number: 12, text: "König Salomo gewährte der Königin von Saba alles, was sie wünschte und begehrte, ausgenommen das, was sie dem König geschenkt hatte. Schließlich kehrte sie mit ihrem Gefolge in ihr Land zurück." },
      { number: 13, text: "Das Gewicht des Goldes, das alljährlich bei Salomo einging, betrug sechshundertsechsundsechzig Goldtalente.", heading: "Salomos Reichtum: 9,13-28", footnotes: ["(13-28) 1 Kön 10,14-28"] },
      { number: 14, text: "Dabei sind nicht eingerechnet die Abgaben, die von den Kaufleuten und Händlern kamen. Auch alle Könige von Arabien und die Statthalter des Landes brachten Salomo Gold und Silber.", footnotes: ["die Abgaben: H: die Männer."] },
      { number: 15, text: "König Salomo ließ zweihundert Schilde aus gehämmertem Gold herstellen. Sechshundert Schekel gehämmertes Gold verwandte er für jeden Schild." },
      { number: 16, text: "Dazu machte er dreihundert kleinere Schilde aus gehämmertem Gold. Dreihundert Schekel Gold verwendete er für jeden. Er brachte sie in das Libanonwaldhaus.", footnotes: ["Das Libanonwaldhaus war einer der Palastbauten Salomos (vgl. 1 Kön 7,2-5)."] },
      { number: 17, text: "Ferner ließ der König einen großen Thron aus Elfenbein anfertigen und mit purem Gold überziehen." },
      { number: 18, text: "Sechs Stufen führten zum Thron hinauf. An seiner Rückseite war ein goldenes Lamm und zu beiden Seiten des Sitzes befanden sich Armlehnen. Zwei Löwen standen neben den Lehnen", footnotes: ["Text korr. nach 1 Kön 10,19."] },
      { number: 19, text: "und zwölf zu beiden Seiten der sechs Stufen. Dergleichen ist noch für kein Königreich geschaffen worden." },
      { number: 20, text: "Alle Trinkgefäße des Königs Salomo waren aus Gold; ebenso waren alle Geräte des Libanonwaldhauses aus bestem Gold. Silber galt in den Tagen Salomos als wertlos;" },
      { number: 21, text: "denn der König hatte eine Flotte, die mit den Leuten Hirams nach Tarschisch fuhr. Einmal in drei Jahren kam die Tarschischflotte und brachte Gold, Silber, Elfenbein, Affen und Perlhühner." },
      { number: 22, text: "So übertraf König Salomo alle Könige der Erde an Reichtum und Weisheit." },
      { number: 23, text: "Alle Könige der Erde begehrten, ihn zu sehen und die Weisheit zu hören, die Gott in sein Herz gelegt hatte." },
      { number: 24, text: "Alle brachten ihm Jahr für Jahr ihre Gaben: silberne und goldene Gefäße, Gewänder, Waffen, Balsam, Pferde und Maultiere." },
      { number: 25, text: "Salomo hatte viertausend Stallplätze für seine Pferde sowie Wagen und zwölftausend Mann als Besatzung. Er brachte sie in die Wagenstädte sowie in die Umgebung des Königs nach Jerusalem.", footnotes: ["1 Kön 5,6; 10,26"] },
      { number: 26, text: "Er war Herrscher über alle Könige vom Eufrat bis zum Land der Philister und bis an die Grenze Ägyptens.", footnotes: ["1 Kön 5,1"] },
      { number: 27, text: "Salomo machte das Silber in Jerusalem so häufig wie die Steine und die Zedern so zahlreich wie die Maulbeerfeigenbäume in der Schefela.", footnotes: ["(27-28) 1 Kön 10,27f"] },
      { number: 28, text: "Man bezog die Pferde für Salomo aus Ägypten und aus allen Ländern." },
      { number: 29, text: "Die übrige Geschichte Salomos, die frühere und die spätere, ist aufgezeichnet in der Geschichte des Propheten Natan, in der Weissagung Ahijas aus Schilo und in der Vision des Sehers Jedo über Jerobeam, den Sohn Nebats.", heading: "Salomos Tod: 9,29-31", footnotes: ["(29-31) 1 Kön 11,41-43", "Ahija aus Schilo wirkte unter Salomo und Jerobeam I. (vgl. 1 Kön 11,29-39; 14,2-16). Jedo ist vielleicht der in 12,15 und 13,22 genannte Prophet Iddo."] },
      { number: 30, text: "Salomo war vierzig Jahre in Jerusalem König über ganz Israel." },
      { number: 31, text: "Er entschlief zu seinen Vätern und man begrub ihn in der Stadt seines Vaters David. Sein Sohn Rehabeam wurde König an seiner Stelle." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Rehabeam begab sich nach Sichem; denn dorthin war ganz Israel gekommen, um ihn zum König zu machen.", heading: "Die Geschichte Judas bis zum Untergang: 10,1 - 36,23 | Der Abfall der zehn Nordstämme: 10,1 - 11,5", footnotes: ["(1-11,5) 1 Kön 12,1-24", "«Israel» bezeichnet hier und öfter die Angehörigen der Nordstämme, die nach dem Abfall von der Dynastie Davids das Nordreich Israel bildeten. Daneben kann in Chr mit «Israel» das Gesamtvolk der zwölf Stämme oder auch die Bevölkerung des Südreichs Juda gemeint sein."] },
      { number: 2, text: "Jerobeam, der Sohn Nebats, hörte davon, während er in Ägypten war, wohin er vor dem König Salomo hatte fliehen müssen; er kehrte jetzt aus Ägypten zurück.", footnotes: ["1 Kön 11,26-40"] },
      { number: 3, text: "Man sandte zu ihm und ließ ihn rufen. So kamen also Jerobeam und ganz Israel (nach Sichem) und sie sagten zu Rehabeam:" },
      { number: 4, text: "Dein Vater hat uns ein hartes Joch auferlegt. Erleichtere jetzt den harten Dienst deines Vaters und das schwere Joch, das er uns auferlegt hat. Dann wollen wir dir dienen." },
      { number: 5, text: "Er antwortete ihnen: Kommt nach drei Tagen wieder zu mir! Als sich das Volk entfernt hatte," },
      { number: 6, text: "beriet sich König Rehabeam mit den älteren Männern, die zu Lebzeiten seines Vaters Salomo in dessen Dienst gestanden hatten. Er fragte sie: Welchen Rat gebt ihr mir? Was soll ich diesem Volk antworten?" },
      { number: 7, text: "Sie sagten zu ihm: Wenn du gut gegen dieses Volk bist, ihnen zu Willen bist und freundlich mit ihnen redest, dann werden sie immer deine Diener sein." },
      { number: 8, text: "Doch er verwarf den Rat, den die Älteren ihm gegeben hatten, und beriet sich mit den jungen Leuten, die mit ihm groß geworden waren und jetzt in seinem Dienst standen." },
      { number: 9, text: "Er fragte sie: Welchen Rat gebt ihr mir? Was sollen wir diesem Volk antworten, das zu mir sagt: Erleichtere das Joch, das dein Vater uns auferlegt hat." },
      { number: 10, text: "Die jungen Leute, die mit ihm groß geworden waren, sagten zu ihm: So sollst du dem Volk antworten, das zu dir sagt: Dein Vater hat uns ein schweres Joch auferlegt. Erleichtere es uns! So sollst du zu ihnen sagen: Mein kleiner Finger ist stärker als die Lenden meines Vaters." },
      { number: 11, text: "Hat mein Vater euch ein schweres Joch aufgebürdet, so werde ich es noch schwerer machen. Mein Vater hat euch mit Peitschen gezüchtigt, ich werde euch mit Skorpionen züchtigen.", footnotes: ["Vgl. die Anmerkung zu 1 Kön 12,11."] },
      { number: 12, text: "Am dritten Tag kamen Jerobeam und das ganze Volk zu Rehabeam; denn der König hatte ihnen gesagt: Kommt am dritten Tag wieder zu mir!" },
      { number: 13, text: "Der König gab ihnen nun eine harte Antwort. Er verwarf den Rat der Älteren" },
      { number: 14, text: "und antwortete ihnen nach dem Rat der jungen Leute: Mein Vater hat euer Joch schwer gemacht. Ich werde es noch schwerer machen. Mein Vater hat euch mit Peitschen gezüchtigt, ich werde euch mit Skorpionen züchtigen.", footnotes: ["Mein Vater: Text korr. nach G und 1 Kön 12,14."] },
      { number: 15, text: "Der König hörte also nicht auf das Volk; denn Gott hatte es so bestimmt, um das Wort wahr zu machen, das er durch Ahija von Schilo zu Jerobeam, dem Sohn Nebats, gesprochen hatte.", footnotes: ["1 Kön 11,30-39"] },
      { number: 16, text: "Als alle Israeliten sahen, dass der König nicht auf sie hörte, gaben sie ihm zur Antwort: Welchen Anteil haben wir an David? / Wir haben keinen Erbbesitz beim Sohn Isais. / In deine Zelte, Israel! / Nun kümmere dich um dein Haus, David! So begab sich ganz Israel zu seinen Zelten." },
      { number: 17, text: "Nur über die Israeliten, die in den Städten Judas wohnten, blieb Rehabeam König." },
      { number: 18, text: "Und als er den Fronaufseher Hadoram hinschickte, steinigten ihn die Israeliten zu Tode. Dem König Rehabeam gelang es, den Wagen zu besteigen und nach Jerusalem zu entkommen." },
      { number: 19, text: "So fiel Israel vom Haus David ab und ist abtrünnig bis zum heutigen Tag." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Rehabeam kam nach Jerusalem und versammelte das Haus Juda und Benjamin, hundertachtzigtausend auserlesene Krieger, um gegen die Israeliten zu kämpfen und das Königtum für Rehabeam zurückzugewinnen." },
      { number: 2, text: "Doch das Wort des Herrn erging an Schemaja, den Mann Gottes:" },
      { number: 3, text: "Sag zu Rehabeam, dem Sohn Salomos, dem König von Juda, und zu allen Israeliten in Juda und Benjamin:" },
      { number: 4, text: "So spricht der Herr: Zieht nicht in den Krieg gegen eure Brüder! Jeder kehre in sein Haus zurück, denn ich habe es so verfügt. Sie hörten auf die Worte des Herrn und sahen davon ab, gegen Jerobeam zu ziehen." },
      { number: 5, text: "Rehabeam blieb in Jerusalem." },
      { number: 6, text: "Rehabeam baute Städte in Juda zu Festungen aus. Er baute aus: Betlehem, Etam, Tekoa,", heading: "Rehabeam: 11,5 - 12,16", footnotes: ["6-10: Die Orte liegen südlich und westlich von Jerusalem. Durch ihren Ausbau sollte Juda gegen Angriffe aus Ägypten geschützt werden, das unter dem Pharao Schischak Palästina bedrohte (vgl. Kap. 12)."] },
      { number: 7, text: "Bet-Zur, Socho, Adullam," },
      { number: 8, text: "Gat, Marescha, Sif," },
      { number: 9, text: "Adorajim, Lachisch, Aseka," },
      { number: 10, text: "Zora, Ajalon und Hebron, die alle in Juda und Benjamin lagen, lauter Festungen." },
      { number: 11, text: "Er machte die Festungen stark, setzte Befehlshaber über sie ein und legte Vorräte an Nahrungsmitteln, Öl und Wein in ihnen an." },
      { number: 12, text: "In jede einzelne Stadt brachte er Schilde und Lanzen. So machte er sie sehr stark. Rehabeam herrschte über Juda und Benjamin." },
      { number: 13, text: "Auch die Priester und Leviten, die (zerstreut) in ganz Israel lebten, stellten sich aus all ihren Gebieten bei ihm ein." },
      { number: 14, text: "Denn die Leviten verließen ihre Weideflächen und ihren Besitz und zogen nach Juda und Jerusalem, weil Jerobeam und seine Söhne ihnen den Priesterdienst für den Herrn verwehrten." },
      { number: 15, text: "Jerobeam bestellte sich eigene Priester für die Kulthöhen, für die Bocksgeister und für die Kälber, die er gemacht hatte.", footnotes: ["1 Kön 12,28", "Die Einsetzung von Priestern, die nicht dem Stamm Levi angehörten, wurde schon in 1 Kön 12,31 getadelt. - Bocksgeister sind in der Volksphantasie Dämonen in Bocksgestalt. - Die «Kälber» sind die Stierbilder, die Jerobeam I. in Bet-El und Dan aufstellen ließ (vgl. 1 Kön 12,28f)."] },
      { number: 16, text: "Den Leviten folgten aus allen Stämmen Israels auch andere, die darauf bedacht waren, den Herrn, den Gott Israels, zu suchen. Sie kamen nach Jerusalem, um dem Herrn, dem Gott ihrer Väter, Opfer darzubringen." },
      { number: 17, text: "Sie stärkten das Reich Juda und waren drei Jahre lang eine Stütze für Rehabeam, den Sohn Salomos; denn drei Jahre folgten sie den Wegen Davids und Salomos." },
      { number: 18, text: "Rehabeam nahm sich Mahalat, die Tochter Jerimots, des Sohnes Davids, und der Abihajil, der Tochter Eliabs, des Sohnes Isais, zur Frau." },
      { number: 19, text: "Sie gebar ihm die Söhne Jëusch, Schemarja und Saham." },
      { number: 20, text: "Nach ihr nahm er Maacha, die Enkelin Abschaloms. Sie gebar ihm Abija, Attai, Sisa und Schelomit." },
      { number: 21, text: "Rehabeam aber liebte Maacha, die Enkelin Abschaloms, mehr als seine anderen Frauen und Nebenfrauen. Er hatte nämlich achtzehn Frauen und sechzig Nebenfrauen genommen und achtundzwanzig Söhne und sechzig Töchter gezeugt.", footnotes: ["Nachahmung der Haremsbräuche orientalischer Herrscher."] },
      { number: 22, text: "Abija, den Sohn der Maacha, bestellte Rehabeam zum Oberhaupt und Fürsten unter seinen Brüdern; denn er hatte vor, ihn zum König zu machen." },
      { number: 23, text: "In kluger Weise verteilte er alle Gegenden Judas und Benjamins und alle festen Städte an seine Söhne. Auch verschaffte er ihnen reichlichen Unterhalt und warb viele Frauen für sie." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Als aber seine Herrschaft gefestigt und er stark geworden war, fiel er mit ganz Israel vom Gesetz des Herrn ab." },
      { number: 2, text: "Im fünften Jahr des Königs Rehabeam zog Schischak, der König von Ägypten, gegen Jerusalem, da sie dem Herrn untreu geworden waren.", footnotes: ["1 Kön 14,25"] },
      { number: 3, text: "Er kam mit zwölfhundert Wagen und sechzigtausend Wagenkämpfern. Zahllos war das Kriegsvolk, das mit ihm aus Ägypten kam: Libyer, Sukkijiter und Kuschiter." },
      { number: 4, text: "Er eroberte die Festungen in Juda und rückte vor Jerusalem." },
      { number: 5, text: "Damals kam der Prophet Schemaja zu Rehabeam und zu den führenden Leuten Judas, die sich vor Schischak nach Jerusalem zurückgezogen hatten, und sagte zu ihnen: So spricht der Herr: Ihr habt mich verlassen; darum verlasse auch ich euch und übergebe euch der Hand Schischaks." },
      { number: 6, text: "Doch nun demütigten sich die führenden Leute Israels und der König und sagten: Der Herr ist gerecht." },
      { number: 7, text: "Als der Herr sah, dass sie sich demütigten, erging das Wort des Herrn an Schemaja: Sie haben sich gedemütigt; darum werde ich sie nicht verderben. Bald werde ich ihnen Rettung bringen und mein Zorn soll sich nicht durch Schischak über Jerusalem ergießen." },
      { number: 8, text: "Doch sollen sie ihm untertan werden, damit sie den Unterschied zwischen meinem Dienst und dem Dienst der irdischen Reiche erfahren.", footnotes: ["Die Forderungen Gottes sind leichter zu erfüllen als die Ansprüche der Menschen (vgl. 1 Chr 21,13)."] },
      { number: 9, text: "Schischak, der König von Ägypten, zog also gegen Jerusalem. Er raubte die Schätze des Tempels und die Schätze des königlichen Palastes und nahm alles weg, auch die goldenen Schilde, die Salomo hatte anfertigen lassen.", footnotes: ["(9-16) 1 Kön 14,26-28"] },
      { number: 10, text: "An deren Stelle ließ König Rehabeam bronzene anfertigen und übergab sie den Befehlshabern der Läufer, die den Eingang zum Haus des Königs bewachten.", footnotes: ["Läufer: vgl. die Anmerkung zu 1 Kön 14,27."] },
      { number: 11, text: "Sooft nun der König in das Haus des Herrn ging, kamen die Läufer, trugen die Schilde und brachten sie dann wieder in ihre Wachstube zurück." },
      { number: 12, text: "Da der König sich demütigte, ließ der Zorn des Herrn von ihm ab, sodass er ihn nicht ganz zugrunde richtete. Es gab ja in Juda noch manches Gute." },
      { number: 13, text: "So gewann König Rehabeam wieder festen Halt in Jerusalem und herrschte weiter als König. Er war einundvierzig Jahre alt, als er König wurde, und regierte siebzehn Jahre in Jerusalem, der Stadt, die der Herr aus allen Stämmen Israels erwählt hatte, um seinen Namen dorthin zu legen. Seine Mutter hieß Naama und war eine Ammoniterin." },
      { number: 14, text: "Er tat Böses und war nicht darauf bedacht, den Herrn zu suchen." },
      { number: 15, text: "Die frühere und die spätere Geschichte Rehabeams ist aufgezeichnet in der Geschichte des Propheten Schemaja und des Sehers Iddo. Sie enthalten auch seinen Stammbaum. Der Krieg zwischen Rehabeam und Jerobeam dauerte die ganze Zeit an." },
      { number: 16, text: "Rehabeam entschlief zu seinen Vätern und wurde in der Davidstadt begraben. Sein Sohn Abija wurde König an seiner Stelle." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Im achtzehnten Jahr des Königs Jerobeam wurde Abija König von Juda.", heading: "Abija: 13,1-23", footnotes: ["(1-23) 1 Kön 15,1-8"] },
      { number: 2, text: "Er regierte drei Jahre in Jerusalem. Seine Mutter hieß Michaja und war eine Tochter Uriëls aus Gibea. Es war aber Krieg zwischen Abija und Jerobeam." },
      { number: 3, text: "Abija begann den Kampf mit einem Heer von vierhunderttausend auserlesenen Kriegern. Jerobeam stellte ihm achthunderttausend auserlesene, tapfere Krieger entgegen." },
      { number: 4, text: "Da stieg Abija auf die Höhe des Berges Zemarajim im Gebirge Efraim und rief: Hört mich an, Jerobeam und ganz Israel!" },
      { number: 5, text: "Wisst ihr denn nicht, dass der Herr, der Gott Israels, David und seinen Söhnen das Königtum von Israel in einem Salzbund auf ewige Zeiten verliehen hat?", footnotes: ["Zu «Salzbund» vgl. Num 18,19."] },
      { number: 6, text: "Doch Jerobeam, der Sohn Nebats, der Knecht Salomos, des Sohnes Davids, erhob sich und empörte sich gegen seinen Herrn." },
      { number: 7, text: "Um ihn sammelten sich haltlose, nichtswürdige Leute, die Rehabeam, dem Sohn Salomos, überlegen waren. Rehabeam war eben noch jung und zaghaft und konnte gegen sie nicht aufkommen." },
      { number: 8, text: "Ihr glaubt nun, euch gegen das Königtum des Herrn, das in der Hand der Söhne Davids liegt, behaupten zu können, weil ihr ein großer Haufen seid und die goldenen Kälber bei euch habt, die Jerobeam zu euren Göttern gemacht hat." },
      { number: 9, text: "Habt ihr nicht die Priester des Herrn, die Nachkommen Aarons, und die Leviten verstoßen und euch Priester bestellt wie die Völker anderer Länder? Jeder, der mit einem jungen Stier und sieben Widdern zu euch kam, um sich in das Priesteramt einsetzen zu lassen, wurde Priester der Nichtgötter." },
      { number: 10, text: "Doch unser Gott ist der Herr. Wir haben ihn nicht verlassen. Die Nachkommen Aarons dienen ihm als Priester und die Leviten versehen ihren Dienst." },
      { number: 11, text: "Jeden Morgen und jeden Abend bringen sie dem Herrn Brandopfer und wohlriechendes Räucherwerk dar, schichten Brote auf den reinen Tisch und bedienen den goldenen Leuchter mit seinen Lampen, um sie jeden Abend anzuzünden; denn wir achten auf die Anordnung des Herrn, unseres Gottes. Doch ihr habt ihn verlassen." },
      { number: 12, text: "Seht: Gott ist mit uns, er steht an unserer Spitze. Bei uns sind auch seine Priester mit den Lärmtrompeten, um gegen euch Kriegslärm zu blasen. Israeliten, kämpft nicht gegen den Herrn, den Gott eurer Väter; denn ihr werdet keinen Erfolg haben." },
      { number: 13, text: "Doch Jerobeam ließ die Leute im Hinterhalt eine Umgehung ausführen, um den Judäern in den Rücken zu fallen. So stand seine Abteilung vor den Judäern, der Hinterhalt aber in ihrem Rücken." },
      { number: 14, text: "Als daher die Judäer sich umwandten, sahen sie, dass ihnen der Angriff von vorne und von hinten drohte. Sie schrien zum Herrn und die Priester bliesen die Trompeten." },
      { number: 15, text: "Dann erhoben die Judäer das Kriegsgeschrei, und während sie lärmten, schlug Gott den Jerobeam und ganz Israel durch Abija und Juda." },
      { number: 16, text: "Die Israeliten mussten vor den Judäern fliehen. Gott gab sie in ihre Gewalt" },
      { number: 17, text: "und Abija und seine Leute versetzten ihnen harte Schläge, sodass fünfhunderttausend auserlesene Krieger von ihnen fielen." },
      { number: 18, text: "So wurden die Israeliten damals gedemütigt, die Judäer aber gewannen die Oberhand; denn sie stützten sich auf den Herrn, den Gott ihrer Väter." },
      { number: 19, text: "Abija verfolgte Jerobeam und nahm ihm folgende Städte weg: Bet-El mit seinen Tochterstädten, Jeschana mit seinen Tochterstädten und Efron mit seinen Tochterstädten." },
      { number: 20, text: "Jerobeam kam in den Tagen Abijas nicht wieder zu Macht. Schließlich schlug ihn der Herr und er starb." },
      { number: 21, text: "Abija aber wurde mächtig. Er nahm sich vierzehn Frauen und zeugte zweiundzwanzig Söhne und sechzehn Töchter." },
      { number: 22, text: "Die übrige Geschichte Abijas, sein Leben und seine Reden sind aufgezeichnet in den Erläuterungen des Propheten Iddo." },
      { number: 23, text: "Abija entschlief zu seinen Vätern; man begrub ihn in der Davidstadt. Sein Sohn Asa wurde König an seiner Stelle. In seinen Tagen hatte das Land zehn Jahre Ruhe." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Asa tat, was gut und recht war in den Augen des Herrn, seines Gottes.", heading: "Asas Erfolge: 14,1-14", footnotes: ["(1-4) 1 Kön 15,9-12"] },
      { number: 2, text: "Er entfernte die fremden Altäre und die Kulthöhen, zerbrach die Steinmale, hieb die Kultpfähle um", footnotes: ["Zu «Kulthöhen» vgl. die Anmerkung zu 1 Kön 3,2. - Zu «Steinmalen» und «Kultpfählen» vgl. die Anmerkung zu 1 Kön 14,23."] },
      { number: 3, text: "und befahl den Judäern, den Herrn, den Gott ihrer Väter, zu suchen und das Gesetz und die Gebote zu halten." },
      { number: 4, text: "Er beseitigte die Kulthöhen und die Rauchopferaltäre aus allen Städten Judas. Das Reich hatte Ruhe unter ihm." },
      { number: 5, text: "Weil das Land Ruhe hatte und weil er in jenen Jahren keinen Krieg führen musste, konnte Asa Festungen in Juda ausbauen. Der Herr hatte ihm nämlich Ruhe verschafft." },
      { number: 6, text: "Er sagte daher zu den Männern Judas: Wir wollen diese Städte ausbauen und sie mit Mauern, Türmen, Toren und Riegeln versehen; denn noch liegt das Land frei vor uns. Weil wir den Herrn, unseren Gott, eifrig gesucht haben, hat er uns ringsum Ruhe verschafft. So konnten sie bauen und hatten Erfolg." },
      { number: 7, text: "Asa hatte ein Heer von dreihunderttausend Mann aus Juda, die Schild und Lanze führten, und zweihundertachtzigtausend Mann aus Benjamin, die den Rundschild trugen und den Bogen spannten. Sie alle waren tapfere Krieger." },
      { number: 8, text: "Gegen sie zog der Kuschiter Serach mit einem Heer von einer Million Mann und dreihundert Wagen. Er kam bis Marescha.", footnotes: ["Kuschiter sind hier nicht die Bewohner Äthiopiens oder des Sudan, sondern ein kleinerer Volksstamm südlich des israelitischen Siedlungsgebiets, vgl. die Ortsnamen Marescha (VV. 8f) und Gerar (VV. 12f)."] },
      { number: 9, text: "Asa zog ihm entgegen und sie stellten sich im Tal nördlich von Marescha zum Kampf auf.", footnotes: ["nördlich: Text korr. nach G."] },
      { number: 10, text: "Doch Asa rief zum Herrn, seinem Gott, und sagte: Herr, im Streit zwischen einem Mächtigen und einem Schwachen kann niemand so helfen wie du. Hilf uns, Herr, unser Gott; denn du bist unsere Stütze und in deinem Namen sind wir gegen diese Übermacht gezogen. Herr, du bist unser Gott und kein Mensch soll etwas gegen dich vermögen." },
      { number: 11, text: "Da schlug der Herr die Kuschiter vor Asa und Juda und die Kuschiter mussten fliehen;" },
      { number: 12, text: "Asa und seine Leute verfolgten sie bis Gerar. Von den Kuschitern fielen so viele, dass sie sich nicht mehr erholten. Sie zerbrachen vor dem Herrn und seinem Heerlager. Die Judäer aber machten reiche Beute.", footnotes: ["Das israelitische Heer ist das Heerlager Gottes, des eigentlichen Königs Israels."] },
      { number: 13, text: "Sie schlugen alle Städte im Umkreis von Gerar, denn der Schrecken des Herrn war über sie gekommen. Die Judäer plünderten sie aus, denn es gab in ihnen viel zu erbeuten." },
      { number: 14, text: "Auch die Zeltlager bei den Herden überwältigten sie, führten eine Menge Schafe und Ziegen und Kamele fort und kehrten dann nach Jerusalem zurück." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Über Asarja, den Sohn Odeds, kam der Geist Gottes.", heading: "Die Bundeserneuerung unter Asa: 15,1-18" },
      { number: 2, text: "Er ging zu Asa hinaus und sagte zu ihm: Hört mich an, Asa und ihr alle von Juda und Benjamin! Der Herr ist mit euch, wenn ihr zu ihm haltet. Wenn ihr ihn sucht, lässt er sich von euch finden; wenn ihr ihn aber verlasst, verlässt er euch." },
      { number: 3, text: "Lange Zeit lebte Israel ohne den wahren Gott, ohne einen belehrenden Priester, ohne Gesetz.", footnotes: ["3-6: Die Verse beschreiben die Zeit der Richter."] },
      { number: 4, text: "In ihrer Not bekehrten sie sich zum Herrn, dem Gott Israels, und da sie ihn suchten, ließ er sich von ihnen finden." },
      { number: 5, text: "In jenen Zeiten konnte niemand sicher aus- und eingehen, denn große Unruhe lag über allen Bewohnern der Länder." },
      { number: 6, text: "Ein Volk wurde vom andern bedrängt, eine Stadt von der andern, denn Gott beunruhigte sie durch allerlei Not." },
      { number: 7, text: "Ihr aber, seid stark! Eure Hände sollen nicht erschlaffen, denn euer Tun wird seinen Lohn finden." },
      { number: 8, text: "Als Asa diese Worte und Weissagungen des Propheten Asarja, des Sohnes Odeds, hörte, entfernte er mutig die Götzenbilder aus dem ganzen Gebiet von Juda und Benjamin sowie aus den Städten, die er im Gebirge Efraim erobert hatte. Auch erneuerte er den Altar des Herrn, der vor dem Tempel des Herrn stand.", footnotes: ["Asarja, des Sohnes: Text sinngemäß ergänzt."] },
      { number: 9, text: "Dann versammelte er ganz Juda und Benjamin sowie die Leute von Efraim, Manasse und Simeon, die sich bei ihnen aufhielten. Sehr viele waren nämlich aus Israel zu ihm übergegangen, als sie sahen, dass der Herr, sein Gott, mit ihm war." },
      { number: 10, text: "Im dritten Monat des fünfzehnten Jahres der Regierung Asas kamen sie in Jerusalem zusammen" },
      { number: 11, text: "und opferten an jenem Tag dem Herrn siebenhundert Rinder und siebentausend Schafe von der Beute, die sie gemacht hatten." },
      { number: 12, text: "Sie gingen die Verpflichtung ein, mit ganzem Herzen und ganzer Seele den Herrn, den Gott ihrer Väter, zu suchen." },
      { number: 13, text: "Wer aber nicht den Herrn, den Gott Israels, suchen würde, sollte getötet werden, sei er klein oder groß, Mann oder Frau." },
      { number: 14, text: "Das schworen sie dem Herrn mit lauter Stimme, unter Freudenrufen, beim Schall der Trompeten und Hörner." },
      { number: 15, text: "Ganz Juda freute sich über den Schwur, denn sie hatten ihn mit ganzem Herzen geleistet. Und da sie mit bestem Willen den Herrn suchten, ließ er sich von ihnen finden und verschaffte ihnen ringsum Ruhe." },
      { number: 16, text: "König Asa enthob auch seine Großmutter Maacha ihrer Stellung als Herrin, weil sie der Aschera ein Schandbild errichtet hatte. Er ließ das Schandbild umhauen, es zertrümmern und im Kidrontal verbrennen.", footnotes: ["(16-18) 1 Kön 15,13-15"] },
      { number: 17, text: "Nur die Kulthöhen verschwanden nicht aus Israel. Doch das Herz Asas war ungeteilt (beim Herrn), solange er lebte." },
      { number: 18, text: "Er brachte auch die Weihegaben seines Vaters und seine eigenen Weihegaben in das Haus Gottes: Silber, Gold und allerlei Geräte." },
      { number: 19, text: "Es gab keinen Krieg bis zum fünfunddreißigsten Jahr der Regierung Asas.", heading: "Der Krieg Asas mit dem Nordreich: 15,19 - 16,10", footnotes: ["(19-16,6) 1 Kön 15,17-22", "19-16,1: Die Zahlen 35 und 36 beruhen auf einem Versehen des Schreibers, da Bascha schon im 26. Jahr Asas starb (vgl. 1 Kön 16,8)."] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Im sechsunddreißigsten Regierungsjahr Asas zog Bascha, der König von Israel, gegen Juda und baute Rama aus, um Asa, den König von Juda, daran zu hindern, in den Krieg zu ziehen.", footnotes: ["Vgl. die Anmerkung zu 1 Kön 15,17."] },
      { number: 2, text: "Da nahm Asa Silber und Gold aus den Schatzkammern des Tempels und des königlichen Palastes und sandte es an Ben-Hadad, den König von Aram, der in Damaskus seinen Sitz hatte. Dabei ließ er ihm sagen:" },
      { number: 3, text: "Zwischen mir und dir, zwischen meinem Vater und deinem Vater soll ein Bündnis sein. Ich schicke dir Silber und Gold. Löse also dein Bündnis mit Bascha, dem König von Israel, damit er von mir abzieht." },
      { number: 4, text: "Ben-Hadad hörte auf König Asa. Er sandte seine Heerführer gegen die Städte Israels und diese verwüsteten Ijon, Dan und Abel-Majim sowie alle Vorratshäuser in den Städten Naftalis.", footnotes: ["Abel-Majim heißt in 1 Kön 15,20 Abel-Bet-Maacha."] },
      { number: 5, text: "Als Bascha das erfuhr, hörte er auf, Rama auszubauen, und stellte die Arbeit ein." },
      { number: 6, text: "König Asa aber holte ganz Juda herbei und ließ die Steine und Balken wegnehmen, mit denen Bascha Rama ausgebaut hatte. Mit ihnen baute er Geba und Mizpa aus." },
      { number: 7, text: "Damals kam der Seher Hanani zu Asa, dem König von Juda, und hielt ihm vor: Weil du dich auf den König von Aram gestützt hast und nicht auf den Herrn, deinen Gott, darum ist das Heer des Königs von Aram deiner Hand entkommen." },
      { number: 8, text: "Hatten nicht auch die Kuschiter und Libyer ein gewaltiges Heer mit sehr vielen Wagen und Wagenkämpfern? Weil du dich aber auf den Herrn stütztest, gab er es in deine Gewalt.", footnotes: ["14,7-14"] },
      { number: 9, text: "Denn die Augen des Herrn schweifen über die ganze Erde, um denen ein starker Helfer zu sein, die mit ungeteiltem Herzen zu ihm halten. Hierin hast du töricht gehandelt, denn von nun an wirst du Krieg haben." },
      { number: 10, text: "Asa aber wurde zornig über den Seher. Er ließ ihn im Gefängnis in den Block legen, denn er war über ihn sehr aufgebracht. Auch einige aus dem Volk ließ Asa damals misshandeln." },
      { number: 11, text: "Die frühere und die spätere Geschichte Asas ist aufgezeichnet im Buch der Könige von Juda und Israel.", heading: "Der Tod Asas: 16,11-14", footnotes: ["(11-14) 1 Kön 15,23f"] },
      { number: 12, text: "Im neununddreißigsten Jahr seiner Regierung erkrankte Asa an den Füßen. Die Krankheit war sehr heftig. Aber auch in der Krankheit suchte er nicht den Herrn, sondern die Ärzte." },
      { number: 13, text: "Asa entschlief zu seinen Vätern; er starb im einundvierzigsten Jahr seiner Regierung." },
      { number: 14, text: "Man setzte ihn bei in seiner Grabstätte, die er sich in der Davidstadt angelegt hatte. Man legte ihn auf ein Lager, das mit Balsam und allerlei kunstvoll zubereiteten Salben ausgestattet war, und zündete zu seiner Ehre ein gewaltiges Feuer an." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Joschafat, der Sohn Asas, wurde König anstelle seines Vaters. Er wurde mächtig in Israel,", heading: "Joschafats glückliche Regierung: 17,1 - 18,1" },
      { number: 2, text: "brachte Truppen in alle befestigten Städte Judas und legte eine Besatzung in das Land Juda und in die Städte Efraims, die sein Vater Asa erobert hatte." },
      { number: 3, text: "Der Herr war mit Joschafat; denn er folgte den früheren Wegen seines Vaters und suchte nicht die Baale auf,", footnotes: ["seines Vaters: Infolge eines Missverständnisses des Schreibers steht in H: seines Vaters David."] },
      { number: 4, text: "sondern den Gott seines Vaters. Er befolgte seine Gebote und machte es nicht wie Israel." },
      { number: 5, text: "Darum festigte der Herr die Königsherrschaft in seiner Hand. Ganz Juda brachte Joschafat Geschenke, sodass ihm Reichtum und Ehre in hohem Maß zuteil wurden." },
      { number: 6, text: "Da sein Herz den Wegen des Herrn folgte, erstarkte sein Mut und so entfernte er auch die Kulthöhen und die Kultpfähle aus Juda." },
      { number: 7, text: "Im dritten Jahr seiner Regierung sandte er seine Beamten Ben-Hajil, Obadja, Secharja, Netanel und Michaja zur Belehrung des Volkes in die Städte Judas." },
      { number: 8, text: "Bei ihnen waren die Leviten Schemaja, Netanja, Sebadja, Asaël, Schemiramot, Jonatan, Adonija, Tobija und Tob-Adonija sowie die Priester Elischama und Joram." },
      { number: 9, text: "Sie lehrten in Juda; mit dem Gesetzbuch des Herrn durchzogen sie alle Städte Judas und belehrten das Volk." },
      { number: 10, text: "Über alle Königreiche der Länder rings um Juda kam der Schrecken des Herrn, sodass sie mit Joschafat keinen Krieg anfingen.", footnotes: ["«Schrecken des Herrn» ist die Furcht vor Gott, der Joschafat gegen alle Anfeindungen in Schutz nehmen würde."] },
      { number: 11, text: "Die Philister brachten ihm Geschenke und zahlten Tribut. Auch die Araber lieferten ihm Kleinvieh: siebentausendsiebenhundert Widder und siebentausendsiebenhundert Ziegenböcke." },
      { number: 12, text: "So wurde Joschafat immer mächtiger. Er baute in Juda Burgen und Vorratsstädte" },
      { number: 13, text: "und unternahm viel in den Städten Judas. In Jerusalem standen tapfere Krieger zu seiner Verfügung." },
      { number: 14, text: "Und das war ihre Dienstordnung nach ihren Großfamilien: Für Juda waren als Tausendschaftsführer bestellt der Oberst Adna mit 300000 tapferen Kriegern" },
      { number: 15, text: "und ihm zur Seite der Oberst Johanan mit 280000 Mann" },
      { number: 16, text: "sowie Amasja, der Sohn Sichris, der sich freiwillig (zum Kriegsdienst) für den Herrn gestellt hatte, mit 200000 tapferen Kriegern." },
      { number: 17, text: "Aus Benjamin kam Eljada, ein tapferer Krieger, mit 200000 Mann, die Bogen und Schild trugen." },
      { number: 18, text: "Ihm zur Seite stand Josabad mit 180000 zum Heeresdienst gerüsteten Männern." },
      { number: 19, text: "Sie dienten dem König zusätzlich zu jenen, die er in die Festungen in ganz Juda gebracht hatte." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "So wurden Joschafat Reichtum und Ehre in hohem Maß zuteil.Joschafat verschwägerte sich mit Ahab.", heading: "Joschafats Teilnahme am Feldzug Ahabs: 18,1 - 19,3", footnotes: ["Joram, der Sohn Joschafats, war mit Atalja, der Tochter Ahabs, verheiratet (vgl. 21,6).", "(1-34) 1 Kön 22,4-35"] },
      { number: 2, text: "Als er nach einigen Jahren zu Ahab nach Samaria kam, ließ Ahab für ihn und seine Leute eine Menge Schafe und Rinder schlachten und überredete ihn, gegen Ramot-Gilead in den Krieg zu ziehen." },
      { number: 3, text: "Ahab, der König von Israel, fragte Joschafat, den König von Juda: Würdest du mit mir gegen Ramot-Gilead ziehen? Er antwortete: Ich ziehe mit dir, mein Volk mit deinem Volk. Ich nehme mit dir am Krieg teil." },
      { number: 4, text: "Joschafat bat aber den König von Israel: Befrag doch zuvor den Herrn!" },
      { number: 5, text: "Da versammelte der König von Israel die Propheten, vierhundert Mann, und fragte sie: Soll ich gegen Ramot-Gilead zu Felde ziehen oder soll ich es lassen? Sie gaben den Bescheid: Zieh hinauf! Gott gibt die Stadt in die Hand des Königs." },
      { number: 6, text: "Doch Joschafat sagte: Ist hier sonst kein Prophet des Herrn, den wir befragen könnten?" },
      { number: 7, text: "Der König von Israel antwortete Joschafat: Es ist noch einer da, durch den wir den Herrn befragen könnten. Doch ich hasse ihn; denn er weissagt mir nie Gutes, sondern immer nur Schlimmes. Es ist Micha, der Sohn Jimlas. Joschafat erwiderte: Der König sage das nicht." },
      { number: 8, text: "Da rief der König von Israel einen Hofbeamten herbei und befahl ihm, unverzüglich Micha, den Sohn Jimlas, zu holen." },
      { number: 9, text: "Der König von Israel und Joschafat, der König von Juda, saßen in königlichen Gewändern auf ihren Thronen. Sie befanden sich auf der Tenne beim Tor Samarias und alle Propheten weissagten vor ihnen." },
      { number: 10, text: "Zidkija, der Sohn Kenaanas, hatte sich eiserne Hörner gemacht und rief: So spricht der Herr: Mit diesen wirst du die Aramäer niederstoßen, bis du sie vernichtet hast." },
      { number: 11, text: "Alle Propheten weissagten in gleicher Weise und riefen: Zieh nach Ramot-Gilead und sei erfolgreich; der Herr gibt die Stadt in die Hand des Königs." },
      { number: 12, text: "Der Bote aber, der Micha holen sollte, redete ihm zu: Die Worte der Propheten waren ohne Ausnahme günstig für den König. Mögen deine Worte ihren Worten gleichen. Sag daher Gutes an!" },
      { number: 13, text: "Doch Micha erwiderte: So wahr der Herr lebt: Nur was Gott mir sagt, werde ich sagen." },
      { number: 14, text: "Als er zum König kam, fragte ihn dieser: Micha, sollen wir gegen Ramot-Gilead zu Felde ziehen oder sollen wir es lassen? Micha antwortete: Zieht hinauf und seid erfolgreich! Sie werden eurer Gewalt übergeben werden." },
      { number: 15, text: "Doch der König entgegnete: Wie oft muss ich dich beschwören, mir im Namen des Herrn nur die Wahrheit zu sagen?" },
      { number: 16, text: "Da sagte Micha: Ich sah ganz Israel über die Berge zerstreut wie Schafe, die keinen Hirten haben. Und der Herr sagte: Sie haben keine Herren mehr. So gehe jeder in Frieden nach Hause." },
      { number: 17, text: "Da wandte sich der König von Israel an Joschafat: Habe ich es dir nicht gesagt? Er weissagt mir nie Gutes, sondern immer nur Schlimmes." },
      { number: 18, text: "Micha aber fuhr fort: Darum hört das Wort des Herrn: Ich sah den Herrn auf seinem Thron sitzen; das ganze Heer des Himmels stand zu seiner Rechten und seiner Linken.", footnotes: ["Jes 6,1-3"] },
      { number: 19, text: "Und der Herr fragte: Wer will Ahab, den König von Israel, betören, sodass er nach Ramot-Gilead hinaufzieht und dort fällt? Da hatte der eine diesen, der andere jenen Vorschlag." },
      { number: 20, text: "Zuletzt trat der Geist vor, stellte sich vor den Herrn und sagte: Ich werde ihn betören. Der Herr fragte ihn: Auf welche Weise?", footnotes: ["Zum «Geist» vgl. die Anmerkung zu 1 Kön 22,19-23."] },
      { number: 21, text: "Er gab zur Antwort: Ich werde mich aufmachen und zu einem Lügengeist im Mund all seiner Propheten werden. Da sagte der Herr: Du wirst ihn betören; du vermagst es. Geh und tu es!" },
      { number: 22, text: "So hat der Herr jetzt einen Geist der Lüge in den Mund deiner Propheten gelegt; denn er hat über dich Unheil beschlossen." },
      { number: 23, text: "Da trat Zidkija, der Sohn Kenaanas, zu Micha, schlug ihn ins Gesicht und rief: Wie, sollte denn der Geist des Herrn von mir gewichen sein, um mit dir zu reden?" },
      { number: 24, text: "Micha erwiderte: Du wirst es an jenem Tag erfahren, an dem du von einem Gemach in das andere eilst, um dich zu verstecken." },
      { number: 25, text: "Der König von Israel aber gab den Befehl: Nehmt Micha fest, führt ihn zum Stadtobersten Amon und zum Prinzen Joasch" },
      { number: 26, text: "und meldet: So spricht der König: Werft diesen Mann ins Gefängnis, und haltet ihn streng bei Brot und Wasser, bis ich wohlbehalten zurückkomme." },
      { number: 27, text: "Doch Micha erwiderte: Wenn du wohlbehalten zurückkommst, dann hat der Herr nicht durch mich geredet. [Und er sagte: Hört, alle ihr Völker!]", footnotes: ["Mi 1,2"] },
      { number: 28, text: "Darauf zog der König von Israel mit Joschafat, dem König von Juda, gegen Ramot-Gilead." },
      { number: 29, text: "Der König von Israel sagte zu Joschafat: Ich will mich verkleiden und so in den Kampf ziehen. Du aber behalte deine Gewänder an! So ging der König von Israel verkleidet in den Kampf." },
      { number: 30, text: "Der König von Aram hatte aber den Obersten seiner Kriegswagen befohlen: Greift niemanden an, er sei hohen oder niederen Ranges, außer den König von Israel." },
      { number: 31, text: "Als daher die Obersten der Kriegswagen Joschafat erblickten und ihn für den König von Israel hielten, stürmten sie auf ihn ein, sodass er um Hilfe schrie. Doch der Herr half ihm und lenkte sie von ihm weg." },
      { number: 32, text: "Als sie nämlich sahen, dass er nicht der König von Israel war, ließen sie von ihm ab." },
      { number: 33, text: "Ein Mann aber spannte aufs Geratewohl seinen Bogen und traf den König von Israel zwischen Panzer und Leibgurt. Dieser befahl daher dem Wagenlenker: Wende um und bring mich aus der Schlacht; denn ich bin verwundet." },
      { number: 34, text: "Da aber die Schlacht an jenem Tag heftig wurde, hielt der König von Israel den Aramäern gegenüber im Wagen stand bis zum Abend. Zur Zeit des Sonnenuntergangs starb er." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Joschafat aber, der König von Juda, kehrte wohlbehalten nach Jerusalem in sein Haus zurück." },
      { number: 2, text: "Da trat ihm der Seher Jehu, der Sohn Hananis, entgegen und hielt ihm vor: Musstest du dem Frevler helfen und liebst du jene, die den Herrn hassen? So lastet nun der Zorn des Herrn auf dir." },
      { number: 3, text: "Doch fand sich auch Gutes an dir. Du hast die Kultpfähle aus dem Land beseitigt und deinen Sinn darauf gerichtet, den wahren Gott zu suchen." },
      { number: 4, text: "Joschafat blieb in Jerusalem. Dann zog er wieder von Beerscheba bis zum Gebirge Efraim unter dem Volk umher und führte es zum Herrn, dem Gott ihrer Väter, zurück.", heading: "Joschafats Sorge für das Recht: 19,4-11" },
      { number: 5, text: "Er bestellte Richter im Land für jede einzelne feste Stadt Judas", footnotes: ["Die Einsetzung von Richtern in den Städten entspricht dem Gesetz in Dtn 16,18."] },
      { number: 6, text: "und gab ihnen die Weisung: Seht zu, was ihr tut; denn nicht im Auftrag von Menschen haltet ihr Gericht, sondern im Auftrag des Herrn. Er steht euch in der Rechtsprechung zur Seite." },
      { number: 7, text: "Lasst euch also von der Furcht des Herrn leiten und handelt gewissenhaft; denn beim Herrn, unserem Gott, gibt es keine Ungerechtigkeit, kein Ansehen der Person, keine Bestechlichkeit." },
      { number: 8, text: "Außerdem bestellte Joschafat in Jerusalem Leviten, Priester und Familienhäupter für das Gericht des Herrn und für Streitigkeiten unter den Einwohnern Jerusalems.", footnotes: ["Gemeint sind Streitigkeiten unter den Einwohnern (Text korr. nach G) Jerusalems bei Vergehen gegen das göttliche Gesetz."] },
      { number: 9, text: "Er trug ihnen auf: Mit Furcht vor dem Herrn, mit Festigkeit und ehrlichem Herzen verfahrt folgendermaßen:" },
      { number: 10, text: "Bei jedem Streitfall, den eure Brüder in ihren Städten euch vorlegen, mag es sich nun um Blutschuld oder um eine Weisung, um ein Gebot oder um Gesetze und Rechte handeln, sollt ihr die Beteiligten warnen, damit sie nicht vor dem Herrn schuldig werden und ein Zorngericht über euch und eure Brüder kommt. Das sollt ihr tun, damit ihr euch nicht versündigt." },
      { number: 11, text: "Seht, der Oberpriester Amarja ist euer Vorgesetzter in allen Angelegenheiten des Herrn und Sebadja, der Sohn Jischmaels, der Fürst des Hauses Juda, in allen Angelegenheiten des Königs. Als Beamte stehen euch die Leviten zur Verfügung. Geht mutig an die Arbeit und der Herr sei mit dem, der seine Pflicht erfüllt." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Danach zogen die Moabiter und Ammoniter mit einer Anzahl von Mëunitern zum Krieg gegen Joschafat heran.", heading: "Joschafats Sieg über die Feinde: 20,1-30", footnotes: ["Die Mëuniter sind benannt nach der Stadt Maan im Gebirge Seïr, südlich des Toten Meers. Der Name ist durch G bezeugt; H hat «Ammoniter», die unmittelbar vorher genannt waren."] },
      { number: 2, text: "Boten kamen und meldeten Joschafat: Ein großer Heerhaufen zieht von jenseits des Meeres, von Edom, gegen dich. Sie stehen schon in Hazezon-Tamar, das ist En-Gedi.", footnotes: ["von Edom: sinngemäß korr.; H: von Aram."] },
      { number: 3, text: "Joschafat geriet in Furcht; er beschloss, den Herrn zu befragen, und ließ in ganz Juda ein Fasten ausrufen." },
      { number: 4, text: "Die Judäer versammelten sich, um vom Herrn (Hilfe) zu erbitten. Auch aus allen Städten Judas kamen sie herbei, um den Herrn zu bitten." },
      { number: 5, text: "Joschafat aber trat im Haus des Herrn, im neuen Vorhof, vor die Versammlung Judas und Jerusalems" },
      { number: 6, text: "und betete: Herr, Gott unserer Väter, bist nicht du Gott im Himmel und Herrscher über alle Reiche der Völker? In deiner Hand liegen Kraft und Stärke; niemand kann dir widerstehen.", footnotes: ["1 Chr 29,12"] },
      { number: 7, text: "Hast nicht du, unser Gott, die Bewohner dieses Landes vor deinem Volk Israel vertrieben und für alle Zeiten ihr Gebiet den Nachkommen Abrahams, deines Freundes, gegeben?" },
      { number: 8, text: "Sie ließen sich darin nieder, bauten deinem Namen ein Heiligtum und sagten:" },
      { number: 9, text: "Wenn Unglück, Schwert, Überschwemmung, Pest oder Hunger über uns kommen, wollen wir vor dieses Haus und vor dein Angesicht hintreten; denn dein Name ist gegenwärtig in diesem Haus. Wir wollen in unserer Not zu dir rufen und du wirst uns dann hören und wirst helfen.", footnotes: ["6,28"] },
      { number: 10, text: "Da stehen nun die Ammoniter, Moabiter und die Bewohner des Berglands Seïr (an unseren Grenzen). Du hast den Israeliten bei ihrem Auszug aus Ägypten nicht erlaubt, ihr Gebiet zu betreten. Sie haben sich von ihnen ferngehalten und sie nicht ausgerottet.", footnotes: ["Dtn 2,4-19"] },
      { number: 11, text: "Dafür erweisen sie uns nun Böses; sie kommen, um uns aus deinem Besitz zu vertreiben, den du uns verliehen hast." },
      { number: 12, text: "Wirst du, unser Gott, nicht über sie Gericht halten? Wir sind machtlos vor dieser gewaltigen Menge, die gegen uns zieht, und wissen nicht, was wir tun sollen. Nur auf dich sind unsere Augen gerichtet." },
      { number: 13, text: "Während ganz Juda, mit Kindern, Frauen und Söhnen, vor dem Herrn stand," },
      { number: 14, text: "kam mitten in der Versammlung der Geist des Herrn über Jahasiël, den Sohn Secharjas, des Sohnes Benajas, des Sohnes Jëiëls, des Sohnes Mattanjas, einen Leviten von den Söhnen Asafs." },
      { number: 15, text: "Er rief: Ihr Judäer alle, ihr Einwohner Jerusalems und du, König Joschafat, merkt auf! So spricht der Herr zu euch: Fürchtet euch nicht und erschreckt nicht vor diesem großen Heerhaufen; denn nicht eure, sondern Gottes Sache ist der Krieg." },
      { number: 16, text: "Zieht morgen gegen sie hinab! Sie werden die Steige von Ziz heraufkommen und ihr werdet am Ausgang des Tals vor der Wüste Jeruël auf sie stoßen." },
      { number: 17, text: "Doch werdet ihr nicht kämpfen müssen. Tretet an, bleibt aber stehen und seht zu, wie der Herr euch Rettung verschafft. Juda und Jerusalem, fürchtet euch nicht und habt keine Angst! Morgen sollt ihr vor ihren Augen ausrücken und der Herr wird mit euch sein." },
      { number: 18, text: "Da verneigte sich Joschafat mit dem Gesicht zur Erde. Auch alle Judäer und die Einwohner Jerusalems fielen vor dem Herrn nieder und beteten ihn an." },
      { number: 19, text: "Darauf erhoben sich von den Leviten die Nachkommen Kehats, und zwar die Nachkommen Korachs, um den Herrn, den Gott Israels, mit lauter und mächtiger Stimme zu preisen." },
      { number: 20, text: "Früh am nächsten Morgen zogen sie in die Wüste von Tekoa. Beim Aufbruch trat Joschafat hin und rief: Hört mir zu, Juda und ihr Einwohner Jerusalems! Vertraut auf den Herrn, euren Gott, dann werdet ihr bestehen. Vertraut auf seine Propheten, dann werdet ihr Erfolg haben.", footnotes: ["Jes 7,9"] },
      { number: 21, text: "Nachdem er sich mit dem Volk beraten hatte, stellte er Sänger für den Herrn auf, die in heiligem Schmuck dem kampfbereiten Heer voranzogen, Loblieder sangen und riefen: Dankt dem Herrn; denn seine Huld währt ewig.", footnotes: ["Ps 136,1"] },
      { number: 22, text: "Während sie den Jubelruf und Lobpreis anstimmten, führte der Herr Feinde aus dem Hinterhalt gegen die Ammoniter, Moabiter und die Bewohner des Berglands Seïr, die gegen Juda gezogen waren, sodass sie geschlagen wurden." },
      { number: 23, text: "Darauf stellten sich die Ammoniter und Moabiter gegen die Bewohner des Berglands Seïr, weihten sie dem Untergang und vernichteten sie. Nachdem sie die Bewohner Seïrs vollends aufgerieben hatten, gerieten sie selbst gegeneinander zu ihrem eigenen Verderben." },
      { number: 24, text: "Als daher die Judäer an die Stelle kamen, von der aus man die Wüste überschauen konnte, und ihren Blick auf die feindlichen Scharen richteten, sahen sie nur Leichen am Boden liegen; keiner war entkommen." },
      { number: 25, text: "Da rückte Joschafat mit seinen Leuten an, um die Beute zu sammeln. Sie fanden bei ihnen eine Menge Waren, Kleider, kostbare Geräte und nahmen so viel an sich, dass sie es nicht mehr tragen konnten. Drei Tage plünderten sie, denn die Beute war reichlich.", footnotes: ["Kleider: Text korr."] },
      { number: 26, text: "Am vierten Tag sammelten sie sich im Tal Beracha und priesen dort den Herrn. Darum nennt man diese Gegend Tal Beracha (Preistal) bis zum heutigen Tag." },
      { number: 27, text: "Darauf wandten sich alle Männer Judas und Jerusalems mit Joschafat an ihrer Spitze voller Freude zur Heimkehr nach Jerusalem; denn der Herr hatte ihnen Freude über ihre Feinde verliehen." },
      { number: 28, text: "Unter dem Spiel der Harfen, Zithern und Trompeten kamen sie nach Jerusalem zum Haus des Herrn." },
      { number: 29, text: "Der Schrecken Gottes aber erfasste alle Reiche der Länder, als sie hörten, dass der Herr gegen die Feinde Israels gekämpft habe." },
      { number: 30, text: "So hatte das Reich Joschafats Frieden und sein Gott verschaffte ihm ringsum Ruhe." },
      { number: 31, text: "Joschafat war König von Juda. Er war fünfunddreißig Jahre alt, als er König wurde, und regierte fünfundzwanzig Jahre in Jerusalem. Seine Mutter hieß Asuba und war eine Tochter Schilhis.", heading: "Weitere Nachrichten über Joschafat: 20,31-37", footnotes: ["(31-37) 1 Kön 22,41-50"] },
      { number: 32, text: "Er folgte den Wegen seines Vaters Asa, ohne von ihnen abzuweichen, und tat, was dem Herrn gefiel." },
      { number: 33, text: "Nur die Kulthöhen verschwanden nicht. Das Volk hatte seinen Sinn noch nicht auf den Gott seiner Väter gerichtet.", footnotes: ["Der Chronist empfindet es als Mangel, dass nach der Erbauung des Tempels noch Opfer außerhalb Jerusalems dargebracht werden."] },
      { number: 34, text: "Die übrige Geschichte Joschafats, die frühere und die spätere, ist aufgezeichnet in der Geschichte Jehus, des Sohnes Hananis, die in das Buch der Könige von Israel aufgenommen wurde." },
      { number: 35, text: "Zuletzt verbündete sich Joschafat, der König von Juda, mit Ahasja, dem König von Israel, dessen Tun frevelhaft war." },
      { number: 36, text: "Joschafat schloss mit ihm ein Abkommen, um Schiffe zu bauen, die nach Tarschisch fahren sollten. Sie bauten die Schiffe in Ezjon-Geber.", footnotes: ["Hier ist Tarschisch in Spanien (9,21) mit Ofir, das von Ezion-Geber aus durch das Rote Meer erreicht wurde, verwechselt."] },
      { number: 37, text: "Doch Eliëser, der Sohn Dodawas aus Marescha, weissagte gegen Joschafat: Weil du dich mit Ahasja verbündet hast, wird der Herr dein Werk zerstören. So zerschellten denn die Schiffe und konnten nicht nach Tarschisch fahren." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Joschafat entschlief zu seinen Vätern und wurde bei seinen Vätern in der Davidstadt begraben. Sein Sohn Joram wurde König an seiner Stelle.", heading: "Joram: 21,1-20", footnotes: ["1 Kön 22,51"] },
      { number: 2, text: "Seine Brüder, die Söhne Joschafats, waren: Asarja, Jehiël, Secharja, Asarja, Michael und Schefatja; sie alle waren Söhne Joschafats, des Königs von Juda.", footnotes: ["Juda: Text korr. nach G; H: Israel, doch vgl. die Anmerkung zu 10,1."] },
      { number: 3, text: "Ihr Vater gab ihnen reiche Geschenke an Silber, Gold und anderen Kostbarkeiten, dazu befestigte Städte in Juda. Das Königtum aber verlieh er Joram; denn dieser war der Erstgeborene." },
      { number: 4, text: "Doch Joram erhob sich gegen das Königtum seines Vaters, gewann die Oberhand und ließ alle seine Brüder und auch einige führende Männer Israels mit dem Schwert hinrichten." },
      { number: 5, text: "Joram war zweiunddreißig Jahre alt, als er König wurde, und regierte acht Jahre in Jerusalem.", footnotes: ["(5-10) 2 Kön 8,16-24"] },
      { number: 6, text: "Er folgte den Wegen der Könige von Israel, wie es das Haus Ahab getan hatte; denn er hatte eine Tochter Ahabs zur Frau und er tat, was dem Herrn missfiel." },
      { number: 7, text: "Doch der Herr wollte das Haus David nicht verderben wegen des Bundes, den er mit David geschlossen hatte, und weil er ihm versprochen hatte, er werde ihm und seinen Söhnen für immer eine Leuchte geben.", footnotes: ["1 Kön 11,36"] },
      { number: 8, text: "In den Tagen Jorams fiel Edom von Juda ab und setzte einen eigenen König ein.", footnotes: ["8-10: Vgl. die Anmerkung zu 2 Kön 8,21."] },
      { number: 9, text: "Joram zog daher mit seinen Obersten und allen Kriegswagen gegen sie. Während der Nacht griff er an und schlug die Edomiter, die ihn und die Obersten der Kriegswagen umzingelt hatten." },
      { number: 10, text: "Doch Edom fiel von Juda ab und ist abtrünnig bis zum heutigen Tag. Damals, zur gleichen Zeit, fiel auch Libna von Juda ab; denn Joram hatte den Herrn, den Gott seiner Väter, verlassen." },
      { number: 11, text: "Auch errichtete er Kulthöhen auf den Bergen Judas und verführte die Einwohner Jerusalems zur Untreue und Juda zum Abfall." },
      { number: 12, text: "Damals traf ein Schreiben des Propheten Elija bei ihm ein. Es hatte folgenden Inhalt: So spricht der Herr, der Gott deines Vaters David: Weil du nicht auf den Wegen deines Vaters Joschafat und auf den Wegen Asas, des Königs von Juda," },
      { number: 13, text: "sondern auf den Wegen der Könige von Israel gegangen bist, weil du Juda und die Einwohner Jerusalems zur Untreue verführt hast, wie es das Haus Ahab getan hat, und weil du deine eigenen Brüder aus der Familie deines Vaters, die besser waren als du, getötet hast," },
      { number: 14, text: "darum wird der Herr dein Volk, deine Söhne und Frauen und deinen ganzen Besitz mit harten Schlägen treffen." },
      { number: 15, text: "Du selbst wirst in schweres Siechtum verfallen und an deinen Eingeweiden erkranken, sodass sie nach Jahr und Tag infolge der Krankheit herausfallen werden." },
      { number: 16, text: "Der Herr reizte nun den Wagemut der Philister und der Araber, die neben den Kuschitern wohnen, gegen Joram auf.", footnotes: ["Kuschiter: wie in 14,8."] },
      { number: 17, text: "Sie zogen gegen Juda, überfielen das Land und nahmen den ganzen Besitz weg, der sich im Palast des Königs fand; auch seine Söhne und Frauen führten sie weg. Es blieb ihm kein Sohn außer Joahas, dem jüngsten von ihnen.", footnotes: ["Joahas: Im folgenden wird er Ahasja genannt."] },
      { number: 18, text: "Nach all dem schlug der Herr den Joram mit einer unheilbaren Krankheit in den Eingeweiden." },
      { number: 19, text: "Nach Jahr und Tag, zwei Tage vor seinem Ende, fielen infolge der Krankheit seine Eingeweide heraus und er starb unter großen Schmerzen. Das Volk zündete zu seiner Ehre kein Feuer an, wie das bei seinen Vätern geschehen war." },
      { number: 20, text: "Er war zweiunddreißig Jahre alt, als er König wurde, und regierte acht Jahre in Jerusalem. Er ging dahin, von niemand bedauert. Man begrub ihn in der Davidstadt, aber nicht in den Gräbern der Könige." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Die Einwohner Jerusalems machten nun seinen jüngsten Sohn Ahasja zum König an seiner Stelle. Alle älteren Brüder hatte die Räuberbande getötet, die mit den Arabern in das Lager eingedrungen war. So kam Ahasja, der Sohn Jorams, des Königs von Juda, zur Herrschaft.", heading: "Ahasja: 22,1-9" },
      { number: 2, text: "Ahasja war zweiundzwanzig Jahre alt, als er König wurde, und regierte ein Jahr in Jerusalem. Seine Mutter hieß Atalja; sie war eine Enkelin Omris.", footnotes: ["(2-9) 2 Kön 8,25-29; 9,27f", "zweiundzwanzig: so nach G und 2 Kön 8,26; H: zweiundvierzig."] },
      { number: 3, text: "Auch er folgte den Wegen des Hauses Ahab, denn seine Mutter verführte ihn durch ihren Rat zum Bösen." },
      { number: 4, text: "Wie die Angehörigen des Hauses Ahab tat er, was dem Herrn missfiel; denn diese stürzten ihn nach dem Tod seines Vaters durch ihren Rat ins Verderben." },
      { number: 5, text: "Ihrem Rat folgend, zog er mit Joram, dem Sohn Ahabs, dem König von Israel, gegen Hasaël, den König von Aram, nach Ramot-Gilead in den Krieg. Dabei verwundeten die Aramäer Joram", footnotes: ["5f: Vgl. 2 Kön 8,28f."] },
      { number: 6, text: "und er musste heimkehren, um in Jesreel von den Wunden Heilung zu suchen, die ihm die Aramäer geschlagen hatten, als er in Ramot gegen ihren König Hasaël kämpfte. Ahasja, der Sohn Jorams, der König von Juda, kam hinab, um Joram, den Sohn Ahabs, in Jesreel zu besuchen, als er krank daniederlag.", footnotes: ["von den Wunden: Text korr. nach G und 2 Kön 8,29. Für Ahasja hat H Asarja."] },
      { number: 7, text: "Von Gott aber war es zum Verderben Ahasjas bestimmt, dass er sich zu Joram begab. Mit Joram fuhr er nach seiner Ankunft Jehu, dem Enkel Nimschis, entgegen, den der Herr gesalbt hatte, damit er das Haus Ahab ausrotte.", footnotes: ["2 Kön 9,21"] },
      { number: 8, text: "Als Jehu seinen Streit mit dem Haus Ahab austrug, stieß er auf die Hofleute Judas und die Verwandten Ahasjas, die im Dienst Ahasjas standen, und machte sie nieder.", footnotes: ["2 Kön 10,12-14"] },
      { number: 9, text: "Dann ließ er Ahasja suchen. Man nahm ihn fest, während er sich in Samaria verborgen hielt, brachte ihn vor Jehu und tötete ihn. Doch gab man ihm ein Grab; denn sie sagten: Er ist ein Enkel Joschafats, der den Herrn mit ganzem Herzen gesucht hat. Im Haus Ahasjas war nun niemand mehr, der fähig gewesen wäre, die Königsherrschaft zu übernehmen." },
      { number: 10, text: "Als Atalja, die Mutter Ahasjas sah, dass ihr Sohn tot war, ging sie daran, die ganze Nachkommenschaft der königlichen Familie des Hauses Juda auszurotten.", heading: "Atalja: 22,10 - 23,21", footnotes: ["(10-23,21) 2 Kön 11,1-20", "10-23,21: Vgl. die Anmerkungen zu 2 Kön 11,1-17. auszurotten: Text korr. nach G und 2 Kön 11,1."] },
      { number: 11, text: "Doch Joscheba, die Tochter des Königs, nahm Joasch, den Sohn Ahasjas, aus dem Kreis der Königssöhne, die ermordet werden sollten, weg und brachte ihn heimlich mit seiner Amme in die Bettenkammer. Dort versteckte sie ihn vor Atalja, sodass diese ihn nicht töten konnte. Joscheba, die Tochter des Königs Joram und Frau des Priesters Jojada, war die Schwester Ahasjas.", footnotes: ["Als Frau des Priesters Jojada konnte Joscheba den Königssohn im Tempelbereich verborgen halten."] },
      { number: 12, text: "Joasch blieb sechs Jahre bei ihr im Haus Gottes verborgen, während Atalja das Land regierte.", footnotes: ["bei ihr: Text korr. nach G und 2 Kön 11,3; H: bei ihnen."] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Doch im siebten Jahr fasste Jojada Mut und verbündete sich mit den Hundertschaftsführern, mit Asarja, dem Sohn Jerohams, Jischmaël, dem Sohn Johanans, Asarja, dem Sohn Obeds, Maaseja, dem Sohn Adajas, und Elischafat, dem Sohn Sichris." },
      { number: 2, text: "Sie durchzogen Juda und versammelten die Leviten aus allen Städten Judas sowie die Häupter der israelitischen Großfamilien in Jerusalem." },
      { number: 3, text: "Dort schloss die Versammlung im Haus Gottes einen Bund mit dem König. Jojada legte ihnen dar: Seht da den Sohn des Königs! Er soll herrschen, wie der Herr den Nachkommen Davids versprochen hat." },
      { number: 4, text: "Das ist es, was ihr tun sollt: Ein Drittel von der Wache, die am Sabbat aufzieht, soll bei den Priestern und Leviten sein, die an den Schwellen wachen." },
      { number: 5, text: "Ein Drittel soll am Königspalast und ein Drittel am Jesodtor stehen. Das ganze Volk aber soll in den Höfen des Hauses des Herrn bleiben." },
      { number: 6, text: "Niemand darf in das Haus des Herrn kommen als nur die Priester und die diensttuenden Leviten; diese können eintreten, denn sie sind heilig. Das ganze Volk aber soll die Anordnungen des Herrn befolgen." },
      { number: 7, text: "Die Leviten sollen sich mit der Waffe in der Hand um den König scharen. Wer in das Haus einzudringen versucht, soll getötet werden. Seid beim König, wenn er einzieht und wenn er auszieht." },
      { number: 8, text: "Die Leviten und Judäer befolgten alle Weisungen des Priesters Jojada. Jeder holte seine Leute, sowohl jene, die am Sabbat aufzogen, als auch jene, die am Sabbat abzogen; denn der Priester Jojada hatte die Abteilungen nicht entlassen." },
      { number: 9, text: "Er gab den Anführern der Hundertschaften die Lanzen sowie die großen und kleinen Schilde, die dem König David gehört hatten und sich jetzt im Haus Gottes befanden." },
      { number: 10, text: "Die ganze Mannschaft stellte er mit der Waffe in der Hand von der Südseite des Tempels bis zur Nordseite vor dem Altar und dem Tempel rings um den König auf." },
      { number: 11, text: "Dann führte man den Königssohn heraus. Man überreichte ihm den Stirnreif und das Königsgesetz und machte ihn so zum König. Jojada und seine Söhne salbten ihn und riefen: Es lebe der König!", footnotes: ["Dtn 17,18"] },
      { number: 12, text: "Als Atalja das Geschrei des Volkes hörte, das herbeilief und dem König zujubelte, kam sie zu den Leuten in das Haus des Herrn." },
      { number: 13, text: "Da sah sie den König am Eingang bei seiner Säule stehen; die Obersten und die Trompeter waren bei ihm und alle Bürger des Landes waren voller Freude und bliesen die Trompeten, während die Sänger mit ihren Instrumenten dastanden und das Zeichen zum Lobpreis gaben. Atalja zerriss ihre Kleider und schrie: Verrat, Verrat!" },
      { number: 14, text: "Doch der Priester Jojada befahl den Hundertschaftsführern, die das Kommando über die Truppen hatten: Führt sie durch die Reihen hinaus und jeder, der ihr folgen will, soll mit dem Schwert niedergeschlagen werden. Er sagte: Tötet sie nicht im Haus des Herrn!", footnotes: ["befahl den Hundertschaftsführern: Text korr. nach G und 2 Kön 11,15; H: führte die Hundertschaftsführer . . . heraus."] },
      { number: 15, text: "Da legte man Hand an sie, und als sie zum Eingang des Rosstores am königlichen Palast kam, machte man sie dort nieder." },
      { number: 16, text: "Jojada schloss nun den Bund des Herrn mit dem ganzen Volk und mit dem König. Sie versprachen, dass sie das Volk des Herrn sein wollten.", footnotes: ["des Herrn: Text korr. nach 2 Kön 11,17."] },
      { number: 17, text: "Darauf zog das ganze Volk zum Baalstempel und riss ihn nieder. Sie zertrümmerten seine Altäre und Bilder und erschlugen den Baalspriester Mattan vor den Altären." },
      { number: 18, text: "Dann betraute Jojada die Priester und Leviten mit der Aufsicht über das Haus des Herrn. David hatte sie für das Haus des Herrn in Klassen eingeteilt; nach seiner Anweisung sollten sie unter Freudengesängen und Liedern die Brandopfer für den Herrn darbringen, wie sie im Gesetz des Mose vorgeschrieben sind." },
      { number: 19, text: "Auch stellte er Wächter an die Tore des Hauses des Herrn, damit niemand hereinkäme, der mit einer Unreinheit behaftet wäre." },
      { number: 20, text: "Hierauf nahm Jojada die Hundertschaftsführer, die Vornehmen und Großen des Volkes sowie alle Bürger des Landes und führte den König vom Haus des Herrn hinab. Sie kamen durch das obere Tor in den königlichen Palast und setzten Joasch auf den Königsthron." },
      { number: 21, text: "Alle Bürger des Landes waren voller Freude und die Stadt blieb ruhig. Atalja aber hatte man mit dem Schwert umgebracht." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Joasch war sieben Jahre alt, als er König wurde, und regierte vierzig Jahre in Jerusalem. Seine Mutter hieß Zibja und stammte aus Beerscheba.", heading: "Joasch: 24,1-27", footnotes: ["(1-27) 2 Kön 12,1-22"] },
      { number: 2, text: "Joasch tat, was dem Herrn gefiel, solange der Priester Jojada lebte." },
      { number: 3, text: "Jojada warb für ihn zwei Frauen und er zeugte Söhne und Töchter." },
      { number: 4, text: "Danach fasste Joasch den Entschluss, das Haus des Herrn zu erneuern." },
      { number: 5, text: "Er ließ die Priester und die Leviten zusammenkommen und sagte zu ihnen: Geht in die Städte Judas und sammelt Geld von ganz Israel, um Jahr für Jahr Ausbesserungen am Haus eures Gottes vornehmen zu können. Beeilt euch dabei! Als sich aber die Leviten nicht beeilten," },
      { number: 6, text: "rief Joasch den Oberpriester Jojada zu sich und hielt ihm vor: Warum hast du die Leviten nicht angehalten, von Juda und Jerusalem die Abgabe zu erheben, die Mose, der Knecht des Herrn, und die Versammlung Israels für das Zelt der Bundesurkunde vorgeschrieben haben?", footnotes: ["Ex 30,12-16; 38,24-26"] },
      { number: 7, text: "Die ruchlose Atalja und ihre Bauleute haben das Haus Gottes verfallen lassen und sogar die Weihegaben des Hauses des Herrn für die Baale verwendet." },
      { number: 8, text: "Der König ließ nun einen Kasten anfertigen und außen am Tor des Hauses des Herrn aufstellen." },
      { number: 9, text: "Dann rief man in Juda und Jerusalem aus, man solle die Abgabe für den Herrn entrichten, die Mose, der Knecht Gottes, den Israeliten in der Wüste befohlen hat." },
      { number: 10, text: "Alle führenden Männer und das ganze Volk waren darüber erfreut. Sie brachten ihre Beiträge und warfen sie in den Kasten, bis er voll war." },
      { number: 11, text: "Zu bestimmten Zeiten ließ man den Kasten durch Leviten zur Überprüfung zum König bringen. Wenn man sah, dass viel Geld darin war, kamen der Schreiber des Königs und der Beauftragte des Oberpriesters, leerten den Kasten und trugen ihn wieder an seinen Platz zurück. Das taten sie Tag für Tag und brachten viel Geld zusammen." },
      { number: 12, text: "Der König und Jojada übergaben es den Werkmeistern, die am Haus des Herrn tätig waren, und diese bestellten Maurer und Zimmerleute sowie Eisen- und Bronzeschmiede, die das Haus des Herrn erneuern und seine Schäden beseitigen sollten." },
      { number: 13, text: "Die Werkmeister griffen zu und unter ihrer Leitung gingen die Ausbesserungsarbeiten gut voran. Sie stellten das Haus Gottes nach seinen Plänen wieder her und erhöhten seine Festigkeit." },
      { number: 14, text: "Als sie fertig waren, brachten sie den Rest des Geldes vor den König und vor Jojada und dieser ließ dafür Geräte für das Haus des Herrn, Kult- und Opfergeräte, Schalen und sonstige Gefäße aus Gold und Silber anfertigen. Solange Jojada lebte, wurden ständig im Haus des Herrn Opfer dargebracht." },
      { number: 15, text: "Jojada aber wurde alt und satt an Lebenstagen; er war hundertdreißig Jahre alt, als er starb." },
      { number: 16, text: "Man begrub ihn bei den Königen in der Davidstadt; denn er hatte an Israel, für Gott und sein Haus, Gutes getan." },
      { number: 17, text: "Nach dem Tod Jojadas kamen die führenden Männer Judas zum König und warfen sich vor ihm nieder. Dieser hörte damals auf sie," },
      { number: 18, text: "sodass sie den Bund des Herrn, des Gottes ihrer Väter, verließen und die Kultpfähle und Götzenbilder verehrten. Wegen dieser Schuld kam ein Zorngericht über Juda und Jerusalem." },
      { number: 19, text: "Der Herr schickte Propheten zu ihnen, um sie zur Umkehr zum Herrn zu bewegen, aber man hörte nicht auf ihre Warnung." },
      { number: 20, text: "Da kam der Geist Gottes über Secharja, den Sohn des Priesters Jojada. Er trat vor das Volk und hielt ihm vor: So spricht Gott: Warum übertretet ihr die Gebote des Herrn? So könnt ihr kein Glück mehr haben. Weil ihr den Herrn verlassen habt, wird er euch verlassen.", footnotes: ["15,2"] },
      { number: 21, text: "Sie aber taten sich gegen ihn zusammen und steinigten ihn auf Befehl des Königs im Hof des Hauses des Herrn.", footnotes: ["Darauf bezieht sich das Wort Jesu in Mt 23,35."] },
      { number: 22, text: "König Joasch dachte nicht mehr an die Treue, mit der ihm Jojada, der Vater Secharjas, gedient hatte, sondern ließ dessen Sohn töten. Dieser aber rief sterbend aus: Der Herr möge es sehen und vergelten." },
      { number: 23, text: "Um die Jahreswende zog das Heer der Aramäer gegen Joasch. Sie drangen nach Juda und Jerusalem vor und machten alle führenden Männer des Volkes nieder. Ihre gesamte Beute brachte man zum König von Damaskus." },
      { number: 24, text: "Mit nur wenig Kriegern war das Heer der Aramäer gekommen; aber der Herr gab ein sehr großes Heer in ihre Gewalt, weil die Israeliten den Herrn, den Gott ihrer Väter, verlassen hatten. So vollzogen die Aramäer an Joasch das Strafgericht." },
      { number: 25, text: "Als sie abzogen und ihn schwer krank zurückließen, verschworen sich seine Diener gegen ihn wegen der Blutschuld am Sohn des Priesters Jojada und erschlugen ihn auf seinem Bett. Man begrub ihn in der Davidstadt, aber nicht in den Gräbern der Könige." },
      { number: 26, text: "Die Verschwörer waren Sabad, der Sohn der Ammoniterin Schimat, und Josabad, der Sohn der Moabiterin Schomer." },
      { number: 27, text: "Weitere Nachrichten über seine Söhne, über die vielen Prophetensprüche gegen ihn und über die Wiederherstellung des Hauses Gottes sind aufgezeichnet in den Erläuterungen zum Buch der Könige. Sein Sohn Amazja wurde König an seiner Stelle." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Amazja war fünfundzwanzig Jahre alt, als er König wurde, und regierte neunundzwanzig Jahre in Jerusalem. Seine Mutter hieß Joaddan und stammte aus Jerusalem.", heading: "Amazja: 25,1-13", footnotes: ["(1-4) 2 Kön 14,1-6"] },
      { number: 2, text: "Er tat, was dem Herrn gefiel, wenn auch nicht mit ungeteiltem Herzen.", footnotes: ["Die Einschränkung bezieht sich auf V. 14."] },
      { number: 3, text: "Sobald die Herrschaft fest in seiner Hand war, ließ er die Diener hinrichten, die seinen Vater, den König, erschlagen hatten." },
      { number: 4, text: "Ihre Söhne aber verschonte er, wie es im Gesetz, im Buch des Mose, geschrieben steht, wo der Herr geboten hat: Die Väter sollen nicht für ihre Söhne und die Söhne nicht für ihre Väter mit dem Tod bestraft werden, sondern jeder soll nur für sein eigenes Verbrechen sterben.", footnotes: ["Dtn 24,16; Ez 18,10-20"] },
      { number: 5, text: "Amazja versammelte die Judäer aus ganz Juda und Benjamin und stellte sie nach Großfamilien unter den Führern der Tausend- und Hundertschaften auf. Er ließ alle, die zwanzig Jahre und darüber waren, mustern. Ihre Zählung ergab dreihunderttausend auserlesene, wehrfähige Männer, die Lanze und Schild trugen." },
      { number: 6, text: "Dazu ließ er in Israel hunderttausend tapfere Krieger um hundert Silbertalente anwerben." },
      { number: 7, text: "Doch ein Gottesmann kam zu ihm und sagte: Das Heer Israels, o König, soll nicht mit dir ziehen; denn der Herr ist nicht mit Israel, er hilft keinem Efraimiter.", footnotes: ["19,2; 20,37"] },
      { number: 8, text: "Rück vielmehr allein aus und nimm mutig und entschlossen den Kampf auf! Gott könnte dich sonst vor dem Feind zu Fall bringen; denn Gott hat die Macht, zu helfen oder zu stürzen." },
      { number: 9, text: "Amazja erwiderte dem Gottesmann: Wozu habe ich dann den israelitischen Söldnerscharen hundert Talente gegeben? Der Gottesmann antwortete: Der Herr kann dir viel mehr als diese geben." },
      { number: 10, text: "Da schied Amazja die Truppe, die aus Efraim zu ihm gekommen war, aus und ließ sie in ihre Heimat zurückkehren. Sie geriet deswegen in heftigen Zorn über Juda und zog grollend in ihr Land zurück." },
      { number: 11, text: "Amazja aber fasste Mut und führte sein Volk in den Krieg. Er zog in das Salztal und erschlug von den Seïritern zehntausend Mann." },
      { number: 12, text: "Zehntausend nahmen die Judäer lebend gefangen, führten sie auf die Höhe eines Felsens und stürzten sie hinab, sodass alle zerschmettert wurden." },
      { number: 13, text: "Die Angehörigen der Söldnertruppe aber, die Amazja zurückgeschickt und am Kriegszug nicht hatte teilnehmen lassen, überfielen die Städte Judas zwischen Samaria und Bet-Horon, erschlugen dort dreitausend Mann und machten reiche Beute." },
      { number: 14, text: "Nach der Rückkehr vom Sieg über die Edomiter stellte Amazja die Götter der Seïriter, die er mitgebracht hatte, als Götter für sich auf, fiel vor ihnen nieder und brachte ihnen Opfer dar.", heading: "Amazjas Übermut und Tod: 25,14-28" },
      { number: 15, text: "Da entbrannte der Zorn des Herrn gegen Amazja. Er sandte einen Propheten zu ihm und ließ ihm sagen: Warum suchst du die Götter des Volkes, die ihr Volk aus deiner Hand nicht retten konnten?" },
      { number: 16, text: "Amazja fiel ihm ins Wort und rief: Haben wir dich zum Berater des Königs bestellt? Halt ein! Warum soll man dich schlagen? Da hielt der Prophet inne und sagte: Ich weiß, dass Gott dein Verderben plant, weil du das getan und auf meinen Rat nicht gehört hast." },
      { number: 17, text: "Nach einer Beratung sandte Amazja, der König von Juda, Boten an Joasch, den Sohn des Joahas, des Sohnes Jehus, den König von Israel, und ließ ihm sagen: Komm, wir wollen (im Kampf) einander gegenübertreten.", footnotes: ["(17-28) 2 Kön 14,8-20"] },
      { number: 18, text: "Doch Joasch, der König von Israel, ließ dem König Amazja von Juda sagen: Der Dornstrauch auf dem Libanon ließ der Zeder auf dem Libanon sagen: Gib deine Tochter meinem Sohn zur Frau! Aber die Tiere des Libanon liefen über den Dornstrauch und zertraten ihn." },
      { number: 19, text: "Du denkst: Ich habe Edom besiegt!, und bist übermütig geworden. Wahre jetzt deinen Ruhm und bleib zu Hause! Wozu willst du das Unglück herausfordern und zu Fall kommen, du und Juda mit dir?" },
      { number: 20, text: "Doch Amazja wollte nicht hören; denn Gott hatte es so bestimmt, um sie ihren Feinden preiszugeben, weil sie die Götter der Edomiter suchten." },
      { number: 21, text: "Joasch, der König von Israel, rückte daraufhin aus. Er und Amazja, der König von Juda, traten bei Bet-Schemesch, das zu Juda gehört, einander gegenüber." },
      { number: 22, text: "Die Judäer wurden von den Israeliten geschlagen und flohen zu ihren Zelten." },
      { number: 23, text: "Den König Amazja von Juda, den Sohn des Joasch, des Sohnes des Joahas, nahm König Joasch von Israel bei Bet-Schemesch gefangen und brachte ihn nach Jerusalem. Dort riss er die Mauer der Stadt vom Efraimtor bis zum Ecktor auf einer Strecke von vierhundert Ellen nieder,", footnotes: ["Joahas: vgl. die Anmerkung zu 21,17."] },
      { number: 24, text: "nahm alles Gold und Silber sowie alle Geräte, die sich im Haus Gottes bei Obed-Edom befanden, samt den Schätzen des königlichen Palastes, ließ sich Geiseln stellen und kehrte nach Samaria zurück.", footnotes: ["1 Chr 26,15"] },
      { number: 25, text: "Amazja, der Sohn des Joasch, der König von Juda, lebte nach dem Tod des Joasch, des Sohnes des Joahas, des Königs von Israel, noch fünfzehn Jahre." },
      { number: 26, text: "Die übrige Geschichte Amazjas, die frühere und die spätere, ist aufgezeichnet im Buch der Könige von Juda und Israel." },
      { number: 27, text: "Zur Zeit, da Amazja sich vom Herrn abwandte, bildete sich in Jerusalem gegen ihn eine Verschwörung. Er floh nach Lachisch; aber man sandte Verfolger hinter ihm her nach Lachisch, die ihn dort erschlugen." },
      { number: 28, text: "Man brachte ihn auf Pferden nach Jerusalem und begrub ihn bei seinen Vätern in der Davidstadt.", footnotes: ["in der Davidstadt: Text korr. nach G und 2 Kön 14,20; H: in der Stadt Judas."] },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Das ganze Volk von Juda nahm Usija, der damals sechzehn Jahre alt war, und machte ihn zum König anstelle seines Vaters Amazja.", heading: "Usija (Asarja): 26,1-23", footnotes: ["(1-23) 2 Kön 14,21-22; 15,1-7"] },
      { number: 2, text: "Er baute Elat aus, das er für Juda zurückgewonnen hatte, nachdem Amazja zu seinen Vätern entschlafen war." },
      { number: 3, text: "Usija war sechzehn Jahre alt, als er König wurde, und regierte zweiundfünfzig Jahre in Jerusalem. Seine Mutter hieß Jecholja und stammte aus Jerusalem." },
      { number: 4, text: "Genau wie sein Vater Amazja tat er, was dem Herrn gefiel.", footnotes: ["Der Vergleich mit Amazja ist aus 2 Kön 15,3 übernommen; vgl. aber die Anmerkung zu 25,2."] },
      { number: 5, text: "Er war bestrebt, Gott zu suchen, solange Secharja lebte, der ihn in der Furcht Gottes unterwies, und solange er den Herrn suchte, ließ ihn Gott erfolgreich sein.", footnotes: ["in der Furcht: Text korr. nach G. - Secharja ist sonst unbekannt."] },
      { number: 6, text: "Er unternahm einen Kriegszug gegen die Philister, riss die Mauern von Gat, Jabne und Aschdod nieder und baute Städte bei Aschdod und im übrigen Gebiet der Philister aus." },
      { number: 7, text: "Gott stand ihm bei gegen die Philister, gegen die Araber, die in Gur-Baal wohnten, und gegen die Mëuniter.", footnotes: ["Zu den Mëunitern vgl. die Anmerkung zu 20,1."] },
      { number: 8, text: "Die Ammoniter zahlten ihm Tribut und sein Ruhm reichte bis an die Grenze Ägyptens, denn er war überaus mächtig geworden." },
      { number: 9, text: "In Jerusalem baute Usija Türme am Ecktor und am Taltor sowie im Winkel und befestigte sie." },
      { number: 10, text: "Auch in der Steppe baute er Türme und grub viele Zisternen. Er besaß nämlich große Herden in der Schefela und in der Ebene und beschäftigte Acker- und Weinbauern in den Bergen und im Fruchtland, denn er liebte den Ackerbau." },
      { number: 11, text: "Usija hatte ein kriegstüchtiges Heer, das, nach Abteilungen gegliedert, in der Stärke ausrückte, die der Staatsschreiber Jëiël und der Amtmann Maaseja unter Aufsicht Hananjas, eines der Obersten des Königs, bei der Musterung festgestellt hatten." },
      { number: 12, text: "Unter den Kriegern waren insgesamt zweitausendsechshundert Häupter von Großfamilien." },
      { number: 13, text: "Sie befehligten ein Heer von dreihundertsiebentausendfünfhundert kampffähigen Männern, die stark genug waren, um dem König gegen seine Feinde zu helfen." },
      { number: 14, text: "Für das ganze Heer stellte Usija Schilde, Lanzen, Helme, Panzer, Bogen und Schleudersteine bereit." },
      { number: 15, text: "In Jerusalem ließ er kunstvolle Wurfmaschinen bauen und auf den Türmen und Mauerecken aufstellen, um Pfeile und große Steine abschießen zu können. So wurde er weithin berühmt; denn ihm wurde außergewöhnliche Hilfe zuteil, sodass er an Macht gewann." },
      { number: 16, text: "Doch als er mächtig geworden war, wurde sein Herz übermütig und er handelte verkehrt. Er wurde dem Herrn, seinem Gott, untreu und drang in den Tempel des Herrn ein, um auf dem Rauchopferaltar zu opfern." },
      { number: 17, text: "Aber der Priester Asarja folgte ihm mit achtzig mutigen Priestern des Herrn." },
      { number: 18, text: "Sie traten dem König Usija entgegen und sagten zu ihm: Nicht dir, Usija, steht es zu, dem Herrn Rauchopfer darzubringen, sondern den Priestern, den Söhnen Aarons, die geweiht sind, das Rauchopfer darzubringen. Verlass das Heiligtum; denn du bist untreu geworden und es gereicht dir nicht zur Ehre vor Gott, dem Herrn." },
      { number: 19, text: "Usija wurde zornig; er hielt schon die Räucherpfanne in der Hand, um das Rauchopfer darzubringen. Als er sich aber zornig gegen die Priester wandte, brach an seiner Stirn der Aussatz hervor. Es geschah vor den Augen der Priester, während er im Tempel neben dem Rauchopferaltar stand." },
      { number: 20, text: "Als daher der Oberpriester Asarja und alle anderen Priester sich ihm zuwandten, zeigte sich der Aussatz an seiner Stirn. Sie drängten ihn eiligst von dort weg und auch er selbst beeilte sich hinauszukommen, da der Herr ihn geschlagen hatte." },
      { number: 21, text: "So war König Usija aussätzig bis zu seinem Tod. Da er vom Haus des Herrn ausgeschlossen war, musste er als Aussätziger in einem abgesonderten Haus wohnen, während sein Sohn Jotam Vorsteher des königlichen Palastes war und die Bürger des Landes regierte." },
      { number: 22, text: "Die übrige Geschichte Usijas, die frühere und die spätere, schrieb der Prophet Jesaja, der Sohn des Amoz." },
      { number: 23, text: "Usija entschlief zu seinen Vätern und man begrub ihn bei seinen Vätern auf dem Feld bei der Grabstätte der Könige; denn man sagte: Er war aussätzig. Sein Sohn Jotam wurde König an seiner Stelle." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Jotam war fünfundzwanzig Jahre alt, als er König wurde, und regierte sechzehn Jahre in Jerusalem. Seine Mutter hieß Jeruscha und war eine Tochter Zadoks.", heading: "Jotam: 27,1-9", footnotes: ["(1-9) 2 Kön 15,32-38"] },
      { number: 2, text: "Er tat, was dem Herrn gefiel, genau wie sein Vater Usija, aber er drang nicht in den Tempel des Herrn ein. Das Volk jedoch handelte immer noch verkehrt.", footnotes: ["26,16"] },
      { number: 3, text: "Er baute das obere Tor am Haus des Herrn. Auch an der Mauer des Ofel baute er viel." },
      { number: 4, text: "Ebenso baute er Städte im Gebirge Juda aus, errichtete Burgen und Türme in den Waldgebieten," },
      { number: 5, text: "führte Krieg mit dem König der Ammoniter und besiegte ihn. Die Ammoniter mussten ihm in jenem Jahr hundert Talente Silber, zehntausend Kor Weizen und zehntausend Kor Gerste abliefern. Die gleiche Abgabe leisteten sie auch im zweiten und dritten Jahr." },
      { number: 6, text: "So wurde Jotam mächtig; denn er achtete in seinem Verhalten auf den Herrn, seinen Gott." },
      { number: 7, text: "Die übrige Geschichte Jotams, alle seine Kriege und Unternehmungen, sind aufgezeichnet im Buch der Könige von Israel und Juda." },
      { number: 8, text: "Er war fünfundzwanzig Jahre alt, als er König wurde, und regierte sechzehn Jahre in Jerusalem." },
      { number: 9, text: "Jotam entschlief zu seinen Vätern und man begrub ihn in der Davidstadt. Sein Sohn Ahas wurde König an seiner Stelle." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Ahas war zwanzig Jahre alt, als er König wurde, und regierte sechzehn Jahre in Jerusalem. Er tat nicht wie sein Vater David, was dem Herrn gefiel,", heading: "Ahas: 28,1-15", footnotes: ["(1-8) 2 Kön 16,1-5", "Wahrscheinlich regierte Ahas nur sechs, nicht sechzehn Jahre (vgl. die Anmerkung zu 2 Kön 16,2)."] },
      { number: 2, text: "sondern er folgte den Wegen der Könige von Israel. Auch ließ er Gussbilder für die Baale anfertigen," },
      { number: 3, text: "opferte im Tal Ben-Hinnom, verbrannte seine Söhne im Feuer und ahmte so die Gräuel der Völker nach, die der Herr vor den Augen der Israeliten vertrieben hatte." },
      { number: 4, text: "Auf den Kulthöhen und Hügeln und unter jedem üppigen Baum brachte er Schlacht- und Rauchopfer dar." },
      { number: 5, text: "Darum gab ihn der Herr, sein Gott, in die Hand des Königs der Aramäer. Sie schlugen ihn, führten ihm viele Gefangene weg und brachten sie nach Damaskus. Ebenso geriet er in die Hand des Königs von Israel. Pekach, der Sohn Remaljas, bereitete ihm eine schwere Niederlage." },
      { number: 6, text: "Er erschlug an einem Tag in Juda hundertzwanzigtausend Mann, lauter tapfere Krieger, da sie den Herrn, den Gott ihrer Väter, verlassen hatten." },
      { number: 7, text: "Sichri, ein efraimitischer Kriegsheld, tötete den Königssohn Maaseja, den Hausvorsteher Asrikam und Elkana, den Zweiten nach dem König." },
      { number: 8, text: "Die Israeliten führten von ihren Stammesbrüdern zweihunderttausend Frauen, Söhne und Töchter als Gefangene weg, machten bei ihnen auch reiche Beute und brachten sie nach Samaria." },
      { number: 9, text: "Dort lebte ein Prophet des Herrn namens Oded. Dieser ging dem Heer entgegen, das nach Samaria zurückkehrte, und hielt ihm vor: Seht, weil der Herr, der Gott eurer Väter, über Juda erzürnt war, hat er sie in eure Hand gegeben. Ihr habt unter ihnen mit einer Wut gemordet, die zum Himmel schreit." },
      { number: 10, text: "Jetzt wollt ihr euch Leute aus Juda und Jerusalem als Knechte und Mägde untertan machen. Steht denn nicht ihr gerade in Schuld vor dem Herrn, eurem Gott?" },
      { number: 11, text: "Hört daher jetzt auf mich! Gebt die Gefangenen zurück, die ihr von euren Stammesbrüdern weggeführt habt. Es würde sonst der glühende Zorn des Herrn euch treffen." },
      { number: 12, text: "Darauf traten einige von den Häuptern der Efraimiter, nämlich Asarja, der Sohn Johanans, Berechja, der Sohn Meschillemots, Jehiskija, der Sohn Schallums, und Amasa, der Sohn Hadlais, zu den Heimkehrern vom Feldzug hin," },
      { number: 13, text: "redeten mit ihnen und sagten: Bringt die Gefangenen nicht hierher! Schon liegt eine Schuld vor dem Herrn auf uns und ihr wollt unsere Sünde und Schuld noch vermehren. Ist doch unsere Schuld schon groß genug und der glühende Zorn des Herrn lastet auf Israel." },
      { number: 14, text: "Daraufhin gaben die bewaffneten Krieger in Gegenwart der Obersten und der ganzen Versammlung die Gefangenen und die Beute frei." },
      { number: 15, text: "Männer, die namentlich dazu bestimmt waren, gingen hin und nahmen sich der Gefangenen an. Sie bekleideten alle, die nackt waren, aus der Beute und versahen sie mit Gewändern und Schuhen. Sie gaben ihnen zu essen und zu trinken, salbten die Schwachen unter ihnen und setzten sie auf Esel. So brachten sie die Gefangenen in die Palmenstadt Jericho in die Nähe ihrer Stammesbrüder. Sie selbst kehrten nach Samaria zurück." },
      { number: 16, text: "In jener Zeit sandte König Ahas einen Hilferuf an den König von Assur;", heading: "Die Bedrängnis durch Assur: 28,16-27", footnotes: ["(16-27) 2 Kön 16,7-19", "16-18: Das geschah während des Syrisch-efraimitischen Kriegs (vgl. 2 Kön 16,5-7)."] },
      { number: 17, text: "denn auch die Edomiter waren eingedrungen, hatten Juda besiegt und Gefangene weggeführt." },
      { number: 18, text: "Zugleich überfielen die Philister die Städte der Schefela und des judäischen Negeb, eroberten Bet-Schemesch, Ajalon, Gederot, Socho mit seinen Tochterstädten, Timna mit seinen Tochterstädten und Gimso mit seinen Tochterstädten und setzten sich darin fest;" },
      { number: 19, text: "denn der Herr demütigte Juda wegen Ahas', des Königs von Juda, der die Zügellosigkeit in Juda förderte und gegen den Herrn treulos war.", footnotes: ["König von Juda: Text korr. nach G; H: Israel, vgl. aber die Anmerkung zu 10,1."] },
      { number: 20, text: "Tiglat-Pileser, der König von Assur, kam zu ihm; doch brachte er ihm nur Bedrängnis, aber keine Stärkung." },
      { number: 21, text: "Ahas musste den Tempel, den königlichen Palast und die führenden Männer berauben und alles dem König von Assur geben, fand jedoch keine Hilfe." },
      { number: 22, text: "Selbst in der Zeit der Bedrängnis setzte König Ahas seine Treulosigkeit gegen den Herrn fort." },
      { number: 23, text: "Er brachte den Göttern von Damaskus, die ihn geschlagen hatten, Opfer dar und sagte: Die Götter der aramäischen Könige haben ihren Verehrern geholfen. Ihnen will ich opfern und sie werden mir helfen. Doch sie dienten nur dazu, ihn und ganz Israel zu Fall zu bringen." },
      { number: 24, text: "Ahas ließ die Geräte des Hauses Gottes zusammenholen und zerschlagen, schloss die Tore des Hauses des Herrn und errichtete Altäre an allen Ecken Jerusalems." },
      { number: 25, text: "In jeder einzelnen Stadt Judas baute er Kulthöhen, um anderen Göttern zu opfern. So erzürnte er den Herrn, den Gott seiner Väter." },
      { number: 26, text: "Seine übrige Geschichte und alle seine Unternehmungen, die früheren und die späteren, sind aufgezeichnet im Buch der Könige von Juda und Israel." },
      { number: 27, text: "Ahas entschlief zu seinen Vätern und man begrub ihn in Jerusalem, innerhalb der Stadt, legte ihn also nicht in die Gräber der Könige von Israel. Sein Sohn Hiskija wurde König an seiner Stelle." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Hiskija war fünfundzwanzig Jahre alt, als er König wurde, und regierte neunundzwanzig Jahre in Jerusalem. Seine Mutter hieß Abi und war eine Tochter Secharjas.", heading: "Einleitung zur Geschichte Hiskijas: 29,1-2", footnotes: ["(1-2) 2 Kön 18,1-3"] },
      { number: 2, text: "Genau wie sein Vater David tat er, was dem Herrn gefiel." },
      { number: 3, text: "Im ersten Jahr seiner Regierung, im ersten Monat, öffnete er die Tore des Hauses des Herrn und setzte sie wieder instand.", heading: "Die Wiederherstellung des Kultes: 29,3-36", footnotes: ["(3-36) 2 Kön 18,4-8"] },
      { number: 4, text: "Darauf ließ er die Priester und die Leviten kommen, versammelte sie auf dem Platz im Osten" },
      { number: 5, text: "und sagte zu ihnen: Leviten, hört mich an! Heiligt euch jetzt und heiligt das Haus des Herrn, des Gottes eurer Väter! Schafft alles Unreine aus dem Heiligtum!" },
      { number: 6, text: "Unsere Väter haben treulos gehandelt und getan, was dem Herrn, unserem Gott, missfällt. Sie haben ihn verlassen und ihre Blicke von der Wohnstätte des Herrn abgewandt und ihr den Rücken gekehrt." },
      { number: 7, text: "Sogar die Tore der Vorhalle (des Tempels) haben sie geschlossen, die Lampen ausgelöscht, kein Rauchopfer angezündet und dem Gott Israels im Heiligtum kein Brandopfer dargebracht." },
      { number: 8, text: "Darum kam der Zorn des Herrn über Juda und Jerusalem. Er machte sie zum Schreckbild, zum Gegenstand des Entsetzens und zum Gespött, wie ihr es mit eigenen Augen seht." },
      { number: 9, text: "Deswegen sind unsere Väter unter dem Schwert gefallen und sind unsere Söhne, Töchter und Frauen in Gefangenschaft." },
      { number: 10, text: "Ich habe nun vor, einen Bund mit dem Herrn, dem Gott Israels, zu schließen, damit sein glühender Zorn von uns ablässt." },
      { number: 11, text: "Meine Söhne, seid jetzt nicht nachlässig! Euch hat ja der Herr dazu erwählt, dass ihr vor ihm steht und ihm dient. Ihr sollt seine Diener sein und ihm Opfer darbringen." },
      { number: 12, text: "Da standen folgende Leviten auf: Mahat, der Sohn Amasais, und Joël, der Sohn Asarjas, von den Nachkommen der Kehatiter; Kisch, der Sohn Abdis, und Asarja, der Sohn Jehallelels, von den Nachkommen Meraris; Joach, der Sohn Simmas, und Eden, der Sohn Joachs, von den Gerschonitern;" },
      { number: 13, text: "Schimri und Jëiël von den Nachkommen Elizafans; Secharja und Mattanja von den Nachkommen Asafs;" },
      { number: 14, text: "Jehiël und Schimi von den Nachkommen Hemans; Schemaja und Usiël von den Nachkommen Jedutuns." },
      { number: 15, text: "Sie versammelten ihre Stammesbrüder und heiligten sich. Dann gingen sie auf Befehl des Königs, der sich auf die Worte des Herrn stützte, daran, das Haus des Herrn zu reinigen." },
      { number: 16, text: "Die Priester betraten das Innere des Hauses des Herrn, um es zu reinigen. Sie schafften alles Unreine, das sie im Tempel des Herrn fanden, in den Hof des Hauses des Herrn. Dort übernahmen es die Leviten und trugen es in das Kidrontal hinaus." },
      { number: 17, text: "Am ersten Tag des ersten Monats begannen sie mit der Reinigung; am achten Tag des Monats kamen sie zur Vorhalle des Herrn; dann heiligten sie das Haus des Herrn innerhalb von acht Tagen. Am sechzehnten Tag des ersten Monats hatten sie die Arbeit vollendet." },
      { number: 18, text: "Darauf gingen sie zum König Hiskija hinein und meldeten: Wir haben das ganze Haus des Herrn gesäubert. Den Brandopferaltar mit all seinen Geräten, den Tisch für die Schaubrote mit all seinen Geräten," },
      { number: 19, text: "alle Geräte, die König Ahas während seiner Regierung durch seine Treulosigkeit entweiht hat, haben wir instand gesetzt und geheiligt. Sie stehen nun vor dem Altar des Herrn." },
      { number: 20, text: "Am nächsten Morgen versammelte König Hiskija die führenden Männer der Stadt und ging mit ihnen in das Haus des Herrn hinauf." },
      { number: 21, text: "Man führte sieben Stiere, sieben Widder, sieben Lämmer und sieben Ziegenböcke als Sündopfer für das Königshaus, für das Heiligtum und für Juda herbei und der König befahl den Priestern, den Nachkommen Aarons, sie auf dem Altar des Herrn darzubringen." },
      { number: 22, text: "Man schlachtete die Rinder; die Priester fingen das Blut auf und sprengten es an den Altar. Dann schlachteten sie die Widder und sprengten das Blut an den Altar. Dann schlachteten sie die Lämmer und sprengten das Blut an den Altar." },
      { number: 23, text: "Zuletzt brachte man die Böcke für das Sündopfer vor den König und die Versammlung, damit sie die Hände auf sie legten." },
      { number: 24, text: "Dann schlachteten die Priester die Böcke und sprengten das Blut als Sündopfer an den Altar, um für ganz Israel Sühne zu erwirken; denn der König hatte das Brandopfer und das Sündopfer für ganz Israel bestimmt." },
      { number: 25, text: "Er stellte die Leviten mit Zimbeln, Harfen und Zithern im Haus des Herrn auf, wie es der Anordnung Davids, des königlichen Sehers Gad und des Propheten Natan entsprach. Diese Weisungen waren vom Herrn durch seine Propheten ergangen.", footnotes: ["1 Chr 16,4-6"] },
      { number: 26, text: "Die Leviten traten mit den Instrumenten Davids und die Priester mit ihren Trompeten an." },
      { number: 27, text: "Dann ließ Hiskija das Brandopfer auf dem Altar darbringen. Als das Opfer begann, setzten gleichzeitig auch der Gesang zur Ehre des Herrn und die Trompeten ein, begleitet von den Instrumenten Davids, des Königs von Israel." },
      { number: 28, text: "Die ganze Versammlung warf sich nieder, während der Gesang ertönte und die Trompeten schmetterten. Dies alles dauerte bis zum Abschluss des Opfers." },
      { number: 29, text: "Nach der Darbringung des Brandopfers verneigten sich der König und alle, die sich bei ihm eingefunden hatten, und warfen sich nieder." },
      { number: 30, text: "König Hiskija und die führenden Männer befahlen den Leviten, den Herrn mit den Worten Davids und des Sehers Asaf zu preisen. Diese sangen den Lobpreis mit Freuden, verneigten sich und warfen sich nieder." },
      { number: 31, text: "Hierauf nahm Hiskija das Wort und sagte: Ihr steht jetzt mit vollen Händen vor dem Herrn. Tretet heran und bringt Schlacht- und Dankopfer zum Haus des Herrn! Da brachte die Versammlung Schlachtund Dankopfer herbei; manche gaben freiwillig noch Brandopfer hinzu.", footnotes: ["Wörtlich: Ihr habt jetzt eure Hand für den Herrn gefüllt."] },
      { number: 32, text: "Die Anzahl der Brandopfer, die die Versammlung spendete, betrug siebzig Rinder, hundert Widder und zweihundert Lämmer. Sie wurden alle als Brandopfer dem Herrn dargebracht." },
      { number: 33, text: "Die Weihegaben beliefen sich auf sechshundert Rinder und dreitausend Schafe.", footnotes: ["Die Menge der Tiere benötigte man zum Opfermahl, das mit der Einweihungsfeier verbunden war."] },
      { number: 34, text: "Es waren aber zu wenig Priester vorhanden, um alle Brandopfer enthäuten zu können. Daher halfen ihnen ihre Amtsbrüder, die Leviten, bis die Arbeit geschehen war und die Priester sich geheiligt hatten; denn die Leviten hatten sich eifriger um ihre Heiligung bemüht als die Priester.", footnotes: ["Die Priester und Leviten mussten vor der Ausübung ihres Dienstes «sich heiligen», d.h. verschiedene Reinigungsriten auf sich nehmen."] },
      { number: 35, text: "Es war aber auch die Anzahl der Brandopfer sehr groß, die neben den Fettstücken der Heilsopfer und den zum Brandopfer gehörenden Trankopfern dargebracht werden mussten. So wurde der Dienst im Haus des Herrn wiederhergestellt." },
      { number: 36, text: "Hiskija und das ganze Volk freuten sich, dass Gott dies dem Volk gewährt hatte; denn es war wider Erwarten schnell geschehen." },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Hiskija sandte Boten zu ganz Israel und Juda, schrieb auch Briefe an Efraim und Manasse und forderte sie auf, zum Haus des Herrn nach Jerusalem zu kommen und zur Ehre des Herrn, des Gottes Israels, das Pascha zu feiern.", heading: "Die Feier des Paschafestes: 30,1 - 31,1" },
      { number: 2, text: "Dann beriet er sich mit seinen Hofleuten und der ganzen Versammlung in Jerusalem, ob sie das Pascha nicht erst im zweiten Monat begehen sollten;", footnotes: ["Num 9,10f gestattet in Ergänzung zu Ex 12,6 in besonderen Fällen die Verlegung des Pascha auf den zweiten Monat. Zur Paschafeier des ganzen Volkes vgl. die Anmerkung zu Ex 12,1 - 13,6."] },
      { number: 3, text: "denn sie konnten es damals nicht abhalten, da sich nicht genügend Priester geheiligt hatten und das Volk nicht in Jerusalem versammelt war." },
      { number: 4, text: "Der Plan gefiel dem König und der ganzen Versammlung." },
      { number: 5, text: "Sie beschlossen, man solle in ganz Israel von Dan bis Beerscheba ausrufen, dass sie kommen und in Jerusalem zur Ehre des Herrn, des Gottes Israels, das Pascha feiern sollten; denn man hatte es bisher nicht vollzählig so gehalten, wie es vorgeschrieben war." },
      { number: 6, text: "Die Eilboten durchzogen nun mit den Briefen aus der Hand des Königs und seiner Hofleute ganz Israel und Juda und verkündeten im Auftrag des Königs: Israeliten, kehrt um zum Herrn, dem Gott Abrahams, Isaaks und Israels, damit er sich dem Rest zuwendet, der von der Hand der Könige von Assur verschont geblieben ist." },
      { number: 7, text: "Seid nicht wie eure Väter und Brüder, die dem Herrn, dem Gott ihrer Väter, untreu geworden sind, sodass er sie zu einem Bild des Entsetzens machte, wie ihr es vor Augen habt." },
      { number: 8, text: "Seid jetzt nicht hartnäckig wie eure Väter! Reicht dem Herrn die Hand und kommt in sein Heiligtum, das er für immer geheiligt hat. Dient dem Herrn, eurem Gott, damit sein Zorn von euch ablässt." },
      { number: 9, text: "Wenn ihr zum Herrn zurückkehrt, werden eure Brüder und Söhne Erbarmen finden bei denen, die sie als Gefangene weggeführt haben. Sie werden in dieses Land zurückkehren dürfen; denn der Herr, euer Gott, ist gnädig und barmherzig. Er wird sein Angesicht nicht von euch abwenden, wenn ihr zu ihm umkehrt.", footnotes: ["Im Jahr 722 v. Chr. war das Nordreich untergegangen und ein Teil der Bevölkerung nach Assyrien in die Gefangenschaft verschleppt worden."] },
      { number: 10, text: "Die Eilboten zogen von Stadt zu Stadt durch das Gebiet von Efraim und Manasse bis nach Sebulon. Doch man lachte und spottete über sie." },
      { number: 11, text: "Nur einige Männer von Ascher, Manasse und Sebulon beugten sich und kamen nach Jerusalem." },
      { number: 12, text: "Auch in Juda waltete die Hand Gottes und bewirkte, dass sie einmütig den Befehl des Königs und der führenden Männer befolgten, wie es dem Wort des Herrn entsprach." },
      { number: 13, text: "So versammelte sich viel Volk in Jerusalem, um im zweiten Monat das Fest der Ungesäuerten Brote zu begehen. Es war eine sehr große Versammlung." },
      { number: 14, text: "Sie machten sich auf, entfernten die Altäre in Jerusalem, beseitigten alle Rauchopferaltäre und warfen sie in das Kidrontal." },
      { number: 15, text: "Am vierzehnten Tag des zweiten Monats schlachteten sie das Pascha. Die Priester und Leviten aber fühlten sich beschämt. Sie heiligten sich, brachten Brandopfer zum Haus des Herrn" },
      { number: 16, text: "und begaben sich pflichtgemäß an ihren Platz, nach dem Gesetz des Mose, des Mannes Gottes. Die Priester sprengten das Blut (an den Altar), das sie von den Leviten entgegennahmen." },
      { number: 17, text: "Weil viele in der Versammlung sich nicht geheiligt hatten, schlachteten die Leviten die Paschalämmer für alle, denen die Reinheit fehlte, die man für die Weihe an den Herrn brauchte." },
      { number: 18, text: "Denn ein großer Teil des Volkes, viele nämlich aus Efraim, Manasse, Issachar und Sebulon, hatten sich nicht gereinigt. Sie aßen das Pascha nicht in der vorgeschriebenen Weise. Doch Hiskija betete für sie und sagte: Der Herr, der Gütige, entsühne" },
      { number: 19, text: "jeden, der seinen Sinn darauf richtet, den Herrn, den Gott seiner Väter, zu suchen, auch wenn er nicht die Reinheit besitzt, die dem Heiligtum gebührt." },
      { number: 20, text: "Der Herr hörte auf Hiskija und ließ das Volk unversehrt." },
      { number: 21, text: "So feierten die Israeliten, die sich in Jerusalem eingefunden hatten, das Fest der Ungesäuerten Brote sieben Tage hindurch mit großer Freude. Tag für Tag priesen die Leviten und Priester den Herrn mit lauten Instrumenten." },
      { number: 22, text: "Die Leviten zeigten großes Verständnis für den Dienst des Herrn und Hiskija ermunterte sie zu ihrer Arbeit. Als dann die sieben Tage der Festzeit zu Ende waren, an denen sie Heilsopfer darbrachten und den Herrn, den Gott ihrer Väter, priesen,", footnotes: ["zu Ende waren: Text korr. nach G."] },
      { number: 23, text: "beschloss die ganze Versammlung, weitere sieben Tage zu feiern, und so machten sie auch diese sieben Tage zu einem Freudenfest." },
      { number: 24, text: "Hiskija, der König von Juda, hatte nämlich tausend Stiere und siebentausend Schafe für die Versammlung gestiftet und die führenden Männer hatten tausend Stiere und zehntausend Schafe für die Versammlung gestiftet; von den Priestern aber hatten sich sehr viele geheiligt." },
      { number: 25, text: "So freute sich die ganze Versammlung Judas, die Priester und die Leviten und alle, die aus Israel gekommen waren, auch die Fremden, die sich aus dem Gebiet Israels eingefunden hatten oder in Juda wohnten." },
      { number: 26, text: "Die Freude war groß in Jerusalem; denn seit den Tagen Salomos, des Sohnes Davids, des Königs von Israel, hatte es dergleichen in Jerusalem nicht mehr gegeben.", footnotes: ["Hinweis auf das Tempelweihfest unter Salomo."] },
      { number: 27, text: "Zuletzt standen die levitischen Priester auf und segneten das Volk. Gott hörte ihr Rufen und ihr Gebet drang zu seiner heiligen Wohnung, zum Himmel, empor." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Nachdem sie dies alles beendet hatten, zogen die anwesenden Israeliten insgesamt in die Städte Judas, zertrümmerten die Steinmale, hieben die Kultpfähle um und zerstörten die Kulthöhen und Altäre in ganz Juda, Benjamin, Efraim und Manasse vollständig. Darauf kehrten alle in ihre Städte auf ihren Besitz zurück.", footnotes: ["2 Kön 18,4"] },
      { number: 2, text: "Hiskija stellte die Abteilungen der Priester und Leviten nach ihren Dienstklassen auf. Er wies alle Priester und Leviten einzeln in ihren Dienst ein und bestellte sie zum Vollzug der Brand- und Heilsopfer, zu Dienstleistungen und zum Vortrag der Dank- und Lobgesänge an den Toren des Lagers des Herrn.", heading: "Die Neuordnung des Tempeldienstes: 31,2-21", footnotes: ["Der Ausdruck «Lager des Herrn» vergleicht den Tempelbereich mit dem Zeltlager der Wüstenzeit."] },
      { number: 3, text: "Der Beitrag, den der König aus seinem Vermögen für die Brandopfer leistete, betraf die Opfer am Morgen und am Abend sowie die Brandopfer an den Sabbaten, Neumondtagen und Festen, die durch das Gesetz des Herrn vorgeschrieben waren." },
      { number: 4, text: "Hiskija befahl dem Volk, das heißt, den Einwohnern Jerusalems, die den Priestern und den Leviten zustehenden Anteile abzuliefern, damit sie sich ganz dem Gesetz des Herrn widmen könnten.", footnotes: ["Nach Num 18,21-32 gehörten den Priestern und Leviten die Zehnten und Erstlingsfrüchte."] },
      { number: 5, text: "Sobald der Befehl bekannt wurde, brachten die Israeliten die Ersterträge von Getreide, Most, Öl, Honig und allen Früchten des Feldes reichlich herbei und lieferten den Zehnten von allem vollzählig ab." },
      { number: 6, text: "Ebenso brachten die Israeliten und die Judäer, die in den Städten Judas wohnten, den Zehnten von den Rindern und Schafen sowie den Zehnten von den Weihegaben, die dem Herrn, ihrem Gott, übergeben wurden. Sie reihten Stapel an Stapel.", footnotes: ["Num 18,8-32"] },
      { number: 7, text: "Im dritten Monat fingen sie an, die unterste Schicht zu legen, und im siebten Monat waren sie damit fertig." },
      { number: 8, text: "Als dann Hiskija und die führenden Männer kamen und die aufgestapelten Mengen sahen, priesen sie den Herrn und sein Volk Israel." },
      { number: 9, text: "Hiskija erkundigte sich bei den Priestern und den Leviten wegen der aufgestapelten Mengen" },
      { number: 10, text: "und der Oberpriester Asarja aus dem Haus Zadok gab zur Antwort: Seitdem man begonnen hat, die Abgaben in das Haus des Herrn zu bringen, haben wir zu essen, werden satt und lassen noch reichlich übrig; denn der Herr hat sein Volk gesegnet. Diese Menge ist übrig geblieben." },
      { number: 11, text: "Hiskija ließ nun Kammern im Haus des Herrn herrichten, und als man sie hergerichtet hatte," },
      { number: 12, text: "brachte man die Abgaben, die Zehnten und die Weihegaben gewissenhaft dorthin. Der Levit Konanja war Aufseher über sie und sein Bruder Schimi sein Stellvertreter." },
      { number: 13, text: "Ihnen standen auf Anordnung des Königs Hiskija und Asarjas, des Fürsten des Hauses Gottes, Jehiël, Asasja, Nahat, Asaël, Jerimot, Josabad, Eliël, Jismachja, Mahat und Benaja als Beamte zur Seite." },
      { number: 14, text: "Der Levit Kore, der Sohn Jimnas, der Wächter am Osttor, wachte über die freiwilligen Spenden an Gott, damit die für den Herrn bestimmte Abgabe und das Hochheilige abgeliefert würden.", footnotes: ["das Hochheilige: die Anteile, die bei bestimmten Opfern von den Priestern am Heiligtum gegessen werden mussten (vgl. Lev 2,3; 6,10.18; 7,6; 14,13)."] },
      { number: 15, text: "Ihm standen Eden, Minjamin, Jeschua, Schemaja, Amarja und Schechanja in den Priesterstädten gewissenhaft zur Seite, damit ihre Amtsbrüder, die Vornehmen wie die Geringen, abteilungsweise ihre Anteile erhielten." },
      { number: 16, text: "Außerdem hatte man für alle, die abteilungsweise in das Haus des Herrn kamen, um aufgrund ihrer Obliegenheiten und entsprechend den Erfordernissen eines jeden Tages ihren Dienst zu verrichten, Verzeichnisse angelegt. Sie erfassten alle männlichen Personen, die drei Jahre und darüber waren." },
      { number: 17, text: "Dabei war die Eintragung der Priester nach Großfamilien geschehen; die Leviten wurden, von zwanzig Jahren an aufwärts, nach ihren Obliegenheiten und nach ihren Abteilungen aufgeschrieben." },
      { number: 18, text: "Die Eintragung erfasste bei jeder Gemeinschaft alle Kinder, Frauen, Söhne und Töchter; denn auch diese wurden durch deren ständigen Dienst geheiligt." },
      { number: 19, text: "Für die Söhne Aarons, die Priester, die auf den Weideflächen ihrer Städte wohnten, wurden in jeder einzelnen Stadt Männer bestellt, die namentlich bestimmt waren, um allen männlichen Angehörigen der Priester und allen im Verzeichnis stehenden Leviten ihre Anteile zu geben." },
      { number: 20, text: "So verfuhr Hiskija in ganz Juda. Er tat, was gut, recht und getreu war vor dem Herrn, seinem Gott." },
      { number: 21, text: "Bei jedem Unternehmen, das er im Dienst des Hauses Gottes oder für das Gesetz und die Gebote begann, um seinen Gott zu suchen, handelte er mit Hingabe seines ganzen Herzens und hatte Erfolg." },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Nach diesen Maßnahmen und diesen Beweisen der Treue Hiskijas zog Sanherib, der König von Assur, heran. Er fiel in Juda ein, belagerte die festen Städte und gedachte, sie für sich zu erobern.", heading: "Sanheribs Feldzug gegen Jerusalem: 32,1-23", footnotes: ["(1-23) 2 Kön 18,13 - 19,37; Jes 36 und 37"] },
      { number: 2, text: "Als Hiskija sah, dass Sanherib herankam und sich zum Krieg gegen Jerusalem anschickte," },
      { number: 3, text: "überlegte er mit seinen Obersten und Helden, ob man nicht die Wasserquellen außerhalb der Stadt verstopfen solle. Sie unterstützten sein Vorhaben." },
      { number: 4, text: "Man holte viel Volk zusammen und verstopfte alle Quellen und den Bach, der mitten durch das Tal fließt; denn man sagte sich: Wozu sollen die Könige von Assur bei ihrer Ankunft reichlich Wasser finden?" },
      { number: 5, text: "Auch unternahm der König Anstrengungen, um die ganze Mauer auszubessern, in der Risse entstanden waren. Er erhöhte die Türme, baute draußen die andere Mauer, befestigte den Millo in der Davidstadt und ließ viele Wurfspieße und Schilde anfertigen." },
      { number: 6, text: "Dann setzte er Kriegsoberste über das Volk und versammelte sie vor sich am Platz vor dem Stadttor. Dort sprach er ihnen Mut zu und sagte:" },
      { number: 7, text: "Seid mutig und tapfer! Fürchtet euch nicht und erschreckt nicht vor dem König von Assur und dem großen Heer, das bei ihm ist; denn bei uns ist mehr als bei ihm." },
      { number: 8, text: "Bei ihm sind Arme aus Fleisch, bei uns aber ist der Herr, unser Gott, der uns hilft und unsere Kriege führt. Das Volk wurde durch die Worte Hiskijas, des Königs von Juda, ermutigt." },
      { number: 9, text: "Als dann Sanherib, der König von Assur, später mit seiner ganzen Streitmacht vor Lachisch stand, sandte er seine Diener nach Jerusalem zu Hiskija, dem König von Juda, und zu allen Judäern, die in Jerusalem wohnten. Er ließ ihnen sagen:", footnotes: ["9-22: Vgl. die Anmerkungen zu 2 Kön 18 und 19."] },
      { number: 10, text: "So spricht Sanherib, der König von Assur: Worauf vertraut ihr denn, dass ihr im belagerten Jerusalem bleibt?" },
      { number: 11, text: "Hiskija führt euch nur irre, um euch durch Hunger und Durst umkommen zu lassen, wenn er sagt: Der Herr, unser Gott, wird uns aus der Hand des Königs von Assur retten." },
      { number: 12, text: "Hat nicht gerade Hiskija seine Kulthöhen und Altäre beseitigt und in Juda und Jerusalem angeordnet: Nur vor einem einzigen Altar dürft ihr euch niederwerfen und auf ihm Opfer anzünden?" },
      { number: 13, text: "Wisst ihr denn nicht, was ich und meine Väter mit allen Völkern in anderen Ländern gemacht haben? Konnten denn die Götter der Völker in diesen Ländern ihr Land aus meiner Hand retten?" },
      { number: 14, text: "Wer von all den Göttern dieser Völker, die meine Väter dem Untergang geweiht haben, konnte sein Volk aus meiner Hand retten? Wie sollte dann euer Gott euch aus meiner Hand retten?" },
      { number: 15, text: "Lasst euch jetzt nicht von Hiskija betören und euch nicht auf diese Weise irreführen! Glaubt ihm nicht! Denn kein Gott irgendeines anderen Volkes oder Reiches konnte sein Volk aus meiner Hand und aus der Hand meiner Väter retten. Wie viel weniger wird euer Gott euch aus meiner Hand retten!" },
      { number: 16, text: "Noch vieles andere redeten die Abgesandten Sanheribs gegen Gott, den Herrn, und seinen Knecht Hiskija." },
      { number: 17, text: "Sanherib schrieb auch einen Brief, in dem er den Herrn, den Gott Israels, verhöhnte. Er schrieb darin über ihn: Wie die Götter der Völker in anderen Ländern ihr Volk nicht aus meiner Hand gerettet haben, so wird auch der Gott Hiskijas sein Volk nicht aus meiner Hand retten." },
      { number: 18, text: "Der Bevölkerung Jerusalems, die auf der Mauer stand, riefen die Abgesandten ihren Auftrag mit lauter Stimme auf Judäisch zu, um sie in Furcht und Schrecken zu versetzen und so die Stadt erobern zu können." },
      { number: 19, text: "Sie redeten vom Gott Jerusalems wie von den Göttern der anderen Völker auf der Erde, die nur ein Werk von Menschen sind." },
      { number: 20, text: "Doch König Hiskija und der Prophet Jesaja, der Sohn des Amoz, beteten in dieser Angelegenheit zum Herrn und riefen zum Himmel." },
      { number: 21, text: "Da sandte der Herr einen Engel, der alle Kriegshelden, Fürsten und Hauptleute im Lager des Königs von Assur vernichtete. Sanherib musste, mit Schande bedeckt, in sein Land zurückkehren. Als er in den Tempel seines Gottes kam, machten ihn dort seine eigenen Söhne mit dem Schwert nieder." },
      { number: 22, text: "So befreite der Herr den Hiskija und die Einwohner Jerusalems aus der Hand Sanheribs, des Königs von Assur, und aus der Hand aller Feinde. Er verlieh ihnen ringsum Ruhe.", footnotes: ["verlieh ihnen Ruhe: Text korr. nach G."] },
      { number: 23, text: "Viele brachten Gaben für den Herrn nach Jerusalem und kostbare Geschenke für Hiskija, den König von Juda. Er war seitdem hochgeehrt in den Augen aller Völker." },
      { number: 24, text: "In jenen Tagen wurde Hiskija schwer krank und war dem Tod nahe. Er betete zum Herrn und dieser erhörte ihn und gab ihm ein Wunderzeichen.", heading: "Weitere Nachrichten über Hiskija: 32,24-33", footnotes: ["(24-26) 2 Kön 20,1-11", "erhörte ihn: Text korr. nach G. 2 Kön 20,1-11 berichtet ausführlich über die Heilung und das Wunderzeichen."] },
      { number: 25, text: "Doch Hiskija zeigte keine Dankbarkeit für die empfangene Wohltat und wurde hochmütig. Da kam ein Zorngericht über ihn und über Juda und Jerusalem." },
      { number: 26, text: "Nun aber demütigte sich Hiskija mit den Einwohnern Jerusalems in seinem Stolz und so kam das Zorngericht des Herrn nicht schon in den Tagen Hiskijas über sie." },
      { number: 27, text: "Hiskija wurden Reichtum und Ehre in hohem Maß zuteil. Er erwarb Schätze an Silber, Gold, Edelsteinen, Balsam, Schilden und allerlei kostbaren Geräten." },
      { number: 28, text: "Auch hatte er Speicher für Getreide, Most und Öl, dazu Ställe für jede Art von Vieh und Hürden für die Herden.", footnotes: ["Hürden für die Herden: Text korr. nach G und Vg."] },
      { number: 29, text: "Er hatte sich nämlich Esel und einen reichen Besitz an Schafen und Rindern verschafft, denn Gott hatte ihm ein sehr großes Vermögen verliehen.", footnotes: ["Esel: Text korr.; H: Städte."] },
      { number: 30, text: "Hiskija war es auch, der den oberen Abfluss des Gihonwassers verstopfte und es nach Westen in die Davidstadt hinableitete. Bei allem, was er unternahm, hatte er Erfolg." },
      { number: 31, text: "So war es auch, als die babylonischen Obersten ihre Unterhändler zu ihm sandten, um Auskunft über das Zeichen zu erlangen, das im Land erschienen war. Da überließ ihn Gott sich selbst, um zu erproben, wie er wirklich gesinnt war.", footnotes: ["2 Kön 20,10-19"] },
      { number: 32, text: "Die übrige Geschichte Hiskijas und seine frommen Taten sind aufgezeichnet in der Vision des Propheten Jesaja, des Sohnes des Amoz, und im Buch der Könige von Juda und Israel." },
      { number: 33, text: "Hiskija entschlief zu seinen Vätern und man begrub ihn beim Aufgang zu den Gräbern der Nachkommen Davids. Bei seinem Tod erwiesen ihm ganz Juda und die Einwohner Jerusalems die Ehre. Sein Sohn Manasse wurde König an seiner Stelle." },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Manasse war zwölf Jahre alt, als er König wurde, und regierte fünfundfünfzig Jahre in Jerusalem.", heading: "Manasse: 33,1-20", footnotes: ["(1-20) 2 Kön 21,1-18"] },
      { number: 2, text: "Er tat, was dem Herrn missfiel, und ahmte die Gräuel der Völker nach, die der Herr vor den Augen der Israeliten vertrieben hatte." },
      { number: 3, text: "Er baute die Kulthöhen wieder auf, die sein Vater Hiskija zerstört hatte, errichtete Altäre für die Baale, ließ Kultpfähle anfertigen, warf sich vor dem ganzen Heer des Himmels nieder und diente ihm.", footnotes: ["31,1", "Heer des Himmels: die Sterne, die vielfach als göttliche Wesen verehrt wurden."] },
      { number: 4, text: "Auch baute er solche Altäre im Haus des Herrn, obwohl der Herr gesagt hatte: In Jerusalem soll mein Name auf ewig bleiben.", footnotes: ["1 Kön 9,3"] },
      { number: 5, text: "In den beiden Höfen des Tempels baute er Altäre für das ganze Heer des Himmels." },
      { number: 6, text: "Er ließ im Tal Ben-Hinnom seine Söhne durch das Feuer gehen, trieb Zauberei, Wahrsagerei und andere geheime Künste, bestellte Totenbeschwörer und Zeichendeuter. So tat er vieles, was dem Herrn missfiel und ihn erzürnte.", footnotes: ["Er ließ sie durch das Feuer gehen: vgl. die Anmerkung zu 2 Kön 16,3."] },
      { number: 7, text: "Er brachte auch das Götterbild, das er hatte anfertigen lassen, in das Haus Gottes, von dem Gott zu David und dessen Sohn Salomo gesagt hatte: Auf dieses Haus und auf Jerusalem, das ich aus allen Stämmen Israels auserwählt habe, will ich meinen Namen auf ewig legen.", footnotes: ["1 Kön 9,3", "Gemeint ist das Bild der Göttin Aschera, der weiblichen Entsprechung zu Baal (vgl. 2 Kön 21,7)."] },
      { number: 8, text: "Ich werde Israels Fuß nicht mehr außerhalb des Landes ziehen lassen, das ich für eure Väter bestimmt habe, wenn sie nur alles befolgen, was ich ihnen im ganzen Gesetz, in den Geboten und Rechtsvorschriften des Mose befohlen habe.", footnotes: ["2 Sam 7,10"] },
      { number: 9, text: "Doch Manasse verführte Juda und die Einwohner Jerusalems, noch Schlimmeres zu tun als die Völker, die der Herr vor den Augen der Israeliten vernichtet hatte." },
      { number: 10, text: "Der Herr redete zu Manasse und zu seinem Volk. Doch sie achteten nicht darauf." },
      { number: 11, text: "Da ließ der Herr die Heerführer des Königs von Assur gegen sie anrücken. Sie ergriffen Manasse in seinem Versteck, fesselten ihn mit bronzenen Ketten und führten ihn nach Babel.", footnotes: ["In assyrischen Texten wird Manasse als Vasall der Könige Asarhaddon und Assurbanipal erwähnt; von seiner Gefangenschaft ist nicht die Rede."] },
      { number: 12, text: "Als man ihn so bedrängte, suchte er den Herrn, seinen Gott, zu besänftigen. Er beugte sich tief vor dem Gott seiner Väter" },
      { number: 13, text: "und betete zu ihm. Gott erbarmte sich seiner; er hörte sein Flehen und ließ ihn als König nach Jerusalem zurückkehren. So musste Manasse erfahren, dass der Herr der wahre Gott ist." },
      { number: 14, text: "Danach baute er draußen an der Davidstadt eine Mauer, die im Tal westlich vom Gihon gegen das Fischtor lief, sodass er den Ofel umschloss; er machte sie sehr hoch. Auch bestellte er Kriegsoberste für alle befestigten Städte Judas.", footnotes: ["Vielleicht Fortsetzung der von Hiskija begonnenen Mauer (vgl. 32,5)."] },
      { number: 15, text: "Sodann entfernte er die fremden Götter und das Götzenbild aus dem Haus des Herrn, auch alle Altäre, die er auf dem Berg des Hauses des Herrn und in Jerusalem errichtet hatte, und warf sie vor die Stadt hinaus." },
      { number: 16, text: "Den Altar des Herrn aber stellte er wieder her, brachte auf ihm Heils- und Dankopfer dar und befahl Juda, dem Herrn, dem Gott Israels, zu dienen." },
      { number: 17, text: "Doch opferte das Volk immer noch auf den Kulthöhen, wenn auch nur dem Herrn, seinem Gott." },
      { number: 18, text: "Die übrige Geschichte Manasses, sein Gebet zu seinem Gott und die Worte der Seher, die im Namen des Herrn, des Gottes Israels, zu ihm redeten, sind aufgezeichnet in der Geschichte der Könige von Israel." },
      { number: 19, text: "Sein Gebet und dessen Erhörung, alle seine Sünden und treulosen Taten, die Orte, an denen er Kulthöhen errichtete und Kultpfähle und Götzenbilder aufstellte, bevor er sich demütigte, sind aufgezeichnet in der Geschichte seiner Seher.", footnotes: ["seiner Seher: Text korr.; der Ausdruck in H wird von manchen Auslegern als Eigenname (Hosai) verstanden."] },
      { number: 20, text: "Manasse entschlief zu seinen Vätern und man begrub ihn im Garten seines Hauses. Sein Sohn Amon wurde König an seiner Stelle.", footnotes: ["im Garten: nach G und 2 Kön 21,18 eingefügt."] },
      { number: 21, text: "Amon war zweiundzwanzig Jahre alt, als er König wurde, und regierte zwei Jahre in Jerusalem.", heading: "Amon: 33,21-25", footnotes: ["(21-25) 2 Kön 21,19-24"] },
      { number: 22, text: "Wie sein Vater Manasse tat er, was dem Herrn missfiel; er opferte allen Götzenbildern, die sein Vater Manasse hatte machen lassen, und verehrte sie." },
      { number: 23, text: "Doch demütigte er sich nicht vor dem Herrn wie sein Vater Manasse, sondern vermehrte die Schuld." },
      { number: 24, text: "Gegen ihn zettelten seine Diener eine Verschwörung an und töteten ihn in seinem Haus." },
      { number: 25, text: "Doch die Bürger des Landes erschlugen alle, die sich gegen König Amon verschworen hatten, und machten seinen Sohn Joschija zum König an seiner Stelle." },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Joschija war acht Jahre alt, als er König wurde, und regierte einunddreißig Jahre in Jerusalem.", heading: "Joschijas Kampf gegen den Götzendienst: 34,1-7", footnotes: ["(1-2) 2 Kön 22,1-2"] },
      { number: 2, text: "Er tat, was dem Herrn gefiel, und folgte den Wegen seines Vaters David, ohne nach rechts oder links abzuweichen." },
      { number: 3, text: "Im achten Jahr seiner Regierung, als er noch jung war, begann er, den Gott seines Vaters David zu suchen. Im zwölften Jahr fing er an, Juda und Jerusalem von den Kulthöhen, den Kultpfählen, den Schnitzund Gussbildern zu reinigen." },
      { number: 4, text: "Vor seinen Augen riss man die Altäre der Baale nieder. Er ließ die Rauchopferaltäre, die auf ihnen standen, zerschlagen, die Kultpfähle zerstören, die Schnitz- und Gussbilder zermalmen, ihren Staub auf die Gräber derer streuen, die ihnen geopfert hatten," },
      { number: 5, text: "und Gebeine von Priestern auf ihren Altären verbrennen. So reinigte er Juda und Jerusalem.", footnotes: ["Die Berührung mit Totengebeinen zieht die größte Verunreinigung nach sich."] },
      { number: 6, text: "Auch in den Städten von Manasse, Efraim, Simeon bis nach Naftali" },
      { number: 7, text: "riss er überall auf ihren Plätzen die Altäre nieder, zerstörte die Kultpfähle, zermalmte die Götzenbilder und zertrümmerte die Rauchopferaltäre im ganzen Land Israel. Dann kehrte er nach Jerusalem zurück." },
      { number: 8, text: "Im achtzehnten Jahr seiner Regierung, während der Säuberung des Landes und des Tempels, sandte König Joschija Schafan, den Sohn Azaljas, den Stadtobersten Maaseja und den Sprecher des Königs, Joach, den Sohn des Joahas, in das Haus des Herrn, seines Gottes, um es instand setzen zu lassen.", heading: "Die Auffindung des Gesetzbuchs: 34,8-28", footnotes: ["(8-28) 2 Kön 22,3-20", "8-28: Vgl. die Anmerkungen zu 2 Kön 22."] },
      { number: 9, text: "Sie kamen zum Hohenpriester Hilkija und man gab das Geld heraus, das in das Haus Gottes gebracht worden war und das die Leviten, die Wächter an den Schwellen, von Manasse, Efraim und dem ganzen übrigen Israel, von ganz Juda, Benjamin und den Einwohnern Jerusalems gesammelt hatten." },
      { number: 10, text: "Sie gaben es den Werkmeistern, die im Haus des Herrn angestellt waren, und diese verwendeten es für die Arbeiter, die im Haus des Herrn tätig waren, um seine Schäden auszubessern und das Haus instand zu setzen." },
      { number: 11, text: "Sie gaben es den Zimmerleuten und Bauarbeitern zum Ankauf von Bruchsteinen und Holz zur Beschaffung der Klammern und zur Erneuerung des Gebälks in den Gebäuden, die von den Königen von Juda vernachlässigt worden waren." },
      { number: 12, text: "Die Männer handelten bei ihrer Arbeit auf Treu und Glauben. Zu ihrer Leitung waren die Leviten Jahat und Obadja von den Nachkommen Meraris sowie Secharja und Meschullam von den Nachkommen Kehats bestellt. Die Leviten, die sich auf die Musikinstrumente verstanden," },
      { number: 13, text: "befehligten die Lastträger und hatten die Aufsicht über alle Arbeiter bei verschiedenen Dienstleistungen. Einzelne Leviten waren Schreiber, Listenführer und Torwächter." },
      { number: 14, text: "Als man das Geld herausnahm, das in das Haus des Herrn gebracht worden war, fand der Priester Hilkija das Buch des Gesetzes des Herrn, das durch Mose verkündet worden war." },
      { number: 15, text: "Hilkija berichtete darüber dem Staatsschreiber Schafan und sagte: Ich habe im Haus des Herrn das Gesetzbuch gefunden. Hilkija übergab Schafan das Buch" },
      { number: 16, text: "und dieser brachte es zum König mit der Meldung: Deine Knechte haben alles ausgeführt, was ihnen aufgetragen wurde." },
      { number: 17, text: "Sie haben das Geld ausgeschüttet, das sich im Haus des Herrn vorfand, und es den Aufsehern und Arbeitern gegeben." },
      { number: 18, text: "Dann sagte der Staatsschreiber zum König: Der Priester Hilkija hat mir ein Buch gegeben. Schafan las dem König daraus vor." },
      { number: 19, text: "Als der König die Worte des Gesetzes hörte, zerriss er seine Kleider" },
      { number: 20, text: "und befahl dem Priester Hilkija sowie Ahikam, dem Sohn Schafans, Abdon, dem Sohn Michas, dem Staatsschreiber Schafan und Asaja, dem Diener des Königs:", footnotes: ["Abdon: In 2 Kön 22,12 lautet der Name Achbor."] },
      { number: 21, text: "Geht und befragt den Herrn für mich und den Rest Israels und Judas wegen des Buches, das aufgefunden wurde. Denn der Zorn des Herrn, der sich über uns ergossen hat, muss groß sein, weil unsere Väter auf das Wort des Herrn nicht geachtet und weil sie nicht getan haben, was in diesem Buch niedergeschrieben ist." },
      { number: 22, text: "Da ging Hilkija mit den Männern, die der König bestimmt hatte, zur Prophetin Hulda. Sie war die Frau Schallums, des Sohnes Tokhats, des Sohnes Hasras, des Verwalters der Kleiderkammer, und wohnte in Jerusalem in der Neustadt. Die Abgesandten trugen ihr die Angelegenheit vor", footnotes: ["Statt Tokhat und Hasra stehen in 2 Kön 22,14 die Namen Tikwa und Harhas."] },
      { number: 23, text: "und sie gab ihnen diese Antwort: So spricht der Herr, der Gott Israels: Sagt zu dem Mann, der euch zu mir geschickt hat:" },
      { number: 24, text: "So spricht der Herr: Ich bringe Unheil über diesen Ort und seine Bewohner, alle Flüche, die im Buch geschrieben stehen, das man dem König von Juda vorgelesen hat." },
      { number: 25, text: "Denn sie haben mich verlassen, anderen Göttern geopfert und mich durch alle Werke ihrer Hände erzürnt. Darum hat sich mein Zorn über diesen Ort ergossen und er wird nicht erlöschen." },
      { number: 26, text: "Sagt aber zum König von Juda, der euch hergesandt hat, um den Herrn zu befragen: So spricht der Herr, der Gott Israels: Durch die Worte, die du gehört hast," },
      { number: 27, text: "wurde dein Herz erweicht. Du hast dich vor Gott gedemütigt, als du seine Drohungen gegen diesen Ort und seine Bewohner vernommen hast. Du hast dich vor mir gedemütigt, deine Kleider zerrissen und hast vor mir geweint. Darum habe ich dich erhört - Spruch des Herrn." },
      { number: 28, text: "Ich werde dich mit deinen Vätern vereinen und du sollst in Frieden in deinem Grab beigesetzt werden. Deine Augen sollen all das Unheil nicht mehr sehen, das ich über diesen Ort und seine Bewohner bringen werde. - Sie berichteten das dem König." },
      { number: 29, text: "Der König ließ alle Ältesten Judas und Jerusalems bei sich zusammenkommen.", heading: "Die Erneuerung des Bundes: 34,29-33", footnotes: ["(29-33) 2 Kön 23,1-3"] },
      { number: 30, text: "Er ging zum Haus des Herrn hinauf mit allen Männern Judas und den Einwohnern Jerusalems, den Priestern und Leviten und allem Volk, alt und jung. Er ließ ihnen alle Worte des Bundesbuches vorlesen, das im Haus des Herrn gefunden worden war." },
      { number: 31, text: "Dann trat der König an seinen Platz und schloss vor dem Herrn diesen Bund: Er wolle dem Herrn folgen, auf seine Gebote, Satzungen und Gesetze von ganzem Herzen und ganzer Seele achten und die Vorschriften des Bundes durchführen, die in diesem Buch niedergeschrieben sind." },
      { number: 32, text: "Er ließ auch alle, die sich in Jerusalem und Benjamin befanden, dem Bund beitreten und die Einwohner Jerusalems hielten sich an den Bund Gottes, des Gottes ihrer Väter." },
      { number: 33, text: "Joschija entfernte alle Gräuel aus allen Gebieten der Israeliten und veranlasste alle, die in Israel lebten, dem Herrn, ihrem Gott, zu dienen. Solange er lebte, fielen sie nicht mehr vom Herrn, dem Gott ihrer Väter, ab." },
    ] },
    { number: 35, verses: [
      { number: 1, text: "Joschija feierte in Jerusalem ein Pascha zur Ehre des Herrn. Man schlachtete das Pascha am vierzehnten Tag des ersten Monats.", heading: "Die Feier des Paschafestes: 35,1-19", footnotes: ["(1-19) 2 Kön 23,21-23"] },
      { number: 2, text: "Er ließ die Priester zu ihrem Dienst antreten und ermunterte sie zu ihrer Tätigkeit im Haus des Herrn." },
      { number: 3, text: "Zu den Leviten, die ganz Israel zu unterweisen hatten und dem Herrn heilig waren, sagte er: Die heilige Lade hat man in das Haus gebracht, das Salomo, der Sohn Davids, der König von Israel, gebaut hat. Ihr braucht sie nicht mehr auf euren Schultern zu tragen. Dient nun dem Herrn, eurem Gott, und seinem Volk Israel!", footnotes: ["hat man . . . gebracht: Text korr. nach G; H: bringt."] },
      { number: 4, text: "Haltet euch bereit nach euren Großfamilien und euren Abteilungen gemäß der Vorschrift Davids, des Königs von Israel, und der Weisung seines Sohnes Salomo." },
      { number: 5, text: "Seid den Familiengruppen eurer Brüder, den Leuten des Volkes, im Heiligtum zu Diensten; ein Teil einer levitischen Großfamilie (soll jeweils bereitstehen)." },
      { number: 6, text: "Schlachtet das Pascha und heiligt euch! Bereitet es auch für eure Brüder, damit alles geschieht, was der Herr durch Mose gesagt hat." },
      { number: 7, text: "Joschija spendete den Leuten aus dem Volk, die sich eingefunden hatten, 30000 Stück Kleinvieh, Lämmer und junge Ziegen zu Paschaopfern und 3000 Rinder. Sie stammten aus dem Besitz des Königs." },
      { number: 8, text: "Auch seine hohen Beamten gaben freiwillige Spenden für das Volk, die Priester und die Leviten. Hilkija, Secharja und Jehiël, die Vorsteher des Gotteshauses, gaben den Priestern 2600 Stück Kleinvieh zu Paschaopfern und 300 Rinder." },
      { number: 9, text: "Konanja und seine Brüder Schemaja und Netanel, ferner Haschabja, Jëiël und Josabad, die Vorsteher der Leviten, spendeten für die Leviten 5000 Tiere zu Paschaopfern und 500 Rinder." },
      { number: 10, text: "Als man so zum Dienst gerüstet war, traten die Priester an ihren Platz und die Leviten zu ihren Abteilungen gemäß dem Befehl des Königs." },
      { number: 11, text: "Die Leviten schlachteten die Paschaopfer und die Priester sprengten das Blut aus, das sie aus ihrer Hand empfingen. Darauf enthäuteten die Leviten die Tiere." },
      { number: 12, text: "Die zum Brandopfer bestimmten Stücke legten sie beiseite und gaben sie den Familiengruppen des Volkes, damit sie dem Herrn dargebracht würden, wie es im Buch des Mose vorgeschrieben war. Ebenso machten sie es mit den Rindern.", footnotes: ["Lev 3,8-11", "Der Bemerkung ist zu entnehmen, dass Teile des Paschalamms wie beim Friedopfer auf dem Altar verbrannt wurden."] },
      { number: 13, text: "Dann brieten sie das Pascha der Vorschrift gemäß am Feuer. Die heiligen Gaben kochten sie in Kesseln, Töpfen und Schüsseln und brachten sie eilends zu allen Angehörigen des Volkes.", footnotes: ["Ex 12,8"] },
      { number: 14, text: "Hierauf besorgten sie das Pascha für sich selbst und für die Priester; denn die Priester, die Nachkommen Aarons, waren mit der Darbringung der Brandopfer und der Fettstücke bis in die Nacht hinein beschäftigt. Darum bereiteten es die Leviten für sich und die Priester, die Nachkommen Aarons." },
      { number: 15, text: "Die Sänger, die Nachkommen Asafs, waren an ihrem Platz gemäß der Vorschrift Davids, Asafs, Hemans und Jedutuns, des königlichen Sehers. Ebenso standen die Torwächter an den einzelnen Toren. Sie brauchten ihren Dienst nicht zu verlassen; denn ihre Amtsbrüder, die Leviten, bereiteten für sie das Pascha." },
      { number: 16, text: "So war der gesamte Dienst für den Herrn an jenem Tag gut geordnet. Man feierte das Pascha und brachte auf dem Altar des Herrn Brandopfer dar, wie es König Joschija befohlen hatte." },
      { number: 17, text: "Sieben Tage lang begingen damals die Israeliten, die sich eingefunden hatten, das Pascha und das Fest der Ungesäuerten Brote." },
      { number: 18, text: "Ein Pascha wie dieses war seit den Tagen des Propheten Samuel in Israel nicht mehr gefeiert worden. Keiner von den Königen Israels hat ein Pascha veranstaltet, wie es Joschija mit den Priestern und Leviten, mit ganz Juda und den anwesenden Israeliten und den Einwohnern Jerusalems feierte.", footnotes: ["Im Unterschied zum Pascha Hiskijas, bei dem das erstrebte Ziel nur teilweise erreicht wurde (30,10), war diesmal das ganze Volk vertreten. Das war zum letzten Mal zur Zeit Samuels, nach 2 Kön 23,22 zur Zeit der Richter, der Fall gewesen. Jos 5,10 erwähnt die letzte gemeinsame Paschafeier am Ende der Wüstenwanderung."] },
      { number: 19, text: "Im achtzehnten Jahr der Regierung Joschijas wurde dieses Pascha begangen." },
      { number: 20, text: "Nachdem Joschija all dies zur Instandsetzung des Hauses getan hatte, zog Necho, der König von Ägypten, herauf, um bei Karkemisch am Eufrat zu kämpfen. Joschija stellte sich ihm entgegen.", heading: "Das Ende Joschijas: 35,20-27", footnotes: ["(20-24) 2 Kön 23,28-30"] },
      { number: 21, text: "Necho aber sandte Boten zu ihm und ließ ihm sagen: Was habe ich mit dir zu tun, König von Juda? Nicht gegen dich ziehe ich heute, sondern gegen das Herrscherhaus, das mit mir im Krieg steht. Gott hat mir Eile geboten; lass daher ab von Gott, der auf meiner Seite steht; sonst wird er dich verderben." },
      { number: 22, text: "Doch Joschija zog sich nicht vor Necho zurück, sondern wagte es, ihn anzugreifen. Er hörte nicht auf die Worte Nechos, die aus dem Mund Gottes kamen, sondern trat in der Ebene von Megiddo zum Kampf gegen ihn an.", footnotes: ["wagte es: Text korr. nach G."] },
      { number: 23, text: "Aber die Bogenschützen trafen den König Joschija, der nun seinen Dienern zurief: Bringt mich weg, denn ich bin schwer verwundet." },
      { number: 24, text: "Sie hoben ihn vom Kriegswagen, setzten ihn auf seinen zweiten Wagen und brachten ihn nach Jerusalem. Dort starb er und wurde in den Gräbern seiner Väter beigesetzt. Ganz Juda und Jerusalem trauerten um Joschija." },
      { number: 25, text: "Jeremia aber hielt Klage über Joschija und alle Sänger und Sängerinnen singen auf ihn Klagelieder bis zum heutigen Tag. Es wurde dies zu einem festen Brauch in Israel. Sie sind aufgezeichnet in den Klageliedern.", footnotes: ["Das biblische Buch der Klagelieder trauert über den Untergang Jerusalems, ist daher von dem hier genannten verschieden."] },
      { number: 26, text: "Die übrige Geschichte Joschijas und seine frommen Taten, die ganz dem entsprachen, was im Gesetz des Herrn vorgeschrieben ist," },
      { number: 27, text: "seine frühere und spätere Geschichte, all das ist aufgezeichnet im Buch der Könige von Israel und Juda." },
    ] },
    { number: 36, verses: [
      { number: 1, text: "Die Bürger des Landes nahmen nun Joahas, den Sohn Joschijas, und machten ihn anstelle seines Vaters zum König in Jerusalem.", heading: "Joahas: 36,1-4", footnotes: ["(1-4) 2 Kön 23,30-35"] },
      { number: 2, text: "Joahas war dreiundzwanzig Jahre alt, als er König wurde. Er regierte drei Monate in Jerusalem;" },
      { number: 3, text: "dann setzte ihn der König von Ägypten als König in Jerusalem ab und legte dem Land eine Geldbuße von hundert Talenten Silber und einem Talent Gold auf." },
      { number: 4, text: "Er machte Eljakim, den Bruder des Joahas, zum König von Juda und Jerusalem und änderte seinen Namen in Jojakim. Seinen Bruder Joahas aber nahm Necho fest und brachte ihn nach Ägypten." },
      { number: 5, text: "Jojakim war fünfundzwanzig Jahre alt, als er König wurde. Er regierte elf Jahre in Jerusalem und tat, was dem Herrn, seinem Gott, missfiel.", heading: "Jojakim: 36,5-8", footnotes: ["(5-8) 2 Kön 23,36f; 25,5f"] },
      { number: 6, text: "Gegen ihn zog Nebukadnezzar, der König von Babel, herauf und legte ihn in Ketten, um ihn nach Babel wegzuführen.", footnotes: ["Die hier erwähnte Gefangennahme wird sonst nicht berichtet."] },
      { number: 7, text: "Auch einen Teil der Geräte des Hauses des Herrn brachte Nebukadnezzar nach Babel und stellte sie in seinem Palast in Babel auf." },
      { number: 8, text: "Die übrige Geschichte Jojakims, die Gräueltaten, die er verübte, und was sich sonst über ihn findet, das alles ist aufgezeichnet im Buch der Könige von Israel und Juda. Sein Sohn Jojachin wurde König an seiner Stelle." },
      { number: 9, text: "Jojachin war achtzehn Jahre alt, als er König wurde. Er regierte drei Monate und zehn Tage in Jerusalem und tat, was dem Herrn missfiel.", heading: "Jojachin: 36,9-10", footnotes: ["(9-10) 2 Kön 24,8-17", "achtzehn: Text korr. nach 2 Kön 24,8; H: acht."] },
      { number: 10, text: "Um die Jahreswende ließ König Nebukadnezzar ihn samt den kostbaren Geräten des Hauses des Herrn nach Babel bringen. Er machte Zidkija, den Bruder Jojakims, zum König von Juda und Jerusalem.", footnotes: ["Jojachin ergab sich freiwillig den Babyloniern und wurde nach Babylon gebracht, wo er bis zu seiner Begnadigung gefangen gehalten wurde (vgl. 2 Kön 25,27). - den Bruder Jojakims: H: seinen Bruder."] },
      { number: 11, text: "Zidkija war einundzwanzig Jahre alt, als er König wurde. Er regierte elf Jahre in Jerusalem", heading: "Zidkija: 36,11-16", footnotes: ["(11-16) 2 Kön 24,18-20"] },
      { number: 12, text: "und tat, was dem Herrn, seinem Gott, missfiel; er beugte sich nicht vor dem Propheten Jeremia, der im Auftrag des Herrn zu ihm sprach." },
      { number: 13, text: "Auch fiel er vom König Nebukadnezzar ab, der ihn bei Gott einen Eid hatte schwören lassen. Er versteifte seinen Nacken, verhärtete sein Herz und kehrte nicht um zum Herrn, dem Gott Israels." },
      { number: 14, text: "Auch alle führenden Männer Judas und die Priester und das Volk begingen viel Untreue. Sie ahmten die Gräueltaten der Völker nach und entweihten das Haus, das der Herr in Jerusalem zu seinem Heiligtum gemacht hatte.", footnotes: ["führenden Männer Judas: so mit G; H: Obersten der Priester."] },
      { number: 15, text: "Immer wieder hatte der Herr, der Gott ihrer Väter, sie durch seine Boten gewarnt; denn er hatte Mitleid mit seinem Volk und seiner Wohnung." },
      { number: 16, text: "Sie aber verhöhnten die Boten Gottes, verachteten sein Wort und verspotteten seine Propheten, bis der Zorn des Herrn gegen sein Volk so groß wurde, dass es keine Heilung mehr gab." },
      { number: 17, text: "Der Herr ließ nun den König der Chaldäer gegen sie heranziehen. Dieser tötete ihre jungen Krieger in ihrem Heiligtum mit dem Schwert und verschonte keinen jungen Mann und keine junge Frau, keinen Greis und Betagten; alle gab Gott in seine Hand.", heading: "Das Ende des Reiches Juda: 36,17-21", footnotes: ["(17-21) 2 Kön 25,8-21"] },
      { number: 18, text: "Nebukadnezzar ließ die großen und kleinen Geräte des Hauses Gottes, die Tempelschätze und die Schätze des Königs und seiner hohen Beamten insgesamt nach Babel bringen." },
      { number: 19, text: "Die Chaldäer verbrannten das Haus Gottes, rissen die Mauern Jerusalems nieder, legten Feuer an alle seine Paläste und zerstörten alle wertvollen Geräte." },
      { number: 20, text: "Alle, die dem Schwert entgangen waren, führte Nebukadnezzar in die Verbannung nach Babel. Dort mussten sie ihm und seinen Söhnen als Sklaven dienen, bis das Reich der Perser zur Herrschaft kam." },
      { number: 21, text: "Da ging das Wort in Erfüllung, das der Herr durch den Mund Jeremias verkündet hatte. Das Land bekam seine Sabbate ersetzt, es lag brach während der ganzen Zeit der Verwüstung, bis siebzig Jahre voll waren.", footnotes: ["Jer 27,7", "Während der Dauer der Gefangenschaft blieben große Teile des Landes unbebaut. Auf diese Weise wurde ihm die Ruhe ersetzt, die es wegen der Nichteinhaltung des Sabbatjahrs hatte entbehren müssen (vgl. Lev 26,34f)."] },
      { number: 22, text: "Im ersten Jahr des Königs Kyrus von Persien sollte sich erfüllen, was der Herr durch Jeremia gesprochen hatte. Darum erweckte der Herr den Geist des Königs Kyrus von Persien und Kyrus ließ in seinem ganzen Reich mündlich und schriftlich den Befehl verkünden:", heading: "Der Beginn der Wiederherstellung Israels: 36,22-23", footnotes: ["(22-23) Esra 1,1-3"] },
      { number: 23, text: "So spricht Kyrus, der König von Persien: Der Herr, der Gott des Himmels, hat mir alle Reiche der Erde verliehen. Er selbst hat mir aufgetragen, ihm in Jerusalem in Juda ein Haus zu bauen. Jeder unter euch, der zu seinem Volk gehört - der Herr, sein Gott, sei mit ihm -, der soll hinaufziehen." },
    ] },
  ],
};
