import { Book } from "@/lib/types";

export const _2kings: Book = {
  id: "2kings",
  name: "2. Könige",
  shortName: "2.Kön",
  testament: "old",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Nach dem Tod Ahabs fiel Moab von Israel ab.", heading: "Die Geschichte der getrennten Reiche: Zweiter Teil: 1,1 - 17,41 | Elija und Ahasja: 1,1-18" },
      { number: 2, text: "Ahasja war in Samaria durch das Gitter seines Obergemachs gefallen und hatte sich verletzt. Er sandte Boten ab mit dem Auftrag: Geht, befragt Beelzebul, den Gott von Ekron, ob ich von diesem Leiden genesen werde.", footnotes: ["Die Bezeichnung Zebul (= Fürst) wurde Baal und anderen Göttern beigelegt; sie ist auch im Namen des Gottes von Ekron enthalten. Mt 10,25 hat die ursprüngliche Namensform Beelzebul. Baalzebub in H ist eine Entstellung des Namens (= Herr der Fliegen)."] },
      { number: 3, text: "Doch der Engel des Herrn sprach zu Elija aus Tischbe: Mach dich auf, geh den Boten des Königs von Samaria entgegen und sag zu ihnen: Gibt es denn keinen Gott in Israel, sodass ihr fortgehen müsst, um Beelzebul, den Gott von Ekron, zu befragen?" },
      { number: 4, text: "Darum: So spricht der Herr: Vom Lager, auf das du dich gelegt hast, wirst du nicht mehr aufstehen; denn du musst sterben. Elija ging weiter." },
      { number: 5, text: "Die Boten aber kehrten zum König zurück und er fragte sie: Wie kommt es, dass ihr schon zurück seid?" },
      { number: 6, text: "Sie antworteten ihm: Ein Mann kam uns entgegen und trug uns auf: Kehrt zum König zurück, der euch gesandt hat, und sagt zu ihm: So spricht der Herr: Gibt es denn keinen Gott in Israel, sodass du Boten aussenden musst, die Beelzebul, den Gott von Ekron, befragen sollen? Darum wirst du von dem Lager, auf das du dich gelegt hast, nicht mehr aufstehen; denn du musst sterben." },
      { number: 7, text: "Da fragte er sie: Wie sah der Mann aus, der euch entgegenkam und diese Worte zu euch sprach?" },
      { number: 8, text: "Sie erwiderten: Er trug einen Mantel aus Ziegenhaaren und hatte einen ledernen Gurt um die Hüften. Da sagte er: Das war Elija aus Tischbe.", footnotes: ["Der Mantel war das besondere Kleidungsstück der Propheten (1 Sam 28,14; 1 Kön 19,13.19; 2 Kön 2,8.14; Sach 13,4; Mt 3,4)."] },
      { number: 9, text: "Er sandte nun den Hauptmann über Fünfzig und seine Leute zu ihm. Dieser stieg zu Elija hinauf, der auf dem Gipfel des Berges saß, und rief ihm zu: Mann Gottes, der König befiehlt dir herabzukommen." },
      { number: 10, text: "Doch Elija antwortete dem Hauptmann der Fünfzig: Wenn ich ein Mann Gottes bin, so falle Feuer vom Himmel und verzehre dich und deine Fünfzig. Sogleich fiel Feuer vom Himmel und verzehrte ihn und seine Leute." },
      { number: 11, text: "Darauf sandte der König einen anderen Hauptmann über Fünfzig und seine Leute zu ihm. Auch dieser ergriff das Wort und rief Elija zu: Mann Gottes, so spricht der König: Komm sofort herab!" },
      { number: 12, text: "Doch Elija antwortete ihnen: Wenn ich ein Mann Gottes bin, so falle Feuer vom Himmel und verzehre dich und deine Fünfzig. Sogleich fiel das Feuer Gottes vom Himmel und verzehrte ihn und seine Leute." },
      { number: 13, text: "Nun schickte der König einen dritten Hauptmann über Fünfzig und seine Leute. Dieser kam hinauf, kniete vor Elija nieder und flehte ihn an: Mann Gottes, möchte doch mein Leben und das Leben deiner fünfzig Knechte kostbar sein in deinen Augen." },
      { number: 14, text: "Feuer ist vom Himmel gefallen und hat die ersten zwei Hauptleute und ihre fünfzig Mann verzehrt. Möchte nun mein Leben kostbar sein in deinen Augen." },
      { number: 15, text: "Da sagte der Engel des Herrn zu Elija: Geh mit ihm hinab und fürchte dich nicht vor ihm! Elija stand also auf, ging mit ihm zum König hinab" },
      { number: 16, text: "und hielt ihm vor: So spricht der Herr: Du hast Boten ausgesandt, um Beelzebul, den Gott von Ekron, zu befragen, als gäbe es in Israel keinen Gott, dessen Wort man einholen könnte. Darum wirst du von dem Lager, auf das du dich gelegt hast, nicht mehr aufstehen; denn du musst sterben." },
      { number: 17, text: "So starb Ahasja nach dem Wort des Herrn, das Elija verkündet hatte. (Sein Bruder) Joram wurde König an seiner Stelle im zweiten Jahr Jorams, des Sohnes Joschafats, des Königs von Juda; denn er hatte keinen Sohn.", footnotes: ["Sein Bruder: eingefügt wegen des Zusammenhangs, vgl. 3,1. Der Regierungswechsel wird hier in das zweite Jahr Jorams, des Nachfolgers Joschafats, in 3,1 dagegen in das 18. Jahr Joschafats datiert. Der Unterschied entstand dadurch, dass in 3,1 die Regierungsjahre Omris und seiner Nachfolger Ahab und Ahasja vom Todesjahr Simris (= 27. Jahr Asas), in 1,17 dagegen, wie in G, erst vom Beginn der Alleinherrschaft Omris (= 31. Jahr Asas, nach Ausschaltung Tibnis) an berechnet wurden."] },
      { number: 18, text: "Die übrige Geschichte Ahasjas und seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "An dem Tag, da der Herr Elija im Wirbelsturm in den Himmel aufnehmen wollte, ging Elija mit Elischa von Gilgal weg.", heading: "Die Entrückung Elijas: 2,1-18", footnotes: ["Gilgal ist nicht der in Jos 4,19 genannte Ort bei Jericho, sondern ein Ort nördlich von Bet-El."] },
      { number: 2, text: "Er sagte zu Elischa: Bleib hier; denn der Herr hat mich nach Bet-El gesandt. Doch Elischa erwiderte: So wahr der Herr lebt und so wahr du lebst: Ich verlasse dich nicht. So gingen sie nach Bet-El." },
      { number: 3, text: "Dort kamen die Prophetenjünger, die in Bet-El waren, zu Elischa heraus und sagten zu ihm: Weißt du, dass der Herr heute deinen Meister über dein Haupt hinweg aufnehmen wird? Er antwortete: Auch ich weiß es. Seid still!", footnotes: ["Prophetenjünger: die Mitglieder der Prophetengemeinschaften."] },
      { number: 4, text: "Elija aber sagte zu ihm: Bleib hier, Elischa; denn der Herr hat mich nach Jericho gesandt. Elischa erwiderte: So wahr der Herr lebt und so wahr du lebst: Ich verlasse dich nicht. So kamen sie nach Jericho." },
      { number: 5, text: "Dort traten die Prophetenjünger, die in Jericho waren, an Elischa heran und sagten zu ihm: Weißt du, dass der Herr heute deinen Meister über dein Haupt hinweg aufnehmen wird? Er antwortete ihnen: Auch ich weiß es. Seid still!" },
      { number: 6, text: "Elija aber bat ihn: Bleib hier; denn der Herr hat mich an den Jordan gesandt. Elischa erwiderte: So wahr der Herr lebt und so wahr du lebst: Ich verlasse dich nicht. So gingen beide miteinander." },
      { number: 7, text: "Fünfzig Prophetenjünger folgten ihnen und blieben dann seitwärts in einiger Entfernung stehen. Die beiden traten an den Jordan." },
      { number: 8, text: "Hier nahm Elija seinen Mantel, rollte ihn zusammen und schlug mit ihm auf das Wasser. Dieses teilte sich nach beiden Seiten und sie schritten trockenen Fußes hindurch." },
      { number: 9, text: "Als sie drüben angekommen waren, sagte Elija zu Elischa: Sprich eine Bitte aus, die ich dir erfüllen soll, bevor ich von dir weggenommen werde. Elischa antwortete: Möchten mir doch zwei Anteile deines Geistes zufallen.", footnotes: ["Durch die Verleihung von zwei Anteilen des Geistes des Elija fällt Elischa die Stellung eines Erstgeborenen zu (vgl. Dtn 21,17)."] },
      { number: 10, text: "Elija entgegnete: Du hast etwas Schweres erbeten. Wenn du siehst, wie ich von dir weggenommen werde, wird es dir zuteil werden. Sonst aber wird es nicht geschehen." },
      { number: 11, text: "Während sie miteinander gingen und redeten, erschien ein feuriger Wagen mit feurigen Pferden und trennte beide voneinander. Elija fuhr im Wirbelsturm zum Himmel empor." },
      { number: 12, text: "Elischa sah es und rief laut: Mein Vater, mein Vater! Wagen Israels und sein Lenker! Als er ihn nicht mehr sah, fasste er sein Gewand und riss es mitten entzwei.", footnotes: ["Der Ausruf drückt die Bedeutung Elijas aus. Der Kriegswagen gehörte zur wirkungsvollsten Ausrüstung des damaligen Heeres."] },
      { number: 13, text: "Dann hob er den Mantel auf, der Elija entfallen war, kehrte um und trat an das Ufer des Jordan." },
      { number: 14, text: "Er nahm den Mantel, der Elija entfallen war, schlug mit ihm auf das Wasser und rief: Wo ist der Herr, der Gott des Elija? Als er auf das Wasser schlug, teilte es sich nach beiden Seiten und Elischa ging hinüber." },
      { number: 15, text: "Die Prophetenjünger von Jericho, die in der Nähe standen, sahen ihn und sagten: Der Geist des Elija ruht auf Elischa. Sie kamen ihm entgegen und warfen sich vor ihm zur Erde nieder." },
      { number: 16, text: "Dann sagten sie zu ihm: Unter deinen Knechten sind fünfzig starke Männer. Sie sollen auf die Suche nach deinem Meister gehen. Der Geist des Herrn könnte ihn fortgetragen und auf einem der Berge oder in einem der Täler niedergesetzt haben. Doch Elischa entgegnete: Schickt niemand!" },
      { number: 17, text: "Als sie aber heftig darauf bestanden, sagte er: Schickt sie also hin! Sie schickten fünfzig Mann aus und diese suchten drei Tage lang. Da sie ihn nicht fanden," },
      { number: 18, text: "kehrten sie zu Elischa zurück. Er hielt sich noch in Jericho auf und sagte zu ihnen: Habe ich euch nicht gesagt: Ihr sollt nicht hingehen?" },
      { number: 19, text: "Die Männer der Stadt sagten zu Elischa: Unser Herr sieht, dass man in dieser Stadt gut wohnen kann; nur das Wasser ist ungesund und in der Gegend gibt es viele Fehlgeburten.", heading: "Die ersten Taten Elischas: 2,19-25" },
      { number: 20, text: "Elischa befahl: Bringt mir eine neue Schüssel und schüttet Salz hinein! Man brachte sie ihm" },
      { number: 21, text: "und er ging zur Wasserquelle und warf das Salz hinein mit den Worten: So spricht der Herr: Ich mache dieses Wasser gesund. Es wird keinen Tod und keine Fehlgeburt mehr verursachen." },
      { number: 22, text: "Daher ist das Wasser bis zum heutigen Tag gesund, wie es Elischa vorausgesagt hatte." },
      { number: 23, text: "Von dort ging er nach Bet-El. Während er den Weg hinaufstieg, kamen junge Burschen aus der Stadt und verspotteten ihn: Sie riefen ihm zu: Kahlkopf, komm herauf! Kahlkopf, komm herauf!" },
      { number: 24, text: "Er wandte sich um, sah sie an und verfluchte sie im Namen des Herrn. Da kamen zwei Bären aus dem Wald und zerrissen zweiundvierzig junge Leute." },
      { number: 25, text: "Von dort ging er zum Berg Karmel und kehrte dann nach Samaria zurück." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Im achtzehnten Jahr Joschafats, des Königs von Juda, wurde Joram, der Sohn Ahabs, in Samaria König von Israel. Er regierte zwölf Jahre", heading: "Joram von Israel: 3,1-27" },
      { number: 2, text: "und tat, was dem Herrn missfiel. Doch trieb er es nicht so schlimm wie sein Vater und seine Mutter; denn er entfernte das Steinmal des Baal, das sein Vater errichtet hatte.", footnotes: ["1 Kön 16,32"] },
      { number: 3, text: "Nur an der Sünde, zu der Jerobeam, der Sohn Nebats, Israel verführt hatte, hielt er fest und ließ nicht von ihr ab." },
      { number: 4, text: "Mescha, der König von Moab, betrieb Schafzucht und musste dem König von Israel hunderttausend Lämmer und die Wolle von hunderttausend Widdern liefern." },
      { number: 5, text: "Nach dem Tod Ahabs aber fiel der König von Moab vom König von Israel ab.", footnotes: ["Über die Erfolge Meschas berichtet ein nach ihm benannter Gedenkstein."] },
      { number: 6, text: "König Joram rückte daher zu dieser Zeit von Samaria aus und musterte ganz Israel." },
      { number: 7, text: "Auch ließ er Joschafat, dem König von Juda, sagen: Der König von Moab ist von mir abgefallen. Willst du mit mir gegen Moab in den Krieg ziehen? Joschafat antwortete: Ich ziehe mit dir, mein Volk mit deinem Volk, meine Pferde mit deinen Pferden.", footnotes: ["1 Kön 22,4"] },
      { number: 8, text: "Dann fragte Joram: Auf welchem Weg sollen wir hinaufziehen? Joschafat entschied: Durch die Wüste von Edom." },
      { number: 9, text: "Der König von Israel, der König von Juda und der König von Edom rückten nun aus. Als sie bereits einen Weg von sieben Tagen zurückgelegt hatten, fehlte es dem Heer und dem Vieh, das sie mitführten, an Wasser." },
      { number: 10, text: "Der König von Israel sagte: Weh uns, der Herr hat diese drei Könige hierher gerufen, um sie in die Gewalt der Moabiter zu geben." },
      { number: 11, text: "Joschafat aber fragte: Ist hier kein Prophet des Herrn, durch den wir den Herrn befragen könnten? Einer von den Dienern des Königs von Israel antwortete: Hier ist Elischa, der Sohn Schafats, der Elija Wasser über die Hände gegossen hat." },
      { number: 12, text: "Joschafat sagte: Bei ihm ist das Wort des Herrn. Da ging der König von Israel mit Joschafat und dem König von Edom zu ihm hinab." },
      { number: 13, text: "Doch Elischa sagte zum König von Israel: Was habe ich mit dir zu schaffen? Geh zu den Propheten deines Vaters und zu den Propheten deiner Mutter! Der König von Israel entgegnete: Darum geht es jetzt nicht. Der Herr hat diese drei Könige hierher gerufen, um sie in die Gewalt der Moabiter zu geben." },
      { number: 14, text: "Elischa antwortete: So wahr der Herr der Heere lebt, in dessen Dienst ich stehe: Wenn ich nicht auf Joschafat, den König von Juda, Rücksicht nähme, dich würde ich nicht beachten und keines Blickes würdigen." },
      { number: 15, text: "Aber nun holt mir einen Harfenspieler! Als der Spieler über die Saiten fuhr, kam die Hand des Herrn über Elischa" },
      { number: 16, text: "und er rief: So spricht der Herr: Macht in diesem Tal Grube neben Grube!" },
      { number: 17, text: "Denn so spricht der Herr: Ihr werdet weder Wind noch Regen sehen. Doch dieses Tal wird sich mit Wasser füllen und ihr werdet trinken, wie auch eure Herden und euer Vieh." },
      { number: 18, text: "Aber das ist dem Herrn noch zu wenig. Er wird die Moabiter in eure Gewalt geben." },
      { number: 19, text: "Ihr werdet alle Festungen und ihre besten Städte erobern, alle wertvollen Bäume fällen, alle Wasserquellen zuschütten und alle guten Äcker mit Steinen verderben." },
      { number: 20, text: "Und wirklich, am nächsten Morgen, zur Zeit, da man das Speiseopfer darzubringen pflegt, kam Wasser von Edom her und die ganze Gegend wurde vom Wasser überflutet.", footnotes: ["Der Hinweis auf das Speiseopfer ist Zeitangabe wie in 1 Kön 18,29."] },
      { number: 21, text: "Als ganz Moab erfuhr, dass die Könige zum Krieg gegen das Land anrückten, wurden alle aufgeboten, die Waffen tragen konnten; sie stellten sich an der Grenze auf." },
      { number: 22, text: "Frühmorgens aber, als die Sonne über dem Wasser aufging, erschien ihnen das Wasser drüben rot wie Blut.", footnotes: ["Die rote Färbung des Wassers könnte vom mitgeführten Sand oder von einer Luftspiegelung kommen, bei der das Wasser unter der aufgehenden Sonne rot erschien."] },
      { number: 23, text: "Sie sagten: Das ist Blut. Die Könige haben sich selbst umgebracht; einer hat den andern erschlagen. Auf jetzt, zur Beute, ihr Moabiter!" },
      { number: 24, text: "Doch als sie sich dem Lager der Israeliten näherten, erhoben sich diese und schlugen die Moabiter in die Flucht. Unaufhörlich drängten die Israeliten nach und machten sie nieder.", footnotes: ["drängten die Israeliten nach: Text korr. nach G."] },
      { number: 25, text: "Sie zerstörten die Städte und auf alle guten Äcker warf jeder seinen Stein, bis sie ganz bedeckt waren. Auch schütteten sie alle Wasserquellen zu und fällten alle wertvollen Bäume. Zuletzt blieb nur noch Kir-Heres übrig; diese Stadt umstellten und beschossen die Schleuderer." },
      { number: 26, text: "Als der König von Moab sah, dass er dem Angriff nicht mehr standhalten konnte, sammelte er siebenhundert mit dem Schwert bewaffnete Männer um sich und versuchte beim König von Edom durchzubrechen. Doch es gelang ihnen nicht." },
      { number: 27, text: "Nun nahm er seinen erstgeborenen Sohn, der nach ihm König werden sollte, und brachte ihn auf der Mauer als Brandopfer dar. Da kam ein gewaltiger Zorn über Israel. Sie zogen von Moab ab und kehrten in ihr Land zurück." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Eine von den Frauen der Prophetenjünger wandte sich laut rufend an Elischa: Mein Mann, dein Knecht, ist gestorben. Du weißt, dass dein Knecht gottesfürchtig war. Nun kommt der Gläubiger, um sich meine beiden Söhne als Sklaven zu nehmen.", heading: "Das Öl der Witwe: 4,1-7", footnotes: ["Zahlungsunfähige Schuldner konnten zu Sklaven gemacht werden (vgl. Am 2,6; 8,6; Mt 18,25)."] },
      { number: 2, text: "Elischa fragte sie: Was kann ich für dich tun? Sag mir: Was hast du im Haus? Sie antwortete: Deine Magd hat nichts im Haus als einen Krug Öl." },
      { number: 3, text: "Da sagte er: Geh und erbitte dir auf der Gasse von allen deinen Nachbarn leere Gefäße, aber nicht zu wenige!" },
      { number: 4, text: "Dann geh heim, verschließ die Tür hinter dir und deinen Söhnen, gieß Öl in alle diese Gefäße und stell die gefüllten beiseite!" },
      { number: 5, text: "Sie ging von ihm weg und verschloss die Tür hinter sich und ihren Söhnen. Diese reichten ihr die Gefäße hin und sie füllte ein." },
      { number: 6, text: "Als alle Gefäße voll waren, sagte sie zu ihrem Sohn: Bring mir noch ein Gefäß! Er antwortete: Es ist keines mehr da. Da floss das Öl nicht mehr weiter." },
      { number: 7, text: "Sie aber kam und erzählte es dem Gottesmann. Dieser befahl ihr: Geh, verkauf das Öl und bezahl deine Schuld! Von dem, was übrig bleibt, magst du mit deinen Söhnen leben." },
      { number: 8, text: "Eines Tages ging Elischa nach Schunem. Dort lebte eine vornehme Frau, die ihn dringend bat, bei ihr zu essen. Seither kehrte er zum Essen bei ihr ein, sooft er vorbeikam.", heading: "Die Totenerweckung: 4,8-37", footnotes: ["Schunem: nördlich von Jesreel."] },
      { number: 9, text: "Sie aber sagte zu ihrem Mann: Ich weiß, dass dieser Mann, der ständig bei uns vorbeikommt, ein heiliger Gottesmann ist." },
      { number: 10, text: "Wir wollen ein kleines, gemauertes Obergemach herrichten und dort ein Bett, einen Tisch, einen Stuhl und einen Leuchter für ihn bereitstellen. Wenn er dann zu uns kommt, kann er sich dorthin zurückziehen." },
      { number: 11, text: "Als Elischa eines Tages wieder hinkam, ging er in das Obergemach, um dort zu schlafen." },
      { number: 12, text: "Dann befahl er seinem Diener Gehasi: Ruf diese Schunemiterin! Er rief sie, und als sie vor ihm stand," },
      { number: 13, text: "befahl er dem Diener: Sag zu ihr: Du hast dir so viel Mühe um uns gemacht. Was können wir für dich tun? Sollen wir beim König oder beim Obersten des Heeres ein Wort für dich einlegen? Doch sie entgegnete: Ich wohne inmitten meiner Verwandten.", footnotes: ["Die Frau hält eine Vermittlung beim König für unnötig und bezeugt damit die starke Stellung des Familienverbands im israelitischen Staat."] },
      { number: 14, text: "Und als er weiter fragte, was man für sie tun könne, sagte Gehasi: Nun, sie hat keinen Sohn und ihr Mann ist alt." },
      { number: 15, text: "Da befahl er: Ruf sie herein! Er rief sie und sie blieb in der Tür stehen." },
      { number: 16, text: "Darauf versicherte ihr Elischa: Im nächsten Jahr um diese Zeit wirst du einen Sohn liebkosen. Sie aber entgegnete: Ach nein, Herr, Mann Gottes, täusche doch deiner Magd nichts vor!" },
      { number: 17, text: "Doch die Frau wurde schwanger und im nächsten Jahr, um die Zeit, die Elischa genannt hatte, gebar sie einen Sohn." },
      { number: 18, text: "Als das Kind herangewachsen war, ging es eines Tages zu seinem Vater hinaus zu den Schnittern.", footnotes: ["(18-37) 1 Kön 17,17-24"] },
      { number: 19, text: "Dort klagte es ihm: Mein Kopf, mein Kopf! Der Vater befahl seinem Knecht: Trag das Kind heim zu seiner Mutter!" },
      { number: 20, text: "Der Knecht nahm es und brachte es zu ihr. Es saß noch bis zum Mittag auf ihren Knien; dann starb es." },
      { number: 21, text: "Sie stieg nun in das obere Gemach hinauf, legte das Kind auf das Bett des Gottesmannes und schloss die Tür hinter ihm ab. Dann verließ sie das Haus," },
      { number: 22, text: "rief ihren Mann und bat ihn: Schick mir einen von den Knechten und einen Esel! Ich will zum Gottesmann eilen und komme bald zurück." },
      { number: 23, text: "Er wandte ein: Warum gehst du heute zu ihm? Es ist doch nicht Neumond und nicht Sabbat. Doch sie sagte nur: Friede mit dir!,", footnotes: ["Aus der Frage geht hervor, dass man an Neumondtagen und Sabbaten Propheten aufsuchte."] },
      { number: 24, text: "sattelte den Esel und befahl dem Knecht: Treib tüchtig an und halte mich beim Reiten nicht auf, es sei denn, dass ich es dir sage." },
      { number: 25, text: "So reiste sie ab und kam zum Gottesmann auf den Karmel. Als er sie von ferne sah, sagte er zu seinem Diener Gehasi: Da kommt die Schunemiterin." },
      { number: 26, text: "Lauf ihr entgegen und frag sie: Geht es dir gut? Geht es auch deinem Mann und dem Kind gut? Sie antwortete: Es geht gut." },
      { number: 27, text: "Sobald sie aber zum Gottesmann auf den Berg kam, umfasste sie seine Füße. Gehasi trat hinzu, um sie wegzudrängen; aber der Gottesmann wehrte ab: Lass sie; denn ihre Seele ist betrübt. Doch der Herr hat mir den Grund verborgen und mir nicht mitgeteilt." },
      { number: 28, text: "Darauf sagte sie: Habe ich denn meinen Herrn um einen Sohn gebeten? Habe ich nicht gesagt: Mach mir keine falschen Hoffnungen?" },
      { number: 29, text: "Elischa befahl nun Gehasi: Gürte dich, nimm meinen Stab in die Hand und mach dich auf den Weg! Wenn du jemand begegnest, so grüß ihn nicht; und wenn dich jemand grüßt, so antworte ihm nicht! Leg meinen Stab auf das Gesicht des Kindes!" },
      { number: 30, text: "Aber die Mutter des Kindes sagte: So wahr der Herr lebt und so wahr du lebst: Ich lasse nicht von dir ab. Da stand er auf und folgte ihr." },
      { number: 31, text: "Gehasi war vorausgeeilt und hatte den Stab auf das Gesicht des Kindes gelegt; doch es kam kein Laut und kein Lebenszeichen. Daher lief er zum Gottesmann zurück und berichtete: Das Kind ist nicht aufgewacht." },
      { number: 32, text: "Als Elischa in das Haus kam, lag das Kind tot auf seinem Bett." },
      { number: 33, text: "Er ging in das Gemach, schloss die Tür hinter sich und dem Kind und betete zum Herrn." },
      { number: 34, text: "Dann trat er an das Bett und warf sich über das Kind; er legte seinen Mund auf dessen Mund, seine Augen auf dessen Augen, seine Hände auf dessen Hände. Als er sich so über das Kind hinstreckte, kam Wärme in dessen Leib.", footnotes: ["1 Kön 17,21; Apg 20,10"] },
      { number: 35, text: "Dann stand er auf, ging im Haus einmal hin und her, trat wieder an das Bett und warf sich über das Kind. Da nieste es siebenmal und öffnete die Augen." },
      { number: 36, text: "Nun rief Elischa seinen Diener Gehasi und befahl ihm, die Schunemiterin zu rufen. Er rief sie, und als sie kam, sagte der Gottesmann zu ihr: Nimm deinen Sohn!" },
      { number: 37, text: "Sie trat hinzu, fiel Elischa zu Füßen und verneigte sich bis zur Erde. Dann nahm sie ihren Sohn und ging hinaus." },
      { number: 38, text: "Elischa kehrte nach Gilgal zurück. Im Land herrschte damals eine Hungersnot. Als die Prophetenjünger vor ihm saßen, befahl er seinem Diener: Setz den großen Topf auf und koch ein Gericht für die Prophetenjünger!", heading: "Die ungenießbare Speise: 4,38-41" },
      { number: 39, text: "Einer von ihnen ging auf das Feld hinaus, um Malven zu holen. Dabei fand er ein wildes Rankengewächs und pflückte davon so viele Früchte, wie sein Gewand fassen konnte. Dann kam er zurück und schnitt sie in den Kochtopf hinein, da man sie nicht kannte." },
      { number: 40, text: "Als man sie aber den Männern zum Essen vorsetzte und sie von der Speise kosteten, schrien sie laut und riefen: Der Tod ist im Topf, Mann Gottes. Sie konnten nichts essen." },
      { number: 41, text: "Doch er befahl: Bringt mir etwas Mehl! Er streute das Mehl in den Topf und sagte: Setzt es nun den Leuten zum Essen vor! Jetzt war nichts Schädliches mehr im Topf." },
      { number: 42, text: "Einmal kam ein Mann von Baal-Schalischa und brachte dem Gottesmann Brot von Erstlingsfrüchten, zwanzig Gerstenbrote, und frische Körner in einem Beutel. Elischa befahl seinem Diener: Gib es den Leuten zu essen!", heading: "Die Brotvermehrung: 4,42-44" },
      { number: 43, text: "Doch dieser sagte: Wie soll ich das hundert Männern vorsetzen? Elischa aber sagte: Gib es den Leuten zu essen! Denn so spricht der Herr: Man wird essen und noch übrig lassen." },
      { number: 44, text: "Nun setzte er es ihnen vor; und sie aßen und ließen noch übrig, wie der Herr gesagt hatte." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Naaman, der Feldherr des Königs von Aram, galt viel bei seinem Herrn und war angesehen; denn durch ihn hatte der Herr den Aramäern den Sieg verliehen. Der Mann war tapfer, aber an Aussatz erkrankt.", heading: "Die Heilung des Aramäers Naaman: 5,1-27" },
      { number: 2, text: "Nun hatten die Aramäer bei einem Streifzug ein junges Mädchen aus dem Land Israel verschleppt. Es war in den Dienst der Frau Naamans gekommen." },
      { number: 3, text: "Es sagte zu seiner Herrin: Wäre mein Herr doch bei dem Propheten in Samaria! Er würde seinen Aussatz heilen." },
      { number: 4, text: "Naaman ging zu seinem Herrn und meldete ihm: Das und das hat das Mädchen aus Israel gesagt." },
      { number: 5, text: "Der König von Aram antwortete: So geh doch hin; ich werde dir ein Schreiben an den König von Israel mitgeben. Naaman machte sich auf den Weg. Er nahm zehn Talente Silber, sechstausend Schekel Gold und zehn Festkleider mit" },
      { number: 6, text: "und überbrachte dem König von Israel das Schreiben. Es hatte folgenden Inhalt: Wenn jetzt dieser Brief zu dir gelangt, so wisse: Ich habe meinen Knecht Naaman zu dir geschickt, damit du seinen Aussatz heilst.", footnotes: ["In Damaskus zählte man Elischa zu den Hofpropheten; daher richtete der König das Schreiben an den König von Israel, der einen Propheten mit der Heilung beauftragen kann."] },
      { number: 7, text: "Als der König von Israel den Brief gelesen hatte, zerriss er seine Kleider und rief: Bin ich denn ein Gott, der töten und zum Leben erwecken kann? Er schickt einen Mann zu mir, damit ich ihn von seinem Aussatz heile. Merkt doch und seht, dass er nur Streit mit mir sucht." },
      { number: 8, text: "Als der Gottesmann Elischa hörte, der König von Israel habe seine Kleider zerrissen, ließ er ihm sagen: Warum hast du deine Kleider zerrissen? Naaman soll zu mir kommen; dann wird er erfahren, dass es in Israel einen Propheten gibt." },
      { number: 9, text: "So kam Naaman mit seinen Pferden und Wagen und hielt vor dem Haus Elischas." },
      { number: 10, text: "Dieser schickte einen Boten zu ihm hinaus und ließ ihm sagen: Geh und wasch dich siebenmal im Jordan! Dann wird dein Leib wieder gesund, und du wirst rein." },
      { number: 11, text: "Doch Naaman wurde zornig. Er ging weg und sagte: Ich dachte, er würde herauskommen, vor mich hintreten, den Namen Jahwes, seines Gottes, anrufen, seine Hand über die kranke Stelle bewegen und so den Aussatz heilen." },
      { number: 12, text: "Sind nicht der Abana und der Parpar, die Flüsse von Damaskus, besser als alle Gewässer Israels? Kann ich nicht dort mich waschen, um rein zu werden? Voll Zorn wandte er sich ab und ging weg." },
      { number: 13, text: "Doch seine Diener traten an ihn heran und redeten ihm zu: Wenn der Prophet etwas Schweres von dir verlangt hätte, würdest du es tun; wie viel mehr jetzt, da er zu dir nur gesagt hat: Wasch dich und du wirst rein.", footnotes: ["Wenn: Text korr."] },
      { number: 14, text: "So ging er also zum Jordan hinab und tauchte siebenmal unter, wie ihm der Gottesmann befohlen hatte. Da wurde sein Leib gesund wie der Leib eines Kindes und er war rein." },
      { number: 15, text: "Nun kehrte er mit seinem ganzen Gefolge zum Gottesmann zurück, trat vor ihn hin und sagte: Jetzt weiß ich, dass es nirgends auf der Erde einen Gott gibt außer in Israel. So nimm jetzt von deinem Knecht ein Dankgeschenk an!" },
      { number: 16, text: "Elischa antwortete: So wahr der Herr lebt, in dessen Dienst ich stehe: Ich nehme nichts an. Auch als Naaman ihn dringend bat, es zu nehmen, lehnte er ab." },
      { number: 17, text: "Darauf sagte Naaman: Wenn es also nicht sein kann, dann gebe man deinem Knecht so viel Erde, wie zwei Maultiere tragen können; denn dein Knecht wird keinem andern Gott mehr Brand- und Schlachtopfer darbringen als Jahwe allein.", footnotes: ["Der besonderen Beziehung Jahwes zum Land Israel wegen will er Opfer auf israelitischer Erde darbringen."] },
      { number: 18, text: "Nur dies möge Jahwe deinem Knecht verzeihen: Wenn mein Herr zur Anbetung in den Tempel Rimmons geht, stützt er sich dort auf meinen Arm. Ich muss mich dann im Tempel Rimmons niederwerfen, wenn er sich dort niederwirft. Dann möge das Jahwe deinem Knecht verzeihen.", footnotes: ["wenn er sich dort niederwirft: Text korr. nach G; H: wenn ich mich dort niederwerfe."] },
      { number: 19, text: "Elischa antwortete: Geh in Frieden! Als Naaman schon eine Strecke Weges von ihm entfernt war," },
      { number: 20, text: "sagte sich Gehasi, der Diener Elischas, des Gottesmannes: Mein Herr hat diesen Aramäer Naaman geschont und nichts von dem angenommen, was er mitgebracht hatte. So wahr der Herr lebt: Ich werde ihm nachlaufen und mir etwas von ihm holen." },
      { number: 21, text: "Gehasi eilte ihm also nach. Als ihn Naaman hinter sich herankommen sah, beugte er sich ihm vom Wagen aus zu und fragte: Steht alles gut?" },
      { number: 22, text: "Er antwortete: Ja; nur lässt mein Herr sagen: Soeben sind vom Gebirge Efraim zwei junge Männer, zwei Prophetenjünger, zu mir gekommen. Gib ihnen doch ein Talent Silber und zwei Festkleider!" },
      { number: 23, text: "Naaman erwiderte: Tu mir den Gefallen und nimm zwei Talente! Er bat ihn dringend darum und tat zwei Talente Silber in zwei Beutel, legte zwei Festkleider dazu und ließ sie durch zwei Diener vor ihm hertragen." },
      { number: 24, text: "Als Gehasi auf der Höhe angekommen war, nahm er ihnen die Geschenke ab und brachte sie in das Haus. Die Männer schickte er weg und sie kehrten zurück." },
      { number: 25, text: "Er selbst ging hinein und trat vor seinen Herrn. Elischa fragte ihn: Woher kommst du, Gehasi? Er antwortete: Dein Knecht ist nirgendwohin gegangen." },
      { number: 26, text: "Da sagte Elischa zu ihm: War nicht mein Geist zugegen, als sich jemand von seinem Wagen aus dir zuwandte? Ist es denn Zeit, Geld anzunehmen und Kleider, Ölgärten, Weinberge, Schafe und Rinder, Knechte und Mägde zu erwerben?" },
      { number: 27, text: "Der Aussatz Naamans aber soll für immer an dir und deinen Nachkommen haften. Gehasi ging hinaus und war vom Aussatz weiß wie Schnee." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Die Prophetenjünger klagten bei Elischa: Der Raum, in dem wir vor dir sitzen, ist zu eng für uns.", heading: "Das verlorene Beil: 6,1-7" },
      { number: 2, text: "Wir wollen an den Jordan gehen und dort soll jeder von uns einen Balken holen, damit wir uns hier einen Raum errichten, in dem wir wohnen können. Er antwortete: Geht!" },
      { number: 3, text: "Einer aber bat ihn: Erweis uns den Gefallen und geh mit deinen Knechten! Er erwiderte: Ich gehe mit." },
      { number: 4, text: "Er ging also mit ihnen und sie kamen an den Jordan und fällten Bäume." },
      { number: 5, text: "Einem aber fiel beim Fällen seines Stammes die eiserne Klinge des Beils in das Wasser. Er rief laut: Wehe, mein Herr, das Beil ist auch noch geliehen!" },
      { number: 6, text: "Der Gottesmann fragte: Wohin ist es gefallen? Er zeigte ihm die Stelle; Elischa schnitt ein Stück Holz ab, warf es dorthin und brachte das Eisen zum Schwimmen." },
      { number: 7, text: "Dann befahl er: Hol es herauf! Der Mann streckte die Hand aus und ergriff es." },
      { number: 8, text: "Der König von Aram führte Krieg mit Israel. Er verabredete mit seinen Untergebenen, an einer bestimmten Stelle einen Hinterhalt zu legen.", heading: "Die Gefangenen des Propheten: 6,8-23", footnotes: ["8f: Hinterhalt: Text korr. nach G (V. 9)."] },
      { number: 9, text: "Aber der Gottesmann ließ dem König von Israel sagen: Hüte dich, an jener Stelle vorbeizugehen; denn die Aramäer liegen dort im Hinterhalt." },
      { number: 10, text: "Der König von Israel schickte nun Späher an die Stelle, die ihm der Gottesmann genannt und vor der er ihn gewarnt hatte, und nahm sich dort in Acht. Als das nicht nur einmal oder zweimal geschah," },
      { number: 11, text: "wurde der König von Aram beunruhigt. Er rief seine Untergebenen und fragte sie: Könnt ihr mir nicht angeben, wer von den Unsrigen zum König von Israel hält?" },
      { number: 12, text: "Da sagte einer von seinen Leuten: Niemand, mein Herr und König, sondern Elischa, der Prophet in Israel, verrät dem König von Israel, was du in deinem Schlafzimmer sprichst." },
      { number: 13, text: "Da befahl er: Geht und erkundet, wo er sich aufhält, damit ich ihn festnehmen lasse. Man meldete ihm: Er ist in Dotan.", footnotes: ["Dotan: nördlich von Samaria."] },
      { number: 14, text: "Er schickte also Pferde und Wagen und eine starke Truppe dorthin. Sie erreichten die Stadt in der Nacht und umstellten sie." },
      { number: 15, text: "Als der Diener des Gottesmannes am nächsten Morgen aufstand und hinaustrat, hatte die Truppe die Stadt mit Pferden und Wagen umstellt. Da sagte der Diener zu seinem Herrn: Wehe, mein Herr, was sollen wir tun?" },
      { number: 16, text: "Doch dieser sagte: Fürchte dich nicht! Bei uns sind mehr als bei ihnen." },
      { number: 17, text: "Dann betete Elischa: Herr, öffne ihm die Augen, damit er sieht. Und der Herr öffnete dem Diener die Augen: Er sah den Berg rings um Elischa voll von feurigen Pferden und Wagen." },
      { number: 18, text: "Als dann die Aramäer anrückten, betete Elischa zum Herrn und rief: Schlag doch diese Leute mit Verblendung! Und der Herr schlug sie auf das Wort Elischas hin mit Verblendung." },
      { number: 19, text: "Daraufhin sagte Elischa zu ihnen: Das ist nicht der richtige Weg und nicht die richtige Stadt. Folgt mir! Ich werde euch zu dem Mann führen, den ihr sucht. Er führte sie aber nach Samaria." },
      { number: 20, text: "Als sie dort angekommen waren, betete Elischa: Herr, öffne ihnen die Augen, damit sie sehen. Der Herr öffnete ihnen die Augen und sie sahen, dass sie mitten in Samaria waren." },
      { number: 21, text: "Sobald nun der König von Israel sie erblickte, rief er Elischa zu: Soll ich sie totschlagen, mein Vater?" },
      { number: 22, text: "Doch dieser erwiderte: Töte sie nicht! Erschlägst du denn jene, die du mit deinem Schwert und Bogen gefangen nimmst? Setz ihnen Brot und Wasser vor, damit sie essen und trinken und dann zu ihrem Herrn zurückkehren." },
      { number: 23, text: "Der König gab ihnen reichlich zu essen und zu trinken und entließ sie zu ihrem Herrn. Seitdem kamen keine aramäischen Streifscharen mehr in das Land Israel." },
      { number: 24, text: "Danach sammelte Ben-Hadad, der König von Aram, seine ganze Streitmacht, zog gegen Samaria und belagerte es.", heading: "Die Rettung aus Hungersnot: 6,24 - 7,20", footnotes: ["Ben-Hadad III., der Nachfolger Hasaëls. In Israel regiert Joasch (vgl. 13,24f)."] },
      { number: 25, text: "In der eingeschlossenen Stadt entstand eine große Hungersnot, sodass der Kopf eines Esels achtzig Silberschekel kostete und ein Viertelkab Milchsterne fünf Silberschekel.", footnotes: ["Milchstern: ein Zwiebelgewächs, das man nur in Notzeiten aß; andere Übersetzungsmöglichkeit: Taubenmist."] },
      { number: 26, text: "Als der König von Israel einmal auf der Mauer vorbeischritt, rief ihm eine Frau zu: Hilf mir, mein Herr und König!" },
      { number: 27, text: "Er antwortete: Wenn dir der Herr nicht hilft, wie soll ich dir helfen? Soll es etwas von der Tenne oder von der Kelter sein?" },
      { number: 28, text: "Dann fragte er sie: Was fehlt dir? Sie sagte: Diese Frau hat von mir verlangt: Gib deinen Sohn her, damit wir ihn heute aufessen. Meinen Sohn werden wir dann morgen verzehren." },
      { number: 29, text: "So haben wir meinen Sohn gekocht und aufgegessen. Als ich aber am nächsten Tag zu ihr sagte: Gib jetzt deinen Sohn her, damit wir ihn verzehren, hatte sie ihren Sohn versteckt." },
      { number: 30, text: "Als der König die Worte der Frau hörte, zerriss er seine Kleider, und da er auf der Mauer entlangging, sah das Volk, dass er ein Bußgewand auf dem bloßen Leib trug." },
      { number: 31, text: "Er rief: Gott soll mir dies und das antun, wenn Elischa, der Sohn Schafats, bis heute Abend seinen Kopf behält." },
      { number: 32, text: "Elischa hielt sich in seinem Haus auf und die Ältesten saßen bei ihm. Der König hatte einen Mann vor sich her gesandt; aber noch vor der Ankunft des Boten sagte Elischa zu den Ältesten: Merkt ihr, dass dieser Sohn eines Mörders (seinen Henker) schickt, der mir den Kopf abschlagen soll? Gebt nun Acht! Wenn der Bote kommt, verschließt die Tür und haltet ihn an der Tür zurück! Sind nicht schon die Schritte seines Herrn hinter ihm zu hören?" },
      { number: 33, text: "Noch während er mit ihnen redete, kam der König und sagte: Dieses Elend kommt vom Herrn. Was soll ich noch vom Herrn erwarten?", footnotes: ["kam der König: H: kam der Bote. - Der König konnte Elischa für die Not verantwortlich machen, wenn sie von ihm angekündigt worden war (vgl. die Anmerkung zu 1 Kön 18,10) oder wenn Elischa gegen den Willen des Königs zum Durchhalten geraten hatte."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Doch Elischa entgegnete: Hört das Wort des Herrn! So spricht der Herr: Morgen um diese Zeit kostet am Tor von Samaria ein Sea Feinmehl nur noch einen Schekel und auch zwei Sea Gerste kosten nur noch einen Schekel.", footnotes: ["Man wird Lebensmittel wieder zu normalen Preisen erhalten, sie werden also wieder in genügender Menge vorhanden sein."] },
      { number: 2, text: "Doch der Adjutant, auf dessen Arm sich der König stützte, antwortete dem Gottesmann: Selbst wenn der Herr Schleusen am Himmel anbrächte, könnte das nicht geschehen. Elischa erwiderte: Du wirst es mit deinen eigenen Augen sehen, aber nicht davon essen." },
      { number: 3, text: "Vor dem Eingang des Stadttors saßen vier aussätzige Männer. Sie sagten zueinander: Warum sitzen wir hier, bis wir sterben?" },
      { number: 4, text: "Wollten wir in die Stadt gehen, in der Hungersnot herrscht, dann sterben wir in ihr. Bleiben wir draußen, dann sterben wir auch. Kommt, wir gehen in das Lager der Aramäer hinüber! Wenn sie uns am Leben lassen, bleiben wir am Leben. Wenn sie uns töten, so sterben wir." },
      { number: 5, text: "Sie machten sich daher in der Abenddämmerung auf, um in das Lager der Aramäer zu gehen. Doch als sie in den Bereich des aramäischen Lagers kamen, war dort niemand zu sehen." },
      { number: 6, text: "Der Herr hatte nämlich das Rollen von Wagen, das Getrampel von Pferden und das Lärmen eines großen Heeres im Lager vernehmen lassen, sodass einer zum andern sagte: Der König von Israel hat die Könige der Hetiter und die Könige von Ägypten gegen uns angeworben, um uns überfallen zu lassen." },
      { number: 7, text: "Sie waren daher in der Dämmerung aufgebrochen und geflohen. Dabei hatten sie ihre Zelte, Pferde und Esel und das ganze Lager, so wie es war, zurückgelassen, um durch die Flucht ihr Leben zu retten." },
      { number: 8, text: "Als nun die Aussätzigen in den Bereich des Lagers kamen, gingen sie in ein Zelt, aßen und tranken, nahmen Silber, Gold und Kleider und entfernten sich, um die Beute zu verstecken. Dann kamen sie zurück, gingen in ein anderes Zelt, machten auch hier ihre Beute und entfernten sich wieder, um sie zu verstecken." },
      { number: 9, text: "Dann aber sagten sie zueinander: Wir handeln nicht recht. Heute ist ein Tag froher Botschaft. Wenn wir schweigen und bis zum Morgengrauen warten, trifft uns Schuld. Kommt also; wir gehen und melden es im Palast des Königs." },
      { number: 10, text: "Sie machten sich auf, riefen die Wächter der Stadt und erzählten ihnen: Wir sind in das Lager der Aramäer gekommen. Aber dort war niemand zu sehen und kein menschlicher Laut zu hören. Die Pferde und Esel waren angebunden und die Zelte standen so da, wie sie waren." },
      { number: 11, text: "Da schlugen die Wächter Lärm und man meldete es drinnen im Palast des Königs.", footnotes: ["schlugen Lärm: Text korr. nach G."] },
      { number: 12, text: "Noch in der Nacht stand der König auf und sagte zu seinen Leuten: Ich will euch erklären, was die Aramäer gegen uns planen. Sie wissen, dass wir Hunger leiden, und haben das Lager nur verlassen, um sich auf dem freien Feld zu verstecken mit dem Hintergedanken: Wenn sie die Stadt verlassen, nehmen wir sie lebendig gefangen und dringen in die Stadt ein." },
      { number: 13, text: "Doch einer von den Leuten schlug vor: Man nehme doch fünf von den noch vorhandenen Pferden. Ihnen mag es ergehen wie den vielen Israeliten, die schon zugrunde gegangen sind. Wir wollen Männer mit den Pferden hinschicken und dann weiter sehen.", footnotes: ["H ist durch Doppelschreibung einer Zeile entstellt."] },
      { number: 14, text: "Man nahm also zwei Wagen mit den Pferden. Der König schickte sie der Streitmacht der Aramäer nach und befahl ihnen: Brecht auf und seht nach!" },
      { number: 15, text: "Sie fuhren hinter ihnen her bis an den Jordan und fanden den ganzen Weg mit Kleidern und Waffen übersät, die die Aramäer auf ihrer überstürzten Flucht weggeworfen hatten. Als sie zurückkamen und dem König Meldung erstatteten," },
      { number: 16, text: "strömte das Volk hinaus und plünderte das Lager der Aramäer. Jetzt kostete ein Sea Feinmehl nur noch einen Schekel und auch zwei Sea Gerste kosteten nur noch einen Schekel, wie es der Herr vorausgesagt hatte." },
      { number: 17, text: "Der König hatte die Aufsicht über das Tor dem Adjutanten übertragen, auf dessen Arm er sich zu stützen pflegte. Ihn trat das Volk im Stadttor nieder und so starb er, wie es ihm der Gottesmann vorausgesagt hatte, als der König gekommen war, um mit ihm zu reden." },
      { number: 18, text: "Als nämlich der Gottesmann zum König sagte: Morgen um diese Zeit kosten am Tor von Samaria zwei Sea Gerste nur noch einen Schekel und ein Sea Feinmehl kostet nur noch einen Schekel," },
      { number: 19, text: "hatte der Adjutant dem Gottesmann geantwortet: Selbst wenn der Herr Schleusen am Himmel anbrächte, könnte dies nicht geschehen. Und Elischa hatte ihm erwidert: Du wirst es mit deinen Augen sehen, aber nicht davon essen." },
      { number: 20, text: "So geschah es ihm nun: Das Volk trat ihn im Tor nieder, sodass er starb." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Elischa sagte zu der Frau, deren Sohn er zum Leben erweckt hatte: Mach dich auf, zieh mit deiner Familie fort und halte dich irgendwo in der Fremde auf; denn der Herr hat eine Hungersnot verhängt. Schon kommt sie über das Land und sie wird sieben Jahre dauern.", heading: "Die Sorge für die Frau aus Schunem: 8,1-6", footnotes: ["4,32-37"] },
      { number: 2, text: "Da machte sich die Frau auf den Weg und tat, was ihr der Gottesmann geraten hatte. Sie zog mit ihren Angehörigen fort und hielt sich sieben Jahre im Land der Philister auf." },
      { number: 3, text: "Nach Ablauf von sieben Jahren kehrte sie aus dem Land der Philister zurück und ging zum König, um wegen ihres Hauses und ihrer Felder seine Hilfe zu erbitten." },
      { number: 4, text: "Der König war gerade im Gespräch mit Gehasi, dem Diener des Gottesmannes, und hatte ihn aufgefordert: Erzähl mir alles Große, das Elischa vollbracht hat." },
      { number: 5, text: "Während dieser dem König erzählte, wie Elischa den Toten zum Leben erweckt hatte, kam die Frau, deren Sohn er zum Leben erweckt hatte, um wegen ihres Hauses und ihrer Felder die Hilfe des Königs zu erbitten. Da sagte Gehasi: Das, mein Herr und König, ist die Frau und das ist ihr Sohn, den Elischa zum Leben erweckt hat." },
      { number: 6, text: "Nun fragte der König die Frau selbst und sie erzählte ihm alles. Darauf gab ihr der König einen Beamten mit und trug ihm auf: Verschaff ihr alles wieder, was ihr gehört, auch den ganzen Ertrag ihrer Felder von dem Tag an, da sie das Land verlassen hat, bis heute." },
      { number: 7, text: "Elischa kam nach Damaskus, wo Ben-Hadad, der König von Damaskus, krank daniederlag. Man meldete ihm, dass der Gottesmann gekommen sei.", heading: "Elischa in Damaskus: 8,7-15", footnotes: ["Ben-Hadad II., der Vorgänger Hasaëls."] },
      { number: 8, text: "Darauf befahl der König dem Hasaël: Nimm ein Geschenk, geh dem Gottesmann entgegen und befrag durch ihn Jahwe, ob ich von dieser Krankheit wieder genesen werde." },
      { number: 9, text: "Hasaël ging Elischa entgegen; als Geschenk nahm er allerlei Kostbarkeiten von Damaskus mit, so viel, wie vierzig Kamele tragen konnten. Er kam zu Elischa, trat vor ihn hin und sagte: Dein Sohn Ben-Hadad, der König von Aram, hat mich zu dir gesandt und lässt fragen: Werde ich von dieser Krankheit wieder genesen?" },
      { number: 10, text: "Elischa antwortete ihm: Geh und sag ihm: Du wirst genesen. - Doch der Herr hat mir gezeigt, dass er sterben muss.", footnotes: ["Der Bescheid des Propheten besteht aus zwei scheinbar sich widersprechenden Sätzen. Der erste wird von manchen so verstanden: Du könntest genesen, aber der Tod wird durch andere Ursachen herbeigeführt werden. Hasaël täuscht den kranken König, indem er ihm den zweiten Teil des Bescheids vorenthält und dadurch auch den Sinn des ersten Teils verfälscht."] },
      { number: 11, text: "Hasaël verzog keine Miene und blickte ihn scharf an. Der Gottesmann aber weinte." },
      { number: 12, text: "Als Hasaël dann fragte: Warum weint mein Herr?, gab er zur Antwort: Weil ich weiß, wie viel Leid du den Israeliten bringen wirst. Du wirst ihre Festungen in Brand stecken, ihre jungen Männer mit dem Schwert töten, ihre Kinder zerschmettern, ihren schwangeren Frauen den Leib aufschlitzen.", footnotes: ["10,32f; Am 1,3"] },
      { number: 13, text: "Hasaël entgegnete: Was ist denn dein Knecht, dieser Hund, dass er so gewaltige Dinge tun könnte? Elischa antwortete: Der Herr hat dich mir als König von Aram gezeigt." },
      { number: 14, text: "Hasaël verließ Elischa und kehrte zu seinem Herrn zurück. Dieser fragte ihn: Was hat Elischa zu dir gesagt? Und er gab zur Antwort: Er hat zu mir gesagt, dass du genesen wirst." },
      { number: 15, text: "Am folgenden Tag aber nahm er eine Decke, tauchte sie ins Wasser und legte sie ihm über das Gesicht, sodass er starb. Hasaël wurde König an seiner Stelle.", footnotes: ["Das Subjekt des Satzes wird nicht ausdrücklich genannt. Wahrscheinlich wurde der König von Hasaël ermordet."] },
      { number: 16, text: "Im fünften Jahr Jorams, des Sohnes Ahabs, des Königs von Israel, während Joschafat noch König von Juda war, wurde Joram, der Sohn Joschafats, König von Juda.", heading: "Joram von Juda: 8,16-24", footnotes: ["(16-24) 2 Chr 21,5-10", "Das fünfte Jahr Jorams von Israel war erst das 22./23. Jahr Joschafats. Sein Sohn übernahm also die Regierung zu Lebzeiten seines Vaters vgl. 2 Chr 21,4."] },
      { number: 17, text: "Er war zweiunddreißig Jahre alt, als er König wurde, und regierte acht Jahre in Jerusalem." },
      { number: 18, text: "Er folgte den Wegen der Könige von Israel, wie es das Haus Ahab getan hatte; denn er hatte eine Tochter Ahabs zur Frau und er tat, was dem Herrn missfiel." },
      { number: 19, text: "Doch der Herr wollte Juda nicht verderben wegen seines Knechtes David, dem er versprochen hatte, er werde ihm für immer eine Leuchte vor seinen Augen geben.", footnotes: ["1 Kön 11,36"] },
      { number: 20, text: "In den Tagen Jorams fiel Edom von Juda ab und setzte einen eigenen König ein." },
      { number: 21, text: "Joram zog daher mit all seinen Kriegswagen nach Zaïr. Während der Nacht griff er an und schlug die Edomiter, die ihn und die Obersten der Kriegswagen umzingelt hatten; seine Leute aber waren in die Zelte geflohen.", footnotes: ["Joram und die Hauptleute seiner Wagentruppe waren von den Edomitern eingeschlossen, nachdem die Mannschaften geflohen waren. Doch gelang ihnen der Durchbruch und die Flucht."] },
      { number: 22, text: "Doch Edom fiel von Juda ab und ist abtrünnig bis zum heutigen Tag. Damals, zur gleichen Zeit, fiel auch Libna ab." },
      { number: 23, text: "Die übrige Geschichte Jorams und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 24, text: "Joram entschlief zu seinen Vätern und wurde bei seinen Vätern in der Davidstadt begraben. Sein Sohn Ahasja wurde König an seiner Stelle." },
      { number: 25, text: "Im zwölften Jahr Jorams, des Sohnes Ahabs, des Königs von Israel, wurde Ahasja, der Sohn Jorams, König von Juda.", heading: "Ahasja von Juda: 8,25-29", footnotes: ["(25-29) 2 Chr 22,2-6"] },
      { number: 26, text: "Er war zweiundzwanzig Jahre alt, als er König wurde, und regierte ein Jahr in Jerusalem. Seine Mutter hieß Atalja; sie war eine Enkelin Omris, des Königs von Israel." },
      { number: 27, text: "Er folgte den Wegen des Hauses Ahab und tat, was dem Herrn missfiel, wie das Haus Ahab; denn er war mit dem Haus Ahab verschwägert." },
      { number: 28, text: "Er zog auch mit Joram, dem Sohn Ahabs, gegen Hasaël, den König von Aram, nach Ramot-Gilead in den Krieg. Dabei verwundeten die Aramäer Joram.", footnotes: ["Joram nahm den Versuch, Ramot-Gilead zu erobern, wieder auf (vgl. 1 Kön 22)."] },
      { number: 29, text: "König Joram musste heimkehren, um in Jesreel von den Wunden Heilung zu suchen, die ihm die Aramäer geschlagen hatten, als er in Ramot gegen ihren König Hasaël kämpfte. Ahasja, der Sohn Jorams, der König von Juda, kam hinab, um Joram, den Sohn Ahabs, in Jesreel zu besuchen, als er krank daniederlag." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Der Prophet Elischa rief einen von den Prophetenjüngern und trug ihm auf: Gürte dich, nimm diesen Ölkrug und geh nach Ramot-Gilead!", heading: "Der Aufstand Jehus: 9,1-15a" },
      { number: 2, text: "Wenn du dorthin kommst, such Jehu, den Sohn Joschafats, des Sohnes Nimschis! Geh zu ihm, ruf ihn aus dem Kreis seiner Brüder und begib dich mit ihm in das innerste Gemach!" },
      { number: 3, text: "Dann nimm den Ölkrug, gieß ihn über seinem Haupt aus und sag: So spricht der Herr: Ich salbe dich zum König über Israel. Dann öffne die Tür und eile unverzüglich fort!" },
      { number: 4, text: "Der Prophetenjünger ging nach Ramot-Gilead." },
      { number: 5, text: "Als er hinkam, saßen die Obersten des Heeres gerade beisammen. Er sagte: Ich habe einen Auftrag an dich, Oberst. Jehu fragte: An wen von uns? Der junge Mann antwortete: An dich, Oberst." },
      { number: 6, text: "Da stand Jehu auf und ging in das Haus. Der Prophetenjünger goss ihm das Öl über das Haupt mit den Worten: So spricht der Herr, der Gott Israels: Ich salbe dich zum König über das Volk des Herrn, über Israel." },
      { number: 7, text: "Du wirst dem Haus Ahabs, deines Herrn, schwere Schläge versetzen und ich werde Rache nehmen für das Blut meiner Knechte, der Propheten, und für das Blut aller Diener des Herrn, das Isebel vergossen hat." },
      { number: 8, text: "Das ganze Haus Ahab wird zugrunde gehen. Ich werde vom Haus Ahab alles, was männlich ist, bis zum letzten Mann in Israel ausrotten", footnotes: ["1 Kön 14,10f; 16,4; 21,21-24"] },
      { number: 9, text: "und es dem Haus Ahab ergehen lassen wie dem Haus Jerobeams, des Sohnes Nebats, und dem Haus Baschas, des Sohnes Ahijas." },
      { number: 10, text: "Isebel werden auf der Flur von Jesreel die Hunde fressen und niemand wird sie begraben. Dann öffnete er die Tür und eilte davon." },
      { number: 11, text: "Als Jehu zu den Leuten seines Herrn herauskam, fragten sie ihn: Steht es gut? Warum ist denn dieser Verrückte zu dir gekommen? Er antwortete: Ihr kennt doch den Mann und sein Gerede.", footnotes: ["fragten sie: Text korr. nach G; H: fragte er."] },
      { number: 12, text: "Doch sie sagten: Das sind Ausflüchte. Teil uns nur alles mit! Da gab er zu: So und so hat er zu mir gesagt: So spricht der Herr: Ich salbe dich zum König über Israel." },
      { number: 13, text: "Sogleich nahmen alle ihre Kleider, legten sie ihm zu Füßen auf die bloßen Stufen, stießen in das Horn und riefen: Jehu ist König.", footnotes: ["Durch das Hinbreiten ihrer Kleider drücken sie aus, dass sie sich ihm unterwerfen (vgl. Mt 21,8)."] },
      { number: 14, text: "So verschwor sich Jehu, der Sohn Joschafats, des Sohnes Nimschis, gegen Joram. - Joram hatte mit ganz Israel vor Ramot-Gilead im Abwehrkampf gegen Hasaël, den König von Aram, gestanden." },
      { number: 15, text: "Er war dann heimgekehrt, um in Jesreel Heilung von den Wunden zu suchen, die ihm die Aramäer geschlagen hatten, als er gegen ihren König Hasaël kämpfte." },
      { number: 16, text: "Jehu sagte nun: Wenn ihr einverstanden seid, lassen wir keinen aus der Stadt entkommen, der nach Jesreel gehen und dort Meldung machen könnte. Dann bestieg er seinen Wagen, um selbst nach Jesreel zu fahren; denn dort lag Joram danieder. Auch Ahasja, der König von Juda, war dorthin gekommen, um Joram zu besuchen.", heading: "Das Ende der beiden Könige: 9,15b-29" },
      { number: 17, text: "Der Wächter, der in Jesreel auf dem Turm stand, sah die Schar Jehus herankommen und meldete: Ich sehe eine Schar. Da befahl Joram: Hol einen Reiter und schick ihn der Schar entgegen! Er soll fragen, ob er in friedlicher Absicht kommt." },
      { number: 18, text: "Als der Reiter sie erreichte, sagte er: Der König lässt fragen, ob ihr in friedlicher Absicht kommt. Doch Jehu antwortete: Was geht es dich an, ob wir in friedlicher Absicht kommen? Reih dich hinter mir ein! Der Wächter meldete: Der Bote hat sie erreicht, kehrt aber nicht mehr zurück." },
      { number: 19, text: "Nun schickte der König einen zweiten Reiter. Dieser kam zu ihnen und sagte: Der König lässt fragen, ob ihr in friedlicher Absicht kommt. Doch Jehu antwortete: Was geht es dich an, ob wir in friedlicher Absicht kommen? Reih dich hinter mir ein!" },
      { number: 20, text: "Der Wächter meldete wieder: Er hat sie erreicht, kehrt aber nicht mehr zurück. Die Art, wie ihr Anführer fährt, ist die Art Jehus, des Enkels Nimschis; denn er fährt wie ein Rasender." },
      { number: 21, text: "Da ließ Joram selbst anspannen. Man machte seinen Wagen bereit, und Joram, der König von Israel, und Ahasja, der König von Juda, fuhren, jeder auf seinem Wagen, Jehu entgegen. Sie trafen ihn beim Acker Nabots aus Jesreel." },
      { number: 22, text: "Als Joram Jehu sah, fragte er: Kommst du in friedlicher Absicht, Jehu? Doch dieser erwiderte: Wie sollte ich in friedlicher Absicht kommen, solange die Unzucht deiner Mutter Isebel und ihre vielen Zaubereien andauern?" },
      { number: 23, text: "Da lenkte Joram um und wollte fliehen, während er Ahasja zurief: Verrat, Ahasja!" },
      { number: 24, text: "Doch Jehu ergriff den Bogen und traf Joram zwischen die Schultern, sodass der Pfeil sein Herz durchbohrte und er in seinem Wagen niedersank." },
      { number: 25, text: "Dann befahl Jehu seinem Adjutanten Bidkar: Nimm ihn, und wirf ihn auf den Acker Nabots aus Jesreel. Denk daran: Ich und du fuhren auf unseren Gespannen hinter seinem Vater Ahab her, als der Herr dieses Wort über ihn verkünden ließ:" },
      { number: 26, text: "Fürwahr, ich habe gestern das Blut Nabots und seiner Söhne gesehen Spruch des Herrn. Ich werde an dir auf diesem Acker Vergeltung üben Spruch des Herrn. Nimm ihn also und wirf ihn auf den Acker, wie es der Herr gesagt hat.", footnotes: ["1 Kön 21,19"] },
      { number: 27, text: "Als Ahasja, der König von Juda, dies sah, floh er in Richtung Bet-Gan. Doch Jehu verfolgte ihn und rief: Schlagt auch diesen nieder! Man schlug ihn beim Anstieg nach Gur, das bei Jibleam liegt, im Wagen nieder. Er kam noch bis Megiddo, wo er starb." },
      { number: 28, text: "Seine Diener brachten ihn nach Jerusalem und begruben ihn bei seinen Vätern in seinem Grab in der Davidstadt." },
      { number: 29, text: "Im elften Jahr Jorams, des Sohnes Ahabs, war Ahasja König von Juda geworden.", footnotes: ["Nachtrag, der das in 8,25 genannte zwölfte Jahr Jorams berichtigen soll. Da beide Könige im zwölften Jahr Jorams starben und Ahasja nur ein Jahr regierte, muss er im 11. Jahr Jorams König geworden sein."] },
      { number: 30, text: "Als Jehu nach Jesreel kam und Isebel dies erfuhr, legte sie Schminke auf ihre Augen, schmückte ihr Haupt und schaute durch das Fenster hinab.", heading: "Das Ende Isebels: 9,30-37", footnotes: ["Sie weiß, was ihr bevorsteht, und will noch im Tod ihre Würde zeigen."] },
      { number: 31, text: "Während dann Jehu an das Tor trat, rief sie ihm zu: Geht es Simri, dem Mörder seines Herrn, gut?", footnotes: ["1 Kön 16,10", "Sie bezeichnet höhnisch Jehu als Simri, der den König tötete und sich bereits nach sieben Tagen das Leben nahm (vgl. 1 Kön 16,10.15-18)."] },
      { number: 32, text: "Jehu schaute zum Fenster empor und fragte: Ist jemand da, der zu mir hält? Zwei oder drei Hofleute sahen zu ihm herab" },
      { number: 33, text: "und er befahl ihnen: Werft sie herunter! Sie warfen sie herunter und Isebels Blut bespritzte die Wand und die Pferde, die sie zertraten." },
      { number: 34, text: "Dann ging Jehu hinein, um zu essen und zu trinken. Schließlich befahl er: Seht nach dieser Verfluchten und begrabt sie; denn sie ist eine Königstochter." },
      { number: 35, text: "Doch als sie hinkamen, um sie zu begraben, fanden sie von ihr nur noch den Schädel, die Füße und die Hände." },
      { number: 36, text: "Und sie kamen zurück, um es ihm zu melden. Er aber sagte: Das ist das Wort, das der Herr durch seinen Knecht Elija aus Tischbe verkündet hat: Auf der Flur von Jesreel werden die Hunde das Fleisch Isebels fressen.", footnotes: ["1 Kön 21,23"] },
      { number: 37, text: "Die Leiche Isebels soll wie Mist auf dem Feld in der Flur Jesreels liegen, sodass man nicht mehr sagen kann: Das ist Isebel." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Ahab hatte siebzig Söhne in Samaria. Jehu sandte nun Briefe nach Samaria an die Fürsten der Stadt, an die Ältesten und die Erzieher der Söhne Ahabs. Darin schrieb er:", heading: "Der Untergang des Hauses Ahab: 10,1-17", footnotes: ["Fürsten der Stadt . . . Erzieher der Söhne: Text korr. nach dem Zusammenhang."] },
      { number: 2, text: "Wenn jetzt dieses Schreiben zu euch kommt, bedenkt: Bei euch sind die Söhne eures Herrn. Ihr habt die Wagen und die Pferde, eine befestigte Stadt und die Waffen." },
      { number: 3, text: "Seht euch nach dem besten und tüchtigsten unter den Söhnen eures Herrn um, setzt ihn auf den Thron seines Vaters und kämpft für das Haus eures Herrn!" },
      { number: 4, text: "Doch sie bekamen große Angst und sagten: Die beiden Könige konnten ihm nicht standhalten. Wie sollten wir es können?" },
      { number: 5, text: "Daher ließen der Palastvorsteher, der Stadtoberste, die Ältesten und die Erzieher dem Jehu melden: Wir sind deine Knechte und wollen alles tun, was du uns befiehlst; wir werden keinen König aufstellen. Tu, was du für richtig hältst." },
      { number: 6, text: "Darauf schrieb er einen zweiten Brief an sie mit folgendem Inhalt: Wenn ihr auf meiner Seite steht und meinen Befehlen gehorchen wollt, dann nehmt die Köpfe der Söhne eures Herrn und kommt morgen um diese Zeit zu mir nach Jesreel! Die siebzig Söhne des Königs waren bei den Großen der Stadt, die sie erzogen." },
      { number: 7, text: "Sobald der Brief angekommen war, ergriff man die Söhne des Königs, machte alle siebzig nieder, legte ihre Köpfe in Körbe und schickte sie zu Jehu nach Jesreel." },
      { number: 8, text: "Als der Bote mit der Meldung eintraf, dass man die Köpfe der Königssöhne gebracht habe, befahl Jehu: Schichtet sie bis zum Morgen in zwei Haufen vor dem Tor auf!" },
      { number: 9, text: "Am nächsten Morgen ging er hinaus, trat vor das Volk und sagte: Ihr seid ohne Schuld. Ich bin es, der die Verschwörung gegen meinen Herrn angezettelt und ihn getötet hat. Doch wer hat diese alle erschlagen?" },
      { number: 10, text: "Erkennt also, dass keine der Drohungen unerfüllt bleibt, die der Herr gegen das Haus Ahab ausgesprochen hat. Der Herr hat das getan, was er durch seinen Knecht Elija verkündet hat.", footnotes: ["1 Kön 21,18-24"] },
      { number: 11, text: "Hierauf ließ Jehu alle vom Haus Ahab erschlagen, die noch in Jesreel übrig geblieben waren, alle Großen und Vertrauten Ahabs, auch alle seine Priester; keinen von ihnen ließ er entkommen." },
      { number: 12, text: "Dann brach er auf, um nach Samaria zu ziehen. Als er unterwegs bei Bet-Eked-Roïm war," },
      { number: 13, text: "traf er die Brüder Ahasjas, des Königs von Juda. Er fragte sie: Wer seid ihr? Sie antworteten: Wir sind die Brüder Ahasjas und sind gekommen, um die Söhne des Königs und die Söhne der Herrin zu begrüßen.", footnotes: ["Brüder: im weiteren Sinn von «Verwandten»; die «Herrin» ist die Königin-Mutter."] },
      { number: 14, text: "Da befahl er: Ergreift sie lebendig! Man ergriff sie lebendig und machte sie am Brunnen von Bet-Eked nieder. Es waren zweiundvierzig Mann; keinen von ihnen ließ er am Leben." },
      { number: 15, text: "Von da zog er weiter und traf Jonadab, den Sohn Rechabs, der ihm entgegenkam. Er grüßte ihn und fragte: Ist dein Herz aufrichtig gegen mich wie mein Herz gegen dich? Jonadab antwortete: So ist es. (Da sagte Jehu:) Wenn es so ist, dann reich mir deine Hand! Da reichte er ihm die Hand und Jehu nahm ihn zu sich auf den Wagen", footnotes: ["Jer 35,1-11"] },
      { number: 16, text: "und sagte: Komm mit mir und sieh meinen Eifer für den Herrn! So ließ er ihn bei sich im Wagen fahren.", footnotes: ["ließ er ihn . . . fahren: Text korr. nach G; H: ließen sie ihn . . . fahren."] },
      { number: 17, text: "Als er nach Samaria kam, ließ er alle erschlagen, die vom Haus Ahab dort noch übrig geblieben waren. Sie wurden ausgerottet, wie es der Herr zu Elija gesagt hatte." },
      { number: 18, text: "Jehu versammelte das ganze Volk und trug ihm vor: Ahab hat Baal nur wenig gedient; Jehu wird ihm eifriger dienen.", heading: "Die Beseitigung der Baalspriester: 10,18-29" },
      { number: 19, text: "Ruft jetzt alle Propheten Baals, alle seine Diener und Priester zu mir! Keiner darf fernbleiben; denn ich will ein großes Schlachtopfer für Baal darbringen. Wer nicht erscheint, soll sein Leben verlieren. Jehu tat dies aus Hinterlist, weil er die Baalsdiener vernichten wollte." },
      { number: 20, text: "Darauf befahl Jehu: Bereitet eine Festversammlung zur Ehre Baals vor! Und um sie einzuberufen," },
      { number: 21, text: "sandte Jehu Boten durch ganz Israel. Alle Baalsdiener kamen; es gab keinen, der fernblieb. Sie betraten den Baalstempel, der sich von einem Ende zum andern füllte." },
      { number: 22, text: "Nun befahl Jehu dem Verwalter der Kleiderkammer, jedem Baalsdiener ein Kleid zu reichen. Nachdem dieser die Gewänder überreicht hatte," },
      { number: 23, text: "betrat Jehu mit Jonadab, dem Sohn Rechabs, den Baalstempel und forderte die Baalsdiener auf: Vergewissert euch, dass nur Baalsdiener hier sind und dass kein Diener Jahwes sich unter euch befindet." },
      { number: 24, text: "Darauf begannen sie, Schlacht- und Brandopfer darzubringen. Jehu hatte aber draußen achtzig Mann aufgestellt und ihnen gesagt: Wer einen von den Männern, die ich in eure Hand gebe, entkommen lässt, wird mit seinem Leben dafür büßen." },
      { number: 25, text: "Nach Beendigung des Brandopfers befahl er den Läufern und ihren Hauptleuten: Kommt her und macht sie nieder! Keiner darf entrinnen. Die Läufer und ihre Hauptleute erschlugen sie mit scharfem Schwert und warfen die Leichen hinaus. Dann machten sie sich über den Tempel des Baal her,", footnotes: ["Zu den Läufern vgl. die Anmerkung zu 1 Kön 14,27. - Der Schluss des Verses in H: machten sie sich über die Stadt des Tempels des Baal her."] },
      { number: 26, text: "schafften das Steinmal des Baalstempels weg und verbrannten es." },
      { number: 27, text: "Sie zerschlugen das Steinmal des Baal, rissen den Baalstempel nieder und machten ihn zu einer Stätte des Unrats; das blieb er bis zum heutigen Tag." },
      { number: 28, text: "So beseitigte Jehu den Baal aus Israel." },
      { number: 29, text: "Doch von den Sünden, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte, den goldenen Kälbern in Bet-El und Dan, ließ Jehu nicht ab." },
      { number: 30, text: "Der Herr sprach zu Jehu: Weil du mein Vorhaben genau vollstreckt und am Haus Ahab alles ausgeführt hast, was ich ihm zugedacht hatte, sollen Nachkommen von dir bis in das vierte Geschlecht auf dem Thron Israels sitzen.", heading: "Verschiedene Angaben über Jehu: 10,30-36", footnotes: ["Das Vorgehen gegen das Haus Ahab wird als Ausführung der von Gott beschlossenen Strafgerichte anerkannt; anders jedoch in Hos 1,4."] },
      { number: 31, text: "Doch Jehu war nicht darauf bedacht, mit ganzem Herzen das Gesetz des Herrn, des Gottes Israels, zu befolgen. Er ließ nicht von den Sünden ab, zu denen Jerobeam die Israeliten verführt hatte." },
      { number: 32, text: "In jenen Tagen begann der Herr, Israel zu verkleinern. Hasaël schlug sie in allen Gebieten Israels" },
      { number: 33, text: "östlich des Jordan: Er verwüstete das ganze Land Gilead, das Gebiet der Stämme Gad, Ruben und Manasse bis nach Aroër am Arnon, Gilead und den Baschan.", footnotes: ["Dadurch ging das ganze Ostjordanland verloren."] },
      { number: 34, text: "Die übrige Geschichte Jehus, alle seine Taten und Erfolge, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 35, text: "Jehu entschlief zu seinen Vätern; man begrub ihn in Samaria. Sein Sohn Joahas wurde König an seiner Stelle." },
      { number: 36, text: "Die Zeit, die Jehu in Samaria König von Israel war, betrug achtundzwanzig Jahre." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Als Atalja, die Mutter Ahasjas, sah, dass ihr Sohn tot war, ging sie daran, die ganze Nachkommenschaft der königlichen Familie auszurotten.", heading: "Atalja: 11,1-20", footnotes: ["(1-20) 2 Chr 22,10 - 23,21"] },
      { number: 2, text: "Doch Joscheba, die Tochter des Königs Joram und Schwester Ahasjas, nahm Joasch, den Sohn Ahasjas, aus dem Kreis der Königssöhne, die ermordet werden sollten, weg und brachte ihn heimlich mit seiner Amme in die Bettenkammer. Dort versteckte sie ihn vor Atalja, sodass er nicht getötet wurde.", footnotes: ["versteckte sie: Text korr. nach G; H: versteckten sie."] },
      { number: 3, text: "Er blieb sechs Jahre bei ihr im Haus des Herrn verborgen, während Atalja das Land regierte.", footnotes: ["Durch Atalja, die Tochter Ahabs, war die davidische Linie unterbrochen. Ihre Regierung galt daher als illegitim."] },
      { number: 4, text: "Im siebten Jahr bestellte der Priester Jojada die Hundertschaftsführer der Karer und Läufer zu sich. Er führte sie in das Haus des Herrn, schloss mit ihnen ein Abkommen, ließ sie im Haus des Herrn schwören und zeigte ihnen den Sohn des Königs.", footnotes: ["Karer waren ausländische Söldner wie die Kereter und Peleter. Zu den «Läufern» vgl. die Anmerkung zu 1 Kön 14,27."] },
      { number: 5, text: "Dann ordnete er an: Das ist es, was ihr tun sollt: Ein Drittel von der Wache, die am Sabbat aufzieht, soll den Königspalast bewachen,", footnotes: ["soll den Königspalast bewachen: Text korr. nach G."] },
      { number: 6, text: "ein Drittel soll am Tor Sur und ein Drittel am Tor hinter den Läufern stehen. Auf diese Weise sollt ihr abwechselnd die Bewachung des Tempels übernehmen." },
      { number: 7, text: "Die zwei Abteilungen aber, die am Sabbat abziehen, sollen im Haus des Herrn beim König als Wache stehen." },
      { number: 8, text: "Schart euch mit der Waffe in der Hand um den König! Wer in die Reihen einzudringen versucht, soll getötet werden. Seid beim König, wenn er auszieht und wenn er einzieht." },
      { number: 9, text: "Die Führer der Hundertschaften befolgten alle Weisungen des Priesters Jojada. Jeder holte seine Leute, sowohl jene, die am Sabbat aufzogen, als auch jene, die am Sabbat abzogen. Sie kamen zum Priester Jojada" },
      { number: 10, text: "und dieser gab den Anführern der Hundertschaften die Lanzen und Schilde, die dem König David gehört hatten und sich jetzt im Haus des Herrn befanden." },
      { number: 11, text: "Die Läufer stellten sich mit der Waffe in der Hand von der Südseite des Tempels bis zur Nordseite vor dem Altar und dem Tempel rings um den König auf." },
      { number: 12, text: "Dann führte Jojada den Königssohn heraus und überreichte ihm den Stirnreif und das Königsgesetz. So machten sie ihn zum König, salbten ihn, klatschten in die Hände und riefen: Es lebe der König!", footnotes: ["Dtn 17,18"] },
      { number: 13, text: "Als Atalja das Geschrei des Volkes hörte, kam sie zu den Leuten in das Haus des Herrn." },
      { number: 14, text: "Da sah sie den König am gewohnten Platz bei der Säule stehen; die Obersten und die Trompeter waren bei ihm und alle Bürger des Landes waren voller Freude und bliesen die Trompeten. Atalja zerriss ihre Kleider und schrie: Verrat, Verrat!" },
      { number: 15, text: "Doch der Priester Jojada befahl den Hundertschaftsführern, die das Kommando über die Truppen hatten: Führt sie durch die Reihen hinaus und schlagt jeden mit dem Schwert nieder, der ihr folgen will; denn so sagte der Priester - sie soll nicht im Haus des Herrn getötet werden." },
      { number: 16, text: "Da legte man Hand an sie, und als sie an den Weg kam, auf dem man die Pferde zum Palast des Königs führt, wurde sie dort getötet." },
      { number: 17, text: "Jojada schloss nun den Bund des Herrn mit König und Volk. Sie versprachen, dass sie das Volk des Herrn sein wollten. Auch König und Volk ließ er einen Bund schließen.", footnotes: ["Der Bund zwischen König und Volk hatte die Wiedereinsetzung der davidischen Dynastie zum Ziel."] },
      { number: 18, text: "Darauf zogen alle Bürger des Landes zum Baalstempel und rissen ihn nieder. Sie zertrümmerten seine Altäre und Bilder vollständig und erschlugen den Baalspriester Mattan vor den Altären. Auch stellte Jojada Posten vor das Haus des Herrn" },
      { number: 19, text: "und rief die Hundertschaftsführer, die Karer und Läufer sowie alle Bürger des Landes herbei. Sie führten den König aus dem Haus des Herrn durch das Tor der Läufer hinab in den königlichen Palast. Dort setzte er sich auf den Thron der Könige." },
      { number: 20, text: "Alle Bürger des Landes waren voll Freude und die Stadt blieb ruhig. Atalja aber hatte man vor dem Palast des Königs mit dem Schwert umgebracht." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Joasch war sieben Jahre alt, als er König wurde.", heading: "Joasch von Juda: 12,1-22", footnotes: ["(1-22) 2 Chr 24,1-27"] },
      { number: 2, text: "Im siebten Jahr Jehus wurde er König und vierzig Jahre regierte er in Jerusalem. Seine Mutter hieß Zibja und stammte aus Beerscheba." },
      { number: 3, text: "Er tat, was dem Herrn gefiel, solange der Priester Jojada ihn unterwies." },
      { number: 4, text: "Nur die Kulthöhen verschwanden nicht. Das Volk brachte noch Schlacht- und Rauchopfer auf ihnen dar." },
      { number: 5, text: "Joasch gab den Priestern die Weisung: Alles Geld, das als Weihegabe in das Haus des Herrn gebracht wird, ferner das Geld, das jemandem durch Schätzung auferlegt wird oder das er nach der Schätzung für andere zu entrichten hat, endlich das Geld, das jemand freiwillig in das Haus des Herrn bringt,", footnotes: ["Lev 27,1-8"] },
      { number: 6, text: "das alles sollen die Priester an sich nehmen, jeder von seinen Bekannten. Sie sollen damit die Schäden ausbessern, die man am Tempel feststellt." },
      { number: 7, text: "Als aber die Priester im dreiundzwanzigsten Jahr des Königs Joasch die Schäden des Hauses noch nicht beseitigt hatten," },
      { number: 8, text: "ließ König Joasch den Priester Jojada und die übrigen Priester kommen und hielt ihnen vor: Warum bessert ihr die Schäden am Tempel nicht aus? Von jetzt an dürft ihr das Geld, das von euren Bekannten kommt, nicht mehr an euch nehmen, sondern müsst es unverzüglich zur Ausbesserung der Schäden des Hauses abliefern." },
      { number: 9, text: "Die Priester waren damit einverstanden, dass sie vom Volk kein Geld mehr annehmen durften, aber auch für die Schäden des Hauses nicht mehr aufkommen mussten." },
      { number: 10, text: "Der Priester Jojada nahm nun einen Kasten, bohrte ein Loch in seinen Deckel und stellte ihn neben dem Altar rechts vom Eingang zum Tempel des Herrn auf. Dahinein legten die Priester, die an der Schwelle Wache hielten, alles Geld, das in den Tempel des Herrn gebracht wurde." },
      { number: 11, text: "Wenn sie dann sahen, dass viel Geld im Kasten war, kam der Schreiber des Königs mit dem Hohenpriester. Sie leerten den Kasten, zählten das Geld, das sich im Haus des Herrn befand,", footnotes: ["sie leerten: Text korr. nach 2 Chr 24,11; H: sie verschnürten."] },
      { number: 12, text: "und übergaben es abgewogen den Werkmeistern, die die Arbeiten im Haus des Herrn beaufsichtigten. Diese verwendeten es für die Zimmerleute und Bauarbeiter, die im Haus des Herrn beschäftigt waren," },
      { number: 13, text: "für die Maurer und Steinmetzen, ebenso zum Ankauf von Holz und Bruchsteinen, die man zur Beseitigung der Schäden am Haus des Herrn benötigte, und zur Bestreitung aller Unkosten, die bei der Ausbesserung des Hauses entstanden." },
      { number: 14, text: "Man ließ jedoch von dem Geld, das in den Tempel kam, keine silbernen Becken, Lichtscheren, Schalen, Trompeten, auch keinerlei Gold- und Silbergeräte für das Haus des Herrn anfertigen," },
      { number: 15, text: "sondern gab alles den Werkmeistern, die damit das Haus des Herrn instand setzten." },
      { number: 16, text: "Man rechnete mit den Männern, denen man das Geld zur Ausbezahlung an die Arbeiter übergab, nicht ab; denn sie waren zuverlässige Leute." },
      { number: 17, text: "Geld für Schuld- und Sündopfer dagegen wurde nicht an den Tempel abgeführt, sondern gehörte den Priestern." },
      { number: 18, text: "Damals zog Hasaël, der König von Aram, herauf, griff Gat an und eroberte es. Als er sich anschickte, auch gegen Jerusalem zu ziehen," },
      { number: 19, text: "nahm Joasch, der König von Juda, alle Weihegaben, die Joschafat, Joram und Ahasja, seine Vorgänger, die Könige von Juda, gespendet hatten, dazu seine eigenen Weihegaben und alles Gold, das in den Schatzkammern des Tempels und des königlichen Palastes war, und sandte alles an Hasaël, den König von Aram, der daraufhin von Jerusalem abzog." },
      { number: 20, text: "Die übrige Geschichte des Joasch und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 21, text: "Seine Diener erhoben sich gegen ihn; sie zettelten eine Verschwörung an und erschlugen ihn im Haus am Millo beim Abstieg nach Silla." },
      { number: 22, text: "Sabad, der Sohn Schimats, und Josabad, der Sohn Schomers, seine Diener, waren es, die ihn erschlugen. So starb er und man begrub ihn bei seinen Vätern in der Davidstadt. Sein Sohn Amazja wurde König an seiner Stelle." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Im dreiundzwanzigsten Jahr des Joasch, des Sohnes Ahasjas, des Königs von Juda, wurde Joahas, der Sohn Jehus, König von Israel. Er regierte siebzehn Jahre in Samaria", heading: "Joahas von Israel: 13,1-9" },
      { number: 2, text: "und tat, was dem Herrn missfiel; er hielt an der Sünde fest, zu der Jerobeam, der Sohn Nebats, Israel verführt hatte, und ließ nicht von ihr ab." },
      { number: 3, text: "Deswegen entbrannte der Zorn des Herrn gegen Israel. Er gab sie in die Gewalt Hasaëls, des Königs von Aram, und seines Sohnes Ben-Hadad, die ganze Zeit hindurch." },
      { number: 4, text: "Als aber Joahas den Herrn besänftigte, erhörte ihn der Herr; denn er sah die Bedrängnis, die der König von Aram über Israel brachte." },
      { number: 5, text: "Er gab Israel einen Helfer, sodass es sich aus der Gewalt Arams befreien konnte und die Israeliten wieder in ihren Zelten wohnten wie früher." },
      { number: 6, text: "Doch ließen sie nicht von der Sünde ab, zu der das Haus Jerobeam Israel verführt hatte, sondern hielten an ihr fest. Auch der Kultpfahl blieb in Samaria stehen." },
      { number: 7, text: "Der Herr ließ vom Heer des Joahas nur fünfzig Wagenkämpfer, zehn Wagen und zehntausend Mann Fußtruppen übrig. Alles andere hatte der König von Aram vernichtet und dem Staub gleichgemacht, auf den man tritt.", footnotes: ["Der Vers ist Fortsetzung von V. 3."] },
      { number: 8, text: "Die übrige Geschichte des Joahas, alle seine Taten und Erfolge, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 9, text: "Er entschlief zu seinen Vätern; man begrub ihn in Samaria. Sein Sohn Joasch wurde König an seiner Stelle." },
      { number: 10, text: "Im siebenunddreißigsten Jahr des Königs Joasch von Juda wurde Joasch, der Sohn des Joahas, König von Israel. Er regierte sechzehn Jahre in Samaria", heading: "Joasch von Israel: 13,10-13" },
      { number: 11, text: "und tat, was dem Herrn missfiel; er ließ nicht von der Sünde ab, zu der Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 12, text: "Die übrige Geschichte des Joasch, alle seine Taten und Erfolge, wie er mit Amazja, dem König von Juda, Krieg führte, all das ist aufgezeichnet in der Chronik der Könige von Israel.", footnotes: ["(12-13) 14,15f"] },
      { number: 13, text: "Joasch entschlief zu seinen Vätern und Jerobeam bestieg seinen Thron. Joasch wurde in Samaria bei den Königen von Israel begraben." },
      { number: 14, text: "Als Elischa von der Krankheit befallen wurde, an der er sterben sollte, ging Joasch, der König von Israel, zu ihm hinab. Er weinte vor ihm und rief: Mein Vater, mein Vater! Wagen Israels und sein Lenker!", heading: "Die Krankheit und der Tod Elischas: 13,14-21", footnotes: ["2,12"] },
      { number: 15, text: "Doch Elischa befahl ihm: Hol einen Bogen und Pfeile! Er holte sie herbei." },
      { number: 16, text: "Dann sagte Elischa zum König von Israel: Leg deine Hand auf den Bogen! Da legte er die Hand auf den Bogen, Elischa aber legte seine Hände auf die Hände des Königs." },
      { number: 17, text: "Hierauf sagte er: Öffne das Fenster nach Osten! Der König öffnete es und Elischa forderte ihn auf zu schießen. Als er abschoss, rief Elischa: Ein Siegespfeil vom Herrn, ein Siegespfeil gegen Aram. Du wirst Aram bei Afek vernichtend schlagen.", footnotes: ["Der König soll die Worte des Propheten durch sinnbildliche Handlungen begleiten. Die Erfüllung der Weissagung berichtet 1 Kön 20,26-30."] },
      { number: 18, text: "Weiter sagte er: Nimm die Pfeile! Als der König von Israel sie genommen hatte, befahl ihm Elischa, auf den Boden zu schlagen. Der König tat drei Schläge und hielt dann inne." },
      { number: 19, text: "Da wurde der Gottesmann unwillig über ihn und sagte: Du hättest fünf- oder sechsmal schlagen sollen; dann hättest du die Aramäer vernichtend geschlagen. Jetzt aber wirst du sie nur dreimal schlagen.", footnotes: ["13,25"] },
      { number: 20, text: "Elischa starb und man begrub ihn. In jenem Jahr fielen moabitische Räuberscharen in das Land ein.", footnotes: ["In jenem Jahr: Text korr.; andere Korrektur: Jahr für Jahr."] },
      { number: 21, text: "Als man einmal einen Toten begrub und eine dieser Scharen erblickte, warf man den Toten in das Grab Elischas und floh. Sobald aber der Tote die Gebeine Elischas berührte, wurde er wieder lebendig und richtete sich auf." },
      { number: 22, text: "Hasaël, der König von Aram, bedrängte Israel, solange Joahas lebte.", heading: "Die Erfolge gegen Aram: 13,22-25" },
      { number: 23, text: "Doch der Herr war seinem Volk gnädig und hatte Erbarmen mit ihm. Wegen seines Bundes mit Abraham, Isaak und Jakob wandte er sich ihm wieder zu. Er wollte es nicht verderben und hatte es bis dahin nicht von seinem Angesicht verstoßen." },
      { number: 24, text: "Als daher Hasaël, der König von Aram, starb und sein Sohn Ben-Hadad an seiner Stelle König wurde," },
      { number: 25, text: "konnte Joasch, der Sohn des Joahas, Ben-Hadad, dem Sohn Hasaëls, die Städte wieder entreißen, die dieser seinem Vater Joahas im Krieg weggenommen hatte. Dreimal schlug ihn Joasch und gewann die israelitischen Städte zurück.", footnotes: ["1 Kön 20,34"] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Im zweiten Jahr des Joasch, des Sohnes des Joahas, des Königs von Israel, wurde Amazja, der Sohn des Joasch, König von Juda.", heading: "Amazja von Juda: 14,1-22", footnotes: ["(1-20) 2 Chr 25,1-4.17-28"] },
      { number: 2, text: "Er war fünfundzwanzig Jahre alt, als er König wurde, und regierte neunundzwanzig Jahre in Jerusalem. Seine Mutter hieß Joaddan und stammte aus Jerusalem." },
      { number: 3, text: "Er tat, was dem Herrn gefiel, wenn auch nicht in der Weise seines Ahnherrn David; sein Verhalten glich dem seines Vaters Joasch." },
      { number: 4, text: "Die Kulthöhen verschwanden nicht; das Volk brachte noch Schlachtund Rauchopfer auf ihnen dar." },
      { number: 5, text: "Sobald die Herrschaft fest in seiner Hand war, ließ er die Diener hinrichten, die seinen Vater, den König, erschlagen hatten." },
      { number: 6, text: "Die Söhne der Mörder aber verschonte er, wie der Herr es geboten hatte und wie es im Gesetzbuch des Mose niedergeschrieben ist: Die Väter sollen nicht für ihre Söhne und die Söhne nicht für ihre Väter mit dem Tod bestraft werden, sondern jeder soll nur für sein eigenes Verbrechen sterben.", footnotes: ["Dtn 24,16; Ez 18,10-23"] },
      { number: 7, text: "Er besiegte die Edomiter, zehntausend Mann, im Salztal, nahm Sela im Kampf und nannte es Jokteel, wie es bis heute genannt wird." },
      { number: 8, text: "Damals sandte Amazja Boten an Joasch, den Sohn des Joahas, des Sohnes Jehus, den König von Israel, und ließ ihm sagen: Komm, wir wollen (im Kampf) einander gegenübertreten." },
      { number: 9, text: "Doch Joasch, der König von Israel ließ dem König Amazja von Juda sagen: Der Dornstrauch auf dem Libanon ließ der Zeder auf dem Libanon sagen: Gib deine Tochter meinem Sohn zur Frau! Aber die Tiere des Libanon liefen über den Dornstrauch und zertraten ihn." },
      { number: 10, text: "Du hast Edom besiegt und bist übermütig geworden. Wahre doch deinen Ruhm und bleib zu Hause! Wozu willst du das Unglück herausfordern und zu Fall kommen, du und Juda mit dir?" },
      { number: 11, text: "Doch Amazja wollte nicht hören. Daraufhin rückte Joasch, der König von Israel, aus. Er und Amazja, der König von Juda, traten bei Bet-Schemesch, das zu Juda gehört, einander gegenüber." },
      { number: 12, text: "Die Judäer wurden von den Israeliten geschlagen und flohen zu ihren Zelten." },
      { number: 13, text: "Den König Amazja von Juda, den Sohn des Joasch, des Sohnes Ahasjas, nahm König Joasch von Israel bei Bet-Schemesch gefangen und brachte ihn nach Jerusalem. Dort riss er die Mauer der Stadt vom Efraimtor bis zum Ecktor auf einer Strecke von vierhundert Ellen nieder,", footnotes: ["und brachte ihn: Text korr. nach 2 Chr 25,23; H: und kam."] },
      { number: 14, text: "nahm alles Gold und Silber sowie alle Geräte, die sich im Haus des Herrn und in den Schatzkammern des königlichen Palastes befanden, ließ sich Geiseln stellen und kehrte nach Samaria zurück." },
      { number: 15, text: "Die übrige Geschichte des Joasch, seine Taten und Erfolge, auch wie er mit Amazja, dem König von Juda, Krieg führte, all das ist aufgezeichnet in der Chronik der Könige von Israel.", footnotes: ["(15-16) 13,12f"] },
      { number: 16, text: "Joasch entschlief zu seinen Vätern und wurde in Samaria bei den Königen von Israel begraben. Sein Sohn Jerobeam wurde König an seiner Stelle." },
      { number: 17, text: "Amazja, der Sohn des Joasch, der König von Juda, lebte nach dem Tod des Joasch, des Sohnes des Joahas, des Königs von Israel, noch fünfzehn Jahre." },
      { number: 18, text: "Die übrige Geschichte Amazjas ist aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 19, text: "Gegen ihn zettelte man in Jerusalem eine Verschwörung an. Er floh nach Lachisch; aber man sandte Verfolger hinter ihm her nach Lachisch, die ihn dort erschlugen." },
      { number: 20, text: "Man brachte ihn auf Pferden nach Jerusalem; dort wurde er bei seinen Vätern in der Davidstadt begraben." },
      { number: 21, text: "Das ganze Volk von Juda nahm nun Asarja, der damals sechzehn Jahre alt war, und machte ihn zum König anstelle seines Vaters Amazja.", footnotes: ["(21-22) 2 Chr 26,1f", "21f: V. 21 und vielleicht auch 22 gehören in den Zusammenhang von V. 14."] },
      { number: 22, text: "Er baute Elat aus, das er für Juda zurückgewonnen hatte, nachdem der König zu seinen Vätern entschlafen war." },
      { number: 23, text: "Im fünfzehnten Jahr Amazjas, des Sohnes des Joasch, des Königs von Juda, wurde Jerobeam, der Sohn des Joasch, König von Israel. Er regierte einundvierzig Jahre in Samaria", heading: "Jerobeam II. von Israel: 14,23-29" },
      { number: 24, text: "und tat, was dem Herrn missfiel; er ließ nicht von all den Sünden ab, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 25, text: "Er stellte die Grenzen Israels wieder her von Lebo-Hamat bis zum Meer der Araba, wie es der Herr, der Gott Israels, durch seinen Knecht, den Propheten Jona, den Sohn Amittais aus Gat-Hefer, vorhergesagt hatte.", footnotes: ["Zu Lebo-Hamat vgl. die Anmerkung zu 1 Kön 8,65. Das «Meer der Araba» ist das Tote Meer. Auch Am 6,13 erwähnt Eroberungen im Ostjordanland. Eine entsprechende Weissagung findet sich nicht im AT."] },
      { number: 26, text: "Denn der Herr sah die bittere Not Israels: dass bis zum letzten Mann alle dahinschwanden und dass es für Israel keinen Retter gab." },
      { number: 27, text: "Er hatte nicht im Sinn, den Namen Israels unter dem Himmel auszutilgen. Darum half er ihnen durch Jerobeam, den Sohn des Joasch." },
      { number: 28, text: "Die übrige Geschichte Jerobeams, alle seine Taten und Erfolge, wie er Krieg führte, . . . all das ist aufgezeichnet in der Chronik der Könige von Israel.", footnotes: ["H: und wie er Damaskus und Hamat für Juda in Israel zurückbrachte. Sinn unklar."] },
      { number: 29, text: "Jerobeam entschlief zu seinen Vätern, den Königen von Israel, und sein Sohn Secharja wurde König an seiner Stelle." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Im siebenundzwanzigsten Jahr Jerobeams, des Königs von Israel, wurde Asarja, der Sohn Amazjas, König von Juda.", heading: "Asarja (Usija) von Juda: 15,1-7", footnotes: ["(1-7) 2 Chr 26,3-4.16-23", "Der Name des Königs ist in beiden Formen überliefert (vgl. V. 13 und 2 Chr 26,1)."] },
      { number: 2, text: "Er war sechzehn Jahre alt, als er König wurde, und regierte zweiundfünfzig Jahre in Jerusalem. Seine Mutter hieß Jecholja und stammte aus Jerusalem.", footnotes: ["Die Zeit vom 27. Jahr Jerobeams bis zum 2. Jahr Pekachs (V. 32) umspannt nur 28 Jahre. Die hier genannte Zahl 52 setzt voraus, dass er schon 24 Jahre vor dem Tod Amazjas als Mitregent oder Stellvertreter seines Vaters König geworden war."] },
      { number: 3, text: "Genau wie sein Vater Amazja tat er, was dem Herrn gefiel." },
      { number: 4, text: "Nur die Kulthöhen verschwanden nicht; das Volk brachte noch Schlacht- und Rauchopfer auf ihnen dar." },
      { number: 5, text: "Doch der Herr schlug den König mit Aussatz. Er musste bis zu seinem Tod in einem abgesonderten Haus wohnen, während Jotam, der Sohn des Königs, Vorsteher des Palastes war und die Bürger des Landes regierte.", footnotes: ["2 Chr 26,21"] },
      { number: 6, text: "Die übrige Geschichte Asarjas und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 7, text: "Asarja entschlief zu seinen Vätern; man begrub ihn bei seinen Vätern in der Davidstadt. Sein Sohn Jotam wurde König an seiner Stelle." },
      { number: 8, text: "Im achtunddreißigsten Jahr des Königs Asarja von Juda wurde Secharja, der Sohn Jerobeams, König von Israel. Er regierte sechs Monate in Samaria.", heading: "Secharja von Israel: 15,8-12" },
      { number: 9, text: "Wie seine Väter tat er, was dem Herrn missfiel. Er ließ nicht von den Sünden ab, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 10, text: "Gegen ihn zettelte Schallum, der Sohn des Jabesch, eine Verschwörung an; er erschlug ihn in Jibleam und wurde König an seiner Stelle.", footnotes: ["Jibleam: südlich von Jesreel; in H ist der Name entstellt."] },
      { number: 11, text: "Die übrige Geschichte Secharjas ist aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 12, text: "So erfüllte sich das Wort, das der Herr zu Jehu gesprochen hatte: Nachkommen von dir sollen bis in das vierte Geschlecht auf dem Thron Israels sitzen. So ist es geschehen.", footnotes: ["10,30"] },
      { number: 13, text: "Im neununddreißigsten Jahr Usijas, des Königs von Juda, wurde Schallum, der Sohn des Jabesch, König und regierte einen Monat in Samaria.", heading: "Schallum von Israel: 15,13-16" },
      { number: 14, text: "Gegen ihn zog Menahem, der Sohn Gadis, von Tirza heran, kam nach Samaria, erschlug Schallum, den Sohn des Jabesch, in Samaria und wurde König an seiner Stelle." },
      { number: 15, text: "Die übrige Geschichte Schallums und die Verschwörung, die er angezettelt hat, sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 16, text: "Damals eroberte Menahem von Tirza aus die Stadt Tifsach und tötete alle Bewohner der Stadt und ihrer Umgebung, weil sie ihm die Tore nicht geöffnet hatten. Er eroberte sie und ließ ihren schwangeren Frauen den Leib aufschlitzen." },
      { number: 17, text: "Im neununddreißigsten Jahr Asarjas, des Königs von Juda, wurde Menahem, der Sohn Gadis, König von Israel. Er regierte zehn Jahre in Samaria", heading: "Menahem von Israel: 15,17-22" },
      { number: 18, text: "und tat, was dem Herrn missfiel; er ließ nicht von den Sünden ab, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 19, text: "In seinen Tagen kam Pul, der König von Assur, in das Land. Menahem gab ihm tausend Talente Silber, damit er ihm helfe, seine Herrschaft zu festigen.", footnotes: ["In seinen Tagen: Text korr. nach G; H: alle seine Tage (mit dem vorausgehenden Satz verbunden). - Pul ist der babylonische Thronname des assyrischen Königs Tiglat-Pileser III. (745-727 v. Chr.). Seine Annalen berichten ausführlich über seine Vasallen und erwähnen unter ihnen auch Menahem von Samaria."] },
      { number: 20, text: "Um das Geld dem König von Assur abliefern zu können, musste er den wohlhabenden Männern in Israel eine Steuer auferlegen; fünfzig Silberschekel kamen auf jeden. Daraufhin zog der König von Assur ab und blieb nicht länger im Land.", footnotes: ["Von der Steuer wurden auf diese Weise 72000 Bürger betroffen."] },
      { number: 21, text: "Die übrige Geschichte Menahems und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 22, text: "Er entschlief zu seinen Vätern und sein Sohn Pekachja wurde König an seiner Stelle." },
      { number: 23, text: "Im fünfzigsten Jahr Asarjas, des Königs von Juda, wurde Pekachja, der Sohn Menahems, König von Israel. Er regierte zwei Jahre in Samaria", heading: "Pekachja von Israel: 15,23-26" },
      { number: 24, text: "und tat, was dem Herrn missfiel; er ließ nicht von den Sünden ab, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 25, text: "Gegen ihn zettelte sein Adjutant Pekach, der Sohn Remaljas, eine Verschwörung an und erschlug ihn im Wohnturm des königlichen Palastes in Samaria . . . Er hatte fünfzig Mann aus Gilead bei sich, tötete Pekachja und wurde König an seiner Stelle.", footnotes: ["H unverständlich."] },
      { number: 26, text: "Die übrige Geschichte Pekachjas und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 27, text: "Im zweiundfünfzigsten Jahr des Königs Asarja von Juda wurde Pekach, der Sohn Remaljas, König von Israel. Er regierte zwanzig Jahre in Samaria", heading: "Pekach von Israel: 15,27-31" },
      { number: 28, text: "und tat, was dem Herrn missfiel; er ließ nicht von den Sünden ab, zu denen Jerobeam, der Sohn Nebats, Israel verführt hatte." },
      { number: 29, text: "In den Tagen Pekachs, des Königs von Israel, zog Tiglat-Pileser, der König von Assur, heran. Er eroberte Ijon, Abel-Bet-Maacha, Janoach, Kedesch, Hazor, Gilead, Galiläa, das ganze Land Naftali, und verschleppte ihre Bewohner nach Assur.", footnotes: ["Die genannten Städte liegen im Norden des Reiches Israel. Naftali ist der Landstrich westlich des Jordan zwischen dem See Gennesaret und dem Hule-See. Assyrische Texte berichten ausführlich über den Feldzug Tiglat-Pilesers."] },
      { number: 30, text: "Hoschea aber, der Sohn Elas, zettelte eine Verschwörung gegen Pekach, den Sohn Remaljas, an. Er erschlug ihn und wurde König an seiner Stelle im zwanzigsten Jahr Jotams, des Sohnes Usijas." },
      { number: 31, text: "Die übrige Geschichte Pekachs und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Israel." },
      { number: 32, text: "Im zweiten Jahr Pekachs, des Sohnes Remaljas, des Königs von Israel, wurde Jotam, der Sohn Usijas, König von Juda.", heading: "Jotam von Juda: 15,32-38", footnotes: ["(32-38) 2 Chr 27,1-9"] },
      { number: 33, text: "Er war fünfundzwanzig Jahre alt, als er König wurde, und regierte sechzehn Jahre in Jerusalem. Seine Mutter hieß Jeruscha und war eine Tochter Zadoks.", footnotes: ["In den sechzehn Jahren sind zehn Jahre enthalten, in denen Jotam für seinen erkrankten Vater die Regierungsgeschäfte führte (V. 5). Auf den Tod Usijas folgten daher nicht sechzehn, sondern nur sechs Jahre Jotams. Die Nichtbeachtung dieser Tatsache führte zu Unstimmigkeiten in den Daten Pekachs, Hoscheas und des Ahas, die auch durch nachträgliche Änderungen nicht beseitigt werden konnten."] },
      { number: 34, text: "Er tat, was dem Herrn gefiel, genau wie sein Vater Usija." },
      { number: 35, text: "Nur die Kulthöhen verschwanden nicht. Das Volk brachte noch Schlacht- und Rauchopfer auf ihnen dar. Er baute das obere Tor am Haus des Herrn." },
      { number: 36, text: "Die übrige Geschichte Jotams und seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 37, text: "In jenen Tagen begann der Herr, Rezin, den König von Aram, und Pekach, den Sohn Remaljas, gegen Juda ziehen zu lassen." },
      { number: 38, text: "Jotam aber entschlief zu seinen Vätern und wurde bei seinen Vätern in der Stadt seines Vaters David begraben. Sein Sohn Ahas wurde König an seiner Stelle." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Im siebzehnten Jahr Pekachs, des Sohnes Remaljas, wurde Ahas, der Sohn Jotams, König von Juda.", heading: "Ahas von Juda: 16,1-20", footnotes: ["(1-20) 2 Chr 28,1-27", "In Wirklichkeit ist es das siebte Jahr Pekachs."] },
      { number: 2, text: "Er war zwanzig Jahre alt, als er König wurde, und regierte sechzehn Jahre in Jerusalem. Er tat nicht wie sein Vater David, was dem Herrn, seinem Gott, gefiel,", footnotes: ["Ahas regierte nur sechs Jahre: drei gleichzeitig mit Pekach (15,27) und drei mit Hoschea (18,1)."] },
      { number: 3, text: "sondern er folgte den Wegen der Könige von Israel. Er ließ sogar seinen Sohn durch das Feuer gehen und ahmte so die Gräuel der Völker nach, die der Herr vor den Israeliten vertrieben hatte.", footnotes: ["Er ließ ihn durch das Feuer gehen: Er verbrannte ihn als Opfergabe für Baal (vgl. die Anmerkung zu Lev 18,21)."] },
      { number: 4, text: "Auf den Kulthöhen und Hügeln und unter jedem üppigen Baum brachte er Schlacht- und Rauchopfer dar.", footnotes: ["Vgl. die Anmerkung zu 1 Kön 14,23."] },
      { number: 5, text: "Damals unternahmen Rezin, der König von Aram, und Pekach, der Sohn Remaljas, der König von Israel, einen Kriegszug gegen Jerusalem. Sie schlossen Ahas ein, konnten ihn aber nicht zum Kampf zwingen.", footnotes: ["Damaskus hatte das Nordreich und andere Staaten für ein Bündnis gegen Assur gewonnen. Da Jotam und Ahas den Beitritt verweigerten, kam es zum sog. Syrisch-efraimitischen Krieg. Er gab Anlass zur Immanuel-Weissagung von Jes 7,1-17."] },
      { number: 6, text: "Zur gleichen Zeit gewann Rezin, der König von Aram, Elat für Edom zurück. Er vertrieb die Judäer aus Elat, und die Edomiter kamen und blieben dort ansässig bis zum heutigen Tag.", footnotes: ["Edom: Text korr.; H: Aram."] },
      { number: 7, text: "Ahas aber sandte Boten an Tiglat-Pileser, den König von Assur, und ließ ihm sagen: Ich bin dein Knecht und dein Sohn; zieh herauf und rette mich aus der Hand des Königs von Aram und des Königs von Israel, die mich bedrohen." },
      { number: 8, text: "Zugleich nahm Ahas das Silber und Gold, das sich im Haus des Herrn und in den Schatzkammern des königlichen Palastes befand, und sandte es als Huldigungsgeschenk an den König von Assur." },
      { number: 9, text: "Dieser hörte auf ihn, zog gegen Damaskus, nahm es ein und verschleppte seine Bewohner nach Kir; Rezin aber ließ er hinrichten." },
      { number: 10, text: "Als König Ahas zu Tiglat-Pileser, dem König von Assur, nach Damaskus kam und den Altar in Damaskus sah, schickte er dem Priester Urija ein Abbild und eine genaue Beschreibung des Altars.", footnotes: ["10-16: Ahas wollte keinen neuen Kult einführen, sondern nur die Bauweise des Altars übernehmen."] },
      { number: 11, text: "Daraufhin baute der Priester Urija einen Altar; genau nach dem Auftrag, den König Ahas von Damaskus aus erteilt hatte, ließ er ihn anfertigen, bevor der König aus Damaskus zurückkehrte." },
      { number: 12, text: "Als dann Ahas nach seiner Rückkehr aus Damaskus den Altar sah, ging er zu ihm hin, stieg hinauf" },
      { number: 13, text: "und verbrannte auf ihm sein Brand- und Speiseopfer, goss sein Trankopfer aus und besprengte den Altar mit dem Blut seiner Heilsopfer." },
      { number: 14, text: "Den bronzenen Altar aber, der zwischen dem neuen Altar und dem Tempel vor dem Herrn stand, ließ er von seinem Platz vor dem Tempel wegrücken und nördlich vom neuen Altar aufstellen." },
      { number: 15, text: "Zugleich gab er dem Priester Urija die Anweisung: Auf dem neuen großen Altar sollst du das Brandopfer am Morgen und das Speiseopfer am Abend, das Brandopfer des Königs und sein Speiseopfer sowie das Brandopfer aller Bürger des Landes, ihr Speiseopfer und ihre Trankopfer darbringen. Auf ihn sollst du auch das Blut aller Brand- und Schlachtopfer sprengen. Was aber mit dem bronzenen Altar geschehen soll, will ich noch überlegen." },
      { number: 16, text: "Der Priester Urija tat alles, was König Ahas angeordnet hatte." },
      { number: 17, text: "König Ahas ließ die Leisten an den fahrbaren Gestellen abtrennen und die Kessel aus ihnen nehmen, auch das «Meer» von den bronzenen Rindern, auf denen es ruhte, wegnehmen und auf das Steinpflaster setzen.", footnotes: ["Die bronzenen Gestelle und das «Meer» waren von Salomo für den Tempel angefertigt worden (vgl. 1 Kön 7,23-39). Ahas war vielleicht durch Not gezwungen, das entbehrliche Metall für andere Zwecke (Tribut an Assur) zu verwenden."] },
      { number: 18, text: "Ferner ließ er den überdachten Sabbatgang, den man an den Tempel angebaut hatte, und den äußeren Zugang für den König vom Haus des Herrn entfernen. Er tat dies mit Rücksicht auf den König von Assur.", footnotes: ["Sabbatgang: Übersetzung unsicher."] },
      { number: 19, text: "Die übrige Geschichte des Ahas und seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 20, text: "Ahas entschlief zu seinen Vätern und wurde bei seinen Vätern in der Davidstadt begraben. Sein Sohn Hiskija wurde König an seiner Stelle." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Im zwölften Jahr des Königs Ahas von Juda wurde Hoschea, der Sohn Elas, in Samaria König von Israel. Er regierte neun Jahre", heading: "Die Eroberung Samarias: 17,1-23" },
      { number: 2, text: "und tat, was dem Herrn missfiel, jedoch nicht in dem Maß wie die Könige von Israel, die vor ihm herrschten." },
      { number: 3, text: "Gegen ihn zog Salmanassar, der König von Assur. Hoschea musste sich ihm unterwerfen und Abgaben entrichten." },
      { number: 4, text: "Dann aber erfuhr der König von Assur, dass Hoschea an einer Verschwörung beteiligt war. Er hatte nämlich Boten nach So zum König von Ägypten gesandt und die jährliche Abgabe an den König von Assur nicht mehr geleistet. Daher ließ ihn dieser festnehmen und ins Gefängnis werfen.", footnotes: ["So: Name der Stadt Saïs, Sitz der 24. Dynastie."] },
      { number: 5, text: "Der König von Assur fiel über das ganze Land her, rückte gegen Samaria vor und belagerte es drei Jahre lang.", footnotes: ["5f: Beide Verse sind Parallelbericht zu V. 3f."] },
      { number: 6, text: "Im neunten Jahr Hoscheas eroberte er die Stadt, verschleppte die Israeliten nach Assur und siedelte sie in Halach, am Habor, einem Fluss von Gosan, und in den Städten der Meder an.", footnotes: ["Samaria wurde im Spätherbst 722 v. Chr. erobert. Die Wegführung der Bevölkerung erfolgte erst 720 durch Sargon. Die genannten Gegenden liegen im nördlichen Mesopotamien. Medien ist das Bergland südwestlich des Kaspischen Meers."] },
      { number: 7, text: "Das geschah, weil die Israeliten sich gegen den Herrn, ihren Gott, versündigten, der sie aus Ägypten, aus der Gewalt des Pharao, des Königs von Ägypten, heraufgeführt hatte. Sie verehrten fremde Götter," },
      { number: 8, text: "ahmten die Bräuche der Völker nach, die der Herr vor den Israeliten vertrieben hatte, und folgten dem Beispiel, das die Könige von Israel gaben." },
      { number: 9, text: "Gegen den Herrn, ihren Gott, ersannen die Israeliten Dinge, die nicht recht waren. Sie bauten sich Kulthöhen in allen ihren Städten, vom Wachtturm angefangen bis zur befestigten Stadt," },
      { number: 10, text: "errichteten Steinmale und Kultpfähle auf jedem hohen Hügel und unter jedem üppigen Baum." },
      { number: 11, text: "Auf allen Kulthöhen brachten sie Opfer dar wie die Völker, die der Herr vor ihnen vertrieben hatte, taten böse Dinge und erzürnten dadurch den Herrn." },
      { number: 12, text: "Sie dienten den Götzen, obwohl der Herr es ihnen verboten hatte." },
      { number: 13, text: "Der Herr warnte Israel und Juda durch alle seine Propheten, durch alle Seher: Kehrt um von euren bösen Wegen, achtet auf meine Befehle und meine Gebote genau nach dem Gesetz, das ich euren Vätern gegeben und euch durch meine Knechte, die Propheten, verkündet habe." },
      { number: 14, text: "Doch sie wollten nicht hören, sondern versteiften ihre Nacken wie ihre Väter, die nicht auf den Herrn, ihren Gott, vertrauten." },
      { number: 15, text: "Sie verwarfen seine Gebote und den Bund, den er mit ihren Vätern geschlossen hatte, und verschmähten die Warnungen, die er an sie richtete. Sie liefen nichtigen Göttern nach und wurden selbst zunichte; sie ahmten die Völker ihrer Umgebung nach, obwohl der Herr verboten hatte, ihrem Beispiel zu folgen.", footnotes: ["Jer 2,5"] },
      { number: 16, text: "Sie übertraten alle Gebote des Herrn, ihres Gottes, schufen sich Gussbilder, zwei Kälber, stellten einen Kultpfahl auf, beteten das ganze Heer des Himmels an und dienten dem Baal.", footnotes: ["Heer des Himmels: die Sterne, die von den östlichen Völkern als Götter verehrt wurden."] },
      { number: 17, text: "Ihre Söhne und Töchter ließen sie durch das Feuer gehen, trieben Wahrsagerei und Zauberei und gaben sich dazu her zu tun, was dem Herrn missfiel, und ihn zu erzürnen." },
      { number: 18, text: "Darum wurde der Herr über Israel sehr zornig. Er verstieß es von seinem Angesicht, sodass der Stamm Juda allein übrig blieb." },
      { number: 19, text: "Doch auch Juda befolgte nicht die Befehle des Herrn, seines Gottes, sondern ahmte die Bräuche nach, die Israel eingeführt hatte." },
      { number: 20, text: "Darum verwarf der Herr das ganze Geschlecht Israels. Er demütigte sie und gab sie Räubern preis; schließlich verstieß er sie von seinem Angesicht." },
      { number: 21, text: "Er hatte Israel vom Haus David losgerissen und sie hatten Jerobeam, den Sohn Nebats, als König eingesetzt. Jerobeam aber machte Israel vom Herrn abtrünnig und verführte es zu schwerer Sünde.", footnotes: ["1 Kön 11,31"] },
      { number: 22, text: "Die Israeliten begingen all die Sünden, die Jerobeam begangen hatte, und ließen nicht von ihnen ab." },
      { number: 23, text: "Schließlich verstieß der Herr Israel von sich, wie er es durch seine Knechte, die Propheten, angedroht hatte. So wanderte Israel aus seinem Land weg in die Verbannung nach Assur und blieb dort bis zum heutigen Tag." },
      { number: 24, text: "Der König von Assur brachte Leute aus Babel, Kuta, Awa, Hamat und Sefarwajim in das Land und siedelte sie anstelle der Israeliten in den Städten Samariens an. Sie nahmen Samarien in Besitz und ließen sich in seinen Städten nieder.", heading: "Die Umsiedlung der Bevölkerung: 17,24-28", footnotes: ["Die neue Bevölkerung kam aus dem Osten und Norden."] },
      { number: 25, text: "In der ersten Zeit, in der sie dort wohnten, erwiesen sie dem Herrn keine Verehrung. Er schickte deshalb Löwen unter sie, die manche von ihnen töteten." },
      { number: 26, text: "Da meldete man dem König von Assur: Die Völker, die du weggeführt und in den Städten Samariens angesiedelt hast, wissen nicht, wie man den Landesgott verehren soll. Er hat daher Löwen unter sie gesandt, von denen sie getötet werden, weil sie nicht wissen, wie man den Landesgott verehren soll." },
      { number: 27, text: "Da befahl der König von Assur: Bringt einen von den Priestern zurück, die ihr von dort weggeführt habt. Er soll zu ihnen gehen, bei ihnen wohnen und sie belehren, wie man den Landesgott verehren soll.", footnotes: ["Er soll zu ihnen gehen: H: Sie sollen . . ."] },
      { number: 28, text: "So kam einer von den Priestern zurück, die man aus Samarien weggeführt hatte. Er ließ sich in Bet-El nieder und belehrte sie, wie man den Herrn verehren müsse." },
      { number: 29, text: "Jedes Volk aber schuf sich seine eigenen Götter und stellte sie in den Höhentempeln auf, die von den Bewohnern Samariens erbaut worden waren. Jedes Volk tat dies in der Stadt, in der es wohnte.", heading: "Die Religion der neuen Bevölkerung: 17,29-41" },
      { number: 30, text: "Die Leute aus Babel machten sich Bilder Sukkot-Benots. Die Ansiedler aus Kuta stellten Bilder Nergals her. Jene aus Hamat schufen Bilder Aschimas.", footnotes: ["30f: Die Aufzählung nennt Namen von weniger oder nicht bekannten Gottheiten, die vom Volk mehr verehrt wurden als die Staatsgottheiten. Nergal ist der Gott der Unterwelt, der Sonnenhitze und der Pest."] },
      { number: 31, text: "Die Awiter fertigten Bilder des Nibhas und des Tartak an. Die, die aus Sefarwajim gekommen waren, verbrannten ihre Kinder zur Ehre Adrammelechs und Anammelechs, der Götter von Sefarwajim." },
      { number: 32, text: "Gleichzeitig verehrten sie aber auch den Herrn. Auch setzten sie aus ihren eigenen Reihen Priester für die Kulthöhen ein, die für sie in den Höhentempeln Dienst taten." },
      { number: 33, text: "So verehrten sie den Herrn und dienten daneben ihren Göttern nach den Bräuchen der Völker, aus denen man sie weggeführt hatte." },
      { number: 34, text: "Bis zum heutigen Tag handeln sie nach ihren früheren Bräuchen. Sie fürchten den Herrn nicht und halten sich nicht an die Satzungen und Bräuche, an das Gesetz und die Gebote, auf die der Herr die Nachkommen Jakobs, dem er den Namen Israel gegeben hatte, verpflichtet hat.", footnotes: ["34b-40a: Einschaltung, die die Schuld der im Land verbliebenen Israeliten hervorhebt."] },
      { number: 35, text: "Der Herr hat nämlich mit ihnen einen Bund geschlossen und ihnen befohlen: Ihr dürft keine fremden Götter verehren, sie nicht anbeten, ihnen nicht dienen und ihnen keine Opfer darbringen." },
      { number: 36, text: "Den Herrn allein, der euch mit großer Kraft und hoch erhobenem Arm aus Ägypten heraufgeführt hat, sollt ihr fürchten und anbeten und ihm eure Opfer darbringen." },
      { number: 37, text: "Die Satzungen und Bräuche, das Gesetz und die Gebote, die er für euch niedergeschrieben hat, sollt ihr befolgen und alle Tage erfüllen. Fremde Götter aber dürft ihr nicht verehren." },
      { number: 38, text: "Ihr sollt den Bund, den er mit euch geschlossen hat, nicht vergessen und fremde Götter nicht verehren." },
      { number: 39, text: "Den Herrn allein, euren Gott, sollt ihr fürchten. Er wird euch aus der Gewalt all eurer Feinde erretten." },
      { number: 40, text: "Doch sie wollten nicht hören, sondern sie handelten, wie sie es immer schon gewohnt waren." },
      { number: 41, text: "Sie verehren den Herrn und dienen zugleich ihren Götzen. Was ihre Väter getan haben, das tun auch ihre Kinder und Kindeskinder bis zum heutigen Tag." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Im dritten Jahr Hoscheas, des Sohnes Elas, des Königs von Israel, wurde Hiskija, der Sohn des Ahas, König von Juda.", heading: "Die weitere Geschichte des Reiches Juda: 18,1 - 25,30 | Hiskija: 18,1-12", footnotes: ["(1-3) 2 Chr 29,1f"] },
      { number: 2, text: "Er war fünfundzwanzig Jahre alt, als er König wurde, und regierte neunundzwanzig Jahre in Jerusalem. Seine Mutter hieß Abi und war eine Tochter Secharjas." },
      { number: 3, text: "Genau wie sein Vater David tat er, was dem Herrn gefiel." },
      { number: 4, text: "Er schaffte die Kulthöhen ab, zerbrach die Steinmale, zerstörte den Kultpfahl und zerschlug die Kupferschlange, die Mose angefertigt hatte und der die Israeliten bis zu jener Zeit Rauchopfer darbrachten - man nannte sie Nehuschtan (Kupferbild).", footnotes: ["Zum ersten Mal wird berichtet, dass ein judäischer König die Kulthöhen beseitigt. - Zur Kupferschlange vgl. Num 21,4-9 und die Anmerkung zu Num 21,9."] },
      { number: 5, text: "Er setzte sein Vertrauen auf den Herrn, den Gott Israels. Unter allen Königen Judas, die nach ihm kamen oder vor ihm lebten, war keiner wie er." },
      { number: 6, text: "Er hing dem Herrn an, ohne von ihm abzuweichen, und hielt die Gebote, die der Herr dem Mose gegeben hatte." },
      { number: 7, text: "Daher war der Herr mit ihm; in allem, was er unternahm, hatte er Erfolg. So fiel er vom König von Assur ab und war ihm nicht länger untertan." },
      { number: 8, text: "Auch schlug er die Philister bis Gaza und den Umkreis dieser Stadt, vom Wachtturm bis zur befestigten Stadt." },
      { number: 9, text: "Im vierten Jahr des Königs Hiskija, das ist im siebten Jahr Hoscheas, des Sohnes Elas, des Königs von Israel, zog Salmanassar, der König von Assur, gegen Samaria und belagerte es.", footnotes: ["(9-11) 17,5f"] },
      { number: 10, text: "Nach drei Jahren, das ist im sechsten Jahr Hiskijas und im neunten Jahr Hoscheas, des Königs von Israel, wurde Samaria erobert." },
      { number: 11, text: "Der König von Assur verschleppte die Israeliten nach Assur und brachte sie nach Halach, an den Habor, einen Fluss von Gosan, und in die Städte der Meder." },
      { number: 12, text: "Denn sie hatten auf die Stimme des Herrn, ihres Gottes, nicht gehört, seinen Bund gebrochen und die Gebote, die Mose, der Knecht des Herrn, verkündet hatte, übertreten und sie nicht befolgt." },
      { number: 13, text: "Im vierzehnten Jahr des Königs Hiskija zog Sanherib, der König von Assur, gegen alle befestigten Städte Judas und nahm sie ein.", heading: "Sanheribs Feldzug gegen Jerusalem: 18,13 - 19,37 | Erste Gesandtschaft Sanheribs: 18,13-37", footnotes: ["(13-37) Jes 36,1-22; 2 Chr 32,1-19", "13-16: Nach assyrischen Quellen fand der Feldzug 701 v. Chr. statt; das ist das 26. Jahr Hiskijas. Das 14. Jahr ist aus 20,6 errechnet."] },
      { number: 14, text: "Hiskija aber, der König von Juda, schickte Boten an den König von Assur nach Lachisch und ließ ihm sagen: Ich habe gefehlt. Lass ab von mir! Alles, was du mir auferlegst, will ich tragen. Der König von Assur verlangte von Hiskija, dem König von Juda, dreihundert Talente Silber und dreißig Talente Gold." },
      { number: 15, text: "Hiskija musste alles Geld abliefern, das sich im Haus des Herrn und in den Schatzkammern des königlichen Palastes befand." },
      { number: 16, text: "Damals ließ Hiskija, der König von Juda, die Türen am Tempel des Herrn und die Pfosten, die er mit Gold und Silber überzogen hatte, zerschlagen und lieferte das Metall an den König von Assur.", footnotes: ["Ähnliches geschah unter Ahas (vgl. 16,17)."] },
      { number: 17, text: "Doch der König von Assur sandte den Tartan, den Rabsaris und den Rabschake mit einer großen Streitmacht von Lachisch aus gegen König Hiskija. Sie zogen nach Jerusalem hinauf, stellten sich an der Wasserleitung des oberen Teiches auf, der an der Walkerfeldstraße liegt,", footnotes: ["Jes 7,3", "17-19,36: Die zeitliche Einordnung des hier beschriebenen Vorgangs ist schwer möglich. Tartan, Rabsaris und Rabschake: assyrische Beamtenbezeichnungen, etwa Oberfeldherr, Oberkämmerer und Obermundschenk. - In Lachisch, südwestlich von Jerusalem, war das Hauptquartier des assyrischen Heeres. Die Eroberung der Stadt ist auf einem Relief in Ninive dargestellt."] },
      { number: 18, text: "und ließen den König rufen. Der Palastvorsteher Eljakim, der Sohn Hilkijas, der Staatsschreiber Schebna und Joach, der Sohn Asafs, der Sprecher des Königs, gingen zu ihnen hinaus." },
      { number: 19, text: "Der Rabschake sagte zu ihnen: Sagt zu Hiskija: So spricht der Großkönig, der König von Assur: Worauf vertraust du denn, dass du dich so sicher fühlst?" },
      { number: 20, text: "Du glaubst wohl, bloßes Gerede sei im Krieg schon Rat und Stärke? Auf wen vertraust du also, dass du von mir abgefallen bist?" },
      { number: 21, text: "Du vertraust gewiss auf Ägypten, dieses geknickte Schilfrohr, das jeden, der sich darauf stützt, in die Hand sticht und sie durchbohrt. Denn so macht es der Pharao, der König von Ägypten, mit allen, die ihm vertrauen.", footnotes: ["Der Zug Sanheribs war durch ein Bündnis der westlichen Vasallen mit Ägypten veranlasst."] },
      { number: 22, text: "Wenn ihr aber zu mir sagt: Wir vertrauen auf Jahwe, unseren Gott!, dann bedenkt: Ist nicht gerade er der Gott, dessen Kulthöhen und Altäre Hiskija beseitigt hat? Hat nicht Hiskija in Juda und Jerusalem angeordnet: Nur vor diesem Altar in Jerusalem dürft ihr euch niederwerfen?" },
      { number: 23, text: "Geh doch mit meinem Herrn, dem König von Assur, eine Wette ein! Ich gebe dir zweitausend Pferde: Kannst du die Reiter für sie stellen?" },
      { number: 24, text: "Wie willst du auch nur einen einzigen Statthalter meines Herrn in die Flucht schlagen, und wäre es der unbedeutendste seiner Knechte? Du vertraust ja nur auf Ägypten, auf seine Wagen und deren Besatzung." },
      { number: 25, text: "Außerdem: Bin ich denn gegen den Willen Jahwes heraufgezogen, um dieses Land zu verwüsten? Jahwe selbst hat mir befohlen: Zieh gegen dieses Land, und verwüste es!" },
      { number: 26, text: "Da sagten Eljakim, der Sohn Hilkijas, sowie Schebna und Joach zu dem Rabschake: Sprich doch aramäisch mit deinen Knechten; wir verstehen es. Sprich vor den Ohren des Volkes, das auf der Mauer steht, nicht judäisch mit uns!", footnotes: ["Das Aramäische begann vom 8. bis 6. Jh. zur Sprache des Handels und der Diplomatie zu werden. Der Text setzt voraus, dass es in Jerusalem von den höheren Beamten, nicht aber vom Volk verstanden wurde."] },
      { number: 27, text: "Der Rabschake antwortete ihnen: Hat mich mein Herr etwa beauftragt, das alles nur zu deinem Herrn und zu dir zu sagen und nicht vielmehr zu all den Männern, die auf der Mauer sitzen und ihren eigenen Kot essen und ihren Harn trinken wie ihr?" },
      { number: 28, text: "Dann trat der Rabschake vor und rief laut auf Judäisch: Hört die Worte des Großkönigs, des Königs von Assur!" },
      { number: 29, text: "So spricht der König: Lasst euch nicht von Hiskija betören; denn er kann euch nicht aus meiner Hand retten.", footnotes: ["aus meiner Hand: Text korr.; H: aus seiner Hand."] },
      { number: 30, text: "Er soll euch nicht verleiten, auf Jahwe zu vertrauen, und sagen: Jahwe wird uns sicher retten und diese Stadt wird dem König von Assur nicht in die Hände fallen." },
      { number: 31, text: "Hört nicht auf Hiskija! Denn so spricht der König von Assur: Trefft mit mir ein Abkommen und ergebt euch! Dann kann jeder von euch von seinem Weinstock und von seinem Feigenbaum essen und Wasser aus seiner Zisterne trinken," },
      { number: 32, text: "bis ich komme und euch in ein Land bringe, das eurem Land gleicht: in ein Land voll Getreide und Most, ein Land voll Brot und Wein, ein Land mit Ölbäumen und Honig. So werdet ihr am Leben bleiben und nicht umkommen. Hört nicht auf Hiskija; denn er führt euch in die Irre, wenn er sagt: Jahwe wird uns retten." },
      { number: 33, text: "Hat denn einer von den Göttern der anderen Völker sein Land vor dem König von Assur gerettet?" },
      { number: 34, text: "Wo sind die Götter von Hamat und Arpad? Wo sind die Götter von Sefarwajim, Hena und Awa? Haben sie etwa Samaria vor mir gerettet?" },
      { number: 35, text: "Wer von all den Göttern der anderen Länder hat sein Land vor mir gerettet? Wie sollte dann Jahwe Jerusalem vor mir retten?" },
      { number: 36, text: "Das Volk aber schwieg und gab ihm keine Antwort; denn der König hatte befohlen: Ihr dürft ihm nicht antworten." },
      { number: 37, text: "Der Palastvorsteher Eljakim, der Sohn Hilkijas, der Staatsschreiber Schebna und Joach, der Sohn Asafs, der Sprecher des Königs, zerrissen ihre Kleider, gingen zu Hiskija und berichteten ihm, was der Rabschake gesagt hatte." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Als König Hiskija das hörte, zerriss er seine Kleider, legte ein Trauergewand an und ging in das Haus des Herrn.", heading: "Die Reaktion des Königs Hiskija: 19,1-7", footnotes: ["(1-7) Jes 37,1-7"] },
      { number: 2, text: "Dann sandte er den Palastvorsteher Eljakim, den Staatsschreiber Schebna und die Ältesten der Priester in Trauergewändern zu dem Propheten Jesaja, dem Sohn des Amoz." },
      { number: 3, text: "Sie sagten zu ihm: So spricht Hiskija: Heute ist ein Tag der Not, der Strafe und der Schande. Die Kinder sind bis an die Öffnung des Mutterschoßes gelangt, doch den Frauen fehlt die Kraft zum Gebären.", footnotes: ["Hos 13,13"] },
      { number: 4, text: "Aber vielleicht hört der Herr, dein Gott, alle Worte des Rabschake, den sein Herr, der König von Assur, hergesandt hat, damit er den lebendigen Gott beschimpft, und vielleicht schickt der Herr, dein Gott, eine Strafe für die Worte, die er gehört hat. Darum bete für den Rest, der noch übrig ist." },
      { number: 5, text: "Jesaja antwortete den Abgesandten des Königs Hiskija, die zu ihm gekommen waren:" },
      { number: 6, text: "Sagt zu eurem Herrn Folgendes: So spricht der Herr: Fürchte dich nicht wegen der Worte, die du gehört hast und mit denen die Knechte des Königs von Assur mich verhöhnt haben." },
      { number: 7, text: "Seht, ich lege einen Geist in ihn, sodass er ein Gerücht hört und in sein Land zurückkehrt; dort bringe ich ihn durch das Schwert zu Fall." },
      { number: 8, text: "Der Rabschake trat den Rückweg an und fand den König von Assur im Kampf gegen die Stadt Libna. Sanherib war inzwischen, wie der Rabschake gehört hatte, von Lachisch abgezogen.", heading: "Die zweite Gesandtschaft Sanheribs: 19,8-13", footnotes: ["(8-13) Jes 37,8-13"] },
      { number: 9, text: "Dann erfuhr Sanherib, dass Tirhaka, der König von Kusch, zum Kampf gegen ihn heranzog. Er schickte wiederum Boten zu Hiskija mit dem Auftrag:", footnotes: ["Tirhaka, der 690-664 v. Chr. als dritter Pharao der 25., nubischen Dynastie regierte, war 701 für seinen Bruder Schebitku Oberbefehlshaber des ägyptischen Heeres in Palästina."] },
      { number: 10, text: "So sollt ihr zu Hiskija, dem König von Juda, sagen: Lass dir nicht von deinem Gott, auf den du vertraust, einreden, Jerusalem werde dem König von Assur nicht in die Hände fallen." },
      { number: 11, text: "Du hast doch gehört, was die Könige von Assur mit allen anderen Ländern gemacht haben. Sie haben sie dem Untergang geweiht. Und du meinst, du wirst gerettet?" },
      { number: 12, text: "Sind denn die Völker, die von meinen Vätern vernichtet wurden, von ihren Göttern gerettet worden, die Völker von Gosan, Haran und Rezef, die Söhne von Eden, die in Telassar wohnten?" },
      { number: 13, text: "Wo ist der König von Hamat, der König von Arpad, der König der Stadt Sefarwajim, wo sind die Könige von Hena und Awa?" },
      { number: 14, text: "Hiskija nahm das Schreiben von den Boten in Empfang und las es. Dann ging er zum Haus des Herrn hinauf, breitete das Schreiben vor dem Herrn aus", heading: "Das Gebet des Königs: 19,14-19", footnotes: ["(14-19) Jes 37,14-20"] },
      { number: 15, text: "und betete vor dem Herrn; er sagte: Herr, Gott Israels, der über den Kerubim thront, du allein bist der Gott aller Reiche der Erde. Du hast den Himmel und die Erde gemacht." },
      { number: 16, text: "Wende mir dein Ohr zu, Herr, und höre! Öffne, Herr, deine Augen und sieh her! Hör alles, was Sanherib sagt, der seinen Boten hergesandt hat, um den lebendigen Gott zu verhöhnen." },
      { number: 17, text: "Es ist wahr, Herr, die Könige von Assur haben die Völker vernichtet, ihre Länder verwüstet" },
      { number: 18, text: "und ihre Götter ins Feuer geworfen. Aber das waren keine Götter, sondern Werke von Menschenhand, aus Holz und Stein; darum konnte man sie vernichten." },
      { number: 19, text: "Nun aber, Herr, unser Gott, rette uns aus seiner Hand, damit alle Reiche der Erde erkennen, dass du, Jahwe, Gott bist, du allein." },
      { number: 20, text: "Jesaja, der Sohn des Amoz, schickte zu Hiskija (einen Boten) und ließ ihm sagen: So spricht der Herr, der Gott Israels: Ich habe gehört, wie du wegen des Königs Sanherib von Assur zu mir gebetet hast.", heading: "Die Verheißung der göttlichen Hilfe durch Jesaja: 19,20-34", footnotes: ["(20-34) Jes 37,21-35"] },
      { number: 21, text: "Das ist das Wort des Herrn gegen ihn: Dich verachtet, dich verspottet / die Jungfrau, die Tochter Zion. Die Tochter Jerusalem / schüttelt spöttisch den Kopf über dich.", footnotes: ["«Jungfrau» und «Tochter» sind bei den Propheten Bezeichnungen für die Bevölkerung von Ländern oder Städten."] },
      { number: 22, text: "Wen hast du beschimpft und verhöhnt, / gegen wen die Stimme erhoben, / auf wen voll Hochmut herabgeblickt? / Auf den Heiligen Israels." },
      { number: 23, text: "Durch deine Gesandten hast du den Herrn verhöhnt; / du hast gesagt: / Mit meinen zahlreichen Wagen fuhr ich / auf die Höhen der Berge, / in die fernsten Winkel des Libanon. / Ich fällte seine hohen Zedern, / seine schönsten Zypressen, / kam bis zu seinen entlegensten Hütten, / in das Dickicht seiner Wälder." },
      { number: 24, text: "Ich habe Brunnen gegraben / und fremdes Wasser getrunken. / Ich ließ unter dem Schritt meiner Füße / alle Ströme Ägyptens vertrocknen." },
      { number: 25, text: "Hast du nicht gehört: / Schon vor langer Zeit habe ich es so gefügt, / seit den Tagen der Vorzeit habe ich es so geplant; / jetzt ließ ich es kommen. / So konntest du befestigte Städte zerstören / und in Trümmer verwandeln." },
      { number: 26, text: "Ihre Bewohner waren machtlos, / in Schrecken und Schande gestoßen. / Sie glichen den Pflanzen auf dem Feld / und dem frischen Grün, / dem Gras auf den Dächern, / das im Ostwind verdorrt.", footnotes: ["Ps 129,6", "Ostwind: Text korr. nach dem Jesaja-Text von Qumran."] },
      { number: 27, text: "Ich weiß, ob du ruhst, ob du gehst oder kommst, / ob du dich gegen mich auflehnst." },
      { number: 28, text: "Weil du gegen mich wütest / und dein Lärm meine Ohren erreicht hat, / ziehe ich dir einen Ring durch die Nase / und lege dir einen Zaum in das Maul. / Auf dem Weg, auf dem du herankamst, / treibe ich dich wieder zurück.", footnotes: ["Ez 38,4", "dein Lärm: Text sinngemäß korr.; H: deine Sorglosigkeit."] },
      { number: 29, text: "Und das soll für dich (Hiskija) ein Vorzeichen sein: In diesem Jahr isst man, was von selbst nachwächst, im nächsten Jahr, was wild wächst; im dritten Jahr aber sollt ihr wieder säen und ernten, die Weinberge bepflanzen und ihre Früchte genießen.", footnotes: ["29f: Der Spruch setzt voraus, dass zwei Jahre hindurch keine Aussaat, aber eine kärgliche Ernte möglich ist."] },
      { number: 30, text: "Wer vom Haus Juda entronnen und übrig geblieben ist, wird wieder unten Wurzeln treiben und oben Frucht tragen.", footnotes: ["Ps 80,10"] },
      { number: 31, text: "Denn von Jerusalem wird ein Rest ausziehen, vom Berg Zion ziehen die Geretteten hinaus. Der leidenschaftliche Eifer des Herrn wird das vollbringen." },
      { number: 32, text: "Darum - so spricht der Herr über den König von Assur: Er wird nicht in diese Stadt eindringen; er wird keinen einzigen Pfeil hineinschießen, er wird nicht unter dem Schutz seines Schildes gegen sie anrennen und keinen Damm gegen sie aufschütten." },
      { number: 33, text: "Auf dem Weg, auf dem er gekommen ist, wird er wieder zurückkehren. Aber in diese Stadt wird er nicht eindringen - Spruch des Herrn." },
      { number: 34, text: "Ich werde diese Stadt beschützen und retten, um meinetwillen und um meines Knechtes David willen." },
      { number: 35, text: "In jener Nacht zog der Engel des Herrn aus und erschlug im Lager der Assyrer hundertfünfundachtzigtausend Mann. Als man am nächsten Morgen aufstand, fand man sie alle als Leichen.", heading: "Die Rettung der Stadt: 19,35-37", footnotes: ["(35-37) Jes 37,36-38"] },
      { number: 36, text: "Da brach Sanherib, der König von Assur, auf und kehrte in sein Land zurück. Er blieb in Ninive." },
      { number: 37, text: "Als er eines Tages im Tempel seines Gottes Nisroch betete, erschlugen ihn seine Söhne Adrammelech und Sarezer mit dem Schwert. Darauf mussten sie in das Land Ararat fliehen und Sanheribs Sohn Asarhaddon wurde König an seiner Stelle.", footnotes: ["seine Söhne: ergänzt nach Jes 37,38."] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "¥In jenen Tagen wurde Hiskija schwer krank und war dem Tod nahe. Der Prophet Jesaja, der Sohn des Amoz, kam zu ihm und sagte: So spricht der Herr: Bestell dein Haus; denn du wirst sterben, du wirst nicht am Leben bleiben.", heading: "Die Krankheit Hiskijas: 20,1-11", footnotes: ["(1-11) Jes 38,1-8; 2 Chr 32,24-26"] },
      { number: 2, text: "Da drehte sich Hiskija mit dem Gesicht zur Wand und betete zum Herrn:" },
      { number: 3, text: "Ach, Herr, denk daran, dass ich mein Leben lang treu und mit aufrichtigem Herzen meinen Weg vor deinen Augen gegangen bin und dass ich immer getan habe, was dir gefällt. Und Hiskija begann laut zu weinen." },
      { number: 4, text: "Jesaja hatte aber die innere Stadt noch nicht verlassen, als das Wort des Herrn an ihn erging:" },
      { number: 5, text: "Kehr um und sag zu Hiskija, dem Fürsten meines Volkes: So spricht der Herr, der Gott deines Vaters David: Ich habe dein Gebet gehört und deine Tränen gesehen. Nun heile ich dich. Übermorgen wirst du zum Haus des Herrn hinaufgehen;" },
      { number: 6, text: "zu deiner Lebenszeit will ich noch fünfzehn Jahre hinzufügen. Und ich will dich und diese Stadt aus der Hand des Königs von Assur retten und diese Stadt beschützen, um meinetwillen und um meines Knechtes David willen." },
      { number: 8, text: "Hiskija aber fragte Jesaja: Was ist das Zeichen dafür, dass der Herr mich heilen wird und ich übermorgen zum Haus des Herrn hinaufgehen werde?", footnotes: ["V. 7 ist wegen des Zusammenhangs nach V. 11 eingereiht."] },
      { number: 9, text: "Jesaja antwortete: Das soll für dich das Zeichen des Herrn sein, dass der Herr sein Versprechen halten wird: Soll der Schatten zehn Stufen weiter abwärts oder zehn Stufen rückwärts gehen?", footnotes: ["soll der Schatten: Text korr. nach G. Es kann sich um die Beobachtung des Schattens an einer Sonnenuhr oder an den Stufen der Treppe handeln."] },
      { number: 10, text: "Hiskija erwiderte: Für den Schatten ist es ein Leichtes, zehn Stufen weiter abwärts zu gehen. Nein, er soll zehn Stufen rückwärts gehen." },
      { number: 11, text: "Da rief der Prophet Jesaja zum Herrn und dieser ließ den Schatten die zehn Stufen zurückgehen, die er auf den Stufen des Ahas bereits herabgestiegen war." },
      { number: 7, text: "Darauf sagte Jesaja: Holt einen Feigenbrei! Man holte ihn, strich ihn auf das Geschwür und der König wurde gesund." },
      { number: 12, text: "Damals sandte Merodach-Baladan, der Sohn Baladans, der König von Babel, einen Brief und Geschenke an Hiskija; denn er hatte von seiner Krankheit gehört.", heading: "Die Gesandtschaft aus Babel: 20,12-21", footnotes: ["(12-19) Jes 39,1-8", "Merodach: nach G und Jes 39,1; H: Berodach."] },
      { number: 13, text: "Hiskija freute sich darüber und zeigte den Gesandten sein ganzes Schatzhaus, das Silber und das Gold, die Vorräte an Balsam und feinem Öl, sein Waffenlager und alle anderen Schätze, die er besaß. Es gab nichts in seinem Haus und in seinem Herrschaftsbereich, das er ihnen nicht gezeigt hätte.", footnotes: ["freute sich: Text korr. nach G und Jes 39,2; H: hörte davon. Merodach-Baladan hatte sich im Kampf gegen Assur zum König von Babylon gemacht und suchte Bundesgenossen. Hiskija scheint bereit gewesen zu sein und zeigte seine Schätze, um sich als leistungsfähigen Helfer zu empfehlen. Jesaja hatte wiederholt vor solchen Bündnissen gewarnt."] },
      { number: 14, text: "Danach kam der Prophet Jesaja zu König Hiskija und fragte ihn: Was haben diese Männer gesagt? Woher sind sie gekommen? Hiskija antwortete: Sie sind aus einem fernen Land, aus Babel, gekommen." },
      { number: 15, text: "Er fragte weiter: Was haben sie in deinem Haus gesehen? Hiskija antwortete: Sie haben alles gesehen, was in meinem Haus ist. Es gibt nichts in meinen Schatzkammern, das ich ihnen nicht gezeigt hätte." },
      { number: 16, text: "Da sagte Jesaja zu Hiskija: Höre das Wort des Herrn:" },
      { number: 17, text: "Es werden Tage kommen, an denen man alles, was in deinem Haus ist, alles, was deine Väter bis zum heutigen Tag angesammelt haben, nach Babel bringt. Nichts wird übrig bleiben, spricht der Herr." },
      { number: 18, text: "Auch von deinen eigenen Söhnen, die du noch bekommen wirst, wird man einige mitnehmen und sie werden als Kämmerer im Palast des Königs von Babel dienen müssen." },
      { number: 19, text: "Hiskija sagte zu Jesaja: Das Wort des Herrn, das du mir gesagt hast, ist gut. Und er dachte: Wenn nur zu meinen Lebzeiten noch Friede und Sicherheit herrschen." },
      { number: 20, text: "Die übrige Geschichte Hiskijas und alle seine Erfolge, wie er den Teich und die Wasserleitung angelegt und das Wasser in die Stadt geleitet hat, das alles ist aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 21, text: "Hiskija entschlief zu seinen Vätern und sein Sohn Manasse wurde König an seiner Stelle." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Manasse war zwölf Jahre alt, als er König wurde, und regierte fünfundfünfzig Jahre in Jerusalem. Seine Mutter hieß Hefzi-Bah.", heading: "Manasse: 21,1-18", footnotes: ["(1-18) 2 Chr 33,1-20"] },
      { number: 2, text: "Er tat, was dem Herrn missfiel, und ahmte die Gräuel der Völker nach, die der Herr vor den Israeliten vertrieben hatte." },
      { number: 3, text: "Er baute die Kulthöhen wieder auf, die sein Vater Hiskija zerstört hatte, errichtete Altäre für den Baal, ließ einen Kultpfahl anfertigen, wie es schon Ahab, der König von Israel, getan hatte, warf sich vor dem ganzen Heer des Himmels nieder und diente ihm.", footnotes: ["1 Kön 16,33", "Heer des Himmels: vgl. die Anmerkung zu 17,16."] },
      { number: 4, text: "Auch baute er Altäre im Haus des Herrn, obwohl der Herr gesagt hatte: Auf Jerusalem will ich meinen Namen legen.", footnotes: ["1 Kön 8,16"] },
      { number: 5, text: "In den beiden Höfen des Tempels baute er Altäre für das ganze Heer des Himmels." },
      { number: 6, text: "Er ließ seinen Sohn durch das Feuer gehen, trieb Zauberei und Wahrsagerei, bestellte Totenbeschwörer und Zeichendeuter. So tat er vieles, was dem Herrn missfiel und ihn erzürnte." },
      { number: 7, text: "Er brachte auch den Kultpfahl, den er hatte anfertigen lassen, in das Haus, von dem der Herr zu David und dessen Sohn Salomo gesagt hatte: Auf dieses Haus und auf Jerusalem, das ich aus allen Stämmen Israels auserwählt habe, will ich meinen Namen auf ewig legen." },
      { number: 8, text: "Ich werde Israels Fuß nicht mehr unstet außerhalb des Landes wandern lassen, das ich ihren Vätern gegeben habe, wenn sie nur alles befolgen, was ich ihnen geboten habe, und mein ganzes Gesetz einhalten, das ihnen mein Knecht Mose gegeben hat.", footnotes: ["2 Sam 7,10"] },
      { number: 9, text: "Doch sie wollten nicht hören und Manasse verführte sie, noch Schlimmeres zu tun als die Völker, die der Herr vor den Augen der Israeliten vernichtet hatte." },
      { number: 10, text: "Da ließ der Herr durch seine Knechte, die Propheten, sagen:" },
      { number: 11, text: "Weil Manasse, der König von Juda, diese Gräuel verübt und noch Schlimmeres getrieben hat als die Amoriter vor ihm, weil er auch Juda durch seine Götzen zur Sünde verführt hat," },
      { number: 12, text: "darum - so spricht der Herr, der Gott Israels: Ich bringe Unheil über Jerusalem und Juda, sodass jedem, der davon hört, beide Ohren gellen werden." },
      { number: 13, text: "Ich will an Jerusalem die Messschnur Samarias und die Waage des Hauses Ahab anlegen und Jerusalem auswischen, wie man eine Schüssel auswischt und dann umdreht." },
      { number: 14, text: "Den Rest meines Erbbesitzes will ich preisgeben und den Feinden ausliefern. So wird mein Volk ein Raub und eine Beute all seiner Feinde werden;" },
      { number: 15, text: "denn es hat getan, was mir missfällt, und mich erzürnt seit dem Tag, da seine Väter aus Ägypten zogen, bis zum heutigen Tag." },
      { number: 16, text: "Manasse vergoss unschuldiges Blut in Strömen, bis er Jerusalem von einem Ende zum andern damit anfüllte; er verführte Juda zur Sünde, sodass es tat, was dem Herrn missfiel." },
      { number: 17, text: "Die übrige Geschichte Manasses, alle seine Taten und die Sünden, die er beging, sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 18, text: "Manasse entschlief zu seinen Vätern und wurde im Garten seines Hauses, im Garten Usas, begraben. Sein Sohn Amon wurde König an seiner Stelle.", footnotes: ["2 Chr 33,20"] },
      { number: 19, text: "Amon war zweiundzwanzig Jahre alt, als er König wurde, und regierte zwei Jahre in Jerusalem. Seine Mutter hieß Meschullemet und war eine Tochter des Haruz aus Jotba.", heading: "Amon: 21,19-26", footnotes: ["(19-24) 2 Chr 33,21-25"] },
      { number: 20, text: "Wie sein Vater Manasse tat er, was dem Herrn missfiel," },
      { number: 21, text: "und folgte ganz den Wegen, die sein Vater gegangen war. Er diente den Götzen, die sein Vater verehrt hatte, und warf sich vor ihnen nieder." },
      { number: 22, text: "Er verließ den Herrn, den Gott seiner Väter, und hielt sich nicht an die Wege des Herrn." },
      { number: 23, text: "Gegen ihn zettelten seine Diener eine Verschwörung an und töteten ihn in seinem Haus." },
      { number: 24, text: "Doch die Bürger des Landes erschlugen alle, die sich gegen den König Amon verschworen hatten, und machten seinen Sohn Joschija zum König an seiner Stelle." },
      { number: 25, text: "Die übrige Geschichte Amons, seine Taten, sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 26, text: "Man begrub ihn in seinem Grab im Garten Usas und sein Sohn Joschija wurde König an seiner Stelle." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Joschija war acht Jahre alt, als er König wurde, und regierte einunddreißig Jahre in Jerusalem. Seine Mutter hieß Jedida und war eine Tochter Adajas aus Bozkat.", heading: "Joschija, Einleitung: 22,1-2", footnotes: ["(1-2) 2 Chr 34,1-2"] },
      { number: 2, text: "Er tat, was dem Herrn gefiel, und folgte ganz den Wegen seines Vaters David, ohne nach rechts oder links abzuweichen." },
      { number: 3, text: "Im achtzehnten Jahr seiner Regierung sandte König Joschija den Staatsschreiber Schafan, den Sohn Azaljas, des Sohnes Meschullams, in das Haus des Herrn mit dem Auftrag:", heading: "Die Auffindung des Gesetzbuches: 22,3-20", footnotes: ["(3-20) 2 Chr 34,8-28", "3f: Es ist das Jahr 623/22 v. Chr. Ähnlich wurde unter Joasch Geld zur Instandsetzung des Tempels beschafft (vgl. 12,10-16)."] },
      { number: 4, text: "Geh zum Hohenpriester Hilkija! Er soll das Geld ausschütten, das in das Haus des Herrn gebracht worden ist und das die Wächter an den Schwellen vom Volk gesammelt haben.", footnotes: ["ausschütten: korr. nach V. 9; H: fertig machen."] },
      { number: 5, text: "Man soll es den Werkmeistern geben, die im Haus des Herrn angestellt sind, und diese sollen es für die Arbeiter verwenden, die im Tempel die Schäden des Hauses zu beseitigen haben," },
      { number: 6, text: "für die Zimmerleute, Bauarbeiter, Maurer, sowie zum Ankauf von Holz und Bruchsteinen, die man zur Beseitigung der Schäden am Haus des Herrn benötigt." },
      { number: 7, text: "Doch soll man über das Geld, das ihnen übergeben wird, nicht abrechnen. Sie sollen auf Treu und Glauben handeln." },
      { number: 8, text: "Damals teilte der Hohepriester Hilkija dem Staatsschreiber Schafan mit: Ich habe im Haus des Herrn das Gesetzbuch gefunden. Hilkija übergab Schafan das Buch und dieser las es.", footnotes: ["Die Reform Joschijas (Kap. 23) betrifft hauptsächlich den Kult, lässt aber den Schluss zu, dass es sich beim aufgefundenen Gesetzbuch um eine Sammlung von Gesetzen handelt, die im wesentlichen Dtn 12-29 entspricht. Sie konnte während der langen Regierung Manasses in Vergessenheit geraten sein."] },
      { number: 9, text: "Darauf begab sich der Staatsschreiber Schafan zum König und meldete ihm: Deine Knechte haben das Geld ausgeschüttet, das sich im Haus vorfand, und es den Werkmeistern übergeben, die im Haus des Herrn angestellt sind." },
      { number: 10, text: "Dann sagte der Staatsschreiber Schafan zum König: Der Priester Hilkija hat mir ein Buch gegeben. Schafan las es dem König vor." },
      { number: 11, text: "Als der König die Worte des Gesetzbuches hörte, zerriss er seine Kleider" },
      { number: 12, text: "und befahl dem Priester Hilkija sowie Ahikam, dem Sohn Schafans, Achbor, dem Sohn Michas, dem Staatsschreiber Schafan und Asaja, dem Diener des Königs:" },
      { number: 13, text: "Geht und befragt den Herrn für mich, für das Volk und für ganz Juda wegen dieses Buches, das aufgefunden wurde. Der Zorn des Herrn muss heftig gegen uns entbrannt sein, weil unsere Väter auf die Worte dieses Buches nicht gehört und weil sie nicht getan haben, was in ihm niedergeschrieben ist.", footnotes: ["in ihm niedergeschrieben ist: Text korr. nach 2 Chr 34,21; H: gegen uns niedergeschrieben ist."] },
      { number: 14, text: "Da gingen der Priester Hilkija, Ahikam, Achbor, Schafan und Asaja zur Prophetin Hulda. Sie war die Frau Schallums, des Sohnes Tikwas, des Sohnes des Harhas, des Verwalters der Kleiderkammer, und wohnte in Jerusalem in der Neustadt. Die Abgesandten trugen ihr alles vor" },
      { number: 15, text: "und sie gab ihnen diese Antwort: So spricht der Herr, der Gott Israels: Sagt zu dem Mann, der euch zu mir geschickt hat:" },
      { number: 16, text: "So spricht der Herr: Ich bringe Unheil über diesen Ort und seine Bewohner, alle Drohungen des Buches, das der König von Juda gelesen hat." },
      { number: 17, text: "Denn sie haben mich verlassen, anderen Göttern geopfert und mich durch alle Werke ihrer Hände erzürnt. Darum ist mein Zorn gegen diesen Ort entbrannt und er wird nicht erlöschen." },
      { number: 18, text: "Sagt aber zum König von Juda, der euch hergesandt hat, um den Herrn zu befragen: So spricht der Herr, der Gott Israels: Durch die Worte, die du gehört hast," },
      { number: 19, text: "wurde dein Herz erweicht. Du hast dich vor dem Herrn gedemütigt, als du vernahmst, was ich über diesen Ort und seine Bewohner gesprochen habe: dass sie zu einem Bild des Entsetzens und zum Fluch werden sollen. Du hast deine Kleider zerrissen und vor mir geweint. Darum habe ich dich erhört - Spruch des Herrn." },
      { number: 20, text: "Ich werde dich mit deinen Vätern vereinen und du sollst in Frieden in deinem Grab beigesetzt werden. Deine Augen sollen all das Unheil nicht mehr sehen, das ich über diesen Ort bringen werde. - Sie berichteten dies dem König.", footnotes: ["Der Aufschub der Strafe wird als Wohltat bezeichnet (vgl. 1 Kön 11,35; 21,29; 2 Kön 20,19)."] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Der König ließ alle Ältesten Judas und Jerusalems bei sich zusammenkommen.", heading: "Die Beseitigung von Missständen: 23,1-27", footnotes: ["(1-3) 2 Chr 34,29-33"] },
      { number: 2, text: "Er ging zum Haus des Herrn hinauf mit allen Männern Judas und allen Einwohnern Jerusalems, den Priestern und Propheten und allem Volk, Jung und Alt. Er ließ ihnen alle Worte des Bundesbuches vorlesen, das im Haus des Herrn gefunden worden war." },
      { number: 3, text: "Dann trat der König an die Säule und schloss vor dem Herrn diesen Bund: Er wolle dem Herrn folgen, auf seine Gebote, Satzungen und Gesetze von ganzem Herzen und ganzer Seele achten und die Vorschriften des Bundes einhalten, die in diesem Buch niedergeschrieben sind. Das ganze Volk trat dem Bund bei.", footnotes: ["schloss diesen Bund: Er verpflichtete das Volk zur Befolgung der im Buch verzeichneten Gebote."] },
      { number: 4, text: "Hierauf befahl der König dem Hohenpriester Hilkija, den Priestern des zweiten Ranges und den Wächtern an den Schwellen, alle Gegenstände aus dem Tempel des Herrn hinauszuschaffen, die für den Baal, die Aschera und das ganze Heer des Himmels angefertigt worden waren. Er ließ sie außerhalb Jerusalems bei den Terrassen des Kidrontals verbrennen und die Asche nach Bet-El bringen." },
      { number: 5, text: "Auch setzte er die Götzenpriester ab, die von den Königen von Juda bestellt worden waren und die auf den Kulthöhen, in den Städten Judas und in der Umgebung Jerusalems Opfer verbrannt sowie dem Baal, der Sonne, dem Mond, den Bildern des Tierkreises und dem ganzen Heer des Himmels geopfert hatten." },
      { number: 6, text: "Den Kultpfahl schaffte er aus dem Haus des Herrn und aus Jerusalem hinaus in das Kidrontal und verbrannte ihn dort; er zermalmte ihn zu Staub und streute diesen auf die Gräber des einfachen Volkes." },
      { number: 7, text: "Ferner riss er die Gemächer der Hierodulen am Tempel nieder, in denen die Frauen Schleier für die Aschera webten.", footnotes: ["Hierodulen: Tempeldirnen (vgl. die Anmerkung zu 1 Kön 14,24). Die Bedeutung der Schleier ist unbekannt."] },
      { number: 8, text: "Er holte alle Priester aus den Städten Judas weg und machte die Kulthöhen von Geba bis Beerscheba, auf denen die Priester geopfert hatten, unrein. Er zerstörte die Torhöhen, die am Eingang zum Tor des Stadtobersten Josua auf der linken Seite dessen waren, der das Stadttor betrat.", footnotes: ["Es sind Jahwepriester, die auf den Kulthöhen außerhalb Jerusalems Dienst taten (vgl. die Anmerkung zu 1 Kön 3,2). Durch ihre Abberufung hörten die Opfer an jenen Orten auf und der Tempel wurde zur einzigen Kultstätte gemäß Dtn 12,5-18. - Torhöhen sind Opferstätten, die nach dem Vorbild der Kulthöhen an den Stadttoren errichtet wurden."] },
      { number: 9, text: "Doch durften die Höhenpriester nicht an den Altar des Herrn in Jerusalem treten, sondern nur von den ungesäuerten Broten inmitten ihrer Brüder essen.", footnotes: ["Missstände, die sich an den Kulthöhen eingestellt hatten, konnten zu Zweifeln an der Eignung dieser Priester führen. Erst Dtn 18,6-8 brachte ihnen die volle Gleichberechtigung."] },
      { number: 10, text: "Ebenso machte er das Tofet im Tal der Söhne Hinnoms unrein, damit niemand mehr seinen Sohn oder seine Tochter für den Moloch durch das Feuer gehen ließ.", footnotes: ["Tofet: eine heidnische Kultstätte zu Ehren des Gottes Moloch im Hinnomtal südlich von Jerusalem (vgl. Jes 30,33; Jer 7,32; 19,6.11-14). Zu Moloch vgl. die Anmerkung zu Lev 18,21."] },
      { number: 11, text: "Er entfernte die Pferde, die die Könige von Juda zu Ehren der Sonne am Eingang zum Haus des Herrn bei der Zelle des Kämmerers Netan-Melech am Parwar aufgestellt hatten, und verbrannte die Sonnenwagen im Feuer.", footnotes: ["Die Sonne wurde in der Umwelt Israels als Gottheit verehrt. Die hier gemachte Aussage ist dunkel. Parwar ist wahrscheinlich ein Hof oder Platz an der Westseite des Tempels."] },
      { number: 12, text: "Auch die Altäre, die die Könige von Juda auf dem Dach über dem Obergemach des Ahas errichtet hatten, sowie die Altäre, die Manasse in den beiden Höfen des Tempels aufgestellt hatte, ließ der König abbrechen. Er schlug sie dort in Trümmer und warf ihren Schutt in das Kidrontal.", footnotes: ["Die Altäre auf dem Dach des Obergemachs haben wohl dem Gestirnkult gedient."] },
      { number: 13, text: "Desgleichen entweihte der König die Kulthöhen östlich von Jerusalem, südlich vom Berg des Verderbens, die Salomo, der König von Israel, für Astarte, die Göttin der Sidonier, für Kemosch, den Götzen der Moabiter, und für Milkom, den Gräuel der Ammoniter, erbaut hatte.", footnotes: ["1 Kön 11,7"] },
      { number: 14, text: "Er zerbrach die Steinmale, hieb die Kultpfähle um und füllte ihre Stätten mit Menschenknochen." },
      { number: 15, text: "Auch den Altar von Bet-El, die Kulthöhe, die Jerobeam, der Sohn Nebats, der Verführer Israels, errichtet hatte, auch diesen Altar zerstörte er samt der Kulthöhe. Er steckte das Höhenheiligtum in Brand, zermalmte die Steine zu Staub und verbrannte den Kultpfahl." },
      { number: 16, text: "Als Joschija sich umwandte und die Gräber sah, die dort auf dem Berg waren, ließ er die Gebeine aus ihnen nehmen und auf dem Altar verbrennen. So machte er ihn unrein gemäß dem Wort des Herrn, das der Gottesmann ausgerufen hatte, der diese Drohung aussprach.", footnotes: ["1 Kön 13,2"] },
      { number: 17, text: "Und als dann der König fragte: Was ist das für ein Denkmal, das ich hier sehe?, antworteten ihm die Männer der Stadt: Das ist das Grab des Gottesmannes, der aus Juda gekommen war und vorausgesagt hatte, was du am Altar von Bet-El getan hast." },
      { number: 18, text: "Da befahl er: Lasst ihn ruhen! Niemand soll seine Gebeine berühren. So ließ man seine Gebeine samt den Gebeinen des Propheten, der aus Samaria stammte, ungestört." },
      { number: 19, text: "Auch beseitigte Joschija alle Höhentempel in den Städten Samariens, die die Könige von Israel errichtet und durch die sie den Herrn erzürnt hatten. Er verfuhr mit ihnen so, wie er es in Bet-El getan hatte.", footnotes: ["Joschija hatte demnach Teile des ehemaligen Nordreichs unter seine Herrschaft gebracht und so das Reich Davids in kleinerem Umfang wiederhergestellt. Durch die Zerstörung von Bet-El war zugleich das Heiligtum beseitigt, das die religiöse Spaltung des Volkes begründet hatte."] },
      { number: 20, text: "Alle Höhenpriester, die dort waren, ließ er auf den Altären niedermachen und Menschengebeine auf den Altären verbrennen. Darauf kehrte er nach Jerusalem zurück." },
      { number: 21, text: "Nun befahl der König dem ganzen Volk: Feiert das Paschafest zur Ehre des Herrn, eures Gottes, wie es in diesem Bundesbuch vorgeschrieben ist.", footnotes: ["2 Chr 35,1-19"] },
      { number: 22, text: "Ein solches Pascha war nämlich nicht gefeiert worden seit den Tagen der Richter, die Israel regierten, auch nicht in der ganzen Zeit der Könige von Israel und Juda.", footnotes: ["Vgl. die Anmerkung zu 2 Chr 35,18."] },
      { number: 23, text: "Erst im achtzehnten Jahr des Königs Joschija wurde dieses Pascha zur Ehre des Herrn in Jerusalem begangen." },
      { number: 24, text: "Auch die Totenbeschwörer und Zeichendeuter, die Hausgötter, Götzen und alle Gräuel, die im Land Juda und in Jerusalem zu sehen waren, fegte Joschija hinweg. So führte er die Worte des Gesetzes aus, die in dem Buch niedergeschrieben waren, das der Priester Hilkija im Haus des Herrn gefunden hatte." },
      { number: 25, text: "Es gab vor ihm keinen König, der so mit ganzem Herzen, mit ganzer Seele und mit all seinen Kräften zum Herrn umkehrte und so getreu das Gesetz des Mose befolgte, und auch nach ihm war keiner wie er.", footnotes: ["Dtn 6,5"] },
      { number: 26, text: "Doch der Herr ließ von der gewaltigen Glut seines Zornes nicht ab. Sein Zorn war über Juda entbrannt wegen all der Kränkungen, die Manasse ihm zugefügt hatte." },
      { number: 27, text: "Darum sprach der Herr: Auch Juda will ich von meinem Angesicht entfernen, wie ich Israel entfernt habe. Ich verwerfe diese Stadt Jerusalem, die ich erwählt habe, und das Haus, von dem ich gesagt habe: Hier wird mein Name sein." },
      { number: 28, text: "Die übrige Geschichte Joschijas und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda.", heading: "Das Ende Joschijas: 23,28-30", footnotes: ["(28-30) 2 Chr 35,20-24; 36,1"] },
      { number: 29, text: "In seinen Tagen unternahm der Pharao Necho, der König von Ägypten, einen Kriegszug gegen den König von Assur an den Eufrat. König Joschija stellte sich ihm entgegen. Doch der Pharao tötete ihn bei Megiddo, sobald er ihn sah.", footnotes: ["Vgl. die Anmerkung zu 2 Chr 35,20."] },
      { number: 30, text: "Die Diener Joschijas hoben den Toten auf einen Wagen, brachten ihn von Megiddo weg nach Jerusalem und begruben ihn in seinem Grab. Dann nahmen die Bürger des Landes Joahas, den Sohn Joschijas, salbten ihn und machten ihn zum König an seines Vaters Stelle." },
      { number: 31, text: "Joahas war dreiundzwanzig Jahre alt, als er König wurde, und regierte drei Monate in Jerusalem. Seine Mutter hieß Hamutal und war eine Tochter Jirmejas aus Libna.", heading: "Joahas: 23,31-35" },
      { number: 32, text: "Genau wie seine Väter tat er, was dem Herrn missfiel.", footnotes: ["(32-35) 2 Chr 36,2-4"] },
      { number: 33, text: "Doch der Pharao Necho setzte ihn zu Ribla im Land Hamat als König von Jerusalem ab und legte dem Land eine Geldbuße von hundert Talenten Silber und einem Talent Gold auf.", footnotes: ["setzte ihn als König ab: Text korr. nach G."] },
      { number: 34, text: "Dann machte der Pharao Necho Eljakim, den Sohn Joschijas, anstelle seines Vaters Joschija zum König und änderte seinen Namen in Jojakim. Joahas aber nahm er fest und brachte ihn nach Ägypten, wo er starb.", footnotes: ["brachte ihn: Text korr. nach 2 Chr 36,4. Durch die Verleihung eines neuen Namens drückt der Pharao aus, dass Jojakim sein Untertan ist."] },
      { number: 35, text: "Jojakim lieferte dem Pharao das Silber und das Gold ab. Doch musste er das Land besteuern, um das Geld aufbringen zu können, das der Pharao forderte. Nach dem Vermögen eines jeden Bürgers trieb er von den Bürgern des Landes das Silber und das Gold ein, um es an den Pharao Necho abzuliefern.", footnotes: ["Das Gleiche wird von Menahem berichtet (vgl. 15,20)."] },
      { number: 36, text: "Jojakim war fünfundzwanzig Jahre alt, als er König wurde, und regierte elf Jahre in Jerusalem. Seine Mutter hieß Sebuda und war eine Tochter Pedajas aus Ruma.", heading: "Jojakim: 23,36 - 24,7", footnotes: ["(36-24,6) 2 Chr 36,5-8"] },
      { number: 37, text: "Wie seine Väter tat er, was dem Herrn missfiel." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "In seinen Tagen zog Nebukadnezzar, der König von Babel, herauf. Jojakim war ihm drei Jahre untertan; dann aber fiel er von ihm ab." },
      { number: 2, text: "Der Herr sandte nun die Räuberscharen der Chaldäer, Aramäer, Moabiter und Ammoniter gegen ihn. Er ließ sie über Juda herfallen und es verwüsten, wie er durch seine Diener, die Propheten, angedroht hatte." },
      { number: 3, text: "Nur weil der Herr zürnte, kam dieses Unglück über Juda, sodass er es von seinem Angesicht verstieß. Es geschah wegen der Sünde Manasses, für alles, was dieser getan hatte,", footnotes: ["zürnte: Text korr. nach V. 20 und G."] },
      { number: 4, text: "auch wegen des unschuldigen Blutes, das Manasse vergossen und mit dem er Jerusalem angefüllt hatte. Das wollte der Herr nicht mehr verzeihen." },
      { number: 5, text: "Die übrige Geschichte Jojakims und alle seine Taten sind aufgezeichnet in der Chronik der Könige von Juda." },
      { number: 6, text: "Jojakim entschlief zu seinen Vätern und sein Sohn Jojachin wurde König an seiner Stelle." },
      { number: 7, text: "Der König von Ägypten unternahm keinen Kriegszug mehr aus seinem Land; denn der König von Babel hatte ihm alles genommen, was vom Grenzbach Ägyptens bis zum Eufrat den Königen von Ägypten gehört hatte.", footnotes: ["Ägypten, das die Vorherrschaft über Syrien und Palästina anstrebte, wurde von Nebukadnezzar 605 v. Chr. bei Karkemisch am Eufrat entscheidend geschlagen."] },
      { number: 8, text: "Jojachin war achtzehn Jahre alt, als er König wurde, und regierte drei Monate in Jerusalem. Seine Mutter hieß Nehuschta und war eine Tochter Elnatans aus Jerusalem.", heading: "Jojachin: 24,8-17", footnotes: ["(8-17) 2 Chr 36,9-10"] },
      { number: 9, text: "Wie sein Vater tat er, was dem Herrn missfiel." },
      { number: 10, text: "In jener Zeit zogen die Truppen Nebukadnezzars, des Königs von Babel, gegen Jerusalem und belagerten die Stadt." },
      { number: 11, text: "Als dann König Nebukadnezzar von Babel selbst vor der Stadt erschien, während seine Krieger sie belagerten," },
      { number: 12, text: "ging Jojachin, der König von Juda, mit seiner Mutter, seinen Dienern, Fürsten und Kämmerern zum König von Babel hinaus, und dieser nahm ihn im achten Jahr seiner Regierung fest." },
      { number: 13, text: "Wie der Herr angedroht hatte, nahm Nebukadnezzar auch alle Schätze des Hauses des Herrn und die Schätze des königlichen Palastes weg und zerbrach alle goldenen Geräte, die Salomo, der König von Israel, im Haus des Herrn hatte anfertigen lassen." },
      { number: 14, text: "Von ganz Jerusalem verschleppte er alle Vornehmen und alle wehrfähigen Männer, insgesamt zehntausend Mann, auch alle Schmiede und Schlosser. Von den Bürgern des Landes blieben nur die geringen Leute zurück." },
      { number: 15, text: "Jojachin verschleppte er nach Babel. Auch die Mutter des Königs, die königlichen Frauen und Kämmerer sowie die einflussreichen Männer des Landes verschleppte er von Jerusalem nach Babel,", footnotes: ["Die Gefangennahme erfolgte 597 v. Chr. Babylonische Texte aus der Zeit Nebukadnezzars verzeichnen Lebensmittelzuweisungen an Jojachin und seine Söhne."] },
      { number: 16, text: "dazu alle Wehrfähigen, siebentausend Mann, die Schmiede und Schlosser, tausend an der Zahl, lauter kriegstüchtige Männer. Sie alle verschleppte der babylonische König nach Babel." },
      { number: 17, text: "Dann machte der König von Babel den Mattanja, den Onkel Jojachins, an dessen Stelle zum König und änderte seinen Namen in Zidkija." },
      { number: 18, text: "Zidkija war einundzwanzig Jahre alt, als er König wurde, und regierte elf Jahre in Jerusalem. Seine Mutter hieß Hamutal und war eine Tochter Jirmejas aus Libna.", heading: "Zidkija: 24,18 - 25,7", footnotes: ["(18-25,7) 2 Chr 36,11-12; Jer 52,1-11"] },
      { number: 19, text: "Er tat, was dem Herrn missfiel, genauso wie es Jojakim getan hatte." },
      { number: 20, text: "Weil der Herr über Juda und Jerusalem erzürnt war, kam es so weit, dass er sie von seinem Angesicht verstieß." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Zidkija hatte sich gegen den König von Babel empört. Im neunten Regierungsjahr, am zehnten Tag des zehnten Monats, rückte Nebukadnezzar, der König von Babel, mit seiner ganzen Streitmacht vor Jerusalem und belagerte es. Man errichtete ringsherum einen Belagerungswall." },
      { number: 2, text: "Bis zum elften Jahr des Königs Zidkija wurde die Stadt belagert." },
      { number: 3, text: "Am neunten Tag des vierten Monats war in der Stadt die Hungersnot groß geworden, und die Bürger des Landes hatten kein Brot mehr." },
      { number: 4, text: "Damals wurden Breschen in die Stadtmauer geschlagen. Der König und alle Krieger verließen die Stadt bei Nacht auf dem Weg durch das Tor zwischen den beiden Mauern, das zum königlichen Garten hinausführt, obwohl die Chaldäer rings um die Stadt lagen. Sie schlugen die Richtung nach der Araba ein.", footnotes: ["Zwei in H fehlende Wörter sind aus Jer 52,7 ergänzt."] },
      { number: 5, text: "Aber die chaldäischen Truppen setzten dem König nach und holten ihn in den Niederungen von Jericho ein, nachdem alle seine Truppen ihn verlassen und sich zerstreut hatten." },
      { number: 6, text: "Man ergriff den König und brachte ihn nach Ribla, zum König von Babel, und dieser sprach über ihn das Urteil." },
      { number: 7, text: "Die Söhne Zidkijas machte man vor dessen Augen nieder. Zidkija ließ er blenden, in Fesseln legen und nach Babel bringen." },
      { number: 8, text: "Am siebten Tag des fünften Monats - das ist im neunzehnten Jahr des Königs Nebukadnezzar, des Königs von Babel - rückte Nebusaradan, der Kommandant der Leibwache und Diener des Königs von Babel, in Jerusalem ein", heading: "Das Ende des Reiches Juda: 25,8-26", footnotes: ["(8-21) 2 Chr 36,17-21; Jer 52,12-27"] },
      { number: 9, text: "und steckte das Haus des Herrn, den königlichen Palast und alle Häuser Jerusalems in Brand. Jedes große Haus ließ er in Flammen aufgehen." },
      { number: 10, text: "Auch die Umfassungsmauern Jerusalems rissen die chaldäischen Truppen, die dem Kommandanten der Leibwache unterstanden, nieder." },
      { number: 11, text: "Den Rest der Bevölkerung, der noch in der Stadt geblieben war, sowie alle, die zum König von Babel übergelaufen waren, und den Rest der Handwerker schleppte Nebusaradan, der Kommandant der Leibwache, in die Verbannung." },
      { number: 12, text: "Nur von den armen Leuten im Land ließ der Kommandant der Leibwache einen Teil als Wein- und Ackerbauern zurück." },
      { number: 13, text: "Die bronzenen Säulen am Haus des Herrn, die fahrbaren Gestelle und das Eherne Meer beim Haus des Herrn zerschlugen die Chaldäer und nahmen alle Gegenstände aus Bronze mit nach Babel." },
      { number: 14, text: "Auch die Töpfe, Schaufeln, Messer und Becher sowie alle bronzenen Geräte, die man beim Tempeldienst verwendete, nahmen sie weg." },
      { number: 15, text: "Ebenso nahm der Kommandant der Leibwache die Kohlenpfannen und die Schalen weg, die sämtlich aus Gold oder aus Silber waren," },
      { number: 16, text: "ferner die zwei Säulen, das eine «Meer», die Gestelle, die Salomo für das Haus des Herrn hatte anfertigen lassen - die Bronze von all diesen Geräten war nicht zu wägen." },
      { number: 17, text: "Achtzehn Ellen betrug die Höhe der einen Säule, und oben hatte sie ein Kapitell aus Bronze. Die Höhe des einen Kapitells betrug fünf Ellen; das Kapitell umgaben Flechtwerk und Granatäpfel, alles aus Bronze. Ebenso war es bei der zweiten Säule auf dem Flechtwerk." },
      { number: 18, text: "Der Kommandant der Leibwache nahm ferner den Oberpriester Seraja, den zweiten Priester Zefanja und die drei Schwellenwächter mit." },
      { number: 19, text: "Aus der Stadt nahm er einen Hofbeamten, der Kommandant der Soldaten war, und fünf Leute vom persönlichen Dienst des Königs mit, die sich noch in der Stadt befanden, sowie den Schreiber des Heerführers, der die Bürger des Landes auszuheben hatte, schließlich sechzig Mann von der Bürgerschaft, die sich noch in der Stadt befanden." },
      { number: 20, text: "Nebusaradan, der Kommandant der Leibwache nahm sie fest und schickte sie zum König von Babel nach Ribla." },
      { number: 21, text: "Der König von Babel ließ sie in Ribla in der Landschaft Hamat hinrichten. So wurde Juda von seiner Heimat weggeführt." },
      { number: 22, text: "Über das Volk, das im Land Juda geblieben war und das Nebukadnezzar, der König von Babel, zurückgelassen hatte, setzte er Gedalja, den Sohn Ahikams und Enkel Schafans, als Statthalter ein.", footnotes: ["Jer 40,7 - 41,18"] },
      { number: 23, text: "Als nun alle Truppenführer und ihre Mannschaften hörten, dass der König von Babel Gedalja als Statthalter eingesetzt habe, kamen sie zu ihm nach Mizpa. Es waren Jischmael, der Sohn Netanjas, Johanan, der Sohn Kareachs, Seraja, der Sohn Tanhumets aus Netofa, und Jaasanja, der Sohn des Maachatiters, mit ihren Leuten." },
      { number: 24, text: "Gedalja schwor ihnen und ihren Mannschaften und sagte ihnen: Fürchtet euch nicht vor den chaldäischen Beamten! Bleibt im Land und dient dem König von Babel; dann wird es euch gut gehen." },
      { number: 25, text: "Aber im siebten Monat kam Jischmael, der Sohn Netanjas, des Sohnes Elischamas, ein Mann aus königlichem Geschlecht, mit zehn Mann. Sie erschlugen Gedalja samt den Judäern und Chaldäern, die bei ihm in Mizpa waren." },
      { number: 26, text: "Dann machte sich das ganze Volk vom Kleinsten bis zum Größten mit den Truppenführern auf und zog nach Ägypten. Sie fürchteten sich nämlich vor den Chaldäern." },
      { number: 27, text: "Im siebenunddreißigsten Jahr nach der Wegführung Jojachins, des Königs von Juda, am siebenundzwanzigsten Tag des zwölften Monats, begnadigte Ewil-Merodach, der König von Babel, im Jahr seines Regierungsantritts Jojachin, den König von Juda, und entließ ihn aus dem Kerker.", heading: "Die Begnadigung Jojachins: 25,27-30", footnotes: ["(27-30) Jer 52,31-34", "Es ist das Jahr 561 v. Chr. Ewil-Merodach (babylonisch Amel-Marduk) war der Sohn und Nachfolger Nebukadnezzars."] },
      { number: 28, text: "Er söhnte sich mit ihm aus und wies ihm seinen Sitz oberhalb des Sitzes der anderen Könige an, die bei ihm in Babel waren." },
      { number: 29, text: "Er durfte seine Gefängniskleidung ablegen und ständig bei ihm speisen, solange er lebte." },
      { number: 30, text: "Sein Unterhalt - ein dauernder Unterhalt - wurde ihm vom König von Babel in der bestimmten Menge täglich geliefert, solange er lebte." },
    ] },
  ],
};
