import { Book } from "@/lib/types";

export const _2maccabees: Book = {
  id: "2maccabees",
  name: "2. Makkabäer",
  shortName: "2.Makk",
  testament: "old",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Wir, eure Brüder, die Juden aus Jerusalem und aus dem Land Judäa, grüßen euch, unsere Brüder, die Juden, die in Ägypten wohnen, und wünschen euch Frieden.", heading: "Briefe der Juden Jerusalems an die Juden in Ägypten: 1,1 - 2,18 | Der erste Brief zum Tempelweihfest: 1,1-9", footnotes: ["Seit langem gab es jüdische Kolonien in Ägypten. Die am meisten bekannte in Elefantine geht bis in die Anfänge des 6. Jh. v. Chr. zurück."] },
      { number: 2, text: "Gott möge euch Gutes erweisen und seines Bundes gedenken, den er mit seinen treuen Dienern Abraham, Isaak und Jakob geschlossen hat." },
      { number: 3, text: "Er gebe euch allen ein Herz, das euch fähig macht, ihn zu fürchten und seiner Lehre mutig und bereitwillig zu folgen." },
      { number: 4, text: "Er öffne euer Herz für sein Gesetz und für die Gebote und schenke euch Frieden." },
      { number: 5, text: "Er erhöre eure Gebete, schenke euch Versöhnung und verlasse euch nicht in der Not." },
      { number: 6, text: "So beten wir hier für euch." },
      { number: 7, text: "Unter der Regierung des Demetrius, im Jahr 169, haben wir Juden euch geschrieben: In der höchsten Not, die in diesen Jahren über uns kam, als Jason und sein Anhang vom Heiligen Land und vom Königreich abfielen,", footnotes: ["1 Makk 10,67; 2 Makk 4,7-22", "7.9: Das Jahr 169 entspricht dem Jahr 143 v. Chr., das Jahr 188 dem Jahr 124 v. Chr. (vgl. die Anmerkung zu 1 Makk 1,10)."] },
      { number: 8, text: "verbrannten sie das Tempeltor und vergossen unschuldiges Blut. Wir aber beteten zum Herrn und er hat uns erhört. So konnten wir wieder Brand- und Speiseopfer darbringen; wir zündeten die Leuchter an und legten die Schaubrote aus.", footnotes: ["1 Makk 4,38"] },
      { number: 9, text: "Und nun begeht die Tage des Laubhüttenfestes im Monat Kislew! Geschrieben im Jahr 188.", footnotes: ["Mit dem Laubhüttenfest im Monat Kislew (Dezember) ist in Wirklichkeit das Tempelweihfest gemeint."] },
      { number: 10, text: "Die Bewohner Jerusalems und Judäas, der Hohe Rat und Judas wünschen dem Aristobul, dem Lehrer des Königs Ptolemäus, aus dem Geschlecht der gesalbten Priester, und den Juden Ägyptens Glück und Heil.", heading: "Der zweite Brief zum Tempelweihfest: 1,10 - 2,18" },
      { number: 11, text: "Wir danken Gott von Herzen, der uns aus großen Gefahren errettet hat. So sind wir bereit, selbst mit einem König zu streiten." },
      { number: 12, text: "Gott selbst nämlich hat alle verjagt, die gegen die Heilige Stadt gekämpft haben.", footnotes: ["(12f) 9,1-29"] },
      { number: 13, text: "Denn als der Fürst mit seinem Heer, das als unüberwindlich galt, nach Persien zog, fanden sie im Tempel der Nanäa den Tod. Die Priester der Nanäa hatten sie nämlich überlistet.", footnotes: ["Der Tempel der Nanäa, vergleichbar dem der Artemis von Ephesus, den Antiochus IV. berauben wollte, befand sich in Elymaïs."] },
      { number: 14, text: "Unter dem Vorwand, sich mit der Göttin zu vermählen, war Antiochus mit seinen Freunden, die ihn begleiteten, zum Tempel gekommen; sozusagen als Mitgift wollte er sich dabei die großen Reichtümer aneignen." },
      { number: 15, text: "Die Priester der Nanäa legten die Schätze für sie auch bereit und Antiochus ging mit einigen wenigen Männern in den heiligen Bezirk. Sobald er aber das Heiligtum betreten hatte, schlossen sie das Tor." },
      { number: 16, text: "Sie öffneten eine geheime Tür an der Decke, warfen schwere Steine herab, zerschmetterten den Fürsten (und seine Begleiter), zerstückelten sie, schnitten ihnen die Köpfe ab und warfen sie hinaus zu denen, die draußen stehen geblieben waren." },
      { number: 17, text: "Für all das sei unser Gott gepriesen: Er hat die Sünder dem Untergang preisgegeben." },
      { number: 18, text: "Wir wollen nun am fünfundzwanzigsten Kislew die Reinigung des Tempels feiern. Darum hielten wir es für unsere Pflicht, euch davon zu benachrichtigen, damit auch ihr sie wie die Tage des Laubhüttenfestes feiern könnt und wie die Tage des Feuers. (Letztere erinnern an den Tag,) an dem Nehemia nach dem Aufbau von Tempel und Altar erstmals wieder Opfer darbrachte." },
      { number: 19, text: "Denn als unsere Väter nach Persien in die Verbannung geführt wurden, nahmen die Priester, die fromm geblieben waren, etwas von dem Feuer des Altars mit und verbargen es heimlich im Schacht eines leeren Brunnens. Sie versteckten es so, dass die Stelle allen unbekannt blieb.", footnotes: ["Lev 6,5"] },
      { number: 20, text: "Darüber vergingen viele Jahre. Doch als es Gott gefiel, sandte der König von Persien den Nehemia her. Der schickte die Nachkommen jener Priester aus, um das Feuer holen zu lassen, das ihre Väter einst versteckt hatten." },
      { number: 21, text: "Als sie uns erklärten, kein Feuer gefunden zu haben, sondern nur eine dicke Flüssigkeit, befahl er ihnen, etwas davon zu schöpfen und zu ihm zu bringen. Das Opfer wurde hergerichtet. Dann ließ Nehemia von den Priestern das Brennholz, und was darauf lag, mit diesem zähflüssigen Wasser begießen." },
      { number: 22, text: "So geschah es. Nach einiger Zeit brach die Sonne hervor, die von Wolken verdeckt gewesen war. Da flammte ein großes Feuer auf und alle staunten." },
      { number: 23, text: "Während das Opfer verbrannte, beteten die Priester und alle Anderen, die bei ihnen waren; Jonatan stimmte an und die Übrigen, darunter auch Nehemia, beteten laut mit." },
      { number: 24, text: "Das Gebet aber lautete so: Herr, o Herr, du Gott und Schöpfer aller Dinge, furchtbarer, starker, gerechter und barmherziger Gott! Du allein bist König und du bist gütig." },
      { number: 25, text: "Du allein gibst alle Gaben. Nur du bist gerecht, allmächtig und ewig. Du rettest Israel aus aller Not. Du hast unsere Väter erwählt und sie heilig gemacht." },
      { number: 26, text: "Nimm dieses Opfer an für dein ganzes Volk Israel! Behüte dein Erbteil und mach es heilig!" },
      { number: 27, text: "Sammle uns aus der Zerstreuung, befrei alle, die bei den Heiden in Knechtschaft leben, schau auf die Verachteten und Verabscheuten! Dadurch sollen die Heiden erkennen, dass du unser Gott bist." },
      { number: 28, text: "Strafe die stolzen und frechen Unterdrücker!" },
      { number: 29, text: "Pflanz dein Volk an deinem heiligen Ort ein! Denn so hat es Mose zugesagt.", footnotes: ["Dtn 30,3-5"] },
      { number: 30, text: "Die Priester begleiteten die Loblieder, die man sang, mit Musik." },
      { number: 31, text: "Als das Opfer verbrannt war, ließ Nehemia das übrig gebliebene Wasser auf große Steine schütten." },
      { number: 32, text: "Da entzündete sich eine lodernde Flamme. Sie verzehrte sich im Schein des Feuers, das vom Altar her leuchtete." },
      { number: 33, text: "Dieses Ereignis wurde überall bekannt und man meldete dem persischen König, an der Stelle, an der die Priester, die in die Verbannung gingen, das Feuer versteckt hätten, sei das Wasser zum Vorschein gekommen, das die Leute des Nehemia dann über das Opfer gossen." },
      { number: 34, text: "Nachdem der König die Sache geprüft hatte, ließ er den Ort umfrieden und für heilig erklären." },
      { number: 35, text: "Auch nahm der König viele kostbare Geschenke und verteilte sie unter die, denen er wohlgesinnt war." },
      { number: 36, text: "Die Leute um Nehemia nannten das Wasser Neftar, das heißt: Reinigung. Bei den meisten aber heißt es Neftai.", footnotes: ["Neftar (= Erdöl): Manche lesen Naftan, andere Nafta."] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "In den Schriften steht, Jeremia, der Prophet sei es gewesen, der befohlen habe, etwas von dem Feuer - wie schon gesagt - zu nehmen, als sie in die Verbannung geführt wurden.", footnotes: ["Diese Schriften sind, ebenso wie die Erinnerungen des Nehemia (V. 13), verloren gegangen."] },
      { number: 2, text: "Der Prophet habe ferner den Verbannten das Gesetz übergeben und ihnen eingeschärft, die Gebote des Herrn nicht zu vergessen noch im Herzen irre zu werden, wenn sie die goldenen und silbernen Götzen und ihren Prunk sähen.", footnotes: ["Bar 6"] },
      { number: 3, text: "Mit manchen Reden solcher Art ermahnte er sie, das Gesetz nicht aus ihrem Herzen schwinden zu lassen." },
      { number: 4, text: "In dem Buch stand weiter zu lesen, dass der Prophet einen Gottesspruch empfangen habe und daraufhin das Zelt und die Lade hinter sich hertragen ließ. Er sei hinausgegangen zu dem Berg, auf den Mose gestiegen war, um das von Gott verheißene Erbteil zu sehen.", footnotes: ["Dtn 34"] },
      { number: 5, text: "Dort fand Jeremia eine Höhle wie ein Haus. Er trug das Zelt, die Lade und den Rauchopferaltar hinein; dann verschloss er den Eingang." },
      { number: 6, text: "Einige von seinen Begleitern gingen hin, um sich den Weg zu markieren; aber sie konnten ihn nicht finden." },
      { number: 7, text: "Als Jeremia davon hörte, schalt er sie und sagte: Die Stelle soll unbekannt bleiben, bis Gott sein Volk wieder sammelt und ihm wieder gnädig ist.", footnotes: ["Jer 3,16"] },
      { number: 8, text: "Dann aber bringt der Herr dies alles wieder ans Licht und die Herrlichkeit des Herrn wird erscheinen und auch die Wolke, genauso wie sie sich in den Tagen des Mose gezeigt hat und in der Zeit, als Salomo betete, dass der Ort hochheilig werden möge.", footnotes: ["Ex 24,16; 40,34-38; 1 Kön 8,10f"] },
      { number: 9, text: "Es wurde ferner erzählt, wie jener Weise bei der Einweihung und bei der Vollendung des Tempels opferte." },
      { number: 10, text: "So wie Mose zum Herrn gebetet hatte - und dann war Feuer vom Himmel gefallen und hatte die Opferstücke verzehrt -, so betete auch Salomo und das Feuer fiel herab und verzehrte die Brandopfer.", footnotes: ["Lev 9,23f; 2 Chr 7,1"] },
      { number: 11, text: "Mose sagte: Weil man das Sündopfer nicht gegessen hat, wurde es verbrannt.", footnotes: ["Lev 10,16f"] },
      { number: 12, text: "Ebenso hat auch Salomo acht Tage lang gefeiert.", footnotes: ["1 Kön 8,65f"] },
      { number: 13, text: "Das gleiche wird auch in den Schriften und in den Erinnerungen Nehemias erzählt; dort steht auch, wie er eine Bücherei anlegte und die Bücher der Könige und der Propheten und die (Lieder) Davids sammelte, auch königliche Urkunden über Weihegaben.", footnotes: ["Erinnerungen Nehemias: ein sonst unbekanntes, nicht-kanonisches Werk."] },
      { number: 14, text: "Genauso hat auch Judas alle Bücher wieder gesammelt, die in dem Krieg, den wir führen mussten, zerstreut worden waren. Sie befinden sich heute bei uns.", footnotes: ["1 Makk 1,56"] },
      { number: 15, text: "Sollten euch einige davon fehlen, so lasst sie durch Boten holen!" },
      { number: 16, text: "Wir haben euch geschrieben, weil wir nun die Tempelreinigung begehen wollen. Ihr werdet gut daran tun, diese Tage mitzufeiern.", footnotes: ["1 Makk 4,59"] },
      { number: 17, text: "Gott hat sein ganzes Volk gerettet und allen das Erbe und die Königsherrschaft und das Priestertum und die Heiligung verliehen,", footnotes: ["Ex 19,5f"] },
      { number: 18, text: "wie er es durch das Gesetz verheißen hat. Darum hoffen wir nun, dass Gott bald mit uns Erbarmen hat und uns aus der ganzen Welt an seinen heiligen Ort zusammenführt. Denn er hat uns schon aus großen Gefahren gerettet und hat den heiligen Ort gereinigt." },
      { number: 19, text: "Die Ereignisse um den Makkabäer Judas und seine Brüder - wie sie den erhabenen Tempel reinigten und den Altar wieder einweihten;", heading: "Das Vorwort des Verfassers: 2,19-32" },
      { number: 20, text: "ferner die Kriege, die sie gegen Antiochus Epiphanes und seinen Sohn Eupator führten;" },
      { number: 21, text: "die himmlischen Erscheinungen, die den ruhmreichen und tapferen Verteidigern des Judentums halfen, sodass es ihnen, obschon sie nur wenige waren, gelang, das ganze Land zurückzuerobern, die Massen der Barbaren zu verjagen," },
      { number: 22, text: "auch das auf der ganzen Welt hochberühmte Heiligtum wiederzugewinnen, die Stadt zu befreien, die Gesetze, die abgeschafft werden sollten, wieder in Kraft zu setzen - denn der Herr war ihnen in seiner großen Güte gnädig -," },
      { number: 23, text: "all das hat Jason aus Zyrene in fünf Büchern genau beschrieben. Wir nun wollen versuchen, es hier in einem einzigen Buch kurz zusammenzufassen.", footnotes: ["Jason war ein Gelehrter und gehörte der bedeutenden jüdischen Gemeinde in Zyrene an."] },
      { number: 24, text: "Wir bemerkten nämlich die Flut der Zahlen und wie schwierig es wegen der Menge des Stoffes ist, sich in die geschichtliche Darstellung einzuarbeiten." },
      { number: 25, text: "So nahmen wir uns vor, die, die gern lesen, zu unterhalten, denen, die mit Eifer auswendig lernen, zu helfen, allen aber, die das Buch auf irgendeine Weise in die Hand bekommen, zu nützen." },
      { number: 26, text: "Uns ist es allerdings nicht leicht gefallen, in mühseliger Arbeit diesen Auszug anzufertigen; es hat vielmehr Schweiß und durchwachte Nächte gekostet." },
      { number: 27, text: "Wer ein Gastmahl anordnet und den Nutzen anderer sucht, hat es ja auch nicht leicht. Dennoch haben wir die Mühe gern auf uns genommen, um uns viele zu Dank zu verpflichten." },
      { number: 28, text: "Die Einzelheiten genau zu untersuchen, überließen wir dem Geschichtsschreiber. Wir haben uns nur darum bemüht, einen ordentlichen Auszug anzufertigen." },
      { number: 29, text: "Wenn man ein neues Haus baut, muss sich der Architekt um das ganze Gebäude kümmern; Dekorateur und Maler dagegen müssen nur das prüfen, was zur Ausschmückung nötig ist. Ähnlich beurteile ich auch unsere Aufgabe." },
      { number: 30, text: "Sich daran zu machen, die überlieferten Nachrichten kritisch zu beurteilen und bis ins einzelne genau zu untersuchen, ist Sache des Historikers." },
      { number: 31, text: "Wer aber nur nacherzählen will, darf die Darstellung straffen, auch wenn die genaue Ausarbeitung nach den Regeln der Geschichtsschreibung dabei zu kurz kommt." },
      { number: 32, text: "Nun aber wollen wir sofort mit unserer Erzählung beginnen; wir haben uns schon allzu lang mit dem Vorwort aufgehalten und es wäre ja unsinnig, vor der Erzählung viele Worte zu machen, die Erzählung selbst aber zu kürzen." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Die Bewohner der Heiligen Stadt lebten in tiefem Frieden und hielten die Gesetze aufs treueste; denn der Hohepriester Onias war ein frommer Mann und hasste alles Böse.", heading: "Jerusalem unter Gottes Schutz: 3,1-40 | Die Heilige Stadt: 3,1-3", footnotes: ["Onias III. ist der Sohn Simeons III., der in Sir 50,1-21 gepriesen wird."] },
      { number: 2, text: "Darum ehrten sogar die Könige den Ort und schmückten das Heiligtum mit den kostbarsten Weihegaben." },
      { number: 3, text: "So bestritt Seleukus, der König Asiens, aus seinen eigenen Einkünften alle Kosten, die durch den Opferdienst entstanden.", footnotes: ["«Asien» ist hier Bezeichnung für den Vorderen Orient."] },
      { number: 4, text: "Ein gewisser Simeon aus dem Stamm Benjamin war als Tempelvorsteher eingesetzt worden. Er entzweite sich jedoch mit dem Hohenpriester wegen der Marktordnung in der Stadt.", heading: "Der Verrat Simeons am Tempel: 3,4-7" },
      { number: 5, text: "Weil er sich gegen Onias nicht durchsetzen konnte, ging er zu Apollonius, dem Sohn des Tharseas, der damals Befehlshaber in Zölesyrien und Phönizien war." },
      { number: 6, text: "Er erzählte ihm, der Tempelschatz in Jerusalem sei voll von unvorstellbaren Reichtümern; unzählbar sei die Menge des Geldes. Sie stehe in keinem Verhältnis zu dem, was man für die Opfer aufwenden müsse, und lasse sich leicht für den König beschlagnahmen." },
      { number: 7, text: "Als Apollonius mit dem König zusammentraf, berichtete er, was man ihm über die Gelder hinterbracht hatte. Der aber bestimmte seinen Kanzler Heliodor und schickte ihn auf den Weg mit dem Auftrag, sich die erwähnten Gelder ausliefern zu lassen." },
      { number: 8, text: "Heliodor machte sich sofort auf die Reise, angeblich um die Städte in Zölesyrien und Phönizien zu besuchen, in Wirklichkeit jedoch, um das Vorhaben des Königs auszuführen.", heading: "Der Anschlag auf den Tempelschatz: 3,8-14a" },
      { number: 9, text: "Als er nach Jerusalem kam, wurde er vom Hohenpriester der Stadt freundlich empfangen. Da gab er bekannt, welche Anzeige gemacht worden sei, und teilte den wahren Grund seiner Anwesenheit mit. Er fragte, ob sich die Sache wirklich so verhalte." },
      { number: 10, text: "Der Hohepriester erklärte ihm, es handele sich um hinterlegtes Gut von Witwen und Waisen;" },
      { number: 11, text: "ein Teil gehöre auch Hyrkanus, dem Sohn des Tobija, einem sehr einflussreichen Mann. - So hatte der ruchlose Simeon gelogen. - Alles zusammen belaufe sich nur auf vierhundert Talente Silber und zweihundert Talente Gold." },
      { number: 12, text: "Man dürfe aber doch denen nicht Unrecht tun, die ihr Vertrauen auf die Heiligkeit des Ortes und auf die Würde und Unantastbarkeit des weltberühmten Heiligtums gesetzt hätten." },
      { number: 13, text: "Heliodor berief sich jedoch auf die Befehle, die er vom König erhalten hatte, und bestand darauf, alles für die königliche Schatzkammer zu beschlagnahmen." },
      { number: 14, text: "Am festgesetzten Tag schickte er sich an hineinzugehen, um eine Untersuchung der Schätze anzustellen." },
      { number: 15, text: "Da geriet die ganze Stadt in nicht geringe Bestürzung. Die Priester warfen sich in ihren heiligen Gewändern vor dem Altar nieder und riefen den Himmel an: Er habe die Hinterlegung von Geld durch Gesetze geordnet; so solle er es jetzt denen, die es hinterlegt hatten, unversehrt bewahren.", heading: "Der Bittgottesdienst um die Rettung des Tempels: 3,14b-22" },
      { number: 16, text: "Wer aber die Gestalt des Hohenpriesters sah, dem blutete das Herz. Wie er aussah und wie sein Gesicht sich verfärbt hatte, verriet seine innere Qual." },
      { number: 17, text: "Furcht und Zittern nämlich hatten den Mann befallen und er bebte am ganzen Leib. Allen, die ihn sahen, wurde der Schmerz seines Herzens offenbar." },
      { number: 18, text: "Die Leute stürzten in Scharen aus den Häusern heraus zum öffentlichen Gebet; denn dem Tempel drohte Schande." },
      { number: 19, text: "Die Frauen zogen Trauerkleider an, die die Brüste freiließen, und drängten sich auf die Straßen. Von den jungen Mädchen aber, die man sonst eingeschlossen hielt, liefen die einen an die Türen, die andern auf die Mauern; einige beugten sich aus den Fenstern heraus." },
      { number: 20, text: "Sie alle streckten die Hände zum Himmel empor und beteten flehentlich." },
      { number: 21, text: "Es war zum Erbarmen, wie die Menge sich in heillosem Durcheinander zu Boden warf und wie der Hohepriester sich in seinem Kummer so schrecklich ängstigte." },
      { number: 22, text: "So riefen sie zum Herrn, dem Allherrscher, er möge das anvertraute Gut denen, die es hinterlegt hatten, unversehrt und ganz sicher bewahren." },
      { number: 23, text: "Heliodor jedoch machte sich daran, seinen Entschluss auszuführen.", heading: "Die Bestrafung des Tempelräubers: 3,23-30" },
      { number: 24, text: "Schon stand er mit der Leibwache an der Schatzkammer. Da ließ der Herr der Geister und aller Macht eine gewaltige Erscheinung sichtbar werden. Alle, die ihn frech begleitet hatten, erschraken vor Gottes Macht; ihre Kräfte verließen sie und sie bekamen große Angst." },
      { number: 25, text: "Denn es erschien ihnen ein Pferd mit einem schrecklichen Reiter darauf; das Pferd war mit prächtigem Geschirr geschmückt. Es stürmte wild auf Heliodor ein und traf ihn heftig mit den Vorderhufen. Sein Reiter aber trug eine goldene Rüstung." },
      { number: 26, text: "Noch zwei andere junge Männer erschienen, voll gewaltiger Kraft, in strahlender Schönheit und herrlich gekleidet. Sie traten auf Heliodor zu und peitschten von beiden Seiten auf ihn ein; pausenlos schlugen sie ihn mit vielen Hieben." },
      { number: 27, text: "Da stürzte er zu Boden und es wurde ihm schwarz vor den Augen. Man hob ihn schnell auf und legte ihn auf eine Bahre." },
      { number: 28, text: "Eben noch war er mit großem Gefolge und der ganzen Leibwache zu der genannten Schatzkammer gekommen; nun trug man ihn hilflos hinaus. Deutlich hatte man die Herrschermacht Gottes erkannt." },
      { number: 29, text: "So lag er da, durch Gottes Macht gestürzt, der Sprache beraubt, ohne jede Hoffnung auf Rettung." },
      { number: 30, text: "Die Juden aber priesen den Herrn, der an seinem Ort so herrlich seine Macht gezeigt hatte; und das Heiligtum, das eben noch voll war von Angst und Verwirrung, war erfüllt von Freude und Jubel; denn der allmächtige Herr hatte sich offenbart." },
      { number: 31, text: "Sehr bald kamen ein paar Vertraute Heliodors zu Onias und baten ihn, er möge doch den Höchsten anrufen und so dem das Leben schenken, der in den letzten Zügen lag.", heading: "Die Bekehrung des Tempelräubers: 3,31-40" },
      { number: 32, text: "Aus Sorge, der König könne der Meinung verfallen, Heliodor sei einem hinterhältigen Anschlag der Juden zum Opfer gefallen, brachte der Hohepriester ein Opfer dar, damit der Mann wieder gesund würde." },
      { number: 33, text: "Während der Hohepriester noch mit dem Versöhnungsopfer beschäftigt war, erschienen dem Heliodor dieselben jungen Männer wie zuvor, in der gleichen Kleidung. Sie traten zu ihm und sagten: Danke dem Hohenpriester Onias vielmals; denn seinetwegen schenkt der Herr dir gnädig das Leben." },
      { number: 34, text: "Der Himmel hat dich gezüchtigt. Nun verkünde du allen die gewaltige Kraft Gottes! Nach diesen Worten entschwanden sie." },
      { number: 35, text: "Da ließ Heliodor dem Herrn ein Opfer darbringen und machte ihm große Gelübde, weil er ihn am Leben gelassen hatte. Er nahm Abschied von Onias und zog mit seinen Truppen zum König zurück." },
      { number: 36, text: "Vor allen Menschen bezeugte er die Taten des größten Gottes, die er mit eigenen Augen gesehen hatte." },
      { number: 37, text: "Als der König ihn fragte, wer geeignet sei, noch einmal nach Jerusalem geschickt zu werden, gab er zur Antwort:" },
      { number: 38, text: "Wenn du einen Feind oder einen Hochverräter weißt, dann schick ihn dorthin! Du kannst sicher sein, dass er geprügelt zurückkommt, wenn er überhaupt am Leben bleibt; denn an jenem Ort wirkt wahrhaftig eine göttliche Kraft." },
      { number: 39, text: "Er, der im Himmel wohnt, ist selbst der Wächter und Schützer jenes Ortes; und wer in böser Absicht dorthin kommt, den schlägt er nieder." },
      { number: 40, text: "Das waren die Ereignisse um Heliodor und um die Rettung des Tempels." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Der oben genannte Simeon, der den Tempelschatz und das Vaterland verraten hatte, verleumdete Onias, er sei es gewesen, der Heliodor habe schlagen lassen und der das Unheil ins Werk gesetzt habe.", heading: "Der Einbruch der Sünde: 4,1-50 | Neue Umtriebe Simeons: 4,1-6" },
      { number: 2, text: "Den Wohltäter der Stadt, den fürsorglichen Beschützer seiner Mitbürger und Eiferer für die Gesetze, wagte er einen Hochverräter zu nennen." },
      { number: 3, text: "Die Feindschaft verschärfte sich derart, dass einer von den Vertrauten Simeons mehrere Morde verübte." },
      { number: 4, text: "Onias erkannte, dass der Streit unerträglich wurde und dass außerdem Apollonius, der Sohn des Menestheus, Befehlshaber von Zölesyrien und Phönizien, die Bosheit Simeons noch unterstützte." },
      { number: 5, text: "Darum begab er sich zum König, nicht um die Mitbürger zu verklagen, sondern weil er das allgemeine und das besondere Wohl des ganzen Volkes im Auge hatte." },
      { number: 6, text: "Er erkannte nämlich, dass ohne Einschreiten des Königs der öffentliche Friede nicht wiederherzustellen sei; denn Simeon würde nicht von seiner Raserei ablassen." },
      { number: 7, text: "Seleukus starb und Antiochus mit dem Beinamen Epiphanes übernahm die Herrschaft. Da erschlich sich Jason, der Bruder des Onias, das Hohepriesteramt.", heading: "Die Einführung heidnischer Sitten in Jerusalem: 4,7-22", footnotes: ["1 Makk 1,10"] },
      { number: 8, text: "Bei einer Unterredung versprach er dem König nämlich dreihundertsechzig Talente Silber, dazu noch aus anderen Einkünften achtzig Talente." },
      { number: 9, text: "Außerdem wolle er sich schriftlich verpflichten, weitere hundertfünfzig Talente zu zahlen, wenn er die Vollmacht erhalte, eine Sportschule und einen Übungsplatz für junge Leute zu errichten denn daran sei ihm sehr gelegen - sowie den Einwohnern Jerusalems das antiochenische Bürgerrecht zu verleihen." },
      { number: 10, text: "Der König war einverstanden. Sobald Jason das Amt an sich gebracht hatte, führte er unter seinen Landsleuten die griechische Lebensart ein.", footnotes: ["1 Makk 1,11-15"] },
      { number: 11, text: "Er schaffte die günstigen Privilegien ab, die die Juden durch Vermittlung des Johanan vom König erhalten hatten. Dieser Johanan war der Vater des Eupolemus, der als Gesandter nach Rom gegangen war, um dort ein Freundschaftsbündnis zu schließen. Jason hob die althergebrachte Verfassung auf und führte neue, widerrechtliche Gebräuche ein.", footnotes: ["1 Makk 8,17"] },
      { number: 12, text: "Absichtlich ließ er unmittelbar unterhalb der Burg eine Sportschule errichten und die Söhne der besten Familien brachte er dazu, den griechischen Hut aufzusetzen." },
      { number: 13, text: "So kam das Griechentum in Mode; man fiel ab zu der fremden Art. Schuld daran war die maßlose Schlechtigkeit des ruchlosen Jason, der den Namen des Hohenpriesters zu Unrecht trug." },
      { number: 14, text: "Schließlich kümmerten sich die Priester nicht mehr um den Dienst am Altar; der Tempel galt in ihren Augen nichts und für die Opfer hatten sie kaum mehr Zeit. Dafür gingen sie eilig auf den Sportplatz, sobald die Aufforderung zum Diskuswerfen erging, um an dem Spiel, das vom Gesetz verboten war, teilzunehmen." },
      { number: 15, text: "Die Ehren ihres Vaterlandes achteten sie gering, auf griechische Auszeichnungen dagegen waren sie ganz versessen." },
      { number: 16, text: "Darum sollten sie auch in große Not geraten. Gerade die, denen sie alles nachmachten und denen sie ganz gleich werden wollten, wurden ihre Feinde und Peiniger." },
      { number: 17, text: "Man kann sich nämlich nicht leichthin über die göttlichen Gesetze hinwegsetzen. Aber das wird die Folgezeit deutlich zeigen." },
      { number: 18, text: "Als der König die Wettkämpfe besuchte, die alle fünf Jahre in Tyrus ausgetragen werden," },
      { number: 19, text: "sandte der nichtswürdige Jason Männer aus Jerusalem, die das antiochenische Bürgerrecht erworben hatten, als Zuschauer dorthin und gab ihnen dreihundert Silberdrachmen mit für das Opfer an Herakles. Doch baten die Überbringer, das Geld nicht zum Opfer zu verwenden, weil sich das nicht zieme, sondern es für einen anderen Zweck zurückzulegen." },
      { number: 20, text: "Nach der Absicht des Auftraggebers wäre es also für das Heraklesopfer bestimmt gewesen; es lag allein an den Überbringern, dass man es zur Ausrüstung der Galeeren verwendete." },
      { number: 21, text: "Zur Thronbesteigung des Königs Philometor entsandte Antiochus den Apollonius, den Sohn des Menestheus, nach Ägypten. Dabei brachte er in Erfahrung, dass der ägyptische König seiner Politik feindlich gegenüberstehe, und sorgte sich um seine Sicherheit. Er zog deshalb nach Jafo und von dort nach Jerusalem." },
      { number: 22, text: "Jason und die Stadt bereiteten ihm einen großartigen Empfang; unter Fackelschein und Freudengeschrei hielt er seinen Einzug. Dann brachte er seine Truppen wieder nach Phönizien ins Quartier." },
      { number: 23, text: "Drei Jahre darauf schickte Jason den Menelaus, den Bruder des vorhin erwähnten Simeon, zum König; er sollte ihm das Geld überbringen und schwebende Verhandlungen über wichtige Staatsgeschäfte zum Abschluss bringen.", heading: "Das Hohepriesteramt in der Hand der Sünder: 4,23-29", footnotes: ["3,4; 4,1"] },
      { number: 24, text: "Menelaus verschaffte sich jedoch Empfehlungen an den König, trat als bedeutender Mann auf und schmeichelte ihm, überbot Jason um dreihundert Talente Silber und brachte so das Amt des Hohenpriesters an sich." },
      { number: 25, text: "Mit der königlichen Ernennungsurkunde kam er zurück. Sonst hatte er nichts an sich, was des hohenpriesterlichen Amtes würdig gewesen wäre. Statt dessen besaß er die Leidenschaft eines rohen Tyrannen und die Wut eines wilden Tieres." },
      { number: 26, text: "Jason, der seinen eigenen Bruder hinterlistig verdrängt hatte, wurde nun selbst durch einen anderen hinterlistig verdrängt und als Flüchtling ins Ammoniterland vertrieben." },
      { number: 27, text: "Menelaus hatte sich zwar der Herrschaft bemächtigt, machte jedoch keine Anstalten, das Geld aufzubringen, das er dem König versprochen hatte," },
      { number: 28, text: "obschon Sostratus, der Befehlshaber der Burg, ihn wiederholt mahnte; dieser hatte nämlich die Gelder einzutreiben. Deswegen bestellte der König beide vor sich." },
      { number: 29, text: "Menelaus ließ als Stellvertreter im Hohenpriesteramt seinen Bruder Lysimachus zurück, Sostratus aber Krates, den Befehlshaber der zyprischen Truppen." },
      { number: 30, text: "In diesem Augenblick brach in den Städten Tarsus und Mallus ein Aufstand aus, weil sie Antiochis, der Nebenfrau des Königs, als Geschenk vermacht worden waren.", heading: "Die Ermordung des rechtmäßigen Hohenpriesters: 4,30-38" },
      { number: 31, text: "In großer Eile begab sich der König dorthin, um die Sache beizulegen, und ließ als seinen Stellvertreter einen hohen Beamten, Andronikus, zurück." },
      { number: 32, text: "Da glaubte Menelaus, eine günstige Gelegenheit gefunden zu haben: Er entwendete aus dem Tempel einige goldene Geräte und schenkte sie Andronikus; andere hatte er nach Tyrus und in die benachbarten Städte verkaufen können." },
      { number: 33, text: "Onias, der davon sichere Kenntnis erhalten hatte, tadelte ihn scharf; er hatte sich in einen Asylort bei Daphne, einem Vorort Antiochias, zurückgezogen.", footnotes: ["Daphne war ein Heiligtum der Götter Apollo und Artemis."] },
      { number: 34, text: "Menelaus ging deswegen zu Andronikus, sprach mit ihm unter vier Augen und redete ihm zu, Onias aus dem Weg zu schaffen. Andronikus suchte Onias auf. Da er sich zur Hinterlist hatte verleiten lassen, erhob er die rechte Hand zum Schwur, reichte sie dann Onias und überredete ihn, trotz seines Argwohns den Ort zu verlassen. Dann ließ ihn Andronikus, ohne das Recht zu scheuen, auf der Stelle umbringen.", footnotes: ["Dan 9,26"] },
      { number: 35, text: "Nicht nur die Juden, sondern auch viele aus anderen Völkern entsetzten sich über dieses Unrecht und waren empört über die Ermordung des Mannes." },
      { number: 36, text: "Als der König aus den Orten Ziliziens zurückkam, gingen die Juden der Stadt zu ihm und beschwerten sich. Wie sie, waren auch die Griechen sehr entrüstet, weil man Onias gegen alle Ordnung ermordet hatte." },
      { number: 37, text: "Antiochus wurde von Herzen betrübt; es ergriff ihn Mitleid und er vergoss Tränen, weil der Verstorbene ein so besonnener und edler Mann gewesen war." },
      { number: 38, text: "Dann entbrannte sein Zorn; er ließ Andronikus sofort den Purpur abnehmen, die Kleider vom Leib reißen und ihn so durch die ganze Stadt führen, bis zu der Stelle, an der er das Verbrechen gegen Onias begangen hatte. Dort ließ er den Mörder hinrichten. So hat ihm der Herr mit der verdienten Strafe vergolten." },
      { number: 39, text: "In der Stadt aber verging sich Lysimachus mit Wissen des Menelaus mehrmals am Tempelschatz. Als sich das Gerücht davon weit verbreitete, rottete sich das Volk gegen Lysimachus zusammen. Viele goldene Geräte waren schon verschleppt worden.", heading: "Der Raub am Tempelschatz: 4,39-50" },
      { number: 40, text: "Als nun die Menge sich erhob und in heftigen Zorn geriet, bewaffnete Lysimachus fast dreitausend Mann und begann gewaltsam gegen die Leute vorzugehen. Ein gewisser Auranus führte sie an, der ebenso alt wie wahnsinnig war." },
      { number: 41, text: "Als die Leute merkten, dass Lysimachus angreifen ließ, rafften sie Steine zusammen oder dicke Stöcke - ein paar füllten ihre Hände sogar mit der Asche, die dort lag - und schleuderten alles durcheinander gegen die Männer des Lysimachus." },
      { number: 42, text: "So verwundeten sie viele von ihnen, einige streckten sie nieder, alle aber jagten sie in die Flucht. Den Tempelräuber selbst aber schlugen sie beim Schatzhaus tot." },
      { number: 43, text: "Wegen dieser Sache wurde gegen Menelaus ein gerichtliches Verfahren eingeleitet." },
      { number: 44, text: "Als der König nach Tyrus kam, erhoben drei Männer, die vom Hohen Rat geschickt waren, vor ihm die Anklage." },
      { number: 45, text: "Menelaus war schon verloren; da versprach er Ptolemäus, dem Sohn des Dorymenes, viel Geld, damit er den König zu seinen Gunsten überrede.", footnotes: ["8,8; 1 Makk 3,38"] },
      { number: 46, text: "Ptolemäus nahm also den König in einen Säulengang beiseite, als wolle er ihn etwas ausruhen lassen, und stimmte ihn um." },
      { number: 47, text: "Darauf sprach der König den Menelaus, der an dem ganzen Unheil schuld war, von den Anklagepunkten frei; die Unglücklichen aber, die, selbst wenn sie vor Skythen gesprochen hätten, wegen erwiesener Unschuld freigesprochen worden wären, verurteilte er zum Tod.", footnotes: ["Die Skythen waren ein wegen ihrer Grausamkeit berüchtigtes Reitervolk am Schwarzen Meer (vgl. auch 7,4)."] },
      { number: 48, text: "Unverzüglich mussten sie die ungerechte Strafe erleiden, sie, die doch nur für ihre Stadt, ihr Volk und die heiligen Geräte eingetreten waren." },
      { number: 49, text: "Sogar Einwohner von Tyrus entrüsteten sich darüber und gaben ihnen ein prunkvolles Begräbnis." },
      { number: 50, text: "Menelaus aber blieb aufgrund der Habgier der Mächtigen im Amt. Seine Bosheit nahm immer mehr zu und er wurde zu einem großen Feind seiner Mitbürger." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Um diese Zeit unternahm Antiochus seinen zweiten Feldzug gegen Ägypten.", heading: "Der Einbruch des Unheils: 5,1 - 7,42 | Ein Vorzeichen: 5,1-4", footnotes: ["1 Makk 1,17"] },
      { number: 2, text: "Da erschienen fast vierzig Tage lang über der ganzen Stadt Reiter, die durch die Lüfte jagten, in golddurchwirkten Gewändern; Lanzenträger rückten in Abteilungen zum Kampf aus, Schwerter zuckten." },
      { number: 3, text: "Reiterscharen ordneten sich zur Schlacht, Angriffe wurden gemacht, von beiden Seiten rannte man gegeneinander an, Schilde bewegten sich, Speere gab es in Menge, Wurfgeschosse flogen, goldener Waffenschmuck blitzte auf und man sah Rüstungen aller Art." },
      { number: 4, text: "Alle beteten deshalb, die Erscheinung möge etwas Gutes bedeuten." },
      { number: 5, text: "Es kam aber das falsche Gerücht auf, Antiochus sei gestorben. Da unternahm Jason mit nicht weniger als tausend Mann einen überraschenden Angriff auf die Stadt. Er drängte die Verteidiger auf der Mauer rasch in die Enge; als er die Stadt ganz besetzt hatte, flüchtete sich Menelaus in die Burg.", heading: "Der Bürgerkrieg in Jerusalem: 5,5-10" },
      { number: 6, text: "Jason aber richtete unter seinen Mitbürgern ein schonungsloses Blutbad an, ohne zu bedenken, dass Glück gegenüber den eigenen Leuten das größte Unglück ist. Es sah aus, als habe er Feinde und nicht Landsleute besiegt und ausgeplündert." },
      { number: 7, text: "Dennoch konnte er die Herrschaft nicht an sich reißen. Sein Anschlag brachte ihm am Schluss nur Schande und er musste zum zweiten Mal in das Ammoniterland fliehen." },
      { number: 8, text: "Er nahm schließlich ein böses Ende. Aretas, der Fürst der Araber, ließ ihn gefangen setzen; er aber entkam und floh von Stadt zu Stadt. Von allen gehetzt, als Verräter der Gesetze verhasst, verwünscht als Henker des Vaterlandes und seiner Mitbürger, verschlug es ihn nach Ägypten." },
      { number: 9, text: "Und wie er viele Menschen aus ihrem Vaterland in die Fremde getrieben hatte, so kam er selbst in der Fremde um, nämlich bei den Spartanern, zu denen er übers Meer gefahren war, um bei ihnen Schutz zu finden; die Spartaner waren ja mit den Juden verwandt.", footnotes: ["1 Makk 12,7"] },
      { number: 10, text: "So wie er viele ohne Grab hatte daliegen lassen, so trauerte jetzt auch niemand um ihn; auch er erhielt keinerlei Begräbnis und kein Grab bei seinen Vätern." },
      { number: 11, text: "Als dem König zu Ohren kam, was geschehen war, glaubte er, Judäa wolle von ihm abfallen. Wütend wie ein wildes Tier brach er daher mit seinem Heer von Ägypten auf und nahm die Stadt mit Waffengewalt ein.", heading: "Der Überfall des heidnischen Königs: 5,11-20", footnotes: ["(11-16) 1 Makk 1,20-24"] },
      { number: 12, text: "Er befahl seinen Soldaten, alle, die ihnen begegneten, rücksichtslos niederzuhauen und auch die zu erschlagen, die sich auf das Dach ihrer Häuser geflüchtet hätten." },
      { number: 13, text: "Sie richteten unter Jung und Alt ein großes Blutbad an; junge Männer, Frauen und Kinder kamen um, man erstach Mädchen und Säuglinge." },
      { number: 14, text: "In nur drei Tagen verlor die Stadt achtzigtausend Einwohner; vierzigtausend fanden im Kampf den Tod, ebenso viele, wie man ermordet hatte, wurden in die Sklaverei verkauft." },
      { number: 15, text: "Doch das genügte dem König noch nicht; in seiner Frechheit betrat er den heiligsten Tempel der ganzen Erde unter der Führung des Menelaus, der die Gesetze und sein Vaterland verraten hatte." },
      { number: 16, text: "Seine blutbefleckten Hände griffen nach den heiligen Geräten, und was andere Könige gestiftet hatten, um Glanz und Würde des Ortes zu erhöhen, raffte er mit unreinen Händen zusammen." },
      { number: 17, text: "In seinem Übermut erkannte Antiochus nicht, dass der Herr nur für eine kurze Zeit zornig war, weil die Einwohner der Stadt gesündigt hatten, und deswegen nicht auf den Ort achtete.", footnotes: ["6,12-17"] },
      { number: 18, text: "Hätten sich die Juden damals nicht in viele Sünden verstrickt, dann wäre ihm, sobald er sich vorwagte, seine Frechheit durch Peitschenhiebe ausgetrieben worden, ähnlich wie dem Heliodor, der von König Seleukus zur Durchsuchung der Schatzkammer ausgeschickt worden war." },
      { number: 19, text: "Aber der Herr hat nicht das Volk erwählt wegen des Ortes, sondern den Ort wegen des Volkes." },
      { number: 20, text: "Deswegen litt auch der Ort mit unter den Unglücksschlägen, die das Volk trafen, wie er später Anteil hatte an seinem Glück. Als der Allherrscher zürnte, lag der Ort verlassen da; als aber der große Herr sich wieder versöhnen ließ, wurde er in aller Pracht wiederhergestellt." },
      { number: 21, text: "Antiochus also ließ tausendachthundert Talente aus dem Tempel schaffen und zog eilig ab nach Antiochia. In seiner Vermessenheit glaubte er, das Land schiffbar und das Meer gangbar machen zu können - so überheblich war er.", heading: "Jerusalem in der Hand der Heiden: 5,21-27" },
      { number: 22, text: "Er ließ bei seinem Abzug mehrere Befehlshaber zurück, die das Volk unterdrücken sollten. In Jerusalem war es Philippus, seiner Herkunft nach ein Phrygier, seiner Gesinnung nach ein Barbar, noch wilder als der, der ihn eingesetzt hatte;" },
      { number: 23, text: "auf dem Berg Garizim war es Andronikus. Dazu kam Menelaus, der sich seinen Mitbürgern gegenüber noch herrischer aufführte als die anderen; denn er hasste die jüdischen Bürger." },
      { number: 24, text: "Außerdem schickte der König den Anführer der Mysier, Apollonius, mit einem Heer von zweiundzwanzigtausend Mann; er hatte ihm befohlen, alle wehrfähigen Männer umzubringen, die Frauen und Kinder aber in die Sklaverei zu verkaufen.", footnotes: ["(24-26) 1 Makk 1,29f"] },
      { number: 25, text: "Als Apollonius in Jerusalem ankam, spielte er zunächst den Friedfertigen. Er wartete bis zum heiligen Sabbattag. Als er nun bemerkte, dass die Juden sich jeder Arbeit enthielten, gab er seinen Leuten Befehl, zu den Waffen zu greifen und auszurücken." },
      { number: 26, text: "Alle, die zum festlichen Gottesdienst gegangen waren, ließ er niederstechen. Dann fielen sie mit der blanken Waffe in die Stadt ein und erschlugen viele Menschen." },
      { number: 27, text: "Judas aber, mit dem Beinamen der Makkabäer, schloss sich mit neun Gefährten zusammen und zog sich in die Wüste zurück. Er lebte mit seinen Leuten in den Bergen wie die Tiere. Sie ernährten sich die ganze Zeit nur von Pflanzen, um nicht ebenfalls unrein zu werden.", footnotes: ["1 Makk 2,28; Hebr 11,38"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Nicht lange darauf schickte der König einen alten Athener; der sollte die Juden zwingen, die Gesetze ihrer Väter aufzugeben und ihr Leben nicht mehr durch Gottes Gesetze lenken zu lassen.", heading: "Die Verfolgung der jüdischen Religion: 6,1-11", footnotes: ["(1-11) 1 Makk 1,45-59"] },
      { number: 2, text: "Auch sollte er den Tempel zu Jerusalem schänden und ihn Zeus, dem Herrscher des Olymp, weihen; ähnlich sollte er den Tempel auf dem Berg Garizim nach Zeus, dem Hüter des Gastrechts, benennen, was der (gastfreundlichen) Art der Einwohner jenes Ortes entgegenkam.", footnotes: ["Auf dem Garizim hatten die Samariter ihren Tempel errichtet."] },
      { number: 3, text: "Der Ansturm der Bosheit war kaum zu ertragen und allen zuwider." },
      { number: 4, text: "Denn die Heiden erfüllten das Heiligtum mit wüstem Treiben und mit Gelagen. Sie gaben sich mit Dirnen ab und ließen sich in den heiligen Vorhöfen mit Frauen ein. Auch brachten sie vieles hinein, was nicht hineingehörte." },
      { number: 5, text: "Auf den Brandopferaltar häuften sie unerlaubte und vom Gesetz verbotene Dinge." },
      { number: 6, text: "Man konnte weder den Sabbat halten noch die alten Feste begehen, ja, man durfte sich überhaupt nicht mehr als Jude bekennen." },
      { number: 7, text: "Zu ihrer Erbitterung mussten die Einwohner sich jeden Monat am Geburtstag des Königs zum Opfermahl führen lassen und am Fest der Dionysien zwang man sie, zu Ehren des Dionysos mit Efeu bekränzt in der Prozession mitzugehen.", footnotes: ["Dionysos war der griechische Gott des Weins."] },
      { number: 8, text: "Auf Vorschlag der Einwohner von Ptolemaïs wurde in den benachbarten griechischen Städten ein Beschluss bekannt gegeben, sie sollten mit den Juden ebenso verfahren und Opfermahlzeiten veranstalten.", footnotes: ["der Einwohner von Ptolemaïs: Text korr. nach Vg."] },
      { number: 9, text: "Wer sich aber nicht entschließen wolle, zur griechischen Lebensweise überzugehen, sei hinzurichten. Da konnte man nun das Elend sehen, das hereinbrach." },
      { number: 10, text: "Man führte nämlich zwei Frauen vor, die ihre Kinder beschnitten hatten. Darauf hängte man ihnen die Säuglinge an die Brüste, führte sie öffentlich in der Stadt umher und stürzte sie dann von der Mauer.", footnotes: ["1 Makk 1,60"] },
      { number: 11, text: "Andere waren in der Nähe zusammengekommen, um heimlich in Höhlen den Sabbat zu begehen. Sie wurden an Philippus verraten, und da sie sich wegen der Würde des heiligen Tages scheuten, sich zu wehren, wurden sie alle zusammen verbrannt.", footnotes: ["1 Makk 2,29-38"] },
      { number: 12, text: "An dieser Stelle möchte ich die Leser des Buches ermahnen, sich durch die schlimmen Ereignisse nicht entmutigen zu lassen. Sie mögen bedenken, dass die Strafen unser Volk nicht vernichten, sondern erziehen sollen.", heading: "Eine Geschichtsbetrachtung des Verfassers: 6,12-17", footnotes: ["(12-16) 5,17-20; 7,18f.32f.38"] },
      { number: 13, text: "Denn wenn die Sünder nicht lange geschont, sondern sofort bestraft werden, ist das ein Zeichen großer Güte.", footnotes: ["Weish 11,9f; 12,2.22"] },
      { number: 14, text: "Bei den anderen Völkern wartet der Herr geduldig, bis das Maß ihrer Sünden voll ist; dann erst schlägt er zu. Mit uns aber beschloss er, anders zu verfahren," },
      { number: 15, text: "damit er uns nicht am Ende verurteilen müsse, wenn wir es mit unseren Sünden bis zum Äußersten getrieben hätten." },
      { number: 16, text: "Daher entzieht er uns nie sein Erbarmen, sondern er erzieht sein Volk durch Unglück und lässt es nicht im Stich." },
      { number: 17, text: "Das soll uns zur Beherzigung gesagt sein. Nach dieser kurzen Abschweifung aber wollen wir mit der Erzählung fortfahren." },
      { number: 18, text: "Unter den angesehensten Schriftgelehrten war Eleasar, ein Mann von hohem Alter und edlen Gesichtszügen. Man sperrte ihm den Mund auf und wollte ihn zwingen, Schweinefleisch zu essen.", heading: "Das Martyrium des Eleasar: 6,18-31", footnotes: ["1 Makk 1,62-64; Hebr 11,35; Lev 11,7f", "Viele Kirchenväter haben Eleasar als einen vorchristlichen Märtyrer gepriesen."] },
      { number: 19, text: "Er aber zog den ehrenvollen Tod einem Leben voll Schande vor, ging freiwillig auf die Folterbank zu" },
      { number: 20, text: "und spuckte das Fleisch wieder aus. In solcher Haltung mussten alle herantreten, die sich standhaft wehrten zu essen, was man nicht essen darf - nicht einmal um des geliebten Lebens willen." },
      { number: 21, text: "Die Leute, die bei dem gesetzwidrigen Opfermahl Dienst taten und die den Mann von früher her kannten, nahmen ihn heimlich beiseite und redeten ihm zu, er solle sich doch Fleisch holen lassen, das er essen dürfe, und es selbst zubereiten. Dann solle er tun, als ob er von dem Opferfleisch esse, wie es der König befohlen habe." },
      { number: 22, text: "Wenn er es so mache, entgehe er dem Tod; weil sie alte Freunde seien, würden sie ihn mit Nachsicht behandeln." },
      { number: 23, text: "Er aber fasste einen edlen Entschluss, wie es sich gehörte für einen Mann, der so alt und wegen seines Alters angesehen war, in Würde ergraut, der von Jugend an vorbildlich gelebt und - was noch wichtiger ist - den heiligen, von Gott gegebenen Gesetzen gehorcht hatte. So erklärte er ohne Umschweife, man solle ihn ruhig zur Unterwelt schicken." },
      { number: 24, text: "Wer so alt ist wie ich, soll sich nicht verstellen. Viele jungen Leute könnten sonst glauben, Eleasar sei mit seinen neunzig Jahren noch zu der fremden Lebensart übergegangen." },
      { number: 25, text: "Wenn ich jetzt heucheln würde, um eine geringe, kurze Zeit länger zu leben, würde ich sie irreleiten, meinem Alter aber Schimpf und Schande bringen." },
      { number: 26, text: "Vielleicht könnte ich mich für den Augenblick der Bestrafung durch die Menschen entziehen; doch nie, weder lebendig noch tot, werde ich den Händen des Allherrschers entfliehen." },
      { number: 27, text: "Darum will ich jetzt wie ein Mann sterben und mich so meines Alters würdig zeigen." },
      { number: 28, text: "Der Jugend aber hinterlasse ich ein leuchtendes Beispiel, wie man mutig und mit Haltung für die ehrwürdigen und heiligen Gesetze eines schönen Todes stirbt. Nach diesen Worten ging er geradewegs zur Folterbank." },
      { number: 29, text: "Da schlug die Freundlichkeit, die ihm seine Begleiter eben noch erwiesen hatten, in Feindschaft um; denn was er gesagt hatte, hielten sie für Wahnsinn." },
      { number: 30, text: "Als man ihn zu Tod prügelte, sagte er stöhnend: Der Herr mit seiner heiligen Erkenntnis weiß, dass ich dem Tod hätte entrinnen können. Mein Körper leidet qualvoll unter den Schlägen, meine Seele aber erträgt sie mit Freuden, weil ich ihn fürchte." },
      { number: 31, text: "So starb er; durch seinen Tod hinterließ er nicht nur der Jugend, sondern den meisten aus dem Volk ein Beispiel für edle Gesinnung und ein Denkmal der Tugend." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Ein andermal geschah es, dass man sieben Brüder mit ihrer Mutter festnahm. Der König wollte sie zwingen, entgegen dem göttlichen Gesetz Schweinefleisch zu essen, und ließ sie darum mit Geißeln und Riemen peitschen.", heading: "Das Martyrium der sieben Brüder und ihrer Mutter: 7,1-42", footnotes: ["Lev 11,7f"] },
      { number: 2, text: "Einer von ihnen ergriff für die andern das Wort und sagte: Was willst du uns fragen und von uns wissen? Eher sterben wir, als dass wir die Gesetze unserer Väter übertreten." },
      { number: 3, text: "Da wurde der König zornig und befahl, Pfannen und Kessel heiß zu machen." },
      { number: 4, text: "Kaum waren sie heiß geworden, ließ er ihrem Sprecher die Zunge abschneiden, ihm nach Skythenart die Kopfhaut abziehen und Nase, Ohren, Hände und Füße stückweise abhacken. Dabei mussten die anderen Brüder und die Mutter zuschauen." },
      { number: 5, text: "Den grässlich Verstümmelten, der noch atmete, ließ er ans Feuer bringen und in der Pfanne braten. Während sich der Dunst aus der Pfanne nach allen Seiten verbreitete, sprachen sie und ihre Mutter einander Mut zu, in edler Haltung zu sterben. Sie sagten:" },
      { number: 6, text: "Gott der Herr schaut auf uns und gewiss hat er Erbarmen mit uns. Denn so hat es Mose klar gesagt in dem Lied, in dem er öffentlich das Volk anklagte: Und er wird mit seinen Dienern Erbarmen haben.", footnotes: ["Dtn 32,36"] },
      { number: 7, text: "Als der erste der Brüder auf diese Weise gestorben war, führten sie den zweiten zur Folterung. Sie zogen ihm die Kopfhaut samt den Haaren ab und fragten ihn: Willst du essen, bevor wir dich Glied für Glied foltern?" },
      { number: 8, text: "Er antwortete in seiner Muttersprache: Nein! Deshalb wurde er genauso wie der erste gefoltert." },
      { number: 9, text: "Als er in den letzten Zügen lag, sagte er: Du Unmensch! Du nimmst uns dieses Leben; aber der König der Welt wird uns zu einem neuen, ewigen Leben auferwecken, weil wir für seine Gesetze gestorben sind.", footnotes: ["7,11.14.23.29.36; 12,43f; 14,46; Dan 12,2"] },
      { number: 10, text: "Nach ihm folterten sie den dritten. Als sie seine Zunge forderten, streckte er sie sofort heraus und hielt mutig die Hände hin." },
      { number: 11, text: "Dabei sagte er gefasst: Vom Himmel habe ich sie bekommen und wegen seiner Gesetze achte ich nicht auf sie. Von ihm hoffe ich sie wiederzuerlangen." },
      { number: 12, text: "Sogar der König und seine Leute staunten über den Mut des jungen Mannes, dem die Schmerzen nichts bedeuteten." },
      { number: 13, text: "Als er tot war, quälten und misshandelten sie den vierten genauso." },
      { number: 14, text: "Dieser sagte, als er dem Ende nahe war: Gott hat uns die Hoffnung gegeben, dass er uns wieder auferweckt. Darauf warten wir gern, wenn wir von Menschenhand sterben. Für dich aber gibt es keine Auferstehung zum Leben." },
      { number: 15, text: "Anschließend nahmen sie sich den fünften vor und misshandelten ihn." },
      { number: 16, text: "Der sah den König an und sagte: Du bist ein vergänglicher Mensch und doch hast du die Macht unter den Menschen zu tun, was du willst. Aber glaub nicht, unser Volk sei von Gott verlassen." },
      { number: 17, text: "Mach nur so weiter! Du wirst seine gewaltige Kraft spüren, wenn er dich und deine Nachkommen züchtigt." },
      { number: 18, text: "Nach ihm holten sie den sechsten. Sterbend sagte er: Lass dich nicht täuschen! Du wirst nichts ausrichten. Denn wir sind selbst schuld an unserem Leid, weil wir gegen unseren Gott gesündigt haben. Darum konnte so Unfassbares geschehen." },
      { number: 19, text: "Glaub aber ja nicht, dass du heil davonkommst; denn du hast es gewagt, mit Gott zu kämpfen." },
      { number: 20, text: "Auch die Mutter war überaus bewundernswert und sie hat es verdient, dass man sich an sie mit Hochachtung erinnert. An einem einzigen Tag sah sie nacheinander ihre sieben Söhne sterben und ertrug es tapfer, weil sie dem Herrn vertraute." },
      { number: 21, text: "In edler Gesinnung stärkte sie ihr weibliches Gemüt mit männlichem Mut, redete jedem von ihnen in ihrer Muttersprache zu und sagte:" },
      { number: 22, text: "Ich weiß nicht, wie ihr in meinem Leib entstanden seid, noch habe ich euch Atem und Leben geschenkt; auch habe ich keinen von euch aus den Grundstoffen zusammengefügt.", footnotes: ["Ijob 10,8-12; Ps 139,13-15"] },
      { number: 23, text: "Nein, der Schöpfer der Welt hat den werdenden Menschen geformt, als er entstand; er kennt die Entstehung aller Dinge. Er gibt euch gnädig Atem und Leben wieder, weil ihr jetzt um seiner Gesetze willen nicht auf euch achtet." },
      { number: 24, text: "Antiochus aber glaubte, sie verachte ihn, und er hatte den Verdacht, sie wolle ihn beschimpfen. Nun war nur noch der Jüngste übrig. Auf ihn redete der König nicht nur mit guten Worten ein, sondern versprach ihm unter vielen Eiden, ihn reich und sehr glücklich zu machen, wenn er von der Lebensart seiner Väter abfalle; auch wolle er ihn zu seinem Freund machen und ihn mit hohen Staatsämtern betrauen." },
      { number: 25, text: "Als der Junge nicht darauf einging, rief der König die Mutter und redete ihr zu, sie solle dem Knaben doch raten, sich zu retten." },
      { number: 26, text: "Erst nach langem Zureden willigte sie ein, ihren Sohn zu überreden." },
      { number: 27, text: "Sie beugte sich zu ihm nieder, und den grausamen Tyrannen verspottend, sagte sie in ihrer Muttersprache: Mein Sohn, hab Mitleid mit mir! Neun Monate habe ich dich in meinem Leib getragen, ich habe dich drei Jahre gestillt, dich ernährt, erzogen und für dich gesorgt, bis du nun so groß geworden bist." },
      { number: 28, text: "Ich bitte dich, mein Kind, schau dir den Himmel und die Erde an; sieh alles, was es da gibt, und erkenne: Gott hat das aus dem Nichts erschaffen und so entstehen auch die Menschen.", footnotes: ["Ijob 26,7"] },
      { number: 29, text: "Hab keine Angst vor diesem Henker, sei deiner Brüder würdig und nimm den Tod an! Dann werde ich dich zur Zeit der Gnade mit deinen Brüdern wiederbekommen." },
      { number: 30, text: "Kaum hatte sie aufgehört, da sagte der Junge: Auf wen wartet ihr? Dem Befehl des Königs gehorche ich nicht; ich höre auf den Befehl des Gesetzes, das unseren Vätern durch Mose gegeben wurde." },
      { number: 31, text: "Du aber, der sich alle diese Bosheiten gegen die Hebräer ausgedacht hat, du wirst Gottes Händen nicht entkommen." },
      { number: 32, text: "Denn wir leiden nur, weil wir gesündigt haben." },
      { number: 33, text: "Wenn auch der lebendige Herr eine kurze Zeit lang zornig auf uns ist, um uns durch Strafen zu erziehen, so wird er sich doch mit seinen Dienern wieder versöhnen." },
      { number: 34, text: "Du Ruchloser aber, du größter Verbrecher der Menschheit, überheb dich nicht und werde nicht durch falsche Hoffnungen übermütig, wenn du deine Hand gegen die Kinder des Himmels erhebst." },
      { number: 35, text: "Denn noch bist du dem Gericht des allmächtigen Gottes, der alles sieht, nicht entronnen." },
      { number: 36, text: "Unsere Brüder sind nach kurzem Leiden mit der göttlichen Zusicherung ewigen Lebens gestorben; du jedoch wirst beim Gericht Gottes die gerechte Strafe für deinen Übermut zahlen." },
      { number: 37, text: "Ich gebe wie meine Brüder Leib und Leben hin für die Gesetze unserer Väter und rufe zu Gott, er möge seinem Volk bald wieder gnädig sein; du aber sollst unter Qualen und Schlägen bekennen müssen, dass nur er Gott ist." },
      { number: 38, text: "Bei mir und meinen Brüdern möge der Zorn des Allherrschers aufhören, der sich zu Recht über unser ganzes Volk ergossen hat." },
      { number: 39, text: "Da wurde der König zornig und verfuhr mit ihm noch schlimmer als mit den anderen - so sehr hatte ihn der Hohn verletzt." },
      { number: 40, text: "Auch der Jüngste starb also mit reinem Herzen und vollendetem Gottvertrauen." },
      { number: 41, text: "Zuletzt starb nach ihren Söhnen die Mutter.", footnotes: ["In der christlichen Überlieferung gelten die sieben makkabäischen Brüder als Märtyrer, denen man im Mittelmeerraum viele Kirchen weihte."] },
      { number: 42, text: "Soviel sei über die Opfergelage und die schlimmen Misshandlungen berichtet." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Judas aber, den man auch Makkabäer nennt, und seine Leute schlichen sich heimlich in die Dörfer und holten ihre Verwandten zu sich; auch gewannen sie die treu gebliebenen Juden, sodass sie etwa sechstausend Mann zusammenbrachten.", heading: "Die Wiederherstellung Jerusalems: 8,1 - 10,8 | Der Aufstand des Makkabäers Judas: 8,1-7", footnotes: ["5,27; (1-7) 1 Makk 2,42-48"] },
      { number: 2, text: "Sie riefen zum Herrn, er möge auf das von allen geschundene Volk schauen und Mitleid haben mit dem Tempel, den ruchlose Menschen entweiht hätten," },
      { number: 3, text: "er möge auch der Stadt gnädig sein, die zerstört werde und bald dem Erdboden gleichgemacht sei, und auf das unschuldig vergossene Blut hören, das (anklagend) zu ihm aufschreie." },
      { number: 4, text: "Er solle daran denken, dass man entgegen jedem Recht unschuldige Kinder ermordet und seinen Namen gelästert habe, und zeigen, dass er das Böse hasse." },
      { number: 5, text: "Sobald der Makkabäer eine Streitmacht aufgestellt hatte, konnten ihn die Heiden nicht mehr aufhalten; denn der Herr hatte seinem Zorn Gnade folgen lassen." },
      { number: 6, text: "Er überfiel Städte und Dörfer und steckte sie in Brand. Da er günstige Stellungen bezog, jagte er nicht wenige Feinde in die Flucht." },
      { number: 7, text: "Meist nutzte er die Nächte zu solchen Unternehmungen und der Ruf seiner Kühnheit verbreitete sich überall." },
      { number: 8, text: "Philippus merkte, dass der Mann rasch an Macht gewann und von Tag zu Tag erfolgreicher wurde; er schrieb daher an Ptolemäus, den Befehlshaber von Zölesyrien und Phönizien, er solle der Sache des Königs zu Hilfe kommen.", heading: "Der Sieg des Judas über Nikanor und andere Feinde: 8,8-36", footnotes: ["5,22; 4,45; (8-36) 1 Makk 3,38 - 4,25", "Philippus ist «Vorsteher» von Jerusalem."] },
      { number: 9, text: "Dieser benannte sofort Nikanor, den Sohn des Patroklus, der zu den ersten Freunden des Königs gehörte, und ließ ihn an der Spitze eines Heeres von mindestens zwanzigtausend Mann aus aller Herren Länder ausrücken, mit dem Auftrag, die gesamte Bevölkerung Judäas auszurotten. Er gab ihm auch Gorgias mit, einen im Kriegswesen erfahrenen Befehlshaber." },
      { number: 10, text: "Der König musste den Römern noch zweitausend Talente Kriegsschulden auszahlen; Nikanor beschloss, diese Summe aus dem Verkauf gefangener Juden aufzubringen." },
      { number: 11, text: "Unverzüglich ließ er durch Boten in den Hafenstädten bekannt geben, man könne zu ihm kommen und jüdische Sklaven kaufen; für ein Talent versprach er neunzig Personen zu liefern. Er ahnte nicht, dass ihn bald die Strafe des Allherrschers ereilen würde." },
      { number: 12, text: "Judas hörte vom Anmarsch Nikanors und ließ seinen Leuten bekannt geben, dass jener mit seinem Heer in der Nähe ein Lager aufgeschlagen habe." },
      { number: 13, text: "Die Feiglinge und die, die dem Urteil Gottes misstrauten, liefen weg und machten sich davon." },
      { number: 14, text: "Die anderen aber verkauften alles, was sie noch hatten; zugleich baten sie den Herrn, sie zu retten; denn der verruchte Nikanor habe sie schon verkauft, bevor er ihnen überhaupt begegnet sei," },
      { number: 15, text: "und wenn er es schon nicht ihretwegen tun wolle, dann doch wegen der Bündnisse, die er mit ihren Vätern geschlossen habe, und weil über ihnen selbst sein heiliger und herrlicher Name ausgerufen worden sei.", footnotes: ["Jer 14,9"] },
      { number: 16, text: "Der Makkabäer ließ seine Leute zusammenkommen, sechstausend an der Zahl. Er redete ihnen zu, sich von den Feinden nicht aus der Fassung bringen zu lassen und nicht ängstlich zu werden, weil die Heiden in solchen Massen ohne jeden Grund gegen sie heranrückten. Sie sollten tapfer kämpfen" },
      { number: 17, text: "und sich stets vor Augen halten, mit welch frechem Übermut man entgegen allem Recht den heiligen Ort behandelt habe, wie übel man der Stadt mitgespielt und wie schlimm man sie zugerichtet habe und dass die von den Vorfahren ererbte Verfassung aufgelöst worden sei." },
      { number: 18, text: "Er sagte: Sie verlassen sich auf ihre Waffen und auf die Kühnheit, mit der sie angreifen; wir aber verlassen uns auf den allmächtigen Gott, der unsere Angreifer und die ganze Welt mit einem einzigen Wink vernichten kann." },
      { number: 19, text: "Weiter zählte er ihnen auf, wie oft Gott ihren Vorfahren geholfen habe, etwa gegen Sanherib, als hundertfünfundachtzigtausend Mann den Tod fanden.", footnotes: ["2 Kön 19,35"] },
      { number: 20, text: "Er erwähnte auch die Schlacht mit den Galatern in Babylon: Da waren sie, zusammen mit viertausend Mazedoniern nur achttausend Mann stark, zum Kampf angetreten, und als die Mazedonier in eine verzweifelte Lage gerieten, schlugen sie, weil der Himmel ihnen half, mit sechstausend Mann hundertzwanzigtausend vernichtend und machten reiche Beute." },
      { number: 21, text: "So machte er ihnen Mut, damit sie bereit wären, für die Gesetze und das Vaterland zu sterben. Dann teilte er sein Heer in vier Abteilungen ein." },
      { number: 22, text: "An die Spitze der einzelnen Gruppen stellte er seine Brüder Simeon, Josef und Jonatan, denen er je fünfzehnhundert Mann zuwies," },
      { number: 23, text: "und außerdem den Eleasar. Er selbst las aus der Heiligen Schrift vor und gab die Losung aus: Mit Gottes Hilfe! Dann trat er vor die erste Reihe und griff Nikanor an." },
      { number: 24, text: "Da ihnen der Allherrscher im Kampf half, machten sie mehr als neuntausend Feinde nieder, verwundeten und verstümmelten den größten Teil des Heeres Nikanors und zwangen alle Übrigen zur Flucht." },
      { number: 25, text: "Dabei erbeuteten sie auch das Geld der Leute, die gekommen waren, um sie zu kaufen. Sie verfolgten sie eine Strecke weit, kehrten dann aber um, weil sie keine Zeit mehr hatten." },
      { number: 26, text: "Es war nämlich am Tag vor dem Sabbat; darum setzten sie ihnen nicht länger nach." },
      { number: 27, text: "Sie nahmen den Feinden die Waffen weg und zogen ihnen die Rüstungen aus. Dann feierten sie den Sabbat; begeistert priesen sie den Herrn und lobten seine Taten. Durch seinen Schutz hatten sie diesen Tag erleben können, an dem er ihnen von neuem seine Gnade zugewandt hatte." },
      { number: 28, text: "Nach dem Sabbat verteilten sie die Beute. Zuerst gaben sie davon den Misshandelten und den Witwen und Waisen; den Rest verteilten sie unter sich selbst und ihre Kinder." },
      { number: 29, text: "Danach hielten sie einen Bittgottesdienst ab und baten den barmherzigen Herrn, er möge sich mit seinen Dienern wieder völlig aussöhnen." },
      { number: 30, text: "Sie kämpften auch gegen die Truppen des Timotheus und des Bakchides, töteten von ihnen über zwanzigtausend Mann und eroberten einige hoch in den Bergen gelegene Festungen. Die reiche Beute verteilten sie zu gleichen Teilen unter sich und unter die Misshandelten, die Waisen und Witwen und unter die alten Leute.", footnotes: ["1 Makk 5,6; 7,8"] },
      { number: 31, text: "Sie sammelten die Waffen der Erschlagenen ein und bewahrten sie sorgfältig an geeigneten Orten auf. Die übrige Beute schafften sie nach Jerusalem." },
      { number: 32, text: "Auch Phylarches, einen ganz ruchlosen Mann aus der Umgebung des Timotheus, töteten sie; er hatte den Juden viel Böses getan." },
      { number: 33, text: "Bei der Siegesfeier in der Vaterstadt verbrannten sie die Männer, die die heiligen Tore angezündet hatten, sowie den Kallisthenes; er hatte sich in ein kleines Haus geflüchtet. So erhielt er den verdienten Lohn für seine Verruchtheit.", footnotes: ["1,8"] },
      { number: 34, text: "Der dreimal verfluchte Nikanor aber, der die tausend Händler mitgebracht hatte, um die Juden zu verkaufen," },
      { number: 35, text: "wurde von denen, die er verachtet hatte, mit Hilfe des Herrn gedemütigt. Er musste sein Prachtgewand ausziehen und sich wie ein entlaufener Sklave mitten durch das Land allein nach Antiochia durchschlagen, wo er völlig niedergeschlagen über den Verlust seines Heeres ankam." },
      { number: 36, text: "Er, der sich unterfangen hatte, den Tribut für die Römer aus den Gefangenen Jerusalems aufzubringen, musste nun verkünden, dass die Juden jemanden hätten, der für sie kämpfe, und dass sie deshalb unverwundbar seien; denn sie achteten auf die Gesetze, die jener erlassen hatte." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Etwa zur selben Zeit musste Antiochus mit Schimpf und Schande aus Persien abziehen.", heading: "Das Gottesgericht über den heidnischen König: 9,1-29", footnotes: ["(1-29) 1 Makk 6,1-16; 2 Makk 1,11-17"] },
      { number: 2, text: "Er war in die Stadt, die man Persepolis nennt, einmarschiert. Bei dem Versuch, den Tempel auszurauben und eine Besatzung in die Stadt zu legen, erhob sich die Bevölkerung in Massen und griff zu den Waffen. Die Truppen wurden geschlagen und Antiochus von den Einwohnern verjagt; er musste schmählich den Rückzug antreten." },
      { number: 3, text: "In der Gegend von Ekbatana erhielt er die Nachricht von der Niederlage Nikanors und der Truppen des Timotheus." },
      { number: 4, text: "Da geriet er in heftigen Zorn und glaubte, seine Wut über die unglückliche Flucht an den Juden auslassen zu können. Darum befahl er dem Wagenlenker, ohne Unterbrechung bis zum Ziel durchzufahren. Aber schon drohte ihm das Gericht des Himmels; denn in seiner Vermessenheit sagte er: Sobald ich in Jerusalem bin, mache ich die Stadt zu einem Friedhof für alle Juden." },
      { number: 5, text: "Doch der Herr, der alles sieht, Israels Gott, traf ihn, ohne dass es jemand sehen konnte, mit einem Schlag, für den es keine Heilung gab. Kaum hatte er zu reden aufgehört, da spürte er in seinen Eingeweiden quälende Schmerzen, die kein Arzt lindern konnte.", footnotes: ["Apg 12,20-23"] },
      { number: 6, text: "Damit geschah ihm ganz recht, hatte er doch die Eingeweide anderer durch zahllose ausgefallene Foltern gequält." },
      { number: 7, text: "Dennoch blieb sein Stolz ungebrochen; die Vermessenheit hatte ihn ganz und gar in Besitz genommen. Glühende Wut gegen die Juden verzehrte ihn und er befahl dem Wagenlenker, noch schneller zu fahren. Doch dann geschah es: In voller Fahrt fiel er aus dem dahinrasenden Wagen und stürzte so schwer, dass er sich alle Glieder verrenkte." },
      { number: 8, text: "Eben noch hatte er in maßloser Aufgeblasenheit geglaubt, er könne den Wogen des Meeres gebieten und die Gipfel der Berge auf einer Waage wiegen. Nun lag er auf der Erde und man musste ihn auf eine Bahre legen. So zeigte sich an ihm sichtbar Gottes Macht.", footnotes: ["Ps 65,7f; Jes 40,12"] },
      { number: 9, text: "Aus den Augen des Verruchten krochen Würmer; während er noch lebte, verfaulte sein Fleisch unter Schmerzen und Qualen und der Verwesungsgeruch, der von ihm ausging, verpestete das ganze Lager.", footnotes: ["Apg 12,23; Sir 7,17", "Aus den Augen, andere Lesart: Aus dem Leib."] },
      { number: 10, text: "Kurz zuvor hatte er noch geglaubt, er könne nach den Sternen des Himmels greifen; jetzt konnte es niemand mehr bei ihm aushalten, so unerträglich war der Gestank." },
      { number: 11, text: "Da endlich begann der Gepeinigte, von seinem maßlosen Hochmut abzulassen und unter Gottes Schlägen zur Einsicht zu kommen; denn seine Schmerzen wurden immer schlimmer." },
      { number: 12, text: "Als er seinen Geruch selbst nicht mehr ertragen konnte, sagte er: Wenn man nur ein sterblicher Mensch ist, soll man sich Gott unterordnen und nicht überheblich sein.", footnotes: ["nicht überheblich sein, andere Lesart: nicht wie Gott sein wollen."] },
      { number: 13, text: "Der Verbrecher rief sogar den Herrn an, fand aber bei ihm kein Erbarmen mehr. Er gelobte," },
      { number: 14, text: "die Heilige Stadt, die er kurzerhand hatte dem Erdboden gleichmachen und in einen Friedhof umwandeln wollen, in den Rang einer freien Stadt zu erheben." },
      { number: 15, text: "Hatte er zuerst beschlossen, die Juden nicht einmal eines Grabes zu würdigen, sondern sie samt den Säuglingen den Raubvögeln und den wilden Tieren zum Fraß vorzuwerfen, so wollte er sie nun alle den Bürgern von Athen gleichstellen." },
      { number: 16, text: "Er versprach, den heiligen Tempel, den er zuvor geplündert hatte, mit den schönsten Weihegeschenken auszuschmücken, die heiligen Geräte um ein Vielfaches zu ersetzen und die nötigen Aufwendungen für die Opfer aus eigenen Mitteln aufbringen." },
      { number: 17, text: "Ja, er wollte sogar selbst Jude werden und überall hingehen, wo Menschen wohnen, um Gottes Macht zu verkünden." },
      { number: 18, text: "Trotzdem ließen seine Schmerzen nicht nach; denn das gerechte Gericht Gottes war über ihn gekommen. Da gab er alle Hoffnung auf und schrieb den Juden einen Brief, der eigentlich eine Bittschrift war. Der Brief hatte diesen Inhalt:" },
      { number: 19, text: "Seinen guten jüdischen Bürgern wünscht Antiochus, König und Befehlshaber, viel Freude, Gesundheit und Wohlergehen." },
      { number: 20, text: "Ich danke Gott sehr, wenn ihr gesund seid und wenn es auch euren Kindern und eurem Besitz nach Wunsch ergeht. Dafür setze ich meine Hoffnung auf den Himmel." },
      { number: 21, text: "Ich erinnere mich in Liebe an die Achtung und freundliche Hochschätzung, die ihr mir entgegengebracht habt. Bei meiner Rückkehr aus Persien zog ich mir eine Krankheit zu, die mich sehr belastet. Darum hielt ich es für nötig, für die öffentliche Sicherheit aller Bürger zu sorgen." },
      { number: 22, text: "Nicht dass ich mich schon aufgegeben hätte - ich habe vielmehr gute Hoffnung, wieder gesund zu werden." },
      { number: 23, text: "Aber ich dachte daran, dass schon mein Vater jedes Mal einen Nachfolger bestimmte, wenn er sich mit seinem Heer im Osten aufhielt." },
      { number: 24, text: "Falls dann etwas Unvorhergesehenes eintrat oder ein Missgeschick gemeldet wurde, wussten die Bewohner des Reiches, wem die Regierung übertragen worden war, und sie brauchten sich nicht zu beunruhigen." },
      { number: 25, text: "Auch sehe ich, wie die Machthaber an den Grenzen unseres Landes, unsere Nachbarn, nur auf eine günstige Gelegenheit lauern und die kommende Entwicklung abwarten. Darum habe ich in aller Form meinen Sohn Antiochus zum Nachfolger bestimmt; ihn habe ich den meisten von euch ja schon oft anvertraut und empfohlen, wenn ich plötzlich in die östlichen Provinzen hinaufziehen musste. An ihn habe ich einen Brief geschrieben, den ich beilege." },
      { number: 26, text: "Ich bitte euch eindringlich: Denkt daran, wie viel Gutes ich eurer Gemeinschaft und jedem einzelnen von euch erwiesen habe, und bewahrt mir und meinem Sohn euer Wohlwollen!" },
      { number: 27, text: "Ich bin überzeugt, dass er meine Politik der Güte und Freundschaft weiterführen und in gutem Einvernehmen mit euch bleiben wird." },
      { number: 28, text: "Der Menschenmörder und Gotteslästerer endete also fern seiner Heimat im Gebirge auf jämmerliche Weise, unter entsetzlichen Schmerzen, ganz wie er sie anderen zugefügt hatte." },
      { number: 29, text: "Sein Jugendfreund Philippus ließ den Leichnam überführen; dann begab er sich nach Ägypten zu Ptolemäus Philometor, weil er dem Sohn des Antiochus nicht traute." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Der Makkabäer aber und seine Leute konnten unter der Führung des Herrn das Heiligtum und die Stadt wieder in Besitz nehmen.", heading: "Die Reinigung des entweihten Tempels: 10,1-8", footnotes: ["(1-8) 1 Makk 4,36-61", "1-8: Im Herbst 164 v. Chr. starb Antiochus; im Dezember des gleichen Jahres wurde der Tempel gereinigt."] },
      { number: 2, text: "Sie rissen die Altäre ein, die die Heiden auf dem Tempelplatz errichtet hatten, und legten die Umfriedungsmauern nieder." },
      { number: 3, text: "Den Tempel selbst reinigten sie und bauten einen neuen Brandopferaltar. Sie schlugen Feuer aus Steinen und zündeten so die Opfer an, die sie nach zweijähriger Unterbrechung wieder darbringen konnten. Auch bemühten sie sich um Räucherwerk, Leuchter und Schaubrote." },
      { number: 4, text: "Dann warfen sie sich auf die Erde nieder und flehten zum Herrn, dass sie nie wieder in solches Unglück gerieten. Für den Fall, dass sie noch einmal sündigen sollten, wollten sie lieber von ihm selbst in Güte gezüchtigt werden als in die Hände frecher und barbarischer Heiden fallen.", footnotes: ["2 Sam 24,14; 1 Chr 21,13"] },
      { number: 5, text: "Es traf sich, dass die Reinigung des Tempels auf den gleichen Tag fiel, an dem ihn die Fremden entweiht hatten, nämlich auf den fünfundzwanzigsten Kislew." },
      { number: 6, text: "Sie feierten acht Tage lang ein fröhliches Fest nach Art des Laubhüttenfestes; dabei dachten sie daran, dass sie noch vor kurzem das Laubhüttenfest wie wilde Tiere in den Höhlen der Berge verbracht hatten.", footnotes: ["5,27"] },
      { number: 7, text: "Sie nahmen Stäbe, die sie mit grünen Blättern umwunden hatten, in die Hand und Laubzweige - auch Palmzweige - und brachten dem Loblieder dar, der den Weg zur Reinigung des Ortes bereitet hatte, der sein Eigentum ist." },
      { number: 8, text: "Sie setzten durch eine öffentliche Entschließung und Abstimmung fest, dass das ganze jüdische Volk jedes Jahr diese Tage festlich zu begehen habe." },
      { number: 9, text: "So starb Antiochus, den man Epiphanes nannte.", heading: "Die Ereignisse unter Antiochus Eupator: 10,9 - 13,26 | Der Regierungsantritt des Antiochus Eupator: 10,9-13" },
      { number: 10, text: "Jetzt wollen wir noch berichten, was unter Antiochus Eupator geschah, dem Sohn dieses ruchlosen Menschen. Dabei fassen wir kurz zusammen, welches Unheil aus den kriegerischen Verwicklungen entstand." },
      { number: 11, text: "Als Eupator seine Herrschaft antrat, ernannte er einen Mann namens Lysias zum Reichsverweser; außerdem machte er ihn zum Befehlshaber über Zölesyrien und Phönizien und zum Oberkommandierenden über die dort liegenden Truppen.", footnotes: ["1 Makk 3,32f; 6,17"] },
      { number: 12, text: "Ptolemäus, mit dem Zunamen Makron, hatte als erster den Juden Gerechtigkeit widerfahren lassen, weil ihnen so viel Unrecht geschehen war; er hatte versucht, den Streit mit ihnen friedlich beizulegen.", footnotes: ["4,45"] },
      { number: 13, text: "Seine Vertrauten verklagten ihn deswegen bei Eupator. Schon vorher hatte er allerorten als Verräter gegolten; denn er hatte Zypern, das ihm von Philometor anvertraut worden war, verlassen und war zu Antiochus Epiphanes übergelaufen. Da er seine ehrenvolle Stellung nicht mehr in Ehren verwalten konnte, machte er seinem Leben durch Gift ein Ende." },
      { number: 14, text: "Gorgias wurde Befehlshaber in dieser Gegend; er warb Söldner an und führte Krieg gegen die Juden, wo er nur konnte.", heading: "Die Kämpfe mit den Idumäern und mit Timotheus: 10,14-38", footnotes: ["1 Makk 3,38"] },
      { number: 15, text: "Außer ihm machten auch die Idumäer, die eine Reihe gut gelegener Festungen besaßen, den Juden zu schaffen. Sie hatten Flüchtlinge aus Jerusalem bei sich aufgenommen und begannen, Krieg zu führen.", footnotes: ["1 Makk 5,3"] },
      { number: 16, text: "Die Männer um den Makkabäer hielten einen Bittgottesdienst ab und baten Gott, ihr Bundesgenosse zu sein. Dann berannten sie die Festungen der Idumäer." },
      { number: 17, text: "Sie eroberten sie im Sturm, schlugen die Verteidiger auf den Mauern in die Flucht und machten jeden nieder, der ihnen in die Hände fiel. Dabei töteten sie mindestens zwanzigtausend Mann." },
      { number: 18, text: "Nicht weniger als neuntausend Mann konnten sich in zwei stark befestigte Burgen flüchten, die für eine Belagerung mit allem aufs Beste ausgerüstet waren.", footnotes: ["1 Makk 5,4f"] },
      { number: 19, text: "Der Makkabäer zog ab, da seine Anwesenheit an einem anderen Ort dringend notwendig war. Doch ließ er Simeon und Josef zurück, ferner Zachäus mit seinen Leuten; sie reichten aus, um die Burgen zu belagern." },
      { number: 20, text: "Simeons Soldaten nahmen jedoch aus Habgier Bestechungsgelder an und ließen für siebzigtausend Drachmen einige Leute aus den Burgen entweichen." },
      { number: 21, text: "Sobald der Makkabäer davon erfuhr, rief er die Führer des Volkes zusammen und erhob Anklage: Man habe die Brüder um Geld verkauft, da man zu deren Schaden die Feinde freigelassen habe." },
      { number: 22, text: "Er ließ die Verräter hinrichten und eroberte die beiden Burgen auf der Stelle." },
      { number: 23, text: "Mit seinen Waffen hatte er überall Erfolg und so vernichtete er in den beiden Festungen über zwanzigtausend Mann." },
      { number: 24, text: "Timotheus - derselbe, den die Juden schon einmal geschlagen hatten sammelte ein großes Söldnerheer und brachte auch ziemlich viel Reiterei aus der Provinz Asien zusammen. Damit rückte er an, um Judäa zu erobern.", footnotes: ["1 Makk 5,6f; 2 Makk 8,30; 12,10.21-25"] },
      { number: 25, text: "Als er schon in der Nähe war, riefen die Männer des Makkabäers Gott um Hilfe an, streuten sich Erde auf das Haupt und zogen Bußgewänder an." },
      { number: 26, text: "Sie warfen sich vor den Stufen des Brandopferaltars nieder und baten den, der ihnen wieder gnädig war, ihren Feinden feindlich gesinnt zu sein und ihren Widersachern zu widerstehen, wie das Gesetz sagt.", footnotes: ["Ex 23,22"] },
      { number: 27, text: "Nach diesem Gebet nahmen sie ihre Waffen, verließen die Stadt und rückten Timotheus entgegen. Als sie sich den Feinden näherten, hielten sie sich zunächst zurück." },
      { number: 28, text: "Sobald aber die Sonne aufging und es hell wurde, stießen die beiden Heere aufeinander. Die einen hatten als Bürgschaft für einen glücklichen Sieg neben ihrer Tapferkeit nur ihr Gottvertrauen; die anderen ließen sich im Kampf durch ihre wilde Wut treiben." },
      { number: 29, text: "Schon war die Schlacht heftig entbrannt, da erschienen den Kämpfenden vom Himmel her fünf herrliche Reiter auf goldgezäumten Pferden und stellten sich an die Spitze der Juden." },
      { number: 30, text: "Zwei von ihnen nahmen den Makkabäer in ihre Mitte, deckten ihn mit ihren Rüstungen und schützten ihn vor jeder Verwundung; auf die Feinde aber schossen sie Pfeile und Blitze. Diese wurden geblendet und flohen verwirrt nach allen Seiten." },
      { number: 31, text: "So kamen zwanzigtausendfünfhundert Mann und sechshundert Reiter um." },
      { number: 32, text: "Timotheus selbst flüchtete sich mit anderen in eine Festung namens Geser, die sehr stark befestigt war; ihr Kommandant war Chäreas." },
      { number: 33, text: "Die Truppen des Makkabäers belagerten vier Tage lang voll Begeisterung die Festung." },
      { number: 34, text: "Die Verteidiger, die sich in ihren Befestigungen sicher fühlten, führten lästerliche Reden und schrien freche Worte herunter." },
      { number: 35, text: "Am Morgen des fünften Tages packte zwanzig junge Männer aus dem Heer des Makkabäers der Zorn, als sie die Lästerreden hörten. Sie stürmten mutig auf die Mauer los und schlugen in wilder Wut jeden nieder, der sich ihnen in den Weg stellte." },
      { number: 36, text: "Andere umgingen die Stadt und erzwangen sich mit demselben Mut den Zugang zu den Verteidigern; sie legten Feuer an die Türme und steckten sie in Brand und verbrannten so die Lästerer bei lebendigem Leib. Wieder andere brachen die Tore auf, ließen die übrigen Truppen ein und eroberten die Stadt im Handstreich." },
      { number: 37, text: "Timotheus, der sich in einer Zisterne versteckt hatte, stachen sie nieder, ebenso seinen Bruder Chäreas und den Apollophanes." },
      { number: 38, text: "Darauf priesen sie mit Lob- und Dankliedern den Herrn, der so Großes für Israel getan und ihnen den Sieg geschenkt hatte." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Der Reichsverweser Lysias, des Königs Vormund und Verwandter, war über diese Ereignisse sehr verärgert.", heading: "Der Sieg des Judas über Lysias: 11,1-12", footnotes: ["(1-12) 1 Makk 4,26-35"] },
      { number: 2, text: "Er zog daher in kürzester Zeit an die achtzigtausend Mann zusammen, dazu seine ganze Reiterei, und marschierte gegen die Juden. Er hatte vor, aus der Stadt Jerusalem eine griechische Siedlung zu machen," },
      { number: 3, text: "das Heiligtum ebenso wie die heidnischen Kultstätten zu besteuern und das Amt des Hohenpriesters jedes Jahr für Geld auszuschreiben." },
      { number: 4, text: "Doch rechnete er überhaupt nicht mit der Macht Gottes, sondern stützte seine Überlegungen allein auf die Zehntausende von Fußtruppen, die Tausende von Reitern und seine achtzig Elefanten." },
      { number: 5, text: "Er drang in Judäa ein, rückte vor die Festung Bet-Zur, die hundertfünfzig Stadien von Jerusalem entfernt liegt, und bestürmte sie." },
      { number: 6, text: "Als die Leute des Makkabäers von der Belagerung der Festung erfuhren, flehten sie mit dem ganzen Volk unter Klagen und Weinen den Herrn an, er möge doch einen guten Engel schicken, um Israel zu retten.", footnotes: ["Ex 23,20"] },
      { number: 7, text: "Der Makkabäer griff als erster nach den Waffen und hielt eine anfeuernde Rede an die anderen: Sie sollten, zusammen mit ihm, die Gefahr auf sich nehmen und ihren Brüdern zu Hilfe eilen. Geschlossen und voll Kampfesmut brachen sie auf." },
      { number: 8, text: "Sie waren noch in der Nähe von Jerusalem, da erschien ihnen ein Reiter und zog in einem weißen Gewand und einer blinkenden goldenen Waffenrüstung vor ihnen her." },
      { number: 9, text: "Gemeinsam priesen sie den barmherzigen Gott und ihr Mut wurde so groß, dass sie bereit gewesen wären, nicht nur Menschen, sondern auch die wildesten Tiere und eiserne Mauern zusammenzuschlagen." },
      { number: 10, text: "Sie rückten mit ihrem himmlischen Bundesgenossen, zum Kampf bereit, vor; denn der Herr hatte Erbarmen mit ihnen." },
      { number: 11, text: "Sie stürzten sich wie Löwen auf die Feinde und erschlugen elftausend von ihnen, dazu sechzehnhundert Reiter. Alle Übrigen jagten sie in die Flucht;" },
      { number: 12, text: "die meisten davon waren verwundet und konnten nur das nackte Leben retten. Auch Lysias selbst konnte sich nur durch eine schimpfliche Flucht retten." },
      { number: 13, text: "Da Lysias jedoch nicht ohne Verstand war und über die erlittene Niederlage nachdachte, begriff er, dass die Hebräer unbesiegbar waren, weil der starke Gott auf ihrer Seite kämpfte.", heading: "Der Friedensschluss des Lysias mit den Juden: 11,13-38", footnotes: ["(13-38) 1 Makk 6,55-63"] },
      { number: 14, text: "Darum schickte er Unterhändler, die auf einen Friedensvertrag drängen und jeder annehmbaren Bedingung zustimmen sollten; er ließ sagen, er wolle auch dem König sehr zureden, mit ihnen Freundschaft zu schließen." },
      { number: 15, text: "Der Makkabäer ging auf alle Vorschläge des Lysias ein, ohne indes seinen Vorteil zu vernachlässigen. Alle Forderungen, die er für die Juden stellte und dem Lysias schriftlich vorlegte, nahm der König an." },
      { number: 16, text: "Lysias schrieb an die Juden einen Brief, der folgenden Inhalt hatte: Lysias grüßt das jüdische Volk." },
      { number: 17, text: "Eure Abgesandten Johanan und Abschalom haben euer Antwortschreiben vorgelegt, das unten beigefügt ist, und mich gebeten, die darin geäußerten Forderungen zu genehmigen." },
      { number: 18, text: "Was in die Zuständigkeit des Königs fällt, habe ich ihm dargelegt; er ist auf jede annehmbare Bedingung eingegangen." },
      { number: 19, text: "Wenn ihr weiterhin die Politik der Regierung wohlwollend aufnehmt, werde ich mich bemühen, euch Vorteile zu verschaffen." },
      { number: 20, text: "Was die Einzelheiten betrifft, habe ich euren und meinen Unterhändlern aufgetragen, mit euch zu verhandeln." },
      { number: 21, text: "Lebt wohl! Im Jahr 148, am Vierundzwanzigsten des Monats Dioskorus.", footnotes: ["Gemeint ist das Frühjahr 164 v. Chr."] },
      { number: 22, text: "Im Brief des Königs stand dies: König Antiochus grüßt seinen Bruder Lysias." },
      { number: 23, text: "Nachdem unser Vater zu den Göttern hinübergegangen ist, wollen wir, dass die Untertanen des Reichs ungestört ihren Beschäftigungen nachgehen können.", footnotes: ["Vergöttlichung und kultische Verehrung der Herrscher war bei den Seleuziden ebenso üblich wie bei den Lagiden in Ägypten."] },
      { number: 24, text: "Andererseits haben wir erfahren, dass die Juden mit der von meinem Vater gewünschten Übernahme griechischer Sitten nicht einverstanden sind, es vielmehr vorziehen, auf ihre eigene Art zu leben, und verlangen, dass man ihnen wieder gestattet, ihren Gewohnheiten zu folgen." },
      { number: 25, text: "Wir beschließen darum, dass auch dieses Volk ungestört bleibt, und verfügen, dass man ihnen ihr Heiligtum zurückgibt und dass sie ihr Leben so einrichten können, wie es schon zur Zeit ihrer Vorfahren Brauch war." },
      { number: 26, text: "Du wirst nun am besten zu ihnen Gesandte schicken und ihnen die Hand zum Frieden reichen, damit sie die Grundlinien unserer Politik erkennen, Vertrauen fassen und ihre Angelegenheiten zu ihrer Zufriedenheit regeln können." },
      { number: 27, text: "Der Brief des Königs an das Volk lautete so: König Antiochus grüßt den Rat und alle übrigen Juden." },
      { number: 28, text: "Wir hoffen, dass es euch gut geht; auch wir erfreuen uns guter Gesundheit." },
      { number: 29, text: "Menelaus hat uns wissen lassen, dass ihr nach Hause zu eurer Arbeit zurückkehren wollt." },
      { number: 30, text: "Jedem also, der bis zum dreißigsten Tag des Monats Xanthikus heimkehrt, wird Friede angeboten mit der Zusicherung," },
      { number: 31, text: "dass die Juden ihre gewohnten Speisevorschriften und Gesetze befolgen dürfen; keiner von ihnen darf dabei irgendwie belangt werden für Vergehen, die er in Unkenntnis der Verhältnisse begangen hat." },
      { number: 32, text: "Ich habe Menelaus zu eurer Beruhigung zu euch geschickt." },
      { number: 33, text: "Lebt wohl! Im Jahr 148, am Fünfzehnten des Monats Xanthikus." },
      { number: 34, text: "Auch die Römer schickten ihnen einen Brief; er hatte folgenden Inhalt: Die römischen Gesandten Quintus Memmius und Titus Manius grüßen das jüdische Volk." },
      { number: 35, text: "Auch wir sind einverstanden mit den Bedingungen, die Lysias, der Verwandte des Königs, mit euch ausgehandelt hat." },
      { number: 36, text: "Lasst uns die Vorschläge, die er dem König unterbreiten will, zukommen, sobald ihr sie geprüft habt, damit wir eure Sache vortragen können, wie es für euch am günstigsten ist; wir sind nämlich auf dem Weg nach Antiochia." },
      { number: 37, text: "Teilt uns deswegen auch umgehend durch Boten eure Meinung darüber mit." },
      { number: 38, text: "Lebt wohl! Im Jahr 148, am Fünfzehnten des Monats Xanthikus." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Nach dem Abschluss der Verträge kehrte Lysias zum König zurück. Die Juden begannen, wieder ihre Felder zu bestellen.", heading: "Die Rache an den Judenverfolgern in Jafo und Jamnia: 12,1-9" },
      { number: 2, text: "Die Befehlshaber in jener Gegend aber, Timotheus, Apollonius, der Sohn des Gennäus, ferner Hieronymus, Demophon und auch Nikanor, der Statthalter von Zypern, ließen die Juden nicht in Ruhe und Frieden leben." },
      { number: 3, text: "Die Einwohner von Jafo begingen folgendes entsetzliche Verbrechen: Sie luden die Juden, die unter ihnen wohnten, ein, mit ihren Frauen und Kindern in Schiffe, die sie zur Verfügung stellten, einzusteigen. Sie taten, als ob sie nichts Böses gegen sie im Schild führten," },
      { number: 4, text: "sondern aufgrund eines öffentlichen Beschlusses der Stadt handelten. Die Juden nahmen das Angebot an, da sie den Frieden wollten und keinen bösen Verdacht hegten. Aber als sie auf dem offenen Meer waren, wurden die Boote versenkt; mindestens zweihundert Menschen ertranken." },
      { number: 5, text: "Als Judas von der schrecklichen Tat gegen die Angehörigen seines Volkes erfuhr, gab er sie seinen Männern bekannt," },
      { number: 6, text: "rief Gott, den gerechten Richter, an und überfiel die Mörder, die sich mit dem Blut seiner Brüder befleckt hatten. In der Nacht zündete er den Hafen an, verbrannte die Schiffe und stach alle nieder, die sich dorthin geflüchtet hatten." },
      { number: 7, text: "Die Stadt selbst hatte ihre Tore verrammelt. So musste er abziehen, aber er nahm sich vor, wiederzukommen und die ganze Stadt Jafo zu vernichten." },
      { number: 8, text: "Als er erfuhr, dass die Bürger von Jamnia mit den Juden, die bei ihnen lebten, dasselbe tun wollten," },
      { number: 9, text: "überfiel er auch die Leute von Jamnia bei Nacht und steckte den Hafen samt der Flotte in Brand. Der Feuerschein war zweihundertvierzig Stadien weit bis nach Jerusalem zu sehen." },
      { number: 10, text: "Von dort zogen sie weiter gegen Timotheus. Sie waren gerade neun Stadien weit marschiert, da griffen fünftausend Araber mit fünfhundert Reitern Judas an.", heading: "Die erfolgreichen Feldzüge des Judas jenseits des Jordan: 12,10-31", footnotes: ["(10-31) 1 Makk 5,24-54"] },
      { number: 11, text: "Nach hitzigem Kampf gewannen die Männer des Judas die Oberhand; denn Gott half ihnen. Die bedrängten Nomaden baten Judas um Frieden, versprachen, ihm und seinen Leuten Vieh zu liefern und ihnen auch sonst gute Dienste zu erweisen." },
      { number: 12, text: "Da Judas der Ansicht war, ihre Hilfe könne ihnen tatsächlich bei vielen Gelegenheiten von Nutzen sein, war er damit einverstanden, mit ihnen Frieden zu schließen. Die Araber verpflichteten sich durch Handschlag und zogen sich zu ihren Zelten zurück." },
      { number: 13, text: "Judas griff auch eine Stadt namens Kaspin an; sie war ringsum mit Wällen und Mauern befestigt. Ihre Bevölkerung setzte sich aus Menschen verschiedenster Völker zusammen." },
      { number: 14, text: "Weil sie sich auf ihre hohen Mauern und auf ihre Vorräte verließen, wurden sie immer unverschämter gegen die Leute des Judas, beschimpften sie, lästerten Gott und schrien unflätige Worte." },
      { number: 15, text: "Die Männer des Judas riefen den großen Herrn der Welt an, der Jericho zur Zeit Josuas ohne Rammböcke und Belagerungsmaschinen zu Boden geschmettert hatte. Dann stürmten sie mit dem Mut von Löwen gegen die Mauern an,", footnotes: ["Jos 6,20"] },
      { number: 16, text: "und weil Gott es so wollte, konnten sie die Stadt einnehmen. Sie richteten in ihr ein unbeschreibliches Blutbad an, sodass ein zwei Stadien breiter See, der neben der Stadt lag, von dem Blut, das in ihn geflossen war, angefüllt zu sein schien." },
      { number: 17, text: "Dann marschierten sie siebenhundertfünfzig Stadien weiter, bis sie zum Charax kamen, wo die sogenannten Tubianer-Juden wohnten." },
      { number: 18, text: "Timotheus trafen sie dort nicht an; denn er war, ohne etwas unternommen zu haben, von dort abgezogen. Aber er hatte in einem stark befestigten Ort eine Garnison zurückgelassen." },
      { number: 19, text: "Dositheus und Sosipater, zwei Offiziere aus dem Stab des Makkabäers, rückten aus und töteten die über zehntausend Mann starke Besatzung, die Timotheus in der Festung zurückgelassen hatte." },
      { number: 20, text: "Dann teilte der Makkabäer sein Heer in mehrere Gruppen auf, übertrug jeweils einem seiner Offiziere den Befehl über eine Gruppe und zog in Eilmärschen gegen Timotheus, der über hundertzwanzigtausend Mann an Fußtruppen und zweitausendfünfhundert Reiter verfügte." },
      { number: 21, text: "Als Timotheus vom Anmarsch des Judas erfuhr, schickte er Frauen, Kinder und den übrigen Tross in eine Stadt namens Karnajim voraus. Diesen Ort konnte man kaum belagern, da er wegen zahlreicher Engpässe fast unzugänglich war." },
      { number: 22, text: "Kaum zeigte sich die erste Abteilung des Judas, da gerieten die Feinde in Furcht und Schrecken, denn der, der alles überschaut, ließ eine Erscheinung sichtbar werden. Sie rannten in wilder Flucht davon, jeder in eine andere Richtung. Dabei fügten sie vielfach einander selbst Schaden zu, ja, sie durchbohrten sich gegenseitig mit den Spitzen ihrer Schwerter." },
      { number: 23, text: "Doch Judas setzte ihnen stürmisch nach und erschlug von den Verruchten gegen dreißigtausend Mann." },
      { number: 24, text: "Timotheus selbst fiel den Leuten des Dositheus und des Sosipater in die Hände. Er flehte mit schönen, doch falschen Worten, ihn unbehelligt freizulassen; die Eltern und Brüder sehr vieler Juden seien in der Gewalt seiner Leute und es könne sein, dass man auf sie keine Rücksicht nehme." },
      { number: 25, text: "Mit vielen Worten brachte er sie dazu, seinen Versprechungen, er werde diese Menschen unversehrt zurückgeben, zu glauben. Um die Brüder zu retten, ließen sie ihn laufen." },
      { number: 26, text: "Darauf zog Judas gegen Karnajim und das Heiligtum der Atargatis und erschlug fünfundzwanzigtausend Menschen.", footnotes: ["Atargatis ist die große syrische Göttin, vergleichbar der Astarte. Karnajim scheint das Heiligtum der gehörnten Astarte zu sein."] },
      { number: 27, text: "Als sie geschlagen und vernichtet waren, rückte Judas vor das stark befestigte Efron, dessen große Einwohnerschaft sich aus vielen Völkern zusammensetzte. Junge und kräftige Männer standen vor den Mauern und wehrten sich tapfer. In der Stadt selbst war eine große Anzahl von Wurfmaschinen mit Geschossen aufgestellt." },
      { number: 28, text: "Da riefen die Juden zum Herrn, der den Ansturm der Feinde mit Macht zermalmt; sie brachten die Stadt in ihre Gewalt und töteten in ihr etwa fünfundzwanzigtausend Menschen." },
      { number: 29, text: "Dann brachen sie auf und zogen in Eilmärschen von dort nach Skythopolis, das sechshundert Stadien von Jerusalem entfernt liegt.", footnotes: ["Skythopolis ist der griechische Name für Bet-Schean."] },
      { number: 30, text: "Die Juden, die dort wohnten, stellten jedoch den Einwohnern von Skythopolis das Zeugnis aus, sie seien freundlich zu ihnen und hätten ihnen in der bösen Zeit ihr Mitgefühl bewiesen." },
      { number: 31, text: "Da bedankten sie sich und forderten sie auf, auch in Zukunft dem jüdischen Volk wohlgesinnt zu bleiben. Darauf begaben sie sich nach Jerusalem, da das Wochenfest unmittelbar bevorstand." },
      { number: 32, text: "Nach dem sogenannten Pfingstfest zogen sie in Eilmärschen gegen Gorgias, den Befehlshaber der Idumäer.", heading: "Die bestrafte Untreue einiger Juden und das Sühnopfer für die Toten: 12,32-45", footnotes: ["10,14"] },
      { number: 33, text: "Er rückte mit dreitausend Fußsoldaten und vierhundert Reitern aus." },
      { number: 34, text: "Als sie zum Kampf antraten, fielen einige von den Juden." },
      { number: 35, text: "Dositheus, ein Reiter von den Leuten Bakenors, ein sehr starker Mann, packte Gorgias fest am Mantel und zog mit allen Kräften an ihm. Aber als er den Verfluchten lebendig gefangen nehmen wollte, preschte ein thrakischer Reiter heran und schlug ihm den Arm ab. Gorgias konnte sich nach Marescha flüchten." },
      { number: 36, text: "Die Leute Esris kämpften weiter bis zur Erschöpfung. Da rief Judas zum Herrn, er solle als ihr Bundesgenosse und Bahnbrecher im Kampf erscheinen." },
      { number: 37, text: "In der Sprache seiner Väter erhob er das Kriegsgeschrei und stimmte Preislieder an, drang unversehens auf die Leute des Gorgias ein und schlug sie in die Flucht." },
      { number: 38, text: "Daraufhin führte Judas das Heer in die Stadt Adullam. Als der siebte Tag der Woche anbrach, reinigten sie sich, wie es bei ihnen Brauch war, und begingen dort den Sabbat." },
      { number: 39, text: "Am nächsten Tag kamen die Leute des Judas, um die Leichen der Gefallenen zu überführen - es war inzwischen höchste Zeit geworden und sie inmitten ihrer Angehörigen in den Familiengräbern zu bestatten." },
      { number: 40, text: "Da entdeckten sie, dass alle Toten unter ihren Kleidern Amulette der Götter von Jamnia trugen, obwohl das den Juden vom Gesetz her verboten ist. Da wurde allen klar, dass die Männer deswegen gefallen waren,", footnotes: ["Dtn 7,25"] },
      { number: 41, text: "und sie priesen nun alle das Wirken des Herrn, des gerechten Richters, der das Verborgene ans Licht bringt." },
      { number: 42, text: "Anschließend hielten sie einen Bittgottesdienst ab und beteten, dass die begangene Sünde wieder völlig ausgelöscht werde. Der edle Judas aber ermahnte die Leute, sich von Sünden rein zu halten; sie hätten ja mit eigenen Augen gesehen, welche Folgen das Vergehen der Gefallenen gehabt habe." },
      { number: 43, text: "Er veranstaltete eine Sammlung, an der sich alle beteiligten, und schickte etwa zweitausend Silberdrachmen nach Jerusalem, damit man dort ein Sündopfer darbringe. Damit handelte er sehr schön und edel; denn er dachte an die Auferstehung." },
      { number: 44, text: "Hätte er nicht erwartet, dass die Gefallenen auferstehen werden, wäre es nämlich überflüssig und sinnlos gewesen, für die Toten zu beten." },
      { number: 45, text: "Auch hielt er sich den herrlichen Lohn vor Augen, der für die hinterlegt ist, die in Frömmigkeit sterben. Ein heiliger und frommer Gedanke! Darum ließ er die Toten entsühnen, damit sie von der Sünde befreit werden.", footnotes: ["In diesem Vers steht der einzige alttestamentliche Hinweis auf einen Zwischenzustand, der als Läuterungsort verstanden wird."] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Im Jahr 149 erfuhren die Leute des Judas, dass Antiochus Eupator mit einem großen Heer gegen Judäa marschierte.", heading: "Ein erfolgloser Angriff auf Jerusalem: 13,1-26", footnotes: ["(1-26) 1 Makk 6,28-63"] },
      { number: 2, text: "Er wurde begleitet von dem Reichsverweser Lysias, seinem Vormund. Jeder führte ein Heer von hundertzehntausend griechischen Fußsoldaten, fünftausenddreihundert Reitern, zweiundzwanzig Elefanten und dreihundert Sichelwagen." },
      { number: 3, text: "Ihnen schloss sich auch Menelaus an; er redete heuchlerisch auf den König ein, nicht um das Vaterland zu retten, sondern weil er glaubte, er habe Aussicht, wieder in sein Amt (als Hoherpriester) eingesetzt zu werden." },
      { number: 4, text: "Der höchste König über allen Königen aber ließ Antiochus zornig werden auf den Verruchten, und als Lysias andeutete, dieser sei an allem Unglück schuld, befahl er, ihn nach Beröa zu schaffen und dort umzubringen. So ist es Brauch in jener Gegend." },
      { number: 5, text: "Es gibt dort nämlich einen fünfzig Ellen hohen Turm, der mit glühender Asche gefüllt ist. In ihm ist eine drehbare Maschine angebracht, die sich nach allen Seiten schräg zur Asche neigt." },
      { number: 6, text: "Wer des Tempelraubes schuldig ist oder ein anderes schweres Verbrechen begangen hat, den stoßen dort alle in den Tod." },
      { number: 7, text: "Auf diese Weise fand der Gesetzesbrecher Menelaus den Tod. Nicht einmal Erde zum Begräbnis wurde ihm zuteil." },
      { number: 8, text: "Damit geschah ihm nur recht; denn er hatte viele Verbrechen gegen den Altar begangen, dessen Feuer und Asche heilig sind. Nun fand er in Asche den Tod." },
      { number: 9, text: "Der König aber hatte das Herz eines Barbaren; er begann, den Juden noch weit Schlimmeres in Aussicht zu stellen als das, was zur Zeit seines Vaters geschehen war." },
      { number: 10, text: "Judas hörte davon und ließ dem Volk bekannt geben, man solle Tag und Nacht zum Herrn beten. Wenn je zu einer Zeit, dann müsse er ihnen jetzt helfen. Man wolle ihnen das Gesetz, das Vaterland und den heiligen Tempel rauben." },
      { number: 11, text: "Gerade erst habe das Volk ein wenig aufatmen können; Gott möge doch nicht zulassen, dass sie den ruchlosen Heiden in die Hände fielen." },
      { number: 12, text: "Drei Tage lang lagen sie ohne Unterbrechung auf den Knien und flehten unter Tränen und Fasten gemeinsam den barmherzigen Herrn an. Judas sprach ihnen Mut zu; dann gab er ihnen den Befehl, sich bereitzuhalten." },
      { number: 13, text: "Auf einer geheimen Sitzung der Ältesten ließ er den Beschluss fassen, man solle, bevor der König in Judäa eindringen und die Stadt in seine Gewalt bringen könne, ausrücken und die Sache mit Gottes Hilfe entscheiden." },
      { number: 14, text: "Er stellte den Ausgang dem Schöpfer der Welt anheim und feuerte seine Männer an, bis zum Tod tapfer für Gesetze, Heiligtum, Stadt, Vaterland und Verfassung zu kämpfen. Dann schlug er bei Modeïn ein Lager auf." },
      { number: 15, text: "Er gab seinen Truppen als Losung die Worte: Gottes Sieg. Mit den besten seiner jungen Krieger, die er eigens ausgewählt hatte, überfiel er nachts das Zelt des Königs, erschlug etwa zweitausend Mann und erstach den Leitelefanten des Königs mitsamt seinem Treiber." },
      { number: 16, text: "Als sie schließlich das ganze Lager in Furcht und Verwirrung gebracht hatten, zogen sie sich siegreich wieder zurück." },
      { number: 17, text: "Bei Tagesanbruch war alles vorbei; der Herr hatte ihnen mit seiner schützenden Macht beigestanden." },
      { number: 18, text: "Nachdem der König diese Kostprobe jüdischer Tapferkeit erhalten hatte, versuchte er, das Land mit Hilfe von List zu besetzen." },
      { number: 19, text: "Er rückte gegen die starke jüdische Festung Bet-Zur vor, wurde aber zurückgeschlagen; dann stieß er wieder vor und wurde wieder geschlagen." },
      { number: 20, text: "Judas ließ die Besatzung mit allem versorgen, was sie brauchte." },
      { number: 21, text: "Ein jüdischer Soldat namens Rhodokus aber verriet den Feinden die militärischen Geheimnisse. Er wurde verhört, festgenommen und eingekerkert." },
      { number: 22, text: "Dann verhandelte der König zum zweiten Mal mit der Besatzung von Bet-Zur, schloss mit ihnen einen Vertrag und zog ab. Dabei stieß er auf das Heer des Judas und erlitt eine Schlappe." },
      { number: 23, text: "Da erfuhr er, dass Philippus in Antiochia abgefallen sei; er hatte ihn dort zurückgelassen, um die Regierungsgeschäfte zu führen. Der König war sehr niedergeschlagen. Er sprach freundlich mit den Juden, gab ihnen nach, sicherte ihnen eidlich die Erfüllung aller gerechten Ansprüche zu, versöhnte sich mit ihnen und brachte ein Opfer dar, ehrte den Tempel und bewies dem Ort sein Wohlwollen." },
      { number: 24, text: "Für den Makkabäer gab er einen Empfang. Als Befehlshaber über das ganze Gebiet Ptolemaïs bis zu den Gerrenern ließ er den Hegemonides zurück" },
      { number: 25, text: "und begab sich dann nach Ptolemaïs. Die Einwohner dieser Stadt waren nämlich über die Verträge sehr verärgert; sie hatten sich heftig beschwert und wollten die Abmachungen rückgängig machen." },
      { number: 26, text: "Lysias trat auf die Rednerbühne, gab Rechenschaft, so gut er konnte, überredete, beschwichtigte, stimmte die Leute um und brach dann nach Antiochia auf. So verliefen der Anmarsch und der Abzug des Königs." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Drei Jahre später erfuhren die Leute des Judas, dass Demetrius, der Sohn des Seleukus, mit starken Truppen und einer Flotte im Hafen von Tripolis gelandet sei.", heading: "Der Feldzug Nikanors: 14,1 - 15,36 | Die Intrige des Hohenpriesters Alkimus gegen Judas: 14,1-14", footnotes: ["(1-14) 1 Makk 7,1-26"] },
      { number: 2, text: "Antiochus und dessen Vormund Lysias habe er aus dem Weg räumen lassen und das Land in seine Gewalt gebracht." },
      { number: 3, text: "Damals lebte ein Mensch namens Alkimus; er war früher einmal Hoherpriester gewesen, hatte sich aber schon vor der Zeit der Religionsvermischung freiwillig unrein gemacht. Wie er wohl wusste, hatte er sich dadurch in eine ausweglose Lage gebracht, sodass er nie wieder an den heiligen Altar treten konnte.", footnotes: ["Alkimus hatte die griechische Lebensweise angenommen."] },
      { number: 4, text: "Darum kam er um das Jahr 151 zu König Demetrius und überreichte ihm einen goldenen Kranz mit einem Palmzweig, dazu Ölzweige, wie sie am Heiligtum gebräuchlich sind. An diesem Tag unternahm er weiter nichts." },
      { number: 5, text: "Als aber Demetrius ihn vor seinen Rat rufen ließ und ihn fragte, wie die Stimmung unter den Juden sei und welche Pläne sie hätten, ergriff er die Gelegenheit, um sie für sein eigenes wahnwitziges Vorhaben zu nutzen. Er antwortete:" },
      { number: 6, text: "Es gibt unter den Juden Leute, die sich Hasidäer nennen; sie stehen unter der Führung des Makkabäers Judas. Sie hetzen zu Krieg und Aufruhr und lassen das Reich nicht zur Ruhe kommen." },
      { number: 7, text: "Dadurch wurde mir meine ehrenvolle Stellung geraubt, die mir aufgrund meiner Abstammung zukommt, nämlich das Amt des Hohenpriesters. Nun bin ich hierher gekommen," },
      { number: 8, text: "einmal weil ich der königlichen Sache aufrichtig ergeben bin, dann aber auch aus Sorge für meine Mitbürger. Denn der Unverstand der eben genannten Leute hat unser ganzes Volk in nicht geringes Elend gestürzt." },
      { number: 9, text: "Wenn du, mein König, dir einen genauen Überblick über die Lage verschafft hast, dann sorge für das Land und für unser bedrängtes Volk; du bist ja gegen jedermann freundlich und wohlgesinnt." },
      { number: 10, text: "Solange aber Judas noch lebt, kann es im Reich keinen Frieden geben." },
      { number: 11, text: "Kaum hatte er das vorgebracht, da hetzten die anderen Vertrauten des Königs, die den Bestrebungen des Judas feindlich gegenüberstanden, Demetrius noch mehr auf." },
      { number: 12, text: "Dieser berief sofort Nikanor, dem er das Kommando über die Elefanten gegeben hatte, und setzte ihn zum Befehlshaber über Judäa ein. Er schickte ihn weg" },
      { number: 13, text: "mit dem schriftlichen Auftrag, Judas zu beseitigen, seine Anhänger zu zerstreuen, Alkimus aber als Hohenpriester über den allerhöchsten Tempel einzusetzen." },
      { number: 14, text: "Dem Nikanor schlossen sich in Scharen die Heiden an, die vor Judas aus Judäa geflohen waren; denn sie glaubten, das Unglück und Missgeschick der Juden werde ihnen Glück bringen." },
      { number: 15, text: "Als die Juden vom Anmarsch Nikanors erfuhren und hörten, dass zusammen mit ihm die Heiden anrückten, streuten sie sich Erde auf das Haupt und flehten Gott an, der sein Volk für ewige Zeiten geschaffen hat und sich immer vor aller Welt sichtbar seines Erbteils annimmt.", heading: "Die Freundschaft zwischen Nikanor und Judas: 14,15-25", footnotes: ["(15-25) 1 Makk 7,27-29"] },
      { number: 16, text: "Auf Beschluss ihres Anführers brachen sie sofort auf und trafen bei dem Dorf Dessau auf die Feinde.", footnotes: ["1 Makk 7,31f"] },
      { number: 17, text: "Simeon, der Bruder des Judas, war zwar schon mit Nikanor zusammengestoßen und hatte sich langsam zurückziehen müssen, weil sein Gegner ihn völlig unvermutet überrascht hatte." },
      { number: 18, text: "Aber Nikanor kam zu Ohren, wie tapfer die Männer des Judas seien und wie mutig sie für ihr Vaterland kämpften; darum wich er der Entscheidung in offener Schlacht aus." },
      { number: 19, text: "Er schickte statt dessen Posidonius, Theodotus und Mattatias, um Friedensverhandlungen aufzunehmen." },
      { number: 20, text: "Nach langen Überlegungen legte der Anführer den Leuten seine Ansicht dar. Es zeigte sich, dass man einer Meinung war, und so billigte man die Verträge." },
      { number: 21, text: "Man verabredete einen Termin, um sich unter Ausschluss der Öffentlichkeit an einem bestimmten Ort zu treffen. Von jeder Seite fuhr ein Wagen vor und man stellte Stühle auf." },
      { number: 22, text: "Auf Anordnung des Judas hielten sich Bewaffnete in günstiger Stellung bereit, damit die Feinde nicht heimtückisch überraschend einen Anschlag verüben konnten. Doch die Unterredung verlief ohne Zwischenfälle." },
      { number: 23, text: "Nikanor hielt sich in Jerusalem auf, ohne irgendwelchen Anstoß zu erregen; er entließ die Anhänger, die ihm massenweise zugeströmt waren." },
      { number: 24, text: "Auch hatte er Judas ständig um sich und war ihm herzlich zugetan." },
      { number: 25, text: "Er redete ihm zu, er solle heiraten und eine Familie gründen. So heiratete Juda und es ging ihm gut und er freute sich seines Lebens." },
      { number: 26, text: "Dem Alkimus blieb nicht verborgen, dass die beiden einander wohlgesinnt waren. Er verschaffte sich die abgeschlossenen Verträge, begab sich zu Demetrius und hinterbrachte ihm, Nikanor handle dem Staatswohl zuwider: Er habe Judas, den Feind seines Reiches, zum Nachfolger bestimmt.", heading: "Die Entzweiung zwischen Judas und Nikanor: 14,26-36", footnotes: ["(26-36) 1 Makk 7,30-38"] },
      { number: 27, text: "Der König war sehr aufgebracht und gereizt durch die Verleumdungen dieses Schurken. Er schrieb an Nikanor, dass er die Verträge für untragbar halte, und befahl ihm, den Makkabäer auf der Stelle gefesselt nach Antiochia zu schaffen." },
      { number: 28, text: "Nikanor geriet beim Empfang dieser Nachricht in große Bestürzung. Es war ihm sehr zuwider, die Vereinbarungen zu brechen; denn Judas hatte ja kein Unrecht getan." },
      { number: 29, text: "Da er sich aber dem König nicht widersetzen konnte, wartete er auf eine günstige Gelegenheit, um seinen Auftrag mit Hilfe einer List ausführen zu können." },
      { number: 30, text: "Doch der Makkabäer merkte, dass Nikanor ihm gegenüber immer abweisender wurde und dass seine gewohnte Freundlichkeit sich abkühlte. Er wusste, dass diese Schroffheit nichts Gutes bedeuten könne. Darum rief er eine Anzahl von seinen Leuten zusammen und versteckte sich mit ihnen vor Nikanor." },
      { number: 31, text: "Als Nikanor dahinter kam, dass ihn der Mann geschickt überlistet hatte, begab er sich zum allerhöchsten, heiligen Tempel, gerade als die Priester die vorgeschriebenen Opfer darbrachten, und befahl ihnen, Judas auszuliefern." },
      { number: 32, text: "Als diese unter Eid versicherten, sie wüssten nicht, wo sich der Gesuchte zur Zeit aufhalte," },
      { number: 33, text: "erhob er die rechte Hand gegen den Tempel und schwor: Wenn ihr mir Judas nicht gefesselt herausgebt, werde ich dieses Gotteshaus dem Erdboden gleichmachen, den Brandopferaltar niederreißen und an seiner Stelle dem Dionysos einen herrlichen Tempel errichten." },
      { number: 34, text: "Nach diesen Worten ging er weg. Die Priester erhoben ihre Hände zum Himmel und riefen zu dem, der immer unser Volk beschützt. Sie beteten:" },
      { number: 35, text: "Herr, du bist auf nichts angewiesen; dennoch hat es dir gefallen, einen Tempel bauen zu lassen, in dem du unter uns wohnst." },
      { number: 36, text: "Nun, heiliger Herr, von dem alle Heiligung ausgeht, bewahre dieses Haus, das vor kurzem erst entsühnt wurde, unbefleckt in Ewigkeit." },
      { number: 37, text: "Unter den Ältesten der Stadt Jerusalem gab es einen Mann namens Rasi. Er war seinen Mitbürgern freundlich zugetan, stand in hohem Ansehen und hieß wegen seiner Güte Vater der Juden. Dieser Mann wurde bei Nikanor angezeigt.", heading: "Der Tod Rasis: 14,37-46" },
      { number: 38, text: "Er hatte sich nämlich schon vor der Zeit der Religionsvermischung offen für das Judentum entschieden und sich dafür bis zum Äußersten mit Leib und Leben eingesetzt." },
      { number: 39, text: "Nikanor beschloss, seine Abneigung gegen die Juden sichtbar zu bekunden, und schickte über fünfhundert Soldaten aus, um ihn verhaften zu lassen." },
      { number: 40, text: "Er glaubte nämlich, durch seine Festnahme den Juden einen schweren Schlag zu versetzen." },
      { number: 41, text: "Schon waren die Truppen dabei, den Turm zu besetzen; sie versuchten, sich den Eingang durch das Hoftor mit Gewalt zu erzwingen, und riefen nach Feuer, um die Türen in Brand zu setzen. Rasi war von allen Seiten umzingelt. Da stürzte er sich in das Schwert;", footnotes: ["1 Sam 31,4"] },
      { number: 42, text: "denn er wollte lieber in Ehren sterben als den Verruchten in die Hände fallen und eine schimpfliche Behandlung erfahren, die seiner edlen Herkunft unwürdig war." },
      { number: 43, text: "In der Hast aber hatte er sich nicht sofort tödlich getroffen; die Männer stürmten bereits durch die Türen herein. Da lief er mutig hinauf auf die Mauer und stürzte sich entschlossen auf die Menge hinab." },
      { number: 44, text: "Weil diese sofort zurückwich, entstand ein freier Raum und er fiel mitten auf den leeren Platz." },
      { number: 45, text: "Doch er lebte immer noch; in höchster Erregung erhob er sich, während das Blut in Strömen aus seinen schrecklichen Wunden schoss, durchbrach im Laufschritt die Menge und stellte sich auf einen steil abfallenden Felsen." },
      { number: 46, text: "Fast schon verblutet, riss er sich die Eingeweide aus dem Leib, packte sie mit beiden Händen und schleuderte sie auf die Leute hinunter; dabei rief er den Herrn über Leben und Tod an, er möge sie ihm wiedergeben. So starb er.", footnotes: ["7,11.14.23.29.36", "Selbstmord kommt in der Bibel nur in ganz aussichtslosen Lagen vor (vgl. 1 Sam 31,4)."] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Nikanor erfuhr, dass Judas sich mit seinen Männern in der Gegend von Samaria aufhielt; da beschloss er, sie ohne jede Gefahr am Ruhetag anzugreifen.", heading: "Nikanors Lästerungen: 15,1-5" },
      { number: 2, text: "Aber die Juden, die gezwungen waren, ihn zu begleiten, sagten: Bring sie doch nicht um, als seist du ein wildes Tier oder ein Barbar. Halte den Tag in Ehren, der von dem, der die ganze Welt überblickt, in besonderer Weise mit Heiligkeit ausgezeichnet wurde." },
      { number: 3, text: "Der dreimal Verfluchte fragte, ob es im Himmel wirklich einen Herrscher gebe, der befohlen habe, den Sabbat zu begehen." },
      { number: 4, text: "Sie bekannten: Der lebendige Herr selbst, der Herrscher im Himmel, hat angeordnet, den Sabbat zu halten.", footnotes: ["Ex 20,8-11; Dtn 5,12-15"] },
      { number: 5, text: "Da erwiderte er: Und ich bin der Herrscher auf der Erde; ich befehle, die Waffen zu ergreifen und zu tun, was das Staatswohl verlangt. Dennoch gelang es ihm nicht, seinen verbrecherischen Plan auszuführen." },
      { number: 6, text: "Nikanor, der in seinem Stolz seinen Kopf hoch trug, hatte beschlossen, öffentlich ein Denkmal seines Sieges über die Männer des Judas zu errichten.", heading: "Die Ermutigung der Kämpfer durch Judas: 15,6-24" },
      { number: 7, text: "Der Makkabäer aber hörte nicht auf, sein Vertrauen und all seine Hoffnung auf die Hilfe des Herrn zu setzen." },
      { number: 8, text: "Er ermahnte seine Männer, sich vor den anrückenden Heiden nicht zu fürchten. Sie sollten daran denken, wie der Himmel ihnen in der Vergangenheit geholfen habe; auch jetzt dürften sie vom Allherrscher den Sieg erwarten." },
      { number: 9, text: "Mit Worten aus dem Gesetz und aus den Propheten flößte er ihnen Mut ein, erinnerte sie auch an die Kämpfe, die sie schon bestanden hatten, und stärkte so ihren Kampfesmut." },
      { number: 10, text: "Nachdem er ihre Begeisterung geweckt hatte, spornte er sie noch weiter an, indem er sie daran erinnerte, wie die Heiden die Verträge nicht gehalten und ihre Schwüre gebrochen hatten." },
      { number: 11, text: "So wappnete er jeden von ihnen, nicht mit der Sicherheit, die Schild und Lanze verleihen, sondern mit dem Mut, den rechte Worte entfachen. Auch erzählte er ihnen einen überaus glaubwürdigen Traum, der alle sehr erfreute." },
      { number: 12, text: "Er hatte folgendes gesehen: Ihm war der frühere Hohepriester Onias erschienen, ein edler und gerechter Mann, bescheiden im Umgang, von gütigem Wesen und besonnen im Reden, von Kindheit an in allem aufs Gute bedacht; dieser breitete seine Hände aus und betete für das ganze jüdische Volk.", footnotes: ["3,1"] },
      { number: 13, text: "In gleicher Haltung erschien dann ein Mann mit grauem Haar, von herrlicher Gestalt; der Glanz einer wunderbaren, überwältigenden Hoheit ging von ihm aus." },
      { number: 14, text: "Onias begann zu reden und sagte: Das ist der Freund seiner Brüder, der viel für das Volk und die heilige Stadt betet, Jeremia, der Prophet Gottes.", footnotes: ["Jeremia ist beispielhaft durch sein Fürbittgebet (vgl. Jer 7,16; 11,14; 14,11; 15,11; 18,20; 37,3; 42,2.12)."] },
      { number: 15, text: "Dann streckte Jeremia die rechte Hand aus und übergab ihm ein goldenes Schwert; dabei sagte er:" },
      { number: 16, text: "Nimm das heilige Schwert, das Gott dir schenkt. Mit ihm wirst du die Feinde schlagen." },
      { number: 17, text: "Die Worte des Judas gaben ihnen Zuversicht; denn sie waren sehr schön und hatten die Kraft, zur Tapferkeit anzuspornen und die Jugend mit männlichem Mut zu erfüllen. Man beschloss, kein Lager zu beziehen, sondern kühn anzugreifen und mit allem Mut im Kampf Mann gegen Mann die Entscheidung herbeizuführen; denn die Stadt, die Religion und das Heiligtum seien in Gefahr." },
      { number: 18, text: "Sie fürchteten weniger um Frauen und Kinder, um Brüder und Verwandte als um die Heiligkeit des Tempels." },
      { number: 19, text: "Die in der Stadt Zurückgebliebenen waren hauptsächlich in Angst wegen des Angriffs draußen auf offenem Feld." },
      { number: 20, text: "Alle warteten, dass die Entscheidung falle. Schon hatten die Feinde sich vereinigt und ihr Heer rückte geordnet vor; die Elefanten wurden aufgestellt, wo es am günstigsten war; die Reiterei verteilte sich auf die Flügel." },
      { number: 21, text: "Der Makkabäer sah, wie die Massen der Feinde dastanden, die verschiedenen Waffengattungen, die zur Wildheit gereizten Tiere. Da erhob er seine Hände zum Himmel und rief zum Herrn, der Wunder vollbringt. Denn er wusste, dass es nicht auf die Waffen ankommt, sondern dass denen der Sieg zufällt, die Gott für würdig erachtet." },
      { number: 22, text: "Dabei sprach er dieses Gebet: Unser Herrscher, du hast zu Hiskija, dem König von Juda, deinen Engel gesandt und er erschlug im Heer Sanheribs hundertfünfundachtzigtausend Mann.", footnotes: ["2 Kön 19,35"] },
      { number: 23, text: "Herrscher im Himmel, sende auch jetzt einen guten Engel vor uns her, damit er Furcht und Schrecken verbreitet." },
      { number: 24, text: "Vor deinem gewaltigen Arm sollen alle erschrecken, die lästernd gegen dein heiliges Volk heranziehen. So sprach er." },
      { number: 25, text: "Die Truppen Nikanors rückten mit Trompetengeschmetter und Kampfliedern vor.", heading: "Die Niederlage und der Tod Nikanors: 15,25-36", footnotes: ["(25-36) 1 Makk 7,39-49"] },
      { number: 26, text: "Die Leute des Judas dagegen griffen die Feinde unter Beten und Flehen an." },
      { number: 27, text: "Mit den Händen kämpften sie, im Herzen beteten sie zu Gott. Mindestens fünfunddreißigtausend Mann streckten sie zu Boden, hocherfreut, dass Gott sich so sichtbar offenbarte." },
      { number: 28, text: "Schon war der Kampf beendet und sie wollten voller Freude aufbrechen, da entdeckten sie Nikanor, der in seiner Rüstung erschlagen dalag." },
      { number: 29, text: "Es gab ein großes Geschrei und Getümmel und sie priesen den Herrn in der Sprache ihrer Väter." },
      { number: 30, text: "Judas, der immer mit Leib und Seele für seine Mitbürger gestritten hatte, der von Jugend an seinen Landsleuten herzlich zugetan war, gab den Befehl, ihm den Kopf und eine Hand mitsamt dem Arm abzuschlagen und nach Jerusalem zu bringen." },
      { number: 31, text: "Sobald er dort angekommen war, rief er seine Landsleute zusammen und befahl den Priestern, sich vor dem Altar aufzustellen; auch schickte er Boten zu der Besatzung der Burg." },
      { number: 32, text: "Dann zeigte er den Kopf des Verbrechers Nikanor und die Hand des Lästerers, die dieser prahlend gegen das heilige Haus des Allherrschers ausgestreckt hatte." },
      { number: 33, text: "Die Zunge des ruchlosen Nikanor ließ er herausschneiden, zerstückeln und den Vögeln zum Fraß vorwerfen; den Arm ließ er vor dem Tempel aufhängen als Zeichen des bestraften Wahnsinns." },
      { number: 34, text: "Alle priesen den Herrn im Himmel, der seine Macht so sichtbar gezeigt hatte, und riefen: Gepriesen sei der, der seinen Ort vor der Entweihung bewahrt hat." },
      { number: 35, text: "Den Kopf Nikanors hängte Judas an die Burg als ein für alle sichtbares und offenkundiges Zeichen der Hilfe des Herrn." },
      { number: 36, text: "In einer öffentlichen Abstimmung beschlossen alle einstimmig, diesen Tag nicht ohne Feier vergehen zu lassen, sondern ihm einen besonderen Rang zu verleihen; es ist der dreizehnte Tag des zwölften Monats, der auf aramäisch Adar heißt, der Tag vor dem Mordechai-Tag.", footnotes: ["Est 9,19.31; 10,3k"] },
      { number: 37, text: "Das waren die Ereignisse, die mit Nikanor zusammenhingen. Seit jener Zeit blieb die Stadt im Besitz der Hebräer. Darum höre ich hier mit der Erzählung auf.", heading: "Nachwort: 15,37-39", footnotes: ["1 Makk 7,27-50", "die Stadt: die Heilige Stadt Jerusalem mit dem Berg Zion als Zentrum."] },
      { number: 38, text: "Ist sie gut und geschickt erzählt, habe ich mein Ziel erreicht; ist sie aber schlecht oder mittelmäßig - ich habe mein Bestes getan." },
      { number: 39, text: "Es ist gleich ungesund, unvermischten Wein oder pures Wasser zu trinken. Wein mit Wasser vermischt hingegen schmeckt vorzüglich. Ähnlich hängt es auch vom Aufbau der Erzählung ab, ob sie den Geist des Lesers erfreut, dem dieses Buch in die Hände kommt. Damit will ich schließen." },
    ] },
  ],
};
