import { Book } from "@/lib/types";

export const _2samuel: Book = {
  id: "2samuel",
  name: "2. Samuel",
  shortName: "2.Sam",
  testament: "old",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Als David nach dem Tod Sauls von seinem Sieg über die Amalekiter zurückgekehrt war und sich zwei Tage lang in Ziklag aufgehalten hatte,", heading: "König David: 1,1 - 9,13 | Sauls Tod und Davids Klage: 1,1-27" },
      { number: 2, text: "kam am dritten Tag ein Mann aus dem Lager Sauls, mit zerrissenen Kleidern und Staub auf dem Haupt. Als er bei David angelangt war, warf er sich (vor ihm) auf den Boden nieder und huldigte ihm." },
      { number: 3, text: "David fragte ihn: Woher kommst du? Er antwortete ihm: Ich habe mich aus dem Lager Israels gerettet." },
      { number: 4, text: "David sagte zu ihm: Wie stehen die Dinge? Berichte mir! Er erwiderte: Das Volk ist aus dem Kampf geflohen, viele von den Männern sind gefallen und umgekommen; auch Saul und sein Sohn Jonatan sind tot.", footnotes: ["1 Sam 31,1-4"] },
      { number: 5, text: "David fragte den jungen Mann, der ihm die Nachricht brachte: Woher weißt du, dass Saul und sein Sohn Jonatan tot sind?" },
      { number: 6, text: "Der junge Mann, der ihm die Nachricht brachte, sagte: Ich kam zufällig auf das Gebirge von Gilboa; da sah ich, wie sich Saul auf seinen Speer stützte und Kriegswagen und Reiter auf ihn eindrangen." },
      { number: 7, text: "Er wandte sich um, und als er mich sah, rief er mich. Ich antwortete: Hier bin ich." },
      { number: 8, text: "Er fragte mich: Wer bist du? Ich gab ihm zur Antwort: Ich bin ein Amalekiter." },
      { number: 9, text: "Da sagte er zu mir: Komm her zu mir und töte mich! Denn mich hat ein Schwächeanfall erfasst, aber noch ist alles Leben in mir." },
      { number: 10, text: "Ich ging also zu ihm hin und tötete ihn; denn ich wusste, dass er seine Niederlage nicht überleben würde. Dann nahm ich den Stirnreif, den er auf dem Kopf trug, und die Spange, die er am Arm hatte, und bringe sie nun hierher zu meinem Herrn." },
      { number: 11, text: "Da fasste David sein Gewand und zerriss es und ebenso (machten es) alle Männer, die bei ihm waren." },
      { number: 12, text: "Sie klagten, weinten und fasteten bis zum Abend wegen Saul, seines Sohnes Jonatan, des Volkes des Herrn und des Hauses Israel, die unter dem Schwert gefallen waren." },
      { number: 13, text: "Und David fragte den jungen Mann, der ihm die Nachricht gebracht hatte: Woher bist du? Er antwortete: Ich bin der Sohn eines Einwanderers aus Amalek." },
      { number: 14, text: "David fragte ihn: Wie kommt es, dass du dich nicht davor gefürchtet hast, deine Hand auszustrecken, um den Gesalbten des Herrn umzubringen?", footnotes: ["1 Sam 24,7; 26,9"] },
      { number: 15, text: "Darauf rief David einen von seinen jungen Männern zu sich und sagte: Komm her, stoß ihn nieder! Und er schlug ihn tot.", footnotes: ["(15-16) 4,10.22"] },
      { number: 16, text: "David aber sagte zu ihm: Dein Blut über dein Haupt; denn dein Mund hat dich verurteilt, als du sagtest: Ich habe den Gesalbten des Herrn getötet." },
      { number: 17, text: "Und David sang die folgende Totenklage auf Saul und seinen Sohn Jonatan;" },
      { number: 18, text: "er sagte, man solle es die Söhne Judas als Bogenlied lehren; es steht im «Buch des Aufrechten»:", footnotes: ["Jos 10,13", "Die Überschrift des Lieds ist schwer deutbar."] },
      { number: 19, text: "Israel, dein Stolz liegt erschlagen auf deinen Höhen. / Ach, die Helden sind gefallen!" },
      { number: 20, text: "Meldet es nicht in Gat, / verkündet es nicht auf Aschkelons Straßen, / damit die Töchter der Philister sich nicht freuen, / damit die Töchter der Unbeschnittenen nicht jauchzen." },
      { number: 21, text: "Ihr Berge in Gilboa, kein Tau und kein Regen / falle auf euch, ihr trügerischen Gefilde. / Denn dort wurde der Schild der Helden befleckt, / der Schild des Saul, als wäre er nicht mit Öl gesalbt.", footnotes: ["ihr trügerischen Gefilde: Text korr.; H: ihr Gefilde der Erstlingsgarben."] },
      { number: 22, text: "Ohne das Blut von Erschlagenen, / ohne das Mark der Helden / kam der Bogen Jonatans nie zurück; / auch das Schwert Sauls / kehrte niemals erfolglos zurück." },
      { number: 23, text: "Saul und Jonatan, die Geliebten und Teuren, / im Leben und Tod sind sie nicht getrennt. / Sie waren schneller als Adler, / waren stärker als Löwen." },
      { number: 24, text: "Ihr Töchter Israels, um Saul müsst ihr weinen; / er hat euch in köstlichen Purpur gekleidet, / hat goldenen Schmuck auf eure Gewänder geheftet." },
      { number: 25, text: "Ach, die Helden sind gefallen mitten im Kampf. / Jonatan liegt erschlagen auf deinen Höhen." },
      { number: 26, text: "Weh ist mir um dich, mein Bruder Jonatan. / Du warst mir sehr lieb. / Wunderbarer war deine Liebe für mich / als die Liebe der Frauen.", footnotes: ["1 Sam 18,3"] },
      { number: 27, text: "Ach, die Helden sind gefallen, / die Waffen des Kampfes verloren." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Danach befragte David den Herrn: Soll ich in eine der Städte Judas hinaufziehen? Der Herr antwortete ihm: Zieh hinauf! David fragte: Wohin soll ich ziehen? Er antwortete: Nach Hebron.", heading: "David wird König von Juda: 2,1-11" },
      { number: 2, text: "David zog also nach Hebron mit seinen beiden Frauen, Ahinoam aus Jesreel und Abigajil, der (früheren) Frau Nabals aus Karmel.", footnotes: ["1 Sam 25,42f"] },
      { number: 3, text: "Auch die Männer, die bei ihm waren, führte David hinauf, jeden mit seiner Familie, und sie ließen sich in den Städten um Hebron nieder." },
      { number: 4, text: "Dann kamen die Männer Judas (nach Hebron) und salbten David dort zum König über das Haus Juda. Als man David berichtete, dass die Einwohner von Jabesch-Gilead Saul begraben hatten,", footnotes: ["5,3; 1 Sam 16,13; 1 Sam 31,12f"] },
      { number: 5, text: "schickte er Boten zu den Männern von Jabesch-Gilead und ließ ihnen sagen: Ihr sollt vom Herrn gesegnet sein, weil ihr Saul, eurem Herrn, dieses Wohlwollen erwiesen und ihn begraben habt." },
      { number: 6, text: "Möge der Herr euch jetzt auch sein Wohlwollen und seine Treue erweisen; auch ich will euch dafür Gutes tun, dass ihr das getan habt." },
      { number: 7, text: "Nun aber zeigt euren Mut und seid tapfer; denn Saul, euer Herr, ist tot, mich aber hat das Haus Juda zu seinem König gesalbt." },
      { number: 8, text: "Doch Abner, der Sohn Ners, der Heerführer Sauls, hatte Ischbaal, den Sohn Sauls, mit sich nach Mahanajim genommen", footnotes: ["1 Sam 14,50", "Ischbaal: so lautet der Name richtig (vgl. 1 Chr 8,39; 9,33); H verändert den Namen, um den darin enthaltenen Gottesnamen Baal verächtlich zu machen, in Isch-Boschet (Mann der Schande); vgl. auch 4,4."] },
      { number: 9, text: "und ihn (dort) zum König über Gilead, Ascher, Jesreel, Efraim und Benjamin und über ganz Israel gemacht." },
      { number: 10, text: "Sauls Sohn Ischbaal war vierzig Jahre alt, als er König von Israel wurde, und er war zwei Jahre König; nur das Haus Juda stand hinter David.", footnotes: ["4,5"] },
      { number: 11, text: "Die Zeit, die David in Hebron König über das Haus Juda war, betrug sieben Jahre und sechs Monate." },
      { number: 12, text: "Abner, der Sohn Ners, zog mit den Knechten Ischbaals, des Sohnes Sauls, von Mahanajim nach Gibeon.", heading: "Der Krieg zwischen David und Ischbaal: 2,12 - 3,1" },
      { number: 13, text: "Auch Joab, der Sohn der Zeruja, und die Knechte Davids waren ausgezogen; sie trafen mit ihnen am Teich von Gibeon zusammen. Die einen blieben diesseits, die anderen jenseits des Teiches.", footnotes: ["Joab, der Sohn Zerujas, der Schwester Davids (1 Chr 2,16), war der Heerführer Davids (vgl. 8,16)."] },
      { number: 14, text: "Da sagte Abner zu Joab: Die jungen Männer könnten doch zu einem Kampfspiel vor uns antreten. Joab erwiderte: Sie sollen antreten." },
      { number: 15, text: "Sie stellten sich also auf und es wurde abgezählt: zwölf für Benjamin und Ischbaal, den Sohn Sauls, und zwölf von den Leuten Davids." },
      { number: 16, text: "Jeder fasste seinen Gegner am Kopf und stieß ihm das Schwert in die Seite, sodass alle miteinander fielen. Deshalb nannte man diesen Ort bei Gibeon: Helkat-Hazzurim (Feld der Steinmesser)." },
      { number: 17, text: "Der Kampf wurde an jenem Tag sehr hart. Abner und die Israeliten wurden von den Leuten Davids geschlagen." },
      { number: 18, text: "Auch die drei Söhne der Zeruja waren dabei: Joab, Abischai und Asaël. Asaël war so flink auf den Beinen wie eine Gazelle im Gelände." },
      { number: 19, text: "Asaël jagte Abner nach, ohne nach rechts oder links abzuweichen." },
      { number: 20, text: "Da wandte sich Abner um und sagte: Bist du es, Asaël? Asaël antwortete: Ja, ich bin es." },
      { number: 21, text: "Abner sagte zu ihm: Bieg doch nach rechts oder links ab, greif dir einen von den jungen Männern und nimm dir seine Rüstung! Asaël aber wollte nicht von ihm ablassen." },
      { number: 22, text: "Abner sagte noch einmal zu Asaël: Lass von mir ab! Warum soll ich dich zu Boden schlagen? Wie könnte ich dann noch deinem Bruder Joab in die Augen blicken?" },
      { number: 23, text: "Als Asaël sich weigerte, von ihm abzulassen, stieß ihm Abner das (stumpfe) Ende des Speers in den Bauch, sodass der Speer hinten wieder herauskam. Asaël stürzte zu Boden und starb auf der Stelle. Alle aber, die an den Ort kamen, wo Asaël niedergestürzt und gestorben war, blieben stehen." },
      { number: 24, text: "Joab und Abischai jedoch jagten Abner nach. Als die Sonne unterging, waren sie bis Gibeat-Amma gelangt, das Giach gegenüber am Weg in die Steppe von Gibeon liegt." },
      { number: 25, text: "Die Benjaminiter in der Gefolgschaft Abners sammelten sich, bildeten eine geschlossene Gruppe und stellten sich auf dem Gipfel eines Hügels auf." },
      { number: 26, text: "Abner rief Joab zu: Soll denn das Schwert unaufhörlich um sich fressen? Weißt du nicht, dass das zu einem bitteren Ende führen wird? Wann endlich wirst du deinen Leuten befehlen, die Verfolgung ihrer Brüder aufzugeben?" },
      { number: 27, text: "Joab antwortete: So wahr Gott lebt: Wenn du nichts gesagt hättest, dann hätten die Leute erst am Morgen den Rückzug angetreten und aufgehört, ihre Brüder zu verfolgen." },
      { number: 28, text: "Dann ließ Joab das Widderhorn blasen und alle (seine) Leute machten Halt; sie setzten den Kampf nicht mehr fort und verfolgten die Israeliten nicht mehr." },
      { number: 29, text: "Abner und seine Männer durchzogen während jener Nacht die Araba, überschritten den Jordan, gingen durch die Schlucht und kamen nach Mahanajim zurück." },
      { number: 30, text: "Als Joab aufhörte, Abner zu verfolgen, und seine Leute wieder sammelte, fehlten außer Asaël von den Leuten Davids neunzehn Mann." },
      { number: 31, text: "Davids Leute aber hatten von den Benjaminitern und von den Männern Abners dreihundertsechzig Mann erschlagen [die waren tot]." },
      { number: 32, text: "Sie nahmen Asaël mit und begruben ihn im Grab seines Vaters in Betlehem. Dann marschierte Joab mit seinen Männern die ganze Nacht hindurch in Richtung Hebron, das sie bei Tagesanbruch erreichten." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Der Krieg zwischen dem Haus Saul und dem Haus David zog sich lange hin. David wurde immer stärker, während das Haus Saul immer schwächer wurde.", footnotes: ["5,10"] },
      { number: 2, text: "In Hebron wurden David folgende Söhne geboren: Sein Erstgeborener Amnon stammte von Ahinoam aus Jesreel,", heading: "Die in Hebron geborenen Söhne Davids: 3,2-5", footnotes: ["(2-5) 5,13-16; 1 Chr 3,1-4"] },
      { number: 3, text: "sein zweiter, Kilab, von Abigajil, der (früheren) Frau Nabals aus Karmel; der dritte war Abschalom, der Sohn der Maacha, der Tochter des Königs Talmai von Geschur," },
      { number: 4, text: "der vierte Adonija, der Sohn der Haggit, der fünfte Schefatja, der Sohn der Abital," },
      { number: 5, text: "der sechste Jitream von Davids Frau Egla. Diese Söhne wurden David in Hebron geboren." },
      { number: 6, text: "Solange zwischen dem Haus Saul und dem Haus David Krieg herrschte, hielt Abner entschieden zum Haus Saul.", heading: "David und Abner: 3,6-21" },
      { number: 7, text: "Saul hatte eine Nebenfrau namens Rizpa gehabt; sie war eine Tochter Ajas. Da sagte Ischbaal zu Abner: Warum bist du zu der Nebenfrau meines Vaters gegangen?", footnotes: ["21,8"] },
      { number: 8, text: "Da wurde Abner sehr zornig über die Frage Ischbaals und sagte: Bin ich denn ein Hundskopf aus Juda? Ich erweise dem Haus Sauls, deines Vaters, und seinen Brüdern und Verwandten auch heute mein Wohlwollen und habe dich nicht in die Hände Davids fallen lassen. Und du willst mich jetzt wegen eines Vergehens mit dieser Frau zur Rechenschaft ziehen?" },
      { number: 9, text: "Gott soll Abner dies und das antun, wenn ich nicht gegenüber David so handle, wie der Herr es ihm geschworen hat:" },
      { number: 10, text: "das Königtum dem Haus Saul zu nehmen und den Thron Davids aufzustellen in Israel und Juda, von Dan bis Beerscheba." },
      { number: 11, text: "Darauf konnte Ischbaal Abner nichts erwidern; denn er hatte Angst vor ihm." },
      { number: 12, text: "Abner schickte in eigener Sache Boten zu David und ließ ihm sagen: Wem gehört das Land? Schließ also einen Vertrag mit mir; dann werde ich dir helfen, um ganz Israel auf deine Seite zu bringen.", footnotes: ["in eigener Sache: Bedeutung des betreffenden hebräischen Wortes unsicher."] },
      { number: 13, text: "David antwortete: Gut, ich will einen Vertrag mit dir schließen; nur das eine verlange ich von dir: Du darfst mir nicht unter die Augen treten, falls du nicht Michal, die Tochter Sauls, mitbringst, wenn du kommst und vor mir erscheinst." },
      { number: 14, text: "Dann schickte David Boten zu Ischbaal, dem Sohn Sauls, und ließ ihm sagen: Gib meine Frau Michal heraus, für die ich die hundert Vorhäute der Philister als Brautpreis bezahlt habe.", footnotes: ["(14-15) 1 Sam 18,27; 25,44"] },
      { number: 15, text: "Ischbaal schickte (einen Boten) zu ihrem Mann Paltiël, dem Sohn des Lajisch, und ließ sie ihm wegnehmen." },
      { number: 16, text: "Ihr Mann lief bis nach Bahurim weinend hinter ihr her. Erst als Abner zu ihm sagte: Geh, kehr um!, kehrte er um." },
      { number: 17, text: "Darauf verhandelte Abner mit den Ältesten Israels und sagte: Schon seit langer Zeit wollt ihr David zu eurem König haben." },
      { number: 18, text: "Jetzt müsst ihr handeln. Denn der Herr hat zu David gesagt: Durch meinen Knecht David will ich mein Volk Israel aus der Gewalt der Philister und all seiner Feinde retten." },
      { number: 19, text: "Auch mit den Benjaminitern redete Abner (in dieser Weise). Dann ging Abner nach Hebron, um David alles zu berichten, was Israel und das ganze Haus Benjamin für gut befunden hatten." },
      { number: 20, text: "So kam Abner mit zwanzig Männern zu David nach Hebron. David veranstaltete für Abner und seine Begleiter ein Mahl." },
      { number: 21, text: "Abner sagte zu David: Ich will mich auf den Weg machen und ganz Israel um meinen Herrn, den König, sammeln. Sie sollen mit dir einen Vertrag schließen und du sollst überall König sein, wo du es wünschst. Damit ließ David Abner in Frieden ziehen." },
      { number: 22, text: "Inzwischen kamen die Leute Davids und Joab von einem Streifzug; sie brachten reiche Beute mit. Abner war nicht mehr bei David in Hebron; denn David hatte ihn in Frieden ziehen lassen.", heading: "Abners Tod: 3,22-39" },
      { number: 23, text: "Als Joab mit seiner ganzen Mannschaft ankam, meldete man ihm: Abner, der Sohn Ners, ist zum König gekommen und der König hat ihn wieder in Frieden ziehen lassen." },
      { number: 24, text: "Joab ging zum König und sagte zu ihm: Was hast du getan? Abner ist zu dir gekommen. Warum hast du ihn wieder weggehen lassen?" },
      { number: 25, text: "Du kennst doch Abner, den Sohn Ners. Er ist nur gekommen, um dich zu täuschen und zu erkunden, wann du kommst und wann du gehst, und so zu erfahren, was du alles vorhast." },
      { number: 26, text: "Als Joab David verlassen hatte, schickte er Boten hinter Abner her, die ihn von Bor-Sira zurückholten. David aber wusste nichts davon." },
      { number: 27, text: "Als Abner nach Hebron zurückkehrte, führte ihn Joab beiseite in das Innere des Tores, als wolle er ungestört mit ihm reden. Dort stach er ihn, um Blutrache für seinen Bruder Asaël zu nehmen, in den Bauch, sodass er starb.", footnotes: ["2,23"] },
      { number: 28, text: "Als David später davon hörte, sagte er: Ich und mein Königtum sind vor dem Herrn für alle Zeit ohne Schuld am Blut Abners, des Sohnes Ners.", footnotes: ["1 Kön 2,5"] },
      { number: 29, text: "Sie falle auf Joab und seine ganze Familie zurück. Immer soll es in Joabs Familie Menschen geben, die an Blutungen und Aussatz leiden, die an Krücken gehen, durch das Schwert umkommen und denen es an Brot mangelt.", footnotes: ["David, der zu schwach ist, um gegen den Mörder Joab vorzugehen (V. 39), begnügt sich mit einem Fluch."] },
      { number: 30, text: "Joab und sein Bruder Abischai brachten Abner um, weil er ihren Bruder Asaël bei Gibeon im Kampf getötet hatte.", footnotes: ["2,23"] },
      { number: 31, text: "David aber sagte zu Joab und allen Leuten, die bei ihm waren: Zerreißt eure Kleider, legt Trauergewänder an und geht klagend vor Abner her! König David selbst aber ging hinter der Bahre her." },
      { number: 32, text: "Man begrub Abner in Hebron. Und der König begann am Grab Abners laut zu weinen und auch das ganze Volk weinte." },
      { number: 33, text: "Der König stimmte die Totenklage für Abner an und sang: Musste Abner sterben, / wie ein schlechter Mensch stirbt?" },
      { number: 34, text: "Deine Hände waren nicht gefesselt / und deine Füße lagen nicht in Ketten. / Du bist gefallen, / wie man unter der Hand von Verbrechern fällt. Da weinten alle noch mehr um ihn." },
      { number: 35, text: "Als nun die Leute kamen, um David zum Essen zu bewegen, während es noch Tag war, schwor David: Gott soll mir dies und das antun, wenn ich Brot oder sonst etwas zu mir nehme, bevor die Sonne untergeht." },
      { number: 36, text: "Als das Volk das erfuhr, gefiel es ihm sehr, wie überhaupt alles, was der König tat, dem ganzen Volk gefiel." },
      { number: 37, text: "Alle Leute, auch ganz Israel, erkannten an jenem Tag, dass die Ermordung Abners, des Sohnes Ners, nicht vom König ausgegangen war." },
      { number: 38, text: "Und der König sagte zu seinen Dienern: Wisst ihr nicht, dass heute ein Fürst gefallen ist und ein großer Mann in Israel?" },
      { number: 39, text: "Obwohl ich zum König gesalbt worden bin, bin ich heute noch zu schwach und diese Männer, die Söhne der Zeruja, sind stärker als ich. Dem, der das Verbrechen begangen hat, vergelte der Herr so, wie es seiner bösen Tat entspricht.", footnotes: ["2,18"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Als Ischbaal, der Sohn Sauls, hörte, dass Abner in Hebron ums Leben gekommen war, verlor er allen Mut und ganz Israel war bestürzt.", heading: "Ischbaals Tod: 4,1-12", footnotes: ["3,27"] },
      { number: 2, text: "Der Sohn Sauls hatte zwei Männer als Truppenführer; der eine hieß Baana, der andere Rechab; sie waren Söhne Rimmons aus Beerot, Benjaminiter; denn auch Beerot wird zu Benjamin gerechnet." },
      { number: 3, text: "Die Beerotiter sind nämlich einst nach Gittajim geflohen und leben dort bis zum heutigen Tag als Fremde." },
      { number: 4, text: "Jonatan, der Sohn Sauls, hatte einen Sohn, der an beiden Füßen gelähmt war. Er war fünf Jahre alt gewesen, als die Nachricht vom Tod Sauls und Jonatans aus Jesreel eintraf; seine Amme hatte ihn aufgehoben, um mit ihm zu fliehen, aber in der Eile der Flucht war er ihr vom Arm gefallen; seitdem war er gelähmt. Sein Name war Merib-Baal.", footnotes: ["9,3", "Merib-Baal: So lautet der Name richtig (vgl. 1 Chr 8,34; 9,40); er wurde ähnlich wie der Name Ischbaal (vgl. Anm. zu 2,8) in H verunstaltet zu Mefiboschet."] },
      { number: 5, text: "Rechab und Baana, die Söhne Rimmons aus Beerot, machten sich auf den Weg und kamen um die heißeste Zeit des Tages zum Haus Ischbaals, der eben seine Mittagsruhe hielt.", footnotes: ["2,8"] },
      { number: 6, text: "Die Türhüterin des Hauses war beim Weizenreinigen schläfrig geworden und eingeschlafen;", footnotes: ["Text korr. nach G; in H nimmt V. 6, teilweise mit anderem Inhalt, V. 7 vorweg."] },
      { number: 7, text: "so konnten Rechab und sein Bruder Baana unbemerkt in das Haus eindringen. Ischbaal lag in seinem Schlafzimmer auf dem Bett. Sie schlugen ihn tot, hieben ihm den Kopf ab und nahmen den Kopf mit. Sie gingen die ganze Nacht hindurch auf dem Weg durch die Araba," },
      { number: 8, text: "brachten den Kopf Ischbaals zu David nach Hebron und sagten zum König: Da ist der Kopf deines Feindes Ischbaal, des Sohnes Sauls, der dir nach dem Leben trachtete. Aber der Herr hat heute unserem Herrn, dem König, Rache an Saul und seinen Nachkommen verschafft." },
      { number: 9, text: "David erwiderte Rechab und seinem Bruder Baana, den Söhnen Rimmons aus Beerot: So wahr der Herr lebt, der mich aus aller Not befreit hat:" },
      { number: 10, text: "Den, der mir die Nachricht gebracht hat: Saul ist tot!, und der gemeint hat, ein Freudenbote zu sein, den habe ich in Ziklag ergreifen und umbringen lassen; so gab ich ihm den verdienten Botenlohn.", footnotes: ["1,15"] },
      { number: 11, text: "Und wenn nun ruchlose Männer einen rechtschaffenen Mann in seinem Haus auf seinem Bett erschlagen haben, sollte ich dann nicht sein Blut von euch zurückfordern und euch von der Erde austilgen?" },
      { number: 12, text: "Und David gab seinen Leuten den Befehl, sie zu töten; sie taten es. Sie schlugen ihnen Hände und Füße ab und hängten die Leichen am Teich von Hebron auf. Den Kopf Ischbaals aber nahmen sie und begruben ihn im Grab Abners in Hebron." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Alle Stämme Israels kamen zu David nach Hebron und sagten: Wir sind doch dein Fleisch und Bein.", heading: "Die Anerkennung Davids als König von ganz Israel: 5,1-12", footnotes: ["19,13; Gen 2,23; (1-12) 1 Chr 11,1-10; 14,1f"] },
      { number: 2, text: "Schon früher, als noch Saul unser König war, bist du es gewesen, der Israel in den Kampf und wieder nach Hause geführt hat. Der Herr hat zu dir gesagt: Du sollst der Hirt meines Volkes Israel sein, du sollst Israels Fürst werden.", footnotes: ["1 Sam 13,14; 18,13.16; 25,30; 2 Sam 6,21; 7,8"] },
      { number: 3, text: "Alle Ältesten Israels kamen zum König nach Hebron; der König David schloss mit ihnen in Hebron einen Vertrag vor dem Herrn und sie salbten David zum König von Israel.", footnotes: ["2,4; 1 Sam 16,13"] },
      { number: 4, text: "David war dreißig Jahre alt, als er König wurde, und er regierte vierzig Jahre lang.", footnotes: ["(4-5) 1 Kön 2,11; 1 Chr 3,4; 29,27"] },
      { number: 5, text: "In Hebron war er sieben Jahre und sechs Monate König von Juda und in Jerusalem war er dreiunddreißig Jahre König von ganz Israel und Juda." },
      { number: 6, text: "Der König zog mit seinen Männern nach Jerusalem gegen die Jebusiter, die in dieser Gegend wohnten. Die Jebusiter aber sagten zu David: Du kommst hier nicht herein; die Blinden und Lahmen werden dich vertreiben. Das sollte besagen: David wird hier nicht eindringen.", footnotes: ["Jos 15,63; Ri 1,21"] },
      { number: 7, text: "Dennoch eroberte David die Burg Zion; sie wurde die Stadt Davids." },
      { number: 8, text: "David sagte an jenem Tag: Jeder, der den Schacht erreicht, soll die Jebusiter erschlagen, auch die Lahmen und Blinden, die David in der Seele verhasst sind. Daher sagt man: Ein Blinder und ein Lahmer kommt nicht ins Haus.", footnotes: ["Text schwer verständlich."] },
      { number: 9, text: "David ließ sich in der Burg nieder und nannte sie die Stadt Davids. Und David begann ringsum zu bauen, und zwar vom Millo an bis zur Burg.", footnotes: ["Millo: Stadtteil von Jerusalem westlich des Tempelbereichs. Der Name bedeutet «Auffüllung», «Aufschüttung» o. dgl."] },
      { number: 10, text: "David wurde immer mächtiger und der Herr, der Gott der Heere, war mit ihm.", footnotes: ["3,1"] },
      { number: 11, text: "Hiram, der König von Tyrus, schickte eine Gesandtschaft zu David und ließ ihm Zedernholz überbringen; auch Zimmerleute und Steinmetzen schickte er und sie bauten für David einen Palast." },
      { number: 12, text: "So erkannte David, dass der Herr ihn als König von Israel bestätigt hatte und dass der Herr sein Königtum wegen seines Volkes Israel zu hohem Ansehen gebracht hatte." },
      { number: 13, text: "Als David von Hebron gekommen war, nahm er sich noch Nebenfrauen, auch Frauen aus Jerusalem. So wurden ihm noch mehr Söhne und Töchter geboren.", heading: "Die in Jerusalem geborenen Söhne Davids: 5,13-16", footnotes: ["3,2-5; 1 Chr 3,5-9; (13-16) 1 Chr 14,3-7"] },
      { number: 14, text: "Das sind die Namen der Söhne, die ihm in Jerusalem geboren wurden: Schima, Schobab, Natan, Salomo," },
      { number: 15, text: "Jibhar, Elischua, Nefeg, Jafia," },
      { number: 16, text: "Elischama, Eljada und Elifelet." },
      { number: 17, text: "Die Philister hörten, dass man David zum König von Israel gesalbt hatte. Da zogen alle herauf, um David gefangen zu nehmen. David erfuhr davon und zog sich in eine Bergfestung zurück.", heading: "Der Krieg mit den Philistern: 5,17-25", footnotes: ["(17-25) 1 Chr 14,8-17"] },
      { number: 18, text: "Als die Philister herankamen und in der Rafaïterebene umherstreiften,", footnotes: ["23,13; 1 Sam 22,1", "Rafaïterebene: die Ebene südwestlich von Jerusalem."] },
      { number: 19, text: "befragte David den Herrn; er sagte: Soll ich die Philister angreifen? Wirst du sie in meine Hand geben? Der Herr antwortete David: Greif sie an; denn ich werde die Philister bestimmt in deine Hand geben." },
      { number: 20, text: "Da zog David nach Baal-Perazim. Dort schlug er die Philister und sagte: Der Herr hat die Reihen meiner Feinde vor meinen Augen durchbrochen, wie Wasser (einen Damm) durchbricht. Deshalb nennt man jenen Ort Baal-Perazim (Herr der Durchbrüche)." },
      { number: 21, text: "Die Philister ließen dort ihre Götterbilder zurück, sodass David und seine Männer sie mitnehmen konnten." },
      { number: 22, text: "Doch die Philister zogen noch einmal herauf und streiften in der Rafaïterebene umher." },
      { number: 23, text: "David befragte wieder den Herrn und der Herr antwortete ihm: Zieh nicht hinauf, umgeh sie in ihrem Rücken und komm von den Baka-Bäumen her an sie heran!", footnotes: ["Die Baka-Bäume sind wahrscheinlich Mastixsträucher, die im Gebirge Juda häufig vorkommen."] },
      { number: 24, text: "Wenn du dann in den Wipfeln der Baka-Bäume ein Geräusch wie von Schritten hörst, dann beeil dich; denn dann geht der Herr vor dir her, um das Heer der Philister zu schlagen." },
      { number: 25, text: "David tat, was der Herr ihm befohlen hatte, und er schlug die Philister (im ganzen Gebiet) zwischen Geba und der Gegend von Geser." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "David versammelte wiederum alle jungen Krieger aus Israel, dreißigtausend Mann,", heading: "Die Überführung der Bundeslade nach Jerusalem: 6,1-23", footnotes: ["12-23: Vgl. 1 Chr 13,1-14"] },
      { number: 2, text: "brach auf und zog mit seinem ganzen Heer nach Baala in Juda, um von dort die Lade Gottes heraufzuholen, über der der Name des Herrn der Heere, der über den Kerubim thront, ausgerufen worden ist.", footnotes: ["1 Sam 6,21; 1 Chr 13,6; Ps 132,3-6", "nach Baala in Juda: Text korr. nach 1 Chr 13,6; H: von den Bürgern Judas."] },
      { number: 3, text: "Sie stellten die Lade Gottes auf einen neuen Wagen und holten sie so vom Haus Abinadabs, das auf einem Hügel stand; Usa und Achjo, die Söhne Abinadabs, lenkten den neuen Wagen", footnotes: ["Usa und Achjo sind wohl Enkel Abinadabs."] },
      { number: 4, text: "[sie holten sie also aus dem Haus Abinadabs, das auf dem Hügel stand,] mit der Lade Gottes und Achjo ging vor der Lade her." },
      { number: 5, text: "David und das ganze Haus Israel tanzten und sangen vor dem Herrn mit ganzer Hingabe und spielten auf Zithern, Harfen und Pauken, mit Rasseln und Zimbeln.", footnotes: ["mit ganzer Hingabe: Text korr. nach G und 1 Chr 13,8; H: mit Zypressenhölzern."] },
      { number: 6, text: "Als sie zur Tenne Nachons kamen, brachen die Rinder aus und Usa streckte seine Hand nach der Lade Gottes aus und fasste sie an." },
      { number: 7, text: "Da entbrannte der Zorn des Herrn gegen Usa und Gott erschlug ihn auf der Stelle wegen dieser Vermessenheit, sodass er neben der Lade Gottes starb.", footnotes: ["Num 4,20; 1 Sam 6,19"] },
      { number: 8, text: "David war sehr erregt darüber, dass der Herr den Usa so plötzlich dahingerafft hatte, und man nennt den Ort bis heute darum Perez-Usa (Wegraffung Usas).", footnotes: ["Der Ortsname wird durch die erzählte Begebenheit erklärt. Den Leviten war es unter Todesstrafe verboten, die Lade zu berühren (vgl. Num 4,15)."] },
      { number: 9, text: "An jenem Tag bekam David Angst vor dem Herrn und er sagte: Wie soll die Lade des Herrn jemals zu mir kommen?" },
      { number: 10, text: "Darum wollte David die Lade des Herrn nicht zu sich in die Davidstadt bringen lassen, sondern stellte sie in das Haus des Obed-Edom aus Gat." },
      { number: 11, text: "Drei Monate lang blieb die Lade des Herrn im Haus Obed-Edoms aus Gat, und der Herr segnete Obed-Edom und sein ganzes Haus." },
      { number: 12, text: "Als man König David berichtete: Der Herr hat das Haus Obed-Edoms und alles, was ihm gehört, um der Lade Gottes willen gesegnet, da ging David hin und brachte die Lade Gottes voll Freude aus dem Haus Obed-Edoms in die Davidstadt hinauf." },
      { number: 13, text: "Sobald die Träger der Lade des Herrn sechs Schritte gegangen waren, opferte er einen Stier und ein Mastkalb." },
      { number: 14, text: "Und David tanzte mit ganzer Hingabe vor dem Herrn her und trug dabei das leinene Efod.", footnotes: ["(14-19) 1 Chr 15,27f"] },
      { number: 15, text: "So brachten David und das ganze Haus Israel die Lade des Herrn unter Jubelgeschrei und unter dem Klang des Widderhorns hinauf." },
      { number: 16, text: "Als die Lade des Herrn in die Davidstadt kam, schaute Michal, Sauls Tochter, aus dem Fenster, und als sie sah, wie der König David vor dem Herrn hüpfte und tanzte, verachtete sie ihn in ihrem Herzen." },
      { number: 17, text: "Man trug die Lade des Herrn in das Zelt, das David für sie aufgestellt hatte, und setzte sie an ihren Platz in der Mitte des Zeltes und David brachte dem Herrn Brandopfer und Heilsopfer dar." },
      { number: 18, text: "Als David mit dem Darbringen der Brandopfer und Heilsopfer fertig war, segnete er das Volk im Namen des Herrn der Heere" },
      { number: 19, text: "und ließ an das ganze Volk, an alle Israeliten, Männer und Frauen, je einen Laib Brot, einen Dattelkuchen und einen Traubenkuchen austeilen. Dann gingen alle wieder nach Hause.", footnotes: ["(19-20) 1 Chr 16,43"] },
      { number: 20, text: "Als David zurückkehrte, um seine Familie zu begrüßen, kam ihm Michal, die Tochter Sauls, entgegen und sagte: Wie würdevoll hat sich heute der König von Israel benommen, als er sich vor den Augen der Mägde seiner Untertanen bloßgestellt hat, wie sich nur einer vom Gesindel bloßstellen kann." },
      { number: 21, text: "David erwiderte Michal: Vor dem Herrn, der mich statt deines Vaters und seines ganzen Hauses erwählt hat, um mich zum Fürsten über das Volk des Herrn, über Israel, zu bestellen, vor dem Herrn habe ich getanzt;", footnotes: ["5,2"] },
      { number: 22, text: "für ihn will ich mich gern noch geringer machen als diesmal und in meinen eigenen Augen niedrig erscheinen. Bei den Mägden jedenfalls, von denen du gesprochen hast, stehe ich in Ehren." },
      { number: 23, text: "Michal aber, die Tochter Sauls, bekam bis zu ihrem Tod kein Kind.", footnotes: ["Die Kinderlosigkeit Michals wird als Strafe für die Verspottung Davids verstanden."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Als nun der König in seinem Haus wohnte und der Herr ihm Ruhe vor allen seinen Feinden ringsum verschafft hatte,", heading: "Die Verheißung an David: 7,1-29", footnotes: ["(1-29) 1 Chr 17,1-27", "1-17: Die Natan-Verheißung bildet die Grundlage für das dauernde Bestehen der Daviddynastie und damit für die messianische Erwartung."] },
      { number: 2, text: "sagte er zu dem Propheten Natan: Ich wohne in einem Haus aus Zedernholz, die Lade Gottes aber wohnt in einem Zelt.", footnotes: ["Ps 132,1-5"] },
      { number: 3, text: "Natan antwortete dem König: Geh nur und tu alles, was du im Sinn hast; denn der Herr ist mit dir." },
      { number: 4, text: "Aber in jener Nacht erging das Wort des Herrn an Natan:" },
      { number: 5, text: "Geh zu meinem Knecht David und sag zu ihm: So spricht der Herr: Du willst mir ein Haus bauen, damit ich darin wohne?", footnotes: ["(5-6) 1 Kön 5,17; 8,16.27; 1 Chr 22,8; Jes 66,1"] },
      { number: 6, text: "Seit dem Tag, als ich die Israeliten aus Ägypten heraufgeführt habe, habe ich bis heute nie in einem Haus gewohnt, sondern bin in einer Zeltwohnung umhergezogen." },
      { number: 7, text: "Habe ich in der Zeit, als ich bei den Israeliten von Ort zu Ort zog, jemals zu einem der Richter Israels, die ich als Hirten über mein Volk Israel eingesetzt hatte, ein Wort gesagt und sie gefragt: Warum habt ihr mir kein Haus aus Zedernholz gebaut?", footnotes: ["Richter: Text korr. mit 1 Chr 17,6; H: Stämme."] },
      { number: 8, text: "Sag also jetzt meinem Knecht David: So spricht der Herr der Heere: Ich habe dich von der Weide und von der Herde weggeholt, damit du Fürst über mein Volk Israel wirst,", footnotes: ["5,2; 1 Sam 16,11-13"] },
      { number: 9, text: "und ich bin überall mit dir gewesen, wohin du auch gegangen bist. Ich habe alle deine Feinde vor deinen Augen vernichtet und ich will dir einen großen Namen machen, der dem Namen der Großen auf der Erde gleich ist.", footnotes: ["Gen 12,2"] },
      { number: 10, text: "Ich will meinem Volk Israel einen Platz zuweisen und es einpflanzen, damit es an seinem Ort (sicher) wohnen kann und sich nicht mehr ängstigen muss und schlechte Menschen es nicht mehr unterdrücken wie früher" },
      { number: 11, text: "und auch von dem Tag an, an dem ich Richter in meinem Volk Israel eingesetzt habe. Ich verschaffe dir Ruhe vor allen deinen Feinden. Nun verkündet dir der Herr, dass der Herr dir ein Haus bauen wird.", footnotes: ["1 Kön 8,20"] },
      { number: 12, text: "Wenn deine Tage erfüllt sind und du dich zu deinen Vätern legst, werde ich deinen leiblichen Sohn als deinen Nachfolger einsetzen und seinem Königtum Bestand verleihen." },
      { number: 13, text: "Er wird für meinen Namen ein Haus bauen und ich werde seinem Königsthron ewigen Bestand verleihen.", footnotes: ["1 Kön 5,19; 6,12; Ps 89,4f; Jes 9,6"] },
      { number: 14, text: "Ich will für ihn Vater sein und er wird für mich Sohn sein. Wenn er sich verfehlt, werde ich ihn nach Menschenart mit Ruten und mit Schlägen züchtigen.", footnotes: ["Ps 89,27; 2,7; Hebr 1,5; Lk 1,32"] },
      { number: 15, text: "Meine Huld aber soll nicht von ihm weichen, wie sie von Saul gewichen ist, den ich vor deinen Augen verstoßen habe.", footnotes: ["1 Sam 15,23.26"] },
      { number: 16, text: "Dein Haus und dein Königtum sollen durch mich auf ewig bestehen bleiben; dein Thron soll auf ewig Bestand haben." },
      { number: 17, text: "Natan sprach zu David genauso, wie es (ihm) gesagt und offenbart worden war." },
      { number: 18, text: "Da ging König David hin und setzte sich vor dem Herrn nieder und sagte: Wer bin ich, mein Herr und Gott, und was ist mein Haus, dass du mich bis hierher geführt hast?" },
      { number: 19, text: "Weil das in deinen Augen noch zu wenig war, mein Herr und Gott, hast du dem Haus deines Knechtes sogar Zusagen für die ferne Zukunft gemacht. Ist das eine Weisung, wie sie einem (schwachen) Menschen zukommt, mein Herr und Gott?" },
      { number: 20, text: "Was soll David noch weiter zu dir sagen? Du kennst deinen Knecht, mein Herr und Gott." },
      { number: 21, text: "Um deines Wortes willen und nach der Absicht deines Herzens hast du alle diese großen Taten getan und deinem Knecht offenbart." },
      { number: 22, text: "Darum bist du groß, mein Herr und Gott. Ja, keiner ist dir gleich, und außer dir gibt es keinen Gott nach allem, was wir mit unseren Ohren gehört haben." },
      { number: 23, text: "Welches andere Volk auf der Erde ist wie dein Volk Israel? Wo wäre ein Gott hingegangen, um ein Volk für sich als sein Volk freizukaufen und ihm einen Namen zu machen und für dieses Volk große und erstaunliche Taten zu vollbringen, so wie du ganze Völker und ihre Götter vertrieben hast vor den Augen deines Volkes, das du dir von den Ägyptern freigekauft hast?", footnotes: ["Text korr.; vgl. die alten Übersetzungen und 1 Chr 17,21."] },
      { number: 24, text: "Du hast Israel auf ewig zu deinem Volk bestimmt und du, Herr, bist sein Gott geworden." },
      { number: 25, text: "Doch nun, Herr und Gott, verleih dem Wort, das du über deinen Knecht und über sein Haus gesprochen hast, für immer Geltung und tu, was du gesagt hast." },
      { number: 26, text: "Dann wird dein Name groß sein für ewige Zeiten und man wird sagen: Der Herr der Heere ist Israels Gott!, und das Haus deines Knechtes David wird vor deinen Augen Bestand haben." },
      { number: 27, text: "Denn du, Herr der Heere, Gott Israels, hast deinem Knecht offenbart: Ich will dir ein Haus bauen. Darum fand dein Knecht den Mut, so zu dir zu beten:" },
      { number: 28, text: "Ja, mein Herr und Gott, du bist der einzige Gott und deine Worte sind wahr. Du hast deinem Knecht ein solches Glück zugesagt." },
      { number: 29, text: "So segne jetzt gnädig das Haus deines Knechtes, damit es ewig vor deinen Augen Bestand hat. Denn du, mein Herr und Gott, hast es versprochen und mit deinem Segen wird das Haus deines Knechtes für immer gesegnet sein." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Hernach schlug David die Philister. Er unterwarf sie und nahm ihnen die Zügel der Herrschaft aus der Hand.", heading: "Die Siege Davids: 8,1-14", footnotes: ["(1-14) 1 Chr 18,1-13", "die Zügel der Herrschaft, wörtlich: den Zaum des Unterarms."] },
      { number: 2, text: "Auch die Moabiter schlug er. Sie mussten sich nebeneinander auf die Erde legen und er maß die Reihe mit einer Messschnur ab: jeweils zwei Schnurlängen wurden getötet und jeweils eine volle Schnurlänge ließ er am Leben. So wurden die Moabiter David untertan und tributpflichtig.", footnotes: ["Nach 1 Sam 22,3 hatte David gute Beziehungen zu den Moabitern. Warum diese Bluttat geschah, ist unbekannt."] },
      { number: 3, text: "David schlug auch Hadad-Eser, den Sohn Rehobs, den König von Zoba, als dieser ausgezogen war, um seine Macht am Eufrat wiederzugewinnen.", footnotes: ["Zoba: ein aramäisches Stadtkönigtum nördlich von Damaskus."] },
      { number: 4, text: "David nahm von ihm siebzehnhundert Wagenkämpfer und zwanzigtausend Fußsoldaten gefangen; er ließ alle Wagenpferde lähmen und behielt nur hundert (für sich) zurück." },
      { number: 5, text: "Als die Aramäer von Damaskus Hadad-Eser, dem König von Zoba, zu Hilfe kamen, erschlug David 22000 von den Aramäern" },
      { number: 6, text: "und besetzte das Aramäerreich von Damaskus. So wurden die Aramäer David untertan und tributpflichtig. Der Herr half David bei allem, was er unternahm." },
      { number: 7, text: "David erbeutete die goldenen Schilde, die die Krieger Hadad-Esers trugen, und brachte sie nach Jerusalem." },
      { number: 8, text: "In Tebach und Berotai, den Städten Hadad-Esers, erbeutete der König David eine große Menge Bronze." },
      { number: 9, text: "Als Toï, der König von Hamat, hörte, dass David das ganze Heer Hadad-Esers geschlagen hatte,", footnotes: ["Hamat: eine Stadt am Orontes."] },
      { number: 10, text: "schickte er seinen Sohn Hadoram mit Gefäßen aus Gold, Silber und Bronze zu König David. Er ließ ihm den Friedensgruß entbieten und ihn dazu beglückwünschen, dass er Hadad-Eser im Krieg geschlagen hatte; Toï lebte nämlich im Krieg mit Hadad-Eser." },
      { number: 11, text: "König David weihte auch diese Gefäße dem Herrn, zusammen mit dem Gold und Silber, das er von all den Völkern, die er unterwarf, erbeutet hatte:", footnotes: ["erbeutet hatte: Text korr. nach 1 Chr 18,11; H: geheiligt hatte."] },
      { number: 12, text: "von Aram und Moab, von den Ammonitern, den Philistern, von Amalek, sowie die Beute von Hadad-Eser, dem Sohn Rehobs, dem König von Zoba." },
      { number: 13, text: "So machte sich David einen Namen. Als er nach dem Sieg über Aram zurückkehrte, schlug er Edom im Salztal, achtzehntausend Mann,", footnotes: ["Ps 60,2", "schlug er Edom: Text korr., vgl. G und 1 Chr 18,12."] },
      { number: 14, text: "und setzte in Edom Vögte ein. [In ganz Edom setzte er Vögte ein.] So wurde ganz Edom von David unterworfen. Der Herr half David bei allem, was er unternahm.", footnotes: ["Num 24,18"] },
      { number: 15, text: "David war König von ganz Israel und sorgte für Recht und Gerechtigkeit in seinem ganzen Volk.", heading: "Die Beamten Davids: 8,15-18", footnotes: ["(15-18) 20,23-26; 1 Chr 18,14-17"] },
      { number: 16, text: "Joab, der Sohn der Zeruja, war Heerführer und Joschafat, der Sohn Ahiluds, war Sprecher des Königs." },
      { number: 17, text: "Zadok, der Sohn Ahitubs, und Abjatar, der Sohn Ahimelechs, waren Priester, Seraja war Staatsschreiber.", footnotes: ["1 Sam 22,20; 1 Kön 2,27", "Abjatar, der Sohn Ahimelechs: Text korr. nach S und 1 Sam 22,20; 23,6; 30,7; H: Ahimelech, der Sohn Abjatars."] },
      { number: 18, text: "Benaja, der Sohn Jojadas, war der Befehlshaber der Kereter und Peleter. Auch die Söhne Davids waren Priester.", footnotes: ["der Befehlshaber: Text korr. nach 20,23. - Die Kereter und Peleter sind ausländische Söldner im Dienst Davids (vgl. 15,18; 20,7.23; 1 Kön 1,38.44)."] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "David sagte: Gibt es noch jemand, der vom Haus Saul übrig geblieben ist? Ich will ihm um Jonatans willen eine Huld erweisen.", heading: "Jonatans Sohn: 9,1-13" },
      { number: 2, text: "Zum Haus Sauls gehörte ein Knecht namens Ziba; man rief ihn zu David und der König fragte ihn: Bist du Ziba? Er antwortete: Ja, dein Knecht.", footnotes: ["16,1"] },
      { number: 3, text: "Der König fragte ihn: Ist vom Haus Sauls niemand mehr am Leben, dass ich ihm göttliche Huld erweisen könnte? Ziba antwortete dem König: Es gibt einen Sohn Jonatans, der an beiden Füßen gelähmt ist.", footnotes: ["4,4"] },
      { number: 4, text: "Der König sagte zu ihm: Wo ist er? Ziba antwortete dem König: Er lebt jetzt im Haus Machirs, des Sohnes Ammiëls, in Lo-Dabar.", footnotes: ["Lo-Dabar: im Ostjordanland."] },
      { number: 5, text: "Da schickte der König David hin und ließ ihn aus dem Haus Machirs, des Sohnes Ammiëls, in Lo-Dabar holen.", footnotes: ["17,27"] },
      { number: 6, text: "Als Merib-Baal, der Sohn Jonatans, des Sohnes Sauls, zu David kam, warf er sich (vor ihm) auf sein Gesicht nieder und huldigte ihm. David sagte: Merib-Baal! Er antwortete: Hier ist dein Knecht.", footnotes: ["19,25"] },
      { number: 7, text: "David sagte zu ihm: Fürchte dich nicht; denn ich will dir um deines Vaters Jonatan willen eine Huld erweisen: Ich gebe dir alle Felder deines Großvaters Saul zurück und du sollst immer an meinem Tisch essen.", footnotes: ["19,29"] },
      { number: 8, text: "Da warf sich Merib-Baal (vor ihm) nieder und sagte: Was ist dein Knecht, dass du dich einem toten Hund zuwendest, wie ich es bin?" },
      { number: 9, text: "Der König David rief Ziba, den Diener Sauls, und sagte zu ihm: Alles, was Saul und seiner ganzen Familie gehört hat, gebe ich dem Sohn deines Herrn.", footnotes: ["(9-11) 19,29f"] },
      { number: 10, text: "Du sollst mit deinen Söhnen und Knechten für ihn das Land bebauen und ihm den Ertrag bringen, damit der Sohn deines Herrn zu essen hat. Merib-Baal selbst aber, der Sohn deines Herrn, soll immer an meinem Tisch essen. Ziba hatte fünfzehn Söhne und zwanzig Knechte." },
      { number: 11, text: "Ziba sagte zum König: Genauso, wie es mein Herr, der König, seinem Knecht befohlen hat, wird es dein Knecht tun. Und Merib-Baal aß am Tisch Davids wie einer von den Söhnen des Königs." },
      { number: 12, text: "Merib-Baal hatte einen kleinen Sohn namens Micha und alle, die im Haus Zibas wohnten, waren Merib-Baals Knechte." },
      { number: 13, text: "Merib-Baal blieb also in Jerusalem; denn er aß immer am Tisch des Königs; er war aber an beiden Füßen gelähmt." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Als darauf der König der Ammoniter starb und sein Sohn Hanun an seiner Stelle König wurde,", heading: "Die Geschichte von Davids Thronfolge: 10,1 - 20,26; 1 Kön 1,1 - 2,46 | Der Krieg mit den Ammonitern und den Aramäern: 10,1-19", footnotes: ["(1-19) 1 Chr 19,1-19"] },
      { number: 2, text: "sagte David: Ich will Hanun, dem Sohn des Nahasch, mein Wohlwollen zeigen, so wie sein Vater mir sein Wohlwollen gezeigt hat. Und David schickte eine Gesandtschaft zu ihm und ließ ihm durch seine Diener sein Beileid zum Tod seines Vaters aussprechen. Als die Diener in das Land der Ammoniter kamen,", footnotes: ["1 Sam 11,1"] },
      { number: 3, text: "sagten die Fürsten der Ammoniter zu Hanun, ihrem Herrn: Will David wirklich deinen Vater vor deinen Augen ehren, weil er Leute schickt, die dir sein Beileid aussprechen sollen? Hat David seine Diener nicht viel eher zu dir geschickt, um die Stadt zu durchforschen und auszukundschaften und sie dann verwüsten zu lassen?" },
      { number: 4, text: "Darauf ließ Hanun die Diener Davids festnehmen, ihnen die Hälfte des Bartes abscheren und ihnen die Kleider zur Hälfte abschneiden, bis zum Gesäß herauf. So schickte er sie weg." },
      { number: 5, text: "Als man David das meldete, schickte er ihnen jemand entgegen und ließ ihnen, weil sie so schwer geschändet waren, sagen: Bleibt in Jericho und kehrt erst zurück, wenn euer Bart wieder gewachsen ist." },
      { number: 6, text: "Als die Ammoniter merkten, dass sie sich bei David verhasst gemacht hatten, schickten sie (Boten) und warben die Aramäer von Bet-Rehob und von Zoba an, zwanzigtausend Mann Fußvolk, außerdem den König von Maacha mit tausend Mann und die Leute von Tob mit zwölftausend Mann.", footnotes: ["Die aramäischen Städte liegen nördlich der Jordanquellen und im nördlichen Ostjordanland."] },
      { number: 7, text: "David erfuhr davon und schickte Joab mit dem ganzen Heer, allen kriegstüchtigen Männern, hin." },
      { number: 8, text: "Die Ammoniter rückten aus und stellten sich vor dem Stadttor zum Kampf auf, während die Aramäer von Zoba und Rehob und die Leute von Tob und Maacha jeweils für sich draußen auf dem freien Feld standen." },
      { number: 9, text: "Als Joab sah, dass ihm ein Angriff von vorn und von hinten drohte, traf er eine Auswahl aus den Kerntruppen Israels und stellte sie gegen die Aramäer auf." },
      { number: 10, text: "Den Rest des Heeres unterstellte er seinem Bruder Abischai, damit er sie den Ammonitern gegenüber aufstelle." },
      { number: 11, text: "Joab sagte: Wenn die Aramäer stärker sind als ich, dann komm du mir zu Hilfe! Wenn die Ammoniter stärker sind als du, dann werde ich dir zu Hilfe kommen." },
      { number: 12, text: "Sei tapfer! Wir wollen mutig für unser Volk und für die Städte unseres Gottes kämpfen. Der Herr aber möge tun, was er für recht hält." },
      { number: 13, text: "Da griff Joab mit seinen Leuten die Aramäer an und diese ergriffen die Flucht vor ihm." },
      { number: 14, text: "Als die Ammoniter sahen, dass die Aramäer flohen, ergriffen auch sie die Flucht vor Abischai und zogen sich in die Stadt zurück. Joab kehrte nach dem Kampf gegen die Ammoniter nach Jerusalem zurück." },
      { number: 15, text: "Die Aramäer sahen, dass sie von Israel geschlagen waren, aber sie sammelten sich wieder." },
      { number: 16, text: "Hadad-Eser schickte Boten und ließ die Aramäer, die diesseits des Stroms wohnten, ausrücken. Sie kamen nach Helam, an ihrer Spitze Schobach, der Oberbefehlshaber Hadad-Esers.", footnotes: ["diesseits, wörtlich: jenseits; gemeint ist das Gebiet westlich des Eufrat (vgl. 1 Kön 5,4). «Jenseits des Stroms» ist in assyrischen Texten, vor allem in der Perserzeit, das Gebiet der westlichen Provinzen."] },
      { number: 17, text: "Als man das David meldete, versammelte er ganz Israel, überschritt den Jordan und kam nach Helam. Die Aramäer nahmen David gegenüber Aufstellung und eröffneten den Kampf gegen ihn." },
      { number: 18, text: "Doch sie mussten vor den Israeliten fliehen. David vernichtete siebenhundert aramäische Kriegswagen und tötete vierzigtausend Reiter. Auch Schobach, den Oberbefehlshaber des Heeres, schlug er nieder, sodass er an Ort und Stelle starb." },
      { number: 19, text: "Die Könige, die Hadad-Esers Vasallen waren, sahen nun ein, dass sie von den Israeliten besiegt waren. Sie schlossen Frieden mit den Israeliten und unterwarfen sich ihnen. Von da an fürchteten sich die Aramäer, den Ammonitern noch weiter zu helfen." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Um die Jahreswende, zu der Zeit, in der die Könige in den Krieg ziehen, schickte David den Joab mit seinen Männern und ganz Israel aus und sie verwüsteten das Land der Ammoniter und belagerten Rabba. David selbst aber blieb in Jerusalem.", heading: "David und Batseba: 11,1-27", footnotes: ["12,26", "das Land: so sinngemäß nach 1 Chr 20,1."] },
      { number: 2, text: "Als David einmal zur Abendzeit von seinem Lager aufstand und auf dem Flachdach des Königspalastes hin- und herging, sah er von dort aus eine Frau, die badete. Die Frau war sehr schön anzusehen." },
      { number: 3, text: "David schickte jemand hin und erkundigte sich nach ihr. Man sagte ihm: Das ist Batseba, die Tochter Ammiëls, die Frau des Hetiters Urija." },
      { number: 4, text: "Darauf schickte David Boten zu ihr und ließ sie holen; sie kam zu ihm, und er schlief mit ihr - sie hatte sich gerade von ihrer Unreinheit gereinigt. Dann kehrte sie in ihr Haus zurück.", footnotes: ["Ex 20,14.17; Dtn 5,18.21"] },
      { number: 5, text: "Die Frau war aber schwanger geworden und schickte deshalb zu David und ließ ihm mitteilen: Ich bin schwanger." },
      { number: 6, text: "Darauf sandte David einen Boten zu Joab (und ließ ihm sagen): Schick den Hetiter Urija zu mir! Und Joab schickte Urija zu David." },
      { number: 7, text: "Als Urija zu ihm kam, fragte David, ob es Joab und dem Volk gut gehe und wie es mit dem Kampf stehe." },
      { number: 8, text: "Dann sagte er zu Urija: Geh in dein Haus hinab und wasch dir die Füße! Urija verließ das Haus des Königs und es wurde ihm ein Geschenk des Königs nachgetragen." },
      { number: 9, text: "Urija aber legte sich am Tor des Königshauses bei den Knechten seines Herrn nieder und ging nicht in sein Haus hinab." },
      { number: 10, text: "Man berichtete David: Urija ist nicht in sein Haus hinabgegangen. Darauf sagte David zu Urija: Bist du nicht gerade von einer Reise gekommen? Warum bist du nicht in dein Haus hinuntergegangen?" },
      { number: 11, text: "Urija antwortete David: Die Lade und Israel und Juda wohnen in Hütten und mein Herr Joab und die Knechte meines Herrn lagern auf freiem Feld; da soll ich in mein Haus gehen, um zu essen und zu trinken und bei meiner Frau zu liegen? So wahr du lebst und so wahr deine Seele lebt, das werde ich nicht tun.", footnotes: ["Die Lade Gottes wurde in den Kampf mitgenommen (vgl. 1 Sam 4,3f; Num 10,35f)."] },
      { number: 12, text: "Darauf sagte David zu Urija: Bleib auch heute noch hier; morgen werde ich dich wegschicken. So blieb Urija an jenem Tag in Jerusalem. Am folgenden Tag" },
      { number: 13, text: "lud David ihn ein, bei ihm zu essen und zu trinken, und machte ihn betrunken. Am Abend aber ging Urija weg, um sich wieder auf seinem Lager bei den Knechten seines Herrn niederzulegen; er ging nicht in sein Haus hinab." },
      { number: 14, text: "Am anderen Morgen schrieb David einen Brief an Joab und ließ ihn durch Urija überbringen." },
      { number: 15, text: "Er schrieb in dem Brief: Stellt Urija nach vorn, wo der Kampf am heftigsten ist, dann zieht euch von ihm zurück, sodass er getroffen wird und den Tod findet." },
      { number: 16, text: "Joab hatte die Stadt beobachtet und er stellte Urija an einen Platz, von dem er wusste, dass dort besonders tüchtige Krieger standen." },
      { number: 17, text: "Als dann die Leute aus der Stadt einen Ausfall machten und gegen Joab kämpften, fielen einige vom Volk, das heißt von den Kriegern Davids; auch der Hetiter Urija fand den Tod." },
      { number: 18, text: "Joab schickte (einen Boten) zu David und ließ ihm den Verlauf des Kampfes berichten." },
      { number: 19, text: "Und er befahl dem Boten: Wenn du dem König alles über den Verlauf des Kampfes bis zu Ende berichtet hast" },
      { number: 20, text: "und wenn dann der König in Zorn gerät und zu dir sagt: Warum seid ihr beim Kampf so nahe an die Stadt herangegangen? Habt ihr nicht gewusst, dass sie von der Mauer herabschießen?" },
      { number: 21, text: "Wer hat Abimelech, den Sohn Jerubbaals, erschlagen? Hat nicht eine Frau in Tebez einen Mühlstein von der Mauer auf ihn herabgeworfen, sodass er starb? Warum seid ihr so nahe an die Mauer herangegangen?, dann sollst du sagen: Auch dein Knecht, der Hetiter Urija, ist tot.", footnotes: ["Ri 9,53f", "Jerubbaals: Text korr. nach G (vgl. Ri 7,1); H liest Jerubbeschet, um den Namen des Gottes Baal verächtlich zu machen (vgl. die Anmerkungen zu 2,8; 4,4)."] },
      { number: 22, text: "Der Bote ging fort, kam zu David und berichtete ihm alles, was Joab ihm aufgetragen hatte." },
      { number: 23, text: "Der Bote sagte zu David: Die Männer waren stärker als wir und waren gegen uns bis aufs freie Feld vorgedrungen; wir aber drängten sie bis zum Eingang des Tores zurück." },
      { number: 24, text: "Da schossen die Schützen von der Mauer herab auf deine Knechte, sodass einige von den Knechten des Königs starben; auch dein Knecht, der Hetiter Urija, ist tot." },
      { number: 25, text: "Da sagte David zu dem Boten: So sollst du zu Joab sagen: Betrachte die Sache nicht als so schlimm; denn das Schwert frisst bald hier, bald dort. Setz den Kampf gegen die Stadt mutig fort und zerstöre sie! So sollst du ihm Mut machen." },
      { number: 26, text: "Als die Frau Urijas hörte, dass ihr Mann Urija tot war, hielt sie für ihren Gemahl die Totenklage." },
      { number: 27, text: "Sobald die Trauerzeit vorüber war, ließ David sie zu sich in sein Haus holen. Sie wurde seine Frau und gebar ihm einen Sohn. Dem Herrn aber missfiel, was David getan hatte." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Darum schickte der Herr den Natan zu David; dieser ging zu David und sagte zu ihm: In einer Stadt lebten einst zwei Männer; der eine war reich, der andere arm.", heading: "Gottes Strafe für David: 12,1-25" },
      { number: 2, text: "Der Reiche besaß sehr viele Schafe und Rinder," },
      { number: 3, text: "der Arme aber besaß nichts außer einem einzigen kleinen Lamm, das er gekauft hatte. Er zog es auf und es wurde bei ihm zusammen mit seinen Kindern groß. Es aß von seinem Stück Brot und es trank aus seinem Becher, in seinem Schoß lag es und war für ihn wie eine Tochter." },
      { number: 4, text: "Da kam ein Besucher zu dem reichen Mann und er brachte es nicht über sich, eines von seinen Schafen oder Rindern zu nehmen, um es für den zuzubereiten, der zu ihm gekommen war. Darum nahm er dem Armen das Lamm weg und bereitete es für den Mann zu, der zu ihm gekommen war." },
      { number: 5, text: "Da geriet David in heftigen Zorn über den Mann und sagte zu Natan: So wahr der Herr lebt: Der Mann, der das getan hat, verdient den Tod." },
      { number: 6, text: "Das Lamm soll er vierfach ersetzen, weil er das getan und kein Mitleid gehabt hat.", footnotes: ["Ex 21,37"] },
      { number: 7, text: "Da sagte Natan zu David: Du selbst bist der Mann. So spricht der Herr, der Gott Israels: Ich habe dich zum König von Israel gesalbt und ich habe dich aus der Hand Sauls gerettet." },
      { number: 8, text: "Ich habe dir das Haus deines Herrn und die Frauen deines Herrn in den Schoß gegeben und ich habe dir das Haus Israel und Juda gegeben, und wenn das zu wenig ist, gebe ich dir noch manches andere dazu." },
      { number: 9, text: "Aber warum hast du das Wort des Herrn verachtet und etwas getan, was ihm missfällt? Du hast den Hetiter Urija mit dem Schwert erschlagen und hast dir seine Frau zur Frau genommen; durch das Schwert der Ammoniter hast du ihn umgebracht.", footnotes: ["1 Kön 15,5"] },
      { number: 10, text: "Darum soll jetzt das Schwert auf ewig nicht mehr von deinem Haus weichen; denn du hast mich verachtet und dir die Frau des Hetiters genommen, damit sie deine Frau werde.", footnotes: ["13,28f; 18,14; 1 Kön 2,24f"] },
      { number: 11, text: "So spricht der Herr: Ich werde dafür sorgen, dass sich aus deinem eigenen Haus das Unheil gegen dich erhebt, und ich werde dir vor deinen Augen deine Frauen wegnehmen und sie einem andern geben; er wird am hellen Tag bei deinen Frauen liegen.", footnotes: ["16,22; 20,3"] },
      { number: 12, text: "Ja, du hast es heimlich getan, ich aber werde es vor ganz Israel und am hellen Tag tun." },
      { number: 13, text: "Darauf sagte David zu Natan: Ich habe gegen den Herrn gesündigt. Natan antwortete David: Der Herr hat dir deine Sünde vergeben; du wirst nicht sterben.", footnotes: ["Ps 51,2"] },
      { number: 14, text: "Weil du aber die Feinde des Herrn durch diese Sache zum Lästern veranlasst hast, muss der Sohn, der dir geboren wird, sterben." },
      { number: 15, text: "Dann ging Natan nach Hause. Der Herr aber ließ das Kind, das die Frau des Urija dem David geboren hatte, schwer krank werden." },
      { number: 16, text: "David suchte Gott wegen des Knaben auf und fastete streng; und wenn er heimkam, legte er sich bei Nacht auf die bloße Erde." },
      { number: 17, text: "Die Ältesten seines Hauses kamen zu ihm, um ihn dazu zu bewegen, von der Erde aufzustehen. Er aber wollte nicht und aß auch nicht mit ihnen." },
      { number: 18, text: "Am siebten Tag aber starb das Kind. Davids Diener fürchteten sich, ihm mitzuteilen, dass das Kind tot war; denn sie sagten: Wir haben ihm zugeredet, als das Kind noch am Leben war; er aber hat nicht auf uns gehört. Wie können wir ihm jetzt sagen: Das Kind ist tot? Er würde ein Unheil anrichten." },
      { number: 19, text: "David jedoch sah, dass seine Diener miteinander flüsterten, und merkte daran, dass das Kind tot war. Er fragte seine Diener: Ist das Kind tot? Sie antworteten: Ja, es ist tot." },
      { number: 20, text: "Da erhob sich David von der Erde, wusch sich, salbte sich, wechselte seine Kleider, ging zum Haus des Herrn und warf sich (davor) nieder. Als er dann nach Hause zurückkehrte, verlangte er (zu essen). Man setzte ihm etwas vor und er aß." },
      { number: 21, text: "Da fragten ihn seine Diener: Was soll das bedeuten, was du getan hast? Als das Kind noch am Leben war, hast du seinetwegen gefastet und geweint. Nachdem aber das Kind tot ist, stehst du auf und isst." },
      { number: 22, text: "Er antwortete: Als das Kind noch am Leben war, habe ich gefastet und geweint; denn ich dachte: Wer weiß, vielleicht ist der Herr mir gnädig und das Kind bleibt am Leben." },
      { number: 23, text: "Jetzt aber, da es tot ist, warum soll ich da noch fasten? Kann ich es zurückholen? Ich werde einmal zu ihm gehen, aber es kommt nicht zu mir zurück." },
      { number: 24, text: "Und David tröstete seine Frau Batseba; er ging zu ihr hinein und schlief mit ihr. Und sie gebar einen Sohn und er gab ihm den Namen Salomo. Der Herr liebte Salomo" },
      { number: 25, text: "und sandte den Propheten Natan, damit er ihm um des Herrn willen den Namen Jedidja (Liebling des Herrn) gebe.", footnotes: ["Der Name Jedidja, der nur hier vorkommt, weist schon auf die Nachfolge Davids hin und bringt auch zum Ausdruck, dass Gott nun dem König wieder gnädig ist."] },
      { number: 26, text: "Joab kämpfte gegen Rabba, (die Stadt) der Ammoniter, und er eroberte die Königsstadt.", heading: "Die Eroberung von Rabba: 12,26-31", footnotes: ["11,1", "Rabba: die Hauptstadt des Ammoniterreichs, heute Amman."] },
      { number: 27, text: "Darauf schickte Joab Boten zu David und ließ ihm sagen: Ich habe gegen Rabba gekämpft und dabei auch die Wasserstadt eingenommen." },
      { number: 28, text: "Darum versammle jetzt den Rest des Heeres, belagere die Stadt und nimm sie selbst ein, damit nicht ich sie einnehme und mein Name über ihr ausgerufen wird." },
      { number: 29, text: "David sammelte also das ganze Heer, zog nach Rabba, kämpfte gegen die Stadt und nahm sie ein." },
      { number: 30, text: "Dann nahm er ihrem König die Krone vom Haupt, deren Gewicht ein Talent Gold betrug und an der ein kostbarer Stein war; sie wurde nun Davids Krone. Und er schaffte eine sehr große Beute aus der Stadt fort.", footnotes: ["(30-31) 1 Chr 20,2f", "Wahrscheinlich ist eher der Wert als das Gewicht (etwa 41 kg) der Krone gemeint."] },
      { number: 31, text: "Auch ihre Einwohner führte er fort und stellte sie an die Steinsägen, an die eisernen Spitzhacken und an die eisernen Äxte und ließ sie in den Ziegeleien arbeiten. So machte er es mit allen Städten der Ammoniter. Dann kehrte David mit dem ganzen Heer nach Jerusalem zurück.", footnotes: ["ließ sie arbeiten: Text korr.; H: führte sie hindurch."] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Danach geschah folgendes: Abschalom, der Sohn Davids, hatte eine schöne Schwester namens Tamar und Amnon, der Sohn Davids, verliebte sich in sie.", heading: "Amnon und Tamar: 13,1-22", footnotes: ["Abschaloms und Tamars Mutter war Maacha; Amnons Mutter war Ahinoam."] },
      { number: 2, text: "Amnon war sehr bedrückt und wurde fast krank wegen seiner Schwester Tamar; denn sie war Jungfrau und es schien Amnon unmöglich, ihr etwas anzutun." },
      { number: 3, text: "Nun hatte Amnon einen Freund namens Jonadab, einen Sohn des Schima, des Bruders Davids. Jonadab war ein sehr kluger Mann." },
      { number: 4, text: "Er sagte zu Amnon: Warum bist du jeden Morgen so bedrückt, Sohn des Königs? Willst du es mir nicht erzählen? Amnon antwortete ihm: Ich liebe Tamar, die Schwester meines Bruders Abschalom." },
      { number: 5, text: "Da sagte Jonadab zu ihm: Leg dich ins Bett und stell dich krank! Wenn dann dein Vater kommt, um nach dir zu sehen, sag zu ihm: Lass doch meine Schwester Tamar zu mir kommen und mir etwas zu essen machen; sie soll die Krankenkost vor meinen Augen zubereiten, sodass ich zusehen und aus ihrer Hand essen kann." },
      { number: 6, text: "Amnon legte sich also hin und stellte sich krank. Als der König kam, um nach ihm zu sehen, sagte Amnon zum König: Meine Schwester Tamar möge doch zu mir kommen; sie soll mir vor meinen Augen zwei Kuchen backen und ich will die Krankenkost aus ihrer Hand essen." },
      { number: 7, text: "David schickte jemand ins Haus der Tamar und ließ ihr sagen: Geh doch in das Haus deines Bruders Amnon und mach ihm etwas zu essen!" },
      { number: 8, text: "Tamar ging in das Haus ihres Bruders Amnon, der im Bett lag. Sie nahm Teig, knetete vor seinen Augen die Kuchen und backte sie." },
      { number: 9, text: "Dann nahm sie die Pfanne und legte ihm (das Gericht) vor. Amnon aber wollte nichts essen, sondern sagte: Schickt alle hinaus! Als alle aus dem Zimmer hinausgegangen waren," },
      { number: 10, text: "sagte Amnon zu Tamar: Bring das Essen in die (innere) Kammer, ich möchte sie aus deiner Hand essen. Tamar nahm die Kuchen, die sie zubereitet hatte, und brachte sie ihrem Bruder Amnon in die Kammer." },
      { number: 11, text: "Als sie ihm aber die Kuchen zum Essen reichte, griff er nach ihr und sagte zu ihr: Komm, leg dich zu mir, Schwester!" },
      { number: 12, text: "Sie antwortete ihm: Nein, mein Bruder, entehre mich nicht! So etwas tut man in Israel nicht. Begeh keine solche Schandtat!" },
      { number: 13, text: "Wohin sollte ich denn in meiner Schande gehen? Du würdest als einer der niederträchtigsten Menschen in Israel dastehen. Rede doch mit dem König, er wird mich dir nicht verweigern.", footnotes: ["Demnach waren Ehen zwischen Halbgeschwistern möglich; nach Lev 18,11; 20,17 und Dtn 27,22 sind sie streng verboten."] },
      { number: 14, text: "Doch Amnon wollte nicht auf sie hören, sondern packte sie und zwang sie, mit ihm zu schlafen." },
      { number: 15, text: "Hinterher aber empfand Amnon eine sehr große Abneigung gegen sie; ja, der Hass, mit dem er sie nun hasste, war größer als die Liebe, mit der er sie geliebt hatte. Amnon sagte zu ihr: Steh auf, geh weg!" },
      { number: 16, text: "Sie erwiderte ihm: Nicht doch! Wenn du mich wegschickst, wäre das ein noch größeres Unrecht als das, das du mir schon angetan hast. Er aber wollte nicht auf sie hören," },
      { number: 17, text: "sondern rief den jungen Mann, der in seinen Diensten stand, und sagte: Bringt dieses Mädchen da von mir weg auf die Straße hinaus und schließt die Tür hinter ihr ab!" },
      { number: 18, text: "Sein Diener brachte sie hinaus und schloss die Tür hinter ihr zu. Sie hatte ein Ärmelkleid an; denn solche Obergewänder trugen die Königstöchter, solange sie Jungfrauen waren.", footnotes: ["Die beiden Sätze sind sinngemäß umgestellt."] },
      { number: 19, text: "Tamar aber streute sich Asche auf das Haupt und zerriss das Ärmelkleid, das sie anhatte, sie legte ihre Hand auf den Kopf und ging schreiend weg." },
      { number: 20, text: "Ihr Bruder Abschalom fragte sie: War dein Bruder Amnon mit dir zusammen? Sprich nicht darüber, meine Schwester, er ist ja dein Bruder. Nimm dir die Sache nicht so zu Herzen! Von da an lebte Tamar einsam im Haus ihres Bruders Abschalom." },
      { number: 21, text: "Doch der König David erfuhr von der ganzen Sache und wurde darüber sehr zornig." },
      { number: 22, text: "Abschalom aber redete nicht mehr mit Amnon, weder im Guten noch im Bösen; er hasste Amnon, weil dieser seine Schwester Tamar vergewaltigt hatte." },
      { number: 23, text: "Zwei Jahre später ließ Abschalom in Baal-Hazor, das bei Efraim liegt, seine Schafe scheren und lud alle Söhne des Königs ein.", heading: "Abschaloms Rache an Amnon: 13,23-37", footnotes: ["Baal-Hazor: nordöstlich von Bet-El."] },
      { number: 24, text: "Er ging zum König und sagte: Dein Knecht lässt gerade seine Schafe scheren. Der König möge doch samt seinen Dienern seinen Knecht dorthin begleiten." },
      { number: 25, text: "Der König antwortete Abschalom: Nein, mein Sohn, wir können doch nicht alle kommen; wir wollen dir nicht zur Last fallen. Obwohl Abschalom ihn dringend bat, wollte er nicht mitgehen, sondern wollte ihn mit dem Segensgruß verabschieden." },
      { number: 26, text: "Da sagte Abschalom: Kann nicht wenigstens mein Bruder Amnon mit uns gehen? Der König fragte ihn: Warum soll er mit dir gehen?" },
      { number: 27, text: "Abschalom aber drängte ihn (noch mehr) und der König ließ Amnon und seine anderen Söhne mit ihm gehen." },
      { number: 28, text: "Abschalom befahl seinen jungen Leuten: Gebt Acht: Wenn Amnon vom Wein guter Laune ist, werde ich zu euch sagen: Schlagt Amnon tot! Dann tötet ihn! Habt keine Angst! Ich selbst habe es euch ja befohlen. Seid mutig und tapfer!" },
      { number: 29, text: "Die jungen Leute Abschaloms machten mit Amnon, was ihnen Abschalom befohlen hatte. Da sprangen alle Söhne des Königs auf, stiegen auf ihre Maultiere und flohen." },
      { number: 30, text: "Sie waren noch auf dem Weg, als zu David das Gerücht gelangte: Abschalom hat alle Söhne des Königs erschlagen; nicht einer von ihnen ist übrig geblieben." },
      { number: 31, text: "Da stand der König auf, zerriss seine Kleider und warf sich zu Boden. Auch alle seine Diener, die um ihn herumstanden, zerrissen ihre Kleider." },
      { number: 32, text: "Jonadab aber, der Sohn Schimas, des Bruders Davids, sagte: Mein Herr soll nicht glauben, dass alle jungen Männer, alle Söhne des Königs, tot sind. Nur Amnon ist tot; denn Abschalom war verbittert seit dem Tag, an dem Amnon seine Schwester vergewaltigte.", footnotes: ["Abschalom war verbittert, wörtlich: es lag über Abschaloms Mund."] },
      { number: 33, text: "Mein Herr, der König, nehme sich die Sache nicht so zu Herzen und denke nicht: Alle Söhne des Königs sind tot. Nur Amnon ist tot." },
      { number: 34, text: "Abschalom aber war geflohen. Der junge Mann jedoch, der Wache hielt, schaute aus und sah plötzlich, dass eine Menge Leute auf dem hinter ihm liegenden Weg von der Seite des Berges herabkamen." },
      { number: 35, text: "Da sagte Jonadab zum König: Sieh, da kommen die Söhne des Königs. Es ist so, wie dein Knecht gesagt hat." },
      { number: 36, text: "Kaum hatte er das gesagt, da kamen die Söhne des Königs. Sie begannen laut zu weinen und auch der König und alle seine Diener brachen in heftiges Weinen aus." },
      { number: 37, text: "Abschalom aber floh und ging zu Talmai, dem Sohn des Königs Ammihud von Geschur und David trauerte lange Zeit um seinen Sohn.", footnotes: ["14,23", "Abschaloms Mutter war eine Königstochter aus Geschur (3,3)."] },
      { number: 38, text: "Abschalom war also geflohen und nach Geschur gegangen; dort blieb er drei Jahre.", heading: "Abschaloms Rückkehr: 13,38 - 14,33" },
      { number: 39, text: "Dann aber hörte der König allmählich auf, gegen Abschalom zu hadern; denn er hatte sich damit abgefunden, dass Amnon tot war.", footnotes: ["zu hadern: Text korr., vgl. G."] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Als Joab, der Sohn der Zeruja, merkte, dass das Herz des Königs sich wieder Abschalom zuwandte,", footnotes: ["Tekoa: südlich von Betlehem, Heimat des Propheten Amos."] },
      { number: 2, text: "schickte er (einen Boten) nach Tekoa und ließ von dort eine kluge Frau holen. Er sagte zu ihr: Tu so, als ob du trauerst, zieh Trauergewänder an und salbe dich nicht mit Öl! Stell dich wie eine Frau, die schon lange Zeit um einen Toten trauert." },
      { number: 3, text: "Dann geh zum König und sprich Folgendes zu ihm - und Joab sagte ihr, was sie sagen sollte." },
      { number: 4, text: "Die Frau aus Tekoa ging zum König, warf sich mit dem Gesicht auf die Erde nieder, um ihm zu huldigen und sagte: Hilf mir, mein König!" },
      { number: 5, text: "Der König fragte sie: Was hast du? Sie antwortete: Ach, ich bin eine Witwe; mein Mann ist tot." },
      { number: 6, text: "Deine Magd hatte zwei Söhne. Die beiden bekamen auf dem Feld Streit miteinander, und weil niemand da war, der sich zwischen die beiden gestellt hätte, schlug der eine den anderen tot." },
      { number: 7, text: "Da erhob sich die ganze Verwandtschaft gegen deine Magd. Sie sagten: Gib den, der seinen Bruder erschlagen hat, heraus, damit wir ihn töten, weil er seinen Bruder ums Leben gebracht hat. So wollten sie sogar den Erben vernichten. Sie wollten die Kohle, die mir geblieben ist, auslöschen, um meinem Mann weder Namen noch Nachkommen auf dem Erdboden zu lassen.", footnotes: ["Dtn 19,11-13"] },
      { number: 8, text: "Da sagte der König zu der Frau: Geh nach Hause! Ich werde deine Sache in Ordnung bringen.", footnotes: ["Ich werde . . . in Ordnung bringen, wörtlich: Ich werde deinetwegen Befehl geben."] },
      { number: 9, text: "Die Frau aus Tekoa erwiderte dem König: Mein Herr und König, dann lastet die Schuld auf mir und dem Haus meines Vaters. Der König aber und sein Thron werden schuldlos sein." },
      { number: 10, text: "Der König antwortete: Wenn jemand gegen dich spricht, bring ihn zu mir; er wird dir nicht wieder zu nahe treten." },
      { number: 11, text: "Sie aber sagte: Der König rufe doch den Herrn, deinen Gott, dafür an, dass der Bluträcher nicht noch mehr Unheil anrichtet und meinen Sohn umbringt. Der König antwortete: So wahr der Herr lebt, deinem Sohn soll kein Haar gekrümmt werden.", footnotes: ["Wörtlich: Keines von den Haaren deines Sohnes soll zu Boden fallen."] },
      { number: 12, text: "Darauf sagte die Frau: Deine Magd möchte meinem Herrn, dem König, noch ein Wort sagen. Er antwortete: Sprich!" },
      { number: 13, text: "Da fragte die Frau: Warum planst du genau das Gleiche gegen das Volk Gottes? Wenn der König ein solches Urteil gefällt hat, spricht er sich selbst schuldig, da er seinen verstoßenen (Sohn) nicht zurückkehren lässt." },
      { number: 14, text: "Wir müssen alle sterben und sind wie das Wasser, das man auf die Erde schüttet und nicht wieder einsammeln kann. Aber Gott wird dem das Leben nicht nehmen, der darauf aus ist, dass ein von ihm Verstoßener nicht verstoßen bleibt.", footnotes: ["Einen Toten kann man nicht wieder ins Leben rufen, selbst wenn Abschalom verstoßen bleibt. Der Sinn des zweiten Satzes ist nicht ganz klar. Mit V. 15 kehrt die Rede der Frau zu dem angenommenen Fall zurück."] },
      { number: 15, text: "Dass ich aber hergekommen bin, um dem König, meinem Herrn, das zu sagen, geschah, weil die Leute mir Angst machten. Darum sagte sich deine Magd: Ich will mit dem König reden; vielleicht wird der König tun, was seine Magd sagt." },
      { number: 16, text: "Ja, der König wird (auf mich) hören und seine Magd aus der Hand des Mannes retten, der mich zusammen mit meinem Sohn aus dem Erbbesitz austilgen will." },
      { number: 17, text: "Deine Magd hat sich gesagt: Das Wort meines Herrn, des Königs, wird mich beruhigen; denn mein Herr, der König, ist gerade so wie der Engel Gottes: Er hört Gutes und Böses. Der Herr, dein Gott, sei mit dir." },
      { number: 18, text: "Der König erwiderte der Frau: Verhehl mir nichts von dem, was ich dich jetzt frage. Die Frau sagte: Mein Herr und König, rede!" },
      { number: 19, text: "Da fragte der König: Hat etwa Joab bei all dem die Hand im Spiel? Die Frau antwortete: So wahr du lebst, mein Herr und König, an all dem, was mein Herr, der König, sagt, kann man sich unmöglich rechts oder links vorbeidrücken. Ja, dein Knecht Joab war es, der mir das befohlen hat; er hat all diese Worte deiner Magd in den Mund gelegt." },
      { number: 20, text: "Das hat dein Knecht Joab getan, um der Sache ein anderes Gesicht zu geben. Mein Herr ist weise wie der Engel Gottes, der alles weiß, was im Land geschieht." },
      { number: 21, text: "Darauf sagte der König zu Joab: Gut, ich erfülle dir diese deine Bitte. Geh, bring den jungen Abschalom zurück!" },
      { number: 22, text: "Joab warf sich mit dem Gesicht auf die Erde nieder und huldigte (dem König). Er segnete den König und sagte: Heute hat dein Knecht erkannt, dass ich dein Wohlwollen gefunden habe, mein Herr und König, weil der König die Bitte seines Knechtes erfüllt hat." },
      { number: 23, text: "Dann brach Joab auf, ging nach Geschur und brachte Abschalom nach Jerusalem zurück.", footnotes: ["13,37"] },
      { number: 24, text: "Der König aber sagte: Er soll in sein Haus gehen, aber er soll mir nicht unter die Augen treten. Abschalom ging in sein Haus, aber dem König durfte er nicht unter die Augen treten." },
      { number: 25, text: "In ganz Israel gab es keinen schöneren und lobenswerteren Mann als Abschalom. Vom Scheitel bis zur Sohle war kein Makel an ihm." },
      { number: 26, text: "Und wenn er sein Haar schneiden ließ - das geschah von Zeit zu Zeit, weil es so schwer wurde, dass er es schneiden lassen musste - und man wog sein Haar, dann wog es zweihundert Schekel nach königlichem Gewicht." },
      { number: 27, text: "Drei Söhne wurden Abschalom geboren und eine Tochter namens Tamar; sie wurde eine Frau von großer Schönheit.", footnotes: ["18,18"] },
      { number: 28, text: "Abschalom wohnte zwei Jahre lang in Jerusalem, ohne dem König unter die Augen zu treten." },
      { number: 29, text: "Da schickte Abschalom jemand zu Joab, weil er ihn zum König senden wollte; aber Joab wollte nicht kommen. Er schickte noch ein zweites Mal jemand hin, aber er wollte wieder nicht kommen." },
      { number: 30, text: "Da sagte Abschalom zu seinen Knechten: Seht, das Feld Joabs, das neben meinem liegt; er hat dort Gerste (angebaut). Geht hin, steckt es in Brand! Die Diener Abschaloms steckten das Feld in Brand." },
      { number: 31, text: "Darauf machte sich Joab auf den Weg, suchte Abschalom in seinem Haus auf und fragte ihn: Warum haben deine Knechte das Feld in Brand gesteckt, das mir gehört?" },
      { number: 32, text: "Abschalom antwortete: Ich hatte doch jemand zu dir geschickt und dir sagen lassen: Komm her, ich möchte dich zum König senden. Du sollst ihn fragen: Wozu bin ich eigentlich von Geschur hergekommen? Es wäre für mich besser gewesen, ich wäre dort geblieben. Jetzt aber möchte ich dem König vor die Augen treten. Wenn eine Schuld auf mir liegt, soll er mich töten." },
      { number: 33, text: "Joab ging zum König und berichtete ihm alles und der König ließ Abschalom rufen. Er kam zum König und fiel vor ihm mit dem Gesicht zur Erde nieder. Und der König küsste Abschalom." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Einige Zeit danach schaffte sich Abschalom einen Wagen und Pferde an sowie fünfzig Mann, die immer vor ihm herliefen.", heading: "Abschaloms Aufstand gegen David: 15,1-12", footnotes: ["1 Kön 1,5"] },
      { number: 2, text: "Früh am Morgen stellte er sich neben den Weg zum Stadttor, und sooft jemand mit einer Streitsache zum König vor Gericht gehen wollte, sprach er ihn an und sagte: Aus welcher Stadt bist du? Wenn der dann antwortete: Dein Knecht ist aus einem der Stämme Israels!,", footnotes: ["Abschalom wendet sich werbend vor allem an die Nordstämme. Der oberste Richter im Land (V. 4) ist der König. Anscheinend gab es auch unter David Grund zur Klage über die Rechtspflege."] },
      { number: 3, text: "sagte Abschalom zu ihm: Was du sagst, ist ja gut und recht, aber beim König gibt es niemand, der dich anhört." },
      { number: 4, text: "Und Abschalom fügte hinzu: Würde mich doch jemand zum Richter im Land machen, damit jeder, der einen Streit oder eine Rechtssache hat, zu mir kommt; ich würde ihm Recht verschaffen." },
      { number: 5, text: "Ging dann einer auf ihn zu, um vor ihm niederzufallen, dann streckte er die Hand aus, hielt ihn fest und küsste ihn." },
      { number: 6, text: "So machte es Abschalom bei allen Israeliten, die zum König vor Gericht gehen wollten, und so stahl er sich in das Herz der Israeliten." },
      { number: 7, text: "Nach vier Jahren sagte Abschalom zum König: Ich möchte nach Hebron gehen, um ein Gelübde zu erfüllen, das ich dem Herrn abgelegt habe.", footnotes: ["vier: Text korr., vgl. G und S; H: vierzig."] },
      { number: 8, text: "Denn dein Knecht hat bei seinem Aufenthalt in Geschur in Aram das Gelübde gemacht: Wenn der Herr mich wirklich nach Jerusalem zurückkehren lässt, dann will ich für den Herrn einen Gottesdienst feiern.", footnotes: ["13,37f"] },
      { number: 9, text: "Der König antwortete ihm: Geh in Frieden! Da brach Abschalom auf und ging nach Hebron." },
      { number: 10, text: "Doch (von dort) schickte er Boten an alle Stämme Israels und ließ ihnen sagen: Wenn ihr den Klang des Widderhorns hört, dann sollt ihr rufen: Abschalom ist König in Hebron." },
      { number: 11, text: "Zweihundert Männer aus Jerusalem waren mit Abschalom gegangen; er hatte sie eingeladen und sie waren mit ihm gezogen, ohne von der ganzen Sache etwas zu ahnen." },
      { number: 12, text: "Als er seine Opfer darbrachte, ließ Abschalom auch den Giloniter Ahitofel, den Berater Davids, aus seiner Heimatstadt Gilo kommen. So wurde die Verschwörung immer größer und immer mehr Leute schlossen sich Abschalom an." },
      { number: 13, text: "Als ein Bote kam und David meldete: Das Herz der Israeliten hat sich Abschalom zugewandt!,", heading: "Davids Flucht aus Jerusalem: 15,13 - 16,14" },
      { number: 14, text: "sagte David zu allen seinen Dienern, die noch bei ihm in Jerusalem waren: Auf, wir müssen fliehen, denn für uns gibt es keine Rettung vor Abschalom. Beeilt euch mit dem Aufbruch, sonst kommt er und holt uns ein, bringt Unglück über uns und schlägt die Stadt mit scharfem Schwert." },
      { number: 15, text: "Die Diener des Königs sagten zu ihm: Ganz wie der Herr und König sich entscheidet. Hier sind deine Knechte." },
      { number: 16, text: "So zog der König fort und sein ganzes Haus folgte ihm. Nur die zehn Nebenfrauen ließ der König zurück; sie sollten das Haus bewachen.", footnotes: ["16,21; 20,3"] },
      { number: 17, text: "Als der König mit den Leuten, die ihm folgten, fortzog, blieben sie beim letzten Haus stehen" },
      { number: 18, text: "und alle seine Diener zogen an ihm vorüber, dazu alle Kereter und Peleter und alle Gatiter, jene sechshundert Mann, die ihm aus Gat gefolgt waren; sie alle zogen vor dem König vorüber." },
      { number: 19, text: "Da sagte der König zu Ittai aus Gat: Warum willst auch du mit uns gehen? Kehr um und bleib beim (neuen) König! Denn du bist ein Ausländer und aus deiner Heimat verbannt." },
      { number: 20, text: "Erst gestern bist du gekommen - und schon heute sollte ich dich aufjagen, um mit uns zu gehen? Ich gehe, wohin ich eben gehe. Du aber kehr um und bring deine Brüder in Liebe und Treue zurück!" },
      { number: 21, text: "Doch Ittai erwiderte dem König: So wahr der Herr lebt und so wahr mein Herr, der König, lebt: Nur an dem Ort, wo mein Herr, der König, ist, dort wird auch dein Diener sein, sei es um zu leben oder um zu sterben." },
      { number: 22, text: "Da sagte David zu Ittai: Dann geh und zieh (mit deinen Leuten) vorüber! Und der Gatiter zog mit allen seinen Männern und dem ganzen Tross, den er bei sich hatte, vorüber." },
      { number: 23, text: "Alle weinten laut, als die Leute (an David) vorüberzogen. Dann überschritt der König den Bach Kidron und alle zogen weiter auf dem Weg zur Steppe." },
      { number: 24, text: "Auch Zadok und all die Leviten um ihn, die die Bundeslade Gottes trugen, waren dabei. Sie stellten die Lade Gottes nieder und Abjatar brachte Opfer dar, bis alle Leute aus der Stadt vollzählig (an David) vorübergezogen waren." },
      { number: 25, text: "Der König sagte zu Zadok: Bring die Lade Gottes in die Stadt zurück! Wenn ich vor den Augen des Herrn Gnade finde, dann wird er mich zurückführen und mich die Lade und ihre Stätte wieder sehen lassen." },
      { number: 26, text: "Wenn er aber sagt: Ich habe an dir keinen Gefallen!, gut, dann mag er mit mir machen, was ihm gefällt." },
      { number: 27, text: "Und (weiter) sagte der König zu dem Priester Zadok: Siehst du das ein? Kehr in Frieden in die Stadt zurück, ebenso dein Sohn Ahimaaz und Abjatars Sohn Jonatan, eure beiden Söhne." },
      { number: 28, text: "Merkt euch: Ich werde an den Furten zur Steppe warten, bis von euch eine Nachricht kommt, die mir Bescheid gibt." },
      { number: 29, text: "Darauf brachten Zadok und Abjatar die Lade Gottes nach Jerusalem zurück und blieben dort." },
      { number: 30, text: "David stieg weinend und mit verhülltem Haupte den Ölberg hinauf; er ging barfuß und alle Leute, die bei ihm waren, verhüllten ihr Haupt und zogen weinend hinauf." },
      { number: 31, text: "Man hatte David die Nachricht gebracht, dass auch Ahitofel unter den Verschwörern aufseiten Abschaloms war. Da sagte David: Herr, vereitle den Rat Ahitofels!", footnotes: ["17,14"] },
      { number: 32, text: "Als David auf den Gipfel des Berges kam, auf dem man sich vor Gott niederwirft, kam ihm plötzlich der Arkiter Huschai entgegen, mit zerrissenen Kleidern und Erde auf dem Haupt.", footnotes: ["1 Kön 3,2", "Hier wird ein uns sonst nicht bekanntes Heiligtum auf dem Ölberg vorausgesetzt."] },
      { number: 33, text: "David sagte zu ihm: Wenn du mit mir ziehst, wärest du mir nur eine Last." },
      { number: 34, text: "Wenn du aber in die Stadt zurückkehrst und zu Abschalom sagst: Ich bin dein Knecht, mein König, ich war früher der Knecht deines Vaters, jetzt aber will ich dein Knecht sein!, dann kannst du für mich den Rat Ahitofels durchkreuzen.", footnotes: ["17,7"] },
      { number: 35, text: "Die Priester Zadok und Abjatar werden auch dort bei dir sein und du kannst alles, was du aus dem Haus des Königs hörst, den Priestern Zadok und Abjatar berichten." },
      { number: 36, text: "Sie haben dort auch ihre beiden Söhne bei sich, Zadok den Ahimaaz und Abjatar den Jonatan; durch sie könnt ihr mir alles übermitteln, was ihr hört." },
      { number: 37, text: "So kam Huschai, der Freund Davids, in die Stadt zurück, als Abschalom gerade in Jerusalem ankam.", footnotes: ["16,15; 1 Chr 27,33"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Als David vom Gipfel des Berges aus ein Stück weiterzog, da kam ihm Ziba, der Diener Merib-Baals, mit einem Paar gesattelter Esel entgegen. Sie waren mit zweihundert Broten, hundert Traubenkuchen, hundert frischen Früchten und einem Schlauch Wein beladen." },
      { number: 2, text: "Der König fragte Ziba: Was hast du damit vor? Ziba antwortete: Die Esel sind für die königliche Familie als Reittiere bestimmt, das Brot und die Früchte zur Verpflegung deiner Diener, der Wein ist zum Trinken für die, die in der Steppe müde werden." },
      { number: 3, text: "Der König fragte: Wo ist der Sohn deines Herrn? Ziba antwortete dem König: Er ist in Jerusalem geblieben; denn er hat gesagt: Heute wird mir das Haus Israel das Königtum meines Vaters wiedergeben.", footnotes: ["(3-4) 19,25-31"] },
      { number: 4, text: "Da sagte der König zu Ziba: Jetzt soll alles, was Merib-Baal gehört, dir gehören. Ziba antwortete: Ich habe mich (vor dir) niedergeworfen; möge ich Gnade finden vor deinen Augen, mein Herr und König." },
      { number: 5, text: "Als König David nach Bahurim kam, da kam plötzlich aus der Stadt ein Mann namens Schimi, ein Sohn Geras aus der Sippe des Hauses Saul. Er kam David mit Flüchen entgegen,", footnotes: ["(5-12) 19,17-24; 1 Kön 2,8f"] },
      { number: 6, text: "und warf mit Steinen nach ihm und allen Dienern des Königs David, obwohl das ganze Volk und alle Krieger rechts und links um ihn standen." },
      { number: 7, text: "Schimi schrie und fluchte: Verschwinde, verschwinde, du Mörder, du Niederträchtiger!" },
      { number: 8, text: "Der Herr hat all deine Blutschuld am Haus Sauls, an dessen Stelle du König geworden bist, auf dich zurückfallen lassen. Der Herr hat das Königtum in die Hand deines Sohnes Abschalom gegeben. Nun bist du ins Unglück geraten; denn du bist ein Mörder." },
      { number: 9, text: "Da sagte Abischai, der Sohn der Zeruja, zum König: Warum flucht dieser tote Hund meinem Herrn, dem König? Ich will hinübergehen und ihm den Kopf abschlagen." },
      { number: 10, text: "Doch der König antwortete: Was habe ich mit euch zu schaffen, ihr Söhne der Zeruja? Wenn er flucht und wenn der Herr ihm gesagt hat: Verfluch David!, wer darf dann fragen: Warum tust du das?" },
      { number: 11, text: "Und weiter sagte David zu Abischai und all seinen Dienern: Seht, mein leiblicher Sohn trachtet mir nach dem Leben, wie viel mehr muss es dann dieser Benjaminiter tun. Lasst ihn fluchen! Sicherlich hat es ihm der Herr geboten." },
      { number: 12, text: "Vielleicht sieht der Herr mein Elend an und erweist mir Gutes für den Fluch, der mich heute trifft.", footnotes: ["mein Elend: Text korr. nach G."] },
      { number: 13, text: "David und seine Männer setzten ihren Weg fort; Schimi ging am Berghang neben ihnen her und fluchte ununterbrochen, warf mit Steinen nach ihm und bewarf ihn mit Erde." },
      { number: 14, text: "Erschöpft kam der König mit allen Leuten, die er bei sich hatte, an den Jordan; dort konnte er wieder Atem schöpfen.", footnotes: ["an den Jordan: Ortsangabe, die in H fehlt, ergänzt nach G und 15,28."] },
      { number: 15, text: "Abschalom war mit dem ganzen Heer der Israeliten nach Jerusalem gekommen; auch Ahitofel war bei ihm.", heading: "Abschalom in Jerusalem: 16,15 - 17,23", footnotes: ["15,37"] },
      { number: 16, text: "Als der Arkiter Huschai, der Freund Davids, zu Abschalom kam, sagte er zu Abschalom: Es lebe der König, es lebe der König!" },
      { number: 17, text: "Abschalom fragte Huschai: So sieht die Liebe zu deinem Freund aus? Warum bist du nicht mit deinem Freund gegangen?" },
      { number: 18, text: "Huschai sagte zu Abschalom: Nein, ich gehöre vielmehr zu dem, den der Herr und dieses Volk und alle Israeliten erwählt haben; bei ihm will ich bleiben." },
      { number: 19, text: "Und außerdem: Wem diene ich denn? Doch nur seinem Sohn. Wie ich vor den Augen deines Vaters Dienst getan habe, so will ich auch dir zur Verfügung stehen." },
      { number: 20, text: "Abschalom sagte zu Ahitofel: Gebt einen Rat, was wir tun sollen." },
      { number: 21, text: "Ahitofel sagte zu Abschalom: Geh zu den Nebenfrauen deines Vaters, die er hiergelassen hat, um das Haus zu bewachen. So wird ganz Israel erfahren, dass du dich bei deinem Vater verhasst gemacht hast, und alle, die zu dir halten, werden ermutigt.", footnotes: ["15,16", "Der Rat des angesehenen Ahitofel will erreichen, dass der Bruch Abschaloms mit seinem Vater unheilbar wird und dass dadurch auch die Schwankenden auf die Seite Abschaloms gezogen werden."] },
      { number: 22, text: "Man errichtete für Abschalom ein Zelt auf dem Dach, und Abschalom ging vor den Augen ganz Israels zu den Nebenfrauen seines Vaters.", footnotes: ["12,11"] },
      { number: 23, text: "Ein Rat, den Ahitofel gab, galt in jenen Tagen so viel, als hätte man ein Gotteswort erbeten. So viel galt jeder Rat Ahitofels, bei David wie bei Abschalom." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Und Ahitofel sagte weiter zu Abschalom: Ich möchte zwölftausend Mann auswählen, mit ihnen noch heute Nacht aufbrechen und David nachsetzen." },
      { number: 2, text: "Ich will ihn überfallen, wenn er noch müde und ermattet ist, und ihm einen Schrecken einjagen. Alle Leute, die bei ihm sind, werden fliehen und ich kann den König, wenn er allein ist, erschlagen." },
      { number: 3, text: "Dann werde ich das ganze Volk zu dir zurückführen, wie eine Neuvermählte zu ihrem Mann heimgeholt wird. Du trachtest ja nur dem einen Mann nach dem Leben; das ganze übrige Volk aber soll Frieden haben.", footnotes: ["Text korr. nach G."] },
      { number: 4, text: "Der Plan erschien Abschalom und allen Ältesten Israels gut." },
      { number: 5, text: "Abschalom aber sagte: Ruft auch den Arkiter Huschai! Wir wollen hören, was er zu sagen hat." },
      { number: 6, text: "Als Huschai kam, sagte Abschalom zu ihm: Das und das hat Ahitofel gesagt. Sollen wir seinen Vorschlag ausführen? Wenn nicht, dann rede du!" },
      { number: 7, text: "Da sagte Huschai zu Abschalom: Diesmal ist der Rat, den Ahitofel gegeben hat, nicht gut.", footnotes: ["15,34"] },
      { number: 8, text: "Und er fuhr fort: Du kennst deinen Vater und seine Männer; sie sind Krieger und von wildem Mut erfüllt, wie eine Bärin im freien Gelände, der man die Jungen geraubt hat. Dein Vater ist ein Krieger, der mit seinen Leuten keine Nachtruhe hält." },
      { number: 9, text: "Sicher hält er sich jetzt in einer Höhle oder an einem anderen Ort versteckt. Wenn nun gleich zu Anfang einige von deinen Leuten fallen und man hört davon, wird man sagen: Die Anhänger Abschaloms haben eine Niederlage erlitten.", footnotes: ["von deinen Leuten, wörtlich: von ihnen; gemeint sind die Leute Abschaloms."] },
      { number: 10, text: "Dann wird auch der Tapferste - und habe er ein Herz wie ein Löwe völlig den Mut verlieren; denn ganz Israel weiß, dass dein Vater ein Held ist und tapfere Männer bei sich hat." },
      { number: 11, text: "Darum rate ich: Alle Israeliten zwischen Dan und Beerscheba sollen sich bei dir versammeln, (ein Heer) so zahlreich wie der Sand am Ufer des Meeres. Du selbst musst (mit ihnen) in den Kampf ziehen." },
      { number: 12, text: "Wenn wir ihn dann in einem der Orte, wo er sich aufhält, finden, überfallen wir ihn, wie der Tau auf die Erde fällt; dann wird von ihm und allen Männern, die bei ihm sind, auch nicht einer übrig bleiben." },
      { number: 13, text: "Und wenn er sich in eine Stadt zurückzieht, so wird ganz Israel Seile an (den Mauern) jener Stadt befestigen und wir schleifen sie ins Tal hinab, sodass dort, wo sie sind, kein Stein mehr zu finden ist." },
      { number: 14, text: "Da sagten Abschalom und alle Israeliten: Der Rat des Arkiters Huschai ist besser als der Rat Ahitofels. Der Herr hatte es nämlich so bestimmt; der gute Rat Ahitofels sollte durchkreuzt werden, weil der Herr Unheil über Abschalom bringen wollte.", footnotes: ["15,31", "Der Rat Huschais soll David Zeitgewinn bringen."] },
      { number: 15, text: "Darauf sagte Huschai zu den Priestern Zadok und Abjatar: Das und das hat Ahitofel Abschalom und den Ältesten Israels geraten und das und das habe ich geraten.", footnotes: ["(15-21) 15,36"] },
      { number: 16, text: "Schickt jetzt schnell jemand zu David und lasst ihm sagen: Bleib heute Nacht nicht bei den Furten, (die) zur Steppe (hinüberführen), sondern zieh sofort hinüber, damit nicht der König samt allen Leuten, die er bei sich hat, vernichtet wird." },
      { number: 17, text: "Jonatan und Ahimaaz aber hielten sich an der Rogel-Quelle auf; eine Magd ging zu ihnen und brachte ihnen die Nachricht, sie aber gingen zu König David und machten ihm Meldung. Sie durften sich nämlich nicht sehen lassen und konnten deshalb die Stadt nicht betreten.", footnotes: ["Die Rogel-Quelle ist bei Jerusalem, südlich der Vereinigung des Kidrontals mit dem Hinnomtal."] },
      { number: 18, text: "Doch ein junger Mann sah sie trotzdem und meldete es Abschalom. Die beiden aber gingen schnell weiter und kamen zum Haus eines Mannes in Bahurim, der in seinem Hof einen Brunnen hatte. Sie stiegen hinein" },
      { number: 19, text: "und die Frau nahm eine Decke, breitete sie über die Öffnung des Brunnens und streute Körner darauf, sodass man nichts merken konnte." },
      { number: 20, text: "Als Abschaloms Knechte zu der Frau ins Haus kamen, fragten sie: Wo sind Ahimaaz und Jonatan? Die Frau antwortete ihnen: Sie sind von hier zum Wasser weitergegangen. Die Knechte machten sich auf die Suche, als sie sie aber nicht fanden, kehrten sie nach Jerusalem zurück.", footnotes: ["von hier zum Wasser: Text korr."] },
      { number: 21, text: "Nachdem sie weggegangen waren, stiegen die beiden aus dem Brunnen heraus, gingen zu König David und brachten ihm die Nachricht. Sie sagten zu David: Brecht auf und geht schnell über den Fluss hinüber; denn Ahitofel hat gegen euch den und den Rat erteilt." },
      { number: 22, text: "Da brach David mit allen Leuten, die noch bei ihm waren, auf, und zog über den Jordan. Als es Morgen wurde, war auch nicht mehr einer da, der den Jordan nicht überschritten hätte." },
      { number: 23, text: "Als Ahitofel sah, dass sein Rat nicht ausgeführt wurde, sattelte er seinen Esel, brach auf und kehrte in seine Heimatstadt zurück. Dann bestellte er sein Haus und erhängte sich. So starb er und man begrub ihn im Grab seines Vaters.", footnotes: ["Selbstmord begeht im AT nur jemand, der einem grausamen Tod entgehen will (vgl. Ri 9,54; 1 Sam 31,4; 1 Kön 16,18; 2 Makk 14,43-46)."] },
      { number: 24, text: "Als David nach Mahanajim gekommen war, überschritt Abschalom mit allen Israeliten den Jordan.", heading: "Davids Vorbereitungen zum Kampf: 17,24 - 18,5" },
      { number: 25, text: "Abschalom hatte Amasa anstelle Joabs an die Spitze des Heeres gestellt. Amasa war der Sohn eines Ismaeliters namens Jeter und der Abigal, die eine Tochter des Nahasch und eine Schwester von Joabs Mutter Zeruja war.", footnotes: ["Ismaeliters: Text korr. nach 1 Chr 2,17."] },
      { number: 26, text: "Die Israeliten unter Abschalom schlugen ihr Lager im Gebiet von Gilead auf." },
      { number: 27, text: "Als nun David nach Mahanajim gekommen war, brachten ihm Schobi, der Sohn des Nahasch aus der Ammoniterstadt Rabba, und Machir, der Sohn Ammiëls aus Lo-Dabar, sowie der Gileaditer Barsillai aus Roglim", footnotes: ["9,4; 19,32; 1 Kön 2,7", "brachten: ergänzt nach G."] },
      { number: 28, text: "Matten und Decken, irdene Gefäße, Weizen, Gerste, Mehl und geröstetes Korn, Bohnen und Linsen [und geröstetes Korn];" },
      { number: 29, text: "außerdem brachten sie David und seinen Leuten Honig und Butter, Schafe und Käse als Nahrung. Denn sie sagten: Das Volk in der Steppe ist hungrig, durstig und erschöpft." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Dann musterte David die Leute, die bei ihm waren, und setzte Anführer ein über die einzelnen Tausendschaften und Hundertschaften." },
      { number: 2, text: "Dann ließ er seine Leute ausrücken, ein Drittel (von ihnen) unter Joab, ein Drittel unter Abischai, dem Sohn der Zeruja und Bruder Joabs, und ein Drittel unter dem Gatiter Ittai. Der König sagte zu seinen Leuten: Auch ich will mit euch in den Kampf ziehen.", footnotes: ["(2f) 15,19"] },
      { number: 3, text: "Doch sie erwiderten: Du sollst nicht mit uns in den Kampf ziehen; denn wenn wir fliehen, wird man sich um uns nicht kümmern; und wenn die Hälfte von uns stirbt, wird man sich um uns nicht kümmern. Doch du giltst so viel wie zehntausend von uns und außerdem wäre es besser, wenn du uns von der Stadt aus zu Hilfe kommen könntest." },
      { number: 4, text: "Der König antwortete ihnen: Ich will tun, was ihr für richtig haltet. Darauf stellte sich der König neben das Tor (der Stadt) und die Leute zogen nach Hundertschaften und Tausendschaften geordnet hinaus." },
      { number: 5, text: "Und der König befahl Joab, Abischai und Ittai: Geht mir mit dem Jungen, mit Abschalom, schonend um! Alle Leute hörten, wie der König seinen Anführern wegen Abschalom diese Anweisung gab.", footnotes: ["18,12"] },
      { number: 6, text: "Die Leute zogen ins Feld, den Israeliten entgegen, und im Wald Efraim kam es zur Schlacht.", heading: "Abschaloms Tod: 18,6-32" },
      { number: 7, text: "Die Israeliten wurden von den Leuten Davids geschlagen und erlitten eine schwere Niederlage; an jenem Tag fielen zwanzigtausend von ihnen." },
      { number: 8, text: "Der Kampf breitete sich über die ganze dortige Gegend aus und der Wald verschlang an jenem Tag mehr Krieger als das Schwert." },
      { number: 9, text: "Plötzlich kam Abschalom in das Blickfeld der Krieger Davids; er ritt auf einem Maultier. Als das Maultier unter den Ästen einer großen Eiche hindurchlief, blieb Abschalom mit dem Kopf fest an der Eiche hängen, sodass er zwischen Himmel und Erde schwebte und das Maultier unter ihm weglief." },
      { number: 10, text: "Jemand sah es und meldete Joab: Ich habe gerade Abschalom an einer Eiche hängen sehen." },
      { number: 11, text: "Joab sagte zu dem Mann, der ihm das meldete: Wenn du es gesehen hast, warum hast du ihn nicht sofort an Ort und Stelle niedergestoßen? Ich hätte dir dann gern zehn Silberstücke und einen Gürtel gegeben." },
      { number: 12, text: "Der Mann antwortete Joab: Auch wenn ich tausend Silberstücke auf die Hand ausgezahlt erhielte, würde ich meine Hand nicht gegen den Sohn des Königs erheben; denn der König hat dir, Abischai und Ittai vor unseren Ohren befohlen: Gebt mir auf den Jungen, auf Abschalom, Acht!", footnotes: ["18,5"] },
      { number: 13, text: "Hätte ich heimtückisch gehandelt und ihm etwas getan - dem König bleibt ohnehin nichts verborgen -, dann würdest du dich (aus der Sache) heraushalten." },
      { number: 14, text: "Joab erwiderte: Ich kann mich nicht noch länger mit dir aufhalten. Und er nahm drei Spieße in die Hand und stieß sie Abschalom, der noch lebend an der Eiche hing, ins Herz." },
      { number: 15, text: "Die zehn Waffenträger Joabs umringten Abschalom und schlugen ihn tot." },
      { number: 16, text: "Dann ließ Joab das Widderhorn blasen und die Krieger hörten auf, die Israeliten zu verfolgen, weil Joab ihnen Einhalt gebot." },
      { number: 17, text: "Sie nahmen Abschalom und warfen ihn im Wald in eine tiefe Grube und errichteten über ihm einen riesigen Steinhaufen. Alle Israeliten aber flohen, jeder in sein Zelt. -" },
      { number: 18, text: "Abschalom hatte sich schon zu Lebzeiten den Gedenkstein, der jetzt im Königstal steht, herbeischaffen und für sich aufstellen lassen; denn er sagte sich: Ich habe keinen Sohn, der meinen Namen im Gedächtnis (der Menschen) halten würde. Er benannte den Stein nach seinem Namen; deshalb heißt er bis zum heutigen Tag «Abschaloms Hand»", footnotes: ["14,27"] },
      { number: 19, text: "Ahimaaz, der Sohn Zadoks, sagte: Ich will zum König eilen und ihm die freudige Nachricht bringen, dass der Herr ihm Recht gegenüber seinen Feinden verschafft hat." },
      { number: 20, text: "Joab sagte zu ihm: Du würdest heute kein Freudenbote sein. An einem anderen Tag kannst du eine gute Nachricht überbringen; heute würdest du nichts Erfreuliches melden; denn der Sohn des Königs ist tot." },
      { number: 21, text: "Und Joab befahl einem Kuschiter: Geh, melde dem König, was du gesehen hast. Der Kuschiter warf sich vor Joab nieder, dann eilte er davon." },
      { number: 22, text: "Ahimaaz, der Sohn Zadoks, sagte noch einmal zu Joab: Mag kommen, was will, ich möchte auch hineilen, hinter dem Kuschiter her. Joab antwortete: Warum willst du denn hinlaufen, mein Sohn? Du bekommst keinen Botenlohn." },
      { number: 23, text: "Doch Ahimaaz sagte: Mag kommen, was will; ich laufe hin. Joab erwiderte: Dann lauf! Ahimaaz lief los, nahm den Weg durch die Jordansenke und überholte den Kuschiter.", footnotes: ["Doch Ahimaaz sagte: ergänzt nach G."] },
      { number: 24, text: "David saß zwischen den beiden Toren. Der Späher aber war auf das Dach des Tores, auf die Mauer, gestiegen, und als er Ausschau hielt, sah er einen einzelnen Mann herbeilaufen." },
      { number: 25, text: "Der Späher rief dem König die Meldung zu. Der König sagte: Wenn er allein ist, dann bringt er eine gute Nachricht. Während der Mann herankam," },
      { number: 26, text: "sah der Späher noch einen anderen herbeieilen und rief zum Tor hinab: Da läuft noch ein einzelner Mann herbei. Der König sagte: Auch er bringt eine gute Nachricht." },
      { number: 27, text: "Darauf sagte der Späher: Ich sehe, der erste läuft in der Art des Ahimaaz, des Sohnes Zadoks. Und der König sagte: Das ist ein guter Mann, er kommt mit einer guten Nachricht." },
      { number: 28, text: "Ahimaaz rief dem König den Friedensgruß zu, warf sich vor ihm mit dem Gesicht zur Erde nieder und sagte: Gepriesen sei der Herr, dein Gott, der (dir) die Männer ausgeliefert hat, die ihre Hand gegen meinen Herrn, den König, erhoben haben." },
      { number: 29, text: "Der König fragte: Geht es dem Jungen, Abschalom, gut? Ahimaaz antwortete: Ich sah ein großes Getümmel, als Joab den Knecht des Königs, deinen Knecht, wegschickte; darum weiß ich nicht, was da geschah." },
      { number: 30, text: "Der König befahl: Tritt zur Seite und stell dich hierher! Ahimaaz trat zur Seite und blieb dort stehen." },
      { number: 31, text: "Da kam auch der Kuschiter und sagte: Mein Herr, der König, lasse sich die gute Nachricht bringen, dass der Herr dir heute Recht verschafft hat gegenüber allen, die sich gegen dich erhoben hatten." },
      { number: 32, text: "Der König fragte den Kuschiter: Geht es dem Jungen, Abschalom, gut? Der Kuschiter antwortete: Wie dem jungen Mann möge es allen Feinden meines Herrn, des Königs, ergehen, allen, die sich in böser Absicht gegen dich erhoben haben." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Da zuckte der König zusammen, stieg in den oberen Raum des Tores hinauf und weinte. Während er hinaufging, rief er (immer wieder): Mein Sohn Abschalom, mein Sohn, mein Sohn Abschalom! Wäre ich doch an deiner Stelle gestorben, Abschalom, mein Sohn, mein Sohn!", heading: "Davids Klage um Abschalom: 19,1-9a" },
      { number: 2, text: "Man meldete Joab: Der König weint und trauert um Abschalom." },
      { number: 3, text: "So wurde der Tag der Rettung für das ganze Volk zu einem Trauertag; denn die Leute hörten an diesem Tag: Der König ist voll Schmerz wegen seines Sohnes." },
      { number: 4, text: "Die Leute schlichen sich an jenem Tag in die Stadt, wie sich Leute davonschleichen, die Schande auf sich geladen haben, weil sie im Kampf geflohen sind." },
      { number: 5, text: "Der König aber hatte sein Gesicht verhüllt und rief laut: Mein Sohn Abschalom! Abschalom, mein Sohn, mein Sohn!" },
      { number: 6, text: "Da ging Joab zum König ins Haus hinein und sagte: Du hast heute alle deine Diener offen beschimpft, die dir, deinen Söhnen und Töchtern, deinen Frauen und Nebenfrauen das Leben gerettet haben." },
      { number: 7, text: "Du zeigst ja denen deine Liebe, die dich hassen, und deinen Hass denen, die dich lieben; denn du gabst uns heute zu verstehen, dass dir die Anführer und die Krieger nichts bedeuten. Jetzt weiß ich, dass es in deinen Augen ganz richtig wäre, wenn Abschalom noch am Leben wäre, wir alle aber heute gestorben wären." },
      { number: 8, text: "Doch nun steh auf, geh hinaus und sag deinen Leuten einige anerkennende Worte! Denn ich schwöre dir beim Herrn: Wenn du nicht (zu ihnen) hinausgehst, dann wird bis zur kommenden Nacht keiner mehr bei dir sein und das wird für dich schlimmer sein als alles Unheil, das dir von deiner Jugend an bis jetzt zugestoßen ist." },
      { number: 9, text: "Da stand der König auf und setzte sich in das Tor. Und im Volk wurde bekannt: Der König sitzt im Tor. Da kamen alle Leute zum König." },
      { number: 10, text: "Nachdem die Israeliten geflohen und in ihre Zelte zurückgekehrt waren, stritten sich alle Leute in allen Stämmen Israels und sagten: Der König hat uns aus der Gewalt unserer Feinde befreit, er hat uns aus der Gewalt der Philister gerettet und jetzt hat er vor Abschalom aus dem Land fliehen müssen.", heading: "Davids Rückkehr nach Jerusalem: 19,9b-44" },
      { number: 11, text: "Abschalom aber, den wir zu unserem König gesalbt haben, ist im Kampf gefallen. Warum zögert ihr jetzt, den König zurückzuholen?" },
      { number: 12, text: "So redete man in ganz Israel. Und die Nachricht davon drang bis zum König vor [in sein Haus]. Da schickte König David (einen Boten) zu den Priestern Zadok und Abjatar und ließ ihnen sagen: Sagt zu den Ältesten Judas: Warum wollt ihr die letzten sein, die den König in sein Haus zurückholen?", footnotes: ["V. 12 a und b sind sinngemäß umgestellt."] },
      { number: 13, text: "Ihr seid meine Brüder, ihr seid mein Fleisch und Bein. Warum wollt ihr die letzten sein, die den König zurückholen?", footnotes: ["Gen 2,23"] },
      { number: 14, text: "Und zu Amasa sollt ihr sagen: Bist du nicht mein Fleisch und Bein? Gott soll mir dies und das antun, wenn du nicht bei mir für alle Zeit anstelle Joabs der Befehlshaber des Heeres sein wirst." },
      { number: 15, text: "So machte David sich das Herz aller Männer in Juda geneigt, sodass sie wie ein Mann zu ihm hielten. Sie sandten dem König die Aufforderung: Kehr zurück, du selbst und alle deine Diener!" },
      { number: 16, text: "Da machte sich der König auf den Rückweg und kam an den Jordan, die Judäer aber, die dem König entgegengezogen waren, um ihn über den Jordan zu geleiten, waren bis nach Gilgal gekommen.", footnotes: ["Gilgal: in der Nähe von Jericho (vgl. 1 Sam 7,16)."] },
      { number: 17, text: "Auch der Benjaminiter Schimi aus Bahurim, der Sohn Geras, eilte herbei und zog mit den Männern von Juda dem König David entgegen.", footnotes: ["(17-24) 16,5-12; 1 Kön 2,8"] },
      { number: 18, text: "Bei ihm waren tausend Mann aus Benjamin, darunter Ziba, der Diener des Hauses Saul, mit seinen fünfzehn Söhnen und seinen zwanzig Knechten. Sie waren schon vor dem König an den Jordan gelangt" },
      { number: 19, text: "und durchschritten die Furt, um die Familie des Königs hinüberzugeleiten und alles zu tun, was dem König gut erschien. Als der König gerade den Jordan überschreiten wollte, fiel Schimi, der Sohn Geras, vor ihm nieder", footnotes: ["und durchschritten: Text korr."] },
      { number: 20, text: "und sagte zu ihm: Mein Herr möge mir meine Schuld nicht anrechnen; mögest du nicht an das denken, was dein Knecht sich an dem Tag zu Schulden kommen ließ, als mein Herr, der König, Jerusalem verließ; der König nehme es sich nicht so zu Herzen;" },
      { number: 21, text: "denn dein Knecht weiß, dass er gesündigt hat. Sieh her, ich bin heute als Erster vom ganzen Haus Josef hergekommen, um meinem Herrn, dem König, entgegenzugehen." },
      { number: 22, text: "Da fragte Abischai, der Sohn der Zeruja: Müsste nicht Schimi dafür mit dem Tod bestraft werden, dass er den Gesalbten des Herrn verflucht hat?" },
      { number: 23, text: "David aber sagte: Was habe ich mit euch zu schaffen, ihr Söhne der Zeruja? Warum benehmt ihr euch plötzlich wie Feinde von mir? Soll heute jemand in Israel getötet werden, wo ich doch weiß, dass ich ab heute wieder König von Israel bin?" },
      { number: 24, text: "Und der König sagte zu Schimi: Du sollst nicht sterben. Der König bekräftigte es ihm mit einem Schwur." },
      { number: 25, text: "Auch Merib-Baal, der Sohn Sauls, kam dem König entgegen. Er hatte seine Füße und seinen Bart nicht mehr gepflegt und seine Kleider nicht gewaschen seit dem Tag, an dem der König weggegangen war, bis zu dem Tag, an dem er wohlbehalten zurückkam.", footnotes: ["(25-31) 9,6; 16,3f", "Merib-Baal hat wegen des Unglücks, das David getroffen hat, getrauert."] },
      { number: 26, text: "Als er von Jerusalem aus dem König entgegenkam, fragte ihn der König: Warum bist du damals nicht mit mir weggegangen, Merib-Baal?", footnotes: ["von Jerusalem aus: Text korr. (vgl. 9,10f); H: nach Jerusalem."] },
      { number: 27, text: "Er antwortete: Mein Herr und König, mein Diener hat mich hintergangen. Dein Knecht sagte zu ihm: Ich will mir die Eselin satteln und mit dem König zusammen wegreiten; dein Knecht ist ja lahm." },
      { number: 28, text: "Man hat deinen Knecht bei meinem Herrn, dem König, verleumdet. Aber mein Herr, der König, ist wie der Engel Gottes; darum tu, was dir gefällt." },
      { number: 29, text: "Obwohl das ganze Haus meines Vaters von meinem Herrn, dem König, nichts anderes als den Tod zu erwarten hatte, hast du deinen Knecht unter die aufgenommen, die an deinem Tisch essen. Was habe ich noch für ein Recht, den König weiterhin anzurufen?", footnotes: ["(29-30) 9,7.9-11"] },
      { number: 30, text: "Der König antwortete ihm: Warum machst du so (viele) Worte? Ich habe bestimmt: Du und Ziba, ihr sollt euch das Land teilen." },
      { number: 31, text: "Darauf sagte Merib-Baal: Er kann auch das ganze nehmen, nachdem mein Herr, der König, wohlbehalten in sein Haus zurückgekehrt ist." },
      { number: 32, text: "Der Gileaditer Barsillai war aus Roglim herabgekommen und mit dem König an den Jordan gezogen, um ihn am Jordan zu verabschieden.", footnotes: ["17,27"] },
      { number: 33, text: "Barsillai war sehr alt, ein Mann von achtzig Jahren. Er hatte den König versorgt, als dieser sich in Mahanajim aufhielt; er war nämlich sehr reich." },
      { number: 34, text: "Der König sagte zu Barsillai: Zieh mit mir hinüber, ich will für dich bei mir in Jerusalem sorgen." },
      { number: 35, text: "Doch Barsillai antwortete dem König: Wie viele Jahre habe ich denn noch zu leben, dass ich mit dem König nach Jerusalem hinaufziehen sollte?" },
      { number: 36, text: "Ich bin jetzt achtzig Jahre alt. Kann ich denn noch Gutes und Böses unterscheiden? Kann dein Knecht noch Geschmack finden an dem, was er isst und trinkt? Höre ich denn noch die Stimme der Sänger und Sängerinnen? Warum soll denn dein Knecht noch meinem Herrn, dem König, zur Last fallen?" },
      { number: 37, text: "Nur eine kleine Strecke wollte dein Knecht den König zum Jordan begleiten. Warum will sich der König bei mir in dieser Weise erkenntlich zeigen?" },
      { number: 38, text: "Dein Knecht möchte umkehren und in seiner Heimatstadt beim Grab seines Vaters und seiner Mutter sterben. Aber hier ist dein Knecht Kimham; er mag mit meinem Herrn, dem König, hinüberziehen. Tu für ihn, was dir gefällt.", footnotes: ["Kimham ist der Sohn Barsillais; als Untertan Davids ist er dessen Knecht."] },
      { number: 39, text: "Der König erwiderte: Kimham soll mit mir hinüberziehen; ich werde für ihn tun, was du für gut hältst. Alles aber, was du von mir begehrst, will ich für dich tun." },
      { number: 40, text: "Darauf zog das ganze Volk über den Jordan und auch der König ging hinüber. Der König küsste Barsillai und segnete ihn und Barsillai kehrte in seinen Heimatort zurück." },
      { number: 41, text: "Der König aber zog weiter nach Gilgal und Kimham ging mit ihm. Das ganze Volk von Juda zog mit dem König, dazu halb Israel." },
      { number: 42, text: "Da kamen alle Israeliten zum König und sagten zu ihm: Warum haben unsere Brüder, die Männer von Juda, dich uns geraubt und den König, seine Familie und alle seine Männer über den Jordan geführt?", footnotes: ["Die Israeliten hatten früher als die Judäer die Rückführung des Königs geplant; sie fühlen sich zurückgesetzt."] },
      { number: 43, text: "Alle Judäer antworteten den Israeliten: Weil der König uns näher steht. Warum bist du darüber erzürnt? Haben wir denn ein Stück vom König gegessen oder ist er etwa von uns weggetragen worden?" },
      { number: 44, text: "Die Israeliten antworteten den Judäern: Ich habe zehn Anteile am König, außerdem bin ich dir gegenüber der Erstgeborene. Warum hast du mich also gering geachtet? War es nicht zuerst mein Wunsch, meinen König zurückzuholen? Die Antwort der Judäer hierauf war noch schärfer als die Rede der Israeliten." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Damals lebte ein niederträchtiger Mensch namens Scheba, ein Sohn des Bichri, ein Benjaminiter. Er ließ das Widderhorn blasen und rief: Welchen Anteil haben wir an David? / Wir haben keinen Erbbesitz beim Sohn Isais. / In deine Zelte, Israel!", heading: "Schebas Aufstand gegen David: 20,1-22", footnotes: ["1 Kön 12,16"] },
      { number: 2, text: "Da verließen alle Israeliten Davids Gefolgschaft und folgten Scheba, dem Sohn Bichris. Die Männer aus Juda aber hielten weiter zu ihrem König, vom Jordan bis nach Jerusalem." },
      { number: 3, text: "Als David in sein Haus nach Jerusalem zurückkehrte, nahm der König die zehn Nebenfrauen, die er zurückgelassen hatte, um das Haus zu bewachen, und ließ sie in Gewahrsam bringen. Er versorgte sie, ging aber nicht mehr zu ihnen und sie blieben bis zu ihrem Tod eingesperrt - schon zu Lebzeiten des Mannes im Witwenstand.", footnotes: ["12,11; 16,21f"] },
      { number: 4, text: "Der König befahl Amasa: Ruf mir in drei Tagen die Männer Judas zusammen; dann sei auch du selbst wieder hier!" },
      { number: 5, text: "Amasa ging, um die Judäer zusammenzurufen. Aber er versäumte die Frist, die der König ihm gesetzt hatte." },
      { number: 6, text: "Da sagte David zu Abischai: Nun wird uns Scheba, der Sohn Bichris, gefährlicher als Abschalom. Nimm darum die Knechte deines Herrn mit und verfolg Scheba, damit er nicht die befestigten Städte einnimmt und uns die Augen ausreißt.", footnotes: ["und uns die Augen ausreißt: Übersetzung und Bedeutung unsicher."] },
      { number: 7, text: "Da zog Abischai mit Joab und den Keretern und Peletern und allen Helden in den Kampf. Sie zogen von Jerusalem aus, um Scheba, den Sohn Bichris, zu verfolgen.", footnotes: ["zog Abischai mit Joab: Text korr., vgl. G; H: zogen mit ihm die Männer Joabs und die . . ."] },
      { number: 8, text: "Als sie bei dem großen Stein von Gibeon waren, war Amasa gerade vor ihnen angekommen. Joab trug sein Gewand [seine Kleidung] und darüber einen Gurt für das Schwert. Dieses hing so in einer Scheide an seiner Hüfte, dass es herausglitt, wenn die Scheide hervorkam.", footnotes: ["Text und Sinn unsicher."] },
      { number: 9, text: "Joab sagte zu Amasa: Geht es dir gut, mein Bruder? und griff mit der rechten Hand nach dem Bart Amasas, um ihn zu küssen." },
      { number: 10, text: "Amasa aber achtete nicht auf das Schwert, das Joab in der (linken) Hand hatte, und Joab stieß es ihm in den Bauch, sodass seine Eingeweide zu Boden quollen. Amasa starb, ohne dass Joab ein zweites Mal zustieß. Dann setzten Joab und sein Bruder Abischai die Verfolgung Schebas, des Sohnes Bichris, fort.", footnotes: ["1 Kön 2,5"] },
      { number: 11, text: "Einer von den Leuten Joabs aber blieb bei Amasa stehen und rief: Wer zu Joab hält und wer zu David gehört, der folge Joab nach." },
      { number: 12, text: "Amasa aber hatte sich in seinem Blut mitten auf der Straße gewälzt. Als der Mann sah, dass alle Leute stehen blieben, schaffte er Amasa von der Straße weg auf das Feld und warf ein Kleidungsstück über ihn, weil jeder, der vorbeikam und ihn sah, stehen blieb." },
      { number: 13, text: "Erst nachdem er ihn von der Straße weggeschafft hatte, zogen alle Männer vorüber und Joab nach, um Scheba, den Sohn Bichris, zu verfolgen." },
      { number: 14, text: "Scheba zog in allen Stämmen Israels umher, bis nach Abel-Bet-Maacha. Alle Bichriter sammelten sich um ihn und folgten ihm.", footnotes: ["Alle Bichriter sammelten sich um ihn: Text korr. Abel-Bet-Maacha: in Nordgaliläa."] },
      { number: 15, text: "Als aber die Leute Joabs angekommen waren, schlossen sie ihn in Abel-Bet-Maacha ein; sie schütteten einen Damm gegen die Stadt auf, der bis an die Mauer heranreichte, und alle Leute, die bei Joab geblieben waren, bemühten sich, die Mauer zu zerstören und zum Einsturz zu bringen." },
      { number: 16, text: "Da rief eine kluge Frau aus der Stadt: Hört her! Hört her! Sagt Joab: Komm hierher, ich will mit dir reden." },
      { number: 17, text: "Er trat zu ihr heran. Die Frau fragte: Bist du Joab? Er antwortete: Ja, ich bin es. Da sagte sie zu ihm: Hör auf die Worte deiner Magd! Er antwortete: Ich höre." },
      { number: 18, text: "Sie sagte: Früher pflegte man zu sagen: Man frage doch in Abel an, dann kann man die Sache zu einem (guten) Ende führen." },
      { number: 19, text: "Wir sind die friedlichsten, treuesten Menschen in Israel. Du aber bist darauf aus, eine Stadt, die für Israel (wie) eine Mutter ist, zu vernichten. Warum zerstörst du das Erbe des Herrn?" },
      { number: 20, text: "Joab antwortete: Das liegt mir ganz und gar fern: Ich will die Stadt nicht vernichten und zerstören." },
      { number: 21, text: "So ist es nicht. Vielmehr hat ein Mann aus dem Gebirge Efraim namens Scheba, ein Sohn Bichris, seine Hand gegen den König, gegen David, erhoben. Ihn allein gebt heraus! Dann werde ich von der Stadt abziehen. Die Frau sagte zu Joab: Gib Acht, sein Kopf wird dir über die Mauer zugeworfen werden." },
      { number: 22, text: "Dann redete die kluge Frau mit allen Leuten (in der Stadt), und sie schlugen Scheba, dem Sohn Bichris, den Kopf ab und warfen ihn Joab zu. Da ließ Joab das Widderhorn blasen und alle zogen von der Stadt ab und gingen wieder zu ihren Zelten; Joab aber kehrte nach Jerusalem zum König zurück." },
      { number: 23, text: "Joab war Befehlshaber des ganzen Heeres der Israeliten und Benaja, der Sohn Jojadas, war der Befehlshaber der Kereter und Peleter.", heading: "Die Beamten Davids: 20,23-26", footnotes: ["1 Kön 4,4.6; (23-26) 8,16-18; 1 Chr 18,14-17"] },
      { number: 24, text: "Adoniram beaufsichtigte die Fronarbeiten und Joschafat, der Sohn Ahiluds, war der Sprecher des Königs." },
      { number: 25, text: "Schewa war Staatsschreiber und Zadok und Abjatar waren Priester." },
      { number: 26, text: "Auch der Jaïriter Ira war Davids Priester." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Zur Zeit Davids herrschte drei Jahre hintereinander eine Hungersnot. Da suchte David den Herrn auf (um ihn zu befragen). Der Herr sagte: Auf Saul und seinem Haus lastet eine Blutschuld, weil er die Gibeoniter getötet hat.", heading: "Nachträge: 21,1 - 24,25 | Die Rache der Gibeoniter und die Treue der Rizpa: 21,1-14" },
      { number: 2, text: "Da rief der König die Gibeoniter zu sich und redete mit ihnen. Die Gibeoniter stammten nicht von den Söhnen Israels ab, sondern von einem Rest der Amoriter. Obwohl die Israeliten sich ihnen gegenüber mit einem Eid (zum Wohlwollen) verpflichtet hatten, versuchte Saul in seinem Eifer für die Söhne Israels und Judas, sie zu vernichten.", footnotes: ["Jos 9,3.15.19"] },
      { number: 3, text: "David sagte also zu den Gibeonitern: Was soll ich für euch tun? Womit kann ich Sühne leisten, damit ihr dem Erbe des Herrn wieder Segen bringt?" },
      { number: 4, text: "Die Gibeoniter antworteten ihm: Wir wollen weder Silber noch Gold von Saul und seinem Haus; auch steht es uns nicht zu, jemand in Israel zu töten. David sagte: Was meint ihr, was soll ich für euch tun?" },
      { number: 5, text: "Sie sagten zum König: Der Mann, der uns vernichten wollte und der darauf aus war, uns auszurotten, sodass wir uns in keinem Gebiet Israels mehr hätten halten können," },
      { number: 6, text: "von dessen Söhnen soll man uns sieben Männer geben. Wir wollen sie vor dem Herrn im Gibea Sauls, des vom Herrn Erwählten, hinrichten. Der König antwortete: Ich will sie euch geben.", footnotes: ["Die Gibeoniter wollen die Blutrache ausüben."] },
      { number: 7, text: "Merib-Baal, den Sohn Jonatans, des Sohnes Sauls, aber verschonte der König wegen der Abmachung zwischen David und Jonatan, dem Sohn Sauls, die sie mit einem Eid beim Herrn bekräftigt hatten.", footnotes: ["1 Sam 18,3", "Merib-Baal: H: Mefi-Boschet (vgl. die Anmerkung zu 4,4)."] },
      { number: 8, text: "Der König nahm Armoni und Mefi-Boschet, die beiden Söhne, die Rizpa, die Tochter Ajas, dem Saul geboren hatte, und die fünf Söhne, die Michal, die Tochter Sauls, dem Adriël, dem Sohn Barsillais aus Mehola, geboren hatte.", footnotes: ["3,7; 1 Sam 18,19"] },
      { number: 9, text: "Er lieferte sie den Gibeonitern aus, die sie auf dem Berg vor dem Angesicht des Herrn hinrichteten; so kamen alle sieben auf einmal um. Sie wurden in den ersten Tagen der Ernte getötet, zu Beginn der Gerstenernte." },
      { number: 10, text: "Rizpa, die Tochter Ajas, aber nahm Sackleinen und legte es für sich auf den Felsen und (sie blieb dort) bei den Toten vom Beginn der Ernte, bis sich Wasser vom Himmel über die Toten ergoss. Sie ließ nicht zu, dass bei Tag die Vögel des Himmels und bei Nacht die Tiere des Feldes an sie herankamen." },
      { number: 11, text: "Als David erzählt wurde, was Rizpa, die Tochter Ajas und Nebenfrau Sauls, getan hatte," },
      { number: 12, text: "holte er die Gebeine Sauls und die Gebeine seines Sohnes Jonatan von den Bürgern von Jabesch-Gilead, die sie heimlich vom Marktplatz in Bet-Schean weggenommen hatten, wo die Philister sie aufgehängt hatten, als sie Israel am Gilboa schlugen.", footnotes: ["1 Sam 31,12f"] },
      { number: 13, text: "Er ließ also von dort die Gebeine Sauls und die Gebeine seines Sohnes Jonatan heraufbringen. Dann sammelte man die Gebeine der Hingerichteten ein" },
      { number: 14, text: "und begrub sie zusammen mit den Gebeinen Sauls und seines Sohnes Jonatan in Zela im Land Benjamin, im Grab von Sauls Vater Kisch. Man tat alles, was der König befohlen hatte, und Gott ließ sich daraufhin für das Land gnädig stimmen." },
      { number: 15, text: "Wieder einmal kam es zum Krieg zwischen den Philistern und Israel. David zog mit seinen Leuten hinab. Als sie gegen die Philister kämpften, wurde David müde.", heading: "Die Helden Davids und ihre Taten: 21,15-22" },
      { number: 16, text: "Jischbi aus Nob, ein Rafaïter, dessen Bronze-Speer dreihundert Schekel wog und der mit einem neuen Schwert umgürtet war, sagte, er werde David erschlagen." },
      { number: 17, text: "Aber Abischai, der Sohn der Zeruja, kam David zu Hilfe und schlug den Philister tot. Damals leisteten die Männer Davids ihm folgenden Schwur: Du wirst nicht mehr mit uns in den Kampf ziehen, damit du nicht die Leuchte Israels auslöschst.", footnotes: ["Die «Leuchte Israels» ist der König, also David."] },
      { number: 18, text: "Danach kam es bei Gob wieder zum Kampf mit den Philistern. Damals erschlug Sibbechai aus Huscha den Sippai, der zu den Rafaïtern gehörte.", footnotes: ["(18-22) 1 Chr 20,4-8"] },
      { number: 19, text: "Als es wieder einmal bei Gob zum Kampf gegen die Philister kam, erschlug Elhanan, der Sohn Jaïrs aus Betlehem, den Goliat aus Gat, dessen Speer einem Weberbaum glich.", footnotes: ["Jaïrs: Text korr. nach 1 Chr 20,5."] },
      { number: 20, text: "Dann kam es noch einmal bei Gat zum Kampf. Da trat ein Mann von riesenhafter Größe auf; er hatte an jeder Hand sechs Finger und an jedem Fuß sechs Zehen, zusammen vierundzwanzig; auch er stammte von Rafa ab." },
      { number: 21, text: "Als er Israel verhöhnte, erschlug ihn Jonatan, der Sohn von Davids Bruder Schima." },
      { number: 22, text: "Diese vier stammten von Rafa aus Gat ab; sie fielen durch die Hand Davids und seiner Krieger." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "David sang dem Herrn an dem Tag, als ihn der Herr aus der Gewalt all seiner Feinde und aus der Gewalt Sauls errettet hatte, folgendes Lied:", heading: "Davids Dankgebet: 22,1-51", footnotes: ["(1-51) Ps 18"] },
      { number: 2, text: "Herr, du mein Fels, meine Burg, mein Retter," },
      { number: 3, text: "mein Gott, mein Fels, bei dem ich mich berge, / mein Schild und sicheres Heil, meine Feste, / meine Zuflucht, mein Helfer, / der mich vor der Gewalttat rettet." },
      { number: 4, text: "Ich rufe: Der Herr sei gepriesen!, / und ich werde vor meinen Feinden gerettet." },
      { number: 5, text: "Denn mich umfingen die Wellen des Todes, / mich erschreckten die Fluten des Verderbens." },
      { number: 6, text: "Die Bande der Unterwelt umstrickten mich, / über mich fielen die Schlingen des Todes." },
      { number: 7, text: "In meiner Not rief ich zum Herrn / und rief zu meinem Gott. / Aus seinem Heiligtum hörte er mein Rufen, / mein Hilfeschrei (drang) zu seinen Ohren." },
      { number: 8, text: "Da wankte und schwankte die Erde, / die Grundfesten des Himmels erbebten. / Sie wankten, denn sein Zorn war entbrannt." },
      { number: 9, text: "Rauch stieg aus seiner Nase auf, / aus seinem Mund kam verzehrendes Feuer, / glühende Kohlen sprühten aus von ihm." },
      { number: 10, text: "Er neigte den Himmel und fuhr herab, / zu seinen Füßen dunkle Wolken." },
      { number: 11, text: "Er fuhr auf dem Kerub und flog daher; / er schwebte auf den Flügeln des Windes." },
      { number: 12, text: "Er hüllte sich in Finsternis, / in dunkles Wasser und dichtes Gewölk wie in ein Zelt.", footnotes: ["dunkles: Text korr. nach Ps 18."] },
      { number: 13, text: "Von seinem Glanz flammten glühende Kohlen auf." },
      { number: 14, text: "Da ließ der Herr den Donner vom Himmel her dröhnen, / der Höchste ließ seine Stimme erschallen." },
      { number: 15, text: "Er schoss seine Pfeile und streute sie, / er schleuderte Blitze und jagte sie dahin." },
      { number: 16, text: "Da wurden sichtbar die Tiefen des Meeres, / die Grundfesten der Erde wurden entblößt / durch das Drohen des Herrn, / vor dem Schnauben seines zornigen Atems." },
      { number: 17, text: "Er griff aus der Höhe herab und fasste mich, / zog mich heraus aus gewaltigen Wassern." },
      { number: 18, text: "Er entriss mich meinen mächtigen Feinden, / die stärker waren als ich und mich hassten." },
      { number: 19, text: "Sie überfielen mich am Tag meines Unheils, / doch der Herr wurde mein Halt." },
      { number: 20, text: "Er führte mich hinaus ins Weite, / er befreite mich, denn er hatte an mir Gefallen." },
      { number: 21, text: "Der Herr hat mir vergolten, / weil ich gerecht bin und meine Hände rein sind." },
      { number: 22, text: "Denn ich hielt mich an die Wege des Herrn / und fiel nicht ruchlos ab von meinem Gott." },
      { number: 23, text: "Ja, ich habe alle seine Gebote vor Augen, / weiche von seinen Gesetzen niemals ab." },
      { number: 24, text: "Ich war vor ihm ohne Makel, / ich nahm mich in Acht vor der Sünde." },
      { number: 25, text: "Darum hat der Herr mir vergolten, / weil ich gerecht bin / und weil ich rein bin vor seinen Augen." },
      { number: 26, text: "Gegen den Treuen zeigst du dich treu, / an dem Aufrichtigen handelst du recht." },
      { number: 27, text: "Gegen den Reinen zeigst du dich rein, / doch falsch gegen den Falschen.", footnotes: ["doch falsch: Text korr. nach Ps 18."] },
      { number: 28, text: "Dem bedrückten Volk bringst du Heil, / doch die Blicke der Stolzen zwingst du nieder.", footnotes: ["doch die Blicke der Stolzen: Text korr. nach Ps 18."] },
      { number: 29, text: "Ja, du bist meine Leuchte, Herr. / Der Herr macht meine Finsternis hell." },
      { number: 30, text: "Mit dir erstürme ich Wälle, / mit meinem Gott überspringe ich Mauern." },
      { number: 31, text: "Vollkommen ist Gottes Weg, / das Wort des Herrn ist im Feuer geläutert. / Ein Schild ist er für alle, die sich bei ihm bergen." },
      { number: 32, text: "Denn wer ist Gott als allein der Herr, / wer ist ein Fels, wenn nicht unser Gott?" },
      { number: 33, text: "Gott ist meine starke Burg, / er gab mir meinen Weg ohne Hindernis frei." },
      { number: 34, text: "Er ließ mich springen schnell wie Hirsche, / auf hohem Weg ließ er mich gehen." },
      { number: 35, text: "Er lehrte meine Hände kämpfen, / meine Arme, den ehernen Bogen zu spannen." },
      { number: 36, text: "Du gabst mir deine Hilfe zum Schild, / dein Zuspruch machte mich groß." },
      { number: 37, text: "Du schaffst meinen Schritten weiten Raum, / meine Knöchel wanken nicht." },
      { number: 38, text: "Ich verfolge meine Feinde und vertilge sie, / ich kehre nicht um, bis sie vernichtet sind." },
      { number: 39, text: "Ich vernichte sie, ich schlage sie nieder; / sie können sich nicht mehr erheben, / sie fallen und liegen unter meinen Füßen." },
      { number: 40, text: "Du hast mich zum Kampf mit Kraft umgürtet, / hast (alle) in die Knie gezwungen, / die sich gegen mich erhoben." },
      { number: 41, text: "Meine Feinde hast du zur Flucht gezwungen; / ich konnte die vernichten, die mich hassen." },
      { number: 42, text: "Sie schreien, doch hilft ihnen niemand, / sie schreien zum Herrn, doch er gibt keine Antwort.", footnotes: ["Sie schreien: Text korr. nach Ps 18."] },
      { number: 43, text: "Ich zermalme sie wie Staub auf der Erde, / wie Unrat auf der Straße zertrete, zermalme ich sie." },
      { number: 44, text: "Du rettest mich aus dem Streit meines Volkes, / bewahrst mich als Haupt ganzer Völker. / Stämme, die ich früher nicht kannte, / sind mir nun untertan." },
      { number: 45, text: "Mir huldigen die Söhne der Fremde; / sobald sie mich nur hören, gehorchen sie mir." },
      { number: 46, text: "Die Söhne der Fremde schwinden dahin, / sie kommen zitternd aus ihren Burgen hervor.", footnotes: ["sie kommen zitternd: Text korr. nach Ps 18."] },
      { number: 47, text: "Es lebt der Herr! Mein Fels sei gepriesen! / Der Gott, der Fels meines Heils, sei hoch erhoben;" },
      { number: 48, text: "denn Gott verschaffte mir Vergeltung / und unterwarf mir die Völker." },
      { number: 49, text: "Du hast mich meinen Feinden entführt, / mich über meine Gegner erhoben, / dem Mann der Gewalt mich entrissen." },
      { number: 50, text: "Darum will ich dir danken, Herr, vor den Völkern, / ich will deinem Namen singen und spielen." },
      { number: 51, text: "Seinem König verlieh er große Hilfe, / Huld erwies er seinem Gesalbten, / David und seinem Stamm auf ewig." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Das sind die letzten Worte Davids: Spruch Davids, des Sohnes Isais, / Spruch des hochgestellten Mannes, / des Gesalbten des Gottes Jakobs, / des Lieblingshelden der Lieder Israels:", heading: "Davids letzte Worte: 23,1-7" },
      { number: 2, text: "Der Geist des Herrn sprach durch mich, / sein Wort war auf meiner Zunge.", footnotes: ["2-7: Davids «letzte Worte» (V. 1) sind keine Abschiedsrede, sondern ein Danklied für die Wohltaten Gottes (V. 5), eingerahmt von einer Weisung für den König (VV. 3f.6f)."] },
      { number: 3, text: "Der Gott Israels sprach, / zu mir sagte der Fels Israels: / Wer gerecht über die Menschen herrscht, / wer voll Gottesfurcht herrscht," },
      { number: 4, text: "der ist wie das Licht am Morgen, / wenn die Sonne aufstrahlt / an einem Morgen ohne Wolken, / der nach dem Regen grünes Gras / aus der Erde hervorsprießen lässt.", footnotes: ["hervorsprießen lässt: Text korr."] },
      { number: 5, text: "Ist nicht mein Haus durch Gott gesichert? / Weil er mir einen ewigen Bund gewährt hat, ist es in allem wohlgeordnet und gut gesichert. / All mein Heil und alles, was ich begehrte, / ließ er es nicht aufsprießen?", footnotes: ["gesichert: Text korr."] },
      { number: 6, text: "All die Übeltäter aber sind wie verwehte Dornen; / man fasst sie nicht mit der Hand an." },
      { number: 7, text: "Wenn jemand sie anrühren will, / rüstet er sich mit Eisen und einer Lanze; / sie werden im Feuer verbrannt.", footnotes: ["H liest am Ende des Verses noch ein Wort, das wohl aus dem folgenden Vers hierher geraten ist."] },
      { number: 8, text: "Das sind die Namen der Helden Davids: Jischbaal, der Hachmoniter, das Haupt der Drei; er schwang seine Streitaxt über achthundert Männer und erschlug sie alle auf einmal.", heading: "Davids Helden: 23,8-39", footnotes: ["(8-39) 1 Chr 11,10-47", "Text korr., vgl. G und 1 Chr 11,11."] },
      { number: 9, text: "Nach ihm kommt (als Zweiter) der Ahoachiter Eleasar, der Sohn Dodos, unter den drei Helden. Er war bei David in Pas-Dammim, als die Philister sich zum Kampf sammelten. Als sich die Israeliten zurückzogen,", footnotes: ["Pas-Dammim: Text korr., vgl. 1 Chr 11,13."] },
      { number: 10, text: "hielt er (allein) stand und schlug auf die Philister ein, bis seine Hand erlahmte und am Schwert erstarrte. So verschaffte der Herr an jenem Tag (Israel) einen großen Sieg. Das Volk kehrte wieder um und folgte Eleasar, aber nur noch, um zu plündern." },
      { number: 11, text: "Nach ihm kommt (als dritter) der Harariter Schamma, der Sohn Ages. Einst versammelten sich die Philister bei Lehi. Dort war ein Linsenfeld. Als das Volk vor den Philistern floh,", footnotes: ["bei Lehi: Text korr."] },
      { number: 12, text: "stellte sich Schamma mitten in das Feld, behauptete es und schlug die Philister. So verlieh ihm der Herr einen großen Sieg." },
      { number: 13, text: "Als sich einst das Lager der Philister in der Rafaïterebene befand, kamen drei von den Dreißig zu Beginn der Ernte hinab zur Höhle Adullam." },
      { number: 14, text: "David hielt sich damals in der Bergfestung auf und ein Posten der Philister lag in Betlehem." },
      { number: 15, text: "Da bekam David großen Durst und er sagte: Wer bringt mir Wasser aus der Zisterne am Tor von Betlehem?", footnotes: ["Da bekam David großen Durst, wörtlich: Da hatte David ein Verlangen."] },
      { number: 16, text: "Da drangen die drei Helden in das Lager der Philister ein, schöpften Wasser aus der Zisterne am Tor von Betlehem, nahmen es mit und brachten es David. Doch er wollte es nicht trinken, sondern goss es für den Herrn als Trankopfer aus" },
      { number: 17, text: "und sagte: Das sei fern von mir, Herr, dass ich so etwas tue. Ist es nicht sozusagen das Blut der Männer, die unter Lebensgefahr hingegangen sind? Darum wollte er es nicht trinken. Eine solche Tat vollbrachten die drei Helden." },
      { number: 18, text: "Joabs Bruder Abischai, der Sohn der Zeruja, war der Anführer der Dreißig. Er schwang seinen Speer über dreihundert Männer und erschlug sie; bei den drei Helden hatte er einen großen Namen." },
      { number: 19, text: "Unter den Dreißig war er hoch geehrt und er wurde ihr Anführer, aber an die Drei reichte er nicht heran.", footnotes: ["Unter den Dreißig: Text korr.; H: Unter den Drei."] },
      { number: 20, text: "Benaja aus Kabzeel, der Sohn Jojadas, war ein tapferer Mann, der große Taten vollbrachte. Er erschlug die beiden Söhne Ariëls aus Moab. Als einmal Schnee gefallen war, kam er zu einer Zisterne und erlegte darin einen Löwen.", footnotes: ["Söhne: eingefügt nach G."] },
      { number: 21, text: "Auch erschlug er einen Ägypter, einen Furcht erregenden Mann. Der Ägypter hatte einen Speer in der Hand. Benaja aber ging nur mit einem Stock auf ihn los, riss ihm den Speer aus der Hand und tötete ihn mit diesem Speer." },
      { number: 22, text: "Solche Taten vollbrachte Benaja, der Sohn Jojadas. Er hatte bei den drei Helden einen großen Namen." },
      { number: 23, text: "Unter den Dreißig war er hoch geehrt, aber an die Drei reichte er nicht heran. David stellte ihn an die Spitze seiner Leibwache." },
      { number: 24, text: "Zu den Dreißig gehörte Joabs Bruder Asaël, ferner Elhanan aus Betlehem, der Sohn Dodos," },
      { number: 25, text: "Schamma und Elika aus Harod," },
      { number: 26, text: "Helez aus Pelet, Ira aus Tekoa, der Sohn des Ikkesch," },
      { number: 27, text: "Abiëser aus Anatot, Sibbechai aus Huscha,", footnotes: ["Sibbechai: Text korr., vgl. G-Handschriften und 1 Chr 11,29."] },
      { number: 28, text: "Zalmon, der Ahoachiter, Machrai aus Netofa," },
      { number: 29, text: "Heled aus Netofa, der Sohn Baanas, Ittai aus Gibea in Benjamin, der Sohn Ribais," },
      { number: 30, text: "Benaja aus Piraton, Hiddai aus Nahale-Gaasch," },
      { number: 31, text: "Abialbon aus Bet-Araba, Asmawet aus Bahurim," },
      { number: 32, text: "Eljachba aus Schaalbon, Jaschen aus Nun,", footnotes: ["Jaschen aus Nun: Text korr., vgl. 1 Chr 11,34 in G-Handschriften."] },
      { number: 33, text: "Schamma aus Harar, Ahiam aus Harar, der Sohn Scharars," },
      { number: 34, text: "Elifelet aus Maacha, der Sohn Ahasbais, Eliam aus Gilo, der Sohn Ahitofels," },
      { number: 35, text: "Hezro aus Karmel, Paarai aus Arab," },
      { number: 36, text: "Jigal aus Zoba, der Sohn Natans, Mibhar aus Gad," },
      { number: 37, text: "Zelek, der Ammoniter, Nachrai aus Beerot, der Waffenträger Joabs, des Sohnes der Zeruja," },
      { number: 38, text: "Ira aus Jattir, Gareb aus Jattir", footnotes: ["aus Jattir: Text korr. vgl. G-Handschriften."] },
      { number: 39, text: "sowie Urija, der Hetiter. Im Ganzen (waren es) siebenunddreißig." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Der Zorn des Herrn entbrannte noch einmal gegen Israel und er reizte David gegen das Volk auf und sagte: Geh, zähl Israel und Juda!", heading: "Davids Volkszählung: 24,1-25", footnotes: ["(1-25) 1 Chr 21,1-26", "Der Vers schließt sachlich an 21,1-14 an. Eine Volkszählung wird als Sünde des Misstrauens gegenüber Gott betrachtet; sie konnte als Nachprüfung des Segens, der sich im Wachstum des Volkes ausdrückte (V. 3), verstanden werden."] },
      { number: 2, text: "Der König befahl Joab, dem Obersten des Heeres, der bei ihm war: Durchstreift alle Stämme Israels von Dan bis Beerscheba, und mustert das Volk, damit ich weiß, wie viele es sind.", footnotes: ["durchstreift: Text korr.; Angleichung an V. 4."] },
      { number: 3, text: "Joab aber sagte zum König: Der Herr, dein Gott, möge das Volk vermehren, hundertmal mehr, als es jetzt ist, und mein Herr, der König, möge es mit eigenen Augen sehen. Warum aber hat mein Herr, der König, Gefallen an einer solchen Sache?" },
      { number: 4, text: "Doch der König beharrte gegenüber Joab und den Anführern des Heeres auf seinem Befehl und Joab und die Anführer des Heeres verließen den König, um das Volk Israel zu mustern." },
      { number: 5, text: "Sie überschritten den Jordan und begannen bei Aroër, rechts von der Stadt, die mitten im Tal liegt. Dann gingen sie auf Gad und Jaser zu", footnotes: ["begannen: Text korr., vgl. G; H: lagerten sich."] },
      { number: 6, text: "und weiter nach Gilead und ins Land der Hetiter nach Kadesch. Von dort aus zogen sie nach Dan und von Dan wandten sie sich nach Sidon.", footnotes: ["nach Kadesch: Text korr., vgl. G. - wandten sie sich: Text korr., vgl. G."] },
      { number: 7, text: "Dann kamen sie zur Festung Tyrus und zu all den Städten der Hiwiter und der Kanaaniter. Darauf zogen sie in den Negeb von Juda hinab nach Beerscheba." },
      { number: 8, text: "So durchstreiften sie das ganze Land und kamen nach neun Monaten und zwanzig Tagen wieder nach Jerusalem zurück." },
      { number: 9, text: "Und Joab gab dem König das Ergebnis der Volkszählung bekannt: Israel zählte achthunderttausend Krieger, die mit dem Schwert kämpfen konnten, und Juda fünfhunderttausend." },
      { number: 10, text: "Dann aber schlug David das Gewissen, weil er das Volk gezählt hatte, und er sagte zum Herrn: Ich habe schwer gesündigt, weil ich das getan habe. Doch vergib deinem Knecht seine Schuld, Herr; denn ich habe sehr unvernünftig gehandelt." },
      { number: 11, text: "Als David am Morgen aufstand, war bereits folgendes Wort des Herrn an den Propheten Gad, den Seher Davids, ergangen:" },
      { number: 12, text: "Geh und sag zu David: So spricht der Herr: Dreierlei lege ich dir vor. Wähl dir eines davon! Das werde ich dir antun." },
      { number: 13, text: "Gad kam zu David, teilte ihm das Wort mit und sagte: Was soll über dich kommen? Sieben Jahre Hungersnot in deinem Land? Oder drei Monate, in denen dich deine Feinde verfolgen und du vor ihnen fliehen musst? Oder soll drei Tage lang die Pest in deinem Land wüten? Überleg dir sehr genau, was ich dem, der mich gesandt hat, als Antwort überbringen soll." },
      { number: 14, text: "Da sagte David zu Gad: Ich habe große Angst. Wir wollen lieber dem Herrn in die Hände fallen, denn seine Barmherzigkeit ist groß; den Menschen aber möchte ich nicht in die Hände fallen." },
      { number: 15, text: "Da ließ der Herr über Israel eine Pest kommen; sie dauerte von jenem Morgen an bis zu dem festgesetzten Zeitpunkt und es starben zwischen Dan und Beerscheba siebzigtausend Menschen im Volk.", footnotes: ["Die Strafe entspricht dem Vergehen. Wenn das Volk vermindert wird, ist der König, dessen Macht sich mindert, getroffen."] },
      { number: 16, text: "Als der Engel seine Hand gegen Jerusalem ausstreckte, um es ins Verderben zu stürzen, reute den Herrn das Unheil und er sagte zu dem Engel, der das Volk ins Verderben stürzte: Es ist jetzt genug, lass deine Hand sinken! Der Engel war gerade bei der Tenne des Jebusiters Arauna." },
      { number: 17, text: "Als David den Engel sah, der das Volk schlug, sagte er zum Herrn: Ich bin es doch, der gesündigt hat; ich bin es, der sich vergangen hat. Aber diese, die Herde, was haben denn sie getan? Erheb deine Hand gegen mich und gegen das Haus meines Vaters!" },
      { number: 18, text: "Am gleichen Tag kam Gad zu David und sagte zu ihm: Geh hinauf und errichte dem Herrn auf der Tenne des Jebusiters Arauna einen Altar!", footnotes: ["18-25: Die Erzählung erklärt, warum die Tenne des Arauna ein heiliger Ort geworden ist. Hier baute Salomo den Tempel (vgl. 1 Chr 22,1; 2 Chr 3,1)."] },
      { number: 19, text: "David ging hinauf, wie es Gad gesagt und der Herr befohlen hatte." },
      { number: 20, text: "Arauna hielt gerade Ausschau und sah den König mit seinen Dienern kommen. Er ging hinaus, warf sich vor dem König mit dem Gesicht zur Erde nieder" },
      { number: 21, text: "und sagte: Warum kommt mein Herr, der König, zu seinem Knecht? David antwortete: Um von dir die Tenne zu kaufen und dem Herrn einen Altar zu errichten, damit die Plage im Volk aufhört." },
      { number: 22, text: "Arauna antwortete David: Mein Herr, der König, möge alles nehmen, was er für gut findet, und es (als Opfer) darbringen. Sieh her, hier sind die Rinder für das Brandopfer und die Dreschschlitten und das Geschirr der Rinder als Brennholz." },
      { number: 23, text: "Das alles gab [der König] Arauna dem König und sagte zu ihm: Der Herr, dein Gott, sei dir gnädig." },
      { number: 24, text: "Der König erwiderte Arauna: Nein, ich will es dir gegen Bezahlung abkaufen; ich will dem Herrn, meinem Gott, keine unbezahlten Brandopfer darbringen. David kaufte also die Tenne und die Rinder für fünfzig Silberschekel." },
      { number: 25, text: "Und er baute dort einen Altar für den Herrn und brachte Brandopfer und Heilsopfer dar; der Herr aber ließ sich um des Landes willen erweichen und die Plage hörte auf in Israel." },
    ] },
  ],
};
