import { Book } from "@/lib/types";

export const daniel: Book = {
  id: "daniel",
  name: "Daniel",
  shortName: "Dan",
  testament: "old",
  introduction: "Das Buch Daniel berichtet zunächst, wie im Zusammenhang mit der Verschleppung vornehmer Judäer durch Nebukadnezzar auch Daniel und seine drei Freunde nach Babylonien kommen, wo sie trotz ihrer Erziehung im Geist der heidnischen Weltmacht dem Glauben ihrer Väter treu bleiben. Anschließend erzählt das Buch, wie einerseits Daniel als Empfänger und Deuter göttlicher Offenbarungen Aufschluss über den Gang der Weltgeschichte und ihre Vollendung in der Königsherrschaft Gottes gibt, wobei er sein Augenmerk besonders dem Auftreten und Schicksal der gottwidrigen Vertreter der einzelnen heidnischen Weltmächte zuwendet, und wie andererseits Daniel und seine Freunde in der Verfolgung durch diese heidnischen Weltmächte sich als Anhänger des Gottes Israels beispielhaft bewähren (Kap. 1 - 7). Daniel berichtet danach selbst von drei weiteren Offenbarungen, die ihm den Verlauf der Geschichte Gottes mit seinem Volk vom Babylonischen Exil bis zur Verfolgung der Juden in der Zeit des syrischen Königs Antiochus IV. Epiphanes (175-164 v. Chr.) offen legen (Kap. 8 - 12). Im folgenden Abschnitt, der sich mit dem Wirken Daniels in der babylonischen Diaspora beschäftigt, wird sodann erzählt, wie er eine zu Unrecht angeklagte Frau vor ihren verkommenen Richtern rettet und wie er, nachdem er wegen seiner offenkundigen Missachtung des heidnischen Götzenkultes in Todesgefahr geraten ist, selbst die rettende Macht seines Gottes erfährt (Kap. 13f). Die Abfassung des Buches in seiner heutigen Gestalt fällt in die Makkabäerzeit. Jedoch ist eine längere Entstehungszeit des Buches anzunehmen, in deren Verlauf ältere Überlieferungen mit jeweils verschiedener Aussageabsicht aufgenommen und überarbeitet worden sind. Dafür spricht der Umstand, dass Teile des Buches in Hebräisch (1,1 - 2,4a; 8-12), andere in Aramäisch (2,4b - 7,28) und wieder andere in Griechisch (3,26-90; Kap. 13 und 14) abgefasst wurden. Die nur griechisch vorhandenen Abschnitte waren ursprünglich selbstständige Texteinheiten. Der Lobgesang der drei jungen Männer im Feuerofen (3,26-90) wurde bereits in die griechische Übersetzung des Danielbuches eingefügt, während die Erzählungen der Kapitel 13 und 14 erst in der lateinischen Bibel mit dem Buch zu einer Einheit verbunden wurden. Literarisch gehört das Buch Daniel trotz verschiedenartiger Literaturformen in den Einzeldarstellungen als Ganzes zur Apokalyptik. Darunter versteht man eine Geistesströmung der alttestamentlichen Spätzeit, die sich mit der Offenbarung der himmlischen Welt und mit der Erschließung göttlicher Geheimnisse im Hinblick auf das Ende beschäftigt. Mit dieser apokalyptischen Eigenart des Buches hängt es zusammen, dass die Darstellung der Geschichte bewusste Schematisierungen und historische Unrichtigkeiten aufweist, die jedoch im Rahmen der übergeordneten Glaubensaussage eine gleichnishafte Bedeutung erlangen. Die theologische Bedeutung des Buches ist in der Überzeugung zu sehen, dass Gott den gesamten Verlauf der Weltgeschichte, auch wenn dort das Gottesvolk keine beherrschende Rolle spielt, fest in der Hand hat und dass er in dem Augenblick, in dem seine Getreuen von heidnischen Machthabern aufgerieben zu werden drohen, seine endzeitliche Königsherrschaft offenbart. Als Träger dieser Königsherrschaft Gottes erscheint der »Menschensohn auf den Wolken des Himmels« (Kap. 7). Im Endgericht werden sodann die Gerechten durch die Auferstehung von den Toten der Herrlichkeit Gottes teilhaftig, während die Bösen zu ewiger Schmach verurteilt werden (Kap. 12,1-3).",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Im dritten Jahr der Herrschaft des Königs Jojakim von Juda zog Nebukadnezzar, der König von Babel, gegen Jerusalem und belagerte es.", heading: "Daniel und seine Freunde am babylonischen Hof: 1,1-21", footnotes: ["(1-2) 2 Chr 36,5-7"] },
      { number: 2, text: "Und der Herr gab König Jojakim von Juda sowie einen Teil der Geräte aus dem Haus Gottes in Nebukadnezzars Gewalt. Er verschleppte sie in das Land Schinar, in den Tempel seines Gottes, die Geräte aber brachte er in das Schatzhaus seines Gottes.", footnotes: ["Schinar ist Babylonien (vgl. Gen 10,10; 11,2; Sach 5,11)."] },
      { number: 3, text: "Dann befahl der König seinem Oberkämmerer Aschpenas, einige junge Israeliten an den Hof zu bringen, Söhne von königlicher Abkunft oder wenigstens aus vornehmer Familie;", footnotes: ["2 Kön 20,18"] },
      { number: 4, text: "sie sollten frei von jedem Fehler sein, schön an Gestalt, in aller Weisheit unterrichtet und reich an Kenntnissen; sie sollten einsichtig und verständig sein und geeignet, im Palast des Königs Dienst zu tun; Aschpenas sollte sie auch in Schrift und Sprache der Chaldäer unterrichten." },
      { number: 5, text: "Als tägliche Kost wies ihnen der König Speisen und Wein von der königlichen Tafel zu. Sie sollten drei Jahre lang ausgebildet werden und dann in den Dienst des Königs treten." },
      { number: 6, text: "Unter diesen jungen Männern waren aus dem Stamm Juda Daniel, Hananja, Mischaël und Asarja." },
      { number: 7, text: "Der Oberkämmerer gab ihnen andere Namen: Daniel nannte er Beltschazzar, Hananja Schadrach, Mischaël Meschach und Asarja Abed-Nego." },
      { number: 8, text: "Daniel war entschlossen, sich nicht mit den Speisen und dem Wein der königlichen Tafel unrein zu machen, und er bat den Oberkämmerer darum, sich nicht unrein machen zu müssen.", footnotes: ["Lev 11,4-8; Est 4,17; Jer 35,6"] },
      { number: 9, text: "Gott ließ ihn beim Oberkämmerer Wohlwollen und Nachsicht finden.", footnotes: ["Gen 39,21"] },
      { number: 10, text: "Der Oberkämmerer sagte aber zu Daniel: Ich fürchte mich vor meinem Herrn, dem König, der euch die Speisen und Getränke zugewiesen hat; er könnte finden, dass ihr schlechter ausseht als die anderen jungen Leute eures Alters; dann wäre durch eure Schuld mein Kopf beim König verwirkt." },
      { number: 11, text: "Da sagte Daniel zu dem Mann, den der Oberkämmerer als Aufseher für ihn selbst sowie für Hananja, Mischaël und Asarja eingesetzt hatte:" },
      { number: 12, text: "Versuch es doch einmal zehn Tage lang mit deinen Knechten! Lass uns nur pflanzliche Nahrung zu essen und Wasser zu trinken geben!" },
      { number: 13, text: "Dann vergleiche unser Aussehen mit dem der jungen Leute, die von den Speisen des Königs essen. Je nachdem, was du dann siehst, verfahr weiter mit deinen Knechten!" },
      { number: 14, text: "Der Aufseher nahm ihren Vorschlag an und machte mit ihnen eine zehntägige Probe." },
      { number: 15, text: "Am Ende der zehn Tage sahen sie besser und wohlgenährter aus als all die jungen Leute, die von den Speisen des Königs aßen." },
      { number: 16, text: "Da ließ der Aufseher ihre Speisen und auch den Wein, den sie trinken sollten, beiseite und gab ihnen Pflanzenkost." },
      { number: 17, text: "Und Gott verlieh diesen vier jungen Leuten Wissen und Verständnis in jeder Art Schrifttum und Weisheit; Daniel verstand sich auch auf Visionen und Träume aller Art.", footnotes: ["Am 2,11; Gen 41,12"] },
      { number: 18, text: "Als ihre Zeit zu Ende war und man sie vor den König bringen musste, wie er es bestimmt hatte, stellte sie der Oberkämmerer dem Nebukadnezzar vor." },
      { number: 19, text: "Der König unterhielt sich mit ihnen und fand Daniel, Hananja, Mischaël und Asarja allen anderen überlegen. Sie traten also in den Dienst des Königs." },
      { number: 20, text: "Sooft der König in Fragen, die Weisheit und Einsicht erfordern, ihren Rat einholte, fand er sie allen Zeichendeutern und Wahrsagern in seinem ganzen Reich zehnmal überlegen." },
      { number: 21, text: "Daniel blieb im königlichen Dienst bis ins erste Jahr des Königs Kyrus." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Im zweiten Jahr der Herrschaft Nebukadnezzars hatte dieser einen Traum. Sein Geist wurde davon so beunruhigt, dass er nicht mehr schlafen konnte.", heading: "Nebukadnezzars Traum von den Weltreichen: 2,1-49", footnotes: ["Gen 40,8.16; 41"] },
      { number: 2, text: "Da ließ der König die Zeichendeuter und Wahrsager, die Beschwörer und Chaldäer zusammenrufen; sie sollten ihm Aufschluss geben über seinen Traum. Sie kamen und traten vor den König.", footnotes: ["Jes 47,12", "Chaldäer: Gemeint sind die chaldäischen (babylonischen) Zeichenund Sterndeuter."] },
      { number: 3, text: "Der König sagte zu ihnen: Ich habe einen Traum gehabt; mein Geist ist voll Unruhe und ich möchte den Traum verstehen." },
      { number: 4, text: "Die Chaldäer sagten zu ihm [aramäisch]: O König, mögest du ewig leben. Erzähl deinen Knechten den Traum, dann geben wir dir die Deutung.", footnotes: ["Aramäisch: An dieser Stelle wechselt der Urtext vom Hebräischen zum Aramäischen (vgl. die Einleitung)."] },
      { number: 5, text: "Der König antwortete den Chaldäern: Das ist mein unwiderruflicher Entschluss: Wenn ihr mir nicht den Traum und seine Deutung sagen könnt, dann werdet ihr in Stücke gerissen und eure Häuser werden in Schutthaufen verwandelt." },
      { number: 6, text: "Sagt ihr mir aber den Traum und seine Deutung, dann empfangt ihr von mir Geschenke, Gaben und hohe Ehrungen. Gebt mir also den Traum und seine Deutung an!" },
      { number: 7, text: "Sie antworteten zum zweiten Mal: Der König erzähle seinen Knechten den Traum, dann geben wir ihm die Deutung." },
      { number: 8, text: "Da erwiderte der König: Nun bin ich sicher, dass ihr nur Zeit gewinnen wollt; denn ihr seht, dass mein Entschluss unwiderruflich ist." },
      { number: 9, text: "Wenn ihr mir den Traum nicht sagen könnt, gibt es nur ein Urteil über euch, nämlich: Ihr habt euch verabredet, mir einen erlogenen und verkehrten Spruch vorzutragen, in der Hoffnung, dass sich die Lage ändert. Erzählt mir also den Traum; daran werde ich erkennen, dass ihr ihn auch deuten könnt." },
      { number: 10, text: "Die Chaldäer hielten dem König entgegen: Es gibt keinen Menschen auf der Welt, der sagen könnte, was der König verlangt. Auch hat noch nie ein König, mag er noch so groß und mächtig gewesen sein, ein solches Ansinnen an irgendeinen Zeichendeuter, Wahrsager oder Chaldäer gestellt." },
      { number: 11, text: "Was der König verlangt, ist zu schwierig. Es gibt auch sonst niemand, der es dem König sagen könnte, außer den Göttern; doch diese wohnen nicht bei den Sterblichen." },
      { number: 12, text: "Darüber wurde der König so wütend und zornig, dass er befahl, alle Weisen in Babel umzubringen." },
      { number: 13, text: "Als der Befehl erging, die Weisen zu töten, waren auch Daniel und seine Freunde in Gefahr, getötet zu werden." },
      { number: 14, text: "Aber Daniel, klug und rechtskundig, wandte sich an Arjoch, den Obersten der königlichen Leibwache, der schon unterwegs war, um die Weisen Babels zu töten." },
      { number: 15, text: "Daniel fragte den Bevollmächtigten des Königs, warum der König einen so harten Befehl gegeben habe. Da erklärte ihm Arjoch die Sache." },
      { number: 16, text: "Daniel ging darauf zum König und bat ihn, er möge ihm eine Frist bewilligen, damit er ihm die Deutung des Traumes geben könne." },
      { number: 17, text: "Dann eilte Daniel nach Hause, teilte seinen Gefährten Hananja, Mischaël und Asarja alles mit" },
      { number: 18, text: "und sagte, sie sollten wegen dieses Geheimnisses den Gott des Himmels um Erbarmen bitten, damit nicht Daniel und seine Gefährten samt den anderen Weisen Babels umkämen." },
      { number: 19, text: "Darauf wurde ihm das Geheimnis in einer nächtlichen Vision enthüllt und Daniel pries den Gott des Himmels dafür." },
      { number: 20, text: "Er betete: Der Name Gottes sei gepriesen von Ewigkeit zu Ewigkeit. Denn er hat die Weisheit und die Macht.", footnotes: ["Neh 9,5; Ijob 12,13; Spr 2,6"] },
      { number: 21, text: "Er bestimmt den Wechsel der Zeiten und Fristen; er setzt Könige ab und setzt Könige ein. Er gibt den Weisen die Weisheit und den Einsichtigen die Erkenntnis." },
      { number: 22, text: "Er enthüllt tief verborgene Dinge; er weiß, was im Dunkeln ist, und bei ihm wohnt das Licht.", footnotes: ["Ijob 12,22"] },
      { number: 23, text: "Dich, Gott meiner Väter, preise und rühme ich; denn du hast mir Weisheit und Macht verliehen und jetzt hast du mich wissen lassen, was wir von dir erfleht haben: Du hast uns die Sache des Königs wissen lassen." },
      { number: 24, text: "Darauf ging Daniel zu Arjoch, dem der König aufgetragen hatte, die Weisen Babels umzubringen; er trat ein und sagte zu ihm: Bring die Weisen Babels nicht um! Führe mich vor den König! Ich werde dem König die Deutung seines Traumes geben." },
      { number: 25, text: "In aller Eile brachte Arjoch Daniel vor den König und meldete ihm: Ich habe unter den verschleppten Juden einen Mann gefunden, der dem König die Deutung des Traums geben will." },
      { number: 26, text: "Darauf sagte der König zu Daniel, den man auch Beltschazzar nannte: Bist du wirklich imstande, mir das Traumgesicht, das ich hatte, und seine Deutung zu sagen?" },
      { number: 27, text: "Daniel antwortete dem König: Weise und Wahrsager, Zeichendeuter und Astrologen vermögen dem König das Geheimnis, nach dem er fragt, nicht zu enthüllen." },
      { number: 28, text: "Aber es gibt im Himmel einen Gott, der Geheimnisse offenbart; er ließ den König Nebukadnezzar wissen, was am Ende der Tage geschehen wird. Der Traum, den dein Geist auf deinem Lager hatte, war so:", footnotes: ["Gen 41,16"] },
      { number: 29, text: "Auf deinem Lager kamen dir, König, Gedanken darüber, was dereinst geschehen werde; da ließ er, der die Geheimnisse enthüllt, dich wissen, was geschehen wird.", footnotes: ["Offb 4,1"] },
      { number: 30, text: "Dieses Geheimnis wurde mir enthüllt, nicht durch eine Weisheit, die ich vor allen anderen Lebenden voraus hätte, sondern nur, damit du, König, die Deutung erfährst und die Gedanken deines Herzens verstehst." },
      { number: 31, text: "Du, König, hattest eine Vision: Du sahst ein gewaltiges Standbild. Es war groß und von außergewöhnlichem Glanz; es stand vor dir und war furchtbar anzusehen." },
      { number: 32, text: "An diesem Standbild war das Haupt aus reinem Gold; Brust und Arme waren aus Silber, der Körper und die Hüften aus Bronze." },
      { number: 33, text: "Die Beine waren aus Eisen, die Füße aber zum Teil aus Eisen, zum Teil aus Ton." },
      { number: 34, text: "Du sahst, wie ohne Zutun von Menschenhand sich ein Stein von einem Berg löste, gegen die eisernen und tönernen Füße des Standbildes schlug und sie zermalmte." },
      { number: 35, text: "Da wurden Eisen und Ton, Bronze, Silber und Gold mit einem Mal zu Staub. Sie wurden wie Spreu auf dem Dreschplatz im Sommer. Der Wind trug sie fort und keine Spur war mehr von ihnen zu finden. Der Stein aber, der das Standbild getroffen hatte, wurde zu einem großen Berg und erfüllte die ganze Erde." },
      { number: 36, text: "Das war der Traum. Nun wollen wir dem König sagen, was er bedeutet." },
      { number: 37, text: "Du, König, bist der König der Könige; dir hat der Gott des Himmels Herrschaft und Macht, Stärke und Ruhm verliehen.", footnotes: ["Ez 26,7"] },
      { number: 38, text: "Und in der ganzen bewohnten Welt hat er die Menschen, die Tiere auf dem Feld und die Vögel am Himmel in deine Hand gegeben; dich hat er zum Herrscher über sie alle gemacht: Du bist das goldene Haupt.", footnotes: ["Jer 27,6; Jdt 11,7"] },
      { number: 39, text: "Nach dir kommt ein anderes Reich, geringer als deines; dann ein drittes Reich, von Bronze, das die ganze Erde beherrschen wird." },
      { number: 40, text: "Ein viertes endlich wird hart wie Eisen sein; Eisen zerschlägt und zermalmt ja alles; und wie Eisen alles zerschmettert, so wird dieses Reich alle anderen zerschlagen und zerschmettern.", footnotes: ["7,7"] },
      { number: 41, text: "Die Füße und Zehen waren, wie du gesehen hast, teils aus Töpferton, teils aus Eisen; das bedeutet: Das Reich wird geteilt sein; es wird aber etwas von der Härte des Eisens haben, darum hast du das Eisen mit Ton vermischt gesehen." },
      { number: 42, text: "Dass aber die Zehen teils aus Eisen, teils aus Ton waren, bedeutet: Zum Teil wird das Reich hart sein, zum Teil brüchig." },
      { number: 43, text: "Wenn du das Eisen mit Ton vermischt gesehen hast, so heißt das: Sie werden sich zwar durch Heiraten miteinander verbinden; doch das eine wird nicht am anderen haften, wie sich Eisen nicht mit Ton verbindet.", footnotes: ["11,6"] },
      { number: 44, text: "Zur Zeit jener Könige wird aber der Gott des Himmels ein Reich errichten, das in Ewigkeit nicht untergeht; dieses Reich wird er keinem anderen Volk überlassen. Es wird alle jene Reiche zermalmen und endgültig vernichten; es selbst aber wird in alle Ewigkeit bestehen.", footnotes: ["7,14.27; Jes 9,6; 1 Kor 15,24"] },
      { number: 45, text: "Du hast ja gesehen, dass ohne Zutun von Menschenhand ein Stein vom Berg losbrach und Eisen, Bronze und Ton, Silber und Gold zermalmte. Der große Gott hat den König wissen lassen, was dereinst geschehen wird. Der Traum ist sicher und die Deutung zuverlässig." },
      { number: 46, text: "Da warf sich König Nebukadnezzar auf sein Gesicht nieder, huldigte Daniel und befahl, man sollte ihm Opfer und Weihrauch darbringen.", footnotes: ["Apg 14,13.18"] },
      { number: 47, text: "Und der König sagte zu Daniel: Es ist wahr: Euer Gott ist der Gott der Götter und der Herr der Könige und er kann Geheimnisse offenbaren; nur deshalb konntest du dieses Geheimnis enthüllen.", footnotes: ["3,90; Dtn 10,17; Ps 50,1"] },
      { number: 48, text: "Dann verlieh der König dem Daniel einen hohen Rang und gab ihm viele, reiche Geschenke; er machte ihn zum Gebieter über die ganze Provinz Babel und zum obersten Präfekten aller Weisen von Babel." },
      { number: 49, text: "Auf Daniels Bitte betraute der König Schadrach, Meschach und Abed-Nego mit der Verwaltung der Provinz Babel; Daniel selbst aber blieb am königlichen Hof." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "König Nebukadnezzar ließ ein goldenes Standbild machen, sechzig Ellen hoch und sechs Ellen breit, und ließ es in der Ebene von Dura in der Provinz Babel aufstellen.", heading: "Die drei jungen Männer im Feuerofen: 3,1-97 | Die Standhaftigkeit der drei Freunde: 3,1-23" },
      { number: 2, text: "Dann berief König Nebukadnezzar die Satrapen, Präfekten und Statthalter ein, die Räte, Schatzmeister, Richter und Polizeiobersten und alle anderen hohen Beamten der Provinzen; sie sollten zur Einweihung des Standbildes kommen, das König Nebukadnezzar errichtet hatte." },
      { number: 3, text: "Da versammelten sich die Satrapen, Präfekten und Statthalter, die Räte, Schatzmeister, Richter und Polizeiobersten und alle anderen hohen Beamten der Provinzen zur Einweihung des Standbildes, das König Nebukadnezzar errichtet hatte. Sie stellten sich vor dem Standbild auf, das König Nebukadnezzar errichtet hatte." },
      { number: 4, text: "Nun verkündete der Herold mit mächtiger Stimme: Ihr Männer aus allen Völkern, Nationen und Sprachen, hört den Befehl!", footnotes: ["Offb 7,9; 13,7"] },
      { number: 5, text: "Sobald ihr den Klang der Hörner, Pfeifen und Zithern, der Harfen, Lauten und Sackpfeifen und aller anderen Instrumente hört, sollt ihr niederfallen und das goldene Standbild anbeten, das König Nebukadnezzar errichtet hat." },
      { number: 6, text: "Wer aber nicht niederfällt und es anbetet, wird noch zur selben Stunde in den glühenden Feuerofen geworfen.", footnotes: ["Jer 29,21f"] },
      { number: 7, text: "Sobald daher alle Völker den Klang der Hörner, Pfeifen und Zithern, der Harfen, Lauten und Sackpfeifen und der anderen Instrumente hörten, fielen die Männer aus allen Völkern, Nationen und Sprachen sogleich nieder und beteten das goldene Standbild an, das König Nebukadnezzar errichtet hatte." },
      { number: 8, text: "Sogleich traten einige Chaldäer auf und verklagten die Juden." },
      { number: 9, text: "Sie sagten zum König Nebukadnezzar: O König, mögest du ewig leben." },
      { number: 10, text: "Du, König, hast doch selbst den Befehl erlassen: Jeder soll niederfallen und das goldene Standbild anbeten, wenn er den Klang der Hörner, Pfeifen und Zithern, der Harfen, Lauten und Sackpfeifen und aller anderen Instrumente hört.", footnotes: ["Offb 13,15"] },
      { number: 11, text: "Wer aber nicht niederfällt und es anbetet, wird in den glühenden Feuerofen geworfen." },
      { number: 12, text: "Nun sind da einige Juden, denen du die Verwaltung der Provinz Babel anvertraut hast: Schadrach, Meschach und Abed-Nego. Diese Männer missachten dich, König. Sie verehren deine Götter nicht und beten das goldene Standbild, das du errichtet hast, nicht an.", footnotes: ["Götter: Man kann hier und V. 18 auch Einzahl «Gott» lesen."] },
      { number: 13, text: "Da befahl Nebukadnezzar voll Zorn und Wut, Schadrach, Meschach und Abed-Nego herbeizuholen. Man führte die Männer also vor den König." },
      { number: 14, text: "Nebukadnezzar sagte zu ihnen: Ist es wahr, Schadrach, Meschach und Abed-Nego: Ihr verehrt meine Götter nicht und betet das goldene Standbild nicht an, das ich errichtet habe?" },
      { number: 15, text: "Nun, wenn ihr bereit seid, sobald ihr den Klang der Hörner, Pfeifen und Zithern, der Harfen, Lauten und Sackpfeifen und aller anderen Instrumente hört, sofort niederzufallen und das Standbild anzubeten, das ich habe machen lassen, ist es gut; betet ihr es aber nicht an, dann werdet ihr noch zur selben Stunde in den glühenden Feuerofen geworfen. Welcher Gott kann euch dann aus meiner Gewalt erretten?", footnotes: ["Ex 5,2; 2 Kön 18,35; Jes 36,20"] },
      { number: 16, text: "Schadrach, Meschach und Abed-Nego erwiderten dem König Nebukadnezzar: Wir haben es nicht nötig, dir darauf zu antworten:" },
      { number: 17, text: "Wenn überhaupt jemand, so kann nur unser Gott, den wir verehren, uns erretten; auch aus dem glühenden Feuerofen und aus deiner Hand, König, kann er uns retten.", footnotes: ["Dtn 4,20; Ps 66,12; Jes 43,2"] },
      { number: 18, text: "Tut er es aber nicht, so sollst du, König, wissen: Auch dann verehren wir deine Götter nicht und beten das goldene Standbild nicht an, das du errichtet hast.", footnotes: ["Ex 20,3-5"] },
      { number: 19, text: "Da wurde Nebukadnezzar wütend; sein Gesicht verzerrte sich vor Zorn über Schadrach, Meschach und Abed-Nego. Er ließ den Ofen siebenmal stärker heizen, als man ihn gewöhnlich heizte." },
      { number: 20, text: "Dann befahl er, einige der stärksten Männer aus seinem Heer sollten Schadrach, Meschach und Abed-Nego fesseln und in den glühenden Feuerofen werfen." },
      { number: 21, text: "Da wurden die Männer, wie sie waren - in ihren Mänteln, Röcken und Mützen und den übrigen Kleidungsstücken - gefesselt und in den glühenden Feuerofen geworfen." },
      { number: 22, text: "Nach dem strengen Befehl des Königs war aber der Ofen übermäßig geheizt worden und die herausschlagenden Flammen töteten die Männer, die Schadrach, Meschach und Abed-Nego hingebracht hatten." },
      { number: 23, text: "Die drei Männer aber, Schadrach, Meschach und Abed-Nego, fielen gefesselt in den glühenden Feuerofen." },
      { number: 24, text: "Doch sie gingen mitten in den Flammen umher, lobten Gott und priesen den Herrn.", heading: "Das Gebet des Asarja: 3,24-50", footnotes: ["24-90: Das Gebet des Asarja (VV. 24-50) und der Lobgesang der drei jungen Männer (VV. 51-90) sind nur griechisch erhalten; die Übersetzung folgt dem Text des Theodotion."] },
      { number: 25, text: "Asarja blieb stehen, öffnete den Mund und sprach mitten im Feuer folgendes Gebet:" },
      { number: 26, text: "Gepriesen und gelobt bist du, Herr, Gott unserer Väter; herrlich ist dein Name in alle Ewigkeit.", footnotes: ["(26-45) 9,4-19; Esra 9,6-15"] },
      { number: 27, text: "Denn du bist gerecht in allem, was du getan hast. All deine Taten sind richtig, deine Wege gerade. Alle deine Urteile sind wahr.", footnotes: ["Neh 9,33"] },
      { number: 28, text: "Du hast gerechte Strafen verhängt, in allem, was du über uns gebracht hast und über Jerusalem, die heilige Stadt unserer Väter. Ja, nach Wahrheit und Recht hast du all dies wegen unserer Sünden herbeigeführt." },
      { number: 29, text: "Denn wir haben gesündigt und durch Treubruch gefrevelt und haben in allem gefehlt.", footnotes: ["Bar 1,17f"] },
      { number: 30, text: "Wir haben deinen Geboten nicht gehorcht, haben weder beachtet noch getan, was du uns zu unserem Wohl befohlen hast.", footnotes: ["Neh 1,7"] },
      { number: 31, text: "Alles, was du uns geschickt hast, alles, was du uns getan hast, das hast du nach deiner gerechten Entscheidung getan.", footnotes: ["Dtn 28,15.48; Lev 26,14.39"] },
      { number: 32, text: "Du hast uns der Gewalt gesetzloser Feinde und gehässiger Verräter preisgegeben und einem ungerechten König, dem schlimmsten König der ganzen Welt." },
      { number: 33, text: "Und jetzt dürfen wir nicht einmal den Mund auftun. Schande und Schmach kam über deine Diener und Verehrer." },
      { number: 34, text: "Um deines Namens willen verwirf uns nicht für immer; löse deinen Bund nicht auf!", footnotes: ["Ex 32,11-13"] },
      { number: 35, text: "Versag uns nicht dein Erbarmen, deinem Freund Abraham zuliebe, deinem Knecht Isaak und Israel, deinem Heiligen,", footnotes: ["Jes 41,8"] },
      { number: 36, text: "denen du Nachkommen verheißen hast so zahlreich wie die Sterne am Himmel und wie der Sand am Ufer des Meeres.", footnotes: ["Gen 15,5; 22,17"] },
      { number: 37, text: "Ach, Herr, wir sind geringer geworden als alle Völker. In aller Welt sind wir heute wegen unserer Sünden erniedrigt.", footnotes: ["Dtn 28,62"] },
      { number: 38, text: "Wir haben in dieser Zeit weder Vorsteher noch Propheten und keinen, der uns anführt, weder Brandopfer noch Schlachtopfer, weder Speiseopfer noch Räucherwerk, noch einen Ort, um dir die Erstlingsgaben darzubringen und um Erbarmen zu finden bei dir.", footnotes: ["Hos 3,4; Klgl 2,9; Ps 51,18f"] },
      { number: 39, text: "Du aber nimm uns an! Wir kommen mit zerknirschtem Herzen und demütigem Sinn.", footnotes: ["Weish 3,6"] },
      { number: 40, text: "Wie Brandopfer von Widdern und Stieren, wie Tausende fetter Lämmer, so gelte heute unser Opfer vor dir und verschaffe uns bei dir Sühne. Denn wer dir vertraut, wird nicht beschämt.", footnotes: ["2 Makk 7,37"] },
      { number: 41, text: "Wir folgen dir jetzt von ganzem Herzen, fürchten dich und suchen dein Angesicht." },
      { number: 42, text: "Überlass uns nicht der Schande, sondern handle an uns nach deiner Milde, nach deinem überreichen Erbarmen!" },
      { number: 43, text: "Errette uns, deinen wunderbaren Taten entsprechend; verschaff deinem Namen Ruhm, Herr!" },
      { number: 44, text: "Doch alle, die deinen Dienern Böses tun, sollen beschämt werden. Sie sollen zuschanden werden und ihre Herrschaft verlieren. Ihre Stärke soll zerschlagen werden." },
      { number: 45, text: "Sie sollen erkennen, dass du allein der Herr und Gott bist, ruhmreich auf der ganzen Erde.", footnotes: ["Ps 83,19"] },
      { number: 46, text: "Die Knechte des Königs, die die drei Männer in den Ofen geworfen hatten, hörten inzwischen nicht auf, den Ofen mit Harz und Werg, Pech und Reisig zu heizen." },
      { number: 47, text: "So schlugen die Flammen bis zu neunundvierzig Ellen hoch aus dem Ofen heraus." },
      { number: 48, text: "Sie griffen um sich und verbrannten jeden Chaldäer, den sie im Umkreis des Ofens erfassen konnten." },
      { number: 49, text: "Aber der Engel des Herrn war zusammen mit Asarja und seinen Gefährten in den Ofen hinabgestiegen. Er trieb die Flammen des Feuers aus dem Ofen hinaus" },
      { number: 50, text: "und machte das Innere des Ofens so, als wehte ein taufrischer Wind. Das Feuer berührte sie gar nicht; es tat ihnen nichts zuleide und belästigte sie nicht." },
      { number: 51, text: "Da sangen die drei im Ofen wie aus einem Mund, sie rühmten und priesen Gott mit den Worten:", heading: "Der Lobgesang der drei jungen Männer: 3,51-90" },
      { number: 52, text: "Gepriesen bist du, Herr, du Gott unserer Väter, / gelobt und gerühmt in Ewigkeit. Gepriesen ist dein heiliger, herrlicher Name, / hoch gelobt und verherrlicht in Ewigkeit.", footnotes: ["3,26"] },
      { number: 53, text: "Gepriesen bist du im Tempel deiner heiligen Herrlichkeit, / hoch gerühmt und verherrlicht in Ewigkeit.", footnotes: ["Ps 150,1"] },
      { number: 54, text: "Gepriesen bist du, der in die Tiefen schaut und auf Kerubim thront, / gelobt und gerühmt in Ewigkeit.", footnotes: ["Ex 25,18-22"] },
      { number: 55, text: "Gepriesen bist du auf dem Thron deiner Herrschaft, / hoch gerühmt und gefeiert in Ewigkeit." },
      { number: 56, text: "Gepriesen bist du am Gewölbe des Himmels, / gerühmt und verherrlicht in Ewigkeit." },
      { number: 57, text: "Preist den Herrn, all ihr Werke des Herrn; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["Ps 103,22"] },
      { number: 58, text: "Preist den Herrn, ihr Himmel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 59, text: "Preist den Herrn, ihr Engel des Herrn; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["(59-90) Ps 148,2-12"] },
      { number: 60, text: "Preist den Herrn, all ihr Wasser über dem Himmel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 61, text: "Preist den Herrn, all ihr Mächte des Herrn; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 62, text: "Preist den Herrn, Sonne und Mond; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 63, text: "Preist den Herrn, ihr Sterne am Himmel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 64, text: "Preist den Herrn, aller Regen und Tau; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 65, text: "Preist den Herrn, all ihr Winde; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 66, text: "Preist den Herrn, Feuer und Glut; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 67, text: "Preist den Herrn, Frost und Hitze; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 68, text: "Preist den Herrn, Tau und Schnee; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 69, text: "Preist den Herrn, Eis und Kälte; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 70, text: "Preist den Herrn, Raureif und Schnee; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 71, text: "Preist den Herrn, ihr Nächte und Tage; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["Vg, deren Verszählung hier zugrunde gelegt wird, hat in VV. 71-78 eine andere Versfolge als Theodotion (vgl. die Anmerkung zu 3,24-90)."] },
      { number: 72, text: "Preist den Herrn, Licht und Dunkel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 73, text: "Preist den Herrn, ihr Blitze und Wolken; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 74, text: "Die Erde preise den Herrn; / sie lobe und rühme ihn in Ewigkeit." },
      { number: 75, text: "Preist den Herrn, ihr Berge und Hügel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 76, text: "Preist den Herrn, all ihr Gewächse auf Erden; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 77, text: "Preist den Herrn, ihr Quellen; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 78, text: "Preist den Herrn, ihr Meere und Flüsse; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 79, text: "Preist den Herrn, ihr Tiere des Meeres / und alles, was sich regt im Wasser; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 80, text: "Preist den Herrn, all ihr Vögel am Himmel; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 81, text: "Preist den Herrn, all ihr Tiere, wilde und zahme; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 82, text: "Preist den Herrn, ihr Menschen; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 83, text: "Preist den Herrn, ihr Israeliten; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["Ps 135,19"] },
      { number: 84, text: "Preist den Herrn, ihr seine Priester; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 85, text: "Preist den Herrn, ihr seine Knechte; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["Ps 134,1"] },
      { number: 86, text: "Preist den Herrn, ihr Geister und Seelen der Gerechten; / lobt und rühmt ihn in Ewigkeit!" },
      { number: 87, text: "Preist den Herrn, ihr Demütigen und Frommen; / lobt und rühmt ihn in Ewigkeit!", footnotes: ["Zef 2,3"] },
      { number: 88, text: "Preist den Herrn, Hananja, Asarja und Mischaël; / lobt und rühmt ihn in Ewigkeit! Denn er hat uns der Unterwelt entrissen / und aus der Gewalt des Todes errettet. Er hat uns aus dem lodernden Ofen befreit, / uns mitten aus dem Feuer erlöst." },
      { number: 89, text: "Dankt dem Herrn, denn er ist gütig; / denn seine Huld währt ewig.", footnotes: ["Ps 106,1; 136,1"] },
      { number: 90, text: "Preist alle den Herrn, ihr seine Verehrer, / preist den Gott der Götter; singt ihm Lob und Dank; / denn ewig währt seine Güte.", footnotes: ["Ps 50,1"] },
      { number: 91, text: "Da erschrak der König Nebukadnezzar; er sprang auf und fragte seine Räte: Haben wir nicht drei Männer gefesselt ins Feuer geworfen? Sie gaben dem König zur Antwort: Gewiss, König!", heading: "Die Rettung der drei Freunde: 3,91-97", footnotes: ["(24) Mit V. 91 setzt wieder der in V. 23 unterbrochene aramäische Text ein, dessen Verszählung in Klammern steht."] },
      { number: 92, text: "Er erwiderte: Ich sehe aber vier Männer frei im Feuer umhergehen. Sie sind unversehrt und der vierte sieht aus wie ein Göttersohn.", footnotes: ["Jes 43,2", "(25) Der «Göttersohn» ist der in V. 49 genannte Engel des Herrn."] },
      { number: 93, text: "Dann ging Nebukadnezzar zu der Tür des glühenden Ofens und rief: Schadrach, Meschach und Abed-Nego, ihr Diener des höchsten Gottes, steigt heraus, kommt her! Da kamen Schadrach, Meschach und Abed-Nego aus dem Feuer heraus." },
      { number: 94, text: "Nun drängten auch die Satrapen, Präfekten, Statthalter und die königlichen Räte herbei. Sie sahen sich die Männer an und fanden, dass das Feuer keine Macht über ihren Körper gehabt hatte. Kein Haar auf ihrem Kopf war versengt. Ihre Mäntel waren unversehrt und nicht einmal Brandgeruch haftete ihnen an.", footnotes: ["Hebr 11,34"] },
      { number: 95, text: "Da rief Nebukadnezzar aus: Gepriesen sei der Gott Schadrachs, Meschachs und Abed-Negos. Denn er hat seinen Engel gesandt und seine Diener gerettet. Im Vertrauen auf ihn haben sie lieber den Befehl des Königs missachtet und ihr Leben dahingegeben, als dass sie irgendeinen anderen als ihren eigenen Gott verehrten und anbeteten.", footnotes: ["2,47; 6,27"] },
      { number: 96, text: "Darum ordne ich an: Jeder, der vom Gott des Schadrach, Meschach und Abed-Nego verächtlich spricht, zu welcher Völkerschaft, Nation oder Sprache er auch gehört, soll in Stücke gerissen und sein Haus soll in einen Trümmerhaufen verwandelt werden. Denn es gibt keinen anderen Gott, der auf diese Weise retten kann." },
      { number: 97, text: "Darauf sorgte der König dafür, dass es Schadrach, Meschach und Abed-Nego in der Provinz Babel gut ging." },
      { number: 98, text: "Der König Nebukadnezzar an alle Völker, Nationen und Sprachen auf der ganzen Erde: Friede sei mit euch in Fülle.", heading: "Nebukadnezzars Traum vom stolzen Baum: 3,98 - 4,34" },
      { number: 99, text: "Es ist mir eine Freude, die Zeichen und Wunder zu verkünden, die der höchste Gott an mir getan hat." },
      { number: 100, text: "Wie groß sind seine Zeichen, wie gewaltig seine Wunder! Sein Reich ist ein ewiges Reich; seine Herrschaft überdauert alle Generationen.", footnotes: ["2,44; 4,31"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Ich, Nebukadnezzar, lebte ohne Sorge in meinem Haus und war glücklich in meinem Palast.", footnotes: ["(1-5) 2,1-3.10"] },
      { number: 2, text: "Da hatte ich einen Traum, der mich erschreckte. Was ich auf meinem Lager sah, was meine Augen da erblickten, versetzte mich in Angst." },
      { number: 3, text: "Darum ließ ich alle Weisen Babels zu mir rufen, damit sie mir den Traum deuteten." },
      { number: 4, text: "Da kamen die Zeichendeuter, Wahrsager, Chaldäer und Astrologen herbei. Ich erzählte ihnen den Traum; aber sie konnten ihn mir nicht deuten." },
      { number: 5, text: "Zuletzt erschien Daniel vor mir, der nach dem Namen meines Gottes auch Beltschazzar heißt und in dem der Geist der heiligen Götter ist. Ihm erzählte ich nun den Traum und sagte:", footnotes: ["2,28.47; 5,11"] },
      { number: 6, text: "Beltschazzar, Oberster der Zeichendeuter, von dir weiß ich, dass der Geist der heiligen Götter in dir ist und dass dir kein Geheimnis verschlossen bleibt. Hör also, was ich im Traum gesehen habe, und deute es mir!" },
      { number: 7, text: "Was ich auf meinem Lager vor Augen hatte, war dies: Da stand ein Baum mitten auf der Erde; er war sehr hoch.", footnotes: ["Ez 31,3-14"] },
      { number: 8, text: "Der Baum wuchs zusehends und wurde immer mächtiger; seine Höhe reichte bis an den Himmel; er war bis ans Ende der ganzen Erde zu sehen." },
      { number: 9, text: "Er hatte prächtiges Laub und trug so viele Früchte, dass er Nahrung für alle bot. Unter ihm fanden die wilden Tiere des Feldes Schatten; die Vögel nisteten in seinen Zweigen; alle Lebewesen ernährten sich von ihm." },
      { number: 10, text: "Während ich auf meinem Lager noch das Traumbild sah, stieg ein Wächter, ein Heiliger, vom Himmel herab.", footnotes: ["7,9f; 10,13.21"] },
      { number: 11, text: "Er befahl mit mächtiger Stimme: Fällt den Baum und schlagt seine Äste ab! Streift sein Laubwerk ab und zerstreut seine Früchte! Die Tiere sollen aus seinem Schatten fliehen und die Vögel aus seinen Zweigen." },
      { number: 12, text: "Aber lasst ihm den Wurzelstock in der Erde, im Gras des Feldes, mit einer Fessel aus Eisen und Bronze. Der Tau des Himmels soll ihn benetzen und mit den Tieren soll er teilhaben am Gras der Erde." },
      { number: 13, text: "Sein Herz sei nicht mehr ein Menschenherz; ein Tierherz soll ihm gegeben werden und sieben Zeiten sollen über ihn hingehen.", footnotes: ["Nach der Vorstellung des Alten Orients hat im Herzen der Verstand seinen Sitz (vgl. VV. 31.33)."] },
      { number: 14, text: "Dieser Befehl beruht auf einem Beschluss der Wächter; ein Spruch der Heiligen fordert es. Die Lebenden sollen erkennen: Über die Herrschaft bei den Menschen gebietet der Höchste; er verleiht sie, wem er will, selbst den Niedrigsten der Menschen kann er dazu erheben.", footnotes: ["2,21; Ijob 36,7-11"] },
      { number: 15, text: "Das ist der Traum, den ich, König Nebukadnezzar, gehabt habe. Nun deute ihn, Beltschazzar! Von allen Weisen meines Reiches konnte ihn mir keiner auslegen; du aber kannst es, denn in dir ist der Geist der heiligen Götter." },
      { number: 16, text: "Da war Daniel, der auch Beltschazzar heißt, eine Zeit lang ganz verstört; denn seine Gedanken machten ihm Angst. Doch der König sagte: Beltschazzar, lass dich von dem Traum und seiner Deutung nicht ängstigen! Beltschazzar erwiderte: Mein Herr, der Traum gelte deinen Feinden; seine Deutung treffe deine Widersacher." },
      { number: 17, text: "Du hast einen Baum gesehen; er wuchs zusehends und wurde immer mächtiger. Seine Höhe reichte bis an den Himmel und er war auf der ganzen Erde zu sehen." },
      { number: 18, text: "Er hatte prächtiges Laub und trug so viele Früchte, dass er Nahrung für alle bot. Unter ihm fanden die wilden Tiere des Feldes Zuflucht, und in seinen Zweigen wohnten die Vögel des Himmels." },
      { number: 19, text: "Dieser Baum bist du, König; du bist groß und mächtig geworden; deine Größe ist immer mehr gewachsen; sie reicht bis zum Himmel und deine Herrschaft bis ans Ende der Erde." },
      { number: 20, text: "Dann hat der König gesehen, wie ein Wächter, ein Heiliger, vom Himmel herabstieg und befahl: Fällt den Baum und vernichtet ihn! Aber lasst ihm in der Erde, im Gras des Feldes, den Wurzelstock, mit einer Fessel aus Eisen und Bronze. Der Tau des Himmels soll ihn benetzen und mit den Tieren des Feldes soll er teilhaben (am Gras der Erde), bis sieben Zeiten über ihn hingegangen sind." },
      { number: 21, text: "Das, o König, bedeutet - es ist ein Beschluss des Höchsten, der meinen Herrn, den König, betrifft -:" },
      { number: 22, text: "Man wird dich aus der Gemeinschaft der Menschen verstoßen und du musst bei den wilden Tieren des Feldes leben. Du wirst dich von Gras ernähren wie die Ochsen und der Tau des Himmels wird dich benetzen. So gehen sieben Zeiten über dich hin, bis du erkennst, dass der Höchste über die Herrschaft bei den Menschen gebietet und sie verleiht, wem er will.", footnotes: ["3,100"] },
      { number: 23, text: "Schließlich hieß es, man solle den Wurzelstock des Baumes stehen lassen; das bedeutet: Deine Herrschaft bleibt dir erhalten, sobald du anerkennst, dass der Himmel die Macht hat." },
      { number: 24, text: "Darum, o König, nimm meinen Rat an: Lösch deine Sünden aus durch rechtes Tun, tilge deine Vergehen, indem du Erbarmen hast mit den Armen. Dann mag dein Glück vielleicht von Dauer sein.", footnotes: ["Spr 19,17; Sir 3,30f"] },
      { number: 25, text: "All das kam dann über König Nebukadnezzar." },
      { number: 26, text: "Als er nämlich zwölf Monate später auf (der Dachterrasse) des königlichen Palastes zu Babel spazieren ging," },
      { number: 27, text: "sagte der König: Ist das nicht das großartige Babel, das ich durch meine gewaltige Macht als Königsstadt erbaut habe, zum Ruhm meiner Herrlichkeit?", footnotes: ["Spr 16,18"] },
      { number: 28, text: "Noch hatte der König diese Worte auf den Lippen, da fiel eine Stimme vom Himmel: Dir, König Nebukadnezzar, sei gesagt: Die Herrschaft wird dir genommen." },
      { number: 29, text: "Man wird dich aus der Gemeinschaft der Menschen ausstoßen. Du musst bei den wilden Tieren leben und dich von Gras ernähren wie die Ochsen. So werden sieben Zeiten über dich hingehen, bis du erkennst, dass der Höchste über die Herrschaft bei den Menschen gebietet und sie verleiht, wem er will.", footnotes: ["5,21"] },
      { number: 30, text: "Noch in derselben Stunde erfüllte sich dieser Spruch an Nebukadnezzar: Man verstieß ihn aus der Gemeinschaft der Menschen und er musste sich von Gras ernähren wie die Ochsen. Der Tau des Himmels benetzte seinen Körper, bis seine Haare so lang wie Adlerfedern waren und seine Nägel wie Vogelkrallen." },
      { number: 31, text: "Als die Zeit verstrichen war, erhob ich, Nebukadnezzar, meine Augen zum Himmel und mein Verstand kehrte zurück. Da pries ich den Höchsten; ich lobte und verherrlichte den, der ewig lebt. Ja, seine Herrschaft ist eine ewige Herrschaft; sein Reich überdauert alle Generationen.", footnotes: ["3,100"] },
      { number: 32, text: "Alle Bewohner der Erde gelten vor ihm wie nichts. Er macht mit dem Heer des Himmels und mit den Bewohnern der Erde, was er will. Es gibt niemand, der seiner Hand wehren und zu ihm sagen dürfte: Was tust du da?", footnotes: ["Ijob 9,12; Jes 40,17"] },
      { number: 33, text: "Zu derselben Zeit kehrte mein Verstand zurück und ich erhielt zum Ruhm meines Königtums auch meine Herrlichkeit und meinen königlichen Glanz zurück. Meine Räte und Großen suchten mich auf; man setzte mich wieder in meine Herrschaft ein und meine Macht wurde noch größer.", footnotes: ["Lk 1,52; 18,14"] },
      { number: 34, text: "Ich, Nebukadnezzar, lobe, preise und rühme nun den König des Himmels. Denn alle seine Taten sind vortrefflich und seine Wege gerecht. Die Menschen, die in stolzer Höhe dahinschreiten, kann er erniedrigen." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "König Belschazzar gab ein großes Gastmahl für seine Großen; es waren tausend Menschen und zusammen mit den Tausend sprach er dem Wein zu.", heading: "Das Gastmahl Belschazzars: 5,1 - 6,1" },
      { number: 2, text: "In seiner Weinlaune nun ließ Belschazzar die goldenen und silbernen Gefäße holen, die sein Vater Nebukadnezzar aus dem Tempel in Jerusalem mitgenommen hatte. Jetzt sollten der König und seine Großen, seine Frauen und Nebenfrauen daraus trinken.", footnotes: ["1,2"] },
      { number: 3, text: "Man holte also die goldenen Gefäße, die man aus dem Tempel des Gotteshauses in Jerusalem mitgenommen hatte, und der König und seine Großen, seine Frauen und Nebenfrauen tranken daraus." },
      { number: 4, text: "Sie tranken Wein und lobten die Götter aus Gold und Silber, aus Bronze, Eisen, Holz und Stein.", footnotes: ["2,35; Bar 6,3; Offb 9,20"] },
      { number: 5, text: "In derselben Stunde erschienen die Finger einer Menschenhand und schrieben gegenüber dem Leuchter etwas auf die weißgetünchte Wand des königlichen Palastes. Der König sah den Rücken der Hand, als sie schrieb." },
      { number: 6, text: "Da erbleichte er und seine Gedanken erschreckten ihn. Seine Glieder wurden schwach und ihm schlotterten die Knie." },
      { number: 7, text: "Der König schrie laut, man solle die Wahrsager, Chaldäer und Astrologen holen. Dann sagte er zu den Weisen von Babel: Wer diese Schrift lesen und mir deuten kann - was er auch sei: er soll in Purpur gekleidet werden, eine goldene Kette um den Hals tragen und als der Dritte in meinem Reich herrschen.", footnotes: ["2,2.6.48; Gen 41,42f; Est 8,15", "der Dritte: hier Rangbezeichnung für eines der höchsten Ämter im Staat (vgl. auch 6,3)."] },
      { number: 8, text: "Da kamen alle Weisen des Königs herbei; aber sie waren nicht imstande, die Schrift zu lesen oder dem König zu sagen, was sie bedeutete." },
      { number: 9, text: "Darüber erschrak König Belschazzar noch mehr und sein Gesicht wurde bleich. Auch seine Großen gerieten in Angst." },
      { number: 10, text: "Da die Rufe des Königs und seiner Großen bis zur Königin drangen, kam sie in den Festsaal und sagte: O König, mögest du ewig leben. Lass dich von deinen Gedanken nicht erschrecken; du brauchst nicht zu erbleichen." },
      { number: 11, text: "In deinem Reich gibt es einen Mann, in dem der Geist der heiligen Götter wohnt. Schon zu deines Vaters Zeiten fand man bei ihm Erleuchtung und Einsicht und Weisheit, wie nur die Götter sie haben; deshalb hat König Nebukadnezzar, dein Vater, ihn zum Obersten der Zeichendeuter, Wahrsager, Chaldäer und Astrologen ernannt, dein eigener Vater, o König!" },
      { number: 12, text: "Bei diesem Daniel also, dem der König den Namen Beltschazzar gegeben hat, fand man außergewöhnlichen Geist sowie Erkenntnis und Einsicht und die Gabe, Träume auszulegen, Rätsel zu erklären und schwierige Fragen zu lösen. Darum lass jetzt Daniel herrufen; er wird die Deutung geben.", footnotes: ["1,17"] },
      { number: 13, text: "Daniel wurde vor den König gebracht und der König sagte zu ihm: Du also bist Daniel, einer von den verschleppten Juden, die mein Vater, der König, aus Juda hierher gebracht hat." },
      { number: 14, text: "In dir, so habe ich gehört, ist der Geist der Götter und bei dir fand man Erleuchtung und Einsicht und außergewöhnliche Weisheit." },
      { number: 15, text: "Man hat die Weisen und die Wahrsager vor mich gebracht, damit sie diese Schrift lesen und mir deuten. Sie konnten mir aber nicht sagen, was das Geschriebene bedeutet." },
      { number: 16, text: "Doch du, so habe ich gehört, kannst Deutungen geben und schwierige Fragen lösen. Wenn du nun die Schrift lesen und mir deuten kannst, sollst du in Purpur gekleidet werden, um den Hals eine goldene Kette tragen und als der Dritte in meinem Reich herrschen." },
      { number: 17, text: "Daniel gab dem König zur Antwort: Behalte deine Gaben oder schenk sie einem andern! Aber die Schrift will ich für den König lesen und deuten." },
      { number: 18, text: "Mein König! Der höchste Gott hat deinem Vater Nebukadnezzar Herrschaft und Macht, Herrlichkeit und Majestät gegeben.", footnotes: ["2,37"] },
      { number: 19, text: "Vor der Macht, die ihm verliehen war, zitterten und bebten alle Völker, Nationen und Sprachen. Er tötete, wen er wollte, und ließ am Leben, wen er wollte. Er erhöhte, wen er wollte, und stürzte, wen er wollte." },
      { number: 20, text: "Als aber sein Herz überheblich und sein Geist hochmütig wurde, stürzte man ihn von seinem königlichen Thron und er verlor die Herrscherwürde." },
      { number: 21, text: "Man verstieß ihn aus der Gemeinschaft der Menschen. Sein Herz wurde dem der Tiere gleichgemacht. Er musste bei den wilden Eseln hausen und sich von Gras ernähren wie die Ochsen. Der Tau des Himmels benetzte seinen Körper, bis er erkannte: Der höchste Gott gebietet über die Herrschaft bei den Menschen und gibt sie, wem er will.", footnotes: ["4,13.29"] },
      { number: 22, text: "Obgleich nun du, sein Sohn Belschazzar, das alles weißt, bist du in deinem Herzen doch nicht bescheiden geblieben." },
      { number: 23, text: "Du hast dich gegen den Herrn des Himmels erhoben und dir die Gefäße aus seinem Tempel herbeischaffen lassen. Du und deine Großen, deine Frauen und Nebenfrauen, ihr habt daraus Wein getrunken. Du hast die Götter aus Gold und Silber, aus Bronze, Eisen, Holz und Stein gepriesen, die weder sehen noch hören können und keinen Verstand haben. Aber den Gott, der deinen Lebensatem in seiner Hand hat und dem all deine Wege gehören, den hast du nicht verherrlicht.", footnotes: ["Ijob 12,10; 34,21"] },
      { number: 24, text: "Darum hat er diese Hand geschickt und diese Schrift geschrieben." },
      { number: 25, text: "Das Geschriebene lautet aber: Mene mene tekel u-parsin." },
      { number: 26, text: "Diese Worte bedeuten: Mene: Gezählt hat Gott die Tage deiner Herrschaft und macht ihr ein Ende.", footnotes: ["26-28: In Daniels Deutung ist mene = gezählt, tekel = gewogen, peres = geteilt; peres, Mehrzahl parsin, erinnert zugleich an «Perser»."] },
      { number: 27, text: "Tekel: Gewogen wurdest du auf der Waage und zu leicht befunden." },
      { number: 28, text: "Peres: Geteilt wird dein Reich und den Medern und Persern gegeben." },
      { number: 29, text: "Da befahl Belschazzar, Daniel in Purpur zu kleiden und ihm eine goldene Kette um den Hals zu legen, und er ließ verkünden, dass Daniel als der Dritte im Reich herrschen sollte.", footnotes: ["5,7"] },
      { number: 30, text: "Aber noch in derselben Nacht wurde Belschazzar, der König der Chaldäer, getötet" },
    ] },
    { number: 6, verses: [
      { number: 1, text: "und der Meder Darius übernahm die Königsherrschaft im Alter von zweiundsechzig Jahren." },
      { number: 2, text: "Darius fand es für gut, über das Reich hundertzwanzig Satrapen einzusetzen, die über das ganze Reich verteilt sein sollten.", heading: "Daniel in der Löwengrube: 6,2-29" },
      { number: 3, text: "Über diese wieder setzte er drei oberste Beamte, zu denen auch Daniel gehörte. Ihnen sollten die Satrapen Rechenschaft ablegen, damit der König keinen Schaden erleide." },
      { number: 4, text: "Daniel nun zeichnete sich vor den anderen obersten Beamten und den Satrapen aus; denn in ihm war ein außergewöhnlicher Geist. Der König erwog sogar, ihn zum höchsten Beamten des ganzen Reiches zu machen.", footnotes: ["1,19f"] },
      { number: 5, text: "Da suchten die obersten Beamten und die Satrapen einen Grund, um Daniel wegen seiner Amtsführung anzuklagen. Sie konnten aber keinen Grund zur Anklage und kein Vergehen finden; denn er war zuverlässig; keine Nachlässigkeit und kein Vergehen konnte man ihm nachweisen." },
      { number: 6, text: "Da sagten jene Männer: Wir werden keinen Grund finden, um diesen Daniel anzuklagen, es sei denn, wir finden gegen ihn etwas wegen des Gesetzes seines Gottes." },
      { number: 7, text: "Darum bestürmten die obersten Beamten und Satrapen den König und sagten zu ihm: König Darius, mögest du ewig leben." },
      { number: 8, text: "Alle obersten Beamten des Reiches, die Präfekten, Satrapen, Räte und Statthalter raten dem König, ein Dekret zu erlassen und folgendes Verbot in Kraft zu setzen: Jeder, der innerhalb von dreißig Tagen an irgendeinen Gott oder Menschen außer an dich, König, eine Bitte richtet, der soll in die Löwengrube geworfen werden." },
      { number: 9, text: "Erlass dieses Verbot, o König, und fertige ein Schreiben darüber aus! Es soll nach dem unwandelbaren Gesetz der Meder und Perser unabänderlich sein.", footnotes: ["Est 1,19"] },
      { number: 10, text: "König Darius unterzeichnete das Verbot." },
      { number: 11, text: "Als Daniel erfuhr, dass das Schreiben unterzeichnet war, ging er in sein Haus. In seinem Obergemach waren die Fenster nach Jerusalem hin offen. Dort kniete er dreimal am Tag nieder und richtete sein Gebet und seinen Lobpreis an seinen Gott, ganz so, wie er es gewohnt war.", footnotes: ["1 Kön 8,48f"] },
      { number: 12, text: "Nun schlichen sich jene Männer heran und fanden Daniel, wie er zu seinem Gott betete und flehte." },
      { number: 13, text: "Darauf gingen sie zum König und erinnerten ihn an sein Verbot; sie sagten: O König, hast du nicht ein Verbot unterzeichnet, nach dem jeder, der innerhalb von dreißig Tagen an irgendeinen Gott oder Menschen außer an dich, König, eine Bitte richtet, in die Löwengrube geworfen werden soll? Der König gab zur Antwort: Die Anordnung steht fest nach dem unwandelbaren Gesetz der Meder und Perser.", footnotes: ["3,10-12"] },
      { number: 14, text: "Da berichteten sie dem König: Daniel, einer von den verschleppten Juden, achtet weder dich, König, noch das Verbot, das du unterschrieben hast, sondern verrichtet dreimal am Tag sein Gebet." },
      { number: 15, text: "Als der König das hörte, war es ihm sehr peinlich und er dachte nach, wie er Daniel retten könne. Bis Sonnenuntergang bemühte er sich, ihn freizubekommen." },
      { number: 16, text: "Doch jene Männer bestürmten ihn und sagten: Bedenke, König, es ist bei den Medern und Persern Gesetz, dass jedes Verbot und Dekret, das der König erlässt, unabänderlich ist." },
      { number: 17, text: "Darauf befahl der König, Daniel herzubringen, und man warf ihn zu den Löwen in die Grube. Der König sagte noch zu Daniel: Möge dein Gott, dem du so unablässig dienst, dich erretten.", footnotes: ["3,15-18"] },
      { number: 18, text: "Und man nahm einen großen Stein und wälzte ihn auf die Öffnung der Grube. Der König versiegelte ihn mit seinem Siegel und den Siegeln seiner Großen, um zu verhindern, dass an der Lage Daniels etwas verändert würde." },
      { number: 19, text: "Dann ging der König in seinen Palast; fastend verbrachte er die Nacht; er ließ sich keine Speisen bringen und konnte keinen Schlaf finden." },
      { number: 20, text: "Früh am Morgen, als es gerade hell wurde, stand der König auf und ging in Eile zur Löwengrube." },
      { number: 21, text: "Als er sich der Grube näherte, rief er mit schmerzlicher Stimme nach Daniel und fragte: Daniel, du Diener des lebendigen Gottes! Hat dein Gott, dem du so unablässig dienst, dich vor den Löwen erretten können?", footnotes: ["Ps 22,22"] },
      { number: 22, text: "Daniel antwortete ihm: O König, mögest du ewig leben." },
      { number: 23, text: "Mein Gott hat seinen Engel gesandt und den Rachen der Löwen verschlossen. Sie taten mir nichts zuleide; denn in seinen Augen war ich schuldlos und auch dir gegenüber, König, bin ich ohne Schuld.", footnotes: ["3,49.95; Hebr 11,33"] },
      { number: 24, text: "Darüber war der König hoch erfreut und befahl, Daniel aus der Grube herauszuholen. So wurde Daniel aus der Grube herausgeholt; man fand an ihm nicht die geringste Verletzung, denn er hatte seinem Gott vertraut." },
      { number: 25, text: "Nun aber ließ der König die Männer herbeiholen, die Daniel verklagt hatten, und ließ sie mit ihren Kindern und Frauen in die Löwengrube werfen. Sie waren noch nicht am Boden der Grube angelangt, da stürzten sich die Löwen auf sie und zermalmten ihnen alle Knochen." },
      { number: 26, text: "Daraufhin schrieb König Darius an alle Völker, Nationen und Sprachen auf der ganzen Erde: Friede sei mit euch in Fülle!" },
      { number: 27, text: "Hiermit ordne ich an: Im ganzen Gebiet meines Reiches soll man vor dem Gott Daniels zittern und sich vor ihm fürchten. Denn er ist der lebendige Gott; er lebt in Ewigkeit. Sein Reich geht niemals unter; seine Herrschaft hat kein Ende.", footnotes: ["3,100"] },
      { number: 28, text: "Er rettet und befreit; er wirkt Zeichen und Wunder am Himmel und auf der Erde; er hat Daniel aus den Tatzen der Löwen errettet." },
      { number: 29, text: "Daniel aber ging es gut unter dem König Darius und auch unter dem Perserkönig Kyrus.", footnotes: ["1,21"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Im ersten Jahr Belschazzars, des Königs von Babel, hatte Daniel einen Traum; auf seinem Lager hatte er eine Vision. Er schrieb den Traum auf und sein Bericht hat folgenden Inhalt:", heading: "Daniels Vision von den vier Tieren und vom Menschensohn: 7,1-28", footnotes: ["5,1"] },
      { number: 2, text: "Ich hatte während der Nacht eine Vision: Die vier Winde des Himmels wühlten das große Meer auf.", footnotes: ["Jes 17,12f"] },
      { number: 3, text: "Dann stiegen aus dem Meer vier große Tiere herauf; jedes hatte eine andere Gestalt.", footnotes: ["Offb 13,1f"] },
      { number: 4, text: "Das erste war einem Löwen ähnlich, hatte jedoch Adlerflügel. Während ich es betrachtete, wurden ihm die Flügel ausgerissen; es wurde vom Boden emporgehoben und wie ein Mensch auf zwei Füße gestellt und es wurde ihm ein menschliches Herz gegeben.", footnotes: ["4,13"] },
      { number: 5, text: "Dann erschien ein zweites Tier; es glich einem Bären und war nach einer Seite hin aufgerichtet. Es hielt drei Rippen zwischen den Zähnen in seinem Maul und man ermunterte es: Auf, friss noch viel mehr Fleisch!" },
      { number: 6, text: "Danach sah ich ein anderes Tier; es glich einem Panther, hatte aber auf dem Rücken vier Flügel, wie die Flügel eines Vogels; auch hatte das Tier vier Köpfe; ihm wurde die Macht eines Herrschers verliehen.", footnotes: ["Ez 1,6"] },
      { number: 7, text: "Danach sah ich in meinen nächtlichen Visionen ein viertes Tier; es war furchtbar und schrecklich anzusehen und sehr stark; es hatte große Zähne aus Eisen. Es fraß und zermalmte alles, und was übrig blieb, zertrat es mit den Füßen. Von den anderen Tieren war es völlig verschieden. Auch hatte es zehn Hörner." },
      { number: 8, text: "Als ich die Hörner betrachtete, da wuchs zwischen ihnen ein anderes, kleineres Horn empor und vor ihm wurden drei von den früheren Hörnern ausgerissen; und an diesem Horn waren Augen wie Menschenaugen und ein Maul, das anmaßend redete." },
      { number: 9, text: "Ich sah immer noch hin; da wurden Throne aufgestellt und ein Hochbetagter nahm Platz. Sein Gewand war weiß wie Schnee, sein Haar wie reine Wolle. Feuerflammen waren sein Thron und dessen Räder waren loderndes Feuer.", footnotes: ["Ez 1,26; Offb 4,2f"] },
      { number: 10, text: "Ein Strom von Feuer ging von ihm aus. Tausendmal Tausende dienten ihm, zehntausendmal Zehntausende standen vor ihm. Das Gericht nahm Platz und es wurden Bücher aufgeschlagen.", footnotes: ["Offb 5,11; Ps 69,29; Offb 20,12"] },
      { number: 11, text: "Ich sah immer noch hin, bis das Tier - wegen der anmaßenden Worte, die das Horn redete - getötet wurde. Sein Körper wurde dem Feuer übergeben und vernichtet.", footnotes: ["Offb 19,20"] },
      { number: 12, text: "Auch den anderen Tieren wurde die Herrschaft genommen. Doch ließ man ihnen das Leben bis zu einer bestimmten Frist.", footnotes: ["2,21"] },
      { number: 13, text: "Immer noch hatte ich die nächtlichen Visionen: Da kam mit den Wolken des Himmels / einer wie ein Menschensohn. Er gelangte bis zu dem Hochbetagten / und wurde vor ihn geführt.", footnotes: ["Offb 1,13; 14,14", "«Menschensohn» ist im Hebräischen und Aramäischen allgemeiner Ausdruck für den einzelnen Menschen. Hier aber bezeichnet der Ausdruck im Unterschied zu den durch Tiere symbolisierten heidnischen Weltreichen den messianischen Herrscher des endzeitlichen Gottesreiches; anders bei Ezechiel (vgl. die Anmerkung zu Ez 2,1)."] },
      { number: 14, text: "Ihm wurden Herrschaft, / Würde und Königtum gegeben. Alle Völker, Nationen und Sprachen / müssen ihm dienen. Seine Herrschaft ist eine ewige, / unvergängliche Herrschaft. / Sein Reich geht niemals unter.", footnotes: ["2,44; Mt 24,30; 26,64"] },
      { number: 15, text: "Darüber war ich, Daniel, im Geist bekümmert, und was mir vor Augen stand, erschreckte mich." },
      { number: 16, text: "Ich wandte mich an einen der Umstehenden und bat ihn, mir das alles genau zu erklären. Er deutete mir die Vorgänge und sagte:" },
      { number: 17, text: "Diese großen Tiere, vier an der Zahl, bedeuten vier Könige, die sich auf der Erde erheben werden." },
      { number: 18, text: "Das Königtum aber werden die Heiligen des Höchsten erhalten und sie werden es behalten für immer und ewig.", footnotes: ["4,14"] },
      { number: 19, text: "Dann wollte ich noch Genaueres über das vierte Tier erfahren, das Tier, das anders war als alle anderen, ganz furchtbar anzusehen, mit Zähnen aus Eisen und mit Klauen aus Bronze, das alles fraß und zermalmte, und was übrig blieb, mit den Füßen zertrat." },
      { number: 20, text: "Auch (wollte ich Genaueres erfahren) über die zehn Hörner an seinem Kopf und über das andere Horn, das emporgewachsen war und vor dem die drei Hörner abgefallen waren, das Horn, das Augen und einen Mund hatte, der anmaßend redete, und das schließlich größer als die anderen zu sein schien." },
      { number: 21, text: "Ich sah dieses Horn gegen die Heiligen kämpfen. Es überwältigte sie,", footnotes: ["Offb 13,7"] },
      { number: 22, text: "bis der Hochbetagte kam. Da wurde den Heiligen des Höchsten Recht verschafft und es kam die Zeit, in der die Heiligen das Königtum erhielten." },
      { number: 23, text: "Der (Engel) antwortete mir: Das vierte Tier bedeutet: Ein viertes Reich wird sich auf der Erde erheben, ganz anders als alle anderen Reiche. Es wird die ganze Erde verschlingen, sie zertreten und zermalmen." },
      { number: 24, text: "Die zehn Hörner bedeuten: In jenem Reich werden zehn Könige regieren; doch nach ihnen kommt ein anderer. Dieser ist ganz anders als die früheren. Er stürzt drei Könige,", footnotes: ["Offb 17,12"] },
      { number: 25, text: "er lästert über den Höchsten und unterdrückt die Heiligen des Höchsten. Die Festzeiten und das Gesetz will er ändern. Ihm werden die Heiligen für eine Zeit und zwei Zeiten und eine halbe Zeit ausgeliefert.", footnotes: ["11,36; Offb 13,5f"] },
      { number: 26, text: "Dann aber wird Gericht gehalten. Jenem König wird seine Macht genommen; er wird endgültig ausgetilgt und vernichtet." },
      { number: 27, text: "Die Herrschaft und Macht und die Herrlichkeit aller Reiche unter dem ganzen Himmel werden dem Volk der Heiligen des Höchsten gegeben. Sein Reich ist ein ewiges Reich und alle Mächte werden ihm dienen und gehorchen." },
      { number: 28, text: "Hier endet die Mitteilung. Mich, Daniel, erschreckten meine Gedanken sehr und ich erbleichte. Aber ich bewahrte die Mitteilung in meinem Herzen.", footnotes: ["8,18; 10,8", "Hier endet der aramäische Teil des Danielbuches. Die Kapitel 8 - 12 sind hebräisch abgefasst."] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Im dritten Jahr der Herrschaft des Königs Belschazzar hatte ich, Daniel, eine Vision, nach jener anderen, die ich früher gehabt hatte.", heading: "Daniels Vision vom Widder und Ziegenbock: 8,1-27", footnotes: ["7,1"] },
      { number: 2, text: "Ich hatte eine Vision, und während ich sie sah, befand ich mich in der Burg Susa, die in der Provinz Elam liegt, am Ulai-Kanal." },
      { number: 3, text: "Ich blickte auf und sah, wie ein Widder am Kanal stand; er hatte zwei Hörner. Beide Hörner waren sehr hoch; doch das eine war noch höher als das andere und das höhere wuchs ihm zuletzt." },
      { number: 4, text: "Ich sah, wie der Widder nach Westen, Norden und Süden stieß; kein Tier hielt ihm stand und es gab keinen, der sich aus seiner Gewalt retten konnte. Er tat, was er wollte, und machte sich groß." },
      { number: 5, text: "Dann bemerkte ich einen Ziegenbock; er überquerte von Westen her die ganze Erde, ohne aber den Boden zu berühren; der Bock hatte ein auffallendes Horn zwischen den Augen." },
      { number: 6, text: "Er lief zu dem Widder mit den zwei Hörnern, den ich am Kanal stehen sah, und rannte mit grimmiger Kraft auf ihn los." },
      { number: 7, text: "Ich sah, wie er auf den Widder losging und ihm wütend zusetzte. Er stieß gegen den Widder und brach ihm beide Hörner ab. Der Widder hatte nicht die Kraft, ihm standzuhalten. Da warf der Ziegenbock ihn zu Boden und zertrat ihn; und niemand war da, um den Widder aus seiner Gewalt zu retten." },
      { number: 8, text: "Der Ziegenbock wurde über die Maßen groß. Als er aber am stärksten war, brach das große Horn ab. An seiner Stelle wuchsen ihm vier auffallende Hörner, und zwar nach den vier Himmelsrichtungen.", footnotes: ["7,8"] },
      { number: 9, text: "Aus einem der Hörner ging dann ein anderes Horn hervor. Anfangs klein, wuchs es gewaltig nach Süden und Osten, nach dem Ort der Zierde hin.", footnotes: ["Ez 20,6", "Der «Ort der Zierde» ist Jerusalem (vgl. V. 13); entsprechend gilt als «Land der Zierde» das Heilige Land (vgl. 11,16.41), während der «Berg der Zierde» den Zion meint (vgl. 11,45)."] },
      { number: 10, text: "Es wuchs bis zum Sternenheer am Himmel hinauf und warf einige aus dem Sternenheer auf die Erde herab und zertrat sie.", footnotes: ["Sach 7,14"] },
      { number: 11, text: "Ja, bis zum Gebieter des Himmelsheeres reckte es sich empor; es entzog ihm das tägliche Opfer und verwüstete sein Heiligtum.", footnotes: ["9,27; 11,31"] },
      { number: 12, text: "Ein Heer wurde verbrecherisch gegen das tägliche Opfer eingesetzt. Das Horn stürzte die Wahrheit zu Boden, und was es unternahm, das gelang ihm." },
      { number: 13, text: "Da hörte ich einen Heiligen (einen Engel) reden und ein anderer Heiliger fragte den Redenden: Wie lange gilt die Vision vom täglichen Opfer, wie lange bleibt der Gräuel der Verwüstung bestehen und werden das Heiligtum und der Ort der Zierde zertreten?", footnotes: ["12,6f; Sach 1,9f"] },
      { number: 14, text: "Er sagte zu mir: Zweitausenddreihundert Abende und Morgen wird es dauern; dann erhält das Heiligtum wieder sein Recht.", footnotes: ["7,25"] },
      { number: 15, text: "Während ich, Daniel, noch diese Vision hatte und sie zu verstehen suchte, da stand vor mir einer, der aussah wie ein Mann.", footnotes: ["9,21"] },
      { number: 16, text: "Und über dem Ulai-Kanal hörte ich eine Menschenstimme, die rief: Gabriel, erkläre ihm die Vision!", footnotes: ["Lk 1,19"] },
      { number: 17, text: "Da kam er auf mich zu. Als er näher trat, erschrak ich und fiel mit dem Gesicht zu Boden. Er sagte zu mir: Mensch, versteh: Die Vision betrifft die Zeit des Endes.", footnotes: ["(17-18) 10,15-19"] },
      { number: 18, text: "Während er mit mir redete, lag ich ohnmächtig da, mit dem Gesicht am Boden. Da berührte er mich und stellte mich wieder auf die Beine.", footnotes: ["Offb 1,17; Ez 2,1f"] },
      { number: 19, text: "Dann sagte er: Siehe, ich kündige dir an, was in der letzten Zeit, der Zeit des Zorns, geschehen wird; denn die Vision bezieht sich auf die Zeit des Endes." },
      { number: 20, text: "Der Widder mit den zwei Hörnern, den du gesehen hast, bedeutet die Könige von Medien und Persien." },
      { number: 21, text: "Der Ziegenbock ist der König von Jawan. Das große Horn zwischen seinen Augen ist der erste König." },
      { number: 22, text: "Das Horn brach ab und vier andere traten an seine Stelle; das bedeutet: Aus seinem Volk entstehen vier Reiche; sie haben aber nicht die gleiche Kraft wie er." },
      { number: 23, text: "In der letzten Zeit ihrer Herrschaft, wenn die Frevler ihr Maß voll gemacht haben, kommt ein König voll Härte und Verschlagenheit.", footnotes: ["11,21"] },
      { number: 24, text: "Er wird mächtig und stark und richtet ungeheures Verderben an; alles, was er unternimmt, gelingt ihm. Mächtige Herrscher wird er vernichten, auch das Volk der Heiligen.", footnotes: ["7,18"] },
      { number: 25, text: "Dank seiner Schlauheit gelingt ihm sein Betrug. Er wird überheblich und bringt über viele unversehens Verderben. Selbst gegen den höchsten Gebieter steht er auf; doch ohne Zutun eines Menschen wird er zerschmettert." },
      { number: 26, text: "Die Vision von den Abenden und den Morgen, die dir offenbart wurde, ist wahr; doch du sollst sie geheim halten; denn sie bezieht sich auf eine ferne Zeit.", footnotes: ["12,4; Offb 22,6"] },
      { number: 27, text: "Darauf war ich, Daniel, erschöpft und lag mehrere Tage krank zu Bett. Dann stand ich auf und versah wieder meinen Dienst beim König. Doch die Vision bedrückte mich und ich verstand sie nicht." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Im ersten Jahr, nachdem Darius, der Sohn des Xerxes, aus dem Stamm der Meder, König über das Reich der Chaldäer geworden war,", heading: "Die Weissagung von den siebzig Jahrwochen: 9,1-27", footnotes: ["6,1"] },
      { number: 2, text: "in diesem ersten Jahr seiner Herrschaft suchte ich, Daniel, in den Schriften die Zahl der Jahre zu ergründen, die Jerusalem nach dem Wort des Herrn an den Propheten Jeremia verwüstet sein sollte; es waren siebzig Jahre.", footnotes: ["Jer 25,11f; 29,10"] },
      { number: 3, text: "Ich richtete mein Gesicht zu Gott, dem Herrn, um ihn mit Gebet und Flehen, bei Fasten in Sack und Asche, zu bitten." },
      { number: 4, text: "Ich betete zum Herrn, meinem Gott, legte ein Bekenntnis ab und sagte: Herr, du großer und Furcht erregender Gott, du bewahrst denen, die dich lieben und deine Gebote halten, deinen Bund und deine Gnade.", footnotes: ["(4-19) 3,25-45; Neh 1,5-11; 9; Bar 1,15 - 2,26"] },
      { number: 5, text: "Wir haben gesündigt und Unrecht getan, wir sind treulos gewesen und haben uns gegen dich empört; von deinen Geboten und Gesetzen sind wir abgewichen." },
      { number: 6, text: "Wir haben nicht auf deine Diener, die Propheten, gehört, die in deinem Namen zu unseren Königen und Vorstehern, zu unseren Vätern und zu allen Bürgern des Landes geredet haben.", footnotes: ["Jer 7,25f"] },
      { number: 7, text: "Du, Herr, bist im Recht; uns aber steht bis heute die Schamröte im Gesicht, den Leuten von Juda, den Einwohnern Jerusalems und allen Israeliten, seien sie nah oder fern in all den Ländern, wohin du sie verstoßen hast; denn sie haben dir die Treue gebrochen.", footnotes: ["Esra 9,6-8"] },
      { number: 8, text: "Ja, Herr, uns steht die Schamröte im Gesicht, unseren Königen, Oberen und Vätern; denn wir haben uns gegen dich versündigt." },
      { number: 9, text: "Aber der Herr, unser Gott, schenkt Erbarmen und Vergebung. Ja, wir haben uns gegen ihn empört." },
      { number: 10, text: "Wir haben nicht auf die Stimme des Herrn, unseres Gottes, gehört und seine Befehle nicht befolgt, die er uns durch seine Diener, die Propheten, gegeben hat." },
      { number: 11, text: "Ganz Israel hat dein Gesetz übertreten, ist davon abgewichen und hat nicht auf deine Stimme gehört. Darum kamen der Fluch und die Verwünschung über uns, die im Gesetz des Mose, des Dieners Gottes, geschrieben stehen; denn wir haben uns gegen Gott versündigt.", footnotes: ["Lev 26,14-39; Dtn 28,15-68"] },
      { number: 12, text: "Gott machte seine Drohung wahr, die er gegen uns und unsere Richter, die uns regierten, ausgesprochen hatte: Er werde so schweres Unheil über uns bringen, dass unter dem ganzen Himmel nie solche Dinge geschehen sein werden wie in Jerusalem.", footnotes: ["Mit den «Richtern» sind alle Regierenden gemeint."] },
      { number: 13, text: "Wie es im Gesetz des Mose geschrieben steht, ist all dieses Unheil über uns gekommen. Und doch haben wir den Herrn, unseren Gott, nicht begütigt, haben uns nicht von unserem bösen Tun abgewandt und nicht auf deine Wahrheit geachtet." },
      { number: 14, text: "Der Herr aber war wach und ließ dieses Unheil über uns kommen. Denn der Herr, unser Gott, ist gerecht in allem, was er tut. Wir aber hörten nicht auf seine Stimme.", footnotes: ["Jer 1,12"] },
      { number: 15, text: "Jetzt aber, Herr, unser Gott, der du dein Volk mit starker Hand aus Ägypten geführt und dir damit einen Namen gemacht hast bis auf den heutigen Tag! Wir haben gesündigt; wir haben gefrevelt.", footnotes: ["Dtn 6,21; Jer 32,20f; (15-16) Bar 2,11-19"] },
      { number: 16, text: "Herr, wende jetzt deinen grimmigen Zorn von deiner Stadt Jerusalem und von deinem heiligen Berg ab, wie es deinen früheren hilfreichen Taten entspricht. Wegen unserer Sünden und der bösen Taten unserer Väter sind Jerusalem und dein Volk zum Gespött für alle geworden, die rings um uns wohnen.", footnotes: ["Ps 103,6"] },
      { number: 17, text: "Darum höre jetzt, unser Gott, das Gebet und Flehen deines Knechtes: Herr, lass auch um deiner selbst willen dein Angesicht über deinem Heiligtum leuchten, das verwüstet daliegt." },
      { number: 18, text: "Mein Gott, neig mir dein Ohr zu und höre mich; öffne deine Augen und sieh auf die Trümmer, auf unsere Stadt, über der dein Name ausgerufen ist. Nicht im Vertrauen auf unsere guten Taten legen wir dir unsere Bitten vor, sondern im Vertrauen auf dein großes Erbarmen.", footnotes: ["2 Kön 19,16"] },
      { number: 19, text: "Herr, erhöre! Herr, verzeih! Herr, vernimm das Gebet und handle! Mein Gott, auch um deiner selbst willen zögere nicht! Dein Name ist doch über deiner Stadt und deinem Volk ausgerufen." },
      { number: 20, text: "Während ich noch redete und betete, meine Sünden und die Sünden meines Volkes Israel bekannte und meine Bitte für den heiligen Berg meines Gottes vor den Herrn, meinen Gott, brachte," },
      { number: 21, text: "während ich also noch mein Gebet sprach, da kam im Flug der Mann Gabriel, den ich früher in der Vision gesehen hatte; er kam um die Zeit des Abendopfers zu mir," },
      { number: 22, text: "redete mit mir und sagte: Daniel, ich bin gesandt worden, um dir klare Einsicht zu geben." },
      { number: 23, text: "Schon zu Beginn deines Gebets erging ein Gotteswort und ich bin gekommen, um es dir zu verkünden; denn du bist (von Gott) geliebt. Achte also auf das Wort und begreife die Vision!" },
      { number: 24, text: "Siebzig Wochen sind für dein Volk / und deine heilige Stadt bestimmt, bis der Frevel beendet ist, / bis die Sünde versiegelt und die Schuld gesühnt ist, bis ewige Gerechtigkeit gebracht wird, / bis Visionen und Weissagungen besiegelt werden / und ein Hochheiliges gesalbt wird.", footnotes: ["Jes 53,11; 1 Chr 23,13; Ez 45,3; Ex 29,37", "Gemeint sind sog. Jahreswochen (je sieben Jahre). - ein Hochheiliges: Gemeint ist wahrscheinlich das sog. Allerheiligste d. h. der innerste Raum des Tempels."] },
      { number: 25, text: "Nun begreif und versteh: Von der Verkündigung des Wortes über die Rückführung des Volkes und den Wiederaufbau Jerusalems bis zur Ankunft eines Gesalbten, eines Fürsten, sind es sieben Wochen; und zweiundsechzig Wochen lang baut man die Stadt wieder auf mit ihren Plätzen und Gräben, obwohl es eine bedrängte Zeit sein wird." },
      { number: 26, text: "Nach den zweiundsechzig Wochen wird ein Gesalbter umgebracht, aber ohne (Richterspruch). Das Volk eines Fürsten, der kommen wird, bringt Verderben über die Stadt und das Heiligtum. Er findet sein Ende in der Flut; bis zum Ende werden Krieg und Verwüstung herrschen, wie es längst beschlossen ist." },
      { number: 27, text: "Vielen macht er den Bund schwer, eine Woche lang. In der Mitte dieser Woche setzt er den Schlachtopfern und Speiseopfern ein Ende. Oben auf dem Heiligtum wird ein unheilvoller Gräuel stehen, bis das Verderben, das beschlossen ist, über den Verwüster kommt.", footnotes: ["11,31; 12,11; Mt 24,15; Jes 10,22f"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Im dritten Jahr des Königs Kyrus von Persien empfing Daniel, der auch Beltschazzar heißt, eine Offenbarung. Das Wort ist zuverlässig und kündigt große Not an. Er suchte das Wort zu verstehen und das Verständnis wurde ihm in einer Vision gegeben.", heading: "Die letzten Offenbarungen an Daniel: 10,1 - 12,13" },
      { number: 2, text: "In jenen Tagen hielt ich, Daniel, drei Wochen lang Trauer." },
      { number: 3, text: "Nahrung, die mir sonst schmeckte, aß ich nicht; Fleisch und Wein kamen nicht in meinen Mund; auch salbte ich mich nicht, bis drei volle Wochen vorbei waren." },
      { number: 4, text: "Am vierundzwanzigsten Tag des ersten Monats stand ich am Ufer des großen Flusses, des Tigris." },
      { number: 5, text: "Ich blickte auf und sah, wie ein Mann vor mir stand, der in Leinen gekleidet war und einen Gürtel aus feinstem Gold um die Hüften trug.", footnotes: ["8,15; Offb 1,13-15; Ez 1,27f"] },
      { number: 6, text: "Sein Körper glich einem Chrysolith, sein Gesicht leuchtete wie ein Blitz und die Augen waren wie brennende Fackeln. Seine Arme und Beine glänzten wie polierte Bronze. Seine Worte waren wie das Getöse einer großen Menschenmenge." },
      { number: 7, text: "Nur ich, Daniel, sah diese Erscheinung; die Männer, die bei mir waren, sahen die Erscheinung nicht; doch ein großer Schrecken befiel sie, sodass sie wegliefen und sich versteckten." },
      { number: 8, text: "So blieb ich allein zurück und sah diese gewaltige Erscheinung. Meine Kräfte verließen mich; ich wurde totenbleich und konnte mich nicht mehr aufrecht halten." },
      { number: 9, text: "Ich hörte den Schall seiner Worte; beim Schall seiner Worte fiel ich betäubt zu Boden und blieb, mit dem Gesicht am Boden, liegen.", footnotes: ["8,18"] },
      { number: 10, text: "Doch eine Hand fasste mich an und half mir auf Knie und Hände." },
      { number: 11, text: "Dann sagte er zu mir: Daniel, du (von Gott) geliebter Mann, achte auf die Worte, die ich dir zu sagen habe. Stell dich aufrecht hin; denn ich bin jetzt zu dir gesandt. Als er so mit mir redete, erhob ich mich zitternd.", footnotes: ["9,23"] },
      { number: 12, text: "Dann sagte er zu mir: Fürchte dich nicht, Daniel! Schon vom ersten Tag an, als du dich um Verständnis bemühtest und dich deswegen vor deinem Gott beugtest, wurden deine Worte gehört und wegen deiner Worte bin ich gekommen.", footnotes: ["Esra 8,21"] },
      { number: 13, text: "Der Engelfürst des Perserreiches hat sich mir einundzwanzig Tage lang entgegengestellt, aber Michael, einer der ersten unter den Engelfürsten, kam mir zu Hilfe. Darum war ich dort bei den Königen von Persien entbehrlich.", footnotes: ["Sach 3,1f; Jud 9; Offb 12,7"] },
      { number: 14, text: "Und jetzt bin ich gekommen, um dich verstehen zu lassen, was deinem Volk in den letzten Tagen zustoßen wird. Denn auch diese Vision bezieht sich auf jene fernen Tage." },
      { number: 15, text: "Während er das zu mir sagte, blickte ich zu Boden und blieb stumm." },
      { number: 16, text: "Da berührte eine Gestalt, die aussah wie ein Mensch, meine Lippen. Nun konnte ich den Mund wieder öffnen und sprechen. Ich sagte zu dem, der vor mir stand: Mein Herr, als ich die Vision sah, wand ich mich in Schmerzen und verlor alle Kraft.", footnotes: ["7,13; Jes 6,7; Jer 1,9"] },
      { number: 17, text: "Wie kann ich, der Knecht meines Herrn, mit meinem Herrn reden? Mir fehlt seitdem jede Kraft, selbst der Atem stockt mir." },
      { number: 18, text: "Da berührte mich die Gestalt, die wie ein Mensch aussah, von neuem, stärkte mich", footnotes: ["8,18"] },
      { number: 19, text: "und sagte: Fürchte dich nicht, du (von Gott) geliebter Mann! Friede sei mit dir. Sei stark und hab Vertrauen! Als er so mit mir redete, fühlte ich mich gestärkt und sagte: Nun rede, mein Herr! Du hast mich gestärkt.", footnotes: ["Offb 1,17"] },
      { number: 20, text: "Er sagte: Weißt du, warum ich zu dir gekommen bin? Ich muss bald zurückkehren und mit dem Engelfürsten von Persien kämpfen. Wenn ich mit ihm fertig bin, dann wird der Engelfürst von Jawan kommen." },
      { number: 21, text: "Vorher aber will ich dir mitteilen, was im Buch der Wahrheit aufgezeichnet ist. Doch keiner hilft mir tatkräftig gegen sie außer eurem Engelfürsten Michael." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Im ersten Jahr des Meders Darius stand ich ihm helfend und schützend zur Seite.", footnotes: ["9,1"] },
      { number: 2, text: "Und jetzt will ich dir die Wahrheit mitteilen: Noch drei Könige kommen in Persien; der vierte aber wird größeren Reichtum erwerben als alle anderen vor ihm. Nachdem er reich und mächtig geworden ist, bietet er all seine Macht gegen das Reich von Jawan auf." },
      { number: 3, text: "Dann wird ein kraftvoller König kommen; er herrscht mit großer Macht und tut, was er will.", footnotes: ["1 Makk 1,2-6"] },
      { number: 4, text: "Doch kaum ist er aufgetreten, da bricht sein Reich auseinander und teilt sich nach den vier Himmelsrichtungen. Es fällt aber nicht seinen Nachkommen zu und ist nicht mehr so mächtig wie unter seiner Herrschaft. Denn sein Reich wird vernichtet; es fällt anderen zu, nicht seinen Erben." },
      { number: 5, text: "Dann erstarkt der König des Südens; aber einer von seinen Feldherrn wird mächtiger als er und tritt die Herrschaft an und seine Herrschaft wird noch gewaltiger sein." },
      { number: 6, text: "Nach Jahren schließen sie dann ein Bündnis und um den Frieden zu bekräftigen, kommt die Tochter des Königs des Südens zum König des Nordens. Doch sie verliert die Macht und auch ihr Kind bleibt nicht am Leben; zur (bestimmten) Zeit wird sie dem Untergang preisgegeben, sie, ihre Begleiter, auch der, der sie gezeugt hat, und der, der sie zur Frau genommen hat.", footnotes: ["2,43", "Übersetzung unsicher."] },
      { number: 7, text: "Aber an seiner Stelle tritt ein Spross aus ihren Wurzeln auf; er zieht gegen das Heer aus, dringt in die Festung des Königs des Nordens ein und verfährt mit ihnen, wie es ein Sieger tut." },
      { number: 8, text: "Sogar ihre Götter nimmt er als Beute nach Ägypten mit, ebenso ihre Götterbilder und die kostbaren Geräte aus Silber und Gold. Dann lässt er den König des Nordens einige Jahre in Ruhe." },
      { number: 9, text: "Darauf zieht dieser gegen das Reich des Königs des Südens, muss aber in sein Land zurückkehren." },
      { number: 10, text: "Doch seine Söhne rüsten zum Krieg und bringen gewaltige Heere zusammen. Einer (von ihnen) zieht gegen (den König des Südens); er rückt vor und überflutet alles. Dann rüstet er nochmals und dringt bis zu seiner Festung vor." },
      { number: 11, text: "Das erbittert den König des Südens; er zieht gegen den König des Nordens aus und kämpft gegen ihn. Dieser hat zwar ein großes Heer aufgestellt, aber das Heer fällt dem andern in die Hand" },
      { number: 12, text: "und wird aufgerieben. Da wird sein Herz stolz; er wirft Zehntausende zu Boden, aber er bleibt nicht stark." },
      { number: 13, text: "Der König des Nordens stellt nochmals ein Heer auf, noch größer als das erste, und nach einigen Jahren zieht er gegen jenen mit einer großen Streitmacht und einem riesigen Tross ins Feld." },
      { number: 14, text: "In jener Zeit erheben sich viele gegen den König des Südens; auch gewalttätige Leute aus deinem Volk stehen auf und so erfüllt sich eine Vision. Aber sie kommen zu Fall." },
      { number: 15, text: "Da zieht der König des Nordens heran, schüttet einen Wall auf und erobert eine stark befestigte Stadt. Die Kräfte des Königs des Südens halten nicht stand; selbst die Truppe seiner auserlesenen Kämpfer ist nicht stark genug, um sich zu halten.", footnotes: ["8,23-25"] },
      { number: 16, text: "Der, der gegen ihn vorrückt, tut (mit ihnen), was er will. Keiner kann ihm widerstehen; so fasst er Fuß im Land der Zierde und seine Hand bringt Vernichtung.", footnotes: ["8,9"] },
      { number: 17, text: "Dann fasst er den Plan, das ganze Reich (des Königs des Südens) zu unterjochen. Er schließt einen Vergleich mit ihm und gibt ihm eine seiner Töchter zur Frau, um seine Macht zu zerstören. Doch das kommt nicht zustande, es gelingt ihm nicht.", footnotes: ["2,43"] },
      { number: 18, text: "Jetzt wendet er sich gegen die Inseln und erobert viele von ihnen. Aber ein Feldherr macht seiner Herausforderung ein Ende, ja, er vergilt ihm seine Herausforderung." },
      { number: 19, text: "Nun wendet er sich den Festungen des eigenen Landes zu. Er stolpert jedoch, fällt und ist nicht mehr zu finden." },
      { number: 20, text: "An seine Stelle tritt einer, der einen Steuereintreiber durch die Zierde des Reiches ziehen lässt. Doch wird er schon nach kurzer Zeit beseitigt, aber nicht öffentlich und nicht im Kampf.", footnotes: ["Vgl. die Anmerkung zu 8,9."] },
      { number: 21, text: "An seine Stelle tritt ein verächtlicher Mensch. Ihm überträgt man die Würde des Königtums nicht; er kommt aber unversehens und reißt die Herrschaft durch List an sich.", footnotes: ["8,23f"] },
      { number: 22, text: "Ganze Heere werden vor ihm hinweggeschwemmt und vernichtet, auch der Fürst des Bundes." },
      { number: 23, text: "Wer sich mit ihm verbündet, gegen den handelt er heimtückisch. Er kommt empor, und obwohl er nur wenige Anhänger hat, wird er stark." },
      { number: 24, text: "Unversehens dringt er in die reichsten Bezirke einer Provinz ein und tut, was weder seine Väter noch seine Ahnen getan haben: Er raubt, macht Beute und verteilt den Besitz an seine Leute. Er denkt Anschläge gegen die Festungen aus, aber das dauert nur eine bestimmte Zeit." },
      { number: 25, text: "Er bietet seine ganze Kraft und seinen ganzen Mut auf und zieht mit einem großen Heer gegen den König des Südens. Darauf rüstet auch der König des Südens mit einem großen und sehr starken Heer zum Krieg; er kann aber nicht standhalten; denn man plant Anschläge gegen ihn." },
      { number: 26, text: "Die eigenen Tischgenossen führen seinen Sturz herbei. Sein Heer wird weggeschwemmt, viele werden erschlagen und fallen." },
      { number: 27, text: "Beide Könige sinnen auf Böses; sie sitzen am selben Tisch zusammen und belügen einander. Aber sie erreichen nichts, denn das Ende steht noch aus, bis zur bestimmten Zeit." },
      { number: 28, text: "Mit großem Tross tritt dann der König des Nordens den Rückweg in sein Land an; sein Sinn ist gegen den Heiligen Bund gerichtet. Er handelt auch entsprechend und kehrt schließlich in sein Land zurück." },
      { number: 29, text: "Zu einer bestimmten Zeit fällt er wieder in den Süden ein. Doch das zweite Mal geht es nicht wie das erste Mal." },
      { number: 30, text: "Kittäische Schiffe greifen ihn an und er kehrt eingeschüchtert um. Nun wendet er seine Wut gegen den Heiligen Bund und handelt entsprechend. Dann kehrt er heim und erkennt jene an, die den Heiligen Bund verlassen." },
      { number: 31, text: "Er stellt Streitkräfte auf, die das Heiligtum auf der Burg entweihen, das tägliche Opfer abschaffen und den unheilvollen Gräuel aufstellen.", footnotes: ["8,11f; 9,27; 12,11; Mt 24,15"] },
      { number: 32, text: "Er verführt mit seinen glatten Worten die Menschen dazu, vom Bund abzufallen; doch die Schar derer, die ihrem Gott treu sind, bleibt fest und handelt entsprechend." },
      { number: 33, text: "Die Verständigen im Volk bringen viele zur Einsicht; aber eine Zeit lang zwingt man sie nieder mit Feuer und Schwert, mit Haft und Plünderung.", footnotes: ["12,3"] },
      { number: 34, text: "Doch während man sie niederzwingt, erfahren sie eine kleine Hilfe; viele schließen sich ihnen an, freilich nur zum Schein." },
      { number: 35, text: "Aber auch manche von den Verständigen kommen zu Fall; so sollen sie geprüft, geläutert und gereinigt werden bis zur Zeit des Endes; denn es dauert noch eine Weile bis zu der bestimmten Zeit." },
      { number: 36, text: "Der König tut, was er will. Er wird übermütig und prahlt gegenüber allen Göttern, auch gegenüber dem höchsten Gott führt er unglaubliche Reden. Dabei hat er Erfolg, bis der Zorn (Gottes) zu Ende ist. Denn was beschlossen ist, muss ausgeführt werden.", footnotes: ["7,8.25; 2 Thess 2,4; Offb 13,5f"] },
      { number: 37, text: "Er missachtet sogar die Götter seiner Väter, auch den Liebling der Frauen achtet er nicht und überhaupt keinen Gott; er prahlt gegenüber allen." },
      { number: 38, text: "Stattdessen verehrt er den Gott der Festungen; einen Gott, den seine Väter nicht gekannt haben, verehrt er mit Gold und Silber, mit Edelsteinen und Kostbarkeiten." },
      { number: 39, text: "Starke Festungen greift er an mit Hilfe des fremden Gottes. Alle, die ihn anerkennen, überhäuft er mit Ehren; er verleiht ihnen die Herrschaft über viele Menschen und teilt ihnen als Belohnung Land zu." },
      { number: 40, text: "Zur Zeit des Endes streitet mit ihm der König des Südens. Da stürmt der König des Nordens gegen ihn heran mit Wagen und Reitern und mit vielen Schiffen. Er dringt in die Länder ein, überschwemmt sie und rückt vor." },
      { number: 41, text: "Auch ins Land der Zierde dringt er ein. Viele werden niedergezwungen; nur Edom und Moab und der Hauptteil der Ammoniter entgehen ihm.", footnotes: ["11,16; 8,9"] },
      { number: 42, text: "Er streckt seine Hand nach den Ländern aus; auch für Ägypten gibt es keine Rettung." },
      { number: 43, text: "Er wird Herr über die Schätze von Gold und Silber und über alle Kostbarkeiten Ägyptens. Libyer und Kuschiter leisten ihm Gefolgschaft." },
      { number: 44, text: "Da erschrecken ihn Gerüchte aus dem Osten und dem Norden. In großem Zorn zieht er aus, um viele zu vernichten und auszurotten." },
      { number: 45, text: "Zwischen dem Meer und dem Berg der heiligen Zierde schlägt er seine Prunkzelte auf. Dann geht er seinem Ende zu und niemand ist da, der ihm hilft." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "In jener Zeit tritt Michael auf, der große Engelfürst, der für die Söhne deines Volkes eintritt. Dann kommt eine Zeit der Not, wie noch keine da war, seit es Völker gibt, bis zu jener Zeit. Doch dein Volk wird in jener Zeit gerettet, jeder, der im Buch verzeichnet ist.", footnotes: ["10,13; Offb 12,7; Mt 24,21"] },
      { number: 2, text: "Von denen, die im Land des Staubes schlafen, werden viele erwachen, die einen zum ewigen Leben, die anderen zur Schmach, zu ewigem Abscheu.", footnotes: ["2 Makk 7,9; Jes 66,24; Joh 5,29"] },
      { number: 3, text: "Die Verständigen werden strahlen, wie der Himmel strahlt; und die Männer, die viele zum rechten Tun geführt haben, werden immer und ewig wie die Sterne leuchten.", footnotes: ["11,33; Spr 4,18; Mt 13,43"] },
      { number: 4, text: "Du, Daniel, halte diese Worte geheim und versiegle das Buch bis zur Zeit des Endes! Viele werden nachforschen und die Erkenntnis wird groß sein.", footnotes: ["8,26; Offb 10,4"] },
      { number: 5, text: "Als ich, Daniel, aufblickte, standen noch zwei andere Männer da, der eine diesseits des Flussufers, der andere jenseits." },
      { number: 6, text: "Einer fragte den Mann, der in Leinen gekleidet war und über dem Wasser des Flusses stand: Wie lange dauert es noch bis zum Ende der unbegreiflichen Geschehnisse?", footnotes: ["Offb 10,5f"] },
      { number: 7, text: "Darauf hörte ich die Stimme des Mannes, der in Leinen gekleidet war und über dem Wasser des Flusses stand; er erhob seine rechte und seine linke Hand zum Himmel, schwor bei dem, der ewig lebt, und sagte: Es dauert noch eine Zeit, zwei Zeiten und eine halbe Zeit. Wenn der am Ende ist, der die Macht des heiligen Volkes zerschlägt, dann wird sich das alles vollenden.", footnotes: ["7,25"] },
      { number: 8, text: "Ich hörte es, verstand es aber nicht. Darum fragte ich: Mein Herr, was wird das letzte von all dem sein?" },
      { number: 9, text: "Er erwiderte: Geh, Daniel! Diese Worte bleiben verschlossen und versiegelt bis zur Zeit des Endes." },
      { number: 10, text: "Viele werden geläutert, gereinigt und geprüft. Doch die ruchlosen Sünder sündigen weiter. Von den Sündern wird es keiner verstehen; aber die Verständigen verstehen es.", footnotes: ["11,35"] },
      { number: 11, text: "Von der Zeit an, in der man das tägliche Opfer abschafft und den unheilvollen Gräuel aufstellt, sind es zwölfhundertneunzig Tage." },
      { number: 12, text: "Wohl dem, der aushält und dreizehnhundertfünfunddreißig Tage erreicht!", footnotes: ["(11-12) 8,14"] },
      { number: 13, text: "Du aber geh nun dem Ende zu! Du wirst ruhen und am Ende der Tage wirst du auferstehen, um dein Erbteil zu empfangen." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "In Babylon wohnte ein Mann mit Namen Jojakim.", heading: "Die Anhänge zum Buch Daniel: 13,1 - 14,42 | Die Rettung der Susanna durch Daniel: 13,1-64", footnotes: ["Mit 12,13 endet das hebräisch-aramäische Danielbuch. In Vg folgen als Kapitel 13 und 14 noch drei weitere Erzählungen von Daniel. Die Übersetzung folgt Theodotion; zur griechischen Gestalt des Danielbuches vgl. die Einleitung."] },
      { number: 2, text: "Er hatte Susanna, die Tochter Hilkijas, zur Frau; sie war sehr schön und gottesfürchtig." },
      { number: 3, text: "Auch ihre Eltern waren gerecht und hatten ihre Tochter nach dem Gesetz des Mose erzogen." },
      { number: 4, text: "Jojakim war sehr reich; er besaß einen Garten nahe bei seinem Haus. Die Juden pflegten bei ihm zusammenzukommen, weil er der Angesehenste von allen war." },
      { number: 5, text: "Als Richter amtierten in jenem Jahr zwei Älteste aus dem Volk, von denen galt, was der Herr gesagt hat: Ungerechtigkeit ging von Babylon aus, von den Ältesten, von den Richtern, die als Leiter des Volkes galten.", footnotes: ["Jer 23,15; 29,22f"] },
      { number: 6, text: "Sie hielten sich regelmäßig im Haus Jojakims auf und alle, die eine Rechtssache hatten, kamen zu ihnen." },
      { number: 7, text: "Hatten sich nun die Leute um die Mittagszeit wieder entfernt, dann kam Susanna und ging im Garten ihres Mannes spazieren." },
      { number: 8, text: "Die beiden Ältesten sahen sie täglich kommen und umhergehen; da regte sich in ihnen die Begierde nach ihr." },
      { number: 9, text: "Ihre Gedanken gerieten auf Abwege und ihre Augen gingen in die Irre; sie sahen weder zum Himmel auf, noch dachten sie an die gerechten Strafen Gottes." },
      { number: 10, text: "Beide hatten wegen Susanna Liebeskummer; doch keiner sagte dem anderen etwas von seinem Schmerz." },
      { number: 11, text: "Denn sie schämten sich darüber, dass sie so begierig waren, mit ihr zusammen zu sein." },
      { number: 12, text: "Ungeduldig warteten sie jeden Tag darauf, sie zu sehen." },
      { number: 13, text: "Eines Tages sagte der eine zum andern: Gehen wir nach Hause, es ist Zeit zum Essen. Sie trennten sich also und gingen weg," },
      { number: 14, text: "dann aber kehrte jeder um und sie trafen wieder zusammen. Sie fragten einander nach der Ursache und gestanden sich ihre Leidenschaft. Daraufhin verabredeten sie eine Zeit, zu der es ihnen möglich sein sollte, Susanna allein anzutreffen." },
      { number: 15, text: "Während sie auf einen günstigen Tag warteten, kam Susanna eines Tages wie gewöhnlich in den Garten, nur von zwei Mädchen begleitet, und wollte baden; denn es war heiß.", footnotes: ["Ex 2,5"] },
      { number: 16, text: "Niemand war dort außer den beiden Ältesten, die sich versteckt hatten und ihr auflauerten." },
      { number: 17, text: "Sie sagte zu den Mädchen: Holt mir Öl und Salben und verriegelt das Gartentor, damit ich baden kann." },
      { number: 18, text: "Die Mädchen taten, wie ihnen befohlen war. Sie verriegelten das Tor und verließen den Garten durch die Seitenpforte, um zu holen, was ihnen aufgetragen war. Von den Ältesten bemerkten sie nichts, denn diese hatten sich versteckt." },
      { number: 19, text: "Als die Mädchen weg waren, standen die beiden Ältesten auf, liefen zu Susanna hin" },
      { number: 20, text: "und sagten: Das Gartentor ist verschlossen und niemand sieht uns; wir brennen vor Verlangen nach dir: Sei uns zu Willen und gib dich uns hin!" },
      { number: 21, text: "Weigerst du dich, dann bezeugen wir gegen dich, dass ein junger Mann bei dir war und dass du deshalb die Mädchen weggeschickt hast." },
      { number: 22, text: "Da seufzte Susanna und sagte: Ich bin bedrängt von allen Seiten: Wenn ich es tue, so droht mir der Tod; tue ich es aber nicht, so werde ich euch nicht entrinnen.", footnotes: ["Lev 20,10; Dtn 22,22; Joh 8,4f"] },
      { number: 23, text: "Es ist besser für mich, es nicht zu tun und euch in die Hände zu fallen, als gegen den Herrn zu sündigen." },
      { number: 24, text: "Dann schrie Susanna, so laut sie konnte. Aber zugleich mit ihr schrien auch die beiden Ältesten" },
      { number: 25, text: "und einer von ihnen lief zum Gartentor und öffnete es." },
      { number: 26, text: "Als die Leute im Haus das Geschrei im Garten hörten, eilten sie durch die Seitentür herbei, um zu sehen, was ihr zugestoßen sei." },
      { number: 27, text: "Als die Ältesten ihre Erklärung gaben, schämten sich die Diener sehr; denn noch nie war so etwas über Susanna gesagt worden." },
      { number: 28, text: "Als am nächsten Morgen das Volk bei Jojakim, ihrem Mann, zusammenkam, erschienen auch die beiden Ältesten. Sie kamen mit der verbrecherischen Absicht, gegen Susanna die Todesstrafe zu erwirken. Sie sagten vor dem Volk:" },
      { number: 29, text: "Schickt nach Susanna, der Tochter Hilkijas, der Frau Jojakims! Man schickte nach ihr." },
      { number: 30, text: "Sie kam, begleitet von ihren Eltern, ihren Kindern und allen Verwandten." },
      { number: 31, text: "Susanna war anmutig und sehr schön." },
      { number: 32, text: "Sie war aber verschleiert. Um sich an ihrer Schönheit zu weiden, befahlen die Gewissenlosen, sie zu entschleiern." },
      { number: 33, text: "Da weinten ihre Angehörigen und alle, die sie sahen, begannen ebenfalls zu weinen." },
      { number: 34, text: "Vor dem ganzen Volk standen nun die beiden Ältesten auf und legten die Hände auf den Kopf Susannas.", footnotes: ["Num 5,18; Lev 24,14"] },
      { number: 35, text: "Sie aber blickte weinend zum Himmel auf; denn ihr Herz vertraute dem Herrn." },
      { number: 36, text: "Die Ältesten sagten: Während wir allein im Garten spazieren gingen, kam diese Frau mit zwei Mägden herein. Sie ließ das Gartentor verriegeln und schickte die Mägde fort." },
      { number: 37, text: "Dann kam ein junger Mann zu ihr, der sich versteckt hatte, und legte sich zu ihr." },
      { number: 38, text: "Wir waren gerade in einer abgelegenen Ecke des Gartens; als wir aber die Sünde sahen, eilten wir zu ihnen hin" },
      { number: 39, text: "und sahen, wie sie zusammen waren. Den Mann konnten wir nicht festhalten; denn er war stärker als wir; er öffnete das Tor und entkam." },
      { number: 40, text: "Aber diese da hielten wir fest und fragten sie, wer der junge Mann war." },
      { number: 41, text: "Sie wollte es uns aber nicht verraten. Das alles können wir bezeugen. Die versammelte Gemeinde glaubte ihnen, weil sie Älteste des Volkes und Richter waren, und verurteilte Susanna zum Tod." },
      { number: 42, text: "Da rief sie laut: Ewiger Gott, du kennst auch das Verborgene; du weißt alles, noch bevor es geschieht.", footnotes: ["2,20-22; Hebr 4,13"] },
      { number: 43, text: "Du weißt auch, dass sie eine falsche Aussage gegen mich gemacht haben. Darum muss ich jetzt sterben, obwohl ich nichts von dem getan habe, was diese Menschen mir vorwerfen." },
      { number: 44, text: "Der Herr erhörte ihr Rufen." },
      { number: 45, text: "Als man sie zur Hinrichtung führte, erweckte Gott den heiligen Geist in einem jungen Mann namens Daniel.", footnotes: ["4,5; 5,14"] },
      { number: 46, text: "Dieser rief laut: Ich bin unschuldig am Tod dieser Frau." },
      { number: 47, text: "Da wandten sich alle Leute nach ihm um und fragten ihn: Was soll das heißen, was du da gesagt hast?" },
      { number: 48, text: "Er trat mitten unter sie und sagte: Seid ihr so töricht, ihr Söhne Israels? Ohne Verhör und ohne Prüfung der Beweise habt ihr eine Tochter Israels verurteilt." },
      { number: 49, text: "Kehrt zurück zum Ort des Gerichts! Denn diese Ältesten haben eine falsche Aussage gegen Susanna gemacht." },
      { number: 50, text: "Eilig kehrten alle Leute wieder um und die Ältesten sagten zu Daniel: Setz dich hier mitten unter uns und sag uns, was du zu sagen hast. Denn dir hat Gott den Vorsitz verliehen." },
      { number: 51, text: "Daniel sagte zu ihnen: Trennt diese beiden Männer, bringt sie weit auseinander! Ich will sie verhören." },
      { number: 52, text: "Als man sie voneinander getrennt hatte, rief er den einen von ihnen her und sagte zu ihm: In Schlechtigkeit bist du alt geworden; doch jetzt kommt die Strafe für die Sünden, die du bisher begangen hast.", footnotes: ["Weish 4,8f"] },
      { number: 53, text: "Ungerechte Urteile hast du gefällt, Schuldlose verurteilt, aber Schuldige freigesprochen; und doch hat der Herr gesagt: Einen Schuldlosen und Gerechten sollst du nicht töten.", footnotes: ["Ex 23,7"] },
      { number: 54, text: "Wenn du also diese Frau wirklich gesehen hast, dann sag uns: Was für ein Baum war das, unter dem du die beiden zusammen gesehen hast? Er antwortete: Unter einer Zeder.", footnotes: ["54-59: Wortspiel mit «Zeder» (schinos) und «spalten» (schizein) sowie mit «Eiche» (prinos) und «entzweihauen» (prisai)."] },
      { number: 55, text: "Da sagte Daniel: Mit deiner Lüge hast du dein eigenes Haupt getroffen. Der Engel Gottes wird dich zerspalten; schon hat er von Gott den Befehl dazu erhalten." },
      { number: 56, text: "Dann ließ er ihn wegbringen und befahl, den andern vorzuführen. Zu ihm sagte er: Du Sohn Kanaans, nicht Judas, dich hat die Schönheit verführt, die Leidenschaft hat dein Herz verdorben.", footnotes: ["Ez 16,3.44"] },
      { number: 57, text: "So konntet ihr an den Töchtern Israels handeln, sie fürchteten sich und waren euch zu Willen. Aber die Tochter Judas hat eure Gemeinheit nicht geduldet." },
      { number: 58, text: "Nun sag mir: Was für ein Baum war das, unter dem du die beiden ertappt hast? Er antwortete: Unter einer Eiche." },
      { number: 59, text: "Da sagte Daniel zu ihm: Mit deiner Lüge hast auch du dein eigenes Haupt getroffen. Der Engel Gottes wartet schon mit dem Schwert in der Hand, um dich mitten entzweizuhauen. So wird er euch beide vernichten." },
      { number: 60, text: "Da schrie die ganze Gemeinde laut auf und pries Gott, der alle rettet, die auf ihn hoffen." },
      { number: 61, text: "Dann erhoben sie sich gegen die beiden Ältesten, die Daniel durch ihre eigenen Worte als falsche Zeugen entlarvt hatte. Das Böse, das sie ihrem Nächsten hatten antun wollen, tat man", footnotes: ["(61-62) Dtn 19,16-21"] },
      { number: 62, text: "nach dem Gesetz des Mose ihnen an: Man tötete sie. So wurde an jenem Tag unschuldiges Blut gerettet." },
      { number: 63, text: "Hilkija und seine Frau priesen Gott wegen ihrer Tochter Susanna, ebenso ihr Mann Jojakim und alle Verwandten, weil sich zeigte, dass sie nichts Schändliches getan hatte." },
      { number: 64, text: "Daniel aber gewann seit jenem Tag und auch weiterhin beim Volk großes Ansehen." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Der König Astyages ging zu seinen Vätern heim und sein Reich übernahm der Perser Kyrus.", heading: "Daniel und die Priester des Bel: 14,1-22", footnotes: ["6,29", "Vg zählt 14,1 als 13,65; entsprechend verschiebt sich die Verszählung in Kap. 14."] },
      { number: 2, text: "Daniel war der Vertraute des Königs, der ihn höher schätzte als alle seine Freunde." },
      { number: 3, text: "Nun hatten die Babylonier ein Bild des Gottes Bel. Sie wendeten für ihn täglich zwölf Scheffel Feinmehl auf, dazu vierzig Schafe und sechs Krüge Wein." },
      { number: 4, text: "Auch der König verehrte ihn und kam jeden Tag, um ihn anzubeten. Daniel aber betete seinen eigenen Gott an. Der König sagte zu ihm: Warum betest du Bel nicht an?" },
      { number: 5, text: "Er erwiderte: Ich verehre keine Standbilder, die von Menschen gemacht worden sind, sondern nur den lebendigen Gott, der den Himmel und die Erde erschaffen hat und die Herrschaft besitzt über alles, was lebt.", footnotes: ["Gen 14,19"] },
      { number: 6, text: "Der König entgegnete ihm: Du meinst also, Bel sei kein lebendiger Gott? Siehst du nicht, welche Mengen er Tag für Tag isst und trinkt?" },
      { number: 7, text: "Da lachte Daniel und sagte: Lass dich nicht täuschen, König! Dieser Bel ist innen von Lehm und außen von Bronze; er hat niemals gegessen oder getrunken.", footnotes: ["Jes 44,12; Bar 6,50"] },
      { number: 8, text: "Da wurde der König zornig; er rief die Priester des Bel herbei und sagte zu ihnen: Wenn ihr mir nicht sagt, wer all diese Mengen verzehrt, müsst ihr sterben.", footnotes: ["2,5"] },
      { number: 9, text: "Beweist ihr aber, dass Bel das alles verzehrt, dann muss Daniel sterben, weil er über Bel gelästert hat. Daniel sagte zum König: Es soll geschehen, was du gesagt hast." },
      { number: 10, text: "Es waren aber siebzig Belpriester, nicht gerechnet die Frauen und Kinder. Der König ging nun mit Daniel in den Tempel Bels." },
      { number: 11, text: "Die Belpriester sagten: Wir gehen jetzt hinaus. Trag du, König, selbst die Speisen auf, mische den Wein und stell ihn hin! Verschließ dann die Tür und versiegle sie mit deinem Ring!" },
      { number: 12, text: "Wenn du morgen früh wiederkommst und dich nicht davon überzeugen kannst, dass Bel alles verzehrt hat, dann wollen wir sterben, andernfalls aber Daniel, der uns verleumdet hat." },
      { number: 13, text: "Sie waren unbesorgt; denn sie hatten sich zum Opfertisch einen verborgenen Zugang gemacht, durch den sie jeweils hereinkamen, um alles zu verzehren." },
      { number: 14, text: "Als die Priester hinausgegangen waren, trug der König die Speisen für Bel auf. Daniel aber ließ durch seine Diener Asche holen und damit den ganzen Boden des Tempels bestreuen, wobei nur der König zusah. Dann gingen sie hinaus, verschlossen die Tür, versiegelten sie mit dem Ring des Königs und entfernten sich." },
      { number: 15, text: "In der Nacht kamen wie gewöhnlich die Priester mit ihren Frauen und Kindern; sie aßen alles auf und tranken den Wein." },
      { number: 16, text: "Früh am Morgen aber ging der König mit Daniel zum Tempel.", footnotes: ["6,20"] },
      { number: 17, text: "Der König fragte: Sind die Siegel unversehrt, Daniel? Er antwortete: Sie sind unversehrt, mein König." },
      { number: 18, text: "Kaum war das Portal geöffnet, da blickte der König auf den Opfertisch und rief laut: Groß bist du, Bel! Bei dir gibt es nie einen Betrug." },
      { number: 19, text: "Doch Daniel lachte; er hinderte den König, das Innere zu betreten und sagte: Sieh dir doch den Fußboden an und prüfe, von wem diese Fußspuren sind." },
      { number: 20, text: "Der König sagte: Ich sehe Fußspuren von Männern, Frauen und Kindern." },
      { number: 21, text: "Und er wurde zornig und ließ die Priester mit ihren Frauen und Kindern festnehmen. Sie mussten ihm die geheime Tür zeigen, durch die sie hereingekommen waren, um das, was auf dem Tisch stand, zu verzehren." },
      { number: 22, text: "Darauf ließ sie der König töten. Den Bel aber übergab er Daniel, der ihn und sein Heiligtum zerstörte.", footnotes: ["6,25; Jer 51,44"] },
      { number: 23, text: "Es gab auch einen großen Drachen, den die Babylonier wie einen Gott verehrten.", heading: "Daniel und der Drache: 14,23-42", footnotes: ["Weish 15,18f; Röm 1,23"] },
      { number: 24, text: "Der König sagte zu Daniel: Von diesem Drachen kannst du nicht sagen, er sei kein lebendiger Gott. Bete ihn also an!" },
      { number: 25, text: "Daniel erwiderte: Nur den Herrn, meinen Gott, bete ich an; denn er ist wirklich ein lebendiger Gott." },
      { number: 26, text: "Du aber, König, gib mir die Erlaubnis, den Drachen zu töten, ohne Schwert und Keule! Der König sagte: Ich gebe sie dir." },
      { number: 27, text: "Da nahm Daniel Pech, Talg und Haare, schmolz alles zusammen, formte Kuchen daraus und warf sie dem Drachen ins Maul. Der Drache fraß sie und zerbarst. Da sagte Daniel: Seht, was ihr für Götter verehrt!" },
      { number: 28, text: "Als die Babylonier davon hörten, waren sie empört und taten sich gegen den König zusammen. Sie sagten: Der König ist Jude geworden. Den Bel hat er zertrümmert, den Drachen getötet und die Priester hingeschlachtet." },
      { number: 29, text: "Sie gingen zum König und verlangten: Liefere uns Daniel aus! Sonst töten wir dich und deine Familie." },
      { number: 30, text: "Da sich der König aufs Äußerste bedroht sah, lieferte er ihnen Daniel notgedrungen aus." },
      { number: 31, text: "Sie aber warfen ihn in die Löwengrube. Dort blieb er sechs Tage lang.", footnotes: ["6,17"] },
      { number: 32, text: "In der Grube waren sieben Löwen; man gab ihnen täglich zwei Menschen und zwei Schafe zu fressen. Jetzt aber gab man ihnen nichts, damit sie Daniel fressen sollten." },
      { number: 33, text: "In Judäa lebte damals der Prophet Habakuk. Er hatte sich eine Mahlzeit gekocht und Brot in einen Napf gebrockt und ging gerade auf das Feld, um das Essen den Arbeitern zu bringen." },
      { number: 34, text: "Da sagte der Engel des Herrn zu Habakuk: Bring das Essen, das du in der Hand hast, dem Daniel nach Babylon in die Löwengrube!" },
      { number: 35, text: "Habakuk antwortete: Herr, ich habe Babylon nie gesehen und die Grube kenne ich nicht." },
      { number: 36, text: "Da fasste ihn der Engel des Herrn am Schopf, trug ihn an seinen Haaren fort und versetzte ihn mit der Gewalt seines Geistes nach Babylon an den Rand der Grube." },
      { number: 37, text: "Habakuk rief: Daniel, Daniel, nimm das Essen, das Gott dir geschickt hat." },
      { number: 38, text: "Da sagte Daniel: Gott, du hast also an mich gedacht; du lässt die nicht im Stich, die dich lieben." },
      { number: 39, text: "Dann stand Daniel auf und aß. Den Habakuk aber versetzte der Engel Gottes sogleich an seinen früheren Ort zurück." },
      { number: 40, text: "Am siebten Tag kam der König, um Daniel zu betrauern. Er trat an die Grube und schaute hinein. Da sah er Daniel sitzen", footnotes: ["6,21"] },
      { number: 41, text: "und er rief laut: Groß bist du, Herr, du Gott Daniels. Außer dir gibt es keinen anderen Gott." },
      { number: 42, text: "Dann ließ er Daniel herausziehen und statt seiner die Männer in die Grube werfen, die ihn hatten vernichten wollen. Und vor seinen Augen wurden sie sofort aufgefressen.", footnotes: ["6,25"] },
    ] },
  ],
};
