import { Book } from "@/lib/types";

export const deuteronomy: Book = {
  id: "deuteronomy",
  name: "Deuteronomium",
  shortName: "Dtn",
  testament: "old",
  introduction: "Das Buch Deuteronomium erzählt von den letzten Lebenstagen des Mose und schließt den Pentateuch ab. Vor seinem Tod verkündet Mose noch einmal das Gesetz vom Sinai. Daher trägt dieses Buch in der griechischen und in der lateinischen Bibel den Namen Deuteronomium (Zweites Gesetz; vgl. 17,18). Die Juden nennen dieses Buch Dewarim (Worte) nach den Anfangsworten Elle haddebarim (Das sind die Worte). Das Buch Deuteronomium enthält zum Teil sehr alte Texte. Doch in seine jetzige Gestalt wuchs es erst in der ausgehenden Königszeit, im Exil und nach dem Exil hinein. Indem Israel seine späteren Ordnungen und Einsichten bis in die Zeit des Mose zurückverlegt, bekennt es, dass es sich dabei um Gottes Weisung und Setzung handelt, an der man für immer festhalten soll. Die letzte Wurzel des deuteronomischen Gesetzes ist wahrscheinlich ein Bundestext aus der Richterzeit, der noch in Ex 34,10-26 erhalten ist. Er spiegelt eine Ordnung, in der Jahwe, der Gott Israels, selbst als Herrscher des Volkes galt. Nachdem Israel einen König erhalten hatte, konnte man zunächst mit solchen Vorstellungen nichts anfangen. Die Tradition wurde aber weitergepflegt und in der frühen Königszeit hatte das Bundesgesetz die Gestalt angenommen, die in Ex 20,22 - 23,33, dem sog. Bundesbuch, erhalten ist. Im ausgehenden 8. Jahrhundert scheint man in Juda bei religiösen Reformversuchen, erstmalig wohl unter König Hiskija, auf diese alten Traditionen zurückgegriffen zu haben. In den neu redigierten Text kamen die Gesetze über die Zentralisierung der Opfer, Abgaben und Wallfahrtsfeste an einem einzigen Heiligtum (Jerusalem; vgl. 2 Kön 18,22); irgendwann wurden Gesetze eingefügt, die am alten Text der Zehn Gebote orientiert waren; damals entstand auch die feierliche »deuteronomische Sprache«, die wohl für den öffentlichen Vortrag bei Bundesschluss oder Bundeserneuerung gedacht war. Im Jahr 622 v. Chr. verpflichtete König Joschija von Jerusalem sein Reich auf ein im Tempel gefundenes Gesetz (2 Kön 22f). Dieses dürfte eine Fassung des deuteronomischen Gesetzes gewesen sein, die mit Dtn 6,4 einsetzte und große Teile der jetzigen Kapitel 6 - 28 enthielt. Dieses Gesetz, das im Lauf der Zeit noch viele Erweiterungen erfuhr, war von da an bis zum Untergang Jerusalems (586 v. Chr.), im Exil und auch nach der Heimkehr die Lebensgrundlage des jüdischen Volkes, bis es durch das »Heiligkeitsgesetz« (Lev 19 - 26) ergänzt und dann zusammen mit vielen anderen gesetzlichen Materialien in der Gesetzgebung des Pentateuch zusammengefasst wurde. Noch zu Lebzeiten Joschijas wurde ein Geschichtswerk verfasst, das von Mose bis zu diesem König reichte. An seinen Anfang wurde das deuteronomische Gesetz gestellt, gewissermaßen als göttlicher Maßstab für die Geschichte des Volkes. Damals wurde es als Moserede stilisiert und mit anderen Reden des Mose umgeben, die zum Teil Rückblicke auf die Wüstenzeit Israels und Ausblicke auf die Geschichte Israels enthalten. Dieses »Deuteronomistische Geschichtswerk« wurde in und nach dem Babylonischen Exil noch mehrfach überarbeitet. Es umfasst jetzt die Bücher Dtn bis 2 Kön. Bei der Herstellung des Pentateuch in nachexilischer Zeit wurde das Buch Deuteronomium von diesem Geschichtswerk abgetrennt und durch einige weitere Texte, vor allem aus der »Priesterschrift«, mit den ersten vier Büchern des Pentateuch verklammert (vgl. die Einleitung zu den Fünf Büchern des Mose). Das Buch stellt sich jetzt als eine Sammlung von Mosereden dar, die durch ein Überschriftensystem in vier Teile gegliedert ist: 1,1 - 4,43 »Rede« des Mose (Rückblick und Mahnung); 4,44 - 28,68 »Weisung« des Mose (Verkündigung des Gesetzes); 28,69 - 32,52 die »Worte, mit denen der Bund geschlossen wurde« (letzte Verfügungen des Mose); 33,1-29 »Segen« des Mose (mit angehängtem Bericht über seinen Tod und sein Begräbnis 34,1-12). Das Moselied (32,1-43) und der Mosesegen (33,2-29) sind alte Dichtungen. Man kann das Deuteronomium als Zeugnis der ersten großen theologischen Synthese in Israel betrachten. Hier werden zum ersten Mal unter den Leitgedanken der Verpflichtung Israels auf den ausschließlichen Dienst für seinen Gott Jahwe und des Verhältnisses Gott-Volk nach Art eines Lehnsverhältnisses zwischen einem Herrn und denen, die sich ihm durch einen Vertrag anvertraut haben (»Bund«), die verschiedensten Traditionen zu einer Einheit zusammengefasst. Für uns sind viele Institutionen, die sich in den Gesetzen spiegeln, Vergangenheit. Doch zeigt dieses alle Lebensbereiche in das Gottesverhältnis hineinziehende Buch, dass Gott sich ein Volk schaffen will, als Zeichen unter den Völkern und als Zeuge seiner Herrschaft.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Das sind die Worte, die Mose vor ganz Israel gesprochen hat. Er sprach sie jenseits des Jordan, in der Wüste, in der Araba, östlich von Suf, zwischen Paran und Tofel, Laban, Hazerot und Di-Sahab.", heading: "Die Rede des Mose: Rückblick und Mahnung: 1,1 - 4,43 | Ort und Zeit: 1,1-5", footnotes: ["1-5: Vorbemerkung zur Rede des Mose 1,6 - 4,40. Die Orts- und Zeitangaben gelten auch für die weiteren Mosereden, vor allem auch für die in 1,5 ausdrücklich genannte «Weisung» 5,1 - 28,68. Die Ortsund Entfernungsangaben sind spätere ergänzende Zusätze, wie sie sich noch mehrfach in Kap. 1 - 4 finden."] },
      { number: 2, text: "Elf Tage sind es vom Horeb auf dem Weg zum Gebirge Seïr bis nach Kadesch-Barnea." },
      { number: 3, text: "Es war im vierzigsten Jahr, im elften Monat, am ersten Tag des Monats. Mose sagte den Israeliten genau das, was ihm der Herr für sie aufgetragen hatte." },
      { number: 4, text: "Nachdem er Sihon, den König der Amoriter, der in Heschbon seinen Sitz hatte, und Og, den König des Baschan, der in Aschtarot seinen Sitz hatte, bei Edreï geschlagen hatte,", footnotes: ["2,24 - 3,11"] },
      { number: 5, text: "begann Mose jenseits des Jordan im Land Moab, diese Weisung aufzuschreiben. Er sagte:" },
      { number: 6, text: "Der Herr, unser Gott, hat am Horeb zu uns gesagt: Ihr habt euch lange genug an diesem Berg aufgehalten.", heading: "Der Aufbruch vom Horeb: 1,6-18", footnotes: ["(6-8) Ex 33,1", "Horeb ist in Dtn der Name des Sinai."] },
      { number: 7, text: "Nun wendet euch dem Bergland der Amoriter zu, brecht auf und zieht hinauf! Zieht aus gegen alle seine Bewohner in der Araba, auf dem Gebirge, in der Schefela, im Negeb und an der Meeresküste! Zieht in das Land der Kanaaniter und in das Gebiet des Libanon, bis an den großen Strom, den Eufrat!", footnotes: ["19,8", "«Bergland der Amoriter» ist in Dtn der Name für das verheißene Land westlich des Jordan. Es wird hier näher umschrieben und in einer Texterweiterung bis zum Eufrat hin ausgedehnt, ähnlich wie in Dtn 11,24 und Jos 1,4. Das entspricht dem Umfang des davidischen Reichs."] },
      { number: 8, text: "Hiermit liefere ich euch das Land aus. Zieht hinein und nehmt es in Besitz, das Land, von dem ihr wisst: Der Herr hat euren Vätern Abraham, Isaak und Jakob geschworen, es ihnen und später ihren Nachkommen zu geben.", footnotes: ["von dem ihr wisst: Die Wendung ist hier und an anderen Stellen eingefügt, weil man die komplizierte hebräische Satzkonstruktion im Deutschen so am besten wiedergeben kann."] },
      { number: 9, text: "Damals habe ich zu euch gesagt: Ich kann euch nicht allein tragen.", footnotes: ["(9-18) Ex 18,13-27; Num 11,11-17", "9-18: Wahrscheinlich ein Zusatz."] },
      { number: 10, text: "Der Herr, euer Gott, hat euch zahlreich gemacht. Ja, ihr seid heute schon so zahlreich wie die Sterne am Himmel.", footnotes: ["Hebr 11,12"] },
      { number: 11, text: "Und der Herr, der Gott eurer Väter, lasse eure Zahl auf das Tausendfache wachsen und segne euch, wie er es euch versprochen hat." },
      { number: 12, text: "Wie soll ich allein euch tragen: eure Bürde, eure Last, eure Rechtshändel?" },
      { number: 13, text: "Schlagt für jeden eurer Stämme weise, gebildete und bewährte Männer vor, damit ich sie als eure Führer einsetze.", footnotes: ["1,15; 5,23; 29,9; 33,5"] },
      { number: 14, text: "Ihr habt mir geantwortet: Das ist ein guter Vorschlag. Führ ihn aus!" },
      { number: 15, text: "Also habe ich die Führer eurer Stämme, weise und bewährte Männer, genommen und sie zu euren Führern ernannt: als Anführer für je tausend, Anführer für je hundert, Anführer für je fünfzig, Anführer für je zehn, und als Listenführer, für jeden eurer Stämme.", footnotes: ["1,13; 16,18"] },
      { number: 16, text: "Damals habe ich eure Richter verpflichtet: Lasst jeden Streit zwischen euren Brüdern vor euch kommen. Entscheidet gerecht, sei es der Streit eines Mannes mit einem Bruder oder mit einem Fremden.", footnotes: ["(16-17) 16,18-20; 17,8-13; 2 Chr 19,5-11; Joh 7,51", "Im Dtn werden alle Angehörigen des eigenen Volkes als «Brüder» betrachtet. «Fremder» meint hier und häufig in Dtn den Gastbürger mit eingeschränkten Rechten."] },
      { number: 17, text: "Kennt vor Gericht kein Ansehen der Person! Klein wie Groß hört an! Fürchtet euch nicht vor angesehenen Leuten; denn das Gericht hat mit Gott zu tun. Und ist euch eine Sache zu schwierig, legt sie mir vor; dann werde ich sie anhören.", footnotes: ["Jak 2,9", "Andere Übersetzungsmöglichkeit: denn das Gericht kommt von Gott."] },
      { number: 18, text: "Damals habe ich euch alle Vorschriften gegeben, nach denen ihr handeln sollt.", footnotes: ["Ex 24,3-8"] },
      { number: 19, text: "Dann brachen wir auf, verließen den Horeb und wanderten durch diese ganze große und Furcht erregende Wüste - ihr habt sie erlebt - auf dem Weg zum Amoriterbergland, wie es uns der Herr, unser Gott, befohlen hatte. Wir kamen bis Kadesch-Barnea.", heading: "Israels Unglaube in Kadesch-Barnea: 1,19-46", footnotes: ["29,1; (19-46) 9,23; 13-14", "19-45: Der wegen des Unglaubens Israels misslungene Versuch, das verheißene Land von Süden aus zu erobern, ist nach Num 10,11 ins zweite Jahr des Wüstenzugs zu setzen."] },
      { number: 20, text: "Dort sagte ich zu euch: Nun seid ihr am Bergland der Amoriter angekommen, das der Herr, unser Gott, uns gibt." },
      { number: 21, text: "Sieh, der Herr, dein Gott, hat dir das Land ausgeliefert. Zieh hinauf und nimm es in Besitz, wie der Herr, der Gott deiner Väter, es dir befohlen hat. Fürchte dich nicht und hab keine Angst!" },
      { number: 22, text: "Da seid ihr zu mir gekommen, ihr alle, und habt gesagt: Wir wollen einige Männer vorausschicken. Sie sollen uns das Land auskundschaften und uns Bericht erstatten über den Weg, den wir hinaufziehen, und über die Städte, auf die wir treffen werden." },
      { number: 23, text: "Der Vorschlag erschien mir gut. Ich wählte unter euch zwölf Männer aus, für jeden Stamm einen." },
      { number: 24, text: "Sie wendeten sich dem Bergland zu, zogen hinauf, gelangten bis zum Traubental und erkundeten es.", footnotes: ["Das Traubental liegt bei Hebron."] },
      { number: 25, text: "Sie pflückten einige von den Früchten des Landes, brachten sie zu uns herab und erstatteten uns Bericht. Sie sagten: Das Land, das der Herr, unser Gott, uns gibt, ist prächtig." },
      { number: 26, text: "Doch ihr habt euch geweigert hinaufzuziehen. Ihr habt euch dem Befehl des Herrn, eures Gottes, widersetzt" },
      { number: 27, text: "und habt in euren Zelten gemurrt. Ihr habt gesagt: Weil er uns hasst, hat der Herr uns aus Ägypten geführt. Er will uns in die Gewalt der Amoriter geben, um uns zu vernichten." },
      { number: 28, text: "Wohin geraten wir, wenn wir hinaufziehen? Unsere Brüder haben uns den Mut genommen, als sie berichteten: Ein Volk, größer und zahlreicher als wir, Städte, groß, mit himmelhohen Mauern. Sogar Anakiter haben wir dort gesehen.", footnotes: ["9,1f", "Andere Lesart: größer und höher gewachsen als wir. Die Anakiter sind eine auch aus ägyptischen Texten bekannte Gruppe aus der Urbevölkerung des Landes. Sie lebten im Gebiet von Hebron."] },
      { number: 29, text: "Da habe ich zu euch gesagt: Ihr dürft nicht vor ihnen zurückweichen und dürft euch nicht vor ihnen fürchten.", footnotes: ["(29-31) 20,2-4"] },
      { number: 30, text: "Der Herr, euer Gott, der euch vorangeht, wird für euch kämpfen, genau so, wie er vor euren Augen in Ägypten auf eurer Seite gekämpft hat." },
      { number: 31, text: "Das Gleiche tat er in der Wüste, du hast es selbst erlebt. Da hat der Herr, dein Gott, dich auf dem ganzen Weg, den ihr gewandert seid, getragen, wie ein Vater seinen Sohn trägt, bis ihr an diesen Ort kamt.", footnotes: ["29,1; Apg 13,18"] },
      { number: 32, text: "Trotzdem habt ihr nicht an den Herrn, euren Gott, geglaubt," },
      { number: 33, text: "der euch auf dem Weg vorangegangen war, um euch die Stelle für das Lager zu suchen. Bei Nacht ging er im Feuer voran, um euch den Weg zu zeigen, auf dem ihr gehen solltet, bei Tag in der Wolke.", footnotes: ["Ex 13,21f"] },
      { number: 34, text: "Der Herr hörte euer lautes Murren, wurde unwillig und schwor:" },
      { number: 35, text: "Kein einziger von diesen Männern, von dieser verdorbenen Generation, soll das prächtige Land sehen, von dem ihr wisst: Ich habe geschworen, es euren Vätern zu geben." },
      { number: 36, text: "Nur Kaleb, der Sohn Jefunnes, wird es sehen. Ihm und seinen Söhnen werde ich das Land geben, das er betreten hat. Denn er ist dem Herrn ganz und gar nachgefolgt.", footnotes: ["Jos 14,6-15"] },
      { number: 37, text: "Auch mir grollte der Herr euretwegen und sagte: Auch du sollst nicht in das Land hineinkommen.", footnotes: ["3,23-27; 4,21f; 31,2", "Mose wird der Zugang zum verheißenen Land wegen seiner Verantwortung für das Volk verweigert (vgl. 3,26). Anders in Num 20,12 (P); vgl. Dtn 32,51."] },
      { number: 38, text: "Josua, der Sohn Nuns, dein Gehilfe, wird hineinkommen. Verleih ihm Macht: Er soll das Land an Israel als Erbbesitz verteilen.", footnotes: ["3,28; 31,6-8.23; Jos 1,2-9; 13,1-7", "Macht verleihen: Ritus der Amtseinsetzung."] },
      { number: 39, text: "Und eure Kleinen, von denen ihr sagt: Zur Beute werden sie!, und eure Kinder, die heute noch nichts von Gut und Böse wissen, sie werden in das Land hineinkommen. Ihnen gebe ich es und sie nehmen es dann auch in Besitz." },
      { number: 40, text: "Ihr aber, wendet euch zur Wüste, brecht auf und nehmt den Weg zum Schilfmeer!" },
      { number: 41, text: "Ihr habt mir darauf erwidert: Wir haben vor dem Herrn gesündigt. Doch jetzt wollen wir hinaufziehen und kämpfen, genau so, wie es uns der Herr, unser Gott, befohlen hat. Und jeder legte die Waffen an und gürtete sich, um ins Bergland zu ziehen.", footnotes: ["Andere Übersetzungsmöglichkeit: Jeder legte die Waffen an und zog leichten Sinnes ins Bergland hinauf."] },
      { number: 42, text: "Doch der Herr sprach zu mir: Sag ihnen: Ihr sollt nicht hinaufziehen und nicht kämpfen; denn ich bin nicht in eurer Mitte. Ich will nicht, dass eure Feinde euch niederstoßen." },
      { number: 43, text: "Ich habe euch zugeredet, doch ihr habt nicht gehört. Ihr habt euch dem Befehl des Herrn widersetzt. In eurer Vermessenheit seid ihr ins Bergland gezogen." },
      { number: 44, text: "Da rückten die Amoriter, die dort im Bergland wohnen, gegen euch aus. Sie verfolgten euch wie ein Bienenschwarm und versprengten euch in Seïr bis nach Horma hin." },
      { number: 45, text: "Als ihr zurückkamt, weintet ihr vor dem Herrn. Doch er hat auf eure Klagen nicht gehört und hatte kein Ohr mehr für euch." },
      { number: 46, text: "Dann hieltet ihr euch lange in Kadesch auf - die ganze Zeit, die ihr dort geblieben seid.", footnotes: ["Num 20,1", "Die umständliche und zugleich unbestimmte Zeitangabe überspielt die Theorie des Buchs Num, nach der die Israeliten 38 Jahre in Kadesch waren. Nach Dtn 2,1.14 sind die Israeliten bald wieder aufgebrochen und 38 Jahre lang in der Wüste umhergezogen."] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Dann wendeten wir uns der Wüste zu, brachen auf und nahmen den Weg zum Schilfmeer, wie es der Herr mir befohlen hatte. Wir umzogen lange Zeit das Gebirge Seïr.", heading: "Der Zug ins Ostjordanland: 2,1-13", footnotes: ["(1-25) Num 21,4-20"] },
      { number: 2, text: "Dann sagte der Herr zu mir:", footnotes: ["(2-8) Num 20,14-21"] },
      { number: 3, text: "Ihr seid jetzt lange genug um dieses Gebirge herumgezogen. Wendet euch jetzt nach Norden!" },
      { number: 4, text: "Befiehl dem Volk: Ihr werdet jetzt durch das Gebiet von Stammverwandten ziehen, durch das Gebiet der Nachkommen Esaus, die in Seïr wohnen. Wenn sie Furcht vor euch zeigen, dann seid auf der Hut," },
      { number: 5, text: "und beginnt keine Feindseligkeiten gegen sie! Von ihrem Land gebe ich euch keinen Fußbreit; denn das Gebirge Seïr habe ich für Esau zum Besitz bestimmt.", footnotes: ["Apg 7,5"] },
      { number: 6, text: "Was ihr an Getreide zum Essen braucht, kauft von ihnen für Silber; selbst das Trinkwasser beschafft euch von ihnen gegen Silber!" },
      { number: 7, text: "Der Herr, dein Gott, hatte dich reich gesegnet bei der Arbeit deiner Hände. Er wusste, dass du in dieser großen Wüste unterwegs warst. Vierzig Jahre lang war der Herr, dein Gott, bei dir. Nichts hat dir gefehlt.", footnotes: ["8,4; 28,12; 29,4"] },
      { number: 8, text: "Wir zogen also weg aus dem Gebiet in der Nähe der Söhne Esaus, unserer Stammverwandten, die in Seïr wohnen, weg vom Weg durch die Araba, von Elat und Ezjon-Geber. Wir wendeten uns (nach Norden) und zogen den Weg zur Wüste Moab entlang." },
      { number: 9, text: "Und der Herr sagte zu mir: Begegne Moab nicht feindlich, beginn keinen Kampf mit ihnen! Von ihrem Land bestimme ich dir kein Stück zum Besitz; denn Ar habe ich für die Nachkommen Lots zum Besitz bestimmt." },
      { number: 10, text: "Einst saßen dort die Emiter, ein Volk, das groß, zahlreich und hoch gewachsen war wie die Anakiter.", footnotes: ["10-12: Erklärender Zusatz (vgl. die Anmerkung zu 1,1-5)."] },
      { number: 11, text: "Wie die Anakiter galten auch sie als Rafaïter, die Moabiter aber nennen sie Emiter." },
      { number: 12, text: "In Seïr saßen einst die Horiter, aber deren Besitz haben die Nachkommen Esaus übernommen. Als sie vordrangen, vernichteten sie die Horiter und setzten sich an deren Stelle, so wie die Israeliten es mit dem Land taten, das ihnen der Herr zum Besitz bestimmt hatte." },
      { number: 13, text: "Und jetzt steht auf und überquert das Tal des Sered! Da überquerten wir das Tal des Sered." },
      { number: 14, text: "Die Zeit, die wir von Kadesch-Barnea an gewandert waren, bis wir das Tal des Sered überquerten, betrug achtunddreißig Jahre. So lange dauerte es, bis die Generation der waffenfähigen Männer vollständig ausgestorben war, sodass sich keiner von ihnen mehr im Lager befand, wie es ihnen der Herr geschworen hatte.", heading: "Die Eroberung des Ostjordanlandes: 2,14 - 3,17" },
      { number: 15, text: "Der Herr hatte seine Hand gegen sie ausgestreckt und Verwirrung unter ihnen verbreitet, bis sie ausgestorben waren, sodass sich keiner von ihnen mehr im Lager befand.", footnotes: ["Ex 14,24"] },
      { number: 16, text: "Als alle waffenfähigen Männer ausgestorben und tot waren, sodass keiner von ihnen mehr im Volk lebte," },
      { number: 17, text: "sagte der Herr zu mir:" },
      { number: 18, text: "Wenn du heute durch das Gebiet von Moab, durch Ar, ziehst," },
      { number: 19, text: "kommst du nahe an den Ammonitern vorbei. Begegne ihnen nicht feindlich, beginne keine Feindseligkeiten gegen sie! Vom Land der Ammoniter bestimme ich dir kein Stück zum Besitz; denn ich habe es für die Nachkommen Lots zum Besitz bestimmt." },
      { number: 20, text: "Auch dieses gilt als Land der Rafaïter. Einst saßen die Rafaïter darin. Die Ammoniter nennen sie die Samsummiter,", footnotes: ["20-23: Erklärender Zusatz (vgl. die Anmerkung zu 1,1-5)."] },
      { number: 21, text: "ein Volk, das groß, zahlreich und hoch gewachsen war wie die Anakiter. Der Herr vernichtete die Rafaïter, als die Ammoniter eindrangen. Diese übernahmen ihren Besitz und setzten sich an ihre Stelle." },
      { number: 22, text: "Das war das Gleiche, was der Herr für die Nachkommen Esaus getan hat, die in Seïr sitzen. Als sie vordrangen, vernichtete er die Horiter. Die Nachkommen Esaus übernahmen ihren Besitz und setzten sich an ihre Stelle. So blieb es bis heute." },
      { number: 23, text: "Das Gleiche geschah mit den Awitern, die in einzelnen Dörfern bis nach Gaza hin saßen. Die Kaftoriter, die aus Kaftor ausgewandert waren, vernichteten sie und setzten sich an ihre Stelle." },
      { number: 24, text: "Steht auf, brecht auf und überquert das Tal des Arnon! Hiermit gebe ich Sihon, den König von Heschbon, den Amoriter, mit seinem Land in eure Gewalt. Fang an, in Besitz zu nehmen! Bei Sihon sollst du den Kampf beginnen." },
      { number: 25, text: "Und ich fange heute an, den Völkern überall unter dem Himmel Schrecken und Furcht vor dir ins Gesicht zu zeichnen. Wenn sie von dir nur hören, zittern sie. Sie winden sich vor Angst, wenn sie dich sehen.", footnotes: ["11,25; 28,10; Ex 15,14"] },
      { number: 26, text: "Da sandte ich aus der Wüste Kedemot Boten zu Sihon, dem König von Heschbon, und ließ ihm folgendes Abkommen vorschlagen:", footnotes: ["(26-37) Num 21,21-25"] },
      { number: 27, text: "Ich will durch dein Land ziehen. Ich werde mich genau an den Weg halten, ohne ihn rechts oder links zu verlassen." },
      { number: 28, text: "Was ich an Getreide zum Essen brauche, wirst du mir für Silber verkaufen, auch das Trinkwasser wirst du mir gegen Silber geben. Ich werde nur zu Fuß durchziehen," },
      { number: 29, text: "so wie die Nachkommen Esaus, die in Seïr wohnen, und die Moabiter, die in Ar wohnen, es mir erlaubt haben. (Das soll gelten,) bis ich über den Jordan in das Land gezogen bin, das der Herr, unser Gott, uns gibt." },
      { number: 30, text: "Doch Sihon, der König von Heschbon, weigerte sich, uns bei sich durchziehen zu lassen. Denn der Herr, dein Gott, hatte seinen Kampfgeist gestärkt und ihm Mut gemacht, um ihn in deine Gewalt zu geben, wie es inzwischen geschehen ist." },
      { number: 31, text: "Zu mir aber sagte der Herr: Hiermit fange ich an. Ich liefere dir Sihon und sein Land aus. Du fang an, in Besitz zu nehmen! Fang mit seinem Land an!" },
      { number: 32, text: "Sihon rückte mit seinem ganzen Volk gegen uns aus, um bei Jahaz zu kämpfen." },
      { number: 33, text: "Der Herr, unser Gott, lieferte ihn uns aus. Wir schlugen ihn, seine Söhne und sein ganzes Volk." },
      { number: 34, text: "Damals eroberten wir alle seine Städte. Wir weihten die ganze männliche Bevölkerung, die Frauen, die Kinder und die Greise der Vernichtung; keinen ließen wir überleben.", footnotes: ["20,16-18", "weihten der Vernichtung: In älteren Bibelübersetzungen stand hier und an ähnlichen Stellen das Wort «Bann» oder «bannen», das aber missverständlich ist. Gemeint ist die Vernichtung der Bevölkerung, und es ist dabei ein Wort benutzt, das in früheren Zeiten Vernichtung aufgrund von Gelübden oder Götterweisungen bezeichnete. Zur deuteronomischen Kriegstheorie vgl. 20,10-18."] },
      { number: 35, text: "Als Beute behielten wir nur das Vieh und das, was wir in den eroberten Städten geplündert hatten." },
      { number: 36, text: "Von Aroër am Rand des Arnontals und von der Stadt, die im Tal liegt, bis hin nach Gilead gab es keine befestigte Stadt, deren Mauern für uns zu hoch waren. Alle hat uns der Herr, unser Gott, ausgeliefert." },
      { number: 37, text: "Nur dem Land der Ammoniter hast du dich nicht genähert, dem gesamten Randgebiet des Jabboktals und den Städten im Gebirge, also allem, was der Herr, unser Gott, uns verwehrt hatte." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Dann wendeten wir uns dem Weg zum Baschan zu und zogen hinauf. Og, der König des Baschan, rückte mit seinem ganzen Volk gegen uns aus, um bei Edreï zu kämpfen.", footnotes: ["(1-7) Num 21,33-35"] },
      { number: 2, text: "Der Herr sagte zu mir: Fürchte ihn nicht, denn ich gebe ihn, sein ganzes Volk und sein Land in deine Gewalt. Tu mit ihm, was du mit Sihon getan hast, dem König der Amoriter, der in Heschbon seinen Sitz hatte." },
      { number: 3, text: "Und der Herr, unser Gott, gab auch Og, den König des Baschan, und sein ganzes Volk in unsere Gewalt. Wir schlugen ihn und ließen keinen überleben." },
      { number: 4, text: "Damals eroberten wir alle seine Städte. Es gab keine befestigte Stadt, die wir ihnen nicht genommen hätten: sechzig Städte, den ganzen Bezirk von Argob, das Königreich des Og im Baschan." },
      { number: 5, text: "Alle diese Städte waren durch hohe Mauern, Torflügel und Torbalken befestigt. Hinzu kamen die zahlreichen offenen Landstädte." },
      { number: 6, text: "Wir weihten sie der Vernichtung, wie wir es mit Sihon, dem König von Heschbon, getan hatten. Wir weihten die ganze männliche Bevölkerung und die Frauen, Kinder und Greise der Vernichtung.", footnotes: ["20,16-18"] },
      { number: 7, text: "Alles Vieh und das, was wir in den Städten geplündert hatten, behielten wir als Beute." },
      { number: 8, text: "Damals entrissen wir der Gewalt der beiden Amoriterkönige jenseits des Jordan das Land vom Arnontal bis zum Gebirge Hermon -" },
      { number: 9, text: "die Sidonier nennen den Hermon Sirjon, die Amoriter nennen ihn Senir -,", footnotes: ["9.13f: Erklärender Zusatz (vgl. die Anmerkung zu 1,1-5)."] },
      { number: 10, text: "alle Städte der Hochebene, ganz Gilead und den ganzen Baschan bis nach Salka und Edreï, das heißt nur die Städte des Königreichs Ogs im Baschan." },
      { number: 11, text: "Denn Og, der König des Baschan, war als Einziger von den letzten Rafaïtern noch übrig geblieben. Sein Bett war aus Eisen. Steht es nicht in Rabba, der Hauptstadt der Ammoniter? Es ist neun gewöhnliche Ellen lang und vier breit." },
      { number: 12, text: "Dieses Land nahmen wir damals in Besitz. Von Aroër an, das am Arnontal liegt, einschließlich der Hälfte des Gebirges Gilead und ihrer Städte, übergab ich es (euch,) den Rubenitern und Gaditern.", footnotes: ["(12-17) Num 32; Jos 13,15-33"] },
      { number: 13, text: "Den Rest von Gilead und den ganzen Baschan, soweit er zum Königreich Ogs gehört hatte, übergab ich (euch,) der einen Hälfte des Stammes Manasse - den ganzen Bezirk von Argob. Diesen ganzen Baschan bezeichnet man als Land der Rafaïter." },
      { number: 14, text: "Jaïr, der Sohn Manasses, übernahm den ganzen Bezirk von Argob einschließlich des Gebiets der Geschuriter und der Maachatiter und benannte sie nach seinem eigenen Namen: Er nannte den Baschan Hawot-Jaïr (Zeltdörfer Jaïrs). So heißt er noch heute.", footnotes: ["14-17: Wohl spätere Variante zu V. 12f."] },
      { number: 15, text: "Dem Machir übergab ich Gilead," },
      { number: 16, text: "den Rubenitern und Gaditern aber gab ich das Gebiet von Gilead bis zum Arnontal, das Tal selbst und das zugehörige Gebiet bis zum Jabbok, wo das Tal die Grenze zu den Ammonitern bildet," },
      { number: 17, text: "dazu die Araba und den Jordan mit dem zugehörigen Gebiet, vom See Kinneret bis zum Meer der Araba, dem Salzmeer am Fuß der Steilhänge des Pisga, nach Osten hin." },
      { number: 18, text: "Damals wies ich euch an: Der Herr, euer Gott, hat euch dieses Land gegeben, sodass ihr es in Besitz nehmen könnt. Doch jetzt, in Waffen, sollt ihr zunächst mit allen Wehrfähigen an der Spitze eurer Brüder, der Israeliten, über den Jordan ziehen.", heading: "Die Vorbereitungen zur Eroberung des Westjordanlandes: 3,18-29", footnotes: ["(18-20) Num 32,16f; Jos 1,13"] },
      { number: 19, text: "Nur eure Frauen, Kinder, Greise und Herden - ich weiß, ihr habt viele Herden - können in den Städten bleiben, die ich für euch bestimmt habe." },
      { number: 20, text: "Erst wenn der Herr euren Brüdern wie euch Ruhe verschafft hat, erst wenn auch sie das Land jenseits des Jordan in Besitz genommen haben, das der Herr, euer Gott, ihnen gibt, dürft ihr zurückkehren, jeder zu seinem Besitz, den ich für euch bestimmt habe." },
      { number: 21, text: "Damals wies ich Josua an: Du hast mit eigenen Augen alles gesehen, was der Herr, euer Gott, mit diesen beiden Königen getan hat. Das Gleiche wird der Herr mit allen Königreichen tun, zu denen du hinüberziehst.", footnotes: ["(21-22) 20,2-4"] },
      { number: 22, text: "Ihr sollt sie nicht fürchten; denn der Herr, euer Gott, ist es, der für euch kämpft." },
      { number: 23, text: "Damals rief ich den Herrn um Gnade für mich an:", footnotes: ["(23-27) 1,37"] },
      { number: 24, text: "Gott, mein Herr! Du hast angefangen, deinen Knecht deine Macht und deine starke Hand schauen zu lassen. Welcher Gott im Himmel oder auf der Erde hat etwas vollbracht, was deinen Taten und deinen Siegen vergleichbar wäre?" },
      { number: 25, text: "Lass mich doch hinüberziehen! Lass mich das prächtige Land jenseits des Jordan sehen, dieses prächtige Bergland und den Libanon!" },
      { number: 26, text: "Doch euretwegen zürnte mir der Herr und erhörte mich nicht. Der Herr sagte zu mir: Genug! Trag mir diese Sache niemals wieder vor!" },
      { number: 27, text: "Steig auf den Gipfel des Pisga, richte die Augen nach Westen, nach Norden, nach Süden und nach Osten und schau mit eigenen Augen hinüber! Doch hinüberziehen über den Jordan hier wirst du nicht.", footnotes: ["34,1-3"] },
      { number: 28, text: "Setz Josua ein, verleih ihm Macht und Stärke: Er soll an der Spitze dieses Volkes hinüberziehen. Er soll an sie das Land als Erbbesitz verteilen, das du nur schauen darfst." },
      { number: 29, text: "So blieben wir in der Talschlucht gegenüber Bet-Pegor.", footnotes: ["Num 25", "Bet-Pegor ist der Ort, Baal-Pegor (4,3) der dort verehrte kanaanäische Gott."] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Und nun, Israel, höre die Gesetze und Rechtsvorschriften, die ich euch zu halten lehre. Hört und ihr werdet leben, ihr werdet in das Land, das der Herr, der Gott eurer Väter, euch gibt, hineinziehen und es in Besitz nehmen.", heading: "Ermahnung und Ausblick: 4,1-40", footnotes: ["(1-4) 30,15-20"] },
      { number: 2, text: "Ihr sollt dem Wortlaut dessen, worauf ich euch verpflichte, nichts hinzufügen und nichts davon wegnehmen; ihr sollt auf die Gebote des Herrn, eures Gottes, achten, auf die ich euch verpflichte.", footnotes: ["13,1; Offb 22,18f"] },
      { number: 3, text: "Ihr habt mit eigenen Augen gesehen, was der Herr wegen des Baal-Pegor getan hat. Jeden, der dem Baal-Pegor nachfolgte, hat der Herr, dein Gott, in deiner Mitte vernichtet.", footnotes: ["29,1; Num 25,1-9"] },
      { number: 4, text: "Ihr aber habt euch am Herrn, eurem Gott, fest gehalten, und darum seid ihr alle heute noch am Leben." },
      { number: 5, text: "Hiermit lehre ich euch, wie es mir der Herr, mein Gott, aufgetragen hat, Gesetze und Rechtsvorschriften. Ihr sollt sie innerhalb des Landes halten, in das ihr hineinzieht, um es in Besitz zu nehmen.", footnotes: ["12,1; 31,13", "Das deuteronomische Gesetz verpflichtet Israel innerhalb des verheißenen Landes nach dessen Einnahme."] },
      { number: 6, text: "Ihr sollt auf sie achten und sollt sie halten. Denn darin besteht eure Weisheit und eure Bildung in den Augen der Völker. Wenn sie dieses Gesetzeswerk kennen lernen, müssen sie sagen: In der Tat, diese große Nation ist ein weises und gebildetes Volk.", footnotes: ["6f: Hier ist besonders an die Zeit Salomos gedacht, wie sie in 1 Kön 3 - 11 geschildert wird."] },
      { number: 7, text: "Denn welche große Nation hätte Götter, die ihr so nah sind, wie Jahwe, unser Gott, uns nah ist, wo immer wir ihn anrufen?", footnotes: ["(7-8) Röm 3,2"] },
      { number: 8, text: "Oder welche große Nation besäße Gesetze und Rechtsvorschriften, die so gerecht sind wie alles in dieser Weisung, die ich euch heute vorlege?" },
      { number: 9, text: "Jedoch, nimm dich in Acht, achte gut auf dich! Vergiss nicht die Ereignisse, die du mit eigenen Augen gesehen, und die Worte, die du gehört hast. Lass sie dein ganzes Leben lang nicht aus dem Sinn! Präge sie deinen Kindern und Kindeskindern ein!", footnotes: ["(9-14) 5,2-31; Ex 19-20; Dtn 5,24; 29,1; 31,13; 32,46"] },
      { number: 10, text: "Vergiss nicht den Tag, als du am Horeb vor dem Herrn, deinem Gott, standest. Der Herr hatte zu mir gesagt: Ruf mir das Volk zusammen! Ich will sie meine Worte hören lassen. Sie sollen lernen, mich zu fürchten, so lange, wie sie im Land leben, und sie sollen es auch ihre Kinder lehren." },
      { number: 11, text: "Ihr wart herangekommen und standet unten am Berg und der Berg brannte: Feuer, hoch bis in den Himmel hinauf, Finsternis, Wolken und Dunkel.", footnotes: ["(11-12) Hebr 12,18f"] },
      { number: 12, text: "Der Herr sprach zu euch mitten aus dem Feuer. Ihr hörtet den Donner der Worte. Eine Gestalt habt ihr nicht gesehen. Ihr habt nur den Donner gehört.", footnotes: ["Joh 1,18; 5,37; 6,46"] },
      { number: 13, text: "Der Herr offenbarte euch seinen Bund, er verpflichtete euch, ihn zu halten: die Zehn Worte. Er schrieb sie auf zwei Steintafeln." },
      { number: 14, text: "Mir befahl damals der Herr, euch Gesetze und Rechtsvorschriften zu lehren, die ihr in dem Land halten sollt, in das ihr hinüberzieht, um es in Besitz zu nehmen." },
      { number: 15, text: "Nehmt euch um eures Lebens willen gut in Acht! Denn eine Gestalt habt ihr an dem Tag, als der Herr am Horeb mitten aus dem Feuer zu euch sprach, nicht gesehen.", footnotes: ["(15-19) Röm 1,23"] },
      { number: 16, text: "Lauft nicht in euer Verderben und macht euch kein Gottesbildnis, das irgendetwas darstellt, keine Statue, kein Abbild eines männlichen oder weiblichen Wesens,", footnotes: ["(16-19) 5,8f", "16-20: Umschreibende Erklärung zum ersten der Zehn Gebote. Dabei ist weniger eine bildhafte Jahweverehrung gemeint, als eher die Verehrung anderer Götter unter verschiedensten Gestalten (vgl. 2 Kön 21,3-8; 23,4-14; Ez 8)."] },
      { number: 17, text: "kein Abbild irgendeines Tiers, das auf der Erde lebt, kein Abbild irgendeines gefiederten Vogels, der am Himmel fliegt," },
      { number: 18, text: "kein Abbild irgendeines Tiers, das am Boden kriecht, und kein Abbild irgendeines Meerestieres im Wasser unter der Erde." },
      { number: 19, text: "Wenn du die Augen zum Himmel erhebst und das ganze Himmelsheer siehst, die Sonne, den Mond und die Sterne, dann lass dich nicht verführen! Du sollst dich nicht vor ihnen niederwerfen und ihnen nicht dienen. Der Herr, dein Gott, hat sie allen anderen Völkern überall unter dem Himmel zugewiesen.", footnotes: ["(19-20) 32,8f; 17,2-5; 29,25", "Die für Israel nicht erlaubte Verehrung der Gestirne wird anderen Völkern zugestanden."] },
      { number: 20, text: "Euch aber hat der Herr genommen und aus dem Schmelzofen, aus Ägypten, herausgeführt, damit ihr sein Volk, sein Erbbesitz werdet wie ihr es heute seid.", footnotes: ["9,29; 1 Kön 8,51"] },
      { number: 21, text: "Zwar hat der Herr mir wegen eures Murrens gegrollt und mir geschworen, ich dürfe nicht über den Jordan ziehen und das prächtige Land betreten, das der Herr, dein Gott, dir als Erbbesitz gibt.", footnotes: ["1,37"] },
      { number: 22, text: "Ich muss in diesem Land hier sterben und werde nicht über den Jordan ziehen. Aber ihr werdet hinüberziehen und dieses prächtige Land in Besitz nehmen." },
      { number: 23, text: "Nehmt euch in Acht! Vergesst nicht den Bund, den der Herr, euer Gott, mit euch geschlossen hat. Ihr sollt euch kein Gottesbildnis machen, das irgendetwas darstellt, was der Herr, dein Gott, dir verboten hat.", footnotes: ["29,24"] },
      { number: 24, text: "Denn der Herr, dein Gott, ist verzehrendes Feuer. Er ist ein eifersüchtiger Gott.", footnotes: ["5,9; Hebr 12,29"] },
      { number: 25, text: "Wenn du Kinder und Kindeskinder zeugst und ihr im Land heimisch seid, wenn ihr dann ins Verderben lauft und ein Gottesbildnis macht, das irgendetwas darstellt, wenn ihr also tut, was in den Augen des Herrn, deines Gottes, böse ist, und wenn ihr ihn erzürnt -", footnotes: ["(25-28) 28,15-68"] },
      { number: 26, text: "den Himmel und die Erde rufe ich heute als Zeugen gegen euch an: dann werdet ihr unverzüglich aus dem Land ausgetilgt sein, in das ihr jetzt über den Jordan zieht, um es in Besitz zu nehmen. Nicht lange werdet ihr darin leben. Ihr werdet vernichtet werden.", footnotes: ["30,18f; 31,28"] },
      { number: 27, text: "Der Herr wird euch unter die Völker verstreuen. Nur einige von euch werden übrig bleiben in den Nationen, zu denen der Herr euch führt." },
      { number: 28, text: "Dort müsst ihr Göttern dienen, Machwerken von Menschenhand, aus Holz und Stein. Sie können nicht sehen und nicht hören, nicht essen und nicht riechen.", footnotes: ["27,15; 28,36; Ps 115,4-8; 135,15-18"] },
      { number: 29, text: "Dort werdet ihr den Herrn, deinen Gott, wieder suchen. Du wirst ihn auch finden, wenn du dich mit ganzem Herzen und mit ganzer Seele um ihn bemühst.", footnotes: ["(29-31) 30,1-10"] },
      { number: 30, text: "Wenn du in Not bist, werden alle diese Worte dich finden. In späteren Tagen wirst du zum Herrn, deinem Gott, zurückkehren und auf seine Stimme hören." },
      { number: 31, text: "Denn der Herr, dein Gott, ist ein barmherziger Gott. Er lässt dich nicht fallen und gibt dich nicht dem Verderben preis und vergisst nicht den Bund mit deinen Vätern, den er ihnen beschworen hat.", footnotes: ["Gen 15,18"] },
      { number: 32, text: "Forsche doch einmal in früheren Zeiten nach, die vor dir gewesen sind, seit dem Tag, als Gott den Menschen auf der Erde schuf; forsche nach vom einen Ende des Himmels bis zum andern Ende: Hat sich je etwas so Großes ereignet wie dieses und hat man je solche Worte gehört?", footnotes: ["(32-40) 8,2-6"] },
      { number: 33, text: "Hat je ein Volk einen Gott mitten aus dem Feuer im Donner sprechen hören, wie du ihn gehört hast, und ist am Leben geblieben?" },
      { number: 34, text: "Oder hat je ein Gott es ebenso versucht, zu einer Nation zu kommen und sie mitten aus einer anderen herauszuholen unter Prüfungen, unter Zeichen, Wundern und Krieg, mit starker Hand und hoch erhobenem Arm und unter großen Schrecken, wie es der Herr, euer Gott, in Ägypten mit euch getan hat, vor deinen Augen?", footnotes: ["34,11f"] },
      { number: 35, text: "Das hast du sehen dürfen, damit du erkennst: Jahwe ist der Gott, kein anderer ist außer ihm.", footnotes: ["Mk 12,32; 1 Kor 8,4", "35.39: Die Frage nach der Existenz fremder Götter stellt das Dtn noch nicht; Israel ist jedoch einzig auf seinen Gott Jahwe verpflichtet. Doch hier wird ähnlich wie in Jes 41,22f; 43,10-13; 44,6 und 45,5 eine streng monotheistische Aussage erreicht."] },
      { number: 36, text: "Vom Himmel herab ließ er dich seinen Donner hören, um dich zu erziehen. Auf der Erde ließ er dich sein großes Feuer sehen und mitten aus dem Feuer hast du seine Worte gehört." },
      { number: 37, text: "Weil er deine Väter lieb gewonnen hatte, hat er alle Nachkommen eines jeden von ihnen erwählt und dich dann in eigener Person durch seine große Kraft aus Ägypten geführt,", footnotes: ["10,15", "Andere Übersetzungsmöglichkeit: Weil er deine Väter lieb gewonnen hatte, hat er «seinen Nachkommen» erwählt. - Dann läge eine für den Leser damals erkennbare Anspielung an Gen 17,19 vor, wo Abraham gesagt bekommt, Gott werde seinen Bund mit ihm über die Isaak-Linie weiterführen."] },
      { number: 38, text: "um bei deinem Angriff Völker zu vertreiben, die größer und mächtiger sind als du, um dich in ihr Land zu führen und es dir als Erbbesitz zu geben, wie es jetzt geschieht.", footnotes: ["18,12"] },
      { number: 39, text: "Heute sollst du erkennen und dir zu Herzen nehmen: Jahwe ist der Gott im Himmel droben und auf der Erde unten, keiner sonst.", footnotes: ["1 Kor 8,4"] },
      { number: 40, text: "Daher sollst du auf seine Gesetze und seine Gebote, auf die ich dich heute verpflichte, achten, damit es dir und später deinen Nachkommen gut geht und du lange lebst in dem Land, das der Herr, dein Gott, dir gibt für alle Zeit." },
      { number: 41, text: "Damals hat Mose jenseits des Jordan, nach Osten hin, drei Städten eine Sonderstellung zugewiesen.", heading: "Die Asylstädte im Ostjordanland: 4,41-43", footnotes: ["(41-43) 19,1-10; Num 35,6-15", "41-43: Die Einrichtung der Asylstädte dient dazu, die bei jeder Tötung mögliche Blutrache zu mildern. Schuldloser Unfall und fahrlässige Tötung werden von der Blutrache ausgenommen, nur noch Totschlag und Mord fallen unter sie."] },
      { number: 42, text: "Dorthin soll jeder, der einen Menschen getötet hat, fliehen können, falls er den andern ohne Vorsatz getötet hat und nicht schon früher mit ihm verfeindet gewesen ist. Wenn er in eine dieser Städte flieht, darf er am Leben bleiben." },
      { number: 43, text: "Es sind Bezer im Wüstengebiet der Hochebene für die Rubeniter, Ramot in Gilead für die Gaditer und Golan im Baschan für die Manassiter." },
      { number: 44, text: "Und das ist die Weisung, die Mose den Israeliten vorgelegt hat.", heading: "Die Verkündigung des Gesetzes: 4,44 - 28,68 | Ort und Zeit: 4,44-49", footnotes: ["44-5,1a: Vorbemerkungen zur Rede des Mose 5,1 - 26,19; 28,1-68. «als sie aus Ägypten zogen» (V. 45) meint ganz unbestimmt: in den Jahren des Anfangs der Volksgeschichte. Die «Weisung» wird nicht einfach geboten, sondern Israel von Mose «vorgelegt»; denn sie ist ein Bundestext, der frei angenommen werden soll (vgl. 26,17-19 und 28,69 - 30,20, ferner Ex 19,7f; 21,1 und 24,3). Auch hinter «Satzungen» (4,45) steht ein Wort, das im Alten Orient «Vertragsurkunde» bedeutet und das in den vorausgehenden Büchern stets mit «Bundesurkunde» übersetzt ist."] },
      { number: 45, text: "Das sind die Satzungen, die Gesetze und Rechtsvorschriften, die Mose den Israeliten verkündet hat, als sie aus Ägypten zogen.", footnotes: ["Andere Lesart: die Satzungen, Gesetze und Rechtsvorschriften."] },
      { number: 46, text: "Es geschah jenseits des Jordan, in der Talschlucht gegenüber Bet-Pegor, im Land Sihons, des Königs der Amoriter, der in Heschbon seinen Sitz hatte. Ihn hatten Mose und die Israeliten geschlagen, als sie aus Ägypten zogen." },
      { number: 47, text: "Sie hatten sein Land und das Land Ogs, des Königs des Baschan, in Besitz genommen, das Land der beiden Amoriterkönige jenseits des Jordan, im Osten," },
      { number: 48, text: "von Aroër am Rand des Arnontals bis zum Gebirge Sion - das ist der Hermon -" },
      { number: 49, text: "und die ganze Araba jenseits des Jordan nach Osten hin, bis zum Meer der Araba unterhalb der Steilhänge des Pisga." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Mose rief ganz Israel zusammen. Er sagte zu ihnen: Höre, Israel, die Gesetze und Rechtsvorschriften, die ich euch heute vortrage. Ihr sollt sie lernen, auf sie achten und sie halten.", heading: "Der Bund am Horeb: Die Zehn Gebote:", footnotes: ["11,32; 12,1; 26,16"] },
      { number: 2, text: "Der Herr, unser Gott, hat am Horeb einen Bund mit uns geschlossen.", footnotes: ["(2-31) Apg 7,38"] },
      { number: 3, text: "Nicht mit unseren Vätern hat der Herr diesen Bund geschlossen, sondern mit uns, die wir heute hier stehen, mit uns allen, mit den Lebenden.", footnotes: ["28,69; 29,13f", "Der Vers widerspricht nicht 2,14. In ihren Vätern stand die gegenwärtige Generation selbst am Gottesberg. Die Vergangenheit wird kultisch vergegenwärtigt."] },
      { number: 4, text: "Auge in Auge hat der Herr auf dem Berg mitten aus dem Feuer mit euch geredet.", footnotes: ["(4-22) Ex 19f"] },
      { number: 5, text: "Ich stand damals zwischen dem Herrn und euch, um euch das Wort des Herrn weiterzugeben; denn ihr wart aus Furcht vor dem Feuer nicht auf den Berg gekommen. Der Herr sprach:" },
      { number: 6, text: "Ich bin Jahwe, dein Gott, der dich aus Ägypten geführt hat, aus dem Sklavenhaus.", footnotes: ["(6-22) Ex 20,2-18", "6-21: Der Text der Zehn Gebote weist gegenüber Ex 20,2-17 kleine Unterschiede auf. Am wichtigsten ist, dass das Sabbatgebot neu begründet und zum eigentlichen Zentrum des Textes ausgebaut wird. Diese Fassung der Zehn Gebote ist wohl die jüngere."] },
      { number: 7, text: "Du sollst neben mir keine anderen Götter haben.", footnotes: ["Ex 23,13; 34,14"] },
      { number: 8, text: "Du sollst dir kein Gottesbildnis machen, das irgendetwas darstellt am Himmel droben, auf der Erde unten oder im Wasser unter der Erde.", footnotes: ["27,15"] },
      { number: 9, text: "Du sollst dich nicht vor anderen Göttern niederwerfen und dich nicht verpflichten, ihnen zu dienen. Denn ich, der Herr, dein Gott, bin ein eifersüchtiger Gott: Bei denen, die mir Feind sind, verfolge ich die Schuld der Väter an den Söhnen und an der dritten und vierten Generation;", footnotes: ["(9-10) 7,9f; Ex 34,7; Ex 34,14"] },
      { number: 10, text: "bei denen, die mich lieben und auf meine Gebote achten, erweise ich Tausenden meine Huld.", footnotes: ["11,1"] },
      { number: 11, text: "Du sollst den Namen des Herrn, deines Gottes, nicht missbrauchen; denn der Herr lässt den nicht ungestraft, der seinen Namen missbraucht.", footnotes: ["Andere Übersetzungsmöglichkeit: Du sollst bei dem Namen des Herrn, deines Gottes, keinen Meineid schwören, denn der Herr spricht den nicht frei, der bei seinem Namen einen Meineid schwört. - Die oben gegebene weitere Übersetzung erlaubt es, auch anderen Missbrauch des Jahwenamens, etwa durch Magie, einzuschließen."] },
      { number: 12, text: "Achte auf den Sabbat: Halte ihn heilig, wie es dir der Herr, dein Gott, zur Pflicht gemacht hat." },
      { number: 13, text: "Sechs Tage darfst du schaffen und jede Arbeit tun.", footnotes: ["Ex 23,12; 34,21; Lk 13,14"] },
      { number: 14, text: "Der siebte Tag ist ein Ruhetag, dem Herrn, deinem Gott, geweiht. An ihm darfst du keine Arbeit tun: du, dein Sohn und deine Tochter, dein Sklave und deine Sklavin, dein Rind, dein Esel und dein ganzes Vieh und der Fremde, der in deinen Stadtbereichen Wohnrecht hat. Dein Sklave und deine Sklavin sollen sich ausruhen wie du.", footnotes: ["Mk 2,27; Lk 23,56", "der in deinen Stadtbereichen Wohnrecht hat, wörtlich: der in deinen Stadttoren ist. - So immer, wenn diese Wendung wiederkehrt."] },
      { number: 15, text: "Denk daran: Als du in Ägypten Sklave warst, hat dich der Herr, dein Gott, mit starker Hand und hoch erhobenem Arm dort herausgeführt. Darum hat es dir der Herr, dein Gott, zur Pflicht gemacht, den Sabbat zu halten.", footnotes: ["15,15; 16,12; 24,18.22; Apg 13,17"] },
      { number: 16, text: "Ehre deinen Vater und deine Mutter, wie es dir der Herr, dein Gott, zur Pflicht gemacht hat, damit du lange lebst und es dir gut geht in dem Land, das der Herr, dein Gott, dir gibt.", footnotes: ["Mt 15,4; Mk 7,10; Eph 6,2f (16-21) Mt 19,18f; Mk 10,19; Lk 18,20; Röm 13,9"] },
      { number: 17, text: "Du sollst nicht morden,", footnotes: ["(17f) Jak 2,11; Mt 5,21", "Manche Bibelausgaben zählen die VV. 17-20 nur als V. 17; entsprechend ändert sich die Zählung der folgenden Verse."] },
      { number: 18, text: "du sollst nicht die Ehe brechen,", footnotes: ["Mt 5,27"] },
      { number: 19, text: "du sollst nicht stehlen," },
      { number: 20, text: "du sollst nicht Falsches gegen deinen Nächsten aussagen," },
      { number: 21, text: "du sollst nicht nach der Frau deines Nächsten verlangen und du sollst nicht das Haus deines Nächsten begehren, nicht sein Feld, seinen Sklaven oder seine Sklavin, sein Rind oder seinen Esel, nichts, was deinem Nächsten gehört.", footnotes: ["Röm 7,7"] },
      { number: 22, text: "Diese Worte sagte der Herr auf dem Berg zu eurer vollzähligen Versammlung, mitten aus dem Feuer, aus Wolken und Dunkel, unter lautem Donner, diese Worte und sonst nichts. Er schrieb sie auf zwei Steintafeln und übergab sie mir.", footnotes: ["(22-27) Hebr 12,18f"] },
      { number: 23, text: "Als ihr den Donner mitten aus der Finsternis gehört hattet und der Berg immer noch in Feuer stand, seid ihr zu mir gekommen - eure Stammesführer und Ältesten -", heading: "Die Einsetzung des Mose als Gesetzesmittler: 5,23-33", footnotes: ["1,13; 19,12; 27,1; 29,9; 31,9.28 (23-31) 18,16-19; Ex 20,18-21"] },
      { number: 24, text: "und habt gesagt: Sieh, der Herr, unser Gott, hat uns seine Herrlichkeit und Macht gezeigt und wir haben seine donnernde Stimme mitten aus dem Feuer gehört. Heute ist es uns geschehen, dass Gott zu Menschen sprach und sie am Leben blieben.", footnotes: ["4,9; 29,1"] },
      { number: 25, text: "Trotzdem: Warum sollen wir noch einmal das Leben aufs Spiel setzen? Denn dieses große Feuer könnte uns verzehren. Wenn wir noch einmal die donnernde Stimme des Herrn, unseres Gottes, hören, werden wir sterben." },
      { number: 26, text: "Denn welches Wesen aus Fleisch wäre am Leben geblieben, wenn es wie wir die donnernde Stimme des lebendigen Gottes gehört hätte, als er mitten aus dem Feuer redete?" },
      { number: 27, text: "Geh du allein hin! Höre alles, was der Herr, unser Gott, sagt. Berichte uns dann alles, was der Herr, unser Gott, dir gesagt hat, und wir werden es hören und halten." },
      { number: 28, text: "Der Herr hörte euer Geschrei, als ihr auf mich einredetet, und sagte zu mir: Ich habe das Geschrei dieses Volkes gehört, mit dem es dich bedrängt hat. Alles, was sie von dir verlangen, ist recht." },
      { number: 29, text: "Möchten sie doch diese Gesinnung behalten, mich fürchten und ihr Leben lang auf alle meine Gebote achten, damit es ihnen und ihren Nachkommen immer gut geht." },
      { number: 30, text: "Geh und sag ihnen: Kehrt zu euren Zelten zurück!" },
      { number: 31, text: "Und du, stell dich hierher zu mir! Ich will dir das ganze Gebot mitteilen, die Gesetze und Rechtsvorschriften, die du sie lehren sollst und die sie halten sollen in dem Land, das ich ihnen gebe und das sie in Besitz nehmen sollen.", footnotes: ["Andere Lesart: Ich will dir das ganze Gebot, die Gesetze und die Rechtsvorschriften mitteilen."] },
      { number: 32, text: "Daher sollt ihr darauf achten, dass ihr handelt, wie es der Herr, euer Gott, euch vorgeschrieben hat. Ihr sollt weder rechts noch links abweichen." },
      { number: 33, text: "Ihr sollt nur auf dem Weg gehen, den der Herr, euer Gott, euch vorgeschrieben hat, damit ihr Leben habt und es euch gut geht und ihr lange lebt in dem Land, das ihr in Besitz nehmt." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Und das ist das Gebot, das sind die Gesetze und Rechtsvorschriften, die ich euch im Auftrag des Herrn, eures Gottes, lehren soll und die ihr halten sollt in dem Land, in das ihr hinüberzieht, um es in Besitz zu nehmen.", heading: "Die Grundforderung Gottes: Liebe und Furcht:" },
      { number: 2, text: "Wenn du den Herrn, deinen Gott, fürchtest, indem du auf alle seine Gesetze und Gebote, auf die ich dich verpflichte, dein ganzes Leben lang achtest, du, dein Sohn und dein Enkel, wirst du lange leben.", footnotes: ["2f: Die den Zusammenhang unterbrechende Erweiterung will die Motive von 5,27-29 noch einmal anklingen lassen."] },
      { number: 3, text: "Deshalb, Israel, sollst du hören und darauf achten, (alles, was der Herr, unser Gott, mir gesagt hat,) zu halten, damit es dir gut geht und ihr so unermesslich zahlreich werdet, wie es der Herr, der Gott deiner Väter, dir zugesagt hat, in dem Land, wo Milch und Honig fließen.", footnotes: ["Ex 3,8.17"] },
      { number: 4, text: "Höre, Israel! Jahwe, unser Gott, Jahwe ist einzig.", footnotes: ["Röm 3,30; 1 Kor 8,4 (4-5) Mk 12,29-33;", "Andere Übersetzungsmöglichkeit: Jahwe ist unser Gott, Jahwe allein. - Wie man auch übersetzt, der Vers bietet keine Lehre vom Monotheismus, sondern besagt, dass Jahwe für Israel der einzige Gott ist. Nach Sach 14,9 gilt diese Aussage nicht nur für Israel, sondern für alle Völker. Später wird der Vers als Bekenntnis zum Monotheismus verstanden. Die im Text gegebene Übersetzung zeigt, dass auf die Sprache der Liebeslyrik angespielt wird (vgl. Hld 6,8f)."] },
      { number: 5, text: "Darum sollst du den Herrn, deinen Gott, lieben mit ganzem Herzen, mit ganzer Seele und mit ganzer Kraft.", footnotes: ["5,10; 11,1; 2 Kön 23,3; Lk 10,27"] },
      { number: 6, text: "Diese Worte, auf die ich dich heute verpflichte, sollen auf deinem Herzen geschrieben stehen.", footnotes: ["(6-9) 11,18-20; 30,11-14", "Das Herz wird mit einer Schreibtafel verglichen. Da das Herz auch als Sitz des Gedächtnisses galt, ist hier gesagt, man solle «diese Worte» auswendig wissen."] },
      { number: 7, text: "Du sollst sie deinen Söhnen wiederholen. Du sollst von ihnen reden, wenn du zu Hause sitzt und wenn du auf der Straße gehst, wenn du dich schlafen legst und wenn du aufstehst.", footnotes: ["Du sollst von ihnen reden, andere Übersetzungsmöglichkeit: Du sollst sie aufsagen."] },
      { number: 8, text: "Du sollst sie als Zeichen um das Handgelenk binden. Sie sollen zum Schmuck auf deiner Stirn werden.", footnotes: ["(8-9) Mt 23,5; Ex 13,9.16"] },
      { number: 9, text: "Du sollst sie auf die Türpfosten deines Hauses und in deine Stadttore schreiben." },
      { number: 10, text: "Und wenn der Herr, dein Gott, dich in das Land führt, von dem du weißt: er hat deinen Vätern Abraham, Isaak und Jakob geschworen, es dir zu geben - große und schöne Städte, die du nicht gebaut hast,", footnotes: ["(10-11) Jos 24,13"] },
      { number: 11, text: "mit Gütern gefüllte Häuser, die du nicht gefüllt hast, in den Felsen gehauene Zisternen, die du nicht gehauen hast, Weinberge und Ölbäume, die du nicht gepflanzt hast -, wenn du dann isst und satt wirst:" },
      { number: 12, text: "nimm dich in Acht, dass du nicht den Herrn vergisst, der dich aus Ägypten, dem Sklavenhaus, geführt hat.", footnotes: ["(12-15) 5,6-9"] },
      { number: 13, text: "Den Herrn, deinen Gott, sollst du fürchten; ihm sollst du dienen, bei seinem Namen sollst du schwören.", footnotes: ["Mt 4,10; Lk 4,8", "«fürchten» meint die Gottesverehrung in all ihren Formen."] },
      { number: 14, text: "Ihr sollt nicht anderen Göttern nachfolgen, keinem Gott eines Volkes, das in eurer Nachbarschaft wohnt.", footnotes: ["2 Kön 23,3"] },
      { number: 15, text: "Denn der Herr, dein Gott, ist als eifersüchtiger Gott in deiner Mitte. Der Zorn des Herrn, deines Gottes, könnte gegen dich entbrennen, er könnte dich im ganzen Land vernichten." },
      { number: 16, text: "Ihr sollt den Herrn, euren Gott, nicht auf die Probe stellen, wie ihr ihn bei Massa auf die Probe gestellt habt.", footnotes: ["Ex 17,2-7; Num 20,1-13; Mt 4,7; Lk 4,12"] },
      { number: 17, text: "Ihr sollt auf die Gebote des Herrn, eures Gottes, genau achten, auf seine Satzungen und Gesetze, auf die er dich verpflichtet hat.", footnotes: ["5,10; 2 Kön 23,3", "Andere Lesart: Ihr sollt auf die Gebote des Herrn, eures Gottes, genau achten und auf seine Satzungen und Gesetze, auf die er dich verpflichtet hat."] },
      { number: 18, text: "Du sollst tun, was in seinen Augen richtig und gut ist. Dann wird es dir gut gehen und du kannst in das prächtige Land, das der Herr deinen Vätern mit einem Schwur versprochen hat, hineinziehen und es in Besitz nehmen.", footnotes: ["(18-19) 16,20; 1,8"] },
      { number: 19, text: "Der Herr wird alle deine Feinde vor dir herjagen, wie er es zugesagt hat.", footnotes: ["2,25; Ex 23,27-31; 34,11"] },
      { number: 20, text: "Wenn dich morgen dein Sohn fragt: Warum achtet ihr auf die Satzungen, die Gesetze und Rechtsvorschriften, auf die der Herr, unser Gott, euch verpflichtet hat?,", footnotes: ["Ex 13,14"] },
      { number: 21, text: "dann sollst du deinem Sohn antworten: Wir waren Sklaven des Pharao in Ägypten und der Herr hat uns mit starker Hand aus Ägypten geführt." },
      { number: 22, text: "Der Herr hat vor unseren Augen gewaltige, unheilvolle Zeichen und Wunder an Ägypten, am Pharao und an seinem ganzen Haus getan,", footnotes: ["Das «Haus» des Pharao ist wohl die Gesamtheit seiner Untertanen (vgl. Gen 41,40)."] },
      { number: 23, text: "uns aber hat er dort herausgeführt, um uns in das Land, das er unseren Vätern mit einem Schwur versprochen hatte, hineinzuführen und es uns zu geben." },
      { number: 24, text: "Der Herr hat uns verpflichtet, alle diese Gesetze zu halten und den Herrn, unseren Gott, zu fürchten, damit es uns das ganze Leben lang gut geht und er uns Leben schenkt, wie wir es heute haben." },
      { number: 25, text: "Nur dann werden wir (vor Gott) im Recht sein, wenn wir darauf achten, dieses ganze Gesetz vor dem Herrn, unserem Gott, so zu halten, wie er es uns zur Pflicht gemacht hat." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Wenn der Herr, dein Gott, dich in das Land geführt hat, in das du jetzt hineinziehst, um es in Besitz zu nehmen, wenn er dir viele Völker aus dem Weg räumt - Hetiter, Girgaschiter und Amoriter, Kanaaniter und Perisiter, Hiwiter und Jebusiter, sieben Völker, die zahlreicher und mächtiger sind als du -,", heading: "Israel und die Völker des Landes: 7,1-26", footnotes: ["Apg 13,19 (1-26) Ex 23,20-33; 34,11-16; Ri 2,1-5; (1-2) 20,17f; Jos 10,40"] },
      { number: 2, text: "wenn der Herr, dein Gott, sie dir ausliefert und du sie schlägst, dann sollst du sie der Vernichtung weihen. Du sollst keinen Vertrag mit ihnen schließen, sie nicht verschonen" },
      { number: 3, text: "und dich nicht mit ihnen verschwägern. Deine Tochter gib nicht seinem Sohn und nimm seine Tochter nicht für deinen Sohn!" },
      { number: 4, text: "Wenn er deinen Sohn verleitet, mir nicht mehr nachzufolgen, und sie dann anderen Göttern dienen, wird der Zorn des Herrn gegen euch entbrennen und wird dich unverzüglich vernichten.", footnotes: ["13,3; 28,14", "mir nicht mehr nachzufolgen: Mose wechselt für einen Augenblick in das Ich Gottes."] },
      { number: 5, text: "So sollt ihr gegen sie vorgehen: Ihr sollt ihre Altäre niederreißen, ihre Steinmale zerschlagen, ihre Kultpfähle umhauen und ihre Götterbilder im Feuer verbrennen.", footnotes: ["Ex 23,24; 34,13"] },
      { number: 6, text: "Denn du bist ein Volk, das dem Herrn, deinem Gott, heilig ist. Dich hat der Herr, dein Gott, ausgewählt, damit du unter allen Völkern, die auf der Erde leben, das Volk wirst, das ihm persönlich gehört.", footnotes: ["(6-8) 10,15; 14,2.21; 26,18f; 28,9; Ex 19,3-6; 22,30; Röm 9,4; Tit 2,14; 1 Petr 2,9", "das ihm persönlich gehört: Hier wird in H ein Begriff für Eigentum gebraucht, der schwer zu übersetzen ist. Der Familienvater konnte neben dem Familienbesitz und der König neben dem Krongut noch ganz besonderen, persönlichen Besitz haben. Der betreffende Ausdruck konnte im Alten Orient auch auf die Vasallen eines Großkönigs angewandt werden. Das klingt hier an."] },
      { number: 7, text: "Nicht weil ihr zahlreicher als die anderen Völker wäret, hat euch der Herr ins Herz geschlossen und ausgewählt; ihr seid das kleinste unter allen Völkern." },
      { number: 8, text: "Weil der Herr euch liebt und weil er auf den Schwur achtet, den er euren Vätern geleistet hat, deshalb hat der Herr euch mit starker Hand herausgeführt und euch aus dem Sklavenhaus freigekauft, aus der Hand des Pharao, des Königs von Ägypten.", footnotes: ["(8-11) 8,2-6; (8-10) 5,6-10; Hos 11,1; Gen 15,18"] },
      { number: 9, text: "Daran sollst du erkennen: Jahwe, dein Gott, ist der Gott; er ist der treue Gott; noch nach tausend Generationen achtet er auf den Bund und erweist denen seine Huld, die ihn lieben und auf seine Gebote achten.", footnotes: ["11,1; 1 Kor 1,9; 10,13"] },
      { number: 10, text: "Denen aber, die ihm Feind sind, vergilt er sofort und tilgt einen jeden aus; er zögert nicht, wenn einer ihm Feind ist, sondern vergilt ihm sofort." },
      { number: 11, text: "Deshalb sollst du auf das Gebot achten, auf die Gesetze und Rechtsvorschriften, auf die ich dich heute verpflichte, und du sollst sie halten.", footnotes: ["Andere Lesart: Deshalb sollst du auf das Gebot, die Gesetze und die Rechtsvorschriften achten, auf die ich dich heute verpflichte."] },
      { number: 12, text: "Wenn ihr diese Rechtsvorschriften hört, auf sie achtet und sie haltet, wird der Herr, dein Gott, dafür auf den Bund achten und dir die Huld bewahren, die er deinen Vätern geschworen hat.", footnotes: ["(12-16) 28,1-14"] },
      { number: 13, text: "Er wird dich lieben, dich segnen und dich zahlreich machen. Er wird die Frucht deines Leibes und die Frucht deines Ackers segnen, dein Korn, deinen Wein und dein Öl, die Kälber, Lämmer und Zicklein, in dem Land, von dem du weißt: Er hat deinen Vätern geschworen, es dir zu geben.", footnotes: ["28,4; Lk 1,42"] },
      { number: 14, text: "Du wirst mehr als die anderen Völker gesegnet sein. Weder Mann noch Frau noch Vieh, nichts wird bei dir unfruchtbar sein." },
      { number: 15, text: "Alle Krankheiten wird der Herr von dir ablenken. Keine der schweren ägyptischen Seuchen, die du kennst, wird er dir auferlegen, sondern über alle deine Feinde wird er sie bringen.", footnotes: ["28,27; Ex 15,26"] },
      { number: 16, text: "Du wirst alle Völker verzehren, die der Herr, dein Gott, für dich bestimmt. Du sollst in dir kein Mitleid mit ihnen aufsteigen lassen. Und du sollst ihren Göttern nicht dienen; denn dann liefest du in eine Falle.", footnotes: ["19,13"] },
      { number: 17, text: "Wenn du überlegst: Diese Völker sind größer als ich - wie sollte ich sie vertreiben können?,", footnotes: ["(17-18) 1,28-31; 20,2-4; 18,12"] },
      { number: 18, text: "dann sollst du vor ihnen keine Furcht haben. Du sollst an das denken, was der Herr, dein Gott, mit dem Pharao und mit ganz Ägypten gemacht hat:", footnotes: ["29,1"] },
      { number: 19, text: "an die schweren Prüfungen, die du mit eigenen Augen gesehen hast, an die Zeichen und Wunder, an die starke Hand und den hoch erhobenen Arm, mit denen der Herr, dein Gott, dich herausgeführt hat. So wird es der Herr, dein Gott, mit allen Völkern machen, vor denen du Furcht hast." },
      { number: 20, text: "Außerdem wird der Herr, dein Gott, Panik unter ihnen ausbrechen lassen, so lange, bis auch die ausgetilgt sind, die überleben konnten und sich vor dir versteckt haben." },
      { number: 21, text: "Du sollst nicht erschreckt zurückweichen, wenn sie angreifen; denn der Herr, dein Gott, ist als großer und Furcht erregender Gott in deiner Mitte." },
      { number: 22, text: "Doch der Herr, dein Gott, wird diese Völker dir nur nach und nach aus dem Weg räumen. Du kannst sie nicht rasch ausmerzen, weil sonst die wilden Tiere überhand nehmen und dir schaden." },
      { number: 23, text: "Doch wird der Herr, dein Gott, dir diese Völker ausliefern. Er wird sie in ausweglose Verwirrung stürzen, bis sie vernichtet sind." },
      { number: 24, text: "Er wird ihre Könige in deine Gewalt geben. Du wirst ihren Namen unter dem Himmel austilgen. Keiner wird deinem Angriff standhalten können, bis du sie schließlich vernichtet hast." },
      { number: 25, text: "Ihre Götterbilder sollt ihr im Feuer verbrennen. Du sollst nicht das Silber oder Gold haben wollen, mit dem sie überzogen sind. Du sollst es nicht an dich nehmen, damit du dabei nicht in eine Falle läufst. Denn es ist dem Herrn, deinem Gott, ein Gräuel.", footnotes: ["7,5; 27,15 (25-26) Jos 7", "25f: Das mit «Gräuel» übersetzte Wort, das noch öfter als Begründung von Gesetzen vorkommt, meint etwas Ekelhaftes, dessen man sich schämt."] },
      { number: 26, text: "Du sollst aber keinen Gräuel in dein Haus bringen, sonst bist du wie er der Vernichtung geweiht. Du sollst Grauen und Abscheu vor ihm haben, denn er ist der Vernichtung geweiht." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Ihr sollt auf das ganze Gebot, auf das ich dich heute verpflichte, achten und es halten, damit ihr Leben habt und zahlreich werdet und in das Land, das der Herr euren Vätern mit einem Schwur versprochen hat, hineinziehen und es in Besitz nehmen könnt.", heading: "Israel und der Reichtum: 8,1-20", footnotes: ["16,20"] },
      { number: 2, text: "Du sollst an den ganzen Weg denken, den der Herr, dein Gott, dich während dieser vierzig Jahre in der Wüste geführt hat, um dich gefügig zu machen und dich zu prüfen. Er wollte erkennen, wie du dich entscheiden würdest: ob du auf seine Gebote achtest oder nicht.", footnotes: ["13,4; Gen 22,1; Ex 15,25; 16,4; 20,20 (2-6) 4,32-40; 7,8-11; 9,4-7; 10,20-22; 11,1-7; 29,1-8"] },
      { number: 3, text: "Durch Hunger hat er dich gefügig gemacht und hat dich dann mit dem Manna gespeist, das du nicht kanntest und das auch deine Väter nicht kannten. Er wollte dich erkennen lassen, dass der Mensch nicht nur von Brot lebt, sondern dass der Mensch von allem lebt, was der Mund des Herrn spricht.", footnotes: ["Ex 17; Num 11,4-9; Mt 4,4; Lk 4,4; 1 Kor 10,3", "Wörtlich: was aus dem Mund des Herrn hervorgeht."] },
      { number: 4, text: "Deine Kleider sind dir nicht in Lumpen vom Leib gefallen und dein Fuß ist nicht geschwollen, diese vierzig Jahre lang.", footnotes: ["2,7; 29,4"] },
      { number: 5, text: "Daraus sollst du die Erkenntnis gewinnen, dass der Herr, dein Gott, dich erzieht, wie ein Vater seinen Sohn erzieht.", footnotes: ["Hebr 12,7"] },
      { number: 6, text: "Du sollst auf die Gebote des Herrn, deines Gottes, achten, auf seinen Wegen gehen und ihn fürchten." },
      { number: 7, text: "Wenn der Herr, dein Gott, dich in ein prächtiges Land führt, ein Land mit Bächen, Quellen und Grundwasser, das im Tal und am Berg hervorquillt,", footnotes: ["(7-20) 6,10-14; 11,10-15; 2 Kön 18,32"] },
      { number: 8, text: "ein Land mit Weizen und Gerste, mit Weinstock, Feigenbaum und Granatbaum, ein Land mit Ölbaum und Honig," },
      { number: 9, text: "ein Land, in dem du nicht armselig dein Brot essen musst, in dem es dir an nichts fehlt, ein Land, dessen Steine aus Eisen sind, aus dessen Bergen du Erz gewinnst;" },
      { number: 10, text: "wenn du dort isst und satt wirst und den Herrn, deinen Gott, für das prächtige Land, das er dir gegeben hat, preist," },
      { number: 11, text: "dann nimm dich in acht und vergiss den Herrn, deinen Gott, nicht, missachte nicht seine Gebote, Rechtsvorschriften und Gesetze, auf die ich dich heute verpflichte." },
      { number: 12, text: "Und wenn du gegessen hast und satt geworden bist und prächtige Häuser gebaut hast und sie bewohnst," },
      { number: 13, text: "wenn deine Rinder, Schafe und Ziegen sich vermehren und Silber und Gold sich bei dir häuft und dein gesamter Besitz sich vermehrt," },
      { number: 14, text: "dann nimm dich in Acht, dass dein Herz nicht hochmütig wird und du den Herrn, deinen Gott, nicht vergisst, der dich aus Ägypten, dem Sklavenhaus, geführt hat;" },
      { number: 15, text: "der dich durch die große und Furcht erregende Wüste geführt hat, durch Feuernattern und Skorpione, durch ausgedörrtes Land, wo es kein Wasser gab; der für dich Wasser aus dem Felsen der Steilwand hervorsprudeln ließ;", footnotes: ["Num 21,4-9; Ex 17,1-7"] },
      { number: 16, text: "der dich in der Wüste mit dem Manna speiste, das deine Väter noch nicht kannten, (und der das alles tat,) um dich gefügig zu machen, dich zu prüfen und dir zuletzt Gutes zu tun." },
      { number: 17, text: "Dann nimm dich in Acht und denk nicht bei dir: Ich habe mir diesen Reichtum aus eigener Kraft und mit eigener Hand erworben." },
      { number: 18, text: "Denk vielmehr an den Herrn, deinen Gott: Er war es, der dir die Kraft gab, Reichtum zu erwerben, weil er seinen Bund, den er deinen Vätern geschworen hatte, so verwirklichen wollte, wie er es heute tut.", footnotes: ["Gen 15,18"] },
      { number: 19, text: "Wenn du aber den Herrn, deinen Gott, vergisst und anderen Göttern nachfolgst, ihnen dienst und dich vor ihnen niederwirfst - heute rufe ich Zeugen gegen euch an: dann werdet ihr völlig ausgetilgt werden.", footnotes: ["30,19 (19-20) 28,15-68"] },
      { number: 20, text: "Wie die Völker, die der Herr bei eurem Angriff austilgt, so werdet auch ihr dafür ausgetilgt werden, dass ihr nicht auf die Stimme des Herrn, eures Gottes, gehört habt." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Höre, Israel! Heute wirst du den Jordan überschreiten, um in das Land von Völkern, die größer und mächtiger sind als du, hineinzuziehen und ihren Besitz zu übernehmen. Du wirst in große Städte mit himmelhohen Mauern kommen,", heading: "Kein Rechtsanspruch Israels auf das Land: 9,1-8", footnotes: ["(1-6) 18,12; (1-2) 1,28; 11,23"] },
      { number: 2, text: "zu einem großen, hoch gewachsenen Volk, den Anakitern - du kennst ja die Redensart und hast sie oft gehört: Wer kann den Söhnen Anaks widerstehen?", footnotes: ["Num 13,32f"] },
      { number: 3, text: "Heute wirst du erkennen, dass der Herr, dein Gott, wie ein verzehrendes Feuer selbst vor dir hinüberzieht. Er wird sie vernichten und er wird sie dir unterwerfen, sodass du sie unverzüglich vertreiben und austilgen kannst, wie es der Herr dir zugesagt hat.", footnotes: ["6,19; 31,3; Hebr 12,29"] },
      { number: 4, text: "Wenn der Herr, dein Gott, sie vor dir herjagt, sollst du nicht meinen: Ich bin im Recht, daher lässt mich der Herr in das Land hineinziehen und es in Besitz nehmen; diese Völker sind im Unrecht, daher vertreibt sie der Herr vor mir.", footnotes: ["6,19.25; 8,17; Röm 10,6 (4-7) 8,2-6; (4-6) Röm 10,3; Phil 3,9"] },
      { number: 5, text: "Denn nicht, weil du im Recht bist und die richtige Gesinnung hast, kannst du in ihr Land hineinziehen und es in Besitz nehmen. Vielmehr vertreibt der Herr, dein Gott, diese Völker vor dir, weil sie im Unrecht sind und weil der Herr die Zusage einlösen will, die er deinen Vätern Abraham, Isaak und Jakob mit einem Schwur bekräftigt hat.", footnotes: ["Gen 15,18"] },
      { number: 6, text: "Du sollst erkennen: Du bist ein halsstarriges Volk. Daher kann dir der Herr, dein Gott, dieses prächtige Land nicht etwa aufgrund eines Rechtsanspruchs geben, damit du es in Besitz nimmst.", footnotes: ["9,13; Ex 32,9; Apg 7,51"] },
      { number: 7, text: "Denk daran und vergiss nicht, dass du in der Wüste den Unwillen des Herrn, deines Gottes, erregt hast. Von dem Tag an, als du aus Ägypten auszogst, bis zur Ankunft an diesem Ort habt ihr euch dem Herrn ständig widersetzt.", footnotes: ["1,26.43; Num 20,10.24; Ez 20,8.13.21; Ps 78,17.40.56"] },
      { number: 8, text: "Vor allem am Horeb habt ihr den Unwillen des Herrn erregt. Damals grollte er euch so sehr, dass er euch vernichten wollte." },
      { number: 9, text: "Als ich auf den Berg gestiegen war, um die Steintafeln entgegenzunehmen, die Tafeln des Bundes, den der Herr mit euch geschlossen hatte, blieb ich vierzig Tage und vierzig Nächte auf dem Berg, aß kein Brot und trank kein Wasser.", heading: "Der Bundesbruch und die Bundeserneuerung am Horeb: 9,9 - 10,11", footnotes: ["5,22; Mt 4,2; Lk 4,2; (9-29) Ex 32"] },
      { number: 10, text: "Der Herr übergab mir die beiden Steintafeln, die mit dem Gottesfinger beschrieben waren. Auf den Tafeln standen alle die Worte, die der Herr am Tag der Versammlung auf dem Berg mitten aus dem Feuer zu euch gesprochen hatte.", footnotes: ["Ex 31,18; Apg 7,38; (10-11) 2 Kor 3,3"] },
      { number: 11, text: "Es war am Ende der vierzig Tage und der vierzig Nächte, als mir der Herr die beiden Steintafeln, die Tafeln des Bundes, übergab" },
      { number: 12, text: "und zu mir sagte: Steh auf, steig rasch hinunter, weg von hier; denn dein Volk, das du aus Ägypten geführt hast, läuft ins Verderben. Sie sind rasch von dem Weg abgewichen, den ich ihnen vorgeschrieben habe. Sie haben sich ein Bildnis gegossen." },
      { number: 13, text: "Weiter sagte der Herr zu mir: Ich habe mir dieses Volk angesehen. Ja, es ist ein halsstarriges Volk." },
      { number: 14, text: "Lass mich, damit ich sie vernichte, ihren Namen unter dem Himmel auslösche und dich zu einem Volk mache, das mächtiger und zahlreicher als dieses ist.", footnotes: ["Ex 32,10; Num 14,12"] },
      { number: 15, text: "Ich wandte mich um und stieg den Berg hinunter. Der Berg stand in Feuer. Ich trug die beiden Tafeln des Bundes auf meinen Armen." },
      { number: 16, text: "Und ich sah, was geschehen war: Ja, ihr hattet euch an dem Herrn, eurem Gott, versündigt, ihr hattet euch ein Kalb gegossen, ihr wart rasch von dem Weg abgewichen, den der Herr euch vorgeschrieben hatte." },
      { number: 17, text: "Ich packte die beiden Tafeln, die ich auf meinen Armen trug, schleuderte sie fort und zerschmetterte sie vor euren Augen." },
      { number: 18, text: "Dann warf ich mich vor dem Herrn nieder. Wie beim ersten Mal blieb ich vierzig Tage und vierzig Nächte vor ihm, aß kein Brot und trank kein Wasser, wegen all der Sünde, die ihr begangen hattet, indem ihr tatet, was in den Augen des Herrn böse ist, sodass ihr ihn erzürntet." },
      { number: 19, text: "Denn ich hatte Angst vor dem glühenden Zorn des Herrn. Er war voll Unwillen gegen euch und wollte euch vernichten. Doch der Herr erhörte mich auch diesmal.", footnotes: ["Hebr 12,21"] },
      { number: 20, text: "[Auch dem Aaron grollte der Herr sehr und er wollte ihn vernichten. Damals betete ich auch für Aaron.]" },
      { number: 21, text: "Ich nahm das Kalb, die Sünde, die ihr euch gemacht hattet, verbrannte es im Feuer, zerstieß es und zermahlte es vollständig, bis es feiner Staub war; den Staub streute ich in den Bach, der vom Berg herunterfließt." },
      { number: 22, text: "Auch in Tabera, in Massa und in Kibrot-Taawa habt ihr den Unwillen des Herrn erregt.", footnotes: ["Ex 17,2-7; Num 11"] },
      { number: 23, text: "Als der Herr euch von Kadesch-Barnea aussandte mit dem Befehl: Zieht hinauf und nehmt das Land in Besitz, das ich euch gebe!, da habt ihr euch dem Befehl des Herrn, eures Gottes, widersetzt, ihr habt ihm nicht geglaubt und nicht auf seine Stimme gehört.", footnotes: ["1,19-40; Num 13f"] },
      { number: 24, text: "Ihr habt euch dem Herrn widersetzt, seit er euch kennt.", footnotes: ["Andere Lesart: seit ich euch kenne."] },
      { number: 25, text: "Ich warf mich also vor dem Herrn nieder und lag vor ihm vierzig Tage und vierzig Nächte lang. Ich warf mich nieder, weil der Herr gedroht hatte, er werde euch vernichten." },
      { number: 26, text: "Ich betete zum Herrn und sagte: Gott, mein Herr, bring nicht das Verderben über dein Volk und deinen Erbbesitz, die du in deiner Macht freigekauft und mit starker Hand aus Ägypten geführt hast.", footnotes: ["(26-29) Ex 32,11-13; Num 14,13-19"] },
      { number: 27, text: "Denk an deine Knechte, an Abraham, Isaak und Jakob! Beachte nicht den Starrsinn dieses Volkes, sein Verschulden und seine Sünde," },
      { number: 28, text: "damit man nicht in dem Land, aus dem du uns geführt hast, sagt: Offenbar kann der Herr sie nicht in das Land führen, das er ihnen zugesagt hat, oder er liebt sie nicht; also hat er sie nur herausgeführt, um sie in der Wüste sterben zu lassen." },
      { number: 29, text: "Sie sind aber doch dein Volk und dein Erbbesitz, die du mit großer Kraft und hoch erhobenem Arm herausgeführt hast." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Damals sagte der Herr zu mir: Hau zwei Steintafeln zurecht, so wie die ersten, und steig zu mir auf den Berg! Fertige auch eine hölzerne Lade an!", footnotes: ["Ex 34,1"] },
      { number: 2, text: "Ich will auf die Tafeln die gleichen Worte schreiben wie auf die ersten, die du zerschmettert hast; diese Tafeln sollst du dann in die Lade legen." },
      { number: 3, text: "Ich fertigte eine Lade aus Akazienholz an, hieb zwei Steintafeln zurecht, so wie die ersten, und stieg auf den Berg. Ich hielt die beiden Tafeln in der Hand.", footnotes: ["(3-5) Hebr 9,4"] },
      { number: 4, text: "Wie bei der ersten Inschrift schrieb der Herr auf die Tafeln die Zehn Worte, die er am Tag der Versammlung auf dem Berg mitten aus dem Feuer zu euch gesprochen hatte, und der Herr übergab sie mir." },
      { number: 5, text: "Ich wandte mich um, stieg den Berg hinunter und legte die Tafeln in die Lade, die ich angefertigt hatte. Dort blieben sie, wie der Herr es mir befohlen hatte." },
      { number: 6, text: "[Die Israeliten zogen von Beerot-Bene-Jaakan nach Moser. Dort starb Aaron, dort wurde er auch begraben. Sein Sohn Eleasar wurde an seiner Stelle Priester.", footnotes: ["(6-7) Num 33,31-33; Num 20,22-29", "6f: Erklärender Zusatz (vgl. die Anmerkung zu 1,1-5)."] },
      { number: 7, text: "Von dort zogen sie nach Hor-Gidgad, von Hor-Gidgad nach Jotbata, einer Gegend, wo es Bäche gab, die immer Wasser führten.]" },
      { number: 8, text: "Damals sonderte der Herr den Stamm Levi aus, damit er die Lade des Bundes des Herrn trage, vor dem Herrn stehe, vor ihm Dienst tue und in seinem Namen den Segen spreche. So geschieht es noch heute.", footnotes: ["17,9.18; 18,1.5; 21,5; 24,8; 27,9; 31,9"] },
      { number: 9, text: "Deshalb erhielt Levi nicht wie seine Brüder Landanteil und Erbbesitz. Der Herr ist sein Erbbesitz, wie es der Herr, dein Gott, ihm zugesagt hat.", footnotes: ["12,12; 14,27.29; 18,1f"] },
      { number: 10, text: "Zu mir aber, als ich wie beim ersten Mal vierzig Tage und vierzig Nächte lang auf dem Berg blieb und als der Herr mich auch diesmal erhörte, weil er dich nicht dem Verderben preisgeben wollte," },
      { number: 11, text: "sprach der Herr: Steh auf und tritt zum Aufbruch an die Spitze des Volkes! Sie sollen in das Land hineinziehen und es in Besitz nehmen, das Land, von dem du weißt: Ich habe ihren Vätern geschworen, es ihnen zu geben.", footnotes: ["Ex 32,34; 33,1"] },
      { number: 12, text: "Und nun, Israel, was fordert der Herr, dein Gott, von dir außer dem einen: dass du den Herrn, deinen Gott, fürchtest, indem du auf allen seinen Wegen gehst, ihn liebst und dem Herrn, deinem Gott, mit ganzem Herzen und mit ganzer Seele dienst;", heading: "Bundestreue und Ernte: 10,12 - 11,21", footnotes: ["Mi 6,8; (12f) 6,4-25; 11,1", "12-11,12: In diesem Stück wechseln immer wieder Gebot und Begründung. In den Begründungen stehen der Reihe nach die wichtigsten Elemente der Heilsgeschichte. Zugleich wird der Gott Israels als Schöpfergott und Spender der Fruchtbarkeit gezeichnet."] },
      { number: 13, text: "dass du ihn fürchtest, indem du auf die Gebote des Herrn und seine Gesetze achtest, auf die ich dich heute verpflichte. Dann wird es dir gut gehen." },
      { number: 14, text: "Sieh, dem Herrn, deinem Gott, gehören der Himmel, der Himmel über den Himmeln, die Erde und alles, was auf ihr lebt.", footnotes: ["Andere Übersetzungsmöglichkeit: Sieh, dem Herrn, deinem Gott, gehören der Himmel, ja, der hohe Himmel, und die Erde mit allem, was auf ihr lebt."] },
      { number: 15, text: "Doch nur deine Väter hat der Herr ins Herz geschlossen, nur sie hat er geliebt. Und euch, ihre Nachkommen, hat er später unter allen Völkern ausgewählt, wie es sich heute zeigt.", footnotes: ["7,6-8"] },
      { number: 16, text: "Ihr sollt die Vorhaut eures Herzens beschneiden und nicht länger halsstarrig sein.", footnotes: ["9,6.13; Jer 4,4; Röm 2,29"] },
      { number: 17, text: "Denn der Herr, euer Gott, ist der Gott über den Göttern und der Herr über den Herren. Er ist der große Gott, der Held und der Furchterregende. Er lässt kein Ansehen gelten und nimmt keine Bestechung an.", footnotes: ["19,16; Apg 10,34; Röm 2,11; Gal 2,6; Eph 6,9; Kol 3,25; 1 Tim 6,15; 1 Petr 1,17; Offb 17,14", "17f: Hier werden die wichtigsten Elemente des altorientalischen Königsideals auf Gott übertragen. Andere Übersetzungsmöglichkeit: Denn der Herr, euer Gott, ist der wahre Gott und der wahre Herrscher."] },
      { number: 18, text: "Er verschafft Waisen und Witwen ihr Recht. Er liebt die Fremden und gibt ihnen Nahrung und Kleidung -" },
      { number: 19, text: "auch ihr sollt die Fremden lieben, denn ihr seid Fremde in Ägypten gewesen.", footnotes: ["Ex 22,20; 23,9"] },
      { number: 20, text: "Du sollst den Herrn, deinen Gott, fürchten. Ihm sollst du dienen, an ihm sollst du dich festhalten, bei seinem Namen sollst du schwören.", footnotes: ["6,13; (20-22) 8,2-6", "«Sich fest halten», meint die Loyalität gegenüber einem Oberherrn (vgl. 2 Sam 20,2)."] },
      { number: 21, text: "Er ist dein Lobgesang, er ist dein Gott. Für dich hat er all das Große und Furchterregende getan, das du mit eigenen Augen gesehen hast.", footnotes: ["29,1"] },
      { number: 22, text: "Als deine Vorfahren nach Ägypten zogen, waren sie nur siebzig an der Zahl; jetzt aber hat der Herr, dein Gott, dich so zahlreich gemacht wie die Sterne am Himmel.", footnotes: ["Gen 46,27; Ex 1,5; Apg 7,14; Hebr 11,12"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Darum sollst du den Herrn, deinen Gott, lieben und dein Leben lang auf seine Dienstordnung, auf seine Gesetze, Rechtsvorschriften und Gebote achten.", footnotes: ["5,10; 6,5; 7,9; 10,12; 11,13.22; 13,4; 19,9; 30,6.16.20; (1-7) 8,2-6"] },
      { number: 2, text: "Heute sollt ihr erkennen, dass der Herr euch erzogen hat. Denn nicht eure Kinder, die die Erziehung durch den Herrn, euren Gott, nicht kennen gelernt und nicht miterlebt haben, sondern ihr selbst habt alle großen Taten, die der Herr getan hat, mit eigenen Augen gesehen, seine Macht, seine starke Hand und seinen hoch erhobenen Arm,", footnotes: ["(2/7) 29,1", "2/7: In V. 2 scheint ein Stück des ursprünglichen Textes durch ein Abschreibversehen verloren gegangen zu sein. Deshalb ist der erste Satz entsprechend dem ähnlichen Vers 8,5 ergänzt. Die deutsche Sprache erfordert es, V. 7 zu V. 2 vorzuziehen."] },
      { number: 3, text: "seine Zeichen und seine Taten: was er in Ägypten mit dem Pharao, dem König von Ägypten, und mit seinem ganzen Land getan hat;", footnotes: ["Ex 7-15"] },
      { number: 4, text: "was er mit dem ägyptischen Heer, den Rossen und Streitwagen getan hat - das Wasser des Schilfmeers ließ der Herr über ihnen zusammenschlagen, als sie euch nachsetzten, und er riss sie in die Tiefe, sodass sie heute nicht mehr sind -;", footnotes: ["Ex 14f"] },
      { number: 5, text: "was er mit euch in der Wüste getan hat, bis ihr an diesen Ort gekommen seid;", footnotes: ["Ex 16; Num 21"] },
      { number: 6, text: "was er mit Datan und Abiram getan hat, den Söhnen Eliabs, des Sohnes Rubens - die Erde riss ihren Rachen auf und verschlang sie mit ihren Familien, ihren Zelten und ihrem ganzen Tross in der Mitte von ganz Israel.", footnotes: ["Num 16"] },
      { number: 8, text: "Daher sollt ihr auf das ganze Gebot, auf das ich dich heute verpflichte, achten, damit ihr stark seid und in das Land, in das ihr jetzt hinüberzieht, um es in Besitz zu nehmen, hineinziehen und es in Besitz nehmen könnt.", footnotes: ["16,20"] },
      { number: 9, text: "Ihr sollt darauf achten, damit ihr lange lebt in dem Land, von dem ihr wisst: Der Herr hat euren Vätern geschworen, es ihnen und ihren Nachkommen zu geben, ein Land, in dem Milch und Honig fließen.", footnotes: ["6,3"] },
      { number: 10, text: "Denn das Land, in das du hineinziehst, um es in Besitz zu nehmen, ist nicht wie das Land Ägypten, aus dem ihr ausgezogen seid. Dort musstest du, wenn der Same gesät war, den Boden künstlich bewässern wie in einem Gemüsegarten." },
      { number: 11, text: "Das Land, in das ihr jetzt hinüberzieht, um es in Besitz zu nehmen, ist ein Land mit Bergen und Tälern und es trinkt das Wasser, das als Regen vom Himmel fällt." },
      { number: 12, text: "Es ist ein Land, um das der Herr, dein Gott, sich kümmert. Stets ruhen auf ihm die Augen des Herrn, deines Gottes, vom Anfang des Jahres bis zum Ende des Jahres." },
      { number: 13, text: "Und wenn ihr auf meine Gebote hört, auf die ich euch heute verpflichte, wenn ihr also den Herrn, euren Gott, liebt und ihm mit ganzem Herzen und mit ganzer Seele dient,", footnotes: ["11,1; (13-17) 28", "13-15: Mose gebraucht teilweise das Ich der Gottesrede. Vielleicht spielt er auf einen bekannten Text an."] },
      { number: 14, text: "dann gebe ich eurem Land seinen Regen zur rechten Zeit, den Regen im Herbst und den Regen im Frühjahr, und du kannst Korn, Most und Öl ernten;", footnotes: ["Jak 5,7"] },
      { number: 15, text: "dann gebe ich deinem Vieh sein Gras auf dem Feld und du kannst essen und satt werden." },
      { number: 16, text: "Aber nehmt euch in Acht! Lasst euer Herz nicht verführen, weicht nicht vom Weg ab, dient nicht anderen Göttern und werft euch nicht vor ihnen nieder!" },
      { number: 17, text: "Sonst wird der Zorn des Herrn gegen euch entbrennen; er wird den Himmel zuschließen, es wird kein Regen fallen, der Acker wird keinen Ertrag bringen und ihr werdet unverzüglich aus dem prächtigen Land getilgt sein, das der Herr euch geben will.", footnotes: ["28,23"] },
      { number: 18, text: "Diese meine Worte sollt ihr auf euer Herz und auf eure Seele schreiben. Ihr sollt sie als Zeichen um das Handgelenk binden. Sie sollen zum Schmuck auf eurer Stirn werden.", footnotes: ["(18-20) 6,6-9; 30,11-14", "Andere Übersetzungsmöglichkeit: Diese meine Worte sollt ihr auf euren Herzen und auf euren Seelen anbringen."] },
      { number: 19, text: "Ihr sollt sie eure Söhne lehren, indem ihr von ihnen redet, wenn du zu Hause sitzt und wenn du auf der Straße gehst, wenn du dich schlafen legst und wenn du aufstehst." },
      { number: 20, text: "Du sollst sie auf die Türpfosten deines Hauses und in deine Stadttore schreiben." },
      { number: 21, text: "So sollen die Tage, die ihr und eure Söhne in dem Land lebt, von dem ihr wisst: der Herr hat euren Vätern geschworen, es ihnen zu geben, so zahlreich werden wie die Tage, die der Himmel sich über der Erde wölbt." },
      { number: 22, text: "Wenn ihr auf dieses ganze Gebot, auf das ich euch heute verpflichte, genau achtet und es haltet, wenn ihr den Herrn, euren Gott, liebt, auf allen seinen Wegen geht und euch an ihm fest haltet,", heading: "Die Verheißung der Eroberung des Landes: 11,22-25", footnotes: ["11,1; (22-25) 18,12; Jos 1,1-9"] },
      { number: 23, text: "dann wird der Herr alle diese Völker vor euch vertreiben und ihr werdet den Besitz von Völkern übernehmen, die größer und mächtiger sind als ihr.", footnotes: ["7,1; 1,28; 9,1"] },
      { number: 24, text: "Jede Stelle, die euer Fuß berührt, soll euch gehören, von der Wüste an. Dazu soll der Libanon euer Gebiet sein, vom Strom, dem Eufrat, bis zum Meer im Westen.", footnotes: ["1,7; 19,8", "Im Hintergrund steht der Rechtsbrauch, zum Zeichen des Besitzantritts ein Grundstück zu betreten (vgl. Ps 60,10)."] },
      { number: 25, text: "Keiner wird eurem Angriff standhalten können. Dem ganzen Land, das ihr betretet, wird der Herr, euer Gott, Schrecken und Furcht vor euch ins Gesicht zeichnen, wie er es euch zugesagt hat.", footnotes: ["2,25; 28,10"] },
      { number: 26, text: "Seht, heute werde ich euch den Segen und den Fluch vorlegen:", heading: "Überleitung: 11,26-32", footnotes: ["(26-28) 28"] },
      { number: 27, text: "den Segen, weil ihr auf die Gebote des Herrn, eures Gottes, auf die ich euch heute verpflichte, hört," },
      { number: 28, text: "und den Fluch für den Fall, dass ihr nicht auf die Gebote des Herrn, eures Gottes, hört, sondern von dem Weg abweicht, den ich euch heute vorschreibe, und anderen Göttern nachfolgt, die ihr früher nicht gekannt habt." },
      { number: 29, text: "Und wenn der Herr, dein Gott, dich in das Land geführt hat, in das du jetzt hineinziehst, um es in Besitz zu nehmen, dann sollst du auf dem Berg Garizim den Segen und auf dem Berg Ebal den Fluch verkünden.", footnotes: ["27,2-26"] },
      { number: 30, text: "[Liegen denn beide nicht jenseits des Jordan, hinter der Straße, im Westen, im Gebiet der Kanaaniter, die in der Araba wohnen, gegenüber Gilgal bei den Orakel-Eichen?]", footnotes: ["Dieser späte Zusatz verlegt Garizim und Ebal in die Nähe von Gilgal. Vermutlich stammt er aus antisamaritanischer Tendenz."] },
      { number: 31, text: "Wenn ihr jetzt den Jordan überschreitet, um in das Land, das der Herr, euer Gott, euch gibt, hineinzuziehen und es in Besitz zu nehmen, und wenn ihr es in Besitz genommen habt und es bewohnt,", footnotes: ["(31-32) 12-26"] },
      { number: 32, text: "dann sollt ihr auf alle Gesetze und Rechtsvorschriften, die ich euch heute vorlege, achten und sie halten.", footnotes: ["5,1"] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Das sind die Gesetze und Rechtsvorschriften, auf die ihr achten und die ihr halten sollt in dem Land, das der Herr, der Gott deiner Väter, dir gegeben hat, damit du es in Besitz nimmst. Sie sollen so lange gelten, wie ihr in dem Land leben werdet.", heading: "Die deuteronomische Gesetzessammlung: 12,1 - 26,19 | Einleitung: 12,1", footnotes: ["4,5; 5,1; 31,13"] },
      { number: 2, text: "Ihr sollt alle Kultstätten zerstören, an denen die Völker, deren Besitz ihr übernehmt, ihren Göttern gedient haben: auf den hohen Bergen, auf den Hügeln und unter jedem üppigen Baum.", heading: "Die Vernichtung fremder Kultstätten: 12,2f", footnotes: ["1 Kön 14,23; 2 Kön 16,4; 17,10; Jer 2,20; Hos 4,13"] },
      { number: 3, text: "Ihr sollt ihre Altäre niederreißen und ihre Steinmale zerschlagen. Ihre Kultpfähle sollt ihr im Feuer verbrennen und die Bilder ihrer Götter umhauen. Ihre Namen sollt ihr an jeder solchen Stätte tilgen.", footnotes: ["7,5.25; Ex 23,24; 34,13"] },
      { number: 4, text: "Ihr sollt nicht das Gleiche tun (wie diese Völker), wenn ihr den Herrn, euren Gott, verehrt,", heading: "Eine einzige Kultstätte für Israel: 12,4-7", footnotes: ["(4-28) Ex 20,24; 2 Kön 18,22; Joh 4,20"] },
      { number: 5, text: "sondern ihr sollt nach der Stätte fragen, die der Herr, euer Gott, aus allen euren Stammesgebieten auswählen wird, indem er dort seinen Namen anbringt. Nach seiner Wohnung sollt ihr fragen und dorthin sollst du ziehen.", footnotes: ["12,21; 14,24; 1 Kön 9,3; 11,36; 14,21; 2 Kön 21,4.7; 1 Kön 6,13; 8,12", "Ursprünglich lautete der Text vermutlich: indem er dort seinen Namen anbringt, um ihn (dort) wohnen zu lassen. Dorthin sollst du ziehen. - Die Anbringung des Namens an einem Ort, etwa durch eine königliche Inschrift, war im Alten Orient Zeichen der Besitzergreifung und Herrschaft. Das Gleiche meinte der Ausdruck «seinen Namen an einem Ort wohnen lassen». Beide Wendungen werden in den deuteronomischen Gesetzen nebeneinander gebraucht. Die Rede vom Wohnenlassen des Namens (vgl. 12,11; 14,23; 16,2.6.11; 26,2) wehrt zugleich die Vorstellung ab, Gott selbst wohne im Tempel (vgl. 1 Kön 8,12f; im Tempel wohnt nur Gottes Name. Das bedeutet: Wenn dieser Name im Tempel angerufen wird, erhört Gott das Gebet. Gott selbst wohnt im Himmel (vgl. 26,15)."] },
      { number: 6, text: "Dorthin sollt ihr eure Brandopfertiere und Schlachtopfertiere bringen, eure Zehnten und Handerhebungsopfer, was ihr dem Herrn gelobt habt und was ihr freiwillig gebt, und die Erstlinge eurer Rinder, Schafe und Ziegen." },
      { number: 7, text: "Dort sollt ihr vor dem Herrn, eurem Gott, das Mahl halten. Ihr sollt fröhlich sein, ihr und eure Familien, aus Freude über alles, was eure Hände geschafft haben, weil der Herr, dein Gott, dich gesegnet hat." },
      { number: 8, text: "Ihr sollt nicht tun, was jeder Einzelne für richtig hält, wie es hier bei uns heute noch geschieht.", heading: "Der Zeitpunkt des Inkrafttretens: 12,8-12" },
      { number: 9, text: "Denn ihr seid bis jetzt nicht in die Ruhe und in den Erbbesitz eingezogen, die der Herr, dein Gott, dir gibt." },
      { number: 10, text: "Wenn ihr aber den Jordan überschritten habt und in dem Land wohnt, das der Herr, euer Gott, an euch als Erbbesitz verteilt, wenn er euch Ruhe vor allen euren Feinden ringsum verschafft hat und ihr in Sicherheit wohnt,", footnotes: ["2 Sam 7,1.11; 1 Kön 5,5"] },
      { number: 11, text: "dann sollt ihr alles, wozu ich euch verpflichte, an die Stätte bringen, die der Herr, euer Gott, auswählt, indem er dort seinen Namen wohnen lässt: eure Brandopfertiere und Schlachtopfertiere, eure Zehnten und Handerhebungsopfer und alle eure auserlesenen Gaben, die ihr dem Herrn gelobt habt.", footnotes: ["14,23; 16,2.6.11; 26,2; Jer 7,12"] },
      { number: 12, text: "Dort sollt ihr vor dem Herrn, eurem Gott, fröhlich sein, ihr, eure Söhne und Töchter, eure Sklaven und Sklavinnen sowie die Leviten, die in euren Stadtbereichen Wohnrecht haben; denn der Levit hat nicht wie ihr Landanteil und Erbbesitz.", footnotes: ["10,9"] },
      { number: 13, text: "Nimm dich in Acht! Verbrenn deine Brandopfertiere nicht an irgendeiner Stätte, die dir gerade vor die Augen kommt,", heading: "Die Unterscheidung von Opfer und Schlachtung: 12,13-19" },
      { number: 14, text: "sondern nur an der Stätte, die der Herr im Gebiet eines deiner Stämme auswählt. Dort sollst du deine Brandopfertiere verbrennen und dort sollst du alles ausführen, wozu ich dich verpflichte." },
      { number: 15, text: "Doch darfst du immer, wenn du es möchtest, und überall in deinen Stadtbereichen schlachten und Fleisch essen, so weit der Segen reicht, den dir der Herr, dein Gott, geschenkt hat. Jeder, der Reine wie der Unreine, darf davon essen, wie bei Gazelle und Damhirsch.", footnotes: ["(15-16) 15,22f; 14,5"] },
      { number: 16, text: "Das Blut aber sollt ihr nicht genießen, sondern wie Wasser auf die Erde schütten." },
      { number: 17, text: "Auch darfst du in deinen Stadtbereichen nicht den Zehnten von Korn, Wein und Öl verzehren, die Erstlinge von Rindern, Schafen und Ziegen, alle Gaben, die du dem Herrn gelobt hast, die freiwilligen Gaben und deine Handerhebungsopfer.", footnotes: ["(17-18) 14,22-27"] },
      { number: 18, text: "Vor dem Herrn, deinem Gott, sollst du sie verzehren, an der Stätte, die der Herr, dein Gott, auswählen wird - du, dein Sohn und deine Tochter, dein Sklave und deine Sklavin sowie die Leviten, die in deinen Stadtbereichen Wohnrecht haben -, und du sollst vor dem Herrn, deinem Gott, fröhlich sein und dich freuen über alles, was deine Hände geschafft haben." },
      { number: 19, text: "Nimm dich in Acht, und lass die Leviten nicht im Stich, solange du im Land lebst.", footnotes: ["14,27"] },
      { number: 20, text: "Wenn der Herr, dein Gott, dein Gebiet vergrößert, wie er es dir zugesagt hat, und du, weil du Appetit auf Fleisch hast, sagst: Ich möchte gern Fleisch essen, dann darfst du so viel Fleisch essen, wie du möchtest.", heading: "Das Blut bei Opfer und Schlachtung: 12,20-28", footnotes: ["19,8"] },
      { number: 21, text: "Und wenn die Entfernung zu der Stätte, die der Herr, dein Gott, auswählen wird, indem er dort seinen Namen anbringt, zu groß ist, dann schlachte, so wie ich es dir erlaubt habe, Rinder, Schafe oder Ziegen, die der Herr dir geschenkt hat, und iss in deinen Stadtbereichen, so viel du möchtest.", footnotes: ["12,5; 14,24"] },
      { number: 22, text: "Auch sollst du davon essen, wie man von Gazelle und Damhirsch isst: Der Reine und der Unreine dürfen gemeinsam davon essen.", footnotes: ["(22-25) 15,22f"] },
      { number: 23, text: "Doch beherrsche dich und genieße kein Blut; denn Blut ist Lebenskraft und du sollst nicht zusammen mit dem Fleisch die Lebenskraft verzehren." },
      { number: 24, text: "Du sollst es nicht genießen, sondern wie Wasser auf die Erde schütten." },
      { number: 25, text: "Du sollst es nicht genießen, damit es dir und später deinen Söhnen gut geht, weil du tust, was in den Augen des Herrn richtig ist." },
      { number: 26, text: "Die heiligen Abgaben jedoch, zu denen du verpflichtet bist, und die Gaben, die du selbst dem Herrn gelobt hast, sollst du aufladen und du sollst damit zu der Stätte ziehen, die der Herr auswählen wird." },
      { number: 27, text: "Dort sollst du deine Brandopfertiere auf dem Altar des Herrn, deines Gottes, darbringen, das Fleisch und das Blut. Bei deinen Schlachtopfertieren soll das Blut auf den Altar des Herrn, deines Gottes, geschüttet werden, das Fleisch sollst du essen." },
      { number: 28, text: "Achte und höre auf alle diese Worte, auf die ich dich verpflichte, damit es dir und später deinen Söhnen immer gut geht, weil du tust, was in den Augen des Herrn, deines Gottes, gut und richtig ist." },
      { number: 29, text: "Wenn der Herr, dein Gott, die Völker, in deren Land du hineinziehst, um ihren Besitz zu übernehmen, vor dir niedergestreckt hat, wenn du ihren Besitz übernommen hast und dort wohnst,", heading: "Das Verbot kanaanäischer Kultbräuche: 12,29-31", footnotes: ["(29-31) 7"] },
      { number: 30, text: "dann nimm dich in Acht! Lauf nicht hinter ihnen her in die Falle, nachdem sie bei deinem Angriff vernichtet worden sind. Erkundige dich nicht nach ihren Göttern und frag nicht: Wie dienen denn diese Völker ihren Göttern? Ich will das Gleiche tun wie sie.", footnotes: ["Ex 23,33; 34,12"] },
      { number: 31, text: "Wenn du dem Herrn, deinem Gott, dienst, sollst du nicht das Gleiche tun wie sie; denn sie haben, wenn sie ihren Göttern dienten, alle Gräuel begangen, die der Herr hasst. Sie haben sogar ihre Söhne und Töchter im Feuer verbrannt, wenn sie ihren Göttern dienten.", footnotes: ["18,9f; 2 Kön 17,31; Jer 7,31; 19,5"] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Ihr sollt auf den vollständigen Wortlaut dessen, worauf ich euch verpflichte, achten und euch daran halten. Ihr sollt nichts hinzufügen und nichts wegnehmen.", heading: "Das Verbot einer Änderung dieses Gesetzes: 13,1", footnotes: ["4,2; Offb 22,18f", "Manche Bibelausgaben zählen diesen Vers als 12,32; entsprechend ändert sich die Verszählung in Kap. 13."] },
      { number: 2, text: "Wenn in deiner Mitte ein Prophet oder ein Traumseher auftritt und dir ein Zeichen oder Wunder ankündigt,", heading: "Die Anstiftung zum Abfall durch Propheten und Traumseher: 13,2-6", footnotes: ["Jer 23,25-32; (2-19) 5,6-9; Ex 22,19; (2-4) Mt 24,24; Mk 13,22; 2 Petr 2,1; Offb 13,14"] },
      { number: 3, text: "wobei er sagt: Folgen wir anderen Göttern nach, die du bisher nicht kanntest, und verpflichten wir uns, ihnen zu dienen!, und wenn das Zeichen und Wunder, das er dir angekündigt hatte, eintrifft,", footnotes: ["7,4; 28,14"] },
      { number: 4, text: "dann sollst du nicht auf die Worte dieses Propheten oder Traumsehers hören; denn der Herr, euer Gott, prüft euch, um zu erkennen, ob ihr das Volk seid, das den Herrn, seinen Gott, mit ganzem Herzen und mit ganzer Seele liebt.", footnotes: ["6,5; 8,2; 11,1; 1 Kor 11,19"] },
      { number: 5, text: "Ihr sollt dem Herrn, eurem Gott, nachfolgen, ihn sollt ihr fürchten, auf seine Gebote sollt ihr achten, auf seine Stimme sollt ihr hören, ihm sollt ihr dienen, an ihm sollt ihr euch fest halten.", footnotes: ["10,20; 2 Kön 23,3"] },
      { number: 6, text: "Der Prophet oder Traumseher aber soll mit dem Tod bestraft werden. Er hat euch aufgewiegelt gegen den Herrn, euren Gott, der euch aus Ägypten geführt und dich aus dem Sklavenhaus freigekauft hat. Denn er wollte dich davon abbringen, auf dem Weg zu gehen, den der Herr, dein Gott, dir vorgeschrieben hat. Du sollst das Böse aus deiner Mitte wegschaffen.", footnotes: ["21,21; 1 Kor 5,13", "«Du sollst das Böse aus deiner Mitte wegschaffen» und ähnliche Wendungen stehen am Schluss mehrerer Gesetze, die inhaltlich den Zehn Geboten zuzuordnen sind. Daraus ersieht man, dass die Zehn Gebote ursprünglich eine Liste der todeswürdigen Verbrechen in Israel waren."] },
      { number: 7, text: "Wenn dein Bruder, der dieselbe Mutter hat wie du, oder dein Sohn oder deine Tochter oder deine Frau, mit der du schläfst, oder dein Freund, den du liebst wie dich selbst, dich heimlich verführen will und sagt: Gehen wir und dienen wir anderen Göttern - (wobei er Götter meint,) die du und deine Vorfahren noch nicht kannten,", heading: "Die geheime Anstiftung zum Abfall: 13,7-12", footnotes: ["13,3", "dein Bruder, der dieselbe Mutter hat wie du: In einer polygamen Familie besteht eine besondere Zuneigung zwischen den Kindern der gleichen Mutter."] },
      { number: 8, text: "unter den Göttern der Völker, die in eurer Nachbarschaft wohnen, in der Nähe oder weiter entfernt, zwischen dem einen Ende der Erde und dem andern Ende der Erde -," },
      { number: 9, text: "dann sollst du nicht nachgeben und nicht auf ihn hören. Du sollst in dir kein Mitleid mit ihm aufsteigen lassen, sollst keine Nachsicht für ihn kennen und die Sache nicht vertuschen.", footnotes: ["19,13"] },
      { number: 10, text: "Sondern du sollst ihn anzeigen. Wenn er hingerichtet wird, sollst du als Erster deine Hand gegen ihn erheben, dann erst das ganze Volk.", footnotes: ["17,7", "So nach G; H: sondern du sollst ihn töten."] },
      { number: 11, text: "Du sollst ihn steinigen und er soll sterben; denn er hat versucht, dich vom Herrn, deinem Gott, abzubringen, der dich aus Ägypten geführt hat, aus dem Sklavenhaus." },
      { number: 12, text: "Ganz Israel soll davon hören, damit sie sich fürchten und nicht noch einmal einen solchen Frevel in deiner Mitte begehen.", footnotes: ["21,21"] },
      { number: 13, text: "Wenn du aus einer deiner Städte, die der Herr, dein Gott, dir als Wohnort gibt, erfährst:", heading: "Der Abfall einer Stadt: 13,13-19" },
      { number: 14, text: "Niederträchtige Menschen sind aus deiner Mitte herausgetreten und haben ihre Mitbürger vom Herrn abgebracht, indem sie sagten: Gehen wir und dienen wir anderen Göttern, die ihr bisher nicht kanntet!,", footnotes: ["13,3"] },
      { number: 15, text: "wenn du dann durch Augenschein und Vernehmung genaue Ermittlungen angestellt hast und sich gezeigt hat: Ja, es ist wahr, der Tatbestand steht fest, dieser Gräuel ist in deiner Mitte geschehen,", footnotes: ["19,18"] },
      { number: 16, text: "dann sollst du die Bürger dieser Stadt mit scharfem Schwert erschlagen, du sollst an der Stadt und an allem, was darin lebt, auch am Vieh, mit scharfem Schwert die Vernichtungsweihe vollstrecken.", footnotes: ["Jos 8,28; (16-17) 20,16-18"] },
      { number: 17, text: "Alles, was du in der Stadt erbeutet hast, sollst du auf dem Marktplatz aufhäufen, dann sollst du die Stadt und die gesamte Beute als Ganzopfer für den Herrn, deinen Gott, im Feuer verbrennen. Für immer soll sie ein Schutthügel bleiben und nie wieder aufgebaut werden." },
      { number: 18, text: "Von dem, was der Vernichtung geweiht war, soll nichts in deiner Hand zurückbleiben, damit der Herr von seinem glühenden Zorn ablässt und dir wieder sein Erbarmen schenkt, sich deiner annimmt und dich wieder zahlreich macht, wie er es deinen Vätern geschworen hat", footnotes: ["Jos 7"] },
      { number: 19, text: "für den Fall, dass du auf die Stimme des Herrn, deines Gottes, hörst, auf alle seine Gebote, auf die ich dich heute verpflichte, achtest und tust, was in den Augen des Herrn, deines Gottes, richtig ist." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Ihr seid Kinder des Herrn, eures Gottes. Ihr sollt euch für einen Toten nicht wund ritzen und keine Stirnglatzen scheren.", heading: "Das Verbot kanaanäischer Trauerbräuche: 14,1-2", footnotes: ["(1-2) Mt 5,45; 6,9; Lk 6,35; 11,2"] },
      { number: 2, text: "Denn du bist ein Volk, das dem Herrn, deinem Gott, heilig ist, und dich hat der Herr ausgewählt, damit du unter allen Völkern, die auf der Erde leben, das Volk wirst, das ihm persönlich gehört.", footnotes: ["7,6; 26,18f; Tit 2,14; 1 Petr 2,9"] },
      { number: 3, text: "Du sollst nichts essen, was ein Gräuel ist.", heading: "Die Speiseverbote: 14,3-21a", footnotes: ["(3-21a) Lev 11"] },
      { number: 4, text: "Dies sind die Großtiere, die ihr essen dürft: Rind, Lamm, Zicklein,", footnotes: ["4-18: Die Übersetzung der Tiernamen ist nicht immer sicher."] },
      { number: 5, text: "Damhirsch, Gazelle, Rehbock, Wildziege, Wisent, Wildschaf und Steinbock." },
      { number: 6, text: "Ihr dürft jedes Großtier essen, das gespaltene Klauen hat, und zwar ganz gespaltene Klauen, und das zu den Wiederkäuern gehört." },
      { number: 7, text: "Von den Großtieren, die wiederkäuen oder ganz gespaltene Klauen haben, dürft ihr aber Folgende nicht essen: Kamel, Hase, Klippdachs. Sie sind zwar Wiederkäuer, haben aber keine gespaltenen Klauen. Sie sollen euch als unrein gelten." },
      { number: 8, text: "Ebenso das Wildschwein, denn es hat zwar gespaltene Klauen, ist aber kein Wiederkäuer. Es soll euch als unrein gelten. Vom Fleisch dieser Tiere dürft ihr nicht essen und ihr Aas dürft ihr nicht berühren.", footnotes: ["8.13: In beiden Versen liegt ein sehr früh verderbter Text vor; die Wiederherstellung ist unsicher."] },
      { number: 9, text: "Von allem, was im Wasser lebt, dürft ihr Folgendes essen: Alles, was Flossen und Schuppen hat, dürft ihr essen." },
      { number: 10, text: "Alles, was keine Flossen und keine Schuppen hat, dürft ihr nicht essen. Es soll euch als unrein gelten." },
      { number: 11, text: "Alle reinen Vögel dürft ihr essen." },
      { number: 12, text: "Dies sind die Vögel, die ihr nicht essen dürft: Aasgeier, Schwarzgeier, Bartgeier," },
      { number: 13, text: "Milan, die verschiedenen Bussardarten," },
      { number: 14, text: "alle Arten des Raben," },
      { number: 15, text: "Adlereule, Kurzohreule, Langohreule und die verschiedenen Falkenarten," },
      { number: 16, text: "Kauz, Bienenfresser, Weißeule," },
      { number: 17, text: "Kleineule, Fischadler, Fischeule," },
      { number: 18, text: "Storch und die verschiedenen Reiherarten, Wiedehopf, Fledermaus" },
      { number: 19, text: "und alles fliegende Kleingetier: Sie sollen euch als unrein gelten und dürfen nicht gegessen werden." },
      { number: 20, text: "Alle reinen geflügelten Tiere dürft ihr essen." },
      { number: 21, text: "Ihr dürft keinerlei Aas essen. Du sollst es dem Fremden, der in euren Stadtbereichen Wohnrecht hat, zum Essen überlassen oder es einem Ausländer verkaufen. Denn du bist ein Volk, das dem Herrn, deinem Gott, heilig ist.", footnotes: ["Ex 22,30; 7,6"] },
      { number: 21, text: "Du sollst ein Zicklein nicht in der Milch seiner Mutter kochen.", heading: "Das Verbot eines Fruchtbarkeitszaubers: 14,21b", footnotes: ["Ex 23,19; 34,26"] },
      { number: 22, text: "Du sollst jedes Jahr den Zehnten von der gesamten Ernte geben, die dein Acker erbringt aus dem, was du angebaut hast.", heading: "Die jährlichen Abgaben an das Heiligtum: 14,22-27", footnotes: ["(22-27) 12,17-19"] },
      { number: 23, text: "Vor dem Herrn, deinem Gott, sollst du an der Stätte, die er auswählt, indem er dort seinen Namen wohnen lässt, deinen Zehnten an Korn, Wein und Öl und die Erstlinge deiner Rinder, Schafe und Ziegen verzehren, damit du lernst, den Herrn, deinen Gott, zu fürchten, solange du lebst.", footnotes: ["12,11; 15,19-23"] },
      { number: 24, text: "Wenn aber der Weg dorthin deine Kräfte übersteigt, weil die Stätte, die der Herr auswählt, indem er dort seinen Namen anbringt, so weit entfernt liegt und der Herr, dein Gott, dich so gesegnet hat, dass du den Zehnten nicht dorthin tragen kannst,", footnotes: ["12,5.21"] },
      { number: 25, text: "dann sollst du alles für Silber verkaufen, das Silber als deinen Besitz zusammenbinden, zu der Stätte ziehen, die der Herr, dein Gott, auswählt," },
      { number: 26, text: "dort für das Silber alles kaufen, worauf du Appetit hast - Rinder, Schafe, Ziegen, Wein und Bier, alles, wonach es deinen Gaumen verlangt -, und dann sollst du vor dem Herrn, deinem Gott, Mahl halten und fröhlich sein, du und deine Familie." },
      { number: 27, text: "Auch sollst du die Leviten, die in deinen Stadtbereichen Wohnrecht haben, nicht im Stich lassen, denn sie haben nicht wie du Landanteil und Erbbesitz.", footnotes: ["10,9"] },
      { number: 28, text: "In jedem dritten Jahr sollst du den ganzen Zehnten deiner Jahresernte in deinen Stadtbereichen abliefern und einlagern", heading: "Der Zehnt für die Armen in jedem dritten Jahr: 14,28f", footnotes: ["(28-29) 26,12-15", "Wahrscheinlich in jedem dritten Jahr nach einem Sabbatjahr; denn im Sabbatjahr gehörten alle Erträge den Armen (vgl. Ex 23,11)."] },
      { number: 29, text: "und die Leviten, die ja nicht wie du Landanteil und Erbbesitz haben, die Fremden, die Waisen und die Witwen, die in deinen Stadtbereichen wohnen, können kommen, essen und satt werden, damit der Herr, dein Gott, dich stets segnet bei der Arbeit, die deine Hände tun.", footnotes: ["10,9; 28,12"] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "In jedem siebten Jahr sollst du die Ackerbrache einhalten.", heading: "Der Verzicht auf Forderungen in jedem siebten Jahr: 15,1-6", footnotes: ["(1-11) 15,9.12; 31,10; Ex 23,10f", "1-6: Das Gesetz über den Verzicht auf Forderungen in jedem siebten Jahr wird als Weiterentwicklung des alten Brauchs der Ackerbrache im siebten Jahr und zum Teil mit den dorthin gehörenden Ausdrücken formuliert."] },
      { number: 2, text: "Und so lautet eine Bestimmung für die Brache: Jeder Gläubiger soll den Teil seines Vermögens, den er einem andern unter Personalhaftung als Darlehen gegeben hat, brachliegen lassen. Er soll gegen den andern, falls dieser sein Bruder ist, nicht mit Zwang vorgehen; denn er hat die Brache für den Herrn verkündet." },
      { number: 3, text: "Gegen einen Ausländer darfst du mit Zwang vorgehen. Wenn es sich aber um deinen Bruder handelt, dann lass deinen Vermögensteil brachliegen!", footnotes: ["23,20f"] },
      { number: 4, text: "Doch eigentlich sollte es bei dir gar keine Armen geben; denn der Herr wird dich reich segnen in dem Land, das der Herr, dein Gott, dir als Erbbesitz gibt und das du in Besitz nimmst,", footnotes: ["(4-6) 28,1-13"] },
      { number: 5, text: "wenn du auf die Stimme des Herrn, deines Gottes, hörst, auf dieses Gebot, auf das ich dich heute verpflichte, achtest und es hältst." },
      { number: 6, text: "Wenn der Herr, dein Gott, dich segnet, wie er es dir zugesagt hat, dann kannst du vielen Völkern gegen Pfand leihen, du selbst aber brauchst nichts zu verpfänden; du wirst über viele Völker Gewalt haben, über dich aber werden sie keine Gewalt haben.", footnotes: ["28,12"] },
      { number: 7, text: "Wenn bei dir ein Armer lebt, irgendeiner deiner Brüder in irgendeinem deiner Stadtbereiche in dem Land, das der Herr, dein Gott, dir gibt, dann sollst du nicht hartherzig sein und sollst deinem armen Bruder deine Hand nicht verschließen.", heading: "Die Kredithilfen an arme Israeliten: 15,7-11", footnotes: ["(7-8) 1 Joh 3,17"] },
      { number: 8, text: "Du sollst ihm deine Hand öffnen und ihm gegen Pfand leihen, was der Not, die ihn bedrückt, abhilft." },
      { number: 9, text: "Nimm dich in Acht, dass du nicht in niederträchtigem Herzen den Gedanken hegst: Bald kommt das siebte Jahr, das Brachjahr!, und deinen armen Bruder böse ansiehst und ihm nichts gibst, sodass er den Herrn gegen dich anruft und Strafe für diese Sünde über dich kommt.", footnotes: ["15,1"] },
      { number: 10, text: "Du sollst ihm etwas geben, und wenn du ihm gibst, soll auch dein Herz nicht böse darüber sein; denn wegen dieser Tat wird dich der Herr, dein Gott, segnen in allem, was du arbeitest, und in allem, was deine Hände schaffen." },
      { number: 11, text: "Die Armen werden niemals ganz aus deinem Land verschwinden. Darum mache ich dir zur Pflicht: Du sollst deinem Not leidenden und armen Bruder, der in deinem Land lebt, deine Hand öffnen.", footnotes: ["Mt 26,11; Mk 14,7; Joh 12,8"] },
      { number: 12, text: "Wenn dein Bruder, ein Hebräer - oder auch eine Hebräerin -, sich dir verkauft, soll er dir sechs Jahre als Sklave dienen. Im siebten Jahr sollst du ihn als freien Mann entlassen.", heading: "Die Selbstverknechtung: 15,12-18", footnotes: ["15,1; Joh 8,35; (12-18) Ex 21,2-11; Jer 34,8-22"] },
      { number: 13, text: "Und wenn du ihn als freien Mann entlässt, sollst du ihn nicht mit leeren Händen entlassen." },
      { number: 14, text: "Du sollst ihm von deinen Schafen und Ziegen, von deiner Tenne und von deiner Kelter so viel mitgeben, wie er tragen kann. Wie der Herr, dein Gott, dich gesegnet hat, so sollst du ihn bedenken.", footnotes: ["Andere Lesart: Womit der Herr, dein Gott, dich gesegnet hat, damit sollst du ihn bedenken."] },
      { number: 15, text: "Denk daran: Als du in Ägypten Sklave warst, hat der Herr, dein Gott, dich freigekauft. Darum verpflichte ich dich heute auf dieses Gebot.", footnotes: ["5,15"] },
      { number: 16, text: "Wenn dieser Sklave dir aber erklärt: Ich will nicht von dir freigelassen werden - denn er hat dich und deine Familie lieb gewonnen, weil es ihm bei dir gut ging -," },
      { number: 17, text: "so nimm einen Pfriem und stich ihn durch sein Ohr in die Tür: Dann ist er dein Sklave für immer. Bei einer Sklavin sollst du das Gleiche tun." },
      { number: 18, text: "Halt es nicht für eine Härte, wenn du ihn als freien Mann entlassen musst; denn was er in den sechs Jahren für dich erarbeitet hat, entspricht dem, was du einem Tagelöhner als Lohn hättest zahlen müssen. Dann wird der Herr, dein Gott, dich in allem segnen, was du tust." },
      { number: 19, text: "Alle männlichen Erstlinge, die unter deinen Rindern, Schafen und Ziegen geboren werden, sollst du dem Herrn, deinem Gott, weihen. Mit einem Erstlingsstier darfst du nicht arbeiten und ein Erstlingsschaf darfst du nicht scheren.", heading: "Die Erstgeburt des Viehs: 15,19-23", footnotes: ["(19-23) Ex 13,2.11-16; 22,29; 34,19"] },
      { number: 20, text: "Jahr für Jahr sollst du die Erstlinge vor dem Herrn, deinem Gott, an der Stätte, die der Herr auswählt, essen, du und deine Familie.", footnotes: ["14,23"] },
      { number: 21, text: "Doch wenn ein Tier einen Fehler hat, wenn es hinkt oder blind ist, wenn es irgendeinen Makel hat, sollst du es nicht für den Herrn, deinen Gott, schlachten." },
      { number: 22, text: "In deinen Stadtbereichen sollst du es verzehren; der Reine und der Unreine können es gemeinsam essen, wie bei Gazelle und Damhirsch.", footnotes: ["(22-23) 12,15f.22-25"] },
      { number: 23, text: "Nur das Blut sollst du nicht genießen, sondern wie Wasser auf die Erde schütten." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Achte auf den Monat Abib und feiere dem Herrn, deinem Gott, das Paschafest; denn im Monat Abib hat der Herr, dein Gott, dich nachts aus Ägypten geführt.", heading: "Das Frühlingsfest: 16,1-8", footnotes: ["(1-17) Ex 23,14-17; 34,18-26; (1-8) Lk 2,41; Ex 12; 2 Kön 23,21", "Andere Übersetzungsmöglichkeit: Achte auf den Neumond im Abib! Der Monat Abib entspricht dem März/April unseres Kalenders."] },
      { number: 2, text: "Als Paschatiere für den Herrn, deinen Gott, sollst du Schafe, Ziegen oder Rinder schlachten an der Stätte, die der Herr auswählen wird, indem er dort seinen Namen wohnen lässt.", footnotes: ["12,11"] },
      { number: 3, text: "Du sollst nichts Gesäuertes dazu essen. Sieben Tage lang sollst du ungesäuertes Brot dazu essen, die Speise der Bedrängnis, damit du dein ganzes Leben lang des Tages gedenkst, an dem du aus Ägypten gezogen bist. Denn in Hast bist du aus Ägypten gezogen.", footnotes: ["(3-4) 26,7; Ex 3,7; 12,11; 13,3-10; 23,18; 34,25; 1 Kor 5,8"] },
      { number: 4, text: "In deinem ganzen Gebiet soll sieben Tage lang kein Sauerteig zu finden sein und vom Fleisch des Tieres, das du am Abend des ersten Tages schlachtest, darf bis zum andern Morgen nichts übrig bleiben." },
      { number: 5, text: "Du darfst das Paschatier nicht in irgendeinem der Stadtbereiche schlachten, die der Herr, dein Gott, dir geben wird," },
      { number: 6, text: "sondern nur an der Stätte, die der Herr, dein Gott, auswählt, indem er dort seinen Namen wohnen lässt. Dort sollst du das Paschatier schlachten, am Abend bei Sonnenuntergang, zu der Stunde, in der du aus Ägypten gezogen bist.", footnotes: ["12,11"] },
      { number: 7, text: "Du sollst es an der Stätte kochen und verzehren, die der Herr, dein Gott, ausgewählt hat, und am Morgen darfst du wieder zu deinen Zelten zurückkehren." },
      { number: 8, text: "Sechs Tage sollst du ungesäuertes Brot essen, am siebten Tag ist eine Festversammlung für den Herrn, deinen Gott; da sollst du keine Arbeit tun." },
      { number: 9, text: "Du sollst sieben Wochen zählen. Wenn man die Sichel an den Halm legt, sollst du beginnen, die sieben Wochen zu zählen.", heading: "Das Wochenfest: 16,9-12", footnotes: ["(9-11) Apg 2,1; 1 Kor 16,8"] },
      { number: 10, text: "Danach sollst du dem Herrn, deinem Gott, das Wochenfest feiern und dabei eine freiwillige Gabe darbringen, die du danach bemisst, wie der Herr, dein Gott, dich gesegnet hat." },
      { number: 11, text: "Du sollst vor dem Herrn, deinem Gott, fröhlich sein, du, dein Sohn und deine Tochter, dein Sklave und deine Sklavin, auch die Leviten, die in deinen Stadtbereichen Wohnrecht haben, und die Fremden, Waisen und Witwen, die in deiner Mitte leben. Du sollst fröhlich sein an der Stätte, die der Herr, dein Gott, auswählt, indem er dort seinen Namen wohnen lässt.", footnotes: ["12,11"] },
      { number: 12, text: "Denk daran: Du bist in Ägypten Sklave gewesen. Daher sollst du auf diese Gesetze achten und sie halten.", footnotes: ["5,15"] },
      { number: 13, text: "Das Laubhüttenfest sollst du sieben Tage lang feiern, nachdem du das Korn von der Tenne und den Wein aus der Kelter eingelagert hast.", heading: "Das Laubhüttenfest: 16,13-15" },
      { number: 14, text: "Du sollst an deinem Fest fröhlich sein, du, dein Sohn und deine Tochter, dein Sklave und deine Sklavin, die Leviten und die Fremden, Waisen und Witwen, die in deinen Stadtbereichen wohnen.", footnotes: ["26,11"] },
      { number: 15, text: "Sieben Tage lang sollst du dem Herrn, deinem Gott, das Fest feiern an der Stätte, die der Herr auswählt. Wenn dich der Herr, dein Gott, in allem gesegnet hat, in deiner Ernte und in der Arbeit deiner Hände, dann sollst du wirklich fröhlich sein.", footnotes: ["28,12"] },
      { number: 16, text: "Dreimal im Jahr sollen alle deine Männer hingehen, um das Angesicht des Herrn, deines Gottes, an der Stätte, die er auswählt, zu schauen: am Fest der Ungesäuerten Brote, am Wochenfest und am Laubhüttenfest. Man soll nicht mit leeren Händen hingehen, um das Angesicht des Herrn zu schauen,", heading: "Die Verpflichtung zu Wallfahrten: 16,16f", footnotes: ["Ex 23,14-17; 34,18-23", "um das Angesicht des Herrn . . . zu schauen: so nach dem mutmaßlichen Urtext. In den erhaltenen Textzeugen ist zur Wahrung der Unsichtbarkeit Gottes der Text leicht abgeändert: um vor dem Angesicht des Herrn . . . zu erscheinen."] },
      { number: 17, text: "sondern jeder mit seiner Gabe, die dem Segen entspricht, den du vom Herrn, deinem Gott, erhalten hast." },
      { number: 18, text: "Richter und Listenführer sollst du in allen Stadtbereichen einsetzen, die der Herr, dein Gott, dir in deinen Stammesgebieten gibt. Sie sollen dem Volk Recht sprechen und gerechte Urteile fällen.", heading: "Das Verhalten der Richter in den Ortschaften: 16,18-20", footnotes: ["1,15; 29,5-9; 29,9; 31,28; (18-20) 1,16f; 2 Chr 19,5-7"] },
      { number: 19, text: "Du sollst das Recht nicht beugen. Du sollst kein Ansehen der Person kennen. Du sollst keine Bestechung annehmen; denn Bestechung macht Weise blind und verdreht die Fälle derer, die im Recht sind.", footnotes: ["(19-20) Ex 23,1-3.6-8; Jak 2,9"] },
      { number: 20, text: "Gerechtigkeit, Gerechtigkeit - ihr sollst du nachjagen, damit du Leben hast und das Land in Besitz nehmen kannst, das der Herr, dein Gott, dir gibt.", footnotes: ["6,18f; 8,1; 11,8; Jos 23,5"] },
      { number: 21, text: "Du sollst neben dem Altar des Herrn, deines Gottes, den du dir baust, keinen Kultpfahl, keinerlei Holz einpflanzen.", heading: "Das Verbot nichtjahwistischer Kultsymbole: 16,21f", footnotes: ["Ex 20,24; 1 Kön 14,15; 2 Kön 21,7; 23,6f; (21-22) 12,3"] },
      { number: 22, text: "Du sollst kein Steinmal von der Art errichten, die der Herr, dein Gott, hasst.", footnotes: ["1 Kön 14,23; 2 Kön 17,10"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Du sollst dem Herrn, deinem Gott, keinen Stier und kein Lamm schlachten, die einen Fehler, irgendeine Missbildung haben, denn das ist dem Herrn, deinem Gott, ein Gräuel.", heading: "Das Verbot fehlerhafter Opfer: 17,1", footnotes: ["Mal 1,6-9"] },
      { number: 2, text: "Wenn in deiner Mitte, in einem der Stadtbereiche, die der Herr, dein Gott, dir gibt, ein Mann - oder auch eine Frau - lebt, der tut, was in den Augen des Herrn, deines Gottes, böse ist, und sich über seinen Bund hinwegsetzt,", heading: "Das Verfahren beim Abfall zu Gestirngöttern: 17,2-7", footnotes: ["(2-5) 4,19; 13,2-19; 2 Kön 17,16; 21,3.5; 23,4f; Dtn 29,24"] },
      { number: 3, text: "wenn er hingeht, anderen Göttern dient und sich vor ihnen niederwirft - und zwar vor der Sonne, dem Mond oder dem ganzen Himmelsheer, was ich verboten habe -,", footnotes: ["was ich verboten habe: vgl. die Anmerkung zu 11,13-15."] },
      { number: 4, text: "wenn dir das gemeldet wird, wenn du den Fall anhängig machst, genaue Ermittlungen anstellst und es sich zeigt: Ja, es ist wahr, der Tatbestand steht fest, dieser Gräuel ist in Israel geschehen!,", footnotes: ["19,18"] },
      { number: 5, text: "dann sollst du diesen Mann oder diese Frau, die den Frevel begangen haben, den Mann oder die Frau, zu einem deiner Stadttore führen und steinigen und sie sollen sterben." },
      { number: 6, text: "Wenn es um Leben oder Tod eines Angeklagten geht, darf er nur auf die Aussage von zwei oder drei Zeugen hin zum Tod verurteilt werden. Auf die Aussage eines einzigen Zeugen hin darf er nicht zum Tod verurteilt werden.", footnotes: ["19,15; Mt 18,16; Joh 8,17; 2 Kor 13,1; 1 Tim 5,19; Hebr 10,28"] },
      { number: 7, text: "Wenn er hingerichtet wird, sollen die Zeugen als Erste ihre Hand gegen ihn erheben, dann erst das ganze Volk. Du sollst das Böse aus deiner Mitte wegschaffen.", footnotes: ["13,10; 21,21; Joh 8,7; 1 Kor 5,13"] },
      { number: 8, text: "Wenn bei einem Verfahren wegen Mord, Eigentumsdelikt oder Körperverletzung - also wegen Streitsachen, über die in deinen Stadtbereichen entschieden werden darf - der Fall für dich zu ungewöhnlich liegt, dann sollst du dich aufmachen, zu der Stätte hinaufziehen, die der Herr auswählt,", heading: "Das sakrale Gerichtsverfahren: 17,8-13", footnotes: ["(8-13) 21,5; Ex 18,13-26; 2 Chr 19,8-11"] },
      { number: 9, text: "und vor die levitischen Priester und den Richter treten, der dann amtiert. Du sollst genaue Ermittlungen anstellen lassen und sie sollen dir den Urteilsspruch verkünden.", footnotes: ["10,8; 19,17; 26,3"] },
      { number: 10, text: "Dann sollst du dich an den Spruch halten, den sie dir an dieser Stätte, die der Herr auswählt, verkünden, und du sollst auf alles, was sie dich lehren, genau achten und es halten." },
      { number: 11, text: "An den Wortlaut der Weisung, die sie dich lehren, und an das Urteil, das sie fällen, sollst du dich halten. Von dem Spruch, den sie dir verkünden, sollst du weder rechts noch links abweichen." },
      { number: 12, text: "Ein Mann aber, der so vermessen ist, auf den Priester, der dort steht, um vor dem Herrn, deinem Gott, Dienst zu tun, oder auf den Richter nicht zu hören, dieser Mann soll sterben. Du sollst das Böse aus Israel wegschaffen.", footnotes: ["(12-13) 21,21; 20,2"] },
      { number: 13, text: "Das ganze Volk soll davon hören, damit sie sich fürchten und nicht noch einmal so vermessen sind." },
      { number: 14, text: "Wenn du in das Land, das der Herr, dein Gott, dir gibt, hineingezogen bist, es in Besitz genommen hast, in ihm wohnst und dann sagst: Ich will einen König über mich einsetzen wie alle Völker in meiner Nachbarschaft!,", heading: "Der König: 17,14-20", footnotes: ["(14-20) 1 Sam 7,2 - 8,22; 10,18f; 21,1-25"] },
      { number: 15, text: "dann darfst du einen König über dich einsetzen, doch nur einen, den der Herr, dein Gott, auswählt. Nur aus der Mitte deiner Brüder darfst du einen König über dich einsetzen. Einen Ausländer darfst du nicht über dich einsetzen, weil er nicht dein Bruder ist.", footnotes: ["1 Sam 10,24; 16,1-13; 2 Sam 5,1-3"] },
      { number: 16, text: "Der König soll sich aber nicht zu viele Pferde halten. Er soll das Volk nicht nach Ägypten zurückbringen, um mehr Pferde zu bekommen; denn der Herr hat zu euch gesagt: Ihr sollt auf diesem Weg nie wieder zurückkehren.", footnotes: ["28,68; Gen 26,2; 2 Sam 8,4; 1 Kön 10,26-29"] },
      { number: 17, text: "Er soll sich auch keine große Zahl von Frauen nehmen, damit sein Sinn nicht vom rechten Weg abweicht. Er soll nicht zu viel Silber und Gold anhäufen.", footnotes: ["1 Kön 10,14-25; 11,1-8"] },
      { number: 18, text: "Und wenn er seinen Königsthron bestiegen hat, soll er sich von dieser Weisung, die die levitischen Priester aufbewahren, auf einer Schriftrolle eine Zweitschrift anfertigen lassen.", footnotes: ["10,8; 31,9.26; (18-20) Jos 1,7-9; 1 Kön 2,1-4", "Mit «dieser Weisung» dürfte der Text der Kapitel 5 - 28 gemeint sein. Der Buchname «Deuteronomium» kommt vielleicht von diesem Vers."] },
      { number: 19, text: "Sein Leben lang soll er die Weisung mit sich führen und in der Rolle lesen, damit er lernt, den Herrn, seinen Gott, zu fürchten, auf alle Worte dieser Weisung und dieser Gesetze zu achten, sie zu halten," },
      { number: 20, text: "sein Herz nicht über seine Brüder zu erheben und von dem Gebot weder rechts noch links abzuweichen, damit er lange als König in Israels Mitte lebt, er und seine Nachkommen.", footnotes: ["5,32f"] },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Die levitischen Priester - der ganze Stamm Levi - sollen nicht wie das übrige Israel Landanteil und Erbbesitz haben. Sie sollen sich von den Opferanteilen des Herrn, von seinem Erbbesitz, ernähren.", heading: "Die Einkünfte der Priester: 18,1-8", footnotes: ["(1-5) 1 Kor 9,13; (1-2) 10,8f; 1 Sam 2,28"] },
      { number: 2, text: "Der Stamm Levi soll inmitten seiner Brüder leben, aber keinen Erbbesitz haben. Der Herr selbst ist sein Erbbesitz, wie er es ihm zugesagt hat." },
      { number: 3, text: "Und das ist das Recht, das die Priester gegenüber dem Volk haben, gegenüber denen, die ein Schlachtopfertier schlachten, sei es ein Stier oder ein Lamm: Man soll dem Priester den Bug, die Kinnbacken und den Labmagen geben." },
      { number: 4, text: "Du sollst ihm den ersten Ertrag von Korn, Wein und Öl und den ersten Ertrag der Schafschur geben.", footnotes: ["26,1-11; Ex 23,19", "Andere Übersetzungsmöglichkeit: Du sollst ihm das Beste von Korn, Wein und Öl und das Beste aus der Schafschur geben."] },
      { number: 5, text: "Denn der Herr, dein Gott, hat den Stamm Levi unter allen deinen Stämmen dazu ausgewählt, dass er im Namen des Herrn dasteht und Dienst tut - Levi und seine Nachkommen, ihr Leben lang.", footnotes: ["10,8"] },
      { number: 6, text: "Wenn ein Levit aus einem deiner Stadtbereiche irgendwo in Israel, in dem er als Fremder gewohnt hat, zu der Stätte kommt, die der Herr ausgewählt hat, und zwar, wann immer er möchte,", footnotes: ["Ri 17,7; 19,1; (6-8) 2 Kön 23,9"] },
      { number: 7, text: "und wenn er dann wie alle seine levitischen Brüder, die dort vor dem Herrn stehen, im Namen des Herrn, seines Gottes, Dienst tut," },
      { number: 8, text: "sollen alle die gleiche Zuteilung erhalten, ohne dass man berücksichtigt, wie groß sein väterliches Vermögen ist.", footnotes: ["Deutung von H unsicher."] },
      { number: 9, text: "Wenn du in das Land hineinziehst, das der Herr, dein Gott, dir gibt, sollst du nicht lernen, die Gräuel dieser Völker nachzuahmen.", heading: "Die Zauberer und Propheten: 18,9-22", footnotes: ["12,31; 20,18; 32,16; 1 Kön 14,24"] },
      { number: 10, text: "Es soll bei dir keinen geben, der seinen Sohn oder seine Tochter durchs Feuer gehen lässt, keinen, der Losorakel befragt, Wolken deutet, aus dem Becher weissagt, zaubert,", footnotes: ["2 Kön 16,3; 21,2.11; 23,13; Dtn 12,31; Ex 22,17; 2 Kön 9,22; 17,17; 21,6; 23,10"] },
      { number: 11, text: "Gebetsbeschwörungen hersagt oder Totengeister befragt, keinen Hellseher, keinen, der Verstorbene um Rat fragt.", footnotes: ["1 Sam 28; 2 Kön 21,6; 23,24"] },
      { number: 12, text: "Denn jeder, der so etwas tut, ist dem Herrn ein Gräuel. Wegen dieser Gräuel vertreibt sie der Herr, dein Gott, vor dir.", footnotes: ["4,38; 7,17; 9,1-6; 11,22-25"] },
      { number: 13, text: "Du sollst ganz und gar bei dem Herrn, deinem Gott, bleiben.", footnotes: ["2 Sam 22,24; Mt 5,48"] },
      { number: 14, text: "Denn diese Völker, deren Besitz du übernimmst, hören auf Wolkendeuter und Orakelleser. Für dich aber hat der Herr, dein Gott, es anders bestimmt.", footnotes: ["18,10"] },
      { number: 15, text: "Einen Propheten wie mich wird dir der Herr, dein Gott, aus deiner Mitte, unter deinen Brüdern, erstehen lassen. Auf ihn sollt ihr hören.", footnotes: ["(15-19) 2 Kön 17,13; Mt 17,5; Mk 9,7; Lk 9,35; Joh 1,21; 6,14; 7,40; Apg 3,22f; 7,37", "15.18: Hier ist Israel zugesagt, dass es immer Propheten haben wird, die ihm den Willen Gottes mitteilen. Später wurde aus diesem Text die Verheißung eines endzeitlichen Propheten herausgelesen."] },
      { number: 16, text: "Der Herr wird ihn als Erfüllung von allem erstehen lassen, worum du am Horeb, am Tag der Versammlung, den Herrn, deinen Gott, gebeten hast, als du sagtest: Ich kann die donnernde Stimme des Herrn, meines Gottes, nicht noch einmal hören und dieses große Feuer nicht noch einmal sehen, ohne dass ich sterbe.", footnotes: ["(16-19) 5,23-31"] },
      { number: 17, text: "Damals sagte der Herr zu mir: Was sie von dir verlangen, ist recht." },
      { number: 18, text: "Einen Propheten wie dich will ich ihnen mitten unter ihren Brüdern erstehen lassen. Ich will ihm meine Worte in den Mund legen und er wird ihnen alles sagen, was ich ihm auftrage.", footnotes: ["Ex 4,15"] },
      { number: 19, text: "Einen Mann aber, der nicht auf meine Worte hört, die der Prophet in meinem Namen verkünden wird, ziehe ich selbst zur Rechenschaft." },
      { number: 20, text: "Doch ein Prophet, der sich anmaßt, in meinem Namen ein Wort zu verkünden, dessen Verkündigung ich ihm nicht aufgetragen habe, oder der im Namen anderer Götter spricht, ein solcher Prophet soll sterben.", footnotes: ["Jer 14,13-18"] },
      { number: 21, text: "Und wenn du denkst: Woran können wir ein Wort erkennen, das der Herr nicht gesprochen hat?,", footnotes: ["(21-22) Jer 28; Ez 33,33"] },
      { number: 22, text: "dann sollst du wissen: Wenn ein Prophet im Namen des Herrn spricht und sein Wort sich nicht erfüllt und nicht eintrifft, dann ist es ein Wort, das nicht der Herr gesprochen hat. Der Prophet hat sich nur angemaßt, es zu sprechen. Du sollst dich dadurch nicht aus der Fassung bringen lassen." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Wenn der Herr, dein Gott, die Völker, deren Land der Herr, dein Gott, dir geben will, niederstreckt und du ihren Besitz übernimmst und in ihren Städten und Häusern wohnst,", heading: "Die Asylstädte: 19,1-10", footnotes: ["(1-13) 4,41-43; Ex 21,12-14; Jos 20"] },
      { number: 2, text: "sollst du in dem Land, das der Herr, dein Gott, dir gibt, damit du es in Besitz nimmst, drei Städten eine Sonderstellung zuweisen." },
      { number: 3, text: "Du sollst die Wegstrecken berechnen und die Fläche des Landes, das der Herr, dein Gott, dir als Erbbesitz gibt, in drei Teile teilen. Dann kann jeder, der einen Menschen getötet hat, in diese Städte fliehen." },
      { number: 4, text: "Und so lautet die Bestimmung für einen, der jemand getötet hat und dorthin flieht, um am Leben zu bleiben: Wenn er den andern ohne Vorsatz erschlagen hat und nicht schon früher mit ihm verfeindet gewesen ist," },
      { number: 5, text: "zum Beispiel wenn er mit einem andern in den Wald gegangen ist, um Bäume zu fällen, seine Hand mit der Axt ausgeholt hat, um einen Baum umzuhauen, das Eisenblatt sich vom Stiel gelöst und den andern getroffen hat und dieser gestorben ist, dann kann er in eine dieser Städte fliehen, um am Leben zu bleiben." },
      { number: 6, text: "Es darf nicht sein, dass der Weg (zum Heiligtum) zu weit ist, damit nicht der Bluträcher, der aus Rachedurst den, der getötet hat, verfolgt, ihn einholt und tödlich trifft, obwohl kein Recht besteht, ihn zu töten, da er ja mit dem Getöteten nicht schon früher verfeindet war.", footnotes: ["2 Sam 14,7"] },
      { number: 7, text: "Deshalb mache ich dir zur Pflicht: Du sollst drei Städten eine Sonderstellung zuweisen." },
      { number: 8, text: "Und wenn der Herr, dein Gott, dein Gebiet vergrößert, wie er es deinen Vätern geschworen hat, und dir das ganze Land, von dem er gesagt hat, er werde es deinen Vätern geben, wirklich gibt,", footnotes: ["1,7; 11,24; 12,20; Gen 15,18; Ex 23,31; 34,24; Jos 1,4; 1 Kön 5,1-4"] },
      { number: 9, text: "weil du auf dieses ganze Gebot, auf das ich dich heute verpflichte, achtest und es hältst, indem du den Herrn, deinen Gott, liebst und dein Leben lang auf seinen Wegen gehst, dann sollst du diese drei Städte um drei weitere vermehren.", footnotes: ["11,1"] },
      { number: 10, text: "So soll verhindert werden, dass mitten in dem Land, das der Herr, dein Gott, dir als Erbbesitz gibt, unschuldiges Blut vergossen wird und Blutschuld über dich kommt.", footnotes: ["19,13; 21,7-9; 22,8; 27,25"] },
      { number: 11, text: "Wenn es sich um einen Mann handelt, der mit einem andern verfeindet war, wenn er ihm auflauerte, ihn überfiel und tödlich traf, sodass er starb, und wenn er in eine dieser Städte floh,", heading: "Die Auslieferung eines Mörders aus der Asylstadt: 19,11-13" },
      { number: 12, text: "dann sollen die Ältesten seiner Stadt ihn von dort holen lassen und in die Gewalt des Bluträchers geben und er soll sterben.", footnotes: ["5,23; 21,1-9.19; 22,13-21; 25,5-10"] },
      { number: 13, text: "Du sollst in dir kein Mitleid mit ihm aufsteigen lassen. Du sollst das Blut des Unschuldigen aus Israel wegschaffen und es wird dir gut gehen.", footnotes: ["7,16; 13,9; 19,21; 25,12; 21,21; 19,10"] },
      { number: 14, text: "An deinem Erbbesitz, der dir in dem Land zugeteilt werden soll, das der Herr, dein Gott, dir gibt, damit du es in Besitz nimmst, sollst du die Grenzmarkierung zu deinem Nachbarn hin, die die Vorfahren errichtet haben, nicht verrücken.", heading: "Die Grenzverrückung: 19,14", footnotes: ["27,17"] },
      { number: 15, text: "Wenn es um ein Verbrechen oder ein Vergehen geht, darf ein einzelner Belastungszeuge nicht Recht bekommen, welches Vergehen auch immer der Angeklagte begangen hat. Erst auf die Aussage von zwei oder drei Zeugen darf eine Sache Recht bekommen.", heading: "Die Zahl der Zeugen: 19,15", footnotes: ["17,6; Mt 18,16; Joh 8,17; 2 Kor 13,1; 1 Tim 5,19; Hebr 10,28", "15f: Die Funktion des Zeugen und die des Anklägers waren nicht klar geschieden."] },
      { number: 16, text: "Wenn jemand vor Gericht geht und als Zeuge einen andern zu Unrecht der Anstiftung zum Aufruhr bezichtigt,", heading: "Die falsche Aussage vor Gericht: 19,16-21", footnotes: ["(16-21) 5,20; Ex 23,1", "Aufruhr, andere Übersetzungsmöglichkeit: Abfall (vom Herrn). Welches Vergehen hier genau gemeint ist, bleibt unklar."] },
      { number: 17, text: "wenn die beiden Parteien mit ihrem Rechtsstreit vor den Herrn hintreten, vor die Priester und Richter, die dann amtieren,", footnotes: ["17,9; 26,3", "Gemeint ist die Anwendung des Gesetzes von 17,8-13 oder die Anrufung eines Gottesentscheids durch den zu Unrecht Angeklagten."] },
      { number: 18, text: "wenn die Richter eine genaue Ermittlung anstellen und sich zeigt: Der Mann ist ein falscher Zeuge, er hat seinen Bruder fälschlich bezichtigt,", footnotes: ["13,15; 17,4"] },
      { number: 19, text: "dann sollt ihr mit ihm so verfahren, wie er mit seinem Bruder verfahren wollte. Du sollst das Böse aus deiner Mitte wegschaffen.", footnotes: ["1 Kor 5,13; (19-20) 21,21"] },
      { number: 20, text: "Die Übrigen sollen davon hören, damit sie sich fürchten und nicht noch einmal ein solches Verbrechen in deiner Mitte begehen." },
      { number: 21, text: "Und du sollst in dir kein Mitleid aufsteigen lassen: Leben für Leben, Auge für Auge, Zahn für Zahn, Hand für Hand, Fuß für Fuß.", footnotes: ["19,13; Ex 21,23-25; Mt 5,38"] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Wenn du zum Kampf gegen deine Feinde ausziehst und Pferde und Wagen und ein Kriegsvolk erblickst, das zahlreicher ist als du, dann sollst du dich nicht vor ihnen fürchten; denn der Herr, dein Gott, der dich aus Ägypten heraufgeführt hat, ist bei dir.", heading: "Das Aufgebot des Volksheeres: 20,1-9" },
      { number: 2, text: "Wenn ihr in den Kampf zieht, soll der Priester vortreten, dem Kriegsvolk eine Ansprache halten", footnotes: ["(2-4) 1,29-31; 3,21f; Ex 14,13f; Jos 23,3-13; Dtn 17,12"] },
      { number: 3, text: "und zu ihnen sagen: Höre, Israel! Ihr zieht heute in den Kampf gegen eure Feinde. Verliert nicht den Mut! Fürchtet euch nicht, geratet nicht durcheinander und weicht nicht erschreckt zurück, wenn sie angreifen." },
      { number: 4, text: "Denn der Herr, euer Gott, zieht mit euch, um für euch gegen eure Feinde zu kämpfen und euch zu retten.", footnotes: ["Ex 14,30; Ri 2,18; 6,37; 7,7; 10,12; 1 Sam 7,8; 14,23.39"] },
      { number: 5, text: "Dann sollen die Listenführer zum Kriegsvolk sagen: Ist unter euch einer, der ein neues Haus gebaut und noch nicht eingeweiht hat? Er trete weg und kehre zu seinem Haus zurück, damit er nicht im Kampfe fällt und ein anderer es einweiht.", footnotes: ["(5-9) 16,18; 28,30"] },
      { number: 6, text: "Ist unter euch einer, der einen Weinberg angelegt und noch nicht die erste Lese gehalten hat? Er trete weg und kehre nach Hause zurück, damit er nicht im Kampfe fällt und ein anderer die erste Lese hält." },
      { number: 7, text: "Ist unter euch einer, der sich mit einer Frau verlobt und sie noch nicht geheiratet hat? Er trete weg und kehre nach Hause zurück, damit er nicht im Kampfe fällt und ein anderer seine Frau heiratet.", footnotes: ["24,5"] },
      { number: 8, text: "Außerdem sollen die Listenführer zum Kriegsvolk sagen: Ist unter euch einer, der sich fürchtet und keinen Mut hat? Er trete weg und kehre nach Hause zurück, damit nicht auch noch seinen Brüdern der Mut genommen wird.", footnotes: ["Ri 7,3"] },
      { number: 9, text: "Und wenn die Listenführer damit zu Ende sind, dies dem Kriegsvolk zu sagen, sollen sie Truppenführer ernennen und ihnen das Kommando über das Kriegsvolk übertragen." },
      { number: 10, text: "Wenn du vor eine Stadt ziehst, um sie anzugreifen, dann sollst du ihr zunächst eine friedliche Einigung vorschlagen.", heading: "Der Krieg gegen Städte: 20,10-18" },
      { number: 11, text: "Nimmt sie die friedliche Einigung an und öffnet dir die Tore, dann soll die gesamte Bevölkerung, die du dort vorfindest, zum Frondienst verpflichtet und dir untertan sein." },
      { number: 12, text: "Lehnt sie eine friedliche Einigung mit dir ab und will sich mit dir im Kampf messen, dann darfst du sie belagern." },
      { number: 13, text: "Wenn der Herr, dein Gott, sie in deine Gewalt gibt, sollst du alle männlichen Personen mit scharfem Schwert erschlagen." },
      { number: 14, text: "Die Frauen aber, die Kinder und Greise, das Vieh und alles, was sich sonst in der Stadt befindet, alles, was sich darin plündern lässt, darfst du dir als Beute nehmen. Was du bei deinen Feinden geplündert hast, darfst du verzehren; denn der Herr, dein Gott, hat es dir geschenkt." },
      { number: 15, text: "So sollst du mit allen Städten verfahren, die sehr weit von dir entfernt liegen und nicht zu den Städten dieser Völker hier gehören." },
      { number: 16, text: "Aus den Städten dieser Völker jedoch, die der Herr, dein Gott, dir als Erbbesitz gibt, darfst du nichts, was Atem hat, am Leben lassen.", footnotes: ["(16-18) 13,16f"] },
      { number: 17, text: "Vielmehr sollst du die Hetiter und Amoriter, Kanaaniter und Perisiter, Hiwiter und Jebusiter der Vernichtung weihen, so wie es der Herr, dein Gott, dir zur Pflicht gemacht hat,", footnotes: ["(17-18) 7,1-4"] },
      { number: 18, text: "damit sie euch nicht lehren, alle Gräuel nachzuahmen, die sie begingen, wenn sie ihren Göttern dienten, und ihr nicht gegen den Herrn, euren Gott, sündigt.", footnotes: ["18,9"] },
      { number: 19, text: "Wenn du eine Stadt längere Zeit hindurch belagerst, um sie anzugreifen und zu erobern, dann sollst du ihrem Baumbestand keinen Schaden zufügen, indem du die Axt daran legst. Du darfst von den Bäumen essen, sie aber nicht fällen mit dem Gedanken, die Bäume auf dem Feld seien der Mensch selbst, sodass sie von dir belagert werden müssten.", heading: "Die Schonung des Baumbestandes: 20,19f", footnotes: ["die Bäume auf dem Feld seien der Mensch selbst: Von den Bäumen leben die Menschen der Stadt. Wer sie vernichtet, nimmt den Menschen für lange Zeit ihren Lebensunterhalt."] },
      { number: 20, text: "Nur den Bäumen, von denen du weißt, dass sie keine Fruchtbäume sind, darfst du Schaden zufügen. Du darfst sie fällen und daraus Belagerungswerk bauen gegen die Stadt, die gegen dich kämpfen will, bis sie schließlich fällt." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Wenn in dem Land, das der Herr, dein Gott, dir gibt, damit du es in Besitz nimmst, einer auf freiem Feld ermordet aufgefunden wird und man nicht weiß, wer ihn erschlagen hat,", heading: "Der Mord durch einen unbekannten Täter: 21,1-9", footnotes: ["(1-9) 5,17; 19,12", "1-9: War der Mörder nicht bekannt, dann konnte die Blutrache sich gegen jeden Menschen aus der nächstliegenden Stadt richten. Durch die hier vorgeschriebene rechtlich-rituelle Aktion befreite sich die Stadt von dieser Bedrohung."] },
      { number: 2, text: "dann sollen deine Ältesten und Richter hinausgehen und feststellen, wie weit die Städte ringsum von dem Ermordeten entfernt sind." },
      { number: 3, text: "Wenn feststeht, welche Stadt dem Ermordeten am nächsten liegt, sollen die Ältesten dieser Stadt eine junge Kuh aussuchen, die noch nicht zur Arbeit verwendet worden ist, das heißt, die noch nicht unter dem Joch gegangen ist." },
      { number: 4, text: "Die Ältesten dieser Stadt sollen die Kuh in ein ausgetrocknetes Bachtal bringen, wo weder geackert noch gesät wird. Dort sollen sie im Bachbett der Kuh das Genick brechen." },
      { number: 5, text: "Dann sollen die Priester, die Nachkommen Levis, herantreten; denn sie hat der Herr, dein Gott, dazu ausgewählt, vor ihm Dienst zu tun und im Namen des Herrn den Segen zu sprechen. Nach ihrem Spruch soll jeder Rechtsstreit und jeder Fall von Körperverletzung entschieden werden.", footnotes: ["10,8; 17,8-13; 19,17"] },
      { number: 6, text: "Alle Ältesten dieser Stadt sollen, weil sie dem Ermordeten am nächsten sind, über der Kuh, der im Bachbett das Genick gebrochen wurde, ihre Hände waschen.", footnotes: ["(6-7) Mt 27,24"] },
      { number: 7, text: "Sie sollen feierlich sagen: Unsere Hände haben dieses Blut nicht vergossen und unsere Augen haben nichts gesehen.", footnotes: ["(7-9) 19,10"] },
      { number: 8, text: "Deck es zu, zum Schutz deines Volkes Israel, das du freigekauft hast, Herr, und lass kein unschuldig vergossenes Blut in der Mitte deines Volkes Israel bleiben. Dann ist das Blut zu ihrem Schutz zugedeckt." },
      { number: 9, text: "Du wirst das unschuldig vergossene Blut aus deiner Mitte wegschaffen können, wenn du tust, was in den Augen des Herrn richtig ist.", footnotes: ["21,21"] },
      { number: 10, text: "Wenn du zum Kampf gegen deine Feinde ausziehst und der Herr, dein Gott, sie alle in deine Gewalt gibt, wenn du dabei Gefangene machst", heading: "Die Heirat einer Kriegsgefangenen: 21,10-14" },
      { number: 11, text: "und unter den Gefangenen eine Frau von schöner Gestalt erblickst, wenn sie dein Herz gewinnt und du sie heiraten möchtest," },
      { number: 12, text: "dann sollst du sie in dein Haus bringen und sie soll sich den Kopf scheren, ihre Nägel kürzen", footnotes: ["12f: Haarescheren, Nägelschneiden und Neueinkleidung sind wahrscheinlich Symbolhandlungen, die besagen, hier beginne ein neues Leben. Der Sinn der Trauer ist die seelische Loslösung von der Vergangenheit."] },
      { number: 13, text: "und die Gefangenenkleidung ablegen. Sie soll in deinem Haus wohnen und einen Monat lang ihren Vater und ihre Mutter beweinen. Danach darfst du mit ihr Verkehr haben, du darfst ihr Mann werden und sie deine Frau." },
      { number: 14, text: "Wenn sie dir aber nicht mehr gefällt, darfst du sie entlassen, und sie darf tun, was sie will. Auf keinen Fall darfst du sie für Silber verkaufen. Auch darfst du sie nicht als Sklavin kennzeichnen. Denn du hast sie dir gefügig gemacht." },
      { number: 15, text: "Wenn ein Mann zwei Frauen hat, eine, die er liebt, und eine, die er nicht liebt, und wenn beide ihm Söhne gebären, die geliebte wie die ungeliebte, und der erstgeborene Sohn von der ungeliebten stammt,", heading: "Das Erbrecht des erstgeborenen Sohnes: 21,15-17" },
      { number: 16, text: "dann darf er, wenn er sein Erbe unter seine Söhne verteilt, den Sohn der geliebten Frau nicht als Erstgeborenen behandeln und damit gegen das Recht des wirklichen Erstgeborenen, des Sohnes der ungeliebten Frau, verstoßen." },
      { number: 17, text: "Vielmehr soll er den Erstgeborenen, den Sohn der Ungeliebten, anerkennen, indem er ihm von allem, was er besitzt, den doppelten Anteil gibt. Ihn hat er zuerst gezeugt, er besitzt das Erstgeborenenrecht.", footnotes: ["Gen 49,3", "den doppelten Anteil, andere Übersetzungsmöglichkeit: zwei Drittel."] },
      { number: 18, text: "Wenn ein Mann einen störrischen und widerspenstigen Sohn hat, der nicht auf die Stimme seines Vaters und seiner Mutter hört, und wenn sie ihn züchtigen und er trotzdem nicht auf sie hört,", heading: "Die Verstoßung eines Sohnes: 21,18-21", footnotes: ["(18-21) 5,16; Ex 21,15.17", "18-21: Das Gesetz begrenzt die in der patriarchalischen Gesellschaft unbegrenzte väterliche Gewalt auf doppelte Weise: Die Verstoßung eines Sohnes muss über ein öffentliches Gerichtsverfahren laufen und neben dem Vater muss auch die Mutter tätig werden."] },
      { number: 19, text: "dann sollen Vater und Mutter ihn packen, vor die Ältesten der Stadt und die Torversammlung des Ortes führen", footnotes: ["19,12"] },
      { number: 20, text: "und zu den Ältesten der Stadt sagen: Unser Sohn hier ist störrisch und widerspenstig, er hört nicht auf unsere Stimme, er ist ein Verschwender und Trinker." },
      { number: 21, text: "Dann sollen alle Männer der Stadt ihn steinigen und er soll sterben. Du sollst das Böse aus deiner Mitte wegschaffen. Ganz Israel soll davon hören, damit sie sich fürchten.", footnotes: ["13,6.12; 17,7.12; 19,13.19; 21,9; 22,21f; 24,7; Ri 20,13"] },
      { number: 22, text: "Wenn jemand ein Verbrechen begangen hat, auf das die Todesstrafe steht, wenn er hingerichtet wird und du den Toten an einen Pfahl hängst,", heading: "Die Bestattung von Hingerichteten: 21,22f", footnotes: ["Apg 10,39; (22-23) Jos 8,29; Mt 27,57f; Joh 19,31"] },
      { number: 23, text: "dann soll die Leiche nicht über Nacht am Pfahl hängen bleiben, sondern du sollst ihn noch am gleichen Tag begraben; denn ein Gehenkter ist ein von Gott Verfluchter. Du sollst das Land nicht unrein werden lassen, das der Herr, dein Gott, dir als Erbbesitz gibt.", footnotes: ["Gal 3,13"] },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Du sollst nicht untätig zusehen, wie ein Stier oder ein Lamm deines Bruders sich verläuft. Du sollst dann nicht so tun, als gingen sie dich nichts an, sondern sie deinem Bruder zurückbringen.", heading: "Gefundenes Gut: 22,1-3", footnotes: ["(1-3) Ex 23,4"] },
      { number: 2, text: "Wenn dein Bruder nicht in der Nähe wohnt oder wenn du ihn nicht kennst, sollst du das Tier in deinen Stall tun und es soll dir zur Verfügung stehen, bis dein Bruder es sucht und du es ihm zurückgeben kannst." },
      { number: 3, text: "Ebenso sollst du es mit einem Esel halten, ebenso mit einem Gewand, ebenso mit allem anderen, was dein Bruder verloren hat: was er verloren hat und was du findest. Du kannst gar nicht so tun, als ginge dich das nichts an." },
      { number: 4, text: "Du sollst nicht untätig zusehen, wie ein Esel oder ein Ochse deines Bruders auf dem Weg zusammenbricht. Du sollst dann nicht so tun, als gingen sie dich nichts an, sondern ihm helfen, sie wieder aufzurichten.", heading: "Die nachbarschaftliche Hilfe: 22,4", footnotes: ["Ex 23,5"] },
      { number: 5, text: "Eine Frau soll nicht die Ausrüstung eines Mannes tragen und ein Mann soll kein Frauenkleid anziehen; denn jeder, der das tut, ist dem Herrn, deinem Gott, ein Gräuel.", heading: "Kleidung und Geschlecht: 22,5", footnotes: ["Kleidertausch spielte in kanaanäischen Kulten eine Rolle."] },
      { number: 6, text: "Wenn du unterwegs auf einem Baum oder auf der Erde zufällig ein Vogelnest mit Jungen oder mit Eiern darin findest und die Mutter auf den Jungen oder auf den Eiern sitzt, sollst du die Mutter nicht zusammen mit den Jungen herausnehmen.", heading: "Die Aushebung von Vogelnestern: 22,6f" },
      { number: 7, text: "Sondern du sollst die Mutter fliegen lassen und nur die Jungen nehmen, damit es dir gut geht und du lange lebst.", footnotes: ["5,16"] },
      { number: 8, text: "Wenn du ein neues Haus baust, sollst du um die Dachterrasse eine Brüstung ziehen. Du sollst nicht dadurch, dass jemand herunterfällt, Blutschuld auf dein Haus legen.", heading: "Schutzgeländer an Dachterrassen: 22,8", footnotes: ["19,10"] },
      { number: 9, text: "Du sollst in deinem Weinberg keine anderen Pflanzen anbauen, sonst verfällt das Ganze dem Heiligtum, sowohl was du zusätzlich angebaut hast als auch was der Weinberg trägt.", heading: "Das Verbot von Vermischung: 22,9-11" },
      { number: 10, text: "Du sollst nicht Ochse und Esel zusammen vor den Pflug spannen." },
      { number: 11, text: "Du sollst für deine Kleidung kein Mischgewebe aus Wolle und Flachs verwenden." },
      { number: 12, text: "Du sollst an den vier Zipfeln des Überwurfs, den du trägst, Quasten anbringen.", heading: "Die Quasten: 22,12", footnotes: ["Mt 23,5"] },
      { number: 13, text: "Wenn ein Mann eine Frau geheiratet und mit ihr Verkehr gehabt hat, sie aber später nicht mehr liebt", heading: "Die Beschuldigung der Ehefrau wegen vorehelichen Verkehrs: 22,13-21", footnotes: ["(13-29) 5,18.21; (13-21) 19,12"] },
      { number: 14, text: "und ihr Anrüchiges vorwirft, sie in Verruf bringt und behauptet: Diese Frau habe ich geheiratet, aber als ich mich ihr näherte, entdeckte ich, dass sie nicht mehr unberührt war!," },
      { number: 15, text: "wenn Vater und Mutter des Mädchens dann das Beweisstück ihrer Unberührtheit holen und zu den Ältesten der Stadt ans Tor bringen", footnotes: ["das Beweisstück ihrer Unberührtheit, wörtlich: ihre Unberührtheit. Es gehörte wohl zu den Hochzeitsbräuchen, dass nach dem ersten Verkehr den Eltern das letzte vorhochzeitliche Gewand ihrer Tochter, das als Betttuch gedient hatte und nun Blutspuren aufwies, überreicht wurde. Es konnte deshalb in einem Prozess als Beweisstück dafür dienen, dass die Frau unberührt in die Ehe gekommen war."] },
      { number: 16, text: "und der Vater des Mädchens den Ältesten erklärt: Ich habe diesem Mann meine Tochter zur Frau gegeben, aber er liebt sie nicht mehr," },
      { number: 17, text: "ja er wirft ihr jetzt Anrüchiges vor, indem er sagt: Ich habe entdeckt, dass deine Tochter nicht mehr unberührt war!; aber hier ist das Beweisstück für die Unberührtheit meiner Tochter!, und wenn sie das Gewand (aus der Hochzeitsnacht) vor den Ältesten der Stadt ausbreiten," },
      { number: 18, text: "dann sollen die Ältesten dieser Stadt den Mann packen und züchtigen lassen." },
      { number: 19, text: "Sie sollen ihm eine Geldbuße von hundert Silberschekel auferlegen und sie dem Vater des Mädchens übergeben, weil der Mann eine unberührte Israelitin in Verruf gebracht hat. Sie soll seine Frau bleiben. Er darf sie niemals entlassen." },
      { number: 20, text: "Wenn der Vorwurf aber zutrifft, wenn sich keine Beweisstücke für die Unberührtheit des Mädchens beibringen lassen," },
      { number: 21, text: "soll man das Mädchen hinausführen und vor die Tür ihres Vaterhauses bringen. Dann sollen die Männer ihrer Stadt sie steinigen und sie soll sterben; denn sie hat eine Schandtat in Israel begangen, indem sie in ihrem Vaterhaus Unzucht trieb. Du sollst das Böse aus deiner Mitte wegschaffen.", footnotes: ["21,21; Gen 34,7; Jos 7,15; Ri 20,6.10; 2 Sam 13,12; 1 Kor 5,13"] },
      { number: 22, text: "Wenn ein Mann dabei ertappt wird, wie er bei einer verheirateten Frau liegt, dann sollen beide sterben, der Mann, der bei der Frau gelegen hat, und die Frau. Du sollst das Böse aus Israel wegschaffen.", heading: "Der Ehebruch mit einer verheirateten Frau: 22,22", footnotes: ["21,21; Joh 8,5"] },
      { number: 23, text: "Wenn ein unberührtes Mädchen mit einem Mann verlobt ist und ein anderer Mann ihr in der Stadt begegnet und sich mit ihr hinlegt,", heading: "Der Beischlaf mit der Verlobten eines anderen: 22,23-27", footnotes: ["Durch die «Verlobung» erwarb der Bräutigam das Anrecht auf die Frau."] },
      { number: 24, text: "dann sollt ihr beide zum Tor dieser Stadt führen. Ihr sollt sie steinigen und sie sollen sterben, das Mädchen, weil es in der Stadt nicht um Hilfe geschrien hat, und der Mann, weil er sich die Frau eines andern gefügig gemacht hat. Du sollst das Böse aus deiner Mitte wegschaffen.", footnotes: ["1 Kor 5,13"] },
      { number: 25, text: "Wenn der Mann dem verlobten Mädchen aber auf freiem Feld begegnet, sie fest hält und sich mit ihr hinlegt, dann soll nur der Mann sterben, der bei ihr gelegen hat," },
      { number: 26, text: "dem Mädchen aber sollst du nichts tun. Bei dem Mädchen handelt es sich nicht um ein Verbrechen, auf das der Tod steht; denn dieser Fall ist so zu beurteilen, wie wenn ein Mann einen andern überfällt und ihn tötet." },
      { number: 27, text: "Auf freiem Feld ist er ihr begegnet, das verlobte Mädchen mag um Hilfe geschrien haben, aber es ist kein Helfer da gewesen." },
      { number: 28, text: "Wenn ein Mann einem unberührten Mädchen, das noch nicht verlobt ist, begegnet, sie packt und sich mit ihr hinlegt und sie ertappt werden,", heading: "Der Beischlaf mit einer noch nicht Verlobten: 22,28f", footnotes: ["(28-29) Ex 22,15f"] },
      { number: 29, text: "soll der Mann, der bei ihr gelegen hat, dem Vater des Mädchens fünfzig Silberschekel zahlen und sie soll seine Frau werden, weil er sie sich gefügig gemacht hat. Er darf sie niemals entlassen." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Ein Mann darf eine Frau seines Vaters nicht heiraten; denn er darf das Bett seines Vaters nicht aufdecken.", heading: "Die Blutschande: 23,1", footnotes: ["27,20; 1 Kor 5,1", "Manche Bibelausgaben zählen diesen Vers als 22,30; entsprechend ändern sich die Verszahlen von Kap. 23."] },
      { number: 2, text: "In die Versammlung des Herrn darf keiner aufgenommen werden, dessen Hoden zerquetscht sind oder dessen Glied verstümmelt ist.", heading: "Die Aufnahme in die Versammlung des Herrn: 23,2-9" },
      { number: 3, text: "In die Versammlung des Herrn darf kein Bastard aufgenommen werden; auch in der zehnten Generation dürfen seine Nachkommen nicht in die Versammlung des Herrn aufgenommen werden.", footnotes: ["Sach 9,6", "Bastard: in H ein Wort mit umstrittener Bedeutung."] },
      { number: 4, text: "In die Versammlung des Herrn darf kein Ammoniter oder Moabiter aufgenommen werden, auch nicht in der zehnten Generation. Niemals dürfen ihre Nachkommen in die Versammlung des Herrn aufgenommen werden;" },
      { number: 5, text: "denn sie sind euch nicht mit Brot und Wasser auf dem Weg entgegengegangen, als ihr aus Ägypten zogt, und Moab hat Bileam, den Sohn Beors, aus Petor in Mesopotamien gedungen und gegen dich ausgesandt, damit er dich verfluchte -", footnotes: ["(5-6) Num 22-24"] },
      { number: 6, text: "doch der Herr, dein Gott, hat sich geweigert, Bileam zu erhören, und der Herr, dein Gott, hat für dich den Fluch in Segen verwandelt; denn der Herr, dein Gott, liebt dich." },
      { number: 7, text: "Du sollst dich nie und nimmer um einen Friedens- und Freundschaftsvertrag mit ihnen bemühen." },
      { number: 8, text: "Der Edomiter dagegen soll dir kein Gräuel sein; denn er ist dein Bruder. Der Ägypter soll dir kein Gräuel sein; denn du hast als Fremder in seinem Land gewohnt." },
      { number: 9, text: "In der dritten Generation dürfen ihre leiblichen Nachkommen in die Versammlung des Herrn aufgenommen werden." },
      { number: 10, text: "Wenn du ins Feld ziehst und gegenüber deinen Feinden das Lager aufschlägst, sollst du dich vor jeder Unsauberkeit hüten.", heading: "Die Reinheit des Heerlagers: 23,10-15" },
      { number: 11, text: "Wenn jemand unter dir ist, der nicht mehr rein ist, weil nachts etwas geschah, soll er in das Vorgelände des Lagers gehen und das Lager nicht betreten." },
      { number: 12, text: "Wenn der Abend kommt, soll er sich mit Wasser waschen, und wenn die Sonne untergeht, darf er in das Lager zurückkehren." },
      { number: 13, text: "Du sollst im Vorgelände des Lagers eine Ecke haben, wo du austreten kannst." },
      { number: 14, text: "In deinem Gepäck sollst du eine Schaufel haben, und wenn du dich draußen hinhocken willst, dann grab damit ein Loch und nachher deck deine Notdurft wieder zu!" },
      { number: 15, text: "Denn der Herr, dein Gott, hält sich in der Mitte deines Lagers auf, um dich der Gefahr zu entreißen und dir deine Feinde auszuliefern. Dein Lager soll heilig sein, damit er bei dir nichts Anstößiges sieht und sich nicht von dir abwendet." },
      { number: 16, text: "Du sollst einen fremden Untertan, der vor seinem Herrn bei dir Schutz sucht, seinem Herrn nicht ausliefern.", heading: "Das Verbot der Auslieferung von Flüchtlingen: 23,16f", footnotes: ["einen fremden Untertan, andere Übersetzungsmöglichkeit: einen Sklaven (aus dem Ausland)."] },
      { number: 17, text: "Bei dir soll er wohnen dürfen, in deiner Mitte, in einem Ort, den er sich in einem deiner Stadtbereiche auswählt, wo es ihm gefällt. Du sollst ihn nicht ausbeuten." },
      { number: 18, text: "Unter den Frauen Israels soll es keine sakrale Prostitution geben, und unter den Männern Israels soll es keine sakrale Prostitution geben.", heading: "Das Verbot sakraler Prostitution: 23,18f", footnotes: ["(18-19) 1 Kön 14,24; 15,12; 22,47; 2 Kön 23,7"] },
      { number: 19, text: "Du sollst weder Dirnenlohn noch Hundegeld in den Tempel des Herrn, deines Gottes, bringen. Kein Gelübde kann dazu verpflichten; denn auch diese beiden Dinge sind dem Herrn, deinem Gott, ein Gräuel.", footnotes: ["Hundegeld: die Bezahlung bei männlicher Prostitution."] },
      { number: 20, text: "Du darfst von deinem Bruder keine Zinsen nehmen: weder Zinsen für Geld noch Zinsen für Getreide noch Zinsen für sonst etwas, wofür man Zinsen nimmt.", heading: "Zinsen: 23,20f", footnotes: ["(20-21) 15,3; Ex 22,24", "20f: Zumindest innerhalb Israels spielten Darlehen keine Rolle, außer wenn eine Familie in Not geraten war. Das vorausgesetzt, ist ein Darlehen Pflicht der Nächstenliebe und Zins - der damals sehr hoch war - würde seine Natur verkehren."] },
      { number: 21, text: "Von einem Ausländer darfst du Zinsen nehmen, von deinem Bruder darfst du keine Zinsen nehmen, damit der Herr, dein Gott, dich segnet in allem, was deine Hände schaffen, in dem Land, in das du hineinziehst, um es in Besitz zu nehmen." },
      { number: 22, text: "Wenn du vor dem Herrn, deinem Gott, ein Gelübde machst, sollst du nicht zögern, es zu erfüllen; sonst wird es der Herr, dein Gott, von dir einfordern und die Strafe für diese Sünde wird über dich kommen.", heading: "Gelübde: 23,22-24", footnotes: ["Mt 5,33"] },
      { number: 23, text: "Wenn du davon absiehst, Gelübde zu machen, wird auch die Strafe für diese Sünde nicht über dich kommen." },
      { number: 24, text: "Was deinem Mund entfahren ist, darauf sollst du auch achten und du sollst es halten, da du dem Herrn, deinem Gott, ja aus freien Stücken gelobt hast, was dein Mund genannt hat." },
      { number: 25, text: "Wenn du in den Weinberg eines andern kommst, darfst du so viel Trauben essen, wie du magst, bis du satt bist, nur darfst du nichts in ein Gefäß tun.", heading: "Früchte am Weg: 23,25f" },
      { number: 26, text: "Wenn du durch das Kornfeld eines andern kommst, darfst du mit der Hand Ähren abreißen, aber die Sichel darfst du auf dem Kornfeld eines andern nicht schwingen.", footnotes: ["Mt 12,1; Mk 2,23; Lk 6,1"] },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Wenn ein Mann eine Frau geheiratet hat und ihr Ehemann geworden ist, sie ihm dann aber nicht gefällt, weil er an ihr etwas Anstößiges entdeckt, wenn er ihr dann eine Scheidungsurkunde ausstellt, sie ihr übergibt und sie aus seinem Haus fortschickt,", heading: "Die Wiederaufnahme einer geschiedenen Ehefrau: 24,1-4", footnotes: ["Mt 5,31; 19,7; Mk 10,4", "Anstößiges: Was genau gemeint ist, wird nicht deutlich. Vielleicht liegt gar keine Einschränkung des Falles auf einen ganz bestimmten Scheidungsgrund vor. Die Ehescheidung wird jedenfalls als selbstverständlich vorausgesetzt."] },
      { number: 2, text: "wenn sie sein Haus dann verlässt, hingeht und die Frau eines anderen Mannes wird," },
      { number: 3, text: "wenn auch der andere Mann sie nicht mehr liebt, ihr eine Scheidungsurkunde ausstellt, sie ihr übergibt und sie aus seinem Haus fortschickt, oder wenn der andere Mann, der sie geheiratet hat, stirbt," },
      { number: 4, text: "dann darf sie ihr erster Mann, der sie fortgeschickt hat, nicht wieder heiraten, sodass sie wieder seine Frau würde, nachdem sie für ihn unberührbar geworden ist. Das wäre dem Herrn ein Gräuel. Du sollst das Land, das der Herr, dein Gott, dir als Erbbesitz gibt, nicht der Sünde verfallen lassen." },
      { number: 5, text: "Wenn ein Mann neu vermählt ist, muss er nicht mit dem Heer ausrücken. Man soll auch keine andere Leistung von ihm verlangen. Ein Jahr lang darf er frei von Verpflichtungen zu Hause bleiben und die Frau, die er geheiratet hat, erfreuen.", heading: "Die Befreiung Neuvermählter vom Kriegsdienst: 24,5", footnotes: ["20,7"] },
      { number: 6, text: "Man darf nicht die Handmühle oder den oberen Mühlstein als Pfand nehmen; denn dann nimmt man das Leben selbst als Pfand.", heading: "Verbotenes Pfandgut: 24,6" },
      { number: 7, text: "Wenn ein Mann dabei ertappt wird, wie er einen seiner Brüder, einen Israeliten, entführt, ihn als Sklaven kennzeichnet und verkauft, dann soll dieser Entführer sterben. Du sollst das Böse aus deiner Mitte wegschaffen.", heading: "Der Menschenraub: 24,7", footnotes: ["19,19; 21,21; Ex 21,16; 1 Kor 5,13", "Möglicherweise ist hier der ursprüngliche Sinn des siebten Gebots zu greifen. «Entführen» in diesem Gesetz und «stehlen» in 5,19 geben dasselbe hebräische Wort wieder."] },
      { number: 8, text: "Nimm dich in Acht, wenn Aussatz als Seuche auftritt. Achte genau auf alles, wozu euch die levitischen Priester anweisen, und haltet es! So wie ich es ihnen aufgetragen habe, sollt ihr darauf achten und es halten.", heading: "Der Aussatz: 24,8f", footnotes: ["10,8"] },
      { number: 9, text: "Denkt an das, was der Herr, dein Gott, als ihr aus Ägypten zogt, unterwegs mit Mirjam getan hat.", footnotes: ["Num 12"] },
      { number: 10, text: "Wenn du einem andern irgendein Darlehen gibst, sollst du, um das Pfand zu holen, nicht sein Haus betreten.", heading: "Die Pfandeinbringung und Pfandbehandlung: 24,10-13" },
      { number: 11, text: "Du sollst draußen stehen bleiben und der Mann, dem du das Darlehen gibst, soll dir ein Pfand nach draußen bringen." },
      { number: 12, text: "Wenn er in Not ist, sollst du sein Pfand nicht über Nacht behalten.", footnotes: ["(12-13) Ex 22,25f"] },
      { number: 13, text: "Bei Sonnenuntergang sollst du ihm sein Pfand zurückgeben. Dann kann er in seinem Mantel schlafen, er wird dich segnen und du wirst vor dem Herrn, deinem Gott, im Recht sein." },
      { number: 14, text: "Du sollst den Lohn eines Notleidenden und Armen unter deinen Brüdern oder unter den Fremden, die in deinem Land innerhalb deiner Stadtbereiche wohnen, nicht zurückhalten.", heading: "Die Lohnauszahlung an den Tagelöhner: 24,14f", footnotes: ["(14-15) Mt 20,8; Mk 10,19; Jak 5,4; Mal 3,5", "Andere Lesart: Du sollst einen Not leidenden und armen Taglöhner unter deinen Brüdern oder unter den Fremden, die in deinem Land innerhalb deiner Stadtbereiche wohnen, nicht ausbeuten."] },
      { number: 15, text: "An dem Tag, an dem er arbeitet, sollst du ihm auch seinen Lohn geben. Die Sonne soll darüber nicht untergehen; denn er ist in Not und lechzt danach. Dann wird er nicht den Herrn gegen dich anrufen und es wird keine Strafe für eine Sünde über dich kommen." },
      { number: 16, text: "Väter sollen nicht für ihre Söhne und Söhne nicht für ihre Väter mit dem Tod bestraft werden. Jeder soll nur für sein eigenes Verbrechen mit dem Tod bestraft werden.", heading: "Das Verbot der Sippenhaft: 24,16", footnotes: ["7,10; 2 Kön 14,6; Ez 18"] },
      { number: 17, text: "Du sollst das Recht von Fremden, die Waisen sind, nicht beugen; du sollst das Kleid einer Witwe nicht als Pfand nehmen.", heading: "Die sozial Schwachen: 24,17f", footnotes: ["27,19; (17-18) Ex 22,20-23; 23,9"] },
      { number: 18, text: "Denk daran: Als du in Ägypten Sklave warst, hat dich der Herr, dein Gott, dort freigekauft. Darum mache ich es dir zur Pflicht, diese Bestimmung einzuhalten.", footnotes: ["5,15"] },
      { number: 19, text: "Wenn du dein Feld aberntest und eine Garbe auf dem Feld vergisst, sollst du nicht umkehren, um sie zu holen. Sie soll den Fremden, Waisen und Witwen gehören, damit der Herr, dein Gott, dich bei jeder Arbeit deiner Hände segnet.", heading: "Die vergessene Garbe: 24,19", footnotes: ["28,12"] },
      { number: 20, text: "Wenn du einen Ölbaum abgeklopft hast, sollst du nicht auch noch die Zweige absuchen. Was noch hängt, soll den Fremden, Waisen und Witwen gehören.", heading: "Die Nachlese: 24,20-22" },
      { number: 21, text: "Wenn du in deinem Weinberg die Trauben geerntet hast, sollst du keine Nachlese halten. Sie soll den Fremden, Waisen und Witwen gehören." },
      { number: 22, text: "Denk daran: Du bist in Ägypten Sklave gewesen. Darum mache ich es dir zur Pflicht, diese Bestimmung einzuhalten.", footnotes: ["5,15"] },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Wenn zwei Männer eine Auseinandersetzung haben, vor Gericht gehen und man zwischen ihnen die Entscheidung fällt, indem man dem Recht gibt, der im Recht ist, und den schuldig spricht, der schuldig ist,", heading: "Die Prügelstrafe: 25,1-3" },
      { number: 2, text: "dann soll der Richter, falls der Schuldige zu einer Prügelstrafe verurteilt wurde, anordnen, dass er sich hinlegt und in seiner Gegenwart eine bestimmte Anzahl von Schlägen erhält, wie es seiner Schuld entspricht." },
      { number: 3, text: "Vierzig Schläge darf er ihm geben lassen, mehr nicht. Sonst könnte dein Bruder, wenn man ihm darüber hinaus noch viele Schläge gibt, in deinen Augen entehrt werden.", footnotes: ["2 Kor 11,24"] },
      { number: 4, text: "Du sollst dem Ochsen zum Dreschen keinen Maulkorb anlegen.", heading: "Der Ochse beim Dreschen: 25,4", footnotes: ["1 Kor 9,9; 1 Tim 5,18"] },
      { number: 5, text: "Wenn zwei Brüder zusammen wohnen und der eine von ihnen stirbt und keinen Sohn hat, soll die Frau des Verstorbenen nicht die Frau eines fremden Mannes außerhalb der Familie werden. Ihr Schwager soll sich ihrer annehmen, sie heiraten und die Schwagerehe mit ihr vollziehen.", heading: "Die Schwagerehe: 25,5-10", footnotes: ["(5-10) 19,12; (5-6) Mt 22,24; Mk 12,19; Lk 20,28", "5-10: Die Schwagerehe war üblich, doch konnte man sie verweigern. Das hatte jedoch Rechtsfolgen, die in den Symbolhandlungen von VV. 9f deutlich werden. Beim Verkauf eines Grundstücks zog man den Schuh aus und gab ihn dem Käufer (vgl. Rut 4,7). Wenn die Frau ihrem Schwager den Schuh auszieht, entzieht sie ihm den Erbbesitz ihres verstorbenen Mannes, der sonst an ihn fiele. Als Frau, der die Schwagerehe verweigert wurde, ist sie öffentlich entehrt. Durch Anspeien gibt sie die Ehrlosigkeit an den Schuldigen weiter."] },
      { number: 6, text: "Der erste Sohn, den sie gebiert, soll den Namen des verstorbenen Bruders weiterführen. So soll dessen Name in Israel nicht erlöschen." },
      { number: 7, text: "Wenn der Mann aber seine Schwägerin nicht heiraten will und seine Schwägerin zu den Ältesten ans Tor hinaufgeht und sagt: Mein Schwager will dem Namen seines Bruders in Israel keinen Bestand sichern und hat es deshalb abgelehnt, mit mir die Schwagerehe einzugehen!," },
      { number: 8, text: "wenn die Ältesten seiner Stadt ihn dann vorladen und zur Rede stellen, er aber bei seiner Haltung bleibt und erklärt: Ich will sie nicht heiraten!," },
      { number: 9, text: "dann soll seine Schwägerin vor den Augen der Ältesten zu ihm hintreten, ihm den Schuh vom Fuß ziehen, ihm ins Gesicht spucken und ausrufen: So behandelt man einen, der seinem Bruder das Haus nicht baut." },
      { number: 10, text: "Ihm soll man in Israel den Namen geben: Barfüßerhaus." },
      { number: 11, text: "Wenn zwei Männer, ein Mann und sein Bruder, miteinander raufen und die Frau des einen hinzukommt, um ihren Mann aus der Gewalt des andern, der auf ihn einschlägt, zu befreien, und wenn sie die Hand ausstreckt und dessen Schamteile ergreift,", heading: "Übergriff beim Streit: 25,11f" },
      { number: 12, text: "dann sollst du ihr die Hand abhacken. Du sollst in dir kein Mitleid aufsteigen lassen.", footnotes: ["19,13"] },
      { number: 13, text: "Du sollst in deinem Beutel nicht zwei verschiedene Gewichte haben, ein größeres und ein kleineres.", heading: "Falsches Gewicht und Maß: 25,13-16" },
      { number: 14, text: "Du sollst in deinem Haus nicht zwei verschiedene Efa haben, ein größeres und ein kleineres." },
      { number: 15, text: "Volle und richtige Gewichte sollst du haben, volle und richtige Hohlmaße sollst du haben, damit du lange in dem Land lebst, das der Herr, dein Gott, dir gibt." },
      { number: 16, text: "Denn alle, die so etwas tun, alle Betrüger, sind dem Herrn ein Gräuel." },
      { number: 17, text: "Denk daran, was Amalek dir unterwegs angetan hat, als ihr aus Ägypten zogt:", heading: "Die Vergeltung an den Amalekitern: 25,17-19", footnotes: ["(17-19) Ex 17,14; 1 Sam 15"] },
      { number: 18, text: "wie er unterwegs auf dich stieß und, als du müde und matt warst, ohne jede Gottesfurcht alle erschöpften Nachzügler von hinten niedermachte." },
      { number: 19, text: "Wenn der Herr, dein Gott, dir von allen deinen Feinden ringsum Ruhe verschafft hat in dem Land, das der Herr, dein Gott, dir als Erbbesitz gibt, damit du es in Besitz nimmst, dann lösche die Erinnerung an Amalek unter dem Himmel aus! Du sollst nicht vergessen.", footnotes: ["12,10"] },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Wenn du in das Land, das der Herr, dein Gott, dir als Erbbesitz gibt, hineinziehst, es in Besitz nimmst und darin wohnst,", heading: "Die Darbringung der Erstlingsfrüchte: 26,1-11", footnotes: ["(1-11) 14,22-27; 18,4; Ex 23,19; 34,26"] },
      { number: 2, text: "dann sollst du von den ersten Erträgen aller Feldfrüchte, die du in dem Land, das der Herr, dein Gott, dir gibt, eingebracht hast, etwas nehmen und in einen Korb legen. Dann sollst du zu der Stätte ziehen, die der Herr, dein Gott, auswählt, indem er dort seinen Namen wohnen lässt.", footnotes: ["12,10f"] },
      { number: 3, text: "Du sollst vor den Priester treten, der dann amtiert, und sollst zu ihm sagen: Heute bestätige ich vor dem Herrn, deinem Gott, dass ich in das Land gekommen bin, von dem ich weiß: Er hat unseren Vätern geschworen, es uns zu geben.", footnotes: ["17,9; 19,17"] },
      { number: 4, text: "Dann soll der Priester den Korb aus deiner Hand entgegennehmen und ihn vor den Altar des Herrn, deines Gottes, stellen." },
      { number: 5, text: "Du aber sollst vor dem Herrn, deinem Gott, folgendes Bekenntnis ablegen: Mein Vater war ein heimatloser Aramäer. Er zog nach Ägypten, lebte dort als Fremder mit wenigen Leuten und wurde dort zu einem großen, mächtigen und zahlreichen Volk.", footnotes: ["Gen 12,10; 18,18; 46,3; 47,4; (5-8) Num 20,15f"] },
      { number: 6, text: "Die Ägypter behandelten uns schlecht, machten uns rechtlos und legten uns harte Fronarbeit auf.", footnotes: ["Ex 1,11f; 5,9.23; (6-9) 6,21-23"] },
      { number: 7, text: "Wir schrien zum Herrn, dem Gott unserer Väter, und der Herr hörte unser Schreien und sah unsere Rechtlosigkeit, unsere Arbeitslast und unsere Bedrängnis.", footnotes: ["Ex 3,7.9"] },
      { number: 8, text: "Der Herr führte uns mit starker Hand und hoch erhobenem Arm, unter großem Schrecken, unter Zeichen und Wundern aus Ägypten,", footnotes: ["Ex 3,19; 4,8f.21; 8,19; 15,11.16"] },
      { number: 9, text: "er brachte uns an diese Stätte und gab uns dieses Land, ein Land, in dem Milch und Honig fließen.", footnotes: ["6,3; Ex 3,8; 23,20"] },
      { number: 10, text: "Und siehe, nun bringe ich hier die ersten Erträge von den Früchten des Landes, das du mir gegeben hast, Herr. Wenn du den Korb vor den Herrn, deinen Gott, gestellt hast, sollst du dich vor dem Herrn, deinem Gott, niederwerfen." },
      { number: 11, text: "Dann sollst du fröhlich sein und dich freuen über alles Gute, das der Herr, dein Gott, dir und deiner Familie gegeben hat: du, die Leviten und die Fremden in deiner Mitte.", footnotes: ["16,14"] },
      { number: 12, text: "Wenn du im dritten Jahr, dem Zehntjahr, alle Zehntanteile von deiner Ernte vollständig ausgesondert und für die Leviten, Fremden, Waisen und Witwen abgeliefert hast und sie davon in deinen Stadtbereichen essen und satt werden,", heading: "Das Gebet nach der Ablieferung des Zehnten für die Armen: 26,12-15", footnotes: ["14,28f"] },
      { number: 13, text: "dann sollst du vor dem Herrn, deinem Gott, sagen: Ich habe alle heiligen Abgaben aus meinem Haus geschafft. Ich habe sie für die Leviten und die Fremden, für die Waisen und die Witwen gegeben, genau nach deinem Gebot, auf das du mich verpflichtet hast. Ich habe dein Gebot nicht übertreten und habe es nicht vergessen." },
      { number: 14, text: "Ich habe in der Trauerzeit nicht davon gegessen. Ich habe nichts davon weggeschafft, als ich unrein war. Ich habe nichts davon einem Toten gespendet. Ich habe auf die Stimme des Herrn, meines Gottes, gehört. Ich habe alles so gehalten, wie du es mir zur Pflicht gemacht hast." },
      { number: 15, text: "Blick von deiner heiligen Wohnung, vom Himmel, herab, und segne dein Volk Israel und das Land, das du uns gegeben hast, wie du es unseren Vätern geschworen hattest, das Land, wo Milch und Honig fließen.", footnotes: ["28,8"] },
      { number: 16, text: "Heute, an diesem Tag, verpflichtet dich der Herr, dein Gott, diese Gesetze und die Rechtsvorschriften zu halten. Du sollst auf sie achten und sie halten mit ganzem Herzen und mit ganzer Seele.", heading: "Der Grund, das Gesetz zu halten: der Bund:", footnotes: ["5,1"] },
      { number: 17, text: "Heute hast du der Erklärung des Herrn zugestimmt. Er hat dir erklärt: Er will dein Gott werden und du sollst auf seinen Wegen gehen, auf seine Gesetze, Gebote und Rechtsvorschriften achten und auf seine Stimme hören.", footnotes: ["(17-19) 29,12; 2 Sam 7,24", "17-19: Diese in H sehr kompakten, die Gesetzessammlung abschließenden Sätze interpretieren das Gesetz als «Bund» zwischen Gott und Israel. Entsprechend der Struktur von Verträgen der damaligen Welt nennt jede der beiden Parteien sowohl ihre eigenen Verpflichtungen als auch die der Gegenseite. Hinter den Formulierungen für die Verpflichtung Gottes steht die Bundesformel: Ihr seid mein Volk und ich bin euer Gott (vgl. 29,12)."] },
      { number: 18, text: "Und der Herr hat heute deiner Erklärung zugestimmt. Du hast ihm erklärt: Du möchtest das Volk werden, das ihm persönlich gehört, wie er es dir zugesagt hat. Du willst auf alle seine Gebote achten;", footnotes: ["(18-19) 7,6"] },
      { number: 19, text: "er soll dich über alle Völker, die er geschaffen hat, erheben - zum Lob, zum Ruhm, zur Zierde -; und du möchtest ein Volk werden, das ihm, dem Herrn, deinem Gott, heilig ist, wie er es zugesagt hat.", footnotes: ["28,1.9"] },
    ] },
    { number: 27, verses: [
      { number: 1, text: "¥Mose und die Ältesten Israels befahlen dem Volk: Achtet auf das ganze Gebot, auf das ich euch heute verpflichte.", heading: "Einschub: Aufträge für die Zeit nach dem Jordanübergang: 27,1-26", footnotes: ["5,23; (1-13) Jos 8,30-35"] },
      { number: 2, text: "An dem Tag, wenn ihr über den Jordan zieht in das Land, das der Herr, dein Gott, dir gibt, sollst du große Steine aufrichten, sie mit Kalk bestreichen" },
      { number: 3, text: "und alle Worte dieser Weisung darauf schreiben, wenn du hinüberziehst, um in das Land, das der Herr, dein Gott, dir gibt, das Land, wo Milch und Honig fließen, hineinzuziehen, wie der Herr, der Gott deiner Väter, es dir zugesagt hat." },
      { number: 4, text: "Wenn ihr über den Jordan zieht, sollt ihr diese Steine, die zu errichten ich euch heute befehle, auf dem Berg Garizim aufrichten. Mit Kalk sollst du sie bestreichen.", footnotes: ["Joh 4,20", "Garizim, andere Lesart: Ebal; sie stammt aus antisamaritischer Tendenz, denn nach V. 13 ist der Ebal der Berg des Verfluchens und der Tempel der Samariter stand auf dem Garizim."] },
      { number: 5, text: "Dort sollst du dem Herrn, deinem Gott, einen Altar bauen, einen Altar aus Steinen. Du darfst nicht mit Eisenwerkzeug daran arbeiten.", footnotes: ["(5-6) Ex 20,25"] },
      { number: 6, text: "Aus unbehauenen Steinen sollst du den Altar des Herrn, deines Gottes, bauen und auf ihm sollst du Brandopfertiere für den Herrn, deinen Gott, verbrennen." },
      { number: 7, text: "Dort sollst du Heilsopfertiere schlachten und verzehren und vor dem Herrn, deinem Gott, fröhlich sein." },
      { number: 8, text: "Und auf die Steine sollst du in schöner Schrift alle Worte dieser Weisung schreiben." },
      { number: 9, text: "Mose und die levitischen Priester sagten zu ganz Israel: Sei still und höre, Israel: Heute, an diesem Tag, bist du das Volk des Herrn, deines Gottes, geworden.", footnotes: ["7,6; 10,8; 26,17-19"] },
      { number: 10, text: "Du sollst auf die Stimme des Herrn, deines Gottes, hören und seine Gebote und Gesetze halten, auf die ich dich heute verpflichte.", footnotes: ["28,1"] },
      { number: 11, text: "Am gleichen Tag befahl Mose dem Volk:", footnotes: ["(11-13) 11,29"] },
      { number: 12, text: "Folgende Stämme sollen sich auf dem Berg Garizim aufstellen, um das Volk zu segnen, wenn ihr den Jordan überschritten habt: Simeon, Levi, Juda, Issachar, Josef und Benjamin." },
      { number: 13, text: "Folgende Stämme sollen sich am Berg Ebal aufstellen, um den Fluch zu sprechen: Ruben, Gad, Ascher, Sebulon, Dan und Naftali." },
      { number: 14, text: "Die Leviten sollen über alle Männer Israels mit lauter Stimme ausrufen:", footnotes: ["14-26: Vom letzten Satz abgesehen, richten sich die Verfluchungen alle gegen Verbrechen, die im Geheimen geschehen sind. Da menschliche Justiz sie nicht fassen kann, werden sie der göttlichen Ahndung überantwortet."] },
      { number: 15, text: "Verflucht ist der Mann, der ein Gottesbildnis, das dem Herrn ein Gräuel ist, ein Künstlermachwerk, schnitzt oder gießt und es heimlich aufstellt. Und das ganze Volk soll ausrufen: Amen.", footnotes: ["4,16.28; 5,8; 7,25; 9,12; 31,29; Ri 17,3; 1 Kön 14,9; 2 Kön 17,16; 21,7"] },
      { number: 16, text: "Verflucht, wer Vater oder Mutter schmäht. Und das ganze Volk soll rufen: Amen.", footnotes: ["5,16; Ex 21,17"] },
      { number: 17, text: "Verflucht, wer den Grenzstein seines Nachbarn verrückt. Und das ganze Volk soll rufen: Amen.", footnotes: ["5,21; 19,14"] },
      { number: 18, text: "Verflucht, wer einem Blinden den falschen Weg weist. Und das ganze Volk soll rufen: Amen." },
      { number: 19, text: "Verflucht, wer das Recht der Fremden, die Waisen sind, und das der Witwen beugt. Und das ganze Volk soll rufen: Amen.", footnotes: ["24,17; Ex 22,20-23; 23,9"] },
      { number: 20, text: "Verflucht, wer sich mit der Frau seines Vaters hinlegt, denn er deckt das Bett seines Vaters auf. Und das ganze Volk soll rufen: Amen.", footnotes: ["23,1; 2 Sam 16,22; 1 Kor 5,1"] },
      { number: 21, text: "Verflucht, wer sich mit irgendeinem Tier hinlegt. Und das ganze Volk soll rufen: Amen.", footnotes: ["Ex 22,18"] },
      { number: 22, text: "Verflucht, wer sich mit seiner Schwester hinlegt, mit der Tochter seines Vaters oder mit der Tochter seiner Mutter. Und das ganze Volk soll rufen: Amen." },
      { number: 23, text: "Verflucht, wer sich mit seiner Schwiegermutter hinlegt. Und das ganze Volk soll rufen: Amen." },
      { number: 24, text: "Verflucht, wer einen andern heimlich erschlägt. Und das ganze Volk soll rufen: Amen.", footnotes: ["5,17; Ex 21,12"] },
      { number: 25, text: "Verflucht, wer sich bestechen lässt, einen unschuldigen Menschen zu töten. Und das ganze Volk soll rufen: Amen.", footnotes: ["19,10; Ex 23,8"] },
      { number: 26, text: "Verflucht, wer nicht die Worte dieser Weisung stützt, indem er sie hält. Und das ganze Volk soll rufen: Amen.", footnotes: ["2 Kor 3,9; Gal 3,10"] },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Wenn du auf die Stimme des Herrn, deines Gottes, hörst, indem du auf alle seine Gebote, auf die ich dich heute verpflichte, achtest und sie hältst, wird dich der Herr, dein Gott, über alle Völker der Erde erheben.", heading: "Der Abschluss der Verkündigung des Gesetzes: 28,1-68 | Der Segen für Gehorsam: 28,1-14", footnotes: ["26,19; (1-14) Lev 26,3-13; Dtn 28,15-45; 7,12-16; 11,13-15; 30,16"] },
      { number: 2, text: "Alle diese Segnungen werden über dich kommen und dich erreichen, wenn du auf die Stimme des Herrn, deines Gottes, hörst:" },
      { number: 3, text: "Gesegnet bist du in der Stadt, gesegnet bist du auf dem Land." },
      { number: 4, text: "Gesegnet ist die Frucht deines Leibes, die Frucht deines Ackers und die Frucht deines Viehs, die Kälber, Lämmer und Zicklein.", footnotes: ["7,13; 30,9; Lk 1,42"] },
      { number: 5, text: "Gesegnet ist dein Korb und dein Backtrog." },
      { number: 6, text: "Gesegnet bist du, wenn du heimkehrst, gesegnet bist du, wenn du ausziehst." },
      { number: 7, text: "Der Herr stößt die Feinde, die sich gegen dich erheben, nieder und liefert sie dir aus. Auf einer Straße ziehen sie gegen dich aus, auf sieben Straßen fliehen sie vor dir." },
      { number: 8, text: "Der Herr befiehlt dem Segen, an deiner Seite zu sein: in deinen Speichern und bei allem, was deine Hände schaffen. Der Herr segnet dich in dem Land, das er, dein Gott, dir gibt.", footnotes: ["26,15"] },
      { number: 9, text: "Der Herr lässt dich erstehen als das Volk, das ihm heilig ist, wie er es dir unter der Bedingung geschworen hat, dass du auf die Gebote des Herrn, deines Gottes, achtest und auf seinen Wegen gehst.", footnotes: ["7,6; 26,18f"] },
      { number: 10, text: "Dann sehen alle Völker der Erde, dass der Name des Herrn über dir ausgerufen ist, und fürchten sich vor dir.", footnotes: ["2,25; 11,25; 2 Sam 12,28; 1 Kön 8,43; Jer 14,9"] },
      { number: 11, text: "Der Herr schenkt dir Gutes im Überfluss bei der Frucht deines Leibes, bei der Frucht deines Viehs und bei der Frucht deines Ackers in dem Land, von dem du weißt: Der Herr hat deinen Vätern geschworen, es dir zu geben." },
      { number: 12, text: "Der Herr öffnet dir den Himmel, seine Schatzkammer voll köstlichen Wassers: Er gibt deinem Land zur rechten Zeit Regen und segnet jede Arbeit deiner Hände. An viele Völker kannst du ausleihen und du brauchst nichts zu entleihen.", footnotes: ["2,7; 14,29; 16,15; 24,19; 30,9; 15,6", "Andere Übersetzungsmöglichkeit: Der Herr tut dir sein köstliches Schatzhaus auf, den Himmel."] },
      { number: 13, text: "Der Herr macht dich zum Kopf und nicht zum Schwanz. Du kennst nur den Aufstieg, du kennst keinen Abstieg, wenn du auf die Gebote des Herrn, deines Gottes, auf die ich dich heute verpflichte, hörst, auf sie achtest und sie hältst." },
      { number: 14, text: "Von allen Worten, die ich euch heute vorschreibe, sollst du weder rechts noch links abweichen. Du sollst nicht anderen Göttern nachfolgen und ihnen dienen.", footnotes: ["7,4; 13,3"] },
      { number: 15, text: "Wenn du nicht auf die Stimme des Herrn, deines Gottes, hörst, indem du nicht auf alle seine Gebote und Gesetze, auf die ich dich heute verpflichte, achtest und sie nicht hältst, werden alle diese Verfluchungen über dich kommen und dich erreichen:", heading: "Der Fluch für Ungehorsam: 28,15-68", footnotes: ["(15-68) Lev 26,14-38; Dtn 4,25-28; 8,19f; 11,16f; 30,17f (15-45) 28,1-14"] },
      { number: 16, text: "Verflucht bist du in der Stadt, verflucht bist du auf dem Land." },
      { number: 17, text: "Verflucht ist dein Korb und dein Backtrog." },
      { number: 18, text: "Verflucht ist die Frucht deines Leibes und die Frucht deines Ackers, die Kälber, Lämmer und Zicklein." },
      { number: 19, text: "Verflucht bist du, wenn du heimkehrst, verflucht bist du, wenn du ausziehst." },
      { number: 20, text: "Verfluchtsein, Verwirrtsein, Verwünschtsein lässt der Herr auf dich los, auf alles, was deine Hände schaffen und was du tust, bis du bald vernichtet und bis du ausgetilgt bist wegen deines Tuns, durch das du mich böswillig verlassen hast.", footnotes: ["zum Ende des Verses vgl. die Anmerkung zu 11,13-15."] },
      { number: 21, text: "Der Herr heftet die Pest an dich, bis er dich ausgemerzt hat aus dem Land, in das du hineinziehst, um es in Besitz zu nehmen.", footnotes: ["1 Kön 8,37"] },
      { number: 22, text: "Der Herr schlägt dich mit Schwindsucht, Fieber und Brand, mit Glut und Trockenheit, Versengung und Vergilbung. Sie verfolgen dich, bis du ausgetilgt bist.", footnotes: ["mit Glut und Trockenheit, andere Lesart: mit Glut und Schwert. - Der Vers zählt wahrscheinlich nur menschliche Krankheiten auf."] },
      { number: 23, text: "Der Himmel über deinem Kopf wird zu Erz, die Erde unter dir wird zu Eisen.", footnotes: ["11,17; 1 Kön 8,35"] },
      { number: 24, text: "Der Herr verwandelt den Regen, den dein Land erhält, in Staub. Asche fällt vom Himmel auf dich herab, bis du vernichtet bist.", footnotes: ["Andere Lesart: Der Herr verwandelt den Regen, den dein Land erhält, in Staub und Asche. Beide fallen vom Himmel auf dich herab, bis du vernichtet bist."] },
      { number: 25, text: "Der Herr stößt dich nieder und liefert dich deinen Feinden aus. Auf einer Straße ziehst du gegen sie aus, auf sieben Straßen fliehst du vor ihnen. Alle Könige der Erde erschauern vor dir.", footnotes: ["1 Kön 8,33"] },
      { number: 26, text: "Deine Leichen liegen da, zum Fraß für alle Vögel des Himmels und für die Tiere der Erde und keiner verscheucht sie." },
      { number: 27, text: "Der Herr schlägt dich mit dem ägyptischen Geschwür, mit Beulen, Krätze und Grind und keiner kann dich heilen.", footnotes: ["7,15; 28,60; Ex 9,9; Offb 16,2"] },
      { number: 28, text: "Der Herr schlägt dich mit Wahnsinn, Blindheit und Irresein." },
      { number: 29, text: "Am hellen Mittag tappst du im Dunkel wie ein Blinder. Deine Wege führen nicht zum Erfolg. Dein Leben lang wirst du ausgebeutet und ausgeraubt und niemand hilft dir." },
      { number: 30, text: "Du verlobst dich mit einer Frau und ein anderer schläft mit ihr. Du baust ein Haus und wohnst nicht darin. Du legst einen Weinberg an und hältst nicht einmal die erste Lese.", footnotes: ["20,5-7"] },
      { number: 31, text: "Dein Ochse wird vor deinen Augen geschlachtet und du bekommst nicht einmal davon zu essen. Dein Esel wird dir weggerissen und kehrt nicht zurück. Deine Schafe und Ziegen werden deinen Feinden ausgeliefert und niemand kommt dir zu Hilfe." },
      { number: 32, text: "Deine Söhne und Töchter werden einem anderen Volk ausgeliefert, du siehst dir den ganzen Tag nach ihnen die Augen aus und zwingst sie doch nicht herbei." },
      { number: 33, text: "Den Ertrag deines Ackers und all deinen Gewinn verzehrt ein Volk, das du früher nicht einmal gekannt hast, und du wirst dein Leben lang nur ausgebeutet und misshandelt." },
      { number: 34, text: "Wahnsinn befällt dich bei dem Anblick, der sich deinen Augen bietet." },
      { number: 35, text: "Der Herr schlägt dich mit bösen Geschwüren am Knie und am Schenkel, und keiner kann dich heilen. Von der Sohle bis zum Scheitel bist du krank.", footnotes: ["Offb 16,2"] },
      { number: 36, text: "Der Herr bringt dich und den König, den du über dich eingesetzt hast, zu einem Volk, das du und deine Väter früher nicht einmal gekannt haben, und dort musst du anderen Göttern dienen, Göttern aus Holz und Stein.", footnotes: ["4,28; 17,15; 28,64; 2 Kön 17,4-6; 25,7.11"] },
      { number: 37, text: "Alle Völker, zu denen der Herr dich führt, wenden sich entsetzt von dir ab, sagen Spottverse über dich auf und stimmen Hohngelächter an.", footnotes: ["1 Kön 9,7"] },
      { number: 38, text: "Viel Saatgut trägst du aufs Feld, aber du erntest wenig. Das andere hat die Heuschrecke gefressen." },
      { number: 39, text: "Weinberge legst du an und pflegst sie, aber du trinkst keinen Wein und bringst keinen in den Keller. Der Wurm hat ihn weggefressen." },
      { number: 40, text: "Ölbäume wachsen überall in deinem Land, aber du hast kein Öl, um dich zu salben. Dein Ölbaum hat die Oliven abgeworfen." },
      { number: 41, text: "Söhne und Töchter hast du gezeugt, aber sie sind nicht bei dir. Sie sind als Gefangene weggezogen." },
      { number: 42, text: "Alle deine Bäume und Feldfrüchte nimmt das Ungeziefer in Besitz." },
      { number: 43, text: "Der Fremde, der in deiner Mitte wohnt, steigt immer höher nach oben, hoch über dich hinaus, und du steigst immer tiefer hinab." },
      { number: 44, text: "Er leiht dir aus und du kannst ihm nichts ausleihen. Er wird zum Kopf und du wirst zum Schwanz." },
      { number: 45, text: "Alle diese Verfluchungen werden über dich kommen, dich verfolgen und dich erreichen, bis du vernichtet bist, wenn du auf die Stimme des Herrn, deines Gottes, nicht hörst und nicht auf seine Gebote und Gesetze, auf die er dich verpflichtet hat, achtest." },
      { number: 46, text: "Für immer werden sie als Zeichen und Wunder an dir und an deinen Nachkommen haften." },
      { number: 47, text: "Weil du dem Herrn, deinem Gott, nicht gedient hast aus Freude und Dankbarkeit dafür, dass alles in Fülle da war," },
      { number: 48, text: "musst du deinen Feinden dienen, die der Herr gegen dich ausgesandt hat. Hunger und Durst wirst du leiden, nackt sein und nichts mehr haben. Er legt dir ein eisernes Joch auf den Nacken, bis er dich vernichtet hat.", footnotes: ["Jer 28,13f"] },
      { number: 49, text: "Der Herr trägt zum Kampf gegen dich ein Volk aus der Ferne herbei, von den Enden der Erde, das wie ein Adler herabstößt, ein Volk, dessen Sprache du noch nie gehört hast,", footnotes: ["Jer 5,15; 1 Kor 14,21"] },
      { number: 50, text: "ein Volk mit unbeweglichem Gesicht, das sich dem Greis nicht zuwendet und für das Kind kein Mitleid zeigt." },
      { number: 51, text: "Es verzehrt die Frucht deines Viehs und die Frucht deines Ackers, bis du vernichtet bist. Es lässt dir nichts übrig vom Korn, vom Wein und vom Öl, von den Kälbern, Lämmern und Zicklein, bis es dich ausgetilgt hat." },
      { number: 52, text: "Es belagert dich in allen deinen Städten, bis die Mauern fallen, die hohen, fest gefügten Mauern, auf die du dich in deinem ganzen Land verlässt. Es belagert dich in allen deinen Städten in dem ganzen Land, das der Herr, dein Gott, dir gegeben hat.", footnotes: ["Jer 5,17"] },
      { number: 53, text: "In der Not der Belagerung, wenn dein Feind dich einschnürt, musst du die Frucht deines eigenen Leibes essen, das Fleisch deiner Söhne und Töchter, die dir der Herr, dein Gott, geschenkt hat.", footnotes: ["2 Kön 6,28f; Jer 19,9"] },
      { number: 54, text: "Der weichlichste und verwöhnteste Mann blickt dann bei dir missgünstig auf seinen Bruder, auf die Frau, mit der er schläft, und auf den Rest der Kinder, die er noch übrig gelassen hat," },
      { number: 55, text: "und möchte niemandem etwas abgeben von dem Fleisch seiner Kinder, das er isst, weil ihm keine Nahrung mehr übrig geblieben ist in der Not der Belagerung, wenn dein Feind dich in allen deinen Städten einschnürt." },
      { number: 56, text: "Die weichlichste und verwöhnteste Frau, die noch nie versucht hat, ihren Fuß auf die Erde zu setzen vor lauter Verwöhntheit und Verweichlichung, blickt missgünstig auf den Mann, mit dem sie schläft, auf ihren Sohn und ihre Tochter," },
      { number: 57, text: "auf die Nachgeburt, die zwischen ihren Beinen hervorkommt, und auf die Kinder, die sie noch gebären wird; denn sie will sie heimlich essen, weil sie nichts mehr hat in der Not der Belagerung, wenn dein Feind dich in allen deinen Städten einschnürt." },
      { number: 58, text: "Wenn du nicht auf alle Worte dieser Weisung, die in dieser Urkunde aufgezeichnet sind, achtest und sie hältst, aus Furcht vor diesem herrlichen und Furcht erregenden Namen, vor Jahwe, deinem Gott," },
      { number: 59, text: "wird der Herr die Schläge, die er dir und deinen Nachkommen versetzt, über alles Gewohnte hinaus steigern zu gewaltigen und hartnäckigen Schlägen, zu schlimmen und hartnäckigen Krankheiten.", footnotes: ["(59-60) 28,27"] },
      { number: 60, text: "Er wird alle ägyptischen Seuchen, vor denen du Angst hast, wieder über dich bringen und sie werden an dir haften bleiben." },
      { number: 61, text: "Auch alle Krankheiten und Schläge, die nicht in der Urkunde dieser Weisung aufgezeichnet sind, wird der Herr über dich bringen, bis du vernichtet bist." },
      { number: 62, text: "Dann werden nur noch wenige Leute von euch übrig bleiben, statt dass ihr zahlreich seid wie die Sterne am Himmel; denn du hast nicht auf die Stimme des Herrn, deines Gottes, gehört.", footnotes: ["1,10"] },
      { number: 63, text: "So wie der Herr seine Freude daran hatte, euch Gutes zu tun und euch zahlreich zu machen, so wird der Herr seine Freude daran haben, euch auszutilgen und euch zu vernichten. Ihr werdet aus dem Land, in das du nun hineinziehst, um es in Besitz zu nehmen, herausgerissen werden.", footnotes: ["30,9"] },
      { number: 64, text: "Der Herr wird dich unter alle Völker verstreuen, vom einen Ende der Erde bis zum anderen Ende der Erde. Dort musst du anderen Göttern dienen, die du und deine Väter vorher nicht einmal gekannt haben, Göttern aus Holz und Stein.", footnotes: ["28,36; Lk 21,24"] },
      { number: 65, text: "Unter diesen Nationen wirst du keine Ruhe finden. Es wird keine Stelle geben, wohin du deinen Fuß setzen kannst. Der Herr wird dir dort das Herz erzittern, die Augen verlöschen und den Atem stocken lassen." },
      { number: 66, text: "Du wirst in Lebensgefahr schweben, bei Nacht und bei Tag erschrecken und deines Lebens nicht mehr sicher sein." },
      { number: 67, text: "Am Morgen wirst du sagen: Wenn es doch schon Abend wäre!, und am Abend: Wenn es doch schon Morgen wäre! - um dem Schrecken zu entfliehen, der dein Herz befällt, und dem Anblick, der sich deinen Augen bietet." },
      { number: 68, text: "Der Herr wird dich auf Schiffen nach Ägypten zurückbringen, auf einem Weg, von dem ich dir gesagt hatte: Du sollst ihn niemals wieder sehen. Dort werdet ihr euch deinen Feinden als Sklaven und Sklavinnen zum Verkauf anbieten, aber niemand wird euch kaufen.", footnotes: ["17,16; Gen 26,2; Hos 8,13; 9,3"] },
      { number: 69, text: "¥Das sind die Worte, mit denen der Bund geschlossen wurde, den Mose im Auftrag des Herrn in Moab mit den Israeliten schloss, zusätzlich zu dem Bund, den er mit ihnen am Horeb geschlossen hatte.", heading: "Die letzten Verfügungen des Mose: 28,69 - 32,52", footnotes: ["5,2", "Manche Bibelausgaben zählen diesen Vers schon als 29,1; entsprechend ändert sich die Verszählung in Kap. 29. V. 69 ist jedoch Einführung zum Folgenden, nicht Abschluss des Vorangehenden, und steht parallel zu 1,1; 4,44 und 33,1."] },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Mose rief ganz Israel zusammen und sagte zu den Israeliten: Ihr habt alles gesehen, was der Herr in Ägypten vor euren Augen mit dem Pharao, mit seinem ganzen Hof und seinem ganzen Land getan hat.", heading: "Rückblick: 29,1-8", footnotes: ["(1-8) 8,2-6; (1-3) 1,19.31; 3,21; 4,3.9.35; 5,24; 7,18f; 10,21; 17,7; 29,16.21"] },
      { number: 2, text: "Mit eigenen Augen hast du jene schweren Prüfungen, die großen Zeichen und Wunder gesehen." },
      { number: 3, text: "Aber einen Verstand, der wirklich erkennt, Augen, die wirklich sehen, und Ohren, die wirklich hören, hat der Herr euch bis zum heutigen Tag nicht gegeben.", footnotes: ["Mt 13,14; Röm 11,8"] },
      { number: 4, text: "Ich habe euch vierzig Jahre lang durch die Wüste geführt. Eure Kleider sind euch nicht in Lumpen vom Leib gefallen, deine Schuhe sind dir nicht an den Füßen zerrissen,", footnotes: ["2,7; 8,4", "4f: Gottesrede; vgl. die Anmerkung zu 11,13-15."] },
      { number: 5, text: "ihr habt kein Brot gegessen und keinen Wein und kein Bier getrunken, denn ihr solltet erkennen: Ich bin Jahwe, euer Gott." },
      { number: 6, text: "Und so kamt ihr bis an diesen Ort. Sihon, der König von Heschbon, und Og, der König des Baschan, sind gegen uns zum Kampf ausgerückt und wir haben sie geschlagen.", footnotes: ["(6-7) 2,24 - 3,13"] },
      { number: 7, text: "Wir haben ihnen ihr Land genommen und es den Rubenitern, den Gaditern und der Hälfte des Stammes der Manassiter als Erbbesitz gegeben." },
      { number: 8, text: "Darum achtet auf die Bestimmungen dieses Bundes und haltet sie, damit euch alles, was ihr tut, gelingt.", footnotes: ["Jos 1,7f"] },
      { number: 9, text: "Ihr habt euch heute alle vor dem Herrn, eurem Gott, aufgestellt: eure Anführer, Stammesführer, Ältesten und Listenführer, alle Männer Israels,", heading: "Partner und Inhalt des Bundes: 29,9-14", footnotes: ["1,13; 5,23; 16,18; 33,5", "eure Anführer, Stammesführer, Ältesten und Listenführer, andere Übersetzungsmöglichkeit: eure Stammesführer, Ältesten und Listenführer."] },
      { number: 10, text: "eure Kinder und Greise, eure Frauen und auch die Fremden in deinem Lager, vom Holzarbeiter bis zum Wasserträger.", footnotes: ["Jos 9,27"] },
      { number: 11, text: "Du schreitest jetzt zwischen den Zeichen des Bundes mit dem Herrn, deinem Gott, hindurch, den Zeichen der Selbstverwünschung. Der Herr, dein Gott, schließt heute mit dir diesen Bund,", footnotes: ["Wörtlich: Du schreitest jetzt durch in dem Bund deines Gottes und in seiner Verwünschung. - Diese Worte begleiteten wahrscheinlich eine symbolische Handlung, in der man zwischen den Teilen zerstückelter Tiere hindurchschritt. Man brachte dadurch zum Ausdruck, dass es einem so wie den Tieren ergehen soll, wenn man seine Zusage nicht hält (vgl. Gen 15,9f.17; Jer 34,18-20)."] },
      { number: 12, text: "um dich heute als sein Volk einzusetzen und dein Gott zu werden, wie er es dir zugesagt und deinen Vätern Abraham, Isaak und Jakob geschworen hat.", footnotes: ["26,17-19"] },
      { number: 13, text: "Nicht mit euch allein schließe ich diesen Bund und setze diese Verwünschung in Kraft,", footnotes: ["(13-14) 5,3"] },
      { number: 14, text: "sondern ich schließe ihn mit denen, die heute hier bei uns vor dem Herrn, unserem Gott, stehen, und mit denen, die heute nicht hier bei uns sind." },
      { number: 15, text: "Ihr wisst noch von unserem Aufenthalt in Ägypten und von unserem Zug mitten durch die Völker, deren Gebiet ihr durchziehen musstet.", heading: "Die Strafe für den geheimen Vorbehalt beim Bundesschwur: 29,15-20" },
      { number: 16, text: "Ihr habt bei ihnen Scheusale und Götzen aus Holz und Stein, aus Silber und Gold gesehen." },
      { number: 17, text: "Es soll keinen unter euch geben, weder Mann noch Frau, weder Sippe noch Stamm, der heute sein Herz vom Herrn, unserem Gott, abwendet und anfängt, den Göttern dieser Völker zu dienen. Es soll bei euch keine Wurzel wachsen, die Gift und Wermut hervorbringt,", footnotes: ["Apg 8,23; Hebr 12,15"] },
      { number: 18, text: "das heißt keinen, der beim Hören der Worte dieser Verwünschung insgeheim folgenden Gegensegen über sich spricht: Mir soll nichts geschehen, wenn ich aus eigenem Entschluss etwas tue, damit Wasserfülle die Dürre beendet.", footnotes: ["Gemeint ist, dass er insgeheim sich an einen anderen Gott wendet, den er als Regenspender betrachtet. Andere Übersetzungsmöglichkeit: keinen, der sich beim Hören deiner Worte insgeheim Segen verspricht, indem er sagt: Mir soll nichts geschehen, auch wenn ich mit verstocktem Herzen lebe, sodass das bewässerte Land mit dem verdorrten zusammen weggerafft wird. - Bewässertes und verdorrtes Land zusammen würde dann «alle» bedeuten, die Guten und die Sünder."] },
      { number: 19, text: "Der Herr wird sich weigern, ihm zu verzeihen, er wird schnauben vor Zorn und Eifersucht gegen einen solchen Menschen. Jede Verwünschung, die in dieser Urkunde aufgezeichnet ist, wird auf ihn lauern und der Herr wird seinen Namen unter dem Himmel auslöschen.", footnotes: ["(19-20) 28,15-68; Offb 22,18"] },
      { number: 20, text: "Entsprechend den Verwünschungen, die beim Abschluss des Bundes gesprochen werden und in dieser Urkunde der Weisung einzeln aufgezeichnet sind, wird der Herr ihn von allen Stämmen Israels absondern, damit es ihm schlecht ergeht." },
      { number: 21, text: "Dann wird eine spätere Generation, also eure Söhne, die nach euch erstehen, und die Ausländer, die aus fernen Ländern kommen, die Schläge sehen, die dieses Land getroffen haben, und die Seuchen, die der Herr in ihm ausbrechen ließ:", heading: "Die Androhung der Vertreibung: 29,21-28", footnotes: ["(21-27) 1 Kön 9,8f; Jer 5,19; 13,22; 16,10-13; 22,8f"] },
      { number: 22, text: "Schwefel und Salz bedecken es; seine Fläche ist eine einzige Brandstätte; es kann nicht besät werden und lässt nichts aufkeimen; kein Hälmchen kann wachsen; alles ist wie nach der Zerstörung von Sodom und Gomorra, Adma und Zebojim, die der Herr in seinem glühenden Zorn zerstört hat.", footnotes: ["Gen 14,2; 19,15-29; Jer 49,18; 50,40"] },
      { number: 23, text: "Dann werden sie und alle Völker fragen: Warum hat der Herr diesem Land so etwas angetan? Warum entbrannte dieser gewaltige Zorn?" },
      { number: 24, text: "Und man wird antworten: Weil sie den Bund verlassen haben, den Jahwe, der Gott ihrer Väter, mit ihnen geschlossen hatte, als er sie aus Ägypten führte,", footnotes: ["4,23; 17,2; 31,16.20; Jos 7,11.15; 23,16; Ri 2,20; 1 Kön 11,11; 2 Kön 17,15.38; 18,12"] },
      { number: 25, text: "weil sie angefangen haben, anderen Göttern zu dienen und sich vor ihnen niederzuwerfen, Göttern, die sie vorher nicht einmal gekannt hatten und die er ihnen nicht zugewiesen hatte.", footnotes: ["4,19"] },
      { number: 26, text: "Deshalb entbrannte der Zorn des Herrn gegen dieses Land. Deshalb brachte der Herr den ganzen Fluch, der in dieser Urkunde aufgezeichnet ist, über das Land," },
      { number: 27, text: "riss sie mit glühendem Zorn und großem Unwillen aus ihrem Land heraus und warf sie in ein anderes Land, in dem sie heute noch sind." },
      { number: 28, text: "Was noch verborgen ist, steht bei dem Herrn, unserem Gott. Was schon offenbar ist, gilt für uns und unsere Kinder auf ewig: dass wir alle Bestimmungen dieser Weisung halten sollen.", footnotes: ["32,34", "Andere Übersetzungsmöglichkeit: Die verhüllte Rolle ist das Exemplar für den Herrn, unseren Gott. Die unverhüllte Rolle ist für alle Zeit das Exemplar für uns und unsere Kinder. Wir sollen alle Bestimmungen dieser Weisung halten. - In diesem Fall würde darauf angespielt, dass bei Bundesschlüssen für jeden der beiden Partner eine Urkunde ausgestellt wurde."] },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Und wenn alle diese Worte über dich gekommen sind, der Segen und der Fluch, die ich dir vorgelegt habe, wenn du sie dir zu Herzen nimmst mitten unter den Völkern, unter die der Herr, dein Gott, dich versprengt hat,", heading: "Die Heimkehrverheißung: 30,1-10", footnotes: ["28,1-68; (1-10) 4,29-31; Jer 29,10-14; 32,37-42"] },
      { number: 2, text: "und wenn du zum Herrn, deinem Gott, zurückkehrst und auf seine Stimme hörst in allem, wozu ich dich heute verpflichte, du und deine Kinder, mit ganzem Herzen und mit ganzer Seele,", footnotes: ["4,30"] },
      { number: 3, text: "dann wird der Herr, dein Gott, dein Schicksal wenden, er wird sich deiner erbarmen, sich dir zukehren und dich aus allen Völkern zusammenführen, unter die der Herr, dein Gott, dich verstreut hat.", footnotes: ["28,64"] },
      { number: 4, text: "Und wenn einige von dir bis ans Ende des Himmels versprengt sind, wird dich der Herr, dein Gott, von dort zusammenführen, von dort wird er dich holen.", footnotes: ["Mt 24,31"] },
      { number: 5, text: "Und der Herr, dein Gott, wird dich in das Land, das deine Väter in Besitz genommen haben, zurückbringen. Du wirst es wieder in Besitz nehmen und er wird dich glücklicher und zahlreicher machen als deine Väter." },
      { number: 6, text: "Der Herr, dein Gott, wird dein Herz und das Herz deiner Nachkommen beschneiden. Dann wirst du den Herrn, deinen Gott, mit ganzem Herzen und mit ganzer Seele lieben können, damit du Leben hast.", footnotes: ["10,16; 11,1; Röm 2,29"] },
      { number: 7, text: "Alle diese Verwünschungen aber wird der Herr, dein Gott, über deine Feinde und Gegner verhängen, die dich verfolgt haben." },
      { number: 8, text: "Du jedoch wirst umkehren, auf die Stimme des Herrn hören und alle seine Gebote, auf die ich dich heute verpflichte, halten," },
      { number: 9, text: "und der Herr, dein Gott, wird dir Gutes im Überfluss schenken, bei jeder Arbeit deiner Hände, bei der Frucht deines Leibes, bei der Frucht deines Viehs und bei der Frucht deines Ackers. Denn der Herr wird sich, wie er sich an deinen Vätern gefreut hat, auch an dir wieder freuen. Er wird dir Gutes tun,", footnotes: ["28,4.11f.63"] },
      { number: 10, text: "wenn du auf die Stimme des Herrn, deines Gottes, hörst und auf seine Gebote und Gesetze achtest, die in dieser Urkunde der Weisung einzeln aufgezeichnet sind, und wenn du zum Herrn, deinem Gott, mit ganzem Herzen und mit ganzer Seele zurückkehrst." },
      { number: 11, text: "Dieses Gebot, auf das ich dich heute verpflichte, geht nicht über deine Kraft und ist nicht fern von dir.", heading: "Das dem Menschen nahe Gebot: 30,11-14", footnotes: ["1 Joh 5,3; (11-14) Jer 31,31-34"] },
      { number: 12, text: "Es ist nicht im Himmel, sodass du sagen müsstest: Wer steigt für uns in den Himmel hinauf, holt es herunter und verkündet es uns, damit wir es halten können?", footnotes: ["(12-14) Röm 10,6-10; Joh 3,13"] },
      { number: 13, text: "Es ist auch nicht jenseits des Meeres, sodass du sagen müsstest: Wer fährt für uns über das Meer, holt es herüber und verkündet es uns, damit wir es halten können?" },
      { number: 14, text: "Nein, das Wort ist ganz nah bei dir, es ist in deinem Mund und in deinem Herzen, du kannst es halten.", footnotes: ["6,6-9; 11,18-20; Röm 2,15"] },
      { number: 15, text: "Hiermit lege ich dir heute das Leben und das Glück, den Tod und das Unglück vor.", heading: "Segen und Fluch - Leben und Tod: 30,15-20", footnotes: ["(15-20) 4,1-4; 32,47"] },
      { number: 16, text: "Wenn du auf die Gebote des Herrn, deines Gottes, auf die ich dich heute verpflichte, hörst, indem du den Herrn, deinen Gott, liebst, auf seinen Wegen gehst und auf seine Gebote, Gesetze und Rechtsvorschriften achtest, dann wirst du leben und zahlreich werden und der Herr, dein Gott, wird dich in dem Land, in das du hineinziehst, um es in Besitz zu nehmen, segnen.", footnotes: ["11,1; 28,1-14", "Der Anfang des Verses ist nur in G erhalten."] },
      { number: 17, text: "Wenn du aber dein Herz abwendest und nicht hörst, wenn du dich verführen lässt, dich vor anderen Göttern niederwirfst und ihnen dienst -", footnotes: ["(17-18) 28,15-68"] },
      { number: 18, text: "heute erkläre ich euch: Dann werdet ihr ausgetilgt werden; ihr werdet nicht lange in dem Land leben, in das du jetzt über den Jordan hinüberziehst, um hineinzuziehen und es in Besitz zu nehmen.", footnotes: ["8,19"] },
      { number: 19, text: "Den Himmel und die Erde rufe ich heute als Zeugen gegen euch an. Leben und Tod lege ich dir vor, Segen und Fluch. Wähle also das Leben, damit du lebst, du und deine Nachkommen.", footnotes: ["4,26; 8,19; 31,28"] },
      { number: 20, text: "Liebe den Herrn, deinen Gott, hör auf seine Stimme und halte dich an ihm fest; denn er ist dein Leben. Er ist die Länge deines Lebens, das du in dem Land verbringen darfst, von dem du weißt: Der Herr hat deinen Vätern Abraham, Isaak und Jakob geschworen, es ihnen zu geben.", footnotes: ["11,1"] },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Mose trat vor ganz Israel hin und sprach diese Worte.", heading: "Die Einsetzung Josuas durch Mose: 31,1-8", footnotes: ["1f: Andere Lesart: Als Mose damit zu Ende war, diese Worte ganz Israel vorzutragen, sagte er zu ihnen."] },
      { number: 2, text: "Er sagte zu ihnen: Ich bin jetzt hundertzwanzig Jahre alt. Ich kann nicht mehr in den Kampf ziehen. Auch hat der Herr zu mir gesagt: Du wirst den Jordan hier nicht überschreiten.", footnotes: ["1,37; 34,7; (2-6) 20,2-4"] },
      { number: 3, text: "Der Herr, dein Gott, zieht selbst vor dir hinüber, er selbst vernichtet diese Völker bei deinem Angriff, sodass du ihren Besitz übernehmen kannst. Josua zieht vor dir hinüber, wie es der Herr zugesagt hat.", footnotes: ["1,38; 9,3"] },
      { number: 4, text: "Der Herr wird an ihnen tun, was er an Sihon und Og, den Amoriterkönigen, die er vernichtete, und an ihrem Land getan hat.", footnotes: ["7,1-4; 20,16-18"] },
      { number: 5, text: "Der Herr wird sie euch ausliefern: Dann sollt ihr an ihnen genau nach dem Gebot handeln, auf das ich euch verpflichtet habe." },
      { number: 6, text: "Empfangt Macht und Stärke: Fürchtet euch nicht und weicht nicht erschreckt zurück, wenn sie angreifen; denn der Herr, dein Gott, zieht mit dir. Er lässt dich nicht fallen und verlässt dich nicht." },
      { number: 7, text: "Mose rief Josua herbei und sagte vor den Augen ganz Israels zu ihm: Empfange Macht und Stärke: Du sollst mit diesem Volk in das Land hineinziehen, von dem du weißt: Der Herr hat ihren Vätern geschworen, es ihnen zu geben. Du sollst es an sie als Erbbesitz verteilen.", footnotes: ["(7-8) 1,38; Hebr 4,8", "Du sollst mit diesem Volk in das Land hineinziehen, andere Lesart: Du sollst dieses Volk in das Land führen."] },
      { number: 8, text: "Der Herr selbst zieht vor dir her. Er ist mit dir. Er lässt dich nicht fallen und verlässt dich nicht. Du sollst dich nicht fürchten und keine Angst haben.", footnotes: ["Hebr 13,5"] },
      { number: 9, text: "Mose schrieb diese Weisung auf und übergab sie den Priestern, den Nachkommen Levis, die die Lade des Bundes des Herrn trugen, und allen Ältesten Israels.", heading: "Die Verlesung des Gesetzes im Sabbatjahr: 31,9-13", footnotes: ["5,23; 10,8; 17,18; 31,26"] },
      { number: 10, text: "Mose schrieb ihnen vor: In jedem siebten Jahr, in einer der Festzeiten des Brachjahres, beim Laubhüttenfest," },
      { number: 11, text: "wenn ganz Israel zusammenkommt, um an der Stätte, die der Herr auswählt, das Angesicht des Herrn, deines Gottes zu schauen, sollst du diese Weisung vor ganz Israel laut vortragen.", footnotes: ["Vgl. die Anmerkung zu 16,16."] },
      { number: 12, text: "Versammle das Volk - die Männer und Frauen, Kinder und Greise, dazu die Fremden, die in deinen Stadtbereichen Wohnrecht haben -, damit sie zuhören und auswendig lernen und den Herrn, euren Gott, fürchten und darauf achten, dass sie alle Bestimmungen dieser Weisung halten." },
      { number: 13, text: "Vor allem ihre Kinder, die das alles noch nicht kennen, sollen zuhören und lernen, den Herrn, euren Gott, zu fürchten. Das sollt ihr so lange tun, wie ihr in dem Land lebt, in das ihr jetzt über den Jordan hinüberzieht, um es in Besitz zu nehmen.", footnotes: ["4,5; 12,1; 32,46"] },
      { number: 14, text: "Der Herr sagte zu Mose: Sieh, deine Zeit ist gekommen: Du wirst sterben. Ruf Josua und tritt mit ihm in das Offenbarungszelt, damit ich ihn einsetzen kann. Mose und Josua gingen hin und traten in das Offenbarungszelt hinein.", heading: "Die Erscheinung Gottes: 31,14f", footnotes: ["Ex 33,7-11", "14f.23: Nachdem Mose Josua in das Amt des Heerführers bei der Landnahme und des Landverteilers nach der Landnahme eingesetzt hat (VV. 7f), bestätigt Gott hier das erste der beiden Ämter; die Bestätigung des zweiten findet sich in Jos 1,6-9."] },
      { number: 15, text: "Der Herr erschien ihnen am Zelt in einer Wolkensäule. Die Wolkensäule stand über dem Eingang des Zeltes." },
      { number: 16, text: "Und der Herr sagte zu Mose: Sieh, du wirst jetzt bald zu deinen Vätern gebettet werden. Dann wird dieses Volk sich erheben; man wird in seiner Mitte Unzucht treiben, indem man den fremden Göttern des Landes nachfolgt, in das es jetzt hineinzieht, es wird mich verlassen und den Bund brechen, den ich mit ihm geschlossen habe.", heading: "Die Offenbarung des Moseliedes: 31,16-22", footnotes: ["29,24; 32,12; Ex 34,15f; Ri 2,17; 8,27.33", "16-21: Diese erste Überleitung zum Moselied spielt nur auf die erste Hälfte des Lieds an, die von Israels Abfall und Bestrafung handelt. Offenbar soll vor allem dieser Teil des Lieds vom Leser beachtet werden. Ähnliches gilt von der zweiten Überleitung in VV. 28f."] },
      { number: 17, text: "An jenem Tag wird mein Zorn gegen sie entbrennen. Ich werde sie verlassen und mein Angesicht vor ihnen verbergen. Dann wird dieses Volk verzehrt werden. Not und Zwang jeder Art werden es treffen. An jenem Tag wird es sich fragen: Hat mich diese Not nicht deshalb getroffen, weil mein Gott nicht mehr in meiner Mitte ist?", footnotes: ["32,20-30"] },
      { number: 18, text: "Aber ich werde an jenem Tag mein Angesicht nur noch mehr verbergen wegen all des Bösen, das dieses Volk getan hat; denn es hat sich anderen Göttern zugewandt." },
      { number: 19, text: "Doch jetzt schreibt dieses Lied auf! Lehre es die Israeliten! Lass es sie auswendig lernen, damit dieses Lied mein Zeuge gegen die Israeliten werde.", footnotes: ["32,1-43"] },
      { number: 20, text: "Wenn ich dieses Volk in das Land geführt habe, das ich seinen Vätern mit einem Schwur versprochen habe, in das Land, wo Milch und Honig fließen, und wenn es gegessen hat und satt und fett geworden ist und sich anderen Göttern zugewandt hat, wenn sie ihnen gedient und mich verworfen haben und es so meinen Bund gebrochen hat,", footnotes: ["29,24; 32,13-18;"] },
      { number: 21, text: "dann wird, wenn Not und Zwang jeder Art es treffen, dieses Lied vor ihm als Zeuge aussagen; denn seine Nachkommen werden es nicht vergessen, sondern es auswendig wissen. Ich kenne seine Neigung, die sich schon heute regt, noch ehe ich es in das Land gebracht habe, das ich mit einem Schwur versprochen habe.", footnotes: ["31,26; 32,23-25"] },
      { number: 22, text: "An jenem Tag schrieb Mose dieses Lied auf und lehrte es die Israeliten." },
      { number: 23, text: "Der Herr aber setzte Josua, den Sohn Nuns, in sein Amt ein und sagte: Empfange Macht und Stärke: Du sollst die Israeliten in das Land führen, das ich ihnen mit einem Schwur versprochen habe. Ich werde bei dir sein.", heading: "Die Einsetzung Josuas durch Gott: 31,23", footnotes: ["1,38"] },
      { number: 24, text: "Als Mose damit zu Ende war, den Text dieser Weisung in eine Urkunde einzutragen, ohne irgendetwas auszulassen,", heading: "Die Hinterlegung des Gesetzes: 31,24-29" },
      { number: 25, text: "befahl Mose den Leviten, die die Lade des Bundes des Herrn trugen:", footnotes: ["(25-26) 17,18; 31,9"] },
      { number: 26, text: "Nehmt diese Urkunde der Weisung entgegen und legt sie neben die Lade des Bundes des Herrn, eures Gottes! Dort diene sie als Zeuge gegen euch.", footnotes: ["(26-27) Joh 5,45; Dtn 31,12"] },
      { number: 27, text: "Denn ich kenne deine Widersetzlichkeit und deine Halsstarrigkeit. Seht, schon jetzt, wo ich noch unter euch lebe, habt ihr euch dem Herrn widersetzt. Was wird erst nach meinem Tod geschehen?", footnotes: ["9,6f.24"] },
      { number: 28, text: "Versammelt um mich alle Ältesten eurer Stämme und alle eure Listenführer, damit ich ihnen diesen Text vortragen und Himmel und Erde gegen sie als Zeugen anrufen kann.", footnotes: ["4,26; 5,23; 16,18; 30,19; 32,1"] },
      { number: 29, text: "Denn ich weiß: Nach meinem Tod werdet ihr ins Verderben laufen und von dem Weg abweichen, den ich euch vorgeschrieben habe. Dann, in künftigen Tagen, wird euch die Not begegnen, weil ihr tut, was in den Augen des Herrn böse ist, und weil ihr ihn durch euer Machwerk erzürnt.", footnotes: ["4,16.25; 9,12f; 27,15; 32,5.20.23"] },
      { number: 30, text: "Und Mose trug der vollzähligen Versammlung Israels den Wortlaut dieses Liedes vor, ohne irgendetwas auszulassen:", heading: "Das Lied des Mose: 31,30 - 32,44" },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Hört zu, ihr Himmel, ich will reden, / die Erde lausche meinen Worten.", footnotes: ["meinen Worten, wörtlich: den Worten meines Mundes."] },
      { number: 2, text: "Meine Lehre wird strömen wie Regen, / meine Botschaft wird fallen wie Tau, / wie Regentropfen auf das Gras / und wie Tauperlen auf die Pflanzen." },
      { number: 3, text: "Ich will den Namen des Herrn verkünden. / Preist die Größe unseres Gottes!", footnotes: ["den Namen des Herrn, andere Übersetzungsmöglichkeit: einen Namen Jahwes. - Auf jeden Fall wird hier angekündigt, dass in diesem Lied für den unter dem Namen Jahwe bekannten Gott Israels ein neuer Name ausgerufen werden soll. Er beherrscht das ganze Lied und lautet: der Fels. Unter diesem Namen wurde Gott wohl in Jerusalem verehrt (vgl. Ps 62,8; 89,27)."] },
      { number: 4, text: "Er heißt: der Fels. Vollkommen ist, was er tut; / denn alle seine Wege sind recht. / Er ist ein unbeirrbar treuer Gott, / er ist gerecht und gerade.", footnotes: ["Röm 9,14; 2 Thess 3,3; Offb 15,3; 16,5"] },
      { number: 5, text: "Ein falsches, verdrehtes Geschlecht fiel von ihm ab, / Verkrüppelte, die nicht mehr seine Söhne sind.", footnotes: ["Mt 17,17; Lk 9,41; Apg 2,40; Phil 2,15; Mt 6,9; Lk 11,2; Joh 8,41", "Schwieriger, wahrscheinlich verderbter Text."] },
      { number: 6, text: "Ist das euer Dank an den Herrn, / du dummes, verblendetes Volk? / Ist er nicht dein Vater, dein Schöpfer? / Hat er dich nicht geformt und hingestellt?" },
      { number: 7, text: "Denk an die Tage der Vergangenheit, / lerne aus den Jahren der Geschichte! / Frag deinen Vater, er wird es dir erzählen, / frag die Alten, sie werden es dir sagen." },
      { number: 8, text: "Als der Höchste (den Göttern) die Völker übergab, / als er die Menschheit aufteilte, / legte er die Gebiete der Völker / nach der Zahl der Götter fest;", footnotes: ["Apg 17,27; Ps 82", "nach der Zahl der Götter, andere Lesart: nach der Zahl der Söhne Israels. - Diese Lesart ist jünger. Sie wurde eingeführt, um polytheistische Vorstellungen, die dieses Lied noch ohne Bedenken benutzt, zu beseitigen. Die gemeinte Zahl ist 70. Es gab 70 Götter, die die 70 Völker der Welt regierten. Da Israel (Jakob) 70 Nachkommen hatte, als er nach Ägypten zog (Ex 1,5), erlaubte man sich die Änderung von «Götter» in «Söhne Israels»."] },
      { number: 9, text: "der Herr nahm sich sein Volk als Anteil, / Jakob wurde sein Erbland." },
      { number: 10, text: "Er fand ihn in der Steppe, / in der Wüste, wo wildes Getier heult. / Er hüllte ihn ein, gab auf ihn Acht / und hütete ihn wie seinen Augenstern," },
      { number: 11, text: "wie der Adler, der sein Nest beschützt / und über seinen Jungen schwebt, / der seine Schwingen ausbreitet, ein Junges ergreift / und es flügelschlagend davonträgt." },
      { number: 12, text: "Der Herr allein hat Jakob geleitet, / kein fremder Gott stand ihm zur Seite." },
      { number: 13, text: "Er führte ihn auf die Berge des Landes, / er nährte ihn mit den Früchten des Feldes, / er stillte ihn mit Wein aus den Felsen, / mit Öl aus Felsspalten.", footnotes: ["er nährte ihn mit den Früchten des Feldes, andere Lesart: und er aß von den Früchten des Feldes. - Er stillte ihn mit Wein aus dem Felsen, mit Öl aus den Felsspalten: Es ist wohl an Weinberge und Ölhaine gedacht, die auf felsendurchwachsenen Berghängen angelegt sind. Andere Übersetzungsmöglichkeit: mit Honig aus dem Felsen."] },
      { number: 14, text: "Mit Butter von Kühen, Milch von Schafen und Ziegen, / dazu kam Fett von Lämmern, / von Widdern aus Baschan / und von Ziegenböcken, / dazu Feinmehl aus Weizen. / Das Blut der Trauben trankst du gegoren." },
      { number: 15, text: "Und Jakob aß und wurde satt, / Jeschurun wurde fett und bockte. / Ja, fett und voll und feist bist du geworden. / Er stieß den Gott, der ihn geformt hatte, von sich / und hielt den Fels für dumm, der ihn gerettet hatte.", footnotes: ["33,5.26", "Die erste Zeile fehlt bei einem Teil der Textzeugen. - Jeschurun: kultischer Name für Israel."] },
      { number: 16, text: "Sie weckten seine Eifersucht durch Fremde, / durch gräuliche Wesen reizten sie ihn zum Zorn:", footnotes: ["1 Kor 10,20; Offb 9,20"] },
      { number: 17, text: "Sie opferten Geistern, die keine Gottheiten sind, / und Göttern, die sie früher nicht kannten, / Neulingen, die erst vor kurzem gekommen waren, / vor denen eure Väter sich nicht fürchteten." },
      { number: 18, text: "An den Fels, der dich gezeugt hat, / dachtest du nicht mehr, / du vergaßest den Gott, der dich geboren hat." },
      { number: 19, text: "Da sah der Herr, dass er geschmäht wurde / von seinen Söhnen und Töchtern, / die seinen Zorn erregten.", footnotes: ["Andere Lesart: Der Herr sah (es) und er verstieß im Zorn seine Söhne und Töchter."] },
      { number: 20, text: "Und er sagte: Ich will mein Gesicht vor ihnen verbergen / und dann sehen, was in Zukunft mit ihnen geschieht. / Denn sie sind eine Generation des Aufruhrs, / Söhne, in denen die Untreue sitzt.", footnotes: ["Mt 17,17; Lk 9,41"] },
      { number: 21, text: "Sie haben meine Eifersucht geweckt / durch einen Gott, der kein Gott ist, / mich zum Zorn gereizt durch ihre Götter aus Luft - / so wecke ich ihre Eifersucht / durch ein Volk, das kein Volk ist, / durch ein dummes Volk reize ich sie zum Zorn.", footnotes: ["Röm 10,19; 11,11; 1 Kor 10,22"] },
      { number: 22, text: "In meiner Nase ist Feuer entbrannt. / Es lodert bis in die unterste Totenwelt, / verzehrt die Erde und was auf ihr wächst / und schmilzt die Fundamente der Berge." },
      { number: 23, text: "Immer neue Not bürde ich ihnen auf, / ich setze gegen sie alle meine Pfeile ein." },
      { number: 24, text: "Sie werden ausgemergelt durch den Hunger, / verzehrt durch die Pest und die verheerende Seuche. / Den Zahn der Raubtiere lasse ich auf sie los, / dazu das Gift der im Staube Kriechenden.", footnotes: ["im Staube Kriechende: Schlangen."] },
      { number: 25, text: "Auf der Straße raubt das Schwert die Kinder / und in den Zimmern der Schrecken. / Da stirbt der junge Mann und das Mädchen, / der Säugling und der Greis." },
      { number: 26, text: "Ich könnte sagen: Sie sollen nicht mehr sein, / kein Mensch soll später noch an sie denken," },
      { number: 27, text: "müsste ich nicht auch ihren Feind angreifen, / der meinen Zorn erregt, / ihre Gegner, die sich nicht täuschen sollen, / die nicht sagen sollen: Unsere Hand ist erhoben, / der Herr hat nichts von allem getan." },
      { number: 28, text: "Doch diesem Volk fehlt es an Rat, / ihm mangelt es an Verstand.", footnotes: ["diesem Volk: gemeint sind die Gegner Israels."] },
      { number: 29, text: "Wären sie klug, so begriffen sie alles / und verstünden, was in Zukunft mit ihnen geschieht.", footnotes: ["Lk 19,42"] },
      { number: 30, text: "Wie kann ein Einziger hinter tausend herjagen / und zwei zehntausend in die Flucht schlagen, / es sei denn, ihr Fels hat sie verkauft, / der Herr hat sie preisgegeben?" },
      { number: 31, text: "Doch der Fels unserer Feinde ist nicht wie unser Fels; / das beweisen unsere Feinde.", footnotes: ["1 Sam 2,2", "In der zweiten Vershälfte ist die Deutung von H unsicher."] },
      { number: 32, text: "Ihr Weinstock stammt von dem Weinstock Sodoms, / vom Todesacker Gomorras. / Ihre Trauben sind giftige Trauben / und tragen bittere Beeren." },
      { number: 33, text: "Ihr Wein ist Schlangengift / und Gift von ekligen Ottern." },
      { number: 34, text: "Liegt dies nicht bei mir verborgen, / in meinen Vorratskammern versiegelt" },
      { number: 35, text: "bis zum Tag der Strafe und Vergeltung, / bis zu der Zeit, da ihr Fuß wanken wird? / Doch der Tag ihres Verderbens ist nah / und ihr Verhängnis kommt schnell. -", footnotes: ["1 Thess 4,6; (35-36) Lk 21,22; Röm 12,19; Hebr 10,30", "Andere Lesart: Mein ist die Strafe und die Vergeltung zu der Zeit, da ihr Fuß wanken wird. - Röm 12,19 und Hebr 10,30 setzen diese Lesart voraus."] },
      { number: 36, text: "Ja, der Herr wird seinem Volk Recht geben / und mit seinen Dienern Mitleid haben. / Er wird sehen: Jede Hand ist ermüdet, / es gibt nur noch Unterdrückte und Hilflose.", footnotes: ["2 Kön 14,26; Ps 135,14"] },
      { number: 37, text: "Und er wird sagen: Wo sind ihre Götter? / Wo ist der Fels, bei dem sie Schutz suchten?" },
      { number: 38, text: "Die das Fett ihrer Schlachtopfer essen, / die den Wein ihrer Trankopfer trinken - / die sollen vortreten und euch helfen. / Dieser Fels soll ein Schutzdach über euch sein." },
      { number: 39, text: "Jetzt seht: Ich bin es, nur ich, / und kein Gott tritt mir entgegen. / Ich bin es, der tötet und der lebendig macht. / Ich habe verwundet; nur ich werde heilen. / Niemand kann retten, wonach meine Hand gegriffen hat.", footnotes: ["Jes 43,10-13"] },
      { number: 40, text: "Ich hebe meine Hand zum Himmel empor / und sage: So wahr ich ewig lebe:", footnotes: ["Offb 10,5f"] },
      { number: 41, text: "Habe ich erst die Klinge meines Schwertes geschliffen, / um das Recht in meine Hand zu nehmen, / dann zwinge ich meinen Gegnern die Strafe auf / und denen, die mich hassen, die Vergeltung." },
      { number: 42, text: "Meine Pfeile mache ich trunken von Blut, / während mein Schwert sich ins Fleisch frisst - / trunken vom Blut Erschlagener und Gefangener, / ins Fleisch des höchsten feindlichen Fürsten." },
      { number: 43, text: "Erhebt das Siegesgeschrei, ihr Himmel, zusammen mit ihm, / werft euch vor ihm nieder, ihr Götter! / Denn er erzwingt die Strafe für das Blut seiner Söhne / und entsühnt das Land seines Volkes.", footnotes: ["Röm 15,10 G; Hebr 1,6; Offb 6,10; 18,20; 19,2"] },
      { number: 44, text: "Dann kam Mose zum Volk und trug ihm das Lied in seinem vollen Wortlaut vor, er und Josua, der Sohn Nuns." },
      { number: 45, text: "Als Mose damit zu Ende war, alle diese Worte vor ganz Israel vorzutragen,", heading: "Die Mahnung zur Belehrung der nächsten Generation:", footnotes: ["Hier sind drei verschiedene Textfassungen überliefert. Die Übersetzung folgt der wahrscheinlichsten Rekonstruktion."] },
      { number: 46, text: "sagte er zu ihnen: Schenkt allen Bestimmungen eure Beachtung. Heute beschwöre ich euch: Verpflichtet eure Kinder, dass auch sie auf alle Bestimmungen dieser Weisung achten und sie halten.", footnotes: ["4,9; 31,13"] },
      { number: 47, text: "Das ist kein leeres Wort, das ohne Bedeutung für euch wäre, sondern es ist euer Leben. Wenn ihr diesem Wort folgt, werdet ihr lange in dem Land leben, in das ihr jetzt über den Jordan hinüberzieht, um es in Besitz zu nehmen.", footnotes: ["4,1-4; 30,15-20"] },
      { number: 48, text: "Am selben Tag sagte der Herr zu Mose:", heading: "Gottes Weisung über das Sterben des Mose: 32,48-52", footnotes: ["(48-52) Num 27,12-14; Num 33,47f; Apg 7,45"] },
      { number: 49, text: "Geh hinauf in das Gebirge Abarim, das du vor dir siehst, steig auf den Berg Nebo, der in Moab gegenüber Jericho liegt, und schau auf das Land Kanaan, das ich den Israeliten als Grundbesitz geben werde." },
      { number: 50, text: "Dort auf dem Berg, den du ersteigst, sollst du sterben und sollst mit deinen Vorfahren vereint werden, wie dein Bruder Aaron auf dem Berg Hor gestorben ist und mit seinen Vorfahren vereint wurde.", footnotes: ["Num 20,22-29"] },
      { number: 51, text: "Denn ihr seid mir untreu gewesen inmitten der Israeliten beim Haderwasser von Kadesch in der Wüste Zin und habt mich inmitten der Israeliten nicht als den Heiligen geehrt.", footnotes: ["Num 20,2-13"] },
      { number: 52, text: "Du darfst das Land von der anderen Talseite aus sehen. Aber du darfst das Land, das ich den Israeliten geben werde, nicht betreten." },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Und das ist der Segen, mit dem Mose, der Mann Gottes, die Israeliten segnete, bevor er starb.", heading: "Der Segen des Mose: 33,1-29" },
      { number: 2, text: "Er sprach: Der Herr kam hervor aus dem Sinai, / er leuchtete vor ihnen auf aus Seïr, / er strahlte aus dem Gebirge Paran, / er trat heraus aus Tausenden von Heiligen. / Ihm zur Rechten flammte vor ihnen das Feuer des Gesetzes.", heading: "Einleitung: 33,2-5", footnotes: ["Ri 5,4; Ps 68,8; Mt 25,31; Jud 14", "2-29: Ein sehr alter, mehrfach neu gedeuteter und erweiterter Text. Er ist hier nach der ältesten in Handschriften erfassbaren Gestalt übersetzt. Einleitung und Schluss bilden zusammen einen rahmenden Psalm. Er ist in der hier übersetzten Fassung heilsgeschichtlich zu verstehen. vor ihnen: vor den Israeliten. Der Vers spricht von der Offenbarung am Sinai, nimmt aber auch anderes aus der Wüstenwanderung Israels hinein. Die «Heiligen» sind die Engel. Der Ausdruck «Tausende von Heiligen» ist in H aber zugleich eine Anspielung auf eine weitere Örtlichkeit der Wüstenwanderung: Meribat Kadesch."] },
      { number: 3, text: "Der du die Völker liebst: / In deiner Hand sind alle Heiligen eines jeden von ihnen. / Sie haben sich dir zu Füßen geworfen, / jeder wird sich erheben, wenn du es befiehlst.", footnotes: ["Apg 20,32; 26,18; Eph 1,18", "die Völker: hier die zwölf Stämme Israels. Ihre «Heiligen» sind entweder ihre Schutzengel oder die einzelnen Menschen aus den Stämmen."] },
      { number: 4, text: "Mose hat uns eine Weisung übergeben, / ein Besitztum für eine Gemeinde Jakobs,", footnotes: ["Weisung: die deuteronomische Gesetzgebung im Land Moab."] },
      { number: 5, text: "und ein König erstand in Jeschurun - / als sich die Häupter des Volkes versammelten, / die Vereinigung der Stämme Israels.", footnotes: ["1,13; 29,9; 32,15", "Einführung des Königtums unter Samuel. Zu Jeschurun vgl. die Anmerkung zu 32,15."] },
      { number: 6, text: "Ruben soll leben, er sterbe nicht aus - / doch habe er wenig Männer.", heading: "Der Segen über die Stämme: 33,6-25", footnotes: ["(6-25) Gen 49,3-27"] },
      { number: 7, text: "Und dies sagte er für Juda: Höre, Herr, die Stimme Judas, / führ ihn heim zu seinem Volk. / Mit eigenen Händen kämpfe er dafür - / sei du ihm Hilfe gegen seine Feinde.", footnotes: ["Entweder die Zeit nach Sauls Tod oder die Zeit nach der Reichsspaltung."] },
      { number: 8, text: "Für Levi sagte er: Levi hat deine Tummim erhalten, / deine Urim dein treuer Gefolgsmann, / den du in Massa auf die Probe stelltest, / mit dem du strittest am Wasser von Meriba;", footnotes: ["1 Sam 14,37-42 G; Ex 17; Num 20,2-13", "Tummim und Urim sind Orakelinstrumente. Andere Lesart: Deine Tummim und Urim gebühren deinem treuen Gefolgsmann."] },
      { number: 9, text: "der von seinem Vater und seiner Mutter sagte: / Ich habe beide nie gesehen!, / und der seine Brüder nicht erkannte / und von seinen Kindern nichts wissen wollte. / Denn die Leviten haben auf dein Wort geachtet - / nun wachen sie über deinen Bund.", footnotes: ["Ex 32,27-29; Mt 10,37; Lk 14,26"] },
      { number: 10, text: "Sie lehren Jakob deine Rechtsvorschriften, / Israel deine Weisung. / Sie legen Weihrauch auf, damit du ihn riechst, / legen das Ganzopfer auf deinen Altar.", footnotes: ["Wörtlich: Sie legen Weihrauch in deine Nase."] },
      { number: 11, text: "Segne, Herr, Levis Besitz, / freu dich am Werk seiner Hände! / Zerschlag seinen Feinden die Hüften, / seinen Hassern, sodass sie sich nicht mehr erheben." },
      { number: 12, text: "Für Benjamin sagte er: In Sicherheit wohne / der Liebling des Herrn. / Täglich wacht / über ihn der Höchste / und zwischen seinen Schultern wohne er.", footnotes: ["2 Thess 2,13", "Andere Lesart: In Sicherheit wohne der Liebling des Herrn an dessen Seite. Täglich wacht er über ihn und zwischen seinen Schultern wohnt er. - Das Wohnen zwischen den Schultern kann sich auf Benjamin beziehen: Er sei an Gottes Schultern, an Gottes Brust geborgen. Oder es kann sich auf Gott beziehen: Sein Heiligtum stehe zwischen den Schultern, d. h. den Bergen des Stammesgebiets von Benjamin."] },
      { number: 13, text: "Und für Josef sagte er: Sein Land sei vom Herrn gesegnet / mit Köstlichem des Himmels, mit Tau, / mit Grundwasser, das in der Tiefe lagert," },
      { number: 14, text: "mit Köstlichem aus den Erzeugnissen der Sonne, / mit Köstlichem aus dem, was jeden Monat sprießt,", footnotes: ["Erzeugnisse der Sonne: wohl die Früchte, die nur einmal im Jahr wachsen, im Gegensatz zu dem, «was jeden Monat sprießt», d. h. während des ganzen Jahres wächst."] },
      { number: 15, text: "mit dem Besten uralter Berge, / mit Köstlichem ewiger Hügel," },
      { number: 16, text: "mit Köstlichem des Landes und seiner Bewohner / und (über ihn komme) die Gnade dessen, / der im Dornbusch wohnt. / Das komme über Josefs Haupt, / auf den Scheitel des Geweihten aus seiner Brüder Schar.", footnotes: ["Ex 3,2-4", "seiner Brüder Schar: die zwölf Söhne Jakobs."] },
      { number: 17, text: "Der Erstling seines Stiers - wie herrlich ist er! / Seine Hörner sind Büffelhörner. / Mit ihnen stoße er die Völker alle zusammen nieder, / die Enden der Welt. / Das sind die Zehntausende aus Efraim, / das sind die Tausende aus Manasse.", footnotes: ["Josefs «Stier» ist der Gott Israels. Noch in der Königszeit wurde Gott auch in Gestalt eines Stierbildes verehrt. Josef gilt dem Dichter als Gottes Erstgeborener, wird also selbst auch mit einem gewaltigen Stier verglichen."] },
      { number: 18, text: "Und für Sebulon sagte er: Lache, Sebulon, wenn du in See stichst, / und du, Issachar, in deinen Zelten!" },
      { number: 19, text: "Sie werden Völker zum Berge rufen, / dort werden sie gültige Opfer schlachten. / Denn sie nähren sich vom Überfluss der Meere, / von Schätzen, die im Sand verborgen sind.", footnotes: ["Der «Berg» war vielleicht ursprünglich der Tabor. «Schätze, die im Sand verborgen sind»: entweder zugewehtes Strandgut oder der Sand selbst, der für die Glaserzeugung gebraucht wurde."] },
      { number: 20, text: "Und für Gad sagte er: Gepriesen sei der, der Gad Raum schafft. / Gad lauert wie ein Löwe, / Arm und Kopf reißt er ab.", footnotes: ["Der, «der Gad Raum schafft», ist Jahwe, der Gott Israels."] },
      { number: 21, text: "Das erste Stück hat er sich ausgesucht, / denn wo der Anteil des Anführers war, / da versammelten sich die Häupter des Volkes. / Er tat, was vor dem Herrn recht ist, / (hielt sich) gemeinsam mit Israel (an) seine Rechtsvorschriften.", footnotes: ["Andere Lesart: Das erste Stück hat er sich ausgesucht, denn dort ist der Anteil des verborgenen Anführers. Er kam zu den Häuptern des Volkes. - Der «verborgene Anführer» ist nach der jüdischen Tradition Mose, der im Gebiet des Stammes Gad an einem unbekannten Ort begraben ist. Bei der Versammlung der «Häupter des Volkes» kann man für die hier übersetzte Textfassung an die deuteronomische Gesetzgebung durch Mose denken."] },
      { number: 22, text: "Und für Dan sagte er: Dan ist ein junger Löwe, / der aus dem Baschan hervorspringt." },
      { number: 23, text: "Und für Naftali sagte er: Naftali, gesättigt mit Gnade, / gefüllt mit dem Segen des Herrn - / See und Süden nimm in Besitz!", footnotes: ["See und Süden: See Gennesaret und dessen Südufer. Der Hauptbesitz von Naftali war im galiläischen Bergland. Hier wird nur auf eine Gebietsausdehnung nach Süden hingewiesen."] },
      { number: 24, text: "Und für Ascher sagte er: Mehr als die (anderen) Söhne sei Ascher gesegnet, / bei seinen Brüdern sei er beliebt, / er bade seinen Fuß in Öl." },
      { number: 25, text: "Deine Riegel seien von Eisen und Bronze. / Hab Frieden, solange du lebst!" },
      { number: 26, text: "Keiner ist wie der Gott Jeschuruns, / der in den Himmel steigt, um dir zu helfen, / auf die Wolken in seiner Hoheit.", heading: "Schluss: 33,26-29", footnotes: ["Zu Jeschurun vgl. die Anmerkung zu 32,15."] },
      { number: 27, text: "Eine Wohnung ist der Gott der Urzeit, / von unten (tragen sie) die Arme des Ewigen. / Er trieb den Feind vor dir her, / er sagte (zu dir): Vernichte!" },
      { number: 28, text: "So siedelte Israel sich sicher an, / die Quelle Jakobs für sich allein, / in einem Land voller Korn und Wein, / dessen Himmel Tau träufeln lässt.", footnotes: ["Gen 27,28", "Quelle Jakobs: alle, die aus Jakob hervorgegangen sind, also das Volk Israel durch die Generationen hindurch."] },
      { number: 29, text: "Wie glücklich bist du, Israel! / Wer ist dir gleich, / du Volk, gerettet durch den Herrn, / den Schild, der dir hilft, / deine Hoheit, wenn das Schwert kommt? Deine Feinde werden sich vor dir erniedrigen / und du setzt deinen Fuß auf ihre Nacken." },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Mose stieg aus den Steppen von Moab hinauf auf den Nebo, den Gipfel des Pisga gegenüber Jericho, und der Herr zeigte ihm das ganze Land. Er zeigte ihm Gilead bis nach Dan hin,", heading: "Der Tod des Mose: 34,1-9", footnotes: ["(1-3) 3,27", "1-3: Ein Teil der Textzeugen bringt eine andere, kürzere Landbeschreibung."] },
      { number: 2, text: "ganz Naftali, das Gebiet von Efraim und Manasse, ganz Juda bis zum Mittelmeer," },
      { number: 3, text: "den Negeb und die Jordangegend, den Talgraben von Jericho, der Palmenstadt, bis Zoar." },
      { number: 4, text: "Der Herr sagte zu ihm: Das ist das Land, das ich Abraham, Isaak und Jakob versprochen habe mit dem Schwur: Deinen Nachkommen werde ich es geben. Ich habe es dich mit deinen Augen schauen lassen. Hinüberziehen wirst du nicht." },
      { number: 5, text: "Danach starb Mose, der Knecht des Herrn, dort in Moab, wie es der Herr bestimmt hatte.", footnotes: ["1,37; 3,26-28; 4,21f; 31,2"] },
      { number: 6, text: "Man begrub ihn im Tal, in Moab, gegenüber Bet-Pegor. Bis heute kennt niemand sein Grab." },
      { number: 7, text: "Mose war hundertzwanzig Jahre alt, als er starb. Sein Auge war noch nicht getrübt, seine Frische war noch nicht geschwunden.", footnotes: ["31,2"] },
      { number: 8, text: "Die Israeliten beweinten Mose dreißig Tage lang in den Steppen von Moab. Danach war die Zeit des Weinens und der Klage um Mose beendet.", footnotes: ["Num 20,29"] },
      { number: 9, text: "Josua, der Sohn Nuns, war vom Geist der Weisheit erfüllt, denn Mose hatte ihm die Hände aufgelegt. Die Israeliten hörten auf ihn und taten, was der Herr dem Mose aufgetragen hatte.", footnotes: ["Num 27,18-23"] },
      { number: 10, text: "Niemals wieder ist in Israel ein Prophet wie Mose aufgetreten. Ihn hat der Herr Auge in Auge berufen.", heading: "Der Abschluss der fünf Bücher des Mose: 34,10-12", footnotes: ["Ex 33,11"] },
      { number: 11, text: "Keiner ist ihm vergleichbar, wegen all der Zeichen und Wunder, die er in Ägypten im Auftrag des Herrn am Pharao, an seinem ganzen Hof und an seinem ganzen Land getan hat,", footnotes: ["Num 12,6-8"] },
      { number: 12, text: "wegen all der Beweise seiner starken Hand und wegen all der Furcht erregenden und großen Taten, die Mose vor den Augen von ganz Israel vollbracht hat.", footnotes: ["4,34"] },
    ] },
  ],
};
