import { Book } from "@/lib/types";

export const ecclesiastes: Book = {
  id: "ecclesiastes",
  name: "Kohelet",
  shortName: "Koh",
  testament: "old",
  introduction: "Das Buch Kohelet wurde um die Mitte des 3. Jahrhunderts v. Chr. geschrieben. Der Verfasser, der sich selbst Kohelet nennt, ist uns nicht bekannt. Palästina gehörte damals zum Ptolemäerreich. Es war die Zeit noch vor den hellenistischen Religionsverfolgungen und vor der nationalen Erhebung der Makkabäer. Die gebildete Oberschicht von Judäa war wohlhabend und weltoffen. Man versuchte, die Traditionen Israels mit der die Welt beherrschenden griechischen Bildung und Lebensform zu einer neuen Einheit zu verschmelzen. Das Buch Kohelet ist das eindrucksvollste Zeugnis dieses Bemühens. Die Bildungstraditionen Israels, ja des ganzen Orients, besonders auch Ägyptens, sind Kohelet bekannt (zur »Weisheit« vgl. die Einleitung zu Spr). Sein Denken und Fühlen ist von den Schriftstellern, die man in den griechischen Schulen liest, und von der sogenannten Popularphilosophie bestimmt. Seine Sprache ist ein ganz neuartiges Hebräisch, in dem einerseits die aramäische Alltagssprache, andererseits manches griechische Wort- und Satzmuster durchschlägt. Das Buch lehnt sich nur noch teilweise an die poetische Gestalt der alten Lehrschriften an. Es enthält auch schon viele Merkmale der damals aufkommenden Predigt von Wanderphilosophen (»Diatribe«). Das Buch entwirft zunächst eine Lehre vom Kosmos (1,4-11). Der herrliche, ewig kreisende Kosmos ist die Bühne, auf der die vergänglichen Menschen kommen und gehen. Sprache, Sinne und Gedächtnis der Menschen können die Fülle der Welt nicht erfassen. Es folgt als grundlegender Teil des Buches die Lehre vom Menschen (1,12 - 3,15). Kohelet versetzt sich in die höchste menschliche Möglichkeit: Er sieht sich als gebildeten, weltgestaltenden und das Leben voll auskostenden Herrscher und fragt dann nach dem Sinn. Angesichts des sicher kommenden Todes erweist sich selbst dann alles als »Windhauch«. Jeder Augenblick ist von Gott her bestimmt; der Mensch kann ihn weder berechnen noch in den Griff bekommen. Er kann nur in Furcht das, was ihm jeweils gegeben wird, annehmen. Dieses Daseinsverständnis bestätigt sich dann in einem anschließenden Teil, der die Übel der Welt anhand ausgewählter Beispiele aus dem öffentlichen, wirtschaftlichen und familiären Leben kritisiert (3,16 - 6,10). Im nächsten Teil werden Beispiele traditioneller Spruchweisheit und die weisheitliche Theorie über den Zusammenhang von Tun und Ergehen kritisch überprüft (6,11 - 9,6). Am Ende steht wieder das Thema Tod. Daran schließt sich die Ethik an, die Weisung für die rechte Lebensführung (9,7 - 12,7). Sie ist sehr vielfältig, doch ist sie geleitet von der Mahnung, in Gottesfurcht die Gabe des jeweiligen Augenblicks zu ergreifen: jede Freude zu genießen und überall da, wo es sich anbietet, tatkräftig zu handeln. Denn jedes »Jetzt« ist die dem Menschen gegebene Zeit. Er soll nie vergessen, dass er auf Alter und Tod zugeht. Aus dieser Ethik sind die Anweisungen über das religiöse Verhalten herausgenommen und schon vorher als eine Art Zwischenstück in die Gesellschaftskritik eingesetzt (4,17 - 5,6). So bilden sie im Gesamtaufbau des Buches eine Art Mitte. Im modernen Denken wird man mit dem Buch Kohelet vor allem die Existenzphilosophie vergleichen können. Doch ist im Buch Kohelet bei aller Neigung zur Erfahrungsweisheit und zur kritischen Auseinandersetzung mit gängigen Meinungen zugleich eine sehr radikale Theorie von der Bindung der Welt an Gott vorausgesetzt. Nach ihr kann nichts, auch nicht das Böseste und Schlimmste, von der Allursächlichkeit Gottes ausgenommen werden. Und von Gott her ist letztlich alles »schön«. Dies sollte man nicht übersehen, wenn man beim Lesen des Buches von Melancholie überwältigt wird und Kohelet gerade deshalb als modern empfinden möchte. Wer Kohelet als Teil des ganzen Kanons der heiligen Schriften liest, wird das Buch, ohne dabei die Härte seines Fragens und Denkens preiszugeben, mit der Botschaft vom Handeln Gottes in der Geschichte verbinden.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Worte Kohelets, des Davidsohnes, der König in Jerusalem war.", heading: "Buchtitel: 1,1", footnotes: ["1,12; Spr 1,1; 1 Kön 8,1"] },
      { number: 2, text: "Windhauch, Windhauch, sagte Kohelet, Windhauch, Windhauch, das ist alles Windhauch.", heading: "Vorspruch: 1,2-3", footnotes: ["12,8; Ijob 7,16; Ps 39,6; 62,10; Röm 8,20"] },
      { number: 3, text: "Welchen Vorteil hat der Mensch von all seinem Besitz, für den er sich anstrengt unter der Sonne?", footnotes: ["2,11.18-22; 3,9; 5,15.17; 9,9", "Besitz, für den er sich anstrengt: ein Leitmotiv des Buches; in H ein Wortspiel. Das Wort für «Besitz» meint sowohl die menschliche Arbeit, die den Besitz hervorbringt, als auch den durch die Arbeit und Mühe erworbenen Besitz selbst."] },
      { number: 4, text: "Eine Generation geht, eine andere kommt. / Die Erde steht in Ewigkeit.", heading: "Wechsel, Dauer und Vergessen: 1,4-11", footnotes: ["Sir 14,18; (4-11) 3,11-15"] },
      { number: 5, text: "Die Sonne, die aufging und wieder unterging, / atemlos jagt sie zurück an den Ort, wo sie wieder aufgeht.", footnotes: ["Nach dem antiken Weltbild muss die Sonne nachts unterhalb der Erdscheibe wieder zum östlichen Horizont zurückkehren."] },
      { number: 6, text: "Er weht nach Süden, dreht nach Norden, dreht, dreht, weht, der Wind. / Weil er sich immerzu dreht, kehrt er zurück, der Wind." },
      { number: 7, text: "Alle Flüsse fließen ins Meer, / das Meer wird nicht voll. Zu dem Ort, wo die Flüsse entspringen, / kehren sie zurück, um wieder zu entspringen." },
      { number: 8, text: "Alle Dinge sind rastlos tätig, / kein Mensch kann alles ausdrücken, nie wird ein Auge satt, wenn es beobachtet, / nie wird ein Ohr vom Hören voll.", footnotes: ["5,9; 8,17; Spr 27,20", "Alle Dinge sind rastlos tätig, andere Übersetzungsmöglichkeit: Alle Worte sind überanstrengt."] },
      { number: 9, text: "Was geschehen ist, wird wieder geschehen, / was man getan hat, wird man wieder tun: / Es gibt nichts Neues unter der Sonne.", footnotes: ["3,15; 6,10; 4,13-16", "Dass es «nichts Neues unter der Sonne» gibt, ist entgegen unserer Empfindung eine positive Aussage."] },
      { number: 10, text: "Zwar gibt es bisweilen ein Ding, von dem es heißt: / Sieh dir das an, das ist etwas Neues - / aber auch das gab es schon in den Zeiten, die vor uns gewesen sind.", footnotes: ["10cd: Andere Übersetzungsmöglichkeit: aber schon seit Ewigkeiten hat es das gegeben, was sich vor unseren Augen ereignet hat."] },
      { number: 11, text: "Nur gibt es keine Erinnerung an die Früheren / und auch an die Späteren, die erst kommen werden, auch an sie wird es keine Erinnerung geben / bei denen, die noch später kommen werden.", footnotes: ["2,16; 9,5"] },
      { number: 12, text: "Ich, Kohelet, war in Jerusalem König über Israel.", heading: "Die Bedingtheit des Menschen - die Undurchschaubarkeit Gottes: 1,12 - 3,15 | Drei Überblicke: 1,12 - 2,2", footnotes: ["1,12 - 3,15: In diesem Teil des Buches spielt der Verfasser in literarischer Fiktion die Rolle eines Königs."] },
      { number: 13, text: "Ich hatte mir vorgenommen, das Wissen daraufhin zu untersuchen und zu erforschen, ob nicht alles, was unter dem Himmel getan wurde, ein schlechtes Geschäft war, für das die einzelnen Menschen durch Gottes Auftrag sich abgemüht haben.", footnotes: ["3,10; 8,9.16f; 5,13"] },
      { number: 14, text: "Ich beobachtete alle Taten, die unter der Sonne getan wurden. Das Ergebnis: Das ist alles Windhauch und Luftgespinst.", footnotes: ["2,11; 4,4", "Luftgespinst, andere Übersetzungsmöglichkeit: Haschen nach Luft. So auch später, wenn dieses Wort wiederkommt."] },
      { number: 15, text: "Was krumm ist, kann man nicht gerade biegen; / was nicht da ist, kann man nicht zählen.", footnotes: ["7,13; Spr 27,22"] },
      { number: 16, text: "Ich überlegte mir Folgendes: Ich habe mein Wissen immerzu vergrößert, sodass ich jetzt darin jeden übertreffe, der vor mir über Jerusalem geherrscht hat. Oft konnte ich Wissen und Können beobachten.", footnotes: ["2,9; 1 Kön 5,9f"] },
      { number: 17, text: "So habe ich mir vorgenommen zu erkennen, was Wissen wirklich ist, und zu erkennen, was Verblendung und Unwissen wirklich sind. Ich erkannte, dass auch dies ein Luftgespinst ist.", footnotes: ["2,12; 7,25; 8,16f"] },
      { number: 18, text: "Denn: Viel Wissen, viel Ärger, / wer das Können mehrt, der mehrt die Sorge.", footnotes: ["2,23; 5,16; Spr 22,15"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Ich dachte mir: Auf, versuch es mit der Freude, genieß das Glück! Das Ergebnis: Auch das ist Windhauch.", footnotes: ["2,3.10.24; 3,12; Weish 2,6"] },
      { number: 2, text: "Über das Lachen sagte ich: Wie verblendet!, / über die Freude: Was bringt sie schon ein?", footnotes: ["Spr 14,13; 20,1; 23,29-35"] },
      { number: 3, text: "Ich trieb meine Forschung an mir selbst, indem ich meinen Leib mit Wein lockte, während mein Verstand das Wissen auf die Weide führte, und indem ich das Unwissen gefangen nahm. Ich wollte dabei beobachten, wo es vielleicht für die einzelnen Menschen möglich ist, sich unter dem Himmel Glück zu verschaffen während der wenigen Tage ihres Lebens.", heading: "Menschliches Glück durch Weltgestaltung: 2,3-11", footnotes: ["2,1", "Der Sinn von H ist stellenweise nicht klar."] },
      { number: 4, text: "Ich vollbrachte meine großen Taten: Ich baute mir Häuser, / ich pflanzte Weinberge.", footnotes: ["1 Kön 7; Hld 8,11"] },
      { number: 5, text: "Ich legte mir Gärten und Parks an, / darin pflanzte ich alle Arten von Bäumen.", footnotes: ["Hld 4,13"] },
      { number: 6, text: "Ich legte Wasserbecken an, / um aus ihnen den sprossenden Baumbestand zu bewässern.", footnotes: ["Neh 2,14"] },
      { number: 7, text: "Ich kaufte Sklaven und Sklavinnen, / obwohl ich schon hausgeborene Sklaven besaß. Auch Vieh besaß ich in großer Zahl, Rinder, Schafe, Ziegen, / mehr als alle meine Vorgänger in Jerusalem.", footnotes: ["1 Kön 10,5; 8,63; 10,23"] },
      { number: 8, text: "Ich hortete auch Silber und Gold / und, als meinen persönlichen Schatz, Könige und ihre Provinzen. Ich besorgte mir Sänger und Sängerinnen / und die Lust jedes Menschen: einen großen Harem.", footnotes: ["1 Kön 10,21; 5,1-8; 2 Sam 19,36; 1 Kön 11,3", "einen großen Harem: Übersetzung nicht ganz sicher."] },
      { number: 9, text: "Ich war schon groß gewesen, doch ich gewann noch mehr hinzu, sodass ich alle meine Vorgänger in Jerusalem übertraf. Und noch mehr: Mein Wissen stand mir zur Verfügung", footnotes: ["1,16; 1 Kön 10,23; 5,9-14"] },
      { number: 10, text: "und was immer meine Augen sich wünschten, verwehrte ich ihnen nicht. Ich musste meinem Herzen keine einzige Freude versagen. Denn mein Herz konnte immer durch meinen ganzen Besitz Freude gewinnen. Und das war mein Anteil, den ich durch meinen ganzen Besitz gewinnen konnte.", footnotes: ["2,1.24; 3,12.22; 5,17f; 8,15; 9,6-9; 11,7-10"] },
      { number: 11, text: "Doch dann dachte ich nach über alle meine Taten, die meine Hände vollbracht hatten, und über den Besitz, für den ich mich bei diesem Tun angestrengt hatte. Das Ergebnis: Das ist alles Windhauch und Luftgespinst. Es gibt keinen Vorteil unter der Sonne.", footnotes: ["1,3"] },
      { number: 12, text: "Ich dachte nach, indem ich beobachtete, was Wissen wirklich ist und was Verblendung und Unwissen wirklich sind. Außerdem: Was für ein Mann wird auf den König folgen, den sie einst eingesetzt haben?", heading: "Bildung und Besitz in ihrer Bedingtheit: 2,12-23", footnotes: ["1,17; 7,25", "Dieser Vers deutet nacheinander die beiden Themen an, die dann entfaltet werden: Wissen endet mit dem Tod (2,13-17) und Besitz endet mit dem Tod (2,18-23)."] },
      { number: 13, text: "Ich beobachtete: Es gibt einen Vorteil, den das Wissen bietet, aber nicht das Unwissen, wie es einen Vorteil gibt, den das Licht bietet, aber nicht die Dunkelheit:", footnotes: ["6,8"] },
      { number: 14, text: "Der Gebildete hat Augen im Kopf, der Ungebildete tappt im Dunkeln. Aber ich erkannte auch: Beide trifft ein und dasselbe Geschick.", footnotes: ["8,1; 10,2; 3,19; 9,2f; Ijob 9,22"] },
      { number: 15, text: "Da dachte ich mir: Was den Ungebildeten trifft, trifft also auch mich. Warum bin ich dann über die Maßen gebildet? Und ich überlegte mir, dass auch das Windhauch ist.", footnotes: ["7,16"] },
      { number: 16, text: "Denn an den Gebildeten gibt es ebenso wenig wie an den Ungebildeten eine Erinnerung, die ewig währt, weil man schon in den Tagen, die bald kommen, beide vergessen wird. Wie ist es möglich, dass der Gebildete ebenso sterben muss wie der Ungebildete?", footnotes: ["1,11; 9,5; Weish 2,4; Ps 49,11"] },
      { number: 17, text: "Da verdross mich das Leben. Denn das Tun, das unter der Sonne getan wurde, lastete auf mir als etwas Schlimmes. Denn es ist alles Windhauch und Luftgespinst.", footnotes: ["5,12; 6,1f"] },
      { number: 18, text: "Mich verdross auch mein ganzer Besitz, für den ich mich unter der Sonne anstrenge und den ich dem Menschen lassen muss, der nach mir kommt.", footnotes: ["1 Kön 12,1-17; (18-19) 2,12; 6,1-7; Ps 39,7"] },
      { number: 19, text: "Wer weiß, ob er ein Wissender ist oder ein Unwissender? Jedenfalls wird er über meinen ganzen Besitz verfügen, für den ich mich unter der Sonne angestrengt und mein Wissen eingesetzt habe. Auch das ist Windhauch." },
      { number: 20, text: "Ich stellte mich um und überließ mich der Verzweiflung über meinen ganzen Besitz, für den ich mich unter der Sonne angestrengt hatte.", footnotes: ["20-23: Nicht nur nach dem Tod, sondern sogar schon während des Lebens kann man seinen Besitz verlieren. Dieser Gedanke leitet zu 2,24 - 3,9 über, wo dargelegt wird, dass auch während des Lebens kein Augenblick ganz in der Hand des Menschen selbst ist."] },
      { number: 21, text: "Denn es kommt vor, dass ein Mensch, dessen Besitz durch Wissen, Können und Erfolg erworben wurde, ihn einem andern, der sich nicht dafür angestrengt hat, als dessen Anteil überlassen muss. Auch das ist Windhauch und etwas Schlimmes, das häufig vorkommt.", footnotes: ["6,2; Sir 11,18f"] },
      { number: 22, text: "Was erhält der Mensch dann durch seinen ganzen Besitz und durch das Gespinst seines Geistes, für die er sich unter der Sonne anstrengt?", footnotes: ["1,3"] },
      { number: 23, text: "Alle Tage besteht sein Geschäft nur aus Sorge und Ärger / und selbst in der Nacht kommt sein Geist nicht zur Ruhe. Auch das ist Windhauch.", footnotes: ["1,18; 5,16; 8,16f; 11,10; Ijob 7,1-4"] },
      { number: 24, text: "Nicht im Menschen selbst gründet das Glück, dass er essen und trinken und durch seinen Besitz das Glück selbst kennen lernen kann. Ich habe vielmehr beobachtet, dass dies von Gottes Verfügung abhängt.", heading: "Gottes Handeln in seiner Vollkommenheit und Undurchschaubarkeit: 2,24 - 3,15", footnotes: ["2,10"] },
      { number: 25, text: "Denn wer hat zu essen, wer weiß zu genießen, wenn nicht ich?", footnotes: ["9,1"] },
      { number: 26, text: "Aber es gibt Menschen, denen Gott wohlwill. Es sind die, denen er Wissen, Können und Freude geschenkt hat. Und es gibt Menschen, deren Leben verfehlt ist. Es sind diejenigen, die er mit dem Geschäft beauftragt hat, zu sammeln und zu horten und dann alles denen zu geben, denen er wohl will. Auch das ist Windhauch und Luftgespinst.", footnotes: ["7,26; Ijob 27,16f; Spr 13,22; 28,8"] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Alles hat seine Stunde. Für jedes Geschehen unter dem Himmel gibt es eine bestimmte Zeit:", footnotes: ["3,17; 8,5f; 9,11f; Ps 31,16"] },
      { number: 2, text: "eine Zeit zum Gebären / und eine Zeit zum Sterben, / eine Zeit zum Pflanzen / und eine Zeit zum Abernten der Pflanzen,", footnotes: ["7,17; Ijob 5,26; Jes 28,23-29"] },
      { number: 3, text: "eine Zeit zum Töten / und eine Zeit zum Heilen, / eine Zeit zum Niederreißen / und eine Zeit zum Bauen," },
      { number: 4, text: "eine Zeit zum Weinen / und eine Zeit zum Lachen, / eine Zeit für die Klage / und eine Zeit für den Tanz;" },
      { number: 5, text: "eine Zeit zum Steinewerfen / und eine Zeit zum Steinesammeln, / eine Zeit zum Umarmen / und eine Zeit, die Umarmung zu lösen," },
      { number: 6, text: "eine Zeit zum Suchen / und eine Zeit zum Verlieren, / eine Zeit zum Behalten / und eine Zeit zum Wegwerfen," },
      { number: 7, text: "eine Zeit zum Zerreißen / und eine Zeit zum Zusammennähen, / eine Zeit zum Schweigen / und eine Zeit zum Reden,", footnotes: ["Spr 15,23"] },
      { number: 8, text: "eine Zeit zum Lieben / und eine Zeit zum Hassen, / eine Zeit für den Krieg / und eine Zeit für den Frieden.", footnotes: ["9,1"] },
      { number: 9, text: "Wenn jemand etwas tut - welchen Vorteil hat er davon, dass er sich anstrengt?", footnotes: ["1,3"] },
      { number: 10, text: "Ich sah mir das Geschäft an, für das jeder Mensch durch Gottes Auftrag sich abmüht.", footnotes: ["1,13"] },
      { number: 11, text: "Gott hat das alles zu seiner Zeit auf vollkommene Weise getan. Überdies hat er die Ewigkeit in alles hineingelegt, doch ohne dass der Mensch das Tun, das Gott getan hat, von seinem Anfang bis zu seinem Ende wieder finden könnte.", footnotes: ["11,5; 3,14f; 7,14.23-29; 8,16f; Ps 139,13-18", "11b: Andere Übersetzungsmöglichkeit: Überdies hat er den Menschen eine Vorstellung von der Ewigkeit eingegeben . . ."] },
      { number: 12, text: "Ich hatte erkannt: Es gibt kein in allem Tun gründendes Glück, es sei denn, ein jeder freut sich und so verschafft er sich Glück, während er noch lebt,", footnotes: ["2,10"] },
      { number: 13, text: "wobei zugleich immer, wenn ein Mensch isst und trinkt und durch seinen ganzen Besitz das Glück kennen lernt, das ein Geschenk Gottes ist.", footnotes: ["2,24f"] },
      { number: 14, text: "Jetzt erkannte ich: Alles, was Gott tut, geschieht in Ewigkeit. Man kann nichts hinzufügen und nichts abschneiden und Gott hat bewirkt, dass die Menschen ihn fürchten.", footnotes: ["Spr 30,6; Sir 18,6; 42,21 (14-15) 1,4-11"] },
      { number: 15, text: "Was auch immer geschehen ist, war schon vorher da, und was geschehen soll, ist schon geschehen und Gott wird das Verjagte wieder suchen.", footnotes: ["1,9; Sir 5,3"] },
      { number: 16, text: "Noch etwas habe ich beobachtet unter der Sonne: An der Stätte, wo man Urteil spricht, geschieht Unrecht; an der Stätte, wo man gerechtes Urteil sprechen sollte, geschieht Unrecht.", heading: "Die Übel der Welt: 3,16 - 4,16 | Unrecht bei Gericht: 3,16-22", footnotes: ["5,7; (16-22) 8,12-15"] },
      { number: 17, text: "Da dachte ich mir: Gott ist es, der den Unschuldigen wie den Schuldigen verurteilt. Denn eine bestimmte Zeit für jedes Geschehen und für jedes Tun gibt es (auch) dort.", footnotes: ["3,1; 11,3", "Wenn menschliche Richter Unschuldige zum Tod verurteilen, muss das zugleich als Tun Gottes betrachtet werden; denn 3,1 gilt auch bei menschlichen Gerichtsentscheidungen. Von 3,18 ab wird dann erwogen, welche Absicht Gott dabei haben könnte."] },
      { number: 18, text: "Was die einzelnen Menschen angeht, dachte ich mir, dass Gott sie herausgegriffen hat und dass sie selbst (daraus) erkennen müssen, dass sie eigentlich Tiere sind.", footnotes: ["Die «einzelnen Menschen», die Gott «herausgegriffen» hat, sind die unschuldig zum Tod Verurteilten. In H ein Wortspiel: sie selbst (hemmah) - Tiere (behemah)."] },
      { number: 19, text: "Denn jeder Mensch unterliegt dem Geschick und auch die Tiere unterliegen dem Geschick. Sie haben ein und dasselbe Geschick. Wie diese sterben, so sterben jene. Beide haben ein und denselben Atem. Einen Vorteil des Menschen gegenüber dem Tier gibt es da nicht. Beide sind Windhauch.", footnotes: ["2,14; Ps 49,13.21", "19a: Wörtlich: Denn jeder Mensch ist Geschick und auch die Tiere sind Geschick. Oder: Denn jeder Mensch ist ein Zufall und auch die Tiere sind Zufall."] },
      { number: 20, text: "Beide gehen an ein und denselben Ort. Beide sind aus Staub entstanden, beide kehren zum Staub zurück.", footnotes: ["6,6; 12,7; Gen 2,7; 3,19; Ps 103,14; 104,29; 146,4; Ijob 34,14f; Sir 17,1; 40,11; Weish 2,2f"] },
      { number: 21, text: "Wer weiß, ob der Atem der einzelnen Menschen wirklich nach oben steigt, während der Atem der Tiere ins Erdreich hinabsinkt?" },
      { number: 22, text: "So habe ich eingesehen: Es gibt kein Glück, es sei denn, der Mensch kann durch sein Tun Freude gewinnen. Das ist sein Anteil. Wer könnte es ihm ermöglichen, etwas zu genießen, das erst nach ihm sein wird?", footnotes: ["2,10; 6,12"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Dann wieder habe ich alles beobachtet, was unter der Sonne getan wird, um Menschen auszubeuten. Sieh, die Ausgebeuteten weinen und niemand tröstet sie; von der Hand ihrer Ausbeuter geht Gewalt aus und niemand tröstet sie.", heading: "Ausbeutung und Konkurrenzkampf: 4,1-6", footnotes: ["5,7"] },
      { number: 2, text: "Da preise ich immer wieder die Toten, die schon gestorben sind, und nicht die Lebenden, die noch leben müssen.", footnotes: ["7,2; Ijob 3,20-23"] },
      { number: 3, text: "Glücklicher aber als beide preise ich den, der noch nicht geworden ist, der noch nicht das schlimme Tun gesehen hat, das unter der Sonne getan wird.", footnotes: ["6,3-5; Jer 20,18"] },
      { number: 4, text: "Denn ich beobachtete: Jede Arbeit und jedes erfolgreiche Tun bedeutet Konkurrenzkampf zwischen den Menschen. Auch das ist Windhauch und Luftgespinst." },
      { number: 5, text: "Der Ungebildete legt die Hände in den Schoß / und hat doch sein Fleisch zum Essen.", footnotes: ["Spr 6,10; 24,33"] },
      { number: 6, text: "Besser eine Hand voll und Ruhe, / als beide Hände voll und Arbeit und Luftgespinst.", footnotes: ["6,5; Spr 15,16; 16,8; 17,1"] },
      { number: 7, text: "Und wieder habe ich etwas unter der Sonne beobachtet, das Windhauch ist.", heading: "Der allein stehende Mensch: 4,7-12" },
      { number: 8, text: "Es kommt vor, dass jemand allein steht und niemanden bei sich hat. Ja, er besitzt nicht einmal einen Sohn oder Bruder. Aber sein Besitz ist ohne Grenzen und überdies kann sein Auge vom Reichtum nicht genug bekommen. Doch für wen strenge ich mich dann an und warum gönne ich mir kein Glück? Auch das ist Windhauch und ein schlechtes Geschäft.", footnotes: ["1,8; 5,9; Spr 27,20; Koh 2,18-20"] },
      { number: 9, text: "Zwei sind besser als einer allein, falls sie nur reichen Ertrag aus ihrem Besitz ziehen." },
      { number: 10, text: "Denn wenn sie hinfallen, richtet einer den anderen auf. Doch wehe dem, der allein ist, wenn er hinfällt, ohne dass einer bei ihm ist, der ihn aufrichtet." },
      { number: 11, text: "Außerdem: Wenn zwei zusammen schlafen, wärmt einer den andern; / einer allein - wie soll er warm werden?", footnotes: ["1 Kön 1,1-4"] },
      { number: 12, text: "Und wenn jemand einen Einzelnen auch überwältigt, / zwei sind ihm gewachsen / und eine dreifache Schnur reißt nicht so schnell." },
      { number: 13, text: "Besser ein junger Mann, der niedriger Herkunft, aber gebildet ist, / als ein König, der alt, aber ungebildet ist - weil er es nicht mehr verstand, auf Ratschläge zu hören.", heading: "Wankelmütige Volksgunst: 4,13-16", footnotes: ["Spr 17,2; (13-16) Sir 11,1-6", "13-16: Kohelet beginnt hier zwar mit einem damals allgemein bekannten Sprichwort, meint aber von Anfang an einen bestimmten Fall. Das wird aus dem plötzlichen Umsprung in die Form des Berichts deutlich. Kohelet spricht dabei von drei verschiedenen Männern, die nacheinander herrschten."] },
      { number: 14, text: "Der junge Mann wurde aus dem Gefängnis befreit und wurde König, obwohl er, während der andere schon regierte, arm zur Welt gekommen war." },
      { number: 15, text: "Aber ich habe beobachtet, dass alle Lebenden, die unter der Sonne umherlaufen, sich auf die Seite des nächsten jungen Mannes stellten, der statt seiner hochkommt." },
      { number: 16, text: "Die Volksmenge nimmt kein Ende, gleichgültig, wer an ihre Spitze getreten ist. Im Übrigen werden die Späteren auch mit ihm nicht zufrieden sein. Denn auch das ist Windhauch und Luftgespinst.", footnotes: ["1,9-11"] },
      { number: 17, text: "Zügle deinen Schritt, wenn du zum Gotteshaus gehst. Tritt ein, um zuzuhören, / und nicht, wie die Ungebildeten, um Opfer abzugeben. Sie verstehen nicht einmal, Böses zu tun.", heading: "Ratschläge für das religiöse Verhalten: 4,17 - 5,6 | Hören, Opfern und Sprechen im Gottesdienst: 4,17 - 5,2", footnotes: ["1 Sam 15,22; Spr 15,7f; Hos 6,6; (17-5,2) Jak 1,19", "Wortgottesdienst steht über Opfergottesdienst."] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Sei nicht zu schnell mit dem Mund, ja selbst innerlich fiebere nicht, vor Gott das Wort zu ergreifen. Gott ist im Himmel, du bist auf der Erde, also mach wenig Worte!" },
      { number: 2, text: "Im Traum schließt man viele Geschäfte ab, der Ungebildete macht viele Worte.", footnotes: ["10,14; Spr 10,19"] },
      { number: 3, text: "Wenn du Gott ein Gelübde machst, / dann zögere nicht, es zu erfüllen. Die Ungebildeten gefallen Gott nicht: / Was du gelobst, erfülle!", heading: "Gelübde: 5,3-6", footnotes: ["Num 30,3; Dtn 23,22; (3-6) Spr 20,25; Sir 18,22f"] },
      { number: 4, text: "Es ist besser, wenn du nichts gelobst, / als wenn du etwas gelobst und nicht erfüllst." },
      { number: 5, text: "Lass nicht zu, dass dein Mund / dein Fleisch in Sünde stürzt. Erkläre nie vor dem Boten: / Es war ein Versehen. Warum soll Gott zürnen über das, was du redest, / und vernichten, was deine Hände tun?", footnotes: ["Lev 4f; Num 15,22-31; 35,9-25", "Unter dem «Boten» ist wahrscheinlich der Priester zu verstehen (vgl. Mal 2,7)."] },
      { number: 6, text: "Vielmehr, wo Träume sich mehren und Windhauch und viele Worte, / da fürchte du Gott!" },
      { number: 7, text: "Wenn du beobachtest, dass in der Provinz die Armen ausgebeutet und Gericht und Gerechtigkeit nicht gewährt werden, dann wundere dich nicht über solche Vorgänge: Ein Mächtiger deckt den andern, / hinter beiden stehen noch Mächtigere", heading: "Über Armut und Reichtum: 5,7 - 6,10 | Beamtenherrschaft: 5,7-8", footnotes: ["3,16; 4,1; Ex 23,6-9"] },
      { number: 8, text: "und es ist auf jeden Fall ein Vorteil für das Land, wenn das bebaute Feld einem König untersteht.", footnotes: ["Andere Übersetzungsmöglichkeit: wenn ein König sich um das Ackerland kümmert. - Gegenüber einem Beamtenstaat wird ausgedehnter königlicher Grundbesitz als das geringere Übel betrachtet."] },
      { number: 9, text: "Wer das Geld liebt, / bekommt vom Geld nie genug; wer den Luxus liebt, / hat nie genug Einnahmen - auch das ist Windhauch.", heading: "Die Nutzlosigkeit des Reichtums: 5,9-11", footnotes: ["1,8; 4,8"] },
      { number: 10, text: "Mehrt sich das Vermögen, / so mehren sich auch die, die es verzehren. Was für ein Erfolg bleibt dem Besitzer? / Seine Augen dürfen zusehen.", footnotes: ["Spr 19,6"] },
      { number: 11, text: "Süß ist der Schlaf des Arbeiters, / ob er wenig oder viel zu essen hat. Dem Reichen raubt sein voller Bauch / die Ruhe des Schlafs." },
      { number: 12, text: "Es gibt etwas Schlimmes, etwas wie eine Krankheit, das ich unter der Sonne beobachtet habe: wenn Reichtum, der für seinen Besitzer ängstlich gehütet wurde, diesem Schlimmes brachte.", heading: "Verlust des Reichtums: 5,12-16", footnotes: ["2,17"] },
      { number: 13, text: "Durch ein schlechtes Geschäft ging ihm dieser Reichtum verloren. Er hatte einen Sohn gezeugt, aber jetzt hat er nichts mehr, das ihm gehört.", footnotes: ["Durch ein schlechtes Geschäft, andere Übersetzungsmöglichkeit: Durch ein schlimmes Ereignis."] },
      { number: 14, text: "Wie er aus dem Leib seiner Mutter herausgekommen ist - nackt, wie er kam, muss er wieder gehen. Von seinem Besitz darf er überhaupt nichts forttragen, nichts, das er als ihm gehörig mitnehmen könnte.", footnotes: ["Ijob 1,21; Ps 49,17f; 1 Tim 6,7"] },
      { number: 15, text: "So ist auch dies etwas Schlimmes, etwas wie eine Krankheit. Genau wie er kam, muss er gehen. Welchen Vorteil bringt es ihm, dass er sich anstrengt für den Wind?" },
      { number: 16, text: "Auch wird er während seines ganzen restlichen Lebens sein Essen im Dunkeln einnehmen; er wird sich häufig ärgern und Krankheit und Unmut werden ihn plagen.", footnotes: ["2,23"] },
      { number: 17, text: "Dies ist etwas, was ich eingesehen habe: Das vollkommene Glück besteht darin, dass jemand isst und trinkt und das Glück kennen lernt durch seinen eigenen Besitz, für den er sich unter der Sonne anstrengt während der wenigen Tage seines Lebens, die Gott ihm geschenkt hat. Denn das ist sein Anteil.", heading: "Unverwerteter Reichtum: 5,17 - 6,2", footnotes: ["2,10"] },
      { number: 18, text: "Außerdem: Immer wenn Gott einem Menschen Reichtum und Wohlstand geschenkt und ihn ermächtigt hat, davon zu essen und seinen Anteil fortzutragen und durch seinen Besitz Freude zu gewinnen, besteht das eigentliche Geschenk Gottes darin," },
      { number: 19, text: "dass dieser Mensch sich nicht so oft daran erinnern muss, wie wenige Tage sein Leben zählt, weil Gott ihn sich um die Freude seines Herzens bemühen lässt." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Doch es gibt etwas Schlimmes, das ich unter der Sonne beobachtet habe; es lastet häufig auf dem Menschen:", footnotes: ["2,17; 8,6; (1-9) 2,18-23"] },
      { number: 2, text: "Gott schenkt einem Menschen so viel Reichtum, Wohlstand und Geltung, dass ihm nichts fehlt von allem, was er sich wünschen könnte; aber Gott ermächtigt ihn nicht, davon zu essen, sondern ein Fremder isst es auf. Das ist Windhauch und eine schlimme Krankheit.", footnotes: ["Lk 12,20"] },
      { number: 3, text: "Wenn ein Mann hundert Söhne zeugt und viele Jahre lebt, sodass seine Lebenszeit wirklich lang ist, wenn er sich selbst aber seines Vermögens doch nicht bedienen kann, um sich satt zu essen, auch wenn niemals ein Grab auf ihn warten würde - ich sage: Eine Fehlgeburt hat es besser als er.", heading: "Langes Leben und Reichtum ohne Freude: 6,3-5", footnotes: ["4,3; Ijob 3,10-16; Jer 20,17", "Auch wenn niemals ein Grab auf ihn warten würde, Sinn: Wenn das Unmögliche einträte, dass er unsterblich wäre."] },
      { number: 4, text: "Denn: Als Windhauch kam sie, ins Dunkel geht sie, / in Dunkel bleibt ihr Name gehüllt." },
      { number: 5, text: "Sie hat auch die Sonne nicht gesehen und nicht gekannt. So hat sie Ruhe, er nicht.", footnotes: ["4,6"] },
      { number: 6, text: "Und wenn er zweimal tausend Jahre lebte, aber das Glück nicht kennen lernte: Gehen nicht beide zu ein und demselben Ort?", heading: "Langes Leben in Armut und ohne Freude: 6,6-10" },
      { number: 7, text: "Alles Arbeiten des Menschen ist für den Rachen des Totenreichs, und dessen Schlund wird niemals voll.", footnotes: ["Andere Übersetzungsmöglichkeit: Alles Arbeiten des Menschen ist für seinen Mund und doch wird sein Rachen niemals voll."] },
      { number: 8, text: "Denn was hat der Gebildete dem Ungebildeten voraus, was nutzt es dem Armen, auch wenn er etwas kann, noch unter den Lebenden zu weilen?" },
      { number: 9, text: "Besser, etwas vor Augen zu haben / als ein hungriger Rachen. Aber auch das ist Windhauch und Luftgespinst." },
      { number: 10, text: "Was auch immer jemand war, er hat vorher schon seinen Namen bekommen; es war erkannt, dass er nur ein Mensch sein wird, und er kann nicht mit dem streiten, der mächtiger als er ist.", footnotes: ["1,9; 3,14f; Gen 1; Am 3,2; Jer 1,5; Ijob 9; Weish 12,12", "Derjenige, der jedem Menschen schon vorher seinen Namen gibt und ihn in seiner menschlichen Begrenztheit durchschaut, ist Gott. Ob mit dem Mächtigeren, mit dem ein Mensch nicht streiten kann, der Tod gemeint ist oder Gott, bleibt ungesagt."] },
      { number: 11, text: "Es gibt viele Worte, die nur den Windhauch vermehren. Was nützt das dem Menschen?", heading: "Alte Spruchweisheiten - kritisch überprüft: 6,11 - 9,6 | Einleitung: 6,11-12", footnotes: ["11f: Beispiele solcher «Worte» werden in 7,1 - 9,6 angeführt und kritisiert. Die Fragen in 6,12 deuten den Aufbau dieses kommenden Abschnitts an."] },
      { number: 12, text: "Denn: Wer kann erkennen, was für den Menschen besser ist in seinem Leben, während der wenigen Tage seines Lebens voll Windhauch, die er wie ein Schatten verbringt? Und wer kann dem Menschen verkünden, was nach ihm unter der Sonne geschehen wird?", footnotes: ["3,22; 7,14f; 8,7.13; 9,9; 10,14; 1 Chr 29,15; Ps 39,7; Ijob 14,1"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Besser ein guter Name als Parfüm - / und der Tag eines Todes als der Tag einer Geburt;", heading: "Zum Thema: Nachruhm: 7,1-4", footnotes: ["Spr 22,1; Sir 41,11-13", "In H ein Wortspiel zwischen «Name» (schem) und «Parfüm» (schemen tob). «Name» meint den guten Ruf, aber auch den Nachruhm über den Tod hinaus (vgl. Spr 10,7; Ijob 18,17). An diese Nuance des Sprichworts knüpft die eigene Überlegung Kohelets an. Ihr Anfang ist, wie immer im Folgenden, in der Übersetzung durch einen Gedankenstrich gekennzeichnet."] },
      { number: 2, text: "besser der Gang in ein Haus, wo man trauert, / als der Gang in ein Haus, wo man trinkt. Weil dies das Ende jedes Menschen ist, / macht, wer noch lebt, sich Gedanken.", footnotes: ["11,9 - 12,7; Sir 7,36"] },
      { number: 3, text: "Besser sich ärgern als lachen; / denn bei einem vergrämten Gesicht wird das Herz heiter.", footnotes: ["1 Kor 3,10f"] },
      { number: 4, text: "Das Herz der Gebildeten ist im Haus, wo man trauert, / das Herz der Ungebildeten im Haus, wo man sich freut." },
      { number: 5, text: "Besser die Mahnrede eines Gebildeten anhören, / als dem Gesang der Ungebildeten lauschen;", heading: "Zum Thema: Bildung: 7,5-7", footnotes: ["Spr 13,1; 15,32"] },
      { number: 6, text: "denn wie das Prasseln der Dornen unter dem Kessel, / so ist das Lachen des Ungebildeten. - Aber auch das ist Windhauch, denn:", footnotes: ["In H ein Wortspiel: Gesang (schir) - Dornen (sirim) - Kessel (sir) - Ungebildeter (kesil)."] },
      { number: 7, text: "Erpressung verblendet den Gebildeten / und Bestechung verdirbt den Verstand.", footnotes: ["Ex 23,8; Dtn 16,19"] },
      { number: 8, text: "Besser der Ausgang einer Sache als ihr Anfang, / besser der Vorsichtige als der Stürmische.", heading: "Zum Thema: Zurückhaltung und Hängen am Hergebrachten: 7,8-10", footnotes: ["Spr 24,20"] },
      { number: 9, text: "Lass dich nicht aufregen, sodass du dich ärgerst, / denn Ärger steckt in den Ungebildeten. -", footnotes: ["Spr 22,24"] },
      { number: 10, text: "Doch frag nicht: Wie kommt es, dass die früheren Zeiten besser waren als unsere? Denn deine Frage zeugt nicht von Wissen." },
      { number: 11, text: "Wissen ist so viel wert wie Erbbesitz, / es ist sogar mehr wert für die, welche die Sonne sehen;", heading: "Zum Thema: Wissen als Mittel zu langem Leben: 7,11-18", footnotes: ["Spr 16,16"] },
      { number: 12, text: "denn wer sich im Schatten des Wissens birgt, der ist auch im Schatten des Geldes; / aber das ist der Vorteil des Könnens: Das Wissen erhält seinen Besitzer am Leben. -", footnotes: ["Spr 3,1f.13-18", "Diese Bildungstheorie ist von der Überzeugung getragen, der Mensch könne mit seinem Wissen die Zukunft überschaubar und beherrschbar machen; dadurch ließe sich vorzeitiger Tod vermeiden. Wichtig ist dabei die Annahme eines Zusammenhangs zwischen menschlichem Verhalten und späterem Ergehen. Das stellt Kohelet im Folgenden immer wieder in Frage. Deshalb kommt er dann auch zu einer anderen Ethik."] },
      { number: 13, text: "Doch sieh ein, dass Gottes Tun noch hinzukommt. Denn: Wer kann gerade biegen, was er gekrümmt hat?", footnotes: ["1,15", "Beweisführung mit der Aussage von 1,15."] },
      { number: 14, text: "Am Glückstag erfreue dich deines Glücks und am Unglückstag sieh ein: Auch diesen hat Gott geschaffen, genau wie jenen, sodass der Mensch von dem, was nach ihm kommt, gar nichts herausfinden kann.", footnotes: ["3,11; Ijob 2,10; Sir 11,14"] },
      { number: 15, text: "In meinen Tagen voll Windhauch habe ich beides beobachtet: Es kommt vor, dass ein gesetzestreuer Mensch trotz seiner Gesetzestreue elend endet, und es kommt vor, dass einer, der sich nicht um das Gesetz kümmert, trotz seines bösen Tuns ein langes Leben hat.", footnotes: ["8,14; 9,1f; Ps 73,12-14; Ijob 21,7"] },
      { number: 16, text: "Halte dich nicht zu streng an das Gesetz und sei nicht maßlos im Erwerb von Wissen! Warum solltest du dich selbst ruinieren?", footnotes: ["2,15; Lk 18,9-14"] },
      { number: 17, text: "Entfern dich nicht zu weit vom Gesetz und verharre nicht im Unwissen: Warum solltest du vor der Zeit sterben?", footnotes: ["3,2"] },
      { number: 18, text: "Es ist am besten, wenn du an dem einen fest hältst, aber auch das andere nicht loslässt. Wer Gott fürchtet, wird sich in jedem Fall richtig verhalten.", footnotes: ["Spr 10,27", "Andere Übersetzungsmöglichkeit: Wer Gott fürchtet, wird beiden Gefahren entgehen. - An dieser Stelle ist Gottesfurcht jenes Ethos, das sich aus der Einsicht von 7,13 ergibt: dass Gottes Handeln letztlich alle Ereignisse bestimmt und dass es sich nicht nach Regeln festlegen lässt (vgl. 3,14)."] },
      { number: 19, text: "Das Wissen ist für den Gebildeten ein stärkerer Schutz / als zehn Machthaber zusammen, / die in der Stadt geherrscht haben. -", heading: "Zum Thema: Wissen als Schutz: 7,19-22", footnotes: ["9,16; Spr 21,22; 24,5", "19-22: Der Behauptung der Tradition, das Wissen schütze vor Gefahren, begegnet Kohelet mit der These, es gebe überhaupt keinen Menschen, der es fertig bringt, fehlerfrei nach Wissen und Erziehung zu handeln."] },
      { number: 20, text: "Doch gibt es auf der Erde keinen einzigen Menschen, der so gesetzestreu wäre, dass er stets richtig handelt, ohne je einen Fehler zu begehen.", footnotes: ["1 Kön 8,46; Ps 14,3; Ijob 15,14-16; Röm 1,18 - 3,20; 5,12; 1 Joh 1,8"] },
      { number: 21, text: "Hör auch nicht auf all die Worte, die man so sagt. Denn niemals wirst du einen Untergebenen über dich schimpfen hören," },
      { number: 22, text: "und doch bist du dir bewusst, dass auch du sehr oft über andere geschimpft hast." },
      { number: 23, text: "Auf allen Wegen habe ich es mit dem Wissen versucht. Ich habe gesagt: Ich will lernen und dadurch gebildet werden. Aber das Wissen blieb für mich in der Ferne.", heading: "Zum Thema: Überliefertes Wissen und Wissen aus Beobachtung: 7,23 - 8,1a", footnotes: ["23-25: Diese den Abschnitt einleitenden Sätze dienen innerhalb von 6,11 - 9,6 auch als Rückblick und Vorblick."] },
      { number: 24, text: "Fern ist alles, was geschehen ist, / und tief, tief versunken - / wer könnte es wieder finden?", footnotes: ["1,11; 3,11"] },
      { number: 25, text: "So habe ich, genauer: mein Verstand, mich umgestellt. Ich wollte forschend und suchend erkennen, was dasjenige Wissen wirklich ist, das Einzelbeobachtungen zusammenrechnet. Ferner wollte ich erkennen, ob Gesetzesübertretung mit mangelnder Bildung und Unwissen mit Verblendung zusammenhängt.", footnotes: ["1,17"] },
      { number: 26, text: "Immer wieder finde ich die Ansicht, stärker als der Tod sei die Frau. Denn: Sie ist ein Ring von Belagerungstürmen / und ihr Herz ist ein Fangnetz, / Fesseln sind ihre Arme. Wem Gott wohlwill, der kann sich vor ihr retten, / wessen Leben verfehlt ist, wird von ihr eingefangen.", footnotes: ["Ri 16,4-21; Spr 2,16-19; 5,2-6; Koh 2,26; Spr 22,14"] },
      { number: 27, text: "Aber sieh dir an, was ich, Beobachtung um Beobachtung, herausgefunden habe, sagte Kohelet, bis ich schließlich das Rechenergebnis fand,", footnotes: ["1,2; 12,8"] },
      { number: 28, text: "oder vielmehr: wie ich immer wieder suchte und nichts fand: Von tausend Menschen habe ich nur einen wieder gefunden, / aber der, den ich von ihnen allen wieder gefunden habe, war keine Frau.", footnotes: ["Widerspricht der in 7,26a zitierten Vorstellung. Kohelet nimmt diese Aussage ganz wörtlich und widerlegt sie durch die Erfahrung: Auch die Frauen sind sterblich."] },
      { number: 29, text: "Sieh dir an, was ich als Einziges herausgefunden habe: Gott hat die Menschen rechtschaffen gemacht, / aber sie haben sich in allen möglichen Berechnungen versucht.", footnotes: ["Schwer übersetzbarer Text, der auf einem Wortspiel beruht. Das hebräische Wort für «Berechnungen» (es bedeutet auch «Wurfmaschinen») verweist auf die Kriegstechnik. Kohelet spielt immer noch auf 7,25f an."] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Wer ist hier gebildet? Wer versteht es, ein Wort zu deuten? Das Wissen eines Menschen macht seine Miene strahlend / und seine strengen Züge lösen sich. -", heading: "Zum Thema: Gebildete und König: 8,1b-4", footnotes: ["2,14; Spr 16,13f", "1a: Andere Lesart: Wer tut es dem Gebildeten gleich? Wer dem, der es versteht, ein Wort zu deuten? - Wählt man diese Lesart, dann bilden die beiden Fragen nicht mehr das Ende des vorangehenden Abschnitts, sondern den Anfang des folgenden."] },
      { number: 2, text: "Ich (dagegen sage): Achte auf die Befehle des Königs, und zwar im Hinblick auf den vor Gott geleisteten Eid.", footnotes: ["2-4: Kohelet widerspricht der Meinung von der Unwiderstehlichkeit des Gebildeten durch den Hinweis auf die größere Durchsetzungskraft der realen Machtverhältnisse. Er kleidet den Hinweis in Ratschläge für das Verhalten bei Hof."] },
      { number: 3, text: "Entferne dich nicht hastig aus seiner Gegenwart und versteife dich nicht auf eine Sache, wenn sie schlimm auszugehen droht. Denn alles, wozu er sich entscheidet, setzt er auch durch.", footnotes: ["10,4; Spr 20,2"] },
      { number: 4, text: "Hinter dem Wort des Königs steht nun einmal die Macht. Wer also kann ihm sagen: Was tust du?" },
      { number: 5, text: "Wer auf das Gebot achtet, den trifft nichts Schlimmes, / der Verstand des Gebildeten weiß die rechte Zeit. -", heading: "Zum Thema: Schicksal des gebildeten Gesetzestreuen und des ungebildeten Gesetzesübertreters: 8,5-12a", footnotes: ["Spr 19,16; (5-6) 3,1"] },
      { number: 6, text: "Allerdings: Es gibt die rechte Zeit für jedes Geschehen und: Schlimmes Geschick lastet häufig auf dem Menschen und:", footnotes: ["2,17; 6,1", "6-12: Die Beweisführung greift zunächst auf 2,17 und 6,1.12 zurück, dann weist sie auf Sprichwörter und auf gemachte Erfahrungen hin. Am Ende stehen Schlussfolgerungen in Spruchform."] },
      { number: 7, text: "Er weiß nicht, was geschehen wird. / Wie es geschehen wird - wer verkündet es ihm?", footnotes: ["6,12"] },
      { number: 8, text: "Es gibt keinen Menschen, der Macht hat über den Wind, / sodass er den Wind einschließen könnte. Es gibt keine Macht über den Sterbetag. / Es gibt im Krieg keinen Urlaub. / Selbst ein Gesetzesbruch kann die Gesetzesbrecher nicht retten.", footnotes: ["Spr 30,4; Koh 9,12"] },
      { number: 9, text: "All dies habe ich beobachtet, als ich mich zu einer Zeit, wo ein Mensch seine Macht über den andern Menschen dazu benutzte, diesem zu schaden, mit jedem Tun beschäftigte, das unter der Sonne getan wurde.", footnotes: ["1,13"] },
      { number: 10, text: "Dabei habe ich beobachtet, wie Menschen, die das Gesetz übertreten hatten, ein Begräbnis erhielten, während andere, die recht getan hatten, ankamen und vom Ort des Heiligtums wieder weggehen und bald in der Stadt vergessen sein werden. Auch das ist Windhauch.", footnotes: ["Ijob 21,27-34"] },
      { number: 11, text: "Denn: Wo keine Strafe verhängt wird, / ist die Bosheit schnell am Werk. Deshalb wächst im Herzen der Menschen die Lust, Böses zu tun." },
      { number: 12, text: "Denn: Ein Sünder kann hundertmal Böses tun / und dennoch lange leben." },
      { number: 13, text: "Freilich kenne ich das Wort: Denen, die Gott fürchten, wird es gut gehen, / weil sie sich vor ihm fürchten; dem, der das Gesetz übertritt, wird es nicht gut gehen / und er wird kein langes Leben haben, gleich dem Schatten, / weil er sich nicht vor Gott fürchtet.", heading: "Weiterführung: 8,12b-15", footnotes: ["6,12; (12-13) Ps 37,17-20; Spr 10,27; (12b-15) 3,16-22"] },
      { number: 14, text: "Doch es gibt etwas, das auf der Erde getan wurde und Windhauch ist: Es gibt gesetzestreue Menschen, denen es so ergeht, / als hätten sie wie Gesetzesbrecher gehandelt; und es gibt Gesetzesbrecher, / denen es so ergeht, / als hätten sie wie Gesetzestreue gehandelt. Ich schloss daraus, dass auch dies Windhauch ist.", footnotes: ["7,15; Ps 73,2-12; Jer 12,1"] },
      { number: 15, text: "Da pries ich die Freude; denn es gibt für den Menschen kein Glück unter der Sonne, es sei denn, er isst und trinkt und freut sich. Das soll ihn begleiten bei seiner Arbeit während der Lebenstage, die Gott ihm unter der Sonne geschenkt hat.", footnotes: ["2,10"] },
      { number: 16, text: "Als ich mir vorgenommen hatte zu erkennen, was Wissen wirklich ist, und zu beobachten, welches Geschäft eigentlich auf der Erde getätigt wird, da sah ich ein, dass der Mensch, selbst wenn er seinen Augen bei Tag und Nacht keinen Schlaf gönnt, das Tun Gottes in seiner Ganzheit nicht wieder finden kann, das Tun, das unter der Sonne getan wurde. Deshalb strengt der Mensch, danach suchend, sich an und findet es doch nicht wieder. Selbst wenn der Gebildete behauptet, er erkenne - er kann es doch nicht wieder finden.", heading: "Zum Thema: Grenzen der Erkenntnis: 8,16 - 9,6", footnotes: ["1,8.13.17; 2,23; 3,11; 11,5; Sir 18,5"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Denn ich habe über dies alles nachgedacht und dies alles überprüft, wobei sich ergab: Die Gesetzestreuen und Gebildeten mit ihrem Tun stehen unter Gottes Verfügung. Der Mensch erkennt nicht, ob er geliebt ist oder ob er verschmäht ist. So liegt auch bei ihnen beides offen vor ihnen.", footnotes: ["7,15; 2,24; 3,8"] },
      { number: 2, text: "Beides - wie bei allen Menschen. Aber ein und dasselbe Geschick trifft den Gesetzestreuen und den Gesetzesbrecher, den Guten, den Reinen und den Unreinen, den Opfernden und den, der nicht opfert. Dem Guten ergeht es wie dem Sünder, dem Schwörenden ebenso wie dem, der den Schwur scheut.", footnotes: ["2,14; 7,15"] },
      { number: 3, text: "Das ist das Schlimme an allem, was unter der Sonne getan wurde, dass alle dann ein und dasselbe Geschick trifft und dass in den Menschen überdies die Lust zum Bösen wächst und Verblendung ihren Geist erfasst, während sie leben und danach, wenn sie zu den Toten müssen", footnotes: ["8,11", "3f: Andere Lesart: und danach müssen sie zu den Toten. Wer unter die Lebenden eingereiht ist, für den gibt es noch Zuversicht."] },
      { number: 4, text: "ja, wer würde da ausgenommen? Für jeden Lebenden gibt es noch Zuversicht. Denn: Ein lebender Hund ist besser als ein toter Löwe." },
      { number: 5, text: "Und: Die Lebenden erkennen, dass sie sterben werden; die Toten aber erkennen überhaupt nichts mehr. Sie erhalten auch keine Belohnung mehr; denn die Erinnerung an sie ist in Vergessenheit versunken.", footnotes: ["1,11", "5f: Andere Übersetzungsmöglichkeit: Sie erhalten auch keine Belohnung mehr, ihre Erinnerung ist geschwunden, ihre Liebe, ihr Hass und ihre Eifersucht sind längst erloschen."] },
      { number: 6, text: "Liebe, Hass und Eifersucht gegen sie, all dies ist längst erloschen. Auf ewig haben sie keinen Anteil mehr an allem, was unter der Sonne getan wurde.", footnotes: ["2,10"] },
      { number: 7, text: "Also: Iss freudig dein Brot und trink vergnügt deinen Wein; denn das, was du tust, hat Gott längst so festgelegt, wie es ihm gefiel.", heading: "Ratschläge Kohelets: 9,7 - 12,8 | Freude und kraftvolles Handeln: 9,7-10", footnotes: ["2,10", "7-10: Dieser Abschnitt gehört zu einer Art Rahmen. VV. 7-9 bringen schon Motive des Schlussgedichts 11,9 - 12,7. In V. 10 wird 11,4-8, der Abschnitt vor dem Schlussgedicht, angekündigt. Innerhalb dieses Rahmens stehen in 9,11 - 11,3 in lockerer Folge verschiedene Sentenzen und Ratschläge."] },
      { number: 8, text: "Trag jederzeit frische Kleider und nie fehle duftendes Öl auf deinem Haupt." },
      { number: 9, text: "Mit einer Frau, die du liebst, genieß das Leben alle Tage deines Lebens voll Windhauch, die er dir unter der Sonne geschenkt hat, alle deine Tage voll Windhauch. Denn das ist dein Anteil am Leben und an dem Besitz, für den du dich unter der Sonne anstrengst.", footnotes: ["Spr 5,18f"] },
      { number: 10, text: "Alles, was deine Hand, solange du Kraft hast, zu tun vorfindet, das tu! Denn es gibt weder Tun noch Rechnen noch Können noch Wissen in der Unterwelt, zu der du unterwegs bist." },
      { number: 11, text: "Wiederum habe ich unter der Sonne beobachtet: Nicht den Schnellen gehört im Wettlauf der Sieg, / nicht den Tapferen der Sieg im Kampf, / auch nicht den Gebildeten die Nahrung, / auch nicht den Klugen der Reichtum, / auch nicht den Könnern der Beifall, / sondern jeden treffen Zufall und Zeit.", heading: "Zufall und Zeit: 9,11-12", footnotes: ["Spr 16,9; Röm 9,6; (11-12) 3,1"] },
      { number: 12, text: "Außerdem: Der Mensch kennt seine Zeit nicht. Wie Fische, die ins Unglücksnetz geraten sind, / wie Vögel, die ins Klappnetz geraten sind, / ebenso verfangen sich die einzelnen Menschen in ihre Unglückszeit, / wenn sie plötzlich über sie herabfällt." },
      { number: 13, text: "Auch Folgendes habe ich unter der Sonne beobachtet, ein Beispiel von Wissen, das ich für bedeutsam hielt:", heading: "Wissen und Macht: 9,13-18" },
      { number: 14, text: "Es war eine kleine Stadt. Die hatte nur wenige Einwohner. Ein mächtiger König zog gegen sie aus. Er schloss sie ein und baute gegen sie hohe Belagerungstürme." },
      { number: 15, text: "In der Stadt fand sich ein armer, aber gebildeter Mann. Der rettete die Stadt durch sein Wissen. Später aber erinnerte sich kein Mensch mehr an diesen armen Mann.", footnotes: ["Andere Übersetzungsmöglichkeit: Der hätte die Stadt durch sein Wissen retten können. Aber kein Mensch dachte an diesen Mann."] },
      { number: 16, text: "Da sagte ich: Wissen ist besser als Macht, / aber das Wissen des Armen gilt nichts / und niemand will seine Worte hören.", footnotes: ["7,19; Spr 24,5; Sir 13,22f"] },
      { number: 17, text: "Bedächtige Worte von Gebildeten hört man sich lieber an / als das Geschrei des Herrschers der Ungebildeten", footnotes: ["Spr 21,22"] },
      { number: 18, text: "und Wissen ist besser als Waffen - / aber ein Einziger, der falsch entscheidet, / kann viele Werte zerstören." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Sterbende Fliegen - da stinkt und gärt sogar das (duftende) Öl für die Schönheitspflege; / schwerer als Wissen und Geltung wiegt eine kleine Dummheit.", heading: "Dummheit: 10,1-3", footnotes: ["Wörtlich: Sterbende Fliegen - da stinken und gären die Wässer des Parfümherstellers."] },
      { number: 2, text: "Der Verstand des Gebildeten wählt den rechten Weg, / der Verstand des Ungebildeten den linken;", footnotes: ["2,14"] },
      { number: 3, text: "doch der Dumme - welchen Weg er auch einschlägt, / ihm fehlt der Verstand, / obwohl er von jedem andern gesagt hat: Er ist dumm." },
      { number: 4, text: "Wenn der Herrscher gegen dich in Zorn gerät, bewahre die Ruhe; / denn Gelassenheit bewahrt vor großen Fehlern.", heading: "Gelassenheit: 10,4", footnotes: ["8,2-4", "4b: Andere Übersetzungsmöglichkeit: Gib deine Stellung nicht auf!"] },
      { number: 5, text: "Es gibt etwas Schlimmes, das ich unter der Sonne beobachtet habe - solch ein Versehen, wie es vom Machthaber zu kommen pflegt:", heading: "Ungerechte Behandlung bei Hof: 10,5-7", footnotes: ["Versehen: ironisch gemeint."] },
      { number: 6, text: "Die Dummheit wurde auf höchste Posten gestellt / und Reiche müssen unten sitzen.", footnotes: ["Spr 30,22"] },
      { number: 7, text: "Ich habe Sklaven hoch zu Pferd gesehen / und Fürsten, die wie Sklaven zu Fuß gehen mussten." },
      { number: 8, text: "Wer eine Grube gräbt, kann hineinfallen, / wer eine Mauer einreißt, den kann die Schlange beißen,", heading: "Lauernde Gefahren: 10,8-9", footnotes: ["Ps 7,16; 9,16; 35,8; 57,7; Spr 26,27; Sir 27,26"] },
      { number: 9, text: "wer Steine bricht, kann sich dabei verletzen, / wer Holz spaltet, bringt sich dadurch in Gefahr." },
      { number: 10, text: "Wenn die Axt stumpf geworden ist / und ihr Benutzer hat sie nicht vorher geschliffen, / dann braucht er mehr Kraft - / Wissen hätte ihm den Vorteil gebracht, dass er sein Werkzeug vorbereitet hätte.", heading: "Anwendung des Wissens: 10,10-11", footnotes: ["10d: Wörtlich: Der Vorteil, vorzubereiten, ist Wissen."] },
      { number: 11, text: "Der Schlangenbeschwörer hat keinen Vorteil, / wenn die Schlange beißt, bevor er sie beschworen hat.", footnotes: ["Sir 12,13"] },
      { number: 12, text: "Worte aus dem Mund des Gebildeten finden Beifall, / jedes Wort von den Lippen des Ungebildeten bringt ihn selbst in Verwirrung.", heading: "Der Ungebildete: 10,12-15", footnotes: ["Spr 10,32; 15,2; Sir 21,16"] },
      { number: 13, text: "Wenn er redet, steht Dummheit am Anfang, / am Ende schlimme Verblendung." },
      { number: 14, text: "Und der Dumme redet endlos. Dabei kann doch der Mensch nicht erkennen, was geschehen wird. Und was nach ihm geschieht - wer verkündet es ihm?", footnotes: ["5,2; 6,12"] },
      { number: 15, text: "Die Arbeit erschöpft die Ungebildeten: Keiner hat es verstanden, in die Stadt zu ziehen.", footnotes: ["Andere Übersetzungsmöglichkeit: Wer es nicht versteht, in die Stadt zu ziehen, der macht sich müde als ungelernter Arbeiter. - Es ist an die Lage des Landproletariats gedacht."] },
      { number: 16, text: "Weh dir, Land, / dessen König ein Knabe ist / und dessen Fürsten schon früh am Morgen tafeln.", heading: "Königshof und Schicksal des Volks: 10,16-17", footnotes: ["Jes 3,4; 5,11; Spr 31,4-7", "Knabe: andere Übersetzungsmöglichkeit: Sklave. Für diese Übersetzung spräche V. 17, der von einem König aus «edlem Geschlecht» handelt."] },
      { number: 17, text: "Wohl dir, Land, / dessen König von edlem Geschlecht ist und dessen Fürsten zur richtigen Zeit tafeln, / beherrscht und nicht wie Zecher." },
      { number: 18, text: "Ist einer träge, so senkt sich das Gebälk, / lässt er die Hände sinken, so dringt der Regen ins Haus.", heading: "Faulheit: 10,18", footnotes: ["Spr 20,4"] },
      { number: 19, text: "Man schlemmt und will dabei lachen, / der Wein erfreut die Lebenden, / das Geld macht alles möglich.", heading: "Geld: 10,19", footnotes: ["Ps 104,15"] },
      { number: 20, text: "Nicht einmal in Gedanken / schimpf auf den König, / nicht einmal im Schlafzimmer schimpf auf einen Reichen; denn die Vögel des Himmels können dein Wort verbreiten, / alles, was Flügel hat, / könnte die Nachricht weitermelden.", heading: "Vom Reden über Mächtige: 10,20" },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Leg dein Brot auf die Wasserfläche, / denn noch nach vielen Tagen wirst du es wieder finden -", heading: "Berechenbarkeit und Unberechenbarkeit der Zukunft: 11,1-3" },
      { number: 2, text: "verteil dein Kapital auf sieben oder gar auf acht; / denn du weißt nicht, welches Unglück über das Land kommt." },
      { number: 3, text: "Wenn die Wolken sich mit Regen füllen, / schütten sie ihn auch über das Land aus; wenn ein Baum nach Süden oder Norden fällt - / wohin der Baum auch fällt, da bleibt er liegen.", footnotes: ["3,17"] },
      { number: 4, text: "Wer ständig nach dem Wind schaut, kommt nicht zum Säen, / wer ständig die Wolken beobachtet, kommt nicht zum Ernten.", heading: "Tatkräftiges Handeln: 11,4-8" },
      { number: 5, text: "Wie du den Weg des Windes ebenso wenig wie das Werden des Kindes im Leib der Schwangeren erkennen kannst, so kannst du auch das Tun Gottes nicht erkennen, der alles tut.", footnotes: ["3,11; 8,17; Ps 139,15"] },
      { number: 6, text: "Am Morgen beginne zu säen, auch gegen Abend lass deine Hand noch nicht ruhen; denn du kannst nicht im Voraus erkennen, was Erfolg haben wird, das eine oder das andere, oder ob sogar beide zugleich zu guten Ergebnissen führen." },
      { number: 7, text: "Dann wird das Licht süß sein / und den Augen wird es wohl tun, die Sonne zu sehen." },
      { number: 8, text: "Denn selbst wenn ein Mensch viele Jahre zu leben hat, / freue er sich in dieser ganzen Zeit / und er denke zugleich an die dunklen Tage: / Auch sie werden viele sein. / Alles, was kommt, ist Windhauch.", footnotes: ["8e: Andere Übersetzungsmöglichkeit: Jeder, der (zur Welt) kommt, ist Windhauch (d. h. er muss sterben)."] },
      { number: 9, text: "Freu dich, junger Mann, in deiner Jugend, / sei heiteren Herzens in deinen frühen Jahren! Geh auf den Wegen, die dein Herz dir sagt, / zu dem, was deine Augen vor sich sehen. [ Aber sei dir bewusst, dass Gott dich für all das vor Gericht ziehen wird.]", heading: "Freude in der Jugend im Blick auf Alter und Tod: 11,9 - 12,7", footnotes: ["(9-12,7) 7,1-4", "Die zweite Vershälfte ist vermutlich vom Verfasser des zweiten Nachworts hinzugefügt worden (vgl. 12,14)."] },
      { number: 10, text: "Halte deinen Sinn von Ärger frei / und schütz deinen Leib vor Krankheit; / denn die Jugend und das dunkle Haar sind Windhauch.", footnotes: ["2,23", "Andere Übersetzungsmöglichkeit: denn die Jugend und die Morgenröte sind . . ."] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Denk an deinen Schöpfer in deinen frühen Jahren, / ehe die Tage der Krankheit kommen und die Jahre dich erreichen, / von denen du sagen wirst: Ich mag sie nicht!," },
      { number: 2, text: "ehe Sonne und Licht und Mond und Sterne erlöschen / und auch nach dem Regen wieder Wolken aufziehen:" },
      { number: 3, text: "am Tag, da die Wächter des Hauses zittern, / die starken Männer sich krümmen, / die Müllerinnen ihre Arbeit einstellen, weil sie zu wenige sind, / es dunkel wird bei den Frauen, die aus den Fenstern blicken,", footnotes: ["3f: Allegorie; gemeint sind Arme, Beine, Zähne, Augen, Mund und Ohren des alternden Menschen."] },
      { number: 4, text: "und das Tor zur Straße verschlossen wird; / wenn das Geräusch der Mühle verstummt, / steht man auf beim Zwitschern der Vögel, / doch die Töne des Lieds verklingen;", footnotes: ["4d: Wörtlich: Alle Töchter des Liedes ducken sich. - Die «Töchter des Liedes» sind in der Übersetzung als Töne der Melodie verstanden. Es könnte sich aber auch um eine poetische Umschreibung für die Vögel handeln, die verstummen."] },
      { number: 5, text: "selbst vor der Anhöhe fürchtet man sich und vor den Schrecken am Weg; / der Mandelbaum blüht, / die Heuschrecke schleppt sich dahin, / die Frucht der Kaper platzt, / doch ein Mensch geht zu seinem ewigen Haus / und die Klagenden ziehen durch die Straßen -", footnotes: ["Sir 25,20", "Das «ewige Haus» ist das Grab."] },
      { number: 6, text: "ja, ehe die silberne Schnur zerreißt, / die goldene Schale bricht, / der Krug an der Quelle zerschmettert wird, / das Rad zerbrochen in die Grube fällt," },
      { number: 7, text: "der Staub auf die Erde zurückfällt als das, was er war, / und der Atem zu Gott zurückkehrt, / der ihn gegeben hat.", footnotes: ["3,20f"] },
      { number: 8, text: "Windhauch, Windhauch, sagte Kohelet, das ist alles Windhauch.", heading: "Rahmenvers: 12,8", footnotes: ["1,2"] },
      { number: 9, text: "Kohelet war ein Gelehrter. Außerdem hat er einfachen Leuten Kenntnisse beigebracht. Er hörte zu und prüfte, er hat viele Sprichwörter selbst in Form gebracht.", heading: "Zwei Nachworte von Herausgebern: 12,9-14 | Das erste Nachwort: 12,9-11", footnotes: ["1 Kön 5,12"] },
      { number: 10, text: "Kohelet hat sich bemüht, gut formulierte Worte zu entdecken, und hier sind diese wahren Worte sorgfältig aufgeschrieben." },
      { number: 11, text: "Worte von Gelehrten sind wie Ochsenstecken, / Sprüche aus Sammlungen aber sitzen wie eingetriebene Nägel - / sie sind die Gabe eines einzigen Hirten.", footnotes: ["Hier ist vom Vorteil schriftlicher Aufzeichnungen gegenüber mündlicher Lehre die Rede. Vielleicht hatte Kohelet selbst nur mündlich gelehrt und der Verfasser des ersten Nachwortes mag seine von den Schülern auswendig gelernten oder in privaten Aufzeichnungen festgehaltenen Lehren gesammelt und als Buch veröffentlicht haben."] },
      { number: 12, text: "Im Übrigen, mein Sohn, lass dich warnen! Es nimmt kein Ende mit dem vielen Bücherschreiben und viel Studieren ermüdet den Leib.", heading: "Das zweite Nachwort: 12,12-14", footnotes: ["12-14: Das zweite Nachwort bringt einige Absicherungen an dem Buch an, damit dieses Buch als rechtgläubig gelten kann."] },
      { number: 13, text: "Hast du alles gehört, so lautet der Schluss: Fürchte Gott und achte auf seine Gebote! Das allein hat jeder Mensch nötig.", footnotes: ["3,14; 5,6; 7,18; Dtn 10,12; Spr 1,7; Sir 1,13"] },
      { number: 14, text: "Denn Gott wird jedes Tun vor das Gericht bringen, das über alles Verborgene urteilt, es sei gut oder böse.", footnotes: ["11,9"] },
    ] },
  ],
};
