import { Book } from "@/lib/types";

export const exodus: Book = {
  id: "exodus",
  name: "Exodus",
  shortName: "Ex",
  testament: "old",
  introduction: "Das Buch Exodus ist vom Buch Genesis deutlich abgehoben. Es schildert die weiteren Schicksale der Nachkommen Jakobs, die in Ägypten zu einem großen Volk werden. Das Buch trägt in der griechischen und lateinischen Bibel den Namen Exodus, das heißt »Auszug«, weil die Schilderung des Auszugs der Israeliten aus Ägypten unter Führung des Mose wesentlicher Bestandteil dieses Buches ist. Bei den Juden heißt das Buch nach den Anfangsworten Elle Schemót (Das sind die Namen). Die Darstellung reicht von der Volkwerdung Israels in Ägypten bis zu den Ereignissen am Sinai. Da aber die erzählenden Teile durch Gesetzestexte und Anordnungen über die Errichtung des Heiligtums sowie Berichte über die Ausführung dieser Anordnungen unterbrochen werden, ist die Gliederung nicht so durchsichtig wie im Buch Genesis. Man kann den Inhalt folgendermaßen gliedern: Volkwerdung und Fronknechtschaft in Ägypten (Kap. 1); Jugend und Flucht des Mose (Kap. 2); Berufung des Mose und seine Rückkehr nach Ägypten (Kap. 3 und 4); Verhandlungen mit dem Pharao, die ägyptischen Plagen (Kap. 5 - 11); die Feier des Pascha und die Vorbereitungen für den Auszug (12,1 - 13,16); Auszug und Rettung am Schilfmeer (13,17 - 15,21); Wanderung zum Sinai (15,22 - 19,2); Bundesschluss am Sinai (19,3 - 24,18; hier sind eingearbeitet die Zehn Gebote 20,1-17 und das so genannte Bundesbuch Kap. 21 - 23); Anordnungen über Bundeslade, Zeltheiligtum und Priesterschaft (Kap. 25 - 31); der Bundesbruch und seine Folgen (Kap. 32 - 34); die Ausführung der Anordnungen über Bundeslade und Heiligtum (Kap. 35 - 40). In der Geschichtsdarstellung lassen sich die gleichen literarischen Schichten feststellen wie in Genesis (vgl. die Einleitung zu Genesis). Dazu kommen umfangreiche Gesetze, von denen die Zehn Gebote, das sogenannte Bundesbuch und die Vorschriften in 34,11-26 einst selbstständige kleine Gesetzbücher waren. Sie alle können in ihrem Kern auf die Zeit des Mose (13. Jahrhundert v. Chr.) zurückgehen. Auch ist ein längeres Lied aufgenommen, das die Rettungstat Gottes am Schilfmeer verherrlicht (15,1-18). Der Auszug aus Ägypten wird heute allgemein in die zweite Hälfte des 13. Jahrhunderts v. Chr. datiert. Da wir über diese Ereignisse keine außerbiblischen Quellen besitzen und außerdem die Texte erst nach langer mündlicher Überlieferung Jahrhunderte nach den Ereignissen ihre heutige Gestalt erhalten haben, können wir den genauen geschichtlichen Hergang nur schwer feststellen. Der wesentliche Verlauf, wie die Knechtschaft in Ägypten, der Auszug unter der Führung des Mose, die Rettung am Schilfmeer, der Bundesschluss am Sinai durch Mose als Mittler zwischen Volk und Gott und die Anfänge einer israelitischen Gesetzgebung dürften aber zuverlässig wiedergegeben sein. Das Buch ist von großer theologischer Bedeutung wegen der Aussagen über die Erlösung aus Knechtschaft durch das rettende Eingreifen Gottes, über den Bund zwischen Gott und dem Volk Israel, über Mose als den Führer des Volkes, den Mittler des Bundes und auch den großen Fürbitter, der Gottes Zorn nach dem Bundesbruch Israels durch sein Eintreten für das Volk besänftigt. An den großen Jahresfesten Pascha, Pfingsten und Laubhüttenfest wurden die in Exodus geschilderten Ereignisse im Gottesdienst Israels vergegenwärtigt. Sie werden im Christentum als Vorwegnahme und Bilder für die Erlösung durch Jesus Christus verstanden. Die Zehn Gebote haben die Ethik der Kulturvölker maßgeblich geprägt.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Das sind die Namen der Söhne Israels, die nach Ägypten gekommen waren - mit Jakob waren sie gekommen, jeder mit seiner Familie:", heading: "Israel in Ägypten: 1,1 - 11,10 | Jakobs Nachkommen in Ägypten: 1,1-22" },
      { number: 2, text: "Ruben, Simeon, Levi, Juda," },
      { number: 3, text: "Issachar, Sebulon, Benjamin," },
      { number: 4, text: "Dan, Naftali, Gad und Ascher." },
      { number: 5, text: "Zusammen waren es siebzig Personen; sie alle stammten von Jakob ab. Josef aber war bereits in Ägypten." },
      { number: 6, text: "Josef, alle seine Brüder und seine Zeitgenossen waren gestorben." },
      { number: 7, text: "Aber die Söhne Israels waren fruchtbar, sodass das Land von ihnen wimmelte. Sie vermehrten sich und wurden überaus stark; sie bevölkerten das Land." },
      { number: 8, text: "In Ägypten kam ein neuer König an die Macht, der Josef nicht gekannt hatte." },
      { number: 9, text: "Er sagte zu seinem Volk: Seht nur, das Volk der Israeliten ist größer und stärker als wir." },
      { number: 10, text: "Gebt Acht! Wir müssen überlegen, was wir gegen sie tun können, damit sie sich nicht weiter vermehren. Wenn ein Krieg ausbricht, können sie sich unseren Feinden anschließen, gegen uns kämpfen und sich des Landes bemächtigen.", footnotes: ["und sich des Landes bemächtigen, andere Übersetzungsmöglichkeit: und aus dem Land fortziehen."] },
      { number: 11, text: "Da setzte man Fronvögte über sie ein, um sie durch schwere Arbeit unter Druck zu setzen. Sie mussten für den Pharao die Städte Pitom und Ramses als Vorratslager bauen.", footnotes: ["Die beiden Städte wurden vom Pharao Ramses II. (1304-1237 v. Chr.) im östlichen Nildelta als Garnisons- und Vorratsstädte zur Vorbereitung seines Feldzugs gegen die Hetiter angelegt."] },
      { number: 12, text: "Je mehr man sie aber unter Druck hielt, umso stärker vermehrten sie sich und breiteten sie sich aus, sodass die Ägypter vor ihnen das Grauen packte." },
      { number: 13, text: "Daher gingen sie hart gegen die Israeliten vor und machten sie zu Sklaven." },
      { number: 14, text: "Sie machten ihnen das Leben schwer durch harte Arbeit mit Lehm und Ziegeln und durch alle möglichen Arbeiten auf den Feldern. So wurden die Israeliten zu harter Sklavenarbeit gezwungen." },
      { number: 15, text: "Zu den hebräischen Hebammen - die eine hieß Schifra, die andere Pua - sagte der König von Ägypten:" },
      { number: 16, text: "Wenn ihr den Hebräerinnen Geburtshilfe leistet, dann achtet auf das Geschlecht! Ist es ein Knabe, so lasst ihn sterben! Ist es ein Mädchen, dann kann es am Leben bleiben." },
      { number: 17, text: "Die Hebammen aber fürchteten Gott und taten nicht, was ihnen der König von Ägypten gesagt hatte, sondern ließen die Kinder am Leben." },
      { number: 18, text: "Da rief der König von Ägypten die Hebammen zu sich und sagte zu ihnen: Warum tut ihr das und lasst die Kinder am Leben?" },
      { number: 19, text: "Die Hebammen antworteten dem Pharao: Bei den hebräischen Frauen ist es nicht wie bei den Ägypterinnen, sondern wie bei den Tieren: Wenn die Hebamme zu ihnen kommt, haben sie schon geboren." },
      { number: 20, text: "Gott verhalf den Hebammen zu Glück; das Volk aber vermehrte sich weiter und wurde sehr stark." },
      { number: 21, text: "Weil die Hebammen Gott fürchteten, schenkte er ihnen Kindersegen." },
      { number: 22, text: "Daher gab der Pharao seinem ganzen Volk den Befehl: Alle Knaben, die den Hebräern geboren werden, werft in den Nil! Die Mädchen dürft ihr alle am Leben lassen." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Ein Mann aus einer levitischen Familie ging hin und nahm eine Frau aus dem gleichen Stamm.", heading: "Die Jugend des Mose: 2,1-14" },
      { number: 2, text: "Sie wurde schwanger und gebar einen Sohn. Weil sie sah, dass es ein schönes Kind war, verbarg sie es drei Monate lang." },
      { number: 3, text: "Als sie es nicht mehr verborgen halten konnte, nahm sie ein Binsenkästchen, dichtete es mit Pech und Teer ab, legte den Knaben hinein und setzte ihn am Nilufer im Schilf aus." },
      { number: 4, text: "Seine Schwester blieb in der Nähe stehen, um zu sehen, was mit ihm geschehen würde." },
      { number: 5, text: "Die Tochter des Pharao kam herab, um im Nil zu baden. Ihre Dienerinnen gingen unterdessen am Nilufer auf und ab. Auf einmal sah sie im Schilf das Kästchen und ließ es durch ihre Magd holen." },
      { number: 6, text: "Als sie es öffnete und hineinsah, lag ein weinendes Kind darin. Sie bekam Mitleid mit ihm und sie sagte: Das ist ein Hebräerkind." },
      { number: 7, text: "Da sagte seine Schwester zur Tochter des Pharao: Soll ich zu den Hebräerinnen gehen und dir eine Amme rufen, damit sie dir das Kind stillt?" },
      { number: 8, text: "Die Tochter des Pharao antwortete ihr: Ja, geh! Das Mädchen ging und rief die Mutter des Knaben herbei." },
      { number: 9, text: "Die Tochter des Pharao sagte zu ihr: Nimm das Kind mit und still es mir! Ich werde dich dafür entlohnen. Die Frau nahm das Kind zu sich und stillte es." },
      { number: 10, text: "Als der Knabe größer geworden war, brachte sie ihn der Tochter des Pharao. Diese nahm ihn als Sohn an, nannte ihn Mose und sagte: Ich habe ihn aus dem Wasser gezogen.", footnotes: ["Der Name Mose ist ägyptisch und bedeutet «Kind»; der israelitische Erzähler hört das hebräische Zeitwort «ziehen» (mascháh) heraus."] },
      { number: 11, text: "Die Jahre vergingen und Mose wuchs heran. Eines Tages ging er zu seinen Brüdern hinaus und schaute ihnen bei der Fronarbeit zu. Da sah er, wie ein Ägypter einen Hebräer schlug, einen seiner Stammesbrüder." },
      { number: 12, text: "Mose sah sich nach allen Seiten um, und als er sah, dass sonst niemand da war, erschlug er den Ägypter und verscharrte ihn im Sand." },
      { number: 13, text: "Als er am nächsten Tag wieder hinausging, sah er zwei Hebräer miteinander streiten. Er sagte zu dem, der im Unrecht war: Warum schlägst du deinen Stammesgenossen?" },
      { number: 14, text: "Der Mann erwiderte: Wer hat dich zum Aufseher und Schiedsrichter über uns bestellt? Meinst du, du könntest mich umbringen, wie du den Ägypter umgebracht hast? Da bekam Mose Angst und sagte: Die Sache ist also bekannt geworden." },
      { number: 15, text: "Der Pharao hörte von diesem Vorfall und wollte Mose töten; Mose aber entkam ihm. Er wollte in Midian bleiben und setzte sich an einen Brunnen.", heading: "Mose in Midian: 2,15-22" },
      { number: 16, text: "Der Priester von Midian hatte sieben Töchter. Sie kamen zum Wasserschöpfen und wollten die Tröge füllen, um die Schafe und Ziegen ihres Vaters zu tränken." },
      { number: 17, text: "Doch die Hirten kamen und wollten sie verdrängen. Da stand Mose auf, kam ihnen zu Hilfe und tränkte ihre Schafe und Ziegen." },
      { number: 18, text: "Als sie zu ihrem Vater Reguël zurückkehrten, fragte er: Warum seid ihr heute so schnell wieder da?", footnotes: ["Reguël: So auch Num 10,29. In Ex 3,1; 4,18; 18,1 heißt er Jitro, in Ri 4,11 aber Hobab. In den verschiedenen Traditionsschichten lautet der Name anders, ähnlich wie der Berg des Bundesschlusses bald Sinai, bald Horeb heißt."] },
      { number: 19, text: "Sie erzählten: Ein Ägypter hat uns gegen die Hirten verteidigt; er hat uns sogar Wasser geschöpft und das Vieh getränkt." },
      { number: 20, text: "Da fragte Reguël seine Töchter: Wo ist er? Warum habt ihr ihn dort gelassen? Holt ihn und ladet ihn zum Essen ein!" },
      { number: 21, text: "Mose entschloss sich, bei dem Mann zu bleiben, und dieser gab seine Tochter Zippora Mose zur Frau." },
      { number: 22, text: "Als sie einen Sohn gebar, nannte er ihn Gerschom (Ödgast) und sagte: Gast bin ich in fremdem Land.", footnotes: ["18,3", "Wortspiel mit dem Namen Gerschom, der aus ger (= Gast) und schom (= Öde) zusammengesetzt ist; in der Deutung des Namens wird aber das ungewöhnliche schom durch das geläufigere nokrí (= fremd) verdeutlicht."] },
      { number: 23, text: "Nach vielen Jahren starb der König von Ägypten. Die Israeliten stöhnten noch unter der Sklavenarbeit; sie klagten und ihr Hilferuf stieg aus ihrem Sklavendasein zu Gott empor.", heading: "Die Berufung des Mose: 2,23 - 4,17" },
      { number: 24, text: "Gott hörte ihr Stöhnen und Gott gedachte seines Bundes mit Abraham, Isaak und Jakob." },
      { number: 25, text: "Gott blickte auf die Söhne Israels und gab sich ihnen zu erkennen.", footnotes: ["und gab sich ihnen zu erkennen: so nach G."] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Mose weidete die Schafe und Ziegen seines Schwiegervaters Jitro, des Priesters von Midian. Eines Tages trieb er das Vieh über die Steppe hinaus und kam zum Gottesberg Horeb." },
      { number: 2, text: "Dort erschien ihm der Engel des Herrn in einer Flamme, die aus einem Dornbusch emporschlug. Er schaute hin: Da brannte der Dornbusch und verbrannte doch nicht." },
      { number: 3, text: "Mose sagte: Ich will dorthin gehen und mir die außergewöhnliche Erscheinung ansehen. Warum verbrennt denn der Dornbusch nicht?" },
      { number: 4, text: "Als der Herr sah, dass Mose näher kam, um sich das anzusehen, rief Gott ihm aus dem Dornbusch zu: Mose, Mose! Er antwortete: Hier bin ich." },
      { number: 5, text: "Der Herr sagte: Komm nicht näher heran! Leg deine Schuhe ab; denn der Ort, wo du stehst, ist heiliger Boden." },
      { number: 6, text: "Dann fuhr er fort: Ich bin der Gott deines Vaters, der Gott Abrahams, der Gott Isaaks und der Gott Jakobs. Da verhüllte Mose sein Gesicht; denn er fürchtete sich, Gott anzuschauen." },
      { number: 7, text: "Der Herr sprach: Ich habe das Elend meines Volkes in Ägypten gesehen und ihre laute Klage über ihre Antreiber habe ich gehört. Ich kenne ihr Leid." },
      { number: 8, text: "Ich bin herabgestiegen, um sie der Hand der Ägypter zu entreißen und aus jenem Land hinaufzuführen in ein schönes, weites Land, in ein Land, in dem Milch und Honig fließen, in das Gebiet der Kanaaniter, Hetiter, Amoriter, Perisiter, Hiwiter und Jebusiter." },
      { number: 9, text: "Jetzt ist die laute Klage der Israeliten zu mir gedrungen und ich habe auch gesehen, wie die Ägypter sie unterdrücken." },
      { number: 10, text: "Und jetzt geh! Ich sende dich zum Pharao. Führe mein Volk, die Israeliten, aus Ägypten heraus!" },
      { number: 11, text: "Mose antwortete Gott: Wer bin ich, dass ich zum Pharao gehen und die Israeliten aus Ägypten herausführen könnte?" },
      { number: 12, text: "Gott aber sagte: Ich bin mit dir; ich habe dich gesandt und als Zeichen dafür soll dir dienen: Wenn du das Volk aus Ägypten herausgeführt hast, werdet ihr Gott an diesem Berg verehren." },
      { number: 13, text: "Da sagte Mose zu Gott: Gut, ich werde also zu den Israeliten kommen und ihnen sagen: Der Gott eurer Väter hat mich zu euch gesandt. Da werden sie mich fragen: Wie heißt er? Was soll ich ihnen darauf sagen?" },
      { number: 14, text: "Da antwortete Gott dem Mose: Ich bin der «Ich-bin-da». Und er fuhr fort: So sollst du zu den Israeliten sagen: Der «Ich-bin-da» hat mich zu euch gesandt.", footnotes: ["14f: Im Hebräischen ist «Jahwe» eine Zeitwortform in der 3. Person Einzahl, etwa «er ist da»; da hier Gott von sich selbst in der 1. Person redet, gebraucht er das Zeitwort in der Ich-Form: Ich bin da. Für «Jahwe» werden allerdings auch andere Erklärungen versucht, von denen noch am ehesten zutreffen könnte: Er setzt ins Dasein. Zur Umschreibung des Gottesnamens Jahwe mit «der Herr» vgl. die Anmerkung zu Gen 4,26."] },
      { number: 15, text: "Weiter sprach Gott zu Mose: So sag zu den Israeliten: Jahwe, der Gott eurer Väter, der Gott Abrahams, der Gott Isaaks und der Gott Jakobs, hat mich zu euch gesandt. Das ist mein Name für immer und so wird man mich nennen in allen Generationen." },
      { number: 16, text: "Geh, versammle die Ältesten Israels und sag ihnen: Jahwe, der Gott eurer Väter, der Gott Abrahams, Isaaks und Jakobs, ist mir erschienen und hat mir gesagt: Ich habe sorgsam auf euch geachtet und habe gesehen, was man euch in Ägypten antut." },
      { number: 17, text: "Darum habe ich beschlossen, euch aus dem Elend Ägyptens hinaufzuführen in das Land der Kanaaniter, Hetiter, Amoriter, Perisiter, Hiwiter und Jebusiter, in ein Land, in dem Milch und Honig fließen." },
      { number: 18, text: "Wenn sie auf dich hören, so geh mit den Ältesten Israels zum König von Ägypten; sagt ihm: Jahwe, der Gott der Hebräer, ist uns begegnet. Und jetzt wollen wir drei Tagesmärsche weit in die Wüste ziehen und Jahwe, unserem Gott, Schlachtopfer darbringen.", footnotes: ["Hier und an ähnlichen Stellen, wo der Name Jahwe im Gespräch mit fremden Völkern gebraucht wird, ist die Umschreibung mit «der Herr» nicht angebracht, weil die nicht israelitischen Völker den Gott Israels nicht als Herrn anerkennen und weil sie für ihre Götter Eigennamen haben."] },
      { number: 19, text: "Ich weiß, dass euch der König von Ägypten nicht ziehen lässt, es sei denn, er würde von starker Hand dazu gezwungen." },
      { number: 20, text: "Erst wenn ich meine Hand ausstrecke und Ägypten niederschlage mit allen meinen Wundern, die ich in seiner Mitte vollbringe, wird er euch ziehen lassen." },
      { number: 21, text: "Dann werde ich die Ägypter zugunsten dieses Volkes umstimmen, und wenn ihr wegzieht, werdet ihr nicht mit leeren Händen gehen." },
      { number: 22, text: "Jede Frau kann von ihrer Nachbarin oder Hausgenossin silberne und goldene Geräte und Kleider verlangen. Übergebt sie euren Söhnen und Töchtern und plündert so die Ägypter aus!" },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Mose antwortete: Was aber, wenn sie mir nicht glauben und nicht auf mich hören, sondern sagen: Jahwe ist dir nicht erschienen?" },
      { number: 2, text: "Der Herr entgegnete ihm: Was hast du da in der Hand? Er antwortete: Einen Stab." },
      { number: 3, text: "Da sagte der Herr: Wirf ihn auf die Erde! Mose warf ihn auf die Erde. Da wurde der Stab zu einer Schlange und Mose wich vor ihr zurück." },
      { number: 4, text: "Der Herr aber sprach zu Mose: Streck deine Hand aus und fasse sie am Schwanz! Er streckte seine Hand aus und packte sie. Da wurde sie in seiner Hand wieder zu einem Stab." },
      { number: 5, text: "So sollen sie dir glauben, dass dir Jahwe erschienen ist, der Gott ihrer Väter, der Gott Abrahams, der Gott Isaaks und der Gott Jakobs." },
      { number: 6, text: "Weiter sprach der Herr zu ihm: Leg deine Hand in deinen Gewandbausch! Er legte seine Hand hinein. Als er sie herauszog, war seine Hand von Aussatz weiß wie Schnee." },
      { number: 7, text: "Darauf sagte der Herr: Leg deine Hand noch einmal in deinen Gewandbausch! Er legte seine Hand noch einmal hinein. Als er sie wieder herauszog, sah sie wieder aus wie der übrige Leib." },
      { number: 8, text: "Wenn sie dir nicht glauben und sich durch das erste Zeichen nicht überzeugen lassen, werden sie auf das zweite Zeichen hin glauben." },
      { number: 9, text: "Glauben sie aber selbst nach diesen beiden Zeichen nicht und lassen sie sich nicht überzeugen, dann nimm etwas Nilwasser und schütt es auf trockenen Boden! Das Wasser, das du aus dem Nil geholt hast, wird auf dem Boden zu Blut werden." },
      { number: 10, text: "Doch Mose sagte zum Herrn: Aber bitte, Herr, ich bin keiner, der gut reden kann, weder gestern noch vorgestern, noch seitdem du mit deinem Knecht sprichst. Mein Mund und meine Zunge sind nämlich schwerfällig." },
      { number: 11, text: "Der Herr entgegnete ihm: Wer hat dem Menschen den Mund gegeben und wer macht taub oder stumm, sehend oder blind? Doch wohl ich, der Herr!" },
      { number: 12, text: "Geh also! Ich bin mit deinem Mund und weise dich an, was du reden sollst." },
      { number: 13, text: "Doch Mose antwortete: Aber bitte, Herr, schick doch einen andern!", footnotes: ["Wörtlich: Schicke, wen du schickst!"] },
      { number: 14, text: "Da entbrannte der Zorn des Herrn über Mose und er sprach: Hast du nicht noch einen Bruder, den Leviten Aaron? Ich weiß, er kann reden; außerdem bricht er gerade auf und wird dir begegnen. Wenn er dich sieht, wird er sich von Herzen freuen." },
      { number: 15, text: "Sprich mit ihm und leg ihm die Worte in den Mund! Ich aber werde mit deinem und seinem Mund sein, ich werde euch anweisen, was ihr tun sollt," },
      { number: 16, text: "und er wird für dich zum Volk reden. Er wird für dich der Mund sein und du wirst für ihn Gott sein." },
      { number: 17, text: "Diesen Stab nimm in deine Hand! Mit ihm wirst du die Zeichen vollbringen." },
      { number: 18, text: "Darauf kehrte Mose zu seinem Schwiegervater Jitro zurück. Er sagte zu ihm: Ich will zu meinen Brüdern nach Ägypten zurückkehren. Ich will sehen, ob sie noch am Leben sind. Jitro antwortete Mose: Geh in Frieden!", heading: "Die Rückkehr des Mose nach Ägypten: 4,18-31", footnotes: ["Zu Jitro vgl. die Anmerkung zu 2,18."] },
      { number: 19, text: "Der Herr sprach zu Mose in Midian: Mach dich auf und kehr nach Ägypten zurück; denn alle, die dir nach dem Leben getrachtet haben, sind tot." },
      { number: 20, text: "Da holte Mose seine Frau und seine Söhne, setzte sie auf einen Esel und trat den Rückweg nach Ägypten an. Den Gottesstab hielt er in der Hand.", footnotes: ["18,3f"] },
      { number: 21, text: "Der Herr sprach zu Mose: Wenn du gehst und nach Ägypten zurückkehrst, halte dir alle Wunder vor Augen, die ich in deine Hand gelegt habe, und vollbring sie vor dem Pharao! Ich will sein Herz verhärten, sodass er das Volk nicht ziehen lässt." },
      { number: 22, text: "Dann sag zum Pharao: So spricht Jahwe: Israel ist mein erstgeborener Sohn." },
      { number: 23, text: "Ich sage dir: Lass meinen Sohn ziehen, damit er mich verehren kann. Wenn du dich weigerst, ihn ziehen zu lassen, bringe ich deinen erstgeborenen Sohn um." },
      { number: 24, text: "Unterwegs am Rastplatz trat der Herr dem Mose entgegen und wollte ihn töten.", footnotes: ["24-26: Eine ähnlich geheimnisvolle Szene wie Gen 32,23-33. Wahrscheinlich betont diese Erzählung die religiöse Bedeutung der Beschneidung. Der schwer kranke Mose konnte nicht beschnitten werden, weil bei den damaligen hygienischen Verhältnissen die Beschneidung fast immer Wundfieber zur Folge hatte (vgl. Gen 34,25 und Jos 5,8f); daher nimmt seine Frau einen Ersatzritus an ihrem Sohn vor."] },
      { number: 25, text: "Zippora ergriff einen Feuerstein und schnitt ihrem Sohn die Vorhaut ab. Damit berührte sie die Beine des Mose und sagte: Ein Blutbräutigam bist du mir." },
      { number: 26, text: "Da ließ der Herr von ihm ab. «Blutbräutigam», sagte sie damals wegen der Beschneidung." },
      { number: 27, text: "Der Herr sprach zu Aaron: Geh hinaus in die Wüste, Mose entgegen! Da ging er. Am Gottesberg traf er ihn und küsste ihn." },
      { number: 28, text: "Mose erzählte Aaron von dem Auftrag, mit dem der Herr ihn gesandt hatte, und von allen Zeichen, zu denen er ihn ermächtigt hatte." },
      { number: 29, text: "Mose und Aaron gingen und versammelten alle Ältesten der Israeliten." },
      { number: 30, text: "Aaron wiederholte vor ihnen alle Worte, die der Herr zu Mose gesprochen hatte, und Mose vollbrachte die Zeichen vor den Augen des Volkes." },
      { number: 31, text: "Da glaubte das Volk, und als sie hörten, dass der Herr sich der Israeliten angenommen und ihr Elend gesehen habe, verneigten sie sich und warfen sich vor ihm nieder." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Danach gingen Mose und Aaron zum Pharao und sagten: So spricht Jahwe, der Gott Israels: Lass mein Volk ziehen, damit sie mir in der Wüste ein Fest feiern können.", heading: "Die erfolglosen Verhandlungen mit dem Pharao: 5,1-19" },
      { number: 2, text: "Der Pharao erwiderte: Wer ist Jahwe, dass ich auf ihn hören und Israel ziehen lassen sollte? Ich kenne Jahwe nicht und denke auch nicht daran, Israel ziehen zu lassen." },
      { number: 3, text: "Da sagten sie: Der Gott der Hebräer ist uns begegnet und jetzt wollen wir drei Tagesmärsche weit in die Wüste ziehen und Jahwe, unserem Gott, Schlachtopfer darbringen, damit er uns nicht mit Pest oder Schwert straft." },
      { number: 4, text: "Der König von Ägypten entgegnete ihnen: Warum, Mose und Aaron, wollt ihr die Leute zum Nichtstun verleiten? Fort mit euch, tut euren Frondienst!" },
      { number: 5, text: "Der Pharao fuhr fort: So viele Leute sind jetzt im Land und ihr wollt sie vom Frondienst abhalten?" },
      { number: 6, text: "Am selben Tag noch gab der Pharao den Antreibern der Leute und den Listenführern die Anweisung:" },
      { number: 7, text: "Gebt den Leuten nicht mehr, wie bisher, Stroh zum Ziegelmachen! Sie sollen selber gehen und sich Stroh besorgen.", footnotes: ["Stroh, zu Häcksel geschnitten, dient bei der Ziegelherstellung als Bindemittel für den Lehm."] },
      { number: 8, text: "Legt ihnen aber das gleiche Soll an Ziegeln auf, das sie bisher erfüllen mussten. Lasst ihnen davon nichts nach! Denn sie sind faul, und deshalb schreien sie: Wir wollen gehen und unserem Gott Schlachtopfer darbringen." },
      { number: 9, text: "Erschwert man den Leuten die Arbeit, dann sind sie beschäftigt und kümmern sich nicht um leeres Geschwätz." },
      { number: 10, text: "Da gingen die Antreiber der Leute und die Listenführer zu den Leuten und sagten: So spricht der Pharao: Ich gebe euch kein Stroh mehr." },
      { number: 11, text: "Geht selbst und besorgt euch Stroh, wo ihr es findet. Von eurem Arbeitssoll aber wird euch nichts erlassen." },
      { number: 12, text: "Die Leute verteilten sich also über ganz Ägypten, um sich Stroh zu besorgen." },
      { number: 13, text: "Die Antreiber drängten und sagten: Ihr müsst euer tägliches Soll erfüllen wie bisher, als euch noch Stroh geliefert wurde." },
      { number: 14, text: "Die Antreiber des Pharao schlugen die israelitischen Listenführer, die sie eingesetzt hatten, und sagten: Warum habt ihr heute nicht wie neulich noch das festgesetzte Soll an Ziegeln erfüllt?" },
      { number: 15, text: "Da gingen die israelitischen Listenführer zum Pharao und erhoben vor ihm Klage: Warum tust du deinen Sklaven das an?" },
      { number: 16, text: "Man gibt deinen Sklaven kein Stroh, aber man sagt uns: Macht Ziegel! Schau, man hat deine Sklaven geschlagen; die Schuld aber liegt bei deinen Leuten." },
      { number: 17, text: "Er entgegnete: Faul seid ihr, faul. Nur deshalb sagt ihr: Wir wollen gehen und Jahwe Schlachtopfer darbringen." },
      { number: 18, text: "Jetzt aber fort mit euch und tut eure Arbeit! Stroh bekommt ihr nicht, aber euer Soll an Ziegeln müsst ihr erfüllen." },
      { number: 19, text: "Da sahen sich die israelitischen Listenführer in einer üblen Lage, weil man ihnen sagte: Nichts von eurem täglichen Soll an Ziegeln wird euch erlassen." },
      { number: 20, text: "Als sie vom Pharao kamen, stießen sie auf Mose und Aaron, die ihnen entgegenkamen.", heading: "Die Klage des Mose und die Verheißung Gottes: 5,20 - 6,13" },
      { number: 21, text: "Die Listenführer sagten zu ihnen: Der Herr soll euch erscheinen und euch richten; denn ihr habt uns beim Pharao und seinen Dienern in Verruf gebracht und ihnen ein Schwert in die Hand gegeben, mit dem sie uns umbringen können." },
      { number: 22, text: "Da wandte sich Mose an den Herrn und sagte: Mein Herr, warum behandelst du dieses Volk so schlecht? Wozu hast du mich denn gesandt?" },
      { number: 23, text: "Seit ich zum Pharao gegangen bin, um in deinem Namen zu reden, behandelt er dieses Volk noch schlechter, aber du hast dein Volk nicht gerettet." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Der Herr antwortete Mose: Jetzt wirst du sehen, was ich dem Pharao antue. Denn von starker Hand gezwungen, wird er sie ziehen lassen, ja, von starker Hand gezwungen, wird er sie sogar aus seinem Land ausweisen." },
      { number: 2, text: "Gott redete mit Mose und sprach zu ihm: Ich bin Jahwe." },
      { number: 3, text: "Ich bin Abraham, Isaak und Jakob als El-Schaddai (Gott, der Allmächtige) erschienen, aber unter meinem Namen Jahwe habe ich mich ihnen nicht zu erkennen gegeben." },
      { number: 4, text: "Auch habe ich einen Bund mit ihnen geschlossen und habe versprochen, ihnen das Land Kanaan zu geben, das Land, in dem sie als Fremde lebten." },
      { number: 5, text: "Ferner habe ich gehört, wie die Israeliten darüber stöhnen, dass die Ägypter sie wie Sklaven behandeln. Da habe ich meines Bundes gedacht" },
      { number: 6, text: "und deshalb sag zu den Israeliten: Ich bin Jahwe. Ich führe euch aus dem Frondienst für die Ägypter heraus und rette euch aus der Sklaverei. Ich erlöse euch mit hoch erhobenem Arm und durch ein gewaltiges Strafgericht über sie." },
      { number: 7, text: "Ich nehme euch als mein Volk an und werde euer Gott sein. Und ihr sollt wissen, dass ich Jahwe bin, euer Gott, der euch aus dem Frondienst in Ägypten herausführt." },
      { number: 8, text: "Ich führe euch in das Land, das ich Abraham, Isaak und Jakob unter Eid versprochen habe. Ich übergebe es euch als Eigentum, ich, der Herr." },
      { number: 9, text: "So redete Mose zu den Israeliten. Sie aber hörten nicht auf Mose, weil sie vor harter Arbeit verzagten." },
      { number: 10, text: "Da sprach der Herr zu Mose:" },
      { number: 11, text: "Geh, sag dem Pharao, dem König von Ägypten, er solle die Israeliten aus seinem Land fortziehen lassen." },
      { number: 12, text: "Mose erwiderte dem Herrn: Wenn schon die Israeliten nicht auf mich hörten, wie sollte mich dann der Pharao anhören, zumal ich ungeschickt im Reden bin?" },
      { number: 13, text: "So redete der Herr mit Mose und mit Aaron. Er gab ihnen den Auftrag, zu den Israeliten und zum Pharao, dem König von Ägypten, zu gehen und die Israeliten aus Ägypten herauszuführen." },
      { number: 14, text: "Das sind die Oberhäupter ihrer Großfamilien: Die Söhne Rubens, des Erstgeborenen Israels: Henoch, Pallu, Hezron und Karmi; das waren die Sippenverbände Rubens.", heading: "Die Nachkommen Rubens, Simeons und Levis: 6,14-30", footnotes: ["14f: Wahrscheinlich Rest eines verloren gegangenen Verzeichnisses der Zwölf Stämme Israels."] },
      { number: 15, text: "Die Söhne Simeons: Jemuël, Jamin, Ohad, Jachin, Zohar und Schaul, der Sohn der Kanaaniterin; das waren die Sippenverbände Simeons." },
      { number: 16, text: "Das sind die Namen der Söhne Levis nach ihrer Geschlechterfolge: Gerschon, Kehat und Merari. Die Lebenszeit Levis betrug hundertsiebenunddreißig Jahre." },
      { number: 17, text: "Die Söhne Gerschons: Libni und Schimi, nach ihren Sippenverbänden." },
      { number: 18, text: "Die Söhne Kehats: Amram, Jizhar, Hebron und Usiël. Die Lebenszeit Kehats betrug hundertdreiunddreißig Jahre." },
      { number: 19, text: "Die Söhne Meraris: Machli und Muschi; das waren die Sippenverbände der Leviten nach ihrer Geschlechterfolge." },
      { number: 20, text: "Amram nahm seine Tante Jochebed zur Frau. Sie gebar ihm Aaron und Mose. Die Lebenszeit Amrams betrug hundertsiebenunddreißig Jahre." },
      { number: 21, text: "Die Söhne Jizhars: Korach, Nefeg und Sichri." },
      { number: 22, text: "Die Söhne Usiëls: Mischaël, Elizafan und Sitri." },
      { number: 23, text: "Aaron nahm Elischeba, die Tochter Amminadabs, die Schwester Nachschons, zur Frau. Sie gebar ihm Nadab, Abihu, Eleasar und Itamar." },
      { number: 24, text: "Die Söhne Korachs: Assir, Elkana und Abiasaf. Das waren die Sippenverbände der Korachiter." },
      { number: 25, text: "Eleasar, der Sohn Aarons, nahm eine Tochter Putiëls zur Frau. Sie gebar ihm Pinhas. Das waren die Oberhäupter der levitischen Großfamilien nach ihren Sippenverbänden." },
      { number: 26, text: "Das waren also Aaron und Mose, zu denen der Herr gesagt hatte: Führt die Israeliten aus Ägypten und übernehmt dabei die Leitung der Scharen!" },
      { number: 27, text: "Die beiden waren es, die mit dem Pharao, dem König von Ägypten, reden und die Israeliten aus Ägypten herausführen sollten, Mose und Aaron." },
      { number: 28, text: "Damals, als der Herr mit Mose in Ägypten redete," },
      { number: 29, text: "sagte er zu Mose: Ich bin Jahwe. Sag dem Pharao, dem König von Ägypten, alles, was ich dir auftrage." },
      { number: 30, text: "Mose aber antwortete dem Herrn: Ich bin doch ungeschickt im Reden; wie soll der Pharao auf mich hören?", footnotes: ["4,10; 6,12"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Der Herr sprach zu Mose: Hiermit mache ich dich für den Pharao zum Gott; dein Bruder Aaron soll dein Prophet sein.", heading: "Die ägyptischen Plagen: 7,1 - 11,10", footnotes: ["4,15f", "Wie Gott durch einen Propheten zu Menschen redet, so soll Mose durch seinen Bruder Aaron zum Pharao reden."] },
      { number: 2, text: "Du sollst alles sagen, was ich dir auftrage; dein Bruder Aaron soll es dem Pharao sagen und der Pharao muss die Israeliten aus seinem Land fortziehen lassen." },
      { number: 3, text: "Ich aber will das Herz des Pharao verhärten und dann werde ich meine Zeichen und Wunder in Ägypten häufen." },
      { number: 4, text: "Der Pharao wird nicht auf euch hören. Deshalb werde ich meine Hand auf Ägypten legen und unter gewaltigem Strafgericht meine Scharen, mein Volk, die Israeliten, aus Ägypten führen." },
      { number: 5, text: "Erst wenn ich meine Hand gegen die Ägypter ausstrecke, werden sie erkennen, dass ich der Herr bin, und dann werde ich die Israeliten aus ihrer Mitte herausführen." },
      { number: 6, text: "Mose und Aaron taten, was ihnen der Herr aufgetragen hatte. So machten sie es." },
      { number: 7, text: "Mose war achtzig Jahre und Aaron dreiundachtzig Jahre alt, als sie mit dem Pharao verhandelten." },
      { number: 8, text: "Der Herr sprach zu Mose und Aaron:" },
      { number: 9, text: "Wenn der Pharao zu euch sagt: Tut doch ein Wunder zu eurer Beglaubigung!, dann sag zu Aaron: Nimm deinen Stab und wirf ihn vor den Pharao hin! Er wird zu einer Schlange werden.", footnotes: ["4,1-5"] },
      { number: 10, text: "Als Mose und Aaron zum Pharao kamen, taten sie, was ihnen der Herr aufgetragen hatte: Aaron warf seinen Stab vor den Pharao und seine Diener hin und er wurde zu einer Schlange." },
      { number: 11, text: "Da rief auch der Pharao Weise und Beschwörungspriester und sie, die Wahrsager der Ägypter, taten mit Hilfe ihrer Zauberkunst das Gleiche:" },
      { number: 12, text: "Jeder warf seinen Stab hin und die Stäbe wurden zu Schlangen. Doch Aarons Stab verschlang die Stäbe der Wahrsager." },
      { number: 13, text: "Das Herz des Pharao aber blieb hart und er hörte nicht auf sie. So hatte es der Herr vorausgesagt." },
      { number: 14, text: "Der Herr sprach zu Mose: Das Herz des Pharao ist ungerührt und er ist nicht bereit, das Volk ziehen zu lassen." },
      { number: 15, text: "Geh morgen früh zum Pharao, wenn er an den Fluss hinuntergeht, und tritt am Nilufer vor ihn hin! Den Stab, der sich in eine Schlange verwandelt hat, nimm mit!" },
      { number: 16, text: "Sag zum Pharao: Jahwe, der Gott der Hebräer, hat mich zu dir gesandt und lässt dir sagen: Lass mein Volk ziehen, damit sie mich in der Wüste verehren können. Bis jetzt hast du nicht hören wollen." },
      { number: 17, text: "So spricht Jahwe: Daran sollst du erkennen, dass ich Jahwe bin: Mit dem Stab in meiner Hand schlage ich auf das Wasser im Nil und es wird sich in Blut verwandeln." },
      { number: 18, text: "Die Fische im Nil werden sterben und der Nil wird stinken, sodass sich die Ägypter davor ekeln, Nilwasser zu trinken." },
      { number: 19, text: "Dann sprach der Herr zu Mose: Sag Aaron: Nimm deinen Stab und streck deine Hand über die Gewässer Ägyptens aus, über ihre Flüsse und Nilarme, über ihre Sümpfe und alle Wasserstellen; sie sollen zu Blut werden. Blut soll es geben in ganz Ägypten, in den Gefäßen aus Holz und Stein." },
      { number: 20, text: "Mose und Aaron taten, was ihnen der Herr aufgetragen hatte. Er erhob den Stab und schlug vor den Augen des Pharao und seiner Höflinge auf das Wasser im Nil. Da verwandelte sich alles Nilwasser in Blut." },
      { number: 21, text: "Die Fische im Nil starben und der Nil stank, sodass die Ägypter kein Nilwasser mehr trinken konnten. Das Blut gab es in ganz Ägypten." },
      { number: 22, text: "Doch die Wahrsager der Ägypter taten mit Hilfe ihrer Zauberkunst das Gleiche. Das Herz des Pharao blieb hart und er hörte nicht auf sie. So hatte es der Herr vorausgesagt." },
      { number: 23, text: "Der Pharao kehrte nach Hause zurück und nahm die Sache nicht ernst." },
      { number: 24, text: "Alle Ägypter gruben in der Umgebung des Nil nach Trinkwasser, denn das Nilwasser konnten sie nicht trinken." },
      { number: 25, text: "So vergingen sieben Tage, nachdem der Herr den Nil geschlagen hatte." },
      { number: 26, text: "Dann sprach der Herr zu Mose: Geh zum Pharao und sag ihm: So spricht Jahwe: Lass mein Volk ziehen, damit sie mich verehren können." },
      { number: 27, text: "Weigerst du dich, sie ziehen zu lassen, so bringe ich eine Froschplage über dein ganzes Land." },
      { number: 28, text: "Der Nil wird von Fröschen wimmeln; sie werden heraufkommen und in dein Haus eindringen, in dein Schlafgemach, auf dein Bett werden sie kommen, in die Häuser deiner Diener und deines Volkes, in deine Backöfen und Backschüsseln." },
      { number: 29, text: "Über dich, dein Volk und alle deine Diener werden die Frösche kommen." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Der Herr sprach zu Mose: Sag zu Aaron: Streck deine Hand mit dem Stab aus über die Flüsse, über die Nilarme und die Sümpfe und lass die Frösche über Ägypten kommen!" },
      { number: 2, text: "Aaron streckte seine Hand über die Gewässer Ägyptens aus. Da stiegen die Frösche herauf und bedeckten ganz Ägypten." },
      { number: 3, text: "Doch die Wahrsager taten mit Hilfe ihrer Zauberkunst das Gleiche und ließen die Frösche über Ägypten kommen." },
      { number: 4, text: "Der Pharao rief Mose und Aaron zu sich und sagte: Betet zu Jahwe, er möge mich und mein Volk von den Fröschen befreien. Dann will ich das Volk ziehen lassen und sie können Jahwe Schlachtopfer darbringen." },
      { number: 5, text: "Da sagte Mose zum Pharao: Verfüg über mich! Wann soll ich für dich, deine Diener und dein Volk um Beseitigung der Frösche von dir und aus deinen Häusern beten? Nur im Nil sollen sie erhalten bleiben." },
      { number: 6, text: "Er sagte: Morgen. Mose antwortete: Wie du willst; du sollst erkennen, dass keiner Jahwe, unserem Gott, gleichkommt." },
      { number: 7, text: "Die Frösche werden von dir und deinen Häusern, von deinen Dienern und deinem Volk weichen; nur im Nil werden sie bleiben." },
      { number: 8, text: "Als Mose und Aaron vom Pharao weggegangen waren, schrie Mose zum Herrn um Befreiung von der Froschplage, die er über den Pharao gebracht hatte." },
      { number: 9, text: "Der Herr erfüllte Mose die Bitte und die Frösche in den Häusern, in den Höfen und auf den Feldern starben." },
      { number: 10, text: "Man sammelte sie zu riesigen Haufen und das ganze Land stank davon." },
      { number: 11, text: "Als der Pharao sah, dass die Not vorbei war, verschloss er sein Herz wieder und hörte nicht auf sie. So hatte es der Herr vorausgesagt." },
      { number: 12, text: "Darauf sprach der Herr zu Mose: Sag zu Aaron: Streck deinen Stab aus und schlag damit auf die Erde in den Staub! In ganz Ägypten sollen daraus Stechmücken werden." },
      { number: 13, text: "Sie taten es. Aaron streckte die Hand aus und schlug mit seinem Stab auf die Erde in den Staub. Da wurden Stechmücken daraus, die sich auf Mensch und Vieh setzten. In ganz Ägypten wurden aus dem Staub auf der Erde Stechmücken." },
      { number: 14, text: "Die Wahrsager versuchten mit Hilfe ihrer Zauberkunst ebenfalls Stechmücken hervorzubringen, konnten es aber nicht. Die Stechmücken saßen auf Mensch und Vieh." },
      { number: 15, text: "Da sagten die Wahrsager zum Pharao: Das ist der Finger Gottes. Doch das Herz des Pharao blieb hart und er hörte nicht auf sie. So hatte es der Herr vorausgesagt." },
      { number: 16, text: "Darauf sprach der Herr zu Mose: Steh früh auf, tritt vor den Pharao, wenn er an den Fluss hinuntergeht, und sag zu ihm: So spricht Jahwe: Lass mein Volk ziehen, damit sie mich verehren können." },
      { number: 17, text: "Denn wenn du mein Volk nicht ziehen lässt, lasse ich Ungeziefer auf dich los, auf deine Diener, dein Volk und deine Häuser. Die Häuser in Ägypten werden voll Ungeziefer sein; es wird sogar den Boden, auf dem sie stehen, bedecken." },
      { number: 18, text: "Das Land Goschen aber, in dem mein Volk lebt, will ich an jenem Tag auszeichnen: Dort wird es kein Ungeziefer geben. Daran wirst du erkennen, dass ich, Jahwe, Herr mitten im Land bin." },
      { number: 19, text: "Ich mache einen Unterschied zwischen meinem und deinem Volk. Morgen wird das Zeichen geschehen." },
      { number: 20, text: "Und so tat es der Herr. Ungeziefer kam in Massen über das Haus des Pharao, über das Haus seiner Diener und über ganz Ägypten. Das Land erlitt durch das Ungeziefer schweren Schaden." },
      { number: 21, text: "Da ließ der Pharao Mose und Aaron rufen und sagte: Geht, bringt eurem Gott hier im Land Schlachtopfer dar!" },
      { number: 22, text: "Doch Mose erwiderte: Das können wir nicht. Denn wir müssen dem Herrn, unserem Gott, Schlachtopfer darbringen, die bei den Ägyptern Anstoß erregen. Wenn wir vor ihren Augen Schlachtopfer darbringen, die bei ihnen Anstoß erregen, werden sie uns dann nicht steinigen?" },
      { number: 23, text: "Wir wollen drei Tagesmärsche weit in die Wüste ziehen und dem Herrn, unserem Gott, Schlachtopfer darbringen, wie er es uns gesagt hat." },
      { number: 24, text: "Der Pharao antwortete: Ich lasse euch ziehen. Bringt also Jahwe, eurem Gott, in der Wüste Schlachtopfer dar! Aber zu weit dürft ihr euch nicht entfernen. Betet auch für mich!" },
      { number: 25, text: "Darauf sagte Mose: Gut, ich gehe von dir fort und bete zu Jahwe. Dann wird morgen das Ungeziefer vom Pharao, von seinen Dienern und seinem Volk ablassen. Nur darf der Pharao nicht wieder wortbrüchig werden und das Volk daran hindern, wegzuziehen und Jahwe zu opfern." },
      { number: 26, text: "Mose verließ den Pharao und betete zum Herrn." },
      { number: 27, text: "Der Herr erfüllte Mose die Bitte und befreite den Pharao, seine Diener und sein Volk von dem Ungeziefer; nichts blieb übrig." },
      { number: 28, text: "Der Pharao aber verschloss sein Herz auch diesmal und ließ das Volk nicht ziehen." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Wieder sprach der Herr zu Mose: Geh zum Pharao und sag zu ihm: So spricht Jahwe, der Gott der Hebräer: Lass mein Volk ziehen, damit sie mich verehren können." },
      { number: 2, text: "Wenn du dich weigerst, sie ziehen zu lassen, und sie immer noch festhältst," },
      { number: 3, text: "wird die Hand Jahwes dein Vieh auf dem Feld, die Pferde und Esel, die Kamele und Rinder, die Schafe und Ziegen, überfallen und über sie eine sehr schwere Seuche bringen." },
      { number: 4, text: "Aber Jahwe wird einen Unterschied zwischen dem Vieh Israels und dem Vieh der Ägypter machen; nichts von dem, was den Israeliten gehört, wird eingehen." },
      { number: 5, text: "Auch den Zeitpunkt hat Jahwe schon festgesetzt: Morgen wird Jahwe das im Lande tun." },
      { number: 6, text: "Am folgenden Tag tat es der Herr. Alles Vieh der Ägypter ging ein, vom Vieh der Israeliten aber ging kein einziges Stück ein." },
      { number: 7, text: "Der Pharao erkundigte sich, und wirklich: Vom Vieh Israels war kein einziges Stück eingegangen. Doch der Pharao verschloss sein Herz und ließ das Volk nicht ziehen." },
      { number: 8, text: "Da sprach der Herr zu Mose und Aaron: Holt euch eine Hand voll Ofenruß und Mose soll ihn vor den Augen des Pharao in die Höhe werfen." },
      { number: 9, text: "Er wird als Staub auf ganz Ägypten niedergehen und an Mensch und Vieh Geschwüre mit aufplatzenden Blasen hervorrufen, in ganz Ägypten." },
      { number: 10, text: "Sie holten den Ofenruß, traten vor den Pharao und Mose warf ihn in die Höhe. Da bildeten sich an Mensch und Vieh Geschwüre mit aufplatzenden Blasen." },
      { number: 11, text: "Die Wahrsager konnten wegen der Geschwüre Mose nicht gegenübertreten, sie waren wie alle Ägypter von Geschwüren befallen." },
      { number: 12, text: "Aber der Herr verhärtete das Herz des Pharao, sodass er nicht auf sie hörte. So hatte es der Herr dem Mose vorausgesagt." },
      { number: 13, text: "Darauf sprach der Herr zu Mose: Steh früh am Morgen auf, tritt vor den Pharao hin und sag zu ihm: So spricht Jahwe, der Gott der Hebräer: Lass mein Volk ziehen, damit sie mich verehren können." },
      { number: 14, text: "Denn diesmal will ich alle meine Plagen loslassen auf dich, deine Diener und dein Volk. Daran wirst du erkennen, dass mir keiner im ganzen Land gleichkommt." },
      { number: 15, text: "Denn schon jetzt hätte ich meine Hand ausstrecken und dich und dein Volk mit der Pest schlagen können und du wärst vom Erdboden verschwunden." },
      { number: 16, text: "Ich habe dich aber am Leben gelassen, um meine Macht zu zeigen und meinen Namen auf der ganzen Erde bekannt zu machen." },
      { number: 17, text: "Wenn du dich weiterhin als den großen Herrn über mein Volk aufspielst und sie nicht ziehen lässt," },
      { number: 18, text: "dann lasse ich morgen um diese Zeit ein sehr schweres Hagelwetter niedergehen, wie es in Ägypten seit seiner Gründung bis auf den heutigen Tag noch keines gegeben hat." },
      { number: 19, text: "Und nun schick Leute aus, bring dein Vieh in Sicherheit und alles, was dir auf dem Feld gehört. Auf alle Menschen und auf das Vieh, das auf dem Feld bleibt und nicht unter Dach gebracht wird, geht der Hagel nieder und erschlägt sie." },
      { number: 20, text: "Wer sich von den Dienern des Pharao vor der Drohung des Herrn fürchtete, ließ seine Knechte und sein Vieh unter Dach bringen." },
      { number: 21, text: "Wer aber das Wort des Herrn nicht ernst nahm, ließ seine Knechte und sein Vieh auf dem Feld." },
      { number: 22, text: "Und der Herr sprach zu Mose: Streck deine Hand zum Himmel empor! Dann wird Hagel auf ganz Ägypten niedergehen, auf Mensch und Vieh und auf alle Feldpflanzen in Ägypten." },
      { number: 23, text: "Mose streckte seinen Stab zum Himmel empor und der Herr ließ es donnern und hageln. Blitze fuhren auf die Erde herab und der Herr ließ Hagel über Ägypten niedergehen." },
      { number: 24, text: "Schwerer Hagel prasselte herab und in den sehr schweren Hagel hinein zuckten Blitze. Ähnliches hatte es im ganzen Land der Ägypter noch nicht gegeben, seit sie ein Volk geworden waren." },
      { number: 25, text: "Der Hagel erschlug in ganz Ägypten alles, was auf dem Feld war. Menschen, Vieh und alle Feldpflanzen erschlug der Hagel und alle Feldbäume zerbrach er." },
      { number: 26, text: "Nur in Goschen, wo sich die Israeliten aufhielten, hagelte es nicht." },
      { number: 27, text: "Da ließ der Pharao Mose und Aaron rufen und sagte zu ihnen: Diesmal bekenne ich mich schuldig. Jahwe ist im Recht; ich aber und mein Volk, wir sind im Unrecht." },
      { number: 28, text: "Betet zu Jahwe! Die Donnerstimme Gottes und der Hagel, das ist zu viel. Ich will euch jetzt ziehen lassen; ihr müsst nicht länger bleiben." },
      { number: 29, text: "Mose antwortete ihm: Sobald ich außerhalb der Stadt bin, werde ich meine Hände vor Jahwe ausbreiten; der Donner wird aufhören und es wird kein Hagel mehr fallen. So wirst du erkennen, dass das Land Jahwe gehört." },
      { number: 30, text: "Du und Deine Diener aber, das weiß ich, ihr fürchtet euch noch immer nicht vor dem Gott Jahwe." },
      { number: 31, text: "Der Flachs und die Gerste waren zerschlagen; denn die Gerste hatte gerade Ähren angesetzt und der Flachs stand in Blüte." },
      { number: 32, text: "Der Weizen und der Spelt wurden nicht zerschlagen, denn sie kommen später heraus." },
      { number: 33, text: "Mose verließ den Pharao, ging vor die Stadt hinaus und breitete seine Hände vor dem Herrn aus. Da hörte der Donner auf und kein Hagel und kein Regen fiel mehr auf die Erde." },
      { number: 34, text: "Doch als der Pharao sah, dass Regen, Hagel und Donner aufgehört hatten, blieb er bei seiner Sünde; er und seine Diener verschlossen wieder ihr Herz." },
      { number: 35, text: "Das Herz des Pharao blieb hart und er ließ die Israeliten nicht ziehen. So hatte es der Herr durch Mose vorausgesagt." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Der Herr sprach zu Mose: Geh zum Pharao! Ich habe sein Herz und das Herz seiner Diener verschlossen, damit ich diese Zeichen unter ihnen vollbringen konnte" },
      { number: 2, text: "und damit du deinem Sohn und deinem Enkel erzählen kannst, was ich den Ägyptern angetan und welche Zeichen ich unter ihnen vollbracht habe. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 3, text: "Mose und Aaron gingen zum Pharao und sagten: So spricht Jahwe, der Gott der Hebräer: Wie lange willst du dich noch weigern, dich mir zu unterwerfen? Lass mein Volk ziehen, damit sie mich verehren können." },
      { number: 4, text: "Wenn du dich weigerst, mein Volk ziehen zu lassen, so schicke ich morgen Heuschrecken über dein Land." },
      { number: 5, text: "Sie werden die Oberfläche der Erde bedecken, sodass man den Erdboden nicht mehr sehen kann. Sie werden auch noch das verzehren, was der Hagel verschont hat, und alle Bäume kahl fressen, die auf euren Feldern wachsen." },
      { number: 6, text: "Deine Häuser, die Häuser aller deiner Diener und die aller Ägypter werden voll davon sein. So etwas haben deine Väter und Vorväter bis heute nicht gesehen, seitdem sie in diesem Land leben. Dann wandte sich Mose um und verließ den Pharao." },
      { number: 7, text: "Die Diener sagten zum Pharao: Wie lange soll uns dieser Mann noch Unglück bringen? Lass die Leute ziehen, damit sie Jahwe, ihren Gott, verehren können. Merkst du denn noch immer nicht, dass Ägypten zugrunde geht?" },
      { number: 8, text: "Da holte man Mose und Aaron zum Pharao zurück und er sagte zu ihnen: Geht, verehrt Jahwe, euren Gott! Wer von euch will denn mitgehen?" },
      { number: 9, text: "Mose antwortete: Wir gehen mit Jung und Alt, mit unseren Söhnen und Töchtern; auch die Schafe, Ziegen und Rinder nehmen wir mit. Denn wir feiern ein Jahwefest." },
      { number: 10, text: "Da sagte er zu ihnen: Dann sei Jahwe ebenso wenig mit euch, wie ich euch und eure Kinder ziehen lasse. Seht, ihr habt Böses im Sinn." },
      { number: 11, text: "Nein, nur ihr Männer dürft gehen und Jahwe verehren; denn das habt ihr verlangt. Und man jagte sie vom Pharao weg." },
      { number: 12, text: "Darauf sprach der Herr zu Mose: Streck deine Hand über Ägypten aus! Dann werden Heuschrecken kommen und über Ägypten herfallen. Sie werden den ganzen Pflanzenwuchs des Landes auffressen, alles, was der Hagel verschont hat." },
      { number: 13, text: "Da streckte Mose seinen Stab über Ägypten aus und der Herr schickte den Ostwind in das Land, einen ganzen Tag und eine ganze Nacht lang. Als es Morgen wurde, hatte der Ostwind die Heuschrecken ins Land gebracht." },
      { number: 14, text: "Sie fielen über ganz Ägypten her und ließen sich in Schwärmen auf dem Gebiet von Ägypten nieder. Niemals vorher gab es so viele Heuschrecken wie damals, auch wird es nie wieder so viele geben." },
      { number: 15, text: "Sie bedeckten die Oberfläche des ganzen Landes und das Land war schwarz von ihnen. Sie fraßen allen Pflanzenwuchs des Landes und alle Baumfrüchte auf, die der Hagel verschont hatte, und an den Bäumen und Feldpflanzen in ganz Ägypten blieb nichts Grünes." },
      { number: 16, text: "Da ließ der Pharao Mose und Aaron eiligst rufen und sagte zu ihnen: Ich habe gegen Jahwe, euren Gott, gesündigt und auch gegen euch." },
      { number: 17, text: "Nur noch diesmal nehmt meine Sünde von mir und betet zu Jahwe, eurem Gott, er möge mich wenigstens noch von dieser tödlichen Gefahr befreien." },
      { number: 18, text: "Mose verließ den Pharao wieder und betete zum Herrn." },
      { number: 19, text: "Der Herr ließ den Wind in einen sehr starken Westwind umschlagen, der die Heuschrecken forttrug und ins Schilfmeer warf. Im ganzen Gebiet von Ägypten blieb keine einzige Heuschrecke mehr übrig." },
      { number: 20, text: "Der Herr aber verhärtete das Herz des Pharao, sodass er die Israeliten nicht ziehen ließ." },
      { number: 21, text: "Da sprach der Herr zu Mose: Streck deine Hand zum Himmel aus; dann wird eine Finsternis über Ägypten kommen und es wird stockdunkel werden." },
      { number: 22, text: "Mose streckte seine Hand zum Himmel aus und schon breitete sich tiefe Finsternis über ganz Ägypten aus, drei Tage lang." },
      { number: 23, text: "Man konnte einander nicht sehen und sich nicht von der Stelle rühren, drei Tage lang. Wo aber die Israeliten wohnten, blieb es hell." },
      { number: 24, text: "Da ließ der Pharao Mose rufen und sagte: Geht und verehrt Jahwe! Nur eure Schafe, Ziegen und Rinder sollen bleiben. Eure Kinder dürfen mitziehen." },
      { number: 25, text: "Mose erwiderte: Selbst wenn du uns Schlacht- und Brandopfer mitgäbest, damit wir sie Jahwe, unserem Gott, darbringen," },
      { number: 26, text: "müssten unsere Herden doch mitgehen, keine Klaue darf zurückbleiben. Denn aus unseren Herden nehmen wir das Opfer, mit dem wir Jahwe, unseren Gott, verehren; aber mit welchem Opfertier wir Jahwe verehren sollen, wissen wir nicht, ehe wir dort angekommen sind." },
      { number: 27, text: "Der Herr verhärtete das Herz des Pharao, sodass er sie nicht ziehen lassen wollte." },
      { number: 28, text: "Der Pharao sagte zu Mose: Weg von mir! Hüte dich, mir jemals wieder unter die Augen zu treten. Denn an dem Tag, an dem du mir unter die Augen trittst, musst du sterben." },
      { number: 29, text: "Da sagte Mose: Gut, dein Wort soll gelten. Ich werde dir nie mehr unter die Augen treten." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Da sprach der Herr zu Mose: Noch eine Plage schicke ich dem Pharao und seinem Land. Danach wird er euch von hier wegziehen lassen. Und wenn er euch endlich ziehen lässt, wird er euch sogar fortjagen." },
      { number: 2, text: "Lass unter dem Volk ausrufen, jeder Mann und jede Frau soll sich von dem Nachbarn Geräte aus Silber und Gold erbitten.", footnotes: ["3,22; 12,35f"] },
      { number: 3, text: "Der Herr ließ das Volk bei den Ägyptern Gunst finden. Auch Mose genoss in Ägypten bei den Dienern des Pharao und beim Volk hohes Ansehen." },
      { number: 4, text: "Mose sagte: So spricht Jahwe: Um Mitternacht will ich mitten durch Ägypten gehen." },
      { number: 5, text: "Dann wird jeder Erstgeborene in Ägypten sterben, vom Erstgeborenen des Pharao, der auf dem Thron sitzt, bis zum Erstgeborenen der Magd an der Handmühle und bis zu den Erstlingen unter dem Vieh.", footnotes: ["4,23"] },
      { number: 6, text: "Geschrei wird sich in ganz Ägypten erheben, so groß, wie es keines je gegeben hat oder geben wird." },
      { number: 7, text: "Doch gegen keinen der Israeliten wird auch nur ein Hund die Zähne fletschen, weder gegen Mensch noch Vieh; denn ihr sollt wissen, dass Jahwe zwischen Ägypten und Israel einen Unterschied macht." },
      { number: 8, text: "Dann werden alle deine Diener hier zu mir herabsteigen, sich vor mir niederwerfen und sagen: Zieht doch fort, du und das ganze Volk, das du anführst. Danach werde ich fortziehen. Er verließ den Pharao, rot vor Zorn." },
      { number: 9, text: "Der Herr sprach zu Mose: Der Pharao hört nicht auf euch; denn ich will viele Wunder in Ägypten vollbringen." },
      { number: 10, text: "Mose und Aaron vollbrachten alle diese Wunder vor den Augen des Pharao, aber der Herr verhärtete das Herz des Pharao, sodass er die Israeliten nicht aus seinem Land fortziehen ließ." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Der Herr sprach zu Mose und Aaron in Ägypten:", heading: "Der Auszug aus Ägypten: 12,1 - 18,27 | Das Pascha: 12,1 - 13,16", footnotes: ["1-13,16: Pascha und das Fest der ungesäuerten Brote waren wohl ursprünglich verschiedene Feste: Pascha ein Hirtenfest und Ungesäuerte Brote ein Bauernfest. In Kanaan sind sie zusammengefallen. Das Pascha wurde in den einzelnen Familien gefeiert. Ein Zusatz zu Dtn 16,1-4 in den VV. 5-8 ordnet eine gemeinsame Feier im Zentralheiligtum an. Später schlachten die Priester im Tempel zu Jerusalem die Paschalämmer, die dann von den einzelnen Wallfahrergruppen beim Paschamahl gegessen werden. So feiert auch Jesus das Paschamahl mit seinen Jüngern in Jerusalem."] },
      { number: 2, text: "Dieser Monat soll die Reihe eurer Monate eröffnen, er soll euch als der erste unter den Monaten des Jahres gelten." },
      { number: 3, text: "Sagt der ganzen Gemeinde Israel: Am Zehnten dieses Monats soll jeder ein Lamm für seine Familie holen, ein Lamm für jedes Haus." },
      { number: 4, text: "Ist die Hausgemeinschaft für ein Lamm zu klein, so nehme er es zusammen mit dem Nachbarn, der seinem Haus am nächsten wohnt, nach der Anzahl der Personen. Bei der Aufteilung des Lammes müsst ihr berücksichtigen, wie viel der Einzelne essen kann." },
      { number: 5, text: "Nur ein fehlerfreies, männliches, einjähriges Lamm darf es sein, das Junge eines Schafes oder einer Ziege müsst ihr nehmen." },
      { number: 6, text: "Ihr sollt es bis zum vierzehnten Tag dieses Monats aufbewahren. Gegen Abend soll die ganze versammelte Gemeinde Israel die Lämmer schlachten." },
      { number: 7, text: "Man nehme etwas von dem Blut und bestreiche damit die beiden Türpfosten und den Türsturz an den Häusern, in denen man das Lamm essen will." },
      { number: 8, text: "Noch in der gleichen Nacht soll man das Fleisch essen. Über dem Feuer gebraten und zusammen mit ungesäuertem Brot und Bitterkräutern soll man es essen." },
      { number: 9, text: "Nichts davon dürft ihr roh oder in Wasser gekocht essen, sondern es muss über dem Feuer gebraten sein. Kopf und Beine dürfen noch nicht vom Rumpf getrennt sein." },
      { number: 10, text: "Ihr dürft nichts bis zum Morgen übrig lassen. Wenn aber am Morgen noch etwas übrig ist, dann verbrennt es im Feuer!" },
      { number: 11, text: "So aber sollt ihr es essen: eure Hüften gegürtet, Schuhe an den Füßen, den Stab in der Hand. Esst es hastig! Es ist die Paschafeier für den Herrn." },
      { number: 12, text: "In dieser Nacht gehe ich durch Ägypten und erschlage in Ägypten jeden Erstgeborenen bei Mensch und Vieh. Über alle Götter Ägyptens halte ich Gericht, ich, der Herr." },
      { number: 13, text: "Das Blut an den Häusern, in denen ihr wohnt, soll ein Zeichen zu eurem Schutz sein. Wenn ich das Blut sehe, werde ich an euch vorübergehen und das vernichtende Unheil wird euch nicht treffen, wenn ich in Ägypten dreinschlage." },
      { number: 14, text: "Diesen Tag sollt ihr als Gedenktag begehen. Feiert ihn als Fest zur Ehre des Herrn! Für die kommenden Generationen macht euch diese Feier zur festen Regel!" },
      { number: 15, text: "Sieben Tage lang sollt ihr ungesäuertes Brot essen. Gleich am ersten Tag schafft den Sauerteig aus euren Häusern! Denn jeder, der zwischen dem ersten und dem siebten Tag Gesäuertes isst, soll aus Israel ausgemerzt werden." },
      { number: 16, text: "Am ersten Tag sollt ihr eine heilige Versammlung einberufen und ebenso eine heilige Versammlung am siebten Tag. An diesen beiden Tagen darf man keinerlei Arbeit tun. Nur das, was jeder zum Essen braucht, dürft ihr zubereiten." },
      { number: 17, text: "Begeht das Fest der ungesäuerten Brote! Denn gerade an diesem Tag habe ich eure Scharen aus Ägypten herausgeführt. Begeht diesen Tag in allen kommenden Generationen; das sei für euch eine feste Regel." },
      { number: 18, text: "Im ersten Monat, vom Abend des vierzehnten Tags bis zum Abend des einundzwanzigsten Tags, esst ungesäuerte Brote!" },
      { number: 19, text: "Sieben Tage lang darf sich in euren Häusern kein Sauerteig befinden; denn jeder, der Gesäuertes isst, sei er fremd oder einheimisch, soll aus der Gemeinde Israel ausgemerzt werden." },
      { number: 20, text: "Esst also nichts Gesäuertes! Überall, wo ihr wohnt, sollt ihr ungesäuerte Brote essen." },
      { number: 21, text: "Da rief Mose alle Ältesten Israels zusammen und sagte zu ihnen: Holt Schafe oder Ziegen für eure Sippenverbände herbei und schlachtet das Paschalamm!" },
      { number: 22, text: "Dann nehmt einen Ysopzweig, taucht ihn in die Schüssel mit Blut und streicht etwas von dem Blut in der Schüssel auf den Türsturz und auf die beiden Türpfosten! Bis zum Morgen darf niemand von euch das Haus verlassen." },
      { number: 23, text: "Der Herr geht umher, um die Ägypter mit Unheil zu schlagen. Wenn er das Blut am Türsturz und an den beiden Türpfosten sieht, wird er an der Tür vorübergehen und dem Vernichter nicht erlauben, in eure Häuser einzudringen und euch zu schlagen." },
      { number: 24, text: "Haltet euch an diese Anordnung! Sie gelte dir und deinen Nachkommen als feste Regel." },
      { number: 25, text: "Wenn ihr in das Land kommt, das euch der Herr gibt, wie er gesagt hat, so begeht diese Feier!" },
      { number: 26, text: "Und wenn euch eure Söhne fragen: Was bedeutet diese Feier?," },
      { number: 27, text: "dann sagt: Es ist das Pascha-Opfer zur Ehre des Herrn, der in Ägypten an den Häusern der Israeliten vorüberging, als er die Ägypter mit Unheil schlug, unsere Häuser aber verschonte. Das Volk verneigte sich und warf sich nieder." },
      { number: 28, text: "Dann gingen die Israeliten und taten, was der Herr Mose und Aaron befohlen hatte. So machten sie es." },
      { number: 29, text: "Es war Mitternacht, als der Herr alle Erstgeborenen in Ägypten erschlug, vom Erstgeborenen des Pharao, der auf dem Thron saß, bis zum Erstgeborenen des Gefangenen im Kerker, und jede Erstgeburt beim Vieh." },
      { number: 30, text: "Da standen der Pharao, alle seine Diener und alle Ägypter noch in der Nacht auf und großes Wehgeschrei erhob sich bei den Ägyptern; denn es gab kein Haus, in dem nicht ein Toter war." },
      { number: 31, text: "Der Pharao ließ Mose und Aaron noch in der Nacht rufen und sagte: Auf, verlasst mein Volk, ihr beide und die Israeliten! Geht und verehrt Jahwe, wie ihr gesagt habt." },
      { number: 32, text: "Auch eure Schafe, Ziegen und Rinder nehmt mit, wie ihr gesagt habt. Geht und betet auch für mich!" },
      { number: 33, text: "Die Ägypter drängten das Volk, eiligst das Land zu verlassen, denn sie sagten: Sonst kommen wir noch alle um." },
      { number: 34, text: "Das Volk nahm den Brotteig ungesäuert mit; sie wickelten ihre Backschüsseln in Kleider ein und luden sie sich auf die Schultern." },
      { number: 35, text: "Die Israeliten taten, was Mose gesagt hatte. Sie erbaten von den Ägyptern Geräte aus Silber und Gold und auch Gewänder.", footnotes: ["(35f) 3,22; 11,2f; Ps 105,37"] },
      { number: 36, text: "Der Herr ließ das Volk bei den Ägyptern Gunst finden, sodass sie auf ihre Bitte eingingen. Auf diese Weise plünderten sie die Ägypter aus." },
      { number: 37, text: "Die Israeliten brachen von Ramses nach Sukkot auf. Es waren an die sechshunderttausend Mann zu Fuß, nicht gerechnet die Kinder.", footnotes: ["Die Zahl von 600000 Männern würde etwa 3 Millionen Personen voraussetzen. Wahrscheinlich handelt es sich um eine symbolische Zahl, deren Bedeutung uns nicht mehr bekannt ist."] },
      { number: 38, text: "Auch ein großer Haufen anderer Leute zog mit, dazu Schafe, Ziegen und Rinder, eine sehr große Menge Vieh." },
      { number: 39, text: "Aus dem Teig, den sie aus Ägypten mitgebracht hatten, backten sie ungesäuerte Brotfladen; denn der Teig war nicht durchsäuert, weil sie aus Ägypten verjagt worden waren und nicht einmal Zeit hatten, für Reiseverpflegung zu sorgen." },
      { number: 40, text: "Der Aufenthalt der Israeliten in Ägypten dauerte vierhundertdreißig Jahre." },
      { number: 41, text: "Nach Ablauf der vierhundertdreißig Jahre, genau an jenem Tag, zogen alle Scharen des Herrn aus Ägypten fort." },
      { number: 42, text: "Eine Nacht des Wachens war es für den Herrn, als er sie aus Ägypten herausführte. Als eine Nacht des Wachens zur Ehre des Herrn gilt sie den Israeliten in allen Generationen." },
      { number: 43, text: "Der Herr sprach zu Mose und Aaron: Folgende Regel gilt für das Pascha: Kein Fremder darf davon essen;" },
      { number: 44, text: "aber jeder Sklave, den du für Geld gekauft hast, darf davon essen, sobald du ihn beschnitten hast." },
      { number: 45, text: "Halbbürger und Lohnarbeiter dürfen nicht davon essen." },
      { number: 46, text: "In einem Haus muss man es essen. Trag nichts vom Fleisch aus dem Haus! Und ihr sollt keinen Knochen des Paschalammes zerbrechen." },
      { number: 47, text: "Die ganze Gemeinde Israel soll es so halten." },
      { number: 48, text: "Lebt bei dir jemand als Fremder, der das Pascha zur Ehre des Herrn feiern will, so muss er alle männlichen Angehörigen beschneiden lassen; dann darf er sich am Pascha beteiligen. Er gilt dann wie ein Einheimischer. Doch kein Unbeschnittener darf davon essen." },
      { number: 49, text: "Für Einheimische und für Fremde, die dauernd bei euch leben, gilt das gleiche Gesetz." },
      { number: 50, text: "Alle Israeliten taten, was der Herr Mose und Aaron aufgetragen hatte. So machten sie es." },
      { number: 51, text: "Genau an jenem Tag führte der Herr die Israeliten aus Ägypten heraus, an der Spitze ihrer Scharen." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:" },
      { number: 2, text: "Erkläre alle Erstgeburt als mir geheiligt! Alles, was bei den Israeliten den Mutterschoß durchbricht, bei Mensch und Vieh, gehört mir." },
      { number: 3, text: "Mose sagte zum Volk: Denkt an diesen Tag, an dem ihr aus Ägypten, dem Sklavenhaus, fortgezogen seid; denn mit starker Hand hat euch der Herr von dort herausgeführt. Nichts Gesäuertes soll man essen." },
      { number: 4, text: "Heute im Monat Abib seid ihr weggezogen." },
      { number: 5, text: "Wenn dich der Herr in das Land der Kanaaniter, Hetiter, Amoriter, Hiwiter und Jebusiter geführt hat - er hat deinen Vätern mit einem Eid zugesichert, dir das Land zu geben, wo Milch und Honig fließen -, begeh die Feier in diesem Monat!" },
      { number: 6, text: "Sieben Tage sollst du ungesäuerte Brote essen, am siebten Tag ist ein Fest zur Ehre des Herrn." },
      { number: 7, text: "Ungesäuerte Brote soll man sieben Tage lang essen. Nichts Gesäuertes soll man bei dir sehen und kein Sauerteig soll in deinem ganzen Gebiet zu finden sein." },
      { number: 8, text: "An diesem Tag erzähl deinem Sohn: Das geschieht für das, was der Herr an mir getan hat, als ich aus Ägypten auszog." },
      { number: 9, text: "Es sei dir ein Zeichen an der Hand und ein Erinnerungsmal an der Stirn, damit das Gesetz des Herrn in deinem Mund sei. Denn mit starker Hand hat dich der Herr aus Ägypten herausgeführt." },
      { number: 10, text: "Halte dich an diese Regel, Jahr für Jahr, zur festgesetzten Zeit!" },
      { number: 11, text: "Der Herr wird dich in das Land der Kanaaniter bringen und wird es dir geben, wie er dir und deinen Vätern mit einem Eid zugesichert hat." },
      { number: 12, text: "Dann musst du alles, was den Mutterschoß durchbricht, vor den Herrn bringen; alle männlichen Erstlinge, die dein Vieh wirft, gehören dem Herrn." },
      { number: 13, text: "Jeden Erstling vom Esel aber löse durch ein Schaf aus! Willst du ihn nicht auslösen, dann brich ihm das Genick! Jeden Erstgeborenen deiner Söhne musst du auslösen." },
      { number: 14, text: "Wenn dich morgen dein Sohn fragt: Was bedeutet das?, dann sag ihm: Mit starker Hand hat uns der Herr aus Ägypten, aus dem Sklavenhaus, herausgeführt." },
      { number: 15, text: "Als der Pharao hart blieb und uns nicht ziehen ließ, erschlug der Herr alle Erstgeborenen in Ägypten, bei Mensch und Vieh. Darum opfere ich dem Herrn alle männlichen Tiere, die den Mutterschoß durchbrechen; alle Erstgeborenen meiner Söhne aber löse ich aus." },
      { number: 16, text: "Das sei dir ein Zeichen an deiner Hand und ein Schmuck auf deiner Stirn; denn mit starker Hand hat uns der Herr aus Ägypten herausgeführt." },
      { number: 17, text: "Als der Pharao das Volk ziehen ließ, führte sie Gott nicht den Weg ins Philisterland, obwohl er der kürzere war. Denn Gott sagte: Die Leute könnten es sonst, wenn sie Krieg erleben, bereuen und nach Ägypten zurückkehren wollen.", heading: "Die Rettung am Schilfmeer: 13,17 - 14,31" },
      { number: 18, text: "So ließ sie Gott einen Umweg machen, der durch die Wüste zum Schilfmeer führte. Geordnet zogen die Israeliten aus Ägypten hinauf." },
      { number: 19, text: "Mose nahm die Gebeine Josefs mit; denn dieser hatte die Söhne Israels beschworen: Wenn Gott sich euer annimmt, dann nehmt meine Gebeine von hier mit hinauf!", footnotes: ["Gen 50,25"] },
      { number: 20, text: "Sie brachen von Sukkot auf und schlugen ihr Lager in Etam am Rand der Wüste auf." },
      { number: 21, text: "Der Herr zog vor ihnen her, bei Tag in einer Wolkensäule, um ihnen den Weg zu zeigen, bei Nacht in einer Feuersäule, um ihnen zu leuchten. So konnten sie Tag und Nacht unterwegs sein." },
      { number: 22, text: "Die Wolkensäule wich bei Tag nicht von der Spitze des Volkes und die Feuersäule nicht bei Nacht." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:" },
      { number: 2, text: "Sag den Israeliten, sie sollen umkehren und vor Pi-Hahirot zwischen Migdol und dem Meer ihr Lager aufschlagen. Gegenüber von Baal-Zefon sollt ihr am Meer das Lager aufschlagen." },
      { number: 3, text: "Dann denkt der Pharao: Die Israeliten haben sich im Land verlaufen, die Wüste hat sie eingeschlossen." },
      { number: 4, text: "Ich will das Herz des Pharao verhärten, sodass er ihnen nachjagt; dann will ich am Pharao und an seiner ganzen Streitmacht meine Herrlichkeit erweisen und die Ägypter sollen erkennen, dass ich der Herr bin. Und so taten sie es." },
      { number: 5, text: "Als man dem König von Ägypten meldete, das Volk sei geflohen, änderten der Pharao und seine Diener ihre Meinung über das Volk und sagten: Wie konnten wir nur Israel aus unserem Dienst entlassen!" },
      { number: 6, text: "Er ließ seinen Streitwagen anspannen und nahm seine Leute mit." },
      { number: 7, text: "Sechshundert auserlesene Streitwagen nahm er mit und alle anderen Streitwagen der Ägypter und drei Mann auf jedem Wagen." },
      { number: 8, text: "Der Herr verhärtete das Herz des Pharao, des Königs von Ägypten, sodass er den Israeliten nachjagte, während sie voll Zuversicht weiterzogen." },
      { number: 9, text: "Die Ägypter jagten mit allen Pferden und Streitwagen des Pharao, mit seiner Reiterei und seiner Streitmacht hinter ihnen her und holten sie ein, als sie gerade am Meer lagerten. Es war bei Pi-Hahirot vor Baal-Zefon." },
      { number: 10, text: "Als der Pharao sich näherte, blickten die Israeliten auf und sahen plötzlich die Ägypter von hinten anrücken. Da erschraken die Israeliten sehr und schrien zum Herrn." },
      { number: 11, text: "Zu Mose sagten sie: Gab es denn keine Gräber in Ägypten, dass du uns zum Sterben in die Wüste holst? Was hast du uns da angetan? Warum hast du uns aus Ägypten herausgeführt?" },
      { number: 12, text: "Haben wir dir in Ägypten nicht gleich gesagt: Lass uns in Ruhe! Wir wollen Sklaven der Ägypter bleiben; denn es ist für uns immer noch besser, Sklaven der Ägypter zu sein, als in der Wüste zu sterben." },
      { number: 13, text: "Mose aber sagte zum Volk: Fürchtet euch nicht! Bleibt stehen und schaut zu, wie der Herr euch heute rettet. Wie ihr die Ägypter heute seht, so seht ihr sie niemals wieder." },
      { number: 14, text: "Der Herr kämpft für euch, ihr aber könnt ruhig abwarten." },
      { number: 15, text: "Der Herr sprach zu Mose: Was schreist du zu mir? Sag den Israeliten, sie sollen aufbrechen." },
      { number: 16, text: "Und du heb deinen Stab hoch, streck deine Hand über das Meer und spalte es, damit die Israeliten auf trockenem Boden in das Meer hineinziehen können." },
      { number: 17, text: "Ich aber will das Herz der Ägypter verhärten, damit sie hinter ihnen hineinziehen. So will ich am Pharao und an seiner ganzen Streitmacht, an seinen Streitwagen und Reitern meine Herrlichkeit erweisen." },
      { number: 18, text: "Die Ägypter sollen erkennen, dass ich der Herr bin, wenn ich am Pharao, an seinen Streitwagen und Reitern meine Herrlichkeit erweise." },
      { number: 19, text: "Der Engel Gottes, der den Zug der Israeliten anführte, erhob sich und ging an das Ende des Zuges und die Wolkensäule vor ihnen erhob sich und trat an das Ende." },
      { number: 20, text: "Sie kam zwischen das Lager der Ägypter und das Lager der Israeliten. Die Wolke war da und Finsternis und Blitze erhellten die Nacht. So kamen sie die ganze Nacht einander nicht näher.", footnotes: ["H unklar, darum Übersetzung unsicher."] },
      { number: 21, text: "Mose streckte seine Hand über das Meer aus und der Herr trieb die ganze Nacht das Meer durch einen starken Ostwind fort. Er ließ das Meer austrocknen und das Wasser spaltete sich." },
      { number: 22, text: "Die Israeliten zogen auf trockenem Boden ins Meer hinein, während rechts und links von ihnen das Wasser wie eine Mauer stand." },
      { number: 23, text: "Die Ägypter setzten ihnen nach; alle Pferde des Pharao, seine Streitwagen und Reiter zogen hinter ihnen ins Meer hinein." },
      { number: 24, text: "Um die Zeit der Morgenwache blickte der Herr aus der Feuer- und Wolkensäule auf das Lager der Ägypter und brachte es in Verwirrung." },
      { number: 25, text: "Er hemmte die Räder an ihren Wagen und ließ sie nur schwer vorankommen. Da sagte der Ägypter: Ich muss vor Israel fliehen; denn Jahwe kämpft auf ihrer Seite gegen Ägypten." },
      { number: 26, text: "Darauf sprach der Herr zu Mose: Streck deine Hand über das Meer, damit das Wasser zurückflutet und den Ägypter, seine Wagen und Reiter, zudeckt." },
      { number: 27, text: "Mose streckte seine Hand über das Meer und gegen Morgen flutete das Meer an seinen alten Platz zurück, während die Ägypter auf der Flucht ihm entgegenliefen. So trieb der Herr die Ägypter mitten ins Meer." },
      { number: 28, text: "Das Wasser kehrte zurück und bedeckte Wagen und Reiter, die ganze Streitmacht des Pharao, die den Israeliten ins Meer nachgezogen war. Nicht ein Einziger von ihnen blieb übrig." },
      { number: 29, text: "Die Israeliten aber waren auf trockenem Boden mitten durch das Meer gezogen, während rechts und links von ihnen das Wasser wie eine Mauer stand." },
      { number: 30, text: "So rettete der Herr an jenem Tag Israel aus der Hand der Ägypter. Israel sah die Ägypter tot am Strand liegen." },
      { number: 31, text: "Als Israel sah, dass der Herr mit mächtiger Hand an den Ägyptern gehandelt hatte, fürchtete das Volk den Herrn. Sie glaubten an den Herrn und an Mose, seinen Knecht." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Damals sang Mose mit den Israeliten dem Herrn dieses Lied; sie sagten: Ich singe dem Herrn ein Lied, / denn er ist hoch und erhaben. / Rosse und Wagen warf er ins Meer.", heading: "Das Lied des Mose: 15,1-21" },
      { number: 2, text: "Meine Stärke und mein Lied ist der Herr, / er ist für mich zum Retter geworden. / Er ist mein Gott, ihn will ich preisen; / den Gott meines Vaters will ich rühmen.", footnotes: ["Ps 118,14; Jes 12,2"] },
      { number: 3, text: "Der Herr ist ein Krieger, / Jahwe ist sein Name." },
      { number: 4, text: "Pharaos Wagen und seine Streitmacht / warf er ins Meer. / Seine besten Kämpfer versanken im Schilfmeer." },
      { number: 5, text: "Fluten deckten sie zu, / sie sanken in die Tiefe wie Steine." },
      { number: 6, text: "Deine Rechte, Herr, ist herrlich an Stärke; / deine Rechte, Herr, zerschmettert den Feind." },
      { number: 7, text: "In deiner erhabenen Größe / wirfst du die Gegner zu Boden. / Du sendest deinen Zorn; / er frisst sie wie Stoppeln." },
      { number: 8, text: "Du schnaubtest vor Zorn, / da türmte sich Wasser, / da standen Wogen als Wall, / Fluten erstarrten im Herzen des Meeres." },
      { number: 9, text: "Da sagte der Feind: Ich jage nach, hole ein. / Ich teile die Beute, ich stille die Gier. / Ich zücke mein Schwert, meine Hand jagt sie davon." },
      { number: 10, text: "Da schnaubtest du Sturm. Das Meer deckte sie zu. / Sie sanken wie Blei ins tosende Wasser." },
      { number: 11, text: "Wer ist wie du unter den Göttern, o Herr? / Wer ist wie du gewaltig und heilig, / gepriesen als furchtbar, Wunder vollbringend?" },
      { number: 12, text: "Du strecktest deine Rechte aus, / da verschlang sie die Erde." },
      { number: 13, text: "Du lenktest in deiner Güte / das Volk, das du erlöst hast, / du führtest sie machtvoll / zu deiner heiligen Wohnung." },
      { number: 14, text: "Als die Völker das hörten, erzitterten sie, / die Philister packte das Schütteln." },
      { number: 15, text: "Damals erschraken die Häuptlinge Edoms, / die Mächtigen von Moab packte das Zittern, / Kanaans Bewohner, sie alle verzagten." },
      { number: 16, text: "Schrecken und Furcht überfiel sie, / sie erstarrten zu Stein / vor der Macht deines Arms, / bis hindurchzog, o Herr, dein Volk, / bis hindurchzog das Volk, das du erschufst." },
      { number: 17, text: "Du brachtest sie hin / und pflanztest sie ein / auf dem Berg deines Erbes. / Einen Ort, wo du thronst, Herr, / hast du gemacht; / ein Heiligtum, Herr, haben deine Hände gegründet.", footnotes: ["Der «Berg deines Erbes» ist wahrscheinlich der Zion in Jerusalem. Demnach dürfte das Lied erst im Tempelgottesdienst von Jerusalem seine heutige Gestalt erhalten haben."] },
      { number: 18, text: "Der Herr ist König für immer und ewig." },
      { number: 19, text: "Denn als die Rosse des Pharao mit Wagen und Reitern ins Meer zogen, ließ der Herr das Wasser des Meeres auf sie zurückfluten, nachdem die Israeliten auf trockenem Boden mitten durchs Meer gezogen waren." },
      { number: 20, text: "Die Prophetin Mirjam, die Schwester Aarons, nahm die Pauke in die Hand und alle Frauen zogen mit Paukenschlag und Tanz hinter ihr her." },
      { number: 21, text: "Mirjam sang ihnen vor: Singt dem Herrn ein Lied, / denn er ist hoch und erhaben! / Rosse und Wagen warf er ins Meer." },
      { number: 22, text: "Mose ließ Israel vom Schilfmeer aufbrechen und sie zogen zur Wüste Schur weiter. Drei Tage waren sie in der Wüste unterwegs und fanden kein Wasser.", heading: "Auf dem Weg zum Sinai: 15,22 - 17,16" },
      { number: 23, text: "Als sie nach Mara kamen, konnten sie das Wasser von Mara nicht trinken, weil es bitter war. Deshalb nannte man es Mara (Bitterbrunn)." },
      { number: 24, text: "Da murrte das Volk gegen Mose und sagte: Was sollen wir trinken?" },
      { number: 25, text: "Er schrie zum Herrn und der Herr zeigte ihm ein Stück Holz. Als er es ins Wasser warf, wurde das Wasser süß. Dort gab Gott dem Volk Gesetz und Rechtsentscheidungen und dort stellte er es auf die Probe." },
      { number: 26, text: "Er sagte: Wenn du auf die Stimme des Herrn, deines Gottes, hörst und tust, was in seinen Augen gut ist, wenn du seinen Geboten gehorchst und auf alle seine Gesetze achtest, werde ich dir keine der Krankheiten schicken, die ich den Ägyptern geschickt habe. Denn ich bin der Herr, dein Arzt." },
      { number: 27, text: "Dann kamen sie nach Elim. Dort gab es zwölf Quellen und siebzig Palmen; dort am Wasser schlugen sie ihr Lager auf." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Die ganze Gemeinde der Israeliten brach von Elim auf und kam in die Wüste Sin, die zwischen Elim und dem Sinai liegt. Es war der fünfzehnte Tag des zweiten Monats nach ihrem Auszug aus Ägypten." },
      { number: 2, text: "Die ganze Gemeinde der Israeliten murrte in der Wüste gegen Mose und Aaron." },
      { number: 3, text: "Die Israeliten sagten zu ihnen: Wären wir doch in Ägypten durch die Hand des Herrn gestorben, als wir an den Fleischtöpfen saßen und Brot genug zu essen hatten. Ihr habt uns nur deshalb in diese Wüste geführt, um alle, die hier versammelt sind, an Hunger sterben zu lassen." },
      { number: 4, text: "Da sprach der Herr zu Mose: Ich will euch Brot vom Himmel regnen lassen. Das Volk soll hinausgehen, um seinen täglichen Bedarf zu sammeln. Ich will es prüfen, ob es nach meiner Weisung lebt oder nicht." },
      { number: 5, text: "Wenn sie am sechsten Tag feststellen, was sie zusammengebracht haben, wird es doppelt so viel sein, wie sie sonst täglich gesammelt haben." },
      { number: 6, text: "Da sagten Mose und Aaron zu allen Israeliten: Heute Abend sollt ihr erfahren, dass der Herr euch aus Ägypten geführt hat," },
      { number: 7, text: "und morgen werdet ihr die Herrlichkeit des Herrn schauen; denn er hat euer Murren gegen ihn gehört. Aber wer sind schon wir, dass ihr gegen uns murrt?" },
      { number: 8, text: "Weiter sagte Mose: Wenn der Herr euch heute Abend Fleisch zu essen gibt und euch am Morgen mit Brot sättigt, wenn er also euer Murren hört, mit dem ihr ihn bedrängt, was sind wir dann? Nicht uns galt euer Murren, sondern dem Herrn.", footnotes: ["Num 11,31-33; Ps 78,26-29; Weish 19,11f"] },
      { number: 9, text: "Dann sagte Mose zu Aaron: Sag der ganzen Gemeinde der Israeliten: Tretet hin vor den Herrn; denn er hat euer Murren gehört." },
      { number: 10, text: "Während Aaron zur ganzen Gemeinde der Israeliten sprach, wandten sie sich zur Wüste hin. Da erschien plötzlich in der Wolke die Herrlichkeit des Herrn." },
      { number: 11, text: "Der Herr sprach zu Mose:" },
      { number: 12, text: "Ich habe das Murren der Israeliten gehört. Sag ihnen: Am Abend werdet ihr Fleisch zu essen haben, am Morgen werdet ihr satt sein von Brot und ihr werdet erkennen, dass ich der Herr, euer Gott, bin." },
      { number: 13, text: "Am Abend kamen die Wachteln und bedeckten das Lager. Am Morgen lag eine Schicht von Tau rings um das Lager." },
      { number: 14, text: "Als sich die Tauschicht gehoben hatte, lag auf dem Wüstenboden etwas Feines, Knuspriges, fein wie Reif, auf der Erde." },
      { number: 15, text: "Als das die Israeliten sahen, sagten sie zueinander: Was ist das? Denn sie wussten nicht, was es war. Da sagte Mose zu ihnen: Das ist das Brot, das der Herr euch zu essen gibt.", footnotes: ["16,4; Num 11,7-9; Dtn 8,3.16; Ps 78,24; Weish 16,20-28"] },
      { number: 16, text: "Das ordnet der Herr an: Sammelt davon so viel, wie jeder zum Essen braucht, ein Gomer je Kopf. Jeder darf so viel Gomer holen, wie Personen im Zelt sind." },
      { number: 17, text: "Die Israeliten taten es und sammelten ein, der eine viel, der andere wenig." },
      { number: 18, text: "Als sie die Gomer zählten, hatte keiner, der viel gesammelt hatte, zu viel und keiner, der wenig gesammelt hatte, zu wenig. Jeder hatte so viel gesammelt, wie er zum Essen brauchte." },
      { number: 19, text: "Mose sagte zu ihnen: Davon darf bis zum Morgen niemand etwas übrig lassen." },
      { number: 20, text: "Doch sie hörten nicht auf Mose, sondern einige ließen etwas bis zum Morgen übrig. Aber es wurde wurmig und stank. Da geriet Mose in Zorn über sie." },
      { number: 21, text: "Sie sammelten es Morgen für Morgen, jeder so viel, wie er zum Essen brauchte. Sobald die Sonnenhitze einsetzte, zerging es." },
      { number: 22, text: "Am sechsten Tag sammelten sie die doppelte Menge Brot, zwei Gomer für jeden. Da kamen alle Sippenhäupter der Gemeinde und berichteten es Mose." },
      { number: 23, text: "Er sagte zu ihnen: Es ist so, wie der Herr gesagt hat: Morgen ist Feiertag, heiliger Sabbat zur Ehre des Herrn. Backt, was ihr backen wollt, und kocht, was ihr kochen wollt, den Rest bewahrt bis morgen früh auf!" },
      { number: 24, text: "Sie bewahrten es also bis zum Morgen auf, wie es Mose angeordnet hatte, und es faulte nicht, noch wurde es madig." },
      { number: 25, text: "Da sagte Mose: Esst es heute, denn heute ist Sabbat zur Ehre des Herrn. Heute findet ihr draußen nichts." },
      { number: 26, text: "Sechs Tage dürft ihr es sammeln, am siebten Tag ist Sabbat; da findet ihr nichts." },
      { number: 27, text: "Am siebten Tag gingen trotzdem einige vom Volk hinaus, um zu sammeln, fanden aber nichts." },
      { number: 28, text: "Da sprach der Herr zu Mose: Wie lange wollt ihr euch noch weigern, meine Gebote und Weisungen zu befolgen?" },
      { number: 29, text: "Ihr seht, der Herr hat euch den Sabbat gegeben; daher gibt er auch am sechsten Tag Brot für zwei Tage. Jeder bleibe, wo er ist. Am siebten Tag verlasse niemand seinen Platz." },
      { number: 30, text: "Das Volk ruhte also am siebten Tag." },
      { number: 31, text: "Das Haus Israel nannte das Brot Manna. Es war weiß wie Koriandersamen und schmeckte wie Honigkuchen.", footnotes: ["Manna bedeutet «Was ist das?» (vgl. V. 15). Man vergleicht das biblische Manna oft mit dem genießbaren süßlichen Harz der auf der Sinaihalbinsel noch heute vorkommenden Manna-Tamariske; doch kommt dieses «Manna» in so geringen Mengen vor, dass es nicht für eine größere wandernde Gruppe die Nahrung ersetzen kann. Die Bibel versteht die Mannagabe als Wunder."] },
      { number: 32, text: "Mose sagte: Der Herr ordnet Folgendes an. Ein volles Gomer Manna ist für die Generationen nach euch aufzubewahren, damit sie das Brot sehen, das ich euch in der Wüste zu essen gab, als ich euch aus Ägypten herausführte." },
      { number: 33, text: "Zu Aaron sagte Mose: Nimm ein Gefäß, schütte ein volles Gomer Manna hinein und stell es vor den Herrn! Es soll für die nachkommenden Generationen aufbewahrt werden." },
      { number: 34, text: "Wie der Herr dem Mose befohlen hatte, stellte Aaron das Manna vor die Bundesurkunde, damit es dort aufbewahrt würde." },
      { number: 35, text: "Die Israeliten aßen vierzig Jahre lang Manna, bis sie in bewohntes Land kamen. Sie aßen Manna, bis sie die Grenze von Kanaan erreichten.", footnotes: ["Jos 5,12"] },
      { number: 36, text: "Ein Gomer ist der zehnte Teil eines Efa." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Die ganze Gemeinde der Israeliten zog von der Wüste Sin weiter, von einem Rastplatz zum andern, wie es der Herr jeweils bestimmte. In Refidim schlugen sie ihr Lager auf. Weil das Volk kein Wasser zu trinken hatte," },
      { number: 2, text: "geriet es mit Mose in Streit und sagte: Gebt uns Wasser zu trinken! Mose aber antwortete: Was streitet ihr mit mir? Warum stellt ihr den Herrn auf die Probe?" },
      { number: 3, text: "Das Volk dürstete dort nach Wasser und murrte gegen Mose. Sie sagten: Warum hast du uns überhaupt aus Ägypten hierher geführt? Um uns, unsere Söhne und unser Vieh verdursten zu lassen?" },
      { number: 4, text: "Mose schrie zum Herrn: Was soll ich mit diesem Volk anfangen? Es fehlt nur wenig und sie steinigen mich." },
      { number: 5, text: "Der Herr antwortete Mose: Geh am Volk vorbei und nimm einige von den Ältesten Israels mit; nimm auch den Stab in die Hand, mit dem du auf den Nil geschlagen hast, und geh!" },
      { number: 6, text: "Dort drüben auf dem Felsen am Horeb werde ich vor dir stehen. Dann schlag an den Felsen! Es wird Wasser herauskommen und das Volk kann trinken. Das tat Mose vor den Augen der Ältesten Israels." },
      { number: 7, text: "Den Ort nannte er Massa und Meriba (Probe und Streit), weil die Israeliten Streit begonnen und den Herrn auf die Probe gestellt hatten, indem sie sagten: Ist der Herr in unserer Mitte oder nicht?", footnotes: ["Num 20,7-13; Dtn 32,51; 33,8; Ps 95,8f; 106,32f"] },
      { number: 8, text: "Als Amalek kam und in Refidim den Kampf mit Israel suchte," },
      { number: 9, text: "sagte Mose zu Josua: Wähl uns Männer aus und zieh in den Kampf gegen Amalek! Ich selbst werde mich morgen auf den Gipfel des Hügels stellen und den Gottesstab mitnehmen." },
      { number: 10, text: "Josua tat, was ihm Mose aufgetragen hatte, und kämpfte gegen Amalek, während Mose, Aaron und Hur auf den Gipfel des Hügels stiegen." },
      { number: 11, text: "Solange Mose seine Hand erhoben hielt, war Israel stärker; sooft er aber die Hand sinken ließ, war Amalek stärker." },
      { number: 12, text: "Als dem Mose die Hände schwer wurden, holten sie einen Steinbrocken, schoben ihn unter Mose und er setzte sich darauf. Aaron und Hur stützten seine Arme, der eine rechts, der andere links, sodass seine Hände erhoben blieben, bis die Sonne unterging." },
      { number: 13, text: "So besiegte Josua mit scharfem Schwert Amalek und sein Heer." },
      { number: 14, text: "Danach sprach der Herr zu Mose: Halte das zur Erinnerung in einer Urkunde fest und präg es Josua ein! Denn ich will die Erinnerung an Amalek unter dem Himmel austilgen." },
      { number: 15, text: "Mose baute einen Altar und gab ihm den Namen «Jahwe mein Feldzeichen»." },
      { number: 16, text: "Er sagte: Die Hand an Jahwes Feldzeichen! Krieg ist zwischen Jahwe und Amalek von Generation zu Generation." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Jitro, der Priester von Midian, der Schwiegervater des Mose, hörte, was Gott alles an Mose und seinem Volk Israel getan und wie der Herr Israel aus Ägypten herausgeführt hatte.", heading: "Jitros Rat: Die Berufung von Richtern:" },
      { number: 2, text: "Da nahm Jitro, der Schwiegervater des Mose, Zippora mit sich, die Frau des Mose - Mose hatte sie wieder zurückgeschickt -," },
      { number: 3, text: "und ihre beiden Söhne. Der eine hieß Gerschom (Ödgast), weil Mose gesagt hatte: Gast bin ich in fremdem Land.", footnotes: ["2,22"] },
      { number: 4, text: "Der andere hieß Eliëser (Gotthelf), weil Mose gesagt hatte: Der Gott meines Vaters hat mir geholfen und hat mich vor dem Schwert des Pharao gerettet." },
      { number: 5, text: "Jitro, der Schwiegervater des Mose, kam mit dessen Söhnen und dessen Frau in die Wüste am Gottesberg, wo Mose gerade lagerte." },
      { number: 6, text: "Er ließ Mose sagen: Ich, dein Schwiegervater Jitro, komme zu dir zusammen mit deiner Frau und ihren beiden Söhnen." },
      { number: 7, text: "Da ging Mose seinem Schwiegervater entgegen, fiel vor ihm nieder und küsste ihn. Dann fragten sie einander nach ihrem Wohlergehen und gingen ins Zelt." },
      { number: 8, text: "Mose erzählte seinem Schwiegervater alles, was der Herr dem Pharao und den Ägyptern um Israels willen angetan hatte, auch von allen Schwierigkeiten, denen sie unterwegs begegnet waren, und wie der Herr sie gerettet hatte." },
      { number: 9, text: "Jitro freute sich über alles, was der Herr an Israel Gutes getan hatte, als er es aus der Hand der Ägypter rettete." },
      { number: 10, text: "Jitro sagte: Gepriesen sei der Herr, der euch aus der Hand der Ägypter und des Pharao gerettet hat." },
      { number: 11, text: "Jetzt weiß ich: Jahwe ist größer als alle Götter. Denn die Ägypter haben Israel hochmütig behandelt, doch der Herr hat das Volk aus ihrer Hand gerettet.", footnotes: ["Der Satz «doch der Herr hat sein Volk aus ihrer Hand gerettet» steht in H schon in V. 10."] },
      { number: 12, text: "Dann holte Jitro, der Schwiegervater des Mose, Tiere für Brandopfer und Schlachtopfer zur Ehre Gottes. Aaron und alle Ältesten Israels kamen, um mit dem Schwiegervater des Mose vor dem Angesicht Gottes ein Mahl zu halten." },
      { number: 13, text: "Am folgenden Morgen setzte sich Mose, um für das Volk Recht zu sprechen. Die Leute mussten vor Mose vom Morgen bis zum Abend anstehen." },
      { number: 14, text: "Als der Schwiegervater des Mose sah, was er alles für das Volk zu tun hatte, sagte er: Was soll das, was du da für das Volk tust? Warum sitzt du hier allein und die vielen Leute müssen vom Morgen bis zum Abend vor dir anstehen?" },
      { number: 15, text: "Mose antwortete seinem Schwiegervater: Die Leute kommen zu mir, um Gott zu befragen." },
      { number: 16, text: "Wenn sie einen Streitfall haben, kommen sie zu mir. Ich entscheide dann ihren Fall und teile ihnen die Gesetze und Weisungen Gottes mit." },
      { number: 17, text: "Da sagte der Schwiegervater zu Mose: Es ist nicht richtig, wie du das machst." },
      { number: 18, text: "So richtest du dich selbst zugrunde und auch das Volk, das bei dir ist. Das ist zu schwer für dich; allein kannst du es nicht bewältigen." },
      { number: 19, text: "Nun hör zu, ich will dir einen Rat geben und Gott wird mit dir sein. Vertritt du das Volk vor Gott! Bring ihre Rechtsfälle vor ihn," },
      { number: 20, text: "unterrichte sie in den Gesetzen und Weisungen und lehre sie, wie sie leben und was sie tun sollen." },
      { number: 21, text: "Du aber sieh dich im ganzen Volk nach tüchtigen, gottesfürchtigen und zuverlässigen Männern um, die Bestechung ablehnen. Gib dem Volk Vorsteher für je tausend, hundert, fünfzig und zehn!" },
      { number: 22, text: "Sie sollen dem Volk jederzeit als Richter zur Verfügung stehen. Alle wichtigen Fälle sollen sie vor dich bringen, die leichteren sollen sie selber entscheiden. Entlaste dich und lass auch andere Verantwortung tragen!" },
      { number: 23, text: "Wenn du das tust, sofern Gott zustimmt, bleibst du der Aufgabe gewachsen und die Leute hier können alle zufrieden heimgehen." },
      { number: 24, text: "Mose hörte auf seinen Schwiegervater und tat alles, was er vorschlug." },
      { number: 25, text: "Mose wählte sich tüchtige Männer in ganz Israel aus und setzte sie als Hauptleute über das Volk ein, als Vorsteher für je tausend, hundert, fünfzig und zehn." },
      { number: 26, text: "Sie standen dem Volk jederzeit als Richter zur Verfügung. Die schwierigen Fälle brachten sie vor Mose, alle leichteren entschieden sie selber." },
      { number: 27, text: "Mose verabschiedete seinen Schwiegervater und dieser kehrte in sein Land zurück." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Im dritten Monat nach dem Auszug der Israeliten aus Ägypten - am heutigen Tag - kamen sie in der Wüste Sinai an.", heading: "Am Sinai: 19,1 - 40,38 | Das Bundesangebot Gottes: 19,1-25", footnotes: ["am heutigen Tag: Hinweis auf die liturgische Verwendung des Textes an einem bestimmten Tag; die Juden lesen ihn am Wochenfest, unserem Pfingstfest."] },
      { number: 2, text: "Sie waren von Refidim aufgebrochen und kamen in die Wüste Sinai. Sie schlugen in der Wüste das Lager auf. Dort lagerte Israel gegenüber dem Berg." },
      { number: 3, text: "Mose stieg zu Gott hinauf. Da rief ihm der Herr vom Berg her zu: Das sollst du dem Haus Jakob sagen und den Israeliten verkünden:" },
      { number: 4, text: "Ihr habt gesehen, was ich den Ägyptern angetan habe, wie ich euch auf Adlerflügeln getragen und hierher zu mir gebracht habe." },
      { number: 5, text: "Jetzt aber, wenn ihr auf meine Stimme hört und meinen Bund haltet, werdet ihr unter allen Völkern mein besonderes Eigentum sein. Mir gehört die ganze Erde," },
      { number: 6, text: "ihr aber sollt mir als ein Reich von Priestern und als ein heiliges Volk gehören. Das sind die Worte, die du den Israeliten mitteilen sollst.", footnotes: ["(5f) 1 Petr 2,9"] },
      { number: 7, text: "Mose ging und rief die Ältesten des Volkes zusammen. Er legte ihnen alles vor, was der Herr ihm aufgetragen hatte." },
      { number: 8, text: "Das ganze Volk antwortete einstimmig und erklärte: Alles, was der Herr gesagt hat, wollen wir tun. Mose überbrachte dem Herrn die Antwort des Volkes." },
      { number: 9, text: "Der Herr sprach zu Mose: Ich werde zu dir in einer dichten Wolke kommen; das Volk soll es hören, wenn ich mit dir rede, damit sie auch an dich immer glauben. Da berichtete Mose dem Herrn, was das Volk gesagt hatte." },
      { number: 10, text: "Der Herr sprach zu Mose: Geh zum Volk! Ordne an, dass sie sich heute und morgen heilig halten und ihre Kleider waschen." },
      { number: 11, text: "Sie sollen sich für den dritten Tag bereithalten. Am dritten Tag nämlich wird der Herr vor den Augen des ganzen Volkes auf den Berg Sinai herabsteigen." },
      { number: 12, text: "Zieh um das Volk eine Grenze und sag: Hütet euch, auf den Berg zu steigen oder auch nur seinen Fuß zu berühren. Jeder, der den Berg berührt, wird mit dem Tod bestraft." },
      { number: 13, text: "Keine Hand soll den Berg berühren. Wer es aber tut, soll gesteinigt oder mit Pfeilen erschossen werden; ob Tier oder Mensch, niemand darf am Leben bleiben. Erst wenn das Horn ertönt, dürfen sie auf den Berg steigen." },
      { number: 14, text: "Mose stieg vom Berg zum Volk hinunter und ordnete an, das Volk solle sich heilig halten und seine Kleider waschen." },
      { number: 15, text: "Er sagte zum Volk: Haltet euch für den dritten Tag bereit! Berührt keine Frau!" },
      { number: 16, text: "Am dritten Tag, im Morgengrauen, begann es zu donnern und zu blitzen. Schwere Wolken lagen über dem Berg und gewaltiger Hörnerschall erklang. Das ganze Volk im Lager begann zu zittern." },
      { number: 17, text: "Mose führte es aus dem Lager hinaus Gott entgegen. Unten am Berg blieben sie stehen." },
      { number: 18, text: "Der ganze Sinai war in Rauch gehüllt, denn der Herr war im Feuer auf ihn herabgestiegen. Der Rauch stieg vom Berg auf wie Rauch aus einem Schmelzofen. Der ganze Berg bebte gewaltig" },
      { number: 19, text: "und der Hörnerschall wurde immer lauter. Mose redete und Gott antwortete im Donner." },
      { number: 20, text: "Der Herr war auf den Sinai, auf den Gipfel des Berges, herabgestiegen. Er hatte Mose zu sich auf den Gipfel des Berges gerufen und Mose war hinaufgestiegen." },
      { number: 21, text: "Da sprach der Herr zu Mose: Geh hinunter und schärf dem Volk ein, sie sollen nicht neugierig sein und nicht versuchen, zum Herrn vorzudringen; sonst müssten viele von ihnen umkommen." },
      { number: 22, text: "Auch die Priester, die sich dem Herrn nähern, müssen sich geheiligt haben, damit der Herr in ihre Reihen keine Bresche reißt." },
      { number: 23, text: "Mose entgegnete dem Herrn: Das Volk kann nicht auf den Sinai steigen. Denn du selbst hast uns eingeschärft: Zieh eine Grenze um den Berg und erklär ihn für heilig!" },
      { number: 24, text: "Doch der Herr sprach zu ihm: Geh hinunter und komm zusammen mit Aaron wieder herauf! Die Priester aber und das Volk sollen nicht versuchen hinaufzusteigen und zum Herrn vorzudringen, sonst reißt er in ihre Reihen eine Bresche." },
      { number: 25, text: "Da ging Mose zum Volk hinunter und sagte es ihnen." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Dann sprach Gott alle diese Worte:", heading: "Die Zehn Gebote: 20,1-21" },
      { number: 2, text: "Ich bin Jahwe, dein Gott, der dich aus Ägypten geführt hat, aus dem Sklavenhaus.", footnotes: ["(2-18) Dtn 5,6-22"] },
      { number: 3, text: "Du sollst neben mir keine anderen Götter haben." },
      { number: 4, text: "Du sollst dir kein Gottesbild machen und keine Darstellung von irgendetwas am Himmel droben, auf der Erde unten oder im Wasser unter der Erde." },
      { number: 5, text: "Du sollst dich nicht vor anderen Göttern niederwerfen und dich nicht verpflichten, ihnen zu dienen. Denn ich, der Herr, dein Gott, bin ein eifersüchtiger Gott: Bei denen, die mir Feind sind, verfolge ich die Schuld der Väter an den Söhnen, an der dritten und vierten Generation;", footnotes: ["23,24; Dtn 5,9; 13,3"] },
      { number: 6, text: "bei denen, die mich lieben und auf meine Gebote achten, erweise ich Tausenden meine Huld." },
      { number: 7, text: "Du sollst den Namen des Herrn, deines Gottes, nicht missbrauchen; denn der Herr lässt den nicht ungestraft, der seinen Namen missbraucht." },
      { number: 8, text: "Gedenke des Sabbats: Halte ihn heilig!" },
      { number: 9, text: "Sechs Tage darfst du schaffen und jede Arbeit tun." },
      { number: 10, text: "Der siebte Tag ist ein Ruhetag, dem Herrn, deinem Gott, geweiht. An ihm darfst du keine Arbeit tun: du, dein Sohn und deine Tochter, dein Sklave und deine Sklavin, dein Vieh und der Fremde, der in deinen Stadtbereichen Wohnrecht hat." },
      { number: 11, text: "Denn in sechs Tagen hat der Herr Himmel, Erde und Meer gemacht und alles, was dazugehört; am siebten Tag ruhte er. Darum hat der Herr den Sabbattag gesegnet und ihn für heilig erklärt." },
      { number: 12, text: "Ehre deinen Vater und deine Mutter, damit du lange lebst in dem Land, das der Herr, dein Gott, dir gibt." },
      { number: 13, text: "Du sollst nicht morden." },
      { number: 14, text: "Du sollst nicht die Ehe brechen." },
      { number: 15, text: "Du sollst nicht stehlen." },
      { number: 16, text: "Du sollst nicht falsch gegen deinen Nächsten aussagen." },
      { number: 17, text: "Du sollst nicht nach dem Haus deines Nächsten verlangen. Du sollst nicht nach der Frau deines Nächsten verlangen, nach seinem Sklaven oder seiner Sklavin, seinem Rind oder seinem Esel oder nach irgendetwas, das deinem Nächsten gehört." },
      { number: 18, text: "Das ganze Volk erlebte, wie es donnerte und blitzte, wie Hörner erklangen und der Berg rauchte. Da bekam das Volk Angst, es zitterte und hielt sich in der Ferne." },
      { number: 19, text: "Sie sagten zu Mose: Rede du mit uns, dann wollen wir hören. Gott soll nicht mit uns reden, sonst sterben wir." },
      { number: 20, text: "Da sagte Mose zum Volk: Fürchtet euch nicht! Gott ist gekommen, um euch auf die Probe zu stellen. Die Furcht vor ihm soll über euch kommen, damit ihr nicht sündigt." },
      { number: 21, text: "Das Volk hielt sich in der Ferne und Mose näherte sich der dunklen Wolke, in der Gott war." },
      { number: 22, text: "Der Herr sprach zu Mose: Sag den Israeliten: Ihr habt gesehen, dass ich vom Himmel her mit euch geredet habe.", heading: "Das Altargesetz: 20,22-26" },
      { number: 23, text: "Ihr sollt euch neben mir keine Götter aus Silber machen, auch Götter aus Gold sollt ihr euch nicht machen." },
      { number: 24, text: "Du sollst mir einen Altar aus Erde errichten und darauf deine Schafe, Ziegen und Rinder als Brandopfer und Heilsopfer schlachten. An jedem Ort, an dem ich meinem Namen ein Gedächtnis stifte, will ich zu dir kommen und dich segnen." },
      { number: 25, text: "Wenn du mir einen Altar aus Steinen errichtest, so sollst du ihn nicht aus behauenen Quadern bauen. Du entweihst ihn, wenn du mit einem Meißel daran arbeitest.", footnotes: ["Dtn 27,5f"] },
      { number: 26, text: "Du sollst nicht auf Stufen zu meinem Altar hinaufsteigen, damit deine Blöße dabei nicht zum Vorschein komme." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Das sind die Rechtsvorschriften, die du ihnen vorlegen sollst:", heading: "Das Bundesbuch: 21,1 - 23,33 | Einleitung: 21,1", footnotes: ["1-23,33: Diese Gesetzessammlung bezeichnet man heute als Bundesbuch (vgl. 24,7)."] },
      { number: 2, text: "Wenn du einen hebräischen Sklaven kaufst, soll er sechs Jahre Sklave bleiben, im siebten Jahr soll er ohne Entgelt als freier Mann entlassen werden.", heading: "Hebräische Sklaven: 21,2-11" },
      { number: 3, text: "Ist er allein gekommen, soll er allein gehen. War er verheiratet, soll seine Frau mitgehen." },
      { number: 4, text: "Hat ihm sein Herr eine Frau gegeben und hat sie ihm Söhne oder Töchter geboren, dann gehören Frau und Kinder ihrem Herrn und er muss allein gehen." },
      { number: 5, text: "Erklärt aber der Sklave: Ich liebe meinen Herrn, meine Frau und meine Kinder und will nicht als freier Mann fortgehen," },
      { number: 6, text: "dann soll ihn sein Herr vor Gott bringen, er soll ihn an die Tür oder an den Torpfosten bringen und ihm das Ohr mit einem Pfriem durchbohren; dann bleibt er für immer sein Sklave." },
      { number: 7, text: "Wenn einer seine Tochter als Sklavin verkauft hat, soll sie nicht wie andere Sklaven entlassen werden." },
      { number: 8, text: "Hat ihr Herr sie für sich selbst bestimmt, mag er sie aber nicht mehr, dann soll er sie zurückkaufen lassen. Er hat nicht das Recht, sie an Fremde zu verkaufen, da er seine Zusage nicht eingehalten hat." },
      { number: 9, text: "Hat er sie für seinen Sohn bestimmt, verfahre er mit ihr nach dem Recht, das für Töchter gilt." },
      { number: 10, text: "Nimmt er sich noch eine andere Frau, darf er sie in Nahrung, Kleidung und Beischlaf nicht benachteiligen." },
      { number: 11, text: "Wenn er ihr diese drei Dinge nicht gewährt, darf sie unentgeltlich, ohne Bezahlung, gehen." },
      { number: 12, text: "Wer einen Menschen so schlägt, dass er stirbt, wird mit dem Tod bestraft.", heading: "Totschlag und Mord: 21,12-14" },
      { number: 13, text: "Wenn er ihm aber nicht aufgelauert hat, sondern Gott es durch seine Hand geschehen ließ, werde ich dir einen Ort festsetzen, an den er fliehen kann." },
      { number: 14, text: "Hat einer vorsätzlich gehandelt und seinen Mitbürger aus dem Hinterhalt umgebracht, sollst du ihn von meinem Altar wegholen, damit er stirbt." },
      { number: 15, text: "Wer seinen Vater oder seine Mutter schlägt, wird mit dem Tod bestraft.", heading: "Misshandlung der Eltern: 21,15", footnotes: ["Lev 20,9; Dtn 27,16"] },
      { number: 16, text: "Wer einen Menschen raubt, gleichgültig, ob er ihn verkauft hat oder ob man ihn noch in seiner Gewalt vorfindet, wird mit dem Tod bestraft.", heading: "Menschenraub: 21,16" },
      { number: 17, text: "Wer seinen Vater oder seine Mutter verflucht, wird mit dem Tod bestraft.", heading: "Entehrung der Eltern: 21,17" },
      { number: 18, text: "Wenn Männer in Streit geraten und einer den andern mit einem Stein oder einer Hacke verletzt, sodass er zwar nicht stirbt, aber bettlägerig wird,", heading: "Körperverletzung durch Menschen: 21,18-27" },
      { number: 19, text: "später wieder aufstehen und mit Krücken draußen umhergehen kann, so ist der freizusprechen, der geschlagen hat; nur für die Arbeitsunfähigkeit des Geschädigten muss er Ersatz leisten und er muss für die Heilung aufkommen." },
      { number: 20, text: "Wenn einer seinen Sklaven oder seine Sklavin mit dem Stock so schlägt, dass er unter seiner Hand stirbt, dann muss der Sklave gerächt werden." },
      { number: 21, text: "Wenn er noch einen oder zwei Tage am Leben bleibt, dann soll den Täter keine Rache treffen; es geht ja um sein eigenes Geld." },
      { number: 22, text: "Wenn Männer miteinander raufen und dabei eine schwangere Frau treffen, sodass sie eine Fehlgeburt hat, ohne dass ein weiterer Schaden entsteht, dann soll der Täter eine Buße zahlen, die ihm der Ehemann der Frau auferlegt; er kann die Zahlung nach dem Urteil von Schiedsrichtern leisten." },
      { number: 23, text: "Ist weiterer Schaden entstanden, dann musst du geben: Leben für Leben," },
      { number: 24, text: "Auge für Auge, Zahn für Zahn, Hand für Hand, Fuß für Fuß,", footnotes: ["Lev 24,19f; Dtn 19,21; Mt 5,38"] },
      { number: 25, text: "Brandmal für Brandmal, Wunde für Wunde, Strieme für Strieme." },
      { number: 26, text: "Wenn einer seinem Sklaven oder seiner Sklavin ein Auge ausschlägt, soll er ihn für das ausgeschlagene Auge freilassen." },
      { number: 27, text: "Wenn er seinem Sklaven oder seiner Sklavin einen Zahn ausschlägt, soll er ihn für den ausgeschlagenen Zahn freilassen." },
      { number: 28, text: "Wenn ein Rind einen Mann oder eine Frau so stößt, dass der Betreffende stirbt, dann muss man das Rind steinigen und sein Fleisch darf man nicht essen; der Eigentümer des Rinds aber bleibt straffrei.", heading: "Körperverletzung durch Haustiere: 21,28-32" },
      { number: 29, text: "Hat das Rind aber schon früher gestoßen und hat der Eigentümer, obwohl man ihn darauf aufmerksam gemacht hat, auf das Tier nicht aufgepasst, sodass es einen Mann oder eine Frau getötet hat, dann soll man das Rind steinigen und auch sein Eigentümer soll getötet werden." },
      { number: 30, text: "Will man ihm aber eine Sühne auferlegen, soll er als Lösegeld für sein Leben so viel geben, wie man von ihm fordert." },
      { number: 31, text: "Stößt das Rind einen Sohn oder eine Tochter, verfahre man nach dem gleichen Grundsatz." },
      { number: 32, text: "Stößt das Rind einen Sklaven oder eine Sklavin, soll der Eigentümer dem Herrn dreißig Silberschekel zahlen; das Rind aber soll gesteinigt werden." },
      { number: 33, text: "Wenn jemand einen Brunnen offen lässt oder einen Brunnen gräbt, ohne ihn abzudecken, und es fällt ein Rind oder ein Esel hinein,", heading: "Ersatz bei Schädigung fremden Viehs: 21,33-36" },
      { number: 34, text: "dann soll der Eigentümer des Brunnens Ersatz leisten; er soll dem Eigentümer des Tieres Geld zahlen, das verendete Tier aber gehört ihm." },
      { number: 35, text: "Wenn jemandes Rind das Rind eines anderen stößt, sodass es eingeht, soll man das lebende Rind verkaufen und den Erlös aufteilen; auch das verendete Rind soll man aufteilen." },
      { number: 36, text: "Wenn jedoch der Eigentümer wusste, dass das Rind schon früher stößig war, aber trotzdem nicht darauf aufgepasst hat, soll er das Rind ersetzen, Rind für Rind, das verendete Rind aber gehört ihm." },
      { number: 37, text: "Wenn einer ein Rind oder ein Schaf stiehlt und es schlachtet oder verkauft, soll er fünf Stück Großvieh für das Rind oder vier Stück Kleinvieh für das Schaf als Ersatz geben.", heading: "Ersatz bei Diebstahl: 21,37 - 22,3" },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Wird ein Dieb beim Einbruch ertappt und so geschlagen, dass er stirbt, so entsteht dadurch keine Blutschuld." },
      { number: 2, text: "Doch ist darüber bereits die Sonne aufgegangen, dann entsteht Blutschuld. Ein Dieb muss Ersatz leisten. Besitzt er nichts, soll man ihn für den Wert des Gestohlenen verkaufen." },
      { number: 3, text: "Findet man das Gestohlene, sei es Rind, Esel oder Schaf, noch lebend in seinem Besitz, dann soll er doppelten Ersatz leisten." },
      { number: 4, text: "Wenn jemand ein Feld oder einen Weinberg abbrennt und das Feuer sich ausbreiten lässt, sodass es das Feld eines andern in Brand steckt, dann soll er den besten Ertrag seines Feldes oder Weinbergs als Ersatz dafür geben.", heading: "Ersatz bei fahrlässigem Feldbrand: 22,4-5" },
      { number: 5, text: "Breitet sich das Feuer aus, erfasst es eine Dornenhecke und vernichtet einen Getreidehaufen, auf dem Halm stehendes Getreide oder ein Feld, dann soll der für den Brand Verantwortliche den Schaden ersetzen." },
      { number: 6, text: "Übergibt jemand einem andern Geld oder Gerät zur Aufbewahrung und es wird aus dessen Haus gestohlen, dann soll der Dieb, wenn man ihn findet, doppelten Ersatz leisten.", heading: "Haftung für fremdes Eigentum: 22,6-14" },
      { number: 7, text: "Findet man den Dieb nicht, soll der Hausherr vor Gott erklären, dass er sich nicht selbst am Eigentum des andern vergriffen hat." },
      { number: 8, text: "Wenn jemandem etwas veruntreut wurde, ein Rind, ein Esel, ein Schaf, ein Kleid oder sonst etwas, und er behauptet: Ja, das ist es, dann soll der Streitfall der beiden vor Gott kommen. Wen Gott als schuldig bezeichnet, soll dem andern doppelten Ersatz leisten." },
      { number: 9, text: "Wenn jemand einem andern einen Esel, ein Rind, ein Schaf oder sonst ein Haustier zur Verwahrung übergibt und das Tier eingeht, sich etwas bricht oder fortgetrieben wird, ohne dass es jemand sieht," },
      { number: 10, text: "dann soll ein Eid beim Namen des Herrn Klarheit darüber schaffen, dass der eine sich nicht am Eigentum des andern vergriffen hat. Der Eigentümer soll an sich nehmen, was noch da ist, ohne dass der andere Ersatz zu leisten hätte." },
      { number: 11, text: "Ist es ihm aber gestohlen worden, muss er dem Eigentümer Ersatz leisten." },
      { number: 12, text: "Ist das Tier gerissen worden, bringe er es zum Beweis herbei; dann braucht er für das Gerissene keinen Ersatz zu leisten." },
      { number: 13, text: "Leiht jemand von einem andern ein Tier und bricht es sich etwas oder geht ein, und zwar in Abwesenheit des Eigentümers, so muss er Ersatz leisten." },
      { number: 14, text: "War der Eigentümer aber anwesend, so braucht der andere keinen Ersatz zu leisten. Ist er Taglöhner, so geht es von seinem Lohn ab." },
      { number: 15, text: "Wenn jemand ein noch nicht verlobtes Mädchen verführt und bei ihm schläft, dann soll er das Brautgeld zahlen und sie zur Frau nehmen.", heading: "Verführung einer Jungfrau: 22,15-16" },
      { number: 16, text: "Weigert sich aber ihr Vater, sie ihm zu geben, dann hat er ihm so viel zu zahlen, wie der Brautpreis für eine Jungfrau beträgt." },
      { number: 17, text: "Eine Hexe sollst du nicht am Leben lassen.", heading: "Todeswürdige Verbrechen: Zauberei, Bestialität, Götzendienst:", footnotes: ["Lev 20,27; Dtn 18,10-12"] },
      { number: 18, text: "Jeder, der mit einem Tier verkehrt, soll mit dem Tod bestraft werden.", footnotes: ["27,21"] },
      { number: 19, text: "Wer einer Gottheit außer Jahwe Schlachtopfer darbringt, an dem soll die Vernichtungsweihe vollstreckt werden.", footnotes: ["Dtn 13,3f; 13-16; 18,13"] },
      { number: 20, text: "Einen Fremden sollst du nicht ausnützen oder ausbeuten, denn ihr selbst seid in Ägypten Fremde gewesen.", heading: "Schutz vor Unterdrückung und Ausbeutung: 22,20-26" },
      { number: 21, text: "Ihr sollt keine Witwe oder Waise ausnützen." },
      { number: 22, text: "Wenn du sie ausnützt und sie zu mir schreit, werde ich auf ihren Klageschrei hören." },
      { number: 23, text: "Mein Zorn wird entbrennen und ich werde euch mit dem Schwert umbringen, sodass eure Frauen zu Witwen und eure Söhne zu Waisen werden." },
      { number: 24, text: "Leihst du einem aus meinem Volk, einem Armen, der neben dir wohnt, Geld, dann sollst du dich gegen ihn nicht wie ein Wucherer benehmen. Ihr sollt von ihm keinen Wucherzins fordern.", footnotes: ["Lev 25,36f; Dtn 23,20f"] },
      { number: 25, text: "Nimmst du von einem Mitbürger den Mantel zum Pfand, dann sollst du ihn bis Sonnenuntergang zurückgeben;" },
      { number: 26, text: "denn es ist seine einzige Decke, der Mantel, mit dem er seinen bloßen Leib bedeckt. Worin soll er sonst schlafen? Wenn er zu mir schreit, höre ich es, denn ich habe Mitleid." },
      { number: 27, text: "Du sollst Gott nicht verächtlich machen und den Fürsten deines Volkes nicht verfluchen.", heading: "Gotteslästerung und Majestätsbeleidigung: 22,27" },
      { number: 28, text: "Deinen Reichtum und Überfluss sollst du nicht für dich behalten. Den Erstgeborenen unter deinen Söhnen sollst du mir geben.", heading: "Erstlingsopfer: 22,28-29", footnotes: ["34,19f; Dtn 15,19-23"] },
      { number: 29, text: "Ebenso sollst du es mit deinen Rindern, Schafen und Ziegen halten. Sieben Tage sollen sie bei ihrer Mutter bleiben, am achten Tag sollst du sie mir übergeben." },
      { number: 30, text: "Als heilige Männer sollt ihr mir gehören. Fleisch von einem Tier, das auf dem Feld gerissen wurde, sollt ihr nicht essen; ihr sollt es den Hunden vorwerfen.", heading: "Verbot, gerissene Tiere zu essen: 22,30", footnotes: ["Lev 17,15"] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Du sollst kein leeres Gerücht verbreiten. Biete deine Hand nicht dem, der Unrecht hat, indem du als falscher Zeuge auftrittst.", heading: "Verhalten im Rechtsverfahren: 23,1-3" },
      { number: 2, text: "Du sollst dich nicht der Mehrheit anschließen, wenn sie im Unrecht ist, und sollst in einem Rechtsverfahren nicht so aussagen, dass du dich der Mehrheit fügst und das Recht beugst." },
      { number: 3, text: "Du sollst auch den Geringen in seinem Rechtsstreit nicht begünstigen.", footnotes: ["Lev 19,15"] },
      { number: 4, text: "Wenn du dem verirrten Rind oder dem Esel deines Feindes begegnest, sollst du ihm das Tier zurückbringen.", heading: "Verhalten gegen den Feind: 23,4-5" },
      { number: 5, text: "Wenn du siehst, wie der Esel deines Gegners unter der Last zusammenbricht, dann lass ihn nicht im Stich, sondern leiste ihm Hilfe!" },
      { number: 6, text: "Du sollst das Recht des Armen in seinem Rechtsstreit nicht beugen.", heading: "Verbot von Rechtsbeugung: 23,6-8" },
      { number: 7, text: "Von einem unlauteren Verfahren sollst du dich fern halten. Wer unschuldig und im Recht ist, den bring nicht um sein Leben; denn ich spreche den Schuldigen nicht frei." },
      { number: 8, text: "Du sollst dich nicht bestechen lassen; denn Bestechung macht Sehende blind und verkehrt die Sache derer, die im Recht sind." },
      { number: 9, text: "Einen Fremden sollst du nicht ausbeuten. Ihr wisst doch, wie es einem Fremden zumute ist; denn ihr selbst seid in Ägypten Fremde gewesen.", heading: "Rechtsschutz des Fremden: 23,9" },
      { number: 10, text: "Sechs Jahre kannst du in deinem Land säen und die Ernte einbringen;", heading: "Sabbatjahr und Sabbatfeier: 23,10-12", footnotes: ["Lev 25,2-4"] },
      { number: 11, text: "im siebten sollst du es brach liegen lassen und nicht bestellen. Die Armen in deinem Volk sollen davon essen, den Rest mögen die Tiere des Feldes fressen. Das Gleiche sollst du mit deinem Weinberg und deinen Ölbäumen tun." },
      { number: 12, text: "Sechs Tage kannst du deine Arbeit verrichten, am siebten Tag aber sollst du ruhen, damit dein Rind und dein Esel ausruhen und der Sohn deiner Sklavin und der Fremde zu Atem kommen.", footnotes: ["20,9f; Dtn 5,12-14"] },
      { number: 13, text: "Auf alles, was ich euch gesagt habe, sollt ihr achten. Den Namen eines anderen Gottes sollt ihr nicht aussprechen, er soll dir nicht über die Lippen kommen.", heading: "Mahnung: Treue zum Bundesgott:" },
      { number: 14, text: "Dreimal im Jahr sollst du mir ein Fest feiern.", heading: "Die drei Hauptfeste: 23,14-17", footnotes: ["(14-17) 13,6f; 34,18-23"] },
      { number: 15, text: "Du sollst das Fest der ungesäuerten Brote halten. Im Monat Abib sollst du zur festgesetzten Zeit sieben Tage lang ungesäuertes Brot essen, wie ich es dir geboten habe. Denn in diesem Monat bist du aus Ägypten ausgezogen. Man soll nicht mit leeren Händen vor mir erscheinen." },
      { number: 16, text: "Du sollst auch das Fest der Ernte, des ersten Ertrags deiner Aussaat auf dem Feld, halten, ebenso das Fest der Lese am Ende des Jahres, wenn du den Ertrag deines Feldes eingebracht hast." },
      { number: 17, text: "Dreimal im Jahr sollen alle deine Männer vor dem Herrn erscheinen." },
      { number: 18, text: "Beim Schlachten sollst du das Blut meines Opfers nicht über gesäuertes Brot fließen lassen. Das Fett meines Festopfers darf nicht bis zum Morgen liegen bleiben.", heading: "Opfervorschriften: 23,18-19", footnotes: ["34,25f"] },
      { number: 19, text: "Von den Erstlingsfrüchten deines Ackers sollst du die besten in das Haus des Herrn, deines Gottes, bringen. Das Junge einer Ziege sollst du nicht in der Milch seiner Mutter kochen." },
      { number: 20, text: "Ich werde einen Engel schicken, der dir vorausgeht. Er soll dich auf dem Weg schützen und dich an den Ort bringen, den ich bestimmt habe.", heading: "Schlussmahnung: Verheißung und Warnung:" },
      { number: 21, text: "Achte auf ihn und hör auf seine Stimme! Widersetz dich ihm nicht! Er würde es nicht ertragen, wenn ihr euch auflehnt; denn in ihm ist mein Name gegenwärtig." },
      { number: 22, text: "Wenn du auf seine Stimme hörst und alles tust, was ich sage, dann werde ich der Feind deiner Feinde sein und alle in die Enge treiben, die dich bedrängen." },
      { number: 23, text: "Wenn mein Engel dir vorausgeht und dich in das Land der Amoriter, Hetiter, Perisiter, Kanaaniter, Hiwiter und Jebusiter führt und wenn ich sie verschwinden lasse," },
      { number: 24, text: "dann sollst du dich vor ihren Göttern nicht niederwerfen und dich nicht verpflichten, ihnen zu dienen. Du sollst keine Kultgegenstände herstellen wie sie, sondern sie zerstören und ihre Steinmale zerschlagen.", footnotes: ["20,5; Dtn 5,9; 13,3"] },
      { number: 25, text: "Wenn ihr dem Herrn, eurem Gott, dient, wird er dein Brot und dein Wasser segnen. Ich werde Krankheiten von dir fern halten." },
      { number: 26, text: "In deinem Land wird es keine Frau geben, die eine Fehlgeburt hat oder kinderlos bleibt. Ich lasse dich die volle Zahl deiner Lebenstage erreichen." },
      { number: 27, text: "Ich sende meinen Schrecken vor dir her, ich verwirre jedes Volk, zu dem du kommst, und alle deine Feinde lasse ich vor dir die Flucht ergreifen." },
      { number: 28, text: "Ich lasse vor dir Panik ausbrechen; sie wird die Hiwiter, Kanaaniter und Hetiter vor dir hertreiben.", footnotes: ["Dtn 7,20; Jos 24,12"] },
      { number: 29, text: "Ich vertreibe sie aber nicht gleich im ersten Jahr; sonst verödet das Land und die wilden Tiere könnten zu deinem Schaden überhand nehmen." },
      { number: 30, text: "Nur allmählich will ich sie vor dir zurückdrängen, bis du so zahlreich geworden bist, dass du das Land in Besitz nehmen kannst." },
      { number: 31, text: "Ich setze deine Landesgrenzen fest vom Schilfmeer bis zum Philistermeer, von der Wüste bis zum Strom. Wenn ich die Einwohner des Landes in deine Hand gebe und du sie vertreibst,", footnotes: ["Der «Strom» ist der Eufrat. Bis an den Eufrat reichten die Grenzen Israels nur zur Zeit Davids und Salomos."] },
      { number: 32, text: "dann sollst du keinen Bund mit ihnen und ihren Göttern schließen." },
      { number: 33, text: "Sie sollen nicht in deinem Land bleiben. Sonst könnten sie dich zur Sünde gegen mich verführen, sodass du ihre Götter verehrst; denn dann würde dir das zu einer Falle." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "¥Zu Mose sprach er: Steig zum Herrn hinauf zusammen mit Aaron, Nadab, Abihu und mit siebzig von den Ältesten Israels; werft euch in einiger Entfernung nieder!", heading: "Der Bundesschluß: 24,1-18" },
      { number: 2, text: "Mose allein soll sich dem Herrn nähern, die anderen dürfen nicht näher kommen und das Volk darf den Berg nicht mit ihm zusammen besteigen." },
      { number: 3, text: "Mose kam und übermittelte dem Volk alle Worte und Rechtsvorschriften des Herrn. Das ganze Volk antwortete einstimmig und sagte: Alles, was der Herr gesagt hat, wollen wir tun." },
      { number: 4, text: "Mose schrieb alle Worte des Herrn auf. Am nächsten Morgen stand er zeitig auf und errichtete am Fuß des Berges einen Altar und zwölf Steinmale für die zwölf Stämme Israels." },
      { number: 5, text: "Er schickte die jungen Männer Israels aus. Sie brachten Brandopfer dar und schlachteten junge Stiere als Heilsopfer für den Herrn." },
      { number: 6, text: "Mose nahm die Hälfte des Blutes und goss es in eine Schüssel, mit der anderen Hälfte besprengte er den Altar." },
      { number: 7, text: "Darauf nahm er die Urkunde des Bundes und verlas sie vor dem Volk. Sie antworteten: Alles, was der Herr gesagt hat, wollen wir tun; wir wollen gehorchen." },
      { number: 8, text: "Da nahm Mose das Blut, besprengte damit das Volk und sagte: Das ist das Blut des Bundes, den der Herr aufgrund all dieser Worte mit euch geschlossen hat." },
      { number: 9, text: "Danach stiegen Mose, Aaron, Nadab, Abihu und die siebzig von den Ältesten Israels hinauf" },
      { number: 10, text: "und sie sahen den Gott Israels. Die Fläche unter seinen Füßen war wie mit Saphir ausgelegt und glänzte hell wie der Himmel selbst." },
      { number: 11, text: "Gott streckte nicht seine Hand gegen die Edlen der Israeliten aus; sie durften Gott sehen und sie aßen und tranken." },
      { number: 12, text: "Der Herr sprach zu Mose: Komm herauf zu mir auf den Berg und bleib hier! Ich will dir die Steintafeln übergeben, die Weisung und die Gebote, die ich aufgeschrieben habe. Du sollst das Volk darin unterweisen." },
      { number: 13, text: "Da erhob sich Mose mit seinem Diener Josua und stieg den Gottesberg hinauf." },
      { number: 14, text: "Zu den Ältesten sagte er: Bleibt hier, bis wir zu euch zurückkehren; Aaron und Hur sind ja bei euch. Wer ein Anliegen hat, wende sich an sie." },
      { number: 15, text: "Dann stieg Mose auf den Berg und die Wolke bedeckte den Berg." },
      { number: 16, text: "Die Herrlichkeit des Herrn ließ sich auf den Sinai herab und die Wolke bedeckte den Berg sechs Tage lang. Am siebten Tag rief der Herr mitten aus der Wolke Mose herbei." },
      { number: 17, text: "Die Erscheinung der Herrlichkeit des Herrn auf dem Gipfel des Berges zeigte sich vor den Augen der Israeliten wie verzehrendes Feuer." },
      { number: 18, text: "Mose ging mitten in die Wolke hinein und stieg auf den Berg hinauf. Vierzig Tage und vierzig Nächte blieb Mose auf dem Berg." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Anordnungen für Heiligtum und Kult: 25,1 - 31,17 | Das Heiligtum und seine Geräte:", footnotes: ["(1-9) 35,4-10"] },
      { number: 2, text: "Sag zu den Israeliten, sie sollen für mich eine Abgabe erheben. Von jedem, den sein Sinn dazu bewegt, sollt ihr die Abgabe erheben." },
      { number: 3, text: "Das ist die Abgabe, die ihr von ihnen erheben sollt: Gold, Silber, Kupfer," },
      { number: 4, text: "violetten und roten Purpur, Karmesin, Byssus, Ziegenhaare," },
      { number: 5, text: "rötliche Widderfelle, Tahaschhäute und Akazienholz;", footnotes: ["Was Tahaschhäute sind, wissen wir nicht; man vermutet, dass es Haut von Meerestieren (Delphinen?) war."] },
      { number: 6, text: "Öl für den Leuchter, Balsame für das Salböl und für duftendes Räucherwerk;" },
      { number: 7, text: "Karneolsteine und Ziersteine für Efod und Lostasche.", footnotes: ["Das Efod ist hier ein Kleidungsstück des Priesters. Seine Form hat sich im Lauf der Zeit geändert. In Ri 8,24-27 und 18,14-20 scheint es eher ein Kultobjekt, ein Gottesbild, gewesen zu sein."] },
      { number: 8, text: "Macht mir ein Heiligtum! Dann werde ich in ihrer Mitte wohnen." },
      { number: 9, text: "Genau nach dem Muster der Wohnstätte und aller ihrer Gegenstände, das ich dir zeige, sollt ihr es herstellen.", footnotes: ["«Wohnstätte» ist in der P-Schicht Ausdruck für das Zeltheiligtum, das als Wohnstätte Gottes unter dem wandernden Volk galt."] },
      { number: 10, text: "Macht eine Lade aus Akazienholz, zweieinhalb Ellen lang, anderthalb Ellen breit und anderthalb Ellen hoch!", footnotes: ["(10-22) 37,1-9"] },
      { number: 11, text: "Überzieh sie innen und außen mit purem Gold und bring daran ringsherum eine Goldleiste an!" },
      { number: 12, text: "Gieß für sie vier Goldringe und befestige sie an ihren vier Füßen, zwei Ringe an der einen Seite und zwei Ringe an der anderen Seite!" },
      { number: 13, text: "Fertige Stangen aus Akazienholz an und überzieh sie mit Gold!" },
      { number: 14, text: "Steck die Stangen durch die Ringe an den Seiten der Lade, sodass man die Lade damit tragen kann." },
      { number: 15, text: "Die Stangen sollen in den Ringen der Lade bleiben; man soll sie nicht herausziehen." },
      { number: 16, text: "In die Lade sollst du die Bundesurkunde legen, die ich dir gebe." },
      { number: 17, text: "Verfertige auch eine Deckplatte aus purem Gold zweieinhalb Ellen lang und anderthalb Ellen breit!" },
      { number: 18, text: "Mach zwei Kerubim aus getriebenem Gold und arbeite sie an den beiden Enden der Deckplatte heraus!" },
      { number: 19, text: "Mach je einen Kerub an dem einen und dem andern Ende; auf der Deckplatte macht die Kerubim an den beiden Enden!" },
      { number: 20, text: "Die Kerubim sollen die Flügel nach oben ausbreiten, mit ihren Flügeln die Deckplatte beschirmen und sie sollen ihre Gesichter einander zuwenden; der Deckplatte sollen die Gesichter der Kerubim zugewandt sein." },
      { number: 21, text: "Setz die Deckplatte oben auf die Lade und in die Lade leg die Bundesurkunde, die ich dir gebe." },
      { number: 22, text: "Dort werde ich mich dir zu erkennen geben und dir über der Deckplatte zwischen den beiden Kerubim, die auf der Lade der Bundesurkunde sind, alles sagen, was ich dir für die Israeliten auftragen werde." },
      { number: 23, text: "Fertige auch einen Tisch aus Akazienholz an, zwei Ellen lang, eine Elle breit und anderthalb Ellen hoch!", footnotes: ["(23-30) 37,10-16"] },
      { number: 24, text: "Überzieh ihn mit purem Gold und bring daran ringsherum eine Goldleiste an!" },
      { number: 25, text: "Mach ihm ringsherum eine handbreite Einfassung und verfertige um diese Einfassung eine goldene Leiste!" },
      { number: 26, text: "Mach ihm vier Goldringe und befestige die Ringe an den vier Ecken, die von seinen vier Füßen gebildet werden." },
      { number: 27, text: "Die Ringe sollen dicht unter der Einfassung die Stangen aufnehmen, mit denen man den Tisch trägt." },
      { number: 28, text: "Mach die Stangen aus Akazienholz und überzieh sie mit Gold! Mit ihnen soll man den Tisch tragen." },
      { number: 29, text: "Dazu mach Schüsseln, Schalen, Kannen und Krüge für die Trankopfer! Aus purem Gold sollst du sie anfertigen." },
      { number: 30, text: "Auf dem Tisch sollst du ständig Schaubrote vor mir auflegen." },
      { number: 31, text: "Verfertige auch einen Leuchter aus purem Gold! Der Leuchter, sein Gestell, sein Schaft, seine Kelche, Knospen und Blüten sollen aus einem Stück getrieben sein.", footnotes: ["(31-40) 37,17-24"] },
      { number: 32, text: "Von seinen Seiten sollen sechs Arme ausgehen, drei Leuchterarme auf der einen Seite und drei auf der anderen Seite." },
      { number: 33, text: "Der erste Arm soll drei mandelblütenförmige Kelche mit je einer Knospe und einer Blüte aufweisen und der zweite Arm soll drei mandelblütenförmige Kelche mit je einer Knospe und einer Blüte aufweisen; so alle sechs Arme, die von dem Leuchter ausgehen." },
      { number: 34, text: "Auf dem Schaft des Leuchters sollen vier mandelblütenförmige Kelche, Knospen und Blüten sein," },
      { number: 35, text: "je eine Knospe unten zwischen zwei Armen, entsprechend den sechs Armen, die vom Leuchter ausgehen." },
      { number: 36, text: "Seine Knospen und die Arme sollen ein Ganzes mit dem Schaft bilden; das Ganze soll ein Stück aus getriebenem purem Gold sein." },
      { number: 37, text: "Dann mach für den Leuchter sieben Lampen und setze seine Lampen so auf, dass sie das Licht nach vorn fallen lassen;" },
      { number: 38, text: "dazu Dochtscheren und Pfannen aus purem Gold." },
      { number: 39, text: "Aus einem Talent puren Goldes soll man den Leuchter und alle diese Geräte machen." },
      { number: 40, text: "Sieh zu, dass du ihn nach dem Muster ausführst, das du auf dem Berg gesehen hast." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Die Wohnstätte sollst du aus zehn Zelttüchern herstellen; aus gezwirntem Byssus, violettem und rotem Purpur und Karmesin mit Kerubim sollst du sie machen, wie es ein Kunstweber macht.", footnotes: ["(1-6) 36,8-13", "Zu Byssus vgl. die Anmerkung zu Gen 41,42."] },
      { number: 2, text: "Ein Zelttuch soll achtundzwanzig Ellen lang und vier Ellen breit sein; alle Zelttücher sollen dasselbe Maß haben." },
      { number: 3, text: "Fünf Zelttücher sollen jeweils aneinander gefügt sein." },
      { number: 4, text: "Mach Schleifen aus violettem Purpur am Rand des einen Zelttuchs, am Ende des zusammengefügten Stückes; ebenso sollst du es am Rand des letzten Zelttuchs des außen zusammengefügten Stückes machen." },
      { number: 5, text: "Fünfzig Schleifen sollst du an dem einen Zelttuch anbringen und fünfzig Schleifen am Zelttuchende des zweiten zusammengefügten Stückes; die Schleifen sollen einander entsprechen." },
      { number: 6, text: "Du sollst auch fünfzig Goldhaken machen und die Zelttücher mit den Haken verbinden, eines mit dem andern; so soll die Wohnstätte ein Ganzes bilden." },
      { number: 7, text: "Mach Decken aus Ziegenhaar für das Zelt über der Wohnstätte; elf Stück sollst du herstellen.", footnotes: ["(7-14) 36,14-19"] },
      { number: 8, text: "Eine Decke soll dreißig Ellen lang und vier Ellen breit sein; die elf Decken sollen dasselbe Maß haben." },
      { number: 9, text: "Verbinde fünf Decken zu einem Stück und sechs Decken zu einem Stück; die sechste Decke sollst du an der Vorderseite des Zeltes doppelt zusammenlegen." },
      { number: 10, text: "Fünfzig Schleifen mach am Saum der Decke, die das Ende eines zusammengesetzten Stückes bildet, und ebenso fünfzig Schleifen am Saum der Decke, die das Ende des andern zusammengesetzten Stückes bildet." },
      { number: 11, text: "Fertige fünfzig Kupferhaken an, häng die Haken in die Schleifen und setz das Zelt so zusammen, dass es ein Ganzes bildet." },
      { number: 12, text: "Den herabhängenden überschüssigen Teil der Zeltdecke lass zur Hälfte an der Rückseite der Wohnstätte herabhängen!" },
      { number: 13, text: "Die überschüssige Elle auf beiden Längsseiten der Zeltdecken soll auf die beiden Seiten der Wohnstätte herabhängen, sodass sie sie bedeckt." },
      { number: 14, text: "Schließlich mach für das Zelt eine Decke aus rötlichen Widderfellen und darüber eine Decke aus Tahaschhäuten!", footnotes: ["Zu den Tahaschhäuten vgl. die Anmerkung zu 25,5."] },
      { number: 15, text: "Mach für die Wohnstätte Bretter aus Akazienholz zum Aufstellen!", footnotes: ["(15-25) 36,20-30"] },
      { number: 16, text: "Jedes Brett soll zehn Ellen lang und anderthalb Ellen breit sein;" },
      { number: 17, text: "jedes Brett soll durch zwei Zapfen mit dem nächsten verbunden werden. So mach es mit allen Brettern für die Wohnstätte!" },
      { number: 18, text: "An Brettern für die Wohnstätte verfertige zwanzig für die Südseite!" },
      { number: 19, text: "Stell vierzig Sockel aus Silber her als Unterlage für die zwanzig Bretter je zwei Sockel als Unterlage eines Brettes für seine beiden Zapfen!" },
      { number: 20, text: "Für die zweite Seite der Wohnstätte, die Nordseite, ebenfalls zwanzig Bretter" },
      { number: 21, text: "und vierzig Sockel aus Silber, je zwei Sockel als Unterlage eines Brettes." },
      { number: 22, text: "Für die Rückseite der Wohnstätte, die Westseite, verfertige sechs Bretter" },
      { number: 23, text: "und zwei Bretter stell für die Eckstücke an der Rückseite der Wohnstätte her!" },
      { number: 24, text: "Sie sollen einander entsprechen und von unten bis oben zum ersten Ring reichen. So soll es mit den beiden Brettern geschehen, die die Eckstücke bilden." },
      { number: 25, text: "Acht Bretter und sechzehn Sockel aus Silber sollen vorhanden sein, je zwei Sockel als Unterlage für jedes Brett." },
      { number: 26, text: "Verfertige Querlatten aus Akazienholz, fünf für die Bretter auf der einen Seite der Wohnstätte,", footnotes: ["(26-30) 36,31-34"] },
      { number: 27, text: "fünf für die Bretter der zweiten Seite der Wohnstätte und fünf für die Bretter der Rückseite der Wohnstätte, der Westseite!" },
      { number: 28, text: "Die mittlere Querlatte soll in der Mitte der Bretter angebracht werden und von einem Ende zum andern reichen." },
      { number: 29, text: "Überzieh die Bretter mit Gold und mach Ringe aus Gold, welche die Querlatten aufnehmen; auch die Querlatten überzieh mit Gold!" },
      { number: 30, text: "So errichte die Wohnstätte nach dem Muster, das dir auf dem Berg gezeigt worden ist." },
      { number: 31, text: "Mach einen Vorhang aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus; wie Kunstweberarbeit soll er gemacht werden, mit Kerubim.", footnotes: ["(31-37) 36,35-38"] },
      { number: 32, text: "Häng ihn an die vier mit Gold überzogenen Akaziensäulen, die auf vier Sockeln aus Silber stehen sollen. Auch die Nägel der Säulen sollen aus Gold sein." },
      { number: 33, text: "Häng den Vorhang an die Haken und bring dorthin, hinter den Vorhang, die Lade der Bundesurkunde! Der Vorhang trenne euch das Heiligtum vom Allerheiligsten." },
      { number: 34, text: "Setz die Deckplatte auf die Lade der Bundesurkunde im Allerheiligsten!" },
      { number: 35, text: "Stell den Tisch außen vor den Vorhang, den Leuchter gegenüber dem Tisch an die Südseite der Wohnstätte; den Tisch stell an die Nordseite!" },
      { number: 36, text: "Für den Eingang des Zeltes mach einen Vorhang aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus; Arbeit eines Buntwirkers soll er sein." },
      { number: 37, text: "Für den Vorhang mach fünf Akaziensäulen, überzieh sie mit Gold und gieß für sie fünf kupferne Sockel! Auch die Nägel der Säulen sollen aus Gold sein." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Dann mach aus Akazienholz den Altar, fünf Ellen lang und fünf Ellen breit - der Altar soll also quadratisch sein - und drei Ellen hoch.", footnotes: ["(1-8) 38,1-7"] },
      { number: 2, text: "Mach ihm Hörner an seinen vier Ecken - seine Hörner sollen mit ihm ein Ganzes bilden - und überzieh ihn mit Kupfer!" },
      { number: 3, text: "Stell auch die Gefäße her für seine Fett-Asche, seine Schaufeln und Schalen, seine Gabeln und Feuerpfannen! Alle seine Geräte sollst du aus Kupfer herstellen." },
      { number: 4, text: "Mach für den Altar ein Gitterwerk, ein Netzgitter aus Kupfer, und befestige am Netzgitter vier Kupferringe, und zwar an seinen vier Enden!" },
      { number: 5, text: "Bring das Gitterwerk unterhalb der Altareinfassung unten an! Das Netzgitter soll bis zur Mitte des Altars reichen." },
      { number: 6, text: "Verfertige für den Altar Stangen - Stangen aus Akazienholz - und überzieh sie mit Kupfer!" },
      { number: 7, text: "Man soll die Stangen in die Ringe stecken, und zwar sollen die Stangen an beiden Seiten des Altars angebracht sein, wenn man ihn trägt." },
      { number: 8, text: "Mach ihn hohl, aus Brettern! Wie man es dir auf dem Berg gezeigt hat, so soll man es ausführen." },
      { number: 9, text: "Dann mach den Vorhof der Wohnstätte: für die Südseite die Behänge des Vorhofs aus gezwirntem Byssus, hundert Ellen lang für eine Seite,", footnotes: ["(9-19) 38,9-20"] },
      { number: 10, text: "seine zwanzig Säulen und deren zwanzig Sockel aus Kupfer, die Nägel der Säulen und deren Querstangen aus Silber;" },
      { number: 11, text: "ebenso für die nördliche Längsseite Behänge, hundert Ellen lang, ihre zwanzig Säulen und deren zwanzig Sockel aus Kupfer, die Nägel der Säulen und deren Querstangen aus Silber;" },
      { number: 12, text: "für die Breitseite des Vorhofs im Westen Behänge von fünfzig Ellen, ihre zehn Säulen und deren Sockel." },
      { number: 13, text: "Der Vorhof an der Ostseite soll fünfzig Ellen breit sein," },
      { number: 14, text: "und zwar fünfzehn Ellen Behänge für die eine Seitenwand mit ihren drei Säulen und deren drei Sockel;" },
      { number: 15, text: "für die andere Seitenwand ebenso fünfzehn Ellen Behänge mit ihren drei Säulen und deren drei Sockel;" },
      { number: 16, text: "für das Tor des Vorhofs als Buntwirkerarbeit einen Vorhang von zwanzig Ellen aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus, mit ihren vier Säulen und deren vier Sockeln;" },
      { number: 17, text: "alle Säulen des Vorhofs ringsum mit Querstangen aus Silber sowie ihre Nägel aus Silber und ihre Sockel aus Kupfer." },
      { number: 18, text: "Der Vorhof soll hundert Ellen lang, fünfzig Ellen breit und fünf Ellen hoch sein, die Behänge aus gezwirntem Byssus, ihre Sockel aus Kupfer." },
      { number: 19, text: "Alle Geräte der Wohnstätte, die darin zu irgendeinem Dienst verwendet werden, alle ihre Zeltpflöcke und alle Zeltpflöcke des Vorhofs sollen aus Kupfer sein." },
      { number: 20, text: "Du aber befiehl den Israeliten, dass sie dir reines Öl aus gestoßenen Oliven für den Leuchter liefern, damit immer Licht brennt.", footnotes: ["(20-21) Lev 24,2f"] },
      { number: 21, text: "Im Offenbarungszelt außerhalb des Vorhangs vor der Bundesurkunde sollen es Aaron und seine Söhne zurichten; es soll vom Abend bis zum Morgen vor dem Herrn brennen, als eine ständig eingehaltene Verpflichtung bei den Israeliten von Generation zu Generation." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Lass aus der Mitte der Israeliten deinen Bruder Aaron und mit ihm auch seine Söhne zu dir kommen, damit sie mir als Priester dienen, Aaron mit Nadab, Abihu, Eleasar und Itamar, den Söhnen Aarons.", heading: "Die Priestergewänder: 28,1-43", footnotes: ["(1-43) 39,1-31"] },
      { number: 2, text: "Lass für deinen Bruder Aaron heilige Gewänder anfertigen, die ihm zur Ehre und zum Schmuck gereichen." },
      { number: 3, text: "Rede mit allen Sachverständigen, die ich mit dem Geist der Weisheit erfüllt habe; sie sollen Aarons Gewänder anfertigen, damit er geheiligt sei und mir als Priester dient." },
      { number: 4, text: "Das sind die Gewänder, die sie anfertigen sollen: Lostasche, Efod, Obergewand, Leibrock aus gewirktem Stoff, Turban und Gürtel. Sie sollen also für deinen Bruder Aaron und für seine Söhne heilige Gewänder anfertigen, damit er mir als Priester dient." },
      { number: 5, text: "Sie sollen dazu Gold, violetten und roten Purpur, Karmesin und Byssus verwenden." },
      { number: 6, text: "Das Efod sollen sie als Kunstweberarbeit herstellen, aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus.", footnotes: ["Zum Efod vgl. die Anmerkung zu 25,7."] },
      { number: 7, text: "Es soll zwei miteinander verbundene Schulterstücke haben, und zwar an seinen beiden Enden sollen sie miteinander verbunden sein." },
      { number: 8, text: "Die Schärpe am Efod soll von derselben Machart sein und mit ihm ein einziges Stück bilden, aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus." },
      { number: 9, text: "Nimm die beiden Karneolsteine und schneide die Namen der Söhne Israels ein:" },
      { number: 10, text: "sechs von den Namen in den einen Stein und die übrigen sechs Namen in den andern Stein, in der Reihenfolge, wie sie geboren wurden." },
      { number: 11, text: "In Steinschneidearbeit wie Siegelgravierung sollst du in die beiden Steine die Namen der Söhne Israels schneiden, in Goldfassungen eingesetzt sollst du sie herstellen." },
      { number: 12, text: "Befestige die beiden Steine an den Schulterstücken des Efod als Steine, die den Herrn an die Israeliten erinnern. Aaron soll ihre Namen auf beiden Schulterstücken vor dem Herrn zur Erinnerung tragen." },
      { number: 13, text: "Du sollst also die Fassung aus Gold" },
      { number: 14, text: "und zwei Kettchen aus purem Gold machen; dreh sie wie eine Schnur, und befestige die schnurartigen Kettchen an den Fassungen!" },
      { number: 15, text: "Mach eine Lostasche für den Schiedsspruch; als Kunstweberarbeit wie das Efod sollst du sie herstellen; aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus sollst du sie herstellen." },
      { number: 16, text: "Sie soll quadratisch sein, zusammengefaltet, eine Spanne lang und eine Spanne breit." },
      { number: 17, text: "Besetze sie mit gefassten Edelsteinen in vier Reihen: die erste Reihe mit Rubin, Topas und Smaragd;" },
      { number: 18, text: "die zweite Reihe mit Karfunkel, Saphir und Jaspis;" },
      { number: 19, text: "die dritte Reihe mit Achat, Hyazinth und Amethyst;" },
      { number: 20, text: "die vierte Reihe mit Chrysolith, Karneol und Onyx; sie sollen in Gold gefasst und eingesetzt sein." },
      { number: 21, text: "Die Steine sollen auf die Namen der Söhne Israels lauten, zwölf Steine auf ihre Namen - in Siegelgravierung. Jeder laute auf einen Namen der zwölf Stämme." },
      { number: 22, text: "Befestige an der Lostasche schnurartige, gedrehte Kettchen aus purem Gold!" },
      { number: 23, text: "An der Lostasche bring zwei goldene Ringe an und befestige die Ringe an den Enden der Lostasche!" },
      { number: 24, text: "Sodann verbinde die beiden Goldschnüre mit den beiden Ringen an den Enden der Lostasche;" },
      { number: 25, text: "die zwei andern Enden der Schnüre befestige an den beiden Einfassungen und häng sie an die Vorderseite der Schulterstücke des Efod!" },
      { number: 26, text: "Mach noch zwei Goldringe und befestige sie an den beiden Enden der Lostasche, und zwar am inneren Rand, der dem Efod zugekehrt ist." },
      { number: 27, text: "Mach zwei weitere Goldringe und befestige sie unten an den beiden Schulterstücken des Efod, und zwar an der Vorderseite, nahe ihrer Naht, aber oberhalb der Efodschärpe." },
      { number: 28, text: "Man soll die Lostasche mit ihren Ringen an die Ringe des Efod mit einer violetten Purpurschnur binden, damit sie über der Efodschärpe bleibt und sich die Lostasche nicht vom Efod verschiebt." },
      { number: 29, text: "Aaron soll die Namen der Söhne Israels in der Lostasche für den Schiedsspruch über seinem Herzen tragen, wenn er zum Heiligtum kommt, zur immer währenden Erinnerung vor dem Herrn." },
      { number: 30, text: "In die Lostasche für den Schiedsspruch steck die Lose Urim und Tummim; sie sollen sich über dem Herzen Aarons befinden, wenn er vor den Herrn kommt; Aaron trage den Schiedsspruch für die Israeliten über seinem Herzen ständig vor dem Herrn." },
      { number: 31, text: "Verfertige den Efodmantel ganz aus violettem Purpur;" },
      { number: 32, text: "in seiner Mitte soll sich eine Öffnung für den Kopf befinden; an seiner Öffnung ringsum soll wie bei der Öffnung eines Panzerhemds ein gewebter, unzerreißbarer Rand angebracht sein." },
      { number: 33, text: "An seinem unteren Saum mach Granatäpfel aus violettem und rotem Purpur und aus Karmesin, an seinem Saum ringsum, und dazwischen goldene Glöckchen ringsum:" },
      { number: 34, text: "ein Goldglöckchen und ein Granatapfel abwechselnd ringsum am Saum des Mantels." },
      { number: 35, text: "Aaron soll ihn beim Dienst tragen; sein Ton soll zu hören sein, wenn er in das Heiligtum vor den Herrn hintritt und wenn er wieder herauskommt; sonst muss er sterben." },
      { number: 36, text: "Mach eine Rosette aus purem Gold und bring darauf nach Art der Siegelgravierung die Inschrift an: Heilig dem Herrn." },
      { number: 37, text: "Befestige die Rosette an einer Schnur aus violettem Purpur und bring sie am Turban an; sie soll an der Vorderseite des Turbans angebracht werden." },
      { number: 38, text: "Sie soll auf Aarons Stirn sein, denn Aaron ist verantwortlich für die Verfehlungen an den Weihegaben, die die Israeliten weihen, für alle heiligen Abgaben; sie soll ständig auf Aarons Stirn sein, damit sie dem Herrn genehm sind." },
      { number: 39, text: "Web den Leibrock aus Byssus; mach einen Turban aus Byssus und einen Gürtel in Buntwirkerarbeit!" },
      { number: 40, text: "Für die Söhne Aarons verfertige Leibröcke und mach ihnen Gürtel! Mach für sie auch Kopfbünde, die ihnen zur Ehre und zum Schmuck gereichen." },
      { number: 41, text: "Dann bekleide damit deinen Bruder Aaron und zusammen mit ihm auch seine Söhne und salbe sie, setze sie ins Priesteramt ein und weihe sie, damit sie mir als Priester dienen." },
      { number: 42, text: "Mach ihnen Beinkleider aus Leinen, damit sie ihre Scham bedecken; von den Hüften bis zu den Schenkeln sollen sie reichen." },
      { number: 43, text: "Aaron und seine Söhne sollen sie tragen, wenn sie zum Offenbarungszelt kommen oder sich dem Altar nähern, um den Dienst am Heiligtum zu verrichten; so werden sie keine Schuld auf sich laden und nicht sterben. Als ständig einzuhaltende Verpflichtung gelte dies für ihn und seine Nachkommen." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Das ist es, was du mit ihnen tun sollst, wenn du sie zum Priesterdienst für mich weihst: Nimm einen Jungstier und zwei fehlerlose Widder,", heading: "Die Weihe der Priester: 29,1-37", footnotes: ["(1-37) Lev 8,1-36"] },
      { number: 2, text: "ungesäuerte Brote, mit Öl vermengte ungesäuerte Kuchen und mit Öl bestrichene ungesäuerte Brotfladen; aus Feinmehl sollst du sie zubereiten." },
      { number: 3, text: "Leg sie in einen Korb und bring sie im Korb herbei, dazu den Jungstier und die beiden Widder!" },
      { number: 4, text: "Lass Aaron und seine Söhne zum Eingang des Offenbarungszeltes treten, und wasche sie mit Wasser!" },
      { number: 5, text: "Nimm die Gewänder und bekleide Aaron mit Leibrock, Efodmantel, Efod und Lostasche und bind ihm die Efodschärpe um!" },
      { number: 6, text: "Setz ihm den Turban auf den Kopf und befestige das heilige Diadem am Turban!" },
      { number: 7, text: "Nimm Salböl, gieß es auf sein Haupt und salb ihn!" },
      { number: 8, text: "Lass seine Söhne herantreten und bekleide sie mit Leibröcken!" },
      { number: 9, text: "Gürte Aaron und seine Söhne mit einem Gürtel und bind ihnen die Kopfbünde um! Das Priestertum gehört dann ihnen als dauerndes Anrecht. Setz also Aaron und seine Söhne ins Priesteramt ein!" },
      { number: 10, text: "Lass den Jungstier vor das Offenbarungszelt bringen und Aaron und seine Söhne sollen ihre Hände auf den Kopf des Jungstiers legen." },
      { number: 11, text: "Dann schlachte den Jungstier vor dem Herrn am Eingang des Offenbarungszeltes!" },
      { number: 12, text: "Nimm vom Blut des Jungstiers und gib etwas davon mit deinem Finger auf die Hörner des Altars! Das ganze übrige Blut aber gieß am Sockel des Altars aus!" },
      { number: 13, text: "Nimm das ganze Fett, das die Eingeweide bedeckt, die Fettmasse über der Leber, die beiden Nieren und ihr Fett und lass es auf dem Altar in Rauch aufgehen!" },
      { number: 14, text: "Das Fleisch des Jungstiers, sein Fell und seinen Mageninhalt verbrenn im Feuer außerhalb des Lagers, denn es ist ein Sündopfer." },
      { number: 15, text: "Dann hol den einen Widder und Aaron und seine Söhne sollen ihre Hände auf den Kopf des Widders legen." },
      { number: 16, text: "Schlachte den Widder, nimm sein Blut und gieß es ringsum an den Altar!" },
      { number: 17, text: "Zerleg den Widder in seine Teile, wasche seine Eingeweide und seine Beine und leg sie auf seine übrigen Teile und auf seinen Kopf!" },
      { number: 18, text: "Dann lass den ganzen Widder auf dem Altar in Rauch aufgehen! Ein Brandopfer ist es für den Herrn zum beruhigenden Duft, ein Feueropfer für den Herrn ist es." },
      { number: 19, text: "Dann hol den zweiten Widder und Aaron und seine Söhne sollen ihre Hände auf den Kopf des Widders legen." },
      { number: 20, text: "Schlachte den Widder, nimm von seinem Blut und streich es auf das rechte Ohrläppchen Aarons und seiner Söhne, auf den Daumen ihrer rechten Hand und auf die große Zehe ihres rechten Fußes! Das übrige Blut gieß ringsum an den Altar!" },
      { number: 21, text: "Nimm vom Blut auf dem Altar und vom Salböl und bespreng damit Aaron und dessen Gewänder sowie seine Söhne und deren Gewänder! Er und seine Gewänder werden so geweiht sein und mit ihm auch seine Söhne und deren Gewänder." },
      { number: 22, text: "Dann nimm vom Widder das Fett, den Fettschwanz, das Fett, das die Eingeweide bedeckt, die Fettmasse über der Leber, die beiden Nieren und ihr Fett sowie die rechte Schenkelkeule; denn es ist der Widder der Priestereinsetzung." },
      { number: 23, text: "Nimm dazu ein rundes Brot, einen mit Öl zubereiteten Brotkuchen und einen Brotfladen aus dem Korb der ungesäuerten Brote, der sich vor dem Herrn befindet." },
      { number: 24, text: "Das alles leg Aaron und seinen Söhnen in die Hände und lass sie es vor dem Herrn hin- und herschwingen und so darbringen!" },
      { number: 25, text: "Dann nimm die Stücke wieder aus ihren Händen und lass sie auf dem Altar mit dem Brandopfer in Rauch aufgehen, zum beruhigenden Duft vor dem Herrn; ein Feueropfer für den Herrn ist es." },
      { number: 26, text: "Dann nimm die Brust des Widders der Priestereinsetzung Aarons und schwing sie vor dem Herrn zur Darbringung hin und her! Die Brust gehört dir als Anteil." },
      { number: 27, text: "Erkläre die Brust des Darbringungsritus und die Keule des Erhebungsritus als heilig; denn sie wurden vom Widder des Einsetzungsopfers genommen, dem Widder Aarons und dem seiner Söhne." },
      { number: 28, text: "Sie gehören Aaron und seinen Söhnen als dauerndes Anrecht gegenüber den Israeliten, denn es ist ein Erhebungsopfer, und zwar ein Erhebungsopfer von den Israeliten, von ihren Heilsopfern, ihren Erhebungsopfern zur Ehre des Herrn." },
      { number: 29, text: "Die heiligen Gewänder Aarons gehören nach ihm seinen Söhnen, damit man sie darin salbt und ins Priesteramt einsetzt." },
      { number: 30, text: "Sieben Tage lang soll sie derjenige von seinen Söhnen tragen, der an seiner Stelle Priester wird und ins Offenbarungszelt eintritt, um im Heiligtum den Dienst zu verrichten." },
      { number: 31, text: "Nimm den Einsetzungswidder und koch sein Fleisch an heiliger Stätte!" },
      { number: 32, text: "Aaron und seine Söhne sollen das Widderfleisch und das Brot aus dem Korb am Eingang des Offenbarungszeltes essen." },
      { number: 33, text: "Sie sollen das essen, womit sie bei ihrer Priestereinsetzung und Weihe entsühnt wurden; kein Laie darf davon essen, denn es ist heilig." },
      { number: 34, text: "Wenn vom Fleisch des Einsetzungsopfers und vom Brot etwas bis zum Morgen übrig bleibt, dann sollst du das Übriggebliebene im Feuer verbrennen; man darf es nicht essen, denn es ist heilig." },
      { number: 35, text: "Tu mit Aaron und seinen Söhnen genau so, wie ich es dir befohlen habe. Sieben Tage verwende für ihre Priestereinsetzung!" },
      { number: 36, text: "Bereite täglich einen Sündopferstier für die Entsühnung zu und entsündige den Altar, indem du ihn entsühnst. Salb ihn, um ihn zu weihen." },
      { number: 37, text: "Sieben Tage sollst du den Altar entsühnen und so weihen. Der Altar ist dann hochheilig; jeder, der den Altar berührt, wird heilig." },
      { number: 38, text: "Folgendes sollst du auf dem Altar darbringen: Tagtäglich und ständig zwei männliche einjährige Lämmer.", heading: "Vorschriften für den Kult: 29,38 - 30,38", footnotes: ["Num 28,1-10"] },
      { number: 39, text: "Das eine Lamm sollst du am Morgen, das andere zur Zeit der Abenddämmerung darbringen," },
      { number: 40, text: "dazu ein Zehntel Feinmehl, das mit einem viertel Hin Öl aus gestoßenen Oliven vermengt ist, und als Trankopfer ein viertel Hin Wein für ein Lamm." },
      { number: 41, text: "Das zweite Lamm bring zur Zeit der Abenddämmerung dar, mit einer Opfergabe und einem Trankopfer wie am Morgen, zum beruhigenden Duft als Feueropfer für den Herrn." },
      { number: 42, text: "Es soll von Generation zu Generation ein immer währendes Brandopfer am Eingang des Offenbarungszeltes vor dem Herrn sein, wo ich mich euch offenbare, um mit dir dort zu reden." },
      { number: 43, text: "Ich werde mich dort den Israeliten offenbaren und mich in meiner Herrlichkeit als heilig erweisen." },
      { number: 44, text: "Ich werde das Offenbarungszelt, den Altar, Aaron und seine Söhne heiligen und für meinen Priesterdienst weihen." },
      { number: 45, text: "Ich werde mitten unter den Israeliten wohnen und ihnen Gott sein." },
      { number: 46, text: "Sie sollen erkennen, dass ich der Herr, ihr Gott bin, der sie aus Ägypten herausgeführt hat, um in ihrer Mitte zu wohnen, ich, der Herr, ihr Gott." },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Mach auch einen Altar zum Verbrennen von Räucherwerk; aus Akazienholz sollst du ihn machen.", footnotes: ["(1-5) 37,25-28"] },
      { number: 2, text: "Er soll eine Elle lang, eine Elle breit, also quadratisch, und zwei Ellen hoch sein; seine Hörner sollen mit ihm eine Einheit bilden." },
      { number: 3, text: "Mit purem Gold überzieh ihn, seine Platte, seine Wände ringsum und seine Hörner und bring an ihm ringsum eine Goldleiste an!" },
      { number: 4, text: "Mach für ihn zwei Paar Goldringe, befestige sie unterhalb seiner Leiste an seinen beiden Seiten, seinen Seitenwänden, zum Aufnehmen der Stangen, sodass man ihn damit tragen kann." },
      { number: 5, text: "Mach die Stangen aus Akazienholz und überzieh sie mit Gold!" },
      { number: 6, text: "Stell ihn vor den Vorhang vor der Lade der Bundesurkunde, vor der Deckplatte über der Bundesurkunde! Dort will ich mich dir offenbaren." },
      { number: 7, text: "Aaron soll auf ihm Morgen für Morgen duftendes Räucherwerk verbrennen; wenn er die Lampen herrichtet, soll er es verbrennen." },
      { number: 8, text: "Wenn Aaron zur Zeit der Abenddämmerung die Lampen wieder aufsetzt, soll er das Räucherwerk verbrennen; es soll ein immer währendes Rauchopfer vor dem Herrn sein von Generation zu Generation." },
      { number: 9, text: "Ihr dürft auf ihm weder ein unerlaubtes Rauchopfer, noch ein Brandopfer, noch ein Speiseopfer darbringen; auch dürft ihr auf ihm kein Trankopfer ausgießen." },
      { number: 10, text: "An seinen Hörnern soll Aaron einmal im Jahr die Sühne für die Sünden vollziehen; mit dem Blut des Sühneopfers soll man einmal im Jahr auf ihm die Sühne vollziehen von Generation zu Generation. Etwas Hochheiliges ist es für den Herrn.", footnotes: ["Lev 16,18f"] },
      { number: 11, text: "Der Herr sprach zu Mose:" },
      { number: 12, text: "Wenn du die Zählung der Israeliten für ihre Veranlagung durchführst, soll jeder von ihnen ein Lösegeld für seine Person anlässlich der Veranlagung an den Herrn zahlen, damit sie kein Unheil wegen der Veranlagung trifft.", footnotes: ["38,21"] },
      { number: 13, text: "Jeder von ihnen, der zur Veranlagung kommt, soll einen halben Schekel, entsprechend dem Schekelgewicht des Heiligtums, entrichten: zwanzig Gera auf einen Schekel; einen halben Schekel soll die Abgabe für den Herrn betragen." },
      { number: 14, text: "Jeder von zwanzig Jahren und darüber, der zur Veranlagung kommt, soll eine Abgabe für den Herrn entrichten." },
      { number: 15, text: "Der Reiche soll nicht mehr, der Arme nicht weniger als einen halben Schekel geben, wenn ihr die Abgabe für den Herrn als Lösegeld für eure Person entrichtet." },
      { number: 16, text: "Nimm das Silber des Lösegeldes von den Israeliten und verwende es für den Dienst im Offenbarungszelt; es diene den Israeliten zur Erinnerung vor dem Herrn, als Lösegeld für eure Person." },
      { number: 17, text: "Der Herr sprach zu Mose:" },
      { number: 18, text: "Verfertige ein Becken aus Kupfer und ein Gestell aus Kupfer für die Waschungen und stell es zwischen das Offenbarungszelt und den Altar; dann füll Wasser ein!" },
      { number: 19, text: "Aaron und seine Söhne sollen darin ihre Hände und Füße waschen." },
      { number: 20, text: "Wenn sie zum Offenbarungszelt kommen, sollen sie sich mit Wasser waschen, damit sie nicht sterben. Ebenso sollen sie es halten, wenn sie zum Altar treten, um den Dienst zu verrichten und um Feueropfer für den Herrn in Rauch aufgehen zu lassen." },
      { number: 21, text: "Sie sollen sich ihre Hände und Füße waschen, damit sie nicht sterben. Dies soll für sie eine immer währende Verpflichtung sein, für Aaron und seine Nachkommen von Generation zu Generation." },
      { number: 22, text: "Der Herr sprach zu Mose:", footnotes: ["(22-33) 37,29"] },
      { number: 23, text: "Nimm dir Balsam von bester Sorte: fünfhundert Schekel erstarrte Tropfenmyrrhe, halb so viel, also zweihundertfünfzig Schekel, wohlriechenden Zimt, zweihundertfünfzig Schekel Gewürzrohr" },
      { number: 24, text: "und fünfhundert Schekel Zimtnelken, nach dem Schekelgewicht des Heiligtums, dazu ein Hin Olivenöl," },
      { number: 25, text: "und mach daraus ein heiliges Salböl, eine würzige Salbe, wie sie der Salbenmischer bereitet. Ein heiliges Salböl soll es sein." },
      { number: 26, text: "Damit salbe das Offenbarungszelt und die Lade der Bundesurkunde," },
      { number: 27, text: "den Tisch und den Leuchter mit ihren Geräten und den Rauchopferaltar," },
      { number: 28, text: "ferner den Brandopferaltar samt allen seinen Geräten und das Becken mit seinem Gestell." },
      { number: 29, text: "So sollst du sie weihen, damit sie hochheilig seien; ein jeder, der sie berührt, wird heilig." },
      { number: 30, text: "Auch Aaron und seine Söhne sollst du salben und sie weihen, damit sie mir als Priester dienen." },
      { number: 31, text: "Zu den Israeliten aber sag: Das soll euch als ein mir heiliges Salböl gelten von Generation zu Generation." },
      { number: 32, text: "Auf keinen menschlichen Körper darf es gegossen werden und ihr dürft auch keines in der gleichen Mischung herstellen; denn heilig ist es, heilig soll es euch sein." },
      { number: 33, text: "Wer eine solche Mischung herstellt oder damit einen Laien salbt, soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 34, text: "Der Herr sprach zu Mose: Nimm dir Duftstoffe, Staktetropfen, Räucherklaue, Galbanum, Gewürzkräuter und reinen Weihrauch, von jedem gleich viel," },
      { number: 35, text: "und mach Räucherwerk daraus, ein Würzgemisch, wie es der Salbenmischer herstellt, gesalzen, rein und heilig." },
      { number: 36, text: "Zerstoß einen Teil davon ganz fein und bring davon wieder einen Teil vor die Bundesurkunde im Offenbarungszelt, wo ich dir begegnen werde; hochheilig soll es euch sein." },
      { number: 37, text: "Das Räucherwerk, das du bereiten sollst - in derselben Mischung dürft ihr euch kein anderes herstellen -, soll dir als dem Herrn heilig gelten." },
      { number: 38, text: "Wer solches um des Duftes willen herstellt, soll aus seinen Stammesgenossen ausgemerzt werden." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Beauftragung von Künstlern und Handwerkern: 31,1-11" },
      { number: 2, text: "Siehe, ich habe Bezalel, den Sohn Uris, den Enkel Hurs, vom Stamm Juda, beim Namen gerufen" },
      { number: 3, text: "und ihn mit dem Geist Gottes erfüllt, mit Weisheit, mit Verstand und mit Kenntnis für jegliche Arbeit:" },
      { number: 4, text: "Pläne zu entwerfen und sie in Gold, Silber und Kupfer auszuführen" },
      { number: 5, text: "und durch Schneiden und Fassen von Steinen und durch Schnitzen von Holz allerlei Werke herzustellen." },
      { number: 6, text: "Ich habe ihm Oholiab, den Sohn Ahisamachs, vom Stamm Dan, beigegeben und allen Kunstverständigen Weisheit verliehen, damit sie alles ausführen, was ich dir aufgetragen habe:" },
      { number: 7, text: "das Offenbarungszelt, die Lade für die Bundesurkunde, die Deckplatte darauf und alle Geräte des Zeltes," },
      { number: 8, text: "den Tisch mit seinen Geräten, den Leuchter aus reinem Gold mit allen seinen Geräten und den Rauchopferaltar," },
      { number: 9, text: "den Brandopferaltar mit allen seinen Geräten und das Becken mit seinem Gestell," },
      { number: 10, text: "die gewirkten Gewänder und die heiligen Gewänder des Priesters Aaron sowie die Gewänder seiner Söhne für den Priesterdienst," },
      { number: 11, text: "das Salböl und das duftende Räucherwerk für das Heiligtum. Sie sollen alles so ausführen, wie ich es dir befohlen habe." },
      { number: 12, text: "Der Herr sprach zu Mose:", heading: "Die Feier des Sabbat: 31,12-17" },
      { number: 13, text: "Sag den Israeliten: Ihr sollt meine Sabbate halten; denn das ist ein Zeichen zwischen mir und euch von Generation zu Generation, damit man erkennt, dass ich, der Herr, es bin, der euch heiligt." },
      { number: 14, text: "Darum haltet den Sabbat; denn er soll euch heilig sein. Wer ihn entweiht, soll mit dem Tod bestraft werden. Denn jeder, der an ihm eine Arbeit verrichtet, soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 15, text: "Sechs Tage soll man arbeiten; der siebte Tag ist Sabbat, Ruhetag, heilig für den Herrn. Jeder, der am Sabbat arbeitet, soll mit dem Tod bestraft werden.", footnotes: ["20,9f; Dtn 5,12-14"] },
      { number: 16, text: "Die Israeliten sollen also den Sabbat halten, indem sie ihn von Generation zu Generation als einen ewigen Bund halten." },
      { number: 17, text: "Für alle Zeiten wird er ein Zeichen zwischen mir und den Israeliten sein. Denn in sechs Tagen hat der Herr Himmel und Erde gemacht; am siebten Tag ruhte er und atmete auf.", footnotes: ["Gen 2,2f"] },
      { number: 18, text: "¥Nachdem der Herr zu Mose auf dem Berg Sinai alles gesagt hatte, übergab er ihm die beiden Tafeln der Bundesurkunde, steinerne Tafeln, auf die der Finger Gottes geschrieben hatte.", heading: "Der Bruch des Bundes: das goldene Kalb:" },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Als das Volk sah, dass Mose noch immer nicht vom Berg herabkam, versammelte es sich um Aaron und sagte zu ihm: Komm, mach uns Götter, die vor uns herziehen. Denn dieser Mose, der Mann, der uns aus Ägypten heraufgebracht hat - wir wissen nicht, was mit ihm geschehen ist.", footnotes: ["1-35: Der Stier (im AT oft abwertend: Kalb) ist Sinnbild der Kraft und der Fruchtbarkeit. Darum werden in der Umwelt Israels die Götter oft in Stiergestalt oder mit Hörnern dargestellt. Als später Jerobeam die Nordstämme von der Dynastie Davids und vom Kult in Jerusalem losreißt, lässt er in seinen Staatsheiligtümern Bet-El und Dan mit Gold überzogene Stierbilder aufstellen, die zunächst als Symbole für den Gott Israels gedacht waren, aber vom Volk bald als Bilder des Fruchtbarkeitsgottes Baal verehrt wurden (vgl. 1 Kön 12,26-33)."] },
      { number: 2, text: "Aaron antwortete: Nehmt euren Frauen, Söhnen und Töchtern die goldenen Ringe ab, die sie an den Ohren tragen, und bringt sie her!" },
      { number: 3, text: "Da nahm das ganze Volk die goldenen Ohrringe ab und brachte sie zu Aaron." },
      { number: 4, text: "Er nahm sie von ihnen entgegen, zeichnete mit einem Griffel eine Skizze und goss danach ein Kalb. Da sagten sie: Das sind deine Götter, Israel, die dich aus Ägypten heraufgeführt haben.", footnotes: ["Er zeichnete eine Skizze: Die Bedeutung von H ist umstritten; andere Übersetzungsmöglichkeit: Er formte das Gold in einer Gussform und goss daraus . . ."] },
      { number: 5, text: "Als Aaron das sah, baute er vor dem Kalb einen Altar und rief aus: Morgen ist ein Fest zur Ehre des Herrn." },
      { number: 6, text: "Am folgenden Morgen standen sie zeitig auf, brachten Brandopfer dar und führten Tiere für das Heilsopfer herbei. Das Volk setzte sich zum Essen und Trinken und stand auf, um sich zu vergnügen." },
      { number: 7, text: "Da sprach der Herr zu Mose: Geh, steig hinunter, denn dein Volk, das du aus Ägypten heraufgeführt hast, läuft ins Verderben." },
      { number: 8, text: "Schnell sind sie von dem Weg abgewichen, den ich ihnen vorgeschrieben habe. Sie haben sich ein Kalb aus Metall gegossen und werfen sich vor ihm zu Boden. Sie bringen ihm Schlachtopfer dar und sagen: Das sind deine Götter, Israel, die dich aus Ägypten heraufgeführt haben." },
      { number: 9, text: "Weiter sprach der Herr zu Mose: Ich habe dieses Volk durchschaut: Ein störrisches Volk ist es." },
      { number: 10, text: "Jetzt lass mich, damit mein Zorn gegen sie entbrennt und sie verzehrt. Dich aber will ich zu einem großen Volk machen." },
      { number: 11, text: "Da versuchte Mose, den Herrn, seinen Gott, zu besänftigen, und sagte: Warum, Herr, ist dein Zorn gegen dein Volk entbrannt? Du hast es doch mit großer Macht und starker Hand aus Ägypten herausgeführt." },
      { number: 12, text: "Sollen etwa die Ägypter sagen können: In böser Absicht hat er sie herausgeführt, um sie im Gebirge umzubringen und sie vom Erdboden verschwinden zu lassen? Lass ab von deinem glühenden Zorn und lass dich das Böse reuen, das du deinem Volk antun wolltest." },
      { number: 13, text: "Denk an deine Knechte, an Abraham, Isaak und Israel, denen du mit einem Eid bei deinem eigenen Namen zugesichert und gesagt hast: Ich will eure Nachkommen zahlreich machen wie die Sterne am Himmel, und: Dieses ganze Land, von dem ich gesprochen habe, will ich euren Nachkommen geben und sie sollen es für immer besitzen.", footnotes: ["Gen 22,17; 26,4; 17,8"] },
      { number: 14, text: "Da ließ sich der Herr das Böse reuen, das er seinem Volk angedroht hatte." },
      { number: 15, text: "Mose kehrte um und stieg den Berg hinab, die zwei Tafeln der Bundesurkunde in der Hand, die Tafeln, die auf beiden Seiten beschrieben waren. Auf der einen wie auf der andern Seite waren sie beschrieben." },
      { number: 16, text: "Die Tafeln hatte Gott selbst gemacht und die Schrift, die auf den Tafeln eingegraben war, war Gottes Schrift." },
      { number: 17, text: "Josua hörte das Lärmen und Schreien des Volkes und sagte zu Mose: Horch, Krieg ist im Lager." },
      { number: 18, text: "Mose antwortete: Nicht Siegesgeschrei, auch nicht Geschrei nach Niederlage ist das Geschrei, das ich höre." },
      { number: 19, text: "Als Mose dem Lager näher kam und das Kalb und den Tanz sah, entbrannte sein Zorn. Er schleuderte die Tafeln fort und zerschmetterte sie am Fuß des Berges." },
      { number: 20, text: "Dann packte er das Kalb, das sie gemacht hatten, verbrannte es im Feuer und zerstampfte es zu Staub. Den Staub streute er in Wasser und gab es den Israeliten zu trinken." },
      { number: 21, text: "Zu Aaron sagte Mose: Was hat dir dieses Volk getan, dass du ihm eine so große Schuld aufgeladen hast?" },
      { number: 22, text: "Aaron erwiderte: Mein Herr möge sich doch nicht vom Zorn hinreißen lassen. Du weißt doch, wie böse das Volk ist." },
      { number: 23, text: "Sie haben zu mir gesagt: Mach uns Götter, die uns vorangehen. Denn dieser Mose, der Mann, der uns aus Ägypten heraufgeführt hat - wir wissen nicht, was mit ihm geschehen ist." },
      { number: 24, text: "Da habe ich zu ihnen gesagt: Wer Goldschmuck trägt, soll ihn ablegen. Sie haben mir das Gold übergeben, ich habe es ins Feuer geworfen und herausgekommen ist dieses Kalb." },
      { number: 25, text: "Mose sah, wie verwildert das Volk war. Denn Aaron hatte es verwildern lassen, zur Schadenfreude ihrer Widersacher." },
      { number: 26, text: "Mose trat an das Lagertor und sagte: Wer für den Herrn ist, her zu mir! Da sammelten sich alle Leviten um ihn." },
      { number: 27, text: "Er sagte zu ihnen: So spricht der Herr, der Gott Israels: Jeder lege sein Schwert an. Zieht durch das Lager von Tor zu Tor! Jeder erschlage seinen Bruder, seinen Freund, seinen Nächsten." },
      { number: 28, text: "Die Leviten taten, was Mose gesagt hatte. Vom Volk fielen an jenem Tag gegen dreitausend Mann." },
      { number: 29, text: "Dann sagte Mose: Füllt heute eure Hände mit Gaben für den Herrn! Denn jeder von euch ist heute gegen seinen Sohn und seinen Bruder vorgegangen und der Herr hat Segen auf euch gelegt.", footnotes: ["Die Hände mit Gaben füllen war ein Ritus der Priestereinsetzung. Die Priester mussten dabei Opfergaben in die Hände nehmen, die sie Gott darbrachten."] },
      { number: 30, text: "Am folgenden Morgen sprach Mose zum Volk: Ihr habt eine große Sünde begangen. Jetzt will ich zum Herrn hinaufsteigen; vielleicht kann ich für eure Sünde Sühne erwirken." },
      { number: 31, text: "Mose kehrte zum Herrn zurück und sagte: Ach, dieses Volk hat eine große Sünde begangen. Götter aus Gold haben sie sich gemacht." },
      { number: 32, text: "Doch jetzt nimm ihre Sünde von ihnen! Wenn nicht, dann streich mich aus dem Buch, das du angelegt hast." },
      { number: 33, text: "Der Herr antwortete Mose: Nur den, der gegen mich gesündigt hat, streiche ich aus meinem Buch." },
      { number: 34, text: "Aber jetzt geh, führe das Volk, wohin ich dir gesagt habe. Mein Engel wird vor dir hergehen. Am Tag aber, an dem ich Rechenschaft verlange, werde ich über ihre Sünde mit ihnen abrechnen." },
      { number: 35, text: "Der Herr schlug das Volk mit Unheil, weil sie das Kalb gemacht hatten, das Aaron anfertigen ließ.", footnotes: ["gemacht, andere Übersetzungsmöglichkeit (nach den alten Übersetzungen): verehrt."] },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Der Herr sprach zu Mose: Geh, zieh mit dem Volk, das du aus Ägypten heraufgeführt hast, fort von hier, in das Land hinauf, von dem ich Abraham, Isaak und Jakob mit einem Eid versichert habe: Deinen Nachkommen gebe ich es." },
      { number: 2, text: "Ich sende einen Engel, der dir vorangeht, und ich vertreibe die Kanaaniter, Amoriter, Hetiter, Perisiter, Hiwiter und Jebusiter." },
      { number: 3, text: "In ein Land, in dem Milch und Honig fließen, wirst du kommen. Ich selbst ziehe nicht in deiner Mitte hinauf, denn du bist ein störrisches Volk. Es könnte sonst geschehen, dass ich dich unterwegs vertilge." },
      { number: 4, text: "Als das Volk diese Drohung hörte, trauerten sie und keiner legte seinen Schmuck an." },
      { number: 5, text: "Da sprach der Herr zu Mose: Sag zu den Israeliten: Ihr seid ein störrisches Volk. Wenn ich auch nur einen einzigen Augenblick mit dir zöge, müsste ich dir ein Ende machen. Jetzt aber leg deinen Schmuck ab! Dann will ich sehen, was ich mit dir tun kann." },
      { number: 6, text: "Da trugen die Israeliten keinen Schmuck mehr, vom Berg Horeb an." },
      { number: 7, text: "Mose nahm das Zelt und schlug es für sich außerhalb des Lagers auf, in einiger Entfernung vom Lager. Er nannte es Offenbarungszelt. Wenn einer den Herrn aufsuchen wollte, ging er zum Offenbarungszelt vor das Lager hinaus.", heading: "Die Erneuerung des Bundes: 33,7 - 34,35" },
      { number: 8, text: "Wenn Mose zum Zelt hinausging, erhob sich das ganze Volk. Jeder trat vor sein Zelt und sie schauten Mose nach, bis er in das Zelt eintrat." },
      { number: 9, text: "Sobald Mose das Zelt betrat, ließ sich die Wolkensäule herab und blieb am Zelteingang stehen. Dann redete der Herr mit Mose." },
      { number: 10, text: "Wenn das ganze Volk die Wolkensäule am Zelteingang stehen sah, erhoben sich alle und warfen sich vor ihren Zelten zu Boden." },
      { number: 11, text: "Der Herr und Mose redeten miteinander Auge in Auge, wie Menschen miteinander reden. Wenn Mose aber dann ins Lager zurückging, wich sein Diener Josua, der Sohn Nuns, ein junger Mann, nicht vom Zelt." },
      { number: 12, text: "Mose sagte zum Herrn: Du sagst zwar zu mir: Führ dieses Volk hinauf! Du hast mich aber nicht wissen lassen, wen du mitschickst. Du hast doch gesagt: Ich kenne deinen Namen und habe dir meine Gnade geschenkt." },
      { number: 13, text: "Wenn ich aber wirklich deine Gnade gefunden habe, so lass mich doch deinen Weg wissen! Dann werde ich dich erkennen und es wird sich bestätigen, dass ich deine Gnade gefunden habe. Sieh diese Leute an: Es ist doch dein Volk!" },
      { number: 14, text: "Der Herr antwortete: Mein Angesicht wird mitgehen, bis ich dir Ruhe verschafft habe." },
      { number: 15, text: "Mose entgegnete dem Herrn: Wenn dein Angesicht nicht mitgeht, dann führ uns lieber nicht von hier hinauf!" },
      { number: 16, text: "Woran soll man erkennen, dass ich zusammen mit deinem Volk deine Gnade gefunden habe? Doch wohl daran, dass du mit uns ziehst. Und dann werden wir, ich und dein Volk, vor allen Völkern auf der Erde ausgezeichnet werden." },
      { number: 17, text: "Der Herr erwiderte Mose: Auch das, was du jetzt verlangt hast, will ich tun; denn du hast nun einmal meine Gnade gefunden und ich kenne dich mit Namen." },
      { number: 18, text: "Dann sagte Mose: Lass mich doch deine Herrlichkeit sehen!" },
      { number: 19, text: "Der Herr gab zur Antwort: Ich will meine ganze Schönheit vor dir vorüberziehen lassen und den Namen des Herrn vor dir ausrufen. Ich gewähre Gnade, wem ich will, und ich schenke Erbarmen, wem ich will." },
      { number: 20, text: "Weiter sprach er: Du kannst mein Angesicht nicht sehen; denn kein Mensch kann mich sehen und am Leben bleiben." },
      { number: 21, text: "Dann sprach der Herr: Hier, diese Stelle da! Stell dich an diesen Felsen!" },
      { number: 22, text: "Wenn meine Herrlichkeit vorüberzieht, stelle ich dich in den Felsspalt und halte meine Hand über dich, bis ich vorüber bin." },
      { number: 23, text: "Dann ziehe ich meine Hand zurück und du wirst meinen Rücken sehen. Mein Angesicht aber kann niemand sehen." },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Weiter sprach der Herr zu Mose: Hau dir zwei steinerne Tafeln zurecht wie die ersten! Ich werde darauf die Worte schreiben, die auf den ersten Tafeln standen, die du zerschmettert hast." },
      { number: 2, text: "Halte dich für morgen früh bereit! Steig am Morgen auf den Sinai und dort auf dem Gipfel des Berges stell dich vor mich hin!" },
      { number: 3, text: "Niemand soll mit dir hinaufsteigen; auch soll sich kein Mensch auf dem ganzen Berg sehen lassen und kein Schaf oder Rind soll am Abhang des Berges weiden." },
      { number: 4, text: "Da hieb Mose zwei Tafeln aus Stein zurecht wie die ersten. Am Morgen stand Mose zeitig auf und ging auf den Sinai hinauf, wie es ihm der Herr aufgetragen hatte. Die beiden steinernen Tafeln nahm er mit." },
      { number: 5, text: "Der Herr aber stieg in der Wolke herab und stellte sich dort neben ihn hin. Er rief den Namen Jahwe aus." },
      { number: 6, text: "Der Herr ging an ihm vorüber und rief: Jahwe ist ein barmherziger und gnädiger Gott, langmütig, reich an Huld und Treue:", footnotes: ["(6f) 20,5f; Num 14,18; Dtn 5,9f"] },
      { number: 7, text: "Er bewahrt Tausenden Huld, nimmt Schuld, Frevel und Sünde weg, lässt aber (den Sünder) nicht ungestraft; er verfolgt die Schuld der Väter an den Söhnen und Enkeln, an der dritten und vierten Generation." },
      { number: 8, text: "Sofort verneigte sich Mose bis zur Erde und warf sich zu Boden." },
      { number: 9, text: "Er sagte: Wenn ich deine Gnade gefunden habe, mein Herr, dann ziehe doch mein Herr mit uns. Es ist zwar ein störrisches Volk, doch vergib uns unsere Schuld und Sünde und lass uns dein Eigentum sein!" },
      { number: 10, text: "Da sprach der Herr: Hiermit schließe ich einen Bund: Vor deinem ganzen Volk werde ich Wunder wirken, wie sie auf der ganzen Erde und unter allen Völkern nie geschehen sind. Das ganze Volk, in dessen Mitte du bist, wird die Taten des Herrn sehen; denn was ich mit dir vorhabe, wird Furcht erregen." },
      { number: 11, text: "Halte dich an das, was ich dir heute auftrage. Ich werde die Amoriter, Kanaaniter, Hetiter, Perisiter, Hiwiter und Jebusiter vor dir vertreiben." },
      { number: 12, text: "Du hüte dich aber, mit den Bewohnern des Landes, in das du kommst, einen Bund zu schließen; sie könnten dir sonst, wenn sie in deiner Mitte leben, zu einer Falle werden." },
      { number: 13, text: "Ihre Altäre sollt ihr vielmehr niederreißen, ihre Steinmale zerschlagen, ihre Kultpfähle umhauen." },
      { number: 14, text: "Du darfst dich nicht vor einem andern Gott niederwerfen. Denn Jahwe trägt den Namen «der Eifersüchtige»; ein eifersüchtiger Gott ist er." },
      { number: 15, text: "Hüte dich, einen Bund mit den Bewohnern des Landes zu schließen. Sonst werden sie dich einladen, wenn sie mit ihren Göttern Unzucht treiben und ihren Göttern Schlachtopfer darbringen, und du wirst von ihren Schlachtopfern essen." },
      { number: 16, text: "Du wirst von ihren Töchtern für deine Söhne Frauen nehmen; sie werden mit ihren Göttern Unzucht treiben und auch deine Söhne zur Unzucht mit ihren Göttern verführen." },
      { number: 17, text: "Du sollst dir keine Götter aus Metall gießen." },
      { number: 18, text: "Du sollst das Fest der ungesäuerten Brote halten. Im Monat Abib sollst du zur festgesetzten Zeit sieben Tage lang ungesäuertes Brot essen, wie ich es dir geboten habe. Denn im Monat Abib bist du aus Ägypten ausgezogen.", footnotes: ["23,15"] },
      { number: 19, text: "Das Erste, was den Mutterschoß durchbricht, jeder männliche Erstling beim Vieh, bei deinen Rindern und Schafen, gehört mir.", footnotes: ["13,12f; 22,28f"] },
      { number: 20, text: "Den Erstling vom Esel aber sollst du durch ein Schaf auslösen. Willst du ihn nicht auslösen, dann brich ihm das Genick! Jeden Erstgeborenen deiner Söhne musst du auslösen. Man soll vor mir nicht mit leeren Händen erscheinen." },
      { number: 21, text: "Sechs Tage sollst du arbeiten, am siebten Tag sollst du ruhen; selbst zur Zeit des Pflügens und des Erntens sollst du ruhen.", footnotes: ["20,9f; 23,12"] },
      { number: 22, text: "Du sollst das Wochenfest feiern, das Fest der Erstlingsfrüchte von der Weizenernte und das Fest der Lese an der Jahreswende." },
      { number: 23, text: "Dreimal im Jahr sollen alle deine Männer vor dem Herrn, dem Gott Israels, erscheinen." },
      { number: 24, text: "Wenn ich die Völker vor dir vertrieben und deine Grenzen weiter vorgeschoben habe, wird niemand in dein Land einfallen, während du dreimal im Jahr hinaufziehst, um vor dem Herrn, deinem Gott, zu erscheinen." },
      { number: 25, text: "Beim Schlachten sollst du das Blut meines Opfers nicht über gesäuertes Brot fließen lassen und vom Schlachttier des Paschafestes darf nichts bis zum Morgen liegen bleiben.", footnotes: ["23,18f"] },
      { number: 26, text: "Von den Erstlingsfrüchten deines Ackers sollst du die besten in das Haus des Herrn, deines Gottes, bringen. Das Junge einer Ziege sollst du nicht in der Milch seiner Mutter kochen." },
      { number: 27, text: "Dann sprach der Herr zu Mose: Schreib diese Worte auf! Denn aufgrund dieser Worte schließe ich mit dir und mit Israel einen Bund." },
      { number: 28, text: "Mose blieb dort beim Herrn vierzig Tage und vierzig Nächte. Er aß kein Brot und trank kein Wasser. Er schrieb die Worte des Bundes, die zehn Worte, auf Tafeln." },
      { number: 29, text: "Als Mose vom Sinai herunterstieg, hatte er die beiden Tafeln der Bundesurkunde in der Hand. Während Mose vom Berg herunterstieg, wusste er nicht, dass die Haut seines Gesichtes Licht ausstrahlte, weil er mit dem Herrn geredet hatte." },
      { number: 30, text: "Als Aaron und alle Israeliten Mose sahen, strahlte die Haut seines Gesichtes Licht aus und sie fürchteten sich, in seine Nähe zu kommen." },
      { number: 31, text: "Erst als Mose sie rief, kamen Aaron und alle Sippenhäupter der Gemeinde zu ihm zurück und Mose redete mit ihnen." },
      { number: 32, text: "Dann kamen alle Israeliten herbei und er übergab ihnen alle Gebote, die der Herr ihm auf dem Sinai mitgeteilt hatte." },
      { number: 33, text: "Als Mose aufhörte, mit ihnen zu reden, legte er über sein Gesicht einen Schleier." },
      { number: 34, text: "Wenn Mose zum Herrn hineinging, um mit ihm zu reden, nahm er den Schleier ab, bis er wieder herauskam. Wenn er herauskam, trug er den Israeliten alles vor, was ihm aufgetragen worden war." },
      { number: 35, text: "Wenn die Israeliten das Gesicht des Mose sahen und merkten, dass die Haut seines Gesichtes Licht ausstrahlte, legte er den Schleier über sein Gesicht, bis er wieder hineinging, um mit dem Herrn zu reden." },
    ] },
    { number: 35, verses: [
      { number: 1, text: "Mose versammelte die ganze Gemeinde der Israeliten und sagte zu ihnen: Das ist es, was der Herr euch zu tun befohlen hat:", heading: "Ausführung der kultischen Anordnungen 35,1 - 40,38 | Das Gesetz über den Sabbat: 35,1-3" },
      { number: 2, text: "Sechs Tage soll man arbeiten; der siebte Tag ist heilig, Sabbat, Ruhetag zur Ehre des Herrn. Jeder, der an ihm arbeitet, soll mit dem Tod bestraft werden.", footnotes: ["20,9f; 31,15; Dtn 5,12-14"] },
      { number: 3, text: "Am Sabbat sollt ihr in keiner eurer Wohnstätten Feuer anzünden." },
      { number: 4, text: "Mose sagte zur ganzen Gemeinde der Israeliten: Der Herr hat Folgendes befohlen:", heading: "Die Spenden für das Heiligtum: 35,4-29", footnotes: ["(4-29) 25,1-9"] },
      { number: 5, text: "Erhebt von euch eine Abgabe für den Herrn! Jeder, den sein Herz dazu bewegt, soll eine Abgabe für den Herrn bringen: Gold, Silber, Kupfer," },
      { number: 6, text: "violetten und roten Purpur, Karmesin, Byssus, Ziegenhaare," },
      { number: 7, text: "rötliche Widderfelle, Tahaschhäute und Akazienholz," },
      { number: 8, text: "Öl für den Leuchter, Balsam für das Salböl und für das duftende Räucherwerk," },
      { number: 9, text: "Karneolsteine und Ziersteine für Efod und Lostasche." },
      { number: 10, text: "Alle Sachverständigen unter euch sollen kommen und alles anfertigen, was der Herr angeordnet hat:" },
      { number: 11, text: "die Wohnstätte, ihr Zelt und seine Decke, seine Haken, Bretter, Querlatten, Säulen und Sockel," },
      { number: 12, text: "die Lade und ihre Stangen, die Deckplatte und den Vorhang," },
      { number: 13, text: "den Tisch, seine Stangen, alle seine Geräte und die Schaubrote," },
      { number: 14, text: "den Leuchter mit seinen Geräten und Lampen sowie das Öl für den Leuchter," },
      { number: 15, text: "den Räucheraltar mit seinen Stangen, das Salböl, das duftende Räucherwerk und den Türvorhang für den Eingang der Wohnstätte," },
      { number: 16, text: "den Brandopferaltar, sein Gitter aus Kupfer, seine Stangen, all sein Gerät, das Becken und sein Gestell," },
      { number: 17, text: "die Behänge des Vorhofs, seine Säulen und Sockel sowie den Vorhang für das Tor des Vorhofs," },
      { number: 18, text: "die Zeltpflöcke der Wohnstätte und des Vorhofs mit ihren Stricken," },
      { number: 19, text: "die gewirkten Gewänder für den Dienst im Heiligtum, die heiligen Gewänder für den Priester Aaron und die Gewänder für seine Söhne zum Priesterdienst." },
      { number: 20, text: "Da ging die ganze Gemeinde der Israeliten weg von Mose;" },
      { number: 21, text: "als sie wiederkamen, brachte jeder, den sein Herz bewog, und jeder, den sein Geist dazu antrieb, die Abgabe für den Herrn zur Herstellung des Offenbarungszeltes und für seinen gesamten Dienst und die heiligen Gewänder." },
      { number: 22, text: "Männer und Frauen kamen; alle brachten auf eigenen Antrieb hin Spangen, Nasenreife und Fingerringe, Halsketten, allerlei Goldgegenstände, jeder, der Gold für den Herrn als Weihegabe bringen wollte." },
      { number: 23, text: "Alle, die etwas an violettem und rotem Purpur, Karmesin, Byssus, Ziegenhaaren, rötlichen Widderfellen oder Tahaschhäuten bei sich fanden, brachten es." },
      { number: 24, text: "Alle, die eine Abgabe an Silber oder Kupfer leisten wollten, brachten die Abgabe für den Herrn; alle, die Akazienholz beschaffen konnten, geeignet für jede Bearbeitung, brachten es herbei." },
      { number: 25, text: "Alle Frauen, die sich auf Handarbeit verstanden, spannen und brachten das Gesponnene: violetten und roten Purpur, Karmesin und Byssus." },
      { number: 26, text: "Alle kunstfertigen Frauen, die sich angespornt fühlten, spannen Ziegenhaare." },
      { number: 27, text: "Die Sippenhäupter brachten Karneolsteine und Ziersteine für Efod und Lostasche," },
      { number: 28, text: "Balsam und Öl für den Leuchter, für das Salböl und für das duftende Räucherwerk." },
      { number: 29, text: "Alle Männer und Frauen, die ihr Herz dazu trieb, etwas zu irgendeiner Arbeit beizutragen, die der Herr durch Mose anzufertigen befohlen hatte, alle diese Israeliten brachten eine Spende für den Herrn." },
      { number: 30, text: "Dann sagte Mose zu den Israeliten: Seht, der Herr hat Bezalel, den Sohn Uris, des Sohnes Hurs, vom Stamm Juda, beim Namen gerufen", heading: "Die Beauftragung von Künstlern und Handwerkern: 35,30 - 36,7", footnotes: ["(30-35) 31,1-11"] },
      { number: 31, text: "und ihn mit dem Geist Gottes erfüllt, mit Weisheit, Klugheit und Kenntnis für jegliche Arbeit," },
      { number: 32, text: "Pläne zu entwerfen und sie in Gold, Silber und Kupfer auszuführen" },
      { number: 33, text: "und durch Schneiden und Fassen von Steinen und durch Schnitzen von Holz allerlei Kunstwerke herzustellen." },
      { number: 34, text: "Auch hat er ihm und Oholiab, dem Sohn Ahisamachs, vom Stamm Dan, die Gabe verliehen, andere zu lehren." },
      { number: 35, text: "Er hat sie erfüllt mit Kunstsinn zum Ausführen jeder Arbeit eines Steinschneiders, eines Kunstwebers und eines Buntwirkers in violettem und rotem Purpur, Karmesin und Byssus sowie eines Webers. Sie können alle Arbeiten ausführen und die Pläne dazu entwerfen." },
    ] },
    { number: 36, verses: [
      { number: 1, text: "Bezalel und Oholiab und alle kunstverständigen Männer, denen der Herr Weisheit und Klugheit zum Entwerfen und Ausführen der ganzen Arbeiten für den Dienst am Heiligtum gegeben hat, sollen alles tun, was der Herr angeordnet hat." },
      { number: 2, text: "Mose berief also Bezalel, Oholiab und alle kunstverständigen Männer, denen der Herr Weisheit in ihr Herz gegeben hatte, jeden, den sein Herz antrieb, sich ans Werk zu machen und es durchzuführen." },
      { number: 3, text: "Sie nahmen von Mose alle Abgaben entgegen, die die Israeliten gebracht hatten, damit die Arbeiten für den Dienst am Heiligtum ausgeführt werden könnten. Man brachte ihm auch weiterhin Morgen für Morgen Spenden." },
      { number: 4, text: "Alle Kunstverständigen, die alle Arbeiten für das Heiligtum ausführten, kamen aber - jeder von seiner Arbeit, die er gerade verrichtete -" },
      { number: 5, text: "und sagten zu Mose: Das Volk bringt mehr, als man für die Arbeit benötigt, die der Herr auszuführen befahl." },
      { number: 6, text: "Da erließ Mose einen Befehl und ließ im Lager ausrufen: Weder Mann noch Frau soll sich weiterhin um eine Abgabe für das Heiligtum bemühen. So bewog man das Volk, nichts mehr zu bringen." },
      { number: 7, text: "Es war Material mehr als genug vorhanden, um alle Arbeiten durchzuführen." },
      { number: 8, text: "Die Kunstverständigen unter den Arbeitern verfertigten die Wohnstätte aus zehn Zelttüchern: aus gezwirntem Byssus, violettem und rotem Purpur und Karmesin, mit Kerubimfiguren; als Kunstweberarbeit machten sie es.", heading: "Die Ausstattung des Heiligtums: 36,8 - 38,31", footnotes: ["(8-13) 26,1-6"] },
      { number: 9, text: "Ein Zelttuch war achtundzwanzig Ellen lang und vier Ellen breit; alle Zelttücher hatten dasselbe Maß." },
      { number: 10, text: "Er fügte jeweils fünf Zelttücher aneinander." },
      { number: 11, text: "Dann machte er Schleifen aus violettem Purpur am Rand des einen Zelttuchs, das das Ende des zusammengefügten Stückes bildete; ebenso machte er es am Rande des letzten Zelttuchs des anderen zusammengefügten Stückes." },
      { number: 12, text: "Fünfzig Schleifen brachte er an dem einen Zelttuch an und fünfzig Schleifen am Zelttuchende des zweiten zusammengefügten Stückes; die Schleifen entsprachen einander." },
      { number: 13, text: "Dann machte er fünfzig Goldhaken und verband die Zelttücher mit den Haken, eines mit dem andern; so wurde die Wohnstätte ein Ganzes." },
      { number: 14, text: "Er machte Decken aus Ziegenhaar für das Zelt über der Wohnstätte; elf Decken stellte er her.", footnotes: ["(14-19) 26,7-14"] },
      { number: 15, text: "Eine Decke war dreißig Ellen lang und vier Ellen breit; die elf Decken hatten dasselbe Maß." },
      { number: 16, text: "Er verband fünf Decken zu einem Stück und sechs Decken zu einem Stück." },
      { number: 17, text: "Er machte fünfzig Schleifen am Saum der Decke, die das Ende des einen zusammengesetzten Stückes bildete, und ebenso fünfzig Schleifen am Saum der Decke, welche das Ende des anderen zusammengesetzten Stückes bildete." },
      { number: 18, text: "Er fertigte fünfzig Kupferhaken an, um das Zelt zusammenzusetzen, damit es ein Ganzes ergäbe." },
      { number: 19, text: "Schließlich machte er für das Zelt eine Decke aus rötlichen Widderfellen und darüber eine Decke aus Tahaschhäuten." },
      { number: 20, text: "Er machte für die Wohnstätte Bretter aus Akazienholz zum Aufstellen.", footnotes: ["(20-30) 26,15-25"] },
      { number: 21, text: "Jedes Brett war zehn Ellen lang und anderthalb Ellen breit;" },
      { number: 22, text: "jedes Brett wurde durch zwei Zapfen mit dem nächsten verbunden. So machte er es mit allen Brettern der Wohnstätte." },
      { number: 23, text: "An Brettern für die Wohnstätte verfertigte er zwanzig für die Südseite." },
      { number: 24, text: "Er stellte vierzig Sockel aus Silber als Unterlage für die zwanzig Bretter her: je zwei Sockel als Unterlage eines Brettes für seine beiden Zapfen." },
      { number: 25, text: "Für die zweite Seite der Wohnstätte, die Nordseite, machte er ebenfalls zwanzig Bretter" },
      { number: 26, text: "und vierzig Sockel aus Silber, je zwei Sockel als Unterlage eines Brettes." },
      { number: 27, text: "Für die Rückseite der Wohnstätte, die Westseite, verfertigte er sechs Bretter" },
      { number: 28, text: "und zwei Bretter stellte er für die Eckstücke an der Rückseite der Wohnstätte her." },
      { number: 29, text: "Sie entsprachen einander und reichten von unten bis oben, zum ersten Ring. So machte er es mit den beiden Brettern, die die beiden Eckstücke bildeten." },
      { number: 30, text: "Es waren also acht Bretter und sechzehn Sockel aus Silber vorhanden: je zwei Sockel als Unterlage für ein Brett." },
      { number: 31, text: "Er verfertigte Querlatten aus Akazienholz, fünf für die Bretter auf der einen Seite der Wohnstätte,", footnotes: ["(31-34) 26,26-30"] },
      { number: 32, text: "fünf für die Bretter auf der zweiten Seite der Wohnstätte und fünf für die Bretter der Rückseite der Wohnstätte, der Westseite." },
      { number: 33, text: "Er brachte die mittlere Querlatte in der Mitte der Bretter an; sie reichte von einem Ende zum andern." },
      { number: 34, text: "Er überzog die Bretter mit Gold und machte Ringe aus Gold, die die Querlatten aufnahmen; auch die Querlatten überzog er mit Gold." },
      { number: 35, text: "Er machte einen Vorhang aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus; er machte ihn in Kunstweberarbeit, mit Kerubimfiguren.", footnotes: ["(35-38) 26,31-37"] },
      { number: 36, text: "Er machte für ihn vier Akaziensäulen, überzog sie mit Gold - auch die Nägel waren aus Gold - und goss für sie vier Sockel aus Silber." },
      { number: 37, text: "Für den Eingang des Zeltes machte er einen Vorhang aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus in Buntwirkerarbeit" },
      { number: 38, text: "sowie seine fünf Säulen mit ihren Nägeln und überzog ihre Kapitelle und ihre Querstangen mit Gold; ihre fünf Sockel waren aus Kupfer." },
    ] },
    { number: 37, verses: [
      { number: 1, text: "Dann machte Bezalel die Lade aus Akazienholz, zweieinhalb Ellen lang, anderthalb Ellen breit und anderthalb Ellen hoch.", footnotes: ["(1-9) 25,10-22"] },
      { number: 2, text: "Er überzog sie innen und außen mit purem Gold und brachte daran ringsherum eine Goldleiste an." },
      { number: 3, text: "Er goss für sie vier Goldringe und befestigte sie an ihren vier Füßen, zwei Ringe an der einen Seite und zwei Ringe an der anderen Seite." },
      { number: 4, text: "Er fertigte Stangen aus Akazienholz an und überzog sie mit Gold." },
      { number: 5, text: "Er steckte die Stangen durch die Ringe an den Seiten der Lade, sodass man damit die Lade tragen konnte." },
      { number: 6, text: "Er verfertigte auch eine Deckplatte aus purem Gold, zweieinhalb Ellen lang und anderthalb Ellen breit." },
      { number: 7, text: "Er machte zwei Kerubim aus getriebenem Gold und arbeitete sie aus den beiden Enden der Deckplatte heraus," },
      { number: 8, text: "und zwar einen Kerub an dem einen Ende und einen Kerub an dem andern Ende der Deckplatte; er arbeitete die Kerubim an ihren beiden Enden heraus." },
      { number: 9, text: "Die Kerubim breiteten ihre Flügel nach oben aus; mit ihren Flügeln beschirmten sie die Deckplatte und sie wandten ihre Gesichter einander zu; die Gesichter waren der Deckplatte zugewandt." },
      { number: 10, text: "Er fertigte auch den Tisch aus Akazienholz an, zwei Ellen lang, eine Elle breit und anderthalb Ellen hoch.", footnotes: ["(10-16) 25,23-30"] },
      { number: 11, text: "Er überzog ihn mit purem Gold und brachte daran ringsherum eine Goldleiste an." },
      { number: 12, text: "Er machte ihm ringsherum eine handbreite Einfassung und verfertigte um diese Einfassung eine goldene Leiste." },
      { number: 13, text: "Er goss für ihn vier Goldringe und befestigte die Ringe an den vier Ecken, die von seinen vier Füßen gebildet wurden." },
      { number: 14, text: "Die Ringe waren dicht unter der Einfassung, sodass sie die Stangen aufnahmen und man den Tisch tragen konnte." },
      { number: 15, text: "Er machte die Stangen aus Akazienholz und überzog sie mit Gold, sodass man den Tisch tragen konnte." },
      { number: 16, text: "Dazu machte er die Geräte, die auf dem Tisch stehen sollten: seine Schüsseln, Schalen, Kannen und Krüge für die Trankopfer - alles aus purem Gold." },
      { number: 17, text: "Er machte den Leuchter aus purem Gold. Der Leuchter, sein Gestell und sein Schaft, seine Kelche, Knospen und Blüten waren aus einem Stück getrieben.", footnotes: ["(17-24) 25,31-39"] },
      { number: 18, text: "Von seinen Seiten gingen sechs Arme aus, drei Leuchterarme auf der einen Seite und drei Leuchterarme auf der anderen Seite." },
      { number: 19, text: "Der erste Arm wies drei mandelblütenförmige Kelche auf mit je einer Knospe und einer Blüte und der zweite Arm wies drei mandelblütenförmige Kelche auf mit je einer Knospe und einer Blüte; so alle sechs Arme, die von dem Leuchter ausgingen." },
      { number: 20, text: "An dem Leuchter waren vier mandelblütenförmige Kelche, Knospen und Blüten:" },
      { number: 21, text: "je eine Knospe unten zwischen zwei Armen, entsprechend den sechs Armen, die vom Leuchter ausgingen." },
      { number: 22, text: "Die Knospen und Arme bildeten mit dem Schaft ein Ganzes; das Ganze war ein Stück aus getriebenem purem Gold." },
      { number: 23, text: "Dann machte er auch für den Leuchter die sieben Lampen, seine Dochtscheren und Pfannen aus purem Gold." },
      { number: 24, text: "Aus einem Talent puren Goldes machte er den Leuchter und alle dazugehörenden Geräte." },
      { number: 25, text: "Auch machte er den Räucheraltar aus Akazienholz, eine Elle lang, eine Elle breit - also quadratisch - und zwei Ellen hoch; seine Hörner bildeten mit ihm eine Einheit.", footnotes: ["(25-28) 30,1-10"] },
      { number: 26, text: "Er überzog ihn mit purem Gold, seine Platte, seine Wände ringsum und seine Hörner, und brachte an ihm ringsum eine Goldleiste an." },
      { number: 27, text: "Er machte für ihn zwei Paar Goldringe und befestigte sie unterhalb der Leiste an seinen beiden Seiten, seinen Seitenwänden, zum Aufnehmen der Stangen, sodass man ihn damit tragen konnte." },
      { number: 28, text: "Er machte die Stangen aus Akazienholz und überzog sie mit Gold." },
      { number: 29, text: "Er bereitete auch das heilige Salböl und das reine duftende Räucherwerk, wie Salbenmischer sie zubereiten.", footnotes: ["30,22-33"] },
    ] },
    { number: 38, verses: [
      { number: 1, text: "Dann machte er den Brandopferaltar aus Akazienholz, fünf Ellen lang, fünf Ellen breit - also quadratisch - und drei Ellen hoch.", footnotes: ["(1-7) 27,1-8"] },
      { number: 2, text: "Er machte ihm Hörner an seinen vier Ecken - seine Hörner bildeten mit ihm ein Ganzes - und überzog ihn mit Kupfer." },
      { number: 3, text: "Er stellte auch alle Altargeräte her: die Gefäße, die Schaufeln, die Schalen, die Gabeln und die Feuerpfannen. Alle seine Geräte stellte er aus Kupfer her." },
      { number: 4, text: "Er machte auch für den Altar ein Gitterwerk, ein Netzgitter aus Kupfer, unterhalb der Einfassung bis zu seiner halben Höhe." },
      { number: 5, text: "Dann goss er vier Ringe an die vier Enden des kupfernen Gitterwerkes zum Aufnehmen der Stangen." },
      { number: 6, text: "Er verfertigte die Stangen aus Akazienholz und überzog sie mit Kupfer." },
      { number: 7, text: "Er steckte die Stangen in die Ringe an den Seiten des Altars, sodass man ihn damit tragen konnte; er machte ihn hohl, aus Brettern." },
      { number: 8, text: "Er machte das Becken und sein Gestell aus Kupfer, und zwar aus den Spiegeln der Frauen, die am Eingang des Offenbarungszeltes Dienst taten." },
      { number: 9, text: "Dann machte er den Vorhof: für die südliche Längsseite Vorhofbehänge aus gezwirntem Byssus, hundert Ellen.", footnotes: ["(9-20) 27,9-19"] },
      { number: 10, text: "Ihre zwanzig Säulen und deren zwanzig Sockel waren aus Kupfer, die Nägel der Säulen und ihre Querstangen waren aus Silber." },
      { number: 11, text: "Für die nördliche Längsseite hundert Ellen Behänge; ihre zwanzig Säulen und deren zwanzig Sockel waren aus Kupfer; die Nägel der Säulen und ihre Querstangen waren aus Silber." },
      { number: 12, text: "Für die Westseite fünfzig Ellen Behänge, ihre zehn Säulen und deren zehn Sockel; die Nägel der Säulen und ihre Querstangen waren aus Silber." },
      { number: 13, text: "Für die Vorderseite, gegen Osten hin, fünfzig Ellen Behänge," },
      { number: 14, text: "und zwar fünfzehn Ellen Behänge für die eine Seitenwand mit den drei Säulen und den drei Sockeln," },
      { number: 15, text: "ebenso für die andere Seitenwand. So waren beiderseits des Vorhoftores fünfzehn Ellen Behänge mit den drei Säulen und den drei Sockeln." },
      { number: 16, text: "Alle Behänge des Vorhofs ringsum bestanden aus gezwirntem Byssus." },
      { number: 17, text: "Die Sockel der Säulen waren aus Kupfer, die Nägel der Säulen und ihre Querstangen aus Silber, der Überzug ihrer Kapitelle aus Silber. Alle Säulen des Vorhofs hatten Querstangen aus Silber.", footnotes: ["Der letzte Satz des Verses ist in H unklar."] },
      { number: 18, text: "Für das Tor des Vorhofs machte er einen Vorhang: Er war Buntwirkerarbeit aus violettem und rotem Purpur, Karmesin und gezwirntem Byssus, zwanzig Ellen breit und fünf Ellen hoch, entsprechend den übrigen Behängen des Vorhofs." },
      { number: 19, text: "Ihre vier Säulen und deren vier Sockel waren aus Kupfer, ihre Nägel aus Silber und auch der Überzug ihrer Kapitelle und ihre Querstangen aus Silber." },
      { number: 20, text: "Alle Zeltpflöcke für die Wohnstätte und den Vorhof ringsum waren aus Kupfer." },
      { number: 21, text: "Das ist die Veranlagung zugunsten der Wohnstätte, der Stätte der Bundesurkunde; diese Veranlagung hatte Mose den Leviten unter dem Priester Itamar, dem Sohn Aarons, übertragen.", footnotes: ["(21-26) 30,11-16"] },
      { number: 22, text: "Bezalel, der Sohn Uris, des Sohnes Hurs, vom Stamm Juda, führte alles aus, was der Herr dem Mose befohlen hatte." },
      { number: 23, text: "Mit Oholiab, dem Sohn Ahisamachs, vom Stamm Dan, als Steinschneider, Kunstweber und Buntwirker des violetten und roten Purpurs, des Karmesins und des Byssus führte er alles aus." },
      { number: 24, text: "Das gesamte Gold, das man für alle Arbeiten zur Errichtung des Heiligtums verwendete - es war das Gold der Weihegaben -, wog 29 Talente und 730 Schekel, und zwar nach dem Schekelgewicht des Heiligtums." },
      { number: 25, text: "Das Silber betrug aufgrund der Veranlagung der Gemeinde 100 Talente und 1775 Schekel, und zwar nach dem Schekelgewicht des Heiligtums," },
      { number: 26, text: "einen Halbschekel für jede Person, also die Hälfte eines Schekels, und zwar nach dem Schekelgewicht des Heiligtums, für jeden von zwanzig Jahren und darüber, der zur Veranlagung vortreten musste, also für 603550 Mann." },
      { number: 27, text: "Verwendet wurden 100 Talente Silber zum Guss der Sockel des Heiligtums und der Sockel der Behänge, 100 Talente für 100 Sockel, also ein Talent für einen Sockel." },
      { number: 28, text: "Aus 1775 Schekel machte er die Nägel für die Säulen, überzog die Kapitelle und verband sie miteinander." },
      { number: 29, text: "Das Kupfer der Weihegaben betrug 70 Talente und 2400 Schekel." },
      { number: 30, text: "Daraus machte er die Sockel für den Eingang des Offenbarungszeltes, den kupfernen Altar samt seinem kupfernen Gitterwerk und alle Altargeräte" },
      { number: 31, text: "sowie die Sockel für den Vorhof ringsum, die Torsockel des Vorhofs, alle Zeltpflöcke der Wohnstätte und alle Zeltpflöcke des Vorhofs ringsum." },
    ] },
    { number: 39, verses: [
      { number: 1, text: "Aus violettem und rotem Purpur und aus Karmesin verfertigten sie die gewirkten Gewänder für den Dienst im Heiligtum und verfertigten die heiligen Gewänder für Aaron, wie es der Herr dem Mose befohlen hatte.", heading: "Die Anfertigung der Priestergewänder: 39,1-31", footnotes: ["(1-31) 28,1-43"] },
      { number: 2, text: "Sie stellten das Efod her aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus." },
      { number: 3, text: "Sie schlugen goldene Plättchen breit, schnitten sie in Fäden, um sie zwischen violettem und rotem Purpur, Karmesin und Byssus als Kunstweberarbeit einzuwirken." },
      { number: 4, text: "Für das Efod machten sie miteinander verbundene Schulterstücke und befestigten sie an ihren beiden Enden." },
      { number: 5, text: "Eine Efodschärpe daran bildete mit ihm ein einziges Stück und war von derselben Machart, aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus, wie es der Herr dem Mose befohlen hatte." },
      { number: 6, text: "Sie bearbeiteten die in Goldfassungen eingesetzten Karneolsteine und schnitten nach Siegelart die Namen der Söhne Israels ein." },
      { number: 7, text: "Man setzte sie dann auf die Schulterstücke des Efod als Steine, die den Herrn an die Israeliten erinnern, wie es der Herr dem Mose befohlen hatte." },
      { number: 8, text: "Die Lostasche verfertigte man als Kunstweberarbeit wie das Efod, aus Gold, violettem und rotem Purpur, Karmesin und gezwirntem Byssus." },
      { number: 9, text: "Sie machten die Lostasche quadratisch, zusammengefaltet, eine Spanne lang und eine Spanne breit." },
      { number: 10, text: "Sie besetzten sie mit Edelsteinen in vier Reihen: die erste Reihe mit Rubin, Topas und Smaragd," },
      { number: 11, text: "die zweite Reihe mit Karfunkel, Saphir und Jaspis," },
      { number: 12, text: "die dritte Reihe mit Achat, Hyazinth und Amethyst," },
      { number: 13, text: "die vierte Reihe mit Chrysolith, Karneol und Onyx; sie waren in Gold gefasst." },
      { number: 14, text: "Die Steine lauteten auf die Namen der Söhne Israels, zwölf auf ihre Namen - in Siegelgravierung. Jeder lautete auf den Namen eines der zwölf Stämme." },
      { number: 15, text: "Sie befestigten an der Lostasche schnurartige, gedrehte Kettchen aus purem Gold." },
      { number: 16, text: "Sie machten zwei goldene Einfassungen und zwei Goldringe und befestigten die beiden Ringe an den beiden Enden der Lostasche." },
      { number: 17, text: "Die beiden goldenen Schnüre verbanden sie mit den beiden Ringen an den Enden der Lostasche;" },
      { number: 18, text: "die zwei anderen Enden der Schnüre befestigten sie an den beiden Einfassungen und hängten sie an die Vorderseite der Schulterstücke des Efod." },
      { number: 19, text: "Sie machten noch zwei Goldringe und befestigten sie an den beiden Enden der Lostasche, und zwar am inneren Rand, der dem Efod zugekehrt ist." },
      { number: 20, text: "Sie machten zwei weitere Goldringe und befestigten sie unten an den beiden Schulterstücken des Efod, und zwar an der Vorderseite, nahe ihrer Naht, aber oberhalb der Efodschärpe." },
      { number: 21, text: "Sie banden die Lostasche mit ihren Ringen an die Ringe des Efod mit einer violetten Purpurschnur, sodass sie über der Efodschärpe blieb und sich die Lostasche nicht vom Efod verschob, wie es der Herr dem Mose befohlen hatte." },
      { number: 22, text: "Er machte den Efodmantel als Weberarbeit gänzlich aus violettem Purpur." },
      { number: 23, text: "In seiner Mitte war die Mantelöffnung, wie die Öffnung eines Panzerhemds; die Öffnung hatte ringsum einen gewebten unzerreißbaren Rand." },
      { number: 24, text: "An seinem unteren Saum machten sie Granatäpfel aus violettem und rotem Purpur und aus gezwirntem Karmesin." },
      { number: 25, text: "Sie machten Glöckchen aus reinem Gold und befestigten die Glöckchen zwischen den Granatäpfeln am Saum des Mantels, ringsum zwischen den Granatäpfeln:" },
      { number: 26, text: "ein Glöckchen und ein Granatapfel abwechselnd ringsum am Saum des Mantels für den Dienst, wie es der Herr dem Mose befohlen hatte." },
      { number: 27, text: "Danach machten sie die Leibröcke aus Byssus als Weberarbeit für Aaron und seine Söhne," },
      { number: 28, text: "den Turban aus Byssus, den Schmuck der Kopfbünde aus Byssus, die leinenen Beinkleider aus gezwirntem Byssus" },
      { number: 29, text: "und den Gürtel aus gezwirntem Byssus, violettem und rotem Purpur und Karmesin, als Buntwirkerarbeit, wie es der Herr dem Mose befohlen hatte." },
      { number: 30, text: "Sie machten eine Rosette aus purem Gold, das heilige Diadem, und brachten darauf die Inschrift nach Art der Siegelgravierung an: Heilig dem Herrn." },
      { number: 31, text: "Sie befestigten sie an einer Schnur aus violettem Purpur, um sie oben am Turban anzubringen, wie es der Herr dem Mose befohlen hatte." },
      { number: 32, text: "So wurde das ganze Werk für die Wohnstätte des Offenbarungszeltes vollendet; die Israeliten taten genau so, wie es der Herr dem Mose befohlen hatte. So machten sie es.", heading: "Der Abschluss der Arbeiten: 39,32-43" },
      { number: 33, text: "Sie brachten die Wohnstätte zu Mose: das Zelt mit allen seinen Geräten, seinen Haken, Brettern, Querlatten, Säulen und Sockeln," },
      { number: 34, text: "die Decke aus rötlichen Widderfellen, die Decke aus Tahaschhäuten und die Vorhangdecke," },
      { number: 35, text: "die Lade der Bundesurkunde mit ihren Stangen und der Deckplatte," },
      { number: 36, text: "den Tisch und alle dazugehörigen Geräte sowie die Schaubrote," },
      { number: 37, text: "den Leuchter aus purem Gold, seine Lampen - die Lampen der Reihe nach aufgestellt - und alle seine Geräte sowie das Öl für den Leuchter," },
      { number: 38, text: "den goldenen Altar, das Salböl, das duftende Räucherwerk und den Vorhang für den Eingang des Zeltes," },
      { number: 39, text: "den kupfernen Altar und das zugehörige kupferne Gitterwerk mit seinen Stangen und allen seinen Geräten, das Becken und sein Gestell," },
      { number: 40, text: "die Behänge des Vorhofs, seine Säulen und Sockel sowie den Vorhang für das Tor des Vorhofs, seine Schnüre und Zeltpflöcke und alle Geräte für den Dienst an der Wohnstätte, am Offenbarungszelt," },
      { number: 41, text: "die gewirkten Gewänder für den Dienst im Heiligtum, die heiligen Gewänder für den Priester Aaron und die Gewänder seiner Söhne für den Priesterdienst." },
      { number: 42, text: "Genau so, wie es der Herr dem Mose befohlen hatte, führten die Israeliten die gesamte Arbeit aus." },
      { number: 43, text: "Mose besichtigte das ganze Werk: Sie hatten es gemacht, wie der Herr es befohlen hatte. So hatten sie es gemacht und Mose segnete sie." },
    ] },
    { number: 40, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Weihe des Heiligtums: 40,1-38" },
      { number: 2, text: "Am ersten Tag des ersten Monats sollst du die Wohnstätte des Offenbarungszeltes aufstellen." },
      { number: 3, text: "Stell die Lade der Bundesurkunde hinein und verdeck die Lade durch den Vorhang!" },
      { number: 4, text: "Bring den Tisch hinein und leg seine Ausrüstung zurecht; stell den Leuchter auf und setz seine Lampen auf!" },
      { number: 5, text: "Stell den goldenen Räucheraltar vor die Lade der Bundesurkunde und häng den Vorhang vor den Eingang der Wohnstätte!" },
      { number: 6, text: "Errichte den Brandopferaltar vor dem Eingang der Wohnstätte, des Offenbarungszeltes!" },
      { number: 7, text: "Das Becken stell zwischen das Offenbarungszelt und den Altar und gieß Wasser hinein!" },
      { number: 8, text: "Errichte den Vorhof ringsum und häng den Vorhang an das Tor des Vorhofs!" },
      { number: 9, text: "Nimm das Salböl und salbe die Wohnstätte und alles, was in ihr ist. Weihe sie mit allen ihren Geräten! So wird sie heilig sein." },
      { number: 10, text: "Salbe auch den Brandopferaltar mit all seinen Geräten und weihe den Altar! So wird der Altar hochheilig sein." },
      { number: 11, text: "Salbe das Becken mit seinem Gestell und weihe es!" },
      { number: 12, text: "Dann lass Aaron und seine Söhne zum Eingang des Offenbarungszeltes herantreten und wasche sie mit Wasser!" },
      { number: 13, text: "Bekleide Aaron mit den heiligen Gewändern, salbe und weihe ihn, damit er mir als Priester dient." },
      { number: 14, text: "Dann lass seine Söhne herantreten und bekleide sie mit Leibröcken;" },
      { number: 15, text: "salbe sie, wie du ihren Vater gesalbt hast, damit sie mir als Priester dienen. Ihre Salbung soll ihnen ein immer währendes Priestertum sichern von Generation zu Generation." },
      { number: 16, text: "Mose tat es. Er machte alles, wie es der Herr ihm befohlen hatte." },
      { number: 17, text: "Im zweiten Jahr, am ersten Tag des ersten Monats, stellte man die Wohnstätte auf." },
      { number: 18, text: "Mose stellte die Wohnstätte auf, legte ihre Sockel hin, setzte ihre Bretter darauf, brachte ihre Querlatten an und stellte ihre Säulen auf." },
      { number: 19, text: "Dann spannte er das Zelt über die Wohnstätte und legte die Decke des Zeltes darüber, wie es der Herr dem Mose befohlen hatte." },
      { number: 20, text: "Dann nahm er die Bundesurkunde, legte sie in die Lade, brachte die Stangen an der Lade an und setzte die Deckplatte oben auf die Lade." },
      { number: 21, text: "Er brachte die Lade in die Wohnstätte, spannte die Vorhangdecke auf und verdeckte so die Lade der Bundesurkunde, wie es der Herr dem Mose befohlen hatte." },
      { number: 22, text: "Er stellte den Tisch in das Offenbarungszelt, an die Nordseite der Wohnstätte, vor den Vorhang." },
      { number: 23, text: "Darauf schichtete er die Brote vor dem Herrn auf, wie es der Herr dem Mose befohlen hatte." },
      { number: 24, text: "Er stellte den Leuchter in das Offenbarungszelt, dem Tisch gegenüber, an die Südseite der Wohnstätte." },
      { number: 25, text: "Er setzte die Lampen vor dem Herrn auf, wie es der Herr dem Mose befohlen hatte." },
      { number: 26, text: "Dann stellte er den goldenen Altar in das Offenbarungszelt vor den Vorhang." },
      { number: 27, text: "Er ließ auf ihm duftendes Räucherwerk verbrennen, wie es der Herr dem Mose befohlen hatte." },
      { number: 28, text: "Dann hängte er den Vorhang an den Eingang der Wohnstätte." },
      { number: 29, text: "Den Brandopferaltar stellte er an den Eingang der Wohnstätte, des Offenbarungszeltes, und brachte auf ihm das Brand- und Speiseopfer dar, wie es der Herr dem Mose befohlen hatte." },
      { number: 30, text: "Er stellte das Becken zwischen das Offenbarungszelt und den Altar und goss Wasser zum Waschen hinein." },
      { number: 31, text: "Darin wuschen Mose, Aaron und seine Söhne ihre Hände und Füße." },
      { number: 32, text: "Wenn sie in das Offenbarungszelt eintraten oder sich dem Altar näherten, wuschen sie sich, wie es der Herr dem Mose befohlen hatte." },
      { number: 33, text: "Er errichtete den Vorhof um die Wohnstätte und den Altar und ließ den Vorhang am Tor des Vorhofs aufhängen. So vollendete Mose das Werk." },
      { number: 34, text: "Dann verhüllte die Wolke das Offenbarungszelt und die Herrlichkeit des Herrn erfüllte die Wohnstätte." },
      { number: 35, text: "Mose konnte das Offenbarungszelt nicht betreten, denn die Wolke lag darauf, und die Herrlichkeit des Herrn erfüllte die Wohnstätte." },
      { number: 36, text: "Immer, wenn die Wolke sich von der Wohnstätte erhob, brachen die Israeliten auf, solange ihre Wanderung dauerte." },
      { number: 37, text: "Wenn sich aber die Wolke nicht erhob, brachen sie nicht auf, bis zu dem Tag, an dem sie sich erhob." },
      { number: 38, text: "Bei Tag schwebte über der Wohnstätte die Wolke des Herrn, bei Nacht aber war an ihr Feuer vor den Augen des ganzen Hauses Israel, solange ihre Wanderung dauerte." },
    ] },
  ],
};
