import { Book } from "@/lib/types";

export const ezekiel: Book = {
  id: "ezekiel",
  name: "Ezechiel",
  shortName: "Ez",
  testament: "old",
  introduction: "Zusammen mit König Jojachin und vielen aus seinem Volk wurde Ezechiel, der einer priesterlichen Familie entstammte, 597 v. Chr. von Nebukadnezzar in die Verbannung nach Babylonien geführt. Dort bei Tel-Abib, an einem Eufratkanal, wurde er von Gott zum Propheten berufen. Er wirkte unter den Verschleppten in Babylonien. Ihnen musste er die falsche Hoffnung, dass sie bald wieder nach Jerusalem zurückkehren könnten, nehmen, indem er Gottes Gericht über die Stadt und den Tempel ansagte. Nachdem die Stadt erobert und zerstört war (586 v. Chr.), verkündete er Gottes rettendes Eingreifen und stellte die Wiederherstellung des Volkes um den neu erbauten Tempel in Aussicht. Für seine Aufgabe brachte Ezechiel eine besondere Begabung mit. Er war ein Visionär, wusste sich von Gottes Geist ergriffen, von seiner Hand gepackt, nach Jerusalem oder in die Ebene hinaus entrückt und wiederholt bis in merkwürdig erscheinende körperliche Zustände hinein von seinem Verkündigungsauftrag beansprucht. Manches, was er über sich sagt oder was andere über ihn berichten, dürfte jedoch Ausgestaltung seiner prophetischen Worte und Berichte durch spätere Bearbeiter sein. Dass seinen Aussagen vieles hinzugefügt wurde, zeigt sich allenthalben, am auffallendsten vielleicht bei der Findelkindgeschichte (Kap. 16) und dem Gleichnis von den beiden Schwestern (Kap. 23). Da er manche seiner Worte mit Datum versehen hat und demnach selbst schriftlich festgehalten haben muss, ergibt sich für seine Wirksamkeit etwa der Zeitraum von 592-571 v. Chr. Für seine Verkündigung standen dem Propheten eine große Zahl von Ausdrucksmöglichkeiten und Redeformen zur Verfügung. Neben Droh- und Scheltworten stehen Visionsberichte, von denen drei besonders hervorzuheben sind: die Berufung (Kap. 1 - 3); der sündhafte Tempelkult, der die Zerstörung von Tempel und Stadt nach sich zieht (Kap. 8 - 11); die Wiederherstellung des Tempels und die Neuverteilung des Landes an die Stämme Israels (Kap. 40 - 48). Wie kein anderer Prophet führte Ezechiel viele symbolische Handlungen aus (Kap. 3,22-27; 4f; 12; 21; 24; 37). Die Geschichtsdarstellung (Kap. 20) macht er ebenso zum Mittel der Verkündigung wie die Totenklage (z. B. Kap. 19; 27; 32), die Bildrede und das Gleichnis (z. B. Kap. 17; 21; 27; 31). Er beherrscht die Diskussionsrede (Kap. 14; 18), kennt sich in den gesetzlichen Vorschriften aus (Kap. 18; 44) und versteht es, wie ein Architekt einen Bau zu beschreiben (Kap. 40 43). Auch Mahnworte und prophetische Belehrung fehlen nicht. Die Verheißung nimmt in der zweiten Periode seiner Verkündigung einen breiten Raum ein. Das Buch Ezechiel lässt sich in folgende große Teile gliedern: Berufungsvision (Kap. 1 - 3); Drohungen gegen Juda und Jerusalem (Kap. 4 - 24); Drohungen gegen fremde Völker (Kap. 25 - 32); Verheißungen für das eigene Volk (Kap. 33 - 37); endzeitlicher Kampf Jahwes gegen Gog (Kap. 38f); das neue Jerusalem (der sog. Verfassungsentwurf Ezechiels, Kap. 40 - 48). Zunächst werden verschiedene kleinere Sammlungen entstanden sein, wie etwa die Berichte über symbolische Handlungen (Kap. 4f; 12; 24); Worte gegen die Götzen (Kap. 6), über die Nähe des Gerichts (Kap. 7), über die Propheten (12,21 - 13,21), über Sünde, Strafe und Verantwortlichkeit (Kap. 15 - 20), gegen Israels Nachbarn Ammon, Moab, Edom und die Philister (Kap. 25); Visionen über Gottes Strafgericht an Tempel und Stadt (Kap. 8 - 11); Gleichnisreden gegen Ägypten (Kap. 29 - 32) und über Hirten und Schafe (Kap. 34); Verheißungen über das Werden eines neuen Israel (Kap. 36f). Auch in der großen abschließenden Vision vom neuen Israel (Kap. 40 - 48) sind ekstatische Erlebnisse zusammen mit Anweisungen, theologischen Erörterungen und Mahnungen festgehalten. Apokalyptische Züge tragen die Worte über Gog und Magog (Kap. 38 f). Die prophetische Botschaft des Buches Ezechiel hat eine große Spannweite. Da wird nicht nur von Schuld, Gericht und Umkehr, von Heil, Wiederherstellung und göttlichem Erbarmen gesprochen, sondern auch von menschlicher Verantwortung. Jahwe, der Gott Israels, ist nicht an sein Land gebunden; er ist überall, er ist der Herr über alle Völker. Ihm soll ein innerlich erneuertes, heiliges Volk dienen; er will es als Hirt führen. Auch die messianische Hoffnung klingt an (17,22; 34,23f; 37,22-25).",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Am fünften Tag des vierten Monats im dreißigsten Jahr, als ich unter den Verschleppten am Fluss Kebar lebte, öffnete sich der Himmel und ich sah eine Erscheinung Gottes.", heading: "Die Berufung Ezechiels zum Propheten: 1,1 - 3,27 | Ort und Zeit der Berufung: 1,1-3", footnotes: ["3,23; 10,15.20.22; 43,3", "1-3: Das Buch hat zwei Überschriften erhalten (VV. 2f und V. 1), die hier vereinigt sind. Das «dreißigste Jahr» ist vielleicht das Lebensalter Ezechiels; das fünfte Jahr nach der Verschleppung Jojachins ist 592 v. Chr. - Der Fluss Kebar ist ein Eufratkanal zwischen Babylon und Nippur."] },
      { number: 2, text: "Am fünften Tag des Monats - es war im fünften Jahr nach der Verschleppung des Königs Jojachin -" },
      { number: 3, text: "erging das Wort des Herrn an Ezechiel, den Sohn Busis, den Priester, im Land der Chaldäer, am Fluss Kebar. Dort kam die Hand des Herrn über ihn.", footnotes: ["3,22; 8,1; 33,22; 37,1; 40,1"] },
      { number: 4, text: "Ich sah: Ein Sturmwind kam von Norden, eine große Wolke mit flackerndem Feuer, umgeben von einem hellen Schein. Aus dem Feuer strahlte es wie glänzendes Gold.", heading: "Die Erscheinung Gottes: 1,4-28", footnotes: ["(4-28) 10,8-17", "4-28: Die «Lebewesen» sind mischgestaltig; sie werden in 10,8-17 als Kerubim beschrieben. Die Vision ist in ihren Einzelheiten nicht klar zu deuten."] },
      { number: 5, text: "Mitten darin erschien etwas wie vier Lebewesen. Und das war ihre Gestalt: Sie sahen aus wie Menschen." },
      { number: 6, text: "Jedes der Lebewesen hatte vier Gesichter und vier Flügel." },
      { number: 7, text: "Ihre Beine waren gerade und ihre Füße wie die Füße eines Stieres; sie glänzten wie glatte und blinkende Bronze." },
      { number: 8, text: "Unter den Flügeln an ihren vier Seiten hatten sie Menschenhände. [Auch Gesichter und Flügel hatten die vier.]" },
      { number: 9, text: "Ihre Flügel berührten einander. Die Lebewesen änderten beim Gehen ihre Richtung nicht: Jedes ging in die Richtung, in die eines seiner Gesichter wies.", footnotes: ["Offb 4,7"] },
      { number: 10, text: "Und ihre Gesichter sahen so aus: Ein Menschengesicht (blickte bei allen vier nach vorn), ein Löwengesicht bei allen vier nach rechts, ein Stiergesicht bei allen vier nach links und ein Adlergesicht bei allen vier (nach hinten)." },
      { number: 11, text: "Ihre Flügel waren nach oben ausgespannt. Mit zwei Flügeln berührten sie einander und mit zwei bedeckten sie ihren Leib.", footnotes: ["Jes 6,2"] },
      { number: 12, text: "Jedes Lebewesen ging in die Richtung, in die eines seiner Gesichter wies. Sie gingen, wohin der Geist sie trieb, und änderten beim Gehen ihre Richtung nicht." },
      { number: 13, text: "Zwischen den Lebewesen war etwas zu sehen wie glühende Kohlen, etwas wie Fackeln, die zwischen den Lebewesen hin- und herzuckten. Das Feuer gab einen hellen Schein und aus dem Feuer zuckten Blitze.", footnotes: ["Ex 19,18"] },
      { number: 14, text: "Die Lebewesen liefen vor und zurück und es sah aus wie Blitze." },
      { number: 15, text: "Ich schaute auf die Lebewesen: Neben jedem der vier sah ich ein Rad auf dem Boden.", footnotes: ["(15-17) 10,9-13"] },
      { number: 16, text: "Die Räder sahen aus, als seien sie aus Chrysolith gemacht. Alle vier Räder hatten die gleiche Gestalt. Sie waren so gemacht, dass es aussah, als laufe ein Rad mitten im andern." },
      { number: 17, text: "Sie konnten nach allen vier Seiten laufen und änderten beim Laufen ihre Richtung nicht." },
      { number: 18, text: "Ihre Felgen waren so hoch, dass ich erschrak; sie waren voll Augen, ringsum bei allen vier Rädern.", footnotes: ["Offb 4,8"] },
      { number: 19, text: "Gingen die Lebewesen, dann liefen die Räder an ihrer Seite mit. Hoben sich die Lebewesen vom Boden, dann hoben sich auch die Räder.", footnotes: ["10,16f"] },
      { number: 20, text: "Sie liefen, wohin der Geist sie trieb. Die Räder hoben sich zugleich mit ihnen; denn der Geist der Lebewesen war in den Rädern." },
      { number: 21, text: "Gingen die Lebewesen, dann liefen auch die Räder; blieben jene stehen, dann standen auch sie still. Hoben sich jene vom Boden, dann hoben sich die Räder zugleich mit ihnen; denn der Geist der Lebewesen war in den Rädern." },
      { number: 22, text: "Über den Köpfen der Lebewesen war etwas wie eine gehämmerte Platte befestigt, furchtbar anzusehen, wie ein strahlender Kristall, oben über ihren Köpfen.", footnotes: ["10,1; Ex 24,10; Offb 4,6"] },
      { number: 23, text: "Unter der Platte waren ihre Flügel ausgespannt, einer zum andern hin. Mit zwei Flügeln bedeckte jedes Lebewesen seinen Leib.", footnotes: ["Jes 6,2"] },
      { number: 24, text: "Ich hörte das Rauschen ihrer Flügel; es war wie das Rauschen gewaltiger Wassermassen, wie die Stimme des Allmächtigen. Wenn sie gingen, glich das tosende Rauschen dem Lärm eines Heerlagers. Wenn sie standen, ließen sie ihre Flügel herabhängen.", footnotes: ["10,5"] },
      { number: 25, text: "Ein Rauschen war auch oberhalb der Platte, die über ihren Köpfen war. Wenn sie standen, ließen sie ihre Flügel herabhängen." },
      { number: 26, text: "Oberhalb der Platte über ihren Köpfen war etwas, das wie Saphir aussah und einem Thron glich. Auf dem, was einem Thron glich, saß eine Gestalt, die wie ein Mensch aussah.", footnotes: ["Offb 4,2f; Ex 24,10; Ez 8,2; Offb 1,13"] },
      { number: 27, text: "Oberhalb von dem, was wie seine Hüften aussah, sah ich etwas wie glänzendes Gold in einem Feuerkranz. Unterhalb von dem, was wie seine Hüften aussah, sah ich etwas wie Feuer und ringsum einen hellen Schein." },
      { number: 28, text: "Wie der Anblick des Regenbogens, der sich an einem Regentag in den Wolken zeigt, so war der helle Schein ringsum. So etwa sah die Herrlichkeit des Herrn aus. Als ich diese Erscheinung sah, fiel ich nieder auf mein Gesicht. Und ich hörte, wie jemand redete.", footnotes: ["Gen 9,13-16; Ez 8,4; 10,4.18; 11,23; 43,3; 44,4"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Er sagte zu mir: Stell dich auf deine Füße, Menschensohn; ich will mit dir reden.", heading: "Die Sendung Ezechiels: 2,1 - 3,15", footnotes: ["Dan 8,17; 10,11", "Menschensohn: Im Hebräischen bezeichnet der Ausdruck den einzelnen Menschen; bei Ezechiel ist damit der Prophet in seiner menschlichen Begrenztheit gemeint, anders dagegen in Dan 7 (vgl. die Anmerkung zu Dan 7,13)."] },
      { number: 2, text: "Als er das zu mir sagte, kam der Geist in mich und stellte mich auf die Füße. Und ich hörte den, der mit mir redete." },
      { number: 3, text: "Er sagte zu mir: Menschensohn, ich sende dich zu den abtrünnigen Söhnen Israels, die sich gegen mich aufgelehnt haben. Sie und ihre Väter sind immer wieder von mir abgefallen, bis zum heutigen Tag.", footnotes: ["zu den abtrünnigen Söhnen Israels: H: zu den Söhnen Israels, zu den Völkern, den abtrünnigen; diese Erweiterung fehlt zu Recht in G."] },
      { number: 4, text: "Es sind Söhne mit trotzigem Gesicht und hartem Herzen. Zu ihnen sende ich dich. Du sollst zu ihnen sagen: So spricht Gott, der Herr." },
      { number: 5, text: "Ob sie dann hören oder nicht - denn sie sind ein widerspenstiges Volk -, sie werden erkennen müssen, dass mitten unter ihnen ein Prophet war.", footnotes: ["2,7; 3,11.27; 33,33", "5.8: ein widerspenstiges Volk, wörtlich: ein Haus der Widerspenstigkeit."] },
      { number: 6, text: "Du aber, Menschensohn, fürchte dich nicht vor ihnen, hab keine Angst vor ihren Worten! Wenn dich auch Dornen umgeben und du auf Skorpionen sitzt, hab keine Angst vor ihren Worten und erschrick nicht vor ihrem Blick; denn sie sind ein widerspenstiges Volk.", footnotes: ["Jer 1,8.17", "dich auch Dornen umgeben: Text korr.; vgl. G."] },
      { number: 7, text: "Du sollst ihnen meine Worte sagen, ob sie hören oder nicht, denn sie sind widerspenstig." },
      { number: 8, text: "Du aber, Menschensohn, höre, was ich zu dir sage. Sei nicht widerspenstig wie dieses widerspenstige Volk! Öffne deinen Mund und iss, was ich dir gebe." },
      { number: 9, text: "Und ich sah: Eine Hand war ausgestreckt zu mir; sie hielt eine Buchrolle.", footnotes: ["Sach 5,2; Offb 5,1"] },
      { number: 10, text: "Er rollte sie vor mir auf. Sie war innen und außen beschrieben und auf ihr waren Klagen, Seufzer und Weherufe geschrieben." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Er sagte zu mir: Menschensohn, iss, was du vor dir hast. Iss diese Rolle! Dann geh und rede zum Haus Israel!", footnotes: ["Jer 15,16; Offb 10,9"] },
      { number: 2, text: "Ich öffnete meinen Mund und er ließ mich die Rolle essen." },
      { number: 3, text: "Er sagte zu mir: Menschensohn, gib deinem Bauch zu essen, fülle dein Inneres mit dieser Rolle, die ich dir gebe. Ich aß sie und sie wurde in meinem Mund süß wie Honig.", footnotes: ["Ps 119,103"] },
      { number: 4, text: "Er sagte zu mir: Geh zum Haus Israel, Menschensohn, und sprich mit meinen Worten zu ihnen!" },
      { number: 5, text: "Nicht zu einem Volk mit fremder Sprache und unverständlicher Rede wirst du gesandt, sondern zum Haus Israel,", footnotes: ["Jes 33,19"] },
      { number: 6, text: "auch nicht zu vielen Völkern mit fremder Sprache und unverständlicher Rede, deren Worte du nicht verstehst. Würde ich dich zu ihnen senden, sie würden auf dich hören." },
      { number: 7, text: "Doch das Haus Israel will nicht auf dich hören, es fehlt ihnen der Wille, auf mich zu hören; denn jeder vom Haus Israel hat eine harte Stirn und ein trotziges Herz." },
      { number: 8, text: "Ich aber mache dein Gesicht ebenso hart wie ihr Gesicht und deine Stirn ebenso hart wie ihre Stirn.", footnotes: ["(8-9) Jes 50,7"] },
      { number: 9, text: "Wie Diamant und härter als Kieselstein mache ich deine Stirn. Fürchte sie nicht, erschrick nicht vor ihrem Blick; denn sie sind ein widerspenstiges Volk.", footnotes: ["Jer 1,17f"] },
      { number: 10, text: "Er sagte zu mir: Menschensohn, nimm alle meine Worte, die ich dir sage, mit deinem Herzen auf und höre mit deinen Ohren!" },
      { number: 11, text: "Geh zu den Verschleppten, zu den Söhnen deines Volkes, und ob sie hören oder nicht, sprich zu ihnen und sag zu ihnen: So spricht Gott, der Herr." },
      { number: 12, text: "Da hob mich der Geist empor und ich hörte hinter mir ein Geräusch, ein gewaltiges Dröhnen, als sich die Herrlichkeit des Herrn von ihrem Ort erhob,", footnotes: ["8,3; 11,1.24; 37,1", "Der Geist, d. h. die Kraft Gottes bringt den Propheten vom Ort der Vision zu den Verschleppten, die in Tel-Abib am Eufratkanal Kebar wohnen. - erhob: Text korr.; vgl. 10,4."] },
      { number: 13, text: "das Geräusch von den Flügeln der Lebewesen, die einander berührten, und das Geräusch der Räder neben ihnen, ein lautes, gewaltiges Dröhnen." },
      { number: 14, text: "Der Geist, der mich emporgehoben hatte, trug mich fort. Ich ging dahin, mit bitterem und grollendem Herzen, und die Hand des Herrn lag schwer auf mir." },
      { number: 15, text: "So kam ich zu den Verschleppten, [die am Fluss Kebar wohnten,] die in Tel-Abib wohnten, und ich saß dort sieben Tage lang verstört mitten unter ihnen." },
      { number: 16, text: "Am Ende der sieben Tage erging das Wort des Herrn an mich:", heading: "Ezechiel als Wächter Israels: 3,16-21", footnotes: ["16-21: Die Berufung Ezechiels zum Wächter Israels erfolgte vermutlich erst nach der Zerstörung Jerusalems (vgl. 33,1-9)."] },
      { number: 17, text: "Menschensohn, ich gebe dich dem Haus Israel als Wächter. Wenn du ein Wort aus meinem Mund hörst, musst du sie vor mir warnen." },
      { number: 18, text: "Wenn ich zu einem, der sich schuldig gemacht hat, sage: Du musst sterben!, und wenn du ihn nicht warnst und nicht redest, um den Schuldigen von seinem schuldhaften Weg abzubringen, damit er am Leben bleibt, dann wird der Schuldige seiner Sünde wegen sterben; von dir aber fordere ich Rechenschaft für sein Blut.", footnotes: ["Gen 9,5"] },
      { number: 19, text: "Wenn du aber den Schuldigen warnst und er sich von seiner Schuld und seinem schuldhaften Weg nicht abwendet, dann wird er seiner Sünde wegen sterben; du aber hast dein Leben gerettet." },
      { number: 20, text: "Und wenn ein Gerechter sein rechtschaffenes Leben aufgibt und Unrecht tut, werde ich ihn zu Fall bringen und er wird sterben, weil du ihn nicht gewarnt hast. Seiner Sünde wegen wird er sterben und an seine gerechten Taten von einst wird man nicht mehr denken. Von dir aber fordere ich Rechenschaft für sein Blut.", footnotes: ["18,24; 33,12f"] },
      { number: 21, text: "Wenn du aber den Gerechten davor warnst zu sündigen und er sündigt nicht, dann wird er am Leben bleiben, weil er gewarnt wurde, und du hast dein Leben gerettet." },
      { number: 22, text: "Dann legte sich die Hand des Herrn auf mich. Er sagte zu mir: Steh auf und geh hinaus in die Ebene! Ich will dort mit dir reden.", heading: "Das Verstummen Ezechiels: 3,22-27", footnotes: ["1,3; 8,1; 33,22; 37,1; 40,1", "22-27: Nach 24,25-27 und 33,21f verstummt der Prophet erst bei der Eroberung Jerusalems."] },
      { number: 23, text: "Ich stand auf und ging in die Ebene hinaus. Und siehe, dort stand die Herrlichkeit des Herrn, so wie ich sie schon am Fluss Kebar gesehen hatte, und ich fiel nieder auf mein Gesicht.", footnotes: ["1,28"] },
      { number: 24, text: "Doch der Geist kam in mich und stellte mich wieder auf die Füße. Er redete mit mir und sagte zu mir: Geh in dein Haus und schließ dich ein!", footnotes: ["2,2"] },
      { number: 25, text: "Und du, Menschensohn - sie werden dich fesseln und mit Stricken binden, sodass du nicht mehr zum Volk hinausgehen kannst." },
      { number: 26, text: "Deine Zunge lasse ich dir am Gaumen kleben. Du wirst verstummen und nicht mehr ihr Mahner sein können; denn sie sind ein widerspenstiges Volk." },
      { number: 27, text: "Wenn ich aber mit dir rede, werde ich deinen Mund öffnen. Dann sag zu ihnen: So spricht Gott, der Herr. Wer hören will, der höre, wer nicht hören will, der lasse es; denn sie sind ein widerspenstiges Volk.", footnotes: ["24,27; 33,22", "Gott, der Herr, wörtlich: der Herr Jahwe. So an allen einschlägigen Stellen."] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Du, Menschensohn, nimm dir einen Lehmziegel, leg ihn vor dich hin und ritze eine Stadt darauf ein [nämlich Jerusalem]!", heading: "Die Drohsprüche gegen Juda und Jerusalem: 4,1 - 24,27 | Symbolische Handlungen: 4,1 - 5,17 | Die Belagerung Jerusalems: 4,1-3", footnotes: ["In Babylonien wurden solche auf Ton gezeichnete Stadtpläne, z. B. von Nippur, gefunden."] },
      { number: 2, text: "Belagere sie; bau ihr gegenüber einen Belagerungswall; schütte einen Damm gegen sie auf; leg vor ihr ein Truppenlager an und stell gegen sie ringsum Sturmböcke auf!", footnotes: ["21,27; 26,8"] },
      { number: 3, text: "Nimm eine Eisenplatte und stell sie als eiserne Mauer zwischen dich und die Stadt! Richte dein Gesicht auf die Stadt: Nun ist sie belagert und du belagerst sie. Das ist ein (warnendes) Zeichen für das Haus Israel." },
      { number: 4, text: "Du, leg dich auf deine linke Seite! Dann lege ich die Schuld des Hauses Israel auf dich. So viele Tage, wie du auf dieser Seite liegst, trägst du ihre Schuld.", heading: "Die Dauer der Verbannung: 4,4-8", footnotes: ["Dann lege ich . . . auf dich: Text korr.; H: Und du legst dann. . ."] },
      { number: 5, text: "Und ich setze für dich fest: So viele Jahre, wie die Schuld des Hauses Israel dauert, so viele Tage sollst du ihre Schuld tragen: dreihundertneunzig Tage.", footnotes: ["5f: Die 390 Tage stehen für die Zahl der Jahre, in denen sich Israel seit Salomo (1 Kön 11,1-8) durch die Verunreinigung des Tempels versündigt hat (vgl. Ez 8). - Die 40 Tage bedeuten die Jahre der Strafe, die Israel im Exil zu tragen hat."] },
      { number: 6, text: "Wenn du diese Zeit beendet hast, leg dich auf die andere, die rechte Seite und trag vierzig Tage lang die Schuld des Hauses Juda: einen Tag für jedes Jahr; so setze ich es für dich fest.", footnotes: ["Num 14,33f"] },
      { number: 7, text: "Dann sollst du dein Gesicht unbeweglich auf das belagerte Jerusalem richten und mit erhobenem Arm gegen die Stadt weissagen." },
      { number: 8, text: "Ich aber lege dir Stricke an, sodass du dich nicht von einer Seite auf die andere drehen kannst, so lange, bis du die Tage der Belagerung beendet hast.", footnotes: ["3,25"] },
      { number: 9, text: "Du, nimm dir Weizen, Gerste, Bohnen, Linsen, Hirse und Dinkel; tu sie zusammen in ein Gefäß und mach dir Brot daraus! Solange du auf der Seite liegst, dreihundertneunzig Tage lang, sollst du davon essen.", heading: "Hungersnot und Unreinheit: 4,9-17" },
      { number: 10, text: "Das Brot, das du isst, soll genau abgewogen sein, zwanzig Schekel am Tag; davon sollst du von Zeit zu Zeit essen.", footnotes: ["10f: Zwanzig Schekel sind etwa 230 Gramm. - Ein sechstel Hin ist höchstens 1 Liter."] },
      { number: 11, text: "Auch das Wasser, das du trinkst, soll genau abgemessen sein: ein sechstel Hin; davon sollst du von Zeit zu Zeit trinken." },
      { number: 12, text: "Das Brot sollst du wie Gerstenbrot zubereiten und essen; auf Menschenkot sollst du es vor aller Augen backen." },
      { number: 13, text: "Und der Herr sprach: Ebenso werden die Israeliten unreines Brot essen bei den Völkern, zu denen ich sie verstoße." },
      { number: 14, text: "Ich aber sagte: Ach, Herr und Gott, ich habe mich noch nie unrein gemacht. Seit meiner Jugend habe ich noch nie von verendeten oder zerrissenen Tieren gegessen. Niemals ist verdorbenes Fleisch in meinen Mund gekommen.", footnotes: ["Ex 22,30; Lev 17,15; 22,8; Apg 10,14"] },
      { number: 15, text: "Da sagte er zu mir: Gut, ich erlaube dir, dein Brot auf Rindermist statt auf Menschenkot zu backen." },
      { number: 16, text: "Dann sagte er zu mir: Menschensohn, ich entziehe Jerusalem seinen Vorrat an Brot. Sie werden ihr Brot wiegen und es mit Sorgen essen; das Wasser werden sie genau abmessen und mit Schaudern trinken;", footnotes: ["Ps 105,16; Ez 12,18f", "ich entziehe Jerusalem seinen Vorrat an Brot: vgl. die Anmerkung zu Lev 26,26."] },
      { number: 17, text: "denn sie sollen an Brot und Wasser Mangel haben und sich entsetzen, einer wie der andere, und sollen dahinsiechen wegen ihrer Schuld.", footnotes: ["Lev 26,39"] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Du, Menschensohn, nimm ein scharfes Schwert! Benutz es als Schermesser und schneide dir damit das Haar und den Bart ab! Dann nimm eine Waage und wiege die Haare!", heading: "Die Vernichtung: 5,1-4" },
      { number: 2, text: "Ein Drittel verbrenne mitten in der Stadt, wenn die Tage ihrer Belagerung zu Ende sind. Ein anderes Drittel zerhau mit dem Schwert in der Umgebung der Stadt! Das letzte Drittel streu in den Wind! Ich will hinter ihnen das Schwert zücken.", footnotes: ["Die Verbrennung der Haare geschieht auf dem in 4,1 genannten Lehmziegel."] },
      { number: 3, text: "Einige wenige davon nimm und binde sie in einen Zipfel deines Mantels!" },
      { number: 4, text: "Aber auch von diesen nimm noch ein paar Haare, wirf sie ins Feuer und verbrenn sie! Von dort wird sich das Feuer ausdehnen auf das ganze Haus Israel." },
      { number: 5, text: "So spricht Gott, der Herr: Das ist Jerusalem. Ich habe es mitten unter die Völker und die Länder ringsum gesetzt.", heading: "Die Deutung der symbolischen Handlungen: 5,5-17" },
      { number: 6, text: "Aber es war böse und widersetzte sich meinen Rechtsvorschriften mehr als die Völker und meinen Gesetzen mehr als die Länder ringsum; meine Rechtsvorschriften haben sie verachtet und nicht nach meinen Gesetzen gelebt." },
      { number: 7, text: "Darum - so spricht Gott, der Herr: Weil ihr noch aufsässiger gewesen seid als die Völker rings um euch, weil ihr nicht nach meinen Gesetzen gelebt und meine Rechtsvorschriften nicht befolgt habt, ja, weil ihr nicht einmal nach den Bräuchen der Völker ringsum gehandelt habt," },
      { number: 8, text: "darum - so spricht Gott, der Herr: Nun gehe ich gegen dich (Jerusalem) vor. Vor den Augen der Völker werde ich mitten in dir Gericht halten." },
      { number: 9, text: "Wegen all deiner Gräueltaten werde ich mit dir tun, was ich noch nie getan habe und auch nie wieder tun werde." },
      { number: 10, text: "Dann werden mitten in dir (Jerusalem) Väter ihre Kinder essen und Kinder ihre Väter. Ich werde ein Strafgericht über dich abhalten und werde die Menschen, die in dir noch übrig sind, in alle Winde zerstreuen.", footnotes: ["Dtn 28,53-55; Lev 26,29; Jer 19,9"] },
      { number: 11, text: "So wahr ich lebe - Spruch Gottes, des Herrn -, weil du mein Heiligtum mit all deinen Götzen und Gräueltaten unrein gemacht hast, will ich dich kahlscheren. Mein Auge wird kein Mitleid zeigen und ich werde keine Schonung üben.", footnotes: ["7,4; 8,18; 9,10; 24,14"] },
      { number: 12, text: "Ein Drittel deiner Einwohner wird an der Pest sterben und durch den Hunger in der Stadt zugrunde gehen. Ein anderes Drittel wird vor deinen Mauern durch das Schwert umkommen. Das letzte Drittel werde ich in alle Winde zerstreuen und ich werde hinter ihnen das Schwert zücken.", footnotes: ["Offb 6,8; Lev 26,33"] },
      { number: 13, text: "So tobt sich mein Zorn aus und ich stille meinen Grimm an ihnen und verschaffe mir Genugtuung. Wenn ich meinen Zorn an ihnen auslasse, dann erkennen sie, dass ich, der Herr, mit leidenschaftlichem Eifer gesprochen habe.", footnotes: ["16,42"] },
      { number: 14, text: "Vor den Augen aller, die vorübergehen, mache ich dich zum Trümmerhaufen, zum Gespött für die Völker ringsum.", footnotes: ["(14-15) Dtn 28,37; Jer 22,5; 24,9"] },
      { number: 15, text: "Zum Gespött und zum Hohn, zur Warnung und zum Schreckbild wirst du für die Völker ringsum, wenn ich an dir voll Zorn, Grimm und Groll mein Strafgericht vollziehe. Ich, der Herr, habe gesprochen.", footnotes: ["wirst du: Text korr. nach den alten Übersetzungen; H: wird sie."] },
      { number: 16, text: "Ich schieße die quälenden, vernichtenden Pfeile des Hungers gegen euch ab; ich schicke sie ab, um euch zu vernichten. Um euren Hunger zu vergrößern, entziehe ich euch euren Vorrat an Brot.", footnotes: ["4,16"] },
      { number: 17, text: "Hungersnot und wilde Tiere schicke ich gegen dich, damit sie dir deine Kinder rauben. Pest und Blutvergießen sollen über dich kommen. Ich bringe das Schwert über dich. Ich, der Herr, habe gesprochen.", footnotes: ["14,21"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Gerichtsworte: 6,1 - 7,27 | Die Zerstörung der Kultstätten: 6,1-14" },
      { number: 2, text: "Menschensohn, richte dein Gesicht auf die Berge Israels; sprich als Prophet zu ihnen", footnotes: ["36,1"] },
      { number: 3, text: "und sag: Ihr Berge Israels, hört das Wort Gottes, des Herrn! So spricht Gott, der Herr, zu den Bergen und Hügeln, zu den Schluchten und Tälern: Ich selbst werde das Schwert über euch bringen und eure Kulthöhen zerstören.", footnotes: ["Lev 26,30f"] },
      { number: 4, text: "Eure Opferaltäre werden vernichtet und eure Räuchertische zerbrochen. Eure Erschlagenen werfe ich vor eure Götzen hin." },
      { number: 5, text: "Die Leichen der Israeliten lege ich ihren Götzen zu Füßen. Eure Gebeine verstreue ich rings um eure Altäre.", footnotes: ["2 Kön 23,14"] },
      { number: 6, text: "Überall, wo ihr wohnt, werden die Städte verwüstet und die Kulthöhen veröden; denn eure Altäre sollen verwüstet und öde sein, eure Götzen zerbrochen und verschwunden, eure Räuchertische zerschlagen, eure Machwerke vernichtet." },
      { number: 7, text: "Mitten unter euch liegen die Erschlagenen. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 8, text: "Wenn ihr in alle Länder vertrieben seid, lasse ich einen Rest von euch übrig. Bei den Völkern werden einige von euch sein, die dem Schwert entronnen sind." },
      { number: 9, text: "Doch die von euch, die davongekommen sind, werden bei den Völkern, zu denen sie verschleppt wurden, an mich denken; denn ich zerbreche ihr Herz, das mir untreu geworden ist, und zermalme ihre Augen, die treulos nach den Götzen schielten. Dann werden sie vor sich selbst Abscheu empfinden wegen der bösen Gräueltaten, die sie begangen haben," },
      { number: 10, text: "und sie werden erkennen, dass ich der Herr bin und nicht umsonst gedroht habe, dieses Unheil über sie zu bringen." },
      { number: 11, text: "So spricht Gott, der Herr: Schlag die Hände zusammen, stampf mit dem Fuß auf den Boden und schrei: Weh über all die bösen Gräueltaten des Hauses Israel! Ihretwegen werden sie durch Schwert, Hunger und Pest umkommen." },
      { number: 12, text: "Wer in der Ferne ist, wird an der Pest sterben; wer nahe ist, wird unter dem Schwert fallen. Wer übrig und verschont geblieben ist, wird vor Hunger sterben. So lasse ich meinen Grimm an ihnen aus." },
      { number: 13, text: "Wenn die Erschlagenen mitten unter ihren Götzenbildern liegen, rings um ihre Altäre, auf allen Hügeln, auf allen Bergeshöhen, unter jedem üppigen Baum und jeder schattigen Eiche, an den Stellen, wo sie all ihren Götzen den beruhigenden Duft ihrer Opfer spendeten, dann werdet ihr erkennen, dass ich der Herr bin.", footnotes: ["1 Kön 14,23"] },
      { number: 14, text: "Ich strecke meine Hand gegen sie aus und mache ihr Land zur öden Wüste, von der Steppe bis nach Ribla, überall, wo sie wohnen. Dann werden sie erkennen, dass ich der Herr bin.", footnotes: ["2 Kön 25,21", "von der Steppe bis nach Ribla: Text korr.; H: bis nach der Steppe von Dibla. Die ganze Ausdehnung des Landes von der Arabischen Wüste bis nach Ribla am Orontes ist betroffen."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Die Ankündigung des Gerichts: 7,1-27" },
      { number: 2, text: "Du, Menschensohn, sag: So spricht Gott, der Herr, zum Land Israel: Das Ende kommt, das Ende kommt über die vier Ecken der Erde.", footnotes: ["Offb 7,1", "sag: eingefügt nach G."] },
      { number: 3, text: "Jetzt ist das Ende für dich da; ich lasse meinen Zorn gegen dich los, ich spreche dir das Urteil, das dein Verhalten verdient, und strafe dich für alle deine Gräueltaten." },
      { number: 4, text: "Mein Auge zeigt kein Mitleid und ich übe keine Schonung, sondern dein Verhalten lasse ich auf dich zurückfallen und deine Gräueltaten sollen sich in deiner Mitte auswirken. Dann werdet ihr erkennen, dass ich der Herr bin.", footnotes: ["5,11; 8,18; 9,10"] },
      { number: 5, text: "So spricht Gott, der Herr: Schon kommt Unglück auf Unglück.", footnotes: ["5-7.10-13: Text unsicher und teilweise verderbt."] },
      { number: 6, text: "Das Ende kommt. Es kommt das Ende. Das Ende nähert sich dir. Siehe, es kommt." },
      { number: 7, text: "Jetzt ist die Reihe an dir, Bewohner des Landes. Die Zeit ist da, der Tag ist nahe: Tumult, kein Jauchzen mehr auf den Bergen!" },
      { number: 8, text: "Bald gieße ich meinen Zorn über dich aus. Ich stille meinen Zorn an dir, ich spreche dir das Urteil, das dein Verhalten verdient, und lasse alle deine Gräueltaten auf dich zurückfallen." },
      { number: 9, text: "Mein Auge zeigt kein Mitleid und ich übe keine Schonung, nach deinem Verhalten vergelte ich dir. Deine Gräueltaten sollen sich in deiner Mitte auswirken. Dann werdet ihr erkennen, dass ich der Herr bin und dass ich zuschlage." },
      { number: 10, text: "Der Tag ist da. Die Reihe ist an dir; es hat schon begonnen. Der Rechtsbruch gedeiht, die Anmaßung wächst." },
      { number: 11, text: "Die Gewalttat erhebt sich und wird zum Zepter der Bösen. Nichts bleibt von ihnen, nichts von ihrem Reichtum, nichts von ihrer Pracht und Herrlichkeit." },
      { number: 12, text: "Die Zeit kommt; der Tag ist nahe. Der Käufer soll sich nicht freuen, der Verkäufer nicht traurig sein; denn glühender Zorn trifft das ganze Volk.", footnotes: ["Jes 24,2"] },
      { number: 13, text: "Der Verkäufer wird das Verkaufte nicht wiedererlangen, auch wenn sie am Leben bleiben; denn glühender Zorn trifft das ganze Volk. Er erlangt es nicht wieder. Und weil alle schuldig sind, wird keiner sein Leben festhalten können." },
      { number: 14, text: "Blast nur die Trompete und bietet alles auf - es zieht doch keiner in den Kampf; denn mein glühender Zorn trifft das ganze Volk." },
      { number: 15, text: "Draußen das Schwert, drinnen die Pest und der Hunger. Wer auf dem Feld ist, der stirbt durch das Schwert. Wer in der Stadt ist, den fressen Hunger und Pest." },
      { number: 16, text: "Wer entrinnt und verschont wird, haust im Gebirge wie die Tauben in den Felsen; jeder stöhnt wegen seiner Schuld." },
      { number: 17, text: "Alle Hände sinken kraftlos herunter und an allen Knien läuft das Wasser herab.", footnotes: ["Jes 13,7"] },
      { number: 18, text: "Sie legen Trauergewänder an und Schauder erfasst sie. Alle Gesichter sind voll Scham und alle Köpfe sind kahl geschoren.", footnotes: ["Am 8,10; Jer 48,37"] },
      { number: 19, text: "Sie werfen ihr Silber auf die Straße und ihr Gold ekelt sie an. Ihr Silber und Gold kann sie nicht retten am Tag des Zornes des Herrn. Sie werden damit ihre Gier nicht sättigen und ihren Bauch nicht füllen; denn all das war für sie der Anlass, in Sünde zu fallen.", footnotes: ["Zef 1,18"] },
      { number: 20, text: "Ihren kostbaren Schmuck haben sie in ihrer Anmaßung genommen und daraus ihre abscheulichen Bilder, ihre Götzen gemacht. Deshalb verekle ich ihnen ihren Schmuck.", footnotes: ["16,17; Hos 8,4"] },
      { number: 21, text: "Ich gebe ihn den Fremden zur Beute, die Bösen der Erde sollen ihn rauben und entweihen." },
      { number: 22, text: "Ich wende mein Angesicht von ihnen ab und man wird meinen kostbaren Besitz entweihen. Räuber werden kommen und ihn entweihen." },
      { number: 23, text: "Sie werden ein Blutbad anrichten; denn das Land ist voll von Todesurteilen und die Stadt ist voll von Gewalttat.", footnotes: ["Sie werden ein Blutbad anrichten: Text korr.; vgl. G."] },
      { number: 24, text: "Ich führe die schlimmsten Völker herbei, damit sie die Häuser besetzen. Ich mache dem Hochmut der Mächtigen ein Ende, ihre Heiligtümer werden entweiht." },
      { number: 25, text: "Dann bekommen sie Angst und suchen Frieden; doch es wird keinen geben." },
      { number: 26, text: "Unglück kommt über Unglück und eine Schreckensnachricht folgt der andern. Sie verlangen vom Propheten Visionen. Doch der Priester gibt keinen Bescheid, die Ältesten wissen keinen Rat." },
      { number: 27, text: "Der König ist voll Trauer, der Fürst in Entsetzen gehüllt, den Bürgern des Landes erlahmen die Hände. Ich will sie behandeln, wie es ihr Verhalten verdient, und will ihnen das Urteil sprechen, das ihren Urteilen entspricht. Dann werden sie erkennen, dass ich der Herr bin." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Am fünften Tag des sechsten Monats im sechsten Jahr saß ich in meinem Haus und die Ältesten von Juda saßen vor mir. Da legte sich die Hand Gottes, des Herrn, auf mich.", heading: "Visionen: 8,1 - 11,25 | Die Entrückung des Propheten nach Jerusalem: 8,1-4", footnotes: ["14,1; 20,1"] },
      { number: 2, text: "Und ich sah eine Gestalt, die wie ein Mann aussah. Unterhalb von dem, was wie seine Hüften aussah, war Feuer und oberhalb von seinen Hüften schien etwas zu leuchten, wie glänzendes Gold.", footnotes: ["1,26-28", "ein Mann: Text korr. nach G."] },
      { number: 3, text: "Er streckte etwas aus, das wie eine Hand aussah, und packte mich an meinen Haaren. Und der Geist hob mich empor zwischen Erde und Himmel und brachte mich in einer göttlichen Vision nach Jerusalem, an den Eingang des inneren Nordtors, dorthin, wo das Bild steht, das die Eifersucht (des Herrn) erregt.", footnotes: ["3,12; Dan 14,36", "Das «Bild» ist wohl ein Götterbild, möglicherweise das einer Wächtergottheit am Toreingang."] },
      { number: 4, text: "Dort sah ich die Herrlichkeit des Gottes Israels, wie in der Vision, die ich in der Ebene gesehen hatte.", footnotes: ["1,28; 3,22f; 37,1f"] },
      { number: 5, text: "Er sagte zu mir: Menschensohn, richte deinen Blick nach Norden! Ich blickte nach Norden; da sah ich nördlich des Tores, beim Eingang, den Altar mit jenem Bild, das die Eifersucht (des Herrn) erregt.", heading: "Die Entweihung des Tempels: 8,5-18" },
      { number: 6, text: "Er sagte zu mir: Menschensohn, siehst du, was man hier treibt? Es sind große Gräueltaten, die das Haus Israel hier begeht; sie bleiben meinem Heiligtum fern. Aber du wirst noch größere Gräueltaten sehen." },
      { number: 7, text: "Dann brachte er mich zum Eingang des Vorhofs. Ich sah: Ein Loch war in der Wand." },
      { number: 8, text: "Er sagte zu mir: Menschensohn, durchbrich die Wand! Ich durchbrach die Wand - da war ein Eingang." },
      { number: 9, text: "Er sagte zu mir: Geh hinein, sieh dir die schlimmen Gräueltaten an, die man dort begeht." },
      { number: 10, text: "Ich ging hinein und sah: viele Bilder von abscheulichen kleinen und großen Tieren und allen Götzen des Hauses Israel; sie waren ringsum in die Wand eingeritzt." },
      { number: 11, text: "Siebzig Männer von den Ältesten des Hauses Israel, darunter auch Jaasanja, der Sohn Schafans, standen davor. Jeder hatte seine Räucherpfanne in der Hand und der Duft der Weihrauchwolken stieg empor.", footnotes: ["Ex 24,1; Num 11,16"] },
      { number: 12, text: "Er sagte zu mir: Hast du gesehen, Menschensohn, was die Ältesten des Hauses Israel im Finstern treiben, jeder in der Kammer seines Götterbildes? Sie denken: Der Herr sieht uns nicht; der Herr hat das Land verlassen.", footnotes: ["9,9"] },
      { number: 13, text: "Er sagte zu mir: Du wirst sehen, dass sie noch größere Gräueltaten begehen." },
      { number: 14, text: "Dann brachte er mich zum Nordtor am Haus des Herrn. Dort saßen Frauen, die Tammus beweinten.", footnotes: ["Tammus ist ein mesopotamischer Fruchtbarkeitsgott, dessen Abstieg zur Unterwelt in der Zeit der Sommerdürre als ein beklagenswertes Ereignis galt."] },
      { number: 15, text: "Er sagte zu mir: Hast du es gesehen, Menschensohn? Aber du wirst noch größere Gräueltaten sehen." },
      { number: 16, text: "Dann brachte er mich zum Innenhof des Hauses des Herrn. Am Eingang zum Tempel des Herrn, zwischen Vorhalle und Altar, standen etwa fünfundzwanzig Männer, mit dem Rücken zum Tempel des Herrn, mit dem Gesicht nach Osten. Sie beteten, nach Osten gewandt, die Sonne an.", footnotes: ["2 Kön 23,5.11; Jer 8,2; Dtn 4,19"] },
      { number: 17, text: "Er sagte zu mir: Hast du es gesehen, Menschensohn? Waren dem Haus Juda die Gräueltaten, die es hier beging, immer noch nicht genug? Mussten sie auch noch das Land mit ihrer Gewalttätigkeit anfüllen, mussten sie mich immer wieder beleidigen und sich den Zweig an die Nase halten?", footnotes: ["Bedeutung des zuletzt genannten Kultbrauchs unklar."] },
      { number: 18, text: "Darum werde auch ich voll Zorn handeln. Mein Auge wird kein Mitleid zeigen und ich werde keine Schonung üben. Auch wenn sie mir laut in die Ohren schreien, werde ich sie nicht hören.", footnotes: ["5,11; Jes 1,15"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Und er schrie mir laut in die Ohren: Das Strafgericht über die Stadt ist nahe. Jeder soll sein Werkzeug zum Zertrümmern in die Hand nehmen.", heading: "Die Zerstörung der Stadt: 9,1 - 10,7" },
      { number: 2, text: "Da kamen sechs Männer vom oberen Tor, das im Norden liegt. Jeder hatte sein Werkzeug zum Zertrümmern in der Hand. Unter ihnen war auch ein Mann, der ein leinenes Gewand anhatte; an seinem Gürtel hing Schreibzeug. Sie kamen herein und stellten sich neben den Altar aus Bronze.", footnotes: ["Offb 1,13"] },
      { number: 3, text: "Die Herrlichkeit des Gottes Israels schwebte von den Kerubim, über denen sie war, hinüber zur Schwelle des Tempels. Er rief den Mann, der das leinene Gewand anhatte und an dessen Gürtel das Schreibzeug hing.", footnotes: ["1,28", "von den Kerubim: Text korr."] },
      { number: 4, text: "Der Herr sagte zu ihm: Geh mitten durch die Stadt Jerusalem und schreib ein T auf die Stirn aller Männer, die über die in der Stadt begangenen Gräueltaten seufzen und stöhnen.", footnotes: ["Offb 7,2f; 9,4", "Das T hat in der althebräischen Schrift die Form unseres Malzeichens (x) und dient zur besonderen Kennzeichnung."] },
      { number: 5, text: "Und ich hörte, wie er zu den anderen sagte: Geht hinter ihm her durch die Stadt und schlagt zu! Euer Auge soll kein Mitleid zeigen, gewährt keine Schonung!" },
      { number: 6, text: "Alt und jung, Mädchen, Kinder und Frauen sollt ihr erschlagen und umbringen. Doch von denen, die das T auf der Stirn haben, dürft ihr keinen anrühren. Beginnt in meinem Heiligtum! Da begannen sie bei den Ältesten, die vor dem Tempel standen." },
      { number: 7, text: "Er sagte zu ihnen: Macht den Tempel unrein, füllt seine Höfe mit Erschlagenen! Dann geht hinaus und schlagt in der Stadt zu!", footnotes: ["Dann . . . schlagt zu!: Text korr.; vgl. G."] },
      { number: 8, text: "Sie schlugen zu und ich allein blieb übrig; da fiel ich nieder auf mein Gesicht und schrie: Ach, Herr und Gott, willst du deinen ganzen Zorn über Jerusalem ausschütten und auch noch den letzten Rest Israels vernichten?", footnotes: ["11,13; Am 7,2f"] },
      { number: 9, text: "Er sagte zu mir: Die Schuld des Hauses Israel und des Hauses Juda ist groß, ja übergroß. Das Land ist voll Blutschuld, die Stadt ist voll Unrecht. Sie sagen: Der Herr sieht es nicht; der Herr hat das Land verlassen.", footnotes: ["8,12"] },
      { number: 10, text: "Darum zeigt mein Auge kein Mitleid und ich übe keine Schonung. Ihr Verhalten lasse ich auf sie selbst zurückfallen.", footnotes: ["5,11", "Ihr Verhalten lasse ich auf sie zurückfallen, wörtlich: Ihren Weg gebe ich auf ihren Kopf (ebenso 11,21)."] },
      { number: 11, text: "Und der Mann, der das leinene Gewand anhatte und an dessen Gürtel das Schreibzeug hing, (kam und) berichtete: Ich habe getan, was du mir befohlen hast." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Ich sah: Oberhalb der gehämmerten Platte über den Köpfen der Kerubim war etwas, das wie Saphir aussah und einem Thron glich.", footnotes: ["1,22"] },
      { number: 2, text: "Er sagte zu dem Mann, der das leinene Gewand anhatte: Geh zwischen die Räder unter den Kerubim, nimm zwei Hände voll von den glühenden Kohlen, die zwischen den Kerubim sind, und streu sie über die Stadt! Da ging der Mann vor meinen Augen", footnotes: ["Offb 8,5"] },
      { number: 3, text: "zu den Kerubim. Sie standen rechts vom Tempel, als der Mann zu ihnen ging, und die Wolke erfüllte den Innenhof." },
      { number: 4, text: "Die Herrlichkeit des Herrn schwebte von den Kerubim hinüber zur Schwelle des Tempels. Der Tempel wurde von der Wolke erfüllt und der Vorhof war voll vom Glanz der Herrlichkeit des Herrn.", footnotes: ["1,28; 40,34; 1 Kön 8,10f; Jes 6,4"] },
      { number: 5, text: "Das Rauschen der Flügel der Kerubim war bis zum Vorhof zu hören; es war wie die Stimme des allmächtigen Gottes, wenn er spricht." },
      { number: 6, text: "Als er dem Mann, der das leinene Gewand anhatte, befahl: Nimm von dem Feuer, das zwischen den Rädern und zwischen den Kerubim ist!, ging der Mann und stellte sich neben das Rad." },
      { number: 7, text: "Und ein Kerub streckte seine Hand aus, nahm von dem Feuer, das zwischen den Kerubim war, und legte es in die Hände des Mannes, der das leinene Gewand anhatte. Der Mann nahm das Feuer und ging hinaus." },
      { number: 8, text: "Unter den Flügeln der Kerubim wurde etwas sichtbar, das wie eine Menschenhand aussah.", heading: "Der Thronwagen Gottes: 10,8-17", footnotes: ["(8-17) 1,4-28"] },
      { number: 9, text: "Und ich sah neben den Kerubim vier Räder, ein Rad neben jedem Kerub. Die Räder waren wie glitzernder Chrysolith." },
      { number: 10, text: "Alle vier sahen gleich aus und es schien so, als laufe ein Rad mitten im andern." },
      { number: 11, text: "Sie konnten nach allen vier Seiten laufen und änderten beim Laufen ihre Richtung nicht; denn der Richtung, die das vordere Rad einschlug, folgten die anderen. Sie änderten beim Laufen ihre Richtung nicht." },
      { number: 12, text: "Ihr ganzer Leib, ihr Rücken, ihre Hände und Flügel und auch die Räder waren bei allen vier ringsum voll Augen.", footnotes: ["Offb 4,8"] },
      { number: 13, text: "Die Räder wurden, wie ich deutlich hörte, «Wirbel» genannt.", footnotes: ["Wirbel: H: galgal, das auch Rad, Räderwerk bedeuten kann."] },
      { number: 14, text: "Jedes Lebewesen hatte vier Gesichter. Das erste war ein Kerubgesicht, das zweite ein Menschengesicht, das dritte ein Löwengesicht und das vierte ein Adlergesicht.", footnotes: ["H wörtlich: Das erste (Lebewesen) hatte ein Kerubgesicht, das zweite ein . . ."] },
      { number: 15, text: "Die Kerubim konnten emporschweben. Es waren die Lebewesen, die ich am Fluss Kebar gesehen hatte." },
      { number: 16, text: "Wenn die Kerubim gingen, dann liefen die Räder an ihrer Seite mit. Auch wenn die Kerubim ihre Flügel bewegten, um sich von der Erde zu erheben, lösten sich die Räder nicht von ihrer Seite." },
      { number: 17, text: "Blieben die Kerubim stehen, dann standen auch die Räder still. Hoben sich die Kerubim empor, dann hoben sich die Räder mit ihnen; denn der Geist der Lebewesen war in den Rädern." },
      { number: 18, text: "Da verließ die Herrlichkeit des Herrn die Schwelle des Tempels und nahm wieder ihren Platz über den Kerubim ein.", heading: "Der Auszug des Herrn aus dem Tempel: 10,18-22", footnotes: ["1,28; 43,8"] },
      { number: 19, text: "Die Kerubim bewegten ihre Flügel und hoben sich vor meinen Augen vom Boden empor. Sie gingen hinaus und die Räder liefen an ihrer Seite mit. Vor dem östlichen Tor am Haus des Herrn blieben sie stehen. Und die Herrlichkeit des Gottes Israels schwebte über ihnen." },
      { number: 20, text: "Es waren die Lebewesen, die ich unter dem Thron des Gottes Israels am Fluss Kebar gesehen hatte, und ich erkannte, dass es Kerubim waren." },
      { number: 21, text: "Jedes dieser Lebewesen hatte vier Gesichter und vier Flügel. Unter ihren Flügeln hatten sie etwas, das wie Menschenhände aussah." },
      { number: 22, text: "Ihre Gesichter glichen den Gesichtern, die ich am Fluss Kebar gesehen hatte. Jedes Lebewesen ging in die Richtung, in die eines seiner Gesichter wies." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Da hob mich der Geist empor und brachte mich zum Osttor am Haus des Herrn, das nach Osten schaut. Vor dem Tor standen fünfundzwanzig Männer. Unter ihnen sah ich Jaasanja, den Sohn Asurs, und Pelatja, den Sohn Benajas, führende Männer im Volk.", heading: "Das Strafgericht über die führenden Männer Jerusalems: 11,1-13", footnotes: ["3,12"] },
      { number: 2, text: "Er sagte zu mir: Menschensohn, das sind die Männer, die in der Stadt Unheil ersinnen und böse Pläne fassen." },
      { number: 3, text: "Sie sagen: In nächster Zeit braucht man keine Häuser zu bauen. Die Stadt ist der Topf und wir sind das Fleisch.", footnotes: ["Der Sinn des ersten Satzes ist nicht klar. Das Bild vom Topf bezeichnet hier die schützende Stadt, in der der bessere Teil des Volkes geborgen ist."] },
      { number: 4, text: "Darum tritt als Prophet gegen sie auf, Menschensohn, tritt als Prophet auf!" },
      { number: 5, text: "Da überfiel mich der Geist des Herrn und er sagte zu mir: Sag: So spricht der Herr: Das habt ihr gesagt, ihr vom Haus Israel. Ich weiß sehr gut, was ihr im Sinn hattet.", footnotes: ["24,1-14"] },
      { number: 6, text: "Ihr habt viele in dieser Stadt erschlagen, die Straßen der Stadt sind voll von Erschlagenen." },
      { number: 7, text: "Darum - so spricht Gott, der Herr: Das Fleisch, das sind die Erschlagenen, die ihr mitten in der Stadt getötet habt. Der Topf, das ist die Stadt. Euch aber werde ich aus ihr vertreiben.", footnotes: ["werde ich . . . vertreiben: Text korr. nach den alten Übersetzungen und manchen H-Handschriften."] },
      { number: 8, text: "Das Schwert fürchtet ihr; darum werde ich das Schwert über euch bringen - Spruch Gottes, des Herrn.", footnotes: ["5,17; 6,3; 33,2"] },
      { number: 9, text: "Ich vertreibe euch aus der Stadt, ich liefere euch den Fremden aus und halte ein Strafgericht über euch ab.", footnotes: ["5,8.10.15"] },
      { number: 10, text: "Unter dem Schwert werdet ihr fallen. An Israels Grenze halte ich Gericht über euch. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 11, text: "Die Stadt wird für euch nicht der Topf sein und ihr nicht das Fleisch darin. An Israels Grenze halte ich Gericht über euch." },
      { number: 12, text: "Daran werdet ihr erkennen, dass ich der Herr bin, dessen Gesetze ihr nicht gehalten und dessen Rechtsvorschriften ihr nicht befolgt habt. Stattdessen habt ihr nach den Bräuchen der Völker ringsum gehandelt.", footnotes: ["5,7"] },
      { number: 13, text: "Während ich diese prophetischen Worte sprach, starb Pelatja, der Sohn Benajas. Da fiel ich nieder auf mein Gesicht, schrie laut auf und rief: Ach, Herr und Gott, willst du auch noch den Rest Israels ausrotten?", footnotes: ["9,8"] },
      { number: 14, text: "Das Wort des Herrn erging an mich:", heading: "Die Erneuerung Israels: 11,14-25" },
      { number: 15, text: "Menschensohn, die Einwohner Jerusalems sagen von deinen Brüdern, deinen Verwandten und dem ganzen Haus Israel: Sie sind fern vom Herrn; das Land ist uns zum Besitz gegeben." },
      { number: 16, text: "Darum sag: So spricht Gott, der Herr: Auch wenn ich sie weit weg unter die Völker geführt und in alle Länder zerstreut habe, so bin ich doch in den Ländern, wohin sie gekommen sind, beinahe zum Heiligtum für sie geworden.", footnotes: ["6,8-10", "Sinn unklar."] },
      { number: 17, text: "Darum sag: So spricht Gott, der Herr: Ich führe euch aus allen Völkern zusammen, sammle euch aus den Ländern, in die ihr zerstreut seid, und gebe euch das Land Israel.", footnotes: ["Jer 24,5f; 29,14"] },
      { number: 18, text: "Und sie werden dorthin kommen und alle ihre abscheulichen Götzen aus dem Land entfernen." },
      { number: 19, text: "Ich schenke ihnen ein anderes Herz und schenke ihnen einen neuen Geist. Ich nehme das Herz von Stein aus ihrer Brust und gebe ihnen ein Herz von Fleisch,", footnotes: ["36,26; Jer 24,7", "ein anderes Herz: Text korr. nach G; H: ein einziges (d. h. ungeteilt treues) Herz."] },
      { number: 20, text: "damit sie nach meinen Gesetzen leben und auf meine Rechtsvorschriften achten und sie erfüllen. Sie werden mein Volk sein und ich werde ihr Gott sein.", footnotes: ["14,11; Jer 24,7"] },
      { number: 21, text: "Die aber, deren Herz an ihren Götzen und an ihren Gräueltaten hängt - Spruch Gottes, des Herrn: Ihr Verhalten lasse ich auf sie selbst zurückfallen.", footnotes: ["Vgl. die Anmerkung zu 9,10."] },
      { number: 22, text: "Dann hoben die Kerubim ihre Flügel. Die Räder bewegten sich zugleich mit den Kerubim und die Herrlichkeit des Gottes Israels war über ihnen." },
      { number: 23, text: "Die Herrlichkeit des Herrn stieg aus der Mitte der Stadt empor; auf dem Berg im Osten der Stadt blieb sie stehen.", footnotes: ["43,3"] },
      { number: 24, text: "Der Geist hob mich empor und brachte mich in dieser göttlichen Vision nach Chaldäa zur Gemeinde der Verschleppten. Dann hob sich das, was ich in der Vision gesehen hatte, empor und verschwand vor meinen Augen.", footnotes: ["3,12", "in dieser göttlichen Vision, wörtlich: in der Vision durch den Geist Gottes; die Übersetzung ist an 8,3 angeglichen."] },
      { number: 25, text: "Und ich erzählte den Verschleppten alles, was der Herr mich hatte sehen lassen." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Weitere symbolische Handlungen: 12,1-20 | Die Verschleppung: 12,1-16" },
      { number: 2, text: "Menschensohn, du wohnst mitten unter einem widerspenstigen Volk, das Augen hat, um zu sehen, und doch nicht sieht, das Ohren hat, um zu hören, und doch nicht hört; denn sie sind ein widerspenstiges Volk.", footnotes: ["2,5-7; Jer 5,21"] },
      { number: 3, text: "Du, Menschensohn, pack deine Sachen, als würdest du verschleppt, und geh am hellen Tag vor ihren Augen weg, als ob du vor ihren Augen von deinem Wohnsitz an einen andern verschleppt würdest. Vielleicht sehen sie es; aber sie sind ja ein widerspenstiges Volk." },
      { number: 4, text: "Trag dein Gepäck bei Tag vor ihren Augen hinaus wie ein Mann, der verschleppt wird. Am Abend aber geh selbst vor ihren Augen hinaus wie die Leute, die in die Verbannung ziehen." },
      { number: 5, text: "Brich dir vor ihren Augen ein Loch in die Wand und kriech hindurch!", footnotes: ["kriech hindurch: Text korr. nach den alten Übersetzungen; entsprechend auch VV. 7 und 12."] },
      { number: 6, text: "Vor ihren Augen nimm das Gepäck auf die Schulter! Bring es in der Dunkelheit weg! Verhülle dein Gesicht, damit du das Land nicht mehr siehst. Denn ich habe dich zum Mahnzeichen für das Haus Israel gemacht.", footnotes: ["24,24.27; Jes 8,18; 20,3"] },
      { number: 7, text: "Ich tat, was mir befohlen wurde. Bei Tag trug ich mein Gepäck hinaus wie ein Mann, der verschleppt wird. Am Abend brach ich mit den Händen ein Loch durch die Wand; in der Dunkelheit kroch ich hindurch. Dann nahm ich vor ihren Augen das Gepäck auf die Schulter." },
      { number: 8, text: "Am nächsten Morgen erging das Wort des Herrn an mich:" },
      { number: 9, text: "Menschensohn, hat nicht das Haus Israel, das widerspenstige Volk, zu dir gesagt: Was machst du da?" },
      { number: 10, text: "Sag zu ihnen: So spricht Gott, der Herr: Dieses drohende Wort gilt dem Fürsten von Jerusalem und dem ganzen Volk Israel, das in Jerusalem wohnt." },
      { number: 11, text: "Sag: Ich bin ein Mahnzeichen für euch: Was ich getan habe, das wird mit ihnen geschehen; in die Verbannung, in die Gefangenschaft werden sie ziehen." },
      { number: 12, text: "Ihr Fürst wird in der Dunkelheit sein Gepäck auf die Schulter nehmen und hinausgehen. In die Mauer wird man ein Loch brechen, um hindurchzugehen. Er wird sein Gesicht verhüllen, um mit seinen Augen das Land nicht zu sehen.", footnotes: ["2 Kön 25,4"] },
      { number: 13, text: "Ich aber werfe mein Netz über ihn, damit er sich in meinen Schlingen fängt. Dann bringe ich ihn nach Babel, ins Land der Chaldäer; doch er wird nichts davon sehen. Dort wird er sterben.", footnotes: ["17,20"] },
      { number: 14, text: "Alle, die bei ihm sind und ihm helfen wollen, alle seine Truppen zerstreue ich in alle Winde und ich zücke das Schwert hinter ihnen." },
      { number: 15, text: "Dann werden sie erkennen, dass ich der Herr bin. Wenn ich sie unter die Völker zerstreue und in alle Länder vertreibe,", footnotes: ["20,23"] },
      { number: 16, text: "lasse ich einige von ihnen das Schwert, den Hunger und die Pest überleben, damit sie bei den Völkern, zu denen sie kommen, von all ihren Gräueltaten erzählen. Dann werden sie erkennen, dass ich der Herr bin.", footnotes: ["6,8"] },
      { number: 17, text: "Das Wort des Herrn erging an mich:", heading: "Die Not der Belagerten: 12,17-20" },
      { number: 18, text: "Menschensohn, iss dein Brot mit Zittern und trink dein Wasser mit Angst und Entsetzen!" },
      { number: 19, text: "Dann sag zum Volk im Land: So spricht Gott, der Herr, zu den Einwohnern Jerusalems über das Land Israel: Sie werden ihr Brot mit Angst essen und ihr Wasser mit Schaudern trinken; denn ihr Land wird verwüstet und ausgeplündert wegen der Gewalttaten all seiner Bewohner.", footnotes: ["4,16; 19,7", "ihr Land: Text korr. nach den alten Übersetzungen und mehreren H-Handschriften."] },
      { number: 20, text: "Die bewohnten Städte sollen verheert und das Land verwüstet werden. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 21, text: "Das Wort des Herrn erging an mich:", heading: "Drohungen und Mahnungen: 12,21 - 23,49 | Gegen die Verächter des Propheten: 12,21-28", footnotes: ["21-23: Das Sprichwort bedeutet Kritik und Ablehnung der Verkündigung Ezechiels. Der Prophet kehrt es um und widerlegt es so. Entsprechendes gilt von VV. 27f."] },
      { number: 22, text: "Menschensohn, was habt ihr da für ein Sprichwort im Land Israel? Ihr sagt: Die Zeit zieht sich hin, die Visionen erfüllen sich nie.", footnotes: ["18,2; 2 Petr 3,1-4"] },
      { number: 23, text: "Darum sag zu ihnen: So spricht Gott, der Herr: Ich werde mit diesem Sprichwort Schluss machen und man wird es in Israel nicht mehr gebrauchen. Sag stattdessen zu ihnen: Die Zeit und alles, was die Visionen verkünden, ist nahe.", footnotes: ["Hab 2,3"] },
      { number: 24, text: "Denn nichtige Sehersprüche und trügerische Orakel wird es im Haus Israel nicht mehr geben." },
      { number: 25, text: "Denn ich, der Herr, sage, was ich sage, damit es geschieht. Es lässt nicht mehr lange auf sich warten; denn, du widerspenstiges Volk, wenn ich in euren Tagen spreche, dann führe ich auch aus, was ich sage - Spruch Gottes, des Herrn." },
      { number: 26, text: "Das Wort des Herrn erging an mich:" },
      { number: 27, text: "Menschensohn, das Haus Israel sagt: Die Vision, die er hat, handelt von späteren Tagen, er weissagt für ferne Zeiten." },
      { number: 28, text: "Darum sag zu ihnen: So spricht Gott, der Herr: Nichts von dem, was ich sage, lässt lange auf sich warten; was ich sage, geschieht Spruch Gottes, des Herrn." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Gegen die falschen Propheten: 13,1-23" },
      { number: 2, text: "Menschensohn, sprich als Prophet gegen die Propheten Israels und sag zu denen, die aus ihrem eigenen Herzen heraus prophetisch reden: Hört das Wort des Herrn!", footnotes: ["Text korr. nach G."] },
      { number: 3, text: "So spricht Gott, der Herr: Weh den törichten Propheten, die nur ihrem eigenen Geist folgen und nichts geschaut haben.", footnotes: ["Jer 14,14"] },
      { number: 4, text: "Wie Füchse in Ruinen sind deine Propheten, Israel.", footnotes: ["4f: Statt auf das Fehlverhalten des Volkes aufmerksam zu machen, haben die falschen Propheten das Volk in seinem Verhalten bestärkt."] },
      { number: 5, text: "Ihr seid nicht in die Bresche gesprungen. Ihr habt keine Mauer für das Haus Israel errichtet, damit es am Tag des Herrn im Kampf standhalten kann.", footnotes: ["22,30"] },
      { number: 6, text: "Sie haben nichtige Visionen, verkünden falsche Orakel und sagen: Spruch des Herrn - obwohl der Herr sie nicht gesandt hat. Trotzdem warten sie darauf, dass er ihre Worte erfüllt.", footnotes: ["22,28", "verkünden . . . Orakel: Text korr.; vgl. G."] },
      { number: 7, text: "Ist es nicht so, dass ihr nichtige Visionen gehabt, falsche Orakel verkündet und gesagt habt: Spruch des Herrn - obwohl ich gar nicht gesprochen hatte?" },
      { number: 8, text: "Darum - so spricht Gott, der Herr: Weil ihr leere Worte gemacht habt und trügerische Visionen hattet, darum gehe ich gegen euch vor Spruch Gottes, des Herrn." },
      { number: 9, text: "Ich strecke meine Hand gegen die Propheten aus, die nichtige Visionen haben und falsche Orakel verkünden. Sie gehören nicht in die Gemeinschaft meines Volkes und sollen nicht in der Liste des Hauses Israel verzeichnet sein; sie werden nicht in das Land Israel kommen. Dann werdet ihr erkennen, dass ich Gott, der Herr, bin.", footnotes: ["14,9"] },
      { number: 10, text: "Sie führen mein Volk in die Irre und verkünden Heil, wo es kein Heil gibt, und wenn das Volk eine Mauer aufrichtet, dann übertünchen sie sie.", footnotes: ["Jer 6,14"] },
      { number: 11, text: "Deshalb sag denen, die sie übertünchen: Sie wird einstürzen. Es kommt ein Wolkenbruch, Hagel fällt wie Steine vom Himmel, ein Sturm bricht los", footnotes: ["Text korr."] },
      { number: 12, text: "und schon stürzt die Mauer ein. Wird man dann nicht zu euch sagen: Wo ist jetzt eure Tünche?" },
      { number: 13, text: "Darum - so spricht Gott, der Herr: Ich lasse in meinem Zorn einen Sturm losbrechen, in meinem Groll schicke ich einen Wolkenbruch, in meinem Zorn einen verheerenden Hagelschlag.", footnotes: ["schicke ich einen, wörtlich: wird es sein, ein. . ."] },
      { number: 14, text: "Ich reiße die Wand ein, die ihr übertüncht habt, ich lasse sie zu Boden stürzen und ihr Fundament wird bloßgelegt. Und wenn sie einstürzt, werdet ihr in ihren Trümmern umkommen. Dann werdet ihr erkennen, dass ich der Herr bin.", footnotes: ["in ihren Trümmern, wörtlich: mitten in ihr."] },
      { number: 15, text: "Ich lasse meinen Zorn an der Mauer aus und an denen, die sie übertüncht haben, und ich sage euch: Die Mauer ist weg und weg sind die, die sie übertüncht haben," },
      { number: 16, text: "die Propheten Israels, die über Jerusalem prophezeien und der Stadt mit ihren Visionen Heil versprechen, obwohl es kein Heil gibt - Spruch Gottes, des Herrn." },
      { number: 17, text: "Du, Menschensohn, richte dein Gesicht auf die Töchter deines Volkes, die aus ihrem eigenen Herzen heraus prophetisch reden. Sprich als Prophet gegen sie!" },
      { number: 18, text: "Sag: So spricht Gott, der Herr: Weh den Frauen, die Zauberbinden für alle Handgelenke nähen und Zaubermützen für Leute jeder Größe anfertigen, um damit auf Menschenjagd zu gehen. Meint ihr, ihr könnt in meinem Volk Menschen jagen und Menschen verschonen, je nachdem, wie es euch passt?", footnotes: ["für alle Handgelenke: Text korr.; vgl. die alten Übersetzungen. - Die Frauen üben magische Zauberhandlungen aus, mit denen sie unheilvolle Kräfte an die Menschen binden oder von ihnen fernhalten wollen."] },
      { number: 19, text: "Ihr habt mich entweiht in meinem Volk für ein paar Hände voll Gerste und für ein paar Bissen Brot: Ihr habt Menschen getötet, die nicht sterben sollten, und Menschen verschont, die nicht am Leben bleiben sollten; ihr habt mein Volk belogen, das so gern auf Lügen hört.", footnotes: ["1 Sam 9,7"] },
      { number: 20, text: "Darum - so spricht Gott, der Herr: Ich gehe gegen eure Zauberbinden vor, mit denen ihr die Menschen jagt wie Vögel, und reiße sie von euren Armen und lasse die Menschen frei, die ihr gefangen habt wie Vögel." },
      { number: 21, text: "Ich reiße die Zaubermützen, die ihr gemacht habt, herunter und befreie mein Volk aus eurer Gewalt. Es wird nicht länger in eurer Gewalt bleiben. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 22, text: "Ihr habt dem Herzen des Gerechten durch eure Lügen Kummer bereitet; ich aber wollte ihm keinen Kummer bereiten. Statt das Leben des Bösen zu retten, habt ihr ihn ermutigt, sein verkehrtes Verhalten nicht zu ändern.", footnotes: ["Jer 23,14", "Ihr habt. . . Kummer bereitet: Text korr.; vgl. den zweiten Satz."] },
      { number: 23, text: "Darum sollt ihr keine nichtigen Visionen mehr haben und keine Orakel mehr verkünden. Ich werde mein Volk aus eurer Gewalt befreien. Dann werdet ihr erkennen, dass ich der Herr bin." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Einige von den Ältesten Israels kamen zu mir und setzten sich vor mir nieder.", heading: "Gegen die Götzendiener: 14,1-11", footnotes: ["8,1; 20,1"] },
      { number: 2, text: "Da erging das Wort des Herrn an mich:" },
      { number: 3, text: "Menschensohn, diese Männer haben die Götzen in ihr Herz geschlossen, sie haben sie vor sich aufgestellt; das wurde für sie der Anlass, in Schuld zu fallen. Und jetzt soll ich mich von ihnen befragen lassen?" },
      { number: 4, text: "Darum sprich mit ihnen und sag zu ihnen: Wenn jemand aus dem Haus Israel die Götzen in sein Herz geschlossen und sie vor sich aufgestellt hat - was für ihn der Anlass geworden ist, in Schuld zu fallen - und wenn er dann zum Propheten kommt, dann werde ich, der Herr, dem, der mit seinen vielen Götzen zu mir kommt, in eigener Person antworten:", footnotes: ["in eigener Person: Text korr. nach V. 7."] },
      { number: 5, text: "Ich werde das Haus Israel hart anfassen, weil sie alle sich um ihrer Götzen willen von mir abgewandt haben.", footnotes: ["hart anfassen, wörtlich: an seinem Herzen packen."] },
      { number: 6, text: "Darum sag zum Haus Israel: So spricht Gott, der Herr: Kehrt um! Verlasst eure Götzen und wendet eure Augen ab von all euren abscheulichen Göttern!" },
      { number: 7, text: "Wenn jemand aus dem Haus Israel oder aus den in Israel lebenden Fremden sich von mir abwendet, wenn er die Götzen in sein Herz schließt und sie vor sich aufstellt - was für ihn der Anlass wird, in Schuld zu fallen - und wenn er dann zum Propheten kommt, um mich zu befragen, dann werde ich, der Herr, ihm in eigener Person antworten:" },
      { number: 8, text: "Ich richte meinen Blick gegen diesen Mann und mache ihn zum sprichwörtlichen (warnenden) Zeichen, ich merze ihn aus meinem Volk aus. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 9, text: "Wenn der Prophet sich jedoch dazu verleiten lässt, etwas zu sagen, dann habe ich, der Herr, diesen Propheten verleitet und ich werde meine Hand gegen ihn ausstrecken und ihn mitten in meinem Volk Israel vernichten.", footnotes: ["1 Kön 22,20-23; Ez 13,9"] },
      { number: 10, text: "Sie werden beide die Folgen ihrer Schuld tragen müssen; denn die Schuld dessen, der mich befragt, und die Schuld des Propheten sind gleich." },
      { number: 11, text: "Dann wird sich das Haus Israel nicht mehr davon abbringen lassen, mir zu folgen, und wird sich nicht länger durch all seine Sünden unrein machen. Sie werden mein Volk sein und ich werde ihr Gott sein - Spruch Gottes, des Herrn." },
      { number: 12, text: "Das Wort des Herrn erging an mich:", heading: "Das unerbittliche Gericht Gottes: 14,12-23", footnotes: ["(12-23) 18; 33,10-20"] },
      { number: 13, text: "Menschensohn, wenn sich ein Land gegen mich versündigt und mir die Treue bricht und wenn ich dann meine Hand gegen das Land ausstrecke, ihm seinen Vorrat an Brot entziehe, den Hunger ins Land schicke und Mensch und Tier ausrotte", footnotes: ["4,16"] },
      { number: 14, text: "und wenn in diesem Land die drei Männer Noach, Daniel und Ijob leben würden, dann würden nur diese drei um ihrer Gerechtigkeit willen ihr Leben retten - Spruch Gottes, des Herrn.", footnotes: ["Noach (Gen 6,9; 7,1), Daniel (in ugaritischen Texten ein gerechter Herrscher, kaum identisch mit dem biblischen Daniel) und Ijob (Ijob 1,1 - 2,10) gelten als beispielhaft gerecht."] },
      { number: 15, text: "Oder wenn ich wilde Tiere gegen das Land losließe, die es entvölkern, sodass es zur Wüste würde und wegen der wilden Tiere kein Mensch mehr durch das Land reisen könnte,", footnotes: ["Lev 26,22"] },
      { number: 16, text: "und wenn diese drei Männer darin wären - so wahr ich lebe, Spruch Gottes, des Herrn: sie würden nicht einmal ihre eigenen Söhne und Töchter retten. Nur sie selbst könnten sich retten, das Land aber würde zur Wüste werden." },
      { number: 17, text: "Oder wenn ich das Schwert über dieses Land bringen und sagen würde: Ein Schwert soll durch das Land fahren, ich will Mensch und Tier darin ausrotten!," },
      { number: 18, text: "und wenn diese drei Männer darin wären - so wahr ich lebe, Spruch Gottes, des Herrn: sie würden nicht einmal ihre eigenen Söhne und Töchter retten. Nur sie selbst könnten sich retten." },
      { number: 19, text: "Oder wenn ich die Pest in jenes Land schicken und meinen Zorn darüber ausgießen würde, um Mensch und Tier in einem Blutbad zu vernichten," },
      { number: 20, text: "und wenn Noach, Daniel und Ijob in dem Land wären - so wahr ich lebe, Spruch Gottes, des Herrn: nicht einmal ihren Sohn und ihre Tochter würden sie retten. Sie würden nur ihr eigenes Leben retten, um ihrer Gerechtigkeit willen." },
      { number: 21, text: "Wahrhaftig, so spricht Gott, der Herr: Selbst wenn ich die vier schlimmsten Strafen, Schwert, Hunger, wilde Tiere und Pest, über Jerusalem bringe, um Mensch und Tier auszurotten,", footnotes: ["5,17; Lev 26,16-25; Jer 15,3; Offb 6,8"] },
      { number: 22, text: "werden einige in der Stadt verschont werden und übrig bleiben und ihre Söhne und Töchter zu euch herausführen. Wenn ihr dann ihr Verhalten und ihre Taten seht, werdet ihr euch über das Unheil hinwegtrösten, das ich über Jerusalem verhängt habe, über alles, was ich über die Stadt kommen ließ." },
      { number: 23, text: "Sie trösten euch darüber hinweg, weil ihr ihr Verhalten und ihre Taten seht und daran erkennt, dass ich all das, was ich mit der Stadt machte, nicht ohne Grund getan habe - Spruch Gottes, des Herrn." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Gegen Jerusalem, das unnütze Holz vom Weinstock: 15,1-8" },
      { number: 2, text: "Menschensohn, was hat das Holz des Weinstocks / vor dem Holz aller anderen Sträucher voraus, / die zwischen den Bäumen des Waldes wachsen?" },
      { number: 3, text: "Nimmt man es etwa, um daraus etwas zu machen? / Oder gebraucht man es als Pflock / und hängt allerlei Geräte daran auf?" },
      { number: 4, text: "Nein, man wirft es dem Feuer zum Fraß vor. / Sind dann die beiden Enden des Holzes vom Feuer verzehrt und hat die Glut seine Mitte erfasst, / ist es dann noch geeignet, / um etwas daraus zu machen?", footnotes: ["Joh 15,6"] },
      { number: 5, text: "Schon als es noch ganz war, / konnte man nichts daraus machen. Hat das Feuer es aber gefressen und ist es verglüht, / kann man erst recht nichts daraus machen." },
      { number: 6, text: "Darum - so spricht Gott, der Herr: / Wie ich das Holz des Weinstocks, / das zwischen den Bäumen des Waldes heranwuchs, dem Feuer zum Fraß übergab, / so behandle ich auch die, die in Jerusalem wohnen.", footnotes: ["6f: Das Gleichnis vom Rebholz mit dem anschließenden Drohwort ist in die Situation Jerusalems von 586 v. Chr. hineingesprochen. Das Feuer des göttlichen Gerichts hat 597 gewütet; es wird auch den Rest verzehren."] },
      { number: 7, text: "Wenn ich meinen Blick auf sie richte, / dann wird sie, auch wenn sie dem Feuer entkommen, das Feuer verzehren. Ihr werdet erkennen, dass ich der Herr bin, / wenn ich meinen Blick auf sie richte." },
      { number: 8, text: "Ich mache das Land zur Wüste; / denn sie haben die Treue gebrochen - / Spruch Gottes, des Herrn." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Gegen Jerusalem, die treulose Frau: 16,1-63" },
      { number: 2, text: "Menschensohn, mach Jerusalem seine Gräueltaten bewusst!" },
      { number: 3, text: "Sag: So spricht Gott, der Herr, zu Jerusalem: Deiner Herkunft und deiner Geburt nach stammst du aus dem Land der Kanaaniter. Dein Vater war ein Amoriter, deine Mutter eine Hetiterin.", footnotes: ["16,45", "Jerusalem war eine kanaanäische Stadt mit (zumindest in einer bestimmten Zeit) hetitischer Oberschicht, ehe es unter David in dessen Reich einbezogen wurde."] },
      { number: 4, text: "Bei deiner Geburt, als du geboren wurdest, hat man deine Nabelschnur nicht abgeschnitten. Man hat dich nicht mit Wasser abgewaschen, nicht mit Salz eingerieben, nicht in Windeln gewickelt.", footnotes: ["abgewaschen: H fügt ein Wort unsicherer Bedeutung hinzu, das in G fehlt."] },
      { number: 5, text: "Nichts von all dem hat man getan, kein Auge zeigte dir Mitleid, niemand übte Schonung an dir, sondern am Tag deiner Geburt hat man dich auf freiem Feld ausgesetzt, weil man dich verabscheute." },
      { number: 6, text: "Da kam ich an dir vorüber und sah dich in deinem Blut zappeln; und ich sagte zu dir, als du blutverschmiert dalagst: Bleib am Leben!", footnotes: ["6b: Der Satz steht doppelt im Text."] },
      { number: 7, text: "Wie eine Blume auf der Wiese ließ ich dich wachsen. Und du bist herangewachsen, bist groß geworden und herrlich aufgeblüht. Deine Brüste wurden fest; dein Haar wurde dicht. Doch du warst nackt und bloß.", footnotes: ["ließ ich dich wachsen: sinngemäße Wiedergabe des schwierigen Textes."] },
      { number: 8, text: "Da kam ich an dir vorüber und sah dich, und siehe, deine Zeit war gekommen, die Zeit der Liebe. Ich breitete meinen Mantel über dich und bedeckte deine Nacktheit. Ich leistete dir den Eid und ging mit dir einen Bund ein - Spruch Gottes, des Herrn - und du wurdest mein.", footnotes: ["Rut 3,9"] },
      { number: 9, text: "Dann habe ich dich gebadet, dein Blut von dir abgewaschen und dich mit Öl gesalbt." },
      { number: 10, text: "Ich kleidete dich in bunte Gewänder, zog dir Schuhe aus Tahasch-Leder an und hüllte dich in Leinen und kostbare Gewänder.", footnotes: ["Tahasch-Leder: vgl. die Anmerkung zu Ex 25,5."] },
      { number: 11, text: "Ich legte dir prächtigen Schmuck an, legte dir Spangen an die Arme und eine Kette um den Hals." },
      { number: 12, text: "Deine Nase schmückte ich mit einem Reif, Ohrringe hängte ich dir an die Ohren und setzte dir eine herrliche Krone auf." },
      { number: 13, text: "Mit Gold und Silber konntest du dich schmücken, in Byssus, Seide und bunte Gewebe dich kleiden. Feinmehl, Honig und Öl war deine Nahrung. So wurdest du strahlend schön und wurdest sogar Königin." },
      { number: 14, text: "Der Ruf deiner Schönheit drang zu allen Völkern; denn mein Schmuck, den ich dir anlegte, hatte deine Schönheit vollkommen gemacht - Spruch Gottes, des Herrn." },
      { number: 15, text: "Doch dann hast du dich auf deine Schönheit verlassen, du hast deinen Ruhm missbraucht und dich zur Dirne gemacht. Jedem, der vorbeiging, hast du dich angeboten, jedem bist du zu Willen gewesen.", footnotes: ["bist du zu Willen gewesen: so nach G."] },
      { number: 16, text: "Du hast deine bunten Gewänder genommen und dir an den Kulthöhen ein Lager bereitet und darauf Unzucht getrieben.", footnotes: ["Der Schluss des Verses ist in H unverständlich; er ist deshalb nicht übersetzt."] },
      { number: 17, text: "Deinen prächtigen Schmuck aus meinem Gold und Silber, den ich dir geschenkt hatte, hast du genommen und hast dir daraus männliche Figuren gemacht, um mit ihnen Unzucht zu treiben.", footnotes: ["Hos 8,4"] },
      { number: 18, text: "Deine bunten Gewänder hast du genommen und sie damit bekleidet. Mein Öl und meinen Weihrauch hast du vor sie hingestellt." },
      { number: 19, text: "Das Brot, das ich dir gab - mit Feinmehl, Öl und Honig nährte ich dich -, das hast du ihnen als beruhigenden Duft dargebracht [ja, so war es] - Spruch Gottes, des Herrn." },
      { number: 20, text: "Du hast deine Söhne und Töchter, die du mir geboren hast, genommen und ihnen als Schlachtopfer zum Essen vorgesetzt. War dir dein unzüchtiges Treiben noch nicht genug?", footnotes: ["20,26; 23,37; 2 Kön 16,3; Jer 7,31; Dtn 12,31"] },
      { number: 21, text: "Musstest du auch noch meine Söhne schlachten, um sie ihnen darzubringen und für sie durch das Feuer gehen zu lassen?" },
      { number: 22, text: "Bei all deinen Gräueltaten und deiner Unzucht hast du die Zeit deiner Jugend vergessen, in der du noch nackt und bloß und zappelnd in deinem Blut lagst." },
      { number: 23, text: "Nach all diesen schändlichen Taten - wehe, weh dir! Spruch Gottes, des Herrn -" },
      { number: 24, text: "hast du dir auf jedem freien Platz ein Bett und eine Kulthöhe errichtet." },
      { number: 25, text: "An jeder Straßenecke hast du deine Kulthöhen errichtet, du hast deine Schönheit schändlich missbraucht, hast dich jedem angeboten, der vorbeiging, und hast unaufhörlich Unzucht getrieben." },
      { number: 26, text: "Du hast dich den Ägyptern, deinen Nachbarn mit dem großen Glied, hingegeben und mit ihnen unaufhörlich Unzucht getrieben, um mich zu erzürnen." },
      { number: 27, text: "Da streckte ich meine Hand aus und nahm dir weg, was dir zustand. Ich lieferte dich deinen rachgierigen Feindinnen aus, den Philisterinnen; sogar sie schämten sich wegen deines schändlichen Treibens." },
      { number: 28, text: "Dann hast du dich mit den Assyrern eingelassen, weil du noch nicht genug hattest. Du hast dich mit ihnen eingelassen, doch auch dann hattest du noch nicht genug.", footnotes: ["23,12"] },
      { number: 29, text: "Immer mehr Unzucht hast du getrieben und bist sogar den chaldäischen Krämern nachgelaufen; doch auch dann hattest du noch nicht genug.", footnotes: ["den chaldäischen Krämern nachgelaufen, wörtlich: nach dem Krämerland Chaldäa hingelaufen."] },
      { number: 30, text: "Wie fiebert dein Herz - Spruch Gottes, des Herrn -, weil du all das getan hast, du selbstherrliche Dirne." },
      { number: 31, text: "Du hast dir an jeder Straßenecke ein Bett und auf jedem freien Platz eine Kulthöhe errichtet. Du warst keine gewöhnliche Dirne; denn du hast es verschmäht, dich bezahlen zu lassen." },
      { number: 32, text: "[Die Ehebrecherin nimmt sich statt ihres Mannes fremde Männer.]" },
      { number: 33, text: "Jede Dirne bezahlt man; du aber hast all deinen Liebhabern Geschenke gegeben und sie bestochen, damit sie von überall kamen, um mit dir Unzucht zu treiben." },
      { number: 34, text: "Bei deiner Unzucht hast du es gerade umgekehrt gemacht wie andere Frauen: Dir lief niemand nach; du hast selbst bezahlt und dich nicht bezahlen lassen. So hast du es umgekehrt gemacht wie andere." },
      { number: 35, text: "Darum, du Dirne, höre das Wort des Herrn!" },
      { number: 36, text: "So spricht Gott, der Herr: Weil du deinen Körper schamlos entblößt hast bei der Unzucht mit deinen Liebhabern, mit all deinen abscheulichen Götzen, und weil du ihnen das Blut deiner Kinder hingegeben hast," },
      { number: 37, text: "deshalb will ich alle deine Liebhaber zusammenrufen, denen du gefallen hast, alle, die du geliebt hast, und auch alle, die du verachtet hast. Und wenn ich sie von allen Seiten bei dir zusammengerufen habe, dann entblöße ich vor ihren Augen deine Scham, damit sie deine Scham unverhüllt sehen." },
      { number: 38, text: "Ich spreche dir das Urteil nach den Rechtsvorschriften für Ehebrecherinnen und Mörderinnen, voll Zorn zahle ich dir heim mit Blut, Grimm und Eifersucht.", footnotes: ["Lev 20,10"] },
      { number: 39, text: "Ich gebe dich in ihre Hand, damit sie dein Bett zerstören und deine Kulthöhen einreißen, damit sie dir deine Gewänder ausziehen, deinen prächtigen Schmuck wegnehmen und dich nackt und bloß daliegen lassen." },
      { number: 40, text: "Sie berufen eine Volksversammlung gegen dich ein, steinigen dich und hauen dich mit ihren Schwertern in Stücke." },
      { number: 41, text: "Sie werden deine Häuser anzünden und vor den Augen vieler Frauen das Urteil an dir vollstrecken. So mache ich deiner Unzucht ein Ende. Keinem wirst du mehr Dirnenlohn zahlen.", footnotes: ["2 Kön 25,9"] },
      { number: 42, text: "Wenn ich meinen Zorn an dir gestillt habe, wird meine Eifersucht aufhören, gegen dich zu wüten. Ich werde Ruhe haben und mich nicht mehr ärgern." },
      { number: 43, text: "Weil du die Tage deiner Jugend vergessen und mich durch dein Treiben gereizt hast, darum lasse ich dein Verhalten auf dich selbst zurückfallen - Spruch Gottes, des Herrn. Hast du nicht neben all deinen Gräueln auch noch andere Schandtaten verübt?", footnotes: ["9,10"] },
      { number: 44, text: "Wer ein Sprichwort auf dich anwenden will, der sagt: Wie die Mutter, so die Tochter!" },
      { number: 45, text: "Du bist die Tochter deiner Mutter, die vor ihrem Mann und ihren Söhnen Abscheu empfand. Du bist die Schwester deiner Schwestern, die vor ihren Männern und Söhnen Abscheu empfanden. Eure Mutter war eine Hetiterin und euer Vater ein Amoriter.", footnotes: ["16,3"] },
      { number: 46, text: "Deine ältere Schwester ist Samaria mit ihren Töchtern, die links von dir wohnt; deine jüngere Schwester ist Sodom mit ihren Töchtern, die rechts von dir wohnt." },
      { number: 47, text: "Du bist nicht nur ihrem Beispiel gefolgt und hast nicht nur die gleichen Gräueltaten begangen, sondern in kurzer Zeit hast du es in allem noch schlimmer getrieben als sie.", footnotes: ["Sinn des Satzes in H nicht eindeutig."] },
      { number: 48, text: "So wahr ich lebe - Spruch Gottes, des Herrn: Deine Schwester Sodom und ihre Töchter haben es nicht so getrieben wie du und deine Töchter." },
      { number: 49, text: "Die Schuld deiner Schwester Sodom war, dass sie und ihre Töchter hochmütig waren, dass sie in Überfluss zu essen hatten und in sorgloser Ruhe dahinlebten, ohne den Elenden und Armen zu helfen.", footnotes: ["ohne . . . zu helfen, wörtlich: die Hand des Armen und Elenden machte sie nicht stark."] },
      { number: 50, text: "Sie wurden hochmütig und begingen Gräueltaten vor meinen Augen. Darum habe ich sie verstoßen, wie du selbst gesehen hast." },
      { number: 51, text: "Samaria hat nicht die Hälfte deiner Sünden begangen. Du hast mehr Gräueltaten verübt als sie beide zusammen und du lässt deine Schwestern gerecht erscheinen, sieht man auf all die Gräueltaten, die du begangen hast.", footnotes: ["Jer 3,11"] },
      { number: 52, text: "Darum trag auch du deine Schande, die du deine Schwestern durch deine Sünden entlastet und abscheulicher als sie gehandelt hast; deshalb erscheinen sie gerechter als du. Darum schäme auch du dich und trag deine Schande, weil du deine Schwestern gerecht erscheinen lässt." },
      { number: 53, text: "Aber ich werde ihr Schicksal wenden, das Schicksal Sodoms und ihrer Töchter, das Schicksal Samarias und ihrer Töchter, und ich werde auch dein Schicksal wenden zusammen mit ihrem Schicksal;", footnotes: ["und ich werde auch dein Schicksal wenden: Text korr.; vgl. die alten Übersetzungen. - zusammen mit ihrem Schicksal, wörtlich: in ihrer Mitte."] },
      { number: 54, text: "denn du sollst deine Schande tragen und du sollst dich schämen über all das, was du getan und wodurch du sie in Sicherheit gewiegt hast." },
      { number: 55, text: "Deine Schwestern werden wieder sein wie früher, Sodom und ihre Töchter, Samaria und ihre Töchter werden wieder sein wie früher. Auch deine Töchter werden wieder sein wie früher." },
      { number: 56, text: "Hast du nicht über deine Schwester Sodom gelästert, als du hochmütig warst," },
      { number: 57, text: "bevor deine Schlechtigkeit offenbar wurde? So lästern jetzt ringsum über dich all deine Nachbarinnen, die Edomiterinnen und die Philisterinnen, die dich verachten.", footnotes: ["So lästern jetzt: Text korr.; vgl. G. - Edomiterinnen: Text korr. nach mehreren H-Handschriften und S; H: Aramäerinnen."] },
      { number: 58, text: "Die Folgen deines schamlosen Treibens und deiner Gräueltaten hast du zu tragen - Spruch des Herrn." },
      { number: 59, text: "Denn so spricht Gott, der Herr: Ich habe mit dir gemacht, was du gemacht hast; du hast den Eid missachtet und den Bund gebrochen." },
      { number: 60, text: "Aber ich will meines Bundes gedenken, den ich mit dir in deiner Jugend geschlossen habe, und will einen ewigen Bund mit dir eingehen.", footnotes: ["36,22; 37,26; Lev 26,45; Jer 31,31-34; Hos 2,17"] },
      { number: 61, text: "Du sollst dich an dein Verhalten erinnern und dich schämen, wenn ich deine älteren und jüngeren Schwestern nehme und sie dir zu Töchtern gebe, aber nicht deshalb, weil du den Bund gehalten hättest.", footnotes: ["wenn ich . . . nehme: Text korr.; vgl. G; H: wenn du . . . nimmst."] },
      { number: 62, text: "Ich selbst gehe einen Bund mit dir ein, damit du erkennst, dass ich der Herr bin." },
      { number: 63, text: "Dann sollst du dich erinnern, sollst dich schämen und vor Scham nicht mehr wagen, den Mund zu öffnen, weil ich dir alles vergebe, was du getan hast - Spruch Gottes, des Herrn.", footnotes: ["36,31"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das Lied von der Untreue des Königs: 17,1-24" },
      { number: 2, text: "Menschensohn, trag dem Haus Israel ein Rätsel vor, erzähl ihm ein Gleichnis!" },
      { number: 3, text: "Sag: So spricht Gott, der Herr: Ein mächtiger Adler mit gewaltigen Flügeln, / mit weiten Schwingen, mit dichtem, buntem Gefieder kam zum Libanon / und nahm den Wipfel der Zeder weg.", footnotes: ["3f: In der bildhaften Rede bezeichnet der Adler den Großkönig Nebukadnezzar, die Zeder das davidische Königshaus, der Libanon Juda und Jerusalem. Die «Stadt der Händler» ist Babylon, der «oberste Zweig» der 597 v. Chr. deportierte König Jojachin."] },
      { number: 4, text: "Den obersten Zweig riss er ab. / Ins Land der Krämer brachte er ihn, / in die Stadt der Händler legte er ihn." },
      { number: 5, text: "Dann nahm er vom Samen des Landes / und streute ihn auf ein Saatfeld. Er setzte ihn an reichlich strömendes Wasser, / als Uferpflanze pflanzte er ihn" },
      { number: 6, text: "und er wuchs heran / und wurde zum üppigen Weinstock / von niedrigem Wuchs. Er sollte seine Ranken dem Adler zuwenden, / seine Wurzeln sollten in die Tiefe wachsen. Und er wurde zum Weinstock, / bildete Triebe, entfaltete Zweige.", footnotes: ["6f: Der «Weinstock» ist König Zidkija, der «andere Adler» der Pharao Psammetich II."] },
      { number: 7, text: "Doch es kam noch ein anderer mächtiger Adler / mit gewaltigen Flügeln und dichtem Gefieder. Da drehte jener Weinstock seine Wurzeln ihm zu, / trieb ihm seine Ranken entgegen: Der Adler sollte ihn tränken, / mehr als das Beet, in das der Weinstock gepflanzt war.", footnotes: ["ein anderer: Text korr. nach G; H: einer. - das Beet: Einzahl nach G und anderen Textzeugen; ebenso V. 10."] },
      { number: 8, text: "Er war doch auf guten Boden gepflanzt, / an reichlich fließendem Wasser, um Zweige zu treiben und Früchte zu tragen / und ein herrlicher Weinstock zu werden." },
      { number: 9, text: "Sag: So spricht Gott, der Herr: / Wird das gelingen? Wird der Adler nicht seine Wurzeln ausreißen / und seine Früchte vernichten, / sodass all seine grünenden Triebe verdorren? Man braucht keinen starken Arm und nicht viele Menschen, / um ihn von den Wurzeln zu reißen." },
      { number: 10, text: "Wohl ist er gepflanzt, / doch wird er gedeihen? Wird er nicht völlig verdorren, / wenn der Ostwind ihn trifft? / Auf dem Beet, wo er wuchs, wird er verdorren." },
      { number: 11, text: "Und das Wort des Herrn erging an mich:" },
      { number: 12, text: "Sag doch dem widerspenstigen Volk: / Merkt ihr denn nicht, was all das bedeutet? Sag ihnen: / Seht, nach Jerusalem kam der König von Babel; er nahm ihren König und ihre Beamten gefangen / und führte sie mit sich nach Babel." },
      { number: 13, text: "Und er nahm einen Mann aus königlichem Stamm / und schloss mit ihm einen Bund und er ließ ihn schwören. / Doch die Mächtigen des Landes nahm er gefangen," },
      { number: 14, text: "damit das Königreich klein blieb / und keinen Aufstand begann, damit es den Bund auch hielt, / sodass er Bestand haben konnte." },
      { number: 15, text: "Doch jener erhob sich gegen den König von Babel / und schickte seine Boten ins Land der Ägypter, / damit man ihm Pferde und viele Krieger entsandte. Aber wird es gelingen? / Kommt er davon, wenn er das unternimmt? / Wenn er den Bund bricht, kommt er dann noch davon?" },
      { number: 16, text: "So wahr ich lebe - Spruch Gottes, des Herrn: In der Residenz des Königs, der ihn zum König gemacht hat, dessen Eid er missachtet und dessen Bund er gebrochen hat, in Babel wird er sterben." },
      { number: 17, text: "Denn der Pharao wird ihn nicht mit einer starken Streitmacht und mit einem großen Heer im Kampf unterstützen, sondern man wird einen Damm aufschütten, einen Belagerungswall bauen und viele Menschen umbringen." },
      { number: 18, text: "Er hat den Eid missachtet und den Bund gebrochen. Obwohl er sich mit Handschlag verpflichtet hatte, hat er all das getan. Er kommt nicht davon." },
      { number: 19, text: "Darum - so spricht Gott, der Herr: So wahr ich lebe - / meinen Eid, den er missachtet, und meinen Bund, den er gebrochen hat, / ich lasse sie auf ihn selbst zurückfallen." },
      { number: 20, text: "Ich werfe mein Netz über ihn, er gerät in mein Garn. / Nach Babel führe ich ihn und gehe dort mit ihm ins Gericht, / denn er hat mir die Treue gebrochen.", footnotes: ["32,3"] },
      { number: 21, text: "Die tapfersten Krieger in all seinen Truppen / fallen unter dem Schwert. Die Übriggebliebenen aber / werden in alle Winde zerstreut. Dann werdet ihr erkennen, / dass ich, der Herr, gesprochen habe.", footnotes: ["Die tapfersten Krieger: Text korr. nach S und der aramäischen Übersetzung; vgl. G; H: Seine Flüchtlinge."] },
      { number: 22, text: "So spricht Gott, der Herr: Ich selbst nehme ein Stück / vom hohen Wipfel der Zeder / und pflanze es ein. Einen zarten Zweig aus den obersten Ästen breche ich ab, / ich pflanze ihn auf einen hoch aufragenden Berg.", footnotes: ["Jes 11,1; Ez 20,40; Dan 4,9; Mt 13,32", "22-24: Das Heilswort spricht unter Verwendung der vorausgehenden Bilder von der Wiederherstellung durch Gott."] },
      { number: 23, text: "Auf die Höhe von Israels Bergland pflanze ich ihn. / Dort treibt er dann Zweige, / er trägt Früchte und wird zur prächtigen Zeder. Allerlei Vögel wohnen darin; / alles, was Flügel hat, wohnt im Schatten ihrer Zweige." },
      { number: 24, text: "Dann werden alle Bäume auf den Feldern erkennen, / dass ich der Herr bin. Ich mache den hohen Baum niedrig, / den niedrigen mache ich hoch. Ich lasse den grünenden Baum verdorren, / den verdorrten erblühen. / Ich, der Herr, habe gesprochen und ich führe es aus." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Schuld und Gerechtigkeit: 18,1-32" },
      { number: 2, text: "Wie kommt ihr dazu, im Land Israel das Sprichwort zu gebrauchen: Die Väter essen saure Trauben und den Söhnen werden die Zähne stumpf?", footnotes: ["Jer 31,29", "Das Sprichwort umschreibt die Vergeltungslehre, nach der die Söhne für die Schuld der Väter büßen müssen."] },
      { number: 3, text: "So wahr ich lebe - Spruch Gottes, des Herrn -, keiner von euch in Israel soll mehr dieses Sprichwort gebrauchen." },
      { number: 4, text: "Alle Menschenleben sind mein Eigentum, das Leben des Vaters ebenso wie das Leben des Sohnes, sie gehören mir. Nur wer sündigt, soll sterben." },
      { number: 5, text: "Ist jemand gerecht, so handelt er nach Recht und Gerechtigkeit." },
      { number: 6, text: "Er hält auf den Bergen keine Opfermahlzeiten ab. Er blickt nicht zu den Götzen des Hauses Israel auf. Er schändet nicht die Frau seines Nächsten. Einer Frau tritt er nicht nahe während ihrer Blutung.", footnotes: ["22,9; Lev 18,19f"] },
      { number: 7, text: "Er unterdrückt niemand. Er gibt dem Schuldner das Pfand zurück. Er begeht keinen Raub. Dem Hungrigen gibt er von seinem Brot und den Nackten bekleidet er.", footnotes: ["Dtn 24,10-13; Jes 58,7"] },
      { number: 8, text: "Er leiht nicht gegen Zins und treibt keinen Wucher. Er hält seine Hand vom Unrecht fern. Zwischen Streitenden fällt er ein gerechtes Urteil." },
      { number: 9, text: "Er lebt nach meinen Gesetzen, er achtet auf meine Rechtsvorschriften und befolgt sie treu. Er ist gerecht und deshalb wird er am Leben bleiben - Spruch Gottes, des Herrn." },
      { number: 10, text: "Angenommen aber, er zeugt einen Sohn, der gewalttätig wird, der Blut vergießt oder eine andere von diesen Sünden begeht,", footnotes: ["eine andere von diesen Sünden: Text korr.; vgl. Vg und S."] },
      { number: 11, text: "während er (der Vater), all das nicht getan hat, (einen Sohn,) der auf den Bergen Opfermahlzeiten abhält, der die Frau seines Nächsten schändet," },
      { number: 12, text: "der die Elenden und Armen unterdrückt, andere beraubt und dem Schuldner das Pfand nicht zurückgibt, der zu den Götzen aufblickt und Gräueltaten verübt," },
      { number: 13, text: "der gegen Zins leiht und Wucher treibt - soll der dann am Leben bleiben? Er soll nicht am Leben bleiben. Er hat alle diese Gräueltaten verübt, darum muss er sterben. Er ist selbst schuld an seinem Tod.", footnotes: ["33,4", "Er ist selbst schuld an seinem Tod, wörtlich: sein Blut wird auf ihm sein."] },
      { number: 14, text: "Nun hat auch dieser Sohn wieder einen Sohn gezeugt und der Sohn sieht alle die Sünden, die sein Vater begeht. Er sieht sie, begeht sie aber nicht." },
      { number: 15, text: "Er hält auf den Bergen keine Opfermahlzeiten ab. Er blickt nicht zu den Götzen des Hauses Israel auf. Er schändet nicht die Frau seines Nächsten." },
      { number: 16, text: "Er unterdrückt niemand. Er fordert kein Pfand und begeht keinen Raub. Dem Hungrigen gibt er von seinem Brot und den Nackten bekleidet er." },
      { number: 17, text: "Er hält seine Hand vom Unrecht fern. Er nimmt keinen Zins und treibt keinen Wucher. Er befolgt meine Rechtsvorschriften und lebt nach meinen Gesetzen. Dieser Sohn wird nicht wegen der Schuld seines Vaters sterben; er wird bestimmt am Leben bleiben.", footnotes: ["vom Unrecht: Text korr. nach G; vgl. V. 8; H: vom Elenden."] },
      { number: 18, text: "Sein Vater aber musste wegen seiner Schuld sterben; denn er hat andere erpresst und beraubt und in seiner Familie getan, was nicht recht ist.", footnotes: ["andere . . . beraubt: Text korr., vgl. VV. 7.12.16; H unklar."] },
      { number: 19, text: "Ihr aber fragt: Warum trägt der Sohn nicht mit an der Schuld seines Vaters? Weil der Sohn nach Recht und Gerechtigkeit gehandelt hat. Er hat auf alle meine Gesetze geachtet und sie befolgt. Er wird bestimmt am Leben bleiben.", footnotes: ["Ex 20,5"] },
      { number: 20, text: "Nur wer sündigt, soll sterben. Ein Sohn soll nicht die Schuld seines Vaters tragen und ein Vater nicht die Schuld seines Sohnes. Die Gerechtigkeit kommt nur dem Gerechten zugute und die Schuld lastet nur auf dem Schuldigen.", footnotes: ["18,4; Dtn 24,16; 2 Kön 14,6"] },
      { number: 21, text: "Wenn der Schuldige sich von allen Sünden, die er getan hat, abwendet, auf alle meine Gesetze achtet und nach Recht und Gerechtigkeit handelt, dann wird er bestimmt am Leben bleiben und nicht sterben." },
      { number: 22, text: "Keines der Vergehen, deren er sich schuldig gemacht hat, wird ihm angerechnet. Wegen seiner Gerechtigkeit wird er am Leben bleiben.", footnotes: ["33,16"] },
      { number: 23, text: "Habe ich etwa Gefallen am Tod des Schuldigen - Spruch Gottes, des Herrn - und nicht vielmehr daran, dass er seine bösen Wege verlässt und so am Leben bleibt?", footnotes: ["18,13; 33,11; Hos 11,9; 1 Tim 2,4"] },
      { number: 24, text: "Wenn jedoch ein Gerechter sein rechtschaffenes Leben aufgibt, wenn er Unrecht tut und all die Gräueltaten begeht, die auch der Böse verübt, sollte er dann etwa am Leben bleiben? Keine seiner gerechten Taten wird ihm angerechnet. Wegen seiner Treulosigkeit und wegen der Sünde, die er begangen hat, ihretwegen muss er sterben.", footnotes: ["3,20"] },
      { number: 25, text: "Ihr aber sagt: Das Verhalten des Herrn ist nicht richtig. Hört doch, ihr vom Haus Israel: Mein Verhalten soll nicht richtig sein? Nein, euer Verhalten ist nicht richtig.", footnotes: ["33,17", "Der letzte Satz ist in H als Frage formuliert, ebenso V. 29."] },
      { number: 26, text: "Wenn der Gerechte sein rechtschaffenes Leben aufgibt und Unrecht tut, muss er dafür sterben. Wegen des Unrechts, das er getan hat, wird er sterben." },
      { number: 27, text: "Wenn sich der Schuldige von dem Unrecht abwendet, das er begangen hat, und nach Recht und Gerechtigkeit handelt, wird er sein Leben bewahren." },
      { number: 28, text: "Wenn er alle Vergehen, deren er sich schuldig gemacht hat, einsieht und umkehrt, wird er bestimmt am Leben bleiben. Er wird nicht sterben." },
      { number: 29, text: "Das Haus Israel aber sagt: Das Verhalten des Herrn ist nicht richtig. Mein Verhalten soll nicht richtig sein, ihr vom Haus Israel? Nein, euer Verhalten ist nicht richtig." },
      { number: 30, text: "Darum will ich euch richten, jeden nach seinem Verhalten, ihr vom Haus Israel - Spruch Gottes, des Herrn. Kehrt um, wendet euch ab von all euren Vergehen! Sie sollen für euch nicht länger der Anlass sein, in Sünde zu fallen.", footnotes: ["33,20; 7,19; 44,12"] },
      { number: 31, text: "Werft alle Vergehen von euch, die ihr verübt habt! Schafft euch ein neues Herz und einen neuen Geist! Warum wollt ihr sterben, ihr vom Haus Israel?", footnotes: ["11,19; 36,26"] },
      { number: 32, text: "Ich habe doch kein Gefallen am Tod dessen, der sterben muss - Spruch Gottes, des Herrn. Kehrt um, damit ihr am Leben bleibt.", footnotes: ["33,11"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Du aber, stimm die Totenklage an über den Fürsten von Israel", heading: "Die Totenklage über den Fürsten von Israel: 19,1-14" },
      { number: 2, text: "und sag: Was war doch deine Mutter für eine Löwin / unter den Löwen! Bei jungen Löwen hatte sie ihr Lager / und zog ihren Nachwuchs auf.", footnotes: ["Die Totenklage in Form einer bildhaften Rede bezeichnet wohl mit der «Löwenmutter» das judäische Königshaus, mit dem «Löwenjungen» in VV. 3f Joahas, in VV. 5-9 Jojachin. - Auch der Weinstock (VV. 10-14) stellt das davidische Königshaus dar."] },
      { number: 3, text: "Eins von ihren Jungen zog sie groß / und es wurde ein starker Löwe. Er lernte, zu reißen und zu rauben, / er fraß Menschen." },
      { number: 4, text: "Da bot man Völker gegen ihn auf. / In ihrer Grube fingen sie ihn / und schleppten ihn an Haken ins Land Ägypten.", footnotes: ["2 Kön 23,31-34", "bot man . . . auf: Text korr.; H: hörten sie."] },
      { number: 5, text: "Als sie sah, dass ihre Hoffnung vereitelt, / dass sie zunichte war, nahm sie von ihren Jungen ein anderes / und machte es zum starken Löwen.", footnotes: ["ein anderes: vgl. die Anmerkung zu 17,7."] },
      { number: 6, text: "Er tummelte sich unter den Löwen / und wurde ein starker Löwe. Er lernte zu reißen und zu rauben, / er fraß Menschen." },
      { number: 7, text: "Er zerbrach ihre Burgen / und verheerte ihre Städte; das Land, und was in ihm lebte, / entsetzte sich bei seinem lauten Gebrüll.", footnotes: ["Er zerbrach ihre Burgen: Text korr., vgl. G."] },
      { number: 8, text: "Da setzte man Völker gegen ihn ein / aus den Ländern ringsum. Sie warfen ihr Netz über ihn; / in ihrer Grube fingen sie ihn." },
      { number: 9, text: "Sie zerrten ihn an Haken in einen Käfig / und brachten ihn zum König von Babel, [sie brachten ihn in Gewahrsam,] / damit man seine Stimme nicht mehr hörte / auf Israels Bergen.", footnotes: ["2 Kön 24,15"] },
      { number: 10, text: "Deine Mutter war wie ein Weinstock im Garten, / der am Wasser gepflanzt ist. Voll von Früchten und Ranken war er / wegen des Reichtums an Wasser.", footnotes: ["17,6", "im Garten: Text korr.; H: in deinem Blut."] },
      { number: 11, text: "Er hatte kräftige Zweige, / für Zepter von Herrschern geeignet; sein Wuchs war hoch, / er ragte bis in die Wolken. Weithin war er sichtbar wegen seiner Höhe / und wegen seines dichten Laubes." },
      { number: 12, text: "Doch im Zorn riss man ihn aus / und warf ihn auf die Erde. Der Ostwind dörrte ihn aus, / seine Früchte riss man ab. Sein kräftiger Stamm verdorrte. / Feuer verzehrte ihn.", footnotes: ["Joh 15,6"] },
      { number: 13, text: "Nun verpflanzte man ihn in die Wüste, / in trockenes, dürstendes Land." },
      { number: 14, text: "Und Feuer ging aus von den Zweigen am Stamm / und fraß seine Früchte. Kein kräftiger Zweig war mehr an ihm, / kein Zepter für Herrscher. - Eine Totenklage ist dieses Lied; / zur Totenklage ist es geworden." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Im fünften Monat des siebten Jahres kamen am zehnten Tag des Monats einige von den Ältesten Israels, um den Herrn zu befragen. Sie setzten sich vor mir nieder.", heading: "Die zahlreichen Treulosigkeiten Israels: 20,1-44", footnotes: ["8,1; 14,1.3"] },
      { number: 2, text: "Da erging das Wort des Herrn an mich:" },
      { number: 3, text: "Menschensohn, rede mit den Ältesten Israels und sag zu ihnen: So spricht Gott, der Herr: Seid ihr gekommen, um mich zu befragen? So wahr ich lebe, ich lasse mich von euch nicht befragen - Spruch Gottes, des Herrn." },
      { number: 4, text: "Willst du nicht über sie Gericht halten, Menschensohn, willst nicht du Gericht halten? Mach ihnen die Gräueltaten ihrer Väter bewusst", footnotes: ["22,2; 23,36; 16,2"] },
      { number: 5, text: "und sag zu ihnen: So spricht Gott, der Herr: An dem Tag, als ich Israel erwählt habe, erhob ich meine Hand, (um) den Nachkommen des Hauses Jakob (einen Eid zu leisten). Ich offenbarte mich ihnen in Ägypten. Ich erhob meine Hand (zum Schwur) und sprach: Ich bin der Herr, euer Gott.", footnotes: ["Dtn 7,6"] },
      { number: 6, text: "An jenem Tag erhob ich meine Hand (zum Schwur), dass ich sie aus Ägypten herausführe in ein Land, das ich für sie erkundete, in dem Milch und Honig fließen, ein Schmuckstück unter allen Ländern.", footnotes: ["Ex 3,8"] },
      { number: 7, text: "Und ich sagte zu ihnen: Ihr alle, werft die Götzen weg, an denen eure Augen hängen. Macht euch nicht unrein durch die Gräueltaten Ägyptens! Ich bin der Herr, euer Gott.", footnotes: ["Jos 24,14.23"] },
      { number: 8, text: "Sie aber widersetzten sich mir und wollten nicht auf mich hören. Keiner warf die Götzen weg, an denen seine Augen hingen, und sie sagten sich nicht los von den Götzen Ägyptens. Da sagte ich: Ich will meinen Zorn über sie ausgießen und meinen Grimm an ihnen auslassen, mitten in Ägypten." },
      { number: 9, text: "Aber dann handelte ich um meines Namens willen anders; ich wollte ihn nicht entweihen vor den Augen der Völker, in deren Mitte sie lebten und vor deren Augen ich mich ihnen offenbarte, als ich sie aus Ägypten herausführte.", footnotes: ["20,14; 36,21f"] },
      { number: 10, text: "Ich führte sie aus Ägypten heraus, brachte sie in die Wüste" },
      { number: 11, text: "und gab ihnen meine Gesetze. Ich gab ihnen meine Rechtsvorschriften bekannt, die der Mensch befolgen muss, damit er durch sie am Leben bleibt.", footnotes: ["Lev 18,5"] },
      { number: 12, text: "Auch meine Sabbat-Tage gab ich ihnen zum Zeichen (des Bundes) zwischen mir und ihnen. Daran sollte man erkennen, dass ich, der Herr, Israel heilige.", footnotes: ["Ex 31,13-17"] },
      { number: 13, text: "Aber das Haus Israel widersetzte sich mir in der Wüste. Sie lebten nicht nach meinen Gesetzen und wiesen meine Rechtsvorschriften zurück, die der Mensch befolgen muss, damit er durch sie am Leben bleibt. Auch meine Sabbat-Tage entweihten sie völlig. Da sagte ich: Ich will in der Wüste über sie meinen Zorn ausgießen und sie vernichten.", footnotes: ["Ex 14,11"] },
      { number: 14, text: "Aber ich handelte anders um meines Namens willen; ich wollte ihn nicht entweihen vor den Völkern, vor deren Augen ich sie (aus Ägypten) herausgeführt hatte.", footnotes: ["20,9; Ex 32,11f"] },
      { number: 15, text: "Doch in der Wüste erhob ich meine Hand (zum Schwur), dass ich sie nicht in das Land bringe, das ich für sie bestimmt hatte, in das Land, wo Milch und Honig fließen, das Schmuckstück unter allen Ländern.", footnotes: ["Num 14,28-30; Ps 95,11", "für sie: ergänzt nach den alten Übersetzungen und H-Handschriften."] },
      { number: 16, text: "Denn sie wiesen meine Rechtsvorschriften zurück, sie lebten nicht nach meinen Gesetzen und entweihten meine Sabbat-Tage, weil ihr Herz hinter den Götzen her war." },
      { number: 17, text: "Aber mein Auge zeigte Mitleid mit ihnen: Ich rottete sie nicht völlig aus in der Wüste." },
      { number: 18, text: "Zu ihren Söhnen sagte ich in der Wüste: Lebt nicht nach den Gesetzen eurer Väter, achtet nicht auf ihre Rechtsvorschriften und macht euch nicht unrein an ihren Götzen!" },
      { number: 19, text: "Ich bin der Herr, euer Gott. Lebt nach meinen Gesetzen, achtet auf meine Rechtsvorschriften und befolgt sie!" },
      { number: 20, text: "Haltet meine Sabbat-Tage heilig; sie sollen das Zeichen (des Bundes) zwischen mir und euch sein. Daran wird man erkennen, dass ich, der Herr, euer Gott bin." },
      { number: 21, text: "Aber die Söhne widersetzten sich mir. Sie lebten nicht nach meinen Gesetzen und sie beachteten und befolgten meine Rechtsvorschriften nicht, die der Mensch befolgen muss, damit er durch sie am Leben bleibt. Meine Sabbat-Tage entweihten sie. Da sagte ich: Ich werde in der Wüste meinen Zorn über sie ausgießen und meinen Grimm an ihnen auslassen." },
      { number: 22, text: "Aber ich zog meine Hand zurück und handelte anders, um meines Namens willen; ich wollte ihn nicht vor all den Völkern entweihen, vor deren Augen ich sie (aus Ägypten) herausgeführt hatte.", footnotes: ["20,14"] },
      { number: 23, text: "Doch ich erhob in der Wüste meine Hand (zum Schwur), dass ich sie unter die Völker zerstreuen und in alle Länder vertreiben werde," },
      { number: 24, text: "weil sie meine Rechtsvorschriften nicht befolgten, meine Gesetze ablehnten und meine Sabbat-Tage entweihten und weil ihre Augen hinter den Götzen ihrer Väter her waren." },
      { number: 25, text: "Auch gab ich ihnen Gesetze, die nicht gut waren, und Rechtsvorschriften, die es ihnen unmöglich machten, am Leben zu bleiben.", footnotes: ["Nach V. 26 denkt Ezechiel hier an das Gesetz über die Erstgeburtsopfer. In Notzeiten brachte man die erstgeborenen Kinder als Opfer dar, obwohl nach Ex 34,19f die menschliche Erstgeburt durch ein Tier auszulösen ist."] },
      { number: 26, text: "Ich machte sie unrein durch ihre Opfergaben; sie ließen nämlich alle Erstgeborenen durch das Feuer gehen. Ich wollte ihnen Entsetzen einjagen; denn sie sollten erkennen, dass ich der Herr bin.", footnotes: ["Lev 18,21"] },
      { number: 27, text: "Darum sprich zum Haus Israel, Menschensohn, und sag zu ihnen: So spricht Gott, der Herr: Eure Väter haben mich auch dadurch verhöhnt, dass sie mir untreu wurden." },
      { number: 28, text: "Als ich sie in das Land gebracht hatte - ich hatte ja meine Hand erhoben (und geschworen), es ihnen zu geben -, schlachteten sie, sobald sie irgendeinen hohen Hügel und einen dichtbelaubten Baum sahen, dort ihre Opfer; dort brachten sie ihre ärgerlichen Gaben dar, legten ihre duftenden Spenden nieder, um mich zu beruhigen, und gossen dort ihre Trankopfer aus.", footnotes: ["6,13; Dtn 12,2"] },
      { number: 29, text: "Da sagte ich zu ihnen: Was ist das schon, die Höhe, zu der ihr kommt? - Deshalb wird ein solcher Platz bis zum heutigen Tag Kulthöhe genannt.", footnotes: ["die Höhe, hebräisch bamáh, sonst mit «Kulthöhe» übersetzt. In H Wortspiel mit bamáh, aus dem man auch die Wörter für «was» und «kommen» heraushören konnte."] },
      { number: 30, text: "Darum sag zum Haus Israel: So spricht Gott, der Herr: Wie schon eure Väter, so macht auch ihr euch unrein und lauft in eurer Untreue ihren Götzen nach." },
      { number: 31, text: "Wenn ihr eure Gaben darbringt, eure Söhne durch das Feuer gehen lasst, wenn ihr euch bis zum heutigen Tag immer wieder unrein macht mit all euren Götzen, soll ich mich dann von euch befragen lassen, ihr vom Haus Israel? So wahr ich lebe - Spruch Gottes, des Herrn -, ich lasse mich von euch nicht befragen." },
      { number: 32, text: "Niemals soll geschehen, was euch eingefallen ist, als ihr sagtet: Wir wollen wie die anderen Völker sein, wie die Völkerstämme in anderen Ländern, und wollen Holz und Stein verehren.", footnotes: ["1 Sam 8,5"] },
      { number: 33, text: "So wahr ich lebe - Spruch Gottes, des Herrn: Ich will mit starker Hand und hoch erhobenem Arm über euch als König herrschen und dabei meinen Zorn über euch ausgießen.", footnotes: ["Jer 21,5"] },
      { number: 34, text: "Ich will euch mit starker Hand und hoch erhobenem Arm aus den Völkern herausführen und aus den Ländern, in die ihr zerstreut seid, sammeln und dabei meinen Zorn über euch ausgießen." },
      { number: 35, text: "Ich bringe euch in die Wüste der Völker; dort trete ich euch von Angesicht zu Angesicht als Richter gegenüber.", footnotes: ["Hos 2,16; Num 14,22-38"] },
      { number: 36, text: "Wie ich mit euren Vätern in der Wüste Ägyptens ins Gericht gegangen bin, so will ich auch mit euch ins Gericht gehen - Spruch Gottes, des Herrn." },
      { number: 37, text: "Ich lasse euch unter dem Hirtenstab an mir vorbeiziehen und zähle euch ab.", footnotes: ["Lev 27,32; Jer 33,13", "und zähle euch ab: Text korr.; vgl. G; der Text ist unklar."] },
      { number: 38, text: "Die Abtrünnigen und alle, die sich gegen mich auflehnten, sondere ich von euch ab. Ich führe sie zwar aus dem Land, in dem sie als Fremde leben, heraus, in das Land Israel aber werden sie nicht kommen; dann werdet ihr erkennen, dass ich der Herr bin.", footnotes: ["Der letzte Satz korr. nach den alten Übersetzungen und einigen hebräischen Handschriften."] },
      { number: 39, text: "Ihr aber vom Haus Israel - so spricht Gott, der Herr -, geht doch alle und dient euren Götzen! Doch später werdet ihr auf mich hören und meinen heiligen Namen nicht mehr mit euren Opfergaben und mit euren Götzen entweihen.", footnotes: ["werdet ihr auf mich hören: Text korr.; H: werdet ihr nicht auf mich hören."] },
      { number: 40, text: "Denn auf meinem heiligen Berg, auf dem hohen Berg Israels - Spruch Gottes, des Herrn -, dort im Land wird mir das ganze Haus Israel dienen. Dort will ich sie gnädig annehmen und dort fordere ich eure Abgaben und Erstlingsopfer mit all euren heiligen Gaben." },
      { number: 41, text: "Beim beruhigenden Duft eurer Opfer will ich euch gnädig annehmen. Wenn ich euch aus den Völkern herausführe und aus den Ländern sammle, in die ihr zerstreut seid, werde ich mich vor den Augen der Völker an euch als heilig erweisen." },
      { number: 42, text: "Ihr sollt erkennen, dass ich der Herr bin, wenn ich euch in das Land Israel bringe; denn ich habe meine Hand erhoben (zum Schwur), dieses Land euren Vätern zu geben." },
      { number: 43, text: "Dort werdet ihr euch an euer Verhalten und an all eure Taten erinnern, durch die ihr euch unrein gemacht habt, und es wird euch ekeln vor euch selbst wegen all der bösen Taten, die ihr begangen habt.", footnotes: ["36,31f"] },
      { number: 44, text: "Ihr werdet erkennen, dass ich der Herr bin, wenn ich um meines Namens willen so an euch handle und nicht nach eurem verkehrten Verhalten und nach euren verwerflichen Taten, ihr vom Haus Israel - Spruch Gottes, des Herrn.", footnotes: ["20,14"] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das Gleichnis vom Waldbrand: 21,1-5" },
      { number: 2, text: "Menschensohn, richte dein Gesicht nach Süden, weissage gegen das Südland, tritt als Prophet auf gegen den Wald im Süden" },
      { number: 3, text: "und sag zum Wald des Südlands: Höre das Wort des Herrn: So spricht Gott, der Herr: Ich will Feuer an dich legen, jeden grünen Baum und jeden dürren Baum in dir wird es verzehren. Seine lodernde Flamme wird nicht erlöschen. Alle Gesichter sollen von ihr versengt werden, vom Süden bis zum Norden.", footnotes: ["Jer 21,14", "Das Bild vom Wald im Südland meint Juda und Jerusalem."] },
      { number: 4, text: "Dann wird jeder Sterbliche sehen, dass ich, der Herr, das Feuer entfacht habe. Und es wird nicht erlöschen." },
      { number: 5, text: "Da sagte ich: Ach, Herr und Gott, sie sagen über mich: Er redet ja immer nur in Gleichnissen." },
      { number: 6, text: "Das Wort des Herrn erging an mich:", heading: "Das Lied vom Schwert des Herrn: 21,6-22" },
      { number: 7, text: "Menschensohn, richte dein Gesicht gegen Jerusalem, weissage gegen sein Heiligtum, tritt als Prophet auf gegen das Land Israel" },
      { number: 8, text: "und sag zu ihm: So spricht Gott, der Herr: Nun gehe ich gegen dich vor. Ich ziehe mein Schwert aus der Scheide und rotte bei dir die Gerechten ebenso wie die Schuldigen aus." },
      { number: 9, text: "Weil ich bei dir die Gerechten und die Schuldigen ausrotten will, deshalb wird mein Schwert aus seiner Scheide fahren, gegen jeden Sterblichen vom Süden bis zum Norden." },
      { number: 10, text: "Dann werden alle Sterblichen erkennen, dass ich, der Herr, mein Schwert aus der Scheide gezogen habe. Es wird nicht mehr in die Scheide zurückkehren." },
      { number: 11, text: "Du, Menschensohn, stöhne: Stöhne mit gebrochenen Hüften und in bitterem Schmerz vor ihren Augen." },
      { number: 12, text: "Wenn sie dann zu dir sagen: Warum stöhnst du?, antworte: Weil eine Nachricht kommt, bei der jedes Herz verzagt, alle Hände kraftlos heruntersinken, bei der jeder Atem stockt und an allen Knien das Wasser herabläuft. Seht, es kommt und geschieht - Spruch Gottes, des Herrn.", footnotes: ["7,17"] },
      { number: 13, text: "Das Wort des Herrn erging an mich:" },
      { number: 14, text: "Menschensohn, tritt als Prophet auf und sag: So spricht Gott, der Herr: Sag: Ein Schwert, ein Schwert, / geschärft und poliert." },
      { number: 15, text: "Zum Schlachten, zum Schlachten ist es geschärft; / um wie ein Blitz zu leuchten, ist es poliert. [Oder sollen wir uns freuen? / Der Stock meines Sohnes verachtet jedes andere Holz.]", footnotes: ["15.18: Text des Zusatzes verderbt und unverständlich."] },
      { number: 16, text: "Man gab es zum Polieren, dann packt es die Faust, / ein Schwert, geschärft und poliert / für des Henkers Hand." },
      { number: 17, text: "Schrei und heule, Menschensohn! / Denn es richtet sich gegen mein Volk, / gegen alle Fürsten von Israel. Dem Schwert sind sie verfallen, / sie und mein Volk. Darum schlag auf die Hüfte! /" },
      { number: 18, text: "Denn es wurde erprobt. [Doch was wird, wenn auch der verachtende Stock nicht mehr ist?] - / Spruch Gottes, des Herrn." },
      { number: 19, text: "Du, Menschensohn, sprich als Prophet / und schlag die Hände zusammen! Verdoppelt wird das Schwert, ja verdreifacht. / Ein Schwert zum Morden ist es, zum Morden, / das gewaltige Schwert, das sie durchbohrt." },
      { number: 20, text: "Das Herz soll verzagen und viele sollen straucheln. / An all ihren Toren habe ich dem Schwert zu schlachten befohlen. Ja, zum Blitzen bist du gemacht, / zum Schlachten poliert.", footnotes: ["Text unsicher."] },
      { number: 21, text: "Zeig, dass du scharf bist! / Zucke nach rechts und nach links, / wohin deine Schneide gelenkt wird.", footnotes: ["Zeig, dass du scharf bist: Text korr.; vgl. G."] },
      { number: 22, text: "Auch ich schlage die Hände zusammen; / meinen Zorn will ich stillen. - / Ich, der Herr, habe gesprochen." },
      { number: 23, text: "Das Wort des Herrn erging an mich:", heading: "Eine weitere symbolische Handlung: Das Schwert des Königs von Babel: 21,23-37" },
      { number: 24, text: "Du, Menschensohn, mach dir zwei Wege, auf denen das Schwert des Königs von Babel kommt. Sie sollen beide vom gleichen Land ausgehen. Am Anfang der beiden Wege stell einen Wegweiser zu je einer Stadt auf!" },
      { number: 25, text: "Gib die Richtung an, die das Schwert einschlagen kann: entweder Rabbat-Ammon oder Juda mit dem befestigten Jerusalem." },
      { number: 26, text: "Denn der König von Babel steht an der Wegscheide, am Anfang der zwei Wege, und lässt das Orakel entscheiden: Er schüttelt die Pfeile, befragt die Götterbilder und hält Leberschau." },
      { number: 27, text: "In seiner rechten Hand hält er den Entscheid des Orakels: Jerusalem. Jetzt wird er die Sturmböcke aufstellen, das Kampflied anstimmen, das Kriegsgeschrei erheben, die Sturmböcke auf die Tore richten, einen Damm aufschütten und Belagerungstürme bauen.", footnotes: ["das Kampflied anstimmen: Text korr. nach G; H ist unklar."] },
      { number: 28, text: "Doch in den Augen der Leute (von Jerusalem) ist das Orakel bedeutungslos. Für sie galten heilige Eide, darum wird er (der König von Babel) sie an ihre Schuld erinnern und sie packen." },
      { number: 29, text: "Darum - so spricht Gott, der Herr: Ihr habt selbst an eure Schuld erinnert - eure Vergehen liegen offen zutage, eure Sünden sind samt all euren Untaten ans Licht gekommen -, und weil ihr überführt seid, wird man euch mit Gewalt packen." },
      { number: 30, text: "Du entweihter, verbrecherischer Fürst von Israel, für den der Tag gekommen ist, die Zeit der endgültigen Abrechnung!" },
      { number: 31, text: "So spricht Gott, der Herr: Weg mit dem Turban, herunter mit der Krone! Nichts soll bleiben, wie es ist. Das Niedrige wird hoch, das Hohe wird niedrig.", footnotes: ["Jes 40,4"] },
      { number: 32, text: "Zu Trümmern, Trümmern, Trümmern mache ich die Stadt. [Auch dies geschieht nicht, bis der kommt, der auf sie ein Anrecht hat und dem ich sie geben will.]", footnotes: ["Sinn des Zusatzes unklar. Im Blick auf V. 31, dritter Satz, könnte an eine messianische Zukunft gedacht sein (vgl. Gen 49,10)."] },
      { number: 33, text: "Du aber, Menschensohn, sprich als Prophet und sag: So spricht Gott, der Herr, über die Ammoniter und ihr Hohngelächter. Du sollst sagen: Schwert, Schwert, gezückt zum Schlachten, poliert, um ganze Arbeit zu tun, um wie ein Blitz einzuschlagen." },
      { number: 34, text: "Während sie noch für dich leere Visionen haben und dir falsche Orakel verkünden, ist den todgeweihten Verbrechern das Schwert schon an den Hals gelegt; jetzt ist für sie der Tag gekommen, die Zeit der endgültigen Abrechnung." },
      { number: 35, text: "Steck das Schwert in die Scheide! An dem Ort, wo du erschaffen wurdest, im Land deiner Herkunft will ich dich richten." },
      { number: 36, text: "Ich gieße meinen Groll über dich aus, das Feuer meines Zorns will ich gegen dich entfachen. Ich liefere dich grausamen Menschen aus, die ihr mörderisches Handwerk verstehen." },
      { number: 37, text: "Das Feuer soll dich verzehren. Mitten im Land soll dein Blut fließen. An dich wird sich niemand erinnern. Denn ich, der Herr, habe gesprochen." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Anklage gegen Jerusalem: 22,1-16" },
      { number: 2, text: "Du, Menschensohn, willst du das Urteil sprechen, willst du das Urteil sprechen über die Stadt voll Blutschuld, willst du ihr alle ihre Gräueltaten bewusst machen,", footnotes: ["20,4; 23,36"] },
      { number: 3, text: "dann sag zu ihr: So spricht Gott, der Herr: Du Stadt, die in ihrer Mitte Blut vergießt, sodass die Zeit (des Gerichts) über sie kommt, und die sich Götzen macht und dadurch unrein wird:" },
      { number: 4, text: "Durch das Blut, das du vergossen hast, bist du schuldig geworden und durch die Götzen, die du gemacht hast, bist du unrein geworden. Du hast dafür gesorgt, dass die Tage (deines Gerichts) nahe gerückt sind; so bist du ans Ende deiner Jahre gekommen. Deshalb mache ich dich zu einem Zeichen der Schande unter den Völkern und des höhnischen Spottes in allen Ländern.", footnotes: ["5,14"] },
      { number: 5, text: "Nah und Fern verhöhnt dich; denn dein Name ist befleckt und deine Bestürzung ist groß." },
      { number: 6, text: "Jeder der Fürsten Israels strebt mit aller Macht danach, in dir Blut zu vergießen." },
      { number: 7, text: "In dir verachtet man Vater und Mutter. In deiner Mitte beutet man die Fremden aus. In dir unterdrückt man Waisen und Witwen.", footnotes: ["Ex 22,21f"] },
      { number: 8, text: "Was mir heilig ist, verachtest du. Meine Sabbat-Tage entweihst du." },
      { number: 9, text: "In dir gibt es Verleumder, die Blut vergießen wollen. Man hält bei dir auf den Bergen Opfermahlzeiten ab. Schandtaten verübt man bei dir:", footnotes: ["18,5-9"] },
      { number: 10, text: "Man schändet die Frau des Vaters, man missbraucht bei dir Frauen während ihrer Blutung.", footnotes: ["Lev 18,20; 20,10"] },
      { number: 11, text: "Der eine treibt abscheuliche Dinge mit der Frau seines Nächsten; ein anderer begeht die Schandtat, seine Schwiegertochter unrein zu machen, wieder ein anderer missbraucht seine Schwester, die Tochter seines Vaters.", footnotes: ["Lev 18,15; 20,12; 18,9; 20,17"] },
      { number: 12, text: "Bei dir lässt man sich bestechen und vergießt dadurch Blut. Du nimmst Zins und treibst Wucher und erpresst deinen Nächsten. Mich aber hast du vergessen - Spruch Gottes, des Herrn.", footnotes: ["Ex 22,24; Lev 25,36; Dtn 23,20"] },
      { number: 13, text: "Ich schlage meine Hände zusammen wegen des Gewinns, den du gemacht hast, und wegen der Morde, die in deiner Mitte geschehen sind." },
      { number: 14, text: "Wird dein Herz standhalten können, werden deine Hände stark bleiben in den Tagen, in denen ich gegen dich vorgehe? Ich, der Herr, habe gesprochen und ich führe es aus." },
      { number: 15, text: "Ich zerstreue deine Einwohner unter die Völker und vertreibe sie in alle Länder, ich mache der Unreinheit bei dir ein Ende.", footnotes: ["Lev 26,33"] },
      { number: 16, text: "Durch deine eigene Schuld bist du entweiht vor den Augen der Völker und du wirst erkennen, dass ich der Herr bin." },
      { number: 17, text: "Das Wort des Herrn erging an mich:", heading: "Der Schmelzofen des göttlichen Zorns: 22,17-22" },
      { number: 18, text: "Menschensohn, das Haus Israel ist für mich zu Schlacke geworden. Sie alle sollten Silber, Kupfer und Zinn, Eisen und Blei im Schmelzofen sein; doch sie sind Schlacke.", footnotes: ["Jes 1,22-25; Jer 6,28", "Text korr."] },
      { number: 19, text: "Darum - so spricht Gott, der Herr: Weil ihr alle zu Schlacke geworden seid, werde ich euch mitten in Jerusalem sammeln." },
      { number: 20, text: "Wie man Silber, Kupfer, Eisen, Blei und Zinn im Schmelzofen zusammentut und darunter das Feuer anzündet, um alles zum Schmelzen zu bringen, so will ich euch in meinem Zorn und Grimm zusammentun, will euch in den Ofen legen und euch zum Schmelzen bringen." },
      { number: 21, text: "Ich tue euch alle zusammen hinein und lasse das Feuer meines Zorns gegen euch auflodern; darin werdet ihr dann schmelzen." },
      { number: 22, text: "Wie Silber im Schmelzofen schmilzt, so werdet ihr darin zum Schmelzen gebracht. Dann erkennt ihr, dass ich, der Herr, meinen Zorn über euch ausschütte." },
      { number: 23, text: "Das Wort des Herrn erging an mich:", heading: "Die Verderbtheit aller Schichten des Volkes: 22,23-31" },
      { number: 24, text: "Menschensohn, sag zu dem Land: Du bist ein Land, das nicht vom Regen begossen, nicht benetzt wird am Tag des Zorns." },
      { number: 25, text: "Mitten in ihm sind seine Fürsten wie brüllende Löwen, die auf Beute aus sind. Sie fressen Menschen, nehmen Schätze und Kostbarkeiten an sich und machen viele Frauen im Land zu Witwen.", footnotes: ["Zef 3,3f", "sind seine Fürsten: Text korr. nach G; H: ist die Verschwörung seiner Propheten."] },
      { number: 26, text: "Seine Priester vergewaltigen mein Gesetz. Sie entweihen, was mir heilig ist. Zwischen heilig und nicht heilig machen sie keinen Unterschied. Sie belehren niemand mehr über unrein und rein und vor meinen Sabbat-Tagen verschließen sie die Augen. So werde ich mitten unter ihnen entweiht." },
      { number: 27, text: "Mitten in ihm sind seine Beamten wie Wölfe, die auf Beute aus sind; sie vergießen Blut und richten Menschenleben zugrunde, um Gewinn zu machen." },
      { number: 28, text: "Seine Propheten aber übertünchen ihnen alles. Sie haben nichtige Visionen, verkünden ihnen falsche Orakel und sagen: So spricht Gott, der Herr - obwohl der Herr gar nicht gesprochen hat.", footnotes: ["13,3-10"] },
      { number: 29, text: "Die Bürger des Landes erpressen und rauben. Sie beuten die Schwachen und Armen aus und erpressen die Fremden gegen jedes Recht." },
      { number: 30, text: "Da suchte ich unter ihnen einen Mann, der eine Mauer baut oder für das Land in die Bresche springt und mir entgegentritt, damit ich es nicht vernichten muss; aber ich fand keinen." },
      { number: 31, text: "Darum schütte ich meinen Groll über sie aus. Ich vernichte sie im Feuer meines Zorns. Ihr Verhalten lasse ich auf sie selbst zurückfallen - Spruch Gottes, des Herrn.", footnotes: ["9,10; 11,21; 16,43"] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das Gleichnis von den schamlosen Schwestern Israel und Juda: 23,1-49" },
      { number: 2, text: "Menschensohn! Es waren einst zwei Frauen, Töchter der gleichen Mutter." },
      { number: 3, text: "Sie trieben Unzucht in Ägypten, schon in ihrer Jugend trieben sie Unzucht; dort griff man nach ihren Brüsten, dort streichelte man ihre jugendliche Brust.", footnotes: ["20,7f"] },
      { number: 4, text: "Die ältere hieß Ohola, ihre Schwester Oholiba. Sie wurden meine Frauen und gebaren Söhne und Töchter. Der Name Ohola meint Samaria, Oholiba Jerusalem.", footnotes: ["Die beiden Namen, abgeleitet von ohel (= Zelt), besagen, dass die beiden Frauen zu den in Zelten lebenden Nomaden gehören. Den Namen Ohola konnte man deuten als «ihr Zelt», Oholiba als «mein (Gottes) Zelt in ihr», Anspielungen auf den unrechtmäßigen Kult Samarias und den rechtmäßigen in Jerusalem."] },
      { number: 5, text: "Ohola wurde mir untreu. Sie hatte Verlangen nach ihren Liebhabern, den kriegerischen Assyrern," },
      { number: 6, text: "den in Purpur gekleideten Statthaltern und Herren; alle waren begehrenswerte junge Männer, Reiter hoch zu Ross." },
      { number: 7, text: "Auf sie richtete sie ihr unzüchtiges Verlangen. Sie alle gehörten zu den Tüchtigsten unter den Assyrern. Mit allen, nach denen sie Verlangen hatte, und mit all ihren Götzen machte sie sich unrein." },
      { number: 8, text: "Die Unzucht, die sie in Ägypten getrieben hatte, gab sie nicht auf; denn schon als sie noch jung war, lagen die Ägypter bei ihr, drückten ihre jugendlichen Brüste und besudelten sie mit ihrer Unzucht." },
      { number: 9, text: "Darum gab ich sie in die Gewalt ihrer Liebhaber, in die Gewalt der Assyrer, nach denen sie Verlangen hatte." },
      { number: 10, text: "Sie entblößten ihre Scham, nahmen ihr die Söhne und Töchter weg und erschlugen sie selbst mit dem Schwert. So wurde sie zum warnenden Beispiel für alle Frauen und man vollstreckte an ihr das Urteil." },
      { number: 11, text: "All das sah ihre Schwester Oholiba; trotzdem trieb sie es in ihrer Gier noch toller als sie und war in ihrer Schamlosigkeit noch schlimmer als sie." },
      { number: 12, text: "Auch sie hatte Verlangen nach den Assyrern, nach den Statthaltern und Herren, den prächtig gekleideten Kriegern, den Reitern hoch zu Ross. Alle waren begehrenswerte junge Männer." },
      { number: 13, text: "Ich sah, dass sie sich unrein machte. Beide trieben es gleich." },
      { number: 14, text: "Doch Oholiba ging noch weiter in ihrem unzüchtigen Treiben: Sie sah mit Mennig gemalte Wandzeichnungen chaldäischer Männer," },
      { number: 15, text: "die um die Hüften einen Lendenschurz und auf dem Kopf einen herabhängenden Kopfbund trugen. Alle sahen aus wie Helden, wie Babylonier [Chaldäa ist ihre Heimat]." },
      { number: 16, text: "Als ihre Augen sie sahen, erwachte in ihr die Gier und sie schickte Boten zu ihnen nach Chaldäa." },
      { number: 17, text: "Die Söhne Babels kamen zu ihrem Liebeslager und machten sie unrein mit ihrer Unzucht. Als sie aber durch sie unrein geworden war, wandte sie sich jäh von ihnen ab.", footnotes: ["2 Kön 20,12-19"] },
      { number: 18, text: "Weil sie so offen ihre Unzucht trieb und ihre Scham entblößte, wandte auch ich mich jäh von ihr ab, wie ich mich von ihrer Schwester abgewandt hatte." },
      { number: 19, text: "Sie jedoch ging noch weiter in ihrem unzüchtigen Treiben. Sie dachte an die Tage ihrer Jugend, als sie in Ägypten Unzucht getrieben hatte." },
      { number: 20, text: "Und es erwachte in ihr die Gier nach ihren Liebhabern, deren Glieder wie die Glieder der Esel und deren Erguss wie der Erguss der Hengste waren." },
      { number: 21, text: "Du hattest nämlich das schändliche Treiben deiner Jugend vermisst, als die Ägypter nach deinen Brüsten griffen und deine jugendliche Brust streichelten.", footnotes: ["21b: Sinngemäß übersetzt nach V. 3 und V. 8."] },
      { number: 22, text: "Darum, Oholiba - so spricht Gott, der Herr -, hetze ich deine Liebhaber, von denen du dich jäh abgewandt hast, gegen dich auf, von allen Seiten führe ich sie gegen dich heran:" },
      { number: 23, text: "die Söhne Babels und alle Chaldäer, Männer aus Pekod, Schoa und Koa, dazu alle Assyrer, begehrenswerte junge Männer, lauter Statthalter und Herren, Helden und Krieger, alle hoch zu Ross.", footnotes: ["Krieger, wörtlich: die Aufgebotenen. - Pekod, Schoa und Koa sind Völkerschaften im Osten Babyloniens."] },
      { number: 24, text: "In Scharen überfallen sie dich mit Reitern und Wagen und einem Heer aus vielen Völkern. Mit Langschilden, Rundschilden und Helmen bewaffnet, umstellen sie dich. Dann lege ich ihnen den Fall vor, damit sie nach ihrem Recht über dich richten." },
      { number: 25, text: "Ich lasse dich meine Eifersucht fühlen, damit sie an dir voll Grimm die Strafe vollziehen: Nase und Ohren werden sie dir abschneiden. Was von dir übrig bleibt, fällt unter dem Schwert. Sie nehmen dir die Söhne und Töchter weg. Was dann noch übrig ist von dir, wird vom Feuer verzehrt." },
      { number: 26, text: "Sie ziehen dir die Kleider aus und nehmen dir deinen Schmuck weg." },
      { number: 27, text: "So mache ich deinem schändlichen Treiben und deiner Unzucht, die du schon in Ägypten getrieben hast, ein Ende. Du sollst deine Liebhaber nicht mehr ansehen und nie mehr an Ägypten denken." },
      { number: 28, text: "Denn - so spricht Gott, der Herr - ich will dich in die Gewalt derer geben, gegen die du jetzt voll Hass bist, in die Gewalt derer, von denen du dich jäh abgewandt hast." },
      { number: 29, text: "Sie werden voll Hass gegen dich vorgehen und dir alles nehmen, was du mühsam erworben hast. Sie werden dich nackt und bloß zurücklassen, deine lüsterne, schändliche und unzüchtige Scham wird entblößt sein." },
      { number: 30, text: "Das tut man dir an, weil du den Völkern so unzüchtig nachgelaufen bist und dich mit ihren Götzen unrein gemacht hast." },
      { number: 31, text: "Auf dem Weg deiner Schwester bist du gegangen, darum gebe ich dir ihren Becher in die Hand.", footnotes: ["31-34: Der Becher mit dem bitteren Trank ist Bild für das Strafgericht Gottes, das über Samaria bereits hereingebrochen ist."] },
      { number: 32, text: "So spricht Gott, der Herr: Den Becher deiner Schwester sollst du leeren, / den tiefen und weiten, der viel fasst. / [Zum Gelächter und Gespött sollst du werden.]", footnotes: ["Jes 51,17"] },
      { number: 33, text: "Von Trunkenheit und Qual wirst du voll sein. / Ein Becher des Grauens und des Schauderns / ist der Becher deiner Schwester Samaria." },
      { number: 34, text: "Du sollst ihn leertrinken, ja ausschlürfen, / du sollst seine Scherben zerbeißen / und dir die Brüste zerreißen. Denn ich habe gesprochen Spruch Gottes, des Herrn." },
      { number: 35, text: "Darum - so spricht Gott, der Herr: Weil du mich vergessen und mich gänzlich verworfen hast, sollst auch du die Strafe für deine Unzucht und dein schändliches Treiben erleiden.", footnotes: ["Jer 2,32"] },
      { number: 36, text: "Der Herr sagte zu mir: Menschensohn, willst du über Ohola und Oholiba zu Gericht sitzen? Dann halt ihnen ihre Gräueltaten vor:", footnotes: ["20,4"] },
      { number: 37, text: "Sie haben die Ehe gebrochen und an ihren Händen klebt Blut. Mit ihren Götzen haben sie Ehebruch begangen, sogar ihre Söhne, die sie mir geboren hatten, ließen sie durch das Feuer gehen, den Götzen zum Fraß.", footnotes: ["16,20; Lev 18,21"] },
      { number: 38, text: "Noch mehr taten sie mir an: Am gleichen Tag haben sie mein Heiligtum unrein gemacht und meine Sabbat-Tage entweiht." },
      { number: 39, text: "Denn noch am selben Tag, an dem sie ihre Söhne den Götzen schlachteten, kamen sie in mein Heiligtum und entweihten es. Ja, so trieben sie es in meinem Haus." },
      { number: 40, text: "Sogar in weite Ferne hast du Boten geschickt und Männer zu dir eingeladen. Als sie kamen, hast du ihretwegen gebadet, deine Augen geschminkt und deinen Schmuck angelegt." },
      { number: 41, text: "Du hast dich auf ein prunkvolles Lager gesetzt; davor stand ein Tisch bereit, auf den du meinen Weihrauch und mein Öl gestellt hattest." },
      { number: 42, text: "Der Lärm einer sorglosen Menge umgab sie; denn laut zechten die Männer, die Unzahl von Menschen, die man aus der Wüste herbeigeholt hatte. Sie legten den beiden Schwestern Spangen an die Arme und setzten ihnen prächtige Kronen auf." },
      { number: 43, text: "Ich dachte: So haben sie die Ehe gebrochen; wie eine Dirne es treibt, so treiben sie Unzucht.", footnotes: ["H ist verderbt, die Übersetzung daher unsicher."] },
      { number: 44, text: "Die Männer gingen zu ihr, wie man zu einer Dirne geht. So gingen sie zu Ohola und Oholiba, den schamlosen Frauen." },
      { number: 45, text: "Doch gerechte Männer werden ihnen das Urteil sprechen nach der Rechtsvorschrift für Ehebrecherinnen und Mörderinnen. Denn sie haben Ehebruch begangen und an ihren Händen klebt Blut.", footnotes: ["16,38.40; Lev 20,10"] },
      { number: 46, text: "Ja, so spricht Gott, der Herr: Man berufe eine Volksversammlung gegen sie ein; sie sollen misshandelt und ausgeraubt werden." },
      { number: 47, text: "Die Volksversammlung soll sie steinigen und mit Schwertern in Stücke hauen. Ihre Söhne und Töchter soll man töten und ihre Häuser verbrennen." },
      { number: 48, text: "So mache ich dem schändlichen Treiben im Land ein Ende, damit alle Frauen gewarnt sind und nicht ebenso schamlos handeln wie ihr." },
      { number: 49, text: "Man wird euch für euer schändliches Treiben bestrafen und ihr müsst für die Sünden büßen, die ihr mit euren Götzen begangen habt. Dann werdet ihr erkennen, dass ich Gott, der Herr, bin." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Am zehnten Tag des zehnten Monats im neunten Jahr erging das Wort des Herrn an mich:", heading: "Weitere symbolische Handlungen: 24,1-27 | Die vergebliche Reinigung der Stadt: 24,1-14" },
      { number: 2, text: "Menschensohn, schreib dir das Datum dieses Tages auf, genau den heutigen Tag! Am heutigen Tag begann der König von Babel seinen Angriff auf Jerusalem." },
      { number: 3, text: "Leg dem widerspenstigen Volk ein Gleichnis vor und sag zu ihnen: So spricht Gott, der Herr: Stell einen Kessel aufs Feuer, / stell ihn auf; auch Wasser gieß ein!", footnotes: ["(3-14) 11,3-13"] },
      { number: 4, text: "Leg Fleisch hinein, / lauter gute Stücke, Lende und Schulter! / Gib die besten Knochen dazu!" },
      { number: 5, text: "Nimm auserlesene Schafe! / Auch Brennholz leg ringsum unter den Kessel! Lass die Fleischstücke kochen! / Auch die Knochen koch mit!", footnotes: ["Brennholz: Text korr.; H: die Knochen. - koch mit: Text korr.; H: kochen sie."] },
      { number: 6, text: "Darum - so spricht Gott, der Herr: Weh der Stadt voll Blutschuld, / weh dem verrosteten Kessel und weh dem Rost, der nicht abgeht. / Stück für Stück nimm wahllos heraus!", footnotes: ["22,2"] },
      { number: 7, text: "Denn das Blut, das die Stadt vergoss, / ist noch mitten in ihr. An den nackten Felsen hat sie es hingeschüttet. / Nicht auf die Erde hat sie es vergossen / und nicht mit Erde bedeckt,", footnotes: ["Lev 17,13; Ijob 16,18"] },
      { number: 8, text: "sodass mein Zorn entbrannte und ich Rache nahm: / Auf dem nackten Felsen vergieße ich ihr Blut; / es wird nicht mit Erde bedeckt." },
      { number: 9, text: "Darum - so spricht Gott, der Herr: Weh der Stadt voll Blutschuld. / Auch ich schichte einen großen Holzstoß auf," },
      { number: 10, text: "ich häufe das Holz, / ich entzünde das Feuer, ich koche das Fleisch, / ich gieße die Brühe ab / [und die Knochen sollen verbrennen].", footnotes: ["ich gieße die Brühe ab: Text korr. nach G."] },
      { number: 11, text: "Dann stelle ich den Kessel leer auf die Glut, damit das Metall sich erhitzt und glüht, damit schmilzt, was an ihm unrein ist, und der Rost verschwindet." },
      { number: 12, text: "Doch umsonst die Mühe: Der starke Rost geht im Feuer nicht ab." },
      { number: 13, text: "Wegen deiner Schandtaten und deiner Unreinheit - weil ich dich rein machen wollte, du aber nicht frei wurdest von deiner Unreinheit sollst du nicht mehr rein werden, bis ich meinen Zorn an dir gestillt habe.", footnotes: ["5,13"] },
      { number: 14, text: "Ich, der Herr, habe gesprochen. Jetzt ist es soweit, ich führe es aus. Ich sehe nicht tatenlos zu. Ich habe kein Mitleid, es reut mich nicht. Nach deinem Verhalten und deinen Taten will ich dich richten Spruch Gottes, des Herrn.", footnotes: ["5,11", "ich will dich richten: Text korr. nach den alten Übersetzungen und einigen H-Handschriften."] },
      { number: 15, text: "Das Wort des Herrn erging an mich:", heading: "Die Zerstörung der Stadt: 24,15-27" },
      { number: 16, text: "Menschensohn, ich nehme dir die Freude deiner Augen durch einen jähen Tod. Doch du sollst weder klagen noch weinen. Keine Träne darfst du vergießen," },
      { number: 17, text: "nur leise stöhnen. Keine Trauerfeier sollst du halten. Binde deinen Kopfbund um und zieh deine Schuhe an! Verhülle deinen Bart nicht, und iss kein Trauerbrot!", footnotes: ["Trauerbrot: Text korr. nach Vg und der aramäischen Übersetzung; H: Menschenbrot."] },
      { number: 18, text: "Ich redete am Morgen zum Volk. Meine Frau starb am Abend und ich tat am Morgen, was mir befohlen war." },
      { number: 19, text: "Da sagte das Volk zu mir: Willst du uns nicht erklären, was dein Verhalten für uns zu bedeuten hat?" },
      { number: 20, text: "Ich antwortete ihnen: Das Wort des Herrn ist an mich ergangen." },
      { number: 21, text: "Sag zum Haus Israel: So spricht Gott, der Herr: Ich will mein Heiligtum entweihen, den Zufluchtsort, auf den ihr so stolz seid, die Freude eurer Augen und die Sehnsucht eurer Seele. Eure Söhne und Töchter, die ihr zurückgelassen habt, werden unter dem Schwert fallen." },
      { number: 22, text: "Dann werdet ihr genauso handeln wie ich: Ihr werdet den Bart nicht verhüllen und kein Trauerbrot essen." },
      { number: 23, text: "Euren Kopfbund werdet ihr auf dem Kopf behalten und eure Schuhe an den Füßen. Ihr werdet weder klagen noch weinen, sondern wegen eurer Sünden dahinsiechen und miteinander stöhnen." },
      { number: 24, text: "Ezechiel wird ein Mahnzeichen für euch sein. Genauso wie er gehandelt hat, werdet ihr handeln; wenn das eintrifft, werdet ihr erkennen, dass ich Gott, der Herr, bin." },
      { number: 25, text: "Du aber, Menschensohn, an jenem Tag, wenn ich ihnen ihren Zufluchtsort nehme, ihre Pracht und Wonne, die Freude ihrer Augen und die Sehnsucht ihrer Seele, ihre Söhne und Töchter," },
      { number: 26, text: "an jenem Tag wird zu dir ein Flüchtling kommen, um dir die Nachricht zu bringen.", footnotes: ["33,21"] },
      { number: 27, text: "Und an jenem Tag wird in Gegenwart des Flüchtlings dein Mund geöffnet. Du wirst reden und nicht mehr stumm sein. So wirst du zu einem Mahnzeichen für sie und sie werden erkennen, dass ich der Herr bin.", footnotes: ["12,6.11"] },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Die Drohsprüche gegen die Nachbarvölker: 25,1 - 32,32 | Gegen die unmittelbaren Nachbarn Israels: 25,1 - 28,26 | Gegen Ammon, Moab, Edom und die Philister: 25,1-17" },
      { number: 2, text: "Menschensohn, richte dein Gesicht gegen die Ammoniter, sprich als Prophet über sie,", footnotes: ["(1-7) 21,33-37; Am 1,13-15; Jer 49,1-6"] },
      { number: 3, text: "und sag zu ihnen: Hört das Wort Gottes, des Herrn: So spricht Gott, der Herr: Weil du gegen mein Heiligtum hin «Ha, ha» geschrien hast, als es entweiht wurde, und gegen das Land Israel, als es verwüstet wurde, und gegen das Haus Juda, als es verschleppt wurde," },
      { number: 4, text: "darum gebe ich dich jetzt den Völkern des Ostens zum Besitz. Sie werden ihre Zelte bei dir aufschlagen und ihre Siedlungen bei dir errichten. Sie werden deine Früchte essen und deine Milch trinken." },
      { number: 5, text: "Rabba mache ich zum Weideplatz der Kamele und die Städte der Ammoniter zum Lager der Schafe und Ziegen. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 6, text: "Denn so spricht Gott, der Herr: Weil du in die Hände geklatscht und mit dem Fuß auf den Boden gestampft hast, voll höhnischer Schadenfreude über das Land Israel," },
      { number: 7, text: "darum strecke ich jetzt meine Hand gegen dich aus, ich überlasse dich den Völkern zur Plünderung, ich rotte dich aus unter den Nationen und sorge dafür, dass du aus der Zahl der Länder verschwindest; ich vernichte dich. Dann wirst du erkennen, dass ich der Herr bin." },
      { number: 8, text: "So spricht Gott, der Herr: Weil Moab und Seïr sagen: Dem Haus Juda geht es wie allen anderen Völkern!,", footnotes: ["(8-11) Jes 15; Jer 48"] },
      { number: 9, text: "darum will ich die Flanke Moabs aufreißen, sodass es bei ihm keine Stadt mehr gibt von einem Ende zum andern, nicht mehr Bet-Jeschimot, Baal-Meon und Kirjatajim, die Zierde des Landes.", footnotes: ["Die hier genannten Städte liegen östlich des Toten Meeres."] },
      { number: 10, text: "Zusammen mit den Ammonitern gebe ich es den Völkern des Ostens zum Besitz, sodass die Ammoniter unter den Nationen nicht mehr erwähnt werden" },
      { number: 11, text: "und ich an Moab das Urteil vollstrecke. Dann werden sie erkennen, dass ich der Herr bin." },
      { number: 12, text: "So spricht Gott, der Herr: Weil Edom sich am Haus Juda gerächt hat, weil es Schuld auf sich geladen und Rache an ihm genommen hat,", footnotes: ["(12-14) 35,1-15; Jer 49,7-22; Ps 137,7; Am 1,11f"] },
      { number: 13, text: "darum - so spricht Gott, der Herr: Ich strecke meine Hand gegen Edom aus, ich vernichte darin Mensch und Tier und mache es zur Wüste. Von Teman bis Dedan sollen sie unter dem Schwert fallen.", footnotes: ["Die Lage von Teman ist nicht mehr zu bestimmen; es lag aber in Edom; Dedan ist ein Gebiet südlich von Edom."] },
      { number: 14, text: "Ich lege meine Rache an Edom in die Hand meines Volkes Israel. Sie werden an Edom meinem Zorn und Grimm entsprechend handeln. Dann wird Edom meine Rache kennen lernen - Spruch Gottes, des Herrn." },
      { number: 15, text: "So spricht Gott, der Herr: Weil die Philister rachsüchtig waren und voll Verachtung Rache nahmen, um Juda aus uralter Feindschaft zu vernichten,", footnotes: ["(15-17) Jer 47; Jes 14,29-32; Am 1,6-8; Zef 2,4-7"] },
      { number: 16, text: "darum - so spricht Gott, der Herr: Ich will jetzt meine Hand gegen die Philister ausstrecken, ich will die Kereter ausrotten und die übrigen Völker an der Küste vernichten.", footnotes: ["Die Kereter sind eine Gruppe innerhalb der Philister; «ausrotten» und «Kereter» bilden in H ein Wortspiel."] },
      { number: 17, text: "Ich nehme an ihnen gewaltige Rache mit grimmigen Strafen. Dann werden sie erkennen, dass ich der Herr bin, wenn ich mich an ihnen räche." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Am ersten Tag des (zwölften) Monats im elften Jahr erging das Wort des Herrn an mich:", heading: "Gegen Tyrus: 26,1-21" },
      { number: 2, text: "Menschensohn, weil Tyrus über Jerusalem sagte: / Ha, das Tor zu den Völkern ist zerbrochen; alles fällt mir zu, jetzt kann ich mich mästen; / denn die Stadt ist verwüstet!,", footnotes: ["Jes 23; Am 1,9f"] },
      { number: 3, text: "darum - so spricht Gott, der Herr: / Jetzt gehe ich gegen dich vor, Tyrus, und lasse viele Völker gegen dich anbranden, / wie das Meer seine Wogen anbranden lässt." },
      { number: 4, text: "Sie zerstören die Mauern von Tyrus / und seine Türme reißen sie ein. Sein Erdreich schwemme ich weg, / zum nackten Felsen mache ich Tyrus." },
      { number: 5, text: "Ein Platz zum Trocknen der Netze / wird es mitten im Meer; denn ich habe gesprochen - Spruch Gottes, des Herrn. / Es wird eine Beute der Völker." },
      { number: 6, text: "Seine Töchter auf dem Festland werden / vom Schwert erschlagen. / Dann wird man erkennen, dass ich der Herr bin." },
      { number: 7, text: "Denn so spricht Gott, der Herr: Nebukadnezzar, den König von Babel, / den König der Könige, führe ich von Norden gegen Tyrus heran, / mit Rossen und Wagen und Reitern, / mit einem großen Aufgebot, einem gewaltigen Heer.", footnotes: ["29,17-21; Jer 27,6"] },
      { number: 8, text: "Deine Töchter auf dem Festland / wird er mit dem Schwert erschlagen. Er baut dir gegenüber einen Belagerungswall, / schüttet einen Damm gegen dich auf / und errichtet ein Schilddach." },
      { number: 9, text: "Seinen Sturmbock setzt er an / zum Stoß gegen deine Mauern, / deine Türme reißt er mit Brecheisen ein.", footnotes: ["Tyrus wurde von Nebukadnezzar dreizehn Jahre lang belagert."] },
      { number: 10, text: "Eine Staubwolke wird dich bedecken, / so viele Pferde hat er. Unter dem Dröhnen der Reiter und Räder und Wagen / erzittern deine Mauern, wenn er durch deine Tore eindringt, / wie man eindringt in eine Stadt voller Breschen." },
      { number: 11, text: "Mit den Hufen seiner Pferde / zerstampft er all deine Straßen. Mit dem Schwert erschlägt er dein Volk / und deine mächtigen Säulen stürzt er zu Boden." },
      { number: 12, text: "Sie plündern deinen Besitz / und rauben dir deine Waren. Deine Mauern reißen sie ein. / Sie zerstören die prächtigen Bauten. Deine Steine, deine Balken, deinen ganzen Schutt / werfen sie mitten ins Meer." },
      { number: 13, text: "So mache ich deinen lärmenden Liedern ein Ende, / vom Klang deiner Zithern ist nichts mehr zu hören.", footnotes: ["Jes 24,8f; Jer 25,10"] },
      { number: 14, text: "Zum nackten Felsen mache ich dich. / Du wirst ein Platz zum Trocknen der Netze. Man baut dich nie wieder auf; / denn ich, der Herr, habe gesprochen - / Spruch Gottes, des Herrn.", footnotes: ["Text korr."] },
      { number: 15, text: "So spricht Gott, der Herr, zu Tyrus: Ist es nicht so, dass die Inseln erzittern beim Getöse deines Sturzes, beim Gestöhn der zu Tode Getroffenen, beim mörderischen Blutbad in deiner Mitte?" },
      { number: 16, text: "Dann steigen alle Fürsten der Küstenländer von ihren Thronen herab, sie legen ihre Gewänder ab und ziehen ihre bunt gewebten Kleider aus. Sie hüllen sich in Schrecken und setzen sich auf den Boden; immer wieder erfasst sie ein Zittern und sie sind entsetzt über dich." },
      { number: 17, text: "Sie stimmen die Totenklage über dich an und sagen zu dir: Ach, wie bist du zerstört, / du Bewohnerin der Meere, ruhmreiche Stadt, einst eine Macht auf dem Meer, / du und deine Bewohner, / die alle ihre Nachbarn in Schrecken versetzten." },
      { number: 18, text: "Nun aber beben die Inseln / seit dem Tag deines Sturzes. / Bestürzt sind die Inseln des Meeres über dein Ende." },
      { number: 19, text: "Denn so spricht Gott, der Herr: Ich mache dich zur verwüsteten Stadt; dann wirst du wie die Städte sein, die nicht mehr bewohnt sind. Die Urflut lasse ich steigen, sodass gewaltige Wassermassen dich zudecken." },
      { number: 20, text: "Ich stoße dich zu denen hinab, die ins Grab gesunken sind, zum Volk der Vorzeit. Ich weise dir einen Wohnsitz zu in den Tiefen der Erde, in der ewigen Einöde bei denen, die ins Grab gesunken sind. Denn du sollst nicht mehr bewohnt sein und dich nicht mehr erheben im Land der Lebenden.", footnotes: ["32,18; Jes 14,11.15", "in der ewigen Einöde: Text korr.; H: wie ewige Trümmer. - und dich nicht mehr erheben: Text korr. nach G."] },
      { number: 21, text: "Zu einem Bild des Schreckens mache ich dich. Du bist nicht mehr da, und wer dich sucht, wird dich in Ewigkeit nicht finden - Spruch Gottes, des Herrn." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Die Totenklage über Tyrus, das prächtige Schiff: 27,1-36" },
      { number: 2, text: "Du, Menschensohn, stimm die Totenklage über Tyrus an!" },
      { number: 3, text: "Sag zu Tyrus: Du, die du wohnst am Zugang zum Meer, / die du Handel treibst mit den Völkern / bis hin zu den vielen Inseln, so spricht Gott, der Herr: Tyrus, du sagst: / Ich bin (ein Schiff) von vollendeter Schönheit." },
      { number: 4, text: "Im Herzen der Meere liegt dein Gebiet. / Vollendet schön schufen dich deine Erbauer." },
      { number: 5, text: "Aus Zypressenholz vom Senirgebirge / bauten sie all deine Planken, eine Zeder vom Libanon nahmen sie, / um auf dir den Mast zu errichten.", footnotes: ["Senir ist nach Dtn 3,9 der amoritische Name des Hermon."] },
      { number: 6, text: "Deine Ruder machten sie aus Eichen vom Baschan, / dein Deck aus Elfenbein und Eschenholz / von den Inseln der Kittäer.", footnotes: ["Eschenholz: Text korr.; H ist verschrieben. - Die Kittäer sind nach der Stadt Kition in Zypern benannt, sind also die Bewohner Zyperns."] },
      { number: 7, text: "Dein Segel war bunt gewebtes ägyptisches Leinen. / Es sollte dein Erkennungszeichen sein. Deine Planen waren aus violettem und rotem Purpur / von den Küsten Elischas.", footnotes: ["Die Stadt Elischa lag wohl in Zypern."] },
      { number: 8, text: "Deine Ruderer stammten aus Sidon und Arwad. / Erfahrene Männer, Tyrus, gab es bei dir. / Sie waren deine Matrosen.", footnotes: ["Arwad: nördlich von Tripolis an der syrischen Küste."] },
      { number: 9, text: "Die Ältesten von Gebal / und seine erfahrensten Männer / fuhren auf dir, um deine Risse zu dichten. Alle Schiffe des Meeres und ihre Matrosen / kamen zu dir, um mit dir Handel zu treiben.", footnotes: ["Gebal ist Byblus, nördlich von Beirut."] },
      { number: 10, text: "Leute von Paras, Lud und Put / dienten in deiner Mannschaft als Krieger. Schilde und Helme hängten sie auf an dir; / diese verliehen dir prächtigen Glanz.", footnotes: ["38,5"] },
      { number: 11, text: "Die Söhne von Arwad und deine Mannschaft standen ringsum auf deinen Mauern und die Wächter auf deinen Türmen. Ihre Schilde hängten sie rings an deinen Mauern auf. Sie machten deine Schönheit vollkommen.", footnotes: ["und die Wächter: Text korr. nach G; H ist unklar."] },
      { number: 12, text: "Tarschisch kaufte bei dir wegen der Fülle deiner Güter; Silber, Eisen, Zinn und Blei gaben sie für deine Waren.", footnotes: ["Jes 23,1"] },
      { number: 13, text: "Jawan, Tubal und Meschech waren deine Händler. Menschen und Kupfergeräte gaben sie für deine Handelswaren.", footnotes: ["32,26; 38,2"] },
      { number: 14, text: "Die von Bet-Togarma gaben Zugpferde und Reitpferde und Maultiere für deine Waren.", footnotes: ["38,6", "Bet-Togarma lag in Kleinasien."] },
      { number: 15, text: "Die Söhne von Rhodos waren deine Händler. Viele Inseln standen als Kaufleute in deinem Dienst; als Abgaben brachten sie dir Elfenbein und Ebenholz." },
      { number: 16, text: "Edom kaufte bei dir wegen der Fülle deiner Erzeugnisse. Karfunkelstein, Purpur, bunte Stoffe, Byssus, Korallen und Rubine gaben sie für deine Waren.", footnotes: ["Edom: Text korr. nach vielen H-Handschriften; vgl. G; H: Aram."] },
      { number: 17, text: "Juda und das Land Israel waren deine Händler. Weizen, Oliven, Wachs, Honig, Öl und Mastix gaben sie für deine Handelswaren.", footnotes: ["Oliven, Wachs: H unklar."] },
      { number: 18, text: "Damaskus kaufte bei dir wegen der Fülle deiner Erzeugnisse, wegen der Fülle deiner Güter. Wein aus Helbon und Wolle aus Zahar," },
      { number: 19, text: "Wein aus Usal gaben sie für deine Waren. Gehämmertes Eisen, Zimt und Gewürzrohr gehörte zu deinen Handelswaren.", footnotes: ["Gen 10,27", "Wein: Text korr. nach G; H: Dan und Jawan."] },
      { number: 20, text: "Dedan war dein Händler für Satteldecken." },
      { number: 21, text: "Arabien und alle Fürsten von Kedar, sie waren Kaufleute in deinen Diensten. Sie zahlten mit Lämmern, Widdern und Böcken.", footnotes: ["Gen 25,13", "21-23: Die Namen in VV. 21f weisen nach Arabien, die von V. 23 wohl nach Mesopotamien. - ganz Medien: Text korr."] },
      { number: 22, text: "Händler von Saba und Ragma trieben Handel mit dir. Für den besten Balsam, für alle Arten von Edelsteinen und Gold gaben sie deine Waren.", footnotes: ["Gen 10,7"] },
      { number: 23, text: "Haran, Kanne und Eden, die Händler von Saba, Assur, ganz Medien trieben Handel mit dir." },
      { number: 24, text: "Sie waren deine Händler. Prunkgewänder und Mäntel aus violettem Purpur, bunte Stoffe und mehrfarbige Tücher, feste gedrehte Seile kauften sie ein für dich." },
      { number: 25, text: "Die Schiffe von Tarschisch dienten dir / als Karawanen für deine Waren. So fülltest du dich, / wurdest schwer beladen mitten im Meer." },
      { number: 26, text: "Über gewaltige Wasser brachten dich deine Ruderer. / Da zerbrach dich der Ostwind mitten im Meer." },
      { number: 27, text: "Dein Reichtum, deine Waren, dein Handelsgut, / deine Seeleute und deine Matrosen, die Männer, die deine Risse dichten, / die deine Waren eintauschen, alle deine Soldaten auf dir / und das ganze Heer, das du hast, / fallen mitten ins Meer am Tag deines Sturzes." },
      { number: 28, text: "Beim lauten Geschrei deiner Matrosen / zittern die Küsten." },
      { number: 29, text: "Alle, die das Ruder führen, verlassen ihr Schiff. / Alle Seefahrer und alle Matrosen bleiben an Land." },
      { number: 30, text: "Deinetwegen schreien sie laut, / bitterlich jammern sie. Sie streuen sich Staub auf das Haupt / und wälzen sich auf dem Boden." },
      { number: 31, text: "Deinetwegen scheren sie sich eine Glatze, / Trauerkleider legen sie an und weinen über dich mit verbitterter Seele, / mit bitterer Klage." },
      { number: 32, text: "In ihrem Jammer stimmen sie über dich / ein Totenlied an und halten die Totenklage für dich: / Wer war Tyrus vergleichbar, mitten im Meer?", footnotes: ["war vergleichbar: Text korr.; H ist unklar."] },
      { number: 33, text: "Als deine Waren vom Meer her kamen, / hast du viele Völker gesättigt. Die Könige der Erde machtest du reich / mit deinem gewaltigen Reichtum / und deinen Handelswaren." },
      { number: 34, text: "Jetzt liegst du zerbrochen im Meer, / in den Tiefen der Fluten. Deine Handelswaren, dein ganzes Heer / sind mit dir versunken.", footnotes: ["34a: Text korr.; vgl. die alten Übersetzungen."] },
      { number: 35, text: "Alle Bewohner der Küsten sind entsetzt über dich; / ihren Königen sträubt sich das Haar. / Verstört sind ihre Gesichter." },
      { number: 36, text: "Deine Kaufleute unter den Völkern / zischen (voll Hohn) über dich. Zu einem Bild des Schreckens bist du geworden, / für immer dahin.", footnotes: ["28,19"] },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Gegen den Fürsten von Tyrus: 28,1-10" },
      { number: 2, text: "Menschensohn, sag zum Fürsten von Tyrus: So spricht Gott, der Herr: Dein Herz war stolz und du sagtest: Ich bin ein Gott, einen Wohnsitz für Götter bewohne ich mitten im Meer. Doch du bist nur ein Mensch und kein Gott, obwohl du im Herzen geglaubt hast, dass du wie Gott bist.", footnotes: ["Gen 3,5; Jes 14,13f"] },
      { number: 3, text: "Gewiss, du bist weiser als Daniel. / Kein Geheimnis war dir zu dunkel.", footnotes: ["14,14; Dan 1,17"] },
      { number: 4, text: "Durch deine Weisheit und Einsicht schufst du dir Reichtum. / Mit Gold und Silber fülltest du deine Kammern." },
      { number: 5, text: "Durch deine gewaltige Weisheit, durch deinen Handel / hast du deinen Reichtum vermehrt. Doch dein Herz wurde stolz / wegen all deines Reichtums." },
      { number: 6, text: "Darum - so spricht Gott, der Herr: Weil du im Herzen geglaubt hast, / dass du wie Gott bist," },
      { number: 7, text: "darum schicke ich Fremde gegen dich, / tyrannische Völker. Sie zücken das Schwert gegen all deine prächtige Weisheit, / entweihen deinen strahlenden Glanz." },
      { number: 8, text: "Man stößt dich hinab in das Grab; / wie einer durchbohrt wird und stirbt, / so stirbst du mitten im Meer." },
      { number: 9, text: "Willst du dann angesichts deiner Mörder noch sagen: / Ich bin ein Gott? Du bist nur ein Mensch und kein Gott / in der Hand deiner Mörder.", footnotes: ["Jes 31,3"] },
      { number: 10, text: "Wie Unbeschnittene sterben, / so stirbst du durch Fremde; denn ich habe gesprochen - / Spruch Gottes, des Herrn." },
      { number: 11, text: "Das Wort des Herrn erging an mich:", heading: "Die Totenklage über den König von Tyrus: 28,11-19" },
      { number: 12, text: "Menschensohn, stimm die Totenklage an über den König von Tyrus und sag zu ihm: So spricht Gott, der Herr: Du warst ein vollendet gestaltetes Siegel, / voll Weisheit und vollkommener Schönheit.", footnotes: ["27,2", "Siegel: Text korr.; vgl. die alten Übersetzungen."] },
      { number: 13, text: "Im Garten Gottes, in Eden, bist du gewesen. / Allerlei kostbare Steine umgaben dich: Rubin, Topas, dazu Jaspis, / Chrysolith, Karneol und Onyx, / Saphir, Karfunkelstein und Smaragd. Aus Gold war alles gemacht, / was an dir erhöht und vertieft war, all diese Zierden brachte man an, / als man dich schuf.", footnotes: ["31,8f; Gen 2,8"] },
      { number: 14, text: "Einem Kerub mit ausgebreiteten, schützenden Flügeln gesellte ich dich bei. / Auf dem heiligen Berg der Götter bist du gewesen. / Zwischen den feurigen Steinen gingst du umher.", footnotes: ["Gen 3,24; Jes 14,13; Ez 10,2", "einem Kerub: Text korr. nach G."] },
      { number: 15, text: "Ohne Tadel war dein Verhalten / seit dem Tag, an dem man dich schuf, / bis zu dem Tag, an dem du Böses getan hast.", footnotes: ["bis zu dem Tag, an dem du Böses getan hast, wörtlich: bis an dir Schlechtigkeit gefunden wurde."] },
      { number: 16, text: "Durch deinen ausgedehnten Handel / warst du erfüllt von Gewalttat, / in Sünde bist du gefallen. Darum habe ich dich vom Berg der Götter verstoßen, / aus der Mitte der feurigen Steine / hat dich der schützende Kerub verjagt." },
      { number: 17, text: "Hochmütig warst du geworden, / weil du so schön warst. Du hast deine Weisheit vernichtet, / verblendet vom strahlenden Glanz. Ich stieß dich auf die Erde hinab. / Den Blicken der Könige gab ich dich preis, / damit sie dich alle begaffen." },
      { number: 18, text: "Du hast durch gewaltige Schuld, / durch unredliche Handelsgeschäfte / deine Heiligtümer entweiht. So ließ ich mitten in dir ein Feuer ausbrechen, / das dich verzehrt hat. Vor den Augen all derer, die dich sahen, / machte ich dich zu Asche auf der Erde." },
      { number: 19, text: "All deine Freunde unter den Völkern / waren entsetzt über dich. Zu einem Bild des Schreckens bist du geworden, / du bist für immer dahin.", footnotes: ["27,36"] },
      { number: 20, text: "Das Wort des Herrn erging an mich:", heading: "Gegen Sidon: 28,20-23" },
      { number: 21, text: "Menschensohn, richte dein Gesicht auf Sidon, tritt als Prophet gegen die Stadt auf" },
      { number: 22, text: "und sag: So spricht Gott, der Herr: Jetzt gehe ich gegen dich vor, Sidon, / und verherrliche mich in deiner Mitte. Dann werden sie erkennen, dass ich der Herr bin, / wenn ich an der Stadt das Urteil vollstrecke / und mich so als heilig erweise." },
      { number: 23, text: "Ich schicke Pest und Mord in die Gassen der Stadt. / Viele fallen in ihr, erschlagen vom Schwert, das von überall her auf sie einschlägt. / Dann werden sie erkennen, dass ich der Herr bin." },
      { number: 24, text: "Dann droht dem Haus Israel kein stechender Dorn und kein verletzender Stachel mehr von all seinen feindlichen Nachbarn. Daran werden sie erkennen, dass ich Gott, der Herr, bin.", heading: "Verheißung für Israel: 28,24-26" },
      { number: 25, text: "So spricht Gott, der Herr: Wenn ich die vom Haus Israel aus all den Ländern zusammenführe, in die sie zerstreut sind, dann erweise ich mich an ihnen vor den Augen der Völker als heilig. Sie werden in ihrem Land wohnen, das ich meinem Knecht Jakob gegeben habe.", footnotes: ["11,17; 20,34.41; 34,13; 36,24; 37,21"] },
      { number: 26, text: "Dort werden sie in Sicherheit leben; sie werden Häuser bauen und Weinberge pflanzen. Sie wohnen in Sicherheit, sobald ich an all ihren Feinden ringsum das Urteil vollstrecke. Dann werden sie erkennen, dass ich, der Herr, ihr Gott bin." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Am zwölften Tag des zehnten Monats im zehnten Jahr erging das Wort des Herrn an mich:", heading: "Gegen den Pharao und Ägypten: 29,1 - 32,32 | Das Gericht über den König und sein Land: 29,1-16" },
      { number: 2, text: "Menschensohn, richte dein Gesicht auf den Pharao, den König von Ägypten, tritt als Prophet auf gegen ihn und gegen ganz Ägypten", footnotes: ["Jes 19; Jer 46"] },
      { number: 3, text: "und sag: So spricht Gott, der Herr: Jetzt gehe ich gegen dich vor, Pharao, du König Ägyptens, du großes Krokodil, das zwischen den Armen des Nil liegt und sagt: Mir gehören die Arme des Nil, / ich habe sie selber erschaffen.", footnotes: ["32,2", "sie: Text korr. nach G; H: mich."] },
      { number: 4, text: "Aber ich schlage dir Haken durch die Kinnbacken und lasse die Fische deines Nil an deinen Schuppen kleben. Ich ziehe dich herauf aus deinem Nil samt all den Fischen deines Nil, die an deinen Schuppen kleben." },
      { number: 5, text: "Dann werfe ich dich in die Wüste hinaus, / dich und all die Fische des Nil. Aufs trockene Land wirst du fallen. / Niemand holt dich von dort und begräbt dich. Den wilden Tieren und den Vögeln des Himmels / werfe ich dich vor zum Fraß.", footnotes: ["und begräbt dich, wörtlich: und sammelt dich ein."] },
      { number: 6, text: "Dann werden alle Ägypter erkennen, / dass ich der Herr bin. Eine Stütze aus Schilfrohr bist du / für das Haus Israel.", footnotes: ["2 Kön 18,21", "bist du: Text korr. nach G."] },
      { number: 7, text: "Nehmen sie dich in die Hand, dann zerbrichst du / und durchbohrst ihnen die Schulter; stützen sie sich auf dich, dann zerbrichst du / und allen beginnen die Hüften zu wanken.", footnotes: ["zu wanken: Text korr."] },
      { number: 8, text: "Darum - so spricht Gott, der Herr: Ich bringe das Schwert über dich und rotte Mensch und Vieh bei dir aus." },
      { number: 9, text: "Ägypten wird zu Wüste und Ödland. Dann werden sie erkennen, dass ich der Herr bin. Du hast gesagt: Mir gehören die Arme des Nil; / ich habe sie selber erschaffen." },
      { number: 10, text: "Darum gehe ich jetzt gegen dich und deine Nilarme vor. Ich mache Ägypten zum dürren Ödland, zur Wüste von Migdol bis Syene und bis an die Grenzen von Kusch.", footnotes: ["30,6", "Migdol: die nördlichste Grenzfestung Ägyptens. - Syene (= Assuan): die südlichste Grenzfestung Ägyptens."] },
      { number: 11, text: "Weder Mensch noch Tier geht mehr darüber hin. Vierzig Jahre lang wird es nicht mehr bewohnt sein." },
      { number: 12, text: "Ich mache Ägypten zur Wüste inmitten verwüsteter Länder. Seine Städte sollen vierzig Jahre lang verwüstet daliegen inmitten verödeter Städte. Ich zerstreue die Ägypter unter die Völker und vertreibe sie in alle Länder." },
      { number: 13, text: "Denn so spricht Gott, der Herr: Nach vierzig Jahren führe ich die Ägypter aus den Völkern zusammen, unter die sie zerstreut wurden." },
      { number: 14, text: "Ich wende das Geschick Ägyptens und bringe sie zurück in das Land Patros, das Land ihrer Herkunft. Dort werden sie ein unbedeutendes Reich gründen.", footnotes: ["Patros ist ein Name für Oberägypten."] },
      { number: 15, text: "Es wird unbedeutend sein im Vergleich zu den anderen Königreichen, und es wird sich nicht mehr über die anderen Völker erheben. Ich mache sie ganz klein, damit sie nicht mehr über die Völker herrschen können." },
      { number: 16, text: "Das Haus Israel wird ihnen nicht mehr vertrauen und sich ihnen nicht mehr anschließen; es wird sich hüten, seine alte Schuld wieder in Erinnerung zu rufen. Sie werden erkennen, dass ich Gott, der Herr, bin." },
      { number: 17, text: "Am ersten Tag des ersten Monats im siebenundzwanzigsten Jahr erging das Wort des Herrn an mich:", heading: "Der Sieg Nebukadnezzars: 29,17-21" },
      { number: 18, text: "Menschensohn! Nebukadnezzar, der König von Babel, hat sein Heer vor Tyrus schwere Arbeit verrichten lassen; alle Köpfe wurden kahl und jede Schulter war zerschunden. Aber Tyrus hat ihn und sein Heer nicht belohnt für die Arbeit, die sie geleistet haben." },
      { number: 19, text: "Darum - so spricht Gott, der Herr: Ich gebe Nebukadnezzar, dem König von Babel, das Land Ägypten. Er wird seine Schätze wegschleppen; er wird alles plündern und reiche Beute machen. Das wird der Lohn seines Heeres sein.", footnotes: ["30,10.24"] },
      { number: 20, text: "Als Belohnung für seine Arbeit gebe ich ihm Ägypten; denn sie haben für mich gearbeitet - Spruch Gottes, des Herrn." },
      { number: 21, text: "An jenem Tag werde ich dem Haus Israel neue Kraft verleihen und du sollst wieder zu ihnen reden. Dann werden sie erkennen, dass ich der Herr bin.", footnotes: ["Ps 132,17", "Wörtlich: Ich werde dem Haus Israel ein Horn sprossen lassen und ich gebe dir Öffnung des Mundes in ihrer Mitte."] },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das Gericht über Ägypten: 30,1-26" },
      { number: 2, text: "Menschensohn, tritt als Prophet auf und sag: So spricht Gott, der Herr: Jammert und schreit: / Weh über diesen Tag!" },
      { number: 3, text: "Denn der Tag ist nahe, / der Tag des Herrn ist nahe, ein Tag dunkler Wolken. / Die Zeit für die Völker ist da.", footnotes: ["Joël 1,15; Am 5,18; Zef 1,14"] },
      { number: 4, text: "Das Schwert dringt ein in Ägypten. / Ein Zittern überfällt die Kuschiter, / wenn in Ägypten die Menschen erschlagen werden und fallen. Man schleppt seine Schätze hinweg / und reißt seine Grundmauern nieder." },
      { number: 5, text: "(Die Männer aus) Kusch, aus Put und Lud, / das ganze Völkergemisch, (die Männer aus) Kub und die Söhne des Landes, / mit dem ich einst meinen Bund schloss, / fallen wie sie unter dem Schwert.", footnotes: ["Jer 46,9"] },
      { number: 6, text: "So spricht der Herr: Es fallen die Helfer Ägyptens, / seine herrliche Macht sinkt dahin. Von Migdol bis hin nach Syene / fallen die Krieger unter dem Schwert - / Spruch Gottes, des Herrn.", footnotes: ["29,10", "Vgl. die Anmerkung zu 29,10."] },
      { number: 7, text: "Verwüstet liegt es dann da / inmitten verwüsteter Länder / und seine Städte inmitten verödeter Städte." },
      { number: 8, text: "Sie werden erkennen, dass ich der Herr bin, / wenn ich Ägypten verbrenne, / wenn all seine Helfer zusammenbrechen." },
      { number: 9, text: "An jenem Tag schicke ich meine Boten auf Schiffen hinaus, / um Kusch aus seiner Ruhe zu schrecken. Ein Zittern überfällt sie am Gerichtstag Ägyptens; / schon trifft all das ein." },
      { number: 10, text: "So spricht Gott, der Herr: Ich mache der Pracht Ägyptens ein Ende / durch die Hand Nebukadnezzars, / des Königs von Babel.", footnotes: ["29,10"] },
      { number: 11, text: "Er und sein Heer, / die gewalttätigsten unter den Völkern, / werden herbeigeholt, um das Land zu vernichten. Sie zücken ihr Schwert und schlagen Ägypten / und füllen das Land mit erschlagenen Menschen.", footnotes: ["28,7"] },
      { number: 12, text: "Ich lege die Nilarme trocken, / verkaufe das Land an Verbrecher, ich verwüste das Land und alles darin / durch die Hände von Fremden. / Ich, der Herr, habe gesprochen.", footnotes: ["Jes 19,5"] },
      { number: 13, text: "So spricht Gott, der Herr: Ich will die Götzen vernichten. / Ich führe das Ende der Götter von Memfis herbei. Der Fürst von Ägypten wird (bald) nicht mehr leben. / Ich stürze Ägypten in Angst." },
      { number: 14, text: "Patros will ich verwüsten, / Zoan will ich verbrennen, / an No vollstrecke ich das Urteil.", footnotes: ["14-16: Zu Patros vgl. die Anmerkung zu 29,14. - Zoan liegt im Nildelta: No ist Residenz- und Tempelstadt; Theben liegt in Oberägypten; Sin (V. 16) liegt nahe bei Zoan."] },
      { number: 15, text: "Über Sin, die Festung Ägyptens, gieße ich meinen Zorn aus und ich vernichte die Pracht von No." },
      { number: 16, text: "Ägypten will ich verbrennen. Sin wird sich in Krämpfen winden; No wird man erstürmen und Memfis wird am hellen Tag von Feinden bedrängt." },
      { number: 17, text: "Die jungen Männer von On und Pi-Beset fallen unter dem Schwert und die Einwohner dieser Städte ziehen in die Gefangenschaft.", footnotes: ["17f: On: Text korr. mit G. On (= Heliopolis) ist der zentrale Ort des ägyptischen Sonnenkults, nordwestlich von Kairo. Pi-Beset (= Bubastis) liegt im östlichen Nildelta, ebenso Tachpanhes (= Daphne). - die Zepter: Text korr. nach G und Vg; H: die Jochstangen."] },
      { number: 18, text: "Es wird ein schwarzer Tag für Tachpanhes, wenn ich dort die Zepter Ägyptens zerbreche; dann ist es mit seiner herrlichen Macht zu Ende. Finstere Wolken werden Ägypten bedecken und seine Töchter ziehen in die Gefangenschaft.", footnotes: ["Jer 43,9"] },
      { number: 19, text: "So vollstrecke ich an Ägypten das Urteil und sie werden erkennen, dass ich der Herr bin." },
      { number: 20, text: "Am siebten Tag des ersten Monats im elften Jahr erging das Wort des Herrn an mich:" },
      { number: 21, text: "Menschensohn, ich habe dem Pharao, dem König von Ägypten, den Arm zerbrochen. Man hat ihn nicht verbunden und nicht geschient und ihm keine Heilung gegönnt, damit er nicht wieder zu Kräften kommt und zum Schwert greift." },
      { number: 22, text: "Darum - so spricht Gott, der Herr: Jetzt gehe ich gegen den Pharao vor, den König von Ägypten: Ich zerbreche ihm beide Arme, den gesunden und den bereits zerbrochenen, und schlage ihm das Schwert aus der Hand." },
      { number: 23, text: "Ich zerstreue die Ägypter unter die Völker und vertreibe sie in alle Länder.", footnotes: ["(23-24) 29,19f"] },
      { number: 24, text: "Ich stärke die Arme des Königs von Babel und gebe ihm mein Schwert in die Hand. Dem Pharao aber zerbreche ich die Arme, sodass er vor ihm stöhnt wie ein tödlich Verletzter." },
      { number: 25, text: "Ich stärke die Arme des Königs von Babel; doch die Arme des Pharao sinken kraftlos herunter. Wenn ich dem König von Babel mein Schwert in die Hand gebe und er es über Ägypten schwingt, dann werden sie erkennen, dass ich der Herr bin." },
      { number: 26, text: "Ich zerstreue die Ägypter unter die Völker und vertreibe sie in alle Länder. Dann werden sie erkennen, dass ich der Herr bin." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Am ersten Tag des dritten Monats im elften Jahr erging das Wort des Herrn an mich:", heading: "Das Gleichnis vom Pharao, dem prächtigen Baum: 31,1-18" },
      { number: 2, text: "Menschensohn, sag zum Pharao, dem König von Ägypten, und zu seinem Gefolge: Wem war deine Größe vergleichbar? /" },
      { number: 3, text: "Auf dem Libanon stand eine [Esche] Zeder. Die Pracht ihrer Äste gab reichlichen Schatten. / Hoch war ihr Wuchs / und in die Wolken ragte ihr Wipfel.", footnotes: ["Esche: Text korr.; H: Assur."] },
      { number: 4, text: "Das Wasser machte sie groß. / Die Flut in der Tiefe ließ sie hoch emporwachsen. Die Tiefe ließ ihre Ströme fließen / rings um den Ort, wo sie gepflanzt war, sie leitete (von dort) ihre Kanäle / zu allen anderen Bäumen des Feldes." },
      { number: 5, text: "So war sie höher gewachsen / als alle anderen Bäume des Feldes. Ihre Zweige wurden sehr zahlreich / und ihre Äste breiteten sich aus wegen des Reichtums an Wasser, / als sie emporwuchs.", footnotes: ["Übersetzung unsicher."] },
      { number: 6, text: "Alle Vögel des Himmels / hatten ihr Nest in den Zweigen. Alle wilden Tiere brachten unter den Ästen ihre Jungen zur Welt. / All die vielen Völker wohnten in ihrem Schatten." },
      { number: 7, text: "Schön war sie in ihrer Größe mit ihrem breiten Geäst; / denn ihre Wurzeln hatten viel Wasser." },
      { number: 8, text: "Keine Zeder im Garten Gottes war ihr vergleichbar. / Keine Zypresse hatte Zweige wie sie, / keine Platane so mächtige Äste. Keiner der Bäume im Garten Gottes / glich ihr an Schönheit." },
      { number: 9, text: "Ja, ich hatte sie herrlich gemacht / mit ihren zahlreichen Zweigen. Voll Eifersucht auf sie waren im Garten Gottes / alle Bäume von Eden.", footnotes: ["Gen 2,8"] },
      { number: 10, text: "Darum - so spricht Gott, der Herr: Weil sie so hoch emporwuchs und mit ihrem Wipfel in die Wolken ragte und wegen ihrer Höhe überheblich wurde,", footnotes: ["Dan 5,20", "sie . . . emporwuchs: Text korr. (3. statt 2. Person); vgl. G."] },
      { number: 11, text: "deshalb liefere ich sie dem mächtigsten Herrscher der Völker aus. Er behandelt sie so, wie sie es in ihrer Schlechtigkeit verdient hat; ich beseitige sie." },
      { number: 12, text: "Fremde, die gewalttätigsten unter den Völkern, werden sie umhauen und hinwerfen. Ihre Zweige fallen auf die Berge und in alle Täler, ihre Äste zerbrechen in allen Schluchten der Erde. Alle Völker der Erde verlassen den Schatten der Zeder und lassen sie liegen." },
      { number: 13, text: "Auf ihren gefällten Stamm setzen sich alle Vögel des Himmels, die wilden Tiere hausen in ihren Zweigen." },
      { number: 14, text: "Darum soll kein Baum mehr am Wasser emporwachsen und mit seinem Wipfel in die Wolken ragen, keiner der Bäume am Wasser soll mehr so mächtig und hoch dastehen. Denn alle werden dem Tod ausgeliefert, sie müssen hinab in die Unterwelt zu den Menschen, die ins Grab gesunken sind." },
      { number: 15, text: "So spricht Gott, der Herr: Wenn die Zeder in die Unterwelt stürzt, dann lasse ich die Flut in der Tiefe versiegen, ich decke sie zu; ich halte ihre Ströme zurück, sodass der Reichtum an Wasser versiegt. Ihretwegen hülle ich den Libanon in Trauer und alle Bäume des Feldes sinken in Ohnmacht." },
      { number: 16, text: "Durch das Getöse ihres Sturzes lasse ich die Völker erzittern, wenn ich den Baum in die Unterwelt stürze, hinab zu denen, die ins Grab gesunken sind. Dann trösten sich in der Unterwelt alle Bäume von Eden, die erlesenen und besten Bäume des Libanon, alle Bäume am Wasser.", footnotes: ["32,17-32; 28,8"] },
      { number: 17, text: "Denn auch sie stürzen zusammen mit ihr in die Unterwelt, hinab zu denen, die vom Schwert erschlagen wurden. Alle aber, die im Schatten der Zeder wohnten, werden unter die Völker zerstreut.", footnotes: ["wohnten, werden . . . zerstreut: Text korr., vgl. G."] },
      { number: 18, text: "Welcher der Bäume von Eden glich dir an Größe und Pracht? Und doch wirst du zusammen mit den Bäumen von Eden in die Unterwelt gestürzt. Dort wirst du mitten unter den Unbeschnittenen liegen, unter denen, die vom Schwert erschlagen wurden. So geht es dem Pharao und seinem ganzen Gefolge - Spruch Gottes, des Herrn." },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Am ersten Tag des zwölften Monats im zwölften Jahr erging das Wort des Herrn an mich:", heading: "Die Totenklage über den Pharao, das gefangene Krokodil: 32,1-16" },
      { number: 2, text: "Menschensohn, stimm die Totenklage an über den Pharao, den König von Ägypten, und sag zu ihm: Löwe der Völker, jetzt bist du verstummt. / Und doch warst du wie ein Krokodil in den Seen, hast die Flüsse aufgepeitscht, / das Wasser mit deinen Füßen verschmutzt / und die Fluten aufgewühlt.", footnotes: ["29,3-5", "Löwe der Völker, jetzt bist du verstummt, andere Übersetzung: Einem Völkerlöwen bist du gleich geworden."] },
      { number: 3, text: "So spricht Gott, der Herr: / Ich werfe über dich mein Netz, ein Heer von vielen Völkern, / die ziehen dich herauf in meinem Schleppnetz.", footnotes: ["12,13"] },
      { number: 4, text: "Dann werfe ich dich aufs Land, / schleudere dich aufs freie Feld. Alle Vögel des Himmels sollen sich auf dich setzen / und ich sättige mit dir alle Tiere der Erde." },
      { number: 5, text: "Ich lege dein Fleisch auf den Bergen aus / und fülle die Täler mit deinem Aas." },
      { number: 6, text: "Ich tränke das Land bis hin zu den Bergen / mit der Flut deines Blutes; / die Schluchten sollen sich damit füllen." },
      { number: 7, text: "Wenn dein Leben erlischt, / will ich den Himmel bedecken / und die Sterne verdüstern. Die Sonne decke ich zu mit Wolken, / der Mond lässt sein Licht nicht mehr leuchten.", footnotes: ["Jes 13,10"] },
      { number: 8, text: "Deinetwegen verdunkle ich / alle die strahlenden Lichter am Himmel und lege Finsternis über dein Land - / Spruch Gottes, des Herrn." },
      { number: 9, text: "Ich versetze viele Völker in Kummer, / wenn ich ihnen und all den Ländern, die du nicht kanntest, / deinen Zusammenbruch melde." },
      { number: 10, text: "Über dich werden viele Völker entsetzt sein, / ihren Königen sträuben sich deinetwegen die Haare, / wenn ich mein Schwert gegen sie schwinge. Sie zittern erregt am Tag deines Sturzes, / jeder hat Angst um sein Leben.", footnotes: ["10a: Wörtlich: Ich bewirke, dass sich viele Völker über dich entsetzen."] },
      { number: 11, text: "Denn so spricht Gott, der Herr: / Das Schwert des Königs von Babel kommt über dich.", footnotes: ["29,19"] },
      { number: 12, text: "Durch das Schwert der Krieger / bringe ich dein Gefolge zu Fall. Sie sind die gewalttätigsten unter den Völkern. / Sie verwüsten den Stolz von Ägypten, / all seine Pracht wird zerstört." },
      { number: 13, text: "All sein Vieh lasse ich sterben / an den großen Gewässern. Keines Menschen Fuß wird das Wasser mehr trüben, / noch werden die Hufe der Tiere es trüben." },
      { number: 14, text: "Dann mache ich die Gewässer klar / und die Flüsse lasse ich dahingleiten wie Öl - / Spruch Gottes, des Herrn." },
      { number: 15, text: "Wenn ich Ägypten zum Ödland mache, / wenn das Land und alles, was darin ist, verödet, wenn ich alle seine Bewohner erschlage, / dann werden sie erkennen, dass ich der Herr bin." },
      { number: 16, text: "Das ist eine Totenklage, die man anstimmen soll; die Töchter der Völker sollen sie anstimmen. Die Totenklage über Ägypten und all seine Pracht sollen sie singen - Spruch Gottes, des Herrn." },
      { number: 17, text: "Am fünfzehnten Tag des ersten Monats im zwölften Jahr erging das Wort des Herrn an mich:", heading: "Die Totenklage über den Pharao und sein Gefolge: 32,17-32", footnotes: ["des ersten: Text korr. nach G."] },
      { number: 18, text: "Menschensohn, jammre über die Pracht Ägyptens und geleite sie hinab in die Unterwelt, sie und die Töchter mächtiger Völker, bring sie zu denen, die ins Grab gesunken sind.", footnotes: ["31,16; Jes 14,9-11"] },
      { number: 19, text: "Wen hast du an Schönheit übertroffen? / Stürz hinab und lieg bei den unbeschnittenen Männern!", footnotes: ["19f: H ist schwer verständlich. Der zweite Teil von V. 20 ist verderbt und gibt keinen Sinn."] },
      { number: 20, text: "Mitten unter die, die das Schwert erschlug, / werden sie fallen . . ." },
      { number: 21, text: "Dann sagen die starken Helden in der Unterwelt über die Ägypter: / Samt ihren Helfern sind sie herabgestürzt und liegen da wie die unbeschnittenen Männer, / wie die, die das Schwert erschlug." },
      { number: 22, text: "Dort liegt Assur und sein ganzes Heer, / ringsum sind seine Gräber. Sie alle hat man erschlagen, / sie fielen unter dem Schwert." },
      { number: 23, text: "Im tiefsten Grund einer Grube / gab man ihm sein Grab. Sein Heer liegt rings um sein Grab. / Sie alle hat man erschlagen, sie fielen unter dem Schwert, / sie, die einst im Land der Lebenden Schrecken verbreitet haben." },
      { number: 24, text: "Dort liegt Elam und all sein Gefolge / rings um sein Grab. Sie alle hat man erschlagen, / sie fielen unter dem Schwert, in die Unterwelt sind sie hinabgestürzt / wie die unbeschnittenen Männer, sie, die einst im Land der Lebenden / ihren Schrecken verbreitet haben. Jetzt aber haben sie ihre Schande zu tragen / zusammen mit denen, die ins Grab gesunken sind." },
      { number: 25, text: "Mitten unter denen, die man erschlug, / hat man ihm und seinem ganzen Gefolge das Lager bereitet. Rings um ihn her sind die Gräber / von all den Unbeschnittenen, / die man mit dem Schwert erschlug. Im Land der Lebenden haben sie Schrecken verbreitet, / jetzt aber haben sie ihre Schande zu tragen, / zusammen mit denen, die ins Grab gesunken sind. Mitten unter Erschlagene legte man sie. /" },
      { number: 26, text: "Dort liegen Meschech und Tubal und all ihr Gefolge. Rings um sie her sind die Gräber / von all den Unbeschnittenen, / die man mit dem Schwert erschlug; / und doch haben sie einst im Land der Lebenden Schrecken verbreitet.", footnotes: ["27,13; 38,2; 39,1"] },
      { number: 27, text: "Aber sie liegen nicht bei den gefallenen Kriegern aus früheren Zeiten, / die mit ihrer Rüstung in die Unterwelt hinabgestiegen sind, denen man das Schwert unter den Kopf / und den Schild auf die Gebeine gelegt hat; / denn die Krieger hatten im Land der Lebenden Schrecken verbreitet.", footnotes: ["aus früheren Zeiten: Text korr. nach den alten Übersetzungen; H: von den Unbeschnittenen. - den Schild: Text korr.; H: ihre Verschuldungen."] },
      { number: 28, text: "Auch du wirst inmitten der Unbeschnittenen zerbrochen / und liegst bei denen, die das Schwert erschlug." },
      { number: 29, text: "Dort liegt Edom, mit seinen Königen / und all seinen Fürsten, die man trotz ihrer Stärke zu denen gelegt hat, / die das Schwert erschlug. Sie liegen da bei den unbeschnittenen Männern, / bei denen, die ins Grab gesunken sind.", footnotes: ["25,12-14"] },
      { number: 30, text: "Dort liegen alle Fürsten des Nordens / und alle Männer von Sidon; / sie stürzten hinab wie alle, die man erschlug. Trotz ihrer Schrecken erregenden Stärke / sind sie zuschanden geworden; bei den Unbeschnittenen liegen sie, / bei denen, die das Schwert erschlug. Jetzt haben sie ihre Schande zu tragen / zusammen mit denen, die ins Grab gesunken sind.", footnotes: ["28,21-23"] },
      { number: 31, text: "Der Pharao wird sie sehen / und er tröstet sich über den Verlust / seines ganzen Gefolges. Jetzt sind sie vom Schwert erschlagen, / der Pharao und seine ganze Streitmacht - / Spruch Gottes, des Herrn." },
      { number: 32, text: "Denn ich habe vor ihm im Land der Lebenden Schrecken verbreitet. / Doch jetzt liegt der Pharao da mit seinem ganzen Gefolge / inmitten der unbeschnittenen Männer, bei denen, die das Schwert erschlug - / Spruch Gottes, des Herrn." },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das Gericht und das neue Heil: 33,1 - 37,28 | Der Prophet als Wächter: 33,1-9" },
      { number: 2, text: "Menschensohn, sprich zu den Söhnen deines Volkes und sag zu ihnen: Wenn ich über ein Land das Schwert kommen lasse und das Volk in dem Land aus seiner Mitte einen Mann wählt und ihn zu seinem Wächter macht" },
      { number: 3, text: "und wenn dieser Wächter das Schwert über das Land kommen sieht, in das Widderhorn bläst und das Volk warnt" },
      { number: 4, text: "und wenn dann jemand den Schall des Horns zwar hört, sich aber nicht warnen lässt, sodass das Schwert kommt und ihn dahinrafft, dann ist er selbst schuld an seinem Tod.", footnotes: ["18,13", "4f: ist er selbst schuld an seinem Tod, wörtlich: wird sein Blut auf seinem Kopf (V. 5: auf ihm) sein."] },
      { number: 5, text: "Denn er hat den Schall des Horns zwar gehört, sich aber nicht warnen lassen; deshalb ist er selbst schuld an seinem Tod. Wenn er sich jedoch warnen lässt, dann hat er sein Leben gerettet." },
      { number: 6, text: "Wenn aber der Wächter das Schwert kommen sieht und nicht in das Widderhorn bläst und das Volk nicht gewarnt wird und wenn das Schwert kommt und irgendeinen dahinrafft, dann wird dieser zwar wegen seiner eigenen Schuld dahingerafft, aber ich fordere für sein Blut Rechenschaft von dem Wächter." },
      { number: 7, text: "Du aber, Menschensohn, ich gebe dich dem Haus Israel als Wächter; wenn du ein Wort aus meinem Mund hörst, musst du sie vor mir warnen.", footnotes: ["(7-9) 3,17-21"] },
      { number: 8, text: "Wenn ich zu einem, der sich schuldig gemacht hat, sage: Du musst sterben!, und wenn du nicht redest und den Schuldigen nicht warnst, um ihn von seinem Weg abzubringen, dann wird der Schuldige seiner Sünde wegen sterben. Von dir aber fordere ich Rechenschaft für sein Blut." },
      { number: 9, text: "Wenn du aber den Schuldigen vor seinem Weg gewarnt hast, damit er umkehrt, und wenn er dennoch auf seinem Weg nicht umkehrt, dann wird er seiner Sünde wegen sterben; du aber hast dein Leben gerettet." },
      { number: 10, text: "Du aber, Menschensohn, sag zum Haus Israel: Ihr behauptet: Unsere Vergehen und unsere Sünden lasten auf uns, wir siechen ihretwegen dahin. Wie sollen wir da am Leben bleiben?", heading: "Die Freiheit zur Umkehr: 33,10-20", footnotes: ["18,21-32"] },
      { number: 11, text: "Sag zu ihnen: So wahr ich lebe - Spruch Gottes, des Herrn -, ich habe kein Gefallen am Tod des Schuldigen, sondern daran, dass er auf seinem Weg umkehrt und am Leben bleibt. Kehrt um, kehrt um auf euren bösen Wegen! Warum wollt ihr sterben, ihr vom Haus Israel?", footnotes: ["18,23.32; Jes 55,7; Joël 2,12f"] },
      { number: 12, text: "Du aber, Menschensohn, sag zu den Söhnen deines Volkes: Den Gerechten wird seine Gerechtigkeit nicht retten, sobald er Böses tut. Und der Schuldige wird durch seine Schuld nicht zu Fall kommen, sobald er sein schuldhaftes Leben aufgibt. Der Gerechte aber kann trotz seiner Gerechtigkeit nicht am Leben bleiben, sobald er sündigt.", footnotes: ["3,20"] },
      { number: 13, text: "Wenn ich zu dem Gerechten sage: Du wirst am Leben bleiben!, er aber im Vertrauen auf seine Gerechtigkeit Unrecht tut, dann wird ihm seine ganze (bisherige) Gerechtigkeit nicht angerechnet. Wegen des Unrechts, das er getan hat, muss er sterben.", footnotes: ["Du wirst am Leben bleiben: Text korr. nach G-Handschriften; vgl. V. 14 (2. statt 3. Person)."] },
      { number: 14, text: "Wenn ich aber zu dem Schuldigen sage: Du musst sterben!, und er gibt sein sündhaftes Leben auf, handelt nach Recht und Gerechtigkeit," },
      { number: 15, text: "gibt (dem Schuldner) das Pfand zurück, ersetzt, was er geraubt hat, richtet sich nach den Gesetzen, die zum Leben führen, und tut kein Unrecht mehr, dann wird er gewiss am Leben bleiben und nicht sterben.", footnotes: ["18,7; 20,11-13; 18,22"] },
      { number: 16, text: "Keine der Sünden, die er früher begangen hat, wird ihm angerechnet. Er hat nach Recht und Gerechtigkeit gehandelt, darum wird er gewiss am Leben bleiben." },
      { number: 17, text: "Die Söhne deines Volkes aber sagen: Das Verhalten des Herrn ist nicht richtig. Dabei ist gerade ihr Verhalten nicht richtig.", footnotes: ["18,25.29"] },
      { number: 18, text: "Wenn der Gerechte seine Gerechtigkeit aufgibt und Unrecht tut, muss er dafür sterben." },
      { number: 19, text: "Und wenn der Schuldige sein sündhaftes Leben aufgibt und nach Recht und Gerechtigkeit handelt, so wird er deswegen am Leben bleiben." },
      { number: 20, text: "Ihr aber sagt: Das Verhalten des Herrn ist nicht richtig. Doch ich werde euch richten, ihr vom Haus Israel, jeden nach seinem Verhalten.", footnotes: ["18,30"] },
      { number: 21, text: "Am fünften Tag des zehnten Monats im elften Jahr nach unserer Verschleppung kam ein Flüchtling aus Jerusalem zu mir und sagte: Die Stadt ist gefallen.", heading: "Die Nachricht von der Eroberung Jerusalems: 33,21-22", footnotes: ["2 Kön 25,2-4", "im elften: Text korr. nach H-Handschriften; vgl. G, S und 2 Kön 25,2; H: im zwölften."] },
      { number: 22, text: "Aber am Abend, bevor der Flüchtling eintraf, hatte sich die Hand des Herrn auf mich gelegt. Ehe am Morgen der Flüchtling kam, öffnete der Herr meinen Mund und mein Mund wurde geöffnet und ich war nicht mehr stumm.", footnotes: ["3,26f; 24,27"] },
      { number: 23, text: "Das Wort des Herrn erging an mich:", heading: "Die Erwartung der nicht Verschleppten: 33,23-29" },
      { number: 24, text: "Menschensohn, die Bewohner der Ruinen im Land Israel sagen: Abraham war nur ein einzelner Mann und bekam doch das ganze Land; wir aber sind viele. Um so mehr ist das Land uns zum Besitz gegeben.", footnotes: ["Jes 51,2"] },
      { number: 25, text: "Deshalb sag zu ihnen: So spricht Gott, der Herr: Ihr esst (das Opferfleisch) mitsamt dem Blut, ihr blickt zu euren Göttern auf und vergießt Blut. Und ihr wollt das Land in Besitz nehmen?", footnotes: ["Lev 17,10-14; 1 Sam 14,32-34"] },
      { number: 26, text: "Ihr verlasst euch auf euer Schwert, begeht Gräueltaten und einer schändet die Frau des andern. Und ihr wollt das Land in Besitz nehmen?", footnotes: ["Lev 18,20; Ez 11,15"] },
      { number: 27, text: "Darum sag zu ihnen: So spricht Gott, der Herr: So wahr ich lebe, wer in den Ruinen ist, fällt unter dem Schwert, wer auf dem freien Feld ist, den werfe ich den wilden Tieren zum Fraß vor, und wer sich in den Burgen und Höhlen aufhält, stirbt an der Pest." },
      { number: 28, text: "Ich mache das Land zur Öde und Wüste; seine stolze Macht hat ein Ende; das Bergland Israels verödet, sodass niemand mehr durchreist." },
      { number: 29, text: "Dann werden sie erkennen, dass ich der Herr bin, wenn ich das Land zur Öde und Wüste mache wegen all der Gräueltaten, die sie begangen haben." },
      { number: 30, text: "Du, Menschensohn, die Söhne deines Volkes reden über dich an den Mauern und Toren der Häuser. Einer sagt zum andern: Komm doch und höre, was für ein Wort vom Herrn ausgeht.", heading: "Die unaufrichtigen Hörer: 33,30-33" },
      { number: 31, text: "Dann kommen sie zu dir wie bei einem Volksauflauf, setzen sich vor dich hin [als mein Volk] und hören deine Worte an, aber sie befolgen sie nicht; denn ihr Mund ist voller Lügen und so handeln sie auch und ihr Herz ist nur auf Gewinn aus.", footnotes: ["Lügen: Text korr. nach G und S."] },
      { number: 32, text: "Du bist für sie wie ein Mann, der mit wohlklingender Stimme von der Liebe singt und dazu schön auf der Harfe spielt. Sie hören deine Worte an, aber befolgen sie nicht.", footnotes: ["wie ein Mann, der . . . singt: Text korr.; H: wie ein Lied."] },
      { number: 33, text: "Wenn das aber kommt (was du sagst) - und es kommt -, dann werden sie erkennen, dass mitten unter ihnen ein Prophet war.", footnotes: ["2,5"] },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Die schlechten Hirten: 34,1-10" },
      { number: 2, text: "Menschensohn, sprich als Prophet gegen die Hirten Israels, sprich als Prophet und sag zu ihnen: So spricht Gott, der Herr: Weh den Hirten Israels, die nur sich selbst weiden. Müssen die Hirten nicht die Herde weiden?", footnotes: ["Jer 23,1-4", "Die bildhafte Rede meint die Führer des Volkes."] },
      { number: 3, text: "Ihr trinkt die Milch, nehmt die Wolle für eure Kleidung und schlachtet die fetten Tiere; aber die Herde führt ihr nicht auf die Weide.", footnotes: ["die Milch: Text korr. nach Vg, G; H: das Fett."] },
      { number: 4, text: "Die schwachen Tiere stärkt ihr nicht, die kranken heilt ihr nicht, die verletzten verbindet ihr nicht, die verscheuchten holt ihr nicht zurück, die verirrten sucht ihr nicht und die starken misshandelt ihr.", footnotes: ["Sach 11,15f; Joh 10,11"] },
      { number: 5, text: "Und weil sie keinen Hirten hatten, zerstreuten sich meine Schafe und wurden eine Beute der wilden Tiere.", footnotes: ["Sach 10,2; Mt 9,36"] },
      { number: 6, text: "Meine Herde irrte auf allen Bergen und Höhen umher und war über das ganze Land verstreut. Doch keiner kümmerte sich um sie; niemand suchte sie." },
      { number: 7, text: "Darum ihr Hirten, hört das Wort des Herrn:" },
      { number: 8, text: "So wahr ich lebe - Spruch Gottes, des Herrn: Weil meine Herde geraubt wurde und weil meine Schafe eine Beute der wilden Tiere wurden - denn sie hatten keinen Hirten - und weil meine Hirten nicht nach meiner Herde fragten, sondern nur sich selbst und nicht meine Herde weideten," },
      { number: 9, text: "darum, ihr Hirten, hört das Wort des Herrn:" },
      { number: 10, text: "So spricht Gott, der Herr: Nun gehe ich gegen die Hirten vor und fordere meine Schafe von ihnen zurück. Ich setze sie ab, sie sollen nicht mehr die Hirten meiner Herde sein. Die Hirten sollen nicht länger nur sich selbst weiden: Ich reiße meine Schafe aus ihrem Rachen, sie sollen nicht länger ihr Fraß sein.", footnotes: ["Jer 23,2"] },
      { number: 11, text: "Denn so spricht Gott, der Herr: Jetzt will ich meine Schafe selber suchen und mich selber um sie kümmern.", heading: "Der gute Hirt: 34,11-22", footnotes: ["Jes 40,11; Joh 10,1-18; Lk 15,4"] },
      { number: 12, text: "Wie ein Hirt sich um die Tiere seiner Herde kümmert an dem Tag, an dem er mitten unter den Schafen ist, die sich verirrt haben, so kümmere ich mich um meine Schafe und hole sie zurück von all den Orten, wohin sie sich am dunklen, düsteren Tag zerstreut haben." },
      { number: 13, text: "Ich führe sie aus den Völkern heraus, ich hole sie aus den Ländern zusammen und bringe sie in ihr Land. Ich führe sie in den Bergen Israels auf die Weide, in den Tälern und an allen bewohnten Orten des Landes." },
      { number: 14, text: "Auf gute Weide will ich sie führen, im Bergland Israels werden ihre Weideplätze sein. Dort sollen sie auf guten Weideplätzen lagern, auf den Bergen Israels sollen sie fette Weide finden.", footnotes: ["Ps 23,2"] },
      { number: 15, text: "Ich werde meine Schafe auf die Weide führen, ich werde sie ruhen lassen - Spruch Gottes, des Herrn." },
      { number: 16, text: "Die verloren gegangenen Tiere will ich suchen, die vertriebenen zurückbringen, die verletzten verbinden, die schwachen kräftigen, die fetten und starken behüten. Ich will ihr Hirt sein und für sie sorgen, wie es recht ist.", footnotes: ["behüten: Text korr. nach G; H: austilgen."] },
      { number: 17, text: "Ihr aber, meine Herde - so spricht Gott, der Herr -, ich sorge für Recht zwischen Schafen und Schafen, zwischen Widdern und Böcken.", footnotes: ["Mt 25,32"] },
      { number: 18, text: "War es euch nicht genug, auf der besten Weide zu weiden? Musstet ihr auch noch euer übriges Weideland mit euren Füßen zertrampeln? War es euch nicht genug, das klare Wasser zu trinken? Musstet ihr den Rest des Wassers mit euren Füßen verschmutzen?" },
      { number: 19, text: "Meine Schafe mussten abweiden, was eure Füße zertrampelt hatten, und trinken, was eure Füße verschmutzt hatten." },
      { number: 20, text: "Darum - so spricht Gott, der Herr, zu euch: Ich selbst sorge für Recht zwischen den fetten und den mageren Schafen." },
      { number: 21, text: "Weil ihr mit eurem breiten Körper und eurer Schulter alle schwachen Tiere zur Seite gedrängt und weil ihr sie mit euren Hörnern weggestoßen habt, bis ihr sie weggetrieben hattet," },
      { number: 22, text: "deshalb will ich meinen Schafen zu Hilfe kommen. Sie sollen nicht länger eure Beute sein; denn ich werde für Recht sorgen zwischen Schafen und Schafen." },
      { number: 23, text: "Ich setze für sie einen einzigen Hirten ein, der sie auf die Weide führt, meinen Knecht David. Er wird sie weiden und er wird ihr Hirt sein.", heading: "Das messianische Reich: 34,23-31", footnotes: ["(23-24) 37,24; Ps 78,70; Jer 30,9; Hos 3,5; Joh 10,14-16"] },
      { number: 24, text: "Ich selbst, der Herr, werde ihr Gott sein und mein Knecht David wird in ihrer Mitte der Fürst sein. Ich, der Herr, habe gesprochen." },
      { number: 25, text: "Ich schließe mit ihnen einen Friedensbund: Ich rotte die wilden Tiere im Land aus. Dann kann man in der Steppe sicher wohnen und in den Wäldern schlafen.", footnotes: ["37,26; Lev 26,6; Hos 2,20"] },
      { number: 26, text: "Ich werde sie und die Umgebung meines Berges segnen. Ich schicke Regen zur rechten Zeit und der Regen wird Segen bringen.", footnotes: ["Lev 26,4; Ps 84,7"] },
      { number: 27, text: "Die Bäume des Feldes werden ihre Früchte tragen und das Land wird seinen Ertrag geben. Sie werden auf ihrem Grund und Boden sicher sein. Wenn ich die Stangen ihres Jochs zerbreche und sie der Gewalt derer entreiße, von denen sie versklavt wurden, werden sie erkennen, dass ich der Herr bin.", footnotes: ["Ps 67,7"] },
      { number: 28, text: "Sie werden nicht länger eine Beute der Völker sein, von den wilden Tieren werden sie nicht gefressen. Sie werden in Sicherheit wohnen und niemand wird sie erschrecken.", footnotes: ["Jer 30,10"] },
      { number: 29, text: "Ich pflanze ihnen einen Garten des Heils. Sie werden in ihrem Land nicht mehr vom Hunger dahingerafft werden und die Schmähungen der Völker müssen sie nicht mehr ertragen.", footnotes: ["36,30", "des Heils: Text korr. nach G und S; H: zum Ruhm."] },
      { number: 30, text: "Sie werden erkennen, dass ich, der Herr, ihr Gott, mit ihnen bin und dass sie, das Haus Israel, mein Volk sind - Spruch Gottes, des Herrn.", footnotes: ["11,20"] },
      { number: 31, text: "Ihr seid meine Schafe, ihr seid die Herde meiner Weide. Ich bin euer Gott - Spruch Gottes, des Herrn.", footnotes: ["Ps 95,7; 100,3"] },
    ] },
    { number: 35, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Der Drohspruch gegen Edom: 35,1-15", footnotes: ["(1-15) 25,12-14"] },
      { number: 2, text: "Menschensohn, richte dein Gesicht auf die Berge von Seïr, tritt als Prophet gegen sie auf" },
      { number: 3, text: "und sag zu ihnen: So spricht Gott, der Herr: Ich gehe gegen dich vor, Bergland von Seïr. / Ich strecke meine Hand gegen dich aus / und mache dich zur Wüste und Öde." },
      { number: 4, text: "Deine Städte lege ich in Trümmer / und du sollst zur Wüste werden. / Dann wirst du erkennen, dass ich der Herr bin." },
      { number: 5, text: "Weil du eine ewige Feindschaft mit den Söhnen Israels hattest und sie zur Zeit ihres Unglücks, zur Zeit der endgültigen Abrechnung, dem Schwert ausgeliefert hast,", footnotes: ["Ps 137,7"] },
      { number: 6, text: "darum lasse ich dich bluten, so wahr ich lebe - Spruch Gottes, des Herrn; Blut soll dich verfolgen. Du hast dich nicht gescheut, Blut zu vergießen; darum soll Blut dich verfolgen.", footnotes: ["lasse ich dich bluten, wörtlich: mache ich dich zu Blut. - Text teilweise korr., weil H verderbt ist."] },
      { number: 7, text: "Ich mache die Berge von Seïr zur Öde und Wüste und vernichte alles, was sich dort bewegt und regt." },
      { number: 8, text: "Ich fülle sein Bergland mit Erschlagenen; auf deinen Hügeln, in deinen Tälern und all deinen Schluchten werden die Menschen vom Schwert erschlagen und fallen." },
      { number: 9, text: "Für immer mache ich dich zum Ödland, / deine Städte sollen nicht mehr bewohnt sein. / Dann wirst du erkennen, dass ich der Herr bin." },
      { number: 10, text: "Weil du sagst: Beide Völker und beide Länder gehören mir; sie sind mein Besitz!, (weil du das sagst) obwohl doch der Herr dort ist,", footnotes: ["Edom, das mit dem «Bergland von Seïr» (V. 3) gemeint ist, beansprucht das Land von Israel und Juda."] },
      { number: 11, text: "darum will ich gegen dich - so wahr ich lebe, Spruch Gottes, des Herrn - mit dem gleichen leidenschaftlichen Zorn vorgehen, mit dem du gegen sie in deinem Hass vorgegangen bist. Ich werde dir zeigen, wer ich bin, wenn ich dich richte.", footnotes: ["dir: Text korr. nach G; H: an ihnen."] },
      { number: 12, text: "Dann wirst du erkennen, dass ich der Herr bin und dass ich gehört habe, wie oft du die Berge Israels geschmäht und gesagt hast: Sie sind völlig verödet. Sie sind uns zum Fraß vorgeworfen." },
      { number: 13, text: "So habt ihr geprahlt und seid frech gegen mich gewesen; ich habe es selbst gehört." },
      { number: 14, text: "So spricht Gott, der Herr: Weil du dich darüber freust, dass das ganze Land verödet daliegt, darum will ich auch dich (zur Wüste) machen:" },
      { number: 15, text: "Weil du dich darüber freust, dass der Erbbesitz des Hauses Israel verödet daliegt, darum will ich auch dich (zur Wüste) machen; das Bergland von Seïr und ganz Edom sollen veröden. Dann wird man erkennen, dass ich der Herr bin." },
    ] },
    { number: 36, verses: [
      { number: 1, text: "Du aber, Menschensohn, sprich als Prophet zu den Bergen Israels und sag: Ihr Berge Israels, hört das Wort des Herrn!", heading: "Die Weissagung über Israels Berge: 36,1-15", footnotes: ["6,2"] },
      { number: 2, text: "So spricht Gott, der Herr: Die Feinde haben von euch gesagt: Ha, ihr seid für immer verödet; jetzt gehört ihr uns.", footnotes: ["25,3", "verödet: Text korr. nach G."] },
      { number: 3, text: "Darum tritt als Prophet auf und sag: So spricht Gott, der Herr: Weil ihr verödet seid, weil man von allen Seiten nach euch geschnappt hat, sodass ihr jetzt Besitz der übrigen Völker seid, weil ihr ins Gerede gekommen und zum Gespött der Leute geworden seid," },
      { number: 4, text: "darum, ihr Berge Israels, hört das Wort Gottes, des Herrn: So spricht Gott, der Herr, zu den Bergen und Hügeln, den Schluchten und Tälern, zu den verfallenen Ruinen und den verlassenen Städten: Weil ihr von den übrigen Völkern ringsum ausgeplündert und verspottet worden seid," },
      { number: 5, text: "darum - so spricht Gott, der Herr - werde ich mit glühender Leidenschaft über die übrigen Völker und über ganz Edom reden. Voll Frohlocken haben sie sich mein Land angeeignet. Voll Schadenfreude haben sie das Weideland erbeutet.", footnotes: ["35,15"] },
      { number: 6, text: "Darum sprich als Prophet zum Land Israel und sag zu den Bergen und Hügeln, zu den Schluchten und Tälern: So spricht Gott, der Herr: Ich rede voll Leidenschaft und Grimm, weil ihr von den Völkern so viel Schimpf ertragen müsst." },
      { number: 7, text: "Darum - so spricht Gott, der Herr: Ich erhebe meine Hand (zum Schwur) und sage: Die Völker rings um euch werden den gleichen Schimpf ertragen müssen." },
      { number: 8, text: "Ihr aber, ihr Berge Israels, sollt wieder grün werden und Früchte hervorbringen für mein Volk Israel; denn es wird bald zurückkommen." },
      { number: 9, text: "Seht, ich wende mich euch wieder zu und dann ackert und sät man wieder auf euch" },
      { number: 10, text: "und ich lasse viele Menschen dort leben, das ganze Haus Israel. Die Städte werden wieder bewohnt sein und die Ruinen aufgebaut.", footnotes: ["Ps 69,36"] },
      { number: 11, text: "Ich lasse viele Menschen und Tiere auf euch leben und sie werden sich vermehren und fruchtbar sein. Ich will dafür sorgen, dass ihr wieder bewohnt seid, wie ihr es früher wart; ich will euch mehr Gutes erweisen als je zuvor. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 12, text: "Viele Menschen, mein ganzes Volk Israel, lasse ich zu euch kommen und sie werden euch in Besitz nehmen; ihr werdet für immer ihr Erbbesitz sein und ihnen nie mehr ihre Kinder wegnehmen." },
      { number: 13, text: "So spricht Gott, der Herr: Man sagt zu dir: Du bist eine Menschenfresserin und hast deinem eigenen Volk die Kinder weggenommen.", footnotes: ["13f: Das Land wird mit einem wilden Tier verglichen (vgl. Num 13,32). Vielleicht ist an den Untergang Israels 722 v. Chr. und Judas 586 v. Chr. gedacht, als bei der Eroberung des Landes auch zum Teil das Volk umkam."] },
      { number: 14, text: "Aber du sollst keine Menschen mehr fressen und deinem eigenen Volk nicht mehr die Kinder wegnehmen - Spruch Gottes, des Herrn." },
      { number: 15, text: "Du sollst nie mehr anhören müssen, wie die Völker dich beschimpfen, und nie mehr ertragen müssen, dass die Nationen dich verhöhnen. Du wirst deinem eigenen Volk nicht mehr die Kinder wegnehmen - Spruch Gottes, des Herrn." },
      { number: 16, text: "Das Wort des Herrn erging an mich:", heading: "Die Verheißung eines neuen Lebens: 36,16-38" },
      { number: 17, text: "(Hör zu,) Menschensohn! Als Israel in seinem Land wohnte, machten sie das Land durch ihr Verhalten und ihre Taten unrein. Wie die monatliche Unreinheit der Frau war ihr Verhalten in meinen Augen.", footnotes: ["Lev 15,19-27"] },
      { number: 18, text: "Da goss ich meinen Zorn über sie aus, weil sie Blut vergossen im Land und das Land mit ihren Götzen befleckten." },
      { number: 19, text: "Ich zerstreute sie unter die Völker; in alle Länder wurden sie vertrieben. Nach ihrem Verhalten und nach ihren Taten habe ich sie gerichtet.", footnotes: ["11,16"] },
      { number: 20, text: "Als sie aber zu den Völkern kamen, entweihten sie überall, wohin sie kamen, meinen heiligen Namen; denn man sagte von ihnen: Das ist das Volk Jahwes und doch mussten sie sein Land verlassen.", footnotes: ["20,39; Jes 52,5"] },
      { number: 21, text: "Da tat mir mein heiliger Name leid, den das Haus Israel bei den Völkern entweihte, wohin es auch kam.", footnotes: ["20,9"] },
      { number: 22, text: "Darum sag zum Haus Israel: So spricht Gott, der Herr: Nicht euretwegen handle ich, Haus Israel, sondern um meines heiligen Namens willen, den ihr bei den Völkern entweiht habt, wohin ihr auch gekommen seid.", footnotes: ["Jes 48,11; Ps 79,9; 106,8; 115,1; Jer 14,7"] },
      { number: 23, text: "Meinen großen, bei den Völkern entweihten Namen, den ihr mitten unter ihnen entweiht habt, werde ich wieder heiligen. Und die Völker - Spruch Gottes, des Herrn - werden erkennen, dass ich der Herr bin, wenn ich mich an euch vor ihren Augen als heilig erweise.", footnotes: ["Mt 6,9"] },
      { number: 24, text: "Ich hole euch heraus aus den Völkern, ich sammle euch aus allen Ländern und bringe euch in euer Land.", footnotes: ["11,17"] },
      { number: 25, text: "Ich gieße reines Wasser über euch aus, dann werdet ihr rein. Ich reinige euch von aller Unreinheit und von allen euren Götzen.", footnotes: ["Ps 51,9; Sach 13,1; Jer 33,8"] },
      { number: 26, text: "Ich schenke euch ein neues Herz und lege einen neuen Geist in euch. Ich nehme das Herz von Stein aus eurer Brust und gebe euch ein Herz von Fleisch.", footnotes: ["11,19f; 39,29"] },
      { number: 27, text: "Ich lege meinen Geist in euch und bewirke, dass ihr meinen Gesetzen folgt und auf meine Gebote achtet und sie erfüllt.", footnotes: ["11,20; 37,24"] },
      { number: 28, text: "Dann werdet ihr in dem Land wohnen, das ich euren Vätern gab. Ihr werdet mein Volk sein und ich werde euer Gott sein." },
      { number: 29, text: "Ich befreie euch von allem, womit ihr euch unrein gemacht habt. Ich rufe dem Getreide zu und befehle ihm zu wachsen. Ich verhänge über euch keine Hungersnot mehr." },
      { number: 30, text: "Ich vermehre die Früchte der Bäume und den Ertrag des Feldes, damit ihr nicht mehr unter den Völkern die Schande einer Hungersnot ertragen müsst.", footnotes: ["34,29; Joël 2,19"] },
      { number: 31, text: "Dann werdet ihr an euer verkehrtes Verhalten und an eure bösen Taten denken und es wird euch ekeln vor euch selbst wegen eurer Gräueltaten.", footnotes: ["16,61-63; 20,43"] },
      { number: 32, text: "Doch nicht euretwegen handle ich so - Spruch Gottes, des Herrn -, das sollt ihr wissen. Errötet und vergeht vor Scham wegen eures Treibens, ihr vom Haus Israel." },
      { number: 33, text: "So spricht Gott, der Herr: Wenn ich euch von all euren Sünden gereinigt habe, mache ich die Städte wieder bewohnbar und die Ruinen werden wieder aufgebaut." },
      { number: 34, text: "Das verödete Land wird bestellt, es liegt nicht mehr öde vor den Augen all derer, die vorübergehen." },
      { number: 35, text: "Dann wird man sagen: Dieses verödete Land ist wie der Garten Eden geworden; die zerstörten, verödeten, vernichteten Städte sind wieder befestigt und bewohnt." },
      { number: 36, text: "Dann werden die Völker, die rings um euch noch übrig sind, erkennen, dass ich, der Herr, das Zerstörte wieder aufgebaut und das Ödland wieder bepflanzt habe. Ich, der Herr, habe gesprochen und ich führe es aus.", footnotes: ["Jer 1,10"] },
      { number: 37, text: "So spricht Gott, der Herr: Ich lasse mich vom Haus Israel dazu bewegen, auch noch das zu tun: Ich werde die Menschen vermehren wie eine Schafherde." },
      { number: 38, text: "Wie die zum Opfer geweihten Schafe, wie die Schafe an den Festen Jerusalem füllen, so sollen Herden von Menschen die zerstörten Städte bevölkern. Dann wird man erkennen, dass ich der Herr bin.", footnotes: ["Mi 2,12"] },
    ] },
    { number: 37, verses: [
      { number: 1, text: "Die Hand des Herrn legte sich auf mich und der Herr brachte mich im Geist hinaus und versetzte mich mitten in die Ebene. Sie war voll von Gebeinen.", heading: "Die Vision von der Auferweckung Israels: 37,1-14", footnotes: ["1,3; 3,22"] },
      { number: 2, text: "Er führte mich ringsum an ihnen vorüber und ich sah sehr viele über die Ebene verstreut liegen; sie waren ganz ausgetrocknet." },
      { number: 3, text: "Er fragte mich: Menschensohn, können diese Gebeine wieder lebendig werden? Ich antwortete: Herr und Gott, das weißt nur du." },
      { number: 4, text: "Da sagte er zu mir: Sprich als Prophet über diese Gebeine und sag zu ihnen: Ihr ausgetrockneten Gebeine, hört das Wort des Herrn!" },
      { number: 5, text: "So spricht Gott, der Herr, zu diesen Gebeinen: Ich selbst bringe Geist in euch, dann werdet ihr lebendig.", footnotes: ["Jes 26,19"] },
      { number: 6, text: "Ich spanne Sehnen über euch und umgebe euch mit Fleisch; ich überziehe euch mit Haut und bringe Geist in euch, dann werdet ihr lebendig. Dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 7, text: "Da sprach ich als Prophet, wie mir befohlen war; und noch während ich redete, hörte ich auf einmal ein Geräusch: Die Gebeine rückten zusammen, Bein an Bein." },
      { number: 8, text: "Und als ich hinsah, waren plötzlich Sehnen auf ihnen und Fleisch umgab sie und Haut überzog sie. Aber es war noch kein Geist in ihnen." },
      { number: 9, text: "Da sagte er zu mir: Rede als Prophet zum Geist, rede, Menschensohn, sag zum Geist: So spricht Gott, der Herr: Geist, komm herbei von den vier Winden! Hauch diese Erschlagenen an, damit sie lebendig werden." },
      { number: 10, text: "Da sprach ich als Prophet, wie er mir befohlen hatte, und es kam Geist in sie. Sie wurden lebendig und standen auf - ein großes, gewaltiges Heer.", footnotes: ["Gen 2,7; Ps 104,30"] },
      { number: 11, text: "Er sagte zu mir: Menschensohn, diese Gebeine sind das ganze Haus Israel. Jetzt sagt Israel: Ausgetrocknet sind unsere Gebeine, unsere Hoffnung ist untergegangen, wir sind verloren." },
      { number: 12, text: "Deshalb tritt als Prophet auf und sag zu ihnen: So spricht Gott, der Herr: Ich öffne eure Gräber und hole euch, mein Volk, aus euren Gräbern herauf. Ich bringe euch zurück in das Land Israel." },
      { number: 13, text: "Wenn ich eure Gräber öffne und euch, mein Volk, aus euren Gräbern heraufhole, dann werdet ihr erkennen, dass ich der Herr bin." },
      { number: 14, text: "Ich hauche euch meinen Geist ein, dann werdet ihr lebendig und ich bringe euch wieder in euer Land. Dann werdet ihr erkennen, dass ich der Herr bin. Ich habe gesprochen und ich führe es aus - Spruch des Herrn.", footnotes: ["39,29"] },
      { number: 15, text: "Das Wort des Herrn erging an mich:", heading: "Eine weitere symbolische Handlung: Die Wiedervereinigung Israels und Judas: 37,15-28" },
      { number: 16, text: "Du, Menschensohn, nimm dir ein Holz und schreib darauf: Juda und die mit ihm verbündeten Israeliten. Dann nimm dir ein anderes Holz, und schreib darauf: Josef [Holz Efraims] und das ganze mit ihm verbündete Haus Israel." },
      { number: 17, text: "Dann füge beide zu einem einzigen Holz zusammen, sodass sie eins werden in deiner Hand." },
      { number: 18, text: "Und wenn die Söhne deines Volkes dich fragen: Willst du uns nicht erklären, was das bedeuten soll?," },
      { number: 19, text: "dann antworte ihnen: So spricht Gott, der Herr: Ich nehme das Holz Josefs [das in der Hand Efraims ist] und der mit ihm verbündeten Stämme Israels und lege es auf das Holz Judas. Ich mache sie zu einem einzigen Holz und sie werden eins in meiner Hand." },
      { number: 20, text: "Die Hölzer, auf die du geschrieben hast, sollst du vor ihren Augen in deiner Hand halten." },
      { number: 21, text: "Dann sag zu ihnen: So spricht Gott, der Herr: Ich hole die Israeliten aus den Völkern heraus, zu denen sie gehen mussten; ich sammle sie von allen Seiten und bringe sie in ihr Land.", footnotes: ["34,13; 36,24"] },
      { number: 22, text: "Ich mache sie in meinem Land, auf den Bergen Israels, zu einem einzigen Volk. Sie sollen alle einen einzigen König haben. Sie werden nicht länger zwei Völker sein und sich nie mehr in zwei Reiche teilen.", footnotes: ["Jes 11,11.13; Ez 34,23", "in meinem Land: ergänzt nach G."] },
      { number: 23, text: "Sie werden sich nicht mehr unrein machen durch ihre Götzen und Gräuel und durch all ihre Untaten. Ich befreie sie von aller Sünde, die sie in ihrer Untreue begangen haben, und ich mache sie rein. Dann werden sie mein Volk sein und ich werde ihr Gott sein.", footnotes: ["11,20", "Sünde: Text korr.; vgl. G."] },
      { number: 24, text: "Mein Knecht David wird ihr König sein und sie werden alle einen einzigen Hirten haben. Sie werden nach meinen Rechtsvorschriften leben und auf meine Gesetze achten und sie erfüllen." },
      { number: 25, text: "Sie werden in dem Land wohnen, das ich meinem Knecht Jakob gegeben habe und in dem ihre Väter gewohnt haben. Sie und ihre Kinder und Kindeskinder werden für immer darin wohnen und mein Knecht David wird für alle Zeit ihr Fürst sein.", footnotes: ["28,25", "ihre Väter: Text korr. nach G und S; H: eure Väter."] },
      { number: 26, text: "Ich schließe mit ihnen einen Friedensbund; es soll ein ewiger Bund sein. Ich werde sie zahlreich machen. Ich werde mitten unter ihnen für immer mein Heiligtum errichten", footnotes: ["Jes 54,10", "Text korr.; H hat zwei weitere Wörter, die kaum ursprünglich sind."] },
      { number: 27, text: "und bei ihnen wird meine Wohnung sein. Ich werde ihr Gott sein und sie werden mein Volk sein.", footnotes: ["43,7; 11,20; Lev 26,11f"] },
      { number: 28, text: "Wenn mein Heiligtum für alle Zeit in ihrer Mitte ist, dann werden die Völker erkennen, dass ich der Herr bin, der Israel heiligt.", footnotes: ["36,23"] },
    ] },
    { number: 38, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Der Kampf Gottes gegen Gog: 38,1 - 39,29 | Die Kriegsvorbereitungen Gogs: 38,1-16", footnotes: ["1-39,29: Die Prophetie über «Gog im Land Magog», hinter dessen Gestalt sich vielleicht der Name des mächtigen lydischen Königs Gyges verbirgt, ist eine mit apokalyptischen Zügen ausgestattete Schilderung des Feinds «aus dem Norden» (38,15)."] },
      { number: 2, text: "Menschensohn, richte dein Gesicht auf Gog im Land Magog, auf den Großfürsten von Meschech und Tubal, tritt als Prophet gegen ihn auf", footnotes: ["39,1; 27,13; 32,26"] },
      { number: 3, text: "und sag: So spricht Gott, der Herr: Ich will gegen dich vorgehen, Gog, gegen dich, den Großfürsten von Meschech und Tubal.", footnotes: ["39,1; Gen 10,2"] },
      { number: 4, text: "Ich reiße dich herum und schlage dir Haken durch deine Kinnbacken und führe dich und deine ganze Streitmacht heraus: Pferde und Reiter, alle prächtig gekleidet, ein großes Heer, mit Langschild und Rundschild; alle haben ihr Schwert in der Hand.", footnotes: ["29,4"] },
      { number: 5, text: "Auch Paras, Kusch und Put sind dabei, alle ausgerüstet mit Schild und Helm, dazu" },
      { number: 6, text: "Gomer und all seine Truppen, Bet-Togarma aus dem äußersten Norden und all seine Truppen. Viele Völker ziehen mit dir.", footnotes: ["27,14"] },
      { number: 7, text: "Rüste dich, halte dich bereit, du und dein ganzes Heer, das bei dir versammelt ist; sei mit ihnen auf dem Posten." },
      { number: 8, text: "Nach langer Zeit wirst du deinen Auftrag erhalten. Nach vielen Jahren wirst du in ein Land kommen, dessen Volk dem Schwert entronnen ist und aus vielen Völkern wieder auf den Bergen Israels zusammengeführt wurde, die lange verödet waren. Mitten aus den Völkern wurde es herausgeführt und alle leben in Sicherheit." },
      { number: 9, text: "Wie ein Unwetter ziehst du herauf; wie eine Wolke, die das ganze Land bedeckt - du und all deine Truppen und viele Völker mit dir." },
      { number: 10, text: "So spricht Gott, der Herr: An jenem Tag steigen (unheilvolle) Gedanken in deinem Herzen auf und du ersinnst einen bösen Plan." },
      { number: 11, text: "Du sagst: Ich will gegen das ungeschützte Land hinaufziehen und die friedlichen Menschen überfallen, die dort in Sicherheit leben; alle leben sie ohne Mauern und sie haben keine Riegel und Tore." },
      { number: 12, text: "Ich will Beute machen und plündern, ich will diese wieder bewohnten Trümmer angreifen und das Volk überfallen, das aus den Völkern zusammengeführt wurde, das sich wieder Herden und Besitz erworben hat und jetzt auf dem Nabel der Erde wohnt." },
      { number: 13, text: "Dann werden dich Saba und Dedan und die Kaufleute und Händler von Tarschisch fragen: Kommst du, um Beute zu machen? Hast du deine Heere versammelt, um zu plündern, um Silber und Gold wegzuschleppen, Herden und Besitz zu rauben und große Beute zu machen?", footnotes: ["25,13", "Händler: Text korr. H: alle seine Junglöwen."] },
      { number: 14, text: "Darum tritt als Prophet auf, Menschensohn, und sag zu Gog: So spricht Gott, der Herr: Dann, wenn mein Volk Israel sich in Sicherheit wähnt, brichst du auf", footnotes: ["brichst du auf: Text korr. nach G; H: wirst du es wissen."] },
      { number: 15, text: "und ziehst aus deinem Land heran, aus dem äußersten Norden, du und viele Völker mit dir, alle zu Pferd, ein großes Heer, eine gewaltige Streitmacht." },
      { number: 16, text: "Du ziehst gegen mein Volk Israel heran wie eine Wolke, die das ganze Land bedeckt. Am Ende der Tage wird es geschehen: Ich lasse dich gegen mein Land heranziehen; denn die Völker sollen mich erkennen, wenn ich mich vor ihren Augen an dir, Gog, als heilig erweise.", footnotes: ["Ex 14,4"] },
      { number: 17, text: "So spricht Gott, der Herr: Du bist der, von dem ich in früherer Zeit durch meine Knechte gesprochen habe, durch die Propheten Israels, die in jenen Tagen und Jahren weissagten, dass ich dich gegen Israel heranziehen lasse.", heading: "Der Drohspruch gegen Gog: 38,17-23", footnotes: ["Du bist der: Text korr. nach G und Vg; H formuliert als Fragesatz."] },
      { number: 18, text: "Und an jenem Tag, wenn Gog gegen das Land Israel heranzieht - Spruch Gottes, des Herrn -, wird der Groll in mir aufsteigen." },
      { number: 19, text: "In meinem leidenschaftlichen Eifer, im Feuer meines Zorns, schwöre ich: An jenem Tag wird es im ganzen Land Israel ein gewaltiges Erdbeben geben." },
      { number: 20, text: "Dann zittern die Fische im Meer und die Vögel am Himmel vor mir, das Wild auf dem Feld und alle kleinen Tiere, die auf dem Erdboden kriechen, und alle Menschen auf Erden. Es bersten die Berge, die Felswände stürzen ein und alle Mauern fallen zu Boden." },
      { number: 21, text: "Dann rufe ich mein ganzes Bergland zum Krieg gegen Gog auf - Spruch Gottes, des Herrn. Da wird sich das Schwert des einen gegen den andern wenden.", footnotes: ["Offb 20,8f"] },
      { number: 22, text: "Ich richte ihn durch Pest und (Ströme von) Blut; ich lasse Wolkenbrüche und Hagel, Feuer und Schwefel über ihn und seine Truppen und über die vielen Völker, die bei ihm sind, herabregnen." },
      { number: 23, text: "So werde ich mich als groß und heilig erweisen und mich vor den Augen vieler Völker zu erkennen geben. Dann werden sie erkennen, dass ich der Herr bin." },
    ] },
    { number: 39, verses: [
      { number: 1, text: "Du, Menschensohn, tritt als Prophet gegen Gog auf und sag: So spricht Gott, der Herr: Ich will gegen dich vorgehen, Gog, gegen dich, den Großfürsten von Meschech und Tubal.", heading: "Der Untergang Gogs: 39,1-22", footnotes: ["38,2; 32,26; 38,3f"] },
      { number: 2, text: "Ich führe dich und locke dich herbei, ich lasse dich aus dem äußersten Norden heranziehen und führe dich ins Bergland von Israel." },
      { number: 3, text: "Dann schlage ich dir den Bogen aus der linken Hand und lasse deiner rechten die Pfeile entfallen." },
      { number: 4, text: "Im Bergland von Israel wirst du umkommen, du und alle deine Truppen und die Völker, die bei dir sind. Raubvögeln aller Art und den wilden Tieren werfe ich dich zum Fraß vor." },
      { number: 5, text: "Auf dem freien Feld wirst du fallen; denn ich habe gesprochen Spruch Gottes, des Herrn." },
      { number: 6, text: "Gegen Magog und die sorglosen Küstenbewohner sende ich Feuer. Dann werden sie erkennen, dass ich der Herr bin." },
      { number: 7, text: "Meinen heiligen Namen offenbare ich in meinem Volk Israel; ich will meinen heiligen Namen nie mehr entweihen. Dann werden die Völker erkennen, dass ich der Herr bin, heilig in Israel." },
      { number: 8, text: "Es kommt und geschieht - Spruch Gottes, des Herrn. Das ist der Tag, von dem ich gesprochen habe:" },
      { number: 9, text: "Die Einwohner der Städte Israels werden hinausgehen; mit den Waffen, mit Langschild und Rundschild, mit Bogen und Pfeilen, mit Keulen und Lanzen machen sie Feuer und heizen. Sieben Jahre lang machen sie Feuer damit.", footnotes: ["Ps 46,10"] },
      { number: 10, text: "Sie sammeln kein Holz auf den Feldern, sie schlagen kein Holz im Wald, sondern verbrennen die Waffen. Sie plündern die Plünderer und berauben die Räuber - Spruch Gottes, des Herrn." },
      { number: 11, text: "An jenem Tag weise ich Gog einen Platz für sein Grab in Israel zu: das Tal der Durchreisenden, östlich vom (Toten) Meer. Das Grab versperrt dann den Durchreisenden den Weg. Dort wird man Gog und all seine Pracht begraben und den Ort «Tal der Pracht Gogs» nennen." },
      { number: 12, text: "Sieben Monate wird das Haus Israel brauchen, um alle zu begraben, um das Land wieder rein zu machen.", footnotes: ["Tote, die nicht begraben sind, machen das Land unrein (vgl. Num 19,11-22)."] },
      { number: 13, text: "Das ganze Volk im Land wird helfen, sie zu begraben, und das wird ihm großes Ansehen verschaffen an dem Tag, an dem ich mich als herrlich erweise - Spruch Gottes, des Herrn." },
      { number: 14, text: "Man wird Männer auswählen, die ständig durch das Land ziehen und alle bestatten sollen, die im Land verstreut liegen geblieben sind. So soll man das Land wieder rein machen. Und am Ende der sieben Monate sollen sie das Land gründlich durchsuchen.", footnotes: ["Num 19,16; Dtn 21,23"] },
      { number: 15, text: "Wenn diese Männer durch das Land ziehen und einer von ihnen einen Menschenknochen findet, soll er die Stelle kennzeichnen, bis die Totengräber ihn im Tal der Pracht Gogs begraben." },
      { number: 16, text: "[Auch eine Stadt trägt den Namen Hamona, «Die Pracht».] So wird man das Land rein machen.", footnotes: ["Der Zusatz nimmt aus V. 11 das Stichwort «Pracht» auf."] },
      { number: 17, text: "Du aber, Menschensohn - so spricht Gott, der Herr -, sag zu allen Vögeln und zu allen wilden Tieren: Versammelt euch und kommt her! Von überall kommt zu meinem großen Opfer zusammen, das ich für euch schlachte, zu meinem großen Opfer auf den Bergen Israels, kommt und fresst Fleisch und trinkt Blut!", footnotes: ["Offb 19,17-19"] },
      { number: 18, text: "Das Fleisch der Helden sollt ihr fressen, das Blut der Fürsten der Erde sollt ihr trinken. Lauter Widder, Lämmer und Böcke, Stiere und gemästete Rinder aus dem Baschan sind es." },
      { number: 19, text: "Fresst euch satt am Fett und berauscht euch am Blut meines Opfers, das ich für euch geschlachtet habe." },
      { number: 20, text: "An meinem Tisch könnt ihr euch sättigen mit Pferden und Reitern, mit Helden und Kriegern aller Art - Spruch Gottes, des Herrn.", footnotes: ["Reitern: Text korr. nach G."] },
      { number: 21, text: "So zeige ich unter den Völkern meine Herrlichkeit. Alle Völker sehen, wie ich mein Strafgericht abhalte, sie sehen, wie ich meine Hand auf sie lege.", footnotes: ["Ex 14,4"] },
      { number: 22, text: "Dann wird das Haus Israel erkennen, dass ich der Herr, sein Gott, bin, von jenem Tag an und auch weiterhin." },
      { number: 23, text: "Die Völker werden erkennen, dass das Haus Israel wegen seiner Schuld verschleppt wurde. Denn sie sind mir untreu geworden; darum habe ich mein Angesicht vor ihnen verborgen und sie ihren Feinden ausgeliefert, sodass alle unter dem Schwert fielen.", heading: "Die Wiederherstellung Israels: 39,23-29" },
      { number: 24, text: "Wie ihre Unreinheit und ihre Vergehen es verdienten, so habe ich sie behandelt und mein Angesicht vor ihnen verborgen." },
      { number: 25, text: "Darum - so spricht Gott, der Herr: Jetzt werde ich das Geschick Jakobs wenden, ich will Erbarmen haben mit dem ganzen Haus Israel und will mit leidenschaftlichem Eifer für meinen heiligen Namen eintreten.", footnotes: ["Ps 106,8"] },
      { number: 26, text: "Sie werden ihre Schande vergessen, vergessen, wie oft sie mir untreu waren, wenn sie auf ihrem Grund und Boden in Sicherheit leben, ohne dass sie jemand erschreckt," },
      { number: 27, text: "wenn ich sie aus den Völkern zurückgeholt und aus den Ländern ihrer Feinde gesammelt habe und so mich an ihnen vor den Augen vieler Völker als heilig erweise." },
      { number: 28, text: "Dann werden sie erkennen, dass ich, der Herr, ihr Gott bin; denn nachdem ich sie zu den Völkern weggeführt hatte, habe ich sie wieder in ihrem Land versammelt und keinen von ihnen in der Fremde zurückgelassen." },
      { number: 29, text: "Ich verberge mein Gesicht nicht mehr vor ihnen; denn ich habe meinen Geist über das Haus Israel ausgegossen - Wort Gottes, des Herrn.", footnotes: ["Jes 54,8; Ez 36,26; Joël 3,1"] },
    ] },
    { number: 40, verses: [
      { number: 1, text: "Am zehnten Tag des Monats am Jahresanfang im fünfundzwanzigsten Jahr nach unserer Verschleppung und im vierzehnten Jahr nach der Eroberung der Stadt (Jerusalem), genau an diesem Tag legte sich die Hand des Herrn auf mich und er brachte mich dorthin.", heading: "Die Vision vom neuen Israel: 40,1 - 48,35 | Der neue Tempel: 40,1 - 44,3 | Die Entrückung des Propheten: 40,1-4", footnotes: ["1,3; 37,1", "1-44,3: Die Beschreibung des Tempels ist knapp, oft nicht recht klar, der Text vielfach schwierig und bisweilen verderbt. Die Übersetzung versucht eine durchschaubare Darstellung zu geben."] },
      { number: 2, text: "In göttlichen Visionen brachte er mich ins Land Israel und stellte mich auf einen sehr hohen Berg. In südlicher Richtung war auf dem Berg etwas wie eine Stadt erbaut." },
      { number: 3, text: "Dorthin brachte er mich. Da sah ich einen Mann, der aussah, als sei er aus Bronze. Er hatte eine leinene Schnur und eine Messlatte in der Hand und stand im Tor." },
      { number: 4, text: "Der Mann sagte zu mir: Menschensohn, öffne deine Augen und Ohren, sieh und höre und achte auf alles, was ich dir zeige. Denn du bist hierher gebracht worden, damit ich es dir zeige. Berichte alles, was du siehst, dem Haus Israel." },
      { number: 5, text: "Da stand eine Mauer, die den Tempel ringsum außen umgab. Der Mann hatte in der Hand eine Messlatte von sechs Ellen, die je eine gewöhnliche Elle und eine Handbreit maßen. Und er maß die Dicke der Mauer - eine Latte - und die Höhe - eine Latte.", heading: "Der Tempel, seine Tore und Höfe: 40,5 - 41,4", footnotes: ["(5-42,20) 1 Kön 7", "Die Messung erfolgt mit der größeren, königlichen Elle."] },
      { number: 6, text: "Dann ging er zum Osttor, stieg seine Stufen hinauf und maß die Schwellen des Tores: Sie waren eine Latte breit [jede Schwelle war eine Latte breit]." },
      { number: 7, text: "Dann maß er die Torkammer: Sie war eine Latte lang und eine Latte breit. Der Abstand zwischen den Torkammern betrug fünf Ellen. Und er maß die Schwelle des Tors auf der Tempelseite der Vorhalle des Tors." },
      { number: 8, text: "[Er maß die Vorhalle des Tors auf der Tempelseite - eine Latte.]" },
      { number: 9, text: "Dann maß er die Vorhalle des Tors - acht Ellen - sowie die Pfeiler des Tors - zwei Ellen. Die Vorhalle des Tors lag auf der Tempelseite des Tors." },
      { number: 10, text: "Drei Torkammern des Osttors lagen auf der einen Seite, drei auf der andern; alle drei hatten jeweils die gleichen Maße und auch die Pfeiler hüben und drüben hatten die gleichen Maße." },
      { number: 11, text: "Dann maß er die Breite der Toröffnung - zehn Ellen - und die Länge des Torgebäudes - dreizehn Ellen." },
      { number: 12, text: "Vor allen Torkammern war eine Schranke, jeweils eine Elle hoch. Die Torkammern auf beiden Seiten maßen jeweils sechs Ellen." },
      { number: 13, text: "Dann maß er die Breite des Torbaus vom Dachansatz einer Torkammer bis zum Dachansatz der gegenüberliegenden Torkammer von Tür zu Tür fünfundzwanzig Ellen." },
      { number: 14, text: "[Dann maß er die Vorhalle - zwanzig Ellen. An das Tor stieß ringsum der Vorhof.]", footnotes: ["H verderbt; übersetzt nach G; Sinnzusammenhang unklar."] },
      { number: 15, text: "Von der Vorderseite des Toreingangs bis zur Tempelseite der Vorhalle des Tors innen waren es fünfzig Ellen." },
      { number: 16, text: "Die Torkammern ringsum hatten verschließbare Fenster, ebenso innen die Pfeiler und die Vorhalle. Ringsum befanden sich innen Fenster. Die Pfeiler waren mit Palmen verziert." },
      { number: 17, text: "Dann brachte er mich in den Vorhof. Rings um den Vorhof lagen Räume und davor war ringsum ein Steinpflaster angelegt; dreißig Räume grenzten an das Pflaster." },
      { number: 18, text: "Das Steinpflaster schloss sich seitlich an die Tore an und es war genauso breit wie der Vorsprung der Tore (in den Vorhof). Das war das untere Steinpflaster." },
      { number: 19, text: "Dann maß er den Abstand von der Innenseite der Außentore zur Außenseite der Innentore im Osten und Norden - je hundert Ellen.", footnotes: ["Übersetzt nach G."] },
      { number: 20, text: "Auch das Nordtor des Vorhofs maß er der Länge und Breite nach." },
      { number: 21, text: "Es hatte je drei Torkammern auf beiden Seiten. Seine Pfeiler und seine Torkammern hatten die gleichen Maße wie die des ersten Tors. Im Ganzen war es fünfzig Ellen lang und fünfundzwanzig Ellen breit." },
      { number: 22, text: "Seine Fenster, seine Vorhalle und sein Palmenschmuck hatten die gleichen Maße wie die des Osttors. Man musste sieben Stufen zu ihm hinaufsteigen. Auf der Innenseite lag seine Vorhalle.", footnotes: ["22.26: Auf der Innenseite: Text korr. nach G; H: Nach ihrer Vorderseite."] },
      { number: 23, text: "Wie beim Osttor lag auch dem Nordtor ein Tor gegenüber, das zum Innenhof führte, und er maß den Abstand von Tor zu Tor - hundert Ellen." },
      { number: 24, text: "Dann führte er mich nach Süden und auch im Süden war ein Tor. Und er maß seine Pfeiler und seine Vorhalle; sie entsprachen den Maßen der anderen." },
      { number: 25, text: "Auch die Fenster des Tors und seiner Vorhalle ringsum entsprachen den anderen Fenstern. Im Ganzen war es fünfzig Ellen lang und fünfundzwanzig Ellen breit." },
      { number: 26, text: "Sieben Stufen führten zu ihm hinauf. Auf der Innenseite lag seine Vorhalle. Die beiden Pfeiler dieses Tors waren hüben und drüben mit je einer Palme verziert." },
      { number: 27, text: "Auch auf der Südseite war ein Tor zum Innenhof und er maß den Abstand von Tor zu Tor im Süden - hundert Ellen." },
      { number: 28, text: "Dann brachte er mich durch das Südtor in den Innenhof und maß das Südtor. Es hatte die gleichen Maße wie die anderen Tore:" },
      { number: 29, text: "Seine Torkammern, seine Pfeiler und seine Vorhalle hatten die gleichen Maße wie dort. Auch dieses Tor und seine Vorhalle hatten ringsum Fenster. Im Ganzen war es fünfzig Ellen lang und fünfundzwanzig Ellen breit." },
      { number: 30, text: "[Ringsherum waren Vorhallen, fünfundzwanzig Ellen lang und fünf Ellen breit.]" },
      { number: 31, text: "Seine Vorhalle lag auf der Seite zum Vorhof. Seine Pfeiler waren mit Palmen verziert und acht Stufen führten zu ihm hinauf." },
      { number: 32, text: "Dann brachte er mich zur Ostseite des Innenhofs und maß das Tor ab. Es hatte die gleichen Maße wie die anderen Tore:" },
      { number: 33, text: "Seine Torkammern, seine Pfeiler und seine Vorhalle hatten die gleichen Maße wie dort. Auch dieses Tor und seine Vorhalle hatten ringsum Fenster. Im Ganzen war es fünfzig Ellen lang und fünfundzwanzig Ellen breit." },
      { number: 34, text: "Seine Vorhalle lag auf der Seite zum Vorhof. Seine beiden Pfeiler waren hüben und drüben mit je einer Palme verziert und acht Stufen führten zu ihm hinauf." },
      { number: 35, text: "Dann brachte er mich zum (inneren) Nordtor und stellte auch bei seinen Torkammern, seinen Pfeilern und seiner Vorhalle die gleichen Maße fest. Auch dieses Tor hatte ringsum Fenster. Im Ganzen war es fünfzig Ellen lang und fünfundzwanzig Ellen breit." },
      { number: 37, text: "Seine Vorhalle lag auf der Seite zum Vorhof, seine beiden Pfeiler waren hüben und drüben mit je einer Palme verziert und acht Stufen führten zu ihm hinauf.", footnotes: ["seine Vorhalle: Text korr. nach G (vgl. V. 31 und V. 34)."] },
      { number: 38, text: "An den Pfeilern des Tors war der Eingang zu einem besonderen Raum, in dem man das Brandopfer abspülte." },
      { number: 39, text: "In der Vorhalle des Tors standen an beiden Seiten je zwei Tische, auf denen die Brand-, Sünd- und Schuldopfer geschlachtet wurden." },
      { number: 40, text: "An der Außenwand standen neben dem Toreingang im Norden je zwei Tische und an der gegenüberliegenden Wand der Vorhalle des Tors ebenfalls je zwei Tische;" },
      { number: 41, text: "je vier Tische standen also an den beiden Stirnwänden (der Vorhalle) des Tors, zusammen acht Tische. Auf ihnen schlachtete man (die Opfertiere)." },
      { number: 42, text: "Die vier Tische für das Brandopfer waren aus behauenen Steinen. Sie waren anderthalb Ellen lang, anderthalb Ellen breit und eine Elle hoch. Innen (in der Vorhalle) hatte man ringsum handbreite Randleisten angebracht. Auf sie legte man die Geräte, mit denen man die Brand- und Schlachtopfer schlachtete, und auf die Tische legte man das Opferfleisch." },
      { number: 44, text: "Neben dem (nördlichen und südlichen) Innentor lagen zwei Räume; der neben dem Nordtor liegende öffnete sich nach Süden zum Innenhof, der neben dem Südtor liegende öffnete sich nach Norden zum Innenhof.", footnotes: ["zwei: Text korr. nach G. - Südtor: Text korr. nach G; H: Osttor."] },
      { number: 45, text: "Er sagte zu mir: Der Raum, der sich nach Süden öffnet, gehört den Priestern, die im Tempel dienen." },
      { number: 46, text: "Der Raum aber, der sich nach Norden öffnet, gehört den Priestern, die am Altar dienen; das sind die Nachkommen Zadoks, die als einzige aus dem Stamm Levi vor den Herrn hintreten dürfen, um ihm zu dienen.", footnotes: ["43,19; 44,15; 48,11; 1 Chr 5,34-41; 1 Kön 1,8.39"] },
      { number: 47, text: "Dann maß er den Innenhof - ein Quadrat von hundert Ellen Länge und hundert Ellen Breite. Vor dem Tempelgebäude stand der Altar." },
      { number: 48, text: "Darauf führte er mich zur Vorhalle des Tempels und maß die Pfeiler der Vorhalle zu beiden Seiten - je fünf Ellen. Die Breite der Toröffnung betrug vierzehn Ellen und die Breite der beiden Seitenwände (neben der Toröffnung) je drei Ellen.", footnotes: ["vierzehn Ellen und die Breite der beiden Seitenwände: Text korr. nach G; vgl. 41,1."] },
      { number: 49, text: "Die Vorhalle war zwanzig Ellen breit und zwölf Ellen lang. Zehn Stufen musste man zu ihr hinaufsteigen. An den Pfeilern standen außen Säulen, je eine auf jeder Seite.", footnotes: ["zwölf: Text korr. nach G; H: elf. - zehn: Text korr. nach G."] },
    ] },
    { number: 41, verses: [
      { number: 1, text: "Dann brachte er mich zur Tempelhalle und maß die Pfeiler zu beiden Seiten (des Eingangs); jeder war sechs Ellen dick. [So dick waren die Pfeiler.]", footnotes: ["Text korr."] },
      { number: 2, text: "Die Breite der Türöffnung betrug zehn Ellen und die der beiden Seitenwände neben der Türöffnung je fünf Ellen. Darauf maß er die Länge der Tempelhalle - vierzig Ellen - und ihre Breite - zwanzig Ellen." },
      { number: 3, text: "Er trat ins Innere und maß die Pfeiler der Türöffnung (zum Allerheiligsten) - zwei Ellen - und die Türöffnung - sechs Ellen sowie die Breite der Seitenwände neben der Türöffnung - sieben Ellen.", footnotes: ["der Seitenwände: ergänzt nach G."] },
      { number: 4, text: "Und er maß seine Länge - zwanzig Ellen - und seine Breite an der Seite der Tempelhalle - zwanzig Ellen. Und er sagte zu mir: Das ist das Allerheiligste." },
      { number: 5, text: "Dann maß er die Mauer des Tempelgebäudes - sechs Ellen dick - und die Breite des Anbaus rings um das Tempelgebäude - vier Ellen.", heading: "Die Nebengebäude des Tempels: 41,5-12" },
      { number: 6, text: "(In dem Anbau waren) Nebenräume, einer am andern, im ganzen dreißig in drei Stockwerken. An der Mauer des Tempelgebäudes waren ringsum Vorsprünge für die (Deckenbalken der) Nebenräume; die Vorsprünge dienten als Auflagen; es brauchten also in der Wand des Tempelgebäudes keine Auflagen zu sein.", footnotes: ["dreißig in drei: Text korr.; vgl. die alten Übersetzungen; H: dreiunddreißig."] },
      { number: 7, text: "Durch die Nebenräume ergab sich bis zu einer bestimmten Höhe hinauf eine Verbreiterung, denn das Tempelgebäude war ringsum umbaut; durch diesen Anbau ergab sich eine Verbreiterung bis zu einer bestimmten Höhe. Vom untersten Stockwerk konnte man (im Innern) über das mittlere zum oberen hinaufsteigen.", footnotes: ["Text korr. nach G. Der ganze Vers ist unklar."] },
      { number: 8, text: "Das Tempelgebäude selbst konnte man von allen Seiten (über die Anbauten) herausragen sehen. Die Fundamente der Nebenräume waren eine ganze Messlatte dick - sechs Ellen.", footnotes: ["8a: Sinn von H nicht ganz klar."] },
      { number: 9, text: "Die Außenmauer des Anbaus war fünf Ellen dick. Zwischen den Nebenräumen des Tempelgebäudes", footnotes: ["Zwischen: Text korr.; vgl. G."] },
      { number: 10, text: "und den (Priester-)Räumen war ein freier Platz von zwanzig Ellen Breite, der rings um den Tempel herumlief." },
      { number: 11, text: "Aus dem Anbau führten zwei Türen ins Freie, eine im Norden und eine im Süden. Die Mauer des freien Platzes war ringsum fünf Ellen dick.", footnotes: ["Die Mauer: Text korr.; vgl. G; H: Der Platz."] },
      { number: 12, text: "Vor dem eingefriedeten Platz lag auf der Westseite ein (großes) Gebäude, das neunzig Ellen breit und siebzig Ellen lang war. Die Mauer dieses Gebäudes war ringsum fünf Ellen dick." },
      { number: 13, text: "Dann maß er das Tempelgebäude - hundert Ellen lang - sowie den eingefriedeten Platz, das andere Gebäude und seine Mauern - auch hundert Ellen lang.", heading: "Die Maße des Tempels: 41,13-15a" },
      { number: 14, text: "Die Breite der Vorderseite des Tempels und des eingefriedeten Platzes betrug im Osten zusammen ebenfalls hundert Ellen." },
      { number: 15, text: "Und er maß die Breite des (großen) Gebäudes, das vor dem eingefriedeten Platz lag, auf der Rückseite und (die Länge) der Terrassenbauten auf seinen beiden Seiten - wieder je hundert Ellen.Das Innere der Tempelhalle und der Vorhalle, die aus dem Innenhof in den Tempel führt,", heading: "Das Innere des Tempels: 41,15b-26" },
      { number: 16, text: "waren getäfelt. Die Rahmen der verschließbaren Fenster und die drei Gesimse, die von der Schwelle an ringsum liefen, waren aus Holz; ebenso gab es vom Fußboden bis zu den Fenstern - die Fenster waren verdeckbar -", footnotes: ["waren getäfelt: Text korr. nach G."] },
      { number: 17, text: "und bis über die Türöffnungen hinauf eine Holztäfelung. Auf allen Wänden ringsum, auch auf den Wänden zum Innenraum und nach draußen, war die Täfelung in Felder eingeteilt, auf denen", footnotes: ["Text unklar."] },
      { number: 18, text: "geschnitzte Kerubim und Palmen zu sehen waren, je eine Palme zwischen zwei Kerubim. Jeder Kerub hatte zwei Gesichter:" },
      { number: 19, text: "Ein Menschengesicht (blickte) zur einen Palme und ein Löwengesicht zur andern. So war das ganze Haus ringsum ausgestaltet." },
      { number: 20, text: "Vom Fußboden bis über die Türöffnungen hinauf waren an der Tempelwand Kerubim und Palmen angebracht." },
      { number: 21, text: "Die Tür der Tempelhalle hatte einen vierfach gestuften Türrahmen. Vor dem Allerheiligsten war etwas, das aussah wie" },
      { number: 22, text: "ein Altar aus Holz, drei Ellen hoch, zwei Ellen lang und zwei Ellen breit. Seine Ecken, sein Sockel und seine Wände waren aus Holz. Der Mann sagte zu mir: Das ist der Tisch, der vor dem Herrn steht.", footnotes: ["Ex 25,23-30; 40,22f", "und zwei Ellen breit: Text korr., eingefügt nach G. - sein Sockel: Text korr. nach G; H: seine Länge."] },
      { number: 23, text: "Die Tempelhalle und das Allerheiligste hatten je eine Doppeltür." },
      { number: 24, text: "Die Türen hatten zwei Türflügel, zwei drehbare Türflügel, zwei die eine Tür und zwei die andere Tür." },
      { number: 25, text: "An den Türen der Tempelhalle waren Kerubim und Palmen angebracht wie an den Wänden. Außen, an der Vorderseite der Vorhalle, war ein Vordach aus Holz." },
      { number: 26, text: "An den beiden Seitenwänden der Vorhalle und an den Nebenräumen des Tempels [und an den Vordächern] waren verschließbare Fenster und Palmen." },
    ] },
    { number: 42, verses: [
      { number: 1, text: "Dann führte er mich in den nördlichen Teil des Vorhofs hinaus und brachte mich zu einem Bau mit Einzelräumen, der gegenüber dem eingefriedeten Platz und im Norden des (großen) Gebäudes lag.", heading: "Die Räume für die Priester: 42,1-14" },
      { number: 2, text: "An seiner Längsseite gegenüber dem Nordeingang (des großen Gebäudes) maß dieser Bau hundert Ellen; seine Breite betrug fünfzig Ellen." },
      { number: 3, text: "Der Bau lag gegenüber dem freien Platz von zwanzig Ellen, der zum Innenhof gehörte, und gegenüber dem Steinpflaster, das zum Vorhof gehörte. Er war terrassenartig angelegt, mit drei Stockwerken." },
      { number: 4, text: "Vor den Räumen lief ein Gang entlang, zehn Ellen breit und hundert Ellen lang. Die Eingänge der Räume lagen im Norden.", footnotes: ["hundert Ellen lang: Text korr. nach G und S; H: ein Weg von einer Elle."] },
      { number: 5, text: "Die oberen Räume des Baus waren kürzer als die mittleren und die unteren; denn die Terrassen nahmen ihnen Platz weg." },
      { number: 6, text: "Das Ganze war dreistöckig; die Stockwerke hatten aber keine Säulen wie der Vorhof; deswegen wurden sie von unten nach oben, vom unteren über das mittlere (bis zum oberen) immer kürzer." },
      { number: 7, text: "Außen zog sich an den Räumen zum Vorhof hin eine Mauer entlang. An der Vorderseite der Räume war sie fünfzig Ellen lang;" },
      { number: 8, text: "da die Räume, die zum Vorhof gehörten, ebenfalls fünfzig Ellen lang waren, war die ganze Mauer hundert Ellen lang.", footnotes: ["die ganze (Mauer): Text korr. nach G."] },
      { number: 9, text: "Der Zugang zu den unteren Räumen (des Terrassenbaus) lag im Osten, dort, wo man vom Vorhof herkam," },
      { number: 10, text: "an der Breitseite der Mauer zum Vorhof. Im Süden des eingefriedeten Platzes und des (großen) Gebäudes war ebenfalls ein Bau mit Einzelräumen", footnotes: ["Im Süden: Text korr.; vgl. G; H: Im Osten."] },
      { number: 11, text: "und auch an ihrer Vorderseite lief ein Weg entlang. Sie hatten dasselbe Aussehen, die gleiche Länge und Breite, die gleichen Eingänge, dieselbe Anordnung und ebenso viele Türen wie die Räume im Norden." },
      { number: 12, text: "Der Zugang zu den Türen der Räume im Süden lag am Anfang des nach Osten führenden Weges, der am Kopfende der Schutzmauer begann.", footnotes: ["Text unklar, die Übersetzung ist ein Versuch."] },
      { number: 13, text: "Der Mann sagte zu mir: Die Räume im Norden und die Räume im Süden, die vor dem umfriedeten Platz liegen, sind heilige Räume. Dort sollen die Priester, die vor den Herrn hintreten dürfen, die hochheiligen Gaben verzehren und dort sollen sie die hochheiligen Gaben, die Speise-, Sünd- und Schuldopfer niederlegen; denn der Ort ist heilig." },
      { number: 14, text: "Wenn die Priester in das Heiligtum gegangen sind, sollen sie von dort nicht zum Vorhof hinausgehen, sondern ihre Gewänder, in denen sie Dienst getan haben, in diesen Räumen ablegen; denn die Gewänder sind heilig. Sie sollen andere Kleider anziehen und erst dann auf den Platz hinaustreten, der für das Volk vorgesehen ist.", footnotes: ["44,19"] },
      { number: 15, text: "Als er den inneren Tempelbezirk vermessen hatte, führte er mich zum Osttor hinaus und vermaß den ganzen Tempelbezirk ringsum.", heading: "Die Maße des Tempelbezirks: 42,15-20" },
      { number: 16, text: "Er maß mit seiner Messlatte die Ostseite - fünfhundert Ellen [mit der Messlatte]. Dann wandte er sich (der Nordseite zu)", footnotes: ["16f: Dann wandte er sich: Text korr. nach G."] },
      { number: 17, text: "und maß mit der Messlatte die Nordseite - fünfhundert Ellen. Dann wandte er sich" },
      { number: 18, text: "der Südseite zu und maß mit der Messlatte wieder fünfhundert Ellen." },
      { number: 19, text: "Dann wandte er sich der Westseite zu und maß mit der Messlatte wieder fünfhundert Ellen." },
      { number: 20, text: "Nach allen vier Windrichtungen vermaß er den Tempelbezirk: Der Tempelbezirk hatte ringsum eine Mauer, fünfhundert Ellen in der Länge und fünfhundert Ellen in der Breite; sie sollte das Heilige vom Unheiligen trennen." },
    ] },
    { number: 43, verses: [
      { number: 1, text: "Dann führte er mich zu einem der Tore, dem Tor, das im Osten lag.", heading: "Der Einzug des Herrn in den Tempel: 43,1-12" },
      { number: 2, text: "Da sah ich, wie die Herrlichkeit des Gottes Israels aus dem Osten herankam. Ihr Rauschen war wie das Rauschen gewaltiger Wassermassen und die Erde leuchtete auf von seiner Herrlichkeit.", footnotes: ["1,28; Offb 1,15"] },
      { number: 3, text: "Die Erscheinung, die ich sah, war wie die Erscheinung, die ich damals sah, als er kam, um die Stadt zu vernichten, und wie die Erscheinung, die ich am Fluss Kebar gesehen hatte. Da fiel ich nieder auf mein Gesicht.", footnotes: ["8 - 11; 1,3", "als er kam: Text korr. nach einigen H-Handschriften und Vg; H: als ich kam."] },
      { number: 4, text: "Und die Herrlichkeit des Herrn zog in den Tempel ein durch das Tor, das im Osten lag.", footnotes: ["Ex 40,34; 1 Kön 8,10f"] },
      { number: 5, text: "Der Geist hob mich empor und brachte mich in den Innenhof. Und die Herrlichkeit des Herrn erfüllte den Tempel." },
      { number: 6, text: "Dann hörte ich vom Tempel her, während der Mann neben mir stand, einen, der mit mir redete;" },
      { number: 7, text: "er sagte zu mir: Menschensohn, das ist der Ort, wo mein Thron steht, und der Ort, wo meine Füße ruhen; hier will ich für immer mitten unter den Israeliten wohnen. Das Haus Israel aber und seine Könige sollen meinen heiligen Namen nie mehr beflecken mit ihrer Unzucht und mit den Leichen ihrer Könige [wenn sie tot sind].", footnotes: ["Ps 132,7f; Klgl 2,1; Ez 37,26f"] },
      { number: 8, text: "Sie legten ihre Schwelle neben meine Schwelle und setzten ihre Türpfosten neben meine Türpfosten, sodass zwischen mir und ihnen nur eine Wand war. So befleckten sie meinen heiligen Namen durch die Gräueltaten, die sie begingen, und ich musste sie in meinem Zorn vernichten." },
      { number: 9, text: "Jetzt aber sollen sie ihre Unzucht und die Leichen ihrer Könige von mir fern halten, sodass ich für immer mitten unter ihnen wohnen kann." },
      { number: 10, text: "Du, Menschensohn, berichte dem Haus Israel über den Tempel, damit sie sich wegen ihrer bösen Taten schämen. Sie sollen den Entwurf des Tempels kennen lernen.", footnotes: ["kennen lernen, wörtlich: ausmessen."] },
      { number: 11, text: "Wenn sie sich all dessen schämen, was sie getan haben, dann mach ihnen den Plan des Tempels bekannt, seine Einrichtung, seine Ausgänge und Eingänge und seinen ganzen Plan, alle ihn betreffenden Gesetze [meine ganzen Pläne] und alle ihn betreffenden Anweisungen. Zeichne alles vor ihren Augen auf! Sie sollen darauf achten, dass sie seinen ganzen Plan ausführen und alle ihn betreffenden Gesetze befolgen." },
      { number: 12, text: "Das ist meine Anweisung für den Tempelbezirk. Das ganze Gebiet auf dem Gipfel des Berges ringsum ist hochheiliger Boden. [Siehe, das ist die Anweisung für den Tempelbezirk.]" },
      { number: 13, text: "Das waren die Maße des Altars, gemessen in Ellen, die Elle zu einer gewöhnlichen Elle und einer Handbreit gerechnet: Der Graben (um den Altar) war eine Elle tief und eine Elle breit und die gemauerte Einfassung an seinem Rand war eine Spanne breit. Die Höhe des Altars maß:", heading: "Der Altar im Innenhof und die Altarweihe: 43,13-27", footnotes: ["Ex 27,1-8; 1 Kön 8,64"] },
      { number: 14, text: "vom Boden des Grabens bis zur unteren Stufe zwei Ellen; die Stufe war eine Elle breit; auf diese niedrige Stufe folgte eine höhere Stufe von vier Ellen, die oben wieder eine Elle zurücksprang.", footnotes: ["die oben . . . zurücksprang: H wörtlich: und die Breite eine Elle."] },
      { number: 15, text: "Der (darauf folgende) Opferherd war vier Ellen hoch und an dem Opferherd ragten vier Hörner empor." },
      { number: 16, text: "Der Opferherd hatte eine Länge von zwölf Ellen und eine Breite von zwölf Ellen; er war quadratisch." },
      { number: 17, text: "Auch die (mittlere) Stufe war quadratisch, vierzehn Ellen lang und vierzehn Ellen breit. Rings um die untere Stufe lief eine gemauerte Einfassung von einer halben Elle (Höhe). Der Graben, der sie umgab, maß ringsum eine Elle. Im Osten führte eine Treppe zum Altar hinauf.", footnotes: ["Text ergänzt; vermutlich ist ein Satz ausgefallen."] },
      { number: 18, text: "Dann sagte er zu mir: Menschensohn, so spricht Gott, der Herr: Das sind die Gesetze für den Tag, an dem der Altar fertig gestellt wird, damit man Brandopfer auf ihm darbringen und Blut auf ihn sprengen kann:", footnotes: ["Ex 29,36f"] },
      { number: 19, text: "Du sollst den levitischen Priestern, den Nachkommen Zadoks, die vor mich hintreten dürfen, um mir zu dienen - Spruch Gottes, des Herrn -, einen jungen Stier zum Sündopfer geben.", footnotes: ["40,46; 44,15"] },
      { number: 20, text: "Dann sollst du etwas von seinem Blut nehmen und es auf die vier Hörner und auf die vier Ecken der (mittleren) Stufe sowie ringsum auf die gemauerte Einfassung tun. So sollst du den Altar entsündigen und entsühnen." },
      { number: 21, text: "Dann sollst du den Stier des Sündopfers nehmen und man soll ihn auf dem dafür vorgesehenen Platz beim Tempel außerhalb des Heiligtums verbrennen." },
      { number: 22, text: "Am zweiten Tag sollst du einen Ziegenbock, der ohne Fehler ist, als Sündopfer darbringen und man soll den Altar damit entsündigen, wie man ihn mit dem Stier entsündigt hat." },
      { number: 23, text: "Wenn du die Entsündigung vollzogen hast, sollst du einen jungen Stier, der ohne Fehler ist, sowie einen Widder aus der Herde, der ohne Fehler ist, darbringen." },
      { number: 24, text: "Du sollst beide vor den Herrn bringen und die Priester sollen sie mit Salz bestreuen und dem Herrn als Brandopfer darbringen." },
      { number: 25, text: "Sieben Tage lang sollst du täglich einen Bock als Sündopfer darbringen und man soll (täglich) einen jungen Stier und einen Widder aus der Herde - Tiere ohne Fehler - darbringen." },
      { number: 26, text: "Sieben Tage lang soll man den Altar entsühnen, rein machen und einweihen." },
      { number: 27, text: "Man soll diese Tage genau einhalten. Vom achten Tag an sollen dann die Priester auf dem Altar eure Brand- und Heilsopfer darbringen. Dann will ich euch gnädig sein - Spruch Gottes, des Herrn." },
    ] },
    { number: 44, verses: [
      { number: 1, text: "Dann führte er mich zum äußeren Osttor des Heiligtums zurück. Es war geschlossen.", heading: "Das verschlossene Tor: 44,1-3" },
      { number: 2, text: "Da sagte der Herr zu mir: Dieses Tor soll geschlossen bleiben, es soll nie geöffnet werden, niemand darf hindurchgehen; denn der Herr, der Gott Israels, ist durch dieses Tor eingezogen; deshalb bleibt es geschlossen.", footnotes: ["Ps 24,7"] },
      { number: 3, text: "Nur der Fürst darf, weil er der Fürst ist, sich dort niedersetzen, um vor den Augen des Herrn die Opfermahlzeit abzuhalten. Dabei soll er von der Vorhalle des Tores aus eintreten und auf dem gleichen Weg wieder hinausgehen.", footnotes: ["46,2"] },
      { number: 4, text: "Dann führte er mich zum Nordtor, zur Vorderseite des Tempels. Ich sah: Die Herrlichkeit des Herrn erfüllte den Tempel des Herrn. Und ich fiel nieder auf mein Gesicht.", heading: "Die Vorschriften für den Kult und die Verteilung des Landes: 44,4 - 48,35 | Die Leviten und Priester: 44,4-31" },
      { number: 5, text: "Der Herr sagte zu mir: Menschensohn, gib Acht, öffne deine Augen und deine Ohren und hör auf alles, was ich zu dir sage, vernimm alle Gesetze und Anweisungen über den Tempel des Herrn! Achte an allen Eingängen des Heiligtums darauf, wer den Tempel betritt." },
      { number: 6, text: "Sag zu dem widerspenstigen Haus Israel: So spricht Gott, der Herr: Hört endlich auf mit all euren Gräueltaten, ihr vom Haus Israel!" },
      { number: 7, text: "Fremde, die unbeschnitten sind am Herzen und unbeschnitten am Körper, sie habt ihr eintreten lassen, sie waren in meinem Heiligtum und haben meinen Tempel entweiht, wenn ihr mir meine Opferspeise, Fett und Blut, dargebracht habt. So habt ihr mit all euren Gräueltaten meinen Bund gebrochen.", footnotes: ["Jes 52,1; Jer 9,25"] },
      { number: 8, text: "Ihr wolltet den Dienst in meinem Heiligtum nicht verrichten, sondern ihr habt sie an eurer Stelle den Dienst in meinem Heiligtum verrichten lassen." },
      { number: 9, text: "So spricht Gott, der Herr: Kein Fremder, der unbeschnitten ist am Herzen und unbeschnitten am Körper, darf mein Heiligtum betreten, keiner von all den Fremden, die bei den Israeliten leben," },
      { number: 10, text: "sondern nur die Leviten. Doch weil sie mich verlassen haben, als Israel in die Irre ging, und weil sie statt mir ihren Götzen gefolgt sind, müssen sie die Folgen ihrer Schuld tragen:" },
      { number: 11, text: "Sie dürfen in meinem Heiligtum nur Wächter an den Tempeltoren und Tempeldiener sein; sie dürfen (die Tiere für) das Brandopfer und das Schlachtopfer des Volkes schlachten und sollen vor den Leuten bereitstehen, um ihnen zu dienen." },
      { number: 12, text: "Weil sie ihnen bei ihrem Götzendienst geholfen haben und für das Haus Israel der Anlass wurden, in Schuld zu fallen, darum habe ich meine Hand gegen sie erhoben - Spruch Gottes, des Herrn -; sie müssen die Folgen ihrer Schuld tragen." },
      { number: 13, text: "Sie dürfen nicht mehr vor mich hintreten, um mir als Priester zu dienen und alle meine heiligen Geräte und hochheiligen Opfer zu berühren. Sie müssen ihre Schande und die Folgen der Gräueltaten tragen, die sie begangen haben." },
      { number: 14, text: "Ich setze sie als Tempeldiener ein, damit sie alles erledigen, was im Tempel zu tun ist." },
      { number: 15, text: "Die levitischen Priester, die Nachkommen Zadoks, die mir in meinem Heiligtum treu gedient haben, als die Israeliten mich verließen und in die Irre gingen, sie sollen vor mich hintreten und mir dienen, sie sollen vor mir stehen, um mir Fett und Blut darzubringen - Spruch Gottes, des Herrn.", footnotes: ["40,46"] },
      { number: 16, text: "Sie dürfen in mein Heiligtum kommen und an meinen Tisch herantreten, um mir zu dienen; sie sollen meinen Dienst verrichten." },
      { number: 17, text: "Wenn sie durch die Tore des Innenhofs hineingehen, sollen sie Leinengewänder tragen. Sie dürfen nichts Wollenes tragen, wenn sie in den Toren des Innenhofs und im Tempel Dienst tun." },
      { number: 18, text: "Sie sollen einen Kopfbund aus Leinen aufsetzen und leinene Hosen anziehen; sie sollen keine Kleider tragen, in denen man schwitzt.", footnotes: ["Lev 6,3f; 16,4", "Schweiß macht demnach wie andere Ausscheidungen (vgl. Dtn 23,12-14) unrein und kultunfähig."] },
      { number: 19, text: "Wenn sie in den Vorhof zum Volk hinausgehen, sollen sie die Leinengewänder ausziehen, in denen sie Dienst getan haben; sie sollen sie in die heiligen Räume legen und andere Kleider anziehen, damit sie das Volk nicht durch ihre Leinengewänder heilig machen.", footnotes: ["46,20"] },
      { number: 20, text: "Sie sollen weder ihren Kopf kahl scheren, noch ihre Haare lang herabhängen lassen, sondern sie sollen ihr Haar kurz schneiden.", footnotes: ["Lev 21,5; 19,27"] },
      { number: 21, text: "Ein Priester darf keinen Wein trinken, wenn er den Innenhof betreten muss.", footnotes: ["Lev 10,9"] },
      { number: 22, text: "Die Priester dürfen keine Witwe und keine verstoßene Frau heiraten, sondern nur Jungfrauen aus der Nachkommenschaft des Hauses Israel. Eine Witwe aber, die mit einem Priester verheiratet war, dürfen sie zur Frau nehmen.", footnotes: ["Lev 21,7.13f"] },
      { number: 23, text: "Sie sollen mein Volk über den Unterschied zwischen heilig und nicht heilig belehren und ihnen den Unterschied zwischen unrein und rein deutlich machen.", footnotes: ["22,26; Lev 10,10"] },
      { number: 24, text: "Bei einem Rechtsstreit sind sie die Richter; nach meinen Rechtsvorschriften sollen sie ihn entscheiden. An allen meinen Festen sollen sie auf meine Weisungen und meine Gesetze achten und sie sollen meine Sabbat-Tage heilig halten.", footnotes: ["Dtn 17,8f"] },
      { number: 25, text: "Einem Toten sollen sie sich nicht nähern; denn sie werden dadurch unrein. Nur beim Tod von Vater und Mutter, Sohn und Tochter, Bruder und unverheirateter Schwester dürfen sie sich unrein machen;", footnotes: ["Lev 21,1-4"] },
      { number: 26, text: "wenn der Priester dann wieder rein geworden ist, soll er noch sieben Tage warten." },
      { number: 27, text: "Wenn er danach wieder in den Innenhof des Heiligtums geht, um dort Dienst zu tun, dann soll er ein Sündopfer für sich darbringen - Spruch Gottes, des Herrn." },
      { number: 28, text: "Erbbesitz dürfen sie nicht haben; ich bin ihr Erbbesitz. Auch Eigentum sollt ihr ihnen in Israel nicht geben; ich bin ihr Eigentum.", footnotes: ["Num 18,20-24; Dtn 18,1f", "nicht (haben): Text korr. nach Vg."] },
      { number: 29, text: "Sie dürfen das Speise-, Sünd- und Schuldopfer essen. Jede Gabe, die man in Israel Gott weiht, gehört ihnen.", footnotes: ["(29-30) Ex 25,2; Num 15,20; Neh 10,38"] },
      { number: 30, text: "Das Beste von allen Erstlingsopfern, all eure Abgaben, die ihr entrichten müsst, gehören den Priestern; auch das Beste von dem, was ihr backt, sollt ihr dem Priester geben, damit ihr Segen über eure Häuser bringt." },
      { number: 31, text: "Verendete und (von Raubtieren) zerrissene Vögel und Tiere dürfen die Priester nicht essen.", footnotes: ["Lev 22,8"] },
    ] },
    { number: 45, verses: [
      { number: 1, text: "Wenn ihr das Land durch das Los verteilt, sollt ihr ein Stück von fünfundzwanzigtausend Ellen Länge und zwanzigtausend Ellen Breite als eine Abgabe für den Herrn zurückbehalten. Es soll in seinem ganzen Umfang heilig sein.", heading: "Die Aufteilung des Kerngebietes: 45,1-8", footnotes: ["(1-8) 48,8-20", "zwanzigtausend: Text korr. nach G; H: zehntausend."] },
      { number: 2, text: "Davon gehört um den Tempel herum ein Quadrat von fünfhundert mal fünfhundert Ellen dem Heiligtum, dazu ringsum fünfzig Ellen unbestelltes Land." },
      { number: 3, text: "Von diesem abgemessenen Stück Land sollt ihr wiederum ein Stück von fünfundzwanzigtausend Ellen Länge und zehntausend Ellen Breite abmessen, auf dem das hochheilige Heiligtum stehen soll." },
      { number: 4, text: "Das ist das heilige Gebiet des Landes und es soll den Priestern gehören, die den Dienst im Heiligtum verrichten und sich beim Gottesdienst dem Herrn nähern dürfen. Es ist der Platz für ihre Häuser und die Weidefläche für ihre Herden.", footnotes: ["die Weideflächen für ihre Herden: Text korr.; vgl. G und die aramäische Übersetzung; H: ein Heiligtum zum Heiligtum."] },
      { number: 5, text: "Das (übrige) Stück von fünfundzwanzigtausend Ellen Länge und zehntausend Ellen Breite soll den Leviten gehören, die den Tempeldienst verrichten. Es ist ihr Eigentum; dort können sie ihre Städte bauen und darin wohnen." },
      { number: 6, text: "Als Eigentum der Stadt sollt ihr ein (weiteres) Stück von fünftausend Ellen Breite und fünfundzwanzigtausend Ellen Länge abmessen, genauso lang wie die heilige Abgabe. Dieses Stück gehört dem ganzen Haus Israel." },
      { number: 7, text: "Dem Fürsten soll auf beiden Seiten der heiligen Abgabe und des Eigentums der Stadt, im Westen und Osten, ein Gebiet gehören, das an die heilige Abgabe und das Eigentum der Stadt angrenzt und in der Länge einem der Stammesanteile von der Westgrenze bis zur Ostgrenze" },
      { number: 8, text: "des Landes entspricht. Es gehört dem Fürsten als Eigentum in Israel. Meine Fürsten werden mein Volk nicht mehr unterdrücken, sondern das Land dem Haus Israel und seinen Stämmen überlassen." },
      { number: 9, text: "So spricht Gott, der Herr: Genug, ihr Fürsten Israels! Beseitigt Gewalt und Unterdrückung! Sorgt für Recht und Gerechtigkeit! Hört auf, mein Volk von seinem Grund und Boden zu vertreiben - Spruch Gottes, des Herrn.", heading: "Mahnung an die Fürsten: 45,9-17", footnotes: ["Jer 21,11f; 22,3f; Mi 3,1-3"] },
      { number: 10, text: "Verwendet richtige Waagen, richtiges Efa und richtiges Bat!", footnotes: ["(10-11) Lev 19,35f; Dtn 25,13-15"] },
      { number: 11, text: "Das Efa und das Bat sollen das gleiche Maß haben; das Bat soll der zehnte Teil eines Hómer sein und ebenso das Efa der zehnte Teil eines Hómer; nach dem Hómer sollen sich die anderen Maße richten." },
      { number: 12, text: "Ein Schekel soll bei euch zwanzig Gera entsprechen. Zehn Schekel sind ein Fünftel einer Mine, fünf Schekel ein Zehntel; fünfzig Schekel sind eine ganze Mine.", footnotes: ["fünfzig: Text korr. nach G; H: fünf."] },
      { number: 13, text: "Folgende Abgaben sollt ihr erheben: von jedem Hómer Weizen ein Sechstel Efa und ebenso von jedem Hómer Gerste ein Sechstel Efa;" },
      { number: 14, text: "und für das Öl gilt: von jedem Kor ein Zehntel Bat; das Bat ist das Ölmaß; zehn Bat entsprechen einem Hómer und zehn Bat entsprechen auch einem Kor;", footnotes: ["einem Kor: Text korr. nach Vg."] },
      { number: 15, text: "ferner aus jeder Herde ein Schaf auf je zweihundert Tiere vom Viehbesitz Israels. (Diese Abgabe ist) für die Speise-, Brand- und Heilsopfer (bestimmt), die euch entsühnen sollen - Spruch Gottes, des Herrn.", footnotes: ["vom Viehbesitz: Text korr."] },
      { number: 16, text: "Das ganze Volk im Land hat diese Abgaben an den Fürsten von Israel zu entrichten." },
      { number: 17, text: "Der Fürst aber hat an allen Feiertagen des Hauses Israel, an den Festen und an den Neumond- und Sabbat-Tagen für Brandopfer, Speiseopfer und Trankopfer zu sorgen. Er veranstaltet die Sünd-, Speise-, Brand- und Heilsopfer, die das Haus Israel entsühnen sollen.", footnotes: ["Lev 1,3; 2,1; 3,1"] },
      { number: 18, text: "So spricht Gott, der Herr: Am ersten Tag des ersten Monats sollst du einen jungen Stier nehmen, der ohne Fehler ist, und das Heiligtum entsündigen.", heading: "Die großen Feste: 45,18-25" },
      { number: 19, text: "Der Priester nimmt etwas Blut von dem Sündopfer und bestreicht damit die Türpfosten des Tempels und die vier Ecken der (mittleren) Stufe des Altars und die Türpfosten des Tors zum Innenhof." },
      { number: 20, text: "Dasselbe sollst du am siebten Tag des Monats tun für die, die sich aus Versehen oder aus Unwissenheit verfehlt haben. So sollt ihr den Tempel entsühnen.", footnotes: ["Lev 5,17f"] },
      { number: 21, text: "Am vierzehnten Tag des ersten Monats sollt ihr das Pascha feiern, ein Fest von sieben Tagen. Da soll man ungesäuerte Brote essen.", footnotes: ["Lev 23,5", "sieben: Text korr. nach den alten Übersetzungen."] },
      { number: 22, text: "Der Fürst soll an diesem Tag für sich und für das ganze Volk im Land einen Stier zum Sündopfer bereitstellen.", footnotes: ["Lev 4,22"] },
      { number: 23, text: "An den sieben Festtagen soll er als Brandopfer für den Herrn sieben Stiere und sieben Widder, die ohne Fehler sind, bereitstellen, und zwar an jedem der sieben Tage, dazu täglich einen Ziegenbock als Sündopfer.", footnotes: ["(23-24) Num 28,17-23"] },
      { number: 24, text: "Für das Speiseopfer soll er ein Efa Mehl je Stier und ein Efa Mehl je Widder bereitstellen, dazu je Efa Mehl ein Hin Öl." },
      { number: 25, text: "Genau dasselbe soll er sieben Tage lang mit dem Sünd-, Brand- und Speiseopfer und mit dem Öl an dem Fest machen, das am fünfzehnten Tag des siebten Monats beginnt.", footnotes: ["Num 15,4-12; Lev 23,34; Ex 23,14"] },
    ] },
    { number: 46, verses: [
      { number: 1, text: "So spricht Gott, der Herr: Das Osttor des Innenhofs soll an den sechs Werktagen geschlossen bleiben, nur am Sabbat und am Neumondtag soll es geöffnet werden.", heading: "Die Opfer: 46,1-15" },
      { number: 2, text: "Der Fürst soll von außen her durch die Vorhalle den Torbau betreten und sich an den Torpfosten stellen. Dann sollen die Priester sein Brand- und Heilsopfer darbringen, er aber soll sich an der Schwelle des Tors niederwerfen und wieder hinausgehen. Das Tor soll erst am Abend geschlossen werden.", footnotes: ["44,3"] },
      { number: 3, text: "Die Bürger des Landes sollen sich an den Sabbat- und Neumond-Tagen am Eingang dieses Tors ebenfalls vor dem Herrn niederwerfen." },
      { number: 4, text: "Das Brandopfer, das der Fürst dem Herrn darbringen lässt, soll am Sabbat-Tag aus sechs Lämmern und einem Widder bestehen, die ohne Fehler sind.", footnotes: ["Num 28,9f"] },
      { number: 5, text: "Zu dem Widder gehört ein Speiseopfer von einem Efa Mehl, zu den Lämmern ein Speiseopfer, dessen Menge er selbst bestimmen kann, dazu ein Hin Öl je Efa Mehl.", footnotes: ["5.11: dessen Menge (er bestimmen kann): sinngemäß übersetzt."] },
      { number: 6, text: "Am Neumond-Tag sollen es ein junger Stier, sechs Lämmer und ein Widder sein, die alle ohne Fehler sind." },
      { number: 7, text: "Zu dem Stier und zu dem Widder soll er je ein Efa Mehl als Speiseopfer besorgen, außerdem zu den Lämmern so viel, wie er gerade zur Verfügung hat, und dazu je Efa Mehl ein Hin Öl." },
      { number: 8, text: "Wenn der Fürst den Torbau betritt, soll er ihn durch die Vorhalle betreten und auf dem gleichen Weg muss er wieder hinausgehen." },
      { number: 9, text: "Wenn aber das Volk an den Festen vor den Herrn tritt, dann soll der, der durch das Nordtor gekommen ist, um (den Herrn) anzubeten, durch das Südtor hinausgehen, und wer durch das Südtor gekommen ist, soll durch das Nordtor hinausgehen. Niemand soll durch das Tor wieder hinausgehen, durch das er gekommen ist, sondern jeder soll durch das gegenüberliegende Tor hinausgehen.", footnotes: ["Ex 23,14-17"] },
      { number: 10, text: "Der Fürst soll mitten unter ihnen sein; wenn sie hineingehen, soll auch er hineingehen, und wenn sie hinausgehen, soll auch er hinausgehen." },
      { number: 11, text: "An den Fest- und Feiertagen gehört zu dem Stier und zu dem Widder ein Speiseopfer von je einem Efa Mehl und zu den Lämmern ein Speiseopfer, dessen Menge er selbst bestimmen kann, dazu ein Hin Öl je Efa Mehl." },
      { number: 12, text: "Wenn der Fürst dem Herrn von sich aus ein Brandopfer oder ein Heilsopfer darbringen lassen will, soll man für ihn das Osttor öffnen. Dann soll er sein Brand- und Heilsopfer genauso darbringen lassen wie am Sabbat. Daraufhin soll er hinausgehen, und wenn er hinausgegangen ist, soll man das Tor schließen." },
      { number: 13, text: "Täglich sollst du dem Herrn ein einjähriges Lamm, das ohne Fehler ist, als Brandopfer darbringen, und zwar jeden Morgen.", footnotes: ["Ex 29,38-42; Num 28,3"] },
      { number: 14, text: "Dazu sollst du als Speiseopfer jeden Morgen ein Sechstel Efa Mehl und ein Drittel Hin Öl herrichten, mit dem das Mehl besprengt wird. Das soll das Speiseopfer für den Herrn sein. Diese Vorschrift über das tägliche Opfer soll für immer gelten." },
      { number: 15, text: "Bringt also jeden Morgen das Lamm, das Speiseopfer und das Öl als das tägliche Brandopfer dar!" },
      { number: 16, text: "So spricht Gott, der Herr: Wenn der Fürst einem seiner Söhne etwas von seinem Erbbesitz schenkt, gehört es seinen Söhnen; es ist ihr Eigentum und sie können es weitervererben.", heading: "Der Besitz des Fürsten: 46,16-18", footnotes: ["etwas von seinem Erbbesitz: Text korr.; vgl. G, V. 17."] },
      { number: 17, text: "Wenn er aber etwas von seinem Erbbesitz einem seiner Diener schenkt, gehört es diesem nur bis zum nächsten Erlassjahr; dann fällt es an den Fürsten zurück. Nur seine Söhne dürfen behalten, was er ihnen von seinem Erbbesitz geschenkt hat." },
      { number: 18, text: "Vom Erbbesitz des Volkes darf der Fürst nichts nehmen; er darf sie nicht mit Gewalt von ihrem Eigentum verdrängen. Wenn er seinen Söhnen etwas vererben will, muss er es von seinem Eigentum nehmen, damit die Angehörigen meines Volkes nicht von ihrem Eigentum vertrieben werden." },
      { number: 19, text: "Dann brachte er mich durch den Eingang auf der Torseite zu den heiligen Priesterräumen im Norden (des Tempels), und zwar zu einer Stelle am äußersten Ende (der Räume) im Westen.", heading: "Die Opferküchen des Tempels: 46,19-24", footnotes: ["zu den heiligen Priesterräumen: Text korr.; vgl. die alten Übersetzungen."] },
      { number: 20, text: "Dort sagte er zu mir: Das ist die Stelle, wo die Priester das Schuld- und das Sündopfer kochen und das Speiseopfer backen, damit sie es nicht in den Vorhof hinausbringen müssen und dadurch das Volk heiligen." },
      { number: 21, text: "Dann führte er mich in den Vorhof hinaus und brachte mich zu den vier Ecken des Vorhofs. Und an jeder Ecke des Vorhofs war noch ein Hof." },
      { number: 22, text: "An allen vier Ecken des Vorhofs waren kleine Höfe, vierzig Ellen lang und dreißig Ellen breit. Alle vier Höfe an den Ecken hatten das gleiche Maß.", footnotes: ["kleine: Text korr. nach G."] },
      { number: 23, text: "Und ringsum lief in den vier Höfen eine Steinmauer und unten an den Steinmauern waren ringsum Kochstellen angelegt." },
      { number: 24, text: "Da sagte er zu mir: Das sind die Küchen, wo die Tempeldiener das Schlachtopfer des Volkes kochen." },
    ] },
    { number: 47, verses: [
      { number: 1, text: "Dann führte er mich zum Eingang des Tempels zurück und ich sah, wie unter der Tempelschwelle Wasser hervorströmte und nach Osten floss; denn die vordere Seite des Tempels schaute nach Osten. Das Wasser floss unterhalb der rechten Seite des Tempels herab, südlich vom Altar.", heading: "Die Tempelquelle: 47,1-12", footnotes: ["Sach 14,8; Joël 4,18; Ps 46,5; Offb 22,1"] },
      { number: 2, text: "Dann führte er mich durch das Nordtor hinaus und ließ mich außen herum zum äußeren Osttor gehen. Und ich sah das Wasser an der Südseite hervorrieseln." },
      { number: 3, text: "Der Mann ging nach Osten hinaus, mit der Messschnur in der Hand, maß tausend Ellen ab und ließ mich durch das Wasser gehen; das Wasser reichte mir bis an die Knöchel." },
      { number: 4, text: "Dann maß er wieder tausend Ellen ab und ließ mich durch das Wasser gehen; das Wasser reichte mir bis zu den Knien. Darauf maß er wieder tausend Ellen ab und ließ mich hindurchgehen; das Wasser ging mir bis an die Hüften." },
      { number: 5, text: "Und er maß noch einmal tausend Ellen ab. Da war es ein Fluss, den ich nicht mehr durchschreiten konnte; denn das Wasser war tief, ein Wasser, durch das man schwimmen musste, ein Fluss, den man nicht mehr durchschreiten konnte." },
      { number: 6, text: "Dann fragte er mich: Hast du es gesehen, Menschensohn? Darauf führte er mich zurück, am Ufer des Flusses entlang." },
      { number: 7, text: "Als ich zurückging, sah ich an beiden Ufern des Flusses sehr viele Bäume." },
      { number: 8, text: "Er sagte zu mir: Dieses Wasser fließt in den östlichen Bezirk, es strömt in die Araba hinab und läuft in das Meer, in das Meer mit dem salzigen Wasser. So wird das salzige Wasser gesund.", footnotes: ["mit dem salzigen Wasser: Text korr."] },
      { number: 9, text: "Wohin der Fluss gelangt, da werden alle Lebewesen, alles, was sich regt, leben können und sehr viele Fische wird es geben. Weil dieses Wasser dort hinkommt, werden (die Fluten) gesund; wohin der Fluss kommt, dort bleibt alles am Leben.", footnotes: ["der Fluss: Text korr. nach den alten Übersetzungen."] },
      { number: 10, text: "Von En-Gedi bis En-Eglajim werden Fischer am Ufer des Meeres stehen und ihre Netze zum Trocknen ausbreiten. Alle Arten von Fischen wird es geben, so zahlreich wie die Fische im großen Meer." },
      { number: 11, text: "Die Lachen und Tümpel aber sollen nicht gesund werden; sie sind für die Salzgewinnung bestimmt." },
      { number: 12, text: "An beiden Ufern des Flusses wachsen alle Arten von Obstbäumen. Ihr Laub wird nicht welken und sie werden nie ohne Frucht sein. Jeden Monat tragen sie frische Früchte; denn das Wasser des Flusses kommt aus dem Heiligtum. Die Früchte werden als Speise und die Blätter als Heilmittel dienen." },
      { number: 13, text: "So spricht Gott, der Herr: Das ist die Grenze, innerhalb derer ihr das Land als Erbbesitz an die zwölf Stämme Israels verteilen sollt. Josef erhält das Doppelte.", heading: "Die Verteilung des Landes: 47,13 - 48,29" },
      { number: 14, text: "Jeder einzelne soll darin seinen Erbbesitz erhalten; denn ich habe meine Hand (zum Schwur) erhoben, es euren Vätern zu geben. Darum soll euch dieses Land als Erbbesitz zufallen." },
      { number: 15, text: "Das ist die Grenze des Landes im Norden: vom großen Meer in Richtung Hetlon bis Lebo-Hamat, Zedad,", footnotes: ["(15-20) Num 34,1-12", "Lebo-Hamat: Text korr. nach G; vgl. V. 20."] },
      { number: 16, text: "Berota, Sibrajim, das zwischen dem Gebiet von Damaskus und dem Gebiet von Hamat liegt, und bis Hazar-Enan am Rand des Haurangebirges.", footnotes: ["Hazar-Enan: vgl. V. 17."] },
      { number: 17, text: "Die Grenze läuft also vom Meer nach Hazar-Enan, wobei das Gebiet von Damaskus und Zafon und ebenso das Gebiet von Hamat im Norden liegt. Das ist die Nordgrenze." },
      { number: 18, text: "Im Osten bildet der Jordan die Grenze zwischen Gilead und dem Land Israel (von Hazar-Enan), das zwischen dem Haurangebirge und Damaskus liegt, bis hinab zum östlichen Meer und Tamar: Das ist die Ostgrenze.", footnotes: ["Tamar: vgl. V. 19."] },
      { number: 19, text: "Die Südgrenze im Negeb: von Tamar bis zu den Quellen von Meribat-Kadesch und dem Bach, der ins große Meer fließt. Das ist die Südgrenze im Negeb.", footnotes: ["Num 20,13; 34,5"] },
      { number: 20, text: "Im Westen bildet das große Meer die Grenze bis zur Höhe von Lebo-Hamat. Das ist die Westgrenze." },
      { number: 21, text: "Dieses Land sollt ihr unter die Stämme Israels aufteilen." },
      { number: 22, text: "Ihr sollt es als Erbbesitz unter euch und unter die Fremden verlosen, die bei euch leben und die bei euch Söhne und Töchter gezeugt haben. Sie sollen für euch wie einheimische Israeliten sein und sollen sich mit euch zusammen ihren Erbbesitz mitten unter den Stämmen Israels erlosen.", footnotes: ["Lev 19,33f"] },
      { number: 23, text: "In dem Stamm, bei dem der Fremde lebt, sollt ihr ihm seinen Erbbesitz zuteilen - Spruch Gottes, des Herrn." },
    ] },
    { number: 48, verses: [
      { number: 1, text: "Das sind die Namen der Stämme: Im Norden, entlang dem Weg von Hetlon nach Lebo-Hamat und nach Hazar-Enan - das Gebiet von Damaskus liegt dabei nördlich, seitlich von Hamat - gehört von Osten nach Westen Dan ein Anteil." },
      { number: 2, text: "Neben dem Gebiet Dans von Osten nach Westen Ascher - ein Anteil;" },
      { number: 3, text: "neben dem Gebiet Aschers von Osten nach Westen Naftali - ein Anteil;" },
      { number: 4, text: "neben dem Gebiet Naftalis von Osten nach Westen Manasse - ein Anteil;" },
      { number: 5, text: "neben dem Gebiet Manasses von Osten nach Westen Efraim - ein Anteil;" },
      { number: 6, text: "neben dem Gebiet Efraims von Osten nach Westen Ruben - ein Anteil;" },
      { number: 7, text: "neben dem Gebiet Rubens von Osten nach Westen Juda - ein Anteil." },
      { number: 8, text: "Neben dem Gebiet Judas liegt von Osten nach Westen das Land, das ihr (dem Herrn) als Abgabe entrichten sollt, 25000 Ellen breit und genauso lang wie jeder andere Anteil von Osten nach Westen. In der Mitte dieses Stückes liegt das Heiligtum.", footnotes: ["(8-20) 45,1-8"] },
      { number: 9, text: "Das Land, das ihr dem Herrn als Abgabe entrichtet, hat eine Länge von 25000 Ellen und eine Breite von 20000 Ellen.", footnotes: ["9.13: 20000 Ellen: Text korr., vgl. 45,1."] },
      { number: 10, text: "Die heilige Abgabe soll wie folgt eingeteilt werden: Den Priestern soll ein Stück gehören, das im Norden 25000 Ellen lang ist, im Westen und im Osten 10000 Ellen breit und im Süden auch 25000 Ellen lang. Das Heiligtum des Herrn soll in der Mitte liegen." },
      { number: 11, text: "Den geweihten Priestern aus den Nachkommen Zadoks, die ihren Dienst verrichtet und mich nicht verlassen haben, als die Israeliten ebenso in die Irre gingen wie die Leviten," },
      { number: 12, text: "ihnen soll als Abgabe ein Stück von dem Land gehören, das ihr (dem Herrn) als Abgabe entrichtet habt; dieses Stück ist hochheilig und liegt neben dem Gebiet der Leviten.", footnotes: ["Lev 25,32-34"] },
      { number: 13, text: "Den Leviten soll wie den Priestern ein Gebiet von 25000 Ellen Länge und 10000 Ellen Breite gehören. Das Ganze hat also eine Länge von 25000 Ellen und eine Breite von 20000 Ellen." },
      { number: 14, text: "Man darf nichts davon verkaufen oder zum Tausch anbieten, noch darf man dieses beste Stück des Landes auf andere übertragen; denn es ist dem Herrn heilig." },
      { number: 15, text: "Die 5000 Ellen, die längs der 25000 Ellen in der Breite noch übrig bleiben, sind nicht heilig, sondern gehören der Stadt als Wohngebiet und Weidefläche. Die Stadt soll mitten darin liegen.", footnotes: ["Offb 21,12-17"] },
      { number: 16, text: "Ihre Maße sollen im Norden 4500 Ellen und im Süden 4500 Ellen betragen, ebenso 4500 Ellen im Osten und 4500 Ellen im Westen." },
      { number: 17, text: "Die Weidefläche der Stadt erstreckt sich 250 Ellen nach Norden, 250 Ellen nach Süden, 250 Ellen nach Osten und 250 Ellen nach Westen." },
      { number: 18, text: "Was dann der Länge nach noch übrig ist längs der heiligen Abgabe 10000 Ellen nach Osten und 10000 Ellen nach Westen [es liegt längs der heiligen Abgabe] -, von dessen Erträgen sollen sich die Arbeiter der Stadt ernähren." },
      { number: 19, text: "Die Arbeiter der Stadt, die dieses Stück bebauen, sollen aus allen Stämmen Israels sein." },
      { number: 20, text: "Die ganze Abgabe soll 25000 mal 25000 Ellen betragen. Dieses ganze Quadrat einschließlich des Eigentums der Stadt sollt ihr als heilige Abgabe entrichten." },
      { number: 21, text: "Was zu beiden Seiten der heiligen Abgabe und des Besitzes der Stadt übrig bleibt, von den 25000 Ellen der (Ostseite der ganzen) Abgabe bis zur Ostgrenze (des Landes) und von den 25000 Ellen der Westseite (der Abgabe) bis zur Westgrenze (des Landes), diese (beiden) Stücke entlang den Stammesanteilen sollen dem Fürsten gehören. Die heilige Abgabe und das Tempelheiligtum liegen mitten darin." },
      { number: 22, text: "Der Besitz der Leviten und der Besitz der Stadt soll mitten in dem Gebiet liegen, das dem Fürsten gehört, und das Gebiet des Fürsten liegt zwischen dem Gebiet Judas und dem Gebiet Benjamins." },
      { number: 23, text: "Nun folgen die übrigen Stämme: Von Osten nach Westen Benjamin - ein Anteil;" },
      { number: 24, text: "neben dem Gebiet Benjamins von Osten nach Westen Simeon - ein Anteil;" },
      { number: 25, text: "neben dem Gebiet Simeons von Osten nach Westen Issachar - ein Anteil;" },
      { number: 26, text: "neben dem Gebiet Issachars von Osten nach Westen Sebulon - ein Anteil;" },
      { number: 27, text: "neben dem Gebiet Sebulons von Osten nach Westen Gad - ein Anteil." },
      { number: 28, text: "Neben dem Gebiet Gads verläuft im Negeb die Südgrenze; sie führt von Tamar zu den Quellen von Meribat-Kadesch und zu dem Bach, der ins große Meer fließt." },
      { number: 29, text: "Das ist das Land, das ihr als Erbbesitz an die Stämme Israels verlosen sollt, und das sind ihre Anteile - Spruch Gottes, des Herrn." },
      { number: 30, text: "Das sind die Ausgänge der Stadt. Die Stadttore sind nach den Stämmen Israels benannt: auf der Nordseite, die 4500 Ellen misst, drei Tore: ein Tor ist nach Ruben benannt, ein Tor nach Juda und ein Tor nach Levi;", heading: "Die heilige Stadt: 48,30-35" },
      { number: 32, text: "auf der Ostseite, die 4500 Ellen misst, drei Tore: ein Tor nach Josef, ein Tor nach Benjamin und ein Tor nach Dan;" },
      { number: 33, text: "auf der Südseite, die 4500 Ellen misst, drei Tore: ein Tor nach Simeon, ein Tor nach Issachar und ein Tor nach Sebulon;" },
      { number: 34, text: "auf der Westseite, die 4500 Ellen misst, drei Tore: ein Tor nach Gad, ein Tor nach Ascher und ein Tor nach Naftali." },
      { number: 35, text: "Der Umfang der Stadt beträgt 18000 Ellen. Und der Name der Stadt soll von heute an sein: Hier ist der Herr.", footnotes: ["Offb 21,3"] },
    ] },
  ],
};
