import { Book } from "@/lib/types";

export const isaiah: Book = {
  id: "isaiah",
  name: "Jesaja",
  shortName: "Jes",
  testament: "old",
  introduction: "Die Bücher der Propheten Der Prophet Jesaja aus Jerusalem (etwa 740-701 v. Chr.) hat mit seiner Verkündigung den Grundstock des Buches geschaffen, das seinen Namen trägt. Aus dem Inhalt der von ihm überlieferten Prophetenworte schließt man, dass er, im Todesjahr des Königs Usija (739 v. Chr.) berufen (Kap. 6), in vier wichtigen Perioden der Geschichte Judas das Wort Jahwes verkündete. Zunächst befasste er sich in Drohworten (Kap. 1 - 3 und 5) mit den Zuständen in Juda und Jerusalem. Im Syrischefraimitischen Krieg (734/33 v. Chr.) warnte er vor einem Bündnis mit Assur (7,1 - 9,6). Beim ersten Versuch des Königs Hiskija, sich an einem Aufstand gegen die Assyrer zu beteiligen (716-711 v. Chr.), erhob er warnend seine Stimme (Kap. 18; 20; 28 - 30; auch Teile von Kap. 14). Endlich trat er beim Angriff des assyrischen Königs Sanherib (um 701 v. Chr.) auf, um das rettende Eingreifen Gottes gegen den überheblichen Feind anzusagen (Worte in den Kapiteln 10; 14; 28 - 30). Vom Leben des Propheten ist uns wenig bekannt. Er dürfte den gebildeten höheren Kreisen entstammen; anscheinend war er mit einer prophetisch begabten Frau verheiratet (8,3) und hatte wohl mehrere Kinder (vgl. 7,3; 8,1-3). Die Worte Jesajas, die in Kap. 1 - 39 überliefert sind, wurden von seinen Schülern (8,16) und anderen vielfach kommentiert und beträchtlich erweitert; so wurden sie später in seinem Geist an jeweils neue Situationen angepasst. Dem Propheten kam es vor allem darauf an, die Menschen zu Glauben und Vertrauen gegenüber dem heiligen und erhabenen Gott aufzurufen, der nach seinem weisen Plan die Weltgeschichte und besonders die Geschicke seines Volkes lenkt. Dieses gläubige Hinschauen auf den Herrn bedeutet zugleich, seinem Willen zu entsprechen im politischen wie im sozialen Handeln, insbesondere den Armen gegenüber. Der Ernst der prophetischen Botschaft Jesajas drückt sich vor allem in den scharfen Gerichtsworten aus. Verschiedene Überschriften (1,1; 2,1; 13,1) weisen darauf hin, dass der erste Teil des Buches (Kap. 1 - 39) eine lange Entstehungsgeschichte hat. Noch heute ist ihm anzusehen, dass einer ersten Sammlung (Kap. 2 - 5 und 9,6 - 11,16) die sogenannte Denkschrift des Propheten (6,1 - 9,5) eingefügt und schließlich Kap. 1 als eine Art Zusammenfassung seiner Worte vorausgestellt wurde. Jes 24 - 27 sind apokalyptische Texte (die sog. »große JesajaApokalypse«) und tragen das Kolorit einer späten Zeit an sich. Die Jesajaerzählungen (Kap. 36 - 39) sind fast ganz aus dem zweiten Königsbuch übernommen. So entstand ein Prophetenbuch, das wie bei der Denkschrift in großen Zügen das Grundschema erkennen lässt, nach dem die prophetischen Bücher zusammengestellt wurden: Drohungen gegen das eigene Volk (Kap. 1 - 12); Drohungen gegen andere Völker (Kap. 13 23); Verheißungen, die hier allerdings Drohreden einschließen, für das eigene Volk (Kap. 24 - 35); ein geschichtlicher Anhang rundet das Ganze ab (Kap. 36 - 39). Für das Neue Testament und die christliche Verkündigung sind in besonderer Weise die messianischen Texte wichtig (Kap. 7; 9,1-6; 11; 28,16f). In eine ganz andere Welt versetzen uns die Kapitel 40 - 55. Hier befindet sich Juda im Babylonischen Exil; zu ihm spricht ein Prophet, der den Auftrag erhält (40,1-11), das unmittelbar bevorstehende rettende Eingreifen Gottes anzukündigen. Gott erweckt Kyrus (44,28 - 45,8), damit er das Volk aus der Gefangenschaft entlässt. Dann wird Jahwe, der als der alleinige durch Schöpfung und geschichtliches Handeln ausgewiesene Gott über die Völker herrscht, sein Volk in einem neuen, wunderbaren Exodus nach Zion heimführen. Dort wird er als König Israels herrschen, das er wieder aufblühen lässt. In diese überschwängliche Heilsprophetie sind vier Texte über den »Gottesknecht« eingefügt (42,1-9; 49,1-9; 50,4-9; 52,13 - 53,12), der als der Erwählte Jahwes zum »Bund des Volkes und Licht der Heiden« bestimmt, wie ein Prophet gesendet, mit Aufgaben für Israel und die Völker betraut, nach Leiden und Todesnot errettet und schließlich verherrlicht wird. Durch die sogenannten Gottesknechttexte hat dieser Teil des Jesajabuches weit in die Zukunft gewirkt. Die neutestamentliche Urgemeinde nahm sie auf und sah sie im Wirken und im Weg Jesu erfüllt. Der Verfasser von Jes 40 - 55 ist unbekannt. Man nennt ihn Deuterojesaja (Zweiter Jesaja). In Jes 56 - 66 wechselt die Situation wieder. Hier sind verschiedenartige Texte zusammengestellt, die sich an die Heimgekehrten richten. Sie schöpfen vielfach aus Jes 40 - 55, sprechen aber deutlich die veränderte Lage an. Ein Prophet erhebt seine Stimme (Kap. 60 - 62), der sich durch Gottes Geist zur Verkündigung einer frohen Botschaft gesandt weiß (Kap. 61; vgl. Lk 4,16-19); man nennt ihn Tritojesaja (Dritter Jesaja). Dieser Teil des Jesajabuches enthält in loser Anordnung Heils- und Gerichtsworte. Besonders hervorzuheben sind die Verheißung für die Fremden (56,1-8), die Mahnung zur wahren Frömmigkeit (58,1-14), das leuchtende Bild von der Gottesstadt (Kap. 60 und 62), das Gebet zu Gott, unserem Vater, in Gestalt einer Volksklage (63,7 - 64,11). Mit dem Blick auf den neuen Himmel und die neue Erde (Kap. 66) schließt das Jesajabuch. Unter den Funden von Qumran befand sich eine vollständig erhaltene Jesaja-Handschrift aus dem 2. Jahrhundert v. Chr. Sie ist damit etwa tausend Jahre älter als die meisten bisher bekannten hebräischen Handschriften des Alten Testaments.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Vision des Jesaja, des Sohnes des Amoz, über Juda und Jerusalem, die er zu der Zeit hatte, als Usija, Jotam, Ahas und Hiskija Könige von Juda waren.", heading: "I. Teil Der erste Jesaja (Protojesaja): 1,1 - 39,8 | Worte über Juda und Jerusalem aus der Frühzeit des Propheten: 1,1 - 12,6 | Einleitung: 1,1", footnotes: ["Mi 1,1", "Die Überschrift hat wohl ursprünglich Jes 1 - 12 eingeleitet."] },
      { number: 2, text: "Hört, ihr Himmel! Erde, horch auf! / Denn der Herr spricht: Ich habe Söhne großgezogen und emporgebracht, / doch sie sind von mir abgefallen.", heading: "Die Untreue des Volkes: 1,2-4", footnotes: ["23,4", "2-31: Die Einleitungsrede bietet wie eine vorangestellte Zusammenfassung wichtige Themen der Verkündigung des Propheten."] },
      { number: 3, text: "Der Ochse kennt seinen Besitzer / und der Esel die Krippe seines Herrn; Israel aber hat keine Erkenntnis, / mein Volk hat keine Einsicht.", footnotes: ["Lk 2,16"] },
      { number: 4, text: "Weh dem sündigen Volk, der schuldbeladenen Nation, / der Brut von Verbrechern, den verkommenen Söhnen! Sie haben den Herrn verlassen, / den Heiligen Israels haben sie verschmäht / und ihm den Rücken gekehrt.", footnotes: ["30,9", "Der «Heilige Israels» ist in Jes Bezeichnung für Jahwe, den heiligen Gott (vgl. 6,3)."] },
      { number: 5, text: "Wohin soll man euch noch schlagen? / Ihr bleibt ja doch abtrünnig. Der ganze Kopf ist wund, / das ganze Herz ist krank:", heading: "Die Folgen der Sünde: 1,5-9" },
      { number: 6, text: "Vom Kopf bis zum Fuß kein heiler Fleck, / nur Beulen, Striemen und frische Wunden, sie sind nicht ausgedrückt, nicht verbunden, / nicht mit Öl gelindert." },
      { number: 7, text: "Euer Land ist verödet, / eure Städte sind niedergebrannt. Fremde verzehren vor euren Augen den Ertrag eurer Äcker; / verödet wie das zerstörte Sodom ist euer Land.", footnotes: ["Sodom: Text korr. nach V. 9."] },
      { number: 8, text: "Die Tochter Zion steht verlassen da / wie eine Hütte im Weinberg, wie eine Wächterhütte im Gurkenfeld / [wie eine belagerte Stadt].", footnotes: ["24,20"] },
      { number: 9, text: "Hätte der Herr der Heere nicht einen Rest für uns übrig gelassen, / wir wären wie Sodom geworden, / wir glichen Gomorra.", footnotes: ["Gen 19,1-29"] },
      { number: 10, text: "Hört das Wort des Herrn, ihr Herrscher von Sodom! / Vernimm die Weisung unseres Gottes, du Volk von Gomorra!", heading: "Der falsche und der wahre Gottesdienst: 1,10-17", footnotes: ["Angeredet ist die Stadt Jerusalem."] },
      { number: 11, text: "Was soll ich mit euren vielen Schlachtopfern?, / spricht der Herr. Die Widder, die ihr als Opfer verbrennt, / und das Fett eurer Rinder habe ich satt; / das Blut der Stiere, der Lämmer und Böcke ist mir zuwider.", footnotes: ["Am 5,21-23"] },
      { number: 12, text: "Wenn ihr kommt, um mein Angesicht zu schauen - / wer hat von euch verlangt, dass ihr meine Vorhöfe zertrampelt?" },
      { number: 13, text: "Bringt mir nicht länger sinnlose Gaben, / Rauchopfer, die mir ein Gräuel sind. Neumond und Sabbat und Festversammlung - / Frevel und Feste - ertrage ich nicht." },
      { number: 14, text: "Eure Neumondfeste und Feiertage / sind mir in der Seele verhasst, sie sind mir zur Last geworden, / ich bin es müde, sie zu ertragen." },
      { number: 15, text: "Wenn ihr eure Hände ausbreitet, / verhülle ich meine Augen vor euch. Wenn ihr auch noch so viel betet, / ich höre es nicht. / Eure Hände sind voller Blut." },
      { number: 16, text: "Wascht euch, reinigt euch! / Lasst ab von eurem üblen Treiben! / Hört auf, vor meinen Augen Böses zu tun!" },
      { number: 17, text: "Lernt, Gutes zu tun! / Sorgt für das Recht! Helft den Unterdrückten! / Verschafft den Waisen Recht, / tretet ein für die Witwen!", footnotes: ["Am 5,24", "den Unterdrückten: Text korr. nach den alten Übersetzungen."] },
      { number: 18, text: "Kommt her, wir wollen sehen, / wer von uns Recht hat, / spricht der Herr. Wären eure Sünden auch rot wie Scharlach, / sie sollen weiß werden wie Schnee. Wären sie rot wie Purpur, / sie sollen weiß werden wie Wolle.", heading: "Der Rechtsstreit Gottes mit seinem Volk: 1,18-20" },
      { number: 19, text: "Wenn ihr bereit seid zu hören, / sollt ihr den Ertrag des Landes genießen." },
      { number: 20, text: "Wenn ihr aber trotzig seid und euch weigert, / werdet ihr vom Schwert gefressen. / Ja, der Mund des Herrn hat gesprochen." },
      { number: 21, text: "Ach, sie ist zur Dirne geworden, die treue Stadt. / Einst war dort das Recht in voller Geltung, die Gerechtigkeit war dort zu Hause, / jetzt aber herrschen die Mörder.", heading: "Das Gericht über Jerusalem: 1,21-31" },
      { number: 22, text: "Dein Silber wurde zu Schlacke, / dein Wein ist verwässert.", footnotes: ["Ez 22,18"] },
      { number: 23, text: "Deine Fürsten sind Aufrührer / und eine Bande von Dieben, alle lassen sich gerne bestechen / und jagen Geschenken nach. Sie verschaffen den Waisen kein Recht, / die Sache der Witwen gelangt nicht vor sie." },
      { number: 24, text: "Darum - Spruch Gottes, des Herrn der Heere, / des Starken Israels: Weh meinen Gegnern, / ich will Rache nehmen an ihnen, / mich rächen an meinen Feinden.", footnotes: ["Der «Starke Israels» ist Bezeichnung für Jahwe (vgl. «der Starke Jakobs» Gen 49,24)."] },
      { number: 25, text: "Ich will meine Hand gegen dich wenden, / deine Schlacken will ich mit Lauge ausschmelzen, / all dein Blei schmelze ich aus.", footnotes: ["Ez 22,18f; Mal 3,3; Spr 25,4"] },
      { number: 26, text: "Ich will dir wieder Richter geben wie am Anfang / und Ratsherrn wie zu Beginn. Dann wird man dich die Burg der Gerechtigkeit nennen, / die treue Stadt." },
      { number: 27, text: "Zion wird durch das Recht gerettet, / wer dort umkehrt, durch die Gerechtigkeit." },
      { number: 28, text: "Doch alle Abtrünnigen und Sünder werden zerschmettert. / Wer den Herrn verlässt, wird vernichtet." },
      { number: 29, text: "Ihr werdet in Schande stürzen / wegen der Eichen, die euch gefallen, und werdet euch schämen / wegen der (heiligen) Haine, die ihr so gern habt.", footnotes: ["65,3", "Heidnische Kultbräuche werden oft bei heiligen Bäumen vollzogen (vgl. Jer 2,20; Hos 4,13). - Ihr werdet: Text korr. nach H-Handschriften und der aramäischen Übersetzung."] },
      { number: 30, text: "Ihr werdet wie eine Eiche, deren Blätter verwelken, / und wie ein Garten, dessen Wasser versiegt ist." },
      { number: 31, text: "Dann wird der Starke zu Werg / und sein Tun zum zündenden Funken; beide verbrennen zusammen / und niemand kann löschen." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Das Wort, das Jesaja, der Sohn des Amoz, in einer Vision über Juda und Jerusalem gehört hat.", heading: "Jerusalem als Mittelpunkt des messianischen Reiches: 2,1-5" },
      { number: 2, text: "Am Ende der Tage wird es geschehen: Der Berg mit dem Haus des Herrn / steht fest gegründet als höchster der Berge; er überragt alle Hügel. / Zu ihm strömen alle Völker.", footnotes: ["Mi 4,1-3"] },
      { number: 3, text: "Viele Nationen machen sich auf den Weg. / Sie sagen: Kommt, wir ziehen hinauf zum Berg des Herrn / und zum Haus des Gottes Jakobs. Er zeige uns seine Wege, / auf seinen Pfaden wollen wir gehen. Denn von Zion kommt die Weisung des Herrn, / aus Jerusalem sein Wort.", footnotes: ["51,4"] },
      { number: 4, text: "Er spricht Recht im Streit der Völker, / er weist viele Nationen zurecht. Dann schmieden sie Pflugscharen aus ihren Schwertern / und Winzermesser aus ihren Lanzen. Man zieht nicht mehr das Schwert, Volk gegen Volk, / und übt nicht mehr für den Krieg.", footnotes: ["Joël 4,10"] },
      { number: 5, text: "Ihr vom Haus Jakob, kommt, / wir wollen unsere Wege gehen im Licht des Herrn." },
      { number: 6, text: "Ja, du hast dein Volk, das Haus Jakob, verstoßen; / denn es ist voll von Zauberern und Wahrsagern wie das Volk der Philister / und überflutet von Fremden.", heading: "Der Tag Jahwes: 2,6-22", footnotes: ["Übersetzung unsicher. - von Zauberern: Text korr.; H: vom Osten."] },
      { number: 7, text: "Sein Land ist voll Silber und Gold, / zahllos sind seine Schätze. Sein Land ist voll von Pferden, / zahllos sind seine Wagen.", footnotes: ["31,1"] },
      { number: 8, text: "Sein Land ist voll von Götzen. / Alle beten das Werk ihrer Hände an, / das ihre Finger gemacht haben." },
      { number: 9, text: "Doch die Menschen müssen sich ducken, / jeder Mann muss sich beugen. / Verzeih ihnen nicht!", footnotes: ["2,11.17; 5,15", "9c: Sinn nicht klar."] },
      { number: 10, text: "Verkriech dich im Felsen, / verbirg dich im Staub vor dem Schrecken des Herrn / und seiner strahlenden Pracht!", footnotes: ["2,19.21"] },
      { number: 11, text: "Da senken sich die stolzen Augen der Menschen, / die hochmütigen Männer müssen sich ducken, / der Herr allein ist erhaben an jenem Tag.", footnotes: ["2,9.17; 5,15"] },
      { number: 12, text: "Denn der Tag des Herrn der Heere kommt / über alles Stolze und Erhabene, / über alles Hohe - es wird erniedrigt -," },
      { number: 13, text: "über alle hoch ragenden Zedern des Libanon / und alle Eichen des Baschan,", footnotes: ["Sach 11,2"] },
      { number: 14, text: "über alle hohen Berge und alle stattlichen Hügel,", footnotes: ["30,25"] },
      { number: 15, text: "über jeden hohen Turm und jede steile Mauer," },
      { number: 16, text: "über alle Tarschisch-Schiffe / und die kostbaren Segler.", footnotes: ["Ps 48,8", "Tarschisch-Schiffe: seetüchtige Schiffe, die bis Tarschisch (Tartessus) in Spanien fahren konnten."] },
      { number: 17, text: "Die stolzen Menschen müssen sich ducken, / die hochmütigen Männer sich beugen, / der Herr allein ist erhaben an jenem Tag.", footnotes: ["2,9.11; 5,15"] },
      { number: 18, text: "Die Götzen aber schwinden alle dahin.", footnotes: ["schwinden dahin: nach dem Qumran-Text."] },
      { number: 19, text: "Verkriecht euch in Felshöhlen und Erdlöchern / vor dem Schrecken des Herrn und vor seiner strahlenden Pracht, / wenn er sich erhebt, um die Erde zu erschrecken.", footnotes: ["2,10"] },
      { number: 20, text: "An jenem Tag nimmt jeder seine silbernen und goldenen Götzen, / die er gemacht hat, um sie anzubeten, / und wirft sie den Fledermäusen und Ratten hin;", footnotes: ["den Ratten: nach dem Qumran-Text."] },
      { number: 21, text: "und man wird sich in den Spalten und Höhlen der Felsen verkriechen / vor dem Schrecken des Herrn und vor seiner strahlenden Pracht, / wenn er sich erhebt, um die Erde zu erschrecken.", footnotes: ["2,10"] },
      { number: 22, text: "[Lasst doch ab vom Menschen; / in seiner Nase ist nur ein Lufthauch. / Was bedeutet er schon?]" },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Seht, Gott, der Herr der Heere, / nimmt Jerusalem und Juda jede Stütze und Hilfe [jede Unterstützung mit Brot / und jede Unterstützung mit Wasser]:", heading: "Die Beseitigung der führenden Schichten: 3,1-15", footnotes: ["Lev 26,26"] },
      { number: 2, text: "den Helden und Krieger, / den Richter und den Propheten, / den Wahrsager und den Ältesten," },
      { number: 3, text: "den Hauptmann, den Höfling, den Ratsherrn, / den weisen Zauberer und den klugen Beschwörer." },
      { number: 4, text: "Ich mache junge Burschen zu ihren Fürsten. / Willkür soll über sie herrschen.", footnotes: ["Koh 10,16"] },
      { number: 5, text: "Dann bedrängt im Volk einer den andern / und jeder bedrängt seinen Nächsten. Die Jungen sind frech zu den Alten, / die Geringen zu den geachteten Männern." },
      { number: 6, text: "Dann fasst einer im Haus seines Vaters / den Bruder am Arm und sagt: Du hast noch einen Mantel, / du musst unser Anführer sein. / Sei der Herr dieser Trümmer!" },
      { number: 7, text: "Der aber wird an jenem Tag schreien: / Ich bin doch kein Arzt und in meinem Haus gibt es kein Brot / und es gibt keinen Mantel. / Macht mich nicht zum Führer des Volkes!" },
      { number: 8, text: "Ja, Jerusalem stürzt und Juda fällt; / denn ihre Worte und ihre Taten richten sich gegen den Herrn, / sie trotzen den Augen seiner Majestät." },
      { number: 9, text: "Ihre frechen Gesichter klagen sie an, / wie Sodom reden sie ganz offen von ihren Sünden. / Weh ihnen, sie bereiten sich selber ihr Unglück." },
      { number: 10, text: "Wohl dem Gerechten, denn ihm geht es gut; / er wird die Frucht seiner Taten genießen.", footnotes: ["Wohl dem Gerechten: Text korr."] },
      { number: 11, text: "Weh dem Frevler, ihm geht es schlecht; / denn was er selber getan hat, das wird man ihm antun." },
      { number: 12, text: "Mein Volk - seine Herrscher sind voller Willkür, / Wucherer beherrschen das Volk. Mein Volk, deine Führer führen dich in die Irre, / sie bringen dich ab vom richtigen Weg.", footnotes: ["9,15"] },
      { number: 13, text: "Der Herr steht bereit, um Recht zu sprechen; / er steht da, um sein Volk zu richten.", footnotes: ["sein Volk: Text korr. nach G."] },
      { number: 14, text: "Der Herr geht ins Gericht / mit den Ältesten und den Fürsten seines Volkes: Ihr, ihr habt den Weinberg geplündert; / eure Häuser sind voll von dem, was ihr den Armen geraubt habt." },
      { number: 15, text: "Wie kommt ihr dazu, mein Volk zu zerschlagen? / Ihr zermalmt das Gesicht der Armen - / Spruch des Herrn der Heere.", footnotes: ["Am 2,6-8"] },
      { number: 16, text: "Der Herr sprach: Weil die Töchter Zions hochmütig sind, ihre Hälse recken und mit verführerischen Blicken daherkommen, immerzu trippelnd daherstolzieren und mit ihren Fußspangen klirren,", heading: "Die hochmütigen Frauen von Jerusalem: 3,16 - 4,1", footnotes: ["32,9-14", "16-23: Die hebräischen Wörter für Mode- und Schmuckartikel sind nicht immer sicher zu deuten."] },
      { number: 17, text: "darum wird der Herr den Scheitel der Töchter Zions mit Schorf bedecken und ihre Schläfen kahl werden lassen." },
      { number: 18, text: "An jenem Tag wird ihnen der Herr ihren Schmuck wegnehmen: die Fußspangen, die kleinen Sonnen und Monde," },
      { number: 19, text: "die Ohrgehänge und Armkettchen, die Schleier" },
      { number: 20, text: "und Turbane, die Fußkettchen und die Prachtgürtel, die Riechfläschchen und die Amulette," },
      { number: 21, text: "die Fingerringe und Nasenreife," },
      { number: 22, text: "die Festkleider und Umhänge, die Umschlagtücher und Täschchen" },
      { number: 23, text: "und die Spiegel, die feinen Schleier, die Schals und Kopftücher." },
      { number: 24, text: "Dann habt ihr Moder statt Balsam, / Strick statt Gürtel, Glatze statt kunstvolle Locken, Trauergewand statt Festkleid, / ja, Schande statt Schönheit.", footnotes: ["24d: Schande: nach dem Qumran-Text."] },
      { number: 25, text: "Deine Männer sterben durchs Schwert, / deine jungen Krieger fallen im Kampf." },
      { number: 26, text: "Zions Tore ächzen und klagen; / ausgeplündert sitzt es am Boden." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "An jenem Tag klammern sich sieben Frauen an einen einzigen Mann und sagen: Wir wollen unser eigenes Brot essen und uns selber kleiden, nur lass uns deinen Namen tragen, nimm die Schande von uns!" },
      { number: 2, text: "An jenem Tag wird, was der Herr sprossen lässt, für alle Israeliten, die entronnen sind, eine Zierde und Ehre sein; die Früchte des Landes sind ihr Stolz und Ruhm.", heading: "Die Rettung der Übriggebliebenen: 4,2-6", footnotes: ["was der Herr sprossen lässt: Anklang an den messianischen Ausdruck «Spross» in Jer 23,5; 33,15; Sach 3,8; 6,12."] },
      { number: 3, text: "Dann wird der Rest von Zion und wer in Jerusalem noch übrig ist, heilig genannt werden, jeder, der in Jerusalem in das Verzeichnis derer, die am Leben bleiben sollen, eingetragen ist.", footnotes: ["Das Verzeichnis erinnert an Bürgerlisten (vgl. Ez 13,9; Ps 87,6)."] },
      { number: 4, text: "Wenn der Herr durch den Sturm des Gerichts und den Sturm der Läuterung von den Töchtern Zions den Kot abgewaschen und aus Jerusalems Mitte die Blutschuld weggespült hat,", footnotes: ["Andere Übersetzungsmöglichkeit: den Geist des Gerichts und den Geist der Läuterung."] },
      { number: 5, text: "dann kommt er und über dem ganzen Gebiet des Berges Zion und seinen Festplätzen erscheint bei Tag eine Wolke und bei Nacht Rauch und eine strahlende Feuerflamme. Denn über allem liegt als Schutz und Schirm die Herrlichkeit des Herrn;", footnotes: ["Ex 13,21f", "kommt er: Text korr. nach G."] },
      { number: 6, text: "sie spendet bei Tag Schatten vor der Hitze und ist Zuflucht und Obdach bei Unwetter und Regen." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Ich will ein Lied singen von meinem geliebten Freund, / ein Lied vom Weinberg meines Liebsten. Mein Freund hatte einen Weinberg / auf einer fruchtbaren Höhe.", heading: "Das Lied vom Weinberg: 5,1-7", footnotes: ["(1-7) Mt 21,33-43", "Als Weinstock und Weinberg des Herrn (Jer 2,21; Ez 15; Ps 80,9-16) wird Israel bezeichnet. Das Weinberglied beginnt wie ein Liebeslied und wird dann zum Gerichtswort."] },
      { number: 2, text: "Er grub ihn um und entfernte die Steine / und bepflanzte ihn mit den edelsten Reben. Er baute mitten darin einen Turm / und hieb eine Kelter darin aus. Dann hoffte er, / dass der Weinberg süße Trauben brächte, / doch er brachte nur saure Beeren.", footnotes: ["27,2-4; Jer 2,21", "Die Kelter war ein im Felsen ausgehauener Steintrog."] },
      { number: 3, text: "Nun sprecht das Urteil, Jerusalems Bürger und ihr Männer von Juda, / im Streit zwischen mir und dem Weinberg!" },
      { number: 4, text: "Was konnte ich noch für meinen Weinberg tun, / das ich nicht für ihn tat? Warum hoffte ich denn auf süße Trauben? / Warum brachte er nur saure Beeren?" },
      { number: 5, text: "Jetzt aber will ich euch kundtun, / was ich mit meinem Weinberg mache: Ich entferne seine schützende Hecke; / so wird er zur Weide. Seine Mauer reiße ich ein; / dann wird er zertrampelt." },
      { number: 6, text: "Zu Ödland will ich ihn machen. / Man soll seine Reben nicht schneiden / und soll ihn nicht hacken; Dornen und Disteln werden dort wuchern. / Ich verbiete den Wolken, ihm Regen zu spenden.", footnotes: ["7,23-25"] },
      { number: 7, text: "Ja, der Weinberg des Herrn der Heere / ist das Haus Israel und die Männer von Juda sind die Reben, / die er zu seiner Freude gepflanzt hat. Er hoffte auf Rechtsspruch - / doch siehe da: Rechtsbruch, und auf Gerechtigkeit - / doch siehe da: Der Rechtlose schreit." },
      { number: 8, text: "Weh euch, die ihr Haus an Haus reiht / und Feld an Feld fügt, bis kein Platz mehr da ist / und ihr allein im Land ansässig seid.", heading: "Sechs Weherufe über das trotzige Israel: 5,8-24" },
      { number: 9, text: "Meine Ohren hören das Wort des Herrn der Heere: / Wahrhaftig, alle eure Häuser sollen veröden. So groß und schön sie auch sind: / Sie sollen unbewohnt sein.", footnotes: ["6,11", "hören: ergänzt nach G. - Die Strafe entspricht, auch im folgenden Wehruf (VV. 13f), dem Vergehen (V. 10)."] },
      { number: 10, text: "Ein Weinberg von zehn Morgen bringt nur ein Bat Wein, / ein Hómer Saatgut bringt nur ein Efa Korn.", footnotes: ["Morgen, wörtlich: Joch, Gespann."] },
      { number: 11, text: "Weh euch, die ihr schon früh am Morgen / hinter dem Bier her seid und sitzen bleibt bis spät in die Nacht, / wenn euch der Wein erhitzt." },
      { number: 12, text: "Bei ihren Gelagen spielt man Zither und Harfe, / Pauken und Flöten; aber was der Herr tut, beachten sie nicht, / was seine Hände vollbringen, sehen sie nicht.", footnotes: ["Ijob 21,12; Am 6,5f"] },
      { number: 13, text: "Darum muss mein Volk in die Verbannung; / denn es hat keine Erkenntnis. Seine Reichen sterben vor Hunger, / die Masse der Armen verschmachtet vor Durst.", footnotes: ["sterben: Text korr. nach den alten Übersetzungen. - die Masse der Armen, wörtlich: seine Masse."] },
      { number: 14, text: "Darum sperrt die Unterwelt ihren Rachen auf, / maßlos weit reißt sie ihr Maul auf, sodass des Volkes Pracht und Reichtum hinabfährt, / der ganze lärmende, johlende Haufen.", footnotes: ["Hab 2,5"] },
      { number: 15, text: "Die Menschen müssen sich ducken, / jeder Mann muss sich beugen, / die stolzen Augen werden sich senken.", footnotes: ["2,9.11.17"] },
      { number: 16, text: "Doch der Herr der Heere ist erhaben, / wenn er Gericht hält, durch seine Gerechtigkeit / erweist der heilige Gott sich als heilig." },
      { number: 17, text: "Dann grasen dort Lämmer wie auf der Weide, / in den Ruinen weiden fette Schafe." },
      { number: 18, text: "Weh euch, die ihr die Strafe wie mit Ochsenstricken herbeizieht / und die Sünde wie mit Wagenseilen.", footnotes: ["18a: mit Ochsenstricken: Text korr. in Anlehnung an V. 18b."] },
      { number: 19, text: "Ihr sagt: Was er tun will, das tue er schnell; / er soll sich beeilen, damit wir es sehen; was der Heilige Israels plant, treffe bald ein; / wir wollen es wissen." },
      { number: 20, text: "Weh denen, die das Böse gut und das Gute böse nennen, / die die Finsternis zum Licht und das Licht zur Finsternis machen, / die das Bittere süß und das Süße bitter machen." },
      { number: 21, text: "Weh denen, die in ihren eigenen Augen weise sind / und sich selbst für klug halten.", footnotes: ["Spr 3,7; Röm 12,16"] },
      { number: 22, text: "Weh denen, die Helden sind, / wenn es gilt, Wein zu trinken, / und tapfer, wenn es gilt, starke Getränke zu brauen," },
      { number: 23, text: "die den Schuldigen für Bestechungsgeld freisprechen / und dem Gerechten sein Recht vorenthalten.", footnotes: ["Ex 23,7f; Spr 17,15"] },
      { number: 24, text: "Darum: Wie des Feuers Zunge die Stoppeln frisst / und wie das Heu in der Flamme zusammensinkt, so soll ihre Wurzel verfaulen / und ihre Blüte wie Staub aufgewirbelt werden. Denn sie haben die Weisung des Herrn der Heere von sich gewiesen / und über das Wort des Heiligen Israels gelästert.", footnotes: ["Joël 2,5; Ijob 18,16"] },
      { number: 25, text: "Darum entbrennt der Zorn des Herrn gegen sein Volk; / er streckt seine Hand aus gegen das Volk und schlägt zu. Da erzittern die Berge / und die Leichen liegen auf den Gassen wie Abfall. Doch bei all dem lässt sein Zorn nicht nach, / seine Hand bleibt ausgestreckt.", heading: "Die Ankündigung der assyrischen Invasion: 5,25-30" },
      { number: 26, text: "Er stellt ein Feldzeichen auf / für ein Volk in der Ferne, er pfeift es herbei vom Ende der Erde / und schon kommen sie eilig heran.", footnotes: ["Jer 5,15; Jes 7,18", "für ein Volk: Text korr.; Einzahl wie Jer 5,15. Gemeint sind die Assyrer (vgl. 7,18-20; 10,5-19). Diese fielen zwischen 735 und 701 v. Chr. mehrmals in Palästina ein."] },
      { number: 27, text: "Kein Müder ist unter ihnen, keiner, der stolpert, / keiner, der einnickt und schläft. Bei keinem löst sich der Gürtel von den Hüften, / noch reißt ein Schuhriemen ab." },
      { number: 28, text: "Ihre Pfeile sind scharf, / alle ihre Bogen gespannt. Die Hufe ihrer Pferde sind hart wie Kiesel, / die Räder sausen dahin wie der Sturm." },
      { number: 29, text: "Es ist ein Lärm wie das Brüllen des Löwen, / wie wenn ein Junglöwe brüllt. Er knurrt und packt seine Beute, / er schleppt sie fort / und niemand reißt sie ihm weg." },
      { number: 30, text: "Und es dröhnt über ihnen an jenem Tag / wie das Brausen des Meeres. Wohin man blickt auf der Erde: / nur Finsternis voller Angst; / das Licht ist durch Wolken verdunkelt.", footnotes: ["8,22"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Im Todesjahr des Königs Usija sah ich den Herrn. Er saß auf einem hohen und erhabenen Thron. Der Saum seines Gewandes füllte den Tempel aus.", heading: "Die Berufung des Propheten: 6,1-13", footnotes: ["Demnach fand die Berufung 739 v. Chr. statt; zur Vision vgl. 1 Kön 22,19-23."] },
      { number: 2, text: "Serafim standen über ihm. Jeder hatte sechs Flügel: Mit zwei Flügeln bedeckten sie ihr Gesicht, mit zwei bedeckten sie ihre Füße und mit zwei flogen sie.", footnotes: ["2f: Serafim (= die Brennenden) bilden als himmlische Wesen den Hofstaat Gottes; sie werden als Mischwesen mit Schlangengestalt, Gesicht, Händen und Flügeln vorgestellt. Mit ihrem Ruf bezeugen sie, dass «der Herr der Heere» der absolut Heilige ist, der in seiner Herrlichkeit seine Größe und Macht kundtut."] },
      { number: 3, text: "Sie riefen einander zu: Heilig, heilig, heilig ist der Herr der Heere. / Von seiner Herrlichkeit ist die ganze Erde erfüllt." },
      { number: 4, text: "Die Türschwellen bebten bei ihrem lauten Ruf und der Tempel füllte sich mit Rauch.", footnotes: ["Erdbeben, Feuer und Rauch gehören zur Gotteserscheinung (vgl. Ex 19,18)."] },
      { number: 5, text: "Da sagte ich: Weh mir, ich bin verloren. Denn ich bin ein Mann mit unreinen Lippen und lebe mitten in einem Volk mit unreinen Lippen und meine Augen haben den König, den Herrn der Heere, gesehen.", footnotes: ["ich bin verloren, andere Übersetzungsmöglichkeit: ich muss schweigen."] },
      { number: 6, text: "Da flog einer der Serafim zu mir; er trug in seiner Hand eine glühende Kohle, die er mit einer Zange vom Altar genommen hatte." },
      { number: 7, text: "Er berührte damit meinen Mund und sagte: Das hier hat deine Lippen berührt: Deine Schuld ist getilgt, / deine Sünde gesühnt." },
      { number: 8, text: "Danach hörte ich die Stimme des Herrn, der sagte: Wen soll ich senden? Wer wird für uns gehen? Ich antwortete: Hier bin ich, sende mich!" },
      { number: 9, text: "Da sagte er: Geh und sag diesem Volk: / Hören sollt ihr, hören, aber nicht verstehen. / Sehen sollt ihr, sehen, aber nicht erkennen.", footnotes: ["Mt 13,14f par; Joh 12,39-41; Apg 28,25-27", "9f: Die Verkündigung Jesajas führt zur Verstockung des Volkes statt zur Umkehr. - Verhärte; wörtlich: Verfette."] },
      { number: 10, text: "Verhärte das Herz dieses Volkes, / verstopf ihm die Ohren, / verkleb ihm die Augen, damit es mit seinen Augen nicht sieht / und mit seinen Ohren nicht hört, damit sein Herz nicht zur Einsicht kommt / und sich nicht bekehrt und nicht geheilt wird." },
      { number: 11, text: "Ich fragte: Wie lange, Herr? / Er antwortete: Bis die Städte verödet sind und unbewohnt, / die Häuser menschenleer, / bis das Ackerland zur Wüste geworden ist.", footnotes: ["5,9"] },
      { number: 12, text: "Der Herr wird / die Menschen weit weg treiben; / dann ist das Land leer und verlassen." },
      { number: 13, text: "Bleibt darin noch ein Zehntel übrig - / auch sie werden schließlich vernichtet, wie bei einer Eiche oder Terebinthe, / von der nur der Stumpf bleibt, wenn man sie fällt. [Ihr Stumpf ist heiliger Same.]", footnotes: ["13e: Vermutlich ein späterer Zusatz, der die Drohung mildert."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "In der Zeit, als Ahas, der Sohn Jotams, des Sohnes Usijas, König von Juda war, zogen Rezin, der König von Aram, und Pekach, der Sohn Remaljas, der König von Israel, gegen Jerusalem in den Krieg; aber sie konnten die Stadt nicht einnehmen.", heading: "Die Weissagung über den Immanuel: 7,1-25", footnotes: ["2 Kön 16,5-9", "Ziel des sogenannten Syrisch-efraimitischen Kriegs (734/33 v. Chr.) ist es, Juda als Verbündeten gegen Assyrien zu gewinnen."] },
      { number: 2, text: "Als man dem Haus David meldete: Aram hat sich mit Efraim verbündet!, da zitterte das Herz des Königs und das Herz seines Volkes, wie die Bäume des Waldes im Wind zittern." },
      { number: 3, text: "Der Herr aber sagte zu Jesaja: Geh zur Walkerfeldstraße hinaus, zusammen mit deinem Sohn Schear-Jaschub (Ein Rest kehrt um), an das Ende der Wasserleitung des oberen Teiches, um Ahas zu treffen.", footnotes: ["36,2", "Der symbolische Name des Sohns ist Mahnung und Warnung für Ahas, der anscheinend die Anlagen zur Wasserversorgung inspiziert."] },
      { number: 4, text: "Sag zu ihm: Bewahre die Ruhe, fürchte dich nicht! Dein Herz soll nicht verzagen wegen dieser beiden Holzscheite, dieser rauchenden Stummel, wegen des glühenden Zorns Rezins von Aram und des Sohnes Remaljas.", footnotes: ["Die beiden Könige können nicht mehr gefährlich werden; ihr Schicksal ist bereits besiegelt: 732 v. Chr. erfolgt die Eroberung von Damaskus, 722 die Eroberung von Samaria."] },
      { number: 5, text: "Zwar planen Aram, Efraim und der Sohn Remaljas Böses gegen dich und sagen:" },
      { number: 6, text: "Wir wollen gegen Juda ziehen, es an uns reißen und für uns erobern; dann wollen wir den Sohn Tabeals dort zum König machen." },
      { number: 7, text: "Doch so spricht Gott, der Herr: Das kommt nicht zustande, / das wird nicht geschehen." },
      { number: 8, text: "Denn das Haupt von Aram ist Damaskus / und das Haupt von Damaskus ist Rezin. Noch fünfundsechzig Jahre, dann wird Efraim zerschlagen, / es wird aufhören, ein Volk zu sein.", footnotes: ["8cd: Bezieht sich auf die Ansiedlung einer fremden Oberschicht in Samaria um 720 v. Chr."] },
      { number: 9, text: "Das Haupt von Efraim ist Samaria / und das Haupt von Samaria ist der Sohn Remaljas. / Glaubt ihr nicht, so bleibt ihr nicht.", footnotes: ["28,16; 30,15; 2 Chr 20,20", "Der letzte Satz ist in H ein Wortspiel."] },
      { number: 10, text: "Der Herr sprach noch einmal zu Ahas; er sagte:" },
      { number: 11, text: "Erbitte dir vom Herrn, deinem Gott, ein Zeichen, sei es von unten, aus der Unterwelt, oder von oben, aus der Höhe." },
      { number: 12, text: "Ahas antwortete: Ich will um nichts bitten und den Herrn nicht auf die Probe stellen." },
      { number: 13, text: "Da sagte Jesaja: Hört her, ihr vom Haus David! Genügt es euch nicht, Menschen zu belästigen? Müßt ihr auch noch meinen Gott belästigen?" },
      { number: 14, text: "Darum wird euch der Herr von sich aus ein Zeichen geben: Seht, die Jungfrau wird ein Kind empfangen, sie wird einen Sohn gebären und sie wird ihm den Namen Immanuel (Gott mit uns) geben.", footnotes: ["Mt 1,23; Lk 1,31; Mi 5,2", "Jungfrau: nach G und Mt 1,23; das hebräische Wort almáh wird auch als «junge Frau» gedeutet. Nach V. 16 besagt das Zeichen, dass das Unheil in wenigen Jahren hereinbricht."] },
      { number: 15, text: "Er wird Butter und Honig essen bis zu der Zeit, in der er versteht, das Böse zu verwerfen und das Gute zu wählen." },
      { number: 16, text: "Denn noch bevor das Kind versteht, das Böse zu verwerfen und das Gute zu wählen, wird das Land verödet sein, vor dessen beiden Königen dich das Grauen packt.", footnotes: ["8,4"] },
      { number: 17, text: "Der Herr wird Tage kommen lassen über dich und dein Volk und das Haus deines Vaters [durch den König von Assur], wie man sie nicht mehr erlebt hat, seit Efraim von Juda abgefallen ist." },
      { number: 18, text: "An jenem Tag wird der Herr den Fliegen an den Mündungen des Nil in Ägypten pfeifen und den Bienen im Land Assur", footnotes: ["5,26"] },
      { number: 19, text: "und alle kommen und lassen sich nieder in den Schluchten und Felsspalten, in allen Hecken und an allen Wasserstellen." },
      { number: 20, text: "An jenem Tag wird der Herr mit dem Messer, das er jenseits des Eufrat gekauft hat [mit dem König von Assur], euch den Kopf kahl scheren und die Schamhaare abrasieren; auch den Bart schneidet er ab." },
      { number: 21, text: "An jenem Tag wird ein Mann nur eine junge Kuh und ein paar Schafe halten." },
      { number: 22, text: "Aber sie werden so viel Milch geben, dass man Butter essen kann. Ja, Butter und Honig essen alle, die im Land übrig geblieben sind." },
      { number: 23, text: "An jenem Tag wird jedes Grundstück, auf dem jetzt tausend Weinstöcke im Wert von tausend Silberstücken stehen, voll von Dornen und Disteln sein." },
      { number: 24, text: "Nur mit Pfeil und Bogen geht man dorthin; denn das ganze Land ist voll von Dornen und Disteln." },
      { number: 25, text: "Aus Angst vor den Dornen und Disteln geht man auf keinen von all den Bergen mehr, die man jetzt noch mit der Hacke bearbeitet. Man treibt die Rinder dorthin und lässt die Schafe dort weiden." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Der Herr sagte zu mir: Nimm eine große Tafel und schreib darauf mit einem gewöhnlichen Griffel: Maher-Schalal-Hasch-Bas (Schnelle Beute Rascher Raub).", heading: "Die Geburt eines Sohnes des Propheten: 8,1-4", footnotes: ["1f: mit einem gewöhnlichen Griffel, wörtlich: mit Menschengriffel. - Der symbolische Name verkündet Unheil (V. 4). Die beiden Zeugen (zur Notwendigkeit von mindestens zwei Zeugen vgl. Dtn 17,6 und 19,15) bürgen dafür, dass Jesaja den Namen aufgeschrieben hat."] },
      { number: 2, text: "Und ich nahm mir zuverlässige Zeugen, den Priester Urija und Secharja, den Sohn Jeberechjas." },
      { number: 3, text: "Dann ging ich zu der Prophetin und sie wurde schwanger und gebar einen Sohn. Da sagte der Herr zu mir: Gib ihm den Namen Maher-Schalal-Hasch-Bas!" },
      { number: 4, text: "Denn noch bevor der Knabe «Vater» und «Mutter» sagen lernt, wird man den Reichtum von Damaskus und die Beute von Samaria dem König von Assur vorantragen.", footnotes: ["7,16"] },
      { number: 5, text: "Weiter sagte der Herr zu mir:", heading: "Warnung vor Unglauben und Untreue: 8,5-23" },
      { number: 6, text: "Weil dieses Volk die ruhig dahinfließenden Wasser von Schiloach verachtet und vor Rezin und dem Sohn Remaljas verzagt,", footnotes: ["Schiloach ist der Kanal, der Wasser von der Gihonquelle am Osthang des Zion in den unteren Teich führte, ein Bild für das stille Walten Gottes."] },
      { number: 7, text: "darum wird der Herr die gewaltigen und großen Wasser des Eufrat [den König von Assur und seine ganze Macht] über sie dahinfluten lassen. Und der Fluss wird alle seine Kanäle überfluten und über alle Ufer treten." },
      { number: 8, text: "Auch auf Juda wird er übergreifen, er wird es überfluten und überschwemmen, bis er den Leuten an den Hals reicht. Die Ausläufer seiner Fluten bedecken weit und breit dein Land, Immanuel." },
      { number: 9, text: "Tobt, ihr Völker! Ihr werdet doch zerschmettert. / Horcht auf, ihr Enden der Erde! Rüstet nur! Ihr werdet doch zerschmettert. / Rüstet! Ihr werdet zerschmettert." },
      { number: 10, text: "Macht nur Pläne! Sie werden vereitelt. / Was ihr auch sagt, es kommt nicht zustande. / Denn «Gott ist mit uns».", footnotes: ["Anklang an den Namen Immanuel in 7,14 und 8,8."] },
      { number: 11, text: "Denn so sprach der Herr, als seine Hand mich packte und er mich davon abhielt, auf dem Weg dieses Volkes zu gehen:", footnotes: ["Der «Weg des Volkes» ist Misstrauen und Ungehorsam gegenüber Gott und seinem Wort."] },
      { number: 12, text: "Nennt nicht alles Verschwörung, / was dieses Volk Verschwörung nennt. Was es fürchtet, sollt ihr nicht fürchten; / wovor es erschrickt, davor sollt ihr nicht erschrecken.", footnotes: ["1 Petr 3,14f"] },
      { number: 13, text: "Den Herrn der Heere sollt ihr heilig halten; / vor ihm sollt ihr euch fürchten, / vor ihm sollt ihr erschrecken." },
      { number: 14, text: "Er wird das Heiligtum sein für die beiden Reiche Israels: / der Stein, an dem man anstößt, / der Felsen, an dem man zu Fall kommt. Eine Schlinge und Falle wird er sein / für alle, die in Jerusalem wohnen.", footnotes: ["Röm 9,32f", "Gott wird im Zionsheiligtum als schützender Fels (Ps 62,8) oder als Fels des Heils (Ps 89,27) gepriesen; vgl. auch die Anmerkung zu Dtn 32,3."] },
      { number: 15, text: "Viele stolpern darüber, / sie fallen und zerschellen; / sie verstricken und verfangen sich.", footnotes: ["28,13"] },
      { number: 16, text: "Ich will diese Warnung sorgfältig bewahren / und die Lehre in meinen Jüngern wie mit einem Siegel verschließen." },
      { number: 17, text: "Ich will auf den Herrn warten, / der jetzt sein Angesicht vor dem Haus Jakob verhüllt, / auf ihn will ich hoffen." },
      { number: 18, text: "Seht, ich und die Kinder, / die der Herr mir geschenkt hat, wir sind in Israel ein (warnendes) Zeichen, / ein Mahnmal vom Herrn der Heere, / der auf dem Berg Zion wohnt.", footnotes: ["Hebr 2,13"] },
      { number: 19, text: "Wenn man euch sagt: Befragt die Totengeister und Zauberkundigen, die flüstern und murmeln!, (dann erwidert:) Soll ein Volk nicht lieber seinen Gott befragen? Warum soll man für die Lebenden die Toten befragen?", footnotes: ["19f: Textüberlieferung und Sinn unsicher. Totenbeschwörung war nach Lev 19,31 und Dtn 18,10f verboten."] },
      { number: 20, text: "Lehre und Warnung: Wer nicht so denkt, für den gibt es kein Morgenrot." },
      { number: 21, text: "Er wandert umher, verdrossen und hungrig. Und wenn er hungert, dann wird er wütend und er verflucht seinen König und seinen Gott. Er blickt nach oben" },
      { number: 22, text: "und blickt zur Erde; aber überall sieht er nur Not, Finsternis und beängstigendes Dunkel. Doch die Finsternis wird verscheucht;", footnotes: ["5,30"] },
      { number: 23, text: "denn wer jetzt in Not ist, bleibt nicht im Dunkel. Einst hat er das Land Sebulon und das Land Naftali verachtet, aber später bringt er die Straße am Meer wieder zu Ehren, das Land jenseits des Jordan, das Gebiet der Heiden.", footnotes: ["Mt 4,13-16", "Text unsicher; der zweite Satz leitet zum folgenden Abschnitt über, der die Wende durch die Geburt des Heilskönigs verkündet."] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Das Volk, das im Dunkel lebt, / sieht ein helles Licht; über denen, die im Land der Finsternis wohnen, / strahlt ein Licht auf.", heading: "Die Verheißung der Geburt des göttlichen Kindes: 9,1-6", footnotes: ["60,2"] },
      { number: 2, text: "Du erregst lauten Jubel / und schenkst große Freude. Man freut sich in deiner Nähe, / wie man sich freut bei der Ernte, / wie man jubelt, wenn Beute verteilt wird.", footnotes: ["Jubel: Text korr."] },
      { number: 3, text: "Denn wie am Tag von Midian zerbrichst du das drückende Joch, / das Tragholz auf unserer Schulter und den Stock des Treibers.", footnotes: ["10,27; 14,25; Ri 7"] },
      { number: 4, text: "Jeder Stiefel, der dröhnend daherstampft, / jeder Mantel, der mit Blut befleckt ist, / wird verbrannt, wird ein Fraß des Feuers." },
      { number: 5, text: "Denn uns ist ein Kind geboren, / ein Sohn ist uns geschenkt. Die Herrschaft liegt auf seiner Schulter; / man nennt ihn: Wunderbarer Ratgeber, Starker Gott, / Vater in Ewigkeit, Fürst des Friedens.", footnotes: ["5f: Große Namen werden dem Königskind, ähnlich wie dem Pharao, bei der Thronbesteigung verliehen; sie sagen aus, wie der Sohn als Herrscher regieren wird. Mit Lk 1,32f werden VV. 5f in der Liturgie des Weihnachtsfestes auf Jesus Christus bezogen."] },
      { number: 6, text: "Seine Herrschaft ist groß / und der Friede hat kein Ende. Auf dem Thron Davids herrscht er über sein Reich; / er festigt und stützt es durch Recht und Gerechtigkeit, / jetzt und für alle Zeiten. Der leidenschaftliche Eifer des Herrn der Heere / wird das vollbringen." },
      { number: 7, text: "Der Herr hat ein Wort gegen Jakob geschleudert, / es fiel in Israel nieder.", heading: "Die Ankündigung des Strafgerichts über Israel: 9,7-20" },
      { number: 8, text: "Das ganze Volk sollte zur Einsicht kommen, / Efraim und wer in Samaria wohnt, / alle, die hochmütig prahlten:" },
      { number: 9, text: "Die Ziegelmauern sind gefallen, / jetzt bauen wir mit Quadern; die Maulbeerbäume hat man gefällt, / jetzt pflanzen wir Zedern." },
      { number: 10, text: "Da stachelte der Herr Jakobs Gegner auf / und hetzte seine Feinde gegen ihn,", footnotes: ["Jakobs Gegner: Text korr.; H: die Gegner Rezins."] },
      { number: 11, text: "Aram im Osten, die Philister im Westen, / und sie fraßen Israel mit gierigem Maul. Doch bei all dem lässt sein Zorn nicht nach, / seine Hand bleibt ausgestreckt.", footnotes: ["9,16.20; 5,25"] },
      { number: 12, text: "Aber das Volk kehrte nicht um zu dem, der es schlug; / sie suchten den Herrn der Heere nicht." },
      { number: 13, text: "Da schnitt der Herr dem Volk Israel den Kopf und den Schwanz ab, / Palmzweig und Binse am selben Tag:", footnotes: ["19,15", "Palmzweig und Binse: die vornehmen und die geringen Leute."] },
      { number: 14, text: "Die Ältesten und Vornehmen, sie sind der Kopf; / der Schwanz sind die Propheten, die Lügen verkünden." },
      { number: 15, text: "Die Führer dieses Volks sind Verführer; / wer sich von ihnen führen lässt, / wird in die Irre geleitet.", footnotes: ["3,12"] },
      { number: 16, text: "Deshalb verschont der Herr weder die Männer, / noch hat er mit den Witwen und Waisen Erbarmen. Denn alle sind ruchlos und böse; / aus jedem Mund kommt verruchtes Geschwätz. Doch bei all dem lässt sein Zorn nicht nach, / seine Hand bleibt ausgestreckt." },
      { number: 17, text: "Denn ihre Bosheit loderte auf wie ein Feuer, / das Dornen und Disteln verzehrt. Es entzündete das Dickicht des Waldes, / sodass es in Rauchschwaden aufging." },
      { number: 18, text: "Der Zorn des Herrn der Heere versengte das Land; / das Volk wurde ein Raub der Flammen. / Keiner verschonte den andern:" },
      { number: 19, text: "Man fraß rechts und blieb hungrig, / man fraß links und wurde nicht satt. / Jeder fraß seinen Nachbarn.", footnotes: ["seinen Nachbarn: Text korr. nach G."] },
      { number: 20, text: "Manasse fraß Efraim und Efraim Manasse / und beide zusammen fraßen Juda. Doch bei all dem lässt sein Zorn nicht nach, / seine Hand bleibt ausgestreckt." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Weh denen, die unheilvolle Gesetze erlassen / und unerträgliche Vorschriften machen,", heading: "Ein Wehruf über die ungerechten Richter: 10,1-4", footnotes: ["1-4: Gehört inhaltlich zu 5,8-24."] },
      { number: 2, text: "um die Schwachen vom Gericht fern zu halten / und den Armen meines Volkes ihr Recht zu rauben, um die Witwen auszubeuten / und die Waisen auszuplündern." },
      { number: 3, text: "Was wollt ihr tun, wenn die Strafe naht, / wenn das Unwetter von fern heraufzieht? Zu wem wollt ihr flüchten, um Hilfe zu finden, / wo euren Reichtum verstecken?" },
      { number: 4, text: "Ihr werdet euch unter Gefangenen (am Boden) krümmen / und werdet unter Erschlagenen liegen. Doch bei all dem lässt sein Zorn nicht nach, / seine Hand bleibt ausgestreckt." },
      { number: 5, text: "Weh Assur, dem Stock meines Zorns! / Es ist der Knüppel in meiner wütenden Hand.", heading: "Die Androhung der Vernichtung Assurs: 10,5-34", footnotes: ["in meiner Hand: Text korr.; H: in ihrer Hand. V. 5b ist wie in G umgestellt."] },
      { number: 6, text: "Gegen ein ruchloses Volk schicke ich ihn, / auf die Nation, der ich zürne, lasse ich ihn los, damit er Beute erbeutet und raubt wie ein Räuber, / sie zertritt wie den Staub auf den Straßen." },
      { number: 7, text: "Doch Assur stellt es sich nicht so vor, / sein Herz plant es anders, es hat nur Vernichtung im Sinn, / die Ausrottung nicht weniger Völker." },
      { number: 8, text: "Denn es sagt: Ist nicht jeder meiner Fürsten ein König? /" },
      { number: 9, text: "Ging es nicht Kalne genau so wie Karkemisch, / Hamat wie Arpad, Samaria wie Damaskus?", footnotes: ["36,18-20; 2 Kön 18,34f", "Kalne in Nordsyrien wurde 738 v. Chr. von Tiglat-Pileser III., Karkemisch am mittleren Eufrat 717 von Sargon II., Arpad nördlich von Aleppo 740 von Tiglat-Pileser III. erobert; Damaskus fiel 732, Samaria 722."] },
      { number: 10, text: "Wie meine Hand die Königreiche der Götter erobert hat, / deren Götterbilder die von Jerusalem und Samaria übertrafen," },
      { number: 11, text: "wie ich es mit Samaria und seinen Göttern gemacht habe, / so mache ich es auch mit Jerusalem und seinen Göttern." },
      { number: 12, text: "Wenn der Herr sein Werk auf dem Berg Zion und in Jerusalem vollendet hat, dann straft er das hochmütige Gebaren und die dreiste Überheblichkeit des Königs von Assur;", footnotes: ["straft er: Text korr. nach G."] },
      { number: 13, text: "denn er hat gesagt: Das alles habe ich mit meiner starken Hand / und mit meiner Weisheit vollbracht; / denn ich bin klug. Die Grenzen zwischen den Völkern habe ich aufgehoben, / ihre Schätze geplündert, / wie ein Held habe ich die Könige vom Thron gestoßen." },
      { number: 14, text: "Wie man in ein Nest greift, so griff meine Hand / nach dem Reichtum der Völker. Wie man verlassene Eier sammelt, / so habe ich alle Länder der Erde gesammelt. Da war keiner, der mit den Flügeln schlug, / keiner, der den Schnabel aufriss und piepste." },
      { number: 15, text: "Prahlt denn die Axt gegenüber dem, der mit ihr hackt, / oder brüstet die Säge sich vor dem, der mit ihr sägt? Das wäre, wie wenn der Stock den Mann schwingt, der ihn hochhebt, / oder wie wenn der Knüppel den hochhebt, der nicht aus Holz ist.", footnotes: ["Röm 9,20f", "Assur ist nur ein Werkzeug in der Hand Gottes."] },
      { number: 16, text: "Darum schickt Gott, der Herr der Heere, / den feisten Männern (von Assur) die Schwindsucht. Er entfacht ein Feuer unter Assurs Pracht, / ein loderndes Feuer.", footnotes: ["16-19: Gerichtswort über Assur in verschiedenen Bildern."] },
      { number: 17, text: "Israels Licht wird zum Feuer, / sein Heiliger wird zur Flamme. Sie brennt und verzehrt die Dornen und Disteln von Assur / an einem einzigen Tag." },
      { number: 18, text: "Seinen herrlichen Wald, seinen fruchtbaren Garten, / mit Stumpf und Stiel vernichtet er ihn; / es ist, wie wenn ein Kranker dahinsiecht." },
      { number: 19, text: "Von den Bäumen in seinem Wald / bleiben nur wenige übrig, / selbst ein Kind kann sie zählen." },
      { number: 20, text: "An jenem Tag wird Israels Rest - und wer vom Haus Jakob entkommen ist - sich nicht mehr auf den stützen, der ihn schlägt, sondern er stützt sich in beständiger Treue auf den Herrn, auf den Heiligen Israels." },
      { number: 21, text: "Ein Rest kehrt um zum starken Gott, / ein Rest von Jakob.", footnotes: ["Röm 9,27f"] },
      { number: 22, text: "Israel, wenn auch dein Volk so zahlreich ist / wie der Sand am Meer - / nur ein Rest von ihnen kehrt um. Die Vernichtung ist beschlossen, / die Gerechtigkeit flutet heran." },
      { number: 23, text: "Ja, Gott, der Herr der Heere, vollstreckt auf der ganzen Erde die Vernichtung, die er beschlossen hat." },
      { number: 24, text: "Darum - so spricht Gott, der Herr der Heere: Fürchte dich nicht, mein Volk, das auf dem Berg Zion wohnt, vor Assur, das dich mit dem Stock schlägt und das seinen Knüppel gegen dich erhebt wie einst die Ägypter." },
      { number: 25, text: "Nur noch ganz kurze Zeit, dann wird mein grimmiger Zorn sie völlig vernichten," },
      { number: 26, text: "dann schwingt der Herr der Heere über sie die Peitsche, wie einst, als er Midian am Rabenfels schlug. Er erhebt seinen Stab über das Meer wie einst in Ägypten.", footnotes: ["Ri 7,25; Ex 14,16.26"] },
      { number: 27, text: "An jenem Tag fällt Assurs Last von deiner Schulter, / sein Joch wird von deinem Nacken genommen.", footnotes: ["9,3; 14,25"] },
      { number: 28, text: "Assur zieht von Rimmon herauf, / rückt gegen Aja vor, marschiert durch Migron / und lässt seinen Tross in Michmas zurück.", footnotes: ["Assur zieht von Rimmon herauf: Text korr. 28-32: Die Orte liegen nördlich von Jerusalem."] },
      { number: 29, text: "Sie passieren den Pass und übernachten in Geba. / Rama erschrickt und es flieht Gibea-Saul." },
      { number: 30, text: "Lass deine Stimme gellen, Tochter Gallim! / Lausche, Lajescha! Anatot, antworte ihr!" },
      { number: 31, text: "Madmena flüchtet, / die Bewohner von Gebim ergreifen die Flucht." },
      { number: 32, text: "Heute noch wird er in Nob Stellung beziehen / und seine Hand drohend gegen den Berg der Tochter Zion erheben, / gegen Jerusalems Hügel." },
      { number: 33, text: "Seht, Gott, der Herr der Heere, / schlägt mit schrecklicher Gewalt die Zweige ab. Die mächtigen Bäume werden gefällt / und alles, was hoch ist, wird niedrig.", footnotes: ["33f: Strafwort über Juda und Jerusalem, an das sich das Heilswort 11,1-10 anschließt, das einen vom Geist Gottes erfüllten gerechten Herrscher aus davidischem Geschlecht und paradiesischen Frieden ankündigt."] },
      { number: 34, text: "Das Dickicht des Waldes wird mit dem Eisen gerodet, / der Libanon fällt durch die Hand eines Mächtigen." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Doch aus dem Baumstumpf Isais wächst ein Reis hervor, / ein junger Trieb aus seinen Wurzeln bringt Frucht.", heading: "Die Ankündigung des messianischen Reiches: 11,1-16" },
      { number: 2, text: "Der Geist des Herrn lässt sich nieder auf ihm: / der Geist der Weisheit und der Einsicht, der Geist des Rates und der Stärke, / der Geist der Erkenntnis und der Gottesfurcht." },
      { number: 3, text: "[Er erfüllt ihn mit dem Geist der Gottesfurcht.] / Er richtet nicht nach dem Augenschein / und nicht nur nach dem Hörensagen entscheidet er," },
      { number: 4, text: "sondern er richtet die Hilflosen gerecht / und entscheidet für die Armen des Landes, wie es recht ist. Er schlägt den Gewalttätigen / mit dem Stock seines Wortes und tötet den Schuldigen / mit dem Hauch seines Mundes.", footnotes: ["Ps 72,4; 2 Thess 2,8", "den Gewalttätigen: Text korr."] },
      { number: 5, text: "Gerechtigkeit ist der Gürtel um seine Hüften, / Treue der Gürtel um seinen Leib." },
      { number: 6, text: "Dann wohnt der Wolf beim Lamm, / der Panther liegt beim Böcklein. Kalb und Löwe weiden zusammen, / ein kleiner Knabe kann sie hüten.", footnotes: ["(6-7) 65,25"] },
      { number: 7, text: "Kuh und Bärin freunden sich an, / ihre Jungen liegen beieinander. / Der Löwe frisst Stroh wie das Rind.", footnotes: ["freunden sich an: Text korr."] },
      { number: 8, text: "Der Säugling spielt vor dem Schlupfloch der Natter, / das Kind streckt seine Hand in die Höhle der Schlange." },
      { number: 9, text: "Man tut nichts Böses mehr / und begeht kein Verbrechen / auf meinem ganzen heiligen Berg; denn das Land ist erfüllt von der Erkenntnis des Herrn, / so wie das Meer mit Wasser gefüllt ist.", footnotes: ["Hab 2,14"] },
      { number: 10, text: "An jenem Tag wird es der Spross aus der Wurzel Isais sein, / der dasteht als Zeichen für die Nationen; die Völker suchen ihn auf; / sein Wohnsitz ist prächtig.", footnotes: ["Röm 15,12"] },
      { number: 11, text: "An jenem Tag wird der Herr seine Hand von neuem erheben, / um den übrig gebliebenen Rest seines Volkes zurückzugewinnen, von Assur und Ägypten, von Patros und Kusch, / von Elam, Schinar und Hamat / und von den Inseln des Meeres.", footnotes: ["Patros ist Oberägypten, Kusch der heutige Sudan mit Teilen Äthiopiens; Elam liegt östlich von Babylon; Schinar ist das babylonische Tiefland; Hamat liegt am Orontes in Syrien."] },
      { number: 12, text: "Er stellt für die Völker ein Zeichen auf, / um die Versprengten Israels wieder zu sammeln, / um die Zerstreuten Judas zusammenzuführen von den vier Enden der Erde." },
      { number: 13, text: "Dann hört der Neid Efraims auf, / die Feinde Judas werden vernichtet. Efraim ist nicht mehr eifersüchtig auf Juda / und Juda ist nicht mehr Efraims Feind.", footnotes: ["Die Rivalität zwischen dem Nordreich Israel (Efraim) und dem Südreich Juda, die häufig zu kriegerischen Verwicklungen führte, wird überwunden."] },
      { number: 14, text: "Sie stoßen nach Westen vor wie im Flug, / den Philistern in die Flanke; / vereint plündern sie die Völker des Ostens aus. Sie ergreifen Besitz von Edom und Moab, / die Ammoniter müssen ihnen gehorchen." },
      { number: 15, text: "Der Herr trocknet die Bucht des ägyptischen Meeres aus; / er schwingt in glühendem Zorn seine Faust gegen den Eufrat und zerschlägt ihn in sieben einzelne Bäche, / sodass man in Sandalen hindurchgehen kann.", footnotes: ["trocknet . . . aus: Text korr. nach G; H: weihte. . . dem Untergang."] },
      { number: 16, text: "So entsteht eine Straße für den Rest seines Volkes, / der übrig gelassen wurde von Assur, eine Straße, wie es sie für Israel gab, / als es aus Ägypten heraufzog." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "An jenem Tag wirst du sagen: / Ich danke dir, Herr. Du hast mir gezürnt, / doch dein Zorn hat sich gewendet / und du hast mich getröstet.", heading: "Das Danklied der Geretteten: 12,1-6" },
      { number: 2, text: "Ja, Gott ist meine Rettung; / ihm will ich vertrauen und niemals verzagen. Denn meine Stärke und mein Lied ist der Herr. / Er ist für mich zum Retter geworden.", footnotes: ["Ex 15,2; Ps 118,14"] },
      { number: 3, text: "Ihr werdet Wasser schöpfen voll Freude / aus den Quellen des Heils." },
      { number: 4, text: "An jenem Tag werdet ihr sagen: / Dankt dem Herrn! Ruft seinen Namen an! Macht seine Taten unter den Völkern bekannt, / verkündet: Sein Name ist groß und erhaben!" },
      { number: 5, text: "Preist den Herrn; / denn herrliche Taten hat er vollbracht; / auf der ganzen Erde soll man es wissen." },
      { number: 6, text: "Jauchzt und jubelt, ihr Bewohner von Zion; / denn groß ist in eurer Mitte der Heilige Israels." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Ausspruch über Babel - eine Vision, die Jesaja, der Sohn des Amoz, hatte:", heading: "Worte über einzelne Völker: 13,1 - 23,18 | Das Gericht über Babel: 13,1 - 14,23 | Die Vernichtung Babels: 13,1-22", footnotes: ["(1-22) 21,1-10; 47,1-15; Jer 50f; Offb 17f"] },
      { number: 2, text: "Stellt auf einem kahlen Berg ein Feldzeichen auf, / erhebt die Stimme und ruft meine Helden herbei! Winkt mit der Hand: / Sie sollen einziehen durch die Tore der Edlen.", footnotes: ["meine Helden, wörtlich: sie (ergänzt nach V. 3)."] },
      { number: 3, text: "Ich selbst habe meine heiligen Krieger aufgeboten, / ich habe sie alle zusammengerufen, meine hochgemuten, jauchzenden Helden, / damit sie meinen Zorn vollstrecken.", footnotes: ["meine heiligen Krieger, wörtlich: meine Geheiligten. - Zum Krieg unterzieht man sich bestimmten Reinigungsriten («sich heiligen»)."] },
      { number: 4, text: "Horch, es dröhnt im Gebirge / wie der Lärm von zahllosen Menschen. Horch, ganze Königreiche brausen heran, / viele Völker kommen zusammen. / Der Herr der Heere mustert die Truppen." },
      { number: 5, text: "Sie kommen aus einem fernen Land, vom Ende des Himmels: / der Herr und die Waffen seines Zorns, / um das ganze Land zu verwüsten.", footnotes: ["Jer 50,25"] },
      { number: 6, text: "Schreit auf, denn der Tag des Herrn ist nahe; / er kommt wie eine zerstörende Macht vom Allmächtigen.", footnotes: ["Joël 1,15"] },
      { number: 7, text: "Da sinken alle Hände herab / und das Herz aller Menschen verzagt." },
      { number: 8, text: "Sie sind bestürzt; sie werden von Krämpfen und Wehen befallen, / wie eine Gebärende winden sie sich. Einer starrt auf den andern, / wie Feuer glüht ihr Gesicht." },
      { number: 9, text: "Seht, der Tag des Herrn kommt, / voll Grausamkeit, Grimm und glühendem Zorn; dann macht er die Erde zur Wüste / und die Sünder vertilgt er.", footnotes: ["9-13: Das göttliche Gericht weitet sich über die Welt aus."] },
      { number: 10, text: "Die Sterne und Sternbilder am Himmel / lassen ihr Licht nicht mehr leuchten. Die Sonne ist dunkel, schon wenn sie aufgeht, / der Mond lässt sein Licht nicht mehr scheinen.", footnotes: ["Mt 24,29"] },
      { number: 11, text: "Dann bestrafe ich den Erdkreis für seine Verbrechen / und die Bösen für ihre Vergehen. Dem Hochmut der Stolzen mache ich ein Ende / und werfe die hochmütigen Tyrannen zu Boden." },
      { number: 12, text: "Die Menschen mache ich seltener als Feingold, / die Menschenkinder rarer als Golderz aus Ofir." },
      { number: 13, text: "Dann wird der Himmel erzittern / und die Erde beginnt an ihrem Ort zu wanken wegen des Grimms des Herrn der Heere / am Tag seines glühenden Zorns." },
      { number: 14, text: "Wie aufgescheuchte Gazellen, / wie eine Schafherde, die niemand zusammenhält, so eilt dann jeder zu seinem Volk, / so flieht jeder in sein Land." },
      { number: 15, text: "Man sticht jeden nieder, dem man begegnet; / wen man zu fassen bekommt, der fällt unter dem Schwert.", footnotes: ["(15-16) 2 Kön 8,12; Hos 14,1; Ps 137,9"] },
      { number: 16, text: "Vor ihren Augen werden ihre Kinder zerschmettert, / ihre Häuser geplündert, ihre Frauen geschändet." },
      { number: 17, text: "Seht, ich stachle die Meder gegen sie auf, / denen das Silber nichts gilt / und das Gold nichts bedeutet." },
      { number: 18, text: "Ihre Bogen strecken die jungen Männer nieder; / mit der Leibesfrucht haben sie kein Erbarmen, / mit den Kindern kein Mitleid." },
      { number: 19, text: "Wie es Sodom und Gomorra erging, / als Gott sie zerstörte, so wird es Babel ergehen, / dem Kleinod unter den Königreichen, / dem Schmuckstück der stolzen Chaldäer.", footnotes: ["Jer 49,18"] },
      { number: 20, text: "Für immer wird es unbewohnt sein, / bis zu den fernsten Generationen / wird es nicht mehr besiedelt. Nicht einmal ein Beduine / schlägt dort sein Zelt auf, / kein Hirt lässt seine Herde dort lagern.", footnotes: ["34,10-15"] },
      { number: 21, text: "Dort haben nur Wüstenhunde ihr Lager, / die Häuser sind voller Eulen, Strauße lassen sich dort nieder / und Böcke springen umher.", footnotes: ["34,14; Jer 50,39"] },
      { number: 22, text: "Hyänen heulen in Babels Palästen, / in den Lustschlössern heulen Schakale. Die Zeit (seines Endes) steht nahe bevor, / Babels (letzte) Tage verzögern sich nicht." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Der Herr wird mit Jakob Erbarmen haben und Israel von neuem erwählen. Er wird ihnen Ruhe gewähren in ihrer Heimat; Fremde gesellen sich ihnen bei und schließen sich an das Haus Jakob an.", heading: "Das Spottlied auf den König von Babel: 14,1-21", footnotes: ["61,5"] },
      { number: 2, text: "Die Völker werden Israel nehmen und in seine Heimat zurückführen und im Land des Herrn wird das Haus Israel sie zu Leibeigenen machen, zu Knechten und Mägden. Es wird die gefangen halten, die es gefangen hielten, und wird die unterdrücken, die es einst unterdrückten." },
      { number: 3, text: "Und wenn der Herr dir dann Ruhe gewährt nach deinen Leiden, deiner Unruhe und der harten Knechtschaft, die du erdulden musstest," },
      { number: 4, text: "dann wirst du auf den König von Babel dieses Spottlied singen: Ach, der Unterdrücker fand sein Ende, / ein Ende nahm die Not." },
      { number: 5, text: "Der Herr hat die Knüppel der Frevler zerbrochen, / den Stock der Tyrannen," },
      { number: 6, text: "der in seinem Zorn die Völker erschlug, / sie schlug ohne Ende, der die Völker in seiner Wut zertrat / und sie verfolgte ohne jedes Erbarmen." },
      { number: 7, text: "Nun hat die ganze Welt Ruhe und Frieden, / man bricht in Jubel aus." },
      { number: 8, text: "Selbst die Zypressen und die Zedern des Libanon / machen sich über dich lustig: Seit du am Boden liegst, kommt keiner mehr her, / um uns zu fällen." },
      { number: 9, text: "Das Totenreich drunten gerät in Erregung, / wenn du hinabkommst. Deinetwegen weckt es die Totengeister auf, / alle Fürsten der Erde, alle Könige der Völker lässt es aufstehen / von ihren Thronen." },
      { number: 10, text: "Sie alle rufen dir zu: / Auch du bist nun kraftlos geworden wie wir, / jetzt bist du uns gleich." },
      { number: 11, text: "Hinabgeschleudert zur Unterwelt ist deine Pracht / samt deinen klingenden Harfen. Auf Würmer bist du gebettet, / Maden sind deine Decke.", footnotes: ["Am 5,23"] },
      { number: 12, text: "Ach, du bist vom Himmel gefallen, / du strahlender Sohn der Morgenröte. Zu Boden bist du geschmettert, / du Bezwinger der Völker.", footnotes: ["12-21: Der Text verwendet mythische Bilder. Nach babylonischer Anschauung wurde Helel, der Sohn der Morgenröte, in die Unterwelt gestürzt, als er in den Himmel aufsteigen wollte."] },
      { number: 13, text: "Du aber hattest in deinem Herzen gedacht: / Ich ersteige den Himmel; dort oben stelle ich meinen Thron auf, / über den Sternen Gottes; auf den Berg der (Götter-)versammlung setze ich mich, / im äußersten Norden.", footnotes: ["Jer 51,53"] },
      { number: 14, text: "Ich steige weit über die Wolken hinauf, / um dem Höchsten zu gleichen." },
      { number: 15, text: "Doch in die Unterwelt wirst du hinabgeworfen, / in die äußerste Tiefe." },
      { number: 16, text: "Jeder, der dich sieht, starrt dich an, / er blickt genau auf dich hin und denkt: Ist das der Mann, der die Königreiche in Schrecken versetzte, / der die Erde erbeben ließ," },
      { number: 17, text: "der die Welt zur Wüste gemacht hat, / ihre Städte zerstörte, / der die Gefangenen nicht nach Hause entließ?" },
      { number: 18, text: "Alle Könige der Völker ruhen in Ehren, / jeder in seinem Grab;" },
      { number: 19, text: "du aber wurdest hingeworfen ohne Begräbnis, / wie ein verachteter Bastard. Mit Erschlagenen bist du bedeckt, / die vom Schwert durchbohrt sind, / wie ein zertretener Leichnam. Mit denen, die in steinerne Grüfte hinabsteigen, /", footnotes: ["Bastard, wörtlich: Spross."] },
      { number: 20, text: "bist du nicht vereint im Grab. Du hast dein eigenes Land zugrunde gerichtet, / hingemordet dein eigenes Volk; darum soll man die Namen der Nachkommen dieses Verbrechers / niemals mehr nennen.", footnotes: ["dieses Verbrechers: Text korr., Einzahl nach G und S."] },
      { number: 21, text: "Richtet eine Schlachtbank her für seine Söhne / wegen der Sünden des Vaters, damit sie sich niemals wieder erheben und die Welt erobern / und den Erdkreis mit Städten erfüllen.", footnotes: ["Vielleicht Städte, die der Eroberer neu erbaut."] },
      { number: 22, text: "Ich will mich gegen Babel erheben - Spruch des Herrn der Heere -, mit Stumpf und Stiel will ich seinen Namen und Samen vernichten Spruch des Herrn.", heading: "Die völlige Verwüstung Babels: 14,22-23", footnotes: ["Ijob 18,19", "In H zwei Wortspiele."] },
      { number: 23, text: "Ich mache es zum Platz für die Eulen und zu einem sumpfigen Teich, mit meinem vernichtenden Besen fege ich es hinweg - Spruch des Herrn der Heere.", footnotes: ["34,11"] },
      { number: 24, text: "Der Herr der Heere hat geschworen: / Wie ich es erdacht habe, so wird es geschehen; / wie ich es plante, so wird es auch kommen.", heading: "Das Gericht über andere Völker: 14,24 - 21,17 | Die Ankündigung der Vernichtung des assyrischen Heeres: 14,24-27" },
      { number: 25, text: "In meinem eigenen Land will ich Assur zerschmettern, / ich will es auf meinen Bergen zertreten. Dann wird sein Joch von ihnen genommen / und seine Last fällt von ihrer Schulter.", footnotes: ["9,3; 10,27", "von ihnen: Gemeint sind die Israeliten."] },
      { number: 26, text: "Das ist der Plan, der für die ganze Erde beschlossen ist, / das ist die Hand, die über alle Völker ausgestreckt ist." },
      { number: 27, text: "Denn der Herr der Heere hat es beschlossen. / Wer kann es vereiteln? Seine Hand ist ausgestreckt. / Wer will sie zurückbiegen?" },
      { number: 28, text: "Im Todesjahr des Königs Ahas erging folgender Ausspruch:", heading: "Die Ankündigung des Gerichts über die Philister: 14,28-32" },
      { number: 29, text: "Freu dich nicht, Land der Philister, / weil der Stock zerbrochen ist, der dich schlug; denn aus der Schlange geht wie aus einer Wurzel eine Natter hervor / und ihre Frucht ist ein fliegender Drache.", footnotes: ["30,6", "Der «Stock» ist ein feindlicher Herrscher, vielleicht Tiglat-Pileser III., der 727 v. Chr. starb."] },
      { number: 30, text: "Auf meiner Wiese weiden die Schwachen, / dort leben die Armen in Sicherheit; deine Wurzeln aber lasse ich verhungern, / den Rest von dir werde ich erschlagen." },
      { number: 31, text: "Schreit auf, ihr Tore, jammere, o Stadt, / verzage, Land der Philister! Denn von Norden kommt Rauch / und keiner entfernt sich aus den Reihen des Heeres." },
      { number: 32, text: "Was gibt man den Gesandten der Völker zur Antwort? / Der Herr hat Zion gegründet, / die Armen seines Volkes finden dort ihre Zuflucht." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Ausspruch über Moab: Über Nacht wurde Ar verwüstet, / ging Moab zugrunde. Über Nacht wurde Kir verwüstet, / ging Moab zugrunde.", heading: "Ankündigung des Gerichts über Moab: 15,1 - 16,14", footnotes: ["1f: Kir, auch Kir-Heres (16,7.11), ist die Hauptstadt Moabs, südlich des Arnon. Die in Jes 15f genannten Orte liegen alle östlich des Toten Meers."] },
      { number: 2, text: "Die Tochter Dibon steigt zu den Kulthöhen hinauf, / um dort zu klagen. Über Nebo und Medeba jammert Moab. / Jeder Kopf ist kahl geschoren, / alle Bärte hat man abgeschnitten.", footnotes: ["16,12; Jer 48,37", "Tochter Dibon: Text korr. nach Jer 48,18."] },
      { number: 3, text: "Auf den Gassen geht man in Trauergewändern, / alle weinen auf den Dächern und Plätzen / und zerfließen in Tränen.", footnotes: ["Jer 48,38"] },
      { number: 4, text: "Heschbon schreit und Elale, / bis nach Jahaz ist ihre Stimme zu hören. Darum zittern die Krieger von Moab, / ihre Seele verzagt.", footnotes: ["Jer 48,34"] },
      { number: 5, text: "Mein Herz schreit auf wegen Moab; / bis nach Zoar fliehen die Menschen [bis Eglat-Schelischija]. Die Steige von Luhit steigt man weinend hinauf. / Auf dem Weg nach Horonajim schreien die Leute über das Unglück.", footnotes: ["Jer 48,4f"] },
      { number: 6, text: "Die Quellen von Nimrim versiegen; / das Gras ist verwelkt; verdorrt sind die Wiesen; / alles Grün ist verschwunden." },
      { number: 7, text: "Darum schleppen sie ihre letzte Habe / und ihre Vorräte über den Weidenbach fort.", footnotes: ["Der Weidenbach liegt im Süden Moabs."] },
      { number: 8, text: "Überallhin dringt das Klagegeschrei, / bis an die Grenzen von Moab. Bis nach Eglajim hört man das Jammern, / man hört es bis Beer-Elim." },
      { number: 9, text: "Voll Blut sind die Bäche von Dibon. / Doch ich bringe über Dibon noch größeres Unglück: Löwen über die aus Moab Entronnenen, / über den Rest von Adama.", footnotes: ["Text und Übersetzung unsicher."] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Schickt Lämmer für den Herrscher des Landes / von Sela durch die Wüste zum Berg der Tochter Zion!", footnotes: ["2 Kön 3,4", "Sinn unklar; vielleicht eine Aufforderung, auf dem Zion durch Gaben um Schutz und Hilfe zu bitten (vgl. VV. 3f)."] },
      { number: 2, text: "Wie flüchtende Vögel, aus dem Nest verscheucht, / so sind die Töchter Moabs an den Furten des Arnon." },
      { number: 3, text: "Mach einen Plan, triff eine Entscheidung! / Wie die Nacht breite deinen Schatten aus am helllichten Tag, versteck die Verjagten, / verrate die Flüchtigen nicht!" },
      { number: 4, text: "Lass die Flüchtlinge Moabs bei dir verweilen; / versteck sie bei dir vor ihrem Verfolger! Ist der Unterdrücker beseitigt, / der Verfolger vernichtet / und sind die Eroberer aus dem Land verschwunden,", footnotes: ["29,20"] },
      { number: 5, text: "dann wird durch (Gottes) Huld ein Thron errichtet; / darauf sitzt [im Zelt Davids] ein zuverlässiger Richter, / der das Recht sucht und die Gerechtigkeit fördert.", footnotes: ["9,6; Jer 23,5"] },
      { number: 6, text: "Wir haben von Moabs Stolz gehört - / es ist stolz über die Maßen - von seinem Dünkel (haben wir gehört), / von seinem Stolz und Übermut, / und sein Geschwätz ist nicht wahr.", footnotes: ["Jer 48,29-39"] },
      { number: 7, text: "Darum jammert Moab laut um Moab, / alle jammern laut. Den Traubenkuchen von Kir-Heres weinen sie nach; / sie sind ganz niedergeschlagen." },
      { number: 8, text: "Denn die Pflanzungen von Heschbon sind verwelkt, / verwelkt ist der Weinstock von Sibma. Die Herren der Völker haben seine Reben zertreten, / die bis Jaser reichten, bis in die Wüste hinaus sich verloren, seine Ranken breiteten sich aus, / sie zogen sich hin bis zum Meer." },
      { number: 9, text: "Darum weine ich, wie Jaser um dich weint, / Weinstock von Sibma; / ich benetze euch mit meinen Tränen, / Heschbon und Elale. / Es gibt bei euch keinen Jubel mehr / über Weinlese und Ernte;" },
      { number: 10, text: "verschwunden sind Freude und Jubelgeschrei / aus dem fruchtbaren Land; / in den Weinbergen jauchzt man nicht mehr / und jubelt nicht mehr. Niemand stampft mehr in der Kelter die Trauben. / Verstummt ist das Jauchzen.", footnotes: ["verstummt ist: Text korr. nach G."] },
      { number: 11, text: "Darum jammert mein Herz um Moab wie eine Zither, / mein Inneres klagt um Kir-Heres.", footnotes: ["Jer 48,36"] },
      { number: 12, text: "Wenn aber Moab auf seiner Kulthöhe erscheint / und sich abmüht (mit Opfern), wenn es in sein Heiligtum geht, um zu beten: / Es wird nichts erreichen." },
      { number: 13, text: "Dieses Wort hat der Herr einst über Moab gesprochen.", footnotes: ["13f: Nachtrag, der ein erneutes Gerichtswort bringt. Mit «Söldnerjahren» wird die Zeit der Mühsal bezeichnet."] },
      { number: 14, text: "Jetzt aber hat der Herr so gesprochen: In drei Jahren - drei Söldnerjahren - wird Moabs Macht und all seine Pracht ganz gering; was aber übrig bleibt, wird schwach und unansehnlich sein.", footnotes: ["21,16"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Ausspruch über Damaskus. Seht hin: Damaskus verschwindet / und wird keine Stadt mehr sein, / es wird zu einem Haufen von Trümmern.", heading: "Die Ankündigung des Gerichts über Damaskus und über das Nordreich Israel: 17,1-11" },
      { number: 2, text: "Die Städte um Aroër sind verlassen; / sie gehören den Herden, die dort ungestört lagern." },
      { number: 3, text: "Mit dem Bollwerk von Efraim ist es zu Ende, / mit dem Königreich von Damaskus. Dem Rest von Aram wird es gehen wie der Macht der Israeliten - / Spruch des Herrn der Heere." },
      { number: 4, text: "An jenem Tag schrumpft Jakobs Macht zusammen, / das Fett seines Leibes schwindet dahin." },
      { number: 5, text: "Dann wird es sein, wie wenn ein Schnitter die Halme packt / und mit seinem Arm die Ähren abmäht. Dann wird es sein, wie wenn jemand Ähren aufliest / in der Rafaïterebene:", footnotes: ["ein Schnitter: Text korr. - Die Rafaïterebene liegt zwischen Jerusalem und Betlehem."] },
      { number: 6, text: "Nur ein Rest bleibt für die Nachlese übrig / wie beim Abernten der Ölbäume: zwei, drei reife Oliven an den oberen Ästen des Baumes, / vier oder fünf an seinen Zweigen - / Spruch des Herrn, des Gottes Israels.", footnotes: ["24,13"] },
      { number: 7, text: "An jenem Tag werden die Menschen auf ihren Schöpfer blicken, / ihre Augen werden auf den Heiligen Israels schauen." },
      { number: 8, text: "Sie blicken nicht mehr auf die Altäre, / das Machwerk ihrer Hände, sie schauen nicht mehr auf das, was ihre Finger gemacht haben, / auf die Kultpfähle und die Räucheraltäre.", footnotes: ["27,9"] },
      { number: 9, text: "An jenem Tag sind deine befestigten Städte verlassen / wie die Städte der Hiwiter und Amoriter, die man verlassen hat aus Furcht vor den Israeliten; / es wird eine schaurige Öde entstehen.", footnotes: ["9ab: Text korr.; vgl. G."] },
      { number: 10, text: "Denn du hast den Gott, der dich rettet, vergessen; / an den Felsen, auf dem du Zuflucht findest, / hast du nicht mehr gedacht. Leg nur liebliche Gärten an, / bepflanze sie mit Setzlingen aus der Fremde,", footnotes: ["10f: Hier werden Praktiken verurteilt, die Fruchtbarkeit und Leben garantieren sollen."] },
      { number: 11, text: "pfleg sie an dem Tag, an dem du sie pflanzt, / lass sie wachsen an dem Morgen, an dem du sie säst: Dahin ist die Ernte am Tag deiner Krankheit / und des heillosen Schmerzes." },
      { number: 12, text: "Weh, welch Getöse von zahlreichen Völkern; / wie das Tosen des Meeres, so tosen sie. Man hört das Toben der Nationen; / wie das Toben gewaltiger Fluten, so toben sie.", heading: "Der Wehruf über die Assyrer: 17,12-14" },
      { number: 13, text: "[Die Nationen toben wie das Toben gewaltiger Fluten.] / Doch der Herr wird ihnen drohen, / dann fliehen sie weit in die Ferne, dahingejagt vom Wind wie die Spreu auf den Bergen, / wie Disteln, die der Sturm vor sich herrollt.", footnotes: ["Ps 83,14"] },
      { number: 14, text: "Am Abend herrscht plötzlich Schrecken, / doch ehe es Morgen wird verschwunden sind sie. Das ist das Schicksal derer, die uns ausplündern wollen, / das Los derer, die uns berauben wollen." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Weh dem Land der Heuschreckenschwärme / jenseits der Flüsse von Kusch.", heading: "Ankündigung des Gerichts über Kusch: 18,1-7", footnotes: ["Dtn 28,42", "der Heuschreckenschwärme, wörtlich: des Flügelgeschwirrs. - Zu Kusch vgl. die Anmerkung zu 11,11; gemeint sind die Äthiopier, die Herodot die größten und schönsten aller Menschen nennt."] },
      { number: 2, text: "Es schickt seine Boten aus auf dem Nil, / in Papyruskähnen über das Wasser. Geht, ihr schnellen Boten, / zu dem hoch gewachsenen Volk mit der glänzenden Haut, zu der Nation, die man weit und breit fürchtet, / zu dem Volk, das kraftvoll alles zertritt, / dessen Land von den Flüssen durchschnitten wird." },
      { number: 3, text: "Ihr Bewohner der Welt, ihr Bürger der Erde, / seht alle hin, wenn man das Zeichen aufstellt auf den Bergen, horcht alle auf, / wenn man das Widderhorn bläst." },
      { number: 4, text: "Denn so hat der Herr zu mir gesprochen: / Ich will mir alles betrachten von meinem Platz aus, unbewegt wie die glühende Hitze am Mittag, / wie die Dunstwolken in der Hitze des Sommers." },
      { number: 5, text: "Ja, noch vor der Ernte, wenn die Blüte vorbei ist / und die Frucht zur Traube heranreift, schneidet er die Reben ab mit dem Messer; / er entfernt die Triebe, er reißt sie ab.", footnotes: ["5f: Eine Warnung zuerst an Juda."] },
      { number: 6, text: "Sie alle werden den Raubvögeln der Berge überlassen / und den wilden Tieren im Land. Den Sommer über sitzen die Raubvögel darauf / und im Winter sind dort die wilden Tiere." },
      { number: 7, text: "In jener Zeit werden von dem hoch gewachsenen Volk mit der glänzenden Haut dem Herrn der Heere Geschenke gebracht, von der Nation, die man weit und breit fürchtet, von dem Volk, das kraftvoll alles zertritt, dessen Land von Flüssen durchschnitten wird. Man bringt die Geschenke an den Ort, wo der Name des Herrn der Heere gegenwärtig ist: zum Berg Zion." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Ausspruch über Ägypten. Seht, der Herr fährt auf einer leichten Wolke daher; / er kommt nach Ägypten. Vor seinem Angesicht zittern die Götter Ägyptens, / den Ägyptern verzagt das Herz in der Brust.", heading: "Ankündigung des Gerichts über Ägypten: 19,1-25" },
      { number: 2, text: "Ich hetze Ägypter gegen Ägypter / und sie kämpfen gegeneinander: Bruder gegen Bruder, Nachbar gegen Nachbar, / Stadt gegen Stadt, Gau gegen Gau." },
      { number: 3, text: "Der Geist Ägyptens gerät mitten in ihm in Verwirrung, / ich vereitle seine Pläne. Dann befragen sie die Götter und die Zauberer, / die Totengeister und die Gelehrten." },
      { number: 4, text: "Doch ich gebe die Ägypter in die Gewalt eines strengen Herrn, / ein harter König wird über sie herrschen - / Spruch Gottes, des Herrn der Heere.", footnotes: ["Der «strenge Herr» ist wohl Sargon II."] },
      { number: 5, text: "Das Wasser im Meer versiegt, / der Fluss trocknet aus." },
      { number: 6, text: "Die Kanäle Ägyptens verbreiten üble Gerüche, / seicht und trocken sind die Arme des Nil, / Binsen und Schilfrohr verwelken." },
      { number: 7, text: "Das Riedgras am Nil, an der Mündung des Nil, / alle Saaten am Nil sind vertrocknet, / sie sind verweht und dahin." },
      { number: 8, text: "Die Fischer klagen und trauern, / alle, die ihre Angel auswerfen im Nil. Wer sein Netz im Wasser auslegen will, / ist bekümmert." },
      { number: 9, text: "Wer Flachs anbaut, erntet Enttäuschung. / Die Hechlerinnen und die Weber erblassen;", footnotes: ["Die Hechlerinnen erblassen: Text korr."] },
      { number: 10, text: "die Seiler sind niedergeschlagen; / alle Arbeiter verlieren den Mut." },
      { number: 11, text: "Die Fürsten von Zoan sind Narren; / was die Weisen dem Pharao raten, ist Unsinn. Wie könnt ihr zum Pharao sagen: / Ich bin der Sohn eines Weisen, / der Sohn von königlichen Ahnen?", footnotes: ["Zoan (= Tanis): im Nordosten des Nildeltas."] },
      { number: 12, text: "Wo sind denn deine weisen Berater? / Sie sollen dir sagen und erklären, / was der Herr der Heere beschlossen hat über Ägypten." },
      { number: 13, text: "Die Fürsten von Zoan sind dumm / und die Fürsten von Memfis lassen sich täuschen. / Die Führer der Stämme führen Ägypten in die Irre.", footnotes: ["Memfis: südlich von Kairo am westlichen Nilufer."] },
      { number: 14, text: "Der Herr hat ihnen einen Geist eingegossen, der sie schwindlig macht, / sodass sie Ägypten in die Irre führen bei allem, was es tut, / und es nun wie ein Betrunkener taumelt, der sich erbricht." },
      { number: 15, text: "So wird in Ägypten niemand mehr etwas vollbringen, / niemand, weder Kopf noch Schwanz, / weder Palme noch Binse.", footnotes: ["9,13"] },
      { number: 16, text: "An jenem Tag werden die Ägypter wie die Weiber sein: Sie erschrecken und zittern, wenn der Herr der Heere seine Faust gegen sie schwingt." },
      { number: 17, text: "Das Land Juda wird für Ägypten zum Schrecken werden. Sooft man Judas Namen erwähnt, erschrickt Ägypten vor dem Plan, den der Herr der Heere gegen Ägypten gefasst hat." },
      { number: 18, text: "An jenem Tag werden fünf Städte in Ägypten die Sprache Kanaans sprechen und beim Herrn der Heere schwören. Eine von ihnen wird Ir-Heres (Sonnenstadt) heißen." },
      { number: 19, text: "An jenem Tag wird es für den Herrn mitten in Ägypten einen Altar geben und an Ägyptens Grenze wird ein Steinmal für den Herrn aufgestellt." },
      { number: 20, text: "Das wird ein Zeichen und Zeugnis für den Herrn der Heere in Ägypten sein: Wenn sie beim Herrn gegen ihre Unterdrücker Klage erheben, wird er ihnen einen Retter schicken, der für sie kämpft und sie befreit." },
      { number: 21, text: "Der Herr wird sich den Ägyptern offenbaren und die Ägypter werden an jenem Tag den Herrn erkennen; sie werden ihm Schlachtopfer und Speiseopfer darbringen, sie werden dem Herrn Gelübde ablegen und sie auch erfüllen." },
      { number: 22, text: "Der Herr wird die Ägypter zwar schlagen, er wird sie aber auch heilen: Wenn sie zum Herrn umkehren, lässt er sich durch ihre Bitte erweichen und heilt sie." },
      { number: 23, text: "An jenem Tag wird eine Straße von Ägypten nach Assur führen, sodass die Assyrer nach Ägypten und die Ägypter nach Assur ziehen können. Und Ägypten wird zusammen mit Assur (dem Herrn) dienen." },
      { number: 24, text: "An jenem Tag wird Israel als Drittes dem Bund von Ägypten und Assur beitreten, zum Segen für die ganze Erde." },
      { number: 25, text: "Denn der Herr der Heere wird sie segnen und sagen: Gesegnet ist Ägypten, mein Volk, und Assur, das Werk meiner Hände, und Israel, mein Erbbesitz." },
    ] },
    { number: 20, verses: [
      { number: 2, text: "V1Der Herr hatte durch Jesaja, den Sohn des Amoz gesprochen und gesagt: Geh, leg dein Bußgewand ab und zieh deine Schuhe aus! Jesaja hatte es getan und war nackt und barfuß umhergegangen.", heading: "Die Verschleppung der Ägypter und der Kuschiter: 20,1-6", footnotes: ["1f: Der Verständlichkeit wegen sind die beiden Verse umgestellt."] },
      { number: 1, text: "V2In dem Jahr nun, in dem im Auftrag des Königs Sargon von Assur ein Feldherr nach Aschdod kam, es belagerte und eroberte," },
      { number: 3, text: "sagte der Herr: Dass mein Knecht Jesaja drei Jahre lang nackt und barfuß umherging, ist ein (warnendes) Zeichen und Sinnbild für Ägypten und Kusch:" },
      { number: 4, text: "So werden die gefangenen Ägypter und die aus ihrer Heimat vertriebenen Kuschiter, Jung und Alt, vom König von Assur nackt und barfuß weggeführt - mit entblößtem Gesäß, zur Schande Ägyptens." },
      { number: 5, text: "Dann wird man erschrecken und sich schämen, weil man nach Kusch Ausschau gehalten und mit Ägypten geprahlt hat." },
      { number: 6, text: "Und die Bewohner der Küstenstädte werden an jenem Tag sagen: Seht, so geht es denen, nach denen wir Ausschau hielten und zu denen wir flohen, um Hilfe und Rettung vor dem König von Assur zu finden. Wie können dann wir noch entkommen?", footnotes: ["Die philistäische Küste mit Aschdod. Diese Stadt hatte sich 712 v. Chr. gegen Sargon II. empört."] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Ausspruch über die Wüste am Meer: Wie die Stürme im Negeb toben, / so kommt Unheil aus der Wüste, / aus dem schaurigen Land.", heading: "Erneute Ankündigung des Gerichts über Babel: 21,1-10", footnotes: ["Nach V. 9 ist Babel gemeint. - Wüste am Meer; Bedeutung unklar."] },
      { number: 2, text: "Eine schreckliche Vision wurde mir gezeigt: / Der Empörer empört sich, der Vernichter vernichtet. Zieh herauf, Elam! / Medien, beginne mit der Belagerung! / Ich mache allem Seufzen ein Ende.", footnotes: ["33,1"] },
      { number: 3, text: "Darum zittert mein ganzer Leib, / Krämpfe befallen mich / wie die Wehen eine gebärende Frau. Ich bin betäubt von dem, was ich höre, / bestürzt von dem, was ich sehe." },
      { number: 4, text: "Mein Herz pocht wild, mich schüttelt ein Schauder. / Das ersehnte Dunkel des Abends macht der Herr für mich zum Schrecken." },
      { number: 5, text: "Man deckt den Tisch, legt die Polster zurecht / und isst und trinkt. / Steht auf, ihr Fürsten, ölt euren Schild ein!", footnotes: ["Dan 5"] },
      { number: 6, text: "Denn so hat der Herr zu mir gesagt: / Geh, stell einen Späher auf! / Was er sieht, soll er melden." },
      { number: 7, text: "Sieht er Wagen und Pferdegespanne, / einen Zug von Eseln, einen Zug von Kamelen, / soll er darauf achten, genau darauf achten." },
      { number: 8, text: "Der Späher rief: / Herr, den ganzen Tag stehe ich auf meinem Posten, / die ganze Nacht halte ich Wache.", footnotes: ["Der Späher: Qumran-Text: Der Seher; H: Der Löwe."] },
      { number: 9, text: "Seht, dort kommt ein Zug von Männern, dazu Pferdegespanne. / Und er begann zu rufen: Gefallen ist Babel, gefallen, / und all seine Götterbilder hat man zu Boden geschmettert.", footnotes: ["Jer 51,8; Offb 14,8; 18,2"] },
      { number: 10, text: "Du mein zerschlagenes, zerdroschenes Volk! / Was ich hörte vom Herrn der Heere, / von Israels Gott, das verkünde ich euch." },
      { number: 11, text: "Ausspruch über Edom. Aus Seïr ruft man mir zu: / Wächter, wie lange noch dauert die Nacht? / Wächter, wie lange noch dauert die Nacht?", heading: "Der Spruch über Edom: 21,11-12", footnotes: ["Edom: Text korr. mit G; H: Duma. Der Spruch ist ein sog. Wächterlied."] },
      { number: 12, text: "Der Wächter antwortet: / Es kommt der Morgen, es kommt auch die Nacht. / Wenn ihr fragen wollt, kommt wieder und fragt!" },
      { number: 13, text: "Ausspruch über Arabien. Übernachtet im Gebüsch, in der Steppe, / ihr Karawanen von Dedan!", heading: "Die Ankündigung des Gerichts über Arabien: 21,13-17", footnotes: ["13f: Dedan und Tema sind Orte an der Karawanenstraße in Südarabien, östlich vom Roten Meer; Kedar: nördlich von Tema."] },
      { number: 14, text: "Bringt den Durstigen Wasser, / ihr Bewohner der Gegend von Tema! / Kommt den Fliehenden entgegen mit Brot!" },
      { number: 15, text: "Denn sie sind vor den Schwertern geflohen, / vor dem gezückten Schwert, vor dem gespannten Bogen, / vor dem schweren Kampf." },
      { number: 16, text: "Denn so hat der Herr zu mir gesagt: Noch ein Jahr - ein Söldnerjahr -, dann ist es mit der ganzen Macht Kedars zu Ende.", footnotes: ["Vgl. die Anmerkung zu 16,13f."] },
      { number: 17, text: "Von den Bogenschützen in Kedar bleiben nur wenige übrig. Der Herr, der Gott Israels, hat gesprochen." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Ausspruch über das Tal der Vision. Was ist mit dir? / Warum sind deine Bewohner alle auf die Dächer gestiegen,", heading: "Das Gericht über Jerusalem: 22,1-25 | Die unverbesserlichen Einwohner Jerusalems: 22,1-14", footnotes: ["22,5"] },
      { number: 2, text: "du Stadt voll Lärm und Gedränge, / du fröhliche Burg? Deine Toten wurden nicht vom Schwert getötet, / sie sind nicht im Krieg gefallen." },
      { number: 3, text: "Alle deine Anführer sind gemeinsam geflohen, / ohne einen einzigen Bogenschuss wurden sie gefangen; alle, die man von dir noch fand, wurden gefesselt, / wenn sie auch noch so weit flohen.", footnotes: ["H schwer verständlich, daher Übersetzung unsicher."] },
      { number: 4, text: "Darum sage ich: Blickt von mir weg, / ich weine in bitterem Schmerz. Bemüht euch nicht, mich zu trösten / über die Misshandlung der Tochter, meines Volkes." },
      { number: 5, text: "Denn einen Tag der Bestürzung, der Verwüstung und Verwirrung / schickt Gott, der Herr der Heere. Im Tal der Vision macht man gewaltigen Lärm / und stürmt mit Geschrei gegen den Berg an.", footnotes: ["22,1"] },
      { number: 6, text: "Elam hat den Köcher umgehängt, / vor die Wagen Arams sind Pferde gespannt, / Kir hat den Schild aus der Hülle genommen.", footnotes: ["Aram: Text korr. nach G; H: Adam."] },
      { number: 7, text: "Deine herrlichen Täler füllten sich mit Wagen, / vor deinem Tor stellten sich die Reiter auf." },
      { number: 8, text: "So nahm er Juda jeden Schutz. / Ihr aber habt an jenem Tag nach euren Waffen im «Waldhaus» gesehen;", footnotes: ["Zu «Waldhaus», auch «Libanonwaldhaus» genannt, vgl. die Anmerkung zu 1 Kön 7,2."] },
      { number: 9, text: "ihr habt festgestellt, wie rissig die (Mauer der) Davidstadt war; / ihr habt im unteren Teich das Wasser gesammelt" },
      { number: 10, text: "und habt Jerusalems Häuser gezählt; / ihr habt die Häuser abgerissen / und (mit den Steinen) die Mauer befestigt;" },
      { number: 11, text: "ihr habt zwischen den beiden Mauern ein Becken angelegt, / um das Wasser des alten Teiches zu sammeln, doch ihr habt nicht auf den geblickt, der alles bewirkt; / ihr habt nicht auf den geschaut, der alles aus der Ferne bestimmt.", footnotes: ["2 Kön 20,20; Sir 48,17", "aus der Ferne, andere Übersetzungsmöglichkeit: im Voraus."] },
      { number: 12, text: "An jenem Tag befahl Gott, der Herr der Heere, zu weinen und zu klagen, / sich eine Glatze zu scheren und Trauergewänder zu tragen." },
      { number: 13, text: "Doch was sieht man: Freude und Frohsinn, / Rindertöten und Schafeschlachten, / Fleischessen und Weintrinken, (und ihr sagt:) Lasst uns essen und trinken, / denn morgen sind wir tot.", footnotes: ["1 Kor 15,32"] },
      { number: 14, text: "Der Herr der Heere hat mir offenbart: / Diese Schuld wird euch bis zu eurem Tod nicht vergeben, / spricht Gott, der Herr der Heere." },
      { number: 15, text: "So spricht Gott, der Herr der Heere: Auf, geh zu dem Verwalter hier, zu Schebna, dem Palastvorsteher,", heading: "Die Absetzung Schebnas: 22,15-19", footnotes: ["36,3.11.22"] },
      { number: 16, text: "und sag: Wie kommst du dazu und wer bist du denn, dass du dir hier ein Grab aushauen lässt? - Da lässt er sich hoch oben ein Grab aushauen, im Felsen sich eine Wohnung ausmeißeln! -" },
      { number: 17, text: "Gib Acht, der Herr wird dich in hohem Bogen wegschleudern." },
      { number: 18, text: "Er wird dich zu einem Knäuel zusammenwickeln und wie einen Ball in ein geräumiges Land rollen. Dort wirst du sterben; dorthin kommen dann deine Prunkwagen, du Schandfleck im Haus deines Herrn." },
      { number: 19, text: "Ich verjage dich aus deinem Amt, ich vertreibe dich von deinem Posten.", footnotes: ["Ich vertreibe dich: Text korr. nach den alten Übersetzungen (1. statt 3. Person)."] },
      { number: 20, text: "An jenem Tag werde ich meinen Knecht Eljakim, den Sohn Hilkijas, berufen.", heading: "Die Einsetzung Eljakims: 22,20-25" },
      { number: 21, text: "Ich bekleide ihn mit deinem Gewand und lege ihm deine Schärpe um. Ich übergebe ihm dein Amt und er wird für die Einwohner Jerusalems und für das Haus Juda ein Vater sein." },
      { number: 22, text: "Ich lege ihm den Schlüssel des Hauses David auf die Schulter. Wenn er öffnet, kann niemand schließen; wenn er schließt, kann niemand öffnen.", footnotes: ["Mt 16,19; Offb 3,7"] },
      { number: 23, text: "Ich schlage ihn an einer festen Stelle als Pflock ein; er wird in seinem Vaterhaus den Ehrenplatz einnehmen." },
      { number: 24, text: "Wenn sich aber all die vielen Mitglieder seines Vaterhauses mit Kindern und Kindeskindern an ihn hängen, alle die Kännchen, die Töpfe und Krüge,", footnotes: ["mit Kindern und Kindeskindern, wörtlich: die Schösslinge und die Blätter."] },
      { number: 25, text: "an jenem Tag - Spruch des Herrn der Heere - wird der Pflock, den man an der festen Stelle eingeschlagen hat, nachgeben. Er wird herausbrechen und herunterfallen, sodass alles zerbricht, was an ihm aufgehängt war. Wahrhaftig, der Herr hat gesprochen." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Ausspruch über Tyrus. Jammert, ihr Tarschisch-Schiffe; / denn euer Hafen wurde zerstört. Bei der Heimfahrt aus dem Land der Kittäer / wurde es ihnen bekannt.", heading: "Das Gericht über Tyrus und Sidon: 23,1-18", footnotes: ["23,14; 2,16", "1-3: Übersetzung unsicher. Das «Land der Kittäer» ist Zypern."] },
      { number: 2, text: "Verstummt, ihr Bewohner der Küste, / ihr Kaufleute von Sidon, die ihr über das Meer fahrt / und deren Boten das große Wasser überqueren." },
      { number: 3, text: "Die Saaten am Fluss, die Ernten am Nil brachten reichen Ertrag; / Sidon wurde zum Handelsplatz für die Völker." },
      { number: 4, text: "Schäme dich, Sidon; / denn zu dir sagt das Meer [die Festung am Meer]: Ich lag nicht in Wehen und habe nicht geboren / und ich zog weder Söhne noch Töchter groß.", footnotes: ["1,2"] },
      { number: 5, text: "Wenn das die Ägypter erfahren, / zittern sie wie einst bei der Nachricht aus Tyrus." },
      { number: 6, text: "Fahrt nach Tarschisch hinüber; / jammert, ihr Bewohner der Küste!" },
      { number: 7, text: "Ist das eure fröhliche Stadt, / in den Tagen der Vorzeit gegründet, / die Stadt, die ihre Siedler aussandte in weiteste Fernen?" },
      { number: 8, text: "Wer hat das über Tyrus beschlossen, / das einst Kronen verschenkte, dessen Kaufleute wie Fürsten auftraten / und dessen Händler die vornehmsten Herren der Erde waren?", footnotes: ["Offb 18,23"] },
      { number: 9, text: "Der Herr der Heere hat beschlossen, / die ganze Pracht zuschanden zu machen / und den Stolz aller vornehmen Herren der Erde zu brechen." },
      { number: 10, text: "Bebau dein Land [wie am Nil], Tochter Tarschisch, / es gibt keinen Hafen mehr.", footnotes: ["Bebau: Text korr. nach G und dem Qumran-Text."] },
      { number: 11, text: "Der Herr hat seine Hand ausgestreckt über das Meer, / er hat die Königreiche erschüttert. Er hat den Befehl erlassen, / Kanaans Burgen in Trümmer zu legen." },
      { number: 12, text: "Er sagte: Nie mehr sollst du fröhlich sein, / Tochter Sidon, du vergewaltigte Jungfrau. Steh auf, fahr zu den Kittäern - / auch dort findest du keine Ruhe." },
      { number: 13, text: "[Denn die Chaldäer waren das Volk - nicht Assur ist es gewesen -, die Sidon zum Aufenthalt der Wüstentiere machen wollten. Sie stellten ihre Belagerungstürme auf, zerstörten die Paläste und machten sie zu einem Trümmerhaufen.]", footnotes: ["Text durch spätere Zusätze völlig entstellt."] },
      { number: 14, text: "Jammert, ihr Tarschisch-Schiffe, / denn euer Hafen wurde zerstört.", footnotes: ["23,1; 2,16"] },
      { number: 15, text: "Dann wird Tyrus siebzig Jahre lang - solange wie ein König lebt vergessen sein. Nach siebzig Jahren aber wird es Tyrus gehen, wie es im Lied von der Dirne heißt:" },
      { number: 16, text: "Nimm die Zither, / durchstreife die Stadt, / vergessene Dirne! Spiele schön / und singe viel, / damit man an dich denkt." },
      { number: 17, text: "Nach siebzig Jahren wird sich der Herr wieder um Tyrus kümmern: Die Stadt wird wieder ihren Dirnenlohn erhalten und mit allen Königreichen der Welt, die es auf Erden gibt, Unzucht treiben.", footnotes: ["Offb 17,2"] },
      { number: 18, text: "Aber ihr Gewinn und ihr Dirnenlohn wird dem Herrn als heilige Gabe gehören. Er wird nicht angesammelt und gehortet, sondern wird denen, die in der Nähe des Herrn wohnen, als reiche Nahrung und prächtige Kleidung dienen." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Seht her! Der Herr verheert und verwüstet die Erde; / er verändert ihr Gesicht / und zerstreut ihre Bewohner.", heading: "Die Jesaja-Apokalypse: 24,1 - 27,13 | Die Ankündigung des Weltgerichts: 24,1-23", footnotes: ["1-27,13: Dieser Abschnitt wird «Jesaja-Apokalypse» genannt, weil er apokalyptische Motive (Weltgericht, Auferstehung, eschatologisches Heil) enthält. Er stammt aus spätalttestamentlicher Zeit."] },
      { number: 2, text: "Dann geht es dem Laien wie dem Priester, / dem Knecht wie dem Herrn, der Magd wie der Herrin, / dem Käufer wie dem Verkäufer, dem Gläubiger wie dem Schuldner, / dem, der ausleiht, wie dem, der leiht." },
      { number: 3, text: "Verheert wird die Erde, verheert, / geplündert wird sie, geplündert. / Ja, der Herr hat es gesagt." },
      { number: 4, text: "Die Erde welkt, sie verwelkt, / die Welt zerfällt, sie verwelkt, / Himmel und Erde zerfallen.", footnotes: ["33,9; Jer 12,4; 23,10; Am 1,2", "Text korr."] },
      { number: 5, text: "Die Erde ist entweiht durch ihre Bewohner; / denn sie haben die Weisungen übertreten, die Gesetze verletzt, / den ewigen Bund gebrochen.", footnotes: ["Jer 3,2.9", "Anscheinend ist an den Noachbund gedacht (vgl. Gen 9,8-17)."] },
      { number: 6, text: "Darum wird ein Fluch die Erde zerfressen; / ihre Bewohner haben sich schuldig gemacht. Darum schwinden die Bewohner der Erde dahin, / nur wenige Menschen werden übrig gelassen." },
      { number: 7, text: "Der Wein ist dahin, die Rebe verwelkt; / alle, die einst so heiter waren, seufzen und stöhnen." },
      { number: 8, text: "Verstummt ist der fröhliche Klang der Trommeln, / der Lärm der Übermütigen ist zu Ende, / verstummt ist der fröhliche Klang der Zither." },
      { number: 9, text: "Man trinkt keinen Wein mehr bei frohem Gesang, / das Bier der Zecher ist bitter geworden." },
      { number: 10, text: "Die öde Stadt liegt in Trümmern, / alle Häuser sind für den Zutritt verschlossen." },
      { number: 11, text: "Auf den Gassen jammern die Leute: / Es gibt keinen Wein mehr! Jede Freude ist verschwunden, / aller Jubel hat die Erde verlassen." },
      { number: 12, text: "Von der Stadt blieben nur noch Ruinen, / auch das Tor wurde zertrümmert." },
      { number: 13, text: "Dann ist es unter den Völkern auf der Erde, / wie wenn man Oliven abschlägt, / wie bei der Nachlese, wenn die Ernte vorbei ist.", footnotes: ["17,6"] },
      { number: 14, text: "Sie beginnen zu jubeln, / sie preisen die Größe des Herrn. Jauchzt, ihr im Westen,", footnotes: ["Sie: Gemeint sind vielleicht die Übriggebliebenen."] },
      { number: 15, text: "/ ehrt den Herrn, ihr im Osten! Und ihr auf den Inseln im Meer, / preist den Namen des Herrn, des Gottes Israels!", footnotes: ["15a: Text und Übersetzung unsicher."] },
      { number: 16, text: "Von den äußersten Enden der Erde hören wir Lieder: / Preis dem Gerechten! Ich aber sage: / Weh mir! Elend, Elend kommt über mich. Treulose handeln treulos, / ja, die Treulosen brechen die Treue." },
      { number: 17, text: "Grauen, Grube und Garn warten auf euch, / ihr Bewohner der Erde.", footnotes: ["Jer 48,43f", "17a: Der H-Text enthält ein Wortspiel."] },
      { number: 18, text: "Wer dem Lärm des Grauens entflieht, / fällt in die Grube. Wer aus der Grube entkommt, / fängt sich im Garn. Die Schleusen hoch droben werden geöffnet, / die Fundamente der Erde werden erschüttert." },
      { number: 19, text: "Die Erde birst und zerbirst, / die Erde bricht und zerbricht, / die Erde wankt und schwankt." },
      { number: 20, text: "Wie ein Betrunkener taumelt die Erde, / sie schwankt wie eine wacklige Hütte. Ihre Sünden lasten auf ihr; / sie fällt und kann sich nicht mehr erheben.", footnotes: ["1,8"] },
      { number: 21, text: "An jenem Tag wird der Herr hoch droben / das Heer in der Höhe zur Rechenschaft ziehen / und auf der Erde die Könige der Erde.", footnotes: ["Das «Heer in der Höhe» sind die Sterne, die wie die Könige in das Weltgericht hineingezogen werden."] },
      { number: 22, text: "Sie werden zusammengetrieben / und in eine Grube gesperrt; sie werden ins Gefängnis geworfen / und nach einer langen Zeit wird er sie strafen." },
      { number: 23, text: "Dann muss der Mond sich schämen, / muss die Sonne erbleichen. Denn der Herr der Heere ist König auf dem Berg Zion und in Jerusalem, / er offenbart seinen Ältesten seine strahlende Pracht.", footnotes: ["60,19", "23e: Text korr. nach G."] },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Herr, du bist mein Gott, / ich will dich rühmen und deinen Namen preisen. Denn du hast wunderbare Pläne verwirklicht, / von fern her zuverlässig und sicher.", heading: "Der Dank für den Untergang der feindlichen Stadt: 25,1-5" },
      { number: 2, text: "Du hast die Stadt zu einem Steinhaufen gemacht, / die starke Burg zu einem Trümmerfeld, die Paläste der Fremden zu einem verwüsteten Ort, / den man in Ewigkeit nicht mehr aufbaut.", footnotes: ["Die namentlich nicht genannte Stadt ist Symbol für die gottfeindliche Macht; welche Stadt gemeint ist, lässt sich nicht mit Sicherheit sagen."] },
      { number: 3, text: "Darum ehren dich mächtige Völker; / vor dir fürchten sich die Städte der gewalttätigen Nationen." },
      { number: 4, text: "Du bist die Zuflucht der Schwachen, / die Zuflucht der Armen in ihrer Not; du bietest ihnen ein Obdach bei Regen und Sturm / und Schatten bei glühender Hitze. Denn der Sturm der Gewaltigen ist wie ein Regenguss im Winter, / wie die Hitze im trockenen Land.", footnotes: ["4e: im Winter: Text korr."] },
      { number: 5, text: "Du bringst den Lärm der Fremden zum Schweigen, / wie ein Wolkenschatten die Hitze mildert, / das Lied der Gewaltigen lässt du verstummen.", footnotes: ["lässt du verstummen: Text korr. (2. statt 3. Person)."] },
      { number: 6, text: "Der Herr der Heere wird auf diesem Berg / für alle Völker ein Festmahl geben mit den feinsten Speisen, / ein Gelage mit erlesenen Weinen, mit den besten und feinsten Speisen, / mit besten, erlesenen Weinen.", heading: "Das Festmahl auf dem Berg Zion: 25,6-8", footnotes: ["6f.10: Der «Berg» ist der Zion. Hülle und Decke sind Zeichen der Trauer."] },
      { number: 7, text: "Er zerreißt auf diesem Berg die Hülle, die alle Nationen verhüllt, / und die Decke, die alle Völker bedeckt." },
      { number: 8, text: "Er beseitigt den Tod für immer. / Gott, der Herr, wischt die Tränen ab von jedem Gesicht. Auf der ganzen Erde nimmt er von seinem Volk die Schande hinweg. / Ja, der Herr hat gesprochen.", footnotes: ["Hos 13,14; 1 Kor 15,26.54; Offb 7,17"] },
      { number: 9, text: "An jenem Tag wird man sagen: / Seht, das ist unser Gott, auf ihn haben wir unsere Hoffnung gesetzt, / er wird uns retten. Das ist der Herr, auf ihn setzen wir unsere Hoffnung. / Wir wollen jubeln und uns freuen über seine rettende Tat.", heading: "Der Dank für die Vernichtung Moabs: 25,9-12" },
      { number: 10, text: "Ja, die Hand des Herrn ruht auf diesem Berg. / Moab aber wird an Ort und Stelle zerstampft, / wie Stroh in der Jauche zerstampft wird." },
      { number: 11, text: "Wenn Moab darin auch mit den Händen rudert / wie der Schwimmer beim Schwimmen, so drückt er den Stolzen doch nieder, / auch wenn seine Hände sich wehren." },
      { number: 12, text: "Deine festen, schützenden Mauern werden niedergerissen; / der Herr stürzt sie zu Boden; sie liegen im Staub." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "An jenem Tag singt man in Juda dieses Lied: Wir haben eine befestigte Stadt, / zu unserem Schutz baute der Herr Mauern und Wälle.", heading: "Das Siegeslied: 26,1-6" },
      { number: 2, text: "Öffnet die Tore, / damit ein gerechtes Volk durch sie einzieht, / ein Volk, das dem Herrn die Treue bewahrt.", footnotes: ["Ps 118,19f"] },
      { number: 3, text: "Sein Sinn ist fest; / du schenkst ihm Ruhe und Frieden; / denn es verlässt sich auf dich." },
      { number: 4, text: "Verlasst euch stets auf den Herrn; / denn der Herr ist ein ewiger Fels." },
      { number: 5, text: "Er hat die Bewohner des hohen Berges hinabgestürzt, / die hoch aufragende Stadt; er hat sie zu Boden geworfen, / in den Staub hat er sie gestoßen.", footnotes: ["25,12"] },
      { number: 6, text: "Sie wird zermalmt von den Füßen der Armen, / unter den Tritten der Schwachen." },
      { number: 7, text: "Der Weg des Gerechten ist gerade, / du ebnest dem Gerechten die Bahn.", heading: "Das Lied auf die Gerechtigkeit Gottes: 26,7-19" },
      { number: 8, text: "Herr, auf das Kommen deines Gerichts / vertrauen wir. Deinen Namen anzurufen und an dich zu denken / ist unser Verlangen." },
      { number: 9, text: "Meine Seele sehnt sich nach dir in der Nacht, / auch mein Geist ist voll Sehnsucht nach dir. Denn dein Gericht ist ein Licht für die Welt, / die Bewohner der Erde lernen deine Gerechtigkeit kennen.", footnotes: ["9c: ein Licht: eingefügt mit G."] },
      { number: 10, text: "Aber der Frevler lernt nie, was gerecht ist, / auch wenn du ihm Gnade erweist. Selbst im Land der Gerechtigkeit tut er noch Unrecht, / doch er wird den erhabenen Glanz des Herrn nicht erblicken." },
      { number: 11, text: "Herr, deine Hand ist erhoben, / doch deine Gegner sehen es nicht; aber sie werden es sehen / und sie werden beschämt sein von deiner leidenschaftlichen Liebe zu deinem Volk; / ja, Feuer wird sie verzehren.", footnotes: ["Ex 15,6; Hebr 10,27"] },
      { number: 12, text: "Herr, du wirst uns Frieden schenken; / denn auch alles, was wir bisher erreichten, hast du für uns getan." },
      { number: 13, text: "Herr, unser Gott, es beherrschten uns andere Herren als du, / doch nur deinen Namen werden wir rühmen." },
      { number: 14, text: "Die Toten werden nicht leben, / die Verstorbenen stehen nie wieder auf; denn du hast sie bestraft und vernichtet, / jede Erinnerung an sie hast du getilgt.", footnotes: ["26,19", "Die toten Zwingherren werden sich nicht mehr erheben."] },
      { number: 15, text: "Du hast dein Volk vermehrt, o Herr, / du hast es vermehrt; du hast deine Herrlichkeit erwiesen, / auf allen Seiten hast du die Grenzen des Landes erweitert." },
      { number: 16, text: "Herr, in der Not suchten wir dich; / wir schrien in unserer Qual, als du uns straftest.", footnotes: ["Text korr."] },
      { number: 17, text: "Wie eine schwangere Frau, / die nahe daran ist, ihr Kind zu gebären, die sich in ihren Wehen windet und schreit, / so waren wir, Herr, in deinen Augen." },
      { number: 18, text: "Wir waren schwanger und lagen in Wehen; / doch als wir gebaren, war es ein Wind. Wir brachten dem Land keine Rettung, / kein Erdenbewohner wurde geboren." },
      { number: 19, text: "Deine Toten werden leben, / die Leichen stehen wieder auf; / wer in der Erde liegt, wird erwachen und jubeln. Denn der Tau, den du sendest, / ist ein Tau des Lichts; / die Erde gibt die Toten heraus.", footnotes: ["26,14", "Im Bild von der Auferstehung wird von der Wiederherstellung Israels gesprochen."] },
      { number: 20, text: "Auf, mein Volk, geh in deine Kammern / und verschließ die Tür hinter dir! Verbirg dich für kurze Zeit, / bis der Zorn vergangen ist.", heading: "Das Gericht über die Feinde: 26,20 - 27,1" },
      { number: 21, text: "Denn der Herr verlässt den Ort, wo er ist, / um die Erdenbewohner für ihre Schuld zu bestrafen. Dann deckt die Erde das Blut, das sie trank, wieder auf / und verbirgt die Ermordeten nicht mehr in sich.", footnotes: ["Das Blut, das nicht mit Erde bedeckt ist, schreit nach Rache (vgl. Gen 4,10f; Ijob 16,18)."] },
    ] },
    { number: 27, verses: [
      { number: 1, text: "An jenem Tag bestraft der Herr mit seinem harten, großen, starken Schwert den Leviatan, die schnelle Schlange, den Leviatan, die gewundene Schlange. Den Drachen im Meer wird er töten.", footnotes: ["Ijob 26,13; Ps 74,14", "Leviatan, gewundene Schlange und Drache bezeichnen in mythischer Sprache das Meer; sie werden zum Bild für die gottfeindliche Macht."] },
      { number: 2, text: "An jenem Tag gibt es einen prächtigen Weinberg. / Besingt ihn in einem Lied!", heading: "Das Lied vom Weinberg Israel: 27,2-6", footnotes: ["(2-6) 5,1-7"] },
      { number: 3, text: "Ich, der Herr, bin sein Wächter, / immer wieder bewässere ich ihn. Damit niemand ihm schadet, / bewache ich ihn bei Tag und bei Nacht." },
      { number: 4, text: "Ich habe jetzt keinen Zorn mehr. / Fände ich Dornen und Disteln darin, ich würde sie alle bekämpfen, / ich würde sie alle zusammen verbrennen," },
      { number: 5, text: "es sei denn, man sucht bei mir Schutz / und schließt mit mir Frieden, ja Frieden mit mir." },
      { number: 6, text: "In künftigen Tagen schlägt Jakob wieder Wurzel, / Israel blüht und gedeiht / und der Erdkreis füllt sich mit Früchten." },
      { number: 7, text: "Hat er sie geschlagen, / wie er ihre Schläger schlug? Hat er sie umgebracht, / wie er ihre Mörder umgebracht hat?", heading: "Die Entsühnung Israels: 27,7-9", footnotes: ["ihre Mörder: Text korr. nach dem Qumran-Text."] },
      { number: 8, text: "Du hast sie aufgescheucht und verjagt / und so gegen sie den Rechtsstreit geführt. Mit einem heftigen Sturm / jagte er sie am Tag des Ostwinds davon." },
      { number: 9, text: "Darum sei dadurch Jakobs Schuld wieder gesühnt, / darin bestehe die volle Befreiung von seiner Sünde, dass er alle Altarsteine vernichtet, wie man Kalksteine zerschlägt. / Nie mehr soll man Kultpfähle und Rauchopferaltäre errichten.", footnotes: ["17,8"] },
      { number: 10, text: "Ja, die befestigte Stadt ist einsam geworden, / ein entvölkerter Ort, verlassen wie die Steppe. Dort weiden die Rinder und legen sich nieder. / Sie fressen die Zweige ab.", heading: "Die Bestrafung des Gegners: 27,10-11" },
      { number: 11, text: "Wenn dann die Äste verdorren, bricht man sie ab / und die Frauen kommen und machen Feuer damit. Es ist ein Volk ohne Einsicht; / deshalb hat sein Schöpfer kein Erbarmen mit ihm, / er, der es geformt hat, ist ihm nicht gnädig." },
      { number: 12, text: "An jenem Tag wird der Herr Ähren ausklopfen / vom Eufrat bis zum Grenzbach Ägyptens; dann, ihr Söhne Israels, liest man euch auf, / einen nach dem andern.", heading: "Die Sammlung der verstreuten Israeliten: 27,12-13", footnotes: ["12f: Der Text spricht von der Sammlung und Heimkehr der Juden aus der Diaspora."] },
      { number: 13, text: "An jenem Tag wird man das große Widderhorn blasen, / dann kommen die Verirrten aus Assur nach Hause / und die in Ägypten Verstreuten kehren zurück; sie fallen vor dem Herrn in Jerusalem nieder, / auf dem heiligen Berg." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Weh der stolzen Krone der betrunkenen Efraimiter, / ihrem verwelkten Kranz von prächtigen Blumen, auf dem Gipfel über dem fruchtbaren Tal / derer, die der Wein überwältigt hat.", heading: "Worte über Israel und Juda aus der späteren Zeit des Propheten: 28,1 - 35,10 | Der Untergang Samarias: 28,1-4", footnotes: ["1-4: Die «Krone auf dem Berg» ist Samaria. Dieses Bild und das vom Blumenkranz durchdringen sich."] },
      { number: 2, text: "Seht, der Herr schickt einen gewaltigen Helden: / Wie ein Hagelschlag, wie ein verheerender Sturm, wie ein Wolkenbruch mit seinen mächtigen Fluten / wirft er alles mit Macht zu Boden." },
      { number: 3, text: "Mit seinen Füßen zertritt er die stolze Krone / der betrunkenen Efraimiter." },
      { number: 4, text: "Dann geht es dem verwelkten Kranz von prächtigen Blumen, / auf dem Gipfel über dem fruchtbaren Tal, / wie einer frühreifen Feige vor der Ernte: Wer sie erblickt, der verschlingt sie, / kaum dass er sie in der Hand hat." },
      { number: 5, text: "An jenem Tag wird der Herr der Heere für den Rest seines Volkes / zu einer herrlichen Krone und einem prächtigen Kranz;", heading: "Der heilige Rest: 28,5-6" },
      { number: 6, text: "er verleiht dem, der zu Gericht sitzt, den Geist des Rechts / und gibt denen Kraft, die den Feind zum Stadttor hinausdrängen.", footnotes: ["6b: den Feind, wörtlich: den Kampf."] },
      { number: 7, text: "Sogar diese hier schwanken, berauscht vom Wein, / und taumeln, betäubt vom Bier. Priester und Propheten schwanken vom Bier, / sind überwältigt vom Wein. Sie taumeln vom Bier, / sie schwanken bei ihren Visionen, / sie torkeln, wenn sie ihr Urteil verkünden.", heading: "Die untreuen Priester und Propheten in Jerusalem: 28,7-15", footnotes: ["29,9"] },
      { number: 8, text: "Alle Tische sind voll von Erbrochenem, / sind voll von Kot bis auf den letzten Fleck." },
      { number: 9, text: "Wen will der Mann denn Erkenntnis lehren, / wem das Gehörte erklären? Kindern, die man eben von der Milch entwöhnte, / die man gerade von der Brust nahm?" },
      { number: 10, text: "Was soll sein Gestammel, sein Papperlapapp, / sein Geschwätz bald hier, / sein Geschwätz bald dort?", footnotes: ["10-13: Papperlapapp: H: zaw lazaw, zaw lazaw, qaw laqaw, qaw laqaw, verächtliche Nachäffung der Propheten. - «Er» in VV. 11f ist Jahwe."] },
      { number: 11, text: "Ja, mit stammelnder Lippe und fremder Zunge / redet er künftig zu diesem Volk.", footnotes: ["33,19; 1 Kor 14,21"] },
      { number: 12, text: "Er hatte zu ihnen gesagt: So findet ihr Ruhe; / gönnt doch den Müden die Rast, / hier ist der Ort der Erholung. / Sie aber wollten nicht hören.", footnotes: ["30,15; Jer 6,16"] },
      { number: 13, text: "Darum ergeht das Wort des Herrn an sie / in Form von Gestammel, von Papperlapapp, von Geschwätz bald hier / und Geschwätz bald dort, damit sie gehen und hintenüberfallen, / damit sie sich verfangen und verstricken / und schließlich zerschellen.", footnotes: ["8,15"] },
      { number: 14, text: "Darum hört das Wort des Herrn, ihr Spötter, / ihr Sprüchemacher bei diesem Volk in Jerusalem.", footnotes: ["14b: Sprüchemacher, andere Übersetzungsmöglichkeit: Herrscher."] },
      { number: 15, text: "Ihr habt gesagt: Wir haben mit dem Tod ein Bündnis geschlossen, / wir haben mit der Unterwelt einen Vertrag gemacht. Wenn die Flut heranbraust, / erreicht sie uns nicht; denn wir haben unsere Zuflucht zur Lüge genommen / und uns hinter der Täuschung versteckt.", footnotes: ["Am 9,10"] },
      { number: 16, text: "Darum - so spricht Gott, der Herr: Seht her, ich lege einen Grundstein in Zion, / einen harten und kostbaren Eckstein, ein Fundament, das sicher und fest ist: / Wer glaubt, der braucht nicht zu fliehen.", heading: "Die ungläubigen Spötter: 28,16-22", footnotes: ["7,9; Röm 9,33; 10,11; 1 Petr 2,6", "16f: ich lege: Text korr. nach G. V.16.17ab unterbrechen den Text; V. 17cd schließt an V. 15 an."] },
      { number: 17, text: "Als Senkblei nehme ich das Recht / und als Wasserwaage die Gerechtigkeit. Aber der Hagelsturm fegt eure Lügenzuflucht hinweg / und das Wasser schwemmt euer Versteck fort;" },
      { number: 18, text: "euer Bündnis mit dem Tod ist dann gelöst, / euer Vertrag mit der Unterwelt hat keinen Bestand. Wenn die Flut heranbraust, / werdet ihr wie zertrampeltes Weideland." },
      { number: 19, text: "Sooft sie heranbraust, reißt sie euch mit. / Morgen für Morgen braust sie heran, / sie kommt bei Tag und bei Nacht. / Dann wird man nur noch mit Entsetzen das Gehörte erklären.", footnotes: ["28,9"] },
      { number: 20, text: "Das Bett ist zu kurz, / man kann sich nicht ausstrecken, die Decke ist zu schmal, / man kann sich nicht einhüllen.", footnotes: ["Anscheinend ein Sprichwort."] },
      { number: 21, text: "Denn der Herr wird sich erheben wie am Berg Perazim, / wie im Tal bei Gibeon wird er toben und seine Tat vollbringen, seine seltsame Tat, / sein Werk vollenden, sein befremdliches Werk.", footnotes: ["2 Sam 5,17-25; Jos 10,10-14"] },
      { number: 22, text: "Darum lasst jetzt euren Spott, / sonst werden eure Fesseln noch fester. Denn ich habe es von Gott, dem Herrn der Heere, gehört: / Die Vernichtung der ganzen Welt ist beschlossen.", footnotes: ["10,23"] },
      { number: 23, text: "Horcht auf, hört meine Stimme, / gebt Acht, hört auf mein Wort!", heading: "Das Gleichnis vom Bauern: 28,23-29" },
      { number: 24, text: "Pflügt denn der Bauer jeden Tag, um zu säen, / beackert und eggt er denn jeden Tag seine Felder?" },
      { number: 25, text: "Nein, wenn er die Äcker geebnet hat, / streut er Kümmel und Dill aus, sät Weizen und Gerste / und an den Rändern den Dinkel." },
      { number: 26, text: "So unterweist und belehrt ihn sein Gott, / damit er es recht macht." },
      { number: 27, text: "Auch fährt man nicht mit dem Dreschschlitten über den Dill / und mit den Wagenrädern über den Kümmel, sondern man klopft den Dill mit dem Stock aus / und den Kümmel mit Stecken." },
      { number: 28, text: "Zermalmt man etwa das Getreide (beim Dreschen)? / Nein, man drischt es nicht endlos, man lässt die Wagenräder und die Hufe der Tiere / nicht darüber gehen, bis es zermalmt ist." },
      { number: 29, text: "Auch dies lehrt der Herr der Heere; / sein Rat ist wunderbar, er schenkt großen Erfolg.", footnotes: ["9,5"] },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Weh dir, Ariël, Ariël, / du Stadt, die David einst belagert hat! / Fügt nur Jahr an Jahr, feiert nur Fest auf Fest!", heading: "Die Belagerung und die Rettung der Stadt: 29,1-8", footnotes: ["2 Sam 5,6-8", "1f: «Ariël» ist Jerusalem. Das Wort bedeutet vielleicht «Opferherd»."] },
      { number: 2, text: "Ich greife Ariël an, / sodass man jammert und stöhnt. / Du wirst für mich wie ein Opferherd sein." },
      { number: 3, text: "Ringsum werde ich dich belagern, / ich ziehe Gräben um dich herum / und schütte Wälle gegen dich auf." },
      { number: 4, text: "Dann wirst du am Boden liegen und winseln, / deine Worte dringen dumpf aus dem Staub. Wie wenn aus der Erde ein Totengeist spricht, / so tönt deine Stimme; / deine Worte sind nur noch ein Geflüster im Staub." },
      { number: 5, text: "Doch wie feiner Staub wird der Haufen der Belagerer sein, wie dahinfliegende Spreu die Schar der Unterdrücker. Dann geschieht es plötzlich, ganz plötzlich:" },
      { number: 6, text: "Vom Herrn der Heere wirst du mit Donner und Getöse und mit lautem Dröhnen heimgesucht, mit Wind und Wirbelsturm und mit Flammen verzehrenden Feuers.", footnotes: ["30,30"] },
      { number: 7, text: "Dann wird es dem Haufen all der Völker, die gegen Ariël kämpfen, all denen, die es umzingeln, belagern und bestürmen, gehen wie in einem Traum, einer nächtlichen Vision:" },
      { number: 8, text: "Wie wenn ein Hungriger träumt, dass er isst, dann aber aufwacht und immer noch hungrig ist, und wie wenn ein Durstiger träumt, dass er trinkt, dann aber aufwacht und immer noch matt ist und Durst hat, so wird es dem Haufen all der Völker gehen, die gegen den Berg Zion in den Krieg ziehen." },
      { number: 9, text: "Starrt einander an und erstarrt, / seid verblendet und blind! Seid berauscht, doch nicht vom Wein, / taumelt, doch nicht vom Bier!", heading: "Drohungen gegen die Verblendeten: 29,9-16", footnotes: ["28,7", "9ab: In H ein Wortspiel."] },
      { number: 10, text: "Denn der Herr hat über euch einen Geist der Ohnmacht gebracht; / er hat eure Augen [die Propheten] verschlossen und euren Kopf [die Seher] verhüllt.", footnotes: ["19,14"] },
      { number: 11, text: "So wurde für euch jede Offenbarung wie die Worte in einem versiegelten Buch: Wenn man es einem Menschen gibt, der lesen kann, und zu ihm sagt: Lies es mir vor!, dann antwortet er: Ich kann es nicht lesen, denn es ist versiegelt." },
      { number: 12, text: "Und wenn man das Buch einem Mann gibt, der nicht lesen kann, und zu ihm sagt: Lies es mir vor!, dann antwortet er: Ich kann nicht lesen." },
      { number: 13, text: "Der Herr sagte: Weil dieses Volk sich mir nur mit Worten nähert / und mich bloß mit den Lippen ehrt, / sein Herz aber fern hält von mir, weil seine Furcht vor mir / nur auf einem angelernten menschlichen Gebot beruht,", footnotes: ["13b: mit Worten, wörtlich: mit seinem Mund."] },
      { number: 14, text: "darum will auch ich in Zukunft an diesem Volk seltsam handeln, / so seltsam, wie es niemand erwartet. Dann wird die Weisheit seiner Weisen vergehen / und die Klugheit seiner Klugen verschwinden.", footnotes: ["1 Kor 1,19"] },
      { number: 15, text: "Weh denen, die ihre geheimen Pläne vor dem Herrn verbergen, / damit im Dunkel bleibt, was sie tun. Sie sagen: Wer sieht uns schon / und wer kennt uns?" },
      { number: 16, text: "Weh euch, die ihr alles verdreht. / Ist denn der Ton so viel wie der Töpfer? Sagt denn das Werk von dem, der es herstellt: / Er hat mich nicht gemacht? Oder sagt der Topf von dem Töpfer: / Er versteht nichts?", footnotes: ["45,9; Jer 18,1-6; Röm 9,20f", "Töpfer und Ton sind Bild für die vollständige Abhängigkeit des Menschen von Gott."] },
      { number: 17, text: "Nur noch kurze Zeit, / dann verwandelt sich der Libanon in einen Garten / und der Garten wird zu einem Wald.", heading: "Friede und Glück für Israel: 29,17-24", footnotes: ["32,15"] },
      { number: 18, text: "An jenem Tag hören alle, die taub sind, / sogar Worte, die nur geschrieben sind, / und die Augen der Blinden sehen selbst im Dunkeln und Finstern." },
      { number: 19, text: "Die Erniedrigten freuen sich wieder über den Herrn / und die Armen jubeln über den Heiligen Israels." },
      { number: 20, text: "Denn der Unterdrücker ist nicht mehr da, / der Schurke ist erledigt, / ausgerottet sind alle, die Böses tun wollen,", footnotes: ["16,4"] },
      { number: 21, text: "die andere als Verbrecher verleumden, / die dem Richter, der am Tor sitzt, Fallen stellen / und den Unschuldigen um sein Recht bringen mit haltlosen Gründen." },
      { number: 22, text: "Darum - so spricht der Herr zum Haus Jakob, / der Herr, der Abraham losgekauft hat: Nun braucht sich Jakob nicht mehr zu schämen, / sein Gesicht muss nicht mehr erbleichen." },
      { number: 23, text: "Wenn das Volk sieht, was meine Hände in seiner Mitte vollbringen, / wird es meinen Namen heilig halten. Es wird den Heiligen Jakobs als heilig verehren / und erschrecken vor Israels Gott." },
      { number: 24, text: "Dann kommen die Verwirrten zur Einsicht / und wer aufsässig war, lässt sich belehren." },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Weh den trotzigen Söhnen - Spruch des Herrn -, / die einen Plan ausführen, der nicht von mir ist, und ein Bündnis schließen, / das nicht nach meinem Sinn ist; / sie häufen Sünde auf Sünde.", heading: "Die Nutzlosigkeit des Bündnisses mit Ägypten: 30,1-7", footnotes: ["31,1; 2 Kön 18,21; Jer 2,18"] },
      { number: 2, text: "Sie machen sich auf den Weg nach Ägypten, / ohne meinen Mund zu befragen. Sie suchen beim Pharao Zuflucht und Schutz / und flüchten in den Schatten Ägyptens." },
      { number: 3, text: "Doch der Schutz des Pharao bringt euch nur Schande, / die Flucht in den Schatten Ägyptens bringt euch nur Schmach." },
      { number: 4, text: "Wenn auch Israels Fürsten nach Zoan gingen / und seine Boten nach Hanes:", footnotes: ["Zu Zoan vgl. die Anmerkung zu 19,11. Hanes: südlich von Memfis. Hierher kommen die Boten Hiskijas 703/2 v. Chr."] },
      { number: 5, text: "Sie werden doch alle enttäuscht von dem Volk, / das nichts nützt, das niemand Nutzen und Hilfe verschafft, / sondern nur Schande und Schmach bringt." },
      { number: 6, text: "Ausspruch über die Tiere des Negeb: Durch ein Land der Ängste und Nöte, / der jungen Löwen, der knurrenden Löwen, / der Nattern und fliegenden Schlangen bringen sie ihren Reichtum / auf dem Rücken der Esel, ihre Schätze auf dem Höcker der Kamele / zu dem Volk, das nichts nützt.", footnotes: ["Spricht von den Gefahren, die für die Gesandtschaft Hiskijas von den Tieren drohen."] },
      { number: 7, text: "Nichtig und nutzlos ist die Hilfe Ägyptens; / darum nenne ich es: die untätige Rahab.", footnotes: ["Ägypten wird sich nicht, wie einst Rahab (das Urmeer), die gegen den Schöpfergott kämpfte, erheben, sondern untätig bleiben."] },
      { number: 8, text: "Nun geh, schreib es vor ihren Augen auf eine Tafel, / ritz es als Inschrift ein, / damit es für künftige Zeiten auf immer bezeugt ist:", heading: "Die Bestrafung des trotzigen Volkes: 30,8-17" },
      { number: 9, text: "Sie sind ein trotziges Volk, missratene Söhne, / Söhne, die auf die Weisung des Herrn nicht hören.", footnotes: ["1,4"] },
      { number: 10, text: "Sie sagen zu den Sehern: Seht nichts!, / und zu den Propheten: Erschaut für uns ja nicht, was wahr ist, sondern sagt, was uns schmeichelt, / erschaut für uns das, was uns täuscht.", footnotes: ["Am 2,12; 7,12f", "10b: zu den Propheten; wörtlich: zu den Schauenden."] },
      { number: 11, text: "Weicht nur ab vom rechten Weg, / verlasst den richtigen Pfad, / lasst uns in Ruhe mit dem Heiligen Israels!" },
      { number: 12, text: "Darum - so spricht der Heilige Israels: / Weil ihr dieses Wort missachtet, weil ihr auf Ränke vertraut / und euch auf das Falsche verlasst," },
      { number: 13, text: "darum wird eure Schuld für euch sein / wie ein herabfallendes Bruchstück / von einer hoch aufragenden Mauer, / die dann plötzlich, urplötzlich einstürzt." },
      { number: 14, text: "Sie zerbricht wie der Krug eines Töpfers, / den man ohne Erbarmen zerschlägt, sodass sich unter all den Stücken keine Scherbe mehr findet, / mit der man Feuer vom Herd holen kann / oder Wasser schöpfen aus der Zisterne." },
      { number: 15, text: "Denn so spricht der Herr, der Heilige Israels: Nur in Umkehr und Ruhe liegt eure Rettung, / nur Stille und Vertrauen verleihen euch Kraft. Doch ihr habt nicht gewollt, /" },
      { number: 16, text: "sondern gesagt: Nein, auf Rossen wollen wir dahinfliegen. / Darum sollt ihr jetzt fliehen. Ihr habt gesagt: Auf Rennpferden wollen wir reiten. / Darum rennen die Verfolger euch nach.", footnotes: ["In H zwei Wortspiele."] },
      { number: 17, text: "Tausende werden zittern, wenn ein Einziger droht, / wenn nur fünf euch drohen, ergreift ihr alle die Flucht, bis das, was von euch übrig ist, aussieht, / wie ein Fahnenmast auf dem Gipfel eines Berges, / wie ein Feldzeichen auf dem Hügel.", footnotes: ["Dtn 32,30", "17a: werden zittern: Text korr."] },
      { number: 18, text: "Darum wartet der Herr darauf, / euch seine Gnade zu zeigen, darum erhebt er sich, / um euch sein Erbarmen zu schenken. Denn der Herr ist ein Gott des Rechtes; / wohl denen, die auf ihn warten.", heading: "Die Begnadigung des Volkes: 30,18-26", footnotes: ["Ps 2,12"] },
      { number: 19, text: "Ja, du Volk auf dem Berg Zion, / das in Jerusalem wohnt, / du brauchst jetzt nicht mehr zu weinen. Der Herr ist dir gnädig, wenn du um Hilfe schreist; / er wird dir antworten, sobald er dich hört." },
      { number: 20, text: "Auch wenn dir der Herr bisher nur wenig Brot und nicht genug Wasser gab, / so wird er, dein Lehrer, sich nicht mehr verbergen. Deine Augen werden deinen Lehrer sehen, /" },
      { number: 21, text: "deine Ohren werden es hören, wenn er dir nachruft: Hier ist der Weg, auf ihm müsst ihr gehen, / auch wenn ihr selbst rechts oder links gehen wolltet." },
      { number: 22, text: "Dann wirst du deine Götzen aus Silber / und deine Götterbilder aus Gold entweihen. Wie Abfall wirfst du sie weg und sagst: / Hinaus mit euch!" },
      { number: 23, text: "Dann spendet er Regen für die Saat, / die du auf den Acker gesät hast. Das Korn, das auf dem Acker heranreift, / wird üppig und fett sein. / Auf weiten Wiesen weidet dein Vieh an jenem Tag." },
      { number: 24, text: "Die Rinder und Esel, die dir bei der Feldarbeit helfen, / bekommen würziges Futter zu fressen, / das man mit Schaufel und Gabel gemischt hat." },
      { number: 25, text: "Auf allen hohen Bergen und stattlichen Hügeln / gibt es Bäche voll Wasser am Tag des großen Mordens, / wenn die Türme einstürzen.", footnotes: ["2,14", "25cd.26: Apokalyptischer Ausblick auf die Endzeit."] },
      { number: 26, text: "Zu der Zeit, wenn der Herr die Leiden seines Volkes heilt und seine Wunden verbindet, wird das Licht des Mondes so hell sein wie das Licht der Sonne und das Licht der Sonne wird siebenmal so stark sein wie das Licht von sieben Tagen." },
      { number: 27, text: "Seht her, der Herr [sein Name] kommt aus der Ferne. / Sein Zorn ist entflammt, / gewaltig drohend, zieht er heran. Seine Lippen sind voll grollendem Zorn, / seine Zunge ist wie ein verzehrendes Feuer,", heading: "Das Gericht über Assur: 30,27-33", footnotes: ["29,6"] },
      { number: 28, text: "sein Atem wie ein reißender Bach, / der bis an den Hals reicht. Er spannt die Völker ins Joch / und legt den Nationen den Zaum an, / um sie in die Irre und ins Unheil zu führen." },
      { number: 29, text: "Dann singt ihr Lieder wie in der Nacht, / in der man sich heiligt für das Fest. Ihr freut euch von Herzen / wie die Pilger, die unter dem Klang ihrer Flöten / zum Berg des Herrn, zu Israels Felsen, hinaufziehen." },
      { number: 30, text: "Der Herr lässt seine mächtige Stimme erschallen / und man sieht, wie sein Arm herabzuckt mit zornigem Grollen und verzehrendem Feuer, / mit Sturm, Gewitter und Hagel.", footnotes: ["30,27; 29,6"] },
      { number: 31, text: "Vor der Stimme des Herrn wird Assur erschrecken, / wenn er zuschlägt mit seinem Stock," },
      { number: 32, text: "jedes Mal, wenn die Zuchtrute auf Assur herabsaust, / mit der der Herr auf es einschlägt. Unter dem Klang von Pauken und Zithern / und bei schwungvollem Reigentanz kämpft er gegen Assur.", footnotes: ["Text unsicher."] },
      { number: 33, text: "Ja, schon längst ist eine Feuerstelle bereitet, / auch für den König ist sie bestimmt; / tief ist sie und weit; ein Holzstoß ist da, Feuer und Brennholz in Menge, / der Atem des Herrn brennt darin wie ein Schwefelstrom." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Weh denen, die nach Ägypten ziehen, / um Hilfe zu finden, / und sich auf Pferde verlassen, die auf die Menge ihrer Wagen vertrauen / und auf ihre zahlreichen Reiter. Doch auf den Heiligen Israels blicken sie nicht / und fragen nicht nach dem Herrn.", heading: "Erneute Warnung vor Ägypten: 31,1-3", footnotes: ["(1-3) 30,1-7"] },
      { number: 2, text: "Aber auch er ist klug; er führt das Unheil herbei; / er nimmt sein Wort nicht zurück. Er erhebt sich gegen dieses Haus von Verbrechern / und gegen die Helfer derer, die Böses tun." },
      { number: 3, text: "Auch der Ägypter ist nur ein Mensch und kein Gott, / seine Pferde sind nur Fleisch, nicht Geist. Streckt der Herr seine Hand aus, / dann kommt der Beschützer zu Fall und ebenso fällt auch sein Schützling; / sie gehen alle beide zugrunde." },
      { number: 4, text: "So hat der Herr zu mir gesagt: Wie der Löwe über seiner Beute knurrt, / der junge Löwe, gegen den man alle Hirten zusammenruft, wie er vor ihrem Geschrei nicht erschrickt / und sich bei ihrem Lärm nicht duckt, so ist der Herr der Heere, wenn er herabsteigt, / um auf dem Gipfel des Berges Zion, auf seiner Anhöhe, zu kämpfen.", heading: "Jahwes Kampf für seine Getreuen: 31,4-9" },
      { number: 5, text: "Wie ein Vogel mit ausgebreiteten Flügeln / wird der Herr der Heere Jerusalem schützen, / es beschirmen und befreien, verschonen und retten." },
      { number: 6, text: "Kehrt um zu ihm, Israels Söhne, / zu ihm, von dem ihr euch so weit entfernt habt." },
      { number: 7, text: "An jenem Tag verachtet ihr all die silbernen und goldenen Götzen, / die ihr mit euren schuldbefleckten Händen gemacht habt.", footnotes: ["2,20"] },
      { number: 8, text: "Assur wird fallen durch das Schwert, doch nicht durch das eines Mannes; / das Schwert wird es vernichten, doch nicht das eines Menschen. Es wird vor dem Schwert die Flucht ergreifen, / seine jungen Männer werden zur Fron gezwungen.", footnotes: ["8f: Assur wird durch das Eingreifen Gottes vernichtet. - Seine starken Helden vergehen, wörtlich: Sein Fels vergeht. - Feuer und Ofen (vgl. Gen 15,17) sind Zeichen der göttlichen Gegenwart."] },
      { number: 9, text: "Seine starken Helden vergehen vor Grauen, / seine Fürsten lassen die Feldzeichen im Stich - Spruch des Herrn, der in Zion einen Feuerherd hat, / in Jerusalem einen glühenden Ofen." },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Seht: Ein König wird kommen, der gerecht regiert, / und Fürsten, die herrschen, wie es recht ist.", heading: "Das Reich der Gerechtigkeit: 32,1-8", footnotes: ["11,3-5; Jer 23,5"] },
      { number: 2, text: "Jeder von ihnen wird wie ein Zufluchtsort vor dem Sturm sein, / wie ein schützendes Dach beim Gewitter, wie Wassergräben an einem dürren Ort, / wie der Schatten eines mächtigen Felsens im trockenen Land." },
      { number: 3, text: "Dann sind die Augen der Sehenden nicht mehr verklebt, / die Ohren der Hörenden hören wieder zu.", footnotes: ["(3-5) 6,9f"] },
      { number: 4, text: "Das Herz der Unbesonnenen gewinnt Erkenntnis und Einsicht, / die Zunge der Stammelnden redet wieder deutlich und klar." },
      { number: 5, text: "Der Dummkopf wird nicht mehr edel genannt / und der Schurke wird nicht mehr für vornehm gehalten." },
      { number: 6, text: "Denn der Dummkopf redet nur Unsinn / und er hat nur Unheil im Sinn, er handelt ruchlos / und redet lästerlich über den Herrn. Er lässt den Hungrigen darben, / den Durstigen lässt er nicht trinken." },
      { number: 7, text: "Die Waffen des Schurken bringen Unglück, / er plant nur Verbrechen, um die Schwachen durch trügerische Worte ins Verderben zu stürzen, / selbst wenn der Arme beweist, dass er im Recht ist." },
      { number: 8, text: "Der Edle aber plant nur Edles / und tritt für das Edle ein." },
      { number: 9, text: "Ihr sorglosen Frauen, hört meine Stimme, / ihr selbstsicheren Töchter, hört auf mein Wort!", heading: "Die leichtsinnigen Frauen: 32,9-14", footnotes: ["Am 4,1-3; 6,1; (9-14) 3,16 - 4,1"] },
      { number: 10, text: "Über Jahr und Tag werdet ihr zittern, / auch wenn ihr jetzt so selbstsicher seid; denn die Weinernte ist vernichtet, / es gibt keine Obsternte mehr." },
      { number: 11, text: "Zittert, ihr Sorglosen, / erschreckt, ihr selbstsicheren Frauen, zieht euch aus, entkleidet euch / und legt das Trauerkleid an!" },
      { number: 12, text: "Schlagt euch an die Brust / und klagt um die prächtigen Felder, / die fruchtbaren Reben," },
      { number: 13, text: "um die Äcker meines Volkes, / auf denen nur Dornen und Disteln wachsen, um all die Häuser voll Jubel, / um die fröhliche Stadt." },
      { number: 14, text: "Denn die Paläste sind verlassen, / der Lärm der Stadt ist verstummt. Der Hügel der Burg mit dem Wachtturm / ist für immer verödet; dort tummeln sich die Wildesel, / dort weiden die Herden." },
      { number: 15, text: "Wenn aber der Geist aus der Höhe über uns ausgegossen wird, / dann wird die Wüste zum Garten / und der Garten wird zu einem Wald.", heading: "Die Wirkung des Geistes aus der Höhe: 32,15-20", footnotes: ["29,17"] },
      { number: 16, text: "In der Wüste wohnt das Recht, / die Gerechtigkeit weilt in den Gärten." },
      { number: 17, text: "Das Werk der Gerechtigkeit wird der Friede sein, / der Ertrag der Gerechtigkeit sind Ruhe und Sicherheit für immer.", footnotes: ["30,15"] },
      { number: 18, text: "Mein Volk wird an einer Stätte des Friedens wohnen, / in sicheren Wohnungen, an stillen und ruhigen Plätzen.", footnotes: ["33,20"] },
      { number: 19, text: "Aber der Wald stürzt in jähem Sturz, / die Stadt versinkt in der Tiefe.", footnotes: ["stürzt: Text korr. nach der aramäischen Übersetzung."] },
      { number: 20, text: "Wohl euch! Ihr könnt an allen Gewässern säen / und eure Rinder und Esel frei laufen lassen." },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Weh dir, der du immer zerstörst / und selbst nie zerstört worden bist. Weh dir, du Empörer, / gegen den sich noch niemand empört hat. Denn wenn du alles zerstört hast, / wirst du selbst zerstört. Wenn du das Ziel deiner Empörung erreicht hast, / wirst du selbst zum Ziel einer Empörung.", heading: "Der Weheruf über den Feind: 33,1", footnotes: ["21,2"] },
      { number: 2, text: "Herr, hab mit uns Erbarmen; / denn wir hoffen auf dich. Sei uns ein helfender Arm an jedem Morgen, / sei in der Not unsere Rettung!", heading: "Ein Gebet in der Not: 33,2-6" },
      { number: 3, text: "Vor dem lauten Getöse fliehen die Nationen; / wenn du dich erhebst, flüchten die Völker nach allen Seiten." },
      { number: 4, text: "Man rafft Beute zusammen, / so wie die Heuschrecken alles erraffen; wie die Grashüpfer springen, / so springt man und raubt." },
      { number: 5, text: "Der Herr ist erhaben, er wohnt in der Höhe; / er wird Zion mit Recht und Gerechtigkeit erfüllen.", footnotes: ["1,21"] },
      { number: 6, text: "Es wird sichere Zeiten erleben. / Weisheit und Erkenntnis sind der Reichtum, der es rettet; / sein Schatz ist die Furcht vor dem Herrn." },
      { number: 7, text: "Hört: Draußen schreien die Krieger (vor Angst), / bitterlich weinen die Boten des Friedens.", heading: "Die Verödung des Landes: 33,7-9", footnotes: ["29,1f; 22,4", "die Krieger: in H ein Wort unsicherer Bedeutung."] },
      { number: 8, text: "Die Wege sind verödet, / die Straßen sind leer. Den Vertrag hat man gebrochen, / man verachtet die Zeugen (des Bundes) / und schätzt die Menschen gering.", footnotes: ["Ri 5,6", "8f: die Zeugen: Text korr. nach dem Qumran-Text. - Scharon: die fruchtbare Ebene zwischen Jaffa und Karmel."] },
      { number: 9, text: "Das Land welkt in Trauer dahin, / der Libanon ist beschämt, seine Bäume verdorren. Die Scharon-Ebene ist zur Steppe geworden, / entlaubt sind Baschan und Karmel.", footnotes: ["24,4; Am 1,2"] },
      { number: 10, text: "Jetzt stehe ich auf, spricht der Herr, / jetzt erhebe ich mich, jetzt richte ich mich auf.", heading: "Das Gericht über die Sünder: 33,10-16", footnotes: ["Ps 12,6"] },
      { number: 11, text: "Ihr seid schwanger mit Heu / und bringt Stroh zur Welt. / Mein Atem ist wie ein Feuer, das euch verzehrt.", footnotes: ["11c: Mein Atem: Text korr. nach Vg und der aramäischen Übersetzung; H: Euer Atem."] },
      { number: 12, text: "Die Völker werden zu Kalk verbrannt. / Sie lodern wie abgehauene Dornen im Feuer." },
      { number: 13, text: "Ihr in der Ferne, hört, was ich tue; / ihr in der Nähe, erkennt meine Kraft!" },
      { number: 14, text: "Die Sünder in Zion beginnen zu zittern, / ein Schauder erfasst die ruchlosen Menschen. Wer von uns hält es aus neben dem verzehrenden Feuer, / wer von uns hält es aus neben der ewigen Glut?", footnotes: ["Dtn 4,24; Hebr 12,29"] },
      { number: 15, text: "Wer rechtschaffen lebt und immer die Wahrheit sagt, / wer es ablehnt, Gewinn zu erpressen, wer sich weigert, Bestechungsgelder zu nehmen, / wer sein Ohr verstopft, um keinen Mordplan zu hören, / und die Augen schließt, um nichts Böses zu sehen:", footnotes: ["Ps 15; 24,3-5"] },
      { number: 16, text: "der wird auf den Bergen wohnen, / Felsenburgen sind seine Zuflucht; man reicht ihm sein Brot / und seine Wasserquelle versiegt nicht." },
      { number: 17, text: "Deine Augen werden den König in seiner Schönheit erblicken, / sie sehen ein weites Land.", heading: "Die bessere Zukunft: 33,17-24", footnotes: ["17f: Der «König» ist Gott (vgl. V. 22). - V. 18 denkt an Tribut und Steuern, die fremde Beamte eintreiben."] },
      { number: 18, text: "Dein Herz denkt an die (früheren) Schrecken zurück: / Wo ist der, der damals zählte, wo ist der, der abwog? / Wo ist der, der die Türme gezählt hat?" },
      { number: 19, text: "Du wirst das freche Volk nicht mehr sehen, / das Volk mit der dunklen, unverständlichen Sprache, / mit den gestammelten, sinnlosen Worten." },
      { number: 20, text: "Schau auf Zion, die Stadt unserer Feste! / Deine Augen werden Jerusalem sehen, den Ort der Ruhe, / das Zelt, das man nicht abbricht, dessen Pflöcke man niemals mehr ausreißt, / dessen Stricke nie mehr zerreißen.", footnotes: ["32,18"] },
      { number: 21, text: "Ja, dort wird der Herr unser mächtiger Gott sein. / Es ist ein Ort mit breiten Flüssen und Strömen. Keine Ruderboote fahren auf ihnen; / kein Prunkschiff segelt darauf," },
      { number: 23, text: "denn die Taue sind schlaff, / sie halten den Mastbaum nicht fest, / man kann kein Segel mehr spannen." },
      { number: 22, text: "Ja, der Herr ist unser Richter, / der Herr gibt uns Gesetze; / der Herr ist unser König, er wird uns retten.", footnotes: ["In H ist die Reihenfolge der Sätze durcheinandergeraten."] },
      { number: 23, text: "Dann verteilen die Blinden große Beute untereinander, / die Lahmen machen einen ergiebigen Raubzug.", footnotes: ["23b: verteilen die Blinden: Text korr."] },
      { number: 24, text: "Kein Mensch in der Stadt wird mehr sagen: / Ich bin krank. Dem Volk, das in Zion wohnt, / ist seine Schuld vergeben." },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Kommt her, ihr Völker, und hört, / horcht auf, ihr Nationen! Die Erde und alles, was sie erfüllt, / die Welt und alles, was auf ihr sprosst, sollen es hören,", heading: "Die Ankündigung des Gerichts über Edom: 34,1-17" },
      { number: 2, text: "dass der Herr über alle Völker erzürnt ist, / dass er zornig ist auf all ihre Heere. Er hat sie dem Untergang geweiht / und zum Schlachtopfer bestimmt." },
      { number: 3, text: "Die Erschlagenen wirft man hinaus, / der Gestank ihrer Leichen steigt auf, die Berge triefen von ihrem Blut, /", footnotes: ["Joël 2,20"] },
      { number: 4, text: "alle Hügel zerfließen. Wie eine Buchrolle rollt sich der Himmel zusammen, / sein ganzes Heer welkt dahin, wie Laub am Weinstock verwelkt, / wie Früchte am Feigenbaum schrumpfen.", footnotes: ["4a: Hügel: Text korr.; H: das Heer des Himmels (aus V. 5)."] },
      { number: 5, text: "Am Himmel erscheint das Schwert des Herrn. / Seht her, es fährt auf Edom herab, / auf das Volk, das der Herr im Gericht dem Untergang weiht.", footnotes: ["erscheint: Text korr. nach dem Qumran-Text. - das Schwert des Herrn, wörtlich: mein Schwert."] },
      { number: 6, text: "Das Schwert des Herrn ist voll Blut, / es trieft von Fett, vom Blut der Lämmer und Böcke, / vom Nierenfett der Widder; denn der Herr hält in Bozra ein Opferfest ab, / ein großes Schlachtfest in Edom.", footnotes: ["6e: Bozra: südöstlich des Toten Meeres."] },
      { number: 7, text: "Da fallen die Büffel und Kälber, / die Stiere und Ochsen. Ihr Land wird betrunken vom Blut, / ihr Erdreich ist getränkt von Fett." },
      { number: 8, text: "Denn der Herr hat einen Tag der Rache bestimmt, / ein Jahr der Vergeltung für den Streit um Zion." },
      { number: 9, text: "In Edoms Bächen wird das Wasser zu Pech, / sein Boden verwandelt sich in Schwefel, / sein Land wird zu brennendem Pech." },
      { number: 10, text: "Es erlischt nicht bei Tag und bei Nacht, / der Rauch steigt unaufhörlich empor. Das Land ist für Generationen verödet, / nie mehr zieht jemand hindurch.", footnotes: ["(10-15) 13,20-22"] },
      { number: 11, text: "Dohlen und Eulen nehmen es in Besitz, / Käuze und Raben hausen darin. Der Herr spannt die Messschnur «Öde» darüber, / er legt das Senkblei «Leere» an.", footnotes: ["14,23; Zef 2,14"] },
      { number: 12, text: "Die Bocksgeister werden dort ihr Unwesen treiben. / Die Edlen Edoms leben nicht mehr. Man ruft dort keinen König mehr aus, / mit all seinen Fürsten hat es ein Ende.", footnotes: ["12ab: Ergänzt nach G; in H ist nur erhalten: seine Edlen."] },
      { number: 13, text: "An seinen Palästen ranken sich Dornen empor, / in den Burgen wachsen Nesseln und Disteln. Das Land wird zu einem Ort für Schakale, / zu einem Platz für die Strauße.", footnotes: ["Hos 9,6; Jes 35,7", "13d: Strauße, wörtlich: Töchter der Wüste."] },
      { number: 14, text: "Wüstenhunde und Hyänen treffen sich hier, / die Bocksgeister begegnen einander. Auch Lilit (das Nachtgespenst) ruht sich dort aus / und findet für sich eine Bleibe.", footnotes: ["13,21"] },
      { number: 15, text: "Der Kauz hat hier sein sicheres Nest, / er legt seine Eier und brütet sie aus. Auch die Geier sammeln sich hier, / einer neben dem andern.", footnotes: ["15b: seine Eier: Text korr."] },
      { number: 16, text: "Forscht nach im Buch des Herrn, / dort werdet ihr lesen: Keines dieser Tiere ist ausgeblieben, / keines braucht seinen Gefährten zu suchen; denn der Mund des Herrn hat es befohlen, / sein Geist hat sie zusammengeführt.", footnotes: ["16f: Später wird man im Jesaja-Buch nachlesen und feststellen, dass das Gericht über Edom eingetroffen ist. - der Mund des Herrn: Text korr. nach H-Handschriften und G."] },
      { number: 17, text: "Er selbst hat für sie das Los geworfen, / er hat mit eigener Hand das Land vermessen und ihnen zugeteilt: Für immer sollen sie es besitzen, / von Generation zu Generation darin wohnen." },
    ] },
    { number: 35, verses: [
      { number: 1, text: "Die Wüste und das trockene Land sollen sich freuen, / die Steppe soll jubeln und blühen.", heading: "Die Verheißung des messianischen Heils: 35,1-10" },
      { number: 2, text: "Sie soll prächtig blühen wie eine Lilie, / jubeln soll sie, jubeln und jauchzen. Die Herrlichkeit des Libanon wird ihr geschenkt, / die Pracht des Karmel und der Ebene Scharon. Man wird die Herrlichkeit des Herrn sehen, / die Pracht unseres Gottes." },
      { number: 3, text: "Macht die erschlafften Hände wieder stark / und die wankenden Knie wieder fest!", footnotes: ["Hebr 12,12"] },
      { number: 4, text: "Sagt den Verzagten: / Habt Mut, fürchtet euch nicht! Seht, hier ist euer Gott! / Die Rache Gottes wird kommen und seine Vergeltung; / er selbst wird kommen und euch erretten." },
      { number: 5, text: "Dann werden die Augen der Blinden geöffnet, / auch die Ohren der Tauben sind wieder offen." },
      { number: 6, text: "Dann springt der Lahme wie ein Hirsch, / die Zunge des Stummen jauchzt auf. In der Wüste brechen Quellen hervor / und Bäche fließen in der Steppe." },
      { number: 7, text: "Der glühende Sand wird zum Teich / und das durstige Land zu sprudelnden Quellen. An dem Ort, wo jetzt die Schakale sich lagern, / gibt es dann Gras, Schilfrohr und Binsen." },
      { number: 8, text: "Eine Straße wird es dort geben; / man nennt sie den Heiligen Weg. Kein Unreiner darf ihn betreten. / Er gehört dem, der auf ihm geht. / Unerfahrene gehen nicht mehr in die Irre." },
      { number: 9, text: "Es wird keinen Löwen dort geben, / kein Raubtier betritt diesen Weg, keines von ihnen ist hier zu finden. / Dort gehen nur die Erlösten." },
      { number: 10, text: "Die vom Herrn Befreiten kehren zurück / und kommen voll Jubel nach Zion. Ewige Freude ruht auf ihren Häuptern. / Wonne und Freude stellen sich ein, / Kummer und Seufzen entfliehen.", footnotes: ["51,11"] },
    ] },
    { number: 36, verses: [
      { number: 1, text: "Im vierzehnten Jahr des Königs Hiskija zog Sanherib, der König von Assur, gegen alle befestigten Städte Judas und nahm sie ein.", heading: "Geschichtlicher Anhang: 36,1 - 39,8 | Sanheribs Feldzug gegen Jerusalem: 36,1 - 37,38 | Die erste Gesandtschaft Sanheribs: 36,1-22", footnotes: ["(1-22) 2 Kön 18,13-37; 2 Chr 32,1-19", "1-39,8: Stimmt weithin mit 2 Kön überein; nur das Lied des Hiskija ist Eigengut von Jes. - Vgl. die Anmerkungen zu 2 Kön 18,13 - 20,19."] },
      { number: 2, text: "Der König von Assur sandte den Rabschake mit einer großen Streitmacht von Lachisch aus nach Jerusalem gegen König Hiskija. Er stellte sich an der Wasserleitung des oberen Teiches auf, der an der Walkerfeldstraße liegt.", footnotes: ["Rabschake ist ein assyrischer Beamtentitel, der etwa «Obermundschenk» bedeutet."] },
      { number: 3, text: "Der Palastvorsteher Eljakim, der Sohn Hilkijas, der Staatsschreiber Schebna und der Sprecher des Königs Joach, der Sohn Asafs, gingen zu ihm hinaus." },
      { number: 4, text: "Da sagte der Rabschake zu ihnen: Sagt zu Hiskija: So spricht der Großkönig, der König von Assur: Worauf vertraust du denn, dass du dich so sicher fühlst?" },
      { number: 5, text: "Du glaubst wohl, bloßes Gerede sei im Krieg schon Rat und Stärke? Auf wen vertraust du also, dass du von mir abgefallen bist?" },
      { number: 6, text: "Du vertraust gewiss auf Ägypten, dieses geknickte Schilfrohr, das jeden, der sich darauf stützt, in die Hand sticht und sie durchbohrt. Denn so macht es der Pharao, der König von Ägypten, mit allen, die ihm vertrauen.", footnotes: ["Ez 29,6f"] },
      { number: 7, text: "Wenn ihr aber zu mir sagt: Wir vertrauen auf Jahwe, unseren Gott, dann bedenkt: Ist nicht gerade er der Gott, dessen Kulthöhen und Altäre Hiskija beseitigt hat? Hat nicht Hiskija in Juda und Jerusalem angeordnet: Nur vor diesem Altar in Jerusalem dürft ihr euch niederwerfen?" },
      { number: 8, text: "Geh doch mit meinem Herrn, dem König von Assur, eine Wette ein! Ich gebe dir zweitausend Pferde. Kannst du die Reiter für sie stellen?" },
      { number: 9, text: "Wie willst du auch nur einen einzigen Statthalter meines Herrn in die Flucht schlagen, und wäre es der unbedeutendste seiner Knechte? Du vertraust ja nur auf Ägypten, auf seine Wagen und deren Besatzung." },
      { number: 10, text: "Außerdem: Bin ich denn gegen den Willen Jahwes heraufgezogen, um dieses Land zu verwüsten? Jahwe selbst hat mir befohlen: Zieh gegen dieses Land und verwüste es!" },
      { number: 11, text: "Da sagten Eljakim, Schebna und Joach zu dem Rabschake: Sprich doch aramäisch mit deinen Knechten! Wir verstehen es. Sprich vor den Ohren des Volkes, das auf der Mauer steht, nicht judäisch mit uns!" },
      { number: 12, text: "Der Rabschake antwortete ihnen: Hat mich mein Herr etwa beauftragt, das alles nur zu deinem Herrn und zu dir zu sagen und nicht vielmehr zu all den Männern, die auf der Mauer sitzen und ihren eigenen Kot essen und ihren Harn trinken wie ihr?" },
      { number: 13, text: "Dann trat der Rabschake vor und rief laut auf Judäisch: Hört die Worte des Großkönigs, des Königs von Assur!" },
      { number: 14, text: "So spricht der König: Lasst euch nicht von Hiskija betören; denn er kann euch nicht retten." },
      { number: 15, text: "Er soll euch nicht verleiten, auf Jahwe zu vertrauen, und sagen: Jahwe wird uns sicher retten, diese Stadt wird dem König von Assur nicht in die Hände fallen." },
      { number: 16, text: "Hört nicht auf Hiskija! Denn so spricht der König von Assur: Trefft mit mir ein Abkommen und ergebt euch! Dann kann jeder von euch von seinem Weinstock und von seinem Feigenbaum essen und Wasser aus seiner Zisterne trinken," },
      { number: 17, text: "bis ich komme und euch in ein Land bringe, das eurem Land gleicht: in ein Land voll Getreide und Most, ein Land voll Brot und Wein." },
      { number: 18, text: "Lasst euch von Hiskija nicht in die Irre führen, wenn er sagt: Jahwe wird uns retten. Hat denn einer von den Göttern der anderen Völker sein Land vor dem König von Assur gerettet?" },
      { number: 19, text: "Wo sind die Götter von Hamat und Arpad? Wo sind die Götter von Sefarwajim? Haben sie etwa Samaria vor mir gerettet?" },
      { number: 20, text: "Wer von all den Göttern der anderen Länder hat sein Land vor mir gerettet? Wie sollte denn Jahwe Jerusalem vor mir retten?" },
      { number: 21, text: "Die Männer aber schwiegen und gaben ihm keine Antwort; denn der König hatte befohlen: Ihr dürft ihm nicht antworten." },
      { number: 22, text: "Der Palastvorsteher Eljakim, der Sohn Hilkijas, der Staatsschreiber Schebna und der Sprecher des Königs Joach, der Sohn Asafs, zerrissen ihre Kleider, gingen zu Hiskija und berichteten ihm, was der Rabschake gesagt hatte." },
    ] },
    { number: 37, verses: [
      { number: 1, text: "Als König Hiskija das hörte, zerriss er seine Kleider, legte ein Trauergewand an und ging in das Haus des Herrn.", heading: "Die Reaktion des Königs Hiskija: 37,1-7", footnotes: ["(1-7) 2 Kön 19,1-7"] },
      { number: 2, text: "Dann sandte er den Palastvorsteher Eljakim, den Staatsschreiber Schebna und die Ältesten der Priester in Trauergewändern zum Propheten Jesaja, dem Sohn des Amoz." },
      { number: 3, text: "Sie sagten zu ihm: So spricht Hiskija: Heute ist ein Tag der Not, der Strafe und der Schande. Die Kinder sind bis an die Öffnung des Mutterschoßes gelangt, doch den Frauen fehlt die Kraft zum Gebären.", footnotes: ["Hos 13,13"] },
      { number: 4, text: "Aber vielleicht hört der Herr, dein Gott, die Worte des Rabschake, den sein Herr, der König von Assur, hergesandt hat, damit er den lebendigen Gott beschimpft; und vielleicht schickt der Herr, dein Gott, eine Strafe für die Worte, die er gehört hat. Darum bete für den Rest, der noch übrig ist." },
      { number: 5, text: "Jesaja antwortete den Abgesandten des Königs Hiskija, die zu ihm gekommen waren:" },
      { number: 6, text: "Sagt zu eurem Herrn Folgendes: So spricht der Herr: Fürchte dich nicht wegen der Worte, die du gehört hast und mit denen die Knechte des Königs von Assur mich verhöhnt haben." },
      { number: 7, text: "Seht, ich lege einen Geist in ihn, sodass er ein Gerücht hört und in sein Land zurückkehrt; dort bringe ich ihn durch das Schwert zu Fall." },
      { number: 8, text: "Der Rabschake trat den Rückweg an und fand den König von Assur im Kampf gegen die Stadt Libna. Sanherib war inzwischen, wie der Rabschake gehört hatte, von Lachisch abgezogen.", heading: "Die zweite Gesandtschaft Sanheribs: 37,8-13", footnotes: ["(8-13) 2 Kön 19,8-13"] },
      { number: 9, text: "Dann erfuhr Sanherib, dass Tirhaka, der König von Kusch, zum Kampf gegen ihn heranzog. Er schickte wiederum Boten zu Hiskija mit dem Auftrag:", footnotes: ["wiederum: Text korr. nach 2 Kön 19,9 und G."] },
      { number: 10, text: "So sollt ihr zu Hiskija, dem König von Juda, sagen: Lass dir nicht von deinem Gott, auf den du vertraust, einreden, Jerusalem werde dem König von Assur nicht in die Hände fallen." },
      { number: 11, text: "Du hast doch gehört, was die Könige von Assur mit allen anderen Ländern gemacht haben: Sie haben sie dem Untergang geweiht. Und du meinst, du wirst gerettet?" },
      { number: 12, text: "Sind denn die Völker, die von meinen Vätern vernichtet wurden, von ihren Göttern gerettet worden, die Völker von Gosan, Haran und Rezef, die Söhne von Eden, die in Telassar wohnten?" },
      { number: 13, text: "Wo ist der König von Hamat, der König von Arpad, der König der Stadt Sefarwajim, wo sind die Könige von Hena und Awa?" },
      { number: 14, text: "Hiskija nahm das Schreiben von den Boten in Empfang und las es. Dann ging er zum Haus des Herrn hinauf, breitete das Schreiben vor dem Herrn aus und", heading: "Das Gebet des Königs: 37,14-20", footnotes: ["(14-20) 2 Kön 19,14-19"] },
      { number: 15, text: "betete zum Herrn. Er sagte:" },
      { number: 16, text: "Herr der Heere, Gott Israels, der über den Kerubim thront, du allein bist der Gott aller Reiche der Erde. Du hast den Himmel und die Erde gemacht." },
      { number: 17, text: "Wende mir dein Ohr zu, Herr, und höre! Öffne, Herr, deine Augen und sieh her! Hör alles, was Sanherib sagt, der seinen Boten hergesandt hat, um den lebendigen Gott zu verhöhnen." },
      { number: 18, text: "Es ist wahr, Herr, die Könige von Assur haben alle Völker vernichtet und ihre Länder verwüstet" },
      { number: 19, text: "und ihre Götter ins Feuer geworfen. Aber das waren keine Götter, sondern Werke von Menschenhand, aus Holz und Stein; darum konnte man sie vernichten." },
      { number: 20, text: "Nun aber, Herr, unser Gott, rette uns aus seiner Hand, damit alle Reiche der Erde erkennen, dass du, Jahwe, Gott bist, du allein.", footnotes: ["Gott bist: eingefügt mit 2 Kön 19,19 und dem Qumran-Text."] },
      { number: 21, text: "Jesaja, der Sohn des Amoz, schickte zu Hiskija einen Boten und ließ ihm sagen: So spricht der Herr, der Gott Israels: Ich habe gehört, wie du wegen des Königs Sanherib von Assur zu mir gebetet hast.", heading: "Die Verheißung der göttlichen Hilfe: 37,21-35", footnotes: ["(21-35) 2 Kön 19,20-34", "Ich habe gehört: eingefügt mit 2 Kön 19,20 und G."] },
      { number: 22, text: "Das ist das Wort des Herrn gegen ihn: Dich verachtet, dich verspottet / die Jungfrau, die Tochter Zion. Die Tochter Jerusalem / schüttelt spöttisch den Kopf über dich." },
      { number: 23, text: "Wen hast du beschimpft und verhöhnt, / gegen wen die Stimme erhoben, auf wen voll Hochmut herabgeblickt? / Auf den Heiligen Israels." },
      { number: 24, text: "Durch deine Gesandten hast du den Herrn verhöhnt; / du hast gesagt: Mit meinen zahlreichen Wagen fuhr ich auf die Höhen der Berge, / in die fernsten Winkel des Libanon. Ich fällte seine hohen Zedern, / seine schönsten Zypressen, kam bis zu seinen entlegensten Höhen, / in das Dickicht seiner Wälder." },
      { number: 25, text: "Ich habe Brunnen gegraben / und fremdes Wasser getrunken. Ich ließ unter dem Schritt meiner Füße / alle Ströme Ägyptens vertrocknen.", footnotes: ["fremdes: eingefügt mit 2 Kön 19,24 und dem Qumran-Text."] },
      { number: 26, text: "Hast du es nicht gehört? / Schon vor langer Zeit habe ich es so gefügt, seit den Tagen der Vorzeit habe ich es so geplant, / jetzt ließ ich es kommen. So konntest du befestigte Städte zerstören / und in Trümmer verwandeln." },
      { number: 27, text: "Ihre Bewohner waren machtlos, / in Schrecken und Schande gestoßen. Sie glichen den Pflanzen auf dem Feld, / dem frischen Grün, dem Gras auf den Dächern, / das im Ostwind verdorrt.", footnotes: ["Ps 129,6", "verdorrt: Text korr. nach dem Qumran-Text."] },
      { number: 28, text: "Ich weiß, ob du ruhst, ob du gehst oder kommst, / ob du dich gegen mich auflehnst." },
      { number: 29, text: "Weil du gegen mich wütest / und dein Lärm meine Ohren erreicht hat, ziehe ich dir einen Ring durch die Nase / und lege dir einen Zaum in das Maul. Auf dem Weg, auf dem du herankamst, / treibe ich dich wieder zurück." },
      { number: 30, text: "Und das soll für dich (Hiskija) ein Vorzeichen sein: In diesem Jahr isst man, was von selbst nachwächst, im nächsten Jahr, was wild wächst; im dritten Jahr aber sollt ihr wieder säen und ernten, die Weinberge bepflanzen und ihre Früchte genießen." },
      { number: 31, text: "Wer vom Haus Juda entronnen und übrig geblieben ist, wird unten wieder Wurzeln treiben und oben Frucht tragen." },
      { number: 32, text: "Denn von Jerusalem wird ein Rest (in das Land) hinausziehen, vom Berg Zion ziehen die Geretteten hinaus. Der leidenschaftliche Eifer des Herrn der Heere vollbringt das." },
      { number: 33, text: "Darum - so spricht der Herr über den König von Assur: Er wird nicht in diese Stadt eindringen; er wird keinen einzigen Pfeil hineinschießen, er wird nicht unter dem Schutz seines Schildes gegen sie anrennen und wird keinen Damm gegen sie aufschütten." },
      { number: 34, text: "Auf dem Weg, auf dem er gekommen ist, wird er wieder zurückkehren. Aber in diese Stadt wird er nicht eindringen - Spruch des Herrn." },
      { number: 35, text: "Ich werde diese Stadt beschützen und retten, um meinetwillen und um meines Knechtes David willen." },
      { number: 36, text: "In jener Nacht zog der Engel des Herrn aus und erschlug im Lager der Assyrer hundertfünfundachtzigtausend Mann. Als man am nächsten Morgen aufstand, fand man sie alle als Leichen.", heading: "Die Rettung der Stadt: 37,36-38", footnotes: ["(36-38) 2 Kön 19,35-37"] },
      { number: 37, text: "Da brach Sanherib, der König von Assur, auf und kehrte in sein Land zurück. Er blieb in Ninive." },
      { number: 38, text: "Als er eines Tages im Tempel seines Gottes Nisroch betete, erschlugen ihn seine Söhne Adrammelech und Sarezer mit dem Schwert. Darauf mussten sie in das Land Ararat fliehen und Sanheribs Sohn Asarhaddon wurde an seiner Stelle König." },
    ] },
    { number: 38, verses: [
      { number: 1, text: "In jenen Tagen wurde Hiskija schwer krank und war dem Tod nahe. Der Prophet Jesaja, der Sohn des Amoz, kam zu ihm und sagte: So spricht der Herr: Bestell dein Haus; denn du wirst sterben, du wirst nicht am Leben bleiben.", heading: "Weitere Nachrichten über Jesaja und Hiskija: 38,1 - 39,8 | Die Krankheit Hiskijas: 38,1-8", footnotes: ["(1-8) 2 Kön 20,1-6"] },
      { number: 2, text: "Da drehte sich Hiskija mit dem Gesicht zur Wand und betete zum Herrn:" },
      { number: 3, text: "Ach Herr, denk daran, dass ich mein Leben lang treu und mit aufrichtigem Herzen meinen Weg vor deinen Augen gegangen bin und dass ich immer getan habe, was dir gefällt. Und Hiskija begann laut zu weinen." },
      { number: 4, text: "Da erging das Wort des Herrn an Jesaja:" },
      { number: 5, text: "Geh zu Hiskija und sag zu ihm: So spricht der Herr, der Gott deines Vaters David: Ich habe dein Gebet gehört und deine Tränen gesehen. Ich will zu deiner Lebenszeit noch fünfzehn Jahre hinzufügen." },
      { number: 6, text: "[Und ich will dich und diese Stadt aus der Gewalt des Königs von Assur retten und diese Stadt beschützen.]" },
      { number: 7, text: "Das soll für dich das Zeichen des Herrn sein, dass der Herr sein Versprechen halten wird:" },
      { number: 8, text: "Siehe, ich lasse den Schatten, der auf den Stufen des Ahas bereits herabgestiegen ist, wieder zehn Stufen hinaufsteigen. Da stieg der Schatten auf den Stufen, die er bereits herabgestiegen war, wieder zehn Stufen hinauf." },
      { number: 9, text: "Ein Lied, das König Hiskija von Juda verfasst hat, als er nach seiner Krankheit wieder genesen war:", heading: "Das Danklied Hiskijas: 38,9-20" },
      { number: 10, text: "Ich sagte: In der Mitte meiner Tage / muss ich hinab zu den Pforten der Unterwelt, / man raubt mir den Rest meiner Jahre." },
      { number: 11, text: "Ich sagte: Ich darf den Herrn nicht mehr schauen / im Land der Lebenden, keinen Menschen mehr sehen / bei den Bewohnern der Erde." },
      { number: 12, text: "Meine Hütte bricht man über mir ab, / man schafft sie weg wie das Zelt eines Hirten. Wie ein Weber hast du mein Leben zu Ende gewoben, / du schneidest mich ab wie ein fertig gewobenes Tuch. Vom Anbruch des Tages bis in die Nacht / gibst du mich völlig preis;", footnotes: ["2 Kor 5,1; 2 Petr 1,13f; Ijob 7,6", "hast du gewoben: Text korr. (2. statt 1. Person). - gewoben, wörtlich: aufgerollt."] },
      { number: 13, text: "bis zum Morgen schreie ich um Hilfe. / Wie ein Löwe zermalmt er all meine Knochen.", footnotes: ["H wiederholt am Ende V. 12ef."] },
      { number: 14, text: "Ich zwitschere wie eine Schwalbe, / ich gurre wie eine Taube. Meine Augen blicken ermattet nach oben: / Ich bin in Not, Herr. Steh mir bei!" },
      { number: 15, text: "Was kann ich ihm sagen, was soll ich reden, / da er es selber getan hat? Es flieht mich der Schlaf; / denn meine Seele ist verbittert.", footnotes: ["was soll ich reden: Text korr., vgl. den Qumran-Text; H: und er hat mir gesagt. - 15c: Text korr. nach S."] },
      { number: 16, text: "Herr, ich vertraue auf dich; / du hast mich geprüft. / Mach mich gesund und lass mich wieder genesen!", footnotes: ["H ist unverständlich, die Übersetzung ein Versuch."] },
      { number: 17, text: "Du hast mich aus meiner bitteren Not gerettet, / du hast mich vor dem tödlichen Abgrund bewahrt; / denn all meine Sünden warfst du hinter deinen Rücken.", footnotes: ["Ps 103,3f; Mi 7,19", "Übersetzung unsicher."] },
      { number: 18, text: "Ja, in der Unterwelt dankt man dir nicht, / die Toten loben dich nicht; wer ins Grab gesunken ist, / kann nichts mehr von deiner Güte erhoffen.", footnotes: ["Ps 6,6"] },
      { number: 19, text: "Nur die Lebenden danken dir, / wie ich am heutigen Tag. / Von deiner Treue erzählt der Vater den Kindern.", footnotes: ["Dtn 4,9"] },
      { number: 20, text: "Der Herr war bereit, mir zu helfen; / wir wollen singen und spielen im Haus des Herrn, / solange wir leben." },
      { number: 21, text: "Darauf sagte Jesaja: Man hole einen Feigenbrei und streiche ihn auf das Geschwür, damit der König gesund wird.", heading: "Ein Nachtrag über die Krankheit Hiskijas: 38,21-22", footnotes: ["2 Kön 20,7f", "21f: Die Verse gehören zu 38,1-8, vielleicht vor V. 7."] },
      { number: 22, text: "Hiskija aber fragte Jesaja: Was ist das Zeichen dafür, dass ich wieder zum Haus des Herrn hinaufgehen werde?" },
    ] },
    { number: 39, verses: [
      { number: 1, text: "Damals sandte Merodach-Baladan, der Sohn Baladans, der König von Babel, einen Brief und Geschenke an Hiskija; denn er hatte von seiner Krankheit und von seiner Genesung gehört.", heading: "Die Gesandtschaft aus Babel: 39,1-8", footnotes: ["(1-8) 2 Kön 20,12-19"] },
      { number: 2, text: "Hiskija freute sich darüber und zeigte den Gesandten sein Schatzhaus, das Silber und das Gold, die Vorräte an Balsam und feinem Öl, sein ganzes Waffenlager und alle anderen Schätze, die er besaß. Es gab nichts in seinem Haus und in seinem ganzen Herrschaftsbereich, das er ihnen nicht gezeigt hätte." },
      { number: 3, text: "Danach kam der Prophet Jesaja zu König Hiskija und fragte ihn: Was haben diese Männer gesagt? Woher kamen sie? Hiskija antwortete: Sie sind aus einem fernen Land, aus Babel, zu mir gekommen." },
      { number: 4, text: "Er fragte weiter: Was haben sie in deinem Haus gesehen? Hiskija antwortete: Sie haben alles gesehen, was in meinem Haus ist. Es gibt nichts in meinen Schatzkammern, das ich ihnen nicht gezeigt hätte." },
      { number: 5, text: "Da sagte Jesaja zu Hiskija: Höre das Wort des Herrn der Heere:" },
      { number: 6, text: "Es werden Tage kommen, an denen man alles, was in deinem Haus ist, alles, was deine Väter bis zum heutigen Tag angesammelt haben, nach Babel bringt. Nichts wird übrig bleiben, spricht der Herr." },
      { number: 7, text: "Auch von deinen eigenen Söhnen, die du noch bekommen wirst, wird man einige mitnehmen und sie werden als Kämmerer im Palast des Königs von Babel dienen müssen." },
      { number: 8, text: "Hiskija sagte zu Jesaja: Das Wort des Herrn, das du mir gesagt hast, ist gut. Und er dachte: Zu meinen Lebzeiten herrscht ja noch Friede und Sicherheit." },
    ] },
    { number: 40, verses: [
      { number: 1, text: "Tröstet, tröstet mein Volk, / spricht euer Gott.", heading: "II. Teil Der zweite Jesaja (Deuterojesaja) 40,1 - 55,13 | Einleitung: Die Verheißung der Heimkehr: 40,1-11", footnotes: ["1-55,13: Vgl. die Einleitung zu Jes."] },
      { number: 2, text: "Redet Jerusalem zu Herzen / und verkündet der Stadt, dass ihr Frondienst zu Ende geht, / dass ihre Schuld beglichen ist; denn sie hat die volle Strafe erlitten / von der Hand des Herrn / für all ihre Sünden.", footnotes: ["Jer 16,18", "2e: Wörtlich: «sie hat Zweifaches empfangen». Das Strafrecht fordert in bestimmten Fällen doppelte Wiedergutmachung (vgl. Ex 22,3.6.8)."] },
      { number: 3, text: "Eine Stimme ruft: / Bahnt für den Herrn einen Weg durch die Wüste! Baut in der Steppe eine ebene Straße / für unseren Gott!", footnotes: ["Mt 3,3; Mk 1,3; Lk 3,4-6; Joh 1,23; Jes 62,10"] },
      { number: 4, text: "Jedes Tal soll sich heben, / jeder Berg und Hügel sich senken. Was krumm ist, soll gerade werden, / und was hüglig ist, werde eben." },
      { number: 5, text: "Dann offenbart sich die Herrlichkeit des Herrn, / alle Sterblichen werden sie sehen. / Ja, der Mund des Herrn hat gesprochen." },
      { number: 6, text: "Eine Stimme sagte: Verkünde! / Ich fragte: Was soll ich verkünden? Alles Sterbliche ist wie das Gras / und all seine Schönheit ist wie die Blume auf dem Feld.", footnotes: ["(6-7) Ps 103,15; Jak 1,10; 1 Petr 1,24f", "6-8: Spricht vom Verkündigungsauftrag an Deuterojesaja. Ich fragte: Text korr. nach G, vgl. den Qumran-Text."] },
      { number: 7, text: "Das Gras verdorrt, die Blume verwelkt, / wenn der Atem des Herrn darüber weht. / Wahrhaftig, Gras ist das Volk." },
      { number: 8, text: "Das Gras verdorrt, die Blume verwelkt, / doch das Wort unseres Gottes bleibt in Ewigkeit.", footnotes: ["Ps 119,89"] },
      { number: 9, text: "Steig auf einen hohen Berg, / Zion, du Botin der Freude! Erheb deine Stimme mit Macht, / Jerusalem, du Botin der Freude! Erheb deine Stimme, fürchte dich nicht! / Sag den Städten in Juda: / Seht, da ist euer Gott." },
      { number: 10, text: "Seht, Gott der Herr, kommt mit Macht, / er herrscht mit starkem Arm. Seht, er bringt seinen Siegespreis mit: / Alle, die er gewonnen hat, gehen vor ihm her.", footnotes: ["62,11; Offb 22,12"] },
      { number: 11, text: "Wie ein Hirt führt er seine Herde zur Weide, / er sammelt sie mit starker Hand. Die Lämmer trägt er auf dem Arm, / die Mutterschafe führt er behutsam." },
      { number: 12, text: "Wer misst das Meer mit der hohlen Hand? / Wer kann mit der ausgespannten Hand den Himmel vermessen? / Wer misst den Staub der Erde mit einem Scheffel? Wer wiegt die Berge mit einer Waage / und mit Gewichten die Hügel?", heading: "Preis der Erhabenheit Gottes: 40,12-20; 41,6-7; 40,21-31", footnotes: ["Scheffel: H: Schalisch (ein Hohlmaß unbekannter Größe)."] },
      { number: 13, text: "Wer bestimmt den Geist des Herrn? / Wer kann sein Berater sein und ihn unterrichten?", footnotes: ["Jer 23,18; Röm 11,34"] },
      { number: 14, text: "Wen fragt er um Rat / und wer vermittelt ihm Einsicht? Wer kann ihn über die Pfade des Rechts belehren? / Wer lehrt ihn das Wissen / und zeigt ihm den Weg der Erkenntnis?" },
      { number: 15, text: "Seht, die Völker sind wie ein Tropfen am Eimer, / sie gelten so viel wie ein Stäubchen auf der Waage. / Ganze Inseln wiegen nicht mehr als ein Sandkorn." },
      { number: 16, text: "Der Libanon reicht nicht aus für das Brennholz, / sein Wild genügt nicht für die Opfer." },
      { number: 17, text: "Alle Völker sind vor Gott wie ein Nichts, / für ihn sind sie wertlos und nichtig." },
      { number: 18, text: "Mit wem wollt ihr Gott vergleichen / und welches Bild an seine Stelle setzen?", footnotes: ["40,25; 46,5"] },
      { number: 19, text: "Der Handwerker gießt ein Götterbild, / der Goldschmied überzieht es mit Gold / und fertigt silberne Ketten dazu." },
      { number: 20, text: "Wer arm ist, wählt für ein Weihegeschenk / ein Holz, das nicht fault; er sucht einen fähigen Meister, / der ihm das Götterbild aufstellt, / sodass es nicht wackelt.", footnotes: ["20f: Die Verse 41,6f gehören nach ihrem Inhalt hierher; sie stehen in Kap. 41 isoliert. Sinn von H teilweise unklar."] },
      { number: 41, text: "Dabei hilft einer dem andern; / er sagt zu seinem Bruder: Pack an!" },
      { number: 41, text: "So ermuntert der Handwerker den Goldschmied, / der, der glättet, den Schmied am Amboss; er sagt: Die Lötung ist gut!, / dann befestigt er das Ganze mit Nägeln, / damit es nicht wackelt.", footnotes: ["Jer 10,4"] },
      { number: 21, text: "Wisst ihr es nicht, hört ihr es nicht, / war es euch nicht von Anfang an bekannt? Habt ihr es nicht immer wieder erfahren / seit der Grundlegung der Erde?", footnotes: ["40,28", "seit der Grundlegung: Text korr."] },
      { number: 22, text: "Er ist es, der über dem Erdenrund thront; / wie Heuschrecken sind ihre Bewohner. Wie einen Schleier spannt er den Himmel aus, / er breitet ihn aus wie ein Zelt zum Wohnen." },
      { number: 23, text: "Er macht die Fürsten zunichte, / er nimmt den Richtern der Erde jeden Einfluss." },
      { number: 24, text: "Kaum sind sie gesät und gepflanzt, / kaum wurzelt ihr Stamm in der Erde, da bläst er sie an, sodass sie verdorren; / der Sturm trägt sie fort wie Spreu." },
      { number: 25, text: "Mit wem wollt ihr mich vergleichen? / Wem sollte ich ähnlich sein?, spricht der Heilige.", footnotes: ["40,18"] },
      { number: 26, text: "Hebt eure Augen in die Höhe und seht: / Wer hat die (Sterne) dort oben erschaffen? Er ist es, der ihr Heer täglich zählt und heraufführt, / der sie alle beim Namen ruft. Vor dem Allgewaltigen und Mächtigen / wagt keiner zu fehlen.", footnotes: ["Ps 147,4"] },
      { number: 27, text: "Jakob, warum sagst du, / Israel, warum sprichst du: Mein Weg ist dem Herrn verborgen, / meinem Gott entgeht mein Recht?" },
      { number: 28, text: "Weißt du es nicht, hörst du es nicht? / Der Herr ist ein ewiger Gott, / der die weite Erde erschuf. Er wird nicht müde und matt, / unergründlich ist seine Einsicht.", footnotes: ["40,21"] },
      { number: 29, text: "Er gibt dem Müden Kraft, / dem Kraftlosen verleiht er große Stärke." },
      { number: 30, text: "Die Jungen werden müde und matt, / junge Männer stolpern und stürzen." },
      { number: 31, text: "Die aber, die dem Herrn vertrauen, / schöpfen neue Kraft, / sie bekommen Flügel wie Adler. Sie laufen und werden nicht müde, / sie gehen und werden nicht matt.", footnotes: ["Ps 103,5"] },
    ] },
    { number: 41, verses: [
      { number: 1, text: "Ihr Inseln, hört schweigend auf mich, / ihr Völker, wartet auf mich! Sie sollen kommen und ihre Sache vortragen, / wir wollen vor Gericht gehen, alle zusammen.", heading: "Das Gericht über die Völker: 41,1-29 | Die Berufung des Kyrus: 41,1-5", footnotes: ["ihr Völker, wartet auf mich: Text korr.; H: und Völker schöpfen neue Kraft (aus 40,31 eingedrungen)."] },
      { number: 2, text: "Wer hat im Osten den geweckt, / dem Gerechtigkeit folgt auf Schritt und Tritt? Wer gibt ihm die Völker preis / und unterwirft ihm die Könige? Sein Schwert macht sie zu Staub, / sein Bogen macht sie zu Spreu, die verweht.", footnotes: ["41,25; 45,13; 46,11", "2f: Gemeint ist der Perserkönig Kyrus."] },
      { number: 3, text: "Er verfolgt sie, rückt unversehrt vor, / berührt kaum mit den Füßen den Weg." },
      { number: 4, text: "Wer hat das bewirkt und vollbracht? / Er, der von Anfang an die Generationen (ins Dasein) rief. Ich, der Herr, bin der Erste / und noch bei den Letzten bin ich derselbe.", footnotes: ["44,6; 48,12; Offb 1,8.17"] },
      { number: 5, text: "Die Inseln sehen es und geraten in Furcht, / die Enden der Erde erzittern; / sie nähern sich und kommen herbei.", footnotes: ["6f: Vgl. die Anmerkung zu 40,20f."] },
      { number: 8, text: "Du, mein Knecht Israel, / du, Jakob, den ich erwählte, / Nachkomme meines Freundes Abraham:", heading: "Trost für Israel: 41,8-16", footnotes: ["Jak 2,23"] },
      { number: 9, text: "Ich habe dich von den Enden der Erde geholt, / aus ihrem äußersten Winkel habe ich dich gerufen. Ich habe zu dir gesagt: Du bist mein Knecht, / ich habe dich erwählt und dich nicht verschmäht." },
      { number: 10, text: "Fürchte dich nicht, denn ich bin mit dir; / hab keine Angst, denn ich bin dein Gott. Ich helfe dir, ja, ich mache dich stark, / ja, ich halte dich mit meiner hilfreichen Rechten.", footnotes: ["43,5"] },
      { number: 11, text: "Schmach und Schande kommt über alle, / die sich über dich erhitzen. Die Männer, die mit dir streiten, / werden zunichte und gehen zugrunde." },
      { number: 12, text: "Du wirst sie suchen, aber nicht mehr finden, / die Männer, die mit dir zanken. Sie werden zunichte und finden ihr Ende, / die Männer, die dich bekriegen." },
      { number: 13, text: "Denn ich bin der Herr, dein Gott, / der deine rechte Hand ergreift und der zu dir sagt: Fürchte dich nicht, ich werde dir helfen.", footnotes: ["42,6; 45,1"] },
      { number: 14, text: "Fürchte dich nicht, du armer Wurm Jakob, / du Würmlein Israel! Ich selber werde dir helfen - Spruch des Herrn. / Der Heilige Israels löst dich aus.", footnotes: ["Würmlein Israel: H: Leute Israels. - Zu «der Heilige Israels» vgl. die Anmerkung zu 1,4. - «Auslösen, loskaufen, erlösen», meint die Befreiung aus der babylonischen Knechtschaft, wobei der Prophet auf die «Löser»-Pflicht anspielt (vgl. die Anmerkung zu Lev 25,25)."] },
      { number: 15, text: "Zu einem Dreschschlitten mache ich dich, / zu einem neuen Schlitten mit vielen Schneiden. Berge wirst du dreschen und sie zermalmen / und Hügel machst du zu Spreu.", footnotes: ["15f: Zum «Dreschschlitten» und «Worfeln» vgl. die Anmerkung zu Rut 3,2-4."] },
      { number: 16, text: "Du worfelst sie und es verweht sie der Wind, / es zerstreut sie der Sturm. Du aber jubelst über den Herrn, / du rühmst dich des Heiligen Israels.", footnotes: ["61,10"] },
      { number: 17, text: "Die Elenden und Armen suchen Wasser, / doch es ist keines da; / ihre Zunge vertrocknet vor Durst. Ich, der Herr, will sie erhören, / ich, der Gott Israels, verlasse sie nicht.", heading: "Der wunderbare Auszug aus Babel: 41,17-20" },
      { number: 18, text: "Auf den kahlen Hügeln lasse ich Ströme hervorbrechen / und Quellen inmitten der Täler. Ich mache die Wüste zum Teich / und das ausgetrocknete Land zur Oase.", footnotes: ["Ps 114,8"] },
      { number: 19, text: "In der Wüste pflanze ich Zedern, / Akazien, Ölbäume und Myrten. In der Steppe setze ich Zypressen, / Platanen und auch Eschen.", footnotes: ["60,13"] },
      { number: 20, text: "Dann werden alle sehen und erkennen, / begreifen und verstehen, dass die Hand des Herrn das alles gemacht hat, / dass der Heilige Israels es erschaffen hat." },
      { number: 21, text: "Bringt eure Sache vor, spricht der Herr, / schafft eure Beweise herbei, spricht Jakobs König.", heading: "Die Nichtigkeit der heidnischen Götter: 41,21-29" },
      { number: 22, text: "Sie sollen vorbringen und uns kundtun, / was sich ereignen wird. Was bedeutet das Vergangene? Teilt es uns mit, / damit auch wir unseren Sinn darauf richten. Oder lasst uns das Zukünftige hören, / damit wir das Ende erfahren." },
      { number: 23, text: "Tut kund, was später noch kommt, / damit wir erkennen: Ja, ihr seid Götter. Ja, tut Gutes oder Böses, / damit wir alle zusammen es sehen und staunen.", footnotes: ["44,7", "Die Götter sollen Gutes oder Böses, d. h. irgendetwas tun und so ihre Macht beweisen."] },
      { number: 24, text: "Seht, ihr seid nichts, / euer Tun ist ein Nichts; / einen Gräuel wählt, wer immer euch wählt." },
      { number: 25, text: "Ich habe ihn im Norden geweckt und er kam; / im Osten habe ich ihn beim Namen gerufen. Er hat die Fürsten wie Lehm zertreten, / wie ein Töpfer den Ton stampft.", footnotes: ["41,2; 45,3", "ihn: Gemeint ist Kyrus (vgl. 41,2). - habe ich ihn beim Namen gerufen: Text korr. (vgl. 45,3). - Er hat . . . zertreten: Text korr."] },
      { number: 26, text: "Wer hat es kundgetan von Anfang an, / sodass wir es wussten? Wer hat es im Voraus kundgetan, / sodass wir sagen konnten: Es ist richtig? Niemand hat es kundgetan, / niemand hat es gemeldet, / keiner hörte von euch ein einziges Wort." },
      { number: 27, text: "Ich habe Zion als Erster (gesagt): / Sieh her, da ist es!, / und habe Jerusalem einen Freudenboten geschickt.", footnotes: ["40,9; 52,7"] },
      { number: 28, text: "Ich blickte umher, doch niemand war da, / keiner von diesen hier konnte mir raten / und Antwort geben auf meine Fragen." },
      { number: 29, text: "Seht her: Sie alle sind nichts, / ihr Tun ist ein Nichts; / windig und wesenlos sind die Bilder der Götter." },
    ] },
    { number: 42, verses: [
      { number: 1, text: "Seht, das ist mein Knecht, den ich stütze; / das ist mein Erwählter, an ihm finde ich Gefallen. Ich habe meinen Geist auf ihn gelegt, / er bringt den Völkern das Recht.", heading: "Das erste Lied vom Gottesknecht: 42,1-9", footnotes: ["Mt 12,18-21; 3,17; Jes 51,4"] },
      { number: 2, text: "Er schreit nicht und lärmt nicht / und lässt seine Stimme nicht auf der Straße erschallen." },
      { number: 3, text: "Das geknickte Rohr zerbricht er nicht / und den glimmenden Docht löscht er nicht aus; / ja, er bringt wirklich das Recht." },
      { number: 4, text: "Er wird nicht müde und bricht nicht zusammen, / bis er auf der Erde das Recht begründet hat. / Auf sein Gesetz warten die Inseln.", footnotes: ["51,4"] },
      { number: 5, text: "So spricht Gott, der Herr, / der den Himmel erschaffen und ausgespannt hat, / der die Erde gemacht hat und alles, was auf ihr wächst, der den Menschen auf der Erde den Atem verleiht / und allen, die auf ihr leben, den Geist:" },
      { number: 6, text: "Ich, der Herr, habe dich aus Gerechtigkeit gerufen, / ich fasse dich an der Hand. Ich habe dich geschaffen und dazu bestimmt, / der Bund für mein Volk / und das Licht für die Völker zu sein:", footnotes: ["41,9; 49,6.8; Lk 2,32", "Im Gottesknecht soll sich die Bundeszusage für das Volk Gottes erfüllen."] },
      { number: 7, text: "blinde Augen zu öffnen, / Gefangene aus dem Kerker zu holen und alle, die im Dunkel sitzen, / aus ihrer Haft zu befreien." },
      { number: 8, text: "Ich bin Jahwe, das ist mein Name; / ich überlasse die Ehre, die mir gebührt, keinem andern, / meinen Ruhm nicht den Götzen.", footnotes: ["48,11"] },
      { number: 9, text: "Seht, das Frühere ist eingetroffen, / Neues kündige ich an. Noch ehe es zum Vorschein kommt, / mache ich es euch bekannt.", footnotes: ["48,6"] },
      { number: 10, text: "Singt dem Herrn ein neues Lied, / verkündet seinen Ruhm bis ans Ende der Erde! Es jauchze das Meer und alles, was es erfüllt, / die Inseln und ihre Bewohner.", heading: "Verheißung des Auszugs aus Babel: 42,10 - 44,23 | Der Sieg Gottes: 42,10-17", footnotes: ["Ps 33,3", "Es jauchze: Text korr."] },
      { number: 11, text: "Die Wüste und ihre Städte sollen sich freuen, / die Dörfer, die Kedar bewohnt. Die Bewohner von Sela sollen singen vor Freude / und jubeln auf den Gipfeln der Berge.", footnotes: ["sollen sich freuen: H: sollen (ihre Stimme) erheben."] },
      { number: 12, text: "Sie sollen die Herrlichkeit des Herrn verkünden, / seinen Ruhm auf den Inseln verbreiten." },
      { number: 13, text: "Der Herr zieht in den Kampf wie ein Held, / er entfacht seine Leidenschaft wie ein Krieger. Er erhebt den Schlachtruf und schreit, / er zeigt sich als Held gegenüber den Feinden." },
      { number: 14, text: "Ich hatte sehr lange geschwiegen, / ich war still und hielt mich zurück. Wie eine Gebärende will ich nun schreien, / ich schnaube und schnaufe." },
      { number: 15, text: "Die Berge und Hügel dörre ich aus / und lasse ihr Gras völlig vertrocknen. Flüsse mache ich zu festem Boden / und Teiche lege ich trocken." },
      { number: 16, text: "Blinde führe ich auf Wegen, die sie nicht kennen, / auf unbekannten Pfaden lasse ich sie wandern. Die Finsternis vor ihren Augen mache ich zu Licht; / was krumm ist, mache ich gerade. Das sind die Taten, die ich vollbrachte, / und ich lasse davon nicht mehr ab.", footnotes: ["40,4"] },
      { number: 17, text: "Alle müssen weichen und werden beschämt, / die auf Götzenbilder vertrauen, die zu gegossenen Bildern sagen: / Ihr seid unsere Götter.", footnotes: ["Ps 97,7"] },
      { number: 18, text: "Ihr, die ihr taub seid, hört, / ihr Blinden, blickt auf und seht her!", heading: "Israel, der blinde und taube Knecht Gottes: 42,18-25" },
      { number: 19, text: "Wer ist so blind wie mein Knecht / und so taub wie der Bote, den ich sende? Wer ist so blind wie mein Vertrauter / und so taub wie der Knecht des Herrn?", footnotes: ["mein Vertrauter: Bedeutung des Wortes in H unklar."] },
      { number: 20, text: "Vieles sieht er, aber er beachtet es nicht; / die Ohren hat er offen und hört doch nicht.", footnotes: ["6,9"] },
      { number: 21, text: "Der Herr hatte um seiner Gerechtigkeit willen beschlossen, / das Gesetz groß und herrlich zu machen." },
      { number: 22, text: "Doch jetzt sind sie ein beraubtes, ausgeplündertes Volk; / alle sind in den Kerker geworfen, ins Gefängnis gesperrt. Sie wurden als Beute verschleppt / und kein Retter war da; sie wurden ausgeplündert und niemand sagte: / Gib es zurück!", footnotes: ["in den Kerker geworfen, wörtlich: gebunden in Löchern."] },
      { number: 23, text: "Wer von euch vernimmt diese Worte, / wer merkt auf und hört künftig darauf?" },
      { number: 24, text: "Wer lieferte Jakob den Plünderern aus / und Israel den Räubern? Hat nicht der Herr es getan, / gegen den wir gesündigt hatten? Sie wollten nicht auf seinen Wegen gehen, / sie hörten nicht auf sein Gesetz." },
      { number: 25, text: "Da goss er über sie seinen glühenden Zorn aus / und den Schrecken des Krieges: Ringsum hat er sie umlodert, / doch sie merkten es nicht; du hast sie in Brand gesetzt, / doch sie nahmen es sich nicht zu Herzen." },
    ] },
    { number: 43, verses: [
      { number: 1, text: "Jetzt aber - so spricht der Herr, / der dich geschaffen hat, Jakob, / und der dich geformt hat, Israel: Fürchte dich nicht, denn ich habe dich ausgelöst, / ich habe dich beim Namen gerufen, / du gehörst mir.", heading: "Die Heimkehr Israels: 43,1-7", footnotes: ["1d: ausgelöst: vgl. die Anmerkung zu 41,14."] },
      { number: 2, text: "Wenn du durchs Wasser schreitest, bin ich bei dir, / wenn durch Ströme, dann reißen sie dich nicht fort. Wenn du durchs Feuer gehst, wirst du nicht versengt, / keine Flamme wird dich verbrennen." },
      { number: 3, text: "Denn ich, der Herr, bin dein Gott, / ich, der Heilige Israels, bin dein Retter. Ich gebe Ägypten als Kaufpreis für dich, / Kusch und Seba gebe ich für dich." },
      { number: 4, text: "Weil du in meinen Augen teuer und wertvoll bist / und weil ich dich liebe, gebe ich für dich ganze Länder / und für dein Leben ganze Völker.", footnotes: ["ganze Länder: Text korr.; H: Menschen."] },
      { number: 5, text: "Fürchte dich nicht, denn ich bin mit dir. / Vom Osten bringe ich deine Kinder herbei, / vom Westen her sammle ich euch.", footnotes: ["41,10"] },
      { number: 6, text: "Ich sage zum Norden: Gib her!, / und zum Süden: Halt nicht zurück! Führe meine Söhne heim aus der Ferne, / meine Töchter vom Ende der Erde!" },
      { number: 7, text: "Denn jeden, der nach meinem Namen benannt ist, / habe ich zu meiner Ehre erschaffen, / geformt und gemacht.", footnotes: ["Jer 14,9", "Alle, die zum Volk Gottes gehören, auch die in der weiten Diaspora, sind seine Kinder (vgl. 1,2; 63,16; 64,7)."] },
      { number: 8, text: "Bringt das Volk her, das blind ist, obwohl es Augen hat, / und taub, obwohl es Ohren hat.", heading: "Gott, der einzige Retter: 43,8-13" },
      { number: 9, text: "Alle Völker sollen sich versammeln, / die Nationen sollen zusammenkommen. Wer von ihnen kündigt dies an / und wer kann uns sagen, was früher war? Sie sollen ihre Zeugen stellen, / damit sie Recht bekommen, / damit man (die Zeugen) hört und sagt: Es ist wahr.", footnotes: ["48,3"] },
      { number: 10, text: "Ihr seid meine Zeugen - Spruch des Herrn - / und auch mein Knecht, den ich erwählte, damit ihr erkennt und mir glaubt / und einseht, dass ich es bin. Vor mir wurde kein Gott erschaffen / und auch nach mir wird es keinen geben." },
      { number: 11, text: "Ich bin Jahwe, ich, / und außer mir gibt es keinen Retter." },
      { number: 12, text: "Ich habe es selbst angekündet und euch gerettet, / ich habe es euch zu Gehör gebracht. Kein fremder (Gott) ist bei euch gewesen. / Ihr seid meine Zeugen - Spruch des Herrn. Ich allein bin Gott; /" },
      { number: 13, text: "auch künftig werde ich es sein. Niemand kann mir etwas entreißen. / Ich handle. Wer kann es rückgängig machen?" },
      { number: 14, text: "So spricht der Herr, euer Erlöser, / der Heilige Israels: Um euretwillen schicke ich (Boten) nach Babel / und reiße alle Riegel heraus, / die Chaldäer aber werden mit Ketten gefesselt.", heading: "Der Weg durch die Wüste: 43,14-21", footnotes: ["14e: H ist verderbt, daher Übersetzung unsicher; vgl. G."] },
      { number: 15, text: "Ich bin der Herr, euer Heiliger, / euer König, Israels Schöpfer." },
      { number: 16, text: "So spricht der Herr, der einen Weg durchs Meer bahnt, / einen Pfad durch das gewaltige Wasser,", footnotes: ["16f: Dieses Gotteswort erinnert an den Auszug aus Ägypten und die Rettung am Schilfmeer (vgl. Ex 14)."] },
      { number: 17, text: "der Wagen und Rosse ausziehen lässt, / zusammen mit einem mächtigen Heer; doch sie liegen am Boden und stehen nicht mehr auf, / sie sind erloschen und verglüht wie ein Docht." },
      { number: 18, text: "Denkt nicht mehr an das, was früher war; / auf das, was vergangen ist, sollt ihr nicht achten.", footnotes: ["65,17"] },
      { number: 19, text: "Seht her, nun mache ich etwas Neues. / Schon kommt es zum Vorschein, merkt ihr es nicht? Ja, ich lege einen Weg an durch die Steppe / und Straßen durch die Wüste.", footnotes: ["Offb 21,5", "Straßen: nach dem Qumran-Text; H: Ströme (aus V. 20)."] },
      { number: 20, text: "Die wilden Tiere werden mich preisen, / die Schakale und Strauße, denn ich lasse in der Steppe Wasser fließen / und Ströme in der Wüste, / um mein Volk, mein erwähltes, zu tränken." },
      { number: 21, text: "Das Volk, das ich mir erschaffen habe, / wird meinen Ruhm verkünden.", footnotes: ["1 Petr 2,9"] },
      { number: 22, text: "Jakob, du hast mich nicht gerufen, / Israel, du hast dir mit mir keine Mühe gemacht.", heading: "Die Schuld Israels und die Gnade Gottes: 43,22-28", footnotes: ["22-24: Israel hat Gott, der all das nicht braucht, nicht wirklich gedient, sondern ihm nur Arbeit gemacht."] },
      { number: 23, text: "Du brachtest mir keine Lämmer als Brandopfer dar / und mit Schlachtopfern hast du mich nicht geehrt. Ich habe dich nicht zu Speiseopfern gezwungen / und von dir keinen Weihrauch gefordert und dich dadurch geplagt." },
      { number: 24, text: "Du hast mir für dein Geld kein Gewürzrohr gekauft / und hast mich nicht gelabt mit dem Fett deiner Opfer. Nein, du hast mir mit deinen Sünden Arbeit gemacht, / mit deinen üblen Taten hast du mich geplagt." },
      { number: 25, text: "Ich, ich bin es, der um meinetwillen deine Vergehen auslöscht, / ich denke nicht mehr an deine Sünden.", footnotes: ["44,22"] },
      { number: 26, text: "Lade mich vor, gehen wir miteinander vor Gericht; / verteidige dich, damit du Recht bekommst.", footnotes: ["26b: verteidige dich, wörtlich: zähl auf."] },
      { number: 27, text: "Schon dein Urahn hat gesündigt; / deine Anführer sind mir untreu geworden;" },
      { number: 28, text: "deine Fürsten haben mein Heiligtum entweiht. / Darum habe ich Jakob preisgegeben, / damit man es dem Untergang weiht, / und Israel, damit man es verspottet.", footnotes: ["28a: Text korr., vgl. G; H: und ich entweihte die Fürsten des Heiligtums."] },
    ] },
    { number: 44, verses: [
      { number: 1, text: "Jetzt aber höre, Jakob, mein Knecht, / Israel, den ich erwählte.", heading: "Die Ausgießung des Geistes: 44,1-5" },
      { number: 2, text: "So spricht der Herr, dein Schöpfer, / der dich im Mutterleib geformt hat, der dir hilft: Fürchte dich nicht, Jakob, mein Knecht, / du, Jeschurun, den ich erwählte.", footnotes: ["2d: Jeschurun («der Redliche», vielleicht im Gegensatz zu «Jakob», was als «Betrüger» gedeutet wird; vgl. Hos 12,3f) ist Ehrenname für Israel (vgl. Dtn 32,15; 33,5.26)."] },
      { number: 3, text: "Denn ich gieße Wasser auf den dürstenden Boden, / rieselnde Bäche auf das trockene Land. Ich gieße meinen Geist über deine Nachkommen aus / und meinen Segen über deine Kinder." },
      { number: 4, text: "Dann sprossen sie auf wie das Schilfgras, / wie Weidenbäume an Wassergräben.", footnotes: ["das Schilfgras, wörtlich: das Gras zwischen Wassern; Text korr., ergänzt nach G."] },
      { number: 5, text: "Der eine sagt: Ich gehöre dem Herrn. / Ein anderer benennt sich mit dem Namen Jakobs. Einer schreibt auf seine Hand: für den Herrn. / Ein anderer wird ehrenvoll mit dem Namen Israel benannt." },
      { number: 6, text: "So spricht der Herr, Israels König, / sein Erlöser, der Herr der Heere: Ich bin der Erste, ich bin der Letzte, / außer mir gibt es keinen Gott.", heading: "Gottes Ewigkeit und Einzigkeit: 44,6-8", footnotes: ["41,4"] },
      { number: 7, text: "Wer ist mir gleich? Er soll sich melden, / er tue es mir kund und beweise es mir. Wer hat von Anfang an die Zukunft verkündet? / Sie sollen uns sagen, was alles noch kommt.", footnotes: ["41,23", "7c: Text korr."] },
      { number: 8, text: "Erschreckt nicht und fürchtet euch nicht! / Habe ich es euch nicht schon längst zu Gehör gebracht und verkündet? Ihr seid meine Zeugen: Gibt es einen Gott außer mir? / Es gibt keinen Fels außer mir, ich kenne keinen." },
      { number: 9, text: "Ein Nichts sind alle, die ein Götterbild formen; / ihre geliebten Götzen nützen nichts. Wer sich zu seinen Göttern bekennt, sieht nichts, / ihm fehlt es an Einsicht; darum wird er beschämt.", heading: "Die Hilflosigkeit der Götzen: 44,9-20" },
      { number: 10, text: "Wer sich einen Gott macht / und sich ein Götterbild gießt, / hat keinen Nutzen davon." },
      { number: 11, text: "Seht her, alle, die sich ihm anschließen, werden beschämt, / die Schmiede sind nichts als Menschen. Sie sollen sich alle versammeln und vor mich treten; / dann werden sie alle von Schrecken gepackt und beschämt." },
      { number: 12, text: "Der Schmied facht die Kohlenglut an, / er formt (das Götterbild) mit seinem Hammer / und bearbeitet es mit kräftigem Arm. Dabei wird er hungrig und hat keine Kraft mehr. / Trinkt er kein Wasser, so wird er ermatten.", footnotes: ["(12-17) Jer 10,3f", "Text unklar."] },
      { number: 13, text: "Der Schnitzer misst das Holz mit der Messschnur, / er entwirft das Bild mit dem Stift / und schnitzt es mit seinem Messer; er umreißt es mit seinem Zirkel / und formt die Gestalt eines Mannes, das prächtige Bild eines Menschen; / in einem Haus soll es wohnen." },
      { number: 14, text: "Man fällt eine Zeder, wählt eine Eiche / oder sonst einen mächtigen Baum, den man stärker werden ließ / als die übrigen Bäume im Wald. Oder man pflanzt einen Lorbeerbaum, / den der Regen groß werden lässt." },
      { number: 15, text: "Das Holz nehmen die Menschen zum Heizen; / man macht ein Feuer und wärmt sich daran. Auch schürt man das Feuer und bäckt damit Brot. / Oder man schnitzt daraus einen Gott / und wirft sich nieder vor ihm; man macht ein Götterbild / und fällt vor ihm auf die Knie." },
      { number: 16, text: "Den einen Teil des Holzes wirft man ins Feuer / und röstet Fleisch in der Glut / und sättigt sich an dem Braten. Oder man wärmt sich am Feuer und sagt: / Oh, wie ist mir warm! Ich spüre die Glut.", footnotes: ["16b: in der Glut: Text korr. nach G (vgl. V. 19)."] },
      { number: 17, text: "Aus dem Rest des Holzes aber macht man sich einen Gott, / ein Götterbild, vor das man sich hinkniet, zu dem man betet und sagt: / Rette mich, du bist doch mein Gott!" },
      { number: 18, text: "Unwissend sind sie und ohne Verstand; / denn ihre Augen sind verklebt, sie sehen nichts mehr / und ihr Herz wird nicht klug." },
      { number: 19, text: "Sie überlegen nichts, / sie haben keine Erkenntnis und Einsicht, / sodass sie sich sagen würden: Den einen Teil habe ich ins Feuer geworfen, / habe Brot in der Glut gebacken / und Fleisch gebraten und es gegessen. Aus dem Rest des Holzes aber habe ich mir / einen abscheulichen Götzen gemacht / und nun knie ich nieder vor einem Holzklotz." },
      { number: 20, text: "Wer Asche hütet, / den hat sein Herz verführt und betrogen. Er wird sein Leben nicht retten / und wird nicht sagen: / Ich halte ja nur ein Trugbild in meiner rechten Hand.", footnotes: ["20ab: Nimmt wohl ein Sprichwort auf."] },
      { number: 21, text: "Denk daran, Jakob, und du, Israel, / dass du mein Knecht bist. Ich habe dich geschaffen, du bist mein Knecht; / Israel, ich vergesse dich nicht.", heading: "Die Erlösung Israels: 44,21-23" },
      { number: 22, text: "Ich fege deine Vergehen hinweg wie eine Wolke / und deine Sünden wie Nebel. / Kehr um zu mir; denn ich erlöse dich.", footnotes: ["43,25"] },
      { number: 23, text: "Jauchzt, ihr Himmel, denn der Herr hat gehandelt; / jubelt, ihr Tiefen der Erde! Brecht in Jubel aus, ihr Berge, / ihr Wälder mit all euren Bäumen! Denn der Herr hat Jakob erlöst / und an Israel bewiesen, wie herrlich er ist.", footnotes: ["49,13; 55,12; Ps 96,11-13; Jes 49,3"] },
      { number: 24, text: "So spricht der Herr, dein Erlöser, / der dich im Mutterleib geformt hat: Ich bin der Herr, der alles bewirkt, / der ganz allein den Himmel ausgespannt hat, / der die Erde gegründet hat aus eigener Kraft,", heading: "Die Ankündigung der Befreiung durch Kyrus: 44,24 - 48,22 | Die Berufung und die Aufgabe des Kyrus: 44,24 - 45,8", footnotes: ["44,2; Ijob 9,8"] },
      { number: 25, text: "der das Wirken der Zauberer vereitelt / und die Wahrsager zu Narren macht, der die Weisen zum Rückzug zwingt / und ihre Klugheit als Dummheit entlarvt," },
      { number: 26, text: "der das Wort seiner Knechte erfüllt / und den Plan ausführt, den seine Boten verkünden, der zu Jerusalem sagt: Du wirst wieder bewohnt!, / und zu den Städten Judas: Ihr werdet wieder aufgebaut werden, / ich baue eure Ruinen wieder auf!,", footnotes: ["Am 3,7", "26a: H: seines Knechtes; gemeint sind die Propheten als Boten Gottes."] },
      { number: 27, text: "der zum tiefen Meer sagt: Trockne aus, / ich lasse deine Fluten versiegen!," },
      { number: 28, text: "der zu Kyrus sagt: Mein Hirt - / alles, was ich will, wird er vollenden!, der zu Jerusalem sagt: Du wirst wieder aufgebaut werden!, / und zum Tempel: Du wirst wieder dastehen.", footnotes: ["2 Chr 36,23; Esra 1,1-4", "der zu Jerusalem sagt . . . und zum Tempel: Text korr.; vgl. Vg und G."] },
    ] },
    { number: 45, verses: [
      { number: 1, text: "So spricht der Herr zu Kyrus, seinem Gesalbten, / den er an der rechten Hand gefasst hat, um ihm die Völker zu unterwerfen, um die Könige zu entwaffnen, / um ihm die Türen zu öffnen und kein Tor verschlossen zu halten:", footnotes: ["um die Könige zu entwaffnen, wörtlich: um den Gürtel an den Hüften der Könige zu lösen."] },
      { number: 2, text: "Ich selbst gehe vor dir her / und ebne die Berge ein. Ich zertrümmere die bronzenen Tore / und zerschlage die eisernen Riegel." },
      { number: 3, text: "Ich gebe dir verborgene Schätze / und Reichtümer, die im Dunkel versteckt sind. So sollst du erkennen, dass ich der Herr bin, / der dich bei deinem Namen ruft, ich, Israels Gott.", footnotes: ["41,25"] },
      { number: 4, text: "Um meines Knechtes Jakob willen, / um Israels, meines Erwählten, willen / habe ich dich bei deinem Namen gerufen; ich habe dir einen Ehrennamen gegeben, / ohne dass du mich kanntest." },
      { number: 5, text: "Ich bin der Herr und sonst niemand; / außer mir gibt es keinen Gott. Ich habe dir den Gürtel angelegt / ohne dass du mich kanntest," },
      { number: 6, text: "damit man vom Aufgang der Sonne bis zu ihrem Untergang erkennt, / dass es außer mir keinen Gott gibt. / Ich bin der Herr und sonst niemand.", footnotes: ["59,19; 45,18"] },
      { number: 7, text: "Ich erschaffe das Licht und mache das Dunkel, / ich bewirke das Heil und erschaffe das Unheil. / Ich bin der Herr, der das alles vollbringt.", footnotes: ["Am 3,6"] },
      { number: 8, text: "Taut, ihr Himmel, von oben, / ihr Wolken, lasst Gerechtigkeit regnen! Die Erde tue sich auf und bringe das Heil hervor, / sie lasse Gerechtigkeit sprießen. / Ich, der Herr, will es vollbringen.", footnotes: ["Dieser Vers hat die Anregung zum Lied «Tauet, Himmel, den Gerechten» gegeben."] },
      { number: 9, text: "Weh dem, der mit seinem Schöpfer rechtet, / er, eine Scherbe unter irdenen Scherben. Sagt denn der Ton zu dem Töpfer: / Was machst du mit mir?, und zu dem, der ihn verarbeitet: / Du hast kein Geschick?", heading: "Gegen die Unzufriedenen und die Zweifler: 45,9-13", footnotes: ["29,16; Jer 18,6; Röm 9,20f", "kein Geschick, wörtlich: keine Hände; Text korr."] },
      { number: 10, text: "Weh dem, der zum Vater sagt: Warum zeugtest du mich?, / und zur Mutter: Warum brachtest du mich zur Welt?" },
      { number: 11, text: "So spricht der Herr, / der Heilige Israels und sein Schöpfer: Wollt ihr mir etwa Vorwürfe machen wegen meiner Kinder / und Vorschriften über das Werk meiner Hände?", footnotes: ["11c: Text korr."] },
      { number: 12, text: "Ich habe die Erde gemacht / und die Menschen auf ihr geschaffen. Ich habe den Himmel ausgespannt mit meinen Händen / und ich befehle seinem ganzen Heer." },
      { number: 13, text: "Ich habe ihn (Kyrus) aus Gerechtigkeit zum Aufbruch veranlasst. / Alle Wege ebne ich ihm. Er baut meine Stadt wieder auf, / mein verschlepptes Volk lässt er frei, aber nicht für Lösegeld oder Geschenke. / Der Herr der Heere hat gesprochen." },
      { number: 14, text: "So spricht der Herr: Die Ägypter mit ihren Erträgen, / die Kuschiter mit ihrem Gewinn und die groß gewachsenen Sebaiter / werden zu dir kommen und dir gehören; / in Ketten werden sie hinter dir herziehen. Sie werfen sich nieder vor dir und bekennen: / Nur bei dir gibt es einen Gott / und sonst gibt es keinen.", heading: "Die Huldigung der Heidenvölker: 45,14-25" },
      { number: 15, text: "Wahrhaftig, du bist ein verborgener Gott. / Israels Gott ist der Retter." },
      { number: 16, text: "Schmach und Schande kommt über sie alle, / die Götzenschmiede geraten in Schande.", footnotes: ["41,11", "16a: sie alle: nach G die Gegner Gottes."] },
      { number: 17, text: "Israel aber wird vom Herrn gerettet, / wird für immer errettet. Über euch kommt keine Schande und Schmach mehr / für immer und ewig." },
      { number: 18, text: "Denn so spricht der Herr, der den Himmel erschuf, / er ist der Gott, der die Erde geformt und gemacht hat - er ist es, der sie erhält, / er hat sie nicht als Wüste geschaffen, er hat sie zum Wohnen gemacht -: / Ich bin der Herr und sonst niemand.", footnotes: ["45,6"] },
      { number: 19, text: "Ich habe nicht im Verborgenen geredet, / irgendwo in einem finsteren Land. Ich habe nicht zum Geschlecht Jakobs gesagt: / Sucht mich im leeren Raum! Ich bin der Herr, der die Wahrheit spricht / und der verkündet, was recht ist.", footnotes: ["48,16"] },
      { number: 20, text: "Versammelt euch, kommt alle herbei, / tretet herzu, die ihr aus den Völkern entkommen seid. Wer hölzerne Götzen umherträgt, hat keine Erkenntnis, / wer einen Gott anbetet, der niemanden rettet.", footnotes: ["Angeredet sind die aus den Völkern, die den Kampf überlebt haben."] },
      { number: 21, text: "Macht es bekannt, bringt es vor, / beratet euch untereinander: Wer hat das alles seit langem verkündet / und längst im Voraus angesagt? War es nicht ich, der Herr? / Es gibt keinen Gott außer mir; / außer mir gibt es keinen gerechten und rettenden Gott." },
      { number: 22, text: "Wendet euch mir zu und lasst euch erretten, / ihr Menschen aus den fernsten Ländern der Erde; / denn ich bin Gott und sonst niemand." },
      { number: 23, text: "Ich habe bei mir selbst geschworen / und mein Mund hat die Wahrheit gesprochen, / es ist ein unwiderrufliches Wort: Vor mir wird jedes Knie sich beugen / und jede Zunge wird bei mir schwören:", footnotes: ["Röm 14,11; Phil 2,10f"] },
      { number: 24, text: "Nur beim Herrn - sagt man von mir - gibt es Rettung und Schutz. / Beschämt kommen alle zu ihm, die sich ihm widersetzten." },
      { number: 25, text: "Alle Nachkommen Israels bekommen ihr Recht / und erlangen Ruhm durch den Herrn." },
    ] },
    { number: 46, verses: [
      { number: 1, text: "Bel bricht zusammen, / Nebo krümmt sich am Boden. Babels Götter werden auf Tiere geladen. / Eine Last seid ihr, eine aufgebürdete Last für das ermüdete Vieh.", heading: "Der Sturz der Götzen Babels und die Allmacht Jahwes: 46,1-7", footnotes: ["Jer 50,2", "Bel (= Baal, Herr), genauer Bel Marduk, der Gott der Stadt Babylon, verkörpert die Schöpfermacht und Weltherrschaft. Nebo (vgl. den Namen Nebukadnezzar), der Gott der Weisheit und der Schreibkunst, hält die Schicksalstafeln in der Hand. - Die Übersetzung 46,1d ist unsicher."] },
      { number: 2, text: "Die Tiere krümmen sich und brechen zusammen, / sie können die Lasten nicht retten; / sie müssen selbst mit in die Gefangenschaft ziehen." },
      { number: 3, text: "Hört auf mich, ihr vom Haus Jakob, / und ihr alle, die vom Haus Israel noch übrig sind, die mir aufgebürdet sind vom Mutterleib an, / die von mir getragen wurden, seit sie den Schoß ihrer Mutter verließen.", footnotes: ["(3-4) 63,9", "3e: Wörtlich: vom Mutterschoß an."] },
      { number: 4, text: "Ich bleibe derselbe, so alt ihr auch werdet, / bis ihr grau werdet, will ich euch tragen. Ich habe es getan / und ich werde euch weiterhin tragen, / ich werde euch schleppen und retten.", footnotes: ["Ps 71,18"] },
      { number: 5, text: "Mit wem wollt ihr mich vergleichen, / neben wen mich stellen? An wem wollt ihr mich messen, / um zu sehen, ob wir uns gleichen?", footnotes: ["40,18"] },
      { number: 6, text: "Man schüttet Gold aus dem Beutel / und wiegt Silber ab auf der Waage. Man bezahlt einen Goldschmied, damit er einen Gott daraus macht. / Man kniet nieder und wirft sich sogar zu Boden." },
      { number: 7, text: "Man trägt ihn auf der Schulter / und schleppt ihn umher; dann stellt man ihn wieder auf seinen Platz / und dort bleibt er stehen; / er rührt sich nicht von der Stelle. Ruft man ihn an, so antwortet er nicht; / wenn man in Not ist, kann er nicht helfen." },
      { number: 8, text: "Denkt daran und achtet darauf, / ihr Treulosen, nehmt es zu Herzen!", heading: "Kyrus, das Werkzeug Jahwes: 46,8-13" },
      { number: 9, text: "Denkt an das, was früher galt, in uralten Zeiten: / Ich bin Gott und sonst niemand, / ich bin Gott und niemand ist wie ich.", footnotes: ["43,18"] },
      { number: 10, text: "Ich habe von Anfang an die Zukunft verkündet / und lange vorher gesagt, was erst geschehen sollte. Ich sage: Mein Plan steht fest, / und alles, was ich will, führe ich aus.", footnotes: ["41,22f.26; 44,7f; Ps 115,3"] },
      { number: 11, text: "Ich habe aus dem Osten einen Adler gerufen, / aus einem fernen Land rief ich den Mann, / den ich brauchte für meinen Plan. Ich habe es gesagt und ich lasse es kommen. / Ich habe es geplant und ich führe es aus." },
      { number: 12, text: "Hört auf mich, ihr Verzagten, / denen das Heil noch fern ist.", footnotes: ["ihr Verzagten: Text korr. nach G."] },
      { number: 13, text: "Ich selbst bringe euch das Heil, / es ist nicht mehr fern; / meine Hilfe verzögert sich nicht. Ich bringe Hilfe für Zion / und verleihe Israel meine strahlende Pracht." },
    ] },
    { number: 47, verses: [
      { number: 1, text: "Steig herab, Tochter Babel, / Jungfrau, setz dich in den Staub! Setz dich auf die Erde; / es gibt keinen Thron mehr (für dich), Tochter Chaldäas. / Jetzt nennt man dich nicht mehr die Feine, die Zarte.", heading: "Der Sturz Babels: 47,1-15", footnotes: ["1f: Aus der Königin wird beim Gericht Gottes eine Sklavin."] },
      { number: 2, text: "Nimm die Mühle und mahle das Mehl! / Weg mit dem Schleier! Heb deine Schleppe hoch, entblöße die Beine / und wate durchs Wasser!", footnotes: ["Nah 3,5"] },
      { number: 3, text: "Deine Scham wird entblößt, / man sieht deine Schande. Unerbittlich nehme ich Rache, /" },
      { number: 4, text: "spricht unser Erlöser; «Herr der Heere» heißt er / und «Der Heilige Israels».", footnotes: ["spricht: ergänzt mit G."] },
      { number: 5, text: "Setz dich hin und verstumme! / Geh hinaus ins Dunkel, Tochter Chaldäas! Denn nun nennt dich niemand mehr / «Herrin der Reiche»." },
      { number: 6, text: "Ich war zornig über mein Volk, / ich entweihte mein Erbe / und gab es in deine Gewalt. Doch du hast ihm kein Erbarmen geschenkt, / du hast den Greisen ein zu schweres Joch auferlegt." },
      { number: 7, text: "Du dachtest: / Ich bleibe für immer und ewig die Herrin. Du hast dir das alles nicht zu Herzen genommen, / hast nie an das Ende gedacht." },
      { number: 8, text: "Nun aber höre, du üppiges Weib, / die du in Sicherheit lebst / und in deinem Herzen denkst: Ich und sonst niemand! / Niemals sitze ich da als Witwe, / Kinderlosigkeit kenne ich nicht.", footnotes: ["Zef 2,15; Offb 18,7"] },
      { number: 9, text: "Doch beides wird dich ereilen, / plötzlich, am gleichen Tag: Kinderlos wirst du und Witwe, / in voller Schwere trifft dich das Unheil, trotz all deiner Zauberei / und trotz der Macht deiner beschwörenden Formeln.", footnotes: ["47,12"] },
      { number: 10, text: "Du hast dich auf deine bösen Taten verlassen / und gedacht: Es sieht mich ja keiner. Deine Weisheit und dein Wissen verleiteten dich, in deinem Herzen zu denken: / Ich und sonst niemand!", footnotes: ["10-13: In Babylonien standen Magie und Astrologie in hoher Blüte."] },
      { number: 11, text: "Doch es wird ein Unheil über dich kommen, / das du nicht wegzaubern kannst. Ein Verderben wird dich überfallen, / das du nicht zu bannen vermagst. Und plötzlich wird dein Untergang kommen, / an den du niemals gedacht hast." },
      { number: 12, text: "Dann stell dich hin / mit deinen beschwörenden Formeln und mit deinen vielen Zaubersprüchen, / mit denen du dich seit deiner Jugend abgemüht hast. Vielleicht kannst du dir helfen, / vielleicht das Unglück verscheuchen.", footnotes: ["47,9; Dan 2,2"] },
      { number: 13, text: "Du hast dir große Mühe gemacht / mit deinen vielen Beratern; sollen sie doch auftreten und dich retten, / sie, die den Himmel deuten und die Sterne betrachten, / die dir an jedem Neumond verkünden, was kommt." },
      { number: 14, text: "Wie die Spreu werden sie sein, / die das Feuer verbrennt. Sie können sich nicht retten / vor der Gewalt der Flammen. Das wird keine Glut sein, an der man sich wärmt, / kein Feuer, um das man herumsitzt." },
      { number: 15, text: "So geht es all deinen Zauberern, / um die du dich seit deiner Jugend bemüht hast. Sie machen sich alle davon, / keiner will dir mehr helfen." },
    ] },
    { number: 48, verses: [
      { number: 1, text: "Hört her, ihr vom Haus Jakob, / die ihr den Namen Israels tragt / und Judas Nachkommen seid, die ihr beim Namen des Herrn schwört / und euch bekennt zu Israels Gott, / aber nicht offen und ehrlich.", heading: "Der Erweis der Gottheit des Herrn durch Weissagungen: 48,1-11" },
      { number: 2, text: "Sie nennen sich nach der Heiligen Stadt / und verlassen sich auf Israels Gott, / der «Herr der Heere» genannt wird." },
      { number: 3, text: "Was früher war, hatte ich schon längst im Voraus verkündet, / es kam aus meinem Mund, ich ließ es hören; / plötzlich habe ich gehandelt und es traf ein.", footnotes: ["43,9"] },
      { number: 4, text: "Weil ich wusste, dass du halsstarrig bist, / dass dein Nacken eiserne Sehnen hat / und deine Stirn aus Bronze ist," },
      { number: 5, text: "hatte ich es dir schon längst im Voraus verkündet; / ich ließ es dich hören, bevor es geschah, damit du nicht sagst: Mein Götterbild hat das vollbracht, / mein geschnitztes und gegossenes Bild hat es befohlen." },
      { number: 6, text: "Du hast es gehört. Betrachte nun alles! / Willst du es nicht andern verkünden? Von jetzt an lasse ich dich etwas Neues hören, / etwas Verborgenes, von dem du nichts weißt.", footnotes: ["42,9", "Willst du, wörtlich: Wollt ihr."] },
      { number: 7, text: "Eben erst kam es zustande, / nicht schon vor langer Zeit. Zuvor hast du nichts erfahren davon, / damit du nicht sagst: / Das habe ich längst schon gewusst." },
      { number: 8, text: "Du hast davon nichts gehört und gewusst, / dein Ohr war bisher nicht offen. Denn ich wusste, dass du treulos sein wirst, / man nennt dich «abtrünnig vom Mutterleib an»." },
      { number: 9, text: "Doch um meines Namens willen / halte ich meinen Zorn lange zurück, um meiner Ehre willen bezähme ich mich, / um dich nicht vernichten zu müssen." },
      { number: 10, text: "Ich habe dich geläutert, doch nicht wie Silber: / Im Schmelzofen des Elends prüfte ich dich.", footnotes: ["Dtn 4,20", "10b: prüfte: Text korr. nach dem Qumran-Text; H: erwählte."] },
      { number: 11, text: "Nur um meinetwillen handle ich jetzt, / denn sonst würde mein Name entweiht; / ich überlasse die Ehre, die mir gebührt, keinem andern.", footnotes: ["42,8", "mein Name: ergänzt nach G."] },
      { number: 12, text: "Jakob, höre auf mich, / höre mich, Israel, den ich berief: / Ich bin es, ich, der Erste und auch der Letzte.", heading: "Kyrus, der Beauftragte Jahwes: 48,12-16", footnotes: ["44,6"] },
      { number: 13, text: "Meine Hand hat die Fundamente der Erde gelegt, / meine Rechte hat den Himmel ausgespannt; / ich rief ihnen zu und schon standen sie alle da.", footnotes: ["Ps 33,9"] },
      { number: 14, text: "Versammelt euch alle und hört - / wer von den Göttern hat so etwas jemals verkündet? -: Der, den der Herr liebt, wird meinen Willen an Babel vollstrecken / und sein Arm wird es an den Chaldäern bewirken." },
      { number: 15, text: "Ich habe gesprochen und ich habe ihn auch berufen, / ich habe ihn kommen lassen / und er wird seinen Weg erfolgreich beenden." },
      { number: 16, text: "Kommt her zu mir und hört, was ich sage: / Ich habe von Anfang an nicht im Verborgenen geredet; / seit das alles geschieht, bin ich dabei. / [Und jetzt hat Gott, der Herr, mich und seinen Geist gesandt.]", footnotes: ["45,19", "Zusatz aus einem anderen Zusammenhang (vgl. 61,1)."] },
      { number: 17, text: "So spricht der Herr, dein Erlöser, / der Heilige Israels: Ich bin der Herr, dein Gott, / der dich lehrt, was Nutzen bringt, und der dich auf den Weg führt, / den du gehen sollst.", heading: "Die Lehre aus der Vergangenheit: 48,17-19" },
      { number: 18, text: "Hättest du doch auf meine Gebote geachtet! / Dein Glück wäre wie ein Strom / und dein Heil wie die Wogen des Meeres.", footnotes: ["Dtn 5,29; Ps 81,14-17"] },
      { number: 19, text: "Deine Nachkommen wären (zahlreich) wie der Sand / und deine leiblichen Kinder wie seine Körner. / Ihr Name wäre in meinen Augen nicht getilgt und gelöscht.", footnotes: ["Gen 22,17"] },
      { number: 20, text: "Heraus aus Babel, flieht aus Chaldäa! / Verkündet es jauchzend, damit man es hört. Ruft es hinaus bis ans Ende der Erde! / Ruft: Der Herr hat seinen Knecht Jakob ausgelöst.", heading: "Die glückliche Heimkehr aus Babel: 48,20-22", footnotes: ["52,11; Jer 51,6; Offb 18,4"] },
      { number: 21, text: "Sie litten keinen Durst, als er sie durch die Wüste führte; / Wasser ließ er für sie aus dem Felsen sprudeln, / er spaltete den Felsen und es strömte das Wasser.", footnotes: ["Ex 17,6"] },
      { number: 22, text: "[Die Ruchlosen finden keinen Frieden, spricht der Herr.]", footnotes: ["57,21"] },
    ] },
    { number: 49, verses: [
      { number: 1, text: "Hört auf mich, ihr Inseln, / merkt auf, ihr Völker in der Ferne! Der Herr hat mich schon im Mutterleib berufen; / als ich noch im Schoß meiner Mutter war, hat er meinen Namen genannt.", heading: "Das zweite Lied vom Gottesknecht: 49,1-9c" },
      { number: 2, text: "Er machte meinen Mund zu einem scharfen Schwert, / er verbarg mich im Schatten seiner Hand. Er machte mich zum spitzen Pfeil / und steckte mich in seinen Köcher.", footnotes: ["Offb 19,15; Jes 51,16"] },
      { number: 3, text: "Er sagte zu mir: Du bist mein Knecht, Israel, / an dem ich meine Herrlichkeit zeigen will." },
      { number: 4, text: "Ich aber sagte: Vergeblich habe ich mich bemüht, / habe meine Kraft umsonst und nutzlos vertan. Aber mein Recht liegt beim Herrn / und mein Lohn bei meinem Gott.", footnotes: ["1 Kor 15,58"] },
      { number: 5, text: "Jetzt aber hat der Herr gesprochen, / der mich schon im Mutterleib zu seinem Knecht gemacht hat, damit ich Jakob zu ihm heimführe / und Israel bei ihm versammle. So wurde ich in den Augen des Herrn geehrt / und mein Gott war meine Stärke.", footnotes: ["5e: bei ihm: Text korr. nach dem Qumran-Text und G."] },
      { number: 6, text: "Und er sagte: Es ist zu wenig, dass du mein Knecht bist, / nur um die Stämme Jakobs wieder aufzurichten / und die Verschonten Israels heimzuführen. Ich mache dich zum Licht für die Völker; / damit mein Heil bis an das Ende der Erde reicht.", footnotes: ["42,6; Apg 13,47"] },
      { number: 7, text: "So spricht der Herr, der Befreier Israels, sein Heiliger, / zu dem tief verachteten Mann, dem Abscheu der Leute, / dem Knecht der Tyrannen: Könige werden es sehen und sich erheben, / Fürsten werfen sich nieder, um des Herrn willen, der treu ist, / um des Heiligen Israels willen, der dich erwählt hat." },
      { number: 8, text: "So spricht der Herr: Zur Zeit der Gnade will ich dich erhören, / am Tag der Rettung dir helfen. Ich habe dich geschaffen und dazu bestimmt, / der Bund zu sein für das Volk, aufzuhelfen dem Land / und das verödete Erbe neu zu verteilen,", footnotes: ["2 Kor 6,2"] },
      { number: 9, text: "den Gefangenen zu sagen: Kommt heraus!, / und denen, die in der Finsternis sind: Kommt ans Licht!", footnotes: ["9d: auf allen Bergen: Text korr. nach dem Qumran-Text; H: auf den Wegen."] },
      { number: 10, text: "Auf allen Bergen werden sie weiden, / auf allen kahlen Hügeln finden sie Nahrung. Sie leiden weder Hunger noch Durst, / Hitze und Sonnenglut schaden ihnen nicht. Denn er leitet sie voll Erbarmen / und führt sie zu sprudelnden Quellen.", heading: "Die Wiederherstellung des Zion: 49,9d - 50,3 | Die wunderbare Heimkehr: 49,9d-13", footnotes: ["Offb 7,16"] },
      { number: 11, text: "Alle Berge mache ich zu Wegen / und meine Straßen werden gebahnt sein.", footnotes: ["40,4"] },
      { number: 12, text: "Seht her: Sie kommen von fern, / die einen von Norden und Westen, / andere aus dem Land der Siniter.", footnotes: ["Lk 13,29", "12c: Siniter: wahrscheinlich die Einwohner von Syene, heute Assuan in Oberägypten."] },
      { number: 13, text: "Jubelt, ihr Himmel, jauchze, o Erde, / freut euch, ihr Berge! Denn der Herr hat sein Volk getröstet / und sich seiner Armen erbarmt.", footnotes: ["44,23"] },
      { number: 14, text: "Doch Zion sagt: Der Herr hat mich verlassen, / Gott hat mich vergessen.", heading: "Gottes Trost für Zion: 49,14 - 50,3" },
      { number: 15, text: "Kann denn eine Frau ihr Kindlein vergessen, / eine Mutter ihren leiblichen Sohn? Und selbst wenn sie ihn vergessen würde: / ich vergesse dich nicht.", footnotes: ["1 Kön 3,26; Jer 31,20"] },
      { number: 16, text: "Sieh her: Ich habe dich eingezeichnet in meine Hände, / deine Mauern habe ich immer vor Augen.", footnotes: ["Das Bild bezieht sich auf den Bauplan Jerusalems."] },
      { number: 17, text: "Deine Erbauer eilen herbei / und alle, die dich zerstört und verwüstet haben, ziehen davon." },
      { number: 18, text: "Blick auf und schau umher: / Alle versammeln sich und kommen zu dir. So wahr ich lebe - Spruch des Herrn: / Du sollst sie alle wie einen Schmuck anlegen, / du sollst dich mit ihnen schmücken wie eine Braut.", footnotes: ["60,4"] },
      { number: 19, text: "Denn dein ödes, verheertes, zerstörtes Land / wird jetzt zu eng für seine Bewohner, / weit weg sind alle, die dich verschlingen wollten." },
      { number: 20, text: "Bald wirst du, die du kinderlos warst, / mit eigenen Ohren hören, wie deine Kinder sagen: Mir ist der Platz hier zu eng, / rück zur Seite, ich will mich setzen.", footnotes: ["54,1"] },
      { number: 21, text: "Dann wirst du dich in deinem Herzen fragen: / Wer hat mir diese (Kinder) geboren? Ich war doch kinderlos und unfruchtbar, / war verbannt und verstoßen. Wer hat mir die Kinder herangezogen? / Ich war doch allein übrig geblieben. / Wo kommen sie her?" },
      { number: 22, text: "So spricht Gott, der Herr: / Sieh her, ich hebe die Hand in Richtung der Völker, / ich errichte für die Nationen ein Zeichen / und sie bringen auf ihren Armen deine Söhne herbei / und tragen deine Töchter auf ihren Schultern.", footnotes: ["62,10; 60,4"] },
      { number: 23, text: "Könige werden deine Kinder pflegen / und Fürstinnen ihre Ammen sein. Mit dem Gesicht zur Erde werfen sie sich nieder vor dir / und lecken dir den Staub von den Füßen. Dann wirst du erkennen, dass ich der Herr bin / und dass keiner beschämt wird, der auf mich hofft.", footnotes: ["60,16; Ps 72,9"] },
      { number: 24, text: "Kann man einem Starken die Beute entreißen? / Kann einem Mächtigen der Gefangene entkommen?", footnotes: ["24b: einen Mächtigen: Text korr. nach dem Qumran-Text und den alten Übersetzungen; H: einem Gerechten; gemeint ist der König von Babel."] },
      { number: 25, text: "So spricht der Herr: / Auch einem Starken entreißt man den Gefangenen, / und einem Mächtigen entkommt seine Beute. Ich selbst will mit deinem Gegner streiten, / ich selbst will deine Söhne befreien." },
      { number: 26, text: "Deinen Unterdrückern gebe ich ihr eigenes Fleisch zu essen, / sie sollen sich an ihrem Blut berauschen wie an Most. Dann werden alle Sterblichen erkennen, / dass ich, der Herr, dein Retter bin / und ich, der Starke Jakobs, dein Erlöser.", footnotes: ["60,16"] },
    ] },
    { number: 50, verses: [
      { number: 1, text: "So spricht der Herr: Wo ist denn die Scheidungsurkunde, / mit der ich eure Mutter fortgeschickt habe? Wo ist mein Gläubiger, / dem ich euch verkauft habe? Seht, wegen eurer bösen Taten wurdet ihr verkauft, / wegen eurer Vergehen wurde eure Mutter fortgeschickt.", footnotes: ["Dtn 24,1", "In bildhafter Aussage ist Israel die Gemahlin Jahwes, vgl. Hos 1 - 3. Der zweite Satz verwendet das Bild von der Schuldknechtschaft, in die ein Vater seine Kinder verkauft."] },
      { number: 2, text: "Warum war niemand da, als ich kam, / warum gab niemand Antwort, als ich rief? Ist meine Hand denn zu schwach, um zu befreien, / fehlt mir die Kraft, um zu retten? Durch meine Drohung trockne ich das Meer aus. / Ich mache Flüsse zur Wüste, sodass die Fische verfaulen aus Mangel an Wasser / und sterben vor Durst.", footnotes: ["59,1"] },
      { number: 3, text: "Ich kleide den Himmel in Schwarz / und hülle ihn in ein Trauergewand.", footnotes: ["Offb 6,12"] },
      { number: 4, text: "Gott, der Herr, gab mir die Zunge eines Jüngers, / damit ich verstehe, die Müden zu stärken durch ein aufmunterndes Wort. Jeden Morgen weckt er mein Ohr, / damit ich auf ihn höre wie ein Jünger.", heading: "Das dritte Lied vom Gottesknecht: 50,4-9", footnotes: ["Hören und Verkünden sind Aufgabe des Gottesknechts, der als Prophet das Gotteswort empfängt und weitergibt."] },
      { number: 5, text: "Gott, der Herr, hat mir das Ohr geöffnet. / Ich aber wehrte mich nicht / und wich nicht zurück." },
      { number: 6, text: "Ich hielt meinen Rücken denen hin, / die mich schlugen, und denen, die mir den Bart ausrissen, / meine Wangen. Mein Gesicht verbarg ich nicht / vor Schmähungen und Speichel.", footnotes: ["Mt 26,67f"] },
      { number: 7, text: "Doch Gott, der Herr, wird mir helfen; / darum werde ich nicht in Schande enden. Deshalb mache ich mein Gesicht hart wie einen Kiesel; / ich weiß, dass ich nicht in Schande gerate.", footnotes: ["Ez 3,8f"] },
      { number: 8, text: "Er, der mich freispricht, ist nahe. / Wer wagt es, mit mir zu streiten? Lasst uns zusammen vortreten! / Wer ist mein Gegner im Rechtsstreit? / Er trete zu mir heran." },
      { number: 9, text: "Seht her, Gott, der Herr, wird mir helfen. / Wer kann mich für schuldig erklären? Seht: Sie alle zerfallen / wie ein Gewand, das die Motten zerfressen.", footnotes: ["51,8"] },
      { number: 10, text: "Wer von euch den Herrn fürchtet, / der höre auf die Stimme seines Knechtes. Wer im Dunkel lebt und wem kein Licht leuchtet, / der vertraue auf den Namen des Herrn / und verlasse sich auf seinen Gott.", heading: "Mahnung zum Vertrauen: 50,10 - 52,12 | Die mächtige Hilfe Gottes: 50,10 - 51,8", footnotes: ["9,1"] },
      { number: 11, text: "Ihr alle aber, die ihr Feuer legt / und Brandpfeile entzündet, sollt in die Glut eures eigenen Feuers laufen / und in die Brandpfeile geraten, die ihr entflammt habt. Durch meine Hand kommt das über euch; / am Ort der Qualen werdet ihr liegen.", footnotes: ["11b: entzündet: Text korr."] },
    ] },
    { number: 51, verses: [
      { number: 1, text: "Hört auf mich, die ihr der Gerechtigkeit nachjagt / und die ihr den Herrn sucht. Blickt auf den Felsen, aus dem ihr gehauen seid, / auf den Schacht, aus dem ihr herausgebohrt wurdet.", footnotes: ["51,7"] },
      { number: 2, text: "Blickt auf Abraham, euren Vater, / und auf Sara, die euch gebar. Er war allein, als ich ihn rief; / doch ich habe ihn gesegnet / und ihm viele Nachkommen geschenkt." },
      { number: 3, text: "Denn der Herr hat Erbarmen mit Zion, / er hat Erbarmen mit all seinen Ruinen. Seine Wüste macht er wie Eden, / seine Öde wie den Garten des Herrn. Freude und Fröhlichkeit findet man dort, / Lobpreis und den Klang von Liedern." },
      { number: 4, text: "Horcht her, ihr Völker, / hört auf mich, ihr Nationen! Denn von mir kommt die Weisung / und mein Recht wird zum Licht der Völker.", footnotes: ["2,3; 42,6; 49,6", "ihr Völker, ihr Nationen: Text korr. nach einigen H-Handschriften und S; H: mein Volk, meine Nation."] },
      { number: 5, text: "Plötzlich ist meine Gerechtigkeit da / und von mir kommt die Hilfe. Mein Arm verschafft den Völkern ihr Recht; / auf mich hoffen die Inseln, / sie warten auf meinen Arm.", footnotes: ["42,4", "5a: plötzlich: ergänzt nach G."] },
      { number: 6, text: "Blickt auf zum Himmel, / betrachtet die Erde hier unten! Der Himmel zerflattert wie Rauch, / die Erde zerfällt wie ein Kleid; / ihre Bewohner sterben wie die Fliegen. Doch meine hilfreiche Gnade bleibt für immer bestehen, / meine Gerechtigkeit wird niemals erschüttert." },
      { number: 7, text: "Hört auf mich, die ihr das Recht kennt, / du Volk, das mein Gesetz im Herzen trägt. Fürchtet euch nicht vor der Beschimpfung durch Menschen, / erschreckt nicht vor ihrem Spott!", footnotes: ["51,1; Mt 5,11"] },
      { number: 8, text: "Denn man frisst sie, wie die Motte das Kleid, / man frisst sie, wie die Schabe die Wolle. Doch meine Gerechtigkeit bleibt für immer bestehen / und von Generation zu Generation meine hilfreiche Gnade.", footnotes: ["50,9"] },
      { number: 9, text: "Wach auf, wach auf, bekleide dich mit Macht, / Arm des Herrn! Wach auf wie in den früheren Tagen, / wie bei den Generationen der Vorzeit! Warst du es nicht, der die Rahab zerhieb / und den Drachen durchbohrte?", heading: "Der starke Arm Jahwes: 51,9-16", footnotes: ["52,1; Ijob 26,12f; Ps 89,11", "Rahab und Drache (H: Tannin) sind Urweltungeheuer (vgl. die Anmerkung zu 27,1). Der Schöpfergott Jahwe hat sie besiegt; mit diesem Sieg wird hier seine Rettungstat an Israel beim Auszug aus Ägypten verbunden."] },
      { number: 10, text: "Warst du es nicht, der das Meer austrocknen ließ, / die Wasser der großen Flut, der die Tiefen des Meeres zum Weg gemacht hat, / damit die Erlösten hindurchziehen konnten?", footnotes: ["Ex 14,21f"] },
      { number: 11, text: "Die vom Herrn Befreiten kehren zurück / und kommen voll Jubel nach Zion. Ewige Freude ruht auf ihren Häuptern. / Wonne und Freude stellen sich ein, / Kummer und Seufzen entfliehen.", footnotes: ["35,10"] },
      { number: 12, text: "Ich bin es, ja ich, der euch tröstet. / Was hast du, dass du dich fürchtest vor sterblichen Menschen, / vor Menschen, die dahinschwinden wie Gras?" },
      { number: 13, text: "Warum vergisst du den Herrn, deinen Schöpfer, / der den Himmel ausgespannt und die Fundamente der Erde gelegt hat? Warum zitterst du dauernd vor der Wut dessen, der dich bedrängt, / der darauf ausgeht, dich zu vernichten? / Wo ist denn die Wut dessen, der dich bedrängt?" },
      { number: 14, text: "Bald wird der Gefesselte freigelassen; / er wird nicht im Kerker sterben / und es mangelt ihm nicht mehr an Brot." },
      { number: 15, text: "Ich bin doch der Herr, dein Gott, / der das Meer aufwühlt, sodass die Wogen tosen. / Herr der Heere ist sein Name.", footnotes: ["Jer 31,35"] },
      { number: 16, text: "Ich habe dir meine Worte in den Mund gelegt, / im Schatten meiner Hand habe ich dich verborgen, als ich den Himmel ausspannte und die Fundamente der Erde legte / und zu Zion sagte: Du bist mein Volk.", footnotes: ["59,21; 49,2; Hos 2,25", "Text unklar. - als ich ausspannte: Text korr. nach V. 13 und S."] },
      { number: 17, text: "Raff dich auf, raff dich auf, / steh auf, Jerusalem! Du hast aus dem Becher des Zorns getrunken, / den der Herr in der Hand hielt. Du hast aus dem betäubenden Becher getrunken / und ihn geleert.", heading: "Der Zornbecher Gottes: 51,17-23", footnotes: ["Jer 25,15-19; Ps 60,5; 75,9; Offb 16,19", "17f: Der «Becher des Zorns» (vgl. VV. 21-23) ist Bild für das göttliche Strafgericht. - «sie» in V. 18 ist Jerusalem."] },
      { number: 18, text: "Da war von all den Söhnen, die sie gebar, / keiner, der sie geführt hat. Da war von all den Söhnen, die sie aufzog, / keiner, der sie bei der Hand nahm." },
      { number: 19, text: "Beides hat dich getroffen / - doch wer klagt schon um dich? -: Verheerung und Zerstörung, Hunger und Schwert. / Doch wer tröstet dich schon?", footnotes: ["tröstet: Text korr. nach dem Qumran-Text und den alten Übersetzungen (3. statt 1. Person)."] },
      { number: 20, text: "An allen Straßenecken lagen deine Söhne hilflos da, / wie Wildschafe im Netz, überwältigt vom Zorn des Herrn, / vom Schelten deines Gottes." },
      { number: 21, text: "Darum hör doch her, du Ärmste, / die du betrunken bist, aber nicht vom Wein:" },
      { number: 22, text: "So spricht der Herr, dein Gott und Gebieter, / der für sein Volk kämpft: Schon nehme ich dir den betäubenden Becher aus der Hand, / den Kelch meines Zorns; / du sollst daraus nicht mehr trinken." },
      { number: 23, text: "Ich reiche ihn denen, die dich quälten, / die zu dir sagten: Wirf dich zu Boden, / wir schreiten über dich weg. So musstest du deinen Rücken zum Fußboden machen, / zum Weg für die, die über dich schritten.", footnotes: ["Die Sieger schritten über die Besiegten hinweg, die sich zu Boden werfen mussten."] },
    ] },
    { number: 52, verses: [
      { number: 1, text: "Wach auf, Zion, wach auf, / zieh das Gewand deiner Macht an! Zieh deine Prunkkleider an, / Jerusalem, du heilige Stadt! / Denn Unbeschnittene und Unreine werden dich nicht mehr betreten.", heading: "Das neue Heil für Zion: 52,1-12", footnotes: ["51,9; Offb 21,27"] },
      { number: 2, text: "Schüttle den Staub von dir ab, / steh auf, du gefangenes Jerusalem! Löse die Fesseln von deinem Hals, / gefangene Tochter Zion!" },
      { number: 3, text: "Denn so spricht der Herr: / Umsonst wurdet ihr verkauft / und ihr sollt nicht mit Geld losgekauft werden.", footnotes: ["45,13; 1 Petr 1,18"] },
      { number: 4, text: "Denn so spricht Gott, der Herr: / Nach Ägypten zog mein Volk einst hinab, / um dort in der Fremde zu leben. / Auch von Assur wurde es ohne Grund unterdrückt." },
      { number: 5, text: "Aber was erlebe ich jetzt - Spruch des Herrn -? / Man nahm mein Volk, ohne zu bezahlen, und nun prahlen seine Beherrscher - Spruch des Herrn -; / ständig, jeden Tag wird mein Name gelästert.", footnotes: ["Röm 2,24"] },
      { number: 6, text: "Darum soll mein Volk an jenem Tag meinen Namen erkennen / und wissen, dass ich es bin, der sagt: Ich bin da." },
      { number: 7, text: "Wie willkommen sind auf den Bergen / die Schritte des Freudenboten, der Frieden ankündigt, / der eine frohe Botschaft bringt und Rettung verheißt, / der zu Zion sagt: Dein Gott ist König." },
      { number: 8, text: "Horch, deine Wächter erheben die Stimme, / sie beginnen alle zu jubeln. Denn sie sehen mit eigenen Augen, / wie der Herr nach Zion zurückkehrt.", footnotes: ["Num 14,14"] },
      { number: 9, text: "Brecht in Jubel aus, jauchzt alle zusammen, / ihr Trümmer Jerusalems! Denn der Herr tröstet sein Volk, / er erlöst Jerusalem." },
      { number: 10, text: "Der Herr macht seinen heiligen Arm frei / vor den Augen aller Völker. Alle Enden der Erde / sehen das Heil unseres Gottes.", footnotes: ["Ps 98,1-3", "Gott will eingreifen und für sein Volk kämpfen."] },
      { number: 11, text: "Fort, fort! Zieht von dort weg! / Fasst nichts Unreines an! Zieht von dort weg! / Haltet euch rein; / denn ihr tragt die Geräte des Herrn.", footnotes: ["48,20; 2 Kor 6,17", "Befehl zum Auszug aus Babel, der wie in einer Prozession erfolgen soll."] },
      { number: 12, text: "Doch zieht nicht weg in Hast, / geht nicht fort in Eile; denn der Herr geht vor euch her / und er, Israels Gott, beschließt auch euren Zug.", footnotes: ["Ex 12,11; Dtn 16,3; Jes 58,8"] },
      { number: 13, text: "Seht, mein Knecht hat Erfolg, / er wird groß sein und hoch erhaben.", heading: "Das vierte Lied vom Gottesknecht: 52,13 - 53,12", footnotes: ["13-53,12: Zusammengesetzt aus Gottesworten (52,13-15; 53,11d.12), einem Bericht über das Leiden des «Knechts» (53,1-9) und der Ankündigung der Erhöhung des «Knechts» (53,10.11a-c). Im Einzelnen ist der hebräische Text gelegentlich unklar."] },
      { number: 14, text: "Viele haben sich über ihn entsetzt, / so entstellt sah er aus, nicht mehr wie ein Mensch, / seine Gestalt war nicht mehr die eines Menschen.", footnotes: ["Weish 5,2"] },
      { number: 15, text: "Jetzt aber setzt er viele Völker in Staunen, / Könige müssen vor ihm verstummen. Denn was man ihnen noch nie erzählt hat, / das sehen sie nun; was sie niemals hörten, / das erfahren sie jetzt.", footnotes: ["Röm 15,21"] },
    ] },
    { number: 53, verses: [
      { number: 1, text: "Wer hat unserer Kunde geglaubt? / Der Arm des Herrn - wem wurde er offenbar?", footnotes: ["Joh 12,38; Röm 10,16"] },
      { number: 2, text: "Vor seinen Augen wuchs er auf wie ein junger Spross, / wie ein Wurzeltrieb aus trockenem Boden. Er hatte keine schöne und edle Gestalt, / sodass wir ihn anschauen mochten. Er sah nicht so aus, / dass wir Gefallen fanden an ihm.", footnotes: ["11,1.10"] },
      { number: 3, text: "Er wurde verachtet und von den Menschen gemieden, / ein Mann voller Schmerzen, / mit Krankheit vertraut. Wie einer, vor dem man das Gesicht verhüllt, / war er verachtet; wir schätzten ihn nicht." },
      { number: 4, text: "Aber er hat unsere Krankheit getragen / und unsere Schmerzen auf sich geladen. Wir meinten, er sei von Gott geschlagen, / von ihm getroffen und gebeugt.", footnotes: ["Mt 8,17"] },
      { number: 5, text: "Doch er wurde durchbohrt wegen unserer Verbrechen, / wegen unserer Sünden zermalmt. Zu unserem Heil lag die Strafe auf ihm, / durch seine Wunden sind wir geheilt.", footnotes: ["1 Petr 2,24f"] },
      { number: 6, text: "Wir hatten uns alle verirrt wie Schafe, / jeder ging für sich seinen Weg. Doch der Herr lud auf ihn / die Schuld von uns allen.", footnotes: ["56,11"] },
      { number: 7, text: "Er wurde misshandelt und niedergedrückt, / aber er tat seinen Mund nicht auf. Wie ein Lamm, das man zum Schlachten führt, / und wie ein Schaf angesichts seiner Scherer, / so tat auch er seinen Mund nicht auf.", footnotes: ["Apg 8,32f; Offb 5,6.12; 13,8"] },
      { number: 8, text: "Durch Haft und Gericht wurde er dahingerafft, / doch wen kümmerte sein Geschick? Er wurde vom Land der Lebenden abgeschnitten / und wegen der Verbrechen seines Volkes zu Tode getroffen.", footnotes: ["seines Volkes: Text korr. nach dem Qumran-Text; H: meines Volkes."] },
      { number: 9, text: "Bei den Ruchlosen gab man ihm sein Grab, / bei den Verbrechern seine Ruhestätte, obwohl er kein Unrecht getan hat / und kein trügerisches Wort in seinem Mund war.", footnotes: ["1 Petr 2,22"] },
      { number: 10, text: "Doch der Herr fand Gefallen an seinem zerschlagenen (Knecht), / er rettete den, der sein Leben als Sühnopfer hingab. Er wird Nachkommen sehen und lange leben. / Der Plan des Herrn wird durch ihn gelingen." },
      { number: 11, text: "Nachdem er so vieles ertrug, erblickt er das Licht. / Er sättigt sich an Erkenntnis. Mein Knecht, der gerechte, macht die vielen gerecht; / er lädt ihre Schuld auf sich.", footnotes: ["Dan 12,3; Röm 5,19", "Licht: eingefügt nach dem Qumran-Text und G."] },
      { number: 12, text: "Deshalb gebe ich ihm seinen Anteil unter den Großen / und mit den Mächtigen teilt er die Beute, weil er sein Leben dem Tod preisgab / und sich unter die Verbrecher rechnen ließ. Denn er trug die Sünden von vielen / und trat für die Schuldigen ein.", footnotes: ["Röm 4,25; Lk 22,37; Hebr 9,28; Joh 1,29; Röm 5,16; 1 Petr 2,24"] },
    ] },
    { number: 54, verses: [
      { number: 1, text: "Freu dich, du Unfruchtbare, die nie gebar, / du, die nie in Wehen lag, brich in Jubel aus und jauchze! Denn die Einsame hat jetzt viel mehr Söhne / als die Vermählte, spricht der Herr.", heading: "Die Verheißung des Glücks im neuen Zion: 54,1 - 55,5 | Der Segen Gottes für das Volk: 54,1-10", footnotes: ["Gal 4,27; Jes 62,4", "Angeredet ist Jerusalem."] },
      { number: 2, text: "Mach den Raum deines Zeltes weit, / spann deine Zelttücher aus, ohne zu sparen. / Mach die Stricke lang und die Pflöcke fest!", footnotes: ["spann . . . aus: Text korr. nach den alten Übersetzungen."] },
      { number: 3, text: "Denn nach rechts und links breitest du dich aus. / Deine Nachkommen werden Völker beerben / und verödete Städte besiedeln." },
      { number: 4, text: "Fürchte dich nicht, du wirst nicht beschämt; / schäme dich nicht, du wirst nicht enttäuscht. Denn die Schande in deiner Jugend wirst du vergessen, / an die Schmach deiner Witwenschaft wirst du nicht mehr denken.", footnotes: ["4-6: Die Zeit des Gerichts über Jerusalem ist vorbei."] },
      { number: 5, text: "Denn dein Schöpfer ist dein Gemahl, / «Herr der Heere» ist sein Name. Der Heilige Israels ist dein Erlöser, / «Gott der ganzen Erde» wird er genannt.", footnotes: ["Hos 2,18"] },
      { number: 6, text: "Ja, der Herr hat dich gerufen / als verlassene, bekümmerte Frau. Kann man denn die Frau verstoßen, / die man in der Jugend geliebt hat?, / spricht dein Gott." },
      { number: 7, text: "Nur für eine kleine Weile habe ich dich verlassen, / doch mit großem Erbarmen hole ich dich heim.", footnotes: ["Ps 30,6"] },
      { number: 8, text: "Einen Augenblick nur verbarg ich vor dir mein Gesicht / in aufwallendem Zorn; aber mit ewiger Huld habe ich Erbarmen mit dir, / spricht dein Erlöser, der Herr.", footnotes: ["60,10"] },
      { number: 9, text: "Wie in den Tagen Noachs soll es für mich sein: / So wie ich damals schwor, dass die Flut Noachs die Erde nie mehr überschwemmen wird, / so schwöre ich jetzt, dir nie mehr zu zürnen / und dich nie mehr zu schelten.", footnotes: ["Gen 9,8-17"] },
      { number: 10, text: "Auch wenn die Berge von ihrem Platz weichen / und die Hügel zu wanken beginnen - meine Huld wird nie von dir weichen / und der Bund meines Friedens nicht wanken, / spricht der Herr, der Erbarmen hat mit dir.", footnotes: ["Ez 34,25; 37,26"] },
      { number: 11, text: "Du Ärmste, vom Sturm Gepeitschte, / die ohne Trost ist, sieh her: Ich selbst lege dir ein Fundament aus Malachit / und Grundmauern aus Saphir.", heading: "Das neue Jerusalem: 54,11-17", footnotes: ["(11-12) Tob 13,17; Offb 21,18-21"] },
      { number: 12, text: "Aus Rubinen mache ich deine Zinnen, / aus Beryll deine Tore / und alle deine Mauern aus kostbaren Steinen." },
      { number: 13, text: "Alle deine Söhne werden Jünger des Herrn sein / und groß ist der Friede deiner Söhne.", footnotes: ["Joh 6,45"] },
      { number: 14, text: "Du wirst auf Gerechtigkeit gegründet sein. / Du bist fern von Bedrängnis, denn du brauchst dich nicht mehr zu fürchten / und bist fern von Schrecken; / er kommt an dich nicht heran.", footnotes: ["Ps 91,10"] },
      { number: 15, text: "Wenn dich jemand angreift, misslingt es, / denn es geschieht ohne mich; / wer dich angreift, fällt im Kampf gegen dich." },
      { number: 16, text: "Ich habe den Schmied erschaffen, / der das Kohlenfeuer entfacht und Waffen erzeugt, / wie es seinem Handwerk entspricht. Ich habe auch den, der vernichtet, erschaffen, / damit er zerstört." },
      { number: 17, text: "Keine Waffe wird etwas ausrichten, die man gegen dich schmiedet; / jede Zunge, die dich vor Gericht verklagt, strafst du Lügen. Das ist das Erbteil der Knechte des Herrn: / Von mir kommt ihre Rettung Spruch des Herrn." },
    ] },
    { number: 55, verses: [
      { number: 1, text: "Auf, ihr Durstigen, kommt alle zum Wasser! / Auch wer kein Geld hat, soll kommen. Kauft Getreide und esst, kommt und kauft ohne Geld, / kauft Wein und Milch ohne Bezahlung!", heading: "Die Teilnahme des ganzen Volkes am Heil: 55,1-5", footnotes: ["Joh 7,37; Offb 22,17"] },
      { number: 2, text: "Warum bezahlt ihr mit Geld, was euch nicht nährt, / und mit dem Lohn eurer Mühen, / was euch nicht satt macht? Hört auf mich, dann bekommt ihr das Beste zu essen / und könnt euch laben an fetten Speisen." },
      { number: 3, text: "Neigt euer Ohr mir zu und kommt zu mir, / hört, dann werdet ihr leben. Ich will einen ewigen Bund mit euch schließen / gemäß der beständigen Huld, die ich David erwies.", footnotes: ["61,8; 2 Chr 6,42; Apg 13,34", "3f: Durch seine Siege hat David die Macht und Zuwendung Gottes zu seinem Volk bezeugt."] },
      { number: 4, text: "Seht her: Ich habe ihn zum Zeugen für die Völker gemacht, / zum Fürsten und Gebieter der Nationen." },
      { number: 5, text: "Völker, die du nicht kennst, wirst du rufen; / Völker, die dich nicht kennen, eilen zu dir, um des Herrn, deines Gottes, des Heiligen Israels willen, / weil er dich herrlich gemacht hat." },
      { number: 6, text: "Sucht den Herrn, solange er sich finden lässt, / ruft ihn an, solange er nahe ist.", heading: "Epilog: Mahnung zur Umkehr und zum Vertrauen auf Gottes Wort: 55,6-13" },
      { number: 7, text: "Der Ruchlose soll seinen Weg verlassen, / der Frevler seine Pläne. Er kehre um zum Herrn, / damit er Erbarmen hat mit ihm, und zu unserem Gott; / denn er ist groß im Verzeihen." },
      { number: 8, text: "Meine Gedanken sind nicht eure Gedanken / und eure Wege sind nicht meine Wege - / Spruch des Herrn." },
      { number: 9, text: "So hoch der Himmel über der Erde ist, / so hoch erhaben sind meine Wege über eure Wege / und meine Gedanken über eure Gedanken." },
      { number: 10, text: "Denn wie der Regen und der Schnee vom Himmel fällt / und nicht dorthin zurückkehrt, sondern die Erde tränkt und sie zum Keimen und Sprossen bringt, / wie er dem Sämann Samen gibt und Brot zum Essen,", footnotes: ["2 Kor 9,10"] },
      { number: 11, text: "so ist es auch mit dem Wort, / das meinen Mund verlässt: Es kehrt nicht leer zu mir zurück, / sondern bewirkt, was ich will, / und erreicht all das, wozu ich es ausgesandt habe." },
      { number: 12, text: "Voll Freude werdet ihr fortziehen, / wohlbehalten kehrt ihr zurück. Berge und Hügel brechen bei eurem Anblick in Jubel aus, / alle Bäume auf dem Feld klatschen Beifall.", footnotes: ["44,23"] },
      { number: 13, text: "Statt Dornen wachsen Zypressen, / statt Brennnesseln Myrten. Das geschieht zum Ruhm des Herrn / als ein ewiges Zeichen, das niemals getilgt wird.", footnotes: ["56,5"] },
    ] },
    { number: 56, verses: [
      { number: 1, text: "So spricht der Herr: Wahrt das Recht und sorgt für Gerechtigkeit; / denn bald kommt von mir das Heil, / meine Gerechtigkeit wird sich bald offenbaren.", heading: "III. Teil Der dritte Jesaja (Tritojesaja): 56,1 - 66,24 | Verheißung an die Fremden und die Kinderlosen: 56,1-8", footnotes: ["1-66,24: Vgl. die Einleitung zu Jes."] },
      { number: 2, text: "Wohl dem Mann, der so handelt, / wohl dem Menschen, der daran fest hält, den Sabbat zu halten und nie zu entweihen / und seine Hand vor jeder bösen Tat zu bewahren.", footnotes: ["58,13"] },
      { number: 3, text: "Der Fremde, der sich dem Herrn angeschlossen hat, soll nicht sagen: / Sicher wird der Herr mich ausschließen aus seinem Volk. Der Verschnittene soll nicht sagen: / Ich bin nur ein dürrer Baum." },
      { number: 4, text: "Denn so spricht der Herr: / Den Verschnittenen, die meine Sabbate halten, die gerne tun, was mir gefällt, / und an meinem Bund fest halten," },
      { number: 5, text: "ihnen allen errichte ich in meinem Haus / und in meinen Mauern ein Denkmal, ich gebe ihnen einen Namen, / der mehr wert ist als Söhne und Töchter: Einen ewigen Namen gebe ich ihnen, / der niemals ausgetilgt wird.", footnotes: ["55,13"] },
      { number: 6, text: "Die Fremden, die sich dem Herrn angeschlossen haben, / die ihm dienen und seinen Namen lieben, um seine Knechte zu sein, / alle, die den Sabbat halten und ihn nicht entweihen, / die an meinem Bund fest halten," },
      { number: 7, text: "sie bringe ich zu meinem heiligen Berg / und erfülle sie in meinem Bethaus mit Freude. Ihre Brandopfer und Schlachtopfer finden Gefallen auf meinem Altar, / denn mein Haus wird ein Haus des Gebets für alle Völker genannt.", footnotes: ["Mk 11,17"] },
      { number: 8, text: "Spruch Gottes, des Herrn, / der die verstoßenen Israeliten sammelt: Noch mehr, als ich schon von ihnen gesammelt habe, / will ich dort versammeln.", footnotes: ["Ps 147,2"] },
      { number: 9, text: "Ihr Tiere auf dem Feld, kommt alle und fresst, / kommt alle, ihr Tiere im Wald!", heading: "Eine Drohung gegen die Führer des Volkes: 56,9 - 57,13", footnotes: ["Jer 12,9", "Die wilden Tiere sättigen sich an den Leichen derer, die im Strafgericht Gottes umgekommen sind."] },
      { number: 10, text: "Die Wächter des Volkes sind blind, / sie merken allesamt nichts. Es sind lauter stumme Hunde, / sie können nicht bellen. Träumend liegen sie da / und haben gern ihre Ruhe." },
      { number: 11, text: "Aber gierig sind diese Hunde, sie sind unersättlich. / So sind die Hirten: Sie verstehen nicht aufzumerken. Jeder geht seinen eigenen Weg / und ist ausschließlich auf seinen eigenen Vorteil bedacht;", footnotes: ["53,6"] },
      { number: 12, text: "(er sagt:) Kommt her, ich hole Wein. / Wir trinken uns voll mit Bier. Und wie heute, so soll es auch morgen sein; / hoch soll es hergehen." },
    ] },
    { number: 57, verses: [
      { number: 1, text: "Der Gerechte kommt um, / doch niemand nimmt es sich zu Herzen. Die Frommen werden dahingerafft, / doch es kümmert sich niemand darum. Weil das Unrecht herrscht, / wird der Gerechte dahingerafft.", footnotes: ["Mi 7,2"] },
      { number: 2, text: "Aber er gelangt zum Frieden; / und wer seinen Weg geradeaus ging, / ruht aus auf seinem Lager." },
      { number: 3, text: "Ihr aber, ihr Söhne der Zauberin, kommt herbei, / ihr Kinder eines Ehebrechers und einer Dirne!" },
      { number: 4, text: "Über wen macht ihr euch lustig, / gegen wen reißt ihr das Maul auf, / wem streckt ihr die Zunge heraus? Ihr seid doch selbst Kinder des Frevels, / eine Lügenbrut." },
      { number: 5, text: "Ihr geratet in Gier unter den Eichen, / unter jedem üppigen Baum. Ihr schlachtet Kinder in den Schluchten / und in den Klüften der Felsen.", footnotes: ["5f: Hinweis auf den Kult der Fruchtbarkeitsgötter und auf die Kinderopfer für Moloch."] },
      { number: 6, text: "Zwischen den glatten Wänden der Schlucht erfüllt sich dein Schicksal, / doch gerade sie werden dir zum Verhängnis; auch für sie hast du Trankopfer ausgegossen und Speiseopfer dargebracht. / Und das soll ich ruhig mit ansehen?" },
      { number: 7, text: "Auf hoch aufragenden Bergen / hast du dein Lager aufgeschlagen. Auch dorthin stiegst du hinauf, / um Schlachtopfer darzubringen." },
      { number: 8, text: "Hinter Türen und Pfosten / hast du dein Erinnerungszeichen angebracht. Du hast dich von mir freigemacht und bist hinaufgestiegen / und hast dir dort ein breites Lager zurechtgemacht. Dann kauftest du dir Leute, deren Beilager du liebtest; / du hast ihre Kraft bestaunt.", footnotes: ["kauftest du: Text korr. - du hast ihre Kraft bestaunt, wörtlich: «du hast die Hand geschaut», verhüllende Aussage für das männliche Glied."] },
      { number: 9, text: "Für den Moloch hast du dein Öl verschwendet / und deine Salben aufgehäuft. Du hast deine Boten in die Ferne geschickt, / bis tief hinab in die Unterwelt.", footnotes: ["Moloch: entstellt aus Melech (= König), Beiname Baals."] },
      { number: 10, text: "Auf dem langen Weg bist du müde geworden, / aber du hast nie gesagt: Es ist umsonst! Immer wieder hast du neue Kraft gefunden, / darum bist du nicht schwach geworden.", footnotes: ["10c: Wörtlich: hast du das Leben deiner Hand gefunden. Sinn unklar, vgl. die Anmerkung zu V. 8."] },
      { number: 11, text: "Wen hast du denn so sehr gescheut und gefürchtet, / dass du mich betrogen hast? An mich hast du nicht gedacht, / um mich hast du dich nicht gekümmert. Nicht wahr, weil ich schwieg und mich verbarg, / hast du mich nicht gefürchtet?", footnotes: ["11e: mich verbarg: Text korr. nach G."] },
      { number: 12, text: "Ich will verraten, wie es um deine Gerechtigkeit und um dein Tun bestellt ist: / Sie werden dir nichts mehr nützen." },
      { number: 13, text: "Wenn du um Hilfe schreist, / dann sollen doch deine vielen Götzen dich retten; aber sie alle trägt der Wind davon, / ein Hauch bläst sie weg. Doch wer mir vertraut, / wird das Land zum Erbe bekommen / und meinen heiligen Berg besitzen.", footnotes: ["60,21; 65,9", "13b: deine vielen Götzen: H unklar."] },
      { number: 14, text: "Bahnt eine Straße, ebnet den Weg, / entfernt die Hindernisse auf dem Weg meines Volkes!", heading: "Eine Verheißung für die Frommen: 57,14-21", footnotes: ["40,3; 62,10"] },
      { number: 15, text: "Denn so spricht der Hohe und Erhabene, / der ewig Thronende, dessen Name «Der Heilige» ist: Als Heiliger wohne ich in der Höhe, / aber ich bin auch bei den Zerschlagenen und Bedrückten, um den Geist der Bedrückten wieder aufleben zu lassen / und das Herz der Zerschlagenen neu zu beleben.", footnotes: ["Ps 113,5-9; Jes 66,1f; Ps 51,19"] },
      { number: 16, text: "Denn ich klage nicht für immer an, / noch will ich für immer zürnen. Sonst müsste ihr Geist vor mir vergehen / und ihr Atem, den ich erschuf.", footnotes: ["Ps 103,9; Jer 3,12"] },
      { number: 17, text: "Kurze Zeit zürnte ich wegen der Sünde (des Volkes), / ich schlug es und verbarg mich voll Zorn. Treulos ging es seine eigenen Wege. /" },
      { number: 18, text: "Ich sah, welchen Weg es ging. Aber ich will es heilen und führen und wiederum trösten, /" },
      { number: 19, text: "seinen Trauernden schaffe ich Lob auf den Lippen. Friede, Friede den Fernen und den Nahen, spricht der Herr, / ich werde sie heilen.", footnotes: ["Eph 2,17", "19a: Lob auf den Lippen, wörtlich: Frucht der Lippen."] },
      { number: 20, text: "Aber die Ruchlosen sind wie das aufgewühlte Meer, / das nie zur Ruhe kommen kann / und dessen Wasser Schmutz aufwühlt und Schlamm.", footnotes: ["Jer 49,23"] },
      { number: 21, text: "Die Ruchlosen finden keinen Frieden, / spricht mein Gott.", footnotes: ["48,22"] },
    ] },
    { number: 58, verses: [
      { number: 1, text: "Rufe aus voller Kehle, halte dich nicht zurück! / Lass deine Stimme ertönen wie eine Posaune! Halt meinem Volk seine Vergehen vor / und dem Haus Jakob seine Sünden!", heading: "Die Vorbereitung auf das kommende Heil: 58,1 - 59,21 | Die wahre Frömmigkeit: 58,1-14", footnotes: ["Mi 3,8"] },
      { number: 2, text: "Sie suchen mich Tag für Tag; / denn sie wollen meine Wege erkennen. Wie ein Volk, das Gerechtigkeit übt / und das vom Recht seines Gottes nicht ablässt, so fordern sie von mir ein gerechtes Urteil / und möchten, dass Gott ihnen nah ist." },
      { number: 3, text: "Warum fasten wir und du siehst es nicht? / Warum tun wir Buße und du merkst es nicht? Seht, an euren Fasttagen macht ihr Geschäfte / und treibt alle eure Arbeiter zur Arbeit an." },
      { number: 4, text: "Obwohl ihr fastet, gibt es Streit und Zank / und ihr schlagt zu mit roher Gewalt. So wie ihr jetzt fastet, / verschafft ihr eurer Stimme droben kein Gehör.", footnotes: ["Ex 21,18"] },
      { number: 5, text: "Ist das ein Fasten, wie ich es liebe, / ein Tag, an dem man sich der Buße unterzieht: wenn man den Kopf hängen lässt, so wie eine Binse sich neigt, / wenn man sich mit Sack und Asche bedeckt? Nennst du das ein Fasten / und einen Tag, der dem Herrn gefällt?" },
      { number: 6, text: "Nein, das ist ein Fasten, wie ich es liebe: / die Fesseln des Unrechts zu lösen, / die Stricke des Jochs zu entfernen, die Versklavten freizulassen, / jedes Joch zu zerbrechen,", footnotes: ["61,1"] },
      { number: 7, text: "an die Hungrigen dein Brot auszuteilen, / die obdachlosen Armen ins Haus aufzunehmen, wenn du einen Nackten siehst, ihn zu bekleiden / und dich deinen Verwandten nicht zu entziehen.", footnotes: ["Mt 25,35f"] },
      { number: 8, text: "Dann wird dein Licht hervorbrechen wie die Morgenröte / und deine Wunden werden schnell vernarben. Deine Gerechtigkeit geht dir voran, / die Herrlichkeit des Herrn folgt dir nach.", footnotes: ["52,12"] },
      { number: 9, text: "Wenn du dann rufst, / wird der Herr dir Antwort geben, und wenn du um Hilfe schreist, wird er sagen: / Hier bin ich. Wenn du der Unterdrückung bei dir ein Ende machst, / auf keinen mit dem Finger zeigst und niemand verleumdest,", footnotes: ["30,19; 65,24"] },
      { number: 10, text: "dem Hungrigen dein Brot reichst / und den Darbenden satt machst, dann geht im Dunkel dein Licht auf / und deine Finsternis wird hell wie der Mittag.", footnotes: ["10a: dein Brot: Text korr. nach H-Handschriften und den alten Übersetzungen."] },
      { number: 11, text: "Der Herr wird dich immer führen, / auch im dürren Land macht er dich satt / und stärkt deine Glieder. Du gleichst einem bewässerten Garten, / einer Quelle, deren Wasser niemals versiegt.", footnotes: ["Jer 31,12"] },
      { number: 12, text: "Deine Leute bauen die uralten Trümmerstätten wieder auf, / die Grundmauern aus der Zeit vergangener Generationen stellst du wieder her. Man nennt dich den Maurer, / der die Risse ausbessert, / den, der die Ruinen wieder bewohnbar macht.", footnotes: ["61,4; Am 9,11", "12e: Ruinen: Text korr.; H: Pfade."] },
      { number: 13, text: "Wenn du am Sabbat nicht aus dem Haus gehst / und an meinem heiligen Tag keine Geschäfte machst, wenn du den Sabbat (den Tag der) Wonne nennst, / einen Ehrentag den heiligen Tag des Herrn, wenn du ihn ehrst, indem du keine Gänge machst, / keine Geschäfte betreibst und keine Verhandlungen führst,", footnotes: ["13a: nicht aus dem Haus gehst, wörtlich: deinen Fuß zurückhältst."] },
      { number: 14, text: "dann wirst du am Herrn deine Wonne haben, / dann lasse ich dich über die Höhen der Erde dahinfahren und das Erbe deines Vaters Jakob genießen. / Ja, der Mund des Herrn hat gesprochen.", footnotes: ["Ijob 22,26; Ps 37,4", "14b: Wer den Sabbat hält, wird alle Schwierigkeiten überwinden."] },
    ] },
    { number: 59, verses: [
      { number: 1, text: "Seht her, die Hand des Herrn ist nicht zu kurz, / um zu helfen, sein Ohr ist nicht schwerhörig, / sodass er nicht hört.", heading: "Die Hindernisse für das kommende Heil: 59,1-21", footnotes: ["50,2"] },
      { number: 2, text: "Nein, was zwischen euch und eurem Gott steht, / das sind eure Vergehen; eure Sünden verdecken sein Gesicht, / sodass er euch nicht hört.", footnotes: ["1,15"] },
      { number: 3, text: "Denn eure Hände sind mit Blut befleckt, / eure Finger mit Unrecht. Eure Lippen lügen, / eure Zunge flüstert (Worte voll) Bosheit." },
      { number: 4, text: "Keiner bringt gerechte Klagen vor, / keiner hält ehrlich Gericht. Man stützt sich auf Nichtigkeiten / und stellt haltlose Behauptungen auf; man geht schwanger mit Unheil / und bringt Verderben zur Welt.", footnotes: ["Ijob 15,35; Ps 7,15"] },
      { number: 5, text: "Schlangeneier brüten sie aus / und weben Spinnengewebe. Wer von ihren Eiern isst, muss sterben; / zerdrückt man eines, kriecht eine Natter heraus." },
      { number: 6, text: "Die Fäden, die sie spinnen, taugen nicht zu Gewändern, / man kann sich nicht bekleiden mit dem, was sie erzeugen. Ihre Taten sind Taten des Unheils, / Gewalttat ist in ihren Händen." },
      { number: 7, text: "Sie laufen dem Bösen nach, / schnell sind sie dabei, / unschuldiges Blut zu vergießen. Ihre Gedanken sind Gedanken des Unheils, / Scherben und Verderben sind auf ihren Straßen.", footnotes: ["Spr 1,16; Röm 3,15-17"] },
      { number: 8, text: "Den Weg des Friedens kennen sie nicht, / auf ihren Spuren gibt es kein Recht. Sie gehen krumme Pfade; / keiner, der ihnen folgt, lernt den Frieden kennen." },
      { number: 9, text: "Darum bleibt das Recht von uns fern, / die Gerechtigkeit erreicht uns nicht. Wir hoffen auf Licht, / doch es bleibt finster; wir hoffen auf den Anbruch des Tages, / doch wir gehen im Dunkeln." },
      { number: 10, text: "Wir tasten uns wie Blinde an der Wand entlang / und tappen dahin, als hätten wir keine Augen. Wir stolpern am Mittag, als wäre schon Dämmerung, / wir leben im Finstern wie die Toten.", footnotes: ["Dtn 28,29", "im Finstern: in H ein Wort unbekannter Bedeutung."] },
      { number: 11, text: "Wir brummen alle wie Bären / und gurren wie Tauben. Wir hoffen auf unser Recht, doch es kommt nicht, / und auf die Rettung, doch sie bleibt uns fern.", footnotes: ["11ab: Hier ist an Äußerungen der Trauer gedacht."] },
      { number: 12, text: "Denn unsere Frevel gegen dich sind zahlreich, / unsere Sünden klagen uns an. Wir sind uns unserer Vergehen bewusst, / wir kennen unsere Schuld:", footnotes: ["Jer 14,7"] },
      { number: 13, text: "Untreue und Verleugnung des Herrn, / Abkehr von unserem Gott. Wir reden von Gewalttat und Aufruhr, / wir haben Lügen im Herzen und sprechen sie aus." },
      { number: 14, text: "So weicht das Recht zurück, / die Gerechtigkeit bleibt in der Ferne. Die Redlichkeit kommt auf dem Marktplatz zu Fall, / die Rechtschaffenheit findet nirgendwo Einlass." },
      { number: 15, text: "Jede Redlichkeit wird vermisst, / wer das Böse meidet, wird ausgeraubt. Das hat der Herr gesehen / und ihm missfiel, dass es kein Recht mehr gab." },
      { number: 16, text: "Er sah, dass keiner sich regte, / und war entsetzt, dass niemand einschritt. Da half ihm sein eigener Arm, / seine eigene Gerechtigkeit war seine Stütze.", footnotes: ["(16-17) 63,5; Weish 5,16-20"] },
      { number: 17, text: "Er legte die Gerechtigkeit an wie einen Panzer / und setzte den Helm der Hilfe auf. Er machte die Rache zu seinem Gewand / und umhüllte sich mit leidenschaftlichem Eifer wie mit einem Mantel." },
      { number: 18, text: "Wie es die Taten verdienen, so übt er Vergeltung; / er zürnt seinen Gegnern und vergilt seinen Feinden; / bis hin zu den Inseln übt er Vergeltung.", footnotes: ["66,6"] },
      { number: 19, text: "Dann fürchtet man im Westen den Namen des Herrn / und im Osten seine Herrlichkeit. Ja, er kommt wie ein reißender Strom, / den der Sturm des Herrn vor sich hertreibt.", footnotes: ["45,6; Ps 102,16"] },
      { number: 20, text: "Doch für Zion kommt er als Erlöser / und für alle in Jakob, die umkehren von ihrer Sünde - / Spruch des Herrn.", footnotes: ["Röm 11,26f"] },
      { number: 21, text: "Das ist der Bund, den ich mit ihnen schließe, spricht der Herr: Mein Geist, der auf dir ruht, soll nicht von dir weichen und meine Worte, die ich dir in den Mund gelegt habe, sollen immer in deinem Mund bleiben und im Mund deiner Kinder und im Mund deiner Enkel, jetzt und in Ewigkeit - spricht der Herr.", footnotes: ["51,16; Jer 1,9"] },
    ] },
    { number: 60, verses: [
      { number: 1, text: "Auf, werde licht denn es kommt dein Licht / und die Herrlichkeit des Herrn geht leuchtend auf über dir.", heading: "Die künftige Herrlichkeit Zions: 60,1 - 62,12 | Die Wallfahrt der Völker zum gesegneten Jerusalem: 60,1-22", footnotes: ["Offb 21,23", "1-62,12: Diese Kapitel gehören nach Thema und Gedankenführung enger zusammen und sind darin mit Jes 40 - 55 verwandt."] },
      { number: 2, text: "Denn siehe, Finsternis bedeckt die Erde / und Dunkel die Völker, doch über dir geht leuchtend der Herr auf, / seine Herrlichkeit erscheint über dir." },
      { number: 3, text: "Völker wandern zu deinem Licht / und Könige zu deinem strahlenden Glanz.", footnotes: ["2,2-5; Offb 21,24"] },
      { number: 4, text: "Blick auf und schau umher: / Sie alle versammeln sich und kommen zu dir. Deine Söhne kommen von fern, / deine Töchter trägt man auf den Armen herbei.", footnotes: ["49,18; 66,12"] },
      { number: 5, text: "Du wirst es sehen und du wirst strahlen, / dein Herz bebt vor Freude und öffnet sich weit. Denn der Reichtum des Meeres strömt dir zu, / die Schätze der Völker kommen zu dir.", footnotes: ["Offb 21,24"] },
      { number: 6, text: "Zahllose Kamele bedecken dein Land, / Dromedare aus Midian und Efa. Alle kommen von Saba, / bringen Weihrauch und Gold / und verkünden die ruhmreichen Taten des Herrn.", footnotes: ["Ps 72,10; Mt 2,11"] },
      { number: 7, text: "Alle Schafe von Kedar scharen sich bei dir, / die Widder von Nebajot stehen in deinem Dienst. Als willkommene Opfer steigen sie auf meinen Altar; / so verherrliche ich mein herrliches Haus." },
      { number: 8, text: "Wer sind die, die heranfliegen wie Wolken, / wie Tauben zu ihrem Schlag?" },
      { number: 9, text: "Die Schiffe kommen bei mir zusammen, / voran die Schiffe von Tarschisch, um deine Söhne mit ihrem Gold und Silber / aus der Ferne zu bringen, zum Ruhm des Herrn, deines Gottes, / des Heiligen Israels, / weil er dich herrlich gemacht hat.", footnotes: ["55,5", "Die Schiffe kommen . . . zusammen: Text korr.; H: Auf mich harren die Inseln."] },
      { number: 10, text: "Fremde bauen deine Mauern, / ihre Könige stehen in deinem Dienst. Denn in meinem Zorn habe ich dich geschlagen, / aber in meinem Wohlwollen zeige ich dir mein Erbarmen.", footnotes: ["61,5; 54,8"] },
      { number: 11, text: "Deine Tore bleiben immer geöffnet, / sie werden bei Tag und bei Nacht nicht geschlossen, damit man den Reichtum der Völker zu dir hineintragen kann; / auch ihre Könige führt man herbei.", footnotes: ["Offb 21,25f"] },
      { number: 12, text: "Denn jedes Volk und jedes Reich, / das dir nicht dient, geht zugrunde, / die Völker werden völlig vernichtet." },
      { number: 13, text: "Die Pracht des Libanon kommt zu dir, / Zypressen, Platanen und Eschen zugleich, um meinen heiligen Ort zu schmücken; / dann ehre ich den Platz, wo meine Füße ruhen.", footnotes: ["41,19"] },
      { number: 14, text: "Gebückt kommen die Söhne deiner Unterdrücker zu dir, / alle, die dich verachtet haben, werfen sich dir zu Füßen. Man nennt dich «Die Stadt des Herrn» / und «Das Zion des Heiligen Israels».", footnotes: ["Offb 3,9"] },
      { number: 15, text: "Dafür, dass du verlassen warst und verhasst / und niemand dich besucht hat, mache ich dich zum ewigen Stolz, / zur Freude für alle Generationen.", footnotes: ["62,4"] },
      { number: 16, text: "Du wirst die Milch der Völker saugen / und an der Brust von Königen trinken. Dann wirst du erkennen, / dass ich, der Herr, dein Retter bin / und ich, der Starke Jakobs, dein Erlöser.", footnotes: ["49,23; 66,11; 49,26"] },
      { number: 17, text: "Statt Kupfer bringe ich Gold, / statt Eisen bringe ich Silber, / statt Holz Kupfer und statt Steine Eisen. Ich setze den Frieden als Aufsicht über dich ein / und die Gerechtigkeit als deinen Vogt." },
      { number: 18, text: "Man hört nichts mehr von Unrecht in deinem Land, / von Verheerung und Zerstörung in deinem Gebiet. Deine Mauern nennst du «Rettung» / und deine Tore «Ruhm».", footnotes: ["Jer 6,7"] },
      { number: 19, text: "Bei Tag wird nicht mehr die Sonne dein Licht sein, / und um die Nacht zu erhellen, scheint dir nicht mehr der Mond, sondern der Herr ist dein ewiges Licht, / dein Gott dein strahlender Glanz.", footnotes: ["19b: die Nacht: eingefügt mit G und dem Qumran-Text."] },
      { number: 20, text: "Deine Sonne geht nicht mehr unter / und dein Mond nimmt nicht mehr ab; denn der Herr ist dein ewiges Licht, / zu Ende sind deine Tage der Trauer." },
      { number: 21, text: "Dein Volk besteht nur aus Gerechten; / sie werden für immer das Land besitzen / als aufblühende Pflanzung des Herrn, als das Werk seiner Hände, / durch das er seine Herrlichkeit zeigt.", footnotes: ["57,13; 65,9; Mt 5,5; Jes 61,3", "21d: seiner Hände: Text korr. nach G und dem Qumran-Text; H: meiner Hände."] },
      { number: 22, text: "Der Kleinste wird zu einer Tausendschaft, / der Geringste zu einem starken Volk. Ich, der Herr, führe es schnell herbei, / sobald es Zeit dafür ist." },
    ] },
    { number: 61, verses: [
      { number: 1, text: "Der Geist Gottes, des Herrn, ruht auf mir; / denn der Herr hat mich gesalbt. Er hat mich gesandt, damit ich den Armen eine frohe Botschaft bringe / und alle heile, deren Herz zerbrochen ist, damit ich den Gefangenen die Entlassung verkünde / und den Gefesselten die Befreiung,", heading: "Die frohe Botschaft des Gesalbten Jahwes: 61,1-11", footnotes: ["Lk 4,18f; Jes 58,6"] },
      { number: 2, text: "damit ich ein Gnadenjahr des Herrn ausrufe, / einen Tag der Vergeltung unseres Gottes, / damit ich alle Trauernden tröste,", footnotes: ["34,8; 63,4"] },
      { number: 3, text: "die Trauernden Zions erfreue, / ihnen Schmuck bringe anstelle von Schmutz, Freudenöl statt Trauergewand, / Jubel statt der Verzweiflung. Man wird sie «Die Eichen der Gerechtigkeit» nennen, / «Die Pflanzung, durch die der Herr seine Herrlichkeit zeigt».", footnotes: ["60,21", "erfreue: Text korr. - Schmuck anstelle von Schmutz, wörtlich: «einen Turban anstelle von Staub». Staub streut man sich bei Trauer auf den Kopf. In H ein Wortspiel."] },
      { number: 4, text: "Dann bauen sie die uralten Trümmerstätten wieder auf / und richten die Ruinen ihrer Vorfahren wieder her. Die verödeten Städte erbauen sie neu, / die Ruinen vergangener Generationen.", footnotes: ["58,12"] },
      { number: 5, text: "Fremde stehen bereit und führen eure Herden auf die Weide, / Ausländer sind eure Bauern und Winzer.", footnotes: ["60,10"] },
      { number: 6, text: "Ihr alle aber werdet «Priester des Herrn» genannt, / man sagt zu euch «Diener unseres Gottes». Was die Völker besitzen, werdet ihr genießen, / mit ihrem Reichtum könnt ihr euch brüsten.", footnotes: ["Ex 19,6; 1 Petr 2,5.9; Offb 1,6; 20,6"] },
      { number: 7, text: "Doppelte Schande mussten sie ertragen, / sie wurden angespuckt und verhöhnt; darum erhalten sie doppelten Besitz in ihrem Land, / ewige Freude wird ihnen zuteil.", footnotes: ["Schande: Text korr.; H: eure Schande. - sie wurden angespuckt: Text korr.; H: sie jubeln."] },
      { number: 8, text: "Denn ich, der Herr, liebe das Recht, / ich hasse Verbrechen und Raub. Ich bin treu und gebe ihnen den Lohn, / ich schließe mit ihnen einen ewigen Bund.", footnotes: ["Ps 37,28; Jes 55,3"] },
      { number: 9, text: "Ihre Nachkommen werden bei allen Nationen bekannt sein / und ihre Kinder in allen Völkern. Jeder, der sie sieht, wird erkennen: / Das sind die Nachkommen, die der Herr gesegnet hat.", footnotes: ["65,23"] },
      { number: 10, text: "Von Herzen will ich mich freuen über den Herrn. / Meine Seele soll jubeln über meinen Gott. Denn er kleidet mich in Gewänder des Heils, / er hüllt mich in den Mantel der Gerechtigkeit, wie ein Bräutigam sich festlich schmückt / und wie eine Braut ihr Geschmeide anlegt.", footnotes: ["41,16; Offb 21,2", "10e: sich festlich schmückt: Text korr.; H: Priesterdienst tut."] },
      { number: 11, text: "Denn wie die Erde die Saat wachsen lässt / und der Garten die Pflanzen hervorbringt, so bringt Gott, der Herr, Gerechtigkeit hervor / und Ruhm vor allen Völkern." },
    ] },
    { number: 62, verses: [
      { number: 1, text: "Um Zions willen kann ich nicht schweigen, / um Jerusalems willen nicht still sein, bis das Recht in ihm aufstrahlt wie ein helles Licht / und sein Heil aufleuchtet wie eine brennende Fackel.", heading: "Die verherrlichte Stadt Gottes: 62,1-12" },
      { number: 2, text: "Dann sehen die Völker deine Gerechtigkeit / und alle Könige deine strahlende Pracht. Man ruft dich mit einem neuen Namen, / den der Mund des Herrn für dich bestimmt.", footnotes: ["Offb 2,17; 3,12"] },
      { number: 3, text: "Du wirst zu einer prächtigen Krone / in der Hand des Herrn, zu einem königlichen Diadem / in der Rechten deines Gottes." },
      { number: 4, text: "Nicht länger nennt man dich «Die Verlassene» / und dein Land nicht mehr «Das Ödland», sondern man nennt dich «Meine Wonne» / und dein Land «Die Vermählte». Denn der Herr hat an dir seine Freude / und dein Land wird mit ihm vermählt.", footnotes: ["54,1; 60,15"] },
      { number: 5, text: "Wie der junge Mann sich mit der Jungfrau vermählt, / so vermählt sich mit dir dein Erbauer. Wie der Bräutigam sich freut über die Braut, / so freut sich dein Gott über dich.", footnotes: ["65,19", "5b: dein Erbauer: Text korr.; H: deine Söhne."] },
      { number: 6, text: "Auf deine Mauern, Jerusalem, stellte ich Wächter. / Weder bei Tag noch bei Nacht dürfen sie schweigen. Ihr, die ihr den Herrn (an Zion) erinnern sollt, / gönnt euch keine Ruhe!" },
      { number: 7, text: "Lasst auch ihm keine Ruhe, / bis er Jerusalem wieder aufbaut, / bis er es auf der ganzen Erde berühmt macht." },
      { number: 8, text: "Der Herr hat geschworen bei seiner rechten Hand / und bei seinem starken Arm: Nie mehr gebe ich dein Korn / deinen Feinden zu essen. Nie mehr trinken Fremde deinen Wein, / für den du so hart gearbeitet hast.", footnotes: ["(8-9) 65,21f"] },
      { number: 9, text: "Nein, wer das Korn geerntet hat, soll es auch essen / und den Herrn dafür preisen. Wer den Wein geerntet hat, soll ihn auch trinken / in den Vorhöfen meines Heiligtums." },
      { number: 10, text: "Zieht durch die Tore ein und aus / und bahnt dem Volk einen Weg! Baut, ja baut eine Straße / und räumt die Steine beiseite! / Stellt ein Zeichen auf für die Völker!", footnotes: ["40,3; 57,14; 49,22"] },
      { number: 11, text: "Hört, was der Herr bis ans Ende der Erde bekannt macht: / Sagt der Tochter Zion: Sieh her, jetzt kommt deine Rettung. / Siehe, er bringt seinen Siegespreis mit: Alle, die er gewonnen hat, / gehen vor ihm her.", footnotes: ["Mt 21,5; Jes 40,10"] },
      { number: 12, text: "Dann nennt man sie «Das heilige Volk», / «Die Erlösten des Herrn». Und dich nennt man / «Die begehrte, die nicht mehr verlassene Stadt»." },
    ] },
    { number: 63, verses: [
      { number: 1, text: "Wer ist jener, der aus Edom kommt, / aus Bozra in rot gefärbten Gewändern? Er schreitet in prächtigen Kleidern daher / in seiner gewaltigen Kraft. Ich bin es, ich verkünde Gerechtigkeit, / ich bin der mächtige Helfer.", heading: "Völkergericht und endzeitliches Heil: 63,1 - 66,24 | Die Ankündigung des Gerichts an den Völkern: 63,1-6", footnotes: ["Er schreitet: Text korr. nach Symmachus und Vg. - Gott wird als siegreicher Krieger geschildert."] },
      { number: 2, text: "Warum aber ist dein Gewand so rot, / ist dein Kleid wie das eines Mannes, der die Kelter tritt?" },
      { number: 3, text: "Ich allein trat die Kelter; / von den Völkern war niemand dabei. Da zertrat ich sie voll Zorn, / zerstampfte sie in meinem Grimm. Ihr Blut spritzte auf mein Gewand / und befleckte meine Kleider.", footnotes: ["Offb 14,20; 19,15.13"] },
      { number: 4, text: "Denn ein Tag der Rache lag mir im Sinn / und das Jahr der Erlösung war gekommen.", footnotes: ["34,8; 61,2"] },
      { number: 5, text: "Ich sah mich um, doch niemand wollte mir helfen; / ich war bestürzt, weil keiner mir beistand. Da half mir mein eigener Arm, / mein Zorn war meine Stütze.", footnotes: ["59,16"] },
      { number: 6, text: "Ich zertrat die Völker in meinem Zorn, / ich zerschmetterte sie in meinem Grimm / und ihr Blut ließ ich zur Erde rinnen.", footnotes: ["ich zerschmetterte sie: Text korr. nach vielen H-Handschriften (vgl. 14,25); H: ich machte sie trunken."] },
      { number: 7, text: "Die Huld des Herrn will ich preisen, / die ruhmreichen Taten des Herrn, alles, was der Herr für uns tat, / seine große Güte, die er dem Haus Israel erwies / in seiner Barmherzigkeit und seiner großen Huld.", heading: "Das Gebet des Volkes um Gottes Erscheinen: 63,7 - 64,11", footnotes: ["Ps 89,2"] },
      { number: 8, text: "Er sagte: Sie sind doch mein Volk, / meine Söhne, die nicht enttäuschen. / Er wurde ihr Retter in jeder Not." },
      { number: 9, text: "Nicht ein Bote oder ein Engel, / sondern sein Angesicht hat sie gerettet. In seiner Liebe und seinem Mitleid / hat er selbst sie erlöst. Er hat sie emporgehoben und sie getragen / in all den Tagen der Vorzeit.", footnotes: ["46,3f"] },
      { number: 10, text: "Sie aber lehnten sich gegen ihn auf / und betrübten seinen heiligen Geist. Da wandelte er sich und wurde ihr Feind, / ja, er führte Krieg gegen sie." },
      { number: 11, text: "Nun dachten sie an die Tage der Vorzeit, / die Zeit seines Knechtes Mose: Wo ist der, der den Hirten seiner Schafe aus dem Meer herausgeführt hat? / Wo ist der, der seinen heiligen Geist in ihn gelegt hat,", footnotes: ["Hebr 13,20", "11-14: Es wird an den Auszug aus Ägypten erinnert. 11b: seines Knechtes: Text korr.; H: seines Volkes."] },
      { number: 12, text: "der an der rechten Seite des Mose ging / und ihm half mit mächtigem Arm, der das Wasser vor ihnen zerteilte, / um sich ewigen Ruhm zu verschaffen," },
      { number: 13, text: "der sie durch die Fluten führte wie Pferde durch die Steppe, / ohne dass sie strauchelten?", footnotes: ["Ps 106,9"] },
      { number: 14, text: "Der Geist des Herrn ließ sie zur Ruhe kommen, / wie das Vieh, das ins Tal hinabzieht. So führtest du einst dein Volk, / um dir herrlichen Ruhm zu verschaffen." },
      { number: 15, text: "Blick vom Himmel herab und sieh her / von deiner heiligen, herrlichen Wohnung! Wo ist dein leidenschaftlicher Eifer / und deine Macht, dein großes Mitleid und dein Erbarmen? / Halte dich nicht von uns fern!", footnotes: ["Dtn 26,15; Jes 64,11", "15e: Halte . . . fern: Text korr.; vgl. G und 64,11."] },
      { number: 16, text: "Du bist doch unser Vater; / denn Abraham weiß nichts von uns, Israel will uns nicht kennen. / Du, Herr, bist unser Vater, / «Unser Erlöser von jeher» wirst du genannt.", footnotes: ["Israels Stammväter können dem Volk nicht helfen."] },
      { number: 17, text: "Warum lässt du uns, Herr, von deinen Wegen abirren / und machst unser Herz hart, / sodass wir dich nicht mehr fürchten? Kehre zurück um deiner Knechte willen, / um der Stämme willen, die dein Eigentum sind." },
      { number: 18, text: "Erst vor kurzem haben unsere Feinde dein heiliges Volk vertrieben; / dein Heiligtum haben sie zertreten.", footnotes: ["Offb 11,2"] },
      { number: 19, text: "Uns geht es, als wärest du nie unser Herrscher gewesen, / als wären wir nicht nach deinem Namen benannt. Reiß doch den Himmel auf und komm herab, / sodass die Berge zittern vor dir." },
    ] },
    { number: 64, verses: [
      { number: 1, text: "Komm wie ein Feuer, das Reisig entzündet, / wie ein Feuer, das Wasser zum Sieden bringt. Mach deinen Feinden deinen Namen bekannt, / sodass die Völker zittern vor dir," },
      { number: 2, text: "wenn du schreckliche und nie erwartete Taten vollbringst. / [Komm herab, sodass die Berge zittern vor dir.]", footnotes: ["Der Zusatz ist eine irrtümliche Wiederholung aus 63,19."] },
      { number: 3, text: "Seit Menschengedenken hat man noch nie vernommen, / kein Ohr hat gehört, kein Auge gesehen, dass es einen Gott gibt außer dir, / der denen Gutes tut, die auf ihn hoffen.", footnotes: ["2 Sam 7,22; 1 Kor 2,9", "kein Ohr hat gehört: Text korr.; H: sie haben nicht gehört."] },
      { number: 4, text: "Ach, kämst du doch denen entgegen, / die tun, was recht ist, / und nachdenken über deine Wege. Ja, du warst zornig; / denn wir haben gegen dich gesündigt, / von Urzeit an sind wir treulos geworden.", footnotes: ["H unklar, Übersetzung im Anschluss an G."] },
      { number: 5, text: "Wie unreine (Menschen) sind wir alle geworden, / unsere ganze Gerechtigkeit ist wie ein schmutziges Kleid. Wie Laub sind wir alle verwelkt, / unsere Schuld trägt uns fort wie der Wind." },
      { number: 6, text: "Niemand ruft deinen Namen an, / keiner rafft sich dazu auf, fest zu halten an dir. Denn du hast dein Angesicht vor uns verborgen / und hast uns der Gewalt unserer Schuld überlassen." },
      { number: 7, text: "Und doch bist du, Herr, unser Vater. / Wir sind der Ton und du bist unser Töpfer, / wir alle sind das Werk deiner Hände." },
      { number: 8, text: "Herr, zürne uns doch nicht allzu sehr, / denk nicht für immer an unsere Schuld! / Sieh doch her: Wir alle sind dein Volk." },
      { number: 9, text: "Deine heiligen Städte sind zur Wüste geworden. / Zion ist eine Wüste, Jerusalem eine Öde." },
      { number: 10, text: "Unser heiliger, herrlicher Tempel, / wo unsere Väter dich priesen, ist ein Raub der Flammen geworden; / alles, was uns lieb war, liegt nun in Trümmern." },
      { number: 11, text: "Kannst du dich bei all dem zurückhalten, Herr, / kannst du schweigen und uns so sehr erniedrigen?", footnotes: ["63,15"] },
    ] },
    { number: 65, verses: [
      { number: 1, text: "Ich wäre zu erreichen gewesen für die, / die nicht nach mir fragten, ich wäre zu finden gewesen für die, / die nicht nach mir suchten. Ich sagte zu einem Volk, / das meinen Namen nicht anrief: / Hier bin ich, hier bin ich.", heading: "Gegen die Götzendiener: 65,1-16d", footnotes: ["(1-2) Röm 10,20f", "nach mir: eingefügt mit dem Qumran-Text und den alten Übersetzungen."] },
      { number: 2, text: "Den ganzen Tag streckte ich meine Hände aus / nach einem abtrünnigen Volk, das einen Weg ging, der nicht gut war, / nach seinen eigenen Plänen," },
      { number: 3, text: "nach einem Volk, / das in seinem Trotz mich ständig ärgert. Sie bringen Schlachtopfer dar in Gärten / und Rauchopfer auf Ziegeln;", footnotes: ["3-5: Aufzählung heidnischer Kultbräuche."] },
      { number: 4, text: "sie sitzen in Grabkammern / und verbringen die Nächte in Höhlen; sie essen das Fleisch von Schweinen / und haben Brühe von verdorbenem Fleisch in ihren Töpfen;" },
      { number: 5, text: "sie sagen: Bleib, wo du bist, komm mir nicht nahe, / sonst bist du geweiht. Diese Menschen sind wie Rauch in meiner Nase, / wie ein immer brennendes Feuer.", footnotes: ["5b: Wörtlich: sonst mache ich dich heilig. - Die kultische Heiligkeit (vgl. Ez 44,19; 46,20), hier aus dem Kult fremder Götter, überträgt sich auf alle, die damit in Berührung kommen."] },
      { number: 6, text: "Seht her, alles ist aufgeschrieben bei mir / und ich werde nicht schweigen, / sondern zahle ihnen heim, wie sie es verdienen;", footnotes: ["wie sie es verdienen, wörtlich: in ihren Gewandbausch; so erhielt der Arbeiter seinen Lohn in Naturalien."] },
      { number: 7, text: "ich zahle ihnen den Lohn aus für ihre Schuld / und die Schuld ihrer Väter, spricht der Herr. Weil sie auf den Bergen Weihrauch verbrannten / und mich auf den Hügeln verhöhnten, messe ich ihnen ihren Lohn zu / und zahle ihnen heim, wie sie es verdienen.", footnotes: ["und zahle ihnen heim, wie sie es verdienen: Text korr. nach V. 6."] },
      { number: 8, text: "So spricht der Herr: / Sobald sich Saft in der Traube findet, / sagt man: Verdirb sie nicht, denn es ist Segen darin. Ebenso will ich um meiner Knechte willen handeln, / um nicht alle vernichten zu müssen." },
      { number: 9, text: "Ich lasse aus Jakob Nachkommen hervorgehen / und aus Juda einen Erben für meine Berge. Meine Auserwählten sollen das Land besitzen / und meine Knechte sollen dort wohnen.", footnotes: ["57,13; 60,21"] },
      { number: 10, text: "Für mein Volk, das nach mir fragt, / wird dann (die Ebene) Scharon zur Schafweide / und das Achortal zum Lagerplatz der Rinder.", footnotes: ["33,9; 1 Chr 27,29", "Die Scharon-Ebene liegt zwischen Jaffa und dem Karmel, das Achor-Tal nordwestlich von Jericho."] },
      { number: 11, text: "Euch aber, die ihr den Herrn verlassen, / meinen heiligen Berg vergessen, dem Glücksgott den Tisch gedeckt / und dem Gott des Schicksals den Weinkrug gefüllt habt," },
      { number: 12, text: "überantworte ich dem Schwert: / Ihr müsst euch alle ducken und werdet geschlachtet. Denn ihr gabt keine Antwort, als ich euch rief, / als ich zu euch redete, hörtet ihr nicht, sondern ihr habt getan, was mir missfällt, / und habt euch für das entschieden, / was ich nicht will.", footnotes: ["34,2; 66,4; Jer 7,13.27"] },
      { number: 13, text: "Darum - so spricht Gott, der Herr: / Meine Knechte sollen essen, / doch ihr leidet Hunger. Meine Knechte sollen trinken, / doch ihr leidet Durst. Meine Knechte sollen sich freuen, / doch ihr müsst euch schämen." },
      { number: 14, text: "Meine Knechte sollen aus Herzenslust jubeln, / doch ihr werdet schreien vor Herzeleid und heulen vor Verzweiflung." },
      { number: 15, text: "Ihr müsst euren Namen dazu hergeben, / dass meine Auserwählten ihn beim Eid / als Fluchwort gebrauchen und sagen: Genauso töte dich Gott, der Herr. / Meinen Knechten aber wird man einen anderen Namen geben.", footnotes: ["Meinen Knechten: Text korr., vgl. G; H: Seinen Knechten."] },
      { number: 16, text: "Wer sich segnet im Land, / wird sich Segen wünschen von Gott, dem Getreuen, und wer schwört im Land, / wird schwören bei Gott, dem Getreuen.", footnotes: ["43,18"] },
      { number: 17, text: "Ja, vergessen sind die früheren Nöte, / sie sind meinen Augen entschwunden. Denn schon erschaffe ich einen neuen Himmel / und eine neue Erde. Man wird nicht mehr an das Frühere denken, / es kommt niemand mehr in den Sinn.", heading: "Das endzeitliche Heil: 65,16e-25", footnotes: ["66,22"] },
      { number: 18, text: "Nein, ihr sollt euch ohne Ende freuen und jubeln / über das, was ich erschaffe. Denn ich mache aus Jerusalem Jubel / und aus seinen Einwohnern Freude." },
      { number: 19, text: "Ich will über Jerusalem jubeln / und mich freuen über mein Volk. Nie mehr hört man dort lautes Weinen / und lautes Klagen.", footnotes: ["62,5; Offb 21,4"] },
      { number: 20, text: "Dort gibt es keinen Säugling mehr, / der nur wenige Tage lebt, und keinen Greis, / der nicht das volle Alter erreicht; wer als Hundertjähriger stirbt, / gilt noch als jung, / und wer nicht hundert Jahre alt wird, / gilt als verflucht.", footnotes: ["Sach 8,4"] },
      { number: 21, text: "Sie werden Häuser bauen / und selbst darin wohnen, / sie werden Reben pflanzen / und selbst ihre Früchte genießen.", footnotes: ["(21-22) 62,8f; Ps 128,2"] },
      { number: 22, text: "Sie bauen nicht, / damit ein anderer in ihrem Haus wohnt, und sie pflanzen nicht, / damit ein anderer die Früchte genießt. In meinem Volk werden die Menschen so alt / wie die Bäume. Was meine Auserwählten mit eigenen Händen erarbeitet haben, / werden sie selber verbrauchen." },
      { number: 23, text: "Sie arbeiten nicht mehr vergebens, / sie bringen nicht Kinder zur Welt für einen jähen Tod. Denn sie sind die Nachkommen der vom Herrn Gesegneten / und ihre Sprösslinge zusammen mit ihnen.", footnotes: ["61,9"] },
      { number: 24, text: "Schon ehe sie rufen, gebe ich Antwort, / während sie noch reden, erhöre ich sie.", footnotes: ["58,9"] },
      { number: 25, text: "Wolf und Lamm weiden zusammen, / der Löwe frisst Stroh wie das Rind / [doch die Schlange nährt sich von Staub]. Man tut nichts Böses mehr / und begeht kein Verbrechen / auf meinem ganzen heiligen Berg, spricht der Herr.", footnotes: ["11,6f; Gen 3,14; Jes 11,9"] },
    ] },
    { number: 66, verses: [
      { number: 1, text: "So spricht der Herr: Der Himmel ist mein Thron / und die Erde der Schemel für meine Füße. Was wäre das für ein Haus, / das ihr mir bauen könntet? Was wäre das für ein Ort, / an dem ich ausruhen könnte?", heading: "Die Beschreibung der Endzeit: 66,1-24", footnotes: ["Apg 7,49f"] },
      { number: 2, text: "Denn all das hat meine Hand gemacht; / es gehört mir ja schon - Spruch des Herrn. Ich blicke auf den Armen und Zerknirschten / und auf den, der zittert vor meinem Wort." },
      { number: 3, text: "Man opfert Rinder - und erschlägt Menschen; / man opfert Schafe - und erwürgt Hunde; man bringt Speiseopfer dar - / und auch Schweineblut; man spendet Weihrauch - / und preist einen Götzen. Wie diese Menschen ihre eigenen Wege wählen / und an ihren Götterbildern Gefallen haben,", footnotes: ["Die Opfer für Jahwe stehen neben Opfern (Menschen-, Hunde- und Schweineopfern) für andere Götter."] },
      { number: 4, text: "so wähle ich für sie die Strafe aus / und bringe über sie Schrecken. Denn sie gaben keine Antwort, als ich sie rief, / als ich zu ihnen redete, hörten sie nicht; sondern sie haben getan, was mir missfällt, / und haben sich für das entschieden, was ich nicht will.", footnotes: ["65,12"] },
      { number: 5, text: "Hört das Wort des Herrn, / die ihr zittert vor seinem Wort! Eure Brüder, die euch hassen, / die euch um meines Namens willen verstoßen, sie sagen: Der Herr soll doch seine Herrlichkeit zeigen, / damit wir eure Freude miterleben. / Aber sie werden beschämt." },
      { number: 6, text: "Horcht: Getöse dringt aus der Stadt, / Getöse aus dem Tempel. / Horcht: Der Herr vergilt seinen Feinden ihr Tun.", footnotes: ["59,18"] },
      { number: 7, text: "Noch ehe die Frau ihre Wehen bekommt, / hat sie schon geboren; ehe die Wehen über sie kamen, / brachte sie einen Knaben zur Welt.", footnotes: ["Offb 12,2.5", "7-9: Zion wird die Mutter der kommenden Heilsgemeinde (VV. 10-14)."] },
      { number: 8, text: "Wer hat so etwas je gehört, / wer hat je dergleichen gesehen? Wird ein Land an einem einzigen Tag geboren, / kommt ein Volk auf einmal zur Welt? Doch Zion, kaum in den Wehen, / hat schon ihre Kinder geboren." },
      { number: 9, text: "Hätte ich ihr etwa den Schoß öffnen sollen, / ohne sie gebären zu lassen?, spricht der Herr. Sollte ich, der die Frauen gebären lässt, / ihnen den Schoß verschließen?, spricht dein Gott." },
      { number: 10, text: "Freut euch mit Jerusalem! / Jubelt in der Stadt, alle, die ihr sie liebt. Seid fröhlich mit ihr, / alle, die ihr über sie traurig wart." },
      { number: 11, text: "Saugt euch satt an ihrer tröstenden Brust, / trinkt und labt euch an ihrem mütterlichen Reichtum!", footnotes: ["60,16; 49,23"] },
      { number: 12, text: "Denn so spricht der Herr: / Seht her: Wie einen Strom / leite ich den Frieden zu ihr und den Reichtum der Völker / wie einen rauschenden Bach. Ihre Kinder wird man auf den Armen tragen / und auf den Knien schaukeln.", footnotes: ["30,28; 60,4", "Ihre Kinder: Text korr.; vgl. G."] },
      { number: 13, text: "Wie eine Mutter ihren Sohn tröstet, / so tröste ich euch; / in Jerusalem findet ihr Trost." },
      { number: 14, text: "Wenn ihr das seht, wird euer Herz sich freuen / und ihr werdet aufblühen wie frisches Gras. So offenbart sich die Hand des Herrn an seinen Knechten, / aber seine Feinde wird er bedrohen.", footnotes: ["14b: und ihr werdet aufblühen, wörtlich: und eure Gebeine werden sprossen."] },
      { number: 15, text: "Ja, seht, der Herr kommt wie das Feuer heran, / wie der Sturm sind seine Wagen, um in glühendem Zorn Vergeltung zu üben, / und er droht mit feurigen Flammen." },
      { number: 16, text: "Ja, mit Feuer und Schwert hält der Herr Gericht über alle Sterblichen / und viele sind es, die der Herr erschlägt.", footnotes: ["Jer 25,31"] },
      { number: 17, text: "Alle, die sich weihen und reinigen bei den Gärten / für den einen, der in der Mitte steht, die Schweinefleisch, Würmer und Mäuse verzehren, / sie alle nehmen ein Ende - Spruch des Herrn.", footnotes: ["65,3f; Ez 8,10f", "Vielleicht ein Götterbild in einem Garten; Text und Sinn unklar. Würmer: Text korr.; H: Gräuel."] },
      { number: 18, text: "Ich kenne ihre Taten und ihre Gedanken und komme, um die Völker aller Sprachen zusammenzurufen, und sie werden kommen und meine Herrlichkeit sehen.", footnotes: ["Ich kenne: ergänzt nach G. - und komme: Text korr. nach G."] },
      { number: 19, text: "Ich stelle bei ihnen ein Zeichen auf und schicke von ihnen einige, die entronnen sind, zu den übrigen Völkern: nach Tarschisch, Pul und Lud, Meschech und Rosch, Tubal und Jawan und zu den fernen Inseln, die noch nichts von mir gehört und meine Herrlichkeit noch nicht gesehen haben. Sie sollen meine Herrlichkeit unter den Völkern verkünden.", footnotes: ["Die Völkernamen sind zum Teil im Anschluss an G wiedergegeben. Es ist an weit entfernte Völker gedacht."] },
      { number: 20, text: "Sie werden aus allen Völkern eure Brüder als Opfergabe für den Herrn herbeiholen auf Rossen und Wagen, in Sänften, auf Maultieren und Dromedaren, her zu meinem heiligen Berg nach Jerusalem, spricht der Herr, so wie die Söhne Israels ihr Opfer in reinen Gefäßen zum Haus des Herrn bringen." },
      { number: 21, text: "Und auch aus ihnen werde ich Männer als Priester und Leviten auswählen, spricht der Herr." },
      { number: 22, text: "Wie der neue Himmel und die neue Erde, / die ich erschaffe, / vor mir stehen - Spruch des Herrn -, / so wird euer Stamm und euer Name dastehen.", footnotes: ["65,17; Offb 21,1"] },
      { number: 23, text: "An jedem Neumond und an jedem Sabbat / wird alle Welt kommen, um mir zu huldigen, / spricht der Herr.", footnotes: ["Sach 14,16"] },
      { number: 24, text: "Dann wird man hinausgehen, / um die Leichen derer zu sehen, / die sich gegen mich aufgelehnt haben. Denn der Wurm in ihnen wird nicht sterben / und das Feuer in ihnen wird niemals erlöschen; / ein Ekel sind sie für alle Welt.", footnotes: ["Jdt 16,17; Mk 9,48"] },
    ] },
  ],
};
