import { Book } from "@/lib/types";

export const jeremiah: Book = {
  id: "jeremiah",
  name: "Jeremia",
  shortName: "Jer",
  testament: "old",
  introduction: "Das Buch Jeremia hat eine klare Gliederung, die sich jedoch in der griechischen Bibel von der der hebräischen stark unterscheidet. Der hebräische Text hat folgende Anordnung: Prophetenworte gegen Juda und Jerusalem von der Zeit Joschijas bis Zidkija (1,1 - 25,14); vorwiegend Prosatexte über den Propheten und sein Wirken (Kap. 26 45); hier kann man wieder Prosareden über Israel und Juda, zumeist drohenden Inhalts (Kap. 26 - 29), Heilsworte (Kap. 32 - 35) und Berichte Baruchs über Jeremia (Kap. 36 - 45) unterscheiden; eingeschoben sind die sog. Trostschrift (Kap. 30f), Drohreden gegen fremde Völker (Kap. 46 - 51; die Einleitung dazu steht bereits in 25,15-38) und ein geschichtlicher Anhang, der weitgehend mit 2 Kön 24,18 - 25,21 übereinstimmt (Kap. 52). Der griechische Text bringt die Drohreden gegen die fremden Völker in anderer Reihenfolge nach Kap. 25 und ordnet erst danach die Prosaberichte ein, die jetzt Kap. 52 nicht mehr als Anhang erkennen lassen. Der griechische Text ist etwa um ein Achtel kürzer als der hebräische, da einzelne Verse oder Versteile, aber auch einige längere Abschnitte fehlen. Der Prophet selbst hat im Jahr 605 v. Chr. seinem Sekretär und Jünger Baruch eine erste Sammlung von »Worten über Jerusalem und Juda und alle Völker von den Tagen Joschijas an bis heute« (36,2) diktiert. Baruch hat die Buchrolle im Tempelhof vorgelesen; sie wurde beschlagnahmt und von König Jojakim verbrannt. Darauf diktierte der Prophet den Inhalt der Rolle noch einmal seinem Sekretär und dieser Niederschrift »wurden noch viele ähnliche Worte hinzugefügt« (36,32). Demnach hat Jeremia selbst eine erste Sammlung seiner Verkündigung vorgenommen, die später, teils vielleicht von ihm selbst, teils von Baruch und vielleicht von noch späteren Bearbeitern, ergänzt wurde. Als nachexilische Erweiterungen gelten heute allgemein 10,1-16; 17,19-27; 32,29-35.37-41; 33 sowie die meisten Drohreden gegen fremde Völker (Kap. 46 - 51) und der Anhang (Kap. 52). Ob die sogenannte Trostschrift (Kap. 30f) von Jeremia selbst stammt, ist umstritten; sie dürfte aber einen echten Kern haben. Jeremia ist der Prophet, über dessen Leben und Gotteserfahrung wir am besten unterrichtet sind. Das verdanken wir seinen von leidenschaftlichem Mitgefühl mit seinem Volk geprägten Klagen, seinen »Konfessionen« (Bekenntnissen: 11,18-23; 12,1-6; 15,10-21; 16,1-9; 17,12-18; 18,18-23; 20,7-18; 23,9-19) und den Aufzeichnungen Baruchs. Jeremia stammt aus einer Priesterfamilie in Anatot unweit von Jerusalem. Seine Berufung erfährt er als junger Mann um 628 v. Chr. unter König Joschija (641-609). Zunächst wendet er sich gegen die aus der Zeit des ruchlosen Königs Manasse noch nachwirkenden religiösen und sittlichen Missstände. Als Joschija sich um 622 v. Chr. von dem zerbrechenden Assyrerreich unabhängig macht und eine tief greifende Reform des Jahweglaubens durchführt, schweigt Jeremia. Joschija fällt 609 v. Chr. in der Schlacht gegen die Ägypter bei Megiddo. Als unter seinem Nachfolger Jojakim (609-597) die Reform teilweise rückgängig gemacht wird und wieder heidnische Sitten eindringen, kämpft der Prophet leidenschaftlich dagegen an; er überwirft sich mit dem König und wird von ihm verfolgt. In tiefer Enttäuschung über den Misserfolg seiner Verkündigung, über die Verfolgung durch Jojakim und über die Nachstellungen durch seine eigenen Verwandten und Landsleute ist er der Verzweiflung nahe und wird an Gott fast irre. Aus dieser qualvollen Not entstehen die »Konfessionen«. Von der Verschleppung Jojachins und eines Teils der Einwohner Jerusalems durch Nebukadnezzar 597 v. Chr. bleibt er verschont. In Briefen warnt und tröstet er die nach Babylonien verschleppten Landsleute und versucht Zidkija (597-586) vergeblich von einer antibabylonischen Politik abzuhalten. Während der Belagerung Jerusalems 587/86 v. Chr. wird der Prophet des Verrates verdächtigt, verhaftet und im Wachhof des Königspalastes gefangen gehalten. Selbst dort schweigt er nicht. Nach dem Fall der Stadt 586 v. Chr. wird er von der Verschleppung ausgenommen. Nun versucht er sein Volk zu trösten. Als aber gegen seinen Rat die Zurückgebliebenen nach der Ermordung des Statthalters Gedalja (vgl. 40,7 - 41,15) nach Ägypten fliehen, zwingt man ihn und Baruch mitzugehen. In Ägypten stirbt der Prophet. Seine Botschaft aber wirkt weiter, nicht zuletzt durch die Verheißung des »neuen Bundes« (31,31-34).",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Die Worte Jeremias, des Sohnes Hilkijas, aus der Priesterschaft zu Anatot im Land Benjamin.", heading: "Die Gerichtsworte über Jerusalem und Juda: 1,1 - 25,14 | Überschrift: 1,1-3", footnotes: ["1-3: Joschija regiert 641-609 v. Chr., die Berufung Jeremias erfolgt somit etwa 628. Jojakim regiert 609-597 v. Chr.; dessen Sohn Jojachin wird 597 v. Chr. von Nebukadnezzar gefangen genommen. Zidkija regiert 597-586 v. Chr. Der «fünfte Monat» (V. 3) ist der Juli/August des Jahres 586."] },
      { number: 2, text: "An ihn erging das Wort des Herrn zur Zeit des Königs Joschija von Juda, des Sohnes Amons, im dreizehnten Jahr seiner Regierung," },
      { number: 3, text: "ebenso zur Zeit des Königs Jojakim von Juda, des Sohnes Joschijas, bis das elfte Jahr des Königs Zidkija von Juda, des Sohnes Joschijas, zu Ende ging, als im fünften Monat Jerusalem in die Verbannung ziehen musste." },
      { number: 4, text: "Das Wort des Herrn erging an mich:", heading: "Die Berufung Jeremias zum Propheten: 1,4-10" },
      { number: 5, text: "Noch ehe ich dich im Mutterleib formte, habe ich dich ausersehen, noch ehe du aus dem Mutterschoß hervorkamst, habe ich dich geheiligt, zum Propheten für die Völker habe ich dich bestimmt.", footnotes: ["Jes 49,1; Gal 1,15"] },
      { number: 6, text: "Da sagte ich: Ach, mein Gott und Herr, ich kann doch nicht reden, ich bin ja noch so jung." },
      { number: 7, text: "Aber der Herr erwiderte mir: Sag nicht: Ich bin noch so jung. Wohin ich dich auch sende, dahin sollst du gehen, und was ich dir auftrage, das sollst du verkünden." },
      { number: 8, text: "Fürchte dich nicht vor ihnen; denn ich bin mit dir, um dich zu retten - Spruch des Herrn.", footnotes: ["1,19; 30,11"] },
      { number: 9, text: "Dann streckte der Herr seine Hand aus, berührte meinen Mund und sagte zu mir: Hiermit lege ich meine Worte in deinen Mund." },
      { number: 10, text: "Sieh her! Am heutigen Tag setze ich dich über Völker und Reiche; du sollst ausreißen und niederreißen, vernichten und einreißen, aufbauen und einpflanzen.", footnotes: ["Der Prophet soll, je nach dem Verhalten des Volkes gegenüber seinem Gott, Unheil oder Heil ansagen und seine Worte werden so in Erfüllung gehen, als ob sie das Angekündigte selbst bewirkt hätten."] },
      { number: 11, text: "Das Wort des Herrn erging an mich: Was siehst du, Jeremia? Ich antwortete: Einen Mandelzweig sehe ich.", heading: "Zwei Visionen: 1,11-19" },
      { number: 12, text: "Da sprach der Herr zu mir: Du hast richtig gesehen; denn ich wache über mein Wort und führe es aus.", footnotes: ["Wortspiel mit den hebräischen Ausdrücken für Mandelbaum (schaked) und wachen (schakad)."] },
      { number: 13, text: "Abermals erging an mich das Wort des Herrn: Was siehst du? Ich antwortete: Einen dampfenden Kessel sehe ich; sein Rand neigt sich von Norden her." },
      { number: 14, text: "Da sprach der Herr zu mir: Von Norden her ergießt sich das Unheil über alle Bewohner des Landes.", footnotes: ["4,6; 6,1.22; 10,22; 50,41"] },
      { number: 15, text: "Ja, ich rufe alle Stämme der Nordreiche - Spruch des Herrn -, damit sie kommen und ihre Richterstühle an den Toreingängen Jerusalems aufstellen, gegen all seine Mauern ringsum und gegen alle Städte von Juda." },
      { number: 16, text: "Dann werde ich mein Urteil über sie sprechen und sie strafen für alles Böse, das sie getan haben, weil sie mich verlassen, anderen Göttern geopfert und das Werk ihrer eigenen Hände angebetet haben." },
      { number: 17, text: "Du aber gürte dich, tritt vor sie hin und verkünde ihnen alles, was ich dir auftrage. Erschrick nicht vor ihnen, sonst setze ich dich vor ihren Augen in Schrecken." },
      { number: 18, text: "Ich selbst mache dich heute zur befestigten Stadt, zur eisernen Säule und zur ehernen Mauer gegen das ganze Land, gegen die Könige, Beamten und Priester von Juda und gegen die Bürger des Landes.", footnotes: ["15,20"] },
      { number: 19, text: "Mögen sie dich bekämpfen, sie werden dich nicht bezwingen; denn ich bin mit dir, um dich zu retten - Spruch des Herrn." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Das treulose Volk: 2,1-37" },
      { number: 2, text: "Auf! Ruf Jerusalem laut ins Ohr: So spricht der Herr: Ich denke an deine Jugendtreue, an die Liebe deiner Brautzeit, wie du mir in der Wüste gefolgt bist, im Land ohne Aussaat.", footnotes: ["Hos 2,17"] },
      { number: 3, text: "Heiliger Besitz war Israel dem Herrn, Erstlingsfrucht seiner Ernte. Wer davon aß, machte sich schuldig, Unheil kam über ihn - Spruch des Herrn." },
      { number: 4, text: "Hört das Wort des Herrn, ihr vom Haus Jakob und all ihr Geschlechter des Hauses Israel!" },
      { number: 5, text: "So spricht der Herr: Was fanden eure Väter Unrechtes an mir, dass sie sich von mir entfernten, nichtigen Göttern nachliefen und so selber zunichte wurden?" },
      { number: 6, text: "Sie fragten nicht: Wo ist der Herr, der uns aus Ägypten heraufgeführt, der uns in der Wüste den Weg gewiesen hat, im Land der Steppen und Schluchten, im dürren und düsteren Land, im Land, das keiner durchwandert und niemand bewohnt?" },
      { number: 7, text: "Ich brachte euch dann in das Gartenland, um euch seine Früchte und Güter genießen zu lassen. Aber kaum seid ihr dort gewesen, da habt ihr mein Land entweiht und mir mein Eigentum zum Abscheu gemacht." },
      { number: 8, text: "Die Priester fragten nicht: Wo ist der Herr? Die Hüter des Gesetzes kannten mich nicht, die Hirten des Volkes wurden mir untreu. Die Propheten traten im Dienst des Baal auf und liefen unnützen Götzen nach." },
      { number: 9, text: "Darum muss ich euch weiter anklagen - Spruch des Herrn - und gegen eure Kindeskinder Klage erheben." },
      { number: 10, text: "Geht doch hinüber zu den Inseln der Kittäer und seht euch um oder schickt nach Kedar, forscht genau nach und seht zu, ob irgendwo etwas Ähnliches geschah.", footnotes: ["Die Kittäer sind die Einwohner von Zypern, die «Inseln der Kittäer» die Inseln im Jonischen Meer. Kedar ist eine Oase in der arabischen Wüste."] },
      { number: 11, text: "Hat je ein Volk seine Götter gewechselt? Dabei sind es gar keine Götter. Mein Volk aber hat seinen Ruhm gegen unnütze Götzen vertauscht." },
      { number: 12, text: "Entsetzt euch darüber, ihr Himmel, erschaudert gewaltig - Spruch des Herrn." },
      { number: 13, text: "Denn mein Volk hat doppeltes Unrecht verübt: Mich hat es verlassen, den Quell des lebendigen Wassers, um sich Zisternen zu graben, Zisternen mit Rissen, die das Wasser nicht halten.", footnotes: ["17,13"] },
      { number: 14, text: "Ist Israel denn ein Knecht oder ein im Haus geborener Sklave? Warum wurde es zur Beute," },
      { number: 15, text: "über der Löwen brüllten und knurrten? Man hat sein Land zur Wüste gemacht; seine Städte sind verbrannt und menschenleer.", footnotes: ["4,7"] },
      { number: 16, text: "Sogar die Leute von Memfis und Tachpanhes zertrümmern dir den Schädel." },
      { number: 17, text: "Geschieht das nicht deshalb, weil du den Herrn, deinen Gott, verlassen hast?" },
      { number: 18, text: "Was nützt es dir jetzt, nach Ägypten zu laufen, um Nilwasser zu trinken, oder nach Assur zu laufen, um Eufratwasser zu trinken?" },
      { number: 19, text: "Dein böses Tun straft dich, deine Abtrünnigkeit klagt dich an. So erkenne doch und sieh ein, wie schlimm und bitter es ist, den Herrn, deinen Gott, zu verlassen und keine Furcht vor mir zu haben - Spruch Gottes, des Herrn der Heere." },
      { number: 20, text: "Von jeher hast du dein Joch zerbrochen, deine Stricke zerrissen und gesagt: Ich will nicht dienen. Auf jedem hohen Hügel und unter jedem üppigen Baum hast du dich als Dirne hingestreckt.", footnotes: ["Auf Hügeln und in Hainen fanden die Kultfeiern für die kanaanäischen Fruchtbarkeitsgötter statt; sie waren mit kultischer Unzucht verbunden."] },
      { number: 21, text: "Ich aber hatte dich als Edelrebe gepflanzt, als gutes, edles Gewächs. Wie hast du dich gewandelt zum Wildling, zum entarteten Weinstock!", footnotes: ["Jes 5,1-4"] },
      { number: 22, text: "Selbst wenn du dich mit Lauge waschen und noch so viel Seife verwenden wolltest, deine Schuld bliebe doch ein Schmutzfleck vor meinen Augen - Spruch Gottes, des Herrn." },
      { number: 23, text: "Wie kannst du sagen: Ich bin nicht unrein geworden, den Baalen bin ich nicht nachgelaufen? Schau auf dein Treiben im Tal, erkenne, was du verübt hast. Eine schnelle Kamelstute bist du, die kreuz und quer ihre Wege rennt," },
      { number: 24, text: "eine wilde Eselin, die an die Wüste gewöhnt ist. In ihrer Brunst schnappt sie nach Luft. Wer vermag ihre Gier zu hemmen? Jeder, der sie begehrt, findet sie ohne Mühe zur Zeit ihrer Brunst." },
      { number: 25, text: "Gib Acht, dass du dir den Fuß nicht wund läufst und dass deine Kehle nicht durstig wird. Du aber sagst: Nein, lass mich! Denn ich bin verliebt in die Fremden und will ihnen nachlaufen." },
      { number: 26, text: "Wie ein ertappter Dieb sich schämt, so müssen sich die Leute vom Haus Israel schämen, sie selbst, ihre Könige und Beamten, ihre Priester und Propheten." },
      { number: 27, text: "Sie sagen ja zum Holz: «Du bist mein Vater», und zum Stein: «Du hast mich geboren». Sie kehren mir den Rücken zu und nicht das Gesicht; sind sie aber in Not, dann rufen sie: Erheb dich und hilf uns!", footnotes: ["7,24; 32,33", "Holz und Stein sind die Materialien, aus denen die Götterbilder gefertigt wurden."] },
      { number: 28, text: "Wo sind nun deine Götter, die du dir gemacht hast? Sie mögen sich erheben, falls sie dir helfen können, wenn du in Not bist. Denn so zahlreich wie deine Städte, Juda, sind auch deine Götter.", footnotes: ["Dtn 32,37f; Ps 3,8; Jer 11,12f"] },
      { number: 29, text: "Warum streitet ihr gegen mich? Ihr alle seid mir untreu geworden Spruch des Herrn." },
      { number: 30, text: "Vergeblich schlug ich eure Söhne; sie ließen sich nicht erziehen. Euer Schwert fraß eure Propheten wie ein reißender Löwe." },
      { number: 31, text: "Ihr nun, das gegenwärtige Geschlecht, schaut auf das Wort des Herrn! Bin ich denn für Israel eine Wüste geworden oder ein finsteres Land? Warum sagt mein Volk: Wir wollen frei umherschweifen, wir kommen nicht mehr zu dir?" },
      { number: 32, text: "Vergisst denn ein Mädchen seinen Schmuck, eine Braut ihre Bänder? Mein Volk aber hat mich vergessen seit ungezählten Tagen." },
      { number: 33, text: "Wie gut findest du deinen Weg, wenn du Liebe suchst. Sogar an Verbrechen hast du dein Verhalten gewöhnt." },
      { number: 34, text: "Selbst am Saum deiner Kleider klebt das Blut von Armen, von Unschuldigen, die du nicht etwa beim Einbruch ertappt hast." },
      { number: 35, text: "Und trotzdem sagst du: Ich bin unschuldig; sein Zorn hat sich ja von mir abgewandt. - Aber ich gehe ins Gericht mit dir, weil du sagst: Ich habe mich nicht versündigt." },
      { number: 36, text: "Wie kannst du nur so leicht bereit sein, deinen Weg zu wechseln! Auch von Ägypten wirst du enttäuscht, wie du von Assur enttäuscht worden bist." },
      { number: 37, text: "Auch von dort wirst du wegziehen, die Hände über dem Kopf. Denn der Herr verwirft alle, denen du vertraust; du hast mit ihnen kein Glück." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Wenn ein Mann seine Frau entlässt und wenn sie von ihm weggeht und die Frau eines andern wird, wendet er sich dann ihr wieder zu? Würde das Land nicht völlig entweiht? Du aber hast mit vielen Freunden gebuhlt und da solltest du zu mir zurückkehren dürfen? - Spruch des Herrn.", heading: "Der Bundesbruch als Ehebruch: 3,1-13", footnotes: ["Dtn 24,1-4"] },
      { number: 2, text: "Blick hin und schau zu den Höhen hinauf! Wo hast du dich nicht schänden lassen? An allen Wegen hast du auf sie gewartet wie ein Araber in der Wüste. Mit deiner Unzucht und Verkommenheit hast du das Land entweiht.", footnotes: ["Hos 4,13"] },
      { number: 3, text: "Da blieb der Regen aus und auch der Frühjahrsregen kam nicht. Doch du hattest die freche Stirn einer Dirne und wolltest dich nicht schämen." },
      { number: 4, text: "Gewiss, von da an hast du mir zugerufen: Mein Vater, der Freund meiner Jugend bist du." },
      { number: 5, text: "Wird er denn ewig zürnen oder immerfort nachtragen? Ja, so sagtest du und tatest Böses, so viel du konntest." },
      { number: 6, text: "Der Herr sprach zu mir zur Zeit des Königs Joschija: Hast du gesehen, was Israel, die Abtrünnige, getan hat? Sie begab sich auf jeden hohen Berg und unter jeden üppigen Baum und trieb dort Unzucht.", footnotes: ["Ez 23", "6-8: Die Bevölkerung des Reiches Juda hätte am Schicksal des Bruderreiches Israel, das 722 v. Chr. unterging, sehen können, wohin die Untreue gegen Gott führt; sie hat aber daraus keine Lehre gezogen."] },
      { number: 7, text: "Ich dachte: Nachdem sie dies alles getan hat, wird sie schließlich zu mir zurückkehren; aber sie kehrte nicht zurück. Das sah ihre Schwester Juda, die Treulose." },
      { number: 8, text: "Auch sah sie, dass ich Israel, die Abtrünnige, wegen ihres Ehebruchs entließ und ihr die Scheidungsurkunde gab. Aber das schreckte ihre Schwester Juda, die Treulose, nicht ab; sie ging hin und trieb ebenfalls Unzucht.", footnotes: ["Dtn 24,1-3"] },
      { number: 9, text: "Durch ihre leichtfertige Unzucht hat sie das Land entweiht und mit Stein und Holz Ehebruch getrieben." },
      { number: 10, text: "Bei all dem ist auch ihre Schwester Juda, die Treulose, nicht mit ganzem Herzen zu mir zurückgekehrt, sondern nur zum Schein - Spruch des Herrn." },
      { number: 11, text: "Auch sagte der Herr zu mir: Israel, die Abtrünnige, trifft weniger Schuld als Juda, die Treulose." },
      { number: 12, text: "Geh hin, ruf diese Worte gegen Norden und sprich: Kehr zurück, Israel, du Abtrünnige - Spruch des Herrn! Ich schaue dich nicht mehr zornig an; denn ich bin gütig - Spruch des Herrn -, ich trage nicht ewig nach." },
      { number: 13, text: "Doch erkenne deine Schuld: Dem Herrn, deinem Gott, hast du die Treue gebrochen, überallhin bist du zu den fremden Göttern gelaufen [unter jeden üppigen Baum], auf meine Stimme aber hast du nicht gehört Spruch des Herrn." },
      { number: 14, text: "Kehrt um, ihr abtrünnigen Söhne - Spruch des Herrn; denn ich bin euer Gebieter. Ich hole euch, einen aus jeder Stadt und zwei aus jeder Sippe, und bringe euch nach Zion.", heading: "Die bessere Zukunft: 3,14-18", footnotes: ["14-18: Ein Nachtrag aus der Zeit des Exils. Die Bundeslade wurde vielleicht 586 v. Chr. bei der Zerstörung des Tempels vernichtet."] },
      { number: 15, text: "Ich gebe euch Hirten nach meinem Herzen; mit Einsicht und Klugheit werden sie euch weiden." },
      { number: 16, text: "In jenen Tagen, wenn ihr euch im Land vermehrt und fruchtbar seid Spruch des Herrn -, wird man nicht mehr rufen: Die Bundeslade des Herrn! Sie wird niemand in den Sinn kommen; man denkt nicht mehr an sie, vermisst sie nicht und stellt auch keine neue her." },
      { number: 17, text: "In jener Zeit wird man Jerusalem «Thron des Herrn» nennen; dort, beim Namen des Herrn in Jerusalem, werden sich alle Völker versammeln und sie werden nicht mehr dem Trieb ihres bösen Herzens folgen." },
      { number: 18, text: "In jenen Tagen wird das Haus Juda zum Haus Israel gehen und sie werden vereint aus dem Nordland in das Land kommen, das ich euren Vätern zum Erbe gegeben habe." },
      { number: 19, text: "Ich hatte gedacht: / Ja, ich will dich unter die Söhne aufnehmen und dir ein liebliches Land geben, / das herrlichste Erbteil unter den Völkern. Ich dachte, du würdest mich Vater nennen / und dich nicht abwenden von mir.", heading: "Die Umkehr durch Buße: 3,19 - 4,4" },
      { number: 20, text: "Doch wie eine Frau ihres Freundes wegen treulos wird, / so seid auch ihr mir treulos geworden, / ihr vom Haus Israel - Spruch des Herrn." },
      { number: 21, text: "Horch, man hört auf den Höhen / das Weinen und Flehen der Söhne Israels, weil sie krumme Wege gegangen sind / und den Herrn, ihren Gott, vergessen haben." },
      { number: 22, text: "Kehrt um, ihr abtrünnigen Söhne, / ich will eure Abtrünnigkeit heilen. «Da sind wir, wir kommen zu dir; / denn du bist der Herr, unser Gott!" },
      { number: 23, text: "Fürwahr, Trug sind die Höhen, / der Lärm auf den Bergen. Fürwahr, beim Herrn, unserm Gott, / ist Israels Rettung." },
      { number: 24, text: "Doch der Baal fraß seit unsrer Jugend alles, / was unsere Väter erwarben, / ihre Schafe und Rinder, ihre Söhne und Töchter." },
      { number: 25, text: "Wir betten uns in unsere Schmach / und unsere Schande bedeckt uns. Denn wir haben gesündigt gegen den Herrn, unsern Gott, / wir selbst und unsere Väter, / von Jugend an bis auf den heutigen Tag. / Wir haben nicht gehört auf die Stimme des Herrn, unseres Gottes.»" },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Wenn du umkehren willst, Israel - Spruch des Herrn -, / darfst du zu mir zurückkehren; wenn du deine Gräuel entfernst, / brauchst du vor mir nicht zu fliehen." },
      { number: 2, text: "Schwörst du aufrichtig: So wahr der Herr lebt!, / nach Recht und Gerechtigkeit, dann werden sich Völker mit ihm segnen / und seiner sich rühmen.", footnotes: ["5,2; 12,16", "«mit ihm» und «seiner» bezieht sich auf Gott. Sein Ruhm dringt zu den Völkern, sodass sie sich in seinem Namen segnen. Viele ändern aber zu «mit dir» und «deiner» (vgl. Gen 12,3; 18,18; 22,18; 26,4)."] },
      { number: 3, text: "Denn so spricht der Herr / zu den Leuten von Juda und zu Jerusalem: Nehmt Neuland unter den Pflug / und sät nicht in die Dornen!", footnotes: ["Hos 10,12"] },
      { number: 4, text: "Beschneidet euch für den Herrn / und entfernt die Vorhaut eures Herzens, / ihr Leute von Juda und ihr Einwohner Jerusalems! Sonst bricht mein Zorn wie Feuer los / wegen eurer bösen Taten; / er brennt und niemand kann löschen.", footnotes: ["Dtn 10,16; Jer 21,12"] },
      { number: 5, text: "Meldet es in Juda, / verkündet es in Jerusalem, stoßt überall im Land in die Trompete, / ruft aus voller Kehle und sagt: Sammelt euch! / Hinein in die befestigten Städte!", heading: "Der Krieg im Land: 4,5-31", footnotes: ["8,14"] },
      { number: 6, text: "Stellt Wegzeichen auf: Nach Zion! / Flüchtet, bleibt nicht stehen! Denn Unheil bringe ich von Norden / und großes Verderben." },
      { number: 7, text: "Der Löwe hat sich aus dem Dickicht erhoben, / der Völkerwürger ist aufgebrochen; er hat sein Land verlassen, um dein Land zur Wüste zu machen. / Deine Städte werden zerstört und entvölkert.", footnotes: ["Der «Löwe» und «Völkerwürger» ist Nebukadnezzar, der Herrscher des Neubabylonischen Reiches, auch Chaldäerreich genannt (605-562 v. Chr.)."] },
      { number: 8, text: "Darum legt Trauerkleider an, / klagt und heult: Nein, der glühende Zorn des Herrn / hat sich nicht von uns abgewandt." },
      { number: 9, text: "An jenem Tag wird es geschehen - Spruch des Herrn: / Vergehen wird der Mut des Königs / und der Mut der Machthaber. Die Priester werden starr sein vor Schrecken, / die Propheten werden sich entsetzen." },
      { number: 10, text: "Sie sagen: Ach, Gebieter und Herr, / wahrhaftig, schwer hast du getäuscht dieses Volk und Jerusalem. Du sagtest: Heil werdet ihr finden!, / und nun geht uns das Schwert an die Kehle." },
      { number: 11, text: "In jener Zeit wird man von diesem Volk / und von Jerusalem sagen: Ein Glutwind von den Höhen in der Wüste / ist losgebrochen gegen die Tochter meines Volkes; / kein Wind zum Worfeln und Reinigen;", footnotes: ["Zum Worfeln vgl. die Anmerkung zu Rut 3,2-4."] },
      { number: 12, text: "ein Wind, der viel heftiger ist, kommt auf meinen Befehl. / Jetzt spreche ich selbst das Urteil über sie." },
      { number: 13, text: "Seht, wie Wettergewölk zieht er herauf, / seine Wagen gleichen dem Sturm, seine Rosse sind schneller als Adler. / Weh uns, wir sind verloren!" },
      { number: 14, text: "Wasche dein Herz vom Bösen rein, Jerusalem, / damit du gerettet wirst. Wie lange noch wohnen in dir / deine frevelhaften Gedanken?" },
      { number: 15, text: "Horcht nur, man meldet aus Dan, / aus Efraims Bergland kündet man Unheil:" },
      { number: 16, text: "Berichtet: Die Völker sind da! / Gebt Kunde an Jerusalem: Belagerer kommen aus fernem Land, / sie erheben gegen Judas Städte ihr Kriegsgeschrei." },
      { number: 17, text: "Wie Feldwächter haben sie Juda umstellt; / denn mir hat es getrotzt Spruch des Herrn." },
      { number: 18, text: "Dein Verhalten und Tun haben dir das eingebracht. / Deine bösen Taten sind schuld, dass es so bitter steht, / dass es dich bis ins Herz trifft." },
      { number: 19, text: "O mein Leib, mein Leib! / Ich winde mich vor Schmerz. O meines Herzens Wände! / Mein Herz tobt in mir; ich kann nicht schweigen. / Denn ich höre Trompetenschall und Kriegslärm;" },
      { number: 20, text: "«Schlag auf Schlag» schreit man, / das ganze Land wird verwüstet. Plötzlich sind meine Zelte vernichtet, / im Nu sind meine Zeltdecken dahin." },
      { number: 21, text: "Wie lange noch muss ich die Kriegsfahne sehen, / Trompetenschall hören?" },
      { number: 22, text: "Ach, töricht ist mein Volk; / mich kennen sie nicht. Sie sind unverständige Kinder, / ja, sie sind ohne Einsicht. Sie wissen, wie man Böses tut, / aber Gutes zu tun verstehen sie nicht." },
      { number: 23, text: "Ich schaute die Erde an: Sie war wüst und wirr. / Ich schaute zum Himmel: Er war ohne sein Licht.", footnotes: ["Gen 1,2"] },
      { number: 24, text: "Ich schaute die Berge an: Sie wankten / und alle Hügel bebten." },
      { number: 25, text: "Ich schaute hin: Kein Mensch war da, / auch alle Vögel des Himmels waren verschwunden." },
      { number: 26, text: "Ich schaute hin: Das Gartenland war Wüste / und all seine Städte waren zerstört, zerstört durch den Herrn, / durch seinen glühenden Zorn." },
      { number: 27, text: "Ja, so spricht der Herr: / Das ganze Land soll zur Öde werden; / doch völlig vernichten will ich es nicht.", footnotes: ["5,10.18", "Die Einschränkung, dass nicht ein völliges Ende mit Juda gemacht wird, wirkt in der harten Drohrede störend, ebenso 5,10. Da aber 5,18 deutlich an beide Stellen anknüpft, sind Textänderungen nicht am Platz. Alle drei Stellen als späteren Einschub zu betrachten ist eine Notlösung. Jeremia glaubt trotz der bitteren Heimsuchungen an die Rettung eines Restes des Volkes; vgl. 3,14-18; 4,1f und besonders den doppelt überlieferten Spruch 30,11 und 46,28."] },
      { number: 28, text: "Mag darüber die Erde vertrocknen / und der Himmel droben sich verfinstern: Fürwahr, ich habe gesprochen / und es reut mich nicht; ich habe meinen Plan gefasst / und nehme ihn nicht zurück.", footnotes: ["28-30: Der Text von G scheint in Einzelheiten besser zu sein."] },
      { number: 29, text: "Vor dem Lärm der Pferde und Bogenschützen / fliehen alle Bewohner des Landes; sie kriechen in Höhlen, / verstecken sich im Dickicht / und klettern die Felsen hinauf. Verlassen steht jede Stadt, / niemand wohnt mehr darin." },
      { number: 30, text: "Du aber, was tust du? / Wie kannst du in Purpur dich kleiden, mit Goldschmuck dich zieren, / dir mit Schminke die Augen weiten? Umsonst machst du dich schön. / Die Liebhaber verschmähen dich; / sie trachten dir nach dem Leben." },
      { number: 31, text: "Ja, ich höre Geschrei wie von einer Frau in Wehen, / Stöhnen wie von einer Erstgebärenden, / das Schreien der Tochter Zion, die nach Atem ringt und die Hände ausstreckt: / Weh mir, unter Mörderhand endet mein Leben!" },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Zieht durch Jerusalems Straßen, / schaut genau hin und forscht nach, sucht auf seinen Plätzen, ob ihr einen findet, / ob einer da ist, der Recht übt und auf Treue bedacht ist: / Dann will ich der Stadt verzeihen - Spruch des Herrn.", heading: "Die schwere Schuld: 5,1-31" },
      { number: 2, text: "Doch selbst wenn sie sagen: / «So wahr der Herr lebt», / schwören sie gewiss einen Meineid.", footnotes: ["4,2"] },
      { number: 3, text: "Herr, sind deine Augen nicht auf Treue gerichtet? / Du hast sie geschlagen, / aber es tut ihnen nicht weh; du hast sie beinahe vernichtet, / aber sie wollen sich nicht erziehen lassen. Ihre Stirn ist härter als Stein, / sie weigern sich umzukehren." },
      { number: 4, text: "Ich aber dachte: Nur die geringen Leute, / nur sie handeln töricht, weil sie den Weg des Herrn nicht kennen, / das Recht ihres Gottes.", footnotes: ["«Weg» und «Recht» meinen die sittlichen Forderungen."] },
      { number: 5, text: "Ich will doch lieber zu den Großen gehen / und zu ihnen reden; denn sie kennen den Weg des Herrn, / das Recht ihres Gottes. Doch auch sie haben das Joch zerbrochen, / die Stricke zerrissen.", footnotes: ["2,20"] },
      { number: 6, text: "Darum schlägt sie der Löwe des Waldes, / der Steppenwolf überwältigt sie. Vor ihren Städten lauert der Panther, / alle, die herauskommen, werden zerfleischt. Denn zahlreich sind ihre Verbrechen, / schwer wiegt ihre Abtrünnigkeit." },
      { number: 7, text: "Weshalb sollte ich dir vergeben? / Deine Söhne haben mich verlassen / und bei Nichtgöttern geschworen. Ich machte sie satt, doch sie trieben Ehebruch / und waren zu Gast im Dirnenhaus." },
      { number: 8, text: "Hengste sind sie geworden, feist und geil, / jeder wiehert nach der Frau seines Nächsten.", footnotes: ["Ez 22,11"] },
      { number: 9, text: "Sollte ich das nicht bestrafen - Spruch des Herrn - / und an einem solchen Volk keine Rache nehmen?", footnotes: ["5,29; 9,8"] },
      { number: 10, text: "Steigt auf ihre Rebenhänge, und verwüstet sie! / Doch völlig vernichten sollt ihr sie nicht. Reißt ihre Reben weg; / denn sie gehören nicht dem Herrn.", footnotes: ["5,18; 4,27", "10b.18: Vgl. die Anmerkung zu 4,27."] },
      { number: 11, text: "Sie sind mir ja gänzlich untreu geworden, / das Haus Israel und das Haus Juda - Spruch des Herrn." },
      { number: 12, text: "Sie haben den Herrn verleugnet und gesagt: / Er ist ein Nichts! Kein Unheil kommt über uns, / weder Schwert noch Hunger werden wir spüren." },
      { number: 13, text: "Doch diese Propheten werden zunichte; / das Gotteswort ist nicht bei ihnen. / [So wird es ihnen ergehen.]" },
      { number: 14, text: "Darum - so spricht der Herr, / der Gott der Heere: Weil man solche Reden führt, / seht, darum mache ich meine Worte / in deinem Mund zu Feuersglut und dieses Volk da zum Brennholz, / das von ihr verzehrt wird." },
      { number: 15, text: "Seht, ich lasse über euch herfallen, Haus Israel, / ein Volk aus der Ferne - Spruch des Herrn. Ein unüberwindliches Volk ist es, / ein uraltes Volk, ein Volk, dessen Sprache du nicht kennst / und dessen Rede du nicht verstehst.", footnotes: ["6,22"] },
      { number: 16, text: "Sein Köcher ist wie ein offenes Grab, / sie alle sind Helden." },
      { number: 17, text: "Es frisst deine Ernte und dein Brot, / es frisst deine Söhne und Töchter, es frisst deine Schafe und Rinder, / es frisst deinen Weinstock und Feigenbaum, es zerschlägt mit dem Schwert / deine befestigten Städte, auf die du vertraust." },
      { number: 18, text: "Doch auch in jenen Tagen - Spruch des Herrn - will ich euch nicht völlig vernichten." },
      { number: 19, text: "Wenn man dann fragt: Weshalb hat der Herr, unser Gott, uns das alles angetan?, so sag zu ihnen: Wie ihr mich verlassen und fremden Göttern in eurem Land gedient habt, so müsst ihr Fremden dienen in einem Land, das euch nicht gehört.", footnotes: ["16,10-13"] },
      { number: 20, text: "Verkündet dies im Haus Jakob / und ruft es in Juda aus:" },
      { number: 21, text: "Hör das, du törichtes Volk ohne Verstand: / Augen haben sie und sehen nicht; / Ohren haben sie und hören nicht.", footnotes: ["Jes 6,9f"] },
      { number: 22, text: "Fürchtet ihr mich denn nicht - Spruch des Herrn -, / zittert ihr nicht vor meinem Angesicht? Ich bin es, der dem Meer die Düne als Grenze gesetzt hat, / als ewige Schranke, die es nicht überschreiten darf. Mag es auch toben, es richtet nichts aus; / mögen seine Wogen auch tosen, / sie können die Schranke nicht überschreiten." },
      { number: 23, text: "Dieses Volk aber hat ein störrisches, trotziges Herz. / Sie wichen vom Weg ab und gingen davon." },
      { number: 24, text: "Sie sagten nicht bei sich selbst: / Lasst uns den Herrn fürchten, unseren Gott, der Regen spendet im Herbst / und im Frühjahr zur rechten Zeit, / der uns die feste Ordnung der Erntewochen bewahrt." },
      { number: 25, text: "Eure Frevel haben diese Ordnung gestört, / eure Sünden haben euch den Regen vorenthalten." },
      { number: 26, text: "Ja, Frevler gibt es in meinem Volk; / sie lauern, gebückt wie Vogelsteller, Fallen stellen sie auf, / Menschen wollen sie fangen." },
      { number: 27, text: "Wie ein Korb mit Vögeln gefüllt ist, / so sind ihre Häuser voll Betrug; dadurch sind sie mächtig und reich geworden, /" },
      { number: 28, text: "fett und feist. Auch sündigen sie durch ruchloses Tun. / Das Recht pflegen sie nicht, das Recht der Waisen, die Erfolg erwarten, / und die Sache der Armen entscheiden sie nicht.", footnotes: ["Dtn 32,15"] },
      { number: 29, text: "Sollte ich das nicht bestrafen - Spruch des Herrn - / und an einem solchen Volk keine Rache nehmen?", footnotes: ["5,9"] },
      { number: 30, text: "Wüstes, Grässliches geschieht im Land: /" },
      { number: 31, text: "Die Propheten weissagen Lüge und die Priester richten ihre Lehre nach ihnen aus; / mein Volk aber liebt es so. Doch was werdet ihr tun, / wenn es damit zu Ende geht?" },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Flieht, ihr Leute von Benjamin, / hinaus aus Jerusalem! Stoßt in Tekoa in die Trompete / und richtet über Bet-Kerem ein Zeichen auf! Denn von Norden droht Unheil / und großes Verderben.", heading: "Die Kriegsgefahr als Strafe: 6,1-15", footnotes: ["4,6"] },
      { number: 2, text: "Die Schöne und Verwöhnte, / die Tochter Zion, ich vernichte sie." },
      { number: 3, text: "Hirten kommen zu ihr mit ihren Herden; / sie schlagen rings um sie ihre Zelte auf, / jeder weidet seinen Bereich ab.", footnotes: ["«Hirten» und «Herden» sind die mit Nebukadnezzar verbündeten Könige und deren Heere."] },
      { number: 4, text: "Ruft gegen Zion den Heiligen Krieg aus! / Auf! Greifen wir an am Mittag! - Weh uns: Schon neigt sich der Tag, / die Abendschatten strecken sich." },
      { number: 5, text: "Auf! Greifen wir an in der Nacht, / zerstören wir ihre Paläste!" },
      { number: 6, text: "Denn so spricht der Herr der Heere: Fällt ihre Bäume / und werft einen Wall auf gegen Jerusalem! Das ist die Stadt, von der erwiesen ist: / Alles in ihr ist Unterdrückung." },
      { number: 7, text: "Wie ein Brunnen sein Wasser sprudeln lässt, / so lässt sie ihre Schlechtigkeit sprudeln. Von Gewalttat und Unrecht hört man in ihr; / ständig sind mir vor Augen Leid und Misshandlung." },
      { number: 8, text: "Lass dich warnen, Jerusalem, / sonst trenne ich mich von dir, sonst mache ich dich zur Wüste, / zum Land ohne Bewohner." },
      { number: 9, text: "So spricht der Herr der Heere: Genaue Nachlese wie am Weinstock / soll man halten am Rest Israels. Leg deine Hand an / wie ein Winzer an die Reben!" },
      { number: 10, text: "Zu wem soll ich reden / und wer wird mich hören, wenn ich mahne? Ihr Ohr ist ja unbeschnitten, / sie können nichts vernehmen. Das Wort des Herrn dient ihnen zum Spott; / es gefällt ihnen nicht." },
      { number: 11, text: "Darum bin ich erfüllt vom Zorn des Herrn, / bin es müde, ihn länger zurückzuhalten. - Gieß ihn aus über das Kind auf der Straße / und zugleich über die Schar der jungen Männer! Ja, alle werden gefangen genommen, / Mann und Frau, Greis und Hochbetagter." },
      { number: 12, text: "Ihre Häuser gehen an andere über, / die Felder und auch die Frauen. Denn ich strecke meine Hand aus / gegen die Bewohner des Landes - Spruch des Herrn." },
      { number: 13, text: "Sie sind doch alle, vom Kleinsten bis zum Größten, / nur auf Gewinn aus; vom Propheten bis zum Priester / betrügen sie alle.", footnotes: ["8,10-12"] },
      { number: 14, text: "Den Schaden meines Volkes möchten sie leichthin heilen, indem sie rufen: / Heil, Heil! Aber kein Heil ist da." },
      { number: 15, text: "Schämen müssten sie sich, / weil sie Gräuel verüben. Doch sie schämen sich nicht; / Scham ist ihnen unbekannt. Deshalb müssen sie fallen, / wenn die anderen fallen. Sobald ich sie zur Rechenschaft ziehe, / werden sie stürzen, spricht der Herr." },
      { number: 16, text: "So spricht der Herr: Stellt euch an die Wege und haltet Ausschau, / fragt nach den Pfaden der Vorzeit, fragt, wo der Weg zum Guten liegt; / geht auf ihm, so werdet ihr Ruhe finden für eure Seele. / Sie aber sagten: Wir gehen nicht.", heading: "Jerusalems Trotz: 6,16-26" },
      { number: 17, text: "Auch habe ich Wächter für euch aufgestellt: / Achtet auf den Schall der Trompete! / Sie aber sagten: Wir achten nicht darauf." },
      { number: 18, text: "Darum hört, ihr Völker, und erkennt, / was ich ihnen antun will." },
      { number: 19, text: "Höre es, Erde! / Schon bringe ich Unheil über dieses Volk / als die Frucht seiner bösen Gesinnung. Denn auf meine Worte haben sie nicht geachtet / und meine Weisung haben sie verschmäht.", footnotes: ["Jes 1,2"] },
      { number: 20, text: "Was soll mir der Weihrauch aus Saba / und das gute Gewürzrohr aus fernem Land? Eure Brandopfer gefallen mir nicht, / eure Schlachtopfer sind mir nicht angenehm.", footnotes: ["Ps 40,7"] },
      { number: 21, text: "Darum - so spricht der Herr: Ich lege diesem Volk Hindernisse in den Weg, / sodass sie darüber straucheln. Väter und Söhne zusammen, / einer wie der andere geht zugrunde." },
      { number: 22, text: "So spricht der Herr: Seht, ein Volk zieht vom Nordland heran, / ein großes Volk bricht auf / von den Grenzen der Erde.", footnotes: ["50,41-43"] },
      { number: 23, text: "Sie kommen mit Bogen und Sichelschwert, / grausam sind sie und ohne Erbarmen. Ihr Lärm gleicht dem Brausen des Meeres / und sie reiten auf Rossen, Krieger, zum Kampf gerüstet / gegen dich, Tochter Zion." },
      { number: 24, text: "Kaum hörten wir diese Nachricht, / da erschlafften uns die Hände, es packte uns die Angst, / das Zittern wie eine Gebärende." },
      { number: 25, text: "Geht nicht aufs Feld hinaus, / macht euch nicht auf den Weg; / denn der Feind greift zum Schwert - Grauen ringsum!" },
      { number: 26, text: "Tochter, mein Volk, leg das Trauerkleid an / und wälz dich im Staub! Halte Trauer wie um den einzigen Sohn, bitterste Klage: / «Ach, jählings kam über uns der Verwüster.»", footnotes: ["Am 8,10"] },
      { number: 27, text: "Zum Prüfer für mein Volk habe ich dich bestellt [zum Metallprüfer]; / du sollst sein Verhalten erkennen und prüfen.", heading: "Der Prophet als Prüfer des Volkes: 6,27-30" },
      { number: 28, text: "Sie alle sind schlimme Empörer, / Verleumder sind sie, Bronze und Eisen sind sie, / alle zusammen Verbrecher." },
      { number: 29, text: "Der Blasebalg schnaubt, / doch das Blei bleibt unberührt vom Feuer. Umsonst versucht der Schmelzer zu schmelzen; / die Bösen lassen sich nicht ausscheiden." },
      { number: 30, text: "«Verworfenes Silber» nennt man sie; / denn verworfen hat sie der Herr." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging:", heading: "Die Tempelrede: 7,1-15", footnotes: ["(1-15) 26,1-19", "1-15: Welche Folgen die Tempelrede für Jeremia hatte, schildert 26,1-19."] },
      { number: 2, text: "Stell dich an das Tor des Hauses des Herrn! Dort ruf dieses Wort aus und sprich: Hört das Wort des Herrn, ganz Juda, alle, die ihr durch diese Tore kommt, um dem Herrn zu huldigen." },
      { number: 3, text: "So spricht der Herr der Heere, der Gott Israels: Bessert euer Verhalten und euer Tun, dann will ich bei euch wohnen hier an diesem Ort.", footnotes: ["26,13"] },
      { number: 4, text: "Vertraut nicht auf die trügerischen Worte: Der Tempel des Herrn, der Tempel des Herrn, der Tempel des Herrn ist hier!" },
      { number: 5, text: "Denn nur wenn ihr euer Verhalten und euer Tun von Grund auf bessert, wenn ihr gerecht entscheidet im Rechtsstreit," },
      { number: 6, text: "wenn ihr die Fremden, die Waisen und Witwen nicht unterdrückt, unschuldiges Blut an diesem Ort nicht vergießt und nicht anderen Göttern nachlauft zu eurem eigenen Schaden," },
      { number: 7, text: "dann will ich bei euch wohnen hier an diesem Ort, in dem Land, das ich euren Vätern gegeben habe für ewige Zeiten." },
      { number: 8, text: "Freilich, ihr vertraut auf die trügerischen Worte, die nichts nützen." },
      { number: 9, text: "Wie? Stehlen, morden, die Ehe brechen, falsch schwören, dem Baal opfern und anderen Göttern nachlaufen, die ihr nicht kennt,", footnotes: ["Hos 4,2"] },
      { number: 10, text: "und dabei kommt ihr und tretet vor mein Angesicht in diesem Haus, über dem mein Name ausgerufen ist, und sagt: Wir sind geborgen!, um dann weiter alle jene Gräuel zu treiben." },
      { number: 11, text: "Ist denn in euren Augen dieses Haus, über dem mein Name ausgerufen ist, eine Räuberhöhle geworden? Gut, dann betrachte auch ich es so Spruch des Herrn.", footnotes: ["Mt 21,13"] },
      { number: 12, text: "Geht doch zu meiner Stätte in Schilo, wo ich früher meinen Namen wohnen ließ, und schaut, was ich ihr angetan habe wegen des Bösen, das mein Volk Israel verübt hat.", footnotes: ["26,6", "Das Heiligtum von Schilo wurde in der Richterzeit um 1050 v. Chr. zerstört (vgl. 1 Sam 4); jetzt wird es dem Tempel ähnlich ergehen."] },
      { number: 13, text: "Nun denn, ihr habt genau das Gleiche getan - Spruch des Herrn. Als ich immer wieder zu euch redete, habt ihr nicht gehört; als ich euch rief, habt ihr nicht geantwortet." },
      { number: 14, text: "Deshalb werde ich mit dem Haus, über dem mein Name ausgerufen ist und auf das ihr euch verlasst, und mit der Stätte, die ich euch und euren Vätern gegeben habe, so verfahren, wie ich mit Schilo verfuhr.", footnotes: ["9,10"] },
      { number: 15, text: "Verstoßen werde ich euch von meinem Angesicht, wie ich alle eure Brüder, alle Nachkommen Efraims, verstoßen habe.", footnotes: ["2 Kön 17,18.23"] },
      { number: 16, text: "Du aber, bete nicht für dieses Volk! Fang nicht an, für sie zu flehen und zu bitten! Dränge mich nicht! Denn ich werde dich nicht erhören.", heading: "Die Verwerfung der Abtrünnigen: 7,16-20", footnotes: ["11,14"] },
      { number: 17, text: "Siehst du nicht, was sie in den Städten Judas und auf den Straßen Jerusalems treiben?" },
      { number: 18, text: "Die Kinder sammeln Holz, die Väter zünden das Feuer an, und die Frauen kneten den Teig, um Opferkuchen für die Himmelskönigin zu backen. Anderen Göttern spendet man Trankopfer, um mir wehzutun.", footnotes: ["19,13; 44,17", "Himmelskönigin wird die Fruchtbarkeitsgöttin Astarte genannt."] },
      { number: 19, text: "Aber tun sie wirklich mir weh - Spruch des Herrn -, und nicht vielmehr sich selbst, zu ihrer eigenen Schande?" },
      { number: 20, text: "Darum - so spricht Gott der Herr: Seht, mein Zorn und Grimm ergießt sich über diesen Ort, über Menschen und Vieh, über die Bäume des Feldes und die Früchte des Ackers; er brennt und wird nicht erlöschen." },
      { number: 21, text: "So spricht der Herr der Heere, der Gott Israels: Häuft nur Brandopfer auf Schlachtopfer und esst Opferfleisch!", heading: "Gehorsam, nicht Opfer: 7,21-28" },
      { number: 22, text: "Denn ich habe euren Vätern, als ich sie aus Ägypten herausführte, nichts gesagt und nichts befohlen, was Brandopfer und Schlachtopfer betrifft.", footnotes: ["Am 5,25"] },
      { number: 23, text: "Vielmehr gab ich ihnen folgendes Gebot: Hört auf meine Stimme, dann will ich euer Gott sein und ihr sollt mein Volk sein. Geht in allem den Weg, den ich euch befehle, damit es euch gut geht.", footnotes: ["Lev 26,12"] },
      { number: 24, text: "Sie aber hörten nicht und neigten mir ihr Ohr nicht zu, sondern folgten den Eingebungen und Trieben ihres bösen Herzens. Sie zeigten mir den Rücken und nicht das Gesicht." },
      { number: 25, text: "Von dem Tag an, als eure Väter aus Ägypten auszogen, bis auf den heutigen Tag sandte ich zu euch immer wieder alle meine Knechte, die Propheten.", footnotes: ["25,4; 44,4"] },
      { number: 26, text: "Aber man hörte nicht auf mich und neigte mir nicht das Ohr zu, vielmehr blieben sie hartnäckig und trieben es noch schlimmer als ihre Väter." },
      { number: 27, text: "Auch wenn du ihnen alle diese Worte sagst, werden sie nicht auf dich hören. Wenn du sie rufst, werden sie dir nicht antworten." },
      { number: 28, text: "Sag ihnen also: Dies ist das Volk, das nicht auf die Stimme des Herrn, seines Gottes, hörte und sich nicht erziehen ließ. Die Treue ist dahin, aus ihrem Mund verschwunden." },
      { number: 29, text: "Schneide dein Haar ab und wirf es weg, stimm Klage an auf den Höhen! Denn der Herr hat das Geschlecht, dem er grollt, verworfen und verstoßen.", heading: "Die Gräuel des Götzendienstes: 7,29 - 8,3" },
      { number: 30, text: "Ja, die Söhne Judas taten, was mir missfällt - Spruch des Herrn. Sie haben in dem Haus, über dem mein Name ausgerufen ist, ihre Scheusale aufgestellt, um es zu entweihen.", footnotes: ["32,34f"] },
      { number: 31, text: "Auch haben sie die Kulthöhe des Tofet im Tal Ben-Hinnom gebaut, um ihre Söhne und Töchter im Feuer zu verbrennen, was ich nie befohlen habe und was mir niemals in den Sinn gekommen ist.", footnotes: ["19,5-7", "Das Ben-Hinnom-Tal begrenzt Jerusalem im Südwesten. Das Tofet (Bedeutung unbekannt) ist eine heidnische Opferstätte für Kinderopfer an Moloch."] },
      { number: 32, text: "Seht, darum kommen Tage - Spruch des Herrn -, da wird man nicht mehr vom Tofet reden oder vom Tal Ben-Hinnom, sondern vom Mordtal und im Tofet wird man Tote begraben, weil anderswo kein Platz mehr ist." },
      { number: 33, text: "Ja, die Leichen dieses Volkes werden den Vögeln des Himmels und den Tieren des Feldes zum Fraß dienen und niemand wird sie verscheuchen." },
      { number: 34, text: "Verstummen lasse ich in den Städten Judas und auf den Straßen Jerusalems Jubelruf und Freudenruf, den Ruf des Bräutigams und den Ruf der Braut; denn das Land wird zur Wüste werden.", footnotes: ["16,9; 25,10; 33,11; Bar 2,23"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "In jener Zeit - Spruch des Herrn - wird man die Gebeine der Könige von Juda, die Gebeine seiner Beamten, die Gebeine der Priester, die Gebeine der Propheten und die Gebeine der Einwohner Jerusalems aus ihren Gräbern holen.", footnotes: ["Bar 2,24"] },
      { number: 2, text: "Man wird sie hinstreuen vor die Sonne, den Mond und das ganze Himmelsheer, denen ihre Liebe galt, denen sie dienten und nachliefen, die sie befragten und anbeteten. Sie werden weder gesammelt noch bestattet werden. Dünger auf dem Acker sollen sie sein.", footnotes: ["Dtn 4,19"] },
      { number: 3, text: "Besser als das Leben wäre der Tod auch für den Rest, für alle, die übrig bleiben von diesem bösen Geschlecht an allen Orten, überall, wohin immer ich sie verstoße - Spruch des Herrn der Heere." },
      { number: 4, text: "Du sollst zu ihnen sagen: So spricht der Herr: / Wer hinfällt, steht der nicht wieder auf? Wer vom Weg abkommt, / kehrt der nicht wieder zurück?", heading: "Abkehr und Strafe: 8,4-23" },
      { number: 5, text: "Warum wendet dieses Volk sich ab [Jerusalem] / und beharrt auf der Abkehr? Warum hält es am Irrtum fest, / weigert sich umzukehren?" },
      { number: 6, text: "Ich horche hin und höre: / Schlechtes reden sie, / keiner bereut sein böses Tun und sagt: / Was habe ich getan? Jeder wendet sich ab und läuft weg, / schnell wie ein Ross, das im Kampf dahinstürmt." },
      { number: 7, text: "Selbst der Storch am Himmel kennt seine Zeiten; / Turteltaube, Schwalbe und Drossel halten die Frist ihrer Rückkehr ein; / mein Volk aber kennt nicht die Rechtsordnung des Herrn." },
      { number: 8, text: "Wie könnt ihr sagen: Weise sind wir / und das Gesetz des Herrn ist bei uns? Ja! Aber der Lügengriffel der Schreiber / hat es zur Lüge gemacht." },
      { number: 9, text: "Zuschanden werden die Weisen, / sie stehen bestürzt da und werden gefangen. Das Wort des Herrn haben sie verworfen / und ihre eigene Weisheit, was nützt sie ihnen?" },
      { number: 10, text: "Darum gebe ich ihre Frauen an Fremde, / ihre Felder an Eroberer. Sind sie doch alle, vom Kleinsten bis zum Größten, nur auf Gewinn aus; / vom Propheten bis zum Priester betrügen sie alle.", footnotes: ["10-12: Wiederholung von 6,13-15."] },
      { number: 11, text: "Den Schaden der Tochter, meines Volkes, / möchten sie leichthin heilen, indem sie rufen: / Heil, Heil! Aber kein Heil ist da." },
      { number: 12, text: "Schämen müssten sie sich, weil sie Gräuel verüben. / Doch sie schämen sich nicht; / Scham ist ihnen unbekannt. Deshalb müssen sie fallen, / wenn die anderen fallen. Sobald ich sie zur Rechenschaft ziehe, / werden sie stürzen, spricht der Herr." },
      { number: 13, text: "Will ich bei ihnen ernten - Spruch des Herrn -, / so sind keine Trauben am Weinstock, keine Feigen am Feigenbaum, / und das Laub ist verwelkt. / Darum habe ich für sie Verwüster bestellt." },
      { number: 14, text: "Warum sitzen wir da? Sammelt euch! / Hinein in die befestigten Städte! Dort werden wir umkommen; / denn der Herr, unser Gott, lässt uns umkommen. Er lässt uns Giftwasser trinken, / weil wir gesündigt haben gegen den Herrn.", footnotes: ["4,5"] },
      { number: 15, text: "[Wir hofften auf Heil, / doch kommt nichts Gutes, auf die Zeit der Heilung, / doch ach, nur Schrecken!]", footnotes: ["Passt inhaltlich besser zu 14,19."] },
      { number: 16, text: "Man hört von Dan her das Schnauben der Rosse, / vom Wiehern seiner Hengste bebt das ganze Land. Sie kommen und fressen das Land und seinen Ertrag, / die Stadt und ihre Bewohner." },
      { number: 17, text: "Denn seht, ich sende giftige Schlangen unter euch, / gegen die es keine Beschwörung gibt; sie werden euch beißen / [- Spruch des Herrn -] und es gibt keine Heilung." },
      { number: 18, text: "Kummer steigt in mir auf, / mein Herz ist krank." },
      { number: 19, text: "Horch! Die Tochter, mein Volk, / schreit aus einem fernen Land: Ist denn der Herr nicht in Zion / oder ist sein König nicht dort? - [Warum haben sie mich erzürnt / mit ihren Götterbildern, mit den fremden Götzen?]", footnotes: ["Die letzte Frage passt als Gottesrede nicht in die Klage des Propheten."] },
      { number: 20, text: "Die Ernte ist vorüber, der Herbst ist vorbei, / uns aber ist nicht geholfen worden." },
      { number: 21, text: "Der Zusammenbruch der Tochter, meines Volkes, / hat mich gebrochen, / traurig bin ich, Entsetzen hat mich gepackt." },
      { number: 22, text: "Gibt es denn keinen Balsam in Gilead, / ist dort kein Wundarzt? Warum schließt sich denn nicht / die Wunde der Tochter, meines Volkes?", footnotes: ["46,11"] },
      { number: 23, text: "Ach, wäre mein Haupt doch Wasser, / mein Auge ein Tränenquell: Tag und Nacht beweinte ich / die Erschlagenen der Tochter, meines Volkes." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Hätte ich doch eine Herberge in der Wüste! / Dann könnte ich mein Volk verlassen und von ihm weggehen. Denn sie sind alle Ehebrecher, / eine Rotte von Treulosen.", heading: "Die Klage über Juda: 9,1-25" },
      { number: 2, text: "Sie machen ihre Zunge zu einem gespannten Bogen; / Lüge, nicht Wahrhaftigkeit herrscht im Land. Ja, sie schreiten von Verbrechen zu Verbrechen; / mich aber kennen sie nicht - Spruch des Herrn." },
      { number: 3, text: "Nehmt euch in Acht vor eurem Nächsten, / keiner traue seinem Bruder! Denn jeder Bruder betrügt / und jeder Nächste verleumdet.", footnotes: ["12,6; Ps 41,10; Mi 7,5"] },
      { number: 4, text: "Ein jeder täuscht seinen Nächsten, / die Wahrheit reden sie nicht. Sie haben ihre Zunge ans Lügen gewöhnt, / sie handeln verkehrt, zur Umkehr sind sie zu träge." },
      { number: 5, text: "Überall Unterdrückung, nichts als Betrug! / Sie weigern sich, mich zu kennen - / Spruch des Herrn." },
      { number: 6, text: "Darum - so spricht der Herr der Heere: / Ja, ich werde sie schmelzen und prüfen; denn wie sollte ich sonst verfahren / mit der Tochter, meinem Volk?" },
      { number: 7, text: "Ein tödlicher Pfeil ist ihre Zunge, / trügerisch redet ihr Mund; «Friede», sagt man zum Nächsten, / doch im Herzen plant man den Überfall." },
      { number: 8, text: "Sollte ich sie dafür nicht bestrafen - Spruch des Herrn - / und an einem solchen Volk keine Rache nehmen?", footnotes: ["5,9.29"] },
      { number: 9, text: "Erhebt über die Berge hin Weinen und Klagen, / über die Weideplätze der Steppe ein Totenlied! Denn sie sind verwüstet, niemand zieht hindurch / und sie hören die Stimme der Herden nicht mehr. Von den Vögeln des Himmels bis zum Vieh / ist alles geflohen, auf und davon." },
      { number: 10, text: "Jerusalem mache ich zum Trümmerhaufen, / zur Behausung für Schakale. Judas Städte mache ich zum Ödland, / das niemand bewohnt.", footnotes: ["26,18"] },
      { number: 11, text: "Wer ist so weise, dass er dies einsieht? Zu wem hat der Mund des Herrn geredet, dass er verkünden kann, warum das Land zugrunde geht, warum es verwüstet ist gleich der Wüste, die niemand durchzieht?" },
      { number: 12, text: "Der Herr erwiderte: Weil sie meine Weisung aufgaben, die ich ihnen vorgelegt habe, nicht auf meine Stimme hörten und nicht meine Weisung befolgten," },
      { number: 13, text: "sondern dem Trieb ihres Herzens folgten und den Baalen nachliefen, an die ihre Väter sie gewöhnt hatten." },
      { number: 14, text: "Darum - so spricht der Herr der Heere, der Gott Israels: Ich gebe ihnen [diesem Volk] Wermut zu essen und Giftwasser zu trinken.", footnotes: ["8,14; 23,15"] },
      { number: 15, text: "Ich zerstreue sie unter die Völker, von denen weder sie noch ihre Väter wussten, und schicke das Schwert hinter ihnen her, bis ich sie vernichtet habe.", footnotes: ["49,37"] },
      { number: 16, text: "So spricht der Herr der Heere: Begreift es! Ruft die Klagefrauen herbei! / Schickt nach den weisen Frauen! Sie sollen kommen." },
      { number: 17, text: "Schnell sollen sie kommen / und Klage über uns anstimmen, sodass unsre Augen von Tränen fließen / und unsre Wimpern von Wasser triefen." },
      { number: 18, text: "Da, horch, ein Klagelied ist aus Zion zu hören: / Ach, wie sind wir misshandelt, / in große Schande gestürzt! Wir müssen die Heimat verlassen, / unsre Wohnungen hat man zerstört." },
      { number: 19, text: "Ja, hört, ihr Frauen, das Wort des Herrn, / euer Ohr vernehme das Wort seines Mundes. Lehrt eure Töchter die Klage, / eine lehre die andere das Totenlied:" },
      { number: 20, text: "Der Tod ist durch unsre Fenster gestiegen, / eingedrungen in unsre Paläste. Er rafft das Kind von der Straße weg, / von den Plätzen die jungen Männer." },
      { number: 21, text: "Die Leichen der Leute / liegen wie Dünger auf dem Feld, wie Garben hinter dem Schnitter; / keiner ist da, der sie sammelt.", footnotes: ["16,4; 25,33"] },
      { number: 22, text: "So spricht der Herr: Der Weise rühme sich nicht seiner Weisheit, / der Starke rühme sich nicht seiner Stärke, / der Reiche rühme sich nicht seines Reichtums." },
      { number: 23, text: "Nein, wer sich rühmen will, rühme sich dessen, / dass er Einsicht hat und mich erkennt, dass er weiß: Ich, der Herr, bin es, / der auf der Erde Gnade, Recht und Gerechtigkeit schafft. Denn an solchen Menschen habe ich Gefallen - / Spruch des Herrn.", footnotes: ["1 Kor 1,31; 2 Kor 10,17"] },
      { number: 24, text: "Fürwahr, es werden Tage kommen - Spruch des Herrn -, da ziehe ich alle Beschnittenen zur Rechenschaft:" },
      { number: 25, text: "Ägypten, Juda, Edom, Ammon, Moab und alle mit gestutztem Haar, die in der Wüste wohnen; denn alle Völker gelten mir als unbeschnitten auch das ganze Haus Israel hat ein unbeschnittenes Herz." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Hört das Wort, das der Herr zu euch spricht, ihr vom Haus Israel.", heading: "Gott und die Götzenbilder: 10,1-16" },
      { number: 2, text: "So spricht der Herr: Gewöhnt euch nicht an den Weg der Völker, / erschreckt nicht vor den Zeichen des Himmels, / wenn auch die Völker vor ihnen erschrecken." },
      { number: 3, text: "Denn die Gebräuche der Völker sind leerer Wahn. / Ihre Götzen sind nur Holz, das man im Wald schlägt, / ein Werk aus der Hand des Schnitzers, / mit dem Messer verfertigt.", footnotes: ["Jes 44,12-17"] },
      { number: 4, text: "Er verziert es mit Silber und Gold, / mit Nagel und Hammer macht er es fest, sodass es nicht wackelt.", footnotes: ["Bar 6"] },
      { number: 5, text: "Sie sind wie Vogelscheuchen im Gurkenfeld. / Sie können nicht reden; / man muss sie tragen, weil sie nicht gehen können. Fürchtet euch nicht vor ihnen; / denn sie können weder Schaden zufügen / noch Gutes bewirken." },
      { number: 6, text: "Niemand, Herr, ist wie du: / Groß bist du und groß an Kraft ist dein Name." },
      { number: 7, text: "Wer sollte dich nicht fürchten, du König der Völker? / Ja, das steht dir zu. Denn unter allen Weisen der Völker / und in jedem ihrer Reiche ist keiner wie du." },
      { number: 8, text: "Sie alle sind töricht und dumm. / Was die nichtigen Götzen zu bieten haben - Holz ist es." },
      { number: 9, text: "Sie sind gehämmertes Silber aus Tarschisch und Gold aus Ofir, / Arbeit des Schnitzers und Goldschmieds; violetter und roter Purpur ist ihr Gewand; / sie alle sind nur das Werk kunstfertiger Männer." },
      { number: 10, text: "Der Herr aber ist in Wahrheit Gott, / lebendiger Gott und ewiger König. Vor seinem Zorn erbebt die Erde, / die Völker halten seinen Groll nicht aus." },
      { number: 11, text: "Von jenen dagegen sollt ihr sagen: / Die Götter, die weder Himmel noch Erde erschufen, / sie sollen verschwinden von der Erde und unter dem Himmel.", footnotes: ["In aramäischer Sprache."] },
      { number: 12, text: "Er aber hat die Erde erschaffen durch seine Kraft, / den Erdkreis gegründet durch seine Weisheit, / durch seine Einsicht den Himmel ausgespannt.", footnotes: ["(12-16) 51,15-19"] },
      { number: 13, text: "Lässt er seine Stimme ertönen, / dann rauschen die Wasser am Himmel. Wolken führt er herauf vom Rand der Erde; / er lässt es blitzen und regnen, / aus seinen Kammern entsendet er den Wind.", footnotes: ["Ps 135,7"] },
      { number: 14, text: "Töricht steht jeder Mensch da, ohne Erkenntnis, / beschämt jeder Goldschmied mit seinem Götzenbild; denn seine Bilder sind Trug, / kein Atem ist in ihnen.", footnotes: ["Jes 45,16"] },
      { number: 15, text: "Nichtig sind sie, ein Spottgebilde. / Zur Zeit ihrer Heimsuchung gehen sie zugrunde." },
      { number: 16, text: "Anders der Gott, der Jakobs Anteil ist. / Denn er ist der Schöpfer des Alls und Israel der Stamm, der ihm gehört. / Herr der Heere ist sein Name." },
      { number: 17, text: "Raff dein Bündel zusammen! Fort aus dem Land, / du schwer bedrängte Stadt!", heading: "Klage und Bitte: 10,17-25" },
      { number: 18, text: "Denn so spricht der Herr: Fürwahr, diesmal schleudere ich / die Bewohner des Landes hinweg und bringe sie in Bedrängnis, / damit sie mich finden." },
      { number: 19, text: "Weh mir ob meines Zusammenbruchs! / Unheilbar ist meine Wunde. Ich aber hatte gedacht: / Das ist doch nur eine Krankheit, die ich ertragen kann.", footnotes: ["14,17; 30,12"] },
      { number: 20, text: "Nun ist mein Zelt verwüstet, / alle meine Zeltstricke sind zerrissen. Meine Kinder gingen von mir fort / und sind nicht mehr. Niemand schlägt mein Zelt wieder auf / und breitet darüber die Zeltdecken aus." },
      { number: 21, text: "Denn töricht waren die Hirten, / den Herrn suchten sie nicht; deshalb hatten sie keinen Erfolg / und ihre ganze Herde wurde zerstreut." },
      { number: 22, text: "Horch, eine Kunde trifft eben ein, / großes Getöse vom Nordland her: Judas Städte sollen zum Ödland werden, / zur Behausung für Schakale.", footnotes: ["9,10; 26,18"] },
      { number: 23, text: "Ich weiß, Herr, dass der Mensch seinen Weg nicht zu bestimmen vermag, / dass keiner beim Gehen seinen Schritt lenken kann." },
      { number: 24, text: "Herr, züchtige mich, doch mit rechtem Maß, / nicht in deinem Zorn, / sonst machst du mich allzu elend.", footnotes: ["30,11"] },
      { number: 25, text: "Gieß deinen Zorn aus über die Völker, / die dich nicht kennen, und über die Stämme, / die deinen Namen nicht anrufen. Denn sie haben Jakob verschlungen und vernichtet, / seine Wohnstätte verwüstet." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging:", heading: "Der gebrochene Bund: 11,1-17" },
      { number: 2, text: "Hört die Worte dieses Bundes! Du sollst sie den Leuten von Juda und den Einwohnern Jerusalems verkünden." },
      { number: 3, text: "Du sollst ihnen sagen: So spricht der Herr, der Gott Israels: Verflucht der Mensch, der nicht hört auf die Worte dieses Bundes," },
      { number: 4, text: "die ich euren Vätern aufgetragen habe, als ich sie aus Ägypten herausführte, aus dem Schmelzofen des Eisens: Hört auf meine Stimme, und handelt in allem nach meinen Geboten; dann werdet ihr mein Volk sein und ich will euer Gott sein.", footnotes: ["Lev 26,12; Dtn 26,17f"] },
      { number: 5, text: "Nur so kann ich den Eid halten, den ich euren Vätern geschworen habe: ihnen ein Land zu geben, in dem Milch und Honig fließen, wie ihr es heute habt. Ich antwortete: Ja, Herr." },
      { number: 6, text: "Darauf sprach der Herr zu mir: Verkünde alle diese Worte laut in den Städten Judas und auf den Straßen Jerusalems: Hört die Worte dieses Bundes und handelt danach!" },
      { number: 7, text: "Denn ich habe eure Väter, schon als ich sie aus Ägypten heraufführte und bis zum heutigen Tag, immer wieder beschworen: Hört auf meine Stimme!", footnotes: ["(7-8) 7,25f"] },
      { number: 8, text: "Sie aber haben nicht gehört und mir ihr Ohr nicht zugeneigt; alle folgten dem Trieb ihres bösen Herzens. So musste ich an ihnen alle Worte dieses Bundes erfüllen, den zu halten ich ihnen geboten habe, den sie aber nicht gehalten haben.", footnotes: ["18,12; 25,4"] },
      { number: 9, text: "Der Herr sagte zu mir: Es gibt eine Verschwörung unter den Leuten von Juda und den Einwohnern Jerusalems." },
      { number: 10, text: "Sie sind zurückgekehrt zu den Sünden ihrer Väter, die sich weigerten, meinen Worten zu gehorchen. Auch sie sind fremden Göttern nachgelaufen, um ihnen zu dienen. Das Haus Israel und das Haus Juda haben meinen Bund gebrochen, den ich mit ihren Vätern geschlossen habe." },
      { number: 11, text: "Darum - so spricht der Herr: Jetzt bringe ich Unheil über sie, dem sie nicht entgehen können. Schreien sie dann zu mir, so werde ich nicht auf sie hören." },
      { number: 12, text: "Wenn aber die Städte Judas und die Einwohner Jerusalems hingehen und zu den Göttern schreien, denen sie Opfer darbringen, dann werden diese zur Zeit der Not ihnen ganz gewiss nicht helfen können." },
      { number: 13, text: "Denn so zahlreich wie deine Städte sind auch deine Götter, Juda, und so zahlreich wie die Straßen Jerusalems sind die schändlichen Altäre, die ihr errichtet habt, um dem Baal zu opfern.", footnotes: ["2,28"] },
      { number: 14, text: "Du aber bete nicht für dieses Volk! Fang nicht an, für sie zu flehen und zu bitten. Denn ich höre nicht, wenn du zu mir rufst wegen ihrer Not.", footnotes: ["7,16; 14,11"] },
      { number: 15, text: "Was hat mein Liebling in meinem Haus zu suchen, wenn er nur Schlimmes verübt? Können Zehnt und Opferfleisch das Unheil von dir fernhalten, sodass du dann jubeln könntest?" },
      { number: 16, text: "Einen üppigen Ölbaum von schöner Gestalt hatte der Herr dich genannt. Wenn der gewaltige (Kriegs-)Lärm ertönt, legt er Feuer an ihn, sodass seine Zweige hässlich werden." },
      { number: 17, text: "Der Herr der Heere, der dich pflanzte, hat Unheil über dich verhängt wegen der bösen Taten, die das Haus Israel und das Haus Juda verübten; dem Baal opferten sie, um mich zu erzürnen." },
      { number: 18, text: "Der Herr ließ es mich wissen und so wusste ich es; damals ließest du mich ihr Treiben durchschauen.", heading: "Die Mordpläne gegen Jeremia: 11,18-23" },
      { number: 19, text: "Ich selbst war wie ein zutrauliches Lamm, das zum Schlachten geführt wird, und ahnte nicht, dass sie gegen mich Böses planten: Wir wollen den Baum im Saft verderben; wir wollen ihn ausrotten aus dem Land der Lebenden, sodass man seinen Namen nicht mehr erwähnt.", footnotes: ["Jes 53,7"] },
      { number: 20, text: "Aber der Herr der Heere richtet gerecht, er prüft Herz und Nieren. Ich werde sehen, wie du Rache an ihnen nimmst; denn dir habe ich meine Sache anvertraut.", footnotes: ["20,12; Ps 7,10"] },
      { number: 21, text: "Darum - so spricht der Herr gegen die Leute von Anatot, die mir nach dem Leben trachten und sagen: Du darfst nicht als Prophet im Namen des Herrn auftreten, wenn du nicht durch unsere Hand sterben willst." },
      { number: 22, text: "Darum - so spricht der Herr der Heere: Seht, ich werde sie zur Rechenschaft ziehen. Die jungen Männer sterben durchs Schwert, ihre Söhne und Töchter sterben vor Hunger." },
      { number: 23, text: "So wird den Leuten von Anatot kein Rest mehr bleiben, wenn ich Unheil über sie bringe, das Jahr ihrer Bestrafung." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Du bleibst im Recht, Herr, wenn ich mit dir streite; / dennoch muss ich mit dir rechten. Warum haben die Frevler Erfolg, / weshalb können alle Abtrünnigen sorglos sein?", heading: "Der Prophet in seelischer Not: 12,1-6", footnotes: ["Ps 73,3-12; Ijob 21,7-13"] },
      { number: 2, text: "Du hast sie eingepflanzt und sie schlagen Wurzel, / sie wachsen heran und bringen auch Frucht. Nur ihrem Mund bist du nah, / ihrem Herzen aber fern." },
      { number: 3, text: "Du jedoch, Herr, kennst und durchschaust mich; / du hast mein Herz erprobt / und weißt, dass es an dir hängt. Raff sie weg wie Schafe zum Schlachten, / sondere sie aus für den Tag des Mordens!" },
      { number: 4, text: "Wie lange noch soll das Land vertrocknen, / das Grün auf allen Feldern verdorren? Weil seine Bewohner Böses tun, / schwinden Vieh und Vögel dahin. / Denn sie denken: Er sieht unsre Zukunft nicht. -", footnotes: ["4e: G: Gott sieht unsere Pfade nicht."] },
      { number: 5, text: "Wenn schon der Wettlauf mit Fußgängern dich ermüdet, / wie willst du mit Pferden um die Wette laufen? Wenn du nur im friedlichen Land dich sicher fühlst, / wie wirst du dich verhalten im Dickicht des Jordan?", footnotes: ["Die Antwort Gottes hält dem Propheten in zwei Vergleichen vor, dass er schon kleinen Schwierigkeiten nicht gewachsen ist; er muss auf noch größere gefasst sein."] },
      { number: 6, text: "Selbst deine Brüder und das Haus deines Vaters handeln treulos an dir; / auch sie schreien laut hinter dir her. Trau ihnen nicht, / selbst wenn sie freundlich mit dir reden.", footnotes: ["9,3"] },
      { number: 7, text: "Ich verlasse mein Haus, / ich verstoße mein Erbteil. Meinen Herzensliebling gebe ich preis / in die Hand seiner Feinde.", heading: "Die Klage Gottes über sein Land: 12,7-13" },
      { number: 8, text: "Mein Erbteil wandte sich gegen mich / wie ein Löwe im Wald. Es erhob gegen mich seine Stimme; / deshalb bin ich ihm Feind." },
      { number: 9, text: "Ist mir mein Erbteil zur Höhle einer Hyäne geworden, / dass Raubvögel es umlagern? Auf, sammelt euch, alle Tiere des Feldes, / kommt zum Fraß!", footnotes: ["Jes 56,9"] },
      { number: 10, text: "Hirten in großer Zahl / haben meinen Weinberg verwüstet, mein Feld zertreten, / mein prächtiges Feld zur öden Wüste gemacht." },
      { number: 11, text: "Man hat es in dürres Ödland verwandelt, / verwüstet liegt es vor mir. Das ganze Land ist verödet, / doch keiner nimmt sich das zu Herzen." },
      { number: 12, text: "Über alle Hügel der Steppe drangen Verderber ein; / denn das Schwert des Herrn frisst von einem Ende des Landes zum andern. / Kein Mensch bleibt unversehrt." },
      { number: 13, text: "Sie haben Weizen gesät und Dornen geerntet, / sie haben sich abgemüht, doch ohne Ertrag. Enttäuscht sind sie von ihrer Ernte; / (sie ist vernichtet) durch den glühenden Zorn des Herrn." },
      { number: 14, text: "So spricht der Herr: Alle meine bösen Nachbarn, die das Erbteil antasten, das ich meinem Volk Israel zum Erbe gegeben habe, fürwahr, ich reiße sie von ihrem Boden weg; doch auch das Haus Juda reiße ich aus ihrer Mitte.", heading: "Gericht oder Heil für die Nachbarn: 12,14-17" },
      { number: 15, text: "Aber nachdem ich sie weggerissen habe, will ich mich ihrer wieder erbarmen und sie zurückbringen, einen jeden in sein Erbteil und in seine Heimat." },
      { number: 16, text: "Lernen sie dann die rechte Lebensweise meines Volkes, sodass sie bei meinem Namen schwören: So wahr der Herr lebt!, wie sie vorher mein Volk gelehrt hatten, beim Baal zu schwören, dann sollen sie inmitten meines Volkes wiederhergestellt werden.", footnotes: ["4,2"] },
      { number: 17, text: "Gehorchen sie jedoch nicht, so werde ich dieses Volk völlig ausreißen und vernichten - Spruch des Herrn." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "So hat der Herr zu mir gesagt: Geh, kauf dir einen leinenen Gürtel, und leg ihn dir um die Hüften, aber tauch ihn nicht ins Wasser!", heading: "Gottes Strafe im Gleichnis: 13,1-14" },
      { number: 2, text: "Da kaufte ich, wie der Herr mir aufgetragen hatte, den Gürtel und legte ihn mir um die Hüften." },
      { number: 3, text: "Nun erging das Wort des Herrn zum zweiten Mal an mich; er sagte:" },
      { number: 4, text: "Nimm den gekauften Gürtel, den du um die Hüften trägst, mach dich auf den Weg an den Eufrat und verbirg ihn dort in einer Felsspalte!" },
      { number: 5, text: "Ich ging hin und verbarg ihn am Eufrat, wie mir der Herr befohlen hatte." },
      { number: 6, text: "Nach längerer Zeit sprach der Herr zu mir: Mach dich auf den Weg an den Eufrat und hol den Gürtel zurück, den du dort auf meinen Befehl hin verborgen hast." },
      { number: 7, text: "Da ging ich zum Eufrat, suchte den Gürtel und holte ihn von der Stelle, wo ich ihn verborgen hatte. Doch der Gürtel war verdorben, zu nichts mehr zu gebrauchen." },
      { number: 8, text: "Nun erging das Wort des Herrn an mich:" },
      { number: 9, text: "So spricht der Herr: Ebenso verderbe ich die stolze Pracht Judas und Jerusalems, wie groß sie auch sei." },
      { number: 10, text: "Dieses böse Volk weigert sich, auf meine Worte zu hören, es folgt dem Trieb seines Herzens und läuft anderen Göttern nach, um ihnen zu dienen und sie anzubeten; es soll daher wie dieser Gürtel werden, der zu nichts mehr zu gebrauchen ist.", footnotes: ["11,8"] },
      { number: 11, text: "Denn wie sich der Gürtel den Hüften des Mannes anschmiegt, so wollte ich, dass sich das ganze Haus Juda mir anschmiegte - Spruch des Herrn -, damit es mein Volk und mein Ruhm, mein Preis und mein Schmuck wäre. Sie aber haben nicht gehorcht." },
      { number: 12, text: "Du sollst ihnen das folgende Wort ausrichten: So spricht der Herr, der Gott Israels: Jeden Krug kann man mit Wein füllen. Wenn sie dir darauf erwidern: Wissen wir nicht selbst, dass man jeden Krug mit Wein füllen kann?," },
      { number: 13, text: "dann sollst du ihnen antworten: So spricht der Herr: Seht, ich fülle alle Bewohner dieses Landes mit Trunkenheit, die Könige, die auf Davids Thron sitzen, die Priester, die Propheten und alle Einwohner Jerusalems.", footnotes: ["Jes 51,17"] },
      { number: 14, text: "Ich zerschmettere sie, den einen am andern, Väter und Söhne zugleich - Spruch des Herrn. Keine Schonung, kein Mitleid und kein Erbarmen hält mich ab, sie zu vernichten." },
      { number: 15, text: "Hört und merkt auf! / Seid nicht hochmütig; denn der Herr redet.", heading: "Die Warnung vor Hochmut: 13,15-17" },
      { number: 16, text: "Erweist dem Herrn, eurem Gott, die Ehre, / bevor es dunkel wird, / bevor eure Füße straucheln auf dämmrigen Bergen. Wartet ihr dann auf das Licht - / er verwandelt es in Finsternis / und macht es zur Dunkelheit." },
      { number: 17, text: "Wenn ihr aber darauf nicht hört, / so muss ich im Verborgenen weinen über den Hochmut und mein Auge muss ohne Unterlass Tränen vergießen, / da die Herde des Herrn weggeführt wird.", footnotes: ["14,17"] },
      { number: 18, text: "Sag zum König und zur Herrin: / Setzt euch tief hinunter; denn eure prächtige Krone / sinkt euch vom Haupt.", heading: "Die drohende Wegführung: 13,18-19", footnotes: ["Gemeint sind König Jojachin und seine Mutter (2 Kön 24,10-17) vor ihrer Gefangennahme 597 v. Chr."] },
      { number: 19, text: "Die Städte im Negeb sind verschlossen / und niemand kann sie öffnen. / Weggeführt wird ganz Juda, vollständig weggeführt." },
      { number: 20, text: "Blick auf [Jerusalem] / und schau, wie sie kommen vom Norden! Wo bleibt da die Herde, die dir anvertraut war, / deine prächtigen Schafe?", heading: "Die Schändung Jerusalems: 13,20-27" },
      { number: 21, text: "Was wirst du sagen, / wenn man die Freunde, die du selbst an dich gewöhnt hast, / als Herren über dich setzt? Werden dich nicht Wehen ergreifen / wie eine gebärende Frau?" },
      { number: 22, text: "Und wenn du dich fragst: / Warum hat mich das alles getroffen?, so wisse: Wegen deiner großen Schuld wird deine Schleppe aufgehoben / und dein Leib vergewaltigt.", footnotes: ["Ez 16,37"] },
      { number: 23, text: "Ändert wohl ein Neger seine Hautfarbe / oder ein Leopard seine Flecken? Dann könntet auch ihr euch noch bessern, / die ihr ans Böse gewöhnt seid.", footnotes: ["Neger, wörtlich: Kuschiter. - Kusch entspricht dem heutigen Sudan und Teilen Äthiopiens."] },
      { number: 24, text: "So aber zerstreue ich euch wie Spreu, / die verfliegt, wenn der Wüstenwind weht." },
      { number: 25, text: "Das ist dein Los, dein Lohn, / von mir dir zugemessen - Spruch des Herrn -, weil du mich vergessen / und dich auf Lügen verlassen hast." },
      { number: 26, text: "Nun hebe auch ich deine Schleppe auf, / bis über dein Gesicht, / sodass deine Schande offenbar wird," },
      { number: 27, text: "deine Ehebrüche, dein geiles Wiehern, / deine schändliche Unzucht. Auf den Hügeln und auf dem Feld / habe ich deine Gräuel gesehen. Weh dir, Jerusalem, / weil du dich nicht reinigst - / wie lange noch?", footnotes: ["5,8"] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Das Wort des Herrn erging an Jeremia wegen der großen Dürre:", heading: "Die große Dürre: 14,1-9" },
      { number: 2, text: "Juda ist ausgedörrt; / seine Tore verfallen, sie sinken trauernd zu Boden / und Jerusalems Klageschrei steigt empor.", footnotes: ["Joël 1,10-12"] },
      { number: 3, text: "Die Vornehmen schicken ihre Diener nach Wasser; / sie kommen zu den Brunnen, / finden aber kein Wasser; sie kehren mit leeren Krügen zurück. / [Sie sind bestürzt und enttäuscht / und verhüllen ihr Haupt.]", footnotes: ["Das Eingeklammerte fehlt in G; irrtümliche Doppelschreibung für V. 4cd."] },
      { number: 4, text: "Die Bauern sind um den Ackerboden besorgt; / denn es fiel kein Regen im Land. Sie sind bestürzt / und verhüllen ihr Haupt." },
      { number: 5, text: "Selbst die Hirschkuh im Feld / lässt ihr Junges im Stich, / weil kein Grün mehr da ist." },
      { number: 6, text: "Die Wildesel stehen auf den kahlen Höhen; / sie schnappen nach Luft wie Schakale. Ihre Augen erlöschen; / denn nirgends ist Gras." },
      { number: 7, text: "Unsre Sünden klagen uns an. / Doch um deines Namens willen handle, o Herr! Ja, zahlreich sind unsre Vergehen; / gegen dich haben wir gesündigt." },
      { number: 8, text: "Du, Israels Hoffnung, / sein Retter zur Zeit der Not, warum bist du wie ein Fremder im Land / und wie ein Wanderer, der nur über Nacht einkehrt?" },
      { number: 9, text: "Warum bist du wie ein ratloser Mann, / wie ein Krieger, der nicht zu siegen vermag? Du bist doch in unsrer Mitte, Herr, / und dein Name ist über uns ausgerufen. / Verlass uns nicht!" },
      { number: 10, text: "So spricht der Herr von diesem Volk: Haltlos hin und her zu schweifen, das lieben sie; ihren Füßen gönnen sie keine Ruhe. Doch der Herr hat kein Gefallen an ihnen. Jetzt denkt er an ihre Schuld und straft ihre Sünden.", heading: "Von Gott verworfen: 14,10-18" },
      { number: 11, text: "Und der Herr sprach zu mir: Bete nicht um das Wohlergehen dieses Volkes!", footnotes: ["7,16; 11,14"] },
      { number: 12, text: "Auch wenn sie fasten, höre ich nicht auf ihr Flehen; wenn sie Brandopfer und Speiseopfer darbringen, habe ich kein Gefallen an ihnen. Durch Schwert, Hunger und Pest mache ich ihnen ein Ende." },
      { number: 13, text: "Da sagte ich: Ach, Herr und Gott, die Propheten sagen doch zu ihnen: Ihr werdet das Schwert nicht sehen, der Hunger wird nicht über euch kommen, sondern beständiges Heil gewähre ich euch an diesem Ort." },
      { number: 14, text: "Aber der Herr erwiderte mir: Lüge ist, was die Propheten in meinem Namen verkünden. Ich habe sie weder gesandt noch beauftragt, ich habe nicht zu ihnen gesprochen. Erlogene Visionen, leere Wahrsagerei und selbst erdachten Betrug verkünden sie euch.", footnotes: ["27,14f; 29,8f"] },
      { number: 15, text: "Darum spreche ich, der Herr, so gegen die Propheten, die in meinem Namen weissagen, obwohl ich sie nicht gesandt habe, und die behaupten, Schwert und Hunger werde es nicht geben in diesem Land: Durch Schwert und Hunger werden diese Propheten enden." },
      { number: 16, text: "Die Leute aber, denen sie weissagen, werden auf den Straßen Jerusalems liegen, hingestreckt durch Hunger und Schwert. Niemand wird sie begraben, sie, ihre Frauen, Söhne und Töchter. So gieße ich das verdiente Unheil über sie aus." },
      { number: 17, text: "Du sollst zu ihnen dieses Wort sagen: Meine Augen fließen über von Tränen / bei Tag und bei Nacht / und finden keine Ruhe. Denn großes Verderben brach herein / über die Jungfrau, die Tochter, mein Volk, / eine unheilbare Wunde.", footnotes: ["13,17"] },
      { number: 18, text: "Gehe ich aufs Feld hinaus - / seht, vom Schwert Durchbohrte! Komme ich in die Stadt - / seht, vom Hunger Gequälte! Ja, auch Propheten und Priester werden verschleppt / in ein Land, das sie nicht kennen." },
      { number: 19, text: "Hast du denn Juda ganz verworfen, / wurde dir Zion zum Abscheu? Warum hast du uns so geschlagen, / dass es für uns keine Heilung mehr gibt? Wir hofften auf Heil, / doch kommt nichts Gutes, auf die Zeit der Heilung, / doch ach, nur Schrecken!", heading: "Die Klage des Volkes: 14,19-22", footnotes: ["8,15"] },
      { number: 20, text: "Wir erkennen, Herr, unser Unrecht, / die Schuld unsrer Väter: / Ja, wir haben gegen dich gesündigt." },
      { number: 21, text: "Um deines Namens willen verschmäh nicht, / verstoß nicht den Thron deiner Herrlichkeit! Gedenke deines Bundes mit uns / und löse ihn nicht!" },
      { number: 22, text: "Gibt es etwa Regenspender / unter den Götzen der Völker? Oder ist es der Himmel, der von selbst regnen lässt? / Bist nicht du es, Herr, unser Gott? Wir setzen unsre Hoffnung auf dich; / denn du hast dies alles gemacht." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Doch der Herr sprach zu mir: Selbst wenn Mose und Samuel vor mein Angesicht träten, würde sich mein Herz diesem Volk nicht mehr zuneigen. Schaff sie mir aus den Augen, sie sollen gehen.", heading: "Das unabänderliche Strafgericht: 15,1-4" },
      { number: 2, text: "Fragen sie dich dann: Wohin sollen wir gehen?, so sag ihnen: So spricht der Herr: Wer der Pest verfallen ist, zur Pest! Wer dem Schwert, zum Schwert! Wer dem Hunger, zum Hunger! Wer der Gefangenschaft, zur Gefangenschaft!", footnotes: ["43,11"] },
      { number: 3, text: "Vier Plagen biete ich gegen sie auf - Spruch des Herrn: Das Schwert zum Morden, die Hunde zum Fortschleifen, die Vögel des Himmels und die Tiere des Feldes zum Fressen und Vertilgen.", footnotes: ["Ez 14,21"] },
      { number: 4, text: "Ich mache sie zu einem Bild des Schreckens für alle Reiche der Erde wegen des Manasse, des Sohnes Hiskijas, des Königs von Juda, zur Strafe für das, was er in Jerusalem verübt hat.", footnotes: ["2 Kön 21,1-16"] },
      { number: 5, text: "Wer wird mit dir, Jerusalem, Mitleid haben / und wer wird dich bedauern? Wer wird kommen, / um nach deinem Ergehen zu fragen?", heading: "Jerusalems Ende: 15,5-9" },
      { number: 6, text: "Du selbst hast mich verworfen - Spruch des Herrn - / du hast mir den Rücken gekehrt. Deshalb streckte ich meine Hand gegen dich aus / und zerstörte dich; / ich bin der Nachsicht müde geworden." },
      { number: 7, text: "Die Bewohner habe ich mit der Schaufel geworfelt / auf den freien Plätzen des Landes. Ich habe mein Volk kinderlos gemacht / und es dem Untergang geweiht, weil es von seinen schlimmen Wegen / nicht umkehren wollte.", footnotes: ["Mt 3,12", "Zum Bild vom Worfeln vgl. die Anmerkung zu Rut 3,2-4."] },
      { number: 8, text: "Seine Witwen wurden zahlreicher / als der Sand am Meer; ich brachte über sie [die Mütter der jungen Männer] / am hellen Mittag den Verwüster, ließ jählings auf sie fallen / Angst und Schrecken." },
      { number: 9, text: "Die Mutter, die sieben Söhne gebar, / welkte dahin, verhauchte ihr Leben. Ihr sank die Sonne mitten am Tag, / sie fiel in Schande und Schmach. Den Rest des Volkes gebe ich dem Schwerte preis / vor den Augen seiner Feinde - Spruch des Herrn." },
      { number: 10, text: "Weh mir, Mutter, dass du mich geboren hast, / einen Mann, der mit aller Welt in Zank und Streit liegt. Ich bin niemands Gläubiger und niemands Schuldner / und doch fluchen mir alle.", heading: "Die Klage des Propheten: 15,10-21", footnotes: ["20,14"] },
      { number: 11, text: "Fürwahr, Herr, ich habe dir mit gutem Willen gedient, / ich bin für den Feind bei dir eingetreten / zur Zeit des Unheils und der Bedrängnis." },
      { number: 12, text: "[Kann man Eisen zertrümmern, / Eisen vom Norden, und Kupfer?", footnotes: ["12-14: Stört den Zusammenhang und unterbricht die Klage. Die VV. 13f stammen aus 17,3f."] },
      { number: 13, text: "Dein Vermögen und deine Schätze / gebe ich zur Plünderung preis als Lohn für all deine Sünden / in deinem ganzen Gebiet." },
      { number: 14, text: "Ich mache dich zum Sklaven deiner Feinde / in einem Land, das du nicht kennst. Denn Feuer lodert auf in meinem Zorn, / gegen euch ist es entbrannt.]" },
      { number: 15, text: "Du weißt es, Herr; denk an mich / und nimm dich meiner an! / Nimm für mich Rache an meinen Verfolgern! Raff mich nicht hinweg, / sondern schieb deinen Zorn hinaus! / Bedenke, dass ich deinetwillen Schmach erleide." },
      { number: 16, text: "Kamen Worte von dir, / so verschlang ich sie; / dein Wort war mir Glück und Herzensfreude; denn dein Name ist über mir ausgerufen, / Herr, Gott der Heere." },
      { number: 17, text: "Ich sitze nicht heiter im Kreis der Fröhlichen; / von deiner Hand gepackt, sitze ich einsam; / denn du hast mich mit Groll angefüllt.", footnotes: ["16,8"] },
      { number: 18, text: "Warum dauert mein Leiden ewig / und ist meine Wunde so bösartig, / dass sie nicht heilen will? Wie ein versiegender Bach bist du mir geworden, / ein unzuverlässiges Wasser." },
      { number: 19, text: "Darum - so spricht der Herr: Wenn du umkehrst, lasse ich dich umkehren, / dann darfst du wieder vor mir stehen. Redest du Edles und nicht Gemeines, / dann darfst du mir wieder Mund sein. Jene sollen sich dir zuwenden, / du aber wende dich ihnen nicht zu.", footnotes: ["Der Prophet, der die Umkehr gepredigt hat, muss nun, da er versagt hat und an seiner Berufung zweifelt, erst selbst umkehren, wenn er wieder in den Dienst Gottes treten will."] },
      { number: 20, text: "Dann mache ich dich für dieses Volk / zur festen, ehernen Mauer. Mögen sie dich bekämpfen, / sie werden dich nicht bezwingen; denn ich bin mit dir, / um dir zu helfen und dich zu retten / - Spruch des Herrn.", footnotes: ["1,18f"] },
      { number: 21, text: "Ja, ich rette dich aus der Hand der Bösen, / ich befreie dich aus der Faust der Tyrannen." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Das Wort des Herrn erging an mich:", heading: "Jeremias Einsamkeit - ein Zeichen für Israel: 16,1-9" },
      { number: 2, text: "Du sollst dir keine Frau nehmen und weder Söhne noch Töchter haben an diesem Ort." },
      { number: 3, text: "Denn so spricht der Herr über die Söhne und Töchter, die an diesem Ort geboren werden, über ihre Mütter, die sie gebären, und über ihre Väter, die sie zeugen in diesem Land:" },
      { number: 4, text: "Eines qualvollen Todes müssen sie sterben; man wird sie nicht beklagen und nicht begraben; sie werden zum Dünger auf dem Acker. Durch Schwert und Hunger kommen sie um; ihre Leichen werden zum Fraß für die Vögel des Himmels und die Tiere des Feldes.", footnotes: ["9,21; 25,33"] },
      { number: 5, text: "Ja, so hat der Herr gesprochen: Betritt kein Trauerhaus, geh nicht zur Totenklage und bezeig niemandem Beileid! Denn ich habe diesem Volk mein Heil entzogen - Spruch des Herrn -, die Güte und das Erbarmen." },
      { number: 6, text: "Groß und Klein muss sterben in diesem Land; man wird sie nicht begraben und nicht beklagen. Niemand ritzt sich ihretwegen wund oder schert sich kahl.", footnotes: ["47,5", "6f: Sich wund ritzen, sich kahl scheren und Trauerbrot essen sind Trauerriten."] },
      { number: 7, text: "Keinem wird man das Trauerbrot brechen, um ihn wegen eines Verstorbenen zu trösten; man wird ihm nicht den Trostbecher reichen wegen seines Vaters oder seiner Mutter." },
      { number: 8, text: "Auch ein Haus, in dem ein Gastmahl stattfindet, sollst du nicht betreten, um mit den Leuten bei Speise und Trank zu sitzen." },
      { number: 9, text: "Denn so spricht der Herr der Heere, der Gott Israels: Seht, verstummen lasse ich an diesem Ort, vor euren Augen und in euren Tagen, Jubelruf und Freudenruf, den Ruf des Bräutigams und den Ruf der Braut.", footnotes: ["7,34"] },
      { number: 10, text: "Wenn du nun diesem Volk das alles verkündest und man dich fragt: Warum droht der Herr uns all dieses schwere Unheil an? Worin besteht unsre Schuld und welche Sünde haben wir gegen den Herrn, unsern Gott, begangen?,", heading: "Die Gründe für das Gericht: 16,10-18", footnotes: ["5,19"] },
      { number: 11, text: "so antworte ihnen: Eure Väter haben mich verlassen - Spruch des Herrn; sie liefen anderen Göttern nach, dienten ihnen und beteten sie an. Mich aber haben sie verlassen und meine Weisung nicht befolgt." },
      { number: 12, text: "Ihr selbst aber habt es noch schlimmer getrieben als eure Väter. Seht, jeder von euch folgt dem Trieb seines bösen Herzens, ohne auf mich zu hören." },
      { number: 13, text: "Darum schleudere ich euch aus diesem Land hinaus, in das Land, das euch und euren Vätern unbekannt war. Dort mögt ihr anderen Göttern dienen Tag und Nacht; ich aber werde euch keine Gnade mehr schenken.", footnotes: ["Dtn 28,64"] },
      { number: 14, text: "[Darum seht, es werden Tage kommen - Spruch des Herrn -, da sagt man nicht mehr: So wahr der Herr lebt, der die Söhne Israels aus Ägypten heraufgeführt hat!,", footnotes: ["14f: Diese Verheißung ist aus 23,7f übernommen."] },
      { number: 15, text: "sondern: So wahr der Herr lebt, der die Söhne Israels aus dem Nordland und aus allen Ländern, in die er sie verstoßen hatte, heraufgeführt hat. Ich bringe sie zurück in ihr Heimatland, das ich ihren Vätern gegeben habe.]" },
      { number: 16, text: "Seht, ich hole viele Fischer - Spruch des Herrn -, die sollen sie fangen; dann hole ich viele Jäger, die sollen sie erlegen auf jedem Berg und Hügel und in den Felsenklüften." },
      { number: 17, text: "Denn meine Augen sehen alle ihre Wege; sie können sich vor mir nicht verstecken und ihre Schuld ist vor meinen Augen nicht verborgen." },
      { number: 18, text: "So vergelte ich zunächst nach dem Maß ihrer Schuld und Sünde, weil sie mein Land durch das Aas ihrer Scheusale entweiht und mein Erbteil mit ihren abscheulichen Götzen angefüllt haben." },
      { number: 19, text: "Herr, meine Kraft und meine Burg, / meine Zuflucht am Tag der Not! Zu dir kommen Völker von den Enden der Erde / und sagen: Nur Trug besaßen unsre Väter, / Wahngebilde, die nichts nützen.", heading: "Die Bekehrung der Völker: 16,19-21" },
      { number: 20, text: "Kann ein Mensch sich Götter machen? / Das sind doch keine Götter." },
      { number: 21, text: "Darum seht, ich bringe sie zur Erkenntnis; / ja, diesmal bringe ich sie zur Erkenntnis meiner Macht und meiner Gewalt und sie werden erkennen: / Mein Name ist Jahwe, der Herr." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Judas Sünde ist aufgeschrieben mit eisernem Griffel, / mit diamantenem Stift eingegraben in die Tafel ihres Herzens / und in die Hörner ihrer Altäre,", heading: "Judas Sünde und Strafe: 17,1-4", footnotes: ["Zu den Hörnern der Altäre vgl. die Anmerkung zu Lev 4,7."] },
      { number: 2, text: "damit auch ihre Söhne noch denken an ihre Altäre und Kultpfähle / bei den üppigen Bäumen und auf den hohen Hügeln des Berglands." },
      { number: 3, text: "Dein Vermögen und alle deine Schätze / gebe ich zur Plünderung preis als Lohn für all deine Sünden / in deinem ganzen Gebiet.", footnotes: ["15,13"] },
      { number: 4, text: "Du musst von deinem Erbteil lassen, / das ich dir gegeben habe. Ich mache dich zum Sklaven deiner Feinde / in einem Land, das du nicht kennst. Denn Feuer lodert auf in meinem Zorn, / für immer ist es entbrannt.", footnotes: ["15,14"] },
      { number: 5, text: "[So spricht der Herr:] Verflucht der Mann, der auf Menschen vertraut, / auf schwaches Fleisch sich stützt / und dessen Herz sich abwendet vom Herrn.", heading: "Fluch oder Segen: 17,5-8", footnotes: ["Die Einleitung fehlt in G; es folgt keine Gottesrede."] },
      { number: 6, text: "Er ist wie ein kahler Strauch in der Steppe, / der nie einen Regen kommen sieht; er bleibt auf dürrem Wüstenboden, / im salzigen Land, wo niemand wohnt." },
      { number: 7, text: "Gesegnet der Mann, der auf den Herrn sich verlässt / und dessen Hoffnung der Herr ist." },
      { number: 8, text: "Er ist wie ein Baum, der am Wasser gepflanzt ist / und am Bach seine Wurzeln ausstreckt: Er hat nichts zu fürchten, wenn Hitze kommt; / seine Blätter bleiben grün; auch in einem trockenen Jahr ist er ohne Sorge, / unablässig bringt er seine Früchte.", footnotes: ["Ps 1,3"] },
      { number: 9, text: "Arglistig ohnegleichen ist das Herz und unverbesserlich. / Wer kann es ergründen?", heading: "Weisheitssprüche: 17,9-11" },
      { number: 10, text: "Ich, der Herr, erforsche das Herz / und prüfe die Nieren, um jedem zu vergelten, wie es sein Verhalten verdient, / entsprechend der Frucht seiner Taten.", footnotes: ["Ps 7,10; Röm 2,6"] },
      { number: 11, text: "Wie ein Rebhuhn, das ausbrütet, / was es nicht gelegt hat, so ist ein Mensch, / der Reichtum durch Unrecht erwirbt. In der Mitte seiner Tage muss er ihn verlassen / und am Ende steht er als Narr da." },
      { number: 12, text: "Ein Thron der Herrlichkeit, erhaben von Anbeginn, / ist die Stätte unsres Heiligtums.", heading: "Das Gebet des verfolgten Propheten: 17,12-18", footnotes: ["3,17"] },
      { number: 13, text: "Du Hoffnung Israels, Herr! / Alle, die dich verlassen, werden zuschanden, die sich von dir abwenden, / werden in den Staub geschrieben; denn sie haben den Herrn verlassen, / den Quell lebendigen Wassers.", footnotes: ["Joh 8,6; Jer 2,13", "«In den Staub schreiben» ist Bild für die Vergänglichkeit; das in den Staub Geschriebene wird vom Wind sofort wieder zugeweht."] },
      { number: 14, text: "Heile mich, Herr, so bin ich heil, / hilf mir, so ist mir geholfen; / ja, mein Lobpreis bist du." },
      { number: 15, text: "Jene sagen zu mir: / Wo bleibt denn das Wort des Herrn? / Soll es doch eintreffen!", footnotes: ["Jes 5,19"] },
      { number: 16, text: "Ich aber habe dich nie gedrängt wegen des Unheils / und habe den Unglückstag nicht herbeigewünscht. Du weißt es selbst; / was mir über die Lippen kam, / liegt dir offen vor Augen." },
      { number: 17, text: "Werde nicht zum Schrecken für mich, / du meine Zuflucht am Tag des Unheils!" },
      { number: 18, text: "Meine Verfolger sollen zuschanden werden, / nicht aber ich. Sie sollen erschrecken, / nicht aber ich. Bring über sie den Tag des Unheils, / zerbrich sie im verdienten Zusammenbruch!" },
      { number: 19, text: "So sprach der Herr zu mir: Geh und stell dich in das Tor der Söhne des Volkes, durch das die Könige von Juda aus- und einziehen, und in alle Tore Jerusalems.", heading: "Die Sabbatheiligung: 17,19-27" },
      { number: 20, text: "Sag zu ihnen: Hört das Wort des Herrn, ihr Könige von Juda, ganz Juda und alle Einwohner Jerusalems, die ihr durch diese Tore kommt." },
      { number: 21, text: "So spricht der Herr: Hütet euch um eures Lebens willen, am Tag des Sabbats eine Last zu tragen und durch die Tore Jerusalems hereinzubringen.", footnotes: ["Neh 13,15-22"] },
      { number: 22, text: "Auch dürft ihr am Sabbat keine Last aus euren Häusern hinaustragen und keinerlei Arbeit verrichten. Vielmehr sollt ihr den Sabbat heiligen, wie ich es euren Vätern geboten habe.", footnotes: ["Ex 20,8; Dtn 5,12"] },
      { number: 23, text: "Doch sie haben nicht gehört und ihr Ohr mir nicht zugeneigt, sondern ihren Nacken versteift, ohne zu gehorchen und ohne Zucht anzunehmen." },
      { number: 24, text: "Ihr aber, wenn ihr bereitwillig auf mich hört - Spruch des Herrn und am Sabbat keine Last durch die Tore dieser Stadt bringt, sondern den Sabbat heiligt und an ihm keinerlei Arbeit verrichtet," },
      { number: 25, text: "dann werden durch die Tore dieser Stadt Könige einziehen, die auf dem Thron Davids sitzen; mit Wagen und Rossen werden sie fahren, sie und ihre Beamten, die Männer von Juda und die Einwohner Jerusalems, und diese Stadt wird für immer bewohnt sein.", footnotes: ["22,4"] },
      { number: 26, text: "Dann kommen von den Städten Judas und aus der Umgebung Jerusalems, vom Land Benjamin, von der Schefela, vom Gebirge und vom Negeb her Wallfahrer, die Brandopfer, Schlachtopfer und Speiseopfer samt Weihrauch bringen; auch Dankopfer bringen sie dar im Haus des Herrn." },
      { number: 27, text: "Wenn ihr aber nicht auf mein Gebot hört, den Sabbat zu heiligen, keine Last zu tragen und am Sabbat durch die Tore Jerusalems zu bringen, dann lege ich Feuer an seine Tore, das Jerusalems Paläste verzehrt und nie mehr erlischt." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging:", heading: "Das Gleichnis vom Töpfer: 18,1-17" },
      { number: 2, text: "Mach dich auf und geh zum Haus des Töpfers hinab! Dort will ich dir meine Worte mitteilen." },
      { number: 3, text: "So ging ich zum Haus des Töpfers hinab. Er arbeitete gerade mit der Töpferscheibe." },
      { number: 4, text: "Missriet das Gefäß, das er in Arbeit hatte, wie es beim Ton in der Hand des Töpfers vorkommen kann, so machte der Töpfer daraus wieder ein anderes Gefäß, ganz wie es ihm gefiel." },
      { number: 5, text: "Da erging an mich das Wort des Herrn:" },
      { number: 6, text: "Kann ich nicht mit euch verfahren wie dieser Töpfer, Haus Israel? Spruch des Herrn. Seht, wie der Ton in der Hand des Töpfers, so seid ihr in meiner Hand, Haus Israel.", footnotes: ["Jes 45,9; Röm 9,21"] },
      { number: 7, text: "Bald drohe ich einem Volk oder einem Reich, es auszureißen, niederzureißen und zu vernichten.", footnotes: ["Ez 33,11-20"] },
      { number: 8, text: "Kehrt aber das Volk, dem ich gedroht habe, um von seinem bösen Tun, so reut mich das Unheil, das ich ihm zugedacht hatte." },
      { number: 9, text: "Bald sage ich einem Volk oder einem Reich zu, es aufzubauen und einzupflanzen." },
      { number: 10, text: "Tut es aber dann, was mir missfällt, und hört es nicht auf meine Stimme, so reut mich das Gute, das ich ihm zugesagt habe." },
      { number: 11, text: "Und nun sag zu den Leuten von Juda und zu den Einwohnern Jerusalems: So spricht der Herr: Seht, ich bereite Unheil gegen euch vor und fasse einen Plan gegen euch. Kehrt doch um, ein jeder von seinem bösen Weg, und bessert euer Verhalten und euer Tun!" },
      { number: 12, text: "Aber sie werden sagen: Vergebliche Mühe! Wir wollen unseren eigenen Plänen folgen und jeder von uns will nach dem Trieb seines bösen Herzens handeln.", footnotes: ["6,16; 11,8"] },
      { number: 13, text: "Deshalb spricht der Herr: Fragt unter den Völkern, wer je Ähnliches gehört hat. Ganz Abscheuliches hat die Jungfrau Israel getan." },
      { number: 14, text: "Weicht denn das Felsgestein von der Landschaft, der Schnee vom Libanon, oder versiegen immer strömende Wasser, sprudelnde Quellen?" },
      { number: 15, text: "Mein Volk aber hat mich vergessen; nichtigen Götzen bringt es Opfer dar. Doch ich lasse sie straucheln auf ihren Wegen, den altgewohnten Bahnen, sodass sie auf ungebahnten Pfaden gehen müssen." },
      { number: 16, text: "Ich will ihr Land zu einem Ort des Entsetzens machen, zum Gespött für immer. Jeder, der dort vorbeikommt, wird sich entsetzen und den Kopf schütteln.", footnotes: ["19,8; 49,17; 50,13"] },
      { number: 17, text: "Wie der Ostwind zerstreue ich sie vor dem Feind. Ich zeige ihnen den Rücken und nicht das Gesicht am Tag ihres Verderbens." },
      { number: 18, text: "Sie aber sagten: / Kommt, lasst uns gegen Jeremia Pläne schmieden! Denn nie wird dem Priester die Weisung ausgehen, / dem Weisen der Rat und dem Propheten das Wort. Kommt, wir wollen ihn mit seinen eigenen Worten schlagen / und Acht geben auf alles, was er sagt.", heading: "Die Bitte um Bestrafung der Gegner: 18,18-23" },
      { number: 19, text: "Gib du, Herr, Acht auf mich / und höre das Gerede meiner Widersacher!" },
      { number: 20, text: "Darf man denn Gutes mit Bösem vergelten? / [Denn sie haben (mir) eine Grube gegraben. Denk daran, wie ich vor dir stand, / um zu ihren Gunsten zu sprechen / und deinen Zorn von ihnen abzuwenden.", footnotes: ["Das Eingeklammerte ist aus V. 22 eingedrungen."] },
      { number: 21, text: "Darum gib ihre Kinder dem Hunger preis / und liefere sie der Gewalt des Schwertes aus! Ihre Frauen sollen der Kinder beraubt / und zu Witwen werden, ihre Männer töte die Pest, / ihre jungen Männer erschlage das Schwert in der Schlacht." },
      { number: 22, text: "Geschrei soll man hören aus ihren Häusern, / wenn du plötzlich plündernde Horden über sie kommen lässt. Denn sie haben (mir) eine Grube gegraben, um mich zu fangen; / meinen Füßen haben sie Schlingen gelegt." },
      { number: 23, text: "Du aber, Herr, / du kennst all ihre Mordpläne gegen mich. Nimm für ihre Schuld keine Sühne an, / lösch bei dir ihre Sünde nicht aus! Lass sie zu Fall kommen vor deinen Augen, / handle an ihnen zur Zeit deines Zorns!", footnotes: ["Lk 23,34"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Der Herr sprach zu mir: Geh und kauf dir einen irdenen Krug, und nimm einige Älteste des Volkes und der Priester mit dir!", heading: "Drohreden und ihre Folgen: 19,1 - 20,6" },
      { number: 2, text: "Dann geh hinaus zum Tal Ben-Hinnom am Eingang des Scherbentors! Dort verkünde die Worte, die ich dir sage.", footnotes: ["2.6.11f: Zum Tal Ben-Hinnom und zum Tofet vgl. die Anmerkung zu 7,31."] },
      { number: 3, text: "Du sollst sagen: Hört das Wort des Herrn, ihr Könige und ihr Einwohner Jerusalems! So spricht der Herr der Heere, der Gott Israels: Seht, ich bringe solches Unheil über diesen Ort, dass jedem, der davon hört, die Ohren gellen." },
      { number: 4, text: "Denn sie haben mich verlassen, mir diesen Ort entfremdet und an ihm anderen Göttern geopfert, die ihnen, ihren Vätern und den Königen von Juda früher unbekannt waren. Mit dem Blut Unschuldiger haben sie diesen Ort angefüllt." },
      { number: 5, text: "Sie haben dem Baal eine Kulthöhe gebaut, um ihre Söhne als Brandopfer für den Baal im Feuer zu verbrennen, was ich nie befohlen oder angeordnet habe und was mir niemals in den Sinn gekommen ist.", footnotes: ["(5-6) 7,31-33"] },
      { number: 6, text: "Seht, darum werden Tage kommen - Spruch des Herrn -, da wird man diesen Ort nicht mehr Tofet oder Tal Ben-Hinnom nennen, sondern Mordtal." },
      { number: 7, text: "Dann vereitle ich die Pläne Judas und Jerusalems an diesem Ort. Ich bringe sie vor den Augen ihrer Feinde durch das Schwert zu Fall und durch die Hand derer, die ihnen nach dem Leben trachten. Ich gebe ihre Leichen den Vögeln des Himmels und den Tieren des Feldes zum Fraß." },
      { number: 8, text: "Ich mache diese Stadt zu einem Ort des Entsetzens und zum Gespött; jeder, der dort vorbeikommt, wird sich entsetzen und spotten über alle Schläge, die sie getroffen haben.", footnotes: ["18,16"] },
      { number: 9, text: "Ich gebe ihnen das Fleisch ihrer Söhne und Töchter zu essen; einer wird das Fleisch des andern verzehren in der Not und Bedrängnis, mit der ihre Feinde und alle, die ihnen nach dem Leben trachten, sie bedrängen. -", footnotes: ["Lev 26,29; Dtn 28,53; Bar 2,3"] },
      { number: 10, text: "Dann zerbrich den Krug vor den Augen der Männer, die mit dir gehen." },
      { number: 11, text: "Sag ihnen: So spricht der Herr der Heere: Ebenso zerbreche ich dieses Volk und diese Stadt, wie man Töpfergeschirr zerbricht, sodass es nie wieder heil werden kann. Im Tofet wird man Tote bestatten, weil sonst kein Platz ist zum Begraben.", footnotes: ["7,32"] },
      { number: 12, text: "So werde ich mit diesem Ort verfahren - Spruch des Herrn - und mit seinen Bewohnern, um diese Stadt dem Tofet gleich zu machen." },
      { number: 13, text: "Die Häuser Jerusalems und die Häuser der Könige von Juda sollen unrein werden wie der Ort des Tofet, alle Häuser, auf deren Dächern man dem ganzen Heer des Himmels Rauchopfer und anderen Göttern Trankopfer dargebracht hat. -", footnotes: ["32,29", "Das «Heer des Himmels» sind die Gestirngottheiten; man verehrte sie auf den flachen Hausdächern."] },
      { number: 14, text: "Als nun Jeremia vom Tofet zurückkam, wohin der Herr ihn zum Weissagen gesandt hatte, begab er sich in den Vorhof beim Haus des Herrn und sagte zu allem Volk:" },
      { number: 15, text: "So spricht der Herr der Heere, der Gott Israels: Seht, ich bringe über diese Stadt und über alle ihre Nachbarstädte all das Unheil, das ich ihr angedroht habe, weil sie ihren Nacken versteift haben und nicht auf meine Worte hören wollen." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Der Priester Paschhur, der Sohn des Immer, der Oberaufseher im Haus des Herrn, hörte, wie Jeremia diese prophetischen Worte verkündete." },
      { number: 2, text: "Da ließ Paschhur den Propheten Jeremia schlagen und in den Block spannen, der im oberen Benjamintor beim Haus des Herrn war." },
      { number: 3, text: "Als ihn Paschhur am nächsten Morgen aus dem Block entließ, sagte Jeremia zu ihm: Nicht mehr Paschhur nennt dich der Herr, sondern: «Grauen ringsum».", footnotes: ["20,10; 6,25; 46,5; 49,29; Ps 31,14"] },
      { number: 4, text: "Denn so spricht der Herr: Ja, ich gebe dich dem Grauen preis, dich und alle deine Freunde. Sie werden unter dem Schwert ihrer Feinde fallen und du musst mit eigenen Augen zusehen. Ganz Juda aber gebe ich in die Hand des Königs von Babel; er wird sie nach Babel wegführen und mit dem Schwert erschlagen." },
      { number: 5, text: "Auch allen Besitz dieser Stadt, all ihre Habe, alles Kostbare und alle Schätze der Könige von Juda gebe ich in die Hand ihrer Feinde; sie werden alles rauben, wegschleppen und nach Babel bringen." },
      { number: 6, text: "Du aber, Paschhur, und alle deine Hausgenossen, ihr werdet in die Verbannung ziehen; nach Babel wirst du kommen, dort wirst du sterben und dort begraben werden, du und alle deine Freunde, denen du Lügen geweissagt hast." },
      { number: 7, text: "Du hast mich betört, o Herr, / und ich ließ mich betören; / du hast mich gepackt und überwältigt. Zum Gespött bin ich geworden den ganzen Tag, / ein jeder verhöhnt mich.", heading: "Das Prophetenschicksal: 20,7-18", footnotes: ["7-18: In seiner Verbitterung macht Jeremia seinem Gott selbst den Vorwurf, dass er an seinem Unglück schuld sei, und verflucht sein Schicksal; doch bald ringt er sich wieder zu neuem Vertrauen durch. Er ähnelt darin Ijob."] },
      { number: 8, text: "Ja, sooft ich rede, muss ich schreien, / «Gewalt und Unterdrückung!», muss ich rufen. Denn das Wort des Herrn bringt mir / den ganzen Tag nur Spott und Hohn." },
      { number: 9, text: "Sagte ich aber: Ich will nicht mehr an ihn denken / und nicht mehr in seinem Namen sprechen!, so war es mir, als brenne in meinem Herzen ein Feuer, / eingeschlossen in meinem Innern. Ich quälte mich es auszuhalten / und konnte nicht;" },
      { number: 10, text: "hörte ich doch das Flüstern der Vielen: / Grauen ringsum! Zeigt ihn an! / Wir wollen ihn anzeigen. Meine nächsten Bekannten / warten alle darauf, dass ich stürze: Vielleicht lässt er sich betören, / dass wir ihm beikommen können und uns an ihm rächen." },
      { number: 11, text: "Doch der Herr steht mir bei wie ein gewaltiger Held. / Darum straucheln meine Verfolger und kommen nicht auf. Sie werden schmählich zuschanden, da sie nichts erreichen, / in ewiger, unvergesslicher Schmach." },
      { number: 12, text: "Aber der Herr der Heere prüft den Gerechten, / er sieht Herz und Nieren. Ich werde deine Rache an ihnen erleben; / denn dir habe ich meine Sache anvertraut.", footnotes: ["11,20"] },
      { number: 13, text: "Singt dem Herrn, rühmt den Herrn; / denn er rettet das Leben des Armen aus der Hand der Übeltäter. -" },
      { number: 14, text: "Verflucht der Tag, an dem ich geboren wurde; / der Tag, an dem meine Mutter mich gebar, sei nicht gesegnet.", footnotes: ["Ijob 3,1-12; Jer 15,10"] },
      { number: 15, text: "Verflucht der Mann, / der meinem Vater die frohe Kunde brachte: Ein Kind, ein Knabe ist dir geboren!, / und ihn damit hoch erfreute." },
      { number: 16, text: "Jener Tag gleiche den Städten, / die der Herr ohne Erbarmen zerstört hat. Er höre Wehgeschrei am Morgen / und Kriegslärm um die Mittagszeit," },
      { number: 17, text: "weil er mich nicht sterben ließ im Mutterleib. / So wäre meine Mutter mir zum Grab geworden, / ihr Schoß auf ewig schwanger geblieben." },
      { number: 18, text: "Warum denn kam ich hervor aus dem Mutterschoß, / um nur Mühsal und Kummer zu erleben / und meine Tage in Schande zu beenden?" },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging, als König Zidkija den Paschhur, den Sohn Malkijas, und den Priester Zefanja, den Sohn Maasejas, zu ihm sandte mit dem Auftrag:", heading: "Der Beginn der Belagerung Jerusalems: 21,1-10" },
      { number: 2, text: "Befrag doch den Herrn für uns! Denn Nebukadnezzar, der König von Babel, führt gegen uns Krieg; vielleicht handelt der Herr an uns wie bei all seinen früheren Wundern, sodass Nebukadnezzar von uns abziehen muss." },
      { number: 3, text: "Jeremia aber antwortete ihnen: Meldet Zidkija Folgendes:" },
      { number: 4, text: "So spricht der Herr, der Gott Israels: Fürwahr, ich drehe in eurer Hand die Waffen um, mit denen ihr vor der Mauer gegen den König von Babel und die Chaldäer, die euch belagern, kämpft, und hole sie ins Innere dieser Stadt." },
      { number: 5, text: "Ich selbst kämpfe gegen euch mit hoch erhobener Hand und starkem Arm, mit Zorn, Grimm und großem Groll." },
      { number: 6, text: "Ich schlage die Einwohner dieser Stadt, Mensch und Vieh; an schwerer Pest sollen sie sterben." },
      { number: 7, text: "Und danach - Spruch des Herrn - liefere ich Zidkija, den König von Juda, seine Diener und das Volk, das in dieser Stadt der Pest, dem Schwert und dem Hunger entronnen ist, der Hand Nebukadnezzars, des Königs von Babel, aus, der Hand ihrer Feinde und der Hand derer, die ihnen nach dem Leben trachten. Er wird sie mit scharfem Schwert erschlagen, ohne Mitleid, ohne Schonung, ohne Erbarmen." },
      { number: 8, text: "Zu diesem Volk aber sollst du sagen: So spricht der Herr: Seht, den Weg des Lebens und den Weg des Todes stelle ich euch zur Wahl." },
      { number: 9, text: "Wer in dieser Stadt bleibt, der stirbt durch Schwert, Hunger und Pest. Wer aber hinausgeht und sich den Chaldäern, die euch belagern, ergibt, der wird überleben und sein Leben wie ein Beutestück gewinnen.", footnotes: ["38,2"] },
      { number: 10, text: "Denn ich habe mein Angesicht gegen diese Stadt gerichtet zu ihrem Unheil, nicht zu ihrem Heil - Spruch des Herrn. Der Hand des Königs von Babel wird sie ausgeliefert und er wird sie mit Feuer verbrennen." },
      { number: 11, text: "An das Haus des Königs von Juda: / Hört das Wort des Herrn, Haus David!", heading: "Die Botschaft an die Regierung: 21,11-14" },
      { number: 12, text: "So spricht der Herr: / Haltet jeden Morgen gerechtes Gericht! Rettet den Ausgeplünderten / aus der Hand des Gewalttäters! Sonst bricht mein Zorn wie Feuer los; / er brennt und niemand kann löschen.", footnotes: ["22,3; 4,4"] },
      { number: 13, text: "Nun gehe ich gegen dich vor, du Stadt, / die du in der Mulde des Felsengebirges / über der Ebene wohnst - Spruch des Herrn. Ihr freilich sagt: Wer kann über uns kommen / und eindringen in unsere Bauten?" },
      { number: 14, text: "Ich zahle euch heim, wie es eure Taten verdienen / - Spruch des Herrn. Ich lege Feuer an den Wald dieser Stadt, / das ringsum alles verzehrt.", footnotes: ["50,32", "Mit dem «Wald dieser Stadt» sind wohl die mit Zedernholz erbauten Paläste Jerusalems gemeint (vgl. 22,7; 1 Kön 7,2)."] },
    ] },
    { number: 22, verses: [
      { number: 1, text: "So hat der Herr gesprochen: Geh hinab in den Palast des Königs von Juda und rede dort folgende Worte!", heading: "Das Drohwort im Königspalast: 22,1-9" },
      { number: 2, text: "Du sollst sagen: König von Juda, der du auf dem Thron Davids sitzt, höre das Wort des Herrn, du selbst, deine Diener und deine Leute, die durch diese Tore kommen." },
      { number: 3, text: "So spricht der Herr: Sorgt für Recht und Gerechtigkeit und rettet den Ausgeplünderten aus der Hand des Gewalttäters! Fremde, Waisen und Witwen bedrängt und misshandelt nicht; vergießt kein unschuldiges Blut an diesem Ort!", footnotes: ["21,12"] },
      { number: 4, text: "Wenn ihr wirklich dieses Wort erfüllt, dann werden durch die Tore dieses Palastes Könige einziehen, die auf dem Thron Davids sitzen; mit Wagen und Rossen werden sie fahren, sie selbst, ihre Beamten und ihre Leute.", footnotes: ["17,25"] },
      { number: 5, text: "Hört ihr aber nicht auf diese Worte, so schwöre ich bei mir selbst Spruch des Herrn: Zum Trümmerhaufen wird dieser Palast." },
      { number: 6, text: "Ja, so spricht der Herr gegen den Palast des Königs von Juda: Giltst du mir auch so viel wie Gilead, wie der Gipfel des Libanon, fürwahr, ich mache dich zur Wüste, zur unbewohnten Stadt.", footnotes: ["Gilead ist ein fruchtbares Gebiet östlich des Jordan."] },
      { number: 7, text: "Ich biete Verwüster gegen dich auf, die mit ihren Äxten kommen, deine auserlesenen Zedern umhauen und ins Feuer werfen." },
      { number: 8, text: "Wenn dann Leute aus vielen Völkern an dieser Stadt vorbeikommen und einander fragen: Warum hat der Herr so an dieser großen Stadt gehandelt?," },
      { number: 9, text: "wird man erwidern: Weil sie den Bund mit dem Herrn, ihrem Gott, aufgegeben, andere Götter angebetet und ihnen gedient haben." },
      { number: 10, text: "Weint nicht um den Toten und beklagt ihn nicht! Weint vielmehr um den, der fortmusste; denn er kehrt nie wieder zurück, nie mehr sieht er sein Heimatland.", heading: "Gegen Schallum: 22,10-12", footnotes: ["10f: Der tüchtige, in der Schlacht bei Megiddo gefallene König Joschija wurde sehr betrauert (609 v. Chr.). Sein Sohn Schallum, auch Joahas genannt, wurde vom Pharao Necho gefangen genommen und nach Ägypten verschleppt (vgl. 2 Kön 23,31-34)."] },
      { number: 11, text: "Denn so spricht der Herr über Schallum, den Sohn Joschijas, den König von Juda, der seinem Vater Joschija in der Regierung gefolgt war und wegziehen musste von diesem Ort: Nie mehr kommt er hierher zurück." },
      { number: 12, text: "An dem Ort, wohin man ihn verschleppt hat, dort wird er sterben und dieses Land nie wieder sehen." },
      { number: 13, text: "Weh dem, der seinen Palast mit Ungerechtigkeit baut, / seine Gemächer mit Unrecht, der seinen Nächsten ohne Entgelt arbeiten lässt / und ihm seinen Lohn nicht gibt,", heading: "Gegen Jojakim: 22,13-19", footnotes: ["Mi 3,10; Lev 19,13"] },
      { number: 14, text: "der sagt: Ich baue mir einen stattlichen Palast / und weite Gemächer. Er setzt ihm hohe Fenster ein, / täfelt ihn mit Zedernholz / und bemalt ihn mit Mennigrot." },
      { number: 15, text: "Bist du König geworden, / um mit Zedern zu prunken? Hat dein Vater nicht auch gegessen und getrunken, / dabei aber für Recht und Gerechtigkeit gesorgt? / Und es ging ihm gut." },
      { number: 16, text: "Dem Schwachen und Armen verhalf er zum Recht. / Heißt nicht das, mich wirklich erkennen? / - Spruch des Herrn." },
      { number: 17, text: "Doch deine Augen und dein Herz / sind nur auf deinen Vorteil gerichtet, auf das Blut des Unschuldigen, das du vergießt, / auf Bedrückung und Erpressung, die du verübst.", footnotes: ["2 Kön 24,1-4"] },
      { number: 18, text: "Darum - so spricht der Herr über Jojakim, / den Sohn Joschijas, den König von Juda: Man wird für ihn nicht die Totenklage halten: / «Ach, mein Bruder! Ach, Schwester!» Man wird für ihn nicht die Totenklage halten: / «Ach, der Herrscher! Ach, seine Majestät!»", footnotes: ["34,5"] },
      { number: 19, text: "Ein Eselsbegräbnis wird er bekommen. / Man schleift ihn weg und wirft ihn hin, / draußen vor den Toren Jerusalems.", footnotes: ["36,30", "Von einem «Eselsbegräbnis» weiß 2 Kön 24,6 nichts. Jojakim starb 597 v. Chr., noch vor Beginn der Belagerung Jerusalems, und dürfte ein normales Königsbegräbnis erhalten haben."] },
      { number: 20, text: "(Jerusalem,) steig auf den Libanon und schrei, / im Baschan erheb deine Stimme, schrei vom Abarimgebirge herab, / dass alle deine Freunde zerschmettert sind.", heading: "Jerusalems Sturz: 22,20-23" },
      { number: 21, text: "Ich habe dir zugeredet, als du dich noch sicher fühltest; / du aber hast gesagt: Ich höre nicht. So hast du es getrieben von Jugend an: / Du hast auf meine Stimme nicht gehört." },
      { number: 22, text: "All deine Hirten wird der Wind weiden, / deine Freunde müssen fort in die Gefangenschaft. Dann wirst du Schmach und Schande ernten / wegen all deiner Untaten." },
      { number: 23, text: "Die du auf dem Libanon thronst (Jerusalem), / in Zedern nistest, wie wirst du stöhnen, wenn Wehen über dich kommen, / Schmerzen wie die einer Gebärenden." },
      { number: 24, text: "So wahr ich lebe - Spruch des Herrn -, selbst wenn Jojachin, der Sohn Jojakims und König von Juda, ein Siegelring an meiner Rechten wäre, ich risse dich weg.", heading: "Gegen Jojachin: 22,24-30", footnotes: ["2 Kön 24,8-16", "In H heißt der König Konja, ein Kurzname für Jojachin. Kurz nach der Thronbesteigung beginnt die Belagerung Jerusalems durch Nebukadnezzar. Jojachin ergibt sich bald, wird abgesetzt und als Gefangener nach Babylon gebracht."] },
      { number: 25, text: "Ich gebe dich in die Hand derer, die dir nach dem Leben trachten, in die Hand derer, vor denen dir graut, in die Hand Nebukadnezzars, des Königs von Babel, und in die Hand der Chaldäer." },
      { number: 26, text: "Ich schleudere dich samt deiner Mutter, die dich gebar, in ein anderes Land, in dem ihr nicht geboren seid, und dort werdet ihr sterben." },
      { number: 27, text: "In das Land aber, nach dem ihr Herz sehnlich zurückverlangt, werden sie nie wieder kommen. -" },
      { number: 28, text: "Ist denn dieser Mann Jojachin ein verachtetes, zerschlagenes Gefäß oder ein Gerät, das niemand mehr mag? Warum wird er fortgeschleudert und hingeworfen in ein Land, das er nicht kennt?" },
      { number: 29, text: "Land, Land, Land, höre das Wort des Herrn!" },
      { number: 30, text: "So spricht der Herr: Schreibt diesen Mann als kinderlos ein, als Mann, der in seinem Leben kein Glück hat. Denn keinem seiner Nachkommen wird es glücken, sich auf den Thron Davids zu setzen und wieder über Juda zu herrschen.", footnotes: ["als kinderlos: Er steht als kinderlos in der davidischen Königsliste verzeichnet. Jojachin hatte zwar Söhne, doch kamen sie nicht an die Regierung, sodass er keinen Nachfolger hatte."] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Weh den Hirten, die die Schafe meiner Weide zugrunde richten und zerstreuen - Spruch des Herrn.", heading: "Der Davidspross: 23,1-8", footnotes: ["10,21"] },
      { number: 2, text: "Darum - so spricht der Herr, der Gott Israels, über die Hirten, die mein Volk weiden: Ihr habt meine Schafe zerstreut und versprengt und habt euch nicht um sie gekümmert. Jetzt ziehe ich euch zur Rechenschaft wegen eurer bösen Taten - Spruch des Herrn." },
      { number: 3, text: "Ich selbst aber sammle den Rest meiner Schafe aus allen Ländern, wohin ich sie versprengt habe. Ich bringe sie zurück auf ihre Weide; sie sollen fruchtbar sein und sich vermehren." },
      { number: 4, text: "Ich werde für sie Hirten bestellen, die sie weiden, und sie werden sich nicht mehr fürchten und ängstigen und nicht mehr verloren gehen Spruch des Herrn.", footnotes: ["3,15"] },
      { number: 5, text: "Seht, es kommen Tage - Spruch des Herrn -, da werde ich für David einen gerechten Spross erwecken. Er wird als König herrschen und weise handeln, für Recht und Gerechtigkeit wird er sorgen im Land.", footnotes: ["(5-6) 33,15f; Jes 16,5; 32,1; Sach 3,8"] },
      { number: 6, text: "In seinen Tagen wird Juda gerettet werden, Israel kann in Sicherheit wohnen. Man wird ihm den Namen geben: Der Herr ist unsere Gerechtigkeit.", footnotes: ["(7-8) 16,14f"] },
      { number: 7, text: "Darum seht, es werden Tage kommen - Spruch des Herrn -, da sagt man nicht mehr: So wahr der Herr lebt, der die Söhne Israels aus Ägypten heraufgeführt hat!," },
      { number: 8, text: "sondern: So wahr der Herr lebt, der das Geschlecht des Hauses Israel aus dem Nordland und aus allen Ländern, in die er sie verstoßen hatte, heraufgeführt und zurückgebracht hat. Dann werden sie wieder in ihrem Heimatland wohnen." },
      { number: 9, text: "Über die Propheten: Mir bricht das Herz in der Brust, / alle meine Glieder zittern. Wie ein Betrunkener bin ich, / wie ein Mann, der vom Wein überwältigt ist, / wegen des Herrn und seiner heiligen Worte:", heading: "Gegen die falschen Propheten: 23,9-32" },
      { number: 10, text: "Voll von Ehebrechern ist das Land; / ja, wegen des Fluches vertrocknet das Land, / sind die Weideplätze der Steppe verdorrt. Schlechtigkeit ist ihr Ziel, / Unrecht ihre Stärke.", footnotes: ["12,4; Jes 24,6", "wegen des Fluches: G: ihretwegen (d. h. wegen der Ehebrecher)."] },
      { number: 11, text: "Sogar Prophet und Priester sind ruchlose Frevler, / selbst in meinem Haus stoße ich auf ihre Schlechtigkeit / - Spruch des Herrn." },
      { number: 12, text: "Deshalb wird ihr Weg für sie / wie ein schlüpfriger Pfad; sie stürzen in der Finsternis, / sie kommen darin zu Fall. Denn Unheil bringe ich über sie, / das Jahr ihrer Bestrafung - Spruch des Herrn." },
      { number: 13, text: "Zwar habe ich auch bei Samarias Propheten / Hässliches gesehen: Sie weissagten im Namen des Baal / und verführten mein Volk Israel." },
      { number: 14, text: "Aber bei den Propheten Jerusalems / sah ich grauenhafte Dinge: Sie brechen die Ehe, gehen mit Lügen um und bestärken die Bösen, / sodass keiner umkehrt von seinem bösen Treiben. Für mich sind alle wie Sodom, / Jerusalems Einwohner sind für mich wie Gomorra." },
      { number: 15, text: "Darum - so spricht der Herr der Heere gegen die Propheten: Ich gebe ihnen Wermut zu essen / und Giftwasser zu trinken; denn von den Propheten Jerusalems / ist Frevel ausgegangen ins ganze Land.", footnotes: ["9,14"] },
      { number: 16, text: "So spricht der Herr der Heere: / Hört nicht auf die Worte der Propheten, / die euch weissagen. Sie betören euch nur; sie verkünden Visionen, / die aus dem eigenen Herzen stammen, / nicht aus dem Mund des Herrn.", footnotes: ["14,14"] },
      { number: 17, text: "Immerzu sagen sie denen, die das Wort des Herrn verachten: / Das Heil ist euch sicher!; und jedem, der dem Trieb seines Herzens folgt, / versprechen sie: Kein Unheil kommt über euch." },
      { number: 18, text: "Doch wer hat an der Ratsversammlung des Herrn teilgenommen, / hat ihn gesehen und sein Wort gehört? Wer hat sein Wort vernommen / und kann es verkünden?", footnotes: ["Jes 40,13; 1 Kön 22,19-23"] },
      { number: 19, text: "Hört, der Sturm des Herrn [sein Grimm] bricht los. / Ein Wirbelsturm braust hinweg über die Köpfe der Frevler.", footnotes: ["(19-20) 30,23f"] },
      { number: 20, text: "Der Zorn des Herrn hört nicht auf, / bis er die Pläne seines Herzens ausgeführt und vollbracht hat. / Am Ende der Tage werdet ihr es klar erkennen." },
      { number: 21, text: "Ich habe diese Propheten nicht ausgesandt, / dennoch laufen sie; ich habe nicht zu ihnen gesprochen, / dennoch weissagen sie." },
      { number: 22, text: "Hätten sie an meiner Ratsversammlung teilgenommen, / so könnten sie meinem Volk meine Worte verkünden, / damit es umkehrt von seinem schlechten Weg / und von seinen bösen Taten." },
      { number: 23, text: "Bin ich denn ein Gott aus der Nähe - Spruch des Herrn - / und nicht vielmehr ein Gott aus der Ferne?", footnotes: ["Die unendliche Distanz Gottes zu allem Irdischen wird betont. Ein «Gott aus der Nähe» wäre den Menschen verfügbar."] },
      { number: 24, text: "Kann sich einer in Schlupfwinkeln verstecken, / sodass ich ihn nicht sähe? - Spruch des Herrn. Bin nicht ich es, / der Himmel und Erde erfüllt? - Spruch des Herrn." },
      { number: 25, text: "Ich habe gehört, was die Propheten reden, die in meinem Namen Lügen weissagen und sprechen: Einen Traum habe ich gehabt, einen Traum." },
      { number: 26, text: "Wie lange noch? Haben sie denn wirklich etwas in sich, die Propheten, die Lügen weissagen und selbst erdachten Betrug?" },
      { number: 27, text: "Durch ihre Träume, die sie einander erzählen, möchten sie meinen Namen in Vergessenheit bringen bei meinem Volk, wie ihre Väter meinen Namen wegen des Baal vergessen haben." },
      { number: 28, text: "Der Prophet, der einen Traum hat, erzählt nur einen Traum; wer aber mein Wort hat, der verkündet wahrhaftig mein Wort. Was hat das Stroh mit dem Korn zu tun? - Spruch des Herrn." },
      { number: 29, text: "Ist nicht mein Wort wie Feuer - Spruch des Herrn - und wie ein Hammer, der Felsen zerschmettert?" },
      { number: 30, text: "Darum gehe ich nun gegen die Propheten vor - Spruch des Herrn -, die einander meine Worte stehlen." },
      { number: 31, text: "Nun gehe ich gegen die Propheten vor - Spruch des Herrn -, die ihre Zunge gebrauchen, um Sprüche zu machen." },
      { number: 32, text: "Ja, nun gehe ich gegen die Propheten mit ihren erlogenen Träumen vor - Spruch des Herrn; sie erzählen die Träume und verführen mein Volk durch ihre Lügen und ihr freches Geschwätz. Ich aber habe sie weder gesandt noch beauftragt, und sie sind diesem Volk ganz unnütz Spruch des Herrn." },
      { number: 33, text: "Fragt dich dieses Volk oder ein Prophet oder ein Priester: Was ist der «Last-Spruch» des Herrn?, so antworte ihnen: Ihr selbst seid die Last und ich werfe euch ab - Spruch des Herrn.", heading: "Die «Last» des Herrn: 23,33-40", footnotes: ["33.36: Das hebräische Wort massa bedeutet sowohl «Last» als auch «Ausspruch» (des Propheten); vgl. Jes 13,1 und öfter. Das Verbot erklärt sich wohl daher, dass man darüber gespottet hat."] },
      { number: 34, text: "Den Propheten aber, den Priester und das Volk, jeden, der sagt: «Last-Spruch des Herrn», den ziehe ich samt seinem Haus zur Rechenschaft - Spruch des Herrn." },
      { number: 35, text: "Vielmehr sollt ihr so zueinander und untereinander sagen: Was hat der Herr geantwortet?, oder: Was hat der Herr gesagt?" },
      { number: 36, text: "Aber den Ausdruck «Last-Spruch des Herrn» sollt ihr nicht mehr gebrauchen. Denn «die Last» ist für jeden sein eigenes Wort, weil ihr die Worte des lebendigen Gottes, des Herrn der Heere, unseres Gottes, verdreht habt." },
      { number: 37, text: "So soll man zum Propheten sagen: Was hat der Herr dir geantwortet?, oder: Was hat der Herr gesagt?" },
      { number: 38, text: "Wenn ihr aber «Last-Spruch des Herrn», sagt, so sagt der Herr darauf: Weil ihr dieses Wort «Last-Spruch des Herrn» gebraucht, obwohl ich euch verbieten ließ, «Last-Spruch des Herrn» zu sagen," },
      { number: 39, text: "darum hebe ich euch empor und schleudere euch samt der Stadt, die ich euch und euren Vätern gegeben habe, weg von meinem Angesicht." },
      { number: 40, text: "Ich verhänge über euch ewige Schande und ewige Schmach, die nie vergessen werden soll.", footnotes: ["20,11"] },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Der Herr ließ mich schauen: Da standen zwei Körbe mit Feigen vor dem Tempel des Herrn. Dies geschah, nachdem Nebukadnezzar, der König von Babel, Jojachin, den Sohn Jojakims, den König von Juda, sowie die Großen von Juda samt den Schmieden und Schlossern aus Jerusalem weggeführt und nach Babel gebracht hatte.", heading: "Die beiden Feigenkörbe: 24,1-10", footnotes: ["2 Kön 24,14-16"] },
      { number: 2, text: "In dem einen Korb waren sehr gute Feigen, wie Frühfeigen, im andern Korb sehr schlechte Feigen, so schlecht, dass sie ungenießbar waren." },
      { number: 3, text: "Der Herr fragte mich: Was siehst du, Jeremia? Feigen, antwortete ich. Die guten Feigen sind sehr gut, die schlechten aber sehr schlecht, so schlecht, dass sie ungenießbar sind." },
      { number: 4, text: "Nun erging an mich das Wort des Herrn:" },
      { number: 5, text: "So spricht der Herr, der Gott Israels: Wie auf diese guten Feigen, so schaue ich liebevoll auf die Verschleppten aus Juda, die ich von diesem Ort vertrieben habe ins Land der Chaldäer." },
      { number: 6, text: "Ich richte meine Augen liebevoll auf sie und lasse sie in dieses Land heimkehren. Ich will sie aufbauen, nicht niederreißen, einpflanzen, nicht ausreißen.", footnotes: ["1,10; 31,28"] },
      { number: 7, text: "Ich gebe ihnen ein Herz, damit sie erkennen, dass ich der Herr bin. Sie werden mein Volk sein und ich werde ihr Gott sein; denn sie werden mit ganzem Herzen zu mir umkehren.", footnotes: ["31,33; 30,22; Lev 26,12; Dtn 26,17f"] },
      { number: 8, text: "Aber wie mit den schlechten Feigen, die so schlecht sind, dass sie ungenießbar sind, [ja, so spricht der Herr] so verfahre ich mit Zidkija, dem König von Juda, mit seinen Großen und dem Rest Jerusalems, mit denen, die in diesem Land übrig geblieben sind, und denen, die sich in Ägypten niedergelassen haben.", footnotes: ["29,17f", "Die wiederholte Beteuerung, dass es sich um ein Gotteswort handelt (VV. 4f), ist wohl späterer Zusatz."] },
      { number: 9, text: "Ich mache sie zu einem Bild des Schreckens für alle Reiche der Erde, zum Schimpf und Gespött, zum Hohn und zum Fluch an allen Orten, an die ich sie verstoße.", footnotes: ["15,4"] },
      { number: 10, text: "Ich sende unter sie Schwert, Hunger und Pest, bis sie ganz ausgerottet sind aus dem Land, das ich ihnen und ihren Vätern gegeben habe." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Das Wort über das ganze Volk von Juda, das an Jeremia erging im vierten Jahr Jojakims, des Sohnes Joschijas, des Königs von Juda; es war das erste Jahr Nebukadnezzars, des Königs von Babel.", heading: "Das drohende Exil: 25,1-14", footnotes: ["36,1", "Das 4. Jahr Jojakims ist 605 v. Chr.; von da ab gewann Nebukadnezzar auch in Palästina die Oberhand."] },
      { number: 2, text: "Der Prophet Jeremia richtete es an das ganze Volk von Juda und an alle Einwohner Jerusalems; er sagte:" },
      { number: 3, text: "Seit dem dreizehnten Jahr Joschijas, des Sohnes Amons, des Königs von Juda, bis zum heutigen Tag, also dreiundzwanzig Jahre lang, ist an mich das Wort des Herrn ergangen und ich habe es euch unermüdlich weitergegeben. [Ihr aber habt nicht gehört.", footnotes: ["3-7: Das Ende von V. 3 sowie V. 4 und V. 6 durchbrechen den Zusammenhang; wohl Zusätze nach wie 7,25f; 35,15. Auch V. 7 ist größtenteils Zusatz; nur 7a gehört in die Rede Jeremias."] },
      { number: 4, text: "Der Herr hat immer wieder alle seine Knechte, die Propheten, zu euch gesandt. Ihr aber habt nicht gehört und euer Ohr nicht geneigt, um zu hören.]", footnotes: ["11,7; 26,5; 44,4"] },
      { number: 5, text: "Ich sagte: Kehrt doch alle um von eurem schlechten Weg und von euren bösen Taten; dann dürft ihr in dem Land bleiben, das der Herr euch und euren Vätern gegeben hat seit jeher und für immer." },
      { number: 6, text: "[Lauft nicht anderen Göttern nach, um ihnen zu dienen und sie anzubeten, und erzürnt mich nicht durch das Werk eurer Hände, damit ich euch nicht Schlimmes antun muss.]" },
      { number: 7, text: "Aber ihr habt nicht auf mich gehört [- Spruch des Herrn -, um mich zu erzürnen durch das Werk eurer Hände, zu eurem eigenen Schaden]." },
      { number: 8, text: "Darum - so spricht der Herr der Heere: Weil ihr auf meine Worte nicht gehört habt," },
      { number: 9, text: "darum hole ich alle Stämme des Nordens herbei - Spruch des Herrn -, auch Nebukadnezzar, den König von Babel, meinen Knecht. Ich lasse sie über dieses Land und seine Bewohner kommen und über alle diese Völker ringsum. Ich weihe sie dem Untergang und mache sie zu einem Bild des Entsetzens, zum Gespött und zur dauernden Schmach.", footnotes: ["9-14: Dass auch die umliegenden Völker bestraft werden (V. 9 und V. 11), erscheint hier noch zu früh; es ist wohl Erweiterung im Hinblick auf VV. 15-29; auch die VV. 12 und 14 sind kaum ursprünglich."] },
      { number: 10, text: "Ich lasse bei ihnen aufhören den Jubelruf und den Freudenruf, den Ruf des Bräutigams und den Ruf der Braut, das Geräusch der Handmühle und das Licht der Lampe.", footnotes: ["7,34"] },
      { number: 11, text: "Dieses ganze Land wird zum Trümmerfeld und zu einem Bild des Entsetzens und diese Völker werden dem König von Babel siebzig Jahre lang dienen.", footnotes: ["29,10"] },
      { number: 12, text: "Sind aber die siebzig Jahre vorüber, dann ziehe ich den König von Babel und jenes Volk zur Rechenschaft für ihre Schuld - Spruch des Herrn - und auch das Land der Chaldäer, indem ich es für immer zur schaurigen Wüste mache." },
      { number: 13, text: "Ich lasse über jenes Land all das kommen, was ich ihm angedroht habe, alles, was in diesem Buch aufgezeichnet ist [was Jeremia über alle Völker geweissagt hat]." },
      { number: 14, text: "Denn auch sie werden mächtigen Völkern und großen Königen dienen müssen. So vergelte ich ihnen entsprechend ihren Taten und dem Tun ihrer Hände.", footnotes: ["27,7"] },
      { number: 15, text: "Ja, so hat der Herr, der Gott Israels, zu mir gesprochen: Nimm diesen Becher voll Zornwein aus meiner Hand und gib ihn allen Völkern zu trinken, zu denen ich dich sende.", heading: "Die Einleitung zu den Gerichtsworten über die Völker: 25,15-38 | Der Zornbecher für die Völker: 25,15-29" },
      { number: 16, text: "Trinken sollen sie, taumeln und torkeln vor dem Schwert, das ich unter sie schicke." },
      { number: 17, text: "Da nahm ich den Becher aus der Hand des Herrn und ließ alle Völker trinken, zu denen der Herr mich sandte:", footnotes: ["Jes 51,17"] },
      { number: 18, text: "Jerusalem und die Städte Judas - samt seinen Königen und Fürsten -, um sie zu Trümmerhaufen zu machen, zu einem Bild des Entsetzens, zum Gespött und zum Fluch [wie es heute ist],", footnotes: ["Das Eingeklammerte fehlt zu Recht in G."] },
      { number: 19, text: "den Pharao, den König von Ägypten, samt seinen Höflingen und Fürsten und seinem ganzen Volk," },
      { number: 20, text: "das gesamte Völkergemisch, alle Könige des Landes Uz, alle Könige des Philisterlandes, (die Städte) Aschkelon, Gaza, Ekron und den Rest von Aschdod," },
      { number: 21, text: "Edom, Moab und die Ammoniter," },
      { number: 22, text: "alle Könige von Tyrus, alle Könige von Sidon sowie die Könige der Inseln jenseits des Meeres," },
      { number: 23, text: "Dedan, Tema, Bus und alle mit gestutztem Haar,", footnotes: ["Arabische Beduinen haben die Ränder des Kopfhaars gestutzt (vgl. 9,25; 49,32)."] },
      { number: 24, text: "alle Könige Arabiens und alle Könige des Völkergemisches, die in der Wüste wohnen," },
      { number: 25, text: "alle Könige von Simri, von Elam und Medien," },
      { number: 26, text: "auch alle Könige des Nordens, die in der Nähe und die in der Ferne, einen nach dem andern, ja, alle Reiche [der Welt], die es auf der Erde gibt; und zuletzt soll der König von Scheschach trinken.", footnotes: ["Scheschach ist ein anderer Name für Babel."] },
      { number: 27, text: "Sag zu ihnen: So spricht der Herr der Heere, der Gott Israels: Trinkt, berauscht euch und speit, stürzt hin und steht nicht mehr auf vor dem Schwert, das ich unter euch schicke." },
      { number: 28, text: "Weigern sie sich aber, den Becher aus deiner Hand anzunehmen und zu trinken, dann sag zu ihnen: So spricht der Herr der Heere: Trinken müsst ihr." },
      { number: 29, text: "Denn seht, bei der Stadt, über der mein Name ausgerufen ist, beginne ich mit dem Unheil und da solltet ihr ungestraft bleiben? Nein, ihr werdet nicht ungestraft bleiben; denn ich rufe das Schwert gegen alle Bewohner der Erde - Spruch des Herrn der Heere." },
      { number: 30, text: "Du aber sollst ihnen als Prophet alle diese Worte verkünden und zu ihnen sagen: Aus der Höhe herab donnert der Herr, / von seiner heiligen Wohnung her lässt er seine Stimme erschallen. Mächtig donnert er über seiner Flur / und ruft wie die Keltertreter.", heading: "Das Gericht über die Völker: 25,30-38", footnotes: ["Ps 29; Joël 4,16; Am 1,2"] },
      { number: 31, text: "Zu allen Erdbewohnern dringt der Schall, / ja bis ans Ende der Erde; / denn der Herr hat einen Rechtsstreit mit den Völkern: Er hält Gericht über alle Sterblichen / und liefert die Schuldigen dem Schwert aus / - Spruch des Herrn." },
      { number: 32, text: "So spricht der Herr der Heere: Seht, Unheil schreitet von Volk zu Volk, / ein gewaltiger Sturm bricht los / von den Grenzen der Erde." },
      { number: 33, text: "Die vom Herrn Erschlagenen liegen an jenem Tag / von einem Ende der Erde bis zum andern. Man beklagt sie nicht, / man sammelt sie nicht und begräbt sie nicht; / sie werden zum Dünger auf dem Acker.", footnotes: ["9,21"] },
      { number: 34, text: "Klagt, ihr Hirten, und schreit; / wälzt euch im Staub, ihr Herren der Herde! Denn die Zeit ist gekommen, / dass ihr geschlachtet werdet; ich zerschmettere euch, dass ihr berstet / wie ein Prunkgefäß." },
      { number: 35, text: "Es gibt keine Flucht mehr für die Hirten, / kein Entrinnen für die Herren der Herde." },
      { number: 36, text: "Horcht, wie die Hirten schreien / und die Herren der Herde wehklagen, / weil der Herr ihre Weide verwüstet." },
      { number: 37, text: "Verdorrt sind die friedlichen Wiesen / vor dem glühenden Zorn des Herrn." },
      { number: 38, text: "Er verlässt sein Versteck wie ein Löwe; / ihr Land ist zu einem Bild des Entsetzens geworden durch sein rasendes Schwert / und durch die Glut seines Zorns." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Im Anfang der Regierung Jojakims, des Sohnes Joschijas, des Königs von Juda, erging vom Herrn dieses Wort:", heading: "Die Gerichtsworte über Israel und Juda: 26,1 - 29,32 | Die Tempelrede: 26,1-19", footnotes: ["(1-9) 7,1-15"] },
      { number: 2, text: "So spricht der Herr: Stell dich in den Vorhof des Hauses des Herrn und sag zu den Leuten, die aus allen Städten Judas kommen, um im Haus des Herrn anzubeten, alles, was ich dir ihnen zu verkünden aufgetragen habe; kein Wort sollst du weglassen." },
      { number: 3, text: "Vielleicht hören sie und kehren um, jeder von seinem bösen Weg, sodass mich das Unheil reut, das ich ihnen wegen ihrer schlechten Taten zugedacht habe." },
      { number: 4, text: "Sag also zu ihnen: So spricht der Herr: Wenn ihr nicht auf mein Wort hört und meiner Weisung nicht folgt, die ich euch gegeben habe," },
      { number: 5, text: "wenn ihr nicht auf die Worte meiner Knechte, der Propheten, hört, die ich immer wieder zu euch sende, obwohl ihr nicht hört,", footnotes: ["25,4"] },
      { number: 6, text: "dann verfahre ich mit diesem Haus wie mit Schilo und mache diese Stadt zu einem Fluch bei allen Völkern der Erde.", footnotes: ["7,12; 9,10", "Zu Schilo vgl. die Anmerkung zu 7,12."] },
      { number: 7, text: "Die Priester, die Propheten und das ganze Volk hörten, wie Jeremia diese Worte vor dem Haus des Herrn vortrug." },
      { number: 8, text: "Als Jeremia alles gesagt hatte, was er im Auftrag des Herrn vor dem ganzen Volk zu verkünden hatte, ergriffen ihn die Priester, die Propheten und alles Volk und schrien: Jetzt musst du sterben." },
      { number: 9, text: "Warum weissagst du im Namen des Herrn: Wie Schilo wird es diesem Haus gehen und diese Stadt wird verwüstet und entvölkert werden? Das ganze Volk rottete sich beim Haus des Herrn um Jeremia zusammen." },
      { number: 10, text: "Als die Beamten Judas von diesen Vorgängen hörten, gingen sie vom Königspalast zum Haus des Herrn hinauf und setzten sich am Eingang des Neuen Tempeltors nieder." },
      { number: 11, text: "Die Priester und Propheten sagten zu den Beamten und zum ganzen Volk: Dieser Mann hat den Tod verdient; denn er hat gegen diese Stadt geweissagt, wie ihr mit eigenen Ohren gehört habt." },
      { number: 12, text: "Jeremia aber erwiderte allen Beamten und dem ganzen Volk: Der Herr hat mich gesandt, damit ich als Prophet gegen dieses Haus und diese Stadt alle Worte verkünde, die ihr gehört habt." },
      { number: 13, text: "Nun also, bessert euer Verhalten und euer Tun und hört auf die Stimme des Herrn, eures Gottes! Dann wird den Herrn das Unheil reuen, das er euch angedroht hat.", footnotes: ["7,3"] },
      { number: 14, text: "Ich selbst bin in eurer Hand; macht mit mir, was ihr für gut und recht haltet." },
      { number: 15, text: "Aber das sollt ihr wissen: Wenn ihr mich tötet, bringt ihr unschuldiges Blut über euch, über diese Stadt und ihre Einwohner. Denn der Herr hat mich wirklich zu euch gesandt, damit ich euch alle diese Worte in die Ohren rufe." },
      { number: 16, text: "Da sagten die Beamten und das ganze Volk zu den Priestern und Propheten: Dieser Mann hat den Tod nicht verdient; denn er hat zu uns im Namen des Herrn, unseres Gottes, geredet." },
      { number: 17, text: "Einige von den Ältesten des Landes standen auf und sagten zu der ganzen Volksversammlung:" },
      { number: 18, text: "Micha von Moreschet, der zur Zeit Hiskijas, des Königs von Juda, als Prophet wirkte, hat zum ganzen Volk Juda gesagt: So spricht der Herr der Heere: Zion wird umgepflügt zu Ackerland, Jerusalem wird zum Trümmerhaufen, der Tempelberg zur überwucherten Höhe.", footnotes: ["Mi 3,12"] },
      { number: 19, text: "Haben ihn etwa Hiskija, der König von Juda, und ganz Juda deshalb hingerichtet? Hat er nicht Gott gefürchtet und den Zorn des Herrn besänftigt, sodass den Herrn das Unheil reute, das er ihnen angedroht hatte? Und wir sollten ein so großes Unrecht tun zu unserem eigenen Schaden?" },
      { number: 20, text: "Damals wirkte noch ein anderer Mann als Prophet im Namen des Herrn, Urija, der Sohn Schemajas, aus Kirjat-Jearim. Er weissagte gegen diese Stadt und dieses Land mit ganz ähnlichen Worten wie Jeremia.", heading: "Die Ermordung des Propheten Urija: 26,20-24" },
      { number: 21, text: "Der König Jojakim, alle seine Heerführer und alle Beamten hörten von seinen Reden. Daher suchte der König ihn zu töten. Als Urija davon erfuhr, bekam er Angst, floh und gelangte nach Ägypten." },
      { number: 22, text: "Der König Jojakim aber schickte Leute nach Ägypten, nämlich Elnatan, den Sohn Achbors, mit einigen Männern." },
      { number: 23, text: "Sie holten Urija aus Ägypten und brachten ihn zu König Jojakim; dieser ließ ihn mit dem Schwert erschlagen und seinen Leichnam zu den Gräbern des niedrigen Volkes werfen." },
      { number: 24, text: "Ahikam jedoch, der Sohn Schafans, beschützte Jeremia, sodass man ihn nicht dem Volk auslieferte, das ihn töten wollte." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Im Anfang der Regierung Zidkijas, des Sohnes Joschijas, des Königs von Juda, erging vom Herrn folgendes Wort an Jeremia.", heading: "Das Joch Babels: 27,1-22" },
      { number: 2, text: "So sprach der Herr zu mir: Mach dir Stricke und Jochhölzer, und leg sie dir auf den Nacken!", footnotes: ["28,10"] },
      { number: 3, text: "Dann schick eine Botschaft an den König von Edom, den König von Moab, den König der Ammoniter, den König von Tyrus und den König von Sidon, durch die Gesandten, die zu Zidkija, dem König von Juda, nach Jerusalem gekommen sind." },
      { number: 4, text: "Gib ihnen folgenden Auftrag an ihre Gebieter: So spricht der Herr der Heere, der Gott Israels: Sagt so zu euren Gebietern:" },
      { number: 5, text: "Ich bin es, der die Erde erschaffen hat samt den Menschen und den Tieren, die auf der Erde leben, durch meine gewaltige Kraft und meinen hoch erhobenen Arm, und ich gebe sie, wem ich will.", footnotes: ["32,17"] },
      { number: 6, text: "Jetzt gebe ich alle diese Länder in die Hand meines Knechtes, des Königs Nebukadnezzar von Babel; selbst die Tiere des Feldes mache ich ihm dienstbar." },
      { number: 7, text: "Alle Völker sollen ihm untertan sein, ihm, seinem Sohn und seinem Enkel, bis auch für sein eigenes Land die Zeit kommt, dass große Völker und mächtige Könige es knechten." },
      { number: 8, text: "Will aber ein Volk oder Reich dem König Nebukadnezzar von Babel nicht untertan sein und seinen Nacken nicht unter das Joch des Königs von Babel beugen, so werde ich dieses Volk mit Schwert, Hunger und Pest heimsuchen - Spruch des Herrn -, bis ich es seiner Hand ausgeliefert habe." },
      { number: 9, text: "Ihr aber, hört nicht auf eure Propheten, Wahrsager, Träumer, Zeichendeuter und Zauberer, wenn sie zu euch sagen: Ihr werdet dem König von Babel nicht untertan sein.", footnotes: ["14,13f"] },
      { number: 10, text: "Denn sie lügen, wenn sie euch weissagen, und damit vertreiben sie euch aus eurer Heimat; denn ich verstoße euch, sodass ihr zugrunde geht." },
      { number: 11, text: "Das Volk aber, das seinen Nacken unter das Joch des Königs von Babel beugt und ihm untertan ist, lasse ich ungestört auf seinem heimatlichen Boden - Spruch des Herrn -; es kann ihn bebauen und auf ihm wohnen." },
      { number: 12, text: "Auch zu Zidkija, dem König von Juda, redete ich ganz in diesem Sinn: Beugt euren Nacken unter das Joch des Königs von Babel und seid ihm und seinem Volk untertan; dann bleibt ihr am Leben." },
      { number: 13, text: "Warum sollt ihr, du und dein Volk, durch Schwert, Hunger und Pest umkommen, wie der Herr dem Volk, das dem König von Babel nicht untertan sein will, angedroht hat?" },
      { number: 14, text: "Hört nicht auf die Reden der Propheten, die zu euch sagen: Ihr sollt dem König von Babel nicht untertan sein. Denn was sie euch weissagen, ist Lüge.", footnotes: ["14,14; 29,8f"] },
      { number: 15, text: "Ich habe sie nicht gesandt - Spruch des Herrn -; darum ist es Lüge, wenn sie in meinem Namen weissagen; die Folge wird sein, dass ich euch verstoße und dass ihr zugrunde geht, ihr und die Propheten, die euch weissagen." },
      { number: 16, text: "Zu den Priestern und dem ganzen Volk sagte ich: So spricht der Herr: Hört nicht auf die Reden eurer Propheten, die euch weissagen: Die Geräte des Hauses des Herrn werden aus Babel zurückgebracht werden, und zwar bald. Denn was sie euch weissagen, ist Lüge." },
      { number: 17, text: "Hört nicht auf sie! Seid dem König von Babel untertan; dann bleibt ihr am Leben. Warum soll diese Stadt ein Trümmerhaufen werden?" },
      { number: 18, text: "Wenn sie Propheten sind und das Wort des Herrn wirklich bei ihnen ist, so mögen sie doch den Herrn der Heere bestürmen, dass die Geräte, die noch im Haus des Herrn, im Palast des Königs von Juda und in Jerusalem verblieben sind, nicht auch nach Babel kommen." },
      { number: 19, text: "Denn so spricht der Herr der Heere über die Säulen, das Eherne Meer, die fahrbaren Gestelle und den Rest der in dieser Stadt noch verbliebenen Geräte,", footnotes: ["2 Kön 25,13-17"] },
      { number: 20, text: "die Nebukadnezzar, der König von Babel, nicht mitgenommen hat, als er Jojachin, den Sohn Jojakims, den König von Juda, aus Jerusalem nach Babel verschleppte samt allen Vornehmen Judas und Jerusalems:" },
      { number: 21, text: "Ja, so spricht der Herr der Heere, der Gott Israels, über die Geräte, die im Haus des Herrn, im Palast des Königs von Juda und in Jerusalem verblieben sind:" },
      { number: 22, text: "Nach Babel werden sie gebracht und dort bleiben sie bis zu dem Tag, an dem ich mich ihrer annehme - Spruch des Herrn - und sie wieder an diesen Ort heraufbringe.", footnotes: ["Esra 1,7-11"] },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Im selben Jahr, im Anfang der Regierung Zidkijas, des Königs von Juda, im fünften Monat des vierten Jahres, sagte der Prophet Hananja, der Sohn Asurs aus Gibeon, im Haus des Herrn vor den Priestern und dem ganzen Volk zu Jeremia:", heading: "Der falsche Prophet Hananja: 28,1-17", footnotes: ["zu Jeremia; H: zu mir; vgl. aber V. 5."] },
      { number: 2, text: "So spricht der Herr der Heere, der Gott Israels: Ich zerbreche das Joch des Königs von Babel." },
      { number: 3, text: "Noch zwei Jahre und ich bringe alle Geräte des Hauses des Herrn, die Nebukadnezzar, der König von Babel, von diesem Ort weggenommen und nach Babel gebracht hat, wieder an diesen Ort zurück." },
      { number: 4, text: "Auch Jojachin, den Sohn Jojakims, den König von Juda, samt allen Verschleppten aus Juda, die nach Babel gebracht wurden, führe ich an diesen Ort zurück - Spruch des Herrn -; denn ich zerbreche das Joch des Königs von Babel." },
      { number: 5, text: "Der Prophet Jeremia antwortete dem Propheten Hananja vor den Priestern und vor dem ganzen Volk, das im Haus des Herrn stand." },
      { number: 6, text: "Der Prophet Jeremia sagte: Ganz recht! Mag der Herr so tun. Der Herr erfülle deine Worte, die du verkündet hast, und bringe die Geräte des Hauses des Herrn und alle Verschleppten aus Babel zurück an diesen Ort." },
      { number: 7, text: "Doch höre das Wort, das ich dir und dem ganzen Volk in die Ohren rufe:" },
      { number: 8, text: "Die Propheten, die vor mir und vor dir je gelebt haben, weissagten Krieg, Unheil und Pest gegen viele Länder und mächtige Reiche." },
      { number: 9, text: "Der Prophet aber, der Heil weissagt - an der Erfüllung des prophetischen Wortes erkennt man den Propheten, den der Herr wirklich gesandt hat.", footnotes: ["Dtn 18,21f"] },
      { number: 10, text: "Da nahm der Prophet Hananja das Jochholz vom Nacken des Propheten Jeremia und brach es entzwei.", footnotes: ["27,2"] },
      { number: 11, text: "Vor dem ganzen Volk erklärte Hananja: So spricht der Herr: Ebenso nehme ich binnen zwei Jahren das Joch Nebukadnezzars, des Königs von Babel, vom Nacken aller Völker und zerbreche es. Der Prophet Jeremia ging seines Weges." },
      { number: 12, text: "Nachdem nun der Prophet Hananja das Jochholz vom Nacken des Propheten Jeremia genommen und zerbrochen hatte, erging das Wort des Herrn an Jeremia:" },
      { number: 13, text: "Geh und sag zu Hananja: So spricht der Herr: Jochstangen aus Holz hast du zerbrochen, dafür aber musst du nun Jochstangen aus Eisen machen." },
      { number: 14, text: "Denn so spricht der Herr der Heere, der Gott Israels: Ein eisernes Joch habe ich auf den Nacken aller dieser Völker gelegt; sie müssen Nebukadnezzar, dem König von Babel, untertan sein. [Sie werden ihm untertan sein und auch die Tiere des Feldes gebe ich ihm.]", footnotes: ["Zusatz aus 27,6; fehlt in G."] },
      { number: 15, text: "Der Prophet Jeremia sagte also zum Propheten Hananja: Höre, Hananja! Der Herr hat dich nicht gesandt und du hast dieses Volk dazu verführt, auf Lügen zu vertrauen." },
      { number: 16, text: "Darum - so spricht der Herr: Siehe, ich schaffe dich vom Erdboden fort. Noch in diesem Jahr bist du tot; denn du hast Auflehnung gegen den Herrn gepredigt." },
      { number: 17, text: "Im siebten Monat desselben Jahres starb der Prophet Hananja." },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Das ist der Wortlaut des Briefes, den der Prophet Jeremia aus Jerusalem an den Rest der Ältesten der Gemeinde der Verbannten sandte, an die Priester, Propheten und das ganze Volk, das Nebukadnezzar von Jerusalem nach Babel verschleppt hatte,", heading: "Der Brief an die Verbannten: 29,1-23" },
      { number: 2, text: "nachdem der König Jojachin, die Herrin, die Hofbeamten, die Großen von Juda und Jerusalem sowie die Schmiede und Schlosser aus Jerusalem fortgezogen waren;", footnotes: ["24,1; 2 Kön 24,14f", "Die «Herrin» ist die Mutter Jojachins (vgl. 13,18)."] },
      { number: 3, text: "er schickte den Brief durch Elasa, den Sohn Schafans, und Gemarja, den Sohn Hilkijas, die Zidkija, der König von Juda, nach Babel zu Nebukadnezzar, dem König von Babel, sandte:" },
      { number: 4, text: "So spricht der Herr der Heere, der Gott Israels, zur ganzen Gemeinde der Verbannten, die ich von Jerusalem nach Babel weggeführt habe:" },
      { number: 5, text: "Baut Häuser und wohnt darin, pflanzt Gärten und esst ihre Früchte!" },
      { number: 6, text: "Nehmt euch Frauen und zeugt Söhne und Töchter, nehmt für eure Söhne Frauen und gebt eure Töchter Männern, damit sie Söhne und Töchter gebären. Ihr sollt euch dort vermehren und nicht vermindern." },
      { number: 7, text: "Bemüht euch um das Wohl der Stadt, in die ich euch weggeführt habe, und betet für sie zum Herrn; denn in ihrem Wohl liegt euer Wohl." },
      { number: 8, text: "Denn so spricht der Herr der Heere, der Gott Israels: Lasst euch nicht täuschen von den Propheten, die unter euch sind, und von euren Wahrsagern. Hört nicht auf die Träume, die sie träumen.", footnotes: ["14,14"] },
      { number: 9, text: "Denn Lüge ist das, was sie euch in meinem Namen weissagen; ich habe sie nicht gesandt - Spruch des Herrn." },
      { number: 10, text: "Ja, so spricht der Herr: Wenn siebzig Jahre für Babel vorüber sind, dann werde ich nach euch sehen, mein Heilswort an euch erfüllen und euch an diesen Ort zurückführen.", footnotes: ["25,11-13"] },
      { number: 11, text: "Denn ich, ich kenne meine Pläne, die ich für euch habe - Spruch des Herrn -, Pläne des Heils und nicht des Unheils; denn ich will euch eine Zukunft und eine Hoffnung geben." },
      { number: 12, text: "Wenn ihr mich ruft, wenn ihr kommt und zu mir betet, so erhöre ich euch." },
      { number: 13, text: "Sucht ihr mich, so findet ihr mich. Wenn ihr von ganzem Herzen nach mir fragt,", footnotes: ["(13-14) Dtn 4,29"] },
      { number: 14, text: "lasse ich mich von euch finden - Spruch des Herrn. Ich wende euer Geschick und sammle euch aus allen Völkern und von allen Orten, wohin ich euch versprengt habe - Spruch des Herrn. Ich bringe euch an den Ort zurück, von dem ich euch weggeführt habe." },
      { number: 15, text: "Zwar werdet ihr sagen: Auch in Babel hat der Herr für uns Propheten auftreten lassen." },
      { number: 16, text: "[Fürwahr, so spricht der Herr über den König, der auf dem Thron Davids sitzt, und über das ganze Volk, das in dieser Stadt wohnt, eure Brüder, die noch nicht mit euch in die Verbannung fortgezogen sind,", footnotes: ["16-20: Passt nicht in den Briefzusammenhang, stammt vor allem aus Kap. 24; fehlt in G zu Recht."] },
      { number: 17, text: "so spricht der Herr der Heere: Seht, ich schicke unter sie Schwert, Hunger und Pest und ich behandle sie wie verdorbene Feigen, die so schlecht sind, dass sie ungenießbar sind.", footnotes: ["(17-18) 24,8-10"] },
      { number: 18, text: "Ich verfolge sie mit Schwert, Hunger und Pest und mache sie zu einem Bild des Schreckens für alle Reiche der Erde, zum Fluch und zum Entsetzen, zum Hohn und Gespött aller Völker, zu denen ich sie verstoße," },
      { number: 19, text: "weil sie nicht auf meine Worte gehört haben - Spruch des Herrn -, obwohl ich immer wieder meine Knechte, die Propheten, zu ihnen gesandt habe; ihr aber habt nicht gehört - Spruch des Herrn.", footnotes: ["25,4"] },
      { number: 20, text: "Ihr jedoch, hört jetzt das Wort des Herrn, all ihr Verschleppten, die ich von Jerusalem nach Babel wegführen ließ.]" },
      { number: 21, text: "So spricht der Herr der Heere, der Gott Israels, über Ahab, den Sohn Kolajas, und über Zidkija, den Sohn Maasejas, die euch in meinem Namen Lüge weissagen: Seht, ich liefere sie Nebukadnezzar, dem König von Babel, aus. Er wird sie vor euren Augen niederhauen lassen." },
      { number: 22, text: "Dann wird man bei allen Verschleppten Judas, die in Babel sind, von ihnen das Fluchwort herleiten: Der Herr mache dich Zidkija und Ahab gleich, die der König von Babel im Feuer rösten ließ." },
      { number: 23, text: "Sie haben nämlich Schändliches in Israel getrieben, mit den Frauen ihrer Nächsten Ehebruch begangen und in meinem Namen Worte verkündet, die ich ihnen nicht aufgetragen hatte. Ich bin der Wissende und der Zeuge - Spruch des Herrn." },
      { number: 24, text: "Zu Schemaja aus Nehelam sollst du sagen:", heading: "Das Drohwort gegen Schemaja: 29,24-32" },
      { number: 25, text: "So spricht der Herr der Heere, der Gott Israels: Du hast in deinem eigenen Namen einen Brief [an das ganze Volk in Jerusalem und] an den Priester Zefanja, den Sohn Maasejas, [sowie an alle Priester] geschickt mit diesem Inhalt:", footnotes: ["Die eingeklammerten Zusätze fehlen in G; der Brief ging wohl nur an Zefanja."] },
      { number: 26, text: "Der Herr hat dich anstelle des Priesters Jojada zum Priester bestellt, damit du im Haus des Herrn Acht gibst auf jeden verrückten Propheten und ihn in Block und Halseisen legst." },
      { number: 27, text: "Warum bist du also nicht gegen Jeremia aus Anatot eingeschritten, der sich bei euch wie ein Prophet gebärdet?" },
      { number: 28, text: "Deshalb hat er auch an uns nach Babel die Botschaft geschickt: Es wird noch lange dauern; baut Häuser und wohnt darin, pflanzt Gärten und genießt ihre Früchte!" },
      { number: 29, text: "Der Priester Zefanja aber hatte diesen Brief dem Propheten Jeremia vorgelesen." },
      { number: 30, text: "Da erging das Wort des Herrn an Jeremia:" },
      { number: 31, text: "Schick eine Botschaft an alle Verschleppten: So spricht der Herr über Schemaja aus Nehelam: Weil Schemaja euch geweissagt hat, obwohl ich ihn nicht gesandt hatte, und weil er euch dazu verführt hat, auf Lügen zu vertrauen,", footnotes: ["28,15"] },
      { number: 32, text: "darum - so spricht der Herr: Seht, ich ziehe Schemaja aus Nehelam und seine Nachkommen zur Rechenschaft. Keiner von den Seinen soll unter diesem Volk wohnen bleiben und das Glück schauen, das ich meinem Volk bereite - Spruch des Herrn -; denn er hat Auflehnung gegen den Herrn gepredigt.", footnotes: ["28,16"] },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging:", heading: "Die Trostschrift: 30,1 - 31,40 | Die Rettung Israels und Judas: 30,1-11", footnotes: ["1-31,40: Der Abschnitt hebt sich inhaltlich und formal von der Umgebung ab. Er ist eine Trostschrift, die vor allem die um 722 v. Chr. verschleppten Nordstämme anspricht, aber dann auch auf Juda bezogen wurde. Die Abfassungszeit lässt sich nicht sicher ausmachen, zumal die einzelnen Abschnitte wohl verschieden zu datieren sind. Das Ganze oder größere Teile Jeremia abzusprechen, ist nicht begründet."] },
      { number: 2, text: "So spricht der Herr, der Gott Israels: Schreib dir alle Worte, die ich dir gesagt habe, in ein Buch!" },
      { number: 3, text: "Denn seht, es werden Tage kommen - Spruch des Herrn -, da wende ich das Geschick meines Volkes Israel und Juda, spricht der Herr. Ich führe sie zurück in das Land, das ich ihren Vätern zum Besitz gegeben habe.", footnotes: ["29,14"] },
      { number: 4, text: "Das sind die Worte, die der Herr über Israel und Juda gesprochen hat:" },
      { number: 5, text: "Ja, so spricht der Herr: Angstgeschrei vernehmen wir: / Schrecken und kein Friede." },
      { number: 6, text: "Fragt doch und schaut, / ob je ein Mann Kinder zur Welt bringt. Warum sehe ich alle Männer / mit den Händen auf den Hüften wie eine Gebärende? / Jedes Gesicht ist verstört und leichenblass." },
      { number: 7, text: "Denn groß ist jener Tag, / keiner ist ihm gleich. Eine Notzeit ist es für Jakob, / doch wird er daraus gerettet.", footnotes: ["Zef 1,14f"] },
      { number: 8, text: "An jenem Tag wird es geschehen / - Spruch des Herrn der Heere -, / da zerbreche ich das Joch auf seinem Nacken; ich zerreiße seine Stricke / und Fremde sollen ihn nicht mehr knechten." },
      { number: 9, text: "Vielmehr wird mein Volk dem Herrn, / seinem Gott, dienen / und David, seinem König, den ich ihm erstehen lasse.", footnotes: ["23,5"] },
      { number: 10, text: "Fürchte dich nicht, du, mein Knecht Jakob / - Spruch des Herrn -, / verzage nicht, Israel! Denn ich bin es, der dich aus fernem Land errettet, / deine Kinder aus dem Land ihrer Gefangenschaft. Jakob wird heimkehren und Ruhe haben; / er wird in Sicherheit leben / und niemand wird ihn erschrecken.", footnotes: ["46,27f"] },
      { number: 11, text: "Denn ich bin mit dir / - Spruch des Herrn -, / um dich zu retten. Ja, ich vernichte alle Völker, / unter die ich dich zerstreut habe. Nur dich werde ich niemals vernichten; / ich züchtige dich mit rechtem Maß, / doch ganz ungestraft kann ich dich nicht lassen.", footnotes: ["1,8.19; 10,24; 46,28"] },
      { number: 12, text: "Ja, so spricht der Herr: Arg ist dein Schaden, / unheilbar deine Wunde.", heading: "Die Heilung für die Wunden: 30,12-17", footnotes: ["10,19; 14,17; 15,18"] },
      { number: 13, text: "[Niemand verschafft dir Recht.] / Für das Geschwür gibt es keine Heilung, / keine Genesung gibt es für dich.", footnotes: ["13a: Eine Erläuterung, die das Bild von der Wunde unnötig unterbricht."] },
      { number: 14, text: "Alle deine Freunde haben dich vergessen, / sie kümmern sich nicht mehr um dich. Denn wie ein Feind schlägt, / so habe ich dich geschlagen mit harter Züchtigung [wegen deiner vielfachen Schuld / und deiner zahlreichen Sünden].", footnotes: ["14ef: Irrtümlich aus dem folgenden Vers hier eingefügt."] },
      { number: 15, text: "Was schreist du über deinen Schaden / und dein arges Leiden? Wegen deiner vielfachen Schuld / und deiner zahlreichen Sünden / habe ich dir das getan." },
      { number: 16, text: "Doch alle, die dich fraßen, werden gefressen, / alle deine Bedränger ziehen als Gefangene fort; wer dich ausplünderte, wird ausgeplündert, / wer dich beraubte, den gebe ich dem Raub preis.", footnotes: ["2,3; 8,16; 50,7"] },
      { number: 17, text: "Denn ich lasse dich genesen / und heile dich von deinen Wunden / Spruch des Herrn -, weil man dich [das ist Zion] die Verstoßene genannt hat, / nach der niemand fragt.", footnotes: ["33,6"] },
      { number: 18, text: "So spricht der Herr: Seht, ich wende das Geschick der Zelte Jakobs, / seiner Wohnstätten erbarme ich mich. Die Stadt soll auf ihrem Schutthügel aufgebaut werden, / die Burg auf ihrem alten Platz stehen.", heading: "Die Wiederherstellung des Volkes: 30,18 - 31,1" },
      { number: 19, text: "Lobgesang wird dort erschallen, / die Stimme fröhlicher Menschen. Ich will ihre Zahl vermehren, / sie sollen nicht weniger werden; ich will ihnen Ehre verschaffen, / sie sollen nicht verachtet werden." },
      { number: 20, text: "Die Söhne Jakobs werden sein wie ehedem, / seine Gemeinde wird vor mir bestehen bleiben, doch alle seine Unterdrücker / ziehe ich zur Rechenschaft." },
      { number: 21, text: "Sein Machthaber wird ihm selbst entstammen, / sein Herrscher aus seiner Mitte hervorgehen. Ich gewähre ihm Zutritt, sodass er mir nahen kann; / denn wer sonst dürfte sein Leben wagen, um mir zu nahen? / - Spruch des Herrn." },
      { number: 22, text: "Ihr werdet mein Volk sein / und ich werde euer Gott sein.", footnotes: ["24,7; Lev 26,12; Dtn 26,17f"] },
      { number: 23, text: "Hört, der Sturm des Herrn [sein Grimm] bricht los. / Ein Wirbelsturm braust hinweg über die Köpfe der Frevler.", footnotes: ["23,19f"] },
      { number: 24, text: "Der glühende Zorn des Herrn hört nicht auf, / bis er die Pläne seines Herzens ausgeführt und vollbracht hat. / Am Ende der Tage werdet ihr es klar erkennen." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "In jener Zeit - Spruch des Herrn - / werde ich der Gott aller Stämme Israels sein / und sie werden mein Volk sein.", footnotes: ["7,23; 11,4; 24,7; 30,22"] },
      { number: 2, text: "So spricht der Herr: Gnade fand in der Wüste das Volk, / das vom Schwert verschont blieb; / Israel zieht zum Ort seiner Ruhe.", heading: "Die Heimkehr aller Versprengten: 31,2-14" },
      { number: 3, text: "Aus der Ferne ist ihm der Herr erschienen: / Mit ewiger Liebe habe ich dich geliebt, / darum habe ich dir so lange die Treue bewahrt." },
      { number: 4, text: "Ich baue dich wieder auf, / du sollst neu gebaut werden, Jungfrau Israel. Du sollst dich wieder schmücken mit deinen Pauken, / sollst ausziehen im Reigen der Fröhlichen." },
      { number: 5, text: "Wieder sollst du Weingärten pflanzen / auf Samarias Bergen. Wer Pflanzungen anlegt, / darf ihre Früchte genießen." },
      { number: 6, text: "Denn es kommt der Tag, / da rufen die Wächter auf Efraims Bergland: Auf, lasst uns hinaufpilgern nach Zion / zum Herrn, unserem Gott.", footnotes: ["Der erste König des Nordreichs hatte in Bet-El im Bergland von Efraim ein eigenes Heiligtum geschaffen, um die Leute von der Wallfahrt nach Jerusalem abzuhalten (1 Kön 12,25-33). Jetzt wird die religiöse Einheit wiederhergestellt und die aus Assyrien Heimgekehrten werden wieder zum Zion pilgern."] },
      { number: 7, text: "Ja, so spricht der Herr: Jubelt Jakob voll Freude zu / und jauchzt über das Haupt der Völker! Verkündet, lobsingt und sagt: / Der Herr hat sein Volk gerettet, / den Rest Israels." },
      { number: 8, text: "Seht, ich bringe sie heim aus dem Nordland / und sammle sie von den Enden der Erde, darunter Blinde und Lahme, / Schwangere und Wöchnerinnen; / als große Gemeinde kehren sie hierher zurück." },
      { number: 9, text: "Weinend kommen sie / und tröstend geleite ich sie. Ich führe sie an Wasser führende Bäche, / auf einen ebenen Weg, wo sie nicht straucheln. Denn ich bin Israels Vater / und Efraim ist mein erstgeborener Sohn.", footnotes: ["50,4"] },
      { number: 10, text: "Hört, ihr Völker, das Wort des Herrn, / verkündet es auf den fernsten Inseln und sagt: Er, der Israel zerstreut hat, wird es auch sammeln / und hüten wie ein Hirt seine Herde." },
      { number: 11, text: "Denn der Herr wird Jakob erlösen / und ihn befreien aus der Hand des Stärkeren." },
      { number: 12, text: "Sie kommen und jubeln auf Zions Höhe, / sie strahlen vor Freude über die Gaben des Herrn, / über Korn, Wein und Öl, über Lämmer und Rinder. Sie werden wie ein bewässerter Garten sein / und nie mehr verschmachten." },
      { number: 13, text: "Dann freut sich das Mädchen beim Reigentanz, / Jung und Alt sind fröhlich. Ich verwandle ihre Trauer in Jubel, / tröste und erfreue sie nach ihrem Kummer." },
      { number: 14, text: "Ich labe die Priester mit Opferfett / und mein Volk wird satt an meinen Gaben / - Spruch des Herrn." },
      { number: 15, text: "So spricht der Herr: Ein Geschrei ist in Rama zu hören, / bitteres Klagen und Weinen. Rahel weint um ihre Kinder / und will sich nicht trösten lassen, / um ihre Kinder, denn sie sind dahin.", heading: "Der Trost durch die Heimkehr: 31,15-22", footnotes: ["Mt 2,18"] },
      { number: 16, text: "So spricht der Herr: Verwehre deiner Stimme die Klage / und deinen Augen die Tränen! Denn es gibt einen Lohn für deine Mühe - Spruch des Herrn: / Sie werden zurückkehren aus dem Feindesland." },
      { number: 17, text: "Es gibt eine Hoffnung für deine Nachkommen - Spruch des Herrn: / Die Söhne werden zurückkehren in ihre Heimat." },
      { number: 18, text: "Ich höre gar wohl, wie Efraim klagt: / Du hast mich erzogen und ich ließ mich erziehen / wie ein ungezähmter Jungstier. Führ mich zurück, / umkehren will ich; / denn du bist der Herr, mein Gott." },
      { number: 19, text: "Ja, nach meiner Umkehr fühle ich Reue; / nachdem ich zur Einsicht gekommen bin, schlage ich an meine Brust. Ich bin beschämt und erröte; / denn ich trage die Schande meiner Jugend. -" },
      { number: 20, text: "Ist mir denn Efraim ein so teurer Sohn / oder mein Lieblingskind? Denn sooft ich ihm auch Vorwürfe mache, / muss ich doch immer wieder an ihn denken. Deshalb schlägt mein Herz für ihn, / ich muss mich seiner erbarmen - Spruch des Herrn.", footnotes: ["Hos 11,8"] },
      { number: 21, text: "Stell dir Wegweiser auf, setz dir Wegmarken, / achte genau auf die Straße, / auf den Weg, den du gegangen bist. Kehr um, Jungfrau Israel, / kehr zurück in diese deine Städte!" },
      { number: 22, text: "Wie lange noch willst du dich sträuben, / du abtrünnige Tochter? Denn etwas Neues erschafft der Herr im Land: / Die Frau wird den Mann umgeben.", footnotes: ["22d: Sinn unklar und umstritten; vielleicht: Israel (das wieder vereinte Volk) wird Efraim (den nach Assyrien deportierten Hauptstamm des Nordreichs) schützend umgeben."] },
      { number: 23, text: "So spricht der Herr der Heere, der Gott Israels: Man wird im Land Juda und in seinen Städten, wenn ich ihr Geschick wende, wieder dieses Wort sprechen: Es segne dich der Herr, du Hort der Gerechtigkeit, du heiliger Berg.", heading: "Die Segensverheißung: 31,23-30" },
      { number: 24, text: "Juda und alle seine Städte werden zusammen dort wohnen, Ackerbauern und Wanderhirten." },
      { number: 25, text: "Ja, ich labe den Ermatteten und sättige den Verschmachtenden." },
      { number: 26, text: "Darum heißt es: Ich erwachte und blickte umher und mein Schlaf war süß gewesen." },
      { number: 27, text: "Seht, es werden Tage kommen - Spruch des Herrn -, da säe ich über das Haus Israel und das Haus Juda eine Saat von Menschen und eine Saat von Vieh." },
      { number: 28, text: "Wie ich über sie gewacht habe, um auszureißen und einzureißen, zu zerstören, zu vernichten und zu schaden, so werde ich über sie wachen, um aufzubauen und einzupflanzen - Spruch des Herrn.", footnotes: ["1,10; 24,6"] },
      { number: 29, text: "In jenen Tagen sagt man nicht mehr: Die Väter haben saure Trauben gegessen und den Söhnen werden die Zähne stumpf.", footnotes: ["Ez 18,2"] },
      { number: 30, text: "Nein, jeder stirbt nur für seine eigene Schuld; nur dem, der die sauren Trauben isst, werden die Zähne stumpf." },
      { number: 31, text: "Seht, es werden Tage kommen - Spruch des Herrn -, in denen ich mit dem Haus Israel und dem Haus Juda einen neuen Bund schließen werde,", heading: "Der neue Bund: 31,31-34", footnotes: ["(31-34) Hebr 8,8-12"] },
      { number: 32, text: "nicht wie der Bund war, den ich mit ihren Vätern geschlossen habe, als ich sie bei der Hand nahm, um sie aus Ägypten herauszuführen. Diesen meinen Bund haben sie gebrochen, obwohl ich ihr Gebieter war Spruch des Herrn." },
      { number: 33, text: "Denn das wird der Bund sein, den ich nach diesen Tagen mit dem Haus Israel schließe - Spruch des Herrn: Ich lege mein Gesetz in sie hinein und schreibe es auf ihr Herz. Ich werde ihr Gott sein und sie werden mein Volk sein.", footnotes: ["Hebr 10,16f; Jer 31,1", "Gegensatz: das auf Steintafeln geschriebene Gesetz vom Sinai."] },
      { number: 34, text: "Keiner wird mehr den andern belehren, man wird nicht zueinander sagen: Erkennt den Herrn!, sondern sie alle, Klein und Groß, werden mich erkennen - Spruch des Herrn. Denn ich verzeihe ihnen die Schuld, an ihre Sünde denke ich nicht mehr.", footnotes: ["33,8; Röm 11,27"] },
      { number: 35, text: "So spricht der Herr, / der die Sonne bestimmt zum Licht am Tag, der den Mond und die Sterne bestellt / zum Licht in der Nacht, der das Meer aufwühlt, / dass die Wogen brausen, / - Herr der Heere ist sein Name:", heading: "Das unvergängliche Heil: 31,35-40", footnotes: ["Gen 1,14-18"] },
      { number: 36, text: "Nur wenn jemals diese Ordnungen / vor meinen Augen ins Wanken gerieten / - Spruch des Herrn -, dann hörten auch Israels Nachkommen auf, / für alle Zeit vor meinen Augen ein Volk zu sein.", footnotes: ["33,20.25f"] },
      { number: 37, text: "So spricht der Herr: Nur wenn die Himmel droben abgemessen / und unten die Grundfesten der Erde erforscht werden könnten, dann verwürfe auch ich Israels ganze Nachkommenschaft / zur Strafe für all das, was sie getan haben / - Spruch des Herrn." },
      { number: 38, text: "Seht, es werden Tage kommen - Spruch des Herrn -, da wird die Stadt für den Herrn wieder aufgebaut, vom Turm Hananels bis zum Eckturm." },
      { number: 39, text: "Weiter läuft die Messschnur geradeaus zum Hügel Gareb und wendet sich nach Goa." },
      { number: 40, text: "Das ganze Tal der Leichen und der Fett-Asche und die ganzen Hänge bis zum Kidronbach, bis zur Ecke des Rosstors im Osten werden dem Herrn heilig sein. Sie werden niemals wieder zerstört und eingerissen werden." },
    ] },
    { number: 32, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging im zehnten Jahr Zidkijas, des Königs von Juda - das ist das achtzehnte Jahr Nebukadnezzars.", heading: "Die Heilsworte: 32,1 - 35,19 | Der Ackerkauf in der Notzeit: 32,1-44", footnotes: ["Es ist das Jahr 587 v. Chr."] },
      { number: 2, text: "Damals belagerte das Heer des Königs von Babel Jerusalem. Der Prophet Jeremia befand sich im Wachhof am Palast des Königs von Juda in Haft.", footnotes: ["2 Kön 25,1f"] },
      { number: 3, text: "Dort hatte ihn Zidkija, der König von Juda, gefangen gesetzt mit der Begründung: Warum hast du geweissagt: So spricht der Herr: Ich gebe diese Stadt in die Hand des Königs von Babel und er wird sie erobern.", footnotes: ["(3-4) 34,2f"] },
      { number: 4, text: "Auch Zidkija, der König von Juda, wird der Hand der Chaldäer nicht entrinnen, sondern in die Hand des Königs von Babel gegeben werden, sodass er von Mund zu Mund mit ihm reden und ihn Auge in Auge sehen wird." },
      { number: 5, text: "Er wird Zidkija nach Babel bringen; dort wird er bleiben, bis ich ihn zur Rechenschaft ziehe - Spruch des Herrn. Wenn ihr mit den Chaldäern Krieg führt, werdet ihr kein Glück haben.", footnotes: ["52,11"] },
      { number: 6, text: "Jeremia sagte: Das Wort des Herrn erging an mich:" },
      { number: 7, text: "Hanamel, der Sohn deines Onkels Schallum, wird zu dir kommen und sagen: Kauf dir meinen Acker in Anatot; denn dir steht es nach dem Einlösungsrecht zu, ihn zu kaufen.", footnotes: ["Zum Einlösungsrecht vgl. Lev 25,25 und Rut 4,3 sowie die Anmerkung zu Lev 25,25 und Rut 1,5f. Wahrscheinlich wollte Jeremias Verwandter den Acker verkaufen, weil er in wirtschaftlichen Schwierigkeiten war. Jeremia ist bereit, seiner Löserpflicht nachzukommen, um damit anzudeuten, dass man im Land auch wieder Äcker kaufen, also ein normales Geschäftsleben führen wird (vgl. V. 44)."] },
      { number: 8, text: "Tatsächlich kam Hanamel, der Sohn meines Onkels, dem Wort des Herrn gemäß zu mir in den Wachhof und sagte zu mir: Kauf doch meinen Acker in Anatot [im Land Benjamin]; denn du hast das Erwerbs- und Einlösungsrecht. Kauf ihn dir! Da erkannte ich, dass es das Wort des Herrn war." },
      { number: 9, text: "So kaufte ich von Hanamel, dem Sohn meines Onkels, den Acker in Anatot und wog ihm das Geld ab; siebzehn Silberschekel betrug die Summe." },
      { number: 10, text: "Ich schrieb die Kaufurkunde, versiegelte sie, nahm auch Zeugen hinzu und wog das Silber auf der Waage ab, alles nach Gesetz und Vorschrift." },
      { number: 11, text: "Dann nahm ich die Kaufurkunde, die versiegelte und die offene." },
      { number: 12, text: "Ich übergab die Urkunde Baruch, dem Sohn Nerijas, des Sohnes Machsejas, in Gegenwart Hanamels, des Sohnes meines Onkels, und vor den Zeugen, die die Kaufurkunde unterschrieben hatten, sowie in Gegenwart aller Judäer, die sich im Wachhof aufhielten." },
      { number: 13, text: "In ihrer Gegenwart gab ich Baruch den Auftrag:" },
      { number: 14, text: "[So spricht der Herr der Heere, der Gott Israels:] Nimm diese Urkunden, die versiegelte Kaufurkunde und auch die offene, und leg sie in ein Tongefäß, damit sie lange Zeit erhalten bleiben.", footnotes: ["14a: Zusatz, irrtümlich aus V. 15 eingefügt. - Die Kaufurkunden werden doppelt ausgefertigt: Ein Exemplar wird in einem Tongefäß verwahrt, das zweite wird dem Käufer als Besitzurkunde übergeben."] },
      { number: 15, text: "Denn so spricht der Herr der Heere, der Gott Israels: Man wird wieder Häuser, Äcker und Weinberge kaufen in diesem Land." },
      { number: 16, text: "Nachdem ich die Kaufurkunde Baruch, dem Sohn Nerijas, übergeben hatte, betete ich zum Herrn:" },
      { number: 17, text: "Ach, mein Herr und Gott! Du hast Himmel und Erde erschaffen durch deine große Kraft und deinen hoch erhobenen Arm. Nichts ist dir unmöglich.", footnotes: ["27,5"] },
      { number: 18, text: "Du übst Gnade an Tausenden, doch zahlst du die Schuld der Väter ihren Söhnen heim, die nach ihnen kommen, du gewaltiger, starker Gott, dessen Name Herr der Heere ist.", footnotes: ["Ex 20,5f; 34,6f"] },
      { number: 19, text: "Groß bist du an Rat und mächtig an Tat; deine Augen wachen über alle Wege der Menschen, um jedem entsprechend seinem Verhalten und seinem Verdienst zu vergelten." },
      { number: 20, text: "Du hast an Israel und an den Menschen Zeichen und Wunder getan, in Ägypten und bis auf den heutigen Tag. So hast du dir einen Namen gemacht, wie du ihn noch heute hast." },
      { number: 21, text: "Du hast dein Volk Israel unter Zeichen und Wundern, mit starker Hand, mit hoch erhobenem Arm und gewaltigen Schreckenstaten aus Ägypten herausgeführt." },
      { number: 22, text: "Du hast ihnen dieses Land gegeben, das du ihren Vätern eidlich zugesichert hattest, ein Land, in dem Milch und Honig fließen." },
      { number: 23, text: "Als sie aber dorthin kamen und es in Besitz nahmen, hörten sie nicht auf deine Stimme und folgten deiner Weisung nicht. Nichts von alledem, was du ihnen befohlen hast, haben sie getan; darum hast du über sie all dies Unheil gebracht.", footnotes: ["Dan 9,10-13"] },
      { number: 24, text: "Schon kommen die Wälle bis an die Stadt heran, bald wird man sie einnehmen; durch Schwert, Hunger und Pest ist die Stadt den Chaldäern preisgegeben, die gegen sie ankämpfen. Was du angedroht hast, ist eingetroffen; du siehst es ja selbst." },
      { number: 25, text: "Dennoch, mein Herr und Gott, sagst du zu mir: Kauf dir den Acker für Geld und nimm Zeugen hinzu! Aber die Stadt ist doch den Chaldäern preisgegeben." },
      { number: 26, text: "Nun erging an mich das Wort des Herrn:" },
      { number: 27, text: "Siehe, ich bin der Herr, der Gott aller Sterblichen. Ist mir denn irgendetwas unmöglich?" },
      { number: 28, text: "Darum - so spricht der Herr: Ich gebe diese Stadt in die Hand der Chaldäer und in die Hand Nebukadnezzars, des Königs von Babel, und er wird sie einnehmen." },
      { number: 29, text: "Die Chaldäer, die gegen diese Stadt ankämpfen, werden eindringen, die Stadt in Brand stecken und einäschern samt den Häusern, auf deren Dächern man dem Baal Rauchopfer und fremden Göttern Trankopfer dargebracht hat, um mich zu erzürnen.", footnotes: ["19,13"] },
      { number: 30, text: "Denn die Leute von Israel und Juda haben von Jugend an immer nur das getan, was mir missfiel, ja, die Leute von Israel haben mich durch ihr Verhalten stets nur erzürnt - Spruch des Herrn." },
      { number: 31, text: "In der Tat, diese Stadt hat seit ihrer Gründung bis zum heutigen Tag meinen Zorn und Grimm erregt, sodass ich sie von meinem Angesicht verstoßen muss" },
      { number: 32, text: "wegen all des Bösen, das die Leute von Israel und Juda verübt haben, um mich zu erzürnen, sie, ihre Könige, ihre Beamten, ihre Priester und ihre Propheten, die Leute von Juda und die Einwohner Jerusalems.", footnotes: ["(32-33) 2,26f"] },
      { number: 33, text: "Sie haben mir den Rücken zugewandt und nicht das Gesicht. Ich habe sie unermüdlich belehrt, aber sie hörten nicht darauf und besserten sich nicht." },
      { number: 34, text: "Vielmehr stellten sie in dem Haus, über dem mein Name ausgerufen ist, ihre Scheusale auf, um es zu entweihen.", footnotes: ["7,30f; 19,5"] },
      { number: 35, text: "Sie errichteten die Kulthöhe des Baal im Tal Ben-Hinnom, um ihre Söhne und Töchter für den Moloch durchs Feuer gehen zu lassen. Das habe ich ihnen nie befohlen und niemals ist mir in den Sinn gekommen, solchen Gräuel zu verlangen und Juda in Sünde zu stürzen.", footnotes: ["Zum Hinnom-Tal vgl. 19,5 und die Anmerkung zu 7,31. Kinder durchs Feuer gehen lassen: Umschreibung für Kinderopfer; vgl. die Anmerkung zu Lev 18,21."] },
      { number: 36, text: "Jetzt aber - so spricht der Herr, der Gott Israels, über diese Stadt, von der ihr sagt, sie sei durch Schwert, Hunger und Pest dem König von Babel preisgegeben:" },
      { number: 37, text: "Seht, ich sammle sie aus allen Ländern, wohin ich sie in meinem Zorn und Grimm und in großem Groll versprengt habe. Ich bringe sie wieder zurück an diesen Ort und lasse sie in Sicherheit wohnen." },
      { number: 38, text: "Sie werden mein Volk sein und ich werde ihr Gott sein.", footnotes: ["31,1.33"] },
      { number: 39, text: "Ich bringe sie dazu, nur eines im Sinn zu haben und nur eines zu erstreben: mich alle Tage zu fürchten, ihnen und ihren Nachkommen zum Heil." },
      { number: 40, text: "Ich schließe mit ihnen einen ewigen Bund, dass ich mich nicht von ihnen abwenden will, sondern ihnen Gutes erweise. Ich lege ihnen die Furcht vor mir ins Herz, damit sie nicht von mir weichen." },
      { number: 41, text: "Ich werde mich über sie freuen, wenn ich ihnen Gutes erweise. In meiner Treue pflanze ich sie ein in diesem Land, aus ganzem Herzen und aus ganzer Seele." },
      { number: 42, text: "Denn so spricht der Herr: Wie ich über dieses Volk all das große Unheil gebracht habe, so bringe ich über sie all das Gute, das ich ihnen verspreche." },
      { number: 43, text: "Man wird wieder Felder kaufen in diesem Land, von dem ihr sagt: Es ist eine Wüste, ohne Mensch und Vieh, der Hand der Chaldäer preisgegeben.", footnotes: ["33,10; 32,25"] },
      { number: 44, text: "Äcker wird man wieder kaufen für Geld, Kaufurkunden ausstellen und versiegeln und Zeugen hinzunehmen im Land Benjamin, in der Umgebung Jerusalems, in den Städten Judas und des Gebirges, in den Städten der Schefela und des Negeb. Denn ich wende ihr Geschick - Spruch des Herrn.", footnotes: ["29,14; 30,3; 33,7"] },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Das Wort des Herrn erging an Jeremia zum zweiten Mal, während er noch im Wachhof eingesperrt war:", heading: "Das neue Heil für Jerusalem und Juda: 33,1-26", footnotes: ["32,1f"] },
      { number: 2, text: "So spricht der Herr, der die Erde erschaffen, sie geformt und fest gegründet hat, Jahwe ist sein Name:" },
      { number: 3, text: "Rufe zu mir, so will ich dir antworten und dir große, unfassbare Dinge mitteilen, die du nicht kennst." },
      { number: 4, text: "Denn so spricht der Herr, der Gott Israels, über die Häuser dieser Stadt und die Häuser der Könige Judas, die man eingerissen hat, um sie gegen die Belagerungswälle und für Kriegszwecke zu verwenden,", footnotes: ["4f: Text vielleicht durch Zusätze erweitert."] },
      { number: 5, text: "und die dazu dienten, mit den Chaldäern Krieg zu führen und mit den Leichen der Leute angefüllt zu werden, die ich in meinem Zorn und Grimm erschlug, weil ich mein Angesicht vor dieser Stadt wegen all ihrer bösen Taten verborgen hatte:" },
      { number: 6, text: "Seht, ich bringe ihnen Genesung und Heilung; ich mache sie wieder heil und gewähre ihnen beständiges Wohlergehen.", footnotes: ["30,17"] },
      { number: 7, text: "Ich wende das Geschick Judas und Jerusalems und baue sie auf wie ehedem.", footnotes: ["32,44"] },
      { number: 8, text: "Ich reinige sie von all ihrer Schuld, die sie gegen mich begangen haben, und ich vergebe ihnen alle ihre Verfehlungen, mit denen sie gesündigt und sich gegen mich aufgelehnt haben.", footnotes: ["50,20"] },
      { number: 9, text: "Dann wird Jerusalem meine Freude sein, mein Lobpreis und Ruhm bei allen Völkern der Erde, wenn sie von all dem Guten hören, das ich tue; sie werden zittern und beben wegen all des Guten und des Heils, das ich ihm erweise." },
      { number: 10, text: "So spricht der Herr: An diesem Ort, von dem ihr sagt: Verwüstet ist er, ohne Mensch und Vieh!, in den Städten Judas und auf den Straßen Jerusalems, die verödet sind, ohne Menschen, ohne Bewohner und ohne Vieh,", footnotes: ["32,43"] },
      { number: 11, text: "hört man wieder Jubelruf und Freudenruf, den Ruf des Bräutigams und den Ruf der Braut; sie rufen und singen: Dankt dem Herrn der Heere; denn der Herr ist gütig, denn seine Huld währt ewig!, und bringen Dankopfer zum Tempel des Herrn. Ich wende das Geschick des Landes: Es soll werden wie ehedem, spricht der Herr.", footnotes: ["7,34; 25,10; Ps 106,1; 107,1"] },
      { number: 12, text: "So spricht der Herr der Heere: An diesem Ort, der verwüstet ist, ohne Mensch und Vieh, und in allen seinen Städten wird es eine Weide für Hirten geben, die ihre Herden lagern lassen." },
      { number: 13, text: "In den Städten des Gebirges, der Schefela und des Negeb, im Land Benjamin, in der Umgebung Jerusalems und in den Städten Judas ziehen wieder die Schafe an dem vorbei, der sie mit der Hand zählt, spricht der Herr." },
      { number: 14, text: "Seht, es werden Tage kommen - Spruch des Herrn -, da erfülle ich das Heilswort, das ich über das Haus Israel und über das Haus Juda gesprochen habe." },
      { number: 15, text: "In jenen Tagen und zu jener Zeit werde ich für David einen gerechten Spross aufsprießen lassen. Er wird für Recht und Gerechtigkeit sorgen im Land.", footnotes: ["23,5f"] },
      { number: 16, text: "In jenen Tagen wird Juda gerettet werden, Jerusalem kann in Sicherheit wohnen. Man wird ihm den Namen geben: Jahwe ist unsere Gerechtigkeit.", footnotes: ["Jahwe ist unsere Gerechtigkeit: Anspielung an den Namen des von Jeremia abgelehnten Königs Zidkija, der bedeutet: «Jahwe ist (meine) Gerechtigkeit»; der König trägt den Namen zu Unrecht."] },
      { number: 17, text: "Denn so spricht der Herr: Nie soll es David an einem Nachkommen fehlen, der auf dem Thron des Hauses Israel sitzt.", footnotes: ["1 Kön 2,4"] },
      { number: 18, text: "Auch den levitischen Priestern soll es nie an einem Nachkommen fehlen, der alle Tage vor meinen Augen Brandopfer darbringt, Speiseopfer verbrennt und Schlachtopfer zurichtet." },
      { number: 19, text: "Das Wort des Herrn erging an Jeremia:" },
      { number: 20, text: "So spricht der Herr: Nur wenn mein Bund mit dem Tag und mein Bund mit der Nacht gebrochen werden könnte, sodass es nicht mehr Tag und Nacht würde zur rechten Zeit,", footnotes: ["(20-21) 31,35f", "20.25: Der Bund mit Tag und Nacht ist die unabänderliche Ordnung im Wechsel von Tag und Nacht; vgl. den Noach-Bund von Gen 9,8-17."] },
      { number: 21, text: "dann könnte auch mein Bund mit meinem Knecht David gebrochen werden, sodass er keinen Sohn hätte, der auf seinem Thron König wäre, und ebenso mein Bund mit den levitischen Priestern, die mir dienen." },
      { number: 22, text: "So unzählbar das Heer des Himmels und so unmessbar der Sand des Meeres ist, so zahlreich mache ich die Nachkommen meines Knechtes David und die Leviten, die mir dienen." },
      { number: 23, text: "Das Wort des Herrn erging an Jeremia:" },
      { number: 24, text: "Hast du nicht bemerkt, was diese Leute reden: Die beiden Stammesverbände, die der Herr erwählt hatte, hat er verworfen!, und wie sie mein Volk verachten, sodass es in ihren Augen kein Volk mehr ist?" },
      { number: 25, text: "So spricht der Herr: So gewiss ich meinen Bund mit dem Tag und mit der Nacht und die Ordnungen von Himmel und Erde festgesetzt habe," },
      { number: 26, text: "so sicher werde ich auch die Nachkommen Jakobs und meines Knechtes David nicht verwerfen; aus seinen Nachkommen werde ich die Herrscher über die Nachkommen Abrahams, Isaaks und Jakobs nehmen. Denn ich werde ihr Geschick wenden und mich ihrer erbarmen." },
    ] },
    { number: 34, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging, als Nebukadnezzar, der König von Babel, und sein ganzes Heer, alle Königreiche seines Herrschaftsgebietes und alle Völkerschaften gegen Jerusalem und alle seine umliegenden Städte Krieg führten:", heading: "Zidkijas Ende: 34,1-7" },
      { number: 2, text: "So spricht der Herr, der Gott Israels: Mach dich auf, rede mit Zidkija, dem König von Juda, und sag zu ihm: So spricht der Herr: Ich gebe diese Stadt in die Hand des Königs von Babel und er wird sie niederbrennen.", footnotes: ["(2-3) 32,3f"] },
      { number: 3, text: "Auch du wirst seiner Hand nicht entrinnen, sondern ergriffen und in seine Hand gegeben werden. Auge in Auge wirst du den König von Babel sehen und von Mund zu Mund wird er mit dir reden und nach Babel wirst du kommen." },
      { number: 4, text: "Doch höre das Wort des Herrn, Zidkija, König von Juda! So spricht der Herr über dich: Du brauchst nicht durch das Schwert zu sterben." },
      { number: 5, text: "In Frieden kannst du sterben, und wie deinen Vätern und Vorgängern, den früheren Königen, so wird man auch dir zu Ehren Totenfeuer anzünden und dir die Totenklage halten: «Ach, der Herrscher!» Ich bin es, der dieses Wort gesprochen hat - Spruch des Herrn.", footnotes: ["2 Chr 16,14; Jer 22,18"] },
      { number: 6, text: "Der Prophet Jeremia sagte alle diese Worte zu Zidkija, dem König von Juda, in Jerusalem," },
      { number: 7, text: "als das Heer des Königs von Babel bereits gegen Jerusalem und die übrig gebliebenen Städte Judas, nämlich Lachisch und Aseka, kämpfte; denn nur diese befestigten Städte waren in Juda noch übrig geblieben." },
      { number: 8, text: "Das Wort, das vom Herrn an Jeremia erging, als König Zidkija mit dem ganzen Volk in Jerusalem das Abkommen getroffen hatte, eine Freilassung auszurufen.", heading: "Der Widerruf der Sklavenfreilassung: 34,8-22" },
      { number: 9, text: "Es sollte nämlich jeder seinen hebräischen Sklaven und seine hebräische Sklavin freilassen und keiner sollte mehr seinen hebräischen Stammesbruder als Sklaven halten." },
      { number: 10, text: "Dem hatten sich alle Großen gefügt, ebenso das ganze Volk, das dem Abkommen beigetreten war, dass jeder seinen Sklaven oder seine Sklavin freilassen und nicht mehr als Sklave halten werde. Sie hatten gehorcht und die Sklaven freigelassen." },
      { number: 11, text: "Danach aber holten sie die Sklaven und Sklavinnen, die sie freigelassen hatten, zurück und machten sie mit Gewalt wieder zu Sklaven und Sklavinnen." },
      { number: 12, text: "Da erging das Wort des Herrn an Jeremia:" },
      { number: 13, text: "So spricht der Herr, der Gott Israels: Ich habe mit euren Vätern, als ich sie aus Ägypten, dem Sklavenhaus, herausführte, ein Abkommen getroffen und verlangt:" },
      { number: 14, text: "Alle sieben Jahre soll jeder von euch seinen hebräischen Stammesbruder, der sich ihm verkauft hat, freilassen; sechs Jahre soll er dein Sklave sein, dann sollst du ihn freilassen. Aber eure Väter haben mir nicht gehorcht und mir ihr Ohr nicht zugeneigt.", footnotes: ["Ex 21,2; Lev 25,41; Dtn 15,12"] },
      { number: 15, text: "Da seid ihr jetzt umgekehrt und habt das getan, was in meinen Augen recht ist, indem jeder für seinen Nächsten die Freilassung ausrief. Vor mir hattet ihr ein Abkommen getroffen in dem Haus, über dem mein Name ausgerufen ist." },
      { number: 16, text: "Aber ihr seid wieder umgekehrt und habt meinen Namen entweiht; denn jeder von euch hat seinen Sklaven oder seine Sklavin zurückgeholt, die ihr doch völlig freigelassen hattet. Ihr habt sie gezwungen, wieder eure Sklaven und Sklavinnen zu werden." },
      { number: 17, text: "Darum - so spricht der Herr: Ihr habt mir nicht gehorcht und keiner hat für seinen Stammesbruder und seinen Nächsten die Freilassung ausgerufen. Wohlan, so rufe ich euch eine Freilassung aus - Spruch des Herrn - für Schwert, Pest und Hunger und ich mache euch zu einem Bild des Schreckens für alle Reiche der Erde." },
      { number: 18, text: "Ich mache die Männer, die mein Abkommen verletzt und die Worte der Abmachung, die sie vor mir getroffen hatten, nicht gehalten haben, dem Kalb gleich, das sie in zwei Hälften zerschnitten haben und zwischen dessen Stücken sie hindurchgegangen sind.", footnotes: ["Diese Form des Bundesschlusses wird in Gen 15,9f.17 beschrieben. Das Hindurchschreiten zwischen den Teilen eines Tieres ist sinnbildliche Handlung für eine bedingte Selbstverfluchung: Ich will selbst in Stücke gehauen werden, wenn ich den Vertrag breche."] },
      { number: 19, text: "Die Großen Judas und Jerusalems, die Höflinge, die Priester und alle Bürger des Landes, die zwischen den Stücken des Kalbes hindurchgegangen sind," },
      { number: 20, text: "sie alle gebe ich in die Hand ihrer Feinde und derer, die ihnen nach dem Leben trachten. Ihre Leichen sollen den Vögeln des Himmels und den Tieren des Feldes zum Fraß dienen." },
      { number: 21, text: "Auch Zidkija, den König von Juda, und seine Großen liefere ich ihren Feinden aus und denen, die ihnen nach dem Leben trachten, dem Heer des Königs von Babel, das eben von euch abgezogen ist." },
      { number: 22, text: "Schon gebe ich den Befehl - Spruch des Herrn - und hole sie zu dieser Stadt zurück, damit sie gegen sie kämpfen, sie erobern und niederbrennen. Die Städte Judas mache ich zur menschenleeren Wüste.", footnotes: ["37,8f"] },
    ] },
    { number: 35, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging in den Tagen Jojakims, des Sohnes Joschijas, des Königs von Juda:", heading: "Das Vorbild der Rechabiter: 35,1-19" },
      { number: 2, text: "Geh hin zur Gemeinschaft der Rechabiter, rede mit ihnen, führ sie zum Haus des Herrn in eine der Hallen und gib ihnen Wein zu trinken!" },
      { number: 3, text: "Da holte ich Jaasanja, den Sohn Jirmejas, des Sohnes Habazzinjas, samt seinen Brüdern und allen seinen Söhnen und der ganzen Gemeinschaft der Rechabiter." },
      { number: 4, text: "Ich führte sie zum Haus des Herrn in die Halle der Söhne Hanans, des Sohnes Jigdaljas, des Gottesmannes, die neben der Halle der Großen oberhalb der Halle des Schwellenwächters Maaseja, des Sohnes Schallums, gelegen ist." },
      { number: 5, text: "Dann setzte ich den Leuten von der Gemeinschaft der Rechabiter gefüllte Weinkrüge und Becher vor und sagte zu ihnen: Trinkt Wein!" },
      { number: 6, text: "Sie aber entgegneten: Wir trinken keinen Wein; denn unser Ahnherr Jonadab, der Sohn Rechabs, hat uns geboten: Ihr sollt niemals Wein trinken, weder ihr selbst noch eure Söhne.", footnotes: ["2 Kön 10,15"] },
      { number: 7, text: "Auch sollt ihr kein Haus bauen, keine Saat bestellen, keinen Weinberg pflanzen oder besitzen. Vielmehr sollt ihr euer Leben lang in Zelten wohnen, damit ihr lange lebt in dem Land, in dem ihr euch als Fremde aufhaltet." },
      { number: 8, text: "Wir gehorchten dem Auftrag unseres Ahnherrn Jonadab, des Sohnes Rechabs, in allem, was er uns gebot; wir tranken also zeitlebens keinen Wein, weder wir noch unsere Frauen, Söhne und Töchter." },
      { number: 9, text: "Wir bauten uns keine Wohnhäuser, wir besaßen keinen Weinberg, keinen Acker und keine Saat." },
      { number: 10, text: "Wir wohnten in Zelten und gehorsam handelten wir genau so, wie unser Ahnherr Jonadab es uns geboten hat." },
      { number: 11, text: "Als jedoch Nebukadnezzar, der König von Babel, gegen das Land heranzog, da sagten wir: Kommt, wir wollen uns vor dem Heer der Chaldäer und dem Heer der Aramäer nach Jerusalem begeben. So ließen wir uns in Jerusalem nieder." },
      { number: 12, text: "Nun erging das Wort des Herrn an Jeremia:" },
      { number: 13, text: "So spricht der Herr der Heere, der Gott Israels: Geh hin und sag zu den Leuten von Juda und zu den Einwohnern Jerusalems: Wollt ihr euch nicht endlich belehren lassen und auf meine Worte hören? - Spruch des Herrn." },
      { number: 14, text: "Das Gebot Jonadabs, des Sohnes Rechabs, der seinen Söhnen das Weintrinken verboten hat, wird treu erfüllt; sie trinken keinen Wein bis zum heutigen Tag, weil sie dem Befehl ihres Ahnherrn gehorchen. Ich aber habe immer wieder zu euch geredet, doch ihr habt mir nicht gehorcht." },
      { number: 15, text: "Und immer wieder sandte ich zu euch alle meine Knechte, die Propheten, mit der Mahnung: Kehrt doch alle um von eurem schlechten Weg! Bessert euer Tun und lauft nicht anderen Göttern nach, um ihnen zu dienen. Dann dürft ihr in dem Land bleiben, das ich euch und euren Vätern gegeben habe. Ihr aber habt euer Ohr nicht geneigt und nicht auf mich gehört.", footnotes: ["25,4-7"] },
      { number: 16, text: "Ja, die Söhne Jonadabs, des Sohnes Rechabs, haben das Gebot, das ihnen ihr Ahnherr gegeben hat, erfüllt. Dieses Volk aber hat mir nicht gehorcht." },
      { number: 17, text: "Darum - so spricht der Herr, der Gott der Heere, der Gott Israels: Seht, ich bringe über Juda und über alle Einwohner Jerusalems das ganze Unheil, das ich ihnen angedroht habe, weil sie nicht hörten, sooft ich zu ihnen redete, und keine Antwort gaben, sooft ich sie rief." },
      { number: 18, text: "Zur Gemeinschaft der Rechabiter aber sagte Jeremia: So spricht der Herr der Heere, der Gott Israels: Weil ihr dem Gebot eures Ahnherrn Jonadab gehorcht, alle seine Gebote gehalten und nach allen seinen Anordnungen gehandelt habt," },
      { number: 19, text: "darum - so spricht der Herr der Heere, der Gott Israels: Niemals soll es Jonadab, dem Sohne Rechabs, an einem Nachkommen fehlen, der in meinem Dienst steht." },
    ] },
    { number: 36, verses: [
      { number: 1, text: "Im vierten Jahr Jojakims, des Sohnes Joschijas, des Königs von Juda, erging vom Herrn dieses Wort an Jeremia:", heading: "Die Berichte Baruchs: 36,1 - 45,5 | Die Verbrennung der Buchrolle: 36,1-32", footnotes: ["25,1", "Es ist das Jahr 605 v. Chr. - Man sieht, dass die einzelnen Abschnitte nicht chronologisch, sondern sachlich geordnet sind. Vorher waren bereits Ereignisse berichtet, die viel später geschehen sind."] },
      { number: 2, text: "Nimm dir eine Buchrolle und schreib darauf alle Worte, die ich zu dir über Israel und Juda und über alle Völker gesprochen habe, seitdem ich zu dir rede, von den Tagen Joschijas an bis heute.", footnotes: ["25,3"] },
      { number: 3, text: "Vielleicht werden die Leute vom Haus Juda, wenn sie hören, wie viel Unheil ich ihnen antun will, umkehren von ihrem bösen Weg und ich kann ihnen Schuld und Sünde verzeihen.", footnotes: ["26,3"] },
      { number: 4, text: "Da rief Jeremia Baruch, den Sohn Nerijas, und Baruch schrieb nach dem Diktat Jeremias alle Worte, die der Herr zu ihm gesprochen hatte, auf eine Buchrolle." },
      { number: 5, text: "Jeremia gab hierauf Baruch folgenden Auftrag: Mir ist es verwehrt, das Haus des Herrn zu betreten.", footnotes: ["Jeremia hatte wahrscheinlich vom König Redeverbot erhalten; vielleicht war das Prophetenwort 22,18f vorausgegangen."] },
      { number: 6, text: "Darum geh du hin und lies am Fasttag aus der Rolle, die du nach meinem Diktat geschrieben hast, dem Volk im Haus des Herrn die Worte des Herrn vor. Auch allen Judäern, die aus ihren Städten herbeiströmen, sollst du sie vorlesen." },
      { number: 7, text: "Vielleicht flehen sie vor dem Herrn um Erbarmen und kehren um, jeder von seinem bösen Weg; denn groß ist der Zorn und Grimm, den der Herr diesem Volk angedroht hat." },
      { number: 8, text: "Baruch, der Sohn Nerijas, handelte genau nach dem Auftrag des Propheten Jeremia und las im Haus des Herrn die Worte des Herrn aus dem Buch vor." },
      { number: 9, text: "Im fünften Jahr Jojakims, des Sohnes Joschijas, des Königs von Juda, hatte man im neunten Monat alles Volk in Jerusalem und alle Leute, die von den Städten Judas nach Jerusalem kommen sollten, zu einem Fasten vor dem Herrn aufgerufen." },
      { number: 10, text: "Nun las Baruch im Haus des Herrn dem ganzen Volk aus dem Buch die Worte Jeremias vor, und zwar in der Halle Gemarjas, des Sohnes des Staatsschreibers Schafan, im oberen Vorhof am Eingang des Neuen Tempeltores." },
      { number: 11, text: "Als Micha, der Sohn Gemarjas, des Sohnes Schafans, alle Worte des Herrn aus dem Buch vernommen hatte," },
      { number: 12, text: "ging er hinab in den Königspalast zur Halle des Staatsschreibers. Dort waren gerade alle hohen Beamten zu einer Sitzung versammelt: der Staatsschreiber Elischama, Delaja, der Sohn Schemajas, Elnatan, der Sohn Achbors, Gemarja, der Sohn Schafans, Zidkija, der Sohn Hananjas, und alle sonstigen Beamten." },
      { number: 13, text: "Micha teilte ihnen alle Worte mit, die er gehört hatte, als Baruch dem Volk aus dem Buch vorlas." },
      { number: 14, text: "Da schickten die versammelten Beamten Jehudi, den Sohn Netanjas, des Sohnes Schelemjas, des Sohnes Kuschis, zu Baruch und ließen ihm sagen: Nimm die Rolle, aus der du dem Volk vorgelesen hast, und komm hierher! Sogleich nahm Baruch, der Sohn Nerijas, die Rolle und ging zu ihnen." },
      { number: 15, text: "Sie sagten zu ihm: Setz dich und lies sie uns vor! Nun las ihnen Baruch vor." },
      { number: 16, text: "Als sie all die Worte hörten, schauten sie einander erschrocken an und sagten [zu Baruch]: Wir müssen das alles dem König mitteilen." },
      { number: 17, text: "Sie fragten Baruch: Sag uns doch, wie hast du alle diese Worte niedergeschrieben?" },
      { number: 18, text: "Baruch erwiderte ihnen: Jeremia hat mir alle diese Worte diktiert und ich habe sie mit Tinte in das Buch geschrieben." },
      { number: 19, text: "Darauf sagten die Beamten zu Baruch: Geh und verbirg dich, du und auch Jeremia! Niemand soll wissen, wo ihr seid." },
      { number: 20, text: "Dann begaben sie sich zum König in den Palasthof, nachdem sie die Rolle in der Halle des Staatsschreibers Elischama verwahrt hatten, und berichteten das alles dem König." },
      { number: 21, text: "Da gab der König dem Jehudi den Auftrag, die Rolle zu holen, und dieser holte sie aus der Halle des Staatsschreibers Elischama. Und Jehudi las sie dem König vor und allen Beamten, die um den König herumstanden." },
      { number: 22, text: "Der König wohnte im Winterhaus, es war ja der neunte Monat, und vor ihm brannte das Feuer auf dem Kohlenbecken." },
      { number: 23, text: "Sooft nun Jehudi drei oder vier Spalten gelesen hatte, schnitt sie der König mit dem Schreibermesser ab und warf sie in das Feuer auf dem Kohlenbecken, bis das Feuer auf dem Kohlenbecken die ganze Rolle verzehrt hatte." },
      { number: 24, text: "Niemand erschrak und niemand zerriss seine Kleider, weder der König noch irgendeiner seiner Diener, die all diese Worte gehört hatten.", footnotes: ["2 Kön 22,11"] },
      { number: 25, text: "Selbst als Elnatan, Delaja und Gemarja den König bestürmten, die Rolle nicht zu verbrennen, hörte er nicht auf sie." },
      { number: 26, text: "Vielmehr befahl der König dem Prinzen Jerachmeël, ferner Seraja, dem Sohn Asriëls, und Schelemja, dem Sohn Abdeëls, den Schreiber Baruch und den Propheten Jeremia festzunehmen; aber der Herr hielt sie verborgen." },
      { number: 27, text: "Nachdem der König die Rolle mit den Worten, die Baruch nach dem Diktat Jeremias niedergeschrieben hat, verbrannt hatte, erging das Wort des Herrn an Jeremia:" },
      { number: 28, text: "Nimm dir eine andere Rolle und schreib all die früheren Worte darauf, die auf der ersten Rolle standen, die Jojakim, der König von Juda, verbrannt hat." },
      { number: 29, text: "Über Jojakim aber, den König von Juda, sollst du sagen: So spricht der Herr: Du hast diese Rolle verbrannt und gesagt: Warum hast du darin geschrieben, der König von Babel werde bestimmt kommen, dieses Land verheeren und Mensch und Vieh darin vernichten?" },
      { number: 30, text: "Darum - so spricht der Herr über Jojakim, den König von Juda: Er wird keinen Nachkommen mehr haben, der auf dem Thron Davids sitzt, und sein Leichnam soll weggeworfen werden und der Hitze des Tags und der Kälte der Nacht ausgesetzt sein.", footnotes: ["22,19"] },
      { number: 31, text: "Ich ziehe ihn, seine Nachkommen und seine Diener zur Rechenschaft für ihre Schuld. Ich bringe über sie, über die Einwohner Jerusalems und die Leute von Juda all das Unheil, das ich ihnen angedroht habe, ohne dass sie hören wollten." },
      { number: 32, text: "Da nahm Jeremia eine andere Rolle und übergab sie dem Schreiber Baruch, dem Sohn Nerijas. Dieser schrieb auf sie nach dem Diktat Jeremias alle Worte des Buches, das Jojakim, der König von Juda, im Feuer verbrannt hatte. Ihnen wurden noch viele ähnliche Worte hinzugefügt." },
    ] },
    { number: 37, verses: [
      { number: 1, text: "Anstelle Jojachins, des Sohnes Jojakims, wurde Zidkija, der Sohn Joschijas, König. Ihn hatte Nebukadnezzar, der König von Babel, als König über das Land Juda eingesetzt.", heading: "Die Botschaft an König Zidkija: 37,1-10", footnotes: ["2 Kön 24,17"] },
      { number: 2, text: "Weder er selbst noch seine Diener, noch die Bürger des Landes hörten auf die Worte, die der Herr durch den Propheten Jeremia sprach." },
      { number: 3, text: "König Zidkija sandte einmal Juchal, den Sohn Schelemjas, und den Priester Zefanja, den Sohn Maasejas, zum Propheten Jeremia und ließ ihm sagen: Bete doch für uns zum Herrn, unserem Gott!" },
      { number: 4, text: "Jeremia konnte sich noch frei unter dem Volk bewegen; man hatte ihn noch nicht ins Gefängnis geworfen." },
      { number: 5, text: "Aus Ägypten war damals das Heer des Pharao aufgebrochen, und als die Chaldäer, die Jerusalem belagerten, davon Nachricht erhielten, rückten sie von Jerusalem ab." },
      { number: 6, text: "Nun erging das Wort des Herrn an den Propheten Jeremia:" },
      { number: 7, text: "So spricht der Herr, der Gott Israels: Antwortet dem König von Juda, der euch zu mir gesandt hat, um mich zu befragen: Fürwahr, das Heer des Pharao, das aufgebrochen ist, um euch Hilfe zu bringen, wird in sein Land Ägypten zurückkehren." },
      { number: 8, text: "Dann werden die Chaldäer wieder umkehren, gegen diese Stadt kämpfen, sie erobern und in Brand stecken.", footnotes: ["(8-9) 34,21f"] },
      { number: 9, text: "So spricht der Herr: Täuscht euch nicht selbst mit dem Gedanken: Die Chaldäer ziehen endgültig von uns ab. Nein, sie ziehen nicht ab." },
      { number: 10, text: "Selbst wenn ihr das ganze Heer der Chaldäer, die gegen euch kämpfen, schlagen könntet und nur einige Verwundete von ihnen übrig blieben, sie würden, jeder in seinem Zelt, aufstehen und diese Stadt in Brand stecken." },
      { number: 11, text: "Damals, als das Heer der Chaldäer vor dem Heer des Pharao von Jerusalem abgerückt war,", heading: "Der Prophet im Gefängnis: 37,11-21" },
      { number: 12, text: "wollte sich Jeremia von Jerusalem ins Land Benjamin begeben, um dort mit seinen Verwandten ein Erbe zu teilen.", footnotes: ["32,8f"] },
      { number: 13, text: "Er war bereits am Benjamintor angelangt. Dort stand ein Wachhabender namens Jirija, der Sohn Schelemjas, des Sohnes Hananjas. Er hielt den Propheten Jeremia an und rief: Du willst zu den Chaldäern überlaufen." },
      { number: 14, text: "Jeremia entgegnete: Das ist nicht wahr; ich laufe nicht zu den Chaldäern über. Doch Jirija hörte nicht auf ihn; er nahm Jeremia fest und brachte ihn vor die Beamten." },
      { number: 15, text: "Diese waren über Jeremia wütend, schlugen ihn und warfen ihn in den Kerker, in das Haus des Staatsschreibers Jonatan; denn dieses hatte man als Gefängnis eingerichtet." },
      { number: 16, text: "So kam Jeremia in die Zisternenhöhle mit den Gewölben. Dort saß Jeremia lange Zeit gefangen." },
      { number: 17, text: "Eines Tages ließ ihn König Zidkija holen. Der König fragte ihn heimlich in seinem Palast: Ist ein Wort vom Herrn da? Jeremia bejahte es und antwortete: Du wirst in die Hand des Königs von Babel ausgeliefert werden.", footnotes: ["34,21"] },
      { number: 18, text: "Dann sagte Jeremia zum König Zidkija: Welches Verbrechen habe ich an dir, an deinen Dienern und an diesem Volk begangen, dass ihr mich ins Gefängnis geworfen habt?" },
      { number: 19, text: "Und wo sind jetzt eure Propheten, die euch geweissagt haben: Der König von Babel wird nicht über euch und dieses Land kommen?" },
      { number: 20, text: "Doch nun höre, mein Herr und König! Möge meine Bitte bei dir Gehör finden. Lass mich nicht ins Haus des Staatsschreibers zurückbringen, sonst gehe ich dort zugrunde." },
      { number: 21, text: "Da gab König Zidkija den Befehl, Jeremia im Wachhof zu verwahren, und man versorgte ihn täglich mit einem Laib Brot aus der Bäckergasse, bis alles Brot in der Stadt zu Ende ging. So blieb also Jeremia im Wachhof.", footnotes: ["32,2"] },
    ] },
    { number: 38, verses: [
      { number: 1, text: "Schefatja, der Sohn Mattans, Gedalja, der Sohn Paschhurs, Juchal, der Sohn Schelemjas, und Paschhur, der Sohn Malkijas, hörten von den Worten, die Jeremia zum ganzen Volk redete, indem er sagte:", heading: "Jeremia in der Zisterne: 38,1-6" },
      { number: 2, text: "So spricht der Herr: Wer in dieser Stadt bleibt, der stirbt durch Schwert, Hunger und Pest. Wer aber zu den Chaldäern hinausgeht, der wird überleben; er wird sein Leben wie ein Beutestück gewinnen und davonkommen.", footnotes: ["21,9"] },
      { number: 3, text: "So spricht der Herr: Diese Stadt wird ganz sicher dem Heer des Königs von Babel in die Hände fallen und er wird sie erobern." },
      { number: 4, text: "Darauf sagten die Beamten zum König: Dieser Mann muss mit dem Tod bestraft werden; denn er lähmt mit solchen Reden die Hände der Krieger, die in dieser Stadt noch übrig geblieben sind, und die Hände des ganzen Volkes. Denn dieser Mensch sucht nicht Heil, sondern Unheil für dieses Volk." },
      { number: 5, text: "Der König Zidkija erwiderte: Nun, er ist in eurer Hand; denn der König vermag nichts gegen euch." },
      { number: 6, text: "Da ergriffen sie Jeremia und warfen ihn in die Zisterne des Prinzen Malkija, die sich im Wachhof befand; man ließ ihn an Stricken hinunter. In der Zisterne war kein Wasser, sondern nur Schlamm und Jeremia sank in den Schlamm." },
      { number: 7, text: "Der Kuschiter Ebed-Melech, ein Höfling, der im königlichen Palast bedienstet war, hörte, dass man Jeremia in die Zisterne geworfen hatte. Während der König sich am Benjamintor aufhielt,", heading: "Die Rettung durch den Kuschiter: 38,7-13", footnotes: ["39,15-18", "Ebed-Melech war also trotz seines hebräischen Namens wahrscheinlich ein Neger; vgl. 13,23 und die dortige Anmerkung."] },
      { number: 8, text: "verließ Ebed-Melech den Palast und sagte zum König:" },
      { number: 9, text: "Mein Herr und König, schlecht war alles, was diese Männer dem Propheten Jeremia angetan haben; sie haben ihn in die Zisterne geworfen, damit er dort unten verhungert. Denn es gibt in der Stadt kein Brot mehr." },
      { number: 10, text: "Da befahl der König dem Kuschiter Ebed-Melech: Nimm dir von hier drei Männer mit und zieh den Propheten Jeremia aus der Zisterne herauf, bevor er stirbt." },
      { number: 11, text: "Ebed-Melech nahm die Männer mit sich und ging zum Königspalast in die Kleiderkammer des Vorratshauses. Dort holte er Stücke von abgelegten und zerrissenen Kleidern und ließ sie an Stricken zu Jeremia in die Zisterne hinunter." },
      { number: 12, text: "Dann rief der Kuschiter Ebed-Melech Jeremia zu: Leg die Stücke der abgelegten und zerrissenen Kleider in deine Achselhöhlen unter die Stricke! Jeremia tat es." },
      { number: 13, text: "Nun zogen sie Jeremia an den Stricken hoch und brachten ihn aus der Zisterne herauf. Von da an blieb Jeremia im Wachhof." },
      { number: 14, text: "König Zidkija ließ den Propheten Jeremia zu sich an den dritten Eingang beim Haus des Herrn holen. Der König sagte zu Jeremia: Ich möchte dich nach einem Gotteswort fragen. Verschweig mir nichts!", heading: "Letzte Warnung an den König: 38,14-28" },
      { number: 15, text: "Jeremia antwortete Zidkija: Wenn ich es dir verkünde, lässt du mich bestimmt umbringen, und wenn ich dir einen Rat gebe, hörst du nicht auf mich." },
      { number: 16, text: "Da schwor König Zidkija dem Jeremia heimlich und sagte: So wahr der Herr lebt, der uns dieses Leben gegeben hat, ich lasse dich nicht umbringen und gebe dich nicht in die Hand jener Männer, die dir nach dem Leben trachten." },
      { number: 17, text: "Hierauf sagte Jeremia zu Zidkija: So spricht der Herr, der Gott der Heere, der Gott Israels: Wenn du freiwillig hinausgehst zu den Heerführern des Königs von Babel, dann ist dein Leben gerettet, diese Stadt wird nicht in Brand gesteckt und du bleibst am Leben, du und dein Haus." },
      { number: 18, text: "Gehst du aber nicht hinaus zu den Heerführern des Königs von Babel, dann wird diese Stadt den Chaldäern ausgeliefert. Sie werden sie in Brand stecken und du selbst wirst ihrer Hand nicht entrinnen." },
      { number: 19, text: "König Zidkija entgegnete Jeremia: Ich habe Angst vor den Judäern, die bereits zu den Chaldäern abgefallen sind; man könnte mich ihnen ausliefern und sie würden mir übel mitspielen." },
      { number: 20, text: "Jeremia versicherte: Man wird dich nicht ausliefern. Hör doch auf die Stimme des Herrn in meiner Rede! Dann geht es dir gut und dein Leben bleibt erhalten." },
      { number: 21, text: "Weigerst du dich aber hinauszugehen, so wird geschehen, was der Herr mich sehen ließ:" },
      { number: 22, text: "Ich sah, wie alle Frauen, die im Palast des Königs von Juda noch übrig waren, zu den Obersten des Königs von Babel hinausgeführt wurden. Sie klagten: Überlistet, hereingelegt / haben dich deine guten Freunde; stecken deine Füße im Sumpf, / so machen sich alle davon." },
      { number: 23, text: "Ja, alle deine Frauen und Kinder wird man zu den Chaldäern hinausführen; auch du wirst ihrer Hand nicht entrinnen, sondern wirst gefangen dem König von Babel ausgeliefert werden; diese Stadt aber wird man in Brand stecken.", footnotes: ["32,4; 34,3"] },
      { number: 24, text: "Zidkija sagte zu Jeremia: Niemand darf von diesem Gespräch erfahren, sonst musst du sterben." },
      { number: 25, text: "Wenn aber die Beamten erfahren, dass ich mit dir geredet habe, und wenn sie zu dir kommen und dich auffordern: Teil uns mit, was du zum König gesagt hast und was der König zu dir gesagt hat, verheimliche uns nichts, sonst bringen wir dich um!," },
      { number: 26, text: "dann antworte ihnen: Ich habe an den König die Bitte gerichtet, mich nicht ins Haus Jonatans zurückbringen zu lassen, weil ich dort zugrunde gehe." },
      { number: 27, text: "Tatsächlich kamen alle Beamten zu Jeremia und fragten ihn. Er antwortete ihnen genau so, wie ihm der König geboten hatte. Da ließen sie von ihm ab; denn niemand hatte das Gespräch gehört." },
      { number: 28, text: "So blieb Jeremia im Wachhof bis zu dem Tag, an dem Jerusalem erobert wurde.", footnotes: ["37,21"] },
    ] },
    { number: 39, verses: [
      { number: 1, text: "[Nach der Eroberung Jerusalems:]", heading: "Der Fall Jerusalems: 39,1-10", footnotes: ["1-10: Zusammengesetzt aus Teilen von 52,4-16 und 2 Kön 25,1-12. Im neunten Jahr Zidkijas, des Königs von Juda, im zehnten Monat, rückte Nebukadnezzar, der König von Babel, mit seiner ganzen Streitmacht vor Jerusalem und belagerte es."] },
      { number: 2, text: "Im elften Jahr Zidkijas, am neunten Tag des vierten Monats, wurden Breschen in die Stadtmauer geschlagen." },
      { number: 3, text: "Da zogen alle Heerführer des Königs von Babel ein und ließen sich im Mitteltor nieder: [Nergal-Sarezer, der Fürst von Sin-Magir, und] Nebuschasban, der Oberkämmerer, Nergal-Sarezer, der Oberhofmeister, und alle übrigen Heerführer des Königs von Babel.", footnotes: ["Aufzählung der Namen unsicher; vielleicht nach V. 13 zu korrigieren."] },
      { number: 4, text: "Als Zidkija, der König von Juda, und alle Krieger dies sahen, ergriffen sie die Flucht und verließen bei Nacht die Stadt auf dem Weg zum königlichen Garten; sie gingen durch das Tor zwischen den beiden Mauern und schlugen die Richtung nach der Araba ein." },
      { number: 5, text: "Aber die chaldäischen Truppen setzten ihnen nach und holten Zidkija in den Niederungen von Jericho ein. Sie nahmen ihn gefangen, brachten ihn vor Nebukadnezzar, den König von Babel, nach Ribla in der Landschaft Hamat, und dieser sprach das Urteil über ihn." },
      { number: 6, text: "Der König von Babel ließ in Ribla die Söhne Zidkijas vor dessen Augen niedermachen; auch alle Vornehmen Judas ließ der König von Babel niedermachen." },
      { number: 7, text: "Zidkija ließ er blenden und in Fesseln legen, um ihn nach Babel zu bringen." },
      { number: 8, text: "Den königlichen Palast und die Häuser der Bürger steckten die Chaldäer in Brand und die Mauern Jerusalems rissen sie nieder." },
      { number: 9, text: "Den Rest der Bevölkerung, der noch in der Stadt geblieben war, sowie alle, die zum König von Babel übergelaufen waren, und den Rest der Handwerker verschleppte Nebusaradan, der Kommandant der Leibwache, nach Babel." },
      { number: 10, text: "Nur von den armen Leuten, die nichts hatten, ließ Nebusaradan, der Kommandant der Leibwache, einen Teil im Land Juda zurück und gab ihnen Weinberge und Äcker." },
      { number: 11, text: "Nebukadnezzar, der König von Babel, hatte Nebusaradan, dem Kommandanten der Leibwache, über Jeremia folgende Anweisung gegeben:", heading: "Die Befreiung Jeremias: 39,11-14" },
      { number: 12, text: "Lass ihn holen, kümmere dich um ihn und füg ihm kein Leid zu, sondern richte dich nach seinen Wünschen!", footnotes: ["40,4"] },
      { number: 13, text: "Da schickten Nebusaradan, der Kommandant der Leibwache, Nebuschasban, der Oberkämmerer, und Nergal-Sarezer, der Oberhofmeister, sowie alle Obersten des Königs von Babel Leute zum Wachhof," },
      { number: 14, text: "ließen Jeremia von dort holen und übergaben ihn Gedalja, dem Sohn Ahikams, des Sohnes Schafans, damit er ihn nach Hause entlasse. So blieb er mitten unter dem Volk." },
      { number: 15, text: "Als Jeremia noch im Wachhof gefangen saß, war an ihn das Wort des Herrn ergangen:", heading: "Die Verheißung für den Kuschiter: 39,15-18", footnotes: ["38,7.13"] },
      { number: 16, text: "Geh hin und sag zu dem Kuschiter Ebed-Melech: So spricht der Herr der Heere, der Gott Israels: Bald erfülle ich meine Worte, die ich über diese Stadt gesprochen habe - zu ihrem Unheil und nicht zu ihrem Heil; sie werden an jenem Tag vor deinen Augen eintreffen.", footnotes: ["21,10; 44,11.27"] },
      { number: 17, text: "Aber dich werde ich retten an jenem Tag - Spruch des Herrn - und du wirst den Leuten, vor denen du Angst hast, nicht in die Hände fallen." },
      { number: 18, text: "Ja, ich werde dich heil entrinnen lassen; du wirst nicht unter dem Schwert fallen, sondern dein Leben wie ein Beutestück gewinnen, weil du auf mich vertraut hast - Spruch des Herrn.", footnotes: ["21,9; 38,2; 45,5"] },
    ] },
    { number: 40, verses: [
      { number: 1, text: "Das Wort, das vom Herrn an Jeremia erging, nachdem ihn Nebusaradan, der Kommandant der Leibwache, in Rama freigelassen hatte. Dieser hatte ihn nämlich holen lassen, als er, mit Handschellen gefesselt, sich mitten unter den Gefangenen von Jerusalem und Juda befand, die nach Babel weggeführt werden sollten.", heading: "Jeremia beim Statthalter Gedalja: 40,1-6", footnotes: ["Allgemeine Einleitung eines Abschnitts; es folgt kein Gotteswort. Trotz 39,11-14 kam Jeremia versehentlich unter die Gefangenen."] },
      { number: 2, text: "Der Kommandant der Leibwache holte Jeremia und sagte zu ihm: Jahwe, dein Gott, hatte diesem Ort dieses Unheil angedroht." },
      { number: 3, text: "Jetzt hat Jahwe seine Drohung eintreffen lassen und vollstreckt; denn ihr habt gegen Jahwe gesündigt und nicht auf seine Stimme gehört. So musste euch dieses Schicksal treffen." },
      { number: 4, text: "Nun aber löse ich dir heute die Fesseln an deinen Händen. Wenn es dir beliebt, mit mir nach Babel zu kommen, so komm mit und ich will für dich sorgen. Willst du aber nicht mit mir nach Babel kommen, so lass es! Siehe, das ganze Land liegt vor dir; du kannst gehen, wohin du willst." },
      { number: 5, text: "Wenn du es vorziehst dazubleiben, so kehre um zu Gedalja, dem Sohn Ahikams, des Sohnes Schafans, den der König von Babel über das Land Juda gesetzt hat. Bleib bei ihm mitten unter dem Volk oder geh, wohin du willst. Der Kommandant der Leibwache gab ihm Reisevorrat sowie ein Geschenk und entließ ihn.", footnotes: ["5f: Warum der Sieger den Judäer Gedalja zum Statthalter machte, wissen wir nicht; vielleicht hatte dieser sich rechtzeitig auf die Seite der Chaldäer geschlagen. - Mizpa, eine Kleinstadt nördlich von Jerusalem, wird nach der Zerstörung der Hauptstadt Verwaltungssitz."] },
      { number: 6, text: "Jeremia ging zu Gedalja, dem Sohn Ahikams, nach Mizpa und blieb bei ihm mitten unter dem Volk, das im Land übrig geblieben war.", footnotes: ["39,14"] },
      { number: 7, text: "Alle Truppenführer, die samt ihren Mannschaften noch über das freie Feld verstreut waren, erfuhren, dass der König von Babel Gedalja, den Sohn Ahikams, als Statthalter im Land eingesetzt und ihm Männer, Frauen und Kinder sowie jenen Teil der armen Bevölkerung unterstellt habe, der nicht nach Babel weggeführt worden war.", heading: "Gedalja als Statthalter: 40,7-16", footnotes: ["(7-16) 2 Kön 25,22-24"] },
      { number: 8, text: "Sie kamen nun mit ihren Leuten zu Gedalja nach Mizpa, nämlich Jischmael, der Sohn Netanjas, Johanan und Jonatan, die Söhne Kareachs, Seraja, der Sohn Tanhumets, ferner die Söhne Efais aus Netofa und Jaasanja, der Sohn des Maachatiters." },
      { number: 9, text: "Gedalja, der Sohn Ahikams, des Sohnes Schafans, schwor ihnen und ihren Mannschaften: Fürchtet euch nicht davor, den Chaldäern untertan zu sein. Bleibt im Land und dient dem König von Babel; dann wird es euch gut gehen." },
      { number: 10, text: "Seht, ich selber bleibe in Mizpa und vertrete euch vor den Chaldäern, die zu uns kommen. Ihr aber, erntet Wein, Obst und Öl, sammelt es in euren Gefäßen und bleibt in den Ortschaften, die ihr besiedelt." },
      { number: 11, text: "Auch alle jene Judäer, die sich in Moab, bei den Ammonitern, in Edom oder in irgendeinem anderen Land aufhielten, erfuhren, dass der König von Babel Juda eine Restbevölkerung gelassen und über sie Gedalja, den Sohn Ahikams, des Sohnes Schafans, als Statthalter eingesetzt habe." },
      { number: 12, text: "Daher kehrten alle Judäer aus sämtlichen Orten, wohin sie versprengt waren, zurück und kamen ins Land Juda zu Gedalja nach Mizpa. Man erntete Wein und Obst in großer Menge." },
      { number: 13, text: "Eines Tages kamen Johanan, der Sohn Kareachs, und alle Truppenführer, die über das freie Feld verstreut waren, zu Gedalja nach Mizpa." },
      { number: 14, text: "Sie fragten ihn: Weißt du schon, dass Baalis, der König der Ammoniter, Jischmael, den Sohn Netanjas, geschickt hat, um dich ermorden zu lassen? Aber Gedalja, der Sohn Ahikams, glaubte es ihnen nicht." },
      { number: 15, text: "Nun machte Johanan, der Sohn Kareachs, dem Gedalja in Mizpa insgeheim den Vorschlag: Ich will hingehen und Jischmael, den Sohn Netanjas, erschlagen, ohne dass jemand davon erfährt. Warum soll er dich umbringen, sodass alle Judäer, die sich um dich geschart haben, wieder zerstreut werden und der Rest von Juda zugrunde geht?" },
      { number: 16, text: "Doch Gedalja, der Sohn Ahikams, entgegnete Johanan, dem Sohn Kareachs: Nein, tu das nicht; denn was du über Jischmael sagst, ist nicht wahr." },
    ] },
    { number: 41, verses: [
      { number: 1, text: "Aber im siebten Monat kam Jischmael, der Sohn Netanjas, des Sohnes Elischamas, ein Mann aus königlichem Geschlecht und einer von den Großen des Königs, mit zehn Mann zu Gedalja, dem Sohn Ahikams, nach Mizpa. Während sie dort zusammen aßen,", heading: "Die Ermordung Gedaljas: 41,1-15", footnotes: ["40,8"] },
      { number: 2, text: "erhob sich Jischmael, der Sohn Netanjas, samt den zehn Männern, die bei ihm waren, und sie erschlugen Gedalja, den Sohn Ahikams, des Sohnes Schafans, mit dem Schwert. So tötete er den Mann, den der König von Babel als Statthalter im Land eingesetzt hatte." },
      { number: 3, text: "Auch alle Judäer, die bei ihm [bei Gedalja] in Mizpa waren, und die Chaldäer, die sich dort als militärische Besatzung befanden, erschlug Jischmael." },
      { number: 4, text: "Am zweiten Tag nach Gedaljas Ermordung, als noch niemand etwas erfahren hatte," },
      { number: 5, text: "kamen Männer aus Sichem, Schilo und Samaria, achtzig Mann, mit geschorenen Bärten, zerrissenen Kleidern und eingeritzten Wunden. Sie trugen in ihren Händen Opfergaben und Weihrauch, um sie zum Haus des Herrn zu bringen." },
      { number: 6, text: "Da ging ihnen Jischmael, der Sohn Netanjas, von Mizpa aus entgegen, wobei er laut weinte. Sobald er mit ihnen zusammentraf, lud er sie ein: Kommt mit zu Gedalja, dem Sohn Ahikams!" },
      { number: 7, text: "Als sie nun in die Stadtmitte gekommen waren, ermordete sie Jischmael, der Sohn Netanjas, mit Hilfe der Männer, die er bei sich hatte, und warf sie in die Zisterne." },
      { number: 8, text: "Zehn Männer aber, die sich unter jenen befanden, hatten zu Jischmael gesagt: Töte uns nicht; denn wir haben versteckte Vorräte auf dem Feld: Weizen, Gerste, Öl und Honig. Da sah er davon ab, sie zusammen mit ihren Stammesbrüdern zu töten." },
      { number: 9, text: "Die Zisterne, in die Jischmael alle Leichen der Männer warf, die er Gedaljas wegen erschlagen hatte, war jene, die König Asa im Krieg gegen Bascha, den König von Israel, angelegt hatte. Diese füllte nun Jischmael, der Sohn Netanjas, mit Erschlagenen an.", footnotes: ["1 Kön 15,16-24"] },
      { number: 10, text: "Dann führte Jischmael den ganzen Rest des Volkes, der sich in Mizpa befand, gefangen hinweg: die Prinzessinnen und die ganze Bevölkerung, die in Mizpa geblieben war und die Nebusaradan, der Kommandant der Leibwache, Gedalja, dem Sohn Ahikams, unterstellt hatte. Jischmael, der Sohn Netanjas, führte sie gefangen weg und zog ab, um über die Grenze zu den Ammonitern zu gelangen." },
      { number: 11, text: "Johanan, der Sohn Kareachs, und alle Truppenführer, die bei ihm waren, hörten von all dem Bösen, das Jischmael, der Sohn Netanjas, verübt hatte." },
      { number: 12, text: "Sie nahmen ihre gesamte Mannschaft und zogen aus, um gegen Jischmael, den Sohn Netanjas, zu kämpfen. Am großen Wasser bei Gibeon stießen sie auf ihn." },
      { number: 13, text: "Als das ganze Volk, das bei Jischmael war, Johanan, den Sohn Kareachs, und in seiner Begleitung alle Truppenführer erblickte, war es hoch erfreut." },
      { number: 14, text: "Alle Leute, die Jischmael gefangen von Mizpa weggeführt hatte, wandten sich von ihm ab und traten auf die Seite Johanans, des Sohnes Kareachs." },
      { number: 15, text: "Jischmael aber, der Sohn Netanjas, konnte mit acht Mann dem Johanan entkommen und floh zu den Ammonitern." },
      { number: 16, text: "Nun übernahm Johanan, der Sohn Kareachs, zusammen mit allen Truppenführern, die bei ihm waren, den ganzen Rest des Volkes, den Jischmael, der Sohn Netanjas, nach der Ermordung Gedaljas, des Sohnes Ahikams, aus Mizpa weggeführt hatte: Männer, [Krieger] Frauen, Kinder und Palastbeamte, die er nun alle von Gibeon wegbrachte.", heading: "Die Sammlung des restlichen Volkes: 41,16-18" },
      { number: 17, text: "Sie zogen ab und machten in der Herberge des Kimham bei Betlehem Halt, in der Absicht, nach Ägypten weiterzuziehen." },
      { number: 18, text: "So wollten sie den Chaldäern entkommen; denn sie hatten Angst vor ihnen, weil Jischmael, der Sohn Netanjas, Gedalja, den Sohn Ahikams, erschlagen hatte, den der König von Babel als Statthalter im Land eingesetzt hatte." },
    ] },
    { number: 42, verses: [
      { number: 1, text: "Da kamen alle Truppenführer und Johanan, der Sohn Kareachs, ferner Asarja, der Sohn Haschajas, sowie das ganze Volk, Klein und Groß, herbei", heading: "Die Bitte um ein Gotteswort: 42,1-6" },
      { number: 2, text: "und sagten zum Propheten Jeremia: Möge unsere Bitte bei dir Gehör finden: Bete für uns zum Herrn, deinem Gott, für diesen ganzen Rest; denn von einst so vielen sind nur wir wenige übrig geblieben, wie du mit eigenen Augen siehst." },
      { number: 3, text: "Möge der Herr, dein Gott, uns kundtun, welchen Weg wir einschlagen und was wir tun sollen." },
      { number: 4, text: "Der Prophet Jeremia erwiderte ihnen: Gut, ich will nach eurem Wunsch zum Herrn, eurem Gott, beten und euch dann alles, was der Herr antwortet, mitteilen, ohne euch ein Wort vorzuenthalten." },
      { number: 5, text: "Sie selbst versicherten Jeremia: Der Herr sei ein wahrer und treuer Zeuge gegen uns, wenn wir nicht genau nach dem Wort handeln, mit dem dich der Herr, dein Gott, zu uns sendet." },
      { number: 6, text: "Sei es gut oder schlimm, auf die Stimme des Herrn, unseres Gottes, zu dem wir dich senden, werden wir hören, damit es uns gut gehe, weil wir auf die Stimme des Herrn, unseres Gottes, hören." },
      { number: 7, text: "Zehn Tage später erging das Wort des Herrn an Jeremia.", heading: "Die Warnung vor der Auswanderung nach Ägypten: 42,7-18" },
      { number: 8, text: "Daraufhin rief er Johanan, den Sohn Kareachs, und alle Truppenführer, die bei ihm waren, sowie das ganze Volk, Klein und Groß, zusammen" },
      { number: 9, text: "und sagte zu ihnen: So spricht der Herr, der Gott Israels, zu dem ihr mich gesandt habt, um ihm eure Bitte vorzutragen:" },
      { number: 10, text: "Wenn ihr in diesem Land wohnen bleibt, so werde ich euch aufbauen und nicht niederreißen, euch einpflanzen und nicht ausreißen; ja, ich bereue das Unheil, das ich euch angetan habe.", footnotes: ["1,10"] },
      { number: 11, text: "Fürchtet euch nicht vor dem König von Babel, vor dem ihr Angst habt. Fürchtet euch nicht vor ihm - Spruch des Herrn -; denn ich bin mit euch, um euch zu retten und seiner Hand zu entreißen." },
      { number: 12, text: "Ich bewirke Erbarmen für euch; er wird sich euer erbarmen und euch in eure Heimat zurückkehren lassen." },
      { number: 13, text: "Wenn ihr aber sagt: Wir bleiben nicht in diesem Land!, und auf die Stimme des Herrn, eures Gottes, nicht hört," },
      { number: 14, text: "sondern sagt: Nein, nach Ägypten wollen wir ziehen, wo wir weder Krieg sehen noch Trompetenschall hören, noch nach Brot hungern werden, und dort wollen wir bleiben!," },
      { number: 15, text: "nun, dann hört das Wort des Herrn, ihr, der Rest Judas: So spricht der Herr der Heere, der Gott Israels: Wenn ihr darauf besteht, nach Ägypten zu ziehen, und auswandert, um euch dort niederzulassen," },
      { number: 16, text: "dann wird euch das Schwert, das ihr fürchtet, dort in Ägypten erreichen und der Hunger, vor dem euch bangt, wird euch dort in Ägypten verfolgen und ihr werdet dort umkommen." },
      { number: 17, text: "Ja, alle, die darauf bestehen, nach Ägypten zu ziehen, um sich dort niederzulassen, werden durch Schwert, Hunger und Pest umkommen. Keiner von ihnen wird entkommen, keiner dem Unheil entrinnen, das ich über sie bringe." },
      { number: 18, text: "Denn so spricht der Herr der Heere, der Gott Israels: Wie sich mein Zorn und Grimm über die Einwohner Jerusalems ergossen hat, so wird sich mein Grimm auch über euch ergießen, wenn ihr nach Ägypten zieht. Ihr werdet zum Fluch und zu einem Bild des Entsetzens, zur Verwünschung und zur Schmach werden. Dieses Land aber werdet ihr nie wieder sehen.", footnotes: ["25,9"] },
      { number: 19, text: "Der Herr hat zu euch gesprochen, Rest Judas: Zieht nicht nach Ägypten! Ihr sollt genau wissen: Ich warne euch heute.", heading: "Die Drohung an die Verstockten: 42,19-22", footnotes: ["19-22: Dem Zusammenhang nach wohl ursprünglich Antwort auf die Ablehnung des Gotteswortes in 43,1-3."] },
      { number: 20, text: "Denn ihr gebt euch einer gefährlichen Täuschung hin. Ihr selbst habt mich ja zum Herrn, eurem Gott, gesandt mit den Worten: Bete für uns zum Herrn, unserem Gott, und ganz wie der Herr, unser Gott, spricht, gib uns Bescheid und wir werden danach handeln." },
      { number: 21, text: "Heute nun habe ich euch den Bescheid gegeben, aber ihr habt auf die Stimme des Herrn, eures Gottes, nicht gehört, auf nichts von dem, womit er mich zu euch gesandt hat." },
      { number: 22, text: "Jetzt aber sollt ihr wissen: Durch Schwert, Hunger und Pest werdet ihr umkommen an dem Ort, wohin ihr ziehen wollt, um euch dort niederzulassen." },
    ] },
    { number: 43, verses: [
      { number: 1, text: "Als Jeremia dem ganzen Volk alle Worte des Herrn, ihres Gottes, vollständig mitgeteilt hatte, nämlich all die Worte, mit denen ihn der Herr zu ihnen gesandt hatte,", heading: "Der Zug nach Ägypten: 43,1-7" },
      { number: 2, text: "da sagten Asarja, der Sohn Hoschajas, und Johanan, der Sohn Kareachs, sowie alle übermütigen Männer zu Jeremia: Was du sagst, ist erlogen. Nicht der Herr, unser Gott, hat dich gesandt mit dem Auftrag: Ihr sollt nicht nach Ägypten ziehen, um euch dort niederzulassen." },
      { number: 3, text: "Vielmehr hetzt dich Baruch, der Sohn Nerijas, gegen uns auf, um uns den Chaldäern auszuliefern, sodass sie uns töten oder nach Babel verschleppen." },
      { number: 4, text: "Johanan, der Sohn Kareachs, alle Truppenführer und das ganze Volk hörten also nicht auf die Stimme des Herrn, im Land Juda zu bleiben." },
      { number: 5, text: "Johanan, der Sohn Kareachs, und alle Truppenführer nahmen den ganzen Rest Judas, der aus allen Völkern, unter die er versprengt gewesen, zurückgekehrt war, um sich im Land Juda niederzulassen:" },
      { number: 6, text: "Männer, Frauen und Kinder, die Prinzessinnen und alle Leute, die Nebusaradan, der Kommandant der Leibwache, bei Gedalja, dem Sohn Ahikams, des Sohnes Schafans, gelassen hatte, auch den Propheten Jeremia und Baruch, den Sohn Nerijas," },
      { number: 7, text: "und sie zogen nach Ägypten, weil sie nicht auf die Stimme des Herrn hörten, und kamen bis Tachpanhes." },
      { number: 8, text: "In Tachpanhes erging das Wort des Herrn an Jeremia:", heading: "Die Heimsuchung auch in Ägypten: 43,8-13" },
      { number: 9, text: "Nimm vor den Augen judäischer Männer große Steine in die Hand und maure sie mit Mörtel in das Ziegelpflaster am Eingang zum Haus des Pharao in Tachpanhes!" },
      { number: 10, text: "Dann sag zu ihnen: So spricht der Herr der Heere, der Gott Israels: Seht, ich werde meinen Knecht Nebukadnezzar, den König von Babel, herbeiholen; er wird seinen Thron über diesen Steinen, die ich hier eingemauert habe, aufstellen und sein Prunkzelt darüber ausspannen." },
      { number: 11, text: "Er wird kommen und das Land Ägypten schlagen; wer für den Tod bestimmt ist, verfällt dem Tod, wer für die Gefangenschaft, der Gefangenschaft und wer für das Schwert, dem Schwert.", footnotes: ["46,26; 15,2"] },
      { number: 12, text: "Er wird an die Tempel der Götter Ägyptens Feuer legen und die Götter verbrennen oder wegführen. Er wird das Land Ägypten ablausen, wie ein Hirt sein Gewand ablaust; danach wird er unbehelligt abziehen." },
      { number: 13, text: "Er wird die spitzen Säulen des Sonnentempels in Ägypten zertrümmern und die Tempel der Götter Ägyptens in Brand stecken." },
    ] },
    { number: 44, verses: [
      { number: 1, text: "Das Wort, das an Jeremia erging für alle Judäer, die in Ägypten wohnten und sich in Migdol und Tachpanhes, in Memfis und im Land Patros angesiedelt hatten:", heading: "Die Warnung vor Götzendienst: 44,1-14" },
      { number: 2, text: "So spricht der Herr der Heere, der Gott Israels: Ihr selbst habt all das Unheil miterlebt, das ich über Jerusalem und alle Städte Judas gebracht habe. Seht, heute sind sie Trümmerstätten, die niemand mehr bewohnt." },
      { number: 3, text: "Das ist die Folge ihrer bösen Taten; sie erzürnten mich, indem sie hingingen, um anderen Göttern, die sie früher nicht kannten, zu opfern und zu dienen, sie, ihr und eure Väter." },
      { number: 4, text: "Wohl habe ich immer wieder all meine Knechte, die Propheten, zu euch gesandt mit der Mahnung: Verübt doch nicht einen solchen Gräuel, den ich so hasse.", footnotes: ["7,25; 25,4"] },
      { number: 5, text: "Sie aber haben nicht gehört und mir ihr Ohr nicht zugeneigt, sodass sie sich von ihrer Bosheit bekehrt und nicht mehr anderen Göttern geopfert hätten." },
      { number: 6, text: "Daher ergoss sich mein Grimm und Zorn und wütete in den Städten Judas und in den Straßen Jerusalems, sodass sie zur Trümmerstätte und Wüste wurden, wie sie es heute noch sind." },
      { number: 7, text: "Nun aber spricht so der Herr, der Gott der Heere, der Gott Israels: Warum tut ihr euch selbst so schweres Leid an, indem ihr eure Männer und Frauen, Kinder und Säuglinge aus Juda ausrottet und keinen Rest mehr übrig lasst?" },
      { number: 8, text: "Denn ihr erzürnt mich durch das Tun eurer Hände, weil ihr anderen Göttern opfert in Ägypten, wohin ihr ausgewandert seid, um euch dort niederzulassen. So rottet ihr euch selbst aus und werdet zum Fluch und Gespött bei allen Völkern der Erde." },
      { number: 9, text: "Habt ihr denn all das Böse vergessen, das eure Väter und die Könige von Juda und ihre Frauen, dann auch ihr selbst und eure Frauen im Land Juda und in den Straßen Jerusalems getan haben?", footnotes: ["7,17"] },
      { number: 10, text: "Sie bereuen es nicht bis zum heutigen Tag, fürchten sich nicht und leben nicht nach meiner Weisung und nach meinen Gesetzen, die ich euch und euren Vätern vorgelegt habe." },
      { number: 11, text: "Darum - so spricht der Herr der Heere, der Gott Israels: Seht, ich richte mein Angesicht gegen euch zu eurem Unheil, um ganz Juda auszurotten.", footnotes: ["44,27; 21,10; 39,16"] },
      { number: 12, text: "Ich raffe den Rest Judas hinweg, der darauf bestand, nach Ägypten auszuwandern, um sich dort niederzulassen. Sie alle werden in Ägypten umkommen; sie werden fallen durch Schwert und Hunger; Klein und Groß wird umkommen. Durch Schwert und Hunger werden sie sterben und zum Fluch und zu einem Bild des Entsetzens werden, zur Verwünschung und zur Schmach.", footnotes: ["42,18"] },
      { number: 13, text: "Wie ich Jerusalem heimgesucht habe, so suche ich die in Ägypten Angesiedelten heim mit Schwert, Hunger und Pest." },
      { number: 14, text: "Vom Rest Judas, der ausgewandert ist, um sich hier in Ägypten niederzulassen, wird niemand entrinnen oder entkommen, um ins Land Juda zurückzukehren, obwohl sie sich danach sehnen, zurückzukehren und dort zu bleiben. Ja, nur wenige werden entrinnen und zurückkehren.", footnotes: ["42,17", "«niemand» und «nur wenige» ist allgemeine, ungenaue Angabe wie in VV. 27f."] },
      { number: 15, text: "Da antworteten alle Männer, die wussten, dass ihre Frauen anderen Göttern opferten, und alle Frauen, die dabeistanden, eine große Schar, sowie alle Leute, die in Ägypten und in Patros wohnten, dem Jeremia:", heading: "Der offene Abfall: 44,15-19" },
      { number: 16, text: "Was das Wort betrifft, das du im Namen des Herrn zu uns gesprochen hast, so hören wir nicht auf dich." },
      { number: 17, text: "Vielmehr werden wir alles, was wir gelobt haben, gewissenhaft ausführen: Wir werden der Himmelskönigin Rauchopfer und Trankopfer darbringen, wie wir, unsere Väter, unsere Könige und unsere Großen in den Städten Judas und in den Straßen Jerusalems es getan haben. Damals hatten wir Brot genug; es ging uns gut und wir litten keine Not.", footnotes: ["7,18", "«Himmelskönigin» vgl. die Anm. zu 7,18."] },
      { number: 18, text: "Seit wir aber aufgehört haben, der Himmelskönigin Rauchopfer und Trankopfer darzubringen, fehlt es uns an allem und wir kommen durch Schwert und Hunger um." },
      { number: 19, text: "Die Frauen aber sagten: Geschieht es etwa ohne Wissen und Willen unserer Männer, dass wir der Himmelskönigin Rauchopfer und Trankopfer darbringen, dass wir für sie Opferkuchen bereiten, die ihr Bild wiedergeben, und Trankopfer spenden?" },
      { number: 20, text: "Jeremia entgegnete dem ganzen Volk, den Männern, den Frauen und allen, die ihm so geantwortet hatten:", heading: "Das Strafgericht: 44,20-30" },
      { number: 21, text: "Die Opfer, die ihr in den Städten Judas und auf den Straßen Jerusalems verbrannt habt, ihr und eure Väter, eure Könige und eure Großen und die Bürger des Landes - waren nicht gerade sie es, an die der Herr gedacht und sich erinnert hat?" },
      { number: 22, text: "Schließlich konnte es der Herr nicht mehr aushalten wegen eurer Untaten und wegen der Gräuel, die ihr verübt habt. Deshalb wurde euer Land zur Öde, zu einem Bild des Entsetzens und zum Fluch, unbewohnt, wie es heute noch ist.", footnotes: ["25,9; 49,13"] },
      { number: 23, text: "Weil ihr Rauchopfer dargebracht und gegen den Herrn gesündigt habt, weil ihr auf die Stimme des Herrn nicht gehört und euch nicht nach seiner Weisung, seinen Gesetzen und Mahnungen gerichtet habt, darum hat euch dieses Unheil getroffen, wie es heute noch besteht." },
      { number: 24, text: "Zum ganzen Volk und zu allen Frauen sagte Jeremia: Hört das Wort des Herrn, ganz Juda, das in Ägypten weilt.", footnotes: ["24f: In G werden nur die Frauen angeredet, was wohl ursprünglich ist."] },
      { number: 25, text: "So spricht der Herr der Heere, der Gott Israels: Was ihr und eure Frauen mit dem Mund gelobt, das führt ihr mit den Händen aus. Ihr sagt: Unsere versprochenen Gelübde werden wir gewissenhaft erfüllen und der Himmelskönigin Rauchopfer und Trankopfer darbringen. Haltet nur eure Gelübde genau, ja, eure Gelübde erfüllt gewissenhaft!" },
      { number: 26, text: "So höre denn das Wort des Herrn, ganz Juda, das in Ägypten wohnt: Seht, ich schwöre bei meinem großen Namen, spricht der Herr: Nie mehr soll mein Name vom Mund irgendeines Judäers in ganz Ägypten genannt werden; keiner mehr soll sagen: So wahr Gott, der Herr, lebt!" },
      { number: 27, text: "Seht, ich wache über sie - zu ihrem Unheil, nicht zu ihrem Heil. Alle Judäer in Ägypten werden durch Schwert und Hunger umkommen und völlig vernichtet werden.", footnotes: ["1,11f; 43,11"] },
      { number: 28, text: "Nur wenige werden dem Schwert entrinnen und aus Ägypten ins Land Juda heimkehren und der ganze Rest von Juda, der nach Ägypten ausgezogen ist, um sich dort niederzulassen, wird erkennen, wessen Wort sich erfüllt, das meine oder das ihre." },
      { number: 29, text: "Dies aber soll euch als Zeichen dafür dienen - Spruch des Herrn -, dass ich euch heimsuche an diesem Ort, damit ihr erkennt, dass meine Unheilsworte über euch bestimmt in Erfüllung gehen:" },
      { number: 30, text: "So spricht der Herr: Seht, ich liefere den Pharao Hofra, den König von Ägypten, in die Hand seiner Gegner und Todfeinde aus, wie ich Zidkija, den König von Juda, in die Hand Nebukadnezzars, des Königs von Babel, seines Gegners und Todfeindes, ausgeliefert habe.", footnotes: ["Ez 29,19"] },
    ] },
    { number: 45, verses: [
      { number: 1, text: "Das Wort, das der Prophet Jeremia zu Baruch, dem Sohn Nerijas, sagte, als dieser im vierten Jahr Jojakims, des Sohnes Joschijas, des Königs von Juda, jene Reden nach dem Diktat Jeremias in ein Buch schrieb:", heading: "Ein Gotteswort an Baruch: 45,1-5", footnotes: ["36,4"] },
      { number: 2, text: "So spricht der Herr, der Gott Israels, über dich, Baruch:" },
      { number: 3, text: "Du hast gesagt: Weh mir! Denn der Herr häuft noch Kummer auf mein Leid. Ich bin erschöpft vor Stöhnen und finde keine Ruhe." },
      { number: 4, text: "Sag zu ihm: So spricht der Herr: Was ich gebaut habe, breche ich nieder, und was ich gepflanzt habe, reiße ich aus. [Das betrifft das ganze Land]", footnotes: ["Der Zusatz fehlt in G."] },
      { number: 5, text: "Du aber begehrst Großes für dich? Begehre es nicht! Denn siehe, ich bringe Unheil über alle Sterblichen - Spruch des Herrn; dir aber gebe ich dein Leben wie ein Beutestück überall, wohin du auch gehst.", footnotes: ["21,9; 39,18"] },
    ] },
    { number: 46, verses: [
      { number: 1, text: "Das Wort des Herrn gegen die Völker, das an den Propheten Jeremia erging:", heading: "Die Drohreden über die Völker: 46,1 - 51,64 | Einleitung: 46,1" },
      { number: 2, text: "Über Ägypten: Gegen das Heer des Pharao Necho, des Königs von Ägypten, das bei Karkemisch am Eufrat stand und das Nebukadnezzar, der König von Babel, geschlagen hat. Es war im vierten Jahr Jojakims, des Sohnes Joschijas, des Königs von Juda.", heading: "Über Ägypten: 46,2-26 | Die Niederlage am Eufrat: 46,2-12", footnotes: ["2 Kön 23,29", "Im Jahr 605 v. Chr. machte der Pharao Necho einen zweiten Versuch (der erste war 609), den völligen Untergang des Assyrerreichs infolge des Vormarsches Nebukadnezzars zu verhindern, weil er ein geschwächtes Assyrerreich als Pufferstaat zwischen Ägypten und dem Chaldäerreich wünschte. Der Versuch schlug fehl: Der letzte Rest des Assyrerreichs ging 605 v. Chr. unter. - Karkemisch liegt im westlichen Teil des Eufratbogens."] },
      { number: 3, text: "Rüstet Rundschild und Langschild, / rückt aus zum Kampf!" },
      { number: 4, text: "Schirrt die Rosse an, besteigt die Pferde! / Tretet an im Helm, macht die Speere blank, / legt die Panzer an!" },
      { number: 5, text: "Was sehe ich? Warum sind sie bestürzt? / Sie weichen zurück. Ihre Helden sind zersprengt, sie ergreifen die Flucht / und wenden sich nicht mehr um - / Grauen ringsum! - Spruch des Herrn.", footnotes: ["20,3"] },
      { number: 6, text: "Der Schnelle kann nicht entfliehen / und der Held nicht entrinnen. Im Norden, am Ufer des Eufrat, / stürzen sie hin und fallen." },
      { number: 7, text: "Wer ist es, der anschwoll gleich dem Nil, / dessen Wasser wie Ströme tosen?" },
      { number: 8, text: "Ägypten schwoll an gleich dem Nil, / dessen Wasser wie Ströme tosen. Es sagte: Ich schwelle an, überschwemme das Land, / vernichte die Städte und ihre Einwohner." },
      { number: 9, text: "Bäumt euch, ihr Rosse, stürmt los, ihr Wagen! / Rückt aus, ihr Helden: Kusch und Put, ihr Schildbewehrten, / ihr von Lud, ihr Bogenschützen!", footnotes: ["Ez 27,10; Nah 3,9"] },
      { number: 10, text: "Doch jener Tag ist ein Tag der Rache / für den Herrn, den Gott der Heere; / er rächt sich an seinen Gegnern. Da frisst das Schwert, / wird satt und trunken von ihrem Blut. Denn ein Schlachtfest hält der Herr, der Gott der Heere, / im Land des Nordens am Eufratstrom." },
      { number: 11, text: "Zieh hinauf nach Gilead und hole Balsam, / Jungfrau, Tochter Ägypten! Umsonst verbrauchst du viele Arzneien, / du findest keine Genesung.", footnotes: ["8,22"] },
      { number: 12, text: "Von deiner Schande hören die Völker, / dein Wehgeschrei erfüllt die Erde. Denn Held an Held ist gestürzt, / miteinander sind beide gefallen." },
      { number: 13, text: "Das Wort, das der Herr zum Propheten Jeremia sprach, als Nebukadnezzar, der König von Babel, heranrückte, um Ägypten zu erobern:", heading: "Die Eroberung Ägyptens: 46,13-26" },
      { number: 14, text: "Meldet es in Ägypten, verkündet es in Migdol, / verkündet es in Memfis und Tachpanhes! Sagt: Stell dich auf und mach dich bereit; / denn schon frisst das Schwert rings um dich her." },
      { number: 15, text: "Warum liegt dein Starker zu Boden gestreckt? / Er hielt nicht stand, weil der Herr ihn stieß.", footnotes: ["Der «Starke» ist der Pharao. Nach G könnte auch der göttliche Apis-Stier gemeint sein."] },
      { number: 16, text: "Viele kamen durch ihn zu Fall, / sie stürzten, Mann für Mann. Da sagten sie: / Auf, zurück zu unsrem Volk, ins Land unsrer Sippe, / vor dem rasenden Schwert!", footnotes: ["50,16", "Sinn nicht ganz klar. Vermutlich sind die fremden Söldnertruppen gemeint, die nach der Niederlage in ihre Heimat fliehen (vgl. VV. 21f)."] },
      { number: 17, text: "Gebt dem Pharao, dem König Ägyptens, den Namen: / Getöse, das die Zeit verpasste." },
      { number: 18, text: "So wahr ich lebe - Spruch des Königs, / Herr der Heere ist sein Name -, wie der Tabor unter den Bergen / und wie der Karmel am Meer, / so mächtig rückt er heran.", footnotes: ["48,15"] },
      { number: 19, text: "Rüste dein Gepäck für die Verbannung, / du sesshafte Tochter Ägypten! Denn Memfis wird zur Wüste, / wird niedergebrannt und menschenleer.", footnotes: ["19.26: Zu einer Deportation der Ägypter ist es nicht gekommen. Nebukadnezzar dringt nur bis zum Nildelta vor; erst die Perser erobern Ägypten, deportieren aber die Bevölkerung nicht."] },
      { number: 20, text: "Eine stattliche Jungkuh ist Ägypten, / die Bremse von Norden stürzt sich auf sie." },
      { number: 21, text: "Auch seine Söldnertruppen mitten im Land / sind wie gemästete Kälber. Aber auch sie machen kehrt, / sie fliehen alle und halten nicht stand. Denn der Tag ihres Verderbens kommt über sie, / die Zeit ihrer Bestrafung." },
      { number: 22, text: "Ägyptens Stimme gleicht dem Zischen der Schlange, / wenn jene anrücken mit Macht, wenn sie mit Äxten einfallen, / Holzhackern gleich." },
      { number: 23, text: "Sie fällen seinen Wald - Spruch des Herrn -, / den undurchdringlichen. Zahlreicher als Heuschrecken sind sie, / man kann sie nicht zählen." },
      { number: 24, text: "Zuschanden wird die Tochter Ägypten, / preisgegeben dem Volk aus dem Norden." },
      { number: 25, text: "Der Herr der Heere, der Gott Israels, hat gesprochen: Seht, ich suche heim den Amon von No [und den Pharao], Ägypten und seine Götter, seine Könige und den Pharao samt allen, die sich auf ihn verlassen.", footnotes: ["(25-26) 43,11-13"] },
      { number: 26, text: "Ich gebe sie in die Hand ihrer Todfeinde, in die Hand Nebukadnezzars, des Königs von Babel, und in die Hand seiner Untertanen. Aber danach wird das Land wieder bewohnt sein wie in früheren Zeiten - Spruch des Herrn." },
      { number: 27, text: "Fürchte dich nicht, du, mein Knecht Jakob, / verzage nicht, Israel! Denn ich bin es, der dich aus fernem Land errettet, / deine Kinder aus dem Land ihrer Gefangenschaft. Jakob wird heimkehren und Ruhe haben; / er wird in Sicherheit leben / und niemand wird ihn erschrecken.", heading: "Trost für Israel und Drohungen gegen die Philister: 46,27 - 47,7 | Ein Trostwort für Jakob-Israel: 46,27-28", footnotes: ["30,10"] },
      { number: 28, text: "Fürchte dich nicht, / du, mein Knecht Jakob - Spruch des Herrn -; / denn ich bin mit dir. Ja, ich vernichte alle Völker, / unter die ich dich zerstreut habe. Nur dich werde ich niemals vernichten; / ich züchtige dich mit rechtem Maß, / doch ganz ungestraft kann ich dich nicht lassen.", footnotes: ["30,11; 4,27; 10,24; 30,11"] },
    ] },
    { number: 47, verses: [
      { number: 1, text: "Das Wort des Herrn gegen die Philister, das an den Propheten Jeremia erging, bevor der Pharao Gaza eroberte.", heading: "Über die Philister: 47,1-7" },
      { number: 2, text: "So spricht der Herr: Seht, Wasser wogen vom Norden heran / und werden zum flutenden Wildbach. Sie überfluten das Land und was darin ist, / die Städte und ihre Bewohner. Da schreien die Menschen, / laut klagen alle Bewohner des Landes" },
      { number: 3, text: "vor dem donnernden Hufschlag der Hengste, / vor dem Dröhnen der Streitwagen, / vor dem Rasseln der Räder. Väter schauen sich nicht um nach den Söhnen; / denn ihre Hände sind schlaff" },
      { number: 4, text: "wegen des Tages, der kommt, / um alle Philister zu vernichten, um auszurotten den letzten Helfer / für Tyrus und Sidon. Ja, der Herr vernichtet die Philister, / den Rest von der Insel Kaftor." },
      { number: 5, text: "Kahl scheren wird sich Gaza, / Aschkelon wird verstummen. Wie lange noch, du Rest der Anakiter, / musst du dich wund ritzen?", footnotes: ["16,6; 48,37"] },
      { number: 6, text: "Weh, du Schwert des Herrn, / wie lange noch willst du nicht rasten? Fahr zurück in die Scheide, / halt ein und sei still!" },
      { number: 7, text: "Wie sollte es rasten, / da doch der Herr ihm Befehl gab? Gegen Aschkelon und die Meeresküste, / dorthin hat er es bestellt." },
    ] },
    { number: 48, verses: [
      { number: 1, text: "Über Moab: So spricht der Herr der Heere, der Gott Israels: Weh über Nebo, es wird verwüstet, / erobert wird Kirjatajim. / Zuschanden wird die Hochburg und geschleift.", heading: "Über Moab: 48,1-47 | Die Zerstörung: 48,1-10" },
      { number: 2, text: "Moabs Ruhm ist dahin. / In Heschbon plante man sein Verderben: Kommt, wir rotten es aus, / sodass es kein Volk mehr ist. Auch du, Madmen, wirst verstummen; / das Schwert verfolgt dich." },
      { number: 3, text: "Horch! Wehgeschrei aus Horonajim: / Verwüstung und große Zerstörung!" },
      { number: 4, text: "Zerstört ist Moab! / So gellt der Schrei bis nach Zoar.", footnotes: ["Jes 15,5"] },
      { number: 5, text: "Die Steige von Luhit / steigt man weinend hinauf. Ja, am Abstieg von Horonajim / hört man über die Zerstörung entsetzliches Schreien." },
      { number: 6, text: "Flieht, rettet euer Leben! / Macht es wie der Wildesel in der Wüste!" },
      { number: 7, text: "Weil du auf eigene Werke und Schätze vertraut hast, / wirst auch du erobert werden. In die Verbannung muss Kemosch ziehen / samt seinen Priestern und Fürsten." },
      { number: 8, text: "Der Verwüster kommt über jede Stadt, / keine Stadt kann sich retten. Verheert wird das Tal, verwüstet die Ebene, / so spricht der Herr." },
      { number: 9, text: "Setzt ein Grabmal für Moab; / denn es verfällt ganz und gar. Seine Städte werden zur Wüste, / niemand wohnt mehr darin." },
      { number: 10, text: "Verflucht, wer den Auftrag des Herrn lässig betreibt, / ja, verflucht, wer sein Schwert abhält vom Blutvergießen." },
      { number: 11, text: "Ungestört war Moab von Jugend an, / ruhig lag es auf seiner Hefe. Es wurde nicht umgeschüttet von Gefäß zu Gefäß: / Nie musste es in die Verbannung ziehen. Darum blieb ihm sein Wohlgeschmack erhalten, / sein Duft veränderte sich nicht.", heading: "Der Verwüster: 48,11-28", footnotes: ["Zef 1,12"] },
      { number: 12, text: "Darum kommen Tage - Spruch des Herrn -, / da schicke ich Kellermeister zu ihm; sie gießen es um, / entleeren seine Gefäße und zerschlagen seine Krüge." },
      { number: 13, text: "Moab wird an Kemosch zuschanden, / wie das Haus Israel zuschanden wurde / an Bet-El, auf das es vertraute.", footnotes: ["Kemosch ist der Hauptgott der Moabiter."] },
      { number: 14, text: "Wie könnt ihr sagen: Helden sind wir, / starke Krieger zum Kampf?" },
      { number: 15, text: "Der Verwüster Moabs und seiner Städte steigt hinauf; / da steigen seine besten jungen Männer hinab, um geschlachtet zu werden - Spruch des Königs, / Herr der Heere ist sein Name.", footnotes: ["46,18"] },
      { number: 16, text: "Der Untergang Moabs rückt heran, / schnell eilt sein Unglück herbei." },
      { number: 17, text: "Beklagt es alle, ihr seine Nachbarn, / und alle, die ihr seinen Namen kennt. Sagt: Ach, wie ist zerbrochen der starke Stab, / das herrliche Zepter!" },
      { number: 18, text: "Steig herab von deiner Würde, / setz dich in den Kot, / du thronende Tochter Dibon! Denn Moabs Verwüster zieht hinauf zu dir, / zerstört deine Burgen." },
      { number: 19, text: "Stellt euch an den Weg und späht aus, / ihr Einwohner von Aroër! Fragt den Flüchtling und die fliehende Frau, / sagt: Was ist denn geschehen?" },
      { number: 20, text: "Zuschanden ist Moab, ja, ganz zerbrochen. / Klagt und schreit! / Meldet am Arnon: Moab ist verwüstet." },
      { number: 21, text: "Ein Gericht kam über das ebene Land, / über Holon, Jahaz und Mefaat," },
      { number: 22, text: "über Dibon, Nebo und Bet-Diblatajim, /" },
      { number: 23, text: "über Kirjatajim, Bet-Gamul und Bet-Meon," },
      { number: 24, text: "über Kerijot und Bozra / und über alle Städte des Landes Moab, / die fernen und die nahen." },
      { number: 25, text: "Abgehauen ist Moabs Horn, / zerschmettert sein Arm [- Spruch des Herrn].", footnotes: ["Spruch des Herrn: fehlt in G."] },
      { number: 26, text: "Macht es betrunken; / denn es hat geprahlt gegen den Herrn. So stürze Moab in sein eigenes Gespei / und verfalle nun selbst dem Gespött.", footnotes: ["48,42"] },
      { number: 27, text: "Oder diente nicht Israel dir zum Gespött? / Wurde es etwa beim Diebstahl ertappt, dass du höhnend den Kopf geschüttelt hast, / sooft du von ihm sprachst?", footnotes: ["2,26"] },
      { number: 28, text: "Verlasst die Städte, wohnt in den Felsen, / ihr Bewohner von Moab! Macht es wie die Taube, die nistet / an den Wänden der offenen Schlucht." },
      { number: 29, text: "Wir haben von Moabs Stolz gehört - / es ist stolz über die Maßen -; von seinem Hochmut und Stolz haben wir gehört, / von seinem Dünkel und hochfahrenden Sinn.", heading: "Der Hochmut vor dem Fall: 48,29-39", footnotes: ["Jes 16,6-12"] },
      { number: 30, text: "Ich kenne seinen Übermut - Spruch des Herrn -, / sein Geschwätz ist nicht wahr, / sein Tun ist nicht recht." },
      { number: 31, text: "Darum jammere ich laut über Moab, / über ganz Moab klage ich / und seufze über die Leute von Kir-Heres." },
      { number: 32, text: "Mehr als um Jaser weine ich um dich, / Weinstock von Sibma; bis ans Meer zogen sich deine Ranken hin, / sie reichten bis Jaser. Über deine Weinlese und Ernte / fiel der Verwüster her." },
      { number: 33, text: "Verschwunden sind Freude und Jubelgeschrei / vom Fruchtland und vom Land Moab. Die Kufen sind leer von Wein, / kein Winzer keltert, / kein Jauchzen ertönt." },
      { number: 34, text: "Das Schreien von Heschbon und Elale dringt bis Jahaz, laut klagt man von Zoar bis Horonajim und Eglat-Schelischija; denn selbst das Wasser von Nimrim versiegt.", footnotes: ["Jes 15,4"] },
      { number: 35, text: "Ich mache es für Moab unmöglich - Spruch des Herrn -, dass jemand auf der Kulthöhe opfert und seinen Göttern Rauchopfer darbringt." },
      { number: 36, text: "Darum jammert mein Herz um Moab wie eine Flöte, ja mein Herz jammert um die Leute von Kir-Heres wie eine Flöte, weil sie ihre ganze Habe verloren." },
      { number: 37, text: "Jedes Haupt ist kahl geschoren und jeder Bart abgeschnitten, an allen Händen sind Trauermale und um die Hüften Trauerkleider.", footnotes: ["16,6; Jes 15,2f"] },
      { number: 38, text: "Auf allen Dächern und Plätzen Moabs hört man nichts als Klage. Ja, ich zerschlage Moab wie ein Gefäß, das niemand mehr will - Spruch des Herrn." },
      { number: 39, text: "Ach, wie ist es zerbrochen, wie hat Moab sich schmählich zur Flucht gewandt! So wird Moab zum Gespött und zu einem Bild des Entsetzens für all seine Nachbarn." },
      { number: 40, text: "Denn so spricht der Herr: Seht, wie ein Adler schwebt es heran / und breitet seine Schwingen über Moab aus.", heading: "Das unabwendbare Strafgericht: 48,40-47", footnotes: ["49,22", "Wer «es» oder «er» ist, wird in geheimnisvoller Schwebe gelassen: Gott selbst oder ein menschlicher Vollstrecker seines Strafgerichts."] },
      { number: 41, text: "Die Städte werden erobert, / die Festungen eingenommen. Das Herz der Helden Moabs ist an jenem Tag / wie das Herz einer Frau in Wehen." },
      { number: 42, text: "Vernichtet wird Moab, sodass es kein Volk mehr ist; / denn es hat geprahlt gegen den Herrn.", footnotes: ["48,26"] },
      { number: 43, text: "Grauen, Grube und Garn warten auf dich, / Bewohner von Moab - Spruch des Herrn.", footnotes: ["Jes 24,17f"] },
      { number: 44, text: "Wer dem Grauen entflieht, fällt in die Grube, / wer aus der Grube steigt, fängt sich im Garn. Ja, das werde ich über Moab bringen / im Jahr seiner Bestrafung - Spruch des Herrn." },
      { number: 45, text: "Im Schatten von Heschbon bleiben erschöpft Flüchtlinge stehen. / Doch Feuer geht aus von Heschbon, / eine Flamme von Sihons Haus. Es verzehrt die Schläfe Moabs, / den Scheitel der lärmenden Schreier.", footnotes: ["(45-46) Num 21,28f"] },
      { number: 46, text: "Wehe dir, Moab, / verloren bist du, Volk des Kemosch. Denn deine Söhne schleppt man in die Verbannung, / deine Töchter in die Gefangenschaft." },
      { number: 47, text: "Aber in ferner Zukunft wende ich Moabs Geschick / - Spruch des Herrn. / [Bis hierher geht das Gerichtswort über Moab.]", footnotes: ["49,6.39", "47c: Fehlt in G."] },
    ] },
    { number: 49, verses: [
      { number: 1, text: "Über die Ammoniter: So spricht der Herr: Hat denn Israel keine Söhne / oder besitzt es keinen Erben? Warum hat Milkom den Stamm Gad beerbt, / warum ließ sich sein Volk in dessen Städten nieder?", heading: "Über andere Nachbarvölker: 49,1-39 | Über die Ammoniter: 49,1-6", footnotes: ["Milkom (= König) ist der Hauptgott der Ammoniter."] },
      { number: 2, text: "Darum seht, es kommen Tage - Spruch des Herrn -, / da lasse ich gegen das ammonitische Rabba Kriegslärm ertönen. Zum wüsten Trümmerhaufen wird es werden, / seine Tochterstädte brennen im Feuer nieder und Israel wird die beerben, / die ihm sein Erbteil raubten - spricht der Herr." },
      { number: 3, text: "Klage laut, Heschbon, denn die Stadt ist zerstört. / Schreit, ihr Töchter von Rabba! Legt Trauerkleider an, haltet die Totenklage, / lauft umher mit eingeritzten Wunden! Denn Milkom muss in die Verbannung gehen / zusammen mit seinen Priestern und Fürsten." },
      { number: 4, text: "Was rühmst du dich deiner Kräfte? / Deine Kraft wird zerrinnen, du abtrünnige Tochter, die auf ihre Schätze vertraut und sagt: / Wer kommt an mich heran?" },
      { number: 5, text: "Schon lasse ich über dich kommen Schrecken von allen Seiten / - Spruch des Herrn, des Gottes der Heere. Ihr stoßt einander vorwärts (auf der Flucht) / und niemand wird die Flüchtlinge sammeln." },
      { number: 6, text: "Aber danach wende ich das Geschick der Ammoniter / - Spruch des Herrn.", footnotes: ["48,47"] },
      { number: 7, text: "Über Edom: So spricht der Herr der Heere: Gibt es keine Weisheit mehr in Teman? / Ging den Klugen der Rat verloren, / ist ihre Weisheit verdorben?", heading: "Über Edom: 49,7-22", footnotes: ["Obd 8f"] },
      { number: 8, text: "Flieht, macht kehrt, / tief unten versteckt euch, Einwohner von Dedan! Denn ich bringe über Esau Verderben, / die Zeit seiner Bestrafung.", footnotes: ["8.10: Esau ist nach Gen 36 der Stammvater der Edomiter."] },
      { number: 9, text: "Wenn Winzer zu dir kommen, / lassen sie keine Nachlese übrig. Wenn Diebe in der Nacht kommen, / zerstören sie nach Lust.", footnotes: ["Obd 5"] },
      { number: 10, text: "Ja, ich selbst hole Esau aus dem Versteck, / decke seine Schlupfwinkel auf, / sodass er sich nicht mehr verbergen kann. Seine Brut wird vernichtet, / seine Verwandtschaft und seine Nachbarn, / sodass keiner mehr sagt:", footnotes: ["Obd 6"] },
      { number: 11, text: "Lass deine Waisen! Ich will für sie sorgen, / deine Witwen können sich verlassen auf mich." },
      { number: 12, text: "Denn so spricht der Herr: Sogar jene, die es nicht verdient hätten, aus dem Becher zu trinken, müssen daraus trinken und da solltest du verschont bleiben? Du bleibst nicht verschont, du musst trinken.", footnotes: ["25,15-17"] },
      { number: 13, text: "Denn ich schwöre bei mir selbst - Spruch des Herrn: Zu einem Bild des Entsetzens, zum Hohn, zur Öde und zum Fluch wird Bozra und alle seine Nachbarstädte werden für immer zu Trümmerhaufen.", footnotes: ["44,22"] },
      { number: 14, text: "Ich habe vom Herrn eine Kunde gehört, / ein Bote ist zu den Völkern gesandt: Schart euch zusammen, rückt aus gegen Edom, / tretet an zum Kampf!", footnotes: ["(14-16) Obd 1-4"] },
      { number: 15, text: "Ja, ich mache dich klein unter den Völkern, / verachtet unter den Menschen." },
      { number: 16, text: "Deine Furcht erregende Macht hat dich betört, / dein vermessener Sinn, weil du in Felsklüften wohnst, / an Bergeshöhen dich klammerst. Baust du dein Nest auch hoch wie der Adler, / ich stürze dich von dort hinab - Spruch des Herrn." },
      { number: 17, text: "Edom wird zu einem Bild des Entsetzens; / jeder, der vorbeikommt, ist entsetzt / und spottet über alle Schläge, die es erlitt.", footnotes: ["19,8; 50,13"] },
      { number: 18, text: "Wie beim Untergang von Sodom und Gomorra / und ihrer Nachbarstädte, spricht der Herr, so wird auch dort niemand mehr wohnen, / kein Mensch darin leben.", footnotes: ["50,40; Jes 13,19f"] },
      { number: 19, text: "Wie ein Löwe, der heraufkommt / aus dem Dickicht des Jordan / zu den immer grünen Auen, so jage ich sie jählings davon / und setze meinen Erwählten dort ein. Denn wer ist mir gleich, / wer zieht mich zur Rechenschaft / und wo ist der Hirt, der vor mir standhält?", footnotes: ["19-21: Ist fast wörtlich gleich wie die Drohrede gegen Babel in 50,44-46 und hat dort seinen ursprünglichen Platz, wie man aus V. 21 ersieht. Nicht vom Untergang des Kleinstaats Edom «erbebt die Erde», sondern nur vom Fall des Neubabylonischen Großreichs."] },
      { number: 20, text: "Darum hört den Beschluss, / den der Herr gegen Edom gefasst hat, und die Pläne, die er ersann / gegen Temans Bewohner: Wegschleppen wird man die Hirtenknaben, / ihr Weideplatz wird sich über sie entsetzen." },
      { number: 21, text: "Von Edoms dröhnendem Fall erbebt die Erde, / bis zum Schilfmeer hört man sein Schreien." },
      { number: 22, text: "Seht, wie ein Adler steigt es empor, / schwebt es heran / und breitet seine Schwingen über Bozra aus. Das Herz der Helden Edoms ist an jenem Tag / wie das Herz einer Frau in Wehen.", footnotes: ["48,40f"] },
      { number: 23, text: "Über Damaskus: Bestürzt sind Hamat und Arpad; / denn böse Nachricht mussten sie hören. Sie wanken vor Kummer wie das Meer, / das nicht zur Ruhe kommen kann.", heading: "Über Damaskus: 49,23-27", footnotes: ["Jes 57,20"] },
      { number: 24, text: "Damaskus wird schwach, es wendet sich zur Flucht, / Schrecken erfasst es, / Angst und Wehen ergreifen es wie eine Gebärende." },
      { number: 25, text: "Wie ist doch die ruhmreiche Stadt verlassen, / die Burg der Freude!" },
      { number: 26, text: "Darum fallen ihre jungen Männer auf den Plätzen, / alle Krieger kommen um an jenem Tag / - Spruch des Herrn der Heere.", footnotes: ["50,30"] },
      { number: 27, text: "Ich lege Feuer an die Mauer von Damaskus; / es verzehrt die Paläste Ben-Hadads.", footnotes: ["17,27; Am 1,4"] },
      { number: 28, text: "Über Kedar und die Reiche von Hazor, die Nebukadnezzar, der König von Babel, erobert hat. So spricht der Herr: Auf, zieht gegen Kedar, / bezwingt die Söhne des Ostens!", heading: "Über die arabischen Stämme: 49,28-33", footnotes: ["Söhne des Ostens: die Beduinen der Arabischen Wüste."] },
      { number: 29, text: "Man raubt ihre Zelte und Herden, / ihre Decken und ihr ganzes Gerät; auch ihre Kamele nimmt man mit / und man ruft über sie: Grauen ringsum!", footnotes: ["6,25; 20,3; 46,5"] },
      { number: 30, text: "Flieht, macht euch eilends davon, / tief unten versteckt euch, / ihr Bewohner von Hazor - Spruch des Herrn. Denn Nebukadnezzar, der König von Babel, / hat gegen euch einen Beschluss gefasst / und einen Plan gegen euch ersonnen.", footnotes: ["49,8.20"] },
      { number: 31, text: "Auf, zieht gegen das sorglose Volk, / das in Sicherheit lebt - Spruch des Herrn -, das keine Tore und Riegel hat; / man haust ja einsam für sich." },
      { number: 32, text: "Ihre Kamele werden erbeutet, / ihre vielen Herden geraubt. In alle Winde zerstreue ich sie, / die Leute mit gestutztem Haar, von allen Seiten bringe ich ihnen Verderben / - Spruch des Herrn.", footnotes: ["25,23"] },
      { number: 33, text: "Hazor wird zur Behausung für die Schakale, / eine Wüste für immer. Niemand wird mehr dort wohnen, / kein Mensch darin leben.", footnotes: ["9,10; 49,13.18; 50,3"] },
      { number: 34, text: "Das Wort des Herrn gegen Elam, das zu Beginn der Regierung Zidkijas, des Königs von Juda, an den Propheten Jeremia erging.", heading: "Über Elam: 49,34-39", footnotes: ["Elam war damals noch selbstständig, ging aber später im Kernland des Perserreichs auf. Es ist das Gebiet nördlich des Persischen Golfs."] },
      { number: 35, text: "So spricht der Herr der Heere: Seht, ich zerbreche den Bogen Elams, / seine stärkste Waffe.", footnotes: ["Jes 22,6"] },
      { number: 36, text: "Ich bringe über Elam vier Winde / von den vier Enden des Himmels. In alle diese Winde zerstreue ich sie, / sodass es kein Volk gibt, / zu dem nicht Versprengte aus Elam kommen." },
      { number: 37, text: "Ich jage den Elamitern Schrecken ein vor ihren Feinden, / vor allen, die ihnen nach dem Leben trachten. Unheil lasse ich über sie kommen, / meinen glühenden Zorn - Spruch des Herrn. Ich schicke das Schwert hinter ihnen her, / bis ich sie vernichtet habe.", footnotes: ["9,15"] },
      { number: 38, text: "Ich stelle meinen Thron in Elam auf / und vernichte dort König und Fürsten - / Spruch des Herrn." },
      { number: 39, text: "Aber in ferner Zukunft wende ich Elams Geschick / - Spruch des Herrn.", footnotes: ["48,47; 49,6"] },
    ] },
    { number: 50, verses: [
      { number: 1, text: "Das Wort gegen Babel, das Land der Chaldäer, das der Herr durch den Propheten Jeremia gesprochen hat.", heading: "Über Babel: 50,1 - 51,64" },
      { number: 2, text: "Verkündet unter den Völkern und meldet, / [errichtet ein Wegzeichen und meldet] / verheimlicht nichts, sondern sagt: Erobert ist Babel, zuschanden ist Bel, / zerschmettert Merodach [zuschanden sind seine Götterbilder, / zerschmettert seine Götzen].", heading: "Das Ende des Exils: 50,2-7", footnotes: ["4,6; Jes 46,1", "Bel (wie Baal = Herr) und Merodach sind Bezeichnungen für Marduk, den Hauptgott von Stadt und Reich Babylon. - Die Zusätze fehlen in G."] },
      { number: 3, text: "Denn ein Volk aus dem Norden rückt gegen Babel heran; / das macht sein Land zur Wüste. Niemand mehr wohnt darin, / Mensch und Vieh ergreifen die Flucht / und laufen davon.", footnotes: ["49,33; 9,9"] },
      { number: 4, text: "In jenen Tagen und zu jener Zeit - Spruch des Herrn - / kommen die Söhne Israels gemeinsam mit den Söhnen Judas. Weinend gehen sie ihren Weg / und suchen den Herrn, ihren Gott.", footnotes: ["31,9"] },
      { number: 5, text: "Nach Zion fragen sie, / dorthin ist ihr Blick gerichtet. Sie kommen und verbünden sich mit dem Herrn / zu einem ewigen, unvergesslichen Bund.", footnotes: ["32,40"] },
      { number: 6, text: "Eine verlorene Herde war mein Volk, / ihre Hirten führten sie in die Irre, / trieben sie ziellos in die Berge. Von Berg zu Hügel zogen sie weiter / und vergaßen ihren Lagerplatz.", footnotes: ["50,17; 23,1"] },
      { number: 7, text: "Wer auf sie stieß, fraß sie auf / und ihre Feinde sagten: Wir begehen kein Unrecht; / sie haben ja gegen den Herrn gesündigt, / den Hort der Gerechtigkeit, die Hoffnung ihrer Väter.", footnotes: ["31,23"] },
      { number: 8, text: "Flieht aus Babel und aus dem Land der Chaldäer! / Zieht aus und seid wie Leitböcke, die der Herde vorangehen.", heading: "Der Entscheidungskampf: 50,8-20", footnotes: ["51,6; Jes 48,20"] },
      { number: 9, text: "Denn seht, ich selbst stachle auf / und führe gegen Babel / eine Schar großer Völker vom Nordland her; sie greifen es an / und von dort wird es erobert. Ihre Pfeile sind wie die eines siegreichen Helden, / der nie zurückkehrt ohne Erfolg." },
      { number: 10, text: "Plünderung trifft Chaldäa, / alle, die es plündern, werden satt / Spruch des Herrn." },
      { number: 11, text: "Freut euch nur und jubelt, / ihr, die ihr mein Erbteil geraubt habt. Ja, hüpft wie Kälber auf der Wiese / und wiehert wie Hengste!" },
      { number: 12, text: "Große Schmach trifft eure Mutter; / sie, die euch geboren hat, muss sich schämen. Seht doch, das Letzte unter den Völkern: / Wüste, Dürre und Steppe." },
      { number: 13, text: "Durch den Zorn des Herrn bleibt Babel unbewohnt / und wird völlig zur Wüste; jeder, der an Babel vorbeikommt, ist entsetzt / und spottet über alle Schläge, die es erlitt.", footnotes: ["19,8; 49,17; 51,37"] },
      { number: 14, text: "Rüstet euch ringsum zum Kampf gegen Babel, / all ihr Bogenschützen! Schießt und spart die Pfeile nicht! / [Denn gegen den Herrn hat es gesündigt.]", footnotes: ["Der Zusatz fehlt in G."] },
      { number: 15, text: "Schreit ihm von allen Seiten / den Kampfruf entgegen! Es muss sich ergeben, seine Säulen fallen, / seine Mauern werden niedergerissen. Ja, das ist die Rache des Herrn. / Nehmt Rache an Babel! / Was es selber getan hat, das tut jetzt an ihm!", footnotes: ["50,28f"] },
      { number: 16, text: "Rottet in Babel den Sämann aus / und den, der zur Erntezeit mäht. Vor dem rasenden Schwert / wendet sich jeder zu seinem Volk, / jeder flieht in sein Land.", footnotes: ["46,16"] },
      { number: 17, text: "Ein versprengtes Schaf war Israel, / von Löwen gehetzt. Zuerst hat es der König von Assur gefressen, / zuletzt hat ihm Nebukadnezzar, der König von Babel, die Knochen abgenagt." },
      { number: 18, text: "Darum - so spricht der Herr der Heere, der Gott Israels: / Fürwahr, ich rechne ab mit dem König von Babel und seinem Land, wie ich abgerechnet habe / mit dem König von Assur." },
      { number: 19, text: "Israel aber bringe ich zurück auf seinen Weideplatz; / es soll auf dem Karmel und im Baschan weiden, / sich im Bergland Efraim und in Gilead sättigen." },
      { number: 20, text: "In jenen Tagen und zu jener Zeit / - Spruch des Herrn - / wird man nach der Schuld Israels suchen, doch sie ist nicht mehr vorhanden, / nach den Sünden Judas, / doch man findet sie nicht mehr. Denn ich verzeihe dem Rest, / den ich übrig lasse.", footnotes: ["33,8"] },
      { number: 21, text: "Zieh gegen Meratajim, / zieh gegen das Land / und gegen die Bewohner von Pekod! Erschlag sie und gib sie dem Untergang preis - Spruch des Herrn; / tu genau, was ich dir befehle!", heading: "Die völlige Zerstörung: 50,21-32" },
      { number: 22, text: "Kriegslärm herrscht im Land / und großer Zusammenbruch." },
      { number: 23, text: "Wie wurde zerschlagen und zerschmettert / der Hammer der ganzen Welt! Welch ein Bild des Entsetzens ist Babel geworden / unter den Völkern!", footnotes: ["51,20"] },
      { number: 24, text: "Du, Babel, hast dir selbst eine Falle gestellt / und bist auch gefangen worden, ehe du es merktest. Du wurdest erwischt und gepackt; / denn du hattest den Herrn herausgefordert." },
      { number: 25, text: "Der Herr hat seine Rüstkammer geöffnet / und die Waffen seines Zornes hervorgeholt. Denn das ist ein Werk, / das der Herr, der Gott der Heere, / im Land der Chaldäer vollbringt." },
      { number: 26, text: "Kommt nach Babel vom Ende der Erde! / Öffnet seine Speicher, werft alles auf einen Haufen zusammen, / wie man Haufen von Korn aufschüttet. Dann gebt es dem Untergang preis; / kein Rest soll ihm bleiben." },
      { number: 27, text: "Erschlagt all seine Jungstiere, / hinunter mit ihnen zum Schlachten! Weh über sie; denn ihr Tag ist gekommen, / die Zeit ihrer Bestrafung.", footnotes: ["50,31"] },
      { number: 28, text: "Horcht! Entronnene Flüchtlinge aus dem Land Babel! / Sie verkünden in Zion die Rache des Herrn, unseres Gottes, / [die Rache für seinen Tempel].", footnotes: ["51,11", "28c: Fehlt in G und dürfte aus 51,11 eingedrungen sein."] },
      { number: 29, text: "Ruft Schützen auf gegen Babel, / alle Bogenschützen! Belagert die Stadt ringsum, / lasst keinen entrinnen! Vergeltet ihr nach ihrem Tun; / alles, was sie selber getan hat, / das tut auch an ihr! Denn gegen den Herrn hat sie frech gehandelt, / gegen den Heiligen Israels.", footnotes: ["50,15"] },
      { number: 30, text: "Darum fallen ihre jungen Männer auf den Plätzen, / all ihre Krieger kommen um an jenem Tag / - Spruch des Herrn.", footnotes: ["49,26"] },
      { number: 31, text: "Nun gehe ich gegen dich vor, du Freche / - Spruch des Herrn, des Gottes der Heere. Denn dein Tag ist gekommen, / die Zeit deiner Bestrafung.", footnotes: ["51,25"] },
      { number: 32, text: "Die Freche strauchelt und fällt, / niemand richtet sie auf. Ich lege Feuer an ihre Städte, / das ringsum alles verzehrt.", footnotes: ["21,14"] },
      { number: 33, text: "So spricht der Herr der Heere: Unterdrückt sind die Söhne Israels / zusammen mit den Söhnen Judas. Von allen, die sie in Gefangenschaft führten, / werden sie festgehalten; / man weigert sich, sie zu entlassen.", heading: "Die Befreiung Israels: 50,33-46" },
      { number: 34, text: "Doch ihr Erlöser ist stark, / Herr der Heere ist sein Name. Er führt ihre Sache mit Kraft, / um der Erde Ruhe zu schaffen, / Unruhe aber Babels Bewohnern." },
      { number: 35, text: "Das Schwert über die Chaldäer - Spruch des Herrn - / und über die Bewohner von Babel, / über seine Fürsten und seine Weisen!" },
      { number: 36, text: "Das Schwert über die Wahrsager, / sie werden zu Narren! Das Schwert über seine Helden, / sie brechen zusammen!" },
      { number: 37, text: "Das Schwert [über seine Rosse und Wagen und] / über alles Völkergemisch in seinen Reihen, / es wird zu Weibern! Das Schwert über seine Schätze, / sie werden geraubt!", footnotes: ["37a: Zusatz aus 51,21."] },
      { number: 38, text: "Das Schwert über seine Wasser, / sie vertrocknen! Denn es ist ein Land voll von Götzenbildern / und durch die Schreckbilder werden sie toll." },
      { number: 39, text: "Darum werden Wüstenhunde und Hyänen dort hausen / und Strauße werden sich dort niederlassen. Nie mehr soll es bewohnt sein, / ewig nicht mehr besiedelt werden.", footnotes: ["Jes 13,20"] },
      { number: 40, text: "Wie Gott einst Sodom und Gomorra / und ihre Nachbarstädte zerstört hat / - Spruch des Herrn -, so wird auch dort niemand wohnen, / kein Mensch darin leben.", footnotes: ["49,18"] },
      { number: 41, text: "Seht, ein Volk zieht von Norden heran, / ein großes Volk und viele Könige / brechen auf von den Grenzen der Erde.", footnotes: ["(41-43) 6,22-24"] },
      { number: 42, text: "Sie kommen mit Bogen und Sichelschwert; / grausam sind sie und ohne Erbarmen. Ihr Lärm gleicht dem Brausen des Meeres / und sie reiten auf Rossen, Krieger, zum Kampf gerüstet / gegen dich, Tochter Babel." },
      { number: 43, text: "Sobald der König von Babel von ihnen hört, / da erschlaffen ihm die Hände; es packt ihn die Angst, / das Zittern, wie eine Gebärende." },
      { number: 44, text: "Wie ein Löwe, der heraufkommt aus dem Dickicht des Jordan / zu den immer grünen Auen, so jage ich sie jählings davon / und setze meinen Erwählten dort ein. Denn wer ist mir gleich, / wer zieht mich zur Rechenschaft / und wo ist der Hirt, der vor mir standhält?", footnotes: ["(44-46) 4,7; 49,19-21"] },
      { number: 45, text: "Darum hört den Beschluss, / den der Herr gegen Babel gefasst hat, und die Pläne, die er ersann / gegen das Land der Chaldäer: Wegschleppen wird man die Hirtenknaben, / ihr Weideplatz wird sich über sie entsetzen." },
      { number: 46, text: "Vom Ruf «Erobert ist Babel» erbebt die Erde, / unter den Völkern hört man sein Schreien." },
    ] },
    { number: 51, verses: [
      { number: 1, text: "So spricht der Herr: Seht, gegen Babel und die Bewohner Chaldäas / wecke ich den Geist eines Verwüsters.", heading: "Gottes Strafgericht: 51,1-14", footnotes: ["1-14: Die hier angekündigte Zerstörung Babylons ist nicht eingetreten. Kyros hat es als Befreier betreten. Erst seit etwa 120 v. Chr. verfällt die Stadt allmählich, bis sie schließlich ganz vergessen ist."] },
      { number: 2, text: "Ich sende Worfler nach Babel; / die werden es worfeln und sein Land auskehren, / wenn sie es umzingeln am Tag des Unheils.", footnotes: ["15,7"] },
      { number: 3, text: "Nicht erschlaffe, wer den Bogen spannt, / der Gepanzerte werde nicht müde. Schont seine jungen Männer nicht, / gebt sein ganzes Heer dem Untergang preis!" },
      { number: 4, text: "Erschlagene liegen herum im Land der Chaldäer, / Durchbohrte auf seinen Straßen." },
      { number: 5, text: "Denn nicht verwitwet sind Israel und Juda, / nicht verlassen von ihrem Gott, / dem Herrn der Heere. Doch das Chaldäerland ist voll von Schuld / gegen den Heiligen Israels.", footnotes: ["Jes 54,4"] },
      { number: 6, text: "Flieht aus Babel, jeder rette sein Leben, / damit ihr nicht umkommt bei seinem Schuldgericht. Denn es ist die Zeit der Rache für den Herrn; / was Babel verübt hat, zahlt er ihm heim.", footnotes: ["50,8.15"] },
      { number: 7, text: "Babel war in der Hand des Herrn / ein goldener Becher, / der die ganze Erde berauschte. Von seinem Wein haben die Völker getrunken; / deshalb haben sie den Verstand verloren.", footnotes: ["25,15"] },
      { number: 8, text: "Jählings fällt Babel und wird zerschmettert. / Klagt laut darüber! Holt Balsam für seine Wunde, / vielleicht ist es zu heilen.", footnotes: ["Jes 21,9"] },
      { number: 9, text: "Wir wollten Babel Heilung bringen, / es war aber nicht mehr zu heilen. Verlasst es! Gehen wir, jeder in sein Land! / Denn sein Gericht reicht bis zum Himmel hinauf, / ragt bis zu den Wolken empor." },
      { number: 10, text: "Der Herr hat unsre gerechte Sache / ans Licht gebracht. Kommt, lasst uns in Zion erzählen, / was der Herr, unser Gott, getan hat." },
      { number: 11, text: "Schärft die Pfeile, füllt die Köcher! / Der Herr hat den Geist der Könige von Medien erweckt; denn er hegt den Plan, / Babel zu vernichten. Ja, das ist die Rache des Herrn, / die Rache für seinen Tempel.", footnotes: ["Jes 13,17; Jer 50,28"] },
      { number: 12, text: "Errichtet ein Feldzeichen gegen Babels Mauern, / verstärkt die Wache! Stellt Posten auf, / legt Leute in den Hinterhalt! Denn der Herr hat seinen Plan gefasst / und er führt auch aus, / was er Babels Einwohnern angedroht hat.", footnotes: ["50,2"] },
      { number: 13, text: "Die du an großen Wassern wohnst, so reich an Schätzen, / dein Ende ist da, dein Maß ist voll." },
      { number: 14, text: "Geschworen hat der Herr der Heere bei sich selbst: / Wärst du auch mit Menschen angefüllt wie mit Heuschrecken, / man stimmt doch den Kampfruf gegen dich an." },
      { number: 15, text: "Er hat die Erde erschaffen durch seine Kraft, / den Erdkreis gegründet durch seine Weisheit, / durch seine Einsicht den Himmel ausgespannt.", heading: "Gottes Größe: 51,15-19", footnotes: ["(15-19) 10,12-16", "15-19: Der mit 10,12-16 gleich lautende Hymnus durchbricht den Zusammenhang."] },
      { number: 16, text: "Lässt er seine Stimme ertönen, / dann rauschen die Wasser am Himmel. Wolken führt er herauf vom Rand der Erde; / er lässt es blitzen und regnen, / aus seinen Kammern entsendet er den Wind.", footnotes: ["10,13; Ps 135,7"] },
      { number: 17, text: "Töricht steht jeder Mensch da, ohne Erkenntnis, / beschämt jeder Goldschmied mit seinem Götzenbild; denn seine Bilder sind Trug, / kein Atem ist in ihnen." },
      { number: 18, text: "Nichtig sind sie, ein Spottgebilde. / Zur Zeit ihrer Heimsuchung gehen sie zugrunde." },
      { number: 19, text: "Anders der Gott, der Jakobs Anteil ist. / Denn er ist der Schöpfer des Alls / und Israel der Stamm, der ihm gehört. / Herr der Heere ist sein Name." },
      { number: 20, text: "Du warst mein Hammer, meine Waffe für den Krieg. / Mit dir zerschlug ich Völker, / mit dir stürzte ich Königreiche,", heading: "Babel als Hammer Gottes: 51,20-26", footnotes: ["50,23"] },
      { number: 21, text: "mit dir zerschlug ich Ross und Lenker, / mit dir zerschlug ich Wagen und Fahrer," },
      { number: 22, text: "mit dir zerschlug ich Mann und Frau, / mit dir zerschlug ich Greis und Kind, / mit dir zerschlug ich Knabe und Mädchen," },
      { number: 23, text: "mit dir zerschlug ich Hirt und Herde, / mit dir zerschlug ich Bauer und Gespann, / mit dir zerschlug ich Statthalter und Vorsteher." },
      { number: 24, text: "Aber ich übe Vergeltung an Babel und an allen Bewohnern Chaldäas / für alles Böse, das sie an Zion vor euren Augen verübten - Spruch des Herrn.", footnotes: ["50,15.29"] },
      { number: 25, text: "Nun gehe ich gegen dich vor, / du Berg des Verderbens, / der die ganze Erde verdarb - Spruch des Herrn. Ich strecke meine Hand gegen dich aus, / ich wälze dich weg von den Felsen / und mache dich zum ausgebrannten Berg.", footnotes: ["50,31"] },
      { number: 26, text: "Man wird von dir keinen Eckstein / und keinen Grundstein mehr holen, nein, Wüste bleibst du für immer / - Spruch des Herrn." },
      { number: 27, text: "Errichtet ein Feldzeichen auf der Erde, / stoßt ins Horn unter den Völkern! Bietet Völker zum Heiligen Krieg auf gegen die Stadt, / ruft Königreiche herbei gegen sie! Ararat, Minni und Aschkenas, / hebt Truppen aus gegen sie, lasst Rosse anrücken, / borstigen Heuschrecken gleich!", heading: "Die Strafe für die Schuld: 51,27-40" },
      { number: 28, text: "Bietet Völker zum Heiligen Krieg auf gegen sie, / die Könige von Medien, seine Statthalter und Vorsteher / und das ganze Land ihrer Herrschaft!" },
      { number: 29, text: "Da bebt und zittert die Erde, / wenn sich an Babel der Plan des Herrn erfüllt, das Land von Babel zur Wüste zu machen, / die niemand bewohnt." },
      { number: 30, text: "Die Helden Babels geben den Kampf auf / und hocken in ihren Burgen. Ihre Heldenkraft ist versiegt, / Weiber sind sie geworden. Seine Wohnungen steckt man in Brand, / seine Riegel zerbricht man.", footnotes: ["50,37; Jes 19,16; Nah 3,13"] },
      { number: 31, text: "Läufer über Läufer stürmt heran, / Bote über Bote, um dem König von Babel zu melden, / seine Stadt sei von allen Seiten erobert," },
      { number: 32, text: "die Flussübergänge seien besetzt, / die Kähne im Feuer verbrannt, / die Krieger entmutigt." },
      { number: 33, text: "Denn so spricht der Herr der Heere, / der Gott Israels: Die Tochter Babel gleicht der Tenne, / wenn sie festgestampft wird; noch eine kurze Frist, / dann ist für sie die Erntezeit da." },
      { number: 34, text: "Nebukadnezzar, der König von Babel, / hat mich gefressen und weggerafft, / mich beiseite gestellt wie ein leeres Gefäß. Er hat mich wie ein Drache verschlungen, / hat sich den Bauch gefüllt / und mich aus dem Ort meiner Wonne vertrieben." },
      { number: 35, text: "Was ich an Unrecht und Schaden erlitt, / komme über Babel!, / so sollen die Bürger Zions sagen. Mein Blut komme über die Bewohner Chaldäas!, / so soll Jerusalem sagen." },
      { number: 36, text: "Darum - so spricht der Herr: Ich selber führe deine Sache / und will dich rächen. Ich lasse seinen Strom vertrocknen / und seine Quelle versiegen." },
      { number: 37, text: "Babel wird ein Trümmerhaufen, / eine Behausung für die Schakale, ein Ort des Entsetzens und des Spottes, / wo niemand wohnt.", footnotes: ["50,13"] },
      { number: 38, text: "Alle zusammen brüllen sie wie Löwen, / knurren wie Junglöwen." },
      { number: 39, text: "Ihrer Gier bereite ich das Gelage, / berausche sie, dass sie betäubt werden, in ewigen Schlaf versinken / und nie mehr erwachen - Spruch des Herrn." },
      { number: 40, text: "Ich führe sie hinab wie Lämmer zum Schlachten, / wie Widder und Böcke.", footnotes: ["50,27"] },
      { number: 41, text: "Weh, genommen und erobert / ist der Ruhm der ganzen Erde. Weh, Babel ist zu einem Bild des Entsetzens geworden / unter den Völkern.", heading: "Die endgültige Vernichtung: 51,41-58" },
      { number: 42, text: "Das Meer überflutet Babel, / vom Schwall seiner Wogen wird es bedeckt." },
      { number: 43, text: "Seine Städte werden zur Wüste, / ein Land der Dürre und Steppe, wo niemand wohnt / und wo kein Mensch mehr hindurchzieht.", footnotes: ["2,6"] },
      { number: 44, text: "Den Bel von Babel suche ich heim / und entreiße seinem Rachen, was er verschlungen hat. Die Völker strömen nicht mehr zu ihm. / Auch die Mauer von Babel muss fallen. -" },
      { number: 45, text: "Zieh weg aus seiner Mitte, mein Volk! / Jeder rette sein Leben vor dem glühenden Zorn des Herrn." },
      { number: 46, text: "Euer Herz soll nicht verzagen. / Fürchtet euch nicht bei dem Gerücht, das man im Land hört - man wird im einen Jahr dieses / und im andern Jahr jenes Gerücht hören - und wenn Gewalttat im Land regiert / und Herrscher gegen Herrscher steht." },
      { number: 47, text: "Darum seht, es kommen Tage, / da suche ich die Götzen Babels heim; sein ganzes Land wird zuschanden / und alle seine Erschlagenen, seine Gefallenen, liegen in seinem Gebiet." },
      { number: 48, text: "Dann jubeln über Babel Himmel und Erde / und alles, was in ihnen ist, wenn von Norden her die Verwüster einfallen / - Spruch des Herrn." },
      { number: 49, text: "Babel muss fallen für die Erschlagenen Israels, / ebenso wie die Erschlagenen der ganzen Welt für Babel gefallen sind." },
      { number: 50, text: "Ihr, die ihr dem Schwert entronnen seid, / zieht weg, haltet euch nicht auf! Denkt in der Ferne an den Herrn / und tragt Jerusalem in eurem Herzen!" },
      { number: 51, text: "Schämen müssen wir uns; / denn Schmach mussten wir hören. Schamröte bedeckt unser Gesicht; / denn Fremde sind gekommen / über die Heiligtümer des Hauses des Herrn." },
      { number: 52, text: "Darum seht, es kommen Tage / - Spruch des Herrn -, da suche ich seine Götzen heim / und in seinem ganzen Land röcheln Erschlagene." },
      { number: 53, text: "Wenn Babel auch bis zum Himmel emporsteigt / und sich in unzugänglicher Höhe verschanzt, / so werden doch auf meinen Wink Verwüster es überfallen - Spruch des Herrn.", footnotes: ["Jes 14,13"] },
      { number: 54, text: "Horch, lautes Schreien von Babel her, / großer Zusammenbruch im Land der Chaldäer." },
      { number: 55, text: "Denn der Herr verwüstet Babel / und macht seinem lauten Lärmen ein Ende, mögen seine Wogen brausen wie gewaltige Wasser, / mag tosend ihr Lärm erschallen." },
      { number: 56, text: "Ja, der Verwüster kommt über Babel. / Seine Helden werden gefangen genommen, / ihre Bogen zerbrochen. Denn der Herr ist ein Gott der Vergeltung; / genau rechnet er ab." },
      { number: 57, text: "Die Fürsten und Weisen Babels, / seine Statthalter, Vorsteher und Kriegshelden mache ich betrunken, in ewigen Schlaf sollen sie sinken / und nie mehr erwachen - Spruch des Königs, / Herr der Heere ist sein Name.", footnotes: ["51,39"] },
      { number: 58, text: "So spricht der Herr der Heere: Die breite Mauer von Babel wird geschleift bis auf den Grund, / seine hohen Tore werden niedergebrannt. So mühen sich Völker um nichts, / Nationen plagen sich ab für das Feuer.", footnotes: ["Hab 2,13"] },
      { number: 59, text: "Der Auftrag, den der Prophet Jeremia dem Seraja, dem Sohn Nerijas, des Sohnes Machsejas, erteilt hat, als dieser mit Zidkija, dem König von Juda, in dessen viertem Regierungsjahr nach Babel reiste. Seraja war oberster Quartiermeister.", heading: "Ein geschichtlicher Nachtrag: 51,59-64" },
      { number: 60, text: "Jeremia hatte all das Unheil, das über Babel kommen sollte, in einer einzigen Buchrolle aufgeschrieben, alle jene Worte, die über Babel aufgezeichnet sind.", footnotes: ["50,1 - 51,58"] },
      { number: 61, text: "Jeremia sagte zu Seraja: Wenn du nach Babel kommst, sieh zu, dass du alle diese Worte laut vorliest." },
      { number: 62, text: "Dann sag: Herr, du selbst hast diesem Ort angedroht, ihn zu vernichten, sodass niemand mehr darin wohnt, weder Mensch noch Vieh; für immer soll er zur Wüste werden.", footnotes: ["51,26"] },
      { number: 63, text: "Sobald du diese Buchrolle zu Ende gelesen hast, binde an sie einen Stein und wirf sie in den Eufrat!" },
      { number: 64, text: "Sprich dabei: So soll Babel versinken und nicht wieder hochkommen, wegen des Unheils, das ich über die Stadt bringe. [So weit reichen die Worte Jeremias.]", footnotes: ["Die Schlussbemerkung fehlt in G; vgl. die Anmerkung zu 48,47."] },
    ] },
    { number: 52, verses: [
      { number: 1, text: "Zidkija war einundzwanzig Jahre alt, als er König wurde, und regierte elf Jahre in Jerusalem. Seine Mutter hieß Hamutal und war eine Tochter Jirmejas aus Libna.", heading: "Anhang: Die Zerstörung Jerusalems: 52,1-30 | Die Eroberung Jerusalems: 52,1-11", footnotes: ["(1-11) 2 Kön 24,18 - 25,7", "1-34: Fast wörtlich aus 2 Kön übernommen: 52,1-27 = 2 Kön 24,18 - 25,21; 52,31-34 = 2 Kön 25,27-30. Ein Auszug von 52,4-16 steht bereits in 39,1-10."] },
      { number: 2, text: "Er tat, was dem Herrn missfiel, ganz so, wie es Jojakim getan hatte." },
      { number: 3, text: "Weil der Herr zornig war, kam es mit Jerusalem und Juda so weit, dass er sie von seinem Angesicht verstieß. Zidkija hatte sich gegen den König von Babel empört." },
      { number: 4, text: "Im neunten Regierungsjahr Zidkijas, am zehnten Tag des zehnten Monats, rückte Nebukadnezzar, der König von Babel, mit seiner ganzen Streitmacht vor Jerusalem und belagerte es. Man errichtete ringsherum einen Belagerungswall." },
      { number: 5, text: "Bis zum elften Jahr des Königs Zidkija wurde die Stadt belagert." },
      { number: 6, text: "Am neunten Tag des vierten Monats war in der Stadt die Hungersnot groß geworden und die Bürger des Landes hatten kein Brot mehr." },
      { number: 7, text: "Damals wurden Breschen in die Stadtmauer geschlagen. Als der König und alle Krieger das sahen, ergriffen sie die Flucht und verließen die Stadt bei Nacht auf dem Weg durch das Tor zwischen den beiden Mauern, das zum königlichen Garten hinausführt, obwohl die Chaldäer rings um die Stadt lagen. Sie schlugen die Richtung nach der Araba ein.", footnotes: ["Ergänzt nach 39,4."] },
      { number: 8, text: "Aber die chaldäischen Truppen setzten dem König nach und holten Zidkija in den Niederungen von Jericho ein, nachdem alle seine Truppen ihn verlassen und sich zerstreut hatten." },
      { number: 9, text: "Man ergriff den König und brachte ihn nach Ribla in der Landschaft Hamat zum König von Babel und dieser sprach über ihn das Urteil." },
      { number: 10, text: "Der König von Babel ließ die Söhne Zidkijas vor dessen Augen niedermachen; auch alle Großen Judas ließ er in Ribla niedermachen." },
      { number: 11, text: "Zidkija ließ er blenden und in Fesseln legen. Der König von Babel brachte ihn nach Babel und hielt ihn in Haft bis zu seinem Tod.", footnotes: ["32,5"] },
      { number: 12, text: "Am zehnten Tag des fünften Monats - das ist im neunzehnten Jahr des Königs Nebukadnezzar, des Königs von Babel - rückte Nebusaradan, der Kommandant der Leibwache, der zum engeren Dienst des Königs von Babel gehörte, in Jerusalem ein", heading: "Die Wegführung in die Verbannung: 52,12-30", footnotes: ["(12-17) 2 Kön 25,8-21"] },
      { number: 13, text: "und steckte das Haus des Herrn, den königlichen Palast und alle Häuser Jerusalems in Brand. Jedes große Haus ließ er in Flammen aufgehen." },
      { number: 14, text: "Auch alle Umfassungsmauern Jerusalems rissen die chaldäischen Truppen, die dem Kommandanten der Leibwache unterstanden, nieder." },
      { number: 15, text: "Den Rest der Bevölkerung, der noch in der Stadt geblieben war, sowie alle, die zum König von Babel übergelaufen waren, und den Rest der Handwerker schleppte Nebusaradan, der Kommandant der Leibwache, in die Verbannung." },
      { number: 16, text: "Nur von den armen Leuten im Land ließ Nebusaradan, der Kommandant der Leibwache, einen Teil als Wein- und Ackerbauern zurück." },
      { number: 17, text: "Die bronzenen Säulen am Haus des Herrn, die fahrbaren Gestelle und das Eherne Meer beim Haus des Herrn zerschlugen die Chaldäer und nahmen alle Gegenstände aus Bronze mit nach Babel.", footnotes: ["27,19-22"] },
      { number: 18, text: "Auch die Töpfe, Schaufeln, Messer, Schalen und Becher sowie alle bronzenen Geräte, die man beim Tempeldienst verwendete, nahmen sie weg." },
      { number: 19, text: "Ebenso nahm der Kommandant der Leibwache die Becken, Kohlenpfannen, Schalen, Töpfe, Leuchter, Becher und Schüsseln weg, die sämtlich aus Gold oder aus Silber waren," },
      { number: 20, text: "ferner die zwei Säulen, das eine «Meer» [die zwölf bronzenen Rinder unter dem Meer], die Gestelle, die König Salomo für das Haus des Herrn hatte anfertigen lassen - die Bronze von all diesen Geräten war nicht zu wiegen.", footnotes: ["Der Zusatz fehlt in 2 Kön 25,16. Die Rinder aus Bronze hatte bereits Ahas an den Assyrerkönig abgeliefert (vgl. 2 Kön 16,17)."] },
      { number: 21, text: "Was die Säulen betrifft, so hatte jede Säule eine Höhe von achtzehn Ellen und ein Band von zwölf Ellen umschlang sie; ihre Dicke betrug vier Finger; innen war sie hohl." },
      { number: 22, text: "Oben hatte sie ein Kapitell aus Bronze. Die Höhe des einen Kapitells betrug fünf Ellen; das Kapitell umgaben Flechtwerk und Granatäpfel, alles aus Bronze. Ebenso war es bei der zweiten Säule [und den Granatäpfeln]." },
      { number: 23, text: "Es waren sechsundneunzig frei hängende Granatäpfel; im Ganzen waren hundert Granatäpfel rings um das Flechtwerk." },
      { number: 24, text: "Der Kommandant der Leibwache nahm ferner den Oberpriester Seraja, den zweiten Priester Zefanja und die drei Schwellenwächter mit." },
      { number: 25, text: "Aus der Stadt nahm er einen Hofbeamten, der Kommandant der Soldaten war, und sieben Leute vom persönlichen Dienst des Königs mit, die sich noch in der Stadt befanden, sowie den Schreiber des Heerführers, der die Bürger des Landes auszuheben hatte, schließlich sechzig Mann von der Bürgerschaft, die sich noch in der Stadt befanden." },
      { number: 26, text: "Nebusaradan, der Kommandant der Leibwache, nahm sie fest und schickte sie zum König von Babel nach Ribla." },
      { number: 27, text: "Der König von Babel ließ sie in Ribla in der Landschaft Hamat hinrichten. So wurde Juda von seiner Heimat weggeführt." },
      { number: 28, text: "Das ist die Anzahl der Leute, die Nebukadnezzar wegführen ließ: in seinem siebten Regierungsjahr 3023 Judäer," },
      { number: 29, text: "im achtzehnten Jahr Nebukadnezzars 832 Personen aus Jerusalem;" },
      { number: 30, text: "im dreiundzwanzigsten Jahr Nebukadnezzars führte Nebusaradan, der Kommandant der Leibwache, an Judäern 745 Personen weg; im Ganzen waren es 4600 Personen." },
      { number: 31, text: "Im siebenunddreißigsten Jahr nach der Wegführung Jojachins, des Königs von Juda, am fünfundzwanzigsten Tag des zwölften Monats, begnadigte Ewil-Merodach, der König von Babel, im Jahr seines Regierungsantritts Jojachin, den König von Juda, und entließ ihn aus dem Kerker.", heading: "Ausblick: die Begnadigung Jojachins: 52,31-34", footnotes: ["(31-34) 2 Kön 25,27-30"] },
      { number: 32, text: "Er söhnte sich mit ihm aus und wies ihm seinen Sitz oberhalb des Sitzes der anderen Könige an, die bei ihm in Babel waren." },
      { number: 33, text: "Er durfte seine Gefängniskleidung ablegen und ständig bei ihm speisen, solange er lebte." },
      { number: 34, text: "Sein Unterhalt - ein dauernder Unterhalt - wurde ihm bis zu seinem Todestag vom König von Babel in der bestimmten Menge täglich geliefert, solange er lebte." },
    ] },
  ],
};
