import { Book } from "@/lib/types";

export const joshua: Book = {
  id: "joshua",
  name: "Josua",
  shortName: "Jos",
  testament: "old",
  introduction: "Die Bücher der Geschichte des Volkes Gottes Dieses Buch ist nach Josua, dem Nachfolger des Mose, benannt. Es berichtet vom Einzug Israels in das verheißene Land und umfasst den Zeitraum vom Tod des Mose bis zum Tod Josuas. In dieser Zeit wird nach der Darstellung des Buches das von Gott verheißene Land westlich des Jordan erobert und durch Josua an die Stämme Israels verteilt, soweit sie nicht schon im Ostjordanland (Ruben, Gad und der halbe Stamm Manasse) Landbesitz erhalten haben (vgl. Num 32). Der Bericht über die feierliche Verpflichtung Israels auf Jahwe, seinen Gott, schließt das Buch ab. Dementsprechend besteht das Buch aus drei Teilen: Eroberung des Westjordanlandes unter Führung Josuas (Kap. 1 12); Verteilung des Landes (Kap. 13 - 22); Verpflichtung auf Jahwe (Kap. 23 und 24). Der erste Teil enthält Erzählungen über die Landnahme, die als rein kriegerisches Unternehmen gesehen wird (doch vgl. Ri 1). Die meisten dieser Erzählungen, ausgenommen der Bericht über die Schlacht bei dem im Norden Palästinas gelegenen Merom (11,1-15), spielen in Mittelpalästina. Dort gewann der Stamm Benjamin, dem Josua angehörte, sein Gebiet. Josua wird zur beherrschenden Gestalt der ganzen Darstellung der Eroberung des Landes: Nach seiner Beauftragung (Kap. 1) wird Jericho erkundet (Kap. 2) und der Jordan überschritten (Kap. 3 und 4). Israel lagert in Gilgal (Kap. 5) und erobert von dort aus durch göttliche Hilfe Jericho (Kap. 6). Nach einem Fehlschlag, der durch einen Diebstahl Achans an dem Jahwe geweihten Beutegut verursacht war, wird Ai erobert (Kap. 7 und 8). Die Gibeoniter erlisten sich einen Vertrag mit Israel (Kap. 9); in der Nähe ihrer Stadt werden fünf Könige der Kanaaniter besiegt (Kap. 10). Über Eroberungen im Süden und Norden wird nur zusammenfassend berichtet (10,28 - 11,23). Eine Liste der in Kanaan besiegten Könige beschließt die Darstellung der Landnahme (Kap. 12). Die Erzählungen des Buches Josua sind von recht verschiedener Art: Neben Kampfberichten stehen listenartige Angaben, Anweisungen und Berichte über kultische Begehungen, Erzählungen über Auseinandersetzungen in Israel selbst und mit den Bewohnern des Landes. Einige dieser Erzählungen, darunter Kap. 22, »erklären« auffallende Gegebenheiten: den Steinkreis bei Gilgal, die zwölf Steine im Jordan, die Ortsnamen »Hügel der Vorhäute« und Gilgal, den Steinhaufen im Achortal bei Jericho, den Ruinenhügel bei Ai, das Vertragsverhältnis mit Gibeon, den Altar im Ostjordanland. Man bezeichnet sie deshalb als Ätiologien (Ursprungserzählungen). Der zweite Teil enthält die Angaben über die Verteilung des Landes. Nach dem Befehl Gottes (13,1-7) werden das Ostjordanland (13,15-33) und das Westjordanland an die Stämme verteilt (Kap. 14 19). Asyl- und Levitenstädte werden eingerichtet (Kap. 20 und 21). Der Streit um einen Altar östlich des Jordan wird beigelegt (Kap. 22). In der Gebiets- und Grenzbeschreibung der einzelnen Stämme sind vermutlich alte Listen, vor allem über die verwaltungsmäßige Einteilung Judas, verwendet. Der dritte Teil des Buches enthält zwei Abschiedsreden, in denen Josua Israel zum Gehorsam gegenüber Gott verpflichtet. Diese Verpflichtung übernimmt Israel auf dem »Landtag« zu Sichem (Kap. 23 und 24). Das Buch Josua, das zum Deuteronomistischen Geschichtswerk gehört (vgl. die Einleitung zum Buch Deuteronomium), will bezeugen, dass Gott die Landverheißung eingelöst hat, und es mahnt Israel, seinem Bund mit Gott treu zu bleiben.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Nachdem Mose, der Knecht des Herrn, gestorben war, sagte der Herr zu Josua, dem Sohn Nuns, dem Diener des Mose:", heading: "Die Eroberung des Landes: 1,1 - 12,24 | Der Befehl zur Besetzung des Westjordanlandes: 1,1-18", footnotes: ["Dtn 34,5"] },
      { number: 2, text: "Mein Knecht Mose ist gestorben. Mach dich also auf den Weg und zieh über den Jordan hier mit diesem ganzen Volk in das Land, das ich ihnen, den Israeliten, geben werde." },
      { number: 3, text: "Jeden Ort, den euer Fuß betreten wird, gebe ich euch, wie ich es Mose versprochen habe.", footnotes: ["Dtn 11,24"] },
      { number: 4, text: "Euer Gebiet soll von der Steppe und vom Libanon an bis zum großen Strom, zum Eufrat, reichen - das ist das ganze Land der Hetiter - und bis hin zum großen Meer, wo die Sonne untergeht.", footnotes: ["Umschreibung des verheißenen Landes in seiner idealen größten Ausdehnung. Syrien-Palästina ist nach den assyrischen Königsinschriften das Hetiterland."] },
      { number: 5, text: "Niemand wird dir Widerstand leisten können, solange du lebst. Wie ich mit Mose war, will ich auch mit dir sein. Ich lasse dich nicht fallen und verlasse dich nicht.", footnotes: ["Dtn 31,6.8"] },
      { number: 6, text: "Sei mutig und stark! Denn du sollst diesem Volk das Land zum Besitz geben, von dem du weißt: Ich habe ihren Vätern geschworen, es ihnen zu geben.", footnotes: ["Dtn 3,28", "Vgl. die Anmerkung zu Dtn 1,8."] },
      { number: 7, text: "Sei nur mutig und stark und achte genau darauf, dass du ganz nach der Weisung handelst, die mein Knecht Mose dir gegeben hat. Weich nicht nach rechts und nicht nach links davon ab, damit du Erfolg hast in allem, was du unternimmst.", footnotes: ["Dtn 5,32; 29,8"] },
      { number: 8, text: "Über dieses Gesetzbuch sollst du immer reden und Tag und Nacht darüber nachsinnen, damit du darauf achtest, genau so zu handeln, wie darin geschrieben steht. Dann wirst du auf deinem Weg Glück und Erfolg haben." },
      { number: 9, text: "Habe ich dir nicht befohlen: Sei mutig und stark? Fürchte dich also nicht und hab keine Angst; denn der Herr, dein Gott, ist mit dir bei allem, was du unternimmst." },
      { number: 10, text: "Da befahl Josua den Listenführern im Volk:" },
      { number: 11, text: "Geht durch das Lager und befehlt den Leuten: Versorgt euch mit Lebensmitteln; denn in drei Tagen werdet ihr den Jordan hier überschreiten, um in das Land hineinzuziehen und es in Besitz zu nehmen, das der Herr, euer Gott, euch zu eigen gibt.", footnotes: ["Dtn 3,12-29"] },
      { number: 12, text: "Und den Rubenitern, den Gaditern und dem halben Stamm Manasse sagte Josua:", footnotes: ["(12-18) 4,12", "Diese Stämme hatten ihren Landanteil bereits im Ostjordanland erhalten (vgl. Num 32)."] },
      { number: 13, text: "Erinnert euch an das, was Mose, der Knecht des Herrn, euch geboten hat: Der Herr, euer Gott, gewährt euch Ruhe und gibt euch dieses Land.", footnotes: ["Num 32,20-29"] },
      { number: 14, text: "Eure Frauen, eure Kinder und euer Vieh sollen in dem Land bleiben, das Mose euch östlich des Jordan gegeben hat. Ihr aber sollt mit euren Brüdern hinübergehen, ihnen kampfbereit mit allen Kriegern voranziehen und ihnen helfen,", footnotes: ["14f: östlich, wörtlich: jenseits; vom Standpunkt des Autors aus betrachtet, der in Palästina lebt."] },
      { number: 15, text: "bis der Herr euren Brüdern ebenso wie euch Ruhe gewährt und bis auch sie das Land in Besitz nehmen, das der Herr, euer Gott, ihnen gibt. Dann sollt ihr in euren eigenen Besitz zurückkehren, in das Land, das euch Mose, der Knecht des Herrn, östlich des Jordan gegen Sonnenaufgang als Besitz gegeben hat." },
      { number: 16, text: "Sie antworteten Josua: Alles, was du uns befohlen hast, wollen wir tun und dahin, wohin du uns schickst, werden wir gehen." },
      { number: 17, text: "Genauso, wie wir auf Mose gehört haben, wollen wir auch auf dich hören. Der Herr aber, dein Gott, möge mit dir sein, wie er mit Mose gewesen ist." },
      { number: 18, text: "Jeder, der sich deinem Befehl widersetzt und nicht allen deinen Anordnungen gehorcht, soll mit dem Tod bestraft werden. Sei nur mutig und stark!" },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Josua, der Sohn Nuns, schickte von Schittim heimlich zwei Kundschafter aus und befahl ihnen: Geht, erkundet das Land, besonders die Stadt Jericho! Sie brachen auf und kamen zu dem Haus einer Dirne namens Rahab; dort wollten sie übernachten.", heading: "Die Kundschafter in Jericho: 2,1-24", footnotes: ["6,22", "Schittim lag am Ostrand der Jordansenke, Jericho gegenüber. Das Haus der Rahab scheint eine an der Stadtmauer gelegene (V. 15) Herberge zu sein. Rahab, im Stammbaum Jesu Mt 1,5 als Mutter des Boas genannt, wird Hebr 11,31 wegen ihres Glaubens, Jak 2,25 wegen ihrer guten Werke gerühmt."] },
      { number: 2, text: "Man meldete dem König von Jericho: Heute Nacht sind ein paar Männer hierher gekommen, Israeliten, um das Land auszukundschaften." },
      { number: 3, text: "Da schickte der König von Jericho Boten zu Rahab und ließ ihr sagen: Gib die Männer heraus, die bei dir in deinem Haus eingekehrt sind; denn sie sind gekommen, um das ganze Land auszukundschaften." },
      { number: 4, text: "Da nahm die Frau die beiden Männer und versteckte sie. (Zu den Boten aber) sagte sie: Ja, die Männer sind zu mir gekommen; doch ich wusste nicht, woher sie waren." },
      { number: 5, text: "Als das Stadttor bei Einbruch der Dunkelheit geschlossen werden sollte, sind die Männer weggegangen; ich weiß aber nicht, wohin sie gegangen sind. Lauft ihnen schnell nach, dann könnt ihr sie noch einholen." },
      { number: 6, text: "Sie hatte aber die Männer auf das flache Dach gebracht und unter den Flachsstängeln versteckt, die auf dem Dach aufgeschichtet waren." },
      { number: 7, text: "Inzwischen hatte man die Verfolgung der Männer aufgenommen, und zwar in Richtung Jordan, zu den Furten hin. Und man hatte das Stadttor geschlossen, nachdem die Verfolger hinausgegangen waren." },
      { number: 8, text: "Bevor die Männer sich niederlegten, stieg Rahab zu ihnen auf das Dach hinauf" },
      { number: 9, text: "und sagte zu ihnen: Ich weiß, dass der Herr euch das Land gegeben hat und dass uns Furcht vor euch befallen hat und alle Bewohner des Landes aus Angst vor euch vergehen." },
      { number: 10, text: "Denn wir haben gehört, wie der Herr das Wasser des Schilfmeers euretwegen austrocknen ließ, als ihr aus Ägypten ausgezogen seid. Wir haben auch gehört, was ihr mit Sihon und Og, den beiden Königen der Amoriter jenseits des Jordan, gemacht habt: Ihr habt sie dem Untergang geweiht.", footnotes: ["Ex 14,21; Num 21,21-26", "Dem Untergang weihen: eine alte Kriegssitte, die auf Gottes Befehl bei der Eroberung des Landes anzuwenden war (vgl. 6,18-24; 10,22-27 und die Anmerkung zu Dtn 2,34)."] },
      { number: 11, text: "Als wir das hörten, zerschmolz unser Herz und jedem stockte euretwegen der Atem; denn der Herr, euer Gott, ist Gott droben im Himmel und hier unten auf der Erde." },
      { number: 12, text: "Nun schwört mir beim Herrn, dass ihr der Familie meines Vaters euer Wohlwollen erweist, wie ich es euch erwiesen habe, und gebt mir ein sicheres Zeichen dafür,", footnotes: ["6,25"] },
      { number: 13, text: "dass ihr meinen Vater und meine Mutter, meine Brüder und meine Schwestern und alles, was ihnen gehört, am Leben lasst und dass ihr uns vor dem Tod bewahrt." },
      { number: 14, text: "Die Männer antworteten ihr: Wir bürgen mit unserem Leben für euch, wenn ihr nur unsere Sache nicht verratet. Wenn uns der Herr das Land gibt, werden wir dir unser Wohlwollen und unsere Treue zeigen.", footnotes: ["6,22"] },
      { number: 15, text: "Darauf ließ die Frau sie mit einem Seil durch das Fenster die Stadtmauer hinab; das Haus, in dem sie wohnte, war nämlich in die Stadtmauer eingebaut." },
      { number: 16, text: "Sie riet ihnen: Geht ins Gebirge, damit die Verfolger euch nicht finden; dort haltet euch drei Tage lang verborgen, bis die Verfolger zurückgekehrt sind; dann könnt ihr eures Weges gehen." },
      { number: 17, text: "Die Männer sagten zu ihr: Wir können uns nur unter folgender Bedingung an den Eid halten, den du uns hast schwören lassen:" },
      { number: 18, text: "Wenn wir in das Land eindringen, musst du diese geflochtene purpurrote Schnur an das Fenster binden, durch das du uns herabgelassen hast, und du musst deinen Vater, deine Mutter, deine Brüder und die ganze Familie deines Vaters bei dir in deinem Haus versammeln." },
      { number: 19, text: "Jeder aber, der aus der Tür deines Hauses heraustritt, ist selbst schuld, wenn sein Blut vergossen wird. Wir sind dann ohne Schuld. Doch bei jedem, der mit dir in deinem Haus bleibt, tragen wir die Schuld, wenn Hand an ihn gelegt wird.", footnotes: ["ist selbst schuld, wenn sein Blut vergossen wird: wörtlich: sein Blut ist auf seinem Haupt."] },
      { number: 20, text: "Auch wenn du unsere Sache verrätst, brauchen wir uns nicht an den Eid zu halten, den du uns hast schwören lassen." },
      { number: 21, text: "Sie antwortete: Es sei, wie ihr gesagt habt. Dann ließ sie die beiden gehen und band die purpurrote Schnur an das Fenster." },
      { number: 22, text: "Die Männer gingen also und kamen ins Gebirge; dort blieben sie drei Tage, bis die Verfolger (in die Stadt) zurückgekehrt waren. Die Verfolger hatten sie überall gesucht, aber nicht gefunden." },
      { number: 23, text: "Dann machten sich die beiden Männer auf den Rückweg. Sie stiegen vom Gebirge herab, überschritten den Jordan und kamen zu Josua, dem Sohn Nuns. Sie erzählten ihm alles, was sie erfahren hatten," },
      { number: 24, text: "und sagten: Der Herr hat uns das ganze Land ausgeliefert; alle Bewohner des Landes vergehen aus Angst vor uns." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Am frühen Morgen brach Josua mit allen Israeliten von Schittim auf. Als sie an den Jordan kamen, übernachteten sie dort, bevor sie ihn überschritten.", heading: "Der Übergang über den Jordan: 3,1-17" },
      { number: 2, text: "Nach drei Tagen gingen die Listenführer durch das Lager" },
      { number: 3, text: "und befahlen dem Volk: Wenn ihr die Bundeslade des Herrn, eures Gottes, seht und die levitischen Priester, die sie tragen, dann sollt auch ihr von dort, wo ihr gerade seid, aufbrechen und ihr folgen;" },
      { number: 4, text: "nur müsst ihr zwischen ihr und euch einen Abstand von zweitausend Ellen lassen, ihr dürft nicht zu nahe an sie herankommen. So werdet ihr wissen, welchen Weg ihr gehen sollt; denn ihr seid den Weg ja früher noch nie gegangen." },
      { number: 5, text: "Und Josua sagte zum Volk: Heiligt euch; denn morgen wird der Herr mitten unter euch Wunder tun.", footnotes: ["Wie für eine kultische Handlung muss sich das Volk durch sexuelle Enthaltung und Reinigen der Kleider bereit machen."] },
      { number: 6, text: "Und zu den Priestern sagte Josua: Nehmt die Bundeslade auf und zieht dem Volk voran! Und sie nahmen die Bundeslade auf und gingen dem Volk voraus." },
      { number: 7, text: "Da sagte der Herr zu Josua: Heute fange ich an, dich vor den Augen ganz Israels groß zu machen, damit alle erkennen, dass ich mit dir sein werde, wie ich mit Mose gewesen bin.", footnotes: ["4,14; 1,5.17"] },
      { number: 8, text: "Du aber sollst den Priestern, die die Bundeslade tragen, befehlen: Wenn ihr zum Ufer des Jordan kommt, geht in den Jordan hinein und bleibt dort stehen!" },
      { number: 9, text: "Darauf sagte Josua zu den Israeliten: Kommt her und hört die Worte des Herrn, eures Gottes!" },
      { number: 10, text: "Dann sagte Josua: Daran sollt ihr erkennen, dass ein lebendiger Gott mitten unter euch ist: Er wird die Kanaaniter, die Hetiter, Hiwiter, Perisiter, Girgaschiter, Amoriter und Jebusiter vor euren Augen vertreiben." },
      { number: 11, text: "Seht, die Bundeslade des Herrn der ganzen Erde zieht vor euch her durch den Jordan." },
      { number: 12, text: "Wählt nun zwölf Männer aus den Stämmen Israels aus, aus jedem Stamm einen!", footnotes: ["Die Aufgabe der zwölf Männer wird in 4,2-5 mitgeteilt."] },
      { number: 13, text: "Sobald die Füße der Priester, die die Lade des Herrn tragen, des Herrn der ganzen Erde, im Wasser des Jordan stehen, wird das Wasser des Jordan, das von oben herabkommt, wie abgeschnitten sein und wie ein Wall dastehen.", footnotes: ["4,7"] },
      { number: 14, text: "Als dann das Volk seine Zelte verließ und aufbrach, um den Jordan zu überschreiten, gingen die Priester, die die Bundeslade trugen, an der Spitze des Volkes." },
      { number: 15, text: "Und als die Träger der Lade an den Jordan kamen und die Füße der Priester, die die Lade trugen, das Wasser berührten - der Jordan war aber während der ganzen Erntezeit über alle Ufer getreten -," },
      { number: 16, text: "da blieben die Fluten des Jordan stehen. Das von oben herabkommende Wasser stand wie ein Wall in weiter Entfernung, bei der Stadt Adam, die in der Nähe von Zaretan liegt. Die zum Meer der Araba, zum Salzmeer, hinabfließenden Fluten dagegen liefen vollständig ab und das Volk zog Jericho gegenüber durch den Jordan.", footnotes: ["Ex 14,21; Ps 114,3", "Die Stadt Adam sucht man in der Nähe der Einmündung des Jabbok in den Jordan. Das «Meer der Araba» ist das Tote Meer."] },
      { number: 17, text: "Die Priester, die die Bundeslade des Herrn trugen, standen, während ganz Israel trockenen Fußes hindurchzog, fest und sicher mitten im Jordan auf trockenem Boden, bis das ganze Volk den Jordan durchschritten hatte." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Als das ganze Volk den Jordan durchschritten hatte, sagte der Herr zu Josua:", heading: "Die zwölf Gedenksteine: 4,1 - 5,1", footnotes: ["1-9.20-24: Hier werden Herkunft und Bedeutung der zwölf Steine im Jordan bzw. in Gilgal erklärt."] },
      { number: 2, text: "Wählt aus dem Volk zwölf Männer aus, aus jedem Stamm einen," },
      { number: 3, text: "und befehlt ihnen: Hebt hier, an der Stelle mitten im Jordan, wo die Priester fest und sicher standen, zwölf Steine auf, nehmt sie mit hinüber und legt sie an dem Ort nieder, an dem ihr die nächste Nacht verbringt." },
      { number: 4, text: "Da rief Josua die zwölf Männer, die er aus den Israeliten bestimmt hatte, aus jedem Stamm einen," },
      { number: 5, text: "und sagte zu ihnen: Geht vor der Lade des Herrn, eures Gottes, her bis zur Mitte des Jordan; dort soll jeder von euch einen Stein auf seine Schulter heben, so viele Steine, wie es Stämme der Israeliten gibt." },
      { number: 6, text: "Sie sollen unter euch ein Zeichen sein. Wenn euch eure Söhne morgen fragen: Was bedeuten diese Steine für euch?," },
      { number: 7, text: "dann antwortet ihnen: (Sie bedeuten,) dass die Fluten des Jordan vor der Bundeslade des Herrn wie abgeschnitten waren; als sie durch den Jordan zog, waren die Fluten des Jordan wie abgeschnitten. So sind diese Steine ein ewiges Erinnerungszeichen für die Israeliten." },
      { number: 8, text: "Die Israeliten taten, was Josua befohlen hatte, und nahmen zwölf Steine mitten aus dem Jordan, so viele, wie es Stämme der Israeliten gab, wie es der Herr dem Josua befohlen hatte. Sie nahmen sie mit hinüber zu ihrem Rastplatz und stellten sie dort auf." },
      { number: 9, text: "Und in der Mitte des Jordan richtete Josua zwölf Steine auf, dort, wo die Priester gestanden hatten, die die Bundeslade trugen; sie stehen dort bis zum heutigen Tag." },
      { number: 10, text: "Die Priester aber, die die Lade trugen, blieben mitten im Jordan stehen, bis alles geschehen war, was der Herr durch Josua dem Volk befohlen hatte [ganz so, wie Mose dem Josua befohlen hatte], und das Volk beeilte sich und zog hinüber." },
      { number: 11, text: "Als das ganze Volk vollständig hinübergezogen war, zog auch die Lade des Herrn hinüber und die Priester (gingen wieder) dem Volk voraus." },
      { number: 12, text: "Die Rubeniter, die Gaditer und der halbe Stamm Manasse zogen kampfbereit vor den Israeliten her, wie es ihnen Mose befohlen hatte.", footnotes: ["1,12-16"] },
      { number: 13, text: "Es waren etwa vierzigtausend bewaffnete Männer, die vor den Augen des Herrn zum Kampf in die Steppen von Jericho zogen." },
      { number: 14, text: "An jenem Tag machte der Herr den Josua in den Augen ganz Israels groß und man hatte Ehrfurcht vor ihm, wie man vor Mose zeit seines Lebens Ehrfurcht hatte." },
      { number: 15, text: "Da sagte der Herr zu Josua:" },
      { number: 16, text: "Befiehl den Priestern, die die Lade der Bundesurkunde tragen, aus dem Jordan heraufzusteigen." },
      { number: 17, text: "Da befahl Josua den Priestern: Steigt aus dem Jordan herauf!" },
      { number: 18, text: "Als nun die Priester, die die Bundeslade des Herrn trugen, aus der Mitte des Jordan heraufstiegen und ihre Füße das Ufer berührten, da kehrte das Wasser des Jordan an seinen Ort zurück und trat wie zuvor wieder über alle Ufer." },
      { number: 19, text: "Das Volk zog am zehnten Tag des ersten Monats durch den Jordan und schlug in Gilgal, am Ostrand des Gebietes von Jericho, sein Lager auf." },
      { number: 20, text: "In Gilgal stellte Josua die zwölf Steine auf, die man aus dem Jordan mitgenommen hatte." },
      { number: 21, text: "Er sagte zu den Israeliten: Wenn eure Söhne morgen ihre Väter fragen: Was bedeuten diese Steine?," },
      { number: 22, text: "dann sollt ihr sie belehren: Hier hat Israel trockenen Fußes den Jordan durchschritten;" },
      { number: 23, text: "denn der Herr, euer Gott, hat das Wasser des Jordan vor euren Augen austrocknen lassen, bis ihr hindurchgezogen wart, genau so wie es Jahwe, euer Gott, mit dem Schilfmeer machte, das er vor unseren Augen austrocknen ließ, bis wir hindurchgezogen waren.", footnotes: ["Ex 14,21f"] },
      { number: 24, text: "Daran sollen alle Völker der Erde erkennen, dass die Hand des Herrn stark ist, und ihr sollt allezeit Jahwe, euren Gott, fürchten." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Alle Könige der Amoriter jenseits des Jordan im Westen und alle Könige der Kanaaniter am Meer hörten, dass der Herr das Wasser des Jordan vor den Augen der Israeliten austrocknen ließ, bis sie hinübergezogen waren; da zerschmolz ihr Herz und jedem stockte der Atem wegen der Israeliten." },
      { number: 2, text: "Damals sagte der Herr zu Josua: Mach dir Steinmesser und ordne wieder eine Beschneidung der Israeliten an, eine zweite!", heading: "Die Beschneidung in Gilgal: 5,2-12", footnotes: ["Gen 17,10-14", "Eine frühere Beschneidung ist hier vorausgesetzt, sie wird aber in den bekannten Texten nicht erwähnt."] },
      { number: 3, text: "Da machte sich Josua Steinmesser und er beschnitt die Israeliten auf dem «Hügel der Vorhäute»." },
      { number: 4, text: "Josua nahm die Beschneidung vor, weil das ganze Volk, das aus Ägypten ausgezogen war, das heißt die Männer, alle Krieger, nach ihrem Auszug aus Ägypten auf dem Weg durch die Wüste gestorben waren." },
      { number: 5, text: "Als das Volk auszog, waren alle beschnitten. Alle aber, die nach dem Auszug aus Ägypten unterwegs in der Wüste geboren wurden, hatte man nicht beschnitten." },
      { number: 6, text: "Denn vierzig Jahre lang wanderten die Israeliten durch die Wüste. Schließlich war das ganze Volk, alle Krieger, die aus Ägypten ausgezogen waren, umgekommen, weil sie nicht auf die Stimme des Herrn gehört hatten. Der Herr hatte ihnen geschworen, er werde sie das Land nicht schauen lassen, das er ihren Vätern mit einem Eid zugesichert hatte, ein Land, in dem Milch und Honig fließen.", footnotes: ["Num 14,22f"] },
      { number: 7, text: "Ihre Söhne, die der Herr an ihre Stelle treten ließ, beschnitt Josua; denn sie waren noch unbeschnitten, da man sie unterwegs nicht beschnitten hatte." },
      { number: 8, text: "Als nun das ganze Volk beschnitten war, blieb man an Ort und Stelle, im Lager, bis die Männer wieder gesund waren." },
      { number: 9, text: "Und der Herr sagte zu Josua: Heute habe ich die ägyptische Schande von euch abgewälzt. Darum nennt man diesen Ort bis zum heutigen Tag Gilgal (Wälzplatz).", footnotes: ["Der Name Gilgal bedeutet wohl «Steinkreis» (vgl. 4,19 f); er wird hier von galal (wälzen) abgeleitet."] },
      { number: 10, text: "Als die Israeliten in Gilgal ihr Lager hatten, feierten sie am Abend des vierzehnten Tages jenes Monats in den Steppen von Jericho das Pascha." },
      { number: 11, text: "Am Tag nach dem Pascha, genau an diesem Tag, aßen sie ungesäuerte Brote und geröstetes Getreide aus den Erträgen des Landes.", footnotes: ["Lev 23,10.14", "11f: Ungesäuerte Brote gehören nach Ex 12,18 zum Paschamahl; soeben gereiftes, wohl mit den Ähren geröstetes Getreide (vgl. Lev 2,14) ist die Erstlingsfrucht des Landes. Das Aufhören des Manna bezeichnet das Ende der Wüstenwanderung."] },
      { number: 12, text: "Vom folgenden Tag an, nachdem sie von den Erträgen des Landes gegessen hatten, blieb das Manna aus; von da an hatten die Israeliten kein Manna mehr, denn sie aßen in jenem Jahr von der Ernte des Landes Kanaan.", footnotes: ["Ex 16,35"] },
      { number: 13, text: "Als Josua bei Jericho war und Ausschau hielt, sah er plötzlich einen Mann mit einem gezückten Schwert in der Hand vor sich stehen. Josua ging auf ihn zu und fragte ihn: Gehörst du zu uns oder zu unseren Feinden?", heading: "Der Anführer des Heeres des Herrn: 5,13-15" },
      { number: 14, text: "Er antwortete: Nein, ich bin der Anführer des Heeres des Herrn. Ich bin soeben gekommen. Da warf sich Josua vor ihm zu Boden, um ihm zu huldigen, und fragte ihn: Was befiehlt mein Herr seinem Knecht?" },
      { number: 15, text: "Der Anführer des Heeres des Herrn antwortete Josua: Zieh deine Schuhe aus; denn der Ort, wo du stehst, ist heilig. Und Josua tat es.", footnotes: ["Ex 3,5"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Jericho hielt wegen der Israeliten die Tore fest verschlossen. Niemand konnte heraus und niemand konnte hinein.", heading: "Die Eroberung Jerichos: 6,1-27", footnotes: ["Jericho ist eine bis ins 7. Jahrtausend v. Chr. zurückreichende Stadt; die Eroberung durch Josua ist bisher archäologisch nicht nachgewiesen. Ihre Einnahme wird nach Art einer Prozession mit der Bundeslade geschildert."] },
      { number: 2, text: "Da sagte der Herr zu Josua: Sieh her, ich gebe Jericho und seinen König samt seinen Kriegern in deine Gewalt." },
      { number: 3, text: "Ihr sollt mit allen Kriegern um die Stadt herumziehen und sie einmal umkreisen. Das sollst du sechs Tage lang tun." },
      { number: 4, text: "Sieben Priester sollen sieben Widderhörner vor der Lade hertragen. Am siebten Tag sollt ihr siebenmal um die Stadt herumziehen und die Priester sollen die Hörner blasen." },
      { number: 5, text: "Wenn das Widderhorn geblasen wird und ihr den Hörnerschall hört, soll das ganze Volk in lautes Kriegsgeschrei ausbrechen. Darauf wird die Mauer der Stadt in sich zusammenstürzen; dann soll das Volk hinübersteigen, jeder an der nächstbesten Stelle." },
      { number: 6, text: "Da rief Josua, der Sohn Nuns, die Priester und sagte: Nehmt die Bundeslade und lasst sieben Priester sieben Widderhörner vor der Lade des Herrn hertragen." },
      { number: 7, text: "Und zum Volk sagte er: Zieht rings um die Stadt herum und lasst die bewaffneten Männer vor der Lade des Herrn herziehen!" },
      { number: 8, text: "Und es geschah so, wie Josua es dem Volk gesagt hatte: Sieben Priester trugen die sieben Widderhörner vor dem Herrn her und bliesen im Gehen die Hörner und die Bundeslade des Herrn zog hinter ihnen her." },
      { number: 9, text: "Die bewaffneten Männer gingen vor den Priestern her, die die Hörner bliesen, die Nachhut folgte der Lade und man blies ständig die Hörner." },
      { number: 10, text: "Dem Volk aber befahl Josua: Erhebt kein Kriegsgeschrei und lasst eure Stimmen nicht hören! Kein Wort komme aus eurem Mund bis zu dem Tag, an dem ich zu euch sage: Erhebt das Kriegsgeschrei! Dann sollt ihr losschreien." },
      { number: 11, text: "Darauf ließ er die Lade des Herrn um die Stadt herumziehen und sie einmal umkreisen. Dann kam man zum Lager zurück und übernachtete im Lager." },
      { number: 12, text: "Früh am anderen Morgen brach Josua auf und die Priester trugen die Lade des Herrn." },
      { number: 13, text: "Sieben Priester trugen die sieben Widderhörner der Lade des Herrn voraus und bliesen ständig die Hörner. Die bewaffneten Männer zogen vor ihnen her und die Nachhut folgte der Lade des Herrn. Man blies ständig die Hörner." },
      { number: 14, text: "So zogen sie auch am zweiten Tag einmal um die Stadt herum und kehrten wieder ins Lager zurück. Das machten sie sechs Tage lang." },
      { number: 15, text: "Am siebten Tag aber brachen sie beim Anbruch der Morgenröte auf und zogen, wie gewohnt, um die Stadt, siebenmal; nur an diesem Tag zogen sie siebenmal um die Stadt." },
      { number: 16, text: "Als die Priester beim siebten Mal die Hörner bliesen, sagte Josua zum Volk: Erhebt das Kriegsgeschrei! Denn der Herr hat die Stadt in eure Gewalt gegeben." },
      { number: 17, text: "Die Stadt mit allem, was in ihr ist, soll zu Ehren des Herrn dem Untergang geweiht sein. Nur die Dirne Rahab und alle, die bei ihr im Haus sind, sollen am Leben bleiben, weil sie die Boten versteckt hat, die wir ausgeschickt hatten." },
      { number: 18, text: "Hütet euch aber davor, von dem, was dem Untergang geweiht ist, etwas zu begehren und wegzunehmen; sonst weiht ihr das Lager Israels dem Untergang und stürzt es ins Unglück.", footnotes: ["Lev 27,28", "begehren: Text korr. nach G. - Von der Beute, die Gott geweiht war und vernichtet werden musste, durfte nichts weggenommen werden."] },
      { number: 19, text: "Alles Gold und Silber und die Geräte aus Bronze und Eisen sollen dem Herrn geweiht sein und in den Schatz des Herrn kommen." },
      { number: 20, text: "Darauf erhob das Volk das Kriegsgeschrei und die Widderhörner wurden geblasen. Als das Volk den Hörnerschall hörte, brach es in lautes Kriegsgeschrei aus. Die Stadtmauer stürzte in sich zusammen, und das Volk stieg in die Stadt hinein, jeder an der nächstbesten Stelle. So eroberten sie die Stadt.", footnotes: ["Hebr 11,30"] },
      { number: 21, text: "Mit scharfem Schwert weihten sie alles, was in der Stadt war, dem Untergang, Männer und Frauen, Kinder und Greise, Rinder, Schafe und Esel.", footnotes: ["8,2"] },
      { number: 22, text: "Zu den beiden Männern, die das Land erkundet hatten, sagte Josua: Geht zu dem Haus der Dirne und holt von dort die Frau und alles, was ihr gehört, wie ihr es ihr geschworen habt.", footnotes: ["2,14"] },
      { number: 23, text: "Da gingen die jungen Männer, die Kundschafter, und holten Rahab, ihren Vater, ihre Mutter, ihre Brüder und alles, was ihr gehörte; sie führten ihre ganze Verwandtschaft (aus der Stadt) heraus und wiesen ihnen einen Platz außerhalb des Lagers Israels an." },
      { number: 24, text: "Die Stadt aber und alles, was darin war, brannte man nieder; nur das Silber und Gold und die Geräte aus Bronze und Eisen brachte man in den Schatz im Haus des Herrn." },
      { number: 25, text: "Die Dirne Rahab und die Familie ihres Vaters und alles, was ihr gehörte, ließ Josua am Leben. So wohnt ihre Familie bis heute mitten in Israel; denn Rahab hatte die Boten versteckt, die Josua ausgesandt hatte, um Jericho auskundschaften zu lassen.", footnotes: ["2,13"] },
      { number: 26, text: "Damals schwor Josua: Verflucht beim Herrn sei der Mann, der es unternimmt, diese Stadt Jericho wieder aufzubauen. Seinen Erstgeborenen soll es ihn kosten, wenn er sie neu gründet, und seinen Jüngsten, wenn er ihre Tore wieder aufrichtet.", footnotes: ["Der Fluch deutet auf 1 Kön 16,34 hin."] },
      { number: 27, text: "Der Herr war mit Josua und sein Ruhm verbreitete sich im ganzen Land." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Die Israeliten aber veruntreuten etwas von dem, was dem Untergang geweiht war. Denn Achan, der Sohn Karmis, des Sohnes Sabdis, des Sohnes Serachs, vom Stamm Juda, nahm etwas von dem, was dem Untergang geweiht war. Da entbrannte der Zorn des Herrn über die Israeliten.", heading: "Der Kampf um Ai. Achans Diebstahl: 7,1-26" },
      { number: 2, text: "Josua schickte von Jericho aus einige Männer nach Ai, das bei Bet-Awen, östlich von Bet-El liegt, und sagte zu ihnen: Geht hinauf, und erkundet das Land! Die Männer gingen hinauf und erkundeten Ai.", footnotes: ["Auch die Zerstörung von Ai (Trümmerhaufen) ist wie die von Jericho archäologisch nicht nachgewiesen. - Bet-Awen (Haus des Unheils) ist wohl ein herabwürdigender Name für Bet-El (vgl. Hos 4,15; Am 5,5)."] },
      { number: 3, text: "Als sie zu Josua zurückkamen, sagten sie zu ihm: Es braucht nicht das ganze Volk hinaufzuziehen. Etwa zwei- oder dreitausend Mann sollen hinaufziehen; sie können Ai besiegen. Bemüh nicht das ganze Volk dorthin! Denn dort sind nur wenige Leute." },
      { number: 4, text: "So stiegen vom Volk etwa dreitausend Mann nach Ai hinauf; aber sie mussten vor den Männern von Ai die Flucht ergreifen." },
      { number: 5, text: "Die Männer von Ai erschlugen sechsunddreißig von ihnen; die andern verfolgten sie vom Stadttor bis zu den Steinbrüchen und schlugen sie an der Steige. Da zerschmolz das Herz des Volkes und wurde zu Wasser." },
      { number: 6, text: "Josua zerriss seine Kleider und warf sich zusammen mit den Ältesten vor der Lade des Herrn mit dem Gesicht zu Boden und blieb dort bis zum Abend liegen. Sie streuten sich Asche auf das Haupt", footnotes: ["Das Zerreißen der Kleider und das Bestreuen des Kopfes mit Staub oder Asche waren Zeichen der Trauer und des Schmerzes (vgl. 1 Sam 4,12; 2 Sam 1,2)."] },
      { number: 7, text: "und Josua sagte: Ach Herr, mein Gott! Warum hast du dieses Volk über den Jordan ziehen lassen? Um uns den Amoritern auszuliefern, damit sie uns vernichten? Hätten wir uns doch dazu entschlossen, jenseits des Jordan zu bleiben!" },
      { number: 8, text: "Ach Herr, was soll ich sagen, nachdem Israel seinen Feinden den Rücken gezeigt hat?" },
      { number: 9, text: "Das werden die Kanaaniter und alle Bewohner des Landes hören und sie werden uns umzingeln und unseren Namen im ganzen Land ausrotten. Was willst du dann für deinen großen Namen tun?" },
      { number: 10, text: "Da sagte der Herr zu Josua: Steh auf! Warum liegst du da, mit dem Gesicht zur Erde?" },
      { number: 11, text: "Israel hat gesündigt. Sie haben sich sogar gegen meinen Bund vergangen, den ich ihnen auferlegt habe. Sie haben etwas weggenommen von dem, was dem Untergang geweiht war; sie haben es gestohlen; sie haben es unterschlagen und zu ihren eigenen Sachen getan." },
      { number: 12, text: "Deshalb können die Israeliten dem Angriff ihrer Feinde nicht mehr standhalten und müssen ihren Feinden den Rücken zeigen; denn sie sind nun selbst dem Untergang geweiht. Ich werde nicht mehr mit euch sein, wenn ihr bei euch nicht alles, was dem Untergang geweiht ist, vernichtet." },
      { number: 13, text: "Auf! Heilige das Volk und sag: Heiligt euch für morgen! Denn so hat der Herr, der Gott Israels, gesprochen: Bei dir, Israel, ist etwas, was dem Untergang geweiht ist. Du kannst dem Angriff deiner Feinde nicht standhalten, solange ihr nicht alles, was dem Untergang geweiht ist, aus eurer Mitte entfernt habt." },
      { number: 14, text: "Morgen sollt ihr Stamm für Stamm antreten und der Stamm, den der Herr bezeichnet, soll Sippe für Sippe antreten und die Sippe, die der Herr dann bezeichnet, soll Großfamilie für Großfamilie antreten und die Großfamilie, die der Herr dann bezeichnet, soll Mann für Mann antreten." },
      { number: 15, text: "Bei wem man dann etwas findet, das dem Untergang geweiht ist, der soll mit allem, was er hat, verbrannt werden, weil er sich am Bund des Herrn vergangen und in Israel eine Schandtat verübt hat." },
      { number: 16, text: "Josua ließ also früh am Morgen Israel Stamm für Stamm antreten; da wurde der Stamm Juda bezeichnet.", footnotes: ["1 Sam 10,20"] },
      { number: 17, text: "Dann ließ er die Sippen Judas antreten; da wurde die Sippe der Serachiter bezeichnet. Dann ließ er die Sippe der Serachiter nach Großfamilien antreten; da wurde Sabdi bezeichnet.", footnotes: ["Großfamilien: Text korr. mit S und einigen H-Handschriften. - Zum Ausdruck Großfamilie vgl. die Anmerkung zu Num 1,2."] },
      { number: 18, text: "Dann ließ er dessen Familie Mann für Mann antreten; da wurde Achan, der Sohn Karmis, der Enkel Sabdis, des Sohnes Serachs, vom Stamm Juda bezeichnet." },
      { number: 19, text: "Josua sagte zu Achan: Mein Sohn, gib dem Herrn, dem Gott Israels, die Ehre und leg vor ihm ein Geständnis ab! Sag mir offen, was du getan hast, und verheimliche mir nichts!" },
      { number: 20, text: "Achan antwortete Josua: Es ist wahr, ich habe mich gegen den Herrn, den Gott Israels, versündigt. Das und das habe ich getan:" },
      { number: 21, text: "Ich sah unter der Beute einen schönen Mantel aus Schinar, außerdem zweihundert Schekel Silber und einen Goldbarren, der fünfzig Schekel wog. Ich wollte es haben und nahm es an mich. Es ist in meinem Zelt im Boden vergraben, das Silber an unterster Stelle." },
      { number: 22, text: "Josua schickte Boten und sie liefen zum Zelt, und wirklich: die Sachen waren im Zelt Achans vergraben, das Silber an unterster Stelle." },
      { number: 23, text: "Sie holten alles aus dem Zelt heraus, brachten es zu Josua und allen Israeliten und breiteten es vor dem Herrn aus." },
      { number: 24, text: "Da nahm Josua Achan, den Sohn Serachs, ebenso das Silber und den Mantel und den Goldbarren, seine Söhne und Töchter, seine Rinder, Esel und Schafe, sein Zelt und seine ganze Habe. Er und ganz Israel brachten alles ins Tal Achor." },
      { number: 25, text: "Dann sagte Josua: Womit du uns ins Unglück gestürzt hast, damit stürzt dich der Herr heute ins Unglück. Und ganz Israel steinigte Achan. [Sie verbrannten sie im Feuer und steinigten sie.]" },
      { number: 26, text: "Man errichtete über ihm einen großen Steinhaufen, der bis zum heutigen Tag zu sehen ist, und der Herr ließ ab von seinem glühenden Zorn. Deshalb nennt man diesen Ort bis heute Achortal (Unglückstal).", footnotes: ["Jes 65,10; Hos 2,17", "Achor klingt an «Achan» an. Mit der Erzählung wird der Name des Achortals gedeutet."] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Dann sagte der Herr zu Josua: Fürchte dich nicht und hab keine Angst! Nimm alle kriegstüchtigen Männer, brich auf und zieh nach Ai! Siehe, ich habe den König von Ai und sein Volk, seine Stadt und sein Land in deine Gewalt gegeben.", heading: "Die Vernichtung von Ai: 8,1-29" },
      { number: 2, text: "Du sollst es mit Ai und seinem König ebenso machen, wie du es mit Jericho und seinem König gemacht hast. Doch ihren Besitz und das Vieh in der Stadt dürft ihr erbeuten. Leg im Rücken der Stadt einen Hinterhalt!" },
      { number: 3, text: "Da brach Josua mit dem ganzen Heer auf und zog nach Ai. Er wählte dreißigtausend kampferprobte Männer aus und schickte sie bei Nacht weg" },
      { number: 4, text: "mit dem Befehl: Seht zu, dass ihr im Rücken der Stadt einen Hinterhalt legt, aber nicht zu weit entfernt von der Stadt. Dort haltet euch alle bereit!" },
      { number: 5, text: "Ich aber werde mit allen Leuten, die bei mir sind, gegen die Stadt vorrücken, und wenn man dann wie beim ersten Mal einen Ausfall macht, werden wir vor ihnen fliehen." },
      { number: 6, text: "Sie werden uns verfolgen, bis wir sie von der Stadt weggelockt haben, denn sie werden denken: Sie fliehen vor uns wie beim ersten Mal. Wenn wir vor ihnen geflohen sind,", footnotes: ["7,4"] },
      { number: 7, text: "dann könnt ihr aus dem Hinterhalt hervorbrechen und die Stadt besetzen. Der Herr, euer Gott, wird sie euch ausliefern." },
      { number: 8, text: "Wenn ihr dann die Stadt eingenommen habt, sollt ihr sie in Brand stecken. Handelt nach dem Wort des Herrn! Seht, ich habe es euch befohlen." },
      { number: 9, text: "Dann schickte sie Josua weg und sie legten sich westlich von Ai in den Hinterhalt, zwischen Bet-El und Ai. Josua aber blieb die Nacht über beim Volk." },
      { number: 10, text: "Früh am Morgen stand Josua auf, musterte das Volk und zog zusammen mit den Ältesten Israels an der Spitze des Volkes nach Ai hinauf." },
      { number: 11, text: "Das ganze Heer zog mit ihm hinauf und rückte bis in die Nähe der Stadt vor. Im Norden von Ai schlugen sie ihr Lager auf; zwischen ihnen und Ai lag ein Tal." },
      { number: 12, text: "Dann nahm er etwa fünftausend Mann und legte sie westlich der Stadt, zwischen Bet-El und Ai, in einen Hinterhalt.", footnotes: ["12f: V. 12 fehlt größtenteils, V. 13 ganz in G; wohl ein Nachtrag."] },
      { number: 13, text: "So stellten sie das Volk auf: Das Lager war im Norden der Stadt und der Hinterhalt westlich der Stadt. Josua aber verbrachte die Nacht unten im Tal." },
      { number: 14, text: "Als das der König von Ai sah, brach er mit den Männern der Stadt am Morgen in aller Eile auf und zog mit seinem ganzen Volk zum (sogenannten) Versammlungsplatz vor der Steppe, um gegen Israel zu kämpfen. Er wusste nicht, dass man ihm im Rücken der Stadt einen Hinterhalt gelegt hatte." },
      { number: 15, text: "Josua und die Israeliten ließen sich von ihnen schlagen und flohen zur Wüste hin." },
      { number: 16, text: "Nun wurde das ganze Volk, das noch in der Stadt war, zu ihrer Verfolgung aufgeboten. Sie verfolgten Josua und wurden so von der Stadt weggelockt." },
      { number: 17, text: "Niemand blieb in Ai [und Bet-El] zurück; alle rückten aus, ließen die Stadt ohne Schutz zurück und verfolgten die Israeliten." },
      { number: 18, text: "Da sagte der Herr zu Josua: Streck das Sichelschwert in deiner Hand gegen Ai aus, denn ich gebe es in deine Gewalt. Josua streckte das Sichelschwert in seiner Hand gegen Ai aus." },
      { number: 19, text: "Als er seine Hand ausstreckte, brachen die Männer im Hinterhalt eiligst aus ihrer Stellung hervor, stürmten in die Stadt, nahmen sie ein und steckten sie sogleich in Brand." },
      { number: 20, text: "Als die Männer von Ai sich umwandten, sahen sie plötzlich aus der Stadt Rauch zum Himmel aufsteigen. Aber sie konnten weder vor noch zurück und hatten keine Möglichkeit zur Flucht. Denn das Volk, das zur Wüste hin geflohen war, wandte sich nun gegen den Verfolger." },
      { number: 21, text: "Als nämlich Josua und ganz Israel sahen, dass aus der Stadt Rauch aufstieg, dass also die Männer aus dem Hinterhalt die Stadt eingenommen hatten, machten sie kehrt und schlugen die Männer von Ai." },
      { number: 22, text: "Denn weil diese ihnen aus der Stadt nachgerückt waren, waren sie nun von beiden Seiten her mitten zwischen die Israeliten geraten. Die Israeliten schlugen sie so vernichtend, dass keiner von ihnen mehr übrig blieb, der hätte entkommen und sich in Sicherheit bringen können." },
      { number: 23, text: "Den König von Ai aber nahmen sie lebend gefangen und brachten ihn zu Josua." },
      { number: 24, text: "Als die Israeliten sämtliche Bewohner von Ai, die ihnen nachgejagt waren, ohne Ausnahme auf freiem Feld und in der Wüste mit scharfem Schwert getötet hatten und alle gefallen waren, kehrte ganz Israel nach Ai zurück und machte auch dort alles mit scharfem Schwert nieder." },
      { number: 25, text: "Es gab an jenem Tag insgesamt zwölftausend Gefallene, Männer und Frauen, alle Einwohner von Ai.", footnotes: ["Einwohner: Text korr. nach G."] },
      { number: 26, text: "Josua aber ließ seine Hand mit dem Sichelschwert nicht sinken, bis er alle Einwohner von Ai dem Untergang geweiht hatte." },
      { number: 27, text: "Nur das Vieh und den Besitz der Stadt nahm Israel für sich als Beute, nach dem Befehl, den der Herr Josua gegeben hatte." },
      { number: 28, text: "Dann brannte Josua Ai nieder und machte es für immer zu einem Trümmerhaufen und zu einem öden Platz; das ist es geblieben bis zum heutigen Tag." },
      { number: 29, text: "Den König von Ai aber ließ er an einem Baum aufhängen; dort hing er bis zum Abend. Als die Sonne unterging, nahm man die Leiche auf Befehl Josuas von dem Baum ab und warf sie vor das Tor der Stadt. Man errichtete über ihr einen großen Steinhaufen, der noch heute da ist.", footnotes: ["Dtn 21,23"] },
      { number: 30, text: "Damals errichtete Josua auf dem Berg Ebal für den Herrn, den Gott Israels, einen Altar", heading: "Der Bau des Altars und die Verkündigung des Gesetzes: 8,30-35", footnotes: ["Dtn 27,2-8; Ex 20,25"] },
      { number: 31, text: "aus unbehauenen Steinen, die noch kein eisernes Werkzeug berührt hatte, so wie Mose, der Knecht des Herrn, es den Israeliten geboten hatte und wie es im Gesetzbuch des Mose geschrieben steht. Auf diesem Altar brachte man dem Herrn Brandopfer dar und schlachtete Heilsopfer." },
      { number: 32, text: "Und Josua brachte dort auf den Steinen eine Abschrift des Gesetzes des Mose an; er schrieb vor den Augen der Israeliten darauf.", footnotes: ["Ex 24,1-8"] },
      { number: 33, text: "Ganz Israel und die Ältesten, die Listenführer und die Richter, standen zu beiden Seiten der Lade neben den levitischen Priestern, den Trägern der Bundeslade des Herrn, dazu Fremde und Einheimische. Die eine Hälfte von ihnen war dem Berg Garizim zugewandt, die andere dem Berg Ebal, wie es Mose, der Knecht Jahwes, für die Segnung des Volkes Israel früher angeordnet hatte.", footnotes: ["Dtn 11,29; 27,12f"] },
      { number: 34, text: "Danach verlas Josua das Gesetz im vollen Wortlaut, Segen und Fluch, genau so, wie es im Buch des Gesetzes aufgezeichnet ist." },
      { number: 35, text: "Von all dem, was Mose angeordnet hatte, gab es kein einziges Wort, das Josua nicht vor der ganzen Versammlung Israels verlesen hätte; auch die Frauen und Kinder und die Fremden, die mit ihnen zogen, waren dabei." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Davon hörten alle Könige jenseits des Jordan, auf dem Gebirge, in der Schefela und an der ganzen Küste des großen Meeres bis hin zum Libanon: die Hetiter, die Amoriter, die Kanaaniter, die Perisiter, die Hiwiter und die Jebusiter.", heading: "Die List der Gibeoniter: 9,1-27", footnotes: ["Gibeon, nördlich von Jerusalem, blieb wie die in V. 17 genannten Städte lange Zeit kanaanäisch."] },
      { number: 2, text: "Sie taten sich zusammen, um gemeinsam gegen Josua und Israel den Kampf aufzunehmen." },
      { number: 3, text: "Als die Einwohner von Gibeon erfuhren, was Josua mit Jericho und Ai gemacht hatte,", footnotes: ["6,20f; 8,26.29"] },
      { number: 4, text: "griffen sie zu einer List. Sie versahen sich mit Verpflegung, packten alte Säcke und alte, brüchige und geflickte Weinschläuche auf ihre Esel und machten sich damit auf den Weg." },
      { number: 5, text: "Sie zogen alte, geflickte Schuhe und alte Mäntel an. Das ganze Brot in ihrem Vorrat war trocken und krümelig." },
      { number: 6, text: "So zogen sie zu Josua ins Lager nach Gilgal und sagten zu ihm und zu den Israeliten: Wir kommen aus einem fernen Land. Schließt doch einen Vertrag mit uns!" },
      { number: 7, text: "Da antworteten die Israeliten den Hiwitern: Vielleicht wohnt ihr mitten in unserem Gebiet. Wie können wir da einen Vertrag mit euch schließen?", footnotes: ["7-9: Weit entfernte Völker konnten milder behandelt werden (Dtn 20,10-15) als die Völker Kanaans (Dtn 7,1-11; 20,16-18), mit denen Israel kein Abkommen schließen durfte."] },
      { number: 8, text: "Sie aber sagten zu Josua: Wir sind deine Knechte. Josua fragte sie: Wer seid ihr und woher kommt ihr?" },
      { number: 9, text: "Sie antworteten ihm: Deine Knechte kommen aus einem weit entfernten Land, angezogen vom Ruhm des Herrn, deines Gottes. Wir haben von seinem Ruhm und von allem gehört, was er in Ägypten getan hat," },
      { number: 10, text: "und auch von allem, was er mit den beiden Königen der Amoriter jenseits des Jordan gemacht hat, mit Sihon, dem König von Heschbon, und mit Og, dem König des Baschan in Aschtarot.", footnotes: ["12,2.4; Num 21,21-35"] },
      { number: 11, text: "Da sagten unsere Ältesten und alle Bewohner des Landes zu uns: Nehmt Verpflegung für unterwegs mit, zieht ihnen entgegen und sagt zu ihnen: Wir sind eure Knechte. Nun schließt mit uns einen Vertrag!" },
      { number: 12, text: "Unser Brot hier war noch warm, als wir es aus unseren Häusern als Vorrat mitnahmen an dem Tag, als wir uns auf den Weg zu euch machten. Jetzt aber, seht her: Es ist trocken und krümelig." },
      { number: 13, text: "Auch diese Weinschläuche waren neu, als wir sie füllten; und jetzt, seht her: Sie sind brüchig. Und hier, unsere Mäntel und unsere Schuhe, sie sind durch den weiten Weg ganz abgenützt." },
      { number: 14, text: "Da nahmen die Israeliten etwas von der Verpflegung; aber den Mund des Herrn befragten sie nicht.", footnotes: ["Mund des Herrn: Gemeint ist eine Entscheidung, vermittelt vielleicht durch einen Seher oder Propheten (vgl. Jer 15,19)."] },
      { number: 15, text: "So gewährte ihnen Josua Frieden und schloss mit ihnen einen Vertrag, sie am Leben zu lassen. Auch die Vorsteher der Gemeinde leisteten ihnen einen Eid.", footnotes: ["11,19"] },
      { number: 16, text: "Drei Tage aber, nachdem sie mit ihnen den Vertrag geschlossen hatten, erfuhren sie, dass die Männer aus der Nähe waren und mitten in ihrem Gebiet wohnten." },
      { number: 17, text: "Die Israeliten brachen auf und kamen am dritten Tag zu ihren Städten: nach Gibeon, Kefira, Beerot und Kirjat-Jearim." },
      { number: 18, text: "Aber die Israeliten erschlugen die Einwohner nicht, weil die Vorsteher der Gemeinde ihnen beim Herrn, dem Gott Israels, einen Eid geleistet hatten. Doch die ganze Gemeinde war empört über die Vorsteher." },
      { number: 19, text: "Da sagten alle Vorsteher zu der Gemeinde: Wir haben ihnen beim Herrn, dem Gott Israels, einen Eid geleistet. Darum können wir ihnen nichts tun." },
      { number: 20, text: "Wir wollen es so mit ihnen machen: Wir werden sie am Leben lassen, damit nicht wegen des Eides, den wir ihnen geschworen haben, ein Zorngericht über uns kommt." },
      { number: 21, text: "Die Vorsteher sagten also zu ihnen: Sie sollen am Leben bleiben. So wurden sie Holzfäller und Wasserträger für die ganze Gemeinde, wie es ihnen die Vorsteher sagten.", footnotes: ["Holzfällen und Wassertragen waren Sklavendienste. Nach VV. 23.27 waren Gibeoniter Tempelsklaven."] },
      { number: 22, text: "Darauf rief Josua die Gibeoniter und sagte zu ihnen: Warum habt ihr uns getäuscht und gesagt: Wir wohnen sehr weit entfernt von euch, obwohl ihr mitten in unserem Gebiet wohnt?" },
      { number: 23, text: "Nun seid ihr verflucht; ihr müsst für immer Sklaven, Holzfäller und Wasserträger für das Haus meines Gottes sein." },
      { number: 24, text: "Sie antworteten Josua: Deinen Knechten wurde genau berichtet, was Jahwe, dein Gott, seinem Knecht Mose befohlen hat: euch das ganze Land zu geben und alle Bewohner des Landes vor euren Augen zu vernichten. Darum hatten wir große Angst vor euch und fürchteten um unser Leben. Deshalb haben wir das getan." },
      { number: 25, text: "Jetzt sind wir in deiner Hand. Mach mit uns, was dir gut und recht erscheint." },
      { number: 26, text: "Daraufhin tat er Folgendes: Er rettete sie zwar aus der Gewalt der Israeliten, sodass sie sie nicht töteten;" },
      { number: 27, text: "doch er machte sie an jenem Tag zu Holzfällern und Wasserträgern für die Gemeinde und für den Altar des Herrn an dem Ort, den er erwählen würde. Das sind sie bis zum heutigen Tag geblieben." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Adoni-Zedek, der König von Jerusalem, hörte, dass Josua Ai erobert und dem Untergang geweiht hatte - wie er es mit Jericho und seinem König gemacht hatte, so hatte er es auch mit Ai und seinem König gemacht - und dass die Einwohner von Gibeon mit den Israeliten Frieden geschlossen hatten und weiterhin mitten unter ihnen leben durften.", heading: "Der Krieg mit den fünf Kanaaniter-Königen: 10,1-27", footnotes: ["6; 8; 9", "Jerusalem wurde erst durch David erobert (vgl. 2 Sam 5,6-9)."] },
      { number: 2, text: "Da bekam er große Angst; denn die Stadt Gibeon war ebenso groß wie die Städte mit Königssitz; sie war größer als Ai und alle ihre Männer waren kampferprobte Krieger.", footnotes: ["er: Text korr. nach S und Vg."] },
      { number: 3, text: "Darum sandte Adoni-Zedek, der König von Jerusalem, Boten an Hoham, den König von Hebron, an Piram, den König von Jarmut, an Jafia, den König von Lachisch, und an Debir, den König von Eglon, und ließ ihnen sagen:", footnotes: ["Städte südlich und südwestlich von Jerusalem."] },
      { number: 4, text: "Zieht herauf zu mir und helft mir! Wir wollen Gibeon schlagen, weil es mit Josua und den Israeliten Frieden geschlossen hat." },
      { number: 5, text: "Da sammelten sich die fünf Könige der Amoriter und zogen mit ihren ganzen Heeren hinauf: der König von Jerusalem, der König von Hebron, der König von Jarmut, der König von Lachisch und der König von Eglon. Sie belagerten Gibeon und eröffneten den Kampf gegen die Stadt." },
      { number: 6, text: "Darauf sandten die Männer von Gibeon (Boten) zu Josua ins Lager nach Gilgal und ließen ihm sagen: Lass deine Knechte nicht im Stich! Zieh schnell herauf zu uns, rette uns und komm uns zu Hilfe; denn alle Amoriterkönige, die das Bergland bewohnen, haben sich gegen uns zusammengetan." },
      { number: 7, text: "Da zog Josua mit dem ganzen Heer, mit allen kampferprobten Männern, von Gilgal herauf." },
      { number: 8, text: "Der Herr sagte zu Josua: Fürchte dich nicht vor ihnen; denn ich gebe sie in deine Gewalt. Keiner von ihnen kann dir standhalten." },
      { number: 9, text: "Nachdem Josua die ganze Nacht von Gilgal her auf dem Marsch gewesen war, stieß er plötzlich auf sie." },
      { number: 10, text: "Der Herr versetzte sie beim Anblick der Israeliten in Verwirrung, sodass Josua ihnen bei Gibeon eine schwere Niederlage beibringen konnte. Er verfolgte sie in Richtung auf die Steige von Bet-Horon und schlug sie bis nach Aseka und Makkeda zurück.", footnotes: ["Ex 23,27; Ri 4,15; 1 Sam 7,10"] },
      { number: 11, text: "Als sie auf der Flucht vor Israel an den Abhang von Bet-Horon kamen, warf der Herr große Steine auf sie vom Himmel her, bis nach Aseka hin, sodass viele umkamen. Es kamen mehr durch die Hagelsteine um, als die Israeliten mit dem Schwert töteten." },
      { number: 12, text: "Damals, als der Herr die Amoriter den Israeliten preisgab, redete Josua mit dem Herrn; dann sagte er in Gegenwart der Israeliten: Sonne, bleib stehen über Gibeon / und du, Mond, über dem Tal von Ajalon! -", footnotes: ["Hab 3,11", "12f: Der Sieg, in dem Gott durch ein schweres Unwetter Israel half, wird mit einem Stück aus einem uns nicht bekannten Liederbuch gefeiert und dichterisch ausgemalt."] },
      { number: 13, text: "Und die Sonne blieb stehen / und der Mond stand still, / bis das Volk an seinen Feinden Rache genommen hatte. Das steht im «Buch des Aufrechten». Die Sonne blieb also mitten am Himmel stehen und ihr Untergang verzögerte sich, ungefähr einen ganzen Tag lang.", footnotes: ["2 Sam 1,18"] },
      { number: 14, text: "Weder vorher noch nachher hat es je einen solchen Tag gegeben, an dem der Herr auf die Stimme eines Menschen gehört hätte; der Herr kämpfte nämlich für Israel." },
      { number: 15, text: "Danach kehrte Josua und mit ihm ganz Israel in das Lager nach Gilgal zurück." },
      { number: 16, text: "Die fünf Könige aber waren geflohen und hatten sich in der Höhle bei Makkeda versteckt." },
      { number: 17, text: "Da meldete man Josua: Wir haben die fünf Könige gefunden; sie haben sich in der Höhle bei Makkeda versteckt." },
      { number: 18, text: "Josua sagte: Wälzt große Steine vor den Eingang der Höhle und stellt einige Männer davor, um die Könige zu bewachen." },
      { number: 19, text: "Ihr (anderen) aber bleibt nicht stehen, sondern verfolgt die Feinde und macht auch noch die Nachhut nieder! Lasst sie nicht bis zu ihren Städten gelangen; denn der Herr, euer Gott, hat sie in eure Gewalt gegeben." },
      { number: 20, text: "So brachten Josua und die Israeliten ihnen endgültig eine schwere Niederlage bei, die sie völlig vernichtete; nur einige Flüchtlinge konnten entkommen und in die festen Städte gelangen." },
      { number: 21, text: "Darauf kehrte das ganze Heer wohlbehalten zu Josua nach Makkeda zurück und keiner wagte es mehr, die Israeliten zu bedrohen." },
      { number: 22, text: "Dann sagte Josua: Öffnet den Eingang der Höhle und bringt die fünf Könige aus der Höhle zu mir her!" },
      { number: 23, text: "Sie taten es und führten die fünf Könige aus der Höhle heraus: den König von Jerusalem, den König von Hebron, den König von Jarmut, den König von Lachisch und den König von Eglon." },
      { number: 24, text: "Als man die Könige zu Josua gebracht hatte, rief Josua alle Israeliten herbei und sagte zu den Anführern der Krieger, die mit ihm ausgezogen waren: Kommt her, setzt diesen Königen euren Fuß auf den Nacken! Sie kamen herbei und setzten ihnen den Fuß auf den Nacken." },
      { number: 25, text: "Und Josua sagte zu ihnen: Fürchtet euch nicht und habt keine Angst! Seid mutig und stark! Denn so wird es der Herr mit allen euren Feinden machen, mit denen ihr kämpfen werdet." },
      { number: 26, text: "Danach erschlug Josua die Könige und ließ die Leichen an fünf Bäumen aufhängen. Dort blieben sie bis zum Abend hängen.", footnotes: ["Dtn 21,23"] },
      { number: 27, text: "Bei Sonnenuntergang befahl Josua, sie von den Bäumen abzunehmen und in die Höhle zu werfen, in der sie sich zuvor versteckt hatten. Dann wälzte man große Steine vor den Eingang der Höhle; sie liegen dort bis zum heutigen Tag." },
      { number: 28, text: "Am gleichen Tag nahm Josua Makkeda ein und erschlug seine Einwohner und seinen König mit scharfem Schwert. Er weihte sie und alles, was in der Stadt lebte, dem Untergang; niemand ließ er entkommen. Mit dem König von Makkeda machte er es, wie er es mit dem König von Jericho gemacht hatte.", heading: "Die Eroberung von sechs Städten: 10,28-43" },
      { number: 29, text: "Dann zog Josua mit ganz Israel von Makkeda weiter nach Libna und kämpfte gegen Libna." },
      { number: 30, text: "Der Herr gab auch Libna und seinen König in die Gewalt Israels. Josua erschlug alles, was in ihm lebte, mit scharfem Schwert; niemand ließ er entkommen. Mit seinem König machte er es, wie er es mit dem König von Jericho gemacht hatte." },
      { number: 31, text: "Dann zog Josua mit ganz Israel von Libna weiter nach Lachisch, belagerte die Stadt und griff sie an." },
      { number: 32, text: "Und der Herr gab auch Lachisch in die Gewalt Israels. Josua nahm die Stadt am zweiten Tag ein. Er erschlug alles, was in ihr lebte, mit scharfem Schwert, genau so, wie er es mit Libna gemacht hatte." },
      { number: 33, text: "Als Horam, der König von Geser, heranzog, um Lachisch zu helfen, schlug Josua auch ihn und sein Volk und ließ niemand übrig, der hätte entkommen können." },
      { number: 34, text: "Dann zog Josua mit ganz Israel von Lachisch weiter nach Eglon. Sie belagerten die Stadt und griffen sie an." },
      { number: 35, text: "Noch am gleichen Tag nahmen sie die Stadt ein und erschlugen ihre Einwohner mit scharfem Schwert. Alles, was in ihr lebte, weihte man noch am gleichen Tag dem Untergang, genau so, wie man es mit Lachisch gemacht hatte." },
      { number: 36, text: "Von Eglon zog Josua mit ganz Israel nach Hebron hinauf und griff es an." },
      { number: 37, text: "Sie nahmen die Stadt ein und erschlugen [ihren König und] alles, was in ihr lebte, ebenso die Einwohner aller ihrer Tochterstädte, mit scharfem Schwert; niemand ließ er entkommen, genau so, wie er es mit Eglon gemacht hatte. Er weihte die Stadt und alles, was in ihr lebte, dem Untergang.", footnotes: ["Nach VV. 23-26 ist der König schon tot."] },
      { number: 38, text: "Dann wandte sich Josua mit ganz Israel gegen Debir und griff die Stadt an." },
      { number: 39, text: "Er eroberte die Stadt und ihre (Tochter)städte, nahm ihren König gefangen, erschlug ihre Einwohner mit scharfem Schwert und weihte alles, was in ihr lebte, dem Untergang; niemand ließ er entkommen. Wie er es mit Hebron und mit Libna und seinem König gemacht hatte, so machte er es auch mit Debir und seinem König." },
      { number: 40, text: "So schlug Josua das ganze Land - das Bergland und den Negeb, die Schefela und ihre Ausläufer - mit allen seinen Königen. Niemand ließ er entkommen; alles, was lebte, weihte er dem Untergang, wie es der Herr, der Gott Israels, befohlen hatte." },
      { number: 41, text: "Josua eroberte (das ganze Gebiet) von Kadesch-Barnea bis Gaza und ebenso das ganze Land Goschen und das Gebiet bis nach Gibeon." },
      { number: 42, text: "Aller dieser Könige und ihrer Länder bemächtigte sich Josua mit einem Schlag; denn der Herr, der Gott Israels, kämpfte für Israel." },
      { number: 43, text: "Dann kehrte Josua mit ganz Israel in das Lager nach Gilgal zurück." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Als Jabin, der König von Hazor, das hörte, schickte er Boten zu Jobab, dem König von Madon, zum König von Schimron, zum König von Achschaf", heading: "Der Sieg über die Könige im Norden: 11,1-14", footnotes: ["Ri 4,2", "1f: Nach der Eroberung des Südens (10,1-11) wird die Unterwerfung des nördlichen Palästina (11,1-14) berichtet. Hauptgegner ist der König von Hazor, einer bedeutenden, alten Stadt. «Kinneret» wird der See Gennesaret und sein Umland genannt."] },
      { number: 2, text: "und zu den Königen im Norden auf dem Gebirge und in der Araba südlich von Kinneret, in der Schefela und auf den Höhen von Dor im Westen," },
      { number: 3, text: "zu den Kanaanitern im Osten und im Westen, zu den Amoritern, Hetitern und Perisitern, zu den Jebusitern im Gebirge und den Hiwitern am Fuß des Hermon im Land Mizpe." },
      { number: 4, text: "Da rückten sie aus mit all ihren Truppen, einem Heer so groß und zahlreich wie der Sand am Ufer des Meeres, dazu mit einer großen Menge von Pferden und Wagen." },
      { number: 5, text: "Alle diese Könige taten sich zusammen, rückten heran und bezogen gemeinsam ihr Lager bei den Wassern von Merom, um den Kampf mit Israel aufzunehmen." },
      { number: 6, text: "Der Herr sagte zu Josua: Fürchte dich nicht vor ihnen! Denn morgen um diese Zeit werde ich sie alle erschlagen Israel zu Füßen legen. Du wirst ihre Pferde lähmen und ihre Wagen in Brand stecken." },
      { number: 7, text: "Da rückte Josua mit dem ganzen Heer an den Wassern von Merom plötzlich gegen sie vor und überfiel sie." },
      { number: 8, text: "Der Herr gab sie in die Gewalt der Israeliten und die Israeliten schlugen sie und verfolgten sie bis Groß-Sidon und Misrefot-Majim und bis zur Ebene von Mizpe im Osten. Sie schlugen sie so vernichtend, dass keiner von ihnen übrig blieb, der hätte entkommen können." },
      { number: 9, text: "Josua machte mit ihnen, was der Herr ihm gesagt hatte: Er lähmte ihre Pferde und steckte ihre Wagen in Brand." },
      { number: 10, text: "Daraufhin kehrte Josua um und nahm Hazor ein; ihren König erschlug er mit dem Schwert. Hazor hatte früher die Oberherrschaft über alle diese Königreiche." },
      { number: 11, text: "Die Israeliten erschlugen alles, was in der Stadt lebte, mit scharfem Schwert und weihten es dem Untergang. Nichts Lebendiges blieb übrig. Die Stadt selbst steckte man in Brand." },
      { number: 12, text: "Aller dieser Königsstädte samt ihren Königen bemächtigte sich Josua; er erschlug alles mit scharfem Schwert und weihte es dem Untergang, wie Mose, der Knecht des Herrn, befohlen hatte." },
      { number: 13, text: "Alle die Städte, die (heute noch) auf ihren Trümmerhügeln stehen, verbrannte Israel nicht; nur Hazor brannte Josua nieder." },
      { number: 14, text: "Den ganzen Besitz aus diesen Städten und das Vieh nahmen die Israeliten für sich, die Menschen aber erschlugen sie alle mit scharfem Schwert und rotteten sie völlig aus. Niemand ließen sie am Leben." },
      { number: 15, text: "Wie der Herr es seinem Knecht Mose befohlen hatte, so hatte es Mose Josua befohlen und so führte Josua es aus: Er unterließ nichts von all dem, was der Herr dem Mose befohlen hatte.", heading: "Das eroberte Land: 11,15 - 12,24", footnotes: ["Dtn 20,10-15"] },
      { number: 16, text: "So nahm Josua dieses ganze Land ein, das Gebirge und den ganzen Negeb, das ganze Land Goschen und die Schefela, die Araba und das Gebirge Israels mit seiner Schefela:" },
      { number: 17, text: "(das ganze Land) von den kahlen Bergen, die nach Seïr hin ansteigen, bis nach Baal-Gad in der Libanonsenke am Fuß des Hermongebirges. Alle ihre Könige nahm Josua gefangen und schlug sie tot." },
      { number: 18, text: "Lange Zeit führte Josua gegen alle diese Könige Krieg." },
      { number: 19, text: "Es gab keine Stadt, die mit den Israeliten Frieden geschlossen hätte, außer den Hiwitern, die Gibeon bewohnten. Alle musste man im Kampf nehmen.", footnotes: ["9,15"] },
      { number: 20, text: "Denn vom Herrn war beschlossen worden, ihr Herz angesichts des Kampfes mit Israel zu verhärten, um sie dem Untergang zu weihen; Israel sollte keine Gnade bei ihnen walten lassen, sondern sie ausrotten, wie es der Herr dem Mose befohlen hatte.", footnotes: ["Dtn 7,2"] },
      { number: 21, text: "Damals zog Josua auch gegen die Anakiter, die im Gebirge, in Hebron, in Debir, in Anab, im Bergland von Juda und im Bergland von Israel wohnten; er rottete sie überall aus und weihte sie samt ihren Städten dem Untergang.", footnotes: ["14,12-15; Dtn 9,2"] },
      { number: 22, text: "Im Land der Israeliten blieben keine Anakiter übrig; nur in Gaza, Gat und Aschdod verblieben sie." },
      { number: 23, text: "Josua nahm das ganze Land ein, genau so, wie es der Herr zu Mose gesagt hatte, und Josua verteilte es als Erbbesitz an Israel entsprechend seiner Stammeseinteilung. Dann war der Krieg zu Ende und das Land hatte Ruhe." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Das sind die Könige des Landes, die von den Israeliten geschlagen wurden, und die Länder, die sie in Besitz nahmen, jenseits des Jordan im Osten vom Arnontal bis zum Hermongebirge mit der ganzen Araba im Osten:" },
      { number: 2, text: "Sihon, der Amoriterkönig, der in Heschbon wohnte; er herrschte von Aroër am Rand des Arnontales und von der Mitte des Tals an über das halbe Gilead bis zum Jabboktal, das die Grenze zu den Ammonitern bildet,", footnotes: ["9,10; Num 21,23f"] },
      { number: 3, text: "ferner über die Araba bis zum See Kinneret im Osten und bis zum Meer der Araba, dem Salzmeer im Osten in Richtung auf Bet-Jeschimot und im Süden über das Gebiet am Fuß des Berges Pisga," },
      { number: 4, text: "dann das Gebiet des Og, des Königs von Baschan, der von den Rafaïtern übrig geblieben war und in Aschtarot und in Edreï wohnte;", footnotes: ["9,10; Num 21,33; Dtn 3", "Der Zusatz «das Gebiet des» fehlt in G und nimmt auf 12,1 keine Rücksicht."] },
      { number: 5, text: "er herrschte über das Hermongebirge und Salcha und den ganzen Baschan bis zum Gebiet der Geschuriter und der Maachatiter sowie über das halbe Gilead bis zum Gebiet Sihons, des Königs von Heschbon." },
      { number: 6, text: "Mose, der Knecht des Herrn, und die Israeliten hatten sie geschlagen und Mose, der Knecht des Herrn, hatte das Land den Rubenitern, den Gaditern und dem halben Stamm Manasse zum Besitz gegeben.", footnotes: ["Num 32,33"] },
      { number: 7, text: "Das sind die Könige des Landes, die Josua und die Israeliten jenseits des Jordan im Westen geschlagen hatten, von Baal-Gad in der Libanonsenke bis zu den kahlen Bergen, die nach Seïr hin ansteigen Josua gab ihr Land den Stämmen Israels zum Besitz, deren Stammeseinteilung entsprechend:" },
      { number: 8, text: "im Gebirge und in der Schefela, in der Araba und an den Bergabhängen, in der Wüste und im Negeb, (das Land) der Hetiter, der Amoriter, der Kanaaniter, der Perisiter, der Hiwiter und der Jebusiter:" },
      { number: 9, text: "Der König von Jericho: einer; der König von Ai, das seitlich von Bet-El liegt: einer;" },
      { number: 10, text: "der König von Jerusalem: einer; der König von Hebron: einer;" },
      { number: 11, text: "der König von Jarmut: einer; der König von Lachisch: einer;" },
      { number: 12, text: "der König von Eglon: einer; der König von Geser: einer;" },
      { number: 13, text: "der König von Debir: einer; der König von Geder: einer;" },
      { number: 14, text: "der König von Horma: einer; der König von Arad: einer;" },
      { number: 15, text: "der König von Libna: einer; der König von Adullam: einer;" },
      { number: 16, text: "der König von Makkeda: einer; der König von Bet-El: einer;" },
      { number: 17, text: "der König von Tappuach: einer; der König von Hefer: einer;" },
      { number: 18, text: "der König von Afek: einer; der König von Scharon: einer;" },
      { number: 19, text: "der König von Madon: einer; der König von Hazor: einer;" },
      { number: 20, text: "der König von Schimron-Meron: einer; der König von Achschaf: einer;" },
      { number: 21, text: "der König von Taanach: einer; der König von Megiddo: einer;" },
      { number: 22, text: "der König von Kedesch: einer; der König von Jokneam am Karmel: einer;" },
      { number: 23, text: "der König von Dor an der Höhe von Dor: einer; der König von Gojim in Galiläa: einer;" },
      { number: 24, text: "der König von Tirza: einer; zusammen: einunddreißig Könige." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Als Josua alt und hochbetagt war, sagte der Herr zu ihm: Du bist alt und hochbetagt, aber vom Land bleibt noch sehr viel in Besitz zu nehmen.", heading: "Die Verteilung des Landes: 13,1 - 22,34 | Das Land im Westen: 13,1-14" },
      { number: 2, text: "Das ist das Land, das noch übrig ist: alle Bezirke der Philister und das ganze Gebiet der Geschuriter" },
      { number: 3, text: "vom Schihor-Fluss östlich von Ägypten bis zum Gebiet von Ekron im Norden - es wird den Kanaanitern zugerechnet -, die fünf Philisterfürsten von Gaza, Aschdod, Aschkelon, Gat und Ekron sowie die Awiter", footnotes: ["Ri 3,3"] },
      { number: 4, text: "im Süden, das ganze Land der Kanaaniter und Meara, das zu Sidon gehört, bis nach Afek und bis zum Gebiet der Amoriter," },
      { number: 5, text: "ferner das Land der Gibliter und der ganze Libanon im Osten, von Baal-Gad am Fuß des Hermongebirges bis nach Lebo-Hamat." },
      { number: 6, text: "Alle Bewohner des Berglandes vom Libanon bis nach Misrefot-Majim, alle Sidonier werde ich selbst vor den Israeliten vertreiben. Du aber verlose das Land unter Israel als Erbbesitz, wie ich es dir befohlen habe." },
      { number: 7, text: "Verteil nun dieses Land als Erbbesitz an die neun Stämme und den halben Stamm Manasse." },
      { number: 8, text: "Die andere Hälfte des Stammes Manasse und mit ihm die Rubeniter und Gaditer hatten ihren Erbbesitz schon erhalten; ihn hatte Mose ihnen jenseits des Jordan im Osten gegeben, [wie ihn Mose, der Knecht des Herrn, ihnen zugewiesen hatte],", footnotes: ["13,15-32", "Der Anfang des Verses «Die andere Hälfte des Stammes Manasse» ist sinngemäß ergänzt."] },
      { number: 9, text: "von Aroër am Rand des Arnontales und von der Stadt an, die mitten im Tal liegt, also die ganze Ebene von Medeba bis Dibon," },
      { number: 10, text: "alle Städte des Amoriterkönigs Sihon, der in Heschbon herrschte, bis zum Gebiet der Ammoniter," },
      { number: 11, text: "dazu Gilead und das Gebiet der Geschuriter und Maachatiter und das ganze Hermongebirge und den ganzen Baschan bis nach Salcha," },
      { number: 12, text: "das ganze Reich des Og im Baschan, der in Aschtarot und Edreï herrschte. Er war vom Rest der Rafaïter übrig geblieben. Mose hatte sie geschlagen und vertrieben." },
      { number: 13, text: "Aber die Israeliten vertrieben die Geschuriter und die Maachatiter nicht; so wohnen Geschuriter und Maachatiter bis zum heutigen Tag mitten in Israel." },
      { number: 14, text: "Nur dem Stamm Levi gab Josua keinen Erbbesitz; der Herr, der Gott Israels, ist sein Erbteil, wie der Herr ihm gesagt hatte.", footnotes: ["13,33; 18,7; Num 18,20f; Dtn 18,1", "H liest nach Dtn 18,1, aber im Gegensatz zu Jos 13,33: die Feueropfer des Herrn, des Gottes Israels, sind sein Erbteil."] },
      { number: 15, text: "Die Zuteilung an den Stamm der Rubeniter hatte also Mose entsprechend ihren Sippen vorgenommen.", heading: "Die Verteilung des Ostjordanlandes: 13,15-33", footnotes: ["(15-32) Num 32,1-42"] },
      { number: 16, text: "Ihnen war zuteil geworden das Gebiet von Aroër am Rand des Arnontales und von der Stadt an, die mitten im Tal liegt: die ganze Ebene bei Medeba," },
      { number: 17, text: "Heschbon und alle seine Städte in der Ebene, Dibon, Bamot-Baal und Bet-Baal-Meon," },
      { number: 18, text: "Jahaz, Kedemot und Mefaat," },
      { number: 19, text: "Kirjatajim, Sibma und Zeret-Schahar auf dem Berg im Tal," },
      { number: 20, text: "Bet-Pegor, die Abhänge des Pisga, Bet-Jeschimot" },
      { number: 21, text: "und alle Städte der Ebene, das heißt: das ganze Reich des Amoriterkönigs Sihon, der in Heschbon herrschte. Mose hatte ihn und die Fürsten Midians geschlagen: Ewi, Rekem, Zur, Hur und Reba, die Heerführer Sihons, die im Land wohnten." },
      { number: 22, text: "Auch den Wahrsager Bileam, den Sohn Beors, hatten die Israeliten zusammen mit den anderen mit dem Schwert erschlagen.", footnotes: ["Num 22,5; 31,8"] },
      { number: 23, text: "Die Grenze der Rubeniter bildete der Jordan und sein Ufergebiet. Dieses Gebiet mit seinen Städten und Gehöften ist der Erbbesitz der Rubeniter, entsprechend ihren Sippen." },
      { number: 24, text: "Mose hatte auch die Zuteilung an den Stamm Gad, die Gaditer, entsprechend ihren Sippen vorgenommen." },
      { number: 25, text: "Ihnen war zuteil geworden das Gebiet von Jaser und alle Städte Gileads, das halbe Land der Ammoniter bis Aroër, das gegenüber Rabba liegt," },
      { number: 26, text: "(das Land) von Heschbon bis Ramat-Mizpe und Betonim und von Mahanajim bis zum Gebiet von Lo-Dabar," },
      { number: 27, text: "dazu im Tal Bet-Haram, Bet-Nimra, Sukkot und Zafon - der Rest des Reiches Sihons, des Königs von Heschbon -, ferner der Jordan und das Gebiet bis zum Ende des Sees von Kinneret jenseits des Jordan im Osten." },
      { number: 28, text: "Dieses Gebiet mit seinen Städten und Gehöften ist der Erbbesitz der Gaditer, entsprechend ihren Sippen." },
      { number: 29, text: "Mose hatte auch die Zuteilung an den halben Stamm Manasse vorgenommen, [dem halben Stamm Manasse wurde zuteil] entsprechend seinen Sippen." },
      { number: 30, text: "Sein Gebiet umfasste von Mahanajim an den ganzen Baschan, das ganze Reich des Königs Og vom Baschan und alle Zeltdörfer Jaïrs im Baschan, sechzig Städte." },
      { number: 31, text: "Die Hälfte von Gilead sowie Aschtarot und Edreï, die Königsstädte des Og vom Baschan, fiel den Nachkommen Machirs, des Sohnes Manasses zu, und zwar der Hälfte der Machiriter entsprechend ihren Sippen." },
      { number: 32, text: "Das sind die Gebiete, die Mose in den Steppen von Moab jenseits des Jordan östlich von Jericho als Erbbesitz verteilt hatte." },
      { number: 33, text: "Dem Stamm Levi aber hatte Mose keinen Erbbesitz zugeteilt. Der Herr, der Gott Israels, ist ihr Erbteil, wie er es ihnen gesagt hatte.", footnotes: ["13,14; 18,7"] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Das sind die Gebiete, die die Israeliten als Erbbesitz im Land Kanaan erhielten und die der Priester Eleasar sowie Josua, der Sohn Nuns, und die Anführer der Sippen der Israeliten zuteilten -", heading: "Die Verteilung des Westjordanlandes: 14,1-15", footnotes: ["Num 34,17"] },
      { number: 2, text: "durch das Los teilten sie es ihnen zu, wie es der Herr durch Mose befohlen hatte -, den neuneinhalb Stämmen.", footnotes: ["Num 26,55", "durch das Los teilten sie es ihnen zu: Text korr. nach G."] },
      { number: 3, text: "Denn Mose hatte zweieinhalb Stämmen ihren Erbbesitz schon jenseits des Jordan zugewiesen; den Leviten gab er keinen Erbbesitz in ihrer Mitte.", footnotes: ["13,14.33; Dtn 10,9"] },
      { number: 4, text: "Denn die Nachkommen Josefs bildeten zwei Stämme, Manasse und Efraim. Den Leviten gab man also keinen Landanteil, sondern nur Städte als Wohnsitz und die dazugehörenden Weideflächen für das Vieh, das sie besaßen.", footnotes: ["21,1-42"] },
      { number: 5, text: "Wie es der Herr dem Mose befohlen hatte, so machten es die Israeliten und verteilten das Land." },
      { number: 6, text: "Damals traten die Judäer in Gilgal an Josua heran und Kaleb, der Sohn des Kenasiters Jefunne, sagte zu ihm: Du weißt, was der Herr zu Mose, dem Gottesmann, in Kadesch-Barnea über mich und dich gesagt hat.", footnotes: ["Num 14,24; Dtn 1,36", "Die Verteilung des Westjordanlandes beginnt mit dem südlich von Juda wohnenden kleinen Stamm der Kalebiter und schreitet nach Norden voran."] },
      { number: 7, text: "Ich war vierzig Jahre alt, als mich Mose, der Knecht des Herrn, in Kadesch-Barnea aussandte, damit ich das Land erkundete, und ich erstattete ihm Bericht, wie ich es mir vorgenommen hatte.", footnotes: ["Num 13,6.30"] },
      { number: 8, text: "Während meine Brüder, die mit mir hinaufgezogen waren, das Volk mutlos machten, hielt ich treu zum Herrn, meinem Gott.", footnotes: ["Num 14,24; 32,11f"] },
      { number: 9, text: "An jenem Tag schwor Mose: Das Land, das dein Fuß betreten hat, soll dir und deinen Söhnen für immer als Erbbesitz gehören, weil du treu zum Herrn, deinem Gott, gehalten hast." },
      { number: 10, text: "Nun sieh her: Der Herr hat mich, wie er es versprochen hat, am Leben gelassen. Fünfundvierzig Jahre ist es her, seit der Herr dieses Wort zu Mose gesprochen hat, als Israel durch die Wüste zog. Heute bin ich, wie du siehst, fünfundachtzig Jahre alt." },
      { number: 11, text: "Ich bin immer noch so stark wie damals, als Mose mich ausgesandt hat; wie meine Kraft damals war, so ist sie noch heute, wenn es gilt, zu kämpfen, auszuziehen und heimzukehren." },
      { number: 12, text: "Nun gib mir also dieses Bergland, von dem der Herr an jenem Tag geredet hat. Denn du hast selbst an jenem Tag gehört, dass Anakiter dort sind und große befestigte Städte. Vielleicht ist der Herr mit mir, sodass ich sie vertreiben kann, wie der Herr gesagt hat.", footnotes: ["11,21"] },
      { number: 13, text: "Da segnete Josua Kaleb, den Sohn Jefunnes, und gab ihm Hebron als Erbbesitz.", footnotes: ["15,13-17; 21,12; Ri 1,20"] },
      { number: 14, text: "Deshalb gehört Hebron bis zum heutigen Tag dem Kenasiter Kaleb, dem Sohn Jefunnes, weil er treu zum Herrn, dem Gott Israels, gehalten hat.", footnotes: ["Num 32,12"] },
      { number: 15, text: "Hebron hieß früher Kirjat-Arba (Stadt des Arba); Arba war der größte Mann unter den Anakitern. Danach war der Krieg zu Ende und das Land hatte Ruhe." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "An den Stamm der Judäer fiel durch das Los, entsprechend seinen Sippen, der Anteil gegen die Grenze Edoms hin, südwärts der Wüste Zin zu gelegen, die im äußersten Süden ist.", heading: "Das Gebiet des Stammes Juda: 15,1-63" },
      { number: 2, text: "Die Südgrenze Judas beginnt am Ende des Salzmeeres, an der (Meeres)zunge, die sich nach Süden erstreckt," },
      { number: 3, text: "verläuft dann südlich zur Skorpionen-Steige, zieht sich hinüber nach Zin, steigt südlich von Kadesch-Barnea hinauf, führt hinüber nach Hezron, steigt hinauf nach Addar und wendet sich nach Karka;" },
      { number: 4, text: "dann führt sie hinüber nach Azmon, geht hinaus zum Grenzbach Ägyptens und läuft am Meer aus. Das ist die Südgrenze des Stammes Juda.", footnotes: ["des Stammes Juda: Text korr. nach G (wörtlich: für sie); H: für euch."] },
      { number: 5, text: "Die Ostgrenze bildet das Salzmeer bis zur Jordanmündung. Für die Nordseite beginnt die Grenze an der Meereszunge bei der Jordanmündung." },
      { number: 6, text: "Die Grenze geht dann nach Bet-Hogla hinauf und läuft nördlich an Bet-Araba vorbei. Dann zieht sie sich hinauf zum Stein Bohans, des Sohnes Rubens." },
      { number: 7, text: "Vom Achortal steigt sie empor nach Debir und wendet sich nordwärts nach Gelilot gegenüber der Steige von Adummim, die südlich des Bachtals liegt; dann führt sie hinüber zum Wasser von En-Schemesch und läuft hinaus zur Rogel-Quelle.", footnotes: ["nach Gelilot: Text korr. (vgl. 18,7); H: Gilgal."] },
      { number: 8, text: "Von da an steigt die Grenze hinauf in das Ben-Hinnom-Tal südlich des Bergrückens der Jebusiter - das ist Jerusalem - und weiter empor zum Gipfel des Berges, der westlich des Hinnom-Tals am nördlichen Rand der Rafaïter-Ebene liegt." },
      { number: 9, text: "Vom Gipfel des Berges biegt die Grenze ab zur Neftoach-Quelle und führt hinaus zum Berg Efron. Dann biegt sie ab nach Baala, das ist Kirjat-Jearim.", footnotes: ["zum Berg Efron: Text korr. nach G; H: zu den Städten des Berges Efron."] },
      { number: 10, text: "Von Baala wendet sich die Grenze nach Westen zum Berg Seïr, führt dann an der Nordflanke des Berges von Jearim, das ist Kesalon, vorbei, hinab nach Bet-Schemesch und hinüber nach Timna." },
      { number: 11, text: "Dann verläuft sie hinaus zur Nordflanke von Ekron, biegt ab nach Schikkaron, geht hinüber zum Berg Baala und hinaus nach Jabneel. Die Grenze läuft aus am Meer." },
      { number: 12, text: "Das große Meer und das Küstengebiet bilden die Westgrenze. Das ist das Gebiet der Judäer ringsum, entsprechend ihren Sippen." },
      { number: 13, text: "Kaleb aber, dem Sohn Jefunnes, gab Josua einen Anteil mitten unter den Judäern, nach der Anweisung des Herrn an Josua (und zwar) Kirjat-Arba - Arba war der Vater Anaks -, das heißt Hebron.", footnotes: ["(13-19) 14,13-15; Ri 1,10-15"] },
      { number: 14, text: "Kaleb vertrieb von dort die drei Anakiter Scheschai, Ahiman und Talmai, die Söhne Anaks." },
      { number: 15, text: "Von dort zog er hinauf zu den Einwohnern von Debir. Debir hieß früher Kirjat-Sefer." },
      { number: 16, text: "Und Kaleb sagte: Wer Kirjat-Sefer besiegt und einnimmt, dem gebe ich meine Tochter Achsa zur Frau." },
      { number: 17, text: "Otniël, der Sohn des Kenas, eines Bruders Kalebs, nahm die Stadt ein und Kaleb gab ihm seine Tochter Achsa zur Frau.", footnotes: ["Ri 3,9"] },
      { number: 18, text: "Als sie bei Otniël ankam, drängte er sie, von ihrem Vater ein Stück Land zu verlangen. Da ließ sie sich vom Esel herabgleiten und Kaleb fragte sie: Was hast du?", footnotes: ["Übersetzung unsicher."] },
      { number: 19, text: "Sie antwortete: Gib mir ein Geschenk als Zeichen des Segens! Wenn du mich schon ins Trockenland schickst, dann gib mir auch Wasserstellen! Da gab er ihr die obere und die untere Wasserquelle.", footnotes: ["ein Geschenk als Zeichen des Segens, wörtlich: einen Segen."] },
      { number: 20, text: "Das ist der Erbbesitz des Stammes der Judäer, entsprechend ihren Sippen:", footnotes: ["20-63: Die Aufstellung geht auf alte Ortslisten zurück, in denen die Bezirke Judas umschrieben waren."] },
      { number: 21, text: "Die Städte im Bereich des Stammes der Judäer waren folgende: an der Grenze zu Edom im Negeb: Kabzeel, Eder, Jagur," },
      { number: 22, text: "Kina, Dimona, Adada," },
      { number: 23, text: "Kadesch, Hazor, Jitna," },
      { number: 24, text: "Sif, Telem, Bealot," },
      { number: 25, text: "Hazor-Hadatta, Kerijot-Hezron, das ist Hazor," },
      { number: 26, text: "Amam, Schema, Molada," },
      { number: 27, text: "Hazar-Gadda, Heschmon, Bet-Pelet," },
      { number: 28, text: "Hazar-Schual, Beerscheba und seine Tochterstädte,", footnotes: ["und seine Tochterstädte: Text korr., H ist verderbt, vgl. G."] },
      { number: 29, text: "Baala, Ijim, Ezem," },
      { number: 30, text: "Eltolad, Kesil, Horma," },
      { number: 31, text: "Ziklag, Madmanna, Sansanna," },
      { number: 32, text: "Lebaot, Schilhim und En-Rimmon: im ganzen neunundzwanzig Städte mit den dazugehörenden Gehöften;", footnotes: ["En-Rimmon: Text korr. nach Neh 11,29 (vgl. Jos 19,7)."] },
      { number: 33, text: "in der Schefela: Eschtaol, Zora, Aschna," },
      { number: 34, text: "Sanoach, En-Gannim, Tappuach, Enajim," },
      { number: 35, text: "Jarmut, Adullam, Socho, Aseka," },
      { number: 36, text: "Schaarajim, Aditajim, Gedera und Gederotajim: vierzehn Städte mit den dazugehörenden Gehöften;" },
      { number: 37, text: "Zenan, Hadascha, Migdal-Gad," },
      { number: 38, text: "Dilan, Mizpe, Jokteel," },
      { number: 39, text: "Lachisch, Bozkat, Eglon," },
      { number: 40, text: "Kabbon, Lachmas, Kitlisch," },
      { number: 41, text: "Gederot, Bet-Dagon, Naama und Makkeda: sechzehn Städte mit den dazugehörenden Gehöften;" },
      { number: 42, text: "Libna, Eter, Aschan," },
      { number: 43, text: "Jiftach, Aschna, Nezib," },
      { number: 44, text: "Keïla, Achsib und Marescha: neun Städte mit den dazugehörenden Gehöften;" },
      { number: 45, text: "Ekron mit den dazugehörenden Tochterstädten und Gehöften," },
      { number: 46, text: "von Ekron bis zum Meer alles, was auf der Seite von Aschdod liegt, samt den dazugehörenden Gehöften;" },
      { number: 47, text: "Aschdod mit den dazugehörenden Tochterstädten und Gehöften, Gaza mit den dazugehörenden Tochterstädten und Gehöften bis zum Grenzbach Ägyptens und zum großen Meer mit dem Küstengebiet;" },
      { number: 48, text: "auf dem Gebirge: Schamir, Jattir, Socho," },
      { number: 49, text: "Danna, Kirjat-Sanna, das ist Debir," },
      { number: 50, text: "Anab, Eschtemoa, Anim," },
      { number: 51, text: "Goschen, Holon und Gilo: elf Städte mit den dazugehörenden Gehöften;" },
      { number: 52, text: "Arab, Duma, Eschan," },
      { number: 53, text: "Janum, Bet-Tappuach, Afeka," },
      { number: 54, text: "Humta, Kirjat-Arba, das ist Hebron, und Zior: neun Städte und die dazugehörenden Gehöfte;" },
      { number: 55, text: "Maon, Karmel, Sif, Jutta," },
      { number: 56, text: "Jesreel, Jokdeam, Sanoach," },
      { number: 57, text: "Kaim, Gibea und Timna: zehn Städte mit den dazugehörenden Gehöften;" },
      { number: 58, text: "Halhul, Bet-Zur, Gedor," },
      { number: 59, text: "Maarat, Bet-Anot und Eltekon: sechs Städte mit den dazugehörenden Gehöften; Tekoa, Efrata, das ist Betlehem, Pegor, Etam, Kulon, Tatam, Schoresch, Kerem, Gallim, Bet-Ter und Manocho: elf Städte mit den dazugehörenden Gehöften:", footnotes: ["Dieser Versteil ist nur in G erhalten."] },
      { number: 60, text: "Kirjat-Baal, das ist Kirjat-Jearim, und Rabba: zwei Städte mit den dazugehörenden Gehöften;" },
      { number: 61, text: "in der Steppe: Bet-Araba, Middin, Sechacha," },
      { number: 62, text: "Nibschan, Ir-Melach und En-Gedi: sechs Städte mit den dazugehörenden Gehöften." },
      { number: 63, text: "Nur die Jebusiter, die in Jerusalem wohnten, konnten die Judäer nicht vertreiben und so wohnen die Jebusiter bis zum heutigen Tag mit den Judäern zusammen in Jerusalem." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Das war der Anteil, der den Nachkommen Josefs durch das Los zufiel: (Seine Grenze verläuft) vom Jordan bei Jericho zu den Quellen von Jericho im Osten, dann von Jericho aus in die Wüste aufsteigend und ins Gebirge von Bet-El.", heading: "Das Gebiet der Nachkommen Josefs: 16,1 - 17,18" },
      { number: 2, text: "Von Bet-El geht sie nach Lus hinaus und hinüber zum Gebiet der Arkiter nach Atarot." },
      { number: 3, text: "Dann führt sie westwärts hinab zum Gebiet der Jafletiter bis zum Gebiet von Unter-Bet-Horon und Geser und läuft am Meer aus." },
      { number: 4, text: "Erbbesitz erhielten auch die Söhne Josefs, Manasse und Efraim." },
      { number: 5, text: "Dies war das Gebiet der Efraimiter, entsprechend ihren Sippen: Die Grenze ihres Erbbesitzes [im Osten] verläuft von Atarot-Adar bis Ober-Bet-Horon" },
      { number: 6, text: "und läuft zum Meer. Von Michmetat im Norden wendet sich die Grenze ostwärts nach Taanat-Schilo und geht ostwärts daran vorbei nach Janoach." },
      { number: 7, text: "Von Janoach führt sie hinab nach Atarot und Naara, berührt Jericho und läuft am Jordan aus." },
      { number: 8, text: "Von Tappuach geht die Grenze westwärts zum Bach Kana und läuft am Meer aus. Das ist der Erbbesitz des Stammes der Efraimiter, entsprechend ihren Sippen;" },
      { number: 9, text: "ferner (gehören) den Efraimitern vereinzelte Städte mitten im Erbbesitz der Manassiter, und zwar die Städte samt den dazugehörenden Gehöften." },
      { number: 10, text: "Die Efraimiter konnten aber die Kanaaniter, die in Geser wohnten, nicht vertreiben. So wohnen die Kanaaniter bis zum heutigen Tag mitten in Efraim; sie wurden aber zu Fronknechten gemacht." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Dem Stamm des Manasse - er war der Erstgeborene Josefs - fiel durch das Los folgender Anteil zu: Machir, der Erstgeborene Manasses, der Vater Gileads, - er war ein kampferprobter Mann - erhielt Gilead und den Baschan.", footnotes: ["13,29f; Num 26,29; Ri 5,14"] },
      { number: 2, text: "Auch die übrigen Söhne Manasses erhielten einen (Anteil) entsprechend ihren Sippen: die Söhne Abiësers, die Söhne Heleks, die Söhne Asriëls, die Söhne Schechems, die Söhne Hefers und die Söhne Schemidas; das sind die männlichen Nachkommen Manasses, des Sohnes Josefs, mit ihren Sippen." },
      { number: 3, text: "Zelofhad aber, der Sohn Hefers, des Sohnes Gileads, des Sohnes Machirs, des Sohnes Manasses, hatte keine Söhne, sondern nur Töchter. Seine Töchter hatten folgende Namen: Machla, Noa, Hogla, Milka und Tirza.", footnotes: ["Num 26,33; 27,1-8; 36,2"] },
      { number: 4, text: "Sie erschienen vor dem Priester Eleasar und vor Josua, dem Sohn Nuns, und vor den Anführern und sagten: Der Herr hat Mose befohlen, uns Erbbesitz im Kreis unserer Brüder zu geben. Da gab er ihnen, gemäß der Anweisung des Herrn, Erbbesitz im Kreis der Brüder ihres Vaters." },
      { number: 5, text: "So entfielen auf Manasse, abgesehen vom Land Gilead und dem Baschan, die jenseits des Jordan liegen, zehn Anteile." },
      { number: 6, text: "Denn die Töchter Manasses erhielten Erbbesitz im Kreis seiner Söhne. Das Land Gilead aber erhielten die übrigen Söhne Manasses." },
      { number: 7, text: "Das Gebiet Manasses reicht vom Gebiet Aschers bis nach Michmetat, das gegenüber von Sichem liegt. Von dort verläuft die Grenze nach Süden bis zu den Einwohnern von En-Tappuach." },
      { number: 8, text: "Manasse wurde das Gebiet von Tappuach zuteil, Tappuach selbst aber, an der Grenze Manasses, den Efraimitern." },
      { number: 9, text: "Dann zieht sich die Grenze hinab zum Bach Kana; südlich des Bachs gehören einzelne Städte mitten unter den Städten Manasses zu Efraim. Nördlich des Bachs ist die Grenze Manasses; sie läuft am Meer aus.", footnotes: ["16,8f"] },
      { number: 10, text: "Das Gebiet im Süden gehört Efraim und das Gebiet im Norden Manasse, dessen Grenze das Meer bildet. Im Norden grenzen sie an Ascher, im Osten an Issachar." },
      { number: 11, text: "Manasse erhielt in Issachar und in Ascher Bet-Schean und seine Tochterstädte, Jibleam und seine Tochterstädte, die Einwohner von Dor und seinen Tochterstädten, die Einwohner von En-Dor und seinen Tochterstädten, die Einwohner von Taanach und seinen Tochterstädten, die Einwohner von Megiddo und seinen Tochterstädten [drei Anhöhen].", footnotes: ["Ri 1,27f", "Sinn und Bedeutung des Zusatzes unsicher."] },
      { number: 12, text: "Doch die Manassiter konnten diese Städte nicht in Besitz nehmen; es gelang den Kanaanitern, ihre Wohnsitze in diesem Land zu halten." },
      { number: 13, text: "Als die Israeliten jedoch stark geworden waren, zwangen sie die Kanaaniter zur Fron; aber vertreiben konnten sie sie nicht." },
      { number: 14, text: "Die Nachkommen Josefs sagten zu Josua: Warum hast du uns nur ein einziges Los und nur einen einzigen Anteil als Erbbesitz gegeben, obwohl wir ein zahlreiches Volk sind, nachdem uns der Herr so sehr gesegnet hat?" },
      { number: 15, text: "Josua antwortete ihnen: Wenn ihr ein so zahlreiches Volk seid, dann steigt doch hinauf in das Waldgebiet und rodet für euch dort im Land der Perisiter und Rafaïter, wenn euch das Gebirge Efraim zu eng ist." },
      { number: 16, text: "Da sagten die Nachkommen Josefs: Das Bergland reicht für uns nicht aus. Im ebenen Land aber wohnen die Kanaaniter, die alle eiserne Kampfwagen haben, wie etwa die Leute in Bet-Schean und seinen Tochterstädten und die Leute in der Ebene Jesreel.", footnotes: ["Die Wagen hatten eiserne Beschläge. In Israel wurden solche Wagen erst unter Salomo eingeführt (vgl. 1 Kön 10,28f)."] },
      { number: 17, text: "Josua erwiderte dem Haus Josef, Efraim und Manasse: Ihr seid ein zahlreiches Volk und habt große Kraft. Ihr erhaltet nicht nur einen einzigen Anteil;" },
      { number: 18, text: "denn auch das Bergland soll euch gehören. Es ist bewaldet; ihr könnt es roden. Das so gewonnene Land soll euch gehören, wenn ihr auch die Kanaaniter nicht vertreiben könnt, weil sie eiserne Kampfwagen haben und weil sie stark sind.", footnotes: ["nicht: Text korr. (vgl. V. 13; 16,10)."] },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Die ganze Gemeinde der Israeliten versammelte sich in Schilo. Dort schlugen sie das Offenbarungszelt auf; das ganze Land lag unterworfen vor ihnen.", heading: "Das Gebiet der übrigen Stämme: 18,1 - 19,51", footnotes: ["Nicht mehr Gilgal (14,6), sondern Schilo, ein zentrales Heiligtum nach 1 Sam 1 - 4 und Jer 7,12, ist jetzt Mittelpunkt aller Stämme Israels."] },
      { number: 2, text: "Aber noch waren von den Israeliten sieben Stämme übrig, die ihren Erbbesitz nicht zugeteilt bekommen hatten." },
      { number: 3, text: "Da sagte Josua zu den Israeliten: Wie lange wollt ihr noch zögern, hinzugehen und das Land in Besitz zu nehmen, das der Herr, der Gott eurer Väter, euch gegeben hat?" },
      { number: 4, text: "Bestimmt drei Männer aus jedem Stamm; ich will sie aussenden. Sie sollen sich auf den Weg machen, durch das ganze Land ziehen und es im Hinblick auf ihren Erbbesitz schriftlich aufnehmen. Dann sollen sie (wieder) zu mir kommen." },
      { number: 5, text: "Ihr sollt es unter euch in sieben Teile aufteilen. Juda soll auf seinem Gebiet im Süden bleiben und das Haus Josef soll auf seinem Gebiet im Norden bleiben." },
      { number: 6, text: "Ihr also sollt das Land in sieben Teilen schriftlich aufnehmen und (die Aufzeichnung) zu mir hierher bringen. Ich werde dann hier vor dem Herrn, unserem Gott, das Los für euch werfen." },
      { number: 7, text: "Doch Levi bekommt keinen Anteil unter euch, denn sein Erbbesitz ist das Priestertum des Herrn. Und Gad, Ruben und der halbe Stamm Manasse haben bereits ihren Erbbesitz erhalten; ihn hat Mose, der Knecht des Herrn, ihnen gegeben, jenseits des Jordan im Osten.", footnotes: ["13,14.33", "Levi: Text korr.; H: die Leviten."] },
      { number: 8, text: "Da machten sich die Männer auf den Weg und zogen fort. Josua hatte denen, die fortzogen, befohlen, das Land schriftlich aufzunehmen, und gesagt: Geht, zieht durch das Land und nehmt es schriftlich auf! Dann kommt zu mir zurück und ich will hier in Schilo vor dem Herrn das Los für euch werfen." },
      { number: 9, text: "Die Männer gingen fort, durchquerten das Land und zeichneten es in einem Buch auf, geordnet nach Städten und in sieben Teile gegliedert. Dann kamen sie wieder zu Josua ins Lager nach Schilo." },
      { number: 10, text: "Und Josua warf in Schilo vor den Augen des Herrn das Los für die Israeliten. So verteilte Josua das Land an sie, entsprechend ihren Abteilungen." },
      { number: 11, text: "Das (erste) Los fiel auf den Stamm der Benjaminiter und ihre Sippen. Als ihr Anteil ergab sich das Gebiet zwischen den Judäern und den Nachkommen Josefs." },
      { number: 12, text: "Ihre Grenze im Norden beginnt am Jordan. Dann steigt sie hinauf zum Bergrücken nördlich von Jericho und weiter nach Westen ins Gebirge und läuft sodann ins Weidegebiet von Bet-Awen hinaus." },
      { number: 13, text: "Von dort zieht sie sich hinüber nach Lus, südlich am Bergrücken von Lus, das ist Bet-El, vorbei; dann steigt sie nach Atrot-Addar hinab zu dem Berg, der südlich von Unter-Bet-Horon liegt." },
      { number: 14, text: "Darauf biegt sie ab und wendet sich auf der Westseite nach Süden, von dem Berg aus, der Bet-Horon südlich gegenüberliegt. Sie läuft aus bei Kirjat-Baal, das ist Kirjat-Jearim, eine Stadt der Judäer. Das ist die Westseite." },
      { number: 15, text: "Die Südseite (der Grenze) beginnt am Rand von Kirjat-Jearim. Sie verläuft dann westwärts hin zur Neftoach-Quelle." },
      { number: 16, text: "Dann zieht sie sich hinab zum Rand des Berges, der dem Ben-Hinnom-Tal gegenüber im Norden der Rafaïter-Ebene liegt. Sie steigt in das Ben-Hinnom-Tal hinab südlich des Bergrückens der Jebusiter und führt zur Rogel-Quelle hinunter." },
      { number: 17, text: "Dann biegt sie nach Norden ab, geht weiter nach En-Schemesch und nach Gelilot, gegenüber der Steige von Adummim, und steigt dann zum Stein des Bohan, des Sohnes Rubens, hinab." },
      { number: 18, text: "Sie geht nördlich am Bergrücken von Bet-Araba vorbei und führt weiter in die Araba hinunter.", footnotes: ["Bet-Araba: Text korr. (vgl. V. 22; 15,6.61); H: Araba."] },
      { number: 19, text: "Dann führt die Grenze nördlich am Bergrücken von Bet-Hogla vorbei und läuft nördlich der Landzunge des Salzmeers aus, am südlichen Ende des Jordan. Das ist die Südgrenze." },
      { number: 20, text: "Der Jordan bildet die Grenze auf der Ostseite. Das ist der Erbbesitz der Benjaminiter mit seinen Grenzen ringsum, entsprechend ihren Sippen." },
      { number: 21, text: "Das sind die Städte des Stammes der Benjaminiter, entsprechend ihren Sippen: Jericho, Bet-Hogla, Emek-Keziz," },
      { number: 22, text: "Bet-Araba, Zemarajim, Bet-El," },
      { number: 23, text: "Awim, Para, Ofra," },
      { number: 24, text: "Kefar-Ammoni, Ofni und Geba: zwölf Städte mit den dazugehörenden Gehöften;" },
      { number: 25, text: "ferner Gibeon, Rama, Beerot," },
      { number: 26, text: "Mizpe, Kefira, Moza," },
      { number: 27, text: "Rekem, Jirpeel, Tarala," },
      { number: 28, text: "Zela, Elef, Jebus, das ist Jerusalem, Gibea und Kirjat: vierzehn Städte mit den dazugehörenden Gehöften. Das ist der Erbbesitz der Benjaminiter, entsprechend ihren Sippen." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Das zweite Los fiel [auf Simeon] auf den Stamm der Simeoniter und ihre Sippen. Ihr Erbbesitz lag mitten im Erbbesitz der Judäer.", footnotes: ["1-7: Grenzen des Stammesgebiets von Simeon werden nicht angegeben, weil der Stamm in Juda aufgegangen ist."] },
      { number: 2, text: "Sie erhielten als Erbbesitz: Beerscheba, Scheba, Molada," },
      { number: 3, text: "Hazar-Schual, Baala, Ezem," },
      { number: 4, text: "Eltolad, Betul, Horma," },
      { number: 5, text: "Ziklag, Bet-Markabot, Hazar-Susa," },
      { number: 6, text: "Bet-Lebaot und Scharuhen: dreizehn Städte mit den dazugehörenden Gehöften;" },
      { number: 7, text: "ferner Ajin, Rimmon, Atar und Aschan: vier Städte mit den dazugehörenden Gehöften," },
      { number: 8, text: "dazu alle Gehöfte rings um diese Städte bis hin nach Baalat-Beer und Ramat-Negeb. Das ist der Erbbesitz des Stamms der Simeoniter, entsprechend ihren Sippen." },
      { number: 9, text: "Der Erbbesitz der Simeoniter war ein Stück vom Anteil der Judäer; denn der Anteil der Judäer war für diese zu groß. So erhielten die Simeoniter Erbbesitz mitten in deren Erbbesitz." },
      { number: 10, text: "Das dritte Los fiel auf die Sebuloniter und ihre Sippen. Das Gebiet ihres Erbbesitzes erstreckt sich bis nach Sarid." },
      { number: 11, text: "Ihre Grenze steigt westwärts nach Marala hinauf, berührt Dabbeschet und stößt an das Bachtal gegenüber von Jokneam." },
      { number: 12, text: "Von Sarid aus wendet sie sich nach Osten [dem Sonnenaufgang zu] zum Gebiet von Kislot-Tabor, läuft weiter nach Daberat und hinauf nach Jafia." },
      { number: 13, text: "Von dort geht sie wieder ostwärts dem Sonnenaufgang zu nach Gat-Hefer und Et-Kazin und weiter nach Rimmon und biegt dann ab nach Nea.", footnotes: ["und biegt dann ab: Text korr.; H ist verderbt."] },
      { number: 14, text: "Sie zieht sich um Nea herum, geht dann in nördlicher Richtung nach Hannaton und läuft im Tal Jiftach-El aus." },
      { number: 15, text: "(Dazu kommen) . . . Kattat, Nahalal, Schimron, Jidala und Betlehem: zwölf Städte mit den dazugehörenden Gehöften.", footnotes: ["Der Text ist unvollständig überliefert."] },
      { number: 16, text: "Das ist der Erbbesitz der Sebuloniter, entsprechend ihren Sippen: die Städte mit den dazugehörenden Gehöften." },
      { number: 17, text: "Das vierte Los fiel [auf Issachar,] auf die Issachariter und ihre Sippen.", footnotes: ["17-23: Issachars Gebiet erstreckt sich östlich des Tabor bis zum Jordan und umfasst auch den Ortsteil der Jesreelebene."] },
      { number: 18, text: "Ihr Gebiet erstreckt sich bis Jesreel und (umfasst) Kesullot, Schunem," },
      { number: 19, text: "Hafarajim, Schion, Anahara," },
      { number: 20, text: "Rabbit, Kischjon, Ebez," },
      { number: 21, text: "Remet, En-Gannim, En-Hadda und Bet-Pazzez." },
      { number: 22, text: "Die Grenze berührt Tabor, Schahazajim und Bet-Schemesch. Dann läuft sie am Jordan aus: Im Ganzen sind es sechzehn Städte mit den dazugehörenden Gehöften." },
      { number: 23, text: "Das ist der Erbbesitz der Issachariter, entsprechend ihren Sippen: die Städte mit den dazugehörenden Gehöften." },
      { number: 24, text: "Das fünfte Los fiel auf den Stamm der Ascheriter und ihre Sippen.", footnotes: ["24-31: Ascher erhält das Land am Meer von Tyrus bis südlich des Karmel."] },
      { number: 25, text: "Ihr Gebiet umfasst Helkat, Hali, Beten, Achschaf," },
      { number: 26, text: "Alammelech, Amad und Mischal; es stößt im Westen an den Karmel und an den Fluss Libnat." },
      { number: 27, text: "Im Osten dagegen verläuft die Grenze nach Bet-Dagon, stößt an Sebulon und berührt im Norden das Tal Jiftach-El, ferner Bet-Emek und Negiël und geht dann nordwärts nach Kabul" },
      { number: 28, text: "- Abdon, Rehob, Hammon und Kana gehören dazu - und bis nach Groß-Sidon." },
      { number: 29, text: "Dann wendet sich die Grenze nach Rama und geht bis zur Festung Tyrus. Von dort wendet sie sich nach Hosa und läuft am Meer aus. (Dazu gehören) . . . Mahaleb, Achsib," },
      { number: 30, text: "Umma, Afek und Rehob: (im ganzen) zweiundzwanzig Städte mit den dazugehörenden Gehöften." },
      { number: 31, text: "Das ist der Erbbesitz des Stammes der Ascheriter, entsprechend ihren Sippen: die Städte mit den dazugehörenden Gehöften." },
      { number: 32, text: "Das sechste Los fiel auf die Naftaliter und ihre Sippen.", footnotes: ["32-39: Naftalis Gebiet liegt westlich des oberen Jordan und des Sees Gennesaret, östlich von Ascher und Sebulon, nördlich von Issachar."] },
      { number: 33, text: "Ihre Grenze geht von Helef, von der Eiche bei Zaanannim, aus über Adami-Nekeb und Jabneel bis nach Lakkum und läuft am Jordan aus." },
      { number: 34, text: "Umgekehrt geht die Grenze westwärts nach Asnot-Tabor und von dort hinaus nach Hukkok. Sie stößt im Süden an Sebulon; im Westen stößt sie an Ascher und im Osten [an Juda und] an den Jordan.", footnotes: ["Der Zusatz trifft sachlich nicht zu; er fehlt zu Recht in G."] },
      { number: 35, text: "(Dazu kommen) die befestigten Städte Ziddim, Zer, Hammat-Rakkat, Kinneret," },
      { number: 36, text: "Adama, Rama, Hazor," },
      { number: 37, text: "Kedesch, Edreï, En-Hazor," },
      { number: 38, text: "Jiron, Migdal-El, Horem, Bet-Anat und Bet-Schemesch: neunzehn Städte mit den dazugehörenden Gehöften." },
      { number: 39, text: "Das ist der Erbbesitz der Naftaliter entsprechend ihren Sippen: die Städte mit den dazugehörenden Gehöften." },
      { number: 40, text: "Das siebte Los fiel auf den Stamm der Daniter und ihre Sippen.", footnotes: ["40-48: Zunächst siedelte der Stamm Dan im Nordwesten des Stammes Juda (vgl. 15,33.45.57). Später wird der Stamm Dan zur Auswanderung in das Gebiet nördlich des Sees Gennesaret gezwungen (vgl. Ri 1,34; 18,1-29)."] },
      { number: 41, text: "Das Gebiet ihres Erbbesitzes umfasste Zora, Eschtaol, Ir-Schemesch," },
      { number: 42, text: "Schaalbim, Ajalon, Jitla," },
      { number: 43, text: "Elon, Timna, Ekron," },
      { number: 44, text: "Elteke, Gibbeton, Baalat," },
      { number: 45, text: "Jehud, Bene-Berak, Gat-Rimmon," },
      { number: 46, text: "Me-Jarkon, Rakkon samt dem Gebiet gegenüber Jafo." },
      { number: 47, text: "Doch das Gebiet wurde den Danitern zu eng. Da zogen die Daniter nach Leschem hinauf, eröffneten den Kampf gegen Leschem, nahmen es ein und erschlugen seine Einwohner mit scharfem Schwert. Sie nahmen die Stadt in Besitz und blieben dort. Sie nannten Leschem von nun an Dan, nach dem Namen ihres Stammvaters Dan.", footnotes: ["Ri 18,27-29", "Text korr. (vgl. Ri 1,34); H: Das Gebiet . . . ging hinaus (ging verloren?)."] },
      { number: 48, text: "Das ist der Erbbesitz des Stammes der Daniter, entsprechend ihren Sippen: die Städte mit den dazugehörenden Gehöften." },
      { number: 49, text: "So vollendeten die Israeliten die Verteilung der Gebiete des Landes. Die Israeliten gaben auch Josua, dem Sohn Nuns, Erbbesitz in ihrer Mitte." },
      { number: 50, text: "Auf Befehl des Herrn gaben sie ihm die Stadt, die er sich erbeten hatte: Timnat-Serach im Gebirge Efraim. Er baute die Stadt wieder auf und wohnte dort." },
      { number: 51, text: "Das sind die erblichen Anteile, die der Priester Eleasar und Josua, der Sohn Nuns, und die Häupter der Großfamilien der Israeliten in Schilo vor den Augen des Herrn, am Eingang des Offenbarungszeltes, den Stämmen der Israeliten durch das Los zuteilten. Dann waren sie mit der Verteilung des Landes fertig." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Damals sagte der Herr zu Josua:", heading: "Die Asylstädte: 20,1-9", footnotes: ["(1-9) Num 35,9-29; Dtn 19,1-10"] },
      { number: 2, text: "Sag den Israeliten: Bestimmt die Asylstädte bei euch, von denen ich zu euch durch Mose gesprochen habe." },
      { number: 3, text: "Dorthin kann jeder fliehen, der getötet hat, das heißt, der versehentlich, ohne Vorsatz, einen Menschen erschlagen hat. Die Städte sollen euch als Asyl vor dem Bluträcher dienen." },
      { number: 4, text: "Wenn jemand in eine von diesen Städten flieht, soll er am Eingang des Stadttors stehen bleiben und den Ältesten der Stadt seine Sache vortragen. Sie sollen ihn zu sich in die Stadt aufnehmen und ihm eine Unterkunft geben, damit er bei ihnen bleiben kann." },
      { number: 5, text: "Wenn dann der Bluträcher ihn verfolgt, sollen sie ihm den, der getötet hat, nicht ausliefern; denn er hat seinen Nächsten versehentlich erschlagen und ohne ihn vorher gehasst zu haben." },
      { number: 6, text: "Er soll in dieser Stadt bleiben, bis er vor die Gemeinde zur Gerichtsverhandlung treten kann, bis zum Tod des Hohenpriesters, der in jenen Tagen im Amt ist. Dann darf der, der getötet hat, wieder in seine Stadt und in sein Haus zurückkehren - in die Stadt, aus der er geflohen ist." },
      { number: 7, text: "Man wählte also Kedesch in Galiläa, im Gebirge Naftali, aus, ferner Sichem im Gebirge Efraim und Kirjat-Arba, das ist Hebron, im Gebirge Juda.", footnotes: ["Man wählte: Text korr. (vgl. Num 35,11); H: Sie heiligten; wohl unter Einfluss des folgenden Namens Kedesch (Heiligtum)."] },
      { number: 8, text: "Jenseits des Jordan [östlich von Jericho] bestimmte man Bezer in der Steppe, in der Ebene, vom Stamm Ruben, Ramot in Gilead vom Stamm Gad und Golan im Baschan vom Stamm Manasse." },
      { number: 9, text: "Das waren die Asylstädte für alle Israeliten und für die Fremden, die sich bei ihnen aufhielten. Dorthin konnte jeder, der ohne Vorsatz einen Menschen erschlagen hatte, fliehen, um nicht durch die Hand des Bluträchers zu sterben, bevor er vor der Gemeinde gestanden hätte." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Die Familienoberhäupter der Leviten kamen in Schilo im Land Kanaan zu dem Priester Eleasar, zu Josua, dem Sohn Nuns, und zu den Stammeshäuptern der Israeliten", heading: "Die Städte für die Leviten: 21,1-42", footnotes: ["(1-3) 14,4; Num 35,1-8"] },
      { number: 2, text: "und sagten zu ihnen: Der Herr hat durch Mose befohlen, uns Städte zum Wohnen und ihre Weideflächen für unser Vieh zu geben." },
      { number: 3, text: "Da gaben die Israeliten den Leviten nach der Anordnung des Herrn aus ihrem Erbbesitz die folgenden Städte mit ihren Weideflächen." },
      { number: 4, text: "Als das Los auf die Sippen der Kehatiter fiel, erhielten unter den Leviten die Nachkommen des Priesters Aaron durch das Los dreizehn Städte vom Stamm Juda, vom Stamm der Simeoniter und vom Stamm Benjamin." },
      { number: 5, text: "Die übrigen Kehatiter erhielten, entsprechend ihren Sippen, durch das Los zehn Städte vom Stamm Efraim, vom Stamm Dan und vom halben Stamm Manasse.", footnotes: ["5f: entsprechend ihren Sippen, vom Stamm: Text korr. nach V. 7; H: von den Sippen des Stammes."] },
      { number: 6, text: "Die Nachkommen Gerschons erhielten, entsprechend ihren Sippen, durch das Los dreizehn Städte vom Stamm Issachar, vom Stamm Ascher, vom Stamm Naftali und vom halben Stamm Manasse im Baschan." },
      { number: 7, text: "Die Nachkommen Meraris erhielten, entsprechend ihren Sippen, zwölf Städte vom Stamm Ruben, vom Stamm Gad und vom Stamm Sebulon." },
      { number: 8, text: "Diese Städte mit ihren Weideflächen gaben die Israeliten durch das Los den Leviten, wie es der Herr durch Mose befohlen hatte." },
      { number: 9, text: "Vom Stamm der Judäer und vom Stamm der Simeoniter gaben sie ihnen die folgenden, namentlich genannten Städte.", footnotes: ["9-40: Die in 20,7-9 genannten Asylstädte sind auch Levitenstädte; darin besitzen die Leviten Wohn- und Weiderechte."] },
      { number: 10, text: "Sie wurden den Nachkommen Aarons unter den Kehatitern, unter den Leviten, zuteil; denn auf sie fiel das erste Los." },
      { number: 11, text: "Ihnen gab man die Stadt des Arba, des Vaters Anaks, also Hebron, im Gebirge Juda, mit seinen Weideflächen ringsum." },
      { number: 12, text: "Das Ackerland der Stadt aber und die dazugehörenden Gehöfte hatte man Kaleb, dem Sohn Jefunnes, als Besitz gegeben.", footnotes: ["15,13"] },
      { number: 13, text: "Den Nachkommen des Priesters Aaron gab man also die Asylstadt für solche, die einen Menschen erschlagen haben, Hebron mit seinen Weideflächen, ferner Libna mit seinen Weideflächen," },
      { number: 14, text: "Jattir mit seinen Weideflächen, Eschtemoa mit seinen Weideflächen," },
      { number: 15, text: "Holon mit seinen Weideflächen, Debir mit seinen Weideflächen," },
      { number: 16, text: "Ajin mit seinen Weideflächen, Jutta mit seinen Weideflächen und Bet-Schemesch mit seinen Weideflächen: neun Städte von den zwei Stämmen (den Judäern und den Simeonitern)." },
      { number: 17, text: "Vom Stamm Benjamin (gab man ihnen) Gibeon mit seinen Weideflächen, Geba mit seinen Weideflächen," },
      { number: 18, text: "Anatot mit seinen Weideflächen und Alemet mit seinen Weideflächen: vier Städte." },
      { number: 19, text: "Im Ganzen erhielten die Nachkommen Aarons, die Priester waren, dreizehn Städte mit ihren Weideflächen." },
      { number: 20, text: "Die Sippen der levitischen Kehatiter, die von den Nachkommen Kehats außerdem noch vorhanden waren, erhielten die Städte ihres Anteils vom Stamm Efraim." },
      { number: 21, text: "Man gab ihnen die Asylstädte für solche, die einen Menschen erschlagen haben, Sichem mit seinen Weideflächen im Gebirge Efraim, ferner Geser mit seinen Weideflächen," },
      { number: 22, text: "Kibzajim mit seinen Weideflächen und Bet-Horon mit seinen Weideflächen: vier Städte;" },
      { number: 23, text: "vom Stamm Dan erhielten sie Elteke mit seinen Weideflächen, Gibbeton mit seinen Weideflächen," },
      { number: 24, text: "Ajalon mit seinen Weideflächen und Gat-Rimmon mit seinen Weideflächen: vier Städte;" },
      { number: 25, text: "vom halben Stamm Manasse erhielten sie Taanach mit seinen Weideflächen und Gat-Rimmon mit seinen Weideflächen: zwei Städte." },
      { number: 26, text: "Im Ganzen erhielten die Sippen der übrigen Nachkommen Kehats zehn Städte." },
      { number: 27, text: "Den Gerschonitern aus den levitischen Sippen gab man vom halben Stamm Manasse die Asylstadt für solche, die einen Menschen erschlagen haben, Golan im Baschan mit seinen Weideflächen sowie Aschtarot mit seinen Weideflächen: zwei Städte;" },
      { number: 28, text: "vom Stamm Issachar erhielten sie Kischjon mit seinen Weideflächen, Daberat mit seinen Weideflächen," },
      { number: 29, text: "Jarmut mit seinen Weideflächen und En-Gannim mit seinen Weideflächen: vier Städte;" },
      { number: 30, text: "vom Stamm Ascher erhielten sie Mischal mit seinen Weideflächen, Abdon mit seinen Weideflächen," },
      { number: 31, text: "Helkat mit seinen Weideflächen und Rehob mit seinen Weideflächen: vier Städte;" },
      { number: 32, text: "vom Stamm Naftali erhielten sie die Asylstadt für solche, die einen Menschen erschlagen haben, Kedesch in Galiläa mit seinen Weideflächen sowie Hammot-Dor mit seinen Weideflächen und Kartan mit seinen Weideflächen: drei Städte." },
      { number: 33, text: "Im Ganzen erhielten die Gerschoniter, entsprechend ihren Sippen, dreizehn Städte mit ihren Weideflächen." },
      { number: 34, text: "Den übrigen Leviten, also den Sippen der Nachkommen Meraris, gab man vom Stamm Sebulon Jokneam mit seinen Weideflächen, Karta mit seinen Weideflächen," },
      { number: 35, text: "Dimna mit seinen Weideflächen und Nahalal mit seinen Weideflächen: vier Städte;" },
      { number: 36, text: "dazu vom Stamm Ruben Bezer mit seinen Weideflächen, Jahaz mit seinen Weideflächen," },
      { number: 37, text: "Kedemot mit seinen Weideflächen und Mefaat mit seinen Weideflächen: vier Städte;" },
      { number: 38, text: "vom Stamm Gad erhielten sie die Asylstadt für solche, die einen Menschen erschlagen haben, Ramot-Gilead mit seinen Weideflächen, ferner Mahanajim mit seinen Weideflächen," },
      { number: 39, text: "Heschbon mit seinen Weideflächen und Jaser mit seinen Weideflächen: zusammen vier Städte." },
      { number: 40, text: "Im Ganzen erhielten die Merariter entsprechend ihren Sippen, die unter den Sippen der Leviten noch übrig waren, als ihren Anteil zwölf Städte." },
      { number: 41, text: "Die Gesamtanzahl der Levitenstädte mitten im Besitz der Israeliten betrug achtundvierzig, mit ihren Weideflächen." },
      { number: 42, text: "Diese Städte bestanden jeweils aus der Stadt und den Weideflächen in ihrer Umgebung; so war es bei allen diesen Städten." },
      { number: 43, text: "So gab der Herr Israel das ganze Land, das er ihren Vätern mit einem Eid zugesichert hatte. Sie nahmen es in Besitz und wohnten darin.", heading: "Die Ruhe in der neu gewonnenen Heimat: 21,43 - 22,9" },
      { number: 44, text: "Und der Herr verschaffte ihnen Ruhe ringsum, genau so, wie er es ihren Vätern mit einem Eid zugesichert hatte. Keiner von all ihren Feinden konnte ihnen Widerstand leisten; alle ihre Feinde gab der Herr in ihre Gewalt." },
      { number: 45, text: "Keine von all den Zusagen, die der Herr dem Haus Israel gegeben hatte, war ausgeblieben; jede war in Erfüllung gegangen.", footnotes: ["war ausgeblieben, wörtlich: war dahingefallen (vgl. 23,14)."] },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Damals rief Josua die Rubeniter, die Gaditer und den halben Stamm Manasse zu sich", footnotes: ["Num 32,20-22; Dtn 3,18-20"] },
      { number: 2, text: "und sagte zu ihnen: Ihr habt alles befolgt, was euch Mose, der Knecht des Herrn, befohlen hat; ihr habt auch mir in allem gehorcht, was ich euch befohlen habe." },
      { number: 3, text: "Ihr habt eure Brüder während dieser langen Zeit bis heute nicht im Stich gelassen und habt darauf geachtet, den Befehl des Herrn, eures Gottes, zu befolgen." },
      { number: 4, text: "Nun hat der Herr, euer Gott, euren Brüdern Ruhe verschafft, wie er es ihnen versprochen hatte. Kehrt also nun wieder zu euren Zelten zurück, in das Land, das euch gehört und das euch Mose, der Knecht des Herrn, jenseits des Jordan gegeben hat." },
      { number: 5, text: "Achtet aber genau darauf, das Gebot und das Gesetz zu erfüllen, das euch Mose, der Knecht des Herrn, gegeben hat: den Herrn, euren Gott, zu lieben, auf allen seinen Wegen zu gehen, seine Gebote zu halten, euch ihm anzuschließen und ihm von ganzem Herzen und ganzer Seele zu dienen." },
      { number: 6, text: "Dann segnete sie Josua und ließ sie ziehen und sie begaben sich zu ihren Zelten." },
      { number: 7, text: "Der einen Hälfte des Stammes Manasse hatte Mose im Baschan (Land) gegeben, der anderen Hälfte gab Josua bei ihren Brüdern (Land) auf der anderen Seite des Jordan im Westen. Als Josua sie nun wieder zu ihren Zelten ziehen ließ und sie segnete," },
      { number: 8, text: "sagte er zu ihnen: Mit reichen Schätzen kehrt ihr zu euren Zelten zurück, mit sehr viel Vieh, mit Silber und Gold, mit Bronze, Eisen und sehr vielen Gewändern. Teilt die Beute, die ihr euren Feinden abgenommen habt, mit euren Brüdern." },
      { number: 9, text: "So verließen die Rubeniter, die Gaditer und der halbe Stamm Manasse die Israeliten und zogen von Schilo im Land Kanaan nach Gilead, in das Land, das ihnen gehörte und das sie auf den durch Mose ergangenen Befehl des Herrn hin in Besitz genommen hatten." },
      { number: 10, text: "Als sie zu den Steinkreisen am Jordan kamen, die noch im Land Kanaan liegen, errichteten die Rubeniter, die Gaditer und der halbe Stamm Manasse dort am Jordan einen Altar von stattlichem Aussehen.", heading: "Der Altar am Jordan: 22,10-34", footnotes: ["10-34: Die Kritik an dem Altar geht von dem Grundsatz aus, dass es nur ein einziges Heiligtum in Israel geben darf (vgl. Dtn 12,4-7)."] },
      { number: 11, text: "Da erhielten die Israeliten folgende Nachricht: Schaut, die Rubeniter, die Gaditer und der halbe Stamm Manasse haben gegenüber dem Land Kanaan bei den Steinkreisen am Jordan jenseits des Gebiets der Israeliten einen Altar errichtet." },
      { number: 12, text: "Als die Israeliten das hörten, versammelte sich die ganze Gemeinde Israels in Schilo, um gegen sie ins Feld zu ziehen." },
      { number: 13, text: "Die Israeliten schickten den Priester Pinhas, den Sohn Eleasars, zu den Rubenitern, den Gaditern und zum halben Stamm Manasse in Gilead." },
      { number: 14, text: "Mit ihm gingen zehn Anführer [aus jeder Familie], aus jedem Stamm Israels je ein Anführer; jeder gehörte zu den Familienoberhäuptern der Tausendschaften Israels." },
      { number: 15, text: "Als sie zu den Rubenitern, den Gaditern und dem halben Stamm Manasse ins Land Gilead kamen, sagten sie zu ihnen:" },
      { number: 16, text: "So hat die ganze Gemeinde des Herrn gesagt: Was soll denn dieser Treubruch bedeuten, den ihr gegen den Gott Israels begeht, indem ihr euch heute vom Herrn abwendet, indem ihr euch einen Altar errichtet und euch heute dadurch gegen den Herrn auflehnt?" },
      { number: 17, text: "Ist uns der Frevel von Pegor zu wenig? Von ihm haben wir uns bis heute noch nicht gereinigt und seinetwegen ist die Plage über die Gemeinde des Herrn gekommen.", footnotes: ["Num 25"] },
      { number: 18, text: "Ihr aber wendet euch heute vom Herrn ab. Wenn ihr euch heute gegen den Herrn auflehnt, wird sich sein Zorn morgen gegen die ganze Gemeinde Israels richten." },
      { number: 19, text: "Falls das Land, das euch gehört, unrein ist, dann kommt herüber in das Land, das dem Herrn gehört und wo die Wohnstätte des Herrn ist, und siedelt euch bei uns an! Aber lehnt euch nicht gegen den Herrn auf und zieht uns nicht dadurch in euren Aufruhr hinein, dass ihr für euch selber einen zweiten Altar, neben dem Altar des Herrn, unseres Gottes, baut.", footnotes: ["Gott hat sein Heiligtum im Westjordanland. Wenn das Land östlich des Jordan als unrein betrachtet wird, ist die Verehrung Gottes dort nicht möglich. - zieht uns . . . hinein: Text korr."] },
      { number: 20, text: "Ist nicht sein Zorn über die ganze Gemeinde Israels gekommen, als sich Achan, der Sohn Serachs, treulos an dem Gut vergriffen hatte, das dem Untergang geweiht war? Und er war nur ein einzelner Mann. Musste er nicht für seinen Frevel sterben?", footnotes: ["7,1-26"] },
      { number: 21, text: "Darauf antworteten die Rubeniter, die Gaditer und der halbe Stamm Manasse den Befehlshabern der Tausendschaften Israels:" },
      { number: 22, text: "Gott, ja Gott, der Herr - Gott, ja Gott, der Herr, er weiß es und Israel soll es wissen: Wenn das Untreue, wenn das Auflehnung gegen den Herrn war, dann soll er uns heute nicht helfen.", footnotes: ["soll er uns nicht helfen: Text korr., mit den alten Übersetzungen; H: hilf du uns nicht!"] },
      { number: 23, text: "Ob wir uns einen Altar errichtet haben, um uns vom Herrn abzuwenden, und ob wir auf ihm Brandopfer und Speiseopfer darbringen und Heilsopfer herrichten wollten, möge der Herr selbst untersuchen." },
      { number: 24, text: "Nein, wir haben das nur aus Sorge getan, und zwar aus folgender Überlegung: Eure Söhne könnten morgen zu unseren Söhnen sagen: Was habt ihr mit dem Herrn, dem Gott Israels, zu tun?" },
      { number: 25, text: "Als Grenze hat der Herr den Jordan zwischen uns und euch, das heißt den Rubenitern und Gaditern, gelegt. Ihr habt keinen Anteil am Herrn. So könnten eure Söhne unsere Söhne davon abhalten, den Herrn zu fürchten." },
      { number: 26, text: "Deshalb sagten wir uns: Wir wollen einen Altar errichten, nicht für Brandopfer und nicht für Schlachtopfer;" },
      { number: 27, text: "er soll vielmehr ein Zeuge sein zwischen uns und euch und zwischen den Generationen nach uns dafür, dass wir den Dienst vor dem Angesicht des Herrn durch Brandopfer, Schlachtopfer und Heilsopfer verrichten dürfen. Dann können eure Söhne morgen nicht unseren Söhnen sagen: Ihr habt keinen Anteil am Herrn." },
      { number: 28, text: "Wir dachten: Wenn sie morgen so zu uns und unseren Nachkommen sprechen, werden wir zu ihnen sagen: Seht euch doch die Gestalt des Altares des Herrn an, den unsere Väter errichtet haben, nicht für Brandopfer und nicht für Schlachtopfer, sondern damit er Zeuge zwischen uns und euch sei." },
      { number: 29, text: "Es sei fern von uns, dass wir uns gegen den Herrn auflehnen und uns heute von ihm abwenden, indem wir einen zweiten Altar für Brandopfer, Speiseopfer und Schlachtopfer neben dem Altar des Herrn, unseres Gottes, errichten, der vor seiner Wohnstätte steht." },
      { number: 30, text: "Als der Priester Pinhas und die Oberhäupter der Gemeinde [die Befehlshaber der Tausendschaften Israels], die bei ihm waren, hörten, was die Rubeniter, die Gaditer und die Manassiter sagten, waren sie damit einverstanden." },
      { number: 31, text: "Und der Priester Pinhas, der Sohn Eleasars, sagte zu den Rubenitern, den Gaditern und den Manassitern: Jetzt wissen wir, dass der Herr mitten unter uns ist; denn ihr habt keinen solchen Treubruch gegen den Herrn begangen. Dadurch habt ihr die Israeliten vor der (strafenden) Hand des Herrn bewahrt." },
      { number: 32, text: "Dann verließen der Priester Pinhas, der Sohn Eleasars, und die Oberhäupter die Rubeniter und Gaditer und kehrten aus dem Land Gilead ins Land Kanaan zu den Israeliten zurück. Sie berichteten ihnen alles" },
      { number: 33, text: "und die Israeliten waren damit einverstanden. Sie priesen Gott und dachten nicht mehr daran, gegen die Rubeniter und Gaditer ins Feld zu ziehen und das Land zu verwüsten, in dem sie wohnten." },
      { number: 34, text: "Die Rubeniter und Gaditer nannten den Altar Zeuge, denn (sie sagten): Er steht mitten unter uns als Zeuge dafür, dass Jahwe Gott ist.", footnotes: ["Zeuge: Text korr., eingefügt mit einigen H-Handschriften und S (vgl. V. 27)."] },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Lange Zeit später, nachdem der Herr Israel Ruhe vor all seinen Feinden ringsum verschafft hatte und Josua alt und betagt geworden war,", heading: "Die Verpflichtung auf Jahwe: 23,1 - 24,33 | Josuas Rede an das Volk: 23,1-16", footnotes: ["1-16: Die erste Abschiedsrede Josuas (vgl. 23,2 mit 24,1) ist in Sprache, Stil und Gedankenführung des Dtn gehalten."] },
      { number: 2, text: "rief Josua ganz Israel, seine Ältesten und Häupter, seine Richter und Listenführer zusammen und sagte zu ihnen: Ich bin nun alt und betagt." },
      { number: 3, text: "Ihr habt selbst alles gesehen, was der Herr, euer Gott, mit all diesen Völkern vor euren Augen gemacht hat; denn der Herr, euer Gott, hat selbst für euch gekämpft." },
      { number: 4, text: "Seht, ich habe euch die Gebiete dieser Völker, die noch übrig geblieben sind, und all der Völker, die ich vernichtet habe, vom Jordan bis zum großen Meer im Westen entsprechend euren Stämmen als Erbbesitz zugeteilt." },
      { number: 5, text: "Der Herr, euer Gott, wird sie (auch künftig) vor euren Augen verjagen und sie vor euren Augen vertreiben und ihr werdet ihr Land in Besitz nehmen, wie es euch der Herr, euer Gott, versprochen hat." },
      { number: 6, text: "Haltet aber immer daran fest, alles zu beachten und zu tun, was im Gesetzbuch des Mose geschrieben steht; weicht nicht nach rechts oder links davon ab!" },
      { number: 7, text: "Vermischt euch nicht mit diesen Völkern, die bei euch noch übrig geblieben sind; den Namen ihrer Götter sollt ihr nicht nennen und nicht bei ihnen schwören, ihr sollt ihnen nicht dienen und euch nicht vor ihnen niederwerfen," },
      { number: 8, text: "sondern am Herrn, eurem Gott, fest halten, wie ihr es bis heute getan habt." },
      { number: 9, text: "Der Herr hat vor euren Augen große und mächtige Völker vertrieben; bis heute konnte niemand euch standhalten." },
      { number: 10, text: "Ein Einziger von euch kann tausend verfolgen; denn der Herr, euer Gott, kämpft selbst für euch, wie er es euch versprochen hat." },
      { number: 11, text: "Achtet darum um eures Lebens willen sehr darauf, dass ihr (immer) den Herrn, euren Gott, liebt." },
      { number: 12, text: "Denn wenn ihr euch wirklich von ihm abwendet und euch diesen Völkern, die bei euch noch übrig geblieben sind, anschließt, wenn ihr euch mit ihnen verschwägert, wenn ihr euch mit ihnen vermischt und sie sich mit euch vermischen," },
      { number: 13, text: "dann könnt ihr gewiss sein, dass der Herr, euer Gott, diese Völker nicht mehr vor euren Augen vertreiben wird. Sie werden zur Schlinge und zur Falle für euch, zur Peitsche für euren Rücken und zum Stachel in euren Augen, bis ihr aus diesem schönen Land verschwindet, das der Herr, euer Gott, euch gegeben hat.", footnotes: ["Num 33,55; Ri 2,3"] },
      { number: 14, text: "Ich selbst muss heute den Weg alles Irdischen gehen. Ihr aber sollt mit ganzem Herzen und ganzer Seele erkennen, dass von all den Zusagen, die der Herr, euer Gott, euch gegeben hat, keine einzige ausgeblieben ist; alle sind sie eingetroffen, keine einzige von ihnen ist ausgeblieben.", footnotes: ["ausgeblieben ist: vgl. die Anmerkung zu 21,45."] },
      { number: 15, text: "Wie aber bisher jede Zusage, die der Herr, euer Gott, euch gegeben hat, eingetroffen ist, so wird der Herr, euer Gott, künftig jede Drohung gegen euch verwirklichen, bis er euch aus diesem schönen Land hinweggerafft hat, das euch der Herr, euer Gott, gegeben hat." },
      { number: 16, text: "Wenn ihr euch gegen den Bund, auf den der Herr, euer Gott, euch verpflichtet hat, vergeht, wenn ihr anderen Göttern dient und sie anbetet, dann wird der Zorn des Herrn gegen euch entbrennen und ihr werdet rasch aus dem schönen Land verschwinden, das er euch gegeben hat." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Josua versammelte alle Stämme Israels in Sichem; er rief die Ältesten Israels, seine Oberhäupter, Richter und Listenführer zusammen und sie traten vor Gott hin.", heading: "Die Versammlung des Volkes in Sichem: 24,1-28", footnotes: ["1-28: Der sog. Landtag zu Sichem ist eine Versammlung der Stämme Israels. Vor ihnen hält Josua eine zweite Abschiedsrede. Auch darin klingen deuteronomische Gedanken an."] },
      { number: 2, text: "Josua sagte zum ganzen Volk: So spricht der Herr, der Gott Israels: Jenseits des Stroms wohnten eure Väter von Urzeiten an [Terach, der Vater Abrahams und der Vater Nahors] und dienten anderen Göttern.", footnotes: ["Gen 11,26"] },
      { number: 3, text: "Da holte ich euren Vater Abraham von jenseits des Stroms und ließ ihn durch das ganze Land Kanaan ziehen. Ich schenkte ihm zahlreiche Nachkommenschaft und gab ihm Isaak.", footnotes: ["Gen 12 - 24"] },
      { number: 4, text: "Dem Isaak gab ich Jakob und Esau und ich verlieh Esau das Bergland Seïr, damit er es in Besitz nahm. Jakob aber und seine Söhne zogen nach Ägypten hinab.", footnotes: ["Gen 25,19-26.27; 32,4; 36,1-8; 46,6"] },
      { number: 5, text: "Dann sandte ich Mose und Aaron und strafte Ägypten durch das, was ich in Ägypten tat. Danach habe ich euch herausgeführt", footnotes: ["Ex 3 - 15"] },
      { number: 6, text: "[ich führte eure Väter heraus] aus Ägypten und ihr seid ans Meer gekommen. Die Ägypter aber verfolgten eure Väter mit Wagen und Pferden bis zum Schilfmeer.", footnotes: ["Ex 14"] },
      { number: 7, text: "Da schrien eure Väter zum Herrn und er legte zwischen euch und die Ägypter eine Finsternis und ließ das Meer über sie kommen, sodass es sie überflutete. Mit eigenen Augen habt ihr gesehen, was ich in Ägypten getan habe. Dann habt ihr euch lange in der Wüste aufgehalten." },
      { number: 8, text: "Ich brachte euch in das Land der Amoriter, die jenseits des Jordan wohnten. Sie kämpften mit euch, aber ich gab sie in eure Gewalt; ihr habt ihr Land in Besitz genommen und ich habe sie euretwegen vernichtet.", footnotes: ["Num 21,25.31"] },
      { number: 9, text: "Dann erhob sich der König Balak von Moab, der Sohn Zippors, und kämpfte gegen Israel. Er schickte Boten zu Bileam, dem Sohn Beors, und ließ ihn rufen, damit er euch verflucht.", footnotes: ["(9-10) Num 22-24; Ri 11,25"] },
      { number: 10, text: "Ich aber wollte keinen Fluch von Bileam hören. Darum musste er euch segnen und ich rettete euch aus seiner Gewalt.", footnotes: ["Ich wollte keinen Fluch von Bileam hören, wörtlich: Ich wollte nicht auf Bileam hören."] },
      { number: 11, text: "Dann habt ihr den Jordan durchschritten und seid nach Jericho gekommen; die Bürger von Jericho kämpften gegen euch, ebenso die Amoriter, die Perisiter, die Kanaaniter, die Hetiter, die Girgaschiter, die Hiwiter und die Jebusiter und ich gab sie in eure Gewalt.", footnotes: ["3,16f; 6,1-11"] },
      { number: 12, text: "Ich habe Panik vor euch hergeschickt. Sie trieb sie vor euch her [die beiden Könige der Amoriter]; das geschah nicht durch dein Schwert und deinen Bogen.", footnotes: ["10,1f; Ex 23,27f; Dtn 7,20", "Der Zusatz passt besser zu V. 8."] },
      { number: 13, text: "Ich gab euch ein Land, um das ihr euch nicht bemüht hattet, und Städte, die ihr nicht erbaut hattet. Ihr habt in ihnen gewohnt und ihr habt von Weinbergen und Ölbäumen gegessen, die ihr nicht gepflanzt hattet.", footnotes: ["Dtn 6,10f"] },
      { number: 14, text: "Fürchtet also jetzt den Herrn und dient ihm in vollkommener Treue. Schafft die Götter fort, denen eure Väter jenseits des Stroms und in Ägypten gedient haben, und dient dem Herrn!" },
      { number: 15, text: "Wenn es euch aber nicht gefällt, dem Herrn zu dienen, dann entscheidet euch heute, wem ihr dienen wollt: den Göttern, denen eure Väter jenseits des Stroms dienten, oder den Göttern der Amoriter, in deren Land ihr wohnt. Ich aber und mein Haus, wir wollen dem Herrn dienen." },
      { number: 16, text: "Das Volk antwortete: Das sei uns fern, dass wir den Herrn verlassen und anderen Göttern dienen." },
      { number: 17, text: "Denn der Herr, unser Gott, war es, der uns und unsere Väter aus dem Sklavenhaus Ägypten herausgeführt hat und der vor unseren Augen alle die großen Wunder getan hat. Er hat uns beschützt auf dem ganzen Weg, den wir gegangen sind, und unter allen Völkern, durch deren Gebiet wir gezogen sind." },
      { number: 18, text: "Der Herr hat alle Völker vertrieben, auch die Amoriter, die vor uns im Land wohnten. Auch wir wollen dem Herrn dienen; denn er ist unser Gott." },
      { number: 19, text: "Da sagte Josua zum Volk: Ihr seid nicht imstande, dem Herrn zu dienen, denn er ist ein heiliger Gott, ein eifersüchtiger Gott; er wird euch eure Frevel und eure Sünden nicht verzeihen.", footnotes: ["Ex 20,5"] },
      { number: 20, text: "Wenn ihr den Herrn verlasst und fremden Göttern dient, dann wird er sich von euch abwenden, wird Unglück über euch bringen und euch ein Ende bereiten, obwohl er euch zuvor Gutes getan hat." },
      { number: 21, text: "Das Volk aber sagte zu Josua: Nein, wir wollen dem Herrn dienen." },
      { number: 22, text: "Josua antwortete dem Volk: Ihr seid Zeugen gegen euch selbst, dass ihr euch für den Herrn und für seinen Dienst entschieden habt. [Sie antworteten: Das sind wir.]" },
      { number: 23, text: "Schafft also jetzt die fremden Götter ab, die noch bei euch sind, und neigt eure Herzen dem Herrn zu, dem Gott Israels!" },
      { number: 24, text: "Das Volk sagte zu Josua: Dem Herrn, unserem Gott, wollen wir dienen und auf seine Stimme hören." },
      { number: 25, text: "So schloss Josua an jenem Tag einen Bund für das Volk und gab dem Volk Gesetz und Recht in Sichem.", footnotes: ["25-28: Josua tritt als Bundesmittler auf. Sichem ist ein altes Heiligtum, das bereits in der Patriarchenzeit eine Rolle spielt."] },
      { number: 26, text: "Josua schrieb alle diese Worte in das Buch des Gesetzes Gottes und er nahm einen großen Stein und stellte ihn in Sichem unter der Eiche auf, die im Heiligtum des Herrn steht.", footnotes: ["Gen 35,4; Ri 9,6"] },
      { number: 27, text: "Dabei sagte er zu dem ganzen Volk: Seht her, dieser Stein wird ein Zeuge sein gegen uns; denn er hat alle Worte des Herrn gehört, die er zu uns gesprochen hat. Er soll ein Zeuge sein gegen euch, damit ihr euren Gott nicht verleugnet." },
      { number: 28, text: "Dann entließ Josua das Volk, einen jeden in seinen Erbbesitz." },
      { number: 29, text: "Nach diesen Ereignissen starb Josua, der Sohn Nuns, der Knecht des Herrn, im Alter von hundertzehn Jahren.", heading: "Die Grabstätten Josuas, Josefs und Eleasars: 24,29-33", footnotes: ["Ri 2,8f"] },
      { number: 30, text: "Man begrub ihn in Timnat-Serach, im Gebiet seines Erbbesitzes auf dem Gebirge Efraim, nördlich vom Berg Gaasch." },
      { number: 31, text: "Israel aber diente dem Herrn, solange Josua lebte und solange die Ältesten am Leben waren, die Josua überlebten und alles wussten, was der Herr für Israel getan hatte.", footnotes: ["Ri 2,7"] },
      { number: 32, text: "Die Gebeine Josefs, die die Israeliten aus Ägypten mitgebracht hatten, begrub man in Sichem auf dem Grundstück, das Jakob von den Söhnen Hamors, des Vaters Sichems, für hundert Kesita erworben hatte; es war den Nachkommen Josefs als Erbbesitz zuteil geworden.", footnotes: ["Gen 50,25; Gen 33,19"] },
      { number: 33, text: "Auch Eleasar, der Sohn Aarons, starb und man begrub ihn in Gibea, (der Stadt) seines Sohnes Pinhas, die man ihm im Gebirge Efraim gegeben hatte.", footnotes: ["Dtn 10,6"] },
    ] },
  ],
};
