import { Book } from "@/lib/types";

export const leviticus: Book = {
  id: "leviticus",
  name: "Levitikus",
  shortName: "Lev",
  testament: "old",
  introduction: "Das Buch trägt in der griechischen und lateinischen Bibel den Namen Levitikus, weil es fast ganz aus Vorschriften für den Kult und für die Priester aus dem Stamm Levi besteht. Die Juden nennen es nach den Anfangsworten Wajjikrá (Und er rief). Das ganze Buch gehört der priesterlichen Überlieferungsschicht (P) an. Es enthält nur zwei kurze erzählende Abschnitte: über den Tod der Aaron-Söhne Nadab und Abihu (10,1-5) und über die Steinigung eines Gotteslästerers (24,10-15). Die Vorschriften dieses Buches werden so in die Geschichtsdarstellung des Pentateuch eingefügt, dass sie als Anordnungen Gottes während des Sinai-Aufenthalts erscheinen. Die Gliederung des Stoffes ist nicht leicht überschaubar. Lev 8 10 greift auf Ex 40 (Einsetzung der Priester aus dem Stamm Levi) zurück. In Kap. 1 - 7 stehen Anweisungen für verschiedene Opfer; in Kap. 11 - 15 folgen Vorschriften über die kultische Reinheit; Kap. 16 ist das Ritual für den Versöhnungstag. In Kap. 17 - 26 folgt das sogenannte Heiligkeitsgesetz, eine Gesetzessammlung, die in ihrem Grundbestand alte Vorschriften ethischer und kultischer Art enthält und wie altorientalische Gesetzbücher mit Segenszusagen und Fluchandrohungen schließt. Ein Nachtrag (Kap. 27) regelt die Ablösung von Gelübden und Zehnten durch Geld. Dieser ganze Stoff wurde erst nach dem Babylonischen Exil in seine jetzige Gestalt gebracht; das Buch enthält aber vieles, was bereits lange in der Praxis der Priester und in der zur Unterweisung der Priester dienenden Tradition vorlag. Das alles wird unter die Autorität des Mose und des Bundesgesetzes vom Sinai gestellt und daher Mose in den Mund gelegt. Die für uns teilweise befremdlichen Riten und Vorschriften, die die sogenannte kultische Reinheit regeln und die »Heiligkeit« Israels garantieren sollen, erklären sich daraus, dass die altorientalischen Völker keine scharfe Trennung zwischen Alltagsleben und Religion kannten. Dem heiligen, von aller Befleckung freien Gott dürfen nur Menschen dienen, die sich von ethischen Vergehen freihalten, aber auch die Regeln des Anstands und der Hygiene beachten. Sie müssen sich außerdem von allem fern halten, was an heidnische Bräuche erinnert; dazu gehören der Genuss von bei heidnischen Opfern geschlachteten Tieren und die meisten Trauerriten. Darum machen bestimmte Tiere und Speisen und alles, was mit dem Bereich des Todes zu tun hat, z. B. Leichen und Aas, »unrein«. Wer sich gegen solche Reinheitsbestimmungen vergangen hat, und sei es nur unbeabsichtigt, muss sich bestimmten Reinigungsriten unterziehen und Schuld- oder Sündopfer darbringen, wenn er wieder am Gottesdienst teilnehmen will. Wenn auch diese Vorschriften durch das Neue Testament überholt sind, behält das Buch Levitikus seine Bedeutung für die Kenntnis des Judentums und für das Verständnis des Neuen Testaments. Durch die strenge Befolgung der levitischen Gesetze hat das Judentum bis heute seine Identität bewahrt und ist nicht im Völkergemisch des Mittelmeerraums aufgegangen.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Der Herr rief Mose, redete ihn vom Offenbarungszelt aus an und sprach:", heading: "Die Opfervorschriften für das Volk: 1,1 - 5,26 | Das Brandopfer: 1,1-17", footnotes: ["1-17: Brandopfer sind Tieropfer, bei denen das ganze Tier auf dem Altar verbrannt wird, im Unterschied zu anderen Opfern, bei denen nur ein Teil verbrannt, der andere Teil aber den Priestern übergeben oder von der Gemeinschaft, die das Opfer stiftet, beim Opfermahl verzehrt wird."] },
      { number: 2, text: "Rede zu den Israeliten und sag zu ihnen: Wenn einer von euch dem Herrn von den Haustieren eine Opfergabe darbringt, könnt ihr das mit Rind und Kleinvieh tun." },
      { number: 3, text: "Ist seine Opfergabe ein Brandopfer vom Rind, so bringe er ein männliches Tier ohne Fehler dar; er soll es an den Eingang des Offenbarungszeltes bringen, damit es vor dem Herrn Annahme findet." },
      { number: 4, text: "Er lege seine Hand auf den Kopf des Opfertiers, damit es für ihn angenommen werde, um ihn zu entsühnen." },
      { number: 5, text: "Er soll dann den Stier vor dem Herrn schlachten und die Söhne Aarons, die Priester, sollen das Blut darbringen. Sie sollen es ringsum an den Altar sprengen, der am Eingang des Offenbarungszeltes steht." },
      { number: 6, text: "Dann soll er das Opfer abhäuten und es in Stücke zerlegen." },
      { number: 7, text: "Die Söhne Aarons, die Priester, sollen Feuer auf den Altar bringen und Holz darauf schichten." },
      { number: 8, text: "Hierauf sollen die Söhne Aarons, die Priester, die Stücke sowie den Kopf und das Fett auf das Holz über dem Altarfeuer legen." },
      { number: 9, text: "Der Priester soll dann die Eingeweide und die Beine mit Wasser waschen und das Ganze auf dem Altar in Rauch aufgehen lassen. Ein Brandopfer ist es, ein Feueropfer zum beruhigenden Duft für den Herrn.", footnotes: ["Feueropfer sind alle Opfer, bei denen die Opfergabe ganz oder teilweise verbrannt wird. - Zum «beruhigenden Duft» vgl. die Anmerkung zu Gen 8,21."] },
      { number: 10, text: "Ist seine Opfergabe ein Brandopfer vom Kleinvieh, von den Schafen oder Ziegen, dann soll er ein fehlerloses männliches Tier bringen." },
      { number: 11, text: "Er soll es an der Nordseite des Altars vor dem Herrn schlachten und die Söhne Aarons, die Priester, sollen sein Blut ringsum an den Altar sprengen." },
      { number: 12, text: "Dann soll der Priester es in Stücke zerlegen und diese sowie den Kopf und das Fett auf das Holz über dem Altarfeuer legen." },
      { number: 13, text: "Er soll dann die Eingeweide und die Beine mit Wasser waschen und der Priester soll das Ganze darbringen und auf dem Altar in Rauch aufgehen lassen. Ein Brandopfer ist es, ein Feueropfer zum beruhigenden Duft für den Herrn." },
      { number: 14, text: "Ist seine Opfergabe für den Herrn ein Brandopfer vom Geflügel, dann soll er eine Turteltaube oder eine junge Taube bringen." },
      { number: 15, text: "Der Priester soll sie zum Altar bringen, ihren Kopf abtrennen und ihn auf dem Altar in Rauch aufgehen lassen; ihr Blut soll gegen die Altarwand ausgepresst werden." },
      { number: 16, text: "Dann soll er ihren Kropf mit den Federn entfernen und ihn an der Ostseite des Altars auf den Platz der Fett-Asche werfen." },
      { number: 17, text: "Darauf soll der Priester den Vogel an den Flügeln einreißen, ohne ihn dabei zu teilen, und ihn auf dem Altar, auf dem Holz über dem Feuer, in Rauch aufgehen lassen. Ein Brandopfer ist es, ein Feueropfer zum beruhigenden Duft für den Herrn." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Wenn jemand dem Herrn ein Speiseopfer darbringt, muss seine Opfergabe aus Feinmehl bestehen, auf das er Öl gießen und Weihrauch legen soll.", heading: "Das Speiseopfer: 2,1-16" },
      { number: 2, text: "Er bringe es zu den Söhnen Aarons, den Priestern. Er nehme davon eine Hand voll, vom Feinmehl und vom Öl mit dem ganzen Weihrauch, und der Priester lasse sie auf dem Altar als Gedächtnisanteil in Rauch aufgehen, als ein Feueropfer zum beruhigenden Duft für den Herrn.", footnotes: ["Gedächtnisanteil: der Teil des Opfers, der Gott dazu bewegen soll, an den Opfernden zu denken."] },
      { number: 3, text: "Der Rest des Speiseopfers gehört Aaron und seinen Söhnen als etwas Hochheiliges von den Feueropfern des Herrn." },
      { number: 4, text: "Wenn du ein im Ofen gebackenes Speiseopfer darbringst, so soll es aus Feinmehl sein, ein mit Öl vermengter ungesäuerter Kuchen und ein mit Öl bestrichener ungesäuerter Brotfladen." },
      { number: 5, text: "Ist deine Opfergabe eine auf einer Ofenplatte zubereitete Speise, so soll sie aus ungesäuertem Feinmehl sein, das mit Öl vermengt ist." },
      { number: 6, text: "Du sollst sie zerbröckeln und Öl darüber gießen. Ein Speiseopfer ist es." },
      { number: 7, text: "Ist deine Opfergabe eine im Kochtopf zubereitete Speise, so soll sie aus Feinmehl mit Öl angemacht sein." },
      { number: 8, text: "Du sollst die daraus zubereitete Speise zum Herrn bringen. Man soll sie dem Priester reichen, der sie an den Altar bringt." },
      { number: 9, text: "Der Priester soll davon den Gedächtnisanteil abheben und auf dem Altar in Rauch aufgehen lassen als ein Feueropfer zum beruhigenden Duft für den Herrn." },
      { number: 10, text: "Der Rest des Speiseopfers gehört Aaron und seinen Söhnen als etwas Hochheiliges von den Feueropfern des Herrn." },
      { number: 11, text: "Kein Speiseopfer, das ihr dem Herrn darbringt, darf mit Sauerteig angemacht sein; denn ihr dürft dem Herrn weder Sauerteig noch Honig als Feueropfer darbringen." },
      { number: 12, text: "Ihr sollt sie dem Herrn als Erstlingsopfer darbringen, aber auf dem Altar sollen sie nicht zum beruhigenden Duft aufsteigen." },
      { number: 13, text: "Jedes Speiseopfer sollst du salzen und deinem Speiseopfer sollst du das Salz des Bundes deines Gottes nicht fehlen lassen; jede deiner Opfergaben sollst du mit Salz darbringen.", footnotes: ["Salz des Bundes: Salz gehört zum Mahl und darum auch zum Opfer, das den Bund zwischen Gott und Volk bewusst macht (vgl. Num 18,19 und 2 Chr 13,5)."] },
      { number: 14, text: "Wenn du dem Herrn ein Speiseopfer von den Erstlingsfrüchten darbringst, sollst du geröstete reife Körner und Grütze aus frischen Körnern als Speiseopfer deiner Erstlingsfrüchte darbringen." },
      { number: 15, text: "Du sollst Öl darauf gießen und Weihrauch darauf geben. Ein Speiseopfer ist es." },
      { number: 16, text: "Der Priester soll den Gedächtnisanteil mit einem Teil der Grütze und des Öls mit dem ganzen Weihrauch als ein Feueropfer für den Herrn in Rauch aufgehen lassen." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Ist seine Opfergabe ein Heilsopfer und vom Rind, dann soll er ein männliches oder ein weibliches fehlerloses Tier vor den Herrn bringen.", heading: "Das Heilsopfer: 3,1-17", footnotes: ["1-17: Bei Heilsopfern oder, wie manche übersetzen, bei Fried- oder Bundesopfern, werden nur die besten Teile des geschlachteten Tiers auf dem Altar verbrannt, während der Rest beim Opfermahl von den Teilnehmern der Feier verzehrt wird (vgl. die Anmerkung zu 1,1-17)."] },
      { number: 2, text: "Er soll seine Hand auf den Kopf des Opfers legen und es am Eingang des Offenbarungszeltes schlachten. Dann sollen die Söhne Aarons, die Priester, das Blut ringsum an den Altar sprengen." },
      { number: 3, text: "Er soll einen Teil dieses Opfers als Feueropfer für den Herrn darbringen, und zwar das Fett, das die Eingeweide bedeckt, das gesamte Fett über den Eingeweiden," },
      { number: 4, text: "die beiden Nieren, das Fett über ihnen und das an den Lenden sowie die Fettmasse, die er von der Leber und den Nieren loslöst." },
      { number: 5, text: "Die Söhne Aarons sollen diesen Teil mit dem Brandopfer, das auf dem Holz über dem Feuer liegt, auf dem Altar in Rauch aufgehen lassen. Es ist ein Feueropfer zum beruhigenden Duft für den Herrn." },
      { number: 6, text: "Ist seine Opfergabe ein Heilsopfer für den Herrn vom Kleinvieh, dann soll er ein männliches oder weibliches fehlerloses Tier darbringen." },
      { number: 7, text: "Wenn er ein Schaf darbringt, soll er es vor den Herrn bringen." },
      { number: 8, text: "Er soll seine Hand auf den Kopf des Opfers legen und es vor dem Offenbarungszelt schlachten; dann sollen die Söhne Aarons das Blut ringsum an den Altar sprengen." },
      { number: 9, text: "Von diesem Heilsopfer soll er das Fett als Feueropfer für den Herrn darbringen: den ganzen Fettschwanz, den er nahe an den Schwanzwirbelknochen ablösen soll, das Fett, das die Eingeweide bedeckt, das gesamte Fett über den Eingeweiden," },
      { number: 10, text: "die beiden Nieren, das Fett über ihnen und das an den Lenden sowie die Fettmasse, die er von der Leber und den Nieren loslöst." },
      { number: 11, text: "Der Priester lasse diese Teile auf dem Altar in Rauch aufgehen. Das soll eine Feueropferspeise für den Herrn sein." },
      { number: 12, text: "Wenn er eine Ziege darbringt, soll er sie vor den Herrn bringen." },
      { number: 13, text: "Er soll seine Hand auf ihren Kopf legen und sie vor dem Offenbarungszelt schlachten; dann sollen die Söhne Aarons ihr Blut ringsum an den Altar sprengen." },
      { number: 14, text: "Von ihr soll er als Feueropfer für den Herrn darbringen: das Fett, das die Eingeweide bedeckt, das gesamte Fett über den Eingeweiden," },
      { number: 15, text: "die beiden Nieren, das Fett über ihnen und das an den Lenden sowie die Fettmasse, die er von der Leber und den Nieren loslöst." },
      { number: 16, text: "Der Priester lasse diese Teile auf dem Altar in Rauch aufgehen. Sie sind eine Feueropferspeise zum beruhigenden Duft für den Herrn. Das ganze Fett gehört dem Herrn." },
      { number: 17, text: "Als feste Regel gelte bei euch von Generation zu Generation an allen euren Wohnstätten: Ihr dürft weder Fett noch Blut genießen." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Das Sündopfer eines Priesters: 4,1-12", footnotes: ["1-5,26: Der Unterschied zwischen Sündopfern und Schuldopfern ist nur schwer festzustellen. Bei beiden Opferarten geht es um Sühne für absichtliche und unabsichtliche Übertretungen von zumeist kultischen Vorschriften. Dabei spielt das Blut eine wichtige Rolle. Es wird an den Altar und andere Teile des Heiligtums gesprengt oder gestrichen. Zur Bedeutung des Blutes beim Opfer vgl. 17,11."] },
      { number: 2, text: "Sag zu den Israeliten: Wenn einer ohne Vorsatz gegen eines der Gebote des Herrn sündigt und etwas Verbotenes tut," },
      { number: 3, text: "dann soll er, wenn es ein gesalbter Priester ist, der sündigt und dadurch Schuld auf das Volk lädt, dem Herrn für die von ihm begangene Sünde einen fehlerlosen Jungstier als Sündopfer darbringen." },
      { number: 4, text: "Er soll diesen Jungstier zum Eingang des Offenbarungszeltes vor den Herrn bringen, ihm die Hand auf den Kopf legen und ihn vor dem Herrn schlachten." },
      { number: 5, text: "Dann soll der gesalbte Priester etwas vom Blut dieses Stiers nehmen, es in das Offenbarungszelt bringen," },
      { number: 6, text: "seinen Finger in das Blut tauchen und es vor dem Herrn siebenmal gegen den Vorhang des Heiligtums spritzen." },
      { number: 7, text: "Darauf soll der Priester etwas vom Blut auf die Hörner des Rauchopferaltars tun, der vor dem Herrn im Offenbarungszelt steht, und dann soll er das ganze Blut des Stiers am Sockel des Brandopferaltars ausgießen, der sich am Eingang des Offenbarungszeltes befindet.", footnotes: ["Die Hörner des Altars sind hornförmige Aufbauten an den vier Ecken, die wahrscheinlich die Macht Gottes versinnbilden (vgl. die Anmerkung zu Ex 32,1-35)."] },
      { number: 8, text: "Dann soll er das ganze Fett des Sündopferstieres abheben, und zwar das Fett, das die Eingeweide bedeckt, das gesamte Fett über den Eingeweiden," },
      { number: 9, text: "die beiden Nieren, das Fett über ihnen und das an den Lenden sowie die Fettmasse, die er von der Leber und den Nieren loslöst -" },
      { number: 10, text: "genauso, wie es beim Stier des Heilsopfers geschieht -, und der Priester lasse diese Teile auf dem Brandopferaltar in Rauch aufgehen." },
      { number: 11, text: "Das Fell des Stiers aber, sein ganzes Fleisch mit dem Kopf, den Beinen, den Eingeweiden und dem Mageninhalt," },
      { number: 12, text: "also den ganzen Stier, soll er aus dem Lager hinaus an einen reinen Ort, nämlich den Abfallplatz der Fett-Asche, schaffen und ihn auf einem Holzfeuer verbrennen; auf dem Abfallplatz der Fett-Asche soll es verbrannt werden." },
      { number: 13, text: "Wenn die ganze Gemeinde Israels ohne Vorsatz gesündigt und etwas vom Herrn Verbotenes getan hat, ohne es bemerkt zu haben, und dadurch schuldig wurde,", heading: "Das Sündopfer der Gemeinde: 4,13-21" },
      { number: 14, text: "dann soll die Gemeinde für die Verfehlung, sobald sie bekannt wird, einen Jungstier als Sündopfer darbringen und ihn vor das Offenbarungszelt bringen." },
      { number: 15, text: "Die Ältesten der Gemeinde sollen ihre Hände vor dem Herrn auf den Kopf des Stiers legen und man soll ihn vor dem Herrn schlachten." },
      { number: 16, text: "Dann soll der gesalbte Priester etwas vom Blut des Stiers in das Offenbarungszelt tragen," },
      { number: 17, text: "seinen Finger in das Blut tauchen und es siebenmal gegen den Vorhang vor dem Herrn spritzen." },
      { number: 18, text: "Darauf soll er ein wenig von dem Blut auf die Hörner des Altars tun, der vor dem Herrn im Offenbarungszelt steht, und dann soll er das ganze Blut am Sockel des Brandopferaltars ausgießen, der sich am Eingang des Offenbarungszeltes befindet." },
      { number: 19, text: "Dann soll er das ganze Fett des Tieres abheben und es auf dem Altar in Rauch aufgehen lassen." },
      { number: 20, text: "Er soll mit dem Stier tun, was auch mit dem Sündopferstier gemacht wird; so soll er es mit ihm machen. Dann soll er die Gemeinde entsühnen und es wird ihnen vergeben werden." },
      { number: 21, text: "Er soll den Stier aus dem Lager hinausbringen und ihn verbrennen, wie er den ersten Stier verbrannt hat. Das ist das Sündopfer für die Gemeinde." },
      { number: 22, text: "Angenommen, ein Sippenhaupt sündigt, tut ohne Vorsatz etwas, was der Herr, sein Gott, verboten hat, und wird dadurch schuldig", heading: "Das Sündopfer eines Sippenhauptes: 4,22-26" },
      { number: 23, text: "oder man teilt ihm eine Verfehlung mit, die er begangen hat, so soll er als seine Opfergabe einen fehlerlosen Ziegenbock bringen." },
      { number: 24, text: "Er soll die Hand auf den Kopf des Bockes legen und ihn dort schlachten, wo man das Brandopfer vor dem Herrn schlachtet. Ein Sündopfer ist es." },
      { number: 25, text: "Der Priester soll mit seinem Finger etwas vom Blut des Sündopfers auf die Hörner des Brandopferaltars tun, dann das Blut am Sockel des Brandopferaltars ausgießen" },
      { number: 26, text: "und das ganze Fett, wie das Fett des Heilsopfers, auf dem Altar in Rauch aufgehen lassen. So entsühnt der Priester den Betreffenden und löst ihn von seiner Sünde; dann wird ihm vergeben werden." },
      { number: 27, text: "Wenn jemand aus dem Volk ohne Vorsatz sündigt und schuldig wird, weil er etwas vom Herrn Verbotenes getan hat,", heading: "Das Sündopfer eines gewöhnlichen Israeliten: 4,27-35" },
      { number: 28, text: "oder man teilt ihm eine Verfehlung mit, die er begangen hat, so bringe er als seine Opfergabe für seine Sünde, die er begangen hat, eine fehlerlose Ziege." },
      { number: 29, text: "Er soll die Hand auf den Kopf des Sündopfers legen und es dort schlachten, wo man das Brandopfer schlachtet." },
      { number: 30, text: "Der Priester soll mit seinem Finger etwas vom Blut auf die Hörner des Brandopferaltars tun und dann das ganze Blut am Sockel des Altars ausgießen." },
      { number: 31, text: "Er soll das ganze Fett ablösen, wie man das Fett eines Heilsopfers ablöst, und der Priester soll es auf dem Altar in Rauch aufgehen lassen als beruhigenden Duft für den Herrn und so diesen Mann entsühnen; dann wird ihm vergeben werden." },
      { number: 32, text: "Will er aber zum Sündopfer ein Schaf als Opfergabe bringen, soll er ein fehlerloses weibliches Tier bringen." },
      { number: 33, text: "Er soll die Hand auf den Kopf des Sündopfers legen und es dort schlachten, wo man das Brandopfer schlachtet." },
      { number: 34, text: "Der Priester soll mit seinem Finger etwas vom Blut dieses Opfers nehmen, auf die Hörner des Brandopferaltars tun und das ganze Blut am Sockel des Altars ausgießen." },
      { number: 35, text: "Das ganze Fett soll er ablösen, wie man das Fett des Schafes eines Heilsopfers ablöst. Der Priester soll die Fettteile mit dem Feueropfer des Herrn auf dem Altar in Rauch aufgehen lassen und ihn so entsühnen, um ihn von seiner Sünde zu lösen, die er begangen hat; dann wird ihm vergeben werden." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Angenommen, jemand sündigt in einem der folgenden Fälle: er hat eine laute Verfluchung gehört, ist Zeuge, da er es gesehen oder darum gewusst hat, aber er zeigt es nicht an und lädt damit Schuld auf sich;", heading: "Die Schuld- und Sündopfer für einzelne Vergehen: 5,1-13", footnotes: ["Wie noch heute bei den Beduinen, so hat man in Israel dem Fluch die Kraft zugeschrieben, das angedeutete Unheil auch wirklich herbeizuführen. Ein Fluch ist üblich und erlaubt, wenn jemand etwas verloren hat und den Finder vor Fundunterschlagung oder den Hehler vor Annahme oder Kauf des Gegenstands warnen will. Der unredliche Finder oder der Hehler würde Unheil auf sich bringen."] },
      { number: 2, text: "oder jemand berührt eine unreine Sache, das Aas eines unreinen wilden Tieres oder eines unreinen Haustiers oder eines unreinen Kriechtiers und es blieb ihm verborgen, aber er merkt es dann und wird unrein und schuldig;", footnotes: ["aber er merkt es dann: sinngemäß ergänzt (vgl. V. 3)."] },
      { number: 3, text: "oder er berührt etwas Unreines von einem Menschen, dessen Berührung unrein macht, und bemerkt es nicht, aber er erfährt es später und wird schuldig;" },
      { number: 4, text: "oder jemand schwört unbesonnen, ob zum Schaden oder zum Nutzen, wie eben der Mensch bisweilen unbesonnen schwört, aber er merkt es gar nicht, doch dann erfährt er es und wird schuldig durch so etwas -" },
      { number: 5, text: "wenn also jemand in einem dieser Fälle schuldig wird, so soll er gestehen, wodurch er sich verfehlt hat." },
      { number: 6, text: "Als Schuldopfer für seine begangene Verfehlung soll er dann ein weibliches Stück Kleinvieh, ein Schaf oder eine Ziege, vor den Herrn als Sündopfer bringen und der Priester soll ihn entsühnen und so von seiner Verfehlung lösen." },
      { number: 7, text: "Wenn seine Mittel für ein Schaf nicht ausreichen, soll er als Schuldopfer für seine Verfehlung zwei Turteltauben oder zwei junge Tauben vor den Herrn bringen, die eine als Sünd- und die andere als Brandopfer." },
      { number: 8, text: "Er soll sie zum Priester bringen und dieser soll zuerst die eine für das Sündopfer darbringen. Er soll ihren Kopf vom Genick trennen, aber nicht ganz abreißen;" },
      { number: 9, text: "er soll etwas vom Blut des Sündopfers gegen die Altarwand spritzen und das übrige Blut am Sockel des Altars auspressen. Ein Sündopfer ist das." },
      { number: 10, text: "Die zweite Taube soll er, wie vorgeschrieben, als Brandopfer darbringen. Der Priester soll ihn entsühnen und von der Sünde, die er begangen hat, lösen; dann wird ihm vergeben werden." },
      { number: 11, text: "Falls seine Mittel für zwei Turteltauben oder zwei junge Tauben nicht ausreichen, soll er als Opfergabe für seine Verfehlung ein Zehntel Efa Feinmehl darbringen, aber weder Öl noch Weihrauch darauftun, denn es ist ein Sündopfer." },
      { number: 12, text: "Er bringe es dem Priester, der davon eine Hand voll nimmt und als Gedächtnisanteil auf dem Altar mit den Feueropfern des Herrn in Rauch aufgehen lässt. Ein Sündopfer ist das." },
      { number: 13, text: "Der Priester soll ihn so für die von ihm in einem dieser Fälle begangene Verfehlung entsühnen und es wird ihm vergeben werden. Dem Priester gehört das Gleiche wie beim Speiseopfer." },
      { number: 14, text: "Der Herr sprach zu Mose:", heading: "Das Schuldopfer für sakrale Vergehen: 5,14-19" },
      { number: 15, text: "Wenn jemand sich verfehlt und sich ohne Vorsatz an Dingen vergeht, die dem Herrn heilig sind, soll er als Schuldopfer für den Herrn von seiner Herde einen fehlerlosen Widder bringen, der in Silberschekel nach dem Schekelgewicht des Heiligtums zu schätzen ist." },
      { number: 16, text: "Wenn er sich am Heiligen verfehlt hat, soll er Ersatz leisten und noch ein Fünftel des Wertes hinzufügen. Er soll es dem Priester geben und dieser soll ihn mit dem Schuldopferwidder entsühnen; dann wird ihm vergeben werden." },
      { number: 17, text: "Wenn jemand sich verfehlt und etwas tut, was der Herr verboten hat, es nicht bemerkt, aber dann schuldig wird, soll er die Folgen seiner Schuld tragen." },
      { number: 18, text: "Er soll als Schuldopfer einen fehlerlosen Widder von seiner Herde nach dem üblichen Schätzwert zum Priester bringen und dieser soll ihn für seine unabsichtliche Verfehlung, die er ohne Vorsatz begangen hat, entsühnen; dann wird ihm vergeben werden." },
      { number: 19, text: "Ein Schuldopfer ist das; der Betreffende hat sich tatsächlich vor dem Herrn schuldig gemacht." },
      { number: 20, text: "Der Herr sprach zu Mose:", heading: "Das Schuldopfer für unsoziales Verhalten: 5,20-26" },
      { number: 21, text: "Wenn jemand sündigt und eine Veruntreuung gegen den Herrn begeht, indem er einen aus seinem Volk über anvertrautes oder hinterlegtes oder geraubtes Gut täuscht oder ihn übervorteilt", footnotes: ["(21-26) Ex 22,6-14"] },
      { number: 22, text: "oder verlorenes Gut findet und es leugnet oder einen Meineid leistet hinsichtlich irgendeiner Sünde, die ein Mensch begehen kann," },
      { number: 23, text: "wenn er also auf solche Weise sündigt und schuldig wird, muss er das, was er geraubt oder durch Übervorteilung gewonnen hat oder was ihm anvertraut wurde, oder das Verlorene, das er gefunden hat, zurückgeben" },
      { number: 24, text: "oder er muss den Schaden, den er durch den Meineid angerichtet hat, wieder gutmachen; am Tag seines Schuldopfers erstatte er dem Geschädigten den um ein Fünftel vermehrten Wert." },
      { number: 25, text: "Er soll einen fehlerlosen Widder von seiner Herde nach dem üblichen Schätzwert als sein Schuldopfer vor den Herrn zum Priester bringen" },
      { number: 26, text: "und dieser soll ihn vor dem Herrn entsühnen; dann wird ihm jede Tat vergeben werden, durch die er sich schuldig gemacht hat." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Opfervorschriften für die Priester: 6,1 - 7,38 | Das Brandopfer: 6,1-6" },
      { number: 2, text: "Trag Aaron und seinen Söhnen auf: Für das Brandopfer gilt folgendes Gesetz: Das Brandopfer soll die ganze Nacht bis zum Morgen auf der Feuerstelle des Altars bleiben und das Altarfeuer soll darauf brennen." },
      { number: 3, text: "Der Priester soll sein Leinengewand anlegen und seinen Körper mit einem leinenen Beinkleid bedecken. Dann soll er die Fett-Asche nehmen, zu der das Brandopfer auf dem Altar vom Feuer verzehrt wurde, und sie neben den Altar schütten.", footnotes: ["Ex 28,42f"] },
      { number: 4, text: "Dann soll er seine Kleider ablegen, andere anziehen und die Fett-Asche aus dem Lager hinaus an einen reinen Platz schaffen." },
      { number: 5, text: "Das Feuer soll auf dem Altar brennen bleiben, es darf nicht erlöschen und der Priester soll jeden Morgen Holz nachlegen. Er lege darauf das Brandopfer und lasse auf ihm die Fetteile des Heilsopfers in Rauch aufgehen." },
      { number: 6, text: "Ein ständiges Feuer soll auf dem Altar brennen; es darf nicht verlöschen." },
      { number: 7, text: "Für das Speiseopfer gilt folgendes Gesetz: Einer der Söhne Aarons bringt es vor den Herrn zur Vorderseite des Altars,", heading: "Das Speiseopfer: 6,7-11", footnotes: ["(7-11) 2,1-13"] },
      { number: 8, text: "hebt von ihm eine Hand voll Feinmehl, zusammen mit dem Öl und dem ganzen darauf befindlichen Weihrauch ab und lässt es als Gedächtnisanteil für den Herrn auf dem Altar zum beruhigenden Duft in Rauch aufgehen." },
      { number: 9, text: "Das Übrige sollen Aaron und seine Söhne als ungesäuertes Brot an einem heiligen Ort, im Vorhof des Offenbarungszeltes, essen." },
      { number: 10, text: "Man soll den Anteil an meinen Feueropfern, den ich ihnen gebe, nicht gesäuert backen. Es ist etwas Hochheiliges wie das Sünd- und das Schuldopfer." },
      { number: 11, text: "Jede männliche Person unter den Söhnen Aarons darf von diesem Anteil an den Feueropfern des Herrn essen. Das gelte bei euch als feste Regel von Generation zu Generation. Alles, was damit in Berührung kommt, wird heilig." },
      { number: 12, text: "Der Herr sprach zu Mose:", heading: "Das Opfer bei der Priesterweihe: 6,12-16" },
      { number: 13, text: "Das ist die Opfergabe, die Aaron und seine Söhne am Tag ihrer Salbung dem Herrn darbringen sollen: ein Zehntel Efa Feinmehl als dauerndes Speiseopfer, die Hälfte am Morgen und die Hälfte am Abend." },
      { number: 14, text: "Dieses Speiseopfer soll, mit Öl eingerührt, auf einer Ofenplatte zubereitet werden; du sollst es in mehrere Stücke zerbröckeln und als beruhigenden Duft für den Herrn darbringen." },
      { number: 15, text: "Der Priester, der an Aarons Stelle aus dem Kreis seiner Söhne gesalbt ist, soll es zubereiten. Als feste Regel gelte: Das Speiseopfer soll für den Herrn ganz in Rauch aufgehen;" },
      { number: 16, text: "jedes Speiseopfer eines Priesters soll ein Ganzopfer sein; man darf es nicht essen." },
      { number: 17, text: "Der Herr sprach zu Mose:", heading: "Das Sündopfer: 6,17-23", footnotes: ["(17-23) 4,1-35"] },
      { number: 18, text: "Sag zu Aaron und seinen Söhnen: Für das Sündopfer gilt folgendes Gesetz: Dort, wo man das Brandopfer schlachtet, soll das Sündopfer vor dem Herrn geschlachtet werden. Es ist etwas Hochheiliges." },
      { number: 19, text: "Der Priester, der dieses Opfer darbringt, soll es essen; an einem heiligen Ort, im Vorhof des Offenbarungszeltes, muss es gegessen werden." },
      { number: 20, text: "Alles, was mit dem Fleisch in Berührung kommt, wird heilig, und wenn vom Blut etwas auf die Gewänder spritzt, soll die Stelle an einem heiligen Ort gewaschen werden." },
      { number: 21, text: "Das Tongefäß, in dem das Fleisch gekocht wird, soll zerbrochen werden, und wenn es in einem Bronzegefäß gekocht wird, muss dieses gescheuert und mit Wasser abgespült werden." },
      { number: 22, text: "Jede männliche Person unter den Priestern darf davon essen; es ist etwas Hochheiliges." },
      { number: 23, text: "Aber man darf kein Sündopfer essen, dessen Blut man in das Offenbarungszelt gebracht hat, um im Heiligtum die Sühne zu vollziehen; es muss im Feuer verbrannt werden." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Für das Schuldopfer gilt folgendes Gesetz: Es ist etwas Hochheiliges.", heading: "Das Schuldopfer: 7,1-10", footnotes: ["(1-10) 5,1-26"] },
      { number: 2, text: "Dort, wo man das Brandopfer schlachtet, soll man das Schuldopfer schlachten und der Priester soll das Blut ringsum an den Altar sprengen." },
      { number: 3, text: "Dann soll er das ganze Fett davon darbringen: den Fettschwanz, das Fett, das die Eingeweide bedeckt," },
      { number: 4, text: "die beiden Nieren, das Fett über ihnen und das an den Lenden sowie die Fettmasse, die er von der Leber und den Nieren loslöst." },
      { number: 5, text: "Der Priester lasse das alles auf dem Altar als Feueropfer für den Herrn in Rauch aufgehen; es ist ein Schuldopfer." },
      { number: 6, text: "Jede männliche Person unter den Priestern darf davon essen; man soll es an einem heiligen Ort essen. Es ist etwas Hochheiliges." },
      { number: 7, text: "Für das Schuldopfer gilt dasselbe Gesetz wie für das Sündopfer. Es gehört dem Priester, der damit die Sühne vollzieht." },
      { number: 8, text: "Das Fell des Opfertieres, das einer als ein Brandopfer zum Priester bringt, gehört dem Priester, der es darbringt." },
      { number: 9, text: "Jedes Speiseopfer, das im Ofen gebacken oder in einem Kochtopf oder auf einer Ofenplatte zubereitet wurde, gehört dem Priester, der es darbringt." },
      { number: 10, text: "Jedes mit Öl vermengte und jedes trockene Speiseopfer gehört allen Söhnen Aarons ohne Unterschied." },
      { number: 11, text: "Für das Heilsopfer, das man für den Herrn darbringt, gilt folgendes Gesetz:", heading: "Das Heilsopfer: 7,11-21", footnotes: ["(11-21) 3,1-17"] },
      { number: 12, text: "Wenn man es als Dankopfer darbringt, soll man zu diesem Dankschlachtopfer ungesäuerten, mit Öl vermengten Kuchen hinzutun und ungesäuerte, mit Öl bestrichene Brotfladen sowie Kuchen aus Feinmehl, das mit Öl vermengt und eingerührt ist." },
      { number: 13, text: "Dazu soll man auch Gebäck aus gesäuertem Brot geben und das alles zusammen mit dem Heilsopfer als Dankopfer darbringen." },
      { number: 14, text: "Einen Teil von jeder Opfergabe soll man mit dem Erhebungsritus dem Herrn darbringen; es gehört dem Priester, der das Blut des Heilsopfers sprengt.", footnotes: ["Der Erhebungsritus ist ein ritueller Akt, bei dem der Priester die Opfergabe mit beiden Händen hochhebt zum Zeichen dafür, dass er sie Gott anbietet."] },
      { number: 15, text: "Das Opferfleisch des Schlachtdankopfers soll am Tag der Darbringung gegessen werden; nichts davon darf bis zum nächsten Morgen liegen bleiben." },
      { number: 16, text: "Wenn es sich um ein Gelübde oder um ein freiwilliges Opfer handelt, darf das Fleisch am Tag der Darbringung und am folgenden Tag gegessen werden; was übrig bleibt, darf gegessen werden;" },
      { number: 17, text: "was aber vom Fleisch des Schlachtopfers dann noch übrig bleibt, soll am dritten Tag verbrannt werden." },
      { number: 18, text: "Wenn man vom Fleisch des Heilsopfers am dritten Tag isst, findet der Darbringende keine Annahme. Es wird ihm nicht angerechnet und ist untauglich und die Person, die davon isst, muss die Folgen ihrer Schuld tragen." },
      { number: 19, text: "Fleisch, das mit irgendetwas Unreinem in Berührung kommt, darf man nicht essen; man soll es im Feuer verbrennen. Jeder Reine darf Opferfleisch essen;" },
      { number: 20, text: "wenn aber jemand im Zustand der Unreinheit vom Fleisch des Heilsopfers isst, das dem Herrn gehört, soll er aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 21, text: "Wenn jemand mit irgendetwas Unreinem in Berührung kommt, sei es mit etwas Unreinem von einem Menschen oder einem unreinen Tier oder irgendeiner unreinen, abscheulichen Sache, und dann vom Fleisch eines Heilsopfers isst, das für den Herrn geopfert wird, soll er aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 22, text: "Der Herr sprach zu Mose:", heading: "Das Verbot von Fett- und Blutgenuß: 7,22-27" },
      { number: 23, text: "Sag zu den Israeliten: Von Rind, Schaf oder Ziege dürft ihr keinerlei Fett essen." },
      { number: 24, text: "Das Fett eines verendeten oder zerrissenen Tieres kann zu jedem Zweck verwendet werden, doch essen dürft ihr es auf keinen Fall.", footnotes: ["Ex 22,30"] },
      { number: 25, text: "Jeder, der dennoch das Fett eines Tieres isst, das man als Feueropfer für den Herrn darbringt, soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 26, text: "Wo immer ihr wohnt, dürft ihr kein Blut genießen, weder von Vögeln, noch vom Vieh.", footnotes: ["Gen 9,4"] },
      { number: 27, text: "Wer Blut genießt, soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 28, text: "Der Herr sprach zu Mose:", heading: "Die Feueropfer: 7,28-38" },
      { number: 29, text: "Sag zu den Israeliten: Wer ein Tier als Heilsopfer für den Herrn darbringen will, soll davon als Opfergabe für den Herrn Folgendes herbeibringen:" },
      { number: 30, text: "Mit seinen Händen bringe er als Feueropfer für den Herrn das Fett des Bruststückes; er soll dieses Fett und die Brust bringen, die er vor dem Herrn hin- und herschwingen und so darbringen soll." },
      { number: 31, text: "Der Priester lasse das Fett auf dem Altar in Rauch aufgehen, die Brust aber gehört Aaron und seinen Söhnen." },
      { number: 32, text: "Für den Erhebungsritus gebt von euren Heilsopfern die rechte Schenkelkeule dem Priester!" },
      { number: 33, text: "Diese rechte Schenkelkeule soll jenem der Söhne Aarons als Anteil gehören, der das Blut und das Fett des Heilsopfers darbringt." },
      { number: 34, text: "Ich nehme nämlich von den Heilsopfern der Israeliten die Brust für den Darbringungsritus und die Schenkelkeule für den Erhebungsritus und gebe sie dem Priester Aaron und seinen Söhnen als ein dauerndes Anrecht, das die Israeliten ihnen gewähren müssen." },
      { number: 35, text: "Das also ist der Anteil Aarons und seiner Söhne an den Feueropfern des Herrn an dem Tag, an dem er sie dem Herrn als Priester darbringt." },
      { number: 36, text: "Das hat der Herr ihnen zu geben befohlen an dem Tag, an dem er sie unter den Israeliten salbte. Das gelte für sie als feste Regel von Generation zu Generation." },
      { number: 37, text: "Das ist das Gesetz für Brandopfer, Speiseopfer, Sündopfer, Schuldopfer, Priestereinsetzungsopfer und Heilsopfer." },
      { number: 38, text: "So hat es der Herr dem Mose auf dem Sinai an dem Tag aufgetragen, an dem er die Israeliten verpflichtete, ihre Gaben dem Herrn in der Wüste Sinai darzubringen." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Priestergesetze: 8,1 - 10,20 | Die Priesterweihe: 8,1-36", footnotes: ["(1-36) Ex 29,1-37", "1-10,20: Dass nur Angehörige des Stammes Levi opfern, hat sich erst in der Königszeit durchgesetzt. Aus Ri 17f erfahren wir zwar, dass Leviten als Priester bevorzugt wurden; aus den Samuelerzählungen und aus den Nachrichten über die Überführung der Bundeslade (2 Sam 6,17f) und über die Tempelweihe (1 Kön 8,62f) ersehen wir aber, dass der Nichtlevit Samuel und die Könige David und Salomo Opfer dargebracht haben. Nach 2 Sam 8,18 und 20,26 hat David seine Söhne und andere Nichtleviten als Priester eingesetzt. Die herausragende Stellung und die Weihe des Hohenpriesters oder einer hohepriesterlichen Dynastie ist für die Königszeit noch nicht nachweisbar, sondern erst in der nachexilischen Zeit zu belegen."] },
      { number: 2, text: "Nimm Aaron und seine Söhne, die Gewänder, das Salböl, den Stier für das Sündopfer, die beiden Widder und den Korb mit den ungesäuerten Broten!" },
      { number: 3, text: "Dann ruf die ganze Gemeinde am Eingang des Offenbarungszeltes zusammen!" },
      { number: 4, text: "Mose tat, was ihm der Herr befohlen hatte, und die Gemeinde versammelte sich am Eingang des Offenbarungszeltes." },
      { number: 5, text: "Mose sagte zur Gemeinde: Der Herr hat angeordnet, das Folgende zu tun." },
      { number: 6, text: "Mose ließ Aaron und seine Söhne näher treten und wusch sie mit Wasser." },
      { number: 7, text: "Er legte ihm das Gewand an, gürtete ihn mit dem Gürtel, bekleidete ihn mit dem Obergewand und legte ihm das Efod an. Dann band er ihm die Schärpe um, an der er das Efod befestigte.", footnotes: ["Ex 28,4-43"] },
      { number: 8, text: "Er übergab ihm die Lostasche, in die er die Lose Urim und Tummim steckte." },
      { number: 9, text: "Dann setzte er ihm den Turban auf und befestigte an der Vorderseite des Turbans die goldene Rosette, das heilige Diadem, wie der Herr dem Mose vorgeschrieben hatte." },
      { number: 10, text: "Darauf nahm Mose das Salböl und salbte die Wohnstätte und alles, was darin war, um sie zu weihen.", footnotes: ["Zur Wohnstätte vgl. die Anmerkung zu Ex 25,9."] },
      { number: 11, text: "Mit dem Öl spritzte er siebenmal gegen den Altar, salbte ihn und alle seine Geräte, das Becken und sein Gestell, um sie zu weihen." },
      { number: 12, text: "Vom Salböl goss er etwas auf das Haupt Aarons und salbte ihn, um ihn zu weihen." },
      { number: 13, text: "Dann ließ Mose die Söhne Aarons näher treten, bekleidete sie mit Gewändern und legte ihnen den Gürtel an; er setzte ihnen den Kopfbund auf, wie es der Herr dem Mose befohlen hatte." },
      { number: 14, text: "Dann ließ er den Sündopferstier heranbringen. Auf seinen Kopf legten Aaron und seine Söhne ihre Hände" },
      { number: 15, text: "und Mose schlachtete ihn. Dann nahm Mose das Blut und tat etwas davon mit seinem Finger ringsum auf die Hörner des Altars, um ihn zu entsündigen. Nachher goss er das Blut am Sockel des Altars aus und weihte ihn; so entsühnte er ihn." },
      { number: 16, text: "Danach nahm er das ganze Fett, das die Eingeweide bedeckt, die Fettmasse über der Leber, die beiden Nieren und ihr Fett und ließ diese Stücke auf dem Altar in Rauch aufgehen." },
      { number: 17, text: "Was vom Stier noch übrig war, sein Fell und Fleisch sowie seinen Mageninhalt verbrannte er außerhalb des Lagers, wie es der Herr dem Mose befohlen hatte." },
      { number: 18, text: "Dann ließ er den Widder für das Brandopfer herbeibringen. Aaron und seine Söhne legten ihre Hände auf den Kopf des Widders" },
      { number: 19, text: "und Mose schlachtete ihn. Er sprengte das Blut ringsum gegen den Altar." },
      { number: 20, text: "Dann zerteilte er den Widder und ließ den Kopf, die Teile und das Fett in Rauch aufgehen." },
      { number: 21, text: "Er wusch die Eingeweide und die Beine mit Wasser und ließ den ganzen Widder auf dem Altar in Rauch aufgehen. Es war ein Brandopfer zum beruhigenden Duft, ein Feueropfer für den Herrn, wie es der Herr dem Mose befohlen hatte." },
      { number: 22, text: "Dann ließ er den zweiten Widder, den Widder für das Opfer der Priestereinsetzung, herbeibringen. Aaron und seine Söhne legten ihre Hände auf den Kopf des Widders" },
      { number: 23, text: "und Mose schlachtete ihn. Er nahm etwas Blut und tat es auf das rechte Ohrläppchen Aarons, auf den Daumen seiner rechten Hand und auf die große Zehe seines rechten Fußes." },
      { number: 24, text: "Dann ließ er die Söhne Aarons näher treten und tat etwas Blut auf ihr rechtes Ohrläppchen, auf den Daumen ihrer rechten Hand und auf die große Zehe ihres rechten Fußes. Danach goss Mose das Blut ringsum an den Altar." },
      { number: 25, text: "Er nahm auch das Fett, den Fettschwanz, das ganze Fett, das sich über den Eingeweiden befindet, die Fettmasse über der Leber, die beiden Nieren und ihr Fett und die rechte Schenkelkeule." },
      { number: 26, text: "Aus dem Korb mit den ungesäuerten Broten, der vor dem Herrn stand, nahm er einen ungesäuerten Kuchen, einen Ölbrotkuchen und einen Brotfladen, die er auf die Fettstücke und die rechte Schenkelkeule legte." },
      { number: 27, text: "Das alles gab er in die Hände Aarons und seiner Söhne und ließ sie es vor dem Herrn hin- und herschwingen und so darbringen." },
      { number: 28, text: "Dann nahm es Mose wieder aus ihren Händen und ließ es auf dem Altar mit dem Brandopfer in Rauch aufgehen. Das war das Opfer der Priestereinsetzung zum beruhigenden Duft, ein Feueropfer für den Herrn." },
      { number: 29, text: "Mose nahm auch das Bruststück und vollzog mit ihm vor dem Herrn den Darbringungsritus. Dieser Teil des Einsetzungswidders gehörte Mose als Anteil, wie es der Herr dem Mose befohlen hatte." },
      { number: 30, text: "Danach nahm Mose etwas Salböl und etwas Blut, das auf dem Altar war, und spritzte es auf Aaron und dessen Gewänder sowie auf seine Söhne und deren Gewänder. So weihte er Aaron und dessen Gewänder sowie seine Söhne und deren Gewänder." },
      { number: 31, text: "Mose sagte zu Aaron und seinen Söhnen: Kocht das Fleisch am Eingang des Offenbarungszeltes! Ihr sollt es dort essen, ebenso wie das Brot aus dem Einsetzungskorb, wie ich es angeordnet habe mit den Worten: Aaron und seine Söhne sollen es essen.", footnotes: ["Ex 29,31-34"] },
      { number: 32, text: "Was vom Fleisch und vom Brot übrig bleibt, sollt ihr verbrennen." },
      { number: 33, text: "Sieben Tage lang sollt ihr den Eingang des Offenbarungszeltes nicht verlassen, bis die Zeit eurer Einsetzung um ist; denn sieben Tage dauert eure Einsetzung." },
      { number: 34, text: "Wie man es am heutigen Tag getan hat, so hat der Herr angeordnet, soll man es auch weiter tun, um euch zu entsühnen." },
      { number: 35, text: "Sieben Tage lang, Tag und Nacht, sollt ihr am Eingang des Offenbarungszeltes bleiben und auf die Anordnungen des Herrn achten; dann werdet ihr nicht sterben. Denn so ist es mir befohlen worden." },
      { number: 36, text: "Aaron und seine Söhne taten alles, was der Herr durch Mose angeordnet hatte." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Am achten Tag rief Mose Aaron, seine Söhne und die Ältesten Israels zusammen", heading: "Das Weiheopfer des Hohenpriesters: 9,1-24" },
      { number: 2, text: "und sagte zu Aaron: Nimm ein Kalb, ein junges Rind, für ein Sündopfer und einen Widder für ein Brandopfer, fehlerlose Tiere, und bring sie her vor den Herrn!" },
      { number: 3, text: "Dann sollst du zu den Israeliten sagen: Nehmt einen Ziegenbock für ein Sündopfer, ferner ein Kalb und ein Schaf, einjährige und fehlerlose Tiere, für ein Brandopfer," },
      { number: 4, text: "ein Rind und einen Widder für ein Heilsopfer, um sie vor dem Herrn zu schlachten, und ein Speiseopfer, das mit Öl vermengt ist. Denn heute wird euch der Herr erscheinen." },
      { number: 5, text: "Sie brachten, was Mose befohlen hatte, zum Offenbarungszelt und die ganze Gemeinde kam herbei und stellte sich vor dem Herrn auf." },
      { number: 6, text: "Mose sagte: Das ist es, was der Herr euch zu tun geboten hat, damit euch die Herrlichkeit des Herrn erscheint." },
      { number: 7, text: "Dann sagte Mose zu Aaron: Tritt zum Altar hin, bring dein Sünd- und dein Brandopfer dar und vollzieh so für dich und das Volk die Sühne! Dann bring das Opfer des Volkes dar und entsühne es, wie der Herr befohlen hat.", footnotes: ["für dich und das Volk, vielleicht nach G zu korrigieren: für dich und dein Haus."] },
      { number: 8, text: "Aaron trat an den Altar und schlachtete das Kalb für seine eigene Sünde.", footnotes: ["8,14-21"] },
      { number: 9, text: "Dann brachten ihm die Söhne Aarons das Blut; er tauchte seinen Finger in das Blut, tat es auf die Hörner des Altars und goss das übrige Blut am Sockel des Altars aus." },
      { number: 10, text: "Das Fett des Sündopfers, die Nieren und die Fettmasse über der Leber des Sündopfers ließ er auf dem Altar in Rauch aufgehen, wie es der Herr dem Mose befohlen hatte." },
      { number: 11, text: "Das Fleisch und das Fell verbrannte er außerhalb des Lagers." },
      { number: 12, text: "Danach schlachtete er das Brandopfer; die Söhne Aarons reichten ihm das Blut und er sprengte es ringsum an den Altar." },
      { number: 13, text: "Sie reichten ihm die einzelnen Stücke und den Kopf des Brandopfers und er ließ sie auf dem Altar in Rauch aufgehen." },
      { number: 14, text: "Er wusch die Eingeweide und Beine und ließ auch sie mit dem Brandopfer auf dem Altar in Rauch aufgehen." },
      { number: 15, text: "Dann ließ er das Opfer des Volkes heranbringen. Er nahm den Bock für das Sündopfer des Volkes, schlachtete ihn und brachte ihn wie den ersten als Sündopfer dar." },
      { number: 16, text: "Darauf ließ er das Brandopfer bringen und verfuhr nach der Vorschrift.", footnotes: ["1,10-13"] },
      { number: 17, text: "Er ließ das Speiseopfer bringen, nahm eine Hand voll davon und ließ es auf dem Altar in Rauch aufgehen, dazu noch das Morgenbrandopfer.", footnotes: ["2,1-16"] },
      { number: 18, text: "Dann schlachtete er das Rind und den Widder als Heilsopfer für das Volk. Die Söhne Aarons reichten ihm das Blut und er sprengte es ringsum an den Altar.", footnotes: ["3,1-17"] },
      { number: 19, text: "Die Fettstücke des Rinds und des Widders, und zwar den Fettschwanz, das Eingeweidenetz, die Nieren und die Fettmasse über der Leber," },
      { number: 20, text: "legte er auf die Bruststücke und ließ sie auf dem Altar in Rauch aufgehen." },
      { number: 21, text: "Die Bruststücke und die rechte Schenkelkeule schwang Aaron vor dem Herrn hin und her und brachte sie so dar, wie es Mose befohlen hatte." },
      { number: 22, text: "Dann erhob Aaron seine Hände über das Volk und segnete es. Nachdem er so das Sünd- und das Brandopfer sowie das Heilsopfer vollzogen hatte, stieg er herunter" },
      { number: 23, text: "und Mose ging mit Aaron in das Offenbarungszelt. Dann kamen beide heraus und segneten das Volk. Da erschien die Herrlichkeit des Herrn dem ganzen Volk." },
      { number: 24, text: "Feuer ging vom Herrn aus und verzehrte das Brandopfer und die Fettstücke auf dem Altar. Als das ganze Volk das sah, stieß es Jubelschreie aus und alle fielen auf ihr Gesicht nieder." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Die Söhne Aarons, Nadab und Abihu, nahmen jeder seine Räucherpfanne. Sie legten Feuer auf, taten Räucherwerk darauf und brachten vor dem Herrn ein unerlaubtes Feuer dar, eines, das er ihnen nicht befohlen hatte.", heading: "Die Strafe für unrechtmäßiges Opfern: 10,1-5", footnotes: ["Worin die Unrechtmäßigkeit des Opfers bestand, wird für uns nicht klar."] },
      { number: 2, text: "Da ging vom Herrn ein Feuer aus, das sie verzehrte, und sie kamen vor dem Herrn um." },
      { number: 3, text: "Da sagte Mose zu Aaron: Das ist es, was der Herr meinte, als er sprach: An denen, die mir nahe sind, / erweise ich mich heilig / und vor dem ganzen Volk / zeige ich mich herrlich. Aaron schwieg." },
      { number: 4, text: "Mose rief Mischaël und Elizafan, die Söhne von Aarons Onkel Usiël, und sagte zu ihnen: Kommt her und tragt eure Brüder vom Heiligtum weg, vor das Lager hinaus!" },
      { number: 5, text: "Sie traten heran und trugen sie in ihren Gewändern vor das Lager hinaus, wie es Mose gesagt hatte." },
      { number: 6, text: "Zu Aaron und seinen Söhnen Eleasar und Itamar sagte Mose: Lasst euer Haar nicht lose herunterhängen und zerreißt nicht eure Kleider, sonst müsst ihr sterben und der Herr zürnt über die ganze Gemeinde. Eure Brüder, das ganze Haus Israel, sollen über den Brand weinen, den der Herr entfacht hat.", heading: "Der Dienst im Heiligtum: 10,6-20" },
      { number: 7, text: "Verlasst nicht den Eingang des Offenbarungszeltes, sonst müsst ihr sterben; denn das Salböl des Herrn ist auf euch. Sie taten, was Mose gesagt hatte." },
      { number: 8, text: "Der Herr sagte zu Aaron:" },
      { number: 9, text: "Wenn ihr zum Offenbarungszelt kommt, dürft ihr, du und deine Söhne, weder Wein noch Bier trinken, sonst müsst ihr sterben. Das gelte bei euch als feste Regel von Generation zu Generation." },
      { number: 10, text: "Ihr sollt zwischen heilig und profan, zwischen unrein und rein unterscheiden" },
      { number: 11, text: "und die Israeliten sollt ihr über alle Vorschriften belehren, die der Herr euch durch Mose verkündet hat." },
      { number: 12, text: "Mose sagte zu Aaron und seinen überlebenden Söhnen Eleasar und Itamar: Nehmt das Speiseopfer, das von den Feueropfern des Herrn übrig bleibt, esst davon die ungesäuerten Brote neben dem Altar, denn es ist etwas Hochheiliges." },
      { number: 13, text: "An einem heiligen Ort sollt ihr es essen, denn es ist das, was dir und deinen Söhnen von den Feueropfern des Herrn zusteht; so ist es mir nämlich befohlen worden." },
      { number: 14, text: "Die Brust vom Darbringungsritus und die Schenkelkeule vom Erhebungsritus sollt ihr, du, deine Söhne und deine Töchter, an einem reinen Ort essen, denn sie sind der für dich und deine Söhne bestimmte Anteil an den Heilsopfern der Israeliten, den man dir gibt." },
      { number: 15, text: "Die Schenkelkeule vom Erhebungsritus und die Brust vom Darbringungsritus, die bei den Fettstücken der Feueropfer sind, sollen sie zur Darbringung vor den Herrn bringen; sie sollen dir und deinen Söhnen als dauerndes Anrecht gehören, wie es der Herr befohlen hat." },
      { number: 16, text: "Mose erkundigte sich nach dem Sündopferbock, und siehe da, man hatte ihn verbrannt. Er geriet über Eleasar und Itamar, die überlebenden Söhne Aarons, in Zorn und sagte:" },
      { number: 17, text: "Warum habt ihr das Sündopfer nicht an einem heiligen Ort gegessen? Es ist etwas Hochheiliges und es wurde euch gegeben, damit ihr die Schuld von der Gemeinde wegnehmt, indem ihr sie vor dem Herrn entsühnt." },
      { number: 18, text: "Sein Blut wurde ja nicht in das Innere des Heiligtums gebracht; das Fleisch hättet ihr im Heiligtum essen sollen, wie ich es angeordnet habe." },
      { number: 19, text: "Aaron antwortete Mose: Heute haben sie ihr Sünd- und ihr Brandopfer vor dem Herrn dargebracht und doch hat mich so etwas getroffen. Wenn ich heute das Sündopfer gegessen hätte, hätte das dem Herrn gefallen?", footnotes: ["Die Entschuldigung Aarons ist unklar."] },
      { number: 20, text: "Als Mose das hörte, schien es ihm richtig." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Der Herr sprach zu Mose und Aaron:", heading: "Die Reinheitsgesetze: 11,1 - 15,33 | Reine und unreine Tiere: 11,1-47", footnotes: ["(1-47) Dtn 14,3-21a", "1-47: Die meisten der für unrein gehaltenen Tiere sind für den Menschen Ekel erregend, gelten als besonders unsauber oder spielen im Kult anderer Völker eine Rolle. Die genaue Bedeutung der hebräischen Tiernamen ist nicht immer gesichert."] },
      { number: 2, text: "Sagt den Israeliten: Das sind die Tiere, die ihr von allem Vieh auf der Erde essen dürft:" },
      { number: 3, text: "Alle Tiere, die gespaltene Klauen haben, Paarzeher sind und wiederkäuen, dürft ihr essen.", footnotes: ["Die Einordnung in die Gattung der Wiederkäuer geschieht nach dem Augenschein. Danach gelten z. B. Tiere wie der Hase, die ständig das Maul bewegen, als Wiederkäuer."] },
      { number: 4, text: "Jedoch dürft ihr von den Tieren, die wiederkäuen oder gespaltene Klauen haben, Folgende nicht essen: Ihr sollt für unrein halten das Kamel, weil es zwar wiederkäut, aber keine gespaltenen Klauen hat;" },
      { number: 5, text: "ihr sollt für unrein halten den Klippdachs, weil er zwar wiederkäut, aber keine gespaltenen Klauen hat;" },
      { number: 6, text: "ihr sollt für unrein halten den Hasen, weil er zwar wiederkäut, aber keine gespaltenen Klauen hat;" },
      { number: 7, text: "ihr sollt für unrein halten das Wildschwein, weil es zwar gespaltene Klauen hat und Paarzeher ist, aber nicht wiederkäut." },
      { number: 8, text: "Ihr dürft von ihrem Fleisch nicht essen und ihr Aas nicht berühren; ihr sollt sie für unrein halten." },
      { number: 9, text: "Von allen Tieren, die im Wasser leben, dürft ihr essen; alle Tiere mit Flossen und Schuppen, die im Wasser, in Meeren und Flüssen leben, dürft ihr essen." },
      { number: 10, text: "Aber alles, was in Meeren oder Flüssen lebt, alles Kleingetier des Wassers und alle Lebewesen, die im Wasser leben und keine Flossen oder Schuppen haben, seien euch abscheulich." },
      { number: 11, text: "Ihr sollt sie als abscheulich ansehen; von ihrem Fleisch dürft ihr nicht essen und ihr Aas sollt ihr verabscheuen." },
      { number: 12, text: "Alles, was ohne Flossen oder Schuppen im Wasser lebt, haltet für abscheulich!" },
      { number: 13, text: "Unter den Vögeln sollt ihr Folgende verabscheuen - man darf sie nicht essen, sie sind abscheulich: Aasgeier, Schwarzgeier, Bartgeier," },
      { number: 14, text: "Milan, die verschiedenen Bussardarten," },
      { number: 15, text: "alle Arten des Raben," },
      { number: 16, text: "Adlereule, Kurzohreule, Langohreule und die verschiedenen Falkenarten," },
      { number: 17, text: "Kauz, Fischeule, Bienenfresser," },
      { number: 18, text: "Weißeule, Kleineule, Fischadler," },
      { number: 19, text: "den Storch, die verschiedenen Reiherarten, Wiedehopf und Fledermaus." },
      { number: 20, text: "Alle Kleintiere mit Flügeln und vier Füßen seien euch abscheulich." },
      { number: 21, text: "Von diesen Kleintieren mit Flügeln und vier Füßen dürft ihr aber jene essen, die Springbeine haben, um damit auf dem Boden zu hüpfen." },
      { number: 22, text: "Von ihnen dürft ihr die verschiedenen Arten der Wanderheuschrecke, der Solam-, der Hargol- und der Hagab-Heuschrecke essen." },
      { number: 23, text: "Alle übrigen Kleintiere mit Flügeln und vier Füßen aber seien euch abscheulich." },
      { number: 24, text: "An diesen Tieren verunreinigt ihr euch; jeder, der ihr Aas berührt, wird unrein bis zum Abend." },
      { number: 25, text: "Jeder, der ihr Aas trägt, muss seine Kleider waschen und ist unrein bis zum Abend." },
      { number: 26, text: "Alle Tiere mit gespaltenen Klauen, die aber nicht Paarzeher sind und nicht wiederkäuen, sollt ihr für unrein halten; jeder, der sie berührt, wird unrein." },
      { number: 27, text: "Alle Vierfüßler, die auf Pfoten gehen, sollt ihr für unrein halten; jeder, der ihr Aas berührt, wird unrein bis zum Abend," },
      { number: 28, text: "und wer ihr Aas trägt, muss seine Kleider waschen und ist unrein bis zum Abend. Ihr sollt sie für unrein halten." },
      { number: 29, text: "Unter dem Kleingetier, das auf dem Boden kriecht, sollt ihr für unrein halten den Maulwurf, die Maus und die verschiedenen Arten der Eidechsen," },
      { number: 30, text: "nämlich den Gecko, die Koach- und die Letaa-Eidechse, den Salamander und das Chamäleon." },
      { number: 31, text: "Unter allem Kleingetier sollt ihr diese für unrein halten. Jeder, der sie berührt, wenn sie tot sind, ist unrein bis zum Abend." },
      { number: 32, text: "Jeder Gegenstand, auf den eines dieser Tiere fällt, wenn sie tot sind, wird unrein, jedes Holzgerät, Kleid, Fell, grobes Zeug und jeder Gebrauchsgegenstand. Man muss einen solchen Gegenstand in Wasser tauchen; er ist unrein bis zum Abend und erst dann wieder rein." },
      { number: 33, text: "Jedes Tongefäß, in das ein solches Tier fällt, müsst ihr zerbrechen und sein Inhalt ist unrein." },
      { number: 34, text: "Jede Speise, die man essen will, wird unrein, wenn Wasser aus einem solchen Gefäß darauf kommt; jedes Getränk, das man trinken will, wird durch ein solches Gefäß unrein." },
      { number: 35, text: "Alles, worauf ein Aas von ihnen fällt, wird unrein; ein Backofen und ein kleiner Herd müssen niedergerissen werden, denn sie sind unrein und als unrein sollen sie euch gelten." },
      { number: 36, text: "Nur eine Quelle und eine Zisterne mit angesammeltem Wasser bleiben rein. Jeder, der ein Aas von jenen Tieren berührt, wird unrein." },
      { number: 37, text: "Wenn ein Aas von ihnen auf irgendeinen Samen fällt, der gesät werden soll, so bleibt er rein." },
      { number: 38, text: "Wenn aber das Korn mit Wasser befeuchtet war und ein solches Aas darauf fällt, sollt ihr es für unrein halten." },
      { number: 39, text: "Wenn eines der Tiere, das euch zur Nahrung dient, verendet, so wird der, der sein Aas berührt, unrein bis zum Abend.", footnotes: ["Ex 22,30"] },
      { number: 40, text: "Wer von solchem Fleisch isst, muss seine Kleider waschen und ist unrein bis zum Abend; wer solches Aas trägt, muss seine Kleider waschen und ist unrein bis zum Abend." },
      { number: 41, text: "Jedes Kleintier, das sich auf dem Boden bewegt, ist abscheulich und darf nicht gegessen werden." },
      { number: 42, text: "Alles, was sich auf dem Bauch oder auf vier und mehr Füßen fortbewegt, kurz alles Kleingetier, das sich auf dem Boden bewegt, dürft ihr nicht essen, denn es ist abscheulich." },
      { number: 43, text: "Macht euch nicht selbst abscheulich mit all diesem Gewimmel von Kleintieren und macht euch durch sie nicht unrein, indem ihr euch durch sie verunreinigen lasst." },
      { number: 44, text: "Denn ich bin der Herr, euer Gott. Erweist euch als heilig, und seid heilig, weil ich heilig bin. Verunreinigt euch daher nicht selbst durch alle diese Kleintiere, die auf dem Boden kriechen." },
      { number: 45, text: "Denn ich bin der Herr, der euch aus Ägypten heraufgeführt hat, um euer Gott zu sein. Ihr sollt daher heilig sein, weil ich heilig bin." },
      { number: 46, text: "Das ist das Gesetz über das Vieh, die Vögel, alle Lebewesen, die sich im Wasser bewegen, und alle Lebewesen, die auf dem Boden kriechen." },
      { number: 47, text: "So soll man das Unreine und das Reine unterscheiden, die Tiere, die man essen darf, und jene, die man nicht essen darf." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Reinigung der Wöchnerin: 12,1-8", footnotes: ["1-8: Zur Unreinheit der Wöchnerin vgl. die Einleitung."] },
      { number: 2, text: "Sag zu den Israeliten: Wenn eine Frau niederkommt und einen Knaben gebiert, ist sie sieben Tage unrein, wie sie in der Zeit ihrer Regel unrein ist." },
      { number: 3, text: "Am achten Tag soll man die Vorhaut des Kindes beschneiden", footnotes: ["Gen 17,12"] },
      { number: 4, text: "und dreiunddreißig Tage soll die Frau wegen ihrer Reinigungsblutung zu Hause bleiben. Sie darf nichts Geweihtes berühren und nicht zum Heiligtum kommen, bis die Zeit ihrer Reinigung vorüber ist." },
      { number: 5, text: "Wenn sie ein Mädchen gebiert, ist sie zwei Wochen unrein wie während ihrer Regel. Sechsundsechzig Tage soll sie wegen ihrer Reinigungsblutung zu Hause bleiben." },
      { number: 6, text: "Wenn die Zeit ihrer Reinigung vorüber ist, soll sie, für einen Sohn ebenso wie für eine Tochter, ein einjähriges Schaf als Brandopfer und eine junge Taube oder eine Turteltaube als Sündopfer zum Priester an den Eingang des Offenbarungszeltes bringen." },
      { number: 7, text: "Er soll es vor dem Herrn darbringen und sie entsühnen; so wird sie von ihrem Blutfluss gereinigt. Das ist das Gesetz für eine Frau, die einen Knaben oder ein Mädchen gebiert." },
      { number: 8, text: "Wenn sie die Mittel für ein Schaf nicht aufbringen kann, soll sie zwei Turteltauben oder zwei junge Tauben nehmen, eine als Brandopfer und die andere als Sündopfer; der Priester soll sie entsühnen und so wird sie gereinigt.", footnotes: ["Lk 2,24"] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Der Herr sprach zu Mose und Aaron:", heading: "Der Aussatz an Menschen: 13,1-46", footnotes: ["1-46: Das mit «Aussatz» wiedergegebene hebräische Wort meint nicht immer die Lepra in unserem Sinn, sondern dürfte auch andere Ekel erregende Hautkrankheiten bezeichnen, die man mit Lepra verwechselte."] },
      { number: 2, text: "Wenn sich auf der Haut eines Menschen eine Schwellung, ein Ausschlag oder ein heller Fleck bildet, liegt Verdacht auf Hautaussatz vor. Man soll ihn zum Priester Aaron oder zu einem seiner Söhne, den Priestern, führen." },
      { number: 3, text: "Der Priester soll das Übel auf der Haut untersuchen. Wenn das Haar an der kranken Stelle weiß wurde und die Stelle tiefer als die übrige Haut liegt, ist es Aussatz. Nachdem der Priester das Übel untersucht hat, soll er den Erkrankten für unrein erklären." },
      { number: 4, text: "Wenn aber auf der Haut ein weißer Fleck besteht, der nicht merklich tiefer als die übrige Haut liegt, und das Haar nicht weiß geworden ist, soll der Priester den Befallenen für sieben Tage absondern." },
      { number: 5, text: "Am siebten Tag untersuche er ihn wieder. Wenn er mit seinen eigenen Augen feststellt, dass das Übel gleich geblieben ist und sich auf der Haut nicht ausgebreitet hat, soll er ihn noch einmal für sieben Tage absondern" },
      { number: 6, text: "und ihn am siebten Tag abermals untersuchen. Wenn er dann feststellt, dass das Übel nachgelassen und sich auf der Haut nicht ausgebreitet hat, soll ihn der Priester für rein erklären. Es handelt sich um einen Ausschlag. Der Kranke soll seine Kleider waschen, dann ist er rein." },
      { number: 7, text: "Breitet sich jedoch der Ausschlag auf der Haut aus, nachdem der Kranke vom Priester untersucht und für rein erklärt wurde, soll er sich ihm noch einmal zeigen." },
      { number: 8, text: "Stellt der Priester fest, dass der Ausschlag sich auf der Haut ausgebreitet hat, soll der Priester ihn für unrein erklären: Es handelt sich um Aussatz." },
      { number: 9, text: "Wenn sich also an jemandem ein Übel von der Art des Aussatzes zeigt, soll man ihn zum Priester bringen." },
      { number: 10, text: "Stellt der Priester fest, dass sich auf der Haut eine weiße Schwellung zeigt, dass die Haare heller geworden sind und dass sich an der Schwellung wildes Fleisch gebildet hat," },
      { number: 11, text: "dann ist es ein veralteter Aussatz auf der Haut. Der Priester soll ihn für unrein erklären, ohne ihn erst abzusondern, denn er ist unrein." },
      { number: 12, text: "Wenn aber der Aussatz auf der Haut ausbricht, sie völlig ergreift und sich vom Kopf bis zu den Füßen erstreckt, überall, wohin der Priester schaut," },
      { number: 13, text: "so soll er den Kranken untersuchen und, falls er feststellt, dass der Aussatz den ganzen Körper bedeckt, den Kranken für rein erklären. Da er völlig weiß geworden ist, ist er rein." },
      { number: 14, text: "An dem Tag jedoch, an dem an ihm wildes Fleisch sichtbar wird, ist er unrein." },
      { number: 15, text: "Hat der Priester das wilde Fleisch untersucht, soll er ihn für unrein erklären. Das wilde Fleisch ist etwas Unreines; es ist Aussatz." },
      { number: 16, text: "Wenn aber das wilde Fleisch verschwindet und die befallene Stelle weiß wird, soll der Mann den Priester aufsuchen." },
      { number: 17, text: "Dieser soll ihn untersuchen, und wenn er feststellt, dass die betroffene Stelle tatsächlich weiß geworden ist, soll er den Kranken für rein erklären: Er ist rein." },
      { number: 18, text: "Wenn sich auf der Haut eines Menschen ein Furunkel bildet und wieder abheilt," },
      { number: 19, text: "sich aber dann an der Stelle des Furunkels eine weiße Schwellung oder ein hellroter Fleck bildet, soll er sich dem Priester zeigen;" },
      { number: 20, text: "dieser soll ihn untersuchen. Wenn er eine merkliche Vertiefung der Haut und heller gewordenes Haar feststellt, soll der Priester ihn für unrein erklären; es ist ein Fall von Aussatz, der im Furunkel ausgebrochen ist." },
      { number: 21, text: "Wenn der Priester bei der Untersuchung weder weiße Haare noch eine Vertiefung der Haut, vielmehr ein Abklingen des Übels feststellt, soll er den Kranken sieben Tage lang absondern." },
      { number: 22, text: "Wenn sich das Übel dann doch auf der Haut ausbreitet, soll er ihn für unrein erklären: Es ist ein Fall von Aussatz." },
      { number: 23, text: "Wenn aber der helle Fleck unverändert bleibt, ohne sich auszubreiten, so ist es eine Narbe vom Furunkel; der Priester soll diesen Menschen für rein erklären." },
      { number: 24, text: "Wenn jemand auf der Haut eine Brandwunde hat und sich eine Wucherung als hellroter oder weißer Fleck bildet," },
      { number: 25, text: "soll ihn der Priester untersuchen. Wenn er heller gewordenes Haar oder eine merkliche Vertiefung des Fleckes in der Haut feststellt, ist es Aussatz, der in der Brandwunde ausgebrochen ist. Der Priester soll den Menschen für unrein erklären; es ist ein Fall von Aussatz." },
      { number: 26, text: "Untersucht ihn der Priester und stellt kein weißes Haar auf dem Fleck, keine Vertiefung der Haut, sondern ein Abklingen fest, so soll er ihn sieben Tage lang absondern." },
      { number: 27, text: "Am siebten Tag soll er ihn wieder untersuchen. Hat sich das Übel auf der Haut ausgebreitet, soll er ihn für unrein erklären; es ist ein Fall von Aussatz." },
      { number: 28, text: "Wenn der helle Fleck unverändert geblieben ist, ohne sich auf der Haut auszubreiten, vielmehr abgeblasst ist, so ist es nur eine angeschwollene Brandnarbe. Der Priester soll den Kranken für rein erklären, denn es ist nur eine Brandnarbe." },
      { number: 29, text: "Zeigt sich bei einem Mann oder bei einer Frau an Kopf oder Kinn eine kranke Stelle," },
      { number: 30, text: "soll der Priester sie untersuchen. Stellt er dort eine merkliche Hautvertiefung mit rötlich gelb glänzendem, schütter gewordenem Haar fest, soll er den Kranken für unrein erklären. Es ist eine Flechte, ein Aussatz des Kopfes oder des Kinns." },
      { number: 31, text: "Stellt der Priester bei der Untersuchung dieses Falls von Flechte weder eine merkliche Hautvertiefung noch schwarzes Haar fest, soll er den mit Flechte Behafteten sieben Tage lang absondern." },
      { number: 32, text: "Am siebten Tag soll er das Übel untersuchen. Stellt er fest, dass sich die Flechte nicht ausgebreitet hat, an ihr kein rötlich gelb glänzendes Haar aufgetreten ist und auch keine merkliche Hautvertiefung besteht," },
      { number: 33, text: "soll sich der Kranke rasieren, dabei aber die befallene Stelle aussparen und der Priester soll ihn noch einmal sieben Tage lang absondern." },
      { number: 34, text: "Am siebten Tag soll er das Übel wieder untersuchen. Stellt er fest, dass es sich auf der Haut nicht ausgebreitet hat und dass keine merkliche Hautvertiefung besteht, soll er den Kranken für rein erklären. Dieser soll seine Kleider waschen, dann ist er rein." },
      { number: 35, text: "Hat sich aber die Flechte nach der Reinerklärung doch auf der Haut ausgebreitet," },
      { number: 36, text: "soll ihn der Priester wieder untersuchen. Stellt er fest, dass sich die Flechte auf der Haut ausbreitet, braucht der Priester nicht erst festzustellen, ob das Haar rötlich gelb glänzend ist; er ist unrein." },
      { number: 37, text: "Scheint aber dem Priester die Flechte gleich zu bleiben und wächst an ihr schwarzes Haar, so heilt sie ab; er ist rein und der Priester soll ihn für rein erklären." },
      { number: 38, text: "Zeigen sich bei einem Mann oder bei einer Frau Flecken, weiße Flecken auf der Haut," },
      { number: 39, text: "so soll der Priester sie untersuchen. Stellt er fest, dass diese Flecken auf der Haut verblassen, so handelt es sich um einen Ausschlag, der auf der Haut ausgebrochen ist; der Kranke ist rein." },
      { number: 40, text: "Verliert ein Mann auf seinem Kopf die Haare, so ist es eine Hinterkopfglatze; er ist rein." },
      { number: 41, text: "Geschieht es an der Schädelvorderseite, so ist es eine Stirnglatze; er ist rein." },
      { number: 42, text: "Entsteht aber auf der Glatze des Hinterkopfes oder über der Stirn ein hellroter Fleck, so ist es Aussatz, der auf dem Kopf oder auf der Stirn dieses Menschen ausbricht." },
      { number: 43, text: "Der Priester soll ihn untersuchen. Stellt er auf der Hinterkopf- oder auf der Stirnglatze eine hellrote Aussatzschwellung fest, die wie Hautaussatz aussieht," },
      { number: 44, text: "so ist der Mensch aussätzig; er ist unrein. Der Priester muss ihn für unrein erklären; er ist an seinem Kopf von Aussatz befallen." },
      { number: 45, text: "Der Aussätzige, der von diesem Übel betroffen ist, soll eingerissene Kleider tragen und das Kopfhaar ungepflegt lassen; er soll den Schnurrbart verhüllen und ausrufen: Unrein! Unrein!" },
      { number: 46, text: "Solange das Übel besteht, bleibt er unrein; er ist unrein. Er soll abgesondert wohnen, außerhalb des Lagers soll er sich aufhalten." },
      { number: 47, text: "Zeigt sich Aussatz auf einem Kleidungsstück, sei es ein Woll- oder Leinenkleid,", heading: "Der Aussatz an Kleidern: 13,47-59", footnotes: ["47-59: Da man nur vom äußeren Anschein ausging, galten auch Veränderungen an Kleidern durch Fäulnis oder Verrottung, die entsprechenden Krankheitserscheinungen auf der menschlichen Haut ähnelten, als lepraverdächtig und ansteckend."] },
      { number: 48, text: "ein Gewebe oder Gewirke aus Leinen oder Wolle, oder auf Leder oder auf irgendeinem Ledergegenstand," },
      { number: 49, text: "so ist das ein Fall von Aussatz, der dem Priester dann zu zeigen ist, wenn der Fleck auf dem Kleid, dem Leder, dem Gewebe, dem Gewirke oder irgendeinem Ledergerät grüngelblich oder rötlich erscheint." },
      { number: 50, text: "Der Priester soll das Übel untersuchen und den befallenen Gegenstand sieben Tage lang absondern." },
      { number: 51, text: "Wenn er am siebten Tage beobachtet, dass sich das Übel auf dem Kleid, dem Gewebe, dem Gewirke, dem Leder oder Ledergegenstand, was immer es auch sein mag, ausgebreitet hat, so ist es ein Fall von bösartigem Aussatz: Der befallene Gegenstand ist unrein." },
      { number: 52, text: "Man soll dieses Kleid, dieses Gewebe, dieses Gewirke aus Wolle oder Leinen oder das Ledergerät, was es auch sein mag, auf dem sich das Übel zeigt, verbrennen; denn es ist bösartiger Aussatz, der im Feuer verbrannt werden muss." },
      { number: 53, text: "Wenn aber der Priester bei der Untersuchung feststellt, dass das Übel sich auf diesem Kleid, Gewebe, Gewirke oder Ledergerät nicht ausgebreitet hat," },
      { number: 54, text: "soll er den befallenen Gegenstand waschen lassen und ihn noch einmal sieben Tage lang absondern." },
      { number: 55, text: "Nach dem Abwaschen soll er das Übel untersuchen, und wenn er feststellt, dass sich sein Aussehen nicht verändert hat, so ist der Gegenstand unrein, auch wenn sich das Übel nicht ausbreitet; du sollst ihn im Feuer verbrennen. Es liegt eine ausgefressene Vertiefung an seiner Vorder- oder Rückseite vor." },
      { number: 56, text: "Stellt aber der Priester bei der Untersuchung fest, dass das Übel nach dem Abwaschen abgeblasst ist, so soll er die befallene Stelle von dem Kleid, dem Leder, dem Gewebe oder dem Gewirke abreißen." },
      { number: 57, text: "Sollte aber das Übel auf diesem Kleid, Gewebe, Gewirke oder Ledergerät wieder erscheinen, so greift das Übel weiter um sich und du sollst den befallenen Gegenstand im Feuer verbrennen." },
      { number: 58, text: "Aber das Kleid, das Gewebe, das Gewirke oder das Ledergerät, auf dem das Übel nach dem Abwaschen verschwunden ist, soll noch einmal gewaschen werden und ist dann rein." },
      { number: 59, text: "Das ist das Gesetz für den Fall von Aussatz auf einem Woll- oder Leinenkleid, einem Gewebe, Gewirke oder Ledergerät, wenn es gilt, sie für rein oder unrein zu erklären." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Reinigungsriten für vom Aussatz Geheilte: 14,1-32" },
      { number: 2, text: "Das ist das Gesetz für den Aussätzigen, wenn er für rein erklärt wird: Man soll ihn zum Priester führen" },
      { number: 3, text: "und der Priester soll vor das Lager herauskommen. Stellt er nach der Untersuchung fest, dass der Aussätzige von seinem Aussatz geheilt ist," },
      { number: 4, text: "soll er anordnen, dass man für den, der sich der Reinigung unterzieht, zwei lebende reine Vögel, Zedernholz, Karmesin und Ysop nimmt." },
      { number: 5, text: "Dann soll er anordnen, den einen Vogel über einem Tongefäß mit Quellwasser zu schlachten." },
      { number: 6, text: "Den lebenden Vogel, das Zedernholz, das Karmesin und den Ysop soll er nehmen und alles, auch den lebenden Vogel, in das Blut des über dem Quellwasser geschlachteten Vogels tauchen." },
      { number: 7, text: "Nun soll er den, der sich der Reinigung vom Aussatz unterzieht, siebenmal besprengen und, nachdem er ihn für rein erklärt hat, den lebenden Vogel ins freie Feld fliegen lassen." },
      { number: 8, text: "Der sich der Reinigung unterzieht, der soll seine Kleider waschen, sein ganzes Haar scheren, sich in Wasser baden und dann rein sein. Nachher darf er ins Lager kommen, muss aber noch sieben Tage außerhalb seines Zeltes bleiben." },
      { number: 9, text: "Am siebten Tag soll er sein ganzes Haar scheren, die Kopfhaare, den Bart und die Augenbrauen; alle Haare muss er scheren. Nachdem er seine Kleider gewaschen und seinen Körper in Wasser gebadet hat, ist er rein." },
      { number: 10, text: "Am achten Tag soll er zwei fehlerlose Widder, ein einjähriges fehlerloses Schaf, drei Zehntel Efa Speiseopfermehl, das mit Öl vermengt ist, und ein Log Öl nehmen." },
      { number: 11, text: "Der Priester, der die Reinigung vornimmt, soll den, der sich der Reinigung unterzieht, mit seinen Opfergaben am Eingang des Offenbarungszeltes vor dem Herrn aufstellen." },
      { number: 12, text: "Dann soll er den einen Widder nehmen, ihn zusammen mit dem Log Öl als Schuldopfer darbringen und mit beiden den Darbringungsritus vor dem Herrn vollziehen." },
      { number: 13, text: "Er soll den Widder an der Stelle schlachten, wo man das Sünd- und das Brandopfer schlachtet, an dem heiligen Ort. Dieses Schuldopfer nämlich gehört wie ein Sündopfer dem Priester, es ist etwas Hochheiliges." },
      { number: 14, text: "Der Priester soll etwas Blut vom Schlachtopfer nehmen und es auf das rechte Ohrläppchen dessen tun, der sich der Reinigung unterzieht, und auf den Daumen seiner rechten Hand und auf die große Zehe seines rechten Fußes.", footnotes: ["8,23; Ex 29,20"] },
      { number: 15, text: "Dann soll er etwas von dem Log Öl nehmen und auf seinen eigenen linken Handteller gießen." },
      { number: 16, text: "Er soll einen Finger seiner rechten Hand in das Öl, das auf seinem linken Handteller ist, tauchen und mit diesem Finger siebenmal Öl vor dem Herrn verspritzen." },
      { number: 17, text: "Dann soll er etwas von dem auf seinem Handteller übrig gebliebenen Öl auf das rechte Ohrläppchen dessen tun, der sich der Reinigung unterzieht, auf den Daumen seiner rechten Hand und auf die große Zehe seines rechten Fußes, auf das Blut des Schuldopfers." },
      { number: 18, text: "Den Rest des Öls, das er auf seinem Handteller hat, soll er auf den Kopf dessen tun, der sich der Reinigung unterzieht. So soll er ihn vor dem Herrn entsühnen." },
      { number: 19, text: "Nun soll der Priester das Sündopfer durchführen und den, der sich der Reinigung unterzieht, von seiner Unreinheit entsühnen. Danach soll er das Brandopfer schlachten" },
      { number: 20, text: "und dieses und das Speiseopfer auf dem Altar als Ganzopfer darbringen. Hat der Priester den betreffenden Menschen entsühnt, so ist er rein." },
      { number: 21, text: "Wenn er arm ist und seine Mittel nicht ausreichen, soll der Priester einen einzigen Schuldopferwidder für den Darbringungsritus nehmen, um ihn zu entsühnen. Er soll nur ein Zehntel Efa Feinmehl, das mit Öl vermengt ist, und ein Log Öl als Speiseopfer nehmen," },
      { number: 22, text: "und je nachdem es seine Mittel gestatten, soll er zwei Turteltauben oder zwei junge Tauben bringen, von denen die eine als Sündopfer und die andere als Brandopfer dienen soll." },
      { number: 23, text: "Er soll sie am achten Tag zu seiner Reinigung dem Priester zum Eingang des Offenbarungszeltes vor den Herrn bringen." },
      { number: 24, text: "Der Priester soll den Schuldopferwidder und das Log Öl nehmen und damit den Darbringungsritus vor dem Herrn vollziehen." },
      { number: 25, text: "Hat er diesen Schuldopferwidder geschlachtet, dann nehme er etwas Blut vom Schuldopfer und tue es auf das rechte Ohrläppchen dessen, der sich der Reinigung unterzieht, auf den Daumen seiner rechten Hand und auf die große Zehe seines rechten Fußes." },
      { number: 26, text: "Er soll etwas Öl auf seinen linken Handteller gießen" },
      { number: 27, text: "und etwas von diesem Öl, das auf seinem linken Handteller ist, mit seinem rechten Zeigefinger siebenmal vor dem Herrn verspritzen." },
      { number: 28, text: "Dann soll er etwas von dem Öl, das auf seinem Handteller ist, auf das rechte Ohrläppchen dessen, der sich der Reinigung unterzieht, auf den Daumen seiner rechten Hand und auf die große Zehe seines rechten Fußes tun, und zwar auf die Stelle des Schuldopferblutes." },
      { number: 29, text: "Den Rest des Öls auf seinem Handteller soll der Priester auf den Kopf dessen tun, der sich der Reinigung unterzieht. So soll er ihn vor dem Herrn entsühnen." },
      { number: 30, text: "Seinen Mitteln entsprechend soll er die eine der beiden Turteltauben oder jungen Tauben" },
      { number: 31, text: "als ein Sündopfer und die andere als ein Brandopfer mit einem Speiseopfer verwenden. Der Priester soll damit den, der sich der Reinigung unterzieht, vor dem Herrn entsühnen." },
      { number: 32, text: "Das ist das Gesetz für einen, der vom Aussatz befallen ist und dessen Mittel für seine Reinigung nicht ausreichen." },
      { number: 33, text: "Der Herr sprach zu Mose und Aaron:", heading: "Der Aussatz an Häusern: 14,33-57", footnotes: ["33-57: Ähnliches wie zum Aussatz an Kleidern ist zum Aussatz an Häusern zu sagen (vgl. die Anmerkung zu 13,47-59)."] },
      { number: 34, text: "Wenn ihr in das Land Kanaan kommt, das ich euch zum Besitz gebe, und ich lasse an einem Haus des Landes, das ihr besitzen werdet, Aussatz auftreten," },
      { number: 35, text: "so soll der Hausherr kommen, es dem Priester anzeigen und sagen: Ich habe an meinem Haus so etwas wie Aussatz gesehen." },
      { number: 36, text: "Der Priester soll anordnen, dass man das Haus räumt, bevor er kommt, um das Übel zu untersuchen; auf diese Weise wird das, was sich im Haus befindet, nicht unrein. Danach erst soll der Priester kommen, um das Haus zu besichtigen." },
      { number: 37, text: "Stellt er dabei fest, dass sich an den Mauern des Hauses grünlich gelbe oder rötliche Vertiefungen zeigen, die Mulden in der Mauer bilden," },
      { number: 38, text: "so soll der Priester aus dem Haus hinausgehen und den Eingang für sieben Tage abschließen." },
      { number: 39, text: "Am siebten Tag soll er wiederkommen. Stellt er bei der Besichtigung fest, dass sich das Übel an den Hausmauern ausgebreitet hat," },
      { number: 40, text: "so ordne er an, die Steine, die vom Übel befallen sind, herauszureißen und sie vor die Stadt hinaus an einen unreinen Ort zu werfen." },
      { number: 41, text: "Dann soll er die Innenwände des Hauses abkratzen lassen und man soll den so entfernten Mörtel aus der Stadt hinausschaffen und an einen unreinen Ort schütten." },
      { number: 42, text: "Man soll andere Steine nehmen, um die herausgerissenen zu ersetzen, und das Haus mit frischem Mörtel bestreichen." },
      { number: 43, text: "Hat man die Steine entfernt, das Haus abgekratzt und neu verputzt und das Übel bricht wieder aus," },
      { number: 44, text: "soll der Priester kommen, um es zu besichtigen. Stellt er fest, dass sich das Übel an dem Haus ausgebreitet hat, so ist bösartiger Aussatz an dem Haus; es ist unrein." },
      { number: 45, text: "Man soll es niederreißen und seine Steine, seine Balken und seinen ganzen Mörtelverputz vor die Stadt hinausbringen an einen unreinen Ort." },
      { number: 46, text: "Jeder, der das Haus während der Tage, an denen es durch den Priester verschlossen war, betreten hat, ist unrein bis zum Abend." },
      { number: 47, text: "Wer im Haus geschlafen hat, muss seine Kleider waschen; wer im Haus gegessen hat, muss seine Kleider waschen." },
      { number: 48, text: "Kommt aber der Priester, um das Übel zu besichtigen, und stellt fest, dass sich das Übel, nachdem das Haus neu verputzt wurde, nicht ausgebreitet hat, soll er das Haus für rein erklären, denn das Übel ist abgeheilt." },
      { number: 49, text: "Um das Haus zu entsündigen, soll er zwei Vögel, Zedernholz, Karmesin und Ysop nehmen." },
      { number: 50, text: "Er soll einen der Vögel über einem Tongefäß mit Quellwasser schlachten." },
      { number: 51, text: "Dann soll er das Zedernholz, den Ysop, das Karmesin und den lebenden Vogel nehmen, um sie in das Blut des geschlachteten Vogels und in das Quellwasser zu tauchen. Er soll das Haus siebenmal besprengen und," },
      { number: 52, text: "nachdem er das Haus mit dem Blut des Vogels, dem Quellwasser, dem lebenden Vogel, dem Zedernholz, dem Ysop und dem Karmesin entsündigt hat," },
      { number: 53, text: "den lebenden Vogel aus der Stadt hinaus ins freie Feld fliegen lassen. So entsühnt er das Haus und es ist wieder rein." },
      { number: 54, text: "Das ist das Gesetz für alle Fälle von Aussatz und Flechte," },
      { number: 55, text: "von Aussatz an Kleidern und Häusern," },
      { number: 56, text: "von Geschwülsten, Ausschlag und hellen Flecken," },
      { number: 57, text: "zur Unterweisung, wann etwas unrein oder rein ist. Das ist das Gesetz über den Aussatz." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Der Herr sprach zu Mose und Aaron:", heading: "Die Unreinheit bei Männern: 15,1-18" },
      { number: 2, text: "Redet zu den Israeliten und sagt zu ihnen: Wenn ein Mann einen Ausfluss aus seinem Körper hat, so ist dieser Ausfluss unrein.", footnotes: ["«Ausfluss aus dem Körper» sind alle krankhaften Ausflüsse aus den natürlichen Körperöffnungen und aus ständig nässenden Fisteln. Dazu und zu 15,19-33 vgl. die Einleitung."] },
      { number: 3, text: "Hat er diesen Ausfluss, so besteht seine Unreinheit, ob sein Körper den Ausfluss fließen lässt oder ihn zurückhält; bei ihm liegt Unreinheit vor." },
      { number: 4, text: "Jedes Lager, auf das sich dieser Mann legt, und jeder Gegenstand, auf den er sich setzt, ist unrein." },
      { number: 5, text: "Wer sein Lager berührt, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 6, text: "Wer sich auf einen Gegenstand setzt, auf dem der Leidende saß, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 7, text: "Wer den Körper des Leidenden berührt, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 8, text: "Wenn der Kranke eine reine Person anspuckt, muss sie ihre Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 9, text: "Jeder Sattel, den der Kranke benutzt, ist unrein." },
      { number: 10, text: "Jeder, der irgendeinen Gegenstand berührt, der unter dem Kranken war, ist unrein bis zum Abend. Wer einen solchen Gegenstand trägt, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 11, text: "Jeder, den der Kranke berührt, ohne zuvor seine Hände mit Wasser abzuspülen, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 12, text: "Ein Tongefäß, das der Kranke berührt, muss zerbrochen und jedes Holzgerät mit Wasser abgespült werden." },
      { number: 13, text: "Wird dieser Mann von seinem Ausfluss rein, soll er sieben Tage bis zu seiner Reinigung zählen. Danach muss er seine Kleider waschen, seinen Körper in Quellwasser baden und ist dann rein." },
      { number: 14, text: "Am achten Tag soll er zwei Turteltauben oder zwei junge Tauben nehmen, vor den Herrn zum Eingang des Offenbarungszeltes kommen und sie dem Priester geben." },
      { number: 15, text: "Mit der einen soll der Priester ein Sündopfer und mit der andern ein Brandopfer darbringen. Der Priester soll ihn so vor dem Herrn wegen seines Ausflusses entsühnen." },
      { number: 16, text: "Hat ein Mann Samenerguss, soll er seinen ganzen Körper in Wasser baden und ist unrein bis zum Abend." },
      { number: 17, text: "Jedes Kleid und jedes Lederstück, auf das Samen gekommen ist, muss mit Wasser gesäubert werden und ist unrein bis zum Abend." },
      { number: 18, text: "Schläft ein Mann, der Samenerguss hat, mit einer Frau, müssen sie sich beide in Wasser baden und sind unrein bis zum Abend." },
      { number: 19, text: "Hat eine Frau Blutfluss und ist solches Blut an ihrem Körper, soll sie sieben Tage lang in der Unreinheit ihrer Regel verbleiben. Wer sie berührt, ist unrein bis zum Abend.", heading: "Die Unreinheit bei Frauen: 15,19-33" },
      { number: 20, text: "Alles, worauf sie sich in diesem Zustand legt, ist unrein; alles, worauf sie sich setzt, ist unrein." },
      { number: 21, text: "Wer ihr Lager berührt, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 22, text: "Wer irgendeinen Gegenstand berührt, auf dem sie saß, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 23, text: "Befindet sich etwas auf dem Bett oder auf dem Gegenstand, auf dem sie saß, wird derjenige, der es berührt, unrein bis zum Abend." },
      { number: 24, text: "Schläft ein Mann mit ihr, so kommt die Unreinheit ihrer Regel auf ihn. Er wird für sieben Tage unrein. Jedes Lager, auf das er sich legt, wird unrein." },
      { number: 25, text: "Hat eine Frau mehrere Tage außerhalb der Zeit ihrer Regel einen Blutfluss oder hält ihre Regel länger an, ist sie für die ganze Dauer dieses Ausflusses im gleichen Zustand der Unreinheit wie während der Zeit ihrer Regel." },
      { number: 26, text: "Jedes Lager, auf das sie sich während der ganzen Dauer ihres Ausflusses legt, ist so wie ihr Lager, auf dem sie während ihrer Regel liegt. Jeder Gegenstand, auf den sie sich setzt, wird unrein wie bei der Unreinheit ihrer Regel." },
      { number: 27, text: "Jeder, der diese Gegenstände berührt, wird unrein; er muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend." },
      { number: 28, text: "Ist sie von ihrem Ausfluss rein, soll sie sieben Tage zählen und dann rein sein." },
      { number: 29, text: "Am achten Tag soll sie zwei Turteltauben oder zwei junge Tauben nehmen und sie dem Priester zum Eingang des Offenbarungszeltes bringen." },
      { number: 30, text: "Der Priester soll die eine als Sündopfer und die andere als Brandopfer verwenden. Er soll sie so vor dem Herrn wegen ihres verunreinigenden Ausflusses entsühnen." },
      { number: 31, text: "Ihr sollt die Israeliten vor ihrer Unreinheit warnen, damit sie nicht in ihr sterben müssen, weil sie meine Wohnstätte in ihrer Mitte verunreinigen." },
      { number: 32, text: "Das ist das Gesetz für einen Mann, der einen Ausfluss hat, für einen, den ein Samenerguss unrein gemacht hat," },
      { number: 33, text: "und für die Frau in der Unreinheit ihrer Regel, also für den Mann oder die Frau mit Ausfluss und den Mann, der mit einer unreinen Frau schläft." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Nach dem Tod der beiden Söhne Aarons, die umgekommen waren, als sie vor den Herrn hintraten, redete der Herr mit Mose.", heading: "Das Ritual für den Versöhnungstag: 16,1-34", footnotes: ["10,1-5", "1-34: Der Versöhnungstag wird am 10. Tag des Monats Tischri (September/Oktober), fünf Tage vor dem Laubhüttenfest, gefeiert. Er ist ein Fasttag und der Tag, an dem der Hohepriester sich, die Priester und das Volk für alle Vergehen entsühnt. Nur an diesem Tag darf er das Allerheiligste des Tempels betreten (vgl. auch Num 29,7-11)."] },
      { number: 2, text: "Der Herr sprach zu Mose: Sag deinem Bruder Aaron, er soll nicht zu jeder beliebigen Zeit das Heiligtum hinter dem Vorhang vor der Deckplatte der Lade betreten. Dann wird er nicht sterben, wenn ich über der Deckplatte in einer Wolke erscheine.", footnotes: ["Ex 25,17-21"] },
      { number: 3, text: "Aaron darf nur so in das Heiligtum kommen: mit einem Jungstier für ein Sündopfer und einem Widder für ein Brandopfer." },
      { number: 4, text: "Ein geweihtes Leinengewand soll er anhaben, leinene Beinkleider tragen, sich mit einem Leinengürtel gürten und um den Kopf einen Leinenturban binden. Das sind heilige Gewänder; deshalb soll er seinen ganzen Körper in Wasser baden und sie erst dann anlegen." },
      { number: 5, text: "Von der Gemeinde der Israeliten soll er zwei Ziegenböcke für ein Sündopfer und einen Widder für ein Brandopfer erhalten." },
      { number: 6, text: "Hat er den Jungstier für seine eigene Sünde dargebracht und sich und sein Haus entsühnt," },
      { number: 7, text: "dann soll Aaron die beiden Ziegenböcke nehmen und sie vor dem Herrn am Eingang des Offenbarungszeltes aufstellen." },
      { number: 8, text: "Für die beiden Böcke soll er Lose kennzeichnen, ein Los «für den Herrn» und ein Los «für Asasel».", footnotes: ["8.20-22: Der Ritus mit dem Sündenbock für Asasel geht wohl auf einen alten Brauch zurück, der durch den Jahwekult nicht ganz verdrängt werden konnte und darum als symbolische Handlung in das Ritual des Versöhnungstags eingebaut wurde. Asasel ist ein Wüstendämon. Alle Sünden Israels sollen durch die Handauflegung gleichsam auf den Ziegenbock geladen und mit ihm in die Wüste, zu den Wüstendämonen, getrieben, d. h. aus der Mitte Israels verbannt werden."] },
      { number: 9, text: "Aaron soll den Bock, für den das Los «für den Herrn» herauskommt, herbeiführen und ihn als Sündopfer darbringen." },
      { number: 10, text: "Der Bock, für den das Los «für Asasel» herauskommt, soll lebend vor den Herrn gestellt werden, um für die Sühne zu dienen und zu Asasel in die Wüste geschickt zu werden." },
      { number: 11, text: "Aaron soll den Jungstier für sein eigenes Sündopfer herbeibringen lassen, um sich und sein Haus zu entsühnen, und diesen Jungstier als Sündopfer für sich schlachten." },
      { number: 12, text: "Dann soll er eine Räucherpfanne voll glühender Kohlen vom Altar, der vor dem Herrn steht, und zwei Hand voll zerstoßenen duftenden Räucherwerks nehmen. Er soll alles hinter den Vorhang bringen" },
      { number: 13, text: "und das Räucherwerk auf das Feuer vor dem Herrn tun; die Wolke des Räucherwerks soll die Deckplatte über der Lade einhüllen, damit er nicht sterben muss." },
      { number: 14, text: "Dann soll er vom Blut des Jungstiers nehmen und es mit seinem Finger gegen die Vorderseite der Deckplatte spritzen; auch vor die Deckplatte soll er mit seinem Finger siebenmal etwas Blut spritzen." },
      { number: 15, text: "Nachher soll er den Bock schlachten, der als Sündopfer für das Volk bestimmt ist, und sein Blut hinter den Vorhang tragen. Er soll es mit diesem Blut ebenso machen wie mit dem Blut des Jungstiers und es auf die Deckplatte und vor die Deckplatte spritzen." },
      { number: 16, text: "So soll er das Heiligtum von den Unreinheiten der Israeliten, von all ihren Freveltaten und Sünden entsühnen und so soll er mit dem Offenbarungszelt verfahren, das bei ihnen inmitten ihrer Unreinheiten seinen Sitz hat." },
      { number: 17, text: "Kein Mensch darf im Offenbarungszelt sein, wenn er in das Heiligtum eintritt, um die Sühne zu vollziehen, bis er es wieder verlässt. Hat er sich, sein Haus und die ganze Gemeinde Israels entsühnt," },
      { number: 18, text: "so soll er zum Altar vor dem Herrn hinausgehen und ihn entsühnen. Er soll etwas Blut des Jungstiers und des Bockes nehmen und es auf die Hörner rings um den Altar tun." },
      { number: 19, text: "Etwas von diesem Blut soll er mit seinem Finger siebenmal auf den Altar spritzen. So soll er ihn von den Unreinheiten der Israeliten reinigen und ihn heiligen." },
      { number: 20, text: "Hat er so die Entsühnung des Heiligtums, des Offenbarungszeltes und des Altars beendet, soll er den lebenden Bock herbringen lassen." },
      { number: 21, text: "Aaron soll seine beiden Hände auf den Kopf des lebenden Bockes legen und über ihm alle Sünden der Israeliten, alle ihre Frevel und alle ihre Fehler bekennen. Nachdem er sie so auf den Kopf des Bockes geladen hat, soll er ihn durch einen bereitstehenden Mann in die Wüste treiben lassen" },
      { number: 22, text: "und der Bock soll alle ihre Sünden mit sich in die Einöde tragen. Hat er den Bock in die Wüste geschickt," },
      { number: 23, text: "dann soll Aaron wieder in das Offenbarungszelt gehen, die Leinengewänder, die er beim Betreten des Heiligtums angelegt hat, ablegen und sie dort verwahren." },
      { number: 24, text: "Er soll seinen Körper in Wasser an einem heiligen Ort baden, wieder seine Kleider anlegen und hinausgehen, um sein Brandopfer und das des Volkes darzubringen. Er soll sich und das Volk entsühnen" },
      { number: 25, text: "und das Fett des Sündopfers auf dem Altar in Rauch aufgehen lassen." },
      { number: 26, text: "Der Mann, der den Bock für Asasel hinausgeführt hat, muss seine Kleider waschen, seinen Körper in Wasser baden und darf danach wieder in das Lager kommen." },
      { number: 27, text: "Den Jungstier und den Bock, die man als Sündopfer dargebracht und deren Blut man in das Heiligtum zur Entsühnung gebracht hat, soll man aus dem Lager hinausschaffen und ihr Fell, ihr Fleisch und ihren Mageninhalt im Feuer verbrennen." },
      { number: 28, text: "Wer sie verbrannt hat, muss seine Kleider waschen, den Körper in Wasser baden und darf danach wieder ins Lager kommen." },
      { number: 29, text: "Folgendes soll euch als feste Regel gelten: Im siebten Monat, am zehnten Tag des Monats, sollt ihr euch Enthaltung auferlegen und keinerlei Arbeit tun, der Einheimische und ebenso der Fremde, der in eurer Mitte lebt." },
      { number: 30, text: "Denn an diesem Tag entsühnt man euch, um euch zu reinigen. Vor dem Herrn werdet ihr von allen euren Sünden wieder rein." },
      { number: 31, text: "Dieser Tag ist für euch ein vollständiger Ruhetag, und ihr sollt euch Enthaltung auferlegen. Das gelte als feste Regel." },
      { number: 32, text: "Der Priester, den man gesalbt und an Stelle seines Vaters als Priester eingesetzt hat, soll die Sühne vollziehen. Er soll die Leinengewänder, die heiligen Gewänder, anlegen." },
      { number: 33, text: "Er soll das geweihte Heiligtum, das Offenbarungszelt und den Altar entsühnen; dann soll er die Priester und das ganze Volk der Gemeinde entsühnen." },
      { number: 34, text: "Das soll für euch als feste Regel gelten: Einmal im Jahr sollen die Israeliten von allen ihren Sünden entsühnt werden. Und man tat, wie es der Herr dem Mose befohlen hatte." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Das Heiligkeitsgesetz: 17,1 - 26,46 | Schlachtungen und Blutgenuss: 17,1-16", footnotes: ["1-16: Dieser Text scheint zu fordern, dass die Schlachtung eines Tiers nur am Heiligtum geschehen darf, und zwar in Verbindung mit einem Opfer, wobei zumindest das Fett und das Blut Gott gehören. Ein solches Gesetz wäre nur denkbar zur Zeit der Wüstenwanderung, wie sie sich die Priestertradition vorstellt. In Palästina nach der Sesshaftwerdung wäre es nicht durchführbar gewesen. Vielleicht meint aber 17,1-9 nur, dass man nirgends als nur am Heiligtum Schlachtopfer darbringen darf. So ist jedenfalls Dtn 12 gemeint, wo ausdrücklich festgestellt wird, dass man überall einfache Schlachtungen vornehmen darf, wenn man nur das Blut nicht genießt, sondern zur Erde rinnen lässt und mit Erde bedeckt. Auch in Lev 17 wird der Blutgenuss ausdrücklich verboten. Das Verbot wird in V. 11 damit begründet, dass das Blut Sitz des Lebens und von Gott dazu vorgesehen ist, beim Sühnopfer als Ersatz für das durch die Sünde verwirkte Blut des Sünders zu dienen."] },
      { number: 2, text: "Rede zu Aaron, seinen Söhnen und allen Israeliten und sag zu ihnen: Das ist es, worauf der Herr euch verpflichtet hat:" },
      { number: 3, text: "Jeder Mann aus dem Haus Israel, der innerhalb oder außerhalb des Lagers ein Rind, ein Schaf oder eine Ziege schlachtet" },
      { number: 4, text: "und das Tier nicht zum Eingang des Offenbarungszeltes bringt, um es dem Herrn vor seiner Wohnstätte zu opfern, dem soll es als Blutschuld angerechnet werden; er hat Blut vergossen und soll aus der Mitte seines Volkes ausgemerzt werden." },
      { number: 5, text: "Daher sollen die Israeliten die Opfer, die sie (jetzt noch) auf dem freien Feld schlachten, dem Priester für den Herrn zum Eingang des Offenbarungszeltes bringen und sie als Heilsopfer für den Herrn schlachten.", footnotes: ["3,1-17"] },
      { number: 6, text: "Der Priester soll das Blut auf den Altar des Herrn am Eingang des Offenbarungszeltes gießen und das Fett zum beruhigenden Duft für den Herrn in Rauch aufgehen lassen." },
      { number: 7, text: "Sie sollen nicht mehr ihre Schlachtopfer für Bocksdämonen schlachten, mit denen sie Unzucht treiben. Das gelte ihnen und ihren kommenden Generationen als feste Regel." },
      { number: 8, text: "Du sollst ihnen weiter sagen: Jeder Mann aus dem Haus Israel oder jeder Fremde in eurer Mitte, der ein Brand- oder Schlachtopfer darbringt," },
      { number: 9, text: "ohne es zum Eingang des Offenbarungszeltes zu bringen, um es für den Herrn zu vollziehen, soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 10, text: "Jeder Mann aus dem Haus Israel oder jeder Fremde in eurer Mitte, der irgendwie Blut genießt, gegen einen solchen werde ich mein Angesicht wenden und ihn aus der Mitte seines Volkes ausmerzen." },
      { number: 11, text: "Die Lebenskraft des Fleisches sitzt nämlich im Blut. Dieses Blut habe ich euch gegeben, damit ihr auf dem Altar für euer Leben die Sühne vollzieht; denn das Blut ist es, das für ein Leben sühnt.", footnotes: ["Gen 9,4; Dtn 12,23"] },
      { number: 12, text: "Deshalb habe ich zu den Israeliten gesagt: Niemand unter euch darf Blut genießen, auch der Fremde, der in eurer Mitte lebt, darf kein Blut genießen." },
      { number: 13, text: "Jeder unter den Israeliten oder der Fremde in eurer Mitte, der Wild oder für den Genuss erlaubte Vögel erlegt, muss das Blut ausfließen lassen und es mit Erde bedecken." },
      { number: 14, text: "Denn das Leben aller Wesen aus Fleisch ist das Blut, das darin ist. Ich habe zu den Israeliten gesagt: Das Blut irgendeines Wesens aus Fleisch dürft ihr nicht genießen; denn das Leben aller Wesen aus Fleisch ist ihr Blut. Jeder, der es genießt, soll ausgemerzt werden." },
      { number: 15, text: "Jeder, sei er einheimisch oder fremd, der ein verendetes oder zerrissenes Tier isst, muss seine Kleider waschen, sich in Wasser baden und ist unrein bis zum Abend; dann ist er rein.", footnotes: ["11,40; Ex 22,30"] },
      { number: 16, text: "Wer seine Kleider nicht wäscht und seinen Körper nicht in Wasser badet, muss die Folgen seiner Schuld tragen." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Sexuelle Vorschriften: 18,1-30 | Einleitung: 18,1-5" },
      { number: 2, text: "Rede zu den Israeliten und sag zu ihnen: Ich bin der Herr, euer Gott." },
      { number: 3, text: "Ihr sollt nicht tun, was man in Ägypten tut, wo ihr gewohnt habt; ihr sollt nicht tun, was man in Kanaan tut, wohin ich euch führe. Ihre Bräuche sollt ihr nicht befolgen." },
      { number: 4, text: "Meine Vorschriften sollt ihr einhalten und meine Satzungen sollt ihr beachten und befolgen. Ich bin der Herr, euer Gott." },
      { number: 5, text: "Ihr sollt auf meine Satzungen und meine Vorschriften achten. Wer sie einhält, wird durch sie leben. Ich bin der Herr." },
      { number: 6, text: "Niemand von euch darf sich einer Blutsverwandten nähern, um ihre Scham zu entblößen. Ich bin der Herr.", heading: "Unzucht unter Verwandten: 18,6-18" },
      { number: 7, text: "Die Scham deines Vaters, nämlich die Scham deiner Mutter, darfst du nicht entblößen. Sie ist deine Mutter, du darfst ihre Scham nicht entblößen." },
      { number: 8, text: "Die Scham der Frau deines Vaters darfst du nicht entblößen; sie ist die Scham deines Vaters." },
      { number: 9, text: "Die Scham deiner Schwester, einer Tochter deines Vaters oder einer Tochter deiner Mutter, darfst du nicht entblößen, sei sie im Haus oder außerhalb geboren." },
      { number: 10, text: "Die Scham einer Tochter deines Sohnes oder einer Tochter deiner Tochter darfst du nicht entblößen; denn ihre Scham ist deine eigene Scham." },
      { number: 11, text: "Die Scham der Tochter einer Frau deines Vaters darfst du nicht entblößen. Sie ist deinem Vater geboren, also deine Schwester; du darfst ihre Scham nicht entblößen." },
      { number: 12, text: "Die Scham einer Schwester deines Vaters darfst du nicht entblößen; denn sie ist mit deinem Vater leiblich verwandt." },
      { number: 13, text: "Die Scham der Schwester deiner Mutter darfst du nicht entblößen; denn sie ist mit deiner Mutter leiblich verwandt." },
      { number: 14, text: "Die Scham des Bruders deines Vaters darfst du nicht entblößen; du darfst dich seiner Frau nicht nähern; denn sie ist deine Tante." },
      { number: 15, text: "Die Scham deiner Schwiegertochter darfst du nicht entblößen. Sie ist die Frau deines Sohnes; du darfst ihre Scham nicht entblößen." },
      { number: 16, text: "Die Scham der Frau deines Bruders darfst du nicht entblößen; denn sie ist die Scham deines Bruders." },
      { number: 17, text: "Die Scham einer Frau und gleichzeitig die ihrer Tochter darfst du nicht entblößen; weder die Tochter ihres Sohnes noch die Tochter ihrer Tochter darfst du nehmen, um ihre Scham zu entblößen. Sie sind leiblich verwandt, es wäre Blutschande." },
      { number: 18, text: "Du darfst neben einer Frau nicht auch noch deren Schwester heiraten; du würdest sie zur Nebenbuhlerin machen, wenn du zu Lebzeiten der Frau die Scham ihrer Schwester entblößt." },
      { number: 19, text: "Einer Frau, die wegen ihrer Regel unrein ist, darfst du dich nicht nähern, um ihre Scham zu entblößen.", heading: "Andere Unzuchtvergehen: 18,19-23" },
      { number: 20, text: "Der Frau deines Stammesgenossen darfst du nicht beiwohnen; du würdest dadurch unrein." },
      { number: 21, text: "Von deinen Nachkommen darfst du keinen für Moloch darbringen. Du darfst den Namen deines Gottes nicht entweihen. Ich bin der Herr.", footnotes: ["20,2-5", "Kinderopfer waren in außerordentlichen Notsituationen bei den Kanaanitern üblich (vgl. die Anmerkung zu Gen 22,1-19). Moloch bedeutet eigentlich «König» und ist ein entstellter Beiname für Baal. Nach 2 Kön 23,10; Jer 32,35; Ez 16,20-22 ist das Kinderopfer für Moloch zur Königszeit auch in Israel und Juda geübt worden, wobei man Kinder «durchs Feuer gehen ließ», d. h. verbrannte."] },
      { number: 22, text: "Du darfst nicht mit einem Mann schlafen, wie man mit einer Frau schläft; das wäre ein Gräuel." },
      { number: 23, text: "Keinem Vieh darfst du beiwohnen; du würdest dadurch unrein. Keine Frau darf vor ein Vieh hintreten, um sich mit ihm zu begatten; das wäre eine schandbare Tat." },
      { number: 24, text: "Ihr sollt euch nicht durch all das verunreinigen; denn durch all das haben sich die Völker verunreinigt, die ich vor euch vertrieben habe.", heading: "Schlussmahnung: 18,24-30" },
      { number: 25, text: "Das Land wurde unrein, ich habe an ihm seine Schuld geahndet und das Land hat seine Bewohner ausgespien." },
      { number: 26, text: "Ihr aber sollt auf meine Satzungen und Vorschriften achten und keine dieser Gräueltaten begehen, weder der Einheimische noch der Fremde, der in eurer Mitte lebt." },
      { number: 27, text: "Denn all diese Gräueltaten haben die Leute begangen, die vor euch im Land waren, und so wurde das Land unrein." },
      { number: 28, text: "Wird es etwa euch, wenn ihr es verunreinigt, nicht ebenso ausspeien, wie es das Volk vor euch ausgespien hat?" },
      { number: 29, text: "Alle nämlich, die irgendeine dieser Gräueltaten begehen, werden aus der Mitte ihres Volkes ausgemerzt." },
      { number: 30, text: "Achtet auf meine Anordnungen, befolgt keinen von den gräulichen Bräuchen, die man vor euch befolgt hat, und verunreinigt euch nicht durch sie. Ich bin der Herr, euer Gott." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Kultische und soziale Gebote: 19,1-37 | Einleitung: 19,1-2" },
      { number: 2, text: "Rede zur ganzen Gemeinde der Israeliten und sag zu ihnen: Seid heilig, denn ich, der Herr, euer Gott, bin heilig." },
      { number: 3, text: "Jeder von euch soll Mutter und Vater fürchten und auf meine Sabbate achten; ich bin der Herr, euer Gott.", heading: "Die Ehrfurcht gegenüber den Eltern: 19,3-4", footnotes: ["(3-4) Ex 20,1-12; 23,12; 34,14-17.21; Dtn 5,6-16"] },
      { number: 4, text: "Ihr sollt euch nicht anderen Göttern zuwenden und euch nicht Götterbilder aus Metall gießen; ich bin der Herr, euer Gott." },
      { number: 5, text: "Wenn ihr für den Herrn ein Heilsopfer schlachtet, opfert es so, dass ihr Annahme findet.", heading: "Die rechte Einstellung zum Heilsopfer: 19,5-8", footnotes: ["(5-8) 7,14-18"] },
      { number: 6, text: "An dem Tag, an dem ihr es schlachtet, oder am folgenden Tag soll es gegessen werden; was davon am dritten Tag noch übrig ist, soll im Feuer verbrannt werden." },
      { number: 7, text: "Isst man davon am dritten Tag, so ist das Opfer untauglich und findet keine Annahme mehr." },
      { number: 8, text: "Wer davon isst, muss die Folgen seiner Schuld tragen; denn er hat die Heiligkeit des Herrn entweiht. Ein solcher Mensch soll aus seinen Stammesgenossen ausgemerzt werden." },
      { number: 9, text: "Wenn ihr die Ernte eures Landes einbringt, sollt ihr das Feld nicht bis zum äußersten Rand abernten. Du sollst keine Nachlese von deiner Ernte halten.", heading: "Das Verbot der Nachlese: 19,9-10" },
      { number: 10, text: "In deinem Weinberg sollst du keine Nachlese halten und die abgefallenen Beeren nicht einsammeln. Du sollst sie dem Armen und dem Fremden überlassen. Ich bin der Herr, euer Gott." },
      { number: 11, text: "Ihr sollt nicht stehlen, nicht täuschen und einander nicht betrügen.", heading: "Soziales Verhalten und Nächstenliebe: 19,11-18", footnotes: ["(11-12) Ex 20,7.15f; 23,7; Dtn 5,11.19f"] },
      { number: 12, text: "Ihr sollt nicht falsch bei meinem Namen schwören; du würdest sonst den Namen deines Gottes entweihen. Ich bin der Herr." },
      { number: 13, text: "Du sollst deinen Nächsten nicht ausbeuten und ihn nicht um das Seine bringen. Der Lohn des Tagelöhners soll nicht über Nacht bis zum Morgen bei dir bleiben." },
      { number: 14, text: "Du sollst einen Tauben nicht verfluchen und einem Blinden kein Hindernis in den Weg stellen; vielmehr sollst du deinen Gott fürchten. Ich bin der Herr." },
      { number: 15, text: "Ihr sollt in der Rechtsprechung kein Unrecht tun. Du sollst weder für einen Geringen noch für einen Großen Partei nehmen; gerecht sollst du deinen Stammesgenossen richten.", footnotes: ["(15-16) Ex 23,3-7"] },
      { number: 16, text: "Du sollst deinen Stammesgenossen nicht verleumden und dich nicht hinstellen und das Leben deines Nächsten fordern. Ich bin der Herr." },
      { number: 17, text: "Du sollst in deinem Herzen keinen Hass gegen deinen Bruder tragen. Weise deinen Stammesgenossen zurecht, so wirst du seinetwegen keine Schuld auf dich laden." },
      { number: 18, text: "An den Kindern deines Volkes sollst du dich nicht rächen und ihnen nichts nachtragen. Du sollst deinen Nächsten lieben wie dich selbst. Ich bin der Herr.", footnotes: ["Unter dem «Nächsten» verstand Israel vor allem den Volks- und Glaubensgenossen; doch sind nach V. 34 auch alle Fremden, die Gastrecht genießen, in das Gebot der Nächstenliebe mit eingeschlossen. Jesus hat es nach Mt 5,43 und Lk 10,27-37 auf alle Menschen ausgedehnt."] },
      { number: 19, text: "Ihr sollt auf meine Satzungen achten: Unter deinem Vieh sollst du nicht zwei Tiere verschiedener Art sich begatten lassen. Dein Feld sollst du nicht mit zweierlei Arten besäen. Du sollst kein aus zweierlei Fäden gewebtes Kleid anlegen.", heading: "Das Verbot von Mischungen: 19,19" },
      { number: 20, text: "Wohnt ein Mann einer Frau bei, die einem andern Mann als Sklavin zur Nebenfrau bestimmt ist und die weder losgekauft noch freigelassen ist, dann soll der Fall untersucht werden; sterben sollen sie nicht, da sie nicht freigelassen war.", heading: "Unzucht mit der Nebenfrau eines anderen: 19,20-22", footnotes: ["Ex 21,7-9"] },
      { number: 21, text: "Er soll als sein Schuldopfer für den Herrn einen Widder zum Eingang des Offenbarungszeltes bringen." },
      { number: 22, text: "Mit diesem Widder soll der Priester ihn vor dem Herrn von der begangenen Sünde entsühnen; so wird ihm die Sünde, die er begangen hat, vergeben." },
      { number: 23, text: "Wenn ihr in das Land kommt und einen Fruchtbaum pflanzt, sollt ihr seine Früchte behandeln, als ob sie seine Vorhaut wären. Drei Jahre lang sollen sie für euch etwas Unbeschnittenes sein, das man nicht essen darf.", heading: "Die Erstlingsfrüchte: 19,23-25" },
      { number: 24, text: "Im vierten Jahr sollen alle Früchte als Festgabe für den Herrn geheiligt sein." },
      { number: 25, text: "Erst im fünften Jahr dürft ihr die Früchte essen und den Ertrag für euch ernten. Ich bin der Herr, euer Gott." },
      { number: 26, text: "Ihr sollt nichts mit Blut essen. Wahrsagerei und Zauberei sollt ihr nicht treiben.", heading: "Das Verbot fremder Kultbräuche: 19,26-31", footnotes: ["17,10-12"] },
      { number: 27, text: "Ihr sollt euer Kopfhaar nicht rundum abschneiden. Du sollst deinen Bart nicht stutzen." },
      { number: 28, text: "Für einen Toten dürft ihr keine Einschnitte auf eurem Körper anbringen und ihr dürft euch keine Zeichen einritzen lassen. Ich bin der Herr." },
      { number: 29, text: "Entweih nicht deine Tochter, indem du sie der Unzucht preisgibst, damit das Land nicht der Unzucht verfällt und voller Schandtat wird." },
      { number: 30, text: "Ihr sollt auf meine Sabbate achten und mein Heiligtum fürchten. Ich bin der Herr.", footnotes: ["Ex 20,8; Dtn 5,12"] },
      { number: 31, text: "Wendet euch nicht an die Totenbeschwörer und sucht nicht die Wahrsager auf; sie verunreinigen euch. Ich bin der Herr, euer Gott." },
      { number: 32, text: "Du sollst vor grauem Haar aufstehen, das Ansehen eines Greises ehren und deinen Gott fürchten. Ich bin der Herr.", heading: "Die Ehrfurcht vor dem Alter: 19,32" },
      { number: 33, text: "Wenn bei dir ein Fremder in eurem Land lebt, sollt ihr ihn nicht unterdrücken.", heading: "Verhalten gegen Fremde: 19,33-34" },
      { number: 34, text: "Der Fremde, der sich bei euch aufhält, soll euch wie ein Einheimischer gelten und du sollst ihn lieben wie dich selbst; denn ihr seid selbst Fremde in Ägypten gewesen. Ich bin der Herr, euer Gott." },
      { number: 35, text: "Ihr sollt kein Unrecht begehen bei Gericht, mit Längenmaß, Gewicht und Hohlmaß.", heading: "Übervorteilung: 19,35-36" },
      { number: 36, text: "Ihr sollt richtige Waagen, richtige Gewichtsteine, richtiges Efa und richtiges Hin haben. Ich bin der Herr, euer Gott, der euch aus Ägypten geführt hat." },
      { number: 37, text: "Ihr sollt auf alle meine Satzungen und alle meine Vorschriften achten und sie befolgen. Ich bin der Herr.", heading: "Schlussmahnung: 19,37" },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Todeswürdige Verbrechen: 20,1-27 | Das Kinderopfer: 20,1-5" },
      { number: 2, text: "Sag zu den Israeliten: Jeder Mann unter den Israeliten oder unter den Fremden in Israel, der eines seiner Kinder dem Moloch gibt, wird mit dem Tod bestraft. Die Bürger des Landes sollen ihn steinigen.", footnotes: ["Zu den Kinderopfern für Moloch vgl. die Anmerkung zu 18,21."] },
      { number: 3, text: "Ich richte mein Angesicht gegen einen solchen und merze ihn aus seinem Volk aus, weil er eines seiner Kinder dem Moloch gegeben, dadurch mein Heiligtum verunreinigt und meinen heiligen Namen entweiht hat." },
      { number: 4, text: "Falls die Bürger des Landes ihre Augen diesem Mann gegenüber verschließen, wenn er eines seiner Kinder dem Moloch gibt, und ihn nicht töten," },
      { number: 5, text: "so richte ich mein Angesicht gegen ihn und seine Sippe und merze sie aus der Mitte ihres Volkes aus, ihn und alle, die sich mit ihm dem Molochdienst hingeben." },
      { number: 6, text: "Gegen einen, der sich an Totenbeschwörer und Wahrsager wendet und sich mit ihnen abgibt, richte ich mein Angesicht und merze ihn aus seinem Volk aus.", heading: "Die Totenbeschwörung und Wahrsagerei: 20,6-8", footnotes: ["19,31"] },
      { number: 7, text: "Ihr sollt euch heiligen, um heilig zu sein; denn ich bin der Herr, euer Gott." },
      { number: 8, text: "Ihr sollt auf meine Satzungen achten und sie befolgen. Ich bin der Herr, der euch heiligt." },
      { number: 9, text: "Jeder, der seinen Vater oder seine Mutter verflucht, wird mit dem Tod bestraft. Da er seinen Vater oder seine Mutter verflucht hat, soll sein Blut auf ihn kommen.", heading: "Die Verfluchung der Eltern: 20,9", footnotes: ["Ex 21,17"] },
      { number: 10, text: "Ein Mann, der mit der Frau seines Nächsten die Ehe bricht, wird mit dem Tod bestraft, der Ehebrecher samt der Ehebrecherin.", heading: "Der Ehebruch: 20,10", footnotes: ["Ex 20,14; Dtn 5,18"] },
      { number: 11, text: "Ein Mann, der mit der Frau seines Vaters schläft, hat die Scham seines Vaters entblößt. Beide werden mit dem Tod bestraft; ihr Blut soll auf sie kommen.", heading: "Unzuchtverbrechen: 20,11-17", footnotes: ["(11-17) 18,6-23"] },
      { number: 12, text: "Schläft einer mit seiner Schwiegertochter, so werden beide mit dem Tod bestraft. Sie haben eine schändliche Tat begangen, ihr Blut soll auf sie kommen." },
      { number: 13, text: "Schläft einer mit einem Mann, wie man mit einer Frau schläft, dann haben sie eine Gräueltat begangen; beide werden mit dem Tod bestraft; ihr Blut soll auf sie kommen." },
      { number: 14, text: "Heiratet einer eine Frau und ihre Mutter, so ist das Blutschande. Ihn und die beiden Frauen soll man verbrennen, damit es keine Blutschande unter euch gibt." },
      { number: 15, text: "Ein Mann, der einem Tier beiwohnt, wird mit dem Tod bestraft; auch das Tier sollt ihr töten." },
      { number: 16, text: "Nähert sich eine Frau einem Tier, um sich mit ihm zu begatten, dann sollst du die Frau und das Tier töten. Sie werden mit dem Tod bestraft; ihr Blut soll auf sie kommen." },
      { number: 17, text: "Nimmt einer seine Schwester, eine Tochter seines Vaters oder eine Tochter seiner Mutter und sieht ihre Scham und sie sieht die seine, so ist es eine Schandtat. Sie sollen vor den Augen der Söhne ihres Volkes ausgemerzt werden. Er hat die Scham seiner Schwester entblößt; er muss die Folgen seiner Schuld tragen." },
      { number: 18, text: "Ein Mann, der mit einer Frau während ihrer Regel schläft und ihre Scham entblößt, hat ihre Blutquelle aufgedeckt und sie hat ihre Blutquelle entblößt; daher sollen beide aus ihrem Volk ausgemerzt werden.", heading: "Der Verkehr mit einer Menstruierenden: 20,18" },
      { number: 19, text: "Die Scham der Schwester deiner Mutter oder der Schwester deines Vaters sollst du nicht entblößen; denn wer seine eigene Verwandte entblößt, muss die Folgen seiner Schuld tragen.", heading: "Die Unzucht mit Verwandten: 20,19-21" },
      { number: 20, text: "Ein Mann, der mit seiner Tante schläft, hat die Scham seines Onkels entblößt. Sie müssen die Folgen ihrer Sünde tragen; sie sollen kinderlos sterben." },
      { number: 21, text: "Nimmt einer die Frau seines Bruders, so ist das Befleckung. Er hat die Scham seines Bruders entblößt; sie sollen kinderlos bleiben." },
      { number: 22, text: "Ihr sollt auf alle meine Satzungen und Vorschriften achten und sie befolgen; dann wird euch das Land nicht ausspeien, in das ich euch führe, und ihr werdet dort wohnen können.", heading: "Schlussmahnung: 20,22-26", footnotes: ["(22-26) 18,24-30"] },
      { number: 23, text: "Ihr sollt euch nicht nach den Bräuchen des Volkes richten, das ich vor euren Augen vertreibe; denn all diese Dinge haben sie getan, so dass es mich vor ihnen ekelte." },
      { number: 24, text: "Daher habe ich euch gesagt: Ihr seid es, die ihren Boden in Besitz nehmen sollen. Ich bin es, der ihn euch zum Besitz geben wird, ein Land, in dem Milch und Honig fließen. Ich bin der Herr, euer Gott, der euch von diesen Völkern ausgesondert hat." },
      { number: 25, text: "So unterscheidet zwischen reinem und unreinem Vieh, zwischen unreinen und reinen Vögeln! Macht euch nicht selbst abscheulich mit diesen Tieren, diesen Vögeln, mit allem, was auf dem Boden kriecht. Ich habe es für euch als unrein unterschieden." },
      { number: 26, text: "Seid mir geheiligt; denn ich, der Herr, bin heilig und ich habe euch von all diesen Völkern ausgesondert, damit ihr mir gehört." },
      { number: 27, text: "Männer oder Frauen, in denen ein Toten- oder ein Wahrsagegeist ist, sollen mit dem Tod bestraft werden. Man soll sie steinigen, ihr Blut soll auf sie kommen.", heading: "Nachtrag: Die Totenbeschwörung: 20,27", footnotes: ["19,31"] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Der Herr sprach zu Mose: Rede zu den Priestern, den Söhnen Aarons, und sag zu ihnen: Keiner von ihnen darf sich an der Leiche eines seiner Stammesgenossen verunreinigen,", heading: "Vorschriften für Priester: 21,1-24 | Das Verhalten beim Begräbnis von Verwandten: 21,1-6" },
      { number: 2, text: "außer an seinen nächsten Verwandten: seiner Mutter, seinem Vater, seinem Sohn, seiner Tochter oder seinem Bruder." },
      { number: 3, text: "An seiner unverheirateten Schwester, die seine nahe Verwandte blieb, da sie keinem Mann angehörte, darf er unrein werden." },
      { number: 4, text: "Nicht aber darf er an seiner Schwester, die mit einem Stammesgenossen verheiratet ist, unrein werden; er würde sich entweihen." },
      { number: 5, text: "Die Priester sollen sich auf ihrem Kopf keine Glatze scheren, ihren Bart nicht stutzen und an ihrem Körper keine Einschnitte machen." },
      { number: 6, text: "Sie sollen ihrem Gott geheiligt sein und den Namen ihres Gottes nicht entweihen. Denn sie sind es, die die Feueropfer des Herrn, die Speise ihres Gottes, darbringen; darum sollen sie heilig sein." },
      { number: 7, text: "Sie dürfen weder eine Dirne, noch eine Entehrte, noch eine Frau heiraten, die ihr Mann verstoßen hat; denn der Priester ist seinem Gott geweiht.", heading: "Ehehindernisse für Priester: 21,7-8" },
      { number: 8, text: "Du sollst ihn heilig halten, denn er bringt die Speise deines Gottes dar. Heilig soll er dir sein, denn ich bin heilig, der Herr, der euch heiligt." },
      { number: 9, text: "Wenn sich die Tochter eines Priesters als Dirne entweiht, so entweiht sie ihren Vater; sie soll im Feuer verbrannt werden.", heading: "Die Strafe für unzüchtige Priestertöchter: 21,9" },
      { number: 10, text: "Der Priester, der unter seinen Brüdern den höchsten Rang einnimmt, auf dessen Haupt das Salböl ausgegossen wurde und der durch das Anlegen der heiligen Gewänder eingesetzt ist, soll sein Haar nicht lose herunterhängen lassen, seine Kleider nicht zerreißen,", heading: "Das Verhalten des Hohenpriesters bei Todesfällen: 21,10-12" },
      { number: 11, text: "sich an keinem Leichnam verunreinigen, auch nicht, wenn es sich um Vater oder Mutter handelt." },
      { number: 12, text: "Er soll sich nicht vom Heiligtum entfernen, um nicht das Heiligtum seines Gottes zu entweihen; denn die Weihe des Salböls seines Gottes ist auf ihm. Ich bin der Herr." },
      { number: 13, text: "Er soll nur eine Jungfrau heiraten.", heading: "Ehehindernisse für den Hohenpriester: 21,13-15" },
      { number: 14, text: "Eine Witwe, eine Verstoßene oder eine Entehrte, eine Dirne, darf er nicht heiraten; nur eine Jungfrau aus seinem Stamm darf er zur Frau nehmen;" },
      { number: 15, text: "sonst würde er seine Nachkommenschaft unter seinen Stammesgenossen entweihen; denn ich, der Herr, bin es, der ihn heiligt." },
      { number: 16, text: "Der Herr sprach zu Mose:", heading: "Die körperliche Tauglichkeit für das Priesteramt: 21,16-23" },
      { number: 17, text: "Sag zu Aaron: Keiner deiner Nachkommen, auch in den kommenden Generationen, der ein Gebrechen hat, darf herantreten, um die Speise seines Gottes darzubringen." },
      { number: 18, text: "Denn keiner mit einem Gebrechen darf herantreten: kein Blinder oder Lahmer, kein im Gesicht oder am Körper Entstellter," },
      { number: 19, text: "kein Mann, der einen gebrochenen Fuß oder eine gebrochene Hand hat," },
      { number: 20, text: "keiner mit Buckel, Muskelschwund, Augenstar, Krätze, Flechte oder Hodenquetschung." },
      { number: 21, text: "Keiner der Nachkommen Aarons, des Priesters, darf herantreten, um die Feueropfer des Herrn darzubringen, wenn er ein Gebrechen hat. Er hat ein Gebrechen, er darf nicht herantreten, um die Speise seines Gottes darzubringen." },
      { number: 22, text: "Doch darf er von der Speise seines Gottes, von den hochheiligen und heiligen Dingen, essen,", footnotes: ["«Speise Gottes» ist das Opfer; im AT wird aber nirgends angenommen, dass Gott die Opferspeise genießt oder ihrer bedarf (vgl. Ps 50,8-13)."] },
      { number: 23, text: "aber nicht zum Vorhang kommen und sich nicht dem Altar nähern; denn er hat ein Gebrechen und darf meine heiligen Gegenstände nicht entweihen; denn ich bin der Herr, der sie geheiligt hat." },
      { number: 24, text: "Das sagte Mose zu Aaron, zu dessen Söhnen und zu allen Israeliten.", heading: "Schlussbemerkung: 21,24" },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Weitere Vorschriften für den Genuss und die Darbringung von Opfern: 22,1-33 | Der Ausschluss von Priestern vom Genuss der Opfergaben: 22,1-9" },
      { number: 2, text: "Sag zu Aaron und seinen Söhnen, sie sollen sich mit den heiligen Opfergaben der Israeliten in Acht nehmen, um meinen heiligen Namen nicht zu entweihen; sie müssen für mich heilig gehalten werden. Ich bin der Herr." },
      { number: 3, text: "Sag zu ihnen: Jeder aus euren Nachkommen, auch in den kommenden Generationen, der sich im Zustand der Unreinheit den heiligen Opfergaben nähert, die die Israeliten dem Herrn weihen, soll ausgemerzt und aus meiner Gegenwart weggeschafft werden. Ich bin der Herr." },
      { number: 4, text: "Keiner aus den Nachkommen Aarons, der aussätzig ist oder einen Ausfluss hat, darf von den heiligen Gaben essen, ehe er rein ist. Wer irgendetwas berührt hat, das durch eine Leiche unrein wurde, wer einen Samenerguss hatte," },
      { number: 5, text: "wer Kleintiere berührt hat und sich damit verunreinigte oder einen Menschen, der ihn durch eine eigene Unreinheit befleckte," },
      { number: 6, text: "jeder, der solche Berührungen hatte, soll bis zum Abend unrein sein und darf von den heiligen Gaben erst essen, nachdem er seinen Körper in Wasser gebadet hat." },
      { number: 7, text: "Mit Sonnenuntergang soll er wieder rein sein und darf danach von den heiligen Gaben essen; denn sie sind sein Lebensunterhalt." },
      { number: 8, text: "Er darf kein verendetes oder zerrissenes Tier essen; er würde sich dadurch verunreinigen. Ich bin der Herr." },
      { number: 9, text: "Sie sollen auf meine Anordnungen achten und keine Sünde auf sich laden; sie sollen sterben, falls sie sie entweihen. Ich, der Herr, bin es, der sie heiligt.", footnotes: ["falls sie sie entweihen: H unklar."] },
      { number: 10, text: "Kein Laie darf Heiliges essen; weder der Hausgenosse eines Priesters noch sein Lohnarbeiter darf etwas Heiliges essen.", heading: "Der Ausschluss der Laien vom Genuss der Opfergaben: 22,10-16" },
      { number: 11, text: "Aber wenn ein Priester eine Person mit seinem Geld als Eigentum erwirbt, darf sie davon essen wie einer, der in seinem Haus geboren ist; sie dürfen von seiner Nahrung essen." },
      { number: 12, text: "Wenn eine Priestertochter einen Laien heiratet, darf sie vom Anteil des Erhebungsritus, von den heiligen Gaben nicht essen;" },
      { number: 13, text: "aber wenn sie verwitwet oder verstoßen ist und keine Kinder hat und deshalb in das Haus ihres Vaters zurückkehrt, darf sie wie in ihrer Jugend vom Lebensunterhalt ihres Vaters essen; kein Laie aber darf davon essen." },
      { number: 14, text: "Wenn einer ohne Vorsatz etwas Heiliges isst, soll er es dem Priester ersetzen und ein Fünftel des Wertes hinzufügen." },
      { number: 15, text: "Die Priester dürfen die heiligen Gaben der Israeliten, die sie dem Herrn darbieten, nicht entweihen." },
      { number: 16, text: "Sie würden den Israeliten Schuld aufladen, die zu einem Schuldopfer verpflichtet, wenn sie ihre heiligen Gaben äßen; denn ich bin der Herr, der diese Gaben geheiligt hat.", footnotes: ["H ist nicht völlig klar."] },
      { number: 17, text: "Der Herr sprach zu Mose:", heading: "Die Tauglichkeit der Opfertiere: 22,17-25" },
      { number: 18, text: "Rede zu Aaron, seinen Söhnen und allen Israeliten und sag zu ihnen: Jeder aus dem Haus Israel oder von den Fremden in Israel, der seine Opfergabe wegen eines Gelübdes oder freiwillig bringt und sie für den Herrn als Brandopfer darbringt," },
      { number: 19, text: "muss, damit ihr Annahme findet, ein fehlerloses, männliches Tier von den Rindern, Schafen oder Ziegen darbringen." },
      { number: 20, text: "Ihr dürft kein Tier mit einem Gebrechen darbringen, denn ihr würdet damit keine Annahme finden." },
      { number: 21, text: "Wenn jemand ein Heilsopfer für den Herrn darbringt, sei es, um ein Gelübde zu erfüllen, oder sei es als freiwillige Gabe, so soll es ein fehlerloses Rind oder Schaf oder eine fehlerlose Ziege sein, um Annahme zu finden; es darf kein Gebrechen haben." },
      { number: 22, text: "Ihr dürft dem Herrn kein Tier opfern, das blind, verstümmelt, krätzig, aussätzig, eitrig ist oder zerbrochene Gliedmaßen hat. Kein Stück von solchen Tieren dürft ihr auf den Altar als ein Feueropfer für den Herrn legen." },
      { number: 23, text: "Ein entstelltes oder verstümmeltes Rind oder Schaf kannst du als freiwillige Opfergabe verwenden, aber als Einlösung eines Gelübdes ist es missfällig." },
      { number: 24, text: "Ihr dürft dem Herrn kein Tier darbringen, das zerdrückte, zerschlagene, ausgerissene oder abgeschnittene Hoden hat. Ihr dürft das in eurem Land nicht tun" },
      { number: 25, text: "und ihr dürft kein solches Tier aus der Hand eines Fremden erwerben, um es als Speise eures Gottes darzubringen. Ein derartiger Eingriff an ihnen bewirkt ein Gebrechen; sie würden euch keine Annahme bringen." },
      { number: 26, text: "Der Herr sprach zu Mose:", heading: "Einzelbestimmungen über Opfertiere: 22,26-30" },
      { number: 27, text: "Wenn ein Rind, ein Schaf oder eine Ziege geboren wird, soll das Junge sieben Tage bei seiner Mutter bleiben. Vom achten Tag an ist es als Feueropfer für den Herrn annehmbar.", footnotes: ["Ex 22,29"] },
      { number: 28, text: "Ein Rind oder Schaf sollt ihr nicht an einem Tag zugleich mit seinem Jungen schlachten." },
      { number: 29, text: "Wenn ihr dem Herrn ein Dankopfer darbringt, opfert es so, dass es angenommen werden kann." },
      { number: 30, text: "Man soll es noch am selben Tag essen, ohne etwas davon bis zum Morgen übrig zu lassen. Ich bin der Herr." },
      { number: 31, text: "Ihr sollt auf meine Gebote achten und sie befolgen; ich bin der Herr.", heading: "Schlussmahnung: 22,31-33" },
      { number: 32, text: "Ihr sollt meinen heiligen Namen nicht entweihen, damit ich inmitten der Israeliten geheiligt werde; ich, der Herr, bin es, der euch heiligt." },
      { number: 33, text: "Ich, der euch aus Ägypten herausgeführt hat, um euer Gott zu sein, ich bin der Herr." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Festordnungen und Feiervorschriften: 23,1 - 24,23 | Einleitung: 23,1-2" },
      { number: 2, text: "Rede zu den Israeliten und sag zu ihnen: Die Feste des Herrn, die ihr als Tage heiliger Versammlung ausrufen sollt, sind folgende:" },
      { number: 3, text: "Sechs Tage soll man arbeiten, aber am siebten Tag ist vollständiger Ruhetag, ein Tag heiliger Versammlung, an dem ihr keinerlei Arbeit verrichten dürft. Es ist ein Feiertag zur Ehre des Herrn überall, wo ihr wohnt.", heading: "Der Sabbat: 23,3", footnotes: ["Ex 20,8-11; 31,12-17; 35,2; Dtn 5,12-15"] },
      { number: 4, text: "Das sind die Feste des Herrn, Tage heiliger Versammlungen, die ihr zur festgesetzten Zeit ausrufen sollt:", heading: "Das Pascha und das Fest der ungesäuerten Brote: 23,4-8", footnotes: ["4-8: Zum Pascha und Fest der ungesäuerten Brote vgl. die Anmerkung zu Ex 12,1 - 13,16."] },
      { number: 5, text: "Im ersten Monat, am vierzehnten Tag des Monats, zur Abenddämmerung, ist Pascha zur Ehre des Herrn.", footnotes: ["Ex 12,2-11"] },
      { number: 6, text: "Am fünfzehnten Tag dieses Monats ist das Fest der ungesäuerten Brote zur Ehre des Herrn. Sieben Tage sollt ihr ungesäuertes Brot essen.", footnotes: ["23,15; Ex 34,18"] },
      { number: 7, text: "Am ersten Tag habt ihr heilige Versammlung; ihr dürft keine schwere Arbeit verrichten." },
      { number: 8, text: "Sieben Tage hindurch sollt ihr ein Feueropfer für den Herrn darbringen. Am siebten Tag ist heilige Versammlung; da dürft ihr keine schwere Arbeit verrichten." },
      { number: 9, text: "Der Herr sprach zu Mose:", heading: "Die Erstlingsfrüchte: 23,9-14" },
      { number: 10, text: "Rede zu den Israeliten und sag zu ihnen: Wenn ihr in das Land kommt, das ich euch gebe, und wenn ihr dort die Ernte einbringt, sollt ihr dem Priester die erste Garbe eurer Ernte bringen." },
      { number: 11, text: "Er soll sie vor dem Herrn hin- und herschwingen und sie so darbringen, damit ihr Annahme findet. Am Tag nach dem Sabbat soll der Priester den Ritus ausführen." },
      { number: 12, text: "Am Tag, an dem ihr die Garbe darbringt, sollt ihr dem Herrn auch ein fehlerloses einjähriges Schaf als Brandopfer herrichten," },
      { number: 13, text: "dazu als Speiseopfer zwei Zehntel Feinmehl, das mit Öl vermengt ist. Das Ganze soll ein Feueropfer für den Herrn zum beruhigenden Duft sein; dazu kommt ein viertel Hin Wein als Trankopfer." },
      { number: 14, text: "Vor diesem Tag, bevor ihr eurem Gott die Opfergabe gebracht habt, dürft ihr kein Brot und kein geröstetes oder frisches Korn essen. Das gelte als feste Regel bei euch von Generation zu Generation überall, wo ihr wohnt." },
      { number: 15, text: "Vom Tag nach dem Sabbat, an dem ihr die Garbe für die Darbringung gebracht habt, sollt ihr sieben volle Wochen zählen.", heading: "Das Wochenfest (Pfingstfest): 23,15-22", footnotes: ["Ex 23,16; 34,22"] },
      { number: 16, text: "Zählt fünfzig Tage bis zum Tag nach dem siebten Sabbat, und dann bringt dem Herrn ein neues Speiseopfer dar!" },
      { number: 17, text: "Bringt als Erstlingsgaben für den Herrn aus euren Wohnsitzen zwei Brote dar, gebacken aus zwei zehntel Efa Feinmehl mit Sauerteig." },
      { number: 18, text: "Zum Brot bringt sieben fehlerlose einjährige Lämmer, einen Jungstier und zwei Widder als Brandopfer für den Herrn dar, dazu das Speiseopfer sowie das Trankopfer als ein Feueropfer zum beruhigenden Duft für den Herrn." },
      { number: 19, text: "Bereitet auch einen Ziegenbock als Sündopfer und zwei einjährige Lämmer als Heilsopfer zu!" },
      { number: 20, text: "Der Priester soll sie, zusammen mit dem Brot der Erstlingsgaben, vor dem Herrn hin- und herschwingen und sie so darbringen; sie sind mit den beiden Lämmern heilige Gaben für den Herrn, die dem Priester gehören." },
      { number: 21, text: "Am selben Tag ruft eine heilige Versammlung aus und haltet sie ab! Da dürft ihr keine schwere Arbeit verrichten. Das gelte bei euch als feste Regel von Generation zu Generation überall, wo ihr wohnt." },
      { number: 22, text: "Wenn ihr die Ernte eures Landes einbringt, sollst du dein Feld nicht bis zum äußersten Rand abernten und keine Nachlese deiner Ernte halten. Du sollst das dem Armen und dem Fremden überlassen. Ich bin der Herr, euer Gott.", footnotes: ["19,9f"] },
      { number: 23, text: "Der Herr sprach zu Mose:", heading: "Der Neujahrstag: 23,23-25" },
      { number: 24, text: "Sag zu den Israeliten: Im siebten Monat, am ersten Tag des Monats, ist für euch Ruhetag, in Erinnerung gerufen durch Lärmblasen, eine heilige Versammlung." },
      { number: 25, text: "Da dürft ihr keine schwere Arbeit verrichten und ihr sollt dem Herrn ein Feueropfer darbringen." },
      { number: 26, text: "Der Herr sprach zu Mose:", heading: "Das Versöhnungsfest: 23,26-32", footnotes: ["(26-32) 16,2-34"] },
      { number: 27, text: "Am zehnten Tag dieses siebten Monats ist der Versöhnungstag. Da sollt ihr heilige Versammlung halten. Ihr sollt euch Enthaltung auferlegen und dem Herrn ein Feueropfer darbringen." },
      { number: 28, text: "An ebendiesem Tag dürft ihr keinerlei Arbeit verrichten, denn es ist der Versöhnungstag, an dem man euch vor dem Herrn, eurem Gott, entsühnt." },
      { number: 29, text: "Denn jede Person, die sich an diesem Tag nicht Enthaltung auferlegt, soll aus ihren Stammesgenossen ausgemerzt werden." },
      { number: 30, text: "Wer an diesem Tag irgendeine Arbeit verrichtet, den werde ich aus der Mitte seines Volkes austilgen." },
      { number: 31, text: "Ihr dürft keinerlei Arbeit tun. Das gelte bei euch als feste Regel von Generation zu Generation überall, wo ihr wohnt." },
      { number: 32, text: "Dieser Tag ist für euch ein vollständiger Ruhetag. Ihr sollt euch Enthaltung auferlegen. Vom Abend des neunten Tags in diesem Monat bis zum folgenden Abend sollt ihr Ruhetag halten." },
      { number: 33, text: "Der Herr sprach zu Mose:", heading: "Das Laubhüttenfest: 23,33-36" },
      { number: 34, text: "Sag zu den Israeliten: Am fünfzehnten Tag dieses siebten Monats ist sieben Tage hindurch das Laubhüttenfest zur Ehre des Herrn.", footnotes: ["Ex 23,16; 34,22"] },
      { number: 35, text: "Am ersten Tag, einem Tag heiliger Versammlung, dürft ihr keine schwere Arbeit verrichten." },
      { number: 36, text: "Sieben Tage hindurch sollt ihr ein Feueropfer für den Herrn darbringen. Am achten Tag habt ihr heilige Versammlung und ihr sollt ein Feueropfer für den Herrn darbringen. Es ist der Tag der Festversammlung; da dürft ihr keine schwere Arbeit verrichten." },
      { number: 37, text: "Das sind die Festzeiten des Herrn, die ihr als Tage heiliger Versammlung ausrufen sollt, dazu bestimmt, für den Herrn Feueropfer, Brandopfer, Speiseopfer, Schlachtopfer und Trankopfer darzubringen, dem jeweiligen Tag entsprechend,", heading: "Schlussbemerkung: 23,37-38" },
      { number: 38, text: "abgesehen von den Sabbaten des Herrn und abgesehen von euren Geschenken, den gelobten und freiwilligen Gaben, die ihr dem Herrn gebt." },
      { number: 39, text: "Am fünfzehnten Tag des siebten Monats, wenn ihr den Ertrag des Landes erntet, feiert sieben Tage lang das Fest des Herrn! Am ersten und am achten Tag ist Ruhetag.", heading: "Einzelbestimmung für das Laubhüttenfest: 23,39-44" },
      { number: 40, text: "Am ersten Tag nehmt schöne Baumfrüchte, Palmwedel, Zweige von dicht belaubten Bäumen und von Bachweiden und seid sieben Tage lang vor dem Herrn, eurem Gott, fröhlich!" },
      { number: 41, text: "Feiert dieses Fest zur Ehre des Herrn jährlich sieben Tage lang! Das gelte bei euch als feste Regel von Generation zu Generation. Ihr sollt dieses Fest im siebten Monat feiern." },
      { number: 42, text: "Sieben Tage sollt ihr in Hütten wohnen. Alle Einheimischen in Israel sollen in Hütten wohnen," },
      { number: 43, text: "damit eure kommenden Generationen wissen, dass ich die Israeliten in Hütten wohnen ließ, als ich sie aus Ägypten herausführte. Ich bin der Herr, euer Gott." },
      { number: 44, text: "Da teilte Mose den Israeliten die Festzeiten des Herrn mit." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Einzelbestimmungen über den Leuchter und den Tisch für die Schaubrote: 24,1-9" },
      { number: 2, text: "Befiehl den Israeliten, dass sie dir für den Leuchter reines Öl aus zerstoßenen Oliven bringen, damit man ständig eine Lampe brennen lassen kann.", footnotes: ["(2-4) Ex 25,31-40"] },
      { number: 3, text: "Im Offenbarungszelt, vor dem Vorhang der Lade, soll Aaron diese Lampe aufstellen. Sie soll hier vor dem Herrn ständig vom Abend bis zum Morgen brennen. Das gelte bei euch als feste Regel von Generation zu Generation." },
      { number: 4, text: "Aaron soll die Lampen auf dem Leuchter aus reinem Gold vor dem Herrn ständig in Ordnung halten." },
      { number: 5, text: "Du sollst Feinmehl nehmen und daraus zwölf Kuchen backen; aus zwei zehntel Efa Feinmehl soll jeder einzelne Kuchen zubereitet sein.", footnotes: ["Die zwölf Kuchen werden in Ex 25,30 und 1 Sam 21,7 «Schaubrote» (wörtlich: Brote des Angesichts) genannt, wahrscheinlich in dem Sinn, dass Gott sie anschaut und als Zeichen des Dankes Israels gnädig annimmt."] },
      { number: 6, text: "Dann sollst du sie in zwei Schichten, sechs in jeder Schicht, auf den Tisch aus reinem Gold vor den Herrn legen." },
      { number: 7, text: "Auf jede Schicht sollst du reinen Weihrauch tun; er soll der Gedächtnisanteil des Brotes sein, ein Feueropfer für den Herrn." },
      { number: 8, text: "Sabbat für Sabbat soll man die Brote vor dem Herrn zurichten als dauernde Bundesleistung von Seiten der Israeliten." },
      { number: 9, text: "Sie gehören Aaron und seinen Söhnen, die sie an einem heiligen Ort essen sollen; denn sie sind für ihn etwas Hochheiliges, ein dauerndes Anrecht von den Feueropfern des Herrn." },
      { number: 10, text: "Der Sohn einer Israelitin und eines Ägypters ging unter die Israeliten. Im Lager geriet er in Streit mit einem Mann, der Israelit war.", heading: "Einschub: Die Lästerung des Gottesnamens: 24,10-23" },
      { number: 11, text: "Der Sohn der Israelitin schmähte den Gottesnamen und fluchte. Da brachten sie ihn zu Mose. Der Name der Mutter war Schelomit; sie war die Tochter Dibris aus dem Stamm Dan." },
      { number: 12, text: "Man nahm ihn in Gewahrsam, um auf einen Spruch des Herrn hin zu entscheiden." },
      { number: 13, text: "Der Herr sprach zu Mose:" },
      { number: 14, text: "Lass den, der den Fluch ausgesprochen hat, aus dem Lager hinausführen! Alle, die es gehört haben, sollen ihm ihre Hände auf den Kopf legen; dann soll ihn die ganze Gemeinde steinigen." },
      { number: 15, text: "Sag den Israeliten: Jeder, der seinem Gott flucht, muss die Folgen seiner Sünde tragen.", footnotes: ["Ex 20,7; 22,27"] },
      { number: 16, text: "Wer den Namen des Herrn schmäht, wird mit dem Tod bestraft; die ganze Gemeinde soll ihn steinigen. Der Fremde muss ebenso wie der Einheimische getötet werden, wenn er den Gottesnamen schmäht." },
      { number: 17, text: "Wer einen Menschen erschlägt, wird mit dem Tod bestraft.", footnotes: ["Ex 20,13; 21,12-20"] },
      { number: 18, text: "Wer ein Stück Vieh erschlägt, muss es ersetzen: Leben für Leben." },
      { number: 19, text: "Wenn jemand einen Stammesgenossen verletzt, soll man ihm antun, was er getan hat:" },
      { number: 20, text: "Bruch um Bruch, Auge um Auge, Zahn um Zahn. Der Schaden, den er einem Menschen zugefügt hat, soll ihm zugefügt werden.", footnotes: ["Ex 21,23-25; Dtn 19,21"] },
      { number: 21, text: "Wer ein Stück Vieh erschlägt, muss es ersetzen; wer aber einen Menschen erschlägt, wird mit dem Tod bestraft." },
      { number: 22, text: "Gleiches Recht soll bei euch für den Fremden wie für den Einheimischen gelten; denn ich bin der Herr, euer Gott." },
      { number: 23, text: "Nachdem Mose so zu den Israeliten gesprochen hatte, führten sie den, der den Fluch ausgesprochen hatte, aus dem Lager hinaus und steinigten ihn. So erfüllten sie, was der Herr dem Mose aufgetragen hatte." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Der Herr sprach zu Mose auf dem Berg Sinai:", heading: "Sabbatjahr und Jubeljahr: 25,1-55 | Einleitung: 25,1" },
      { number: 2, text: "Rede zu den Israeliten und sag zu ihnen: Wenn ihr in das Land kommt, das ich euch gebe, soll das Land Sabbatruhe zur Ehre des Herrn halten.", heading: "Das Brachjahr: 25,2-7", footnotes: ["(2-7) Ex 23,10f"] },
      { number: 3, text: "Sechs Jahre sollst du dein Feld besäen, sechs Jahre sollst du deinen Weinberg beschneiden und seinen Ertrag ernten." },
      { number: 4, text: "Aber im siebten Jahr soll das Land eine vollständige Sabbatruhe zur Ehre des Herrn halten: Dein Feld sollst du nicht besäen und deinen Weinberg nicht beschneiden." },
      { number: 5, text: "Den Nachwuchs deiner Ernte sollst du nicht ernten und die Trauben deines nicht beschnittenen Weinstockes sollst du nicht lesen. Für das Land soll es ein Jahr der Sabbatruhe sein." },
      { number: 6, text: "Der Sabbat des Landes selbst soll euch ernähren: dich, deinen Knecht, deine Magd, deinen Lohnarbeiter, deinen Halbbürger, alle, die bei dir leben." },
      { number: 7, text: "Auch deinem Vieh und den Tieren in deinem Land wird sein ganzer Ertrag zur Nahrung dienen." },
      { number: 8, text: "Du sollst sieben Jahreswochen, siebenmal sieben Jahre, zählen; die Zeit von sieben Jahreswochen ergibt für dich neunundvierzig Jahre.", heading: "Das Jubeljahr: 25,8-31", footnotes: ["8-12: Das deutsche Wort «Jubel» kommt vom hebräischen jobél (= Widderhorn). Mit dem Widderhorn blies man Alarm, kündigte man aber auch freudige Ereignisse, Feste und Festzeiten an."] },
      { number: 9, text: "Im siebten Monat, am zehnten Tag des Monats, sollst du das Signalhorn ertönen lassen; am Versöhnungstag sollt ihr das Horn im ganzen Land ertönen lassen." },
      { number: 10, text: "Erklärt dieses fünfzigste Jahr für heilig und ruft Freiheit für alle Bewohner des Landes aus! Es gelte euch als Jubeljahr. Jeder von euch soll zu seinem Grundbesitz zurückkehren, jeder soll zu seiner Sippe heimkehren." },
      { number: 11, text: "Dieses fünfzigste Jahr gelte euch als Jubeljahr. Ihr sollt nicht säen, den Nachwuchs nicht abernten, die unbeschnittenen Weinstöcke nicht lesen." },
      { number: 12, text: "Denn es ist ein Jubeljahr, es soll euch als heilig gelten. Vom Feld weg sollt ihr den Ertrag essen." },
      { number: 13, text: "In diesem Jubeljahr soll jeder von euch zu seinem Besitz zurückkehren." },
      { number: 14, text: "Wenn du deinem Stammesgenossen etwas verkaufst oder von ihm etwas kaufst, sollt ihr einander nicht übervorteilen." },
      { number: 15, text: "Kaufst du von deinem Stammesgenossen, so berücksichtige die Zahl der Jahre nach dem Jubeljahr; verkauft er dir, dann soll er die noch ausstehenden Ertragsjahre berücksichtigen." },
      { number: 16, text: "Je höher die Anzahl der Jahre, desto höher berechne den Kaufpreis; je geringer die Anzahl der Jahre, desto weniger verlang von ihm; denn es ist die Zahl von Ernteerträgen, die er dir verkauft." },
      { number: 17, text: "Ihr sollt einander nicht übervorteilen. Fürchte deinen Gott; denn ich bin der Herr, euer Gott." },
      { number: 18, text: "Ihr sollt meine Satzungen befolgen und auf meine Vorschriften achten und sie ausführen; dann werdet ihr im Land in Sicherheit wohnen." },
      { number: 19, text: "Das Land wird seine Frucht geben, ihr werdet euch satt essen und in Sicherheit darin wohnen." },
      { number: 20, text: "Wenn ihr aber fragt: Was sollen wir im siebten Jahr essen, wenn wir nicht säen und unseren Ertrag nicht ernten dürfen? -" },
      { number: 21, text: "Seht, ich werde für euch im sechsten Jahr meinen Segen aufbieten und er wird den Ertrag für drei Jahre geben." },
      { number: 22, text: "Wenn ihr im achten Jahr sät, werdet ihr noch bis zum neunten Jahr vom alten Ertrag essen können; bis der Ertrag dieses Jahres kommt, werdet ihr vom alten essen können." },
      { number: 23, text: "Das Land darf nicht endgültig verkauft werden; denn das Land gehört mir und ihr seid nur Fremde und Halbbürger bei mir." },
      { number: 24, text: "Für jeden Grundbesitz sollt ihr ein Rückkaufrecht auf das Land gewähren." },
      { number: 25, text: "Wenn dein Bruder verarmt und etwas von seinem Grundbesitz verkauft, soll sein Verwandter als Löser für ihn eintreten und den verkauften Boden seines Bruders auslösen.", footnotes: ["«Löser» ist der nächste haftpflichtige Blutsverwandte, der für die Vollstreckung der Blutrache, für die Leviratsehe (vgl. die Anmerkung zu Gen 38,12-26) und den Loskauf verschuldeter und darum in Schuldknechtschaft geratener Personen bzw. des verpfändeten Familienbesitzes zu sorgen hatte (vgl. Dtn 25,5f und Rut 2,20; 3,8 - 4,10)."] },
      { number: 26, text: "Hat einer keinen Löser, hat er aber die nötigen Mittel für den Rückkauf selbst aufgebracht," },
      { number: 27, text: "dann soll er die Jahre seit dem Verkauf anrechnen und den Restbetrag dem Käufer zurückzahlen; sein Grundbesitz fällt an ihn zurück." },
      { number: 28, text: "Bringt er die nötigen Mittel für diese Ersatzleistung nicht auf, dann soll der verkaufte Grund bis zum Jubeljahr im Besitz des Käufers bleiben. Im Jubeljahr wird das Grundstück frei und es kommt wieder zu seinem Besitz." },
      { number: 29, text: "Verkauft jemand ein Wohnhaus in einer ummauerten Stadt, so besteht das Rückkaufrecht bis zum Ablauf des Jahres, das dem Verkauf folgt; sein Rückkaufrecht ist zeitlich beschränkt." },
      { number: 30, text: "Erfolgt der Rückkauf bis zum Ablauf des Jahres nicht, dann soll das Haus innerhalb der ummauerten Stadt dem Käufer und seinen Nachkommen endgültig verbleiben; er braucht es im Jubeljahr nicht zu verlassen." },
      { number: 31, text: "Aber die Häuser in Dörfern, die nicht von Mauern umgeben sind, werden als Bestandteil des freien Feldes betrachtet; für sie besteht ein Rückkaufrecht und der Käufer muss es im Jubeljahr verlassen." },
      { number: 32, text: "Für die Städte der Leviten, die Häuser der Städte, die ihr Erbbesitz sind, gilt: Die Leviten haben ein zeitlich unbegrenztes Rückkaufrecht.", heading: "Sonderbestimmungen für die Leviten: 25,32-34" },
      { number: 33, text: "Wenn einer von den Leviten auf Einlösung verzichtet, fällt im Jubeljahr das Haus in der Stadt als Erbbesitz zurück; denn die Häuser in den Städten der Leviten sind deren Eigentum mitten unter den Israeliten.", footnotes: ["Wenn einer von den Leviten auf Einlösung verzichtet: so nach Vg."] },
      { number: 34, text: "Das Weideland, das zu diesen Städten gehört, kann nicht verkauft werden; denn es ist zeitlich unbegrenzt ihr Eigentum." },
      { number: 35, text: "Wenn dein Bruder verarmt und sich neben dir nicht halten kann, sollst du ihn, auch einen Fremden oder Halbbürger, unterstützen, damit er neben dir leben kann.", heading: "Soziales Verhalten: 25,35-38" },
      { number: 36, text: "Nimm von ihm keinen Zins und Wucher! Fürchte deinen Gott und dein Bruder soll neben dir leben können.", footnotes: ["Ex 22,24; Dtn 23,20f"] },
      { number: 37, text: "Du sollst ihm weder dein Geld noch deine Nahrung gegen Zins und Wucher geben." },
      { number: 38, text: "Ich bin der Herr, euer Gott, der euch aus Ägypten herausgeführt hat, um euch Kanaan zu geben und euer Gott zu sein." },
      { number: 39, text: "Wenn ein Bruder bei dir verarmt und sich dir verkauft, darfst du ihm keine Sklavenarbeit auferlegen;", heading: "Die Freilassung israelitischer Sklaven: 25,39-43", footnotes: ["(39-43) Ex 21,2-11"] },
      { number: 40, text: "er soll dir wie ein Lohnarbeiter oder ein Halbbürger gelten und bei dir bis zum Jubeljahr arbeiten." },
      { number: 41, text: "Dann soll er von dir frei weggehen, er und seine Kinder, und soll zu seiner Sippe, zum Eigentum seiner Väter zurückkehren." },
      { number: 42, text: "Denn sie sind meine Knechte; ich habe sie aus Ägypten herausgeführt; sie sollen nicht verkauft werden, wie ein Sklave verkauft wird." },
      { number: 43, text: "Du sollst nicht mit Gewalt über ihn herrschen. Fürchte deinen Gott!" },
      { number: 44, text: "Die Sklaven und Sklavinnen, die euch gehören sollen, kauft von den Völkern, die rings um euch wohnen; von ihnen könnt ihr Sklaven und Sklavinnen erwerben.", heading: "Die Sklaven aus fremden Völkern: 25,44-46" },
      { number: 45, text: "Auch von den Kindern der Halbbürger, die bei euch leben, aus ihren Sippen, die mit euch leben, von den Kindern, die sie in eurem Land gezeugt haben, könnt ihr Sklaven erwerben. Sie sollen euer Eigentum sein" },
      { number: 46, text: "und ihr dürft sie euren Söhnen vererben, damit diese sie als dauerndes Eigentum besitzen; ihr sollt sie als Sklaven haben. Aber was eure Brüder, die Israeliten, angeht, so soll keiner über den andern mit Gewalt herrschen." },
      { number: 47, text: "Wenn ein Fremder oder ein Halbbürger bei dir zu Vermögen kommt, aber dein Bruder von ihm wirtschaftlich abhängig wird und sich ihm oder einem Nachkommen aus der Familie eines Fremden verkauft,", heading: "Das Loskaufrecht israelitischer Sklaven bei Fremden: 25,47-54" },
      { number: 48, text: "dann soll es, wenn er sich verkauft hat, für ihn ein Loskaufrecht geben: Einer seiner Brüder soll ihn auslösen." },
      { number: 49, text: "Auslösen sollen ihn sein Onkel, der Sohn seines Onkels oder sonst ein Verwandter aus seiner Sippe. Falls seine eigenen Mittel ausreichen, kann er sich selbst loskaufen." },
      { number: 50, text: "Er soll mit dem, der ihn gekauft hat, die Jahre zwischen dem Verkaufs- und dem Jubeljahr berechnen; die Summe des Verkaufspreises soll auf die Zahl der Jahre verteilt werden, wobei die verbrachte Zeit wie die eines Lohnarbeiters gilt." },
      { number: 51, text: "Wenn noch viele Jahre abzudienen sind, soll er ihrer Zahl entsprechend den Lösepreis von seiner Kaufsumme absetzen." },
      { number: 52, text: "Wenn nur noch wenige Jahre bis zum Jubeljahr übrig sind, soll er es ihm berechnen; den Jahren entsprechend soll er den Lösepreis bezahlen." },
      { number: 53, text: "Er gelte wie ein Lohnarbeiter Jahr um Jahr bei seinem Herrn; dieser soll nicht vor deinen Augen mit Gewalt über ihn herrschen." },
      { number: 54, text: "Wenn er in der Zwischenzeit nicht losgekauft wird, soll er im Jubeljahr freigelassen werden, er und seine Kinder." },
      { number: 55, text: "Denn mir gehören die Israeliten als Knechte, meine Knechte sind sie; ich habe sie aus Ägypten herausgeführt, ich, der Herr, euer Gott.", heading: "Abschließende Begründung: 25,55" },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Ihr sollt euch keine Götzen machen, euch weder ein Gottesbild noch ein Steinmal aufstellen und in eurem Land keine Steine mit Bildwerken aufrichten, um euch vor ihnen niederzuwerfen; denn ich bin der Herr, euer Gott.", heading: "Segen und Fluch: 26,1-46 | Die Grundforderungen Gottes: 26,1-2", footnotes: ["19,4; Ex 20,4; Dtn 5,8"] },
      { number: 2, text: "Ihr sollt auf meine Sabbate achten und mein Heiligtum fürchten; ich bin der Herr." },
      { number: 3, text: "Wenn ihr nach meinen Satzungen handelt, auf meine Gebote achtet und sie befolgt,", heading: "Die Segenszusage für Gehorsam: 26,3-13", footnotes: ["(3-13) Dtn 28,1-14", "3-38: Im Alten Orient war es Brauch, Gesetzbücher und Verträge mit Flüchen und Segenssprüchen abzuschließen, um vor Missachtung der Gesetze oder vor Vertragsbruch abzuschrecken und zur Befolgung der Gesetze oder zur Vertragstreue anzuspornen. So schließt auch das «Heiligkeitsgesetz» hier und das ursprüngliche Deuteronomium in Dtn 28 mit Segen und Fluch."] },
      { number: 4, text: "so gebe ich euch Regen zur rechten Zeit; die Erde liefert ihren Ertrag, und der Baum des Feldes gibt seine Früchte;" },
      { number: 5, text: "die Dreschzeit reicht bei euch bis zur Weinlese und die Weinlese bis zur Aussaat. Ihr esst euch satt an eurem Brot und wohnt in eurem Land in Sicherheit." },
      { number: 6, text: "Ich schaffe Frieden im Land: Ihr legt euch nieder und niemand schreckt euch auf. Ich lasse die Raubtiere aus dem Land verschwinden. Kein Schwert kommt über euer Land." },
      { number: 7, text: "Verfolgt ihr eure Feinde, so werden sie vor euren Augen dem Schwert verfallen." },
      { number: 8, text: "Fünf von euch werden hundert verfolgen, hundert von euch werden zehntausend verfolgen und eure Feinde werden vor euren Augen dem Schwert verfallen." },
      { number: 9, text: "Euch wende ich mich zu, mache euch fruchtbar und zahlreich und halte meinen Bund mit euch aufrecht." },
      { number: 10, text: "Ihr werdet noch von der alten Ernte zu essen haben und das Alte hinausschaffen müssen, um Platz für das Neue zu haben." },
      { number: 11, text: "Ich schlage meine Wohnstätte in eurer Mitte auf und habe gegen euch keine Abneigung." },
      { number: 12, text: "Ich gehe in eurer Mitte; ich bin euer Gott und ihr seid mein Volk." },
      { number: 13, text: "Ich bin der Herr, euer Gott, der euch aus dem Land der Ägypter herausgeführt hat, sodass ihr nicht mehr ihre Sklaven zu sein braucht. Ich habe eure Jochstangen zerbrochen und euch wieder aufrecht gehen lassen." },
      { number: 14, text: "Aber wenn ihr auf mich nicht hört und alle diese Gebote nicht befolgt,", heading: "Strafandrohung und Fluch für Ungehorsam: 26,14-38", footnotes: ["(14-38) Dtn 28,15-68"] },
      { number: 15, text: "wenn ihr meine Satzungen missachtet, meine Vorschriften verabscheut und meinen Bund brecht, indem ihr keines meiner Gebote befolgt," },
      { number: 16, text: "so tue auch ich euch Folgendes an: Ich biete gegen euch Bestürzung auf, Schwindsucht und Fieber, die das Augenlicht zum Verlöschen bringen und den Atem ersticken. Ihr sät euer Saatgut vergeblich; eure Feinde werden es verzehren." },
      { number: 17, text: "Ich wende mein Angesicht gegen euch und ihr werdet von euren Feinden geschlagen. Eure Gegner treten euch nieder; ihr flieht, selbst wenn euch niemand verfolgt." },
      { number: 18, text: "Wenn ihr dann immer noch nicht auf mich hört, fahre ich fort, euch zu züchtigen; siebenfach züchtige ich euch für eure Sünden." },
      { number: 19, text: "Ich breche eure stolze Macht und mache euren Himmel wie Eisen und euer Land wie Bronze." },
      { number: 20, text: "Eure Kraft verbraucht sich vergeblich, euer Land liefert keinen Ertrag mehr und die Bäume im Land tragen keine Früchte mehr." },
      { number: 21, text: "Wenn ihr mir feindlich begegnet und nicht auf mich hören wollt, werde ich noch weitere Schläge über euch kommen lassen, siebenfach, wie es euren Sünden entspricht." },
      { number: 22, text: "Ich lasse auf euch die wilden Tiere los, die euer Land entvölkern, euer Vieh vernichten und euch an Zahl so verringern, dass eure Wege veröden." },
      { number: 23, text: "Wenn ihr euch dadurch noch nicht warnen lasst und mir weiterhin feindlich begegnet," },
      { number: 24, text: "begegne auch ich euch feindlich und schlage auch ich euch siebenfach für eure Sünden." },
      { number: 25, text: "Ich lasse über euch das Schwert kommen, das Rache für den Bund nehmen wird. Zieht ihr euch in eure Städte zurück, so sende ich die Pest in eure Mitte und ihr geratet in Feindeshand." },
      { number: 26, text: "Ich entziehe euch dann euren Vorrat an Brot, sodass zehn Frauen euer Brot in einem einzigen Backofen backen, dass man euch das Brot abgewogen zuteilt und ihr euch nicht satt essen könnt.", footnotes: ["Ich entziehe ihnen ihren Vorrat an Brot, wörtlich: Ich zerbreche ihnen den Brotstab; gemeint ist der Holzpflock an der Wand, auf dem die ringförmigen Brote hängen."] },
      { number: 27, text: "Und wenn ihr daraufhin noch immer nicht auf mich hört und mir immer noch feindlich begegnet," },
      { number: 28, text: "begegne auch ich euch im Zorn und züchtige euch siebenfach für eure Sünden." },
      { number: 29, text: "Ihr esst das Fleisch eurer Söhne und Töchter." },
      { number: 30, text: "Ich vernichte eure Kulthöhen, zerstöre eure Räucheraltäre, häufe eure Leichen über die Leichen eurer Götzen und verabscheue euch." },
      { number: 31, text: "Ich mache eure Städte zu Ruinen, verwüste eure Heiligtümer und will den beruhigenden Duft eurer Opfer nicht mehr riechen." },
      { number: 32, text: "Ich selbst verwüste das Land; eure Feinde, die sich darin niederlassen, werden darüber entsetzt sein." },
      { number: 33, text: "Euch aber zerstreue ich unter die Völker und zücke hinter euch das Schwert. Euer Land wird zur Wüste und eure Städte werden zu Ruinen." },
      { number: 34, text: "Dann erhält das Land seine Sabbate ersetzt, in der ganzen Zeit der Verwüstung, während ihr im Land eurer Feinde seid. Dann hat das Land Ruhe und erhält Ersatz für seine Sabbate.", footnotes: ["Der Vers bezieht sich auf das Sabbatjahr, in dem das Ackerland brachliegen, also Ruhe haben sollte (vgl. 25,2-7)."] },
      { number: 35, text: "Während der ganzen Zeit der Verwüstung hat es Sabbatruhe, die es an euren Sabbaten nicht hatte, als ihr noch darin wohntet." },
      { number: 36, text: "In das Herz derer, die von euch überleben, bringe ich Angst in den Ländern ihrer Feinde; das bloße Rascheln verwelkter Blätter jagt sie auf und sie fliehen, wie man vor dem Schwert flieht; sie fallen, ohne dass jemand sie verfolgt." },
      { number: 37, text: "Sie stürzen übereinander wie vor dem Schwert, ohne dass jemand sie verfolgt. Ihr könnt vor euren Feinden nicht standhalten," },
      { number: 38, text: "ihr geht unter den Völkern zugrunde und das Land eurer Feinde frisst euch." },
      { number: 39, text: "Diejenigen von euch, die dann noch überleben, siechen dahin in den Ländern eurer Feinde wegen ihrer Sünden, auch wegen der Sünden ihrer Väter, sodass sie, gleich ihnen, dahinsiechen.", heading: "Umkehr und Vergebung: 26,39-45" },
      { number: 40, text: "Dann werden sie die Schuld eingestehen, die sie selbst und ihre Väter begangen haben durch ihren Treubruch und auch dadurch, dass sie mir feindlich begegnet sind," },
      { number: 41, text: "sodass auch ich ihnen feindlich begegnete und sie in das Land ihrer Feinde führte. Ihr unbeschnittenes Herz muss sich dann beugen und ihre Sünden müssen sie sühnen." },
      { number: 42, text: "Dann werde ich meines Bundes mit Jakob gedenken, meines Bundes mit Isaak und meines Bundes mit Abraham und ich werde meines Landes gedenken." },
      { number: 43, text: "Das Land aber muss von ihnen verlassen werden und seine Sabbate ersetzt bekommen, indem es ohne seine Bewohner verödet daliegt. Sie sollen ihre Schuld sühnen, weil sie immer wieder meine Vorschriften missachtet und meine Satzungen verabscheut haben." },
      { number: 44, text: "Aber selbst wenn sie im Land ihrer Feinde sind, werde ich sie nicht missachten und sie nicht verabscheuen, um ihnen etwa ein Ende zu machen und meinen Bund mit ihnen zu widerrufen; denn ich bin der Herr, ihr Gott." },
      { number: 45, text: "Ich werde zu ihren Gunsten des Bundes mit den früheren Generationen gedenken, die ich vor den Augen der Völker aus Ägypten herausgeführt habe, um ihr Gott zu sein, ich, der Herr." },
      { number: 46, text: "Das sind die Satzungen, Vorschriften und Gesetze, die der Herr zwischen sich und den Israeliten auf dem Sinai durch die Vermittlung des Mose erlassen hat.", heading: "Schluss des Heiligkeitsgesetzes: 26,46" },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Der Herr sprach zu Mose:", heading: "Die Ablösung von Gelübden und Weihegaben: 27,1-34 | Bei Personen: 27,1-8", footnotes: ["1-8: Diese Bestimmung geht vielleicht auf einen alten Brauch zurück, Personen aus der eigenen Sippe aufgrund eines in Notzeiten abgelegten Gelübdes dem Heiligtum zu weihen, d. h. zur Dienstleistung zu übergeben (vgl. 1 Sam 1,11.23-28). Konnte man sich später von der Person nicht trennen, musste man sie gegen Geld auslösen."] },
      { number: 2, text: "Rede zu den Israeliten und sag zu ihnen: Will jemand ein Gelübde für den Herrn einlösen, das er nach dem üblichen Wert einer Person abgelegt hat," },
      { number: 3, text: "so gilt für einen Mann zwischen zwanzig und sechzig Jahren ein Schätzwert von fünfzig Silberschekel, nach dem Schekelgewicht des Heiligtums," },
      { number: 4, text: "für eine Frau ein Schätzwert von dreißig Schekel," },
      { number: 5, text: "für einen Jugendlichen zwischen fünf und zwanzig Jahren, wenn es ein Junge ist, ein Schätzwert von zwanzig Schekel, wenn es ein Mädchen ist, ein Schätzwert von zehn Schekel," },
      { number: 6, text: "für einen Knaben zwischen einem Monat und fünf Jahren ein Schätzwert von fünf und für ein Mädchen ein Schätzwert von drei Silberschekel," },
      { number: 7, text: "für einen Mann von sechzig und mehr Jahren ein Schätzwert von fünfzehn und für eine Frau ein Schätzwert von zehn Schekel." },
      { number: 8, text: "Ist derjenige, der das Gelübde gemacht hat, nicht in der Lage, den Schätzwert zu entrichten, dann soll er die Person dem Priester vorstellen. Dieser soll den Schätzwert nach Maßgabe dessen, was der Gelobende aufbringen kann, feststellen." },
      { number: 9, text: "Handelt es sich um Tiere, die man für den Herrn als Opfergabe darbringen kann, so wird jedes Tier, das man dem Herrn gibt, etwas Heiliges.", heading: "Bei Tieren: 27,9-13" },
      { number: 10, text: "Man darf es weder auswechseln noch vertauschen, nicht ein gutes gegen ein schlechtes oder ein schlechtes gegen ein gutes geben. Wenn man aber doch ein Tier gegen ein anderes vertauscht, so wird sowohl das eine als auch das andere etwas Heiliges." },
      { number: 11, text: "Handelt es sich um ein unreines Tier, von dem man keine Opfergabe für den Herrn darbringen darf, soll man das Tier dem Priester vorführen," },
      { number: 12, text: "und dieser soll den Durchschnittswert feststellen; an seine Schätzung soll man sich halten." },
      { number: 13, text: "Will man es auslösen, so soll man den Schätzwert um ein Fünftel erhöhen.", footnotes: ["13.15.27: Der Zuschlag von einem Fünftel zum Schätzwert ist wohl als Buße für die Zurücknahme des Gelübdes gedacht."] },
      { number: 14, text: "Will man sein Haus dem Herrn weihen, so soll es der Priester schätzen, indem er seinen Durchschnittswert feststellt. Man halte sich an die Schätzung des Priesters.", heading: "Bei Häusern: 27,14-15" },
      { number: 15, text: "Will der Mann, der das Haus gelobt hat, es auslösen, so soll er ein Fünftel des Geldes zum Schätzwert hinzufügen und es soll wieder ihm gehören." },
      { number: 16, text: "Weiht ein Mann eines von seinen Erbgrundstücken dem Herrn, so soll der Schätzwert seines Saatgutes geschätzt werden. Ein Hómer Saatgerste entspricht fünfzig Silberschekeln.", heading: "Bei Grundstücken: 27,16-24" },
      { number: 17, text: "Weiht jemand das Feld vom Jubeljahr an, so halte man sich an diesen Schätzwert;" },
      { number: 18, text: "weiht er es aber nach dem Jubeljahr, soll ihm der Priester den Preis entsprechend der bis zum nächsten Jubeljahr ausstehenden Jahre berechnen und vom Schätzwert abziehen." },
      { number: 19, text: "Will derjenige, der das Feld geweiht hat, es wieder auslösen, soll er ein Fünftel des Geldes zum Schätzwert hinzufügen, und es soll wieder ihm gehören." },
      { number: 20, text: "Löst er es nicht aus, verkauft es aber einem anderen, so kann es nicht mehr ausgelöst werden," },
      { number: 21, text: "und das Feld wird, wenn es im Jubeljahr frei wird, etwas Heiliges für den Herrn, wie ein Feld, das geweihtes Gut ist. Es wird Eigentum des Priesters." },
      { number: 22, text: "Wenn jemand dem Herrn ein Feld weiht, das er erworben hat und das nicht zu seinem Erbbesitz gehört," },
      { number: 23, text: "berechne der Priester den Schätzwert mit Rücksicht auf die Zeit bis zum Jubeljahr und der Mann bezahle die Summe am selben Tag als etwas Heiliges für den Herrn." },
      { number: 24, text: "Im Jubeljahr soll das Feld an den Verkäufer zurückfallen, dem es als ererbter Landbesitz gehört.", footnotes: ["25,23-28"] },
      { number: 25, text: "Jede Schätzung bei dir soll nach dem Schekelgewicht des Heiligtums erfolgen: Zwanzig Gera entsprechen einem Schekel.", heading: "Das Maß der Schätzung: 27,25" },
      { number: 26, text: "Eine Erstgeburt beim Vieh, die als Erstlingsgabe dem Herrn gehört, kann man nicht weihen, weder ein Rind noch ein Schaf; denn es gehört schon dem Herrn.", heading: "Sonderfälle: 27,26-33" },
      { number: 27, text: "Aber wenn es die Erstgeburt eines unreinen Tiers ist, kann man es um den um ein Fünftel erhöhten Schätzwert auslösen. Wird das Tier nicht ausgelöst, soll es nach dem Schätzwert verkauft werden." },
      { number: 28, text: "Aber nichts von dem, was ein Mann von seinem Eigentum an Menschen, Vieh und Feldbesitz als Banngut dem Herrn geweiht hat, darf verkauft oder ausgelöst werden. Alles Banngut ist etwas Hochheiliges; es gehört dem Herrn." },
      { number: 29, text: "Kein menschliches Wesen, das als Banngut geweiht wird, kann zurückgekauft werden; man muss es töten." },
      { number: 30, text: "Jeder Zehnt des Landes, der vom Ertrag des Landes oder von den Baumfrüchten abzuziehen ist, gehört dem Herrn; es ist etwas Heiliges für den Herrn." },
      { number: 31, text: "Will ein Mann einen Teil seines Zehnten auslösen, muss er ein Fünftel dazuzahlen." },
      { number: 32, text: "Jeder Zehnt an Rind, Schaf und Ziege ist dem Herrn geweiht, jedes zehnte Stück von allem, was unter dem Hirtenstab hindurchgeht." },
      { number: 33, text: "Man soll nicht zwischen dem Guten und dem Schlechten unterscheiden und keinen Tausch vornehmen. Wenn man es dennoch tut, werden das Tier und das mit ihm vertauschte Tier etwas Heiliges; es darf nicht ausgelöst werden." },
      { number: 34, text: "Das sind die Gebote, die der Herr dem Mose für die Israeliten auf dem Sinai gegeben hat.", heading: "Abschluss: 27,34" },
    ] },
  ],
};
