import { Book } from "@/lib/types";

export const nehemiah: Book = {
  id: "nehemiah",
  name: "Nehemia",
  shortName: "Neh",
  testament: "old",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Bericht des Nehemia, des Sohnes Hachaljas. Im Monat Kislew, im zwanzigsten Jahr des Artaxerxes, war ich in der Festung Susa;", heading: "Der Wiederaufbau durch Nehemia: 1,1 - 6,19 | Das Gebet Nehemias: 1,1-11", footnotes: ["Est 1,2f; Dan 8,2", "Das 20. Jahr des Perserkönigs Artaxerxes I. ist das Jahr 445 v. Chr."] },
      { number: 2, text: "da kam Hanani, einer meiner Brüder, mit einigen Männern aus Juda. Ich fragte sie, wie es den Juden gehe, den Geretteten, die von den Gefangenen übrig geblieben waren, und wie es um Jerusalem stehe." },
      { number: 3, text: "Sie sagten zu mir: Der Rest, der von den Gefangenen übrig geblieben ist, lebt dort in der Provinz in großer Not und Schmach. Die Stadtmauer von Jerusalem ist niedergelegt und die Tore sind abgebrannt.", footnotes: ["Esra 4,21-24"] },
      { number: 4, text: "Als ich das hörte, setzte ich mich nieder und weinte. Ich trauerte tagelang, fastete und betete zu dem Gott des Himmels.", footnotes: ["Esra 9,3"] },
      { number: 5, text: "Ich sagte: Ach, Herr, Gott des Himmels, du großer und furchtgebietender Gott! Du hältst deinen Bund und bewahrst deine Gnade denen, die dich lieben und deine Gebote halten.", footnotes: ["Dan 9,4"] },
      { number: 6, text: "Hab ein aufmerksames Ohr und ein offenes Auge und hör das Gebet deines Knechtes! Ich bete jetzt Tag und Nacht vor dir für die Söhne Israels, deine Diener. Ich lege ein Bekenntnis ab wegen der Sünden der Söhne Israels. Wir haben gegen dich gesündigt; auch ich und meine Familie haben gesündigt." },
      { number: 7, text: "Wir haben sehr schlecht gegen dich gehandelt: Wir haben die Gebote, Gesetze und Anordnungen missachtet, die du deinem Diener Mose gegeben hast." },
      { number: 8, text: "Aber denk an das Wort, das du deinem Diener Mose aufgetragen hast: Wenn ihr mir die Treue brecht, dann werde ich euch unter die Völker zerstreuen;", footnotes: ["(8-9) Dtn 30,1-4"] },
      { number: 9, text: "wenn ihr aber zu mir umkehrt, meine Gebote befolgt und sie ausführt, dann sammle ich euch wieder, selbst die, die bis ans Ende des Himmels verschlagen wurden; ich bringe sie an den Ort, den ich erwählt habe, um dort meinen Namen wohnen zu lassen." },
      { number: 10, text: "Sie sind ja deine Knechte, dein Volk, das du erlöst hast mit deiner großen Kraft und deiner starken Hand.", footnotes: ["Dtn 9,29"] },
      { number: 11, text: "Ach, Herr, dein Ohr höre aufmerksam auf das Gebet deines Knechtes und das Gebet deiner Knechte, die von Herzen deinen Namen fürchten: Gewähre deinem Knecht heute Erfolg und lass ihn Erbarmen finden bei diesem Mann! Ich war nämlich Mundschenk beim König." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Im Monat Nisan, im zwanzigsten Jahr des Königs Artaxerxes, nahm ich den Wein und reichte ihn dem König - mein Amt war es nämlich, für den Wein zu sorgen. Nie zuvor hatte der König mein Aussehen schlecht gefunden;", heading: "Die Rückkehr Nehemias: 2,1-10" },
      { number: 2, text: "jetzt aber fragte er mich: Warum siehst du so schlecht aus? Du bist doch nicht etwa krank? Nein, du hast gewiss Kummer. Ich erschrak sehr;" },
      { number: 3, text: "dann sagte ich zum König: Der König möge ewig leben. Wie sollte ich nicht schlecht aussehen? Die Stadt, in der die Gräber meiner Väter sind, liegt in Trümmern und ihre Tore sind vom Feuer verzehrt." },
      { number: 4, text: "Der König erwiderte: Was möchtest du also? Da betete ich zum Gott des Himmels;" },
      { number: 5, text: "dann sagte ich zum König: Wenn du, König, es für gut findest und wenn du deinem Knecht vertraust, so sende mich nach Juda, damit ich die Stadt wieder aufbaue, in der die Gräber meiner Väter sind." },
      { number: 6, text: "Darauf fragte mich der König, während die Königin neben ihm saß: Wie lang soll deine Reise dauern? Wann kommst du zurück? Ich nannte ihm eine bestimmte Zeit; der König war einverstanden und ließ mich ziehen." },
      { number: 7, text: "Ich sagte dem König noch: Wenn der König es für gut findet, dann gebe man mir Briefe mit an die Statthalter des Gebiets jenseits des Stroms, damit sie mich bis nach Juda durchreisen lassen;", footnotes: ["Esra 4,10f; 7,11"] },
      { number: 8, text: "ferner einen Brief an Asaf, den Verwalter der königlichen Wälder: Er soll mir Bauholz liefern für die Tore der Tempelburg, für die Stadtmauer und für das Haus, in das ich ziehen will. Der König bewilligte es mir, weil die gütige Hand meines Gottes über mir war." },
      { number: 9, text: "So kam ich zu den Statthaltern im Gebiet jenseits des Stroms und ich übergab ihnen die Briefe des Königs. Der König hatte mir auch Hauptleute und Reiter als Geleit gegeben.", footnotes: ["Esra 8,22"] },
      { number: 10, text: "Sanballat, der Horoniter, und Tobija, der Knecht von Ammon, hörten davon. Es verdross sie sehr, dass da ein Mann kam, der sich für das Wohl der Israeliten einsetzte." },
      { number: 11, text: "So kam ich nach Jerusalem. Dort blieb ich drei Tage.", heading: "Der Entschluss zum Mauerbau: 2,11-20", footnotes: ["Esra 8,32"] },
      { number: 12, text: "Dann machte ich mich bei Nacht auf, nahm aber nur einige wenige Männer mit. Noch hatte ich keinem Menschen mitgeteilt, was mein Gott mir eingegeben hatte, für Jerusalem zu tun. Ich nahm keine Tiere mit außer dem einen, auf dem ich ritt." },
      { number: 13, text: "So ritt ich bei Nacht zum Taltor hinaus. An der Drachenquelle vorbei gelangte ich zum Aschentor. Dabei besichtigte ich die Mauern Jerusalems: Sie waren niedergerissen und die Tore vom Feuer verzehrt.", footnotes: ["3,13-15; 12,31-34.37"] },
      { number: 14, text: "Ich ritt zum Quelltor und zum Königsteich hinüber. Hier konnte ich mit dem Reittier nicht mehr vorwärts kommen." },
      { number: 15, text: "So ging ich bei Nacht das Bachtal hinauf und besichtigte die Mauer. Dann kehrte ich um und kam durch das Taltor wieder zurück." },
      { number: 16, text: "Die Vorsteher wussten nicht, wohin ich gegangen war und was ich vorhatte. Denn ich hatte bis dahin den Juden nichts mitgeteilt, weder den Priestern noch den Vornehmen, noch den Beamten und den übrigen, die an dem Werk mitwirken sollten." },
      { number: 17, text: "Jetzt aber sagte ich zu ihnen: Ihr seht selbst, in welchem Elend wir leben: Jerusalem liegt in Trümmern und seine Tore sind abgebrannt. Gehen wir daran und bauen wir die Mauern Jerusalems wieder auf! So machen wir unserer Schande ein Ende." },
      { number: 18, text: "Dann berichtete ich ihnen, wie die Hand meines Gottes so gütig über mir gewesen war und was der König mir zugesagt hatte. Da sagten sie: Wir wollen ans Werk gehen und bauen. Und sie nahmen die gute Sache in die Hand." },
      { number: 19, text: "Als aber Sanballat, der Horoniter, Tobija, der Knecht von Ammon, und der Araber Geschem davon hörten, verspotteten sie uns und sagten verächtlich: Was soll das, was ihr da macht? Wollt ihr euch etwa gegen den König auflehnen?", footnotes: ["Esra 4,23"] },
      { number: 20, text: "Ich ließ ihnen antworten: Der Gott des Himmels wird uns Erfolg verleihen. Wir, seine Knechte, wollen ans Werk gehen und bauen. Ihr hingegen habt weder einen Anteil (an der Stadt) noch Anrecht (auf sie); es gibt keine Erinnerung an euch in Jerusalem." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Der Hohepriester Eljaschib und seine Brüder, die Priester, machten sich ans Werk und bauten das Schaftor auf. Sie setzten die Balken ein und brachten die Torflügel an. Sie bauten weiter bis zum Turm der Hundert, setzten die Balken ein und kamen bis zum Turm Hananel.", heading: "Der Bericht über den Bau: 3,1-32", footnotes: ["Jer 31,38-40; Neh 12,31-39", "Text korr.; H unklar."] },
      { number: 2, text: "Anschließend bauten die Männer von Jericho und daneben baute Sakkur, der Sohn Imris." },
      { number: 3, text: "Das Fischtor bauten die Söhne des Senaa; sie setzten die Balken ein und brachten die Torflügel, Riegel und Sperrbalken an." },
      { number: 4, text: "Neben ihnen arbeitete an der Instandsetzung Meremot, der Sohn Urijas, des Sohnes des Koz. Daneben arbeitete Meschullam, der Sohn Berechjas, des Sohnes Meschesabels. Daneben arbeitete Zadok, der Sohn Baanas." },
      { number: 5, text: "Daneben arbeiteten die Leute aus Tekoa. Die Vornehmen unter ihnen freilich beugten den Nacken nicht zum Dienst für ihre Herren." },
      { number: 6, text: "Jojada, der Sohn Paseachs, und Meschullam, der Sohn Besodjas, arbeiteten an der Instandsetzung des Jeschanators; sie setzten die Balken ein und brachten die Torflügel, Riegel und Sperrbalken an." },
      { number: 7, text: "Daneben arbeitete Melatja aus Gibeon und Jadon aus Meronot sowie die Leute aus Gibeon und Mizpa, dort wo der Statthalter des Gebiets jenseits des Stroms seinen Sitz hatte." },
      { number: 8, text: "Daneben arbeitete Usiël, der Sohn Harhajas, einer von den Goldschmieden; und daneben arbeitete Hananja, ein Salbenhersteller. Sie befestigten Jerusalem bis zur Breiten Mauer." },
      { number: 9, text: "Daneben arbeitete Refaja, der Sohn Hurs, der Vorsteher der einen Hälfte des Bezirks von Jerusalem." },
      { number: 10, text: "Daneben arbeitete Jedaja, der Sohn Harumafs, gegenüber seinem Haus; und daneben arbeitete Hattusch, der Sohn Haschabnejas." },
      { number: 11, text: "Malkija, der Sohn Harims, und Haschub, der Sohn des Pahat-Moab, arbeiteten an der Instandsetzung des folgenden Stückes und des Ofenturms." },
      { number: 12, text: "Daneben arbeitete Schallum, der Sohn des Lohesch, der Vorsteher der anderen Hälfte des Bezirks von Jerusalem. Ihm halfen seine Töchter." },
      { number: 13, text: "Hanun und die Einwohner von Sanoach arbeiteten an der Instandsetzung des Taltors; sie bauten es auf und brachten die Torflügel, Riegel und Sperrbalken an; sie setzten auch weitere tausend Ellen der Mauer instand, bis zum Aschentor." },
      { number: 14, text: "Malkija, der Sohn Rechabs, der Vorsteher des Bezirks von Bet-Kerem, arbeitete an der Instandsetzung des Aschentors; er baute es auf und brachte die Torflügel, Riegel und Sperrbalken an." },
      { number: 15, text: "Schallun, der Sohn Kolhoses, der Vorsteher des Bezirks von Mizpa, arbeitete an der Instandsetzung des Quelltors; er baute es auf, versah es mit einem Dach und brachte die Torflügel, Riegel und Sperrbalken an. Weiter setzte er die Mauer am Teich der Wasserleitung beim Königsgarten instand, bis zu den Stufen, die von der Davidstadt herabführen.", footnotes: ["2 Kön 20,20; Joh 9,7"] },
      { number: 16, text: "Hinter ihm arbeitete Nehemja, der Sohn des Asbuk, der Vorsteher der einen Hälfte des Bezirks von Bet-Zur; er arbeitete bis zu der Stelle gegenüber den Gräbern Davids und weiter bis zum künstlichen Teich und zur Kaserne der Leibwache." },
      { number: 17, text: "Hinter ihm arbeiteten die Leviten, nämlich Rehum, der Sohn Banis, und daneben Haschabja, der Vorsteher der einen Hälfte des Bezirks von Keïla; er arbeitete für seinen Bezirk." },
      { number: 18, text: "Hinter ihm arbeiteten die Brüder dieser Leviten unter Binnui, dem Sohn des Henadad, dem Vorsteher der anderen Hälfte des Bezirks von Keïla." },
      { number: 19, text: "Neben ihm arbeitete Eser, der Sohn Jeschuas, der Vorsteher von Mizpa, an der Instandsetzung des folgenden Stückes, gegenüber dem Aufstieg zum Zeughaus am Winkel." },
      { number: 20, text: "Hinter ihm arbeitete Baruch, der Sohn Sabbais, an der Instandsetzung des anschließenden Stückes, vom Winkel bis an den Eingang zum Haus des Hohenpriesters Eljaschib." },
      { number: 21, text: "Hinter ihm arbeitete Meremot, der Sohn Urijas, des Sohnes des Koz, am anschließenden Stück, vom Eingang bis zum Ende des Hauses von Eljaschib." },
      { number: 22, text: "Hinter ihm arbeiteten die Priester, die im Umkreis wohnten." },
      { number: 23, text: "Dahinter arbeiteten Benjamin und Haschub gegenüber ihrem Haus und hinter ihnen arbeitete Asarja, der Sohn Maasejas, des Sohnes Ananejas, neben seinem Haus." },
      { number: 24, text: "Dahinter arbeitete Binnui, der Sohn Henadads, an der Instandsetzung des anschließenden Stückes, vom Haus des Asarja bis zum Winkel und weiter bis zur Ecke." },
      { number: 25, text: "Palal, der Sohn Usais, arbeitete gegenüber dem Winkel und dem oberen Turm, der vom königlichen Palast am Wachthof vorspringt. Hinter ihm arbeiteten Pedaja, der Sohn des Parosch,", footnotes: ["Jer 32,2"] },
      { number: 26, text: "und die Tempeldiener, die auf dem Ofel wohnten, bis zu der Stelle gegenüber dem Wassertor im Osten und dem vorspringenden Turm." },
      { number: 27, text: "Dahinter arbeiteten die Leute von Tekoa an der Instandsetzung des anschließenden Stückes, von der Stelle, die dem großen, vorspringenden Turm gegenüberliegt, bis zur Mauer des Ofel." },
      { number: 28, text: "Oberhalb des Rosstors arbeiteten die Priester, jeder seinem Haus gegenüber." },
      { number: 29, text: "Dahinter arbeitete Zadok, der Sohn Immers, seinem Haus gegenüber, und hinter ihm arbeitete Schemaja, der Sohn Schechanjas, der Wächter des Osttors.", footnotes: ["Ez 40,6"] },
      { number: 30, text: "Dahinter arbeiteten Hananja, der Sohn Schelemjas, und Hanun, der sechste Sohn Zalafs, an der Instandsetzung eines weiteren Stückes. Dahinter arbeitete Meschullam, der Sohn Berechjas, gegenüber seiner Zelle." },
      { number: 31, text: "Hinter ihm arbeitete Malkija, einer von den Goldschmieden, bis zum Haus der Tempeldiener und der Händler, gegenüber dem Wachttor, und bis zum Obergemach an der Ecke." },
      { number: 32, text: "Und zwischen dem Obergemach an der Ecke und dem Schaftor arbeiteten die Goldschmiede und die Händler." },
      { number: 33, text: "Als Sanballat hörte, dass wir die Mauer aufbauten, wurde er zornig und ärgerte sich sehr. Er spottete über die Juden", heading: "Der Spott der Feinde: 3,33-38", footnotes: ["2,10.19"] },
      { number: 34, text: "und sagte vor seinen Brüdern und dem Heer von Samarien: Was machen diese elenden Juden da? Wollen sie Jerusalem wieder für sich befestigen? Wollen sie Opfer darbringen? Wollen sie es an einem Tag vollenden? Können sie die Steine, die doch ausgeglüht sind, aus den Schutthaufen zu neuem Leben aufrichten?" },
      { number: 35, text: "Und Tobija von Ammon, der neben ihm stand, sagte: Lasst sie nur bauen! Springt ein Fuchs hinauf, dann reißt er ihre Steinmauer nieder." },
      { number: 36, text: "Hör, unser Gott, wie wir zum Gespött geworden sind. Lass ihren Hohn auf sie selbst zurückfallen! Gib sie der Plünderung und der Gefangenschaft preis!" },
      { number: 37, text: "Deck ihr Vergehen nicht zu! Ihre Sünde soll bei dir nicht ausgelöscht sein, denn sie haben die Bauenden beleidigt.", footnotes: ["Jer 18,23"] },
      { number: 38, text: "Wir bauten an der Mauer weiter und bald hatte sich die Mauer ringsum bis zur Hälfte geschlossen. Das ermutigte das Volk zur weiteren Arbeit." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Als aber Sanballat und Tobija sowie die Araber, die Ammoniter und die Leute von Aschdod hörten, dass der Wiederaufbau der Mauer von Jerusalem voranging - denn die Breschen schlossen sich allmählich -, wurden sie wütend", heading: "Störaktionen von außen: 4,1-17" },
      { number: 2, text: "und alle zusammen verschworen sich, gegen Jerusalem in den Krieg zu ziehen und dort Unruhe zu stiften." },
      { number: 3, text: "Wir aber beteten zu unserem Gott und stellten Tag und Nacht eine Wache auf, um uns vor ihnen zu schützen." },
      { number: 4, text: "Doch dann sagten die Juden: Den Trägern geht die Kraft aus, denn es liegt zu viel Schutt da; es wird uns nie gelingen, die Mauer aufzubauen." },
      { number: 5, text: "Unsere Feinde aber sagten: Sie sollen nichts merken und nichts von uns sehen, bis wir mitten unter ihnen stehen; dann metzeln wir sie nieder und machen dem Unternehmen ein Ende." },
      { number: 6, text: "Doch die Juden, die bei ihnen lebten, kamen und sagten uns mindestens zehnmal: Aus allen Orten ringsum, wo sie wohnen, ziehen sie gegen uns heran." },
      { number: 7, text: "Da stellte ich unterhalb der Mauer auf dem freien Gelände das Volk nach Sippen geordnet auf, mit Schwertern, Lanzen und Bogen bewaffnet." },
      { number: 8, text: "Ich musterte sie, dann erhob ich mich und sagte zu den Vornehmen, den Beamten und den übrigen Männern: Fürchtet euch nicht vor ihnen! Denkt an den Herrn; er ist groß und furchtgebietend. Kämpft für eure Brüder und Söhne, für eure Töchter und Frauen und für eure Häuser!" },
      { number: 9, text: "Unsere Feinde erfuhren, dass uns ihr Vorhaben bekannt geworden war. So vereitelte Gott ihren Plan und wir alle kehrten zu der Mauer zurück, jeder zu seiner Arbeit." },
      { number: 10, text: "Seit jenem Tag arbeitete nur die Hälfte meiner Leute am Bau; die andere Hälfte hielt Lanzen, Schilde, Bogen und Panzer bereit und die Obersten standen hinter dem ganzen Volk Juda," },
      { number: 11, text: "das an der Mauer baute. Die Lastträger arbeiteten so: Mit der einen Hand taten sie ihre Arbeit, in der andern hielten sie den Wurfspieß." },
      { number: 12, text: "Von den Bauleuten hatte jeder sein Schwert um die Hüften gegürtet und so bauten sie. Ständig hatte ich den Hornbläser bei mir" },
      { number: 13, text: "und ich sagte zu den Vornehmen, den Beamten und den übrigen: Die Arbeit ist vielfältig und weit ausgedehnt. Wir stehen auf der Mauer zerstreut und weit voneinander entfernt." },
      { number: 14, text: "Wo ihr also das Horn ertönen hört, dort sammelt euch um uns! Unser Gott wird für uns streiten.", footnotes: ["Ex 14,14"] },
      { number: 15, text: "So arbeiteten wir am Bau, während die Hälfte die Lanzen bereithielt, vom Anbruch der Morgenröte bis zum Aufgang der Sterne." },
      { number: 16, text: "Damals sagte ich dem Volk noch: Jeder soll mit seinen Leuten auch nachts in Jerusalem bleiben; dann stehen sie uns in der Nacht als Wache zur Verfügung und am Tag zur Arbeit." },
      { number: 17, text: "Weder ich noch meine Brüder, weder meine Leute noch die Wachmannschaft, die mich begleitete, keiner von uns zog seine Kleider aus; jeder hatte seine Waffe an der Seite.", footnotes: ["Übersetzung wegen Störungen in H unsicher."] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Die Männer des einfachen Volkes und ihre Frauen erhoben aber laute Klage gegen ihre jüdischen Stammesbrüder.", heading: "Unfriede in den eigenen Reihen: 5,1-13", footnotes: ["Jer 34,8-16"] },
      { number: 2, text: "Die einen sagten: Wir müssen unsere Söhne und Töchter verpfänden, um Getreide zu bekommen, damit wir zu essen haben und leben können.", footnotes: ["2 Kön 4,1; Mt 18,25"] },
      { number: 3, text: "Andere sagten: Wir müssen unsere Felder, Weinberge und Häuser verpfänden, um in der Hungerzeit Getreide zu bekommen." },
      { number: 4, text: "Wieder andere sagten: Auf unsere Felder und Weinberge mussten wir Geld aufnehmen für die Steuern des Königs." },
      { number: 5, text: "Wir sind doch vom selben Fleisch wie unsere Stammesbrüder; unsere Kinder sind ihren Kindern gleich und doch müssen wir unsere Söhne und Töchter zu Sklaven erniedrigen. Einige von unseren Töchtern sind schon erniedrigt worden. Wir sind machtlos und unsere Felder und Weinberge gehören anderen.", footnotes: ["Lev 25,39.43"] },
      { number: 6, text: "Als ich ihre Klage und diese Worte hörte, wurde ich sehr zornig." },
      { number: 7, text: "Ich überlegte mir die Sache; dann stellte ich die Vornehmen und die Beamten zur Rede und sagte zu ihnen: Die eigenen Stammesbrüder bedrückt ihr mit Schuldforderungen. Und ich berief ihretwegen eine große Versammlung ein" },
      { number: 8, text: "und sagte zu ihnen: Wir haben von unseren jüdischen Stammesbrüdern, die an andere Völker verkauft worden waren, so viele wie möglich losgekauft. Ihr aber, ihr wollt eure eigenen Stammesbrüder verkaufen, damit sie dann wieder an uns verkauft werden. Da schwiegen sie und wussten nichts zu erwidern.", footnotes: ["Lev 25,47f"] },
      { number: 9, text: "Darauf sagte ich: Was ihr tut, ist nicht recht. Wollt ihr nicht das Gebot unseres Gottes gewissenhaft einhalten, um so dem Hohn der uns feindlichen Völker zu entgehen?", footnotes: ["Lev 25,35-37"] },
      { number: 10, text: "Auch ich und meine Brüder und meine Leute haben Stammesbrüdern Geld und Getreide geliehen. Erlassen wir ihnen doch diese Schuldforderungen." },
      { number: 11, text: "Gebt ihnen unverzüglich ihre Äcker und Weinberge, ihre Ölgärten und Häuser zurück und erlasst ihnen die Schuld an Geld und Getreide, Wein und Öl, die sie bei euch haben.", footnotes: ["Dtn 15,1f"] },
      { number: 12, text: "Da erklärten sie: Wir wollen alles zurückgeben und nichts mehr von ihnen fordern. Wir wollen tun, was du gesagt hast. Darauf rief ich die Priester herbei und ließ die Leute schwören, dass sie ihre Zusage halten würden." },
      { number: 13, text: "Dann schüttelte ich den Bausch meines Gewandes aus und sagte: Genauso schüttle Gott jeden, der diese Zusage nicht hält, aus seinem Haus und seinem Eigentum; er sei ebenso ausgeschüttelt und leer. Die ganze Versammlung antwortete: Amen, so sei es!, und pries den Herrn und das Volk erfüllte die Zusage.", footnotes: ["Im Gewandbausch trug man Feldfrüchte (vgl. Ps 129,7; Lk 6,38)."] },
      { number: 14, text: "Außerdem verzichtete ich mit meinen Brüdern auf den Unterhalt, den ich als Statthalter hätte beanspruchen können, und zwar von dem Tag an, an dem mich der König zum Statthalter in Juda bestellt hatte, vom zwanzigsten bis zum zweiunddreißigsten Jahr des Artaxerxes, also zwölf Jahre lang.", heading: "Nehemia als Statthalter: 5,14-19" },
      { number: 15, text: "Die Statthalter, die mir vorangingen, hatten das Volk schwer belastet; sie hatten von ihm täglich vierzig Silberschekel für ihren Unterhalt erhoben; auch ihre Leute hatten das Volk unterdrückt. Ich hingegen tat das aus Gottesfurcht nicht." },
      { number: 16, text: "Auch beim Bau der Mauer habe ich selbst Hand angelegt. Wir haben kein Feld gekauft. Alle meine Leute halfen dort gemeinsam bei der Bauarbeit." },
      { number: 17, text: "An meinem Tisch speisten die führenden Juden und die Beamten, hundertfünfzig an der Zahl, sowie die, die von den Völkern ringsumher zu uns kamen." },
      { number: 18, text: "Täglich wurden ein Ochse, sechs auserlesene Schafe und auch Geflügel zubereitet und all das ging auf meine Kosten. Dazu kam alle zehn Tage eine Menge von verschiedenen Weinen. Trotzdem habe ich den Unterhalt eines Statthalters nicht eingefordert, denn der Frondienst lag schon schwer genug auf diesem Volk." },
      { number: 19, text: "Denk daran, mein Gott, und lass mir all das zugute kommen, was ich für dieses Volk getan habe.", footnotes: ["6,14; 13,14.31; Ps 132,1"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Sanballat, Tobija, der Araber Geschem und unsere übrigen Feinde erfuhren, dass ich die Mauer fertig gebaut hatte und dass in ihr keine Lücke mehr war. Allerdings hatte ich damals die Torflügel noch nicht in die Tore eingesetzt.", heading: "Die Abwehr der Feinde: 6,1-19" },
      { number: 2, text: "Da ließen Sanballat und Geschem mir sagen: Komm, wir wollen uns in Kefirim in der Ebene von Ono treffen. Sie hatten aber Böses gegen mich im Sinn.", footnotes: ["Esra 2,33"] },
      { number: 3, text: "Ich schickte Boten an sie mit der Antwort: Ich arbeite gerade an einem großen Werk; darum kann ich nicht kommen. Die Arbeit würde stocken, wenn ich sie verließe und zu euch käme." },
      { number: 4, text: "Viermal schickten sie mir die gleiche Einladung und jedes Mal gab ich ihnen die gleiche Antwort." },
      { number: 5, text: "Da schickte mir Sanballat in gleicher Weise zum fünften Mal seinen Diener; er brachte einen unverschlossenen Brief," },
      { number: 6, text: "in dem stand: Unter den Völkern geht das Gerücht um - und Geschem bestätigt es -, dass du mit den Juden einen Aufstand planst. Deshalb baust du die Mauer auf. Und du willst, wie man sagt, König der Juden werden.", footnotes: ["Esra 4,11-16"] },
      { number: 7, text: "Du hast auch, so hört man, Propheten bestellt, die in Jerusalem von dir sagen und ausrufen sollen: Juda hat einen König. Solche Gerüchte werden aber dem König zu Ohren kommen. Darum komm jetzt, wir wollen zusammen beraten." },
      { number: 8, text: "Ich ließ ihm antworten: Nichts von dem, was du behauptest, ist geschehen. Das hast du alles selbst erfunden." },
      { number: 9, text: "Sie alle wollten uns nämlich einschüchtern; sie dachten: Dann lassen sie die Hände von dem Werk und es kommt nicht zustande. Nun aber rührte ich die Hände erst recht." },
      { number: 10, text: "Eines Tages ging ich in das Haus Schemajas, des Sohnes Delajas, des Sohnes Mehetabels; er war nämlich dort festgehalten. Er sagte: Gehen wir zusammen in das Haus Gottes, ins Innere des Tempels, und verschließen wir die Türen des Tempels! Denn man wird kommen, um dich zu töten. In der Nacht wird man kommen, um dich zu töten." },
      { number: 11, text: "Ich erwiderte: Sollte ein Mann wie ich fliehen? Wer von meinesgleichen würde am Leben bleiben, wenn er den Tempel beträte? Ich gehe nicht hin.", footnotes: ["Num 18,7; 2 Chr 26,16-20"] },
      { number: 12, text: "Ich erkannte deutlich, dass nicht Gott ihn geschickt hatte; er hatte vielmehr diese Prophezeiung über mich nur gesprochen, weil Tobija und Sanballat ihn gedungen hatten.", footnotes: ["Jer 23,15-17"] },
      { number: 13, text: "Er war gedungen, damit ich aus Furcht so handeln und mich versündigen sollte. Damit wollten sie mich in üblen Ruf bringen, um mich verächtlich zu machen." },
      { number: 14, text: "Mein Gott, vergiss dem Tobija und dem Sanballat nicht, was sie getan haben, auch nicht der Prophetin Noadja und den übrigen Propheten, die mir Angst machen wollten." },
      { number: 15, text: "Nach zweiundfünfzig Tagen, am Fünfundzwanzigsten des Monats Elul, war die Mauer vollendet." },
      { number: 16, text: "Als alle unsere Feinde es hörten, fürchteten sich alle Völker rings um uns her. Ihr Hochmut verging ihnen und sie mussten einsehen, dass unser Gott es war, der dieses Werk vollbracht hatte." },
      { number: 17, text: "In jener Zeit sandten die Vornehmen von Juda auch viele Briefe an Tobija und Briefe von Tobija gelangten an sie." },
      { number: 18, text: "Denn viele Menschen in Juda waren ihm durch einen Eid verpflichtet. Er war nämlich der Schwiegersohn Schechanjas, des Sohnes Arachs, und sein Sohn Johanan hatte die Tochter Meschullams, des Sohnes Berechjas, geheiratet." },
      { number: 19, text: "Auch rühmten sie vor mir seine Verdienste und trugen ihm zu, was ich sagte. Tobija schickte auch Briefe, um mir Furcht zu machen." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Als die Mauer gebaut war und ich die Torflügel hatte einsetzen lassen, wurden die Torwächter bestellt.", heading: "Ordnungen und Reformen: 7,1 - 13,31 | Die Fertigstellung der Mauer: 7,1-3", footnotes: ["H fügt nach den Torwächtern noch «und die Sänger und Leviten» an, ein unpassender späterer Zusatz."] },
      { number: 2, text: "Zu Befehlshabern über Jerusalem ernannte ich meinen Bruder Hanani und den Burghauptmann Hananja; denn dieser war ein zuverlässiger und gottesfürchtiger Mann, wie es nicht viele gab." },
      { number: 3, text: "Ich sagte zu ihnen: Die Tore Jerusalems dürfen erst geöffnet werden, wenn die Sonne heiß scheint; und noch während sie am Himmel steht, soll man die Tore schließen und verriegeln. Auch soll man Wachen aus den Einwohnern Jerusalems bilden; jeder soll eine bestimmte Zeit des Wachdienstes haben, und zwar jeweils vor seinem Haus." },
      { number: 4, text: "Nun war die Stadt weit ausgedehnt und groß; es war aber wenig Volk darin und es gab nicht viele Häuser, die wieder aufgebaut waren.", heading: "Das Verzeichnis der Laienbevölkerung: 7,4-38" },
      { number: 5, text: "Da gab mir mein Gott in den Sinn, die Vornehmen, die Beamten und das Volk zusammenzurufen, um sie nach Familien in Listen einzutragen. Ich fand das Familienverzeichnis derer, die zuerst heimgekehrt waren; darin fand ich geschrieben:" },
      { number: 6, text: "Hier folgt eine Liste der Einwohner der Provinz Juda, der Verschleppten, die aus dem Exil heimgekehrt sind. Nebukadnezzar, der König von Babel, hatte sie verschleppt; nun kehrten sie nach Jerusalem und Juda zurück, jeder in seine Stadt.", footnotes: ["(6-38) Esra 2,1-35"] },
      { number: 7, text: "Sie kamen mit Serubbabel, Jeschua, Nehemja, Asarja, Raamja, Nahamani, Mordochai, Bilschan, Misperet, Bigwai, Rehum und Baana. Das ist die Zahl der Männer des Volkes Israel:" },
      { number: 8, text: "Nachkommen des Parosch 2172." },
      { number: 9, text: "Nachkommen Schefatjas 372." },
      { number: 10, text: "Nachkommen Arachs 652." },
      { number: 11, text: "Nachkommen Pahat-Moabs, und zwar Nachkommen Jeschuas und Joabs, 2818." },
      { number: 12, text: "Nachkommen Elams 1254." },
      { number: 13, text: "Nachkommen Sattus 845." },
      { number: 14, text: "Nachkommen Sakkais 760." },
      { number: 15, text: "Nachkommen Binnuis 648." },
      { number: 16, text: "Nachkommen Bebais 628." },
      { number: 17, text: "Nachkommen Asgads 2322." },
      { number: 18, text: "Nachkommen Adonikams 667." },
      { number: 19, text: "Nachkommen Bigwais 2067." },
      { number: 20, text: "Nachkommen Adins 655." },
      { number: 21, text: "Nachkommen Aters der Linie Hiskija 98." },
      { number: 22, text: "Nachkommen Haschums 328." },
      { number: 23, text: "Nachkommen Bezais 324." },
      { number: 24, text: "Nachkommen Harifs 112." },
      { number: 25, text: "Nachkommen Gibeons 95." },
      { number: 26, text: "Männer von Betlehem und Netofa 188." },
      { number: 27, text: "Männer von Anatot 128." },
      { number: 28, text: "Männer von Bet-Asmawet 42." },
      { number: 29, text: "Männer von Kirjat-Jearim, Kefira und Beerot 743." },
      { number: 30, text: "Männer von Rama und Geba 621." },
      { number: 31, text: "Männer von Michmas 122." },
      { number: 32, text: "Männer von Bet-El und Ai 123." },
      { number: 33, text: "Männer von dem andern Nebo 52." },
      { number: 34, text: "Nachkommen des andern Elam 1254." },
      { number: 35, text: "Nachkommen Harims 320." },
      { number: 36, text: "Männer von Jericho 345." },
      { number: 37, text: "Männer von Lod, Hadid und Ono 721." },
      { number: 38, text: "Nachkommen Senaas 3930." },
      { number: 39, text: "Von den Priestern: Nachkommen Jedajas vom Haus Jeschua 973.", heading: "Das Verzeichnis der Priester und Leviten: 7,39-65", footnotes: ["(39-65) Esra 2,36-63"] },
      { number: 40, text: "Nachkommen Immers 1052." },
      { number: 41, text: "Nachkommen Paschhurs 1247." },
      { number: 42, text: "Nachkommen Harims 1017." },
      { number: 43, text: "Von den Leviten: Nachkommen Jeschuas, nämlich Kadmiël, Binnui und Hodawja, 74." },
      { number: 44, text: "Von den Sängern: Nachkommen Asafs 148." },
      { number: 45, text: "Von den Torwächtern: Nachkommen Schallums, Nachkommen Aters, Nachkommen Talmons, Nachkommen Akkubs, Nachkommen Hatitas, Nachkommen Schobais, zusammen 138." },
      { number: 46, text: "Von den Tempeldienern: Nachkommen Zihas, Nachkommen Hasufas, Nachkommen Tabbaots," },
      { number: 47, text: "Nachkommen Keros', Nachkommen Sias, Nachkommen Padons," },
      { number: 48, text: "Nachkommen Lebanas, Nachkommen Hagabas, Nachkommen Salmais," },
      { number: 49, text: "Nachkommen Hanans, Nachkommen Giddels, Nachkommen Gahars," },
      { number: 50, text: "Nachkommen Reajas, Nachkommen Rezins, Nachkommen Nekodas," },
      { number: 51, text: "Nachkommen Gasams, Nachkommen Usas, Nachkommen Paseachs," },
      { number: 52, text: "Nachkommen Besais, Nachkommen der Mëuniter, Nachkommen der Nefusiter," },
      { number: 53, text: "Nachkommen Bakbuks, Nachkommen Hakufas, Nachkommen Harhurs," },
      { number: 54, text: "Nachkommen Bazluts, Nachkommen Mehidas, Nachkommen Harschas," },
      { number: 55, text: "Nachkommen Barkos', Nachkommen Siseras, Nachkommen Temachs," },
      { number: 56, text: "Nachkommen Neziachs und Nachkommen Hatifas." },
      { number: 57, text: "Von den Nachkommen der Knechte Salomos: Nachkommen Sotais, Nachkommen Soferets, Nachkommen Perudas," },
      { number: 58, text: "Nachkommen Jaalas, Nachkommen Darkons, Nachkommen Giddels," },
      { number: 59, text: "Nachkommen Schefatjas, Nachkommen Hattils, Nachkommen Pocheret-Zebajims und Nachkommen Amons." },
      { number: 60, text: "Zusammen waren es 392 Tempeldiener und Nachkommen der Knechte Salomos." },
      { number: 61, text: "Die Folgenden sind aus Tel-Melach, Tel-Harscha, Kerub-Addon und Immer gekommen, konnten aber nicht angeben, ob sie nach Großfamilie und Abkunft überhaupt aus Israel stammten:" },
      { number: 62, text: "Nachkommen Delajas, Nachkommen Tobijas und Nachkommen Nekodas, insgesamt 642;" },
      { number: 63, text: "dazu bei den Priestern die Nachkommen Habajas, die Nachkommen des Koz und die Nachkommen Barsillais; dieser hatte eine von den Töchtern Barsillais aus Gilead geheiratet und dessen Namen angenommen." },
      { number: 64, text: "Die Genannten suchten ihre Eintragung im Geschlechterverzeichnis; aber man fand sie nicht; deshalb wurden sie aus dem Priesterstand ausgeschlossen;" },
      { number: 65, text: "der Statthalter untersagte ihnen, vom Hochheiligen zu essen, bis der Priester mit den Urim und Tummim auftrete.", footnotes: ["Lev 6,9-11; Ex 28,30"] },
      { number: 66, text: "Die ganze Volksgemeinde zählte insgesamt 42360 Personen.", heading: "Die Volkszählung: 7,66-72", footnotes: ["(66-72) Esra 2,64-70"] },
      { number: 67, text: "Dabei waren ihre Knechte und Mägde nicht mitgerechnet; es waren 7337 Personen. Dazu kamen 245 Sänger und Sängerinnen; und sie hatten 736 Pferde und 245 Maultiere", footnotes: ["Die Angaben über Pferde und Maultiere sind nach Esra 2,66 ergänzt."] },
      { number: 68, text: "sowie 435 Kamele und 6720 Esel." },
      { number: 69, text: "Einige von den Familienoberhäuptern brachten Spenden für das Werk. Der Statthalter spendete für den Bauschatz 1000 Golddariken, 50 Schalen, 30 Priesterkleider und 500 (Minen Silber)." },
      { number: 70, text: "Von den Familienoberhäuptern spendeten einige für den Bauschatz 20000 Golddariken und 2200 Minen Silber." },
      { number: 71, text: "Was das übrige Volk spendete, betrug 20000 Golddariken, 2000 Minen Silber und 67 Priesterkleider." },
      { number: 72, text: "Die Priester, Leviten, Torwächter und Sänger, auch ein Teil des Volkes und die Tempeldiener, ließen sich in ihren Städten nieder, und ebenso alle übrigen Israeliten in ihren Städten. Als der siebte Monat herankam, waren die Israeliten bereits in ihren Städten.", footnotes: ["in ihren Städten: ergänzt aus Esra 2,70."] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Das ganze Volk versammelte sich geschlossen auf dem Platz vor dem Wassertor und bat den Schriftgelehrten Esra, das Buch mit dem Gesetz des Mose zu holen, das der Herr den Israeliten vorgeschrieben hat.", heading: "Die Unterweisung im Gesetz: 8,1-12", footnotes: ["Esra 3,1; 7,6"] },
      { number: 2, text: "Am ersten Tag des siebten Monats brachte der Priester Esra das Gesetz vor die Versammlung; zu ihr gehörten die Männer und die Frauen und alle, die das Gesetz verstehen konnten.", footnotes: ["und alle, die das Gesetz verstehen konnten: die Kinder, die schon das Alter der Vernunft erreicht hatten."] },
      { number: 3, text: "Vom frühen Morgen bis zum Mittag las Esra auf dem Platz vor dem Wassertor den Männern und Frauen und denen, die es verstehen konnten, das Gesetz vor. Das ganze Volk lauschte auf das Buch des Gesetzes." },
      { number: 4, text: "Der Schriftgelehrte Esra stand auf einer Kanzel aus Holz, die man eigens dafür errichtet hatte. Neben ihm standen rechts Mattitja, Schema, Anaja, Urija, Hilkija und Maaseja und links Pedaja, Mischaël, Malkija, Haschum, Haschbaddana, Secharja und Meschullam." },
      { number: 5, text: "Esra öffnete das Buch vor aller Augen, denn er stand höher als das versammelte Volk. Als er das Buch aufschlug, erhoben sich alle." },
      { number: 6, text: "Dann pries Esra den Herrn, den großen Gott; darauf antworteten alle mit erhobenen Händen: Amen, amen! Sie verneigten sich, warfen sich vor dem Herrn nieder, mit dem Gesicht zur Erde.", footnotes: ["5,13; 1 Chr 16,36"] },
      { number: 7, text: "Die Leviten Jeschua, Bani, Scherebja, Jamin, Akkub, Schabbetai, Hodija, Maaseja, Kelita, Asarja, Josabad, Hanan und Pelaja erklärten dem Volk das Gesetz; die Leute blieben auf ihrem Platz." },
      { number: 8, text: "Man las aus dem Buch, dem Gesetz Gottes, in Abschnitten vor und gab dazu Erklärungen, sodass die Leute das Vorgelesene verstehen konnten." },
      { number: 9, text: "Der Statthalter Nehemia, der Priester und Schriftgelehrte Esra und die Leviten, die das Volk unterwiesen, sagten dann zum ganzen Volk: Heute ist ein heiliger Tag zu Ehren des Herrn, eures Gottes. Seid nicht traurig und weint nicht! Alle Leute weinten nämlich, als sie die Worte des Gesetzes hörten." },
      { number: 10, text: "Dann sagte Esra zu ihnen: Nun geht, haltet ein festliches Mahl und trinkt süßen Wein! Schickt auch denen etwas, die selbst nichts haben; denn heute ist ein heiliger Tag zur Ehre des Herrn. Macht euch keine Sorgen; denn die Freude am Herrn ist eure Stärke.", footnotes: ["Dtn 14,29; 26,12"] },
      { number: 11, text: "Auch die Leviten beruhigten das ganze Volk und sagten: Seid still, denn dieser Tag ist heilig. Macht euch keine Sorgen!" },
      { number: 12, text: "Da gingen alle Leute nach Hause, um zu essen und zu trinken und auch andern davon zu geben und um ein großes Freudenfest zu begehen; denn sie hatten die Worte verstanden, die man ihnen verkündet hatte." },
      { number: 13, text: "Am zweiten Tag versammelten sich die Familienoberhäupter des ganzen Volkes sowie die Priester und Leviten bei dem Schriftgelehrten Esra, um die Worte des Gesetzes weiter kennen zu lernen.", heading: "Die Feier des Laubhüttenfestes: 8,13-18" },
      { number: 14, text: "Da fanden sie im Gesetz, das der Herr durch Mose geboten hat, die Stelle, an der es heißt: Die Israeliten sollen während des Festes im siebten Monat in Laubhütten wohnen.", footnotes: ["Lev 23,40-42"] },
      { number: 15, text: "Wie man sie unterrichtet hatte, ließen sie nun in all ihren Städten und in Jerusalem ausrufen: Geht in die Berge und holt Zweige von veredelten und von wilden Ölbäumen, Zweige von Myrten, Palmen und Laubbäumen zum Bau von Laubhütten, wie es vorgeschrieben ist." },
      { number: 16, text: "Da ging das Volk hinaus; man holte Zweige und baute sich Laubhütten, der eine auf seinem flachen Dach, andere in ihren Höfen, in den Vorhöfen des Gotteshauses, auf dem Platz am Wassertor und auf dem Platz am Efraimtor." },
      { number: 17, text: "Die ganze Gemeinde, alle, die aus der Gefangenschaft heimgekehrt waren, bauten Laubhütten und wohnten darin. So hatten die Israeliten es nicht mehr gehalten seit den Tagen Josuas, des Sohnes Nuns, bis zu diesem Tag, und die Freude war überaus groß." },
      { number: 18, text: "Jeden Tag las Esra aus dem Buch des Gesetzes Gottes vor, vom ersten Tag bis zum letzten. So feierte man das Fest sieben Tage lang; am achten Tag war, wie vorgeschrieben, die Festversammlung.", footnotes: ["Lev 23,36"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Am vierundzwanzigsten Tag dieses Monats kamen die Israeliten zu einem Fasten zusammen, in Bußgewänder gehüllt und das Haupt mit Staub bedeckt.", heading: "Der Bußgottesdienst: 9,1-5" },
      { number: 2, text: "Die, die ihrer Abstammung nach Israeliten waren, sonderten sich von allen Fremden ab; sie traten vor und bekannten ihre Sünden und die Vergehen ihrer Väter.", footnotes: ["Esra 10,11f"] },
      { number: 3, text: "Sie erhoben sich von ihren Plätzen und man las drei Stunden lang aus dem Buch des Gesetzes des Herrn, ihres Gottes, vor. Dann bekannten sie drei Stunden lang ihre Schuld und warfen sich vor dem Herrn, ihrem Gott, nieder.", footnotes: ["drei Stunden lang, wörtlich: ein Viertel des Tags."] },
      { number: 4, text: "Auf der Tribüne der Leviten erhoben sich Jeschua, Bani, Kadmiël, Schebanja, Bunni, Scherebja, Bani und Kenani und riefen laut zum Herrn, ihrem Gott." },
      { number: 5, text: "Die Leviten Jeschua, Kadmiël, Bani, Haschabneja, Scherebja, Hodija, Schebanja und Petachja sagten: Erhebt euch und preist den Herrn, euren Gott, von Ewigkeit zu Ewigkeit! Man preise deinen herrlichen Namen, obwohl er erhaben ist über allen Preis und Ruhm.", footnotes: ["Dan 3,26"] },
      { number: 6, text: "Du, Herr, bist der Einzige. Du hast den Himmel geschaffen und den Himmel der Himmel und sein ganzes Heer, die Erde und alles, was auf ihr ist, die Meere und alles, was darin lebt. Ihnen allen gibst du das Leben. Das Heer des Himmels betet dich an.", heading: "Das Bußgebet: 9,6-37", footnotes: ["Dtn 6,4; 10,14", "«Der Himmel der Himmel» ist nach dem damaligen Weltbild der oberste Himmel."] },
      { number: 7, text: "Du, Herr, bist der Gott, der Abraham auserwählt hat. Du hast ihn aus Ur in Chaldäa herausgeführt und ihm den Namen Abraham verliehen.", footnotes: ["Gen 11,31; 12,1-3"] },
      { number: 8, text: "Du hast sein Herz getreu befunden; deshalb hast du mit ihm den Bund geschlossen (und ihm versprochen), seinen Nachkommen das Land der Kanaaniter, Hetiter, Amoriter, Perisiter, Jebusiter und Girgaschiter zu geben, und du hast dein Wort gehalten, denn du bist gerecht.", footnotes: ["Gen 15,18-21"] },
      { number: 9, text: "Du hast das Elend unserer Väter in Ägypten gesehen und du hast ihren Notschrei am Schilfmeer gehört.", footnotes: ["Ex 3,7"] },
      { number: 10, text: "Du hast Zeichen und Wunder getan am Pharao, an all seinen Dienern und am ganzen Volk in seinem Land; denn du wusstest, dass sie mit Israel ihren Übermut getrieben hatten. So hast du dir einen Namen gemacht, der gerühmt wird bis zum heutigen Tag.", footnotes: ["Ex 18,11; Dtn 6,22"] },
      { number: 11, text: "Du hast das Meer vor ihnen zerteilt und sie schritten auf trockenem Boden mitten durchs Meer; doch ihre Verfolger hast du in die Tiefe gestürzt wie einen Stein, der in reißendem Wasser versinkt.", footnotes: ["Ex 14f"] },
      { number: 12, text: "Durch eine Wolkensäule hast du sie bei Tag geleitet und durch eine Feuersäule bei Nacht, um ihnen den Weg zu erhellen, den sie gehen sollten.", footnotes: ["Ex 13,21f"] },
      { number: 13, text: "Du bist auf den Berg Sinai herabgestiegen und hast vom Himmel her mit ihnen gesprochen; du hast ihnen klare Ordnungen und zuverlässige Gesetze gegeben, gute Satzungen und Gebote.", footnotes: ["Ex 19,18"] },
      { number: 14, text: "Deinen heiligen Sabbat hast du ihnen bekannt gemacht und hast ihnen durch deinen Diener Mose Gebote, Satzungen und Anweisungen gegeben.", footnotes: ["Ex 20,8-11"] },
      { number: 15, text: "Du hast ihnen Brot vom Himmel gegeben, als sie Hunger hatten, und hast Wasser aus dem Felsen sprudeln lassen, als sie Durst litten. Endlich hast du ihnen befohlen, in das Land, das du ihnen unter einem Eid zugesagt hattest, hineinzuziehen und es in Besitz zu nehmen.", footnotes: ["Ex 16,4; 17,6"] },
      { number: 16, text: "Unsere Väter aber wurden hochmütig; sie waren trotzig und hörten nicht auf seine Gebote.", footnotes: ["Num 14,1-4"] },
      { number: 17, text: "Sie weigerten sich zu gehorchen und dachten nicht mehr an die Wunder, die du an ihnen getan hattest. Hartnäckig setzten sie sich in den Kopf, als Sklaven nach Ägypten zurückzukehren. Doch du bist ein Gott, der verzeiht, du bist gnädig und barmherzig, langmütig und reich an Huld; darum hast du sie nicht verlassen.", footnotes: ["Ex 34,6", "Übersetzung nach G."] },
      { number: 18, text: "Sie machten sich sogar ein gegossenes Kalb und sagten: Das ist dein Gott, der dich aus Ägypten herausgeführt hat!, und sie verübten schwere Frevel.", footnotes: ["Ex 32,4"] },
      { number: 19, text: "Du aber hast sie in deinem großen Erbarmen nicht in der Wüste verlassen. Die Wolkensäule wich nicht von ihnen bei Tag, sondern führte sie auf ihrem Weg; ebenso erhellte die Feuersäule bei Nacht den Weg, den sie gehen sollten." },
      { number: 20, text: "Du gabst ihnen deinen guten Geist, um sie zur Einsicht zu bringen. Du entzogst ihnen dein Manna nicht und gabst ihnen Wasser für ihren Durst." },
      { number: 21, text: "Vierzig Jahre lang hast du für sie in der Wüste gesorgt. Sie litten keinen Mangel; ihre Kleider zerfielen nicht, ihre Füße schwollen nicht an.", footnotes: ["Dtn 8,4"] },
      { number: 22, text: "Du hast ihnen ganze Reiche und Völker ausgeliefert und als Randgebiet zugeteilt. So nahmen sie das Land des Königs Sihon von Heschbon in Besitz und das Land des Königs Og des Baschan.", footnotes: ["Num 21,21-35"] },
      { number: 23, text: "Du hast ihre Nachkommen so zahlreich gemacht wie die Sterne am Himmel und hast sie in das Land geführt, von dem du ihren Vätern versprochen hattest, sie würden dort hineinziehen und es in Besitz nehmen.", footnotes: ["Dtn 1,10; Jos 12"] },
      { number: 24, text: "Und die Nachkommen zogen in das Land und nahmen es in Besitz. Du hast ihnen die Kanaaniter, die Bewohner des Landes, unterworfen. Du gabst sie in ihre Gewalt, die Völker des Landes samt ihren Königen, und sie durften mit ihnen machen, was sie wollten." },
      { number: 25, text: "Sie eroberten befestigte Städte und fruchtbares Ackerland. Häuser mit all ihrem Reichtum nahmen sie in Besitz, ausgehauene Zisternen, Weinberge, Ölbäume und Obstbäume in Menge. Sie aßen sich satt, wurden fett und lebten gut von deinen reichen Gaben.", footnotes: ["Dtn 6,10f; 32,15"] },
      { number: 26, text: "Dann aber wurden sie trotzig; sie empörten sich gegen dich und kehrten deinem Gesetz den Rücken. Deine Propheten warnten sie zwar und wollten sie zu dir zurückführen; doch man tötete sie und verübte schwere Frevel." },
      { number: 27, text: "Da gabst du unsere Väter in die Gewalt ihrer Feinde, die sie hart bedrängten. Wenn sie dann bedrängt wurden, schrien sie zu dir und du erhörtest sie im Himmel. In deinem großen Erbarmen schicktest du ihnen Retter, die sie aus der Gewalt ihrer Feinde befreiten.", footnotes: ["Ri 2,14-19; 3,9"] },
      { number: 28, text: "Doch sobald sie Ruhe hatten, taten sie wieder Dinge, die dir missfielen. Da liefertest du sie wieder der Gewalt ihrer Feinde aus, die sie unterdrückten. Nun schrien sie wieder zu dir und du erhörtest sie im Himmel; oft hast du sie befreit in deinem großen Erbarmen.", footnotes: ["Ps 106,43"] },
      { number: 29, text: "Du warntest sie, um sie zu deinem Gesetz zurückzuführen. Sie aber waren stolz; sie hörten nicht auf deine Gebote und versündigten sich gegen deine Vorschriften; und doch lebt durch sie der Mensch, der sie befolgt. Sie kehrten dir trotzig den Rücken zu, waren starrsinnig und gehorchten dir nicht.", footnotes: ["Sach 7,11f; Lev 18,5"] },
      { number: 30, text: "Viele Jahre hast du mit ihnen Geduld gehabt, hast sie gewarnt durch deinen Geist, durch deine Propheten; doch sie hörten nicht. Da gabst du sie in die Gewalt der benachbarten Völker.", footnotes: ["Jer 7,25f; 44,4-6", "Wörtlich: Da gabst du sie in die Gewalt der Völker der (anderen) Länder. Gemeint sind die heidnischen Nachbarn Israels (vgl. 10,29; Esra 3,3)."] },
      { number: 31, text: "In deinem großen Erbarmen hast du sie aber nicht ausgerottet; du hast sie nicht verlassen, denn du bist ein gnädiger und barmherziger Gott." },
      { number: 32, text: "Und jetzt, unser Gott, du großer, starker, furchtgebietender Gott, der den Bund hält und uns seine Gnade bewahrt: Achte nicht gering all die Mühsal, die uns getroffen hat, unsere Könige und Vorsteher, unsere Priester und Propheten, unsere Väter und dein ganzes Volk seit den Tagen der Könige von Assur bis heute.", footnotes: ["2 Makk 1,24; Bar 2,11; Dan 9,4"] },
      { number: 33, text: "Du warst gerecht bei allem, was über uns gekommen ist. Du hast uns deine Treue bewiesen; wir aber haben gesündigt." },
      { number: 34, text: "Unsere Könige, Vorsteher und Priester und unsere Väter befolgten dein Gesetz nicht; sie missachteten deine Gebote und die Warnungen, die du an sie gerichtet hast." },
      { number: 35, text: "Sie lebten in ihrem eigenen Königreich, in der Fülle des Reichtums, den du ihnen gewährt hast, in dem weiten, fruchtbaren Land, das du vor sie hingebreitet hast; sie aber haben dir trotzdem nicht gedient und sich nicht von ihrem bösen Treiben abgewandt." },
      { number: 36, text: "Darum sind wir heute Knechte. Du hast unseren Vätern dieses Land gegeben, damit sie seine Früchte und seinen Reichtum genießen; wir aber leben darin als Knechte." },
      { number: 37, text: "Sein reicher Ertrag geht an die Könige, die du wegen unserer Sünden über uns gesetzt hast. Sie verfügen über uns selbst und unser Vieh nach ihrem Belieben. Darum sind wir in großer Not.", footnotes: ["Dtn 28,31; Jes 1,7; Klgl 5,4.13-15"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Wegen all dem schließen wir nun einen Vertrag und schreiben ihn nieder. Auf der gesiegelten Urkunde stehen die Namen unserer Obersten, Leviten und Priester.", heading: "Die Verpflichtung auf das Gesetz: 10,1-40" },
      { number: 2, text: "Auf der gesiegelten Urkunde stehen: der Statthalter Nehemia, der Sohn Hachaljas, und Zidkija," },
      { number: 3, text: "Seraja, Asarja, Jirmeja," },
      { number: 4, text: "Paschhur, Armarja, Malkija," },
      { number: 5, text: "Hattusch, Schebanja, Malluch," },
      { number: 6, text: "Harim, Meremot, Obadja," },
      { number: 7, text: "Daniel, Ginneton, Baruch," },
      { number: 8, text: "Meschullam, Abija, Mijamin," },
      { number: 9, text: "Maasja, Bilga und Schemaja; das sind die Priester." },
      { number: 10, text: "Dann die Leviten: Jeschua, der Sohn Asanjas, Binnui, der Nachkomme Henadads, und Kadmiël," },
      { number: 11, text: "sodann ihre Brüder Schebanja, Hodija, Kelita, Pelaja, Hanan," },
      { number: 12, text: "Micha, Rehob, Haschabja," },
      { number: 13, text: "Sakkur, Scherebja, Schebanja," },
      { number: 14, text: "Hodija, Bani und Beninu." },
      { number: 15, text: "Dann folgen die Oberhäupter des Volkes: Parosch, Pahat-Moab, Elam, Sattu, Bani," },
      { number: 16, text: "Bunni, Asgad, Bebai," },
      { number: 17, text: "Adonija, Bigwai, Adin," },
      { number: 18, text: "Ater, Hiskija, Asur," },
      { number: 19, text: "Hodija, Haschum, Bezai," },
      { number: 20, text: "Harif, Anatot, Nebai," },
      { number: 21, text: "Magpiasch, Meschullam, Hesir," },
      { number: 22, text: "Meschesabel, Zadok, Jaddua," },
      { number: 23, text: "Pelatja, Hanan, Anaja," },
      { number: 24, text: "Hoschea, Hananja, Haschub," },
      { number: 25, text: "Lohesch, Pilha, Schobek," },
      { number: 26, text: "Rehum, Haschabna, Maaseja," },
      { number: 27, text: "Ahija, Hanan, Anan," },
      { number: 28, text: "Malluch, Harim und Baana." },
      { number: 29, text: "Die übrigen vom Volk, von den Priestern und Leviten, die Torwächter, Sänger und Tempeldiener, alle, die sich von den Völkern der anderen Länder abgesondert und dem Gesetz Gottes zugewandt haben, ihre Frauen, Söhne und Töchter, alle, die das nötige Verständnis besitzen:", footnotes: ["Esra 6,21; Neh 8,2f"] },
      { number: 30, text: "Sie schließen sich ihren führenden Brüdern an; sie verpflichten sich unter Eid und Schwur, das Gesetz Gottes zu befolgen, das durch Mose, den Diener Gottes, gegeben wurde, und alle Gebote des Herrn, unseres Gottes, seine Vorschriften und Satzungen zu beachten und zu erfüllen." },
      { number: 31, text: "Wir werden unsere Töchter nicht den Völkern im Land zu Frauen geben, noch ihre Töchter für unsere Söhne nehmen.", footnotes: ["Esra 9,1f; 10,2-11; Ex 34,16; Dtn 7,1-3", "Die «Völker im Land» sind die heidnischen Mitbewohner und Nachbarn Israels (vgl. Esra 9,1f)."] },
      { number: 32, text: "Wenn die Völker des Landes Waren, besonders Getreide jeder Art, am Sabbat zum Verkauf anbieten, werden wir ihnen am Sabbat oder an einem anderen heiligen Tag nichts abnehmen. Wir verzichten in jedem siebten Jahr auf den Ertrag des Bodens und auf jede Schuldforderung.", footnotes: ["Jer 17,19-22; Neh 13,15-22; Ex 23,10f; Dtn 15,1-3"] },
      { number: 33, text: "Ferner übernehmen wir die Pflicht, jährlich ein Drittelschekel für den Dienst im Haus unseres Gottes zu geben,", footnotes: ["Ex 30,13; Mt 17,24", "Ein Drittelschekel ist eine Silbermünze."] },
      { number: 34, text: "für die Schaubrote, das tägliche Speiseopfer und das tägliche Brandopfer, für die Opfer an den Sabbaten, Neumondtagen und Festen, für die Weiheopfer und die Sündopfer, durch die man Israel Sühne verschafft, und für alle Arbeiten im Haus unseres Gottes." },
      { number: 35, text: "Die Lieferung des Brennholzes haben wir ausgelost unter den Priestern, den Leviten und dem Volk. Jede Familie soll es jährlich zu der für sie bestimmten Zeit zum Haus unseres Gottes bringen. Es soll auf dem Altar des Herrn, unseres Gottes, brennen, wie es im Gesetz vorgeschrieben ist.", footnotes: ["13,31; Lev 6,5"] },
      { number: 36, text: "Ferner bringen wir jährlich zum Haus des Herrn die ersten Erträge unserer Felder und die ersten Erträge aller Baumfrüchte,", footnotes: ["Num 18,12-32"] },
      { number: 37, text: "unsere erstgeborenen Söhne und die ersten Jungen unseres Viehs, wie es im Gesetz vorgeschrieben ist. Die ersten Jungen unserer Rinder und Schafe bringen wir zum Haus unseres Gottes für die Priester, die im Haus unseres Gottes Dienst tun.", footnotes: ["Ex 13,2.13"] },
      { number: 38, text: "Den Erstanteil von unserem Brotteig und von den Früchten aller Bäume sowie von Wein und Öl bringen wir für die Priester in die Kammern des Hauses unseres Gottes. Den Leviten geben wir den Zehnten vom Ertrag unseres Bodens. Die Leviten selbst erheben den Zehnten an allen Orten, wo wir das Feld bebauen.", footnotes: ["Num 15,20f; Dtn 14,22; Num 18,26-32"] },
      { number: 39, text: "Wenn die Leviten den Zehnten erheben, soll ein Priester, ein Nachkomme Aarons, die Leviten begleiten. Die Leviten bringen dann den Zehnten vom Zehnten zum Haus unseres Gottes in die Vorratskammern. -" },
      { number: 40, text: "In diese Kammern nämlich bringen die Israeliten und die Leviten die Abgaben an Getreide, Wein und Öl. Dort sind die Vorratsbehälter des Heiligtums, der Priester, die Dienst tun, der Torwächter und Sänger. - So werden wir das Haus unseres Gottes nicht im Stich lassen.", footnotes: ["Jer 40,10"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Die Obersten des Volkes ließen sich in Jerusalem nieder. Die übrigen Männer warfen das Los und veranlassten so jeden Zehnten, sich in der heiligen Stadt Jerusalem niederzulassen. Neun Zehntel blieben in den Landstädten.", heading: "Die Liste der Einwohner Jerusalems: 11,1-19", footnotes: ["7,4"] },
      { number: 2, text: "Das Volk aber segnete alle Männer, die sich bereit erklärten, in Jerusalem zu wohnen." },
      { number: 3, text: "Das sind die Oberhäupter des Bezirks, die in Jerusalem und in den Städten Judas wohnten. Alle wohnten in ihren Städten, jeder auf seinem Besitz: die Israeliten, die Priester, die Leviten, die Tempeldiener und die Nachkommen der Knechte Salomos.", footnotes: ["(3-19) 1 Chr 9,2-17"] },
      { number: 4, text: "In Jerusalem wohnten von den Angehörigen der Stämme Juda und Benjamin folgende Männer: von den Angehörigen des Stammes Juda: Ataja, der Sohn Usijas, des Sohnes Secharjas, des Sohnes Amarjas, des Sohnes Schefatjas, des Sohnes Mahalels, von den Nachkommen des Perez;" },
      { number: 5, text: "ferner Maaseja, der Sohn Baruchs, des Sohnes Kolhoses, des Sohnes Hasajas, des Sohnes Adajas, des Sohnes Jojaribs, des Sohnes Secharjas, von den Nachkommen Schelas." },
      { number: 6, text: "Die Gesamtzahl der Nachkommen des Perez, die in Jerusalem wohnten, betrug 468 kriegstüchtige Männer." },
      { number: 7, text: "Von den Angehörigen des Stammes Benjamin waren es folgende Männer: Sallu, der Sohn Mechullams, des Sohnes Joëds, des Sohnes Pedajas, des Sohnes Kolajas, des Sohnes Maasejas, des Sohnes Itiëls, des Sohnes Jeschajas," },
      { number: 8, text: "und seine Brüder, insgesamt 928 kriegstüchtige Männer.", footnotes: ["Text korr."] },
      { number: 9, text: "Ihr Vorsteher war Joël, der Sohn Sichris; Juda, der Sohn Senuas, stand der Stadt als zweiter vor." },
      { number: 10, text: "Von den Priestern: Jedaja, Jojarib, Jachin;", footnotes: ["(10-11) 1 Chr 9,10f"] },
      { number: 11, text: "Seraja, der Sohn Hilkijas, des Sohnes Meschullams, des Sohnes Zadoks, des Sohnes Merajots, des Sohnes Ahitubs, der Fürst des Hauses Gottes;" },
      { number: 12, text: "dazu ihre Brüder, die den Dienst im Tempel versahen, insgesamt 822 Männer; ferner Adaja, der Sohn Jerohams, des Sohnes Pelaljas, des Sohnes Amzis, des Sohnes Secharjas, des Sohnes Paschhurs, des Sohnes Malkijas," },
      { number: 13, text: "und seine Brüder, 242 Familienoberhäupter; weiter Amaschsai, der Sohn Asarels, des Sohnes Achsais, des Sohnes Meschillemots, des Sohnes Immers," },
      { number: 14, text: "und seine Brüder, 128 kriegstüchtige Männer. Ihr Vorsteher war Sabdiël, der Sohn Haggedolims." },
      { number: 15, text: "Von den Leviten: Schemaja, der Sohn Haschubs, des Sohnes Asrikams, des Sohnes Haschabjas, des Sohnes Bunnis." },
      { number: 16, text: "Von den Oberhäuptern der Leviten: Schabbetai und Josabad, die dem äußeren Dienst des Hauses Gottes vorstanden." },
      { number: 17, text: "Mattanja, der Sohn Michas, des Sohnes Sabdis, des Sohnes Asafs, der den Lobgesang leitete und beim Gebet das Danklied anstimmte; Bakbukja als zweiter unter seinen Brüdern; ferner Abda, der Sohn Schammuas, des Sohnes Galals, des Sohnes Jedutuns." },
      { number: 18, text: "Insgesamt wohnten 284 Leviten in der Heiligen Stadt." },
      { number: 19, text: "Dazu die Torwächter: Akkub, Talmon und ihre Brüder, die an den Toren Wache hielten, insgesamt 172 Männer." },
      { number: 21, text: "Die Tempeldiener wohnten auf dem Ofel. Ziha und Gischpa standen ihnen vor.", heading: "Die Ansiedlungsbestimmungen: 11,20-36", footnotes: ["VV. 21-24 passen besser vor V. 20."] },
      { number: 22, text: "Vorsteher der Leviten in Jerusalem war Usi, der Sohn Banis, des Sohnes Haschabjas, des Sohnes Mattanjas, des Sohnes Michas, einer der Nachkommen Asafs, die nach der Dienstordnung des Hauses Gottes zu singen hatten." },
      { number: 23, text: "Für sie galt nämlich eine königliche Vorschrift, die bestimmte, was die Sänger an den einzelnen Tagen zu singen hatten." },
      { number: 24, text: "Petachja, der Sohn Meschesabels, einer der Nachkommen Serachs, des Sohnes Judas, stand dem König in allem, was das Volk betraf, zur Seite." },
      { number: 20, text: "Die übrigen Israeliten, Priester und Leviten wohnten in den anderen Städten Judas, jeder auf seinem Besitztum," },
      { number: 25, text: "und in den Gehöften auf ihren Fluren. Angehörige des Stammes Juda wohnten in Kirjat-Arba und seinen Tochterstädten, in Dibon und seinen Tochterstädten, in Kabzeel und den dazugehörenden Gehöften," },
      { number: 26, text: "in Jeschua, Molada und Bet-Pelet," },
      { number: 27, text: "in Hazar-Schual sowie in Beerscheba und seinen Tochterstädten," },
      { number: 28, text: "in Ziklag sowie in Mechona und seinen Tochterstädten," },
      { number: 29, text: "in En-Rimmon, Zora, Jarmut," },
      { number: 30, text: "Sanoach, Adullam und den zu ihnen gehörenden Gehöften, in Lachisch und seiner Gemarkung sowie in Aseka und seinen Tochterstädten. Sie siedelten also von Beerscheba bis zum Hinnomtal." },
      { number: 31, text: "Angehörige des Sammes Benjamin wohnten in Geba, Michmas und Aja sowie in Bet-El und seinen Tochterstädten," },
      { number: 32, text: "ferner in Anatot, Nob, Ananeja," },
      { number: 33, text: "Hazor, Rama, Gittajim," },
      { number: 34, text: "Hadid, Zeboim, Neballat," },
      { number: 35, text: "Lod und Ono und im Tal der Werkleute." },
      { number: 36, text: "Von den Leviten kamen einige Abteilungen Judas zu Benjamin." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Folgende Priester und Leviten kamen herauf, zusammen mit Serubbabel, dem Sohn Schealtiëls, und Jeschua: Seraja, Jirmeja, Esra,", heading: "Die Listen von Priester- und Levitenfamilien: 12,1-26" },
      { number: 2, text: "Amarja, Malluch, Hattusch," },
      { number: 3, text: "Schechanja, Harim, Meremot," },
      { number: 4, text: "Iddo, Ginneton, Abija," },
      { number: 5, text: "Mijamin, Maadja, Bilga," },
      { number: 6, text: "Schemaja, Jojarib, Jedaja," },
      { number: 7, text: "Sallu, Amok, Hilkija und Jedaja. Das waren die Oberhäupter der Priester zur Zeit Jeschuas. Ferner ihre Brüder," },
      { number: 8, text: "die Leviten: Jeschua, Binnui, Kadmiël, Scherebja, Juda und Mattanja; ihm und seinen Brüdern oblag der Lobgesang." },
      { number: 9, text: "Ihnen gegenüber standen Bakbukja und Unni und ihre Brüder, so wie sie für den Dienst eingeteilt waren." },
      { number: 10, text: "Jeschua zeugte Jojakim, Jojakim zeugte Eljaschib und Eljaschib Jojada;" },
      { number: 11, text: "Jojada zeugte Jonatan und Jonatan zeugte Jaddua." },
      { number: 12, text: "In der Zeit Jojakims waren folgende Priester die Oberhäupter ihrer Familien: Meraja in der Familie Seraja, Hananja in der Familie Jirmeja," },
      { number: 13, text: "Meschullam in der Familie Esra, Johanan in der Familie Amarja," },
      { number: 14, text: "Jonatan in der Familie Malluch, Josef in der Familie Schebanja," },
      { number: 15, text: "Adna in der Familie Harim, Helkai in der Familie Meremot," },
      { number: 16, text: "Secharja in der Familie Iddo, Meschullam in der Familie Ginneton," },
      { number: 17, text: "Sichri in der Familie Abija, . . . in der Familie Mijamin, Piltai in der Familie Maadja,", footnotes: ["Der Name des Oberhaupts der Familie Mijamin ist ausgefallen"] },
      { number: 18, text: "Schammua in der Familie Bilga, Jonatan in der Familie Schemaja," },
      { number: 19, text: "Mattenai in der Familie Jojarib, Usi in der Familie Jedaja," },
      { number: 20, text: "Kallai in der Familie Sallu, Eber in der Familie Amok," },
      { number: 21, text: "Haschabja in der Familie Hilkija, Netanel in der Familie Jedaja." },
      { number: 22, text: "Die Oberhäupter der levitischen Priesterfamilien in der Zeit Eljaschibs, Jojadas, Johanans und Jadduas sind in der Chronik verzeichnet bis zur Regierung des Persers Darius." },
      { number: 23, text: "Von den Leviten sind die Oberhäupter der Familien in der Chronik verzeichnet bis zur Zeit Johanans, des Sohnes Eljaschibs." },
      { number: 24, text: "Die Oberhäupter der Leviten waren: Haschabja, Scherebja, Jeschua, Binnui, Kadmiël und ihre Brüder, die ihnen beim Lob- und Dankgesang gegenüberstanden, Abteilung für Abteilung, nach der Vorschrift des Gottesmannes David," },
      { number: 25, text: "nämlich Mattanja, Bakbukja und Obadja. Meschullam, Talmon und Akkub hielten als Torwächter die Wache an den Vorratskammern der Tore." },
      { number: 26, text: "Diese Leviten waren im Amt zur Zeit Jojakims, des Sohnes Jeschuas, des Sohnes Jozadaks, und zur Zeit des Statthalters Nehemia und des Priesters und Schriftgelehrten Esra." },
      { number: 27, text: "Bei der Einweihung der Mauer Jerusalems holte man die Leviten aus allen ihren Wohnsitzen nach Jerusalem, um mit frohen Lobgesängen und mit der Musik von Zimbeln, Harfen und Zithern die Einweihung zu vollziehen.", heading: "Die Einweihung der Mauer: 12,27-43", footnotes: ["Esra 3,10"] },
      { number: 28, text: "Da fanden sich die Sänger aus dem Umkreis von Jerusalem ein und aus den Gehöften der Leute von Netofa," },
      { number: 29, text: "aus Bet-Gilgal und den Gemarkungen von Geba und Asmawet; die Sänger hatten sich nämlich rings um Jerusalem Gehöfte gebaut." },
      { number: 30, text: "Die Priester und Leviten reinigten sich selbst; sie reinigten auch das Volk, die Tore und die Mauer.", footnotes: ["Esra 6,20"] },
      { number: 31, text: "Dann ließ ich die Vorsteher von Juda auf die Mauer steigen und stellte zwei große Festchöre auf. Der eine zog oben auf der Mauer nach rechts dem Aschentor zu.", footnotes: ["2,13-15; 3,1-3"] },
      { number: 32, text: "Ihm folgte Hoschaja mit der einen Hälfte der Vorsteher von Juda." },
      { number: 33, text: "Dann kamen Asarja, Esra, Meschullam," },
      { number: 34, text: "Juda, Benjamin, Schemaja und Jirmeja;" },
      { number: 35, text: "sie waren Priester und bliesen die Trompeten. Dann kamen Secharja, der Sohn Jonatans, des Sohnes Schemajas, des Sohnes Mattanjas, des Sohnes Michajas, des Sohnes Sakkurs, des Sohnes Asafs," },
      { number: 36, text: "und seine Brüder Schemaja, Asarel, Milalai, Gilalai, Maai, Netanel, Juda und Hanani mit den Musikinstrumenten des Gottesmannes David. Vor ihnen her schritt Esra, der Schriftgelehrte.", footnotes: ["2 Chr 29,25-27"] },
      { number: 37, text: "Sie zogen zum Quelltor, stiegen dann geradeaus die Stufen zur Davidstadt und den Aufgang zur Mauer hinauf und kamen am Palast Davids entlang zum Wassertor im Osten." },
      { number: 38, text: "Der zweite Festchor ging nach links - ihm folgte ich mit der andern Hälfte des Volkes -, oben auf der Mauer am Ofenturm vorbei zur Breiten Mauer," },
      { number: 39, text: "dann über das Efraimtor, das Jeschanator und das Fischtor, am Turm Hananel und am Tor der Hundert vorbei zum Schaftor; er machte Halt am Wachtor." },
      { number: 40, text: "Dann stellten sich die beiden Festchöre im Hof des Hauses Gottes auf, auch ich und die Hälfte der Beamten, die bei mir waren," },
      { number: 41, text: "sowie die Priester Eljakim, Maaseja, Mijamin, Michaja, Eljoënai, Secharja und Hananja mit den Trompeten" },
      { number: 42, text: "und (die Leviten) Maaseja, Schemaja, Eleasar, Usi, Johanan, Malkija, Elam und Eser. Nun ließen sich die Sänger hören; ihr Leiter war Jisrachja." },
      { number: 43, text: "An diesem Tag brachte man große Schlachtopfer dar und alle freuten sich; denn Gott hatte ihnen eine große Freude bereitet. Auch die Frauen und Kinder waren fröhlich und der Jubel in Jerusalem war weithin zu hören.", footnotes: ["Esra 6,17"] },
      { number: 44, text: "In jener Zeit wurden Männer bestellt zur Aufsicht über die Kammern, die für die Vorräte, Abgaben, Erstlingsfrüchte und Zehnten bestimmt waren. In diesen Kammern sammelte man von den Feldern der einzelnen Städte die Anteile, die den Priestern und Leviten gesetzlich zukamen. Denn die Juden waren den Priestern und Leviten, die im Dienst standen, wohlgesinnt.", heading: "Die Ordnung der heiligen Abgaben: 12,44-47", footnotes: ["10,39f; 13,10-18"] },
      { number: 45, text: "Sie versahen den Dienst (im Haus) ihres Gottes und vollzogen die Reinigungsriten; auch die Sänger und Torwächter taten Dienst nach den Vorschriften Davids und seines Sohnes Salomo.", footnotes: ["1 Chr 16,4f"] },
      { number: 46, text: "Denn schon früher, in der Zeit Davids und Asafs, gab es Vorsteher der Sänger, die zur Ehre Gottes Lob- und Danklieder vortrugen." },
      { number: 47, text: "Zur Zeit Serubbabels und zur Zeit Nehemias lieferte ganz Israel die Anteile für die Sänger und Torwächter ab, je nachdem es der tägliche Bedarf erforderte. Man gab den Leviten einen heiligen Anteil und die Leviten gaben davon den Söhnen Aarons einen heiligen Anteil." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Zu jener Zeit las man dem Volk aus dem Buch des Mose vor; da fand man die Stelle, an der steht: Ammoniter und Moabiter dürfen niemals in die Gemeinde Gottes eintreten;", heading: "Weitere Reformen Nehemias: 13,1-14", footnotes: ["Dtn 23,4-6"] },
      { number: 2, text: "denn sie sind den Israeliten einst nicht mit Brot und Wasser entgegengekommen. Moab hat gegen sie Bileam gedungen, der sie verfluchen sollte. Doch unser Gott verwandelte den Fluch in einen Segen." },
      { number: 3, text: "Als man dieses Gesetz gehört hatte, sonderte man aus Israel alle Mischvölker aus." },
      { number: 4, text: "Früher hatte der Priester Eljaschib die Kammern des Hauses unseres Gottes betreut; er war verwandt mit Tobija", footnotes: ["2,19; 12,44"] },
      { number: 5, text: "und hatte darum für ihn eine große Kammer einrichten lassen. Dort bewahrte man früher das Opfermehl und den Weihrauch auf sowie die Behälter und den Zehnten von Getreide, Wein und Öl, der den Leviten, Sängern und Torwächtern gesetzlich zukam, außerdem die Abgaben für die Priester." },
      { number: 6, text: "Bei all dem war ich aber nicht in Jerusalem gewesen; denn im zweiunddreißigsten Jahr des Artaxerxes, des Königs von Babel, war ich zum König gereist. Nach einiger Zeit erbat ich aber vom König (die Erlaubnis zur Abreise)" },
      { number: 7, text: "und kam wieder nach Jerusalem. Nun bemerkte ich die Untat, die Eljaschib zugunsten Tobijas begangen hatte, indem er ihm in den Vorhöfen des Hauses Gottes eine Kammer eingerichtet hatte.", footnotes: ["Mt 21,12"] },
      { number: 8, text: "Das missfiel mir sehr und ich warf den ganzen Hausrat Tobijas aus der Kammer hinaus." },
      { number: 9, text: "Dann ließ ich die Kammer reinigen und brachte die Behälter des Hauses Gottes, das Opfermehl und den Weihrauch wieder dorthin.", footnotes: ["12,30"] },
      { number: 10, text: "Ich erfuhr auch, dass die Anteile der Leviten nicht abgeliefert wurden; darum waren die Leviten und die Sänger, die den Dienst zu versehen hatten, weggegangen, jeder auf seinen Landbesitz.", footnotes: ["12,47"] },
      { number: 11, text: "Da machte ich den Beamten Vorwürfe und sagte: Warum wird das Haus Gottes vernachlässigt? Und ich holte die Leviten wieder zusammen und stellte sie auf ihren Posten." },
      { number: 12, text: "Nun brachte ganz Juda wieder den Zehnten von Getreide, Wein und Öl zu den Vorratskammern.", footnotes: ["10,38"] },
      { number: 13, text: "Zur Aufsicht über die Vorratskammern bestimmte ich den Priester Schelemja, den Schreiber Zadok und den Leviten Pedaja. Ihnen stellte ich Hanan, den Sohn Sakkurs, des Sohnes Mattanjas, an die Seite, denn sie galten als zuverlässig. Ihnen oblag es nun, den Zehnten an ihre Brüder zu verteilen." },
      { number: 14, text: "Denk dafür an mich, mein Gott, und lösch die guten Taten nicht aus, die ich für das Haus meines Gottes und seine Ordnung vollbracht habe.", footnotes: ["5,19"] },
      { number: 15, text: "Damals sah ich in Juda Leute, die am Sabbat die Kelter traten. Andere brachten Getreide ein und luden es auf Esel; auch Wein, Trauben, Feigen und Lasten aller Art brachten sie am Sabbattag nach Jerusalem. Ich verwarnte sie, weil sie an diesem Tag Lebensmittel verkauften.", heading: "Die Sabbatordnung: 13,15-22", footnotes: ["(15-16) 10,32; Jer 17,19-27"] },
      { number: 16, text: "Die Leute von Tyrus, die in Juda wohnten, brachten Fische und allerlei Waren und verkauften sie am Sabbat an die Juden, sogar in Jerusalem." },
      { number: 17, text: "Da machte ich den Vornehmen von Juda Vorwürfe und sagte zu ihnen: Wie könnt ihr eine solche Untat begehen und den Sabbat entweihen?" },
      { number: 18, text: "Haben das nicht schon eure Väter getan? Dafür ließ unser Gott all dieses Unheil über uns und diese Stadt kommen. Wollt ihr neuen Zorn über Israel bringen, indem ihr den Sabbat entweiht?" },
      { number: 19, text: "Ich ließ von da an vor dem Anbruch des Sabbats, wenn es in den Toren Jerusalems dunkel wurde, die Tore schließen und befahl, sie erst nach dem Sabbat wieder zu öffnen. Auch stellte ich einige meiner Leute an die Tore, damit am Sabbattag keine Lasten hereingebracht wurden.", footnotes: ["Der Sabbat beginnt mit dem Sonnenuntergang."] },
      { number: 20, text: "Einmal und ein zweites Mal übernachteten die Händler und die Verkäufer von allerlei Waren draußen vor Jerusalem." },
      { number: 21, text: "Da warnte ich sie und sagte zu ihnen: Wozu übernachtet ihr gerade vor der Mauer? Wenn ihr es noch einmal tut, lasse ich euch festnehmen. Von da an kamen sie nicht mehr am Sabbat." },
      { number: 22, text: "Den Leviten aber befahl ich, sie sollten sich reinigen und sich als Wächter an die Tore stellen, damit man den Sabbattag heilig halte. Auch dafür denk an mich, mein Gott, und sei mir gnädig in deiner großen Huld!", footnotes: ["12,30"] },
      { number: 23, text: "Damals sah ich auch Juden, die Frauen von Aschdod, Ammon und Moab geheiratet hatten.", heading: "Das Verbot der Mischehen: 13,23-31", footnotes: ["Esra 9,1f"] },
      { number: 24, text: "Die Hälfte ihrer Kinder redete in der Sprache von Aschdod oder in der Sprache eines der anderen Völker, konnten aber nicht mehr Jüdisch." },
      { number: 25, text: "Ich machte ihnen Vorwürfe und verfluchte sie. Einige von ihnen schlug ich und packte sie bei den Haaren. Ich beschwor sie bei Gott: Ihr dürft eure Töchter nicht ihren Söhnen geben noch ihre Töchter zu Frauen für eure Söhne oder für euch selbst nehmen.", footnotes: ["Dtn 7,3"] },
      { number: 26, text: "Hat sich nicht wegen solcher Frauen Salomo, der König Israels, versündigt? Unter den vielen Völkern gab es keinen König wie ihn. Er wurde von seinem Gott geliebt; darum hatte ihn Gott zum König über ganz Israel gemacht. Aber selbst ihn haben die fremden Frauen zur Sünde verführt.", footnotes: ["1 Kön 11,1-8; 10,23; 2 Sam 12,24f"] },
      { number: 27, text: "Und jetzt hört man von euch, dass ihr genau dieselbe Untat begeht und unserem Gott die Treue brecht, indem ihr fremde Frauen heiratet." },
      { number: 28, text: "Einer der Söhne des Hohenpriesters Jojada, des Sohnes Eljaschibs, war der Schwiegersohn des Sanballat von Horon; ihn verwies ich aus meinem Gebiet.", footnotes: ["Lev 21,14"] },
      { number: 29, text: "Vergiss es ihnen nicht, mein Gott, dass sie das Priestertum und den Bund der Priester und Leviten befleckt haben.", footnotes: ["Num 25,12f; Mal 2,8"] },
      { number: 30, text: "So habe ich das Volk von allem gereinigt, was fremd war. Für die Priester und Leviten habe ich Ordnungen aufgestellt, die jedem seinen Dienst zuteilen." },
      { number: 31, text: "Auch habe ich angeordnet, wie man an den festgesetzten Zeiten das Brennholz liefern und die ersten Erträge bringen soll. Denk daran, mein Gott, und halt es mir zugute!", footnotes: ["10,35; 13,14; 5,19"] },
    ] },
  ],
};
