import { Book } from "@/lib/types";

export const proverbs: Book = {
  id: "proverbs",
  name: "Sprichwörter",
  shortName: "Spr",
  testament: "old",
  introduction: "Sprichwörter sind bei allen Völkern sehr verbreitet und nehmen besonders in den Literaturen des Alten Orients eine bedeutende Stelle ein. Im alten Israel war es vor allem der wegen seiner Weisheit berühmte König Salomo, dem zahlreiche Lieder und gelehrte Aussprüche zugeschrieben wurden (1 Kön 5,12-14). Kein Wunder, dass die spätere Tradition in ihm auch den Verfasser des Buches der Sprichwörter gesehen hat (1,1). Ausdrücklich wird er als Verfasser der beiden Hauptsammlungen 10,1 - 22,16 und 25,1 - 29,27 genannt. Weil in den Sammlungen seiner Sprichwörter schriftlich und mündlich überliefertes Material zusammengetragen und fortlaufend erweitert wurde, ist es nicht möglich, den Anteil Salomos an den beiden Hauptsammlungen herauszuschälen. Von der zweiten salomonischen Sammlung wird erwähnt, dass sie von den »Männern Hiskijas, des Königs von Juda«, zusammengestellt wurde (25,1). Eine zweite Blütezeit erlebte die Weisheitsliteratur in der nachexilischen Epoche. Damals entstanden die Bücher Ijob und Kohelet und etwas später Sirach und Weisheit. In dieser Spätzeit sind auch die Anhänge an die zwei genannten salomonischen Sammlungen angefügt worden: »Worte von Weisen« (22,17 - 24,34), »Worte Agurs« (30,1-14), die Zahlensprüche (30,15-33), »Worte an Lemuël« (31,1-9) sowie, als Abschluss des Buches, das Loblied auf die tüchtige Frau (31,10-31). Von den Sprichwörtersammlungen hebt sich die lange Einleitung (Kap. 1 - 9) ab. In ihr stehen größtenteils zusammenhängende Mahnungen eines Weisheitslehrers, der vor Torheit warnt und Weisheit empfiehlt. Für die Endfassung des Buchs kommt somit die Nachexilszeit bis hinab zur griechischen Übersetzung in Frage, also etwa 500 bis 200 v. Chr. Herkunft und Alter der einzelnen Teile und erst recht der einzelnen Sprichwörter können sehr verschieden sein. Die Schlüsselwörter »Weisheit« und »Torheit« sind nicht allein vom Verstand her aufzufassen. »Weisheit« ist vielmehr der Inbegriff einer charaktervollen, religiös und sittlich intakten Verhaltensweise in allen Lebenslagen. Das ganze Buch hat eine erzieherische Tendenz; daher spricht besonders in den ersten neun Kapiteln der Weisheitslehrer wie ein Vater zu seinem Sohn. Für die nachexilische Entwicklung der Weisheitsliteratur ist typisch, dass die »Weisheit« (in 9,13-18 auch die »Torheit«) personifiziert wird und selbstständig redend und handelnd auftritt (vgl. schon 1,20-33). Im Hymnus 8,22-36 stellt sie sich als Erstling der Schöpfung vor. Da sie in Wirklichkeit die göttliche Weisheit selbst ist, steht sie Gott bei der Weltschöpfung zur Verfügung. Sie kann aber auch den Menschen und dem Volk Israel mitgeteilt werden. Die eigentlichen Sprichwörter, die den größten Teil des Buches ausfüllen, haben ihre Wurzel im Volksmund. Deshalb sind sie manchmal etwas derb, stellen drastische Vergleiche an und bieten alltägliche und allgemein menschliche Lebenserfahrungen. Gelegentlich formulieren sie, um einprägsame Kürze zu erreichen, einseitig. Der Vorteil und der materielle Nutzen der Weisheit wird im Sprichwort besonders stark betont. Doch steht selbst hinter den profanen Sprichwörtern eine religiöse Grundhaltung und wenigstens ein Siebtel aller Verse nimmt auf den Gottesglauben Bezug. Der Grundsatz »Anfang der Weisheit ist die Gottesfurcht« (vgl. 1,7; 9,10; Ps 111,10; Sir 1,14) rückt alles Streben nach Weisheit, auch das rein innerweltliche, in eine Beziehung zu Gott. Im Neuen Testament wird unser Buch oft wörtlich oder dem Sinn nach angeführt. Einige Sprichwörter sind auch in den deutschen Sprachschatz eingegangen (vgl. 16,18; 24,16; 25,21f; 26,27).",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Sprichwörter Salomos, des Sohnes Davids, / des Königs von Israel:", heading: "Einleitung: 1,1-7" },
      { number: 2, text: "um Weisheit zu lernen und Zucht, / um kundige Rede zu verstehen," },
      { number: 3, text: "um Zucht und Verständnis zu erlangen, / Gerechtigkeit, Rechtssinn und Redlichkeit," },
      { number: 4, text: "um Unerfahrenen Klugheit zu verleihen, / der Jugend Kenntnis und Umsicht." },
      { number: 5, text: "Der Weise höre und vermehre sein Wissen, / der Verständige lerne kluge Führung,", footnotes: ["9,9"] },
      { number: 6, text: "um Sinnspruch und Gleichnis zu verstehen, / die Worte und Rätsel der Weisen." },
      { number: 7, text: "Gottesfurcht ist Anfang der Erkenntnis, / nur Toren verachten Weisheit und Zucht.", footnotes: ["9,10; Ps 111,10; Sir 1,14"] },
      { number: 8, text: "Höre, mein Sohn, auf die Mahnung des Vaters / und die Lehre deiner Mutter verwirf nicht!", heading: "Eine Sammlung von Weisheitslehren: 1,8 - 9,18 | Die Warnung vor Verführung und Unerfahrenheit: 1,8 - 3,35", footnotes: ["6,20; 23,22"] },
      { number: 9, text: "Sie sind ein schöner Kranz auf deinem Haupt / und eine Kette für deinen Hals." },
      { number: 10, text: "Mein Sohn, wenn dich Sünder locken, / dann folg ihnen nicht," },
      { number: 11, text: "wenn sie sagen: Geh mit uns, / wir wollen darauf lauern, Blut zu vergießen, / ohne Grund dem Arglosen nachzustellen;" },
      { number: 12, text: "wie die Unterwelt wollen wir sie lebendig verschlingen, / Männer in ihrer Kraft, als wären sie dem Grab verfallen." },
      { number: 13, text: "Manch kostbares Stück werden wir finden, / mit Beute unsere Häuser füllen." },
      { number: 14, text: "Wirf dein Los in unserm Kreis, / gemeinsam sei uns der Beutel." },
      { number: 15, text: "Mein Sohn, geh nicht mit ihnen, / halte deinen Fuß fern von ihrem Pfad!" },
      { number: 16, text: "Denn ihre Füße laufen dem Bösen nach, / sie eilen, Blut zu vergießen.", footnotes: ["Jes 59,7; Röm 3,15"] },
      { number: 17, text: "Umsonst wird das Netz ausgespannt / vor den Augen aller Vögel;", footnotes: ["17f: Sinn: Die Vögel meiden ein Netz, das sie sehen; die Verführer aber rennen in ihr eigenes Verderben."] },
      { number: 18, text: "sie aber lauern auf ihr eigenes Blut, / sie trachten sich selbst nach dem Leben." },
      { number: 19, text: "So enden alle, die sich durch Raub bereichern: / Wer ihn an sich nimmt, dem raubt er das Leben." },
      { number: 20, text: "Die Weisheit ruft laut auf der Straße, / auf den Plätzen erhebt sie ihre Stimme.", footnotes: ["8,1-3; Sir 24,1f", "20-33: Die Weisheit wird als Bußpredigerin personifiziert."] },
      { number: 21, text: "Am Anfang der Mauern predigt sie, / an den Stadttoren hält sie ihre Reden:" },
      { number: 22, text: "Wie lang noch, ihr Törichten, liebt ihr Betörung, / gefällt den Zuchtlosen ihr dreistes Gerede, / hassen die Toren Erkenntnis?" },
      { number: 23, text: "Wendet euch meiner Mahnung zu! / Dann will ich auf euch meinen Geist ausgießen / und meine Worte euch kundtun." },
      { number: 24, text: "Als ich rief, habt ihr euch geweigert, / meine drohende Hand hat keiner beachtet;" },
      { number: 25, text: "jeden Rat, den ich gab, habt ihr ausgeschlagen, / meine Mahnung gefiel euch nicht." },
      { number: 26, text: "Darum werde auch ich lachen, / wenn euch Unglück trifft, / werde spotten, wenn Schrecken über euch kommt," },
      { number: 27, text: "wenn der Schrecken euch wie ein Unwetter naht / und wie ein Sturm euer Unglück hereinbricht, / wenn Not und Drangsal euch überfallen." },
      { number: 28, text: "Dann werden sie nach mir rufen, doch ich höre nicht; / sie werden mich suchen, aber nicht finden." },
      { number: 29, text: "Weil sie die Einsicht hassten / und nicht die Gottesfurcht wählten," },
      { number: 30, text: "meinen Rat nicht wollten, / meine ganze Mahnung missachteten," },
      { number: 31, text: "sollen sie nun essen von der Frucht ihres Tuns / und von ihren Plänen sich sättigen." },
      { number: 32, text: "Denn die Abtrünnigkeit der Haltlosen ist ihr Tod, / die Sorglosigkeit der Toren ist ihr Verderben." },
      { number: 33, text: "Wer aber auf mich hört, wohnt in Sicherheit, / ihn stört kein böser Schrecken." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Mein Sohn, wenn du meine Worte annimmst / und meine Gebote beherzigst," },
      { number: 2, text: "der Weisheit Gehör schenkst, / dein Herz der Einsicht zuneigst," },
      { number: 3, text: "wenn du nach Erkenntnis rufst, / mit lauter Stimme um Einsicht bittest," },
      { number: 4, text: "wenn du sie suchst wie Silber, / nach ihr forschst wie nach Schätzen," },
      { number: 5, text: "dann wirst du die Gottesfurcht begreifen / und Gotteserkenntnis finden." },
      { number: 6, text: "Denn der Herr gibt Weisheit, / aus seinem Mund kommen Erkenntnis und Einsicht." },
      { number: 7, text: "Für die Redlichen hält er Hilfe bereit, / den Rechtschaffenen ist er ein Schild.", footnotes: ["Ps 3,4"] },
      { number: 8, text: "Er hütet die Pfade des Rechts / und bewacht den Weg seiner Frommen." },
      { number: 9, text: "Dann begreifst du, was Recht und Gerechtigkeit ist, / Redlichkeit und jedes gute Verhalten;" },
      { number: 10, text: "denn Weisheit zieht ein in dein Herz, / Erkenntnis beglückt deine Seele." },
      { number: 11, text: "Besonnenheit wacht über dir / und Einsicht behütet dich." },
      { number: 12, text: "Sie bewahrt dich vor dem Weg des Bösen, / vor Leuten, die Verkehrtes reden," },
      { number: 13, text: "die den rechten Weg verlassen, / um auf dunklen Pfaden zu gehen," },
      { number: 14, text: "die sich freuen am bösen Tun / und jubeln über die Verkehrtheit des Schlechten," },
      { number: 15, text: "deren Pfade krumm verlaufen / und deren Straßen in die Irre führen." },
      { number: 16, text: "Sie bewahrt dich vor der Frau des andern, / vor der Fremden, die verführerisch redet,", footnotes: ["6,24-35; 7,5"] },
      { number: 17, text: "die den Gefährten ihrer Jugend verlässt / und den Bund ihres Gottes vergisst;", footnotes: ["Im AT ist die Ehe ein Bund vor Gott (vgl. Mal 2,14); sie ist auch Symbol für den Bund Gottes mit dem Volk (vgl. Hos 2; Jer 2,2; 3,1-13)."] },
      { number: 18, text: "ihr Haus sinkt hinunter zur Totenwelt, / ihre Straße führt zu den Totengeistern hinab.", footnotes: ["5,5"] },
      { number: 19, text: "Wer zu ihr geht, kehrt nie zurück, / findet nie wieder die Pfade des Lebens." },
      { number: 20, text: "Darum geh auf dem Weg der Guten, / halte dich an die Pfade der Gerechten;" },
      { number: 21, text: "denn die Redlichen werden das Land bewohnen, / wer rechtschaffen ist, wird darin bleiben.", footnotes: ["Ps 37,11; Mt 5,5"] },
      { number: 22, text: "Die Frevler aber werden aus dem Land verstoßen, / die Verräter aus ihm weggerissen." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Mein Sohn, vergiss meine Lehre nicht, bewahre meine Gebote in deinem Herzen!" },
      { number: 2, text: "Denn sie vermehren die Tage und Jahre deines Lebens / und bringen dir Wohlergehen.", footnotes: ["4,10"] },
      { number: 3, text: "Nie sollen Liebe und Treue dich verlassen; / binde sie dir um den Hals, / schreib sie auf die Tafel deines Herzens!", footnotes: ["Ps 85,11"] },
      { number: 4, text: "Dann erlangst du Gunst und Beifall / bei Gott und den Menschen.", footnotes: ["1 Sam 2,26; Lk 2,52"] },
      { number: 5, text: "Mit ganzem Herzen vertrau auf den Herrn, / bau nicht auf eigene Klugheit;" },
      { number: 6, text: "such ihn zu erkennen auf all deinen Wegen, / dann ebnet er selbst deine Pfade." },
      { number: 7, text: "Halte dich nicht selbst für weise, / fürchte den Herrn und fliehe das Böse!" },
      { number: 8, text: "Das ist heilsam für deine Gesundheit / und erfrischt deine Glieder." },
      { number: 9, text: "Ehre den Herrn mit deinem Vermögen, / mit dem Besten von dem, was du erntest." },
      { number: 10, text: "Dann füllen sich deine Scheunen mit Korn, / deine Fässer laufen über von Wein." },
      { number: 11, text: "Mein Sohn, verachte nicht die Zucht des Herrn, / widersetz dich nicht, wenn er dich zurechtweist." },
      { number: 12, text: "Wen der Herr liebt, den züchtigt er, / wie ein Vater seinen Sohn, den er gern hat." },
      { number: 13, text: "Wohl dem Mann, der Weisheit gefunden, / dem Mann, der Einsicht gewonnen hat." },
      { number: 14, text: "Denn sie zu erwerben ist besser als Silber, / sie zu gewinnen ist besser als Gold." },
      { number: 15, text: "Sie übertrifft die Perlen an Wert, / keine kostbaren Steine kommen ihr gleich.", footnotes: ["8,11"] },
      { number: 16, text: "Langes Leben birgt sie in ihrer Rechten, / in ihrer Linken Reichtum und Ehre;" },
      { number: 17, text: "ihre Wege sind Wege der Freude, / all ihre Pfade führen zum Glück." },
      { number: 18, text: "Wer nach ihr greift, dem ist sie ein Lebensbaum, / wer sie fest hält, ist glücklich zu preisen.", footnotes: ["Der Lebensbaum im Paradies (Gen 2,9) ist Symbol für langes und glückliches Leben (vgl. 11,30; 13,12; 15,4)."] },
      { number: 19, text: "Der Herr hat die Erde mit Weisheit gegründet / und mit Einsicht den Himmel befestigt.", footnotes: ["19f: Die göttliche Schöpfungsweisheit wird poetisch personifiziert (vgl. 8,22-31). Zugrunde liegt das geozentrische Weltbild der Alten."] },
      { number: 20, text: "Durch sein Wissen brechen die tiefen Quellen hervor / und träufeln die Wolken den Tau herab." },
      { number: 21, text: "Mein Sohn, lass beides nicht aus den Augen: / Bewahre Umsicht und Besonnenheit!" },
      { number: 22, text: "Dann werden sie dir ein Lebensquell, / ein Schmuck für deinen Hals;" },
      { number: 23, text: "dann gehst du sicher deinen Weg / und stößt mit deinem Fuß nicht an.", footnotes: ["Ps 91,5-12"] },
      { number: 24, text: "Gehst du zur Ruhe, so schreckt dich nichts auf, / legst du dich nieder, erquickt dich dein Schlaf." },
      { number: 25, text: "Du brauchst dich vor jähem Erschrecken nicht zu fürchten / noch vor dem Verderben, das über die Frevler kommt." },
      { number: 26, text: "Der Herr wird deine Zuversicht sein, / er bewahrt deinen Fuß vor der Schlinge." },
      { number: 27, text: "Versag keine Wohltat dem, der sie braucht, / wenn es in deiner Hand liegt, Gutes zu tun.", footnotes: ["Sir 4,1-10"] },
      { number: 28, text: "Wenn du jetzt etwas hast, / sag nicht zu deinem Nächsten: / Geh, komm wieder, morgen will ich dir etwas geben.", footnotes: ["Jak 2,15f"] },
      { number: 29, text: "Sinne nichts Böses gegen deinen Nächsten, / der friedlich neben dir wohnt." },
      { number: 30, text: "Bring niemand ohne Grund vor Gericht, / wenn er dir nichts Böses getan hat." },
      { number: 31, text: "Beneide den Gewalttätigen nicht, / wähle keinen seiner Wege;" },
      { number: 32, text: "denn ein Gräuel ist dem Herrn der Ränkeschmied, / die Redlichen sind seine Freunde." },
      { number: 33, text: "Der Fluch des Herrn fällt auf das Haus des Frevlers, / die Wohnung der Gerechten segnet er." },
      { number: 34, text: "Die Zuchtlosen verspottet er, / den Gebeugten erweist er seine Gunst.", footnotes: ["Jak 4,6; 1 Petr 5,5"] },
      { number: 35, text: "Die Weisen erlangen Ehre, / die Toren aber häufen Schande auf sich." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Ihr Söhne, hört auf die Mahnung des Vaters, / merkt auf, damit ihr Einsicht lernt;", heading: "Die Mahnung zur Weisheit: 4,1-27" },
      { number: 2, text: "denn gute Lehre gebe ich euch. / Lasst nicht ab von meiner Weisung!" },
      { number: 3, text: "Als ich noch ein Knabe war bei meinem Vater, / das zarte und einzige Kind meiner Mutter," },
      { number: 4, text: "da lehrte er mich und sagte zu mir: / Nimm dir meine Worte zu Herzen, / folge meinen Geboten und du wirst leben.", footnotes: ["4-7: Text in H und G nicht einheitlich."] },
      { number: 5, text: "Erwirb dir Weisheit, erwirb dir Einsicht, / vergiss sie nicht, weich nicht ab von meinen Worten!", footnotes: ["23,23"] },
      { number: 6, text: "Lass nicht von ihr und sie wird dich behüten, / liebe sie und sie wird dich beschützen." },
      { number: 7, text: "Anfang der Weisheit ist: Erwirb dir Weisheit, / erwirb dir Einsicht mit deinem ganzen Vermögen!" },
      { number: 8, text: "Halte sie hoch, dann wird sie dich erhöhen; / sie bringt dich zu Ehren, wenn du sie umarmst." },
      { number: 9, text: "Sie setzt dir einen schönen Kranz auf das Haupt, / eine prächtige Krone wird sie dir schenken." },
      { number: 10, text: "Höre, mein Sohn, und nimm meine Worte an, / dann mehren sich die Jahre deines Lebens." },
      { number: 11, text: "Den Weg der Weisheit zeige ich dir, / ich leite dich auf ebener Bahn." },
      { number: 12, text: "Wenn du gehst, ist dein Schritt nicht beengt, / wenn du läufst, wirst du nicht straucheln." },
      { number: 13, text: "Halt fest an der Zucht und lass davon nicht ab, / bewahre sie; denn sie ist dein Leben." },
      { number: 14, text: "Betritt nicht den Pfad der Frevler, / beschreite nicht den Weg der Bösen!" },
      { number: 15, text: "Meide ihn, geh nicht auf ihm, / kehr dich von ihm ab und geh vorbei!" },
      { number: 16, text: "Denn sie schlafen nicht, ehe sie Böses tun; / der Schlaf flieht sie, bis sie Verbrechen begehen." },
      { number: 17, text: "Sie essen das Brot des Unrechts / und trinken den Wein der Gewalttat." },
      { number: 18, text: "Doch der Pfad der Gerechten / ist wie das Licht am Morgen; / es wird immer heller bis zum vollen Tag." },
      { number: 19, text: "Der Weg der Frevler ist wie dunkle Nacht; / sie merken nicht, worüber sie fallen." },
      { number: 20, text: "Mein Sohn, achte auf meine Worte, / neige dein Ohr meiner Rede zu!" },
      { number: 21, text: "Lass sie nicht aus den Augen, / bewahre sie tief im Herzen!" },
      { number: 22, text: "Denn Leben bringen sie dem, der sie findet, / und Gesundheit seinem ganzen Leib." },
      { number: 23, text: "Mehr als alles hüte dein Herz; / denn von ihm geht das Leben aus." },
      { number: 24, text: "Vermeide alle Falschheit des Mundes / und Verkehrtheit der Lippen halt von dir fern!" },
      { number: 25, text: "Deine Augen sollen geradeaus schauen / und deine Blicke richte nach vorn!" },
      { number: 26, text: "Ebne die Straße für deinen Fuß / und alle deine Wege seien geordnet." },
      { number: 27, text: "Bieg nicht ab, weder rechts noch links, / halt deinen Fuß vom Bösen zurück!", footnotes: ["G und Vg fügen zwei weitere Verse an: Denn die Wege zur Rechten kennt Gott, doch krumm sind die Wege zur Linken. Er aber wird deine Pfade eben machen und deine Bahnen in Frieden geleiten."] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Mein Sohn, merk auf meinen weisen Rat, / neige meiner Einsicht dein Ohr zu,", heading: "Die Warnung vor der fremden Frau: 5,1-23", footnotes: ["(1-23) 7,1-27", "1-23: Der junge Mann wird besonders oft und eindringlich vor der «fremden» Frau, d. h. der bereits verheirateten, gewarnt. In der späten Nachexilszeit setzte sich die Einehe immer mehr durch."] },
      { number: 2, text: "damit du Besonnenheit bewahrst / und deine Lippen auf Klugheit achten." },
      { number: 3, text: "Denn die Lippen der fremden Frau triefen von Honig, / glatter als Öl ist ihr Mund.", footnotes: ["Sir 26,9-27"] },
      { number: 4, text: "Doch zuletzt ist sie bitter wie Wermut, / scharf wie ein zweischneidiges Schwert." },
      { number: 5, text: "Ihre Füße steigen zur Totenwelt hinab, / ihre Schritte gehen der Unterwelt zu.", footnotes: ["2,18; 7,27"] },
      { number: 6, text: "Den ebenen Pfad zum Leben verfehlt sie, / sie geht krumme Wege und merkt es nicht." },
      { number: 7, text: "Nun denn, ihr Söhne, hört auf mich, / weicht nicht ab von den Worten, die mein Mund spricht." },
      { number: 8, text: "Halte deinen Weg von ihr fern, / komm ihrer Haustür nicht nahe!" },
      { number: 9, text: "Sonst schenkst du andern deine Kraft, / deine Jahre einem Rücksichtslosen;" },
      { number: 10, text: "sonst sättigen sich Fremde an deinem Besitz, / die Frucht deiner Arbeit / kommt in das Haus eines andern" },
      { number: 11, text: "und am Ende wirst du stöhnen, / wenn dein Leib und dein Fleisch dahinsiechen." },
      { number: 12, text: "Dann wirst du bekennen: / Weh mir, ich habe die Zucht gehasst, / mein Herz hat die Warnung verschmäht;" },
      { number: 13, text: "ich habe nicht auf die Stimme meiner Erzieher gehört, / mein Ohr nicht meinen Lehrern zugeneigt." },
      { number: 14, text: "Fast hätte mich alles Unheil getroffen / in der Versammlung und in der Gemeinde.", footnotes: ["Auf Ehebruch stand nach Lev 20,10 und Dtn 22,22 die Todesstrafe, die von der Versammlung am Stadttor verhängt wurde (vgl. Dan 13,41)."] },
      { number: 15, text: "Trink Wasser aus deiner eigenen Zisterne, / Wasser, das aus deinem Brunnen quillt.", footnotes: ["D. h.: Begnüge dich mit deiner eigenen Frau!"] },
      { number: 16, text: "Sollen deine Quellen auf die Straße fließen, / auf die freien Plätze deine Bäche?" },
      { number: 17, text: "Dir allein sollen sie gehören, / kein Fremder soll teilen mit dir." },
      { number: 18, text: "Dein Brunnen sei gesegnet; / freu dich der Frau deiner Jugendtage," },
      { number: 19, text: "der lieblichen Gazelle, der anmutigen Gämse! / Ihre Liebkosung mache dich immerfort trunken, / an ihrer Liebe berausch dich immer wieder!" },
      { number: 20, text: "Warum solltest du dich an einer Fremden berauschen, / den Busen einer andern umfangen?" },
      { number: 21, text: "Denn der Weg eines jeden liegt offen vor den Augen des Herrn, / er achtet auf alle seine Pfade." },
      { number: 22, text: "Der Frevler verfängt sich in der eigenen Schuld, / die Stricke seiner Sünde halten ihn fest." },
      { number: 23, text: "Er stirbt aus Mangel an Zucht, / wegen seiner großen Torheit stürzt er ins Verderben." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Mein Sohn, hast du deinem Nächsten Bürgschaft geleistet, / hast du einem Fremden den Handschlag gegeben,", heading: "Die Weisheit im täglichen Leben: 6,1-19", footnotes: ["11,15; 17,18; 20,16; Sir 29,14-20", "1-5: Eine Warnung für Unerfahrene. Der Bürge kann leicht betrogen werden."] },
      { number: 2, text: "hast du dich durch deine Worte gebunden, / bist du gefangen durch deine Worte," },
      { number: 3, text: "dann tu doch dies, mein Sohn: Reiß dich los; / denn du bist in die Hände deines Nächsten geraten. / Geh eilends hin und bestürm deinen Nächsten!" },
      { number: 4, text: "Gönne deinen Augen keinen Schlaf, / keinen Schlummer deinen Wimpern," },
      { number: 5, text: "entreiß dich seiner Hand wie eine Gazelle, / wie ein Vogel der Hand des Jägers!" },
      { number: 6, text: "Geh zur Ameise, du Fauler, / betrachte ihr Verhalten und werde weise!", footnotes: ["30,25"] },
      { number: 7, text: "Sie hat keinen Meister, / keinen Aufseher und Gebieter," },
      { number: 8, text: "und doch sorgt sie im Sommer für Futter, / sammelt sich zur Erntezeit Vorrat." },
      { number: 9, text: "Wie lang, du Fauler, willst du noch daliegen, / wann willst du aufstehen von deinem Schlaf?" },
      { number: 10, text: "Noch ein wenig schlafen, / noch ein wenig schlummern, / noch ein wenig die Arme verschränken, um auszuruhen.", footnotes: ["24,33f"] },
      { number: 11, text: "Da kommt schon die Armut wie ein Strolch über dich, / die Not wie ein zudringlicher Bettler." },
      { number: 12, text: "Ein Nichtsnutz, ja ein Gauner, / wer daherkommt mit Lügen im Mund," },
      { number: 13, text: "wer mit den Augen zwinkert, mit den Füßen deutet, / Zeichen gibt mit den Fingern.", footnotes: ["10,10"] },
      { number: 14, text: "Tücke im Herzen, stets voll böser Ränke, / zettelt er jederzeit Händel an." },
      { number: 15, text: "Darum wird plötzlich das Verderben über ihn kommen, / im Nu, ohne Rettung, wird er zerschmettert." },
      { number: 16, text: "Sechs Dinge sind dem Herrn verhasst, / sieben sind ihm ein Gräuel:", footnotes: ["16-19: Ein so genannter Zahlenspruch (vgl. 30,15-33)."] },
      { number: 17, text: "Stolze Augen, eine falsche Zunge, / Hände, die unschuldiges Blut vergießen," },
      { number: 18, text: "ein Herz, das finstere Pläne hegt, / Füße, die schnell dem Bösen nachlaufen," },
      { number: 19, text: "ein falscher Zeuge, der Lügen zuflüstert, / und wer Streit entfacht unter Brüdern." },
      { number: 20, text: "Achte, mein Sohn, auf das Gebot deines Vaters, / missachte nicht die Lehre deiner Mutter!", heading: "Die eheliche Treue: 6,20 - 7,27" },
      { number: 21, text: "Binde sie dir für immer aufs Herz / und winde sie dir um den Hals!" },
      { number: 22, text: "Wenn du gehst, geleitet sie dich, / wenn du ruhst, behütet sie dich, / beim Erwachen redet sie mit dir." },
      { number: 23, text: "Denn eine Leuchte ist das Gebot / und die Lehre ein Licht, / ein Weg zum Leben sind Mahnung und Zucht." },
      { number: 24, text: "Sie bewahren dich vor der Frau des Nächsten, / vor der glatten Zunge der Fremden." },
      { number: 25, text: "Begehre nicht in deinem Herzen ihre Schönheit, / lass dich nicht fangen durch ihre Wimpern!" },
      { number: 26, text: "Einer Dirne zahlt man bis zu einem Laib Brot, / die Frau eines andern jagt dir das kostbare Leben ab." },
      { number: 27, text: "Trägt man denn Feuer in seinem Gewand, / ohne dass die Kleider in Brand geraten?" },
      { number: 28, text: "Kann man über glühende Kohlen schreiten, / ohne sich die Füße zu verbrennen?" },
      { number: 29, text: "So ist es mit dem, der zur Frau seines Nächsten geht. / Keiner bleibt ungestraft, der sie berührt." },
      { number: 30, text: "Verachtet man nicht den Dieb, / auch wenn er nur stiehlt, / um sein Verlangen zu stillen, weil er Hunger hat?" },
      { number: 31, text: "Wird er ertappt, so muss er siebenfach zahlen, / den ganzen Besitz seines Hauses geben." },
      { number: 32, text: "Wer Ehebruch treibt, ist ohne Verstand, / nur wer sich selbst vernichten will, / lässt sich darauf ein." },
      { number: 33, text: "Schläge und Schande bringt es ihm ein, / unaustilgbar ist seine Schmach." },
      { number: 34, text: "Denn Eifersucht bringt den Ehemann in Wut, / er kennt keine Schonung am Tag der Rache." },
      { number: 35, text: "Kein Sühnegeld nimmt er an; / magst du auch Geschenke häufen, er willigt nicht ein." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Mein Sohn, achte auf meine Worte, / meine Gebote verwahre bei dir!" },
      { number: 2, text: "Achte auf meine Gebote, damit du am Leben bleibst, / hüte meine Lehre wie deinen Augapfel!" },
      { number: 3, text: "Binde sie dir an die Finger, / schreib sie auf die Tafel deines Herzens!" },
      { number: 4, text: "Sag zur Weisheit: Du bist meine Schwester!, / und nenne die Klugheit deine Freundin!" },
      { number: 5, text: "Sie bewahrt dich vor der Frau eines andern, / vor der Fremden, die verführerisch redet.", footnotes: ["2,16; 6,24"] },
      { number: 6, text: "Vom Fenster meines Hauses, / durch das Gitter, habe ich ausgeschaut;", footnotes: ["6-27: Hier wird das Thema «fremde Frau» breit entfaltet. Untergang und Tod sind immer das Ende."] },
      { number: 7, text: "da sah ich bei den Unerfahrenen, / da bemerkte ich bei den Burschen / einen jungen Mann ohne Verstand:" },
      { number: 8, text: "Er ging über die Straße, bog um die Ecke / und nahm den Weg zu ihrem Haus;" },
      { number: 9, text: "als der Tag sich neigte, in der Abenddämmerung, / um die Zeit, da es dunkel wird und die Nacht kommt." },
      { number: 10, text: "Da! Eine Frau kommt auf ihn zu, / im Kleid der Dirnen, mit listiger Absicht;" },
      { number: 11, text: "voll Leidenschaft ist sie und unbändig, / ihre Füße blieben nicht mehr im Haus;" },
      { number: 12, text: "bald auf den Gassen, bald auf den Plätzen, / an allen Straßenecken lauert sie." },
      { number: 13, text: "Nun packt sie ihn, küsst ihn, / sagt zu ihm mit keckem Gesicht:" },
      { number: 14, text: "Ich war zu Heilsopfern verpflichtet / und heute erfüllte ich meine Gelübde." },
      { number: 15, text: "Darum bin ich ausgegangen, dir entgegen, / ich habe dich gesucht und gefunden." },
      { number: 16, text: "Ich habe Decken über mein Bett gebreitet, / bunte Tücher aus ägyptischem Leinen;" },
      { number: 17, text: "ich habe mein Lager besprengt / mit Myrrhe, Aloe und Zimt." },
      { number: 18, text: "Komm, wir wollen bis zum Morgen in Liebe schwelgen, / wir wollen die Liebeslust kosten." },
      { number: 19, text: "Denn mein Mann ist nicht zu Hause, / er ist auf Reisen, weit fort." },
      { number: 20, text: "Den Geldbeutel hat er mitgenommen, / erst am Vollmondstag kehrt er heim." },
      { number: 21, text: "So macht sie ihn willig mit viel Überredung, / mit schmeichelnden Lippen verführt sie ihn." },
      { number: 22, text: "Betört folgt er ihr, / wie ein Ochse, den man zum Schlachten führt, / wie ein Hirsch, den das Fangseil umschlingt," },
      { number: 23, text: "bis ein Pfeil ihm die Leber zerreißt; / wie ein Vogel, der in das Netz fliegt / und nicht merkt, dass es um sein Leben geht." },
      { number: 24, text: "Darum, ihr Söhne, hört auf mich, / achtet auf meine Reden!", footnotes: ["1,8; 4,1; 5,7"] },
      { number: 25, text: "Dein Herz schweife nicht ab auf ihre Wege, / verirre dich nicht auf ihre Pfade!" },
      { number: 26, text: "Denn zahlreich sind die Erschlagenen, / die sie gefällt hat; / viele sind es, die sie ermordet hat;" },
      { number: 27, text: "ihr Haus ist ein Weg zur Unterwelt, / er führt zu den Kammern des Todes.", footnotes: ["5,5; 9,18"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Ruft nicht die Weisheit, / erhebt nicht die Klugheit ihre Stimme?", heading: "Die Weisheit als Gabe Gottes: 8,1 - 9,18", footnotes: ["Sir 24,1f", "1-21: Die Weisheit tritt als Mahnrednerin auf wie in 1,20-33."] },
      { number: 2, text: "Bei der Stadtburg, auf den Straßen, / an der Kreuzung der Wege steht sie;" },
      { number: 3, text: "neben den Toren, wo die Stadt beginnt, / am Zugang zu den Häusern ruft sie laut:" },
      { number: 4, text: "Euch, ihr Leute, lade ich ein, / meine Stimme ergeht an alle Menschen:" },
      { number: 5, text: "Ihr Unerfahrenen, werdet klug, / ihr Törichten, nehmt Vernunft an!" },
      { number: 6, text: "Hört her! Aufrichtig rede ich. / Redlich ist, was meine Lippen reden." },
      { number: 7, text: "Die Wahrheit spricht meine Zunge, / Unrechtes ist meinen Lippen ein Gräuel." },
      { number: 8, text: "Alle meine Worte sind recht, / keines von ihnen ist hinterhältig und falsch." },
      { number: 9, text: "Für den Verständigen sind sie alle klar / und richtig für den, der Erkenntnis fand." },
      { number: 10, text: "Nehmt lieber Bildung an als Silber, / lieber Verständnis als erlesenes Gold!" },
      { number: 11, text: "Ja, Weisheit übertrifft die Perlen an Wert, / keine kostbaren Steine kommen ihr gleich.", footnotes: ["3,15"] },
      { number: 12, text: "Ich, die Weisheit, verweile bei der Klugheit, / ich entdecke Erkenntnis und guten Rat." },
      { number: 13, text: "Gottesfurcht verlangt, Böses zu hassen. / Hochmut und Hoffart, schlechte Taten / und einen verlogenen Mund hasse ich." },
      { number: 14, text: "Bei mir ist Rat und Hilfe; / ich bin die Einsicht, bei mir ist Macht." },
      { number: 15, text: "Durch mich regieren die Könige / und entscheiden die Machthaber, wie es Recht ist;" },
      { number: 16, text: "durch mich versehen die Herrscher ihr Amt, / die Vornehmen und alle Verwalter des Rechts.", footnotes: ["Weish 6,20"] },
      { number: 17, text: "Ich liebe alle, die mich lieben, / und wer mich sucht, der wird mich finden." },
      { number: 18, text: "Reichtum und Ehre sind bei mir, / angesehener Besitz und Glück;", footnotes: ["Weish 8,5"] },
      { number: 19, text: "meine Frucht ist besser als Gold und Feingold, / mein Nutzen übertrifft wertvolles Silber." },
      { number: 20, text: "Ich gehe auf dem Weg der Gerechtigkeit, / mitten auf den Pfaden des Rechtes," },
      { number: 21, text: "um denen, die mich lieben, Gaben zu verleihen / und ihre Scheunen zu füllen." },
      { number: 22, text: "Der Herr hat mich geschaffen im Anfang seiner Wege, / vor seinen Werken in der Urzeit;", footnotes: ["Weish 8,2-4", "22-33: Zur personifizierten Weisheit des Schöpfergottes vgl. die Einleitung. 22a: So mit S, Vg und der aramäischen Übersetzung; andere Übersetzungsmöglichkeit: als Erstling seiner Schöpfungswerke."] },
      { number: 23, text: "in frühester Zeit wurde ich gebildet, / am Anfang, beim Ursprung der Erde." },
      { number: 24, text: "Als die Urmeere noch nicht waren, / wurde ich geboren, / als es die Quellen noch nicht gab, die wasserreichen." },
      { number: 25, text: "Ehe die Berge eingesenkt wurden, / vor den Hügeln wurde ich geboren." },
      { number: 26, text: "Noch hatte er die Erde nicht gemacht und die Fluren / und alle Schollen des Festlands." },
      { number: 27, text: "Als er den Himmel baute, war ich dabei, / als er den Erdkreis abmaß über den Wassern,", footnotes: ["Ijob 28,24-27"] },
      { number: 28, text: "als er droben die Wolken befestigte / und Quellen strömen ließ aus dem Urmeer," },
      { number: 29, text: "als er dem Meer seine Satzung gab / und die Wasser nicht seinen Befehl übertreten durften," },
      { number: 30, text: "als er die Fundamente der Erde abmaß, / da war ich als geliebtes Kind bei ihm. Ich war seine Freude Tag für Tag / und spielte vor ihm allezeit." },
      { number: 31, text: "Ich spielte auf seinem Erdenrund / und meine Freude war es, bei den Menschen zu sein." },
      { number: 32, text: "Nun, ihr Söhne, hört auf mich! / Wohl dem, der auf meine Wege achtet." },
      { number: 33, text: "Hört die Mahnung und werdet weise, / lehnt sie nicht ab!" },
      { number: 34, text: "Wohl dem, der auf mich hört, / der Tag für Tag an meinen Toren wacht / und meine Türpfosten hütet." },
      { number: 35, text: "Wer mich findet, findet Leben / und erlangt das Gefallen des Herrn." },
      { number: 36, text: "Doch wer mich verfehlt, der schadet sich selbst; / alle, die mich hassen, lieben den Tod." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Die Weisheit hat ihr Haus gebaut, / ihre sieben Säulen behauen.", footnotes: ["1-18: Die Personifikation der Weisheit - auch der Torheit - wird beibehalten; beide laden zum Gastmahl ein. Dazwischen sind später zweimal drei Lehrsprüche (VV. 7-12) eingeschoben worden."] },
      { number: 2, text: "Sie hat ihr Vieh geschlachtet, ihren Wein gemischt / und schon ihren Tisch gedeckt." },
      { number: 3, text: "Sie hat ihre Mägde ausgesandt / und lädt ein auf der Höhe der Stadtburg:", footnotes: ["8,2"] },
      { number: 4, text: "Wer unerfahren ist, kehre hier ein. / Zum Unwissenden sagt sie:" },
      { number: 5, text: "Kommt, esst von meinem Mahl / und trinkt vom Wein, den ich mischte." },
      { number: 6, text: "Lasst ab von der Torheit, dann bleibt ihr am Leben, / und geht auf dem Weg der Einsicht!" },
      { number: 7, text: "Wer den Zuchtlosen tadelt, erntet Schimpf, / wer den Frevler rügt, erntet Schande." },
      { number: 8, text: "Rüge den Zuchtlosen nicht; sonst hasst er dich. / Rüge den Weisen, dann liebt er dich." },
      { number: 9, text: "Unterrichte den Weisen, damit er noch weiser wird; / belehre den Gerechten, damit er dazulernt." },
      { number: 10, text: "Anfang der Weisheit ist die Gottesfurcht, / die Kenntnis des Heiligen ist Einsicht.", footnotes: ["1,7"] },
      { number: 11, text: "Ja, durch mich werden deine Tage zahlreich, / nehmen die Jahre deines Lebens zu." },
      { number: 12, text: "Bist du weise, so bist du weise zum eigenen Nutzen, / bist du aber unbeherrscht, hast du allein es zu tragen.", footnotes: ["11,17"] },
      { number: 13, text: "Frau Torheit fiebert nach Verführung; / das ist alles, was sie versteht." },
      { number: 14, text: "Sie sitzt vor der Tür ihres Hauses / auf einem Sessel bei der Stadtburg," },
      { number: 15, text: "um die Vorübergehenden einzuladen, / die geradeaus ihre Pfade gehen:" },
      { number: 16, text: "Wer unerfahren ist, kehre hier ein. / Zum Unwissenden sagt sie:" },
      { number: 17, text: "Süß ist gestohlenes Wasser, / heimlich entwendetes Brot schmeckt lecker." },
      { number: 18, text: "Und er weiß nicht, dass Totengeister dort hausen, / dass ihre Gäste in den Tiefen der Unterwelt sind.", footnotes: ["7,27"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Sprichwörter Salomos: Ein kluger Sohn macht dem Vater Freude, / ein dummer Sohn ist der Kummer seiner Mutter.", heading: "Die erste salomonische Spruchsammlung: 10,1 - 22,16", footnotes: ["1f: Im Unterschied zum Vorausgehenden folgen nun in der ersten salomonischen Sammlung 375 Sprichwörter, größtenteils Zweizeiler in dichterischem Parallelismus. Ihr Alter reicht von König Salomo (etwa 930 v. Chr.) bis zum letzten nachexilischen Redaktor (um 200 v. Chr.). Wahrscheinlich liegen zwei Teilsammlungen vor: 10,1 - 15,33 und 16,1 - 22,16. - 10,1b lautet genau wie 15,20a, 2b wie 11,4b. Die ältesten Sprichwörter bestanden wohl nur aus Einzeilern."] },
      { number: 2, text: "Unrecht Gut gedeiht nicht, / Gerechtigkeit aber rettet vor dem Tod.", footnotes: ["2b: Gerechtigkeit, andere Übersetzungsmöglichkeit: Mildtätigkeit (vgl. Sir 3,30b; Tob 4,10f; 12,9)."] },
      { number: 3, text: "Das Verlangen des Gerechten sättigt der Herr, / die Gier der Frevler stößt er zurück." },
      { number: 4, text: "Lässige Hand bringt Armut, / fleißige Hand macht reich.", footnotes: ["Vg schiebt noch ein (= G hinter 9,12): Wer sich aufs Lügen stützt, der weidet Wind; er jagt hinter fliegenden Vögeln her."] },
      { number: 5, text: "Wer im Sommer sammelt, ist ein kluger Mensch; / in Schande gerät, wer zur Erntezeit schläft." },
      { number: 6, text: "Segen ruht auf dem Haupt des Gerechten, / im Mund der Frevler versteckt sich Gewalttat." },
      { number: 7, text: "Das Andenken des Gerechten ist gesegnet, / der Name der Frevler vermodert." },
      { number: 8, text: "Verständiger Sinn nimmt die Gebote an, / wer Törichtes redet, kommt zu Fall." },
      { number: 9, text: "Wer aufrichtig seinen Weg geht, geht sicher, / wer krumme Wege geht, wird durchschaut." },
      { number: 10, text: "Wer mit den Augen zwinkert, schafft Leid, / wer offen tadelt, stiftet Frieden.", footnotes: ["6,13; 16,30", "10b: So nach G."] },
      { number: 11, text: "Der Mund des Gerechten ist ein Lebensquell, / im Mund der Frevler versteckt sich Gewalttat." },
      { number: 12, text: "Hass weckt Streit, / Liebe deckt alle Vergehen zu." },
      { number: 13, text: "Auf den Lippen des Einsichtigen findet man Weisheit, / auf den Rücken des Unverständigen passt der Stock.", footnotes: ["19,29; 26,3"] },
      { number: 14, text: "Weise verbergen ihr Wissen, / der Mund des Toren ist drohendes Verderben." },
      { number: 15, text: "Dem Reichen ist seine Habe eine feste Burg, / dem Armen bringt seine Armut Verderben." },
      { number: 16, text: "Der Besitz des Gerechten führt zum Leben, / das Einkommen des Frevlers zur Sünde." },
      { number: 17, text: "Den Weg zum Leben geht, wer Zucht bewahrt; / wer Warnung missachtet, geht in die Irre." },
      { number: 18, text: "Wer Hass verbirgt, heuchelt; / wer Verleumdung ausstreut, ist ein Tor." },
      { number: 19, text: "Bei vielem Reden bleibt die Sünde nicht aus, / wer seine Lippen zügelt, ist klug." },
      { number: 20, text: "Erlesenes Silber ist die Zunge des Gerechten, / das Sinnen des Frevlers ist wenig wert." },
      { number: 21, text: "Die Lippen des Gerechten leiten viele, / die Toren sterben an Unverstand." },
      { number: 22, text: "Der Segen des Herrn macht reich, / eigene Mühe tut nichts hinzu." },
      { number: 23, text: "Des Toren Freude ist es, Böses zu tun, / des Verständigen Freude, weise zu sein." },
      { number: 24, text: "Was der Frevler fürchtet, kommt über ihn, / was die Gerechten ersehnen, wird ihnen zuteil.", footnotes: ["Ps 7,4-6; Weish 11,16"] },
      { number: 25, text: "Wenn der Sturm daherbraust, ist der Frevler verloren, / der Gerechte ist fest gegründet für immer.", footnotes: ["Mt 7,24-27"] },
      { number: 26, text: "Wie Essig für die Zähne und Rauch für die Augen / ist der Faule für den, der ihn schickt." },
      { number: 27, text: "Gottesfurcht bringt langes Leben, / doch die Jahre der Frevler sind verkürzt." },
      { number: 28, text: "Die Hoffnung der Gerechten blüht auf, / die Erwartung der Frevler wird zunichte." },
      { number: 29, text: "Dem Schuldlosen ist der Herr eine Zuflucht, / Verderben aber den Übeltätern." },
      { number: 30, text: "Der Gerechte wird niemals wanken, / doch die Frevler bleiben nicht im Land wohnen." },
      { number: 31, text: "Der Mund des Gerechten bringt Weisheit hervor, / eine Zunge voll Falschheit aber wird abgeschnitten." },
      { number: 32, text: "Die Lippen des Gerechten achten auf das, was gefällt, / der Mund der Frevler aber auf das, was verkehrt ist." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Falsche Waage ist dem Herrn ein Gräuel, / volles Gewicht findet sein Gefallen.", footnotes: ["Dtn 25,13-16"] },
      { number: 2, text: "Kommt Übermut, kommt auch Schande, / doch bei den Bescheidenen ist die Weisheit zu Hause." },
      { number: 3, text: "Die Redlichen leitet ihre Lauterkeit, / die Verräter richtet ihre Falschheit zugrunde." },
      { number: 4, text: "Reichtum hilft nicht am Tag des Zorns, / Gerechtigkeit aber rettet vor dem Tod." },
      { number: 5, text: "Dem Lauteren ebnet seine Gerechtigkeit den Weg, / der Frevler aber kommt durch seine Bosheit zu Fall." },
      { number: 6, text: "Die Redlichen rettet ihre Gerechtigkeit, / die Verräter schlägt ihre eigene Gier in Fesseln." },
      { number: 7, text: "Beim Tod des Frevlers wird sein Hoffen zunichte, / die falsche Erwartung schwindet dahin.", footnotes: ["Text unsicher, da in G abweichend."] },
      { number: 8, text: "Der Gerechte wird aus der Not gerettet, / an seine Stelle tritt der Böse." },
      { number: 9, text: "Vom Mund des Ruchlosen droht dem Nächsten Verderben, / die Gerechten befreien sich durch ihre Umsicht." },
      { number: 10, text: "Wenn es dem Gerechten gut geht, freut sich die Stadt; / sie jubelt beim Untergang der Frevler." },
      { number: 11, text: "Eine Stadt kommt hoch durch den Segen der Redlichen, / durch den Mund der Frevler wird sie niedergerissen." },
      { number: 12, text: "Wer den Nächsten verächtlich macht, / ist ohne Verstand, / doch ein kluger Mensch schweigt." },
      { number: 13, text: "Wer als Verleumder umhergeht, / gibt Geheimnisse preis, / der Verlässliche behält eine Sache für sich.", footnotes: ["20,19; Sir 27,17"] },
      { number: 14, text: "Fehlt es an Führung, kommt ein Volk zu Fall, / Rettung ist dort, wo viele Ratgeber sind.", footnotes: ["13,10; Sir 37,7-14"] },
      { number: 15, text: "Wer für einen Fremden bürgt, ist übel daran; / wer den Handschlag ablehnt, geht sicher.", footnotes: ["6,1-5"] },
      { number: 16, text: "Eine liebenswerte Frau kommt zu Ehren, / Sitz der Schande ist ein Weib, das gute Sitten hasst. / Die Faulen bringen es zu nichts, / wer fleißig ist, kommt zu Reichtum.", footnotes: ["So nach G."] },
      { number: 17, text: "Die Güte eines Menschen kommt ihm selbst zugute, / der Hartherzige schneidet sich ins eigene Fleisch.", footnotes: ["9,12"] },
      { number: 18, text: "Der Frevler erzielt trügerischen Gewinn, / wer Gerechtigkeit sät, hat beständigen Ertrag." },
      { number: 19, text: "Wer in der Gerechtigkeit fest steht, erlangt das Leben, / wer dem Bösen nachjagt, den Tod.", footnotes: ["12,28; Dtn 30,15f"] },
      { number: 20, text: "Verkehrte Menschen sind dem Herrn ein Gräuel, / er hat Gefallen an denen, die den rechten Weg gehen." },
      { number: 21, text: "Gewiss, der Böse bleibt nicht ungestraft, / doch die Söhne der Gerechten werden gerettet.", footnotes: ["16,5; Ps 112,2"] },
      { number: 22, text: "Ein goldener Ring im Rüssel eines Schweins / ist ein Weib, schön, aber sittenlos." },
      { number: 23, text: "Das Begehren der Gerechten führt zu vollem Glück, / die Hoffnung der Frevler endet im Zorngericht." },
      { number: 24, text: "Mancher teilt aus und bekommt immer mehr, / ein anderer kargt übers Maß und wird doch ärmer.", footnotes: ["Ps 37,21f"] },
      { number: 25, text: "Wer wohltätig ist, wird reich gesättigt, / wer andere labt, wird selbst gelabt." },
      { number: 26, text: "Wer Getreide zurückhält, den verwünschen die Leute, / wer Korn auf den Markt bringt, auf dessen Haupt kommt Segen." },
      { number: 27, text: "Wer Gutes erstrebt, sucht das Gefallen Gottes; / wer nach dem Bösen trachtet, den trifft es." },
      { number: 28, text: "Wer auf seinen Reichtum vertraut, der fällt, / die Gerechten aber sprossen wie grünes Laub." },
      { number: 29, text: "Wer sein Haus verkommen lässt, erntet Wind, / und der Tor wird Sklave des Weisen.", footnotes: ["Koh 2,11"] },
      { number: 30, text: "Die Frucht der Gerechtigkeit ist ein Lebensbaum, / Gewalttat raubt die Lebenskraft.", footnotes: ["30b: So nach G."] },
      { number: 31, text: "Wird dem Gerechten vergolten auf der Erde, / dann erst recht dem Frevler und Sünder.", footnotes: ["1 Petr 4,18"] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Wer Zucht liebt, liebt Erkenntnis, / wer Zurechtweisung hasst, ist dumm." },
      { number: 2, text: "Der Gute findet Gefallen beim Herrn, / den Heimtückischen aber spricht er schuldig." },
      { number: 3, text: "Wer unrecht tut, hat keinen Bestand, / doch die Wurzel der Gerechten sitzt fest." },
      { number: 4, text: "Eine tüchtige Frau ist die Krone ihres Mannes, / eine schändliche ist wie Fäulnis in seinen Knochen.", footnotes: ["31,11f"] },
      { number: 5, text: "Die Gedanken der Gerechten trachten nach Recht, / die Pläne der Frevler sind auf Betrug aus." },
      { number: 6, text: "Die Reden der Frevler sind ein Lauern auf Blut, / die Redlichen rettet ihr Mund." },
      { number: 7, text: "Die Frevler werden gestürzt und sind dahin, / das Haus der Gerechten hat Bestand." },
      { number: 8, text: "Nach dem Maß seiner Klugheit wird ein jeder gelobt, / verkehrter Sinn fällt der Verachtung anheim." },
      { number: 9, text: "Besser unbeachtet bleiben / und seine Arbeit verrichten, / als großtun und kein Brot haben." },
      { number: 10, text: "Der Gerechte weiß, was sein Vieh braucht, / doch das Herz der Frevler ist hart.", footnotes: ["Dtn 25,4; Sir 7,22"] },
      { number: 11, text: "Wer sein Feld bestellt, wird satt von Brot, / wer nichtigen Dingen nachjagt, ist ohne Verstand.", footnotes: ["28,19"] },
      { number: 12, text: "Schwankender Lehm ist die Burg der Bösen, / die Wurzel der Gerechten hat festen Grund.", footnotes: ["Text verderbt; die alten Übersetzungen sind kaum verständlich."] },
      { number: 13, text: "Der Böse verfängt sich im Lügengespinst, / der Gerechte entkommt der Bedrängnis." },
      { number: 14, text: "Von der Frucht seines Mundes wird der Mensch reichlich gesättigt, / nach dem Tun seiner Hände wird ihm vergolten." },
      { number: 15, text: "Der Tor hält sein eigenes Urteil für richtig, / der Weise aber hört auf Rat." },
      { number: 16, text: "Der Tor zeigt sogleich seinen Ärger, / klug ist, wer Schimpfworte einsteckt." },
      { number: 17, text: "Wer Wahrheit spricht, sagt aus, was recht ist, / der falsche Zeuge aber betrügt." },
      { number: 18, text: "Mancher Leute Gerede verletzt wie Schwertstiche, / die Zunge der Weisen bringt Heilung.", footnotes: ["Ps 57,5"] },
      { number: 19, text: "Ein Mund, der die Wahrheit sagt, / hat für immer Bestand, / eine lügnerische Zunge nur einen Augenblick." },
      { number: 20, text: "Wer auf Böses sinnt, betrügt sich selbst, / wer heilsamen Rat gibt, erntet Freude." },
      { number: 21, text: "Kein Unheil trifft den Gerechten, / doch die Frevler erdrückt das Unglück." },
      { number: 22, text: "Lügnerische Lippen sind dem Herrn ein Gräuel, / doch wer zuverlässig ist in seinem Tun, der gefällt ihm." },
      { number: 23, text: "Ein kluger Mensch verbirgt sein Wissen, / das Herz der Toren schreit die Narrheit hinaus." },
      { number: 24, text: "Die Hand der Fleißigen erringt die Herrschaft, / die lässige Hand muss Frondienste leisten." },
      { number: 25, text: "Kummer im Herzen bedrückt den Menschen, / ein gutes Wort aber heitert ihn auf." },
      { number: 26, text: "Der Gerechte findet fette Weide, / der Weg der Frevler führt in die Irre.", footnotes: ["26-28: Der Text ist schlecht erhalten und darum schwer zu deuten."] },
      { number: 27, text: "Bequemlichkeit erjagt sich kein Wild, / kostbare Güter erlangt der Fleißige." },
      { number: 28, text: "Der Pfad der Gerechtigkeit führt zum Leben, / der Weg der Abtrünnigen führt zum Tod." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Ein weiser Sohn ist die Frucht der Erziehung des Vaters, / der zuchtlose aber hört nicht auf Mahnung." },
      { number: 2, text: "Von der Frucht seiner Worte zehrt der Gute, / aber die Verräter begehren Gewalttat." },
      { number: 3, text: "Wer seine Lippen hütet, bewahrt sein Leben, / wer seinen Mund aufreißt, den trifft Verderben." },
      { number: 4, text: "Das Verlangen des Faulen regt sich vergebens, / das Verlangen der Fleißigen wird befriedigt." },
      { number: 5, text: "Verlogene Worte hasst der Gerechte, / der Frevler handelt schändlich und schimpflich." },
      { number: 6, text: "Gerechtigkeit behütet den Schuldlosen auf seinem Weg, / Frevler bringt die Sünde zu Fall." },
      { number: 7, text: "Mancher stellt sich reich und hat doch nichts, / ein anderer stellt sich arm und hat großen Besitz." },
      { number: 8, text: "Der Reichtum eines Mannes ist das Lösegeld für sein Leben, / der Arme jedoch hört nichts von Loskauf." },
      { number: 9, text: "Das Licht der Gerechten strahlt auf, / die Lampe der Frevler erlischt." },
      { number: 10, text: "Der Leichtsinnige stiftet aus Übermut Zank, / doch wer sich beraten lässt, der ist klug." },
      { number: 11, text: "Schnell errafftes Gut schwindet schnell, / wer Stück für Stück sammelt, wird reich." },
      { number: 12, text: "Hingehaltene Hoffnung macht das Herz krank, / erfülltes Verlangen ist ein Lebensbaum." },
      { number: 13, text: "Wer gute Worte missachtet, erleidet Schaden, / wer Ehrfurcht hat vor dem Gebot, bleibt unversehrt.", footnotes: ["Vg schiebt hier (G hinter V. 9) ein: Trügerische Seelen irren in Sünden; die Gerechten aber haben Mitleid und Erbarmen."] },
      { number: 14, text: "Die Lehre des Weisen ist ein Lebensquell, / um den Schlingen des Todes zu entgehen.", footnotes: ["14,27"] },
      { number: 15, text: "Rechte Einsicht bringt Gunst, / aber den Verrätern bringt ihr Verhalten den Untergang." },
      { number: 16, text: "Der Kluge tut alles mit Überlegung, / der Tor verbreitet nur Dummheit." },
      { number: 17, text: "Ein gewissenloser Bote richtet Unheil an, / ein zuverlässiger Bote bringt Heilung." },
      { number: 18, text: "Armut und Schande erntet ein Verächter der Zucht, / doch wer Tadel beherzigt, wird geehrt." },
      { number: 19, text: "Ein erfüllter Wunsch tut dem Herzen wohl, / vom Bösen zu lassen ist dem Toren ein Gräuel." },
      { number: 20, text: "Wer mit Weisen unterwegs ist, wird weise, / wer mit Toren verkehrt, dem geht es übel." },
      { number: 21, text: "Unglück verfolgt die Sünder, / den Gerechten wird mit Gutem vergolten." },
      { number: 22, text: "Der Gute hinterlässt seinen Enkeln das Erbe, / der Besitz des Sünders wird für den Gerechten aufgespart.", footnotes: ["Ijob 27,13-17"] },
      { number: 23, text: "In der Hand der Vornehmen ist reichlich Nahrung, / der Arme wird zu Unrecht dahingerafft.", footnotes: ["Text unsicher."] },
      { number: 24, text: "Wer die Rute spart, hasst seinen Sohn, / wer ihn liebt, nimmt ihn früh in Zucht.", footnotes: ["29,17"] },
      { number: 25, text: "Der Gerechte hat zu essen, bis sein Hunger gestillt ist, / der Bauch der Frevler aber muss darben." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Frau Weisheit hat ihr Haus gebaut, / die Torheit reißt es nieder mit eigenen Händen." },
      { number: 2, text: "Wer geradeaus seinen Weg geht, fürchtet den Herrn, / wer krumme Wege geht, verachtet ihn." },
      { number: 3, text: "Im Mund des Toren ist eine Rute für seinen Rücken, / den Weisen behüten seine Lippen.", footnotes: ["26,3"] },
      { number: 4, text: "Wo keine Ochsen sind, bleibt die Krippe leer, / reicher Ertrag kommt durch die Kraft des Stieres." },
      { number: 5, text: "Ein zuverlässiger Zeuge lügt nicht, / aber ein falscher Zeuge flüstert Lügen zu." },
      { number: 6, text: "Der Zuchtlose sucht Weisheit, doch vergebens, / dem Verständigen fällt die Erkenntnis leicht." },
      { number: 7, text: "Tritt einem törichten Mann gegenüber / und du erfährst keine verständigen Worte." },
      { number: 8, text: "Die Weisheit des Klugen gibt ihm Einsicht in seinen Weg, / aber die Dummheit der Toren führt zu Täuschung." },
      { number: 9, text: "In den Zelten der Toren wohnt Schuld, / das Haus der Rechtschaffenen findet Gefallen.", footnotes: ["Text unsicher; korr. nach G."] },
      { number: 10, text: "Das Herz allein kennt seinen Kummer, / auch in seine Freude mischt sich kein Fremder." },
      { number: 11, text: "Das Haus der Frevler wird zertrümmert, / das Zelt der Redlichen gedeiht." },
      { number: 12, text: "Manch einem scheint sein Weg der rechte, / aber am Ende sind es Wege des Todes.", footnotes: ["16,25; Sir 21,10; Mt 7,13"] },
      { number: 13, text: "Auch beim Lachen kann ein Herz leiden, / das Ende der Freude ist Gram." },
      { number: 14, text: "Dem Untreuen werden seine Vergehen vergolten, / dem guten Menschen seine edlen Taten." },
      { number: 15, text: "Der Unerfahrene traut jedem Wort, / der Kluge achtet auf seinen Schritt." },
      { number: 16, text: "Der Weise hat Scheu und meidet das Böse, / der Tor lässt sich gehen und ist vermessen." },
      { number: 17, text: "Der Zornige handelt töricht, / der Ränkeschmied ist verhasst.", footnotes: ["17b: Nach S wäre die Antithese zu lesen: der Besonnene aber bleibt ruhig."] },
      { number: 18, text: "Die Unerfahrenen verfallen der Torheit, / die Klugen krönen sich mit Erkenntnis." },
      { number: 19, text: "Die Bösen müssen sich bücken vor den Guten / und die Frevler an der Tür des Gerechten.", footnotes: ["Lk 16,24-31"] },
      { number: 20, text: "Selbst seinem Nächsten ist der Arme verhasst, / der Reiche aber hat viele Freunde.", footnotes: ["19,4; Sir 13,21-23"] },
      { number: 21, text: "Wer seinen Nächsten verachtet, sündigt; / wohl dem, der Erbarmen hat mit den Notleidenden.", footnotes: ["Mt 5,7"] },
      { number: 22, text: "Gewiss geht in die Irre, wer Böses plant; / Liebe und Treue erlangt, wer Gutes plant." },
      { number: 23, text: "Jede Arbeit bringt Erfolg, / leeres Geschwätz führt nur zu Mangel." },
      { number: 24, text: "Die Krone der Weisen ist ihre Klugheit, / der Kranz der Toren ist ihre Narrheit.", footnotes: ["Text und Übersetzung unsicher."] },
      { number: 25, text: "Ein verlässlicher Zeuge rettet Leben, / wer Lügen zuflüstert, der täuscht." },
      { number: 26, text: "Der Gottesfürchtige hat feste Zuversicht, / noch seine Söhne haben eine Zuflucht." },
      { number: 27, text: "Die Gottesfurcht ist ein Lebensquell, / um den Schlingen des Todes zu entgehen.", footnotes: ["13,14"] },
      { number: 28, text: "Viel Volk ist der Glanz des Königs, / wenig Leute sind des Fürsten Untergang." },
      { number: 29, text: "Der Langmütige ist immer der Klügere, / der Jähzornige treibt die Torheit auf die Spitze." },
      { number: 30, text: "Ein gelassenes Herz bedeutet Leben für den Leib, / doch Knochenfraß ist die Leidenschaft." },
      { number: 31, text: "Wer den Geringen bedrückt, schmäht dessen Schöpfer, / ihn ehrt, wer Erbarmen hat mit dem Bedürftigen.", footnotes: ["17,5"] },
      { number: 32, text: "Durch seine Bosheit wird der Frevler gestürzt, / der Gerechte findet Zuflucht in seiner Redlichkeit.", footnotes: ["32b: Redlichkeit: nach G; H und Vg kaum richtig: in seinem Tod."] },
      { number: 33, text: "Im Herzen des Verständigen ruht Weisheit, / im Innern der Toren ist sie nicht bekannt." },
      { number: 34, text: "Gerechtigkeit erhöht ein Volk, / der Völker Schmach ist die Sünde." },
      { number: 35, text: "Die Gunst des Königs ruht auf dem klugen Diener, / den schändlichen aber trifft sein Zorn." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Eine sanfte Antwort dämpft die Erregung, / eine kränkende Rede reizt zum Zorn." },
      { number: 2, text: "Die Zunge der Weisen verkündet Erkenntnis, / der Mund der Narren sprudelt Torheit hervor." },
      { number: 3, text: "An jedem Ort sind die Augen des Herrn, / sie wachen über Gute und Böse." },
      { number: 4, text: "Eine sanfte Zunge ist ein Lebensbaum, / eine falsche Zunge bricht das Herz." },
      { number: 5, text: "Der Tor verschmäht die Zucht seines Vaters, / wer auf Zurechtweisung achtet, ist klug." },
      { number: 6, text: "Im Haus des Gerechten gibt es reichen Vorrat, / was der Frevler erwirbt, wird zerschlagen." },
      { number: 7, text: "Die Lippen der Weisen streuen Erkenntnis aus, / das Herz der Toren ist verkehrt." },
      { number: 8, text: "Das Opfer der Frevler ist dem Herrn ein Gräuel, / am Gebet der Rechtschaffenen aber hat er Gefallen.", footnotes: ["21,27; Sir 7,9"] },
      { number: 9, text: "Ein Gräuel ist dem Herrn der Weg des Frevlers, / wer aber der Gerechtigkeit nachjagt, den liebt er." },
      { number: 10, text: "Schlimme Strafe trifft den, der den rechten Pfad verlässt, / wer Zurechtweisung hasst, muss sterben." },
      { number: 11, text: "Totenreich und Unterwelt liegen offen vor dem Herrn, / wie viel mehr die Herzen der Menschen.", footnotes: ["Ps 139,8; Sir 42,18"] },
      { number: 12, text: "Ein Zuchtloser liebt es nicht, dass man ihn rügt, / zu weisen Menschen begibt er sich nicht." },
      { number: 13, text: "Ein fröhliches Herz macht das Gesicht heiter, / Kummer im Herzen bedrückt das Gemüt.", footnotes: ["Sir 13,25f"] },
      { number: 14, text: "Das Herz des Verständigen sucht Erkenntnis, / der Mund der Toren ergeht sich in Torheit." },
      { number: 15, text: "Der Bedrückte hat lauter böse Tage, / der Frohgemute hat ständig Feiertag." },
      { number: 16, text: "Besser wenig in Gottesfurcht / als reiche Schätze und keine Ruhe.", footnotes: ["17,1; Ps 37,16", "Solche Lehrsätze zeigen, dass der materielle Nutzen der Weisheit, der zwar öfter hervorgehoben wird, nicht das höchste Glück ausmacht."] },
      { number: 17, text: "Besser ein Gericht Gemüse, wo Liebe herrscht, / als ein gemästeter Ochse und Hass dabei." },
      { number: 18, text: "Ein hitziger Mensch erregt Zank, / ein langmütiger besänftigt den Streit." },
      { number: 19, text: "Der Weg des Faulen ist wie ein Dornengestrüpp, / der Pfad der Redlichen aber ist gebahnt." },
      { number: 20, text: "Ein kluger Sohn macht dem Vater Freude, / nur ein törichter Mensch verachtet seine Mutter." },
      { number: 21, text: "Torheit macht dem Unverständigen Freude, / der einsichtige Mann geht den geraden Weg." },
      { number: 22, text: "Wo es an Beratung fehlt, da scheitern die Pläne, / wo viele Ratgeber sind, gibt es Erfolg." },
      { number: 23, text: "Jeden freut es, wenn er (kluge) Antwort geben kann, / und wie gut ist doch ein Wort zur rechten Zeit.", footnotes: ["25,11"] },
      { number: 24, text: "Einen Lebenspfad zur Höhe gibt es für den Klugen, / damit er der Totenwelt drunten entgeht." },
      { number: 25, text: "Das Haus der Stolzen reißt der Herr nieder, / den Grenzstein der Witwe aber macht er fest.", footnotes: ["Dtn 19,14"] },
      { number: 26, text: "Die Pläne des Bösen sind dem Herrn ein Gräuel, / aber freundliche Reden gefallen ihm.", footnotes: ["26b: gefallen ihm, wörtlich: sind rein; «rein» ist kultischer Ausdruck, Gegensatz zu «Gräuel»."] },
      { number: 27, text: "Wer sich durch Raub bereichert, zerstört sein Haus, / wer Bestechung von sich weist, wird lange leben.", footnotes: ["Jes 1,23"] },
      { number: 28, text: "Der Gerechte überlegt sich im Herzen jede Antwort, / aber der Mund der Frevler sprudelt Schlechtes hervor.", footnotes: ["19,28", "Die Beherrschung der Zunge ist ein Lieblingsthema der Weisen."] },
      { number: 29, text: "Fern ist der Herr den Frevlern, / doch das Gebet der Gerechten hört er." },
      { number: 30, text: "Strahlende Augen erfreuen das Herz, / frohe Kunde labt den Leib." },
      { number: 31, text: "Ein Ohr, das auf heilsame Mahnungen hört, / hält sich unter den Weisen auf." },
      { number: 32, text: "Wer Zucht abweist, verachtet sich selbst; / wer aber auf Mahnungen hört, erwirbt Verstand." },
      { number: 33, text: "Gottesfurcht erzieht zur Weisheit / und Demut geht der Ehre voran." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Der Mensch entwirft die Pläne im Herzen, / doch vom Herrn kommt die Antwort auf der Zunge." },
      { number: 2, text: "Jeder meint, sein Verhalten sei fehlerlos, / doch der Herr prüft die Geister.", footnotes: ["21,2"] },
      { number: 3, text: "Befiehl dem Herrn dein Tun an, / so werden deine Pläne gelingen.", footnotes: ["Ps 37,5; 1 Petr 5,7"] },
      { number: 4, text: "Alles hat der Herr für seinen Zweck erschaffen, / so auch den Frevler für den Tag des Unheils.", footnotes: ["4b: Kurze und überspitzte Redeweise des Volksmunds."] },
      { number: 5, text: "Ein Gräuel ist dem Herrn jeder Hochmütige, / er bleibt gewiss nicht ungestraft.", footnotes: ["11,21"] },
      { number: 6, text: "Durch Liebe und Treue wird Schuld gesühnt, / durch Gottesfurcht weicht man dem Bösen aus." },
      { number: 7, text: "Gefallen dem Herrn die Wege eines Menschen, / so versöhnt er auch seine Feinde mit ihm." },
      { number: 8, text: "Besser wenig und gerecht / als viel Besitz und Unrecht.", footnotes: ["15,16"] },
      { number: 9, text: "Des Menschen Herz plant seinen Weg, / doch der Herr lenkt seinen Schritt." },
      { number: 10, text: "Gottesentscheid kommt von den Lippen des Königs, / sein Mund verfehlt sich nicht, wenn er ein Urteil fällt.", footnotes: ["25,2"] },
      { number: 11, text: "Rechte Waage und Waagschalen sind Sache des Herrn, / sein Werk sind alle Gewichtssteine im Beutel.", footnotes: ["Mi 6,11"] },
      { number: 12, text: "Frevlerisches Tun ist Königen ein Gräuel, / denn ein Thron steht fest durch Gerechtigkeit.", footnotes: ["25,5"] },
      { number: 13, text: "Gerechte Lippen gefallen dem König, / wer aufrichtig redet, den liebt er." },
      { number: 14, text: "Des Königs Grimm gleicht Todesboten, / aber ein Weiser kann ihn besänftigen.", footnotes: ["19,12"] },
      { number: 15, text: "Im leuchtenden Gesicht des Königs liegt Leben, / sein Wohlwollen gleicht der Regenwolke im Frühjahr.", footnotes: ["15b: Im März und April fällt der sehnlichst erwartete «Spätregen»."] },
      { number: 16, text: "Weisheit erwerben ist besser als Gold, / Einsicht erwerben vortrefflicher als Silber." },
      { number: 17, text: "Böses zu meiden ist das Ziel der Rechtschaffenen; / wer auf seinen Weg achtet, bewahrt sein Leben." },
      { number: 18, text: "Hoffart kommt vor dem Sturz / und Hochmut kommt vor dem Fall." },
      { number: 19, text: "Besser bescheiden sein mit Demütigen, / als Beute teilen mit Stolzen." },
      { number: 20, text: "Wer auf das Wort des Herrn achtet, findet Glück; / wohl dem, der auf ihn vertraut." },
      { number: 21, text: "Wer ein weises Herz hat, den nennt man verständig, / gefällige Rede fördert die Belehrung.", footnotes: ["Sir 6,5"] },
      { number: 22, text: "Wer Verstand besitzt, dem ist er ein Lebensquell, / die Strafe der Toren ist die Torheit selbst." },
      { number: 23, text: "Das Herz des Weisen macht seinen Mund klug, / es mehrt auf seinen Lippen die Belehrung." },
      { number: 24, text: "Freundliche Worte sind wie Wabenhonig, / süß für den Gaumen, heilsam für den Leib." },
      { number: 25, text: "Manch einem scheint sein Weg der rechte, / aber am Ende sind es Wege des Todes.", footnotes: ["14,12"] },
      { number: 26, text: "Der Hunger des Arbeiters arbeitet für ihn, / denn sein Mund treibt ihn an.", footnotes: ["Koh 6,7"] },
      { number: 27, text: "Ein Taugenichts ist ein Ofen voll Unheil, / auf seinen Lippen ist es wie sengendes Feuer." },
      { number: 28, text: "Ein tückischer Mensch erregt Streit, / ein Verleumder entzweit Freunde." },
      { number: 29, text: "Der Gewalttätige verführt seinen Nächsten, / er bringt ihn auf einen Weg, der nicht gut ist." },
      { number: 30, text: "Wer mit den Augen zwinkert, sinnt auf Tücke; / wer die Lippen verzieht, hat das Böse schon vollbracht.", footnotes: ["6,13; 10,10"] },
      { number: 31, text: "Graues Haar ist eine prächtige Krone, / auf dem Weg der Gerechtigkeit findet man sie." },
      { number: 32, text: "Besser ein Langmütiger als ein Kriegsheld, / besser, wer sich selbst beherrscht, als wer Städte erobert." },
      { number: 33, text: "Im Bausch des Gewandes schüttelt man das Los, / doch jede Entscheidung kommt allein vom Herrn.", footnotes: ["18,18; 1 Sam 14,41f"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Besser ein trockenes Stück Brot und Ruhe dabei / als ein Haus voll Braten und dabei Streit.", footnotes: ["15,16f"] },
      { number: 2, text: "Ein kluger Knecht wird Herr über einen missratenen Sohn / und mit den Brüdern teilt er das Erbe." },
      { number: 3, text: "Der Schmelztiegel ist für Silber da, der Ofen für Gold, / die Herzen aber prüft der Herr.", footnotes: ["1 Petr 1,7"] },
      { number: 4, text: "Der Übeltäter achtet auf böse Lippen, / der Lügner horcht hin auf eine verderbte Zunge." },
      { number: 5, text: "Wer den Armen verspottet, schmäht dessen Schöpfer, / wer sich über ein Unglück freut, bleibt nicht ungestraft.", footnotes: ["14,31"] },
      { number: 6, text: "Eine Krone der Alten sind Kindeskinder, / der Kinder Ruhm sind ihre Väter." },
      { number: 7, text: "Dem Toren stehen hochtönende Worte nicht an, / noch viel weniger dem Edlen die Sprache der Lüge." },
      { number: 8, text: "Bestechungsgeld ist ein Zauberstein in den Augen des Gebers; / wohin er sich wendet, hat er Erfolg.", footnotes: ["18,16"] },
      { number: 9, text: "Wer Fehler zudeckt, sucht Freundschaft; / wer eine Sache weiterträgt, trennt Freunde." },
      { number: 10, text: "Tadel erschüttert einen Verständigen mehr / als hundert Schläge einen Toren." },
      { number: 11, text: "Der Böse trachtet nach Aufruhr, / aber ein strenger Gerichtsbote wird gegen ihn ausgesandt." },
      { number: 12, text: "Lieber einer Bärin begegnen, der man die Jungen geraubt hat, / als einem Toren in seinem Unverstand." },
      { number: 13, text: "Vergilt einer Gutes mit Bösem, / weicht das Unheil nicht von seinem Haus." },
      { number: 14, text: "Wer Streit anfängt, entfesselt eine Wasserflut, / drum halt ein, ehe der Zank ausbricht." },
      { number: 15, text: "Wer Schuldige freispricht / und wer Unschuldige verurteilt, / beide sind dem Herrn ein Gräuel." },
      { number: 16, text: "Wozu denn Geld in der Hand des Toren? / Etwa um Weisheit zu kaufen, da ihm doch der Verstand fehlt?" },
      { number: 17, text: "Der Freund erweist zu jeder Zeit Liebe, / als Bruder für die Not ist er geboren.", footnotes: ["18,24; Sir 37,5"] },
      { number: 18, text: "Ohne Verstand ist, wer Handschlag leistet, / wer Bürgschaft übernimmt für einen andern.", footnotes: ["6,1-5"] },
      { number: 19, text: "Verbrechen liebt, wer Streit liebt; / wer seine Tür zu hoch macht, will den Einsturz." },
      { number: 20, text: "Wer ein unaufrichtiges Herz hat, findet kein Glück, / wer sich beim Reden verstellt, stürzt ins Unheil." },
      { number: 21, text: "Wer einen Toren zeugt, dem bringt es Gram; / der Vater eines Narren kann sich nicht freuen." },
      { number: 22, text: "Ein fröhliches Herz tut dem Leib wohl, / ein bedrücktes Gemüt lässt die Glieder verdorren." },
      { number: 23, text: "Bestechung aus dem Gewandbausch nimmt der Frevler an, / um die Pfade des Rechts zu verkehren.", footnotes: ["21,14", "23a: Die bauschigen Falten des Gewands über der Brust dienten als Tasche."] },
      { number: 24, text: "Vor dem Blick des Verständigen steht Weisheit, / doch die Augen des Toren schweifen bis ans Ende der Erde." },
      { number: 25, text: "Ein törichter Sohn bereitet seinem Vater Verdruss / und Kummer seiner Mutter, die ihn geboren hat.", footnotes: ["19,13; Sir 16,1-3"] },
      { number: 26, text: "Schon eine Geldstrafe für den Unschuldigen ist nicht gut, / aber Edle schlagen zu lassen ist gegen das Recht." },
      { number: 27, text: "Wer sich zurückhält beim Reden hat tiefe Einsicht, / wer kühl überlegt, ist ein verständiger Mann.", footnotes: ["Sir 20,6-8"] },
      { number: 28, text: "Auch ein Tor kann als weise gelten, wenn er schweigt, / als einsichtig, wenn er seine Lippen verschließt." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Der Abtrünnige sucht nach einem Vorwand, / um loszubrechen mit aller Gewalt.", footnotes: ["1a: einem Vorwand: nach G; H: einer Begierde."] },
      { number: 2, text: "Der Tor hat kein Gefallen an Einsicht, / vielmehr daran, sein Herz zur Schau zu stellen.", footnotes: ["2b: Herz: kann Denkweise und überhaupt das Innere bedeuten."] },
      { number: 3, text: "Kommt Frevel, kommt auch Verachtung / und mit der Schandtat die Schmach." },
      { number: 4, text: "Tiefe Wasser sind die Worte aus dem Mund eines Menschen, / ein sprudelnder Bach, eine Quelle der Weisheit." },
      { number: 5, text: "Es ist nicht gut, einen Schuldigen zu begünstigen / und den Unschuldigen abzuweisen vor Gericht." },
      { number: 6, text: "Die Lippen des Toren beginnen Streit, / sein Mund schreit nach Schlägen." },
      { number: 7, text: "Dem Toren wird sein Mund zum Verderben; / seine Lippen werden ihm selbst zur Falle." },
      { number: 8, text: "Die Worte des Verleumders sind wie Leckerbissen, / sie gleiten hinab in die Kammern des Leibes.", footnotes: ["26,22"] },
      { number: 9, text: "Wer lässig ist bei seiner Arbeit, / ist schon ein Bruder des Mörders.", footnotes: ["28,24"] },
      { number: 10, text: "Ein fester Turm ist der Name des Herrn, / dorthin eilt der Gerechte und ist geborgen.", footnotes: ["Ps 61,4"] },
      { number: 11, text: "Für den Reichen ist sein Vermögen wie eine feste Stadt, / wie eine hohe Mauer - in seiner Einbildung.", footnotes: ["10,15"] },
      { number: 12, text: "Vor dem Sturz ist das Herz des Menschen überheblich, / aber der Ehre geht Demut voran.", footnotes: ["16,18; 15,33"] },
      { number: 13, text: "Gibt einer Antwort, bevor er gehört hat, / ist es Torheit und Schande für ihn.", footnotes: ["Sir 32,7-9"] },
      { number: 14, text: "Der Geist des Menschen überwindet die Krankheit, / doch einen zerschlagenen Geist, wer kann den aufrichten?", footnotes: ["Zugrunde liegt wohl die Einsicht, dass physische Leiden leichter heilbar sind als psychische."] },
      { number: 15, text: "Das Herz des Verständigen erwirbt sich Erkenntnis, / das Ohr der Weisen sucht Erkenntnis." },
      { number: 16, text: "Geschenke schaffen dem Geber Raum / und geleiten ihn vor die Großen.", footnotes: ["17,8; 21,14"] },
      { number: 17, text: "Recht bekommt in seinem Streitfall der Erste, / aber dann kommt der andere und geht der Sache nach." },
      { number: 18, text: "Streitigkeiten beendet das Los, / es entscheidet zwischen Mächtigen.", footnotes: ["16,33"] },
      { number: 19, text: "Ein getäuschter Bruder ist verschlossener als eine Festung, / Streitigkeiten sind wie der Sperrriegel einer Burg." },
      { number: 20, text: "Von der Frucht seines Mundes wird ein jeder satt, / vom Ertrag seiner Lippen wird er gesättigt." },
      { number: 21, text: "Tod und Leben stehen in der Macht der Zunge; / wer sie liebevoll gebraucht, genießt ihre Frucht." },
      { number: 22, text: "Wer eine Frau gefunden, hat Glück gefunden / und das Gefallen des Herrn erlangt.", footnotes: ["G und Vg: eine gute Frau. Sie fügen an: Wer eine gute Frau verstößt, verstößt das Glück; wer eine Ehebrecherin aufnimmt, ist töricht und frevelhaft."] },
      { number: 23, text: "Flehentlich redet der Arme, / der Reiche aber antwortet mit Härte." },
      { number: 24, text: "Manche Freunde führen ins Verderben, / manch ein lieber Freund ist anhänglicher als ein Bruder." },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Besser ein Armer, der schuldlos seinen Weg geht, als einer mit verlogenen Lippen, der ein Tor ist.", footnotes: ["28,6"] },
      { number: 2, text: "Schon unvernünftige Begierde ist nicht gut, / und wer hastig rennt, tritt fehl." },
      { number: 3, text: "Die Torheit verdirbt dem Menschen den Weg / und dann grollt sein Herz gegen den Herrn.", footnotes: ["Sir 15,11-20"] },
      { number: 4, text: "Besitz vermehrt die Zahl der Freunde, / der Arme aber wird von seinem Freund verlassen.", footnotes: ["14,20; Sir 13,21"] },
      { number: 5, text: "Ein falscher Zeuge bleibt nicht ungestraft, / wer Lügen flüstert, wird nicht entrinnen.", footnotes: ["21,28"] },
      { number: 6, text: "Viele umschmeicheln den Vornehmen / und jeder will der Freund eines freigebigen Mannes sein." },
      { number: 7, text: "Vom Armen wollen alle seine Brüder nichts wissen, / erst recht bleiben ihm seine Freunde fern. / [Gute Einsicht ist denen nahe, die sich um sie kümmern, / ein verständiger Mann findet sie. / Wer viel redet, versündigt sich.] / Wer Worten nachjagt, wird nicht entrinnen.", footnotes: ["Das Eingeklammerte fehlt in H, steht aber in G und gehört vielleicht zum ursprünglichen Textbestand."] },
      { number: 8, text: "Wer Verstand erwirbt, liebt sich selbst, / wer Einsicht bewahrt, findet sein Glück." },
      { number: 9, text: "Ein falscher Zeuge bleibt nicht ungestraft, / wer Lügen flüstert, geht zugrunde." },
      { number: 10, text: "Wohlleben steht dem Toren nicht an, / noch weniger einem Knecht, über Fürsten zu herrschen." },
      { number: 11, text: "Einsicht macht den Menschen langmütig, / sein Ruhm ist es, über Verfehlungen hinwegzugehen." },
      { number: 12, text: "Wie das Knurren des Löwen ist der Zorn des Königs, / doch wie Tau auf dem Gras ist seine Gunst.", footnotes: ["16,14"] },
      { number: 13, text: "Ein Unglück für den Vater ist ein törichter Sohn / und wie ein ständig tropfendes Dach das Gezänk einer Frau.", footnotes: ["27,15"] },
      { number: 14, text: "Haus und Habe sind das Erbe der Väter, / doch eine verständige Frau kommt vom Herrn." },
      { number: 15, text: "Faulheit versenkt in Schlaf, / ein träger Mensch muss hungern.", footnotes: ["15a: Text umstritten."] },
      { number: 16, text: "Wer (Gottes) Gebot bewahrt, bewahrt sein Leben, / wer seine Wege verachtet, muss sterben.", footnotes: ["16b: Vielleicht zu korrigieren: wer seine Worte verachtet."] },
      { number: 17, text: "Wer Erbarmen hat mit dem Elenden, leiht dem Herrn; / er wird ihm seine Wohltat vergelten." },
      { number: 18, text: "Züchtige deinen Sohn, solange noch Hoffnung ist, / doch lass dich nicht hinreißen, ihn zu töten.", footnotes: ["18b: Andere Übersetzungsmöglichkeit: und achte nicht auf sein Jammern; oder: doch lass dich nicht hinreißen zum Jähzorn!"] },
      { number: 19, text: "Der maßlos Jähzornige muss büßen; / denn willst du schlichten, machst du es noch ärger.", footnotes: ["19b: Übersetzung unsicher."] },
      { number: 20, text: "Hör auf guten Rat und nimm Zucht an, / damit du weise wirst für die Zukunft." },
      { number: 21, text: "Viele Pläne fasst das Herz des Menschen, / doch nur der Ratschluss des Herrn hat Bestand." },
      { number: 22, text: "Die Menschen streben nach Gewinn, / doch besser ein Armer als ein Betrüger.", footnotes: ["22a: Text korr."] },
      { number: 23, text: "Die Gottesfurcht führt zum Leben; / gesättigt geht man zur Ruhe, / von keinem Übel heimgesucht." },
      { number: 24, text: "Greift der Faule mit der Hand in die Schüssel, / bringt er sie nicht einmal zum Mund zurück.", footnotes: ["26,15"] },
      { number: 25, text: "Schlägst du den Zuchtlosen, so wird der Unerfahrene klug; / weist man den Verständigen zurecht, gewinnt er Einsicht.", footnotes: ["21,11"] },
      { number: 26, text: "Wer den Vater misshandelt, die Mutter wegjagt, / ist ein verkommener, schändlicher Sohn." },
      { number: 27, text: "Gibst du es auf, mein Sohn, auf Mahnung zu hören, / so entziehst du dich den Worten der Einsicht." },
      { number: 28, text: "Ein nichtsnutziger Zeuge verspottet das Recht, / der Mund der Frevler sprudelt Unheil hervor.", footnotes: ["15,28"] },
      { number: 29, text: "Für die Zuchtlosen stehen Ruten bereit / und Schläge für den Rücken der Toren.", footnotes: ["10,13", "29a: Ruten: nach G; H: Strafgerichte."] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Ein Zuchtloser ist der Wein, ein Lärmer das Bier; / wer sich hierin verfehlt, wird nie weise.", footnotes: ["23,29-35; Sir 31,25-30"] },
      { number: 2, text: "Wie das Knurren des Löwen ist der Grimm des Königs; / wer ihn erzürnt, verwirkt sein Leben.", footnotes: ["16,14; 19,12"] },
      { number: 3, text: "Es ehrt den Menschen, vom Streit abzulassen, / jeder Tor aber bricht los." },
      { number: 4, text: "Der Faule pflügt nicht im Herbst; / sucht er in der Erntezeit, so ist nichts da." },
      { number: 5, text: "Ein tiefes Wasser sind die Pläne im Herzen des Menschen, / doch der Verständige schöpft es herauf." },
      { number: 6, text: "Viele Menschen rühmen sich ihrer Güte, / aber wer findet einen, auf den Verlass ist?" },
      { number: 7, text: "Wer als Gerechter unbescholten seinen Weg geht: / Wohl den Kindern, die er hinterlässt." },
      { number: 8, text: "Ein König auf dem Richterstuhl / sondert mit seinem Scharfblick alles Böse aus.", footnotes: ["20,26"] },
      { number: 9, text: "Wer kann sagen: Ich habe mein Herz geläutert, / rein bin ich von meiner Sünde?" },
      { number: 10, text: "Zweierlei Gewicht und zweierlei Maß, / beide sind dem Herrn ein Gräuel.", footnotes: ["16,11"] },
      { number: 11, text: "An seinem Treiben lässt schon der Knabe erkennen, / ob sein Tun lauter und redlich sein wird." },
      { number: 12, text: "Das Ohr, das hört, und das Auge, das sieht, / der Herr hat sie beide geschaffen." },
      { number: 13, text: "Liebe nicht den Schlaf, damit du nicht arm wirst; / halte deine Augen offen und du hast Brot genug.", footnotes: ["6,6-11"] },
      { number: 14, text: "Schlecht, schlecht, sagt der Käufer; / geht er aber weg, so rühmt er sich." },
      { number: 15, text: "Gold gibt es und viele Perlen, / ein kostbarer Schmuck aber sind verständige Lippen." },
      { number: 16, text: "Nimm ihm das Kleid, denn er hat für einen andern gebürgt, / fremder Leute wegen pfände bei ihm!", footnotes: ["27,13"] },
      { number: 17, text: "Süß schmeckt dem Menschen das Brot der Lüge, / hernach aber füllt sich sein Mund mit Kieseln." },
      { number: 18, text: "Pläne kommen durch Beratung zustande. / Darum führe den Kampf mit Überlegung!" },
      { number: 19, text: "Geheimnisse verrät, wer als Verleumder umhergeht. / Darum lass dich nicht ein mit einem Schwätzer!", footnotes: ["11,13"] },
      { number: 20, text: "Wer seinem Vater flucht und seiner Mutter, / dessen Lampe erlischt zur Zeit der Finsternis." },
      { number: 21, text: "Ein Besitz, schnell errafft am Anfang, / ist nicht gesegnet an seinem Ende." },
      { number: 22, text: "Sag nicht: Ich will das Böse vergelten. / Vertrau auf den Herrn, er wird dir helfen.", footnotes: ["Sir 28,1-6; Röm 12,17-21"] },
      { number: 23, text: "Ein Gräuel ist dem Herrn zweierlei Gewicht, / eine falsche Waage ist nicht recht.", footnotes: ["16,11"] },
      { number: 24, text: "Der Herr lenkt die Schritte eines jeden. / Wie könnte der Mensch seinen Weg verstehen?" },
      { number: 25, text: "Eine Falle ist es, unbedacht zu rufen: Geweiht!, / und erst nach dem Gelübde zu überlegen.", footnotes: ["Ri 11,29-40"] },
      { number: 26, text: "Ein weiser König sondert die Frevler aus / und vergilt ihnen ihre Untat.", footnotes: ["26b: H: und lässt über sie das Rad (des Dreschwagens?) gehen."] },
      { number: 27, text: "Der Herr wacht über den Atem des Menschen, / er durchforscht alle Kammern des Leibes." },
      { number: 28, text: "Güte und Treue behüten den König, / er stützt seinen Thron durch Güte.", footnotes: ["Ps 61,8"] },
      { number: 29, text: "Der Ruhm der Jungen ist ihre Kraft, / die Zier der Alten ihr graues Haar." },
      { number: 30, text: "Blutige Striemen läutern den Bösen / und Schläge die Kammern des Leibes." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Wie ein Wasserbach ist das Herz des Königs in der Hand des Herrn; / er lenkt es, wohin er will." },
      { number: 2, text: "Jeder meint, sein Verhalten sei richtig, / doch der Herr prüft die Herzen.", footnotes: ["16,2"] },
      { number: 3, text: "Gerechtigkeit üben und Recht / ist dem Herrn lieber als Schlachtopfer.", footnotes: ["Ps 51,18f; Hos 6,6"] },
      { number: 4, text: "Hoffart der Augen, Übermut des Herzens - / die Leuchte der Frevler versagt." },
      { number: 5, text: "Die Pläne des Fleißigen bringen Gewinn, / doch der hastige Mensch hat nur Mangel." },
      { number: 6, text: "Wer Schätze erwirbt mit verlogener Zunge, / jagt nach dem Wind, er gerät in die Schlingen des Todes.", footnotes: ["6b: Text korr.; andere Übersetzungsmöglichkeit: wird wie ein Hauch verjagt in die Schlingen des Todes."] },
      { number: 7, text: "Gewalttat reißt die Frevler hinweg, / denn sie weigern sich, das Rechte zu tun." },
      { number: 8, text: "Der Weg des Unehrlichen ist gewunden, / aber das Tun des Lauteren ist gerade." },
      { number: 9, text: "Besser in einer Ecke des Daches wohnen / als eine zänkische Frau im gemeinsamen Haus.", footnotes: ["25,24"] },
      { number: 10, text: "Das Verlangen des Frevlers geht nach dem Bösen, / sein Nächster findet bei ihm kein Erbarmen." },
      { number: 11, text: "Muss der Zuchtlose büßen, / so wird der Unerfahrene weise, / belehrt man den Weisen, so nimmt er Einsicht an.", footnotes: ["19,25"] },
      { number: 12, text: "Der Gerechte handelt klug am Haus des Frevlers, / wenn er die Frevler ins Unheil stürzt.", footnotes: ["12a: Andere Übersetzungsmöglichkeit: Der Gerechte belehrt das Haus des Frevlers; oder: Der Gerechte gibt Acht auf . . ."] },
      { number: 13, text: "Wer sein Ohr verschließt vor dem Schreien des Armen, / wird selbst nicht erhört, wenn er um Hilfe ruft." },
      { number: 14, text: "Eine heimliche Gabe besänftigt den Zorn, / ein Geschenk aus dem Gewandbausch den heftigen Grimm.", footnotes: ["17,23"] },
      { number: 15, text: "Der Gerechte freut sich, wenn Recht geschieht, / doch den Übeltäter versetzt das in Schrecken." },
      { number: 16, text: "Wer abirrt vom Weg der Einsicht, / wird bald in der Versammlung der Totengeister ruhen." },
      { number: 17, text: "Der Not verfällt, wer Vergnügen liebt, / wer Wein und Salböl liebt, wird nicht reich." },
      { number: 18, text: "Für den Gerechten dient der Frevler als Lösegeld, / anstelle des Redlichen der Treulose.", footnotes: ["Nach den Sprichwörtern würde schon auf dieser Erde am Ende alles gerecht ausgehen: Ijob und Koh bezweifeln das."] },
      { number: 19, text: "Besser in der Wüste hausen / als Ärger mit einer zänkischen Frau." },
      { number: 20, text: "Ein kostbarer Schatz ist in der Wohnung des Weisen, / aber ein törichter Mensch vergeudet ihn." },
      { number: 21, text: "Wer nach Gerechtigkeit und Güte strebt, / findet Leben und Ehre." },
      { number: 22, text: "Der Weise ersteigt die Stadt der Mächtigen / und stürzt das Bollwerk, auf das sie vertraut.", footnotes: ["24,5; Koh 9,14-18"] },
      { number: 23, text: "Wer seinen Mund und seine Zunge behütet, / der behütet sein Leben vor Drangsal." },
      { number: 24, text: "Der Freche und Stolze, einen Zuchtlosen nennt man ihn, / er handelt in maßlosem Übermut." },
      { number: 25, text: "Den Faulen bringt sein Begehren um, / denn zu arbeiten weigern sich seine Hände;" },
      { number: 26, text: "den ganzen Tag begehrt er voll Gier, / der Gerechte aber gibt, ohne zu geizen." },
      { number: 27, text: "Das Opfer der Frevler ist (dem Herrn) ein Gräuel, / zumal wenn es in schlechter Absicht dargebracht wird.", footnotes: ["15,8"] },
      { number: 28, text: "Ein falscher Zeuge geht zugrunde, / wer aber zu hören versteht, redet erfolgreich.", footnotes: ["19,5.9"] },
      { number: 29, text: "Der Frevler zeigt Trotz in seiner Miene, / der Redliche ordnet seine Wege." },
      { number: 30, text: "Keine Weisheit gibt es, keine Einsicht, / keinen Rat gegenüber dem Herrn." },
      { number: 31, text: "Das Ross wird gerüstet für den Tag der Schlacht, / doch der Sieg steht beim Herrn." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Guter Ruf ist kostbarer als großer Reichtum, / hohes Ansehen besser als Silber und Gold." },
      { number: 2, text: "Reiche und Arme begegnen einander, / doch der Herr hat sie alle erschaffen.", footnotes: ["29,13", "Soziale Unterschiede gibt es immer. Da aber vor dem Schöpfer alle Menschen gleich sind, sollen die Gegensätze möglichst gemildert werden."] },
      { number: 3, text: "Der Kluge sieht das Unheil und verbirgt sich, / die Unerfahrenen laufen weiter und müssen es büßen.", footnotes: ["27,12"] },
      { number: 4, text: "Der Lohn für Demut und Gottesfurcht / ist Reichtum, Ehre und Leben." },
      { number: 5, text: "Dornen und Schlingen liegen auf dem Weg des Falschen; / wer sein Leben behütet, bleibt ihnen fern." },
      { number: 6, text: "Erzieh den Knaben für seinen Lebensweg, / dann weicht er auch im Alter nicht davon ab." },
      { number: 7, text: "Der Reiche hat die Armen in seiner Gewalt, / der Schuldner ist seines Gläubigers Knecht." },
      { number: 8, text: "Wer Unrecht sät, erntet Unheil, / der Stecken seines Übermuts versagt." },
      { number: 9, text: "Wer ein gütiges Auge hat, wird gesegnet, / weil er den Armen von seinem Brot gibt." },
      { number: 10, text: "Vertreib den Zuchtlosen, so schwindet der Zank, / Streiten und Schimpfen hören auf." },
      { number: 11, text: "Wer die Lauterkeit des Herzens liebt - / wegen seiner gefälligen Rede wird der König sein Freund.", footnotes: ["Übersetzung unsicher; G hat Änderungen und Einschübe."] },
      { number: 12, text: "Die Augen des Herrn behüten den Einsichtigen, / das Gerede des Verräters bringt er zu Fall.", footnotes: ["12a: Wörtlich: behüten Einsicht."] },
      { number: 13, text: "Der Faule sagt: Ein Löwe ist draußen, / mitten auf der Straße käme ich ums Leben.", footnotes: ["26,13"] },
      { number: 14, text: "Der Mund fremder Frauen ist eine tiefe Grube; / wem der Herr zürnt, der fällt hinein." },
      { number: 15, text: "Steckt Torheit im Herzen des Knaben, / die Rute der Zucht vertreibt sie daraus." },
      { number: 16, text: "Wer den Armen bedrückt, macht ihn reich, / wer dem Reichen gibt, macht ihn arm." },
      { number: 17, text: "Worte von Weisen: Neige mir dein Ohr zu und hör auf meine Worte, / nimm dir meine Lehren zu Herzen!", heading: "Die Worte von Weisen: 22,17 - 24,33 | Eine Sprichwörtersammlung: 22,17 - 23,11", footnotes: ["Eine leichte Textkorrektur nach G stellt die Überschrift dieses ersten Anhangs an die erste salomonische Hauptsammlung wieder her. Eine erste Sammlung von «Worten von Weisen» reicht bis zur nächsten Überschrift in 24,23. Sie enthält drei kurze Teilsammlungen, von denen die erste (22,17 - 23,11) besondere Beachtung verdient, weil sie abhängig ist vom ägyptischen Weisheitsbuch des Amen-em-ope (etwa 10. Jh. v. Chr.). Das ägyptische Spruchgut wurde in Auswahl und unter Angleichung an den Glauben Israels übernommen. Angehängt ist eine weitere Sammlung von «Sprichwörtern von Weisen» (24,23-34)."] },
      { number: 18, text: "Schön ist es, wenn du sie in deinem Innern bewahrst; / sie mögen fest wie ein Zeltpflock auf deinen Lippen haften." },
      { number: 19, text: "Damit dein Vertrauen auf dem Herrn steht, / lehre ich dich heute seinen Weg." },
      { number: 20, text: "Habe ich nicht dreißig Sätze für dich aufgeschrieben / als wissenswerte Ratschläge," },
      { number: 21, text: "um dir verlässliche Worte mitzuteilen, / damit du deinem Auftraggeber antworten kannst?" },
      { number: 22, text: "Beraube den Schwachen nicht, denn er ist ja so schwach, / zertritt den Armen nicht am Tor!", footnotes: ["22b: Am Stadttor finden die Rats- und Gerichtsverhandlungen statt."] },
      { number: 23, text: "Denn der Herr führt den Rechtsstreit für sie / und raubt denen das Leben, die sie berauben.", footnotes: ["Jes 1,17f"] },
      { number: 24, text: "Befreunde dich nicht mit dem Jähzornigen, / verkehre nicht mit einem Hitzkopf," },
      { number: 25, text: "damit du dich nicht an seine Pfade gewöhnst / und dir eine Schlinge legst für dein Leben." },
      { number: 26, text: "Sei nicht unter denen, die sich durch Handschlag verpflichten, / die Bürgschaft leisten für Schulden;", footnotes: ["6,1-5; 20,16"] },
      { number: 27, text: "wenn du nicht zahlen kannst, / nimmt man dein Bett unter dir weg." },
      { number: 28, text: "Verschieb nicht die alte Grenze, / die deine Väter gesetzt haben.", footnotes: ["23,10"] },
      { number: 29, text: "Siehst du einen, der gewandt ist in seinem Beruf: / vor Königen wird er dienen. / [Nicht wird er vor Niedrigen dienen.]" },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Wenn du zu Tisch sitzt bei einem Herrscher, / so achte nur auf das, was vor dir steht.", footnotes: ["(1-13) Sir 31,12-21"] },
      { number: 2, text: "Setz ein Messer an deine Kehle, / wenn du ein gieriger Mensch bist." },
      { number: 3, text: "Sei nicht begierig auf seine Leckerbissen, / sie sind eine trügerische Speise." },
      { number: 4, text: "Müh dich nicht ab, um Reichtum zu erwerben / und dabei deine Einsicht aufzugeben." },
      { number: 5, text: "Flüchtig ist er; schaust du nach ihm, ist er weg; / plötzlich macht er sich Flügel und fliegt wie ein Adler zum Himmel." },
      { number: 6, text: "Iss nicht das Brot des Geizigen, / sei nicht begierig auf seine Leckerbissen!" },
      { number: 7, text: "Denn sie schmecken in der Kehle wie etwas Ekliges. / Er sagt zu dir: Iss und trink!, / doch sein Herz ist dir nicht zugetan." },
      { number: 8, text: "Den Bissen, den du gegessen hast, musst du erbrechen / und deine freundlichen Worte hast du vergeudet." },
      { number: 9, text: "Rede nicht vor den Ohren eines Törichten, / denn er missachtet deine klugen Worte." },
      { number: 10, text: "Verschieb nicht die alte Grenze, / dring nicht in die Felder der Waisen vor!", footnotes: ["22,28"] },
      { number: 11, text: "Denn ihr Anwalt ist mächtig, / er wird ihre Sache gegen dich führen." },
      { number: 12, text: "Öffne dein Herz für die Zucht, / dein Ohr für verständige Reden!", heading: "Mahnungen: 23,12-35", footnotes: ["12-35: Nach Mahnungen, die vom ägyptischen Weisheitsbuch abhängig sind (vgl. die Anmerkung zu 22,17 und die Einleitung), folgt ein Abschnitt mit väterlichen Lehren; er endet mit einem sarkastischen Spottlied auf den Trunkenbold in VV. 29-35."] },
      { number: 13, text: "Erspar dem Knaben die Züchtigung nicht; / wenn du ihn schlägst mit dem Stock, wird er nicht sterben.", footnotes: ["13,24; 19,18; 22,15; 29,15.17; Sir 30,1-13"] },
      { number: 14, text: "Du schlägst ihn mit dem Stock, / bewahrst aber sein Leben vor der Unterwelt." },
      { number: 15, text: "Mein Sohn, wenn dein Herz weise ist, / so freut sich auch mein eigenes Herz." },
      { number: 16, text: "Mein Inneres ist voll Jubel, / wenn deine Lippen reden, was recht ist." },
      { number: 17, text: "Dein Herz ereifere sich nicht wegen der Sünder, / sondern eifere stets nach Gottesfurcht!" },
      { number: 18, text: "Denn sicher gibt es eine Zukunft, / deine Hoffnung wird nicht zerschlagen.", footnotes: ["24,14"] },
      { number: 19, text: "Höre, mein Sohn, und sei weise, / lenk dein Herz auf geraden Weg!" },
      { number: 20, text: "Gesell dich nicht zu den Weinsäufern, / zu solchen, die im Fleischgenuß schlemmen;" },
      { number: 21, text: "denn Säufer und Schlemmer werden arm, / Schläfrigkeit kleidet in Lumpen.", footnotes: ["Sir 18,33"] },
      { number: 22, text: "Hör auf deinen Vater, der dich gezeugt hat, / verachte deine Mutter nicht, wenn sie alt wird.", footnotes: ["1,8; 30,17; Sir 3,1-16"] },
      { number: 23, text: "Erwirb dir Wahrheit und verkauf sie nicht mehr: / Weisheit, Zucht und Einsicht!", footnotes: ["4,7", "Fehlt in G und unterbricht den Zusammenhang, ist also wohl ein Zusatz."] },
      { number: 24, text: "Laut jubelt der Vater des Gerechten; / wer einen weisen Sohn hat, kann sich über ihn freuen." },
      { number: 25, text: "Deine Eltern mögen sich freuen; / jubeln möge die Mutter, die dich gebar." },
      { number: 26, text: "Gib mir dein Herz, mein Sohn, / deine Augen mögen an meinen Wegen Gefallen finden;" },
      { number: 27, text: "denn die Ehebrecherin ist eine tiefe Grube, / die fremde Frau ein enger Brunnen.", footnotes: ["22,14"] },
      { number: 28, text: "Ja, wie ein Räuber lauert sie auf / und mehrt die Verräter unter den Menschen.", footnotes: ["7,12"] },
      { number: 29, text: "Wer hat Ach? Wer hat Weh? / Wer Gezänk? Wer Klage? / Wer hat Wunden wegen nichts? / Wer trübe Augen?" },
      { number: 30, text: "Jene, die bis in die Nacht beim Wein sitzen, / die kommen, um den Mischwein zu probieren.", footnotes: ["20,1"] },
      { number: 31, text: "Schau nicht nach dem Wein, / wie er rötlich schimmert, / wie er funkelt im Becher: / Er trinkt sich so leicht!" },
      { number: 32, text: "Zuletzt beißt er wie eine Schlange, / verspritzt Gift gleich einer Viper." },
      { number: 33, text: "Deine Augen sehen seltsame Dinge, / dein Herz redet wirres Zeug." },
      { number: 34, text: "Du bist wie einer, der auf hoher See schläft, / der einschläft über dem Steuer des Schiffes." },
      { number: 35, text: "Man hat mich geschlagen, doch es tat mir nicht weh, / man hat mich gehauen, aber ich habe nichts gespürt. Wann wache ich auf? / Von neuem will ich zum Wein greifen." },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Sei nicht neidisch auf böse Menschen, / such keinen Umgang mit ihnen!", heading: "Lebensregeln: 24,1-22", footnotes: ["Ps 37,1", "1-22: Diese Lebensregeln bilden einen eigenen Abschnitt mit zweiundzwanzig Versen entsprechend der Anzahl der Buchstaben im hebräischen Alphabet."] },
      { number: 2, text: "Denn ihr Herz sinnt auf Gewalttat, / Unheil reden ihre Lippen." },
      { number: 3, text: "Durch Weisheit wird ein Haus gebaut, / durch Umsicht gewinnt es Bestand." },
      { number: 4, text: "Durch Klugheit werden die Kammern gefüllt / mit allerlei wertvollen, köstlichen Gütern." },
      { number: 5, text: "Der Weise ist dem Starken überlegen, / ein verständiger Mensch dem robusten.", footnotes: ["21,22"] },
      { number: 6, text: "Denn durch Überlegung gewinnst du den Kampf, / viele Ratgeber verhelfen zum Sieg.", footnotes: ["11,14; 20,18"] },
      { number: 7, text: "Zu hoch hängt dem Toren die Weisheit, / am Tor tut er den Mund nicht auf.", footnotes: ["7b: Vgl. die Anmerkung zu 22,22b."] },
      { number: 8, text: "Wer stets darauf aus ist, Böses zu tun, / den nennt man einen Ränkeschmied." },
      { number: 9, text: "Das Trachten des Toren ist Sünde, / der Zuchtlose ist den Menschen ein Gräuel." },
      { number: 10, text: "Zeigst du dich lässig am Tag der Bedrängnis, / so wird auch deine Kraft bedrängt.", footnotes: ["Text von H unklar; wahrscheinlich verderbt. Mit einem Wortspiel soll wohl gesagt werden: Wer anderen in der Bedrängnis nicht hilft (vgl. V. 11), bringt sich selbst in Bedrängnis."] },
      { number: 11, text: "Befrei jene, die man zum Tod schleppt; / die zur Hinrichtung wanken, rette sie doch!" },
      { number: 12, text: "Wolltest du sagen: Gott weiß von uns nichts; - / hat er, der die Herzen prüft, keine Kenntnis? / Hat er, der über dich wacht, kein Wissen? Ja, er vergilt jedem Menschen, / wie sein Tun es verdient." },
      { number: 13, text: "Iss Honig, mein Sohn, denn er ist gut, / Wabenhonig ist süß für den Gaumen." },
      { number: 14, text: "Wisse: Genauso ist die Weisheit für dich. / Findest du sie, dann gibt es eine Zukunft, / deine Hoffnung wird nicht zerschlagen." },
      { number: 15, text: "Belaure nicht frevlerisch die Wohnung des Gerechten, / zerstöre sein Ruhelager nicht!" },
      { number: 16, text: "Denn siebenmal fällt der Gerechte und steht wieder auf, / doch die Frevler stürzen ins Unglück.", footnotes: ["Ps 37,24"] },
      { number: 17, text: "Freu dich nicht über den Sturz deines Feindes, / dein Herz juble nicht, wenn er strauchelt," },
      { number: 18, text: "damit nicht der Herr es sieht und missbilligt / und seinen Zorn von ihm abwendet." },
      { number: 19, text: "Erhitz dich nicht wegen der Übeltäter, / ereifere dich nicht wegen der Frevler!" },
      { number: 20, text: "Denn für den Bösen gibt es keine Zukunft, / die Lampe der Frevler erlischt." },
      { number: 21, text: "Fürchte den Herrn, mein Sohn, und den König; / mit diesen beiden überwirf dich nicht!", footnotes: ["1 Petr 2,17"] },
      { number: 22, text: "Denn plötzlich geht von ihnen Verderben aus / und unvermutet kommt Unheil von beiden." },
      { number: 23, text: "Auch folgende Sprichwörter stammen von Weisen: / Im Gericht auf die Person sehen ist nicht recht.", heading: "Weisheitssprüche: 24,23-34", footnotes: ["28,21", "23a: Überschrift eines weiteren Anhangs, der bis V. 34 reicht (vgl. 22,17). Auch diesen Anhang schließt in VV. 30-34 ein Spottlied ab, hier auf den faulen Landwirt."] },
      { number: 24, text: "Wer zum Schuldigen sagt: Unschuldig bist du!, / den verfluchen die Menschen, / verwünschen die Leute." },
      { number: 25, text: "Denen aber, die entscheiden, wie es recht ist, geht es gut; / über sie kommt Segen und Glück." },
      { number: 26, text: "Einen Kuss auf die Lippen gibt, / wer richtig antwortet." },
      { number: 27, text: "Nimm draußen deine Arbeit auf und bestell dein Feld, / danach gründe deinen Hausstand!" },
      { number: 28, text: "Tritt gegen deinen Nächsten nicht als falscher Zeuge auf, / betrüge nicht mit deinen Worten!", footnotes: ["Ex 20,16; Dtn 5,20"] },
      { number: 29, text: "Sag nicht: Wie er mir getan hat, / so will ich auch ihm tun, / einem jedem will ich vergelten, / wie es seine Taten verdienen.", footnotes: ["20,22"] },
      { number: 30, text: "Am Acker eines Faulen ging ich vorüber, / am Weinberg eines unverständigen Menschen:" },
      { number: 31, text: "Sieh da, er war ganz überwuchert von Disteln, / seine Fläche mit Unkraut bedeckt, / seine Steinmauer eingerissen." },
      { number: 32, text: "Ich sah es und machte mir meine Gedanken, / ich betrachtete es und zog die Lehre daraus:" },
      { number: 33, text: "Noch ein wenig schlafen, noch ein wenig schlummern, / noch ein wenig die Arme verschränken, um auszuruhen.", footnotes: ["(33-34) 6,10f"] },
      { number: 34, text: "Da kommt schon die Armut wie ein Strolch über dich, / die Not wie ein zudringlicher Bettler." },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Auch das sind Sprichwörter Salomos, die die Männer Hiskijas, des Königs von Juda, sammelten.", heading: "Die zweite salomonische Spruchsammlung: 25,1 - 29,27", footnotes: ["25,1 - 29,27: Die zweite salomonische Hauptsammlung umfasst nur die Kapitel 25 - 29. Auch sie ist in zwei Hälften zu teilen: Kap. 25 27 und Kap. 28f. - Hiskija (728-699 v. Chr.) war einer der tüchtigsten Könige Judas, sodass die Mitteilung, unter ihm habe eine Gruppe von Gelehrten die Weisheitssprüche zusammengestellt, durchaus glaubwürdig ist."] },
      { number: 2, text: "Gottes Ehre ist es, eine Sache zu verhüllen, / des Königs Ehre ist es, eine Sache zu erforschen.", footnotes: ["16,10"] },
      { number: 3, text: "Der Himmel so hoch und die Erde so tief / und das Herz des Königs: sie sind nicht zu erforschen." },
      { number: 4, text: "Scheidet man die Schlacken vom Silber, / gelingt dem Feinschmied das Gefäß." },
      { number: 5, text: "Scheidet man den Frevler vom König, / erlangt dessen Thron Bestand durch Gerechtigkeit.", footnotes: ["16,12"] },
      { number: 6, text: "Rühme dich nicht vor dem König / und stell dich nicht an den Platz der Großen;", footnotes: ["Sir 13,9f"] },
      { number: 7, text: "denn besser, man sagt zu dir: Rück hier herauf, / als dass man dich nach unten setzt wegen eines Vornehmen. Was deine Augen sahen, /", footnotes: ["Lk 14,7-11"] },
      { number: 8, text: "bring es nicht übereilt als Streitfall vor; denn was willst du später tun, / wenn dein Nächster dich bloßstellt?" },
      { number: 9, text: "Trag deinen Streit mit deinem Nächsten aus, / doch verrate nicht das Geheimnis eines andern," },
      { number: 10, text: "sonst wird dich schmähen, wer es hört, / und dein Geschwätz wird auf dich zurückfallen.", footnotes: ["10b: Übersetzung unsicher."] },
      { number: 11, text: "Wie goldene Äpfel auf silbernen Schalen / ist ein Wort, gesprochen zur rechten Zeit.", footnotes: ["15,23"] },
      { number: 12, text: "Wie ein goldener Ring und Schmuck aus Feingold / ist ein weiser Mahner für ein Ohr, das zuhört." },
      { number: 13, text: "Wie kühlender Schnee an einem Sommertag / ist ein verlässlicher Bote für den, der ihn sendet; / er erquickt die Seele seines Herrn." },
      { number: 14, text: "Aufziehende Wolken mit Wind, doch kein Regen, / so ist ein Mann, der Versprechungen macht und nicht hält.", footnotes: ["14b: Wörtlich: Ein Mann, der mit erlogener Gabe prahlt."] },
      { number: 15, text: "Mit Geduld wird ein Vorgesetzter umgestimmt, / sanfte Zunge bricht Knochen." },
      { number: 16, text: "Findest du Honig, iss nur, so viel dir bekommt, / sonst wirst du ihn satt und erbrichst ihn." },
      { number: 17, text: "Mach dich rar im Haus deines Nächsten, / sonst wird er dich satt und verabscheut dich." },
      { number: 18, text: "Keule und Schwert und scharfer Pfeil: / das ist einer, der falsch aussagt / gegen seinen Nächsten." },
      { number: 19, text: "Schlechter Zahn und stolpernder Fuß: / der Verräter am Tag der Not." },
      { number: 20, text: "Essig auf Laugensalz - / (so ist,) wer Lieder singt vor einem missmutigen Herzen. [Wie die Motte am Kleid, der Wurm im Holz, / so nagt der Kummer am Herzen des Menschen.]", footnotes: ["20a: H schickt voraus: Ein das Kleid Ablegender am kalten Tag. Hier sind Buchstaben aus V. 19b wiederholt und missdeutet worden. 20c: Fehlt in H, steht aber in allen alten Übersetzungen und ist daher wohl ursprünglich."] },
      { number: 21, text: "Hat dein Feind Hunger, gib ihm zu essen, / hat er Durst, gib ihm zu trinken;", footnotes: ["Röm 12,20"] },
      { number: 22, text: "so sammelst du glühende Kohlen auf sein Haupt / und der Herr wird es dir vergelten." },
      { number: 23, text: "Der Nordwind bringt Regen, / eine heimtückische Zunge zornige Gesichter." },
      { number: 24, text: "Besser in einer Ecke des Daches wohnen / als eine zänkische Frau im gemeinsamen Haus.", footnotes: ["21,9"] },
      { number: 25, text: "Kühles Wasser für eine durstige Kehle / ist eine gute Nachricht aus fernem Land." },
      { number: 26, text: "Ein getrübter Brunnen, ein verschütteter Quell / ist ein Gerechter, der vor dem Frevler wankt." },
      { number: 27, text: "Zu viel Honig essen ist nicht gut: / Ebenso spare mit ehrenden Worten!", footnotes: ["27b: Nach G; H ist verderbt."] },
      { number: 28, text: "Eine Stadt mit eingerissener Mauer / ist ein Mann, der sich nicht beherrscht." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Wie Schnee im Sommer und Regen zur Erntezeit, / so unpassend ist Ehre für einen Toren." },
      { number: 2, text: "Wie der Spatz wegflattert und die Schwalbe davonfliegt, / so ist ein unverdienter Fluch; er trifft nicht ein." },
      { number: 3, text: "Dem Pferd die Peitsche, dem Esel den Zaum, / dem Rücken der Toren den Stock.", footnotes: ["10,13"] },
      { number: 4, text: "Antworte dem Toren nicht, wie es seine Dummheit verdient, / damit nicht auch du ihm gleich wirst.", footnotes: ["Sir 22,13"] },
      { number: 5, text: "Antworte dem Toren, wie es seine Dummheit verdient, / damit er sich nicht einbildet, ein Weiser zu sein." },
      { number: 6, text: "Die Füße haut sich ab, Schaden muss leiden, / wer Botschaft sendet durch einen Toren." },
      { number: 7, text: "Schlaff wie die Schenkel des Lahmen / ist ein Weisheitsspruch im Mund der Toren." },
      { number: 8, text: "Den Stein bindet in der Schleuder fest, / wer einem Toren Ehre erweist." },
      { number: 9, text: "Ein Dornzweig geriet in die Hand eines Betrunkenen: / ein Weisheitsspruch in den Mund der Toren." },
      { number: 10, text: "Ein Schütze, der alle verwundet - / ein Tor und ein Betrunkener, wenn sie vorübergehen.", footnotes: ["H ist verderbt, will vielleicht sagen: Toren (und Betrunkene) sind unnütz, ja gefährlich."] },
      { number: 11, text: "Wie ein Hund, der zurückkehrt zu dem, was er erbrochen hat, / so ist ein Tor, der seine Dummheit wiederholt.", footnotes: ["2 Petr 2,22"] },
      { number: 12, text: "Siehst du jemand, der sich selbst für weise hält - / mehr Hoffnung gibt es für den Toren als für ihn.", footnotes: ["29,20"] },
      { number: 13, text: "Der Faule sagt: Ein Löwe ist auf dem Weg, / ein Raubtier ist auf den Straßen.", footnotes: ["22,13"] },
      { number: 14, text: "Die Tür dreht sich in ihrer Angel / und der Faule in seinem Bett." },
      { number: 15, text: "Greift der Faule mit der Hand in die Schüssel, / ist er zu träg, sie zum Mund zurückzubringen.", footnotes: ["19,24"] },
      { number: 16, text: "Der Faule hält sich selbst für weiser / als sieben, die klug antworten können." },
      { number: 17, text: "Einen vorbeilaufenden Hund packt bei den Ohren, / wer sich in einen Streit mischt, der ihn nichts angeht." },
      { number: 18, text: "Wie ein Verrückter, der Brandpfeile schleudert, / Pfeile und tödliche Waffen," },
      { number: 19, text: "so ist einer, der seinen Nächsten täuscht / und dazu sagt: Ich mach doch nur Spaß." },
      { number: 20, text: "Ist kein Holz mehr da, erlischt das Feuer; / wo kein Verleumder ist, legt sich der Streit.", footnotes: ["Sir 28,10-12"] },
      { number: 21, text: "Wie Kohlen die Glut und Holz das Feuer, / so schürt ein zänkischer Mensch den Streit." },
      { number: 22, text: "Die Worte des Verleumders sind wie Leckerbissen, / sie gleiten hinab in die Kammern des Leibes.", footnotes: ["18,8"] },
      { number: 23, text: "Silberglasur über Tongeschirr - / glatte Lippen und ein böses Herz." },
      { number: 24, text: "Mit seinen Reden verstellt sich der Gehässige, / doch in seinem Herzen ist er voll Tücke." },
      { number: 25, text: "Klingt seine Stimme auch freundlich, trau ihm nicht, / denn sieben Gräuel sind in seinem Herzen." },
      { number: 26, text: "Hüllt sich sein Hass auch in Heuchelei, / seine Schlechtigkeit wird bloßgestellt in der Volksversammlung." },
      { number: 27, text: "Wer eine Grube gräbt, fällt selbst hinein, / wer einen Stein hochwälzt, auf den rollt er zurück.", footnotes: ["Ps 7,16; Sir 27,25f"] },
      { number: 28, text: "Eine verlogene Zunge führt zum Zusammenbruch, / ein heuchlerischer Mund verursacht den Sturz." },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Rühme dich nicht des morgigen Tages, / denn du weißt nicht, was der Tag gebiert.", footnotes: ["Lk 12,19f"] },
      { number: 2, text: "Rühmen soll dich ein anderer, nicht dein eigener Mund, / ein Fremder, nicht deine eigenen Lippen." },
      { number: 3, text: "Schwer ist der Stein und eine Last ist der Sand, / doch der Ärger mit einem Toren ist schwerer als beide.", footnotes: ["Sir 22,15"] },
      { number: 4, text: "Mag der Zorn grausam sein / und überschäumend die Wut, / wer aber besteht vor der Eifersucht?" },
      { number: 5, text: "Besser offener Tadel / als Liebe, die sich nicht zeigt." },
      { number: 6, text: "Treu gemeint sind die Schläge eines Freundes, / doch trügerisch die Küsse eines Feindes.", footnotes: ["6b: trügerisch, andere Übersetzungsmöglichkeit: reichlich."] },
      { number: 7, text: "Der Satte tritt Honig mit Füßen, / doch dem Hungrigen schmeckt alles Bittere süß." },
      { number: 8, text: "Wie ein Vogel, der aus seinem Nest flüchtet, / so ist ein Mensch, der aus seiner Heimat fliehen muss." },
      { number: 9, text: "Salböl und Weihrauch erfreuen das Herz, / die Herzlichkeit eines Freundes erfreut mehr als duftendes Holz.", footnotes: ["9c: Andere Übersetzungsmöglichkeit: mehr als eigenes Planen."] },
      { number: 10, text: "Deinen Freund und deines Vaters Freund gib nicht auf, / geh nicht in das Haus deines Bruders, wenn du in Not bist. Besser ein Nachbar in der Nähe / als ein Bruder in der Ferne." },
      { number: 11, text: "Sei weise, mein Sohn, und erfreue mein Herz, / damit ich dem antworten kann, der mich beschimpft.", footnotes: ["Ps 119,42"] },
      { number: 12, text: "Der Kluge sieht das Unheil und verbirgt sich, / die Unerfahrenen laufen weiter und müssen es büßen.", footnotes: ["22,3"] },
      { number: 13, text: "Nimm ihm das Kleid; / denn er hat für einen andern gebürgt, / fremder Leute wegen pfände bei ihm!", footnotes: ["20,16"] },
      { number: 14, text: "Wer seinen Nächsten zu laut begrüßt, / dem wird es frühmorgens als Verwünschung ausgelegt." },
      { number: 15, text: "Ein ständig tropfendes Dach in der Regenzeit / und eine zänkische Frau gleichen einander.", footnotes: ["19,13"] },
      { number: 16, text: "Wer sie fest hält, hält den Wind fest / und seine Hand greift nach Öl." },
      { number: 17, text: "Eisen wird an Eisen geschliffen; / so schleift einer den Charakter des andern." },
      { number: 18, text: "Wer einen Feigenbaum pflegt, wird seine Frucht essen, / wer auf seinen Herrn Acht gibt, wird geehrt." },
      { number: 19, text: "Wie Wasser ein Spiegel ist für das Gesicht, / so ist das Herz des Menschen ein Spiegel für den Menschen." },
      { number: 20, text: "Unterwelt und Totenreich sind unersättlich / und unersättlich sind die Augen des Menschen.", footnotes: ["30,15f; Jes 5,14"] },
      { number: 21, text: "Der Schmelztiegel prüft das Silber, der Ofen das Gold, / der Mensch aber wird geprüft im Urteil dessen, der ihn lobt." },
      { number: 22, text: "Zerstampfst du den Toren auch mit dem Stößel, / [im Mörser zwischen den Körnern,] / seine Torheit weicht nicht von ihm." },
      { number: 23, text: "Kümmere dich um das Aussehen deiner Schafe / und sorge für deine Herden;" },
      { number: 24, text: "denn Besitz bleibt nicht für ewig / und Reichtum nicht für alle Zeit." },
      { number: 25, text: "Kommt das Gras hervor, erscheint das Grün, / sammelt man die Kräuter auf den Bergen," },
      { number: 26, text: "dann gibt es Lämmer für deine Kleidung, / Böcke als Kaufpreis für Äcker" },
      { number: 27, text: "und genug Ziegenmilch für dich als Nahrung, / als Nahrung für dein Haus / [und Lebensunterhalt für deine Mägde]." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Der Frevler flieht, auch wenn ihn keiner verfolgt, / der Gerechte fühlt sich sicher wie ein Löwe." },
      { number: 2, text: "Durch seine Frevel bekommt ein Land viele Herrscher, / durch einen verständigen, einsichtsvollen Mann erhält die Ordnung Bestand.", footnotes: ["Der Text ist schwierig. Er bezieht sich möglicherweise auf die rasch wechselnden Regierungen des Nordreichs Israel. Nach G wäre zu übersetzen: Durch die Frevel eines Gewalttätigen werden Streitigkeiten geweckt; durch einen verständigen Mann erlöschen sie."] },
      { number: 3, text: "Ein Vornehmer, der die Armen unterdrückt, / ist wie Regen, der alles wegschwemmt und kein Brot bringt.", footnotes: ["3a: Vornehmer: G: Frevelhafter."] },
      { number: 4, text: "Wer die Lehre preisgibt, rühmt den Frevler, / wer die Lehre beachtet, bekämpft ihn." },
      { number: 5, text: "Böse Menschen verstehen nicht, was recht ist, / die aber, die den Herrn suchen, verstehen alles.", footnotes: ["1 Kor 2,14"] },
      { number: 6, text: "Besser ein Armer, der schuldlos seinen Weg geht, / als ein Reicher, der krumme Wege geht.", footnotes: ["19,1"] },
      { number: 7, text: "Wer sich an die Lehre hält, ist ein verständiger Sohn, / wer mit Verschwendern umgeht, macht seinem Vater Schande." },
      { number: 8, text: "Wer sein Vermögen durch Zins und Aufschlag vermehrt, / sammelt für den, der Erbarmen hat mit den Armen.", footnotes: ["Ex 22,24"] },
      { number: 9, text: "Wendet einer sein Ohr ab, um die Lehre nicht zu hören, / dann ist sogar sein Gebet ein Gräuel." },
      { number: 10, text: "Wer Rechtschaffene irreführt auf einen bösen Weg, / der fällt in seine eigene Grube; / die Schuldlosen aber erlangen Gutes." },
      { number: 11, text: "Der Reiche hält sich selbst für klug, / doch ein verständiger Armer durchschaut ihn." },
      { number: 12, text: "Haben Gerechte die Oberhand, gibt es glanzvolle Zeiten, / erheben sich die Frevler, verstecken sich die Menschen.", footnotes: ["28,28; 29,2"] },
      { number: 13, text: "Wer seine Sünden verheimlicht, hat kein Glück, / wer sie bekennt und meidet, findet Erbarmen.", footnotes: ["Ps 32,2-5"] },
      { number: 14, text: "Wohl dem Menschen, der stets Gott fürchtet; / wer aber sein Herz verhärtet, fällt ins Unglück." },
      { number: 15, text: "Ein grollender Löwe, ein gieriger Bär - / ein frevelhafter Herrscher über ein schwaches Volk." },
      { number: 16, text: "Mancher Fürst ist klein an Verstand und groß als Unterdrücker; / wer Ausbeutung hasst, hat ein langes Leben." },
      { number: 17, text: "Ein Mensch, auf dem Blutschuld lastet, / ist flüchtig bis zum Grab; man halte ihn nicht.", footnotes: ["Gen 4,12", "17b: Andere Übersetzungsmöglichkeit: man unterstütze ihn nicht."] },
      { number: 18, text: "Wer schuldlos seinen Weg geht, dem wird geholfen, / wer krumme Wege geht, fällt in die Grube." },
      { number: 19, text: "Wer sein Feld bestellt, wird satt von Brot, / wer nichtigen Dingen nachjagt, wird satt von Armut.", footnotes: ["12,11"] },
      { number: 20, text: "Ein ehrlicher Mensch erntet vielfachen Segen, / wer aber hastet, um sich zu bereichern, bleibt nicht ungestraft.", footnotes: ["Sir 11,10f"] },
      { number: 21, text: "Auf die Person sehen ist nicht recht, / für einen Bissen Brot wird mancher zum Verbrecher.", footnotes: ["24,23"] },
      { number: 22, text: "Nach Reichtum giert ein neidischer Mensch / und bedenkt nicht, dass Mangel über ihn kommen wird." },
      { number: 23, text: "Wer einen andern zurechtweist, / findet schließlich Dank, / mehr als der Schmeichler." },
      { number: 24, text: "Wer Vater oder Mutter beraubt / und meint, er tue kein Unrecht, / macht sich zum Genossen des Mörders.", footnotes: ["18,9"] },
      { number: 25, text: "Der Habgierige erregt Streit, / wer auf den Herrn vertraut, wird reichlich gelabt." },
      { number: 26, text: "Wer auf seinen eigenen Verstand vertraut, ist ein Tor, / wer in Weisheit seinen Weg geht, wird gerettet." },
      { number: 27, text: "Wer dem Armen gibt, hat keinen Mangel, / wer seine Augen verschließt, wird viel verflucht." },
      { number: 28, text: "Erheben sich die Frevler, dann verbergen sich die Menschen, / gehen sie zugrunde, dann kommen die Gerechten an die Macht.", footnotes: ["28,12"] },
    ] },
    { number: 29, verses: [
      { number: 1, text: "Wer bei Tadel halsstarrig bleibt, / wird plötzlich zerschmettert / und es gibt keine Heilung." },
      { number: 2, text: "Kommen die Gerechten an die Macht, dann freut sich das Volk, / herrscht der Frevler, dann stöhnt das Volk.", footnotes: ["28,12.28"] },
      { number: 3, text: "Wer Weisheit liebt, erfreut seinen Vater; / wer mit Dirnen verkehrt, verschleudert das Vermögen." },
      { number: 4, text: "Ein König richtet das Land auf durch Pflege des Rechts, / wer Abgaben erpresst, zerstört es.", footnotes: ["29,14"] },
      { number: 5, text: "Wer seinem Nächsten schmeichelt, / breitet ihm ein Netz vor die Füße." },
      { number: 6, text: "In Sünde verstrickt sich der Böse, / doch der Gerechte jubelt und freut sich." },
      { number: 7, text: "Der Gerechte hat Verständnis für den Rechtsstreit der Armen, / der Frevler aber kennt kein Verständnis.", footnotes: ["Jes 1,17f"] },
      { number: 8, text: "Hetzer bringen eine Stadt in Aufruhr, / Weise beschwichtigen die Erregung.", footnotes: ["Sir 28,14"] },
      { number: 9, text: "Rechtet ein Weiser mit einem Toren, / tobt dieser und lacht und gibt keine Ruhe." },
      { number: 10, text: "Mörder hassen den Schuldlosen, / Rechtschaffene bemühen sich um sein Leben." },
      { number: 11, text: "Ein Tor lässt seiner ganzen Erregung freien Lauf, / aber ein Weiser hält sie zurück." },
      { number: 12, text: "Achtet ein Herrscher auf Lügen, / werden alle seine Beamten zu Schurken." },
      { number: 13, text: "Der Arme und der Ausbeuter begegnen einander, / der Herr gibt beiden das Augenlicht.", footnotes: ["22,2"] },
      { number: 14, text: "Spricht ein König den Geringen zuverlässig Recht, / hat sein Thron für immer Bestand." },
      { number: 15, text: "Rute und Rüge verleihen Weisheit, / ein zügelloser Knabe macht seiner Mutter Schande.", footnotes: ["13,24; 22,15; 23,13; Sir 30,1"] },
      { number: 16, text: "Herrschen die Frevler, dann herrscht die Sünde, / doch die Gerechten erleben ihren Sturz." },
      { number: 17, text: "Züchtige deinen Sohn, so wird er dir Verdruss ersparen / und deinem Herzen Freude machen.", footnotes: ["17a: Wörtlich: so wird er dir Ruhe gewähren."] },
      { number: 18, text: "Ohne prophetische Offenbarung verwildert das Volk; / wohl ihm, wenn es die Lehre bewahrt." },
      { number: 19, text: "Durch Worte wird kein Sklave gebessert, / er versteht sie wohl, aber kehrt sich nicht daran." },
      { number: 20, text: "Siehst du einen, der eilfertig ist im Reden, / mehr Hoffnung gibt es für den Toren als für ihn.", footnotes: ["26,12"] },
      { number: 21, text: "Ein Sklave, verwöhnt von Jugend an, / wird am Ende widerspenstig." },
      { number: 22, text: "Ein aufbrausender Mensch erregt Streit, / ein Jähzorniger begeht viele Sünden." },
      { number: 23, text: "Hochmut erniedrigt den Menschen, / doch der Demütige kommt zu Ehren." },
      { number: 24, text: "Wer mit dem Dieb teilt, hasst sich selbst, / er hört die Verfluchung, doch er macht keine Anzeige.", footnotes: ["24b: Vgl. die Anmerkungen zu Lev 5,1 und Ri 17,2."] },
      { number: 25, text: "Die Angst des Menschen führt ihn in die Falle; / wer auf den Herrn vertraut, ist gesichert." },
      { number: 26, text: "Viele suchen die Gunst des Herrschers, / aber das Recht kommt für alle vom Herrn." },
      { number: 27, text: "Der Übeltäter ist den Gerechten ein Gräuel. / Der Rechtschaffene ist für den Frevler ein Gräuel." },
    ] },
    { number: 30, verses: [
      { number: 1, text: "Worte Agurs, des Sohnes des Jake aus Massa. Spruch des Mannes Laïtiël: / Ich mühte mich ab mit Gott und bin am Ende.", heading: "Die Worte Agurs: 30,1-14", footnotes: ["1-14: Die zwei letzten Kapitel bringen Anhänge an die zweite salomonische Sammlung. Die Worte Agurs aus dem ismaëlitischen Massa (?) scheinen bis 30,14 zu reichen. Sie stehen in G hinter 24,22. Das Gebet in VV. 7-9, der Vers 10 und die VV. 11-14 sind inhaltlich selbstständig. Laïtiël, der Beiname Agurs, konnte verstanden werden als: Ich mühte mich ab mit Gott."] },
      { number: 2, text: "Denn ich bin zu dumm für einen Menschen, / ich habe keinen Menschenverstand," },
      { number: 3, text: "ich habe keine Weisheit gelernt / und keine Kenntnis des Heiligen erlangt.", footnotes: ["9,10"] },
      { number: 4, text: "Wer stieg zum Himmel hinauf und kam wieder herab? / Wer sammelte den Wind in seine Fäuste? / Wer band das Wasser in ein Gewand? / Wer setzte fest alle Enden der Erde? / Wie ist sein Name und wie der Name seines Sohnes, wenn du es weißt?", footnotes: ["Ijob 38,1-35"] },
      { number: 5, text: "Jede Rede Gottes ist im Feuer geläutert; / ein Schild ist er für alle, die bei ihm sich bergen.", footnotes: ["Ps 18,31"] },
      { number: 6, text: "Füg seinen Worten nichts hinzu, / sonst überführt er dich und du stehst als Lügner da." },
      { number: 7, text: "Um zweierlei bitte ich dich, / versag es mir nicht, bevor ich sterbe:", footnotes: ["7-9: Das Stichwort «Lüge» veranlasste wohl den Einschub des Gebets des Bescheidenen."] },
      { number: 8, text: "Falschheit und Lügenwort halt fern von mir; / gib mir weder Armut noch Reichtum, / nähr mich mit dem Brot, das mir nötig ist," },
      { number: 9, text: "damit ich nicht, satt geworden, dich verleugne / und sage: Wer ist denn der Herr?, damit ich nicht als Armer zum Dieb werde / und mich am Namen meines Gottes vergreife.", footnotes: ["Sir 5,1-3"] },
      { number: 10, text: "Verleumde nicht den Knecht bei seinem Herrn, / sonst verflucht er dich und du musst es büßen." },
      { number: 11, text: "Ein Geschlecht, das seinem Vater flucht / und seine Mutter nicht segnet;", footnotes: ["11a.12a.13a.14a: Der Anfang dieser Worte meint sinngemäß: Weh dem Geschlecht . . ."] },
      { number: 12, text: "ein Geschlecht, das rein ist in den eigenen Augen, / doch nicht gewaschen von seinem Schmutz;" },
      { number: 13, text: "ein Geschlecht - wie überheblich sind seine Augen / und wie hochmütig seine Wimpern;" },
      { number: 14, text: "ein Geschlecht, dessen Zähne Schwerter / und dessen Gebiss Messer sind, / um die Notleidenden aus dem Land wegzufressen / und die Armen weg aus der Menschheit." },
      { number: 15, text: "Der Blutegel hat zwei Töchter: Gib! - Gib! Drei sind es, die nie satt werden, / vier sagen nie: Genug:", heading: "Die Zahlensprüche: 30,15-33", footnotes: ["15-33: Die selbstständige Gruppe der Zahlensprüche und die Worte an Lemuël (31,1-9) stehen in G hinter 24,34. 15a: Wohl ein versprengtes Fragment. - Der Blutegel ist Bild für den Habsüchtigen; seine beiden «Töchter» sind seine Saug- und Haftorgane."] },
      { number: 16, text: "Die Unterwelt und der unfruchtbare Mutterschoß, / die Erde, die nicht satt wird an Wasser, / und das Feuer, das nie sagt: Genug!" },
      { number: 17, text: "Ein Auge, das den Vater verspottet / und die alte Mutter verachtet, / das hacken die Raben am Bach aus, / die jungen Adler fressen es auf.", footnotes: ["Eine Unterbrechung, wohl verursacht durch das Stichwort «Adler» in V. 19. Eine andere Erweiterung ist V. 20."] },
      { number: 18, text: "Drei Dinge sind mir unbegreiflich, / vier vermag ich nicht zu fassen:" },
      { number: 19, text: "den Weg des Adlers am Himmel, / den Weg der Schlange über den Felsen, / den Weg des Schiffes auf hoher See, / den Weg des Mannes bei der jungen Frau." },
      { number: 20, text: "So benimmt sich die ehebrecherische Frau: / Sie isst, wischt sich den Mund / und sagt: Ich habe nichts Böses getan." },
      { number: 21, text: "Unter dreien erzittert das Land, / unter vieren wird es ihm unerträglich:" },
      { number: 22, text: "unter einem Sklaven, wenn er König wird, / und einem Toren, wenn er Brot im Überfluss hat," },
      { number: 23, text: "unter einer Verschmähten, wenn sie geheiratet wird, / und einer Sklavin, wenn sie ihre Herrin verdrängt." },
      { number: 24, text: "Vier sind die Kleinsten auf Erden / und sind doch die Allerklügsten:" },
      { number: 25, text: "Die Ameisen sind kein starkes Volk / und besorgen sich doch im Sommer ihr Futter;", footnotes: ["6,6-8"] },
      { number: 26, text: "Klippdachse sind ein Volk ohne Macht / und doch bauen sie ihre Wohnung im Fels;", footnotes: ["Ps 104,18"] },
      { number: 27, text: "die Heuschrecken haben keinen König / und doch schwärmen sie alle geordnet aus;" },
      { number: 28, text: "Eidechsen fängst du mit der Hand / und doch wohnen sie in Königspalästen." },
      { number: 29, text: "Drei sind es, die stolz einherschreiten, / vier haben einen stolzen Gang:" },
      { number: 30, text: "der Löwe, der Held unter den Tieren, / der vor keinem umkehrt;" },
      { number: 31, text: "der Hahn, der einherstolziert, und der Leitbock / und der König, wenn er vor seinem Volk auftritt wie ein Gott.", footnotes: ["H ist unklar."] },
      { number: 32, text: "Wenn du dich stolz erhoben und dabei blamiert hast / oder wenn du nachdenkst - so leg die Hand auf den Mund!", footnotes: ["32a: blamiert, wörtlich: als Tor erwiesen."] },
      { number: 33, text: "Denn stößt man Milch, so gibt es Butter, / stößt man die Nase, so gibt es Blut, / stößt man den Zorn, so gibt es Streit." },
    ] },
    { number: 31, verses: [
      { number: 1, text: "Worte an Lemuël, den König von Massa, mit denen ihn seine Mutter ermahnt hat:", heading: "Die Worte an Lemuël: 31,1-9", footnotes: ["Wie Agur (vgl. 30,1) stammt auch Lemuël aus dem nordarabischen Massa. Sprachliche Anklänge an das Aramäische und der eigenartige Inhalt weisen auf nichtisraelitischen Ursprung hin."] },
      { number: 2, text: "Was soll ich dir sagen, Lemuël, mein Erstgeborener, / du Sohn meines Schoßes, / was, du Sohn meiner Gelübde?", footnotes: ["1 Sam 1,11", "2a: Ergänzt nach G; H hat kein Zeitwort."] },
      { number: 3, text: "Gib deine Kraft nicht den Frauen hin, / dein Tun und Treiben nicht denen, die Könige verderben.", footnotes: ["3b: dein Tun und Treiben, wörtlich: deine Wege."] },
      { number: 4, text: "Könige sollen sich nicht, Lemuël, / Könige sollen sich nicht mit Wein betrinken, / Fürsten nicht berauschenden Trank begehren.", footnotes: ["Koh 10,16"] },
      { number: 5, text: "Er könnte beim Trinken seine Pflicht vergessen / und das Recht aller Notleidenden verdrehen." },
      { number: 6, text: "Gebt berauschenden Trank dem, der zusammenbricht, / und Wein denen, die im Herzen verbittert sind." },
      { number: 7, text: "Ein solcher möge trinken und seine Armut vergessen / und nicht mehr an seine Mühsal denken." },
      { number: 8, text: "Öffne deinen Mund für den Stummen, / für das Recht aller Schwachen!" },
      { number: 9, text: "Öffne deinen Mund, richte gerecht, / verschaff dem Bedürftigen und Armen Recht!" },
      { number: 10, text: "Eine tüchtige Frau, wer findet sie? / Sie übertrifft alle Perlen an Wert.", heading: "Das Lob der tüchtigen Frau: 31,10-31", footnotes: ["10-31: Dieses Lied dürfte zu den jüngsten Teilen des Buches gehören. In H beginnen die Verse der Reihe nach mit den Buchstaben des hebräischen Alphabets."] },
      { number: 11, text: "Das Herz ihres Mannes vertraut auf sie / und es fehlt ihm nicht an Gewinn." },
      { number: 12, text: "Sie tut ihm Gutes und nichts Böses / alle Tage ihres Lebens." },
      { number: 13, text: "Sie sorgt für Wolle und Flachs / und schafft mit emsigen Händen." },
      { number: 14, text: "Sie gleicht den Schiffen des Kaufmanns: / Aus der Ferne holt sie ihre Nahrung." },
      { number: 15, text: "Noch bei Nacht steht sie auf, / um ihrem Haus Speise zu geben / [und den Mägden, was ihnen zusteht].", footnotes: ["27,27"] },
      { number: 16, text: "Sie überlegt es und kauft einen Acker, / vom Ertrag ihrer Hände pflanzt sie einen Weinberg." },
      { number: 17, text: "Sie gürtet ihre Hüften mit Kraft / und macht ihre Arme stark." },
      { number: 18, text: "Sie spürt den Erfolg ihrer Arbeit, / auch des Nachts erlischt ihre Lampe nicht." },
      { number: 19, text: "Nach dem Spinnrocken greift ihre Hand, / ihre Finger fassen die Spindel." },
      { number: 20, text: "Sie öffnet ihre Hand für den Bedürftigen / und reicht ihre Hände dem Armen." },
      { number: 21, text: "Ihr bangt nicht für ihr Haus vor dem Schnee; / denn ihr ganzes Haus hat wollene Kleider." },
      { number: 22, text: "Sie hat sich Decken gefertigt, / Leinen und Purpur sind ihr Gewand." },
      { number: 23, text: "Ihr Mann ist in den Torhallen geachtet, / wenn er zu Rat sitzt mit den Ältesten des Landes." },
      { number: 24, text: "Sie webt Tücher und verkauft sie, / Gürtel liefert sie dem Händler." },
      { number: 25, text: "Kraft und Würde sind ihr Gewand, / sie spottet der drohenden Zukunft." },
      { number: 26, text: "Öffnet sie ihren Mund, dann redet sie klug / und gütige Lehre ist auf ihrer Zunge." },
      { number: 27, text: "Sie achtet auf das, was vorgeht im Haus, / und isst nicht träge ihr Brot." },
      { number: 28, text: "Ihre Söhne stehen auf und preisen sie glücklich, / auch ihr Mann erhebt sich und rühmt sie:" },
      { number: 29, text: "Viele Frauen erwiesen sich tüchtig, / doch du übertriffst sie alle." },
      { number: 30, text: "Trügerisch ist Anmut, vergänglich die Schönheit, / nur eine gottesfürchtige Frau verdient Lob." },
      { number: 31, text: "Preist sie für den Ertrag ihrer Hände, / ihre Werke soll man am Stadttor loben." },
    ] },
  ],
};
