import { Book } from "@/lib/types";

export const psalms: Book = {
  id: "psalms",
  name: "Psalmen",
  shortName: "Ps",
  testament: "old",
  introduction: "Ähnlich wie die Bücher des Mose ist diese Sammlung von 150 Einzelliedern, die eines der bedeutendsten Bücher der Heiligen Schrift und auch der gesamten Weltliteratur darstellen, in fünf Bücher eingeteilt: Ps 1 - 41; 42 - 72; 73 - 89; 90 - 106; 107 - 150. Die griechische und die lateinische Bibel haben eine leicht abweichende Zählweise der Psalmen (vgl. Anhang dieser Bibelausgabe). Die heutige Reihenfolge und Anordnung der Psalmen in der hebräischen Bibel, die eine gewisse Korrespondenz zu den Büchern des Mose aufweist, ist höchstwahrscheinlich von ihrem Gebrauch im Synagogengottesdienst her zu verstehen. Thematisch berühren die Psalmen fast alle Fragen und Probleme der alttestamentlichen Theologie. Die neuere Forschung befasst sich besonders mit den verschiedenen Psalmengattungen und ihrem »Sitz im Leben« sowie mit der Überlieferung der einzelnen Psalmen bis zu ihrer jetzigen Gestalt. Man unterscheidet heute vor allem folgende Gattungen: Hymnen, Danklieder, Klagelieder eines Einzelnen und des Volkes, Bittpsalmen, Wallfahrtslieder, Königslieder, Weisheitslieder, »messianische Psalmen«. Dass der Psalter eine Sonderstellung unter den Schriften des Alten Testaments einnimmt, wird kaum bezweifelt. Denn die Psalmen lassen einen einzigartigen Einblick in die innere Struktur der Offenbarung tun. Der Psalter gibt nämlich davon Kunde, dass die Offenbarung sich nicht nur als monologes Sprechen und Handeln Gottes ereignet, sondern dass Gott sich im auserwählten Volk einen Partner bereitet hat, der mit in das Offenbarungsgeschehen einbezogen wird. Die Offenbarung ist demnach ein dialogischer Vorgang: Gott handelt und spricht nicht nur auf das Volk hin, sondern Israel spricht und handelt mit (vgl. Ex 19,8, wo das Volk sich einmütig bereit erklärt, Gottes Auserwählung anzunehmen). Von der Aufnahme des Dialogs durch das auserwählte Volk künden wie kein anderes biblisches Buch die Psalmen: Sie sind die in der Offenbarung und aus ihr erfolgte Antwort auf Gottes offenbartes Wort. Das sollen einige Entsprechungen der Psalmen zu wichtigen alttestamentlichen Texten zeigen: Schöpfungstat und -werk Gottes: Gen 1 - Ps 104. Der Mensch: Gen 1,26-28 - Ps 8. Gottes Heiligkeit: Jes 6 - Ps 99. Das Meerwunder: Ex 13 - 15 - Ps 66. Lobpreis des Bundesgottes: Ex 19 - 24 - Ps 97. Von Ägypten ins Gelobte Land: Ps 114. Von der Patriarchenzeit bis zur Landverleihung: Ps 105. Gottes Heilswerk an Zion: 2 Sam 6 - Ps 48. Der Neue Bund: Jer 31,31-34; Ez 36,24-28 - Ps 51,12-14. Der Messias als König: Jes 9,1-6; 11,1-5; Mi 5,1-3 - Ps 2; 72; 110. Der Messias als Leidensmann: Jes 53 - Ps 22. Natürlich decken sich die Inhalte der angeführten einander entsprechenden Texte nicht vollauf; aber Hauptmotive werden vom lobpreisenden Gottesvolk aufgegriffen, neu interpretiert, um andere Offenbarungsgehalte vermehrt und weiter entfaltet. Noch etwas anderes geht aus den Psalmen hervor, nämlich dass Israels wichtigste und vornehmste Aufgabe, ja der Grund seiner Sonderexistenz im Gotteslob, im Gott gemäßen Kult besteht. Vom Kult her dürfte auch Licht auf die heutige Anordnung der Psalmen fallen. In Neh 8,1-6 ist ein Muster für den Synagogengottesdienst enthalten, der im Exil oder in der nachexilischen Zeit neben dem Tempelgottesdienst aufkommt. Weil der Opferdienst auf den Tempel beschränkt bleibt, handelt es sich im Synagogengottesdienst um den Typus eines reinen Lesegottesdienstes, der in etwa unserem Wortgottesdienst vergleichbar ist. Er enthält als wesentliche Bestandteile: 1. Nach Einleitungsgebeten folgt die Lesung des fälligen Abschnitts aus dem Pentateuch. Die fünf Bücher des Mose sind in einer einjährigen oder dreijährigen Leseordnung auf die Sabbate des Jahres verteilt (für die Feste sind besondere Texte ausgesucht). 2. Dann antwortet das Volk mit dem zugeordneten, vielleicht gesungenen Psalm. 3. An den Psalm schließt eine zweite Lesung aus den Propheten und eine Homilie an. 4. Segensgebete beschließen den Gottesdienst. Mit ziemlicher Sicherheit lässt sich also sagen, dass zur Zeit der Wiederherstellung der nachexilischen Gemeinde unter Esra und Nehemia der Psalter seine heutige Gestalt erhalten hat. Für die einzelnen Psalmen wird man insgesamt eine Entstehungszeit von mehreren Jahrhunderten, von David bis Esra, anzunehmen haben. Weiter deutet ihre Verwendung im Synagogengottesdienst auch eine vom Kult her bestimmte Anordnung und Reihung verschiedener Texteinheiten im Pentateuch an und legt sie nahe. So hat man also für das Verständnis der einzelnen Psalmen und des gesamten Psalters viel mehr als bisher deren Verwendung im Gottesdienst zu berücksichtigen. Der Psalter hat seinen hohen Rang als Gebetbuch des alten Bundesvolkes auch für Christus und die junge Kirche behalten. Die Kirche hat mit den Psalmen auf die in Christus erfüllte Offenbarung geantwortet. Bis heute verwendet sie daher in der Nachfolge des Herrn den Psalter vor allen anderen Gebetstexten für den Gottesdienst in seiner vielfältigen Gestalt.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Wohl dem Mann, der nicht dem Rat der Frevler folgt, / nicht auf dem Weg der Sünder geht, / nicht im Kreis der Spötter sitzt,", heading: "Das erste Buch | Die beiden Wege", footnotes: ["26,4f; Spr 4,14"] },
      { number: 2, text: "sondern Freude hat an der Weisung des Herrn, / über seine Weisung nachsinnt bei Tag und bei Nacht.", footnotes: ["112,1; Jos 1,8"] },
      { number: 3, text: "Er ist wie ein Baum, / der an Wasserbächen gepflanzt ist, der zur rechten Zeit seine Frucht bringt / und dessen Blätter nicht welken. Alles, was er tut, / wird ihm gut gelingen.", footnotes: ["92,13; Jer 17,8"] },
      { number: 4, text: "Nicht so die Frevler: / Sie sind wie Spreu, die der Wind verweht.", footnotes: ["35,5; Ijob 21,18"] },
      { number: 5, text: "Darum werden die Frevler im Gericht nicht bestehen / noch die Sünder in der Gemeinde der Gerechten." },
      { number: 6, text: "Denn der Herr kennt den Weg der Gerechten, / der Weg der Frevler aber führt in den Abgrund.", footnotes: ["119,1; Spr 10,28"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Warum toben die Völker, / warum machen die Nationen vergebliche Pläne?", heading: "Der Herr und sein Gesalbter", footnotes: ["Offb 11,18; Apg 4,25f"] },
      { number: 2, text: "Die Könige der Erde stehen auf, / die Großen haben sich verbündet gegen den Herrn und seinen Gesalbten.", footnotes: ["Offb 19,19"] },
      { number: 3, text: "«Lasst uns ihre Fesseln zerreißen / und von uns werfen ihre Stricke!»" },
      { number: 4, text: "Doch er, der im Himmel thront, lacht, / der Herr verspottet sie.", footnotes: ["59,9"] },
      { number: 5, text: "Dann aber spricht er zu ihnen im Zorn, / in seinem Grimm wird er sie erschrecken:" },
      { number: 6, text: "«Ich selber habe meinen König eingesetzt / auf Zion, meinem heiligen Berg.»" },
      { number: 7, text: "Den Beschluss des Herrn will ich kundtun. / Er sprach zu mir: «Mein Sohn bist du. / Heute habe ich dich gezeugt.", footnotes: ["89,27f; Apg 13,33; Hebr 1,5; 5,5"] },
      { number: 8, text: "Fordre von mir und ich gebe dir die Völker zum Erbe, / die Enden der Erde zum Eigentum.", footnotes: ["(8-9) 72,8; Offb 2,26f", "dir: ergänzt nach G und S."] },
      { number: 9, text: "Du wirst sie zerschlagen mit eiserner Keule, / wie Krüge aus Ton wirst du sie zertrümmern.»", footnotes: ["110,5f; Offb 12,5; 19,15"] },
      { number: 10, text: "Nun denn, ihr Könige, kommt zur Einsicht, / lasst euch warnen, ihr Gebieter der Erde!" },
      { number: 11, text: "Dient dem Herrn in Furcht / und küsst ihm mit Beben die Füße,", footnotes: ["Text korr.; H ist kaum verständlich: und jauchzt mit Beben, küsst den Sohn (in H zum Teil bereits V. 12)."] },
      { number: 12, text: "damit er nicht zürnt / und euer Weg nicht in den Abgrund führt. Denn wenig nur und sein Zorn ist entbrannt. / Wohl allen, die ihm vertrauen!" },
    ] },
    { number: 3, verses: [
      { number: 1, text: "[Ein Psalm Davids, als er vor seinem Sohn Abschalom floh.]", heading: "Hilferuf in Feindesnot", footnotes: ["2 Sam 15", "Die sog. Psalmenüberschriften, die in dieser Übersetzung in eckigen Klammern stehen, sind späte Zusätze. Sie enthalten musikalische Hinweise, deren Bedeutung wir nicht kennen, und Vermutungen über Verfasser und Entstehungsverhältnisse, die der jüdischen Tradition entnommen sind."] },
      { number: 2, text: "Herr, wie zahlreich sind meine Bedränger; / so viele stehen gegen mich auf.", footnotes: ["25,19"] },
      { number: 3, text: "Viele gibt es, die von mir sagen: / «Er findet keine Hilfe bei Gott.» [Sela]", footnotes: ["Das hier und oft in den Psalmen stehende Wort «Sela» ist ebenfalls ein musikalischer Hinweis unbekannter Bedeutung."] },
      { number: 4, text: "Du aber, Herr, bist ein Schild für mich, / du bist meine Ehre und richtest mich auf.", footnotes: ["7,11; 18,3; 33,20; 84,12; 115,9-11; Gen 15,1; Dtn 33,29; Spr 30,5; Sir 34,16"] },
      { number: 5, text: "Ich habe laut zum Herrn gerufen; / da erhörte er mich von seinem heiligen Berg. [Sela]" },
      { number: 6, text: "Ich lege mich nieder und schlafe ein, / ich wache wieder auf, denn der Herr beschützt mich.", footnotes: ["4,9"] },
      { number: 7, text: "Viele Tausende von Kriegern fürchte ich nicht, / wenn sie mich ringsum belagern.", footnotes: ["27,3"] },
      { number: 8, text: "Herr, erhebe dich, / mein Gott, bring mir Hilfe! Denn all meinen Feinden hast du den Kiefer zerschmettert, / hast den Frevlern die Zähne zerbrochen." },
      { number: 9, text: "Beim Herrn findet man Hilfe. / Auf dein Volk komme dein Segen! [Sela]", footnotes: ["Jona 2,10"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Ein Psalm Davids.]", heading: "Gottes Schutz in der Nacht" },
      { number: 2, text: "Wenn ich rufe, erhöre mich, / Gott, du mein Retter! Du hast mir Raum geschaffen, als mir angst war. / Sei mir gnädig und hör auf mein Flehen!", footnotes: ["Wörtlich: Gott meiner Gerechtigkeit."] },
      { number: 3, text: "Ihr Mächtigen, wie lange noch schmäht ihr meine Ehre, / warum liebt ihr den Schein und sinnt auf Lügen? [Sela]" },
      { number: 4, text: "Erkennt doch: Wunderbar handelt der Herr an den Frommen; / der Herr erhört mich, wenn ich zu ihm rufe.", footnotes: ["wunderbar handelt der Herr . . .: Text korr. mich: ergänzt nach G."] },
      { number: 5, text: "Ereifert ihr euch, so sündigt nicht! / Bedenkt es auf eurem Lager und werdet still! [Sela]", footnotes: ["Eph 4,26"] },
      { number: 6, text: "Bringt rechte Opfer dar / und vertraut auf den Herrn!", footnotes: ["51,19.21; 1 Petr 2,5"] },
      { number: 7, text: "Viele sagen: «Wer lässt uns Gutes erleben?» / Herr, lass dein Angesicht über uns leuchten!", footnotes: ["31,17; 44,4; 67,2; 80,4; Num 6,25f"] },
      { number: 8, text: "Du legst mir größere Freude ins Herz, / als andere haben bei Korn und Wein in Fülle." },
      { number: 9, text: "In Frieden leg ich mich nieder und schlafe ein; / denn du allein, Herr, lässt mich sorglos ruhen.", footnotes: ["3,6"] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "[Für den Chormeister. Zum Flötenspiel. Ein Psalm Davids.]", heading: "Ein Gebet zum Morgenopfer" },
      { number: 2, text: "Höre meine Worte, Herr, / achte auf mein Seufzen!" },
      { number: 3, text: "Vernimm mein lautes Schreien, mein König und mein Gott, / denn ich flehe zu dir.", footnotes: ["44,5; 84,4"] },
      { number: 4, text: "Herr, am Morgen hörst du mein Rufen, / am Morgen rüst ich das Opfer zu, halte Ausschau nach dir.", footnotes: ["88,14; Weish 16,28"] },
      { number: 5, text: "Denn du bist kein Gott, dem das Unrecht gefällt; / der Frevler darf nicht bei dir weilen.", footnotes: ["34,17"] },
      { number: 6, text: "Wer sich brüstet, besteht nicht vor deinen Augen; / denn dein Hass trifft alle, die Böses tun.", footnotes: ["(6-7) Spr 6,16-19"] },
      { number: 7, text: "Du lässt die Lügner zugrunde gehn, / Mörder und Betrüger sind dem Herrn ein Gräuel." },
      { number: 8, text: "Ich aber darf dein Haus betreten / dank deiner großen Güte, ich werfe mich nieder in Ehrfurcht / vor deinem heiligen Tempel.", footnotes: ["26,8; 138,2"] },
      { number: 9, text: "Leite mich, Herr, in deiner Gerechtigkeit, / meinen Feinden zum Trotz; / ebne deinen Weg vor mir!", footnotes: ["25,4"] },
      { number: 10, text: "Aus ihrem Mund kommt kein wahres Wort, / ihr Inneres ist voll Verderben. Ihre Kehle ist ein offenes Grab, / aalglatt ist ihre Zunge.", footnotes: ["Röm 3,13", "Text korr. nach G, S und der aramäischen Übersetzung; H: Aus seinem Mund."] },
      { number: 11, text: "Gott, lass sie dafür büßen; / sie sollen fallen durch ihre eigenen Ränke. Verstoße sie wegen ihrer vielen Verbrechen; / denn sie empören sich gegen dich." },
      { number: 12, text: "Doch alle sollen sich freuen, die auf dich vertrauen, / und sollen immerfort jubeln. Beschütze alle, die deinen Namen lieben, / damit sie dich rühmen." },
      { number: 13, text: "Denn du, Herr, segnest den Gerechten. / Wie mit einem Schild deckst du ihn mit deiner Gnade." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel nach der Achten. Ein Psalm Davids.]", heading: "Ein Bußgebet in Todesnot" },
      { number: 2, text: "Herr, strafe mich nicht in deinem Zorn / und züchtige mich nicht in deinem Grimm!", footnotes: ["38,2; Jer 10,24"] },
      { number: 3, text: "Sei mir gnädig, Herr, ich sieche dahin; / heile mich, Herr, denn meine Glieder zerfallen!", footnotes: ["Jer 17,14", "Text korr.; H: denn meine Glieder sind erschrocken."] },
      { number: 4, text: "Meine Seele ist tief verstört. / Du aber, Herr, wie lange säumst du noch?", footnotes: ["13,2f; 42,6"] },
      { number: 5, text: "Herr, wende dich mir zu und errette mich, / in deiner Huld bring mir Hilfe!" },
      { number: 6, text: "Denn bei den Toten denkt niemand mehr an dich. / Wer wird dich in der Unterwelt noch preisen?", footnotes: ["30,10; 88,11-13; 115,17; Sir 17,27; Jes 38,18; Bar 2,17"] },
      { number: 7, text: "Ich bin erschöpft vom Seufzen, / jede Nacht benetzen Ströme von Tränen mein Bett, / ich überschwemme mein Lager mit Tränen." },
      { number: 8, text: "Mein Auge ist getrübt vor Kummer, / ich bin gealtert wegen all meiner Gegner.", footnotes: ["Wörtlich: und es (das Auge) ist matt (alt) geworden."] },
      { number: 9, text: "Weicht zurück von mir, all ihr Frevler; / denn der Herr hat mein lautes Weinen gehört.", footnotes: ["119,115; Mt 7,23"] },
      { number: 10, text: "Gehört hat der Herr mein Flehen, / der Herr nimmt mein Beten an." },
      { number: 11, text: "In Schmach und Verstörung geraten all meine Feinde, / sie müssen weichen und gehen plötzlich zugrunde.", footnotes: ["35,4; 40,15"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "[Ein Klagelied Davids, das er dem Herrn sang wegen des Benjaminiters Kusch.]", heading: "Gebet in Verfolgung" },
      { number: 2, text: "Herr, mein Gott, ich flüchte mich zu dir; / hilf mir vor allen Verfolgern und rette mich," },
      { number: 3, text: "damit mir niemand wie ein Löwe das Leben raubt, / mich zerreißt, und keiner ist da, der mich rettet.", footnotes: ["17,12"] },
      { number: 4, text: "Wenn ich das getan habe, Herr, mein Gott, / wenn an meinen Händen Unrecht klebt,", footnotes: ["(4-5) Ijob 31,7-34"] },
      { number: 5, text: "wenn ich meinem Freunde Böses tat, / wenn ich den quälte, der mich grundlos bedrängt hat," },
      { number: 6, text: "dann soll mich der Feind verfolgen und ergreifen; / er richte mein Leben zugrunde und trete meine Ehre mit Füßen. [Sela]", footnotes: ["143,3", "Wörtlich: er trete mein Leben zu Boden und trete meine Ehre in den Staub."] },
      { number: 7, text: "Herr, steh auf in deinem Zorn, / erheb dich gegen meine wütenden Feinde! Wach auf, du mein Gott! / Du hast zum Gericht gerufen. / Der Herr richtet die Völker.", footnotes: ["9,20", "Text korr. Der Herr richtet die Völker: in H Anfang von V.9."] },
      { number: 8, text: "Um dich stehe die Schar der Völker im Kreis; / über ihnen throne du in der Höhe!", footnotes: ["Text korr."] },
      { number: 9, text: "Herr, weil ich gerecht bin, verschaff mir Recht / (und tu an mir Gutes), weil ich schuldlos bin!", footnotes: ["18,21-27"] },
      { number: 10, text: "Die Bosheit der Frevler finde ein Ende, / doch gib dem Gerechten Bestand, / gerechter Gott, der du auf Herz und Nieren prüfst.", footnotes: ["26,2; Jer 11,20; 17,10; 20,12; Offb 2,23"] },
      { number: 11, text: "Ein Schild über mir ist Gott, / er rettet die Menschen mit redlichem Herzen.", footnotes: ["3,4"] },
      { number: 12, text: "Gott ist ein gerechter Richter, / ein Gott, der täglich strafen kann." },
      { number: 13, text: "Wenn der Frevler sein Schwert wieder schärft, / seinen Bogen spannt und zielt,", footnotes: ["11,2", "Text korr."] },
      { number: 14, text: "dann rüstet er tödliche Waffen gegen sich selbst, / bereitet sich glühende Pfeile.", footnotes: ["Jes 50,11"] },
      { number: 15, text: "Er hat Böses im Sinn; / er geht schwanger mit Unheil und Tücke gebiert er.", footnotes: ["Ijob 15,35; Jes 59,4"] },
      { number: 16, text: "Er gräbt ein Loch, er schaufelt es aus, / doch er stürzt in die Grube, die er selber gemacht hat.", footnotes: ["9,16; 35,7f; 57,7; Spr 26,27; Koh 10,8"] },
      { number: 17, text: "Seine Untat kommt auf sein eigenes Haupt, / seine Gewalttat fällt auf seinen Scheitel zurück." },
      { number: 18, text: "Ich will dem Herrn danken, denn er ist gerecht; / dem Namen des Herrn, des Höchsten, will ich singen und spielen.", footnotes: ["9,12; 18,50; 30,5; 57,8f; 68,5.33; 71,23; 92,2; 104,33; 135,3; 138,2; 146,2"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "[Für den Chormeister. Nach dem Kelterlied. Ein Psalm Davids.]", heading: "Die Herrlichkeit des Schöpfers - die Würde des Menschen" },
      { number: 2, text: "Herr, unser Herrscher, / wie gewaltig ist dein Name auf der ganzen Erde; / über den Himmel breitest du deine Hoheit aus." },
      { number: 3, text: "Aus dem Mund der Kinder und Säuglinge schaffst du dir Lob, / deinen Gegnern zum Trotz; / deine Feinde und Widersacher müssen verstummen.", footnotes: ["Mt 21,16", "Lob: nach G; H: Bollwerk. - Andere Übersetzungsmöglichkeit: Besungen wird dein Glanz am Himmel von der Kinder und Säuglinge Mund. Du hast ein Bollwerk errichtet, deinen Feinden zum Trotz."] },
      { number: 4, text: "Seh ich den Himmel, das Werk deiner Finger, / Mond und Sterne, die du befestigt:" },
      { number: 5, text: "Was ist der Mensch, dass du an ihn denkst, / des Menschen Kind, dass du dich seiner annimmst?", footnotes: ["144,3; (5-7) Gen 1,26-28; Hebr 2,6-8; 1 Kor 15,27; Eph 1,22"] },
      { number: 6, text: "Du hast ihn nur wenig geringer gemacht als Gott, / hast ihn mit Herrlichkeit und Ehre gekrönt.", footnotes: ["G, S und Vg übersetzen: als die Engel."] },
      { number: 7, text: "Du hast ihn als Herrscher eingesetzt über das Werk deiner Hände, / hast ihm alles zu Füßen gelegt:" },
      { number: 8, text: "All die Schafe, Ziegen und Rinder / und auch die wilden Tiere," },
      { number: 9, text: "die Vögel des Himmels und die Fische im Meer, / alles, was auf den Pfaden der Meere dahinzieht." },
      { number: 10, text: "Herr, unser Herrscher, / wie gewaltig ist dein Name auf der ganzen Erde!" },
    ] },
    { number: 9, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Stirb für den Sohn!» Ein Psalm Davids.]", heading: "Gott, der Retter der Armen und Bedrängten" },
      { number: 2, text: "Ich will dir danken, Herr, aus ganzem Herzen, / verkünden will ich all deine Wunder.", footnotes: ["138,1"] },
      { number: 3, text: "Ich will jauchzen und an dir mich freuen, / für dich, du Höchster, will ich singen und spielen." },
      { number: 4, text: "Denn zurückgewichen sind meine Feinde, / gestürzt und vergangen vor deinem Angesicht." },
      { number: 5, text: "Du hast mir Recht verschafft und für mich entschieden, / dich auf den Thron gesetzt als ein gerechter Richter." },
      { number: 6, text: "Du hast die Völker bedroht, die Frevler vernichtet, / ihren Namen gelöscht für immer und ewig." },
      { number: 7, text: "Die Feinde sind dahin, zerschlagen für immer. / Du hast Städte entvölkert, ihr Ruhm ist versunken." },
      { number: 8, text: "Der Herr aber thront für ewig; / er stellt seinen Thron auf zum Gericht." },
      { number: 9, text: "Er richtet den Erdkreis gerecht, / er spricht den Völkern das Urteil, das sie verdienen.", footnotes: ["96,13"] },
      { number: 10, text: "So wird der Herr für den Bedrückten zur Burg, / zur Burg in Zeiten der Not." },
      { number: 11, text: "Darum vertraut dir, wer deinen Namen kennt; / denn du, Herr, verlässt keinen, der dich sucht." },
      { number: 12, text: "Singt dem Herrn, der thront auf dem Zion, / verkündet unter den Völkern seine Taten!", footnotes: ["108,4"] },
      { number: 13, text: "Denn er, der jede Blutschuld rächt, denkt an die Armen / und ihren Notschrei vergisst er nicht.", footnotes: ["Ijob 34,28"] },
      { number: 14, text: "Sei mir gnädig in meiner Not; / Herr, sieh doch, wie sie mich hassen! Führ mich herauf von den Pforten des Todes, /", footnotes: ["30,4; 49,16; 56,14; 68,21; 71,20; 86,13; 116,8; Weish 16,13; Jona 2,7"] },
      { number: 15, text: "damit ich all deinen Ruhm verkünde in den Toren von Zion / und frohlocke, weil du mir hilfst.", footnotes: ["Wörtlich: in den Toren der Tochter Zion."] },
      { number: 16, text: "Völker versanken in der Grube, die sie selber gegraben; / im Netz, das sie heimlich gelegt, hat ihr Fuß sich verfangen.", footnotes: ["7,16"] },
      { number: 17, text: "Kundgetan hat sich der Herr: Er hielt sein Gericht; / im eigenen Werk hat sich der Frevler verstrickt. [Zwischenspiel. Sela]", footnotes: ["Wörtlich: im Werk seiner Hände, er fängt den Frevler."] },
      { number: 18, text: "Hinabfahren müssen die Frevler zum Totenreich, / alle Heiden, die Gott vergessen." },
      { number: 19, text: "Doch der Arme ist nicht auf ewig vergessen, / des Elenden Hoffnung ist nicht für immer verloren." },
      { number: 20, text: "Erheb dich, Herr, damit nicht der Mensch triumphiert, / damit die Völker gerichtet werden vor deinem Angesicht.", footnotes: ["7,7"] },
      { number: 21, text: "Wirf Schrecken auf sie, o Herr! / Erkennen sollen die Völker: Sie sind nur Menschen. [Sela]" },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Herr, warum bleibst du so fern, / verbirgst dich in Zeiten der Not?", heading: "Ein Hilferuf gegen gewalttätige Menschen" },
      { number: 2, text: "In seinem Hochmut quält der Frevler die Armen. / Er soll sich fangen in den Ränken, die er selbst ersonnen hat." },
      { number: 3, text: "Denn der Frevler rühmt sich nach Herzenslust, / er raubt, er lästert und verachtet den Herrn." },
      { number: 4, text: "Überheblich sagt der Frevler: / «Gott straft nicht. Es gibt keinen Gott.» / So ist sein ganzes Denken.", footnotes: ["14,1"] },
      { number: 5, text: "Zu jeder Zeit glückt ihm sein Tun. / Hoch droben und fern von sich wähnt er deine Gerichte." },
      { number: 6, text: "Er sagt in seinem Herzen: «Ich werde niemals wanken. / Von Geschlecht zu Geschlecht trifft mich kein Unglück.»", footnotes: ["Nach G; H ist unklar."] },
      { number: 7, text: "Sein Mund ist voll Fluch und Trug und Gewalttat; / auf seiner Zunge sind Verderben und Unheil.", footnotes: ["Röm 3,14"] },
      { number: 8, text: "Er liegt auf der Lauer in den Gehöften / und will den Schuldlosen heimlich ermorden; / seine Augen spähen aus nach dem Armen.", footnotes: ["11,2", "Nach G."] },
      { number: 9, text: "Er lauert im Versteck wie ein Löwe im Dickicht, / er lauert darauf, den Armen zu fangen; / er fängt den Armen und zieht ihn in sein Netz.", footnotes: ["17,12"] },
      { number: 10, text: "Er duckt sich und kauert sich nieder, / seine Übermacht bringt die Schwachen zu Fall.", footnotes: ["H ist unklar; andere Übersetzungsmöglichkeit für V. 10: Zerschmettert stürzen die Schwachen nieder und fallen in seine Gewalt."] },
      { number: 11, text: "Er sagt in seinem Herzen: «Gott vergisst es, / er verbirgt sein Gesicht, er sieht es niemals.»", footnotes: ["64,6; 73,11; 94,7; Ijob 22,13f; Jes 29,15; Ez 8,12; 9,9"] },
      { number: 12, text: "Herr, steh auf, Gott, erheb deine Hand, / vergiss die Gebeugten nicht!" },
      { number: 13, text: "Warum darf der Frevler Gott verachten / und in seinem Herzen sagen: «Du strafst nicht»?" },
      { number: 14, text: "Du siehst es ja selbst; / denn du schaust auf Unheil und Kummer. Der Schwache vertraut sich dir an; / du bist den Verwaisten ein Helfer.", footnotes: ["H hat zusätzlich: um es in deine Hand zu geben. - Sinn unklar."] },
      { number: 15, text: "Zerbrich den Arm des Frevlers und des Bösen, / bestraf seine Frevel, / sodass man von ihm nichts mehr findet." },
      { number: 16, text: "Der Herr ist König für immer und ewig, / in seinem Land gehen die Heiden zugrunde.", footnotes: ["24,7-10"] },
      { number: 17, text: "Herr, du hast die Sehnsucht der Armen gestillt, / du stärkst ihr Herz, du hörst auf sie:" },
      { number: 18, text: "Du verschaffst den Verwaisten und Bedrückten ihr Recht. / Kein Mensch mehr verbreite Schrecken im Land.", footnotes: ["68,6; 146,9; Ex 22,21; Dtn 10,18"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "[Für den Chormeister. Von David.] Beim Herrn finde ich Zuflucht. / Wie könnt ihr mir sagen: «In die Berge flieh wie ein Vogel»?", heading: "Gottes Blick auf den Menschen", footnotes: ["Nach G und Hieronymus."] },
      { number: 2, text: "Schon spannen die Frevler den Bogen, / sie legen den Pfeil auf die Sehne, um aus dem Dunkel zu treffen / die Menschen mit redlichem Herzen.", footnotes: ["7,13; 10,8; 37,14.32; 64,4f; 119,95"] },
      { number: 3, text: "Gerät alles ins Wanken, / was kann da der Gerechte noch tun?", footnotes: ["Wörtlich: Wenn die Grundfesten eingerissen werden."] },
      { number: 4, text: "Der Herr weilt in seinem heiligen Tempel, / der Thron des Herrn ist im Himmel. Seine Augen schauen herab, / seine Blicke prüfen die Menschen.", footnotes: ["Hab 2,20"] },
      { number: 5, text: "Der Herr prüft Gerechte und Frevler; / wer Gewalttat liebt, den hasst er aus tiefster Seele." },
      { number: 6, text: "Auf die Frevler lasse er Feuer und Schwefel regnen; / sengender Wind sei ihr Anteil.", footnotes: ["140,11; Gen 19,24; Ijob 18,15", "H fügt nach «Schwefel» hinzu: «Schlinge (Verhängnis)», vielleicht eine weitere göttliche Waffe. Text korr.; H: sei der Anteil ihres Bechers."] },
      { number: 7, text: "Denn der Herr ist gerecht, er liebt gerechte Taten; / wer rechtschaffen ist, darf sein Angesicht schauen." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Achten. Ein Psalm Davids.]", heading: "Die Falschheit der Menschen - die Treue Gottes" },
      { number: 2, text: "Hilf doch, o Herr, die Frommen schwinden dahin, / unter den Menschen gibt es keine Treue mehr.", footnotes: ["14,3; Mi 7,2"] },
      { number: 3, text: "Sie lügen einander an, einer den andern, / mit falscher Zunge und zwiespältigem Herzen reden sie.", footnotes: ["(3-5) 15,2f; 28,3; 52,4-6; 55,22; 116,11; 120,2f; Ijob 5,21; Jes 59,3f; Jer 9,7"] },
      { number: 4, text: "Der Herr vertilge alle falschen Zungen, / jede Zunge, die vermessen redet." },
      { number: 5, text: "Sie sagen: «Durch unsre Zunge sind wir mächtig; / unsre Lippen sind unsre Stärke. Wer ist uns überlegen?»" },
      { number: 6, text: "Die Schwachen werden unterdrückt, die Armen seufzen. / Darum spricht der Herr: «Jetzt stehe ich auf, / dem Verachteten bringe ich Heil.»", footnotes: ["dem Verachteten, wörtlich: dem, gegen den man schnaubt."] },
      { number: 7, text: "Die Worte des Herrn sind lautere Worte, / Silber, geschmolzen im Ofen, / von Schlacken geschieden, geläutert siebenfach.", footnotes: ["18,31; 19,8f; 119,140; Spr 30,5", "Nach Hieronymus."] },
      { number: 8, text: "Du, Herr, wirst uns behüten / und uns vor diesen Leuten für immer erretten," },
      { number: 9, text: "auch wenn die Frevler frei umhergehen / und unter den Menschen die Gemeinheit groß wird." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Klage und Vertrauen in großer Not" },
      { number: 2, text: "Wie lange noch, Herr, vergisst du mich ganz? / Wie lange noch verbirgst du dein Gesicht vor mir?", footnotes: ["6,4; 42,10; 89,47; Klgl 5,20; Hab 1,2"] },
      { number: 3, text: "Wie lange noch muss ich Schmerzen ertragen in meiner Seele, / in meinem Herzen Kummer Tag für Tag? / Wie lange noch darf mein Feind über mich triumphieren?", footnotes: ["Text korr.; H ist unverständlich."] },
      { number: 4, text: "Blick doch her, erhöre mich, Herr, mein Gott, / erleuchte meine Augen, damit ich nicht entschlafe und sterbe," },
      { number: 5, text: "damit mein Feind nicht sagen kann: / «Ich habe ihn überwältigt», damit meine Gegner nicht jubeln, / weil ich ihnen erlegen bin." },
      { number: 6, text: "Ich aber baue auf deine Huld, / mein Herz soll über deine Hilfe frohlocken. Singen will ich dem Herrn, / weil er mir Gutes getan hat." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "[Für den Chormeister. Von David.] Die Toren sagen in ihrem Herzen: / «Es gibt keinen Gott.» Sie handeln verwerflich und schnöde; / da ist keiner, der Gutes tut.", heading: "Die Torheit der Gottesleugner", footnotes: ["10,4; Jes 32,6; Mi 7,2; (1-3) Röm 3,10-12; (1-7) Ps 53,1-7"] },
      { number: 2, text: "Der Herr blickt vom Himmel herab auf die Menschen, / ob noch ein Verständiger da ist, der Gott sucht.", footnotes: ["33,13-15"] },
      { number: 3, text: "Alle sind sie abtrünnig und verdorben, / keiner tut Gutes, auch nicht ein Einziger.", footnotes: ["12,2; Gen 6,12"] },
      { number: 4, text: "Haben denn all die Übeltäter keine Einsicht? / Sie verschlingen mein Volk. Sie essen das Brot des Herrn, / doch seinen Namen rufen sie nicht an.", footnotes: ["Mi 3,3"] },
      { number: 5, text: "Es trifft sie Furcht und Schrecken; / denn Gott steht auf der Seite der Gerechten." },
      { number: 6, text: "Die Pläne der Armen wollt ihr vereiteln, / doch ihre Zuflucht ist der Herr." },
      { number: 7, text: "Ach, käme doch vom Zion Hilfe für Israel! / Wenn einst der Herr das Geschick seines Volkes wendet, / dann jubelt Jakob, dann freut sich Israel.", footnotes: ["126,1f; Jes 35,10"] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Herr, wer darf Gast sein in deinem Zelt, / wer darf weilen auf deinem heiligen Berg?", heading: "Die Bedingungen für den Eintritt ins Heiligtum" },
      { number: 2, text: "Der makellos lebt und das Rechte tut; / der von Herzen die Wahrheit sagt /", footnotes: ["(2-3) 12,3-5"] },
      { number: 3, text: "und mit seiner Zunge nicht verleumdet; der seinem Freund nichts Böses antut / und seinen Nächsten nicht schmäht;" },
      { number: 4, text: "der den Verworfenen verachtet, / doch alle, die den Herrn fürchten, in Ehren hält; der sein Versprechen nicht ändert, / das er seinem Nächsten geschworen hat;", footnotes: ["Nach G und S; H ist verderbt."] },
      { number: 5, text: "der sein Geld nicht auf Wucher ausleiht / und nicht zum Nachteil des Schuldlosen Bestechung annimmt. Wer sich danach richtet, / der wird niemals wanken.", footnotes: ["Ex 22,24; 23,8"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "[Ein Lied Davids.] Behüte mich, Gott, denn ich vertraue dir. /", heading: "Gott, der Anteil seiner Getreuen" },
      { number: 2, text: "Ich sage zum Herrn: «Du bist mein Herr; / mein ganzes Glück bist du allein.»", footnotes: ["73,25", "2-4: Sinn von H ist nicht ganz klar."] },
      { number: 3, text: "An den Heiligen im Lande, den Herrlichen, / an ihnen nur hab ich mein Gefallen." },
      { number: 4, text: "Viele Schmerzen leidet, wer fremden Göttern folgt. / Ich will ihnen nicht opfern, / ich nehme ihre Namen nicht auf meine Lippen." },
      { number: 5, text: "Du, Herr, gibst mir das Erbe und reichst mir den Becher; / du hältst mein Los in deinen Händen.", footnotes: ["23,5"] },
      { number: 6, text: "Auf schönem Land fiel mir mein Anteil zu. / Ja, mein Erbe gefällt mir gut.", footnotes: ["73,26", "mein: ergänzt nach G und S."] },
      { number: 7, text: "Ich preise den Herrn, der mich beraten hat. / Auch mahnt mich mein Herz in der Nacht." },
      { number: 8, text: "Ich habe den Herrn beständig vor Augen. / Er steht mir zur Rechten, ich wanke nicht.", footnotes: ["(8-11) Apg 2,25-28"] },
      { number: 9, text: "Darum freut sich mein Herz und frohlockt meine Seele; / auch mein Leib wird wohnen in Sicherheit." },
      { number: 10, text: "Denn du gibst mich nicht der Unterwelt preis; / du lässt deinen Frommen das Grab nicht schauen.", footnotes: ["Apg 2,31; 13,35"] },
      { number: 11, text: "Du zeigst mir den Pfad zum Leben. / Vor deinem Angesicht herrscht Freude in Fülle, / zu deiner Rechten Wonne für alle Zeit.", footnotes: ["25,4"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "[Ein Gebet Davids.] Höre, Herr, die gerechte Sache, / achte auf mein Flehen, / vernimm mein Gebet von Lippen ohne Falsch!", heading: "Das Gebet eines Verfolgten" },
      { number: 2, text: "Von deinem Angesicht ergehe mein Urteil; / denn deine Augen sehen, was recht ist." },
      { number: 3, text: "Prüfst du mein Herz, / suchst du mich heim in der Nacht und erprobst mich, / dann findest du an mir kein Unrecht. Mein Mund verging sich nicht, /", footnotes: ["139,1; Ijob 7,18; 23,10", "findest du: Text korr. nach G und S."] },
      { number: 4, text: "trotz allem, was die Menschen auch treiben; / ich halte mich an das Wort deiner Lippen.", footnotes: ["(4-5) Ijob 23,11f"] },
      { number: 5, text: "Auf dem Weg deiner Gebote gehn meine Schritte, / meine Füße wanken nicht auf deinen Pfaden." },
      { number: 6, text: "Ich rufe dich an, denn du, Gott, erhörst mich. / Wende dein Ohr mir zu, vernimm meine Rede!" },
      { number: 7, text: "Wunderbar erweise deine Huld! / Du rettest alle, die sich an deiner Rechten vor den Feinden bergen.", footnotes: ["(7-8) 36,8; 57,2; 61,5; 63,8; 91,4; Ex 37,9; Mt 23,37"] },
      { number: 8, text: "Behüte mich wie den Augapfel, den Stern des Auges, / birg mich im Schatten deiner Flügel", footnotes: ["Dtn 32,10; Sach 2,12"] },
      { number: 9, text: "vor den Frevlern, die mich hart bedrängen, / vor den Feinden, die mich wütend umringen." },
      { number: 10, text: "Sie haben ihr hartes Herz verschlossen, / sie führen stolze Worte im Mund,", footnotes: ["ihr hartes Herz, wörtlich: ihr Fett."] },
      { number: 11, text: "sie lauern mir auf, jetzt kreisen sie mich ein; / sie trachten danach, mich zu Boden zu strecken,", footnotes: ["sie lauern mir auf: Text korr.; H: unsere Schritte."] },
      { number: 12, text: "so wie der Löwe voll Gier ist zu zerreißen, / wie der junge Löwe, der im Hinterhalt lauert.", footnotes: ["7,3; 10,9; 22,14.22; 35,17; 57,5; 58,7"] },
      { number: 13, text: "Erheb dich, Herr, tritt dem Frevler entgegen! / Wirf ihn zu Boden, mit deinem Schwert entreiß mich ihm!" },
      { number: 14, text: "Rette mich, Herr, mit deiner Hand vor diesen Leuten, / vor denen, die im Leben schon alles haben. Du füllst ihren Leib mit Gütern, / auch ihre Söhne werden noch satt / und hinterlassen den Enkeln, was übrig bleibt.", footnotes: ["73,12", "ist verderbt."] },
      { number: 15, text: "Ich aber will in Gerechtigkeit dein Angesicht schauen, / mich satt sehen an deiner Gestalt, wenn ich erwache.", footnotes: ["Num 12,8; Offb 22,4"] },
    ] },
    { number: 18, verses: [
      { number: 1, text: "[Für den Chormeister. Von David, dem Knecht des Herrn, der dem Herrn die Worte dieses Liedes sang an dem Tag, als ihn der Herr aus der Gewalt all seiner Feinde und aus der Hand Sauls errettet hatte.", heading: "Ein Danklied des Königs für Rettung und Sieg", footnotes: ["(1-51) 2 Sam 22,1-51"] },
      { number: 2, text: "Er sprach:] Ich will dich rühmen, Herr, meine Stärke, /", footnotes: ["Text korr.; H: Ich will dich lieben."] },
      { number: 3, text: "Herr, du mein Fels, meine Burg, mein Retter, mein Gott, meine Feste, in der ich mich berge, / mein Schild und sicheres Heil, meine Zuflucht.", footnotes: ["92,16; 27,5; 31,3; 3,4"] },
      { number: 4, text: "Ich rufe: Der Herr sei gepriesen!, / und ich werde vor meinen Feinden gerettet." },
      { number: 5, text: "Mich umfingen die Fesseln des Todes, / mich erschreckten die Fluten des Verderbens.", footnotes: ["42,8; (5-7) 116,3f"] },
      { number: 6, text: "Die Bande der Unterwelt umstrickten mich, / über mich fielen die Schlingen des Todes." },
      { number: 7, text: "In meiner Not rief ich zum Herrn / und schrie zu meinem Gott. Aus seinem Heiligtum hörte er mein Rufen, / mein Hilfeschrei drang an sein Ohr." },
      { number: 8, text: "Da wankte und schwankte die Erde, / die Grundfesten der Berge erbebten. / Sie wankten, denn sein Zorn war entbrannt.", footnotes: ["68,9"] },
      { number: 9, text: "Rauch stieg aus seiner Nase auf, / aus seinem Mund kam verzehrendes Feuer, / glühende Kohlen sprühten aus von ihm.", footnotes: ["97,3; Ex 19,18"] },
      { number: 10, text: "Er neigte den Himmel und fuhr herab, / zu seinen Füßen dunkle Wolken.", footnotes: ["(10-17) 144,5-7"] },
      { number: 11, text: "Er fuhr auf dem Kerub und flog daher; / er schwebte auf den Flügeln des Windes.", footnotes: ["99,1; 104,3"] },
      { number: 12, text: "Er hüllte sich in Finsternis, / in dunkles Wasser und dichtes Gewölk wie in ein Zelt." },
      { number: 13, text: "Von seinem Glanz erstrahlten die Wolken, / Hagel fiel nieder und glühende Kohlen.", footnotes: ["die Wolken: Text korr. nach G und 2 Sam 22,14."] },
      { number: 14, text: "Da ließ der Herr den Donner im Himmel erdröhnen, / der Höchste ließ seine Stimme erschallen.", footnotes: ["(14-15) 77,18f", "Text korr. nach G und 2 Sam 22,14; H fügt am Schluss hinzu: Hagel und glühende Kohlen."] },
      { number: 15, text: "Er schoss seine Pfeile und streute sie, / er schleuderte Blitze und jagte sie dahin.", footnotes: ["Text korr. nach 2 Sam 22,15."] },
      { number: 16, text: "Da wurden sichtbar die Tiefen des Meeres, / die Grundfesten der Erde wurden entblößt vor deinem Drohen, Herr, / vor dem Schnauben deines zornigen Atems.", footnotes: ["77,17; Ex 15,8", "Text korr.; H: des Wassers des Meeres."] },
      { number: 17, text: "Er griff aus der Höhe herab und fasste mich, / zog mich heraus aus gewaltigen Wassern.", footnotes: ["32,6; 144,7"] },
      { number: 18, text: "Er entriss mich meinen mächtigen Feinden, / die stärker waren als ich und mich hassten." },
      { number: 19, text: "Sie überfielen mich am Tag meines Unheils, / doch der Herr wurde mein Halt." },
      { number: 20, text: "Er führte mich hinaus ins Weite, / er befreite mich, denn er hatte an mir Gefallen.", footnotes: ["31,9"] },
      { number: 21, text: "Der Herr hat gut an mir gehandelt und mir vergolten, / weil ich gerecht bin und meine Hände rein sind." },
      { number: 22, text: "Denn ich hielt mich an die Wege des Herrn / und fiel nicht ruchlos ab von meinem Gott." },
      { number: 23, text: "Ja, ich habe alle seine Gebote vor Augen, / weise seine Gesetze niemals ab." },
      { number: 24, text: "Ich war vor ihm ohne Makel, / ich nahm mich in Acht vor der Sünde." },
      { number: 25, text: "Darum hat der Herr mir vergolten, weil ich gerecht bin / und meine Hände rein sind vor seinen Augen." },
      { number: 26, text: "Gegen den Treuen zeigst du dich treu, / an dem Aufrichtigen handelst du recht." },
      { number: 27, text: "Gegen den Reinen zeigst du dich rein, / doch falsch gegen den Falschen." },
      { number: 28, text: "Dem bedrückten Volk bringst du Heil, / doch die Blicke der Stolzen zwingst du nieder.", footnotes: ["Ijob 22,29"] },
      { number: 29, text: "Du, Herr, lässt meine Leuchte erstrahlen, / mein Gott macht meine Finsternis hell.", footnotes: ["Ijob 29,3"] },
      { number: 30, text: "Mit dir erstürme ich Wälle, / mit meinem Gott überspringe ich Mauern.", footnotes: ["Text korr.; H ist unklar."] },
      { number: 31, text: "Vollkommen ist Gottes Weg, / das Wort des Herrn ist im Feuer geläutert. / Ein Schild ist er für alle, die sich bei ihm bergen.", footnotes: ["25,4; 12,7; 3,4; Spr 30,5"] },
      { number: 32, text: "Denn wer ist Gott als allein der Herr, / wer ist ein Fels, wenn nicht unser Gott?", footnotes: ["Jes 44,8; Ps 92,16"] },
      { number: 33, text: "Gott hat mich mit Kraft umgürtet, / er führte mich auf einen Weg ohne Hindernis." },
      { number: 34, text: "Er ließ mich springen schnell wie Hirsche, / auf hohem Weg ließ er mich gehen.", footnotes: ["Hab 3,19; Jes 58,14", "Wörtlich: auf meinen Höhen."] },
      { number: 35, text: "Er lehrte meine Hände zu kämpfen, / meine Arme, den ehernen Bogen zu spannen.", footnotes: ["144,1"] },
      { number: 36, text: "Du gabst mir deine Hilfe zum Schild, / deine Rechte stützt mich; / du neigst dich mir zu und machst mich groß.", footnotes: ["Text korr.; in H andere Vokalisierung."] },
      { number: 37, text: "Du schaffst meinen Schritten weiten Raum, / meine Knöchel wanken nicht.", footnotes: ["Ijob 18,7"] },
      { number: 38, text: "Ich verfolge meine Feinde und hole sie ein, / ich kehre nicht um, bis sie vernichtet sind." },
      { number: 39, text: "Ich schlage sie nieder; / sie können sich nicht mehr erheben, sie fallen und liegen unter meinen Füßen." },
      { number: 40, text: "Du hast mich zum Kampf mit Kraft umgürtet, / hast alle in die Knie gezwungen, die sich gegen mich erhoben.", footnotes: ["alle: ergänzt nach G."] },
      { number: 41, text: "Meine Feinde hast du zur Flucht gezwungen; / ich konnte die vernichten, die mich hassen.", footnotes: ["21,13"] },
      { number: 42, text: "Sie schreien, doch hilft ihnen niemand, / sie schreien zum Herrn, doch er gibt keine Antwort." },
      { number: 43, text: "Ich zermalme sie zu Staub vor dem Wind, / schütte sie auf die Straße wie Unrat." },
      { number: 44, text: "Du rettest mich vor zahllosem Kriegsvolk, / du machst mich zum Haupt über ganze Völker. Stämme, die ich früher nicht kannte, sind mir nun untertan. /", footnotes: ["2,8", "Text leicht korr."] },
      { number: 45, text: "Sobald sie mich nur hören, gehorchen sie. Mir huldigen die Söhne der Fremde, /" },
      { number: 46, text: "sie kommen zitternd aus ihren Burgen hervor.", footnotes: ["Mi 7,17", "Text korr.; in H geht eine zusätzliche Verszeile (46a) voraus: Die Söhne der Fremde verschmachten und kommen."] },
      { number: 47, text: "Es lebt der Herr! Mein Fels sei gepriesen. / Der Gott meines Heils sei hoch erhoben;", footnotes: ["92,16"] },
      { number: 48, text: "denn Gott verschaffte mir Vergeltung / und unterwarf mir die Völker.", footnotes: ["144,2"] },
      { number: 49, text: "Du hast mich von meinen Feinden befreit, / mich über meine Gegner erhoben, / dem Mann der Gewalt mich entrissen." },
      { number: 50, text: "Darum will ich dir danken, Herr, vor den Völkern, / ich will deinem Namen singen und spielen.", footnotes: ["108,4; 7,18; Röm 15,9"] },
      { number: 51, text: "Seinem König verlieh er große Hilfe, / Huld erwies er seinem Gesalbten, / David und seinem Stamm auf ewig.", footnotes: ["20,7; 89,29f; 144,10"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Lob der Schöpfung - Lob des Gesetzes" },
      { number: 2, text: "Die Himmel rühmen die Herrlichkeit Gottes, / vom Werk seiner Hände kündet das Firmament.", footnotes: ["50,6; Sir 43,1f"] },
      { number: 3, text: "Ein Tag sagt es dem andern, / eine Nacht tut es der andern kund," },
      { number: 4, text: "ohne Worte und ohne Reden, / unhörbar bleibt ihre Stimme." },
      { number: 5, text: "Doch ihre Botschaft geht in die ganze Welt hinaus, / ihre Kunde bis zu den Enden der Erde. Dort hat er der Sonne ein Zelt gebaut. /", footnotes: ["Röm 10,18", "Text korr. nach G."] },
      { number: 6, text: "Sie tritt aus ihrem Gemach hervor wie ein Bräutigam; sie frohlockt wie ein Held / und läuft ihre Bahn.", footnotes: ["Ergänzt nach G und S."] },
      { number: 7, text: "Am einen Ende des Himmels geht sie auf / und läuft bis ans andere Ende; / nichts kann sich vor ihrer Glut verbergen." },
      { number: 8, text: "Die Weisung des Herrn ist vollkommen, / sie erquickt den Menschen. Das Gesetz des Herrn ist verlässlich, / den Unwissenden macht es weise.", footnotes: ["(8-9) 12,7", "Textvorschlag für die Psalmodie: Die Weisung des Herrn ist vollkommen und gut."] },
      { number: 9, text: "Die Befehle des Herrn sind richtig, / sie erfreuen das Herz; das Gebot des Herrn ist lauter, / es erleuchtet die Augen." },
      { number: 10, text: "Die Furcht des Herrn ist rein, / sie besteht für immer. Die Urteile des Herrn sind wahr, / gerecht sind sie alle." },
      { number: 11, text: "Sie sind kostbarer als Gold, als Feingold in Menge. / Sie sind süßer als Honig, als Honig aus Waben.", footnotes: ["119,72"] },
      { number: 12, text: "Auch dein Knecht lässt sich von ihnen warnen; / wer sie beachtet, hat reichen Lohn." },
      { number: 13, text: "Wer bemerkt seine eigenen Fehler? / Sprich mich frei von Schuld, die mir nicht bewusst ist!" },
      { number: 14, text: "Behüte deinen Knecht auch vor vermessenen Menschen; / sie sollen nicht über mich herrschen. Dann bin ich ohne Makel / und rein von schwerer Schuld." },
      { number: 15, text: "Die Worte meines Mundes mögen dir gefallen; / was ich im Herzen erwäge, stehe dir vor Augen, / Herr, mein Fels und mein Erlöser.", footnotes: ["92,16"] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Bitte für den König" },
      { number: 2, text: "Der Herr erhöre dich am Tag der Not, / der Name von Jakobs Gott möge dich schützen.", footnotes: ["Spr 18,10"] },
      { number: 3, text: "Er sende dir Hilfe vom Heiligtum / und stehe dir bei vom Zion her." },
      { number: 4, text: "An all deine Speiseopfer denke er, / nehme dein Brandopfer gnädig an. [Sela]", footnotes: ["Wörtlich: er erkläre dein Brandopfer für fett."] },
      { number: 5, text: "Er schenke dir, was dein Herz begehrt, / und lasse all deine Pläne gelingen.", footnotes: ["21,3; 37,4"] },
      { number: 6, text: "Dann wollen wir jubeln über deinen Sieg, / im Namen unsres Gottes das Banner erheben. / All deine Bitten erfülle der Herr." },
      { number: 7, text: "Nun bin ich gewiss: / der Herr schenkt seinem Gesalbten den Sieg; er erhört ihn von seinem heiligen Himmel her / und hilft ihm mit der Macht seiner Rechten.", footnotes: ["18,51"] },
      { number: 8, text: "Die einen sind stark durch Wagen, die andern durch Rosse, / wir aber sind stark im Namen des Herrn, unsres Gottes.", footnotes: ["33,16f; 147,10f; Dtn 20,1; Jdt 9,7; Spr 21,31; Jes 31,1; Hos 1,7"] },
      { number: 9, text: "Sie sind gestürzt und gefallen; / wir bleiben aufrecht und stehen." },
      { number: 10, text: "Herr, verleihe dem König den Sieg! / Erhör uns am Tag, da wir rufen!", footnotes: ["Nach G; H: Herr, gib den Sieg! Der König wird uns antworten am Tag, da wir ihn rufen."] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Dank für den Sieg des Königs" },
      { number: 2, text: "An deiner Macht, Herr, freut sich der König; / über deine Hilfe, wie jubelt er laut!" },
      { number: 3, text: "Du hast ihm den Wunsch seines Herzens erfüllt, / ihm nicht versagt, was seine Lippen begehrten. [Sela]", footnotes: ["20,5"] },
      { number: 4, text: "Du kamst ihm entgegen mit Segen und Glück, / du kröntest ihn mit einer goldenen Krone.", footnotes: ["132,18"] },
      { number: 5, text: "Leben erbat er von dir, du gabst es ihm, / viele Tage, für immer und ewig.", footnotes: ["61,7; 1 Kön 3,14"] },
      { number: 6, text: "Groß ist sein Ruhm durch deine Hilfe, / du hast ihn bekleidet mit Hoheit und Pracht." },
      { number: 7, text: "Du machst ihn zum Segen für immer; / wenn du ihn anblickst, schenkst du ihm große Freude.", footnotes: ["Gen 12,2; 48,20; 1 Chr 17,27"] },
      { number: 8, text: "Denn der König vertraut auf den Herrn, / die Huld des Höchsten lässt ihn niemals wanken." },
      { number: 9, text: "Deine Hand wird all deine Feinde finden; / wer dich hasst, den trifft deine Rechte." },
      { number: 10, text: "Du lässt sie glühen wie einen feurigen Ofen, / sobald du erscheinst. Der Herr verschlingt sie im Zorn, / das Feuer verzehrt sie." },
      { number: 11, text: "Du wirst ihre Brut von der Erde vertilgen; / ihr Geschlecht (verschwindet) aus der Mitte der Menschen.", footnotes: ["109,13"] },
      { number: 12, text: "Schmieden sie auch böse und listige Pläne, / richten sie doch nichts aus gegen dich." },
      { number: 13, text: "Du schlägst sie alle in die Flucht, / wenn du mit deinem Bogen auf sie zielst.", footnotes: ["auf sie, wörtlich: auf ihr Gesicht."] },
      { number: 14, text: "Erhebe dich, Herr, in deiner Macht! / Deiner siegreichen Kraft wollen wir singen und spielen." },
    ] },
    { number: 22, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Hinde der Morgenröte». Ein Psalm Davids.]", heading: "Gottverlassenheit und Heilsgewissheit" },
      { number: 2, text: "Mein Gott, mein Gott, warum hast du mich verlassen, / bist fern meinem Schreien, den Worten meiner Klage?", footnotes: ["Mt 27,46"] },
      { number: 3, text: "Mein Gott, ich rufe bei Tag, doch du gibst keine Antwort; / ich rufe bei Nacht und finde doch keine Ruhe." },
      { number: 4, text: "Aber du bist heilig, / du thronst über dem Lobpreis Israels.", footnotes: ["Jes 6,3"] },
      { number: 5, text: "Dir haben unsre Väter vertraut, / sie haben vertraut und du hast sie gerettet.", footnotes: ["(5-6) 25,2f"] },
      { number: 6, text: "Zu dir riefen sie und wurden befreit, / dir vertrauten sie und wurden nicht zuschanden." },
      { number: 7, text: "Ich aber bin ein Wurm und kein Mensch, / der Leute Spott, vom Volk verachtet.", footnotes: ["Ijob 25,6; Jes 41,14; 53,3"] },
      { number: 8, text: "Alle, die mich sehen, verlachen mich, / verziehen die Lippen, schütteln den Kopf:", footnotes: ["109,25; Mt 27,39"] },
      { number: 9, text: "«Er wälze die Last auf den Herrn, / der soll ihn befreien! Der reiße ihn heraus, / wenn er an ihm Gefallen hat.»", footnotes: ["Weish 2,18-20; Mt 27,43", "Er wälze: Text korr.; H: Wälze."] },
      { number: 10, text: "Du bist es, der mich aus dem Schoß meiner Mutter zog, / mich barg an der Brust der Mutter.", footnotes: ["Jes 44,2.24"] },
      { number: 11, text: "Von Geburt an bin ich geworfen auf dich, / vom Mutterleib an bist du mein Gott.", footnotes: ["71,6"] },
      { number: 12, text: "Sei mir nicht fern, denn die Not ist nahe / und niemand ist da, der hilft.", footnotes: ["22,20; 35,22; 38,22f; 40,14; 71,12"] },
      { number: 13, text: "Viele Stiere umgeben mich, / Büffel von Baschan umringen mich." },
      { number: 14, text: "Sie sperren gegen mich ihren Rachen auf, / reißende, brüllende Löwen.", footnotes: ["17,12; 1 Petr 5,8"] },
      { number: 15, text: "Ich bin hingeschüttet wie Wasser, / gelöst haben sich all meine Glieder. / Mein Herz ist in meinem Leib wie Wachs zerflossen." },
      { number: 16, text: "Meine Kehle ist trocken wie eine Scherbe, / die Zunge klebt mir am Gaumen, / du legst mich in den Staub des Todes.", footnotes: ["Joh 19,28", "Meine Kehle: Text korr.; H: Meine Kraft."] },
      { number: 17, text: "Viele Hunde umlagern mich, / eine Rotte von Bösen umkreist mich. / Sie durchbohren mir Hände und Füße.", footnotes: ["Viele: ergänzt nach G und der aramäischen Übersetzung. Sie durchbohren: Übersetzung unsicher; Text korr. nach G; H: wie ein Löwe."] },
      { number: 18, text: "Man kann all meine Knochen zählen; / sie gaffen und weiden sich an mir." },
      { number: 19, text: "Sie verteilen unter sich meine Kleider / und werfen das Los um mein Gewand.", footnotes: ["Mt 27,35; Joh 19,23f"] },
      { number: 20, text: "Du aber, Herr, halte dich nicht fern! / Du, meine Stärke, eil mir zu Hilfe!", footnotes: ["22,12"] },
      { number: 21, text: "Entreiße mein Leben dem Schwert, / mein einziges Gut aus der Gewalt der Hunde!" },
      { number: 22, text: "Rette mich vor dem Rachen des Löwen, / vor den Hörnern der Büffel rette mich Armen!", footnotes: ["17,12; 2 Tim 4,17", "Nach G und S; H: vor den Hörnern der Büffel hast du mich erhört."] },
      { number: 23, text: "Ich will deinen Namen meinen Brüdern verkünden, / inmitten der Gemeinde dich preisen.", footnotes: ["22,26; 9,15; 26,12; 35,18; 40,10f; 107,32; 109,30; 111,1; Hebr 2,12"] },
      { number: 24, text: "Die ihr den Herrn fürchtet, preist ihn, / ihr alle vom Stamm Jakobs, rühmt ihn; / erschauert alle vor ihm, ihr Nachkommen Israels!" },
      { number: 25, text: "Denn er hat nicht verachtet, / nicht verabscheut das Elend des Armen. Er verbirgt sein Gesicht nicht vor ihm; / er hat auf sein Schreien gehört." },
      { number: 26, text: "Deine Treue preise ich in großer Gemeinde; / ich erfülle meine Gelübde vor denen, die Gott fürchten.", footnotes: ["66,13"] },
      { number: 27, text: "Die Armen sollen essen und sich sättigen; / den Herrn sollen preisen, die ihn suchen. / Aufleben soll euer Herz für immer.", footnotes: ["69,33"] },
      { number: 28, text: "Alle Enden der Erde sollen daran denken / und werden umkehren zum Herrn: / Vor ihm werfen sich alle Stämme der Völker nieder.", footnotes: ["Jes 45,22; Tob 13,13", "Text korr. nach mehreren G-Handschriften und S; H: Vor dir."] },
      { number: 29, text: "Denn der Herr regiert als König; / er herrscht über die Völker.", footnotes: ["24,7-10"] },
      { number: 30, text: "Vor ihm allein sollen niederfallen die Mächtigen der Erde, / vor ihm sich alle niederwerfen, die in der Erde ruhen. [Meine Seele, sie lebt für ihn; /", footnotes: ["H ist stark verderbt."] },
      { number: 31, text: "mein Stamm wird ihm dienen.] Vom Herrn wird man dem künftigen Geschlecht erzählen, /", footnotes: ["(31-32) 48,14; 71,18; 78,3-6; 102,19; 145,4"] },
      { number: 32, text: "seine Heilstat verkündet man dem kommenden Volk; / denn er hat das Werk getan." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Der Herr ist mein Hirte, / nichts wird mir fehlen.", heading: "Der gute Hirt", footnotes: ["80,2; 95,7; Gen 48,15; Jes 40,11; Ez 34,2; Mi 7,14; Joh 10,11"] },
      { number: 2, text: "Er lässt mich lagern auf grünen Auen / und führt mich zum Ruheplatz am Wasser.", footnotes: ["Ez 34,13-15; Offb 7,17"] },
      { number: 3, text: "Er stillt mein Verlangen; / er leitet mich auf rechten Pfaden, treu seinem Namen.", footnotes: ["25,4; Spr 4,11"] },
      { number: 4, text: "Muss ich auch wandern in finsterer Schlucht, / ich fürchte kein Unheil; denn du bist bei mir, / dein Stock und dein Stab geben mir Zuversicht.", footnotes: ["Jes 50,10"] },
      { number: 5, text: "Du deckst mir den Tisch / vor den Augen meiner Feinde. Du salbst mein Haupt mit Öl, / du füllst mir reichlich den Becher.", footnotes: ["36,9; 92,11; 16,5"] },
      { number: 6, text: "Lauter Güte und Huld werden mir folgen mein Leben lang / und im Haus des Herrn darf ich wohnen für lange Zeit.", footnotes: ["27,4; 42,3; 63,3; 65,5; 84,3-5.11", "im Haus . . . wohnen: Text korr. nach G, S und der aramäischen Übersetzung; H: ins Haus des Herrn kehre ich zurück."] },
    ] },
    { number: 24, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Dem Herrn gehört die Erde und was sie erfüllt, / der Erdkreis und seine Bewohner.", heading: "Der Einzug des Herrn in sein Heiligtum", footnotes: ["50,12; 89,12; 95,5; 136,6; Ex 9,29; Dtn 10,14; Jes 42,5; 1 Kor 10,26"] },
      { number: 2, text: "Denn er hat ihn auf Meere gegründet, / ihn über Strömen befestigt." },
      { number: 3, text: "Wer darf hinaufziehn zum Berg des Herrn, / wer darf stehn an seiner heiligen Stätte?", footnotes: ["(3-6) 118,19f"] },
      { number: 4, text: "Der reine Hände hat und ein lauteres Herz, / der nicht betrügt und keinen Meineid schwört." },
      { number: 5, text: "Er wird Segen empfangen vom Herrn / und Heil von Gott, seinem Helfer." },
      { number: 6, text: "Das sind die Menschen, die nach ihm fragen, / die dein Antlitz suchen, Gott Jakobs. [Sela]", footnotes: ["27,8", "Nach S."] },
      { number: 7, text: "Ihr Tore, hebt euch nach oben, / hebt euch, ihr uralten Pforten; / denn es kommt der König der Herrlichkeit.", footnotes: ["(7-10) 10,16; 22,29; 29,10; 47,8f; 93,1; 96,10; 97,1; 98,6; 99,1; 145,11; 146,10; Ex 15,18; Jes 52,7"] },
      { number: 8, text: "Wer ist der König der Herrlichkeit? / Der Herr, stark und gewaltig, / der Herr, mächtig im Kampf." },
      { number: 9, text: "Ihr Tore, hebt euch nach oben, / hebt euch, ihr uralten Pforten; / denn es kommt der König der Herrlichkeit." },
      { number: 10, text: "Wer ist der König der Herrlichkeit? / Der Herr der Heerscharen, / er ist der König der Herrlichkeit. [Sela]" },
    ] },
    { number: 25, verses: [
      { number: 1, text: "[Von David.] Zu dir, Herr, erhebe ich meine Seele. /", heading: "Die Bitte um Vergebung und Leitung", footnotes: ["86,4; 143,8"] },
      { number: 2, text: "Mein Gott, auf dich vertraue ich. Lass mich nicht scheitern, / lass meine Feinde nicht triumphieren!" },
      { number: 3, text: "Denn niemand, der auf dich hofft, wird zuschanden; / zuschanden wird, wer dir schnöde die Treue bricht.", footnotes: ["Jes 49,23"] },
      { number: 4, text: "Zeige mir, Herr, deine Wege, / lehre mich deine Pfade!", footnotes: ["5,9; 16,11; 27,11; 86,11; 119,30.35; 128,1; 139,24; 143,8; Ijob 21,14"] },
      { number: 5, text: "Führe mich in deiner Treue und lehre mich; / denn du bist der Gott meines Heiles. / Auf dich hoffe ich allezeit." },
      { number: 6, text: "Denk an dein Erbarmen, Herr, / und an die Taten deiner Huld; / denn sie bestehen seit Ewigkeit." },
      { number: 7, text: "Denk nicht an meine Jugendsünden und meine Frevel! / In deiner Huld denk an mich, Herr, denn du bist gütig.", footnotes: ["Ijob 13,26"] },
      { number: 8, text: "Gut und gerecht ist der Herr, / darum weist er die Irrenden auf den rechten Weg." },
      { number: 9, text: "Die Demütigen leitet er nach seinem Recht, / die Gebeugten lehrt er seinen Weg." },
      { number: 10, text: "Alle Pfade des Herrn sind Huld und Treue / denen, die seinen Bund und seine Gebote bewahren.", footnotes: ["Tob 3,2"] },
      { number: 11, text: "Um deines Namens willen, Herr, verzeih mir; / denn meine Schuld ist groß." },
      { number: 12, text: "Wer ist der Mann, der Gott fürchtet? / Ihm zeigt er den Weg, den er wählen soll.", footnotes: ["32,8"] },
      { number: 13, text: "Dann wird er wohnen im Glück, / seine Kinder werden das Land besitzen.", footnotes: ["37,9.29"] },
      { number: 14, text: "Die sind Vertraute des Herrn, die ihn fürchten; / er weiht sie ein in seinen Bund." },
      { number: 15, text: "Meine Augen schauen stets auf den Herrn; / denn er befreit meine Füße aus dem Netz.", footnotes: ["123,1f; 141,8f"] },
      { number: 16, text: "Wende dich mir zu und sei mir gnädig; / denn ich bin einsam und gebeugt.", footnotes: ["86,16; 119,132"] },
      { number: 17, text: "Befrei mein Herz von der Angst, / führe mich heraus aus der Bedrängnis!", footnotes: ["Text korr.; H: Nöte haben mein Herz weit (einsichtig?) gemacht."] },
      { number: 18, text: "Sieh meine Not und Plage an / und vergib mir all meine Sünden!" },
      { number: 19, text: "Sieh doch, wie zahlreich meine Feinde sind, / mit welch tödlichem Hass sie mich hassen!", footnotes: ["3,2"] },
      { number: 20, text: "Erhalte mein Leben und rette mich, / lass mich nicht scheitern! / Denn ich nehme zu dir meine Zuflucht.", footnotes: ["86,2"] },
      { number: 21, text: "Unschuld und Redlichkeit mögen mich schützen, / denn ich hoffe auf dich, o Herr.", footnotes: ["o Herr: ergänzt nach G."] },
      { number: 22, text: "O Gott, erlöse Israel / aus all seinen Nöten!", footnotes: ["130,8"] },
    ] },
    { number: 26, verses: [
      { number: 1, text: "[Von David.] Verschaff mir Recht, o Herr; denn ich habe ohne Schuld gelebt. / Dem Herrn habe ich vertraut, ohne zu wanken.", heading: "Die Bitte eines unschuldig Verfolgten", footnotes: ["59,4f"] },
      { number: 2, text: "Erprobe mich, Herr, und durchforsche mich, / prüfe mich auf Herz und Nieren!", footnotes: ["7,10; 17,3; 139,23"] },
      { number: 3, text: "Denn mir stand deine Huld vor Augen, / ich ging meinen Weg in Treue zu dir.", footnotes: ["119,1; 86,11"] },
      { number: 4, text: "Ich saß nicht bei falschen Menschen, / mit Heuchlern hatte ich keinen Umgang.", footnotes: ["(4-5) 1,1"] },
      { number: 5, text: "Verhasst ist mir die Schar derer, die Unrecht tun; / ich sitze nicht bei den Frevlern." },
      { number: 6, text: "Ich wasche meine Hände in Unschuld; / ich umschreite, Herr, deinen Altar,", footnotes: ["73,13; Dtn 21,6f; Mt 27,24"] },
      { number: 7, text: "um laut dein Lob zu verkünden / und all deine Wunder zu erzählen." },
      { number: 8, text: "Herr, ich liebe den Ort, wo dein Tempel steht, / die Stätte, wo deine Herrlichkeit wohnt.", footnotes: ["27,4"] },
      { number: 9, text: "Raff mich nicht hinweg mit den Sündern, / nimm mir nicht das Leben zusammen mit dem der Mörder!", footnotes: ["28,3"] },
      { number: 10, text: "An ihren Händen klebt Schandtat, / ihre Rechte ist voll von Bestechung.", footnotes: ["Ex 23,8"] },
      { number: 11, text: "Ich aber gehe meinen Weg ohne Schuld. / Erlöse mich und sei mir gnädig!" },
      { number: 12, text: "Mein Fuß steht auf festem Grund. / Den Herrn will ich preisen in der Gemeinde.", footnotes: ["22,23"] },
    ] },
    { number: 27, verses: [
      { number: 1, text: "[Von David.] Der Herr ist mein Licht und mein Heil: / Vor wem sollte ich mich fürchten? Der Herr ist die Kraft meines Lebens: / Vor wem sollte mir bangen?", heading: "Die Gemeinschaft mit Gott", footnotes: ["97,11; 56,5; Mi 7,8"] },
      { number: 2, text: "Dringen Frevler auf mich ein, / um mich zu verschlingen, meine Bedränger und Feinde, / sie müssen straucheln und fallen.", footnotes: ["Ijob 19,22"] },
      { number: 3, text: "Mag ein Heer mich belagern: / Mein Herz wird nicht verzagen. Mag Krieg gegen mich toben: / Ich bleibe dennoch voll Zuversicht.", footnotes: ["3,7"] },
      { number: 4, text: "Nur eines erbitte ich vom Herrn, / danach verlangt mich: Im Haus des Herrn zu wohnen / alle Tage meines Lebens, die Freundlichkeit des Herrn zu schauen / und nachzusinnen in seinem Tempel.", footnotes: ["23,6"] },
      { number: 5, text: "Denn er birgt mich in seinem Haus / am Tag des Unheils; er beschirmt mich im Schutz seines Zeltes, / er hebt mich auf einen Felsen empor.", footnotes: ["31,21"] },
      { number: 6, text: "Nun kann ich mein Haupt erheben / über die Feinde, die mich umringen. Ich will Opfer darbringen in seinem Zelt, Opfer mit Jubel; / dem Herrn will ich singen und spielen." },
      { number: 7, text: "Vernimm, o Herr, mein lautes Rufen; / sei mir gnädig und erhöre mich!" },
      { number: 8, text: "Mein Herz denkt an dein Wort: «Sucht mein Angesicht!» / Dein Angesicht, Herr, will ich suchen.", footnotes: ["24,6; 105,4; Dtn 4,29"] },
      { number: 9, text: "Verbirg nicht dein Gesicht vor mir; / weise deinen Knecht im Zorn nicht ab! / Du wurdest meine Hilfe. Verstoß mich nicht, verlass mich nicht, / du Gott meines Heiles!" },
      { number: 10, text: "Wenn mich auch Vater und Mutter verlassen, / der Herr nimmt mich auf.", footnotes: ["Jes 49,15"] },
      { number: 11, text: "Zeige mir, Herr, deinen Weg, / leite mich auf ebener Bahn trotz meiner Feinde!", footnotes: ["25,4"] },
      { number: 12, text: "Gib mich nicht meinen gierigen Gegnern preis; / denn falsche Zeugen stehen gegen mich auf und wüten.", footnotes: ["35,11"] },
      { number: 13, text: "Ich aber bin gewiss, zu schauen / die Güte des Herrn im Land der Lebenden.", footnotes: ["52,7; 56,14; 116,9; 142,6; Jes 38,11"] },
      { number: 14, text: "Hoffe auf den Herrn und sei stark! / Hab festen Mut und hoffe auf den Herrn!", footnotes: ["31,25"] },
    ] },
    { number: 28, verses: [
      { number: 1, text: "[Von David.] Zu dir rufe ich, Herr, mein Fels. / Wende dich nicht schweigend ab von mir! Denn wolltest du schweigen, / würde ich denen gleich, die längst begraben sind.", heading: "Hilferuf in Todesgefahr und Dank für Erhörung", footnotes: ["92,16; 143,7"] },
      { number: 2, text: "Höre mein lautes Flehen, wenn ich zu dir schreie, / wenn ich die Hände zu deinem Allerheiligsten erhebe.", footnotes: ["134,2; Dan 6,11"] },
      { number: 3, text: "Raff mich nicht weg mit den Übeltätern und Frevlern, / die ihren Nächsten freundlich grüßen, / doch Böses hegen in ihrem Herzen.", footnotes: ["26,9; 62,5; Spr 26,24-28"] },
      { number: 4, text: "Vergilt ihnen, wie es ihrem Treiben entspricht / und ihren bösen Taten. Vergilt ihnen, wie es das Werk ihrer Hände verdient. / Wende ihr Tun auf sie selbst zurück!", footnotes: ["62,13"] },
      { number: 5, text: "Denn sie achten nicht auf das Walten des Herrn / und auf das Werk seiner Hände. Darum reißt er sie nieder / und richtet sie nicht wieder auf.", footnotes: ["Jes 5,12f"] },
      { number: 6, text: "Der Herr sei gepriesen. / Denn er hat mein lautes Flehen erhört." },
      { number: 7, text: "Der Herr ist meine Kraft und mein Schild, / mein Herz vertraut ihm. Mir wurde geholfen. Da jubelte mein Herz; / ich will ihm danken mit meinem Lied." },
      { number: 8, text: "Der Herr ist die Stärke seines Volkes, / er ist Schutz und Heil für seinen Gesalbten.", footnotes: ["Text korr. nach G und S."] },
      { number: 9, text: "Hilf deinem Volk und segne dein Erbe, / führe und trage es in Ewigkeit!", footnotes: ["29,11; Dtn 9,29"] },
    ] },
    { number: 29, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Bringt dar dem Herrn, ihr Himmlischen, / bringt dar dem Herrn Lob und Ehre!", heading: "Gottes Herrlichkeit im Gewitter", footnotes: ["103,20f"] },
      { number: 2, text: "Bringt dar dem Herrn die Ehre seines Namens, / werft euch nieder vor dem Herrn in heiligem Schmuck!", footnotes: ["96,8f"] },
      { number: 3, text: "Die Stimme des Herrn erschallt über den Wassern. / Der Gott der Herrlichkeit donnert, / der Herr über gewaltigen Wassern.", footnotes: ["(3-4) Ijob 37,4"] },
      { number: 4, text: "Die Stimme des Herrn ertönt mit Macht, / die Stimme des Herrn voll Majestät." },
      { number: 5, text: "Die Stimme des Herrn zerbricht die Zedern, / der Herr zerschmettert die Zedern des Libanon." },
      { number: 6, text: "Er lässt den Libanon hüpfen wie ein Kalb, / wie einen Wildstier den Sirjon.", footnotes: ["114,4; Dtn 3,8f"] },
      { number: 7, text: "Die Stimme des Herrn sprüht flammendes Feuer, /" },
      { number: 8, text: "die Stimme des Herrn lässt die Wüste beben, / beben lässt der Herr die Wüste von Kadesch." },
      { number: 9, text: "Die Stimme des Herrn wirbelt Eichen empor, / sie reißt ganze Wälder kahl. / In seinem Palast rufen alle: O herrlicher Gott!", footnotes: ["Text korr. durch Vokaländerung; andere Übersetzungsmöglichkeit: Die Stimme des Herrn versetzt Hirschkühe in Wehen, bringt die Ziegen zur Frühgeburt."] },
      { number: 10, text: "Der Herr thront über der Flut, / der Herr thront als König in Ewigkeit.", footnotes: ["24,7-10; Bar 3,3"] },
      { number: 11, text: "Der Herr gebe Kraft seinem Volk. / Der Herr segne sein Volk mit Frieden.", footnotes: ["28,9"] },
    ] },
    { number: 30, verses: [
      { number: 1, text: "[Ein Psalm. Ein Lied zur Tempelweihe. Von David.]", heading: "Dank für die Rettung aus Todesnot", footnotes: ["Esra 6,16; 1 Makk 4,36-54"] },
      { number: 2, text: "Ich will dich rühmen, Herr, / denn du hast mich aus der Tiefe gezogen / und lässt meine Feinde nicht über mich triumphieren." },
      { number: 3, text: "Herr, mein Gott, ich habe zu dir geschrien / und du hast mich geheilt.", footnotes: ["Ex 15,26"] },
      { number: 4, text: "Herr, du hast mich herausgeholt aus dem Reich des Todes, / aus der Schar der Todgeweihten mich zum Leben gerufen.", footnotes: ["9,14"] },
      { number: 5, text: "Singt und spielt dem Herrn, ihr seine Frommen, / preist seinen heiligen Namen!", footnotes: ["7,18; 97,12", "Wörtlich: preist sein heiliges Gedenken."] },
      { number: 6, text: "Denn sein Zorn dauert nur einen Augenblick, / doch seine Güte ein Leben lang. Wenn man am Abend auch weint, / am Morgen herrscht wieder Jubel.", footnotes: ["Jes 54,7f"] },
      { number: 7, text: "Im sicheren Glück dachte ich einst: / Ich werde niemals wanken." },
      { number: 8, text: "Herr, in deiner Güte / stelltest du mich auf den schützenden Berg. Doch dann hast du dein Gesicht verborgen. / Da bin ich erschrocken.", footnotes: ["auf den schützenden Berg, wörtlich: auf feste Berge."] },
      { number: 9, text: "Zu dir, Herr, rief ich um Hilfe, / ich flehte meinen Herrn um Gnade an." },
      { number: 10, text: "(Ich sagte:) / Was nützt dir mein Blut, wenn ich begraben bin? / Kann der Staub dich preisen, deine Treue verkünden?", footnotes: ["6,6"] },
      { number: 11, text: "Höre mich, Herr, sei mir gnädig! / Herr, sei du mein Helfer!" },
      { number: 12, text: "Da hast du mein Klagen in Tanzen verwandelt, / hast mir das Trauergewand ausgezogen und mich mit Freude umgürtet.", footnotes: ["126; Jes 61,3"] },
      { number: 13, text: "Darum singt dir mein Herz und will nicht verstummen. / Herr, mein Gott, ich will dir danken in Ewigkeit.", footnotes: ["Text korr. nach G."] },
    ] },
    { number: 31, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Gott, die sichere Zuflucht" },
      { number: 2, text: "Herr, ich suche Zuflucht bei dir. / Lass mich doch niemals scheitern; / rette mich in deiner Gerechtigkeit!", footnotes: ["(2-4) 71,1-3"] },
      { number: 3, text: "Wende dein Ohr mir zu, / erlöse mich bald! Sei mir ein schützender Fels, / eine feste Burg, die mich rettet.", footnotes: ["102,3"] },
      { number: 4, text: "Denn du bist mein Fels und meine Burg; / um deines Namens willen wirst du mich führen und leiten.", footnotes: ["92,16; 23,3"] },
      { number: 5, text: "Du wirst mich befreien aus dem Netz, das sie mir heimlich legten; / denn du bist meine Zuflucht.", footnotes: ["25,15; 140,6"] },
      { number: 6, text: "In deine Hände lege ich voll Vertrauen meinen Geist; / du hast mich erlöst, Herr, du treuer Gott.", footnotes: ["Lk 23,46; Apg 7,59"] },
      { number: 7, text: "Dir sind alle verhasst, die nichtige Götzen verehren, / ich aber verlasse mich auf den Herrn.", footnotes: ["Dir sind: Text korr. nach G, S und Vg; H: Mir sind."] },
      { number: 8, text: "Ich will jubeln und über deine Huld mich freuen; / denn du hast mein Elend angesehn, / du bist mit meiner Not vertraut.", footnotes: ["9,3"] },
      { number: 9, text: "Du hast mich nicht preisgegeben der Gewalt meines Feindes, / hast meinen Füßen freien Raum geschenkt.", footnotes: ["18,20"] },
      { number: 10, text: "Herr, sei mir gnädig, denn mir ist angst; / vor Gram zerfallen mir Auge, Seele und Leib.", footnotes: ["6,8; (10-11) 38,11"] },
      { number: 11, text: "In Kummer schwindet mein Leben dahin, / meine Jahre verrinnen im Seufzen. Meine Kraft ist ermattet im Elend, / meine Glieder sind zerfallen.", footnotes: ["6,3", "im Elend: Text korr. nach G und S; H: in meiner Schuld."] },
      { number: 12, text: "Zum Spott geworden bin ich all meinen Feinden, / ein Hohn den Nachbarn, ein Schrecken den Freunden; / wer mich auf der Straße sieht, der flieht vor mir.", footnotes: ["38,12; 41,10; 44,14f; 55,13f; 69,9.12f; 79,4; 88,9.19; 89,42; Ijob 19,13-19", "Text korr."] },
      { number: 13, text: "Ich bin dem Gedächtnis entschwunden wie ein Toter, / bin geworden wie ein zerbrochenes Gefäß." },
      { number: 14, text: "Ich höre das Zischeln der Menge - Grauen ringsum. / Sie tun sich gegen mich zusammen; / sie sinnen darauf, mir das Leben zu rauben.", footnotes: ["41,6; Jer 20,10"] },
      { number: 15, text: "Ich aber, Herr, ich vertraue dir, / ich sage: «Du bist mein Gott.»", footnotes: ["140,7"] },
      { number: 16, text: "In deiner Hand liegt mein Geschick; / entreiß mich der Hand meiner Feinde und Verfolger!", footnotes: ["139,16"] },
      { number: 17, text: "Lass dein Angesicht leuchten über deinem Knecht, / hilf mir in deiner Güte!", footnotes: ["4,7"] },
      { number: 18, text: "Herr, lass mich nicht scheitern, / denn ich rufe zu dir. Scheitern sollen die Frevler, / verstummen und hinabfahren ins Reich der Toten." },
      { number: 19, text: "Jeder Mund, der lügt, soll sich schließen, / der Mund, der frech gegen den Gerechten redet, / hochmütig und verächtlich." },
      { number: 20, text: "Wie groß ist deine Güte, Herr, / die du bereithältst für alle, die dich fürchten und ehren; du erweist sie allen, / die sich vor den Menschen zu dir flüchten.", footnotes: ["Herr: ergänzt nach G."] },
      { number: 21, text: "Du beschirmst sie im Schutz deines Angesichts / vor dem Toben der Menschen. Wie unter einem Dach bewahrst du sie / vor dem Gezänk der Zungen.", footnotes: ["27,5; Ijob 5,21"] },
      { number: 22, text: "Gepriesen sei der Herr, der wunderbar an mir gehandelt / und mir seine Güte erwiesen hat zur Zeit der Bedrängnis.", footnotes: ["zur Zeit der Bedrängnis: Text korr.; H: in der befestigten Stadt."] },
      { number: 23, text: "Ich aber dachte in meiner Angst: / Ich bin aus deiner Nähe verstoßen. Doch du hast mein lautes Flehen gehört, / als ich zu dir um Hilfe rief.", footnotes: ["Jona 2,5"] },
      { number: 24, text: "Liebt den Herrn, all seine Frommen! / Seine Getreuen behütet der Herr, / doch den Hochmütigen vergilt er ihr Tun mit vollem Maß.", footnotes: ["62,13"] },
      { number: 25, text: "Euer Herz sei stark und unverzagt, / ihr alle, die ihr wartet auf den Herrn.", footnotes: ["27,14"] },
    ] },
    { number: 32, verses: [
      { number: 1, text: "[Von David. Ein Weisheitslied.] Wohl dem, dessen Frevel vergeben / und dessen Sünde bedeckt ist.", heading: "Freude über die Vergebung", footnotes: ["65,4; 78,38; 85,3; 103,3; Jes 1,18; 38,17; Jer 31,34; 1 Joh 1,9; (1-2) Röm 4,7f"] },
      { number: 2, text: "Wohl dem Menschen, dem der Herr die Schuld nicht zur Last legt / und dessen Herz keine Falschheit kennt." },
      { number: 3, text: "Solang ich es verschwieg, waren meine Glieder matt, / den ganzen Tag musste ich stöhnen." },
      { number: 4, text: "Denn deine Hand lag schwer auf mir bei Tag und bei Nacht; / meine Lebenskraft war verdorrt wie durch die Glut des Sommers. [Sela]", footnotes: ["38,3; 102,5f", "Text korr."] },
      { number: 5, text: "Da bekannte ich dir meine Sünde / und verbarg nicht länger meine Schuld vor dir. Ich sagte: Ich will dem Herrn meine Frevel bekennen. / Und du hast mir die Schuld vergeben. [Sela]", footnotes: ["38,19"] },
      { number: 6, text: "Darum soll jeder Fromme in der Not zu dir beten; / fluten hohe Wasser heran, ihn werden sie nicht erreichen.", footnotes: ["66,12", "Text korr.; H ist verderbt."] },
      { number: 7, text: "Du bist mein Schutz, bewahrst mich vor Not; / du rettest mich und hüllst mich in Jubel. [Sela]" },
      { number: 8, text: "«Ich unterweise dich und zeige dir den Weg, den du gehen sollst. / Ich will dir raten; über dir wacht mein Auge.»", footnotes: ["25,12; 119,1; 33,18"] },
      { number: 9, text: "Werdet nicht wie Ross und Maultier, / die ohne Verstand sind. Mit Zaum und Zügel muss man ihr Ungestüm bändigen, / sonst folgen sie dir nicht." },
      { number: 10, text: "Der Frevler leidet viele Schmerzen, / doch wer dem Herrn vertraut, den wird er mit seiner Huld umgeben." },
      { number: 11, text: "Freut euch am Herrn und jauchzt, ihr Gerechten, / jubelt alle, ihr Menschen mit redlichem Herzen!", footnotes: ["33,1"] },
    ] },
    { number: 33, verses: [
      { number: 1, text: "Ihr Gerechten, jubelt vor dem Herrn; / für die Frommen ziemt es sich, Gott zu loben.", heading: "Ein Loblied auf den mächtigen und gütigen Gott", footnotes: ["32,11; 92,2"] },
      { number: 2, text: "Preist den Herrn mit der Zither, / spielt für ihn auf der zehnsaitigen Harfe!", footnotes: ["92,4"] },
      { number: 3, text: "Singt ihm ein neues Lied, / greift voll in die Saiten und jubelt laut!", footnotes: ["40,4; 96,1; 98,1; 144,9; 149,1; Jdt 16,1.13; Jes 42,10; Offb 5,9; 14,3"] },
      { number: 4, text: "Denn das Wort des Herrn ist wahrhaftig, / all sein Tun ist verlässlich." },
      { number: 5, text: "Er liebt Gerechtigkeit und Recht, / die Erde ist erfüllt von der Huld des Herrn.", footnotes: ["119,64"] },
      { number: 6, text: "Durch das Wort des Herrn wurden die Himmel geschaffen, / ihr ganzes Heer durch den Hauch seines Mundes.", footnotes: ["Gen 1,6-8.14-18; Joh 1,3"] },
      { number: 7, text: "Wie in einem Schlauch fasst er das Wasser des Meeres, / verschließt die Urflut in Kammern.", footnotes: ["Ijob 38,8-11", "Text korr. nach G, Vg und der aramäischen Übersetzung."] },
      { number: 8, text: "Alle Welt fürchte den Herrn; / vor ihm sollen alle beben, die den Erdkreis bewohnen.", footnotes: ["67,8"] },
      { number: 9, text: "Denn der Herr sprach und sogleich geschah es; / er gebot und alles war da.", footnotes: ["148,5; Gen 1,3-26; Sir 39,16; Jes 48,13"] },
      { number: 10, text: "Der Herr vereitelt die Beschlüsse der Heiden, / er macht die Pläne der Völker zunichte.", footnotes: ["(10-11) Spr 19,21"] },
      { number: 11, text: "Der Ratschluss des Herrn bleibt ewig bestehen, / die Pläne seines Herzens überdauern die Zeiten.", footnotes: ["Jes 40,8; 46,10"] },
      { number: 12, text: "Wohl dem Volk, dessen Gott der Herr ist, / der Nation, die er sich zum Erbteil erwählt hat.", footnotes: ["144,15; Ex 19,6; Dtn 7,6"] },
      { number: 13, text: "Der Herr blickt herab vom Himmel, / er sieht auf alle Menschen.", footnotes: ["(13-15) 14,2; 102,20; 2 Chr 16,9; Ijob 34,21; Sir 15,18f"] },
      { number: 14, text: "Von seinem Thronsitz schaut er nieder / auf alle Bewohner der Erde." },
      { number: 15, text: "Der ihre Herzen gebildet hat, / er achtet auf all ihre Taten.", footnotes: ["94,9-11"] },
      { number: 16, text: "Dem König hilft nicht sein starkes Heer, / der Held rettet sich nicht durch große Stärke.", footnotes: ["(16-17) 20,8; 1 Sam 14,6; 17,45-47"] },
      { number: 17, text: "Nichts nützen die Rosse zum Sieg, / mit all ihrer Kraft können sie niemand retten." },
      { number: 18, text: "Doch das Auge des Herrn ruht auf allen, die ihn fürchten und ehren, / die nach seiner Güte ausschaun;", footnotes: ["32,8; 34,16; Sir 34,19"] },
      { number: 19, text: "denn er will sie dem Tod entreißen / und in der Hungersnot ihr Leben erhalten.", footnotes: ["37,19; Ijob 5,20"] },
      { number: 20, text: "Unsre Seele hofft auf den Herrn; / er ist für uns Schild und Hilfe.", footnotes: ["3,4; 115,9-11"] },
      { number: 21, text: "Ja, an ihm freut sich unser Herz, / wir vertrauen auf seinen heiligen Namen." },
      { number: 22, text: "Lass deine Güte über uns walten, o Herr, / denn wir schauen aus nach dir.", footnotes: ["90,17"] },
    ] },
    { number: 34, verses: [
      { number: 1, text: "[Von David, als er sich vor Abimelech wahnsinnig stellte und dieser ihn fortjagte und er ging.]", heading: "Unter Gottes Schutz", footnotes: ["1 Sam 21,11-16"] },
      { number: 2, text: "Ich will den Herrn allezeit preisen; / immer sei sein Lob in meinem Mund.", footnotes: ["145,2"] },
      { number: 3, text: "Meine Seele rühme sich des Herrn; / die Armen sollen es hören und sich freuen." },
      { number: 4, text: "Verherrlicht mit mir den Herrn, / lasst uns gemeinsam seinen Namen rühmen." },
      { number: 5, text: "Ich suchte den Herrn und er hat mich erhört, / er hat mich all meinen Ängsten entrissen." },
      { number: 6, text: "Blickt auf zu ihm, so wird euer Gesicht leuchten / und ihr braucht nicht zu erröten.", footnotes: ["euer Gesicht: Text korr.; H: ihr Gesicht."] },
      { number: 7, text: "Da ist ein Armer; er rief und der Herr erhörte ihn. / Er half ihm aus all seinen Nöten.", footnotes: ["91,15"] },
      { number: 8, text: "Der Engel des Herrn umschirmt alle, die ihn fürchten und ehren, / und er befreit sie.", footnotes: ["35,5f; 91,11; Gen 32,2f; Ex 14,19"] },
      { number: 9, text: "Kostet und seht, wie gütig der Herr ist; / wohl dem, der zu ihm sich flüchtet!", footnotes: ["1 Petr 2,3"] },
      { number: 10, text: "Fürchtet den Herrn, ihr seine Heiligen; / denn wer ihn fürchtet, leidet keinen Mangel." },
      { number: 11, text: "Reiche müssen darben und hungern; / wer aber den Herrn sucht, braucht kein Gut zu entbehren.", footnotes: ["Lk 1,53", "Reiche: Text korr. nach G und S; H: junge Löwen."] },
      { number: 12, text: "Kommt, ihr Kinder, hört mir zu! / Ich will euch in der Furcht des Herrn unterweisen." },
      { number: 13, text: "Wer ist der Mensch, der das Leben liebt / und gute Tage zu sehen wünscht?", footnotes: ["(13-17) 1 Petr 3,10-12"] },
      { number: 14, text: "Bewahre deine Zunge vor Bösem / und deine Lippen vor falscher Rede!" },
      { number: 15, text: "Meide das Böse und tu das Gute; / suche Frieden und jage ihm nach!", footnotes: ["37,27; Spr 3,7; Hebr 12,14"] },
      { number: 16, text: "Die Augen des Herrn blicken auf die Gerechten, / seine Ohren hören ihr Schreien.", footnotes: ["33,18"] },
      { number: 17, text: "Das Antlitz des Herrn richtet sich gegen die Bösen, / um ihr Andenken von der Erde zu tilgen.", footnotes: ["109,15; Ijob 18,17"] },
      { number: 18, text: "Schreien die Gerechten, so hört sie der Herr; / er entreißt sie all ihren Ängsten.", footnotes: ["die Gerechten: ergänzt nach G, S und der aramäischen Übersetzung."] },
      { number: 19, text: "Nahe ist der Herr den zerbrochenen Herzen, / er hilft denen auf, die zerknirscht sind.", footnotes: ["51,19; 145,18"] },
      { number: 20, text: "Der Gerechte muss viel leiden, / doch allem wird der Herr ihn entreißen.", footnotes: ["2 Kor 1,5"] },
      { number: 21, text: "Er behütet all seine Glieder, / nicht eines von ihnen wird zerbrochen." },
      { number: 22, text: "Den Frevler wird seine Bosheit töten; / wer den Gerechten hasst, muss es büßen." },
      { number: 23, text: "Der Herr erlöst seine Knechte; / straflos bleibt, wer zu ihm sich flüchtet." },
    ] },
    { number: 35, verses: [
      { number: 1, text: "[Von David.] Streite, Herr, gegen alle, die gegen mich streiten, / bekämpfe alle, die mich bekämpfen!", heading: "Bitte um Rettung vor falschen Anklägern" },
      { number: 2, text: "Ergreife Schild und Waffen; / steh auf, um mir zu helfen!" },
      { number: 3, text: "Schwing den Speer und die Lanze gegen meine Verfolger! / Sag zu mir: «Ich bin deine Hilfe.»" },
      { number: 4, text: "In Schmach und Schande sollen alle fallen, / die mir nach dem Leben trachten. Zurückweichen sollen sie und vor Scham erröten, / die auf mein Unglück sinnen.", footnotes: ["40,15"] },
      { number: 5, text: "Sie sollen werden wie Spreu vor dem Wind; / der Engel des Herrn stoße sie fort.", footnotes: ["1,4; 83,14; 34,8"] },
      { number: 6, text: "Ihr Weg soll finster und schlüpfrig sein; / der Engel des Herrn verfolge sie.", footnotes: ["73,18; Jer 23,12"] },
      { number: 7, text: "Denn sie haben mir ohne Grund ein Netz gelegt, / mir ohne Grund eine Grube gegraben.", footnotes: ["Jer 18,20; (7-8) 7,16; Ijob 18,8f"] },
      { number: 8, text: "Unvermutet ereile ihn das Verderben; / er fange sich selbst in seinem Netz, / er falle in die eigene Grube.", footnotes: ["Wörtlich: er falle hinein zum Verderben."] },
      { number: 9, text: "Meine Seele aber wird jubeln über den Herrn / und sich über seine Hilfe freuen." },
      { number: 10, text: "Mit Leib und Seele will ich sagen: / Herr, wer ist wie du? Du entreißt den Schwachen dem, der stärker ist, / den Schwachen und Armen dem, der ihn ausraubt.", footnotes: ["71,19; 77,14; 89,7f; 113,5; Ex 15,11", "Wörtlich: Alle meine Knochen sollen sagen."] },
      { number: 11, text: "Da treten ruchlose Zeugen auf. / Man wirft mir Dinge vor, von denen ich nichts weiß.", footnotes: ["27,12; Mt 26,59f"] },
      { number: 12, text: "Sie vergelten mir Gutes mit Bösem; / ich bin verlassen und einsam.", footnotes: ["38,21; 109,5; Jer 18,20", "ich bin . . . einsam, wörtlich: Kinderlosigkeit meiner Seele."] },
      { number: 13, text: "Ich aber zog ein Bußkleid an, als sie erkrankten, / und quälte mich ab mit Fasten. / Nun kehre mein Gebet zurück in meine Brust.", footnotes: ["109,4"] },
      { number: 14, text: "Als wäre es ein Freund oder ein Bruder, / so ging ich betrübt umher, wie man Leid trägt um die Mutter, / trauernd und tief gebeugt." },
      { number: 15, text: "Doch als ich stürzte, lachten sie / und taten sich zusammen. Sie taten sich gegen mich zusammen / wie Fremde, die ich nicht kenne. Sie hören nicht auf, mich zu schmähen; /", footnotes: ["H ist unverständlich."] },
      { number: 16, text: "sie verhöhnen und verspotten mich, / knirschen gegen mich mit den Zähnen.", footnotes: ["Text korr."] },
      { number: 17, text: "Herr, wie lange noch wirst du das ansehn? / Rette mein Leben vor den wilden Tieren, / mein einziges Gut vor den Löwen!", footnotes: ["17,12"] },
      { number: 18, text: "Ich will dir danken in großer Gemeinde, / vor zahlreichem Volk dich preisen.", footnotes: ["22,23"] },
      { number: 19, text: "Über mich sollen die sich nicht freuen, / die mich ohne Grund befeinden. Sie sollen nicht mit den Augen zwinkern, / die mich grundlos hassen.", footnotes: ["25,19; 69,5; Joh 15,25"] },
      { number: 20, text: "Denn was sie reden, dient nicht dem Frieden; / gegen die Stillen im Land ersinnen sie listige Pläne." },
      { number: 21, text: "Sie reißen den Mund gegen mich auf und sagen: / «Dir geschieht recht. Jetzt sehen wir's mit eigenen Augen.»", footnotes: ["40,16; Klgl 2,16"] },
      { number: 22, text: "Du hast es gesehen, Herr. So schweig doch nicht! / Herr, bleib mir nicht fern!", footnotes: ["38,22; Hab 1,13; Ps 22,12"] },
      { number: 23, text: "Wach auf, tritt ein für mein Recht, / verteidige mich, mein Gott und mein Herr!" },
      { number: 24, text: "Verschaff mir Recht nach deiner Gerechtigkeit, Herr, mein Gott! / Sie sollen sich über mich nicht freuen." },
      { number: 25, text: "Lass sie nicht denken: «Recht so! Das freut uns.» / Sie sollen nicht sagen: «Wir haben ihn verschlungen.»" },
      { number: 26, text: "In Schmach und Schande sollen alle fallen, / die sich über mein Unglück freuen, in Schimpf und Schande sich kleiden, / die gegen mich prahlen.", footnotes: ["40,15"] },
      { number: 27, text: "Alle sollen sich freuen und jubeln, / die wünschen, dass ich im Recht bin. Sie sollen jederzeit sagen: «Groß ist der Herr, / er will das Heil seines Knechtes.»", footnotes: ["40,17"] },
      { number: 28, text: "Meine Zunge soll deine Gerechtigkeit verkünden, / dein Lob alle Tage.", footnotes: ["71,15"] },
    ] },
    { number: 36, verses: [
      { number: 1, text: "[Für den Chormeister. Von David, dem Knecht des Herrn.]", heading: "Gott, die Quelle des Lebens" },
      { number: 2, text: "Der Frevler spricht: «Ich bin entschlossen zum Bösen.» / In seinen Augen gibt es kein Erschrecken vor Gott.", footnotes: ["14,1; Röm 3,18", "Text korr. nach G."] },
      { number: 3, text: "Er gefällt sich darin, / sich schuldig zu machen und zu hassen." },
      { number: 4, text: "Die Worte seines Mundes sind Trug und Unheil; / er hat es aufgegeben, weise und gut zu handeln." },
      { number: 5, text: "Unheil plant er auf seinem Lager, / er betritt schlimme Wege und scheut nicht das Böse.", footnotes: ["Mi 2,1"] },
      { number: 6, text: "Herr, deine Güte reicht, so weit der Himmel ist, / deine Treue, so weit die Wolken ziehn.", footnotes: ["57,11; 108,5"] },
      { number: 7, text: "Deine Gerechtigkeit steht wie die Berge Gottes, / deine Urteile sind tief wie das Meer. Herr, du hilfst Menschen und Tieren. /" },
      { number: 8, text: "Gott, wie köstlich ist deine Huld! Die Menschen bergen sich im Schatten deiner Flügel, /", footnotes: ["17,7f"] },
      { number: 9, text: "sie laben sich am Reichtum deines Hauses; / du tränkst sie mit dem Strom deiner Wonnen.", footnotes: ["65,5; 46,5"] },
      { number: 10, text: "Denn bei dir ist die Quelle des Lebens, / in deinem Licht schauen wir das Licht.", footnotes: ["16,11; 97,11; Jer 2,13; Joh 4,14"] },
      { number: 11, text: "Erhalte denen, die dich kennen, deine Huld / und deine Gerechtigkeit den Menschen mit redlichem Herzen!" },
      { number: 12, text: "Lass mich nicht kommen unter den Fuß der Stolzen; / die Hand der Frevler soll mich nicht vertreiben." },
      { number: 13, text: "Dann brechen die Bösen zusammen, / sie werden niedergestoßen und können nie wieder aufstehn." },
    ] },
    { number: 37, verses: [
      { number: 1, text: "[Von David.] Errege dich nicht über die Bösen, / wegen der Übeltäter ereifere dich nicht!", heading: "Gott, der Anwalt der Guten", footnotes: ["Spr 23,17; 24,19"] },
      { number: 2, text: "Denn sie verwelken schnell wie das Gras, / wie grünes Kraut verdorren sie.", footnotes: ["90,5f"] },
      { number: 3, text: "Vertrau auf den Herrn und tu das Gute, / bleib wohnen im Land und bewahre Treue!" },
      { number: 4, text: "Freu dich innig am Herrn! / Dann gibt er dir, was dein Herz begehrt.", footnotes: ["20,5"] },
      { number: 5, text: "Befiehl dem Herrn deinen Weg und vertrau ihm; / er wird es fügen.", footnotes: ["119,1; 55,23; Spr 3,5; 16,3"] },
      { number: 6, text: "Er bringt deine Gerechtigkeit heraus wie das Licht / und dein Recht so hell wie den Mittag.", footnotes: ["Ijob 11,17; Spr 4,18"] },
      { number: 7, text: "Sei still vor dem Herrn und harre auf ihn! / Erhitze dich nicht über den Mann, dem alles gelingt, / den Mann, der auf Ränke sinnt.", footnotes: ["dem alles gelingt, wörtlich: dem sein Weg gelingt."] },
      { number: 8, text: "Steh ab vom Zorn und lass den Grimm; / erhitze dich nicht, es führt nur zu Bösem.", footnotes: ["Eph 4,26"] },
      { number: 9, text: "Denn die Bösen werden ausgetilgt; / die aber auf den Herrn hoffen, werden das Land besitzen.", footnotes: ["25,13"] },
      { number: 10, text: "Eine Weile noch und der Frevler ist nicht mehr da; / schaust du nach seiner Wohnung - sie ist nicht mehr zu finden.", footnotes: ["Ijob 20,8f"] },
      { number: 11, text: "Doch die Armen werden das Land bekommen, / sie werden Glück in Fülle genießen.", footnotes: ["Mt 5,5"] },
      { number: 12, text: "Der Frevler sinnt auf Ränke gegen den Gerechten / und knirscht gegen ihn mit den Zähnen." },
      { number: 13, text: "Der Herr verlacht ihn, / denn er sieht, dass sein Tag kommt.", footnotes: ["59,9; Ijob 18,20"] },
      { number: 14, text: "Die Frevler zücken das Schwert / und spannen ihren Bogen; sie wollen den Schwachen und Armen fällen / und alle hinschlachten, die den rechten Weg gehn.", footnotes: ["11,2"] },
      { number: 15, text: "Ihr Schwert dringe in ihr eigenes Herz / und ihre Bogen sollen zerbrechen." },
      { number: 16, text: "Besser das wenige, das der Gerechte besitzt, / als der Überfluss vieler Frevler.", footnotes: ["Spr 15,16; 16,8"] },
      { number: 17, text: "Denn die Arme der Frevler werden zerschmettert, / doch die Gerechten stützt der Herr.", footnotes: ["10,15"] },
      { number: 18, text: "Der Herr kennt die Tage der Bewährten, / ihr Erbe hat ewig Bestand." },
      { number: 19, text: "In bösen Zeiten werden sie nicht zuschanden, / sie werden satt in den Tagen des Hungers.", footnotes: ["33,19"] },
      { number: 20, text: "Doch die Frevler gehen zugrunde, / die Feinde des Herrn sind wie die Pracht der Auen: / Sie schwinden dahin, wie Rauch schwinden sie hin.", footnotes: ["68,3"] },
      { number: 21, text: "Der Frevler muss borgen und kann nicht bezahlen, / doch freigebig schenkt der Gerechte." },
      { number: 22, text: "Denn wen der Herr segnet, der wird das Land besitzen, / aber wen er verflucht, der wird ausgetilgt.", footnotes: ["Spr 2,21f"] },
      { number: 23, text: "Der Herr festigt die Schritte des Mannes, / er hat Gefallen an seinem Weg." },
      { number: 24, text: "Auch wenn er strauchelt, stürzt er nicht hin; / denn der Herr hält ihn fest an der Hand.", footnotes: ["145,14; Spr 24,16"] },
      { number: 25, text: "Einst war ich jung, nun bin ich alt, / nie sah ich einen Gerechten verlassen / noch seine Kinder betteln um Brot." },
      { number: 26, text: "Allzeit ist er mildtätig, gern leiht er aus, / seine Kinder werden zum Segen." },
      { number: 27, text: "Meide das Böse und tu das Gute, / so bleibst du wohnen für immer.", footnotes: ["34,15; Am 5,14"] },
      { number: 28, text: "Denn der Herr liebt das Recht / und verlässt seine Frommen nicht. Doch das Geschlecht der Frevler wird ausgetilgt, / sie werden für immer vernichtet.", footnotes: ["sinngemäß umgestellt; Text korr.; H: sie werden für immer bewahrt."] },
      { number: 29, text: "Die Gerechten werden das Land besitzen / und darin wohnen für alle Zeiten.", footnotes: ["25,13; Jes 60,21"] },
      { number: 30, text: "Der Mund des Gerechten bewegt Worte der Weisheit / und seine Zunge redet, was recht ist.", footnotes: ["Spr 10,31"] },
      { number: 31, text: "Er hat die Weisung seines Gottes im Herzen, / seine Schritte wanken nicht.", footnotes: ["40,9"] },
      { number: 32, text: "Der Frevler belauert den Gerechten / und sucht ihn zu töten.", footnotes: ["11,2"] },
      { number: 33, text: "Der Herr überlässt ihn nicht seiner Hand, / lässt nicht zu, dass man ihn vor Gericht verurteilt." },
      { number: 34, text: "Hoffe auf den Herrn / und bleib auf seinem Weg! Er wird dich erhöhen zum Erben des Landes; / du wirst sehen, wie der Frevler vernichtet wird." },
      { number: 35, text: "Ich sah einen Frevler, bereit zu Gewalttat; / er reckte sich hoch wie eine grünende Zeder.", footnotes: ["92,8; (35-36) Ijob 20,6f", "Text korr.; H: und er entwickelte sich wie eine lebenskräftige Pflanze."] },
      { number: 36, text: "Wieder ging ich vorüber und er war nicht mehr da; / ich suchte ihn, doch er war nicht zu finden.", footnotes: ["Text korr. nach G, S und Hieronymus; H: ging er vorüber."] },
      { number: 37, text: "Achte auf den Frommen und schau auf den Redlichen! / Denn Zukunft hat der Mann des Friedens." },
      { number: 38, text: "Die Sünder aber werden alle zusammen vernichtet; / die Zukunft der Frevler ist Untergang." },
      { number: 39, text: "Die Rettung der Gerechten kommt vom Herrn, / er ist ihre Zuflucht in Zeiten der Not.", footnotes: ["46,2"] },
      { number: 40, text: "Der Herr hilft ihnen und rettet sie, / er rettet sie vor den Frevlern; er schenkt ihnen Heil, / denn sie suchen Zuflucht bei ihm." },
    ] },
    { number: 38, verses: [
      { number: 1, text: "[Ein Psalm Davids. Zum Weihrauchopfer.]", heading: "Die Klage eines Kranken" },
      { number: 2, text: "Herr, strafe mich nicht in deinem Zorn / und züchtige mich nicht in deinem Grimm!", footnotes: ["6,2"] },
      { number: 3, text: "Denn deine Pfeile haben mich getroffen, / deine Hand lastet schwer auf mir.", footnotes: ["32,4; Ijob 6,4"] },
      { number: 4, text: "Nichts blieb gesund an meinem Leib, weil du mir grollst; / weil ich gesündigt, blieb an meinen Gliedern nichts heil." },
      { number: 5, text: "Denn meine Sünden schlagen mir über dem Kopf zusammen, / sie erdrücken mich wie eine schwere Last.", footnotes: ["40,13; Esra 9,6"] },
      { number: 6, text: "Mir schwären, mir eitern die Wunden / wegen meiner Torheit." },
      { number: 7, text: "Ich bin gekrümmt und tief gebeugt, / den ganzen Tag geh ich traurig einher." },
      { number: 8, text: "Denn meine Lenden sind voller Brand, / nichts blieb gesund an meinem Leib." },
      { number: 9, text: "Kraftlos bin ich und ganz zerschlagen, / ich schreie in der Qual meines Herzens." },
      { number: 10, text: "All mein Sehnen, Herr, liegt offen vor dir, / mein Seufzen ist dir nicht verborgen." },
      { number: 11, text: "Mein Herz pocht heftig, mich hat die Kraft verlassen, / geschwunden ist mir das Licht der Augen.", footnotes: ["31,10", "Der sinnstörende Einschub «auch sie» in H, der nach «Licht der Augen» steht, bleibt wie in einigen G-Handschriften und S unübersetzt."] },
      { number: 12, text: "Freunde und Gefährten bleiben mir fern in meinem Unglück / und meine Nächsten meiden mich.", footnotes: ["31,12"] },
      { number: 13, text: "Die mir nach dem Leben trachten, legen mir Schlingen; / die mein Unheil suchen, planen Verderben, / den ganzen Tag haben sie Arglist im Sinn.", footnotes: ["35,20"] },
      { number: 14, text: "Ich bin wie ein Tauber, der nicht hört, / wie ein Stummer, der den Mund nicht auftut." },
      { number: 15, text: "Ich bin wie einer, der nicht mehr hören kann, / aus dessen Mund keine Entgegnung kommt." },
      { number: 16, text: "Doch auf dich, Herr, harre ich; / du wirst mich erhören, Herr, mein Gott." },
      { number: 17, text: "Denn ich sage: Über mich sollen die sich nicht freuen, / die gegen mich prahlen, wenn meine Füße straucheln.", footnotes: ["13,5; 35,19"] },
      { number: 18, text: "Ich bin dem Fallen nahe, / mein Leid steht mir immer vor Augen." },
      { number: 19, text: "Ja, ich bekenne meine Schuld, / ich bin wegen meiner Sünde in Angst.", footnotes: ["32,5; 51,5; 2 Sam 12,13; Ijob 31,33; Spr 28,13; Hos 14,3; Jak 5,16"] },
      { number: 20, text: "Die mich ohne Grund befehden, sind stark; / viele hassen mich wegen nichts.", footnotes: ["Text korr."] },
      { number: 21, text: "Sie vergelten mir Gutes mit Bösem, / sie sind mir Feind; denn ich trachte nach dem Guten.", footnotes: ["35,12"] },
      { number: 22, text: "Herr, verlass mich nicht, bleib mir nicht fern, mein Gott! /", footnotes: ["(22-23) 22,12"] },
      { number: 23, text: "Eile mir zu Hilfe, Herr, du mein Heil!" },
    ] },
    { number: 39, verses: [
      { number: 1, text: "[Für den Chormeister. Von Jedutun. Ein Psalm Davids.]", heading: "Die Not des vergänglichen Menschen" },
      { number: 2, text: "Ich sagte: Ich will auf meine Wege achten, / damit ich nicht sündige mit meiner Zunge. Ich lege meinem Mund einen Zaum an, / solange der Frevler vor mir steht." },
      { number: 3, text: "So blieb ich stumm und still; / ich schwieg, vom Glück verlassen, / doch mein Schmerz war aufgerührt." },
      { number: 4, text: "Heiß wurde mir das Herz in der Brust, / bei meinem Grübeln entbrannte ein Feuer; / da musste ich reden:" },
      { number: 5, text: "Herr, tu mir mein Ende kund und die Zahl meiner Tage! / Lass mich erkennen, wie sehr ich vergänglich bin!", footnotes: ["90,12"] },
      { number: 6, text: "Du machtest meine Tage nur eine Spanne lang, / meine Lebenszeit ist vor dir wie ein Nichts. / Ein Hauch nur ist jeder Mensch. [Sela]", footnotes: ["(6-7) 90,9f; 109,23; 144,4; Ijob 7,6f; 14,2; Jak 4,14"] },
      { number: 7, text: "Nur wie ein Schatten geht der Mensch einher, / um ein Nichts macht er Lärm. / Er rafft zusammen und weiß nicht, wer es einheimst.", footnotes: ["49,18"] },
      { number: 8, text: "Und nun, Herr, worauf soll ich hoffen? / Auf dich allein will ich harren." },
      { number: 9, text: "Entreiß mich allen, die mir Unrecht tun, / und überlass mich nicht dem Spott der Toren!" },
      { number: 10, text: "Ich bin verstummt, ich tue den Mund nicht mehr auf. / Denn so hast du es gefügt." },
      { number: 11, text: "Nimm deine Plage weg von mir! / Unter der Wucht deiner Hand vergehe ich." },
      { number: 12, text: "Du strafst und züchtigst den Mann wegen seiner Schuld, / du zerstörst seine Anmut wie Motten das Kleid, / ein Hauch nur ist jeder Mensch. [Sela]", footnotes: ["Ijob 13,28"] },
      { number: 13, text: "Hör mein Gebet, Herr, vernimm mein Schreien, / schweig nicht zu meinen Tränen! Denn ich bin nur ein Gast bei dir, / ein Fremdling wie all meine Väter.", footnotes: ["119,19; Lev 25,23; Hebr 11,13; 1 Petr 2,11"] },
      { number: 14, text: "Wende dein strafendes Auge ab von mir, / sodass ich heiter blicken kann, / bevor ich dahinfahre und nicht mehr da bin.", footnotes: ["Ijob 7,19; 10,21; 14,6", "Text korr.; H ist unverständlich."] },
    ] },
    { number: 40, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Dank, Hingabe und Bitte" },
      { number: 2, text: "Ich hoffte, ja ich hoffte auf den Herrn. / Da neigte er sich mir zu und hörte mein Schreien." },
      { number: 3, text: "Er zog mich herauf aus der Grube des Grauens, / aus Schlamm und Morast. Er stellte meine Füße auf den Fels, / machte fest meine Schritte.", footnotes: ["69,15f"] },
      { number: 4, text: "Er legte mir ein neues Lied in den Mund, / einen Lobgesang auf ihn, unsern Gott. Viele werden es sehen, sich in Ehrfurcht neigen / und auf den Herrn vertrauen.", footnotes: ["33,3"] },
      { number: 5, text: "Wohl dem Mann, der auf den Herrn sein Vertrauen setzt, / sich nicht zu den Stolzen hält / noch zu treulosen Lügnern.", footnotes: ["1,1; Jer 17,7"] },
      { number: 6, text: "Zahlreich sind die Wunder, die du getan hast, / und deine Pläne mit uns; / Herr, mein Gott, nichts kommt dir gleich. Wollte ich von ihnen künden und reden, / es wären mehr, als man zählen kann.", footnotes: ["139,17f"] },
      { number: 7, text: "An Schlacht- und Speiseopfern hast du kein Gefallen, / Brand- und Sündopfer forderst du nicht. Doch das Gehör hast du mir eingepflanzt; /", footnotes: ["50,7-15; 51,18f; 69,31f; 1 Sam 15,22; Jes 1,11; Jer 6,20; Am 5,22; (7-9) Hebr 10,5-7"] },
      { number: 8, text: "darum sage ich: Ja, ich komme. / In dieser Schriftrolle steht, was an mir geschehen ist." },
      { number: 9, text: "Deinen Willen zu tun, mein Gott, macht mir Freude, / deine Weisung trag ich im Herzen.", footnotes: ["37,31; Joh 4,34"] },
      { number: 10, text: "Gerechtigkeit verkünde ich in großer Gemeinde, / meine Lippen verschließe ich nicht; Herr, du weißt es.", footnotes: ["22,23"] },
      { number: 11, text: "Deine Gerechtigkeit verberge ich nicht im Herzen, / ich spreche von deiner Treue und Hilfe, ich schweige nicht über deine Huld und Wahrheit / vor der großen Gemeinde." },
      { number: 12, text: "Du, Herr, verschließ mir nicht dein Erbarmen, / deine Huld und Wahrheit mögen mich immer behüten!" },
      { number: 13, text: "Denn Leiden ohne Zahl umfangen mich, / meine Sünden holen mich ein, / ich vermag nicht mehr aufzusehn. Zahlreicher sind sie als die Haare auf meinem Kopf, / der Mut hat mich ganz verlassen.", footnotes: ["38,5"] },
      { number: 14, text: "Gewähre mir die Gunst, Herr, und reiß mich heraus; / Herr, eile mir zu Hilfe!", footnotes: ["22,12; (14-18) Ps 70,1-6"] },
      { number: 15, text: "In Schmach und Schande sollen alle fallen, / die mir nach dem Leben trachten. Zurückweichen sollen sie und vor Scham erröten, / die sich über mein Unglück freuen.", footnotes: ["35,4", "Text korr. nach 70,3; H fügt hinzu: um es hinwegzuraffen."] },
      { number: 16, text: "Vor Schande sollen alle schaudern, / die zu mir sagen: «Dir geschieht recht.»", footnotes: ["35,21"] },
      { number: 17, text: "Alle, die dich suchen, frohlocken; / sie mögen sich freuen in dir. Die dein Heil lieben, sollen immer sagen: / Groß ist Gott, der Herr.", footnotes: ["35,27"] },
      { number: 18, text: "Ich bin arm und gebeugt; / der Herr aber sorgt für mich. Meine Hilfe und mein Retter bist du. / Mein Gott, säume doch nicht!" },
    ] },
    { number: 41, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Das Gebet eines Kranken und Verfolgten" },
      { number: 2, text: "Wohl dem, der sich des Schwachen annimmt; / zur Zeit des Unheils wird der Herr ihn retten.", footnotes: ["Spr 14,21; Mt 5,7"] },
      { number: 3, text: "Ihn wird der Herr behüten / und am Leben erhalten. Man preist ihn glücklich im Land. / Gib ihn nicht seinen gierigen Feinden preis!" },
      { number: 4, text: "Auf dem Krankenbett wird der Herr ihn stärken; / seine Krankheit verwandelst du in Kraft." },
      { number: 5, text: "Ich sagte: Herr, sei mir gnädig, / heile mich; denn ich habe gegen dich gesündigt.", footnotes: ["6,3"] },
      { number: 6, text: "Meine Feinde reden böse über mich: / «Wann stirbt er endlich und wann vergeht sein Name?»", footnotes: ["22,7f; 44,14f; 71,7; 89,51; 102,9; 109,25; 123,3f"] },
      { number: 7, text: "Besucht mich jemand, / so kommen seine Worte aus falschem Herzen. Er häuft in sich Bosheit an, / dann geht er hinaus und redet." },
      { number: 8, text: "Im Hass gegen mich sind sich alle einig; / sie tuscheln über mich und sinnen auf Unheil:" },
      { number: 9, text: "«Verderben hat sich über ihn ergossen; / wer einmal daliegt, steht nicht mehr auf.»" },
      { number: 10, text: "Auch mein Freund, dem ich vertraute, / der mein Brot aß, hat gegen mich geprahlt.", footnotes: ["31,12; Obd 7; Joh 13,18", "Text korr."] },
      { number: 11, text: "Du aber, Herr, sei mir gnädig; / richte mich auf, damit ich ihnen vergelten kann." },
      { number: 12, text: "Daran erkenne ich, dass du an mir Gefallen hast: / wenn mein Feind nicht über mich triumphieren darf." },
      { number: 13, text: "Weil ich aufrichtig bin, hältst du mich fest / und stellst mich vor dein Antlitz für immer." },
      { number: 14, text: "Gepriesen sei der Herr, der Gott Israels, / von Ewigkeit zu Ewigkeit. Amen, ja amen.", footnotes: ["Neh 9,5"] },
    ] },
    { number: 42, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Weisheitslied der Korachiter.]", heading: "Das zweite Buch | Sehnsucht nach dem lebendigen Gott" },
      { number: 2, text: "Wie der Hirsch lechzt nach frischem Wasser, / so lechzt meine Seele, Gott, nach dir.", footnotes: ["(2-3) 63,2; 84,3; 143,6"] },
      { number: 3, text: "Meine Seele dürstet nach Gott, / nach dem lebendigen Gott. Wann darf ich kommen / und Gottes Antlitz schauen?", footnotes: ["27,4"] },
      { number: 4, text: "Tränen waren mein Brot bei Tag und bei Nacht; / denn man sagt zu mir den ganzen Tag: / «Wo ist nun dein Gott?»", footnotes: ["80,6; 102,10; Ijob 3,24"] },
      { number: 5, text: "Das Herz geht mir über, wenn ich daran denke: / wie ich zum Haus Gottes zog in festlicher Schar, / mit Jubel und Dank in feiernder Menge.", footnotes: ["122,1", "Übersetzung nicht ganz sicher."] },
      { number: 6, text: "Meine Seele, warum bist du betrübt / und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, / meinem Gott und Retter, auf den ich schaue.", footnotes: ["62,6", "Text korr."] },
      { number: 7, text: "Betrübt ist meine Seele in mir, darum denke ich an dich / im Jordanland, am Hermon, am Mizar-Berg." },
      { number: 8, text: "Flut ruft der Flut zu beim Tosen deiner Wasser, / all deine Wellen und Wogen gehen über mich hin.", footnotes: ["18,5; 32,6; 69,2f.16; 88,8.18; 124,4f; Ijob 22,11; Jona 2,4.6"] },
      { number: 9, text: "Bei Tag schenke der Herr seine Huld; / ich singe ihm nachts und flehe zum Gott meines Lebens." },
      { number: 10, text: "Ich sage zu Gott, meinem Fels: / «Warum hast du mich vergessen? Warum muss ich trauernd umhergehen, / von meinem Feind bedrängt?»", footnotes: ["92,16"] },
      { number: 11, text: "Wie ein Stechen in meinen Gliedern / ist für mich der Hohn der Bedränger; denn sie rufen mir ständig zu: / «Wo ist nun dein Gott?»", footnotes: ["42,4; 79,10; 115,2; Joël 2,17; Mi 7,10"] },
      { number: 12, text: "Meine Seele, warum bist du betrübt / und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, / meinem Gott und Retter, auf den ich schaue.", footnotes: ["Text korr."] },
    ] },
    { number: 43, verses: [
      { number: 1, text: "Verschaff mir Recht, o Gott, / und führe meine Sache gegen ein treuloses Volk! / Rette mich vor bösen und tückischen Menschen!", footnotes: ["Ps 42 und 43 bilden ein einziges Lied, wie der Kehrvers 42,6.12 und 43,5 zeigt."] },
      { number: 2, text: "Denn du bist mein starker Gott. / Warum hast du mich verstoßen? Warum muss ich trauernd umhergehen, / von meinem Feind bedrängt?" },
      { number: 3, text: "Sende dein Licht und deine Wahrheit, / damit sie mich leiten; sie sollen mich führen zu deinem heiligen Berg / und zu deiner Wohnung.", footnotes: ["97,11"] },
      { number: 4, text: "So will ich zum Altar Gottes treten, zum Gott meiner Freude. / Jauchzend will ich dich auf der Harfe loben, / Gott, mein Gott.", footnotes: ["Text korr.; H: zum Gott der Freude meines Jubels."] },
      { number: 5, text: "Meine Seele, warum bist du betrübt / und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, / meinem Gott und Retter, auf den ich schaue.", footnotes: ["Text korr. wie in 42,6d und 12d."] },
    ] },
    { number: 44, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Weisheitslied der Korachiter.]", heading: "Klage in Kriegsnot" },
      { number: 2, text: "Gott, wir hörten es mit eigenen Ohren, / unsere Väter erzählten uns von dem Werk, das du in ihren Tagen vollbracht hast, / in den Tagen der Vorzeit.", footnotes: ["78,3; Dtn 4,9; 2 Sam 7,22f"] },
      { number: 3, text: "Mit eigener Hand hast du Völker vertrieben, / sie aber eingepflanzt. Du hast Nationen zerschlagen, / sie aber ausgesät.", footnotes: ["78,55; 80,9"] },
      { number: 4, text: "Denn sie gewannen das Land nicht mit ihrem Schwert, / noch verschaffte ihr Arm ihnen den Sieg; nein, deine Rechte war es, dein Arm und dein leuchtendes Angesicht; / denn du hattest an ihnen Gefallen.", footnotes: ["Dtn 8,17f; Jos 24,12; 4,7"] },
      { number: 5, text: "Du, mein König und mein Gott, / du bist es, der Jakob den Sieg verleiht.", footnotes: ["5,3", "Text korr."] },
      { number: 6, text: "Mit dir stoßen wir unsere Bedränger nieder, / in deinem Namen zertreten wir unsere Gegner.", footnotes: ["60,14"] },
      { number: 7, text: "Denn ich verlasse mich nicht auf meinen Bogen, / noch kann mein Schwert mir helfen;" },
      { number: 8, text: "nein, du hast uns vor unsern Bedrängern gerettet; / alle, die uns hassen, bedeckst du mit Schande." },
      { number: 9, text: "Wir rühmen uns Gottes den ganzen Tag / und preisen deinen Namen auf ewig. [Sela]" },
      { number: 10, text: "Doch nun hast du uns verstoßen und mit Schmach bedeckt, / du ziehst nicht mit unserm Heer in den Kampf.", footnotes: ["60,12"] },
      { number: 11, text: "Du lässt uns vor unsern Bedrängern fliehen / und Menschen, die uns hassen, plündern uns aus.", footnotes: ["Dtn 28,25"] },
      { number: 12, text: "Du gibst uns preis wie Schlachtvieh, / unter die Völker zerstreust du uns.", footnotes: ["Dtn 28,64"] },
      { number: 13, text: "Du verkaufst dein Volk um ein Spottgeld / und hast an dem Erlös keinen Gewinn.", footnotes: ["Jes 52,3"] },
      { number: 14, text: "Du machst uns zum Schimpf für die Nachbarn, / zu Spott und Hohn bei allen, die rings um uns wohnen.", footnotes: ["79,4; 80,7"] },
      { number: 15, text: "Du machst uns zum Spottlied der Völker, / die Heiden zeigen uns nichts als Verachtung." },
      { number: 16, text: "Meine Schmach steht mir allzeit vor Augen / und Scham bedeckt mein Gesicht" },
      { number: 17, text: "wegen der Worte des lästernden Spötters, / wegen der rachgierigen Blicke des Feindes." },
      { number: 18, text: "Das alles ist über uns gekommen / und doch haben wir dich nicht vergessen, / uns von deinem Bund nicht treulos abgewandt." },
      { number: 19, text: "Unser Herz ist nicht von dir gewichen, / noch hat unser Schritt deinen Pfad verlassen." },
      { number: 20, text: "Doch du hast uns verstoßen an den Ort der Schakale / und uns bedeckt mit Finsternis." },
      { number: 21, text: "Hätten wir den Namen unseres Gottes vergessen / und zu einem fremden Gott die Hände erhoben," },
      { number: 22, text: "würde Gott das nicht ergründen? / Denn er kennt die heimlichen Gedanken des Herzens." },
      { number: 23, text: "Nein, um deinetwillen werden wir getötet Tag für Tag, / behandelt wie Schafe, / die man zum Schlachten bestimmt hat.", footnotes: ["Röm 8,36"] },
      { number: 24, text: "Wach auf! Warum schläfst du, Herr? / Erwache, verstoß nicht für immer!", footnotes: ["74,1; 83,2"] },
      { number: 25, text: "Warum verbirgst du dein Gesicht, / vergisst unsere Not und Bedrängnis?", footnotes: ["89,47; Ijob 13,24"] },
      { number: 26, text: "Unsere Seele ist in den Staub hinabgebeugt, / unser Leib liegt am Boden.", footnotes: ["7,6; 119,25", "Wörtlich: klebt am Boden."] },
      { number: 27, text: "Steh auf und hilf uns! / In deiner Huld erlöse uns!" },
    ] },
    { number: 45, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Lilien». Ein Weisheitslied der Korachiter. Ein Liebeslied.]", heading: "Ein Lied zur Hochzeit des Königs" },
      { number: 2, text: "Mein Herz fließt über von froher Kunde, / ich weihe mein Lied dem König. / Meine Zunge gleicht dem Griffel des flinken Schreibers." },
      { number: 3, text: "Du bist der Schönste von allen Menschen, / Anmut ist ausgegossen über deine Lippen; / darum hat Gott dich für immer gesegnet." },
      { number: 4, text: "Gürte, du Held, dein Schwert um die Hüfte, / kleide dich in Hoheit und Herrlichkeit!" },
      { number: 5, text: "Zieh aus mit Glück, kämpfe für Wahrheit und Recht! / Furcht gebietende Taten / soll dein rechter Arm dich lehren.", footnotes: ["Text korr.; H: Kämpfe für die Sache der Wahrheit, der Armut und des Rechts!"] },
      { number: 6, text: "Deine Pfeile sind scharf, dir unterliegen die Völker, / die Feinde des Königs verlieren den Mut." },
      { number: 7, text: "Dein Thron, du Göttlicher, steht für immer und ewig; / das Zepter deiner Herrschaft ist ein gerechtes Zepter.", footnotes: ["(7-8) Hebr 1,8f"] },
      { number: 8, text: "Du liebst das Recht und hasst das Unrecht, / darum hat Gott, dein Gott, dich gesalbt mit dem Öl der Freude / wie keinen deiner Gefährten." },
      { number: 9, text: "Von Myrrhe, Aloe und Kassia duften all deine Gewänder, / aus Elfenbeinhallen erfreut dich Saitenspiel." },
      { number: 10, text: "Königstöchter gehen dir entgegen, / die Braut steht dir zur Rechten / im Schmuck von Ofirgold.", footnotes: ["Text korr.; H: Königstöchter unter deinen Kostbarkeiten."] },
      { number: 11, text: "Höre, Tochter, sieh her und neige dein Ohr, / vergiss dein Volk und dein Vaterhaus!", footnotes: ["Gen 12,1"] },
      { number: 12, text: "Der König verlangt nach deiner Schönheit; / er ist ja dein Herr, verneig dich vor ihm!" },
      { number: 13, text: "Die Töchter von Tyrus kommen mit Gaben, / deine Gunst begehren die Edlen des Volkes.", footnotes: ["Nach G."] },
      { number: 14, text: "Die Königstochter ist herrlich geschmückt, / ihr Gewand ist durchwirkt mit Gold und Perlen." },
      { number: 15, text: "Man geleitet sie in bunt gestickten Kleidern zum König, / Jungfrauen sind ihr Gefolge, / ihre Freundinnen führt man zu dir.", footnotes: ["Ez 16,10-13"] },
      { number: 16, text: "Man geleitet sie mit Freude und Jubel, / sie ziehen ein in den Palast des Königs." },
      { number: 17, text: "An die Stelle deiner Väter treten einst deine Söhne; / du bestellst sie zu Fürsten im ganzen Land.", footnotes: ["2 Chr 21,3"] },
      { number: 18, text: "Ich will deinen Namen rühmen von Geschlecht zu Geschlecht; / darum werden die Völker dich preisen immer und ewig." },
    ] },
    { number: 46, verses: [
      { number: 1, text: "[Für den Chormeister. Von den Korachitern. Nach der Weise «Mädchen». Ein Lied.]", heading: "Gott, unsre Burg" },
      { number: 2, text: "Gott ist uns Zuflucht und Stärke, / ein bewährter Helfer in allen Nöten.", footnotes: ["37,39; 61,4"] },
      { number: 3, text: "Darum fürchten wir uns nicht, wenn die Erde auch wankt, / wenn Berge stürzen in die Tiefe des Meeres,", footnotes: ["75,4; Ijob 9,5f"] },
      { number: 4, text: "wenn seine Wasserwogen tosen und schäumen / und vor seinem Ungestüm die Berge erzittern. Der Herr der Heerscharen ist mit uns, / der Gott Jakobs ist unsre Burg. [Sela]", footnotes: ["Der Kehrvers von V. 8 und V. 12 wurde auch hier eingefügt."] },
      { number: 5, text: "Die Wasser eines Stromes erquicken die Gottesstadt, / des Höchsten heilige Wohnung." },
      { number: 6, text: "Gott ist in ihrer Mitte, darum wird sie niemals wanken; / Gott hilft ihr, wenn der Morgen anbricht.", footnotes: ["90,14; 2 Kön 19,35"] },
      { number: 7, text: "Völker toben, Reiche wanken, / es dröhnt sein Donner, da zerschmilzt die Erde." },
      { number: 8, text: "Der Herr der Heerscharen ist mit uns, / der Gott Jakobs ist unsre Burg. [Sela]" },
      { number: 9, text: "Kommt und schaut die Taten des Herrn, / der Furchtbares vollbringt auf der Erde.", footnotes: ["66,5"] },
      { number: 10, text: "Er setzt den Kriegen ein Ende / bis an die Grenzen der Erde; er zerbricht die Bogen, zerschlägt die Lanzen, / im Feuer verbrennt er die Schilde.", footnotes: ["76,4; Jes 2,4; Ez 39,9f", "Andere Übersetzungsmöglichkeit: verbrennt er die Wagen."] },
      { number: 11, text: "«Lasst ab und erkennt, dass ich Gott bin, / erhaben über die Völker, erhaben auf Erden.»", footnotes: ["83,19"] },
      { number: 12, text: "Der Herr der Heerscharen ist mit uns, / der Gott Jakobs ist unsre Burg. [Sela]" },
    ] },
    { number: 47, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm der Korachiter.]", heading: "Gott, der König aller Völker" },
      { number: 2, text: "Ihr Völker alle, klatscht in die Hände; / jauchzt Gott zu mit lautem Jubel!" },
      { number: 3, text: "Denn Furcht gebietend ist der Herr, der Höchste, / ein großer König über die ganze Erde.", footnotes: ["95,3"] },
      { number: 4, text: "Er unterwirft uns Völker / und zwingt Nationen unter unsre Füße.", footnotes: ["2,8"] },
      { number: 5, text: "Er wählt unser Erbland für uns aus, / den Stolz Jakobs, den er liebt. [Sela]", footnotes: ["Dtn 32,9"] },
      { number: 6, text: "Gott stieg empor unter Jubel, / der Herr beim Schall der Hörner.", footnotes: ["68,19; 98,6"] },
      { number: 7, text: "Singt unserm Gott, ja singt ihm! / Spielt unserm König, spielt ihm!", footnotes: ["Nach G."] },
      { number: 8, text: "Denn Gott ist König der ganzen Erde. / Spielt ihm ein Psalmenlied!", footnotes: ["(8-9) 24,7-10"] },
      { number: 9, text: "Gott wurde König über alle Völker, / Gott sitzt auf seinem heiligen Thron.", footnotes: ["Jer 10,7", "Nach G."] },
      { number: 10, text: "Die Fürsten der Völker sind versammelt / als Volk des Gottes Abrahams. Denn Gott gehören die Mächte der Erde; / er ist hoch erhaben.", footnotes: ["87,4-6", "die Mächte: Text korr.; H: die Schilde."] },
    ] },
    { number: 48, verses: [
      { number: 1, text: "[Ein Lied. Ein Psalm der Korachiter.]", heading: "Die Stadt des großen Königs" },
      { number: 2, text: "Groß ist der Herr und hoch zu preisen / in der Stadt unsres Gottes.", footnotes: ["95,3; 96,4; 145,3"] },
      { number: 3, text: "Sein heiliger Berg ragt herrlich empor; / er ist die Freude der ganzen Welt. Der Berg Zion liegt weit im Norden; / er ist die Stadt des großen Königs.", footnotes: ["50,2; 78,68f; Jes 14,13; Klgl 2,15"] },
      { number: 4, text: "Gott ist in ihren Häusern bekannt / als ein sicherer Schutz.", footnotes: ["46,6"] },
      { number: 5, text: "Denn seht: Die Könige vereinten sich / und zogen gemeinsam heran;", footnotes: ["Ri 5,19"] },
      { number: 6, text: "doch als sie aufsahen, erstarrten sie vor Schreck, / sie waren bestürzt und liefen davon." },
      { number: 7, text: "Dort packte sie das Zittern, / wie die Wehen eine gebärende Frau,", footnotes: ["Ex 15,14"] },
      { number: 8, text: "wie der Sturm vom Osten, / der die Schiffe von Tarschisch zerschmettert.", footnotes: ["Jes 2,16"] },
      { number: 9, text: "Wie wir's gehört hatten, so erlebten wir's jetzt / in der Stadt des Herrn der Heere, in der Stadt unsres Gottes; / Gott lässt sie ewig bestehen. [Sela]" },
      { number: 10, text: "Über deine Huld, o Gott, denken wir nach / in deinem heiligen Tempel." },
      { number: 11, text: "Wie dein Name, Gott, so reicht dein Ruhm bis an die Enden der Erde; / deine rechte Hand ist voll von Gerechtigkeit.", footnotes: ["113,3; Mal 1,11"] },
      { number: 12, text: "Der Berg Zion freue sich, / die Töchter Judas sollen über deine gerechten Urteile jubeln.", footnotes: ["97,8"] },
      { number: 13, text: "Umkreist den Zion, / umschreitet ihn, zählt seine Türme!", footnotes: ["122,3; Jes 26,1"] },
      { number: 14, text: "Betrachtet seine Wälle, / geht in seinen Palästen umher, / damit ihr dem kommenden Geschlecht erzählen könnt:", footnotes: ["22,31f"] },
      { number: 15, text: "«Das ist Gott, unser Gott für immer und ewig. / Er wird uns führen in Ewigkeit.»", footnotes: ["102,28", "Nach G."] },
    ] },
    { number: 49, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm der Korachiter.]", heading: "Die Vergänglichkeit des Menschen" },
      { number: 2, text: "Hört dies an, ihr Völker alle, / vernehmt es, alle Bewohner der Erde,", footnotes: ["(2-3) Spr 8,4"] },
      { number: 3, text: "ihr Leute aus dem Volk und vom Adel, / Reiche und Arme zusammen!" },
      { number: 4, text: "Mein Mund spreche weise Worte; / was mein Herz ersinnt, sei voller Einsicht." },
      { number: 5, text: "Ich wende mein Ohr einem Weisheitsspruch zu, / ich enthülle mein Geheimnis beim Harfenspiel.", footnotes: ["78,2"] },
      { number: 6, text: "Warum soll ich mich in bösen Tagen fürchten, / wenn mich der Frevel tückischer Feinde umgibt?" },
      { number: 7, text: "Sie verlassen sich ganz auf ihren Besitz / und rühmen sich ihres großen Reichtums.", footnotes: ["52,9; Ijob 31,24f; Jer 9,22"] },
      { number: 8, text: "Loskaufen kann doch keiner den andern / noch an Gott für ihn ein Sühnegeld zahlen", footnotes: ["(8-9) Spr 11,4; Mt 16,26"] },
      { number: 9, text: "- für das Leben ist jeder Kaufpreis zu hoch, / für immer muss man davon abstehn -," },
      { number: 10, text: "damit er auf ewig weiterlebt / und niemals das Grab schaut." },
      { number: 11, text: "Denn man sieht: Weise sterben; / genauso gehen Tor und Narr zugrunde, / sie müssen andern ihren Reichtum lassen.", footnotes: ["Koh 2,16; Ps 49,18"] },
      { number: 12, text: "Das Grab ist ihr Haus auf ewig, / ist ihre Wohnung für immer, / ob sie auch Länder nach ihren Namen benannten.", footnotes: ["Koh 12,5", "Das Grab: Text korr. nach G, S und der aramäischen Übersetzung; H: Ihr Inneres."] },
      { number: 13, text: "Der Mensch bleibt nicht in seiner Pracht; / er gleicht dem Vieh, das verstummt.", footnotes: ["Koh 3,19"] },
      { number: 14, text: "So geht es denen, die auf sich selbst vertrauen, / und so ist das Ende derer, die sich in großen Worten gefallen. [Sela]" },
      { number: 15, text: "Der Tod führt sie auf seine Weide wie Schafe, / sie stürzen hinab zur Unterwelt. Geradewegs sinken sie hinab in das Grab; / ihre Gestalt zerfällt, die Unterwelt wird ihre Wohnstatt.", footnotes: ["55,16", "H ist stark verderbt."] },
      { number: 16, text: "Doch Gott wird mich loskaufen aus dem Reich des Todes, / ja, er nimmt mich auf. [Sela]", footnotes: ["9,14; 16,10; 73,24; Hos 13,14; Gen 5,24"] },
      { number: 17, text: "Lass dich nicht beirren, wenn einer reich wird / und die Pracht seines Hauses sich mehrt;", footnotes: ["(17-18) Sir 11,18f"] },
      { number: 18, text: "denn im Tod nimmt er das alles nicht mit, / seine Pracht steigt nicht mit ihm hinab.", footnotes: ["49,11; 39,7; Koh 2,18.21f; 5,14; 6,2; Sir 11,18; Lk 12,20; 1 Tim 6,7"] },
      { number: 19, text: "Preist er sich im Leben auch glücklich / und sagt zu sich: / «Man lobt dich, weil du dir's wohl sein lässt»," },
      { number: 20, text: "so muss er doch zur Schar seiner Väter hinab, / die das Licht nie mehr erblicken.", footnotes: ["Ijob 10,21f"] },
      { number: 21, text: "Der Mensch in Pracht, doch ohne Einsicht, / er gleicht dem Vieh, das verstummt." },
    ] },
    { number: 50, verses: [
      { number: 1, text: "[Ein Psalm Asafs.] Der Gott der Götter, der Herr, spricht, / er ruft der Erde zu / vom Aufgang der Sonne bis zum Untergang.", heading: "Der rechte Gottesdienst", footnotes: ["Dtn 10,17"] },
      { number: 2, text: "Vom Zion her, der Krone der Schönheit, / geht Gott strahlend auf.", footnotes: ["48,3"] },
      { number: 3, text: "Unser Gott kommt und schweigt nicht; / Feuer frisst vor ihm her; um ihn stürmt es gewaltig.", footnotes: ["97,3f; Jes 63,19; Dan 7,10"] },
      { number: 4, text: "Dem Himmel droben und der Erde ruft er zu, / er werde sein Volk nun richten:", footnotes: ["Dtn 32,1; Jes 1,2"] },
      { number: 5, text: "«Versammelt mir all meine Frommen, / die den Bund mit mir schlossen beim Opfer.»", footnotes: ["Ex 24,4-11"] },
      { number: 6, text: "Die Himmel sollen seine Gerechtigkeit künden; / Gott selbst wird Richter sein. [Sela]", footnotes: ["19,2; 97,6"] },
      { number: 7, text: "«Höre, mein Volk, ich rede. / Israel, ich klage dich an, / ich, der ich dein Gott bin.", footnotes: ["Ex 20,2; (7-15) 40,7"] },
      { number: 8, text: "Nicht wegen deiner Opfer rüg ich dich, / deine Brandopfer sind mir immer vor Augen." },
      { number: 9, text: "Doch nehme ich von dir Stiere nicht an / noch Böcke aus deinen Hürden." },
      { number: 10, text: "Denn mir gehört alles Getier des Waldes, / das Wild auf den Bergen zu Tausenden." },
      { number: 11, text: "Ich kenne alle Vögel des Himmels, / was sich regt auf dem Feld, ist mein Eigen.", footnotes: ["Text korr. nach G; H: die Vögel der Berge."] },
      { number: 12, text: "Hätte ich Hunger, ich brauchte es dir nicht zu sagen, / denn mein ist die Welt und was sie erfüllt.", footnotes: ["24,1"] },
      { number: 13, text: "Soll ich denn das Fleisch von Stieren essen / und das Blut von Böcken trinken?" },
      { number: 14, text: "Bring Gott als Opfer dein Lob / und erfülle dem Höchsten deine Gelübde!", footnotes: ["119,108; 66,13; Hebr 13,15"] },
      { number: 15, text: "Rufe mich an am Tag der Not; / dann rette ich dich und du wirst mich ehren.»", footnotes: ["91,15"] },
      { number: 16, text: "Zum Frevler aber spricht Gott: / «Was zählst du meine Gebote auf / und nimmst meinen Bund in deinen Mund?", footnotes: ["Röm 2,17-24"] },
      { number: 17, text: "Dabei ist Zucht dir verhasst, / meine Worte wirfst du hinter dich." },
      { number: 18, text: "Siehst du einen Dieb, so läufst du mit, / du machst dich mit Ehebrechern gemein." },
      { number: 19, text: "Dein Mund redet böse Worte / und deine Zunge stiftet Betrug an." },
      { number: 20, text: "Von deinem Bruder redest du schändlich, / auf den Sohn deiner Mutter häufst du Verleumdung.", footnotes: ["schändlich: Text korr."] },
      { number: 21, text: "Das hast du getan und ich soll schweigen? / Meinst du, ich bin wie du? / Ich halte es dir vor Augen und rüge dich." },
      { number: 22, text: "Begreift es doch, ihr, die ihr Gott vergesst! / Sonst zerreiße ich euch und niemand kann euch retten." },
      { number: 23, text: "Wer Opfer des Lobes bringt, ehrt mich; / wer rechtschaffen lebt, dem zeig ich mein Heil.»", footnotes: ["50,14; 91,16", "Text korr."] },
    ] },
    { number: 51, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids,", heading: "Bitte um Vergebung und Neuschaffung" },
      { number: 2, text: "als der Prophet Natan zu ihm kam, nachdem sich David mit Batseba vergangen hatte.]", footnotes: ["2 Sam 12"] },
      { number: 3, text: "Gott, sei mir gnädig nach deiner Huld, / tilge meine Frevel nach deinem reichen Erbarmen!", footnotes: ["Lk 18,13"] },
      { number: 4, text: "Wasch meine Schuld von mir ab / und mach mich rein von meiner Sünde!" },
      { number: 5, text: "Denn ich erkenne meine bösen Taten, / meine Sünde steht mir immer vor Augen.", footnotes: ["Jes 59,12"] },
      { number: 6, text: "Gegen dich allein habe ich gesündigt, / ich habe getan, was dir missfällt. So behältst du recht mit deinem Urteil, / rein stehst du da als Richter.", footnotes: ["Röm 3,4"] },
      { number: 7, text: "Denn ich bin in Schuld geboren; / in Sünde hat mich meine Mutter empfangen.", footnotes: ["Ijob 14,4; Joh 3,6"] },
      { number: 8, text: "Lauterer Sinn im Verborgenen gefällt dir, / im Geheimen lehrst du mich Weisheit." },
      { number: 9, text: "Entsündige mich mit Ysop, dann werde ich rein; / wasche mich, dann werde ich weißer als Schnee.", footnotes: ["Num 19,18; Jes 1,18"] },
      { number: 10, text: "Sättige mich mit Entzücken und Freude! / Jubeln sollen die Glieder, die du zerschlagen hast.", footnotes: ["6,3", "Text korr."] },
      { number: 11, text: "Verbirg dein Gesicht vor meinen Sünden, / tilge all meine Frevel!" },
      { number: 12, text: "Erschaffe mir, Gott, ein reines Herz / und gib mir einen neuen, beständigen Geist!", footnotes: ["Ez 36,26f"] },
      { number: 13, text: "Verwirf mich nicht von deinem Angesicht / und nimm deinen heiligen Geist nicht von mir!", footnotes: ["(13-14) Röm 8,9.14-16"] },
      { number: 14, text: "Mach mich wieder froh mit deinem Heil / mit einem willigen Geist rüste mich aus!" },
      { number: 15, text: "Dann lehre ich Abtrünnige deine Wege / und die Sünder kehren um zu dir." },
      { number: 16, text: "Befrei mich von Blutschuld, Herr, du Gott meines Heiles, / dann wird meine Zunge jubeln über deine Gerechtigkeit." },
      { number: 17, text: "Herr, öffne mir die Lippen / und mein Mund wird deinen Ruhm verkünden." },
      { number: 18, text: "Schlachtopfer willst du nicht, ich würde sie dir geben; / an Brandopfern hast du kein Gefallen.", footnotes: ["40,7"] },
      { number: 19, text: "Das Opfer, das Gott gefällt, ist ein zerknirschter Geist, / ein zerbrochenes und zerschlagenes Herz wirst du, Gott, nicht verschmähen.", footnotes: ["34,19; Jes 57,15; 66,2; Dan 3,39"] },
      { number: 20, text: "In deiner Huld tu Gutes an Zion; / bau die Mauern Jerusalems wieder auf!", footnotes: ["147,2"] },
      { number: 21, text: "Dann hast du Freude an rechten Opfern, / an Brandopfern und Ganzopfern, / dann opfert man Stiere auf deinem Altar.", footnotes: ["4,6"] },
    ] },
    { number: 52, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Weisheitslied Davids,", heading: "Die Überheblichkeit des Bösen - das Vertrauen des Frommen" },
      { number: 2, text: "als der Edomiter Doëg zu Saul kam und ihm meldete: David ist in das Haus des Ahimelech gegangen.]", footnotes: ["1 Sam 22,9f"] },
      { number: 3, text: "Was rühmst du dich deiner Bosheit, du Mann der Gewalt, / was prahlst du allzeit vor dem Frommen?", footnotes: ["Text korr. nach S; H erlaubt auch die Übersetzung: Was rühmst du dich, dass Gottes Huld allezeit (auf deiner Seite) ist."] },
      { number: 4, text: "Du Ränkeschmied, du planst Verderben; / deine Zunge gleicht einem scharfen Messer.", footnotes: ["59,8; (4-6) 12,3-5"] },
      { number: 5, text: "Du liebst das Böse mehr als das Gute / und Lüge mehr als wahrhaftige Rede. [Sela]", footnotes: ["Jer 9,4; Joh 3,19f"] },
      { number: 6, text: "Du liebst lauter verderbliche Worte, / du tückische Zunge." },
      { number: 7, text: "Darum wird Gott dich verderben für immer, / dich packen und herausreißen aus deinem Zelt, / dich entwurzeln aus dem Land der Lebenden. [Sela]", footnotes: ["27,13; Ijob 18,14"] },
      { number: 8, text: "Gerechte werden es sehen und sich fürchten; / sie werden über ihn lachen und sagen:", footnotes: ["58,11; 91,8; Ijob 22,19"] },
      { number: 9, text: "«Seht, das ist der Mann, / der nicht zu Gott seine Zuflucht nahm; auf seinen großen Reichtum hat er sich verlassen / und auf seinen Frevel gebaut.»", footnotes: ["49,7"] },
      { number: 10, text: "Ich aber bin im Haus Gottes wie ein grünender Ölbaum; / auf Gottes Huld vertraue ich immer und ewig.", footnotes: ["92,13-16"] },
      { number: 11, text: "Ich danke dir, Herr, in Ewigkeit; / denn du hast das alles vollbracht. Ich hoffe auf deinen Namen im Kreis der Frommen; / denn du bist gütig.", footnotes: ["54,8"] },
    ] },
    { number: 53, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Krankheit». Ein Weisheitslied Davids.]", heading: "Die Torheit der Gottesleugner", footnotes: ["(1-7) Ps 14"] },
      { number: 2, text: "Die Toren sagen in ihrem Herzen: / «Es gibt keinen Gott.» Sie handeln verwerflich und schnöde; / da ist keiner, der Gutes tut." },
      { number: 3, text: "Gott blickt vom Himmel herab auf die Menschen, / ob noch ein Verständiger da ist, der Gott sucht." },
      { number: 4, text: "Alle sind sie abtrünnig und verdorben, / keiner tut Gutes, auch nicht ein Einziger.", footnotes: ["Text korr. nach G und der aramäischen Übersetzung; vgl. auch 14,3."] },
      { number: 5, text: "Haben denn die Übeltäter keine Einsicht? / Sie verschlingen mein Volk. Sie essen Gottes Brot, / doch seinen Namen rufen sie nicht an." },
      { number: 6, text: "Es trifft sie Furcht und Schrecken, / obwohl doch nichts zu fürchten ist. Deinen Bedrängern hat Gott die Glieder zerschlagen. / Gott lässt sie scheitern, denn er hat sie verworfen.", footnotes: ["Wörtlich: Gott zerstreut die Gebeine deines Belagerers. Text korr. nach G."] },
      { number: 7, text: "Ach käme doch vom Zion Hilfe für Israel! / Wenn Gott einst das Geschick seines Volkes wendet, / dann jubelt Jakob, dann freut sich Israel." },
    ] },
    { number: 54, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Ein Weisheitslied Davids,", heading: "Hilferuf eines Bedrängten" },
      { number: 2, text: "als die Sifiter kamen und Saul meldeten: David hält sich bei uns verborgen.]", footnotes: ["1 Sam 23,19; 26,1"] },
      { number: 3, text: "Hilf mir, Gott, durch deinen Namen, / verschaff mir Recht mit deiner Kraft!" },
      { number: 4, text: "Gott, höre mein Flehen, / vernimm die Worte meines Mundes!" },
      { number: 5, text: "Denn es erheben sich gegen mich stolze Menschen, / freche Leute trachten mir nach dem Leben; / sie haben Gott nicht vor Augen. [Sela]", footnotes: ["86,14", "stolze Menschen: Text korr.; H: Fremde."] },
      { number: 6, text: "Doch Gott ist mein Helfer, / der Herr beschützt mein Leben.", footnotes: ["118,7"] },
      { number: 7, text: "Auf meine Gegner falle das Unheil zurück. / Weil du treu bist, vernichte sie!", footnotes: ["143,12"] },
      { number: 8, text: "Freudig bringe ich dir dann mein Opfer dar / und lobe deinen Namen, Herr; denn du bist gütig.", footnotes: ["52,11"] },
      { number: 9, text: "Der Herr hat mich herausgerissen aus all meiner Not / und mein Auge kann auf meine Feinde herabsehn.", footnotes: ["59,11; 91,8; 92,12; 112,8; 118,7"] },
    ] },
    { number: 55, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Ein Weisheitslied Davids.]", heading: "Klage und Vertrauen eines Alleingelassenen" },
      { number: 2, text: "Vernimm, o Gott, mein Beten; / verbirg dich nicht vor meinem Flehen!" },
      { number: 3, text: "Achte auf mich und erhöre mich! / Unstet schweife ich umher und klage." },
      { number: 4, text: "Das Geschrei der Feinde macht mich verstört; / mir ist angst, weil mich die Frevler bedrängen. Sie überhäufen mich mit Unheil / und befehden mich voller Grimm.", footnotes: ["Wörtlich: Sie lassen Unheil auf mich herabfallen."] },
      { number: 5, text: "Mir bebt das Herz in der Brust; / mich überfielen die Schrecken des Todes." },
      { number: 6, text: "Furcht und Zittern erfassten mich; / ich schauderte vor Entsetzen." },
      { number: 7, text: "Da dachte ich: «Hätte ich doch Flügel wie eine Taube, / dann flöge ich davon und käme zur Ruhe.»" },
      { number: 8, text: "Weit fort möchte ich fliehen, / die Nacht verbringen in der Wüste. [Sela]", footnotes: ["Jer 9,1"] },
      { number: 9, text: "An einen sicheren Ort möchte ich eilen / vor dem Wetter, vor dem tobenden Sturm." },
      { number: 10, text: "Entzweie sie, Herr, verwirr ihre Sprache! / Denn in der Stadt sehe ich Gewalttat und Hader.", footnotes: ["Jer 5,1; 6,6; Zef 3,1"] },
      { number: 11, text: "Auf ihren Mauern umschleicht man sie bei Tag und bei Nacht; / sie ist voll Unheil und Mühsal." },
      { number: 12, text: "In ihr herrscht Verderben; / Betrug und Unterdrückung weichen nicht von ihren Märkten." },
      { number: 13, text: "Denn nicht mein Feind beschimpft mich, / das würde ich ertragen; nicht ein Mann, der mich hasst, tritt frech gegen mich auf, / vor ihm könnte ich mich verbergen.", footnotes: ["(13-14) 31,12"] },
      { number: 14, text: "Nein, du bist es, ein Mensch aus meiner Umgebung, / mein Freund, mein Vertrauter,", footnotes: ["Jer 9,3.7; Mt 26,21-24"] },
      { number: 15, text: "mit dem ich, in Freundschaft verbunden, / zum Haus Gottes gepilgert bin inmitten der Menge." },
      { number: 16, text: "Der Tod soll sie überfallen, / lebend sollen sie hinabfahren ins Totenreich. / Denn ihre Häuser und Herzen sind voller Bosheit.", footnotes: ["49,15; Num 16,33; Jes 5,14"] },
      { number: 17, text: "Ich aber, zu Gott will ich rufen, / der Herr wird mir helfen." },
      { number: 18, text: "Am Abend, am Morgen, am Mittag seufze ich und stöhne; / er hört mein Klagen." },
      { number: 19, text: "Er befreit mich, bringt mein Leben in Sicherheit / vor denen, die gegen mich kämpfen, / wenn es auch viele sind, die gegen mich angehen.", footnotes: ["H ist verderbt."] },
      { number: 20, text: "Gott hört mich und beugt sie nieder, / er, der als König thront seit Ewigkeit. [Sela] Denn sie kehren nicht um / und fürchten Gott nicht.", footnotes: ["93,2"] },
      { number: 21, text: "Der Feind legt Hand an Gottes Freunde, / er entweiht Gottes Bund." },
      { number: 22, text: "Glatt wie Butter sind seine Reden, / doch in seinem Herzen sinnt er auf Streit; seine Worte sind linder als Öl / und sind doch gezückte Schwerter.", footnotes: ["57,5; 62,5; 64,4"] },
      { number: 24, text: "Du aber, Gott, wirst sie hinabstürzen in die tiefste Grube. / Gewalttätige und Betrüger erreichen nicht die Mitte ihres Lebens. / Ich aber setze mein Vertrauen auf dich.", footnotes: ["102,25"] },
      { number: 23, text: "Wirf deine Sorge auf den Herrn, er hält dich aufrecht! / Er lässt den Gerechten niemals wanken.", footnotes: ["37,5; 1 Petr 5,7"] },
    ] },
    { number: 56, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Stumme Taube der Ferne». Ein Lied Davids. Aus der Zeit, als die Philister ihn in Gat ergriffen.]", heading: "Das Vertrauensbekenntnis eines Angefeindeten", footnotes: ["1 Sam 21,11f"] },
      { number: 2, text: "Sei mir gnädig, Gott, denn Menschen stellen mir nach; / meine Feinde bedrängen mich Tag für Tag." },
      { number: 3, text: "Täglich stellen meine Gegner mir nach; / ja, es sind viele, die mich voll Hochmut bekämpfen.", footnotes: ["Andere Übersetzungsmöglichkeit: ja, es sind viele, die mich bekämpfen, Allerhöchster."] },
      { number: 4, text: "An dem Tag, da ich mich fürchten muss, / setze ich auf dich mein Vertrauen." },
      { number: 5, text: "Ich preise Gottes Wort. / Ich vertraue auf Gott und fürchte mich nicht. / Was können Menschen mir antun?", footnotes: ["27,1; 118,6; Jes 51,12; Mt 10,28; Röm 8,31"] },
      { number: 6, text: "Sie verdrehen meine Worte den ganzen Tag; / auf mein Verderben geht ihr ganzes Sinnen.", footnotes: ["Wörtlich: Sie kränken meine Worte."] },
      { number: 7, text: "Sie lauern und spähen und beobachten genau meine Schritte; / denn sie trachten mir nach dem Leben." },
      { number: 8, text: "Sie haben gefrevelt; es gibt für sie kein Entrinnen. / In deinem Zorn, o Gott, wirf die Völker zu Boden!", footnotes: ["es gibt für sie kein Entrinnen: Text korr.; H: würden sie trotz der Untat entkommen?"] },
      { number: 9, text: "Mein Elend ist aufgezeichnet bei dir. / Sammle meine Tränen in einem Krug, / zeichne sie auf in deinem Buch!", footnotes: ["2 Kön 20,5", "Wörtlich: sind sie nicht in deinem Buch?"] },
      { number: 10, text: "Dann weichen die Feinde zurück an dem Tag, da ich rufe. / Ich habe erkannt: Mir steht Gott zur Seite.", footnotes: ["124,1f; Röm 8,31"] },
      { number: 11, text: "Ich preise Gottes Wort, / ich preise das Wort des Herrn." },
      { number: 12, text: "Ich vertraue auf Gott und fürchte mich nicht. / Was können Menschen mir antun?" },
      { number: 13, text: "Ich schulde dir die Erfüllung meiner Gelübde, o Gott; / ich will dir Dankopfer weihen.", footnotes: ["66,13"] },
      { number: 14, text: "Denn du hast mein Leben dem Tod entrissen, / meine Füße bewahrt vor dem Fall. So gehe ich vor Gott meinen Weg / im Licht der Lebenden.", footnotes: ["9,14; 116,8f; Ijob 33,30"] },
    ] },
    { number: 57, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Zerstöre nicht!». Ein Lied Davids, als er vor Saul in die Höhle floh.]", heading: "Geborgenheit im Schutz Gottes", footnotes: ["1 Sam 22,1; 24,4"] },
      { number: 2, text: "Sei mir gnädig, o Gott, sei mir gnädig; / denn ich flüchte mich zu dir. Im Schatten deiner Flügel finde ich Zuflucht, / bis das Unheil vorübergeht.", footnotes: ["17,7f"] },
      { number: 3, text: "Ich rufe zu Gott, dem Höchsten, / zu Gott, der mir beisteht." },
      { number: 4, text: "Er sende mir Hilfe vom Himmel; / meine Feinde schmähen mich. [Sela] / Gott sende seine Huld und Treue." },
      { number: 5, text: "Ich muss mich mitten unter Löwen lagern, / die gierig auf Menschen sind. Ihre Zähne sind Spieße und Pfeile, / ein scharfes Schwert ihre Zunge.", footnotes: ["17,12; 55,22; 59,8"] },
      { number: 7, text: "Sie haben meinen Schritten ein Netz gelegt / und meine Seele gebeugt. Sie haben mir eine Grube gegraben; / doch fielen sie selbst hinein. [Sela]", footnotes: ["140,6; 7,16", "sie haben meine Seele gebeugt: Text korr. nach G; H hat die Einzahl: er hat . . . gebeugt."] },
      { number: 6, text: "Erheb dich über die Himmel, o Gott! / Deine Herrlichkeit erscheine über der ganzen Erde." },
      { number: 8, text: "Mein Herz ist bereit, o Gott, / mein Herz ist bereit, / ich will dir singen und spielen.", footnotes: ["7,18; (8-12) 108,2-6"] },
      { number: 9, text: "Wach auf, meine Seele! / Wacht auf, Harfe und Saitenspiel! / Ich will das Morgenrot wecken.", footnotes: ["Ijob 38,12"] },
      { number: 10, text: "Ich will dich vor den Völkern preisen, Herr, / dir vor den Nationen lobsingen.", footnotes: ["7,18; 108,4"] },
      { number: 11, text: "Denn deine Güte reicht, so weit der Himmel ist, / deine Treue, so weit die Wolken ziehn.", footnotes: ["36,6"] },
      { number: 12, text: "Erheb dich über die Himmel, o Gott; / deine Herrlichkeit erscheine über der ganzen Erde." },
    ] },
    { number: 58, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Zerstöre nicht!». Ein Lied Davids.]", heading: "Gott, der gerechte Richter" },
      { number: 2, text: "Sprecht ihr wirklich Recht, ihr Mächtigen? / Richtet ihr die Menschen gerecht?", footnotes: ["Sinn nicht ganz klar."] },
      { number: 3, text: "Nein, ihr schaltet im Land nach Willkür, / euer Herz ist voll Bosheit; / eure Hände bahnen dem Unrecht den Weg.", footnotes: ["Mi 2,1"] },
      { number: 4, text: "Vom Mutterschoß an sind die Frevler treulos, / von Geburt an irren sie vom Weg ab und lügen." },
      { number: 5, text: "Ihr Gift ist wie das Gift der Schlange, / wie das Gift der tauben Natter, die ihr Ohr verschließt,", footnotes: ["140,4; Dtn 32,33"] },
      { number: 6, text: "die nicht auf die Stimme des Beschwörers hört, / der sich auf Zaubersprüche versteht." },
      { number: 7, text: "O Gott, zerbrich ihnen die Zähne im Mund! / Zerschlage, Herr, das Gebiss der Löwen!", footnotes: ["3,8; 17,12"] },
      { number: 8, text: "Sie sollen vergehen wie verrinnendes Wasser, / wie Gras, das verwelkt auf dem Weg,", footnotes: ["Ijob 11,16; Weish 16,29; Ps 90,5f", "Text korr."] },
      { number: 9, text: "wie die Schnecke, die sich auflöst in Schleim; / wie eine Fehlgeburt sollen sie die Sonne nicht schauen.", footnotes: ["Ijob 3,16"] },
      { number: 10, text: "Ehe eure Töpfe das Feuer des Dornstrauchs spüren, / fege Gott die Feinde hinweg, ob frisch, ob verdorrt.", footnotes: ["Ijob 27,21; Nah 1,10", "H ist unverständlich; «die Feinde» ist ergänzt."] },
      { number: 11, text: "Wenn er die Vergeltung sieht, freut sich der Gerechte; / er badet seine Füße im Blut des Frevlers.", footnotes: ["52,8; 68,24"] },
      { number: 12, text: "Dann sagen die Menschen: «Der Gerechte erhält seinen Lohn; / es gibt einen Gott, der auf Erden Gericht hält.»", footnotes: ["Ijob 19,29; Mal 3,18"] },
    ] },
    { number: 59, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Zerstöre nicht!». Ein Lied Davids, als Saul hinschickte und man das Haus bewachte, um ihn zu töten.]", heading: "Klage und Zuversicht eines Verfolgten", footnotes: ["1 Sam 19,11"] },
      { number: 2, text: "Entreiß mich den Feinden, mein Gott, / beschütze mich vor meinen Gegnern!" },
      { number: 3, text: "Entreiß mich denen, die Unrecht tun, / rette mich vor den Mördern!" },
      { number: 4, text: "Sieh her: Sie lauern mir auf, / Mächtige stellen mir nach. Ich aber habe keinen Frevel begangen und keine Sünde; /", footnotes: ["(4-5) 26,1"] },
      { number: 5, text: "Herr, ich bin ohne Schuld. Sie stürmen vor und stellen sich auf. / Wach auf, komm mir entgegen, sieh her!" },
      { number: 6, text: "Herr, du Gott der Heerscharen, Gott Israels, / werde wach, suche alle Völker heim! / Sei keinem treulosen Frevler gnädig! [Sela]" },
      { number: 7, text: "Abend für Abend kommen sie wieder, / sie kläffen wie Hunde, durchstreifen die Stadt." },
      { number: 8, text: "Ja, sie geifern mit ihrem Maul. / Die Schwerter zwischen ihren Lippen, wer nimmt sie wahr?", footnotes: ["52,4; 55,22"] },
      { number: 9, text: "Du aber, Herr, verlachst sie; / du spottest über alle Völker.", footnotes: ["2,4; 37,13; Weish 4,18"] },
      { number: 10, text: "Meine Stärke, an dich will ich mich halten, / denn du, Gott, bist meine Burg.", footnotes: ["9,10f", "Text korr. nach G und der aramäischen Übersetzung. Text korr. nach Hieronymus; H: denn Gott ist meine Burg."] },
      { number: 11, text: "Mein huldreicher Gott kommt mir entgegen; / Gott lässt mich herabsehen auf meine Gegner.", footnotes: ["54,9"] },
      { number: 12, text: "Töte sie nicht, / damit mein Volk nicht vergisst. In deiner Kraft zerstreue sie, / wirf sie nieder, Herr, unser Schild!" },
      { number: 13, text: "Wegen der Sünde ihres Mundes, wegen all ihrer Reden / sollen sie sich in ihrem Hochmut verfangen; / denn sie fluchen und verbreiten nur Lügen.", footnotes: ["Spr 12,13; 18,7"] },
      { number: 14, text: "Vernichte sie im Zorn, / vernichte sie; sie sollen zugrunde gehen. Sie sollen erkennen, dass Gott der Herrscher in Jakob ist / und bis an das Ende der Erde. [Sela]", footnotes: ["83,19"] },
      { number: 15, text: "Abend für Abend kommen sie wieder, / sie kläffen wie Hunde, durchstreifen die Stadt." },
      { number: 16, text: "Sie streunen umher, gierig nach Fraß; / werden sie nicht satt, dann knurren sie.", footnotes: ["Das hebräische Zeitwort ist doppeldeutig: sie verbringen die Nacht, oder: sie knurren."] },
      { number: 17, text: "Ich aber will deine Macht besingen, / will über deine Huld jubeln am Morgen. Denn du bist eine Burg für mich, / bist meine Zuflucht am Tag der Not.", footnotes: ["90,14"] },
      { number: 18, text: "Meine Stärke, dir will ich singen und spielen; / denn du, Gott, bist meine Burg, mein huldreicher Gott." },
    ] },
    { number: 60, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Lilie des Zeugnisses». Ein Lied Davids zum Lehren,", heading: "Bitte um Hilfe nach einer Niederlage" },
      { number: 2, text: "als er mit den Aramäern Mesopotamiens und den Aramäern von Zoba kämpfte und als Joab umkehrte und die Edomiter im Salztal schlug, zwölftausend Mann.]", footnotes: ["2 Sam 8,3.13"] },
      { number: 3, text: "Du hast uns verworfen, o Gott, und zerschlagen. / Du hast uns gezürnt. Richte uns wieder auf!" },
      { number: 4, text: "Erschüttert hast du das Land und gespalten. / Heile seine Risse! Denn es kam ins Wanken." },
      { number: 5, text: "Du hast dein Volk hart geprüft, / du gabst uns betäubenden Wein zu trinken.", footnotes: ["75,9; Jes 51,17"] },
      { number: 6, text: "Für alle, die dich fürchten, hast du ein Zeichen aufgestellt, / zu dem sie fliehen können vor dem Bogen. [Sela]", footnotes: ["dem Bogen: Text durch Änderung der Vokale korr. nach G und S; H: der Wahrheit."] },
      { number: 7, text: "Hilf mit deiner Rechten, erhöre uns, / damit die gerettet werden, die du so sehr liebst.", footnotes: ["(7-14) 108,7-14"] },
      { number: 8, text: "Gott hat in seinem Heiligtum gesprochen: / «Ich will triumphieren, will Sichem verteilen / und das Tal von Sukkot vermessen." },
      { number: 9, text: "Mein ist Gilead, mein auch Manasse, / Efraim ist der Helm auf meinem Haupt, / Juda mein Herrscherstab.", footnotes: ["Gen 49,10"] },
      { number: 10, text: "Doch Moab ist mein Waschbecken, / auf Edom werfe ich meinen Schuh, / ich triumphiere über das Land der Philister.»", footnotes: ["Jes 11,14", "Text korr.; H: über mich jauchze (vgl. 108,10)."] },
      { number: 11, text: "Wer führt mich hin zu der befestigten Stadt, / wer wird mich nach Edom geleiten?" },
      { number: 12, text: "Gott, hast denn du uns verworfen? / Du ziehst ja nicht aus, o Gott, mit unseren Heeren.", footnotes: ["44,10"] },
      { number: 13, text: "Bring uns doch Hilfe im Kampf mit dem Feind! / Denn die Hilfe von Menschen ist nutzlos." },
      { number: 14, text: "Mit Gott werden wir Großes vollbringen; / er selbst wird unsere Feinde zertreten.", footnotes: ["44,6"] },
    ] },
    { number: 61, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Von David.]", heading: "Fürbitte für den König" },
      { number: 2, text: "Gott, höre mein Flehen, / achte auf mein Beten!" },
      { number: 3, text: "Vom Ende der Erde rufe ich zu dir; / denn mein Herz ist verzagt. / Führe mich auf den Felsen, der mir zu hoch ist!", footnotes: ["27,4f"] },
      { number: 4, text: "Du bist meine Zuflucht, / ein fester Turm gegen die Feinde.", footnotes: ["71,3; Spr 18,10"] },
      { number: 5, text: "In deinem Zelt möchte ich Gast sein auf ewig, / mich bergen im Schutz deiner Flügel. [Sela]", footnotes: ["17,7f"] },
      { number: 6, text: "Denn du, o Gott, hast meine Gelübde gehört / und denen das Erbe gegeben, / die deinen Namen fürchten." },
      { number: 7, text: "Füge den Tagen des Königs noch viele hinzu! / Seine Jahre mögen dauern / von Geschlecht zu Geschlecht.", footnotes: ["21,5; 72,5"] },
      { number: 8, text: "Er throne ewig vor Gottes Angesicht. / Huld und Treue mögen ihn behüten.", footnotes: ["89,5.30.34.37", "Text korr."] },
      { number: 9, text: "Dann will ich allzeit deinem Namen singen und spielen / und Tag für Tag meine Gelübde erfüllen.", footnotes: ["7,18; 66,13"] },
    ] },
    { number: 62, verses: [
      { number: 1, text: "[Für den Chormeister. Nach Jedutun. Ein Psalm Davids.]", heading: "Vertrauen auf Gottes Macht und Huld" },
      { number: 2, text: "Bei Gott allein kommt meine Seele zur Ruhe, / von ihm kommt mir Hilfe.", footnotes: ["37,7; Jes 30,15"] },
      { number: 3, text: "Nur er ist mein Fels, meine Hilfe, meine Burg; / darum werde ich nicht wanken.", footnotes: ["Text korr. (vgl. V. 7b)."] },
      { number: 4, text: "Wie lange rennt ihr an gegen einen Einzigen, / stürmt alle heran wie gegen eine fallende Wand, / wie gegen eine Mauer, die einstürzt?" },
      { number: 5, text: "Ja, sie planen, ihn von seiner Höhe zu stürzen; / Lügen ist ihre Lust. Sie segnen mit ihrem Mund, / doch in ihrem Herzen fluchen sie. [Sela]", footnotes: ["28,3"] },
      { number: 6, text: "Bei Gott allein kommt meine Seele zur Ruhe; / denn von ihm kommt meine Hoffnung.", footnotes: ["42,6; Mi 7,7"] },
      { number: 7, text: "Nur er ist mein Fels, meine Hilfe, meine Burg; / darum werde ich nicht wanken." },
      { number: 8, text: "Bei Gott ist mein Heil, meine Ehre; / Gott ist mein schützender Fels, meine Zuflucht.", footnotes: ["3,4; 92,16", "Gott, wörtlich: bei Gott."] },
      { number: 9, text: "Vertrau ihm, Volk (Gottes), zu jeder Zeit! / Schüttet euer Herz vor ihm aus! / Denn Gott ist unsere Zuflucht. [Sela]", footnotes: ["Jes 26,4"] },
      { number: 10, text: "Nur ein Hauch sind die Menschen, / die Leute nur Lug und Trug. Auf der Waage schnellen sie empor, / leichter als ein Hauch sind sie alle.", footnotes: ["39,6f; Jes 40,15"] },
      { number: 11, text: "Vertraut nicht auf Gewalt, / verlasst euch nicht auf Raub! Wenn der Reichtum auch wächst, / so verliert doch nicht euer Herz an ihn!", footnotes: ["Mt 19,22; Lk 12,15-21; 1 Tim 6,17"] },
      { number: 12, text: "Eines hat Gott gesagt, / zweierlei habe ich gehört: Bei Gott ist die Macht; /", footnotes: ["Ijob 40,5"] },
      { number: 13, text: "Herr, bei dir ist die Huld. Denn du wirst jedem vergelten, / wie es seine Taten verdienen.", footnotes: ["28,4; 31,24; 91,8; 94,2.23; 2 Sam 3,39; Ijob 34,11; Klgl 3,64; Jer 50,29; Röm 2,6; 2 Tim 4,14"] },
    ] },
    { number: 63, verses: [
      { number: 1, text: "[Ein Psalm Davids, als er in der Wüste Juda war.]", heading: "Sehnsucht nach Gott", footnotes: ["1 Sam 23,14"] },
      { number: 2, text: "Gott, du mein Gott, dich suche ich, / meine Seele dürstet nach dir. Nach dir schmachtet mein Leib / wie dürres, lechzendes Land ohne Wasser.", footnotes: ["42,2f"] },
      { number: 3, text: "Darum halte ich Ausschau nach dir im Heiligtum, / um deine Macht und Herrlichkeit zu sehen.", footnotes: ["26,8"] },
      { number: 4, text: "Denn deine Huld ist besser als das Leben; / darum preisen dich meine Lippen." },
      { number: 5, text: "Ich will dich rühmen mein Leben lang, / in deinem Namen die Hände erheben." },
      { number: 6, text: "Wie an Fett und Mark wird satt meine Seele, / mit jubelnden Lippen soll mein Mund dich preisen.", footnotes: ["23,5; 43,4"] },
      { number: 7, text: "Ich denke an dich auf nächtlichem Lager / und sinne über dich nach, wenn ich wache.", footnotes: ["119,148; Jes 26,9"] },
      { number: 8, text: "Ja, du wurdest meine Hilfe; / jubeln kann ich im Schatten deiner Flügel.", footnotes: ["17,7f"] },
      { number: 9, text: "Meine Seele hängt an dir, / deine rechte Hand hält mich fest." },
      { number: 10, text: "Viele trachten mir ohne Grund nach dem Leben, / aber sie müssen hinabfahren in die Tiefen der Erde.", footnotes: ["ohne Grund: Text korr.; H: zum Verderben."] },
      { number: 11, text: "Man gibt sie der Gewalt des Schwertes preis, / sie werden eine Beute der Schakale." },
      { number: 12, text: "Der König aber freue sich an Gott. / Wer bei ihm schwört, darf sich rühmen. / Doch allen Lügnern wird der Mund verschlossen.", footnotes: ["21,2; 107,42"] },
    ] },
    { number: 64, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Bitte um Schutz vor den Feinden" },
      { number: 2, text: "Höre, o Gott, mein lautes Klagen, / schütze mein Leben vor dem Schrecken des Feindes!" },
      { number: 3, text: "Verbirg mich vor der Schar der Bösen, / vor dem Toben derer, die Unrecht tun.", footnotes: ["Andere Übersetzungsmöglichkeit: vor der Verschwörung derer, . . ."] },
      { number: 4, text: "Sie schärfen ihre Zunge wie ein Schwert, / schießen giftige Worte wie Pfeile,", footnotes: ["55,22"] },
      { number: 5, text: "um den Schuldlosen von ihrem Versteck aus zu treffen. / Sie schießen auf ihn, plötzlich und ohne Scheu.", footnotes: ["11,2"] },
      { number: 6, text: "Sie sind fest entschlossen zu bösem Tun. / Sie planen, Fallen zu stellen, / und sagen: «Wer sieht uns schon?»", footnotes: ["Spr 1,11f; Ps 10,11", "Text korr."] },
      { number: 7, text: "Sie haben Bosheit im Sinn, / doch halten sie ihre Pläne geheim. Ihr Inneres ist heillos verdorben, / ihr Herz ist ein Abgrund.", footnotes: ["Text korr."] },
      { number: 8, text: "Da trifft sie Gott mit seinem Pfeil; / sie werden jählings verwundet." },
      { number: 9, text: "Ihre eigene Zunge bringt sie zu Fall. / Alle, die es sehen, schütteln den Kopf.", footnotes: ["Text korr."] },
      { number: 10, text: "Dann fürchten sich alle Menschen; / sie verkünden Gottes Taten / und bedenken sein Wirken." },
      { number: 11, text: "Der Gerechte freut sich am Herrn und sucht bei ihm Zuflucht. / Und es rühmen sich alle Menschen mit redlichem Herzen." },
    ] },
    { number: 65, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids. Ein Lied.]", heading: "Dank für Gottes Gaben" },
      { number: 2, text: "Dir gebührt Lobgesang, Gott, auf dem Zion, / dir erfüllt man Gelübde.", footnotes: ["Text korr."] },
      { number: 3, text: "Du erhörst die Gebete. / Alle Menschen kommen zu dir" },
      { number: 4, text: "unter der Last ihrer Sünden. Unsere Schuld ist zu groß für uns, / du wirst sie vergeben.", footnotes: ["32,1", "für uns: Text korr.; H: für mich."] },
      { number: 5, text: "Wohl denen, die du erwählst und in deine Nähe holst, / die in den Vorhöfen deines Heiligtums wohnen. Wir wollen uns am Gut deines Hauses sättigen, / am Gut deines Tempels.", footnotes: ["23,6; 36,9"] },
      { number: 6, text: "Du vollbringst erstaunliche Taten, / erhörst uns in Treue, du Gott unsres Heiles, du Zuversicht aller Enden der Erde / und der fernsten Gestade.", footnotes: ["Text korr."] },
      { number: 7, text: "Du gründest die Berge in deiner Kraft, / du gürtest dich mit Stärke." },
      { number: 8, text: "Du stillst das Brausen der Meere, / das Brausen ihrer Wogen, das Tosen der Völker.", footnotes: ["89,10; Ijob 38,11; Jes 17,12"] },
      { number: 9, text: "Alle, die an den Enden der Erde wohnen, / erschauern vor deinen Zeichen; / Ost und West erfüllst du mit Jubel." },
      { number: 10, text: "Du sorgst für das Land und tränkst es; / du überschüttest es mit Reichtum. Der Bach Gottes ist reichlich gefüllt, / du schaffst ihnen Korn; so ordnest du alles.", footnotes: ["(10-11) 104,10-15; Joël 2,22-24", "reichlich, wörtlich: (Der Bach Gottes ist) mit Wasser (gefüllt)."] },
      { number: 11, text: "Du tränkst die Furchen, ebnest die Schollen, / machst sie weich durch Regen, segnest ihre Gewächse." },
      { number: 12, text: "Du krönst das Jahr mit deiner Güte, / deinen Spuren folgt Überfluss.", footnotes: ["Wörtlich: deine Spuren triefen von Fett."] },
      { number: 13, text: "In der Steppe prangen die Auen, / die Höhen umgürten sich mit Jubel.", footnotes: ["96,12"] },
      { number: 14, text: "Die Weiden schmücken sich mit Herden, / die Täler hüllen sich in Korn. / Sie jauchzen und singen." },
    ] },
    { number: 66, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Lied. Ein Psalm.] Jauchzt vor Gott, alle Länder der Erde! /", heading: "Aufruf zum Lobpreis", footnotes: ["98,4; 100,1"] },
      { number: 2, text: "Spielt zum Ruhm seines Namens! / Verherrlicht ihn mit Lobpreis!" },
      { number: 3, text: "Sagt zu Gott: «Wie Ehrfurcht gebietend sind deine Taten; / vor deiner gewaltigen Macht müssen die Feinde sich beugen.»" },
      { number: 4, text: "Alle Welt bete dich an und singe dein Lob, / sie lobsinge deinem Namen! [Sela]" },
      { number: 5, text: "Kommt und seht die Taten Gottes! / Staunenswert ist sein Tun an den Menschen:", footnotes: ["46,9"] },
      { number: 6, text: "Er verwandelte das Meer in trockenes Land, / sie schritten zu Fuß durch den Strom; / dort waren wir über ihn voll Freude.", footnotes: ["Ex 14,21f; Jos 3,16f"] },
      { number: 7, text: "In seiner Kraft ist er Herrscher auf ewig; / seine Augen prüfen die Völker. / Die Trotzigen können sich gegen ihn nicht erheben. [Sela]" },
      { number: 8, text: "Preist unsern Gott, ihr Völker; / lasst laut sein Lob erschallen!" },
      { number: 9, text: "Er erhielt uns am Leben / und ließ unseren Fuß nicht wanken.", footnotes: ["121,3"] },
      { number: 10, text: "Du hast, o Gott, uns geprüft / und uns geläutert, wie man Silber läutert.", footnotes: ["Jes 48,10; Spr 17,3"] },
      { number: 11, text: "Du brachtest uns in schwere Bedrängnis / und legtest uns eine drückende Last auf die Schulter.", footnotes: ["auf die Schulter, wörtlich: um unsere Hüften."] },
      { number: 12, text: "Du ließest Menschen über unsere Köpfe schreiten. / Wir gingen durch Feuer und Wasser. / Doch du hast uns in die Freiheit hinausgeführt.", footnotes: ["Jes 43,2", "Text korr."] },
      { number: 13, text: "Ich komme mit Opfern in dein Haus; / ich erfülle dir meine Gelübde,", footnotes: ["(13-14) 22,26; 50,14; 61,9; 76,12; 116,14.18; Num 30,3; Jona 2,10"] },
      { number: 14, text: "die ich einst dir versprach, / die dir mein Mund in der Not gelobte." },
      { number: 15, text: "Fette Tiere bringe ich dir als Brandopfer dar, / zusammen mit dem Rauch von Widdern; / ich richte dir Rinder und Böcke zu. [Sela]", footnotes: ["Nach G."] },
      { number: 16, text: "Ihr alle, die ihr Gott fürchtet, kommt und hört; / ich will euch erzählen, was er mir Gutes getan hat." },
      { number: 17, text: "Zu ihm hatte ich mit lauter Stimme gerufen / und schon konnte mein Mund ihn preisen.", footnotes: ["Wörtlich: Rühmen war unter meiner Zunge."] },
      { number: 18, text: "Hätte ich Böses im Sinn gehabt, / dann hätte der Herr mich nicht erhört.", footnotes: ["(18-19) Spr 28,9; Joh 9,31"] },
      { number: 19, text: "Gott aber hat mich erhört, / hat auf mein drängendes Beten geachtet." },
      { number: 20, text: "Gepriesen sei Gott; denn er hat mein Gebet nicht verworfen / und mir seine Huld nicht entzogen." },
    ] },
    { number: 67, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Ein Psalm. Ein Lied.]", heading: "Dank für den Segen Gottes" },
      { number: 2, text: "Gott sei uns gnädig und segne uns. / Er lasse über uns sein Angesicht leuchten, [Sela]", footnotes: ["4,7"] },
      { number: 3, text: "damit auf Erden sein Weg erkannt wird / und unter allen Völkern sein Heil.", footnotes: ["sein Weg: Text korr.; H: dein Weg."] },
      { number: 4, text: "Die Völker sollen dir danken, o Gott, / danken sollen dir die Völker alle." },
      { number: 5, text: "Die Nationen sollen sich freuen und jubeln. / Denn du richtest den Erdkreis gerecht. Du richtest die Völker nach Recht / und regierst die Nationen auf Erden. [Sela]", footnotes: ["98,9", "Mit G ergänzt."] },
      { number: 6, text: "Die Völker sollen dir danken, o Gott, / danken sollen dir die Völker alle." },
      { number: 7, text: "Das Land gab seinen Ertrag. / Es segne uns Gott, unser Gott.", footnotes: ["85,13"] },
      { number: 8, text: "Es segne uns Gott. / Alle Welt fürchte und ehre ihn.", footnotes: ["Alle Welt, wörtlich: Alle Enden der Erde."] },
    ] },
    { number: 68, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids. Ein Lied.]", heading: "Ein Lied auf Gottes Sieg und Herrschaft" },
      { number: 2, text: "Gott steht auf, seine Feinde zerstieben; / die ihn hassen, fliehen vor seinem Angesicht.", footnotes: ["Num 10,35; Jes 33,3"] },
      { number: 3, text: "Sie verfliegen, wie Rauch verfliegt; / wie Wachs am Feuer zerfließt, / so vergehen die Frevler vor Gottes Angesicht.", footnotes: ["92,10", "Sie verfliegen, wie Rauch verfliegt: Text korr.; H: Wie Rauch verweht wird, den du auseinander wehst."] },
      { number: 4, text: "Die Gerechten aber freuen sich und jubeln vor Gott; / sie jauchzen in heller Freude." },
      { number: 5, text: "Singt für Gott, spielt seinem Namen; / jubelt ihm zu, ihm, der auf den Wolken einherfährt! Preist seinen Namen! / Freut euch vor seinem Angesicht!", footnotes: ["7,18; 18,11; Dtn 33,26; Jes 19,1", "Text korr."] },
      { number: 6, text: "Ein Vater der Waisen, ein Anwalt der Witwen / ist Gott in seiner heiligen Wohnung.", footnotes: ["10,18"] },
      { number: 7, text: "Gott bringt die Verlassenen heim, / führt die Gefangenen hinaus in das Glück; / doch die Empörer müssen wohnen im dürren Land.", footnotes: ["146,7"] },
      { number: 8, text: "Gott, als du deinem Volk voranzogst, / als du die Wüste durchschrittest, [Sela] /", footnotes: ["Ex 13,21; Dtn 33,2"] },
      { number: 9, text: "da bebte die Erde, da ergossen sich die Himmel vor Gott, / vor Gott, dem Herrn vom Sinai, vor Israels Gott.", footnotes: ["18,8; 77,19; 97,4; 99,1; 104,32; 114,6f; Ex 19,18; Ri 5,4f; Sir 16,18f; Jes 63,19; Hab 3,6"] },
      { number: 10, text: "Gott, du ließest Regen strömen in Fülle / und erquicktest dein verschmachtendes Erbland." },
      { number: 11, text: "Deine Geschöpfe finden dort Wohnung; / Gott, in deiner Güte versorgst du den Armen." },
      { number: 12, text: "Der Herr entsendet sein Wort; / groß ist der Siegesbotinnen Schar.", footnotes: ["Jes 52,7"] },
      { number: 13, text: "Die Könige der Heere fliehen, sie fliehen. / Im Haus verteilt man die Beute.", footnotes: ["Text korr."] },
      { number: 14, text: "Was bleibt ihr zurück in den Hürden? / Du Taube mit silbernen Schwingen, mit goldenem Flügel!", footnotes: ["Ri 5,16.23"] },
      { number: 15, text: "Als der Allmächtige die Könige vertrieb, / fiel Schnee auf dem Zalmon.", footnotes: ["Ri 9,48"] },
      { number: 16, text: "Ein Gottesberg ist der Baschanberg, / ein Gebirge, an Gipfeln reich, ist der Baschan." },
      { number: 17, text: "Warum blickt ihr voll Neid, ihr hohen Gipfel, / auf den Berg, den Gott sich zum Wohnsitz erwählt hat? / Dort wird der Herr wohnen in Ewigkeit.", footnotes: ["132,13; Ez 43,7"] },
      { number: 18, text: "Die Wagen Gottes sind zahllos, tausendmal tausend. / Vom Sinai zieht der Herr zu seinem Heiligtum.", footnotes: ["2 Kön 6,17", "Text korr.; H: Der Herr ist unter ihnen, (der vom) Sinai (ist) im Heiligtum."] },
      { number: 19, text: "Du zogst hinauf zur Höhe, / führtest Gefangene mit; du nahmst Gaben entgegen von den Menschen. / Auch Empörer müssen wohnen bei Gott, dem Herrn.", footnotes: ["47,6; Eph 4,8-10"] },
      { number: 20, text: "Gepriesen sei der Herr, Tag für Tag! / Gott trägt uns, er ist unsre Hilfe. [Sela]", footnotes: ["145,2; Jes 46,3f; 63,9"] },
      { number: 21, text: "Gott ist ein Gott, der uns Rettung bringt, / Gott, der Herr, führt uns heraus aus dem Tod.", footnotes: ["9,14"] },
      { number: 22, text: "Denn Gott zerschmettert das Haupt seiner Feinde, / den Kopf des Frevlers, der in Sünde dahinlebt." },
      { number: 23, text: "Der Herr hat gesprochen: «Ich bringe (sie) vom Baschan zurück, / ich bringe (sie) zurück aus den Tiefen des Meeres." },
      { number: 24, text: "Dein Fuß wird baden im Blut, / die Zunge deiner Hunde ihren Anteil bekommen an den Feinden.»", footnotes: ["58,11; 1 Kön 21,19; 22,38", "Text korr. nach G, S und der aramäischen Übersetzung; H: damit du (Feinde) zerschmetterst, wobei dein Fuß im Blut (watet)."] },
      { number: 25, text: "Gott, sie sahen deinen Einzug, / den Einzug meines Gottes und Königs ins Heiligtum:", footnotes: ["24,7; 2 Sam 6,13-15"] },
      { number: 26, text: "voraus die Sänger, die Saitenspieler danach, / dazwischen Mädchen mit kleinen Pauken.", footnotes: ["149,3; 2 Sam 6,5"] },
      { number: 27, text: "Versammelt euch und preist unsern Gott, / den Herrn in der Gemeinde Israels:", footnotes: ["in der Gemeinde: Text korr.; H: ihr aus dem Brunnen Israels."] },
      { number: 28, text: "voran der kleine Stamm Benjamin, / im Zug die Fürsten von Juda, / die Fürsten von Sebulon, die Fürsten von Naftali.", footnotes: ["Ri 20,43-48; Jes 8,23", "im Zug, wörtlich: im Lärmen der Menge."] },
      { number: 29, text: "Biete auf, o Gott, deine Macht, / die Gottesmacht, die du an uns erwiesen hast /", footnotes: ["80,3", "o Gott: Text korr.; H: dein Gott."] },
      { number: 30, text: "von deinem Tempel aus, hoch über Jerusalem." },
      { number: 31, text: "Wehr ab das Untier im Schilf, die Rotte der Starken, / wehr ab die Herrscher der Völker! Sie sind gierig nach Silber, tritt sie nieder; / zerstreue die Völker, die Lust haben am Krieg.", footnotes: ["Ez 29,2f", "Text korr."] },
      { number: 30, text: "Könige kommen mit Gaben, /" },
      { number: 32, text: "aus Ägypten bringt man Geräte von Erz, / Kusch erhebt zu Gott seine Hände.", footnotes: ["72,10; Jes 18,7; 45,14; Apg 8,27"] },
      { number: 33, text: "Ihr Königreiche der Erde, singt für Gott, / singt und spielt für den Herrn, [Sela]", footnotes: ["7,18"] },
      { number: 34, text: "der dahinfährt über den Himmel, den uralten Himmel, / der seine Stimme erhebt, seine machtvolle Stimme." },
      { number: 35, text: "Preist Gottes Macht! / Über Israel ragt seine Hoheit empor, / seine Macht ragt bis zu den Wolken." },
      { number: 36, text: "Gott in seinem Heiligtum ist voll Majestät, Israels Gott; / seinem Volk verleiht er Stärke und Kraft. Gepriesen sei Gott.", footnotes: ["in seinem Heiligtum: Text korr.; H: aus deinem Heiligtum."] },
    ] },
    { number: 69, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Lilien». Von David.]", heading: "Der Hilferuf eines unschuldig Verfolgten" },
      { number: 2, text: "Hilf mir, o Gott! / Schon reicht mir das Wasser bis an die Kehle.", footnotes: ["(2-3) 42,8"] },
      { number: 3, text: "Ich bin in tiefem Schlamm versunken / und habe keinen Halt mehr; ich geriet in tiefes Wasser, / die Strömung reißt mich fort.", footnotes: ["40,3"] },
      { number: 4, text: "Ich bin müde vom Rufen, / meine Kehle ist heiser, mir versagen die Augen, / während ich warte auf meinen Gott.", footnotes: ["Jes 38,14"] },
      { number: 5, text: "Zahlreicher als die Haare auf meinem Kopf / sind die, die mich grundlos hassen. Zahlreich sind meine Verderber, meine verlogenen Feinde. / Was ich nicht geraubt habe, soll ich erstatten.", footnotes: ["35,19; Joh 15,25"] },
      { number: 6, text: "Gott, du kennst meine Torheit, / meine Verfehlungen sind dir nicht verborgen." },
      { number: 7, text: "Wer auf dich hofft, Herr, du Herr der Heere, / soll durch mich nicht scheitern; wer dich sucht, Gott Israels, / gerate durch mich nicht in Schande." },
      { number: 8, text: "Denn deinetwegen erleide ich Schmach / und Schande bedeckt mein Gesicht.", footnotes: ["44,23; Jer 15,15"] },
      { number: 9, text: "Entfremdet bin ich den eigenen Brüdern, / den Söhnen meiner Mutter wurde ich fremd.", footnotes: ["31,12"] },
      { number: 10, text: "Denn der Eifer für dein Haus hat mich verzehrt; / die Schmähungen derer, die dich schmähen, haben mich getroffen.", footnotes: ["119,139; Joh 2,17; Röm 15,3"] },
      { number: 11, text: "Ich nahm mich durch Fasten in Zucht, / doch es brachte mir Schmach und Schande.", footnotes: ["Text korr.; H: Ich weinte beim Fasten meiner Seele."] },
      { number: 12, text: "Ich ging in Sack und Asche, / doch sie riefen Spottverse hinter mir her.", footnotes: ["Klgl 3,14"] },
      { number: 13, text: "Man redet über mich in der Versammlung am Tor, / von mir singen die Zecher beim Wein.", footnotes: ["Ijob 30,9"] },
      { number: 14, text: "Ich aber bete zu dir, / Herr, zur Zeit der Gnade. Erhöre mich in deiner großen Huld, / Gott, hilf mir in deiner Treue!", footnotes: ["102,14; Jes 49,8"] },
      { number: 15, text: "Entreiß mich dem Sumpf, / damit ich nicht versinke. Zieh mich heraus aus dem Verderben, / aus dem tiefen Wasser!", footnotes: ["40,3", "Text korr.; H: Lass mich gerettet werden vor denen, die mich hassen."] },
      { number: 16, text: "Lass nicht zu, dass die Flut mich überschwemmt, / die Tiefe mich verschlingt, / der Brunnenschacht über mir seinen Rachen schließt.", footnotes: ["42,8; Gen 29,3"] },
      { number: 17, text: "Erhöre mich, Herr, in deiner Huld und Güte, / wende dich mir zu in deinem großen Erbarmen!" },
      { number: 18, text: "Verbirg nicht dein Gesicht vor deinem Knecht; / denn mir ist angst. Erhöre mich bald!", footnotes: ["102,3; 143,7"] },
      { number: 19, text: "Sei mir nah und erlöse mich! / Befrei mich meinen Feinden zum Trotz!" },
      { number: 20, text: "Du kennst meine Schmach und meine Schande. / Dir stehen meine Widersacher alle vor Augen." },
      { number: 21, text: "Die Schande bricht mir das Herz, / ganz krank bin ich vor Schmach; umsonst habe ich auf Mitleid gewartet, / auf einen Tröster, doch ich habe keinen gefunden.", footnotes: ["Klgl 1,2"] },
      { number: 22, text: "Sie gaben mir Gift zu essen, / für den Durst reichten sie mir Essig.", footnotes: ["Klgl 3,15; Mt 27,34.48"] },
      { number: 23, text: "Der Opfertisch werde für sie zur Falle, / das Opfermahl zum Fangnetz.", footnotes: ["(23-24) Röm 11,9f", "Nach der aramäischen Übersetzung."] },
      { number: 24, text: "Blende ihre Augen, sodass sie nicht mehr sehen; / lähme ihre Hüften für immer!" },
      { number: 25, text: "Gieß über sie deinen Grimm aus, / dein glühender Zorn soll sie treffen!" },
      { number: 26, text: "Ihr Lagerplatz soll veröden, / in ihren Zelten soll niemand mehr wohnen.", footnotes: ["Apg 1,20"] },
      { number: 27, text: "Denn sie verfolgen den Mann, den du schon geschlagen hast, / und mehren den Schmerz dessen, der von dir getroffen ist.", footnotes: ["71,11; Jes 53,4", "Text korr. nach G; H: und sie zählen den Schmerz."] },
      { number: 28, text: "Rechne ihnen Schuld über Schuld an, / damit sie nicht teilhaben an deiner Gerechtigkeit." },
      { number: 29, text: "Sie seien aus dem Buch des Lebens getilgt / und nicht bei den Gerechten verzeichnet.", footnotes: ["139,16; Ex 32,32; Jes 4,3; Dan 7,10; 12,1; Mal 3,16; Lk 10,20; Phil 4,3; Offb 3,5; 20,12"] },
      { number: 30, text: "Ich aber bin elend und voller Schmerzen; / doch deine Hilfe, o Gott, wird mich erhöhen." },
      { number: 31, text: "Ich will den Namen Gottes rühmen im Lied, / in meinem Danklied ihn preisen." },
      { number: 32, text: "Das gefällt dem Herrn mehr als ein Opferstier, / mehr als Rinder mit Hörnern und Klauen.", footnotes: ["40,7"] },
      { number: 33, text: "Schaut her, ihr Gebeugten, und freut euch; / ihr, die ihr Gott sucht: euer Herz lebe auf!", footnotes: ["22,27"] },
      { number: 34, text: "Denn der Herr hört auf die Armen, / er verachtet die Gefangenen nicht." },
      { number: 35, text: "Himmel und Erde sollen ihn rühmen, / die Meere und was sich in ihnen regt." },
      { number: 36, text: "Denn Gott wird Zion retten, / wird Judas Städte neu erbauen. Seine Knechte werden dort wohnen und das Land besitzen, /", footnotes: ["51,20; Jes 44,26; Ez 36,10"] },
      { number: 37, text: "ihre Nachkommen sollen es erben; / wer seinen Namen liebt, soll darin wohnen.", footnotes: ["Jes 65,9"] },
    ] },
    { number: 70, verses: [
      { number: 1, text: "[Für den Chormeister. Von David. Zum Weihrauchopfer.]", heading: "Bitte um Gottes Hilfe", footnotes: ["(1-6) 40,14-18"] },
      { number: 2, text: "Gott, komm herbei, um mich zu retten, / Herr, eil mir zu Hilfe!" },
      { number: 3, text: "In Schmach und Schande sollen alle fallen, / die mir nach dem Leben trachten. Zurückweichen sollen sie und vor Scham erröten, / die sich über mein Unglück freuen." },
      { number: 4, text: "Beschämt sollen sich alle abwenden, die lachen und höhnen / und sagen: «Dir geschieht recht.»" },
      { number: 5, text: "Alle, die dich suchen, frohlocken; / sie mögen sich freuen in dir. Die dein Heil lieben, sollen immer sagen: / «Groß ist Gott, der Herr.»", footnotes: ["der Herr: ergänzt mit einigen hebräischen Handschriften (vgl. Ps 40,17)."] },
      { number: 6, text: "Ich aber bin arm und gebeugt. / Eile, o Gott, mir zu Hilfe! Meine Hilfe und mein Retter bist du. / Herr, säume doch nicht!" },
    ] },
    { number: 71, verses: [
      { number: 1, text: "Herr, ich suche Zuflucht bei dir. / Lass mich doch niemals scheitern!", heading: "Gott, die Zuflucht bis ins Alter", footnotes: ["(1-2) 31,2f"] },
      { number: 2, text: "Reiß mich heraus und rette mich in deiner Gerechtigkeit, / wende dein Ohr mir zu und hilf mir!" },
      { number: 3, text: "Sei mir ein sicherer Hort, / zu dem ich allzeit kommen darf. Du hast mir versprochen zu helfen; / denn du bist mein Fels und meine Burg.", footnotes: ["92,16"] },
      { number: 4, text: "Mein Gott, rette mich aus der Hand des Frevlers, / aus der Faust des Bedrückers und Schurken!", footnotes: ["140,2"] },
      { number: 5, text: "Herr, mein Gott, du bist ja meine Zuversicht, / meine Hoffnung von Jugend auf." },
      { number: 6, text: "Vom Mutterleib an stütze ich mich auf dich, / vom Mutterschoß an bist du mein Beschützer; / dir gilt mein Lobpreis allezeit.", footnotes: ["22,11"] },
      { number: 7, text: "Für viele bin ich wie ein Gezeichneter, / du aber bist meine starke Zuflucht.", footnotes: ["31,12; Jes 52,14"] },
      { number: 8, text: "Mein Mund ist erfüllt von deinem Lob, / von deinem Ruhm den ganzen Tag." },
      { number: 9, text: "Verwirf mich nicht, wenn ich alt bin, / verlass mich nicht, wenn meine Kräfte schwinden.", footnotes: ["Jes 46,4"] },
      { number: 10, text: "Denn meine Feinde reden schlecht von mir, / die auf mich lauern, beraten gemeinsam;" },
      { number: 11, text: "sie sagen: «Gott hat ihn verlassen. / Verfolgt und ergreift ihn! / Für ihn gibt es keinen Retter.»", footnotes: ["3,3; 22,9; 69,27"] },
      { number: 12, text: "Gott, bleib doch nicht fern von mir! / Mein Gott, eil mir zu Hilfe!", footnotes: ["22,12"] },
      { number: 13, text: "Alle, die mich bekämpfen, / sollen scheitern und untergehn; über sie komme Schmach und Schande, / weil sie mein Unglück suchen.", footnotes: ["35,4", "Wörtlich: sie sollen sich kleiden in Schmach und Schande."] },
      { number: 14, text: "Ich aber will jederzeit hoffen, / all deinen Ruhm noch mehren." },
      { number: 15, text: "Mein Mund soll von deiner Gerechtigkeit künden / und von deinen Wohltaten sprechen den ganzen Tag; / denn ich kann sie nicht zählen.", footnotes: ["35,28"] },
      { number: 16, text: "Ich will kommen in den Tempel Gottes, des Herrn, / deine großen und gerechten Taten allein will ich rühmen.", footnotes: ["Andere Übersetzungsmöglichkeit: Ich will kommen mit den Großtaten Gottes, des Herrn."] },
      { number: 17, text: "Gott, du hast mich gelehrt von Jugend auf / und noch heute verkünde ich dein wunderbares Walten." },
      { number: 18, text: "Auch wenn ich alt und grau bin, / o Gott, verlass mich nicht, damit ich von deinem machtvollen Arm der Nachwelt künde, / den kommenden Geschlechtern von deiner Stärke /", footnotes: ["22,31f"] },
      { number: 19, text: "und von deiner Gerechtigkeit, Gott, die größer ist als alles. Du hast Großes vollbracht. / Mein Gott, wer ist wie du?", footnotes: ["40,6; 72,18; 86,8"] },
      { number: 20, text: "Du ließest mich viel Angst und Not erfahren. / Belebe mich neu, / führe mich herauf aus den Tiefen der Erde!", footnotes: ["9,14"] },
      { number: 21, text: "Bring mich wieder zu Ehren! / Du wirst mich wiederum trösten." },
      { number: 22, text: "Dann will ich dir danken mit Saitenspiel / und deine Treue preisen; mein Gott, du Heiliger Israels, / ich will dir auf der Harfe spielen.", footnotes: ["Jes 6,3; 43,3"] },
      { number: 23, text: "Meine Lippen sollen jubeln, / denn dir will ich singen und spielen, / meine Seele, die du erlöst hast, soll jubeln.", footnotes: ["7,18"] },
      { number: 24, text: "Auch meine Zunge soll von deiner Gerechtigkeit reden den ganzen Tag. / Denn alle, die mein Unglück suchen, müssen vor Scham erröten und scheitern.", footnotes: ["40,15"] },
    ] },
    { number: 72, verses: [
      { number: 1, text: "[Von Salomo.] Verleih dein Richteramt, o Gott, dem König, / dem Königssohn gib dein gerechtes Walten!", heading: "Der Friedenskönig und sein Reich" },
      { number: 2, text: "Er regiere dein Volk in Gerechtigkeit / und deine Armen durch rechtes Urteil.", footnotes: ["2 Sam 23,3; Spr 31,8f"] },
      { number: 3, text: "Dann tragen die Berge Frieden für das Volk / und die Höhen Gerechtigkeit." },
      { number: 4, text: "Er wird Recht verschaffen den Gebeugten im Volk, / Hilfe bringen den Kindern der Armen, / er wird die Unterdrücker zermalmen.", footnotes: ["10,18"] },
      { number: 5, text: "Er soll leben, solange die Sonne bleibt und der Mond, / bis zu den fernsten Geschlechtern.", footnotes: ["61,7; 89,37f", "Text korr. nach G."] },
      { number: 6, text: "Er ströme wie Regen herab auf die Felder, / wie Regenschauer, die die Erde benetzen.", footnotes: ["benetzen: Text unsicher."] },
      { number: 7, text: "Die Gerechtigkeit blühe auf in seinen Tagen / und großer Friede, bis der Mond nicht mehr da ist.", footnotes: ["H unklar."] },
      { number: 8, text: "Er herrsche von Meer zu Meer, / vom Strom bis an die Enden der Erde.", footnotes: ["Sach 9,10"] },
      { number: 9, text: "Vor ihm sollen seine Gegner sich beugen, / Staub sollen lecken all seine Feinde.", footnotes: ["Jes 49,23; Mi 7,17", "seine Gegner: Text korr.; H: Schiffe, oder: Dämonen; G: Äthiopier; S: Inseln."] },
      { number: 10, text: "Die Könige von Tarschisch und von den Inseln bringen Geschenke, / die Könige von Saba und Seba kommen mit Gaben.", footnotes: ["68,32; 1 Kön 10,1-13; Jes 60,9"] },
      { number: 11, text: "Alle Könige müssen ihm huldigen, / alle Völker ihm dienen.", footnotes: ["2,8; 47,9"] },
      { number: 12, text: "Denn er rettet den Gebeugten, der um Hilfe schreit, / den Armen und den, der keinen Helfer hat.", footnotes: ["(12-13) Ijob 29,12"] },
      { number: 13, text: "Er erbarmt sich des Gebeugten und Schwachen, / er rettet das Leben der Armen." },
      { number: 14, text: "Von Unterdrückung und Gewalttat befreit er sie, / ihr Blut ist in seinen Augen kostbar.", footnotes: ["9,13; 116,15"] },
      { number: 15, text: "Er lebe und Gold von Saba soll man ihm geben! / Man soll für ihn allezeit beten, / stets für ihn Segen erflehen.", footnotes: ["61,7f"] },
      { number: 16, text: "Im Land gebe es Korn in Fülle. / Es rausche auf dem Gipfel der Berge. Seine Frucht wird sein wie die Bäume des Libanon. / Menschen blühn in der Stadt wie das Gras der Erde.", footnotes: ["Jes 27,6; Am 9,13"] },
      { number: 17, text: "Sein Name soll ewig bestehen; / solange die Sonne bleibt, sprosse sein Name. Glücklich preisen sollen ihn alle Völker / und in ihm sich segnen.", footnotes: ["21,7; Gen 12,3; 22,18"] },
      { number: 18, text: "Gepriesen sei der Herr, der Gott Israels! / Er allein tut Wunder.", footnotes: ["41,14; 136,4"] },
      { number: 19, text: "Gepriesen sei sein herrlicher Name in Ewigkeit! / Seine Herrlichkeit erfülle die ganze Erde. / Amen, ja amen. [Ende der Gebete Davids, des Sohnes Isais.]", footnotes: ["Jes 6,3; Num 14,21"] },
    ] },
    { number: 73, verses: [
      { number: 1, text: "[Ein Psalm Asafs.] Lauter Güte ist Gott für Israel, / für alle Menschen mit reinem Herzen.", heading: "Das dritte Buch | Das scheinbare Glück der Frevler" },
      { number: 2, text: "Ich aber - fast wären meine Füße gestrauchelt, / beinahe wäre ich gefallen.", footnotes: ["37,1; Jer 12,1"] },
      { number: 3, text: "Denn ich habe mich über die Prahler ereifert, / als ich sah, dass es diesen Frevlern so gut ging.", footnotes: ["(3-12) Ijob 21,7-13"] },
      { number: 4, text: "Sie leiden ja keine Qualen, / ihr Leib ist gesund und wohlgenährt.", footnotes: ["Text korr."] },
      { number: 5, text: "Sie kennen nicht die Mühsal der Sterblichen, / sind nicht geplagt wie andere Menschen." },
      { number: 6, text: "Darum ist Hochmut ihr Halsschmuck, / wie ein Gewand umhüllt sie Gewalttat.", footnotes: ["17,10"] },
      { number: 7, text: "Sie sehen kaum aus den Augen vor Fett, / ihr Herz läuft über von bösen Plänen.", footnotes: ["Ijob 15,27; Jer 5,28"] },
      { number: 8, text: "Sie höhnen, und was sie sagen, ist schlecht; / sie sind falsch und reden von oben herab." },
      { number: 9, text: "Sie reißen ihr Maul bis zum Himmel auf / und lassen auf Erden ihrer Zunge freien Lauf." },
      { number: 10, text: "Darum wendet sich das Volk ihnen zu / und schlürft ihre Worte in vollen Zügen.", footnotes: ["das Volk ihnen zu, wörtlich: sein Volk hierher. Text korr.; H: Wasser in Fülle schlürfen sie."] },
      { number: 11, text: "Sie sagen: «Wie sollte Gott das merken? / Wie kann der Höchste das wissen?»", footnotes: ["10,11"] },
      { number: 12, text: "Wahrhaftig, so sind die Frevler: / Immer im Glück, häufen sie Reichtum auf Reichtum.", footnotes: ["17,14"] },
      { number: 13, text: "Also hielt ich umsonst mein Herz rein / und wusch meine Hände in Unschuld.", footnotes: ["26,6; Mal 3,14"] },
      { number: 14, text: "Und doch war ich alle Tage geplagt / und wurde jeden Morgen gezüchtigt.", footnotes: ["Ijob 7,18"] },
      { number: 21, text: "Mein Herz war verbittert, / mir bohrte der Schmerz in den Nieren;" },
      { number: 22, text: "ich war töricht und ohne Verstand, / war wie ein Stück Vieh vor dir." },
      { number: 15, text: "Hätte ich gesagt: «Ich will reden wie sie», / dann hätte ich an deinen Kindern Verrat geübt.", footnotes: ["Text korr. nach S und der aramäischen Übersetzung."] },
      { number: 16, text: "Da sann ich nach, um das zu begreifen; / es war eine Qual für mich," },
      { number: 17, text: "bis ich dann eintrat ins Heiligtum Gottes / und begriff, wie sie enden." },
      { number: 18, text: "Ja, du stellst sie auf schlüpfrigen Grund, / du stürzt sie in Täuschung und Trug." },
      { number: 19, text: "Sie werden plötzlich zunichte, / werden dahingerafft und nehmen ein schreckliches Ende,", footnotes: ["49,15"] },
      { number: 20, text: "wie ein Traum, der beim Erwachen verblasst, / dessen Bild man vergisst, wenn man aufsteht.", footnotes: ["Ijob 20,8", "Wörtlich: Wie ein Traum nach dem Erwachen, Herr, in der Stadt vertreibst du ihr (Götzen)bild."] },
      { number: 23, text: "Ich aber bleibe immer bei dir, / du hältst mich an meiner Rechten.", footnotes: ["121,5; Röm 8,35-39"] },
      { number: 24, text: "Du leitest mich nach deinem Ratschluss / und nimmst mich am Ende auf in Herrlichkeit.", footnotes: ["16,10; 49,16"] },
      { number: 25, text: "Was habe ich im Himmel außer dir? / Neben dir erfreut mich nichts auf der Erde.", footnotes: ["außer dir: ergänzt."] },
      { number: 26, text: "Auch wenn mein Leib und mein Herz verschmachten, / Gott ist der Fels meines Herzens / und mein Anteil auf ewig.", footnotes: ["142,6; Num 18,20; Dtn 10,9; Klgl 3,24"] },
      { number: 27, text: "Ja, wer dir fern ist, geht zugrunde; / du vernichtest alle, die dich treulos verlassen." },
      { number: 28, text: "Ich aber - Gott nahe zu sein ist mein Glück. / Ich setze auf Gott, den Herrn, mein Vertrauen. / Ich will all deine Taten verkünden." },
    ] },
    { number: 74, verses: [
      { number: 1, text: "[Ein Weisheitslied Asafs.] Warum, Gott, hast du uns für immer verstoßen? / Warum ist dein Zorn gegen die Herde deiner Weide entbrannt?", heading: "Klage über die Verwüstung des Heiligtums", footnotes: ["77,8; 80,5"] },
      { number: 2, text: "Denk an deine Gemeinde, die du vorzeiten erworben, / als Stamm dir zu Eigen erkauft, / an den Berg Zion, den du zur Wohnung erwählt hast.", footnotes: ["Ex 15,17; Dtn 7,6; Ps 76,3"] },
      { number: 3, text: "Erheb deine Schritte zu den uralten Trümmern! / Der Feind hat im Heiligtum alles verwüstet." },
      { number: 4, text: "Deine Widersacher lärmten an deiner heiligen Stätte, / stellten ihre Banner auf als Zeichen des Sieges." },
      { number: 5, text: "Wie einer die Axt schwingt im Dickicht des Waldes, /", footnotes: ["Text korr.; H: Es sah sich an, wie wenn man im Unterholz die Äxte hoch emporhebt."] },
      { number: 6, text: "so zerschlugen sie all das Schnitzwerk mit Beil und Hammer.", footnotes: ["Text korr.; H: und dann all das Schnitzwerk."] },
      { number: 7, text: "Sie legten an dein Heiligtum Feuer, / entweihten die Wohnung deines Namens bis auf den Grund.", footnotes: ["2 Kön 25,9; Jes 64,10"] },
      { number: 8, text: "Sie sagten in ihrem Herzen: «Wir zerstören alles.» / Und sie verbrannten alle Gottesstätten ringsum im Land.", footnotes: ["Klgl 2,9"] },
      { number: 9, text: "Zeichen für uns sehen wir nicht, / es ist kein Prophet mehr da, / niemand von uns weiß, wie lange noch." },
      { number: 10, text: "Wie lange, Gott, darf der Bedränger noch schmähen, / darf der Feind ewig deinen Namen lästern?" },
      { number: 11, text: "Warum ziehst du die Hand von uns ab, / hältst deine Rechte im Gewand verborgen?", footnotes: ["Jes 52,10", "hältst verborgen: Text korr.; H: mach ein Ende!"] },
      { number: 12, text: "Doch Gott ist mein König von alters her, / Taten des Heils vollbringt er auf Erden." },
      { number: 13, text: "Mit deiner Macht hast du das Meer zerspalten, / die Häupter der Drachen über den Wassern zerschmettert.", footnotes: ["Ex 14,21f; Ps 89,11; Jes 51,9f"] },
      { number: 14, text: "Du hast die Köpfe des Leviatan zermalmt, / ihn zum Fraß gegeben den Ungeheuern der See.", footnotes: ["Text korr.; H: dem Volk von Dämonen."] },
      { number: 15, text: "Hervorbrechen ließest du Quellen und Bäche, / austrocknen Ströme, die sonst nie versiegen.", footnotes: ["Ex 17,6; Jos 3,16"] },
      { number: 16, text: "Dein ist der Tag, dein auch die Nacht, / hingestellt hast du Sonne und Mond.", footnotes: ["104,19"] },
      { number: 17, text: "Du hast die Grenzen der Erde festgesetzt, / hast Sommer und Winter geschaffen." },
      { number: 18, text: "Denk daran: Der Feind schmäht den Herrn, / ein Volk ohne Einsicht lästert deinen Namen." },
      { number: 19, text: "Gib dem Raubtier das Leben deiner Taube nicht preis; / das Leben deiner Armen vergiss nicht für immer!" },
      { number: 20, text: "Blick hin auf deinen Bund! / Denn voll von Schlupfwinkeln der Gewalt ist unser Land.", footnotes: ["deinen: nach G und S. Andere Übersetzungsmöglichkeit: Denn die Winkel des Landes sind voll von Stätten der Gewalt."] },
      { number: 21, text: "Lass den Bedrückten nicht beschämt von dir weggehn! / Arme und Gebeugte sollen deinen Namen rühmen." },
      { number: 22, text: "Erheb dich, Gott, und führe deine Sache! / Bedenke, wie die Toren dich täglich schmähen." },
      { number: 23, text: "Vergiss nicht das Geschrei deiner Gegner, / das Toben deiner Widersacher, das ständig emporsteigt." },
    ] },
    { number: 75, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Zerstöre nicht!». Ein Psalm Asafs. Ein Lied.]", heading: "Gott, der gerechte Richter" },
      { number: 2, text: "Wir preisen dich, Gott, wir preisen dich; / dein Name ist denen nahe, die deine Wunder erzählen." },
      { number: 3, text: "«Ja, zu der Zeit, die ich selbst bestimme, / halte ich Gericht nach meinem Recht." },
      { number: 4, text: "Die Erde mit allen, die auf ihr wohnen, mag wanken; / doch ich selbst habe ihre Säulen auf festen Grund gestellt.» [Sela]", footnotes: ["24,2; 104,5; 1 Sam 2,8"] },
      { number: 5, text: "Ich sage zu den Stolzen: Seid nicht so vermessen!, / und zu den Frevlern: Brüstet euch nicht mit eurer Macht!", footnotes: ["1 Sam 2,3"] },
      { number: 6, text: "Brüstet euch nicht stolz mit eurer Macht, / redet nicht so überheblich daher!", footnotes: ["94,4"] },
      { number: 7, text: "Denn weder vom Osten noch vom Westen / noch aus der Wüste kommt die Erhöhung.", footnotes: ["Jer 3,23; Mt 24,23-28", "Text korr."] },
      { number: 8, text: "Nein, der Richter ist Gott; / den einen erniedrigt er, den andern erhöht er.", footnotes: ["1 Sam 2,7; Dan 2,21"] },
      { number: 9, text: "Ja, in der Hand des Herrn ist ein Becher, / herben, gärenden Wein reicht er dar; ihn müssen alle Frevler der Erde trinken, / müssen ihn samt der Hefe schlürfen.", footnotes: ["60,5; Ijob 21,20; Jer 25,15f; Hab 2,16"] },
      { number: 10, text: "Ich aber werde jubeln für immer; / dem Gott Jakobs will ich singen und spielen.", footnotes: ["jubeln: Text korr. nach G; H: verkünden."] },
      { number: 11, text: "«Ich schlage die ganze Macht der Frevler nieder; / doch das Haupt des Gerechten wird hoch erhoben.»" },
    ] },
    { number: 76, verses: [
      { number: 1, text: "[Für den Chormeister. Mit Saitenspiel. Ein Psalm Asafs. Ein Lied.]", heading: "Der Weltenrichter auf dem Zion" },
      { number: 2, text: "Gott gab sich zu erkennen in Juda, / sein Name ist groß in Israel." },
      { number: 3, text: "Sein Zelt erstand in Salem, / seine Wohnung auf dem Zion.", footnotes: ["87,2; 132,13"] },
      { number: 4, text: "Dort zerbrach er die blitzenden Pfeile des Bogens, / Schild und Schwert, die Waffen des Krieges. [Sela]", footnotes: ["46,10; Jdt 9,7"] },
      { number: 5, text: "Du bist furchtbar und herrlich, / mehr als die ewigen Berge.", footnotes: ["furchtbar: nach der aramäischen Übersetzung und nach der griechischen von Theodotion; H: leuchtend. Text korr. nach G."] },
      { number: 6, text: "Ausgeplündert sind die tapferen Streiter, / sie sinken hin in den Schlaf; / allen Helden versagen die Hände.", footnotes: ["2 Kön 19,35; Jer 51,39.57"] },
      { number: 7, text: "Wenn du drohst, Gott Jakobs, / erstarren Rosse und Wagen." },
      { number: 8, text: "Furchtbar bist du. Wer kann bestehen vor dir, / vor der Gewalt deines Zornes?", footnotes: ["Dtn 7,21; Nah 1,6"] },
      { number: 9, text: "Vom Himmel her machst du das Urteil bekannt; / Furcht packt die Erde, und sie verstummt," },
      { number: 10, text: "wenn Gott sich erhebt zum Gericht, / um allen Gebeugten auf der Erde zu helfen. [Sela]" },
      { number: 11, text: "Denn auch der Mensch voll Trotz muss dich preisen / und der Rest der Völker dich feiern.", footnotes: ["dich feiern: Text korr.; H: du gürtest dich. - Andere Übersetzungsmöglichkeit: Ja, das grimmige Edom soll dich preisen, der Rest Hamats dich feiern."] },
      { number: 12, text: "Legt Gelübde ab und erfüllt sie dem Herrn, eurem Gott! / Ihr alle ringsum, bringt Gaben ihm, den ihr fürchtet.", footnotes: ["66,13"] },
      { number: 13, text: "Er nimmt den Fürsten den Mut; / Furcht erregend ist er für die Könige der Erde." },
    ] },
    { number: 77, verses: [
      { number: 1, text: "[Für den Chormeister. Nach Jedutun. Ein Psalm Asafs.]", heading: "Gottes Weg mit seinem Volk" },
      { number: 2, text: "Ich rufe zu Gott, ich schreie, / ich rufe zu Gott, bis er mich hört." },
      { number: 3, text: "Am Tag meiner Not suche ich den Herrn; / unablässig erhebe ich nachts meine Hände, / meine Seele lässt sich nicht trösten.", footnotes: ["50,15; 88,2", "Mit den Übersetzungen des Symmachus und des Hieronymus."] },
      { number: 4, text: "Denke ich an Gott, muss ich seufzen; / sinne ich nach, dann will mein Geist verzagen. [Sela]" },
      { number: 5, text: "Du lässt mich nicht mehr schlafen; / ich bin voll Unruhe und kann nicht reden.", footnotes: ["119,148"] },
      { number: 6, text: "Ich sinne nach über die Tage von einst, / ich will denken an längst vergangene Jahre.", footnotes: ["143,5"] },
      { number: 7, text: "Mein Herz grübelt bei Nacht, / ich sinne nach, es forscht mein Geist.", footnotes: ["Nach G und S."] },
      { number: 8, text: "Wird der Herr mich denn auf ewig verstoßen / und mir niemals mehr gnädig sein?", footnotes: ["13,2; 74,1; 85,6; 89,47; Klgl 3,31"] },
      { number: 9, text: "Hat seine Huld für immer ein Ende, / ist seine Verheißung aufgehoben für alle Zeiten?" },
      { number: 10, text: "Hat Gott seine Gnade vergessen, / im Zorn sein Erbarmen verschlossen? [Sela]", footnotes: ["Jes 49,14f"] },
      { number: 11, text: "Da sagte ich mir: «Das ist mein Schmerz, / dass die Rechte des Höchsten so anders handelt.»", footnotes: ["Mal 3,6"] },
      { number: 12, text: "Ich denke an die Taten des Herrn, / ich will denken an deine früheren Wunder." },
      { number: 13, text: "Ich erwäge all deine Werke / und will nachsinnen über deine Taten.", footnotes: ["143,5"] },
      { number: 14, text: "Gott, dein Weg ist heilig. / Wo ist ein Gott, so groß wie unser Gott?", footnotes: ["18,31f; 35,10", "Nach G."] },
      { number: 15, text: "Du allein bist der Gott, der Wunder tut, / du hast deine Macht den Völkern kundgetan.", footnotes: ["98,1"] },
      { number: 16, text: "Du hast mit starkem Arm dein Volk erlöst, / die Kinder Jakobs und Josefs. [Sela]", footnotes: ["Neh 1,10"] },
      { number: 17, text: "Die Wasser sahen dich, Gott, / die Wasser sahen dich und bebten. / Die Tiefen des Meeres tobten.", footnotes: ["18,16; 114,3; Nah 1,4"] },
      { number: 18, text: "Die Wolken gossen ihr Wasser aus, / das Gewölk ließ die Stimme dröhnen, / auch deine Pfeile flogen dahin.", footnotes: ["18,15; (18-19) Hab 3,9-11"] },
      { number: 19, text: "Dröhnend rollte dein Donner, / Blitze erhellten den Erdkreis, / die Erde bebte und wankte.", footnotes: ["68,9; 97,4", "Text korr."] },
      { number: 20, text: "Durch das Meer ging dein Weg, / dein Pfad durch gewaltige Wasser, / doch niemand sah deine Spuren.", footnotes: ["Neh 9,11; Jes 43,16; 51,10; (20-21) Ex 14,21-31"] },
      { number: 21, text: "Du führtest dein Volk wie eine Herde / durch die Hand von Mose und Aaron.", footnotes: ["78,52; Jes 63,11-14; Mi 6,4"] },
    ] },
    { number: 78, verses: [
      { number: 1, text: "[Ein Weisheitslied Asafs.] Mein Volk, vernimm meine Weisung! / Wendet euer Ohr zu den Worten meines Mundes!", heading: "Die Geschichte Israels als Mahnung und Warnung" },
      { number: 2, text: "Ich öffne meinen Mund zu einem Spruch; / ich will die Geheimnisse der Vorzeit verkünden.", footnotes: ["49,5; Mt 13,35"] },
      { number: 3, text: "Was wir hörten und erfuhren, / was uns die Väter erzählten,", footnotes: ["44,2; (3-6) 22,31f"] },
      { number: 4, text: "das wollen wir unseren Kindern nicht verbergen, / sondern dem kommenden Geschlecht erzählen: die ruhmreichen Taten und die Stärke des Herrn, / die Wunder, die er getan hat." },
      { number: 5, text: "Er stellte sein Gesetz auf in Jakob, / gab in Israel Weisung / und gebot unseren Vätern, ihre Kinder das alles zu lehren,", footnotes: ["147,19"] },
      { number: 6, text: "damit das kommende Geschlecht davon erfahre, / die Kinder späterer Zeiten; / sie sollten aufstehen und es weitergeben an ihre Kinder," },
      { number: 7, text: "damit sie ihr Vertrauen auf Gott setzen, / die Taten Gottes nicht vergessen / und seine Gebote bewahren" },
      { number: 8, text: "und nicht werden wie ihre Väter, / jenes Geschlecht voll Trotz und Empörung, / das wankelmütige Geschlecht, dessen Geist nicht treu zu Gott hielt.", footnotes: ["Dtn 32,5f.20"] },
      { number: 9, text: "Die Söhne Efraims, Kämpfer mit Pfeil und Bogen, / wandten den Rücken am Tag der Schlacht;" },
      { number: 10, text: "Gottes Bund hielten sie nicht, / sie weigerten sich, seiner Weisung zu folgen." },
      { number: 11, text: "Sie vergaßen die Taten des Herrn, / die Wunder, die er sie sehen ließ." },
      { number: 12, text: "Vor den Augen ihrer Väter vollbrachte er Wunder / im Land Ägypten, im Gefilde von Zoan.", footnotes: ["Num 13,22"] },
      { number: 13, text: "Er spaltete das Meer und führte sie hindurch, / er ließ das Wasser fest stehen wie einen Damm.", footnotes: ["Ex 14,21f"] },
      { number: 14, text: "Er leitete sie bei Tag mit der Wolke / und die ganze Nacht mit leuchtendem Feuer.", footnotes: ["Ex 13,21"] },
      { number: 15, text: "Er spaltete Felsen in der Wüste / und gab dem Volk reichlich zu trinken wie mit Wassern der Urflut.", footnotes: ["(15-16) Ex 17,6; Num 20,11"] },
      { number: 16, text: "Er ließ Bäche aus dem Gestein entspringen, / ließ Wasser fließen gleich Strömen." },
      { number: 17, text: "Doch sie sündigten weiter gegen ihn, / sie trotzten in der Wüste dem Höchsten.", footnotes: ["Ez 20,13"] },
      { number: 18, text: "In ihrem Herzen versuchten sie Gott, / forderten Nahrung für den Hunger.", footnotes: ["106,14; Ex 16,3"] },
      { number: 19, text: "Sie redeten gegen Gott; sie fragten: / «Kann uns denn Gott den Tisch decken in der Wüste?" },
      { number: 20, text: "Zwar hat er an den Felsen geschlagen, / sodass Wasser floss und Bäche strömten. Kann er uns auch Brot verschaffen / und sein Volk mit Fleisch versorgen?»" },
      { number: 21, text: "Das hörte der Herr und war voll Grimm; / Feuer flammte auf gegen Jakob, / Zorn erhob sich gegen Israel,", footnotes: ["Num 11,1"] },
      { number: 22, text: "weil sie Gott nicht glaubten / und nicht auf seine Hilfe vertrauten." },
      { number: 23, text: "Dennoch gebot er den Wolken droben / und öffnete die Tore des Himmels.", footnotes: ["Mal 3,10"] },
      { number: 24, text: "Er ließ Manna auf sie regnen als Speise, / er gab ihnen Brot vom Himmel.", footnotes: ["Ex 16,4.14f; Joh 6,31"] },
      { number: 25, text: "Da aßen die Menschen Wunderbrot; / Gott gab ihnen Nahrung in Fülle.", footnotes: ["105,40"] },
      { number: 26, text: "Er ließ den Ostwind losbrechen droben am Himmel, / führte in seiner Macht den Südwind herbei," },
      { number: 27, text: "ließ Fleisch auf sie regnen wie Staub, / gefiederte Vögel wie Sand am Meer." },
      { number: 28, text: "Er ließ sie mitten ins Lager fallen, / rings um Israels Zelte." },
      { number: 29, text: "Da aßen alle und wurden satt; / er hatte ihnen gebracht, was sie begehrten." },
      { number: 30, text: "Noch aber hatten sie ihre Gier nicht gestillt, / noch war die Speise in ihrem Mund,", footnotes: ["Num 11,33"] },
      { number: 31, text: "da erhob sich gegen sie Gottes Zorn; / er erschlug ihre Führer / und streckte die jungen Männer Israels nieder." },
      { number: 32, text: "Doch sie sündigten trotz allem weiter / und vertrauten nicht seinen Wundern.", footnotes: ["Num 14,11"] },
      { number: 33, text: "Darum ließ er ihre Tage schwinden wie einen Hauch / und ihre Jahre voll Schrecken vergehen.", footnotes: ["Num 14,22f"] },
      { number: 34, text: "Wenn er dreinschlug, fragten sie nach Gott, / kehrten um und suchten ihn.", footnotes: ["Num 21,7; Jes 26,16"] },
      { number: 35, text: "Sie dachten daran, dass Gott ihr Fels ist, / Gott, der Höchste, ihr Erlöser.", footnotes: ["92,16"] },
      { number: 36, text: "Doch sie täuschten ihn mit falschen Worten / und ihre Zunge belog ihn.", footnotes: ["(36f) Jes 29,13", "Wörtlich: und mit ihrem Mund täuschten sie ihn."] },
      { number: 37, text: "Ihr Herz hielt nicht fest zu ihm, / sie hielten seinem Bund nicht die Treue.", footnotes: ["95,10"] },
      { number: 38, text: "Er aber vergab ihnen voll Erbarmen die Schuld / und tilgte sein Volk nicht aus. Oftmals ließ er ab von seinem Zorn / und unterdrückte seinen Groll.", footnotes: ["Ex 32,14; Num 14,20; Jes 48,9; Hos 11,8f"] },
      { number: 39, text: "Denn er dachte daran, dass sie nichts sind als Fleisch, / nur ein Hauch, der vergeht und nicht wiederkehrt.", footnotes: ["103,14-16; Ijob 7,7"] },
      { number: 40, text: "Wie oft haben sie ihm in der Wüste getrotzt, / ihn gekränkt in der Steppe!", footnotes: ["Dtn 9,22"] },
      { number: 41, text: "Immer wieder stellten sie ihn auf die Probe, / sie reizten den heiligen Gott Israels.", footnotes: ["71,22"] },
      { number: 42, text: "Sie dachten nicht mehr an seine mächtige Hand, / an den Tag, als er sie vom Unterdrücker befreite,", footnotes: ["106,21"] },
      { number: 43, text: "als er in Ägypten Zeichen tat / und Wunder im Gefilde von Zoan:", footnotes: ["135,9; 78,12"] },
      { number: 44, text: "Er verwandelte ihre Flüsse und Bäche in Blut; / sie konnten daraus nicht mehr trinken.", footnotes: ["Ex 7,17-21"] },
      { number: 45, text: "Er schickte einen Schwarm von Fliegen, der fraß sie auf, / ein Heer von Fröschen, das vertilgte sie.", footnotes: ["Ex 8,17-20"] },
      { number: 46, text: "Ihre Ernte überließ er den Grillen / und den Heuschrecken den Ertrag ihrer Mühen.", footnotes: ["Ex 10,4-15"] },
      { number: 47, text: "Ihre Reben zerschlug er mit Hagel, / ihre Maulbeerbäume mit Körnern aus Eis.", footnotes: ["Ex 9,18-25"] },
      { number: 48, text: "Ihr Vieh überließ er der Pest / und ihre Herden den Seuchen.", footnotes: ["Ex 9,3-7", "Text korr.; H: überließ er dem Hagel."] },
      { number: 49, text: "Er ließ die Glut seines Zorns auf sie los: / Grimm und Wut und Bedrängnis, / Boten des Unheils in Scharen." },
      { number: 50, text: "Er ließ seinem Zorn freien Lauf; / er bewahrte sie nicht vor dem Tod / und lieferte ihr Leben der Pest aus.", footnotes: ["Ex 9,8-10"] },
      { number: 51, text: "Er schlug in Ägypten alle Erstgeburt, / in den Zelten Hams die Blüte der Jugend.", footnotes: ["105,36; 135,8; 136,10; Ex 12,29f", "alle Erstgeburt, wörtlich: den Erstling ihrer Zeugungskraft."] },
      { number: 52, text: "Dann führte er sein Volk hinaus wie Schafe, / leitete sie wie eine Herde durch die Wüste.", footnotes: ["77,21"] },
      { number: 53, text: "Er führte sie sicher, sie mussten nichts fürchten, / doch ihre Feinde bedeckte das Meer.", footnotes: ["Ex 14,19-31"] },
      { number: 54, text: "Er brachte sie in sein heiliges Land, / in die Berge, die er erwarb mit mächtiger Hand.", footnotes: ["114,2; Ex 15,17", "H: zu dem Berg, den . . ."] },
      { number: 55, text: "Er vertrieb die Völker vor ihnen, / ließ in ihren Zelten die Stämme Israels wohnen / und teilte ihnen ihr Erbteil zu.", footnotes: ["44,3; Jos 24,8-13"] },
      { number: 56, text: "Doch sie versuchten Gott und trotzten dem Höchsten; / sie hielten seine Satzungen nicht." },
      { number: 57, text: "Wie ihre Väter fielen sie treulos von ihm ab, / sie wandten sich ab wie ein Bogen, der versagt.", footnotes: ["Hos 7,16"] },
      { number: 58, text: "Sie erbitterten ihn mit ihrem Kult auf den Höhen / und reizten seine Eifersucht mit ihren Götzen.", footnotes: ["Dtn 32,16.21"] },
      { number: 59, text: "Als Gott es sah, war er voll Grimm / und sagte sich los von Israel." },
      { number: 60, text: "Er verwarf seine Wohnung in Schilo, / das Zelt, wo er unter den Menschen wohnte.", footnotes: ["1 Sam 1,3; Jer 7,12"] },
      { number: 61, text: "Er gab seine Macht in Gefangenschaft, / seine heilige Lade fiel in die Hand des Feindes.", footnotes: ["1 Sam 4,11.22", "seine heilige Lade: H: seine Pracht (als Bezeichnung der Lade)."] },
      { number: 62, text: "Er lieferte sein Volk dem Schwert aus; / er war voll Grimm über sein Eigentum." },
      { number: 63, text: "Die jungen Männer fraß das Feuer; / den jungen Mädchen sang man kein Brautlied.", footnotes: ["Jer 7,34"] },
      { number: 64, text: "Die Priester wurden mit dem Schwert erschlagen; / die Witwen konnten die Toten nicht beweinen.", footnotes: ["1 Sam 4,11; Ijob 27,15"] },
      { number: 65, text: "Da erwachte der Herr wie aus dem Schlaf, / wie ein Held, der betäubt war vom Wein." },
      { number: 66, text: "Er schlug seine Feinde zurück / und gab sie ewiger Schande preis.", footnotes: ["1 Sam 5,6-12"] },
      { number: 67, text: "Das Zelt Josefs verwarf er, / dem Stamm Efraim entzog er die Erwählung." },
      { number: 68, text: "Doch den Stamm Juda erwählte er, / den Berg Zion, den er liebt.", footnotes: ["2 Chr 6,6"] },
      { number: 69, text: "Dort baute er sein hoch aufragendes Heiligtum, / so fest wie die Erde, / die er für immer gegründet hat.", footnotes: ["48,3"] },
      { number: 70, text: "Und er erwählte seinen Knecht David; / er holte ihn weg von den Hürden der Schafe, /", footnotes: ["(70-71) 89,21; 1 Sam 16,11-13; 2 Sam 7,8"] },
      { number: 71, text: "von den Muttertieren nahm er ihn fort, damit er sein Volk Jakob weide / und sein Erbe Israel." },
      { number: 72, text: "Er sorgte als Hirt für sie mit lauterem Herzen / und führte sie mit klugen Händen." },
    ] },
    { number: 79, verses: [
      { number: 1, text: "[Ein Psalm Asafs.] Gott, die Heiden sind eingedrungen in dein Erbe, / sie haben deinen heiligen Tempel entweiht / und Jerusalem in Trümmer gelegt.", heading: "Die Klage über die Zerstörung Jerusalems", footnotes: ["2 Kön 25,8-10; Klgl 1,10"] },
      { number: 2, text: "Die Leichen deiner Knechte haben sie zum Fraß gegeben den Vögeln des Himmels, / die Leiber deiner Frommen den Tieren des Feldes.", footnotes: ["Jer 7,33; (2-3) 1 Makk 7,17"] },
      { number: 3, text: "Ihr Blut haben sie wie Wasser vergossen / rings um Jerusalem, / und keiner hat sie begraben.", footnotes: ["Jer 14,16"] },
      { number: 4, text: "Zum Schimpf sind wir geworden / in den Augen der Nachbarn, / zu Spott und Hohn bei allen, die rings um uns wohnen.", footnotes: ["31,12; Zef 2,8"] },
      { number: 5, text: "Wie lange noch, Herr? Willst du auf ewig zürnen? / Wie lange noch wird dein Eifer lodern wie Feuer?", footnotes: ["89,47"] },
      { number: 6, text: "Gieß deinen Zorn aus über die Heiden, / die dich nicht kennen, / über jedes Reich, das deinen Namen nicht anruft.", footnotes: ["Sir 36,1-6; 14,4; (6-7) Jer 10,25"] },
      { number: 7, text: "Denn sie haben Jakob aufgezehrt / und seine Felder verwüstet." },
      { number: 8, text: "Rechne uns die Schuld der Vorfahren nicht an! / Mit deinem Erbarmen komm uns eilends entgegen! / Denn wir sind sehr erniedrigt." },
      { number: 9, text: "Um der Ehre deines Namens willen / hilf uns, du Gott unsres Heils! / Um deines Namens willen reiß uns heraus und vergib uns die Sünden!", footnotes: ["Ez 20,44; 36,22"] },
      { number: 10, text: "Warum dürfen die Heiden sagen: / «Wo ist nun ihr Gott?» Lass kund werden an den Heiden vor unsern Augen, / wie du das vergossene Blut deiner Knechte vergiltst.", footnotes: ["42,11; Joël 4,21"] },
      { number: 11, text: "Das Stöhnen der Gefangenen dringe zu dir. / Befrei die Todgeweihten durch die Kraft deines Armes!", footnotes: ["102,21", "Andere Übersetzungsmöglichkeit: Lass am Leben die Todgeweihten!"] },
      { number: 12, text: "Zahl unsern Nachbarn siebenfach heim / die Schmach, die sie dir, Herr, angetan.", footnotes: ["137,7-9", "Wörtlich: Zahl siebenfach in den Schoß unserer Nachbarn!"] },
      { number: 13, text: "Wir aber, dein Volk, die Schafe deiner Weide, / wollen dir ewig danken, / deinen Ruhm verkünden von Geschlecht zu Geschlecht.", footnotes: ["95,7"] },
    ] },
    { number: 80, verses: [
      { number: 1, text: "[Für den Chormeister. Nach der Weise «Lilien». Ein Zeugnis. Ein Psalm Asafs.]", heading: "Bitte für Israel, den Weinstock Gottes" },
      { number: 2, text: "Du Hirte Israels, höre, / der du Josef weidest wie eine Herde! Der du auf den Kerubim thronst, erscheine /", footnotes: ["23,1; 95,7"] },
      { number: 3, text: "vor Efraim, Benjamin und Manasse! Biete deine gewaltige Macht auf / und komm uns zu Hilfe!" },
      { number: 4, text: "Gott, richte uns wieder auf! / Lass dein Angesicht leuchten, dann ist uns geholfen.", footnotes: ["85,5; 4,7"] },
      { number: 5, text: "Herr, Gott der Heerscharen, wie lange noch zürnst du, / während dein Volk zu dir betet?" },
      { number: 6, text: "Du hast sie gespeist mit Tränenbrot, / sie überreich getränkt mit Tränen.", footnotes: ["42,4"] },
      { number: 7, text: "Du machst uns zum Spielball der Nachbarn / und unsere Feinde verspotten uns.", footnotes: ["44,14", "Nach G, S und der griechischen Übersetzung des Symmachus."] },
      { number: 8, text: "Gott der Heerscharen, richte uns wieder auf! / Lass dein Angesicht leuchten, dann ist uns geholfen." },
      { number: 9, text: "Du hobst in Ägypten einen Weinstock aus, / du hast Völker vertrieben, ihn aber eingepflanzt.", footnotes: ["Jer 2,21"] },
      { number: 10, text: "Du schufst ihm weiten Raum; / er hat Wurzeln geschlagen / und das ganze Land erfüllt." },
      { number: 11, text: "Sein Schatten bedeckte die Berge, / seine Zweige die Zedern Gottes.", footnotes: ["(11-12) Dtn 11,24"] },
      { number: 12, text: "Seine Ranken trieb er bis hin zum Meer / und seine Schößlinge bis zum Eufrat." },
      { number: 13, text: "Warum rissest du seine Mauern ein? / Alle, die des Weges kommen, plündern ihn aus.", footnotes: ["89,41f"] },
      { number: 14, text: "Der Eber aus dem Wald wühlt ihn um, / die Tiere des Feldes fressen ihn ab." },
      { number: 15, text: "Gott der Heerscharen, wende dich uns wieder zu! / Blick vom Himmel herab, und sieh auf uns! Sorge für diesen Weinstock /" },
      { number: 16, text: "und für den Garten, den deine Rechte gepflanzt hat.", footnotes: ["Text korr.; H fügt aus V. 18 hinzu: und den Sohn, den du großzogst."] },
      { number: 17, text: "Die ihn im Feuer verbrannten wie Kehricht, / sie sollen vergehen vor deinem drohenden Angesicht.", footnotes: ["Ez 15,2-4"] },
      { number: 18, text: "Deine Hand schütze den Mann zu deiner Rechten, / den Menschensohn, den du für dich groß und stark gemacht." },
      { number: 19, text: "Erhalt uns am Leben! / Dann wollen wir deinen Namen anrufen und nicht von dir weichen." },
      { number: 20, text: "Herr, Gott der Heerscharen, richte uns wieder auf! / Lass dein Angesicht leuchten, dann ist uns geholfen." },
    ] },
    { number: 81, verses: [
      { number: 1, text: "[Für den Chormeister. Nach dem Kelterlied. Von Asaf.]", heading: "Aufruf zur Treue gegen Gott" },
      { number: 2, text: "Jubelt Gott zu, er ist unsre Zuflucht; / jauchzt dem Gott Jakobs zu!" },
      { number: 3, text: "Stimmt an den Gesang, schlagt die Pauke, / die liebliche Laute, dazu die Harfe!" },
      { number: 4, text: "Stoßt in die Posaune am Neumond / und zum Vollmond, am Tag unsres Festes!", footnotes: ["Lev 23,24; Num 10,10"] },
      { number: 5, text: "Denn das ist Satzung für Israel, / Entscheid des Gottes Jakobs." },
      { number: 6, text: "Das hat er als Gesetz für Josef erlassen, / als Gott gegen Ägypten auszog. Eine Stimme höre ich, die ich noch nie vernahm: /" },
      { number: 7, text: "Seine Schulter hab ich von der Bürde befreit, / seine Hände kamen los vom Lastkorb.", footnotes: ["Ex 1,14; 6,6"] },
      { number: 8, text: "Du riefst in der Not / und ich riss dich heraus; ich habe dich aus dem Gewölk des Donners erhört, / an den Wassern von Meríba geprüft. [Sela]", footnotes: ["Ex 17,1-7; Num 20,13; Ps 95,8"] },
      { number: 9, text: "Höre, mein Volk, ich will dich mahnen! / Israel, wolltest du doch auf mich hören!" },
      { number: 10, text: "Für dich gibt es keinen andern Gott. / Du sollst keinen fremden Gott anbeten.", footnotes: ["(10-11) Ex 20,2f", "Textvorschlag für die Psalmodie: Du sollst dich nicht niederwerfen vor einem fremden Gott."] },
      { number: 11, text: "Ich bin der Herr, dein Gott, / der dich heraufgeführt hat aus Ägypten. / Tu deinen Mund auf! Ich will ihn füllen." },
      { number: 12, text: "Doch mein Volk hat nicht auf meine Stimme gehört; / Israel hat mich nicht gewollt.", footnotes: ["95,7"] },
      { number: 13, text: "Da überließ ich sie ihrem verstockten Herzen / und sie handelten nach ihren eigenen Plänen.", footnotes: ["Jer 7,24"] },
      { number: 14, text: "Ach dass doch mein Volk auf mich hörte, / dass Israel gehen wollte auf meinen Wegen!" },
      { number: 15, text: "Wie bald würde ich seine Feinde beugen, / meine Hand gegen seine Bedränger wenden.", footnotes: ["66,3"] },
      { number: 16, text: "Alle, die den Herrn hassen, müssten Israel schmeicheln / und das sollte für immer so bleiben.", footnotes: ["Israel: H: ihm."] },
      { number: 17, text: "Ich würde es nähren mit bestem Weizen / und mit Honig aus dem Felsen sättigen.", footnotes: ["147,14; Dtn 32,13", "Text korr.; H: Er hat es genährt."] },
    ] },
    { number: 82, verses: [
      { number: 1, text: "[Ein Psalm Asafs.] Gott steht auf in der Versammlung der Götter, / im Kreis der Götter hält er Gericht.", heading: "Bitte um Gottes Eingreifen als Richter", footnotes: ["136,2f; 1 Kön 22,19"] },
      { number: 2, text: "«Wie lange noch wollt ihr ungerecht richten / und die Frevler begünstigen? [Sela]", footnotes: ["58,3"] },
      { number: 3, text: "Verschafft Recht den Unterdrückten und Waisen, / verhelft den Gebeugten und Bedürftigen zum Recht!", footnotes: ["Ex 23,6; Dtn 1,17; Jes 1,17"] },
      { number: 4, text: "Befreit die Geringen und Armen, / entreißt sie der Hand der Frevler!»" },
      { number: 5, text: "Sie aber haben weder Einsicht noch Verstand, / sie tappen dahin im Finstern. / Alle Grundfesten der Erde wanken." },
      { number: 6, text: "«Wohl habe ich gesagt: Ihr seid Götter, / ihr alle seid Söhne des Höchsten.", footnotes: ["Joh 10,34"] },
      { number: 7, text: "Doch nun sollt ihr sterben wie Menschen, / sollt stürzen wie jeder der Fürsten.»" },
      { number: 8, text: "Erheb dich, Gott, und richte die Erde! / Denn alle Völker werden dein Erbteil sein.", footnotes: ["67,5", "Wörtlich: Denn du besitzt Erbteil an den Völkern."] },
    ] },
    { number: 83, verses: [
      { number: 1, text: "[Ein Lied. Ein Psalm Asafs.]", heading: "Eine Bitte um Hilfe gegen Feinde des Volkes" },
      { number: 2, text: "Schweig doch nicht, o Gott, bleib nicht still, / o Gott, bleib nicht stumm!" },
      { number: 3, text: "Sieh doch, deine Feinde toben; / die dich hassen, erheben das Haupt.", footnotes: ["35,20"] },
      { number: 4, text: "Gegen dein Volk ersinnen sie listige Pläne / und halten Rat gegen die, die sich bei dir bergen." },
      { number: 5, text: "Sie sagen: «Wir wollen sie ausrotten als Volk; / an den Namen Israel soll niemand mehr denken.»" },
      { number: 6, text: "Ja, sie halten einmütig Rat / und schließen ein Bündnis gegen dich:", footnotes: ["2,2"] },
      { number: 7, text: "Edoms Zelte und die Ismaeliter, / Moab und die Hagariter," },
      { number: 8, text: "Gebal, Ammon und Amalek, / das Philisterland und die Bewohner von Tyrus." },
      { number: 9, text: "Zu ihnen gesellt sich auch Assur / und leiht seinen Arm den Söhnen Lots. [Sela]" },
      { number: 10, text: "Mach es mit ihnen wie mit Midian und Sisera, / wie mit Jabin am Bach Kischon,", footnotes: ["Ri 7,23-25; Jes 9,3; 10,26; Ri 4,15f"] },
      { number: 11, text: "die du bei En-Dór vernichtet hast. / Sie wurden zum Dung für die Äcker." },
      { number: 12, text: "Mach ihre Fürsten wie Oreb und Seeb, / wie Sebach und Zalmunna mach all ihre Führer! /", footnotes: ["Ri 7,25; Ri 8,21", "Text korr. nach G."] },
      { number: 13, text: "Sie sagten: «Wir wollen Gottes Land erobern.»" },
      { number: 14, text: "Mein Gott, lass sie dahinwirbeln wie Staub, / wie Spreu vor dem Wind!", footnotes: ["35,5", "Wörtlich: Mein Gott, mach sie der Distel gleich!"] },
      { number: 15, text: "Wie das Feuer, das ganze Wälder verbrennt, / wie die Flamme, die Berge versengt," },
      { number: 16, text: "so jage sie davon mit deinem Sturm / und schrecke sie mit deinem Wetter!" },
      { number: 17, text: "Bedecke mit Schmach ihr Gesicht, / damit sie, Herr, nach deinem Namen fragen." },
      { number: 18, text: "Beschämt sollen sie sein und verstört für immer, / sollen vor Schande zugrunde gehn." },
      { number: 19, text: "Sie sollen erkennen, dass du es bist. Herr ist dein Name. / Du allein bist der Höchste über der ganzen Erde.", footnotes: ["46,11; 59,14; 100,3; Dtn 4,39; Jes 43,10; Dan 3,45"] },
    ] },
    { number: 84, verses: [
      { number: 1, text: "[Für den Chormeister. Nach dem Kelterlied. Ein Psalm der Korachiter.]", heading: "Die Freude am Heiligtum" },
      { number: 2, text: "Wie liebenswert ist deine Wohnung, Herr der Heerscharen! /" },
      { number: 3, text: "Meine Seele verzehrt sich in Sehnsucht / nach dem Tempel des Herrn. Mein Herz und mein Leib jauchzen ihm zu, / ihm, dem lebendigen Gott.", footnotes: ["27,4", "dem Tempel, wörtlich: den (Tempel-)Vorhöfen des Herrn."] },
      { number: 4, text: "Auch der Sperling findet ein Haus / und die Schwalbe ein Nest für ihre Jungen - / deine Altäre, Herr der Heerscharen, mein Gott und mein König.", footnotes: ["5,3"] },
      { number: 5, text: "Wohl denen, die wohnen in deinem Haus, / die dich allezeit loben. [Sela]", footnotes: ["23,6"] },
      { number: 6, text: "Wohl den Menschen, die Kraft finden in dir, / wenn sie sich zur Wallfahrt rüsten." },
      { number: 7, text: "Ziehen sie durch das trostlose Tal, / wird es für sie zum Quellgrund / und Frühregen hüllt es in Segen." },
      { number: 8, text: "Sie schreiten dahin mit wachsender Kraft; / dann schauen sie Gott auf dem Zion.", footnotes: ["Text korr.; H: er erscheint (ihnen), der Gott der Götter auf dem Zion."] },
      { number: 9, text: "Herr der Heerscharen, höre mein Beten, / vernimm es, Gott Jakobs! [Sela]", footnotes: ["H: Herr, Gott der Heerscharen."] },
      { number: 10, text: "Gott, sieh her auf unsern Schild, / schau auf das Antlitz deines Gesalbten!", footnotes: ["89,19"] },
      { number: 11, text: "Denn ein einziger Tag in den Vorhöfen deines Heiligtums / ist besser als tausend andere. Lieber an der Schwelle stehen im Haus meines Gottes / als wohnen in den Zelten der Frevler.", footnotes: ["27,4"] },
      { number: 12, text: "Denn Gott der Herr ist Sonne und Schild. / Er schenkt Gnade und Herrlichkeit; der Herr versagt denen, die rechtschaffen sind, keine Gabe. /", footnotes: ["3,4"] },
      { number: 13, text: "Herr der Heerscharen, wohl dem, der dir vertraut!" },
    ] },
    { number: 85, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm der Korachiter.]", heading: "Bitte um das verheißene Heil" },
      { number: 2, text: "Einst hast du, Herr, dein Land begnadet / und Jakobs Unglück gewendet,", footnotes: ["Jer 31,23"] },
      { number: 3, text: "hast deinem Volk die Schuld vergeben, / all seine Sünden zugedeckt, [Sela]", footnotes: ["32,1"] },
      { number: 4, text: "hast zurückgezogen deinen ganzen Grimm / und deinen glühenden Zorn gedämpft." },
      { number: 5, text: "Gott, unser Retter, richte uns wieder auf, / lass von deinem Unmut gegen uns ab!", footnotes: ["80,4", "Wörtlich: zerbrich deinen Unmut gegen uns!"] },
      { number: 6, text: "Willst du uns ewig zürnen, / soll dein Zorn dauern von Geschlecht zu Geschlecht?", footnotes: ["77,8; 79,5"] },
      { number: 7, text: "Willst du uns nicht wieder beleben, / sodass dein Volk sich an dir freuen kann?" },
      { number: 8, text: "Erweise uns, Herr, deine Huld / und gewähre uns dein Heil!" },
      { number: 9, text: "Ich will hören, was Gott redet: / Frieden verkündet der Herr seinem Volk und seinen Frommen, / den Menschen mit redlichem Herzen. [Sela]", footnotes: ["Jer 29,11", "H fügt hinzu: und sie sollen nicht zur Torheit zurückkehren."] },
      { number: 10, text: "Sein Heil ist denen nahe, die ihn fürchten. / Seine Herrlichkeit wohne in unserm Land." },
      { number: 11, text: "Es begegnen einander Huld und Treue; / Gerechtigkeit und Friede küssen sich.", footnotes: ["61,8; 89,15"] },
      { number: 12, text: "Treue sprosst aus der Erde hervor; / Gerechtigkeit blickt vom Himmel hernieder.", footnotes: ["Jes 45,8"] },
      { number: 13, text: "Auch spendet der Herr dann Segen / und unser Land gibt seinen Ertrag.", footnotes: ["67,7", "Segen, wörtlich: das Gute."] },
      { number: 14, text: "Gerechtigkeit geht vor ihm her / und Heil folgt der Spur seiner Schritte.", footnotes: ["Jes 58,8", "Text korr.; H: er achtet auf den Weg seiner Schritte."] },
    ] },
    { number: 86, verses: [
      { number: 1, text: "[Ein Gebet Davids.] Wende dein Ohr mir zu, erhöre mich, Herr! / Denn ich bin arm und gebeugt.", heading: "Der Hilferuf eines Armen zu Gott" },
      { number: 2, text: "Beschütze mich, denn ich bin dir ergeben! / Hilf deinem Knecht, der dir vertraut!" },
      { number: 3, text: "Du bist mein Gott. Sei mir gnädig, o Herr! / Den ganzen Tag rufe ich zu dir.", footnotes: ["«Du bist mein Gott» wurde aus V. 2 hierher genommen."] },
      { number: 4, text: "Herr, erfreue deinen Knecht; / denn ich erhebe meine Seele zu dir.", footnotes: ["25,1; 143,8"] },
      { number: 5, text: "Herr, du bist gütig und bereit zu verzeihen, / für alle, die zu dir rufen, reich an Gnade." },
      { number: 6, text: "Herr, vernimm mein Beten, / achte auf mein lautes Flehen!" },
      { number: 7, text: "Am Tag meiner Not rufe ich zu dir; / denn du wirst mich erhören.", footnotes: ["50,15"] },
      { number: 8, text: "Herr, unter den Göttern ist keiner wie du / und nichts gleicht den Werken, die du geschaffen hast.", footnotes: ["«die du geschaffen hast» wurde aus V. 9 hierher genommen."] },
      { number: 9, text: "Alle Völker kommen und beten dich an, / sie geben, Herr, deinem Namen die Ehre.", footnotes: ["22,28; Offb 15,4"] },
      { number: 10, text: "Denn du bist groß und tust Wunder; / du allein bist Gott." },
      { number: 11, text: "Weise mir, Herr, deinen Weg; / ich will ihn gehen in Treue zu dir. Richte mein Herz darauf hin, / allein deinen Namen zu fürchten!", footnotes: ["25,4; 26,3; Joh 14,6"] },
      { number: 12, text: "Ich will dir danken, Herr, mein Gott, / aus ganzem Herzen, / will deinen Namen ehren immer und ewig." },
      { number: 13, text: "Du hast mich den Tiefen des Totenreichs entrissen. / Denn groß ist über mir deine Huld.", footnotes: ["9,14"] },
      { number: 14, text: "Gott, freche Menschen haben sich gegen mich erhoben, / die Rotte der Gewalttäter trachtet mir nach dem Leben; / doch dich haben sie nicht vor Augen.", footnotes: ["54,5"] },
      { number: 15, text: "Du aber, Herr, bist ein barmherziger und gnädiger Gott, / du bist langmütig, reich an Huld und Treue.", footnotes: ["116,5"] },
      { number: 16, text: "Wende dich mir zu und sei mir gnädig, / gib deinem Knecht wieder Kraft / und hilf dem Sohn deiner Magd!", footnotes: ["25,16"] },
      { number: 17, text: "Tu ein Zeichen und schenke mir Glück! / Alle, die mich hassen, sollen es sehen und sich schämen, / weil du, Herr, mich gerettet und getröstet hast." },
    ] },
    { number: 87, verses: [
      { number: 1, text: "[Ein Psalm der Korachiter. Ein Lied.]", heading: "Ein Loblied auf Zion, die Mutter aller Völker" },
      { number: 2, text: "Der Herr liebt (Zion), seine Gründung auf heiligen Bergen; / mehr als all seine Stätten in Jakob liebt er die Tore Zions.", footnotes: ["76,3; 78,68", "Andere Übersetzungsmöglichkeit: mehr als alle anderen Orte in Jakob."] },
      { number: 3, text: "Herrliches sagt man von dir, / du Stadt unseres Gottes. [Sela]" },
      { number: 4, text: "Leute aus Ägypten und Babel / zähle ich zu denen, die mich kennen; auch von Leuten aus dem Philisterland, / aus Tyrus und Kusch / sagt man: Er ist dort geboren.", footnotes: ["(4-5) Jes 19,24f"] },
      { number: 5, text: "Doch von Zion wird man sagen: / Jeder ist dort geboren. / Er, der Höchste, hat Zion gegründet." },
      { number: 6, text: "Der Herr schreibt, wenn er die Völker verzeichnet: / Er ist dort geboren. [Sela]", footnotes: ["Jes 4,3"] },
      { number: 7, text: "Und sie werden beim Reigentanz singen: / All meine Quellen entspringen in dir.", footnotes: ["149,3; Jes 12,3"] },
    ] },
    { number: 88, verses: [
      { number: 1, text: "[Ein Lied. Ein Psalm der Korachiter. Für den Chormeister. Nach der Weise «Krankheit» zu singen. Ein Weisheitslied Hemans, des Esrachiters.]", heading: "Die Klage eines Kranken und Einsamen" },
      { number: 2, text: "Herr, du Gott meines Heils, / zu dir schreie ich am Tag und bei Nacht.", footnotes: ["77,3"] },
      { number: 3, text: "Lass mein Gebet zu dir dringen, / wende dein Ohr meinem Flehen zu!", footnotes: ["17,6; 119,170"] },
      { number: 4, text: "Denn meine Seele ist gesättigt mit Leid, / mein Leben ist dem Totenreich nahe.", footnotes: ["Ijob 17,1; 33,22"] },
      { number: 5, text: "Schon zähle ich zu denen, die hinabsinken ins Grab, / bin wie ein Mann, dem alle Kraft genommen ist.", footnotes: ["143,7"] },
      { number: 6, text: "Ich bin zu den Toten hinweggerafft / wie Erschlagene, die im Grabe ruhen; an sie denkst du nicht mehr, / denn sie sind deiner Hand entzogen.", footnotes: ["Text korr."] },
      { number: 7, text: "Du hast mich ins tiefste Grab gebracht, / tief hinab in finstere Nacht." },
      { number: 8, text: "Schwer lastet dein Grimm auf mir, / all deine Wogen stürzen über mir zusammen. [Sela]", footnotes: ["42,8", "Wörtlich: und alle deine Wogen beugst du hernieder."] },
      { number: 9, text: "Die Freunde hast du mir entfremdet, / mich ihrem Abscheu ausgesetzt; / ich bin gefangen und kann nicht heraus.", footnotes: ["31,12"] },
      { number: 10, text: "Mein Auge wird trübe vor Elend. / Jeden Tag, Herr, ruf ich zu dir; / ich strecke nach dir meine Hände aus." },
      { number: 11, text: "Wirst du an den Toten Wunder tun, / werden Schatten aufstehn, um dich zu preisen? [Sela]", footnotes: ["(11-13) 6,6"] },
      { number: 12, text: "Erzählt man im Grab von deiner Huld, / von deiner Treue im Totenreich?" },
      { number: 13, text: "Werden deine Wunder in der Finsternis bekannt, / deine Gerechtigkeit im Land des Vergessens?" },
      { number: 14, text: "Herr, darum schreie ich zu dir, / früh am Morgen tritt mein Gebet vor dich hin.", footnotes: ["5,4"] },
      { number: 15, text: "Warum, o Herr, verwirfst du mich, / warum verbirgst du dein Gesicht vor mir?", footnotes: ["Ijob 13,24"] },
      { number: 16, text: "Gebeugt bin ich und todkrank von früher Jugend an, / deine Schrecken lasten auf mir und ich bin zerquält.", footnotes: ["Ijob 6,4; 20,25"] },
      { number: 17, text: "Über mich fuhr die Glut deines Zorns dahin, / deine Schrecken vernichten mich." },
      { number: 18, text: "Sie umfluten mich allzeit wie Wasser / und dringen auf mich ein von allen Seiten.", footnotes: ["42,8"] },
      { number: 19, text: "Du hast mir die Freunde und Gefährten entfremdet; / mein Vertrauter ist nur noch die Finsternis.", footnotes: ["31,12; Ijob 17,13"] },
    ] },
    { number: 89, verses: [
      { number: 1, text: "[Ein Weisheitslied Etans, des Esrachiters.]", heading: "Das Klagelied über die Verwerfung des Hauses David" },
      { number: 2, text: "Von den Taten deiner Huld, Herr, will ich ewig singen, / bis zum fernsten Geschlecht laut deine Treue verkünden.", footnotes: ["Jes 63,7"] },
      { number: 3, text: "Denn ich bekenne: Deine Huld besteht für immer und ewig; / deine Treue steht fest im Himmel.", footnotes: ["deine Treue steht fest: Text korr.; H: du befestigst deine Treue."] },
      { number: 4, text: "«Ich habe einen Bund geschlossen mit meinem Erwählten / und David, meinem Knecht, geschworen:", footnotes: ["132,11; 2 Sam 7,8-16"] },
      { number: 5, text: "Deinem Haus gebe ich auf ewig Bestand / und von Geschlecht zu Geschlecht richte ich deinen Thron auf.» [Sela]", footnotes: ["Apg 2,30"] },
      { number: 6, text: "Die Himmel preisen, Herr, deine Wunder / und die Gemeinde der Heiligen deine Treue." },
      { number: 7, text: "Denn wer über den Wolken ist wie der Herr, / wer von den Göttern ist dem Herrn gleich?", footnotes: ["29,1; (7-9) 35,10; 136,2f"] },
      { number: 8, text: "Gewaltig ist Gott im Rat der Heiligen, / für alle rings um ihn her ist er groß und furchtbar.", footnotes: ["Ijob 1,6"] },
      { number: 9, text: "Herr, Gott der Heerscharen, wer ist wie du? / Mächtig bist du, Herr, und von Treue umgeben." },
      { number: 10, text: "Du beherrschst die Empörung des Meeres; / wenn seine Wogen toben - du glättest sie.", footnotes: ["65,8; Mt 8,26"] },
      { number: 11, text: "Rahab hast du durchbohrt und zertreten, / deine Feinde zerstreut mit starkem Arm.", footnotes: ["74,13f; Ijob 26,12", "Text korr."] },
      { number: 12, text: "Dein ist der Himmel, dein auch die Erde; / den Erdkreis und was ihn erfüllt hast du gegründet.", footnotes: ["24,1"] },
      { number: 13, text: "Nord und Süd hast du geschaffen, / Tabor und Hermon jauchzen bei deinem Namen." },
      { number: 14, text: "Dein Arm ist voll Kraft, / deine Hand ist stark, deine Rechte hoch erhoben." },
      { number: 15, text: "Recht und Gerechtigkeit sind die Stützen deines Thrones, / Huld und Treue schreiten vor deinem Antlitz her.", footnotes: ["97,2; 85,11"] },
      { number: 16, text: "Wohl dem Volk, das dich als König zu feiern weiß! / Herr, sie gehen im Licht deines Angesichts.", footnotes: ["47,2"] },
      { number: 17, text: "Sie freuen sich über deinen Namen zu jeder Zeit, / über deine Gerechtigkeit jubeln sie.", footnotes: ["jubeln sie: Text korr.; H: erheben sie sich."] },
      { number: 18, text: "Denn du bist ihre Schönheit und Stärke, / du erhöhst unsre Kraft in deiner Güte." },
      { number: 19, text: "Ja, unser Schild gehört dem Herrn, / unser König dem heiligen Gott Israels.", footnotes: ["84,10"] },
      { number: 20, text: "Einst hast du in einer Vision zu deinen Frommen gesprochen: / «Einen Helden habe ich zum König gekrönt, / einen jungen Mann aus dem Volk erhöht.", footnotes: ["(20-21) 1 Sam 13,14; 16,13", "gekrönt: Text korr."] },
      { number: 21, text: "Ich habe David, meinen Knecht, gefunden / und ihn mit meinem heiligen Öl gesalbt.", footnotes: ["78,70; Apg 13,22"] },
      { number: 22, text: "Beständig wird meine Hand ihn halten / und mein Arm ihn stärken.", footnotes: ["Jes 42,1"] },
      { number: 23, text: "Kein Feind soll ihn täuschen, / kein ruchloser Mensch kann ihn bezwingen." },
      { number: 24, text: "Vor ihm will ich die Feinde zerschmettern / und alle, die ihn hassen, schlage ich nieder." },
      { number: 25, text: "Meine Treue und meine Huld begleiten ihn / und in meinem Namen erhebt er sein Haupt.", footnotes: ["1 Sam 2,10"] },
      { number: 26, text: "Ich lege seine Hand auf das Meer, / über die Ströme herrscht seine Rechte.", footnotes: ["72,8"] },
      { number: 27, text: "Er wird zu mir rufen: Mein Vater bist du, / mein Gott, der Fels meines Heiles.", footnotes: ["(27-28) 2,7; 2 Sam 7,14"] },
      { number: 28, text: "Ich mache ihn zum erstgeborenen Sohn, / zum Höchsten unter den Herrschern der Erde.", footnotes: ["Kol 1,15.18; Offb 1,5"] },
      { number: 29, text: "Auf ewig werde ich ihm meine Huld bewahren, / mein Bund mit ihm bleibt allzeit bestehen.", footnotes: ["18,51; Jes 55,3"] },
      { number: 30, text: "Sein Geschlecht lasse ich dauern für immer / und seinen Thron, solange der Himmel währt.", footnotes: ["61,8; 2 Sam 7,16"] },
      { number: 31, text: "Wenn seine Söhne meine Weisung verlassen, / nicht mehr leben nach meiner Ordnung," },
      { number: 32, text: "wenn sie meine Gesetze entweihen, / meine Gebote nicht mehr halten," },
      { number: 33, text: "dann werde ich ihr Vergehen mit der Rute strafen / und ihre Sünde mit Schlägen.", footnotes: ["2 Sam 7,14"] },
      { number: 34, text: "Doch ich entziehe ihm nicht meine Huld, / breche ihm nicht die Treue.", footnotes: ["Jer 31,36f", "Text korr.; H: ich breche ihm nicht."] },
      { number: 35, text: "Meinen Bund werde ich nicht entweihen; / was meine Lippen gesprochen haben, / will ich nicht ändern." },
      { number: 36, text: "Eines hab ich geschworen, so wahr ich heilig bin, / und niemals werde ich David belügen:" },
      { number: 37, text: "Sein Geschlecht soll bleiben auf ewig, / sein Thron habe Bestand vor mir wie die Sonne;" },
      { number: 38, text: "er soll ewig bestehen wie der Mond, / der verlässliche Zeuge über den Wolken.» [Sela]", footnotes: ["72,7; Sir 43,6"] },
      { number: 39, text: "Nun aber hast du deinen Gesalbten verstoßen, / ihn verworfen und mit Zorn überschüttet," },
      { number: 40, text: "hast den Bund mit deinem Knecht zerbrochen, / zu Boden getreten seine Krone." },
      { number: 41, text: "Eingerissen hast du all seine Mauern, / in Trümmer gelegt seine Burgen.", footnotes: ["(41-42) 80,13"] },
      { number: 42, text: "Alle, die des Weges kommen, plündern ihn aus, / er wird zum Gespött seiner Nachbarn.", footnotes: ["31,12"] },
      { number: 43, text: "Du hast die Hand seiner Bedränger hoch erhoben, / hast all seine Feinde erfreut." },
      { number: 44, text: "Du hast die Spitze seines Schwertes umgekehrt, / hast im Kampf ihm den Sieg verweigert.", footnotes: ["Übersetzung unsicher."] },
      { number: 45, text: "Du hast ein Ende gemacht seinem Glanz / und seinen Thron zu Boden geworfen." },
      { number: 46, text: "Du hast ihm die Tage der Jugend verkürzt / und ihn bedeckt mit Schande. [Sela]" },
      { number: 47, text: "Wie lange noch, Herr? Verbirgst du dich ewig? / Soll dein Zorn wie Feuer brennen?", footnotes: ["77,8; 79,5; (47-48) 90,9f"] },
      { number: 48, text: "Bedenke, Herr: Was ist unser Leben, / wie vergänglich hast du alle Menschen erschaffen!", footnotes: ["Text korr."] },
      { number: 49, text: "Wo ist der Mann, der ewig lebt und den Tod nicht schaut, / der sich retten kann vor dem Zugriff der Unterwelt? [Sela]" },
      { number: 50, text: "Herr, wo sind die Taten deiner Huld geblieben, / die du David in deiner Treue geschworen hast?" },
      { number: 51, text: "Herr, denk an die Schmach deines Knechtes! / Im Herzen brennt mir der Hohn der Völker,", footnotes: ["41,6", "deines Knechtes: Text korr.; H: deiner Knechte. Text korr.; H: Im Herzen trage ich alle die vielen Völker."] },
      { number: 52, text: "mit dem deine Feinde mich schmähen, Herr, / und die Schritte deines Gesalbten verhöhnen.", footnotes: ["Hebr 11,26"] },
      { number: 53, text: "Gepriesen sei der Herr in Ewigkeit. / Amen, ja amen." },
    ] },
    { number: 90, verses: [
      { number: 1, text: "[Ein Gebet des Mose, des Mannes Gottes.] Herr, du warst unsere Zuflucht / von Geschlecht zu Geschlecht.", heading: "Das vierte Buch | Der ewige Gott - der vergängliche Mensch" },
      { number: 2, text: "Ehe die Berge geboren wurden, / die Erde entstand und das Weltall, / bist du, o Gott, von Ewigkeit zu Ewigkeit." },
      { number: 3, text: "Du lässt die Menschen zurückkehren zum Staub / und sprichst: «Kommt wieder, ihr Menschen!»", footnotes: ["146,4"] },
      { number: 4, text: "Denn tausend Jahre sind für dich / wie der Tag, der gestern vergangen ist, / wie eine Wache in der Nacht.", footnotes: ["2 Petr 3,8"] },
      { number: 5, text: "Von Jahr zu Jahr säst du die Menschen aus; / sie gleichen dem sprossenden Gras.", footnotes: ["(5-6) 37,2; 58,8; 102,12; 103,15; Ijob 14,2; Jes 40,6f", "Text korr."] },
      { number: 6, text: "Am Morgen grünt es und blüht, / am Abend wird es geschnitten und welkt." },
      { number: 7, text: "Denn wir vergehen durch deinen Zorn, / werden vernichtet durch deinen Grimm.", footnotes: ["werden vernichtet, wörtlich: werden erschreckt."] },
      { number: 8, text: "Du hast unsre Sünden vor dich hingestellt, / unsere geheime Schuld in das Licht deines Angesichts.", footnotes: ["109,15; Hos 7,2"] },
      { number: 9, text: "Denn all unsre Tage gehn hin unter deinem Zorn, / wir beenden unsere Jahre wie einen Seufzer.", footnotes: ["(9-10) 39,6; 89,48; 102,12; 109,23; 144,4; Ijob 7,6.16; 14,1f; Koh 6,12; Weish 2,5"] },
      { number: 10, text: "Unser Leben währt siebzig Jahre, / und wenn es hoch kommt, sind es achtzig. Das Beste daran ist nur Mühsal und Beschwer, / rasch geht es vorbei, wir fliegen dahin.", footnotes: ["Gen 6,3; Sir 18,9"] },
      { number: 11, text: "Wer kennt die Gewalt deines Zornes / und fürchtet sich vor deinem Grimm?", footnotes: ["Text korr.; H: und wie die Furcht vor dir ist dein Grimm."] },
      { number: 12, text: "Unsre Tage zu zählen, lehre uns! / Dann gewinnen wir ein weises Herz.", footnotes: ["39,5f"] },
      { number: 13, text: "Herr, wende dich uns doch endlich zu! / Hab Mitleid mit deinen Knechten!" },
      { number: 14, text: "Sättige uns am Morgen mit deiner Huld! / Dann wollen wir jubeln und uns freuen all unsre Tage.", footnotes: ["46,6; 143,8; Klgl 3,23"] },
      { number: 15, text: "Erfreue uns so viele Tage, wie du uns gebeugt hast, / so viele Jahre, wie wir Unglück erlitten." },
      { number: 16, text: "Zeig deinen Knechten deine Taten / und ihren Kindern deine erhabene Macht!", footnotes: ["Jes 5,12"] },
      { number: 17, text: "Es komme über uns die Güte des Herrn, unsres Gottes. / Lass das Werk unsrer Hände gedeihen, / ja, lass gedeihen das Werk unsrer Hände!" },
    ] },
    { number: 91, verses: [
      { number: 1, text: "Wer im Schutz des Höchsten wohnt / und ruht im Schatten des Allmächtigen,", heading: "Unter dem Schutz des Höchsten" },
      { number: 2, text: "der sagt zum Herrn: «Du bist für mich Zuflucht und Burg, / mein Gott, dem ich vertraue.»", footnotes: ["18,3", "Text korr. nach G."] },
      { number: 3, text: "Er rettet dich aus der Schlinge des Jägers / und aus allem Verderben.", footnotes: ["124,7", "Andere Übersetzungsmöglichkeit: und aus der Pest des Verderbens; oder: und vor dem (Verderben bringenden) Wort (G). Die hebräischen Ausdrücke für Pest (deber) und Wort (dabar) können leicht verwechselt werden, zumal man keine Vokale schrieb."] },
      { number: 4, text: "Er beschirmt dich mit seinen Flügeln, / unter seinen Schwingen findest du Zuflucht, / Schild und Schutz ist dir seine Treue.", footnotes: ["17,7f"] },
      { number: 5, text: "Du brauchst dich vor dem Schrecken der Nacht nicht zu fürchten, / noch vor dem Pfeil, der am Tag dahinfliegt,", footnotes: ["Spr 3,25"] },
      { number: 6, text: "nicht vor der Pest, die im Finstern schleicht, / vor der Seuche, die wütet am Mittag.", footnotes: ["Jer 15,8"] },
      { number: 7, text: "Fallen auch tausend zu deiner Seite, / dir zur Rechten zehnmal tausend, / so wird es doch dich nicht treffen.", footnotes: ["Dtn 7,15"] },
      { number: 8, text: "Ja, du wirst es sehen mit eigenen Augen, / wirst zuschauen, wie den Frevlern vergolten wird.", footnotes: ["54,9; 62,13"] },
      { number: 9, text: "Denn der Herr ist deine Zuflucht, / du hast dir den Höchsten als Schutz erwählt.", footnotes: ["H: Denn du, Herr, bist meine Zuflucht."] },
      { number: 10, text: "Dir begegnet kein Unheil, / kein Unglück naht deinem Zelt.", footnotes: ["Ijob 5,19.24"] },
      { number: 11, text: "Denn er befiehlt seinen Engeln, / dich zu behüten auf all deinen Wegen.", footnotes: ["34,8; Hebr 1,14; (11-12) Mt 4,6"] },
      { number: 12, text: "Sie tragen dich auf ihren Händen, / damit dein Fuß nicht an einen Stein stößt;", footnotes: ["Spr 3,23"] },
      { number: 13, text: "du schreitest über Löwen und Nattern, / trittst auf Löwen und Drachen.", footnotes: ["Lk 10,19"] },
      { number: 14, text: "«Weil er an mir hängt, will ich ihn retten; / ich will ihn schützen, denn er kennt meinen Namen.", footnotes: ["9,11; 119,132"] },
      { number: 15, text: "Wenn er mich anruft, dann will ich ihn erhören. / Ich bin bei ihm in der Not, / befreie ihn und bringe ihn zu Ehren.", footnotes: ["50,15; Sach 13,9"] },
      { number: 16, text: "Ich sättige ihn mit langem Leben / und lasse ihn schauen mein Heil.»", footnotes: ["50,23"] },
    ] },
    { number: 92, verses: [
      { number: 1, text: "[Ein Psalm. Ein Lied für den Sabbattag.]", heading: "Ein Loblied auf die Treue Gottes" },
      { number: 2, text: "Wie schön ist es, dem Herrn zu danken, / deinem Namen, du Höchster, zu singen,", footnotes: ["33,1-3"] },
      { number: 3, text: "am Morgen deine Huld zu verkünden / und in den Nächten deine Treue" },
      { number: 4, text: "zur zehnsaitigen Laute, zur Harfe, / zum Klang der Zither." },
      { number: 5, text: "Denn du hast mich durch deine Taten froh gemacht; / Herr, ich will jubeln über die Werke deiner Hände." },
      { number: 6, text: "Wie groß sind deine Werke, o Herr, / wie tief deine Gedanken!", footnotes: ["104,24; 139,17f"] },
      { number: 7, text: "Ein Mensch ohne Einsicht erkennt das nicht, / ein Tor kann es nicht verstehen.", footnotes: ["Weish 13,1"] },
      { number: 8, text: "Wenn auch die Frevler gedeihen / und alle, die Unrecht tun, wachsen, / so nur, damit du sie für immer vernichtest.", footnotes: ["37,35f", "Wörtlich: Wenn auch die Frevler aufsprießen wie Gras."] },
      { number: 9, text: "Herr, du bist der Höchste, / du bleibst auf ewig." },
      { number: 10, text: "Doch deine Feinde, Herr, wahrhaftig, deine Feinde vergehen; / auseinander getrieben werden alle, die Unrecht tun.", footnotes: ["68,2f; 125,5"] },
      { number: 11, text: "Du machtest mich stark wie einen Stier, / du salbtest mich mit frischem Öl.", footnotes: ["23,5", "Nach S und der aramäischen Übersetzung; H: übergossen werde ich mit Öl."] },
      { number: 12, text: "Mein Auge blickt herab auf meine Verfolger, / auf alle, die sich gegen mich erheben; / mein Ohr hört vom Geschick der Bösen.", footnotes: ["54,9"] },
      { number: 13, text: "Der Gerechte gedeiht wie die Palme, / er wächst wie die Zedern des Libanon.", footnotes: ["1,3"] },
      { number: 14, text: "Gepflanzt im Haus des Herrn, / gedeihen sie in den Vorhöfen unseres Gottes.", footnotes: ["52,10"] },
      { number: 15, text: "Sie tragen Frucht noch im Alter / und bleiben voll Saft und Frische;" },
      { number: 16, text: "sie verkünden: Gerecht ist der Herr; / mein Fels ist er, an ihm ist kein Unrecht.", footnotes: ["18,3; 18,47; 19,15; 28,1; 42,10; 62,8; 78,35; 94,22; 95,1; 144,1; Gen 49,24; Dtn 32,4.15.18.37"] },
    ] },
    { number: 93, verses: [
      { number: 1, text: "Der Herr ist König, bekleidet mit Hoheit; / der Herr hat sich bekleidet und mit Macht umgürtet. Der Erdkreis ist fest gegründet, / nie wird er wanken.", heading: "Das Königtum Gottes", footnotes: ["24,7-10; 75,4; 104,5"] },
      { number: 2, text: "Dein Thron steht fest von Anbeginn, / du bist seit Ewigkeit.", footnotes: ["9,8; 90,2"] },
      { number: 3, text: "Fluten erheben sich, Herr, / Fluten erheben ihr Brausen, / Fluten erheben ihr Tosen.", footnotes: ["(3-4) 29,10"] },
      { number: 4, text: "Gewaltiger als das Tosen vieler Wasser, / gewaltiger als die Brandung des Meeres / ist der Herr in der Höhe.", footnotes: ["Text korr.; H: mehr als die Herrlichkeit der Brandungen."] },
      { number: 5, text: "Deine Gesetze sind fest und verlässlich; / Herr, deinem Haus gebührt Heiligkeit / für alle Zeiten.", footnotes: ["19,8"] },
    ] },
    { number: 94, verses: [
      { number: 1, text: "Gott der Vergeltung, o Herr, / du Gott der Vergeltung, erscheine!", heading: "Gott, der Anwalt der Gerechten", footnotes: ["Dtn 32,35; Nah 1,2"] },
      { number: 2, text: "Erhebe dich, Richter der Erde, / vergilt den Stolzen ihr Tun!", footnotes: ["Jer 51,56; Ps 62,13"] },
      { number: 3, text: "Wie lange noch dürfen die Frevler, o Herr, / wie lange noch dürfen die Frevler frohlocken?" },
      { number: 4, text: "Sie führen freche Reden, / alle, die Unrecht tun, brüsten sich.", footnotes: ["75,6"] },
      { number: 5, text: "Herr, sie zertreten dein Volk, / sie unterdrücken dein Erbteil." },
      { number: 6, text: "Sie bringen die Witwen und Waisen um / und morden die Fremden.", footnotes: ["Ex 22,21f; Ez 22,7"] },
      { number: 7, text: "Sie denken: Der Herr sieht es ja nicht, / der Gott Jakobs merkt es nicht.", footnotes: ["10,11"] },
      { number: 8, text: "Begreift doch, ihr Toren im Volk! / Ihr Unvernünftigen, wann werdet ihr klug?", footnotes: ["Spr 1,22; 8,5"] },
      { number: 9, text: "Sollte der nicht hören, der das Ohr gepflanzt hat, / sollte der nicht sehen, der das Auge geformt hat?", footnotes: ["33,15; Ex 4,11; Spr 20,12"] },
      { number: 10, text: "Sollte der nicht strafen, der die Völker erzieht, / er, der die Menschen Erkenntnis lehrt?" },
      { number: 11, text: "Der Herr kennt die Gedanken der Menschen: / Sie sind nichts als ein Hauch.", footnotes: ["1 Kor 3,20"] },
      { number: 12, text: "Wohl dem Mann, den du, Herr, erziehst, / den du mit deiner Weisung belehrst.", footnotes: ["119,71; Ijob 5,17"] },
      { number: 13, text: "Du bewahrst ihn vor bösen Tagen, / bis man dem Frevler die Grube gräbt." },
      { number: 14, text: "Ja, der Herr wird sein Volk nicht verstoßen / und niemals sein Erbe verlassen.", footnotes: ["1 Sam 12,22"] },
      { number: 15, text: "Nun spricht man wieder Recht nach Gerechtigkeit; / ihr folgen alle Menschen mit redlichem Herzen." },
      { number: 16, text: "Wer wird sich für mich gegen die Frevler erheben, / wer steht für mich ein gegen den, der Unrecht tut?" },
      { number: 17, text: "Wäre nicht der Herr meine Hilfe, / bald würde ich im Land des Schweigens wohnen.", footnotes: ["115,17"] },
      { number: 18, text: "Wenn ich sage: «Mein Fuß gleitet aus», / dann stützt mich, Herr, deine Huld.", footnotes: ["145,14"] },
      { number: 19, text: "Mehren sich die Sorgen des Herzens, / so erquickt dein Trost meine Seele.", footnotes: ["2 Kor 1,4f"] },
      { number: 20, text: "Kann sich mit dir der bestechliche Richter verbünden, / der willkürlich straft, gegen das Gesetz?", footnotes: ["der bestechliche Richter, wörtlich: ein Richterstuhl des Verderbens."] },
      { number: 21, text: "Sie wollen das Leben des Gerechten vernichten / und verurteilen schuldlose Menschen." },
      { number: 22, text: "Doch meine Burg ist der Herr, / mein Gott ist der Fels meiner Zuflucht.", footnotes: ["92,16"] },
      { number: 23, text: "Er wird ihnen ihr Unrecht vergelten / und sie wegen ihrer Bosheit vernichten; / vernichten wird sie der Herr, unser Gott.", footnotes: ["7,17; Spr 5,22; Ps 62,13"] },
    ] },
    { number: 95, verses: [
      { number: 1, text: "Kommt, lasst uns jubeln vor dem Herrn / und zujauchzen dem Fels unsres Heiles!", heading: "Aufruf zur Treue gegen Gott", footnotes: ["92,16"] },
      { number: 2, text: "Lasst uns mit Lob seinem Angesicht nahen, / vor ihm jauchzen mit Liedern!", footnotes: ["100,2"] },
      { number: 3, text: "Denn der Herr ist ein großer Gott, / ein großer König über allen Göttern.", footnotes: ["47,3; 48,2; 96,4; 99,2; 113,4; 136,2f; 145,3; Ijob 36,22"] },
      { number: 4, text: "In seiner Hand sind die Tiefen der Erde, / sein sind die Gipfel der Berge." },
      { number: 5, text: "Sein ist das Meer, das er gemacht hat, / das trockene Land, das seine Hände gebildet.", footnotes: ["24,1"] },
      { number: 6, text: "Kommt, lasst uns niederfallen, uns vor ihm verneigen, / lasst uns niederknien vor dem Herrn, unserm Schöpfer!" },
      { number: 7, text: "Denn er ist unser Gott, / wir sind das Volk seiner Weide, / die Herde, von seiner Hand geführt. Ach, würdet ihr doch heute auf seine Stimme hören! /", footnotes: ["100,3; (7-11) Hebr 3,7-11.15; 4,1-10"] },
      { number: 8, text: "«Verhärtet euer Herz nicht wie in Meríba, / wie in der Wüste am Tag von Massa!", footnotes: ["81,8"] },
      { number: 9, text: "Dort haben eure Väter mich versucht, / sie haben mich auf die Probe gestellt und hatten doch mein Tun gesehen.", footnotes: ["Num 14,22; Dtn 6,16"] },
      { number: 10, text: "Vierzig Jahre war mir dies Geschlecht zuwider / und ich sagte: Sie sind ein Volk, dessen Herz in die Irre geht; / denn meine Wege kennen sie nicht.", footnotes: ["Num 14,30.34; Dtn 32,5.20; Ijob 21,14", "dies: ergänzt nach G."] },
      { number: 11, text: "Darum habe ich in meinem Zorn geschworen: / Sie sollen nicht kommen in das Land meiner Ruhe.»", footnotes: ["Num 14,22f; Ps 132,14; Ez 20,15"] },
    ] },
    { number: 96, verses: [
      { number: 1, text: "Singt dem Herrn ein neues Lied, / singt dem Herrn, alle Länder der Erde!", heading: "Der Herr, König und Richter aller Welt", footnotes: ["33,3; (1-13) 1 Chr 16,23-33"] },
      { number: 2, text: "Singt dem Herrn und preist seinen Namen, / verkündet sein Heil von Tag zu Tag!" },
      { number: 3, text: "Erzählt bei den Völkern von seiner Herrlichkeit, / bei allen Nationen von seinen Wundern!", footnotes: ["108,4"] },
      { number: 4, text: "Denn groß ist der Herr und hoch zu preisen, / mehr zu fürchten als alle Götter.", footnotes: ["48,2; 95,3"] },
      { number: 5, text: "Alle Götter der Heiden sind nichtig, / der Herr aber hat den Himmel geschaffen." },
      { number: 6, text: "Hoheit und Pracht sind vor seinem Angesicht, / Macht und Glanz in seinem Heiligtum." },
      { number: 7, text: "Bringt dar dem Herrn, ihr Stämme der Völker, / bringt dar dem Herrn Lob und Ehre!", footnotes: ["(7-9) 29,1f"] },
      { number: 8, text: "Bringt dar dem Herrn die Ehre seines Namens, / spendet Opfergaben und tretet ein in sein Heiligtum!", footnotes: ["Wörtlich: und tretet ein in seine (Tempel-)Vorhöfe!"] },
      { number: 9, text: "In heiligem Schmuck werft euch nieder vor dem Herrn, / erbebt vor ihm, alle Länder der Erde!" },
      { number: 10, text: "Verkündet bei den Völkern: / Der Herr ist König. Den Erdkreis hat er gegründet, sodass er nicht wankt. / Er richtet die Nationen so, wie es recht ist.", footnotes: ["24,7-10; 75,3f", "H: Der Erdkreis ist gegründet."] },
      { number: 11, text: "Der Himmel freue sich, die Erde frohlocke, / es brause das Meer und alles, was es erfüllt.", footnotes: ["98,7"] },
      { number: 12, text: "Es jauchze die Flur und was auf ihr wächst. / Jubeln sollen alle Bäume des Waldes", footnotes: ["Jes 55,12"] },
      { number: 13, text: "vor dem Herrn, wenn er kommt, / wenn er kommt, um die Erde zu richten. Er richtet den Erdkreis gerecht / und die Nationen nach seiner Treue.", footnotes: ["98,9; 9,9; Apg 17,31"] },
    ] },
    { number: 97, verses: [
      { number: 1, text: "Der Herr ist König. Die Erde frohlocke. / Freuen sollen sich die vielen Inseln.", heading: "Aufruf zur Freude über den Herrscher der Welt", footnotes: ["24,7-10"] },
      { number: 2, text: "Rings um ihn her sind Wolken und Dunkel, / Gerechtigkeit und Recht sind die Stützen seines Throns.", footnotes: ["89,15"] },
      { number: 3, text: "Verzehrendes Feuer läuft vor ihm her / und frisst seine Gegner ringsum.", footnotes: ["18,9"] },
      { number: 4, text: "Seine Blitze erhellen den Erdkreis; / die Erde sieht es und bebt.", footnotes: ["77,19"] },
      { number: 5, text: "Berge schmelzen wie Wachs vor dem Herrn, / vor dem Antlitz des Herrschers aller Welt.", footnotes: ["68,3; Mi 1,4"] },
      { number: 6, text: "Seine Gerechtigkeit verkünden die Himmel, / seine Herrlichkeit schauen alle Völker.", footnotes: ["50,6"] },
      { number: 7, text: "Alle, die Bildern dienen, werden zuschanden, / alle, die sich der Götzen rühmen. / Vor ihm werfen sich alle Götter nieder." },
      { number: 8, text: "Zion hört es und freut sich, / Judas Töchter jubeln, Herr, über deine Gerichte.", footnotes: ["48,12"] },
      { number: 9, text: "Denn du, Herr, bist der Höchste über der ganzen Erde, / hoch erhaben über alle Götter.", footnotes: ["83,19; 95,3; 136,2f"] },
      { number: 10, text: "Ihr, die ihr den Herrn liebt, hasst das Böse! / Er behütet das Leben seiner Frommen, / er entreißt sie der Hand der Frevler.", footnotes: ["121,7f"] },
      { number: 11, text: "Ein Licht erstrahlt den Gerechten / und Freude den Menschen mit redlichem Herzen.", footnotes: ["27,1; 43,3; 112,4; Spr 13,9; Jes 58,10; Joh 8,12", "Text korr.; H: Licht ist gesät den Gerechten."] },
      { number: 12, text: "Ihr Gerechten, freut euch am Herrn / und lobt seinen heiligen Namen!", footnotes: ["30,5"] },
    ] },
    { number: 98, verses: [
      { number: 1, text: "[Ein Psalm.] Singt dem Herrn ein neues Lied; / denn er hat wunderbare Taten vollbracht. Er hat mit seiner Rechten geholfen / und mit seinem heiligen Arm.", heading: "Ein neues Lied auf den Richter und Retter", footnotes: ["33,3; Jes 59,16; 63,5"] },
      { number: 2, text: "Der Herr hat sein Heil bekannt gemacht / und sein gerechtes Wirken enthüllt vor den Augen der Völker." },
      { number: 3, text: "Er dachte an seine Huld / und an seine Treue zum Hause Israel. Alle Enden der Erde / sahen das Heil unsres Gottes." },
      { number: 4, text: "Jauchzt vor dem Herrn, alle Länder der Erde, / freut euch, jubelt und singt!" },
      { number: 5, text: "Spielt dem Herrn auf der Harfe, / auf der Harfe zu lautem Gesang!" },
      { number: 6, text: "Zum Schall der Trompeten und Hörner / jauchzt vor dem Herrn, dem König!", footnotes: ["24,7-10"] },
      { number: 7, text: "Es brause das Meer und alles, was es erfüllt, / der Erdkreis und seine Bewohner.", footnotes: ["96,11"] },
      { number: 8, text: "In die Hände klatschen sollen die Ströme, / die Berge sollen jubeln im Chor", footnotes: ["96,12f"] },
      { number: 9, text: "vor dem Herrn, wenn er kommt, / um die Erde zu richten. Er richtet den Erdkreis gerecht, / die Nationen so, wie es recht ist.", footnotes: ["96,13; 67,5"] },
    ] },
    { number: 99, verses: [
      { number: 1, text: "Der Herr ist König: Es zittern die Völker. / Er thront auf den Kerubim: Es wankt die Erde.", heading: "Der heilige Gott auf dem Zion", footnotes: ["24,7-10; 80,2"] },
      { number: 2, text: "Groß ist der Herr auf Zion, / über alle Völker erhaben.", footnotes: ["48,2; 95,3"] },
      { number: 3, text: "Preisen sollen sie deinen großen, majestätischen Namen. / Denn er ist heilig.", footnotes: ["111,9"] },
      { number: 4, text: "Stark ist der König, er liebt das Recht. / Du hast die Weltordnung fest begründet, / hast Recht und Gerechtigkeit in Jakob geschaffen.", footnotes: ["H: Und die Stärke des Königs liebt das Recht."] },
      { number: 5, text: "Rühmt den Herrn, unseren Gott; / werft euch am Schemel seiner Füße nieder! / Denn er ist heilig.", footnotes: ["132,7"] },
      { number: 6, text: "Mose und Aaron sind unter seinen Priestern, / Samuel unter denen, die seinen Namen anrufen; / sie riefen zum Herrn und er hat sie erhört.", footnotes: ["Jer 15,1", "Vorschlag für die Psalmodie: auch Samuel rief seinen Namen an."] },
      { number: 7, text: "Aus der Wolkensäule sprach er zu ihnen; / seine Gebote hielten sie, / die Satzung, die er ihnen gab.", footnotes: ["Ex 33,9; Num 12,5"] },
      { number: 8, text: "Herr, unser Gott, du hast sie erhört; / du warst ihnen ein verzeihender Gott, / aber du hast ihre Frevel vergolten.", footnotes: ["Ex 32,11; 34,7"] },
      { number: 9, text: "Rühmt den Herrn, unsern Gott, / werft euch nieder an seinem heiligen Berge! / Denn heilig ist der Herr, unser Gott." },
    ] },
    { number: 100, verses: [
      { number: 1, text: "[Ein Psalm zum Dankopfer.] Jauchzt vor dem Herrn, alle Länder der Erde! /", heading: "Lobgesang des Volkes beim Einzug ins Heiligtum" },
      { number: 2, text: "Dient dem Herrn mit Freude! / Kommt vor sein Antlitz mit Jubel!", footnotes: ["95,2"] },
      { number: 3, text: "Erkennt: Der Herr allein ist Gott. / Er hat uns geschaffen, wir sind sein Eigentum, / sein Volk und die Herde seiner Weide.", footnotes: ["83,19; 95,7; Jes 64,7", "wir sind sein Eigentum: so nach einigen alten Übersetzungen. Nach anderen alten Übersetzungen: und nicht wir (haben uns erschaffen)."] },
      { number: 4, text: "Tretet mit Dank durch seine Tore ein! / Kommt mit Lobgesang in die Vorhöfe seines Tempels! / Dankt ihm, preist seinen Namen!" },
      { number: 5, text: "Denn der Herr ist gütig, / ewig währt seine Huld, / von Geschlecht zu Geschlecht seine Treue.", footnotes: ["107,1"] },
    ] },
    { number: 101, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Von Gnade und Recht will ich singen; / dir, o Herr, will ich spielen.", heading: "Die Vorsätze eines Königs", footnotes: ["7,18"] },
      { number: 2, text: "Ich will auf den Weg der Bewährten achten. / Wann kommst du zu mir? / Ich lebe in der Stille meines Hauses mit lauterem Herzen.", footnotes: ["26,11f", "in der Stille, wörtlich: inmitten."] },
      { number: 3, text: "Ich richte mein Auge nicht auf Schändliches; / ich hasse es, Unrecht zu tun, es soll nicht an mir haften." },
      { number: 4, text: "Falschheit sei meinem Herzen fern; / ich will das Böse nicht kennen." },
      { number: 5, text: "Wer den Nächsten heimlich verleumdet, / den bring ich zum Schweigen. Stolze Augen und hochmütige Herzen / kann ich nicht ertragen.", footnotes: ["Spr 21,4"] },
      { number: 6, text: "Meine Augen suchen die Treuen im Land; / sie sollen bei mir wohnen. / Wer auf rechten Wegen geht, der darf mir dienen.", footnotes: ["Spr 14,35"] },
      { number: 7, text: "In meinem Haus soll kein Betrüger wohnen; / kein Lügner kann vor meinen Augen bestehen." },
      { number: 8, text: "Morgen für Morgen spreche ich das Urteil / über die Frevler im Land, / um in der Stadt des Herrn alle auszurotten, die Unrecht tun.", footnotes: ["Jer 21,12; Zef 3,5; Spr 20,26", "Wörtlich: Morgen für Morgen will ich vernichten alle Frevler im Land."] },
    ] },
    { number: 102, verses: [
      { number: 1, text: "[Gebet eines Unglücklichen, wenn er in Verzweiflung ist und vor dem Herrn seine Sorge ausschüttet.]", heading: "Das Gebet eines Unglücklichen" },
      { number: 2, text: "Herr, höre mein Gebet! / Mein Schreien dringe zu dir." },
      { number: 3, text: "Verbirg dein Antlitz nicht vor mir! / Wenn ich in Not bin, wende dein Ohr mir zu! / Wenn ich dich anrufe, erhöre mich bald!", footnotes: ["31,3; 69,18; 143,7"] },
      { number: 4, text: "Meine Tage sind wie Rauch geschwunden, / meine Glieder wie von Feuer verbrannt.", footnotes: ["38,8f"] },
      { number: 5, text: "Versengt wie Gras und verdorrt ist mein Herz, / sodass ich vergessen habe, mein Brot zu essen." },
      { number: 6, text: "Vor lauter Stöhnen und Schreien / bin ich nur noch Haut und Knochen.", footnotes: ["Ijob 19,20", "Wörtlich: Vor lauter Jammern klebt mein Gebein am Fleisch."] },
      { number: 7, text: "Ich bin wie eine Dohle in der Wüste, / wie eine Eule in öden Ruinen." },
      { number: 8, text: "Ich liege wach und ich klage / wie ein einsamer Vogel auf dem Dach.", footnotes: ["und ich klage wie: Text korr.; H: und ich bin wie . . ."] },
      { number: 9, text: "Den ganzen Tag schmähen mich die Feinde; / die mich verhöhnen, nennen meinen Namen beim Fluchen.", footnotes: ["41,6"] },
      { number: 10, text: "Staub muss ich essen wie Brot, / mit Tränen mische ich meinen Trank;", footnotes: ["42,4"] },
      { number: 11, text: "denn auf mir lasten dein Zorn und dein Grimm. / Du hast mich hochgerissen und zu Boden geschleudert." },
      { number: 12, text: "Meine Tage schwinden dahin wie Schatten, / ich verdorre wie Gras.", footnotes: ["90,9f", "Text korr.; H: Meine Tage (sind) wie ein lang sich dehnender Schatten (d. h.: es geht dem Ende entgegen)."] },
      { number: 13, text: "Du aber, Herr, du thronst für immer und ewig, / dein Name dauert von Geschlecht zu Geschlecht.", footnotes: ["145,13; 135,13; Klgl 5,19"] },
      { number: 14, text: "Du wirst dich erheben, dich über Zion erbarmen; / denn es ist Zeit, ihm gnädig zu sein, die Stunde ist da.", footnotes: ["69,14"] },
      { number: 15, text: "An Zions Steinen hängt das Herz deiner Knechte, / um seine Trümmer tragen sie Leid." },
      { number: 16, text: "Dann fürchten die Völker den Namen des Herrn / und alle Könige der Erde deine Herrlichkeit." },
      { number: 17, text: "Denn der Herr baut Zion wieder auf / und erscheint in all seiner Herrlichkeit.", footnotes: ["51,20; Jes 60,1"] },
      { number: 18, text: "Er wendet sich dem Gebet der Verlassenen zu, / ihre Bitten verschmäht er nicht." },
      { number: 19, text: "Dies sei aufgeschrieben für das kommende Geschlecht, / damit das Volk, das noch erschaffen wird, den Herrn lobpreise.", footnotes: ["22,31f"] },
      { number: 20, text: "Denn der Herr schaut herab aus heiliger Höhe, / vom Himmel blickt er auf die Erde nieder;", footnotes: ["11,4; 33,13"] },
      { number: 21, text: "er will auf das Seufzen der Gefangenen hören / und alle befreien, die dem Tod geweiht sind,", footnotes: ["79,11"] },
      { number: 22, text: "damit sie den Namen des Herrn auf dem Zion verkünden / und sein Lob in Jerusalem," },
      { number: 23, text: "wenn sich dort Königreiche und Völker versammeln, / um den Herrn zu verehren.", footnotes: ["Jes 60,3-10; Lk 13,29f"] },
      { number: 24, text: "Er hat meine Kraft auf dem Weg gebrochen, / er hat meine Tage verkürzt.", footnotes: ["89,46"] },
      { number: 25, text: "Darum sage ich: Raff mich nicht weg in der Mitte des Lebens, / mein Gott, dessen Jahre Geschlecht um Geschlecht überdauern!", footnotes: ["55,24"] },
      { number: 26, text: "Vorzeiten hast du der Erde Grund gelegt, / die Himmel sind das Werk deiner Hände.", footnotes: ["(26-28) Jes 51,6-8; Hebr 1,10-12"] },
      { number: 27, text: "Sie werden vergehen, du aber bleibst; / sie alle zerfallen wie ein Gewand; du wechselst sie wie ein Kleid / und sie schwinden dahin." },
      { number: 28, text: "Du aber bleibst, der du bist, / und deine Jahre enden nie.", footnotes: ["Hebr 13,8"] },
      { number: 29, text: "Die Kinder deiner Knechte werden (in Sicherheit) wohnen, / ihre Nachkommen vor deinem Antlitz bestehen.", footnotes: ["69,36f"] },
    ] },
    { number: 103, verses: [
      { number: 1, text: "[Von David.] Lobe den Herrn, meine Seele, / und alles in mir seinen heiligen Namen!", heading: "Ein Loblied auf den gütigen und verzeihenden Gott" },
      { number: 2, text: "Lobe den Herrn, meine Seele, / und vergiss nicht, was er dir Gutes getan hat:", footnotes: ["Dtn 4,9"] },
      { number: 3, text: "der dir all deine Schuld vergibt / und all deine Gebrechen heilt,", footnotes: ["32,1; Ex 15,26"] },
      { number: 4, text: "der dein Leben vor dem Untergang rettet / und dich mit Huld und Erbarmen krönt," },
      { number: 5, text: "der dich dein Leben lang mit seinen Gaben sättigt; / wie dem Adler wird dir die Jugend erneuert.", footnotes: ["Ijob 33,25; Jes 40,31", "dein Leben lang: Text korr."] },
      { number: 6, text: "Der Herr vollbringt Taten des Heiles, / Recht verschafft er allen Bedrängten.", footnotes: ["146,7"] },
      { number: 7, text: "Er hat Mose seine Wege kundgetan, / den Kindern Israels seine Werke.", footnotes: ["90,16"] },
      { number: 8, text: "Der Herr ist barmherzig und gnädig, / langmütig und reich an Güte.", footnotes: ["116,5"] },
      { number: 9, text: "Er wird nicht immer zürnen, / nicht ewig im Groll verharren.", footnotes: ["Jes 57,16; Jer 3,12"] },
      { number: 10, text: "Er handelt an uns nicht nach unsern Sünden / und vergilt uns nicht nach unsrer Schuld." },
      { number: 11, text: "Denn so hoch der Himmel über der Erde ist, / so hoch ist seine Huld über denen, die ihn fürchten.", footnotes: ["36,6; Jes 55,9", "so hoch: Text korr.; H: so stark."] },
      { number: 12, text: "So weit der Aufgang entfernt ist vom Untergang, / so weit entfernt er die Schuld von uns." },
      { number: 13, text: "Wie ein Vater sich seiner Kinder erbarmt, / so erbarmt sich der Herr über alle, die ihn fürchten.", footnotes: ["145,9; Jdt 16,15; Lk 15,11-24"] },
      { number: 14, text: "Denn er weiß, was wir für Gebilde sind; / er denkt daran: Wir sind nur Staub.", footnotes: ["146,4"] },
      { number: 15, text: "Des Menschen Tage sind wie Gras, / er blüht wie die Blume des Feldes.", footnotes: ["90,5f"] },
      { number: 16, text: "Fährt der Wind darüber, ist sie dahin; / der Ort, wo sie stand, weiß von ihr nichts mehr.", footnotes: ["Ijob 7,10"] },
      { number: 17, text: "Doch die Huld des Herrn währt immer und ewig / für alle, die ihn fürchten und ehren; sein Heil erfahren noch Kinder und Enkel; /", footnotes: ["Ex 20,6; Lk 1,50"] },
      { number: 18, text: "alle, die seinen Bund bewahren, / an seine Gebote denken und danach handeln.", footnotes: ["25,10"] },
      { number: 19, text: "Der Herr hat seinen Thron errichtet im Himmel, / seine königliche Macht beherrscht das All." },
      { number: 20, text: "Lobt den Herrn, ihr seine Engel, / ihr starken Helden, die seine Befehle vollstrecken, / seinen Worten gehorsam!", footnotes: ["(20-22) 29,1; 148,2-4"] },
      { number: 21, text: "Lobt den Herrn, all seine Scharen, / seine Diener, die seinen Willen vollziehen!" },
      { number: 22, text: "Lobt den Herrn, all seine Werke, / an jedem Ort seiner Herrschaft! / Lobe den Herrn, meine Seele!", footnotes: ["145,10; Dan 3,57"] },
    ] },
    { number: 104, verses: [
      { number: 1, text: "Lobe den Herrn, meine Seele! / Herr, mein Gott, wie groß bist du! / Du bist mit Hoheit und Pracht bekleidet.", heading: "Ein Loblied auf den Schöpfer", footnotes: ["40,17"] },
      { number: 2, text: "Du hüllst dich in Licht wie in ein Kleid, / du spannst den Himmel aus wie ein Zelt.", footnotes: ["Ijob 9,8; Jes 40,22; 44,24"] },
      { number: 3, text: "Du verankerst die Balken deiner Wohnung im Wasser. / Du nimmst dir die Wolken zum Wagen, / du fährst einher auf den Flügeln des Sturmes.", footnotes: ["18,11; 68,5"] },
      { number: 4, text: "Du machst dir die Winde zu Boten / und lodernde Feuer zu deinen Dienern.", footnotes: ["Hebr 1,7"] },
      { number: 5, text: "Du hast die Erde auf Pfeiler gegründet; / in alle Ewigkeit wird sie nicht wanken.", footnotes: ["75,4"] },
      { number: 6, text: "Einst hat die Urflut sie bedeckt wie ein Kleid, / die Wasser standen über den Bergen." },
      { number: 7, text: "Sie wichen vor deinem Drohen zurück, / sie flohen vor der Stimme deines Donners.", footnotes: ["(7-9) Spr 8,29"] },
      { number: 8, text: "Da erhoben sich Berge und senkten sich Täler / an den Ort, den du für sie bestimmt hast." },
      { number: 9, text: "Du hast den Wassern eine Grenze gesetzt, / die dürfen sie nicht überschreiten; / nie wieder sollen sie die Erde bedecken.", footnotes: ["Ijob 38,8-11; Jer 5,22; Gen 9,11-15"] },
      { number: 10, text: "Du lässt die Quellen hervorsprudeln in den Tälern, / sie eilen zwischen den Bergen dahin.", footnotes: ["74,15"] },
      { number: 11, text: "Allen Tieren des Feldes spenden sie Trank, / die Wildesel stillen ihren Durst daraus." },
      { number: 12, text: "An den Ufern wohnen die Vögel des Himmels, / aus den Zweigen erklingt ihr Gesang.", footnotes: ["Ez 31,6-9"] },
      { number: 13, text: "Du tränkst die Berge aus deinen Kammern, / aus deinen Wolken wird die Erde satt.", footnotes: ["65,10f; Ijob 36,31", "Text korr.; H ist unverständlich."] },
      { number: 14, text: "Du lässt Gras wachsen für das Vieh, / auch Pflanzen für den Menschen, die er anbaut, damit er Brot gewinnt von der Erde /", footnotes: ["147,8f"] },
      { number: 15, text: "und Wein, der das Herz des Menschen erfreut, damit sein Gesicht von Öl erglänzt / und Brot das Menschenherz stärkt.", footnotes: ["Ri 9,13; Koh 10,19; Sir 31,27"] },
      { number: 16, text: "Die Bäume des Herrn trinken sich satt, / die Zedern des Libanon, die er gepflanzt hat." },
      { number: 17, text: "In ihnen bauen die Vögel ihr Nest, / auf den Zypressen nistet der Storch." },
      { number: 18, text: "Die hohen Berge gehören dem Steinbock, / dem Klippdachs bieten die Felsen Zuflucht." },
      { number: 19, text: "Du hast den Mond gemacht als Maß für die Zeiten, / die Sonne weiß, wann sie untergeht.", footnotes: ["74,16; Sir 43,6-8"] },
      { number: 20, text: "Du sendest Finsternis und es wird Nacht, / dann regen sich alle Tiere des Waldes." },
      { number: 21, text: "Die jungen Löwen brüllen nach Beute, / sie verlangen von Gott ihre Nahrung.", footnotes: ["17,12; Ijob 38,39"] },
      { number: 22, text: "Strahlt die Sonne dann auf, so schleichen sie heim / und lagern sich in ihren Verstecken.", footnotes: ["Ijob 37,8"] },
      { number: 23, text: "Nun geht der Mensch hinaus an sein Tagwerk, / an seine Arbeit bis zum Abend." },
      { number: 24, text: "Herr, wie zahlreich sind deine Werke! / Mit Weisheit hast du sie alle gemacht, / die Erde ist voll von deinen Geschöpfen.", footnotes: ["Spr 8,22-31"] },
      { number: 25, text: "Da ist das Meer, so groß und weit, / darin ein Gewimmel ohne Zahl: kleine und große Tiere.", footnotes: ["Sir 43,25", "so groß und weit, wörtlich: groß und weit nach beiden Seiten."] },
      { number: 26, text: "Dort ziehen die Schiffe dahin, / auch der Leviatan, den du geformt hast, um mit ihm zu spielen.", footnotes: ["107,23"] },
      { number: 27, text: "Sie alle warten auf dich, / dass du ihnen Speise gibst zur rechten Zeit.", footnotes: ["136,25; (27-28) 145,15f"] },
      { number: 28, text: "Gibst du ihnen, dann sammeln sie ein; / öffnest du deine Hand, werden sie satt an Gutem." },
      { number: 29, text: "Verbirgst du dein Gesicht, sind sie verstört; / nimmst du ihnen den Atem, so schwinden sie hin / und kehren zurück zum Staub der Erde.", footnotes: ["30,8; 146,4"] },
      { number: 30, text: "Sendest du deinen Geist aus, so werden sie alle erschaffen / und du erneuerst das Antlitz der Erde.", footnotes: ["Gen 2,7"] },
      { number: 31, text: "Ewig währe die Herrlichkeit des Herrn; / der Herr freue sich seiner Werke." },
      { number: 32, text: "Er blickt auf die Erde und sie erbebt; / er rührt die Berge an und sie rauchen.", footnotes: ["144,5"] },
      { number: 33, text: "Ich will dem Herrn singen, solange ich lebe, / will meinem Gott spielen, solange ich da bin.", footnotes: ["7,18; 146,2"] },
      { number: 34, text: "Möge ihm mein Dichten gefallen. / Ich will mich freuen am Herrn.", footnotes: ["19,15"] },
      { number: 35, text: "Doch die Sünder sollen von der Erde verschwinden / und es sollen keine Frevler mehr da sein. / Lobe den Herrn, meine Seele! / Halleluja!" },
    ] },
    { number: 105, verses: [
      { number: 1, text: "Dankt dem Herrn! Ruft seinen Namen an! / Macht unter den Völkern seine Taten bekannt!", heading: "Ein Loblied auf den Herrn der Geschichte", footnotes: ["(1-15) 1 Chr 16,8-22; Ps 108,4"] },
      { number: 2, text: "Singt ihm und spielt ihm, / sinnt nach über all seine Wunder!" },
      { number: 3, text: "Rühmt euch seines heiligen Namens! / Alle, die den Herrn suchen, sollen sich von Herzen freuen.", footnotes: ["Dtn 4,29"] },
      { number: 4, text: "Fragt nach dem Herrn und seiner Macht; / sucht sein Antlitz allezeit!", footnotes: ["27,8"] },
      { number: 5, text: "Denkt an die Wunder, die er getan hat, / an seine Zeichen und die Beschlüsse aus seinem Mund." },
      { number: 6, text: "Bedenkt es, ihr Nachkommen seines Knechtes Abraham, / ihr Kinder Jakobs, die er erwählt hat." },
      { number: 7, text: "Er, der Herr, ist unser Gott. / Seine Herrschaft umgreift die Erde." },
      { number: 8, text: "Ewig denkt er an seinen Bund, / an das Wort, das er gegeben hat für tausend Geschlechter,", footnotes: ["111,5; Dtn 7,9"] },
      { number: 9, text: "an den Bund, den er mit Abraham geschlossen, / an den Eid, den er Isaak geschworen hat.", footnotes: ["Gen 17,1-21; 26,3"] },
      { number: 10, text: "Er bestimmte ihn als Satzung für Jakob, / als ewigen Bund für Israel." },
      { number: 11, text: "Er sprach: Dir will ich Kanaan geben, / das Land, das dir als Erbe bestimmt ist.", footnotes: ["Gen 12,7; 15,18"] },
      { number: 12, text: "Als sie noch gering waren an Zahl, / nur wenige und fremd im Land,", footnotes: ["Dtn 26,5"] },
      { number: 13, text: "und noch zogen von Volk zu Volk, / von einem Reich zum andern," },
      { number: 14, text: "da ließ er sie von niemand bedrücken, / wies ihretwegen Könige zurecht:", footnotes: ["Gen 12,17; 20,3.7"] },
      { number: 15, text: "«Tastet meine Gesalbten nicht an, / tut meinen Propheten nichts zuleide!»" },
      { number: 16, text: "Dann aber rief er den Hunger ins Land, / entzog ihnen allen Vorrat an Brot.", footnotes: ["Gen 41,54"] },
      { number: 17, text: "Doch hatte er ihnen einen Mann vorausgesandt: / Josef wurde als Sklave verkauft.", footnotes: ["Gen 37,28; 45,5"] },
      { number: 18, text: "Man spannte seine Füße in Fesseln / und zwängte seinen Hals ins Eisen", footnotes: ["Gen 39,20"] },
      { number: 19, text: "bis zu der Zeit, als sein Wort sich erfüllte / und der Spruch des Herrn ihm Recht gab.", footnotes: ["(19-22) Gen 41,38-45"] },
      { number: 20, text: "Da sandte der König einen Boten und ließ ihn frei, / der Herrscher der Völker ließ ihn heraus." },
      { number: 21, text: "Er bestellte ihn zum Herrn über sein Haus, / zum Gebieter über seinen ganzen Besitz." },
      { number: 22, text: "Er sollte die Fürsten lenken nach seinem Sinn / und die Ältesten Weisheit lehren.", footnotes: ["Text korr. nach G, S und Vg."] },
      { number: 23, text: "Und Israel kam nach Ägypten, / Jakob wurde Gast im Lande Hams.", footnotes: ["Gen 46,1-7; 47,11"] },
      { number: 24, text: "Da mehrte Gott sein Volk gewaltig, / machte es stärker als das Volk der Bedrücker.", footnotes: ["Ex 1,7.12"] },
      { number: 25, text: "Er wandelte ihren Sinn zum Hass gegen sein Volk, / sodass sie an seinen Knechten tückisch handelten.", footnotes: ["Ex 1,9-11.15-22"] },
      { number: 26, text: "Dann sandte er Mose, seinen Knecht, / und Aaron, den Gott sich erwählte.", footnotes: ["Ex 3,10; 4,14-16"] },
      { number: 27, text: "Sie wirkten unter ihnen seine Zeichen, / im Lande Hams seine Wunder.", footnotes: ["Text korr. nach S."] },
      { number: 28, text: "Er sandte Finsternis, da wurde es dunkel; / doch achteten sie nicht auf sein Wort.", footnotes: ["Ex 10,21-29", "Text korr.; H: und sie widerstrebten nicht seinem Wort."] },
      { number: 29, text: "Er verwandelte ihre Gewässer in Blut / und ließ ihre Fische sterben.", footnotes: ["Ex 7,14-25"] },
      { number: 30, text: "Ihr Land wimmelte von Fröschen / bis hinein in den Palast des Königs.", footnotes: ["Ex 7,26 - 8,11"] },
      { number: 31, text: "Er gebot, da kamen Schwärme von Fliegen / und von Stechmücken über das ganze Gebiet.", footnotes: ["Ex 8,12-28"] },
      { number: 32, text: "Er schickte ihnen Hagel statt Regen, / flammendes Feuer auf ihr Land.", footnotes: ["(32-33) Ex 9,13-35"] },
      { number: 33, text: "Er zerschlug ihnen Weinstock und Feigenbaum / und knickte in ihrem Gebiet die Bäume um." },
      { number: 34, text: "Er gebot, da kamen Schwärme von Grillen / und Wanderheuschrecken in gewaltiger Zahl.", footnotes: ["(34-35) Ex 10,1-20"] },
      { number: 35, text: "Sie fraßen alles Grün in ihrem Land, / sie fraßen die Frucht ihrer Felder." },
      { number: 36, text: "Er erschlug im Land jede Erstgeburt, / die ganze Blüte der Jugend.", footnotes: ["78,51", "Wörtlich: den Erstling all ihrer Kraft."] },
      { number: 37, text: "Er führte sein Volk heraus mit Silber und Gold; / in seinen Stämmen fand sich kein Schwächling.", footnotes: ["Ex 12,35f"] },
      { number: 38, text: "Bei ihrem Auszug waren die Ägypter froh; / denn Schrecken vor ihnen hatte sie alle befallen.", footnotes: ["Ex 12,33"] },
      { number: 39, text: "Eine Wolke breitete er aus, um sie zu decken, / und Feuer, um die Nacht zu erleuchten.", footnotes: ["78,14"] },
      { number: 40, text: "Als sie ihn baten, schickte er Wachteln / und sättigte sie mit Brot vom Himmel.", footnotes: ["78,24; Ex 16,2-36; Weish 16,20", "Text korr.; H: Als er bat."] },
      { number: 41, text: "Er öffnete den Felsen und Wasser entquoll ihm, / wie ein Strom floss es dahin in der Wüste.", footnotes: ["78,15; Ex 17,1-7"] },
      { number: 42, text: "Denn er dachte an sein heiliges Wort / und an Abraham, seinen Knecht." },
      { number: 43, text: "Er führte sein Volk heraus in Freude, / seine Erwählten in Jubel." },
      { number: 44, text: "Er gab ihnen die Länder der Völker / und ließ sie den Besitz der Nationen gewinnen,", footnotes: ["(44-45) Dtn 4,37-40"] },
      { number: 45, text: "damit sie seine Satzungen hielten / und seine Gebote befolgten. / Halleluja!" },
    ] },
    { number: 106, verses: [
      { number: 1, text: "Halleluja! Danket dem Herrn; denn er ist gütig, / denn seine Huld währt ewig.", heading: "Gottes Güte - Israels Undank", footnotes: ["107,1"] },
      { number: 2, text: "Wer kann die großen Taten des Herrn erzählen, / all seinen Ruhm verkünden?", footnotes: ["Joh 21,25"] },
      { number: 3, text: "Wohl denen, die das Recht bewahren / und zu jeder Zeit tun, was gerecht ist.", footnotes: ["Jes 56,1f"] },
      { number: 4, text: "Denk an mich, Herr, aus Liebe zu deinem Volk, / such mich auf und bring mir Hilfe!" },
      { number: 5, text: "Lass mich das Glück deiner Erwählten schauen, / an der Freude deines Volkes mich freuen, / damit ich gemeinsam mit deinem Erbe mich rühmen kann." },
      { number: 6, text: "Wir haben zusammen mit unsern Vätern gesündigt, / wir haben Unrecht getan und gefrevelt.", footnotes: ["Lev 26,40; Dan 9,5"] },
      { number: 7, text: "Unsre Väter in Ägypten begriffen deine Wunder nicht, / dachten nicht an deine reiche Huld / und trotzten dem Höchsten am Schilfmeer.", footnotes: ["78,11-17", "dem Höchsten am Schilfmeer: Text korr.; H: am Meer, am Schilfmeer."] },
      { number: 8, text: "Er aber hat sie gerettet, um seinen Namen zu ehren / und seine Macht zu bekunden.", footnotes: ["Ez 39,25"] },
      { number: 9, text: "Er bedrohte das Schilfmeer, da wurde es trocken; / wie durch eine Steppe führte er sie durch die Fluten.", footnotes: ["Ex 14,21f; Jes 63,11-14"] },
      { number: 10, text: "Er rettete sie aus der Hand derer, die sie hassten, / erlöste sie aus der Gewalt des Feindes." },
      { number: 11, text: "Ihre Bedränger bedeckte das Wasser, / nicht einer von ihnen blieb übrig." },
      { number: 12, text: "Nun glaubten sie Gottes Worten / und sangen laut seinen Lobpreis.", footnotes: ["Ex 14,31; 15,1-21"] },
      { number: 13, text: "Doch sie vergaßen schnell seine Taten, / wollten auf seinen Ratschluss nicht warten." },
      { number: 14, text: "Sie wurden in der Wüste begehrlich / und versuchten Gott in der Öde.", footnotes: ["78,18; Num 11,4-6"] },
      { number: 15, text: "Er gab ihnen, was sie von ihm verlangten; / dann aber erfasste sie Ekel und Überdruss." },
      { number: 16, text: "Sie wurden im Lager eifersüchtig auf Mose / und auf Aaron, den Heiligen des Herrn.", footnotes: ["Num 16,1-3"] },
      { number: 17, text: "Die Erde tat sich auf, sie verschlang den Datan / und bedeckte die Rotte Abírams.", footnotes: ["(17-18) Num 16,31-35"] },
      { number: 18, text: "Feuer verbrannte die Rotte, / Flammen verzehrten die Frevler." },
      { number: 19, text: "Sie machten am Horeb ein Kalb / und warfen sich vor dem Gussbild nieder.", footnotes: ["(19-20) Ex 32,1-6"] },
      { number: 20, text: "Die Herrlichkeit Gottes tauschten sie ein / gegen das Bild eines Stieres, der Gras frisst.", footnotes: ["Jer 2,11; Röm 1,23"] },
      { number: 21, text: "Sie vergaßen Gott, ihren Retter, / der einst in Ägypten Großes vollbrachte,", footnotes: ["78,42"] },
      { number: 22, text: "Wunder im Lande Hams, / Furcht erregende Taten am Schilfmeer." },
      { number: 23, text: "Da fasste er einen Plan und er hätte sie vernichtet, / wäre nicht Mose, sein Erwählter, für sie in die Bresche gesprungen, / sodass Gott sie im Zorn nicht vertilgte.", footnotes: ["Ex 32,11-14"] },
      { number: 24, text: "Sie verschmähten das köstliche Land; / sie glaubten seinen Verheißungen nicht.", footnotes: ["Num 14,2-4"] },
      { number: 25, text: "In ihren Zelten murrten sie, / hörten nicht auf die Stimme des Herrn." },
      { number: 26, text: "Da erhob er gegen sie die Hand, / um sie niederzustrecken noch in der Wüste,", footnotes: ["Num 14,23.29"] },
      { number: 27, text: "ihre Nachkommen unter die Völker zu zerstreuen, / sie in alle Welt zu versprengen.", footnotes: ["Lev 26,33; Ez 20,23", "zu zerstreuen: Text korr.; H: niederzustrecken, (wie in V. 26b)."] },
      { number: 28, text: "Sie hängten sich an den Báal-Pegór / und aßen die Opfer der toten Götzen.", footnotes: ["Num 25,1-3", "Wörtlich: die Opfer der Toten."] },
      { number: 29, text: "Sie erbitterten Gott mit ihren schändlichen Taten, / bis über sie eine schwere Plage kam." },
      { number: 30, text: "Pinhas trat auf und hielt Gericht; / so wurde die Plage abgewandt.", footnotes: ["Num 25,7-11"] },
      { number: 31, text: "Das rechnete Gott ihm als Gerechtigkeit an, / ihm und seinem Geschlecht für immer und ewig.", footnotes: ["Num 25,12f"] },
      { number: 32, text: "An den Wassern von Meríba reizten sie Gottes Zorn, / ihretwegen erging es Mose übel.", footnotes: ["Num 20,2-13"] },
      { number: 33, text: "Denn sie hatten seinen Geist erbittert, / sein Mund redete unbedacht." },
      { number: 34, text: "Sie rotteten die Völker nicht aus, / wie ihnen der Herr einst befahl.", footnotes: ["(34-36) Dtn 7,1-5; Ri 1,18 - 2,5"] },
      { number: 35, text: "Sie vermischten sich mit den Heiden / und lernten von ihren Taten." },
      { number: 36, text: "Sie dienten ihren Götzen; / die wurden ihnen zur Falle.", footnotes: ["Ri 2,11-13"] },
      { number: 37, text: "Sie brachten ihre Söhne und Töchter dar / als Opfer für die Dämonen.", footnotes: ["(37-38) Lev 18,21"] },
      { number: 38, text: "Sie vergossen schuldloses Blut, / das Blut ihrer Söhne und Töchter, die sie den Götzen Kanaans opferten; / so wurde das Land durch Blutschuld entweiht.", footnotes: ["(38-39) Num 35,33f"] },
      { number: 39, text: "Sie wurden durch ihre Taten unrein / und brachen Gott mit ihrem Tun die Treue." },
      { number: 40, text: "Der Zorn des Herrn entbrannte gegen sein Volk, / er empfand Abscheu gegen sein Erbe.", footnotes: ["(40-42) Ri 2,14f"] },
      { number: 41, text: "Er gab sie in die Hand der Völker / und die sie hassten, beherrschten sie." },
      { number: 42, text: "Ihre Feinde bedrängten sie, / unter ihre Hand mussten sie sich beugen." },
      { number: 43, text: "Oft hat er sie befreit; / sie aber trotzten seinem Beschluss / und versanken in ihrer Schuld.", footnotes: ["Ri 2,16", "Text korr.; H: ihrem Beschluss."] },
      { number: 44, text: "Doch als er ihr Flehen hörte, / sah er auf ihre Not" },
      { number: 45, text: "und dachte ihnen zuliebe an seinen Bund; / er hatte Mitleid in seiner großen Gnade.", footnotes: ["Lev 26,42"] },
      { number: 46, text: "Bei denen, die sie verschleppten, / ließ er sie Erbarmen erfahren.", footnotes: ["Esra 9,9"] },
      { number: 47, text: "Hilf uns, Herr, unser Gott, / führe uns aus den Völkern zusammen! Wir wollen deinen heiligen Namen preisen, / uns rühmen, weil wir dich loben dürfen.", footnotes: ["(47-48) 1 Chr 16,35f"] },
      { number: 48, text: "Gepriesen sei der Herr, der Gott Israels, / vom Anfang bis ans Ende der Zeiten. / Alles Volk soll sprechen: Amen. / Halleluja!" },
    ] },
    { number: 107, verses: [
      { number: 1, text: "Danket dem Herrn, denn er ist gütig, / denn seine Huld währt ewig.", heading: "Das fünfte Buch | Ein Danklied der Erlösten", footnotes: ["106,1; 118,1.29; 136,1f.26"] },
      { number: 2, text: "So sollen alle sprechen, die vom Herrn erlöst sind, / die er von den Feinden befreit hat." },
      { number: 3, text: "Denn er hat sie aus den Ländern gesammelt, / vom Aufgang und Niedergang, vom Norden und Süden.", footnotes: ["Jes 43,5f"] },
      { number: 4, text: "Sie, die umherirrten in der Wüste, im Ödland, / und den Weg zur wohnlichen Stadt nicht fanden,", footnotes: ["Dtn 8,15"] },
      { number: 5, text: "die Hunger litten und Durst, / denen das Leben dahinschwand,", footnotes: ["Jes 49,10"] },
      { number: 6, text: "die dann in ihrer Bedrängnis schrien zum Herrn, / die er ihren Ängsten entriss", footnotes: ["50,15"] },
      { number: 7, text: "und die er führte auf geraden Wegen, / sodass sie zur wohnlichen Stadt gelangten:" },
      { number: 8, text: "sie alle sollen dem Herrn danken für seine Huld, / für sein wunderbares Tun an den Menschen," },
      { number: 9, text: "weil er die lechzende Seele gesättigt, / die hungernde Seele mit seinen Gaben erfüllt hat.", footnotes: ["Jes 55,1; Lk 1,53"] },
      { number: 10, text: "Sie, die saßen in Dunkel und Finsternis, / gefangen in Elend und Eisen,", footnotes: ["Ijob 36,8f"] },
      { number: 11, text: "die den Worten Gottes getrotzt / und verachtet hatten den Ratschluss des Höchsten,", footnotes: ["106,43; Spr 1,25"] },
      { number: 12, text: "deren Herz er durch Mühsal beugte, / die stürzten und denen niemand beistand," },
      { number: 13, text: "die dann in ihrer Bedrängnis schrien zum Herrn, / die er ihren Ängsten entriss," },
      { number: 14, text: "die er herausführte aus Dunkel und Finsternis / und deren Fesseln er zerbrach:", footnotes: ["Jes 42,7.16"] },
      { number: 15, text: "sie alle sollen dem Herrn danken für seine Huld, / für sein wunderbares Tun an den Menschen," },
      { number: 16, text: "weil er die ehernen Tore zerbrochen, / die eisernen Riegel zerschlagen hat.", footnotes: ["Jes 45,2"] },
      { number: 17, text: "Sie, die dahinsiechten in ihrem sündhaften Treiben, / niedergebeugt wegen ihrer schweren Vergehen,", footnotes: ["Text korr.; H: die Toren in ihrem sündhaften Treiben."] },
      { number: 18, text: "denen vor jeder Speise ekelte, / die nahe waren den Pforten des Todes,", footnotes: ["Ijob 6,6f; 33,20"] },
      { number: 19, text: "die dann in ihrer Bedrängnis schrien zum Herrn, / die er ihren Ängsten entriss," },
      { number: 20, text: "denen er sein Wort sandte, die er heilte / und vom Verderben befreite:", footnotes: ["Weish 16,12; Mt 8,8", "vom Verderben, wörtlich: aus ihren Gruben."] },
      { number: 21, text: "sie alle sollen dem Herrn danken für seine Huld, / für sein wunderbares Tun an den Menschen." },
      { number: 22, text: "Sie sollen ihm Dankopfer weihen, / mit Jubel seine Taten verkünden." },
      { number: 23, text: "Sie, die mit Schiffen das Meer befuhren / und Handel trieben auf den großen Wassern,", footnotes: ["(23-24) Sir 43,24f"] },
      { number: 24, text: "die dort die Werke des Herrn bestaunten, / seine Wunder in der Tiefe des Meeres" },
      { number: 25, text: "- Gott gebot und ließ den Sturmwind aufstehn, / der hoch die Wogen türmte -,", footnotes: ["Jona 1,4"] },
      { number: 26, text: "die zum Himmel emporstiegen / und hinabfuhren in die tiefste Tiefe, / sodass ihre Seele in der Not verzagte," },
      { number: 27, text: "die wie Trunkene wankten und schwankten, / am Ende waren mit all ihrer Weisheit,", footnotes: ["Spr 23,34"] },
      { number: 28, text: "die dann in ihrer Bedrängnis schrien zum Herrn, / die er ihren Ängsten entriss", footnotes: ["Jona 1,14f"] },
      { number: 29, text: "- er machte aus dem Sturm ein Säuseln, / sodass die Wogen des Meeres schwiegen -,", footnotes: ["65,8; Mt 8,26"] },
      { number: 30, text: "die sich freuten, dass die Wogen sich legten / und er sie zum ersehnten Hafen führte:" },
      { number: 31, text: "sie alle sollen dem Herrn danken für seine Huld, / für sein wunderbares Tun an den Menschen." },
      { number: 32, text: "Sie sollen ihn in der Gemeinde des Volkes rühmen, / ihn loben im Kreis der Alten.", footnotes: ["22,23"] },
      { number: 33, text: "Er machte Ströme zur dürren Wüste, / Oasen zum dürstenden Ödland,", footnotes: ["Jes 42,15; 50,2"] },
      { number: 34, text: "fruchtbares Land zur salzigen Steppe; / denn seine Bewohner waren böse.", footnotes: ["Gen 19,24-26; Dtn 29,22; Sir 39,23"] },
      { number: 35, text: "Er machte die Wüste zum Wasserteich, / verdorrtes Land zu Oasen.", footnotes: ["Jes 41,18"] },
      { number: 36, text: "Dort siedelte er Hungernde an, / sie gründeten wohnliche Städte.", footnotes: ["Ez 36,35"] },
      { number: 37, text: "Sie bestellten Felder, pflanzten Reben / und erzielten reiche Ernten.", footnotes: ["Jes 65,21"] },
      { number: 38, text: "Er segnete sie, sodass sie sich gewaltig vermehrten, / gab ihnen große Mengen an Vieh.", footnotes: ["Dtn 7,13f"] },
      { number: 39, text: "Dann aber wurden sie geringer an Zahl, / gebeugt unter der Last von Leid und Kummer." },
      { number: 40, text: "Er goss über die Edlen Verachtung aus, / ließ sie umherirren in wegloser Wüste.", footnotes: ["Ijob 12,21.24"] },
      { number: 41, text: "Die Armen hob er aus dem Elend empor / und vermehrte ihre Sippen, einer Herde gleich.", footnotes: ["113,7"] },
      { number: 42, text: "Die Redlichen sehn es und freuen sich, / doch alle bösen Menschen verstummen.", footnotes: ["Ijob 22,19; 5,16; Ps 63,12"] },
      { number: 43, text: "Wer ist weise und beachtet das alles, / wer begreift die reiche Huld des Herrn?" },
    ] },
    { number: 108, verses: [
      { number: 1, text: "[Ein Lied. Ein Psalm Davids.]", heading: "Gott, Hilfe und Schutz seines Volkes" },
      { number: 2, text: "Mein Herz ist bereit, o Gott, / mein Herz ist bereit, / ich will dir singen und spielen. Wach auf, meine Seele! /", footnotes: ["(2-6) 57,8-12", "Ergänzt aus 57,8. Text korr. nach 57,9."] },
      { number: 3, text: "Wacht auf, Harfe und Saitenspiel! / Ich will das Morgenrot wecken." },
      { number: 4, text: "Ich will dich vor den Völkern preisen, Herr, / dir vor den Nationen lobsingen.", footnotes: ["9,12; 18,50; 57,10; 96,3; 105,1"] },
      { number: 5, text: "Denn deine Güte reicht, so weit der Himmel ist, / deine Treue, so weit die Wolken ziehn.", footnotes: ["36,6", "Text korr. nach 57,11."] },
      { number: 6, text: "Erheb dich über die Himmel, o Gott! / Deine Herrlichkeit erscheine über der ganzen Erde." },
      { number: 7, text: "Hilf mit deiner Rechten, erhöre uns, / damit die gerettet werden, die du so sehr liebst.", footnotes: ["(7-14) 60,7-14", "Text korr. nach 60,7."] },
      { number: 8, text: "Gott hat in seinem Heiligtum gesprochen: / «Ich will triumphieren, will Sichem verteilen / und das Tal von Sukkot vermessen." },
      { number: 9, text: "Mein ist Gilead, mein auch Manasse, / Efraim ist der Helm auf meinem Haupt, / Juda mein Herrscherstab." },
      { number: 10, text: "Doch Moab ist mein Waschbecken, / auf Edom werfe ich meinen Schuh, / ich triumphiere über das Land der Philister.»" },
      { number: 11, text: "Wer führt mich hin zu der befestigten Stadt, / wer wird mich nach Edom geleiten?" },
      { number: 12, text: "Gott, hast denn du uns verworfen? / Du ziehst ja nicht aus, o Gott, mit unsern Heeren." },
      { number: 13, text: "Bring uns doch Hilfe im Kampf mit dem Feind! / Denn die Hilfe von Menschen ist nutzlos." },
      { number: 14, text: "Mit Gott werden wir Großes vollbringen; / er selbst wird unsere Feinde zertreten." },
    ] },
    { number: 109, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.] Gott, den ich lobe, schweig doch nicht! /", heading: "Bitte um Hilfe gegen erbarmungslose Feinde", footnotes: ["35,22; 83,2", "Dieser Psalm ist ein so genannter Fluchpsalm. Er ist für Fromme gedacht, die, vielleicht wegen Bestechlichkeit der Richter, nicht hoffen können, in einem Gerichtsverfahren Recht zu bekommen. VV. 2-5 und 21-31 setzen mehrere, VV. 6-20 nur einen Prozessgegner voraus; das deutet darauf hin, dass der Verfasser in VV. 6-20 einen bereits vorgeformten Text eingearbeitet hat. Die schrecklichen Flüche erklären sich als äußerstes Mittel der Notwehr gegen übermächtige Feinde, die nur durch einen Appell an Gottes Gerechtigkeit abgewehrt werden können."] },
      { number: 2, text: "Denn ein Mund voll Frevel, ein Lügenmaul hat sich gegen mich aufgetan. Sie reden zu mir mit falscher Zunge, /", footnotes: ["31,19"] },
      { number: 3, text: "umgeben mich mit Worten voll Hass / und bekämpfen mich ohne Grund." },
      { number: 4, text: "Sie befeinden mich, während ich für sie bete, /", footnotes: ["Sie befeinden mich: H fügt hinzu: für meine Liebe."] },
      { number: 5, text: "sie vergelten mir Gutes mit Bösem, / mit Hass meine Liebe.", footnotes: ["(4-5) Spr 17,13; Ps 35,12f"] },
      { number: 6, text: "Sein Frevel stehe gegen ihn auf als Zeuge, / ein Ankläger trete an seine Seite." },
      { number: 7, text: "Aus dem Gericht gehe er verurteilt hervor, / selbst sein Gebet werde zur Sünde." },
      { number: 8, text: "Nur gering sei die Zahl seiner Tage, / sein Amt soll ein andrer erhalten.", footnotes: ["Apg 1,20"] },
      { number: 9, text: "Seine Kinder sollen zu Waisen werden / und seine Frau zur Witwe.", footnotes: ["Ex 22,23; Jer 18,21"] },
      { number: 10, text: "Unstet sollen seine Kinder umherziehen und betteln, / aus den Trümmern ihres Hauses vertrieben.", footnotes: ["Ijob 5,4f", "Text korr."] },
      { number: 11, text: "Sein Gläubiger reiße all seinen Besitz an sich, / Fremde sollen plündern, was er erworben hat.", footnotes: ["Ijob 20,18"] },
      { number: 12, text: "Niemand sei da, der ihm die Gunst bewahrt, / keiner, der sich der Waisen erbarmt.", footnotes: ["(12-13) Jes 14,21"] },
      { number: 13, text: "Seine Nachkommen soll man vernichten, / im nächsten Geschlecht schon erlösche sein Name.", footnotes: ["21,11", "sein Name: Text korr.; H: ihr Name."] },
      { number: 14, text: "Der Herr denke an die Schuld seiner Väter, / ungetilgt bleibe die Sünde seiner Mutter.", footnotes: ["Ex 20,5"] },
      { number: 15, text: "Ihre Schuld stehe dem Herrn allzeit vor Augen, / ihr Andenken lösche er aus auf Erden.", footnotes: ["90,8; 34,17"] },
      { number: 16, text: "Denn dieser Mensch dachte nie daran, Gnade zu üben; / er verfolgte den Gebeugten und Armen / und wollte den Verzagten töten." },
      { number: 17, text: "Er liebte den Fluch - der komme über ihn; / er verschmähte den Segen der bleibe ihm fern." },
      { number: 18, text: "Er zog den Fluch an wie ein Gewand; / der dringe wie Wasser in seinen Leib, / wie Öl in seine Glieder.", footnotes: ["Num 5,24"] },
      { number: 19, text: "Er werde für ihn wie das Kleid, in das er sich hüllt, / wie der Gürtel, der ihn allzeit umschließt." },
      { number: 20, text: "So lohne der Herr es denen, die mich verklagen, / und denen, die Böses gegen mich reden." },
      { number: 21, text: "Du aber, Herr und Gebieter, / handle an mir, wie es deinem Namen entspricht, / reiß mich heraus in deiner gütigen Huld!" },
      { number: 22, text: "Denn ich bin arm und gebeugt, / mir bebt das Herz in der Brust." },
      { number: 23, text: "Wie ein flüchtiger Schatten schwinde ich dahin; / sie schütteln mich wie eine Heuschrecke ab.", footnotes: ["39,6f"] },
      { number: 24, text: "Mir wanken die Knie vom Fasten, / mein Leib nimmt ab und wird mager." },
      { number: 25, text: "Ich wurde für sie zum Spott und zum Hohn, / sie schütteln den Kopf, wenn sie mich sehen.", footnotes: ["41,6"] },
      { number: 26, text: "Hilf mir, Herr, mein Gott, / in deiner Huld errette mich!" },
      { number: 27, text: "Sie sollen erkennen, dass deine Hand dies vollbracht hat, / dass du, o Herr, es getan hast.", footnotes: ["64,10"] },
      { number: 28, text: "Mögen sie fluchen - du wirst segnen. / Meine Gegner sollen scheitern, dein Knecht aber darf sich freuen.", footnotes: ["Jes 65,13f; Lk 6,27f", "Text korr.; H: Sie treten auf, aber sie scheitern."] },
      { number: 29, text: "Meine Ankläger sollen sich bedecken mit Schmach, / wie in einen Mantel sich in Schande hüllen.", footnotes: ["Jer 20,11"] },
      { number: 30, text: "Ich will den Herrn preisen mit lauter Stimme, / in der Menge ihn loben.", footnotes: ["22,23"] },
      { number: 31, text: "Denn er steht dem Armen zur Seite, / um ihn vor falschen Richtern zu retten." },
    ] },
    { number: 110, verses: [
      { number: 1, text: "[Ein Psalm Davids.] So spricht der Herr zu meinem Herrn: / Setze dich mir zur Rechten / und ich lege dir deine Feinde als Schemel unter die Füße.", heading: "Die Einsetzung des priesterlichen Königs auf dem Zion", footnotes: ["Mt 22,44; Apg 2,34f; Hebr 1,13; 8,1; 10,12f; 12,2"] },
      { number: 2, text: "Vom Zion strecke der Herr das Zepter deiner Macht aus: / «Herrsche inmitten deiner Feinde!»" },
      { number: 3, text: "Dein ist die Herrschaft am Tage deiner Macht / (wenn du erscheinst) in heiligem Schmuck; ich habe dich gezeugt noch vor dem Morgenstern, / wie den Tau in der Frühe.", footnotes: ["2,7", "H ist schwer verständlich; die Übersetzung ist ein Versuch nach G."] },
      { number: 4, text: "Der Herr hat geschworen und nie wird's ihn reuen: / «Du bist Priester auf ewig nach der Ordnung Melchisedeks.»", footnotes: ["Gen 14,18; Hebr 5,6"] },
      { number: 5, text: "Der Herr steht dir zur Seite; / er zerschmettert Könige am Tage seines Zornes.", footnotes: ["(5-6) 2,9"] },
      { number: 6, text: "Er hält Gericht unter den Völkern, er häuft die Toten, / die Häupter zerschmettert er weithin auf Erden." },
      { number: 7, text: "Er trinkt aus dem Bach am Weg; / so kann er (von neuem) das Haupt erheben." },
    ] },
    { number: 111, verses: [
      { number: 1, text: "Halleluja! Den Herrn will ich preisen von ganzem Herzen / im Kreis der Frommen, inmitten der Gemeinde.", heading: "Ein Preislied auf die Wundertaten des Herrn", footnotes: ["22,23"] },
      { number: 2, text: "Groß sind die Werke des Herrn, / kostbar allen, die sich an ihnen freuen." },
      { number: 3, text: "Er waltet in Hoheit und Pracht, / seine Gerechtigkeit hat Bestand für immer." },
      { number: 4, text: "Er hat ein Gedächtnis an seine Wunder gestiftet, / der Herr ist gnädig und barmherzig.", footnotes: ["Ex 12,1-28; Ps 116,5"] },
      { number: 5, text: "Er gibt denen Speise, die ihn fürchten, / an seinen Bund denkt er auf ewig.", footnotes: ["145,15; 105,8"] },
      { number: 6, text: "Er hat seinem Volk seine machtvollen Taten kundgetan, / um ihm das Erbe der Völker zu geben." },
      { number: 7, text: "Die Werke seiner Hände sind gerecht und beständig, / all seine Gebote sind verlässlich." },
      { number: 8, text: "Sie stehen fest für immer und ewig, / geschaffen in Treue und Redlichkeit." },
      { number: 9, text: "Er gewährte seinem Volk Erlösung / und bestimmte seinen Bund für ewige Zeiten. / Furcht gebietend ist sein Name und heilig.", footnotes: ["130,8; Jer 32,40"] },
      { number: 10, text: "Die Furcht des Herrn ist der Anfang der Weisheit; / alle, die danach leben, sind klug. / Sein Ruhm hat Bestand für immer.", footnotes: ["Spr 1,7; 9,10"] },
    ] },
    { number: 112, verses: [
      { number: 1, text: "Halleluja! Wohl dem Mann, der den Herrn fürchtet und ehrt / und sich herzlich freut an seinen Geboten.", heading: "Der Segen der Gottesfurcht", footnotes: ["1,1f; 128,1"] },
      { number: 2, text: "Seine Nachkommen werden mächtig im Land, / das Geschlecht der Redlichen wird gesegnet.", footnotes: ["Spr 20,7"] },
      { number: 3, text: "Wohlstand und Reichtum füllen sein Haus, / sein Heil hat Bestand für immer.", footnotes: ["128,2"] },
      { number: 4, text: "Den Redlichen erstrahlt im Finstern ein Licht: / der Gnädige, Barmherzige und Gerechte.", footnotes: ["97,11; 116,5"] },
      { number: 5, text: "Wohl dem Mann, der gütig und zum Helfen bereit ist, / der das Seine ordnet, wie es recht ist.", footnotes: ["Ijob 29,12f"] },
      { number: 6, text: "Niemals gerät er ins Wanken; / ewig denkt man an den Gerechten.", footnotes: ["Spr 10,7"] },
      { number: 7, text: "Er fürchtet sich nicht vor Verleumdung; / sein Herz ist fest, er vertraut auf den Herrn." },
      { number: 8, text: "Sein Herz ist getrost, er fürchtet sich nie; / denn bald wird er herabschauen auf seine Bedränger.", footnotes: ["54,9"] },
      { number: 9, text: "Reichlich gibt er den Armen, / sein Heil hat Bestand für immer; / er ist mächtig und hoch geehrt.", footnotes: ["Spr 22,9; 2 Kor 9,9"] },
      { number: 10, text: "Voll Verdruss sieht es der Frevler, / er knirscht mit den Zähnen und geht zugrunde. / Zunichte werden die Wünsche der Frevler." },
    ] },
    { number: 113, verses: [
      { number: 1, text: "Halleluja! Lobet, ihr Knechte des Herrn, / lobt den Namen des Herrn!", heading: "Ein Loblied auf Gottes Hoheit und Huld", footnotes: ["135,1"] },
      { number: 2, text: "Der Name des Herrn sei gepriesen / von nun an bis in Ewigkeit." },
      { number: 3, text: "Vom Aufgang der Sonne bis zum Untergang / sei der Name des Herrn gelobt." },
      { number: 4, text: "Der Herr ist erhaben über alle Völker, / seine Herrlichkeit überragt die Himmel.", footnotes: ["95,3; 148,13"] },
      { number: 5, text: "Wer gleicht dem Herrn, unserm Gott, / im Himmel und auf Erden,", footnotes: ["35,10", "Die beiden Verse sind aus sachlichen Gründen folgendermaßen umgestellt: 5a/6b und 5b/6a."] },
      { number: 6, text: "ihm, der in der Höhe thront, / der hinabschaut in die Tiefe,", footnotes: ["(6-7) Jes 57,15"] },
      { number: 7, text: "der den Schwachen aus dem Staub emporhebt / und den Armen erhöht, der im Schmutz liegt?", footnotes: ["(7-8) 1 Sam 2,8"] },
      { number: 8, text: "Er gibt ihm einen Sitz bei den Edlen, / bei den Edlen seines Volkes.", footnotes: ["ihm: Text korr. nach G, S, Vg; H: mir."] },
      { number: 9, text: "Die Frau, die kinderlos war, lässt er im Hause wohnen; / sie wird Mutter und freut sich an ihren Kindern. / Halleluja!", footnotes: ["1 Sam 2,5; Jes 54,1; Lk 1,57f"] },
    ] },
    { number: 114, verses: [
      { number: 1, text: "Als Israel aus Ägypten auszog, / Jakobs Haus aus dem Volk mit fremder Sprache,", heading: "Ein Lobpreis auf die Befreiung Israels", footnotes: ["Ex 12,41"] },
      { number: 2, text: "da wurde Juda Gottes Heiligtum, / Israel das Gebiet seiner Herrschaft.", footnotes: ["Ex 19,6; Jer 2,3; Dtn 7,6"] },
      { number: 3, text: "Das Meer sah es und floh, / der Jordan wich zurück.", footnotes: ["66,6"] },
      { number: 4, text: "Die Berge hüpften wie Widder, / die Hügel wie junge Lämmer.", footnotes: ["29,6"] },
      { number: 5, text: "Was ist mit dir, Meer, dass du fliehst, / und mit dir, Jordan, dass du zurückweichst?" },
      { number: 6, text: "Ihr Berge, was hüpft ihr wie Widder, / und ihr Hügel, wie junge Lämmer?", footnotes: ["(6-7) 68,9"] },
      { number: 7, text: "Vor dem Herrn erbebe, du Erde, / vor dem Antlitz des Gottes Jakobs," },
      { number: 8, text: "der den Fels zur Wasserflut wandelt / und Kieselgestein zu quellendem Wasser.", footnotes: ["78,15f; Jes 48,21"] },
    ] },
    { number: 115, verses: [
      { number: 1, text: "Nicht uns, o Herr, bring zu Ehren, / nicht uns, sondern deinen Namen, / in deiner Huld und Treue!", heading: "Der Gott Israels und die Götter der anderen Völker", footnotes: ["Ez 36,22f"] },
      { number: 2, text: "Warum sollen die Völker sagen: / «Wo ist denn ihr Gott?»", footnotes: ["42,11"] },
      { number: 3, text: "Unser Gott ist im Himmel; / alles, was ihm gefällt, das vollbringt er.", footnotes: ["135,6; Ijob 23,13; Weish 12,18"] },
      { number: 4, text: "Die Götzen der Völker sind nur Silber und Gold, / ein Machwerk von Menschenhand.", footnotes: ["(4-8) 135,15-18; Dtn 4,28; Weish 15,15; Jes 44,9-11; Bar 6,3.7", "Wörtlich: Ihre Götzen sind nur Silber und Gold."] },
      { number: 5, text: "Sie haben einen Mund und reden nicht, / Augen und sehen nicht;" },
      { number: 6, text: "sie haben Ohren und hören nicht, / eine Nase und riechen nicht;" },
      { number: 7, text: "mit ihren Händen können sie nicht greifen, / mit den Füßen nicht gehen, / sie bringen keinen Laut hervor aus ihrer Kehle." },
      { number: 8, text: "Die sie gemacht haben, sollen ihrem Machwerk gleichen, / alle, die den Götzen vertrauen." },
      { number: 9, text: "Israel, vertrau auf den Herrn! / Er ist für euch Helfer und Schild.", footnotes: ["(9-11) 33,20; 3,4; 118,2-4"] },
      { number: 10, text: "Haus Aaron, vertrau auf den Herrn! / Er ist für euch Helfer und Schild." },
      { number: 11, text: "Alle, die ihr den Herrn fürchtet, vertraut auf den Herrn! / Er ist für euch Helfer und Schild." },
      { number: 12, text: "Der Herr denkt an uns, er wird uns segnen, / er wird das Haus Israel segnen, / er wird das Haus Aaron segnen." },
      { number: 13, text: "Der Herr wird alle segnen, die ihn fürchten, / segnen Kleine und Große." },
      { number: 14, text: "Es mehre euch der Herr, / euch und eure Kinder.", footnotes: ["127,3; Dtn 1,11"] },
      { number: 15, text: "Seid gesegnet vom Herrn, / der Himmel und Erde gemacht hat.", footnotes: ["134,3"] },
      { number: 16, text: "Der Himmel ist der Himmel des Herrn, / die Erde aber gab er den Menschen.", footnotes: ["8,7; Gen 1,28"] },
      { number: 17, text: "Tote können den Herrn nicht mehr loben, / keiner, der ins Schweigen hinabfuhr.", footnotes: ["6,6; 94,17"] },
      { number: 18, text: "Wir aber preisen den Herrn / von nun an bis in Ewigkeit. / Halleluja!" },
    ] },
    { number: 116, verses: [
      { number: 1, text: "Ich liebe den Herrn; / denn er hat mein lautes Flehen gehört", heading: "Der Dank für Rettung aus Todesnot" },
      { number: 2, text: "und sein Ohr mir zugeneigt / an dem Tag, als ich zu ihm rief.", footnotes: ["an dem Tag: H: in meinen Tagen (im Sinn von: mein Leben lang)."] },
      { number: 3, text: "Mich umfingen die Fesseln des Todes, / mich befielen die Ängste der Unterwelt, / mich trafen Bedrängnis und Kummer.", footnotes: ["(3-4) 18,5-7; Jona 2,3"] },
      { number: 4, text: "Da rief ich den Namen des Herrn an: / «Ach Herr, rette mein Leben!»" },
      { number: 5, text: "Der Herr ist gnädig und gerecht, / unser Gott ist barmherzig.", footnotes: ["86,15; 103,8; 111,4; 112,4; 145,8; Ex 34,6; Mi 7,18"] },
      { number: 6, text: "Der Herr behütet die schlichten Herzen; / ich war in Not und er brachte mir Hilfe." },
      { number: 7, text: "Komm wieder zur Ruhe, mein Herz! / Denn der Herr hat dir Gutes getan." },
      { number: 8, text: "Ja, du hast mein Leben dem Tod entrissen, / meine Tränen (getrocknet), / meinen Fuß (bewahrt vor) dem Gleiten.", footnotes: ["9,14; 56,14"] },
      { number: 9, text: "So gehe ich meinen Weg vor dem Herrn / im Land der Lebenden.", footnotes: ["27,13"] },
      { number: 10, text: "Voll Vertrauen war ich, auch wenn ich sagte: / Ich bin so tief gebeugt.", heading: "Ein Lied zum Dankopfer" },
      { number: 11, text: "In meiner Bestürzung sagte ich: / Die Menschen lügen alle.", footnotes: ["12,3; Röm 3,4"] },
      { number: 12, text: "Wie kann ich dem Herrn all das vergelten, / was er mir Gutes getan hat?" },
      { number: 13, text: "Ich will den Kelch des Heils erheben / und anrufen den Namen des Herrn." },
      { number: 14, text: "Ich will dem Herrn meine Gelübde erfüllen / offen vor seinem ganzen Volk.", footnotes: ["66,13"] },
      { number: 15, text: "Kostbar ist in den Augen des Herrn / das Sterben seiner Frommen.", footnotes: ["72,14"] },
      { number: 16, text: "Ach Herr, ich bin doch dein Knecht, / dein Knecht bin ich, der Sohn deiner Magd. / Du hast meine Fesseln gelöst." },
      { number: 17, text: "Ich will dir ein Opfer des Dankes bringen / und anrufen den Namen des Herrn." },
      { number: 18, text: "Ich will dem Herrn meine Gelübde erfüllen / offen vor seinem ganzen Volk,", footnotes: ["66,13"] },
      { number: 19, text: "in den Vorhöfen am Haus des Herrn, / in deiner Mitte, Jerusalem. / Halleluja!" },
    ] },
    { number: 117, verses: [
      { number: 1, text: "Lobet den Herrn, alle Völker, / preist ihn, alle Nationen!", heading: "Aufruf an alle Völker zum Lob Gottes", footnotes: ["Röm 15,11"] },
      { number: 2, text: "Denn mächtig waltet über uns seine Huld, / die Treue des Herrn währt in Ewigkeit. / Halleluja!" },
    ] },
    { number: 118, verses: [
      { number: 1, text: "Danket dem Herrn, denn er ist gütig, / denn seine Huld währt ewig.", heading: "Eine Dankliturgie", footnotes: ["107,1"] },
      { number: 2, text: "So soll Israel sagen: / Denn seine Huld währt ewig.", footnotes: ["(2-4) 115,9-13; 135,19f"] },
      { number: 3, text: "So soll das Haus Aaron sagen: / Denn seine Huld währt ewig." },
      { number: 4, text: "So sollen alle sagen, die den Herrn fürchten und ehren: / Denn seine Huld währt ewig." },
      { number: 5, text: "In der Bedrängnis rief ich zum Herrn; / der Herr hat mich erhört und mich frei gemacht." },
      { number: 6, text: "Der Herr ist bei mir, ich fürchte mich nicht. / Was können Menschen mir antun?", footnotes: ["56,5; Hebr 13,6"] },
      { number: 7, text: "Der Herr ist bei mir, er ist mein Helfer; / ich aber schaue auf meine Hasser herab.", footnotes: ["54,6.9"] },
      { number: 8, text: "Besser, sich zu bergen beim Herrn, / als auf Menschen zu bauen.", footnotes: ["(8-9) 62,6; 146,3; Jer 17,5"] },
      { number: 9, text: "Besser, sich zu bergen beim Herrn, / als auf Fürsten zu bauen." },
      { number: 10, text: "Alle Völker umringen mich; / ich wehre sie ab im Namen des Herrn." },
      { number: 11, text: "Sie umringen, ja, sie umringen mich; / ich wehre sie ab im Namen des Herrn." },
      { number: 12, text: "Sie umschwirren mich wie Bienen, / wie ein Strohfeuer verlöschen sie; / ich wehre sie ab im Namen des Herrn.", footnotes: ["Dtn 1,44"] },
      { number: 13, text: "Sie stießen mich hart, sie wollten mich stürzen; / der Herr aber hat mir geholfen.", footnotes: ["129,1f"] },
      { number: 14, text: "Meine Stärke und mein Lied ist der Herr; / er ist für mich zum Retter geworden.", footnotes: ["Ex 15,2; Jes 12,2", "Text korr.; «mein» ist ergänzt."] },
      { number: 15, text: "Frohlocken und Jubel erschallt in den Zelten der Gerechten: / «Die Rechte des Herrn wirkt mit Macht!" },
      { number: 16, text: "Die Rechte des Herrn ist erhoben, / die Rechte des Herrn wirkt mit Macht!»" },
      { number: 17, text: "Ich werde nicht sterben, sondern leben, / um die Taten des Herrn zu verkünden.", footnotes: ["6,6"] },
      { number: 18, text: "Der Herr hat mich hart gezüchtigt, / doch er hat mich nicht dem Tod übergeben.", footnotes: ["2 Kor 6,9"] },
      { number: 19, text: "Öffnet mir die Tore zur Gerechtigkeit, / damit ich eintrete, um dem Herrn zu danken.", footnotes: ["(19-20) Jes 26,2"] },
      { number: 20, text: "Das ist das Tor zum Herrn, / nur Gerechte treten hier ein." },
      { number: 21, text: "Ich danke dir, dass du mich erhört hast; / du bist für mich zum Retter geworden." },
      { number: 22, text: "Der Stein, den die Bauleute verwarfen, / er ist zum Eckstein geworden.", footnotes: ["Jes 28,16; Apg 4,11; 1 Petr 2,7; (22-23) Neh 6,16; Mt 21,42"] },
      { number: 23, text: "Das hat der Herr vollbracht, / vor unseren Augen geschah dieses Wunder." },
      { number: 24, text: "Dies ist der Tag, den der Herr gemacht hat; / wir wollen jubeln und uns an ihm freuen." },
      { number: 25, text: "Ach, Herr, bring doch Hilfe! / Ach, Herr, gib doch Gelingen!" },
      { number: 26, text: "Gesegnet sei er, der kommt im Namen des Herrn. / Wir segnen euch vom Haus des Herrn her. /", footnotes: ["Mt 21,9; 23,39"] },
      { number: 27, text: "Gott, der Herr, erleuchte uns. Mit Zweigen in den Händen / schließt euch zusammen zum Reigen / bis zu den Hörnern des Altars!", footnotes: ["Nach G."] },
      { number: 28, text: "Du bist mein Gott, dir will ich danken; / mein Gott, dich will ich rühmen." },
      { number: 29, text: "Dankt dem Herrn, denn er ist gütig, / denn seine Huld währt ewig.", footnotes: ["107,1"] },
    ] },
    { number: 119, verses: [
      { number: 1, text: "(Alef) Wohl denen, deren Weg ohne Tadel ist, / die leben nach der Weisung des Herrn.", heading: "Ein Lobgesang auf Gottes Wort", footnotes: ["(1-3) 1,1f.6; 26,3; 32,8; 37,5; 139,3; 142,4; 143,10; Tob 4,19; Spr 4,11"] },
      { number: 2, text: "Wohl denen, die seine Vorschriften befolgen / und ihn suchen von ganzem Herzen," },
      { number: 3, text: "die kein Unrecht tun / und auf seinen Wegen gehn." },
      { number: 4, text: "Du hast deine Befehle gegeben, / damit man sie genau beachtet." },
      { number: 5, text: "Wären doch meine Schritte fest darauf gerichtet, / deinen Gesetzen zu folgen!" },
      { number: 6, text: "Dann werde ich niemals scheitern, / wenn ich auf all deine Gebote schaue." },
      { number: 7, text: "Mit lauterem Herzen will ich dir danken, / wenn ich deine gerechten Urteile lerne." },
      { number: 8, text: "Deinen Gesetzen will ich immer folgen. / Lass mich doch niemals im Stich! (Bet)" },
      { number: 9, text: "Wie geht ein junger Mann seinen Pfad ohne Tadel? / Wenn er sich hält an dein Wort." },
      { number: 10, text: "Ich suche dich von ganzem Herzen. / Lass mich nicht abirren von deinen Geboten!" },
      { number: 11, text: "Ich berge deinen Spruch im Herzen, / damit ich gegen dich nicht sündige." },
      { number: 12, text: "Gepriesen seist du, Herr. / Lehre mich deine Gesetze!" },
      { number: 13, text: "Mit meinen Lippen verkünde ich / alle Urteile deines Mundes." },
      { number: 14, text: "Nach deinen Vorschriften zu leben / freut mich mehr als großer Besitz." },
      { number: 15, text: "Ich will nachsinnen über deine Befehle / und auf deine Pfade schauen." },
      { number: 16, text: "Ich habe meine Freude an deinen Gesetzen, / dein Wort will ich nicht vergessen. (Gimel)" },
      { number: 17, text: "Tu deinem Knecht Gutes, erhalt mich am Leben! / Dann will ich dein Wort befolgen." },
      { number: 18, text: "Öffne mir die Augen / für das Wunderbare an deiner Weisung!", footnotes: ["Wörtlich: Öffne mir die Augen, damit ich sehe."] },
      { number: 19, text: "Ich bin nur Gast auf Erden. / Verbirg mir nicht deine Gebote!", footnotes: ["39,13"] },
      { number: 20, text: "In Sehnsucht nach deinem Urteil / verzehrt sich allezeit meine Seele." },
      { number: 21, text: "Du drohst den Stolzen. / Verflucht sei, wer abirrt von deinen Geboten.", footnotes: ["Dtn 27,26; Jer 11,3"] },
      { number: 22, text: "Nimm von mir Schmach und Verachtung! / Denn was du vorschreibst, befolge ich." },
      { number: 23, text: "Wenn auch Fürsten gegen mich beraten: / dein Knecht sinnt nach über deine Gesetze." },
      { number: 24, text: "Deine Vorschriften machen mich froh; / sie sind meine Berater. (Dalet)" },
      { number: 25, text: "Meine Seele klebt am Boden. / Durch dein Wort belebe mich!", footnotes: ["44,26", "Wörtlich: klebt am Staub. Text korr. mit einigen Handschriften; H: gemäß deinem Wort."] },
      { number: 26, text: "Ich habe dir mein Geschick erzählt und du erhörtest mich. / Lehre mich deine Gesetze!" },
      { number: 27, text: "Lass mich den Weg begreifen, den deine Befehle mir zeigen, / dann will ich nachsinnen über deine Wunder." },
      { number: 28, text: "Meine Seele zerfließt vor Kummer. / Richte mich auf durch dein Wort!" },
      { number: 29, text: "Halte mich fern vom Weg der Lüge; / begnade mich mit deiner Weisung!", footnotes: ["119,104"] },
      { number: 30, text: "Ich wählte den Weg der Wahrheit; / nach deinen Urteilen hab ich Verlangen.", footnotes: ["25,4"] },
      { number: 31, text: "Ich halte an deinen Vorschriften fest. / Herr, lass mich niemals scheitern!" },
      { number: 32, text: "Ich eile voran auf dem Weg deiner Gebote, / denn mein Herz machst du weit. (He)" },
      { number: 33, text: "Herr, weise mir den Weg deiner Gesetze! / Ich will ihn einhalten bis ans Ende." },
      { number: 34, text: "Gib mir Einsicht, damit ich deiner Weisung folge / und mich an sie halte aus ganzem Herzen." },
      { number: 35, text: "Führe mich auf dem Pfad deiner Gebote! / Ich habe an ihm Gefallen.", footnotes: ["25,4"] },
      { number: 36, text: "Deinen Vorschriften neige mein Herz zu, / doch nicht der Habgier!" },
      { number: 37, text: "Wende meine Augen ab von eitlen Dingen; / durch dein Wort belebe mich!", footnotes: ["durch dein Wort: Text korr.; H: durch deine Wege."] },
      { number: 38, text: "Erfülle deinem Knecht die Verheißung, / die allen gilt, die dich fürchten und ehren." },
      { number: 39, text: "Wende die Schande ab, vor der mir graut; / denn deine Entscheide sind gut." },
      { number: 40, text: "Nach deinen Befehlen hab ich Verlangen. / Gib mir neue Kraft durch deine Gerechtigkeit! (Waw)" },
      { number: 41, text: "Herr, deine Huld komme auf mich herab / und deine Hilfe, wie du es verheißen hast." },
      { number: 42, text: "Dann kann ich dem, der mich schmäht, erwidern; / denn ich vertraue auf dein Wort." },
      { number: 43, text: "Entziehe meinem Mund nicht das Wort der Wahrheit! / Ich hoffe so sehr auf deine Entscheide." },
      { number: 44, text: "Ich will deiner Weisung beständig folgen, / auf immer und ewig." },
      { number: 45, text: "Dann schreite ich aus auf freier Bahn; / denn ich frage nach deinen Befehlen." },
      { number: 46, text: "Deine Gebote will ich vor Königen bezeugen / und mich nicht vor ihnen schämen.", footnotes: ["Mt 10,18"] },
      { number: 47, text: "An deinen Geboten habe ich meine Freude, / ich liebe sie von Herzen.", footnotes: ["von Herzen: ergänzt aus G."] },
      { number: 48, text: "Ich erhebe meine Hände zu deinen Geboten; / nachsinnen will ich über deine Gesetze. (Sajin)", footnotes: ["H fügt hinzu: die ich liebe."] },
      { number: 49, text: "Denk an das Wort für deinen Knecht, / durch das du mir Hoffnung gabst." },
      { number: 50, text: "Das ist mein Trost im Elend: / Deine Verheißung spendet mir Leben." },
      { number: 51, text: "Frech verhöhnen mich die Stolzen; / ich aber weiche nicht ab von deiner Weisung." },
      { number: 52, text: "Denke ich an deine Urteile seit alter Zeit, / Herr, dann bin ich getröstet." },
      { number: 53, text: "Zorn packt mich wegen der Frevler, / weil sie deine Weisung missachten." },
      { number: 54, text: "Zum Lobgesang wurden mir deine Gesetze / im Haus meiner Pilgerschaft." },
      { number: 55, text: "In der Nacht denke ich, Herr, an deinen Namen; / ich will deine Weisung beachten.", footnotes: ["119,62.148; 63,7"] },
      { number: 56, text: "Deine Befehle zu befolgen / ist das Glück, das mir zufiel. (Chet)" },
      { number: 57, text: "Mein Anteil ist der Herr; / ich habe versprochen, dein Wort zu beachten." },
      { number: 58, text: "Ich suche deine Gunst von ganzem Herzen. / Sei mir gnädig nach deiner Verheißung!" },
      { number: 59, text: "Ich überdenke meine Wege, / zu deinen Vorschriften lenke ich meine Schritte." },
      { number: 60, text: "Ich eile und säume nicht, / deine Gebote zu halten." },
      { number: 61, text: "Auch wenn mich die Stricke der Frevler fesseln, / vergesse ich deine Weisung nicht." },
      { number: 62, text: "Um Mitternacht stehe ich auf, um dich zu preisen / wegen deiner gerechten Entscheide.", footnotes: ["119,55"] },
      { number: 63, text: "Ich bin ein Freund all derer, die dich fürchten und ehren, / und aller, die deine Befehle befolgen." },
      { number: 64, text: "Von deiner Güte, Herr, ist die Erde erfüllt. / Lehre mich deine Gesetze! (Tet)", footnotes: ["33,5"] },
      { number: 65, text: "Du hast deinem Knecht Gutes erwiesen, / o Herr, nach deinem Wort." },
      { number: 66, text: "Lehre mich Erkenntnis und rechtes Urteil! / Ich vertraue auf deine Gebote.", footnotes: ["H fügt vor «Erkenntnis» ein: Gutes."] },
      { number: 67, text: "Ehe ich gedemütigt wurde, ging mein Weg in die Irre; / nun aber halte ich mich an deine Verheißung." },
      { number: 68, text: "Du bist gut und wirkst Gutes. / Lehre mich deine Gesetze!" },
      { number: 69, text: "Stolze verbreiten über mich Lügen, / ich aber halte mich von ganzem Herzen an deine Befehle." },
      { number: 70, text: "Abgestumpft und satt ist ihr Herz, / ich aber ergötze mich an deiner Weisung.", footnotes: ["17,10"] },
      { number: 71, text: "Dass ich gedemütigt wurde, war für mich gut; / denn so lernte ich deine Gesetze.", footnotes: ["94,12"] },
      { number: 72, text: "Die Weisung deines Mundes ist mir lieb, / mehr als große Mengen von Gold und Silber. (Jod)", footnotes: ["119,103.127; 19,11"] },
      { number: 73, text: "Deine Hände haben mich gemacht und geformt. / Gib mir Einsicht, damit ich deine Gebote lerne.", footnotes: ["Ijob 10,8"] },
      { number: 74, text: "Wer dich fürchtet, wird mich sehen und sich freuen; / denn ich warte auf dein Wort." },
      { number: 75, text: "Herr, ich weiß, dass deine Entscheide gerecht sind; / du hast mich gebeugt, weil du treu für mich sorgst." },
      { number: 76, text: "Tröste mich in deiner Huld, / wie du es deinem Knecht verheißen hast." },
      { number: 77, text: "Dein Erbarmen komme über mich, damit ich lebe; / denn deine Weisung macht mich froh." },
      { number: 78, text: "Schande über die Stolzen, die mich zu Unrecht bedrücken! / Ich aber sinne nach über deine Befehle." },
      { number: 79, text: "Mir sollen sich alle zuwenden, die dich fürchten und ehren / und die deine Vorschriften kennen." },
      { number: 80, text: "Mein Herz richte sich ganz nach deinen Gesetzen; / dann werde ich nicht zuschanden. (Kaf)" },
      { number: 81, text: "Nach deiner Hilfe sehnt sich meine Seele; / ich warte auf dein Wort.", footnotes: ["130,5"] },
      { number: 82, text: "Meine Augen sehnen sich nach deiner Verheißung, / sie fragen: Wann wirst du mich trösten?", footnotes: ["123,2"] },
      { number: 83, text: "Ich bin wie ein Schlauch voller Risse, / doch deine Gesetze habe ich nicht vergessen.", footnotes: ["Ijob 30,30", "Text korr.; H: Ich bin wie ein Schlauch im Rauch."] },
      { number: 84, text: "Wie viele Tage noch bleiben deinem Knecht? / Wann wirst du meine Verfolger richten?" },
      { number: 85, text: "Stolze stellen mir Fallen, / sie handeln nicht nach deiner Weisung." },
      { number: 86, text: "Zuverlässig sind all deine Gebote. / Zu Unrecht verfolgt man mich. Komm mir zu Hilfe!" },
      { number: 87, text: "Fast hätte man mich von der Erde ausgetilgt; / dennoch halte ich fest an deinen Befehlen." },
      { number: 88, text: "In deiner großen Huld lass mich leben / und ich will beachten, was dein Mund mir gebietet. (Lamed)" },
      { number: 89, text: "Herr, dein Wort bleibt auf ewig, / es steht fest wie der Himmel.", footnotes: ["Jes 40,8"] },
      { number: 90, text: "Deine Treue währt von Geschlecht zu Geschlecht; / du hast die Erde gegründet, sie bleibt bestehen." },
      { number: 91, text: "Nach deiner Ordnung bestehen sie bis heute / und dir ist alles dienstbar.", footnotes: ["148,6"] },
      { number: 92, text: "Wäre nicht dein Gesetz meine Freude, / ich wäre zugrunde gegangen in meinem Elend." },
      { number: 93, text: "Nie will ich deine Befehle vergessen; / denn durch sie schenkst du mir Leben." },
      { number: 94, text: "Ich bin dein, errette mich! / Ich frage nach deinen Befehlen." },
      { number: 95, text: "Frevler lauern mir auf, um mich zu vernichten; / doch mein Sinn achtet auf das, was du gebietest.", footnotes: ["11,2"] },
      { number: 96, text: "Ich sah, dass alles Vollkommene Grenzen hat; / doch dein Gebot kennt keine Schranken. (Mem)" },
      { number: 97, text: "Wie lieb ist mir deine Weisung; / ich sinne über sie nach den ganzen Tag.", footnotes: ["1,2"] },
      { number: 98, text: "Dein Gebot macht mich weiser als all meine Feinde; / denn immer ist es mir nahe." },
      { number: 99, text: "Ich wurde klüger als all meine Lehrer; / denn über deine Vorschriften sinne ich nach." },
      { number: 100, text: "Mehr Einsicht habe ich als die Alten; / denn ich beachte deine Befehle.", footnotes: ["Weish 4,8f"] },
      { number: 101, text: "Von jedem bösen Weg halte ich meinen Fuß zurück; / denn ich will dein Wort befolgen.", footnotes: ["Spr 1,15; 4,27"] },
      { number: 102, text: "Ich weiche nicht ab von deinen Entscheiden, / du hast mich ja selbst unterwiesen." },
      { number: 103, text: "Wie köstlich ist für meinen Gaumen deine Verheißung, / süßer als Honig für meinen Mund.", footnotes: ["119,72"] },
      { number: 104, text: "Aus deinen Befehlen gewinne ich Einsicht, / darum hasse ich alle Pfade der Lüge. (Nun)", footnotes: ["119,29.128"] },
      { number: 105, text: "Dein Wort ist meinem Fuß eine Leuchte, / ein Licht für meine Pfade.", footnotes: ["Spr 6,23; Joh 8,12; 2 Petr 1,19"] },
      { number: 106, text: "Ich tat einen Schwur und ich will ihn halten: / Ich will deinen gerechten Entscheidungen folgen." },
      { number: 107, text: "Herr, ganz tief bin ich gebeugt. / Durch dein Wort belebe mich!", footnotes: ["Text korr., vgl. V. 25b."] },
      { number: 108, text: "Herr, nimm mein Lobopfer gnädig an / und lehre mich deine Entscheide!", footnotes: ["19,15; 50,14.23"] },
      { number: 109, text: "Mein Leben ist ständig in Gefahr, / doch ich vergesse nie deine Weisung.", footnotes: ["Wörtlich: Mein Leben halte ich ständig in der Hand."] },
      { number: 110, text: "Frevler legen mir Schlingen, / aber ich irre nicht ab von deinen Befehlen.", footnotes: ["140,6"] },
      { number: 111, text: "Deine Vorschriften sind auf ewig mein Erbteil; / denn sie sind die Freude meines Herzens." },
      { number: 112, text: "Mein Herz ist bereit, dein Gesetz zu erfüllen / bis ans Ende und ewig. (Samech)" },
      { number: 113, text: "Zwiespältige Menschen sind mir von Grund auf verhasst, / doch dein Gesetz ist mir lieb.", footnotes: ["1 Kön 18,21"] },
      { number: 114, text: "Du bist mein Schutz und mein Schild, / ich warte auf dein Wort." },
      { number: 115, text: "Weicht zurück von mir, ihr Bösen! / Ich will die Gebote meines Gottes befolgen.", footnotes: ["6,9; 139,19"] },
      { number: 116, text: "Stütze mich, damit ich lebe, wie du es verheißen hast. / Lass mich in meiner Hoffnung nicht scheitern!" },
      { number: 117, text: "Gib mir Halt, dann finde ich Rettung; / immer will ich auf deine Gesetze schauen." },
      { number: 118, text: "Alle, die sich von deinen Gesetzen entfernen, verwirfst du; / denn ihr Sinnen und Trachten ist Lüge." },
      { number: 119, text: "Alle Frevler im Land sind für dich wie Schlacken, / darum liebe ich, was du gebietest.", footnotes: ["Jes 1,25", "sind für dich wie Schlacken: so nach mehreren alten Übersetzungen; H und andere Übersetzungen: schaffst du fort wie Schlacken."] },
      { number: 120, text: "Aus Ehrfurcht vor dir erschauert mein Leib, / vor deinen Urteilen empfinde ich heilige Scheu. (Ajin)", footnotes: ["Ijob 4,14f; 23,15"] },
      { number: 121, text: "Ich tue, was recht und gerecht ist. / Gib mich meinen Bedrückern nicht preis!" },
      { number: 122, text: "Verbürg dich für das Wohl deines Knechtes, / damit die Stolzen mich nicht unterdrücken." },
      { number: 123, text: "Meine Augen sehnen sich nach deiner Hilfe, / nach deiner gerechten Verheißung." },
      { number: 124, text: "Handle an deinem Knecht nach deiner Huld / und lehre mich deine Gesetze!" },
      { number: 125, text: "Ich bin dein Knecht. Gib mir Einsicht, / damit ich verstehe, was du gebietest." },
      { number: 126, text: "Herr, es ist Zeit zu handeln; / man hat dein Gesetz gebrochen." },
      { number: 127, text: "Darum liebe ich deine Gebote / mehr als Rotgold und Weißgold.", footnotes: ["119,72"] },
      { number: 128, text: "Darum lebe ich genau nach deinen Befehlen; / ich hasse alle Pfade der Lüge. (Pe)", footnotes: ["119,104", "Text geringfügig korr."] },
      { number: 129, text: "Deine Vorschriften sind der Bewunderung wert; / darum bewahrt sie mein Herz." },
      { number: 130, text: "Die Erklärung deiner Worte bringt Erleuchtung, / den Unerfahrenen schenkt sie Einsicht." },
      { number: 131, text: "Weit öffne ich meinen Mund / und lechze nach deinen Geboten; / denn nach ihnen hab ich Verlangen." },
      { number: 132, text: "Wende dich mir zu, sei mir gnädig, / wie es denen gebührt, die deinen Namen lieben.", footnotes: ["25,16; 86,16; 91,14"] },
      { number: 133, text: "Festige meine Schritte, wie du es verheißen hast. / Lass kein Unrecht über mich herrschen!" },
      { number: 134, text: "Erlöse mich aus der Gewalt der Menschen; / dann will ich deine Befehle halten.", footnotes: ["125,3"] },
      { number: 135, text: "Lass dein Angesicht leuchten über deinem Knecht / und lehre mich deine Gesetze!", footnotes: ["4,7"] },
      { number: 136, text: "Tränenbäche strömen aus meinen Augen, / weil man dein Gesetz nicht befolgt. (Zade)", footnotes: ["Esra 9,3f"] },
      { number: 137, text: "Herr, du bist gerecht / und deine Entscheide sind richtig." },
      { number: 138, text: "Du hast deine Vorschriften erlassen in Gerechtigkeit / und in großer Treue." },
      { number: 139, text: "Der Eifer für dich verzehrt mich; / denn meine Gegner vergessen deine Worte.", footnotes: ["69,10"] },
      { number: 140, text: "Deine Worte sind rein und lauter; / dein Knecht hat sie lieb.", footnotes: ["12,7"] },
      { number: 141, text: "Ich bin gering und verachtet, / doch ich vergesse nie deine Befehle." },
      { number: 142, text: "Deine Gerechtigkeit bleibt ewig Gerechtigkeit, / deine Weisung ist Wahrheit." },
      { number: 143, text: "Mich trafen Not und Bedrängnis, / doch deine Gebote machen mich froh." },
      { number: 144, text: "Deine Vorschriften sind auf ewig gerecht. / Gib mir Einsicht, damit ich lebe. (Qof)" },
      { number: 145, text: "Erhöre mich, Herr, ich rufe von ganzem Herzen; / deine Gesetze will ich halten." },
      { number: 146, text: "Ich rufe zu dir; errette mich, / dann will ich deinen Vorschriften folgen." },
      { number: 147, text: "Schon beim Morgengrauen komme ich und flehe; / ich warte auf dein Wort." },
      { number: 148, text: "Meine Augen eilen den Nachtwachen voraus; / denn ich sinne nach über deine Verheißung.", footnotes: ["119,55"] },
      { number: 149, text: "Höre auf meine Stimme in deiner Huld; / belebe mich, Herr, durch deine Entscheide!" },
      { number: 150, text: "Mir nähern sich tückische Verfolger; / sie haben sich weit von deiner Weisung entfernt." },
      { number: 151, text: "Doch du bist nahe, Herr, / und alle deine Gebote sind Wahrheit." },
      { number: 152, text: "Aus deinen Vorschriften weiß ich seit langem, / dass du sie für ewig bestimmt hast. (Resch)" },
      { number: 153, text: "Sieh mein Elend an und rette mich; / denn ich habe deine Weisung nicht vergessen." },
      { number: 154, text: "Verschaff mir Recht und erlöse mich; / nach deiner Weisung erhalte mein Leben!" },
      { number: 155, text: "Fern bleibt den Frevlern das Heil; / denn sie fragen nicht nach deinen Gesetzen." },
      { number: 156, text: "Herr, groß ist dein Erbarmen; / durch deine Entscheide belebe mich!" },
      { number: 157, text: "Viele verfolgen und quälen mich, / doch von deinen Vorschriften weich ich nicht ab." },
      { number: 158, text: "Wenn ich Abtrünnige sehe, empfinde ich Abscheu, / weil sie dein Wort nicht befolgen.", footnotes: ["139,21"] },
      { number: 159, text: "Sieh an, wie sehr ich deine Vorschriften liebe; / Herr, in deiner Huld belebe mich!" },
      { number: 160, text: "Das Wesen deines Wortes ist Wahrheit, / deine gerechten Urteile haben alle auf ewig Bestand. (Schin)", footnotes: ["Joh 17,17"] },
      { number: 161, text: "Fürsten verfolgen mich ohne Grund, / doch mein Herz fürchtet nur dein Wort." },
      { number: 162, text: "Ich freue mich über deine Verheißung / wie einer, der reiche Beute gemacht hat." },
      { number: 163, text: "Ich hasse die Lüge, sie ist mir ein Gräuel, / doch deine Weisung habe ich lieb." },
      { number: 164, text: "Siebenmal am Tag singe ich dein Lob / wegen deiner gerechten Entscheide." },
      { number: 165, text: "Alle, die deine Weisung lieben, empfangen Heil in Fülle; / es trifft sie kein Unheil." },
      { number: 166, text: "Herr, ich hoffe auf deine Hilfe / und befolge deine Gebote.", footnotes: ["Gen 49,18"] },
      { number: 167, text: "Meine Seele beachtet, was du gebietest, / und liebt es von Herzen." },
      { number: 168, text: "Ich folge deinen Vorschriften und Befehlen; / denn alle meine Wege (liegen offen) vor dir. (Taw)", footnotes: ["Spr 5,21"] },
      { number: 169, text: "Herr, zu dir dringe mein Rufen. / Gib mir Einsicht, getreu deinem Wort!" },
      { number: 170, text: "Mein Flehen komme vor dein Angesicht. / Reiß mich heraus getreu deiner Verheißung!", footnotes: ["88,3; 79,11"] },
      { number: 171, text: "Meine Lippen sollen überströmen von Lobpreis; / denn du lehrst mich deine Gesetze." },
      { number: 172, text: "Meine Zunge soll deine Verheißung besingen; / denn deine Gebote sind alle gerecht." },
      { number: 173, text: "Deine Hand sei bereit, mir zu helfen; / denn ich habe mir deine Befehle erwählt." },
      { number: 174, text: "Ich sehne mich, Herr, nach deiner Hilfe / und deine Weisung macht mich froh." },
      { number: 175, text: "Lass meine Seele leben, damit sie dich preisen kann. / Deine Entscheidungen mögen mir helfen." },
      { number: 176, text: "Ich bin verirrt wie ein verlorenes Schaf. / Suche deinen Knecht! / Denn deine Gebote habe ich nicht vergessen.", footnotes: ["Jes 53,6; Jer 50,6; Lk 15,4-7"] },
    ] },
    { number: 120, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Ich rief zum Herrn in meiner Not / und er hat mich erhört.", heading: "Ein Hilferuf gegen Verleumder", footnotes: ["Jona 2,3"] },
      { number: 2, text: "Herr, rette mein Leben vor Lügnern, / rette es vor falschen Zungen!", footnotes: ["12,3-5; Sir 51,2"] },
      { number: 3, text: "Was soll er dir tun, was alles dir antun, / du falsche Zunge?" },
      { number: 4, text: "Scharfe Pfeile von Kriegerhand / und glühende Ginsterkohlen dazu.", footnotes: ["140,11", "Vorschlag für die Psalmodie: und glühende Kohlen dazu."] },
      { number: 5, text: "Weh mir, dass ich als Fremder in Meschech bin / und bei den Zelten von Kedar wohnen muss!" },
      { number: 6, text: "Ich muss schon allzu lange wohnen / bei Leuten, die den Frieden hassen.", footnotes: ["35,20; 140,3"] },
      { number: 7, text: "Ich verhalte mich friedlich; / doch ich brauche nur zu reden, dann suchen sie Hader und Streit.", footnotes: ["Röm 12,18", "Text korr.; H: Ich (bin) Friede."] },
    ] },
    { number: 121, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Ich hebe meine Augen auf zu den Bergen: / Woher kommt mir Hilfe?", heading: "Der Wächter Israels" },
      { number: 2, text: "Meine Hilfe kommt vom Herrn, / der Himmel und Erde gemacht hat.", footnotes: ["124,8"] },
      { number: 3, text: "Er lässt deinen Fuß nicht wanken; / er, der dich behütet, schläft nicht.", footnotes: ["66,9; 1 Sam 2,9"] },
      { number: 4, text: "Nein, der Hüter Israels / schläft und schlummert nicht.", footnotes: ["1 Kön 18,27"] },
      { number: 5, text: "Der Herr ist dein Hüter, der Herr gibt dir Schatten; / er steht dir zur Seite.", footnotes: ["16,8; 73,23"] },
      { number: 6, text: "Bei Tag wird dir die Sonne nicht schaden / noch der Mond in der Nacht.", footnotes: ["Jes 25,4; 49,10"] },
      { number: 7, text: "Der Herr behüte dich vor allem Bösen, / er behüte dein Leben.", footnotes: ["97,10; (7-8) Num 6,24-26"] },
      { number: 8, text: "Der Herr behüte dich, wenn du fortgehst und wiederkommst, / von nun an bis in Ewigkeit.", footnotes: ["Dtn 28,6; Tob 5,17"] },
    ] },
    { number: 122, verses: [
      { number: 1, text: "[Ein Wallfahrtslied Davids.] Ich freute mich, als man mir sagte: / «Zum Haus des Herrn wollen wir pilgern.»", heading: "Ein Lied zur Wallfahrt nach Jerusalem", footnotes: ["42,5; Jes 2,3"] },
      { number: 2, text: "Schon stehen wir in deinen Toren, Jerusalem: /", footnotes: ["Wörtlich: Schon stehen unsere Füße in deinen Toren."] },
      { number: 3, text: "Jerusalem, du starke Stadt, / dicht gebaut und fest gefügt.", footnotes: ["48,13f"] },
      { number: 4, text: "Dorthin ziehen die Stämme hinauf, die Stämme des Herrn, / wie es Israel geboten ist, / den Namen des Herrn zu preisen.", footnotes: ["Dtn 16,16"] },
      { number: 5, text: "Denn dort stehen Throne bereit für das Gericht, / die Throne des Hauses David.", footnotes: ["Dtn 17,8f; 1 Kön 7,7; 2 Chr 19,8"] },
      { number: 6, text: "Erbittet für Jerusalem Frieden! / Wer dich liebt, sei in dir geborgen.", footnotes: ["Tob 13,14"] },
      { number: 7, text: "Friede wohne in deinen Mauern, / in deinen Häusern Geborgenheit.", footnotes: ["Wörtlich: in seinen Palästen Geborgenheit."] },
      { number: 8, text: "Wegen meiner Brüder und Freunde / will ich sagen: In dir sei Friede." },
      { number: 9, text: "Wegen des Hauses des Herrn, unseres Gottes, / will ich dir Glück erflehen.", footnotes: ["26,8; 128,5"] },
    ] },
    { number: 123, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Ich erhebe meine Augen zu dir, / der du hoch im Himmel thronst.", heading: "Aufblick zu Gott", footnotes: ["141,8; 1 Kön 8,30"] },
      { number: 2, text: "Wie die Augen der Knechte auf die Hand ihres Herrn, / wie die Augen der Magd auf die Hand ihrer Herrin, so schauen unsre Augen auf den Herrn, unsern Gott, / bis er uns gnädig ist.", footnotes: ["25,15; 119,82"] },
      { number: 3, text: "Sei uns gnädig, Herr, sei uns gnädig! / Denn übersatt sind wir vom Hohn der Spötter,", footnotes: ["41,6; Neh 3,36"] },
      { number: 4, text: "übersatt ist unsre Seele von ihrem Spott, / von der Verachtung der Stolzen.", footnotes: ["Wörtlich: übersatt ist unsere Seele vom Spott der Übermütigen."] },
    ] },
    { number: 124, verses: [
      { number: 1, text: "[Ein Wallfahrtslied Davids.] Hätte sich nicht der Herr für uns eingesetzt / - so soll Israel sagen -,", heading: "Israels Dank für die Befreiung" },
      { number: 2, text: "hätte sich nicht der Herr für uns eingesetzt, / als sich gegen uns Menschen erhoben," },
      { number: 3, text: "dann hätten sie uns lebendig verschlungen, / als gegen uns ihr Zorn entbrannt war.", footnotes: ["Spr 1,12"] },
      { number: 4, text: "Dann hätten die Wasser uns weggespült, / hätte sich über uns ein Wildbach ergossen.", footnotes: ["42,8"] },
      { number: 5, text: "Dann hätten sich über uns die Wasser ergossen, / die wilden und wogenden Wasser." },
      { number: 6, text: "Gelobt sei der Herr, / der uns nicht ihren Zähnen als Beute überließ." },
      { number: 7, text: "Unsre Seele ist wie ein Vogel dem Netz des Jägers entkommen; / das Netz ist zerrissen und wir sind frei.", footnotes: ["Spr 6,5"] },
      { number: 8, text: "Unsre Hilfe steht im Namen des Herrn, / der Himmel und Erde gemacht hat.", footnotes: ["121,2", "In vielen Gebeten der katholischen Liturgie ist die herkömmliche Übersetzung gebräuchlich: Unsere Hilfe ist im Namen des Herrn."] },
    ] },
    { number: 125, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Wer auf den Herrn vertraut, steht fest wie der Zionsberg, / der niemals wankt, der ewig bleibt.", heading: "Gott, der Beschützer seines Volkes", footnotes: ["Spr 10,30"] },
      { number: 2, text: "Wie Berge Jerusalem rings umgeben, / so ist der Herr um sein Volk von nun an auf ewig.", footnotes: ["Dtn 32,10; Mt 28,20"] },
      { number: 3, text: "Das Zepter des Frevlers soll nicht auf dem Erbland der Gerechten lasten, / damit die Hand der Gerechten nicht nach Unrecht greift.", footnotes: ["119,134", "Wörtlich: damit nicht auch die Gerechten ihre Hände nach Unrecht ausstrecken."] },
      { number: 4, text: "Herr, tu Gutes den Guten, / den Menschen mit redlichem Herzen!", footnotes: ["18,26f"] },
      { number: 5, text: "Doch wer auf krumme Wege abbiegt, / den jage, Herr, samt den Frevlern davon! / Frieden über Israel!", footnotes: ["92,10", "Text korr.; H: auf ihre krummen Wege."] },
    ] },
    { number: 126, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Als der Herr das Los der Gefangenschaft Zions wendete, / da waren wir alle wie Träumende.", heading: "Tränen und Jubel", footnotes: ["14,7"] },
      { number: 2, text: "Da war unser Mund voll Lachen / und unsere Zunge voll Jubel. Da sagte man unter den andern Völkern: / «Der Herr hat an ihnen Großes getan.»", footnotes: ["Ijob 8,21; Ez 36,36"] },
      { number: 3, text: "Ja, Großes hat der Herr an uns getan. / Da waren wir fröhlich.", footnotes: ["Lk 1,49"] },
      { number: 4, text: "Wende doch, Herr, unser Geschick, / wie du versiegte Bäche wieder füllst im Südland.", footnotes: ["Dtn 30,3"] },
      { number: 5, text: "Die mit Tränen säen, / werden mit Jubel ernten.", footnotes: ["(5-6) Jes 25,8f; Bar 4,23; Mt 5,4; Joh 16,20; Offb 21,4; Joh 12,24"] },
      { number: 6, text: "Sie gehen hin unter Tränen / und tragen den Samen zur Aussaat. Sie kommen wieder mit Jubel / und bringen ihre Garben ein." },
    ] },
    { number: 127, verses: [
      { number: 1, text: "[Ein Wallfahrtslied Salomos.] Wenn nicht der Herr das Haus baut, / müht sich jeder umsonst, der daran baut. Wenn nicht der Herr die Stadt bewacht, / wacht der Wächter umsonst.", heading: "Die Mühe des Menschen und der Segen Gottes", footnotes: ["Ijob 12,14; (1f) Mt 6,25-34"] },
      { number: 2, text: "Es ist umsonst, dass ihr früh aufsteht / und euch spät erst niedersetzt, um das Brot der Mühsal zu essen; / denn der Herr gibt es den Seinen im Schlaf.", footnotes: ["Spr 3,24-26; 10,22; Sir 11,11", "den Seinen: Text korr.; H: seinem Geliebten; andere Übersetzungsmöglichkeit: zu Recht gibt der Herr den Seinen den Schlaf."] },
      { number: 3, text: "Kinder sind eine Gabe des Herrn, / die Frucht des Leibes ist sein Geschenk.", footnotes: ["115,14; 128,3; Gen 33,5"] },
      { number: 4, text: "Wie Pfeile in der Hand des Kriegers, / so sind Söhne aus den Jahren der Jugend." },
      { number: 5, text: "Wohl dem Mann, der mit ihnen den Köcher gefüllt hat! / Beim Rechtsstreit mit ihren Feinden scheitern sie nicht.", footnotes: ["Ijob 5,4", "Beim Rechtsstreit mit ihren Feinden, wörtlich: Wenn sie reden mit Feinden im Tor."] },
    ] },
    { number: 128, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Wohl dem Mann, der den Herrn fürchtet und ehrt / und der auf seinen Wegen geht!", heading: "Haussegen", footnotes: ["112,1"] },
      { number: 2, text: "Was deine Hände erwarben, kannst du genießen; / wohl dir, es wird dir gut ergehn.", footnotes: ["112,3", "kannst du genießen: so mit G; H: fürwahr, du kannst es genießen."] },
      { number: 3, text: "Wie ein fruchtbarer Weinstock ist deine Frau / drinnen in deinem Haus. Wie junge Ölbäume sind deine Kinder / rings um deinen Tisch.", footnotes: ["127,3; Ijob 29,5; Ps 144,12"] },
      { number: 4, text: "So wird der Mann gesegnet, / der den Herrn fürchtet und ehrt." },
      { number: 5, text: "Es segne dich der Herr vom Zion her. / Du sollst dein Leben lang das Glück Jerusalems schauen", footnotes: ["134,3; (5-6) Gal 6,16"] },
      { number: 6, text: "und die Kinder deiner Kinder sehn. / Frieden über Israel!", footnotes: ["Ijob 42,16"] },
    ] },
    { number: 129, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Sie haben mich oft bedrängt von Jugend auf / - so soll Israel sagen -,", heading: "Hoffnung in der Bedrängnis", footnotes: ["Wörtlich: von meiner Jugend auf."] },
      { number: 2, text: "sie haben mich oft bedrängt von Jugend auf, / doch sie konnten mich nicht bezwingen.", footnotes: ["118,13; Joh 16,33"] },
      { number: 3, text: "Die Pflüger haben auf meinem Rücken gepflügt, / ihre langen Furchen gezogen.", footnotes: ["66,12; Jes 51,23"] },
      { number: 4, text: "Doch der Herr ist gerecht, / er hat die Stricke der Frevler zerhauen." },
      { number: 5, text: "Beschämt sollen alle weichen, / alle, die Zion hassen." },
      { number: 6, text: "Sie sollen wie das Gras auf den Dächern sein, / das verdorrt, noch bevor man es ausreißt.", footnotes: ["Jes 37,27"] },
      { number: 7, text: "Kein Schnitter kann seine Hand damit füllen, / kein Garbenbinder den Arm." },
      { number: 8, text: "Keiner, der vorübergeht, wird sagen: / «Der Segen des Herrn sei mit euch.» - / Wir aber segnen euch im Namen des Herrn.", footnotes: ["Rut 2,4"] },
    ] },
    { number: 130, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Aus der Tiefe rufe ich, Herr, zu dir: /", heading: "Bitte in tiefer Not", footnotes: ["Jona 2,3"] },
      { number: 2, text: "Herr, höre meine Stimme! Wende dein Ohr mir zu, / achte auf mein lautes Flehen!", footnotes: ["5,2f"] },
      { number: 3, text: "Würdest du, Herr, unsere Sünden beachten, / Herr, wer könnte bestehen?", footnotes: ["143,2; Nah 1,6"] },
      { number: 4, text: "Doch bei dir ist Vergebung, / damit man in Ehrfurcht dir dient.", footnotes: ["32,1; Jes 55,7; Mi 7,18"] },
      { number: 5, text: "Ich hoffe auf den Herrn, es hofft meine Seele, / ich warte voll Vertrauen auf sein Wort.", footnotes: ["119,81"] },
      { number: 6, text: "Meine Seele wartet auf den Herrn / mehr als die Wächter auf den Morgen. Mehr als die Wächter auf den Morgen /", footnotes: ["Jes 21,11"] },
      { number: 7, text: "soll Israel harren auf den Herrn. Denn beim Herrn ist die Huld, / bei ihm ist Erlösung in Fülle.", footnotes: ["Jes 30,18; (7-8) 25,22; Mt 1,21"] },
      { number: 8, text: "Ja, er wird Israel erlösen / von all seinen Sünden." },
    ] },
    { number: 131, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Herr, mein Herz ist nicht stolz, / nicht hochmütig blicken meine Augen. Ich gehe nicht um mit Dingen, / die mir zu wunderbar und zu hoch sind.", heading: "Der Frieden in Gott", footnotes: ["Sir 3,21; 23,4; 1 Petr 5,5"] },
      { number: 2, text: "Ich ließ meine Seele ruhig werden und still; / wie ein kleines Kind bei der Mutter ist meine Seele still in mir.", footnotes: ["62,2"] },
      { number: 3, text: "Israel, harre auf den Herrn / von nun an bis in Ewigkeit!", footnotes: ["130,7"] },
    ] },
    { number: 132, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] O Herr, denk an David, / denk an all seine Mühen,", heading: "Die Erwählung Davids und des Zion" },
      { number: 2, text: "wie er dem Herrn geschworen, / dem starken Gott Jakobs gelobt hat:" },
      { number: 3, text: "«Nicht will ich mein Zelt betreten / noch mich zur Ruhe betten,", footnotes: ["(3-5) 2 Sam 7,1f; 1 Chr 28,2"] },
      { number: 4, text: "nicht Schlaf den Augen gönnen / noch Schlummer den Lidern," },
      { number: 5, text: "bis ich eine Stätte finde für den Herrn, / eine Wohnung für den starken Gott Jakobs.»" },
      { number: 6, text: "Wir hörten von seiner Lade in Efrata, / fanden sie im Gefilde von Jáar.", footnotes: ["1 Sam 7,1; 2 Sam 6,2", "von seiner Lade, wörtlich: von ihr."] },
      { number: 7, text: "Lasst uns hingehen zu seiner Wohnung / und niederfallen vor dem Schemel seiner Füße!", footnotes: ["99,5"] },
      { number: 8, text: "Erheb dich, Herr, komm an den Ort deiner Ruhe, / du und deine machtvolle Lade!", footnotes: ["Num 10,35f; (8-10) 2 Chr 6,41f"] },
      { number: 9, text: "Deine Priester sollen sich bekleiden mit Gerechtigkeit / und deine Frommen sollen jubeln.", footnotes: ["Ijob 29,14"] },
      { number: 10, text: "Weil David dein Knecht ist, / weise deinen Gesalbten nicht ab!" },
      { number: 11, text: "Der Herr hat David geschworen, / einen Eid, den er niemals brechen wird: «Einen Spross aus deinem Geschlecht / will ich setzen auf deinen Thron.", footnotes: ["89,4f", "Wörtlich: (Einen) von deiner Leibesfrucht."] },
      { number: 12, text: "Wenn deine Söhne meinen Bund bewahren, / mein Zeugnis, das ich sie lehre, dann sollen auch ihre Söhne / auf deinem Thron sitzen für immer.»" },
      { number: 13, text: "Denn der Herr hat den Zion erwählt, / ihn zu seinem Wohnsitz erkoren:", footnotes: ["68,17; 76,3; 78,68"] },
      { number: 14, text: "«Das ist für immer der Ort meiner Ruhe; / hier will ich wohnen, ich hab ihn erkoren.", footnotes: ["1 Kön 8,13"] },
      { number: 15, text: "Zions Nahrung will ich reichlich segnen, / mit Brot seine Armen sättigen.", footnotes: ["Zions Nahrung, wörtlich: Seine Nahrung."] },
      { number: 16, text: "Seine Priester will ich bekleiden mit Heil, / seine Frommen sollen jauchzen und jubeln.", footnotes: ["132,9; Jes 61,10"] },
      { number: 17, text: "Dort lasse ich Davids Macht erstarken / und stelle für meinen Gesalbten ein Licht auf.", footnotes: ["1 Kön 11,36; Lk 1,69"] },
      { number: 18, text: "Ich bedecke seine Feinde mit Schande; / doch auf ihm erglänzt seine Krone.»" },
    ] },
    { number: 133, verses: [
      { number: 1, text: "[Ein Wallfahrtslied Davids.] Seht doch, wie gut und schön ist es, / wenn Brüder miteinander in Eintracht wohnen.", heading: "Ein Lob der brüderlichen Eintracht", footnotes: ["Gen 13,8"] },
      { number: 2, text: "Das ist wie köstliches Salböl, / das vom Kopf hinabfließt auf den Bart, auf Aarons Bart, / das auf sein Gewand hinabfließt.", footnotes: ["Ex 29,7; 30,25.30", "Andere Übersetzungsmöglichkeit: in den Bart Aarons, der hinuntergeht bis zum Saum seines Gewandes."] },
      { number: 3, text: "Das ist wie der Tau des Hermon, / der auf den Berg Zion niederfällt. / Denn dort spendet der Herr Segen und Leben in Ewigkeit.", footnotes: ["Dtn 28,8"] },
    ] },
    { number: 134, verses: [
      { number: 1, text: "[Ein Wallfahrtslied.] Wohlan, nun preiset den Herrn, / all ihr Knechte des Herrn, die ihr steht im Haus des Herrn, / zu nächtlicher Stunde.", heading: "Nächtliches Loblied im Tempel", footnotes: ["135,1f"] },
      { number: 2, text: "Erhebt eure Hände zum Heiligtum / und preist den Herrn!", footnotes: ["28,2"] },
      { number: 3, text: "Es segne dich der Herr vom Zion her, / (der Herr,) der Himmel und Erde gemacht hat.", footnotes: ["128,5; 115,15"] },
    ] },
    { number: 135, verses: [
      { number: 1, text: "Halleluja! Lobt den Namen des Herrn, / lobt ihn, ihr Knechte des Herrn,", heading: "Loblied auf Gottes Wirken in Schöpfung und Geschichte", footnotes: ["113,1"] },
      { number: 2, text: "die ihr steht im Haus des Herrn, / in den Vorhöfen am Haus unsres Gottes." },
      { number: 3, text: "Lobt den Herrn, denn der Herr ist gütig. / Singt und spielt seinem Namen, denn er ist freundlich." },
      { number: 4, text: "Der Herr hat sich Jakob erwählt, / Israel wurde sein Eigentum.", footnotes: ["33,12"] },
      { number: 5, text: "Ja, das weiß ich: Groß ist der Herr, / unser Herr ist größer als alle Götter.", footnotes: ["95,3; 136,2f"] },
      { number: 6, text: "Alles, was dem Herrn gefällt, vollbringt er, / im Himmel, auf der Erde, in den Meeren, in allen Tiefen.", footnotes: ["115,3"] },
      { number: 7, text: "Er führt Wolken herauf vom Ende der Erde, / er lässt es blitzen und regnen, / aus seinen Kammern holt er den Sturmwind hervor.", footnotes: ["Jer 10,13; Ijob 37,9"] },
      { number: 8, text: "Er erschlug Ägyptens Erstgeburt, / bei Menschen und beim Vieh.", footnotes: ["78,51"] },
      { number: 9, text: "Gegen dich, Ägypten, sandte er Zeichen und Wunder, / gegen den Pharao und all seine Knechte.", footnotes: ["78,42-51", "Wörtlich: In deine Mitte, Ägypten."] },
      { number: 10, text: "Er schlug viele Völker nieder / und tötete mächtige Könige:", footnotes: ["(10-12) 136,17-22; Num 21,21-24.33-35"] },
      { number: 11, text: "Sihon, den König der Amoriter, / Og, den König von Baschan, / und alle Reiche Kanaans." },
      { number: 12, text: "Ihr Land gab er Israel zum Erbe, / zum Erbe Israel, seinem Volk.", footnotes: ["Jos 11,23"] },
      { number: 13, text: "Herr, dein Name währt ewig, / das Gedenken an dich, Herr, dauert von Geschlecht zu Geschlecht.", footnotes: ["102,13"] },
      { number: 14, text: "Denn der Herr verschafft Recht seinem Volk; / er hat mit seinen Knechten Mitleid.", footnotes: ["Dtn 32,36"] },
      { number: 15, text: "Die Götzen der Heiden sind nur Silber und Gold, / ein Machwerk von Menschenhand.", footnotes: ["(15-18) 115,4-6.8"] },
      { number: 16, text: "Sie haben einen Mund und reden nicht, / Augen und sehen nicht;" },
      { number: 17, text: "sie haben Ohren und hören nicht; / auch ist kein Hauch in ihrem Mund." },
      { number: 18, text: "Die sie gemacht haben, sollen ihrem Machwerk gleichen, / alle, die den Götzen vertrauen." },
      { number: 19, text: "Haus Israel, preise den Herrn! / Haus Aaron, preise den Herrn!", footnotes: ["(19-20) 115,9-11"] },
      { number: 20, text: "Haus Levi, preise den Herrn! / Alle, die ihr den Herrn fürchtet, preist den Herrn!" },
      { number: 21, text: "Gepriesen sei der Herr auf Zion, / er, der thront in Jerusalem. / Halleluja!" },
    ] },
    { number: 136, verses: [
      { number: 1, text: "Dankt dem Herrn, denn er ist gütig, / denn seine Huld währt ewig!", heading: "Danklitanei für Gottes ewige Huld", footnotes: ["107,1"] },
      { number: 2, text: "Dankt dem Gott aller Götter, / denn seine Huld währt ewig!", footnotes: ["82,1; 86,8; 89,7-9; 95,3; 97,9; 135,5; Ex 18,11; Dtn 10,17; Dan 2,47"] },
      { number: 3, text: "Dankt dem Herrn aller Herren, / denn seine Huld währt ewig!" },
      { number: 4, text: "Der allein große Wunder tut, / denn seine Huld währt ewig,", footnotes: ["72,18; Ex 15,11"] },
      { number: 5, text: "der den Himmel geschaffen hat in Weisheit, / denn seine Huld währt ewig,", footnotes: ["Spr 3,19; Jer 10,12"] },
      { number: 6, text: "der die Erde über den Wassern gegründet hat, / denn seine Huld währt ewig,", footnotes: ["24,2"] },
      { number: 7, text: "der die großen Leuchten gemacht hat, / denn seine Huld währt ewig,", footnotes: ["(7-9) Gen 1,14-18; Jer 31,35"] },
      { number: 8, text: "die Sonne zur Herrschaft über den Tag, / denn seine Huld währt ewig," },
      { number: 9, text: "Mond und Sterne zur Herrschaft über die Nacht, / denn seine Huld währt ewig." },
      { number: 10, text: "Der die Erstgeburt der Ägypter schlug, / denn seine Huld währt ewig,", footnotes: ["78,51"] },
      { number: 11, text: "und Israel herausführte aus ihrer Mitte, / denn seine Huld währt ewig,", footnotes: ["(11-15) 78,52f"] },
      { number: 12, text: "mit starker Hand und erhobenem Arm, / denn seine Huld währt ewig," },
      { number: 13, text: "der das Schilfmeer zerschnitt in zwei Teile, / denn seine Huld währt ewig," },
      { number: 14, text: "und Israel hindurchführte zwischen den Wassern, / denn seine Huld währt ewig,", footnotes: ["Wörtlich: und Israel hindurchführte durch seine Mitte."] },
      { number: 15, text: "und den Pharao ins Meer stürzte samt seinem Heer, / denn seine Huld währt ewig.", footnotes: ["Wörtlich: und den Pharao ins Schilfmeer stürzte."] },
      { number: 16, text: "Der sein Volk durch die Wüste führte, / denn seine Huld währt ewig," },
      { number: 17, text: "der große Könige schlug, / denn seine Huld währt ewig,", footnotes: ["(17-22) 135,10-12"] },
      { number: 18, text: "und mächtige Könige tötete, / denn seine Huld währt ewig," },
      { number: 19, text: "Sihon, den König der Amoriter, / denn seine Huld währt ewig," },
      { number: 20, text: "und Og, den König von Baschan, / denn seine Huld währt ewig," },
      { number: 21, text: "und der ihr Land zum Erbe gab, / denn seine Huld währt ewig,", footnotes: ["44,3"] },
      { number: 22, text: "der es Israel gab, seinem Knecht, / denn seine Huld währt ewig.", footnotes: ["Wörtlich: als Erbe für Israel, seinen Knecht."] },
      { number: 23, text: "Der an uns dachte in unsrer Erniedrigung, / denn seine Huld währt ewig,", footnotes: ["Lk 1,48"] },
      { number: 24, text: "und uns den Feinden entriss, / denn seine Huld währt ewig,", footnotes: ["106,43f; Lk 1,71"] },
      { number: 25, text: "der allen Geschöpfen Nahrung gibt, / denn seine Huld währt ewig.", footnotes: ["145,15f"] },
      { number: 26, text: "Dankt dem Gott des Himmels, / denn seine Huld währt ewig." },
    ] },
    { number: 137, verses: [
      { number: 1, text: "An den Strömen von Babel, / da saßen wir und weinten, / wenn wir an Zion dachten.", heading: "Heimweh nach dem Zion in der Verbannung", footnotes: ["Ez 3,15; Klgl 3,48"] },
      { number: 2, text: "Wir hängten unsere Harfen / an die Weiden in jenem Land.", footnotes: ["Klgl 5,14"] },
      { number: 3, text: "Dort verlangten von uns die Zwingherren Lieder, / unsere Peiniger forderten Jubel: / «Singt uns Lieder vom Zion!»" },
      { number: 4, text: "Wie könnten wir singen die Lieder des Herrn, / fern, auf fremder Erde?" },
      { number: 5, text: "Wenn ich dich je vergesse, Jerusalem, / dann soll mir die rechte Hand verdorren.", footnotes: ["Jer 51,50", "Text korr.; H: dann soll meine rechte Hand (die Kunst des Spielens) vergessen."] },
      { number: 6, text: "Die Zunge soll mir am Gaumen kleben, / wenn ich an dich nicht mehr denke, / wenn ich Jerusalem nicht zu meiner höchsten Freude erhebe.", footnotes: ["22,16"] },
      { number: 7, text: "Herr, vergiss den Söhnen Edoms nicht den Tag von Jerusalem; / sie sagten: «Reißt nieder, bis auf den Grund reißt es nieder!»", footnotes: ["Klgl 4,21f; Ez 25,12-14; 35; Obd 8-15", "7-9: Zu diesem Fluch vgl. die Anmerkung zu 109,1-31. Hier geht es aber um übermächtige feindliche Völker."] },
      { number: 8, text: "Tochter Babel, du Zerstörerin! / Wohl dem, der dir heimzahlt, was du uns getan hast!", footnotes: ["Jes 14,22; Jer 50; 51", "du Zerstörerin: H: du zu Verwüstende."] },
      { number: 9, text: "Wohl dem, der deine Kinder packt / und sie am Felsen zerschmettert!", footnotes: ["Jes 13,16.18; Hos 14,1; Röm 12,19"] },
    ] },
    { number: 138, verses: [
      { number: 1, text: "[Von David.] Ich will dir danken aus ganzem Herzen, / dir vor den Engeln singen und spielen;", heading: "Dank für Gottes Hilfe", footnotes: ["7,18; 9,2; 111,1"] },
      { number: 2, text: "ich will mich niederwerfen zu deinem heiligen Tempel / hin und deinem Namen danken für deine Huld und Treue. Denn du hast die Worte meines Mundes gehört, / deinen Namen und dein Wort über alles verherrlicht.", footnotes: ["5,8; Dan 6,11", "Ergänzt aus G; dort steht dieser Satz in V. 1. Text korr.; H etwa: du hast Versprechen gemacht, die deinen Namen übersteigen."] },
      { number: 3, text: "Du hast mich erhört an dem Tag, als ich rief; / du gabst meiner Seele große Kraft.", footnotes: ["Nach G; H: du machtest mich ungestüm."] },
      { number: 4, text: "Dich sollen preisen, Herr, alle Könige der Welt, / wenn sie die Worte deines Mundes vernehmen.", footnotes: ["68,33; Jes 2,3"] },
      { number: 5, text: "Sie sollen singen von den Wegen des Herrn; / denn groß ist die Herrlichkeit des Herrn." },
      { number: 6, text: "Ja, der Herr ist erhaben; / doch er schaut auf die Niedrigen / und die Stolzen erkennt er von fern.", footnotes: ["Jes 57,15; Lk 1,51f"] },
      { number: 7, text: "Gehe ich auch mitten durch große Not: / du erhältst mich am Leben. Du streckst die Hand aus gegen meine wütenden Feinde / und deine Rechte hilft mir.", footnotes: ["23,4"] },
      { number: 8, text: "Der Herr nimmt sich meiner an. / Herr, deine Huld währt ewig. / Lass nicht ab vom Werk deiner Hände!" },
    ] },
    { number: 139, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.] Herr, du hast mich erforscht und du kennst mich. /", heading: "Der Mensch vor dem allwissenden Gott", footnotes: ["33,15; 44,22; Jer 12,3"] },
      { number: 2, text: "Ob ich sitze oder stehe, du weißt von mir. / Von fern erkennst du meine Gedanken.", footnotes: ["2 Kön 19,27; Ijob 31,4"] },
      { number: 3, text: "Ob ich gehe oder ruhe, es ist dir bekannt; / du bist vertraut mit all meinen Wegen.", footnotes: ["es ist dir bekannt, wörtlich: du prüfst es; oder: du misst es."] },
      { number: 4, text: "Noch liegt mir das Wort nicht auf der Zunge - / du, Herr, kennst es bereits." },
      { number: 5, text: "Du umschließt mich von allen Seiten / und legst deine Hand auf mich.", footnotes: ["Ijob 1,10", "Wörtlich: Du umschließt mich von hinten und von vorn."] },
      { number: 6, text: "Zu wunderbar ist für mich dieses Wissen, / zu hoch, ich kann es nicht begreifen.", footnotes: ["92,6; 131,1; Röm 11,33"] },
      { number: 7, text: "Wohin könnte ich fliehen vor deinem Geist, / wohin mich vor deinem Angesicht flüchten?", footnotes: ["(7-10) Ijob 23,8f; Sir 16,17f; Jer 23,23f; Am 9,2f"] },
      { number: 8, text: "Steige ich hinauf in den Himmel, so bist du dort; / bette ich mich in der Unterwelt, bist du zugegen.", footnotes: ["Ijob 11,8"] },
      { number: 9, text: "Nehme ich die Flügel des Morgenrots / und lasse mich nieder am äußersten Meer,", footnotes: ["Jona 1,3"] },
      { number: 10, text: "auch dort wird deine Hand mich ergreifen / und deine Rechte mich fassen.", footnotes: ["mich ergreifen: Text korr.; H: mich leiten."] },
      { number: 11, text: "Würde ich sagen: «Finsternis soll mich bedecken, / statt Licht soll Nacht mich umgeben», /", footnotes: ["(11-12) Ijob 26,6; 34,22; Dan 2,22", "Text korr. nach den Übersetzungen von Symmachus und Hieronymus; H: Finsternis soll mich zermalmen."] },
      { number: 12, text: "auch die Finsternis wäre für dich nicht finster, die Nacht würde leuchten wie der Tag, / die Finsternis wäre wie Licht." },
      { number: 13, text: "Denn du hast mein Inneres geschaffen, / mich gewoben im Schoß meiner Mutter.", footnotes: ["(13-15) Ijob 10,8-11; Weish 7,1f"] },
      { number: 14, text: "Ich danke dir, dass du mich so wunderbar gestaltet hast. / Ich weiß: Staunenswert sind deine Werke.", footnotes: ["Text korr. nach G, S und Hieronymus."] },
      { number: 15, text: "Als ich geformt wurde im Dunkeln, / kunstvoll gewirkt in den Tiefen der Erde, / waren meine Glieder dir nicht verborgen.", footnotes: ["Ijob 1,21"] },
      { number: 16, text: "Deine Augen sahen, wie ich entstand, / in deinem Buch war schon alles verzeichnet; meine Tage waren schon gebildet, / als noch keiner von ihnen da war.", footnotes: ["69,29; Ijob 14,5", "Text korr.; H ist unklar."] },
      { number: 17, text: "Wie schwierig sind für mich, o Gott, deine Gedanken, / wie gewaltig ist ihre Zahl!", footnotes: ["92,6; Ijob 11,7"] },
      { number: 18, text: "Wollte ich sie zählen, es wären mehr als der Sand. / Käme ich bis zum Ende, wäre ich noch immer bei dir.", footnotes: ["40,6; Sir 18,5-7", "Käme ich bis zum Ende: Text korr.; H: Erwachte ich."] },
      { number: 19, text: "Wolltest du, Gott, doch den Frevler töten! / Ihr blutgierigen Menschen, lasst ab von mir!", footnotes: ["119,115"] },
      { number: 20, text: "Sie reden über dich voll Tücke / und missbrauchen deinen Namen.", footnotes: ["deinen Namen: Text korr.; H: deine Städte; oder: deine Feinde."] },
      { number: 21, text: "Soll ich die nicht hassen, Herr, die dich hassen, / die nicht verabscheuen, die sich gegen dich erheben?" },
      { number: 22, text: "Ich hasse sie mit glühendem Hass; / auch mir sind sie zu Feinden geworden." },
      { number: 23, text: "Erforsche mich, Gott, und erkenne mein Herz, / prüfe mich und erkenne mein Denken!", footnotes: ["17,3; 26,2"] },
      { number: 24, text: "Sieh her, ob ich auf dem Weg bin, der dich kränkt, / und leite mich auf dem altbewährten Weg!", footnotes: ["25,4"] },
    ] },
    { number: 140, verses: [
      { number: 1, text: "[Für den Chormeister. Ein Psalm Davids.]", heading: "Hilferuf zu Gott, dem Anwalt des Armen" },
      { number: 2, text: "Rette mich, Herr, vor bösen Menschen, / vor gewalttätigen Leuten schütze mich!", footnotes: ["71,4"] },
      { number: 3, text: "Denn sie sinnen in ihrem Herzen auf Böses, / jeden Tag schüren sie Streit." },
      { number: 4, text: "Wie die Schlangen haben sie scharfe Zungen / und hinter den Lippen Gift wie die Nattern. [Sela]", footnotes: ["55,22; 58,5; Röm 3,13"] },
      { number: 5, text: "Behüte mich, Herr, vor den Händen der Frevler, / vor gewalttätigen Leuten schütze mich, / die darauf sinnen, mich zu Boden zu stoßen.", footnotes: ["Wörtlich: darauf sinnen, meine Schritte zu Fall zu bringen."] },
      { number: 6, text: "Hochmütige legen mir heimlich Schlingen, / Böse spannen ein Netz aus, / stellen mir Fallen am Wegrand. [Sela]", footnotes: ["57,7; 141,9; 142,4; Ijob 18,8", "Text korr."] },
      { number: 7, text: "Ich sage zum Herrn: Du bist mein Gott. / Vernimm, o Herr, mein lautes Flehen!", footnotes: ["31,15"] },
      { number: 8, text: "Herr, mein Gebieter, meine starke Hilfe, / du beschirmst mein Haupt am Tag des Kampfes." },
      { number: 9, text: "Herr, erfülle nicht die Wünsche des Frevlers, / lass seine Pläne nicht gelingen! [Sela]", footnotes: ["9f: Text korr. durch Verschiebung der Versgrenze und Wiederholung der Negation von V. 9 in V. 10."] },
      { number: 10, text: "Die mich umzingeln, sollen das Haupt nicht erheben; / die Bosheit ihrer Lippen treffe sie selbst.", footnotes: ["7,17"] },
      { number: 11, text: "Er lasse glühende Kohlen auf sie regnen, / er stürze sie hinab in den Abgrund, / sodass sie nie wieder aufstehn.", footnotes: ["11,6; 120,4; Num 16,31f"] },
      { number: 12, text: "Der Verleumder soll nicht bestehen im Land, / den Gewalttätigen treffe das Unglück Schlag auf Schlag." },
      { number: 13, text: "Ich weiß, der Herr führt die Sache des Armen, / er verhilft den Gebeugten zum Recht.", footnotes: ["9,19; 34,7", "Wörtlich: er richtet den Gebeugten nach dem Armenrecht."] },
      { number: 14, text: "Deinen Namen preisen nur die Gerechten; / vor deinem Angesicht dürfen nur die Redlichen bleiben.", footnotes: ["11,7"] },
    ] },
    { number: 141, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Herr, ich rufe zu dir. Eile mir zu Hilfe; / höre auf meine Stimme, wenn ich zu dir rufe.", heading: "Bitte um Bewahrung vor Sünde" },
      { number: 2, text: "Wie ein Rauchopfer steige mein Gebet vor dir auf; / als Abendopfer gelte vor dir, wenn ich meine Hände erhebe.", footnotes: ["134,2; Ex 30,7f; Jdt 9,1; Num 28,4"] },
      { number: 3, text: "Herr, stell eine Wache vor meinen Mund, / eine Wehr vor das Tor meiner Lippen!", footnotes: ["39,2; Jak 3,5f"] },
      { number: 4, text: "Gib, dass mein Herz sich bösen Worten nicht zuneigt, / dass ich nichts tue, was schändlich ist, zusammen mit Menschen, die Unrecht tun. / Von ihren Leckerbissen will ich nicht kosten." },
      { number: 5, text: "Der Gerechte mag mich schlagen aus Güte: / Wenn er mich bessert, ist es Salböl für mein Haupt; da wird sich mein Haupt nicht sträuben. / Ist er in Not, will ich stets für ihn beten.", footnotes: ["Spr 9,8; 27,5f", "Andere Übersetzungsmöglichkeit: Denn stets (wird laut) mein Gebet trotz ihrer Bosheit, oder: wenn es ihnen schlecht geht . . ."] },
      { number: 6, text: "Haben ihre Richter sich auch die Felsen hinabgestürzt, / sie sollen hören, dass mein Wort für sie freundlich ist.", footnotes: ["H ist unverständlich."] },
      { number: 7, text: "Wie wenn man Furchen zieht und das Erdreich aufreißt, / so sind unsre Glieder hingestreut an den Rand der Unterwelt." },
      { number: 8, text: "Mein Herr und Gott, meine Augen richten sich auf dich; / bei dir berge ich mich. Gieß mein Leben nicht aus!", footnotes: ["(8-9) 25,15"] },
      { number: 9, text: "Vor der Schlinge, die sie mir legten, bewahre mich, / vor den Fallen derer, die Unrecht tun!", footnotes: ["140,6"] },
      { number: 10, text: "Die Frevler sollen sich in ihren eigenen Netzen fangen, / während ich heil entkomme.", footnotes: ["7,16"] },
    ] },
    { number: 142, verses: [
      { number: 1, text: "[Ein Weisheitslied Davids, als er in der Höhle war. Ein Gebet.]", heading: "Hilferuf in schwerer Bedrängnis", footnotes: ["63,1"] },
      { number: 2, text: "Mit lauter Stimme schrei ich zum Herrn, / laut flehe ich zum Herrn um Gnade." },
      { number: 3, text: "Ich schütte vor ihm meine Klagen aus, / eröffne ihm meine Not." },
      { number: 4, text: "Wenn auch mein Geist in mir verzagt, / du kennst meinen Pfad. Auf dem Weg, den ich gehe, / legten sie mir Schlingen.", footnotes: ["143,4; 140,6"] },
      { number: 5, text: "Ich blicke nach rechts und schaue aus, / doch niemand ist da, der mich beachtet. Mir ist jede Zuflucht genommen, / niemand fragt nach meinem Leben." },
      { number: 6, text: "Herr, ich schreie zu dir, / ich sage: Meine Zuflucht bist du, / mein Anteil im Land der Lebenden.", footnotes: ["91,2; 73,26; 27,13"] },
      { number: 7, text: "Vernimm doch mein Flehen; / denn ich bin arm und elend. Meinen Verfolgern entreiß mich; / sie sind viel stärker als ich.", footnotes: ["Wörtlich: denn ich bin sehr schwach."] },
      { number: 8, text: "Führe mich heraus aus dem Kerker, / damit ich deinen Namen preise. Die Gerechten scharen sich um mich, / weil du mir Gutes tust.", footnotes: ["88,9"] },
    ] },
    { number: 143, verses: [
      { number: 1, text: "[Ein Psalm Davids.] Herr, höre mein Gebet, vernimm mein Flehen; / in deiner Treue erhöre mich, in deiner Gerechtigkeit!", heading: "Gebet um Kraft und Hilfe gegen Feinde" },
      { number: 2, text: "Geh mit deinem Knecht nicht ins Gericht; / denn keiner, der lebt, ist gerecht vor dir.", footnotes: ["130,3; Ijob 4,17; 9,2; 25,4; Koh 7,20"] },
      { number: 3, text: "Der Feind verfolgt mich, tritt mein Leben zu Boden, / er lässt mich in der Finsternis wohnen wie längst Verstorbene.", footnotes: ["Klgl 3,6"] },
      { number: 4, text: "Mein Geist verzagt in mir, / mir erstarrt das Herz in der Brust.", footnotes: ["142,4; Ijob 17,1"] },
      { number: 5, text: "Ich denke an die vergangenen Tage, / sinne nach über all deine Taten, / erwäge das Werk deiner Hände.", footnotes: ["77,6-13"] },
      { number: 6, text: "Ich breite die Hände aus (und bete) zu dir; / meine Seele dürstet nach dir wie lechzendes Land. [Sela]", footnotes: ["42,2"] },
      { number: 7, text: "Herr, erhöre mich bald, / denn mein Geist wird müde; verbirg dein Antlitz nicht vor mir, / damit ich nicht werde wie Menschen, die längst begraben sind.", footnotes: ["69,18; 102,3; 28,1; 88,5", "Wörtlich: die hinabgestiegen sind zur Grube."] },
      { number: 8, text: "Lass mich deine Huld erfahren am frühen Morgen; / denn ich vertraue auf dich. Zeig mir den Weg, den ich gehen soll; / denn ich erhebe meine Seele zu dir.", footnotes: ["90,14; 25,1f"] },
      { number: 9, text: "Herr, entreiß mich den Feinden! / Zu dir nehme ich meine Zuflucht.", footnotes: ["meine Zuflucht: Text korr. nach G."] },
      { number: 10, text: "Lehre mich, deinen Willen zu tun; denn du bist mein Gott. / Dein guter Geist leite mich auf ebenem Pfad.", footnotes: ["119,1", "Text korr.; H: leite mich auf ebenem Land."] },
      { number: 11, text: "Um deines Namens willen, Herr, erhalt mich am Leben, / führe mich heraus aus der Not in deiner Gerechtigkeit!" },
      { number: 12, text: "Vertilge in deiner Huld meine Feinde, / lass all meine Gegner untergehn! / Denn ich bin dein Knecht.", footnotes: ["54,7"] },
    ] },
    { number: 144, verses: [
      { number: 1, text: "[Von David] Gelobt sei der Herr, der mein Fels ist, / der meine Hände den Kampf gelehrt hat, meine Finger den Krieg.", heading: "Danklied auf das Glück des Gottesvolkes", footnotes: ["18,35; 92,16"] },
      { number: 2, text: "Du bist meine Huld und Burg, / meine Festung, mein Retter, mein Schild, dem ich vertraue. / Er macht mir Völker untertan.", footnotes: ["3,4; 18,3.48", "mir Völker: Text korr.; H: mein Volk."] },
      { number: 3, text: "Herr, was ist der Mensch, dass du dich um ihn kümmerst, / des Menschen Kind, dass du es beachtest?", footnotes: ["8,5; Ijob 7,17"] },
      { number: 4, text: "Der Mensch gleicht einem Hauch, / seine Tage sind wie ein flüchtiger Schatten.", footnotes: ["39,6f; 90,9"] },
      { number: 5, text: "Herr, neig deinen Himmel und steig herab, / rühre die Berge an, so- dass sie rauchen.", footnotes: ["104,32; Jes 63,19; (5-7) 18,10-17"] },
      { number: 6, text: "Schleudre Blitze und zerstreue die Feinde, / schieß deine Pfeile ab und jag sie dahin!" },
      { number: 7, text: "Streck deine Hände aus der Höhe herab und befreie mich; / reiß mich heraus aus gewaltigen Wassern, / aus der Hand der Fremden!" },
      { number: 8, text: "Alles, was ihr Mund sagt, ist Lüge, / Meineide schwört ihre Rechte." },
      { number: 9, text: "Ein neues Lied will ich, o Gott, dir singen, / auf der zehnsaitigen Harfe will ich dir spielen,", footnotes: ["33,3"] },
      { number: 10, text: "der du den Königen den Sieg verleihst / und David, deinen Knecht, errettest. Vor dem bösen Schwert", footnotes: ["18,51", "Text korr.; H: David, seinen Knecht."] },
      { number: 11, text: "errette mich, / entreiß mich der Hand der Fremden! Alles, was ihr Mund sagt, ist Lüge, / Meineide schwört ihre Rechte." },
      { number: 12, text: "Unsre Söhne seien wie junge Bäume, / hoch gewachsen in ihrer Jugend, unsre Töchter wie schlanke Säulen, / die geschnitzt sind für den Tempel.", footnotes: ["128,3; Ijob 42,15"] },
      { number: 13, text: "Unsre Speicher seien gefüllt, / überquellend von vielerlei Vorrat; unsre Herden mögen sich tausendfach mehren, / vieltausendfach auf unsren Fluren.", footnotes: ["107,37f"] },
      { number: 14, text: "Unsre Kühe mögen tragen, ohne zu verwerfen und ohne Unfall; / kein Wehgeschrei werde laut auf unsern Straßen.", footnotes: ["Andere Übersetzungsmöglichkeit: Unsere Gaue ohne Lasten, kein Durchbruch und kein Auszug, kein Geschrei auf unseren Plätzen."] },
      { number: 15, text: "Wohl dem Volk, dem es so ergeht, / glücklich das Volk, dessen Gott der Herr ist!", footnotes: ["33,12"] },
    ] },
    { number: 145, verses: [
      { number: 1, text: "[Ein Loblied Davids.] Ich will dich rühmen, mein Gott und König, / und deinen Namen preisen immer und ewig;", heading: "Lobpreis der Größe und Güte Gottes" },
      { number: 2, text: "ich will dich preisen Tag für Tag / und deinen Namen loben immer und ewig.", footnotes: ["34,2; 68,20"] },
      { number: 3, text: "Groß ist der Herr und hoch zu loben, / seine Größe ist unerforschlich.", footnotes: ["95,3"] },
      { number: 4, text: "Ein Geschlecht verkünde dem andern den Ruhm deiner Werke / und erzähle von deinen gewaltigen Taten.", footnotes: ["78,4"] },
      { number: 5, text: "Sie sollen vom herrlichen Glanz deiner Hoheit reden; / ich will deine Wunder besingen." },
      { number: 6, text: "Sie sollen sprechen von der Gewalt deiner erschreckenden Taten; / ich will von deinen großen Taten berichten." },
      { number: 7, text: "Sie sollen die Erinnerung an deine große Güte wecken / und über deine Gerechtigkeit jubeln." },
      { number: 8, text: "Der Herr ist gnädig und barmherzig, / langmütig und reich an Gnade.", footnotes: ["116,5"] },
      { number: 9, text: "Der Herr ist gütig zu allen, / sein Erbarmen waltet über all seinen Werken.", footnotes: ["103,13; Weish 11,24"] },
      { number: 10, text: "Danken sollen dir, Herr, all deine Werke / und deine Frommen dich preisen.", footnotes: ["103,22"] },
      { number: 11, text: "Sie sollen von der Herrlichkeit deines Königtums reden, / sollen sprechen von deiner Macht,", footnotes: ["(11-13) 24,7-10"] },
      { number: 12, text: "den Menschen deine machtvollen Taten verkünden / und den herrlichen Glanz deines Königtums.", footnotes: ["Text korr. nach G und S; H: seine machtvollen Taten."] },
      { number: 13, text: "Dein Königtum ist ein Königtum für ewige Zeiten, / deine Herrschaft währt von Geschlecht zu Geschlecht. [Der Herr ist treu in all seinen Worten, / voll Huld in all seinen Taten]", footnotes: ["Der eingeklammerte Textteil fehlt in H; Rekonstruktion nach G, S und Vg."] },
      { number: 14, text: "Der Herr stützt alle, die fallen, / und richtet alle Gebeugten auf.", footnotes: ["94,18; 146,8"] },
      { number: 15, text: "Aller Augen warten auf dich / und du gibst ihnen Speise zur rechten Zeit.", footnotes: ["(15-16) 104,27f; 136,25"] },
      { number: 16, text: "Du öffnest deine Hand / und sättigst alles, was lebt, nach deinem Gefallen.", footnotes: ["Mt 6,25f"] },
      { number: 17, text: "Gerecht ist der Herr in allem, was er tut, / voll Huld in all seinen Werken.", footnotes: ["Dtn 32,4"] },
      { number: 18, text: "Der Herr ist allen, die ihn anrufen, nahe, / allen, die zu ihm aufrichtig rufen.", footnotes: ["Dtn 4,7; Jes 55,6; 58,9; Jer 29,13"] },
      { number: 19, text: "Die Wünsche derer, die ihn fürchten, erfüllt er, / er hört ihr Schreien und rettet sie.", footnotes: ["34,18; Spr 10,24; Joh 9,31"] },
      { number: 20, text: "Alle, die ihn lieben, behütet der Herr, / doch alle Frevler vernichtet er.", footnotes: ["Ri 5,31"] },
      { number: 21, text: "Mein Mund verkünde das Lob des Herrn. / Alles, was lebt, preise seinen heiligen Namen immer und ewig!", footnotes: ["Sir 39,35"] },
    ] },
    { number: 146, verses: [
      { number: 1, text: "Halleluja! Lobe den Herrn, meine Seele! /", heading: "Preislied auf Gott, den Herrn und Helfer Israels" },
      { number: 2, text: "Ich will den Herrn loben, solange ich lebe, / meinem Gott singen und spielen, solange ich da bin.", footnotes: ["7,18; 104,33"] },
      { number: 3, text: "Verlasst euch nicht auf Fürsten, / auf Menschen, bei denen es doch keine Hilfe gibt.", footnotes: ["118,8f"] },
      { number: 4, text: "Haucht der Mensch sein Leben aus / und kehrt er zurück zur Erde, / dann ist es aus mit all seinen Plänen.", footnotes: ["90,3; 103,14; 104,29; Gen 3,19; Ijob 10,9; Koh 12,7", "Wörtlich: an jenem Tag ist es aus."] },
      { number: 5, text: "Wohl dem, dessen Halt der Gott Jakobs ist / und der seine Hoffnung auf den Herrn, seinen Gott, setzt.", footnotes: ["Jer 17,7"] },
      { number: 6, text: "Der Herr hat Himmel und Erde gemacht, / das Meer und alle Geschöpfe; / er hält ewig die Treue.", footnotes: ["Apg 4,24; 14,15", "Wörtlich: das Meer und alles, was in ihnen ist."] },
      { number: 7, text: "Recht verschafft er den Unterdrückten, / den Hungernden gibt er Brot; / der Herr befreit die Gefangenen.", footnotes: ["103,6; 68,7; Jes 61,1"] },
      { number: 8, text: "Der Herr öffnet den Blinden die Augen, / er richtet die Gebeugten auf.", footnotes: ["145,14"] },
      { number: 9, text: "Der Herr beschützt die Fremden / und verhilft den Waisen und Witwen zu ihrem Recht. Der Herr liebt die Gerechten, / doch die Schritte der Frevler leitet er in die Irre.", footnotes: ["10,18", "In H steht dieser Versteil am Ende von V. 8."] },
      { number: 10, text: "Der Herr ist König auf ewig, / dein Gott, Zion, herrscht von Geschlecht zu Geschlecht. Halleluja!", footnotes: ["24,7-10"] },
    ] },
    { number: 147, verses: [
      { number: 1, text: "Halleluja! Gut ist es, unserm Gott zu singen; / schön ist es, ihn zu loben.", heading: "Bekenntnis zu Gott, dem Retter Israels", footnotes: ["92,2"] },
      { number: 2, text: "Der Herr baut Jerusalem wieder auf, / er sammelt die Versprengten Israels.", footnotes: ["51,20; Jes 11,12; Jer 31,10"] },
      { number: 3, text: "Er heilt die gebrochenen Herzen / und verbindet ihre schmerzenden Wunden.", footnotes: ["Ijob 5,18; Jes 61,1; Jer 33,6"] },
      { number: 4, text: "Er bestimmt die Zahl der Sterne / und ruft sie alle mit Namen.", footnotes: ["Jes 40,26; Bar 3,34f"] },
      { number: 5, text: "Groß ist unser Herr und gewaltig an Kraft, / unermesslich ist seine Weisheit.", footnotes: ["145,3; Jes 40,28"] },
      { number: 6, text: "Der Herr hilft den Gebeugten auf / und erniedrigt die Frevler.", footnotes: ["145,14; 75,8; Lk 1,52", "erniedrigt die Frevler: H fügt hinzu: bis zum Boden."] },
      { number: 7, text: "Stimmt dem Herrn ein Danklied an, / spielt unserm Gott auf der Harfe!" },
      { number: 8, text: "Er bedeckt den Himmel mit Wolken, / spendet der Erde Regen / und lässt Gras auf den Bergen sprießen.", footnotes: ["104,11-14; Ijob 5,10; Jer 14,22; Joël 2,23"] },
      { number: 9, text: "Er gibt dem Vieh seine Nahrung, / gibt den jungen Raben, wonach sie schreien.", footnotes: ["Ijob 38,41; Lk 12,24"] },
      { number: 10, text: "Er hat keine Freude an der Kraft des Pferdes, / kein Gefallen am schnellen Lauf des Mannes.", footnotes: ["(10-11) 20,8; 1 Sam 16,7"] },
      { number: 11, text: "Gefallen hat der Herr an denen, die ihn fürchten und ehren, / die voll Vertrauen warten auf seine Huld." },
      { number: 12, text: "Jerusalem, preise den Herrn, / lobsinge, Zion, deinem Gott!", heading: "Dank für Gottes Güte", footnotes: ["Jer 33,10f"] },
      { number: 13, text: "Denn er hat die Riegel deiner Tore fest gemacht, / die Kinder in deiner Mitte gesegnet;" },
      { number: 14, text: "er verschafft deinen Grenzen Frieden / und sättigt dich mit bestem Weizen.", footnotes: ["81,17"] },
      { number: 15, text: "Er sendet sein Wort zur Erde, / rasch eilt sein Befehl dahin.", footnotes: ["33,9; Weish 18,15"] },
      { number: 16, text: "Er spendet Schnee wie Wolle, / streut den Reif aus wie Asche.", footnotes: ["(15-18) Ijob 38,20-30"] },
      { number: 17, text: "Eis wirft er herab in Brocken, / vor seiner Kälte erstarren die Wasser.", footnotes: ["Ijob 37,10", "Text korr.; H: Wer wird bestehen vor seinem Frost?"] },
      { number: 18, text: "Er sendet sein Wort aus und sie schmelzen, / er lässt den Wind wehen, dann rieseln die Wasser." },
      { number: 19, text: "Er verkündet Jakob sein Wort, / Israel seine Gesetze und Rechte.", footnotes: ["78,5; (19-20) Dtn 4,8"] },
      { number: 20, text: "An keinem andern Volk hat er so gehandelt, / keinem sonst seine Rechte verkündet. Halleluja!", footnotes: ["Text korr.; H: und seine Rechte lernten sie nicht kennen."] },
    ] },
    { number: 148, verses: [
      { number: 1, text: "Halleluja! Lobt den Herrn vom Himmel her, / lobt ihn in den Höhen:", heading: "Danklitanei auf Gott, den Schöpfer und Herrn", footnotes: ["Lk 2,13; (1-4) 103,20-22; Ijob 38,7"] },
      { number: 2, text: "Lobt ihn, all seine Engel, / lobt ihn, all seine Scharen;" },
      { number: 3, text: "lobt ihn, Sonne und Mond, / lobt ihn, all ihr leuchtenden Sterne;" },
      { number: 4, text: "lobt ihn, alle Himmel / und ihr Wasser über dem Himmel!" },
      { number: 5, text: "Loben sollen sie den Namen des Herrn; / denn er gebot, und sie waren erschaffen.", footnotes: ["33,9"] },
      { number: 6, text: "Er stellte sie hin für immer und ewig, / er gab ihnen ein Gesetz, das sie nicht übertreten.", footnotes: ["119,91; Jer 31,35f"] },
      { number: 7, text: "Lobt den Herrn, ihr auf der Erde, / ihr Seeungeheuer und all ihr Tiefen," },
      { number: 8, text: "Feuer und Hagel, Schnee und Nebel, / du Sturmwind, der sein Wort vollzieht," },
      { number: 9, text: "ihr Berge und all ihr Hügel, / ihr Fruchtbäume und alle Zedern,", footnotes: ["Jes 44,23"] },
      { number: 10, text: "ihr wilden Tiere und alles Vieh, / Kriechtiere und gefiederte Vögel," },
      { number: 11, text: "ihr Könige der Erde und alle Völker, / ihr Fürsten und alle Richter auf Erden," },
      { number: 12, text: "ihr jungen Männer und auch ihr Mädchen, / ihr Alten mit den Jungen!" },
      { number: 13, text: "Loben sollen sie den Namen des Herrn; / denn sein Name allein ist erhaben, / seine Hoheit strahlt über Erde und Himmel." },
      { number: 14, text: "Seinem Volk verleiht er Macht, / das ist ein Ruhm für all seine Frommen, / für Israels Kinder, das Volk, das ihm nahen darf. Halleluja!", footnotes: ["Dtn 4,7"] },
    ] },
    { number: 149, verses: [
      { number: 1, text: "Halleluja! Singt dem Herrn ein neues Lied! / Sein Lob erschalle in der Gemeinde der Frommen.", heading: "Ein Kampflied des Gottesvolkes", footnotes: ["33,3"] },
      { number: 2, text: "Israel soll sich über seinen Schöpfer freuen, / die Kinder Zions über ihren König jauchzen.", footnotes: ["100,3; 24,7-10"] },
      { number: 3, text: "Seinen Namen sollen sie loben beim Reigentanz, / ihm spielen auf Pauken und Harfen.", footnotes: ["68,26; 81,3"] },
      { number: 4, text: "Der Herr hat an seinem Volk Gefallen, / die Gebeugten krönt er mit Sieg.", footnotes: ["Jes 62,4"] },
      { number: 5, text: "In festlichem Glanz sollen die Frommen frohlocken, / auf ihren Lagern jauchzen:" },
      { number: 6, text: "Loblieder auf Gott in ihrem Mund, / ein zweischneidiges Schwert in der Hand,", footnotes: ["2 Makk 15,27"] },
      { number: 7, text: "um die Vergeltung zu vollziehn an den Völkern, / an den Nationen das Strafgericht," },
      { number: 8, text: "um ihre Könige mit Fesseln zu binden, / ihre Fürsten mit eisernen Ketten," },
      { number: 9, text: "um Gericht über sie zu halten, so wie geschrieben steht. / Herrlich ist das für all seine Frommen. Halleluja!" },
    ] },
    { number: 150, verses: [
      { number: 1, text: "Halleluja! Lobt Gott in seinem Heiligtum, / lobt ihn in seiner mächtigen Feste!", heading: "Das große Halleluja", footnotes: ["Dan 3,53"] },
      { number: 2, text: "Lobt ihn für seine großen Taten, / lobt ihn in seiner gewaltigen Größe!" },
      { number: 3, text: "Lobt ihn mit dem Schall der Hörner, / lobt ihn mit Harfe und Zither!", footnotes: ["(3-5) 2 Sam 6,5"] },
      { number: 4, text: "Lobt ihn mit Pauken und Tanz, / lobt ihn mit Flöten und Saitenspiel!", footnotes: ["149,3"] },
      { number: 5, text: "Lobt ihn mit hellen Zimbeln, / lobt ihn mit klingenden Zimbeln!" },
      { number: 6, text: "Alles, was atmet, / lobe den Herrn! Halleluja!", footnotes: ["Offb 5,13"] },
    ] },
  ],
};
