import { Book } from "@/lib/types";

export const wisdom: Book = {
  id: "wisdom",
  name: "Weisheit",
  shortName: "Weish",
  testament: "old",
  introduction: "Den Titel »Buch der Weisheit« trägt dieses Buch in der lateinischen Bibel; in der griechischen Bibel heißt es »Weisheit Salomos«. Das Buch stammt aus der jüdischen Diaspora in Ägypten, wahrscheinlich aus Alexandria, dem berühmten Zentrum hellenistischer Wissenschaft. Es ist das späteste Buch des Alten Testaments. Die Verfolgung gesetzestreuer Juden durch abgefallene Juden (Kap. 2) weist in die Zeit zwischen 80 und 30 v. Chr.; damals hatte auch die Weltmacht Rom den Juden ihre Gunst entzogen. Der Verfasser ist stolz auf seine jüdische Religion und auf sein Volk; er ist aber auch hellenistisch gebildet und offen für die Schönheit der Natur (Kap. 13). Er versteht es, sich in seiner griechischen Muttersprache klar und genau auszudrücken. Die Bibel las er in der griechischen Übersetzung der Septuaginta. Philosophische Gedankengänge, wie sie die Griechen zu seiner Zeit kannten, waren ihm für seine Darstellung der Offenbarung des Alten Testaments hilfreich. Indem er in der Person des Königs Salomo spricht (vgl. 9,7), stellt er sich, wie der Verfasser von Spr 1 - 9 und wie Kohelet, in die Reihe der Weisheitslehrer Israels. Er nennt Salomo jedoch nicht mit Namen, wie er auch sonst keine Namen nennt, um die allgemein menschliche Geltung dieser Weisheit herauszuheben. Der Verfasser wendet sich tröstend und mahnend an seine verfolgten Glaubensgenossen, aber auch drohend und warnend an ihre Verfolger, die abgefallenen Juden, und einladend und werbend an seine heidnische Umwelt, indem er die Offenbarungsreligion als Gabe der Weisheit darstellt. Die herkömmliche Dreiteilung des Buches (Kap. 1 - 5; 6 - 9; 10 19) ist zwar inhaltlich berechtigt; der kunstvolle Aufbau ermöglicht aber eine genauere Abgrenzung der Teile: Der erste Teil gliedert sich in sieben Abschnitte. Er beginnt und schließt mit einer Mahnrede (1,1-15 und 6,1-21); beide Mahnreden richten sich an die Herrscher der Erde, die aufgefordert werden, die Gerechtigkeit zu lieben (1,1-15) und die Weisheit zu suchen (6,1-21). Wie der erste und siebte, so entsprechen sich auch der zweite und sechste Abschnitt (1,16 - 2,24 und 4,20 - 5,23): Dem Triumph der Sünder über die Gerechten wird der Triumph der Gerechten über die Sünder gegenübergestellt. Die drei mittleren Abschnitte (3,1-12; 3,13 - 4,6; 4,7-19) versuchen mit dem Ausblick auf die Unsterblichkeit eine Lösung der Lebensrätsel, um die sich auch Ijob, Kohelet und Jesus Sirach bemühen. Im zweiten Teil lassen sich ebenfalls sieben Abschnitte feststellen. Nach einer Einleitung (6,22-25) wird ausgeführt, dass auch der Weise ein sterblicher Mensch ist wie alle anderen (7,1-6). Er hat um Weisheit gebetet und sie als kostbarste Gabe erhalten (7,7-14). Er bittet Gott darum, gemäß der Weisheit denken und lehren zu können (7,15-21). Dann beschreibt er das Wesen der Weisheit in leuchtenden Farben (7,22 - 8,1). Er stellt die Weisheit als Lehrerin der Tugend vor (8,2-8). Die Weisheit wird ihm zur Lebensgefährtin (8,9-18). Der dritte Teil (8,19 - 19,22) ist ein einziges großes Gebet Salomos. Er bittet Gott um die Weisheit (9,1-19). Dann betrachtet er das rettende Wirken der Weisheit an sieben Beispielen (10,1 - 11,4) und das strafende und rettende Eingreifen Gottes in sieben Gegenüberstellungen (11,5 - 19,22). Eingeschaltet sind zwei Abschnitte über die Art Gottes zu strafen (11,15 - 12,27) und über die Torheit des Götzendienstes (13,1 - 15,19). Die Auszugserzählungen des Buches Exodus schmückt der Verfasser nach Art der jüdischen Schrifterklärung und der griechischen Redekunst frei aus und übersteigert sie, um das Handeln Gottes eindrucksvoll darzustellen. Da er keinen Namen nennt, bekommen die biblischen Ereignisse, auf die er sich bezieht, eine weit reichende, umfassende Bedeutung. Die Griechisch sprechenden Juden der Diaspora haben das Buch der Weisheit wie auch andere deuterokanonische Bücher gern gelesen. Von ihnen hat es die Kirche in ihren Kanon übernommen. Durch seine klare Lehre von der Unsterblichkeit, die erhabene Schau des göttlichen Wirkens im Zusammenklang von Allmacht, Gerechtigkeit und Barmherzigkeit und durch die Aussagen über die aus Gottes Wesen hervorgehende und mit ihm aufs Innigste verbundene Weisheit (7,22 - 8,1) ist das Buch der krönende Abschluss der alttestamentlichen Weisheitsliteratur und führt bis an die Schwelle des Neuen Testaments heran. Paulus spielt öfter auf Texte dieses Buches an, vgl. besonders Röm 1,23-32; 11,33.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Liebt Gerechtigkeit, ihr Herrscher der Erde, / denkt in Frömmigkeit an den Herrn, / sucht ihn mit reinem Herzen!", heading: "Aufforderung zu einem Leben nach der Weisheit: 1,1 - 6,21 | Die Mahnung zu gerechtem Leben: 1,1-15", footnotes: ["1 Chr 29,17", "Die Anrede ist an die Herrscher (wörtlich: Richter) der Erde gerichtet, weil der Verfasser als König Salomo spricht. Angeredet sind aber auch die unter Verfolgung leidenden Juden."] },
      { number: 2, text: "Denn er lässt sich finden von denen, die ihn nicht versuchen, / und zeigt sich denen, die ihm nicht misstrauen.", footnotes: ["Jer 29,13f"] },
      { number: 3, text: "Verkehrte Gedanken trennen von Gott; / wird seine Macht herausgefordert, / dann weist sie die Toren zurück." },
      { number: 4, text: "In eine Seele, die auf Böses sinnt, / kehrt die Weisheit nicht ein, / noch wohnt sie in einem Leib, / der sich der Sünde hingibt." },
      { number: 5, text: "Denn der heilige Geist, der Lehrmeister, flieht vor der Falschheit, / er entfernt sich von unverständigen Gedanken / und wird verscheucht, wenn Unrecht naht.", footnotes: ["Der heilige Geist ist im AT die von Gott ausgehende Kraft, die den Menschen zu außerordentlichen Taten und zu prophetischem Reden befähigt."] },
      { number: 6, text: "Die Weisheit ist ein menschenfreundlicher Geist, / doch lässt sie die Reden des Lästerers nicht straflos; / denn Gott ist Zeuge seiner heimlichen Gedanken, / untrüglich durchschaut er sein Herz / und hört seine Worte.", footnotes: ["7,23; 1 Sam 16,7; Jer 17,9f; Sir 42,18", "seiner heimlichen Gedanken, wörtlich: seiner Nieren. Die Nieren sind im semitischen Denken Sitz der innersten Empfindungen. Das Herz gilt als geistige Mitte, als Sitz der Gedanken und Willensentschlüsse."] },
      { number: 7, text: "Der Geist des Herrn erfüllt den Erdkreis / und er, der alles zusammenhält, kennt jeden Laut.", footnotes: ["Jer 23,23-25; Weish 8,1"] },
      { number: 8, text: "Darum bleibt keiner verborgen, der Böses redet, / das Strafurteil geht nicht an ihm vorüber." },
      { number: 9, text: "Die Pläne des Frevlers werden untersucht; / der Herr erfährt von seinen Reden / und bestraft seine Vergehen." },
      { number: 10, text: "Denn das eifersüchtige Ohr hört alles, / kein leises Murren bleibt ihm verborgen.", footnotes: ["Gott ist eifersüchtig auf seine Ehre bedacht (vgl. Ex 20,5; Jos 24,19; Jes 42,8; Ez 36,22f; 39,25)."] },
      { number: 11, text: "Hütet euch also vor unnützem Murren / und verwehrt eurer Zunge das Verleumden! / Denn euer heimliches Reden verhallt nicht ungehört / und ein Mund, der lügt, tötet die Seele.", footnotes: ["Spr 19,5.9"] },
      { number: 12, text: "Jagt nicht dem Tod nach in den Irrungen eures Lebens / und zieht nicht durch euer Handeln das Verderben herbei!" },
      { number: 13, text: "Denn Gott hat den Tod nicht gemacht / und hat keine Freude am Untergang der Lebenden.", footnotes: ["2,23f; Ez 18,23.32"] },
      { number: 14, text: "Zum Dasein hat er alles geschaffen / und heilbringend sind die Geschöpfe der Welt. / Kein Gift des Verderbens ist in ihnen, / das Reich des Todes hat keine Macht auf der Erde; /" },
      { number: 15, text: "denn die Gerechtigkeit ist unsterblich.", footnotes: ["3,4; 6,18"] },
      { number: 16, text: "Die Frevler aber holen winkend und rufend den Tod herbei / und sehnen sich nach ihm wie nach einem Freund; / sie schließen einen Bund mit ihm, / weil sie es verdienen, ihm zu gehören.", heading: "Vom Treiben der Frevler: 1,16 - 2,24", footnotes: ["Jes 28,15.18; Weish 2,24"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Sie tauschen ihre verkehrten Gedanken aus und sagen: Kurz und traurig ist unser Leben; / für das Ende des Menschen gibt es keine Arznei / und man kennt keinen, der aus der Welt des Todes befreit." },
      { number: 2, text: "Durch Zufall sind wir geworden / und danach werden wir sein, als wären wir nie gewesen. / Der Atem in unserer Nase ist Rauch / und das Denken ist ein Funke, / der vom Schlag des Herzens entfacht wird;" },
      { number: 3, text: "verlöscht er, dann zerfällt der Leib zu Asche / und der Geist verweht wie dünne Luft." },
      { number: 4, text: "Unser Name wird bald vergessen, / niemand denkt mehr an unsere Taten. / Unser Leben geht vorüber wie die Spur einer Wolke / und löst sich auf wie ein Nebel, / der von den Strahlen der Sonne verscheucht / und von ihrer Wärme zu Boden gedrückt wird.", footnotes: ["Koh 9,5"] },
      { number: 5, text: "Unsere Zeit geht vorüber wie ein Schatten, / unser Ende wiederholt sich nicht; / es ist versiegelt und keiner kommt zurück.", footnotes: ["Ijob 8,9; 7,9"] },
      { number: 6, text: "Auf, lasst uns die Güter des Lebens genießen / und die Schöpfung auskosten, / wie es der Jugend zusteht.", footnotes: ["Koh 9,7-10"] },
      { number: 7, text: "Erlesener Wein und Salböl sollen uns reichlich fließen, / keine Blume des Frühlings darf uns entgehen." },
      { number: 8, text: "Bekränzen wir uns mit Rosen, ehe sie verwelken;" },
      { number: 9, text: "keine Wiese bleibe unberührt / von unserem ausgelassenen Treiben. / Überall wollen wir Zeichen der Fröhlichkeit zurücklassen; / das ist unser Anteil, das fällt uns zu." },
      { number: 10, text: "Lasst uns den Gerechten unterdrücken, / der in Armut lebt, / die Witwe nicht schonen / und das graue Haar des betagten Greises nicht scheuen!", footnotes: ["Hab 1,4; Ex 22,21; Lev 19,32"] },
      { number: 11, text: "Unsere Stärke soll bestimmen, was Gerechtigkeit ist; / denn das Schwache erweist sich als unnütz." },
      { number: 12, text: "Lasst uns dem Gerechten auflauern! / Er ist uns unbequem und steht unserem Tun im Weg. / Er wirft uns Vergehen gegen das Gesetz vor / und beschuldigt uns des Verrats an unserer Erziehung." },
      { number: 13, text: "Er rühmt sich, die Erkenntnis Gottes zu besitzen, / und nennt sich einen Knecht des Herrn.", footnotes: ["Der Verfasser hat bei der Verfolgung des Gerechten den Gottesknecht von Deuterojesaja (vgl. die Einleitung zu Jes) und Ps 22 vor Augen. Das griechische Wort für Kind (pais) ist in G Übersetzung des hebräischen Wortes für Knecht (ebed)."] },
      { number: 14, text: "Er ist unserer Gesinnung ein lebendiger Vorwurf, / schon sein Anblick ist uns lästig;" },
      { number: 15, text: "denn er führt ein Leben, / das dem der andern nicht gleicht, / und seine Wege sind grundverschieden." },
      { number: 16, text: "Als falsche Münze gelten wir ihm; / von unseren Wegen hält er sich fern wie von Unrat. / Das Ende der Gerechten preist er glücklich / und prahlt, Gott sei sein Vater." },
      { number: 17, text: "Wir wollen sehen, ob seine Worte wahr sind, / und prüfen, wie es mit ihm ausgeht." },
      { number: 18, text: "Ist der Gerechte wirklich Sohn Gottes, / dann nimmt sich Gott seiner an / und entreißt ihn der Hand seiner Gegner.", footnotes: ["Ps 22,9; Mt 27,42f"] },
      { number: 19, text: "Roh und grausam wollen wir mit ihm verfahren, / um seine Sanftmut kennen zu lernen, / seine Geduld zu erproben." },
      { number: 20, text: "Zu einem ehrlosen Tod wollen wir ihn verurteilen; / er behauptet ja, es werde ihm Hilfe gewährt.", footnotes: ["5,4"] },
      { number: 21, text: "So denken sie, aber sie irren sich; / denn ihre Schlechtigkeit macht sie blind." },
      { number: 22, text: "Sie verstehen von Gottes Geheimnissen nichts, / sie hoffen nicht auf Lohn für die Frömmigkeit / und erwarten keine Auszeichnung für untadelige Seelen." },
      { number: 23, text: "Gott hat den Menschen zur Unvergänglichkeit erschaffen / und ihn zum Bild seines eigenen Wesens gemacht.", footnotes: ["1,14; 3,4; Gen 1,26f", "seines eigenen Wesens: nach anderen Textzeugen: seiner Ewigkeit."] },
      { number: 24, text: "Doch durch den Neid des Teufels kam der Tod in die Welt / und ihn erfahren alle, die ihm angehören.", footnotes: ["Gen 3,1-5; Joh 8,44; Weish 1,16"] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Die Seelen der Gerechten sind in Gottes Hand / und keine Qual kann sie berühren.", heading: "Das jenseitige Los der Guten und der Bösen: 3,1-12" },
      { number: 2, text: "In den Augen der Toren sind sie gestorben, / ihr Heimgang gilt als Unglück,", footnotes: ["Der Verfasser vermeidet für die Gerechten die Ausdrücke «Tod» und «sterben» (vgl. V. 3a; 4,7.10f.16f); nur die Sünder lässt er vom Tod und Sterben der Gerechten sprechen (2,20). Der Tod, der durch den Neid des Teufels in die Welt gekommen ist, ist für den Verfasser zwar der leibliche Tod, aber nur insofern er zum ewigen Tod führt (4,19f; 17,20), also der Tod der Sünder (2,24)."] },
      { number: 3, text: "ihr Scheiden von uns als Vernichtung; / sie aber sind in Frieden.", footnotes: ["Jes 57,2"] },
      { number: 4, text: "In den Augen der Menschen wurden sie gestraft; / doch ihre Hoffnung ist voll Unsterblichkeit.", footnotes: ["Wie in 3,1.3b.5a.6b handelt es sich um das jenseitige Leben der Gerechten. Die Gerechten können auch noch nach dem Martyrium die Hoffnung auf die Unsterblichkeit haben, weil die Auferstehung des Leibes noch aussteht."] },
      { number: 5, text: "Ein wenig nur werden sie gezüchtigt; / doch sie empfangen große Wohltat. / Denn Gott hat sie geprüft / und fand sie seiner würdig." },
      { number: 6, text: "Wie Gold im Schmelzofen hat er sie erprobt / und sie angenommen als ein vollgültiges Opfer." },
      { number: 7, text: "Beim Endgericht werden sie aufleuchten / wie Funken, die durch ein Stoppelfeld sprühen.", footnotes: ["Dan 12,3; Mt 13,43", "Beim Endgericht, wörtlich: In der Zeit ihrer Heimsuchung."] },
      { number: 8, text: "Sie werden Völker richten / und über Nationen herrschen / und der Herr wird ihr König sein in Ewigkeit.", footnotes: ["1 Kor 6,2"] },
      { number: 9, text: "Alle, die auf ihn vertrauen, / werden die Wahrheit erkennen / und die Treuen werden bei ihm bleiben in Liebe. / Denn Gnade und Erbarmen wird seinen Erwählten zuteil.", footnotes: ["4,15"] },
      { number: 10, text: "Die Frevler aber werden für ihre Pläne bestraft, / sie, die den Gerechten missachtet haben / und vom Herrn abgefallen sind." },
      { number: 11, text: "Unglücklich sind alle, / die Weisheit und Belehrung verachten; / leer ist ihre Hoffnung, vergeblich sind ihre Mühen / und wertlos ihre Taten.", footnotes: ["Sir 34,1"] },
      { number: 12, text: "Ihre Frauen sind unverständig / und ihre Kinder böse, / fluchbeladen ist ihr Geschlecht.", footnotes: ["12,11"] },
      { number: 13, text: "Selig ist die Kinderlose, die unschuldig blieb / und kein Lager der Sünde kannte; / sie wird gleich einer Mutter geehrt, / wenn die Seelen ihren Lohn empfangen.", heading: "Kinderlose Gerechte und kinderreiche Frevler: 3,13 - 4,6", footnotes: ["13-4,6: Kinderlosigkeit gilt sonst im AT als Unglück und Schande (Gen 30,23; 1 Sam 1,6f.15f; vgl. Lk 1,25) oder gar als Strafe (Lev 20,20f); im Blick auf die ewige Vergeltung werden hier die Kinderlosen, die Gott treu dienen, der Mutter zahlreicher Kinder gleich geachtet. 13cd: Wörtlich: denn sie wird Frucht haben bei der Heimsuchung der Seelen."] },
      { number: 14, text: "Selig ist auch der Kinderlose, / der sich nicht frevelhaft verging / und gegen den Herrn nichts Böses plante; / besondere Gnade wird seiner Treue zuteil / und ein gar köstlicher Anteil am Tempel des Herrn.", footnotes: ["Jes 56,3", "14a: der Kinderlose, wörtlich: der Eunuch. 14e: ein gar köstlicher, wörtlich: ein mehr beglückender (als wenn er Kinder hätte)."] },
      { number: 15, text: "Denn ruhmreich ist der Lohn guter Mühe / und unvergänglich die Wurzel der Klugheit." },
      { number: 16, text: "Doch die Kinder von Ehebrechern verkümmern / und die Nachkommen einer sündigen Verbindung schwinden dahin.", footnotes: ["(16-19) 4,3-6; Sir 41,5-7"] },
      { number: 17, text: "Auch wenn sie lange leben, gelten sie nichts / und ehrlos ist am Ende ihr Alter." },
      { number: 18, text: "Sterben sie früh, so haben sie keine Hoffnung / und keinen Trost am Tag des Gerichts;" },
      { number: 19, text: "denn schlimm ist das Ende eines schuldhaften Geschlechts." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Besser ist Kinderlosigkeit mit Tugend; unsterblich ist ihr Ruhm, / sie steht in Ehren bei Gott und bei den Menschen." },
      { number: 2, text: "Ist sie zugegen, ahmt man sie nach; / ist sie entschwunden, sehnt man sie herbei. / In der Ewigkeit triumphiert sie, / geschmückt mit dem Kranz, / Siegerin im Wettstreit um einen edlen Preis.", footnotes: ["5,16"] },
      { number: 3, text: "Doch die große Kinderschar der Frevler bringt keinen Nutzen; / sie ist ein unechtes Gewächs, / treibt keine Wurzeln in die Tiefe / und fasst keinen sicheren Grund.", footnotes: ["Sir 23,25"] },
      { number: 4, text: "Breitet es auch eine Zeit lang üppig seine Zweige aus, / so wird es doch vom Wind hin und her geschüttelt / und von der Gewalt der Stürme entwurzelt." },
      { number: 5, text: "Die Äste, die noch schwach sind, werden geknickt; / ihre Frucht ist unbrauchbar, unreif und ungenießbar, / zu gar nichts geeignet." },
      { number: 6, text: "Denn die Kinder eines sündigen Beischlafs / treten im Gericht als Zeugen auf / für die Schlechtigkeit ihrer Eltern." },
      { number: 7, text: "Der Gerechte aber, kommt auch sein Ende früh, / geht in Gottes Ruhe ein.", heading: "Der frühe Heimgang des Gerechten und das lange Leben der Frevler: 4,7-19", footnotes: ["Jes 57,2"] },
      { number: 8, text: "Denn ehrenvolles Alter besteht nicht in einem langen Leben / und wird nicht an der Zahl der Jahre gemessen." },
      { number: 9, text: "Mehr als graues Haar bedeutet für die Menschen die Klugheit / und mehr als Greisenalter wiegt ein Leben ohne Tadel." },
      { number: 10, text: "Er gefiel Gott und wurde von ihm geliebt; / da er mitten unter Sündern lebte, wurde er entrückt.", footnotes: ["Gen 5,24; Sir 44,16; Hebr 11,5"] },
      { number: 11, text: "Er wurde weggenommen, / damit nicht Schlechtigkeit seine Einsicht verkehrte / und Arglist seine Seele täuschte." },
      { number: 12, text: "Denn der Reiz des Bösen verdunkelt das Gute / und der Taumel der Begierde verdirbt den arglosen Sinn." },
      { number: 13, text: "Früh vollendet, hat der Gerechte doch ein volles Leben gehabt;" },
      { number: 14, text: "da seine Seele dem Herrn gefiel, / enteilte sie aus der Mitte des Bösen. / Die Leute sahen es, ohne es zu verstehen; / sie nahmen es sich nicht zu Herzen,", footnotes: ["Jes 57,1"] },
      { number: 15, text: "dass Gnade und Erbarmen seinen Auserwählten zuteil wird, / Belohnung seinen Heiligen.", footnotes: ["3,9"] },
      { number: 16, text: "Der Gerechte, der entschlafen ist, / verurteilt die Frevler, die noch leben, / die früh vollendete Jugend / das hohe Alter des Ungerechten." },
      { number: 17, text: "Die Frevler sehen das Ende des Weisen, / verstehen aber nicht, was der Herr mit ihm wollte / und warum er ihn in Sicherheit brachte." },
      { number: 18, text: "Sie sehen es und gehen darüber hinweg; / doch der Herr lacht über sie.", footnotes: ["Ps 2,4"] },
      { number: 19, text: "Dann werden sie verachtete Leichen sein, / ewiger Spott bei den Toten. / Sie werden verstummen, / wenn er sie kopfüber hinabstürzt / und aus ihren Grundfesten reißt. / Sie werden völlig vernichtet und erleiden Qualen; / die Erinnerung an sie verschwindet.", footnotes: ["Jes 14,16-19; Ijob 18,17"] },
      { number: 20, text: "Zitternd kommen sie zum Gericht über ihre Sünden; / ihre Vergehen treten ihnen entgegen und überführen sie.", heading: "Die Bösen und die Guten im Endgericht: 4,20 - 5,23", footnotes: ["20-5,23: Gegenstück zu 1,16 - 2,24: Im Endgericht müssen die Sünder in Gegenwart des von ihnen verachteten und verfolgten Gerechten ihren Irrtum und ihr verfehltes Leben beklagen."] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Dann wird der Gerechte voll Zuversicht dastehen vor denen, die ihn bedrängt / und seine Mühen verachtet haben." },
      { number: 2, text: "Wenn sie ihn sehen, packt sie entsetzliche Furcht / und sie geraten außer sich / über seine unerwartete Rettung." },
      { number: 3, text: "Jetzt denken sie anders; / seufzend und voll Angst sagen sie zueinander:" },
      { number: 4, text: "Dieser war es, den wir einst verlachten, / verspotteten und verhöhnten, wir Toren. / Sein Leben hielten wir für Wahnsinn / und sein Ende für ehrlos.", footnotes: ["3,2f"] },
      { number: 5, text: "Jetzt zählt er zu den Söhnen Gottes, / bei den Heiligen hat er sein Erbteil.", footnotes: ["2,18; Kol 1,12"] },
      { number: 6, text: "Also sind wir vom Weg der Wahrheit abgeirrt; / das Licht der Gerechtigkeit strahlte uns nicht / und die Sonne ging nicht für uns auf.", footnotes: ["Ps 119,30.151"] },
      { number: 7, text: "Bis zum Überdruss gingen wir die Pfade des Unrechts / und des Verderbens / und wanderten durch weglose Wüsten, / aber den Weg des Herrn erkannten wir nicht.", footnotes: ["Ps 95,10"] },
      { number: 8, text: "Was nützte uns der Übermut, / was brachten uns Reichtum und Prahlerei?" },
      { number: 9, text: "All das ist vorbei wie ein Schatten, / wie eine flüchtige Nachricht." },
      { number: 10, text: "Wie wenn ein Schiff durch die wogende Flut fährt: / Ist es vorübergezogen, so ist von ihm keine Spur mehr zu finden, / kein Pfad seines Kiels in den Wellen." },
      { number: 11, text: "Wie wenn ein Vogel durch die Luft fliegt: / Kein Zeichen findet sich von seiner Bahn, er peitscht die leichte Luft mit seinem Flügelschlag / und durchschneidet sie mit gewaltig rauschenden Schwingen, / doch bleibt kein Zeichen seines Weges in ihr zurück." },
      { number: 12, text: "Oder wie wenn ein Pfeil auf das Ziel geschossen wird: / Die geteilte Luft strömt sofort wieder zusammen, / sodass man seine Bahn nicht mehr erkennt." },
      { number: 13, text: "So sind wir ins Dasein getreten, um hinzuschwinden; / wir hatten keinerlei Tugend aufzuweisen, / sondern wurden von unserer Schlechtigkeit verschlungen.", footnotes: ["Vg fügt als V. 14 hinzu: So sprachen in der Unterwelt die, die gesündigt haben. - Entsprechend verschiebt sich in Vg die Verszählung."] },
      { number: 14, text: "Ja, die Hoffnung des Frevlers ist wie die Spreu, die der Wind verweht, / wie der Gischt, den der Sturm verjagt, / wie der Rauch, den der Wind zerstäubt; / sie schwindet wie die Erinnerung an einen flüchtigen Gast.", footnotes: ["Ps 1,4; Jes 29,5; Ps 68,3", "Gischt: so nach S und der altlateinischen Übersetzung; G: Reif."] },
      { number: 15, text: "Die Gerechten aber leben in Ewigkeit, / der Herr belohnt sie, der Höchste sorgt für sie." },
      { number: 16, text: "Darum werden sie aus der Hand des Herrn / das Reich der Herrlichkeit empfangen und die Krone der Schönheit. Denn er wird sie mit seiner Rechten behüten / und mit seinem Arm beschützen.", footnotes: ["Jes 62,3; 41,10"] },
      { number: 17, text: "Er rüstet sich mit seinem Eifer / und macht die Schöpfung zur Waffe, mit der er die Feinde bestraft.", footnotes: ["(17-19) Eph 6,13-17"] },
      { number: 18, text: "Als Panzer zieht er Gerechtigkeit an / und als Helm setzt er strenges Gericht auf.", footnotes: ["Jes 59,17"] },
      { number: 19, text: "Als Schild nimmt er unüberwindliche Heiligkeit /" },
      { number: 20, text: "und grimmigen Zorn schärft er zum Schwert; / zusammen mit ihm kämpft die ganze Welt gegen die Toren." },
      { number: 21, text: "Treffsicher fahren die Blitzespfeile dahin; / abgeschossen aus den Wolken wie von einem wohlgerundeten Bogen, / fliegen sie auf ihr Ziel.", footnotes: ["Hab 3,11"] },
      { number: 22, text: "Eine Steinschleuder entsendet Hagelkörner, / die voll von göttlichem Zorn sind. Das Wasser des Meeres wütet gegen die Feinde / und Ströme schlagen grimmig über ihnen zusammen.", footnotes: ["Jos 10,11; Ex 14,27f; 15,10"] },
      { number: 23, text: "Der Atem des Allmächtigen erhebt sich gegen sie / und trägt sie wie ein Sturm davon. So bringt die Gesetzlosigkeit Verheerung über die ganze Erde / und das böse Tun stürzt die Throne der Mächtigen.", footnotes: ["5,14; Jes 24,3; Sir 10,14; Lk 1,52"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Hört also, ihr Könige, und seid verständig, / lernt, ihr Gebieter der ganzen Welt!", heading: "Die Mahnung, die Weisheit zu suchen: 6,1-21", footnotes: ["Ps 2,10", "1-8: Salomo nimmt die Anrede an die Herrscher wieder auf; mit «also» zeigt er, dass er sie auch in 1,16 - 5,23 als seine Hörer voraussetzt, obwohl er sie nicht mehr direkt anredet. Die Mahnung, verständig zu sein, nimmt auf das Weltgericht Bezug (5,17-23). Die Könige sollen sich nicht mit ihrer Herrschermacht brüsten, sondern bedenken, dass sie sich als Diener Gottes und seines Reiches vor dem strengen Richter zu verantworten haben. Vg fügt vor V. 1 ein: Besser ist Weisheit als Kraft und ein kluger Mann (ist besser) als ein starker. Dieser Vers ist in Vg als V. 1 gezählt; entsprechend verschiebt sich die Verszählung."] },
      { number: 2, text: "Horcht, ihr Herrscher der Massen, / die ihr stolz seid auf Völkerscharen!" },
      { number: 3, text: "Der Herr hat euch die Gewalt gegeben, / der Höchste die Herrschaft, / er, der eure Taten prüft und eure Pläne durchforscht.", footnotes: ["Röm 13,1; 1 Chr 28,9"] },
      { number: 4, text: "Ihr seid Diener seines Reichs, / aber ihr habt kein gerechtes Urteil gefällt, das Gesetz nicht bewahrt / und die Weisung Gottes nicht befolgt." },
      { number: 5, text: "Schnell und furchtbar wird er kommen und euch bestrafen; / denn über die Großen ergeht ein strenges Gericht." },
      { number: 6, text: "Der Geringe erfährt Nachsicht und Erbarmen, / doch die Mächtigen werden gerichtet mit Macht." },
      { number: 7, text: "Denn der Herrscher des Alls scheut niemand / und weicht vor keiner Größe zurück. Er hat Klein und Groß erschaffen / und trägt gleiche Sorge für alle;", footnotes: ["Ijob 34,19; Weish 12,13"] },
      { number: 8, text: "den Mächtigen aber droht strenge Untersuchung.", footnotes: ["Lk 12,48"] },
      { number: 9, text: "An euch also, ihr Herrscher, richten sich meine Worte, / damit ihr Weisheit lernt und nicht sündigt." },
      { number: 10, text: "Wer das Heilige heilig hält, wird geheiligt, / und wer sich darin unterweisen lässt, findet Schutz." },
      { number: 11, text: "Verlangt also nach meinen Worten; / sehnt euch danach und ihr werdet gute Belehrung empfangen." },
      { number: 12, text: "Strahlend und unvergänglich ist die Weisheit; / wer sie liebt, erblickt sie schnell, / und wer sie sucht, findet sie.", footnotes: ["Spr 8,17"] },
      { number: 13, text: "Denen, die nach ihr verlangen, / gibt sie sich sogleich zu erkennen." },
      { number: 14, text: "Wer sie am frühen Morgen sucht, braucht keine Mühe, / er findet sie vor seiner Türe sitzen.", footnotes: ["Spr 8,34"] },
      { number: 15, text: "Über sie nachzusinnen ist vollkommene Klugheit; / wer ihretwegen wacht, wird schnell von Sorge frei." },
      { number: 16, text: "Sie geht selbst umher, um die zu suchen, die ihrer würdig sind; / freundlich erscheint sie ihnen auf allen Wegen / und kommt jenen entgegen, die an sie denken.", footnotes: ["Spr 8,2f"] },
      { number: 17, text: "Ihr Anfang ist aufrichtiges Verlangen nach Bildung; / das eifrige Bemühen um Bildung aber ist Liebe.", footnotes: ["17-21: Ein sog. Kettenschluss, der zeigt, wie das Verlangen nach Weisheit in Gottes Nähe und damit zur Herrschaft führt."] },
      { number: 18, text: "Liebe ist Halten ihrer Gebote; / Erfüllen der Gebote sichert Unvergänglichkeit, /", footnotes: ["Joh 14,23"] },
      { number: 19, text: "und Unvergänglichkeit bringt in Gottes Nähe." },
      { number: 20, text: "So führt das Verlangen nach Weisheit zur Herrschaft hinauf." },
      { number: 21, text: "Ihr Herrscher der Völker, wenn ihr Gefallen an Thronen und Zeptern habt, / dann ehrt die Weisheit, damit ihr ewig herrscht.", footnotes: ["Einige wichtige Vg-Handschriften fügen als V. 23 hinzu: Liebt das Licht der Weisheit, ihr alle, die ihr den Völkern vorsteht. - Die VV. 22-25 werden in Vg als VV. 24-27 gezählt."] },
      { number: 22, text: "Ich will verkünden, was die Weisheit ist und wie sie wurde, / und will euch kein Geheimnis verbergen. Ich will ihre Spur vom Anfang der Schöpfung an verfolgen, / ihre Kenntnis will ich verbreiten / und nicht an der Wahrheit vorbeigehen.", heading: "Das Wesen und Wirken der Weisheit: 6,22 - 8,18 | Einleitung: 6,22-25", footnotes: ["Spr 8,22-31", "22-25: Salomo will seine Weisheit neidlos mitteilen und möglichst viele weise machen, weil eine große Zahl von Weisen der Welt Heil, Frieden und Glück bringen kann."] },
      { number: 23, text: "Verzehrender Neid soll mich nicht auf meinem Weg begleiten; / denn er hat mit der Weisheit nichts gemein.", footnotes: ["7,13"] },
      { number: 24, text: "Eine große Anzahl von Weisen ist Heil für die Welt, / ein kluger König ist Wohlstand für das Volk.", footnotes: ["Spr 11,14"] },
      { number: 25, text: "Lasst euch also durch meine Worte unterweisen; / es wird euch von Nutzen sein." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Auch ich bin ein sterblicher Mensch wie alle anderen, / Nachkomme des ersten, aus Erde gebildeten Menschen. Im Schoß der Mutter wurde ich zu Fleisch geformt, /", heading: "Der sterbliche Mensch als Empfänger der Weisheit: 7,1-6", footnotes: ["1-6: Auch der weise König Salomo ist nur ein armseliger Mensch, dem die Weisheit nicht angeboren ist."] },
      { number: 2, text: "zu dem das Blut in zehn Monaten gerann durch den Samen des Mannes / und die Lust, die im Beischlaf hinzukam." },
      { number: 3, text: "Geboren atmete auch ich die gemeinsame Luft, / ich fiel auf die Erde, die Gleiches von allen erduldet, / und Weinen war mein erster Laut wie bei allen." },
      { number: 4, text: "In Windeln und mit Sorgen wurde ich aufgezogen; /" },
      { number: 5, text: "kein König trat anders ins Dasein." },
      { number: 6, text: "Alle haben den einen gleichen Eingang zum Leben; / gleich ist auch der Ausgang." },
      { number: 7, text: "Daher betete ich und es wurde mir Klugheit gegeben; / ich flehte und der Geist der Weisheit kam zu mir.", heading: "Die Gottesgabe der Weisheit: 7,7-14", footnotes: ["8,21", "7-14: Nur durch das Gebet (vgl. 8,21 - 9,19 und 1 Kön 3,6-9) kann Salomo die Weisheit als Geschenk von Gott erhalten."] },
      { number: 8, text: "Ich zog sie Zeptern und Thronen vor, / Reichtum achtete ich für nichts im Vergleich mit ihr.", footnotes: ["Spr 8,11"] },
      { number: 9, text: "Keinen Edelstein stellte ich ihr gleich; / denn alles Gold erscheint neben ihr wie ein wenig Sand / und Silber gilt ihr gegenüber so viel wie Lehm.", footnotes: ["Spr 3,14"] },
      { number: 10, text: "Ich liebte sie mehr als Gesundheit und Schönheit / und zog ihren Besitz dem Lichte vor; / denn niemals erlischt der Glanz, / der von ihr ausstrahlt.", footnotes: ["7,29f"] },
      { number: 11, text: "Zugleich mit ihr kam alles Gute zu mir, / unzählbare Reichtümer waren in ihren Händen.", footnotes: ["1 Kön 3,13; Spr 3,16; 8,18"] },
      { number: 12, text: "Ich freute mich über sie alle, / weil die Weisheit lehrt, sie richtig zu gebrauchen, / wusste aber nicht, dass sie auch deren Ursprung ist." },
      { number: 13, text: "Uneigennützig lernte ich und neidlos gebe ich weiter; / ihren Reichtum behalte ich nicht für mich.", footnotes: ["6,22"] },
      { number: 14, text: "Ein unerschöpflicher Schatz ist sie für die Menschen; / alle, die ihn erwerben, erlangen die Freundschaft Gottes. / Sie sind empfohlen durch die Gaben der Unterweisung.", footnotes: ["7,28"] },
      { number: 15, text: "Mir aber gewähre Gott, nach meiner Einsicht zu sprechen / und zu denken, wie die empfangenen Gaben es wert sind; denn er ist der Führer der Weisheit / und hält die Weisen auf dem rechten Weg.", heading: "Bitte um die Gabe der Lehre: 7,15-21" },
      { number: 16, text: "Wir und unsere Worte sind in seiner Hand, / auch alle Klugheit und praktische Erfahrung." },
      { number: 17, text: "Er verlieh mir untrügliche Kenntnis der Dinge, / sodass ich den Aufbau der Welt und das Wirken der Elemente verstehe,", footnotes: ["1 Kön 5,9f"] },
      { number: 18, text: "Anfang und Ende und Mitte der Zeiten, / die Abfolge der Sonnenwenden und den Wandel der Jahreszeiten," },
      { number: 19, text: "den Kreislauf der Jahre und die Stellung der Sterne," },
      { number: 20, text: "die Natur der Tiere und die Wildheit der Raubtiere, / die Gewalt der Geister und die Gedanken der Menschen, / die Verschiedenheit der Pflanzen und die Kräfte der Wurzeln.", footnotes: ["1 Kön 5,13"] },
      { number: 21, text: "Alles Verborgene und alles Offenbare habe ich erkannt; / denn es lehrte mich die Weisheit, die Meisterin aller Dinge.", footnotes: ["8,5f; 9,9"] },
      { number: 22, text: "In ihr ist ein Geist, / gedankenvoll, heilig, einzigartig, mannigfaltig, zart, beweglich, / durchdringend, unbefleckt, klar, / unverletzlich, das Gute liebend, scharf,", heading: "Das Wesen der Weisheit: 7,22 - 8,1", footnotes: ["22f: Es werden einundzwanzig (7 mal 3) Eigenschaften der Weisheit aufgezählt."] },
      { number: 23, text: "nicht zu hemmen, wohltätig, menschenfreundlich, / fest, sicher, ohne Sorge, alles vermögend, alles überwachend / und alle Geister durchdringend, / die denkenden, reinen und zartesten." },
      { number: 24, text: "Denn die Weisheit ist beweglicher als alle Bewegung; / in ihrer Reinheit durchdringt und erfüllt sie alles." },
      { number: 25, text: "Sie ist ein Hauch der Kraft Gottes / und reiner Ausfluss der Herrlichkeit des Allherrschers; / darum fällt kein Schatten auf sie." },
      { number: 26, text: "Sie ist der Widerschein des ewigen Lichts, / der ungetrübte Spiegel von Gottes Kraft, / das Bild seiner Vollkommenheit.", footnotes: ["Hebr 1,3; Kol 1,15"] },
      { number: 27, text: "Sie ist nur eine und vermag doch alles; / ohne sich zu ändern, erneuert sie alles. Von Geschlecht zu Geschlecht tritt sie in heilige Seelen ein / und schafft Freunde Gottes und Propheten;", footnotes: ["Ps 104,30"] },
      { number: 28, text: "denn Gott liebt nur den, / der mit der Weisheit zusammenwohnt.", footnotes: ["Sir 4,14"] },
      { number: 29, text: "Sie ist schöner als die Sonne / und übertrifft jedes Sternbild. / Sie ist strahlender als das Licht;" },
      { number: 30, text: "denn diesem folgt die Nacht, / doch über die Weisheit siegt keine Schlechtigkeit." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Machtvoll entfaltet sie ihre Kraft von einem Ende zum andern / und durchwaltet voll Güte das All.", footnotes: ["1,7; 12,18; 15,1"] },
      { number: 2, text: "Sie habe ich geliebt und gesucht von Jugend auf, / ich suchte sie als Braut heimzuführen / und fand Gefallen an ihrer Schönheit.", heading: "Die Weisheit als Lehrerin der Tugend: 8,2-8", footnotes: ["Sir 15,2", "2-8: Salomo hat die Weisheit von Jugend auf geliebt, weil sie mit Gott in innigster Verbindung steht, alle Güter gewährt, die vier Kardinaltugenden lehrt, die alle anderen in sich enthalten, und so zu einem rechtschaffenen Leben anleitet."] },
      { number: 3, text: "Im Umgang mit Gott beweist sie ihren Adel, / der Herr über das All gewann sie lieb." },
      { number: 4, text: "Eingeweiht in das Wissen Gottes, / bestimmte sie seine Werke.", footnotes: ["9,4"] },
      { number: 5, text: "Ist Reichtum begehrenswerter Besitz im Leben, / was ist dann reicher als die Weisheit, die in allem wirkt?", footnotes: ["7,22; (5-7) 7,11-14"] },
      { number: 6, text: "Wenn Klugheit wirksam ist, / wer in aller Welt ist ein größerer Meister als sie?" },
      { number: 7, text: "Wenn jemand Gerechtigkeit liebt, / in ihren Mühen findet er die Tugenden. Denn sie lehrt Maß und Klugheit, / Gerechtigkeit und Tapferkeit, / die Tugenden, die im Leben der Menschen nützlicher sind als alles andere." },
      { number: 8, text: "Wenn jemand nach reicher Erfahrung strebt: / sie kennt das Vergangene und errät das Kommende, sie versteht, die Worte schön zu formen und Rätselhaftes zu deuten; / sie weiß im Voraus Zeichen und Wunder / und kennt den Ausgang von Perioden und Zeiten.", footnotes: ["Sir 42,19; Spr 1,6"] },
      { number: 9, text: "So beschloss ich, sie als Lebensgefährtin heimzuführen; / denn ich wusste, dass sie mir guten Rat gibt / und Trost in Sorge und Leid.", heading: "Die Weisheit als Lebensgefährtin: 8,9-18", footnotes: ["Spr 8,14"] },
      { number: 10, text: "Mit ihr werde ich Ruhm beim Volke haben / und trotz meiner Jugend vom Alter geehrt sein." },
      { number: 11, text: "Ich werde als scharfsinniger Richter gelten / und in den Augen der Mächtigen Staunen erregen." },
      { number: 12, text: "Schweige ich, so warten sie in Spannung, / spreche ich, so merken sie auf, / rede ich länger, so legen sie die Hand auf den Mund.", footnotes: ["Ijob 29,21-23; 29,9"] },
      { number: 13, text: "Mit ihr werde ich Unsterblichkeit erlangen / und ewigen Ruhm bei der Nachwelt hinterlassen." },
      { number: 14, text: "Völker werde ich sorgsam leiten / und Nationen werden mir untertan sein.", footnotes: ["Spr 8,15f"] },
      { number: 15, text: "Schreckliche Tyrannen werden mich fürchten, wenn sie von mir hören; / in der Volksversammlung werde ich mich als tüchtig und im Krieg als tapfer erweisen." },
      { number: 16, text: "Komme ich nach Hause, / dann werde ich bei ihr ausruhen; denn der Umgang mit ihr hat nichts Bitteres, / das Leben mit ihr kennt keinen Schmerz, / sondern nur Frohsinn und Freude.", footnotes: ["Sir 15,6"] },
      { number: 17, text: "Als ich dies bei mir überlegte und in meinem Herzen erwog, / dass das Leben mit der Weisheit Unsterblichkeit bringt," },
      { number: 18, text: "die Freundschaft mit ihr reine Freude / und die Mühen ihrer Hände unerschöpflichen Reichtum, dass stete Gemeinschaft mit ihr Klugheit bringt / und das Zwiegespräch mit ihr Ruhm -, / da ging ich auf die Suche nach ihr, um sie heimzuführen.", footnotes: ["Spr 8,18"] },
      { number: 19, text: "Ich war ein begabtes Kind und hatte eine gute Seele erhalten,", heading: "Salomos großes Gebet: Das Walten der Weisheit in der Geschichte: 8,19 - 19,22 | Das Gebet um Weisheit: 8,19 - 9,19 | Einleitung: 8,19-21" },
      { number: 20, text: "oder vielmehr: gut, wie ich war, kam ich in einen unverdorbenen Leib." },
      { number: 21, text: "Ich erkannte aber, dass ich die Weisheit nur als Geschenk Gottes erhalten könne - und schon hier war es die Klugheit, die mich erkennen ließ, wessen Gnadengeschenk sie ist. Daher wandte ich mich an den Herrn und sprach zu ihm aus ganzem Herzen:", footnotes: ["Spr 2,6; Weish 7,7"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Gott der Väter und Herr des Erbarmens, / du hast das All durch dein Wort gemacht.", heading: "Die Bitte um Weisheit: 9,1-19" },
      { number: 2, text: "Den Menschen hast du durch deine Weisheit erschaffen, / damit er über deine Geschöpfe herrscht.", footnotes: ["Gen 1,26-28"] },
      { number: 3, text: "Er soll die Welt in Heiligkeit und Gerechtigkeit leiten / und Gericht halten in rechter Gesinnung.", footnotes: ["Lk 1,75"] },
      { number: 4, text: "Gib mir die Weisheit, die an deiner Seite thront, / und verstoß mich nicht aus der Schar deiner Kinder!", footnotes: ["2 Chr 1,10"] },
      { number: 5, text: "Ich bin ja dein Knecht, der Sohn deiner Magd, / ein schwacher Mensch, dessen Leben nur kurz ist, / und gering ist meine Einsicht in Recht und Gesetz.", footnotes: ["Ps 116,16; Ijob 14,1"] },
      { number: 6, text: "Wäre einer auch vollkommen unter den Menschen, / er wird kein Ansehen genießen, wenn ihm deine Weisheit fehlt." },
      { number: 7, text: "Du bist es, der mich zum König deines Volkes / und zum Richter deiner Söhne und Töchter erwählt hat." },
      { number: 8, text: "Du hast befohlen, einen Tempel auf deinem heiligen Berg zu bauen / und einen Altar in der Stadt deiner Wohnung, / ein Abbild des heiligen Zeltes, das du von Anfang an entworfen hast.", footnotes: ["1 Kön 5,19; Ps 132,13f; Ex 29,9.40; Hebr 8,2", "Nach Ex 25,9.40 sollte Mose das heilige Zelt nach dem Muster bauen, das Gott ihm gezeigt hatte. Da der Tempel nach dem Vorbild des heiligen Zeltes gebaut war, ist er ebenfalls nach jenem Muster gebaut. Nach anderer Auffassung ist mit dem «Zelt» der Himmel gemeint."] },
      { number: 9, text: "Mit dir ist die Weisheit, die deine Werke kennt / und die zugegen war, als du die Welt erschufst. Sie weiß, was dir gefällt / und was recht ist nach deinen Geboten.", footnotes: ["8,4; Spr 8,27-30"] },
      { number: 10, text: "Sende sie vom heiligen Himmel / und schick sie vom Thron deiner Herrlichkeit, damit sie bei mir sei und alle Mühe mit mir teile / und damit ich erkenne, was dir gefällt.", footnotes: ["9,4"] },
      { number: 11, text: "Denn sie weiß und versteht alles; / sie wird mich in meinem Tun besonnen leiten / und mich in ihrem Lichtglanz schützen.", footnotes: ["10,11-14; 11,1"] },
      { number: 12, text: "Dann wird dir mein Handeln gefallen; / ich werde dein Volk gerecht regieren / und des Throns meines Vaters würdig sein." },
      { number: 13, text: "Denn welcher Mensch kann Gottes Plan erkennen, / oder wer begreift, was der Herr will?", footnotes: ["Jes 40,13 G; Röm 11,34"] },
      { number: 14, text: "Unsicher sind die Berechnungen der Sterblichen / und hinfällig unsere Gedanken;" },
      { number: 15, text: "denn der vergängliche Leib beschwert die Seele / und das irdische Zelt belastet den um vieles besorgten Geist.", footnotes: ["2 Kor 5,1"] },
      { number: 16, text: "Wir erraten kaum, was auf der Erde vorgeht, / und finden nur mit Mühe, was doch auf der Hand liegt; / wer kann dann ergründen, was im Himmel ist?", footnotes: ["Joh 3,12"] },
      { number: 17, text: "Wer hat je deinen Plan erkannt, wenn du ihm nicht Weisheit gegeben / und deinen heiligen Geist aus der Höhe gesandt hast?", footnotes: ["Joh 3,13; 1 Kor 2,16; Jes 32,15"] },
      { number: 18, text: "So wurden die Pfade der Erdenbewohner gerade gemacht / und die Menschen lernten, was dir gefällt;", footnotes: ["Bar 4,4"] },
      { number: 19, text: "durch die Weisheit wurden sie gerettet.", footnotes: ["Einige Vg-Handschriften fügen hinzu: alle, die dir gefallen haben, Herr, von Anfang an."] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Sie hat den Urvater der Welt nach seiner Erschaffung behütet, als er noch allein war; sie hat ihn aus seiner Sünde befreit", heading: "Die rettende Macht der Weisheit - sieben Beispiele: 10,1 - 11,4 | Adam: 10,1-3", footnotes: ["Sir 49,16; Gen 5,1"] },
      { number: 2, text: "und ihm die Kraft gegeben, über alles zu herrschen.", footnotes: ["9,2; Gen 1,28"] },
      { number: 3, text: "Ein Ungerechter aber, der in seinem Zorn von ihr abfiel, ging durch seine Leidenschaft zugrunde, die ihn zum Brudermord trieb.", footnotes: ["Gen 4,3-15"] },
      { number: 4, text: "Die Weisheit hat die Erde, die seinetwegen überflutet wurde, wieder gerettet und den Gerechten auf wertlosem Holz durch die Wasser gesteuert.", heading: "Noach: 10,4", footnotes: ["Sir 44,17f; Weish 14,6f"] },
      { number: 5, text: "Als die Völker, einmütig nur in ihrer Schlechtigkeit, durch die Verwirrung ihrer Sprache getrennt wurden, erwählte sie den Gerechten und behütete ihn, sodass er vor Gott ohne Tadel war und trotz der Liebe zu seinem Kind stark blieb.", heading: "Abraham: 10,5", footnotes: ["Gen 11,1-9; 12,1-3; 15,1-6; 22,1-12; Sir 44,19f"] },
      { number: 6, text: "Als die Frevler zugrunde gingen, rettete sie einen Gerechten, sodass er vor dem Feuer fliehen konnte, das auf die fünf Städte fiel;", heading: "Lot: 10,6-9", footnotes: ["Gen 19,23-25"] },
      { number: 7, text: "von ihrer Schlechtigkeit zeugen heute noch rauchendes Ödland und Pflanzen, die zur Unzeit Früchte tragen, und eine Salzsäule ragt als Denkmal einer ungläubigen Seele empor.", footnotes: ["Gen 19,26; Lk 17,32"] },
      { number: 8, text: "Jene, die an der Weisheit achtlos vorübergingen, erlitten nicht nur Schaden, weil sie das Gute nicht erkannten, sondern sie hinterließen auch den Lebenden ein Mahnmal ihrer Torheit, damit nicht verborgen bleibe, worin sie sich verfehlt hatten." },
      { number: 9, text: "Die Weisheit aber rettete ihre Diener aus jeglicher Mühsal." },
      { number: 10, text: "Einen Gerechten, der vor dem Zorn des Bruders floh, geleitete sie auf geraden Wegen, zeigte ihm das Reich Gottes und enthüllte ihm heilige Geheimnisse. Sie machte ihn reich bei seiner harten Arbeit und vermehrte den Ertrag seiner Mühen.", heading: "Jakob: 10,10-12", footnotes: ["Gen 27,41-45; 28,10-15; 30,31-43"] },
      { number: 11, text: "Sie half ihm gegen die Habsucht seiner Unterdrücker und verschaffte ihm Wohlstand." },
      { number: 12, text: "Sie beschützte ihn vor seinen Feinden und gab ihm Sicherheit vor seinen Verfolgern. In einem harten Kampf verlieh sie ihm den Siegespreis, damit er erkannte, dass Gottesfurcht stärker als alles andere ist.", footnotes: ["Gen 31 - 33; Hos 12,4-7"] },
      { number: 13, text: "Einen Gerechten, der verkauft worden war, ließ sie nicht im Stich, sondern bewahrte ihn vor der Sünde.", heading: "Josef: 10,13-14", footnotes: ["Gen 39; Ps 105,17f"] },
      { number: 14, text: "Sie stieg mit ihm in den Kerker hinab und verließ ihn während seiner Gefangenschaft nicht, bis sie ihm das königliche Zepter brachte und Gewalt über seine Bedrücker. Sie überführte alle, die ihn beschuldigt hatten, als Lügner und verlieh ihm ewigen Ruhm.", footnotes: ["Gen 41,39-47; Ps 105,19-22"] },
      { number: 15, text: "Sie hat ein heiliges Volk, ein untadeliges Geschlecht, aus der Gewalt einer Nation gerettet, die es unterdrückte.", heading: "Das Volk Israel: 10,15 - 11,4", footnotes: ["18,7; 19,1-9; Ex 12,37-42; Ps 106,10"] },
      { number: 16, text: "Sie ging in die Seele eines Dieners des Herrn ein und widerstand schrecklichen Königen durch Zeichen und Wunder.", footnotes: ["Ex 7-12; Ps 78,43-51; 105,27-37", "Der «Diener des Herrn» ist Mose."] },
      { number: 17, text: "Sie gab den Heiligen den Lohn ihrer Mühen und geleitete sie auf wunderbarem Weg. Sie wurde ihnen am Tag zum Schutz und in der Nacht zum Sternenlicht.", footnotes: ["Ex 13,21f; Ps 78,14; 105,39"] },
      { number: 18, text: "Sie führte sie durch das Rote Meer und geleitete sie durch gewaltige Wasser.", footnotes: ["Ex 14,21f; Ps 78,13; 106,9"] },
      { number: 19, text: "Ihre Feinde aber ließ sie in der Flut ertrinken und spülte sie aus der Tiefe des Abgrunds ans Land.", footnotes: ["18,5; Ps 106,11"] },
      { number: 20, text: "Darum plünderten die Gerechten die Frevler aus, sie priesen, Herr, deinen heiligen Namen und lobten einmütig deine schützende Hand.", footnotes: ["Ex 15,1-21"] },
      { number: 21, text: "Denn die Weisheit hat den Mund der Stummen geöffnet und die Zungen der Unmündigen hat sie beredt gemacht." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Sie ließ alles gelingen, was sie unter der Führung des heiligen Propheten unternahmen.", footnotes: ["Hos 12,14"] },
      { number: 2, text: "Sie zogen durch eine unbewohnte Wüste und schlugen in unwegsamen Gegenden ihre Zelte auf." },
      { number: 3, text: "Sie leisteten ihren Feinden Widerstand und wehrten ihre Gegner ab.", footnotes: ["Ex 17,8-16"] },
      { number: 4, text: "Als sie dürsteten und dich anriefen, wurde ihnen Wasser aus schroffem Fels gegeben, sodass sie ihren Durst stillen konnten aus hartem Gestein.", footnotes: ["Ex 17,1-7; Ps 78,15f; 105,41"] },
      { number: 5, text: "Denn was ihren Feinden zur Strafe wurde, das empfingen sie als Wohltat in ihrer Not.", heading: "Das Volk Gottes und seine Feinde - sieben Vergleiche: 11,5 - 19,22 | Das Wasser des Nil - das Wasser aus dem Felsen: 11,5-14", footnotes: ["Vg formuliert 5b etwas anders und fügt als V. 6 hinzu: indem es ihnen an Getränk mangelte und die Israeliten freuten sich, als sie Überfluss hatten. Die VV. 6-26 zählt dann Vg als VV. 7-27."] },
      { number: 6, text: "Der ständig fließende Strom wurde durch schmutziges Blut getrübt.", footnotes: ["Ps 78,44; 105,29"] },
      { number: 7, text: "So wurden jene für den befohlenen Kindermord gestraft. Diesen aber gabst du wider Erwarten reichlich Wasser,", footnotes: ["Ex 1,15-22"] },
      { number: 8, text: "nachdem du ihnen vorher durch ihren Durst gezeigt hattest, wie ihre Gegner von dir bestraft wurden.", footnotes: ["11,4; 16,4"] },
      { number: 9, text: "Denn als sie geprüft und, wenn auch nur milde, zurechtgewiesen wurden, da erkannten sie, wie die Frevler im Zorn gerichtet und gepeinigt worden waren.", footnotes: ["12,21f"] },
      { number: 10, text: "Sie hast du wie ein mahnender Vater auf die Probe gestellt, die Frevler aber wie ein strenger König gerichtet und verurteilt.", footnotes: ["Dtn 8,5; Spr 3,11f"] },
      { number: 11, text: "Fern von den Gerechten wurden sie ebenso geplagt wie damals, als sie ihnen noch nahe waren;" },
      { number: 12, text: "denn zweifaches Leid und Seufzen brachte ihnen die Erinnerung an das Vergangene:" },
      { number: 13, text: "Als sie nämlich hörten, dass ihre eigene Bestrafung jenen sogar zur Wohltat geworden war, da erkannten sie das Wirken des Herrn." },
      { number: 14, text: "Den sie einst ausgesetzt und weggeworfen, den sie mit Hohn abgewiesen hatten, den mussten sie am Ende von allem bestaunen, nachdem sie einen viel schlimmeren Durst gelitten hatten als die Gerechten.", footnotes: ["Ex 2,3.15; 5,23; 10,28"] },
      { number: 15, text: "Zur Strafe für ihre frevlerische Torheit, in die sie sich verirrt hatten, als sie vernunftloses Gewürm und armseliges Ungeziefer verehrten, sandtest du ihnen eine Menge vernunftloser Tiere.", heading: "Erste Einschaltung: Gottes Art zu strafen: 11,15 - 12,27", footnotes: ["12,23-27; 15,18 - 16,1", "Die Ägypter verehrten Krokodile, Schlangen, Eidechsen, Frösche, Käfer, Fliegen, überhaupt den größten Teil der in Ägypten heimischen Tierwelt."] },
      { number: 16, text: "Sie sollten erkennen: Man wird mit dem gestraft, womit man sündigt.", footnotes: ["Ri 1,7"] },
      { number: 17, text: "Für deine allmächtige Hand, die aus ungeformtem Stoff die Welt gestaltet hat, wäre es keine Schwierigkeit gewesen, eine Menge von Bären gegen sie zu senden oder grimmige Löwen", footnotes: ["Gen 1,2; 12,9", "Den Ausdruck «ungeformter Stoff» übernimmt der Verfasser aus der griechischen Philosophie, aber nicht im Sinn einer ungeschaffenen Materie, er bezieht sich vielmehr auf Gen 1,2."] },
      { number: 18, text: "oder unbekannte, neu geschaffene, wuterfüllte Tiere, die Feuer sprühenden Atem aushauchen oder zischenden Dampf ausstoßen oder schreckliche Funken aus den Augen sprühen.", footnotes: ["Ijob 41,10-14"] },
      { number: 19, text: "Nicht nur ihre verderbliche Gewalt hätte sie zermalmen, schon ihr Furcht erregender Anblick hätte sie vernichten können." },
      { number: 20, text: "Aber abgesehen davon hätten sie durch einen einzigen Hauch fallen können, verfolgt von deinem Strafgericht und fortgeweht vom Sturm deiner Macht. Du aber hast alles nach Maß, Zahl und Gewicht geordnet.", footnotes: ["Ijob 4,9; Sir 1,9"] },
      { number: 21, text: "Denn du bist immer imstande, deine große Macht zu entfalten. Wer könnte der Kraft deines Arms widerstehen?", footnotes: ["2 Chr 20,6"] },
      { number: 22, text: "Die ganze Welt ist ja vor dir wie ein Stäubchen auf der Waage, wie ein Tautropfen, der am Morgen zur Erde fällt.", footnotes: ["Jes 40,15"] },
      { number: 23, text: "Du hast mit allen Erbarmen, weil du alles vermagst, und siehst über die Sünden der Menschen hinweg, damit sie sich bekehren.", footnotes: ["12,18; Ps 145,9"] },
      { number: 24, text: "Du liebst alles, was ist, und verabscheust nichts von allem, was du gemacht hast; denn hättest du etwas gehasst, so hättest du es nicht geschaffen." },
      { number: 25, text: "Wie könnte etwas ohne deinen Willen Bestand haben, oder wie könnte etwas erhalten bleiben, das nicht von dir ins Dasein gerufen wäre?" },
      { number: 26, text: "Du schonst alles, weil es dein Eigentum ist, Herr, du Freund des Lebens.", footnotes: ["Jona 4,11"] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Denn in allem ist dein unvergänglicher Geist.", footnotes: ["1,7; Ps 104,30"] },
      { number: 2, text: "Darum bestrafst du die Sünder nur nach und nach; du mahnst sie und erinnerst sie an ihre Sünden, damit sie sich von der Schlechtigkeit abwenden und an dich glauben, Herr." },
      { number: 3, text: "Du hast auch die früheren Bewohner deines heiligen Landes gehasst," },
      { number: 4, text: "weil sie abscheuliche Verbrechen verübten, Zauberkünste und unheilige Festbräuche;", footnotes: ["(4-5) 14,23-29", "4f: Dtn 18,9f.12 warnt vor Kinderopfern, die bei den Kanaanitern bezeugt sind (vgl. die Anmerkungen zu Gen 22,1-19 und zu Lev 18,21)."] },
      { number: 5, text: "sie waren erbarmungslose Kindermörder und verzehrten beim Opfermahl Menschenfleisch und Menschenblut. Darum beschlossest du, mitten im Gelage die Teilnehmer" },
      { number: 6, text: "und deren Eltern, die mit eigener Hand hilflose Wesen töteten, durch die Hände unserer Väter zu vernichten;" },
      { number: 7, text: "denn das Land, das dir vor allen anderen teuer ist, sollte eine seiner würdige Bevölkerung von Gotteskindern erhalten.", footnotes: ["Jos 11,14.20; Dtn 11,12; Ps 47,5; Dtn 14,1"] },
      { number: 8, text: "Doch selbst mit jenen gingst du schonend um, weil sie Menschen waren; du sandtest deinem Heer Hornissen voraus, um sie nach und nach zu vernichten.", footnotes: ["11,26", "Von Hornissen sprechen G und Vg in Ex 23,28; Dtn 7,20; Jos 24,12. Heute wird das entsprechende hebräische Wort gewöhnlich mit «Entmutigung», «Furcht» oder «Panik» wiedergegeben. In Ex 23,29f und Dtn 7,22 wird die allmähliche Ausrottung der Kanaaniter als Rücksichtnahme Gottes auf Israel gedeutet; anders Ri 2,3.20-23; 3,1-4."] },
      { number: 9, text: "Obgleich du die Macht hattest, in einer Schlacht die Frevler den Gerechten in die Hand zu geben oder sie durch wilde Tiere oder ein unerbittliches Wort mit einem Schlag auszurotten,", footnotes: ["11,17f"] },
      { number: 10, text: "vollzogst du doch erst nach und nach die Strafe und ließest so Zeit für die Umkehr. Dabei wusstest du genau, dass ihr Ursprung böse und ihre Schlechtigkeit angeboren war und dass sich ihr Denken in Ewigkeit nicht ändern werde;", footnotes: ["12,2; 11,23"] },
      { number: 11, text: "sie waren schon von Anfang an ein verfluchter Stamm. Keine Furcht vor irgendjemand hat dich dazu bestimmt, sie für ihre Sünden ohne Strafe zu lassen." },
      { number: 12, text: "Denn wer darf sagen: Was hast du getan? Wer vermag sich deinem Urteilsspruch zu widersetzen? Wer könnte dich anklagen wegen des Untergangs von Völkern, die du selbst geschaffen hast? Wer wollte gegen dich auftreten als Anwalt schuldiger Menschen?", footnotes: ["Ijob 9,12; Jes 45,9; Ijob 9,19"] },
      { number: 13, text: "Denn es gibt keinen Gott außer dir, der für alles Sorge trägt; daher brauchst du nicht zu beweisen, dass du gerecht geurteilt hast.", footnotes: ["6,7; 15,1"] },
      { number: 14, text: "Kein König und kein Herrscher kann dich zur Rede stellen wegen der Menschen, die du gestraft hast." },
      { number: 15, text: "Gerecht, wie du bist, verwaltest du das All gerecht und hältst es für unvereinbar mit deiner Macht, den zu verurteilen, der keine Strafe verdient.", footnotes: ["Ps 9,9; 11,7"] },
      { number: 16, text: "Deine Stärke ist die Grundlage deiner Gerechtigkeit und deine Herrschaft über alles lässt dich gegen alles Nachsicht üben.", footnotes: ["11,24; 15,1"] },
      { number: 17, text: "Stärke beweist du, wenn man an deine unbeschränkte Macht nicht glaubt, und bei denen, die sie kennen, strafst du die trotzige Auflehnung." },
      { number: 18, text: "Weil du über Stärke verfügst, richtest du in Milde und behandelst uns mit großer Nachsicht; denn die Macht steht dir zur Verfügung, wann immer du willst.", footnotes: ["11,23"] },
      { number: 19, text: "Durch solches Handeln hast du dein Volk gelehrt, dass der Gerechte menschenfreundlich sein muss, und hast deinen Söhnen die Hoffnung geschenkt, dass du den Sündern die Umkehr gewährst.", footnotes: ["1,6; 12,2"] },
      { number: 20, text: "Du hast die Feinde deiner Kinder, auch wenn sie den Tod verdienten, sehr nachsichtig und nur nach und nach gestraft und ihnen Zeit und Möglichkeit gegeben, sich von ihrer Schlechtigkeit abzuwenden.", footnotes: ["12,10"] },
      { number: 21, text: "Aber wie viel umsichtiger noch hast du deine Söhne bestraft, deren Vätern du Gutes verheißen hast, als du mit ihnen unter Eid den Bund schlossest.", footnotes: ["12,18; 11,9f; Gen 15,18; Dtn 7,8"] },
      { number: 22, text: "Während du uns erziehst, geißelst du unsere Feinde zehntausendfach, damit wir als Richter deine Güte uns zum Vorbild nehmen und auf Erbarmen hoffen, wenn wir selber vor dem Gericht stehen." },
      { number: 23, text: "Du hast jene, die in Torheit und Unrecht dahinlebten, mit ihren eigenen Gräueln gepeinigt.", footnotes: ["11,15"] },
      { number: 24, text: "Allzu weit waren sie in die Irre gegangen, als sie die allerhässlichsten und verachtetsten Tiere für Götter hielten und wie unverständige Kinder sich täuschen ließen." },
      { number: 25, text: "Darum hast du ihnen wie unvernünftigen Kindern eine Strafe gesandt, die sie zum Gespött machte.", footnotes: ["15,14"] },
      { number: 26, text: "Wer sich aber durch eine Strafe, die ihn zum Gespött macht, nicht warnen lässt, der wird eine Strafe erleiden, die der Macht Gottes entspricht." },
      { number: 27, text: "In ihren Leiden wurden sie zornig über die Tiere, die sie für Götter hielten und mit denen sie jetzt gestraft wurden. So erfuhren sie jenen, von dem sie vorher nichts wissen wollten, und erkannten ihn als den wahren Gott; deshalb war ja auch die äußerste Strafe über sie gekommen.", footnotes: ["16,16; 18,13; Ex 5,2; 14,25"] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Töricht waren von Natur alle Menschen, denen die Gotteserkenntnis fehlte. Sie hatten die Welt in ihrer Vollkommenheit vor Augen, ohne den wahrhaft Seienden erkennen zu können. Beim Anblick der Werke erkannten sie den Meister nicht,", heading: "Zweite Einschaltung: Die Torheit des Götzendienstes: 13,1 - 15,19", footnotes: ["Ps 14,1; 92,7; Apg 14,17; 17,27; Röm 1,19f"] },
      { number: 2, text: "sondern hielten das Feuer, den Wind, die flüchtige Luft, den Kreis der Gestirne, die gewaltige Flut oder die Himmelsleuchten für weltbeherrschende Götter." },
      { number: 3, text: "Wenn sie diese, entzückt über ihre Schönheit, als Götter ansahen, dann hätten sie auch erkennen sollen, wie viel besser ihr Gebieter ist; denn der Urheber der Schönheit hat sie geschaffen.", footnotes: ["Dtn 4,19; Ijob 31,26f"] },
      { number: 4, text: "Und wenn sie über ihre Macht und ihre Kraft in Staunen gerieten, dann hätten sie auch erkennen sollen, wie viel mächtiger jener ist, der sie geschaffen hat;", footnotes: ["Ps 8,4; 104,24"] },
      { number: 5, text: "denn von der Größe und Schönheit der Geschöpfe lässt sich auf ihren Schöpfer schließen." },
      { number: 6, text: "Dennoch verdienen jene nur geringen Tadel. Vielleicht suchen sie Gott und wollen ihn finden, gehen aber dabei in die Irre." },
      { number: 7, text: "Sie verweilen bei der Erforschung seiner Werke und lassen sich durch den Augenschein täuschen; denn schön ist, was sie schauen." },
      { number: 8, text: "Doch auch sie sind unentschuldbar:" },
      { number: 9, text: "Wenn sie durch ihren Verstand schon fähig waren, die Welt zu erforschen, warum fanden sie dann nicht eher den Herrn der Welt?" },
      { number: 10, text: "Unselig aber sind jene, die auf Totes ihre Hoffnung setzen und Werke von Menschenhand als Götter bezeichnen, Gold und Silber, kunstvolle Gebilde und Tiergestalten oder einen nutzlosen Stein, ein Werk uralter Herkunft.", footnotes: ["15,5.17; Jes 44,9-20; Jer 10,3-9; Ps 135,15-18"] },
      { number: 11, text: "Da sägte ein Holzschnitzer einen geeigneten Baum ab, entrindete ihn ringsum geschickt, bearbeitete ihn sorgfältig und machte daraus ein nützliches Gerät für den täglichen Gebrauch.", footnotes: ["Jes 40,20; 44,9-20"] },
      { number: 12, text: "Die Abfälle seiner Arbeit verwendete er, um sich die Nahrung zu bereiten, und aß sich satt." },
      { number: 13, text: "Was dann noch übrig blieb und zu nichts brauchbar war, ein krummes, knotiges Stück Holz, das nahm er, schnitzte daran so eifrig und fachgemäß, wie man es tut, wenn man am Abend von der Arbeit abgespannt ist, formte es zum Bild eines Menschen" },
      { number: 14, text: "oder machte es einem armseligen Tier ähnlich, beschmierte es mit Mennig und roter Schminke, überstrich alle schadhaften Stellen," },
      { number: 15, text: "machte ihm eine würdige Wohnstatt, stellte es an der Wand auf und befestigte es mit Eisen." },
      { number: 16, text: "So sorgte er dafür, dass es nicht herunterfiel, wusste er doch, dass es sich nicht helfen kann; es ist ein Bild und braucht Hilfe." },
      { number: 17, text: "Aber wenn er um Besitz, Ehe und Kinder betet, dann schämt er sich nicht, das Leblose anzureden. Um Gesundheit ruft er das Kraftlose an," },
      { number: 18, text: "Leben begehrt er vom Toten. Hilfe erfleht er vom ganz Hilflosen und gute Reise von dem, was nicht einmal den Fuß bewegen kann.", footnotes: ["15,15"] },
      { number: 19, text: "Für seine Arbeit, für Gewinn und Erfolg seines Handwerks bittet er um Kraft von einem, dessen Hände völlig kraftlos sind." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Ein anderer, der sich zu einer Seefahrt rüstet, auf der er wilde Wogen durchqueren wird, ruft ein Holz an, das gebrechlicher ist als das Fahrzeug, das ihn trägt." },
      { number: 2, text: "Das Fahrzeug hat der Erwerbstrieb ersonnen und die Weisheit eines Künstlers hergestellt." },
      { number: 3, text: "Deine Vorsehung, Vater, steuert es; denn du hast auch im Meer einen Weg gebahnt und in den Wogen einen sicheren Pfad.", footnotes: ["Jes 43,16"] },
      { number: 4, text: "Damit zeigst du, dass du imstande bist, aus jeder Lage zu retten, so dass auch jemand, der keine Erfahrung hat, ein Schiff besteigen kann.", footnotes: ["Ps 107,28-30"] },
      { number: 5, text: "Du willst, dass die Werke deiner Weisheit nicht ungenutzt bleiben. Darum vertrauen Menschen ihr Leben sogar einem winzigen Holz an und fahren wohlbehalten auf einem Floss durch die Brandung.", footnotes: ["Gen 1,28"] },
      { number: 6, text: "So hat auch in der Urzeit beim Untergang der übermütigen Riesen die Hoffnung der Welt sich auf ein Floss geflüchtet und, durch deine Hand gesteuert, der Welt den Samen eines neuen Geschlechtes hinterlassen.", footnotes: ["Gen 6 - 8; Bar 3,26-28; Weish 10,4f; Sir 44,17f"] },
      { number: 7, text: "Denn Segen ruht auf dem Holz, durch das Gerechtigkeit geschieht." },
      { number: 8, text: "Fluch hingegen trifft das von Händen geformte Holz und seinen Bildner, ihn, weil er es bearbeitet hat, jenes, weil es Gott genannt wurde, obwohl es vergänglich ist.", footnotes: ["Dtn 27,15"] },
      { number: 9, text: "Denn Gott sind in gleicher Weise Frevler wie Frevel verhasst;", footnotes: ["(9-10) Dtn 7,25f"] },
      { number: 10, text: "mit dem Bildner wird sein Werk der Strafe verfallen." },
      { number: 11, text: "Darum kommt auch über die Götzenbilder der Völker das Gericht, weil sie in Gottes Schöpfung zum Gräuel geworden sind, zum Anstoß für die Seelen der Menschen und zur Schlinge für die Füße der Toren.", footnotes: ["Jes 2,18f; Jer 10,15; Weish 14,14; Ps 106,36"] },
      { number: 12, text: "Mit dem Gedanken an Götzenbilder beginnt der Abfall und ihre Erfindung führt zur Sittenverderbnis.", footnotes: ["15,5; Num 25,1f", "12-21: Die Götzenverehrung ist unter dem Druck von Unglück (V. 15) und Herrscherstolz (VV. 16-20) entstanden. Für beides gab es auch in Ägypten Beispiele."] },
      { number: 13, text: "Weder waren sie von Anfang an da, noch werden sie ewig bleiben." },
      { number: 14, text: "Durch die eitle Ruhmsucht der Menschen sind sie in die Welt gekommen; darum ist ihnen auch ein jähes Ende zugedacht.", footnotes: ["14,10f"] },
      { number: 15, text: "Bedrückt durch allzu frühe Trauer ließ ein Vater von seinem Kind, das gar schnell hinweggerafft wurde, ein Bildnis machen; so ehrte er einen toten Menschen als Gott und führte bei seinen Leuten geheime Kulte und festliche Bräuche ein.", footnotes: ["12,4"] },
      { number: 16, text: "Im Lauf der Zeit verfestigte sich die frevelhafte Sitte und wurde schließlich als Gesetz befolgt;" },
      { number: 17, text: "die Standbilder erhielten auf Anordnung der Herrscher göttliche Verehrung. Konnten die Menschen einen König nicht unmittelbar ehren, weil er weit weg wohnte, dann vergegenwärtigten sie den Fernen; sie machten von dem verehrten König ein Bildnis, das allen sichtbar war, um dem Abwesenden, als ob er gegenwärtig wäre, mit Eifer zu huldigen." },
      { number: 18, text: "Der Ehrgeiz des Künstlers führte dazu, dass auch jene, die den König gar nicht kannten, ihm göttliche Verehrung erwiesen." },
      { number: 19, text: "Wohl um dem Herrscher zu gefallen, bot er seine ganze Kunst auf, um ihn schöner darzustellen, als er war." },
      { number: 20, text: "Von der Anmut des Bildes hingerissen, betete die Menge den, der noch kurz zuvor nur als Mensch geehrt wurde, jetzt wie einen Gott an." },
      { number: 21, text: "Der Welt ist dies zum Verhängnis geworden: Die Menschen haben, unter dem Druck von Unglück oder Herrschermacht, Stein und Holz den Namen beigelegt, der mit niemand geteilt werden kann.", footnotes: ["Jes 42,8"] },
      { number: 22, text: "Als ob es nicht genug wäre, in der Erkenntnis Gottes zu irren, nennen sie in dem heftigen Zwiespalt, den die Unwissenheit in ihr Leben bringt, so große Übel auch noch Frieden.", footnotes: ["Röm 1,24-32"] },
      { number: 23, text: "Bei kindermörderischen Festbräuchen, heimlichen Kulten oder wilden Gelagen mit fremdartigen Sitten", footnotes: ["12,4f"] },
      { number: 24, text: "halten sie weder Leben noch Ehe rein, sondern einer tötet heimtückisch den andern oder beleidigt ihn durch Ehebruch.", footnotes: ["Lev 18,24"] },
      { number: 25, text: "Alles ist ein wirres Gemisch von Blut und Mord, Diebstahl und Betrug, Verdorbenheit, Untreue, Aufruhr und Meineid;" },
      { number: 26, text: "es herrscht Umkehrung der Werte, undankbare Vergesslichkeit, Befleckung der Seelen, widernatürliche Unzucht, Zerrüttung der Ehen, Ehebruch und Zügellosigkeit." },
      { number: 27, text: "Die Verehrung der namenlosen Götzenbilder ist aller Übel Anfang, Ursache und Höhepunkt.", footnotes: ["14,12"] },
      { number: 28, text: "Sie rasen im Freudentaumel, weissagen Lügen, leben in Ungerechtigkeit oder schwören leichthin einen Meineid." },
      { number: 29, text: "Im Vertrauen auf leblose Götzen fürchten sie nicht, dass ihre Meineide ihnen schaden könnten." },
      { number: 30, text: "Jedoch für beides wird sie die gerechte Strafe treffen: dass sie sich von Gott eine verkehrte Vorstellung machten, indem sie Götzenbilder verehrten, und dass sie unter Missachtung der Heiligkeit des Eides hinterlistig und ungerecht schworen." },
      { number: 31, text: "Es ist nie die Macht derer, bei denen sie schworen, sondern immer die den Sündern gebührende Strafe, die die Vergehen der Frevler verfolgt." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Du aber, unser Gott, bist gütig, wahrhaftig und langmütig; voll Erbarmen durchwaltest du das All.", footnotes: ["8,1; Ex 34,6; Ps 86,5.15"] },
      { number: 2, text: "Auch wenn wir sündigen, gehören wir dir, da wir deine Stärke kennen; doch wir wollen nicht sündigen, da wir wissen, dass wir dein Eigentum sind.", footnotes: ["12,16-18"] },
      { number: 3, text: "Denn es ist vollendete Gerechtigkeit, dich zu verstehen; und deine Stärke zu kennen ist die Wurzel der Unsterblichkeit.", footnotes: ["Hos 6,6; 1 Joh 2,3-6"] },
      { number: 4, text: "Die arglistige Erfindung der Menschen hat uns nicht verführt, die unfruchtbare Arbeit der Maler, eine mit bunten Farben besudelte Gestalt.", footnotes: ["13,14; 14,20"] },
      { number: 5, text: "Ihr Anblick erregt die Sehnsucht der Toren und weckt in ihnen das Verlangen nach der leblosen Gestalt eines toten Bildes." },
      { number: 6, text: "Liebhaber des Bösen und solcher Hoffnungen würdig sind alle, die es anfertigen, die nach ihm verlangen und die es anbeten.", footnotes: ["14,8-10"] },
      { number: 7, text: "Der Töpfer knetet mühsam den weichen Ton, um daraus Gefäße zu unserem Gebrauch zu formen. Aus dem gleichen Lehm bildet er solche, die sauberen Zwecken dienen, und solche für das Gegenteil, alle in gleicher Weise; über den Gebrauch eines jeden entscheidet der Töpfer.", footnotes: ["Jer 18,3f; Röm 9,21"] },
      { number: 8, text: "Aus dem gleichen Lehm formt er in verkehrter Mühe auch einen nichtigen Gott, er, der vor kurzem aus Erde entstand und bald dorthin zurückkehrt, woher er genommen ist, wenn seine Seele, das ihm anvertraute Darlehen, zurückgefordert wird.", footnotes: ["Gen 2,7; 3,19; Koh 12,7; Lk 12,20"] },
      { number: 9, text: "Doch es kümmert ihn nicht, dass er dahinschwinden wird und nur ein kurzes Leben hat. Er wetteifert mit Goldschmieden und Silbergießern, er ahmt Kupferschmiede nach und sieht seinen Ruhm darin, Trugbilder zu formen." },
      { number: 10, text: "Asche ist sein Herz, noch weniger wert als Erdenstaub seine Hoffnung, und sein Leben ist wertloser als Lehm.", footnotes: ["3,18; 5,14; 14,29; 2 Makk 7,14"] },
      { number: 11, text: "Seinen eigenen Bildner hat er nämlich nicht erkannt, den, der ihm eine wirkende Seele eingehaucht und Lebensatem eingeblasen hat." },
      { number: 12, text: "Nein, er hält unser Leben für ein Kinderspiel, das Dasein für einen einträglichen Jahrmarkt; er sagt, man müsse aus allem, auch aus Schlechtem, Gewinn ziehen." },
      { number: 13, text: "Denn er weiß besser als alle, dass er sündigt, wenn er aus dem gleichen Erdenstoff nicht nur zerbrechliche Gefäße, sondern auch Götzenbilder fertigt.", footnotes: ["15,7f"] },
      { number: 14, text: "Ganz unverständig aber und ärmer als eines Kindes Seele waren die Feinde, die dein Volk knechteten.", footnotes: ["12,24", "14-19: Der Weise redet noch einmal vom Götzendienst der Ägypter, um dann in 16,1 den in 11,15 begonnenen Vergleich zu Ende zu führen."] },
      { number: 15, text: "Sie hielten alle Götzen der Völker für Götter, Götter, die weder ihre Augen gebrauchen können, um zu sehen, noch ihre Nase, um die Luft zu atmen, noch ihre Ohren, um zu hören, noch die Finger ihrer Hände, um zu tasten, und deren Füße nicht gehen können.", footnotes: ["Ps 115,4-7"] },
      { number: 16, text: "Ein Mensch hat sie gemacht, einer, dem der Geist nur geliehen ist, hat sie gebildet; kein Mensch hat die Kraft, einen Gott zu bilden, der auch nur ihm selber ähnlich wäre." },
      { number: 17, text: "Als Sterblicher schafft er mit frevelhaften Händen nur Totes. Er ist besser als seine angebeteten Gebilde; denn er bekam einmal Leben, diese aber nie.", footnotes: ["13,10"] },
      { number: 18, text: "Sie verehren sogar die widerlichsten Tiere, die dümmsten im Vergleich mit den anderen,", footnotes: ["(18-19) 11,15; 12,24"] },
      { number: 19, text: "solche, die nicht einmal schön sind, sodass man an ihnen Gefallen finden könnte, soweit das beim Anblick von Tieren möglich ist, die zudem Gottes Lob und seinen Segen verloren haben.", footnotes: ["Gen 1,21f.25; 3,14"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Darum wurden sie mit Recht durch ähnliche Tiere gezüchtigt und durch eine Menge von Ungeziefer gequält.", heading: "Frösche - Wachteln: 16,1-4", footnotes: ["11,15; 12,23-27; Ex 7,26 - 8,11; Ps 78,45; 105,30"] },
      { number: 2, text: "Während sie auf solche Weise gezüchtigt wurden, hast du deinem Volk eine Wohltat erwiesen und mit den Wachteln seinem heftigen Verlangen eine fremdartige Nahrung gegeben.", footnotes: ["Ex 16,13; Ps 78,26f; 105,40"] },
      { number: 3, text: "Während jenen in ihrem Hunger die Esslust verging wegen der Hässlichkeit der gegen sie gesandten Tiere, bekamen diese nach nur kurzer Entbehrung sogar eine fremdartige Speise." },
      { number: 4, text: "Über jene Unterdrücker sollte unabwendbarer Hunger kommen; diese aber sollten nur kurz spüren, wie ihre Feinde gequält wurden.", footnotes: ["11,8f"] },
      { number: 5, text: "Auch damals, als die schreckliche Wut wilder Tiere über sie hereinbrach und sie durch die Bisse tückischer Schlangen umkamen, dauerte dein Zorn nicht bis ans Ende.", heading: "Heuschrecken und Stechfliegen - giftige Schlangen: 16,5 - 14", footnotes: ["Num 21,5-9; Weish 18,20"] },
      { number: 6, text: "Zur Warnung wurden sie nur kurz in Schrecken versetzt und bekamen ein Rettungszeichen, damit sie sich an die Vorschrift deines Gesetzes erinnerten.", footnotes: ["Joh 3,14f"] },
      { number: 7, text: "Wer sich dorthin wandte, wurde nicht durch das gerettet, was er anschaute, sondern durch dich, den Retter aller.", footnotes: ["Jes 45,21"] },
      { number: 8, text: "Dadurch hast du unsere Feinde überzeugt, dass du es bist, der aus allem Übel erlöst." },
      { number: 9, text: "Denn sie wurden durch die Bisse der Heuschrecken und der Stechfliegen getötet, ohne dass es ein Heilmittel für sie gab; sie verdienten es ja, durch solches Ungeziefer gezüchtigt zu werden.", footnotes: ["Ex 8,12-28; 10,1-20; Ps 78,45f; 105,34f", "In Ex 10,17 wird die Heuschreckenplage als Tod bezeichnet, da sie eine todbringende Hungersnot zur Folge hat. Der Verfasser deutet und steigert diese Aussage, indem er die Bisse der Heuschrecken selbst als todbringend bezeichnet. Im gleichen Sinn deutet er die Stechmückenplage."] },
      { number: 10, text: "Deine Söhne aber wurden nicht einmal durch die Zähne Gift spritzender Schlangen überwältigt; denn dein Erbarmen kam ihnen zu Hilfe und heilte sie." },
      { number: 11, text: "Sie wurden gebissen, aber schnell wieder gerettet, damit sie sich an deine Worte erinnerten; denn sie sollten nicht in tiefes Vergessen versinken, sondern sich ungehindert deiner Wohltaten erfreuen." },
      { number: 12, text: "Weder Kraut noch Wundpflaster machte sie gesund, sondern dein Wort, Herr, das alles heilt.", footnotes: ["Ps 107,20"] },
      { number: 13, text: "Du hast Gewalt über Leben und Tod; du führst zu den Toren der Unterwelt hinab und wieder herauf.", footnotes: ["1 Sam 2,6"] },
      { number: 14, text: "Ein Mensch kann zwar in seiner Schlechtigkeit töten; doch den entschwundenen Geist holt er nicht zurück und die weggenommene Seele kann er nicht befreien.", footnotes: ["Ps 49,8f; Mt 16,26"] },
      { number: 15, text: "Unmöglich ist es, deiner Hand zu entfliehen.", heading: "Hagel - Manna: 16,15-29", footnotes: ["Tob 13,2"] },
      { number: 16, text: "Denn die Frevler, die behaupten, dich nicht zu kennen, wurden durch die Kraft deines Armes gezüchtigt: Ungewöhnliche Regengüsse, Hagelschauer und schreckliche Wolkenbrüche peitschten auf sie nieder und Feuer verzehrte sie.", footnotes: ["Ex 9,13-35"] },
      { number: 17, text: "Das Seltsamste war, dass das Wasser, das sonst alles löscht, die Kraft des Feuers noch verstärkte; denn die Natur kämpft für die Gerechten.", footnotes: ["16,22; Ex 9,24"] },
      { number: 18, text: "Das eine Mal wurde die Flamme gezähmt, damit sie nicht die Tiere verzehrte, die gegen die Ruchlosen gesandt waren; diese sollten sehen und erkennen, dass sie von Gottes Strafe verfolgt wurden.", footnotes: ["12,27", "Vielleicht ist das Feuer gemeint, das die Ägypter anzündeten, um die Heuschreckenschwärme zu vernichten, wie es noch heute geschieht."] },
      { number: 19, text: "Das andere Mal brannte die Flamme mit ungewöhnlicher Kraft mitten im Wasser, um die Erzeugnisse des schuldbeladenen Landes zu vernichten.", footnotes: ["16,17.22"] },
      { number: 20, text: "Dein Volk dagegen nährtest du mit der Speise der Engel und unermüdlich gabst du ihm fertiges Brot vom Himmel. Deine Gabe gewährte jeden Genuss und entsprach jedem Geschmack;", footnotes: ["Ex 16; Num 11,6-9; Ps 78,23-25; 105,40"] },
      { number: 21, text: "sie offenbarte deine zarte Liebe zu deinen Kindern. Sie erfüllte das Verlangen eines jeden, der sie genoss, und verwandelte sich in alles, was einer wollte." },
      { number: 22, text: "Schnee und Eis hielten dem Feuer stand und schmolzen nicht. Deine Kinder sollten erkennen, dass nur die Früchte der Feinde vom Feuer vernichtet wurden, das im Hagel brannte und in den Regengüssen blitzte,", footnotes: ["16,19", "G vergleicht in Num 11,7 das Manna mit Eis (H mit Bdelliumharz, in Ex 16,14 mit Reif)."] },
      { number: 23, text: "und dass es umgekehrt sogar seine eigene Kraft vergaß, damit die Gerechten Nahrung hätten.", footnotes: ["19,21"] },
      { number: 24, text: "Denn die Schöpfung, die dir, ihrem Schöpfer, dient, steigert ihre Kräfte, um die Schuldigen zu bestrafen, und hält sie zurück, um denen Gutes zu tun, die auf dich vertrauen.", footnotes: ["19,18"] },
      { number: 25, text: "Darum diente sie auch damals deinem Geschenk, das alle ernährte, und verwandelte sich in alles, was die Bittenden wünschten." },
      { number: 26, text: "Deine geliebten Söhne, Herr, sollten daraus lernen: Nicht die verschiedenartigen Früchte ernähren den Menschen, sondern dein Wort erhält alle, die dir vertrauen.", footnotes: ["Dtn 8,3; Mt 4,4; Lk 4,4"] },
      { number: 27, text: "Denn dasselbe, das vom Feuer nicht vernichtet wurde, schmolz sogleich, wenn es ein flüchtiger Sonnenstrahl erwärmte.", footnotes: ["Ex 16,21; Num 11,8"] },
      { number: 28, text: "So sollte man erkennen, dass man, um dir zu danken, der Sonne zuvorkommen und sich noch vor dem Aufgang des Lichtes an dich wenden muss.", footnotes: ["Ps 57,9; 92,3"] },
      { number: 29, text: "Denn die Hoffnung des Undankbaren schmilzt wie winterlicher Reif und verrinnt wie unnützes Wasser.", footnotes: ["15,10"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Groß und nicht zu ergründen sind deine Entscheide; darum verfiel in Irrtum, wer sich nicht belehren ließ.", heading: "Finsternis - Feuersäule: 17,1 - 18,4", footnotes: ["Röm 11,33; Weish 5,6", "1-20: Malt die Qualen des bösen Gewissens aus."] },
      { number: 2, text: "Denn die Frevler meinten, das heilige Volk knechten zu können; und jetzt lagen sie da, Gefangene der Finsternis, Gefesselte einer langen Nacht, eingeschlossen in den Häusern, von der ewigen Vorsehung verbannt.", footnotes: ["Ex 1,11-14; 10,21-23; Ps 105,28"] },
      { number: 3, text: "Sie glaubten, mit ihren geheimen Sünden unter der dunklen Decke der Vergessenheit verborgen zu sein; da packte sie furchtbares Entsetzen. Sie wurden durch Trugbilder aufgeschreckt und auseinander gejagt.", footnotes: ["Ps 64,6f; Weish 18,17"] },
      { number: 4, text: "Auch der geheimste Winkel, in den sie sich flüchteten, konnte sie nicht vor Furcht bewahren; Schrecken erregendes Getöse umbrauste sie, und düstere Gespenster mit finsteren Mienen tauchten auf." },
      { number: 5, text: "Keine Kraft irgendeines Feuers war stark genug, Licht zu bringen; nicht einmal der strahlende Glanz der Gestirne vermochte es, diese entsetzliche Nacht zu erhellen." },
      { number: 6, text: "Nur einen schaurigen Feuerherd, der sich von selbst entzündet hatte, sahen sie aufglühen; verschwand die Erscheinung, so hielten sie, außer sich vor Entsetzen, die Dinge, die sie sahen, für noch schlimmer." },
      { number: 7, text: "Da versagten die Gaukeleien der Zauberkunst und die Probe auf das prahlerische Wissen fiel schmählich aus.", footnotes: ["18,13; Ex 8,14f"] },
      { number: 8, text: "Jene, die immer versprachen, Furcht und Verwirrung von der kranken Seele zu bannen, krankten nun selber an einer lächerlichen Angst." },
      { number: 9, text: "Auch wenn nichts Schreckliches sie ängstigte, wurden sie durch raschelndes Getier und zischelnde Schlangen aufgescheucht und vergingen vor Furcht. Nicht einmal in die Luft wollten sie blicken, der man doch nirgends entfliehen kann.", footnotes: ["Ps 53,6; Spr 28,1"] },
      { number: 10, text: "Denn die Schlechtigkeit bezeugt selbst ihr feiges Wesen, wenn sie gestraft wird. Unter dem Druck des Gewissens befürchtet sie immer das Schlimmste.", footnotes: ["17,6; Gen 4,14"] },
      { number: 11, text: "Furcht ist ja nichts anderes als der Verzicht auf die von der Vernunft angebotene Hilfe." },
      { number: 12, text: "Je weniger man solche Hilfe erwartet, umso schlimmer erscheint es, die Ursache der Qual nicht zu kennen." },
      { number: 13, text: "In Wahrheit hatte jene Nacht keine Gewalt; aus den Tiefen der machtlosen Totenwelt war sie heraufgestiegen. Sie aber, die wie sonst schlafen wollten," },
      { number: 14, text: "wurden bald durch Schreckgespenster aufgescheucht, bald durch Mutlosigkeit gelähmt; denn plötzliche und unerwartete Furcht hatte sie befallen." },
      { number: 15, text: "So wurde jeder dort, wo er zu Boden sank, ein Gefangener, der in einen Kerker ohne Eisenfesseln eingeschlossen war.", footnotes: ["17,2; 18,4"] },
      { number: 16, text: "Ob Bauer oder Hirt oder ein Taglöhner, der einsam arbeitete, alle wurden überrascht und mussten sich dem unentrinnbaren Zwang fügen, alle wurden durch die gleiche Kette der Finsternis gefesselt." },
      { number: 17, text: "Das Pfeifen des Windes, der wohlklingende Gesang der Vögel auf den Zweigen der Bäume, das Rauschen ungestüm strömender Wasser, das wilde Donnern stürzender Felsen," },
      { number: 18, text: "das Laufen hüpfender Tiere, die man nicht sehen konnte, das laute Gebrüll wilder Raubtiere, das aus den Schluchten der Berge zurückgeworfene Echo: alles und jedes jagte ihnen lähmende Furcht ein.", footnotes: ["17,9"] },
      { number: 19, text: "Die ganze Welt stand in strahlendem Licht und alle gingen ungehindert ihrer Arbeit nach." },
      { number: 20, text: "Nur über jene breitete sich drückende Nacht aus, Bild der Finsternis, die sie dereinst aufnehmen sollte. Doch mehr als unter der Finsternis litten sie unter ihrer eigenen Angst.", footnotes: ["Ijob 10,21f; Mt 22,13; Jud 13"] },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Deinen Heiligen dagegen strahlte hellstes Licht. Die anderen hörten ihre Stimme, ohne sie selbst zu sehen, und priesen sie glücklich, mochten diese vorher noch so viel erduldet haben.", footnotes: ["Ex 10,23"] },
      { number: 2, text: "Sie dankten ihnen, dass sie für das früher erlittene Unrecht keine Rache nahmen, und baten um Verzeihung für ihr feindliches Verhalten." },
      { number: 3, text: "Statt jener Finsternis gabst du den Deinen eine flammende Feuersäule als Führerin auf unbekanntem Weg, als freundliche Sonne auf ihrer ruhmvollen Wanderung.", footnotes: ["10,17; Ex 13,21f; 40,36-38; Ps 78,14; 105,39"] },
      { number: 4, text: "Jene hingegen hatten es verdient, des Lichtes beraubt und in Finsternis gefangen zu sein, weil sie einst deine Söhne eingeschlossen und gefangen hielten, durch die das unvergängliche Licht des Gesetzes der Welt gegeben werden sollte.", footnotes: ["17,2; Ps 119,105; Spr 6,23"] },
      { number: 5, text: "Sie hatten beschlossen, die Kinder der Heiligen zu töten, und nur ein einziges Kind wurde ausgesetzt und gerettet. Zur Strafe hast du ihnen viele ihrer eigenen Kinder weggenommen und sie alle auf einmal in gewaltiger Wasserflut vernichtet.", heading: "Tod der Erstgeborenen - Tod in der Wüste: 18,5-25", footnotes: ["11,7; Ex 1,15-17; 12,29f; Ps 78,51; 105,36; Weish 10,19; Ex 14,27f", "Das «Kind» ist Mose (vgl. 11,14; Ex 2,1-10)."] },
      { number: 6, text: "Jene Nacht wurde unseren Vätern vorher angekündigt; denn sie sollten zuversichtlich sein und sicher wissen, welchen eidlichen Zusagen sie vertrauen konnten." },
      { number: 7, text: "So erwartete dein Volk die Rettung der Gerechten und den Untergang der Feinde." },
      { number: 8, text: "Während du die Gegner straftest, hast du uns zu dir gerufen und verherrlicht." },
      { number: 9, text: "Denn im Verborgenen feierten die frommen Söhne der Guten ihr Opferfest; sie verpflichteten sich einmütig auf das göttliche Gesetz, dass die Heiligen in gleicher Weise Güter wie Gefahren teilen sollten, und sangen schon im Voraus die Loblieder der Väter.", footnotes: ["2 Chr 30,21f", "Später wurden beim Paschamahl die Psalmen 113 bis 118 gesungen (vgl. Mt 26,30)."] },
      { number: 10, text: "Da hallte ihnen das wirre Geschrei der Feinde entgegen und sie hörten die laute Klage über die toten Kinder.", footnotes: ["Ex 12,29f"] },
      { number: 11, text: "Das gleiche Urteil traf Herrn und Knecht; der Mann aus dem Volk und der König hatten das gleiche Leid zu tragen." },
      { number: 12, text: "Durch die gleiche Todesart hatten alle zusammen unzählige Tote. Es waren nicht genügend Lebende da, um sie zu begraben; denn mit einem Schlag waren die besten Nachkommen vernichtet worden.", footnotes: ["Num 33,4"] },
      { number: 13, text: "Bisher waren sie durch die Künste ihrer Zauberer ungläubig geblieben; jetzt aber mussten sie beim Untergang der Erstgeborenen bekennen: Dieses Volk ist Gottes Sohn.", footnotes: ["17,7; Ex 7,11-13; 8,3; 12,31f; Weish 5,5; Ex 4,22f"] },
      { number: 14, text: "Als tiefes Schweigen das All umfing und die Nacht bis zur Mitte gelangt war,", footnotes: ["Ex 11,4; 12,23.29"] },
      { number: 15, text: "da sprang dein allmächtiges Wort vom Himmel, vom königlichen Thron herab als harter Krieger mitten in das dem Verderben geweihte Land.", footnotes: ["Ps 107,20; 147,15.18", "Das göttliche Wort, nämlich der Vernichtungsbeschluss Gottes, wird hier personifiziert; in Offb 19,11-21 zieht Christus, der «das Wort Gottes» heißt, vom Himmel zum Vernichtungskampf gegen die feindlichen Könige aus."] },
      { number: 16, text: "Es trug das scharfe Schwert deines unerbittlichen Befehls, trat hin und erfüllte alles mit Tod; es berührte den Himmel und stand auf der Erde.", footnotes: ["Jes 55,11; Weish 5,20; Hebr 4,12"] },
      { number: 17, text: "Plötzlich schreckten sie furchtbare Traumgesichte auf und ungeahnte Ängste überfielen sie.", footnotes: ["17,3; Ijob 4,13f"] },
      { number: 18, text: "Einer stürzte hier, ein anderer dort halb tot zu Boden und bekannte, aus welchem Grund er sterben musste." },
      { number: 19, text: "Denn die erschreckenden Träume hatten es ihnen vorausgesagt; sie sollten nicht umkommen, ohne zu wissen, warum sie so Schlimmes erlitten." },
      { number: 20, text: "Auch die Gerechten lernten eine Probe des Todes kennen: Eine große Anzahl wurde in der Wüste dahingerafft; doch der Zorn hielt nicht lange an.", footnotes: ["Num 17,6-14"] },
      { number: 21, text: "Ein Mann ohne Tadel sprang als Vorkämpfer ein mit der Waffe seines heiligen Dienstes, mit Gebet und sühnendem Räucherwerk. Er trat dem Zorn entgegen, machte dem Unheil ein Ende und zeigte so, dass er dein Diener war.", footnotes: ["Ex 28,1; Num 17,16-26", "21-24: Gemeint ist der Hohepriester Aaron. Zu seinem Gewand vgl. Ex 28,5."] },
      { number: 22, text: "Er überwand die Not nicht durch Körperkraft und nicht durch Waffengewalt, sondern durch das Wort bezwang er den Strafenden, indem er ihn an die eidlich bekräftigten Bündnisse mit den Vätern erinnerte." },
      { number: 23, text: "Denn als die Toten sich schon häuften, trat er dazwischen, hielt seinen Ansturm auf und schnitt ihm den Weg zu den Lebenden ab.", footnotes: ["Num 17,11-13", "Ansturm: so nach Vg; G: Zorn (vgl. V. 20)."] },
      { number: 24, text: "Auf seinem langen Gewand war die ganze Welt dargestellt, auf den vier Reihen der Edelsteine waren die ruhmreichen Namen der Väter eingeschnitten und auf seinem Stirnband dein hoheitsvoller Name.", footnotes: ["Ex 28,17-21; Sir 45,11; Ex 28,36-38; Sir 45,12"] },
      { number: 25, text: "Davor wich der Verderber voll Furcht zurück; denn es genügte schon diese Probe des Zornes.", footnotes: ["Ex 12,23"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Über die Frevler kam erbarmungsloser Zorn, bis sie vernichtet waren; denn Gott wusste im Voraus, wie sie sich verhalten würden:", heading: "Untergang im Meer - Rettung durch das Meer: 19,1-17", footnotes: ["Ex 14,13f"] },
      { number: 2, text: "Sie selbst hatten den Abzug der Gerechten gestattet und sie sogar dazu gedrängt; dann aber änderten sie ihren Sinn und verfolgten sie.", footnotes: ["Ex 12,31-33; Ex 14,3-31"] },
      { number: 3, text: "Sie waren noch mit der Bestattung der Toten beschäftigt und klagten an ihren Gräbern, als sie in ihrer Torheit einen anderen Entschluss fassten und denen wie Entlaufenen nachsetzten, denen sie flehentlich zugeredet hatten wegzugehen.", footnotes: ["18,12"] },
      { number: 4, text: "Das selbst verschuldete Verhängnis trieb sie in diesen Untergang und ließ sie alles vergessen, was geschehen war; denn sie sollten über die bisherigen Plagen hinaus die äußerste Strafe erleiden.", footnotes: ["12,27"] },
      { number: 5, text: "Deinem Volk aber sollte sich ein unerwarteter Weg eröffnen, während jene einen ungewöhnlichen Tod fanden.", footnotes: ["10,18f"] },
      { number: 6, text: "Das Wesen der ganzen Schöpfung wurde neu gestaltet; sie gehorchte deinen Befehlen, damit deine Kinder unversehrt bewahrt blieben.", footnotes: ["5,20; 16,24; 19,10f.18"] },
      { number: 7, text: "Man sah die Wolke, die das Lager überschattete, trockenes Land tauchte auf, wo zuvor Wasser war; es zeigte sich ein Weg ohne Hindernisse durch das Rote Meer, eine grüne Ebene stieg aus der gewaltigen Flut.", footnotes: ["Ex 14,19-22"] },
      { number: 8, text: "Von deiner Hand behütet, zogen sie vollzählig hindurch und sahen staunenswerte Wunder." },
      { number: 9, text: "Sie weideten wie Rosse, hüpften wie Lämmer und lobten dich, Herr, ihren Retter.", footnotes: ["Jes 63,13f; Ex 15"] },
      { number: 10, text: "Denn sie dachten zudem auch an das, was im fremden Land geschehen war: wie Mücken nicht von Tieren, sondern von der Erde hervorgebracht wurden und wie der Fluss nicht Wassertiere, sondern eine Menge Frösche auswarf.", footnotes: ["16,9; Ex 8,12f; Weish 16,1; Ex 7,28 - 8,2"] },
      { number: 11, text: "Schließlich sahen sie auch Vögel auf eine neue Weise entstehen, als sie, um ihre Gier zu befriedigen, nach üppigen Speisen verlangten.", footnotes: ["16,2; 19,19", "11f: Der Verfasser deutet «vom Meer her» (Num 11,31) so, wie wenn die Wachteln aus dem Meer aufgetaucht wären."] },
      { number: 12, text: "Zu ihrem Trost entstiegen nämlich Wachteln dem Meer.", footnotes: ["Num 11,31f"] },
      { number: 13, text: "Die Strafen kamen über die Sünder nicht ohne Warnung durch wuchtige Blitze. Mit Recht mussten sie für ihre bösen Taten leiden, weil sie einen so schlimmen Fremdenhass gezeigt hatten.", footnotes: ["Ex 9,18; Weish 12,26"] },
      { number: 14, text: "Während andere die Unbekannten, die zu ihnen kamen, nicht aufnahmen, machten diese sogar Gäste, die ihre Wohltäter waren, zu Sklaven.", footnotes: ["Ri 19,15-30; Gen 47,25", "14-17: Noch schlimmer als die Bewohner von Sodom (vgl. Gen 19,4-11), weil sie die Nachkommen Jakobs, der mit seinen Söhnen vom Pharao nach Ägypten eingeladen worden war (Gen 45,17f), nicht nach den Regeln des Gastrechts behandelten, sondern zu Fronsklaven machten (Ex 1,11-14)."] },
      { number: 15, text: "Noch mehr: Gewiss wird auch jene eine Strafe treffen, weil sie Fremde feindselig empfangen hatten;" },
      { number: 16, text: "diese aber haben Gäste, die sie festlich aufgenommen hatten und die schon die gleichen Bürgerrechte genossen, mit schwerem Frondienst geplagt." },
      { number: 17, text: "Wie jene an der Türe des Gerechten mit Blindheit geschlagen wurden, so auch diese, als sie von dichter Finsternis umgeben waren und jeder versuchte, seine Türe zu finden.", footnotes: ["Gen 19,10f; Weish 17,2; Ex 10,21-23", "Der «Gerechte» ist Lot."] },
      { number: 18, text: "Die Elemente verändern sich untereinander, wie auf einer Harfe die Töne den Rhythmus ändern und doch den gleichen Klang behalten. Dies lässt sich aus der Betrachtung der Geschehnisse deutlich erkennen.", heading: "Schluss: 19,18-22", footnotes: ["16,25"] },
      { number: 19, text: "Landtiere verwandelten sich in Wassertiere und schwimmende Tiere stiegen ans Land." },
      { number: 20, text: "Das Feuer steigerte im Wasser die ihm eigene Kraft und das Wasser vergaß seine löschende Wirkung.", footnotes: ["16,17f"] },
      { number: 21, text: "Flammen verzehrten nicht das Fleisch der hinfälligen Tiere, die hineingerieten, noch schmolz im Feuer die eisartige, leicht schmelzende himmlische Speise.", footnotes: ["16,22f"] },
      { number: 22, text: "In allem hast du, Herr, dein Volk groß gemacht und verherrlicht; du hast es nicht im Stich gelassen, sondern bist ihm immer und überall beigestanden.", footnotes: ["Jes 41,10"] },
    ] },
  ],
};
