import { Book } from "@/lib/types";

export const _1corinthians: Book = {
  id: "1corinthians", name: "1. Korinther", shortName: "1.Kor", testament: "new",
  introduction: `Nach Ausweis der Apostelgeschichte (18,1-18) kam Paulus auf der zweiten Missionsreise im Jahr 50 oder 51 n. Chr. nach Korinth. Die dortige Christengemeinde ist von ihm gegründet. Nach seinem Wegzug blieb er mit der Gemeinde in persönlicher Verbindung. In den beiden erhaltenen Korintherbriefen werden noch andere Schreiben an die Gemeinde erwähnt (vgl. 1 Kor 5,9; 2 Kor 2,4f). Paulus hat den 1. Korintherbrief zwischen 53 und 55 n. Chr. in Ephesus geschrieben. Den Anlass bildeten Fragen, die in der jungen Christengemeinde aufgetreten waren. Der Apostel erhielt davon Kenntnis durch »die Leute der Chloe« (1,11) und durch eine schriftliche Anfrage, die ihm wahrscheinlich von einer Abordnung der Gemeinde überbracht wurde (7,1; 16,17). Die »Leute der Chloe« berichteten von Spaltungen, die das Weiterbestehen der Gemeinde gefährdeten (1,12-17). Sie erzählten von einem schweren Fall von Blutschande (5,1-13), von Streitigkeiten der Christen vor heidnischen Richtern (6,1-11) und von sexuellen Verfehlungen (6,12-20). Ausführlich erörtert werden Fragen über Ehe und Ehelosigkeit (7,1 40), den Genuss von Götzenopferfleisch (8,1 - 11,1) und die Ordnung in der gottesdienstlichen Versammlung (11,2 - 14,40); nachdrücklich wendet sich Paulus gegen Missstände beim Herrenmahl (11,17-34). Das 15. Kapitel hat die Auferweckung Christi und der Christen zum Thema. Das abschließende Kapitel 16 enthält persönliche Mitteilungen und eine ausführliche Grußliste. Der 1. Korintherbrief gibt Einblick in das Leben und in die Probleme einer jungen heidenchristlichen Gemeinde. Aus der Stellungnahme des Paulus wird vor allem sichtbar, was für Glauben und Leben der Christen maßgebend ist: das Kreuz (1,18 - 2,5) und die Auferweckung Christi (15,1-28), das Wirken des Geistes Gottes (2,10-16; 12,1-31), die Ordnung der Gemeinde (3,5 - 4,21; 12,27-30; 16,15-18) und der rechte Gebrauch der Geistesgaben (12,1-11.27-31; 14,1-40). Wie kein anderer Brief zeigt dieses Schreiben, in welcher Weise Paulus sich als Gemeindegründer den Fragen einer jungen Gemeinde stellt und wie stark seine Entscheidungen von Christus, dem Gekreuzigten und Auferstandenen, her bestimmt sind.`,
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Paulus, durch Gottes Willen berufener Apostel Christi Jesu, und der Bruder Sosthenes", footnotes: ["Röm 1,1; Apg 18,17"] },
      { number: 2, heading: "Anschrift und Gruß: 1,1-3", text: "an die Kirche Gottes, die in Korinth ist, - an die Geheiligten in Christus Jesus, berufen als Heilige mit allen, die den Namen Jesu Christi, unseres Herrn, überall anrufen, bei ihnen und bei uns.", footnotes: ["6,11", "Andere Übersetzungsmöglichkeiten: . . . mit allen, die überall den Namen unseres Herrn Jesus Christus, ihres und unseres Herrn, anrufen. Oder (wenn man diesen Satzteil als Fortführung der Anschrift versteht): . . . an die Gemeinde Gottes in Korinth . . . und an alle, die überall den Namen unseres Herrn Jesus Christus anrufen."] },
      { number: 3, text: "Gnade sei mit euch und Friede von Gott, unserem Vater, und dem Herrn Jesus Christus." },
      { number: 4, text: "Ich danke Gott jederzeit euretwegen für die Gnade Gottes, die euch in Christus Jesus geschenkt wurde," },
      { number: 5, heading: "Dank an Gott: 1,4-9", text: "dass ihr an allem reich geworden seid in ihm, an aller Rede und aller Erkenntnis.", footnotes: ["2 Kor 8,7"] },
      { number: 6, text: "Denn das Zeugnis über Christus wurde bei euch gefestigt,", footnotes: ["Apg 18,5"] },
      { number: 7, text: "sodass euch keine Gnadengabe fehlt, während ihr auf die Offenbarung Jesu Christi, unseres Herrn, wartet.", footnotes: ["2 Thess 1,7"] },
      { number: 8, text: "Er wird euch auch festigen bis ans Ende, sodass ihr schuldlos dasteht am Tag Jesu, unseres Herrn.", footnotes: ["Phil 1,6.10; 1 Thess 3,13", "Gemeint ist die Wiederkunft Christi in Herrlichkeit zum Gericht und zur Vollendung der Welt (vgl. 15,23-28)."] },
      { number: 9, text: "Treu ist Gott, durch den ihr berufen worden seid zur Gemeinschaft mit seinem Sohn Jesus Christus, unserem Herrn.", footnotes: ["10,13"] },
      { number: 10, text: "Ich ermahne euch aber, Brüder, im Namen Jesu Christi, unseres Herrn: Seid alle einmütig und duldet keine Spaltungen unter euch; seid ganz eines Sinnes und einer Meinung.", footnotes: ["11,18; Röm 15,5"] },
      { number: 11, heading: "Die Spaltungen in der Gemeinde: 1,10 - 4,21 | Mahnung zur Einheit: 1,10-17", text: "Es wurde mir nämlich, meine Brüder, von den Leuten der Chloë berichtet, dass es Zank und Streit unter euch gibt." },
      { number: 12, text: "Ich meine damit, dass jeder von euch etwas anderes sagt: Ich halte zu Paulus - ich zu Apollos - ich zu Kephas - ich zu Christus.", footnotes: ["3,3f; Apg 18,24", "Apollos war wie Paulus ein erfolgreicher Missionar (vgl. 3,4-9; 4,6; Apg 18,24-28)."] },
      { number: 13, text: "Ist denn Christus zerteilt? Wurde etwa Paulus für euch gekreuzigt? Oder seid ihr auf den Namen des Paulus getauft worden?" },
      { number: 14, text: "Ich danke Gott, dass ich niemand von euch getauft habe, außer Krispus und Gaius,", footnotes: ["Apg 18,8; Röm 16,23", "Zu Krispus vgl. Apg 18,8; zu Gaius vgl. Röm 16,23."] },
      { number: 15, text: "sodass keiner sagen kann, ihr seiet auf meinen Namen getauft worden." },
      { number: 16, text: "Ich habe allerdings auch die Familie des Stephanas getauft. Ob ich sonst noch jemand getauft habe, weiß ich nicht mehr.", footnotes: ["16,15.17", "Zu Stephanas vgl. 16,15.17."] },
      { number: 17, text: "Denn Christus hat mich nicht gesandt zu taufen, sondern das Evangelium zu verkünden, aber nicht mit gewandten und klugen Worten, damit das Kreuz Christi nicht um seine Kraft gebracht wird.", footnotes: ["Apg 9,15"] },
      { number: 18, text: "Denn das Wort vom Kreuz ist denen, die verloren gehen, Torheit; uns aber, die gerettet werden, ist es Gottes Kraft.", footnotes: ["1,23f; Röm 1,16"] },
      { number: 19, heading: "Die Botschaft vom Kreuz: 1,18-31", text: "Es heißt nämlich in der Schrift: Ich lasse die Weisheit der Weisen vergehen / und die Klugheit der Klugen verschwinden.", footnotes: ["Jes 29,14; Ps 33,10"] },
      { number: 20, text: "Wo ist ein Weiser? Wo ein Schriftgelehrter? Wo ein Wortführer in dieser Welt? Hat Gott nicht die Weisheit der Welt als Torheit entlarvt?", footnotes: ["Jes 19,12; 33,18 G; 44,25; 1 Kor 3,19"] },
      { number: 21, text: "Denn da die Welt angesichts der Weisheit Gottes auf dem Weg ihrer Weisheit Gott nicht erkannte, beschloss Gott, alle, die glauben, durch die Torheit der Verkündigung zu retten.", footnotes: ["Mt 11,25"] },
      { number: 22, text: "Die Juden fordern Zeichen, die Griechen suchen Weisheit.", footnotes: ["Mt 12,38 par; Joh 2,18; Apg 17,18-21"] },
      { number: 23, text: "Wir dagegen verkündigen Christus als den Gekreuzigten: für Juden ein empörendes Ärgernis, für Heiden eine Torheit,", footnotes: ["2,2.14; 1,18; 2,5; Kol 2,3"] },
      { number: 24, text: "für die Berufenen aber, Juden wie Griechen, Christus, Gottes Kraft und Gottes Weisheit." },
      { number: 25, text: "Denn das Törichte an Gott ist weiser als die Menschen und das Schwache an Gott ist stärker als die Menschen.", footnotes: ["2 Kor 13,4"] },
      { number: 26, text: "Seht doch auf eure Berufung, Brüder! Da sind nicht viele Weise im irdischen Sinn, nicht viele Mächtige, nicht viele Vornehme,", footnotes: ["im irdischen Sinn, wörtlich: dem Fleisch nach."] },
      { number: 27, text: "sondern das Törichte in der Welt hat Gott erwählt, um die Weisen zuschanden zu machen, und das Schwache in der Welt hat Gott erwählt, um das Starke zuschanden zu machen.", footnotes: ["Jak 2,5"] },
      { number: 28, text: "Und das Niedrige in der Welt und das Verachtete hat Gott erwählt: das, was nichts ist, um das, was etwas ist, zu vernichten," },
      { number: 29, text: "damit kein Mensch sich rühmen kann vor Gott.", footnotes: ["Röm 3,27; Eph 2,9"] },
      { number: 30, text: "Von ihm her seid ihr in Christus Jesus, den Gott für uns zur Weisheit gemacht hat, zur Gerechtigkeit, Heiligung und Erlösung." },
      { number: 31, text: "Wer sich also rühmen will, der rühme sich des Herrn; so heißt es schon in der Schrift.", footnotes: ["Jer 9,22f; 2 Kor 10,17"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Als ich zu euch kam, Brüder, kam ich nicht, um glänzende Reden oder gelehrte Weisheit vorzutragen, sondern um euch das Zeugnis Gottes zu verkündigen.", footnotes: ["1,17", "Anstelle von «Zeugnis Gottes» haben andere Textzeugen: Geheimnis Gottes. Gemeint ist das Evangelium vom gekreuzigten Christus."] },
      { number: 2, heading: "Paulus, Verkünder der Weisheit Gottes: 2,1 - 3,4", text: "Denn ich hatte mich entschlossen, bei euch nichts zu wissen außer Jesus Christus, und zwar als den Gekreuzigten.", footnotes: ["Gal 6,14"] },
      { number: 3, text: "Zudem kam ich in Schwäche und in Furcht, zitternd und bebend zu euch.", footnotes: ["Apg 18,9; 2 Kor 10,10"] },
      { number: 4, text: "Meine Botschaft und Verkündigung war nicht Überredung durch gewandte und kluge Worte, sondern war mit dem Erweis von Geist und Kraft verbunden,", footnotes: ["1 Thess 1,5"] },
      { number: 5, text: "damit sich euer Glaube nicht auf Menschenweisheit stützte, sondern auf die Kraft Gottes.", footnotes: ["1,18.24"] },
      { number: 6, text: "Und doch verkündigen wir Weisheit unter den Vollkommenen, aber nicht Weisheit dieser Welt oder der Machthaber dieser Welt, die einst entmachtet werden.", footnotes: ["Machthaber dieser Welt: menschliche Autoritäten und dämonische Mächte (vgl. 1 Kor 15,24f)."] },
      { number: 7, text: "Vielmehr verkündigen wir das Geheimnis der verborgenen Weisheit Gottes, die Gott vor allen Zeiten vorausbestimmt hat zu unserer Verherrlichung.", footnotes: ["Röm 16,25; Kol 1,26"] },
      { number: 8, text: "Keiner der Machthaber dieser Welt hat sie erkannt; denn hätten sie die Weisheit Gottes erkannt, so hätten sie den Herrn der Herrlichkeit nicht gekreuzigt." },
      { number: 9, text: "Nein, wir verkündigen, wie es in der Schrift heißt, was kein Auge gesehen und kein Ohr gehört hat, was keinem Menschen in den Sinn gekommen ist: das Große, das Gott denen bereitet hat, die ihn lieben.", footnotes: ["Jes 64,3; Jer 3,16; Sir 1,10", "Dieses «Schriftzitat» ist als Ganzes im Alten Testament nicht zu finden."] },
      { number: 10, text: "Denn uns hat es Gott enthüllt durch den Geist. Der Geist ergründet nämlich alles, auch die Tiefen Gottes.", footnotes: ["Dan 2,22"] },
      { number: 11, text: "Wer von den Menschen kennt den Menschen, wenn nicht der Geist des Menschen, der in ihm ist? So erkennt auch keiner Gott - nur der Geist Gottes." },
      { number: 12, text: "Wir aber haben nicht den Geist der Welt empfangen, sondern den Geist, der aus Gott stammt, damit wir das erkennen, was uns von Gott geschenkt worden ist.", footnotes: ["Röm 8,15"] },
      { number: 13, text: "Davon reden wir auch, nicht mit Worten, wie menschliche Weisheit sie lehrt, sondern wie der Geist sie lehrt, indem wir den Geisterfüllten das Wirken des Geistes deuten.", footnotes: ["Andere Übersetzungsmöglichkeiten: indem wir Wirkungen des Geistes mit geistgewirkten Worten wiedergeben; oder: indem wir Wirkungen des Geistes miteinander vergleichen."] },
      { number: 14, text: "Der irdisch gesinnte Mensch aber lässt sich nicht auf das ein, was vom Geist Gottes kommt. Torheit ist es für ihn, und er kann es nicht verstehen, weil es nur mit Hilfe des Geistes beurteilt werden kann.", footnotes: ["1,23", "Der irdisch gesinnte Mensch, wörtlich: Der psychische Mensch. Dieser urteilt über Gott und sein Handeln mit einem nicht vom Geist Gottes erleuchteten Denken. Zum abwertenden Gebrauch von «psychisch» vgl. Jak 3,15; Jud 19; auch 1 Kor 15,44.46 («psychischer Leib», d. h. sterblicher Leib)."] },
      { number: 15, text: "Der geisterfüllte Mensch urteilt über alles, ihn aber vermag niemand zu beurteilen.", footnotes: ["Vgl. die Anmerkung zu 1 Kor 12,10."] },
      { number: 16, text: "Denn wer begreift den Geist des Herrn? Wer kann ihn belehren? Wir aber haben den Geist Christi.", footnotes: ["Jes 40,13 G; Röm 11,34; 8,9"] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Vor euch, Brüder, konnte ich aber nicht wie vor Geisterfüllten reden; ihr wart noch irdisch eingestellt, unmündige Kinder in Christus." },
      { number: 2, text: "Milch gab ich euch zu trinken statt fester Speise; denn diese konntet ihr noch nicht vertragen. Ihr könnt es aber auch jetzt noch nicht;", footnotes: ["1 Petr 2,2"] },
      { number: 3, text: "denn ihr seid immer noch irdisch eingestellt. Oder seid ihr nicht irdisch eingestellt, handelt ihr nicht sehr menschlich, wenn Eifersucht und Streit unter euch herrschen?", footnotes: ["1,11f; 11,18; Gal 5,19f"] },
      { number: 4, text: "Denn wenn einer sagt: Ich halte zu Paulus!, ein anderer: Ich zu Apollos!, seid ihr da nicht Menschen?" },
      { number: 5, text: "Was ist denn Apollos? Und was ist Paulus? Ihr seid durch sie zum Glauben gekommen. Sie sind also Diener, jeder, wie der Herr es ihm gegeben hat:", footnotes: ["1,12"] },
      { number: 6, heading: "Die Verantwortung des Apostels und seiner Mitarbeiter: 3,5 - 4,16", text: "Ich habe gepflanzt, Apollos hat begossen, Gott aber ließ wachsen." },
      { number: 7, text: "So ist weder der etwas, der pflanzt, noch der, der begießt, sondern nur Gott, der wachsen lässt." },
      { number: 8, text: "Wer pflanzt und wer begießt: Beide arbeiten am gleichen Werk, jeder aber erhält seinen besonderen Lohn, je nach der Mühe, die er aufgewendet hat." },
      { number: 9, text: "Denn wir sind Gottes Mitarbeiter; ihr seid Gottes Ackerfeld, Gottes Bau.", footnotes: ["Mt 13,3-9; Eph 2,20"] },
      { number: 10, text: "Der Gnade Gottes entsprechend, die mir geschenkt wurde, habe ich wie ein guter Baumeister den Grund gelegt; ein anderer baut darauf weiter. Aber jeder soll darauf achten, wie er weiterbaut.", footnotes: ["15,10; Röm 15,20"] },
      { number: 11, text: "Denn einen anderen Grund kann niemand legen als den, der gelegt ist: Jesus Christus.", footnotes: ["Eph 2,20; 1 Petr 2,4-6; Apg 4,11f"] },
      { number: 12, text: "Ob aber jemand auf dem Grund mit Gold, Silber, kostbaren Steinen, mit Holz, Heu oder Stroh weiterbaut:" },
      { number: 13, text: "das Werk eines jeden wird offenbar werden; jener Tag wird es sichtbar machen, weil es im Feuer offenbart wird. Das Feuer wird prüfen, was das Werk eines jeden taugt.", footnotes: ["4,5; 2 Thess 1,7f"] },
      { number: 14, text: "Hält das stand, was er aufgebaut hat, so empfängt er Lohn." },
      { number: 15, text: "Brennt es nieder, dann muss er den Verlust tragen. Er selbst aber wird gerettet werden, doch so wie durch Feuer hindurch.", footnotes: ["Jud 23"] },
      { number: 16, text: "wisst ihr nicht, dass ihr Gottes Tempel seid und der Geist Gottes in euch wohnt?", footnotes: ["6,19; 2 Kor 6,16; Eph 2,21f", "Vgl. 6,19, wo nicht die Gemeinde als ganze, sondern der einzelne Christ als «Tempel des Heiligen Geistes» bezeichnet wird."] },
      { number: 17, text: "Wer den Tempel Gottes verdirbt, den wird Gott verderben. Denn Gottes Tempel ist heilig, und der seid ihr." },
      { number: 18, text: "Keiner täusche sich selbst. Wenn einer unter euch meint, er sei weise in dieser Welt, dann werde er töricht, um weise zu werden.", footnotes: ["4,10"] },
      { number: 19, text: "Denn die Weisheit dieser Welt ist Torheit vor Gott. In der Schrift steht nämlich: Er fängt die Weisen in ihrer eigenen List.", footnotes: ["Ijob 5,13; 1 Kor 1,20; Röm 1,22"] },
      { number: 20, text: "Und an einer anderen Stelle: Der Herr kennt die Gedanken der Weisen; er weiß, sie sind nichtig.", footnotes: ["Ps 94,11 G"] },
      { number: 21, text: "Daher soll sich niemand eines Menschen rühmen. Denn alles gehört euch;" },
      { number: 22, text: "Paulus, Apollos, Kephas, Welt, Leben, Tod, Gegenwart und Zukunft: alles gehört euch;", footnotes: ["1,12; Röm 8,38"] },
      { number: 23, text: "ihr aber gehört Christus und Christus gehört Gott." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Als Diener Christi soll man uns betrachten und als Verwalter von Geheimnissen Gottes.", footnotes: ["3,5; Kol 1,25f; 1 Petr 4,10"] },
      { number: 2, text: "Von Verwaltern aber verlangt man, dass sie sich treu erweisen.", footnotes: ["Lk 12,42"] },
      { number: 3, text: "Mir macht es allerdings nichts aus, wenn ihr oder ein menschliches Gericht mich zur Verantwortung zieht; ich urteile auch nicht über mich selbst.", footnotes: ["9,3"] },
      { number: 4, text: "Ich bin mir zwar keiner Schuld bewusst, doch bin ich dadurch noch nicht gerecht gesprochen; der Herr ist es, der mich zur Rechenschaft zieht." },
      { number: 5, text: "Richtet also nicht vor der Zeit; wartet, bis der Herr kommt, der das im Dunkeln Verborgene ans Licht bringen und die Absichten der Herzen aufdecken wird. Dann wird jeder sein Lob von Gott erhalten.", footnotes: ["Röm 2,16"] },
      { number: 6, text: "Brüder, ich habe das auf mich und Apollos bezogen, und zwar euretwegen, damit ihr an uns lernt, dass der Grundsatz gilt: «Nicht über das hinaus, was in der Schrift steht», dass also keiner zugunsten des einen und zum Nachteil des andern sich wichtig machen darf.", footnotes: ["Röm 12,3", "Paulus zitiert hier vermutlich einen in Korinth bekannten Auslegungsgrundsatz."] },
      { number: 7, text: "Denn wer räumt dir einen Vorrang ein? Und was hast du, das du nicht empfangen hättest? Wenn du es aber empfangen hast, warum rühmst du dich, als hättest du es nicht empfangen?" },
      { number: 8, text: "Ihr seid schon satt, ihr seid schon reich geworden, ohne uns seid ihr zur Herrschaft gelangt. Wäret ihr doch nur zur Herrschaft gelangt! Dann könnten auch wir mit euch zusammen herrschen.", footnotes: ["Offb 3,21", "8a: Die Aussage ist ironisch gemeint."] },
      { number: 9, text: "Ich glaube nämlich, Gott hat uns Apostel auf den letzten Platz gestellt, wie Todgeweihte; denn wir sind zum Schauspiel geworden für die Welt, für Engel und Menschen.", footnotes: ["2 Kor 4,11"] },
      { number: 10, text: "Wir stehen als Toren da um Christi willen, ihr dagegen seid kluge Leute in Christus. Wir sind schwach, ihr seid stark; ihr seid angesehen, wir sind verachtet." },
      { number: 11, text: "Bis zur Stunde hungern und dürsten wir, gehen in Lumpen, werden mit Fäusten geschlagen und sind heimatlos.", footnotes: ["2 Kor 11,27"] },
      { number: 12, text: "Wir plagen uns ab und arbeiten mit eigenen Händen; wir werden beschimpft und segnen; wir werden verfolgt und halten stand;", footnotes: ["9,12; Apg 18,3; 20,33f"] },
      { number: 13, text: "wir werden geschmäht und trösten. Wir sind sozusagen der Abschaum der Welt geworden, verstoßen von allen bis heute.", footnotes: ["Apg 22,22"] },
      { number: 14, text: "Nicht um euch bloßzustellen, schreibe ich das, sondern um euch als meine geliebten Kinder zu ermahnen." },
      { number: 15, text: "Hättet ihr nämlich auch ungezählte Erzieher in Christus, so doch nicht viele Väter. Denn in Christus Jesus bin ich durch das Evangelium euer Vater geworden.", footnotes: ["Gal 4,19; Phil 2,22; Phlm 10", "Zur Vorstellung geistlicher Vaterschaft vgl. Gal 4,19; Phlm 10; 1 Petr 1,23."] },
      { number: 16, text: "Darum ermahne ich euch: Haltet euch an mein Vorbild!", footnotes: ["11,1; Phil 4,9"] },
      { number: 17, text: "Eben deswegen schicke ich Timotheus zu euch, mein geliebtes und treues Kind im Herrn. Er wird euch erinnern an meine Weisungen, wie ich sie als Diener Christi Jesu überall in allen Gemeinden gebe.", footnotes: ["16,10; Apg 16,1; 19,22"] },
      { number: 18, heading: "Ankündigung eines baldigen Besuches: 4,17-21", text: "In der Annahme, dass ich nicht selber zu euch komme, haben sich zwar einige wichtig gemacht." },
      { number: 19, text: "Ich werde aber bald zu euch kommen, wenn der Herr will. Dann werde ich diese Wichtigtuer nicht auf ihre Worte prüfen, sondern auf ihre Kraft.", footnotes: ["16,5-7; 2 Kor 13,1-10"] },
      { number: 20, text: "Denn nicht in Worten erweist sich die Herrschaft Gottes, sondern in der Kraft.", footnotes: ["2,4"] },
      { number: 21, text: "Was zieht ihr vor: Soll ich mit dem Stock zu euch kommen oder mit Liebe und im Geist der Sanftmut?", footnotes: ["2 Kor 13,10"] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Übrigens hört man von Unzucht unter euch, und zwar von Unzucht, wie sie nicht einmal unter den Heiden vorkommt, dass nämlich einer mit der Frau seines Vaters lebt.", footnotes: ["Lev 18,7f; 20,11; Dtn 27,20", "Vgl. dazu Lev 18,7f; 20,11; Dtn 27,20; auch das römische Recht stellte dieses Vergehen unter Strafe."] },
      { number: 2, heading: "Missstände in der Gemeinde: 5,1 - 6,20 | Ein Fall von Blutschande: 5,1-13", text: "Und da macht ihr euch noch wichtig, statt traurig zu werden und den aus eurer Mitte zu stoßen, der so etwas getan hat." },
      { number: 3, text: "Was mich angeht, so habe ich - leiblich zwar abwesend, geistig aber anwesend - mein Urteil über den, der sich so vergangen hat, schon jetzt gefällt, als ob ich persönlich anwesend wäre:", footnotes: ["Kol 2,5"] },
      { number: 4, text: "Im Namen Jesu, unseres Herrn, wollen wir uns versammeln, ihr und mein Geist, und zusammen mit der Kraft Jesu, unseres Herrn,", footnotes: ["Mt 18,20"] },
      { number: 5, text: "diesen Menschen dem Satan übergeben zum Verderben seines Fleisches, damit sein Geist am Tag des Herrn gerettet wird.", footnotes: ["1 Tim 1,20; 1 Petr 4,6", "Der Sünder schändet die Heiligkeit der Gemeinde; er soll daher ausgeschlossen werden."] },
      { number: 6, text: "Zu Unrecht rühmt ihr euch. Wisst ihr nicht, dass ein wenig Sauerteig den ganzen Teig durchsäuert?", footnotes: ["Gal 5,9"] },
      { number: 7, text: "Schafft den alten Sauerteig weg, damit ihr neuer Teig seid. Ihr seid ja schon ungesäuertes Brot; denn als unser Paschalamm ist Christus geopfert worden.", footnotes: ["Ex 12,15.19; 13,7; 1 Petr 1,19; Ex 12,21"] },
      { number: 8, text: "Lasst uns also das Fest nicht mit dem alten Sauerteig feiern, nicht mit dem Sauerteig der Bosheit und Schlechtigkeit, sondern mit den ungesäuerten Broten der Aufrichtigkeit und Wahrheit.", footnotes: ["Ex 12,15-20"] },
      { number: 9, text: "Ich habe euch in meinem Brief ermahnt, dass ihr nichts mit Unzüchtigen zu schaffen haben sollt.", footnotes: ["2 Petr 2,2.14.19; Jud 23", "Der hier erwähnte Brief ist vermutlich verloren gegangen; Fragmente dieses Briefs könnten in den 1. Korintherbrief aufgenommen worden sein."] },
      { number: 10, text: "Gemeint waren damit nicht alle Unzüchtigen dieser Welt oder alle Habgierigen und Räuber und Götzendiener; sonst müsstet ihr ja aus der Welt auswandern.", footnotes: ["1 Tim 6,3-10; 1 Joh 5,19"] },
      { number: 11, text: "In Wirklichkeit meinte ich damit: Habt nichts zu schaffen mit einem, der sich Bruder nennt und dennoch Unzucht treibt, habgierig ist, Götzen verehrt, lästert, trinkt oder raubt; mit einem solchen Menschen sollt ihr nicht einmal zusammen essen.", footnotes: ["6,9f; 2 Thess 3,6; Kol 4,5"] },
      { number: 12, text: "Ich will also nicht Außenstehende richten - ihr richtet ja auch nur solche, die zu euch gehören -," },
      { number: 13, text: "die Außenstehenden wird Gott richten. Schafft den Übeltäter weg aus eurer Mitte!", footnotes: ["Dtn 17,7; 19,19"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Wagt es einer von euch, der mit einem anderen einen Rechtsstreit hat, vor das Gericht der Ungerechten zu gehen statt zu den Heiligen?", footnotes: ["Die Nichtchristen werden hier als «Ungerechte» bezeichnet, die Christen als «Heilige» (vgl. z. B. 1,2)."] },
      { number: 2, heading: "Rechtshändel unter Christen: 6,1-11", text: "Wisst ihr denn nicht, dass die Heiligen die Welt richten werden? Und wenn durch euch die Welt gerichtet wird, seid ihr dann nicht zuständig, einen Rechtsstreit über Kleinigkeiten zu schlichten?", footnotes: ["Dan 7,22; Lk 22,30; Offb 20,4", "Paulus vertritt die Auffassung, dass die Christen als Glieder Christi an dessen richterlicher Tätigkeit beim Endgericht teilhaben werden (vgl. 6,15; 12,27)."] },
      { number: 3, text: "Wisst ihr nicht, dass wir über Engel richten werden? Also erst recht über Alltägliches.", footnotes: ["2 Petr 2,4; Jud 6"] },
      { number: 4, text: "Wie könnt ihr dann jene, die im Urteil der Gemeinde nichts gelten, als Richter einsetzen, wenn ihr einen Rechtsstreit über Alltägliches auszutragen habt?", footnotes: ["die im Urteil der Gemeinde nichts gelten: die Nichtchristen."] },
      { number: 5, text: "Ich sage das, damit ihr euch schämt. Gibt es denn unter euch wirklich keinen, der die Gabe hat, zwischen Brüdern zu schlichten?", footnotes: ["4,14"] },
      { number: 6, text: "Stattdessen zieht ein Bruder den andern vor Gericht, und zwar vor Ungläubige." },
      { number: 7, text: "Ist es nicht überhaupt schon ein Versagen, dass ihr miteinander Prozesse führt? Warum leidet ihr nicht lieber Unrecht? Warum lasst ihr euch nicht lieber ausrauben?", footnotes: ["Mt 5,39f; 1 Thess 5,15; 1 Petr 3,9"] },
      { number: 8, text: "Nein, ihr selber begeht Unrecht und Raub, und zwar an Brüdern." },
      { number: 9, text: "Wisst ihr denn nicht, dass Ungerechte das Reich Gottes nicht erben werden? Täuscht euch nicht! Weder Unzüchtige noch Götzendiener, weder Ehebrecher noch Lustknaben, noch Knabenschänder,", footnotes: ["15,50; Gal 5,19-21; Eph 5,5; Offb 21,8; 22,15"] },
      { number: 10, text: "noch Diebe, noch Habgierige, keine Trinker, keine Lästerer, keine Räuber werden das Reich Gottes erben." },
      { number: 11, text: "Und solche gab es unter euch. Aber ihr seid rein gewaschen, seid geheiligt, seid gerecht geworden im Namen Jesu Christi, des Herrn, und im Geist unseres Gottes.", footnotes: ["Röm 6,6-11; Tit 3,4-7; 1 Petr 3,21; 1 Joh 2,12; Hebr 9,14; 10,22", "rein gewaschen . . .: Ein Hinweis auf die Taufe (vgl. Eph 5,26; Tit 3,3-7; Hebr 10,22; 1 Petr 1,2; 3,21)."] },
      { number: 12, text: "«Alles ist mir erlaubt» - aber nicht alles nützt mir. Alles ist mir erlaubt, aber nichts soll Macht haben über mich.", footnotes: ["10,23", "Alles ist (mir) erlaubt: vermutlich ein Schlagwort, das in der Gemeinde von Korinth zu hören war (vgl. 10,23)."] },
      { number: 13, heading: "Unzucht und christliche Freiheit: 6,12-20", text: "Die Speisen sind für den Bauch da und der Bauch für die Speisen; Gott wird beide vernichten. Der Leib ist aber nicht für die Unzucht da, sondern für den Herrn, und der Herr für den Leib.", footnotes: ["Mt 15,17; 1 Thess 4,3-5"] },
      { number: 14, text: "Gott hat den Herrn auferweckt; er wird durch seine Macht auch uns auferwecken.", footnotes: ["15,20f; Röm 8,11; 2 Kor 4,14"] },
      { number: 15, text: "Wisst ihr nicht, dass eure Leiber Glieder Christi sind? Darf ich nun die Glieder Christi nehmen und zu Gliedern einer Dirne machen? Auf keinen Fall!", footnotes: ["12,12.27; Röm 12,5"] },
      { number: 16, text: "Oder wisst ihr nicht: Wer sich an eine Dirne bindet, ist ein Leib mit ihr? Denn es heißt: Die zwei werden ein Fleisch sein.", footnotes: ["Gen 2,24"] },
      { number: 17, text: "Wer sich dagegen an den Herrn bindet, ist ein Geist mit ihm.", footnotes: ["2 Kor 3,17"] },
      { number: 18, text: "Hütet euch vor der Unzucht! Jede andere Sünde, die der Mensch tut, bleibt außerhalb des Leibes. Wer aber Unzucht treibt, versündigt sich gegen den eigenen Leib." },
      { number: 19, text: "Oder wisst ihr nicht, dass euer Leib ein Tempel des Heiligen Geistes ist, der in euch wohnt und den ihr von Gott habt? Ihr gehört nicht euch selbst;", footnotes: ["3,16; Röm 8,11.15"] },
      { number: 20, text: "denn um einen teuren Preis seid ihr erkauft worden. Verherrlicht also Gott in eurem Leib!", footnotes: ["7,23; 1 Petr 1,18f", "Sklaven konnten gegen Lösegeld freigekauft werden. Hier wird der Tod Christi als Kaufpreis verstanden, durch den die Christen aus der Knechtschaft der Sünde befreit wurden (vgl. 7,23; Gal 3,13; 1 Petr 1,18f)."] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Nun zu den Anfragen eures Briefes! «Es ist gut für den Mann(,) keine Frau zu berühren».", footnotes: ["Mt 19,10; 1 Tim 4,3", "Es handelt sich hier wohl um ein Zitat aus dem Brief der Korinther an Paulus."] },
      { number: 2, heading: "Die Standesordnung in der Gemeinde: 7,1-40 | Die christliche Ehe: 7,1-7", text: "Wegen der Gefahr der Unzucht soll aber jeder seine Frau haben und jede soll ihren Mann haben.", footnotes: ["1 Thess 4,3f"] },
      { number: 3, text: "Der Mann soll seine Pflicht gegenüber der Frau erfüllen und ebenso die Frau gegenüber dem Mann.", footnotes: ["Eph 5,21-29"] },
      { number: 4, text: "Nicht die Frau verfügt über ihren Leib, sondern der Mann. Ebenso verfügt nicht der Mann über seinen Leib, sondern die Frau." },
      { number: 5, text: "Entzieht euch einander nicht, außer im gegenseitigen Einverständnis und nur eine Zeit lang, um für das Gebet frei zu sein. Dann kommt wieder zusammen, damit euch der Satan nicht in Versuchung führt, wenn ihr euch nicht enthalten könnt." },
      { number: 6, text: "Das sage ich als Zugeständnis, nicht als Gebot." },
      { number: 7, text: "Ich wünschte, alle Menschen wären (unverheiratet) wie ich. Doch jeder hat seine Gnadengabe von Gott, der eine so, der andere so.", footnotes: ["Mt 19,12"] },
      { number: 8, text: "Den Unverheirateten und den Witwen sage ich: Es ist gut, wenn sie so bleiben wie ich." },
      { number: 9, heading: "Ehe mit Heiden - Ehescheidung: 7,8-16", text: "Wenn sie aber nicht enthaltsam leben können, sollen sie heiraten. Es ist besser zu heiraten, als sich in Begierde zu verzehren.", footnotes: ["1 Tim 5,14"] },
      { number: 10, text: "Den Verheirateten gebiete nicht ich, sondern der Herr: Die Frau soll sich vom Mann nicht trennen", footnotes: ["Mt 5,32; 19,4-6"] },
      { number: 11, text: "- wenn sie sich aber trennt, so bleibe sie unverheiratet oder versöhne sich wieder mit dem Mann - und der Mann darf die Frau nicht verstoßen.", footnotes: ["Vgl. Dtn 24,1-4 und die Erklärung dieser Stelle durch Jesus in Mt 19,8f."] },
      { number: 12, text: "Den Übrigen sage ich, nicht der Herr: Wenn ein Bruder eine ungläubige Frau hat und sie willigt ein, weiter mit ihm zusammenzuleben, soll er sie nicht verstoßen." },
      { number: 13, text: "Auch eine Frau soll ihren ungläubigen Mann nicht verstoßen, wenn er einwilligt, weiter mit ihr zusammenzuleben." },
      { number: 14, text: "Denn der ungläubige Mann ist durch die Frau geheiligt und die ungläubige Frau ist durch ihren gläubigen Mann geheiligt. Sonst wären eure Kinder unrein; sie sind aber heilig.", footnotes: ["durch ihren gläubigen Mann, wörtlich: durch den Bruder."] },
      { number: 15, text: "Wenn aber der Ungläubige sich trennen will, soll er es tun. Der Bruder oder die Schwester ist in solchen Fällen nicht wie ein Sklave gebunden; zu einem Leben in Frieden hat Gott euch berufen.", footnotes: ["15f: Man kann die Stelle auch so verstehen, dass Paulus es für wünschenswert hält, wenn sich der gläubige Teil jede erdenkliche Mühe gibt, die Ehe mit dem ungläubigen Partner fortzusetzen. In diesem Sinn lässt sich übersetzen: Gott hat euch aber zu einem gemeinsamen Leben gerufen. Vielleicht vermagst du, Frau, deinen Mann zu retten; vielleicht vermagst du, Mann, deine Frau zu retten."] },
      { number: 16, text: "Woher weißt du denn, Frau, ob du den Mann retten kannst? Oder woher weißt du, Mann, ob du die Frau retten kannst?", footnotes: ["1 Petr 3,1f"] },
      { number: 17, text: "Im Übrigen soll jeder so leben, wie der Herr es ihm zugemessen, wie Gottes Ruf ihn getroffen hat. Das ist meine Weisung für alle Gemeinden.", footnotes: ["7,20.24"] },
      { number: 18, heading: "Gottes Ruf und der Stand der Berufenen: 7,17-24", text: "Wenn einer als Beschnittener berufen wurde, soll er beschnitten bleiben. Wenn einer als Unbeschnittener berufen wurde, soll er sich nicht beschneiden lassen.", footnotes: ["Gal 5,1f"] },
      { number: 19, text: "Es kommt nicht darauf an, beschnitten oder unbeschnitten zu sein, sondern darauf, die Gebote Gottes zu halten.", footnotes: ["Gal 5,6; 6,15; Röm 2,25f"] },
      { number: 20, text: "Jeder soll in dem Stand bleiben, in dem ihn der Ruf Gottes getroffen hat." },
      { number: 21, text: "Wenn du als Sklave berufen wurdest, soll dich das nicht bedrücken; auch wenn du frei werden kannst, lebe lieber als Sklave weiter.", footnotes: ["Eph 6,5f; Phlm 16", "Der griechische Wortlaut des Verses und der Zusammenhang des Abschnitts empfehlen diese Übersetzung. Es gibt aber auch Gründe für das Verständnis: Ergreif lieber die Gelegenheit (frei zu werden)."] },
      { number: 22, text: "Denn wer im Herrn als Sklave berufen wurde, ist Freigelassener des Herrn. Ebenso ist einer, der als Freier berufen wurde, Sklave Christi." },
      { number: 23, text: "Um einen teuren Preis seid ihr erkauft worden. Macht euch nicht zu Sklaven von Menschen!", footnotes: ["6,20"] },
      { number: 24, text: "Brüder, jeder soll vor Gott in dem Stand bleiben, in dem ihn der Ruf Gottes getroffen hat." },
      { number: 25, text: "Was die Frage der Ehelosigkeit angeht, so habe ich kein Gebot vom Herrn. Ich gebe euch nur einen Rat als einer, den der Herr durch sein Erbarmen vertrauenswürdig gemacht hat.", footnotes: ["Mt 19,12; 2 Kor 8,10"] },
      { number: 26, heading: "Ehe und Jungfräulichkeit: 7,25-38", text: "Ich meine, es ist gut wegen der bevorstehenden Not, ja, es ist gut für den Menschen, so zu sein.", footnotes: ["7,29; 10,11", "Die «bevorstehende Not» ist die dem Ende vorausgehende Notzeit (vgl. Mk 13,14-20)."] },
      { number: 27, text: "Bist du an eine Frau gebunden, suche dich nicht zu lösen; bist du ohne Frau, dann suche keine." },
      { number: 28, text: "Heiratest du aber, so sündigst du nicht; und heiratet eine Jungfrau, sündigt auch sie nicht. Freilich werden solche Leute irdischen Nöten nicht entgehen; ich aber möchte sie euch ersparen." },
      { number: 29, text: "Denn ich sage euch, Brüder: Die Zeit ist kurz. Daher soll, wer eine Frau hat, sich in Zukunft so verhalten, als habe er keine,", footnotes: ["Röm 13,11; 1 Petr 4,7; 1 Joh 2,16f"] },
      { number: 30, text: "wer weint, als weine er nicht, wer sich freut, als freue er sich nicht, wer kauft, als würde er nicht Eigentümer," },
      { number: 31, text: "wer sich die Welt zunutze macht, als nutze er sie nicht; denn die Gestalt dieser Welt vergeht." },
      { number: 32, text: "Ich wünschte aber, ihr wäret ohne Sorgen. Der Unverheiratete sorgt sich um die Sache des Herrn; er will dem Herrn gefallen." },
      { number: 33, text: "Der Verheiratete sorgt sich um die Dinge der Welt; er will seiner Frau gefallen.", footnotes: ["Lk 14,20"] },
      { number: 34, text: "So ist er geteilt. Die unverheiratete Frau aber und die Jungfrau sorgen sich um die Sache des Herrn, um heilig zu sein an Leib und Geist. Die Verheiratete sorgt sich um die Dinge der Welt; sie will ihrem Mann gefallen." },
      { number: 35, text: "Das sage ich zu eurem Nutzen: nicht um euch eine Fessel anzulegen, vielmehr, damit ihr in rechter Weise und ungestört immer dem Herrn dienen könnt." },
      { number: 36, text: "Wer sich gegenüber seiner Jungfrau ungehörig zu verhalten glaubt, wenn sein Verlangen nach ihr zu stark ist, der soll tun, wozu es ihn drängt, wenn es so sein muss; er sündigt nicht; sie sollen heiraten.", footnotes: ["36-38: Diese Übersetzung der VV. 36-38 setzt christliche Verlobte voraus, die angesichts der Erwartung des nahen Weltendes nicht heiraten (vgl. 7,25-35), oder christliche Jungfrauen, die aus Liebe zu Christus auf die Ehe verzichten und zum Schutz gegen Zudringlichkeiten bei einem ebenfalls unverheirateten Christen leben."] },
      { number: 37, text: "Wer aber in seinem Herzen fest bleibt, weil er sich in der Gewalt hat und seinem Trieb nicht ausgeliefert ist, wer also in seinem Herzen entschlossen ist, seine Jungfrau unberührt zu lassen, der handelt richtig." },
      { number: 38, text: "Wer seine Jungfrau heiratet, handelt also richtig; doch wer sie nicht heiratet, handelt besser." },
      { number: 39, text: "Eine Frau ist gebunden, solange ihr Mann lebt; wenn aber der Mann gestorben ist, ist sie frei zu heiraten, wen sie will; nur geschehe es im Herrn.", footnotes: ["Röm 7,2"] },
      { number: 40, heading: "Wiederverheiratung nach dem Tod des Mannes: 7,39-40", text: "Glücklicher aber ist sie zu preisen, wenn sie nach meinem Rat unverheiratet bleibt - und ich denke, dass auch ich den Geist Gottes habe." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Nun zur Frage des Götzenopferfleisches. Gewiss, wir alle haben Erkenntnis. Doch die Erkenntnis macht aufgeblasen, die Liebe dagegen baut auf.", footnotes: ["(1-13) Röm 14,1-23; Apg 15,29", "macht aufgeblasen: Paulus richtet sich gegen eine selbstherrliche, also falsche «Erkenntnis». - «Aufbauen» meint hier (wie 10,23; 14,3-5.12.17.26) alles, was das brüderliche Einvernehmen unter den Gliedern der Gemeinde fördert."] },
      { number: 2, heading: "Heidnische Opfer, christliche Sakramente, christlicher Gottesdienst: 8,1 - 11,34 | Die Freiheit des Christen: 8,1-13", text: "Wenn einer meint, er sei zur Erkenntnis gelangt, hat er noch nicht so erkannt, wie man erkennen muss.", footnotes: ["Gal 6,3"] },
      { number: 3, text: "Wer aber Gott liebt, der ist von ihm erkannt.", footnotes: ["13,12; Gal 4,9"] },
      { number: 4, text: "Was nun das Essen von Götzenopferfleisch angeht, so wissen wir, dass es keine Götzen gibt in der Welt und keinen Gott außer dem einen.", footnotes: ["10,19f"] },
      { number: 5, text: "Und selbst wenn es im Himmel oder auf der Erde sogenannte Götter gibt - und solche Götter und Herren gibt es viele -," },
      { number: 6, text: "so haben doch wir nur einen Gott, den Vater. Von ihm stammt alles und wir leben auf ihn hin. Und einer ist der Herr: Jesus Christus. Durch ihn ist alles, und wir sind durch ihn.", footnotes: ["Röm 11,36; Kol 1,16f"] },
      { number: 7, text: "Aber nicht alle haben die Erkenntnis. Einige, die von ihren Götzen nicht loskommen, essen das Fleisch noch als Götzenopferfleisch und so wird ihr schwaches Gewissen befleckt.", footnotes: ["10,27f"] },
      { number: 8, text: "Zwar kann uns keine Speise vor Gottes Gericht bringen. Wenn wir nicht essen, verlieren wir nichts, und wenn wir essen, gewinnen wir nichts." },
      { number: 9, text: "Doch gebt Acht, dass diese eure Freiheit nicht den Schwachen zum Anstoß wird.", footnotes: ["Gal 5,13"] },
      { number: 10, text: "Wenn nämlich einer dich, der du Erkenntnis hast, im Götzentempel beim Mahl sieht, wird dann nicht sein Gewissen, da er schwach ist, verleitet, auch Götzenopferfleisch zu essen?" },
      { number: 11, text: "Der Schwache geht an deiner «Erkenntnis» zugrunde, er, dein Bruder, für den Christus gestorben ist." },
      { number: 12, text: "Wenn ihr euch auf diese Weise gegen eure Brüder versündigt und ihr schwaches Gewissen verletzt, versündigt ihr euch gegen Christus." },
      { number: 13, text: "Wenn darum eine Speise meinem Bruder zum Anstoß wird, will ich überhaupt kein Fleisch mehr essen, um meinem Bruder keinen Anstoß zu geben." },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Bin ich nicht frei? Bin ich nicht ein Apostel? Habe ich nicht Jesus, unseren Herrn, gesehen? Seid ihr nicht mein Werk im Herrn?", footnotes: ["15,8f; Gal 5,1.13", "Paulus weiß sich durch die Erscheinung des Auferstandenen vor Damaskus zum Apostel berufen und bevollmächtigt (vgl. Apg 9, 3-6; 1 Kor 15,8-10; Gal 1,15f)."] },
      { number: 2, heading: "Das Beispiel des Apostels: 9,1-27", text: "Wenn ich für andere kein Apostel bin, bin ich es doch für euch. Ihr seid ja im Herrn das Siegel meines Apostelamtes.", footnotes: ["4,15"] },
      { number: 3, text: "Das aber ist meine Rechtfertigung vor denen, die abfällig über mich urteilen:", footnotes: ["4,3f"] },
      { number: 4, text: "Haben wir nicht das Recht, zu essen und zu trinken?", footnotes: ["9,14"] },
      { number: 5, text: "Haben wir nicht das Recht, eine gläubige Frau mitzunehmen, wie die übrigen Apostel und die Brüder des Herrn und wie Kephas?", footnotes: ["Mt 8,14", "Wörtlich: Haben wir nicht das Recht, eine Schwester als Frau mitzunehmen (vgl. auch die Anmerkung zu Mt 12,46f)."] },
      { number: 6, text: "Sollen nur ich und Barnabas auf das Recht verzichten, nicht zu arbeiten?", footnotes: ["Apg 13,2"] },
      { number: 7, text: "Wer leistet denn Kriegsdienst und bezahlt sich selber den Sold? Wer pflanzt einen Weinberg und isst nicht von seinem Ertrag? Oder wer weidet eine Herde und trinkt nicht von der Milch der Herde?", footnotes: ["2 Thess 3,9; 2 Tim 2,4.6"] },
      { number: 8, text: "Sage ich das nur aus menschlicher Einsicht? Sagt das nicht auch das Gesetz?" },
      { number: 9, text: "Im Gesetz des Mose steht doch: Du sollst dem Ochsen zum Dreschen keinen Maulkorb anlegen. Liegt denn Gott etwas an den Ochsen?", footnotes: ["Dtn 25,4; 1 Tim 5,18"] },
      { number: 10, text: "Sagt er das nicht offensichtlich unseretwegen? Ja, unseretwegen wurde es geschrieben. Denn der Pflüger wie der Drescher sollen ihre Arbeit in der Erwartung tun, ihren Teil zu erhalten." },
      { number: 11, text: "Wenn wir für euch die Geistesgaben gesät haben, ist es dann zu viel, wenn wir von euch irdische Gaben ernten?", footnotes: ["Röm 15,27"] },
      { number: 12, text: "Wenn andere an dem, was euch gehört, teilhaben dürfen, dann erst recht wir. Aber wir haben von diesem Recht keinen Gebrauch gemacht. Vielmehr ertragen wir alles, um dem Evangelium Christi kein Hindernis in den Weg zu legen.", footnotes: ["2 Kor 11,7-9; Apg 20,33f", "Paulus verzichtet aus freien Stücken auf das Recht, das ihm als Apostel zusteht. Das wird ihm in Korinth als Zeichen mangelnder apostolischer Vollmacht ausgelegt (vgl. 2 Kor 11,7-12)."] },
      { number: 13, text: "Wisst ihr nicht, dass alle, die im Heiligtum Dienst tun, vom Heiligtum leben, und dass alle, die am Altar Dienst tun, vom Altar ihren Anteil erhalten?", footnotes: ["Num 18,8.21; Dtn 18,1-3"] },
      { number: 14, text: "So hat auch der Herr denen, die das Evangelium verkündigen, geboten, vom Evangelium zu leben.", footnotes: ["Mt 10,10; Gal 6,6"] },
      { number: 15, text: "Ich aber habe all das nicht in Anspruch genommen. Ich schreibe dies auch nicht, damit es in meinem Fall so geschieht. Lieber wollte ich sterben, als dass mir jemand diesen Ruhm entreißt." },
      { number: 16, text: "Wenn ich nämlich das Evangelium verkünde, kann ich mich deswegen nicht rühmen; denn ein Zwang liegt auf mir. Weh mir, wenn ich das Evangelium nicht verkünde!" },
      { number: 17, text: "Wäre es mein freier Entschluss, so erhielte ich Lohn. Wenn es mir aber nicht freisteht, so ist es ein Auftrag, der mir anvertraut wurde." },
      { number: 18, text: "Was ist nun mein Lohn? Dass ich das Evangelium unentgeltlich verkünde und so auf mein Recht verzichte." },
      { number: 19, text: "Da ich also von niemand abhängig war, habe ich mich für alle zum Sklaven gemacht, um möglichst viele zu gewinnen.", footnotes: ["10,33; Mt 20,26f"] },
      { number: 20, text: "Den Juden bin ich ein Jude geworden, um Juden zu gewinnen; denen, die unter dem Gesetz stehen, bin ich, obgleich ich nicht unter dem Gesetz stehe, einer unter dem Gesetz geworden, um die zu gewinnen, die unter dem Gesetz stehen.", footnotes: ["Apg 16,3; 21,20-26; Gal 4,12"] },
      { number: 21, text: "Den Gesetzlosen war ich sozusagen ein Gesetzloser - nicht als ein Gesetzloser vor Gott, sondern gebunden an das Gesetz Christi -, um die Gesetzlosen zu gewinnen.", footnotes: ["Gal 6,2"] },
      { number: 22, text: "Den Schwachen wurde ich ein Schwacher, um die Schwachen zu gewinnen. Allen bin ich alles geworden, um auf jeden Fall einige zu retten.", footnotes: ["Röm 15,1; 2 Kor 11,29"] },
      { number: 23, text: "Alles aber tue ich um des Evangeliums willen, um an seiner Verheißung teilzuhaben.", footnotes: ["an seiner Verheißung, wörtlich: an ihm (nämlich: dem Evangelium) teilzuhaben, d. h. an dem vom Evangelium zugesagten Heil."] },
      { number: 24, text: "Wisst ihr nicht, dass die Läufer im Stadion zwar alle laufen, aber dass nur einer den Siegespreis gewinnt? Lauft so, dass ihr ihn gewinnt." },
      { number: 25, text: "Jeder Wettkämpfer lebt aber völlig enthaltsam; jene tun dies, um einen vergänglichen, wir aber, um einen unvergänglichen Siegeskranz zu gewinnen.", footnotes: ["Phil 3,14; 2 Tim 2,4f; 4,7f; 1 Petr 5,4"] },
      { number: 26, text: "Darum laufe ich nicht wie einer, der ziellos läuft, und kämpfe mit der Faust nicht wie einer, der in die Luft schlägt;" },
      { number: 27, text: "vielmehr züchtige und unterwerfe ich meinen Leib, damit ich nicht anderen predige und selbst verworfen werde.", footnotes: ["Röm 13,14"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Ihr sollt wissen, Brüder, dass unsere Väter alle unter der Wolke waren, alle durch das Meer zogen", footnotes: ["Ex 13,21; 14,22; Ps 78,13f; Röm 1,13"] },
      { number: 2, heading: "Israels Geschichte als Warnung: 10,1-13", text: "und alle auf Mose getauft wurden in der Wolke und im Meer.", footnotes: ["«auf Mose getauft werden» ist hier in Entsprechung zum Getauftwerden «auf Christus» formuliert; es gibt keine derartige jüdische Praxis."] },
      { number: 3, text: "Alle aßen auch die gleiche gottgeschenkte Speise", footnotes: ["Ex 16,4.35; Ps 78,24f", "3f: Paulus geht hier von der Vorstellung aus, dass Christus, der ewige Gottessohn, dem Volk Israel auf seiner Wüstenwanderung als Führer vorauszog und es durch seine göttliche Wundermacht am Leben erhielt. Weil dieses Leben auch mit der Wasserspende aus dem Felsen verbunden war, nennt er Christus den Leben spendenden («pneumatischen») Felsen."] },
      { number: 4, text: "und alle tranken den gleichen gottgeschenkten Trank; denn sie tranken aus dem Leben spendenden Felsen, der mit ihnen zog. Und dieser Fels war Christus.", footnotes: ["Ex 17,6; Num 20,7-11; Ps 78,15f"] },
      { number: 5, text: "Gott aber hatte an den meisten von ihnen kein Gefallen; denn er ließ sie in der Wüste umkommen.", footnotes: ["Num 14,16"] },
      { number: 6, text: "Das aber geschah als warnendes Beispiel für uns: damit wir uns nicht von der Gier nach dem Bösen beherrschen lassen, wie jene sich von der Gier beherrschen ließen.", footnotes: ["Num 11,4.34"] },
      { number: 7, text: "Werdet nicht Götzendiener wie einige von ihnen; denn es steht in der Schrift: Das Volk setzte sich zum Essen und Trinken; dann standen sie auf, um sich zu vergnügen.", footnotes: ["Ex 32,6"] },
      { number: 8, text: "Lasst uns nicht Unzucht treiben, wie einige von ihnen Unzucht trieben. Damals kamen an einem einzigen Tag dreiundzwanzigtausend Menschen um.", footnotes: ["Num 25,1.9"] },
      { number: 9, text: "Wir wollen auch nicht den Herrn auf die Probe stellen, wie es einige von ihnen taten, die dann von Schlangen getötet wurden.", footnotes: ["Num 21,5f"] },
      { number: 10, text: "Murrt auch nicht, wie einige von ihnen murrten; sie wurden vom Verderber umgebracht.", footnotes: ["Ex 16,2f; Num 14,2.36f; 17,6-14"] },
      { number: 11, text: "Das aber geschah an ihnen, damit es uns als Beispiel dient; uns zur Warnung wurde es aufgeschrieben, uns, die das Ende der Zeiten erreicht hat.", footnotes: ["7,29; Röm 15,4; 1 Petr 4,7"] },
      { number: 12, text: "Wer also zu stehen meint, der gebe Acht, dass er nicht fällt.", footnotes: ["Röm 11,20"] },
      { number: 13, text: "Noch ist keine Versuchung über euch gekommen, die den Menschen überfordert. Gott ist treu; er wird nicht zulassen, dass ihr über eure Kraft hinaus versucht werdet. Er wird euch in der Versuchung einen Ausweg schaffen, sodass ihr sie bestehen könnt.", footnotes: ["1,8f; 2 Thess 3,3; Jak 1,13f"] },
      { number: 14, text: "Darum, liebe Brüder, meidet den Götzendienst!", footnotes: ["1 Joh 5,21"] },
      { number: 15, heading: "Über Götzendienst und Götzenopfermahl: 10,14 - 11,1", text: "Ich rede doch zu verständigen Menschen; urteilt selbst über das, was ich sage." },
      { number: 16, text: "Ist der Kelch des Segens, über den wir den Segen sprechen, nicht Teilhabe am Blut Christi? Ist das Brot, das wir brechen, nicht Teilhabe am Leib Christi?", footnotes: ["11,24f"] },
      { number: 17, text: "Ein Brot ist es. Darum sind wir viele ein Leib; denn wir alle haben teil an dem einen Brot.", footnotes: ["Apg 2,42; 1 Kor 12,27; Röm 12,5"] },
      { number: 18, text: "Schaut auf das irdische Israel: Haben die, welche von den Opfern essen, nicht teil am Altar?", footnotes: ["Lev 7,6.15f; Dtn 18,1-4"] },
      { number: 19, text: "Was meine ich damit? Ist denn Götzenopferfleisch wirklich etwas? Oder ist ein Götze wirklich etwas?", footnotes: ["8,4"] },
      { number: 20, text: "Nein, aber was man dort opfert, opfert man nicht Gott, sondern den Dämonen. Ich will jedoch nicht, dass ihr euch mit Dämonen einlasst.", footnotes: ["Ps 106,37"] },
      { number: 21, text: "Ihr könnt nicht den Kelch des Herrn trinken und den Kelch der Dämonen. Ihr könnt nicht Gäste sein am Tisch des Herrn und am Tisch der Dämonen.", footnotes: ["2 Kor 6,15f; Mal 1,7.12"] },
      { number: 22, text: "Oder wollen wir die Eifersucht des Herrn wecken? Sind wir stärker als er?", footnotes: ["Dtn 32,21"] },
      { number: 23, text: "«Alles ist erlaubt» - aber nicht alles nützt. «Alles ist erlaubt» - aber nicht alles baut auf.", footnotes: ["6,12", "Vgl. die Anmerkung zu 6,12."] },
      { number: 24, text: "Denkt dabei nicht an euch selbst, sondern an die anderen.", footnotes: ["Röm 15,2; Phil 2,4"] },
      { number: 25, text: "Alles, was auf dem Fleischmarkt verkauft wird, das esst, ohne aus Gewissenhaftigkeit nachzuforschen.", footnotes: ["Röm 14,2-10"] },
      { number: 26, text: "Denn dem Herrn gehört die Erde und was sie erfüllt.", footnotes: ["Ps 24,1"] },
      { number: 27, text: "Wenn ein Ungläubiger euch einlädt und ihr hingehen möchtet, dann esst, was euch vorgesetzt wird, ohne aus Gewissensgründen nachzuforschen." },
      { number: 28, text: "Wenn euch aber jemand darauf hinweist: Das ist Opferfleisch!, dann esst nicht davon, mit Rücksicht auf den, der euch aufmerksam macht, und auf das Gewissen;", footnotes: ["8,7"] },
      { number: 29, text: "ich meine das Gewissen des anderen, nicht das eigene; denn (an sich gilt): Warum soll meine Freiheit vom Gewissensurteil eines anderen abhängig sein?" },
      { number: 30, text: "Wenn ich in Dankbarkeit mitesse, soll ich dann getadelt werden, dass ich etwas esse, wofür ich Dank sage?", footnotes: ["Röm 14,6; 1 Tim 4,3f"] },
      { number: 31, text: "Ob ihr also esst oder trinkt oder etwas anderes tut: Tut alles zur Verherrlichung Gottes!", footnotes: ["Kol 3,17"] },
      { number: 32, text: "Gebt weder Juden noch Griechen, noch der Kirche Gottes Anlass zu einem Vorwurf!", footnotes: ["Röm 14,13"] },
      { number: 33, text: "Auch ich suche allen in allem entgegenzukommen; ich suche nicht meinen Nutzen, sondern den Nutzen aller, damit sie gerettet werden." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Nehmt mich zum Vorbild, wie ich Christus zum Vorbild nehme.", footnotes: ["4,16; Phil 3,17; 4,9"] },
      { number: 2, text: "Ich lobe euch, dass ihr in allem an mich denkt und an den Überlieferungen festhaltet, wie ich sie euch übergeben habe.", footnotes: ["2 Thess 2,15"] },
      { number: 3, heading: "Über das Verhalten der Frau im Gottesdienst: 11,2-16", text: "Ihr sollt aber wissen, dass Christus das Haupt des Mannes ist, der Mann das Haupt der Frau und Gott das Haupt Christi.", footnotes: ["3,23; Gen 3,16; Eph 5,23"] },
      { number: 4, text: "Wenn ein Mann betet oder prophetisch redet und dabei sein Haupt bedeckt hat, entehrt er sein Haupt." },
      { number: 5, text: "Eine Frau aber entehrt ihr Haupt, wenn sie betet oder prophetisch redet und dabei ihr Haupt nicht verhüllt. Sie unterscheidet sich dann in keiner Weise von einer Geschorenen." },
      { number: 6, text: "Wenn eine Frau kein Kopftuch trägt, soll sie sich doch gleich die Haare abschneiden lassen. Ist es aber für eine Frau eine Schande, sich die Haare abschneiden oder sich kahl scheren zu lassen, dann soll sie sich auch verhüllen." },
      { number: 7, text: "Der Mann darf sein Haupt nicht verhüllen, weil er Abbild und Abglanz Gottes ist; die Frau aber ist der Abglanz des Mannes.", footnotes: ["Gen 1,27"] },
      { number: 8, text: "Denn der Mann stammt nicht von der Frau, sondern die Frau vom Mann.", footnotes: ["Gen 2,22f; 1 Tim 2,13"] },
      { number: 9, text: "Der Mann wurde auch nicht für die Frau geschaffen, sondern die Frau für den Mann.", footnotes: ["Gen 2,18; 6,2"] },
      { number: 10, text: "Deswegen soll die Frau mit Rücksicht auf die Engel das Zeichen ihrer Vollmacht auf dem Kopf tragen.", footnotes: ["Es galt damals unter Juden wie Judenchristen als unanständig, wenn eine Frau ihr Haar offen trug. Deswegen war es Vorschrift für sie, ein Kopftuch zu tragen, wenn sie ausging. Durch die judenchristlichen Missionare der Frühzeit fand dieser Brauch auch Eingang in die heidenchristlichen Gemeinden, wenigstens für den Gottesdienst. Paulus bestand auf dieser Verhüllung umso mehr, als er der Frau hier das geisterfüllte laute Beten und prophetische Reden gestattete (vgl. 11,5). So wurde das Kopftuch auch ein Zeichen ihrer Vollmacht, vor der Gemeinde charismatisch aufzutreten."] },
      { number: 11, text: "Doch im Herrn gibt es weder die Frau ohne den Mann noch den Mann ohne die Frau." },
      { number: 12, text: "Denn wie die Frau vom Mann stammt, so kommt der Mann durch die Frau zur Welt; alles aber stammt von Gott." },
      { number: 13, text: "Urteilt selber! Gehört es sich, dass eine Frau unverhüllt zu Gott betet?" },
      { number: 14, text: "Lehrt euch nicht schon die Natur, dass es für den Mann eine Schande," },
      { number: 15, text: "für die Frau aber eine Ehre ist, lange Haare zu tragen? Denn der Frau ist das Haar als Hülle gegeben." },
      { number: 16, text: "Wenn aber einer meint, er müsse darüber streiten: Wir und auch die Gemeinden Gottes kennen einen solchen Brauch nicht." },
      { number: 17, text: "Wenn ich schon Anweisungen gebe: Das kann ich nicht loben, dass ihr nicht mehr zu eurem Nutzen, sondern zu eurem Schaden zusammenkommt." },
      { number: 18, heading: "Die rechte Feier des Herrenmahls: 11,17-34", text: "Zunächst höre ich, dass es Spaltungen unter euch gibt, wenn ihr als Gemeinde zusammenkommt; zum Teil glaube ich das auch.", footnotes: ["1,10-12; 3,3f"] },
      { number: 19, text: "Denn es muss Parteiungen geben unter euch; nur so wird sichtbar, wer unter euch treu und zuverlässig ist." },
      { number: 20, text: "Was ihr bei euren Zusammenkünften tut, ist keine Feier des Herrenmahls mehr;" },
      { number: 21, text: "denn jeder verzehrt sogleich seine eigenen Speisen, und dann hungert der eine, während der andere schon betrunken ist." },
      { number: 22, text: "Könnt ihr denn nicht zu Hause essen und trinken? Oder verachtet ihr die Kirche Gottes? Wollt ihr jene demütigen, die nichts haben? Was soll ich dazu sagen? Soll ich euch etwa loben? In diesem Fall kann ich euch nicht loben.", footnotes: ["Jak 2,5f"] },
      { number: 23, text: "Denn ich habe vom Herrn empfangen, was ich euch dann überliefert habe: Jesus, der Herr, nahm in der Nacht, in der er ausgeliefert wurde, Brot,", footnotes: ["15,3; Mt 26,26-28; Mk 14,22-24; Lk 22,19f"] },
      { number: 24, text: "sprach das Dankgebet, brach das Brot und sagte: Das ist mein Leib für euch. Tut dies zu meinem Gedächtnis!", footnotes: ["Manche Textzeugen haben, dem späteren liturgischen Gebrauch entsprechend, eine erweiterte Fassung: Das ist mein Leib, der für euch hingegeben wird (oder: gebrochen wird)."] },
      { number: 25, text: "Ebenso nahm er nach dem Mahl den Kelch und sprach: Dieser Kelch ist der Neue Bund in meinem Blut. Tut dies, sooft ihr daraus trinkt, zu meinem Gedächtnis!", footnotes: ["Jer 31,31; Lk 22,20"] },
      { number: 26, text: "Denn sooft ihr von diesem Brot esst und aus dem Kelch trinkt, verkündet ihr den Tod des Herrn, bis er kommt.", footnotes: ["Mt 26,29; Mk 14,25; Lk 22,16-18"] },
      { number: 27, text: "Wer also unwürdig von dem Brot isst und aus dem Kelch des Herrn trinkt, macht sich schuldig am Leib und am Blut des Herrn.", footnotes: ["Paulus meint das in V. 21 und V. 22 erwähnte lieblose Verhalten der Korinther."] },
      { number: 28, text: "Jeder soll sich selbst prüfen; erst dann soll er von dem Brot essen und aus dem Kelch trinken." },
      { number: 29, text: "Denn wer davon isst und trinkt, ohne zu bedenken, dass es der Leib des Herrn ist, der zieht sich das Gericht zu, indem er isst und trinkt." },
      { number: 30, text: "Deswegen sind unter euch viele schwach und krank und nicht wenige sind schon entschlafen." },
      { number: 31, text: "Gingen wir mit uns selbst ins Gericht, dann würden wir nicht gerichtet.", footnotes: ["Hebr 12,5f"] },
      { number: 32, text: "Doch wenn wir jetzt vom Herrn gerichtet werden, dann ist es eine Zurechtweisung, damit wir nicht zusammen mit der Welt verdammt werden." },
      { number: 33, text: "Wenn ihr also zum Mahl zusammenkommt, meine Brüder, wartet aufeinander!" },
      { number: 34, text: "Wer Hunger hat, soll zu Hause essen; sonst wird euch die Zusammenkunft zum Gericht. Weitere Anordnungen werde ich treffen, wenn ich komme.", footnotes: ["16,5"] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Auch über die Gaben des Geistes möchte ich euch nicht in Unkenntnis lassen, meine Brüder.", footnotes: ["14,1"] },
      { number: 2, heading: "Die Geistesgaben und das Leben der Christen: 12,1 - 14,40 | Der eine Geist und die vielen Gaben: 12,1-11", text: "Als ihr noch Heiden wart, zog es euch, wie ihr wisst, mit unwiderstehlicher Gewalt zu den stummen Götzen.", footnotes: ["Gal 4,8"] },
      { number: 3, text: "Darum erkläre ich euch: Keiner, der aus dem Geist Gottes redet, sagt: Jesus sei verflucht! Und keiner kann sagen: Jesus ist der Herr!, wenn er nicht aus dem Heiligen Geist redet.", footnotes: ["1 Joh 4,2f; Röm 10,9"] },
      { number: 4, text: "Es gibt verschiedene Gnadengaben, aber nur den einen Geist.", footnotes: ["Röm 12,6; Eph 4,4"] },
      { number: 5, text: "Es gibt verschiedene Dienste, aber nur den einen Herrn." },
      { number: 6, text: "Es gibt verschiedene Kräfte, die wirken, aber nur den einen Gott: Er bewirkt alles in allen.", footnotes: ["8,6; Eph 4,6"] },
      { number: 7, text: "Jedem aber wird die Offenbarung des Geistes geschenkt, damit sie anderen nützt.", footnotes: ["14,26"] },
      { number: 8, text: "Dem einen wird vom Geist die Gabe geschenkt, Weisheit mitzuteilen, dem andern durch den gleichen Geist die Gabe, Erkenntnis zu vermitteln," },
      { number: 9, text: "dem dritten im gleichen Geist Glaubenskraft, einem andern - immer in dem einen Geist - die Gabe, Krankheiten zu heilen," },
      { number: 10, text: "einem andern Wunderkräfte, einem andern prophetisches Reden, einem andern die Fähigkeit, die Geister zu unterscheiden, wieder einem andern verschiedene Arten von Zungenrede, einem andern schließlich die Gabe, sie zu deuten.", footnotes: ["Das «prophetische Reden», meint ein Reden aufgrund göttlicher Eingebung, das in verständlicher Form die christliche Botschaft auf die Nöte und Aufgaben der Gemeinde anwendet. - Zur «Unterscheidung der Geister» vgl. 2,10-15; 14,29.32; 1 Thess 5,21. - Beim «Zungenreden» handelt es sich um ein meist unverständliches Stammeln zum Lob Gottes, bei dem der Mensch vom Geist Gottes ergriffen ist. Für die Deutung des Zungenredens war eine eigene «Gnadengabe» (griechisch: Charisma) erforderlich (vgl. Apg 2,4; 10,46; 19,6)."] },
      { number: 11, text: "Das alles bewirkt ein und derselbe Geist; einem jeden teilt er seine besondere Gabe zu, wie er will.", footnotes: ["7,7; Röm 12,3; Eph 4,7-12; 1 Petr 4,10"] },
      { number: 12, text: "Denn wie der Leib eine Einheit ist, doch viele Glieder hat, alle Glieder des Leibes aber, obgleich es viele sind, einen einzigen Leib bilden: So ist es auch mit Christus.", footnotes: ["10,17; Röm 12,4f"] },
      { number: 13, heading: "Der eine Leib und die vielen Glieder: 12,12-31a", text: "Durch den einen Geist wurden wir in der Taufe alle in einen einzigen Leib aufgenommen, Juden und Griechen, Sklaven und Freie; und alle wurden wir mit dem einen Geist getränkt.", footnotes: ["Gal 3,28"] },
      { number: 14, text: "Auch der Leib besteht nicht nur aus einem Glied, sondern aus vielen Gliedern." },
      { number: 15, text: "Wenn der Fuß sagt: Ich bin keine Hand, ich gehöre nicht zum Leib!, so gehört er doch zum Leib." },
      { number: 16, text: "Und wenn das Ohr sagt: Ich bin kein Auge, ich gehöre nicht zum Leib!, so gehört es doch zum Leib." },
      { number: 17, text: "Wenn der ganze Leib nur Auge wäre, wo bliebe dann das Gehör? Wenn er nur Gehör wäre, wo bliebe dann der Geruchssinn?" },
      { number: 18, text: "Nun aber hat Gott jedes einzelne Glied so in den Leib eingefügt, wie es seiner Absicht entsprach." },
      { number: 19, text: "Wären alle zusammen nur ein Glied, wo bliebe dann der Leib?" },
      { number: 20, text: "So aber gibt es viele Glieder und doch nur einen Leib." },
      { number: 21, text: "Das Auge kann nicht zur Hand sagen: Ich bin nicht auf dich angewiesen. Der Kopf kann nicht zu den Füßen sagen: Ich brauche euch nicht." },
      { number: 22, text: "Im Gegenteil, gerade die schwächer scheinenden Glieder des Leibes sind unentbehrlich." },
      { number: 23, text: "Denen, die wir für weniger edel ansehen, erweisen wir umso mehr Ehre und unseren weniger anständigen Gliedern begegnen wir mit mehr Anstand," },
      { number: 24, text: "während die anständigen das nicht nötig haben. Gott aber hat den Leib so zusammengefügt, dass er dem geringsten Glied mehr Ehre zukommen ließ," },
      { number: 25, text: "damit im Leib kein Zwiespalt entstehe, sondern alle Glieder einträchtig füreinander sorgen." },
      { number: 26, text: "Wenn darum ein Glied leidet, leiden alle Glieder mit; wenn ein Glied geehrt wird, freuen sich alle anderen mit ihm.", footnotes: ["Röm 12,15"] },
      { number: 27, text: "Ihr aber seid der Leib Christi und jeder Einzelne ist ein Glied an ihm.", footnotes: ["Röm 12,5; Eph 5,30"] },
      { number: 28, text: "So hat Gott in der Kirche die einen als Apostel eingesetzt, die andern als Propheten, die dritten als Lehrer; ferner verlieh er die Kraft, Wunder zu tun, sodann die Gaben, Krankheiten zu heilen, zu helfen, zu leiten, endlich die verschiedenen Arten von Zungenrede.", footnotes: ["12,8-10; Eph 4,11f"] },
      { number: 29, text: "Sind etwa alle Apostel, alle Propheten, alle Lehrer? Haben alle die Kraft, Wunder zu tun?" },
      { number: 30, text: "Besitzen alle die Gabe, Krankheiten zu heilen? Reden alle in Zungen? Können alle solches Reden auslegen?" },
      { number: 31, text: "Strebt aber nach den höheren Gnadengaben!", footnotes: ["14,1"] },
      { number: 31, text: "Ich zeige euch jetzt noch einen anderen Weg, einen, der alles übersteigt:" },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Wenn ich in den Sprachen der Menschen und Engel redete, / hätte aber die Liebe nicht, / wäre ich dröhnendes Erz oder eine lärmende Pauke.", footnotes: ["14,6-8"] },
      { number: 2, text: "Und wenn ich prophetisch reden könnte / und alle Geheimnisse wüsste / und alle Erkenntnis hätte; / wenn ich alle Glaubenskraft besäße / und Berge damit versetzen könnte, / hätte aber die Liebe nicht, / wäre ich nichts.", footnotes: ["14,3.5; Mt 7,22; 17,20; 21,22; Mk 11,23"] },
      { number: 3, text: "Und wenn ich meine ganze Habe verschenkte / und wenn ich meinen Leib dem Feuer übergäbe, / hätte aber die Liebe nicht, / nützte es mir nichts.", footnotes: ["Mt 19,21"] },
      { number: 4, text: "Die Liebe ist langmütig, / die Liebe ist gütig. / Sie ereifert sich nicht, / sie prahlt nicht, / sie bläht sich nicht auf.", footnotes: ["Röm 13,8-10"] },
      { number: 5, text: "Sie handelt nicht ungehörig, / sucht nicht ihren Vorteil, / lässt sich nicht zum Zorn reizen, / trägt das Böse nicht nach." },
      { number: 6, text: "Sie freut sich nicht über das Unrecht, / sondern freut sich an der Wahrheit.", footnotes: ["Röm 12,9"] },
      { number: 7, text: "Sie erträgt alles, / glaubt alles, / hofft alles, / hält allem stand.", footnotes: ["Spr 10,12"] },
      { number: 8, text: "Die Liebe hört niemals auf. / Prophetisches Reden hat ein Ende, / Zungenrede verstummt, / Erkenntnis vergeht." },
      { number: 9, text: "Denn Stückwerk ist unser Erkennen, / Stückwerk unser prophetisches Reden;" },
      { number: 10, text: "wenn aber das Vollendete kommt, / vergeht alles Stückwerk." },
      { number: 11, text: "Als ich ein Kind war, / redete ich wie ein Kind, / dachte wie ein Kind / und urteilte wie ein Kind. Als ich ein Mann wurde, / legte ich ab, was Kind an mir war." },
      { number: 12, text: "Jetzt schauen wir in einen Spiegel / und sehen nur rätselhafte Umrisse, / dann aber schauen wir von Angesicht zu Angesicht. Jetzt erkenne ich unvollkommen, / dann aber werde ich durch und durch erkennen, / so wie ich auch durch und durch erkannt worden bin." },
      { number: 13, text: "Für jetzt bleiben Glaube, Hoffnung, Liebe, diese drei; / doch am größten unter ihnen ist die Liebe.", footnotes: ["Hebr 10,22-24; Kol 3,14"] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Jagt der Liebe nach! Strebt aber auch nach den Geistesgaben, vor allem nach der prophetischen Rede!", footnotes: ["12,10"] },
      { number: 2, heading: "Über die Charismen der Prophetie und der Zungenrede: 14,1-25", text: "Denn wer in Zungen redet, redet nicht zu Menschen, sondern zu Gott; keiner versteht ihn: Im Geist redet er geheimnisvolle Dinge." },
      { number: 3, text: "Wer aber prophetisch redet, redet zu Menschen: Er baut auf, ermutigt, spendet Trost.", footnotes: ["Vgl. die Anmerkung zu 8,1."] },
      { number: 4, text: "Wer in Zungen redet, erbaut sich selbst; wer aber prophetisch redet, baut die Gemeinde auf.", footnotes: ["Vgl. die Anmerkung zu 12,10."] },
      { number: 5, text: "Ich wünschte, ihr alle würdet in Zungen reden, weit mehr aber, ihr würdet prophetisch reden. Der Prophet steht höher als der, der in Zungen redet, es sei denn, dieser legt sein Reden aus; dann baut auch er die Gemeinde auf.", footnotes: ["Num 11,29"] },
      { number: 6, text: "Was nützt es euch, Brüder, wenn ich komme und in Zungen vor euch rede, euch aber keine Offenbarung, keine Erkenntnis, keine Weissagung, keine Lehre bringe?" },
      { number: 7, text: "Wenn leblose Musikinstrumente, eine Flöte oder eine Harfe, nicht deutlich unterschiedene Töne hervorbringen, wie soll man dann erkennen, was auf der Flöte oder auf der Harfe gespielt wird?" },
      { number: 8, text: "Und wenn die Trompete unklare Töne hervorbringt, wer wird dann zu den Waffen greifen?" },
      { number: 9, text: "So ist es auch mit euch, wenn ihr in Zungen redet, aber kein verständliches Wort hervorbringt. Wer soll dann das Gesprochene verstehen? Ihr redet nur in den Wind." },
      { number: 10, text: "Es gibt wer weiß wie viele Sprachen in der Welt und nichts ist ohne Sprache." },
      { number: 11, text: "Wenn ich nun den Sinn der Laute nicht kenne, bin ich für den Sprecher ein Fremder, wie der Sprecher für mich." },
      { number: 12, text: "So ist es auch mit euch. Da ihr nach Geistesgaben strebt, gebt euch Mühe, dass ihr damit vor allem zum Aufbau der Gemeinde beitragt." },
      { number: 13, text: "Deswegen soll einer, der in Zungen redet, darum beten, dass er es auch auslegen kann." },
      { number: 14, text: "Denn wenn ich nur in Zungen bete, betet zwar mein Geist, aber mein Verstand bleibt unfruchtbar." },
      { number: 15, text: "Was folgt daraus? Ich will nicht nur im Geist beten, sondern auch mit dem Verstand. Ich will nicht nur im Geist Gott preisen, sondern auch mit dem Verstand." },
      { number: 16, text: "Wenn du nur im Geist den Lobpreis sprichst und ein Unkundiger anwesend ist, so kann er zu deinem Dankgebet das Amen nicht sagen; denn er versteht nicht, was du sagst.", footnotes: ["2 Kor 1,20; 1 Chr 16,36; Neh 8,6"] },
      { number: 17, text: "Dein Dankgebet mag noch so gut sein, der andere hat keinen Nutzen davon." },
      { number: 18, text: "Ich danke Gott, dass ich mehr als ihr alle in Zungen rede." },
      { number: 19, text: "Doch vor der Gemeinde will ich lieber fünf Worte mit Verstand reden, um auch andere zu unterweisen, als zehntausend Worte in Zungen stammeln." },
      { number: 20, text: "Seid doch nicht Kinder an Einsicht, Brüder! Seid Unmündige an Bosheit, an Einsicht aber seid reife Menschen!", footnotes: ["Röm 16,19; Eph 4,14"] },
      { number: 21, text: "Im Gesetz steht: Durch Leute, die anders und in anderen Sprachen reden, werde ich zu diesem Volk sprechen; aber auch so werden sie nicht auf mich hören, spricht der Herr.", footnotes: ["Jes 28,11f"] },
      { number: 22, text: "So ist Zungenreden ein Zeichen nicht für die Gläubigen, sondern für die Ungläubigen, prophetisches Reden aber ein Zeichen nicht für die Ungläubigen, sondern für die Glaubenden." },
      { number: 23, text: "Wenn also die ganze Gemeinde sich versammelt und alle in Zungen reden und es kommen Unkundige oder Ungläubige hinzu, werden sie dann nicht sagen: Ihr seid verrückt!", footnotes: ["Apg 2,12f"] },
      { number: 24, text: "Wenn aber alle prophetisch reden und ein Ungläubiger oder Unkundiger kommt herein, dann wird ihm von allen ins Gewissen geredet und er fühlt sich von allen ins Verhör genommen;" },
      { number: 25, text: "was in seinem Herzen verborgen ist, wird aufgedeckt. Und so wird er sich niederwerfen, Gott anbeten und ausrufen: Wahrhaftig, Gott ist bei euch!", footnotes: ["Jes 45,14; Sach 8,23"] },
      { number: 26, text: "Was soll also geschehen, Brüder? Wenn ihr zusammenkommt, trägt jeder etwas bei: einer einen Psalm, ein anderer eine Lehre, der dritte eine Offenbarung; einer redet in Zungen und ein anderer deutet es. Alles geschehe so, dass es aufbaut.", footnotes: ["12,8-10; Eph 4,12"] },
      { number: 27, heading: "Die rechte Einheit und Ordnung im Gottesdienst: 14,26-40", text: "Wenn man in Zungen reden will, so sollen es nur zwei tun, höchstens drei, und zwar einer nach dem andern; dann soll einer es auslegen." },
      { number: 28, text: "Wenn aber niemand es auslegen kann, soll auch keiner vor der Gemeinde so reden. Er soll es für sich selber tun und vor Gott." },
      { number: 29, text: "Auch zwei oder drei Propheten sollen zu Wort kommen; die anderen sollen urteilen.", footnotes: ["1 Thess 5,19-21"] },
      { number: 30, text: "Wenn aber noch einem andern Anwesenden eine Offenbarung zuteil wird, soll der erste schweigen;" },
      { number: 31, text: "einer nach dem andern könnt ihr alle prophetisch reden. So lernen alle etwas und alle werden ermutigt." },
      { number: 32, text: "Die Äußerung prophetischer Eingebungen ist nämlich dem Willen der Propheten unterworfen.", footnotes: ["Wörtlich: Die Geister der Propheten sind den Propheten unterworfen."] },
      { number: 33, text: "Denn Gott ist nicht ein Gott der Unordnung, sondern ein Gott des Friedens. Wie es in allen Gemeinden der Heiligen üblich ist,", footnotes: ["Röm 15,33"] },
      { number: 34, text: "sollen die Frauen in der Versammlung schweigen; es ist ihnen nicht gestattet zu reden. Sie sollen sich unterordnen, wie auch das Gesetz es fordert.", footnotes: ["11,16; Gen 3,16; 1 Tim 2,11f", "Diese Anordnung steht in Spannung zu dem in 11,5 Gesagten; jedoch in Einklang mit 1 Tim 2,11f (auch VV. 13-15). Vgl. auch die Anmerkung zu 11,10."] },
      { number: 35, text: "Wenn sie etwas wissen wollen, dann sollen sie zu Hause ihre Männer fragen; denn es gehört sich nicht für eine Frau, vor der Gemeinde zu reden.", footnotes: ["Eph 5,22"] },
      { number: 36, text: "Ist etwa das Gotteswort von euch ausgegangen? Ist es etwa nur zu euch gekommen?" },
      { number: 37, text: "Wenn einer meint, Prophet zu sein oder geisterfüllt, soll er in dem, was ich euch schreibe, ein Gebot des Herrn erkennen." },
      { number: 38, text: "Wer das nicht anerkennt, wird nicht anerkannt." },
      { number: 39, text: "Strebt also nach der Prophetengabe, meine Brüder, und hindert niemand daran, in Zungen zu reden.", footnotes: ["14,1.5"] },
      { number: 40, text: "Doch alles soll in Anstand und Ordnung geschehen.", footnotes: ["Kol 2,5"] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Ich erinnere euch, Brüder, an das Evangelium, das ich euch verkündet habe. Ihr habt es angenommen; es ist der Grund, auf dem ihr steht.", footnotes: ["Gal 1,11", "1-5: Paulus weist hier auf die urchristliche Glaubensformel hin, die er selbst übernommen hat und die in knapper Form all das enthält, was er in seinen Briefen «das Evangelium» nennt, die Botschaft vom Heil. Diese Glaubensformel gilt es auch dem Wortlaut nach festzuhalten, damit das Glaubensgut unverfälscht bewahrt wird (vgl. 15,3.11)."] },
      { number: 2, heading: "Die Auferweckung Christi und der Christen: 15,1-58 | Die Auferweckung Christi und das Heil: 15,1-34", text: "Durch dieses Evangelium werdet ihr gerettet, wenn ihr an dem Wortlaut festhaltet, den ich euch verkündet habe. Oder habt ihr den Glauben vielleicht unüberlegt angenommen?" },
      { number: 3, text: "Denn vor allem habe ich euch überliefert, was auch ich empfangen habe: Christus ist für unsere Sünden gestorben, / gemäß der Schrift,", footnotes: ["11,23; Jes 53,4f; Röm 4,25; 1 Petr 2,24", ""] },
      { number: 4, text: "und ist begraben worden. / Er ist am dritten Tag auferweckt worden, / gemäß der Schrift,", footnotes: ["Hos 6,2"] },
      { number: 5, text: "und erschien dem Kephas, dann den Zwölf.", footnotes: ["Lk 24,34.36"] },
      { number: 6, text: "Danach erschien er mehr als fünfhundert Brüdern zugleich; die meisten von ihnen sind noch am Leben, einige sind entschlafen." },
      { number: 7, text: "Danach erschien er dem Jakobus, dann allen Aposteln." },
      { number: 8, text: "Als Letztem von allen erschien er auch mir, dem Unerwarteten, der «Missgeburt».", footnotes: ["9,1; Apg 9,3-5"] },
      { number: 9, text: "Denn ich bin der geringste von den Aposteln; ich bin nicht wert, Apostel genannt zu werden, weil ich die Kirche Gottes verfolgt habe.", footnotes: ["Apg 8,1-3; 9,1-3; Gal 1,13; 1 Tim 1,15; Eph 3,8"] },
      { number: 10, text: "Doch durch Gottes Gnade bin ich, was ich bin, und sein gnädiges Handeln an mir ist nicht ohne Wirkung geblieben. Mehr als sie alle habe ich mich abgemüht - nicht ich, sondern die Gnade Gottes zusammen mit mir.", footnotes: ["3,10; 2 Kor 11,23; 1 Tim 1,14"] },
      { number: 11, text: "Ob nun ich verkündige oder die anderen: das ist unsere Botschaft, und das ist der Glaube, den ihr angenommen habt.", footnotes: ["3,5"] },
      { number: 12, text: "Wenn aber verkündigt wird, dass Christus von den Toten auferweckt worden ist, wie können dann einige von euch sagen: Eine Auferstehung der Toten gibt es nicht?", footnotes: ["2 Tim 2,18", "12-14: Anstelle der in den VV. 12-14 dreimal begegnenden Übersetzung: «ist (nicht) auferweckt worden» kann auch übersetzt werden: «ist (nicht) auferstanden». Die erste Fassung ist deswegen vorzuziehen, weil in Kap. 15 mehrmals das Handeln Gottes an dem toten Jesus wie auch an den verstorbenen Gläubigen hervorgehoben wird (vgl. 15,15.51f)."] },
      { number: 13, text: "Wenn es keine Auferstehung der Toten gibt, ist auch Christus nicht auferweckt worden." },
      { number: 14, text: "Ist aber Christus nicht auferweckt worden, dann ist unsere Verkündigung leer und euer Glaube sinnlos." },
      { number: 15, text: "Wir werden dann auch als falsche Zeugen Gottes entlarvt, weil wir im Widerspruch zu Gott das Zeugnis abgelegt haben: Er hat Christus auferweckt. Er hat ihn eben nicht auferweckt, wenn Tote nicht auferweckt werden." },
      { number: 16, text: "Denn wenn Tote nicht auferweckt werden, ist auch Christus nicht auferweckt worden." },
      { number: 17, text: "Wenn aber Christus nicht auferweckt worden ist, dann ist euer Glaube nutzlos und ihr seid immer noch in euren Sünden;" },
      { number: 18, text: "und auch die in Christus Entschlafenen sind dann verloren.", footnotes: ["1 Thess 4,14"] },
      { number: 19, text: "Wenn wir unsere Hoffnung nur in diesem Leben auf Christus gesetzt haben, sind wir erbärmlicher daran als alle anderen Menschen." },
      { number: 20, text: "Nun aber ist Christus von den Toten auferweckt worden als der Erste der Entschlafenen.", footnotes: ["Apg 26,23; Kol 1,18"] },
      { number: 21, text: "Da nämlich durch einen Menschen der Tod gekommen ist, kommt durch einen Menschen auch die Auferstehung der Toten.", footnotes: ["Gen 3,17-19; Röm 5,12.18"] },
      { number: 22, text: "Denn wie in Adam alle sterben, so werden in Christus alle lebendig gemacht werden." },
      { number: 23, text: "Es gibt aber eine bestimmte Reihenfolge: Erster ist Christus; dann folgen, wenn Christus kommt, alle, die zu ihm gehören.", footnotes: ["1 Thess 4,15f"] },
      { number: 24, text: "Danach kommt das Ende, wenn er jede Macht, Gewalt und Kraft vernichtet hat und seine Herrschaft Gott, dem Vater, übergibt." },
      { number: 25, text: "Denn er muss herrschen, bis Gott ihm alle Feinde unter die Füße gelegt hat.", footnotes: ["Ps 110,1"] },
      { number: 26, text: "Der letzte Feind, der entmachtet wird, ist der Tod.", footnotes: ["Offb 20,14; 21,4"] },
      { number: 27, text: "Sonst hätte er ihm nicht alles zu Füßen gelegt. Wenn es aber heißt, alles sei unterworfen, ist offenbar der ausgenommen, der ihm alles unterwirft.", footnotes: ["Ps 8,7; Hebr 2,8"] },
      { number: 28, text: "Wenn ihm dann alles unterworfen ist, wird auch er, der Sohn, sich dem unterwerfen, der ihm alles unterworfen hat, damit Gott herrscht über alles und in allem.", footnotes: ["8,6; Röm 11,36", "Wörtlich: damit Gott alles in allem sei."] },
      { number: 29, text: "Wie kämen sonst einige dazu, sich für die Toten taufen zu lassen? Wenn Tote gar nicht auferweckt werden, warum lässt man sich dann taufen für sie?", footnotes: ["Anspielung auf die in Korinth gelegentlich vollzogene stellvertretende Taufe zugunsten ungetauft Verstorbener."] },
      { number: 30, text: "Warum setzen dann auch wir uns stündlich der Gefahr aus?", footnotes: ["Röm 8,36"] },
      { number: 31, text: "Täglich sehe ich dem Tod ins Auge, so wahr ihr, Brüder, mein Ruhm seid, den ich in Christus Jesus, unserem Herrn, empfangen habe.", footnotes: ["2 Kor 4,10f"] },
      { number: 32, text: "Was habe ich dann davon, dass ich in Ephesus, wie man so sagt, mit wilden Tieren gekämpft habe? Wenn Tote nicht auferweckt werden, dann lasst uns essen und trinken; denn morgen sind wir tot.", footnotes: ["Jes 22,13; Apg 19,29f; 20,3; 2 Kor 1,8"] },
      { number: 33, text: "Lasst euch nicht irreführen! Schlechter Umgang verdirbt gute Sitten.", footnotes: ["Schlechter Umgang . . .: Geflügeltes Wort, das auf den griechischen Dichter Menander (4. Jh. v. Chr.) zurückgeht."] },
      { number: 34, text: "Werdet nüchtern, wie es sich gehört, und sündigt nicht! Einige Leute wissen nichts von Gott; ich sage das, damit ihr euch schämt.", footnotes: ["6,5; 1 Thess 5,6-8"] },
      { number: 35, text: "Nun könnte einer fragen: Wie werden die Toten auferweckt, was für einen Leib werden sie haben?" },
      { number: 36, heading: "Die Vollendung des Heils bei der Auferstehung: 15,35-58", text: "Was für eine törichte Frage! Auch das, was du säst, wird nicht lebendig, wenn es nicht stirbt." },
      { number: 37, text: "Und was du säst, hat noch nicht die Gestalt, die entstehen wird; es ist nur ein nacktes Samenkorn, zum Beispiel ein Weizenkorn oder ein anderes.", footnotes: ["Joh 12,24"] },
      { number: 38, text: "Gott gibt ihm die Gestalt, die er vorgesehen hat, jedem Samen eine andere.", footnotes: ["Gen 1,11"] },
      { number: 39, text: "Auch die Lebewesen haben nicht alle die gleiche Gestalt. Die Gestalt der Menschen ist anders als die der Haustiere, die Gestalt der Vögel anders als die der Fische." },
      { number: 40, text: "Auch gibt es Himmelskörper und irdische Körper. Die Schönheit der Himmelskörper ist anders als die der irdischen Körper." },
      { number: 41, text: "Der Glanz der Sonne ist anders als der Glanz des Mondes, anders als der Glanz der Sterne; denn auch die Gestirne unterscheiden sich durch ihren Glanz." },
      { number: 42, text: "So ist es auch mit der Auferstehung der Toten. Was gesät wird, ist verweslich, was auferweckt wird, unverweslich." },
      { number: 43, text: "Was gesät wird, ist armselig, was auferweckt wird, herrlich. Was gesät wird, ist schwach, was auferweckt wird, ist stark.", footnotes: ["Phil 3,20f; Kol 3,4"] },
      { number: 44, text: "Gesät wird ein irdischer Leib, auferweckt ein überirdischer Leib. Wenn es einen irdischen Leib gibt, gibt es auch einen überirdischen.", footnotes: ["ein überirdischer Leib, wörtlich: ein pneumatischer («geistlicher») Leib."] },
      { number: 45, text: "So steht es auch in der Schrift: Adam, der Erste Mensch, wurde ein irdisches Lebewesen. Der Letzte Adam wurde lebendig machender Geist.", footnotes: ["Gen 2,7", "Wörtlich: Adam, der Erste Mensch, wurde ein lebendiges Wesen (vgl. Gen 2,7; 3,19)."] },
      { number: 46, text: "Aber zuerst kommt nicht das Überirdische; zuerst kommt das Irdische, dann das Überirdische." },
      { number: 47, text: "Der Erste Mensch stammt von der Erde und ist Erde; der Zweite Mensch stammt vom Himmel.", footnotes: ["Gen 2,7"] },
      { number: 48, text: "Wie der von der Erde irdisch war, so sind es auch seine Nachfahren. Und wie der vom Himmel himmlisch ist, so sind es auch seine Nachfahren.", footnotes: ["Wörtlich: Wie der Irdische, so auch die Irdischen, und wie der Himmlische, so auch die Himmlischen."] },
      { number: 49, text: "Wie wir nach dem Bild des Irdischen gestaltet wurden, so werden wir auch nach dem Bild des Himmlischen gestaltet werden.", footnotes: ["Gen 5,3; Röm 8,29"] },
      { number: 50, text: "Damit will ich sagen, Brüder: Fleisch und Blut können das Reich Gottes nicht erben; das Vergängliche erbt nicht das Unvergängliche.", footnotes: ["Joh 3,5f"] },
      { number: 51, text: "Seht, ich enthülle euch ein Geheimnis: Wir werden nicht alle entschlafen, aber wir werden alle verwandelt werden -", footnotes: ["1 Thess 4,15-17", "Die Erwartung, bei der Wiederkunft Christi noch am Leben zu sein, drückt Paulus auch 1 Thess 4,15.17 aus. Ohne einen Umwandlungsprozess ist aber Unsterblichkeit nicht zu erlangen."] },
      { number: 52, text: "plötzlich, in einem Augenblick, beim letzten Posaunenschall. Die Posaune wird erschallen, die Toten werden zur Unvergänglichkeit auferweckt, wir aber werden verwandelt werden.", footnotes: ["Mt 24,31"] },
      { number: 53, text: "Denn dieses Vergängliche muss sich mit Unvergänglichkeit bekleiden und dieses Sterbliche mit Unsterblichkeit.", footnotes: ["2 Kor 5,4"] },
      { number: 54, text: "Wenn sich aber dieses Vergängliche mit Unvergänglichkeit bekleidet und dieses Sterbliche mit Unsterblichkeit, dann erfüllt sich das Wort der Schrift: Verschlungen ist der Tod vom Sieg.", footnotes: ["Jes 25,8"] },
      { number: 55, text: "Tod, wo ist dein Sieg? / Tod, wo ist dein Stachel?", footnotes: ["Hos 13,14 G"] },
      { number: 56, text: "Der Stachel des Todes aber ist die Sünde, die Kraft der Sünde ist das Gesetz.", footnotes: ["Röm 5,12f; 7,7-9.13"] },
      { number: 57, text: "Gott aber sei Dank, der uns den Sieg geschenkt hat durch Jesus Christus, unseren Herrn.", footnotes: ["Röm 7,25"] },
      { number: 58, text: "Daher, geliebte Brüder, seid standhaft und unerschütterlich, nehmt immer eifriger am Werk des Herrn teil und denkt daran, dass im Herrn eure Mühe nicht vergeblich ist.", footnotes: ["16,13"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Was die Geldsammlung für die Heiligen angeht, sollt auch ihr euch an das halten, was ich für die Gemeinden Galatiens angeordnet habe.", footnotes: ["Apg 11,29; Röm 15,26; 2 Kor 8,4; 9,1; Gal 2,10", "Von der Kollekte für die Gemeinde von Jerusalem ist auch die Rede in Röm 15,25-28; 2 Kor 8 - 9; Gal 2,10; vgl. Apg 11,27-30."] },
      { number: 2, heading: "Der Schluss des Briefes: 16,1-24 | Die Sammlung für Jerusalem: 16,1-4", text: "Jeder soll immer am ersten Tag der Woche etwas zurücklegen und so zusammensparen, was er kann. Dann sind keine Sammlungen mehr nötig, wenn ich komme.", footnotes: ["Apg 20,7; Offb 1,10"] },
      { number: 3, text: "Nach meiner Ankunft werde ich eure Vertrauensleute mit Briefen nach Jerusalem schicken, damit sie eure Liebesgabe überbringen.", footnotes: ["2 Kor 8,16-19"] },
      { number: 4, text: "Ist es der Mühe wert, dass ich selbst hinreise, dann sollen sie mit mir reisen." },
      { number: 5, text: "Ich werde zu euch kommen, wenn ich durch Mazedonien gereist bin. In Mazedonien will ich nämlich nicht bleiben,", footnotes: ["Apg 19,21; 20,2f; 2 Kor 1,16"] },
      { number: 6, heading: "Die Pläne des Apostels: 16,5-12", text: "aber, wenn es möglich ist, bei euch, vielleicht sogar den ganzen Winter. Wenn ich dann weiterreise, könnt ihr mir das Geleit geben." },
      { number: 7, text: "Ich möchte euch diesmal nicht nur auf der Durchreise sehen; ich hoffe, einige Zeit bei euch bleiben zu können, wenn der Herr es zulässt." },
      { number: 8, text: "In Ephesus will ich bis Pfingsten bleiben.", footnotes: ["Apg 19,1.10"] },
      { number: 9, text: "Denn weit und wirksam ist mir hier eine Tür geöffnet worden; doch auch an Gegnern fehlt es nicht.", footnotes: ["Apg 14,27"] },
      { number: 10, text: "Wenn Timotheus kommt, achtet darauf, dass ihr ihn nicht entmutigt; denn er arbeitet im Dienst des Herrn wie ich.", footnotes: ["4,17; Phil 2,19f"] },
      { number: 11, text: "Keiner soll ihn gering schätzen. Verabschiedet ihn dann in Frieden, damit er zu mir zurückkehrt; ich warte auf ihn mit den Brüdern.", footnotes: ["1 Tim 4,12"] },
      { number: 12, text: "Was den Bruder Apollos angeht, so habe ich ihn dringend gebeten, er möge mit den Brüdern zu euch reisen, aber er wollte auf keinen Fall jetzt gehen. Er wird kommen, wenn er eine günstige Gelegenheit findet.", footnotes: ["3,5f"] },
      { number: 13, text: "Seid wachsam, steht fest im Glauben, seid mutig, seid stark!", footnotes: ["15,58; Eph 6,10; Ps 31,25 G; Jos 1,7"] },
      { number: 14, heading: "Letzte Mahnungen und Grüße: 16,13-24", text: "Alles, was ihr tut, geschehe in Liebe.", footnotes: ["Kol 3,14"] },
      { number: 15, text: "Ich habe noch eine Bitte, Brüder. Ihr kennt das Haus des Stephanas: Sie sind die erste Frucht Achaias und haben sich in den Dienst der Heiligen gestellt.", footnotes: ["1,16; Röm 16,5"] },
      { number: 16, text: "Ordnet euch ihnen unter, ebenso ihren Helfern und Mitarbeitern!" },
      { number: 17, text: "Es freut mich, dass Stephanas, Fortunatus und Achaikus zu mir gekommen sind; sie sind mir ein Ersatz für euch, da ihr nicht hier sein könnt.", footnotes: ["Die Genannten sind vermutlich die Überbringer des in 7,1 erwähnten Briefs der Gemeinde von Korinth an Paulus; vgl. die Einleitung zum 1. Korintherbrief."] },
      { number: 18, text: "Sie haben mich und euch erfreut und aufgerichtet. Verweigert solchen Männern eure Anerkennung nicht!", footnotes: ["1 Thess 5,12f"] },
      { number: 19, text: "Es grüßen euch die Gemeinden in der Provinz Asien. Aquila und Priska und ihre Hausgemeinde senden euch viele Grüße im Herrn.", footnotes: ["Offb 1,4.11; Apg 18,2", "Zu Aquila und Priska vgl. Apg 18,2 und Röm 16,3f."] },
      { number: 20, text: "Es grüßen euch alle Brüder. Grüßt einander mit dem heiligen Kuss!", footnotes: ["Röm 16,16"] },
      { number: 21, text: "Den Gruß schreibe ich, Paulus, eigenhändig.", footnotes: ["2 Thess 3,17; Kol 4,18"] },
      { number: 22, text: "Wer den Herrn nicht liebt, sei verflucht! Marána tha - Unser Herr, komm!", footnotes: ["Gal 1,8f", "Gebetsruf aus dem Gottesdienst der Urgemeinde, die auf die Wiederkunft Christi wartet (vgl. Offb 22,20)."] },
      { number: 23, text: "Die Gnade Jesu, des Herrn, sei mit euch!", footnotes: ["Röm 16,24"] },
      { number: 24, text: "Meine Liebe ist mit euch allen in Christus Jesus." },
    ] },
  ],
};
