import { Book } from "@/lib/types";

export const _2corinthians: Book = {
  id: "2corinthians", name: "2. Korinther", shortName: "2.Kor", testament: "new",
  introduction: `Seit der Abfassung des 1. Korintherbriefs nahm das Verhältnis des Apostels Paulus zur korinthischen Gemeinde einen wechselvollen Verlauf. Gegner drangen in die Gemeinde ein, die die Autorität des Apostels untergruben. Dieser sah sich veranlasst, von Ephesus aus einen Besuch in Korinth zu machen (2,5; 7,12). Bei dieser Gelegenheit kam es zu einem schweren Zwischenfall, der für Paulus eine Demütigung bedeutete. Bei seiner Abreise stellte er einen baldigen Besuch in Aussicht (1,15f), schrieb aber dann stattdessen von Ephesus aus in »großer Bedrängnis und Herzensnot« einen Brief (2,4). Titus überbrachte diesen der Gemeinde; währenddessen wartete Paulus mit Spannung das Ergebnis der Mission seines Schülers ab und reiste ihm schließlich über Troas nach Mazedonien entgegen (2,13). Dort erhielt er von Titus gute Nachricht aus Korinth (7,6-16), die die Wiederversöhnung mit der Gemeinde einleitete. Der 2. Korintherbrief ist von dieser wechselvollen Geschichte bestimmt. Im Wesentlichen geht es um drei Themen: Zuerst spricht Paulus von seinem Apostelamt, indem er die Gemeinde als sein apostolisches Werk darstellt (Kap. 1 - 7); zweitens ruft er die Gemeinde auf, sich an der Kollekte für die Armen in Jerusalem zu beteiligen (Kap. 8 - 9), und drittens setzt er sich in leidenschaftlicher Form mit seinen Gegnern auseinander, wobei er uns wichtige Einblicke in sein von Mühe und Kampf erfülltes Apostelleben gewährt (Kap. 10 - 13). Von den Briefen des Paulus gibt der 2. Korintherbrief die bedeutsamsten Aufschlüsse über sein apostolisches Selbstverständnis. Außerdem enthält er wichtige Aussagen über die Beziehung von Amt und Gemeinde.`,
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Paulus, durch Gottes Willen Apostel Christi Jesu, und der Bruder Timotheus an die Kirche Gottes, die in Korinth ist, und an alle Heiligen in ganz Achaia.", heading: "Anschrift und Gruß: 1,1-2", footnotes: ["1 Kor 1,1f; Phil 1,1"] },
      { number: 2, text: "Gnade sei mit euch und Friede von Gott, unserem Vater, und dem Herrn Jesus Christus." },
      { number: 3, text: "Gepriesen sei der Gott und Vater Jesu Christi, unseres Herrn, der Vater des Erbarmens und der Gott allen Trostes.", heading: "Von Leiden und Trost des Apostels: 1,3-11", footnotes: ["Eph 1,3; 1 Petr 1,3; Röm 15,5"] },
      { number: 4, text: "Er tröstet uns in all unserer Not, damit auch wir die Kraft haben, alle zu trösten, die in Not sind, durch den Trost, mit dem auch wir von Gott getröstet werden.", footnotes: ["7,6"] },
      { number: 5, text: "Wie uns nämlich die Leiden Christi überreich zuteil geworden sind, so wird uns durch Christus auch überreicher Trost zuteil.", footnotes: ["4,10f; Kol 1,24"] },
      { number: 6, text: "Sind wir aber in Not, so ist es zu eurem Trost und Heil, und werden wir getröstet, so geschieht auch das zu eurem Trost; er wird wirksam, wenn ihr geduldig die gleichen Leiden ertragt, die auch wir ertragen.", footnotes: ["4,12.15"] },
      { number: 7, text: "Unsere Hoffnung für euch ist unerschütterlich; wir sind sicher, dass ihr mit uns nicht nur an den Leiden teilhabt, sondern auch am Trost." },
      { number: 8, text: "Wir wollen euch die Not nicht verschweigen, Brüder, die in der Provinz Asien über uns kam und uns über alles Maß bedrückte; unsere Kraft war erschöpft, so sehr, dass wir am Leben verzweifelten.", footnotes: ["Apg 19,23; 1 Kor 15,32", "Bezieht sich vermutlich auf eine Gefangenschaft in Ephesus (vgl. 1 Kor 15,32; Apg 19,23-40; 2 Kor 11,23-33)."] },
      { number: 9, text: "Aber wir haben unser Todesurteil hingenommen, weil wir unser Vertrauen nicht auf uns selbst setzen wollten, sondern auf Gott, der die Toten auferweckt.", footnotes: ["Röm 4,17"] },
      { number: 10, text: "Er hat uns aus dieser großen Todesnot errettet und rettet uns noch; auf ihm ruht unsere Hoffnung, dass er uns auch in Zukunft retten wird." },
      { number: 11, text: "Helft aber auch ihr, indem ihr für uns betet, damit viele Menschen in unserem Namen Dank sagen für die Gnade, die uns geschenkt wurde.", footnotes: ["Röm 15,30; 2 Kor 4,15"] },
      { number: 12, text: "Denn das ist unser Ruhm - und dafür zeugt auch unser Gewissen -, dass wir in dieser Welt, vor allem euch gegenüber, in der Aufrichtigkeit und Lauterkeit, wie Gott sie schenkt, gehandelt haben, nicht aufgrund menschlicher Weisheit, sondern aufgrund göttlicher Gnade.", heading: "Klärung von Missverständnissen: 1,12 - 2,11 | Die Zuverlässigkeit des Apostels: 1,12-14", footnotes: ["2,17; Apg 23,1; 1 Kor 2,13"] },
      { number: 13, text: "Und wenn wir euch schreiben, meinen wir nichts anderes, als was ihr lest und versteht; ich hoffe, ihr werdet noch ganz verstehen," },
      { number: 14, text: "was wir meinen und was ihr zum Teil schon verstanden habt, nämlich dass ihr am Tag Jesu, unseres Herrn, auf uns stolz sein dürft, so wie wir auf euch.", footnotes: ["3,2f; Phil 2,16"] },
      { number: 15, text: "In dieser Zuversicht wollte ich zunächst zu euch kommen, damit ihr ein zweites Mal Gnade erfahren hättet.", heading: "Die Änderung des Reiseplans: 1,15 - 2,4", footnotes: ["Röm 15,29"] },
      { number: 16, text: "Von euch wollte ich dann nach Mazedonien reisen und von Mazedonien zu euch zurückkommen, um von euch nach Judäa geleitet zu werden.", footnotes: ["1 Kor 16,5f"] },
      { number: 17, text: "War dieser Entschluss etwa leichtsinnig? Plane ich, wie manche Menschen planen, sodass mein Ja auch ein Nein sein kann?", footnotes: ["Jak 5,12; Mt 5,37"] },
      { number: 18, text: "Gott ist treu, er bürgt dafür, dass unser Wort euch gegenüber nicht Ja und Nein zugleich ist.", footnotes: ["1 Kor 1,9"] },
      { number: 19, text: "Denn Gottes Sohn Jesus Christus, der euch durch uns verkündigt wurde - durch mich, Silvanus und Timotheus -, ist nicht als Ja und Nein zugleich gekommen; in ihm ist das Ja verwirklicht.", footnotes: ["1 Thess 1,1; Apg 18,5"] },
      { number: 20, text: "Er ist das Ja zu allem, was Gott verheißen hat. Darum rufen wir durch ihn zu Gottes Lobpreis auch das Amen.", footnotes: ["Offb 3,14; 1 Kor 14,16"] },
      { number: 21, text: "Gott aber, der uns und euch in der Treue zu Christus festigt und der uns alle gesalbt hat,", footnotes: ["1 Joh 2,27"] },
      { number: 22, text: "er ist es auch, der uns sein Siegel aufgedrückt und als ersten Anteil (am verheißenen Heil) den Geist in unser Herz gegeben hat.", footnotes: ["5,5; Eph 1,13f", "Eine Anspielung auf die Taufe; vom Geist als «erstem Anteil» (wörtlich: «Angeld», erste Rate einer Auszahlung) ist auch in 5,5 und Eph 1,14 die Rede."] },
      { number: 23, text: "Ich rufe aber Gott zum Zeugen an und schwöre bei meinem Leben, dass ich nur, um euch zu schonen, nicht mehr nach Korinth gekommen bin.", footnotes: ["Röm 1,9; 2 Kor 12,20f"] },
      { number: 24, text: "Wir wollen ja nicht Herren über euren Glauben sein, sondern wir sind Helfer zu eurer Freude; denn im Glauben seid ihr fest verwurzelt.", footnotes: ["4,5; 1 Petr 5,3"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Ich entschloss mich also, nicht noch einmal zu euch zu kommen und euch zu betrüben.", footnotes: ["12,21; 1 Kor 4,21"] },
      { number: 2, text: "Wenn ich euch nämlich betrübe, wer wird mich dann erfreuen? Etwa der, den ich selbst betrübt habe?" },
      { number: 3, text: "Und so schrieb ich, statt selber zu kommen, einen Brief, um nicht von denen betrübt zu werden, die mich erfreuen sollten; und ich bin sicher, dass meine Freude auch die Freude von euch allen ist.", footnotes: ["13,10"] },
      { number: 4, text: "Ich schrieb euch aus großer Bedrängnis und Herzensnot, unter vielen Tränen, nicht um euch zu betrüben, nein, um euch meine übergroße Liebe spüren zu lassen.", footnotes: ["7,8; Apg 20,31", "Zum sog. «Tränenbrief» vgl. die Einleitung zum 2. Korintherbrief."] },
      { number: 5, text: "Wenn aber einer Betrübnis verursacht hat, hat er nicht mich betrübt, sondern mehr oder weniger - um nicht zu übertreiben - euch alle.", heading: "Mahnung zur Milde: 2,5-11", footnotes: ["Paulus war offenbar von einem Gemeindemitglied gedemütigt worden."] },
      { number: 6, text: "Die Strafe, die dem Schuldigen von der Mehrheit auferlegt wurde, soll genügen.", footnotes: ["7,11f"] },
      { number: 7, text: "Jetzt sollt ihr lieber verzeihen und trösten, damit der Mann nicht von allzu großer Traurigkeit überwältigt wird.", footnotes: ["Kol 3,13"] },
      { number: 8, text: "Darum bitte ich euch, ihm gegenüber Liebe walten zu lassen." },
      { number: 9, text: "Gerade deswegen habe ich euch ja auch geschrieben, weil ich wissen wollte, ob ihr wirklich in allen Stücken gehorsam seid." },
      { number: 10, text: "Wem ihr aber verzeiht, dem verzeihe auch ich. Denn auch ich habe, wenn hier etwas zu verzeihen war, im Angesicht Christi um euretwillen verziehen," },
      { number: 11, text: "damit wir nicht vom Satan überlistet werden; wir kennen seine Absichten nur zu gut." },
      { number: 12, text: "Als ich dann nach Troas kam, um das Evangelium Christi zu verkünden, und mir der Herr eine Tür öffnete,", heading: "Herrlichkeit und Not des Aposteldienstes: 2,12 - 5,10 | Das Wirken des Apostels als Siegeszug Christi: 2,12-17", footnotes: ["Apg 20,6; 1 Kor 16,9"] },
      { number: 13, text: "hatte mein Geist dennoch keine Ruhe, weil ich meinen Bruder Titus nicht fand. So nahm ich Abschied und reiste nach Mazedonien.", footnotes: ["7,5", "Der Gedankengang dieses Verses wird in 7,5 fortgeführt."] },
      { number: 14, text: "Dank sei Gott, der uns stets im Siegeszug Christi mitführt und durch uns den Duft der Erkenntnis Christi an allen Orten verbreitet." },
      { number: 15, text: "Denn wir sind Christi Wohlgeruch für Gott unter denen, die gerettet werden, wie unter denen, die verloren gehen.", footnotes: ["1 Kor 1,18"] },
      { number: 16, text: "Den einen sind wir Todesgeruch, der Tod bringt; den anderen Lebensduft, der Leben verheißt. Wer aber ist dazu fähig?", footnotes: ["3,5f"] },
      { number: 17, text: "Wir sind jedenfalls nicht wie die vielen anderen, die mit dem Wort Gottes ein Geschäft machen. Wir verkünden es aufrichtig und in Christus, von Gott her und vor Gott.", footnotes: ["4,2; Phil 1,15-17"] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Fangen wir schon wieder an, uns selbst zu empfehlen? Oder brauchen wir - wie gewisse Leute - Empfehlungsschreiben an euch oder von euch?", heading: "Der Apostel und sein Dienst an der Gemeinde: 3,1-3", footnotes: ["5,12; 10,12"] },
      { number: 2, text: "Unser Empfehlungsschreiben seid ihr; es ist eingeschrieben in unser Herz und alle Menschen können es lesen und verstehen.", footnotes: ["1 Kor 9,1f"] },
      { number: 3, text: "Unverkennbar seid ihr ein Brief Christi, ausgefertigt durch unseren Dienst, geschrieben nicht mit Tinte, sondern mit dem Geist des lebendigen Gottes, nicht auf Tafeln aus Stein, sondern - wie auf Tafeln - in Herzen von Fleisch.", footnotes: ["Ex 24,12; 34,1; Ez 11,19; 36,26; Jer 31,33"] },
      { number: 4, text: "Wir haben durch Christus so großes Vertrauen zu Gott.", heading: "Dienst am Buchstaben und Dienst im Geist: 3,4 - 4,6" },
      { number: 5, text: "Doch sind wir dazu nicht von uns aus fähig, als ob wir uns selbst etwas zuschreiben könnten; unsere Befähigung stammt vielmehr von Gott.", footnotes: ["2,16; Phil 2,13"] },
      { number: 6, text: "Er hat uns fähig gemacht, Diener des Neuen Bundes zu sein, nicht des Buchstabens, sondern des Geistes. Denn der Buchstabe tötet, der Geist aber macht lebendig.", footnotes: ["Röm 7,6; 1 Kor 11,25"] },
      { number: 7, text: "Wenn aber schon der Dienst, der zum Tod führt und dessen Buchstaben in Stein gemeißelt waren, so herrlich war, dass die Israeliten das Gesicht des Mose nicht anschauen konnten, weil es eine Herrlichkeit ausstrahlte, die doch vergänglich war,", footnotes: ["Ex 34,29f", "Als Mose vom Berg Sinai herabstieg, strahlte nach Ex 34,29f sein Gesicht die Herrlichkeit Gottes wider, der mit ihm gesprochen hatte. Dieser vergängliche Glanz wird für Paulus zum Bild der Vergänglichkeit, die den Alten Bund im Vergleich mit dem Neuen kennzeichnet."] },
      { number: 8, text: "wie sollte da der Dienst des Geistes nicht viel herrlicher sein?" },
      { number: 9, text: "Wenn schon der Dienst, der zur Verurteilung führt, herrlich war, so wird der Dienst, der zur Gerechtigkeit führt, noch viel herrlicher sein." },
      { number: 10, text: "Eigentlich kann von Herrlichkeit in jenem Fall gar nicht die Rede sein, wo das Verherrlichte vor der größeren Herrlichkeit verblasst.", footnotes: ["Ex 34,29-35"] },
      { number: 11, text: "Wenn nämlich schon das Vergängliche in Herrlichkeit erschien: die Herrlichkeit des Bleibenden wird es überstrahlen." },
      { number: 12, text: "Weil wir eine solche Hoffnung haben, treten wir mit großem Freimut auf," },
      { number: 13, text: "nicht wie Mose, der über sein Gesicht eine Hülle legte, damit die Israeliten das Verblassen des Glanzes nicht sahen.", footnotes: ["Ex 34,33.35"] },
      { number: 14, text: "Doch ihr Denken wurde verhärtet. Bis zum heutigen Tag liegt die gleiche Hülle auf dem Alten Bund, wenn daraus vorgelesen wird, und es bleibt verhüllt, dass er in Christus ein Ende nimmt.", footnotes: ["Jes 6,10; Röm 11,25; 10,4"] },
      { number: 15, text: "Bis heute liegt die Hülle auf ihrem Herzen, wenn Mose vorgelesen wird." },
      { number: 16, text: "Sobald sich aber einer dem Herrn zuwendet, wird die Hülle entfernt.", footnotes: ["Ex 34,34; Röm 11,23.26"] },
      { number: 17, text: "Der Herr aber ist der Geist, und wo der Geist des Herrn wirkt, da ist Freiheit.", footnotes: ["Röm 8,2; 1 Kor 6,17"] },
      { number: 18, text: "Wir alle spiegeln mit enthülltem Angesicht die Herrlichkeit des Herrn wider und werden so in sein eigenes Bild verwandelt, von Herrlichkeit zu Herrlichkeit, durch den Geist des Herrn.", footnotes: ["Röm 8,29; 1 Kor 13,12; Ex 16,7.10; 24,17"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Daher erlahmt unser Eifer nicht in dem Dienst, der uns durch Gottes Erbarmen übertragen wurde." },
      { number: 2, text: "Wir haben uns von aller schimpflichen Arglist losgesagt; wir handeln nicht hinterhältig und verfälschen das Wort Gottes nicht, sondern lehren offen die Wahrheit. So empfehlen wir uns vor dem Angesicht Gottes jedem menschlichen Gewissen.", footnotes: ["2,17; 1 Thess 2,5"] },
      { number: 3, text: "Wenn unser Evangelium dennoch verhüllt ist, ist es nur denen verhüllt, die verloren gehen;", footnotes: ["1 Kor 1,18"] },
      { number: 4, text: "denn der Gott dieser Weltzeit hat das Denken der Ungläubigen verblendet. So strahlt ihnen der Glanz der Heilsbotschaft nicht auf, der Botschaft von der Herrlichkeit Christi, der Gottes Ebenbild ist.", footnotes: ["Eph 2,2; Kol 1,15; Hebr 1,3", "der Gott dieser Weltzeit: der Teufel (vgl. 1 Kor 2,6.8; Eph 2,2; Joh 12,31; 14,30; 16,11)."] },
      { number: 5, text: "Wir verkündigen nämlich nicht uns selbst, sondern Jesus Christus als den Herrn, uns aber als eure Knechte um Jesu willen." },
      { number: 6, text: "Denn Gott, der sprach: Aus Finsternis soll Licht aufleuchten!, er ist in unseren Herzen aufgeleuchtet, damit wir erleuchtet werden zur Erkenntnis des göttlichen Glanzes auf dem Antlitz Christi.", footnotes: ["Gen 1,3; 2 Kor 3,18; 1 Petr 2,9"] },
      { number: 7, text: "Diesen Schatz tragen wir in zerbrechlichen Gefäßen; so wird deutlich, dass das Übermaß der Kraft von Gott und nicht von uns kommt.", heading: "Die Leidensgemeinschaft des Apostels mit Christus: 4,7-18" },
      { number: 8, text: "Von allen Seiten werden wir in die Enge getrieben und finden doch noch Raum; wir wissen weder aus noch ein und verzweifeln dennoch nicht;", footnotes: ["1,8f; 1 Kor 4,10-13"] },
      { number: 9, text: "wir werden gehetzt und sind doch nicht verlassen; wir werden niedergestreckt und doch nicht vernichtet." },
      { number: 10, text: "Wohin wir auch kommen, immer tragen wir das Todesleiden Jesu an unserem Leib, damit auch das Leben Jesu an unserem Leib sichtbar wird.", footnotes: ["Röm 6,8"] },
      { number: 11, text: "Denn immer werden wir, obgleich wir leben, um Jesu willen dem Tod ausgeliefert, damit auch das Leben Jesu an unserem sterblichen Fleisch offenbar wird.", footnotes: ["Röm 8,36"] },
      { number: 12, text: "So erweist an uns der Tod, an euch aber das Leben seine Macht." },
      { number: 13, text: "Doch haben wir den gleichen Geist des Glaubens, von dem es in der Schrift heißt: Ich habe geglaubt, darum habe ich geredet. Auch wir glauben und darum reden wir.", footnotes: ["Ps 116,10 G"] },
      { number: 14, text: "Denn wir wissen, dass der, welcher Jesus, den Herrn, auferweckt hat, auch uns mit Jesus auferwecken und uns zusammen mit euch (vor sein Angesicht) stellen wird.", footnotes: ["Röm 8,11; 1 Kor 6,14"] },
      { number: 15, text: "Alles tun wir euretwegen, damit immer mehr Menschen aufgrund der überreich gewordenen Gnade den Dank vervielfachen, Gott zur Ehre.", footnotes: ["1,11"] },
      { number: 16, text: "Darum werden wir nicht müde; wenn auch unser äußerer Mensch aufgerieben wird, der innere wird Tag für Tag erneuert." },
      { number: 17, text: "Denn die kleine Last unserer gegenwärtigen Not schafft uns in maßlosem Übermaß ein ewiges Gewicht an Herrlichkeit,", footnotes: ["Röm 8,17f; 1 Petr 1,6f"] },
      { number: 18, text: "uns, die wir nicht auf das Sichtbare starren, sondern nach dem Unsichtbaren ausblicken; denn das Sichtbare ist vergänglich, das Unsichtbare ist ewig.", footnotes: ["Röm 8,24f; Hebr 11,1"] },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Wir wissen: Wenn unser irdisches Zelt abgebrochen wird, dann haben wir eine Wohnung von Gott, ein nicht von Menschenhand errichtetes ewiges Haus im Himmel.", heading: "Die Hoffnung des Apostels: 5,1-10", footnotes: ["2 Petr 1,13f", "1-10: Paulus vergleicht den Leib mit einem Zelt, einer Wohnung, einem Haus oder einem Kleid; er erwartet eine Verwandlung (ein Überkleidet-Werden) des irdischen Leibes in den Auferstehungsleib, den Gott im Himmel bereithält (vgl. 1 Kor 15,44). «Nacktsein» (V. 3) ist ein Zustand, bei dem der Mensch nach seinem Tod als Seele ohne Leib existiert."] },
      { number: 2, text: "Im gegenwärtigen Zustand seufzen wir und sehnen uns danach, mit dem himmlischen Haus überkleidet zu werden.", footnotes: ["Röm 8,23"] },
      { number: 3, text: "So bekleidet, werden wir nicht nackt erscheinen." },
      { number: 4, text: "Solange wir nämlich in diesem Zelt leben, seufzen wir unter schwerem Druck, weil wir nicht entkleidet, sondern überkleidet werden möchten, damit so das Sterbliche vom Leben verschlungen werde.", footnotes: ["1 Kor 15,53"] },
      { number: 5, text: "Gott aber, der uns gerade dazu fähig gemacht hat, er hat uns auch als ersten Anteil den Geist gegeben.", footnotes: ["1,22; Eph 1,13f"] },
      { number: 6, text: "Wir sind also immer zuversichtlich, auch wenn wir wissen, dass wir fern vom Herrn in der Fremde leben, solange wir in diesem Leib zu Hause sind;", footnotes: ["Hebr 13,14"] },
      { number: 7, text: "denn als Glaubende gehen wir unseren Weg, nicht als Schauende.", footnotes: ["Röm 8,24; 1 Petr 1,8"] },
      { number: 8, text: "Weil wir aber zuversichtlich sind, ziehen wir es vor, aus dem Leib auszuwandern und daheim beim Herrn zu sein.", footnotes: ["Phil 1,23"] },
      { number: 9, text: "Deswegen suchen wir unsere Ehre darin, ihm zu gefallen, ob wir daheim oder in der Fremde sind." },
      { number: 10, text: "Denn wir alle müssen vor dem Richterstuhl Christi offenbar werden, damit jeder seinen Lohn empfängt für das Gute oder Böse, das er im irdischen Leben getan hat.", footnotes: ["Röm 14,10; 1 Kor 4,5"] },
      { number: 11, text: "So versuchen wir, erfüllt von Ehrfurcht vor dem Herrn, Menschen zu gewinnen; Gott aber kennt uns durch und durch. Ich hoffe, dass auch euer Urteil über mich sich zur vollen Wahrheit durchgerungen hat.", heading: "Die Bewährung des Apostels in seinem Dienst: 5,11 - 6,10 | Der Dienst der Versöhnung: 5,11-21", footnotes: ["4,2"] },
      { number: 12, text: "Damit wollen wir uns nicht wieder vor euch rühmen, sondern wir geben euch Gelegenheit, rühmend auf uns hinzuweisen, damit ihr denen entgegentreten könnt, die sich nur rühmen, um ihr Gesicht zu wahren, ihr Herz aber nicht zeigen dürfen.", footnotes: ["3,1f; 10,12f"] },
      { number: 13, text: "Wenn wir nämlich von Sinnen waren, so geschah es für Gott; wenn wir besonnen sind, geschieht es für euch." },
      { number: 14, text: "Denn die Liebe Christi drängt uns, da wir erkannt haben: Einer ist für alle gestorben, also sind alle gestorben.", footnotes: ["Röm 6,3f; 7,4; 8,31f"] },
      { number: 15, text: "Er ist aber für alle gestorben, damit die Lebenden nicht mehr für sich leben, sondern für den, der für sie starb und auferweckt wurde.", footnotes: ["Röm 14,7f; Gal 2,20"] },
      { number: 16, text: "Also schätzen wir von jetzt an niemand mehr nur nach menschlichen Maßstäben ein; auch wenn wir früher Christus nach menschlichen Maßstäben eingeschätzt haben, jetzt schätzen wir ihn nicht mehr so ein.", footnotes: ["Röm 1,3; 9,5", "Wörtlich: Also kennen wir von jetzt an niemand mehr dem Fleisch nach; auch wenn wir früher Christus dem Fleisch nach gekannt haben, jetzt kennen wir ihn nicht mehr so. - Vgl. die Anmerkung zu 10,2."] },
      { number: 17, text: "Wenn also jemand in Christus ist, dann ist er eine neue Schöpfung: Das Alte ist vergangen, Neues ist geworden.", footnotes: ["Röm 6,4; Gal 6,15; Offb 21,5"] },
      { number: 18, text: "Aber das alles kommt von Gott, der uns durch Christus mit sich versöhnt und uns den Dienst der Versöhnung aufgetragen hat.", footnotes: ["Röm 5,10"] },
      { number: 19, text: "Ja, Gott war es, der in Christus die Welt mit sich versöhnt hat, indem er den Menschen ihre Verfehlungen nicht anrechnete und uns das Wort von der Versöhnung (zur Verkündigung) anvertraute.", footnotes: ["Röm 3,24f; Kol 1,19f"] },
      { number: 20, text: "Wir sind also Gesandte an Christi statt, und Gott ist es, der durch uns mahnt. Wir bitten an Christi statt: Lasst euch mit Gott versöhnen!", footnotes: ["Mt 10,40; Lk 10,16"] },
      { number: 21, text: "Er hat den, der keine Sünde kannte, für uns zur Sünde gemacht, damit wir in ihm Gerechtigkeit Gottes würden.", footnotes: ["Röm 8,3f; Gal 3,13; 1 Petr 2,24", "Dieser Aussage liegt der Gedanke der Stellvertretung zugrunde."] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Als Mitarbeiter Gottes ermahnen wir euch, dass ihr seine Gnade nicht vergebens empfangt.", heading: "Die Herrlichkeit der apostolischen Leiden: 6,1-10" },
      { number: 2, text: "Denn es heißt: Zur Zeit der Gnade erhöre ich dich, / am Tag der Rettung helfe ich dir. Jetzt ist sie da, die Zeit der Gnade; jetzt ist er da, der Tag der Rettung.", footnotes: ["Jes 49,8"] },
      { number: 3, text: "Niemand geben wir auch nur den geringsten Anstoß, damit unser Dienst nicht getadelt werden kann." },
      { number: 4, text: "In allem erweisen wir uns als Gottes Diener: durch große Standhaftigkeit, in Bedrängnis, in Not, in Angst,", footnotes: ["(4-10) 4,8-10; 11,23-29; 1 Kor 4,11-13"] },
      { number: 5, text: "unter Schlägen, in Gefängnissen, in Zeiten der Unruhe, unter der Last der Arbeit, in durchwachten Nächten, durch Fasten," },
      { number: 6, text: "durch lautere Gesinnung, durch Erkenntnis, durch Langmut, durch Güte, durch den Heiligen Geist, durch ungeheuchelte Liebe,", footnotes: ["Gal 5,22f"] },
      { number: 7, text: "durch das Wort der Wahrheit, in der Kraft Gottes, mit den Waffen der Gerechtigkeit in der Rechten und in der Linken,", footnotes: ["Röm 13,12"] },
      { number: 8, text: "bei Ehrung und Schmähung, bei übler Nachrede und bei Lob. Wir gelten als Betrüger und sind doch wahrhaftig;" },
      { number: 9, text: "wir werden verkannt und doch anerkannt; wir sind wie Sterbende und seht: wir leben; wir werden gezüchtigt und doch nicht getötet;", footnotes: ["Ps 118,17f"] },
      { number: 10, text: "uns wird Leid zugefügt und doch sind wir jederzeit fröhlich; wir sind arm und machen doch viele reich; wir haben nichts und haben doch alles.", footnotes: ["8,9; Phil 4,12f"] },
      { number: 11, text: "Unser Mund hat sich für euch aufgetan, Korinther, unser Herz ist weit geworden.", heading: "Die Aussöhnung mit den Korinthern: 6,11 - 7,16 | Werbung und Mahnung: 6,11 - 7,4", footnotes: ["Ps 119,32"] },
      { number: 12, text: "In uns ist es nicht zu eng für euch; eng ist es in eurem Herzen." },
      { number: 13, text: "Lasst doch als Antwort darauf - ich rede wie zu meinen Kindern - auch euer Herz weit aufgehen!" },
      { number: 14, text: "Beugt euch nicht mit Ungläubigen unter das gleiche Joch! Was haben denn Gerechtigkeit und Gesetzwidrigkeit miteinander zu tun? Was haben Licht und Finsternis gemeinsam?", footnotes: ["Dtn 22,10; Eph 5,7.11", "14-7,1: Der Abschnitt unterbricht den Zusammenhang und ist eine kleine Mahnrede gegen das in die Gemeinde eindringende Heidentum."] },
      { number: 15, text: "Was für ein Einklang herrscht zwischen Christus und Beliar? Was hat ein Gläubiger mit einem Ungläubigen gemeinsam?", footnotes: ["1 Kor 10,21", "Beliar ist eine im damaligen Judentum gebräuchliche Bezeichnung für den Teufel."] },
      { number: 16, text: "Wie verträgt sich der Tempel Gottes mit Götzenbildern? Wir sind doch der Tempel des lebendigen Gottes; denn Gott hat gesprochen: Ich will unter ihnen wohnen und mit ihnen gehen. / Ich werde ihr Gott sein / und sie werden mein Volk sein.", footnotes: ["Lev 26,11f; Jer 31,33; 32,38; Ez 37,27; 1 Kor 3,16"] },
      { number: 17, text: "Zieht darum weg aus ihrer Mitte / und sondert euch ab, spricht der Herr, / und fasst nichts Unreines an. / Dann will ich euch aufnehmen", footnotes: ["Jes 52,11; Jer 51,45; Offb 18,4; Zef 3,20 G"] },
      { number: 18, text: "und euer Vater sein / und ihr sollt meine Söhne und Töchter sein, / spricht der Herr, / der Herrscher über die ganze Schöpfung.", footnotes: ["2 Sam 7,14; Jer 31,9; Jes 43,6; Am 3,13 G"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Das sind die Verheißungen, die wir haben, liebe Brüder. Reinigen wir uns also von aller Unreinheit des Leibes und des Geistes und streben wir in Gottesfurcht nach vollkommener Heiligung.", footnotes: ["1 Joh 3,3"] },
      { number: 2, text: "Gebt uns doch Raum in eurem Herzen! Niemand haben wir geschädigt, niemand zugrunde gerichtet, niemand übervorteilt.", footnotes: ["12,17"] },
      { number: 3, text: "Ich sage das nicht, um euch zu verurteilen; denn eben habe ich gesagt, dass ihr in unserem Herzen wohnt, verbunden mit uns zum Leben und zum Sterben.", footnotes: ["6,11f.9"] },
      { number: 4, text: "Ich habe großes Vertrauen zu euch; ich bin sehr stolz auf euch. Trotz all unserer Not bin ich von Trost erfüllt und ströme über von Freude." },
      { number: 5, text: "Als wir nach Mazedonien gekommen waren, fanden wir in unserer Schwachheit keine Ruhe. Überall bedrängten uns Schwierigkeiten: von außen Widerspruch und Anfeindung, im Innern Angst und Furcht.", heading: "Die Sendung des Titus: 7,5-16", footnotes: ["2,13; 4,8", "Hier nimmt der Brief den in 2,13 verlassenen Gedankengang wieder auf."] },
      { number: 6, text: "Aber Gott, der die Niedergeschlagenen aufrichtet, hat auch uns aufgerichtet, und zwar durch die Ankunft des Titus -", footnotes: ["1,3f"] },
      { number: 7, text: "nicht nur durch seine Ankunft, sondern auch durch den Trost, den er bei euch erfahren hatte. Er erzählte uns von eurer Sehnsucht, eurer Klage, eurem Eifer für mich, sodass ich mich noch mehr freute.", footnotes: ["1 Thess 3,6f"] },
      { number: 8, text: "Dass ich euch aber mit meinem Brief traurig gemacht habe, tut mir nicht Leid. Wenn es mir auch eine Weile Leid tat - ich sehe ja, dass dieser Brief euch, wenn auch nur für kurze Zeit, traurig gemacht hat -:", footnotes: ["2,4"] },
      { number: 9, text: "jetzt freue ich mich, nicht weil ihr traurig geworden seid, sondern weil die Traurigkeit euch zur Sinnesänderung geführt hat. Denn es war eine gottgewollte Traurigkeit; so ist euch durch uns kein Nachteil erwachsen." },
      { number: 10, text: "Die gottgewollte Traurigkeit verursacht nämlich Sinnesänderung zum Heil, die nicht bereut zu werden braucht; die weltliche Traurigkeit aber führt zum Tod." },
      { number: 11, text: "Wie groß war doch der Eifer, zu dem euch diese gottgewollte Traurigkeit geführt hat, wie aufrichtig eure Entschuldigung, euer Unwille, eure Furcht, eure Sehnsucht, wie wirksam eure Anstrengung und am Ende die Bestrafung! In jeder Hinsicht hat es sich gezeigt, dass ihr in dieser Sache unschuldig seid.", footnotes: ["2,6f"] },
      { number: 12, text: "Wenn ich euch also geschrieben habe, so tat ich es nicht, um den zu treffen, der Unrecht getan hatte, auch nicht, um dem Recht zu verschaffen, der Unrecht erlitten hatte, sondern ich tat es, damit euer Eifer für uns sichtbar werde vor euch und vor Gott.", footnotes: ["2,9"] },
      { number: 13, text: "Deswegen sind wir jetzt getröstet. Wir wurden aber nicht nur getröstet, sondern darüber hinaus erfreut durch die Freude des Titus, dessen Geist neue Kraft gefunden hat durch euch alle." },
      { number: 14, text: "Wenn ich euch vor ihm gerühmt hatte, so brauchte ich mich jetzt nicht zu schämen. Im Gegenteil, unser Lob vor Titus erwies sich als volle Wahrheit, wie auch alles Wahrheit ist, was wir euch verkündigt haben." },
      { number: 15, text: "Er ist euch von Herzen zugetan, wenn er daran denkt, wie ihr euch alle gehorsam gezeigt und ihn mit Furcht und Zittern aufgenommen habt.", footnotes: ["Phil 2,12"] },
      { number: 16, text: "Ich freue mich, dass ich in jeder Hinsicht auf euch vertrauen kann." },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Brüder, wir wollen euch jetzt von der Gnade erzählen, die Gott den Gemeinden Mazedoniens erwiesen hat.", heading: "Aufruf zur Sammlung für Jerusalem: 8,1-24 | Das Vorbild der Christen Mazedoniens: 8,1-5", footnotes: ["9,1f; Röm 15,26"] },
      { number: 2, text: "Während sie durch große Not geprüft wurden, verwandelten sich ihre übergroße Freude und ihre tiefe Armut in den Reichtum ihres selbstlosen Gebens." },
      { number: 3, text: "Ich bezeuge, dass sie nach Kräften und sogar über ihre Kräfte spendeten, ganz von sich aus,", footnotes: ["Apg 11,29"] },
      { number: 4, text: "indem sie sich geradezu aufdrängten und uns um die Gunst baten, zur Hilfeleistung für die Heiligen beitragen zu dürfen." },
      { number: 5, text: "Und über unsere Erwartung hinaus haben sie sich eingesetzt, zunächst für den Herrn, aber auch für uns, wie es Gottes Wille war." },
      { number: 6, text: "Daraufhin ermutigten wir Titus, dieses Liebeswerk, das er früher bei euch begonnen hatte, nun auch zu vollenden.", heading: "Der Abschluss der Sammlung in Korinth: 8,6-15", footnotes: ["12,18"] },
      { number: 7, text: "Wie ihr aber an allem reich seid, an Glauben, Rede und Erkenntnis, an jedem Eifer und an der Liebe, die wir in euch begründet haben, so sollt ihr euch auch an diesem Liebeswerk mit reichlichen Spenden beteiligen.", footnotes: ["1 Kor 1,5; 1 Kor 16,1-3"] },
      { number: 8, text: "Ich meine das nicht als strenge Weisung, aber ich gebe euch Gelegenheit, angesichts des Eifers anderer auch eure Liebe als echt zu erweisen." },
      { number: 9, text: "Denn ihr wisst, was Jesus Christus, unser Herr, in seiner Liebe getan hat: Er, der reich war, wurde euretwegen arm, um euch durch seine Armut reich zu machen.", footnotes: ["Phil 2,6f"] },
      { number: 10, text: "Ich gebe euch nur einen Rat, der euch helfen soll; ihr habt ja schon voriges Jahr angefangen, etwas zu unternehmen, und zwar aus eigenem Entschluss.", footnotes: ["9,2"] },
      { number: 11, text: "Jetzt sollt ihr das Begonnene zu Ende führen, damit das Ergebnis dem guten Willen entspricht - je nach eurem Besitz." },
      { number: 12, text: "Wenn nämlich der gute Wille da ist, dann ist jeder willkommen mit dem, was er hat, und man fragt nicht nach dem, was er nicht hat.", footnotes: ["Spr 3,27f"] },
      { number: 13, text: "Denn es geht nicht darum, dass ihr in Not geratet, indem ihr anderen helft; es geht um einen Ausgleich." },
      { number: 14, text: "Im Augenblick soll euer Überfluss ihrem Mangel abhelfen, damit auch ihr Überfluss einmal eurem Mangel abhilft. So soll ein Ausgleich entstehen," },
      { number: 15, text: "wie es in der Schrift heißt: Wer viel gesammelt hatte, hatte nicht zu viel, und wer wenig, hatte nicht zu wenig.", footnotes: ["Ex 16,18"] },
      { number: 16, text: "Dank sei Gott, der den gleichen Eifer für euch auch Titus ins Herz gelegt hat.", heading: "Der Eifer und die Selbstlosigkeit der Mitarbeiter: 8,16-24" },
      { number: 17, text: "Denn Titus war mit meinem Vorschlag einverstanden und sein Eifer war so groß, dass er aus eigenem Entschluss gleich zu euch abreiste." },
      { number: 18, text: "Zusammen mit ihm haben wir den Bruder geschickt, der wegen seiner Verkündigung des Evangeliums in allen Gemeinden Anerkennung findet", footnotes: ["12,18", "Der hier erwähnte Bruder ist uns nicht bekannt. Nach alter Überlieferung soll es Lukas gewesen sein."] },
      { number: 19, text: "und außerdem von den Gemeinden dazu bestimmt wurde, unser Reisegefährte zu sein, wenn wir diese Liebesgabe zur Ehre des Herrn und als Zeichen unseres guten Willens überbringen." },
      { number: 20, text: "Denn angesichts der großen Spende, die von uns überbracht werden soll, möchten wir vermeiden, dass man uns verdächtigt." },
      { number: 21, text: "Es liegt uns nämlich daran, dass alles einwandfrei zugeht, nicht nur vor dem Herrn, sondern auch vor den Menschen.", footnotes: ["Spr 3,4 G"] },
      { number: 22, text: "Wir haben aber mit den beiden noch einen weiteren Bruder geschickt, dessen Eifer wir vielfach und bei vielen Gelegenheiten feststellen konnten und der sich in diesem Fall noch eifriger zeigt, weil er viel von euch erwartet.", footnotes: ["Wer dieser Bruder war, lässt sich nicht bestimmen."] },
      { number: 23, text: "Was nun Titus angeht: Er ist mein Gefährte und mein Mitarbeiter, der für euch tätig ist; unsere anderen Brüder aber sind Abgesandte der Gemeinden und ein Abglanz Christi." },
      { number: 24, text: "Legt also ihnen gegenüber und damit vor den Gemeinden das Zeugnis eurer Liebe ab und zeigt, dass wir euch zu Recht gerühmt haben.", footnotes: ["7,14f"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Eigentlich ist es unnötig, euch über das Hilfswerk für die Heiligen zu schreiben.", heading: "Erneuter Aufruf zur Sammlung für Jerusalem: 9,1-15 | Empfehlung der Beauftragten: 9,1-5", footnotes: ["8,4"] },
      { number: 2, text: "Denn ich kenne euren guten Willen und rühme euch vor den Mazedoniern, indem ich ihnen sage: Achaia ist seit einem Jahr gerüstet. Und euer Eifer hat viele andere angespornt.", footnotes: ["8,11"] },
      { number: 3, text: "Trotzdem habe ich die Brüder zu euch geschickt; denn unser Lob für euch könnte in dieser Hinsicht verfrüht gewesen sein. Ihr solltet also jetzt wirklich, wie ich sagte, gerüstet sein.", footnotes: ["8,22.24"] },
      { number: 4, text: "Wenn nämlich Mazedonier mit mir kämen und euch noch nicht gerüstet fänden, dann könnte es geschehen, dass wir uns wegen dieser unserer Erwartung zu schämen hätten - um nicht zu sagen, ihr hättet euch zu schämen." },
      { number: 5, text: "Ich hielt es also für notwendig, die Brüder zu bitten, sie möchten vorausreisen und eure in Aussicht gestellte Spende schon jetzt einsammeln, damit sie dann verfügbar ist, und zwar als großzügige Spende, nicht als Gabe des Geizes.", footnotes: ["als großzügige Spende, wörtlich: als Segen."] },
      { number: 6, text: "Denkt daran: Wer kärglich sät, wird auch kärglich ernten; wer reichlich sät, wird reichlich ernten.", heading: "Der Segen des Hilfswerks: 9,6-15", footnotes: ["Spr 11,24"] },
      { number: 7, text: "Jeder gebe, wie er es sich in seinem Herzen vorgenommen hat, nicht verdrossen und nicht unter Zwang; denn Gott liebt einen fröhlichen Geber.", footnotes: ["Spr 22,9 G"] },
      { number: 8, text: "In seiner Macht kann Gott alle Gaben über euch ausschütten, sodass euch allezeit in allem alles Nötige ausreichend zur Verfügung steht und ihr noch genug habt, um allen Gutes zu tun," },
      { number: 9, text: "wie es in der Schrift heißt: Reichlich gibt er den Armen; / seine Gerechtigkeit hat Bestand für immer.", footnotes: ["Ps 112,9"] },
      { number: 10, text: "Gott, der Samen gibt für die Aussaat und Brot zur Nahrung, wird auch euch das Saatgut geben und die Saat aufgehen lassen; er wird die Früchte eurer Gerechtigkeit wachsen lassen.", footnotes: ["Jes 55,10; Hos 10,12 G"] },
      { number: 11, text: "In allem werdet ihr reich genug sein, um selbstlos schenken zu können; und wenn wir diese Gabe überbringen, wird sie Dank an Gott hervorrufen.", footnotes: ["4,15; 8,7"] },
      { number: 12, text: "Denn euer Dienst und eure Opfergabe füllen nicht nur die leeren Hände der Heiligen, sondern werden weiterwirken als vielfältiger Dank an Gott.", footnotes: ["8,14"] },
      { number: 13, text: "Vom Zeugnis eines solchen Dienstes bewegt, werden sie Gott dafür preisen, dass ihr euch gehorsam zum Evangelium Christi bekannt und dass ihr ihnen und allen selbstlos geholfen habt." },
      { number: 14, text: "In ihrem Gebet für euch werden sie sich angesichts der übergroßen Gnade, die Gott euch geschenkt hat, eng mit euch verbunden fühlen." },
      { number: 15, text: "Dank sei Gott für sein unfassbares Geschenk." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Ich, Paulus, der ja im persönlichen Umgang mit euch so unterwürfig, aus der Ferne aber so unerschrocken sein soll, ich ermahne euch angesichts der Freundlichkeit und Güte Christi", heading: "Zurückweisung von Angriffen gegen den Apostel: 10,1-18 | Die Vorwürfe der Gegner: 10,1-11", footnotes: ["10,10; 11,6; 1 Kor 2,3"] },
      { number: 2, text: "und bitte euch: Zwingt mich nicht, bei meinem Kommen so unerschrocken und fest aufzutreten, wie ich es gegen gewisse Leute zu tun gedenke, die meinen, wir verhalten uns wie Menschen dieser Welt.", footnotes: ["1 Kor 4,21; 2 Kor 1,17", "2f: Wörtlich: die meinen, wir wandeln nach dem Fleisch. Im Fleisch nämlich wandelnd ziehen wir nicht nach dem Fleisch zu Felde. - Der Begriff «Fleisch» kommt im griechischen Text von 10,2-4 wiederholt vor (vgl. die Anmerkung zu Röm 8,1-11)."] },
      { number: 3, text: "Wir leben zwar in dieser Welt, kämpfen aber nicht mit den Waffen dieser Welt." },
      { number: 4, text: "Die Waffen, die wir bei unserem Feldzug einsetzen, sind nicht irdisch, aber sie haben durch Gott die Macht, Festungen zu schleifen; mit ihnen reißen wir", footnotes: ["Röm 13,12; Eph 6,11-13"] },
      { number: 5, text: "alle hohen Gedankengebäude nieder, die sich gegen die Erkenntnis Gottes auftürmen. Wir nehmen alles Denken gefangen, sodass es Christus gehorcht;", footnotes: ["Jes 2,12-17"] },
      { number: 6, text: "wir sind entschlossen, alle Ungehorsamen zu strafen, sobald ihr wirklich gehorsam geworden seid." },
      { number: 7, text: "Schaut auf das, was vor Augen liegt. Wenn jemand überzeugt ist, Christus zu gehören, dann soll er doch auch bedenken, dass nicht nur er, sondern auch wir Christus gehören.", footnotes: ["1 Kor 1,12"] },
      { number: 8, text: "Und wenn ich etwas mehr auf unsere Vollmacht poche, werde ich mich nicht zu scheuen brauchen. Der Herr hat sie mir allerdings verliehen, damit ich bei euch aufbaue, nicht damit ich niederreiße;", footnotes: ["12,6; 13,10"] },
      { number: 9, text: "ich möchte nicht den Anschein erwecken, als wollte ich euch durch meine Briefe einschüchtern." },
      { number: 10, text: "Ja, die Briefe, wird gesagt, die sind wuchtig und voll Kraft, aber sein persönliches Auftreten ist matt und seine Worte sind armselig.", footnotes: ["10,1; 11,6"] },
      { number: 11, text: "Wer so redet, der soll sich merken: Wie wir durch das geschriebene Wort aus der Ferne wirken, so können wir auch in eurer Gegenwart tatkräftig auftreten.", footnotes: ["13,2"] },
      { number: 12, text: "Wir sind allerdings nicht so vermessen, uns gleich zu stellen oder zu vergleichen mit gewissen Leuten, die sich selbst anpreisen. In ihrem Unverstand messen sie sich an sich selbst und vergleichen sich mit sich selbst.", heading: "Die Überzeugung des Apostels: 10,12-18", footnotes: ["3,1"] },
      { number: 13, text: "Wir dagegen wollen uns nicht maßlos rühmen, sondern jenen Maßstab anlegen, den uns Gott zugeteilt hat, dass wir nämlich bis zu euch gelangt sind.", footnotes: ["Röm 12,3"] },
      { number: 14, text: "Wir überschreiten also nicht unser Maß, wie wir es tun würden, wenn wir nicht bis zu euch gelangt wären; denn wir sind wirklich als Erste mit dem Evangelium Christi bis zu euch gekommen." },
      { number: 15, text: "Wir rühmen uns also nicht maßlos und mit fremden Leistungen; aber wir haben die Hoffnung, wenn euer Glaube stärker wird, vor euren Augen über das uns (bisher) gesetzte Maß weit hinauszuwachsen", footnotes: ["Röm 15,20.24"] },
      { number: 16, text: "und die Heilsbotschaft über eure Grenzen hinauszutragen. Nach einem fremden Maßstab und auf einem Feld, das schon bestellt ist, wollen wir keinen Ruhm ernten." },
      { number: 17, text: "Wer sich also rühmen will, der rühme sich des Herrn.", footnotes: ["Jer 9,22f; 1 Kor 1,31"] },
      { number: 18, text: "Denn nicht, wer sich selbst empfiehlt, ist anerkannt, sondern der, den der Herr empfiehlt.", footnotes: ["1 Kor 4,5"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Lasst euch doch ein wenig Unverstand von mir gefallen! Aber das tut ihr ja.", heading: "Die Leistung des Apostels: 11,1 - 12,13 | Die Selbstlosigkeit des Apostels: 11,1-15", footnotes: ["11,16-18", "1-12,13: Paulus sieht sich gezwungen, auf seine Leistungen hinzuweisen, um seinen Gegnern entgegenzutreten, die ihn verächtlich zu machen suchen. Da dieses Eigenlob Paulus widerstrebt, greift er zur Ironie und zur Form der sog. «Narrenrede» (vgl. 11,16)."] },
      { number: 2, text: "Denn ich liebe euch mit der Eifersucht Gottes; ich habe euch einem einzigen Mann verlobt, um euch als reine Jungfrau zu Christus zu führen.", footnotes: ["Eph 5,26f"] },
      { number: 3, text: "Ich fürchte aber, wie die Schlange einst durch ihre Falschheit Eva täuschte, könntet auch ihr in euren Gedanken von der aufrichtigen und reinen Hingabe an Christus abkommen.", footnotes: ["Gen 3,4.13"] },
      { number: 4, text: "Ihr nehmt es ja offenbar hin, wenn irgendeiner daherkommt und einen anderen Jesus verkündigt, als wir verkündigt haben, wenn ihr einen anderen Geist empfangt, als ihr empfangen habt, oder ein anderes Evangelium, als ihr angenommen habt.", footnotes: ["Gal 1,6-9"] },
      { number: 5, text: "Ich denke doch, ich stehe den Überaposteln keineswegs nach.", footnotes: ["11,13; 12,11"] },
      { number: 6, text: "Im Reden mag ich ein Stümper sein, aber nicht in der Erkenntnis; wir haben sie euch in keiner Weise und in keinem Fall vorenthalten.", footnotes: ["10,10; 1 Kor 2,1f.13"] },
      { number: 7, text: "Oder habe ich einen Fehler gemacht, als ich, um euch zu erhöhen, mich selbst erniedrigte und euch das Evangelium Gottes verkündete, ohne etwas dafür zu nehmen?", footnotes: ["1 Kor 9,12f"] },
      { number: 8, text: "Andere Gemeinden habe ich ausgeplündert und Geld von ihnen genommen, um euch dienen zu können.", footnotes: ["Phil 4,10.15"] },
      { number: 9, text: "Aber als ich zu euch kam und in Schwierigkeiten geriet, bin ich niemand zur Last gefallen; was ich zu wenig hatte, ergänzten die Brüder, die aus Mazedonien kamen. Ich habe also darauf Wert gelegt, euch in keiner Weise zur Last zu fallen, und werde auch weiterhin darauf Wert legen.", footnotes: ["12,13"] },
      { number: 10, text: "So gewiss die Wahrheit Christi in mir ist: diesen Ruhm wird mir im Gebiet von Achaia niemand nehmen.", footnotes: ["1 Kor 9,15"] },
      { number: 11, text: "Warum? Liebe ich euch etwa nicht? Gott weiß es." },
      { number: 12, text: "Was ich aber tue, werde ich auch in Zukunft tun: Ich werde denen die Gelegenheit nehmen, die nur die Gelegenheit suchen, sich Achtung zu verschaffen, um so dazustehen wie wir.", footnotes: ["2,17; Phil 3,2"] },
      { number: 13, text: "Denn diese Leute sind Lügenapostel, unehrliche Arbeiter; sie tarnen sich freilich als Apostel Christi.", footnotes: ["Offb 2,2"] },
      { number: 14, text: "Kein Wunder, denn auch der Satan tarnt sich als Engel des Lichts." },
      { number: 15, text: "Es ist also nicht erstaunlich, wenn sich auch seine Handlanger als Diener der Gerechtigkeit tarnen. Ihr Ende wird ihren Taten entsprechen.", footnotes: ["Phil 3,19; 2 Tim 4,14"] },
      { number: 16, text: "Die «Narrenrede»: 11,16 - 12,13 Noch einmal sage ich: Keiner soll mich für einen Narren halten. Tut ihr es aber doch, dann lasst mich auch als Narren gewähren, damit auch ich ein wenig prahlen kann.", heading: "Die Leiden und Mühen des Apostels: 11,16-33", footnotes: ["11,1; 12,6"] },
      { number: 17, text: "Was ich hier sage, sage ich nicht im Sinn des Herrn, sondern sozusagen als Narr im falschen Stolz des Prahlers." },
      { number: 18, text: "Da viele Menschen im Sinn dieser Welt prahlen, will auch ich einmal prahlen.", footnotes: ["Phil 3,4"] },
      { number: 19, text: "Ihr lasst euch die Narren ja gern gefallen, ihr klugen Leute.", footnotes: ["1 Kor 4,10"] },
      { number: 20, text: "Denn ihr nehmt es hin, wenn euch jemand versklavt, ausbeutet und in seine Gewalt bringt, wenn jemand anmaßend auftritt und euch ins Gesicht schlägt." },
      { number: 21, text: "Zu meiner Schande muss ich gestehen: Dazu bin ich allerdings zu schwach gewesen. Womit aber jemand prahlt - ich rede jetzt als Narr -, damit kann auch ich prahlen." },
      { number: 22, text: "Sie sind Hebräer - ich auch. Sie sind Israeliten - ich auch. Sie sind Nachkommen Abrahams - ich auch.", footnotes: ["Apg 22,3; Röm 11,1; Phil 3,5"] },
      { number: 23, text: "Sie sind Diener Christi - jetzt rede ich ganz unvernünftig -, ich noch mehr: Ich ertrug mehr Mühsal, war häufiger im Gefängnis, wurde mehr geschlagen, war oft in Todesgefahr.", footnotes: ["1 Kor 4,1; 15,10"] },
      { number: 24, text: "Fünfmal erhielt ich von Juden die neununddreißig Hiebe;", footnotes: ["Dtn 25,3", "Nach Dtn 25,3 waren vierzig Schläge das Höchstmaß einer vom Richter verhängten Züchtigung. Um diese Zahl auf keinen Fall zu überschreiten, pflegte man nur neununddreißig Schläge zu geben."] },
      { number: 25, text: "dreimal wurde ich ausgepeitscht, einmal gesteinigt, dreimal erlitt ich Schiffbruch, eine Nacht und einen Tag trieb ich auf hoher See.", footnotes: ["Apg 16,22; 14,19; 27,41-43"] },
      { number: 26, text: "Ich war oft auf Reisen, gefährdet durch Flüsse, gefährdet durch Räuber, gefährdet durch das eigene Volk, gefährdet durch Heiden, gefährdet in der Stadt, gefährdet in der Wüste, gefährdet auf dem Meer, gefährdet durch falsche Brüder." },
      { number: 27, text: "Ich erduldete Mühsal und Plage, durchwachte viele Nächte, ertrug Hunger und Durst, häufiges Fasten, Kälte und Blöße.", footnotes: ["6,4f; 1 Kor 4,11; 1 Thess 2,9"] },
      { number: 28, text: "Um von allem andern zu schweigen, weise ich noch auf den täglichen Andrang zu mir und die Sorge für alle Gemeinden hin.", footnotes: ["Apg 20,31"] },
      { number: 29, text: "Wer leidet unter seiner Schwachheit, ohne dass ich mit ihm leide? Wer kommt zu Fall, ohne dass ich von Sorge verzehrt werde?", footnotes: ["1 Kor 9,22"] },
      { number: 30, text: "Wenn schon geprahlt sein muss, will ich mit meiner Schwachheit prahlen.", footnotes: ["12,5.9f"] },
      { number: 31, text: "Gott, der Vater Jesu, des Herrn, er, der gepriesen ist in Ewigkeit, weiß, dass ich nicht lüge." },
      { number: 32, text: "In Damaskus ließ der Statthalter des Königs Aretas die Stadt der Damaszener bewachen, um mich festzunehmen.", footnotes: ["Apg 9,24f"] },
      { number: 33, text: "Aber durch ein Fenster wurde ich in einem Korb die Stadtmauer hinuntergelassen und so entkam ich ihm." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Ich muss mich ja rühmen; zwar nützt es nichts, trotzdem will ich jetzt von Erscheinungen und Offenbarungen sprechen, die mir der Herr geschenkt hat.", heading: "Begnadung und Schwachheit des Apostels: 12,1-13", footnotes: ["Apg 26,16; Gal 2,2"] },
      { number: 2, text: "Ich kenne jemand, einen Diener Christi, der vor vierzehn Jahren bis in den dritten Himmel entrückt wurde; ich weiß allerdings nicht, ob es mit dem Leib oder ohne den Leib geschah, nur Gott weiß es.", footnotes: ["2-4: Den bildhaften Ausdruck von der Entrückung «in den dritten Himmel» (V. 2) oder «ins Paradies» (V. 4) übernimmt Paulus aus zeitgenössischen Jenseitsvorstellungen."] },
      { number: 3, text: "Und ich weiß, dass dieser Mensch in das Paradies entrückt wurde; ob es mit dem Leib oder ohne den Leib geschah, weiß ich nicht, nur Gott weiß es. Er hörte unsagbare Worte, die ein Mensch nicht aussprechen kann." },
      { number: 5, text: "Diesen Mann will ich rühmen; was mich selbst angeht, will ich mich nicht rühmen, höchstens meiner Schwachheit.", footnotes: ["11,30"] },
      { number: 6, text: "Wenn ich mich dennoch rühmen wollte, wäre ich zwar kein Narr, sondern würde die Wahrheit sagen. Aber ich verzichte darauf; denn jeder soll mich nur nach dem beurteilen, was er an mir sieht oder aus meinem Mund hört.", footnotes: ["10,8"] },
      { number: 7, text: "Damit ich mich wegen der einzigartigen Offenbarungen nicht überhebe, wurde mir ein Stachel ins Fleisch gestoßen: ein Bote Satans, der mich mit Fäusten schlagen soll, damit ich mich nicht überhebe.", footnotes: ["Num 33,55; Ez 28,24", "Paulus spricht wohl von einer Krankheit, die sein Wirken beeinträchtigte (vgl. Gal 4,13f). Näheres wissen wir darüber nicht."] },
      { number: 8, text: "Dreimal habe ich den Herrn angefleht, dass dieser Bote Satans von mir ablasse.", footnotes: ["Mt 26,44"] },
      { number: 9, text: "Er aber antwortete mir: Meine Gnade genügt dir; denn sie erweist ihre Kraft in der Schwachheit. Viel lieber also will ich mich meiner Schwachheit rühmen, damit die Kraft Christi auf mich herabkommt.", footnotes: ["Jes 40,29; Phil 4,13"] },
      { number: 10, text: "Deswegen bejahe ich meine Ohnmacht, alle Misshandlungen und Nöte, Verfolgungen und Ängste, die ich für Christus ertrage; denn wenn ich schwach bin, dann bin ich stark.", footnotes: ["6,9f"] },
      { number: 11, text: "Jetzt bin ich wirklich ein Narr geworden; ihr habt mich dazu gezwungen. Eigentlich sollte ich von euch gerühmt werden; denn in nichts bin ich hinter den Überaposteln zurückgeblieben, obgleich ich nichts bin.", footnotes: ["11,1.5.16"] },
      { number: 12, text: "Das, woran man den Apostel erkennt, wurde mit großer Ausdauer unter euch vollbracht: Zeichen, Wunder und machtvolle Taten.", footnotes: ["Apg 5,12; Röm 15,19", "Das, woran man den Apostel erkennt, wörtlich: Die Zeichen des Apostels."] },
      { number: 13, text: "Worin seid ihr denn im Vergleich mit den übrigen Gemeinden zu kurz gekommen? Höchstens darin, dass gerade ich euch nicht zur Last gefallen bin. Dann verzeiht mir bitte dieses Unrecht!", footnotes: ["11,7-9"] },
      { number: 14, text: "Schon zum dritten Mal will ich jetzt zu euch kommen und ich werde euch nicht zur Last fallen; ich suche ja nicht euer Geld, sondern euch. Denn nicht die Kinder sollen für die Eltern sparen, sondern die Eltern für die Kinder.", heading: "Ankündigung eines neuen Besuches: 12,14 - 13,10 | Die Sorgen und Ziele des Apostels: 12,14-18", footnotes: ["13,1"] },
      { number: 15, text: "Ich aber will sehr gern alles aufwenden und mich für euch aufreiben. Wenn ich euch so sehr liebe, soll ich deswegen weniger Liebe empfangen?", footnotes: ["Phil 2,17; 1 Thess 2,8"] },
      { number: 16, text: "Nun gut! Eine schwere Last habe ich euch zwar nicht zugemutet; aber habe ich euch vielleicht mit Verschlagenheit und List in mein Netz gelockt?" },
      { number: 17, text: "Habe ich euch vielleicht durch einen, den ich zu euch sandte, übervorteilt?", footnotes: ["7,2"] },
      { number: 18, text: "Ja, ich habe Titus gebeten, euch zu besuchen, und den Bruder mit ihm gesandt. Hat Titus euch etwa übervorteilt? Haben wir nicht beide im gleichen Geist gehandelt? Sind wir nicht in den gleichen Spuren gegangen?", footnotes: ["8,16-18", "Zu «Bruder» vgl. die Anmerkung zu 8,18.22."] },
      { number: 19, text: "Gewiss denkt ihr schon lange, dass wir uns vor euch nur herausreden wollen. Aber wir reden in Christus, vor dem Angesicht Gottes. Und alles, liebe Brüder, geschieht, um eure Gemeinde aufzubauen.", heading: "Befürchtungen und Besorgnisse: 12,19-21", footnotes: ["2,17"] },
      { number: 20, text: "Denn ich fürchte, dass ich euch bei meinem Kommen nicht so finde, wie ich euch zu finden wünsche, und dass ihr mich so findet, wie ihr mich nicht zu finden wünscht. Ich fürchte, dass es zu Streit, Eifersucht, Zornesausbrüchen, Ehrgeiz, Verleumdungen, übler Nachrede, Überheblichkeit, allgemeiner Verwirrung kommt;", footnotes: ["10,2"] },
      { number: 21, text: "dass mein Gott, wenn ich wiederkomme, mich noch einmal vor euch demütigt; dass ich Grund haben werde, traurig zu sein über viele, die schon früher Sünder waren und sich trotz ihrer Unreinheit, Unzucht und Ausschweifung noch nicht zur Umkehr entschlossen haben.", footnotes: ["13,2"] },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Das ist das dritte Mal, dass ich zu euch komme. Durch die Aussage von zwei oder drei Zeugen wird jede Sache entschieden.", heading: "Seelsorge aus apostolischer Vollmacht: 13,1-10", footnotes: ["12,14; Dtn 19,15; Mt 18,16; 1 Tim 5,19"] },
      { number: 2, text: "Denen, die sich früher verfehlt haben, und allen Übrigen sage ich jetzt aus der Ferne dasselbe, was ich schon bei meinem zweiten Aufenthalt angekündigt habe: Wenn ich komme, werde ich keine Nachsicht mehr üben.", footnotes: ["12,21"] },
      { number: 3, text: "Denn ihr verlangt einen Beweis dafür, dass durch mich Christus spricht, der nicht in seiner Schwachheit, sondern in seiner Kraft unter euch wirkt.", footnotes: ["Röm 15,18f"] },
      { number: 4, text: "Zwar wurde er in seiner Schwachheit gekreuzigt, aber er lebt aus Gottes Kraft. Auch wir sind schwach in ihm, aber wir werden zusammen mit ihm vor euren Augen aus Gottes Kraft leben.", footnotes: ["1 Kor 1,24f.27"] },
      { number: 5, text: "Fragt euch selbst, ob ihr im Glauben seid, prüft euch selbst! Erfahrt ihr nicht an euch selbst, dass Christus Jesus in euch ist? Sonst hättet ihr ja (als Gläubige) schon versagt.", footnotes: ["1 Kor 11,28; Röm 8,9f"] },
      { number: 6, text: "Ich hoffe aber, ihr werdet erkennen, dass wir nicht versagt haben." },
      { number: 7, text: "Doch flehen wir zu Gott, dass ihr nichts Böses tut, nicht, damit wir gerechtfertigt erscheinen, sondern nur, damit ihr das Gute tut, wir aber wie Versager dastehen." },
      { number: 8, text: "Denn wir können unsere Kraft nicht gegen die Wahrheit einsetzen, nur für die Wahrheit." },
      { number: 9, text: "So ist es uns eine Freude, wenn wir schwach dastehen, ihr aber euch als stark erweist. Das ist es, was wir erflehen: eure vollständige Erneuerung." },
      { number: 10, text: "Deswegen schreibe ich das alles aus der Ferne, um nicht, wenn ich zu euch komme, Strenge gebrauchen zu müssen kraft der Vollmacht, die der Herr mir zum Aufbauen, nicht zum Niederreißen gegeben hat.", footnotes: ["2,3; 10,4.8.11"] },
      { number: 11, text: "Im Übrigen, liebe Brüder, freut euch, kehrt zur Ordnung zurück, lasst euch ermahnen, seid eines Sinnes und lebt in Frieden! Dann wird der Gott der Liebe und des Friedens mit euch sein.", heading: "Schlusswort und Segenswunsch: 13,11-13", footnotes: ["Phil 4,4; Röm 12,16; 15,5.33; Phil 2,2"] },
      { number: 12, text: "Grüßt einander mit dem heiligen Kuss! Es grüßen euch alle Heiligen.", footnotes: ["Röm 16,16; 1 Kor 16,20; 1 Thess 5,26"] },
      { number: 13, text: "Die Gnade Jesu Christi, des Herrn, die Liebe Gottes und die Gemeinschaft des Heiligen Geistes sei mit euch allen!", footnotes: ["Gal 6,18; Joh 14,16f; Röm 8,26f"] },
    ] },
  ],
};
