import { Book } from "@/lib/types";

export const acts: Book = {
  id: "acts",
  name: "Apostelgeschichte",
  shortName: "Apg",
  testament: "new",
  introduction: "Das Buch trägt in den griechischen Textausgaben seit dem 2. Jahrhundert den Titel »Taten der Apostel«. Es wird in ihm jedoch hauptsächlich von der Tätigkeit der Apostel Petrus und Johannes und von dem missionarischen Wirken des Paulus erzählt, darüber hinaus noch vor allem von Stephanus, Philippus, Barnabas und Jakobus. Als Verfasser gilt der altkirchlichen Tradition Lukas, dem auch das dritte Evangelium zugeschrieben wird (entstanden zwischen 80 und 90 n. Chr.), das er in Apg 1,1 sein »erstes Buch« nennt. Den unmittelbaren Anlass zur Abfassung des Werkes kennen wir nicht mehr. Lukas berichtet über die Zeit nach Ostern: wie der »Missionsbefehl« Jesu (Lk 24,47; Apg 1,8) von Jerusalem aus »in ganz Judäa und Samarien und bis an die Grenzen der Erde« ausgeführt wurde, wobei »die Grenzen der Erde« mit Rom, der Hauptstadt des römischen Reiches, erreicht sind (28,16-31). Doch wird dabei nicht alles erzählt, sondern nur wichtige Episoden, Einzelereignisse (wie die Ausgießung des Geistes an Pfingsten, die »Bekehrung« des Paulus und das »Apostelkonzil«) werden erwähnt. Breiten Raum in der Schilderung nehmen die »Missionsreisen« ein. Die Apostelgeschichte ist deshalb keine »Geschichte der Urkirche«, sondern eine »Missionschronik«, die den Weg des Evangeliums von den Juden und Samaritern zu den Heiden zeigt. Diese Missionschronik weist drei Teile auf, die geographisch orientiert sind: die Kirche in Jerusalem (1,4 - 8,3), die Kirche in Judäa und Samarien (8,4 - 12,25), die Kirche unter den Völkern (13,1 - 28,31). Ihre »Mitte« bildet die Erzählung über das »Apostelkonzil« (Apg 15), auf dem die mit Paulus und Barnabas in Jerusalem versammelten Apostel und Ältesten die Entscheidung fällen, dass die Heidenchristen sich nicht der Beschneidung und den Weisungen des jüdischen Gesetzes zu unterwerfen haben. Woher Lukas sein Wissen über das von ihm Erzählte hat, entzieht sich unserer Kenntnis. Es besteht jedoch kein Zweifel, dass er auf Überlieferungen zurückgreifen konnte; deren Umfang und Inhalt sind aber nur schwer zu bestimmen. Sicher ist jedenfalls, dass er die Briefe des Paulus bei der Abfassung der Apostelgeschichte nicht benutzt hat. Was Lukas seinen Lesern theologisch sagen will, geht vor allem aus den Reden hervor, die in der Apostelgeschichte eine wichtige Rolle spielen: Jetzt ist die Zeit der Kirche und das ist für Lukas die Zeit des Heiligen Geistes, die die Zeit des Alten Bundes abgelöst hat und die Zeit Jesu in die Gegenwart verlängert. Der Heilige Geist lenkt das Leben der Kirche und die Wege der Missionare. Die »Naherwartung« der Wiederkunft Jesu tritt deshalb zurück; jedoch ist Lukas wie die Verfasser der übrigen Schriften des Neuen Testaments überzeugt, dass die Endzeit bereits angebrochen ist. Das Heil, das Christus gebracht hat, wird von den Missionaren nochmals den Juden angeboten, aber bald auch den Heiden. Mit der Ankunft des Paulus in Rom wird endgültig sichtbar, dass das Herz des jüdischen Volkes dem Evangelium gegenüber »verstockt« bleibt und das Heil den Heiden gesandt wird, »und sie werden es hören« (28,28; vgl. auch 13,46f; 18,6). So ist ein Hauptziel der »Geschichtsschreibung« des Lukas in der Apostelgeschichte, die allmähliche Loslösung der Kirche von Israel zu beschreiben. Am Ende stehen sich Kirche und Judentum als zwei voneinander getrennte Größen gegenüber.",
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Das Vorwort: 1,1-31 Im ersten Buch, lieber Theophilus, habe ich über alles berichtet, was Jesus getan und gelehrt hat,", footnotes: ["Lk 1,1-4", "Bei dem «ersten Buch» handelt es sich um das Lukasevangelium."] },
      { number: 2, text: "bis zu dem Tag, an dem er (in den Himmel) aufgenommen wurde. Vorher hat er durch den Heiligen Geist den Aposteln, die er sich erwählt hatte, Anweisungen gegeben.", heading: "Das Vorwort: 1,1-3", footnotes: ["Lk 24,49; 1 Tim 3,16; Lk 24,44-48; Joh 20,22", "Andere Übersetzungsmöglichkeit: Vorher hat er den Aposteln, die er sich durch den Heiligen Geist erwählt hatte, Anweisungen gegeben."] },
      { number: 3, text: "Ihnen hat er nach seinem Leiden durch viele Beweise gezeigt, dass er lebt; vierzig Tage hindurch ist er ihnen erschienen und hat vom Reich Gottes gesprochen.", footnotes: ["10,41; 13,31"] },
      { number: 4, text: "Die Kirche in Jerusalem: 1,4 - 8,3Letzte Anweisungen und Belehrungen des Auferstandenen: 1,4-84 Beim gemeinsamen Mahl gebot er ihnen: Geht nicht weg von Jerusalem, sondern wartet auf die Verheißung des Vaters, die ihr von mir vernommen habt.", footnotes: ["Lk 24,49", "Beim gemeinsamen Mahl, andere Übersetzungsmöglichkeit: Als sie beisammen waren."] },
      { number: 5, text: "Johannes hat mit Wasser getauft, ihr aber werdet schon in wenigen Tagen mit dem Heiligen Geist getauft.", heading: "Letzte Anweisungen und Belehrungen des Auferstandenen: 1,4-8", footnotes: ["11,16; Mt 3,11"] },
      { number: 6, text: "Als sie nun beisammen waren, fragten sie ihn: Herr, stellst du in dieser Zeit das Reich für Israel wieder her?" },
      { number: 7, text: "Er sagte zu ihnen: Euch steht es nicht zu, Zeiten und Fristen zu erfahren, die der Vater in seiner Macht festgesetzt hat." },
      { number: 8, text: "Aber ihr werdet die Kraft des Heiligen Geistes empfangen, der auf euch herabkommen wird; und ihr werdet meine Zeugen sein in Jerusalem und in ganz Judäa und Samarien und bis an die Grenzen der Erde.", footnotes: ["2,1-13; 10,39; Mt 28,19; Mk 16,15; Lk 24,47f"] },
      { number: 9, text: "Die Himmelfahrt Jesu: 1,9-119 Als er das gesagt hatte, wurde er vor ihren Augen emporgehoben, und eine Wolke nahm ihn auf und entzog ihn ihren Blicken.", footnotes: ["Mk 16,19; Lk 24,51; 1 Tim 3,16; 1 Petr 3,22"] },
      { number: 10, text: "Während sie unverwandt ihm nach zum Himmel emporschauten, standen plötzlich zwei Männer in weißen Gewändern bei ihnen", heading: "Die Himmelfahrt Jesu: 1,9-11" },
      { number: 11, text: "und sagten: Ihr Männer von Galiläa, was steht ihr da und schaut zum Himmel empor? Dieser Jesus, der von euch ging und in den Himmel aufgenommen wurde, wird ebenso wiederkommen, wie ihr ihn habt zum Himmel hingehen sehen.", footnotes: ["Offb 1,7"] },
      { number: 12, text: "Betende Gemeinde: 1,12-1412 Dann kehrten sie vom Ölberg, der nur einen Sabbatweg von Jerusalem entfernt ist, nach Jerusalem zurück.", footnotes: ["Sabbatweg: etwa 1000 Meter - eine Wegstrecke, die man gehen durfte, ohne das Gebot der Sabbatruhe zu verletzen."] },
      { number: 13, text: "Als sie in die Stadt kamen, gingen sie in das Obergemach hinauf, wo sie nun ständig blieben: Petrus und Johannes, Jakobus und Andreas, Philippus und Thomas, Bartholomäus und Matthäus, Jakobus, der Sohn des Alphäus, und Simon, der Zelot, sowie Judas, der Sohn des Jakobus.", heading: "Betende Gemeinde: 1,12-14", footnotes: ["Lk 6,14-16", "13f: Die genannten Gruppen und Personen bilden den Kern der Urgemeinde. Mit den «Frauen» sind wohl jene gemeint, die nach Lk 8,2f; 23,49.55f zur Begleitung Jesu gehörten."] },
      { number: 14, text: "Sie alle verharrten dort einmütig im Gebet, zusammen mit den Frauen und mit Maria, der Mutter Jesu, und mit seinen Brüdern.", footnotes: ["Lk 23,49", "Vgl. die Anmerkung zu Mt 12,46f."] },
      { number: 15, text: "Die Wahl des Matthias zum Apostel: 1,15-2615 In diesen Tagen erhob sich Petrus im Kreis der Brüder - etwa hundertzwanzig waren zusammengekommen - und sagte:" },
      { number: 16, text: "Brüder! Es musste sich das Schriftwort erfüllen, das der Heilige Geist durch den Mund Davids im voraus über Judas gesprochen hat. Judas wurde zum Anführer derer, die Jesus gefangen nahmen.", heading: "Die Wahl des Matthias zum Apostel: 1,15-26", footnotes: ["Lk 22,47"] },
      { number: 17, text: "Er wurde zu uns gezählt und hatte Anteil am gleichen Dienst." },
      { number: 18, text: "Mit dem Lohn für seine Untat kaufte er sich ein Grundstück. Dann aber stürzte er vornüber zu Boden, sein Leib barst auseinander und alle Eingeweide fielen heraus.", footnotes: ["Mt 27,3-10"] },
      { number: 19, text: "Das wurde allen Einwohnern von Jerusalem bekannt; deshalb nannten sie jenes Grundstück in ihrer Sprache Hakeldamach, das heißt Blutacker." },
      { number: 20, text: "Denn es steht im Buch der Psalmen: Sein Gehöft soll veröden, niemand soll darin wohnen! und: Sein Amt soll ein anderer erhalten!", footnotes: ["Ps 69,26; 109,8"] },
      { number: 21, text: "Einer von den Männern, die die ganze Zeit mit uns zusammen waren, als Jesus, der Herr, bei uns ein und aus ging,", footnotes: ["Joh 15,27"] },
      { number: 22, text: "angefangen von der Taufe durch Johannes bis zu dem Tag, an dem er von uns ging und (in den Himmel) aufgenommen wurde, - einer von diesen muss nun zusammen mit uns Zeuge seiner Auferstehung sein." },
      { number: 23, text: "Und sie stellten zwei Männer auf: Josef, genannt Barsabbas, mit dem Beinamen Justus, und Matthias." },
      { number: 24, text: "Dann beteten sie: Herr, du kennst die Herzen aller; zeige, wen von diesen beiden du erwählt hast,", footnotes: ["15,8"] },
      { number: 25, text: "diesen Dienst und dieses Apostelamt zu übernehmen. Denn Judas hat es verlassen und ist an den Ort gegangen, der ihm bestimmt war." },
      { number: 26, text: "Dann gaben sie ihnen Lose; das Los fiel auf Matthias und er wurde den elf Aposteln zugerechnet.", footnotes: ["1 Sam 14,41f; Spr 16,33"] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Das Pfingstereignis: 2,1-131 Als der Pfingsttag gekommen war, befanden sich alle am gleichen Ort.", footnotes: ["1,14; Lev 23,15-21"] },
      { number: 2, text: "Da kam plötzlich vom Himmel her ein Brausen, wie wenn ein heftiger Sturm daherfährt, und erfüllte das ganze Haus, in dem sie waren.", heading: "Das Pfingstereignis: 2,1-13" },
      { number: 3, text: "Und es erschienen ihnen Zungen wie von Feuer, die sich verteilten; auf jeden von ihnen ließ sich eine nieder.", footnotes: ["Lk 3,16"] },
      { number: 4, text: "Alle wurden mit dem Heiligen Geist erfüllt und begannen, in fremden Sprachen zu reden, wie es der Geist ihnen eingab.", footnotes: ["4,31; 8,15.17; 10,44; 11,15; 15,8; 19,2.6", "in fremden Sprachen, wörtlich: in anderen Zungen. - Gemeint ist hier ein Reden in anderen Sprachen, das aber zugleich geistgewirktes «Zungenreden» ist; es bestand wohl vor allem in Gebetsrufen; vgl. auch Apg 10,46; 19,6; 1 Kor 12,10; 14,5."] },
      { number: 5, text: "In Jerusalem aber wohnten Juden, fromme Männer aus allen Völkern unter dem Himmel." },
      { number: 6, text: "Als sich das Getöse erhob, strömte die Menge zusammen und war ganz bestürzt; denn jeder hörte sie in seiner Sprache reden.", footnotes: ["2,11; 10,46; 19,6; Mk 16,17; 1 Kor 12,10.28.30; 14,1-39"] },
      { number: 7, text: "Sie gerieten außer sich vor Staunen und sagten: Sind das nicht alles Galiläer, die hier reden?" },
      { number: 8, text: "Wieso kann sie jeder von uns in seiner Muttersprache hören:" },
      { number: 9, text: "Parther, Meder und Elamiter, Bewohner von Mesopotamien, Judäa und Kappadozien, von Pontus und der Provinz Asien," },
      { number: 10, text: "von Phrygien und Pamphylien, von Ägypten und dem Gebiet Libyens nach Zyrene hin, auch die Römer, die sich hier aufhalten," },
      { number: 11, text: "Juden und Proselyten, Kreter und Araber, wir hören sie in unseren Sprachen Gottes große Taten verkünden." },
      { number: 12, text: "Alle gerieten außer sich und waren ratlos. Die einen sagten zueinander: Was hat das zu bedeuten?", footnotes: ["1 Kor 14,23"] },
      { number: 13, text: "Andere aber spotteten: Sie sind vom süßen Wein betrunken." },
      { number: 14, text: "Die Pfingstpredigt des Petrus: 2,14-3614 Da trat Petrus auf, zusammen mit den Elf; er erhob seine Stimme und begann zu reden: Ihr Juden und alle Bewohner von Jerusalem! Dies sollt ihr wissen, achtet auf meine Worte!" },
      { number: 15, text: "Diese Männer sind nicht betrunken, wie ihr meint; es ist ja erst die dritte Stunde am Morgen;", heading: "Die Pfingstpredigt des Petrus: 2,14-36" },
      { number: 16, text: "sondern jetzt geschieht, was durch den Propheten Joël gesagt worden ist:" },
      { number: 17, text: "In den letzten Tagen wird es geschehen, / so spricht Gott: / Ich werde von meinem Geist ausgießen / über alles Fleisch. / Eure Söhne und eure Töchter werden Propheten sein, / eure jungen Männer werden Visionen haben, / und eure Alten werden Träume haben.", footnotes: ["Joël 3,1-5"] },
      { number: 18, text: "Auch über meine Knechte und Mägde / werde ich von meinem Geist ausgießen / in jenen Tagen und sie werden Propheten sein." },
      { number: 19, text: "Ich werde Wunder erscheinen lassen droben am Himmel / und Zeichen unten auf der Erde: / Blut und Feuer und qualmenden Rauch.", footnotes: ["5,12"] },
      { number: 20, text: "Die Sonne wird sich in Finsternis verwandeln / und der Mond in Blut, / ehe der Tag des Herrn kommt, / der große und herrliche Tag." },
      { number: 21, text: "Und es wird geschehen: / Jeder, der den Namen des Herrn anruft, / wird gerettet.", footnotes: ["Röm 10,13"] },
      { number: 22, text: "Israeliten, hört diese Worte: Jesus, den Nazoräer, den Gott vor euch beglaubigt hat durch machtvolle Taten, Wunder und Zeichen, die er durch ihn in eurer Mitte getan hat, wie ihr selbst wisst -", footnotes: ["10,38; Lk 24,19", "Zu Nazoräer vgl. die Anmerkung zu Mt 2,23."] },
      { number: 23, text: "ihn, der nach Gottes beschlossenem Willen und Vorauswissen hingegeben wurde, habt ihr durch die Hand von Gesetzlosen ans Kreuz geschlagen und umgebracht.", footnotes: ["1 Thess 2,15", "Gesetzlose: Pilatus und die römischen Soldaten."] },
      { number: 24, text: "Gott aber hat ihn von den Wehen des Todes befreit und auferweckt; denn es war unmöglich, dass er vom Tod festgehalten wurde." },
      { number: 25, text: "David nämlich sagt über ihn: Ich habe den Herrn beständig vor Augen. / Er steht mir zur Rechten, ich wanke nicht.", footnotes: ["Ps 16,8-11 G"] },
      { number: 26, text: "Darum freut sich mein Herz / und frohlockt meine Zunge / und auch mein Leib wird in sicherer Hoffnung ruhen;" },
      { number: 27, text: "denn du gibst mich nicht der Unterwelt preis, / noch lässt du deinen Frommen die Verwesung schauen." },
      { number: 28, text: "Du zeigst mir die Wege zum Leben, / du erfüllst mich mit Freude vor deinem Angesicht." },
      { number: 29, text: "Brüder, ich darf freimütig zu euch über den Patriarchen David reden: Er starb und wurde begraben und sein Grabmal ist bei uns erhalten bis auf den heutigen Tag." },
      { number: 30, text: "Da er ein Prophet war und wusste, dass Gott ihm den Eid geschworen hatte, einer von seinen Nachkommen werde auf seinem Thron sitzen,", footnotes: ["Ps 132,11; 2 Sam 7,12f"] },
      { number: 31, text: "sagte er vorausschauend über die Auferstehung des Christus: Er gibt ihn nicht der Unterwelt preis und sein Leib schaut die Verwesung nicht.", footnotes: ["13,35; Ps 16,10"] },
      { number: 32, text: "Diesen Jesus hat Gott auferweckt, dafür sind wir alle Zeugen." },
      { number: 33, text: "Nachdem er durch die rechte Hand Gottes erhöht worden war und vom Vater den verheißenen Heiligen Geist empfangen hatte, hat er ihn ausgegossen, wie ihr seht und hört." },
      { number: 34, text: "David ist nicht zum Himmel aufgestiegen; vielmehr sagt er selbst: Es sprach der Herr zu meinem Herrn: / Setze dich mir zur Rechten,", footnotes: ["Ps 110,1"] },
      { number: 35, text: "und ich lege dir deine Feinde / als Schemel unter die Füße." },
      { number: 36, text: "Mit Gewissheit erkenne also das ganze Haus Israel: Gott hat ihn zum Herrn und Messias gemacht, diesen Jesus, den ihr gekreuzigt habt." },
      { number: 37, text: "Erste Bekehrungen: 2,37-4237 Als sie das hörten, traf es sie mitten ins Herz, und sie sagten zu Petrus und den übrigen Aposteln: Was sollen wir tun, Brüder?", footnotes: ["Lk 3,10"] },
      { number: 38, text: "Petrus antwortete ihnen: Kehrt um und jeder von euch lasse sich auf den Namen Jesu Christi taufen zur Vergebung seiner Sünden; dann werdet ihr die Gabe des Heiligen Geistes empfangen.", heading: "Erste Bekehrungen: 2,37-42", footnotes: ["Lk 3,3"] },
      { number: 39, text: "Denn euch und euren Kindern gilt die Verheißung und all denen in der Ferne, die der Herr, unser Gott, herbeirufen wird.", footnotes: ["Jes 57,19; Eph 2,17"] },
      { number: 40, text: "Mit noch vielen anderen Worten beschwor und ermahnte er sie: Lasst euch retten aus dieser verdorbenen Generation!", footnotes: ["Dtn 32,5; Ps 78,8; Lk 9,41; Phil 2,15"] },
      { number: 41, text: "Die nun, die sein Wort annahmen, ließen sich taufen. An diesem Tag wurden (ihrer Gemeinschaft) etwa dreitausend Menschen hinzugefügt." },
      { number: 42, text: "Sie hielten an der Lehre der Apostel fest und an der Gemeinschaft, am Brechen des Brotes und an den Gebeten.", footnotes: ["Die Wendung «Brechen des Brotes» bezeichnet die urchristliche Eucharistiefeier, die zuerst noch mit einem «Liebesmahl» (einer Agápe) verbunden war; vgl. 20,7-12."] },
      { number: 43, text: "Das Leben der jungen Gemeinde: 2,43-4743 Alle wurden von Furcht ergriffen; denn durch die Apostel geschahen viele Wunder und Zeichen.", footnotes: ["5,12"] },
      { number: 44, text: "Und alle, die gläubig geworden waren, bildeten eine Gemeinschaft und hatten alles gemeinsam.", heading: "Das Leben der jungen Gemeinde: 2,43-47", footnotes: ["4,32-35"] },
      { number: 45, text: "Sie verkauften Hab und Gut und gaben davon allen, jedem so viel, wie er nötig hatte." },
      { number: 46, text: "Tag für Tag verharrten sie einmütig im Tempel, brachen in ihren Häusern das Brot und hielten miteinander Mahl in Freude und Einfalt des Herzens.", footnotes: ["Lk 24,53"] },
      { number: 47, text: "Sie lobten Gott und waren beim ganzen Volk beliebt. Und der Herr fügte täglich ihrer Gemeinschaft die hinzu, die gerettet werden sollten." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Die Heilung des Gelähmten im Tempel: 3,1-101 Petrus und Johannes gingen um die neunte Stunde zum Gebet in den Tempel hinauf." },
      { number: 2, text: "Da wurde ein Mann herbeigetragen, der von Geburt an gelähmt war. Man setzte ihn täglich an das Tor des Tempels, das man die Schöne Pforte nennt; dort sollte er bei denen, die in den Tempel gingen, um Almosen betteln.", heading: "Die Heilung des Gelähmten im Tempel: 3,1-10", footnotes: ["(2-8) 14,8-10"] },
      { number: 3, text: "Als er nun Petrus und Johannes in den Tempel gehen sah, bat er sie um ein Almosen." },
      { number: 4, text: "Petrus und Johannes blickten ihn an und Petrus sagte: Sieh uns an!" },
      { number: 5, text: "Da wandte er sich ihnen zu und erwartete, etwas von ihnen zu bekommen." },
      { number: 6, text: "Petrus aber sagte: Silber und Gold besitze ich nicht. Doch was ich habe, das gebe ich dir: Im Namen Jesu Christi, des Nazoräers, geh umher!" },
      { number: 7, text: "Und er fasste ihn an der rechten Hand und richtete ihn auf. Sogleich kam Kraft in seine Füße und Gelenke;" },
      { number: 8, text: "er sprang auf, konnte stehen und ging umher. Dann ging er mit ihnen in den Tempel, lief und sprang umher und lobte Gott." },
      { number: 9, text: "Alle Leute sahen ihn umhergehen und Gott loben." },
      { number: 10, text: "Sie erkannten ihn als den, der gewöhnlich an der Schönen Pforte des Tempels saß und bettelte. Und sie waren voll Verwunderung und Staunen über das, was mit ihm geschehen war." },
      { number: 11, text: "Die Rede des Petrus auf dem Tempelplatz: 3,11-2611 Da er sich Petrus und Johannes anschloss, lief das ganze Volk bei ihnen in der sogenannten Halle Salomos zusammen, außer sich vor Staunen.", footnotes: ["5,12; Joh 10,23"] },
      { number: 12, text: "Als Petrus das sah, wandte er sich an das Volk: Israeliten, was wundert ihr euch darüber? Was starrt ihr uns an, als hätten wir aus eigener Kraft oder Frömmigkeit bewirkt, dass dieser gehen kann?", heading: "Die Rede des Petrus auf dem Tempelplatz: 3,11-26" },
      { number: 13, text: "Der Gott Abrahams, Isaaks und Jakobs, der Gott unserer Väter, hat seinen Knecht Jesus verherrlicht, den ihr verraten und vor Pilatus verleugnet habt, obwohl dieser entschieden hatte, ihn freizulassen.", footnotes: ["Ex 3,6.15; Jes 52,13; Lk 23,19-25"] },
      { number: 14, text: "Ihr aber habt den Heiligen und Gerechten verleugnet und die Freilassung eines Mörders gefordert.", footnotes: ["Der «Mörder» ist Barabbas; vgl. auch Lk 23,19.25."] },
      { number: 15, text: "Den Urheber des Lebens habt ihr getötet, aber Gott hat ihn von den Toten auferweckt. Dafür sind wir Zeugen.", footnotes: ["4,10; 5,31; Hebr 2,10"] },
      { number: 16, text: "Und weil er an seinen Namen geglaubt hat, hat dieser Name den Mann hier, den ihr seht und kennt, zu Kräften gebracht; der Glaube, der durch ihn kommt, hat ihm vor euer aller Augen die volle Gesundheit geschenkt." },
      { number: 17, text: "Nun, Brüder, ich weiß, ihr habt aus Unwissenheit gehandelt, ebenso wie eure Führer.", footnotes: ["13,27; Lk 23,34; Joh 16,3; 1 Tim 1,13", "Die «Unwissenheit» der Juden besteht darin, dass sie Jesus nicht als Messias erkannten und anerkannten."] },
      { number: 18, text: "Gott aber hat auf diese Weise erfüllt, was er durch den Mund aller Propheten im voraus verkündigt hat: dass sein Messias leiden werde.", footnotes: ["Lk 18,31"] },
      { number: 19, text: "Also kehrt um und tut Buße, damit eure Sünden getilgt werden", footnotes: ["2,38; Lk 3,3"] },
      { number: 20, text: "und der Herr Zeiten des Aufatmens kommen lässt und Jesus sendet als den für euch bestimmten Messias.", footnotes: ["Mt 19,28"] },
      { number: 21, text: "Ihn muss freilich der Himmel aufnehmen bis zu den Zeiten der Wiederherstellung von allem, die Gott von jeher durch den Mund seiner heiligen Propheten verkündet hat.", footnotes: ["Lk 21,27f"] },
      { number: 22, text: "Mose hat gesagt: Einen Propheten wie mich wird euch der Herr, euer Gott, aus euren Brüdern erwecken. Auf ihn sollt ihr hören in allem, was er zu euch sagt.", footnotes: ["Dtn 18,15.18; Apg 7,37; Joh 1,21; 6,14"] },
      { number: 23, text: "Jeder, der auf jenen Propheten nicht hört, wird aus dem Volk ausgemerzt werden.", footnotes: ["Lev 23,29; Dtn 18,19"] },
      { number: 24, text: "Und auch alle Propheten von Samuel an und alle, die später auftraten, haben diese Tage angekündigt." },
      { number: 25, text: "Ihr seid die Söhne der Propheten und des Bundes, den Gott mit euren Vätern geschlossen hat, als er zu Abraham sagte: Durch deinen Nachkommen sollen alle Geschlechter der Erde Segen erlangen.", footnotes: ["Gen 12,3; 18,18; 22,18; 28,4; Gal 3,8"] },
      { number: 26, text: "Für euch zuerst hat Gott seinen Knecht erweckt und gesandt, damit er euch segnet und jeden von seiner Bosheit abbringt." },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Petrus und Johannes vor dem Hohen Rat: 4,1-221 Während sie zum Volk redeten, traten die Priester, der Tempelhauptmann und die Sadduzäer zu ihnen." },
      { number: 2, text: "Sie waren aufgebracht, weil die Apostel das Volk lehrten und in Jesus die Auferstehung von den Toten verkündeten.", heading: "Petrus und Johannes vor dem Hohen Rat: 4,1-22", footnotes: ["23,8"] },
      { number: 3, text: "Sie nahmen sie fest und hielten sie bis zum nächsten Morgen in Haft. Es war nämlich schon Abend." },
      { number: 4, text: "Viele aber, die das Wort gehört hatten, wurden gläubig; und die Zahl der Männer stieg auf etwa fünftausend." },
      { number: 5, text: "Am anderen Morgen versammelten sich ihre Führer sowie die Ältesten und die Schriftgelehrten in Jerusalem," },
      { number: 6, text: "dazu Hannas, der Hohepriester, Kajaphas, Johannes, Alexander und alle, die aus dem Geschlecht der Hohenpriester stammten.", footnotes: ["Lk 3,2", "Hannas war von 6 bis 15 n. Chr., Kajaphas von 18 bis 37 n. Chr. Hoherpriester."] },
      { number: 7, text: "Sie stellten die beiden in die Mitte und fragten sie: Mit welcher Kraft oder in wessen Namen habt ihr das getan?", footnotes: ["Lk 20,2"] },
      { number: 8, text: "Da sagte Petrus zu ihnen, erfüllt vom Heiligen Geist: Ihr Führer des Volkes und ihr Ältesten!" },
      { number: 9, text: "Wenn wir heute wegen einer guten Tat an einem kranken Menschen darüber vernommen werden, durch wen er geheilt worden ist," },
      { number: 10, text: "so sollt ihr alle und das ganze Volk Israel wissen: im Namen Jesu Christi, des Nazoräers, den ihr gekreuzigt habt und den Gott von den Toten auferweckt hat. Durch ihn steht dieser Mann gesund vor euch." },
      { number: 11, text: "Er (Jesus) ist der Stein, der von euch Bauleuten verworfen wurde, der aber zum Eckstein geworden ist.", footnotes: ["Ps 118,22; Lk 20,17; Eph 2,20; 1 Petr 2,4-8"] },
      { number: 12, text: "Und in keinem anderen ist das Heil zu finden. Denn es ist uns Menschen kein anderer Name unter dem Himmel gegeben, durch den wir gerettet werden sollen.", footnotes: ["Mt 1,21; 1 Kor 3,11"] },
      { number: 13, text: "Als sie den Freimut des Petrus und des Johannes sahen und merkten, dass es ungelehrte und einfache Leute waren, wunderten sie sich. Sie erkannten sie als Jünger Jesu," },
      { number: 14, text: "sahen aber auch, dass der Geheilte bei ihnen stand; so konnten sie nichts dagegen sagen." },
      { number: 15, text: "Sie befahlen ihnen, den Hohen Rat zu verlassen; dann berieten sie miteinander" },
      { number: 16, text: "und sagten: Was sollen wir mit diesen Leuten anfangen? Dass offensichtlich ein Wunder durch sie geschehen ist, ist allen Einwohnern von Jerusalem bekannt; wir können es nicht abstreiten." },
      { number: 17, text: "Damit aber die Sache nicht weiter im Volk verbreitet wird, wollen wir ihnen bei Strafe verbieten, je wieder in diesem Namen zu irgendeinem Menschen zu sprechen.", footnotes: ["5,28"] },
      { number: 18, text: "Und sie riefen sie herein und verboten ihnen, jemals wieder im Namen Jesu zu predigen und zu lehren." },
      { number: 19, text: "Doch Petrus und Johannes antworteten ihnen: Ob es vor Gott recht ist, mehr auf euch zu hören als auf Gott, das entscheidet selbst.", footnotes: ["5,29-32"] },
      { number: 20, text: "Wir können unmöglich schweigen über das, was wir gesehen und gehört haben." },
      { number: 21, text: "Jene aber drohten ihnen noch mehr und ließen sie dann gehen; denn sie sahen keine Möglichkeit, sie zu bestrafen, mit Rücksicht auf das Volk, da alle Gott wegen des Geschehenen priesen." },
      { number: 22, text: "Denn der Mann, an dem das Wunder der Heilung geschah, war über vierzig Jahre alt." },
      { number: 23, text: "Gebet der Urgemeinde um Furchtlosigkeit: 4,23-3123 Nach ihrer Freilassung gingen sie zu den Ihren und berichteten alles, was die Hohenpriester und die Ältesten zu ihnen gesagt hatten." },
      { number: 24, text: "Als sie das hörten, erhoben sie einmütig ihre Stimme zu Gott und sprachen: Herr, du hast den Himmel, die Erde und das Meer geschaffen und alles, was dazugehört;", heading: "Gebet der Urgemeinde um Furchtlosigkeit: 4,23-31", footnotes: ["Ps 146,6; Ex 20,11"] },
      { number: 25, text: "du hast durch den Mund unseres Vaters David, deines Knechtes, durch den Heiligen Geist gesagt: Warum toben die Völker, / warum machen die Nationen vergebliche Pläne?", footnotes: ["Ps 2,1f"] },
      { number: 26, text: "Die Könige der Erde stehen auf / und die Herrscher haben sich verbündet / gegen den Herrn und seinen Gesalbten." },
      { number: 27, text: "Wahrhaftig, verbündet haben sich in dieser Stadt gegen deinen heiligen Knecht Jesus, den du gesalbt hast, Herodes und Pontius Pilatus mit den Heiden und den Stämmen Israels,", footnotes: ["Ps 2,2; Lk 23,12f", "Der hier erwähnte Herodes ist Herodes Antipas, ein Sohn Herodes des Großen, der von 4 v. Chr. bis 39 n. Chr. Tetrarch von Galiläa und Peräa war (vgl. die Anmerkung zu Lk 3,1)."] },
      { number: 28, text: "um alles auszuführen, was deine Hand und dein Wille im voraus bestimmt haben." },
      { number: 29, text: "Doch jetzt, Herr, sieh auf ihre Drohungen und gib deinen Knechten die Kraft, mit allem Freimut dein Wort zu verkünden." },
      { number: 30, text: "Streck deine Hand aus, damit Heilungen und Zeichen und Wunder geschehen durch den Namen deines heiligen Knechtes Jesus." },
      { number: 31, text: "Als sie gebetet hatten, bebte der Ort, an dem sie versammelt waren, und alle wurden mit dem Heiligen Geist erfüllt und sie verkündeten freimütig das Wort Gottes.", footnotes: ["2,44f"] },
      { number: 32, text: "Die Gütergemeinschaft der Urgemeinde: 4,32-3732 Die Gemeinde der Gläubigen war ein Herz und eine Seele. Keiner nannte etwas von dem, was er hatte, sein Eigentum, sondern sie hatten alles gemeinsam.", footnotes: ["32-37: Die Gütergemeinschaft der Urgemeinde war freiwillig; sie brachte den Geist der Brüderlichkeit zum Ausdruck."] },
      { number: 33, text: "Mit großer Kraft legten die Apostel Zeugnis ab von der Auferstehung Jesu, des Herrn, und reiche Gnade ruhte auf ihnen allen.", heading: "Die Gütergemeinschaft der Urgemeinde: 4,32-37", footnotes: ["2,22-24"] },
      { number: 34, text: "Es gab auch keinen unter ihnen, der Not litt. Denn alle, die Grundstücke oder Häuser besaßen, verkauften ihren Besitz, brachten den Erlös", footnotes: ["Lk 12,33; Apg 2,44f"] },
      { number: 35, text: "und legten ihn den Aposteln zu Füßen. Jedem wurde davon so viel zugeteilt, wie er nötig hatte." },
      { number: 36, text: "Auch Josef, ein Levit aus Zypern, der von den Aposteln Barnabas, das heißt übersetzt Sohn des Trostes, genannt wurde,", footnotes: ["9,27; 11,22.30; 12,25; 13 - 15; 1 Kor 9,6; Gal 2,1.9.13; Kol 4,10"] },
      { number: 37, text: "verkaufte einen Acker, der ihm gehörte, brachte das Geld und legte es den Aposteln zu Füßen." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Der Betrug des Hananias und der Saphira: 5,1-111 Ein Mann namens Hananias aber und seine Frau Saphira verkauften zusammen ein Grundstück," },
      { number: 2, text: "und mit Einverständnis seiner Frau behielt er etwas von dem Erlös für sich. Er brachte nur einen Teil und legte ihn den Aposteln zu Füßen.", heading: "Der Betrug des Hananias und der Saphira: 5,1-11" },
      { number: 3, text: "Da sagte Petrus: Hananias, warum hat der Satan dein Herz erfüllt, dass du den Heiligen Geist belügst und von dem Erlös des Grundstücks etwas für dich behältst?", footnotes: ["Lk 22,3; Joh 13,2"] },
      { number: 4, text: "Hätte es nicht dein Eigentum bleiben können und konntest du nicht auch nach dem Verkauf frei über den Erlös verfügen? Warum hast du in deinem Herzen beschlossen, so etwas zu tun? Du hast nicht Menschen belogen, sondern Gott." },
      { number: 5, text: "Als Hananias diese Worte hörte, stürzte er zu Boden und starb. Und über alle, die es hörten, kam große Furcht." },
      { number: 6, text: "Die jungen Männer standen auf, hüllten ihn ein, trugen ihn hinaus und begruben ihn." },
      { number: 7, text: "Nach etwa drei Stunden kam seine Frau herein, ohne zu wissen, was geschehen war." },
      { number: 8, text: "Petrus fragte sie: Sag mir, habt ihr das Grundstück für so viel verkauft? Sie antwortete: Ja, für so viel." },
      { number: 9, text: "Da sagte Petrus zu ihr: Warum seid ihr übereingekommen, den Geist des Herrn auf die Probe zu stellen? Siehe, die Füße derer, die deinen Mann begraben haben, stehen vor der Tür; auch dich wird man hinaustragen." },
      { number: 10, text: "Im selben Augenblick brach sie vor seinen Füßen zusammen und starb. Die jungen Männer kamen herein, fanden sie tot, trugen sie hinaus und begruben sie neben ihrem Mann." },
      { number: 11, text: "Da kam große Furcht über die ganze Gemeinde und über alle, die davon hörten." },
      { number: 12, text: "Zeichen und Wunder der Apostel: 5,12-1612 Durch die Hände der Apostel geschahen viele Zeichen und Wunder im Volk. Alle kamen einmütig in der Halle Salomos zusammen.", footnotes: ["2,43; Röm 15,19; 2 Kor 12,12; Apg 3,11", "Die Halle Salomos befand sich an der Ostseite des Tempelplatzes (vgl. die Anmerkung zu Joh 10,23)."] },
      { number: 13, text: "Von den übrigen wagte niemand, sich ihnen anzuschließen; aber das Volk schätzte sie hoch.", heading: "Zeichen und Wunder der Apostel: 5,12-16" },
      { number: 14, text: "Immer mehr wurden im Glauben zum Herrn geführt, Scharen von Männern und Frauen." },
      { number: 15, text: "Selbst die Kranken trug man auf die Straßen hinaus und legte sie auf Betten und Bahren, damit, wenn Petrus vorüberkam, wenigstens sein Schatten auf einen von ihnen fiel.", footnotes: ["19,12; Mk 6,56"] },
      { number: 16, text: "Auch aus den Nachbarstädten Jerusalems strömten die Leute zusammen und brachten Kranke und von unreinen Geistern Geplagte mit. Und alle wurden geheilt.", footnotes: ["Lk 4,40f"] },
      { number: 17, text: "Verhaftung und Befreiung der Apostel: 5,17-21a17 Da erhoben sich voll Eifersucht der Hohepriester und alle, die auf seiner Seite standen, nämlich die Gruppe der Sadduzäer.", footnotes: ["4,1-3"] },
      { number: 18, text: "Sie ließen die Apostel verhaften und in das öffentliche Gefängnis werfen.", heading: "Verhaftung und Befreiung der Apostel: 5,17-21a" },
      { number: 19, text: "Ein Engel des Herrn aber öffnete nachts die Gefängnistore, führte sie heraus und sagte:", footnotes: ["12,7-10"] },
      { number: 20, text: "Geht, tretet im Tempel auf und verkündet dem Volk alle Worte dieses Lebens!", footnotes: ["13,26"] },
      { number: 21, text: "Sie gehorchten und gingen bei Tagesanbruch in den Tempel und lehrten." },
      { number: 21, text: "Die Apostel vor dem Hohen Rat: 5,21b-4221b Währenddessen kam der Hohepriester mit seinen Begleitern. Sie riefen den Hohen Rat und alle Ältesten der Söhne Israels zusammen; man schickte Boten zum Gefängnis, um die Apostel vorführen zu lassen." },
      { number: 22, text: "Die Diener gingen, fanden sie aber nicht im Gefängnis. Sie kehrten zurück und meldeten:", heading: "Die Apostel vor dem Hohen Rat: 5,21b-42" },
      { number: 23, text: "Wir fanden das Gefängnis sorgfältig verschlossen und die Wachen vor den Toren stehen; als wir aber öffneten, fanden wir niemand darin." },
      { number: 24, text: "Der Tempelhauptmann und die Hohenpriester waren ratlos, als sie das hörten, und wussten nicht, was nun werden sollte." },
      { number: 25, text: "Da kam jemand und meldete ihnen: Die Männer, die ihr ins Gefängnis geworfen habt, stehen im Tempel und lehren das Volk." },
      { number: 26, text: "Da ging der Tempelhauptmann mit seinen Leuten hin und holte sie, allerdings nicht mit Gewalt; denn sie fürchteten, vom Volk gesteinigt zu werden.", footnotes: ["Lk 20,19"] },
      { number: 27, text: "Man führte sie herbei und stellte sie vor den Hohen Rat. Der Hohepriester verhörte sie" },
      { number: 28, text: "und sagte: Wir haben euch streng verboten, in diesem Namen zu lehren; ihr aber habt Jerusalem mit eurer Lehre erfüllt; ihr wollt das Blut dieses Menschen über uns bringen.", footnotes: ["Mt 27,25"] },
      { number: 29, text: "Petrus und die Apostel antworteten: Man muss Gott mehr gehorchen als den Menschen.", footnotes: ["4,19"] },
      { number: 30, text: "Der Gott unserer Väter hat Jesus auferweckt, den ihr ans Holz gehängt und ermordet habt.", footnotes: ["Dtn 21,22 G; Apg 2,23f"] },
      { number: 31, text: "Ihn hat Gott als Herrscher und Retter an seine rechte Seite erhoben, um Israel die Umkehr und Vergebung der Sünden zu schenken.", footnotes: ["2,38"] },
      { number: 32, text: "Zeugen dieser Ereignisse sind wir und der Heilige Geist, den Gott allen verliehen hat, die ihm gehorchen.", footnotes: ["Lk 24,48; Joh 15,26f"] },
      { number: 33, text: "Als sie das hörten, gerieten sie in Zorn und beschlossen, sie zu töten." },
      { number: 34, text: "Da erhob sich im Hohen Rat ein Pharisäer namens Gamaliël, ein beim ganzen Volk angesehener Gesetzeslehrer; er ließ die Apostel für kurze Zeit hinausführen.", footnotes: ["22,3", "Es handelt sich um Gamaliël den Älteren, den Lehrer des Apostels Paulus (vgl. 22,3)."] },
      { number: 35, text: "Dann sagte er: Israeliten, überlegt euch gut, was ihr mit diesen Leuten tun wollt." },
      { number: 36, text: "Vor einiger Zeit nämlich trat Theudas auf und behauptete, er sei etwas Besonderes. Ihm schlossen sich etwa vierhundert Männer an. Aber er wurde getötet und sein ganzer Anhang wurde zerstreut und aufgerieben.", footnotes: ["36f: Über Theudas, Judas den Galiläer und die Volkszählung (Zensus) berichtet genauer der zeitgenössische jüdische Geschichtsschreiber Flavius Josephus."] },
      { number: 37, text: "Nach ihm trat in den Tagen der Volkszählung Judas, der Galiläer, auf; er brachte viel Volk hinter sich und verleitete es zum Aufruhr. Auch er kam um und alle seine Anhänger wurden zerstreut.", footnotes: ["Lk 2,2"] },
      { number: 38, text: "Darum rate ich euch jetzt: Lasst von diesen Männern ab und gebt sie frei; denn wenn dieses Vorhaben oder dieses Werk von Menschen stammt, wird es zerstört werden;" },
      { number: 39, text: "stammt es aber von Gott, so könnt ihr sie nicht vernichten; sonst werdet ihr noch als Kämpfer gegen Gott dastehen. Sie stimmten ihm zu," },
      { number: 40, text: "riefen die Apostel herein und ließen sie auspeitschen; dann verboten sie ihnen, im Namen Jesu zu predigen, und ließen sie frei.", footnotes: ["22,19; 4,17"] },
      { number: 41, text: "Sie aber gingen weg vom Hohen Rat und freuten sich, dass sie gewürdigt worden waren, für seinen Namen Schmach zu erleiden.", footnotes: ["Lk 6,22f; 1 Petr 4,13"] },
      { number: 42, text: "Und Tag für Tag lehrten sie unermüdlich im Tempel und in den Häusern und verkündeten das Evangelium von Jesus, dem Christus.", footnotes: ["2,46; 5,20f.25; 8,35; 19,4f"] },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Die Wahl der Sieben: 6,1-71 In diesen Tagen, als die Zahl der Jünger zunahm, begehrten die Hellenisten gegen die Hebräer auf, weil ihre Witwen bei der täglichen Versorgung übersehen wurden.", footnotes: ["4,34f", "1-7: Die Sieben sind nicht «Diakone» im Sinn des späteren kirchlichen Amtes. Philippus wird in 21,8 als «Evangelist» bezeichnet. Die «Hellenisten» sind griechisch sprechende, die «Hebräer» aramäisch sprechende Judenchristen."] },
      { number: 2, text: "Da riefen die Zwölf die ganze Schar der Jünger zusammen und erklärten: Es ist nicht recht, dass wir das Wort Gottes vernachlässigen und uns dem Dienst an den Tischen widmen.", heading: "Die Wahl der Sieben: 6,1-7" },
      { number: 3, text: "Brüder, wählt aus eurer Mitte sieben Männer von gutem Ruf und voll Geist und Weisheit; ihnen werden wir diese Aufgabe übertragen.", footnotes: ["1 Tim 3,7f"] },
      { number: 4, text: "Wir aber wollen beim Gebet und beim Dienst am Wort bleiben." },
      { number: 5, text: "Der Vorschlag fand den Beifall der ganzen Gemeinde, und sie wählten Stephanus, einen Mann, erfüllt vom Glauben und vom Heiligen Geist, ferner Philippus und Prochorus, Nikanor und Timon, Parmenas und Nikolaus, einen Proselyten aus Antiochia.", footnotes: ["Ein «Proselyt» ist ein Heide, der voll zum Judentum übergetreten ist. Davon unterschieden sind die «Gottesfürchtigen», die sich dem Judentum in lockerer Form angeschlossen haben (vgl. 10,2; 11,18; 13,16.26; 16,14; 17,4.17; 18,7)."] },
      { number: 6, text: "Sie ließen sie vor die Apostel hintreten und diese beteten und legten ihnen die Hände auf.", footnotes: ["13,3; 14,23; 1 Tim 4,14; 2 Tim 1,6"] },
      { number: 7, text: "Und das Wort Gottes breitete sich aus und die Zahl der Jünger in Jerusalem wurde immer größer; auch eine große Anzahl von den Priestern nahm gehorsam den Glauben an.", footnotes: ["19,20; 4,4"] },
      { number: 8, text: "Die Verhaftung des Stephanus: 6,8-158 Stephanus aber, voll Gnade und Kraft, tat Wunder und große Zeichen unter dem Volk." },
      { number: 9, text: "Doch einige von der sogenannten Synagoge der Libertiner und Zyrenäer und Alexandriner und Leute aus Zilizien und der Provinz Asien erhoben sich, um mit Stephanus zu streiten;", heading: "Die Verhaftung des Stephanus: 6,8-15", footnotes: ["«Libertiner» bezeichnet hier freigelassene jüdische Sklaven."] },
      { number: 10, text: "aber sie konnten der Weisheit und dem Geist, mit dem er sprach, nicht widerstehen.", footnotes: ["Lk 21,15"] },
      { number: 11, text: "Da stifteten sie Männer zu der Aussage an: Wir haben gehört, wie er gegen Mose und Gott lästerte." },
      { number: 12, text: "Sie hetzten das Volk, die Ältesten und die Schriftgelehrten auf, drangen auf ihn ein, packten ihn und schleppten ihn vor den Hohen Rat." },
      { number: 13, text: "Und sie brachten falsche Zeugen bei, die sagten: Dieser Mensch hört nicht auf, gegen diesen heiligen Ort und das Gesetz zu reden.", footnotes: ["Mt 26,59; Mk 14,55"] },
      { number: 14, text: "Wir haben ihn nämlich sagen hören: Dieser Jesus, der Nazoräer, wird diesen Ort zerstören und die Bräuche ändern, die uns Mose überliefert hat.", footnotes: ["Mt 26,61; 27,40; Mk 14,58; 15,29; Joh 2,19"] },
      { number: 15, text: "Und als alle, die im Hohen Rat saßen, auf ihn blickten, erschien ihnen sein Gesicht wie das Gesicht eines Engels." },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Die Rede des Stephanus: 7,1-531 Der Hohepriester aber fragte: Ist das wahr?" },
      { number: 2, text: "Stephanus antwortete: Brüder und Väter, hört mich an! Der Gott der Herrlichkeit erschien unserem Vater Abraham, als er in Mesopotamien lebte, ehe er sich in Haran niederließ,", heading: "Die Rede des Stephanus: 7,1-53", footnotes: ["Gen 11,31; 15,7"] },
      { number: 3, text: "und sagte zu ihm: Zieh weg aus deinem Land und aus deiner Verwandtschaft und geh in das Land, das ich dir zeigen werde.", footnotes: ["Gen 12,1"] },
      { number: 4, text: "Da zog er aus dem Land der Chaldäer fort und ließ sich in Haran nieder. Von dort ließ Gott ihn nach dem Tod seines Vaters in dieses Land übersiedeln, in dem ihr jetzt wohnt." },
      { number: 5, text: "Er hat ihm darin kein Erbteil gegeben, auch nicht einen Fußbreit, doch hat er verheißen, das Land ihm und seinen Nachkommen zum Besitz zu geben, obwohl er kinderlos war.", footnotes: ["Dtn 2,5; Gen 12,7; 13,15; 17,8"] },
      { number: 6, text: "So sprach Gott: Seine Nachkommen werden als Fremde in einem Land wohnen, das ihnen nicht gehört; und man wird sie zu Sklaven machen und sie vierhundert Jahre lang hart behandeln.", footnotes: ["Gen 15,13f"] },
      { number: 7, text: "Aber auch über das Volk, dem sie als Sklaven dienen, werde ich Gericht halten, sprach Gott, und nachher werden sie ausziehen und mich an diesem Ort verehren.", footnotes: ["Ex 3,12"] },
      { number: 8, text: "Und er gab ihm den Bund der Beschneidung. So wurde Abraham der Vater Isaaks und beschnitt ihn am achten Tag, ebenso Isaak den Jakob, und Jakob die zwölf Patriarchen.", footnotes: ["Gen 21,4; 17,10"] },
      { number: 9, text: "Die Patriarchen aber waren eifersüchtig auf Josef und verkauften ihn nach Ägypten; doch Gott war mit ihm.", footnotes: ["Gen 37,11.28; 39,2"] },
      { number: 10, text: "Er rettete ihn aus allen seinen Nöten, schenkte ihm Weisheit und die Gunst des Pharao, des Königs von Ägypten, und er bestellte ihn zum Herrscher über Ägypten und über sein ganzes Haus.", footnotes: ["Gen 39,21; 41,37-44; Ps 105,21; Weish 10,14"] },
      { number: 11, text: "Es kam aber eine Hungersnot über ganz Ägypten und Kanaan und das Elend war groß. Auch unsere Väter hatten keine Nahrung mehr.", footnotes: ["Gen 41,54"] },
      { number: 12, text: "Als Jakob hörte, dass es in Ägypten Getreide gab, schickte er unsere Väter ein erstes Mal dorthin.", footnotes: ["Gen 42,2.5"] },
      { number: 13, text: "Beim zweiten Mal gab Josef sich seinen Brüdern zu erkennen und dem Pharao wurde Josefs Herkunft bekannt.", footnotes: ["Gen 45,3.16"] },
      { number: 14, text: "Josef aber ließ seinen Vater Jakob und seine ganze Familie holen: fünfundsiebzig Menschen.", footnotes: ["Gen 45,9-11; 46,27; Ex 1,5; Dtn 10,22"] },
      { number: 15, text: "So zog Jakob nach Ägypten hinab; und er starb und auch unsere Väter starben.", footnotes: ["Gen 46,1; 49,33"] },
      { number: 16, text: "Man brachte sie nach Sichem und bestattete sie in dem Grab, das Abraham von den Söhnen Hamors in Sichem für Silbergeld gekauft hatte.", footnotes: ["Gen 23,16f; 33,19; 50,13"] },
      { number: 17, text: "Als aber die Zeit der Verheißung herankam, die Gott dem Abraham zugesagt hatte, vermehrte sich das Volk und breitete sich in Ägypten aus,", footnotes: ["Ex 1,7-9.22"] },
      { number: 18, text: "bis ein anderer über Ägypten König wurde, der von Josef nichts wusste." },
      { number: 19, text: "Er ging gegen unser Volk heimtückisch vor und zwang unsere Väter, ihre Kinder auszusetzen; sie sollten nicht am Leben bleiben." },
      { number: 20, text: "In dieser Zeit wurde Mose geboren und Gott hatte Gefallen an ihm. Drei Monate lang wurde er im Haus seines Vaters aufgezogen;", footnotes: ["Ex 2,2; Hebr 11,23"] },
      { number: 21, text: "als er aber ausgesetzt wurde, nahm ihn die Tochter des Pharao auf und erzog ihn als ihren Sohn.", footnotes: ["Ex 2,3.5.10"] },
      { number: 22, text: "Und Mose wurde in aller Weisheit der Ägypter ausgebildet und er war mächtig in Wort und Tat." },
      { number: 23, text: "Als er vierzig Jahre alt war, reifte in ihm der Gedanke, nach seinen Brüdern, den Söhnen Israels, zu sehen.", footnotes: ["Ex 2,11"] },
      { number: 24, text: "Und als er sah, wie einem von ihnen Unrecht geschah, kam er dem Unterdrückten zu Hilfe und rächte ihn, indem er den Ägypter erschlug." },
      { number: 25, text: "Er dachte, seine Brüder würden begreifen, dass Gott ihnen durch seine Hand Rettung bringen wolle; doch sie begriffen es nicht." },
      { number: 26, text: "Am folgenden Tag kam er dazu, wie sie sich stritten; er versuchte, sie auszusöhnen und Frieden zu stiften, und sagte: Männer, ihr seid doch Brüder. Warum tut ihr einander Unrecht?", footnotes: ["Ex 2,13"] },
      { number: 27, text: "Der Mann aber, der seinem Nächsten Unrecht getan hatte, stieß ihn weg und sagte: Wer hat dich zum Anführer und Schiedsrichter über uns bestellt?", footnotes: ["Ex 2,14 G"] },
      { number: 28, text: "Willst du mich etwa umbringen, wie du gestern den Ägypter umgebracht hast?" },
      { number: 29, text: "Daraufhin floh Mose und hielt sich als Fremder in Midian auf; dort wurden ihm zwei Söhne geboren.", footnotes: ["Ex 2,15.22"] },
      { number: 30, text: "Als vierzig Jahre vergangen waren, erschien ihm in der Wüste beim Berg Sinai ein Engel im Feuer eines brennenden Dornbusches.", footnotes: ["Ex 3,2f"] },
      { number: 31, text: "Als Mose die Erscheinung sah, wunderte er sich darüber. Er ging näher hin, um sie genauer zu betrachten. Da ertönte die Stimme des Herrn:" },
      { number: 32, text: "Ich bin der Gott deiner Väter, der Gott Abrahams, Isaaks und Jakobs. Mose begann zu zittern und wagte nicht hinzusehen.", footnotes: ["Ex 3,6"] },
      { number: 33, text: "Da sagte der Herr zu ihm: Zieh deine Schuhe aus! Denn der Ort, wo du stehst, ist heiliger Boden.", footnotes: ["Ex 3,5"] },
      { number: 34, text: "Ich habe das Elend meines Volkes in Ägypten gesehen und seine Klage gehört. Ich bin herabgestiegen, um sie zu retten. Und jetzt geh, ich sende dich nach Ägypten.", footnotes: ["Ex 3,7f.10"] },
      { number: 35, text: "Diesen Mose, den sie verleugnet hatten mit den Worten: Wer hat dich zum Anführer und Schiedsrichter bestellt?, ihn hat Gott als Anführer und Befreier gesandt durch die Hand des Engels, der ihm im Dornbusch erschien.", footnotes: ["Ex 2,14 G"] },
      { number: 36, text: "Dieser Mose hat sie herausgeführt, indem er Zeichen und Wunder tat in Ägypten und im Roten Meer und in der Wüste, vierzig Jahre lang.", footnotes: ["Ex 7,3.10; 14,21; Num 14,33"] },
      { number: 37, text: "Dies ist der Mose, der zu den Söhnen Israels gesagt hat: Einen Propheten wie mich wird Gott euch aus euren Brüdern erwecken.", footnotes: ["Dtn 18,15; Apg 3,22; Joh 1,21; 6,14"] },
      { number: 38, text: "Dieser stand bei der Versammlung des Volkes in der Wüste zwischen dem Engel, der mit ihm auf dem Berg Sinai redete, und unseren Vätern. Er hat Worte des Lebens empfangen, um sie uns zu geben.", footnotes: ["Ex 19,3; Gal 3,19; Ex 31,18; Dtn 9,10; 2 Kor 3,3"] },
      { number: 39, text: "Aber unsere Väter wollten sich ihm nicht unterordnen; sie wiesen ihn ab und wandten ihr Herz nach Ägypten zurück." },
      { number: 40, text: "Sie sagten zu Aaron: Mach uns Götter, die vor uns herziehen! Denn dieser Mose, der uns aus Ägypten herausgeführt hat - wir wissen nicht, was mit ihm geschehen ist.", footnotes: ["Ex 32,1"] },
      { number: 41, text: "Und sie fertigten in jenen Tagen das Standbild eines Kalbes an, brachten dem Götzen Opfer dar und freuten sich über das Werk ihrer Hände.", footnotes: ["Ex 32,4.6"] },
      { number: 42, text: "Da wandte sich Gott ab und überließ sie dem Sternenkult, wie es im Buch der Propheten heißt: Habt ihr mir etwa Schlachtopfer und Gaben dargebracht während der vierzig Jahre in der Wüste, ihr vom Haus Israel?", footnotes: ["Am 5,25-27 G", "Sternenkult, wörtlich: Himmelsheer."] },
      { number: 43, text: "Das Zelt des Moloch und den Stern des Gottes Romfa habt ihr herumgetragen, die Bilder, die ihr gemacht habt, um sie anzubeten. Darum will ich euch in die Gebiete jenseits von Babylon verbannen." },
      { number: 44, text: "Unsere Väter hatten in der Wüste das Bundeszelt. So hat Gott es angeordnet; er hat dem Mose befohlen, es nach dem Vorbild zu errichten, das er geschaut hatte.", footnotes: ["Ex 25,40"] },
      { number: 45, text: "Und unsere Väter haben es übernommen und mitgebracht, als sie unter Josua das Land der Heidenvölker besetzten, die Gott vor den Augen unserer Väter vertrieb, bis zu den Tagen Davids.", footnotes: ["Dtn 32,49; Jos 3,14; 18,1"] },
      { number: 46, text: "Dieser fand Gnade vor Gott und bat für das Haus Jakob um ein Zeltheiligtum.", footnotes: ["2 Sam 7,2; Ps 132,5"] },
      { number: 47, text: "Salomo aber baute ihm ein Haus.", footnotes: ["1 Kön 6,1"] },
      { number: 48, text: "Doch der Höchste wohnt nicht in dem, was von Menschenhand gemacht ist, wie der Prophet sagt:" },
      { number: 49, text: "Der Himmel ist mein Thron und die Erde der Schemel für meine Füße. Was für ein Haus könnt ihr mir bauen?, spricht der Herr. Oder welcher Ort kann mir als Ruhestätte dienen?", footnotes: ["Jes 66,1f"] },
      { number: 50, text: "Hat nicht meine Hand dies alles gemacht?" },
      { number: 51, text: "Ihr Halsstarrigen, ihr, die ihr euch mit Herz und Ohr immerzu dem Heiligen Geist widersetzt, eure Väter schon und nun auch ihr.", footnotes: ["Dtn 9,6", "Wörtlich: Ihr Halsstarrigen und an Herzen und Ohren Unbeschnittenen, ihr widersetzt euch immerzu dem Heiligen Geist."] },
      { number: 52, text: "Welchen der Propheten haben eure Väter nicht verfolgt? Sie haben die getötet, die die Ankunft des Gerechten geweissagt haben, dessen Verräter und Mörder ihr jetzt geworden seid,", footnotes: ["2 Chr 36,16; Mt 23,34", "Der «Gerechte» ist der Messias."] },
      { number: 53, text: "ihr, die ihr durch die Anordnung von Engeln das Gesetz empfangen, es aber nicht gehalten habt.", footnotes: ["Ex 20,18.21; Dtn 5,2-5; Gal 3,19; Hebr 2,2"] },
      { number: 54, text: "Die Steinigung des Stephanus: 7,54 - 8,1a54 Als sie das hörten, waren sie aufs Äußerste über ihn empört und knirschten mit den Zähnen.", footnotes: ["5,33"] },
      { number: 55, text: "Er aber, erfüllt vom Heiligen Geist, blickte zum Himmel empor, sah die Herrlichkeit Gottes und Jesus zur Rechten Gottes stehen", heading: "Die Steinigung des Stephanus: 7,54 - 8,1a" },
      { number: 56, text: "und rief: Ich sehe den Himmel offen und den Menschensohn zur Rechten Gottes stehen.", footnotes: ["Lk 22,69"] },
      { number: 57, text: "Da erhoben sie ein lautes Geschrei, hielten sich die Ohren zu, stürmten gemeinsam auf ihn los," },
      { number: 58, text: "trieben ihn zur Stadt hinaus und steinigten ihn. Die Zeugen legten ihre Kleider zu Füßen eines jungen Mannes nieder, der Saulus hieß.", footnotes: ["Der Apostel Paulus, Jude mit römischem Bürgerrecht (vgl. 16,37), hatte zwei Namen: einen hebräischen (Saul) und einen griechisch-römischen (Paulus)."] },
      { number: 59, text: "So steinigten sie Stephanus; er aber betete und rief: Herr Jesus, nimm meinen Geist auf!", footnotes: ["Lk 23,46"] },
      { number: 60, text: "Dann sank er in die Knie und schrie laut: Herr, rechne ihnen diese Sünde nicht an! Nach diesen Worten starb er.", footnotes: ["Lk 23,34"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Saulus aber war mit dem Mord einverstanden.", footnotes: ["7,58; 22,20"] },
      { number: 1, text: "Verfolgung und Zerstreuung der Urgemeinde: 8,1b-3An jenem Tag brach eine schwere Verfolgung über die Kirche in Jerusalem herein. Alle wurden in die Gegenden von Judäa und Samarien zerstreut, mit Ausnahme der Apostel.", footnotes: ["11,19"] },
      { number: 2, text: "Fromme Männer bestatteten Stephanus und hielten eine große Totenklage für ihn.", heading: "Verfolgung und Zerstreuung der Urgemeinde: 8,1b-3" },
      { number: 3, text: "Saulus aber versuchte die Kirche zu vernichten; er drang in die Häuser ein, schleppte Männer und Frauen fort und lieferte sie ins Gefängnis ein.", footnotes: ["9,1; 22,4; 1 Kor 15,9; Gal 1,13"] },
      { number: 4, text: "Die Kirche in Judäa und Samarien: 8,4 - 12,23Philippus als Missionar in Samaria: 8,4-134 Die Gläubigen, die zerstreut worden waren, zogen umher und verkündeten das Wort." },
      { number: 5, text: "Philippus aber kam in die Hauptstadt Samariens hinab und verkündigte dort Christus.", heading: "Philippus als Missionar in Samaria: 8,4-13", footnotes: ["6,5"] },
      { number: 6, text: "Und die Menge achtete einmütig auf die Worte des Philippus; sie hörten zu und sahen die Wunder, die er tat." },
      { number: 7, text: "Denn aus vielen Besessenen fuhren unter lautem Geschrei die unreinen Geister aus; auch viele Lahme und Krüppel wurden geheilt.", footnotes: ["Mk 16,17"] },
      { number: 8, text: "So herrschte große Freude in jener Stadt." },
      { number: 9, text: "Ein Mann namens Simon wohnte schon länger in der Stadt; er trieb Zauberei und verwirrte das Volk von Samarien, da er sich als etwas Großes ausgab." },
      { number: 10, text: "Alle hörten auf ihn, Jung und Alt, und sie sagten: Das ist die Kraft Gottes, die man die Große nennt.", footnotes: ["Die Kraft Gottes, die man die Große nennt: eine heidnische Bezeichnung für göttliche Bevollmächtigung."] },
      { number: 11, text: "Und sie schlossen sich ihm an, weil er sie lange Zeit mit seinen Zauberkünsten betörte." },
      { number: 12, text: "Als sie jedoch dem Philippus Glauben schenkten, der das Evangelium vom Reich Gottes und vom Namen Jesu Christi verkündete, ließen sie sich taufen, Männer und Frauen.", footnotes: ["1,3; 19,8; 28,23.31"] },
      { number: 13, text: "Auch Simon wurde gläubig, ließ sich taufen und schloss sich dem Philippus an; und als er die großen Zeichen und Wunder sah, geriet er außer sich vor Staunen." },
      { number: 14, text: "Petrus und Johannes in Samarien: 8,14-2514 Als die Apostel in Jerusalem hörten, dass Samarien das Wort Gottes angenommen hatte, schickten sie Petrus und Johannes dorthin." },
      { number: 15, text: "Diese zogen hinab und beteten für sie, sie möchten den Heiligen Geist empfangen.", heading: "Petrus und Johannes in Samarien: 8,14-25" },
      { number: 16, text: "Denn er war noch auf keinen von ihnen herabgekommen; sie waren nur auf den Namen Jesu, des Herrn, getauft." },
      { number: 17, text: "Dann legten sie ihnen die Hände auf und sie empfingen den Heiligen Geist.", footnotes: ["2,4; 4,31; 10,44-47; 15,8f; 19,2.6"] },
      { number: 18, text: "Als Simon sah, dass durch die Handauflegung der Apostel der Geist verliehen wurde, brachte er ihnen Geld" },
      { number: 19, text: "und sagte: Gebt auch mir diese Macht, damit jeder, dem ich die Hände auflege, den Heiligen Geist empfängt." },
      { number: 20, text: "Petrus aber sagte zu ihm: Dein Silber fahre mit dir ins Verderben, wenn du meinst, die Gabe Gottes lasse sich für Geld kaufen." },
      { number: 21, text: "Du hast weder einen Anteil daran noch ein Recht darauf, denn dein Herz ist nicht aufrichtig vor Gott.", footnotes: ["Eph 5,5"] },
      { number: 22, text: "Wende dich von deiner Bosheit ab und bitte den Herrn; vielleicht wird dir dein Ansinnen vergeben." },
      { number: 23, text: "Denn ich sehe dich voll bitterer Galle und Bosheit." },
      { number: 24, text: "Da antwortete Simon: Betet ihr für mich zum Herrn, damit mich nichts von dem trifft, was ihr gesagt habt." },
      { number: 25, text: "Nachdem sie so das Wort des Herrn bezeugt und verkündet hatten, machten sie sich auf den Weg zurück nach Jerusalem und verkündeten in vielen Dörfern der Samariter das Evangelium." },
      { number: 26, text: "Die Taufe des Äthiopiers: 8,26-4026 Ein Engel des Herrn sagte zu Philippus: Steh auf und zieh nach Süden auf der Straße, die von Jerusalem nach Gaza hinabführt. Sie führt durch eine einsame Gegend." },
      { number: 27, text: "Und er brach auf. Nun war da ein Äthiopier, ein Kämmerer, Hofbeamter der Kandake, der Königin der Äthiopier, der ihren ganzen Schatz verwaltete. Dieser war nach Jerusalem gekommen, um Gott anzubeten,", heading: "Die Taufe des Äthiopiers: 8,26-40", footnotes: ["«Kandake» ist Titel der äthiopischen Königin. - «Kämmerer», wörtlich: «Eunuch», was entweder im gängigen Sinn (Verschnittener, Entmannter) oder als Titel für einen hohen Beamten zu verstehen ist."] },
      { number: 28, text: "und fuhr jetzt heimwärts. Er saß auf seinem Wagen und las den Propheten Jesaja." },
      { number: 29, text: "Und der Geist sagte zu Philippus: Geh und folge diesem Wagen." },
      { number: 30, text: "Philippus lief hin und hörte ihn den Propheten Jesaja lesen. Da sagte er: Verstehst du auch, was du liest?" },
      { number: 31, text: "Jener antwortete: Wie könnte ich es, wenn mich niemand anleitet? Und er bat den Philippus, einzusteigen und neben ihm Platz zu nehmen.", footnotes: ["Joh 16,13"] },
      { number: 32, text: "Der Abschnitt der Schrift, den er las, lautete: Wie ein Schaf wurde er zum Schlachten geführt; / und wie ein Lamm, das verstummt, / wenn man es schert, / so tat er seinen Mund nicht auf.", footnotes: ["Jes 53,7"] },
      { number: 33, text: "In der Erniedrigung wurde seine Verurteilung aufgehoben. / Seine Nachkommen, wer kann sie zählen? / Denn sein Leben wurde von der Erde fortgenommen.", footnotes: ["Jes 53,8 G"] },
      { number: 34, text: "Der Kämmerer wandte sich an Philippus und sagte: Ich bitte dich, von wem sagt der Prophet das? Von sich selbst oder von einem anderen?" },
      { number: 35, text: "Da begann Philippus zu reden und ausgehend von diesem Schriftwort verkündete er ihm das Evangelium von Jesus.", footnotes: ["5,42; Lk 24,27"] },
      { number: 36, text: "Als sie nun weiterzogen, kamen sie zu einer Wasserstelle. Da sagte der Kämmerer: Hier ist Wasser. Was steht meiner Taufe noch im Weg?", footnotes: ["10,47"] },
      { number: 37, text: "[]", footnotes: ["Einige Textzeugen fügen hinzu: Da sagte Philippus zu ihm: Wenn du aus ganzem Herzen glaubst, ist es möglich. Er antwortete: Ich glaube, dass Jesus Christus der Sohn Gottes ist."] },
      { number: 38, text: "Er ließ den Wagen halten und beide, Philippus und der Kämmerer, stiegen in das Wasser hinab und er taufte ihn." },
      { number: 39, text: "Als sie aber aus dem Wasser stiegen, entführte der Geist des Herrn den Philippus. Der Kämmerer sah ihn nicht mehr und er zog voll Freude weiter.", footnotes: ["1 Kön 18,12"] },
      { number: 40, text: "Den Philippus aber sah man in Aschdod wieder. Und er wanderte durch alle Städte und verkündete das Evangelium, bis er nach Cäsarea kam.", footnotes: ["21,8"] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Die Bekehrung des Saulus: 9,1-221 Saulus wütete immer noch mit Drohung und Mord gegen die Jünger des Herrn. Er ging zum Hohenpriester", footnotes: ["8,3; 1 Kor 15,9; Gal 1,13; (1-22) 22,5-16; 26,12-18"] },
      { number: 2, text: "und erbat sich von ihm Briefe an die Synagogen in Damaskus, um die Anhänger des (neuen) Weges, Männer und Frauen, die er dort finde, zu fesseln und nach Jerusalem zu bringen.", heading: "Die Bekehrung des Saulus: 9,1-22", footnotes: ["19,9.23; 22,4; 24,14.22", "«Der Weg» ist in der Apostelgeschichte Bezeichnung für die christliche Lehre."] },
      { number: 3, text: "Unterwegs aber, als er sich bereits Damaskus näherte, geschah es, dass ihn plötzlich ein Licht vom Himmel umstrahlte.", footnotes: ["1 Kor 9,1; 15,8; Gal 1,16"] },
      { number: 4, text: "Er stürzte zu Boden und hörte, wie eine Stimme zu ihm sagte: Saul, Saul, warum verfolgst du mich?" },
      { number: 5, text: "Er antwortete: Wer bist du, Herr? Dieser sagte: Ich bin Jesus, den du verfolgst." },
      { number: 6, text: "Steh auf und geh in die Stadt; dort wird dir gesagt werden, was du tun sollst." },
      { number: 7, text: "Seine Begleiter standen sprachlos da; sie hörten zwar die Stimme, sahen aber niemand." },
      { number: 8, text: "Saulus erhob sich vom Boden. Als er aber die Augen öffnete, sah er nichts. Sie nahmen ihn bei der Hand und führten ihn nach Damaskus hinein." },
      { number: 9, text: "Und er war drei Tage blind und er aß nicht und trank nicht." },
      { number: 10, text: "In Damaskus lebte ein Jünger namens Hananias. Zu ihm sagte der Herr in einer Vision: Hananias! Er antwortete: Hier bin ich, Herr.", footnotes: ["10,17; 16,9"] },
      { number: 11, text: "Der Herr sagte zu ihm: Steh auf und geh zur sogenannten Geraden Straße und frag im Haus des Judas nach einem Mann namens Saulus aus Tarsus. Er betet gerade", footnotes: ["21,39"] },
      { number: 12, text: "und hat in einer Vision gesehen, wie ein Mann namens Hananias hereinkommt und ihm die Hände auflegt, damit er wieder sieht." },
      { number: 13, text: "Hananias antwortete: Herr, ich habe von vielen gehört, wie viel Böses dieser Mann deinen Heiligen in Jerusalem angetan hat." },
      { number: 14, text: "Auch hier hat er Vollmacht von den Hohenpriestern, alle zu verhaften, die deinen Namen anrufen.", footnotes: ["1 Kor 1,2; 2 Tim 2,22"] },
      { number: 15, text: "Der Herr aber sprach zu ihm: Geh nur! Denn dieser Mann ist mein auserwähltes Werkzeug: Er soll meinen Namen vor Völker und Könige und die Söhne Israels tragen.", footnotes: ["22,15; Röm 1,5; Gal 2,9; 1 Kor 1,17; 2 Kor 4,7"] },
      { number: 16, text: "Ich werde ihm auch zeigen, wie viel er für meinen Namen leiden muss.", footnotes: ["Mt 10,22; Lk 21,12f; 2 Kor 11,23-29"] },
      { number: 17, text: "Da ging Hananias hin und trat in das Haus ein; er legte Saulus die Hände auf und sagte: Bruder Saul, der Herr hat mich gesandt, Jesus, der dir auf dem Weg hierher erschienen ist; du sollst wieder sehen und mit dem Heiligen Geist erfüllt werden." },
      { number: 18, text: "Sofort fiel es wie Schuppen von seinen Augen und er sah wieder; er stand auf und ließ sich taufen." },
      { number: 19, text: "Und nachdem er etwas gegessen hatte, kam er wieder zu Kräften. Einige Tage blieb er bei den Jüngern in Damaskus;" },
      { number: 20, text: "und sogleich verkündete er Jesus in den Synagogen und sagte: Er ist der Sohn Gottes.", footnotes: ["Mt 14,33; 16,16; 27,54; Mk 15,39; Lk 1,32.35; 22,70; Joh 1,49; 11,27"] },
      { number: 21, text: "Alle, die es hörten, gerieten in Aufregung und sagten: Ist das nicht der Mann, der in Jerusalem alle vernichten wollte, die diesen Namen anrufen? Und ist er nicht auch hierher gekommen, um sie zu fesseln und vor die Hohenpriester zu führen?" },
      { number: 22, text: "Saulus aber trat um so kraftvoller auf und brachte die Juden in Damaskus in Verwirrung, weil er ihnen bewies, dass Jesus der Messias ist.", footnotes: ["18,28"] },
      { number: 23, text: "Die Flucht des Saulus aus Damaskus: 9,23-3123 So verging einige Zeit; da beschlossen die Juden, ihn zu töten." },
      { number: 24, text: "Doch ihr Plan wurde dem Saulus bekannt. Sie bewachten sogar Tag und Nacht die Stadttore, um ihn zu beseitigen.", heading: "Die Flucht des Saulus aus Damaskus: 9,23-31", footnotes: ["2 Kor 11,32f"] },
      { number: 25, text: "Aber seine Jünger nahmen ihn und ließen ihn bei Nacht in einem Korb die Stadtmauer hinab." },
      { number: 26, text: "Als er nach Jerusalem kam, versuchte er, sich den Jüngern anzuschließen. Aber alle fürchteten sich vor ihm und konnten nicht glauben, dass er ein Jünger war.", footnotes: ["Gal 1,18"] },
      { number: 27, text: "Barnabas jedoch nahm sich seiner an und brachte ihn zu den Aposteln. Er erzählte ihnen, wie Saulus auf dem Weg den Herrn gesehen habe und dass dieser mit ihm gesprochen habe und wie er in Damaskus mutig und offen im Namen Jesu aufgetreten sei.", footnotes: ["4,36"] },
      { number: 28, text: "So ging er bei ihnen in Jerusalem ein und aus, trat unerschrocken im Namen des Herrn auf" },
      { number: 29, text: "und führte auch Streitgespräche mit den Hellenisten. Diese aber planten, ihn zu töten." },
      { number: 30, text: "Als die Brüder das merkten, brachten sie ihn nach Cäsarea hinab und schickten ihn von dort nach Tarsus.", footnotes: ["11,25; Gal 1,21"] },
      { number: 31, text: "Die Kirche in ganz Judäa, Galiläa und Samarien hatte nun Frieden; sie wurde gefestigt und lebte in der Furcht vor dem Herrn. Und sie wuchs durch die Hilfe des Heiligen Geistes." },
      { number: 32, text: "Die Aufnahme von Heiden in die Kirche: 9,32 - 12,25Petrus in Lydda und Joppe: 9,32-4332 Auf einer Reise zu den einzelnen Gemeinden kam Petrus auch zu den Heiligen in Lydda." },
      { number: 33, text: "Dort fand er einen Mann namens Äneas, der seit acht Jahren lahm und bettlägerig war.", heading: "Petrus in Lydda und Joppe: 9,32-43" },
      { number: 34, text: "Petrus sagte zu ihm: Äneas, Jesus Christus heilt dich. Steh auf und richte dir dein Bett! Sogleich stand er auf." },
      { number: 35, text: "Und alle Bewohner von Lydda und der Scharon-Ebene sahen ihn und bekehrten sich zum Herrn." },
      { number: 36, text: "In Joppe lebte eine Jüngerin namens Tabita, das heißt übersetzt: Gazelle. Sie tat viele gute Werke und gab reichlich Almosen." },
      { number: 37, text: "In jenen Tagen aber wurde sie krank und starb. Man wusch sie und bahrte sie im Obergemach auf." },
      { number: 38, text: "Weil aber Lydda nahe bei Joppe liegt und die Jünger hörten, dass Petrus dort war, schickten sie zwei Männer zu ihm und ließen ihn bitten: Komm zu uns, zögere nicht!" },
      { number: 39, text: "Da stand Petrus auf und ging mit ihnen. Als er ankam, führten sie ihn in das Obergemach hinauf; alle Witwen traten zu ihm, sie weinten und zeigten ihm die Röcke und Mäntel, die Gazelle gemacht hatte, als sie noch bei ihnen war." },
      { number: 40, text: "Petrus aber schickte alle hinaus, kniete nieder und betete. Dann wandte er sich zu dem Leichnam und sagte: Tabita, steh auf! Da öffnete sie ihre Augen, sah Petrus an und setzte sich auf.", footnotes: ["Mk 5,40f"] },
      { number: 41, text: "Er gab ihr die Hand und ließ sie aufstehen; dann rief er die Heiligen und die Witwen und zeigte ihnen, dass sie wieder lebte." },
      { number: 42, text: "Das wurde in ganz Joppe bekannt und viele kamen zum Glauben an den Herrn." },
      { number: 43, text: "Petrus aber blieb längere Zeit in Joppe bei einem gewissen Simon, einem Gerber.", footnotes: ["10,6"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Die Vision des Hauptmanns Kornelius in Cäsarea: 10,1-81 In Cäsarea lebte ein Mann namens Kornelius, Hauptmann in der sogenannten Italischen Kohorte;", footnotes: ["Cäsarea am Meer war der Sitz des römischen Statthalters und Standort einer römischen Garnison."] },
      { number: 2, text: "er lebte mit seinem ganzen Haus fromm und gottesfürchtig, gab dem Volk reichlich Almosen und betete beständig zu Gott.", heading: "Die Vision des Hauptmanns Kornelius in Cäsarea: 10,1-8" },
      { number: 3, text: "Er sah um die neunte Tagesstunde in einer Vision deutlich, wie ein Engel Gottes bei ihm eintrat und zu ihm sagte: Kornelius!" },
      { number: 4, text: "Kornelius blickte ihn an und fragte erschrocken: Was ist, Herr? Er sagte zu ihm: Deine Gebete und Almosen sind zu Gott gelangt, und er hat sich an sie erinnert." },
      { number: 5, text: "Schick jetzt einige Männer nach Joppe und lass einen gewissen Simon herbeiholen, der den Beinamen Petrus hat." },
      { number: 6, text: "Er ist zu Gast bei einem Gerber namens Simon, der ein Haus am Meer hat.", footnotes: ["9,43"] },
      { number: 7, text: "Als der Engel, der mit ihm sprach, weggegangen war, rief Kornelius zwei seiner Haussklaven und einen frommen Soldaten aus seinem Gefolge." },
      { number: 8, text: "Er erzählte ihnen alles und schickte sie nach Joppe." },
      { number: 9, text: "Die Vision des Petrus in Joppe: 10,9-23a9 Am folgenden Tag, als jene unterwegs waren und sich der Stadt näherten, stieg Petrus auf das Dach, um zu beten; es war um die sechste Stunde.", footnotes: ["11,5-11"] },
      { number: 10, text: "Da wurde er hungrig und wollte essen. Während man etwas zubereitete, kam eine Verzückung über ihn.", heading: "Die Vision des Petrus in Joppe: 10,9-23a" },
      { number: 11, text: "Er sah den Himmel offen und eine Schale auf die Erde herabkommen, die aussah wie ein großes Leinentuch, das an den vier Ecken gehalten wurde." },
      { number: 12, text: "Darin lagen alle möglichen Vierfüßler, Kriechtiere der Erde und Vögel des Himmels." },
      { number: 13, text: "Und eine Stimme rief ihm zu: Steh auf, Petrus, schlachte und iss!" },
      { number: 14, text: "Petrus aber antwortete: Niemals, Herr! Noch nie habe ich etwas Unheiliges und Unreines gegessen.", footnotes: ["Lev 11; Ez 4,14"] },
      { number: 15, text: "Da richtete sich die Stimme ein zweites Mal an ihn: Was Gott für rein erklärt, nenne du nicht unrein!", footnotes: ["Mk 7,15.19; Gal 2,12"] },
      { number: 16, text: "Das geschah dreimal, dann wurde die Schale plötzlich in den Himmel hinaufgezogen." },
      { number: 17, text: "Petrus war noch ratlos und überlegte, was die Vision, die er gehabt hatte, wohl bedeutete; inzwischen hatten sich die von Kornelius gesandten Männer zum Haus des Simon durchgefragt und standen am Tor." },
      { number: 18, text: "Sie riefen und fragten, ob Simon mit dem Beinamen Petrus hier zu Gast sei." },
      { number: 19, text: "Während Petrus noch über die Vision nachdachte, sagte der Geist zu ihm: Da sind zwei Männer und suchen dich.", footnotes: ["13,2; 15,28"] },
      { number: 20, text: "Steh auf, geh hinunter und zieh ohne Bedenken mit ihnen; denn ich habe sie geschickt." },
      { number: 21, text: "Petrus stieg zu den Männern hinab und sagte: Ich bin der, den ihr sucht. Aus welchem Grund seid ihr hier?" },
      { number: 22, text: "Sie antworteten: Der Hauptmann Kornelius, ein gerechter und gottesfürchtiger Mann, der beim ganzen Volk der Juden in gutem Ruf steht, hat von einem heiligen Engel die Weisung erhalten, dich in sein Haus holen zu lassen und zu hören, was du ihm zu sagen hast." },
      { number: 23, text: "Da ließ er sie eintreten und bewirtete sie." },
      { number: 23, text: "Die Taufe des Kornelius: 10,23b-4823b Tags darauf machte sich Petrus mit ihnen auf den Weg und einige Brüder aus Joppe begleiteten ihn." },
      { number: 24, text: "Am folgenden Tag kamen sie nach Cäsarea. Kornelius erwartete sie schon und hatte seine Verwandten und seine nächsten Freunde zusammengerufen.", heading: "Die Taufe des Kornelius: 10,23b-48" },
      { number: 25, text: "Als nun Petrus ankam, ging ihm Kornelius entgegen und warf sich ehrfürchtig vor ihm nieder." },
      { number: 26, text: "Petrus aber richtete ihn auf und sagte: Steh auf! Auch ich bin nur ein Mensch.", footnotes: ["14,15; Offb 19,10"] },
      { number: 27, text: "Während er sich mit ihm unterhielt, ging er hinein und fand dort viele Menschen versammelt." },
      { number: 28, text: "Da sagte er zu ihnen: Ihr wisst, dass es einem Juden nicht erlaubt ist, mit einem Nichtjuden zu verkehren oder sein Haus zu betreten; mir aber hat Gott gezeigt, dass man keinen Menschen unheilig oder unrein nennen darf.", footnotes: ["11,3; Gal 2,12"] },
      { number: 29, text: "Darum bin ich auch ohne Widerspruch gekommen, als nach mir geschickt wurde. Nun frage ich: Warum habt ihr mich holen lassen?" },
      { number: 30, text: "Da sagte Kornelius: Vor vier Tagen um diese Zeit war ich zum Gebet der neunten Stunde in meinem Haus; da stand plötzlich ein Mann in einem leuchtenden Gewand vor mir" },
      { number: 31, text: "und sagte: Kornelius, dein Gebet wurde erhört und deine Almosen wurden vor Gott in Erinnerung gebracht." },
      { number: 32, text: "Schick jemand nach Joppe und lass Simon, der den Beinamen Petrus hat, holen; er ist Gast im Haus des Gerbers Simon am Meer." },
      { number: 33, text: "Sofort habe ich nach dir geschickt und es ist gut, dass du gekommen bist. Jetzt sind wir alle hier vor Gott zugegen, um all das anzuhören, was dir vom Herrn aufgetragen worden ist." },
      { number: 34, text: "Da begann Petrus zu reden und sagte: Wahrhaftig, jetzt begreife ich, dass Gott nicht auf die Person sieht,", footnotes: ["Dtn 10,17; Röm 2,11.14; 1 Petr 1,17"] },
      { number: 35, text: "sondern dass ihm in jedem Volk willkommen ist, wer ihn fürchtet und tut, was recht ist.", footnotes: ["Röm 2,14; Joh 9,31; 10,16"] },
      { number: 36, text: "Er hat das Wort den Israeliten gesandt, indem er den Frieden verkündete durch Jesus Christus; dieser ist der Herr aller.", footnotes: ["Jes 52,7; 1 Kor 12,3; Phil 1,10f"] },
      { number: 37, text: "Ihr wisst, was im ganzen Land der Juden geschehen ist, angefangen in Galiläa, nach der Taufe, die Johannes verkündet hat:", footnotes: ["Lk 4,44"] },
      { number: 38, text: "wie Gott Jesus von Nazaret gesalbt hat mit dem Heiligen Geist und mit Kraft, wie dieser umherzog, Gutes tat und alle heilte, die in der Gewalt des Teufels waren; denn Gott war mit ihm.", footnotes: ["Jes 61,1; Lk 3,22; 4,18f; Jes 7,14; Mt 1,23"] },
      { number: 39, text: "Und wir sind Zeugen für alles, was er im Land der Juden und in Jerusalem getan hat. Ihn haben sie an den Pfahl gehängt und getötet.", footnotes: ["Dtn 21,22; Gal 3,13"] },
      { number: 40, text: "Gott aber hat ihn am dritten Tag auferweckt und hat ihn erscheinen lassen,", footnotes: ["(40f) 1,3; Mt 20,19; 1 Kor 15,4-7"] },
      { number: 41, text: "zwar nicht dem ganzen Volk, wohl aber den von Gott vorherbestimmten Zeugen: uns, die wir mit ihm nach seiner Auferstehung von den Toten gegessen und getrunken haben.", footnotes: ["Lk 24,41-43"] },
      { number: 42, text: "Und er hat uns geboten, dem Volk zu verkündigen und zu bezeugen: Das ist der von Gott eingesetzte Richter der Lebenden und der Toten.", footnotes: ["17,31; Röm 14,9; 2 Tim 4,1; 1 Petr 4,5"] },
      { number: 43, text: "Von ihm bezeugen alle Propheten, dass jeder, der an ihn glaubt, durch seinen Namen die Vergebung der Sünden empfängt.", footnotes: ["Jes 53,5f; Jer 31,34; Dan 9,24"] },
      { number: 44, text: "Noch während Petrus dies sagte, kam der Heilige Geist auf alle herab, die das Wort hörten.", footnotes: ["2,4"] },
      { number: 45, text: "Die gläubig gewordenen Juden, die mit Petrus gekommen waren, konnten es nicht fassen, dass auch auf die Heiden die Gabe des Heiligen Geistes ausgegossen wurde." },
      { number: 46, text: "Denn sie hörten sie in Zungen reden und Gott preisen. Petrus aber sagte:", footnotes: ["Zu «Zungen» vgl. die Anmerkung zu 2,4."] },
      { number: 47, text: "Kann jemand denen das Wasser zur Taufe verweigern, die ebenso wie wir den Heiligen Geist empfangen haben?" },
      { number: 48, text: "Und er ordnete an, sie im Namen Jesu Christi zu taufen. Danach baten sie ihn, einige Tage zu bleiben." },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Die Rechenschaft des Petrus vor der Gemeinde in Jerusalem: 11,1-181 Die Apostel und die Brüder in Judäa erfuhren, dass auch die Heiden das Wort Gottes angenommen hatten." },
      { number: 2, text: "Als nun Petrus nach Jerusalem hinaufkam, hielten ihm die gläubig gewordenen Juden vor:", heading: "Die Rechenschaft des Petrus vor der Gemeinde in Jerusalem: 11,1-18", footnotes: ["die gläubig gewordenen Juden, wörtlich: die aus der Beschneidung."] },
      { number: 3, text: "Du hast das Haus von Unbeschnittenen betreten und hast mit ihnen gegessen.", footnotes: ["10,28; Gal 2,12"] },
      { number: 4, text: "Da begann Petrus, ihnen der Reihe nach zu berichten:" },
      { number: 5, text: "Ich war in der Stadt Joppe und betete; da hatte ich in einer Verzückung eine Vision: Eine Schale, die aussah wie ein großes Leinentuch, das an den vier Ecken gehalten wurde, senkte sich aus dem Himmel bis zu mir herab." },
      { number: 6, text: "Als ich genauer hinschaute, sah ich darin die Vierfüßler der Erde, die wilden Tiere, die Kriechtiere und die Vögel des Himmels." },
      { number: 7, text: "Ich hörte auch eine Stimme, die zu mir sagte: Steh auf, Petrus, schlachte und iss!" },
      { number: 8, text: "Ich antwortete: Niemals, Herr! Noch nie ist etwas Unheiliges oder Unreines in meinen Mund gekommen." },
      { number: 9, text: "Doch zum zweiten Mal kam eine Stimme vom Himmel; sie sagte: Was Gott für rein erklärt hat, nenne du nicht unrein!" },
      { number: 10, text: "Das geschah dreimal, dann wurde alles wieder in den Himmel hinaufgezogen." },
      { number: 11, text: "Da standen auf einmal drei Männer vor dem Haus, in dem ich wohnte; sie waren aus Cäsarea zu mir geschickt worden." },
      { number: 12, text: "Der Geist aber sagte mir, ich solle ohne Bedenken mit ihnen gehen. Auch diese sechs Brüder zogen mit mir und wir kamen in das Haus jenes Mannes." },
      { number: 13, text: "Er erzählte uns, wie er in seinem Haus den Engel stehen sah, der zu ihm sagte: Schick jemand nach Joppe und lass Simon, der Petrus genannt wird, holen." },
      { number: 14, text: "Er wird dir Worte sagen, durch die du mit deinem ganzen Haus gerettet werden wirst." },
      { number: 15, text: "Während ich redete, kam der Heilige Geist auf sie herab, wie am Anfang auf uns.", footnotes: ["2,3f"] },
      { number: 16, text: "Da erinnerte ich mich an das Wort des Herrn: Johannes hat mit Wasser getauft, ihr aber werdet mit dem Heiligen Geist getauft werden.", footnotes: ["1,5; Lk 3,16"] },
      { number: 17, text: "Wenn nun Gott ihnen, nachdem sie zum Glauben an Jesus Christus, den Herrn, gekommen sind, die gleiche Gabe verliehen hat wie uns: wer bin ich, dass ich Gott hindern könnte?" },
      { number: 18, text: "Als sie das hörten, beruhigten sie sich, priesen Gott und sagten: Gott hat also auch den Heiden die Umkehr zum Leben geschenkt.", footnotes: ["14,27"] },
      { number: 19, text: "Die Entstehung einer christlichen Gemeinde in Antiochia: 11,19-2619 Bei der Verfolgung, die wegen Stephanus entstanden war, kamen die Versprengten bis nach Phönizien, Zypern und Antiochia; doch verkündeten sie das Wort nur den Juden.", footnotes: ["8,1-4", "Antiochia in Syrien war die drittgrößte Stadt des römischen Reiches nach Rom und Alexandria; unter der halben Million Einwohner war eine große jüdische Gemeinde."] },
      { number: 20, text: "Einige aber von ihnen, die aus Zypern und Zyrene stammten, verkündeten, als sie nach Antiochia kamen, auch den Griechen das Evangelium von Jesus, dem Herrn.", heading: "Die Entstehung einer christlichen Gemeinde in Antiochia: 11,19-26" },
      { number: 21, text: "Die Hand des Herrn war mit ihnen und viele wurden gläubig und bekehrten sich zum Herrn." },
      { number: 22, text: "Die Nachricht davon kam der Gemeinde von Jerusalem zu Ohren und sie schickten Barnabas nach Antiochia.", footnotes: ["4,36"] },
      { number: 23, text: "Als er ankam und die Gnade Gottes sah, freute er sich und ermahnte alle, dem Herrn treu zu bleiben, wie sie es sich vorgenommen hatten." },
      { number: 24, text: "Denn er war ein trefflicher Mann, erfüllt vom Heiligen Geist und von Glauben. So wurde für den Herrn eine beträchtliche Zahl hinzugewonnen." },
      { number: 25, text: "Barnabas aber zog nach Tarsus, um Saulus aufzusuchen.", footnotes: ["9,30"] },
      { number: 26, text: "Er fand ihn und nahm ihn nach Antiochia mit. Dort wirkten sie miteinander ein volles Jahr in der Gemeinde und unterrichteten eine große Zahl von Menschen. In Antiochia nannte man die Jünger zum ersten Mal Christen.", footnotes: ["11,30; 12,25; 13,2; Gal 2,1.13; Apg 26,28; 1 Petr 4,16", "Zum ersten Mal im Neuen Testament begegnet hier die Bezeichnung «Christ» (vgl. 26,28; 1 Petr 4,16)."] },
      { number: 27, text: "Die Spende der Gemeinde von Antiochia für die Christen in Judäa: 11,27-3027 In jenen Tagen kamen von Jerusalem Propheten nach Antiochia hinab.", footnotes: ["Gemeint sind hier christliche Propheten, die in der Urkirche eine wichtige Rolle spielten; vgl. z. B. 1 Kor 12,28; 14,3-12; Eph 2,20."] },
      { number: 28, text: "Einer von ihnen namens Agabus trat auf und weissagte durch den Geist, eine große Hungersnot werde über die ganze Erde kommen. Sie brach dann unter Klaudius aus.", heading: "Die Spende der Gemeinde von Antiochia für die Christen in Judäa: 11,27-30", footnotes: ["21,10f", "Kaiser Klaudius regierte von 41 bis 54 n. Chr. Römische Geschichtsschreiber berichten von Hungersnöten während seiner Regierungszeit. Nach 18,2 hat Klaudius die Juden aus Rom ausgewiesen, wahrscheinlich im Jahr 49 n. Chr."] },
      { number: 29, text: "Man beschloss, jeder von den Jüngern solle nach seinem Vermögen den Brüdern in Judäa etwas zur Unterstützung senden.", footnotes: ["Röm 15,26; 1 Kor 16,1; 2 Kor 9,2.12; Gal 2,10"] },
      { number: 30, text: "Das taten sie auch und schickten ihre Gaben durch Barnabas und Saulus an die Ältesten." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Die Hinrichtung des Jakobus und die Verhaftung des Petrus: 12,1-51 Um jene Zeit ließ der König Herodes einige aus der Gemeinde verhaften und misshandeln.", footnotes: ["Es handelt sich um Herodes Agrippa I., einen Enkel Herodes des Großen, dem 38 n. Chr. von Kaiser Kaligula die Tetrarchie des Philippus übertragen wurde (vgl. die Anmerkung zu Lk 3,1f) und der von 41 bis 44 König von ganz Palästina war."] },
      { number: 2, text: "Jakobus, den Bruder des Johannes, ließ er mit dem Schwert hinrichten.", heading: "Die Hinrichtung des Jakobus und die Verhaftung des Petrus: 12,1-5", footnotes: ["Es ist der Apostel Jakobus, der Ältere."] },
      { number: 3, text: "Als er sah, dass es den Juden gefiel, ließ er auch Petrus festnehmen. Das geschah in den Tagen der Ungesäuerten Brote." },
      { number: 4, text: "Er nahm ihn also fest und warf ihn ins Gefängnis. Die Bewachung übertrug er vier Abteilungen von je vier Soldaten. Er beabsichtigte, ihn nach dem Paschafest dem Volk vorführen zu lassen." },
      { number: 5, text: "Petrus wurde also im Gefängnis bewacht. Die Gemeinde aber betete inständig für ihn zu Gott.", footnotes: ["Jak 5,16"] },
      { number: 6, text: "Die wunderbare Befreiung des Petrus: 12,6-19a6 In der Nacht, ehe Herodes ihn vorführen lassen wollte, schlief Petrus, mit zwei Ketten gefesselt, zwischen zwei Soldaten; vor der Tür aber bewachten Posten den Kerker." },
      { number: 7, text: "Plötzlich trat ein Engel des Herrn ein und ein helles Licht strahlte in den Raum. Er stieß Petrus in die Seite, weckte ihn und sagte: Schnell, steh auf! Da fielen die Ketten von seinen Händen.", heading: "Die wunderbare Befreiung des Petrus: 12,6-19a", footnotes: ["5,19"] },
      { number: 8, text: "Der Engel aber sagte zu ihm: Gürte dich und zieh deine Sandalen an! Er tat es. Und der Engel sagte zu ihm: Wirf deinen Mantel um und folge mir!" },
      { number: 9, text: "Dann ging er hinaus und Petrus folgte ihm, ohne zu wissen, dass es Wirklichkeit war, was durch den Engel geschah; es kam ihm vor, als habe er eine Vision." },
      { number: 10, text: "Sie gingen an der ersten und an der zweiten Wache vorbei und kamen an das eiserne Tor, das in die Stadt führt; es öffnete sich ihnen von selbst. Sie traten hinaus und gingen eine Gasse weit; und auf einmal verließ ihn der Engel." },
      { number: 11, text: "Da kam Petrus zu sich und sagte: Nun weiß ich wahrhaftig, dass der Herr seinen Engel gesandt und mich der Hand des Herodes entrissen hat und all dem, was das Volk der Juden erhofft hat." },
      { number: 12, text: "Als er sich darüber klar geworden war, ging er zum Haus der Maria, der Mutter des Johannes mit dem Beinamen Markus, wo nicht wenige versammelt waren und beteten.", footnotes: ["12,25; 13,5.13; 15,37"] },
      { number: 13, text: "Als er am Außentor klopfte, kam eine Magd namens Rhode, um zu öffnen." },
      { number: 14, text: "Sie erkannte die Stimme des Petrus, doch vor Freude machte sie das Tor nicht auf, sondern lief hinein und berichtete: Petrus steht vor dem Tor." },
      { number: 15, text: "Da sagten sie zu ihr: Du bist nicht bei Sinnen. Doch sie bestand darauf, es sei so. Da sagten sie: Es ist sein Engel." },
      { number: 16, text: "Petrus aber klopfte noch immer. Als sie öffneten und ihn sahen, staunten sie." },
      { number: 17, text: "Er gab ihnen mit der Hand ein Zeichen zu schweigen und erzählte ihnen, wie der Herr ihn aus dem Gefängnis herausgeführt hatte. Er sagte: Berichtet das dem Jakobus und den Brüdern! Dann verließ er sie und ging an einen anderen Ort.", footnotes: ["Dieser Jakobus ist der in Mk 6,3 erwähnte «Bruder des Herrn», der nach der Apostelgeschichte (vgl. 15,13-21; 21,18) und dem Galaterbrief (vgl. 1,19; 2,9.12) eine wichtige Rolle in der Urgemeinde von Jerusalem spielte."] },
      { number: 18, text: "Als es Tag wurde, herrschte bei den Soldaten große Aufregung darüber, was wohl mit Petrus geschehen sei.", footnotes: ["5,22f"] },
      { number: 19, text: "Herodes aber ließ ihn suchen, und da man ihn nicht fand, verhörte er die Wachen und befahl, sie abzuführen." },
      { number: 19, text: "Der Tod des Herodes Agrippa: 12,19b-2319b Dann zog Herodes von Judäa nach Cäsarea hinab und blieb dort." },
      { number: 20, text: "Er war über die Bewohner von Tyrus und Sidon sehr aufgebracht. Sie kamen gemeinsam zu ihm, gewannen Blastus, den Kämmerer des Königs, für sich und baten um Frieden, weil sie ihre Nahrung aus dem Land des Königs bezogen.", heading: "Der Tod des Herodes Agrippa: 12,19b-23" },
      { number: 21, text: "Am festgesetzten Tag nahm Herodes im Königsgewand auf der Tribüne Platz und hielt vor ihnen eine feierliche Ansprache." },
      { number: 22, text: "Das Volk aber schrie: Die Stimme eines Gottes, nicht eines Menschen!" },
      { number: 23, text: "Im selben Augenblick schlug ihn ein Engel des Herrn, weil er nicht Gott die Ehre gegeben hatte. Und von Würmern zerfressen, starb er." },
      { number: 24, text: "Die Rückkehr des Barnabas und des Saulus nach Antiochia: 12,24-2524 Das Wort des Herrn aber wuchs und breitete sich aus.", footnotes: ["6,7"] },
      { number: 25, text: "Nachdem Barnabas und Saulus in Jerusalem ihre Aufgabe erfüllt hatten, kehrten sie zurück; Johannes mit dem Beinamen Markus nahmen sie mit.", heading: "Die Rückkehr des Barnabas und des Saulus nach Antiochia: 12,24-25" },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Die Kirche unter den Völkern: 13,1 - 28,31Die Aussendung des Barnabas und des Saulus: 13,1-31 In der Gemeinde von Antiochia gab es Propheten und Lehrer: Barnabas und Simeon, genannt Niger, Luzius von Zyrene, Manaën, ein Jugendgefährte des Tetrarchen Herodes, und Saulus.", footnotes: ["Röm 16,21"] },
      { number: 2, text: "Als sie zu Ehren des Herrn Gottesdienst feierten und fasteten, sprach der Heilige Geist: Wählt mir Barnabas und Saulus zu dem Werk aus, zu dem ich sie mir berufen habe.", heading: "Die Aussendung des Barnabas und des Saulus: 13,1-3", footnotes: ["1 Kor 9,6"] },
      { number: 3, text: "Da fasteten und beteten sie, legten ihnen die Hände auf und ließen sie ziehen.", footnotes: ["6,6; 14,23; 1 Tim 4,14; 2 Tim 1,6"] },
      { number: 4, text: "Die erste Missionsreise des Paulus: 13,4 - 14,28Von Antiochia über Zypern bis Perge: 13,4-134 Vom Heiligen Geist ausgesandt, zogen sie nach Seleuzia hinab und segelten von da nach Zypern.", footnotes: ["Seleuzia war die Hafenstadt von Antiochia."] },
      { number: 5, text: "Als sie in Salamis angekommen waren, verkündeten sie das Wort Gottes in den Synagogen der Juden. Johannes hatten sie als Helfer bei sich.", heading: "Von Antiochia über Zypern bis Perge: 13,4-13", footnotes: ["12,12.25"] },
      { number: 6, text: "Sie durchzogen die ganze Insel bis Paphos. Dort trafen sie einen Mann namens Barjesus, einen Zauberer und falschen Propheten, der Jude war" },
      { number: 7, text: "und zum Gefolge des Prokonsuls Sergius Paulus, eines verständigen Mannes, gehörte. Dieser ließ Barnabas und Saulus rufen und wünschte, von ihnen das Wort Gottes zu hören." },
      { number: 8, text: "Aber Elymas, der Zauberer - so wird nämlich der Name Elymas übersetzt -, trat gegen sie auf und wollte den Prokonsul vom Glauben abhalten." },
      { number: 9, text: "Saulus, der auch Paulus heißt, blickte ihn, vom Heiligen Geist erfüllt, an" },
      { number: 10, text: "und sagte: Du elender und gerissener Betrüger, du Sohn des Teufels, du Feind aller Gerechtigkeit, willst du nicht endlich aufhören, die geraden Wege des Herrn zu durchkreuzen?" },
      { number: 11, text: "Jetzt kommt die Hand des Herrn über dich. Du wirst blind sein und eine Zeit lang die Sonne nicht mehr sehen. Im selben Augenblick fiel Finsternis und Dunkel auf ihn, er tappte umher und suchte jemand, der ihn an der Hand führte." },
      { number: 12, text: "Als der Prokonsul das alles sah, wurde er gläubig, denn er war betroffen von der Lehre des Herrn." },
      { number: 13, text: "Von Paphos fuhr Paulus mit seinen Begleitern ab und kam nach Perge in Pamphylien. Johannes aber trennte sich von ihnen und kehrte nach Jerusalem zurück.", footnotes: ["13,5"] },
      { number: 14, text: "Das Wirken des Barnabas und des Paulus in Antiochia in Pisidien: 13,14-5214 Sie selbst wanderten von Perge weiter und kamen nach Antiochia in Pisidien. Dort gingen sie am Sabbat in die Synagoge und setzten sich.", footnotes: ["15,21; Lk 4,16f"] },
      { number: 15, text: "Nach der Lesung aus dem Gesetz und den Propheten schickten die Synagogenvorsteher zu ihnen und ließen ihnen sagen: Brüder, wenn ihr ein Wort des Trostes für das Volk habt, so redet.", heading: "Das Wirken des Barnabas und des Paulus in Antiochia in Pisidien: 13,14-52" },
      { number: 16, text: "Da stand Paulus auf, gab mit der Hand ein Zeichen und sagte: Ihr Israeliten und ihr Gottesfürchtigen, hört!" },
      { number: 17, text: "Der Gott dieses Volkes Israel hat unsere Väter erwählt und das Volk in der Fremde erhöht, in Ägypten; er hat sie mit hoch erhobenem Arm von dort herausgeführt", footnotes: ["Ex 6,1.6"] },
      { number: 18, text: "und fast vierzig Jahre durch die Wüste getragen.", footnotes: ["Ex 16,35; Num 14,34; Dtn 1,31; 7,1"] },
      { number: 19, text: "Sieben Völker hat er im Land Kanaan vernichtet und ihr Land ihnen zum Besitz gegeben," },
      { number: 20, text: "für etwa vierhundertfünfzig Jahre. Danach hat er ihnen Richter gegeben bis zum Propheten Samuel." },
      { number: 21, text: "Dann verlangten sie einen König und Gott gab ihnen Saul, den Sohn des Kisch, einen Mann aus dem Stamm Benjamin, für vierzig Jahre.", footnotes: ["1 Sam 8,5; 10,21.24"] },
      { number: 22, text: "Nachdem er ihn verworfen hatte, erhob er David zu ihrem König, von dem er bezeugte: Ich habe David, den Sohn des Isai, als einen Mann nach meinem Herzen gefunden, der alles, was ich will, vollbringen wird.", footnotes: ["Ps 89,21; 1 Sam 13,14; Jes 44,28"] },
      { number: 23, text: "Aus seinem Geschlecht hat Gott dem Volk Israel, der Verheißung gemäß, Jesus als Retter geschickt.", footnotes: ["2 Sam 7,12; Jes 11,1"] },
      { number: 24, text: "Vor dessen Auftreten hat Johannes dem ganzen Volk Israel Umkehr und Taufe verkündigt.", footnotes: ["Lk 3,3", "Wörtlich: Taufe der Umkehr."] },
      { number: 25, text: "Als Johannes aber seinen Lauf vollendet hatte, sagte er: Ich bin nicht der, für den ihr mich haltet; aber seht, nach mir kommt einer, dem die Sandalen von den Füßen zu lösen ich nicht wert bin.", footnotes: ["Lk 3,16"] },
      { number: 26, text: "Brüder, ihr Söhne aus Abrahams Geschlecht und ihr Gottesfürchtigen! Uns wurde das Wort dieses Heils gesandt." },
      { number: 27, text: "Denn die Einwohner von Jerusalem und ihre Führer haben Jesus nicht erkannt, aber sie haben die Worte der Propheten, die an jedem Sabbat vorgelesen werden, erfüllt und haben ihn verurteilt.", footnotes: ["3,17; Lk 23,34; Joh 16,3; 1 Tim 1,13"] },
      { number: 28, text: "Obwohl sie nichts fanden, wofür er den Tod verdient hätte, forderten sie von Pilatus seine Hinrichtung.", footnotes: ["Lk 23,4.15-25"] },
      { number: 29, text: "Als sie alles vollbracht hatten, was in der Schrift über ihn gesagt ist, nahmen sie ihn vom Kreuzesholz und legten ihn ins Grab." },
      { number: 30, text: "Gott aber hat ihn von den Toten auferweckt," },
      { number: 31, text: "und er ist viele Tage hindurch denen erschienen, die mit ihm zusammen von Galiläa nach Jerusalem hinaufgezogen waren und die jetzt vor dem Volk seine Zeugen sind." },
      { number: 32, text: "So verkünden wir euch das Evangelium: Gott hat die Verheißung, die an die Väter ergangen ist,", footnotes: ["Röm 1,4"] },
      { number: 33, text: "an uns, ihren Kindern, erfüllt, indem er Jesus auferweckt hat, wie es schon im zweiten Psalm heißt: Mein Sohn bist du, heute habe ich dich gezeugt.", footnotes: ["Ps 2,7"] },
      { number: 34, text: "Dass er ihn aber von den Toten auferweckt hat, um ihn nicht mehr zur Verwesung zurückkehren zu lassen, hat er so ausgedrückt: Ich will euch die Heilsgaben gewähren, die ich David fest zugesagt habe.", footnotes: ["Jes 55,3 G"] },
      { number: 35, text: "Darum sagt er auch an einer anderen Stelle: Du lässt deinen Frommen nicht die Verwesung schauen.", footnotes: ["Ps 16,10; Apg 2,27.31"] },
      { number: 36, text: "David aber ist, nachdem er seinen Zeitgenossen gedient hatte, nach Gottes Willen entschlafen und mit seinen Vätern vereint worden. Er hat die Verwesung gesehen;" },
      { number: 37, text: "der aber, den Gott auferweckte, hat die Verwesung nicht gesehen." },
      { number: 38, text: "Ihr sollt also wissen, meine Brüder: Durch diesen wird euch die Vergebung der Sünden verkündet, und in allem, worin euch das Gesetz des Mose nicht gerecht machen konnte,", footnotes: ["10,43; Hebr 9,9; 10,1-4"] },
      { number: 39, text: "wird jeder, der glaubt, durch ihn gerecht gemacht.", footnotes: ["Röm 10,4"] },
      { number: 40, text: "Gebt also Acht, dass nicht eintrifft, was bei den Propheten gesagt ist:" },
      { number: 41, text: "Schaut hin, ihr Verächter, staunt und erstarrt! Denn ich vollbringe in euren Tagen eine Tat - würde man euch von dieser Tat erzählen, ihr glaubtet es nicht.", footnotes: ["Hab 1,5"] },
      { number: 42, text: "Als sie hinausgingen, bat man sie, am nächsten Sabbat über diese Worte zu ihnen zu sprechen." },
      { number: 43, text: "Und als die Versammlung sich aufgelöst hatte, schlossen sich viele Juden und fromme Proselyten Paulus und Barnabas an. Diese redeten mit ihnen und ermahnten sie, der Gnade Gottes treu zu bleiben." },
      { number: 44, text: "Am folgenden Sabbat versammelte sich fast die ganze Stadt, um das Wort des Herrn zu hören." },
      { number: 45, text: "Als die Juden die Scharen sahen, wurden sie eifersüchtig, widersprachen den Worten des Paulus und stießen Lästerungen aus." },
      { number: 46, text: "Paulus und Barnabas aber erklärten freimütig: Euch musste das Wort Gottes zuerst verkündet werden. Da ihr es aber zurückstoßt und euch des ewigen Lebens unwürdig zeigt, wenden wir uns jetzt an die Heiden.", footnotes: ["3,26; Mt 10,6; Röm 1,16; Apg 18,6"] },
      { number: 47, text: "Denn so hat uns der Herr aufgetragen: Ich habe dich zum Licht für die Völker gemacht, bis an das Ende der Erde sollst du das Heil sein.", footnotes: ["Jes 42,6; 49,6"] },
      { number: 48, text: "Als die Heiden das hörten, freuten sie sich und priesen das Wort des Herrn; und alle wurden gläubig, die für das ewige Leben bestimmt waren." },
      { number: 49, text: "Das Wort des Herrn aber verbreitete sich in der ganzen Gegend.", footnotes: ["2 Thess 3,1"] },
      { number: 50, text: "Die Juden jedoch hetzten die vornehmen gottesfürchtigen Frauen und die Ersten der Stadt auf, veranlassten eine Verfolgung gegen Paulus und Barnabas und vertrieben sie aus ihrem Gebiet.", footnotes: ["14,5.19; 1 Thess 2,15f; 2 Tim 3,11"] },
      { number: 51, text: "Diese aber schüttelten gegen sie den Staub von ihren Füßen und zogen nach Ikonion.", footnotes: ["Mt 10,14; Lk 9,5"] },
      { number: 52, text: "Und die Jünger waren voll Freude und erfüllt vom Heiligen Geist." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "In Ikonion, Lystra und Derbe: 14,1-201 In Ikonion gingen sie ebenfalls in die Synagoge der Juden und redeten in dieser Weise und eine große Zahl von Juden und Griechen wurde gläubig." },
      { number: 2, text: "Die Juden aber, die sich widersetzten, erregten und erbitterten die Heiden gegen die Brüder.", heading: "In Ikonion, Lystra und Derbe: 14,1-20", footnotes: ["13,45"] },
      { number: 3, text: "Längere Zeit nun blieben sie dort und predigten freimütig im Vertrauen auf den Herrn; er legte Zeugnis ab für das Wort seiner Gnade, indem er durch die Hände der Apostel Zeichen und Wunder geschehen ließ." },
      { number: 4, text: "Doch das Volk in der Stadt spaltete sich; die einen hielten zu den Juden, die andern zu den Aposteln." },
      { number: 5, text: "Als die Apostel merkten, dass die Heiden und die Juden zusammen mit ihren Führern entschlossen waren, sie zu misshandeln und zu steinigen,", footnotes: ["13,50; 2 Tim 3,11"] },
      { number: 6, text: "flohen sie in die Städte von Lykaonien, Lystra und Derbe, und in deren Umgebung.", footnotes: ["Mt 10,23"] },
      { number: 7, text: "Dort verkündeten sie das Evangelium." },
      { number: 8, text: "In Lystra war ein Mann, der von Geburt an gelähmt war; er saß ohne Kraft in den Füßen da und hatte nie gehen können.", footnotes: ["(8-10) 3,1-10"] },
      { number: 9, text: "Er hörte der Predigt des Paulus zu. Dieser blickte ihm fest ins Auge; und da er sah, dass der Mann darauf vertraute, gerettet zu werden," },
      { number: 10, text: "rief er laut: Steh auf! Stell dich aufrecht auf deine Füße! Da sprang der Mann auf und ging umher." },
      { number: 11, text: "Als die Menge sah, was Paulus getan hatte, fing sie an zu schreien und rief auf Lykaonisch: Die Götter sind in Menschengestalt zu uns herabgestiegen." },
      { number: 12, text: "Und sie nannten den Barnabas Zeus, den Paulus aber Hermes, weil er der Wortführer war." },
      { number: 13, text: "Der Priester des «Zeus vor der Stadt» brachte Stiere und Kränze an die Tore und wollte zusammen mit der Volksmenge ein Opfer darbringen." },
      { number: 14, text: "Als die Apostel Barnabas und Paulus davon hörten, zerrissen sie ihre Kleider, sprangen unter das Volk und riefen:" },
      { number: 15, text: "Männer, was tut ihr? Auch wir sind nur Menschen, von gleicher Art wie ihr; wir bringen euch das Evangelium, damit ihr euch von diesen nichtigen Götzen zu dem lebendigen Gott bekehrt, der den Himmel, die Erde und das Meer geschaffen hat und alles, was dazugehört.", footnotes: ["10,26; Jak 5,17; Ex 20,11; Ps 146,6; Jes 37,16; Jer 32,17"] },
      { number: 16, text: "Er ließ in den vergangenen Zeiten alle Völker ihre Wege gehen." },
      { number: 17, text: "Und doch hat er sich nicht unbezeugt gelassen: Er tat Gutes, gab euch vom Himmel her Regen und fruchtbare Zeiten; mit Nahrung und mit Freude erfüllte er euer Herz.", footnotes: ["17,24-28; Röm 1,19f"] },
      { number: 18, text: "Doch selbst mit diesen Worten konnten sie die Volksmenge kaum davon abbringen, ihnen zu opfern." },
      { number: 19, text: "Von Antiochia und Ikonion aber kamen Juden und überredeten die Volksmenge. Und sie steinigten den Paulus und schleiften ihn zur Stadt hinaus, in der Meinung, er sei tot.", footnotes: ["2 Kor 11,25; 2 Tim 3,11"] },
      { number: 20, text: "Als aber die Jünger ihn umringten, stand er auf und ging in die Stadt. Am anderen Tag zog er mit Barnabas nach Derbe weiter." },
      { number: 21, text: "Rückkehr nach Syrien: 14,21-2821 Als sie dieser Stadt das Evangelium verkündet und viele Jünger gewonnen hatten, kehrten sie nach Lystra, Ikonion und Antiochia zurück." },
      { number: 22, text: "Sie sprachen den Jüngern Mut zu und ermahnten sie, treu am Glauben fest zu halten; sie sagten: Durch viele Drangsale müssen wir in das Reich Gottes gelangen.", heading: "Rückkehr nach Syrien: 14,21-28", footnotes: ["11,23; 1 Thess 3,2f"] },
      { number: 23, text: "In jeder Gemeinde bestellten sie durch Handauflegung Älteste und empfahlen sie mit Gebet und Fasten dem Herrn, an den sie nun glaubten.", footnotes: ["20,28"] },
      { number: 24, text: "Nachdem sie durch Pisidien gezogen waren, kamen sie nach Pamphylien," },
      { number: 25, text: "verkündeten in Perge das Wort und gingen dann nach Attalia hinab.", footnotes: ["8,4; 10,36.44; 11,19; 16,6"] },
      { number: 26, text: "Von dort fuhren sie mit dem Schiff nach Antiochia, wo man sie für das Werk, das sie nun vollbracht hatten, der Gnade Gottes empfohlen hatte.", footnotes: ["13,1f"] },
      { number: 27, text: "Als sie dort angekommen waren, riefen sie die Gemeinde zusammen und berichteten alles, was Gott mit ihnen zusammen getan und dass er den Heiden die Tür zum Glauben geöffnet hatte.", footnotes: ["1 Kor 16,9"] },
      { number: 28, text: "Und sie blieben noch längere Zeit bei den Jüngern." },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Das Apostelkonzil in Jerusalem: 15,1-35Die Streitfrage: 15,1-51 Es kamen einige Leute von Judäa herab und lehrten die Brüder: Wenn ihr euch nicht nach dem Brauch des Mose beschneiden lasst, könnt ihr nicht gerettet werden.", footnotes: ["15,24; Gal 1,7; 5,10", "1-35: Zum Bericht des Lukas über das «Apostelkonzil» (vermutlich um 48/49 n. Chr) vgl. Gal 2,1-10. Die Frage, ob die Heidenchristen verpflichtet sind, das jüdische Gesetz zu halten, wurde auf diesem Konzil verneint (vgl. besonders 15,8-11)."] },
      { number: 2, text: "Nach großer Aufregung und heftigen Auseinandersetzungen zwischen ihnen und Paulus und Barnabas beschloss man, Paulus und Barnabas und einige andere von ihnen sollten wegen dieser Streitfrage zu den Aposteln und den Ältesten nach Jerusalem hinaufgehen.", heading: "Die Streitfrage: 15,1-5", footnotes: ["Gal 2,1"] },
      { number: 3, text: "Sie wurden von der Gemeinde feierlich verabschiedet und zogen durch Phönizien und Samarien; dabei berichteten sie den Brüdern von der Bekehrung der Heiden und bereiteten damit allen große Freude." },
      { number: 4, text: "Bei ihrer Ankunft in Jerusalem wurden sie von der Gemeinde und von den Aposteln und den Ältesten empfangen. Sie erzählten alles, was Gott mit ihnen zusammen getan hatte." },
      { number: 5, text: "Da erhoben sich einige aus dem Kreis der Pharisäer, die gläubig geworden waren, und sagten: Man muss sie beschneiden und von ihnen fordern, am Gesetz des Mose fest zu halten." },
      { number: 6, text: "Die Versammlung der Apostel und der Ältesten mit der Gemeinde: 15,6-216 Die Apostel und die Ältesten traten zusammen, um die Frage zu prüfen." },
      { number: 7, text: "Als ein heftiger Streit entstand, erhob sich Petrus und sagte zu ihnen: Brüder, wie ihr wisst, hat Gott schon längst hier bei euch die Entscheidung getroffen, dass die Heiden durch meinen Mund das Wort des Evangeliums hören und zum Glauben gelangen sollen.", heading: "Die Versammlung der Apostel und der Ältesten mit der Gemeinde: 15,6-21" },
      { number: 8, text: "Und Gott, der die Herzen kennt, bestätigte dies, indem er ihnen ebenso wie uns den Heiligen Geist gab.", footnotes: ["10,44; 11,15"] },
      { number: 9, text: "Er machte keinerlei Unterschied zwischen uns und ihnen; denn er hat ihre Herzen durch den Glauben gereinigt.", footnotes: ["10,34f"] },
      { number: 10, text: "Warum stellt ihr also jetzt Gott auf die Probe und legt den Jüngern ein Joch auf den Nacken, das weder unsere Väter noch wir tragen konnten?", footnotes: ["Mt 23,4; Gal 3,10; 5,1"] },
      { number: 11, text: "Wir glauben im Gegenteil, durch die Gnade Jesu, des Herrn, gerettet zu werden, auf die gleiche Weise wie jene.", footnotes: ["Gal 2,16; Eph 2,4-10"] },
      { number: 12, text: "Da schwieg die ganze Versammlung. Und sie hörten Barnabas und Paulus zu, wie sie erzählten, welch große Zeichen und Wunder Gott durch sie unter den Heiden getan hatte." },
      { number: 13, text: "Als sie geendet hatten, nahm Jakobus das Wort und sagte: Brüder, hört mich an!", footnotes: ["21,18; Gal 2,9"] },
      { number: 14, text: "Simon hat berichtet, dass Gott selbst zuerst eingegriffen hat, um aus den Heiden ein Volk für seinen Namen zu gewinnen." },
      { number: 15, text: "Damit stimmen die Worte der Propheten überein, die geschrieben haben:" },
      { number: 16, text: "Danach werde ich mich umwenden / und die zerfallene Hütte Davids wieder aufrichten; / ich werde sie aus ihren Trümmern wieder aufrichten / und werde sie wiederherstellen,", footnotes: ["Am 9,11f G; Jer 12,15"] },
      { number: 17, text: "damit die übrigen Menschen den Herrn suchen, / auch alle Völker, / über denen mein Name ausgerufen ist - / spricht der Herr, der das ausführt," },
      { number: 18, text: "was ihm seit Ewigkeit bekannt ist.", footnotes: ["Jes 45,21"] },
      { number: 19, text: "Darum halte ich es für richtig, den Heiden, die sich zu Gott bekehren, keine Lasten aufzubürden;" },
      { number: 20, text: "man weise sie nur an, Verunreinigung durch Götzen(opferfleisch) und Unzucht zu meiden und weder Ersticktes noch Blut zu essen.", footnotes: ["15,29; 21,25; Lev 18,6-18", "20.29: «Unzucht» meint hier wahrscheinlich verbotene Verwandtschaftsehen; «Ersticktes» und «Blut» beziehen sich auf den Genuss nicht geschächteter, d. h. beim Schlachten nicht ausgebluteter Tiere. Man nennt diese Anweisung auch «Aposteldekret» oder «Jakobusklauseln». Sie sollten ein ungestörtes Zusammenleben zwischen Juden- und Heidenchristen in gemischten Gemeinden ermöglichen."] },
      { number: 21, text: "Denn Mose hat seit ältesten Zeiten in jeder Stadt seine Verkündiger, da er in den Synagogen an jedem Sabbat verlesen wird." },
      { number: 22, text: "Die Beschlüsse der Versammlung: 15,22-2922 Da beschlossen die Apostel und die Ältesten zusammen mit der ganzen Gemeinde, Männer aus ihrer Mitte auszuwählen und sie zusammen mit Paulus und Barnabas nach Antiochia zu senden, nämlich Judas, genannt Barsabbas, und Silas, führende Männer unter den Brüdern." },
      { number: 23, text: "Sie gaben ihnen folgendes Schreiben mit: Die Apostel und die Ältesten, eure Brüder, grüßen die Brüder aus dem Heidentum in Antiochia, in Syrien und Zilizien.", heading: "Die Beschlüsse der Versammlung: 15,22-29" },
      { number: 24, text: "Wir haben gehört, dass einige von uns, denen wir keinen Auftrag erteilt haben, euch mit ihren Reden beunruhigt und eure Gemüter erregt haben.", footnotes: ["15,1; Gal 1,7; 5,10"] },
      { number: 25, text: "Deshalb haben wir uns geeinigt und beschlossen, Männer auszuwählen und zusammen mit unseren lieben Brüdern Barnabas und Paulus zu euch zu schicken," },
      { number: 26, text: "die beide für den Namen Jesu Christi, unseres Herrn, ihr Leben eingesetzt haben." },
      { number: 27, text: "Wir haben Judas und Silas abgesandt, die euch das Gleiche auch mündlich mitteilen sollen." },
      { number: 28, text: "Denn der Heilige Geist und wir haben beschlossen, euch keine weitere Last aufzuerlegen als diese notwendigen Dinge:", footnotes: ["Mt 23,4; 1 Kor 8,1"] },
      { number: 29, text: "Götzenopferfleisch, Blut, Ersticktes und Unzucht zu meiden. Wenn ihr euch davor hütet, handelt ihr richtig. Lebt wohl!" },
      { number: 30, text: "Die Mitteilung der Beschlüsse an die Gemeinde von Antiochia: 15,30-3530 Man verabschiedete die Abgesandten und sie zogen hinab nach Antiochia, riefen die Gemeinde zusammen und übergaben ihr den Brief." },
      { number: 31, text: "Die Brüder lasen ihn und freuten sich über die Ermunterung.", heading: "Die Mitteilung der Beschlüsse an die Gemeinde von Antiochia: 15,30-35" },
      { number: 32, text: "Judas und Silas, selbst Propheten, sprachen ihnen mit vielen Worten Mut zu und stärkten sie.", footnotes: ["11,27; 13,1"] },
      { number: 33, text: "Nach einiger Zeit wurden sie von den Brüdern in Frieden wieder zu denen entlassen, die sie abgesandt hatten." },
      { number: 34, text: "[]", footnotes: ["Einzelne Textzeugen fügen hier ein: Silas aber beschloss dazubleiben; so reiste Judas allein (nach Jerusalem) ab."] },
      { number: 35, text: "Paulus aber und Barnabas blieben in Antiochia und lehrten und verkündeten mit vielen anderen das Wort des Herrn." },
      { number: 36, text: "Die zweite Missionsreise des Paulus: 15,36 - 18,22Die Trennung des Paulus von Barnabas: 15,36-4136 Nach einiger Zeit sagte Paulus zu Barnabas: Wir wollen wieder aufbrechen und sehen, wie es den Brüdern in all den Städten geht, in denen wir das Wort des Herrn verkündet haben." },
      { number: 37, text: "Barnabas wollte auch den Johannes, genannt Markus, mitnehmen;", heading: "Die Trennung des Paulus von Barnabas: 15,36-41", footnotes: ["12,12.25"] },
      { number: 38, text: "doch Paulus bestand darauf, ihn nicht mitzunehmen, weil er sie in Pamphylien im Stich gelassen hatte, nicht mit ihnen gezogen war und an ihrer Arbeit nicht mehr teilgenommen hatte.", footnotes: ["13,13; Kol 4,10"] },
      { number: 39, text: "Es kam zu einer heftigen Auseinandersetzung, so dass sie sich voneinander trennten; Barnabas nahm Markus mit und segelte nach Zypern." },
      { number: 40, text: "Paulus aber wählte sich Silas und reiste ab, nachdem die Brüder ihn der Gnade des Herrn empfohlen hatten." },
      { number: 41, text: "Er zog durch Syrien und Zilizien und stärkte die Gemeinden.", footnotes: ["9,30; 11,25; Gal 1,21"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Paulus in Lykaonien: 16,1-51 Er kam auch nach Derbe und nach Lystra. Dort war ein Jünger namens Timotheus, der Sohn einer gläubig gewordenen Jüdin und eines Griechen.", footnotes: ["17,14; Röm 16,21; Phil 2,19; 1 Kor 4,17; 2 Tim 1,5", "Vgl. die Anmerkung zu 1 Thess 1,1."] },
      { number: 2, text: "Er war Paulus von den Brüdern in Lystra und Ikonion empfohlen worden.", heading: "Paulus in Lykaonien: 16,1-5" },
      { number: 3, text: "Paulus wollte ihn als Begleiter mitnehmen und ließ ihn mit Rücksicht auf die Juden, die in jenen Gegenden wohnten, beschneiden; denn alle wussten, dass sein Vater ein Grieche war." },
      { number: 4, text: "Als sie nun durch die Städte zogen, überbrachten sie ihnen die von den Aposteln und den Ältesten in Jerusalem gefassten Beschlüsse und trugen ihnen auf, sich daran zu halten.", footnotes: ["15,23-29"] },
      { number: 5, text: "So wurden die Gemeinden im Glauben gestärkt und wuchsen von Tag zu Tag." },
      { number: 6, text: "Durch Kleinasien bis Troas: 16,6-106 Weil ihnen aber vom Heiligen Geist verwehrt wurde, das Wort in der Provinz Asien zu verkünden, reisten sie durch Phrygien und das galatische Land.", footnotes: ["18,23", "6-10: Der Abschnitt schildert einen Vorgang von weltgeschichtlicher Bedeutung: Das Evangelium gelangt nach Europa."] },
      { number: 7, text: "Sie zogen an Mysien entlang und versuchten, Bithynien zu erreichen; doch auch das erlaubte ihnen der Geist Jesu nicht.", heading: "Durch Kleinasien bis Troas: 16,6-10" },
      { number: 8, text: "So durchwanderten sie Mysien und kamen nach Troas hinab." },
      { number: 9, text: "Dort hatte Paulus in der Nacht eine Vision. Ein Mazedonier stand da und bat ihn: Komm herüber nach Mazedonien und hilf uns!" },
      { number: 10, text: "Auf diese Vision hin wollten wir sofort nach Mazedonien abfahren; denn wir waren überzeugt, dass uns Gott dazu berufen hatte, dort das Evangelium zu verkünden." },
      { number: 11, text: "Das Wirken des Paulus in Philippi: 16,11-4011 So brachen wir von Troas auf und fuhren auf dem kürzesten Weg nach Samothrake und am folgenden Tag nach Neapolis." },
      { number: 12, text: "Von dort gingen wir nach Philippi, in eine Stadt im ersten Bezirk von Mazedonien, eine Kolonie. In dieser Stadt hielten wir uns einige Tage auf.", heading: "Das Wirken des Paulus in Philippi: 16,11-40", footnotes: ["Philippi war seit 31 v. Chr. römische «Kolonie», eine Ansiedlung vorwiegend von ausgedienten Soldaten und Beamten. Deshalb bezeichnen sich die Bewohner in 16,21 als «Römer»."] },
      { number: 13, text: "Am Sabbat gingen wir durch das Stadttor hinaus an den Fluss, wo wir eine Gebetsstätte vermuteten. Wir setzten uns und sprachen zu den Frauen, die sich eingefunden hatten." },
      { number: 14, text: "Eine Frau namens Lydia, eine Purpurhändlerin aus der Stadt Thyatira, hörte zu; sie war eine Gottesfürchtige und der Herr öffnete ihr das Herz, sodass sie den Worten des Paulus aufmerksam lauschte.", footnotes: ["Zu «Gottesfürchtige» vgl. die Anmerkung zu 6,5."] },
      { number: 15, text: "Als sie und alle, die zu ihrem Haus gehörten, getauft waren, bat sie: Wenn ihr überzeugt seid, dass ich fest an den Herrn glaube, kommt in mein Haus und bleibt da. Und sie drängte uns." },
      { number: 16, text: "Als wir einmal auf dem Weg zur Gebetsstätte waren, begegnete uns eine Magd, die einen Wahrsagegeist hatte und mit der Wahrsagerei ihren Herren großen Gewinn einbrachte." },
      { number: 17, text: "Sie lief Paulus und uns nach und schrie: Diese Menschen sind Diener des höchsten Gottes; sie verkünden euch den Weg des Heils.", footnotes: ["Mk 1,24-26.34"] },
      { number: 18, text: "Das tat sie viele Tage lang. Da wurde Paulus ärgerlich, wandte sich um und sagte zu dem Geist: Ich befehle dir im Namen Jesu Christi: Verlass diese Frau! Und im gleichen Augenblick verließ er sie." },
      { number: 19, text: "Als aber ihre Herren sahen, dass sie keinen Gewinn mehr erwarten konnten, ergriffen sie Paulus und Silas, schleppten sie auf den Markt vor die Stadtbehörden," },
      { number: 20, text: "führten sie den obersten Beamten vor und sagten: Diese Männer bringen Unruhe in unsere Stadt. Es sind Juden;" },
      { number: 21, text: "sie verkünden Sitten und Bräuche, die wir als Römer weder annehmen können noch ausüben dürfen." },
      { number: 22, text: "Da erhob sich das Volk gegen sie und die obersten Beamten ließen ihnen die Kleider vom Leib reißen und befahlen, sie mit Ruten zu schlagen.", footnotes: ["2 Kor 11,25; Phil 1,30; 1 Thess 2,2"] },
      { number: 23, text: "Sie ließen ihnen viele Schläge geben und sie ins Gefängnis bringen; dem Gefängniswärter befahlen sie, sie in sicherem Gewahrsam zu halten." },
      { number: 24, text: "Auf diesen Befehl hin warf er sie in das innere Gefängnis und schloss zur Sicherheit ihre Füße in den Block." },
      { number: 25, text: "Um Mitternacht beteten Paulus und Silas und sangen Loblieder; und die Gefangenen hörten ihnen zu." },
      { number: 26, text: "Plötzlich begann ein gewaltiges Erdbeben, sodass die Grundmauern des Gefängnisses wankten. Mit einem Schlag sprangen die Türen auf und allen fielen die Fesseln ab." },
      { number: 27, text: "Als der Gefängniswärter aufwachte und alle Türen des Gefängnisses offen sah, zog er sein Schwert, um sich zu töten; denn er meinte, die Gefangenen seien entflohen." },
      { number: 28, text: "Da rief Paulus laut: Tu dir nichts an! Wir sind alle noch da." },
      { number: 29, text: "Jener rief nach Licht, stürzte hinein und fiel Paulus und Silas zitternd zu Füßen." },
      { number: 30, text: "Er führte sie hinaus und sagte: Ihr Herren, was muss ich tun, um gerettet zu werden?", footnotes: ["2,37; Joh 6,28"] },
      { number: 31, text: "Sie antworteten: Glaube an Jesus, den Herrn und du wirst gerettet werden, du und dein Haus.", footnotes: ["11,14; Joh 6,29"] },
      { number: 32, text: "Und sie verkündeten ihm und allen in seinem Haus das Wort Gottes." },
      { number: 33, text: "Er nahm sie in jener Nachtstunde bei sich auf, wusch ihre Striemen und ließ sich sogleich mit allen seinen Angehörigen taufen." },
      { number: 34, text: "Dann führte er sie in seine Wohnung hinauf, ließ ihnen den Tisch decken und war mit seinem ganzen Haus voll Freude, weil er zum Glauben an Gott gekommen war." },
      { number: 35, text: "Als es Tag wurde, schickten die obersten Beamten die Amtsdiener und ließen sagen: Lass jene Männer frei!" },
      { number: 36, text: "Der Gefängniswärter überbrachte Paulus die Nachricht: Die obersten Beamten haben (die Amtsdiener) hergeschickt und befohlen, euch freizulassen. Geht also, zieht in Frieden!" },
      { number: 37, text: "Paulus aber sagte zu ihnen: Sie haben uns ohne Urteil öffentlich auspeitschen lassen, obgleich wir römische Bürger sind, und haben uns ins Gefängnis geworfen. Und jetzt möchten sie uns heimlich fortschicken? Nein! Sie sollen selbst kommen und uns hinausführen.", footnotes: ["22,25.29", "Paulus besaß von seinem Vater her das römische Bürgerrecht (vgl. 22,25-29)."] },
      { number: 38, text: "Die Amtsdiener meldeten es den obersten Beamten. Diese erschraken, als sie hörten, es seien römische Bürger." },
      { number: 39, text: "Und sie kamen, um sie zu beschwichtigen, führten sie hinaus und baten sie, die Stadt zu verlassen." },
      { number: 40, text: "Vom Gefängnis aus gingen die beiden zu Lydia. Dort fanden sie die Brüder, sprachen ihnen Mut zu und zogen dann weiter." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Paulus in Thessalonich und Beröa: 17,1-151 Auf dem Weg über Amphipolis und Apollonia kamen sie nach Thessalonich. Dort hatten die Juden eine Synagoge.", footnotes: ["1 Thess 2,2"] },
      { number: 2, text: "Nach seiner Gewohnheit ging Paulus zu ihnen und redete an drei Sabbaten zu ihnen, wobei er von den Schriften ausging.", heading: "Paulus in Thessalonich und Beröa: 17,1-15" },
      { number: 3, text: "Er legte sie ihnen aus und erklärte, dass der Messias leiden und von den Toten auferstehen musste. Und er sagte: Jesus, den ich euch verkünde, ist dieser Messias.", footnotes: ["Lk 24,26f.45f"] },
      { number: 4, text: "Einige von ihnen ließen sich überzeugen und schlossen sich Paulus und Silas an, außerdem eine große Schar gottesfürchtiger Griechen, darunter nicht wenige Frauen aus vornehmen Kreisen." },
      { number: 5, text: "Die Juden wurden eifersüchtig, holten sich einige nichtsnutzige Männer, die sich auf dem Markt herumtrieben, wiegelten mit ihrer Hilfe das Volk auf und brachten die Stadt in Aufruhr. Sie zogen zum Haus des Jason und wollten die beiden vor das Volk führen.", footnotes: ["Röm 16,21"] },
      { number: 6, text: "Sie fanden sie aber nicht. Daher schleppten sie den Jason und einige Brüder vor die Stadtpräfekten und schrien: Diese Leute, die die ganze Welt in Aufruhr gebracht haben, sind jetzt auch hier," },
      { number: 7, text: "und Jason hat sie aufgenommen. Sie alle verstoßen gegen die Gesetze des Kaisers; denn sie behaupten, ein anderer sei König, nämlich Jesus.", footnotes: ["Lk 23,2; Joh 19,12"] },
      { number: 8, text: "So brachten sie die Menge und die Stadtpräfekten, die das hörten, in Erregung." },
      { number: 9, text: "Diese nahmen von Jason und den anderen eine Bürgschaft und ließen sie frei." },
      { number: 10, text: "Die Brüder schickten noch in der Nacht Paulus und Silas weiter nach Beröa. Nach ihrer Ankunft gingen sie in die Synagoge der Juden." },
      { number: 11, text: "Diese waren freundlicher als die in Thessalonich; mit großer Bereitschaft nahmen sie das Wort auf und forschten Tag für Tag in den Schriften nach, ob sich dies wirklich so verhielte.", footnotes: ["Joh 5,39"] },
      { number: 12, text: "Viele von ihnen wurden gläubig, und ebenso nicht wenige der vornehmen griechischen Frauen und Männer." },
      { number: 13, text: "Als aber die Juden von Thessalonich erfuhren, dass Paulus auch in Beröa das Wort Gottes verkündete, kamen sie dorthin, um das Volk aufzuwiegeln und aufzuhetzen.", footnotes: ["1 Thess 2,14-16"] },
      { number: 14, text: "Da schickten die Brüder Paulus sogleich weg zum Meer hinunter. Silas und Timotheus aber blieben zurück." },
      { number: 15, text: "Die Begleiter des Paulus brachten ihn nach Athen. Mit dem Auftrag an Silas und Timotheus, Paulus möglichst rasch nachzukommen, kehrten sie zurück.", footnotes: ["1 Thess 3,1"] },
      { number: 16, text: "Paulus in Athen: 17,16-3416 Während Paulus in Athen auf sie wartete, erfasste ihn heftiger Zorn; denn er sah die Stadt voll von Götzenbildern." },
      { number: 17, text: "Er redete in der Synagoge mit den Juden und Gottesfürchtigen und auf dem Markt sprach er täglich mit denen, die er gerade antraf.", heading: "Paulus in Athen: 17,16-34" },
      { number: 18, text: "Einige von den epikureischen und stoischen Philosophen diskutierten mit ihm und manche sagten: Was will denn dieser Schwätzer? Andere aber: Es scheint ein Verkünder fremder Gottheiten zu sein. Er verkündete nämlich das Evangelium von Jesus und von der Auferstehung.", footnotes: ["1 Kor 1,22"] },
      { number: 19, text: "Sie nahmen ihn mit, führten ihn zum Areopag und fragten: Können wir erfahren, was das für eine neue Lehre ist, die du vorträgst?" },
      { number: 20, text: "Du bringst uns recht befremdliche Dinge zu Gehör. Wir wüssten gern, worum es sich handelt." },
      { number: 21, text: "Alle Athener und die Fremden dort taten nichts lieber, als die letzten Neuigkeiten zu erzählen oder zu hören." },
      { number: 22, text: "Da stellte sich Paulus in die Mitte des Areopags und sagte: Athener, nach allem, was ich sehe, seid ihr besonders fromme Menschen." },
      { number: 23, text: "Denn als ich umherging und mir eure Heiligtümer ansah, fand ich auch einen Altar mit der Aufschrift: EINEM UNBEKANNTEN GOTT. Was ihr verehrt, ohne es zu kennen, das verkünde ich euch." },
      { number: 24, text: "Gott, der die Welt erschaffen hat und alles in ihr, er, der Herr über Himmel und Erde, wohnt nicht in Tempeln, die von Menschenhand gemacht sind.", footnotes: ["(24-28) Röm 1,19f"] },
      { number: 25, text: "Er lässt sich auch nicht von Menschen bedienen, als brauche er etwas: er, der allen das Leben, den Atem und alles gibt.", footnotes: ["Ps 50,10-13"] },
      { number: 26, text: "Er hat aus einem einzigen Menschen das ganze Menschengeschlecht erschaffen, damit es die ganze Erde bewohne. Er hat für sie bestimmte Zeiten und die Grenzen ihrer Wohnsitze festgesetzt.", footnotes: ["Dtn 32,8; Ijob 12,23"] },
      { number: 27, text: "Sie sollten Gott suchen, ob sie ihn ertasten und finden könnten; denn keinem von uns ist er fern.", footnotes: ["(27f) Jes 55,6; Jer 29,12-14; Weish 13,6; Ps 145,18; Jer 23,24; Ps 139; Röm 11,36; 1 Kor 8,6; Kol 1,16f"] },
      { number: 28, text: "Denn in ihm leben wir, bewegen wir uns und sind wir, wie auch einige von euren Dichtern gesagt haben: Wir sind von seiner Art.", footnotes: ["Zitat aus dem griechischen Dichter Aratus (3. Jh. v. Chr.)."] },
      { number: 29, text: "Da wir also von Gottes Art sind, dürfen wir nicht meinen, das Göttliche sei wie ein goldenes oder silbernes oder steinernes Gebilde menschlicher Kunst und Erfindung." },
      { number: 30, text: "Gott, der über die Zeiten der Unwissenheit hinweggesehen hat, lässt jetzt den Menschen verkünden, dass überall alle umkehren sollen." },
      { number: 31, text: "Denn er hat einen Tag festgesetzt, an dem er den Erdkreis in Gerechtigkeit richten wird, durch einen Mann, den er dazu bestimmt und vor allen Menschen dadurch ausgewiesen hat, dass er ihn von den Toten auferweckte.", footnotes: ["Ps 96,13; 98,9"] },
      { number: 32, text: "Als sie von der Auferstehung der Toten hörten, spotteten die einen, andere aber sagten: Darüber wollen wir dich ein andermal hören.", footnotes: ["1 Kor 1,23"] },
      { number: 33, text: "So ging Paulus aus ihrer Mitte weg." },
      { number: 34, text: "Einige Männer aber schlossen sich ihm an und wurden gläubig, unter ihnen auch Dionysius, der Areopagit, außerdem eine Frau namens Damaris und noch andere mit ihnen." },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Paulus in Korinth: 18,1-171 Hierauf verließ Paulus Athen und ging nach Korinth.", footnotes: ["18,18.26"] },
      { number: 2, text: "Dort traf er einen aus Pontus stammenden Juden namens Aquila, der vor kurzem aus Italien gekommen war, und dessen Frau Priszilla. Klaudius hatte nämlich angeordnet, dass alle Juden Rom verlassen müssten. Diesen beiden schloss er sich an,", heading: "Paulus in Korinth: 18,1-17", footnotes: ["Röm 16,3; 1 Kor 16,19; 2 Tim 4,19"] },
      { number: 3, text: "und da sie das gleiche Handwerk betrieben, blieb er bei ihnen und arbeitete dort. Sie waren Zeltmacher von Beruf.", footnotes: ["1 Kor 4,12; 9,12"] },
      { number: 4, text: "An jedem Sabbat lehrte er in der Synagoge und suchte Juden und Griechen zu überzeugen." },
      { number: 5, text: "Als aber Silas und Timotheus aus Mazedonien eingetroffen waren, widmete sich Paulus ganz der Verkündigung und bezeugte den Juden, dass Jesus der Messias sei." },
      { number: 6, text: "Als sie sich dagegen auflehnten und Lästerungen ausstießen, schüttelte er seine Kleider aus und sagte zu ihnen: Euer Blut komme über euer Haupt! Ich bin daran unschuldig. Von jetzt an werde ich zu den Heiden gehen.", footnotes: ["Mt 27,24f"] },
      { number: 7, text: "Und er ging von da in das Haus eines gewissen Titius Justus hinüber, eines Gottesfürchtigen, dessen Haus an die Synagoge grenzte." },
      { number: 8, text: "Krispus aber, der Synagogenvorsteher, kam mit seinem ganzen Haus zum Glauben an den Herrn; und viele Korinther, die (Paulus) hörten, wurden gläubig und ließen sich taufen.", footnotes: ["1 Kor 1,14"] },
      { number: 9, text: "Der Herr aber sagte nachts in einer Vision zu Paulus: Fürchte dich nicht! Rede nur, schweige nicht!", footnotes: ["1 Kor 2,3; 2 Kor 10,10"] },
      { number: 10, text: "Denn ich bin mit dir, niemand wird dir etwas antun. Viel Volk nämlich gehört mir in dieser Stadt." },
      { number: 11, text: "So blieb Paulus ein Jahr und sechs Monate und lehrte bei ihnen das Wort Gottes." },
      { number: 12, text: "Als aber Gallio Prokonsul von Achaia war, traten die Juden einmütig gegen Paulus auf, brachten ihn vor den Richterstuhl", footnotes: ["Die Amtszeit des Gallio in Achaia (Griechenland) fiel in die Zeit 51/52 n. Chr. oder 52/53 n. Chr., wie aus der in Delphi gefundenen «Gallio-Inschrift» hervorgeht."] },
      { number: 13, text: "und sagten: Dieser verführt die Menschen zu einer Gottesverehrung, die gegen das Gesetz verstößt." },
      { number: 14, text: "Als Paulus etwas erwidern wollte, sagte Gallio zu den Juden: Läge hier ein Vergehen oder Verbrechen vor, ihr Juden, so würde ich eure Klage ordnungsgemäß behandeln.", footnotes: ["25,18-20; Joh 18,31"] },
      { number: 15, text: "Streitet ihr jedoch über Lehre und Namen und euer Gesetz, dann seht selber zu! Darüber will ich nicht Richter sein." },
      { number: 16, text: "Und er wies sie vom Richterstuhl weg." },
      { number: 17, text: "Da ergriffen alle den Synagogenvorsteher Sosthenes und verprügelten ihn vor dem Richterstuhl. Gallio aber kümmerte sich nicht darum.", footnotes: ["1 Kor 1,1"] },
      { number: 18, text: "Die Rückkehr des Paulus über Ephesus nach Antiochia: 18,18-2218 Paulus blieb noch längere Zeit. Dann verabschiedete er sich von den Brüdern und segelte zusammen mit Priszilla und Aquila nach Syrien ab. In Kenchreä hatte er sich aufgrund eines Gelübdes den Kopf kahl scheren lassen.", footnotes: ["Röm 16,1", "Es handelt sich dabei um das sogenannte Nasiräatsgelübde (vgl. auch 21,23-26), das auf Num 6,1-21 zurückgeht (vgl. auch 1 Makk 3,49); es war zeitlich begrenzt und wurde durch ein Opfer eingelöst, bei dem das geschorene Haar mitverbrannt wurde."] },
      { number: 19, text: "Sie gelangten nach Ephesus. Dort trennte er sich von den beiden; er selbst ging in die Synagoge und redete zu den Juden.", heading: "Die Rückkehr des Paulus über Ephesus nach Antiochia: 18,18-22" },
      { number: 20, text: "Sie baten ihn, noch länger zu bleiben; aber er wollte nicht," },
      { number: 21, text: "sondern verabschiedete sich und sagte: Ich werde wieder zu euch kommen, wenn Gott es will. So fuhr er von Ephesus ab,", footnotes: ["Röm 1,10; 1 Kor 4,19; Jak 4,15"] },
      { number: 22, text: "landete in Cäsarea, zog (nach Jerusalem) hinauf, begrüßte dort die Gemeinde und ging dann nach Antiochia hinab." },
      { number: 23, text: "Die dritte Missionsreise des Paulus: 18,23 - 21,1723 Nachdem er dort einige Zeit geblieben war, zog er weiter, durchwanderte zuerst das galatische Land, dann Phrygien und stärkte alle Jünger." },
      { number: 24, text: "Die Missionsarbeit des Apollos in Ephesus: 18,24-2824 Ein Jude namens Apollos kam nach Ephesus. Er stammte aus Alexandria, war redekundig und in der Schrift bewandert.", footnotes: ["1 Kor 1,12; 3,4-6"] },
      { number: 25, text: "Er war unterwiesen im Weg des Herrn. Er sprach mit glühendem Geist und trug die Lehre von Jesus genau vor; doch kannte er nur die Taufe des Johannes.", heading: "Die Missionsarbeit des Apollos in Ephesus: 18,24-28", footnotes: ["19,3f"] },
      { number: 26, text: "Er begann, offen in der Synagoge zu sprechen. Priszilla und Aquila hörten ihn, nahmen ihn zu sich und legten ihm den Weg Gottes noch genauer dar." },
      { number: 27, text: "Als er nach Achaia gehen wollte, ermunterten ihn die Brüder dazu und schrieben den Jüngern, sie möchten ihn freundlich aufnehmen. Nach seiner Ankunft wurde er den Gläubigen durch die Gnade eine große Hilfe." },
      { number: 28, text: "Denn mit Nachdruck widerlegte er die Juden, indem er öffentlich aus der Schrift nachwies, dass Jesus der Messias sei.", footnotes: ["9,22; 17,3; 18,5"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Die Begegnung des Paulus mit Johannesjüngern: 19,1-71 Während Apollos sich in Korinth aufhielt, durchwanderte Paulus das Hochland und kam nach Ephesus hinab.", footnotes: ["1 Kor 16,8", "Ephesus war seit 133 v. Chr. das Zentrum der römischen Provinz Asien. Der Artemistempel gehörte zu den Sieben «Weltwundern» des Altertums. Die Zauberbücher von Ephesus (vgl. 19,19) waren in der ganzen damaligen Welt bekannt."] },
      { number: 2, text: "Er traf einige Jünger und fragte sie: Habt ihr den Heiligen Geist empfangen, als ihr gläubig wurdet? Sie antworteten ihm: Wir haben noch nicht einmal gehört, dass es einen Heiligen Geist gibt.", heading: "Die Begegnung des Paulus mit Johannesjüngern: 19,1-7" },
      { number: 3, text: "Da fragte er: Mit welcher Taufe seid ihr denn getauft worden? Sie antworteten: Mit der Taufe des Johannes.", footnotes: ["Mt 3,11; Lk 3,16", "Wörtlich: Auf was seid ihr denn getauft worden? Sie antworteten: Auf die Taufe des Johannes."] },
      { number: 4, text: "Paulus sagte: Johannes hat mit der Taufe der Umkehr getauft und das Volk gelehrt, sie sollten an den glauben, der nach ihm komme: an Jesus." },
      { number: 5, text: "Als sie das hörten, ließen sie sich auf den Namen Jesu, des Herrn, taufen." },
      { number: 6, text: "Paulus legte ihnen die Hände auf und der Heilige Geist kam auf sie herab; sie redeten in Zungen und weissagten.", footnotes: ["8,17", "Zu «Zungen» vgl. die Anmerkung zu Apg 2,4."] },
      { number: 7, text: "Es waren im ganzen ungefähr zwölf Männer." },
      { number: 8, text: "Das Wirken des Paulus in Ephesus: 19,8-108 Er ging in die Synagoge und lehrte drei Monate lang freimütig und suchte sie vom Reich Gottes zu überzeugen." },
      { number: 9, text: "Da aber einige verstockt waren, sich widersetzten und vor allen Leuten den (neuen) Weg verspotteten, trennte er sich mit den Jüngern von ihnen und unterwies sie täglich im Lehrsaal des Tyrannus.", heading: "Das Wirken des Paulus in Ephesus: 19,8-10", footnotes: ["18,6; Tit 3,10f; 2 Joh 10"] },
      { number: 10, text: "Das geschah zwei Jahre lang; auf diese Weise hörten alle Bewohner der Provinz Asien, Juden wie Griechen, das Wort des Herrn." },
      { number: 11, text: "Die Wundertaten des Paulus in Ephesus: 19,11-2011 Auch ungewöhnliche Wunder tat Gott durch die Hand des Paulus." },
      { number: 12, text: "Sogar seine Schweiß- und Taschentücher nahm man ihm vom Körper weg und legte sie den Kranken auf; da wichen die Krankheiten und die bösen Geister fuhren aus.", heading: "Die Wundertaten des Paulus in Ephesus: 19,11-20", footnotes: ["5,15"] },
      { number: 13, text: "Auch einige der umherziehenden jüdischen Beschwörer versuchten, den Namen Jesu, des Herrn, über den von bösen Geistern Besessenen anzurufen, indem sie sagten: Ich beschwöre euch bei dem Jesus, den Paulus verkündet.", footnotes: ["Lk 9,49"] },
      { number: 14, text: "Das taten sieben Söhne eines gewissen Skeuas, eines jüdischen Oberpriesters." },
      { number: 15, text: "Aber der böse Geist antwortete ihnen: Jesus kenne ich und auch Paulus ist mir bekannt. Doch wer seid ihr?", footnotes: ["Mk 1,34; Lk 4,41"] },
      { number: 16, text: "Und der Mensch, in dem der böse Geist hauste, sprang auf sie los, überwältigte sie und setzte ihnen so zu, dass sie nackt und zerschunden aus dem Haus fliehen mussten." },
      { number: 17, text: "Das wurde allen Juden und Griechen, die in Ephesus wohnten, bekannt; alle wurden von Furcht gepackt und der Name Jesu, des Herrn, wurde hoch gepriesen.", footnotes: ["5,5.11; 19,27f"] },
      { number: 18, text: "Viele, die gläubig geworden waren, kamen und bekannten offen, was sie (früher) getan hatten." },
      { number: 19, text: "Und nicht wenige, die Zauberei getrieben hatten, brachten ihre Zauberbücher herbei und verbrannten sie vor aller Augen. Man berechnete den Wert der Bücher auf fünfzigtausend Silberdrachmen." },
      { number: 20, text: "So wuchs das Wort des Herrn mit Macht und wurde stark.", footnotes: ["6,7; 12,24"] },
      { number: 21, text: "Der Aufruhr der Silberschmiede: 19,21-4021 Nach diesen Ereignissen nahm sich Paulus vor, über Mazedonien und Achaia nach Jerusalem zu reisen. Er sagte: Wenn ich dort gewesen bin, muss ich auch Rom sehen.", footnotes: ["23,11; Röm 1,10.13; 15,23-25; 1 Kor 16,6"] },
      { number: 22, text: "Er sandte zwei seiner Helfer, Timotheus und Erastus, nach Mazedonien voraus und blieb selbst noch eine Zeit lang in der Provinz Asien.", heading: "Der Aufruhr der Silberschmiede: 19,21-40", footnotes: ["Röm 16,21-23"] },
      { number: 23, text: "Um jene Zeit aber wurde der (neue) Weg Anlass zu einem schweren Aufruhr.", footnotes: ["2 Kor 1,8"] },
      { number: 24, text: "Denn ein Silberschmied namens Demetrius, der silberne Artemistempel herstellte und den Künstlern viel zu verdienen gab," },
      { number: 25, text: "rief diese und die anderen damit beschäftigten Arbeiter zusammen und sagte: Männer, ihr wisst, dass wir unseren Wohlstand diesem Gewerbe verdanken." },
      { number: 26, text: "Nun seht und hört ihr, dass dieser Paulus nicht nur in Ephesus, sondern fast in der ganzen Provinz Asien viele Leute verführt und aufgehetzt hat mit seiner Behauptung, die mit Händen gemachten Götter seien keine Götter.", footnotes: ["17,29"] },
      { number: 27, text: "So kommt nicht nur unser Geschäft in Verruf, sondern auch dem Heiligtum der großen Göttin Artemis droht Gefahr, nichts mehr zu gelten, ja sie selbst, die von der ganzen Provinz Asien und von der ganzen Welt verehrt wird, wird ihre Hoheit verlieren.", footnotes: ["von der ganzen Provinz, wörtlich: von ganz Asien."] },
      { number: 28, text: "Als sie das hörten, wurden sie wütend und schrien: Groß ist die Artemis von Ephesus!", footnotes: ["1 Kor 15,32; 2 Kor 1,8"] },
      { number: 29, text: "Die ganze Stadt geriet in Aufruhr; alles stürmte ins Theater und sie schleppten die Mazedonier Gaius und Aristarch, Reisegefährten des Paulus, mit sich.", footnotes: ["20,4; 27,2"] },
      { number: 30, text: "Als aber Paulus in die Volksversammlung gehen wollte, hielten ihn die Jünger zurück." },
      { number: 31, text: "Auch einige hohe Beamte der Provinz Asien, die mit ihm befreundet waren, schickten zu ihm und rieten ihm, nicht ins Theater zu gehen." },
      { number: 32, text: "Dort schrien die einen dies, die andern das; denn in der Versammlung herrschte ein großes Durcheinander und die meisten wussten gar nicht, weshalb man überhaupt zusammengekommen war." },
      { number: 33, text: "Die Juden schickten Alexander nach vorn und aus der Menge gab man ihm noch Hinweise. Alexander gab mit der Hand ein Zeichen und wollte vor der Volksversammlung eine Verteidigungsrede halten." },
      { number: 34, text: "Doch als sie merkten, dass er ein Jude war, schrien sie alle fast zwei Stunden lang wie aus einem Mund: Groß ist die Artemis von Ephesus!" },
      { number: 35, text: "Der Stadtschreiber aber brachte die Menge zur Ruhe und sagte: Männer von Ephesus! Wer wüsste nicht, dass die Stadt der Epheser die Tempelhüterin der Großen Artemis und ihres vom Himmel gefallenen Bildes ist?" },
      { number: 36, text: "Dies ist unbestreitbar; ihr müsst also Ruhe bewahren und dürft nichts Unüberlegtes tun." },
      { number: 37, text: "Ihr habt diese Männer hergeschleppt, die weder Tempelräuber noch Lästerer unserer Göttin sind." },
      { number: 38, text: "Wenn also Demetrius und seine Zunftgenossen eine Klage gegen irgend jemand haben, so gibt es dafür Gerichtstage und Prokonsuln; dort mögen sie einander verklagen." },
      { number: 39, text: "Wenn ihr aber noch etwas anderes vorzubringen habt, so kann das in der gesetzmäßigen Volksversammlung geklärt werden." },
      { number: 40, text: "Sonst sind wir in Gefahr, dass man uns nach dem heutigen Vorfall des Aufruhrs anklagt, weil kein Grund vorliegt, mit dem wir diesen Volksauflauf rechtfertigen könnten. Nach diesen Worten löste er die Versammlung auf." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Paulus in Mazedonien und Griechenland: 20,1-61 Nachdem der Tumult sich gelegt hatte, rief Paulus die Jünger zusammen und sprach ihnen Mut zu. Dann verabschiedete er sich und ging weg, um nach Mazedonien zu reisen.", footnotes: ["2 Kor 2,13"] },
      { number: 2, text: "Er zog durch die dortigen Gegenden und sprach oft und eindringlich zu den Jüngern. Dann begab er sich nach Griechenland;", heading: "Paulus in Mazedonien und Griechenland: 20,1-6" },
      { number: 3, text: "dort blieb er drei Monate. Als er mit dem Schiff nach Syrien fahren wollte, planten die Juden einen Anschlag auf ihn. So entschloss er sich, den Rückweg über Mazedonien zu nehmen." },
      { number: 4, text: "Dabei begleiteten ihn Sopater, der Sohn des Pyrrhus, aus Beröa, Aristarch und Sekundus aus Thessalonich, Gaius aus Derbe und Timotheus sowie Tychikus und Trophimus aus der Provinz Asien." },
      { number: 5, text: "Diese reisten voraus und warteten auf uns in Troas.", footnotes: ["16,8"] },
      { number: 6, text: "Nach den Tagen der Ungesäuerten Brote segelten wir von Philippi ab und kamen in fünf Tagen zu ihnen nach Troas, wo wir uns sieben Tage aufhielten.", footnotes: ["2 Kor 2,12"] },
      { number: 7, text: "Der Abschiedsbesuch des Paulus in Troas: 20,7-127 Als wir am ersten Wochentag versammelt waren, um das Brot zu brechen, redete Paulus zu ihnen, denn er wollte am folgenden Tag abreisen; und er dehnte seine Rede bis Mitternacht aus.", footnotes: ["1 Kor 16,2; Offb 1,10"] },
      { number: 8, text: "In dem Obergemach, in dem wir versammelt waren, brannten viele Lampen.", heading: "Der Abschiedsbesuch des Paulus in Troas: 20,7-12" },
      { number: 9, text: "Ein junger Mann namens Eutychus saß im offenen Fenster und sank, als die Predigt des Paulus sich länger hinzog, in tiefen Schlaf. Und er fiel im Schlaf aus dem dritten Stock hinunter; als man ihn aufhob, war er tot." },
      { number: 10, text: "Paulus lief hinab, warf sich über ihn, umfasste ihn und sagte: Beunruhigt euch nicht: Er lebt!", footnotes: ["1 Kön 17,21"] },
      { number: 11, text: "Dann stieg er wieder hinauf, brach das Brot und aß und redete mit ihnen bis zum Morgengrauen. So verließ er sie." },
      { number: 12, text: "Den jungen Mann aber führten sie lebend von dort weg. Und sie wurden voll Zuversicht." },
      { number: 13, text: "Von Troas nach Milet: 20,13-1613 Wir gingen voraus zum Schiff und fuhren nach Assos, wo wir Paulus an Bord nehmen sollten; so hatte er es angeordnet, weil er selbst zu Fuß gehen wollte." },
      { number: 14, text: "Als er in Assos zu uns stieß, nahmen wir ihn an Bord und erreichten Mitylene.", heading: "Von Troas nach Milet: 20,13-16" },
      { number: 15, text: "Von dort segelten wir am nächsten Tag ab und kamen bis auf die Höhe von Chios. Am anderen Tag liefen wir Samos an und landeten am folgenden Tag in Milet." },
      { number: 16, text: "Paulus hatte sich entschlossen, an Ephesus vorbeizufahren, um in der Provinz Asien keine Zeit zu verlieren. Denn er hatte es eilig, weil er, wenn möglich, am Pfingstfest in Jerusalem sein wollte." },
      { number: 17, text: "Die Abschiedsrede des Paulus in Milet: 20,17-3817 Von Milet aus schickte er jemand nach Ephesus und ließ die Ältesten der Gemeinde zu sich rufen." },
      { number: 18, text: "Als sie bei ihm eingetroffen waren, sagte er: Ihr wisst, wie ich vom ersten Tag an, seit ich die Provinz Asien betreten habe, die ganze Zeit in eurer Mitte war", heading: "Die Abschiedsrede des Paulus in Milet: 20,17-38" },
      { number: 19, text: "und wie ich dem Herrn in aller Demut diente unter Tränen und vielen Prüfungen, die ich durch die Nachstellungen der Juden erlitten habe," },
      { number: 20, text: "wie ich nichts verschwiegen habe von dem, was heilsam ist. Ich habe es euch verkündigt und habe euch gelehrt, öffentlich und in den Häusern." },
      { number: 21, text: "Ich habe Juden und Griechen beschworen, sich zu Gott zu bekehren und an Jesus Christus, unseren Herrn, zu glauben.", footnotes: ["13,6-40; 14,15; 17,1-4.10-13.16-34; 18,5-11; 19,8-10"] },
      { number: 22, text: "Nun ziehe ich, gebunden durch den Geist, nach Jerusalem und ich weiß nicht, was dort mit mir geschehen wird." },
      { number: 23, text: "Nur das bezeugt mir der Heilige Geist von Stadt zu Stadt, dass Fesseln und Drangsale auf mich warten.", footnotes: ["9,16; 21,4.11"] },
      { number: 24, text: "Aber ich will mit keinem Wort mein Leben wichtig nehmen, wenn ich nur meinen Lauf vollende und den Dienst erfülle, der mir von Jesus, dem Herrn, übertragen wurde: das Evangelium von der Gnade Gottes zu bezeugen.", footnotes: ["21,13; 2 Tim 4,7; Lk 4,18-21"] },
      { number: 25, text: "Nun aber weiß ich, dass ihr mich nicht mehr von Angesicht sehen werdet, ihr alle, zu denen ich gekommen bin und denen ich das Reich verkündet habe.", footnotes: ["Lk 9,2; 10,9"] },
      { number: 26, text: "Darum bezeuge ich euch am heutigen Tag: Ich bin unschuldig, wenn einer von euch allen verloren geht.", footnotes: ["18,6", "Ich bin unschuldig, wenn einer von euch allen verloren geht, wörtlich: Ich bin rein vom Blut aller (vgl. 18,6)."] },
      { number: 27, text: "Denn ich habe mich der Pflicht nicht entzogen, euch den ganzen Willen Gottes zu verkünden." },
      { number: 28, text: "Gebt Acht auf euch und auf die ganze Herde, in der euch der Heilige Geist zu Bischöfen bestellt hat, damit ihr als Hirten für die Kirche Gottes sorgt, die er sich durch das Blut seines eigenen Sohnes erworben hat.", footnotes: ["14,23; 1 Kor 12,28; Eph 4,11; Tit 1,5; 2 Tim 1,6; 1 Tim 4,14-16; 1 Petr 5,2", "Die Bezeichnung «Bischof» entspricht an dieser Stelle noch nicht der späteren Verwendung des Titels (vgl. die Anmerkung zu 1 Tim 3,1)."] },
      { number: 29, text: "Ich weiß: Nach meinem Weggang werden reißende Wölfe bei euch eindringen und die Herde nicht schonen.", footnotes: ["Mt 7,15; Lk 10,3; Joh 10,12"] },
      { number: 30, text: "Und selbst aus eurer Mitte werden Männer auftreten, die mit ihren falschen Reden die Jünger auf ihre Seite ziehen.", footnotes: ["Gal 4,17"] },
      { number: 31, text: "Seid also wachsam und denkt daran, dass ich drei Jahre lang Tag und Nacht nicht aufgehört habe, unter Tränen jeden einzelnen zu ermahnen.", footnotes: ["Mk 13,35.37; 2 Kor 2,4; 11,28; 1 Thess 2,11f"] },
      { number: 32, text: "Und jetzt vertraue ich euch Gott und dem Wort seiner Gnade an, das die Kraft hat, aufzubauen und das Erbe in der Gemeinschaft der Geheiligten zu verleihen.", footnotes: ["1 Thess 2,13"] },
      { number: 33, text: "Silber oder Gold oder Kleider habe ich von keinem verlangt;", footnotes: ["Mt 10,8; 1 Kor 9,12; 1 Sam 12,3"] },
      { number: 34, text: "ihr wisst selbst, dass für meinen Unterhalt und den meiner Begleiter diese Hände hier gearbeitet haben.", footnotes: ["18,3; 1 Kor 4,12; 9,12; 1 Thess 2,9; 2 Thess 3,7-9"] },
      { number: 35, text: "In allem habe ich euch gezeigt, dass man sich auf diese Weise abmühen und sich der Schwachen annehmen soll, in Erinnerung an die Worte Jesu, des Herrn, der selbst gesagt hat: Geben ist seliger als nehmen." },
      { number: 36, text: "Nach diesen Worten kniete er nieder und betete mit ihnen allen.", footnotes: ["21,5"] },
      { number: 37, text: "Und alle brachen in lautes Weinen aus, fielen Paulus um den Hals und küssten ihn;", footnotes: ["Röm 16,16; 1 Kor 16,20; 1 Petr 5,14"] },
      { number: 38, text: "am meisten schmerzte sie sein Wort, sie würden ihn nicht mehr von Angesicht sehen. Dann begleiteten sie ihn zum Schiff." },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Von Milet über Cäsarea nach Jerusalem: 21,1-171 Als die Zeit zur Abfahrt gekommen war, trennten wir uns von ihnen, fuhren auf dem kürzesten Weg nach Kos, am anderen Tag nach Rhodos und von dort nach Patara." },
      { number: 2, text: "Hier fanden wir ein Schiff, das nach Phönizien fuhr; wir gingen an Bord und fuhren ab.", heading: "Von Milet über Cäsarea nach Jerusalem: 21,1-17" },
      { number: 3, text: "Als wir Zypern sichteten, ließen wir es zur Linken liegen, segelten nach Syrien und landeten in Tyrus; hier sollte das Schiff seine Ladung löschen." },
      { number: 4, text: "Nachdem wir die Jünger ausfindig gemacht hatten, blieben wir sieben Tage bei ihnen. Auf eine Eingebung des Geistes hin warnten sie Paulus davor, nach Jerusalem zu gehen.", footnotes: ["20,23"] },
      { number: 5, text: "Als die Tage um waren, brachen wir zur Weiterreise auf, und alle, auch Frauen und Kinder, begleiteten uns bis vor die Stadt. Am Strand knieten wir nieder, beteten", footnotes: ["20,36"] },
      { number: 6, text: "und nahmen Abschied voneinander. Dann gingen wir an Bord; jene aber kehrten nach Hause zurück." },
      { number: 7, text: "So fuhren wir von Tyrus ab und beendeten unsere Seereise in Ptolemaïs. Wir begrüßten die Brüder und blieben einen Tag bei ihnen." },
      { number: 8, text: "Am folgenden Tag kamen wir nach Cäsarea. Wir gingen in das Haus des Evangelisten Philippus, der einer von den Sieben war, und blieben bei ihm.", footnotes: ["8,40"] },
      { number: 9, text: "Er hatte vier Töchter, prophetisch begabte Jungfrauen.", footnotes: ["2,17"] },
      { number: 10, text: "Wir blieben mehrere Tage. Da kam von Judäa ein Prophet namens Agabus herab", footnotes: ["11,28"] },
      { number: 11, text: "und besuchte uns. Er nahm den Gürtel des Paulus, band sich Füße und Hände und sagte: So spricht der Heilige Geist: Den Mann, dem dieser Gürtel gehört, werden die Juden in Jerusalem ebenso fesseln und den Heiden ausliefern.", footnotes: ["Röm 15,31"] },
      { number: 12, text: "Als wir das hörten, redeten wir ihm zusammen mit den Einheimischen zu, nicht nach Jerusalem hinaufzuziehen." },
      { number: 13, text: "Doch Paulus antwortete: Warum weint ihr und macht mir das Herz schwer? Ich bin bereit, mich in Jerusalem für den Namen Jesu, des Herrn, fesseln zu lassen und sogar zu sterben." },
      { number: 14, text: "Da er sich nicht überreden ließ, gaben wir nach und sagten: Der Wille des Herrn geschehe." },
      { number: 15, text: "Nach diesen Tagen bereiteten wir uns zur Reise vor und zogen hinauf nach Jerusalem." },
      { number: 16, text: "Auch einige Jünger aus Cäsarea begleiteten uns und brachten uns zu einem gewissen Mnason aus Zypern, bei dem wir wohnen sollten; er war ein Jünger aus der Anfangszeit." },
      { number: 17, text: "Als wir nach Jerusalem kamen, wurden wir von den Brüdern freudig empfangen." },
      { number: 18, text: "Verhaftung und Gefangenschaft des Paulus: 21,18 - 28,31Paulus bei Jakobus und das Nasiräatsgelübde: 21,18-2618 Am folgenden Tag ging Paulus mit uns zu Jakobus; auch alle Ältesten fanden sich ein.", footnotes: ["15,13; Gal 1,19"] },
      { number: 19, text: "Er begrüßte sie und berichtete im einzelnen alles, was Gott durch seinen Dienst unter den Heiden getan hatte.", heading: "Paulus bei Jakobus und das Nasiräatsgelübde: 21,18-26" },
      { number: 20, text: "Als sie das hörten, priesen sie Gott und sagten zu ihm: Du siehst, Bruder, wie viele Tausende unter den Juden gläubig geworden sind, und sie alle sind Eiferer für das Gesetz." },
      { number: 21, text: "Nun hat man ihnen von dir erzählt: Du lehrst alle unter den Heiden lebenden Juden, von Mose abzufallen, und forderst sie auf, ihre Kinder nicht zu beschneiden und sich nicht an die Bräuche zu halten.", footnotes: ["18,13; Gal 3,25"] },
      { number: 22, text: "Was nun? Sicher werden sie hören, dass du gekommen bist." },
      { number: 23, text: "Tu also, was wir dir sagen: Bei uns sind vier Männer, die ein Gelübde auf sich genommen haben." },
      { number: 24, text: "Nimm sie mit und weihe dich zusammen mit ihnen; trag die Kosten für sie, damit sie sich das Haar abscheren lassen können. So wird jeder einsehen, dass an dem, was man von dir erzählt hat, nichts ist, sondern dass auch du das Gesetz genau beachtest.", footnotes: ["1 Kor 9,19-23; 10,32f"] },
      { number: 25, text: "Über die gläubig gewordenen Heiden aber haben wir ja einen Beschluss gefasst und ihnen geschrieben, sie sollten sich vor Götzenopferfleisch, Blut, Ersticktem und Unzucht hüten.", footnotes: ["15,20.29", "Vgl. die Anmerkung zu 15,20."] },
      { number: 26, text: "Da nahm Paulus die Männer mit und weihte sich am nächsten Tag zusammen mit ihnen, ging dann in den Tempel und meldete das Ende der Weihetage an, damit für jeden von ihnen das Opfer dargebracht werde.", footnotes: ["Num 6,1-20; 1 Kor 9,20"] },
      { number: 27, text: "Die Verhaftung des Paulus: 21,27-4027 Als die sieben Tage zu Ende gingen, sahen ihn die Juden aus der Provinz Asien im Tempel. Sie brachten das ganze Volk in Aufruhr, ergriffen ihn" },
      { number: 28, text: "und schrien: Israeliten! Kommt zu Hilfe! Das ist der Mensch, der in aller Welt Lehren verbreitet, die sich gegen das Volk und das Gesetz und gegen diesen Ort richten; er hat sogar Griechen in den Tempel mitgenommen und diesen heiligen Ort entweiht.", heading: "Die Verhaftung des Paulus: 21,27-40", footnotes: ["6,13; 18,13", "28f: Heiden in die inneren, den Juden vorbehaltenen Vorhöfe des Tempels mitzunehmen, war bei Todesstrafe verboten."] },
      { number: 29, text: "Sie hatten nämlich kurz zuvor den Epheser Trophimus mit ihm zusammen in der Stadt gesehen und meinten, Paulus habe ihn in den Tempel mitgenommen.", footnotes: ["20,4; 2 Tim 4,20"] },
      { number: 30, text: "Da geriet die ganze Stadt in Aufregung und das Volk lief zusammen. Sie ergriffen Paulus und zerrten ihn aus dem Tempel und sofort wurden die Tore geschlossen." },
      { number: 31, text: "Schon wollten sie ihn umbringen, da brachte man dem Obersten der Kohorte die Meldung hinauf: Ganz Jerusalem ist in Aufruhr!" },
      { number: 32, text: "Er eilte sofort mit Soldaten und Hauptleuten zu ihnen hinunter. Als sie den Obersten und die Soldaten sahen, hörten sie auf, Paulus zu schlagen." },
      { number: 33, text: "Der Oberst trat hinzu, verhaftete ihn, ließ ihn mit zwei Ketten fesseln und fragte, wer er sei und was er getan habe.", footnotes: ["20,23; 21,11"] },
      { number: 34, text: "In der Menge schrien die einen dies, die andern das. Da er bei dem Lärm nichts Sicheres ermitteln konnte, befahl er, ihn in die Kaserne zu führen." },
      { number: 35, text: "Als Paulus an die Freitreppe kam, mussten ihn die Soldaten wegen des Andrangs der Menge tragen." },
      { number: 36, text: "Denn das Volk lief hinterher und schrie: Weg mit ihm!" },
      { number: 37, text: "Als man Paulus in die Kaserne bringen wollte, sagte er zum Obersten: Darf ich ein Wort mit dir reden? Der antwortete: Du verstehst Griechisch?" },
      { number: 38, text: "Dann bist du also nicht der Ägypter, der vor einiger Zeit die viertausend Sikarier aufgewiegelt und in die Wüste hinausgeführt hat?", footnotes: ["«Sikarier» (Dolchmänner) waren jüdische Freiheitskämpfer."] },
      { number: 39, text: "Paulus antwortete: Ich bin ein Jude aus Tarsus in Zilizien, Bürger einer nicht unbedeutenden Stadt. Ich bitte dich, gestatte mir, zum Volk zu sprechen.", footnotes: ["9,11"] },
      { number: 40, text: "Als der Oberst es erlaubte, stellte sich Paulus auf die Freitreppe und gab dem Volk mit der Hand ein Zeichen. Alles wurde still und er redete sie in Hebräischer Sprache an:" },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Die Rede des Paulus im Tempelvorhof: 22,1-211 Brüder und Väter! Hört, was ich euch zu meiner Verteidigung zu sagen habe.", footnotes: ["7,2; 13,26"] },
      { number: 2, text: "Als sie hörten, dass er in Hebräischer Sprache zu ihnen redete, waren sie noch ruhiger. Und er sagte:", heading: "Die Rede des Paulus im Tempelvorhof: 22,1-21" },
      { number: 3, text: "Ich bin ein Jude, geboren in Tarsus in Zilizien, hier in dieser Stadt erzogen, zu Füßen Gamaliëls genau nach dem Gesetz der Väter ausgebildet, ein Eiferer für Gott, wie ihr alle es heute seid.", footnotes: ["(3-21) 9,1-29; 26,9-23; 5,34; 2 Kor 11,22; Phil 3,5; Röm 10,2; Gal 1,14"] },
      { number: 4, text: "Ich habe den (neuen) Weg bis auf den Tod verfolgt, habe Männer und Frauen gefesselt und in die Gefängnisse eingeliefert.", footnotes: ["8,3"] },
      { number: 5, text: "Das bezeugen mir der Hohepriester und der ganze Rat der Ältesten. Von ihnen erhielt ich auch Briefe an die Brüder und zog nach Damaskus, um dort ebenfalls die Anhänger (der neuen Lehre) zu fesseln und zur Bestrafung nach Jerusalem zu bringen." },
      { number: 6, text: "Als ich nun unterwegs war und mich Damaskus näherte, da geschah es, dass mich um die Mittagszeit plötzlich vom Himmel her ein helles Licht umstrahlte." },
      { number: 7, text: "Ich stürzte zu Boden und hörte eine Stimme zu mir sagen: Saul, Saul, warum verfolgst du mich?" },
      { number: 8, text: "Ich antwortete: Wer bist du, Herr? Er sagte zu mir: Ich bin Jesus, der Nazoräer, den du verfolgst." },
      { number: 9, text: "Meine Begleiter sahen zwar das Licht, die Stimme dessen aber, der zu mir sprach, hörten sie nicht." },
      { number: 10, text: "Ich sagte: Herr, was soll ich tun? Der Herr antwortete: Steh auf, und geh nach Damaskus, dort wird dir alles gesagt werden, was du nach Gottes Willen tun sollst." },
      { number: 11, text: "Da ich aber vom Glanz jenes Lichtes geblendet war, sodass ich nicht mehr sehen konnte, wurde ich von meinen Begleitern an der Hand geführt und gelangte so nach Damaskus." },
      { number: 12, text: "Ein gewisser Hananias, ein frommer und gesetzestreuer Mann, der bei allen Juden dort in gutem Ruf stand," },
      { number: 13, text: "kam zu mir, trat vor mich und sagte: Bruder Saul, du sollst wieder sehen! Und im gleichen Augenblick konnte ich ihn sehen." },
      { number: 14, text: "Er sagte: Der Gott unserer Väter hat dich dazu erwählt, seinen Willen zu erkennen, den Gerechten zu sehen und die Stimme seines Mundes zu hören;" },
      { number: 15, text: "denn du sollst vor allen Menschen sein Zeuge werden für das, was du gesehen und gehört hast." },
      { number: 16, text: "Was zögerst du noch? Steh auf, lass dich taufen und deine Sünden abwaschen und rufe seinen Namen an!" },
      { number: 17, text: "Als ich später nach Jerusalem zurückgekehrt war und im Tempel betete, da geriet ich in eine Verzückung.", footnotes: ["9,26; Gal 1,18f"] },
      { number: 18, text: "Und ich sah ihn, wie er zu mir sagte: Beeil dich, verlasse sofort Jerusalem; denn sie werden dein Zeugnis über mich nicht annehmen." },
      { number: 19, text: "Da sagte ich: Herr, sie wissen doch, dass ich es war, der deine Gläubigen ins Gefängnis werfen und in den Synagogen auspeitschen ließ.", footnotes: ["22,4"] },
      { number: 20, text: "Auch als das Blut deines Zeugen Stephanus vergossen wurde, stand ich dabei; ich stimmte zu und passte auf die Kleider derer auf, die ihn umbrachten.", footnotes: ["7,58; 8,1"] },
      { number: 21, text: "Aber er sagte zu mir: Brich auf, denn ich will dich in die Ferne zu den Heiden senden.", footnotes: ["9,15; 13,2; 26,16-18; Röm 1,5; Gal 2,7"] },
      { number: 22, text: "Paulus und der römische Oberst: 22,22-2922 Bis zu diesem Wort hörten sie ihm zu, dann fingen sie an zu schreien: Weg mit so einem Menschen! Er darf nicht am Leben bleiben.", footnotes: ["1 Kor 4,13"] },
      { number: 23, text: "Sie lärmten, zerrissen ihre Kleider und warfen Staub in die Luft.", heading: "Paulus und der römische Oberst: 22,22-29" },
      { number: 24, text: "Da befahl der Oberst, ihn in die Kaserne zu führen, und ordnete an, ihn unter Geißelschlägen zu verhören. Auf diese Weise wollte er herausfinden, warum sie derart gegen ihn tobten." },
      { number: 25, text: "Als sie ihn aber für die Geißelung festbanden, sagte Paulus zu dem Hauptmann, der dabeistand: Dürft ihr jemand, der das römische Bürgerrecht besitzt, geißeln, noch dazu ohne Verurteilung?", footnotes: ["16,37; 23,27"] },
      { number: 26, text: "Als der Hauptmann das hörte, ging er zum Obersten, meldete es und sagte: Was hast du vor? Der Mann ist ein Römer." },
      { number: 27, text: "Der Oberst kam zu Paulus und fragte ihn: Sag mir, bist du ein Römer? Er antwortete: Ja." },
      { number: 28, text: "Da antwortete der Oberst: Ich habe für dieses Bürgerrecht ein Vermögen gezahlt. Paulus sagte: Ich bin sogar als Römer geboren." },
      { number: 29, text: "Sofort ließen die, die ihn verhören sollten, von ihm ab. Und der Oberst erschrak, als er erfuhr, dass es ein Römer war, den er hatte fesseln lassen.", footnotes: ["16,38f"] },
      { number: 30, text: "Paulus vor dem Hohen Rat: 22,30 - 23,1130 Weil er genau wissen wollte, was die Juden ihm vorwarfen, ließ er ihn am nächsten Tag aus dem Gefängnis holen und befahl, die Hohenpriester und der ganze Hohe Rat sollten sich versammeln. Und er ließ Paulus hinunterführen und ihnen gegenüberstellen." },
    ] },
    { number: 23, verses: [
      { number: 1, text: "Paulus schaute mit festem Blick auf den Hohen Rat und sagte: Brüder! Bis zum heutigen Tag lebe ich vor Gott mit völlig reinem Gewissen.", footnotes: ["24,16; 2 Kor 1,12"] },
      { number: 2, text: "Der Hohepriester Hananias aber befahl denen, die bei ihm standen, ihn auf den Mund zu schlagen." },
      { number: 3, text: "Da sagte Paulus zu ihm: Dich wird Gott schlagen, du übertünchte Wand! Du sitzt hier, um mich nach dem Gesetz zu richten, und entgegen dem Gesetz befiehlst du, mich zu schlagen?", footnotes: ["Mt 23,27; Lev 19,15"] },
      { number: 4, text: "Die Umstehenden sagten: Du wagst es, den Hohenpriester Gottes zu schmähen?" },
      { number: 5, text: "Paulus antwortete: Ich wusste nicht, Brüder, dass er der Hohepriester ist. Denn es heißt in der Schrift: Einen Fürsten deines Volkes sollst du nicht verfluchen.", footnotes: ["Ex 22,27"] },
      { number: 6, text: "Da Paulus aber wusste, dass der eine Teil zu den Sadduzäern, der andere zu den Pharisäern gehörte, rief er vor dem Hohen Rat aus: Brüder, ich bin Pharisäer und ein Sohn von Pharisäern; wegen der Hoffnung und wegen der Auferstehung der Toten stehe ich vor Gericht.", footnotes: ["4,2; 22,3; 26,5", "Paulus gibt zu verstehen, dass die christliche Auferstehungshoffnung mit der Erwartung der Pharisäer übereinstimmt."] },
      { number: 7, text: "Als er das sagte, brach ein Streit zwischen den Pharisäern und den Sadduzäern aus, und die Versammlung spaltete sich." },
      { number: 8, text: "Die Sadduzäer behaupten nämlich, es gebe weder eine Auferstehung noch Engel noch Geister, die Pharisäer dagegen bekennen sich zu all dem.", footnotes: ["Lk 20,27; Mt 22,23"] },
      { number: 9, text: "Es erhob sich ein lautes Geschrei und einige Schriftgelehrte aus dem Kreis der Pharisäer standen auf und verfochten ihre Ansicht. Sie sagten: Wir finden nichts Schlimmes an diesem Menschen. Vielleicht hat doch ein Geist oder ein Engel zu ihm gesprochen." },
      { number: 10, text: "Als der Streit heftiger wurde, befürchtete der Oberst, sie könnten Paulus zerreißen. Daher ließ er die Wachtruppe herabkommen, ihn mit Gewalt aus ihrer Mitte herausholen und in die Kaserne bringen." },
      { number: 11, text: "In der folgenden Nacht aber trat der Herr zu Paulus und sagte: Hab Mut! Denn so wie du in Jerusalem meine Sache bezeugt hast, sollst du auch in Rom Zeugnis ablegen.", footnotes: ["18,9; 19,21; 27,24; 28,23.31"] },
      { number: 12, text: "Die Verschwörung der Juden gegen Paulus: 23,12-2212 Nach Tagesanbruch rotteten sich die Juden zusammen und schworen einen heiligen Eid, weder zu essen noch zu trinken, bis sie Paulus getötet hätten." },
      { number: 13, text: "An dieser Verschwörung waren mehr als vierzig Männer beteiligt.", heading: "Die Verschwörung der Juden gegen Paulus: 23,12-22" },
      { number: 14, text: "Sie gingen zu den Hohenpriestern und den Ältesten und sagten: Wir haben mit einem heiligen Eid geschworen, nichts zu essen, bis wir Paulus getötet haben." },
      { number: 15, text: "Geht also jetzt zusammen mit dem Hohen Rat zum Obersten und bittet ihn, Paulus zu euch herunterzuführen, da ihr den Fall noch genauer untersuchen wollt; wir aber halten uns bereit, um ihn, noch bevor er hierher kommt, umzubringen." },
      { number: 16, text: "Der Neffe des Paulus, der Sohn seiner Schwester, erfuhr jedoch von dem Anschlag. Er ging in die Kaserne und verständigte Paulus." },
      { number: 17, text: "Paulus ließ einen der Hauptleute rufen und sagte: Bring diesen jungen Mann zum Obersten, denn er hat ihm etwas zu melden." },
      { number: 18, text: "Der nahm ihn mit sich, brachte ihn zum Obersten und sagte: Der Gefangene Paulus hat mich gerufen und gebeten, diesen jungen Mann zu dir zu führen, da er dir etwas mitzuteilen habe." },
      { number: 19, text: "Der Oberst fasste ihn bei der Hand, nahm ihn beiseite und fragte: Was hast du mir zu melden?" },
      { number: 20, text: "Er antwortete: Die Juden haben verabredet, dich zu bitten, du mögest morgen den Paulus vor den Hohen Rat hinunterführen lassen. Angeblich wollen sie Genaueres über ihn erfahren." },
      { number: 21, text: "Trau ihnen nicht! Denn mehr als vierzig Männer von ihnen lauern ihm auf. Sie haben sich geschworen, weder zu essen noch zu trinken, bis sie ihn umgebracht haben; schon stehen sie bereit und warten auf deine Anordnung." },
      { number: 22, text: "Der Oberst befahl dem jungen Mann: Sag niemand etwas darüber, dass du mir das angezeigt hast. Dann ließ er ihn gehen." },
      { number: 23, text: "Die Überführung des Paulus nach Cäsarea: 23,23-3523 Er rief zwei von den Hauptleuten und gab ihnen den Befehl: Haltet von der dritten Stunde der Nacht an zweihundert Soldaten zum Marsch nach Cäsarea bereit, außerdem siebzig Reiter und zweihundert Leichtbewaffnete;" },
      { number: 24, text: "auch Tragtiere soll man bereitstellen, Paulus aufsitzen lassen und sicher zum Statthalter Felix bringen.", heading: "Die Überführung des Paulus nach Cäsarea: 23,23-35" },
      { number: 25, text: "Und er schrieb einen Brief mit folgendem Inhalt:" },
      { number: 26, text: "Klaudius Lysias entbietet dem erlauchten Statthalter Felix seinen Gruß!", footnotes: ["Felix war bis etwa 58 n. Chr. Statthalter von Judäa."] },
      { number: 27, text: "Dieser Mann wurde von den Juden ergriffen und wäre beinahe von ihnen umgebracht worden; da habe ich mit der Wachtruppe eingegriffen und ihn befreit. Ich hatte nämlich erfahren, dass er Römer ist.", footnotes: ["21,33; 22,25"] },
      { number: 28, text: "Und weil ich ermitteln wollte, wessen sie ihn beschuldigen, brachte ich ihn vor ihren Hohen Rat.", footnotes: ["22,30"] },
      { number: 29, text: "Ich fand heraus, dass er wegen Streitfragen über ihr Gesetz angeschuldigt wird, dass aber keine Anklage gegen ihn vorliegt, auf die Tod oder Haft steht.", footnotes: ["18,14f"] },
      { number: 30, text: "Da mir aber angezeigt wurde, gegen den Mann sei ein Anschlag geplant, schicke ich ihn sogleich zu dir; auch habe ich die Kläger angewiesen, ihre Sache gegen ihn bei dir vorzubringen." },
      { number: 31, text: "Die Soldaten übernahmen Paulus, wie ihnen befohlen war, und brachten ihn bei Nacht bis Antipatris." },
      { number: 32, text: "Am anderen Tag ließen sie die Reiter mit ihm weiterziehen und kehrten in die Kaserne zurück." },
      { number: 33, text: "Diese gaben nach ihrer Ankunft in Cäsarea den Brief beim Statthalter ab und führten ihm Paulus vor." },
      { number: 34, text: "Er las (den Brief) und fragte Paulus, aus welcher Provinz er stamme. Als er hörte, er sei aus Zilizien," },
      { number: 35, text: "sagte er: Ich werde dich vernehmen, sobald deine Ankläger eingetroffen sind. Dann befahl er, ihn im Prätorium des Herodes in Gewahrsam zu halten.", footnotes: ["22,3"] },
    ] },
    { number: 24, verses: [
      { number: 1, text: "Paulus vor dem römischen Statthalter Felix: 24,1-271 Nach fünf Tagen kam der Hohepriester Hananias mit einigen Ältesten und dem Anwalt Tertullus herab und sie brachten beim Statthalter ihre Klage gegen Paulus vor." },
      { number: 2, text: "Er wurde herbeigeholt und Tertullus erhob Anklage mit folgenden Worten: Tiefen Frieden genießen wir durch dich, und durch deine Umsicht hat sich für dieses Volk vieles gebessert.", heading: "Paulus vor dem römischen Statthalter Felix: 24,1-27" },
      { number: 3, text: "Das erkennen wir immer und überall mit großer Dankbarkeit an, erlauchter Felix." },
      { number: 4, text: "Um dich aber nicht länger bemühen zu müssen, bitte ich dich, uns in deiner Milde kurz anzuhören." },
      { number: 5, text: "Wir finden nämlich, dieser Mann ist eine Pest, ein Unruhestifter bei allen Juden in der Welt und ein Rädelsführer der Nazoräersekte.", footnotes: ["17,6"] },
      { number: 6, text: "Er hat sogar versucht, den Tempel zu entweihen. Wir haben ihn festgenommen" },
      { number: 6, text: "[]", footnotes: ["Einige Textzeugen fügen ein: . . . und nach unserem Gesetz richten wollen."] },
      { number: 7, text: "[]", footnotes: ["Einige Textzeugen fügen ein: . . . Aber der Oberst Lysias kam herzu und entriss ihn mit Gewalt unseren Händen und befahl, dass seine Ankläger zu dir kommen sollen."] },
      { number: 8, text: "[]", footnotes: ["Einige Textzeugen fügen ein: . . . und befahl, dass seine Ankläger zu dir kommen sollen."] },
      { number: 8, text: "Wenn du ihn verhörst, wirst du selbst alles ermitteln können, wessen wir ihn anklagen." },
      { number: 9, text: "Und die Juden unterstützten ihn, indem sie behaupteten, so sei es." },
      { number: 10, text: "Auf einen Wink des Statthalters erwiderte Paulus: Da ich dich seit vielen Jahren als Richter für dieses Volk kenne, verteidige ich meine Sache voll Zuversicht." },
      { number: 11, text: "Wie du feststellen kannst, sind erst zwölf Tage vergangen, seit ich nach Jerusalem hinaufgezogen bin, um Gott anzubeten.", footnotes: ["21,17"] },
      { number: 12, text: "Sie haben mich weder im Tempel noch in den Synagogen noch anderswo in der Stadt dabei angetroffen, dass ich ein Streitgespräch mit jemand geführt oder einen Aufruhr im Volk erregt hätte." },
      { number: 13, text: "Sie können dir auch nichts von dem beweisen, was sie mir jetzt vorwerfen." },
      { number: 14, text: "Das allerdings bekenne ich dir: Dem (neuen) Weg entsprechend, den sie eine Sekte nennen, diene ich dem Gott meiner Väter. Ich glaube an alles, was im Gesetz und in den Propheten steht,", footnotes: ["9,2"] },
      { number: 15, text: "und ich habe dieselbe Hoffnung auf Gott, die auch diese hier haben: dass es eine Auferstehung der Gerechten und Ungerechten geben wird.", footnotes: ["Dan 12,2; 2 Makk 7,9; Joh 5,28f"] },
      { number: 16, text: "Deshalb bemühe auch ich mich, vor Gott und den Menschen immer ein reines Gewissen zu haben.", footnotes: ["23,1"] },
      { number: 17, text: "Nach mehreren Jahren bin ich nun zu meinem Volk gekommen, um Spenden zu überbringen und zu opfern.", footnotes: ["Röm 15,25f; Gal 2,10"] },
      { number: 18, text: "Als ich mich zu diesem Zweck im Tempel hatte weihen lassen, trafen mich - nicht mit einer Volksmenge und nicht bei einem Tumult -", footnotes: ["21,27"] },
      { number: 19, text: "einige Juden aus der Provinz Asien; sie müssten vor dir erscheinen und Anklage erheben, wenn sie etwas gegen mich vorzubringen haben." },
      { number: 20, text: "Oder diese hier sollen doch selbst sagen, was für ein Vergehen sie herausgefunden haben, als ich vor dem Hohen Rat stand," },
      { number: 21, text: "es sei denn der eine Satz, den ich in ihrer Mitte ausgerufen habe: Wegen der Auferstehung der Toten stehe ich heute vor eurem Gericht.", footnotes: ["23,6"] },
      { number: 22, text: "Felix, der den (neuen) Weg genau kannte, vertagte den Fall mit den Worten: Sobald der Oberst Lysias herabkommt, werde ich eure Sache entscheiden.", footnotes: ["23,26"] },
      { number: 23, text: "Den Hauptmann wies er an, Paulus weiter in Gewahrsam zu halten, jedoch in leichter Haft, und niemand von den Seinen daran zu hindern, für ihn zu sorgen.", footnotes: ["27,3"] },
      { number: 24, text: "Einige Tage darauf erschien Felix mit seiner Gemahlin Drusilla, einer Jüdin, ließ Paulus rufen und hörte an, was er ihm über den Glauben an Christus Jesus berichtete." },
      { number: 25, text: "Als aber die Rede auf Gerechtigkeit, Enthaltsamkeit und das bevorstehende Gericht kam, erschrak Felix und unterbrach ihn: Für jetzt kannst du gehen; wenn ich Zeit finde, werde ich dich wieder rufen." },
      { number: 26, text: "Dabei hoffte er, von Paulus Geld zu erhalten. Deshalb ließ er ihn auch häufig kommen und unterhielt sich mit ihm." },
      { number: 27, text: "Nach zwei Jahren aber wurde Porzius Festus Nachfolger des Felix; und weil Felix den Juden einen Gefallen erweisen wollte, ließ er Paulus in der Haft zurück.", footnotes: ["25,9", "Porzius Festus war von etwa 58 n. Chr. an Statthalter von Judäa."] },
    ] },
    { number: 25, verses: [
      { number: 1, text: "Paulus vor dem Statthalter Festus - Seine Berufung an den Kaiser: 25,1-121 Als Festus in der Provinz eingetroffen war, zog er drei Tage später von Cäsarea nach Jerusalem hinauf." },
      { number: 2, text: "Da erstatteten die Hohenpriester und die Vornehmsten der Juden bei ihm Anzeige gegen Paulus. Sie ersuchten ihn, gegen Paulus vorzugehen,", heading: "Paulus vor dem Statthalter Festus - Seine Berufung an den Kaiser: 25,1-12" },
      { number: 3, text: "und baten ihn um den Gefallen, Paulus nach Jerusalem bringen zu lassen. Sie wollten ihn nämlich unterwegs aus einem Hinterhalt heraus ermorden.", footnotes: ["23,15"] },
      { number: 4, text: "Festus jedoch antwortete, Paulus bleibe in Cäsarea in Haft und er selbst wolle in Kürze abreisen." },
      { number: 5, text: "Die unter euch, die dafür zuständig sind, sagte er, können mit hinabkommen, und wenn gegen den Mann etwas vorliegt, sollen sie gegen ihn Anklage erheben." },
      { number: 6, text: "Er hielt sich nicht länger als acht oder zehn Tage bei ihnen auf, dann reiste er nach Cäsarea hinab. Am folgenden Tag setzte er sich auf den Richterstuhl und ließ Paulus vorführen." },
      { number: 7, text: "Als dieser erschien, umringten ihn die Juden, die von Jerusalem herabgekommen waren, und brachten viele schwere Beschuldigungen vor, konnten sie aber nicht beweisen." },
      { number: 8, text: "Paulus verteidigte sich: Ich habe mich weder gegen das Gesetz der Juden noch gegen den Tempel noch gegen den Kaiser vergangen." },
      { number: 9, text: "Festus jedoch wollte den Juden einen Gefallen erweisen und antwortete dem Paulus: Willst du nach Jerusalem hinaufgehen und dich dort unter meinem Vorsitz dieser Sache wegen richten lassen?", footnotes: ["24,27"] },
      { number: 10, text: "Paulus sagte: Ich stehe vor dem Richterstuhl des Kaisers und da muss ich gerichtet werden. Den Juden habe ich kein Unrecht getan, wie auch du sehr wohl weißt." },
      { number: 11, text: "Wenn ich wirklich ein Unrecht begangen und etwas getan habe, worauf die Todesstrafe steht, weigere ich mich nicht zu sterben. Wenn aber ihre Anklage gegen mich unbegründet ist, kann mich niemand ihnen ausliefern. Ich lege Berufung beim Kaiser ein!" },
      { number: 12, text: "Da besprach sich Festus mit seinen Ratgebern und antwortete: An den Kaiser hast du appelliert; zum Kaiser sollst du gehen." },
      { number: 13, text: "Paulus vor dem Statthalter Festus und König Agrippa: 25,13 - 26,3213 Einige Tage später trafen König Agrippa und Berenike in Cäsarea ein, um Festus ihre Aufwartung zu machen.", footnotes: ["Es handelt sich um Herodes Agrippa II., Sohn des Herodes Agrippa I (vgl. die Anmerkung zu 12,1). Er hielt zu den Römern, wurde 50 n. Chr. Tetrarch und 53 n. Chr. König, hatte aber nur sehr beschränkte Rechte. Er war unverheiratet und lebte mit seiner Schwester Berenike zusammen."] },
      { number: 14, text: "Sie blieben mehrere Tage dort. Da trug Festus dem König den Fall des Paulus vor und sagte: Von Felix ist ein Mann als Gefangener zurückgelassen worden,", heading: "Paulus vor dem Statthalter Festus und König Agrippa: 25,13 - 26,32" },
      { number: 15, text: "gegen den die Hohenpriester und die Ältesten der Juden, als ich in Jerusalem war, vorstellig wurden. Sie forderten seine Verurteilung," },
      { number: 16, text: "ich aber erwiderte ihnen, es sei bei den Römern nicht üblich, einen Menschen auszuliefern, bevor nicht der Angeklagte den Anklägern gegenübergestellt sei und Gelegenheit erhalten habe, sich gegen die Anschuldigungen zu verteidigen." },
      { number: 17, text: "Als sie dann zusammen hierher kamen, setzte ich mich gleich am nächsten Tag auf den Richterstuhl und ließ den Mann vorführen." },
      { number: 18, text: "Bei der Gegenüberstellung brachten die Kläger keine Anklage wegen solcher Verbrechen vor, die ich vermutet hatte;" },
      { number: 19, text: "sie führten nur einige Streitfragen gegen ihn ins Feld, die ihre Religion und einen gewissen Jesus betreffen, der gestorben ist, von dem Paulus aber behauptet, er lebe.", footnotes: ["18,15"] },
      { number: 20, text: "Da ich mich auf die Untersuchung dieser Dinge nicht verstand, fragte ich, ob er nach Jerusalem gehen wolle, um sich dort deswegen richten zu lassen." },
      { number: 21, text: "Paulus jedoch legte Berufung ein; er wollte bis zur Entscheidung der kaiserlichen Majestät in Haft bleiben. Daher gab ich Befehl, ihn in Haft zu halten, bis ich ihn zum Kaiser schicken kann." },
      { number: 22, text: "Da sagte Agrippa zu Festus: Ich würde den Mann gern selbst hören. Morgen, antwortete er, sollst du ihn hören." },
      { number: 23, text: "So kamen am folgenden Tag Agrippa und Berenike mit großem Gepränge und betraten die Empfangshalle, zusammen mit den Obersten und den vornehmsten Männern der Stadt. Auf Befehl des Festus wurde Paulus vorgeführt," },
      { number: 24, text: "und Festus sagte: König Agrippa und all ihr Männer, die ihr hier bei uns seid! Da seht ihr den Mann, dessentwegen mich alle Juden in Jerusalem und auch hier bestürmt haben mit ihrem Geschrei, er dürfe nicht länger am Leben bleiben." },
      { number: 25, text: "Ich aber konnte bei ihm nichts feststellen, worauf die Todesstrafe steht. Da er jedoch selbst an die kaiserliche Majestät appelliert hat, habe ich beschlossen, ihn hinzuschicken." },
      { number: 26, text: "Etwas Genaues über ihn weiß ich meinem Herrn allerdings nicht zu schreiben. Darum habe ich ihn euch und vor allem dir, König Agrippa, vorführen lassen, um nach dem Verhör zu wissen, was ich schreiben kann." },
      { number: 27, text: "Denn es scheint mir unsinnig, einen Gefangenen zu schicken, ohne anzugeben, was man ihm vorwirft." },
    ] },
    { number: 26, verses: [
      { number: 1, text: "Da sagte Agrippa zu Paulus: Du hast die Erlaubnis, in eigener Sache zu reden. Paulus erhob die Hand und sagte zu seiner Verteidigung:" },
      { number: 2, text: "Ich schätze mich glücklich, König Agrippa, dass ich mich heute vor dir verteidigen darf wegen all der Dinge, die mir die Juden vorwerfen," },
      { number: 3, text: "besonders, da du ein Kenner aller jüdischen Satzungen und Streitfragen bist. Deshalb bitte ich, mich geduldig anzuhören." },
      { number: 4, text: "Das Leben, das ich seit meiner Jugend bei meinem Volk und in Jerusalem geführt habe, ist allen Juden von Anfang an bekannt.", footnotes: ["Gal 1,14; Phil 3,5f"] },
      { number: 5, text: "Ich bin ihnen von früher her bekannt, und wenn sie wollen, können sie bezeugen, dass ich nach der strengsten Richtung unserer Religion gelebt habe, nämlich als Pharisäer.", footnotes: ["23,6"] },
      { number: 6, text: "Und jetzt stehe ich vor Gericht wegen der Hoffnung auf die Verheißung, die von Gott an unsere Väter ergangen ist.", footnotes: ["28,20"] },
      { number: 7, text: "Unser Zwölfstämmevolk hofft, sie zu erlangen, und deshalb dient es Gott unablässig, bei Tag und Nacht. Dieser Hoffnung wegen, König, werde ich von den Juden angeklagt." },
      { number: 8, text: "Warum haltet ihr es für unglaubhaft, dass Gott Tote auferweckt?" },
      { number: 9, text: "Ich selbst meinte, ich müsste den Namen Jesu, des Nazoräers, heftig bekämpfen.", footnotes: ["(9-20) 9,1-29; 22,3-21"] },
      { number: 10, text: "Das habe ich in Jerusalem auch getan: Ich ließ mir von den Hohenpriestern Vollmacht geben und sperrte viele der Heiligen ins Gefängnis; und wenn sie hingerichtet werden sollten, stimmte ich dafür." },
      { number: 11, text: "Und in allen Synagogen habe ich oft versucht, sie durch Strafen zur Lästerung zu zwingen; in maßloser Wut habe ich sie sogar bis in Städte außerhalb des Landes verfolgt." },
      { number: 12, text: "So zog ich auch mit der Vollmacht und Erlaubnis der Hohenpriester nach Damaskus." },
      { number: 13, text: "Da sah ich unterwegs, König, mitten am Tag ein Licht, das mich und meine Begleiter vom Himmel her umstrahlte, heller als die Sonne." },
      { number: 14, text: "Wir alle stürzten zu Boden, und ich hörte eine Stimme auf Hebräisch zu mir sagen: Saul, Saul, warum verfolgst du mich? Es wird dir schwer fallen, gegen den Stachel auszuschlagen." },
      { number: 15, text: "Ich antwortete: Wer bist du, Herr? Der Herr sagte: Ich bin Jesus, den du verfolgst." },
      { number: 16, text: "Steh auf, stell dich auf deine Füße! Denn ich bin dir erschienen, um dich zum Diener und Zeugen dessen zu erwählen, was du gesehen hast und was ich dir noch zeigen werde.", footnotes: ["Ez 2,1.3; 2 Kor 12,1; Gal 1,15f"] },
      { number: 17, text: "Ich will dich vor dem Volk und den Heiden retten, zu denen ich dich sende," },
      { number: 18, text: "um ihnen die Augen zu öffnen. Denn sie sollen sich von der Finsternis zum Licht und von der Macht des Satans zu Gott bekehren und sollen durch den Glauben an mich die Vergebung der Sünden empfangen und mit den Geheiligten am Erbe teilhaben.", footnotes: ["Jes 35,5; 42,7.16; 61,1; Apg 20,32; Kol 1,13"] },
      { number: 19, text: "Daher, König Agrippa, habe ich mich der himmlischen Erscheinung nicht widersetzt," },
      { number: 20, text: "sondern zuerst denen in Damaskus und in Jerusalem, dann im ganzen Land Judäa und bei den Heiden verkündet, sie sollten umkehren, sich Gott zuwenden und der Umkehr entsprechend handeln." },
      { number: 21, text: "Aus diesem Grund haben mich einige Juden im Tempel ergriffen und versucht, mich umzubringen.", footnotes: ["21,30f"] },
      { number: 22, text: "Doch ich habe Gottes Hilfe erfahren bis zum heutigen Tag; so stehe ich da als Zeuge für Groß und Klein und sage nichts anderes als das, was nach dem Wort der Propheten und des Mose geschehen soll:" },
      { number: 23, text: "dass der Christus leiden müsse und dass er, als erster von den Toten auferstanden, dem Volk und den Heiden ein Licht verkünden werde.", footnotes: ["Lk 24,26.44-47; 1 Kor 15,20; Kol 1,18"] },
      { number: 24, text: "Als er sich mit diesen Worten verteidigte, rief Festus laut: Du bist verrückt, Paulus! Das viele Studieren in den (heiligen) Schriften treibt dich zum Wahnsinn." },
      { number: 25, text: "Paulus erwiderte: Ich bin nicht verrückt, erlauchter Festus; was ich sage, ist wahr und vernünftig." },
      { number: 26, text: "Der König versteht sich auf diese Dinge; deshalb spreche ich auch freimütig zu ihm. Ich bin überzeugt, dass ihm nichts davon entgangen ist; das alles hat sich ja nicht in irgendeinem Winkel zugetragen." },
      { number: 27, text: "König Agrippa, glaubst du den Propheten? Ich weiß, du glaubst." },
      { number: 28, text: "Darauf sagte Agrippa zu Paulus: Fast überredest du mich dazu, mich als Christ auszugeben.", footnotes: ["11,26; 1 Petr 4,16"] },
      { number: 29, text: "Paulus antwortete: Ich wünschte mir von Gott, dass früher oder später nicht nur du, sondern alle, die mich heute hören, das werden, was ich bin, freilich ohne diese Fesseln." },
      { number: 30, text: "Da erhoben sich der König und der Statthalter, auch Berenike und alle, die bei ihnen saßen." },
      { number: 31, text: "Sie zogen sich zurück, besprachen sich miteinander und sagten: Dieser Mann tut nichts, worauf Tod oder Haft steht." },
      { number: 32, text: "Und Agrippa sagte zu Festus: Der Mann könnte freigelassen werden, wenn er nicht an den Kaiser appelliert hätte.", footnotes: ["25,11"] },
    ] },
    { number: 27, verses: [
      { number: 1, text: "Die Abfahrt des gefangenen Apostels nach Rom: 27,1-131 Als unsere Abfahrt nach Italien feststand, wurden Paulus und einige andere Gefangene einem Hauptmann der kaiserlichen Kohorte namens Julius übergeben.", footnotes: ["25,12"] },
      { number: 2, text: "Wir bestiegen ein Schiff aus Adramyttium, das die Orte entlang der Küste Kleinasiens anlaufen sollte, und fuhren ab; bei uns war Aristarch, der Mazedonier aus Thessalonich.", heading: "Die Abfahrt des gefangenen Apostels nach Rom: 27,1-13", footnotes: ["19,29; 20,4"] },
      { number: 3, text: "Am anderen Tag liefen wir in Sidon ein und Julius, der Paulus wohlwollend behandelte, erlaubte ihm, zu seinen Freunden zu gehen und sich versorgen zu lassen.", footnotes: ["24,23; 28,2.16"] },
      { number: 4, text: "Von dort fuhren wir weiter und umsegelten, weil wir Gegenwind hatten, Zypern." },
      { number: 5, text: "Wir fuhren durch das Meer von Zilizien und Pamphylien und erreichten Myra in Lyzien." },
      { number: 6, text: "Dort fand der Hauptmann ein alexandrinisches Schiff, das nach Italien fuhr, und er brachte uns an Bord." },
      { number: 7, text: "Viele Tage lang kamen wir nur langsam vorwärts und mit Mühe erreichten wir die Höhe von Knidos. Da uns der Wind nicht herankommen ließ, umsegelten wir Kreta bei Salmone," },
      { number: 8, text: "fuhren unter großer Mühe an Kreta entlang und erreichten einen Ort namens Kalói Liménes, in dessen Nähe die Stadt Lasäa liegt.", footnotes: ["Kalói Liménes, wörtlich: Guthafen. Es handelt sich dabei um eine Bucht an der felsigen Küste bei der Stadt Lasäa, die nach Osten offen war."] },
      { number: 9, text: "Da inzwischen längere Zeit vergangen und die Schifffahrt bereits unsicher geworden war - sogar das Fasten war schon vorüber -, warnte Paulus und sagte:" },
      { number: 10, text: "Männer, ich sehe, die Fahrt wird mit Gefahr und großem Schaden verbunden sein, nicht nur für die Ladung und das Schiff, sondern auch für unser Leben." },
      { number: 11, text: "Der Hauptmann aber vertraute dem Steuermann und dem Kapitän mehr als den Worten des Paulus." },
      { number: 12, text: "Da der Hafen zum Überwintern ungeeignet war, beschloss die Mehrheit weiterzufahren, um nach Möglichkeit Phönix zu erreichen, einen nach Südwesten und Nordwesten offenen Hafen von Kreta; dort wollten sie überwintern." },
      { number: 13, text: "Als leichter Südwind aufkam, meinten sie, ihr Vorhaben sei schon geglückt; sie lichteten den Anker und fuhren dicht an Kreta entlang." },
      { number: 14, text: "Der Seesturm: 27,14-2614 Doch kurz darauf brach von der Insel her ein Orkan los, Eurakylon genannt.", footnotes: ["«Eurakýlon» ist der Name eines starken Nordwinds."] },
      { number: 15, text: "Das Schiff wurde mitgerissen, und weil es nicht mehr gegen den Wind gedreht werden konnte, gaben wir auf und ließen uns treiben.", heading: "Der Seesturm: 27,14-26" },
      { number: 16, text: "Während wir unter einer kleinen Insel namens Kauda hinfuhren, konnten wir das Beiboot nur mit Mühe in die Gewalt bekommen." },
      { number: 17, text: "Die Matrosen hoben es hoch, dann sicherten sie das Schiff, indem sie Taue darum herumspannten. Weil sie fürchteten, in die Syrte zu geraten, ließen sie den Treibanker hinab und trieben dahin." },
      { number: 18, text: "Da wir vom Sturm hart bedrängt wurden, erleichterten sie am nächsten Tag das Schiff," },
      { number: 19, text: "und am dritten Tag warfen sie eigenhändig die Schiffsausrüstung über Bord." },
      { number: 20, text: "Mehrere Tage hindurch zeigten sich weder Sonne noch Sterne und der heftige Sturm hielt an. Schließlich schwand uns alle Hoffnung auf Rettung." },
      { number: 21, text: "Niemand wollte mehr essen; da trat Paulus in ihre Mitte und sagte: Männer, man hätte auf mich hören und von Kreta nicht abfahren sollen, dann wären uns dieses Unglück und der Schaden erspart geblieben." },
      { number: 22, text: "Doch jetzt ermahne ich euch: Verliert nicht den Mut! Niemand von euch wird sein Leben verlieren, nur das Schiff wird untergehen." },
      { number: 23, text: "Denn in dieser Nacht ist ein Engel des Gottes, dem ich gehöre und dem ich diene, zu mir gekommen" },
      { number: 24, text: "und hat gesagt: Fürchte dich nicht, Paulus! Du musst vor den Kaiser treten. Und Gott hat dir alle geschenkt, die mit dir fahren.", footnotes: ["23,11"] },
      { number: 25, text: "Habt also Mut, Männer! Denn ich vertraue auf Gott, dass es so kommen wird, wie mir gesagt worden ist." },
      { number: 26, text: "Wir müssen allerdings an einer Insel stranden.", footnotes: ["28,1"] },
      { number: 27, text: "Der Schiffbruch vor Malta: 27,27-4427 Als wir schon die vierzehnte Nacht auf der Adria trieben, merkten die Matrosen um Mitternacht, dass sich ihnen Land näherte." },
      { number: 28, text: "Sie warfen das Lot hinab und maßen zwanzig Faden; kurz danach loteten sie nochmals und maßen fünfzehn Faden.", heading: "Der Schiffbruch vor Malta: 27,27-44", footnotes: ["1 Faden = 1 Klafter = 1,85 Meter."] },
      { number: 29, text: "Aus Furcht, wir könnten auf Klippen laufen, warfen sie vom Heck aus vier Anker und wünschten den Tag herbei." },
      { number: 30, text: "Als aber die Matrosen unter dem Vorwand, sie wollten vom Bug aus Anker auswerfen, vom Schiff zu fliehen versuchten und das Beiboot ins Meer hinunterließen," },
      { number: 31, text: "sagte Paulus zum Hauptmann und zu den Soldaten: Wenn sie nicht auf dem Schiff bleiben, könnt ihr nicht gerettet werden." },
      { number: 32, text: "Da kappten die Soldaten die Taue des Beibootes und ließen es forttreiben." },
      { number: 33, text: "Bis in die Morgendämmerung hinein ermunterte Paulus alle, etwas zu essen, und sagte: Heute ist schon der vierzehnte Tag, dass ihr ausharrt, ohne auch nur die geringste Nahrung zu euch zu nehmen." },
      { number: 34, text: "Deshalb rate ich euch: Esst etwas; das ist gut für eure Rettung. Denn keinem von euch wird auch nur ein Haar von seinem Kopf verloren gehen.", footnotes: ["Lk 12,7"] },
      { number: 35, text: "Nach diesen Worten nahm er Brot, dankte Gott vor den Augen aller, brach es und begann zu essen.", footnotes: ["Joh 6,11; Lk 22,19; 1 Tim 4,4"] },
      { number: 36, text: "Da fassten alle Mut und aßen ebenfalls." },
      { number: 37, text: "Wir waren im ganzen zweihundertsechsundsiebzig Menschen an Bord." },
      { number: 38, text: "Nachdem sie sich satt gegessen hatten, warfen sie das Getreide ins Meer, um das Schiff zu erleichtern." },
      { number: 39, text: "Als es nun Tag wurde, entdeckten die Matrosen eine Bucht mit flachem Strand; auf ihn wollten sie, wenn möglich, das Schiff auflaufen lassen; das Land selbst war ihnen unbekannt." },
      { number: 40, text: "Sie machten die Anker los und ließen sie im Meer zurück. Zugleich lösten sie die Haltetaue der Steuerruder, hissten das Vorsegel und hielten mit dem Wind auf den Strand zu." },
      { number: 41, text: "Als sie aber auf eine Sandbank gerieten, strandeten sie mit dem Schiff; der Bug bohrte sich ein und saß unbeweglich fest; das Heck aber begann in der Brandung zu zerbrechen.", footnotes: ["2 Kor 11,25"] },
      { number: 42, text: "Da beschlossen die Soldaten, die Gefangenen zu töten, damit keiner schwimmend entkommen könne." },
      { number: 43, text: "Der Hauptmann aber wollte Paulus retten und hinderte sie an ihrem Vorhaben. Er befahl, dass zuerst alle, die schwimmen konnten, über Bord springen und an Land gehen sollten," },
      { number: 44, text: "dann die Übrigen, teils auf Planken, teils auf anderen Schiffstrümmern. So kam es, dass alle ans Land gerettet wurden." },
    ] },
    { number: 28, verses: [
      { number: 1, text: "Die Überwinterung auf Malta: 28,1-101 Als wir gerettet waren, erfuhren wir, dass die Insel Malta heißt." },
      { number: 2, text: "Die Einheimischen waren uns gegenüber ungewöhnlich freundlich; sie zündeten ein Feuer an und holten uns alle zu sich, weil es zu regnen begann und kalt war.", heading: "Die Überwinterung auf Malta: 28,1-10", footnotes: ["2.4: Die Einheimischen, wörtlich: Die Barbaren. Bei den Griechen Bezeichnung für Menschen, die nicht Griechisch sprachen."] },
      { number: 3, text: "Als Paulus einen Haufen Reisig zusammenraffte und auf das Feuer legte, fuhr infolge der Hitze eine Viper heraus und biss sich an seiner Hand fest." },
      { number: 4, text: "Als die Einheimischen das Tier an seiner Hand hängen sahen, sagten sie zueinander: Dieser Mensch ist gewiss ein Mörder; die Rachegöttin lässt ihn nicht leben, obwohl er dem Meer entkommen ist." },
      { number: 5, text: "Er aber schleuderte das Tier ins Feuer und erlitt keinen Schaden.", footnotes: ["Mk 16,18; Lk 10,19"] },
      { number: 6, text: "Da erwarteten sie, er werde anschwellen oder plötzlich tot umfallen. Als sie aber eine Zeit lang gewartet hatten und sahen, dass ihm nichts Schlimmes geschah, änderten sie ihre Meinung und sagten, er sei ein Gott.", footnotes: ["14,11"] },
      { number: 7, text: "In jener Gegend lagen Landgüter, die dem Publius, dem Ersten der Insel, gehörten; er nahm uns auf und bewirtete uns drei Tage lang freundlich als seine Gäste.", footnotes: ["«Der Erste der Insel» war ein offizieller Titel, der durch Inschriften bestätigt ist."] },
      { number: 8, text: "Der Vater des Publius lag gerade mit Fieber und Ruhr im Bett. Paulus ging zu ihm hinein und betete; dann legte er ihm die Hände auf und heilte ihn." },
      { number: 9, text: "Daraufhin kamen auch die anderen Kranken der Insel herbei und wurden geheilt." },
      { number: 10, text: "Sie erwiesen uns viele Ehren und bei der Abfahrt gaben sie uns alles mit, was wir brauchten." },
      { number: 11, text: "Von Malta nach Rom: 28,11-1511 Drei Monate später fuhren wir mit einem alexandrinischen Schiff ab, das auf der Insel überwintert hatte und die Dioskuren als Schiffszeichen trug." },
      { number: 12, text: "Wir liefen in Syrakus ein und blieben drei Tage;", heading: "Von Malta nach Rom: 28,11-15" },
      { number: 13, text: "von dort fuhren wir die Küste entlang weiter und erreichten Rhegion. Nach einem Tag setzte Südwind ein und so kamen wir in zwei Tagen nach Puteoli." },
      { number: 14, text: "Hier trafen wir Brüder; sie baten uns, sieben Tage bei ihnen zu bleiben. Und so kamen wir nach Rom." },
      { number: 15, text: "Von dort waren uns die Brüder, die von uns gehört hatten, bis Forum Appii und Tres Tabernae entgegengereist. Als Paulus sie sah, dankte er Gott und fasste Mut.", footnotes: ["«Brüder» sind Christen, nicht Verwandte oder Volksgenossen des Paulus. - Forum Appii ist 64,5 Kilometer, Tres Tabernae 49,5 Kilometer von Rom entfernt."] },
      { number: 16, text: "Ankunft und Wirken des Völkerapostels in Rom: 28,16-3116 Nach unserer Ankunft in Rom erhielt Paulus die Erlaubnis, für sich allein zu wohnen, zusammen mit dem Soldaten, der ihn bewachte.", footnotes: ["27,3"] },
      { number: 17, text: "Drei Tage später rief er die führenden Männer der Juden zusammen. Als sie versammelt waren, sagte er zu ihnen: Brüder, obwohl ich mich nicht gegen das Volk oder die Sitten der Väter vergangen habe, bin ich von Jerusalem aus als Gefangener den Römern ausgeliefert worden.", heading: "Ankunft und Wirken des Völkerapostels in Rom: 28,16-31" },
      { number: 18, text: "Diese haben mich verhört und wollten mich freilassen, da nichts gegen mich vorlag, worauf der Tod steht." },
      { number: 19, text: "Weil aber die Juden Einspruch erhoben, war ich gezwungen, Berufung beim Kaiser einzulegen, jedoch nicht, um mein Volk anzuklagen." },
      { number: 20, text: "Aus diesem Grund habe ich darum gebeten, euch sehen und sprechen zu dürfen. Denn um der Hoffnung Israels willen trage ich diese Fesseln.", footnotes: ["26,6f", "Hoffnung Israels: Gemeint ist der Messias."] },
      { number: 21, text: "Sie antworteten ihm: Wir haben über dich weder Briefe aus Judäa erhalten, noch ist einer von den Brüdern gekommen, der uns etwas Belastendes über dich berichtet oder erzählt hätte." },
      { number: 22, text: "Wir wünschen aber von dir zu hören, was du denkst; denn von dieser Sekte ist uns bekannt, dass sie überall auf Widerspruch stößt." },
      { number: 23, text: "Sie vereinbarten mit ihm einen bestimmten Tag, an dem sie in noch größerer Zahl zu ihm in die Wohnung kamen. Vom Morgen bis in den Abend hinein erklärte und bezeugte er ihnen das Reich Gottes und versuchte, sie vom Gesetz des Mose und von den Propheten aus für Jesus zu gewinnen.", footnotes: ["13,16-25; 17,2f.11f; Lk 24,44; Joh 1,45; 5,45f; 1 Petr 1,10-12"] },
      { number: 24, text: "Die einen ließen sich durch seine Worte überzeugen, die andern blieben ungläubig." },
      { number: 25, text: "Ohne sich einig geworden zu sein, brachen sie auf, nachdem Paulus noch das eine Wort gesagt hatte: Treffend hat der Heilige Geist durch den Propheten Jesaja zu euren Vätern gesagt:" },
      { number: 26, text: "Geh zu diesem Volk und sag: / Hören sollt ihr, hören, aber nicht verstehen; / sehen sollt ihr, sehen, aber nicht erkennen.", footnotes: ["Jes 6,9f G; Mt 13,14; Mk 4,12; Lk 8,10; Joh 12,40"] },
      { number: 27, text: "Denn das Herz dieses Volkes ist hart geworden / und mit ihren Ohren hören sie nur schwer / und ihre Augen halten sie geschlossen, / damit sie mit ihren Augen nicht sehen / und mit ihren Ohren nicht hören, / damit sie mit ihrem Herzen nicht zur Einsicht kommen, / damit sie sich nicht bekehren und ich sie nicht heile." },
      { number: 28, text: "Darum sollt ihr nun wissen: Den Heiden ist dieses Heil Gottes gesandt worden. Und sie werden hören!", footnotes: ["Ps 67,3; 98,3; Apg 18,6; Lk 3,6"] },
      { number: 29, text: "[]", footnotes: ["Einzelne Textzeugen fügen hinzu: Als er das gesagt hatte, gingen die Juden weg und stritten noch lange miteinander."] },
      { number: 30, text: "Er blieb zwei volle Jahre in seiner Mietwohnung und empfing alle, die zu ihm kamen." },
      { number: 31, text: "Er verkündete das Reich Gottes und trug ungehindert und mit allem Freimut die Lehre über Jesus Christus, den Herrn, vor.", footnotes: ["1,3; 28,23; Röm 1,15"] },
    ] },
  ],
};
