import { Book } from "@/lib/types";

export const mark: Book = {
  id: "mark", name: "Markus", shortName: "Mk", testament: "new",
  introduction: `Das älteste, griechisch geschriebene Evangelium wird nach altkirchlicher Überlieferung Markus zugeschrieben. Damit ist gemeint Johannes Markus aus Jerusalem, Sohn einer Maria, in deren Haus sich die Urgemeinde in Jerusalem versammelte; er war ein Vetter des Barnabas, Mitarbeiter des Paulus und später auch des Petrus (Phlm 24; Kol 4,10; Apg 12,12; 13,5; 15,36-39; 1 Petr 5,13). Ebenfalls nach altkirchlicher Überlieferung schrieb er sein Evangelium in Rom. Es steht in zeitlichem Zusammenhang mit der Zerstörung Jerusalems (Mk 13) und ist deshalb um 70 n. Chr. verfasst, und zwar für Heiden und Heidenchristen (vgl. 7,3f). Markus sammelte Überlieferungen über Jesus, vor allem Wundererzählungen, Gleichnisse, Geschichten, die in einem Jesuswort gipfeln, Einzelworte und Zeugnisse über die Passion. Er ordnete diese Stoffe zeitlich und sachlich und verarbeitete sie so zu einem Evangelium. Der geographisch bestimmte Aufriss ist folgender: Wirken Jesu in Galiläa (1,14 - 5,43); Wanderung durch jüdisch-heidnisches Gebiet mit Belehrung der Jünger (6,1 - 9,50); Zug nach Jerusalem, Ringen mit den jüdischen Führern und Tod am Kreuz (10,1 - 15,47). Sein Evangelium beginnt mit dem Auftreten des Täufers (1,1-13) und endet mit dem Auffinden des leeren Grabes durch Frauen am Ostermorgen (16,1-8). Der Anhang (16,9-20) wurde erst im 2. Jahrhundert von unbekannter Hand angefügt, vermutlich weil der jähe Schluss 16,8 nicht befriedigte. Die Annahme, dass ein ursprünglicher Schluss verloren ging, ist wenig wahrscheinlich. Indem Markus vom Wirken und vom Schicksal Jesu berichtet, verkündet er, dass Gott durch diesen Jesus seinen Willen offenbart hat, die Menschen zu retten: Jesus ist der verheißene Messias, der Sohn Gottes; durch sein Wirken ist die Heilszeit angebrochen (1,14). Er überwindet das Unheil, die Herrschaft des Satan und seiner Helfer (Dämonenaustreibungen und Krankenheilungen), er vergibt Sünden (2,1-12), verkündet den unverfälschten Willen Gottes (2,27f; 7,1-23) und richtet das Reich Gottes auf (Kap. 4). Weil in seiner Person Gottes Vollmacht in menschlicher Niedrigkeit auftritt, wird sein Geheimnis nicht begriffen, nicht von den Juden, aber auch nicht von den Jüngern vor der Auferstehung Jesu (6,4-6; 8,14-21; 14,26-42; vgl. auch die Schweigegebote Jesu 5,43; 8,30; 9,9). Die einzig angemessene Haltung der Botschaft Jesu gegenüber ist unbedingter Glaube (5,36; 9,19-24; 11,22-25). Christsein heißt, Jesus in Glaube, Selbstlosigkeit, Bereitschaft zum Dienen und Mut zum Leiden nachzufolgen (8,31-33; 9,42-48; 10,17-27.41-45) und so die Kraft des Reiches Gottes zu erfahren. Der erste Heide, der das Geheimnis Jesu erkannte, ist der Hauptmann unter dem Kreuz: »Wahrhaftig, dieser Mensch war Gottes Sohn« (15,39). In ihm sieht Markus die Heidenkirche beim Kreuz Christi vertreten. Die bedrängten Heidenchristen will Markus durch sein Evangelium in ihrem Glauben stärken und ermutigen, Christus auch in Verfolgungen treu zu bleiben.`,
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Anfang des Evangeliums von Jesus Christus, dem Sohn Gottes:", footnotes: ["15,39; (1-8) Joh 1,19-28; Mt 3,1-6.11; Lk 3,3-6.15f", "1-8: Die ältesten Christengemeinden waren vor allem am öffentlichen Wirken Jesu interessiert (vgl. Apg 10,37-41). Darum bringt Markus, anders als Matthäus und Lukas, keine Vorgeschichte, sondern beginnt sein Evangelium sofort mit dem Auftreten des Täufers und der Taufe Jesu. «dem Sohn Gottes» fehlt bei einigen alten Textzeugen.", ""] },
      { number: 2, heading: "Die Vorbereitung des Wirkens Jesu: 1,1-13 | Johannes der Täufer: 1,1-8", text: "Es begann, wie es bei dem Propheten Jesaja steht: Ich sende meinen Boten vor dir her; / er soll den Weg für dich bahnen.", footnotes: ["Mal 3,1; Ex 23,20; Mt 11,10; Lk 1,76; 7,27", ""] },
      { number: 3, text: "Eine Stimme ruft in der Wüste: / Bereitet dem Herrn den Weg! / Ebnet ihm die Straßen!", footnotes: ["Jes 40,3 G"] },
      { number: 4, text: "So trat Johannes der Täufer in der Wüste auf und verkündigte Umkehr und Taufe zur Vergebung der Sünden.", footnotes: ["Apg 13,24; 19,4", "Umkehr und Taufe, wörtlich: Taufe der Umkehr.", ""] },
      { number: 5, text: "Ganz Judäa und alle Einwohner Jerusalems zogen zu ihm hinaus; sie bekannten ihre Sünden und ließen sich im Jordan von ihm taufen." },
      { number: 6, text: "Johannes trug ein Gewand aus Kamelhaaren und einen ledernen Gürtel um seine Hüften und er lebte von Heuschrecken und wildem Honig." },
      { number: 7, text: "Er verkündete: Nach mir kommt einer, der ist stärker als ich; ich bin es nicht wert, mich zu bücken, um ihm die Schuhe aufzuschnüren.", footnotes: [""] },
      { number: 8, text: "Ich habe euch nur mit Wasser getauft, er aber wird euch mit dem Heiligen Geist taufen." },
      { number: 9, text: "In jenen Tagen kam Jesus aus Nazaret in Galiläa und ließ sich von Johannes im Jordan taufen.", footnotes: ["(9-11) Joh 1,29-34; Mt 3,13-17; Lk 3,21f"] },
      { number: 10, heading: "Die Taufe Jesu: 1,9-11", text: "Und als er aus dem Wasser stieg, sah er, dass der Himmel sich öffnete und der Geist wie eine Taube auf ihn herabkam." },
      { number: 11, text: "Und eine Stimme aus dem Himmel sprach: Du bist mein geliebter Sohn, an dir habe ich Gefallen gefunden.", footnotes: ["Gen 22,2; Ps 2,7; Jes 42,1; Mt 3,17", ""] },
      { number: 12, text: "Danach trieb der Geist Jesus in die Wüste.", footnotes: ["(12-13) Mt 4,1f.11; Lk 4,1f"] },
      { number: 13, heading: "Die Versuchung Jesu: 1,12-13", text: "Dort blieb Jesus vierzig Tage lang und wurde vom Satan in Versuchung geführt. Er lebte bei den wilden Tieren und die Engel dienten ihm." },
      { number: 14, text: "Nachdem man Johannes ins Gefängnis geworfen hatte, ging Jesus wieder nach Galiläa; er verkündete das Evangelium Gottes", footnotes: ["(14-15) Mt 4,12.17; Lk 4,14f"] },
      { number: 15, heading: "Das Wirken Jesu in Galiläa: 1,14 - 8,26 | Erstes Auftreten in Galiläa: 1,14-15", text: "und sprach: Die Zeit ist erfüllt, das Reich Gottes ist nahe. Kehrt um, und glaubt an das Evangelium!", footnotes: ["In diesem programmatischen Satz fasst Markus den Inhalt der Botschaft Jesu zusammen."] },
      { number: 16, text: "Als Jesus am See von Galiläa entlangging, sah er Simon und Andreas, den Bruder des Simon, die auf dem See ihr Netz auswarfen; sie waren nämlich Fischer.", footnotes: ["(16-20) Lk 5,1-11; Joh 1,35-51; Mt 4,18-22"] },
      { number: 17, heading: "Die Berufung der ersten Jünger: 1,16-20", text: "Da sagte er zu ihnen: Kommt her, folgt mir nach! Ich werde euch zu Menschenfischern machen." },
      { number: 18, text: "Sogleich ließen sie ihre Netze liegen und folgten ihm." },
      { number: 19, text: "Als er ein Stück weiterging, sah er Jakobus, den Sohn des Zebedäus, und seinen Bruder Johannes; sie waren im Boot und richteten ihre Netze her." },
      { number: 20, text: "Sofort rief er sie und sie ließen ihren Vater Zebedäus mit seinen Tagelöhnern im Boot zurück und folgten Jesus nach." },
      { number: 21, text: "Sie kamen nach Kafarnaum. Am folgenden Sabbat ging er in die Synagoge und lehrte.", footnotes: ["(21f) Mt 7,28f; (21-28) Lk 4,31-37", "21-34: Mit der Beschreibung des ersten Tages des Wirkens Jesu in Kafarnaum zeigt der Evangelist beispielhaft Jesu Tätigkeit: Jesus beweist durch Wort und Tat seine Vollmacht.", ""] },
      { number: 22, heading: "Jesus in der Synagoge von Kafarnaum: 1,21-28", text: "Und die Menschen waren sehr betroffen von seiner Lehre; denn er lehrte sie wie einer, der (göttliche) Vollmacht hat, nicht wie die Schriftgelehrten." },
      { number: 23, text: "In ihrer Synagoge saß ein Mann, der von einem unreinen Geist besessen war. Der begann zu schreien:", footnotes: [""] },
      { number: 24, text: "Was haben wir mit dir zu tun, Jesus von Nazaret? Bist du gekommen, um uns ins Verderben zu stürzen? Ich weiß, wer du bist: der Heilige Gottes.", footnotes: ["5,7; Mt 8,29; Lk 8,28; Joh 2,4", "Andere Übersetzungsmöglichkeit: Du bist gekommen, um uns ins Verderben zu stürzen."] },
      { number: 25, text: "Da befahl ihm Jesus: Schweig und verlass ihn!" },
      { number: 26, text: "Der unreine Geist zerrte den Mann hin und her und verließ ihn mit lautem Geschrei." },
      { number: 27, text: "Da erschraken alle und einer fragte den andern: Was hat das zu bedeuten? Hier wird mit Vollmacht eine ganz neue Lehre verkündet. Sogar die unreinen Geister gehorchen seinem Befehl.", footnotes: ["Mt 7,29"] },
      { number: 28, text: "Und sein Ruf verbreitete sich rasch im ganzen Gebiet von Galiläa." },
      { number: 29, text: "Sie verließen die Synagoge und gingen zusammen mit Jakobus und Johannes gleich in das Haus des Simon und Andreas.", footnotes: ["(29-31) Mt 8,14f; Lk 4,38f"] },
      { number: 30, heading: "Die Heilung der Schwiegermutter des Petrus: 1,29-31", text: "Die Schwiegermutter des Simon lag mit Fieber im Bett. Sie sprachen mit Jesus über sie," },
      { number: 31, text: "und er ging zu ihr, fasste sie an der Hand und richtete sie auf. Da wich das Fieber von ihr und sie sorgte für sie." },
      { number: 32, text: "Am Abend, als die Sonne untergegangen war, brachte man alle Kranken und Besessenen zu Jesus.", footnotes: ["(32-34) Mt 8,16; Lk 4,40f", "Die Sabbatruhe dauerte vom Sonnenuntergang des Vortags bis zum Sonnenuntergang am Sabbat.", ""] },
      { number: 33, heading: "Die Heilung von Besessenen und Kranken: 1,32-34", text: "Die ganze Stadt war vor der Haustür versammelt," },
      { number: 34, text: "und er heilte viele, die an allen möglichen Krankheiten litten, und trieb viele Dämonen aus. Und er verbot den Dämonen zu reden; denn sie wussten, wer er war.", footnotes: [""] },
      { number: 35, text: "In aller Frühe, als es noch dunkel war, stand er auf und ging an einen einsamen Ort, um zu beten.", footnotes: ["Lk 5,16; 9,18; (35-39) Lk 4,42-44"] },
      { number: 36, heading: "Aufbruch aus Kafarnaum: 1,35-39", text: "Simon und seine Begleiter eilten ihm nach," },
      { number: 37, text: "und als sie ihn fanden, sagten sie zu ihm: Alle suchen dich." },
      { number: 38, text: "Er antwortete: Lasst uns anderswohin gehen, in die benachbarten Dörfer, damit ich auch dort predige; denn dazu bin ich gekommen." },
      { number: 39, text: "Und er zog durch ganz Galiläa, predigte in den Synagogen und trieb die Dämonen aus.", footnotes: ["Mt 4,23", ""] },
      { number: 40, text: "Ein Aussätziger kam zu Jesus und bat ihn um Hilfe; er fiel vor ihm auf die Knie und sagte: Wenn du willst, kannst du machen, dass ich rein werde.", footnotes: ["(40-45) Mt 8,2-4; Lk 5,12-16", "40-44: Aussatz und verschiedene Hautkrankheiten schlossen vom Zusammenleben mit anderen Menschen aus. Erst wenn die dafür zuständigen Priester die Heilung festgestellt hatten, war die Rückkehr in die alte Lebensgemeinschaft wieder erlaubt (vgl. Lev 13 14).", ""] },
      { number: 41, heading: "Die Heilung eines Aussätzigen: 1,40-45", text: "Jesus hatte Mitleid mit ihm; er streckte die Hand aus, berührte ihn und sagte: Ich will es - werde rein!" },
      { number: 42, text: "Im gleichen Augenblick verschwand der Aussatz und der Mann war rein." },
      { number: 43, text: "Jesus schickte ihn weg und schärfte ihm ein:", footnotes: ["(43f) 5,43"] },
      { number: 44, text: "Nimm dich in Acht! Erzähl niemand etwas davon, sondern geh, zeig dich dem Priesterund bring das Reinigungsopfer dar, das Mose angeordnet hat. Das soll für sie ein Beweis (meiner Gesetzestreue) sein.", footnotes: ["Lev 13,49; 14,2-32; Lk 17,14", "ein Beweis (meiner Gesetzestreue): andere Übersetzungsmöglichkeiten: ein Beweis (meiner Vollmacht); oder: ein Beweis (deiner Heilung)."] },
      { number: 45, text: "Der Mann aber ging weg und erzählte bei jeder Gelegenheit, was geschehen war; er verbreitete die ganze Geschichte, sodass sich Jesus in keiner Stadt mehr zeigen konnte; er hielt sich nur noch außerhalb der Städte an einsamen Orten auf. Dennoch kamen die Leute von überallher zu ihm." },
    ] },
    { number: 2, verses: [
      { number: 1, text: "Als er einige Tage später nach Kafarnaum zurückkam, wurde bekannt, dass er (wieder) zu Hause war.", footnotes: ["(1-12) Mt 9,1-8; Lk 5,17-26", "Jesus wohnte vermutlich im Haus des Simon (vgl. 1,29-31).", ""] },
      { number: 2, heading: "Die Heilung eines Gelähmten: 2,1-12", text: "Und es versammelten sich so viele Menschen, dass nicht einmal mehr vor der Tür Platz war; und er verkündete ihnen das Wort." },
      { number: 3, text: "Da brachte man einen Gelähmten zu ihm; er wurde von vier Männern getragen." },
      { number: 4, text: "Weil sie ihn aber wegen der vielen Leute nicht bis zu Jesus bringen konnten, deckten sie dort, wo Jesus war, das Dach ab, schlugen (die Decke) durch und ließen den Gelähmten auf seiner Tragbahre durch die Öffnung hinab.", footnotes: ["Das Dach einer Wohnhütte bestand aus Holzbalken oder Knüppeln, über die Schilf oder Äste gelegt waren. Das Ganze war mit festgetretenem Lehm zugedeckt."] },
      { number: 5, text: "Als Jesus ihren Glauben sah, sagte er zu dem Gelähmten: Mein Sohn, deine Sünden sind dir vergeben!" },
      { number: 6, text: "Einige Schriftgelehrte aber, die dort saßen, dachten im Stillen:" },
      { number: 7, text: "Wie kann dieser Mensch so reden? Er lästert Gott. Wer kann Sünden vergeben außer dem einen Gott?", footnotes: ["außer dem einen Gott, andere Übersetzungsmöglichkeit: außer Gott allein. - Obige Übersetzung ist durch den Anklang an Dtn 6,4 nahegelegt. - Vgl. Mk 10,18; Lk 18,19."] },
      { number: 8, text: "Jesus erkannte sofort, was sie dachten, und sagte zu ihnen: Was für Gedanken habt ihr im Herzen?", footnotes: ["Wörtlich: Jesus merkte in seinem Geist sofort, dass sie so dachten."] },
      { number: 9, text: "Ist es leichter, zu dem Gelähmten zu sagen: Deine Sünden sind dir vergeben!, oder zu sagen: Steh auf, nimm deine Tragbahre und geh umher?" },
      { number: 10, text: "Ihr sollt aber erkennen, dass der Menschensohn die Vollmacht hat, hier auf der Erde Sünden zu vergeben. Und er sagte zu dem Gelähmten:" },
      { number: 11, text: "Ich sage dir: Steh auf, nimm deine Tragbahre, und geh nach Hause!" },
      { number: 12, text: "Der Mann stand sofort auf, nahm seine Tragbahre und ging vor aller Augen weg. Da gerieten alle außer sich; sie priesen Gott und sagten: So etwas haben wir noch nie gesehen." },
      { number: 13, text: "Jesus ging wieder hinaus an den See. Da kamen Scharen von Menschen zu ihm und er lehrte sie.", footnotes: ["(13-17) Mt 9,9-13; Lk 5,27-32"] },
      { number: 14, heading: "Die Berufung des Levi und das Mahl mit den Zöllnern: 2,13-17", text: "Als er weiterging, sah er Levi, den Sohn des Alphäus, am Zoll sitzen und sagte zu ihm: Folge mir nach! Da stand Levi auf und folgte ihm.", footnotes: ["Mt 8,22"] },
      { number: 15, text: "Und als Jesus in seinem Haus beim Essen war, aßen viele Zöllner und Sünder zusammen mit ihm und seinen Jüngern; denn es folgten ihm schon viele.", footnotes: ["Lk 7,34; 19,7", "in seinem Haus: entweder im Haus des Levi (vgl. Lk 5,29) oder im Haus Jesu (vgl. Mk 2,1)."] },
      { number: 16, text: "Als die Schriftgelehrten, die zur Partei der Pharisäer gehörten, sahen, dass er mit Zöllnern und Sündern aß, sagten sie zu seinen Jüngern: Wie kann er zusammen mit Zöllnern und Sündern essen?", footnotes: ["Mt 11,19; Lk 15,1f", "Die Pharisäer bildeten eine religiöse Gemeinschaft, die es sich zur Aufgabe gemacht hatte, das jüdische religiöse Gesetz zu studieren und genau zu beachten. Zu ihnen gehörten Priester und Laien, vor allem viele Schriftgelehrte. - Die Schriftgelehrten bildeten einen eigenen Stand von Gesetzesgelehrten und religiösen Juristen, die das Recht besaßen, öffentlich zu lehren und Schüler um sich zu sammeln. Sie hatten neben den Priestern entscheidenden Einfluss auf das jüdische Leben. Die Zölle wurden an die Meistbietenden verpachtet. Diese wirtschafteten meist auch in die eigene Tasche. Darum galten Zöllner als Diebe und Betrüger; die Begriffe »Zöllner« und »Sünder« waren austauschbar."] },
      { number: 17, text: "Jesus hörte es und sagte zu ihnen: Nicht die Gesunden brauchen den Arzt, sondern die Kranken. Ich bin gekommen, um die Sünder zu rufen, nicht die Gerechten.", footnotes: ["Lk 19,10"] },
      { number: 18, text: "Da die Jünger des Johannes und die Pharisäer zu fasten pflegten, kamen Leute zu Jesus und sagten: Warum fasten deine Jünger nicht, während die Jünger des Johannes und die Jünger der Pharisäer fasten?", footnotes: ["(18-22) Mt 9,14-17; Lk 5,33-38", "Die Juden waren lediglich verpflichtet, am Versöhnungstag (vgl. Lev 16) zu fasten. In Notzeiten wurden eigene Fasttage ausgerufen. Insbesondere Pharisäer fasteten darüber hinaus, stellvertretend für die Sünden anderer, zweimal in der Woche, montags und donnerstags. Das Fasten bestand in völliger Enthaltung von Speise und Trank zwischen Sonnenaufgang und Sonnenuntergang. Jesus fastete mit seinen Jüngern nicht und kritisierte die jüdische Fastenpraxis (vgl. Mt 6,16-18).", ""] },
      { number: 19, heading: "Die Frage nach dem Fasten: 2,18-22", text: "Jesus antwortete ihnen: Können denn die Hochzeitsgäste fasten, solange der Bräutigam bei ihnen ist? Solange der Bräutigam bei ihnen ist, können sie nicht fasten." },
      { number: 20, text: "Es werden aber Tage kommen, da wird ihnen der Bräutigam genommen sein; an jenem Tag werden sie fasten." },
      { number: 21, text: "Niemand näht ein Stück neuen Stoff auf ein altes Kleid; denn der neue Stoff reißt doch vom alten Kleid ab und es entsteht ein noch größerer Riss.", footnotes: ["21f: Vgl. die Anmerkung zu Mt 9,16f."] },
      { number: 22, text: "Auch füllt niemand neuen Wein in alte Schläuche. Sonst zerreißt der Wein die Schläuche; der Wein ist verloren und die Schläuche sind unbrauchbar. Neuer Wein gehört in neue Schläuche." },
      { number: 23, text: "An einem Sabbat ging er durch die Kornfelder und unterwegs rissen seine Jünger Ähren ab.", footnotes: ["Dtn 23,26; (23-28) Mt 12,1-8; Lk 6,1-5"] },
      { number: 24, heading: "Das Abreißen der Ähren am Sabbat: 2,23-28", text: "Da sagten die Pharisäer zu ihm: Sieh dir an, was sie tun! Das ist doch am Sabbat verboten." },
      { number: 25, text: "Er antwortete: Habt ihr nie gelesen, was David getan hat, als er und seine Begleiter hungrig waren und nichts zu essen hatten -", footnotes: ["1 Sam 21,2-7"] },
      { number: 26, text: "wie er zur Zeit des Hohenpriesters Abjatar in das Haus Gottes ging und die heiligen Brote aß, die außer den Priestern niemand essen darf, und auch seinen Begleitern davon gab?", footnotes: ["Lev 24,5-9", "Der Hohepriester hieß nach 1 Sam 21,2 in Wirklichkeit Ahimelech; nach 1 Sam 22,20 hieß sein Sohn, nach 2 Sam 8,17 sein Vater Abjatar."] },
      { number: 27, text: "Und Jesus fügte hinzu: Der Sabbat ist für den Menschen da, nicht der Mensch für den Sabbat.", footnotes: ["27f: Jesus greift damit auf den Ursinn des Sabbats zurück: Er sollte für Menschen und Tiere eine Wohltat sein (vgl. Ex 20,9; Dtn 5, 12-14)."] },
      { number: 28, text: "Deshalb ist der Menschensohn Herr auch über den Sabbat." },
    ] },
    { number: 3, verses: [
      { number: 1, text: "Als er ein andermal in eine Synagoge ging, saß dort ein Mann, dessen Hand verdorrt war.", footnotes: ["(1-6) Lk 14,1-6; Mt 12,9-14; Lk 6,6-11", "1-6: Die ärztliche Tätigkeit galt als Arbeit und durfte deshalb nach pharisäischer Auffassung am Sabbat nur dann ausgeübt werden, wenn Lebensgefahr bestand. 1.3.5: Hand, andere Übersetzungsmöglichkeit: Arm.", ""] },
      { number: 2, heading: "Die Heilung eines Mannes am Sabbat: 3,1-6", text: "Und sie gaben Acht, ob Jesus ihn am Sabbat heilen werde; sie suchten nämlich einen Grund zur Anklage gegen ihn." },
      { number: 3, text: "Da sagte er zu dem Mann mit der verdorrten Hand: Steh auf und stell dich in die Mitte!" },
      { number: 4, text: "Und zu den anderen sagte er: Was ist am Sabbat erlaubt: Gutes zu tun oder Böses, ein Leben zu retten oder es zu vernichten? Sie aber schwiegen." },
      { number: 5, text: "Und er sah sie der Reihe nach an, voll Zorn und Trauer über ihr verstocktes Herz, und sagte zu dem Mann: Streck deine Hand aus! Er streckte sie aus und seine Hand war wieder gesund." },
      { number: 6, text: "Da gingen die Pharisäer hinaus und fassten zusammen mit den Anhängern des Herodes den Beschluss, Jesus umzubringen." },
      { number: 7, text: "Jesus zog sich mit seinen Jüngern an den See zurück. Viele Menschen aus Galiläa aber folgten ihm. Auch aus Judäa,", footnotes: ["(7-12) Mt 4,23-25; 12,15f; Lk 6,17-19"] },
      { number: 8, heading: "Der Andrang des Volkes: 3,7-12", text: "aus Jerusalem und Idumäa, aus dem Gebiet jenseits des Jordan und aus der Gegend von Tyrus und Sidon kamen Scharen von Menschen zu ihm, als sie von all dem hörten, was er tat." },
      { number: 9, text: "Da sagte er zu seinen Jüngern, sie sollten ein Boot für ihn bereithalten, damit er von der Menge nicht erdrückt werde." },
      { number: 10, text: "Denn er heilte viele, sodass alle, die ein Leiden hatten, sich an ihn herandrängten, um ihn zu berühren." },
      { number: 11, text: "Wenn die von unreinen Geistern Besessenen ihn sahen, fielen sie vor ihm nieder und schrien: Du bist der Sohn Gottes!", footnotes: ["Mt 8,29; Lk 4,41"] },
      { number: 12, text: "Er aber verbot ihnen streng, bekannt zu machen, wer er sei." },
      { number: 13, text: "Jesus stieg auf einen Berg und rief die zu sich, die er erwählt hatte, und sie kamen zu ihm.", footnotes: ["(13-19) Mt 10,1-4; Lk 6,12-16", "13-19: Vgl. die Anmerkung zu Mt 9,35 - 11,1.", ""] },
      { number: 14, heading: "Die Wahl der Zwölf: 3,13-19", text: "Und er setzte zwölf ein, die er bei sich haben und die er dann aussenden wollte, damit sie predigten" },
      { number: 15, text: "und mit seiner Vollmacht Dämonen austrieben." },
      { number: 16, text: "Die Zwölf, die er einsetzte, waren: Petrus - diesen Beinamen gab er dem Simon -,", footnotes: ["(16-19) Joh 1,40-44; Apg 1,13"] },
      { number: 17, text: "Jakobus, der Sohn des Zebedäus, und Johannes, der Bruder des Jakobus - ihnen gab er den Beinamen Boanerges, das heißt Donnersöhne -," },
      { number: 18, text: "dazu Andreas, Philippus, Bartholomäus, Matthäus, Thomas, Jakobus, der Sohn des Alphäus, Thaddäus, Simon Kananäus", footnotes: ["Zu «Kananäus» vgl. die Anmerkung zu Mt 10,4."] },
      { number: 19, text: "und Judas Iskariot, der ihn dann verraten hat." },
      { number: 20, text: "Jesus ging in ein Haus und wieder kamen so viele Menschen zusammen, dass er und die Jünger nicht einmal mehr essen konnten.", footnotes: [""] },
      { number: 21, heading: "Jesus und seine Angehörigen: 3,20-21", text: "Als seine Angehörigen davon hörten, machten sie sich auf den Weg, um ihn mit Gewalt zurückzuholen; denn sie sagten: Er ist von Sinnen.", footnotes: ["Joh 7,5"] },
      { number: 22, text: "Die Schriftgelehrten, die von Jerusalem herabgekommen waren, sagten: Er ist von Beelzebul besessen; mit Hilfe des Anführers der Dämonen treibt er die Dämonen aus.", footnotes: ["Mt 9,34; 10,25; (22-30) Mt 12,24-29.31f; Lk 11,15-22", "22-30: Vgl. die Anmerkung zu Mt 12,24.", ""] },
      { number: 23, heading: "Verteidigungsrede Jesu: 3,22-30", text: "Da rief er sie zu sich und belehrte sie in Form von Gleichnissen: Wie kann der Satan den Satan austreiben?" },
      { number: 24, text: "Wenn ein Reich in sich gespalten ist, kann es keinen Bestand haben." },
      { number: 25, text: "Wenn eine Familie in sich gespalten ist, kann sie keinen Bestand haben." },
      { number: 26, text: "Und wenn sich der Satan gegen sich selbst erhebt und mit sich selbst im Streit liegt, kann er keinen Bestand haben, sondern es ist um ihn geschehen." },
      { number: 27, text: "Es kann aber auch keiner in das Haus eines starken Mannes einbrechen und ihm den Hausrat rauben, wenn er den Mann nicht vorher fesselt; erst dann kann er sein Haus plündern.", footnotes: ["Jes 49,24"] },
      { number: 28, text: "Amen, das sage ich euch: Alle Vergehen und Lästerungen werden den Menschen vergeben werden, so viel sie auch lästern mögen;", footnotes: ["(28-29) Lk 12,10"] },
      { number: 29, text: "wer aber den Heiligen Geist lästert, der findet in Ewigkeit keine Vergebung, sondern seine Sünde wird ewig an ihm haften." },
      { number: 30, text: "Sie hatten nämlich gesagt: Er ist von einem unreinen Geist besessen.", footnotes: ["Joh 10,20"] },
      { number: 31, text: "Da kamen seine Mutter und seine Brüder; sie blieben vor dem Haus stehen und ließen ihn herausrufen.", footnotes: ["(31-35) Mt 12,46-50; Lk 8,19-21", "31-35: Vgl. die Anmerkung zu Mt 12,46f.", ""] },
      { number: 32, heading: "Von den wahren Verwandten Jesu: 3,31-35", text: "Es saßen viele Leute um ihn herum und man sagte zu ihm: Deine Mutter und deine Brüder stehen draußen und fragen nach dir.", footnotes: ["Einige Textzeugen fügen nach «Brüder» hinzu: und deine Schwestern."] },
      { number: 33, text: "Er erwiderte: Wer ist meine Mutter und wer sind meine Brüder?" },
      { number: 34, text: "Und er blickte auf die Menschen, die im Kreis um ihn herumsaßen, und sagte: Das hier sind meine Mutter und meine Brüder." },
      { number: 35, text: "Wer den Willen Gottes erfüllt, der ist für mich Bruder und Schwester und Mutter.", footnotes: [""] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Die Gleichnisse vom Reich Gottes: 4,1-34 Ein andermal lehrte er wieder am Ufer des Sees und sehr viele Menschen versammelten sich um ihn. Er stieg deshalb in ein Boot auf dem See und setzte sich; die Leute aber standen am Ufer.", footnotes: ["(1-9) Mt 13,1-9; Lk 8,4-8", "1-34: In diesem Kapitel sind drei Saatgleichnisse (s. VV. 3.26.31) gesammelt; dazu kommen eine nachträgliche allegorische Deutung des ersten Gleichnisses (VV. 13-20) und mehrere Sprüche (VV. 21-24). Ursprünglich sind die Gleichnisse Jesu nicht als Allegorien gemeint, die Zug um Zug auf die Wirklichkeit übertragen werden können, sondern sie stellen jeweils als ein Ganzes einen Grundgedanken bildhaft dar.", ""] },
      { number: 2, heading: "Das Gleichnis vom Sämann: 4,1-9", text: "Und er sprach lange zu ihnen und lehrte sie in Form von Gleichnissen. Bei dieser Belehrung sagte er zu ihnen:" },
      { number: 3, text: "Hört! Ein Sämann ging aufs Feld, um zu säen." },
      { number: 4, text: "Als er säte, fiel ein Teil der Körner auf den Weg und die Vögel kamen und fraßen sie." },
      { number: 5, text: "Ein anderer Teil fiel auf felsigen Boden, wo es nur wenig Erde gab, und ging sofort auf, weil das Erdreich nicht tief war;" },
      { number: 6, text: "als aber die Sonne hochstieg, wurde die Saat versengt und verdorrte, weil sie keine Wurzeln hatte." },
      { number: 7, text: "Wieder ein anderer Teil fiel in die Dornen und die Dornen wuchsen und erstickten die Saat und sie brachte keine Frucht." },
      { number: 8, text: "Ein anderer Teil schließlich fiel auf guten Boden und brachte Frucht; die Saat ging auf und wuchs empor und trug dreißigfach, ja sechzigfach und hundertfach." },
      { number: 9, text: "Und Jesus sprach: Wer Ohren hat zum Hören, der höre!", footnotes: ["Mt 11,15"] },
      { number: 10, text: "Als er mit seinen Begleitern und den Zwölf allein war, fragten sie ihn nach dem Sinn seiner Gleichnisse.", footnotes: ["(10-12) Mt 13,10-17; Lk 8,9f"] },
      { number: 11, heading: "Sinn und Zweck der Gleichnisse: 4,10-12", text: "Da sagte er zu ihnen: Euch ist das Geheimnis des Reiches Gottes anvertraut; denen aber, die draußen sind, wird alles in Gleichnissen gesagt;" },
      { number: 12, text: "denn sehen sollen sie, sehen, aber nicht erkennen; / hören sollen sie, hören, aber nicht verstehen, / damit sie sich nicht bekehren / und ihnen nicht vergeben wird.", footnotes: ["Jes 6,9f; Joh 12,40; Apg 28,26f", ""] },
      { number: 13, text: "Und er sagte zu ihnen: Wenn ihr schon dieses Gleichnis nicht versteht, wie wollt ihr dann all die anderen Gleichnisse verstehen?", footnotes: ["(13-20) Mt 13,18-23; Lk 8,11-15"] },
      { number: 14, heading: "Die Deutung des Gleichnisses vom Sämann: 4,13-20", text: "Der Sämann sät das Wort." },
      { number: 15, text: "Auf den Weg fällt das Wort bei denen, die es zwar hören, aber sofort kommt der Satan und nimmt das Wort weg, das in sie gesät wurde." },
      { number: 16, text: "Ähnlich ist es bei den Menschen, bei denen das Wort auf felsigen Boden fällt: Sobald sie es hören, nehmen sie es freudig auf;" },
      { number: 17, text: "aber sie haben keine Wurzeln, sondern sind unbeständig, und wenn sie dann um des Wortes willen bedrängt oder verfolgt werden, kommen sie sofort zu Fall." },
      { number: 18, text: "Bei anderen fällt das Wort in die Dornen: sie hören es zwar," },
      { number: 19, text: "aber die Sorgen der Welt, der trügerische Reichtum und die Gier nach all den anderen Dingen machen sich breit und ersticken es und es bringt keine Frucht.", footnotes: ["1 Tim 6,9"] },
      { number: 20, text: "Auf guten Boden ist das Wort bei denen gesät, die es hören und aufnehmen und Frucht bringen, dreißigfach, ja sechzigfach und hundertfach." },
      { number: 21, text: "Er sagte zu ihnen: Zündet man etwa ein Licht an und stülpt ein Gefäß darüber oder stellt es unter das Bett? Stellt man es nicht auf den Leuchter?", footnotes: ["Mt 5,15; Lk 11,33; (21-25) Lk 8,16-18", "Vgl. die Anmerkung zu Mt 5,15.", ""] },
      { number: 22, heading: "Vom rechten Hören: 4,21-25", text: "Es gibt nichts Verborgenes, das nicht offenbar wird, und nichts Geheimes, das nicht an den Tag kommt.", footnotes: ["Mt 10,26; Lk 12,2"] },
      { number: 23, text: "Wenn einer Ohren hat zum Hören, so höre er!", footnotes: ["Mt 11,15"] },
      { number: 24, text: "Weiter sagte er: Achtet auf das, was ihr hört! Nach dem Maß, mit dem ihr messt und zuteilt, wird euch zugeteilt werden, ja, es wird euch noch mehr gegeben.", footnotes: ["Mt 7,2; Lk 6,38"] },
      { number: 25, text: "Denn wer hat, dem wird gegeben; wer aber nicht hat, dem wird auch noch weggenommen, was er hat.", footnotes: ["Mt 13,12; 25,29; Lk 19,26"] },
      { number: 26, text: "Er sagte: Mit dem Reich Gottes ist es so, wie wenn ein Mann Samen auf seinen Acker sät;", footnotes: [""] },
      { number: 27, heading: "Das Gleichnis vom Wachsen der Saat: 4,26-29", text: "dann schläft er und steht wieder auf, es wird Nacht und wird Tag, der Samen keimt und wächst und der Mann weiß nicht, wie." },
      { number: 28, text: "Die Erde bringt von selbst ihre Frucht, zuerst den Halm, dann die Ähre, dann das volle Korn in der Ähre." },
      { number: 29, text: "Sobald aber die Frucht reif ist, legt er die Sichel an; denn die Zeit der Ernte ist da.", footnotes: ["Joël 4,13"] },
      { number: 30, text: "Er sagte: Womit sollen wir das Reich Gottes vergleichen, mit welchem Gleichnis sollen wir es beschreiben?", footnotes: ["(30-32) Mt 13,31f; Lk 13,18f"] },
      { number: 31, heading: "Das Gleichnis vom Senfkorn: 4,30-32", text: "Es gleicht einem Senfkorn. Dieses ist das kleinste von allen Samenkörnern, die man in die Erde sät." },
      { number: 32, text: "Ist es aber gesät, dann geht es auf und wird größer als alle anderen Gewächse und treibt große Zweige, sodass in seinem Schatten die Vögel des Himmels nisten können.", footnotes: ["Dan 4,8f.18; Ez 17,23"] },
      { number: 33, text: "Durch viele solche Gleichnisse verkündete er ihnen das Wort, so wie sie es aufnehmen konnten.", footnotes: ["(33-34) Mt 13,34"] },
      { number: 34, heading: "Schlussbemerkung zu den Gleichnissen: 4,33-34", text: "Er redete nur in Gleichnissen zu ihnen; seinen Jüngern aber erklärte er alles, wenn er mit ihnen allein war." },
      { number: 35, text: "Taten und Worte Jesu: 4,35 - 8,26 Am Abend dieses Tages sagte er zu ihnen: Wir wollen ans andere Ufer hinüberfahren.", footnotes: ["(35-41) Mt 8,18.23-27; Lk 8,22-25"] },
      { number: 36, heading: "Der Sturm auf dem See: 4,35-41", text: "Sie schickten die Leute fort und fuhren mit ihm in dem Boot, in dem er saß, weg; einige andere Boote begleiteten ihn." },
      { number: 37, text: "Plötzlich erhob sich ein heftiger Wirbelsturm, und die Wellen schlugen in das Boot, sodass es sich mit Wasser zu füllen begann." },
      { number: 38, text: "Er aber lag hinten im Boot auf einem Kissen und schlief. Sie weckten ihn und riefen: Meister, kümmert es dich nicht, dass wir zugrunde gehen?" },
      { number: 39, text: "Da stand er auf, drohte dem Wind und sagte zu dem See: Schweig, sei still! Und der Wind legte sich und es trat völlige Stille ein.", footnotes: ["6,51"] },
      { number: 40, text: "Er sagte zu ihnen: Warum habt ihr solche Angst? Habt ihr noch keinen Glauben?", footnotes: ["Statt «Habt ihr . . .» haben mehrere Textzeugen: Warum habt ihr keinen Glauben?"] },
      { number: 41, text: "Da ergriff sie große Furcht und sie sagten zueinander: Was ist das für ein Mensch, dass ihm sogar der Wind und der See gehorchen?" },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Sie kamen an das andere Ufer des Sees, in das Gebiet von Gerasa.", footnotes: ["(1-20) Mt 8,28-34; Lk 8,26-39", "Weniger gut bezeugte Lesarten: Gadara, Gergesa.", ""] },
      { number: 2, heading: "Die Heilung des Besessenen von Gerasa: 5,1-20", text: "Als er aus dem Boot stieg, lief ihm ein Mann entgegen, der von einem unreinen Geist besessen war. Er kam von den Grabhöhlen," },
      { number: 3, text: "in denen er lebte. Man konnte ihn nicht bändigen, nicht einmal mit Fesseln." },
      { number: 4, text: "Schon oft hatte man ihn an Händen und Füßen gefesselt, aber er hatte die Ketten gesprengt und die Fesseln zerrissen; niemand konnte ihn bezwingen." },
      { number: 5, text: "Bei Tag und Nacht schrie er unaufhörlich in den Grabhöhlen und auf den Bergen und schlug sich mit Steinen." },
      { number: 6, text: "Als er Jesus von weitem sah, lief er zu ihm hin, warf sich vor ihm nieder" },
      { number: 7, text: "und schrie laut: Was habe ich mit dir zu tun, Jesus, Sohn des höchsten Gottes? Ich beschwöre dich bei Gott, quäle mich nicht!", footnotes: ["1,24; 3,11; Lk 4,34; Joh 2,4"] },
      { number: 8, text: "Jesus hatte nämlich zu ihm gesagt: Verlass diesen Mann, du unreiner Geist!" },
      { number: 9, text: "Jesus fragte ihn: Wie heißt du? Er antwortete: Mein Name ist Legion; denn wir sind viele.", footnotes: ["Eine Legion war die größte römische Heereseinheit (6000 Mann)."] },
      { number: 10, text: "Und er flehte Jesus an, sie nicht aus dieser Gegend zu verbannen." },
      { number: 11, text: "Nun weidete dort an einem Berghang gerade eine große Schweineherde.", footnotes: ["11-17: Die Erzählung spielt im halbheidnischen Ostjordanland, wo auch Schweine gezüchtet wurden. Schweine galten als unrein, darum war den Juden die Aufzucht von Schweinen und das Essen von Schweinefleisch verboten."] },
      { number: 12, text: "Da baten ihn die Dämonen: Lass uns doch in die Schweine hineinfahren!" },
      { number: 13, text: "Jesus erlaubte es ihnen. Darauf verließen die unreinen Geister den Menschen und fuhren in die Schweine und die Herde stürzte sich den Abhang hinab in den See. Es waren etwa zweitausend Tiere und alle ertranken." },
      { number: 14, text: "Die Hirten flohen und erzählten alles in der Stadt und in den Dörfern. Darauf eilten die Leute herbei, um zu sehen, was geschehen war." },
      { number: 15, text: "Sie kamen zu Jesus und sahen bei ihm den Mann, der von der Legion Dämonen besessen gewesen war. Er saß ordentlich gekleidet da und war wieder bei Verstand. Da fürchteten sie sich." },
      { number: 16, text: "Die, die alles gesehen hatten, berichteten ihnen, was mit dem Besessenen und mit den Schweinen geschehen war." },
      { number: 17, text: "Darauf baten die Leute Jesus, ihr Gebiet zu verlassen." },
      { number: 18, text: "Als er ins Boot stieg, bat ihn der Mann, der zuvor von den Dämonen besessen war, bei ihm bleiben zu dürfen." },
      { number: 19, text: "Aber Jesus erlaubte es ihm nicht, sondern sagte: Geh nach Hause und berichte deiner Familie alles, was der Herr für dich getan und wie er Erbarmen mit dir gehabt hat." },
      { number: 20, text: "Da ging der Mann weg und verkündete in der ganzen Dekapolis, was Jesus für ihn getan hatte, und alle staunten." },
      { number: 21, text: "Jesus fuhr im Boot wieder ans andere Ufer hinüber und eine große Menschenmenge versammelte sich um ihn. Während er noch am See war,", footnotes: ["(21-43) Mt 9,18-26; Lk 8,40-56"] },
      { number: 22, heading: "Die Auferweckung der Tochter des Jaïrus und die Heilung einer kranken Frau: 5,21-43", text: "kam ein Synagogenvorsteher namens Jaïrus zu ihm. Als er Jesus sah, fiel er ihm zu Füßen" },
      { number: 23, text: "und flehte ihn um Hilfe an; er sagte: Meine Tochter liegt im Sterben. Komm und leg ihr die Hände auf, damit sie wieder gesund wird und am Leben bleibt." },
      { number: 24, text: "Da ging Jesus mit ihm. Viele Menschen folgten ihm und drängten sich um ihn." },
      { number: 25, text: "Darunter war eine Frau, die schon zwölf Jahre an Blutungen litt.", footnotes: ["25-34: Vgl. die Anmerkung zu Mt 9,20-22."] },
      { number: 26, text: "Sie war von vielen Ärzten behandelt worden und hatte dabei sehr zu leiden; ihr ganzes Vermögen hatte sie ausgegeben, aber es hatte ihr nichts genutzt, sondern ihr Zustand war immer schlimmer geworden." },
      { number: 27, text: "Sie hatte von Jesus gehört. Nun drängte sie sich in der Menge von hinten an ihn heran und berührte sein Gewand." },
      { number: 28, text: "Denn sie sagte sich: Wenn ich auch nur sein Gewand berühre, werde ich geheilt.", footnotes: ["6,56; Mt 14,36"] },
      { number: 29, text: "Sofort hörte die Blutung auf und sie spürte deutlich, dass sie von ihrem Leiden geheilt war." },
      { number: 30, text: "Im selben Augenblick fühlte Jesus, dass eine Kraft von ihm ausströmte, und er wandte sich in dem Gedränge um und fragte: Wer hat mein Gewand berührt?" },
      { number: 31, text: "Seine Jünger sagten zu ihm: Du siehst doch, wie sich die Leute um dich drängen, und da fragst du: Wer hat mich berührt?" },
      { number: 32, text: "Er blickte umher, um zu sehen, wer es getan hatte." },
      { number: 33, text: "Da kam die Frau, zitternd vor Furcht, weil sie wusste, was mit ihr geschehen war; sie fiel vor ihm nieder und sagte ihm die ganze Wahrheit." },
      { number: 34, text: "Er aber sagte zu ihr: Meine Tochter, dein Glaube hat dir geholfen. Geh in Frieden! Du sollst von deinem Leiden geheilt sein.", footnotes: ["10,52; Lk 7,50; 17,19; 18,42"] },
      { number: 35, text: "Während Jesus noch redete, kamen Leute, die zum Haus des Synagogenvorstehers gehörten, und sagten (zu Jaïrus): Deine Tochter ist gestorben. Warum bemühst du den Meister noch länger?" },
      { number: 36, text: "Jesus, der diese Worte gehört hatte, sagte zu dem Synagogenvorsteher: Sei ohne Furcht; glaube nur!" },
      { number: 37, text: "Und er ließ keinen mitkommen außer Petrus, Jakobus und Johannes, den Bruder des Jakobus." },
      { number: 38, text: "Sie gingen zum Haus des Synagogenvorstehers. Als Jesus den Lärm bemerkte und hörte, wie die Leute laut weinten und jammerten," },
      { number: 39, text: "trat er ein und sagte zu ihnen: Warum schreit und weint ihr? Das Kind ist nicht gestorben, es schläft nur." },
      { number: 40, text: "Da lachten sie ihn aus. Er aber schickte alle hinaus und nahm außer seinen Begleitern nur die Eltern mit in den Raum, in dem das Kind lag." },
      { number: 41, text: "Er fasste das Kind an der Hand und sagte zu ihm: Talita kum!, das heißt übersetzt: Mädchen, ich sage dir, steh auf!" },
      { number: 42, text: "Sofort stand das Mädchen auf und ging umher. Es war zwölf Jahre alt. Die Leute gerieten außer sich vor Entsetzen." },
      { number: 43, text: "Doch er schärfte ihnen ein, niemand dürfe etwas davon erfahren; dann sagte er, man solle dem Mädchen etwas zu essen geben." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Von dort brach Jesus auf und kam in seine Heimatstadt; seine Jünger begleiteten ihn.", footnotes: ["(1-6a) Mt 13,54-58; Lk 4,16-30", "1-6: Vgl. die Anmerkung zu Mt 12,46f; 13,54-58.", ""] },
      { number: 2, heading: "Die Ablehnung Jesu in seiner Heimat: 6,1-6a", text: "Am Sabbat lehrte er in der Synagoge. Und die vielen Menschen, die ihm zuhörten, staunten und sagten: Woher hat er das alles? Was ist das für eine Weisheit, die ihm gegeben ist! Und was sind das für Wunder, die durch ihn geschehen!", footnotes: ["Joh 7,15"] },
      { number: 3, text: "Ist das nicht der Zimmermann, der Sohn der Maria und der Bruder von Jakobus, Joses, Judas und Simon? Leben nicht seine Schwestern hier unter uns? Und sie nahmen Anstoß an ihm und lehnten ihn ab.", footnotes: ["Joh 6,42", "«Joses» ist Nebenform von «Josef»."] },
      { number: 4, text: "Da sagte Jesus zu ihnen: Nirgends hat ein Prophet so wenig Ansehen wie in seiner Heimat, bei seinen Verwandten und in seiner Familie.", footnotes: ["Joh 4,44"] },
      { number: 5, text: "Und er konnte dort kein Wunder tun; nur einigen Kranken legte er die Hände auf und heilte sie." },
      { number: 6, text: "Und er wunderte sich über ihren Unglauben." },
      { number: 6, text: "Jesus zog durch die benachbarten Dörfer und lehrte.", footnotes: ["(6b-13) Mt 9,35; 10,1.5-14; Lk 9,1-6"] },
      { number: 7, heading: "Die Aussendung der zwölf Jünger: 6,6b-13", text: "Er rief die Zwölf zu sich und sandte sie aus, jeweils zwei zusammen. Er gab ihnen die Vollmacht, die unreinen Geister auszutreiben," },
      { number: 8, text: "und er gebot ihnen, außer einem Wanderstab nichts auf den Weg mitzunehmen, kein Brot, keine Vorratstasche, kein Geld im Gürtel," },
      { number: 9, text: "kein zweites Hemd und an den Füßen nur Sandalen." },
      { number: 10, text: "Und er sagte zu ihnen: Bleibt in dem Haus, in dem ihr einkehrt, bis ihr den Ort wieder verlasst." },
      { number: 11, text: "Wenn man euch aber in einem Ort nicht aufnimmt und euch nicht hören will, dann geht weiter und schüttelt den Staub von euren Füßen, zum Zeugnis gegen sie." },
      { number: 12, text: "Die Zwölf machten sich auf den Weg und riefen die Menschen zur Umkehr auf." },
      { number: 13, text: "Sie trieben viele Dämonen aus und salbten viele Kranke mit Öl und heilten sie." },
      { number: 14, text: "Der König Herodes hörte von Jesus; denn sein Name war bekannt geworden und man sagte: Johannes der Täufer ist von den Toten auferstanden; deshalb wirken solche Kräfte in ihm.", footnotes: ["(14f) Mt 16,14; (14-16) Mt 14,1f; Lk 9,7-9"] },
      { number: 15, heading: "Das Urteil des Herodes über Jesus: 6,14-16", text: "Andere sagten: Er ist Elija. Wieder andere: Er ist ein Prophet, wie einer von den alten Propheten." },
      { number: 16, text: "Als aber Herodes von ihm hörte, sagte er: Johannes, den ich enthaupten ließ, ist auferstanden." },
      { number: 17, text: "Herodes hatte nämlich Johannes festnehmen und ins Gefängnis werfen lassen. Schuld daran war Herodias, die Frau seines Bruders Philippus, die er geheiratet hatte.", footnotes: ["(17-29) Mt 14,3-12; Lk 3,19f", "17f: Vgl. die Anmerkung zu Mt 14,4.", ""] },
      { number: 18, heading: "Die Enthauptung des Täufers: 6,17-29", text: "Denn Johannes hatte zu Herodes gesagt: Du hattest nicht das Recht, die Frau deines Bruders zur Frau zu nehmen.", footnotes: ["Lev 18,16; 20,21"] },
      { number: 19, text: "Herodias verzieh ihm das nicht und wollte ihn töten lassen. Sie konnte ihren Plan aber nicht durchsetzen," },
      { number: 20, text: "denn Herodes fürchtete sich vor Johannes, weil er wusste, dass dieser ein gerechter und heiliger Mann war. Darum schützte er ihn. Sooft er mit ihm sprach, wurde er unruhig und ratlos, und doch hörte er ihm gern zu." },
      { number: 21, text: "Eines Tages ergab sich für Herodias eine günstige Gelegenheit. An seinem Geburtstag lud Herodes seine Hofbeamten und Offiziere zusammen mit den vornehmsten Bürgern von Galiläa zu einem Festmahl ein." },
      { number: 22, text: "Da kam die Tochter der Herodias und tanzte und sie gefiel dem Herodes und seinen Gästen so sehr, dass der König zu ihr sagte: Wünsch dir, was du willst; ich werde es dir geben." },
      { number: 23, text: "Er schwor ihr sogar: Was du auch von mir verlangst, ich will es dir geben, und wenn es die Hälfte meines Reiches wäre.", footnotes: ["Est 5,3.6; 7,2"] },
      { number: 24, text: "Sie ging hinaus und fragte ihre Mutter: Was soll ich mir wünschen? Herodias antwortete: Den Kopf des Täufers Johannes." },
      { number: 25, text: "Da lief das Mädchen zum König hinein und sagte: Ich will, dass du mir sofort auf einer Schale den Kopf des Täufers Johannes bringen lässt." },
      { number: 26, text: "Da wurde der König sehr traurig, aber weil er vor allen Gästen einen Schwur geleistet hatte, wollte er ihren Wunsch nicht ablehnen." },
      { number: 27, text: "Deshalb befahl er einem Scharfrichter, sofort ins Gefängnis zu gehen und den Kopf des Täufers herzubringen. Der Scharfrichter ging und enthauptete Johannes." },
      { number: 28, text: "Dann brachte er den Kopf auf einer Schale, gab ihn dem Mädchen und das Mädchen gab ihn seiner Mutter." },
      { number: 29, text: "Als die Jünger des Johannes das hörten, kamen sie, holten seinen Leichnam und legten ihn in ein Grab." },
      { number: 30, text: "Die Apostel versammelten sich wieder bei Jesus und berichteten ihm alles, was sie getan und gelehrt hatten.", footnotes: ["(30-31) Lk 9,10; 10,17"] },
      { number: 31, heading: "Die Rückkehr der Jünger und die Speisung der Fünftausend: 6,30-44", text: "Da sagte er zu ihnen: Kommt mit an einen einsamen Ort, wo wir allein sind, und ruht ein wenig aus. Denn sie fanden nicht einmal Zeit zum Essen, so zahlreich waren die Leute, die kamen und gingen." },
      { number: 32, text: "Sie fuhren also mit dem Boot in eine einsame Gegend, um allein zu sein.", footnotes: ["(32-44) 8,1-10; Mt 15,32-39; (32-34) Mt 14,13-21; Lk 9,10-17; Joh 6,1-13"] },
      { number: 33, text: "Aber man sah sie abfahren und viele erfuhren davon; sie liefen zu Fuß aus allen Städten dorthin und kamen noch vor ihnen an." },
      { number: 34, text: "Als er ausstieg und die vielen Menschen sah, hatte er Mitleid mit ihnen; denn sie waren wie Schafe, die keinen Hirten haben. Und er lehrte sie lange.", footnotes: ["Mt 9,36; Num 27,17; 1 Kön 22,17; Ez 34,5"] },
      { number: 35, text: "Gegen Abend kamen seine Jünger zu ihm und sagten: Der Ort ist abgelegen und es ist schon spät." },
      { number: 36, text: "Schick sie weg, damit sie in die umliegenden Gehöfte und Dörfer gehen und sich etwas zu essen kaufen können." },
      { number: 37, text: "Er erwiderte: Gebt ihr ihnen zu essen! Sie sagten zu ihm: Sollen wir weggehen, für zweihundert Denare Brot kaufen und es ihnen geben, damit sie zu essen haben?" },
      { number: 38, text: "Er sagte zu ihnen: Wie viele Brote habt ihr? Geht und seht nach! Sie sahen nach und berichteten: Fünf Brote und außerdem zwei Fische." },
      { number: 39, text: "Dann befahl er ihnen, den Leuten zu sagen, sie sollten sich in Gruppen ins grüne Gras setzen." },
      { number: 40, text: "Und sie setzten sich in Gruppen zu hundert und zu fünfzig." },
      { number: 41, text: "Darauf nahm er die fünf Brote und die zwei Fische, blickte zum Himmel auf, sprach den Lobpreis, brach die Brote und gab sie den Jüngern, damit sie sie an die Leute austeilten. Auch die zwei Fische ließ er unter allen verteilen.", footnotes: ["8,19"] },
      { number: 42, text: "Und alle aßen und wurden satt." },
      { number: 43, text: "Als die Jünger die Reste der Brote und auch der Fische einsammelten, wurden zwölf Körbe voll." },
      { number: 44, text: "Es waren fünftausend Männer, die von den Broten gegessen hatten." },
      { number: 45, text: "Gleich darauf forderte er seine Jünger auf, ins Boot zu steigen und ans andere Ufer nach Betsaida vorauszufahren. Er selbst wollte inzwischen die Leute nach Hause schicken.", footnotes: ["(45-52) Mt 14,22-33; Joh 6,16-21"] },
      { number: 46, heading: "Der Gang Jesu auf dem Wasser: 6,45-52", text: "Nachdem er sich von ihnen verabschiedet hatte, ging er auf einen Berg, um zu beten.", footnotes: ["Lk 6,12"] },
      { number: 47, text: "Spät am Abend war das Boot mitten auf dem See, er aber war allein an Land." },
      { number: 48, text: "Und er sah, wie sie sich beim Rudern abmühten, denn sie hatten Gegenwind. In der vierten Nachtwache ging er auf dem See zu ihnen hin, wollte aber an ihnen vorübergehen.", footnotes: ["Zu «Nachtwache» vgl. die Anmerkung zu Mt 14,25."] },
      { number: 49, text: "Als sie ihn über den See gehen sahen, meinten sie, es sei ein Gespenst, und schrien auf." },
      { number: 50, text: "Alle sahen ihn und erschraken. Doch er begann mit ihnen zu reden und sagte: Habt Vertrauen, ich bin es; fürchtet euch nicht!" },
      { number: 51, text: "Dann stieg er zu ihnen ins Boot und der Wind legte sich. Sie aber waren bestürzt und außer sich.", footnotes: ["4,39"] },
      { number: 52, text: "Denn sie waren nicht zur Einsicht gekommen, als das mit den Broten geschah; ihr Herz war verstockt.", footnotes: ["8,17"] },
      { number: 53, text: "Sie fuhren auf das Ufer zu, kamen nach Gennesaret und legten dort an.", footnotes: ["(53-56) Mt 14,34-36"] },
      { number: 54, heading: "Krankenheilungen in Gennesaret: 6,53-56", text: "Als sie aus dem Boot stiegen, erkannte man ihn sofort." },
      { number: 55, text: "Die Menschen eilten durch die ganze Gegend und brachten die Kranken auf Tragbahren zu ihm, sobald sie hörten, wo er war.", footnotes: ["Mt 4,24"] },
      { number: 56, text: "Und immer, wenn er in ein Dorf oder eine Stadt oder zu einem Gehöft kam, trug man die Kranken auf die Straße hinaus und bat ihn, er möge sie wenigstens den Saum seines Gewandes berühren lassen. Und alle, die ihn berührten, wurden geheilt.", footnotes: ["5,28; Mt 14,36"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Die Pharisäer und einige Schriftgelehrte, die aus Jerusalem gekommen waren, hielten sich bei Jesus auf.", footnotes: ["(1-23) Mt 15,1-20", "1-8.14-23: Die Reinheitsvorschriften, die von den Juden entwickelt wurden (von den «Alten», das heißt den jüdischen Lehrautoritäten), gingen davon aus, dass alles Geschlechtliche, Ausscheidungen des Körpers, bestimmte Tiere und Menschen (Heiden, Sünder, Kranke mit bestimmten Krankheiten, z. B. Aussatz), auch Leichen ebenso wie jede Art von Schmutz verunreinigen, das heißt für den Gottesdienst und das Gebet unwürdig machen. Diese Unreinheit konnte sich durch Berührung auch auf tote Gegenstände übertragen. Zur Wiederherstellung der kultischen Reinheit bedurfte es gewisser Waschungen, Fastenübungen und religiöser Handlungen. Auch Speisen konnten verunreinigen (vgl. Lev 11 - 15). Demgegenüber stellte Jesus fest, dass der Wert einer Handlung allein von der Gesinnung des Handelnden abhängt (vgl. auch die Anmerkung zu Mt 15,11).", ""] },
      { number: 2, heading: "Von Reinheit und Unreinheit: 7,1-23", text: "Sie sahen, dass einige seiner Jünger ihr Brot mit unreinen, das heißt mit ungewaschenen Händen aßen.", footnotes: ["Lk 11,38; Kol 2,21f"] },
      { number: 3, text: "Die Pharisäer essen nämlich wie alle Juden nur, wenn sie vorher mit einer Hand voll Wasser die Hände gewaschen haben, wie es die Überlieferung der Alten vorschreibt.", footnotes: ["mit einer Hand voll Wasser, wörtlich: mit der Faust."] },
      { number: 4, text: "Auch wenn sie vom Markt kommen, essen sie nicht, ohne sich vorher zu waschen. Noch viele andere überlieferte Vorschriften halten sie ein, wie das Abspülen von Bechern, Krügen und Kesseln.", footnotes: ["Mt 23,25", "Andere Übersetzungsmöglichkeit: Und sie essen auch das, was vom Markt kommt, erst, wenn sie es gewaschen haben."] },
      { number: 5, text: "Die Pharisäer und die Schriftgelehrten fragten ihn also: Warum halten sich deine Jünger nicht an die Überlieferung der Alten, sondern essen ihr Brot mit unreinen Händen?" },
      { number: 6, text: "Er antwortete ihnen: Der Prophet Jesaja hatte Recht mit dem, was er über euch Heuchler sagte: Dieses Volk ehrt mich mit den Lippen, / sein Herz aber ist weit weg von mir.", footnotes: ["Jes 29,13 G"] },
      { number: 7, text: "Es ist sinnlos, wie sie mich verehren; / was sie lehren, sind Satzungen von Menschen." },
      { number: 8, text: "Ihr gebt Gottes Gebot preis und haltet euch an die Überlieferung der Menschen." },
      { number: 9, text: "Und weiter sagte Jesus: Sehr geschickt setzt ihr Gottes Gebot außer Kraft und haltet euch an eure eigene Überlieferung.", footnotes: ["9-13: Das Korbán-Gelübde war ein Gelöbnis, durch das ein Gegenstand in den Dienst Gottes, des Tempels oder des Gottesdienstes gestellt und so der Nutznießung durch Menschen entzogen wurde. Wer also alle Verpflichtungen und Leistungen seinen Eltern gegenüber durch das Korbán-Gelübde Gott weihte («Opfergabe ist, was ihr von mir als Unterstützung haben solltet»), entzog diesen ihren Unterhalt, ohne dass er gezwungen werden konnte, das den Eltern Nicht-Geleistete auch wirklich für religiöse Zwecke einzusetzen. Diese Praxis widersprach deutlich dem Sinn des vierten Gebots, das dazu bestimmt war, auch den Unterhalt alter und kranker Eltern zu sichern."] },
      { number: 10, text: "Mose hat zum Beispiel gesagt: Ehre deinen Vater und deine Mutter!, und: Wer Vater oder Mutter verflucht, soll mit dem Tod bestraft werden.", footnotes: ["Ex 20,12; 21,17; Lev 20,9; Dtn 5,16"] },
      { number: 11, text: "Ihr aber lehrt: Es ist erlaubt, dass einer zu seinem Vater oder seiner Mutter sagt: Was ich dir schulde, ist Korbán, das heißt: eine Opfergabe." },
      { number: 12, text: "Damit hindert ihr ihn daran, noch etwas für Vater oder Mutter zu tun." },
      { number: 13, text: "So setzt ihr durch eure eigene Überlieferung Gottes Wort außer Kraft. Und ähnlich handelt ihr in vielen Fällen." },
      { number: 14, text: "Dann rief er die Leute wieder zu sich und sagte: Hört mir alle zu und begreift, was ich sage:" },
      { number: 15, text: "Nichts, was von außen in den Menschen hineinkommt, kann ihn unrein machen, sondern was aus dem Menschen herauskommt, das macht ihn unrein." },
      { number: 16, text: "[]", footnotes: ["Spätere Textzeugen fügen hier entsprechend 4,23 ein: Wenn einer Ohren hat zum Hören, so höre er!"] },
      { number: 17, text: "Er verließ die Menge und ging in ein Haus. Da fragten ihn seine Jünger nach dem Sinn dieses rätselhaften Wortes." },
      { number: 18, text: "Er antwortete ihnen: Begreift auch ihr nicht? Seht ihr nicht ein, dass das, was von außen in den Menschen hineinkommt, ihn nicht unrein machen kann?" },
      { number: 19, text: "Denn es gelangt ja nicht in sein Herz, sondern in den Magen und wird wieder ausgeschieden. Damit erklärte Jesus alle Speisen für rein." },
      { number: 20, text: "Weiter sagte er: Was aus dem Menschen herauskommt, das macht ihn unrein." },
      { number: 21, text: "Denn von innen, aus dem Herzen der Menschen, kommen die bösen Gedanken, Unzucht, Diebstahl, Mord,", footnotes: ["Bei den Juden galt das Herz als Mitte der Gemütsregungen und Sitz des Gewissens. Vgl. die Anmerkung zu Mt 15,11."] },
      { number: 22, text: "Ehebruch, Habgier, Bosheit, Hinterlist, Ausschweifung, Neid, Verleumdung, Hochmut und Unvernunft." },
      { number: 23, text: "All dieses Böse kommt von innen und macht den Menschen unrein." },
      { number: 24, text: "Jesus brach auf und zog von dort in das Gebiet von Tyrus. Er ging in ein Haus, wollte aber, dass niemand davon erfuhr; doch es konnte nicht verborgen bleiben.", footnotes: ["(24-30) Mt 15,21-28", "24-30: Jesus hat sich nicht von sich aus an Heiden gewandt (vgl. Mt 10,5f; 15,24), hat aber Heiden geholfen, wenn sie ihn darum baten. «Hund» war ein jüdisches Schimpfwort für die Heiden. Die Juden verstanden sich demgegenüber als Kinder im Haus Gottes.", ""] },
      { number: 25, heading: "Die Erhörung der Bitte einer heidnischen Frau: 7,24-30", text: "Eine Frau, deren Tochter von einem unreinen Geist besessen war, hörte von ihm; sie kam sogleich herbei und fiel ihm zu Füßen." },
      { number: 26, text: "Die Frau, von Geburt Syrophönizierin, war eine Heidin. Sie bat ihn, aus ihrer Tochter den Dämon auszutreiben.", footnotes: ["Heidin, wörtlich: Griechin."] },
      { number: 27, text: "Da sagte er zu ihr: Lasst zuerst die Kinder satt werden; denn es ist nicht recht, das Brot den Kindern wegzunehmen und den Hunden vorzuwerfen." },
      { number: 28, text: "Sie erwiderte ihm: Ja, du hast recht, Herr! Aber auch für die Hunde unter dem Tisch fällt etwas von dem Brot ab, das die Kinder essen." },
      { number: 29, text: "Er antwortete ihr: Weil du das gesagt hast, sage ich dir: Geh nach Hause, der Dämon hat deine Tochter verlassen." },
      { number: 30, text: "Und als sie nach Hause kam, fand sie das Kind auf dem Bett liegen und sah, dass der Dämon es verlassen hatte." },
      { number: 31, text: "Jesus verließ das Gebiet von Tyrus wieder und kam über Sidon an den See von Galiläa, mitten in das Gebiet der Dekapolis.", footnotes: ["(31-37) Mt 15,29-31"] },
      { number: 32, heading: "Die Heilung eines Taubstummen: 7,31-37", text: "Da brachte man einen Taubstummen zu Jesus und bat ihn, er möge ihn berühren." },
      { number: 33, text: "Er nahm ihn beiseite, von der Menge weg, legte ihm die Finger in die Ohren und berührte dann die Zunge des Mannes mit Speichel;", footnotes: ["8,23", "Speichel galt damals als Heilmittel."] },
      { number: 34, text: "danach blickte er zum Himmel auf, seufzte und sagte zu dem Taubstummen: Effata!, das heißt: Öffne dich!" },
      { number: 35, text: "Sogleich öffneten sich seine Ohren, seine Zunge wurde von ihrer Fessel befreit und er konnte richtig reden." },
      { number: 36, text: "Jesus verbot ihnen, jemand davon zu erzählen. Doch je mehr er es ihnen verbot, desto mehr machten sie es bekannt." },
      { number: 37, text: "Außer sich vor Staunen sagten sie: Er hat alles gut gemacht; er macht, dass die Tauben hören und die Stummen sprechen.", footnotes: ["Jes 35,5"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "In jenen Tagen waren wieder einmal viele Menschen um Jesus versammelt. Da sie nichts zu essen hatten, rief er die Jünger zu sich und sagte:", footnotes: ["(1-10) Mt 14,13-21; Mk 6,32-44; Lk 9,10-17; Joh 6,1-13; Mt 15,32-39"] },
      { number: 2, heading: "Die Speisung der Viertausend: 8,1-10", text: "Ich habe Mitleid mit diesen Menschen; sie sind schon drei Tage bei mir und haben nichts mehr zu essen.", footnotes: ["Mt 9,36"] },
      { number: 3, text: "Wenn ich sie hungrig nach Hause schicke, werden sie unterwegs zusammenbrechen; denn einige von ihnen sind von weither gekommen." },
      { number: 4, text: "Seine Jünger antworteten ihm: Woher soll man in dieser unbewohnten Gegend Brot bekommen, um sie alle satt zu machen?" },
      { number: 5, text: "Er fragte sie: Wie viele Brote habt ihr? Sie antworteten: Sieben." },
      { number: 6, text: "Da forderte er die Leute auf, sich auf den Boden zu setzen. Dann nahm er die sieben Brote, sprach das Dankgebet, brach die Brote und gab sie seinen Jüngern zum Verteilen; und die Jünger teilten sie an die Leute aus.", footnotes: ["8,20"] },
      { number: 7, text: "Sie hatten auch noch ein paar Fische bei sich. Jesus segnete sie und ließ auch sie austeilen." },
      { number: 8, text: "Die Leute aßen und wurden satt. Dann sammelte man die übrig gebliebenen Brotstücke ein, sieben Körbe voll." },
      { number: 9, text: "Es waren etwa viertausend Menschen beisammen. Danach schickte er sie nach Hause." },
      { number: 10, text: "Gleich darauf stieg er mit seinen Jüngern ins Boot und fuhr in das Gebiet von Dalmanuta." },
      { number: 11, text: "Da kamen die Pharisäer und begannen ein Streitgespräch mit ihm; sie forderten von ihm ein Zeichen vom Himmel, um ihn auf die Probe zu stellen.", footnotes: ["Mt 12,38f; Lk 11,16.29; Joh 6,30; 1 Kor 1,22; (11-13) Mt 16,1-4"] },
      { number: 12, heading: "Die Verweigerung eines Zeichens: 8,11-13", text: "Da seufzte er tief auf und sagte: Was fordert diese Generation ein Zeichen? Amen, das sage ich euch: Dieser Generation wird niemals ein Zeichen gegeben werden." },
      { number: 13, text: "Und er verließ sie, stieg in das Boot und fuhr ans andere Ufer." },
      { number: 14, text: "Die Jünger hatten vergessen, bei der Abfahrt Brote mitzunehmen; nur ein einziges hatten sie dabei.", footnotes: ["(14-21) Mt 16,5-12"] },
      { number: 15, heading: "Warnung vor den Pharisäern und vor Herodes: 8,14-21", text: "Und er warnte sie: Gebt Acht, hütet euch vor dem Sauerteig der Pharisäer und dem Sauerteig des Herodes!", footnotes: ["Lk 12,1"] },
      { number: 16, text: "Sie aber machten sich Gedanken, weil sie kein Brot bei sich hatten." },
      { number: 17, text: "Als er das merkte, sagte er zu ihnen: Was macht ihr euch darüber Gedanken, dass ihr kein Brot habt? Begreift und versteht ihr immer noch nicht? Ist denn euer Herz verstockt?", footnotes: ["6,52"] },
      { number: 18, text: "Habt ihr denn keine Augen, um zu sehen, und keine Ohren, um zu hören? Erinnert ihr euch nicht:", footnotes: ["Jer 5,21"] },
      { number: 19, text: "Als ich die fünf Brote für die Fünftausend brach, wie viele Körbe voll Brotstücke habt ihr da aufgesammelt? Sie antworteten ihm: Zwölf.", footnotes: ["6,41-44"] },
      { number: 20, text: "Und als ich die sieben Brote für die Viertausend brach, wie viele Körbe voll habt ihr da aufgesammelt? Sie antworteten: Sieben.", footnotes: ["8,6-9"] },
      { number: 21, text: "Da sagte er zu ihnen: Versteht ihr immer noch nicht?" },
      { number: 22, text: "Sie kamen nach Betsaida. Da brachte man einen Blinden zu Jesus und bat ihn, er möge ihn berühren.", footnotes: [""] },
      { number: 23, heading: "Die Heilung eines Blinden bei Betsaida: 8,22-26", text: "Er nahm den Blinden bei der Hand, führte ihn vor das Dorf hinaus, bestrich seine Augen mit Speichel, legte ihm die Hände auf und fragte ihn: Siehst du etwas?", footnotes: ["7,33; Joh 9,6"] },
      { number: 24, text: "Der Mann blickte auf und sagte: Ich sehe Menschen; denn ich sehe etwas, das wie Bäume aussieht und umhergeht." },
      { number: 25, text: "Da legte er ihm nochmals die Hände auf die Augen; nun sah der Mann deutlich. Er war geheilt und konnte alles ganz genau sehen." },
      { number: 26, text: "Jesus schickte ihn nach Hause und sagte: Geh aber nicht in das Dorf hinein!" },
      { number: 27, text: "Jesus ging mit seinen Jüngern in die Dörfer bei Cäsarea Philippi. Unterwegs fragte er die Jünger: Für wen halten mich die Menschen?", footnotes: ["(27-30) Mt 16,13-20; Lk 9,18-22"] },
      { number: 28, heading: "Auf dem Weg nach Jerusalem: 8,27 - 10,52 | Das Messiasbekenntnis des Petrus: 8,27-30", text: "Sie sagten zu ihm: Einige für Johannes den Täufer, andere für Elija, wieder andere für sonst einen von den Propheten.", footnotes: ["6,14"] },
      { number: 29, text: "Da fragte er sie: Ihr aber, für wen haltet ihr mich? Simon Petrus antwortete ihm: Du bist der Messias!", footnotes: ["Joh 11,27"] },
      { number: 30, text: "Doch er verbot ihnen, mit jemand über ihn zu sprechen." },
      { number: 31, text: "Dann begann er, sie darüber zu belehren, der Menschensohn müsse vieles erleiden und von den Ältesten, den Hohenpriestern und den Schriftgelehrten verworfen werden; er werde getötet, aber nach drei Tagen werde er auferstehen.", footnotes: ["9,31; 10,32-34; (31-33) Mt 16,21-23; Lk 9,22"] },
      { number: 32, heading: "Die erste Ankündigung von Leiden und Auferstehung: 8,31-33", text: "Und er redete ganz offen darüber. Da nahm ihn Petrus beiseite und machte ihm Vorwürfe." },
      { number: 33, text: "Jesus wandte sich um, sah seine Jünger an und wies Petrus mit den Worten zurecht: Weg mit dir, Satan, geh mir aus den Augen! Denn du hast nicht das im Sinn, was Gott will, sondern was die Menschen wollen." },
      { number: 34, text: "Er rief die Volksmenge und seine Jünger zu sich und sagte: Wer mein Jünger sein will, der verleugne sich selbst, nehme sein Kreuz auf sich und folge mir nach.", footnotes: ["Mt 10,38f; Lk 14,27; Mt 8,22; (8,34 - 9,1) Mt 16,24-28; Lk 9,23-27"] },
      { number: 35, heading: "Von Nachfolge und Selbstverleugnung: 8,34 - 9,1", text: "Denn wer sein Leben retten will, wird es verlieren; wer aber sein Leben um meinetwillen und um des Evangeliums willen verliert, wird es retten.", footnotes: ["Mt 10,39; Lk 17,33; Joh 12,25"] },
      { number: 36, text: "Was nützt es einem Menschen, wenn er die ganze Welt gewinnt, dabei aber sein Leben einbüßt?" },
      { number: 37, text: "Um welchen Preis könnte ein Mensch sein Leben zurückkaufen?" },
      { number: 38, text: "Denn wer sich vor dieser treulosen und sündigen Generation meiner und meiner Worte schämt, dessen wird sich auch der Menschensohn schämen, wenn er mit den heiligen Engeln in der Hoheit seines Vaters kommt.", footnotes: ["Mt 10,33", "Zu «treulosen» vgl. die Anmerkung zu Mt 12,39.", ""] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Und er sagte zu ihnen: Amen, ich sage euch: Von denen, die hier stehen, werden einige den Tod nicht erleiden, bis sie gesehen haben, dass das Reich Gottes in (seiner ganzen) Macht gekommen ist.", footnotes: ["Mt 10,23; 24,34", ""] },
      { number: 2, text: "Sechs Tage danach nahm Jesus Petrus, Jakobus und Johannes beiseite und führte sie auf einen hohen Berg, aber nur sie allein. Und er wurde vor ihren Augen verwandelt;", footnotes: ["(2-8) 2 Petr 1,16-18; (2-10) Mt 17,1-9; Lk 9,28-36"] },
      { number: 3, heading: "Die Verklärung Jesu: 9,2-10", text: "seine Kleider wurden strahlend weiß, so weiß, wie sie auf Erden kein Bleicher machen kann." },
      { number: 4, text: "Da erschien vor ihren Augen Elija und mit ihm Mose und sie redeten mit Jesus.", footnotes: ["4f.11-13: Vgl. die Anmerkung zu Mt 17,10-13."] },
      { number: 5, text: "Petrus sagte zu Jesus: Rabbi, es ist gut, dass wir hier sind. Wir wollen drei Hütten bauen, eine für dich, eine für Mose und eine für Elija." },
      { number: 6, text: "Er wusste nämlich nicht, was er sagen sollte; denn sie waren vor Furcht ganz benommen." },
      { number: 7, text: "Da kam eine Wolke und warf ihren Schatten auf sie, und aus der Wolke rief eine Stimme: Das ist mein geliebter Sohn; auf ihn sollt ihr hören.", footnotes: ["Ps 2,7; Jes 42,1; Dtn 18,15; Mt 3,17"] },
      { number: 8, text: "Als sie dann um sich blickten, sahen sie auf einmal niemand mehr bei sich außer Jesus." },
      { number: 9, text: "Während sie den Berg hinabstiegen, verbot er ihnen, irgendjemand zu erzählen, was sie gesehen hatten, bis der Menschensohn von den Toten auferstanden sei.", footnotes: ["Mt 8,4"] },
      { number: 10, text: "Dieses Wort beschäftigte sie und sie fragten einander, was das sei: von den Toten auferstehen." },
      { number: 11, text: "Da fragten sie ihn: Warum sagen die Schriftgelehrten, zuerst müsse Elija kommen?", footnotes: ["(11-13) Mt 17,10-13"] },
      { number: 12, heading: "Von der Wiederkunft des Elija: 9,11-13", text: "Er antwortete: Ja, Elija kommt zuerst und stellt alles wieder her. Aber warum heißt es dann vom Menschensohn in der Schrift, er werde viel leiden müssen und verachtet werden?", footnotes: ["Mal 3,23 G; Jes 52,13 - 53,12; Ps 22,2-20"] },
      { number: 13, text: "Ich sage euch: Elija ist schon gekommen, doch sie haben mit ihm gemacht, was sie wollten, wie es in der Schrift steht.", footnotes: ["Mt 11,14"] },
      { number: 14, text: "Als sie zu den anderen Jüngern zurückkamen, sahen sie eine große Menschenmenge um sie versammelt und Schriftgelehrte, die mit ihnen stritten.", footnotes: ["(14-29) Mt 17,14-21; Lk 9,37-42", "Einige Textzeugen haben: Als er . . . zurückkam, sah er."] },
      { number: 15, heading: "Die Heilung eines besessenen Jungen: 9,14-29", text: "Sobald die Leute Jesus sahen, liefen sie in großer Erregung auf ihn zu und begrüßten ihn." },
      { number: 16, text: "Er fragte sie: Warum streitet ihr mit ihnen?" },
      { number: 17, text: "Einer aus der Menge antwortete ihm: Meister, ich habe meinen Sohn zu dir gebracht. Er ist von einem stummen Geist besessen;" },
      { number: 18, text: "immer wenn der Geist ihn überfällt, wirft er ihn zu Boden und meinem Sohn tritt Schaum vor den Mund, er knirscht mit den Zähnen und wird starr. Ich habe schon deine Jünger gebeten, den Geist auszutreiben, aber sie hatten nicht die Kraft dazu." },
      { number: 19, text: "Da sagte er zu ihnen: O du ungläubige Generation! Wie lange muss ich noch bei euch sein? Wie lange muss ich euch noch ertragen? Bringt ihn zu mir!" },
      { number: 20, text: "Und man führte ihn herbei. Sobald der Geist Jesus sah, zerrte er den Jungen hin und her, sodass er hinfiel und sich mit Schaum vor dem Mund auf dem Boden wälzte." },
      { number: 21, text: "Jesus fragte den Vater: Wie lange hat er das schon? Der Vater antwortete: Von Kind auf;" },
      { number: 22, text: "oft hat er ihn sogar ins Feuer oder ins Wasser geworfen, um ihn umzubringen. Doch wenn du kannst, hilf uns; hab Mitleid mit uns!" },
      { number: 23, text: "Jesus sagte zu ihm: Wenn du kannst? Alles kann, wer glaubt." },
      { number: 24, text: "Da rief der Vater des Jungen: Ich glaube; hilf meinem Unglauben!" },
      { number: 25, text: "Als Jesus sah, dass die Leute zusammenliefen, drohte er dem unreinen Geist und sagte: Ich befehle dir, du stummer und tauber Geist: Verlass ihn und kehr nicht mehr in ihn zurück!" },
      { number: 26, text: "Da zerrte der Geist den Jungen hin und her und verließ ihn mit lautem Geschrei. Der Junge lag da wie tot, so dass alle Leute sagten: Er ist gestorben." },
      { number: 27, text: "Jesus aber fasste ihn an der Hand und richtete ihn auf, und der Junge erhob sich." },
      { number: 28, text: "Als Jesus nach Hause kam und sie allein waren, fragten ihn seine Jünger: Warum konnten denn wir den Dämon nicht austreiben?" },
      { number: 29, text: "Er antwortete ihnen: Diese Art kann nur durch Gebet ausgetrieben werden.", footnotes: ["Einige Textzeugen fügen nach «Gebet» hinzu: und Fasten."] },
      { number: 30, text: "Sie gingen von dort weg und zogen durch Galiläa. Er wollte aber nicht, dass jemand davon erfuhr;", footnotes: ["Lk 17,11; (30-32) Mt 17,22f; Lk 9,43b-45"] },
      { number: 31, heading: "Die zweite Ankündigung von Leiden und Auferstehung: 9,30-32", text: "denn er wollte seine Jünger über etwas belehren. Er sagte zu ihnen: Der Menschensohn wird den Menschen ausgeliefert und sie werden ihn töten; doch drei Tage nach seinem Tod wird er auferstehen.", footnotes: ["8,31; 10,33"] },
      { number: 32, text: "Aber sie verstanden den Sinn seiner Worte nicht, scheuten sich jedoch, ihn zu fragen." },
      { number: 33, text: "Sie kamen nach Kafarnaum. Als er dann im Haus war, fragte er sie: Worüber habt ihr unterwegs gesprochen?", footnotes: ["(33-37) Mt 18,1-5; Lk 9,46-48"] },
      { number: 34, heading: "Der Rangstreit der Jünger: 9,33-37", text: "Sie schwiegen, denn sie hatten unterwegs miteinander darüber gesprochen, wer (von ihnen) der Größte sei.", footnotes: ["Lk 22,24-26"] },
      { number: 35, text: "Da setzte er sich, rief die Zwölf und sagte zu ihnen: Wer der Erste sein will, soll der Letzte von allen und der Diener aller sein.", footnotes: ["10,43; Mt 20,26f; Lk 22,26"] },
      { number: 36, text: "Und er stellte ein Kind in ihre Mitte, nahm es in seine Arme und sagte zu ihnen:" },
      { number: 37, text: "Wer ein solches Kind um meinetwillen aufnimmt, der nimmt mich auf; wer aber mich aufnimmt, der nimmt nicht nur mich auf, sondern den, der mich gesandt hat.", footnotes: ["um meinetwillen, wörtlich: aufgrund meines Namens."] },
      { number: 38, text: "Da sagte Johannes zu ihm: Meister, wir haben gesehen, wie jemand in deinem Namen Dämonen austrieb; und wir versuchten, ihn daran zu hindern, weil er uns nicht nachfolgt.", footnotes: ["(38-40) Lk 9,49f", "Manche Textzeugen fügen nach «jemand» hinzu: der uns nicht nachfolgt.", ""] },
      { number: 39, heading: "Der fremde Wundertäter: 9,38-41", text: "Jesus erwiderte: Hindert ihn nicht! Keiner, der in meinem Namen Wunder tut, kann so leicht schlecht von mir reden." },
      { number: 40, text: "Denn wer nicht gegen uns ist, der ist für uns." },
      { number: 41, text: "Wer euch auch nur einen Becher Wasser zu trinken gibt, weil ihr zu Christus gehört - amen, ich sage euch: er wird nicht um seinen Lohn kommen.", footnotes: ["Mt 10,42", ""] },
      { number: 42, text: "Wer einen von diesen Kleinen, die an mich glauben, zum Bösen verführt, für den wäre es besser, wenn er mit einem Mühlstein um den Hals ins Meer geworfen würde.", footnotes: ["(42-48) Mt 18,6-9; Lk 17,1f", "42-48: Vgl. die Anmerkung zu Mt 18,6 und 18,8f. «an mich» fehlt bei manchen alten Textzeugen.", ""] },
      { number: 43, heading: "Warnung vor der Verführung: 9,42-48", text: "Wenn dich deine Hand zum Bösen verführt, dann hau sie ab; es ist besser für dich, verstümmelt in das Leben zu gelangen, als mit zwei Händen in die Hölle zu kommen, in das nie erlöschende Feuer.", footnotes: ["Mt 5,30"] },
      { number: 44, text: "[]", footnotes: ["44.46: Spätere Textzeugen fügen entsprechend 9,48 hier ein: wo ihr Wurm nicht stirbt und das Feuer nicht erlischt."] },
      { number: 45, text: "Und wenn dich dein Fuß zum Bösen verführt, dann hau ihn ab; es ist besser für dich, verstümmelt in das Leben zu gelangen, als mit zwei Füßen in die Hölle geworfen zu werden." },
      { number: 46, text: "[]" },
      { number: 47, text: "Und wenn dich dein Auge zum Bösen verführt, dann reiß es aus; es ist besser für dich, einäugig in das Reich Gottes zu kommen, als mit zwei Augen in die Hölle geworfen zu werden,", footnotes: ["Mt 5,29"] },
      { number: 48, text: "wo ihr Wurm nicht stirbt und das Feuer nicht erlischt.", footnotes: ["Jes 66,24"] },
      { number: 49, text: "Denn jeder wird mit Feuer gesalzen werden." },
      { number: 50, heading: "Vom Salz: 9,49-50", text: "Das Salz ist etwas Gutes. Wenn das Salz die Kraft zum Salzen verliert, womit wollt ihr ihm seine Würze wiedergeben? Habt Salz in euch und haltet Frieden untereinander!", footnotes: ["Mt 5,13; Lk 14,34f"] },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Von dort brach Jesus auf und kam nach Judäa und in das Gebiet jenseits des Jordan. Wieder versammelten sich viele Leute bei ihm, und er lehrte sie, wie er es gewohnt war.", footnotes: ["Mt 19,1f; Lk 9,51", ""] },
      { number: 2, heading: "Aufbruch nach Judäa: 10,1", text: "Da kamen Pharisäer zu ihm und fragten: Darf ein Mann seine Frau aus der Ehe entlassen? Damit wollten sie ihm eine Falle stellen.", footnotes: ["(2-9) Mt 19,3-9", "2-12: Vgl. die Anmerkung zu Mt 19,3 und 19,4.", ""] },
      { number: 3, heading: "Von der Ehescheidung: 10,2-12", text: "Er antwortete ihnen: Was hat euch Mose vorgeschrieben?" },
      { number: 4, text: "Sie sagten: Mose hat erlaubt, eine Scheidungsurkunde auszustellen und (die Frau) aus der Ehe zu entlassen.", footnotes: ["Dtn 24,1; Mt 5,31f"] },
      { number: 5, text: "Jesus entgegnete ihnen: Nur weil ihr so hartherzig seid, hat er euch dieses Gebot gegeben." },
      { number: 6, text: "Am Anfang der Schöpfung aber hat Gott sie als Mann und Frau geschaffen.", footnotes: ["Gen 1,27"] },
      { number: 7, text: "Darum wird der Mann Vater und Mutter verlassen,", footnotes: ["Gen 2,24", "Einige alte Textzeugen fügen nach «verlassen» hinzu: und sich an seine Frau binden (vgl. Gen 2,24; Mt 19,5)."] },
      { number: 8, text: "und die zwei werden ein Fleisch sein. Sie sind also nicht mehr zwei, sondern eins." },
      { number: 9, text: "Was aber Gott verbunden hat, das darf der Mensch nicht trennen." },
      { number: 10, text: "Zu Hause befragten ihn die Jünger noch einmal darüber." },
      { number: 11, text: "Er antwortete ihnen: Wer seine Frau aus der Ehe entlässt und eine andere heiratet, begeht ihr gegenüber Ehebruch.", footnotes: ["Mt 5,32; Lk 16,18; (11f) 1 Kor 7,10f"] },
      { number: 12, text: "Auch eine Frau begeht Ehebruch, wenn sie ihren Mann aus der Ehe entlässt und einen anderen heiratet." },
      { number: 13, text: "Da brachte man Kinder zu ihm, damit er ihnen die Hände auflegte. Die Jünger aber wiesen die Leute schroff ab.", footnotes: ["(13-16) Mt 19,13-15; Lk 18,15-17", "Wörtlich: damit er sie berührte.", ""] },
      { number: 14, heading: "Die Segnung der Kinder: 10,13-16", text: "Als Jesus das sah, wurde er unwillig und sagte zu ihnen: Lasst die Kinder zu mir kommen; hindert sie nicht daran! Denn Menschen wie ihnen gehört das Reich Gottes." },
      { number: 15, text: "Amen, das sage ich euch: Wer das Reich Gottes nicht so annimmt, wie ein Kind, der wird nicht hineinkommen.", footnotes: ["Mt 18,3"] },
      { number: 16, text: "Und er nahm die Kinder in seine Arme; dann legte er ihnen die Hände auf und segnete sie." },
      { number: 17, text: "Als sich Jesus wieder auf den Weg machte, lief ein Mann auf ihn zu, fiel vor ihm auf die Knie und fragte ihn: Guter Meister, was muss ich tun, um das ewige Leben zu gewinnen?", footnotes: ["(17-31) Mt 19,16-30; Lk 18,18-30"] },
      { number: 18, heading: "Von Reichtum und Nachfolge: 10,17-31", text: "Jesus antwortete: Warum nennst du mich gut? Niemand ist gut außer Gott, dem Einen.", footnotes: ["Dtn 6,4", "Vgl. die Anmerkung zu 2,7."] },
      { number: 19, text: "Du kennst doch die Gebote: Du sollst nicht töten, du sollst nicht die Ehe brechen, du sollst nicht stehlen, du sollst nicht falsch aussagen, du sollst keinen Raub begehen; ehre deinen Vater und deine Mutter!", footnotes: ["Ex 20,12-16; Dtn 5,16-20"] },
      { number: 20, text: "Er erwiderte ihm: Meister, alle diese Gebote habe ich von Jugend an befolgt." },
      { number: 21, text: "Da sah ihn Jesus an, und weil er ihn liebte, sagte er: Eines fehlt dir noch: Geh, verkaufe, was du hast, gib das Geld den Armen, und du wirst einen bleibenden Schatz im Himmel haben; dann komm und folge mir nach!", footnotes: ["Mt 8,22"] },
      { number: 22, text: "Der Mann aber war betrübt, als er das hörte, und ging traurig weg; denn er hatte ein großes Vermögen." },
      { number: 23, text: "Da sah Jesus seine Jünger an und sagte zu ihnen: Wie schwer ist es für Menschen, die viel besitzen, in das Reich Gottes zu kommen!", footnotes: [""] },
      { number: 24, text: "Die Jünger waren über seine Worte bestürzt. Jesus aber sagte noch einmal zu ihnen: Meine Kinder, wie schwer ist es, in das Reich Gottes zu kommen!" },
      { number: 25, text: "Eher geht ein Kamel durch ein Nadelöhr, als dass ein Reicher in das Reich Gottes gelangt." },
      { number: 26, text: "Sie aber erschraken noch mehr und sagten zueinander: Wer kann dann noch gerettet werden?" },
      { number: 27, text: "Jesus sah sie an und sagte: Für Menschen ist das unmöglich, aber nicht für Gott; denn für Gott ist alles möglich.", footnotes: ["Gen 18,14; Ijob 42,2; Lk 1,37"] },
      { number: 28, text: "Da sagte Petrus zu ihm: Du weißt, wir haben alles verlassen und sind dir nachgefolgt." },
      { number: 29, text: "Jesus antwortete: Amen, ich sage euch: Jeder, der um meinetwillen und um des Evangeliums willen Haus oder Brüder, Schwestern, Mutter, Vater, Kinder oder Äcker verlassen hat," },
      { number: 30, text: "wird das Hundertfache dafür empfangen: Jetzt in dieser Zeit wird er Häuser, Brüder, Schwestern, Mütter, Kinder und Äcker erhalten, wenn auch unter Verfolgungen, und in der kommenden Welt das ewige Leben." },
      { number: 31, text: "Viele aber, die jetzt die Ersten sind, werden dann die Letzten sein, und die Letzten werden die Ersten sein.", footnotes: ["Mt 20,16; Lk 13,30"] },
      { number: 32, text: "Während sie auf dem Weg hinauf nach Jerusalem waren, ging Jesus voraus. Die Leute wunderten sich über ihn, die Jünger aber hatten Angst. Da versammelte er die Zwölf wieder um sich und kündigte ihnen an, was ihm bevorstand.", footnotes: ["(32-34) Mt 20,17-19; Lk 18,31-33"] },
      { number: 33, heading: "Die dritte Ankündigung von Leiden und Auferstehung: 10,32-34", text: "Er sagte: Wir gehen jetzt nach Jerusalem hinauf; dort wird der Menschensohn den Hohenpriestern und den Schriftgelehrten ausgeliefert; sie werden ihn zum Tod verurteilen und den Heiden übergeben;", footnotes: ["(33f) 8,31; 9,31"] },
      { number: 34, text: "sie werden ihn verspotten, anspucken, geißeln und töten. Aber nach drei Tagen wird er auferstehen.", footnotes: ["16,6"] },
      { number: 35, text: "Da traten Jakobus und Johannes, die Söhne des Zebedäus, zu ihm und sagten: Meister, wir möchten, dass du uns eine Bitte erfüllst.", footnotes: ["(35-40) Mt 20,20-23"] },
      { number: 36, heading: "Vom Herrschen und vom Dienen: 10,35-45", text: "Er antwortete: Was soll ich für euch tun?" },
      { number: 37, text: "Sie sagten zu ihm: Lass in deinem Reich einen von uns rechts und den andern links neben dir sitzen.", footnotes: ["in deinem Reich, wörtlich: in deiner Herrlichkeit."] },
      { number: 38, text: "Jesus erwiderte: Ihr wisst nicht, um was ihr bittet. Könnt ihr den Kelch trinken, den ich trinke, oder die Taufe auf euch nehmen, mit der ich getauft werde?", footnotes: ["(38f) 14,36; Lk 12,50", "Zu «Kelch trinken» vgl. die Anmerkung zu Mt 20,22f. «Taufe» ist bildlicher Hinweis auf das Eintauchen in Leiden und Tod (vgl. Ps 69,2f; 2 Sam 22,5; Jes 43,2; Röm 6,3-5)."] },
      { number: 39, text: "Sie antworteten: Wir können es. Da sagte Jesus zu ihnen: Ihr werdet den Kelch trinken, den ich trinke, und die Taufe empfangen, mit der ich getauft werde." },
      { number: 40, text: "Doch den Platz zu meiner Rechten und zu meiner Linken habe nicht ich zu vergeben; dort werden die sitzen, für die diese Plätze bestimmt sind." },
      { number: 41, text: "Als die zehn anderen Jünger das hörten, wurden sie sehr ärgerlich über Jakobus und Johannes.", footnotes: ["(41-45) Mt 20,24-28; Lk 22,24-27"] },
      { number: 42, text: "Da rief Jesus sie zu sich und sagte: Ihr wisst, dass die, die als Herrscher gelten, ihre Völker unterdrücken und die Mächtigen ihre Macht über die Menschen missbrauchen." },
      { number: 43, text: "Bei euch aber soll es nicht so sein, sondern wer bei euch groß sein will, der soll euer Diener sein,", footnotes: ["(43f) 9,35; Mt 23,11"] },
      { number: 44, text: "und wer bei euch der Erste sein will, soll der Sklave aller sein." },
      { number: 45, text: "Denn auch der Menschensohn ist nicht gekommen, um sich dienen zu lassen, sondern um zu dienen und sein Leben hinzugeben als Lösegeld für viele.", footnotes: ["Jes 53,10-12", ""] },
      { number: 46, text: "Sie kamen nach Jericho. Als er mit seinen Jüngern und einer großen Menschenmenge Jericho wieder verließ, saß an der Straße ein blinder Bettler, Bartimäus, der Sohn des Timäus.", footnotes: ["(46-52) Mt 9,27-31; 20,29-34; Lk 18,35-43"] },
      { number: 47, heading: "Die Heilung eines Blinden bei Jericho: 10,46-52", text: "Sobald er hörte, dass es Jesus von Nazaret war, rief er laut: Sohn Davids, Jesus, hab Erbarmen mit mir!" },
      { number: 48, text: "Viele wurden ärgerlich und befahlen ihm zu schweigen. Er aber schrie noch viel lauter: Sohn Davids, hab Erbarmen mit mir!" },
      { number: 49, text: "Jesus blieb stehen und sagte: Ruft ihn her! Sie riefen den Blinden und sagten zu ihm: Hab nur Mut, steh auf, er ruft dich." },
      { number: 50, text: "Da warf er seinen Mantel weg, sprang auf und lief auf Jesus zu." },
      { number: 51, text: "Und Jesus fragte ihn: Was soll ich dir tun? Der Blinde antwortete: Rabbuni, ich möchte wieder sehen können.", footnotes: ["«Rabbuni» («Mein Meister») ist eine ehrfurchtsvollere Anrede als das schlichte «Rabbi»."] },
      { number: 52, text: "Da sagte Jesus zu ihm: Geh! Dein Glaube hat dir geholfen. Im gleichen Augenblick konnte er wieder sehen, und er folgte Jesus auf seinem Weg.", footnotes: ["5,34; Mt 9,22; Lk 7,50; 8,48; 17,19"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Als sie in die Nähe von Jerusalem kamen, nach Betfage und Betanien am Ölberg, schickte er zwei seiner Jünger voraus.", footnotes: ["(1-11) Mt 21,1-10; Lk 19,28-38; Joh 12,12-19"] },
      { number: 2, heading: "Die letzten Tage Jesu in Jerusalem: 11,1 - 13,37 | Der Einzug in Jerusalem: 11,1-11", text: "Er sagte zu ihnen: Geht in das Dorf, das vor euch liegt; gleich wenn ihr hineinkommt, werdet ihr einen jungen Esel angebunden finden, auf dem noch nie ein Mensch gesessen hat. Bindet ihn los, und bringt ihn her!" },
      { number: 3, text: "Und wenn jemand zu euch sagt: Was tut ihr da?, dann antwortet: Der Herr braucht ihn; er lässt ihn bald wieder zurückbringen." },
      { number: 4, text: "Da machten sie sich auf den Weg und fanden außen an einer Tür an der Straße einen jungen Esel angebunden und sie banden ihn los." },
      { number: 5, text: "Einige, die dabeistanden, sagten zu ihnen: Wie kommt ihr dazu, den Esel loszubinden?" },
      { number: 6, text: "Sie gaben ihnen zur Antwort, was Jesus gesagt hatte, und man ließ sie gewähren." },
      { number: 7, text: "Sie brachten den jungen Esel zu Jesus, legten ihre Kleider auf das Tier und er setzte sich darauf." },
      { number: 8, text: "Und viele breiteten ihre Kleider auf der Straße aus; andere rissen auf den Feldern Zweige (von den Büschen) ab und streuten sie auf den Weg." },
      { number: 9, text: "Die Leute, die vor ihm hergingen und die ihm folgten, riefen: Hosanna! / Gesegnet sei er, der kommt im Namen des Herrn!", footnotes: ["Ps 118,25f; Mt 23,39; Lk 13,35", "9f: Zu «Hosanna» vgl. die Anmerkung zu Mt 21,9.15."] },
      { number: 10, text: "Gesegnet sei das Reich unseres Vaters David, / das nun kommt. / Hosanna in der Höhe!" },
      { number: 11, text: "Und er zog nach Jerusalem hinein, in den Tempel; nachdem er sich alles angesehen hatte, ging er spät am Abend mit den Zwölf nach Betanien hinaus.", footnotes: [""] },
      { number: 12, text: "Als sie am nächsten Tag Betanien verließen, hatte er Hunger.", footnotes: ["(12-14) Mt 21,18f", "12-14: Vgl. die Anmerkung zu Mt 21,18-22.", ""] },
      { number: 13, heading: "Die Verfluchung eines Feigenbaums: 11,12-14", text: "Da sah er von weitem einen Feigenbaum mit Blättern und ging hin, um nach Früchten zu suchen. Aber er fand an dem Baum nichts als Blätter; denn es war nicht die Zeit der Feigenernte." },
      { number: 14, text: "Da sagte er zu ihm: In Ewigkeit soll niemand mehr eine Frucht von dir essen. Und seine Jünger hörten es." },
      { number: 15, text: "Dann kamen sie nach Jerusalem. Jesus ging in den Tempel und begann, die Händler und Käufer aus dem Tempel hinauszutreiben; er stieß die Tische der Geldwechsler und die Stände der Taubenhändler um", footnotes: ["(15-19) Mt 21,12f; Lk 19,45-48; Joh 2,13-16", "15-19: Vgl. die Anmerkung zu Mt 21,12.", ""] },
      { number: 16, heading: "Die Tempelreinigung: 11,15-19", text: "und ließ nicht zu, dass jemand irgendetwas durch den Tempelbezirk trug." },
      { number: 17, text: "Er belehrte sie und sagte: Heißt es nicht in der Schrift: Mein Haus soll ein Haus des Gebetes für alle Völker sein? Ihr aber habt daraus eine Räuberhöhle gemacht.", footnotes: ["Jes 56,7; Jer 7,11"] },
      { number: 18, text: "Die Hohenpriester und die Schriftgelehrten hörten davon und suchten nach einer Möglichkeit, ihn umzubringen. Denn sie fürchteten ihn, weil alle Leute von seiner Lehre sehr beeindruckt waren.", footnotes: ["12,12; 14,1f"] },
      { number: 19, text: "Als es Abend wurde, verließ Jesus mit seinen Jüngern die Stadt." },
      { number: 20, text: "Als sie am nächsten Morgen an dem Feigenbaum vorbeikamen, sahen sie, dass er bis zu den Wurzeln verdorrt war.", footnotes: ["(20-25) Mt 21,20-22"] },
      { number: 21, heading: "Vom Glauben: 11,20-25", text: "Da erinnerte sich Petrus und sagte zu Jesus: Rabbi, sieh doch, der Feigenbaum, den du verflucht hast, ist verdorrt." },
      { number: 22, text: "Jesus sagte zu ihnen: Ihr müsst Glauben an Gott haben.", footnotes: [""] },
      { number: 23, text: "Amen, das sage ich euch: Wenn jemand zu diesem Berg sagt: Heb dich empor und stürz dich ins Meer!, und wenn er in seinem Herzen nicht zweifelt, sondern glaubt, dass geschieht, was er sagt, dann wird es geschehen.", footnotes: ["Mt 17,20; Lk 17,6"] },
      { number: 24, text: "Darum sage ich euch: Alles, worum ihr betet und bittet - glaubt nur, dass ihr es schon erhalten habt, dann wird es euch zuteil.", footnotes: ["Mt 7,7"] },
      { number: 25, text: "Und wenn ihr beten wollt und ihr habt einem anderen etwas vorzuwerfen, dann vergebt ihm, damit auch euer Vater im Himmel euch eure Verfehlungen vergibt.", footnotes: ["Mt 6,14f; Lk 6,37"] },
      { number: 26, text: "[]", footnotes: ["Spätere Textzeugen haben im Anschluss an Mt 6,15 hier eingefügt: Wenn ihr aber nicht vergebt, dann wird euch euer Vater im Himmel eure Verfehlungen auch nicht vergeben."] },
      { number: 27, text: "Sie kamen wieder nach Jerusalem. Als er im Tempel umherging, kamen die Hohenpriester, die Schriftgelehrten und die Ältesten zu ihm", footnotes: ["(27-33) Mt 21,23-27; Lk 20,1-8"] },
      { number: 28, heading: "Die Frage nach der Vollmacht Jesu: 11,27-33", text: "und fragten ihn: Mit welchem Recht tust du das alles? Wer hat dir die Vollmacht gegeben, das zu tun?" },
      { number: 29, text: "Jesus sagte zu ihnen: Zuerst will ich euch eine Frage vorlegen. Antwortet mir, dann werde ich euch sagen, mit welchem Recht ich das tue." },
      { number: 30, text: "Stammte die Taufe des Johannes vom Himmel oder von den Menschen? Antwortet mir!" },
      { number: 31, text: "Da überlegten sie und sagten zueinander: Wenn wir antworten: Vom Himmel!, so wird er sagen: Warum habt ihr ihm dann nicht geglaubt?" },
      { number: 32, text: "Sollen wir also antworten: Von den Menschen? Sie fürchteten sich aber vor den Leuten; denn alle glaubten, dass Johannes wirklich ein Prophet war." },
      { number: 33, text: "Darum antworteten sie Jesus: Wir wissen es nicht. Jesus erwiderte: Dann sage auch ich euch nicht, mit welchem Recht ich das alles tue." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Jesus begann zu ihnen (wieder) in Form von Gleichnissen zu reden. (Er sagte:) Ein Mann legte einen Weinberg an, zog ringsherum einen Zaun, hob eine Kelter aus und baute einen Turm. Dann verpachtete er den Weinberg an Winzer und reiste in ein anderes Land.", footnotes: ["Jes 5,1f G; (1-12) Mt 21,33-46; Lk 20,9-19", "1f: Vgl. die Anmerkung zu Mt 21,33-46.", ""] },
      { number: 2, heading: "Das Gleichnis von den bösen Winzern: 12,1-12", text: "Als nun die Zeit dafür gekommen war, schickte er einen Knecht zu den Winzern, um bei ihnen seinen Anteil an den Früchten des Weinbergs holen zu lassen." },
      { number: 3, text: "Sie aber packten und prügelten ihn und jagten ihn mit leeren Händen fort." },
      { number: 4, text: "Darauf schickte er einen anderen Knecht zu ihnen; auch ihn misshandelten und beschimpften sie." },
      { number: 5, text: "Als er einen dritten schickte, brachten sie ihn um. Ähnlich ging es vielen anderen; die einen wurden geprügelt, die andern umgebracht." },
      { number: 6, text: "Schließlich blieb ihm nur noch einer: sein geliebter Sohn. Ihn sandte er als letzten zu ihnen, denn er dachte: Vor meinem Sohn werden sie Achtung haben.", footnotes: ["1,11; 9,7"] },
      { number: 7, text: "Die Winzer aber sagten zueinander: Das ist der Erbe. Auf, wir wollen ihn töten, dann gehört sein Erbgut uns." },
      { number: 8, text: "Und sie packten ihn und brachten ihn um und warfen ihn aus dem Weinberg hinaus." },
      { number: 9, text: "Was wird nun der Besitzer des Weinbergs tun? Er wird kommen und die Winzer töten und den Weinberg anderen geben." },
      { number: 10, text: "Habt ihr nicht das Schriftwort gelesen: Der Stein, den die Bauleute verworfen haben, / er ist zum Eckstein geworden;", footnotes: ["Ps 118,22f G", "10f: Vgl. die Anmerkung zu Mt 21,42."] },
      { number: 11, text: "das hat der Herr vollbracht, / vor unseren Augen geschah dieses Wunder?" },
      { number: 12, text: "Daraufhin hätten sie Jesus gern verhaften lassen; aber sie fürchteten die Menge. Denn sie hatten gemerkt, dass er mit diesem Gleichnis sie meinte. Da ließen sie ihn stehen und gingen weg.", footnotes: ["11,18; 14,1f"] },
      { number: 13, text: "Einige Pharisäer und einige Anhänger des Herodes wurden zu Jesus geschickt, um ihn mit einer Frage in eine Falle zu locken.", footnotes: ["(13-17) Mt 22,15-22; Lk 20,20-26", "13-17: Vgl. die Anmerkung zu Mt 22,15-22.", ""] },
      { number: 14, heading: "Die Frage nach der kaiserlichen Steuer: 12,13-17", text: "Sie kamen zu ihm und sagten: Meister, wir wissen, dass du immer die Wahrheit sagst und dabei auf niemand Rücksicht nimmst; denn du siehst nicht auf die Person, sondern lehrst wirklich den Weg Gottes. Ist es erlaubt, dem Kaiser Steuer zu zahlen, oder nicht? Sollen wir sie zahlen oder nicht zahlen?" },
      { number: 15, text: "Er aber durchschaute ihre Heuchelei und sagte zu ihnen: Warum stellt ihr mir eine Falle? Bringt mir einen Denar, ich will ihn sehen." },
      { number: 16, text: "Man brachte ihm einen. Da fragte er sie: Wessen Bild und Aufschrift ist das? Sie antworteten ihm: Des Kaisers." },
      { number: 17, text: "Da sagte Jesus zu ihnen: So gebt dem Kaiser, was dem Kaiser gehört, und Gott, was Gott gehört! Und sie waren sehr erstaunt über ihn." },
      { number: 18, text: "Von den Sadduzäern, die behaupten, es gebe keine Auferstehung, kamen einige zu Jesus und fragten ihn:", footnotes: ["Apg 23,8; (18-27) Mt 22,23-33; Lk 20,27-38", "18-27: Vgl. die Anmerkung zu Mt 22,23-33.", ""] },
      { number: 19, heading: "Die Frage nach der Auferstehung der Toten: 12,18-27", text: "Meister, Mose hat uns vorgeschrieben: Wenn ein Mann, der einen Bruder hat, stirbt und eine Frau hinterlässt, aber kein Kind, dann soll sein Bruder die Frau heiraten und seinem Bruder Nachkommen verschaffen.", footnotes: ["Dtn 25,5f; Gen 38,8"] },
      { number: 20, text: "Es lebten einmal sieben Brüder. Der erste nahm sich eine Frau, und als er starb, hinterließ er keine Nachkommen." },
      { number: 21, text: "Da nahm sie der zweite; auch er starb, ohne Nachkommen zu hinterlassen, und ebenso der dritte." },
      { number: 22, text: "Keiner der sieben hatte Nachkommen. Als letzte von allen starb die Frau." },
      { number: 23, text: "Wessen Frau wird sie nun bei der Auferstehung sein? Alle sieben haben sie doch zur Frau gehabt." },
      { number: 24, text: "Jesus sagte zu ihnen: Ihr irrt euch, ihr kennt weder die Schrift noch die Macht Gottes." },
      { number: 25, text: "Wenn nämlich die Menschen von den Toten auferstehen, werden sie nicht mehr heiraten, sondern sie werden sein wie die Engel im Himmel." },
      { number: 26, text: "Dass aber die Toten auferstehen, habt ihr das nicht im Buch des Mose gelesen, in der Geschichte vom Dornbusch, in der Gott zu Mose spricht: Ich bin der Gott Abrahams, der Gott Isaaks und der Gott Jakobs?", footnotes: ["Ex 3,6"] },
      { number: 27, text: "Er ist doch nicht ein Gott von Toten, sondern von Lebenden. Ihr irrt euch sehr." },
      { number: 28, text: "Ein Schriftgelehrter hatte ihrem Streit zugehört; und da er bemerkt hatte, wie treffend Jesus ihnen antwortete, ging er zu ihm hin und fragte ihn: Welches Gebot ist das erste von allen?", footnotes: ["(28-34) Mt 22,34-40; Lk 10,25-28", "28-34: Vgl. die Anmerkung zu Mt 22,34-40.", ""] },
      { number: 29, heading: "Die Frage nach dem wichtigsten Gebot: 12,28-34", text: "Jesus antwortete: Das erste ist: Höre, Israel, der Herr, unser Gott, ist der einzige Herr.", footnotes: ["(29f) Dtn 6,4f"] },
      { number: 30, text: "Darum sollst du den Herrn, deinen Gott, lieben mit ganzem Herzen und ganzer Seele, mit all deinen Gedanken und all deiner Kraft." },
      { number: 31, text: "Als zweites kommt hinzu: Du sollst deinen Nächsten lieben wie dich selbst. Kein anderes Gebot ist größer als diese beiden.", footnotes: ["Lev 19,18"] },
      { number: 32, text: "Da sagte der Schriftgelehrte zu ihm: Sehr gut, Meister! Ganz richtig hast du gesagt: Er allein ist der Herr, und es gibt keinen anderen außer ihm,", footnotes: ["Lk 20,39; Dtn 4,35; 6,4f"] },
      { number: 33, text: "und ihn mit ganzem Herzen, ganzem Verstand und ganzer Kraft zu lieben und den Nächsten zu lieben wie sich selbst, ist weit mehr als alle Brandopfer und anderen Opfer.", footnotes: ["1 Sam 15,22"] },
      { number: 34, text: "Jesus sah, dass er mit Verständnis geantwortet hatte, und sagte zu ihm: Du bist nicht fern vom Reich Gottes. Und keiner wagte mehr, Jesus eine Frage zu stellen.", footnotes: ["Mt 22,46; Lk 20,40"] },
      { number: 35, text: "Als Jesus im Tempel lehrte, sagte er: Wie können die Schriftgelehrten behaupten, der Messias sei der Sohn Davids?", footnotes: ["(35-37a) Mt 22,41-45; Lk 20,41-44"] },
      { number: 36, heading: "Die Frage nach dem Messias: 12,35-37a", text: "Denn David hat, vom Heiligen Geist erfüllt, selbst gesagt: Der Herr sprach zu meinem Herrn: Setze dich mir zur Rechten und ich lege dir deine Feinde unter die Füße.", footnotes: ["Ps 110,1"] },
      { number: 37, text: "David selbst also nennt ihn «Herr». Wie kann er dann Davids Sohn sein?" },
      { number: 37, text: "Es war eine große Menschenmenge versammelt und hörte ihm mit Freude zu.", footnotes: ["(37b-40) Mt 23,1.5-7.14; Lk 20,46f"] },
      { number: 38, heading: "Worte gegen die Schriftgelehrten: 12,37b-40", text: "Er lehrte sie und sagte: Nehmt euch in Acht vor den Schriftgelehrten! Sie gehen gern in langen Gewändern umher, lieben es, wenn man sie auf den Straßen und Plätzen grüßt,", footnotes: ["(38-39) Lk 11,43"] },
      { number: 39, text: "und sie wollen in der Synagoge die vordersten Sitze und bei jedem Festmahl die Ehrenplätze haben." },
      { number: 40, text: "Sie bringen die Witwen um ihre Häuser und verrichten in ihrer Scheinheiligkeit lange Gebete. Aber um so härter wird das Urteil sein, das sie erwartet.", footnotes: [""] },
      { number: 41, text: "Als Jesus einmal dem Opferkasten gegenübersaß, sah er zu, wie die Leute Geld in den Kasten warfen. Viele Reiche kamen und gaben viel.", footnotes: ["(41-44) Lk 21,1-4", "Im Vorhof der Frauen, zu dem beide Geschlechter Zutritt hatten, befand sich die Schatzkammer mit dreizehn trompetenförmigen Opferstöcken. Die Opfermünzen wurden von einem Priester überprüft und dann in den Opferstock geworfen.", ""] },
      { number: 42, heading: "Das Opfer der Witwe: 12,41-44", text: "Da kam auch eine arme Witwe und warf zwei kleine Münzen hinein.", footnotes: ["Wörtlich: und warf zwei Leptá hinein, das ist ein Quadrans."] },
      { number: 43, text: "Er rief seine Jünger zu sich und sagte: Amen, ich sage euch: Diese arme Witwe hat mehr in den Opferkasten hineingeworfen als alle andern." },
      { number: 44, text: "Denn sie alle haben nur etwas von ihrem Überfluss hergegeben; diese Frau aber, die kaum das Nötigste zum Leben hat, sie hat alles gegeben, was sie besaß, ihren ganzen Lebensunterhalt." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Die Rede über die Endzeit: 13,1-37 Als Jesus den Tempel verließ, sagte einer von seinen Jüngern zu ihm: Meister, sieh, was für Steine und was für Bauten!", footnotes: ["(1-2) Mt 24,1f; Lk 21,5f", "1-37: Die Rede über die Endzeit ist aus verschiedenen Überlieferungen zusammengewachsen.", ""] },
      { number: 2, heading: "Die Ankündigung der Zerstörung des Tempels: 13,1-2", text: "Jesus sagte zu ihm: Siehst du diese großen Bauten? Kein Stein wird auf dem andern bleiben, alles wird niedergerissen.", footnotes: ["Lk 19,44"] },
      { number: 3, text: "Und als er auf dem Ölberg saß, dem Tempel gegenüber, fragten ihn Petrus, Jakobus, Johannes und Andreas, die mit ihm allein waren:", footnotes: ["(3-13) Mt 24,3-14; Lk 21,7-19"] },
      { number: 4, heading: "Vom Anfang der Not: 13,3-13", text: "Sag uns, wann wird das geschehen, und an welchem Zeichen wird man erkennen, dass das Ende von all dem bevorsteht?" },
      { number: 5, text: "Jesus sagte zu ihnen: Gebt Acht, dass euch niemand irreführt!", footnotes: [""] },
      { number: 6, text: "Viele werden unter meinem Namen auftreten und sagen: Ich bin es! Und sie werden viele irreführen." },
      { number: 7, text: "Wenn ihr dann von Kriegen hört und Nachrichten über Kriege euch beunruhigen, lasst euch nicht erschrecken! Das muss geschehen. Es ist aber noch nicht das Ende.", footnotes: ["Dan 2,28 G"] },
      { number: 8, text: "Denn ein Volk wird sich gegen das andere erheben und ein Reich gegen das andere. Und an vielen Orten wird es Erdbeben und Hungersnöte geben. Doch das ist erst der Anfang der Wehen.", footnotes: ["Jes 19,2"] },
      { number: 9, text: "Ihr aber, macht euch darauf gefasst: Man wird euch um meinetwillen vor die Gerichte bringen, in den Synagogen misshandeln und vor Statthalter und Könige stellen, damit ihr vor ihnen Zeugnis ablegt.", footnotes: ["(9-13) Mt 10,17-22; Joh 16,1-4a", "damit ihr vor ihnen Zeugnis ablegt, andere Übersetzungsmöglichkeit: zum Zeugnis gegen sie.", ""] },
      { number: 10, text: "Vor dem Ende aber muss allen Völkern das Evangelium verkündet werden." },
      { number: 11, text: "Und wenn man euch abführt und vor Gericht stellt, dann macht euch nicht im voraus Sorgen, was ihr sagen sollt; sondern was euch in jener Stunde eingegeben wird, das sagt! Denn nicht ihr werdet dann reden, sondern der Heilige Geist.", footnotes: ["Lk 12,11f"] },
      { number: 12, text: "Brüder werden einander dem Tod ausliefern und Väter ihre Kinder, und die Kinder werden sich gegen ihre Eltern auflehnen und sie in den Tod schicken.", footnotes: ["Mi 7,6"] },
      { number: 13, text: "Und ihr werdet um meines Namens willen von allen gehasst werden; wer aber bis zum Ende standhaft bleibt, der wird gerettet.", footnotes: ["Joh 15,18"] },
      { number: 14, text: "Wenn ihr aber den unheilvollen Gräuel an dem Ort seht, wo er nicht stehen darf - der Leser begreife -, dann sollen die Bewohner von Judäa in die Berge fliehen;", footnotes: ["Dan 9,27; 11,31; 12,11; 1 Makk 1,54; (14-23) Mt 24,15-28; Lk 21,20-24", "Vgl. die Anmerkung zu Mt 24,15.", ""] },
      { number: 15, heading: "Vom Höhepunkt der Not: 13,14-23", text: "wer gerade auf dem Dach ist, soll nicht hinabsteigen und ins Haus gehen, um etwas mitzunehmen;", footnotes: ["Lk 17,31"] },
      { number: 16, text: "wer auf dem Feld ist, soll nicht zurückkehren, um seinen Mantel zu holen." },
      { number: 17, text: "Weh aber den Frauen, die in jenen Tagen schwanger sind oder ein Kind stillen." },
      { number: 18, text: "Betet darum, dass dies alles nicht im Winter eintritt." },
      { number: 19, text: "Denn jene Tage werden eine Not bringen, wie es noch nie eine gegeben hat, seit Gott die Welt erschuf, und wie es auch keine mehr geben wird.", footnotes: ["Dan 12,1; Joël 2,2"] },
      { number: 20, text: "Und wenn der Herr diese Zeit nicht verkürzen würde, dann würde kein Mensch gerettet; aber um seiner Auserwählten willen hat er diese Zeit verkürzt." },
      { number: 21, text: "Wenn dann jemand zu euch sagt: Seht, hier ist der Messias!, oder: Seht, dort ist er!, so glaubt es nicht!", footnotes: ["Lk 17,23"] },
      { number: 22, text: "Denn es wird mancher falsche Messias und mancher falsche Prophet auftreten und sie werden Zeichen und Wunder tun, um, wenn möglich, die Auserwählten irrezuführen.", footnotes: [""] },
      { number: 23, text: "Ihr aber, seht euch vor! Ich habe euch alles vorausgesagt." },
      { number: 24, text: "Aber in jenen Tagen, nach der großen Not, wird sich die Sonne verfinstern und der Mond wird nicht mehr scheinen;", footnotes: ["(24f) Jes 13,10; 34,4 G; Joël 2,10; Hag 2,6.21; 2 Petr 3,10; (24-27) Mt 24,29-31; Lk 21,25-28"] },
      { number: 25, heading: "Vom Kommen des Menschensohnes: 13,24-27", text: "die Sterne werden vom Himmel fallen und die Kräfte des Himmels werden erschüttert werden." },
      { number: 26, text: "Dann wird man den Menschensohn mit großer Macht und Herrlichkeit auf den Wolken kommen sehen.", footnotes: ["Dan 7,13; Mt 26,64; Mk 14,62; Offb 1,7"] },
      { number: 27, text: "Und er wird die Engel aussenden und die von ihm Auserwählten aus allen vier Windrichtungen zusammenführen, vom Ende der Erde bis zum Ende des Himmels.", footnotes: ["Sach 2,10 G"] },
      { number: 28, text: "Lernt etwas aus dem Vergleich mit dem Feigenbaum! Sobald seine Zweige saftig werden und Blätter treiben, wisst ihr, dass der Sommer nahe ist.", footnotes: ["(28-32) Mt 24,32-36; Lk 21,29-33"] },
      { number: 29, heading: "Mahnungen im Hinblick auf das Ende: 13,28-37", text: "Genauso sollt ihr erkennen, wenn ihr (all) das geschehen seht, dass das Ende vor der Tür steht." },
      { number: 30, text: "Amen, ich sage euch: Diese Generation wird nicht vergehen, bis das alles eintrifft.", footnotes: ["9,1; Mt 16,28; Lk 9,27"] },
      { number: 31, text: "Himmel und Erde werden vergehen, aber meine Worte werden nicht vergehen.", footnotes: [""] },
      { number: 32, text: "Doch jenen Tag und jene Stunde kennt niemand, auch nicht die Engel im Himmel, nicht einmal der Sohn, sondern nur der Vater." },
      { number: 33, text: "Seht euch also vor und bleibt wach! Denn ihr wisst nicht, wann die Zeit da ist.", footnotes: ["(33-37) Mt 24,42; 25,13-30; Lk 12,39-46"] },
      { number: 34, text: "Es ist wie mit einem Mann, der sein Haus verließ, um auf Reisen zu gehen: Er übertrug alle Verantwortung seinen Dienern, jedem eine bestimmte Aufgabe; dem Türhüter befahl er, wachsam zu sein." },
      { number: 35, text: "Seid also wachsam! Denn ihr wisst nicht, wann der Hausherr kommt, ob am Abend oder um Mitternacht, ob beim Hahnenschrei oder erst am Morgen." },
      { number: 36, text: "Er soll euch, wenn er plötzlich kommt, nicht schlafend antreffen." },
      { number: 37, text: "Was ich aber euch sage, das sage ich allen: Seid wachsam!" },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Es war zwei Tage vor dem Pascha und dem Fest der Ungesäuerten Brote. Die Hohenpriester und die Schriftgelehrten suchten nach einer Möglichkeit, Jesus mit List in ihre Gewalt zu bringen, um ihn zu töten.", footnotes: ["11,18; 12,12; (1-2) Mt 26,3-5; Lk 22,1f", "Das jüdische Paschafest zum Gedenken an den Auszug aus Ägypten war mit dem Fest der Ungesäuerten Brote (Mazzot) verbunden und umfasste zwei verschiedene Festelemente: die Feier des Paschamahls in der ersten Vollmondnacht nach der Frühjahrs-Tagundnachtgleiche, am 14. Nisan, und das anschließende, 7 Tage dauernde Wallfahrtsfest, das ursprünglich das neue Erntejahr einleitete (vgl. Ex 12; 34,18; Lev 23,5-8; Dtn 16,1-8). Am 14. Nisan wurde alles Sauerteigbrot aus den Häuser entfernt und am Nachmittag dieses Tages wurden im Tempel die Lämmer geschlachtet. Es mussten männliche Tiere sein, einjährig und fehlerlos (Ex 12,5).", ""] },
      { number: 2, heading: "Das Leiden und die Auferstehung Jesu: 14,1 - 16,20 | Der Beschluss des Hohen Rates: 14,1-2", text: "Sie sagten aber: Ja nicht am Fest, damit es im Volk keinen Aufruhr gibt." },
      { number: 3, text: "Als Jesus in Betanien im Haus Simons des Aussätzigen bei Tisch war, kam eine Frau mit einem Alabastergefäß voll echtem, kostbarem Nardenöl, zerbrach es und goss das Öl über sein Haar.", footnotes: ["(3-9) Mt 26,6-13; Lk 7,36-50; Joh 12,1-8"] },
      { number: 4, heading: "Die Salbung in Betanien: 14,3-9", text: "Einige aber wurden unwillig und sagten zueinander: Wozu diese Verschwendung?" },
      { number: 5, text: "Man hätte das Öl um mehr als dreihundert Denare verkaufen und das Geld den Armen geben können. Und sie machten der Frau heftige Vorwürfe." },
      { number: 6, text: "Jesus aber sagte: Hört auf! Warum lasst ihr sie nicht in Ruhe? Sie hat ein gutes Werk an mir getan." },
      { number: 7, text: "Denn die Armen habt ihr immer bei euch und ihr könnt ihnen Gutes tun, so oft ihr wollt; mich aber habt ihr nicht immer." },
      { number: 8, text: "Sie hat getan, was sie konnte. Sie hat im voraus meinen Leib für das Begräbnis gesalbt." },
      { number: 9, text: "Amen, ich sage euch: Überall auf der Welt, wo das Evangelium verkündet wird, wird man sich an sie erinnern und erzählen, was sie getan hat." },
      { number: 10, text: "Judas Iskariot, einer der Zwölf, ging zu den Hohenpriestern. Er wollte Jesus an sie ausliefern.", footnotes: ["(10-11) Mt 26,14-16; Lk 22,3-6"] },
      { number: 11, heading: "Der Verrat durch Judas: 14,10-11", text: "Als sie das hörten, freuten sie sich und versprachen, ihm Geld dafür zu geben. Von da an suchte er nach einer günstigen Gelegenheit, ihn auszuliefern." },
      { number: 12, text: "Am ersten Tag des Festes der Ungesäuerten Brote, an dem man das Paschalamm schlachtete, sagten die Jünger zu Jesus: Wo sollen wir das Paschamahl für dich vorbereiten?", footnotes: ["Ex 12,14-20; (12-16) Mt 26,17-19; Lk 22,7-13"] },
      { number: 13, heading: "Die Vorbereitung des Paschamahls: 14,12-16", text: "Da schickte er zwei seiner Jünger voraus und sagte zu ihnen: Geht in die Stadt; dort wird euch ein Mann begegnen, der einen Wasserkrug trägt. Folgt ihm,", footnotes: ["13f: Normalerweise waren es Frauen, die das Wasser trugen. Insofern handelt es sich hier um ein auffälliges Erkennungszeichen."] },
      { number: 14, text: "bis er in ein Haus hineingeht; dann sagt zu dem Herrn des Hauses: Der Meister lässt dich fragen: Wo ist der Raum, in dem ich mit meinen Jüngern das Paschalamm essen kann?" },
      { number: 15, text: "Und der Hausherr wird euch einen großen Raum im Obergeschoss zeigen, der schon für das Festmahl hergerichtet und mit Polstern ausgestattet ist. Dort bereitet alles für uns vor!" },
      { number: 16, text: "Die Jünger machten sich auf den Weg und kamen in die Stadt. Sie fanden alles so, wie er es ihnen gesagt hatte, und bereiteten das Paschamahl vor." },
      { number: 17, text: "Als es Abend wurde, kam Jesus mit den Zwölf.", footnotes: ["(17-25) Mt 26,20-29; Lk 22,14-23; (17-21) Joh 13,2.21-26"] },
      { number: 18, heading: "Das Mahl: 14,17-25", text: "Während sie nun bei Tisch waren und aßen, sagte er: Amen, ich sage euch: Einer von euch wird mich verraten und ausliefern, einer von denen, die zusammen mit mir essen.", footnotes: ["Ps 41,10"] },
      { number: 19, text: "Da wurden sie traurig und einer nach dem andern fragte ihn: Doch nicht etwa ich?" },
      { number: 20, text: "Er sagte zu ihnen: Einer von euch Zwölf, der mit mir aus derselben Schüssel isst." },
      { number: 21, text: "Der Menschensohn muss zwar seinen Weg gehen, wie die Schrift über ihn sagt. Doch weh dem Menschen, durch den der Menschensohn verraten wird. Für ihn wäre es besser, wenn er nie geboren wäre." },
      { number: 22, text: "Während des Mahls nahm er das Brot und sprach den Lobpreis; dann brach er das Brot, reichte es ihnen und sagte: Nehmt, das ist mein Leib.", footnotes: ["(22-24) 1 Kor 11,23f"] },
      { number: 23, text: "Dann nahm er den Kelch, sprach das Dankgebet, reichte ihn den Jüngern und sie tranken alle daraus." },
      { number: 24, text: "Und er sagte zu ihnen: Das ist mein Blut, das Blut des Bundes, das für viele vergossen wird.", footnotes: ["Ex 24,8; Jes 53,11f; Jer 31,31; Hebr 7,22; 9,15", "Zu «Blut des Bundes» vgl. die Anmerkung zu Mt 26,28."] },
      { number: 25, text: "Amen, ich sage euch: Ich werde nicht mehr von der Frucht des Weinstocks trinken bis zu dem Tag, an dem ich von neuem davon trinke im Reich Gottes." },
      { number: 26, text: "Nach dem Lobgesang gingen sie zum Ölberg hinaus.", footnotes: ["Lk 22,39; Joh 18,1; (26-31) Mt 26,30-35"] },
      { number: 27, heading: "Der Gang zum Ölberg: 14,26-31", text: "Da sagte Jesus zu ihnen: Ihr werdet alle (an mir) Anstoß nehmen und zu Fall kommen; denn in der Schrift steht: Ich werde den Hirten erschlagen, dann werden sich die Schafe zerstreuen.", footnotes: ["Sach 13,7; Joh 16,32"] },
      { number: 28, text: "Aber nach meiner Auferstehung werde ich euch nach Galiläa vorausgehen.", footnotes: [""] },
      { number: 29, text: "Da sagte Petrus zu ihm: Auch wenn alle (an dir) Anstoß nehmen - ich nicht!", footnotes: ["(30-31) 14,72; Lk 22,31-34; Joh 13,36-38"] },
      { number: 30, text: "Jesus antwortete ihm: Amen, ich sage dir: Noch heute Nacht, ehe der Hahn zweimal kräht, wirst du mich dreimal verleugnen." },
      { number: 31, text: "Petrus aber beteuerte: Und wenn ich mit dir sterben müsste - ich werde dich nie verleugnen. Das gleiche sagten auch alle anderen." },
      { number: 32, text: "Sie kamen zu einem Grundstück, das Getsemani heißt, und er sagte zu seinen Jüngern: Setzt euch und wartet hier, während ich bete.", footnotes: ["(32-42) Mt 26,36-46; Lk 22,39-46"] },
      { number: 33, heading: "Das Gebet in Getsemani: 14,32-42", text: "Und er nahm Petrus, Jakobus und Johannes mit sich. Da ergriff ihn Furcht und Angst," },
      { number: 34, text: "und er sagte zu ihnen: Meine Seele ist zu Tode betrübt. Bleibt hier und wacht!", footnotes: ["Ps 42,6.12; 43,5; Joh 12,27"] },
      { number: 35, text: "Und er ging ein Stück weiter, warf sich auf die Erde nieder und betete, dass die Stunde, wenn möglich, an ihm vorübergehe." },
      { number: 36, text: "Er sprach: Abba, Vater, alles ist dir möglich. Nimm diesen Kelch von mir! Aber nicht, was ich will, sondern was du willst (soll geschehen).", footnotes: ["Abba: im Aramäischen die vertrauliche Form, in der man den Familienvater anredet (vgl. Röm 8,15; Gal 4,6)."] },
      { number: 37, text: "Und er ging zurück und fand sie schlafend. Da sagte er zu Petrus: Simon, du schläfst? Konntest du nicht einmal eine Stunde wach bleiben?" },
      { number: 38, text: "Wacht und betet, damit ihr nicht in Versuchung geratet. Der Geist ist willig, aber das Fleisch ist schwach." },
      { number: 39, text: "Und er ging wieder weg und betete mit den gleichen Worten." },
      { number: 40, text: "Als er zurückkam, fand er sie wieder schlafend, denn die Augen waren ihnen zugefallen; und sie wussten nicht, was sie ihm antworten sollten." },
      { number: 41, text: "Und er kam zum dritten Mal und sagte zu ihnen: Schlaft ihr immer noch und ruht euch aus? Es ist genug. Die Stunde ist gekommen; jetzt wird der Menschensohn den Sündern ausgeliefert." },
      { number: 42, text: "Steht auf, wir wollen gehen! Seht, der Verräter, der mich ausliefert, ist da." },
      { number: 43, text: "Noch während er redete, kam Judas, einer der Zwölf, mit einer Schar von Männern, die mit Schwertern und Knüppeln bewaffnet waren; sie waren von den Hohenpriestern, den Schriftgelehrten und den Ältesten geschickt worden.", footnotes: ["(43-50) Mt 26,47-56; Lk 22,47-53; Joh 18,3-12"] },
      { number: 44, heading: "Die Gefangennahme: 14,43-52", text: "Der Verräter hatte mit ihnen ein Zeichen vereinbart und gesagt: Der, den ich küssen werde, der ist es. Nehmt ihn fest, führt ihn ab und lasst ihn nicht entkommen." },
      { number: 45, text: "Und als er kam, ging er sogleich auf Jesus zu und sagte: Rabbi! Und er küsste ihn." },
      { number: 46, text: "Da ergriffen sie ihn und nahmen ihn fest." },
      { number: 47, text: "Einer von denen, die dabeistanden, zog das Schwert, schlug auf den Diener des Hohenpriesters ein und hieb ihm ein Ohr ab." },
      { number: 48, text: "Da sagte Jesus zu ihnen: Wie gegen einen Räuber seid ihr mit Schwertern und Knüppeln ausgezogen, um mich festzunehmen." },
      { number: 49, text: "Tag für Tag war ich bei euch im Tempel und lehrte und ihr habt mich nicht verhaftet; aber (das ist geschehen), damit die Schrift in Erfüllung geht.", footnotes: ["Lk 19,47; Joh 18,20"] },
      { number: 50, text: "Da verließen ihn alle und flohen.", footnotes: ["Joh 16,32"] },
      { number: 51, text: "Ein junger Mann aber, der nur mit einem leinenen Tuch bekleidet war, wollte ihm nachgehen. Da packten sie ihn;" },
      { number: 52, text: "er aber ließ das Tuch fallen und lief nackt davon." },
      { number: 53, text: "Darauf führten sie Jesus zum Hohenpriester und es versammelten sich alle Hohenpriester und Ältesten und Schriftgelehrten.", footnotes: ["(53-65) Mt 26,57-68; Lk 22,54f.66-71; Joh 18,13-24", "53-65: Markus berichtet wie Matthäus von einem nächtlichen Verhör vor dem Hohenpriester und Mitgliedern des Hohen Rates, bei dem der Hohe Rat das Todesurteil fällte. Nach Lk 22,66-71 fand dieses Verhör am Morgen statt, ohne ausdrückliches Todesurteil. Nach Joh 18,12-24 wurde Jesus von dem Hohenpriester Hannas nach seiner Lehre und seinen Anhängern befragt und dann noch dem amtierenden Hohenpriester Kajaphas vorgeführt. Von einem eigentlichen Verhör oder gar einer Verurteilung zum Tod durch die Juden wird bei Johannes nichts erzählt.", ""] },
      { number: 54, heading: "Das Verhör vor dem Hohen Rat: 14,53-65", text: "Petrus aber war Jesus von weitem bis in den Hof des hohepriesterlichen Palastes gefolgt; nun saß er dort bei den Dienern und wärmte sich am Feuer." },
      { number: 55, text: "Die Hohenpriester und der ganze Hohe Rat bemühten sich um Zeugenaussagen gegen Jesus, um ihn zum Tod verurteilen zu können; sie fanden aber nichts." },
      { number: 56, text: "Viele machten zwar falsche Aussagen über ihn, aber die Aussagen stimmten nicht überein." },
      { number: 57, text: "Einige der falschen Zeugen, die gegen ihn auftraten, behaupteten:", footnotes: [""] },
      { number: 58, text: "Wir haben ihn sagen hören: Ich werde diesen von Menschen erbauten Tempel niederreißen und in drei Tagen einen anderen errichten, der nicht von Menschenhand gemacht ist.", footnotes: ["15,29; Joh 2,19; Apg 6,14"] },
      { number: 59, text: "Aber auch in diesem Fall stimmten die Aussagen nicht überein." },
      { number: 60, text: "Da stand der Hohepriester auf, trat in die Mitte und fragte Jesus: Willst du denn nichts sagen zu dem, was diese Leute gegen dich vorbringen?" },
      { number: 61, text: "Er aber schwieg und gab keine Antwort. Da wandte sich der Hohepriester nochmals an ihn und fragte: Bist du der Messias, der Sohn des Hochgelobten?", footnotes: ["8,29; Joh 10,24", "61f: Der Ausdruck «der Hochgelobte» ist ebenso wie «die Macht» Umschreibung für Gott."] },
      { number: 62, text: "Jesus sagte: Ich bin es. Und ihr werdet den Menschensohn zur Rechten der Macht sitzen und mit den Wolken des Himmels kommen sehen.", footnotes: ["Dan 7,13; Ps 110,1; Mt 24,30; Mk 13,26"] },
      { number: 63, text: "Da zerriss der Hohepriester sein Gewand und rief: Wozu brauchen wir noch Zeugen?" },
      { number: 64, text: "Ihr habt die Gotteslästerung gehört. Was ist eure Meinung? Und sie fällten einstimmig das Urteil: Er ist schuldig und muss sterben." },
      { number: 65, text: "Und einige spuckten ihn an, verhüllten sein Gesicht, schlugen ihn und riefen: Zeig, dass du ein Prophet bist! Auch die Diener schlugen ihn ins Gesicht.", footnotes: ["Zeig, dass du ein Prophet bist, wörtlich: Prophezei!"] },
      { number: 66, text: "Als Petrus unten im Hof war, kam eine von den Mägden des Hohenpriesters.", footnotes: ["(66-72) Mt 26,69-75; Lk 22,56-62; Joh 18,17.25-27"] },
      { number: 67, heading: "Die Verleugnung durch Petrus: 14,66-72", text: "Sie sah, wie Petrus sich wärmte, blickte ihn an und sagte: Auch du warst mit diesem Jesus aus Nazaret zusammen." },
      { number: 68, text: "Doch er leugnete es und sagte: Ich weiß nicht und verstehe nicht, wovon du redest. Dann ging er in den Vorhof hinaus.", footnotes: ["Spätere Textzeugen fügen am Ende des Verses hinzu: Da krähte der Hahn."] },
      { number: 69, text: "Als die Magd ihn dort bemerkte, sagte sie zu denen, die dabeistanden, noch einmal: Der gehört zu ihnen." },
      { number: 70, text: "Er aber leugnete es wieder ab. Wenig später sagten die Leute, die dort standen, von neuem zu Petrus: Du gehörst wirklich zu ihnen; du bist doch auch ein Galiläer." },
      { number: 71, text: "Da fing er an zu fluchen und schwor: Ich kenne diesen Menschen nicht, von dem ihr redet." },
      { number: 72, text: "Gleich darauf krähte der Hahn zum zweiten Mal, und Petrus erinnerte sich, dass Jesus zu ihm gesagt hatte: Ehe der Hahn zweimal kräht, wirst du mich dreimal verleugnen. Und er begann zu weinen.", footnotes: ["14,30"] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Gleich in der Frühe fassten die Hohenpriester, die Ältesten und die Schriftgelehrten, also der ganze Hohe Rat, über Jesus einen Beschluss: Sie ließen ihn fesseln und abführen und lieferten ihn Pilatus aus.", footnotes: ["(1-15) Mt 27,1f.11-26; Lk 22,66; 23,1-4.17-25; Joh 18,29 - 19,1", "1-15: Zu Pilatus und Barabbas vgl. die Anmerkung zu Mt 27, 11-26.", ""] },
      { number: 2, heading: "Die Verhandlung vor Pilatus: 15,1-15", text: "Pilatus fragte ihn: Bist du der König der Juden? Er antwortete ihm: Du sagst es.", footnotes: [""] },
      { number: 3, text: "Die Hohenpriester brachten viele Anklagen gegen ihn vor.", footnotes: [""] },
      { number: 4, text: "Da wandte sich Pilatus wieder an ihn und fragte: Willst du denn nichts dazu sagen? Sieh doch, wie viele Anklagen sie gegen dich vorbringen." },
      { number: 5, text: "Jesus aber gab keine Antwort mehr, sodass Pilatus sich wunderte." },
      { number: 6, text: "Jeweils zum Fest ließ Pilatus einen Gefangenen frei, den sie sich ausbitten durften.", footnotes: [""] },
      { number: 7, text: "Damals saß gerade ein Mann namens Barabbas im Gefängnis, zusammen mit anderen Aufrührern, die bei einem Aufstand einen Mord begangen hatten." },
      { number: 8, text: "Die Volksmenge zog (zu Pilatus) hinauf und bat, ihnen die gleiche Gunst zu gewähren wie sonst." },
      { number: 9, text: "Pilatus fragte sie: Wollt ihr, dass ich den König der Juden freilasse?" },
      { number: 10, text: "Er merkte nämlich, dass die Hohenpriester nur aus Neid Jesus an ihn ausgeliefert hatten." },
      { number: 11, text: "Die Hohenpriester aber wiegelten die Menge auf, lieber die Freilassung des Barabbas zu fordern.", footnotes: ["Apg 3,13f"] },
      { number: 12, text: "Pilatus wandte sich von neuem an sie und fragte: Was soll ich dann mit dem tun, den ihr den König der Juden nennt?", footnotes: ["Joh 19,15"] },
      { number: 13, text: "Da schrien sie: Kreuzige ihn!" },
      { number: 14, text: "Pilatus entgegnete: Was hat er denn für ein Verbrechen begangen? Sie schrien noch lauter: Kreuzige ihn!" },
      { number: 15, text: "Darauf ließ Pilatus, um die Menge zufrieden zu stellen, Barabbas frei und gab den Befehl, Jesus zu geißeln und zu kreuzigen.", footnotes: [""] },
      { number: 16, text: "Die Soldaten führten ihn in den Palast hinein, das heißt in das Prätorium, und riefen die ganze Kohorte zusammen.", footnotes: ["(16-20a) Mt 27,27-31a; Joh 19,2f", "16-20a: Zur Verspottung durch die Soldaten vgl. die Anmerkung zu Mt 27,27-31.", ""] },
      { number: 17, heading: "Die Verspottung Jesu durch die Soldaten: 15,16-20a", text: "Dann legten sie ihm einen Purpurmantel um und flochten einen Dornenkranz; den setzten sie ihm auf", footnotes: ["Lk 23,11"] },
      { number: 18, text: "und grüßten ihn: Heil dir, König der Juden!" },
      { number: 19, text: "Sie schlugen ihm mit einem Stock auf den Kopf und spuckten ihn an, knieten vor ihm nieder und huldigten ihm." },
      { number: 20, text: "Nachdem sie so ihren Spott mit ihm getrieben hatten, nahmen sie ihm den Purpurmantel ab und zogen ihm seine eigenen Kleider wieder an.", footnotes: ["20b-32: Zu Einzelheiten der Hinrichtung Jesu vgl. die Anmerkungen zu Mt 27,34-51a."] },
      { number: 20, text: "Dann führten sie Jesus hinaus, um ihn zu kreuzigen.", footnotes: [""] },
      { number: 21, heading: "Die Kreuzigung: 15,20b-32", text: "Einen Mann, der gerade vom Feld kam, Simon von Zyrene, den Vater des Alexander und des Rufus, zwangen sie, sein Kreuz zu tragen.", footnotes: ["(21-32) Mt 27,31b-44; Lk 23,26-38; Joh 19,16b-27", "Die Söhne des Simon von Zyrene werden hier genannt, weil sie wahrscheinlich später der christlichen Gemeinde (in Rom?) angehörten; vgl. Röm 16,13."] },
      { number: 22, text: "Und sie brachten Jesus an einen Ort namens Golgota, das heißt übersetzt: Schädelhöhe.", footnotes: [""] },
      { number: 23, text: "Dort reichten sie ihm Wein, der mit Myrrhe gewürzt war; er aber nahm ihn nicht.", footnotes: ["Ps 69,22"] },
      { number: 24, text: "Dann kreuzigten sie ihn. Sie warfen das Los und verteilten seine Kleider unter sich und gaben jedem, was ihm zufiel.", footnotes: ["Ps 22,19; Joh 19,24"] },
      { number: 25, text: "Es war die dritte Stunde, als sie ihn kreuzigten." },
      { number: 26, text: "Und eine Aufschrift (auf einer Tafel) gab seine Schuld an: Der König der Juden." },
      { number: 27, text: "Zusammen mit ihm kreuzigten sie zwei Räuber, den einen rechts von ihm, den andern links.", footnotes: [""] },
      { number: 28, text: "[]", footnotes: ["Spätere Textzeugen fügen entsprechend Lk 22,37 hier ein: So erfüllte sich das Schriftwort: Er wurde zu den Verbrechern gerechnet."] },
      { number: 29, text: "Die Leute, die vorbeikamen, verhöhnten ihn, schüttelten den Kopf und riefen: Ach, du willst den Tempel niederreißen und in drei Tagen wieder aufbauen?", footnotes: ["Ps 22,8; Mk 14,58; Joh 2,19"] },
      { number: 30, text: "Hilf dir doch selbst und steig herab vom Kreuz!" },
      { number: 31, text: "Auch die Hohenpriester und die Schriftgelehrten verhöhnten ihn und sagten zueinander: Anderen hat er geholfen, sich selbst kann er nicht helfen." },
      { number: 32, text: "Der Messias, der König von Israel! Er soll doch jetzt vom Kreuz herabsteigen, damit wir sehen und glauben. Auch die beiden Männer, die mit ihm zusammen gekreuzigt wurden, beschimpften ihn.", footnotes: [""] },
      { number: 33, text: "Als die sechste Stunde kam, brach über das ganze Land eine Finsternis herein. Sie dauerte bis zur neunten Stunde.", footnotes: ["(33-41) Mt 27,45-56; Lk 23,44-49; Joh 19,28-30"] },
      { number: 34, heading: "Der Tod Jesu: 15,33-41", text: "Und in der neunten Stunde rief Jesus mit lauter Stimme: Eloï, Eloï, lema sabachtani?, das heißt übersetzt: Mein Gott, mein Gott, warum hast du mich verlassen?", footnotes: ["Ps 22,2"] },
      { number: 35, text: "Einige von denen, die dabeistanden und es hörten, sagten: Hört, er ruft nach Elija!" },
      { number: 36, text: "Einer lief hin, tauchte einen Schwamm in Essig, steckte ihn auf einen Stock und gab Jesus zu trinken. Dabei sagte er: Lasst uns doch sehen, ob Elija kommt und ihn herabnimmt.", footnotes: ["Ps 69,22"] },
      { number: 37, text: "Jesus aber schrie laut auf. Dann hauchte er den Geist aus." },
      { number: 38, text: "Da riss der Vorhang im Tempel von oben bis unten entzwei." },
      { number: 39, text: "Als der Hauptmann, der Jesus gegenüberstand, ihn auf diese Weise sterben sah, sagte er: Wahrhaftig, dieser Mensch war Gottes Sohn." },
      { number: 40, text: "Auch einige Frauen sahen von weitem zu, darunter Maria aus Magdala, Maria, die Mutter von Jakobus dem Kleinen und Joses, sowie Salome;", footnotes: ["40.47: Zu «Joses» vgl. die Anmerkung zu Mk 6,3 bzw. zu «Brüder Jesu» Mt 12,46f.", ""] },
      { number: 41, text: "sie waren Jesus schon in Galiläa nachgefolgt und hatten ihm gedient. Noch viele andere Frauen waren dabei, die mit ihm nach Jerusalem hinaufgezogen waren." },
      { number: 42, text: "Da es Rüsttag war, der Tag vor dem Sabbat, und es schon Abend wurde,", footnotes: ["(42-47) Mt 27,57-61; Lk 23,50-56; Joh 19,38-42", "Rüsttag ist der Tag vor dem Sabbat (der Freitag). Nach Dtn 21,23 mussten die am Holz aufgehängten, zuvor gesteinigten Gotteslästerer noch vor Sonnenuntergang abgenommen werden; das galt auch für Gekreuzigte. Die Grablegung fand sofort anschließend statt.", ""] },
      { number: 43, heading: "Das Begräbnis Jesu: 15,42-47", text: "ging Josef von Arimathäa, ein vornehmer Ratsherr, der auch auf das Reich Gottes wartete, zu Pilatus und wagte es, um den Leichnam Jesu zu bitten.", footnotes: ["43-45: Die Freigabe des Leichnams für die private Bestattung war nach römischem Recht dem zuständigen Richter vorbehalten."] },
      { number: 44, text: "Pilatus war überrascht, als er hörte, dass Jesus schon tot sei. Er ließ den Hauptmann kommen und fragte ihn, ob Jesus bereits gestorben sei." },
      { number: 45, text: "Als der Hauptmann ihm das bestätigte, überließ er Josef den Leichnam." },
      { number: 46, text: "Josef kaufte ein Leinentuch, nahm Jesus vom Kreuz, wickelte ihn in das Tuch und legte ihn in ein Grab, das in einen Felsen gehauen war. Dann wälzte er einen Stein vor den Eingang des Grabes." },
      { number: 47, text: "Maria aus Magdala aber und Maria, die Mutter des Joses, beobachteten, wohin der Leichnam gelegt wurde.", footnotes: ["der Leichnam, wörtlich: er."] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Als der Sabbat vorüber war, kauften Maria aus Magdala, Maria, die Mutter des Jakobus, und Salome wohlriechende Öle, um damit zum Grab zu gehen und Jesus zu salben.", footnotes: ["(1-8) Mt 28,1-8; Lk 24,1-12; Joh 20,1-13", "Vgl. die Anmerkung zu Mt 12,46f. 1-8: Mit einem Hinweis auf Erscheinungen des Auferstandenen in Galiläa, den die Frauen, die das leere Grab entdeckten, erhalten, und einem Vermerk über ihre Furcht und ihr Schweigen endet das Markus-Evangelium; vgl. die Einleitung.", ""] },
      { number: 2, heading: "Die Botschaft des Engels im leeren Grab: 16,1-8", text: "Am ersten Tag der Woche kamen sie in aller Frühe zum Grab, als eben die Sonne aufging." },
      { number: 3, text: "Sie sagten zueinander: Wer könnte uns den Stein vom Eingang des Grabes wegwälzen?" },
      { number: 4, text: "Doch als sie hinblickten, sahen sie, dass der Stein schon weggewälzt war; er war sehr groß." },
      { number: 5, text: "Sie gingen in das Grab hinein und sahen auf der rechten Seite einen jungen Mann sitzen, der mit einem weißen Gewand bekleidet war; da erschraken sie sehr." },
      { number: 6, text: "Er aber sagte zu ihnen: Erschreckt nicht! Ihr sucht Jesus von Nazaret, den Gekreuzigten. Er ist auferstanden; er ist nicht hier. Seht, da ist die Stelle, wo man ihn hingelegt hatte.", footnotes: ["10,34"] },
      { number: 7, text: "Nun aber geht und sagt seinen Jüngern, vor allem Petrus: Er geht euch voraus nach Galiläa; dort werdet ihr ihn sehen, wie er es euch gesagt hat.", footnotes: [""] },
      { number: 8, text: "Da verließen sie das Grab und flohen; denn Schrecken und Entsetzen hatte sie gepackt. Und sie sagten niemand etwas davon; denn sie fürchteten sich." },
      { number: 9, text: "Als Jesus am frühen Morgen des ersten Wochentages auferstanden war, erschien er zuerst Maria aus Magdala, aus der er sieben Dämonen ausgetrieben hatte.", footnotes: ["Lk 8,2; (9-20) Mt 28,9f.16-20; Lk 24,13-51; Joh 20,11-23", "9-20: Dieser Abschnitt findet sich nicht bei den ältesten Textzeugen. Er ist eine im 2. Jahrhundert entstandene Zusammenfassung der in anderen Evangelien stehenden Berichte über die Erscheinungen und Anweisungen des Auferstandenen; er gehört also inhaltlich zur Evangelienüberlieferung. In einer altlateinischen Handschrift findet sich stattdessen folgende Ergänzung: Und sie berichteten alles, was ihnen aufgetragen worden war, dem Kreis um Petrus. Danach sandte Jesus selbst durch sie vom Osten bis in den Westen die heilige und unvergängliche Botschaft vom ewigen Heil. Amen. Bei einigen Textzeugen finden sich auch beide Zusätze.", ""] },
      { number: 10, heading: "[Die Erscheinungen des Auferstandenen: 16,9-20", text: "Sie ging und berichtete es denen, die mit ihm zusammen gewesen waren und die nun klagten und weinten." },
      { number: 11, text: "Als sie hörten, er lebe und sei von ihr gesehen worden, glaubten sie es nicht." },
      { number: 12, text: "Darauf erschien er in einer anderen Gestalt zweien von ihnen, als sie unterwegs waren und aufs Land gehen wollten." },
      { number: 13, text: "Auch sie gingen und berichteten es den anderen und auch ihnen glaubte man nicht." },
      { number: 14, text: "Später erschien Jesus auch den Elf, als sie bei Tisch waren; er tadelte ihren Unglauben und ihre Verstocktheit, weil sie denen nicht glaubten, die ihn nach seiner Auferstehung gesehen hatten." },
      { number: 15, text: "Dann sagte er zu ihnen: Geht hinaus in die ganze Welt, und verkündet das Evangelium allen Geschöpfen!" },
      { number: 16, text: "Wer glaubt und sich taufen lässt, wird gerettet; wer aber nicht glaubt, wird verdammt werden." },
      { number: 17, text: "Und durch die, die zum Glauben gekommen sind, werden folgende Zeichen geschehen: In meinem Namen werden sie Dämonen austreiben; sie werden in neuen Sprachen reden;" },
      { number: 18, text: "wenn sie Schlangen anfassen oder tödliches Gift trinken, wird es ihnen nicht schaden; und die Kranken, denen sie die Hände auflegen, werden gesund werden." },
      { number: 19, text: "Nachdem Jesus, der Herr, dies zu ihnen gesagt hatte, wurde er in den Himmel aufgenommen und setzte sich zur Rechten Gottes.", footnotes: ["Apg 1,9-11; 2 Kön 2,3.11; Ps 110,1"] },
      { number: 20, text: "Sie aber zogen aus und predigten überall. Der Herr stand ihnen bei und bekräftigte die Verkündigung durch die Zeichen, die er geschehen ließ.]" },
    ] },
  ],
};
