import { Book } from "@/lib/types";

export const revelation: Book = {
  id: "revelation", name: "Offenbarung", shortName: "Offb", testament: "new",
  introduction: `Das letzte Buch des Neuen Testaments heißt »Offenbarung« oder »Apokalypse« (griechisch: apokálypsis, Enthüllung). Der Titel stammt vom Verfasser selbst (1,1). Apokalypsen gab es schon in alttestamentlicher Zeit. Das bekannteste Beispiel ist das Buch Daniel (Kap. 7 - 12), das zur Zeit der Religionsverfolgung durch den Syrerkönig Antiochus IV. Epiphanes (167-164 v. Chr.) geschrieben wurde, um die Israeliten zum treuen Festhalten am Bundesgott und seinem Gesetz aufzurufen. Eine ähnliche Situation führte zur Abfassung der neutestamentlichen Apokalypse, die gegen Ende der Regierungszeit des römischen Kaisers Domitian (81-96 n. Chr.) geschrieben wurde. Der Verfasser rechnet damit, dass der Zwang zur göttlichen Verehrung des Kaisers in nächster Zukunft zu einer schweren Verfolgung der Kirche führen wird. Domitian verlangte, als »Herr und Gott« angeredet zu werden. In der römischen Provinz Asien wurde der Kaiserkult schon seit langem mit besonderem Eifer betrieben. Sieben Gemeinden dieser Provinz, die zugleich die ganze Kirche repräsentieren, sind denn auch die unmittelbaren Adressaten des Buches (1,4.11). Der uns nicht näher bekannte Verfasser nennt sich schlicht »Knecht Johannes«; er muss aber eine Persönlichkeit von hoher Autorität gewesen sein. Seine gründliche Kenntnis des Alten Testaments und der außerbiblischen jüdischen Offenbarungsschriften (Apokalypsen) weisen auf judenchristliche Herkunft hin. Er ist der Erste, der eine selbstständige christliche Apokalypse verfasst hat. Den Auftrag dazu hat er nach seinen eigenen Worten in einer Vision (Schau) vom himmlischen Christus erhalten (1,10-20). Auf die einleitende Vision folgen zuerst die sog. Sendschreiben, in denen sich der himmlische Christus anerkennend, tadelnd und mahnend an die sieben Gemeinden wendet (Kap. 2 - 3). Der ungleich längere Hauptteil (4,1 - 22,15) beginnt mit der großen Vision vom thronenden Gott im Kreis des himmlischen Hofstaates. Die Offenbarung geschieht, wie in vorchristlichen apokalyptischen Schriften, auch in der christlichen Apokalypse durch »Visionen« und »Auditionen« (Schauen und Hören von himmlischen Botschaften). Wie seine Vorgänger bedient sich auch Johannes nicht nur der gewöhnlichen, ohne weiteres verständlichen Sprache, sondern er verwendet vor allem Bilder, Symbole und allegorische Szenen. Und wie dort ist auch hier das Hauptthema der bevorstehende Triumph der Herrschaft Gottes. Trotz dieser Gemeinsamkeiten ist die Apokalypse des Johannes aber eine echt christliche Schrift mit alttestamentlichjüdischen Zeugnissen. Der endgültige Sieg Gottes, der für die vorchristlichen »Apokalyptiker« noch reine Zukunft ist, hat mit der Auferstehung Jesu bereits begonnen. Ziel der Geschichte kann demnach nur noch die Vollendung und volle Offenbarung dieses Sieges sein. Der prophetische Verfasser will im Blick auf die beginnende Auseinandersetzung zwischen Christuskult und Kaiserkult die noch ausstehenden Ereignisse der Endzeit mit den Ausdrucksmitteln einer Apokalypse veranschaulichen. Die Offenbarung des Johannes will nicht den Gang der Welt- und Kirchengeschichte voraussagen. Ebenso wenig ist es ihre Absicht, das mit der baldigen Wiederkunft Christi verbundene Geschehen in seinem Ablauf genau zu beschreiben. Mit ihren zahlreichen, dem heutigen Leser weithin fremden Bildern will sie vielmehr bedeutsame Wahrheiten über das Schicksal der Kirche und der ungläubigen Menschheit verkünden, um in den Christen die Bereitschaft zum Martyrium zu stärken. Als Zeugnis des unerschütterlichen Glaubens an den Sieg Christi und seiner Getreuen ist diese einzige prophetische Schrift des Neuen Testaments das große Trost- und Mahnbuch der Kirche geworden.`,
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "Offenbarung Jesu Christi, die Gott ihm gegeben hat, damit er seinen Knechten zeigt, was bald geschehen muss; und er hat es durch seinen Engel, den er sandte, seinem Knecht Johannes gezeigt.", footnotes: ["Dan 2,28f"] },
      { number: 2, heading: "Vorwort: 1,1-3", text: "Dieser hat das Wort Gottes und das Zeugnis Jesu Christi bezeugt: alles, was er geschaut hat." },
      { number: 3, text: "Selig, wer diese prophetischen Worte vorliest und wer sie hört und wer sich an das hält, was geschrieben ist; denn die Zeit ist nahe.", footnotes: ["14,13; 16,15; 19,9; 20,6; 22,7.14"] },
      { number: 4, text: "Johannes an die sieben Gemeinden in der Provinz Asien: Gnade sei mit euch und Friede von Ihm, der ist und der war und der kommt, und von den sieben Geistern vor seinem Thron", footnotes: ["1 Kor 16,19; Ex 3,14 G", "«Sieben» ist die symbolische Zahl der Ganzheit und Vollkommenheit. «Die sieben Gemeinden in der Provinz Asien» vertreten also die Gesamtkirche."] },
      { number: 5, heading: "Briefliche Einleitung: 1,4-8", text: "und von Jesus Christus; er ist der treue Zeuge, der Erstgeborene der Toten, der Herrscher über die Könige der Erde. Er liebt uns und hat uns von unseren Sünden erlöst durch sein Blut;", footnotes: ["Ps 89,38.28; 130,8"] },
      { number: 6, text: "er hat uns zu Königen gemacht und zu Priestern vor Gott, seinem Vater. Ihm sei die Herrlichkeit und die Macht in alle Ewigkeit. Amen.", footnotes: ["5,10; Ex 19,6; Jes 61,6"] },
      { number: 7, text: "Siehe, er kommt mit den Wolken, und jedes Auge wird ihn sehen, auch alle, die ihn durchbohrt haben; und alle Völker der Erde werden seinetwegen jammern und klagen. Ja, amen.", footnotes: ["Dan 7,13; Sach 12,10"] },
      { number: 8, text: "Ich bin das Alpha und das Omega, spricht Gott, der Herr, der ist und der war und der kommt, der Herrscher über die ganze Schöpfung.", footnotes: ["Ex 3,14 G", "Alpha ist der erste, Omega der letzte Buchstabe des griechischen Alphabets: Gott umfasst alle Bereiche der Wirklichkeit."] },
      { number: 9, text: "Ich, euer Bruder Johannes, der wie ihr bedrängt ist, der mit euch an der Königsherrschaft teilhat und mit euch in Jesus standhaft ausharrt, ich war auf der Insel Patmos um des Wortes Gottes willen und des Zeugnisses für Jesus." },
      { number: 10, heading: "Die Sendschreiben an die sieben Gemeinden: 1,9 - 3,22 | Die Beauftragung des Johannes: 1,9-20", text: "Am Tag des Herrn wurde ich vom Geist ergriffen und hörte hinter mir eine Stimme, laut wie eine Posaune.", footnotes: ["Apg 20,7; 1 Kor 16,2", "10-20: Die Vision von der Beauftragung des Johannes ist das erste Beispiel apokalyptischer Bildsprache in der Offenbarung."] },
      { number: 11, text: "Sie sprach: Schreib das, was du siehst, in ein Buch und schick es an die sieben Gemeinden: nach Ephesus, nach Smyrna, nach Pergamon, nach Thyatira, nach Sardes, nach Philadelphia und nach Laodizea.", footnotes: ["1 Kor 16,19; Offb 2,1-7.8-11.12-17.18-29; 3,1-6.7-13.14-22"] },
      { number: 12, text: "Da wandte ich mich um, weil ich sehen wollte, wer zu mir sprach. Als ich mich umwandte, sah ich sieben goldene Leuchter" },
      { number: 13, text: "und mitten unter den Leuchtern einen, der wie ein Mensch aussah; er war bekleidet mit einem Gewand, das bis auf die Füße reichte, und um die Brust trug er einen Gürtel aus Gold.", footnotes: ["Dan 7,13; Ez 9,2.11 G; Dan 10,5", "ein Mensch, wörtlich: ein Menschensohn."] },
      { number: 14, text: "Sein Haupt und seine Haare waren weiß wie weiße Wolle, leuchtend weiß wie Schnee, und seine Augen wie Feuerflammen;", footnotes: ["Dan 7,9; 10,6"] },
      { number: 15, text: "seine Beine glänzten wie Golderz, das im Schmelzofen glüht, und seine Stimme war wie das Rauschen von Wassermassen.", footnotes: ["Ez 1,24; 43,2"] },
      { number: 16, text: "In seiner Rechten hielt er sieben Sterne und aus seinem Mund kam ein scharfes, zweischneidiges Schwert und sein Gesicht leuchtete wie die machtvoll strahlende Sonne.", footnotes: ["2,1; Ri 5,31"] },
      { number: 17, text: "Als ich ihn sah, fiel ich wie tot vor seinen Füßen nieder. Er aber legte seine rechte Hand auf mich und sagte: Fürchte dich nicht! Ich bin der Erste und der Letzte", footnotes: ["Jes 44,2.6; Offb 2,8; 22,13"] },
      { number: 18, text: "und der Lebendige. Ich war tot, doch nun lebe ich in alle Ewigkeit, und ich habe die Schlüssel zum Tod und zur Unterwelt.", footnotes: ["Hos 13,14"] },
      { number: 19, text: "Schreib auf, was du gesehen hast: was ist und was danach geschehen wird.", footnotes: ["Dan 2,29"] },
      { number: 20, text: "Der geheimnisvolle Sinn der sieben Sterne, die du auf meiner rechten Hand gesehen hast, und der sieben goldenen Leuchter ist: Die sieben Sterne sind die Engel der sieben Gemeinden und die sieben Leuchter sind die sieben Gemeinden.", footnotes: ["Zu «Engel der sieben Gemeinden» vgl. die Anmerkung zu 2,1."] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "An den Engel der Gemeinde in Ephesus schreibe: So spricht Er, der die sieben Sterne in seiner Rechten hält und mitten unter den sieben goldenen Leuchtern einhergeht:", footnotes: ["1,16", "Die Engel der einzelnen Gemeinden (vgl. auch 2,8.12.18; 3,1.7.14) sind deren Schutzengel und Wächter. Der Engel und die Gemeinde werden zusammen mit «du» angesprochen, weil der Engel das Tun und Lassen der Gemeinde vor Gott mitverantworten muss."] },
      { number: 2, heading: "An die Gemeinde in Ephesus: 2,1-7", text: "Ich kenne deine Werke und deine Mühe und dein Ausharren; ich weiß: Du kannst die Bösen nicht ertragen, du hast die auf die Probe gestellt, die sich Apostel nennen und es nicht sind, und hast sie als Lügner erkannt.", footnotes: ["2 Kor 11,13"] },
      { number: 3, text: "Du hast ausgeharrt und um meines Namens willen Schweres ertragen und bist nicht müde geworden." },
      { number: 4, text: "Ich werfe dir aber vor, dass du deine erste Liebe verlassen hast.", footnotes: ["1 Tim 5,12"] },
      { number: 5, text: "Bedenke, aus welcher Höhe du gefallen bist. Kehr zurück zu deinen ersten Werken! Wenn du nicht umkehrst, werde ich kommen und deinen Leuchter von seiner Stelle wegrücken." },
      { number: 6, text: "Doch für dich spricht: Du verabscheust das Treiben der Nikolaiten, das auch ich verabscheue.", footnotes: ["Die Nikolaiten waren Irrlehrer, die wahrscheinlich behaupteten, Kompromisse mit der Denk- und Lebensweise der heidnischen Umwelt, besonders die Teilnahme am Kaiserkult, seien rein äußerliche Dinge, die das Wesentliche des Christseins nicht berühren."] },
      { number: 7, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt: Wer siegt, dem werde ich zu essen geben vom Baum des Lebens, der im Paradies Gottes steht.", footnotes: ["2,11.17.29; 3,6.13.22; Mt 11,15; Gen 2,9; Ez 31,8; Offb 22,2.14.19"] },
      { number: 8, text: "An den Engel der Gemeinde in Smyrna schreibe: So spricht Er, der Erste und der Letzte, der tot war und wieder lebendig wurde:", footnotes: ["Jes 44,6; Offb 1,17"] },
      { number: 9, heading: "An die Gemeinde in Smyrna: 2,8-11", text: "Ich kenne deine Bedrängnis und deine Armut; und doch bist du reich. Und ich weiß, dass du von solchen geschmäht wirst, die sich als Juden ausgeben; sie sind es aber nicht, sondern sind eine Synagoge des Satans.", footnotes: ["3,9", "Johannes betont stärker als Paulus, dass nur die christliche Gemeinde «das Israel Gottes» (Gal 6,16) ist. Die Juden, die Jesus als den Messias ablehnen und die Christen anfeinden, sind nicht mehr «Gemeinde Gottes» (Num 16,3 u. ö.), sondern «Synagoge» (= Gemeinde) des Satans."] },
      { number: 10, text: "Fürchte dich nicht vor dem, was du noch erleiden musst. Der Teufel wird einige von euch ins Gefängnis werfen, um euch auf die Probe zu stellen, und ihr werdet in Bedrängnis sein, zehn Tage lang. Sei treu bis in den Tod; dann werde ich dir den Kranz des Lebens geben.", footnotes: ["Dan 1,12.14; Jak 1,12"] },
      { number: 11, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt: Wer siegt, dem kann der zweite Tod nichts anhaben.", footnotes: ["2,7"] },
      { number: 12, text: "An den Engel der Gemeinde in Pergamon schreibe: So spricht Er, der das scharfe, zweischneidige Schwert trägt:" },
      { number: 13, heading: "An die Gemeinde in Pergamon: 2,12-17", text: "Ich weiß, wo du wohnst; es ist dort, wo der Thron des Satans steht. Und doch hältst du an meinem Namen fest und hast den Glauben an mich nicht verleugnet, auch nicht in den Tagen, als Antipas, mein treuer Zeuge, bei euch getötet wurde, dort, wo der Satan wohnt." },
      { number: 14, text: "Aber ich habe etwas gegen dich: Bei dir gibt es Leute, die an der Lehre Bileams festhalten; Bileam lehrte Balak, er solle die Israeliten dazu verführen, Fleisch zu essen, das den Götzen geweiht war, und Unzucht zu treiben.", footnotes: ["Num 31,16; 25,1f G", "Der heidnische Seher Bileam (Num 25,1-6; 31,16) galt im damaligen Judentum als erstes Beispiel eines Irrlehrers. - Lehre Bileams: gemeint ist die in den folgenden Versen genannte Lehre der Nikolaiten (vgl. die Anmerkung zu 2,6)."] },
      { number: 15, text: "So gibt es auch bei dir Leute, die in gleicher Weise an der Lehre der Nikolaiten festhalten." },
      { number: 16, text: "Kehr nun um! Sonst komme ich bald und werde sie mit dem Schwert aus meinem Mund bekämpfen." },
      { number: 17, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt: Wer siegt, dem werde ich von dem verborgenen Manna geben. Ich werde ihm einen weißen Stein geben und auf dem Stein steht ein neuer Name, den nur der kennt, der ihn empfängt.", footnotes: ["2,7; Ps 78,24; Jes 62,2"] },
      { number: 18, text: "An den Engel der Gemeinde in Thyatira schreibe: So spricht der Sohn Gottes, der Augen hat wie Feuerflammen und Beine wie Golderz:", footnotes: ["Dan 10,5f"] },
      { number: 19, heading: "An die Gemeinde in Thyatira: 2,18-29", text: "Ich kenne deine Werke, deine Liebe und deinen Glauben, dein Dienen und Ausharren und ich weiß, dass du in letzter Zeit mehr getan hast als am Anfang." },
      { number: 20, text: "Aber ich werfe dir vor, dass du das Weib Isebel gewähren lässt; sie gibt sich als Prophetin aus und lehrt meine Knechte und verführt sie, Unzucht zu treiben und Fleisch zu essen, das den Götzen geweiht ist.", footnotes: ["Num 31,16; 25,1f G", "Isebel, die heidnische Frau des israelitischen Königs Ahab, verführte ihren Mann und Teile des Volkes zur Verehrung des Götzen Baal (1 Kön 16,29-34; 18,13.19). Hier bezeichnet «Isebel» eine Prophetin, die in der Gemeinde von Thyatira die Irrlehre der Nikolaiten oder ähnliche Ideen verbreitete."] },
      { number: 21, text: "Ich habe ihr Zeit gelassen umzukehren; sie aber will nicht umkehren und von ihrer Unzucht ablassen." },
      { number: 22, text: "Darum werfe ich sie auf das Krankenbett und alle, die mit ihr Ehebruch treiben, bringe ich in große Bedrängnis, wenn sie sich nicht abkehren vom Treiben dieses Weibes." },
      { number: 23, text: "Ihre Kinder werde ich töten, der Tod wird sie treffen und alle Gemeinden werden erkennen, dass ich es bin, der Herz und Nieren prüft, und ich werde jedem von euch vergelten, wie es seine Taten verdienen.", footnotes: ["Ps 7,10; Jer 11,20; Ps 62,13; Spr 24,12"] },
      { number: 24, text: "Aber euch Übrigen in Thyatira, denen, die dieser Lehre nicht folgen und die «Tiefen des Satans», wie sie es nennen, nicht erkannt haben, euch sage ich: Ich lege euch keine andere Last auf.", footnotes: ["Tiefen des Satans: wahrscheinlich vertraten die Irrlehrer die Auffassung, man müsse sich auf das Geheimnis des Bösen einlassen, um es entlarven und entmachten zu können."] },
      { number: 25, text: "Aber was ihr habt, das haltet fest, bis ich komme." },
      { number: 26, text: "Wer siegt und bis zum Ende an den Werken festhält, die ich gebiete, dem werde ich Macht über die Völker geben.", footnotes: ["(26-27) Ps 2,8f G"] },
      { number: 27, text: "Er wird über sie herrschen mit eisernem Zepter und sie zerschlagen wie Tongeschirr;" },
      { number: 28, text: "(und ich werde ihm diese Macht geben,) wie auch ich sie von meinem Vater empfangen habe, und ich werde ihm den Morgenstern geben." },
      { number: 29, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt.", footnotes: ["2,7"] },
    ] },
    { number: 3, verses: [
      { number: 1, text: "An den Engel der Gemeinde in Sardes schreibe: So spricht Er, der die sieben Geister Gottes und die sieben Sterne hat: Ich kenne deine Werke. Dem Namen nach lebst du, aber du bist tot." },
      { number: 2, heading: "An die Gemeinde in Sardes: 3,1-6", text: "Werde wach und stärke, was noch übrig ist, was schon im Sterben lag. Ich habe gefunden, dass deine Taten in den Augen meines Gottes nicht vollwertig sind." },
      { number: 3, text: "Denk also daran, wie du die Lehre empfangen und gehört hast. Halte daran fest und kehr um! Wenn du aber nicht aufwachst, werde ich kommen wie ein Dieb und du wirst bestimmt nicht wissen, zu welcher Stunde ich komme." },
      { number: 4, text: "Du hast aber einige Leute in Sardes, die ihre Kleider nicht befleckt haben; sie werden mit mir in weißen Gewändern gehen, denn sie sind es wert." },
      { number: 5, text: "Wer siegt, wird ebenso mit weißen Gewändern bekleidet werden. Nie werde ich seinen Namen aus dem Buch des Lebens streichen, sondern ich werde mich vor meinem Vater und vor seinen Engeln zu ihm bekennen.", footnotes: ["Ex 32,32f; Ps 69,29; Dan 12,1; Offb 13,8; 17,8; 20,12.15; 21,27"] },
      { number: 6, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt.", footnotes: ["2,7"] },
      { number: 7, text: "An den Engel der Gemeinde in Philadelphia schreibe: So spricht der Heilige, der Wahrhaftige, der den Schlüssel Davids hat, / der öffnet, sodass niemand mehr schließen kann, / der schließt, sodass niemand mehr öffnen kann:", footnotes: ["Jes 22,22"] },
      { number: 8, heading: "An die Gemeinde in Philadelphia: 3,7-13", text: "Ich kenne deine Werke, und ich habe vor dir eine Tür geöffnet, die niemand mehr schließen kann. Du hast nur geringe Kraft, und dennoch hast du an meinem Wort festgehalten und meinen Namen nicht verleugnet." },
      { number: 9, text: "Leute aus der Synagoge des Satans, die sich als Juden ausgeben, es aber nicht sind, sondern Lügner - ich werde bewirken, dass sie kommen und sich dir zu Füßen werfen und erkennen, dass ich dir meine Liebe zugewandt habe.", footnotes: ["2,9; Jes 60,14; 43,4", "Wahrscheinlich ist gemeint, die Gegner aus dem Judentum werden bei der Wiederkunft Christi erkennen, dass die Erwählung Israels (Jes 43,4) in der Gemeinde des Messias Jesus zur Erfüllung gekommen ist."] },
      { number: 10, text: "Du hast dich an mein Gebot gehalten, standhaft zu bleiben; daher werde auch ich zu dir halten und dich bewahren vor der Stunde der Versuchung, die über die ganze Erde kommen soll, um die Bewohner der Erde auf die Probe zu stellen.", footnotes: ["Die «Bewohner der Erde» sind in der Offenbarung immer die Gegner Christi und seiner Kirche."] },
      { number: 11, text: "Ich komme bald. Halte fest, was du hast, damit kein anderer deinen Kranz bekommt." },
      { number: 12, text: "Wer siegt, den werde ich zu einer Säule im Tempel meines Gottes machen und er wird immer darin bleiben. Und ich werde auf ihn den Namen meines Gottes schreiben und den Namen der Stadt meines Gottes, des neuen Jerusalem, das aus dem Himmel herabkommt von meinem Gott, und ich werde auf ihn auch meinen neuen Namen schreiben.", footnotes: ["Ez 48,35; Jes 62,2"] },
      { number: 13, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt.", footnotes: ["2,7"] },
      { number: 14, text: "An den Engel der Gemeinde in Laodizea schreibe: So spricht Er, der «Amen» heißt, der treue und zuverlässige Zeuge, der Anfang der Schöpfung Gottes:", footnotes: ["Ps 89,38; Spr 14,5"] },
      { number: 15, heading: "An die Gemeinde in Laodizea: 3,14-22", text: "Ich kenne deine Werke. Du bist weder kalt noch heiß. Wärest du doch kalt oder heiß!", footnotes: ["Spr 8,22 G"] },
      { number: 16, text: "Weil du aber lau bist, weder heiß noch kalt, will ich dich aus meinem Mund ausspeien." },
      { number: 17, text: "Du behauptest: Ich bin reich und wohlhabend und nichts fehlt mir. Du weißt aber nicht, dass gerade du elend und erbärmlich bist, arm, blind und nackt.", footnotes: ["Hos 12,9"] },
      { number: 18, text: "Darum rate ich dir: Kaufe von mir Gold, das im Feuer geläutert ist, damit du reich wirst; und kaufe von mir weiße Kleider und zieh sie an, damit du nicht nackt dastehst und dich schämen musst; und kaufe Salbe für deine Augen, damit du sehen kannst." },
      { number: 19, text: "Wen ich liebe, den weise ich zurecht und nehme ihn in Zucht. Mach also Ernst und kehr um!", footnotes: ["Spr 3,12"] },
      { number: 20, text: "Ich stehe vor der Tür und klopfe an. Wer meine Stimme hört und die Tür öffnet, bei dem werde ich eintreten und wir werden Mahl halten, ich mit ihm und er mit mir." },
      { number: 21, text: "Wer siegt, der darf mit mir auf meinem Thron sitzen, so wie auch ich gesiegt habe und mich mit meinem Vater auf seinen Thron gesetzt habe.", footnotes: ["Mt 19,28; 25,31; Lk 22,30; 1 Kor 4,8"] },
      { number: 22, text: "Wer Ohren hat, der höre, was der Geist den Gemeinden sagt.", footnotes: ["2,7"] },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Danach sah ich: Eine Tür war geöffnet am Himmel; und die Stimme, die vorher zu mir gesprochen hatte und die wie eine Posaune klang, sagte: Komm herauf und ich werde dir zeigen, was dann geschehen muss.", footnotes: ["Ex 19,16.24; Dan 2,29", "1-11: Die Schilderung des himmlischen Hofstaates und seiner Liturgie bringt die Macht und Herrlichkeit Gottes, des Herrn der Welt und der Geschichte, zum Ausdruck."] },
      { number: 2, heading: "Die einleitende Himmelsvision: 4,1 - 5,14 | Die Huldigung vor dem Thron Gottes: 4,1-11", text: "Sogleich wurde ich vom Geist ergriffen. Und ich sah: Ein Thron stand im Himmel; auf dem Thron saß einer,", footnotes: ["Jes 6,1; Ez 1,26-28"] },
      { number: 3, text: "der wie ein Jaspis und ein Karneol aussah. Und über dem Thron wölbte sich ein Regenbogen, der wie ein Smaragd aussah." },
      { number: 4, text: "Und rings um den Thron standen vierundzwanzig Throne und auf den Thronen saßen vierundzwanzig Älteste in weißen Gewändern und mit goldenen Kränzen auf dem Haupt.", footnotes: ["4-8: Die «vierundzwanzig Ältesten» sind hohe Engel, die in Anlehnung an alttestamentliche Vorstellungen eine Art Thronrat Gottes bilden. Die «sieben Geister» sind Engel, die Gott als Boten dienen. Bei den «vier Wesen» (vgl. Ez 1,4-21; Jes 6,2f) handelt es sich um Engel, die als Wächter beim Thron Gottes stehen."] },
      { number: 5, text: "Von dem Thron gingen Blitze, Stimmen und Donner aus. Und sieben lodernde Fackeln brannten vor dem Thron; das sind die sieben Geister Gottes.", footnotes: ["Ex 19,16 G; Sach 4,2; Ez 1,13"] },
      { number: 6, text: "Und vor dem Thron war etwas wie ein gläsernes Meer, gleich Kristall. Und in der Mitte, rings um den Thron, waren vier Lebewesen voller Augen, vorn und hinten.", footnotes: ["Ez 1,22; 1,5.18", "Das «gläserne Meer» symbolisiert das Firmament des Himmels; was besagt, dass Gott jenseits der Schöpfung thront."] },
      { number: 7, text: "Das erste Lebewesen glich einem Löwen, das zweite einem Stier, das dritte sah aus wie ein Mensch, das vierte glich einem fliegenden Adler.", footnotes: ["Ez 1,10"] },
      { number: 8, text: "Und jedes der vier Lebewesen hatte sechs Flügel, außen und innen voller Augen. Sie ruhen nicht, bei Tag und Nacht, und rufen: Heilig, heilig, heilig / ist der Herr, der Gott, der Herrscher über die ganze Schöpfung; / er war und er ist und er kommt.", footnotes: ["Jes 6,2; Ez 1,18 G; Jes 6,3; Ex 3,14 G"] },
      { number: 9, text: "Und wenn die Lebewesen dem, der auf dem Thron sitzt und in alle Ewigkeit lebt, Herrlichkeit und Ehre und Dank erweisen,", footnotes: ["Jes 6,1; Dan 6,27; 12,7"] },
      { number: 10, text: "dann werfen sich die vierundzwanzig Ältesten vor dem, der auf dem Thron sitzt, nieder und beten ihn an, der in alle Ewigkeit lebt. Und sie legen ihre goldenen Kränze vor seinem Thron nieder und sprechen:" },
      { number: 11, text: "Würdig bist du, unser Herr und Gott, / Herrlichkeit zu empfangen und Ehre und Macht. Denn du bist es, der die Welt erschaffen hat, / durch deinen Willen war sie und wurde sie erschaffen." },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Und ich sah auf der rechten Hand dessen, der auf dem Thron saß, eine Buchrolle; sie war innen und außen beschrieben und mit sieben Siegeln versiegelt.", footnotes: ["Jes 6,1; Ez 2,9f; Jes 29,11", "1-11: In der Buchrolle (vgl. Ez 2,9f) sind die von Gott vorherbestimmten Ereignisse der Endzeit aufgezeichnet. Der auferstandene Christus wird die sieben Siegel des Buches lösen und dadurch das Geschehen der Endzeit in Gang setzen. Wie die Visionen von den sieben Siegeln (6,1 - 8,1) sind auch die Visionen von den sieben Posaunen (8,2 - 11,19) und die darauf folgenden Ereignisse (ab Kap. 12) als Inhalt der versiegelten Buchrolle gedacht."] },
      { number: 2, heading: "Das versiegelte Buch und das Lamm: 5,1-14", text: "Und ich sah: Ein gewaltiger Engel rief mit lauter Stimme: Wer ist würdig, die Buchrolle zu öffnen und ihre Siegel zu lösen?" },
      { number: 3, text: "Aber niemand im Himmel, auf der Erde und unter der Erde konnte das Buch öffnen und es lesen." },
      { number: 4, text: "Da weinte ich sehr, weil niemand für würdig befunden wurde, das Buch zu öffnen und es zu lesen." },
      { number: 5, text: "Da sagte einer von den Ältesten zu mir: Weine nicht! Gesiegt hat der Löwe aus dem Stamm Juda, der Spross aus der Wurzel Davids; er kann das Buch und seine sieben Siegel öffnen.", footnotes: ["Gen 49,9; Jes 11,10"] },
      { number: 6, text: "Und ich sah: Zwischen dem Thron und den vier Lebewesen und mitten unter den Ältesten stand ein Lamm; es sah aus wie geschlachtet und hatte sieben Hörner und sieben Augen; die Augen sind die sieben Geister Gottes, die über die ganze Erde ausgesandt sind.", footnotes: ["Jes 53,7; Sach 4,10"] },
      { number: 7, text: "Das Lamm trat heran und empfing das Buch aus der rechten Hand dessen, der auf dem Thron saß.", footnotes: ["Jes 6,1"] },
      { number: 8, text: "Als es das Buch empfangen hatte, fielen die vier Lebewesen und die vierundzwanzig Ältesten vor dem Lamm nieder; alle trugen Harfen und goldene Schalen voll von Räucherwerk; das sind die Gebete der Heiligen.", footnotes: ["Ps 141,2"] },
      { number: 9, text: "Und sie sangen ein neues Lied: Würdig bist du, / das Buch zu nehmen und seine Siegel zu öffnen; denn du wurdest geschlachtet / und hast mit deinem Blut / Menschen für Gott erworben / aus allen Stämmen und Sprachen, / aus allen Nationen und Völkern", footnotes: ["Ps 33,3"] },
      { number: 10, text: "und du hast sie für unsern Gott / zu Königen und Priestern gemacht; / und sie werden auf der Erde herrschen.", footnotes: ["1,6; Ex 19,6; Jes 61,6"] },
      { number: 11, text: "Ich sah und ich hörte die Stimme von vielen Engeln rings um den Thron und um die Lebewesen und die Ältesten; die Zahl der Engel war zehntausendmal zehntausend und tausendmal tausend.", footnotes: ["Dan 7,10"] },
      { number: 12, text: "Sie riefen mit lauter Stimme: Würdig ist das Lamm, das geschlachtet wurde, / Macht zu empfangen, Reichtum und Weisheit, / Kraft und Ehre, Herrlichkeit und Lob.", footnotes: ["Jes 53,7"] },
      { number: 13, text: "Und alle Geschöpfe im Himmel und auf der Erde, unter der Erde und auf dem Meer, alles, was in der Welt ist, hörte ich sprechen: Ihm, der auf dem Thron sitzt, und dem Lamm / gebühren Lob und Ehre und Herrlichkeit und Kraft in alle Ewigkeit.", footnotes: ["Jes 6,1"] },
      { number: 14, text: "Und die vier Lebewesen sprachen: Amen. Und die vierundzwanzig Ältesten fielen nieder und beteten an." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Dann sah ich: Das Lamm öffnete das erste der sieben Siegel; und ich hörte das erste der vier Lebewesen wie mit Donnerstimme rufen: Komm!", footnotes: ["1-8: Die vier Reiter (vgl. Sach 1,7-15; 6,1-8) sind bildhafte Hinweise auf die sog. messianischen Wehen: Völkerkrieg, Bürgerkrieg, Teuerung und Hungersnot, Pest und Massensterben."] },
      { number: 2, heading: "Die sieben Siegel und die sieben Posaunen: 6,1 - 11,19 | Die ersten sechs Siegel: 6,1-17", text: "Da sah ich ein weißes Pferd; und der, der auf ihm saß, hatte einen Bogen. Ein Kranz wurde ihm gegeben und als Sieger zog er aus, um zu siegen.", footnotes: ["Sach 1,8; 6,3.6"] },
      { number: 3, text: "Als das Lamm das zweite Siegel öffnete, hörte ich das zweite Lebewesen rufen: Komm!" },
      { number: 4, text: "Da erschien ein anderes Pferd; das war feuerrot. Und der, der auf ihm saß, wurde ermächtigt, der Erde den Frieden zu nehmen, damit die Menschen sich gegenseitig abschlachteten. Und es wurde ihm ein großes Schwert gegeben.", footnotes: ["Sach 1,8; 6,2"] },
      { number: 5, text: "Als das Lamm das dritte Siegel öffnete, hörte ich das dritte Lebewesen rufen: Komm! Da sah ich ein schwarzes Pferd; und der, der auf ihm saß, hielt in der Hand eine Waage.", footnotes: ["Sach 6,2.6"] },
      { number: 6, text: "Inmitten der vier Lebewesen hörte ich etwas wie eine Stimme sagen: Ein Maß Weizen für einen Denar und drei Maß Gerste für einen Denar. Aber dem Öl und dem Wein füge keinen Schaden zu!" },
      { number: 7, text: "Als das Lamm das vierte Siegel öffnete, hörte ich die Stimme des vierten Lebewesens rufen: Komm!" },
      { number: 8, text: "Da sah ich ein fahles Pferd; und der, der auf ihm saß, heißt «der Tod»; und die Unterwelt zog hinter ihm her. Und ihnen wurde die Macht gegeben über ein Viertel der Erde, Macht, zu töten durch Schwert, Hunger und Tod und durch die Tiere der Erde.", footnotes: ["Hos 13,14; Jer 14,12; 15,3; Ez 5,12.17; 14,21"] },
      { number: 9, text: "Als das Lamm das fünfte Siegel öffnete, sah ich unter dem Altar die Seelen aller, die hingeschlachtet worden waren wegen des Wortes Gottes und wegen des Zeugnisses, das sie abgelegt hatten." },
      { number: 10, text: "Sie riefen mit lauter Stimme: Wie lange zögerst du noch, Herr, du Heiliger und Wahrhaftiger, Gericht zu halten und unser Blut an den Bewohnern der Erde zu rächen?", footnotes: ["Sach 1,12; Ps 79,5; Dtn 32,43; 2 Kön 9,7"] },
      { number: 11, text: "Da wurde jedem von ihnen ein weißes Gewand gegeben; und ihnen wurde gesagt, sie sollten noch kurze Zeit warten, bis die volle Zahl erreicht sei durch den Tod ihrer Mitknechte und Brüder, die noch sterben müssten wie sie." },
      { number: 12, text: "Und ich sah: Das Lamm öffnete das sechste Siegel. Da entstand ein gewaltiges Beben. Die Sonne wurde schwarz wie ein Trauergewand und der ganze Mond wurde wie Blut.", footnotes: ["Jes 13,10; 50,3 G; Ez 32,8; Joël 3,4"] },
      { number: 13, text: "Die Sterne des Himmels fielen herab auf die Erde, wie wenn ein Feigenbaum seine Früchte abwirft, wenn ein heftiger Sturm ihn schüttelt.", footnotes: ["Jes 34,4 G"] },
      { number: 14, text: "Der Himmel verschwand wie eine Buchrolle, die man zusammenrollt, und alle Berge und Inseln wurden von ihrer Stelle weggerückt.", footnotes: ["Jes 34,4"] },
      { number: 15, text: "Und die Könige der Erde, die Großen und die Heerführer, die Reichen und die Mächtigen, alle Sklaven und alle Freien verbargen sich in den Höhlen und Felsen der Berge.", footnotes: ["Ps 2,2; Jes 24,21; 2,10.19"] },
      { number: 16, text: "Sie sagten zu den Bergen und Felsen: Fallt auf uns und verbergt uns vor dem Blick dessen, der auf dem Thron sitzt, und vor dem Zorn des Lammes;", footnotes: ["Hos 10,8; Jes 6,1"] },
      { number: 17, text: "denn der große Tag ihres Zorns ist gekommen. Wer kann da bestehen?", footnotes: ["Joël 2,11; Nah 1,6; Röm 2,5"] },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Danach sah ich: Vier Engel standen an den vier Ecken der Erde. Sie hielten die vier Winde der Erde fest, damit der Wind weder über das Land noch über das Meer wehte, noch gegen irgendeinen Baum.", footnotes: ["Ez 7,2; 37,9"] },
      { number: 2, heading: "Die Bezeichnung der Knechte Gottes: 7,1-17", text: "Dann sah ich vom Osten her einen anderen Engel emporsteigen; er hatte das Siegel des lebendigen Gottes und rief den vier Engeln, denen die Macht gegeben war, dem Land und dem Meer Schaden zuzufügen, mit lauter Stimme zu:" },
      { number: 3, text: "Fügt dem Land, dem Meer und den Bäumen keinen Schaden zu, bis wir den Knechten unseres Gottes das Siegel auf die Stirn gedrückt haben.", footnotes: ["Ez 9,4.6"] },
      { number: 4, text: "Und ich erfuhr die Zahl derer, die mit dem Siegel gekennzeichnet waren. Es waren hundertvierundvierzigtausend aus allen Stämmen der Söhne Israels, die das Siegel trugen:", footnotes: ["4-17: Zwölf ist die Zahl der Stämme Israels, Tausend die Zahl der Fülle. Die Zahl 144000 (12 mal 12 mal 1000) symbolisiert die Gesamtheit derer, die gerettet werden. Ihre «Versiegelung» besagt, dass Gott ihre Zahl im Voraus kennt. In V. 9 werden die Erlösten ausdrücklich als eine unzählbar große Schar aus allen Völkern bezeichnet (vgl. Dan 12,1; Mk 13,10)."] },
      { number: 5, text: "Aus dem Stamm Juda trugen zwölftausend das Siegel, / aus dem Stamm Ruben zwölftausend, / aus dem Stamm Gad zwölftausend," },
      { number: 6, text: "aus dem Stamm Ascher zwölftausend, / aus dem Stamm Naftali zwölftausend, / aus dem Stamm Manasse zwölftausend," },
      { number: 7, text: "aus dem Stamm Simeon zwölftausend, / aus dem Stamm Levi zwölftausend, / aus dem Stamm Issachar zwölftausend," },
      { number: 8, text: "aus dem Stamm Sebulon zwölftausend, / aus dem Stamm Josef zwölftausend, / aus dem Stamm Benjamin trugen zwölftausend das Siegel." },
      { number: 9, text: "Danach sah ich: eine große Schar aus allen Nationen und Stämmen, Völkern und Sprachen; niemand konnte sie zählen. Sie standen in weißen Gewändern vor dem Thron und vor dem Lamm und trugen Palmzweige in den Händen.", footnotes: ["Dan 3,4"] },
      { number: 10, text: "Sie riefen mit lauter Stimme: Die Rettung kommt von unserem Gott, der auf dem Thron sitzt, und von dem Lamm.", footnotes: ["Jes 6,1"] },
      { number: 11, text: "Und alle Engel standen rings um den Thron, um die Ältesten und die vier Lebewesen. Sie warfen sich vor dem Thron nieder, beteten Gott an" },
      { number: 12, text: "und sprachen: Amen, Lob und Herrlichkeit, / Weisheit und Dank, / Ehre und Macht und Stärke / unserem Gott in alle Ewigkeit. Amen." },
      { number: 13, text: "Da fragte mich einer der Ältesten: Wer sind diese, die weiße Gewänder tragen, und woher sind sie gekommen?" },
      { number: 14, text: "Ich erwiderte ihm: Mein Herr, das musst du wissen. Und er sagte zu mir: Es sind die, die aus der großen Bedrängnis kommen; sie haben ihre Gewänder gewaschen und im Blut des Lammes weiß gemacht.", footnotes: ["Dan 12,1; Gen 49,11"] },
      { number: 15, text: "Deshalb stehen sie vor dem Thron Gottes und dienen ihm bei Tag und Nacht in seinem Tempel; und der, der auf dem Thron sitzt, wird sein Zelt über ihnen aufschlagen.", footnotes: ["Jes 6,1"] },
      { number: 16, text: "Sie werden keinen Hunger und keinen Durst mehr leiden und weder Sonnenglut noch irgendeine sengende Hitze wird auf ihnen lasten.", footnotes: ["Jes 49,10"] },
      { number: 17, text: "Denn das Lamm in der Mitte vor dem Thron wird sie weiden und zu den Quellen führen, aus denen das Wasser des Lebens strömt, und Gott wird alle Tränen von ihren Augen abwischen.", footnotes: ["Ez 34,23; Ps 23,2; Jer 2,13; Jes 25,8"] },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Als das Lamm das siebte Siegel öffnete, trat im Himmel Stille ein, etwa eine halbe Stunde lang." },
      { number: 2, heading: "Das siebte Siegel und die sieben Engel: 8,1-5", text: "Und ich sah: Sieben Engel standen vor Gott; ihnen wurden sieben Posaunen gegeben.", footnotes: ["2-9,21: Die in den sieben Posaunenvisionen beschriebenen Vorgänge sind die Antwort auf die Gebete der Heiligen (8,3), d. h. die Bitten der Christen um das Gericht über die Verfolger (vgl. 6,10). Die Schilderung der Gerichtsvorgänge ist weithin der Überlieferung von den ägyptischen Plagen nachgebildet (Ex 7 - 11; Ez 7 - 10). Die Heimsuchungen haben kosmische Ausmaße, bewegen die ungläubige Menschheit aber nicht zur Umkehr (9,20f)."] },
      { number: 3, text: "Und ein anderer Engel kam und trat mit einer goldenen Räucherpfanne an den Altar; ihm wurde viel Weihrauch gegeben, den er auf dem goldenen Altar vor dem Thron verbrennen sollte, um so die Gebete aller Heiligen vor Gott zu bringen.", footnotes: ["Ps 141,2", "3b: Wörtlich: ihm wurde viel Weihrauch gegeben, damit er ihn zusammen mit den Gebeten aller Heiligen auf dem goldenen Altar vor dem Thron darbringe."] },
      { number: 4, text: "Aus der Hand des Engels stieg der Weihrauch mit den Gebeten der Heiligen zu Gott empor." },
      { number: 5, text: "Dann nahm der Engel die Räucherpfanne, füllte sie mit glühenden Kohlen, die er vom Altar nahm, und warf sie auf die Erde; da begann es zu donnern und zu dröhnen, zu blitzen und zu beben.", footnotes: ["Lev 16,12; Ex 19,16"] },
      { number: 6, text: "Dann machten sich die sieben Engel bereit, die sieben Posaunen zu blasen." },
      { number: 7, heading: "Die ersten vier Posaunen: 8,6-13", text: "Der erste Engel blies seine Posaune. Da fielen Hagel und Feuer, die mit Blut vermischt waren, auf das Land. Es verbrannte ein Drittel des Landes, ein Drittel der Bäume und alles grüne Gras.", footnotes: ["Ex 9,23-25; Ez 38,22; Joël 3,3"] },
      { number: 8, text: "Der zweite Engel blies seine Posaune. Da wurde etwas, das einem großen brennenden Berg glich, ins Meer geworfen. Ein Drittel des Meeres wurde zu Blut.", footnotes: ["Jer 51,25 G; Ex 7,20f"] },
      { number: 9, text: "Und ein Drittel der Geschöpfe, die im Meer leben, kam um und ein Drittel der Schiffe wurde vernichtet." },
      { number: 10, text: "Der dritte Engel blies seine Posaune. Da fiel ein großer Stern vom Himmel; er loderte wie eine Fackel und fiel auf ein Drittel der Flüsse und auf die Quellen.", footnotes: ["Jes 14,12"] },
      { number: 11, text: "Der Name des Sterns ist «Wermut». Ein Drittel des Wassers wurde bitter und viele Menschen starben durch das Wasser, weil es bitter geworden war." },
      { number: 12, text: "Der vierte Engel blies seine Posaune. Da wurde ein Drittel der Sonne und ein Drittel des Mondes und ein Drittel der Sterne getroffen, sodass sie ein Drittel ihrer Leuchtkraft verloren und der Tag um ein Drittel dunkler wurde und ebenso die Nacht." },
      { number: 13, text: "Und ich sah und hörte: Ein Adler flog hoch am Himmel und rief mit lauter Stimme: Wehe! Wehe! Wehe den Bewohnern der Erde! Noch drei Engel werden ihre Posaunen blasen.", footnotes: ["Die vier ersten Posaunenvisionen (8,6-12) bilden wie die vier ersten Siegelvisionen eine Gruppe. Die letzten drei Posaunenvisionen, als «die drei Wehe» bezeichnet (9,12; 11,14), sind durch den Weheruf des Adlers von dieser Gruppe abgehoben."] },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Der fünfte Engel blies seine Posaune. Da sah ich einen Stern, der vom Himmel auf die Erde gefallen war; ihm wurde der Schlüssel zu dem Schacht gegeben, der in den Abgrund führt.", footnotes: ["12,4; Mt 24,29; Mk 13,25", "1-21: Die Heimsuchungen der ersten vier Posaunenvisionen treffen nicht unmittelbar die Menschen, sondern Bereiche der Natur. In der fünften und sechsten sind dämonische Mächte am Werk, die die ungläubigen Menschen quälen (9,1-12) und ein Drittel von ihnen töten (9,13-21). 1f: Der «Abgrund» ist nicht der Aufenthaltsort der Toten (die Scheol, der Hades), sondern das ebenfalls unter der Erde gedachte Gefängnis, in dem nach jüdischer Vorstellung die gefallenen Engel auf ihre endgültige Bestrafung warten. Aus diesem Abgrund lässt ein Engel («ein Stern») ein dämonisches Heuschreckenheer (vgl. Ex 10,12-20; Joël 1,6f.15; 2,1-11) heraufsteigen."] },
      { number: 2, heading: "Die fünfte Posaune: 9,1-12", text: "Und er öffnete den Schacht des Abgrunds. Da stieg Rauch aus dem Schacht auf, wie aus einem großen Ofen, und Sonne und Luft wurden verfinstert durch den Rauch aus dem Schacht.", footnotes: ["Ex 19,18; Gen 19,28; Joël 2,10"] },
      { number: 3, text: "Aus dem Rauch kamen Heuschrecken über die Erde und ihnen wurde Kraft gegeben, wie sie Skorpione auf der Erde haben.", footnotes: ["Ex 10,12"] },
      { number: 4, text: "Es wurde ihnen gesagt, sie sollten dem Gras auf der Erde, den grünen Pflanzen und den Bäumen keinen Schaden zufügen, sondern nur den Menschen, die das Siegel Gottes nicht auf der Stirn haben.", footnotes: ["Ex 10,15; Ez 9,4"] },
      { number: 5, text: "Es wurde ihnen befohlen, die Menschen nicht zu töten, sondern nur zu quälen, fünf Monate lang. Und der Schmerz, den sie zufügen, ist so stark, wie wenn ein Skorpion einen Menschen sticht." },
      { number: 6, text: "In jenen Tagen werden die Menschen den Tod suchen, aber nicht finden; sie werden sterben wollen, aber der Tod wird vor ihnen fliehen.", footnotes: ["Ijob 3,21"] },
      { number: 7, text: "Und die Heuschrecken sehen aus wie Rosse, die zur Schlacht gerüstet sind; auf ihren Köpfen tragen sie etwas, das gold schimmernden Kränzen gleicht, und ihre Gesichter sind wie Gesichter von Menschen,", footnotes: ["Joël 2,4f"] },
      { number: 8, text: "ihr Haar ist wie Frauenhaar, ihr Gebiss wie ein Löwengebiss,", footnotes: ["Joël 1,6"] },
      { number: 9, text: "ihre Brust wie ein eiserner Panzer; und das Rauschen ihrer Flügel ist wie das Dröhnen von Wagen, von vielen Pferden, die sich in die Schlacht stürzen.", footnotes: ["Joël 2,5"] },
      { number: 10, text: "Sie haben Schwänze und Stacheln wie Skorpione und in ihren Schwänzen ist die Kraft, mit der sie den Menschen schaden, fünf Monate lang." },
      { number: 11, text: "Sie haben als König über sich den Engel des Abgrunds; er heißt auf hebräisch Abaddon, auf griechisch Apollyon.", footnotes: ["Die Namen Abaddon und Apollyon bedeuten beide «Verderber»; Apollyon ist vielleicht eine Anspielung auf den heidnischen Gott Apollon."] },
      { number: 12, text: "Das erste «Wehe» ist vorüber. Noch zweimal wird das «Wehe» kommen." },
      { number: 13, text: "Der sechste Engel blies seine Posaune: Da hörte ich eine Stimme, die von den vier Hörnern des goldenen Altars her kam, der vor Gott steht." },
      { number: 14, heading: "Die sechste Posaune: 9,13-21", text: "Die Stimme sagte zu dem sechsten Engel, der die Posaune hält: Binde die vier Engel los, die am großen Strom, am Eufrat, gefesselt sind.", footnotes: ["Gen 15,18", "14f: Die vier Strafengel stehen am Eufrat bereit, um ein gewaltiges Heer dämonischer Krieger loszulassen (vgl. besonders Ez 38,14-16). Der Eufrat ist für jüdisches Denken die ideelle Ostgrenze des verheißenen Landes. Der Einfall des feindlichen Heeres vom Osten her ist vielleicht eine Anspielung auf den Ansturm der von den Römern gefürchteten Partherheere."] },
      { number: 15, text: "Da wurden die vier Engel losgebunden, die auf Jahr und Monat, auf Tag und Stunde bereitstanden, um ein Drittel der Menschheit zu töten." },
      { number: 16, text: "Und die Zahl der Reiter dieses Heeres war vieltausendmal tausend; diese Zahl hörte ich.", footnotes: ["Wörtlich: 20000 Myriaden. 1 Myriade = 10000. Die Gesamtzahl von 200 Millionen ist als Symbol unermesslicher Größe gemeint."] },
      { number: 17, text: "Und so sahen die Pferde und die Reiter in der Vision aus: Sie trugen feuerrote, rauchblaue und schwefelgelbe Panzer. Die Köpfe der Pferde glichen Löwenköpfen und aus ihren Mäulern schlug Feuer, Rauch und Schwefel.", footnotes: ["Dan 8,1f"] },
      { number: 18, text: "Ein Drittel der Menschen wurde durch diese drei Plagen getötet, durch Feuer, Rauch und Schwefel, die aus ihren Mäulern hervorkamen." },
      { number: 19, text: "Denn die tödliche Macht der Pferde war in ihren Mäulern und in ihren Schwänzen. Ihre Schwänze glichen Schlangen, die Köpfe haben, mit denen sie Schaden zufügen können." },
      { number: 20, text: "Aber die übrigen Menschen, die nicht durch diese Plagen umgekommen waren, wandten sich nicht ab von den Machwerken ihrer Hände: Sie hörten nicht auf, sich niederzuwerfen vor ihren Dämonen, vor ihren Götzen aus Gold, Silber, Erz, Stein und Holz, den Götzen, die weder sehen, noch hören, noch gehen können.", footnotes: ["Jes 17,8; 2,8.20; Ps 106,37; Dan 5,4.23; Ps 115,4-7; 135,15-17"] },
      { number: 21, text: "Sie ließen nicht ab von Mord und Zauberei, von Unzucht und Diebstahl." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Und ich sah: Ein anderer gewaltiger Engel kam aus dem Himmel herab; er war von einer Wolke umhüllt und der Regenbogen stand über seinem Haupt. Sein Gesicht war wie die Sonne und seine Beine waren wie Feuersäulen.", footnotes: ["1-11,14: Die sechste Posaunenvision (9,13-21) wird durch zwei Zwischenstücke erweitert: die erneute Beauftragung des Sehers (10,1-11; vgl. 1,10-20) und die Szenen von der Messung des Tempels und von den zwei Zeugen (11,1-14)."] },
      { number: 2, heading: "Die Engel und das kleine Buch: 10,1-11", text: "In der Hand hielt er ein kleines, aufgeschlagenes Buch. Er setzte seinen rechten Fuß auf das Meer, den linken auf das Land" },
      { number: 3, text: "und rief laut, so wie ein Löwe brüllt. Nachdem er gerufen hatte, erhoben die sieben Donner ihre Stimme.", footnotes: ["Am 3,8"] },
      { number: 4, text: "Als die sieben Donner gesprochen hatten, wollte ich es aufschreiben. Da hörte ich eine Stimme vom Himmel her rufen: Halte geheim, was die sieben Donner gesprochen haben; schreib es nicht auf!", footnotes: ["Dan 12,4.9"] },
      { number: 5, text: "Und der Engel, den ich auf dem Meer und auf dem Land stehen sah, erhob seine rechte Hand zum Himmel.", footnotes: ["(5-6) Dan 12,7; Gen 14,22.19; Neh 9,6"] },
      { number: 6, text: "Er schwor bei dem, der in alle Ewigkeit lebt, der den Himmel geschaffen hat und was darin ist, die Erde und was darauf ist und das Meer und was darin ist: Es wird keine Zeit mehr bleiben," },
      { number: 7, text: "denn in den Tagen, wenn der siebte Engel seine Stimme erhebt und seine Posaune bläst, wird auch das Geheimnis Gottes vollendet sein; so hatte er es seinen Knechten, den Propheten, verkündet." },
      { number: 8, text: "Und die Stimme aus dem Himmel, die ich gehört hatte, sprach noch einmal zu mir: Geh, nimm das Buch, das der Engel, der auf dem Meer und auf dem Land steht, aufgeschlagen in der Hand hält.", footnotes: ["8-10: Ezechiels Bild von der Buchrolle (2,8 - 3,3) wird erneut aufgenommen und weitergeführt. Die Buchrolle schmeckt zuerst süß (wie in Ez 3,3), dann aber verursacht sie bitteres Aufstoßen. Die Botschaft ist «süß», weil sie Gottes Wort ist (Ps 19,11; 119,103), zugleich aber bitter, weil sie das Gericht ankündigt. Nach anderer Auffassung bilden den Inhalt des kleinen Buches (11,1-14) die geschilderten Szenen, deren Botschaft für die Gläubigen beglückend und schmerzlich zugleich ist."] },
      { number: 9, text: "Und ich ging zu dem Engel und bat ihn, mir das kleine Buch zu geben. Er sagte zu mir: Nimm und iss es! In deinem Magen wird es bitter sein, in deinem Mund aber süß wie Honig.", footnotes: ["Ez 2,8; 3,1-3"] },
      { number: 10, text: "Da nahm ich das kleine Buch aus der Hand des Engels und aß es. In meinem Mund war es süß wie Honig. Als ich es aber gegessen hatte, wurde mein Magen bitter." },
      { number: 11, text: "Und mir wurde gesagt: Du musst noch einmal weissagen über viele Völker und Nationen mit ihren Sprachen und Königen.", footnotes: ["Jer 1,10; Dan 3,4; 7,14"] },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Dann wurde mir ein Messstab gegeben, der aussah wie ein Stock, und mir wurde gesagt: Geh, miss den Tempel Gottes und den Altar und zähle alle, die dort anbeten!", footnotes: ["Ez 40,3", "1f: Dass der Tempel vermessen und die Anbetenden gezählt werden, bedeutet, dass in der Verfolgung der Endzeit die christliche Gemeinde unter dem besonderen Schutz Gottes stehen wird."] },
      { number: 2, heading: "Die Vermessung des Tempels: 11,1-2", text: "Den Hof, der außerhalb des Tempels liegt, lass aus und miss ihn nicht; denn er ist den Heiden überlassen. Sie werden die heilige Stadt zertreten, zweiundvierzig Monate lang.", footnotes: ["Sach 12,3 G; Jes 63,18", "42 Monate entsprechen den in Dan 7,25 genannten dreieinhalb Jahren («eine Zeit und zwei Zeiten und eine halbe Zeit»). Dreieinhalb als die Hälfte von sieben bedeutet Unglück. Dreieinhalb Jahre (Offb 12,14) = zweiundvierzig Monate (11,2; 13,5) = zwölfhundertsechzig Tage (11,3; 12,6) ist daher symbolische Bezeichnung für die Dauer der endzeitlichen Verfolgung."] },
      { number: 3, text: "Und ich will meinen zwei Zeugen auftragen, im Bußgewand aufzutreten und prophetisch zu reden, zwölfhundertsechzig Tage lang.", footnotes: ["3-13: Die zwei prophetischen Zeugen sind nach vorherrschender Auffassung die Repräsentanten der christlichen Gemeinde, die ihren Glauben bis zum Tod bezeugen muss. Nach anderer Auffassung sind damit Mose und Elija gemeint, die als Propheten der Endzeit die Juden für Christus gewinnen werden; in diesem Fall ist unter «der großen Stadt» Jerusalem zu verstehen (vgl. die Anmerkung zu 11,8)."] },
      { number: 4, heading: "Das Zeugnis der beiden Propheten: 11,3-14", text: "Sie sind die zwei Ölbäume und die zwei Leuchter, die vor dem Herrn der Erde stehen.", footnotes: ["Sach 4,3.11-14"] },
      { number: 5, text: "Wenn ihnen jemand Schaden zufügen will, schlägt Feuer aus ihrem Mund und verzehrt ihre Feinde; so muss jeder sterben, der ihnen schaden will.", footnotes: ["2 Kön 1,10; 2 Sam 22,9"] },
      { number: 6, text: "Sie haben Macht, den Himmel zu verschließen, damit kein Regen fällt in den Tagen ihres Wirkens als Propheten. Sie haben auch Macht, das Wasser in Blut zu verwandeln und die Erde zu schlagen mit allen möglichen Plagen, sooft sie wollen.", footnotes: ["1 Kön 17,1; Ex 7,17; 1 Sam 4,8"] },
      { number: 7, text: "Wenn sie ihren Auftrag als Zeugen erfüllt haben, wird sie das Tier, das aus dem Abgrund heraufsteigt, bekämpfen, besiegen und töten.", footnotes: ["Dan 7,3.7.21", "«Das Tier, das aus dem Abgrund heraufsteigt», versinnbildlicht dieselbe «antichristliche» Weltmacht wie das «Tier aus dem Meer» in 13,1 (vgl. die dortige Anmerkung)."] },
      { number: 8, text: "Und ihre Leichen bleiben auf der Straße der großen Stadt liegen. Diese Stadt heißt, geistlich verstanden: Sodom und Ägypten; dort wurde auch ihr Herr gekreuzigt.", footnotes: ["Die große Stadt: das irdische Jerusalem und überhaupt jeder Ort, an dem Christen für ihren Glauben den Tod erleiden."] },
      { number: 9, text: "Menschen aus allen Völkern und Stämmen, Sprachen und Nationen werden ihre Leichen dort sehen, dreieinhalb Tage lang; sie werden nicht zulassen, dass die Leichen begraben werden." },
      { number: 10, text: "Und die Bewohner der Erde freuen sich darüber, beglückwünschen sich und schicken sich gegenseitig Geschenke; denn die beiden Propheten hatten die Bewohner der Erde gequält." },
      { number: 11, text: "Aber nach den dreieinhalb Tagen kam von Gott her wieder Lebensgeist in sie und sie standen auf. Da überfiel alle, die sie sahen, große Angst.", footnotes: ["Ez 37,5.10; Gen 15,12"] },
      { number: 12, text: "Und sie hörten eine laute Stimme vom Himmel her rufen: Kommt herauf! Vor den Augen ihrer Feinde stiegen sie in der Wolke zum Himmel hinauf.", footnotes: ["Ex 19,24; 2 Kön 2,11"] },
      { number: 13, text: "In diesem Augenblick entstand ein gewaltiges Erdbeben. Ein Zehntel der Stadt stürzte ein und siebentausend Menschen kamen durch das Erdbeben um. Die Überlebenden wurden vom Entsetzen gepackt und gaben dem Gott des Himmels die Ehre.", footnotes: ["Ez 38,19f"] },
      { number: 14, text: "Das zweite «Wehe» ist vorüber, das dritte «Wehe» kommt bald." },
      { number: 15, text: "Der siebte Engel blies seine Posaune. Da ertönten laute Stimmen im Himmel, die riefen: Nun gehört die Herrschaft über die Welt / unserem Herrn und seinem Gesalbten; / und sie werden herrschen in alle Ewigkeit.", footnotes: ["Ps 2,2; Dan 7,14.27; Sach 14,9"] },
      { number: 16, heading: "Die siebte Posaune: 11,15-19", text: "Und die vierundzwanzig Ältesten, die vor Gott auf ihren Thronen sitzen, warfen sich nieder, beteten Gott an" },
      { number: 17, text: "und sprachen: Wir danken dir, Herr, / Gott und Herrscher über die ganze Schöpfung, / der du bist und der du warst; denn du hast deine große Macht in Anspruch genommen / und die Herrschaft angetreten.", footnotes: ["Ex 3,14 G; Ps 99,1"] },
      { number: 18, text: "Die Völker gerieten in Zorn. / Da kam dein Zorn und die Zeit, die Toten zu richten: die Zeit, deine Knechte zu belohnen, / die Propheten und die Heiligen und alle, die deinen Namen fürchten, / die Kleinen und die Großen, / die Zeit, alle zu verderben, die die Erde verderben.", footnotes: ["Ps 2,1.5; Ps 115,13"] },
      { number: 19, text: "Der Tempel Gottes im Himmel wurde geöffnet und in seinem Tempel wurde die Lade seines Bundes sichtbar: Da begann es zu blitzen, zu dröhnen und zu donnern, es gab ein Beben und schweren Hagel.", footnotes: ["1 Kön 8,1.6; Ex 19,16; 9,24", "Das Sichtbarwerden der «himmlischen Bundeslade» verweist auf die Gegenwart Gottes in der vollendeten Gemeinde der Endzeit (Kap. 21 22). Die genannten Naturereignisse sind traditionelle Begleitzeichen des Erscheinens Gottes."] },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Dann erschien ein großes Zeichen am Himmel: eine Frau, mit der Sonne bekleidet; der Mond war unter ihren Füßen und ein Kranz von zwölf Sternen auf ihrem Haupt.", footnotes: ["1-6: Wie der Drache in 12,3, nach 12,9 der Teufel, so ist auch die Frau eine symbolische Gestalt, Sinnbild des Gottesvolkes des Alten und Neuen Bundes. Das Bild von der Geburt eines Sohnes und von dessen Herrschaft über die Völker steht in der Tradition messianischer Deutung prophetischer Texte (Jes 7,14; 66,7). - Die Schilderung des Kampfes des Drachen gegen die Frau wird in V. 13 fortgesetzt."] },
      { number: 2, heading: "Der Kampf des Satans gegen das Volk Gottes: 12,1 - 14,5 | Die Frau und der Drache: 12,1-6", text: "Sie war schwanger und schrie vor Schmerz in ihren Geburtswehen.", footnotes: ["Jes 66,7; Mi 4,10"] },
      { number: 3, text: "Ein anderes Zeichen erschien am Himmel: ein Drache, groß und feuerrot, mit sieben Köpfen und zehn Hörnern und mit sieben Diademen auf seinen Köpfen.", footnotes: ["Dan 7,7"] },
      { number: 4, text: "Sein Schwanz fegte ein Drittel der Sterne vom Himmel und warf sie auf die Erde herab. Der Drache stand vor der Frau, die gebären sollte; er wollte ihr Kind verschlingen, sobald es geboren war.", footnotes: ["Dan 8,10; Offb 9,1; Mi 5,2"] },
      { number: 5, text: "Und sie gebar ein Kind, einen Sohn, der über alle Völker mit eisernem Zepter herrschen wird. Und ihr Kind wurde zu Gott und zu seinem Thron entrückt.", footnotes: ["Jes 66,7; Ps 2,9 G"] },
      { number: 6, text: "Die Frau aber floh in die Wüste, wo Gott ihr einen Zufluchtsort geschaffen hatte; dort wird man sie mit Nahrung versorgen, zwölfhundertsechzig Tage lang." },
      { number: 7, text: "Da entbrannte im Himmel ein Kampf; Michael und seine Engel erhoben sich, um mit dem Drachen zu kämpfen. Der Drache und seine Engel kämpften,", footnotes: ["Dan 10,13.20"] },
      { number: 8, heading: "Der Sturz des Drachen: 12,7-12", text: "aber sie konnten sich nicht halten und sie verloren ihren Platz im Himmel.", footnotes: ["Dan 2,35"] },
      { number: 9, text: "Er wurde gestürzt, der große Drache, die alte Schlange, die Teufel oder Satan heißt und die ganze Welt verführt; der Drache wurde auf die Erde gestürzt und mit ihm wurden seine Engel hinabgeworfen.", footnotes: ["Gen 3,1.14; Sach 3,1; Ijob 1,6"] },
      { number: 10, text: "Da hörte ich eine laute Stimme im Himmel rufen: Jetzt ist er da, der rettende Sieg, / die Macht und die Herrschaft unseres Gottes / und die Vollmacht seines Gesalbten; denn gestürzt wurde der Ankläger unserer Brüder, / der sie bei Tag und bei Nacht / vor unserem Gott verklagte." },
      { number: 11, text: "Sie haben ihn besiegt durch das Blut des Lammes / und durch ihr Wort und Zeugnis; sie hielten ihr Leben nicht fest, / bis hinein in den Tod." },
      { number: 12, text: "Darum jubelt, ihr Himmel / und alle, die darin wohnen. Weh aber euch, Land und Meer! / Denn der Teufel ist zu euch hinabgekommen; seine Wut ist groß, / weil er weiß, dass ihm nur noch eine kurze Frist bleibt.", footnotes: ["Jes 44,23"] },
      { number: 13, text: "Als der Drache erkannte, dass er auf die Erde gestürzt war, verfolgte er die Frau, die den Sohn geboren hatte.", footnotes: ["13-17: Da der Teufel die Kirche als Ganze nicht vernichten kann, versucht er, einzelne ihrer Mitglieder («Nachkommen», V. 17) zu Fall zu bringen."] },
      { number: 14, heading: "Der Kampf des Drachen gegen die Frau: 12,13-18", text: "Aber der Frau wurden die beiden Flügel des großen Adlers gegeben, damit sie in die Wüste an ihren Ort fliegen konnte. Dort ist sie vor der Schlange sicher und wird eine Zeit und zwei Zeiten und eine halbe Zeit lang ernährt.", footnotes: ["Dan 7,25; 12,7"] },
      { number: 15, text: "Die Schlange spie einen Strom von Wasser aus ihrem Rachen hinter der Frau her, damit sie von den Fluten fortgerissen werde." },
      { number: 16, text: "Aber die Erde kam der Frau zu Hilfe; sie öffnete sich und verschlang den Strom, den der Drache aus seinem Rachen gespien hatte." },
      { number: 17, text: "Da geriet der Drache in Zorn über die Frau und er ging fort, um Krieg zu führen mit ihren übrigen Nachkommen, die den Geboten Gottes gehorchen und an dem Zeugnis für Jesus festhalten." },
      { number: 18, text: "Und der Drache trat an den Strand des Meeres." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Und ich sah: Ein Tier stieg aus dem Meer, mit zehn Hörnern und sieben Köpfen. Auf seinen Hörnern trug es zehn Diademe und auf seinen Köpfen Namen, die eine Gotteslästerung waren.", footnotes: ["Dan 7,3.7", "1f: Zur Bekämpfung der Christen bedient sich der Drache des aus dem Meer aufsteigenden Tieres. Die Hörner, Diademe usw. sind Symbole seiner Macht (vgl. Dan 7). Die Namen auf seinen sieben Köpfen sind Titel der römischen Kaiser (Hinweis auf den Kaiserkult)."] },
      { number: 2, heading: "Die beiden Tiere: 13,1-18", text: "Das Tier, das ich sah, glich einem Panther; seine Füße waren wie die Tatzen eines Bären und sein Maul wie das Maul eines Löwen. Und der Drache hatte ihm seine Gewalt übergeben, seinen Thron und seine große Macht.", footnotes: ["Dan 7,4-6"] },
      { number: 3, text: "Einer seiner Köpfe sah aus wie tödlich verwundet; aber die tödliche Wunde wurde geheilt. Und die ganze Erde sah dem Tier staunend nach.", footnotes: ["Die sieben Köpfe des Tieres werden in 17,9 ausdrücklich auf sieben «Könige», das heißt auf römische Kaiser, gedeutet. Mit dem erschlagenen Tier, das «wieder zum Leben kam» (V. 14), ist Kaiser Domitian (81-96 n. Chr.), der «wiedererstandene Nero» (54-68 n. Chr.), gemeint. Zu dieser Deutung könnte auch 666 als Zahl des Namens des Tieres passen (vgl. 13,18)."] },
      { number: 4, text: "Die Menschen warfen sich vor dem Drachen nieder, weil er seine Macht dem Tier gegeben hatte; und sie beteten das Tier an und sagten: Wer ist dem Tier gleich und wer kann den Kampf mit ihm aufnehmen?" },
      { number: 5, text: "Und es wurde ermächtigt, mit seinem Maul anmaßende Worte und Lästerungen auszusprechen; es wurde ihm Macht gegeben, dies zweiundvierzig Monate zu tun.", footnotes: ["Dan 7,8.25"] },
      { number: 6, text: "Das Tier öffnete sein Maul, um Gott und seinen Namen zu lästern, seine Wohnung und alle, die im Himmel wohnen." },
      { number: 7, text: "Und es wurde ihm erlaubt, mit den Heiligen zu kämpfen und sie zu besiegen. Es wurde ihm auch Macht gegeben über alle Stämme, Völker, Sprachen und Nationen.", footnotes: ["Dan 7,21", "erlaubt, wörtlich: gegeben."] },
      { number: 8, text: "Alle Bewohner der Erde fallen nieder vor ihm: alle, deren Name nicht seit der Erschaffung der Welt eingetragen ist ins Lebensbuch des Lammes, das geschlachtet wurde.", footnotes: ["Dan 12,1; Ps 69,29; Jes 53,7; Offb 3,5"] },
      { number: 9, text: "Wenn einer Ohren hat, so höre er." },
      { number: 10, text: "Wer zur Gefangenschaft bestimmt ist, geht in die Gefangenschaft. Wer mit dem Schwert getötet werden soll, wird mit dem Schwert getötet. Hier muss sich die Standhaftigkeit und die Glaubenstreue der Heiligen bewähren.", footnotes: ["Jer 15,2; 43,11"] },
      { number: 11, text: "Und ich sah: Ein anderes Tier stieg aus der Erde herauf. Es hatte zwei Hörner wie ein Lamm, aber es redete wie ein Drache.", footnotes: ["11-17: Schon das Alte Testament kennt zwei Tierungeheuer, den Leviatan im Meer und den Behemot auf dem festen Land (vgl. Jes 27,1; Ps 74,14; 104,26; Ijob 3,8; 40,15-32); sie stellen sinnbildlich die ungeordnete, gottwidrige Schöpfung dar (das Chaos). In Offb ist das aus dem Land aufsteigende Tier der Beauftragte des Tieres aus dem Meer. Es wird als «falscher Prophet» gekennzeichnet (16,13; 19,20; 20,10; vgl. Mk 13,22; 2 Thess 2,9f)."] },
      { number: 12, text: "Die ganze Macht des ersten Tieres übte es vor dessen Augen aus. Es brachte die Erde und ihre Bewohner dazu, das erste Tier anzubeten, dessen tödliche Wunde geheilt war." },
      { number: 13, text: "Es tat große Zeichen; sogar Feuer ließ es vor den Augen der Menschen vom Himmel auf die Erde fallen." },
      { number: 14, text: "Es verwirrte die Bewohner der Erde durch die Wunderzeichen, die es im Auftrag des Tieres tat; es befahl den Bewohnern der Erde, ein Standbild zu errichten zu Ehren des Tieres, das mit dem Schwert erschlagen worden war und doch wieder zum Leben kam." },
      { number: 15, text: "Es wurde ihm Macht gegeben, dem Standbild des Tieres Lebensgeist zu verleihen, sodass es auch sprechen konnte und bewirkte, dass alle getötet wurden, die das Standbild des Tieres nicht anbeteten.", footnotes: ["Dan 3,5f"] },
      { number: 16, text: "Die Kleinen und die Großen, die Reichen und die Armen, die Freien und die Sklaven, alle zwang es, auf ihrer rechten Hand oder ihrer Stirn ein Kennzeichen anzubringen." },
      { number: 17, text: "Kaufen oder verkaufen konnte nur, wer das Kennzeichen trug: den Namen des Tieres oder die Zahl seines Namens." },
      { number: 18, text: "Hier braucht man Kenntnis. Wer Verstand hat, berechne den Zahlenwert des Tieres. Denn es ist die Zahl eines Menschennamens; seine Zahl ist sechshundertsechsundsechzig.", footnotes: ["Im Griechischen und Hebräischen dienen die Buchstaben auch als Ziffern. Man kann also den Zahlenwert der Buchstaben eines Namens zusammenzählen. Auf wen der Zahlenwert 666 sich bezieht, ist nicht sicher; möglicherweise auf Kaiser Nero (vgl. die Anmerkung zu 13,3)."] },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Und ich sah: Das Lamm stand auf dem Berg Zion und bei ihm waren hundertvierundvierzigtausend; auf ihrer Stirn trugen sie seinen Namen und den Namen seines Vaters.", footnotes: ["Ez 9,4", "1-5: Die Christen, die in der Verfolgung dem Lamm die Treue gehalten haben, werden aus den Katastrophen der Endzeit gerettet. Zur Zahl «hundertvierundvierzigtausend» vgl. die Anmerkung zu 7,4-17. In V. 4 wird auf alttestamentliche Bildsprache zurückgegriffen; vgl. die Anmerkungen zu 12,1-6 und 21,9f."] },
      { number: 2, heading: "Das Lamm und sein Gefolge: 14,1-5", text: "Dann hörte ich eine Stimme vom Himmel her, die dem Rauschen von Wassermassen und dem Rollen eines gewaltigen Donners glich. Die Stimme, die ich hörte, war wie der Klang der Harfe, die ein Harfenspieler schlägt.", footnotes: ["Ez 1,24; 43,2"] },
      { number: 3, text: "Und sie sangen ein neues Lied vor dem Thron und vor den vier Lebewesen und vor den Ältesten. Aber niemand konnte das Lied singen lernen außer den hundertvierundvierzigtausend, die freigekauft und von der Erde weggenommen worden sind.", footnotes: ["Ps 33,3; Jes 42,10"] },
      { number: 4, text: "Sie sind es, die sich nicht mit Weibern befleckt haben; denn sie sind jungfräulich. Sie folgen dem Lamm, wohin es geht. Sie allein unter allen Menschen sind freigekauft als Erstlingsgabe für Gott und das Lamm." },
      { number: 5, text: "Denn in ihrem Mund fand sich keinerlei Lüge. Sie sind ohne Makel.", footnotes: ["Jes 53,9; Zef 3,13"] },
      { number: 6, text: "Dann sah ich: Ein anderer Engel flog hoch am Himmel. Er hatte den Bewohnern der Erde ein ewiges Evangelium zu verkünden, allen Nationen, Stämmen, Sprachen und Völkern." },
      { number: 7, heading: "Das Gericht: 14,6 - 20,15 | Die Ankündigung des Gerichts: 14,6-13", text: "Er rief mit lauter Stimme: Fürchtet Gott und erweist ihm die Ehre! Denn die Stunde seines Gerichts ist gekommen. Betet ihn an, der den Himmel und die Erde, das Meer und die Wasserquellen geschaffen hat.", footnotes: ["Ex 20,11"] },
      { number: 8, text: "Ein anderer Engel, ein zweiter, folgte und rief: Gefallen, gefallen ist Babylon, die Große, die alle Völker betrunken gemacht hat mit dem Zornwein ihrer Hurerei.", footnotes: ["Jes 21,9; Dan 4,27; Jer 51,7f", "Babylon war für die Juden der Inbegriff weltlicher Macht und Gottlosigkeit, später wurde es apokalyptischer Deckname für Rom (vgl. 1 Petr 5,13)."] },
      { number: 9, text: "Ein anderer Engel, ein dritter, folgte ihnen und rief mit lauter Stimme: Wer das Tier und sein Standbild anbetet und wer das Kennzeichen auf seiner Stirn oder seiner Hand annimmt," },
      { number: 10, text: "der muss den Wein des Zornes Gottes trinken, der unverdünnt im Becher seines Zorns gemischt ist. Und er wird mit Feuer und Schwefel gequält vor den Augen der heiligen Engel und des Lammes.", footnotes: ["Jes 51,17.22; Ps 75,9 G; Jer 25,15; Gen 19,24"] },
      { number: 11, text: "Der Rauch von ihrer Peinigung steigt auf in alle Ewigkeit und alle, die das Tier und sein Standbild anbeten und die seinen Namen als Kennzeichen annehmen, werden bei Tag und Nacht keine Ruhe haben.", footnotes: ["Jes 34,9f"] },
      { number: 12, text: "Hier muss sich die Standhaftigkeit der Heiligen bewähren, die an den Geboten Gottes und an der Treue zu Jesus festhalten." },
      { number: 13, text: "Und ich hörte eine Stimme vom Himmel her rufen: Schreibe! Selig die Toten, die im Herrn sterben, von jetzt an; ja, spricht der Geist, sie sollen ausruhen von ihren Mühen; denn ihre Werke begleiten sie.", footnotes: ["1,3", "Andere Übersetzungsmöglichkeit: . . . im Herrn sterben. Von jetzt an - ja, spricht der Geist - sollen sie ausruhen von ihren Mühen."] },
      { number: 14, text: "Dann sah ich eine weiße Wolke. Auf der Wolke thronte einer, der wie ein Menschensohn aussah. Er trug einen goldenen Kranz auf dem Haupt und eine scharfe Sichel in der Hand.", footnotes: ["Dan 7,13", "14-20: Auf die Ankündigung des Gerichts (14,6-13) folgt die Vision vom Gericht haltenden Menschensohn. Die Bilder (Ernte, Weinlese) stammen aus dem Alten Testament (vgl. Joël 4,12f)."] },
      { number: 15, heading: "Die Stunde der Ernte: 14,14-20", text: "Und ein anderer Engel kam aus dem Tempel und rief dem, der auf der Wolke saß, mit lauter Stimme zu: Schick deine Sichel aus und ernte! Denn die Zeit zu ernten ist gekommen: Die Frucht der Erde ist reif geworden.", footnotes: ["Joël 4,13"] },
      { number: 16, text: "Und der, der auf der Wolke saß, schleuderte seine Sichel über die Erde und die Erde wurde abgeerntet." },
      { number: 17, text: "Und ein anderer Engel trat aus dem himmlischen Tempel. Auch er hatte eine scharfe Sichel." },
      { number: 18, text: "Vom Altar her kam noch ein anderer Engel, der die Macht über das Feuer hatte. Dem, der die scharfe Sichel trug, rief er mit lauter Stimme zu: Schick deine scharfe Sichel aus und ernte die Trauben vom Weinstock der Erde! Seine Beeren sind reif geworden.", footnotes: ["Joël 4,13"] },
      { number: 19, text: "Da schleuderte der Engel seine Sichel auf die Erde, erntete den Weinstock der Erde ab und warf die Trauben in die große Kelter des Zornes Gottes." },
      { number: 20, text: "Die Kelter wurde draußen vor der Stadt getreten und Blut strömte aus der Kelter; es stieg an, bis an die Zügel der Pferde, eintausendsechshundert Stadien weit.", footnotes: ["Joël 4,13; Jes 63,3", "Als ein Vielfaches der symbolischen Zahl der Erde (Vier) symbolisiert die Zahl 1600 (4 mal 4 mal 100) die ganze Erde. - «Stadion» ist ein griechisches Längenmaß, etwa 185 bis 200 Meter."] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Dann sah ich ein anderes Zeichen am Himmel, groß und wunderbar. Ich sah sieben Engel mit sieben Plagen, den sieben letzten; denn in ihnen erreicht der Zorn Gottes sein Ende.", footnotes: ["Lev 26,21 G", "1-16,21: Zwischen die voraufgehende Gerichtsvision und die nachfolgenden Gerichtsschilderungen (17,1 - 20,15) schiebt Johannes eine dritte Reihe endzeitlicher Heimsuchungen ein: die sieben letzten Plagen. 1-8: Die sieben Engel mit den Schalen werden im Rahmen einer liturgischen Szene vorgestellt, wie zuvor auch die Engel mit den Posaunen (vgl. 8,2-6). - Das in V. 3 genannte Lied des Mose (Ex 15) feierte den Sieg über den Pharao; hier ist es auf den endzeitlichen Sieg bezogen, daher zugleich als «Lied des Lammes» bezeichnet."] },
      { number: 2, heading: "Die Ankündigung der sieben letzten Plagen: 15,1-8", text: "Dann sah ich etwas, das einem gläsernen Meer glich und mit Feuer durchsetzt war. Und die Sieger über das Tier, über sein Standbild und über die Zahl seines Namens standen auf dem gläsernen Meer und trugen die Harfen Gottes." },
      { number: 3, text: "Sie sangen das Lied des Mose, des Knechtes Gottes, und das Lied zu Ehren des Lammes: Groß und wunderbar sind deine Taten, / Herr, Gott und Herrscher über die ganze Schöpfung. Gerecht und zuverlässig sind deine Wege, / du König der Völker.", footnotes: ["Ex 15,1; 14,31; 15,11; Ps 92,5f; 111,2; 139,14; Dtn 32,4", "König der Völker, nach anderen Textzeugen: König aller Zeiten. - Christus, das Lamm, ist der neue Mose (vgl. Apg 3,22-26)."] },
      { number: 4, text: "Wer wird dich nicht fürchten, Herr, / wer wird deinen Namen nicht preisen? Denn du allein bist heilig: / Alle Völker kommen und beten dich an; / denn deine gerechten Taten sind offenbar geworden.", footnotes: ["Ps 145,17; 99,3; 86,9"] },
      { number: 5, text: "Danach sah ich: Es öffnete sich der himmlische Tempel, das Zelt des Zeugnisses im Himmel.", footnotes: ["Ex 40,34 G"] },
      { number: 6, text: "Und die sieben Engel mit den sieben Plagen traten heraus; sie waren in reines, glänzendes Leinen gekleidet und trugen um ihre Brust einen Gürtel aus Gold.", footnotes: ["Lev 26,21 G; Dan 10,5; Ez 9,2.11 G"] },
      { number: 7, text: "Und eines der vier Lebewesen reichte den sieben Engeln sieben goldene Schalen; sie waren gefüllt mit dem Zorn des Gottes, der in alle Ewigkeit lebt." },
      { number: 8, text: "Und der Tempel füllte sich mit dem Rauch der Herrlichkeit und Macht Gottes. Niemand konnte den Tempel betreten, bis die sieben Plagen aus der Hand der sieben Engel zu ihrem Ende gekommen waren.", footnotes: ["Ex 40,34f; 1 Kön 8,10; Jes 6,4; Lev 26,21"] },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Dann hörte ich, wie eine laute Stimme aus dem Tempel den sieben Engeln zurief: Geht und gießt die sieben Schalen mit dem Zorn Gottes über die Erde!", footnotes: ["Jes 66,6; Zef 3,8", "1-21: Die Visionen von sieben Schalen erinnern an die ägyptischen Plagen und andere Überlieferungen. Auch diese noch schlimmeren Katastrophen bewegen die Menschen nicht zur Umkehr. Die drei letzten dieser Visionen beziehen sich im Besonderen auf die römische Weltmacht."] },
      { number: 2, heading: "Die sieben Engel mit den Schalen des Zorns: 16,1-21", text: "Der erste ging und goss seine Schale über das Land. Da bildete sich ein böses und schlimmes Geschwür an den Menschen, die das Kennzeichen des Tieres trugen und sein Standbild anbeteten.", footnotes: ["Dtn 28,35; Ex 9,9f"] },
      { number: 3, text: "Der zweite Engel goss seine Schale über das Meer. Da wurde es zu Blut, das aussah wie das Blut eines Toten; und alle Lebewesen im Meer starben.", footnotes: ["Ex 7,17f"] },
      { number: 4, text: "Der dritte goss seine Schale über die Flüsse und Quellen. Da wurde alles zu Blut.", footnotes: ["Ex 7,19"] },
      { number: 5, text: "Und ich hörte den Engel, der die Macht über das Wasser hat, sagen: Gerecht bist du, der du bist und der du warst, du Heiliger; denn damit hast du ein gerechtes Urteil gefällt.", footnotes: ["Ps 119,137; Ex 3,14 G; Dtn 32,4 G"] },
      { number: 6, text: "Sie haben das Blut von Heiligen und Propheten vergossen; deshalb hast du ihnen Blut zu trinken gegeben, so haben sie es verdient.", footnotes: ["Ps 79,3; Jes 49,26 G"] },
      { number: 7, text: "Und ich hörte eine Stimme vom Brandopferaltar her sagen: Ja, Herr, Gott und Herrscher über die ganze Schöpfung. Wahr und gerecht sind deine Gerichtsurteile.", footnotes: ["Ps 19,10"] },
      { number: 8, text: "Der vierte Engel goss seine Schale über die Sonne. Da wurde ihr Macht gegeben, mit ihrem Feuer die Menschen zu verbrennen." },
      { number: 9, text: "Und die Menschen verbrannten in der großen Hitze. Dennoch verfluchten sie den Namen Gottes, der die Macht über diese Plagen hat. Sie bekehrten sich nicht dazu, ihm die Ehre zu geben." },
      { number: 10, text: "Der fünfte Engel goss seine Schale über den Thron des Tieres. Da kam Finsternis über das Reich des Tieres und die Menschen zerbissen sich vor Angst und Schmerz die Zunge.", footnotes: ["Ex 10,21f", "Thron des Tieres: Symbol der Macht des in Rom residierenden Kaisers."] },
      { number: 11, text: "Dennoch verfluchten sie den Gott des Himmels wegen ihrer Schmerzen und ihrer Geschwüre; und sie ließen nicht ab von ihrem Treiben." },
      { number: 12, text: "Der sechste Engel goss seine Schale über den großen Strom, den Eufrat. Da trocknete sein Wasser aus, sodass den Königen des Ostens der Weg offen stand.", footnotes: ["Gen 15,18; Dtn 1,7; Jer 50,38; Jes 41,2.25", "12-16: Diese Vision scheint zwei verschiedene Vorstellungen miteinander zu verbinden: die Erwartung des Einbruchs der Partherkönige in das Römerreich (V. 12) und die alttestamentliche Erwartung eines großen Ansturms der Heidenvölker auf Israel (VV. 13-16)."] },
      { number: 13, text: "Dann sah ich aus dem Maul des Drachen und aus dem Maul des Tieres und aus dem Maul des falschen Propheten drei unreine Geister hervorkommen, die wie Frösche aussahen." },
      { number: 14, text: "Es sind Dämonengeister, die Wunderzeichen tun; sie schwärmten aus zu den Königen der ganzen Erde, um sie zusammenzuholen für den Krieg am großen Tag Gottes, des Herrschers über die ganze Schöpfung.", footnotes: ["Ex 8,3"] },
      { number: 15, text: "Siehe, ich komme wie ein Dieb. Selig, wer wach bleibt und sein Gewand anbehält, damit er nicht nackt gehen muss und man seine Blöße sieht.", footnotes: ["1,3; Mt 24,37-44; Lk 12,35-40"] },
      { number: 16, text: "Die Geister führten die Könige an dem Ort zusammen, der auf hebräisch Harmagedon heißt.", footnotes: ["Harmagedon: wahrscheinlich ein apokalyptischer Symbol- und Geheimname, keine geographische Bezeichnung. Er ist wohl durch die Verknüpfung von hebräisch «har» = Berg («hoher Berg», vgl. Jes 14,13) mit dem Ortsnamen «Megiddo» (Sach 12,11) entstanden."] },
      { number: 17, text: "Und der siebte Engel goss seine Schale über die Luft. Da kam eine laute Stimme aus dem Tempel, die vom Thron her rief: Es ist geschehen.", footnotes: ["Jes 66,6"] },
      { number: 18, text: "Und es folgten Blitze, Stimmen und Donner; es entstand ein gewaltiges Erdbeben, wie noch keines gewesen war, seitdem es Menschen auf der Erde gibt. So gewaltig war dieses Beben.", footnotes: ["Ex 19,16; Dan 12,1"] },
      { number: 19, text: "Die große Stadt brach in drei Teile auseinander und die Städte der Völker stürzten ein. Gott hatte sich an Babylon, die Große, erinnert und reichte ihr den Becher mit dem Wein seines rächenden Zornes.", footnotes: ["Dan 4,27; Jes 51,17.22; Jer 25,15"] },
      { number: 20, text: "Alle Inseln verschwanden und es gab keine Berge mehr." },
      { number: 21, text: "Und gewaltige Hagelbrocken, zentnerschwer, stürzten vom Himmel auf die Menschen herab. Dennoch verfluchten die Menschen Gott wegen dieser Hagelplage; denn die Plage war über die Maßen groß.", footnotes: ["Ex 9,22-26"] },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Dann kam einer der sieben Engel, welche die sieben Schalen trugen, und sagte zu mir: Komm, ich zeige dir das Strafgericht über die große Hure, die an den vielen Gewässern sitzt.", footnotes: ["Jer 51,13", "1-20,15: In mehreren Szenen (17,1 - 19,21; 20,4-15) wird das Endgericht über die im Vorausgehenden geschilderten feindlichen Mächte dargestellt. 1-6: Mit der «großen Hure» und «Babylon» ist die Weltmacht Rom gemeint. Das Tier, auf dem die Hure reitet (V. 3), ist das römische Kaisertum (vgl. die Anmerkung zu 13,1-3). - «Hurerei», im Alten Testament Bild für Götzendienst und Abfall von Gott, meint hier Verführung zum römischen Kaiserkult."] },
      { number: 2, heading: "Die Hure Babylon: 17,1-18", text: "Denn mit ihr haben die Könige der Erde Unzucht getrieben und vom Wein ihrer Hurerei wurden die Bewohner der Erde betrunken.", footnotes: ["Jes 23,17; Ez 27,33; Jer 25,15f"] },
      { number: 3, text: "Der Geist ergriff mich und der Engel entrückte mich in die Wüste. Dort sah ich eine Frau auf einem scharlachroten Tier sitzen, das über und über mit gotteslästerlichen Namen beschrieben war und sieben Köpfe und zehn Hörner hatte.", footnotes: ["Dan 7,7"] },
      { number: 4, text: "Die Frau war in Purpur und Scharlach gekleidet und mit Gold, Edelsteinen und Perlen geschmückt. Sie hielt einen goldenen Becher in der Hand, der mit dem abscheulichen Schmutz ihrer Hurerei gefüllt war.", footnotes: ["Jer 51,7"] },
      { number: 5, text: "Auf ihrer Stirn stand ein Name, ein geheimnisvoller Name: Babylon, die Große, die Mutter der Huren und aller Abscheulichkeiten der Erde.", footnotes: ["Dan 4,27"] },
      { number: 6, text: "Und ich sah, dass die Frau betrunken war vom Blut der Heiligen und vom Blut der Zeugen Jesu. Beim Anblick der Frau ergriff mich großes Erstaunen." },
      { number: 7, text: "Der Engel aber sagte zu mir: Warum bist du erstaunt? Ich will dir das Geheimnis der Frau enthüllen und das Geheimnis des Tieres mit den sieben Köpfen und zehn Hörnern, auf dem sie sitzt.", footnotes: ["7-18: Dieses Tier, das war und jetzt nicht ist und wieder da sein wird (V. 8), ist das schon im Kap. 13 geschilderte Tier (vgl. die Anmerkungen zu 13,1-3)."] },
      { number: 8, text: "Das Tier, das du gesehen hast, war einmal und ist jetzt nicht; es wird aber aus dem Abgrund heraufsteigen und dann ins Verderben gehen. Staunen werden die Bewohner der Erde, deren Namen seit der Erschaffung der Welt nicht im Buch des Lebens verzeichnet sind. Sie werden bei dem Anblick des Tieres staunen; denn es war einmal und ist jetzt nicht, wird aber wieder da sein.", footnotes: ["Dan 7,3; Ex 32,32f; Ps 69,29; Dan 12,1; Offb 3,5"] },
      { number: 9, text: "Hier braucht man Verstand und Kenntnis. Die sieben Köpfe bedeuten die sieben Berge, auf denen die Frau sitzt. Sie bedeuten auch sieben Könige.", footnotes: ["9-11: Die sieben Köpfe des Tieres werden sowohl auf die sieben Hügel Roms als auch auf die sieben «Könige» (römische Kaiser) gedeutet. Das Hauptinteresse gilt einem bestimmten Inhaber des römischen Kaisertums: dem achten Kaiser, der «einer von den sieben» ist und ins Verderben geht (V. 11), d. h. dem als wiedererstandener Nero angesehenen Kaiser Domitian."] },
      { number: 10, text: "Fünf sind bereits gefallen. Einer ist jetzt da, einer ist noch nicht gekommen; wenn er dann kommt, darf er nur kurze Zeit bleiben." },
      { number: 11, text: "Das Tier aber, das war und jetzt nicht ist, bedeutet einen achten König und ist doch einer von den sieben und wird ins Verderben gehen." },
      { number: 12, text: "Die zehn Hörner, die du gesehen hast, bedeuten zehn Könige, die noch nicht zur Herrschaft gekommen sind; sie werden aber königliche Macht für eine einzige Stunde erhalten, zusammen mit dem Tier.", footnotes: ["Dan 7,24", "12-18: Das Horn ist Symbol der Kraft. Bei den zehn Königen ist entweder an Verbündete Roms oder an die öfter genannten «Könige der Erde» (vgl. 1,5; 6,15; 16,14; 19,19; Ps 2,2, auch Dan 7,24 G) gedacht (zehn ist die Zahl der Vollkommenheit). Die Gerichtsvorgänge werden unter sachlichen, nicht unter zeitlichen Gesichtspunkten geschildert, und zwar nach alttestamentlichen Vorlagen (vgl. Ez 16,15-41; 23,11-35)."] },
      { number: 13, text: "Sie sind eines Sinnes und übertragen ihre Macht und Gewalt dem Tier." },
      { number: 14, text: "Sie werden mit dem Lamm Krieg führen, aber das Lamm wird sie besiegen. Denn es ist der Herr der Herren und der König der Könige. Bei ihm sind die Berufenen, Auserwählten und Treuen.", footnotes: ["Dtn 10,17; Dan 2,47; 1 Tim 6,15"] },
      { number: 15, text: "Und er sagte zu mir: Du hast die Gewässer gesehen, an denen die Hure sitzt; sie bedeuten Völker und Menschenmassen, Nationen und Sprachen.", footnotes: ["Jer 51,13; Dan 3,4"] },
      { number: 16, text: "Du hast die zehn Hörner und das Tier gesehen; sie werden die Hure hassen, ihr alles wegnehmen, bis sie nackt ist, werden ihr Fleisch fressen und sie im Feuer verbrennen." },
      { number: 17, text: "Denn Gott lenkt ihr Herz so, dass sie seinen Plan ausführen: Sie sollen einmütig handeln und ihre Herrschaft dem Tier übertragen, bis die Worte Gottes erfüllt sind." },
      { number: 18, text: "Die Frau aber, die du gesehen hast, ist die große Stadt, die die Herrschaft hat über die Könige der Erde.", footnotes: ["Ps 2,2"] },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Danach sah ich einen anderen Engel aus dem Himmel herabsteigen; er hatte große Macht und die Erde leuchtete auf von seiner Herrlichkeit.", footnotes: ["Ez 43,2", "1-3: Das bereits 14,8 angekündigte Gericht über Babylon wird nun Wirklichkeit. Zur Schilderung des Gerichts über gottfeindliche Städte im Alten Testament vgl. Jes 47,10 G (Babylon), Jes 23,15-17 (Tyrus) und Nah 3,4 (Ninive)."] },
      { number: 2, heading: "Die Ankündigung von Babylons Sturz: 18,1-8", text: "Und er rief mit gewaltiger Stimme: Gefallen, gefallen ist Babylon, die Große! Zur Wohnung von Dämonen ist sie geworden, zur Behausung aller unreinen Geister und zum Schlupfwinkel aller unreinen und abscheulichen Vögel.", footnotes: ["Jes 21,9; Dan 4,27; Jes 13,21; 34,11; Jer 50,39"] },
      { number: 3, text: "Denn vom Zornwein ihrer Unzucht haben alle Völker getrunken und die Könige der Erde haben mit ihr Unzucht getrieben. Durch die Fülle ihres Wohlstands sind die Kaufleute der Erde reich geworden.", footnotes: ["Jer 51,7; Jes 23,17; Ez 27,33"] },
      { number: 4, text: "Dann hörte ich eine andere Stimme vom Himmel her rufen: Verlass die Stadt, mein Volk, damit du nicht mitschuldig wirst an ihren Sünden und von ihren Plagen mitgetroffen wirst.", footnotes: ["Jes 48,20; 2 Kor 6,17"] },
      { number: 5, text: "Denn ihre Sünden haben sich bis zum Himmel aufgetürmt und Gott hat ihre Schandtaten nicht vergessen.", footnotes: ["Jer 51,9"] },
      { number: 6, text: "Zahlt ihr mit gleicher Münze heim, gebt ihr doppelt zurück, was sie getan hat. Mischt ihr den Becher, den sie gemischt hat, doppelt so stark.", footnotes: ["Jer 50,15.29; Ps 137,8"] },
      { number: 7, text: "Im gleichen Maß, wie sie in Prunk und Luxus lebte, lasst sie Qual und Trauer erfahren. Sie dachte bei sich: Ich throne als Königin, ich bin keine Witwe und werde keine Trauer kennen.", footnotes: ["Jes 47,7f"] },
      { number: 8, text: "Deshalb werden an einem einzigen Tag die Plagen über sie kommen, die für sie bestimmt sind: Tod, Trauer und Hunger. Und sie wird im Feuer verbrennen; denn stark ist der Herr, der Gott, der sie gerichtet hat.", footnotes: ["Jes 47,9; Jer 50,34", "«An einem einzigen Tag» ist wie «in einer einzigen Stunde» (VV. 10.17.19) symbolischer Ausdruck für das plötzliche Eintreffen des Gerichts."] },
      { number: 9, text: "Die Könige der Erde, die mit ihr gehurt und in Luxus gelebt haben, werden über sie weinen und klagen, wenn sie den Rauch der brennenden Stadt sehen.", footnotes: ["Ez 27,31-33; Jes 23,17", "9-19: Die Klagen «der Könige der Erde», der Kaufleute und der Seefahrer über die verödete Stadt haben ihr Vorbild in Ez 26,15 - 27,36."] },
      { number: 10, heading: "Die Klage über den Untergang Babylons: 18,9-24", text: "Sie bleiben in der Ferne stehen aus Angst vor ihrer Qual und sagen: Wehe! Wehe, du große Stadt Babylon, du mächtige Stadt! In einer einzigen Stunde ist das Gericht über dich gekommen.", footnotes: ["Dan 4,27; Ez 26,17"] },
      { number: 11, text: "Auch die Kaufleute der Erde weinen und klagen um sie, weil niemand mehr ihre Ware kauft:", footnotes: ["Ez 27,31.36"] },
      { number: 12, text: "Gold und Silber, Edelsteine und Perlen, feines Leinen, Purpur, Seide und Scharlach, wohlriechende Hölzer aller Art und alle möglichen Geräte aus Elfenbein, kostbarem Edelholz, Bronze, Eisen und Marmor;" },
      { number: 13, text: "auch Zimt und Balsam, Räucherwerk, Salböl und Weihrauch, Wein und Öl, feinstes Mehl und Weizen, Rinder und Schafe, Pferde und Wagen und sogar Menschen mit Leib und Seele.", footnotes: ["Ez 27,13"] },
      { number: 14, text: "Auch die Früchte, nach denen dein Herz begehrte, sind dir genommen. Und alles, was prächtig und glänzend war, hast du verloren; nie mehr wird man es finden." },
      { number: 15, text: "Die Kaufleute, die durch den Handel mit dieser Stadt reich geworden sind, werden aus Angst vor ihrer Qual in der Ferne stehen und sie werden weinen und klagen:", footnotes: ["Ez 27,31.36"] },
      { number: 16, text: "Wehe! Wehe, du große Stadt, bekleidet mit feinem Leinen, mit Purpur und Scharlach, geschmückt mit Gold, Edelsteinen und Perlen." },
      { number: 17, text: "In einer einzigen Stunde ist dieser ganze Reichtum dahin. Alle Kapitäne und Schiffsreisenden, die Matrosen und alle, die ihren Unterhalt auf See verdienen, machten schon in der Ferne Halt,", footnotes: ["Ez 27,27-34"] },
      { number: 18, text: "als sie den Rauch der brennenden Stadt sahen, und sie riefen: Wer konnte sich mit der großen Stadt messen?" },
      { number: 19, text: "Und sie streuten sich Staub auf den Kopf, sie schrien, weinten und klagten: Wehe! Wehe, du große Stadt, die mit ihren Schätzen alle reich gemacht hat, die Schiffe auf dem Meer haben. In einer einzigen Stunde ist sie verwüstet worden.", footnotes: ["Ez 26,19"] },
      { number: 20, text: "Freu dich über ihren Untergang, du Himmel - und auch ihr, Heilige, Apostel und Propheten, freut euch! Denn Gott hat euch an ihr gerächt.", footnotes: ["Jes 44,23; Dtn 32,43 G"] },
      { number: 21, text: "Dann hob ein gewaltiger Engel einen Stein auf, so groß wie ein Mühlstein; er warf ihn ins Meer und rief: So wird Babylon, die große Stadt, mit Wucht hinabgeworfen werden und man wird sie nicht mehr finden.", footnotes: ["Jer 51,63f; Dan 4,27; Ez 26,21", "21-23: Das symbolische Geschehen verweist auf die Endgültigkeit des vollzogenen Gerichts."] },
      { number: 22, text: "Die Musik von Harfenspielern und Sängern, von Flötenspielern und Trompetern hört man nicht mehr in dir. Einen kundigen Handwerker gibt es nicht mehr in dir. Das Geräusch des Mühlsteins hört man nicht mehr in dir.", footnotes: ["Jes 24,8; Ez 26,13; Jer 25,10"] },
      { number: 23, text: "Das Licht der Lampe scheint nicht mehr in dir. Die Stimme von Braut und Bräutigam hört man nicht mehr in dir. Deine Kaufleute waren die Großen der Erde, deine Zauberei verführte alle Völker.", footnotes: ["Jes 23,8; 47,9; Nah 3,4"] },
      { number: 24, text: "Aber in ihr war das Blut von Propheten und Heiligen und von allen, die auf der Erde hingeschlachtet worden sind.", footnotes: ["Jer 51,49"] },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Danach hörte ich etwas wie den lauten Ruf einer großen Schar im Himmel: Halleluja! / Das Heil und die Herrlichkeit und die Macht / ist bei unserm Gott.", footnotes: ["1-9: Die Triumphlieder der Himmelsbewohner (vgl. 18,20) preisen das gerechte Gericht über die Feinde Gottes («die große Hure») und das künftige Heil des Gottesvolkes. Das Heil wird unter dem Bild einer Hochzeit dargestellt (vgl. Mt 22,1-3; Mk 2,19; Joh 3,29; 2 Kor 11,2)."] },
      { number: 2, heading: "Jubel im Himmel: 19,1-10", text: "Seine Urteile sind wahr und gerecht. / Er hat die große Hure gerichtet, / die mit ihrer Unzucht die Erde verdorben hat. Er hat Rache genommen für das Blut seiner Knechte, / das an ihren Händen klebte.", footnotes: ["Ps 19,10; 2 Kön 9,7"] },
      { number: 3, text: "Noch einmal riefen sie: Halleluja! / Der Rauch der Stadt steigt auf in alle Ewigkeit.", footnotes: ["Jes 34,10"] },
      { number: 4, text: "Und die vierundzwanzig Ältesten und die vier Lebewesen fielen nieder vor Gott, der auf dem Thron sitzt, beteten ihn an und riefen: Amen, halleluja!", footnotes: ["Jes 6,1"] },
      { number: 5, text: "Und eine Stimme kam vom Thron her: Preist unsern Gott, all seine Knechte / und alle, die ihn fürchten, Kleine und Große!", footnotes: ["Ps 134,1; 115,13"] },
      { number: 6, text: "Da hörte ich etwas wie den Ruf einer großen Schar und wie das Rauschen gewaltiger Wassermassen und wie das Rollen mächtiger Donner: Halleluja! / Denn König geworden ist der Herr, unser Gott, / der Herrscher über die ganze Schöpfung.", footnotes: ["Ez 1,24; 43,2; Ps 93,1; Dan 7,14"] },
      { number: 7, text: "Wir wollen uns freuen und jubeln / und ihm die Ehre erweisen. Denn gekommen ist die Hochzeit des Lammes / und seine Frau hat sich bereit gemacht.", footnotes: ["Ps 118,24"] },
      { number: 8, text: "Sie durfte sich kleiden in strahlend reines Leinen. / Das Leinen bedeutet die gerechten Taten der Heiligen.", footnotes: ["Ps 45,14f"] },
      { number: 9, text: "Jemand sagte zu mir: Schreib auf: Selig, wer zum Hochzeitsmahl des Lammes eingeladen ist. Dann sagte er zu mir: Das sind zuverlässige Worte, es sind Worte Gottes.", footnotes: ["1,3"] },
      { number: 10, text: "Und ich fiel ihm zu Füßen, um ihn anzubeten. Er aber sagte zu mir: Tu das nicht! Ich bin ein Knecht wie du und deine Brüder, die das Zeugnis Jesu festhalten. Gott bete an! Das Zeugnis Jesu ist der Geist prophetischer Rede.", footnotes: ["22,9"] },
      { number: 11, text: "Dann sah ich den Himmel offen, und siehe, da war ein weißes Pferd, und der, der auf ihm saß, heißt «Der Treue und Wahrhaftige»; gerecht richtet er und führt er Krieg.", footnotes: ["Ez 1,1; Ps 96,13", "11-21: Die Vision vom Endkampf zwischen dem in die Macht Gottes eingesetzten Christus, dem Reiter auf dem weißen Pferd, und den gottfeindlichen Mächten schildert das endgültige Gericht über das Tier und den Lügenpropheten."] },
      { number: 12, heading: "Sieg über das Tier und seinen Propheten: 19,11-21", text: "Seine Augen waren wie Feuerflammen und auf dem Haupt trug er viele Diademe; und auf ihm stand ein Name, den er allein kennt.", footnotes: ["Dan 10,6"] },
      { number: 13, text: "Bekleidet war er mit einem blutgetränkten Gewand; und sein Name heißt «Das Wort Gottes».", footnotes: ["Jes 63,1f", "Das Blut, mit dem das Gewand Christi getränkt ist, ist das im Endkampf vergossene Blut der Feinde Gottes. Der Name «Das Wort Gottes» verweist auf die durchschlagende Kraft des ausgesprochenen Wortes, des Willens Gottes (vgl. V. 15; 2 Thess 2,8); dieser Name steht in Beziehung zur Bezeichnung Christi als «das Wort» in Joh 1,1-14."] },
      { number: 14, text: "Die Heere des Himmels folgten ihm auf weißen Pferden; sie waren in reines, weißes Leinen gekleidet." },
      { number: 15, text: "Aus seinem Mund kam ein scharfes Schwert; mit ihm wird er die Völker schlagen. Und er herrscht über sie mit eisernem Zepter, und er tritt die Kelter des Weines, des rächenden Zornes Gottes, des Herrschers über die ganze Schöpfung.", footnotes: ["Jes 11,4; 49,2; Ps 2,9 G; Jes 63,3"] },
      { number: 16, text: "Auf seinem Gewand und auf seiner Hüfte trägt er den Namen: «König der Könige und Herr der Herren».", footnotes: ["Dan 2,47; Dtn 10,17"] },
      { number: 17, text: "Dann sah ich einen Engel, der in der Sonne stand. Er rief mit lauter Stimme allen Vögeln zu, die hoch am Himmel flogen: Kommt her! Versammelt euch zum großen Mahl Gottes.", footnotes: ["Ez 39,4.17-20"] },
      { number: 18, text: "Fresst Fleisch von Königen, von Heerführern und von Helden, Fleisch von Pferden und ihren Reitern, Fleisch von allen, von Freien und Sklaven, von Großen und Kleinen!" },
      { number: 19, text: "Dann sah ich das Tier und die Könige der Erde und ihre Heere versammelt, um mit dem Reiter und seinem Heer Krieg zu führen.", footnotes: ["Ps 2,2"] },
      { number: 20, text: "Aber das Tier wurde gepackt und mit ihm der falsche Prophet; er hatte vor seinen Augen Zeichen getan und dadurch alle verführt, die das Kennzeichen des Tieres angenommen und sein Standbild angebetet hatten. Bei lebendigem Leib wurden beide in den See von brennendem Schwefel geworfen.", footnotes: ["Dan 7,11; Jes 30,33", "Der brennende Schwefelsee ist Bild für den Ort ewiger Peinigung. Dieser Ort wurde im Judentum «Gehenna» (Hölle) genannt (vgl. Dtn 32,22; Mt 11,23; 23,33; Lk 12,5; 2 Petr 2,4)."] },
      { number: 21, text: "Die Übrigen wurden getötet mit dem Schwert, das aus dem Mund des Reiters kam; und alle Vögel fraßen sich satt an ihrem Fleisch.", footnotes: ["Ez 39,17.20"] },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Dann sah ich einen Engel vom Himmel herabsteigen; auf seiner Hand trug er den Schlüssel zum Abgrund und eine schwere Kette.", footnotes: ["Zu «Abgrund» vgl. die Anmerkung zu 9,1f."] },
      { number: 2, heading: "Die Tausendjährige Herrschaft: 20,1-6", text: "Er überwältigte den Drachen, die alte Schlange - das ist der Teufel oder der Satan -, und er fesselte ihn für tausend Jahre.", footnotes: ["Gen 3,1; Sach 3,1; Ijob 1,6", "2f: Ob die zeitliche Befristung der Fesselung des Satans (vgl. Jes 24,21f) auf tausend Jahre als Hinweis auf einen Zeitabschnitt oder als Zeichen für die Einschränkung der Macht Satans zu deuten ist, lässt sich nicht eindeutig entscheiden. Jedenfalls aber ist die Zeitangabe selbst nicht wörtlich zu verstehen."] },
      { number: 3, text: "Er warf ihn in den Abgrund, verschloss diesen und drückte ein Siegel darauf, damit der Drache die Völker nicht mehr verführen konnte, bis die tausend Jahre vollendet sind. Danach muss er für kurze Zeit freigelassen werden." },
      { number: 4, text: "Dann sah ich Throne; und denen, die darauf Platz nahmen, wurde das Gericht übertragen. Ich sah die Seelen aller, die enthauptet worden waren, weil sie an dem Zeugnis Jesu und am Wort Gottes festgehalten hatten. Sie hatten das Tier und sein Standbild nicht angebetet und sie hatten das Kennzeichen nicht auf ihrer Stirn und auf ihrer Hand anbringen lassen. Sie gelangten zum Leben und zur Herrschaft mit Christus für tausend Jahre.", footnotes: ["Dan 7,9.22 G; 1 Kor 6,2", "4-6: Die Deutung dieser Vision bereitete seit je besondere Schwierigkeiten. Nach wörtlichem Verständnis würden der himmlische Christus und die bis zum Tod getreuen Christen tausend Jahre auf dieser Erde herrschen (vgl. 5,10). Möglicherweise aber will Offb nur den besonderen jenseitigen Lohn hervorheben, der den Märtyrern zuteil werden soll (vgl. Dan 7,9.22.27 G). Jedenfalls hat der Verfasser die Märtyrer nicht in das allgemeine Gericht einbezogen, von dem er in den VV. 11-15 berichtet (vgl. 6,9-11)."] },
      { number: 5, text: "Die übrigen Toten kamen nicht zum Leben, bis die tausend Jahre vollendet waren. Das ist die erste Auferstehung." },
      { number: 6, text: "Selig und heilig, wer an der ersten Auferstehung teilhat. Über solche hat der zweite Tod keine Gewalt. Sie werden Priester Gottes und Christi sein und tausend Jahre mit ihm herrschen.", footnotes: ["1,3; Jes 61,6"] },
      { number: 7, text: "Wenn die tausend Jahre vollendet sind, wird der Satan aus seinem Gefängnis freigelassen werden.", footnotes: ["7-10: Die Abfolge der Visionen 20,4-6 und 20,7-10 ist wohl durch Ez 33 - 37 und 38 - 39 veranlasst. Dort folgt auf die Darstellung des messianischen Reiches der Ansturm der Heere des Fürsten Gog aus dem Land Magog. In Offb sind die Namen Gog und Magog symbolische Bezeichnungen für alle heidnischen Völker. - Jerusalem ist Sinnbild der Kirche. - Zum Schwefelsee vgl. die Anmerkung zu 19,20."] },
      { number: 8, heading: "Der endgültige Sieg über den Satan: 20,7-10", text: "Er wird ausziehen, um die Völker an den vier Ecken der Erde, den Gog und den Magog, zu verführen und sie zusammenzuholen für den Kampf; sie sind so zahlreich wie die Sandkörner am Meer.", footnotes: ["Ez 38,6; Jes 11,12; Ez 7,2; 38,2"] },
      { number: 9, text: "Sie schwärmten aus über die weite Erde und umzingelten das Lager der Heiligen und Gottes geliebte Stadt. Aber Feuer fiel vom Himmel und verzehrte sie.", footnotes: ["Hab 1,6 G; Ps 87,2; 2 Kön 1,10; Ez 39,6"] },
      { number: 10, text: "Und der Teufel, ihr Verführer, wurde in den See von brennendem Schwefel geworfen, wo auch das Tier und der falsche Prophet sind. Tag und Nacht werden sie gequält, in alle Ewigkeit.", footnotes: ["Ez 38,22"] },
      { number: 11, text: "Dann sah ich einen großen weißen Thron und den, der auf ihm saß; vor seinem Anblick flohen Erde und Himmel und es gab keinen Platz mehr für sie.", footnotes: ["Jes 6,1; Ps 114,3.7; Dan 2,35 G", "11-15: Die letzte Gerichtsvision handelt vom Gericht über die («übrigen») Toten. Sie erreicht ihren Höhepunkt in der Vernichtung der als Person vorgestellten Todesmacht (V. 14)."] },
      { number: 12, heading: "Das Gericht über alle Toten: 20,11-15", text: "Ich sah die Toten vor dem Thron stehen, die Großen und die Kleinen. Und Bücher wurden aufgeschlagen; auch das Buch des Lebens wurde aufgeschlagen. Die Toten wurden nach ihren Werken gerichtet, nach dem, was in den Büchern aufgeschrieben war.", footnotes: ["Dan 7,10; Ps 69,29; Offb 3,5; Ps 28,4"] },
      { number: 13, text: "Und das Meer gab die Toten heraus, die in ihm waren; und der Tod und die Unterwelt gaben ihre Toten heraus, die in ihnen waren. Sie wurden gerichtet, jeder nach seinen Werken.", footnotes: ["Hos 13,14; Ps 28,4"] },
      { number: 14, text: "Der Tod und die Unterwelt aber wurden in den Feuersee geworfen. Das ist der zweite Tod: der Feuersee.", footnotes: ["Hos 13,14; Jes 25,8 G; 1 Kor 15,26"] },
      { number: 15, text: "Wer nicht im Buch des Lebens verzeichnet war, wurde in den Feuersee geworfen.", footnotes: ["Ex 32,32f; Dan 12,1; Ps 69,29; Offb 3,5"] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Dann sah ich einen neuen Himmel und eine neue Erde; denn der erste Himmel und die erste Erde sind vergangen, auch das Meer ist nicht mehr.", footnotes: ["Jes 65,17; 66,22", "Das Verschwinden der Erde und des Himmels (vgl. 20,11) und das Kommen «eines neuen Himmels und einer neuen Erde» (Jes 65,17; 66,12) sind Hinweis auf die völlig andersartigen Bedingungen des Daseins der Erlösten."] },
      { number: 2, heading: "Die neue Welt Gottes: 21,1 - 22,5 | Gottes Wohnen unter den Menschen: 21,1-8", text: "Ich sah die heilige Stadt, das neue Jerusalem, von Gott her aus dem Himmel herabkommen; sie war bereit wie eine Braut, die sich für ihren Mann geschmückt hat.", footnotes: ["Jes 52,1; Gal 4,26; Hebr 12,22; Jes 61,10"] },
      { number: 3, text: "Da hörte ich eine laute Stimme vom Thron her rufen: Seht, die Wohnung Gottes unter den Menschen! Er wird in ihrer Mitte wohnen, und sie werden sein Volk sein; und er, Gott, wird bei ihnen sein.", footnotes: ["Lev 26,11f; Ez 37,27; Sach 2,14; Jes 8,8", "Nach anderen Textzeugen: sie werden seine Völker sein."] },
      { number: 4, text: "Er wird alle Tränen von ihren Augen abwischen: Der Tod wird nicht mehr sein, keine Trauer, keine Klage, keine Mühsal. Denn was früher war, ist vergangen.", footnotes: ["Jes 25,8; 1 Kor 15,26; Jes 35,10"] },
      { number: 5, text: "Er, der auf dem Thron saß, sprach: Seht, ich mache alles neu. Und er sagte: Schreib es auf, denn diese Worte sind zuverlässig und wahr.", footnotes: ["Jes 6,1; 43,19 G; 2 Kor 5,17", "5-8: Wie in 1,8 spricht Gott hier selbst; seine Worte bestätigen die Aussage des Sehers."] },
      { number: 6, text: "Er sagte zu mir: Sie sind in Erfüllung gegangen. Ich bin das Alpha und das Omega, der Anfang und das Ende. Wer durstig ist, den werde ich umsonst aus der Quelle trinken lassen, aus der das Wasser des Lebens strömt.", footnotes: ["Jes 55,1; Sach 14,8"] },
      { number: 7, text: "Wer siegt, wird dies als Anteil erhalten: Ich werde sein Gott sein und er wird mein Sohn sein.", footnotes: ["Röm 8,17; 2 Sam 7,14"] },
      { number: 8, text: "Aber die Feiglinge und Treulosen, die Befleckten, die Mörder und Unzüchtigen, die Zauberer, Götzendiener und alle Lügner - ihr Los wird der See von brennendem Schwefel sein. Dies ist der zweite Tod.", footnotes: ["22,15; Mt 15,19; Ez 38,22"] },
      { number: 9, text: "Und es kam einer von den sieben Engeln, die die sieben Schalen mit den sieben letzten Plagen getragen hatten. Er sagte zu mir: Komm, ich will dir die Braut zeigen, die Frau des Lammes.", footnotes: ["Lev 26,21 G", "9f: Auch hier werden «die Braut, die Frau des Lammes» (Gegenbild der großen Hure) und «die heilige Stadt Jerusalem» (Gegenbild Babylons) miteinander gleichgesetzt."] },
      { number: 10, heading: "Das neue Jerusalem: 21,9 - 22,5", text: "Da entrückte er mich in der Verzückung auf einen großen, hohen Berg und zeigte mir die heiligeStadt Jerusalem, wie sie von Gott her aus dem Himmel herabkam,", footnotes: ["Ez 40,2; Jes 52,1"] },
      { number: 11, text: "erfüllt von der Herrlichkeit Gottes. Sie glänzte wie ein kostbarer Edelstein, wie ein kristallklarer Jaspis.", footnotes: ["Jes 60,1f"] },
      { number: 12, text: "Die Stadt hat eine große und hohe Mauer mit zwölf Toren und zwölf Engeln darauf. Auf die Tore sind Namen geschrieben: die Namen der zwölf Stämme der Söhne Israels.", footnotes: ["Ez 48,31; Ex 28,21"] },
      { number: 13, text: "Im Osten hat die Stadt drei Tore und im Norden drei Tore und im Süden drei Tore und im Westen drei Tore.", footnotes: ["Ez 48,31-35"] },
      { number: 14, text: "Die Mauer der Stadt hat zwölf Grundsteine; auf ihnen stehen die zwölf Namen der zwölf Apostel des Lammes." },
      { number: 15, text: "Und der Engel, der zu mir sprach, hatte einen goldenen Messstab, mit dem die Stadt, ihre Tore und ihre Mauer gemessen wurden.", footnotes: ["Ez 40,3.5"] },
      { number: 16, text: "Die Stadt war viereckig angelegt und ebenso lang wie breit. Er maß die Stadt mit dem Messstab; ihre Länge, Breite und Höhe sind gleich: zwölftausend Stadien.", footnotes: ["Ez 43,16"] },
      { number: 17, text: "Und er maß ihre Mauer; sie ist hundertvierundvierzig Ellen hoch nach Menschenmaß, das der Engel benutzt hatte.", footnotes: ["Ez 40,5"] },
      { number: 18, text: "Ihre Mauer ist aus Jaspis gebaut und die Stadt ist aus reinem Gold, wie aus reinem Glas.", footnotes: ["Jes 54,11f"] },
      { number: 19, text: "Die Grundsteine der Stadtmauer sind mit edlen Steinen aller Art geschmückt; der erste Grundstein ist ein Jaspis, der zweite ein Saphir, der dritte ein Chalzedon, der vierte ein Smaragd,", footnotes: ["(19f) Ez 28,13 G"] },
      { number: 20, text: "der fünfte ein Sardonyx, der sechste ein Sardion, der siebte ein Chrysolith, der achte ein Beryll, der neunte ein Topas, der zehnte ein Chrysopras, der elfte ein Hyazinth, der zwölfte ein Amethyst." },
      { number: 21, text: "Die zwölf Tore sind zwölf Perlen; jedes der Tore besteht aus einer einzigen Perle. Die Straße der Stadt ist aus reinem Gold, wie aus klarem Glas." },
      { number: 22, text: "Einen Tempel sah ich nicht in der Stadt. Denn der Herr, ihr Gott, der Herrscher über die ganze Schöpfung, ist ihr Tempel, er und das Lamm." },
      { number: 23, text: "Die Stadt braucht weder Sonne noch Mond, die ihr leuchten. Denn die Herrlichkeit Gottes erleuchtet sie und ihre Leuchte ist das Lamm.", footnotes: ["Jes 60,1.19"] },
      { number: 24, text: "Die Völker werden in diesem Licht einhergehen und die Könige der Erde werden ihre Pracht in die Stadt bringen.", footnotes: ["Jes 60,3.11; Ps 72,10"] },
      { number: 25, text: "Ihre Tore werden den ganzen Tag nicht geschlossen - Nacht wird es dort nicht mehr geben.", footnotes: ["Sach 14,7"] },
      { number: 26, text: "Und man wird die Pracht und die Kostbarkeiten der Völker in die Stadt bringen." },
      { number: 27, text: "Aber nichts Unreines wird hineinkommen, keiner, der Gräuel verübt und lügt. Nur die, die im Lebensbuch des Lammes eingetragen sind, werden eingelassen.", footnotes: ["Jes 52,1; 4,3; Dan 12,1; Ps 69,29; Offb 3,5"] },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Und er zeigte mir einen Strom, das Wasser des Lebens, klar wie Kristall; er geht vom Thron Gottes und des Lammes aus.", footnotes: ["Gen 2,10; Sach 14,8"] },
      { number: 2, text: "Zwischen der Straße der Stadt und dem Strom, hüben und drüben, stehen Bäume des Lebens. Zwölfmal tragen sie Früchte, jeden Monat einmal; und die Blätter der Bäume dienen zur Heilung der Völker.", footnotes: ["Ez 47,12; Gen 2,9"] },
      { number: 3, text: "Es wird nichts mehr geben, was der Fluch Gottes trifft. Der Thron Gottes und des Lammes wird in der Stadt stehen und seine Knechte werden ihm dienen.", footnotes: ["Sach 14,11"] },
      { number: 4, text: "Sie werden sein Angesicht schauen und sein Name ist auf ihre Stirn geschrieben.", footnotes: ["Ps 17,15"] },
      { number: 5, text: "Es wird keine Nacht mehr geben und sie brauchen weder das Licht einer Lampe noch das Licht der Sonne. Denn der Herr, ihr Gott, wird über ihnen leuchten und sie werden herrschen in alle Ewigkeit.", footnotes: ["Jes 60,19; Dan 7,18.27"] },
      { number: 6, text: "Und der Engel sagte zu mir: Diese Worte sind zuverlässig und wahr. Gott, der Herr über den Geist der Propheten, hat seinen Engel gesandt, um seinen Knechten zu zeigen, was bald geschehen muss.", footnotes: ["Dan 2,28"] },
      { number: 7, heading: "Zeugnis und Abschließende Mahnung des Sehers: 22,6-21", text: "Siehe, ich komme bald. Selig, wer an den prophetischen Worten dieses Buches festhält.", footnotes: ["Sach 2,14; Offb 1,3; 22,12.20"] },
      { number: 8, text: "Ich, Johannes, habe dies gehört und gesehen. Und als ich es hörte und sah, fiel ich dem Engel, der mir dies gezeigt hatte, zu Füßen, um ihn anzubeten." },
      { number: 9, text: "Da sagte er zu mir: Tu das nicht! Ich bin nur ein Knecht wie du und deine Brüder, die Propheten, und wie alle, die sich an die Worte dieses Buches halten. Gott bete an!", footnotes: ["19,10"] },
      { number: 10, text: "Und er sagte zu mir: Versiegle dieses Buch mit seinen prophetischen Worten nicht! Denn die Zeit ist nahe.", footnotes: ["Dan 12,4.9"] },
      { number: 11, text: "Wer Unrecht tut, tue weiter Unrecht, der Unreine bleibe unrein, der Gerechte handle weiter gerecht und der Heilige strebe weiter nach Heiligkeit.", footnotes: ["Dan 12,10"] },
      { number: 12, text: "Siehe, ich komme bald und mit mir bringe ich den Lohn und ich werde jedem geben, was seinem Werk entspricht.", footnotes: ["Sach 2,14; Jes 40,10 G; Ps 28,4"] },
      { number: 13, text: "Ich bin das Alpha und das Omega, der Erste und der Letzte, der Anfang und das Ende.", footnotes: ["Jes 44,6; Offb 1,17"] },
      { number: 14, text: "Selig, wer sein Gewand wäscht: Er hat Anteil am Baum des Lebens, und er wird durch die Tore in die Stadt eintreten können.", footnotes: ["1,3; Gen 2,9; 3,22"] },
      { number: 15, text: "Draußen bleiben die «Hunde» und die Zauberer, die Unzüchtigen und die Mörder, die Götzendiener und jeder, der die Lüge liebt und tut.", footnotes: ["21,8; Mt 15,19"] },
      { number: 16, text: "Ich, Jesus, habe meinen Engel gesandt als Zeugen für das, was die Gemeinden betrifft. Ich bin die Wurzel und der Stamm Davids, der strahlende Morgenstern.", footnotes: ["Jes 11,1.10; Num 24,17"] },
      { number: 17, text: "Der Geist und die Braut aber sagen: Komm! Wer hört, der rufe: Komm! Wer durstig ist, der komme. Wer will, empfange umsonst das Wasser des Lebens.", footnotes: ["Jes 55,1; Sach 14,8"] },
      { number: 18, text: "Ich bezeuge jedem, der die prophetischen Worte dieses Buches hört: Wer etwas hinzufügt, dem wird Gott die Plagen zufügen, von denen in diesem Buch geschrieben steht.", footnotes: ["Dtn 4,2; 13,1; 29,20"] },
      { number: 19, text: "Und wer etwas wegnimmt von den prophetischen Worten dieses Buches, dem wird Gott seinen Anteil am Baum des Lebens und an der heiligen Stadt wegnehmen, von denen in diesem Buch geschrieben steht.", footnotes: ["Dtn 4,2; Gen 2,9; 3,22"] },
      { number: 20, text: "Er, der dies bezeugt, spricht: Ja, ich komme bald. - Amen. Komm, Herr Jesus!", footnotes: ["3,11; 22,7.12; 1 Kor 16,22"] },
      { number: 21, text: "Die Gnade des Herrn Jesus sei mit allen!", footnotes: ["Hebr 13,25"] },
    ] },
  ],
};
