import { Book } from "@/lib/types";

export const revelation: Book = {
  id: "revelation", name: "Offenbarung", shortName: "Offb", testament: "new",
  introduction: `Was Jesus Johannes offenbarte

Anfang der 90er Jahre begann Kaiser Domitian das Denunziantentum im Römischen Reich zu fördern und ließ die Christen systematisch verfolgen. So musste sich um 95 n.Chr. ein Enkel jenes Judas, der den Judasbrief verfasst hatte und ein Halbbruder von Jesus war, vor Domitian verantworten. Zur gleichen Zeit wurde der Apostel Johannes auf die Insel Patmos verbannt.

Dort empfing er eine besondere Offenbarung seines Herrn Jesus Christus und schrieb auf, was ihn Gott sehen und hören ließ. Anschließend schickte er das Buch als Rundschreiben an sieben ausgewählte Gemeinden in Kleinasien.

Diese Offenbarung hat als letztes inspiriertes Buch der Bibel eine besondere Bedeutung, weil in ihm die Linien der Heilsgeschichte, die im 1. Buch Moses beginnen, zusammengeführt werden.

**Jesus zeigte Johannes den Zustand sieben typischer Gemeinden und das, was in Zukunft noch geschehen wird.**`,
  chapters: [
    { number: 1, verses: [
      { number: 1, text: "In diesem Buch enthüllt Jesus Christus, was Gott ihm für seine Diener anvertraut hat. Sie sollten wissen, was bald geschehen muss. Deshalb sandte er seinen Engel und ließ es durch ihn seinem Diener Johannes zukommen.", heading: "Für wen diese prophetischen Worte bestimmt sind" },
      { number: 2, text: "Dieser Johannes berichtet nun alles genauso, wie es ihm gezeigt worden ist und wie er es als Wort Gottes von Jesus Christus empfangen hat." },
      { number: 3, text: "Glücklich, wer diese prophetischen Worte liest, und alle, die sie hören und danach handeln. Denn schon bald wird sich alles erfüllen." },
      { number: 4, text: "Johannes an die sieben Gemeinden in der Asia*: Gnade und Frieden wünsche ich euch von dem, der immer gegenwärtig ist, der schon immer war und der kommen wird, und von den sieben Geistern vor seinem Thron", footnotes: ["Asia. Römische Provinz im westlichen Teil Kleinasiens."] },
      { number: 5, text: "und von Jesus Christus, dem glaubwürdigen Zeugen für diese Dinge, der als Erster von den Toten zu einem unvergänglichen Leben auferstand und Herr ist über die Herrscher der Erde. Ihm, der uns liebt und uns durch sein Blut von unseren Sünden gereinigt hat;" },
      { number: 6, text: "ihm, der uns zu einem Königsvolk* gemacht hat, zu Priestern für seinen Gott und Vater: Ihm sei Ehre und Macht für immer und ewig! Amen.", footnotes: ["Königsvolk. Ein Volk, dessen König Jesus ist. Siehe auch Offenbarung 5,10.", "Amen. Hebräisch: Es werde wahr! Oder: So sei es!"] },
      { number: 7, text: "Passt auf! Mit den Wolken wird er wiederkommen. Alle werden ihn sehen, auch die, die ihn durchbohrt haben! Sein Anblick wird alle Völker auf der Erde in schmerzliche Trauer versetzen. Das ist gewiss! Amen." },
      { number: 8, text: "„Ich bin das Alpha und das Omega, der Ursprung und das Ziel“, sagt Gott, der Herr, der immer gegenwärtig ist, der immer war und der kommen wird, der Allmächtige." },
      { number: 9, text: "Ich, Johannes, euer Bruder, teile mit euch die Bedrängnis, aber auch den Anteil an Gottes Reich und das geduldige Warten darauf, denn wir sind ja mit Jesus vereint. Und weil ich das Wort Gottes verkündige und für die Botschaft von Jesus eintrete, bin ich auf der Insel Patmos* ‹verbannt›.", heading: "Wie Johannes den Auftrag bekam", footnotes: ["Patmos. Felsige Insel in der Ägäis, 90 km südwestlich von Ephesus, 12 km lang und 7 km breit."] },
      { number: 10, text: "An einem ‹Sonntag*, dem› Tag des Herrn, wurde ich vom Geist Gottes ergriffen: Ich hörte hinter mir eine laute Stimme, die wie eine Fanfare klang", footnotes: ["einem Sonntag. Hinzufügung, weil von frühester nachapostolischer Zeit an der erste Tag der Woche als Tag des Herrn bezeichnet wurde. Vergleiche auch Johannes 20,19; Apostelgeschichte 20,7; 1. Korinther 16,2."] },
      { number: 11, text: "und mir befahl: „Schreibe das, was du siehst, in ein Buch und schicke es an die sieben Gemeinden in Ephesus, Smyrna, Pergamon, Thyatira, Sardes, Philadelphia und Laodizea.“" },
      { number: 12, text: "Als ich mich umdrehte, um zu sehen, wer da mit mir sprach, sah ich sieben goldene Leuchter" },
      { number: 13, text: "und in deren Mitte jemanden, der aussah wie der Menschensohn*. Er trug ein Gewand, das bis zu seinen Füßen reichte, und ein breites goldenes Band um die Brust.", footnotes: ["Menschensohn ist eine von Jesus bevorzugte Selbstbezeichnung. Er knüpft damit an Daniel 7,13 an, wo der zukünftige Herrscher des Gottesreiches angekündigt wird."] },
      { number: 14, text: "Das Haar auf seinem Kopf war weiß wie schneeweiße Wolle. Seine Augen brannten wie lodernde Flammen." },
      { number: 15, text: "Seine Füße glänzten wie Golderz, das im Schmelzofen glüht, und seine Stimme klang wie das Donnern der Brandung." },
      { number: 16, text: "Sieben Sterne hielt er in seiner rechten Hand und aus seinem Mund kam ein scharfes, auf beiden Seiten geschliffenes Schwert. Sein Gesicht leuchtete wie die Sonne in ihrem höchsten Stand." },
      { number: 17, text: "Als ich ihn sah, fiel ich wie tot vor seine Füße. Aber er legte seine rechte Hand auf mich und sagte: „Hab keine Angst! Ich bin der Erste und der Letzte" },
      { number: 18, text: "und der Lebendige. Ich war tot, aber jetzt lebe ich in alle Ewigkeit und habe die Schlüssel für den Tod und sein Reich*.", footnotes: ["Das Neue Testament meint damit kein neutrales Totenreich, sondern den Todeszustand, der für Ungläubige schon vor dem Endgericht Höllenqual bedeutet (Lukas 16,23)."] },
      { number: 19, text: "Schreib nun auf, was du gesehen hast, was jetzt geschieht und was danach geschehen wird." },
      { number: 20, text: "Ich will dir erklären, was das Geheimnis der sieben Sterne ist, die du in meiner rechten Hand gesehen hast, und was die sieben goldenen Leuchter bedeuten: Die sieben Sterne sind die Engel* der sieben Gemeinden, und die sieben Leuchter sind die sieben Gemeinden.“", footnotes: ["Engel. Oder: Boten. Entweder handelt es sich um Boten der Gemeinden, die bei Johannes waren oder zu ihm kommen würden, oder um himmlische Repräsentanten der genannten Gemeinden oder der Begriff ist ein symbolischer Ausdruck für die jeweilige Gemeinde selbst."] },
    ] },
    { number: 2, verses: [
      { number: 1, text: "„Schreib an den Engel der Gemeinde in Ephesus*: Der, der die sieben Sterne in seiner rechten Hand hält und zwischen den sieben goldenen Leuchtern umhergeht, lässt dir sagen:", heading: "An die Gemeinde in Ephesus", footnotes: ["Ephesus war die Hauptstadt der Provinz Asia und zweitgrößte Stadt des römischen Reiches. Der reiche Handelsknotenpunkt lag etwa 5 km vom Meer entfernt am Fluss Kaystros, auf dem man damals bis in den Hafen der Stadt segeln konnte. Berühmt war Ephesus für seinen Artemis-Tempel, der zu den sieben Weltwundern zählte."] },
      { number: 2, text: "Ich kenne dein Tun, deine Mühe und Standhaftigkeit. Ich weiß auch, dass du niemand ertragen kannst, der Böses tut. Du hast die, die sich als Apostel ausgeben, geprüft und sie als Lügner entlarvt." },
      { number: 3, text: "Du bist standhaft und hast viel ertragen, weil du dich zu meinem Namen bekennst. Du bist nicht müde geworden." },
      { number: 4, text: "Doch den einen Vorwurf muss ich dir machen: Du hast deine anfängliche Liebe nicht mehr!" },
      { number: 5, text: "Denk einmal darüber nach, wie weit du davon abgekommen bist! Ändere deine Einstellung und handle so wie am Anfang! Wenn aber nicht, dann gehe ich gegen dich vor und stoße deinen Leuchter von seinem Platz weg." },
      { number: 6, text: "Doch es spricht für dich, dass du die Taten der Nikolaïten* genauso verabscheust wie ich.", footnotes: ["Nikolaïten. Anhänger einer frühchristlichen Irrlehre, die freizügig okkulte und sexuelle Praktiken ihrer ungläubigen Umwelt mit dem Glauben verbinden wollten."] },
      { number: 7, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt! Wer den Kampf besteht, dem gebe ich vom Baum des Lebens zu essen, der im Paradies Gottes steht.“" },
      { number: 8, text: "„Schreib an den Engel der Gemeinde in Smyrna*: Der Erste und der Letzte, der tot war und wieder lebendig wurde, lässt dir sagen:", heading: "An die Gemeinde in Smyrna", footnotes: ["Smyrna. Die Stadt lag etwa 55 km nördlich von Ephesus und war für ihre Schönheit berühmt. Das heutige Izmir war damals eine wohlhabende Stadt und stark mit dem römischen Kaiserkult verbunden. Die große jüdische Bevölkerungsgruppe war der Gemeinde feindlich gesonnen."] },
      { number: 9, text: "Ich weiß von deiner Bedrängnis und Armut – obwohl du eigentlich reich bist! Ich weiß auch, wie bösartig du von Leuten verleumdet wirst, die behaupten, Juden zu sein, das aber nicht sind, sondern eine Synagoge des Satans." },
      { number: 10, text: "Hab keine Angst vor dem Leiden, das dir bevorsteht! Der Teufel wird einige von euch ins Gefängnis bringen, um euch zu versuchen. Zehn Tage lang werdet ihr bedrängt sein. Bleib mir treu bis zum Tod! Dann gebe ich dir den Siegeskranz des Lebens." },
      { number: 11, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt! Wer den Kampf besteht, dem wird der zweite Tod nichts anhaben können.“" },
      { number: 12, text: "„Schreib an den Engel der Gemeinde in Pergamon*: Der, der das scharfe zweischneidige Schwert hat, lässt dir sagen:", heading: "An die Gemeinde in Pergamon", footnotes: ["Pergamon. Die Stadt lag ungefähr 80 km nördlich von Smyrna und war auf einem kegelförmigen Hügel erbaut, der sich 300 m über dem umliegenden Tal erhob."] },
      { number: 13, text: "Ich weiß, dass du dort wohnst, wo der Thron des Satans* steht. Trotzdem hast du dich zu meinem Namen bekannt und den Glauben an mich nicht verleugnet, auch damals nicht, als mein treuer Zeuge Antipas in eurer Stadt, dieser Hochburg Satans, ermordet wurde.", footnotes: ["Thron des Satans. Pergamon war das Zentrum des Kaiserkultes in der Provinz Asia. Auf der Akropolis stand der berühmte Zeusaltar, der sich heute im Pergamonmuseum in Berlin befindet. Außerdem besaß die Stadt ein Heiligtum des Asklepios Soter, des Gottes der Heilkunst. Welcher dieser Tempel oder Altäre mit dem Thron Satans gemeint ist, bleibt unsicher."] },
      { number: 14, text: "Doch den einen Vorwurf muss ich dir machen: Du duldest Leute in deiner Mitte, die an der Lehre Bileams* festhalten. Bileam hatte Balak gezeigt, wie er die Israeliten zu Fall bringen könnte. Er verführte sie zum Essen von Opferfleisch, das den Götzen geweiht war, und zu sexueller Zügellosigkeit.", footnotes: ["Bileam. Vergleiche die Geschichte Bileams in 4. Mose 22-24 und besonders 4. Mose 25,1-3 und 31,16."] },
      { number: 15, text: "So gibt es auch bei euch Leute, die den Lehren der Nikolaïten folgen." },
      { number: 16, text: "Kehr um! Sonst gehe ich sehr schnell gegen dich vor und bekämpfe diese Leute mit dem Schwert aus meinem Mund." },
      { number: 17, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt! Wer den Kampf besteht, dem werde ich von dem Manna zu essen geben, das jetzt noch verborgen ist. Und ich werde ihm einen weißen Stein geben, auf dem ein neuer Name eingraviert sein wird, den nur der kennt, der ihn empfängt.“" },
      { number: 18, text: "„Schreib an den Engel der Gemeinde in Thyatira*: Der Sohn Gottes, dessen Augen wie lodernde Flammen brennen und dessen Füße wie leuchtendes Gold glänzen, lässt dir sagen:", heading: "An die Gemeinde in Thyatira", footnotes: ["Thyatira. Handels- und Industriestadt mit Färbereien, Textilproduktion und Kupferverarbeitung, 60 km südöstlich von Pergamon."] },
      { number: 19, text: "Ich kenne dein Tun, dein Lieben, deinen Glauben, dein Dienen und deine Geduld. Ich weiß auch, dass du heute noch mehr tust als früher." },
      { number: 20, text: "Doch den einen Vorwurf muss ich dir machen: Du unternimmst nichts gegen diese Isebel, die sich als Prophetin ausgibt. Und dabei verführt sie mit ihrer Lehre meine Leute zu sexueller Zügellosigkeit und zum Essen von Götzenopferfleisch." },
      { number: 21, text: "Ich habe ihr Zeit gelassen, ihre Einstellung zu ändern. Doch sie weigert sich, ihr lasterhaftes Leben aufzugeben." },
      { number: 22, text: "Darum werfe ich sie jetzt aufs Krankenbett. Und alle, die Sex mit ihr hatten, lasse ich in größte Not geraten, es sei denn, sie ändern ihre Einstellung und wenden sich von dem ab, was diese Frau tut." },
      { number: 23, text: "Isebels Kinder werde ich nicht am Leben lassen. Sie müssen sterben. Dann werden alle Gemeinden wissen, dass mir auch die geheimsten Gedanken und Wünsche nicht verborgen bleiben, und dass ich jedem von euch das gebe, was er verdient." },
      { number: 24, text: "Aber euch, den anderen in Thyatira, sage ich: Ihr seid dieser Lehre nicht gefolgt und wolltet von den sogenannten Tiefen des Satans nichts wissen. Ich lege keine weitere Last auf euch." },
      { number: 25, text: "Haltet nur fest, was ihr habt, bis ich komme." },
      { number: 26, text: "Wer den Kampf besteht und sich bis zuletzt von meinem Wirken bestimmen lässt, dem werde ich Macht über die Völker geben." },
      { number: 27, text: "Mit eisernem Stab wird er sie hüten, sie zerschmettern wie Töpfergeschirr." },
      { number: 28, text: "Die ‹Macht dazu› habe ich von meinem Vater erhalten.* Und ich werde ihm den Morgenstern geben.", footnotes: ["Siehe Psalm 2,8.", "Der Morgenstern ist nach Offenbarung 22,16 wohl Christus selbst, der sich hier den Überwindern zur Seite stellt."] },
      { number: 29, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt!“" },
    ] },
    { number: 3, verses: [
      { number: 1, text: "„Schreib an den Engel der Gemeinde in Sardes*: Der, bei dem die sieben Geister Gottes sind und der die sieben Sterne in seiner Hand hält, lässt dir sagen: Ich kenne dein Tun und weiß, dass du im Ruf stehst, lebendig zu sein. Aber du bist tot.", heading: "An die Gemeinde in Sardes", footnotes: ["Sardes. 50 km südlich von Thyatira. Der Wohlstand von Sardes galt zur Zeit des Königs Krösus als sprichwörtlich für Reichtum."] },
      { number: 2, text: "Werde wach und stärke den Rest, der noch etwas Leben in sich hat, damit er nicht stirbt! Was du tust, kann vor meinem Gott noch nicht bestehen." },
      { number: 3, text: "Denk daran, wie bereitwillig du die Botschaft gehört und angenommen hast. Daran halte fest und ändere deine jetzige Einstellung! Wenn du weiter schläfst, werde ich dich wie ein Dieb überraschen, und du weißt nicht, wann ich komme." },
      { number: 4, text: "Doch einige bei euch in Sardes haben sich nicht besudelt. Sie werden einmal in weißen Festgewändern neben mir hergehen, denn sie sind es wert." },
      { number: 5, text: "Wer den Kampf besteht, wird mit einem weißen Festgewand bekleidet werden. Ich werde seinen Namen nicht aus dem Buch des Lebens streichen, sondern mich vor meinem Vater und seinen Engeln zu ihm bekennen." },
      { number: 6, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt!“" },
      { number: 7, text: "„Schreib an den Engel der Gemeinde in Philadelphia*: Der Heilige und Wahrhaftige, der den Schlüssel Davids hat, der öffnet, so dass niemand schließen kann, und der zuschließt, so dass niemand öffnen kann, der lässt dir sagen:", heading: "An die Gemeinde in Philadelphia", footnotes: ["Philadelphia. 50 km südöstlich von Sardes. Philadelphia war auf Textil- und Lederproduktion spezialisiert."] },
      { number: 8, text: "Ich kenne dein Tun und habe dir eine Tür geöffnet, die niemand schließen kann. Du hast nur wenig Kraft, aber du hast dich nach meinem Wort gerichtet und dich zu meinem Namen bekannt." },
      { number: 9, text: "Pass auf, ich werde dafür sorgen, dass Leute aus der Synagoge des Satans zu dir kommen und sich vor dir niederwerfen. Diese Leute behaupten, Juden zu sein, obwohl sie das gar nicht wirklich sind. Aber sie sollen erkennen, dass ich dich liebe." },
      { number: 10, text: "Weil du meine Aufforderung zur Standhaftigkeit beherzigt hast, werde ich dich bewahren vor der Stunde der Versuchung, in der die ganze Menschheit den Mächten der Verführung ausgesetzt sein wird." },
      { number: 11, text: "Ich komme bald. Halte fest, was du hast, damit dir niemand deinen Ehrenkranz nimmt." },
      { number: 12, text: "Wer den Kampf besteht, den werde ich zu einer Säule im Tempel meines Gottes machen, und er wird diesen Platz für immer behalten. Ich werde ihn mit dem Namen meines Gottes kennzeichnen und mit dem Namen der Stadt meines Gottes, des neuen Jerusalem, das von meinem Gott aus dem Himmel herabkommen wird. Und mein eigener neuer Name wird auf ihm geschrieben sein." },
      { number: 13, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt!“" },
      { number: 14, text: "„Schreib an den Engel der Gemeinde in Laodizea*: Der, der Amen heißt, der treue und wahrhaftige Zeuge, der Ursprung von Gottes Schöpfung, lässt dir sagen:", heading: "An die Gemeinde in Laodizea", footnotes: ["Laodizea. Reiche Handelsstadt, 50 km südlich von Philadelphia. Sie wurde von lauwarmem Wasser aus den 10 km nördlich gelegenen Heißwasserquellen versorgt."] },
      { number: 15, text: "Ich kenne dein Tun und weiß, dass du weder heiß noch kalt bist. Wenn du doch das eine oder andere wärst!" },
      { number: 16, text: "Doch du bist lau, weder heiß noch kalt. Darum werde ich dich aus meinem Mund ausspucken." },
      { number: 17, text: "Du sagst: 'Ich bin reich und wohl versorgt; mir fehlt nichts.' Aber du weißt nicht, wie erbärmlich und jämmerlich du dran bist: arm, nackt und blind." },
      { number: 18, text: "Ich rate dir, Gold von mir zu kaufen, Gold, das im Feuer geläutert ist, damit du reich wirst, und weiße Kleider, damit du etwas anzuziehen hast und man die Schande deiner Nacktheit nicht sieht, und Salbe für deine Augen, damit du sie einsalben und dann wieder sehen kannst." },
      { number: 19, text: "Alle, die ich lieb habe, weise ich zurecht und erziehe sie. Mach endlich Ernst und ändere deine Einstellung!" },
      { number: 20, text: "Merkst du nicht, dass ich vor der Tür stehe und anklopfe? Wer mich rufen hört und mir öffnet, zu dem gehe ich hinein, und wir werden miteinander essen – ich mit ihm und er mit mir." },
      { number: 21, text: "Wer den Kampf besteht, dem werde ich das Recht geben, mit mir auf meinem Thron zu sitzen, so wie auch ich den Kampf bestanden und mich mit meinem Vater auf seinen Thron gesetzt habe." },
      { number: 22, text: "Wer hören will, achte auf das, was der Geist den Gemeinden sagt!“" },
    ] },
    { number: 4, verses: [
      { number: 1, text: "Danach blickte ich auf und sah im Himmel eine offene Tür. Die gleiche Stimme, die schon vorher mit mir gesprochen hatte und die wie eine Fanfare klang, sagte: „Komm hier herauf! Ich werde dir zeigen, was nach diesen Dingen geschehen muss.“", heading: "Der Thron im Himmel" },
      { number: 2, text: "Im gleichen Augenblick wurde ich vom Geist ergriffen. Ich sah einen Thron im Himmel stehen, und auf dem Thron saß jemand." },
      { number: 3, text: "Seine Gestalt funkelte wie ein Diamant und glühte rot wie ein Karneol. Und den ganzen Thron umleuchtete ein Regenbogen, grün wie ein Smaragd." },
      { number: 4, text: "Um den Thron herum standen im Kreis 24 andere Throne. Darauf saßen 24 Älteste, die in weiße Gewänder gehüllt waren und goldene Siegeskränze trugen." },
      { number: 5, text: "Aus dem Thron heraus zuckten Blitze, man hörte ein Dröhnen und Donnerschläge. Vor dem Thron loderten sieben Fackeln. Das sind die sieben Geister Gottes." },
      { number: 6, text: "Die Fläche vor dem Thron war wie ein gläsernes Meer von kristallener Klarheit. In der Mitte, im innersten Kreis um den Thron, standen vier mächtige Wesen, die vorn und hinten voller Augen waren." },
      { number: 7, text: "Das erste Wesen glich einem Löwen, das zweite einem jungen Stier. Das dritte hatte ein Gesicht wie ein Mensch, und das vierte glich einem fliegenden Adler." },
      { number: 8, text: "Jedes der vier hatte sechs Flügel, die ebenfalls innen und außen mit Augen besetzt waren. Und immer wieder, bei Tag und Nacht, rufen diese mächtigen Wesen:" },
      { number: 9, text: "Und wenn diese Wesen dem, der auf dem Thron sitzt und in alle Ewigkeit lebt, Ehre erweisen, wenn sie ihn rühmen und ihm ihren Dank bringen," },
      { number: 10, text: "werfen sich auch die 24 Ältesten nieder und beten ihn an – ihn, der auf dem Thron sitzt und in alle Ewigkeit lebt. Sie legen ihre Siegeskränze vor dem Thron nieder und sagen:" },
      { number: 11, text: "„Würdig bist du, unser Herr und Gott, dir gebührt Ehre und Ruhm und alle Macht, denn du hast alle Dinge erschaffen. Du hast es gewollt, und die Schöpfung entstand.“" },
    ] },
    { number: 5, verses: [
      { number: 1, text: "Jetzt sah ich eine Schriftrolle auf der rechten Hand dessen liegen, der auf dem Thron saß. Sie war innen und außen beschrieben und mit sieben Siegeln verschlossen.", heading: "Wer die sieben Siegel brechen darf" },
      { number: 2, text: "Dann sah ich, wie ein mächtiger Engel mit lauter Stimme ausrief: „Wer ist würdig, das Buch zu öffnen? Und ‹wer hat das Recht›, die Siegel zu lösen?“" },
      { number: 3, text: "Aber im ganzen Himmel, auf der Erde und selbst unter der Erde war niemand, der das Buch öffnen und hineinblicken konnte." },
      { number: 4, text: "Es war keiner zu finden, der würdig gewesen wäre, das Buch zu öffnen und zu sehen, was darin stand. Deshalb weinte ich sehr." },
      { number: 5, text: "Da sagte einer der Ältesten zu mir: „Weine nicht! Sieh doch, einer hat gesiegt! Es ist der Löwe aus dem Stamm Juda, der Spross aus dem Wurzelstock Davids. Er wird die sieben Siegel aufbrechen und das Buch öffnen.“" },
      { number: 6, text: "Da sah ich mitten im Thron, in der Mitte der vier mächtigen Wesen und der Ältesten, ein Lamm stehen, das wie geschlachtet aussah. Es hatte sieben Hörner und sieben Augen. – Die sieben Augen sind die sieben Geister Gottes, die in alle Teile der Erde ausgesandt sind. –" },
      { number: 7, text: "Das Lamm trat zu dem, der auf dem Thron saß, und nahm das Buch aus seiner rechten Hand." },
      { number: 8, text: "Als das geschah, warfen sich die vier mächtigen Wesen und die 24 Ältesten vor dem Lamm nieder. Jeder von den Ältesten hatte eine Harfe und außerdem goldene Schalen, die mit Weihrauch gefüllt waren. – Das sind die Gebete der von Gott geheiligten Menschen. –" },
      { number: 9, text: "Und sie singen ein neues Lied:" },
      { number: 10, text: "und sie zu einem Königsvolk und zu Priestern für ihn gemacht. / Sie regieren in Zukunft die Welt.“" },
      { number: 11, text: "Dann sah und hörte ich eine unzählbar große Schar von Engeln, es waren Tausende und Abertausende. Sie standen im Kreis um den Thron, um die mächtigen Wesen und die Ältesten" },
      { number: 12, text: "und riefen in gewaltigem Chor:" },
      { number: 13, text: "Und jedes Geschöpf, das es gibt – im Himmel und auf der Erde, unter der Erde und im Meer – hörte ich mit einstimmen:" },
      { number: 14, text: "„Amen!“, sagten die vier mächtigen Wesen. Und die Ältesten warfen sich nieder und beteten an." },
    ] },
    { number: 6, verses: [
      { number: 1, text: "Dann sah ich, wie das Lamm das erste von den sieben Siegeln der Schriftrolle aufbrach, und ich hörte eines der vier mächtigen Wesen mit Donnerstimme rufen: „Komm!“", heading: "Das Lamm bricht die ersten sechs Siegel" },
      { number: 2, text: "Da erblickte ich ein weißes Pferd. Der Reiter auf ihm trug einen Bogen und erhielt jetzt einen Siegeskranz. Triumphierend ritt er hinaus, um den Sieg zu erringen." },
      { number: 3, text: "Als das Lamm das zweite Siegel aufbrach, hörte ich das zweite mächtige Wesen rufen: „Komm!“" },
      { number: 4, text: "Wieder erschien ein Pferd. Es war feuerrot. Seinem Reiter wurde ein großes Schwert gegeben, und er bekam die Macht, den Frieden von der Erde wegzunehmen, so dass die Menschen sich gegenseitig abschlachten würden." },
      { number: 5, text: "Dann brach das Lamm das dritte Siegel auf, und ich hörte das dritte der mächtigen Wesen rufen: „Komm!“ Jetzt sah ich ein schwarzes Pferd. Sein Reiter hatte eine Waage in der Hand." },
      { number: 6, text: "Und eine Stimme, die aus der Mitte der vier Lebewesen zu kommen schien, rief: „Ein Kilo* Weizen für einen Denar*! Drei Kilo Gerste für einen Denar! Öl und Wein taste nicht an!“", footnotes: ["Kilo. Griechisch: Choinix. Das Getreidemaß fasste etwa einen Liter und stellte eine Tagesration für eine Person dar.", "Denar. Tagelohn eines Arbeiters. Eine Tagesration Weizen für einen Tagelohn entsprach einer acht- bis zwölffachen Verteuerung."] },
      { number: 7, text: "Als das Lamm das vierte Siegel aufbrach, hörte ich das vierte mächtige Wesen rufen: „Komm!“" },
      { number: 8, text: "Dann sah ich ein leichenfahles Pferd. Sein Reiter hieß Tod und sein ganzes Reich folgte ihm nach. Sie wurden ermächtigt, ein Viertel der Menschen durch Krieg, Hunger, tödliche Seuchen und wilde Tiere umkommen zu lassen." },
      { number: 9, text: "Nun brach das Lamm das fünfte Siegel auf. Da sah ich unten am Altar die Seelen der Menschen, die man abgeschlachtet hatte, weil sie an Gottes Wort festhielten und ihm als seine Zeugen treu geblieben waren." },
      { number: 10, text: "Sie riefen mit lauter Stimme: „Du heiliger und wahrhaftiger Herrscher! Wie lange dauert es noch, bis du Gericht hältst und unser Blut an den Bewohnern der Erde rächst?“" },
      { number: 11, text: "Da erhielt jeder von ihnen ein weißes Gewand mit der Antwort, dass sie noch eine kurze Zeit Geduld haben müssten. Denn erst müsse noch eine bestimmte Zahl ihrer Glaubensgeschwister zum Ziel kommen und so wie sie getötet werden." },
      { number: 12, text: "Als das Lamm das sechste Siegel aufbrach, erschütterte ein schweres Beben die Erde. Die Sonne wurde dunkel wie ein Trauersack, und der Mond erschien auf einmal rot wie Blut." },
      { number: 13, text: "Dann fielen Sterne vom Himmel auf die Erde wie vom Sturm geschüttelte Feigen vom Baum." },
      { number: 14, text: "Der Himmel verschwand wie eine Schriftrolle, die man zusammenrollt, und kein Berg und keine Insel blieben an ihrer Stelle." },
      { number: 15, text: "Da versteckten sich die Könige der Erde, die Herrscher und die Generäle, die Reichen und die Mächtigen, aber auch alle anderen Menschen – Sklaven wie Freie. Sie versteckten sich in Höhlen und Felsspalten" },
      { number: 16, text: "und flehten die Berge und Felsen an: „Fallt auf uns und verbergt uns vor den Blicken dessen, der auf dem Thron sitzt, und vor dem Zorn des Lammes!" },
      { number: 17, text: "Denn jetzt ist der furchtbare Tag ihres Zorns gekommen. Wer kann da bestehen?“" },
    ] },
    { number: 7, verses: [
      { number: 1, text: "Danach sah ich vier Engel an den äußersten Enden der Erde stehen. Sie hinderten die Winde aus den vier Himmelsrichtungen daran, über das Land, das Meer und die Bäume zu blasen.", heading: "Wer das besondere Siegel Gottes bekommt" },
      { number: 2, text: "Und von da, wo die Sonne aufgeht, sah ich noch einen anderen Engel herkommen, der das Siegel des lebendigen Gottes trug. Er rief den vier Engeln, denen Gott die Macht gegeben hatte, der Erde und dem Meer Schaden zuzufügen, mit lauter Stimme zu:" },
      { number: 3, text: "„Verwüstet weder das Land noch das Meer und richtet auch an den Bäumen noch keinen Schaden an! Erst müssen wir allen, die Gott gehören und ihm dienen, sein Siegel auf die Stirn drücken.“" },
      { number: 4, text: "Ich hörte, wie viele Menschen das Siegel bekamen: Es waren 144.000 aus allen Stämmen Israels:" },
      { number: 5, text: "12.000 aus Juda, 12.000 aus Ruben, 12.000 aus Gad," },
      { number: 6, text: "12.000 aus Ascher, 12.000 aus Naftali, 12.000 aus Manasse," },
      { number: 7, text: "12.000 aus Simeon, 12.000 aus Levi, 12.000 aus Issachar," },
      { number: 8, text: "12.000 aus Sebulon, 12.000 aus Josef, 12.000 aus Benjamin." },
      { number: 9, text: "Danach sah ich eine riesige Menschenmenge aus allen Stämmen und Völkern, Sprachen und Kulturen. Es waren so viele, dass niemand sie zählen konnte. Sie standen mit Palmzweigen in den Händen weißgekleidet vor dem Thron und dem Lamm", heading: "Die Menschenmenge vor dem Thron Gottes" },
      { number: 10, text: "und riefen mit lauter Stimme: „Die Rettung kommt von unserem Gott, von dem, der auf dem Thron sitzt, und dem Lamm!“" },
      { number: 11, text: "Und alle Engel, die rings um den Thron herum standen um die Ältesten und die vier mächtigen Wesen, warfen sich mit dem Gesicht auf dem Boden vor dem Thron nieder und beteten Gott an." },
      { number: 12, text: "„Amen!“, sagten sie." },
      { number: 13, text: "Dann fragte mich einer der Ältesten: „Weißt du, wer diese weißgekleideten Menschen sind und woher sie kommen?“" },
      { number: 14, text: "Ich erwiderte: „Du musst es mir sagen, mein Herr, du weißt es.“ Er sagte: „Diese Menschen haben die größte Bedrängnis überstanden, die es je gegeben hat. Sie haben ihre Gewänder gewaschen und im Blut des Lammes weiß gemacht." },
      { number: 15, text: "Darum stehen sie vor dem Thron Gottes und dienen ihm Tag und Nacht in seinem Tempel. Und der, der auf dem Thron sitzt, wird ihnen seine Gegenwart schenken." },
      { number: 16, text: "Sie werden keinen Hunger mehr haben und auch Durst wird sie nie mehr quälen. Die Sonne wird nicht mehr auf sie herabbrennen und auch keine andere Glut." },
      { number: 17, text: "Denn das Lamm, das mitten auf dem Thron sitzt, wird sie weiden und zu den Quellen führen, wo das Wasser des Lebens ist. Und Gott wird jede Träne von ihren Augen abwischen.“" },
    ] },
    { number: 8, verses: [
      { number: 1, text: "Als das Lamm das siebte Siegel aufbrach, war es im Himmel eine halbe Stunde lang völlig still.", heading: "Der Aufbruch des letzten Siegels löst sieben weitere Katastrophen aus" },
      { number: 2, text: "Dann sah ich die sieben Engel, die zum Dienst für Gott abgestellt sind. Ich sah, wie ihnen sieben Posaunen gegeben wurden." },
      { number: 3, text: "Dann trat ein anderer Engel mit einem goldenen Räucherfass an den goldenen Altar, der vor dem Thron stand. Er bekam eine große Menge Weihrauch und sollte diesen zusammen mit den Gebeten aller geheiligten Menschen als Räucheropfer darbringen." },
      { number: 4, text: "So stiegen die Gebete der Heiligen mit dem Duft des Weihrauchs aus der Hand des Engels zu Gott auf." },
      { number: 5, text: "Dann füllte der Engel das Räucherfass mit Glut vom Altar und schleuderte es auf die Erde. Da donnerte und dröhnte es heftig, es blitzte und die Erde bebte." },
      { number: 6, text: "Jetzt machten sich die sieben Engel bereit, die sieben Posaunen zu blasen." },
      { number: 7, text: "Nachdem der erste Engel die Posaune geblasen hatte, prasselten Hagel und Feuer mit Blut vermischt auf die Erde. Ein Drittel der Erdoberfläche und ein Drittel aller Bäume und alles Gras verbrannten." },
      { number: 8, text: "Nachdem der zweite Engel die Posaune geblasen hatte, wurde etwas ins Meer gestürzt, das wie ein großer brennender Berg aussah. Ein Drittel des Meeres wurde zu Blut." },
      { number: 9, text: "Ein Drittel aller Lebewesen im Meer starb und ein Drittel aller Schiffe wurde zerstört." },
      { number: 10, text: "Nachdem der dritte Engel die Posaune geblasen hatte, stürzte ein großer Stern vom Himmel herab. Er brannte wie eine Fackel und fiel auf ein Drittel aller Flüsse und auf die Quellen." },
      { number: 11, text: "Der Stern hieß Wermut* und machte ein Drittel vom Trinkwasser zu Wermut. An diesem Bitterwasser starben sehr viele Menschen.", footnotes: ["Wermut. Heilpflanze, deren bittere Aromastoffe auch für Getränke verwendet werden, zum Beispiel in Wermutwein."] },
      { number: 12, text: "Nachdem der vierte Engel die Posaune geblasen hatte, wurde ein Drittel der Sonne, des Mondes und der Sterne getroffen, so dass ein Drittel des Tages und ein Drittel der Nacht kein Licht mehr hatte." },
      { number: 13, text: "Dann sah ich einen Adler hoch am Himmel fliegen und hörte ihn mit lauter Stimme schreien: „Wehe denen, die auf der Erde leben! Wehe ihnen, wenn die letzten drei Engel in ihre Posaunen stoßen, wehe ihnen!“" },
    ] },
    { number: 9, verses: [
      { number: 1, text: "Nachdem der fünfte Engel die Posaune geblasen hatte, sah ich einen Stern, der vom Himmel auf die Erde gestürzt war. Dieser Stern erhielt den Schlüssel zum Schacht in den Abgrund" },
      { number: 2, text: "und öffnete ihn. Da quoll Rauch heraus wie von einem riesigen Schmelzofen und verdunkelte die Luft und die Sonne." },
      { number: 3, text: "Aus dem Rauch kamen Heuschrecken hervor, die wie Skorpione stechen konnten, und schwärmten über die ganze Erde aus." },
      { number: 4, text: "Doch es wurde ihnen verboten, das Gras abzufressen oder an Bäumen und anderen Pflanzen irgendwelchen Schaden anzurichten. Sie durften nur Menschen angreifen, aber nur solche, die kein Siegel Gottes auf der Stirn hatten." },
      { number: 5, text: "Töten durften sie diese Menschen zwar nicht, aber sie hatten die Macht, sie fünf Monate lang zu quälen. Die Menschen würden unerträgliche Schmerzen erleiden, wie durch den Stich eines Skorpions." },
      { number: 6, text: "In dieser Zeit werden die Menschen den Tod suchen, aber nicht finden. Sie werden sich danach sehnen zu sterben, aber der Tod wird vor ihnen fliehen." },
      { number: 7, text: "Die Heuschrecken sahen aus wie Pferde, die in die Schlacht ziehen. Auf ihren Köpfen hatten sie etwas, das wie ein goldener Kranz aussah, und ihre Gesichter glichen denen von Menschen." },
      { number: 8, text: "Sie hatten lange Haare wie Frauen und Zähne wie Löwen." },
      { number: 9, text: "Ihr Rumpf war wie mit eisernen Rüstungen gepanzert, und ihre Flügel machten einen Lärm wie ein Heer von Pferden und Streitwagen, die in die Schlacht ziehen." },
      { number: 10, text: "Ihre Hinterleiber waren wie die Schwänze eines Skorpions geformt und mit Stacheln bestückt. Damit verursachen sie die Qualen, denen die Menschen fünf Monate lang ausgesetzt sein werden." },
      { number: 11, text: "Als König haben sie den Engel des Abgrunds über sich. Er heißt „Verderber“, auf Hebräisch Abaddon, auf Griechisch Apollyon." },
      { number: 12, text: "Das erste Unheil, das der Wehruf angekündigt hat, ist vorüber, doch zwei weitere stehen noch bevor." },
      { number: 13, text: "Nachdem der sechste Engel die Posaune geblasen hatte, hörte ich eine Stimme, die aus den vier Hörnern des goldenen Altars zu kommen schien, der vor Gott stand." },
      { number: 14, text: "Sie sagte zu dem sechsten Engel mit der Posaune: „Lass die vier Engel frei, die am großen Strom, dem Euphrat, in Ketten liegen!“" },
      { number: 15, text: "Da wurden die vier Engel von ihren Fesseln befreit. Auf dieses Jahr, diesen Monat, diesen Tag, ja genau auf diese Stunde hatten sie gewartet, um ein Drittel der Menschen zu töten." },
      { number: 16, text: "Sie verfügten über ein Heer von zweihundert Millionen berittenen Soldaten – ich hörte, wie ihre Zahl genannt wurde." },
      { number: 17, text: "Und dann sah ich sie in meiner Vision: Die Reiter trugen feuerrote, violette und schwefelgelbe Brustpanzer. Die Köpfe der Pferde sahen wie Löwenköpfe aus, und aus ihren Mäulern schossen Feuer, Rauch und Schwefel." },
      { number: 18, text: "Mit diesen drei Waffen – dem Feuer, dem Rauch und dem Schwefel – töteten sie ein Drittel der Menschheit." },
      { number: 19, text: "Die tödliche Macht der Pferde geht sowohl von ihrem Maul als auch von ihren Schwänzen aus, denn diese Schwänze glichen Schlangen mit Köpfen, die ebenfalls Menschen angriffen." },
      { number: 20, text: "Aber die Menschen, die diese Plage überlebten, waren nicht bereit, ihre Einstellung zu ändern. Sie hörten nicht auf, Dämonen anzubeten und sich vor Götterbildern aus Gold, Silber, Bronze, Stein und Holz niederzuwerfen, die sie mit eigenen Händen gemacht hatten und die weder sehen noch hören noch gehen können." },
      { number: 21, text: "Nein, sie bereuten weder ihre Mordtaten noch ihren Okkultismus, auch nicht ihre sexuelle Unmoral und ihre Diebereien." },
    ] },
    { number: 10, verses: [
      { number: 1, text: "Dann sah ich, wie ein anderer mächtiger Engel aus dem Himmel herabkam. Er war von einer Wolke umhüllt, und der Regenbogen stand über seinem Kopf. Sein Gesicht leuchtete wie die Sonne und seine Beine glichen Feuersäulen.", heading: "Der Engel mit dem kleinen Buch" },
      { number: 2, text: "Er hielt eine kleine geöffnete Schriftrolle in der Hand und setzte seinen rechten Fuß auf das Meer und seinen linken auf das Festland." },
      { number: 3, text: "Seine Stimme dröhnte wie das Brüllen eines Löwen, und laut krachend antworteten ihm sieben Donnerschläge." },
      { number: 4, text: "Als es wieder still war, wollte ich die Worte der sieben Donner aufschreiben. Aber da hörte ich eine Stimme aus dem Himmel rufen: „Nein, was die sieben Donner gerufen haben, muss verborgen bleiben. Schreibe es nicht auf!“" },
      { number: 5, text: "Der Engel, den ich auf dem Meer und auf dem Festland stehen sah, hob jetzt seine rechte Hand zum Himmel" },
      { number: 6, text: "und sagte: „Ich schwöre bei dem, der in alle Ewigkeit lebt, der den Himmel, die Erde, das Meer und alles Lebendige in ihnen geschaffen hat, ich schwöre: Die Zeit ist jetzt vorbei!" },
      { number: 7, text: "Denn wenn der siebte Engel seine Posaune geblasen hat, wird Gottes geheimer Plan zur Vollendung kommen, so wie er es seinen Dienern, den Propheten, als gute Botschaft anvertraut hat.“" },
      { number: 8, text: "Dann sprach die Stimme aus dem Himmel mich noch einmal an. Sie sagte: „Geh zu dem Engel, der auf dem Meer und dem Land steht, und lass dir die kleine offene Schriftrolle geben!“" },
      { number: 9, text: "Ich ging zu dem Engel und bat ihn um die kleine Schriftrolle. Er sagte: „Nimm und iss sie auf! Sie ist so bitter, dass sich dein Magen zusammenziehen wird, aber solange du sie im Mund hast, wird sie süß wie Honig sein.“" },
      { number: 10, text: "Ich nahm die kleine Schriftrolle aus seiner Hand und aß sie auf. Tatsächlich war sie so süß wie Honig, als ich sie im Mund hatte. Aber als ich sie hinunterschluckte, war sie so bitter, dass sich mir der Magen zusammenkrampfte." },
      { number: 11, text: "Dann wurde mir gesagt: „Du wirst noch mehr ankünden müssen. Es betrifft Völker, Sprachen, Kulturen und viele Könige.“" },
    ] },
    { number: 11, verses: [
      { number: 1, text: "Dann wurde mir eine Art Messstab aus Schilfrohr gegeben, und jemand sagte: „Geh und miss den Tempel Gottes, auch den Altar, und zähle die Menschen, die dort anbeten!", heading: "Die zwei Zeugen" },
      { number: 2, text: "Den äußeren Vorhof des Tempels lass beim Messen aus, denn er ist den nichtjüdischen Völkern ausgeliefert worden. Sie werden die Heilige Stadt unterwerfen und 42 Monate lang besetzt halten." },
      { number: 3, text: "Doch ich werde meine beiden Zeugen zu ihnen schicken, und sie werden die ganze Zeit, nämlich 1260 Tage lang, mit dem Trauersack bekleidet als Propheten zu ihnen reden.“" },
      { number: 4, text: "Diese zwei Zeugen sind die zwei Ölbäume und die zwei Leuchter, die vor dem Herrn der Erde stehen." },
      { number: 5, text: "Und wenn ihnen einer schaden will, fährt Feuer aus ihrem Mund und verbrennt ihn bei lebendigem Leib. So vernichten sie alle ihre Feinde." },
      { number: 6, text: "Sie haben die Macht, den Himmel zu verschließen, damit in der Zeit ihres prophetischen Wirkens kein Regen fällt, sie haben auch die Macht, jedes Gewässer in Blut zu verwandeln. Sie können jedes erdenkliche Unheil über die Erde bringen." },
      { number: 7, text: "Und wenn sie ihren Auftrag erfüllt haben, wird das Tier aus dem Abgrund kommen und gegen sie kämpfen. Es wird sie besiegen und sie töten." },
      { number: 8, text: "Ihre Leichen wird man auf offener Straße mitten in der großen Stadt liegen lassen, in der Stadt, in der auch ihr Herr gekreuzigt wurde und die geistlich gesprochen Sodom oder auch Ägypten heißt." },
      { number: 9, text: "Dreieinhalb Tage lang werden Menschen aus allen Völkern und Stämmen, Sprachen und Kulturen sich am Anblick ihrer Leichen ergötzen. Sie werden nicht gestatten, dass sie in ein Grab gelegt werden." },
      { number: 10, text: "Überall auf der Welt werden die Menschen jubeln und feiern und sich gegenseitig Geschenke schicken, denn diese beiden Propheten hatten ihnen das Leben zur Qual gemacht." },
      { number: 11, text: "Doch nach den dreieinhalb Tagen wird der Lebensgeist Gottes in die Propheten zurückkehren, und sie stehen wieder auf. Das blanke Entsetzen wird alle überfallen, die das beobachten." },
      { number: 12, text: "Dann werden die beiden Propheten eine mächtige Stimme aus dem Himmel hören: „Kommt hier herauf!“ Und vor den Augen ihrer Feinde werden sie in einer Wolke zum Himmel hinaufsteigen." },
      { number: 13, text: "In diesem Augenblick wird ein heftiges Erdbeben die Stadt erschüttern und ein Zehntel von ihr vernichten. 7000 Menschen werden dabei umkommen. Die Überlebenden werden zu Tode erschrocken sein und Gott im Himmel die Ehre erweisen, die ihm zusteht." },
      { number: 14, text: "Das zweite Unheil, das der Wehruf angekündigt hat, ist vorüber, doch das dritte steht unmittelbar bevor." },
      { number: 15, text: "Nachdem nun der siebte Engel die Posaune geblasen hatte, erklang ein mächtiger Lobgesang im Himmel: „Jetzt gehört die Herrschaft über die Welt unserem Herrn und seinem Christus*, und er wird herrschen in alle Ewigkeit!“", heading: "Das Unheil, das die siebte Posaune bringt", footnotes: ["Christus bedeutet der (zum König) Gesalbte. Hebräisch: Messias."] },
      { number: 16, text: "Und die 24 Ältesten, die vor Gott auf ihren Thronen sitzen, warfen sich nieder mit dem Gesicht auf dem Boden und beteten Gott an:" },
      { number: 17, text: "„Wir danken dir, Herr, unser Gott, / du allmächtiger Herrscher, / der du bist und immer warst! / Denn nun hast du deine große Macht bewiesen / und die Herrschaft angetreten." },
      { number: 18, text: "Die Völker hatten sich wütend gegen dich aufgelehnt, / doch jetzt entlädt sich dein Zorn über sie. / Jetzt ist die Zeit gekommen, / wo du Gericht über die Toten hältst / und wo du die verdirbst, die die Erde verderben.* / Doch es ist auch die Zeit, wo deine Diener ihren Lohn erhalten: / die Propheten und die, die du geheiligt hast, / die Kleinen und die Großen, / und die mit Ehrfurcht vor dir.“", footnotes: ["die Erde verderben. Damit sind nicht etwa Verursacher von Umweltschäden gemeint, sondern solche, die Gottlosigkeit und Götzendienst gefördert haben, vergleiche 1. Mose 6,11."] },
      { number: 19, text: "Dann wurde der Tempel Gottes im Himmel geöffnet und man konnte die Lade seines Bundes sehen. Blitze zuckten auf, Donnerschläge dröhnten, die Erde bebte und ein schwerer Hagel ging nieder." },
    ] },
    { number: 12, verses: [
      { number: 1, text: "Dann war im Himmel eine außergewöhnliche Erscheinung zu sehen: Eine Frau war mit der Sonne bekleidet, der Mond stand unter ihren Füßen und auf dem Kopf trug sie einen Kranz von zwölf Sternen.", heading: "Die Frau und der Drache" },
      { number: 2, text: "Sie ist schwanger, die Wehen haben begonnen und sie schreit vor Schmerzen." },
      { number: 3, text: "Dann kam es zu einer anderen Erscheinung im Himmel: Ein riesiger feuerroter Drache mit sieben Köpfen und zehn Hörnern, auf jedem seiner Köpfe ein Diadem.", footnotes: ["Diadem. Halbkreisförmiger sehr kostbarer Stirnreif für Könige."] },
      { number: 4, text: "Mit seinem Schwanz fegte er ein Drittel der Sterne vom Himmel und schleuderte sie auf die Erde. Dann stellte er sich vor die Frau hin und wollte das Kind gleich nach der Geburt verschlingen." },
      { number: 5, text: "Doch ihr Kind wurde sofort zu Gott hinaufgenommen und zu seinem Thron gebracht. Es war der Sohn, der einmal alle Völker der Erde mit eisernem Stab regieren würde." },
      { number: 6, text: "Die Frau selbst floh in die Wüste, wo Gott ihr einen Zufluchtsort geschaffen hatte, an dem sie 1260 Tage lang mit allem Nötigen versorgt würde." },
      { number: 7, text: "Dann brach ein Krieg im Himmel aus: Der Engelfürst Michael kämpfte mit seinen Engeln gegen den Drachen. Der Drache und seine Engel wehrten sich," },
      { number: 8, text: "aber sie konnten nicht standhalten. Von da an war für ihn und seine Engel kein Platz mehr im Himmel." },
      { number: 9, text: "Der große Drache, die uralte Schlange, die auch Teufel oder Satan genannt wird und die ganze Menschheit verführt hatte, wurde mit all seinen Engeln auf die Erde hinabgestürzt." },
      { number: 10, text: "Da hörte ich eine laute Stimme im Himmel rufen:" },
      { number: 11, text: "Und sie haben ihn besiegt, / weil das Lamm sein Blut für sie vergossen hat / und weil sie – ohne Rücksicht auf ihr Leben – sich zur Botschaft des Lammes bekannten, / bereit, auch dafür zu sterben." },
      { number: 12, text: "Darum freue dich Himmel! / Jubelt, die ihr darin wohnt! / Doch wehe dir, Erde, / und wehe dir, Meer! / Denn der Teufel ist zu euch herabgekommen und rast vor Wut, / weil er weiß, dass er nicht mehr viel Zeit hat.“" },
      { number: 13, text: "Als der Drache nun sah, dass er auf die Erde geworfen war, begann er, die Frau zu verfolgen, die den Sohn geboren hatte." },
      { number: 14, text: "Aber der Frau wurden Flügel gegeben, die beiden Flügel des großen Adlers. So konnte sie an den Ort in der Wüste fliehen, wo sie vor der Schlange sicher war und dreieinhalb Jahre lang* mit allem Nötigen versorgt werden würde.", footnotes: ["dreieinhalb Jahre lang. Wörtlich: eine, zwei und eine halbe Zeit. Vergleiche Daniel 7,25."] },
      { number: 15, text: "Da spie die Schlange einen mächtigen Wasserstrom hinter der Frau her, der sie mit sich fortreißen sollte." },
      { number: 16, text: "Aber die Erde kam der Frau zur Hilfe. Sie öffnete sich und schluckte den Strom, den der Drache aus seinem Rachen stieß." },
      { number: 17, text: "Da geriet der Drache außer sich vor Wut und bekämpfte jetzt alle, die zu dieser Frau gehörten. Das sind die Menschen, die nach den Geboten Gottes leben und sich zur Botschaft von Jesus bekennen." },
    ] },
    { number: 13, verses: [
      { number: 1, text: "Da sah ich ein Tier aus dem Meer heraufsteigen, das zehn Hörner und sieben Köpfe hatte. Auf jedem Horn trug es ein Diadem, und auf den Köpfen standen gotteslästerliche Namen." },
      { number: 2, text: "Das Tier sah aus wie ein Leopard, aber es hatte Tatzen wie ein Bär und ein Maul wie ein Löwe. Der Drache übergab ihm seine Macht, seinen Thron und alle Befehlsgewalt." },
      { number: 3, text: "An einem seiner Köpfe hatte das Tier eine schwere, ja tödliche Verletzung. Aber seine Todeswunde wurde geheilt, und die ganze Welt folgte ihm mit staunender Bewunderung." },
      { number: 4, text: "Den Drachen beteten sie an, weil er dem Tier die Macht gegeben hatte. Und das Tier beteten sie an, weil sie sagten: „Wer kann sich denn mit dem Tier vergleichen? Wer kann es überhaupt mit ihm aufnehmen?“" },
      { number: 5, text: "42 Monate lang durfte es seinen Einfluss ausüben und anmaßende gotteslästerliche Reden führen. Niemand hinderte es daran." },
      { number: 6, text: "Es riss sein Maul auf und stieß Lästerungen gegen Gott aus, es verhöhnte seinen Namen und sein Heiligtum und alle, die im Himmel wohnen." },
      { number: 7, text: "Es wurde ihm erlaubt, mit allen Menschen Krieg zu führen, mit allen, die zu Gott gehören – und sie zu besiegen. Über jeden Stamm und jedes Volk, jede Sprache und jede Kultur durfte es seine Macht ausüben." },
      { number: 8, text: "Alle Menschen auf der Erde werden das Tier* anbeten, alle, außer denen, deren Namen seit Gründung der Welt im Lebensbuch des geopferten Lammes stehen.", footnotes: ["Tier. Wörtlich: ihn. Das Tier symbolisiert eine männliche Person."] },
      { number: 9, text: "Wer hören will, achte auf das, was gesagt wird!" },
      { number: 10, text: "Wenn einer zur Gefangenschaft bestimmt ist, kommt er in Gefangenschaft. Wer für den Tod durch das Schwert bestimmt ist, wird mit dem Schwert getötet werden. Hier zeigt sich dann die Glaubenstreue und Standhaftigkeit der Menschen, die zu Christus gehören." },
      { number: 11, text: "Dann sah ich ein anderes Tier aus der Erde heraufsteigen. Es hatte zwei Hörner wie ein Lamm, aber es redete wie ein Drache.", heading: "Das Tier aus der Erde" },
      { number: 12, text: "Es handelte unter Aufsicht und mit Vollmacht des ersten Tieres und brachte die ganze Erde und alle ihre Bewohner dazu, das erste Tier anzubeten, das Tier, dessen Todeswunde geheilt worden war." },
      { number: 13, text: "Dieses zweite Tier tat große Wunder und ließ vor den Augen der Menschen sogar Feuer vom Himmel fallen." },
      { number: 14, text: "Mit Hilfe dieser außergewöhnlichen Dinge, zu denen das erste Tier es befähigt hatte, verführte es die Bewohner der Erde. Es überredete sie, ein Standbild zu Ehren jenes ersten Tieres zu errichten, das von einem Schwert tödlich getroffen und doch wieder lebendig geworden war." },
      { number: 15, text: "Es bekam sogar die Macht, das Standbild des ersten Tieres zu beleben, sodass das Standbild reden konnte und bewirkte, dass alle getötet wurden, die das Bild nicht anbeteten." },
      { number: 16, text: "Außerdem sorgte das zweite Tier dafür, dass sich alle Menschen – Hohe und Niedrige, Reiche und Arme, Freie und Sklaven – ein Kennzeichen an ihre rechte Hand oder ihre Stirn machen ließen." },
      { number: 17, text: "Ohne dieses Kennzeichen – das war der Name des Tieres beziehungsweise die Zahl seines Namens* – würde niemand mehr etwas kaufen oder verkaufen können.", footnotes: ["Zahl seines Namens. Weil die Buchstaben des hebräischen und griechischen Alphabets auch Zahlenwerten entsprechen, lässt sich aus einem Namen eine Zahl errechnen, zum Beispiel: Jesus, Griechisch: IESOUS = 10 + 8 + 200 + 70 + 400 + 200 = 888."] },
      { number: 18, text: "Hier ist Weisheit gefragt! Wer Verstand hat, kann herausfinden, was die Zahl des Tieres bedeutet, denn sie steht für den Namen eines Menschen. Die Zahl ist 666." },
    ] },
    { number: 14, verses: [
      { number: 1, text: "Dann sah ich das Lamm auf dem Zionsberg stehen. Bei ihm waren 144.000 Menschen, auf deren Stirn sein Name und der Name seines Vaters geschrieben waren.", heading: "Das Lamm" },
      { number: 2, text: "Dann hörte ich vom Himmel her ein Geräusch, das wie das Tosen einer mächtigen Brandung und wie ein gewaltiges Donnerrollen klang, sich aber gleichzeitig wie Gesang von Harfenspielern anhörte." },
      { number: 3, text: "Dieser große Chor sang ein neues Lied vor dem Thron, den vier mächtigen Wesen und den Ältesten. Und niemand außer den 144.000 Erlösten, die Gott aus der Menschheit freigekauft hat, konnte dieses Lied lernen." },
      { number: 4, text: "Sie hatten sich dem Lamm gegenüber durch keinerlei Untreue schuldig gemacht, sondern sich wie eine Braut unberührt und rein gehalten, und sie folgen dem Lamm, wohin es auch geht. Als Erste waren sie für Gott und das Lamm freigekauft worden." },
      { number: 5, text: "Sie sind ohne Tadel. Keine Falschheit kann ihnen vorgeworfen werden." },
      { number: 6, text: "Dann sah ich einen anderen Engel hoch am Himmel fliegen. Er hatte eine Botschaft von ewiger Bedeutung, die er allen Bewohnern der Erde verkündigen sollte, allen Völkern und Stämmen, den Menschen jeder Sprache und Kultur.", heading: "Die drei Engel" },
      { number: 7, text: "Laut rief er: „Fürchtet Gott und gebt ihm die Ehre, die ihm gebührt! Denn jetzt ist die Stunde gekommen, in der er Gericht hält. Betet den an, der Himmel und Erde, das Meer und alle Quellen geschaffen hat!“" },
      { number: 8, text: "Ein zweiter Engel folgte dem ersten und rief: „Gefallen! Gefallen ist das mächtige Babylon, das mit dem Wein seiner leidenschaftlichen sexuellen Unmoral alle Völker getränkt hat.“" },
      { number: 9, text: "Ein dritter Engel folgte ihnen und rief mit lauter Stimme: „Jeder, der das Tier und sein Standbild anbetet und das Kennzeichen seines Namens an Hand oder Stirn anbringen lässt," },
      { number: 10, text: "der wird den Wein von Gottes furchtbarem Grimm trinken müssen, der unvermischt im Becher seines Zornes eingeschenkt ist. Vor den Augen des Lammes und der heiligen Engel wird er mit Feuer und brennendem Schwefel gequält werden." },
      { number: 11, text: "Der Rauch ihrer Qual steigt auf in ewigen Zeiten. Keiner von denen, die sich vor dem Tier und seinem Standbild niederwerfen und das Kennzeichen seines Namens annehmen, wird jemals Ruhe finden, weder am Tag noch in der Nacht.“" },
      { number: 12, text: "Hier muss sich die Standhaftigkeit der Menschen bewähren, die zu Gott gehören, die seine Gebote befolgen und auf Jesus vertrauen." },
      { number: 13, text: "Aus dem Himmel hörte ich eine Stimme sagen: „Schreibe: Von jetzt an sind die Toten glücklich zu preisen, die in ‹Verbindung mit› dem Herrn sterben.“ – „Ja“, erwiderte der Geist, „sie werden sich von aller Mühe ausruhen, denn ihre Taten sprechen für sie.“" },
      { number: 14, text: "Dann sah ich eine leuchtende Wolke, auf der jemand thronte, der wie ein Menschensohn aussah. Er trug einen goldenen Kranz und hielt eine scharfe Sichel in seiner Hand.", heading: "Die Ernte" },
      { number: 15, text: "Nun kam ein Engel aus dem Tempel und rief dem auf der Wolke mit lauter Stimme zu: „Gebrauche deine Sichel, denn die Zeit zum Ernten ist gekommen! Die Erde ist überreif dafür.“" },
      { number: 16, text: "Da ließ der, der auf der Wolke saß, seine Sichel über die Erde fahren und erntete sie ab." },
      { number: 17, text: "Noch ein anderer Engel kam aus dem Tempel im Himmel. Auch er hatte eine scharfe Sichel bei sich." },
      { number: 18, text: "Schließlich kam ein Engel vom Altar – es war der, dem das Feuer unterstellt war – und rief dem, der die scharfe Sichel hatte, mit lauter Stimme zu: „Lass deine scharfe Sichel schneiden und ernte die Trauben vom Weinstock der Erde! Seine Beeren sind reif.“" },
      { number: 19, text: "Da ließ der Engel seine Sichel durch den Weinstock der Erde fahren und erntete ihn ab. Die Trauben warf er in die große Weinpresse des göttlichen Zorns" },
      { number: 20, text: "außerhalb der Stadt. Als sie gekeltert wurden, schoss das Blut aus der Presse bis an die Zügel der Pferde hoch. Das Ganze erstreckte sich 300 Kilometer* weit.", footnotes: ["300 Kilometer. Wörtlich: 1600 Stadien, das entspricht etwa der Nord-Süd-Länge Israels. Die große endzeitliche Schlacht (Offenbarung 16,16; 19,11-21; Hesekiel 39) wird offenbar außerhalb der Stadt Jerusalem im ganzen Gebiet von Israel wüten."] },
    ] },
    { number: 15, verses: [
      { number: 1, text: "Dann sah ich im Himmel eine andere außergewöhnliche Erscheinung: Sieben Engel standen bereit, die letzten sieben Plagen auszulösen, die den Zorn Gottes vollenden.", heading: "Ankündigung der letzten schrecklichen Plagen" },
      { number: 2, text: "Dazu sah ich so etwas wie ein Meer, wie Glas mit Feuer gemischt. An seinem Rand sah ich die stehen, die dem Tier standgehalten hatten. Sie hatten sein Bild nicht verehrt und seine Namenszahl abgelehnt. Nun hielten sie Gottesharfen in der Hand" },
      { number: 3, text: "und sangen das Lied von Mose, dem Diener Gottes, das Lied vom Gotteslamm." },
      { number: 4, text: "Wer sollte dich nicht fürchten, Herr, / und deinen Namen nicht preisen? / Denn du allein bist heilig! / Alle Völker werden kommen und niederfallen vor dir, / sie beten dich an, weil dein gerechtes Tun offen vor ihnen liegt.“" },
      { number: 5, text: "Dann sah ich, wie der Tempel, also das Offenbarungszelt im Himmel, sich öffnete," },
      { number: 6, text: "und die sieben Engel mit den sieben schrecklichen Plagen kamen heraus. Jeder war in ein strahlend weißes Leinengewand gehüllt und hatte ein breites goldenes Band um die Brust." },
      { number: 7, text: "Eines der vier mächtigen Wesen reichte den Engeln sieben goldene Schalen, die mit dem Zorn Gottes gefüllt waren, dem Zorn dessen, der in alle Ewigkeit lebt." },
      { number: 8, text: "Da füllte sich der ganze Tempel mit einem Rauch von Herrlichkeit und Gottesmacht. Niemand konnte ihn betreten, bis die sieben Plagen der sieben Engel zu Ende waren." },
    ] },
    { number: 16, verses: [
      { number: 1, text: "Dann hörte ich aus dem Inneren des Tempels eine laute Stimme, die den sieben Engeln zurief: „Geht jetzt und gießt die sieben Schalen mit dem Glutfluss von Gottes Zorn über die Erde!“", heading: "Die sieben Schalen voller Unheil" },
      { number: 2, text: "Der erste Engel trat vor und goss seine Schale über dem Festland aus. Da brach an allen Menschen, die das Malzeichen des Tieres trugen und sein Standbild anbeteten, ein schlimmes bösartiges Geschwür aus." },
      { number: 3, text: "Der zweite Engel trat vor und goss seine Schale über das Meer. Da wurde das Wasser im Meer zu Blut, das wie das geronnene Blut einer Leiche aussah; und alle Lebewesen dort gingen zugrunde." },
      { number: 4, text: "Der dritte Engel goss seine Schale über die Flüsse und Quellen aus. Da wurde alles Wasser zu Blut," },
      { number: 5, text: "und ich hörte den Engel, dem die Gewässer unterstellt waren, sagen: „Gerecht bist du, heiliger Gott, der da ist und schon immer war! Dein Gericht ist gerecht!" },
      { number: 6, text: "Denn Blut hast du denen zu trinken gegeben, die das Blut deiner Heiligen* und Propheten vergossen haben. Sie haben es nicht anders verdient!“", footnotes: ["Heilige sind Menschen, die Gott geheiligt hat, also alle, die zu seinem Volk gehören."] },
      { number: 7, text: "Dann hörte ich eine Stimme vom Altar her sagen: „Ja, Herr, du allmächtiger Gott! Wahr und gerecht sind die Urteile deines Gerichts.“" },
      { number: 8, text: "Der vierte Engel goss seine Schale über der Sonne aus, um die Menschen mit ihrer Glut zu verbrennen." },
      { number: 9, text: "Die Hitze war so schlimm, dass es ihnen die Haut am Körper versengte. Da lästerten sie Gott, der für diese Plagen verantwortlich war, und verfluchten seinen Namen. Doch ihre Einstellung änderten sie nicht und verweigerten Gott die Ehre, die ihm gebührt." },
      { number: 10, text: "Der fünfte Engel goss seine Schale über den Thron des Tieres aus. Da wurde sein ganzes Reich in Finsternis gestürzt, und die Menschen zerbissen sich die Zunge vor Qual." },
      { number: 11, text: "Sie verfluchten Gott im Himmel wegen ihrer Schmerzen und ihrer Geschwüre. Doch ihre Taten bereuten sie nicht." },
      { number: 12, text: "Der sechste Engel goss seine Schale über den großen Strom aus, den Euphrat. Da trocknete dieser völlig aus, so dass ein Weg für die Könige gebahnt wurde, die vom Osten her kommen würden." },
      { number: 13, text: "Dann sah ich aus dem Rachen des Drachen, aus dem Maul des Tieres und aus dem Mund des falschen Propheten drei böse Geister hervorkommen, die wie Frösche aussahen." },
      { number: 14, text: "Es waren Dämonen, die Aufsehen erregende Wunder taten. Sie brachten alle Könige der Erde dazu, ihre Truppen zu dem Krieg aufmarschieren zu lassen, der an jenem großen Tag des allmächtigen Gottes beginnt." },
      { number: 15, text: "– „Passt auf! Ich komme so unerwartet wie ein Dieb. Wie glücklich wird dann der sein, der wach geblieben ist und seine Kleidung bei sich hat. Dann wird er nicht nackt dastehen und sich schämen müssen, wenn ich komme.“* –", footnotes: ["Das ist eine Zwischenbemerkung für die Leser der Offenbarung."] },
      { number: 16, text: "Jene Geistesmacht führte die Könige an einen Ort, der auf Hebräisch Harmagedon*, Berg von Megiddo, heißt.", footnotes: ["Harmagedon. Eine strategisch wichtige Stadt in der Jesreel-Ebene im Norden Israels, die auch Schauplatz wichtiger Schlachten in der alttestamentlichen Geschichte war."] },
      { number: 17, text: "Der siebte Engel goss seine Schale in die Luft aus. Da verkündete eine mächtige Stimme vom Thron aus dem Tempel: „Jetzt ist alles geschehen!“" },
      { number: 18, text: "Blitze zuckten über dem Himmel auf, der Donner krachte und dröhnte und ein schreckliches Beben erschütterte die Erde. Seit Menschen auf der Erde leben, hat es noch nie ein so schweres Erdbeben gegeben." },
      { number: 19, text: "Die große Stadt zerfiel in drei Teile und die Städte aller Völker wurden zerstört. Jetzt wurde mit dem großen Babylon abgerechnet. Gott ließ es den Becher trinken, der mit dem Wein seines unerbittlichen Zorns gefüllt war." },
      { number: 20, text: "Alle Inseln versanken im Meer und auch die Berge verschwanden spurlos." },
      { number: 21, text: "Ein furchtbarer Hagel ging über die Erde nieder; zentnerschwer fielen die Eisbrocken vom Himmel auf die Menschen. Und die Menschen verfluchten Gott wegen des Hagels, der eine außerordentlich schreckliche Plage für sie war." },
    ] },
    { number: 17, verses: [
      { number: 1, text: "Nun trat einer von den sieben Engeln, die die Schalen gehabt hatten, zu mir. „Komm“, sagte er, „ich will dir zeigen, wie Gott die große Hure richten wird, die an den vielen Wasserläufen thront,", heading: "Die große Hure auf dem Tier" },
      { number: 2, text: "mit der sich die Mächtigen der Erde eingelassen haben, und die mit dem Wein ihrer sexuellen Unmoral die ganze Menschheit betrunken gemacht hat.“" },
      { number: 3, text: "Da versetzte mich der Engel im Geist in eine Wüste. Dort sah ich eine Frau auf einem scharlachroten Tier sitzen, das über und über mit Namen bedeckt war, die Gott beleidigen sollten. Es hatte sieben Köpfe und zehn Hörner." },
      { number: 4, text: "Die Frau selbst trug Purpur und scharlachrote Kleidung, und alles an ihr glitzerte von Gold, Edelsteinen und Perlen. Sie hielt einen goldenen Becher in der Hand, der von Abscheulichkeiten überquoll und mit dem widerlichen Dreck ihrer sexuellen Unmoral gefüllt war." },
      { number: 5, text: "Ein geheimnisvoller Name stand auf ihrer Stirn: „Babylon die Große, die Mutter aller Huren und Abscheulichkeiten der Erde.“" },
      { number: 6, text: "Ich sah, dass die Frau betrunken war, berauscht vom Blut der Menschen, die Gott geheiligt hat und die als Zeugen für Jesus einstanden. Erstaunt und sehr verwundert blickte ich sie an." },
      { number: 7, text: "„Warum bist du so erstaunt?“, fragte mich der Engel. „Ich werde dir zeigen, was das Geheimnis dieser Frau ist und was sich hinter dem Tier mit den sieben Köpfen und zehn Hörnern verbirgt, auf dem sie sitzt." },
      { number: 8, text: "Das Tier, das du gesehen hast, war schon einmal da. Jetzt ist es nicht mehr da, aber es wird wieder aus dem Abgrund heraufsteigen, um dann endgültig ins Verderben zu gehen. Alle Bewohner der Erde – alle außer denen, deren Namen seit Gründung der Welt im Lebensbuch stehen – werden über die Rückkehr des Tieres staunen, das schon einmal da war." },
      { number: 9, text: "Hier braucht es einen Verstand mit Weisheit ‹von Gott›. Die sieben Köpfe bedeuten zunächst die sieben Hügel, auf denen die Frau thront. Gleichzeitig stehen sie für sieben Könige," },
      { number: 10, text: "von denen fünf schon gestürzt sind. Einer ist gerade an der Macht und der letzte ist noch nicht gekommen. Wenn er dann kommt, wird er nur eine kurze Zeit herrschen." },
      { number: 11, text: "Und das Tier, das schon einmal da war, ist der achte König, aber zugleich auch einer von den sieben, und es läuft seinem Untergang entgegen." },
      { number: 12, text: "Die zehn Hörner, die du gesehen hast, sind zehn Könige, die ihre Herrschaft noch nicht angetreten haben. An der Seite des Tieres werden sie für eine Stunde königliche Macht erhalten." },
      { number: 13, text: "Alle verfolgen sie das gleiche Ziel und stellen ihre Macht und ihren ganzen Einfluss dem Tier zur Verfügung." },
      { number: 14, text: "Gemeinsam werden sie gegen das Lamm Krieg führen. Doch das Lamm wird sie besiegen, denn es ist Herr über alle Herren und König über alle Könige. Und bei ihm sind die, die Gott berufen und ausgewählt hat, seine Treuen.“" },
      { number: 15, text: "Und weiter erklärte mir der Engel: „Die Wasserläufe, die du gesehen hast, an denen die Hure thront, bedeuten Scharen von Menschen aus Völkern, Sprachen und Kulturen." },
      { number: 16, text: "Und die zehn Hörner, die du gesehen hast, und das Tier selbst werden von Hass auf die Hure erfüllt sein. Sie werden sie verwüsten und nackt dastehen lassen. Zuletzt werden sie ihr Fleisch fressen und alles verbrennen, was von ihr übrig bleibt." },
      { number: 17, text: "Denn Gott hat ihnen den Plan eingegeben, mit dem Tier gemeinsame Sache zu machen und ihm ihre Herrschaftsgewalt zu überlassen, bis die Worte Gottes erfüllt sind." },
      { number: 18, text: "Die Frau, die du gesehen hast, ist die große Stadt, die über alle Könige der Erde herrscht.“" },
    ] },
    { number: 18, verses: [
      { number: 1, text: "Danach sah ich einen anderen Engel vom Himmel herabkommen. Er war mit großer Vollmacht ausgestattet, und die Erde wurde von seiner Herrlichkeit erleuchtet.", heading: "Der Untergang Babylons" },
      { number: 2, text: "Er rief mit mächtiger Stimme: „Babylon ist gefallen! Ja, gefallen ist die große Stadt! Sie ist zu einer Behausung für Dämonen geworden, zu einem Schlupfwinkel für böse Geister aller Art, zu einem Tummelplatz für alles unreine und Abscheu erregende Getier." },
      { number: 3, text: "Alle Völker haben von dem schweren Wein ihrer gierigen sexuellen Unmoral getrunken. Die Könige der Erde haben es mit ihr getrieben, und die Kaufleute der Welt sind durch ihren verschwenderischen Luxus reich geworden.“" },
      { number: 4, text: "Dann hörte ich eine andere Stimme aus dem Himmel sagen: „Verlass die Stadt, mein Volk! Komm heraus, damit du nicht in ihre Sünden verstrickt und von ihren Plagen getroffen wirst!" },
      { number: 5, text: "Denn ihre Sünden türmen sich bis zum Himmel auf, und Gott wird sie dafür zur Rechenschaft ziehen." },
      { number: 6, text: "Vergeltet ihr, wie sie auch euch vergolten hat! / Zahlt ihr doppelt heim, was sie anderen antat! / Gießt ihr ein doppelt so starkes Getränk / in ihren eigenen Kelch!" },
      { number: 7, text: "Gebt ihr so viel Qual und Trauer, wie sie in Prunk und Luxus schwelgte!" },
      { number: 8, text: "Darum werden die Nöte des Todes und der Trauer und des Hungers an einem einzigen Tag über sie kommen, und sie wird in Schutt und Asche gelegt. Denn stark ist Gott, der Herr, der sie gerichtet hat." },
      { number: 9, text: "Wenn dann die Mächtigen der Erde, die sich mit ihr eingelassen und das ausschweifende Leben in vollen Zügen genossen haben, den Rauch sehen, der von den brennenden Trümmern aufsteigt, werden sie klagen und jammern." },
      { number: 10, text: "'Was für ein Unglück!', werden sie rufen und aus Furcht vor ihrer Qual weit entfernt stehen bleiben. 'Was für ein Unglück! Babylon, du große und mächtige Stadt! In einer einzigen Stunde kam das Unglück über dich!'" },
      { number: 11, text: "Auch die Kaufleute in aller Welt werden um sie klagen und trauern, denn niemand kauft ihnen ihre Waren mehr ab:" },
      { number: 12, text: "das Gold und das Silber, die Edelsteine und Perlen, die Gewänder aus Seide und feinem Leinen, die Purpurstoffe und scharlachroten Kleider, das Sandelholz, die Gegenstände aus Elfenbein, Edelholz, Bronze, Eisen und Marmor," },
      { number: 13, text: "den Zimt und den Haarbalsam, Duftstoffe, Salböl und Weihrauch, Wein und Olivenöl, Feinmehl und Weizen, Rinder und Schafe, Pferde und Wagen, Leibeigene und Sklaven." },
      { number: 14, text: "'Auch die Früchte, die du so sehr liebtest, / gibt es nicht mehr! / Dahin ist all dein Glanz und deine Pracht! / Und nichts davon kommt jemals zurück!'" },
      { number: 15, text: "So werden die Kaufleute jammern, die durch diese Dinge reich geworden sind. Aus Furcht vor ihrer Qual stehen sie in der Ferne und weinen vor Trauer und Schmerz." },
      { number: 16, text: "'Was für ein Unglück!', werden sie rufen. 'Was für ein Unglück! Diese mächtige Stadt! Einst war sie in feines Leinen gekleidet, in Purpur und scharlachrote Stoffe gehüllt und mit Gold, Edelsteinen und Perlen geschmückt." },
      { number: 17, text: "In einer einzigen Stunde ist solch ein Reichtum verschwunden!'" },
      { number: 18, text: "den Rauch, der von den brennenden Trümmern aufstieg, und riefen: 'An diese großartige Stadt kam keine andere heran!'" },
      { number: 19, text: "Vor Trauer streuten sie sich Staub auf den Kopf und klagten weinend: 'Was für ein Unglück! Wie furchtbar für diese große Stadt. Durch ihre Schätze sind alle reich geworden, die Schiffe auf See unterhielten. Und nun wurde sie in einer einzigen Stunde vernichtet!'" },
      { number: 20, text: "Ihr Himmel, jubelt über ihren Untergang! / Freut euch ihr Heiligen, freut euch Apostel und Propheten! / Für alles, was sie euch antat, / hat Gott nun sein Urteil an ihr vollstreckt!“" },
      { number: 21, text: "Dann hob ein starker Engel einen Felsbrocken hoch, der so groß wie ein Mühlstein war, und warf ihn ins Meer. Dabei sagte er:" },
      { number: 22, text: "Nie mehr wird man Harfenklänge und Gesang in deinen Mauern hören, / nie mehr Flötenspiel und Trompetenklang! / Nie mehr wird ein Handwerker in dir arbeiten, / und nie mehr wird das Geräusch der Mühle gehört." },
      { number: 23, text: "Das Licht der Lampen ist für immer erloschen; / und der Jubel von Bräutigam und Braut für immer verstummt. / Denn deine Kaufleute waren die Großen der Welt / und mit deiner Magie hast du alle Völker verführt." },
      { number: 24, text: "Blut fand man in ihr, das Blut von Propheten / und von Menschen, die Gottes Eigentum sind, / und von allen, die man auf der Erde umgebracht hat.“" },
    ] },
    { number: 19, verses: [
      { number: 1, text: "Danach hörte ich im Himmel einen gewaltigen Jubelchor:", heading: "Jubel über den gerechten Richter", footnotes: ["Der hebräische Jubelruf Halleluja kommt im Neuen Testament nur in diesem Kapitel vor."] },
      { number: 2, text: "Denn seine Urteile sind wahr und gerecht. / Die große Hure hat er hingerichtet, / die mit ihrer Unmoral die ganze Erde verdarb, / und das Blut seiner Diener hat er an ihr gerächt.“" },
      { number: 3, text: "Und von neuem klangen die Jubelrufe auf:" },
      { number: 4, text: "Auch die 24 Ältesten und die vier mächtigen Wesen beteten Gott an. Sie fielen vor seinem Thron nieder und sagten: „Amen! Halleluja!“" },
      { number: 5, text: "Dann war eine Stimme zu hören, die vom Thron her kam und rief: „Lobt unseren Gott, ihr Geringen und ihr Großen, alle, die ihr ihm gehört und ihm ehrfürchtig dient!“" },
      { number: 6, text: "Dann hörte ich einen Jubelgesang, der von einem vielstimmigen Chor zu kommen schien. Er klang wie das Tosen einer starken Brandung und gleichzeitig wie lautes Donnerrollen:", heading: "Die Hochzeit des Lammes" },
      { number: 7, text: "Wir wollen uns freuen und jubeln / und ihm die Ehre geben! / Denn jetzt ist die Hochzeit des Lammes gekommen / und seine Braut hat sich dafür schön gemacht." },
      { number: 8, text: "Strahlend weißes Leinen hatte Gott ihr dazu geschenkt.“" },
      { number: 9, text: "Dann befahl mir der Engel: „Schreibe: Glücklich diejenigen, die zum Hochzeitsmahl des Lammes eingeladen sind!“ Und er fügte hinzu: „Das sind Gottes zuverlässige Worte.“" },
      { number: 10, text: "Da warf ich mich ihm zu Füßen, um ihn anzubeten. Aber er sagte zu mir: „Tu das nicht! Ich bin auch nur ein Diener ‹Gottes› wie du und deine Brüder, denen Jesus das bezeugt. Bete Gott an! Denn was Jesus bezeugt, ist das, was der Geist eingibt.“" },
      { number: 11, text: "Dann sah ich den Himmel geöffnet und auf einmal erschien ein weißes Pferd. Der Reiter heißt: Der Treue und Wahrhaftige. Er führt einen gerechten Krieg und richtet gerecht.", heading: "Christus, der Sieger" },
      { number: 12, text: "Seine Augen lodern wie Feuerflammen, auf seinem Kopf trägt er viele Diademe*, und an seiner Stirn steht ein Name, dessen Bedeutung nur er selber kennt.", footnotes: ["Diademe. Vielleicht eine Krone, die aus vielen halbkreisförmigen kostbaren Reifen besteht. Siehe auch 12,3."] },
      { number: 13, text: "Sein Mantel ist voller Blut und sein Name lautet: Das Wort Gottes." },
      { number: 14, text: "Die Heere des Himmels folgen ihm. Sie reiten auf weißen Pferden und sind in reines weißes Leinen gekleidet." },
      { number: 15, text: "Aus dem Mund des Reiters kommt ein scharfes Schwert heraus, mit dem er die Völker besiegen wird. Und mit eisernem Zepter wird er über sie herrschen. Er vollstreckt den furchtbaren Zorn des allmächtigen Gottes und wird die Völker wie reife Trauben in der Kelter zertreten." },
      { number: 16, text: "Auf der Seite seines Mantels steht noch ein Name: „König der Könige und Herr der Herren!“" },
      { number: 17, text: "Dann sah ich einen Engel in der Sonne stehen, der allen Vögeln, die oben am Himmel flogen, laut zurief: „Kommt her! Sammelt euch zum großen Mahl, das Gott euch gibt." },
      { number: 18, text: "Fresst euch satt am Fleisch von Königen und Generälen, fresst das Fleisch von Mächtigen, von Pferden und ihren Reitern, fresst das Fleisch von Freien und Sklaven, von Großen und Geringen!“" },
      { number: 19, text: "Schließlich sah ich, wie das Tier die Könige der Erde zusammenbrachte und wie sie mit ihren Heeren gegen den Reiter auf dem weißen Pferd und seinem Heer in den Kampf zogen." },
      { number: 20, text: "Doch das Tier wurde gefangen genommen und mit ihm der falsche Prophet, der unter den Augen des Tieres all die auffälligen Wunder getan hatte. Dadurch waren alle verführt worden, die das Zeichen des Tieres angenommen und sein Standbild angebetet hatten. Beide wurden lebendig in den See geworfen, der mit brennendem Schwefel gefüllt ist, den Feuersee." },
      { number: 21, text: "Alle anderen wurden mit dem Schwert umgebracht, das aus dem Mund des Reiters auf dem weißen Pferd kam. Und alle Vögel fraßen sich an ihrem Fleisch satt." },
    ] },
    { number: 20, verses: [
      { number: 1, text: "Dann sah ich einen Engel aus dem Himmel herabsteigen, der den Schlüssel zum Abgrund und eine schwere Kette in der Hand hatte." },
      { number: 2, text: "Er packte den Drachen, die uralte Schlange, die auch Teufel oder Satan genannt wird, und legte ihn für tausend Jahre in Ketten." },
      { number: 3, text: "Dann warf er ihn in den Abgrund, verschloss den Eingang und versiegelte ihn, sodass der Teufel bis zum Ablauf der tausend Jahre die Völker nicht mehr verführen konnte. Danach muss er ‹nach dem Willen Gottes› noch einmal für kurze Zeit losgelassen werden." },
      { number: 4, text: "Dann sah ich Throne und sah, wie alle, die darauf Platz nahmen, ermächtigt wurden, Gericht zu halten. Ich sah auch die Seelen derer, die enthauptet worden waren, weil sie sich zur Botschaft von Jesus bekannt hatten und ‹öffentlich› für Gottes Wort eingetreten waren. Sie hatten das Tier und sein Standbild nicht angebetet und dessen Kennzeichen für Hand und Stirn nicht angenommen. Jetzt lebten sie und herrschten tausend Jahre mit dem Messias." },
      { number: 5, text: "Das ist die erste Auferstehung. Die übrigen Toten wurden erst nach dem Ende der tausend Jahre lebendig." },
      { number: 6, text: "Alle, die an dieser ersten Auferstehung teilhaben dürfen, sind glücklich zu preisen. Sie gehören zu Gottes heiligem Volk und der zweite Tod wird keine Macht über sie haben. Sie werden Gott und Christus als Priester dienen und die tausend Jahre zusammen mit Christus regieren." },
      { number: 7, text: "Wenn die tausend Jahre dann vorüber sind, wird Satan aus seinem Gefängnis freigelassen.", heading: "Der letzte Aufstand gegen Gott" },
      { number: 8, text: "Er wird in alle vier Himmelsrichtungen losziehen, um Gog und Magog, die Völker der ganzen Erde, zu verführen. Er wird sie dazu bringen, gemeinsam in den Krieg zu ziehen. Ihre Zahl wird sein wie der Sand am Meer." },
      { number: 9, text: "Sie überzogen die ganze Fläche der Erde mit ihren Heeren und umringten das Lager der Heiligen und die geliebte Stadt. Doch dann wird Feuer vom Himmel fallen und sie vernichten." },
      { number: 10, text: "Und der Teufel, der sie verführt hatte, wurde in den See von Feuer und Schwefel geworfen, wo auch das Tier und der Falschprophet sind. So werden sie für immer und ewig, Tag und Nacht, gequält." },
      { number: 11, text: "Dann sah ich einen großen weißen Thron und sah, wie Erde und Himmel vor dem, der darauf saß, entflohen. Sie konnten vor ihm nicht bestehen und verschwanden im Nichts.", heading: "Das Weltgericht" },
      { number: 12, text: "Vor dem Thron aber sah ich die Toten stehen, vom Größten bis zum Kleinsten. Es wurden Bücher aufgeschlagen, in denen alle Taten aufgeschrieben sind, und dann noch ein anderes Buch: das Buch des Lebens. Anschließend wurde Gericht über die Toten gehalten. Jeder bekam das Urteil, das seinen Taten entsprach." },
      { number: 13, text: "Auch das Meer gab seine Toten heraus, ebenso der Tod und sein Reich. Jeder von diesen Toten bekam das Urteil, das seinen Taten entsprach." },
      { number: 14, text: "Schließlich wurde der Tod selbst in den Feuersee geworfen und das Totenreich dazu. Das ist der zweite Tod." },
      { number: 15, text: "Wenn also jemand nicht ‹mehr› im Buch des Lebens stand,* wurde er in den Feuersee geworfen.", footnotes: ["Offenbar standen alle Menschen seit Gründung der Welt erst einmal in diesem Lebensbuch. Siehe auch Offb 13,8; 17,8 und 3,5."] },
    ] },
    { number: 21, verses: [
      { number: 1, text: "Dann sah ich einen ganz neuen Himmel und eine völlig neuartige Erde. Der erste Himmel und die erste Erde waren vergangen, auch das Meer gab es nicht mehr.", heading: "Das neue Jerusalem" },
      { number: 2, text: "Ich sah, wie die Heilige Stadt, das neue Jerusalem, von Gott aus dem Himmel herabkam. Sie war schön wie eine Braut, die sich für ihren Bräutigam geschmückt hat." },
      { number: 3, text: "Und vom Thron her hörte ich eine laute Stimme sagen: „Jetzt ist Gottes Wohnung* bei den Menschen. Unter ihnen wird er wohnen und sie alle werden seine Völker sein. Gott selbst wird als ihr Gott bei ihnen sein.", footnotes: ["Das griechische Wort für Wohnung bedeutet auch Zelt und erinnert an das Zeltheiligtum der Israeliten in der Zeit der Wüstenwanderung."] },
      { number: 4, text: "Jede Träne wird er von ihren Augen wischen. Es wird keinen Tod mehr geben und auch keine Traurigkeit, keine Klage, keinen Schmerz. Was früher war, ist für immer vorbei.“" },
      { number: 5, text: "Da sagte der, der auf dem Thron saß: „Sieh her! Ich mache alles neu!“ Und weiter: „Schreib diese Worte auf! Sie sind zuverlässig und wahr.“", heading: "Wort an die Leser" },
      { number: 6, text: "Und er fuhr fort: „Nun ist alles erfüllt. Ich bin das Alpha und das Omega*, der Ursprung und das Ziel. Wer Durst hat, dem werde ich umsonst zu trinken geben: Wasser aus der Quelle des Lebens.", footnotes: ["Alpha und Omega. Erster und letzter Buchstabe des griechischen Alphabets."] },
      { number: 7, text: "Wer den Kampf besteht, wird das alles erben. Ich werde sein Gott und er wird mein Sohn sein." },
      { number: 8, text: "Aber die Feiglinge, die Treulosen und die, die sich mit abscheulichen Dingen abgeben, die sexuell unmoralisch leben, und alle, die okkulte Praktiken ausüben, die Mörder, die Götzendiener und alle Lügner – sie erwartet der See, der mit brennendem Schwefel gefüllt ist. Das ist der zweite Tod.“" },
      { number: 9, text: "Einer von den sieben Engeln, die die Schalen mit den sieben letzten Plagen ausgeschüttet hatten, kam zu mir und sagte: „Komm her! Ich will dir die Braut des Lammes zeigen, die Frau, die zu ihm gehört.“", heading: "Beschreibung der Stadt" },
      { number: 10, text: "Dann nahm er mich im Geist auf einen sehr hohen Berg mit und zeigte mir die heilige Stadt Jerusalem, die von Gott aus dem Himmel herabgekommen war." },
      { number: 11, text: "Gottes Herrlichkeit erfüllte sie, und sie leuchtete wie ein überaus kostbarer Edelstein, sie funkelte wie ein Diamant." },
      { number: 12, text: "Die Stadt war von einer sehr hohen Mauer umgeben und hatte zwölf Tore, auf denen zwölf Namen standen, die Namen der zwölf Stämme Israels. Zwölf Engel standen davor." },
      { number: 13, text: "Drei Tore gingen nach Osten, drei nach Norden, drei nach Süden und drei nach Westen." },
      { number: 14, text: "Die Stadtmauer war auf zwölf Grundsteinen errichtet, auf denen ebenfalls zwölf Namen standen, die Namen der zwölf Apostel des Lammes." },
      { number: 15, text: "Der Engel, der mit mir sprach, hatte einen goldenen Messstab in der Hand, womit er die Stadt, die Tore und ihre Mauern ausmessen wollte." },
      { number: 16, text: "Die Stadt war quadratisch angelegt, Länge und Breite waren gleich. Als er die Stadt ausmaß, ergaben sich je 2200 Kilometer* in Länge, Breite und Höhe.", footnotes: ["2200 Kilometer. 12.000 Stadien. Stadion ist ein griechisches Längenmaß von etwa 185 m. Alle im Text angegebenen Maße sind durch 12 teilbar."] },
      { number: 17, text: "Dann maß er die Höhe der Stadtmauer. Sie betrug, nach menschlichem Maß gerechnet, wie der Engel es gebrauchte, 72 Meter*.", footnotes: ["72 Meter. Wörtlich: 144 Ellen."] },
      { number: 18, text: "Die Mauer bestand aus Diamant*. Die Stadt selbst war aus reinem Gold gebaut, das wie Kristallglas schimmerte.", footnotes: ["Diamant. Der in 21,11.18.19 erwähnte Edelstein wird im Griechischen als Jaspis bezeichnet, aber gleichzeitig als überaus glänzend und wertvoll beschrieben. Diese Eigenschaften treffen viel mehr auf den Diamant zu als auf den heute bekannten minderwertigen Halbedelstein Jaspis, der undurchsichtig und bräunlich bis grün gefärbt ist."] },
      { number: 19, text: "Die Fundamente der Stadtmauer waren mit verschiedenartigen kostbaren Steinen geschmückt. Beim ersten Grundstein war es Diamant, beim zweiten Saphir, beim dritten Rubin, beim vierten Smaragd," },
      { number: 20, text: "beim fünften Achat, beim sechsten Karneol, beim siebten Chrysolith, beim achten Beryll, beim neunten Topas, beim zehnten Chrysopras, beim elften Hyazinth, beim zwölften Amethyst." },
      { number: 21, text: "Die zwölf Stadttore bestanden aus zwölf Perlen, jedes Tor war aus einer einzigen Perle geformt. Die Hauptstraße war aus reinem Gold, glänzend klar wie Glas." },
      { number: 22, text: "Einen Tempel sah ich nicht in der Stadt. Der Herr selbst ist ihr Tempel, der allmächtige Gott und das Lamm." },
      { number: 23, text: "Die Stadt braucht weder Sonne noch Mond, damit es hell in ihr ist. Sie wird von der Herrlichkeit Gottes überstrahlt und ihre Leuchte ist das Lamm." },
      { number: 24, text: "Die Völker der Erde werden in ihrem Licht leben, und ihre Könige werden kommen und ihren Reichtum in die Stadt tragen." },
      { number: 25, text: "Ihre Tore werden den ganzen Tag offenstehen. Sie werden immer offen sein, weil es dort keine Nacht mehr gibt." },
      { number: 26, text: "Die herrlichsten Schätze und Kostbarkeiten der Völker werden in die Stadt gebracht." },
      { number: 27, text: "In diese Stadt wird nie etwas Unreines kommen. Wer immer wieder tut, was Gott verabscheut, wer vom Lügen bestimmt ist, wird niemals dort hineinkommen, sondern nur die, die im Lebensbuch des Lammes stehen.", heading: "Wort an die Leser" },
    ] },
    { number: 22, verses: [
      { number: 1, text: "Der Engel zeigte mir auch einen kristallklaren Strom, der aus dem Thron Gottes und des Lammes hervorkam. Es war der Strom mit dem Wasser des Lebens.", heading: "Beschreibung der Stadt" },
      { number: 2, text: "Er floss in der Mitte der Hauptstraße durch die Stadt. An seinen beiden Ufern wuchs der Baum des Lebens, der zwölfmal im Jahr Früchte trägt, jeden Monat einmal, und dessen Blätter zur Gesundheit der Völker dienen." },
      { number: 3, text: "Dort wird es nichts mehr geben, was unter dem Fluch Gottes steht. Der Thron Gottes und des Lammes wird in der Stadt sein, und ihre Bewohner werden ihm als Priester dienen." },
      { number: 4, text: "Sie werden sein Gesicht sehen und seinen Namen an ihrer Stirn tragen." },
      { number: 5, text: "Dann wird es keine Nacht mehr geben, sodass sie keine Beleuchtung mehr brauchen, nicht einmal das Sonnenlicht. Denn Gott, der Herr, wird über ihnen leuchten, und sie werden königlich herrschen für immer und ewig." },
      { number: 6, text: "Er versicherte mir: „Alles, was dir gesagt wurde, ist zuverlässig und wahr. Der Herr selbst – der Gott, dessen Geist durch die Propheten spricht – hat seinen Engel* gesandt, um seinen Dienern zu zeigen, was bald geschehen muss.“", heading: "Schlusswort an die Leser", footnotes: ["Gemeint ist der Engel von Offenbarung 1,1."] },
      { number: 7, text: "‹Und Jesus sagte:› „Gebt Acht! Ich komme bald! Glücklich ist, wer an den prophetischen Worten dieses Buches festhält.“" },
      { number: 8, text: "Ich, Johannes, habe alles gesehen und gehört, was hier berichtet ist. Überwältigt von dem, was ich gehört und gesehen hatte, warf ich mich vor dem Engel nieder, der mir dies gezeigt hatte, und wollte ihn anbeten." },
      { number: 9, text: "Doch er sagte: „Tu das nicht! Ich bin auch nur ein Diener ‹Gottes› wie du und deine Brüder, die Propheten, und wie alle, die sich nach den Worten dieses Buches richten. Bete Gott an!“" },
      { number: 10, text: "Dann sagte er zu mir: „Versiegle dieses Buch nicht! Halte seine prophetischen Worte nicht geheim, denn die Zeit, ‹in der sie sich erfüllen›, ist nah!" },
      { number: 11, text: "Wer Unrecht tut, mag es weiterhin tun, wer an schmutzigen Dingen Gefallen hat, mag sich weiter beschmutzen. Wer aber gerecht ist, soll weiter gerecht handeln, und wer sich Gott geweiht hat, soll das auch weiterhin tun." },
      { number: 12, text: "Ja, ich komme bald und bringe jedem den Lohn mit, der seinem Werk entspricht." },
      { number: 13, text: "Ich bin das Alpha und das Omega, der Erste und der Letzte, der Ursprung und das Ziel.“" },
      { number: 14, text: "Glücklich diejenigen, die ihre Kleider waschen. Die Tore der Stadt werden ihnen offenstehen, und sie haben das Recht, vom Baum des Lebens zu essen." },
      { number: 15, text: "Doch die Hunde* müssen draußen bleiben und mit ihnen alle, die okkulte Praktiken betreiben oder in sexueller Unmoral leben, alle Mörder und Götzenanbeter, überhaupt alle, die sich für die Lüge entschieden haben, die sie lieben und tun.", footnotes: ["Hunde. Ein Ausdruck, der nach Philipper 3,2 dem Evangelium feindliche Personen bezeichnet, nach 5. Mose 23,18-19 sogar männliche Prostituierte."] },
      { number: 16, text: "„Ich, Jesus, habe meinen Engel gesandt, damit diese Botschaft den Gemeinden bekanntgemacht wird. Ich bin der Wurzelspross und Nachkomme Davids, der glänzende Morgenstern.“", heading: "Jesus selbst bürgt für die Wahrheit" },
      { number: 17, text: "Der Geist und die Braut rufen: „Komm!“ Und wer es hört, soll in den Ruf mit einstimmen: „Komm!“ Und wer Durst hat, der komme. Wer will, der trinke vom Wasser des Lebens! Er bekommt es geschenkt." },
      { number: 18, text: "Ich erkläre jedem, der die prophetischen Worte dieses Buches hört: „Wenn jemand etwas zu dem hinzufügt, was hier geschrieben steht, dem wird Gott die Plagen zufügen, die in diesem Buch beschrieben sind." },
      { number: 19, text: "Und wenn jemand irgendetwas von den prophetischen Worten dieses Buches unterschlägt, dem wird Gott das wegnehmen, was ihm in diesem Buch als Anteil zugesprochen ist, das Recht, in der heiligen Stadt zu wohnen und vom Baum des Lebens zu essen.“" },
      { number: 20, text: "Der, der sich für die Wahrheit dieser Dinge verbürgt, sagt: „Ja, ich komme bald!“ – „Amen, komm doch, Herr Jesus!“" },
      { number: 21, text: "Die Gnade des Herrn Jesus sei mit allen!" },
    ] },
  ]
};